INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',14,replace(replace('<p class="bodytext">Imassi <a name="M0.0014"></a> thaṃ dāni ha to dhesu ca.</p>\r\n<p class="bodytext"><a name="para235"></a><span class="paranum">235</span>, 281. A dhunāmhi ca.</p>\r\n<p class="bodytext"><a name="para236"></a><span class="paranum">236</span>, 280. Eta rahimhi.</p>\r\n<p class="bodytext"><a name="para237"></a><span class="paranum">237</span>, 176. Itthiyamato āpaccayo.</p>\r\n<p class="bodytext"><a name="para238"></a><span class="paranum">238</span>, 187. Nadādito vā ī.</p>\r\n<p class="bodytext"><a name="para239"></a><span class="paranum">239</span>, 190. Ṇava ṇika ṇeyya ṇantuhi.</p>\r\n<p class="bodytext"><a name="para240"></a><span class="paranum">240</span>, 193. Patibhikkhurājīkārantehi inī.</p>\r\n<p class="bodytext"><a name="para241"></a><span class="paranum">241</span>, 191. Ntussa tadhīkāre.</p>\r\n<p class="bodytext"><a name="para242"></a><span class="paranum">242</span>, 192. Bhavato bhoto.</p>\r\n<p class="bodytext"><a name="para243"></a><span class="paranum">243</span>, 110. Bho ge tu.</p>\r\n<p class="bodytext"><a name="para244"></a><span class="paranum">244</span>, 72. Akārapitādyantānamā.</p>\r\n<p class="bodytext"><a name="para245"></a><span class="paranum">245</span>, 152. Jhalapā rassaṃ.</p>\r\n<p class="bodytext"><a name="para246"></a><span class="paranum">246</span>, 73. Ākāro vā.</p>\r\n<p class="centered">Iti nāmakappe catuttho kaṇḍo.</p>\r\n<p class="bodytext"><a name="para247"></a><span class="paranum">247</span>, 261. Tvādayo vibhattisaññāyo.</p>\r\n<p class="bodytext"><a name="para248"></a><span class="paranum">248</span>, 260. Kvaci to pañcamyatthe.</p>\r\n<p class="bodytext"><a name="para249"></a><span class="paranum">249</span>, 266. Tratha sattamiyā sabbanāmehi.</p>\r\n<p class="bodytext"><a name="para250"></a><span class="paranum">250</span>, 268. Sabbato dhi.</p>\r\n<p class="bodytext"><a name="para2