INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',15,replace(replace('<p class="bodytext">Tamhā <a name="M0.0015"></a> ca.</p>\r\n<p class="bodytext"><a name="para254"></a><span class="paranum">254</span>, 274. Imasmā hadhā ca.</p>\r\n<p class="bodytext"><a name="para255"></a><span class="paranum">255</span>, 275. Yato hiṃ.</p>\r\n<p class="bodytext"><a name="para256"></a><span class="paranum">256</span>, 0. Kāle.</p>\r\n<p class="bodytext"><a name="para257"></a><span class="paranum">257</span>, 276. Kiṃsabbaññekayakuhi dādācanaṃ.</p>\r\n<p class="bodytext"><a name="para258"></a><span class="paranum">258</span>, 278. Tamhā dāni ca.</p>\r\n<p class="bodytext"><a name="para259"></a><span class="paranum">259</span>, 279. Imasmā rahidhunādāni ca.</p>\r\n<p class="bodytext"><a name="para260"></a><span class="paranum">260</span>, 277. Sabbassa so dāmhi vā.</p>\r\n<p class="bodytext"><a name="para261"></a><span class="paranum">261</span>, 369. Avaṇṇo ye lopañca.</p>\r\n<p class="bodytext"><a name="para262"></a><span class="paranum">262</span>, 391. Vuḍḍhassa jo iyiṭṭhesu.</p>\r\n<p class="bodytext"><a name="para263"></a><span class="paranum">263</span>, 392. Pasatthassa so ca.</p>\r\n<p class="bodytext"><a name="para264"></a><span class="paranum">264</span>, 393. Antikassa nedo.</p>\r\n<p class="bodytext"><a name="para265"></a><span class="paranum">265</span>, 394. Bāḷhassa sādho.</p>\r\n<p class="bodytext"><a name="para266"></a><span class="paranum">266</span>, 395. Appassa kaṇa.</p>\r\n<p class="bodytext"><a name="para267"></a><span class="paranum">267</span>, 396. Yuvānañca.</p>\r\n<p class="bodytext"><a name="para268"></a><span class="paranum">268</span>, 397. Vantumantuvīnañca lopo.</p>\r\n<p class="bodytext"><a name="para269"></a><span class="paranum">269</span>, 41. Yavataṃ talaṇadakārānaṃ byañjanāni calañajakārattaṃ.</p>\r\n<p class="bodytext"><a name="para270"></a><span class="paranum">270</span>, 120. Amha tumha ntu