INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',18,replace(replace('<p class="bodytext">Okāse <a name="M0.0018"></a> sattamī.</p>\r\n<p class="bodytext"><a name="para303"></a><span class="paranum">303</span>, 321. Sāmissarādhipati dāyāda sakkhī pathibhū pasuta kusalehi ca.</p>\r\n<p class="bodytext"><a name="para304"></a><span class="paranum">304</span>, 322. Niddhāraṇe ca.</p>\r\n<p class="bodytext"><a name="para305"></a><span class="paranum">305</span>, 323. Anādare ca.</p>\r\n<p class="bodytext"><a name="para306"></a><span class="paranum">306</span>, 289. Kvaci dutiyā chaṭṭhīnamatthe.</p>\r\n<p class="bodytext"><a name="para307"></a><span class="paranum">307</span>, 290. Tatiyāsattamīnañca.</p>\r\n<p class="bodytext"><a name="para308"></a><span class="paranum">308</span>, 317. Chaṭṭhī ca.</p>\r\n<p class="bodytext"><a name="para309"></a><span class="paranum">309</span>, 318. Dutiyāpañcamīnañca.</p>\r\n<p class="bodytext"><a name="para310"></a><span class="paranum">310</span>, 324. Kammakaraṇanimittatthesu sattamī.</p>\r\n<p class="bodytext"><a name="para311"></a><span class="paranum">311</span>, 325. Sampadāne ca.</p>\r\n<p class="bodytext"><a name="para312"></a><span class="paranum">312</span>, 326. Pañcamyatthe ca.</p>\r\n<p class="bodytext"><a name="para313"></a><span class="paranum">313</span>, 327. Kālabhāvesu ca.</p>\r\n<p class="bodytext"><a name="para314"></a><span class="paranum">314</span>, 328. Upa’dhyādhikissaravacane.</p>\r\n<p class="bodytext"><a name="para315"></a><span class="paranum">315</span>, 329. Maṇḍitussukkesu tatiyā ca.</p>\r\n<p class="centered">Iti nāmakappe kārakakappo chaṭṭho kaṇḍo.</p>\r\n<p class="centered">Kārakasuttaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">4. Samāsa</p>\r\n<p class="bodytext"><a name="para316"></a><span class="paranum">316</span>, 331. Nāmāna samāso yuttattho.</p>\r\n<p class="bodytext"><a name="para317"></a><span class="paranum">317</span>, 332. Tesaṃ vibhattiyo lopā