INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',19,replace(replace('<p class="bodytext">Upasagganipātapubbako <a name="M0.0019"></a> abyayībhāvo.</p>\r\n<p class="bodytext"><a name="para320"></a><span class="paranum">320</span>, 335. So napuṃsakaliṅgo.</p>\r\n<p class="bodytext"><a name="para321"></a><span class="paranum">321</span>. 349. Digussekattaṃ.</p>\r\n<p class="bodytext"><a name="para322"></a><span class="paranum">322</span>, 359. Tathā dvande pāṇi tūriye yogga senaṅga khuddajantuka vividha viruddha visabhāgatthādīnañca.</p>\r\n<p class="bodytext"><a name="para323"></a><span class="paranum">323</span>, 360. Vibhāsā rukkha tiṇa pasu dhana dhañña janapadā dīnañca.</p>\r\n<p class="bodytext"><a name="para324"></a><span class="paranum">324</span>, 339. Dvipade tulyādhikaraṇe kammadhārayo.</p>\r\n<p class="bodytext"><a name="para325"></a><span class="paranum">325</span>, 348. Saṅkhyāpubbo digu.</p>\r\n<p class="bodytext"><a name="para326"></a><span class="paranum">326</span>, 341. Ubhe tappurisā.</p>\r\n<p class="bodytext"><a name="para327"></a><span class="paranum">327</span>, 351. Amādayo parapadebhi.</p>\r\n<p class="bodytext"><a name="para328"></a><span class="paranum">328</span>, 352. Aññapadatthesu bahubbīhi.</p>\r\n<p class="bodytext"><a name="para329"></a><span class="paranum">329</span>, 357. Nāmānaṃ samuccayo dvando.</p>\r\n<p class="bodytext"><a name="para330"></a><span class="paranum">330</span>, 340. Mahataṃ mahā tulyādhikaraṇe pade.</p>\r\n<p class="bodytext"><a name="para331"></a><span class="paranum">331</span>, 353. Itthiyaṃ bhāsitapumitthī pumāva ce.</p>\r\n<p class="bodytext"><a name="para332"></a><span class="paranum">332</span>, 343. Kammadhārayasaññe ca.</p>\r\n<p class="bodytext"><a name="para333"></a><span class="paranum">333</span>, 344. Attaṃ nassa tappurise.</p>\r\n<p class="bodytext"><a name="para334"></a><span class="paranum">334</span>, 345. Sare ana.</p>\r\n<p class="bodytext"><a na