INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',20,replace(replace('<p class="bodytext">Kvaci <a name="M0.0020"></a> samāsanta gatānamakāranto.</p>\r\n<p class="bodytext"><a name="para338"></a><span class="paranum">338</span>, 356. Nadimhā ca.</p>\r\n<p class="bodytext"><a name="para339"></a><span class="paranum">339</span>, 358. Jāyāya tudaṃ jāni patimhi.</p>\r\n<p class="bodytext"><a name="para340"></a><span class="paranum">340</span>, 355. Dhanumhā ca.</p>\r\n<p class="bodytext"><a name="para341"></a><span class="paranum">341</span>, 336. Aṃvibhattīnamakārantā abyāyitāvā.</p>\r\n<p class="bodytext"><a name="para342"></a><span class="paranum">342</span>, 337. Saro rasso napuṃsake.</p>\r\n<p class="bodytext"><a name="para343"></a><span class="paranum">343</span>, 338. Aññasmā lopo ca.</p>\r\n<p class="centered">Iti nāmakappe samāsakappo sattamo kaṇḍo.</p>\r\n<p class="centered">Samāsasutthaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">5. Taddhita</p>\r\n<p class="bodytext"><a name="para244"></a><span class="paranum">244</span>, 361. Vā ṇā’pacce.</p>\r\n<p class="bodytext"><a name="para345"></a><span class="paranum">345</span>, 366. Ṇāyana ṇāna vacchādito.</p>\r\n<p class="bodytext"><a name="para346"></a><span class="paranum">346</span>, 367. Ṇeyyo kattiyādīhi.</p>\r\n<p class="bodytext"><a name="para347"></a><span class="paranum">347</span>, 368. Ato ṇi vā.</p>\r\n<p class="bodytext"><a name="para348"></a><span class="paranum">348</span>, 371. Ṇavopakvādīhi.</p>\r\n<p class="bodytext"><a name="para349"></a><span class="paranum">349</span>, 372. Ṇera vidhavādito.</p>\r\n<p class="bodytext"><a name="para350"></a><span class="paranum">350</span>, 373. Yena vā saṃsaṭṭhaṃ tarati carati vahati ṇiko.</p>\r\n<p class="bodytext"><a name="para351"></a><span class="paranum">351</span>, 374. Tamadhīte tenakatādhi sannidhānaniyoga sippa bhaṇḍa jīvikatthesu ca.</p>\r\n<p class="bodytext"><a name="para352"></a><span class="paranum">352</span>, 376. </p>','\r',char(13)),'\n',char(10)),'-338-339-340-341-342-343-244-345-346-347-348-349-350-351-352-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',21,replace(replace('<p class="bodytext">Ṇa <a name="M0.0021"></a> rāgā tassedamaññatthesu ca.</p>\r\n<p class="bodytext"><a name="para353"></a><span class="paranum">353</span>, 378. Jātādīnamimiyā ca.</p>\r\n<p class="bodytext"><a name="para354"></a><span class="paranum">354</span>, 379. Samūhatthe kaṇa ṇā.</p>\r\n<p class="bodytext"><a name="para355"></a><span class="paranum">355</span>, 380. Gāma jana bandhu sahāyādīhi tā.</p>\r\n<p class="bodytext"><a name="para356"></a><span class="paranum">356</span>, 381. Tadassa ṭhānamiyo ca.</p>\r\n<p class="bodytext"><a name="para357"></a><span class="paranum">357</span>, 382. Upamatthāyitattaṃ.</p>\r\n<p class="bodytext"><a name="para358"></a><span class="paranum">358</span>, 383. Tannissitatthe lo.</p>\r\n<p class="bodytext"><a name="para359"></a><span class="paranum">359</span>, 384. Ālu tabbahule.</p>\r\n<p class="bodytext"><a name="para360"></a><span class="paranum">360</span>, 387. Ṇuttatā bhāve tu.</p>\r\n<p class="bodytext"><a name="para361"></a><span class="paranum">361</span>, 388. Ṇa visamādīhi.</p>\r\n<p class="bodytext"><a name="para362"></a><span class="paranum">362</span>, 389. Ramaṇīyādito kaṇa</p>\r\n<p class="bodytext"><a name="para363"></a><span class="paranum">363</span>, 390. Visese taratamisikiyiṭṭhā.</p>\r\n<p class="bodytext"><a name="para364"></a><span class="paranum">364</span>, 398. Tadassatthīti vī ca.</p>\r\n<p class="bodytext"><a name="para365"></a><span class="paranum">365</span>, 399. Tapādito sī.</p>\r\n<p class="bodytext"><a name="para366"></a><span class="paranum">366</span>, 400. Daṇḍādito ika ī.</p>\r\n<p class="bodytext"><a name="para367"></a><span class="paranum">367</span>, 401. Madhvādito ro.</p>\r\n<p class="bodytext"><a name="para368"></a><span class="paranum">368</span>, 402. Guṇādito vantu.</p>\r\n<p class="bodytext"><a name="para369"></a><span class="paranum">369</span>, 403. Satyādīhi mantu.</p>\r\n<p class="bodytext"><a name="para370"></a><span class="paranum">370</span>, 405. Saddhādito ṇa.</p>\r\n<p class="bodytext"><a name="para371"></a><span class="paranum">371</span>, 404. Āyussukārāsa mantumhi.</p>\r\n<p class="bodytext"><a name="para372"></a><span class="paranum">372</span>, 385. </p>','\r',char(13)),'\n',char(10)),'-353-354-355-356-357-358-359-360-361-362-363-364-365-366-367-368-369-370-371-372-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',22,replace(replace('<p class="bodytext">Tappakativacane <a name="M0.0022"></a> mayo.</p>\r\n<p class="bodytext"><a name="para373"></a><span class="paranum">373</span>, 406. Saṅkhyāpūraṇe mo.</p>\r\n<p class="bodytext"><a name="para374"></a><span class="paranum">374</span>, 408. Sa chassa vā.</p>\r\n<p class="bodytext"><a name="para375"></a><span class="paranum">375</span>, 412. Ekādito dasassī.</p>\r\n<p class="bodytext"><a name="para376"></a><span class="paranum">376</span>, 257. Dase so niccañca.</p>\r\n<p class="bodytext"><a name="para377"></a><span class="paranum">377</span>, 0. Ante niggahitañca.</p>\r\n<p class="bodytext"><a name="para378"></a><span class="paranum">378</span>, 414. Ti ca.</p>\r\n<p class="bodytext"><a name="para379"></a><span class="paranum">379</span>, 258. La da rānaṃ.</p>\r\n<p class="bodytext"><a name="para380"></a><span class="paranum">380</span>, 255. Vīsati dasesu bā dvissa tu.</p>\r\n<p class="bodytext"><a name="para381"></a><span class="paranum">381</span>, 254. Ekādito dassa ra saṅkhyāne.</p>\r\n<p class="bodytext"><a name="para382"></a><span class="paranum">382</span>, 259. Aṭṭhādito ca.</p>\r\n<p class="bodytext"><a name="para383"></a><span class="paranum">383</span>, 353. Dvekaṭṭhānamākāro vā.</p>\r\n<p class="bodytext"><a name="para384"></a><span class="paranum">384</span>, 407. Catucche hi tha ṭhā.</p>\r\n<p class="bodytext"><a name="para385"></a><span class="paranum">385</span>, 409. Dvitīhi tiyo.</p>\r\n<p class="bodytext"><a name="para386"></a><span class="paranum">386</span>, 410. Tiye dutāpi ca.</p>\r\n<p class="bodytext"><a name="para387"></a><span class="paranum">387</span>, 411. Tesamaḍḍhūpapadena aḍḍhuḍḍhadivaḍḍha diyaḍḍha’ ḍḍhatiyā.</p>\r\n<p class="bodytext"><a name="para388"></a><span class="paranum">388</span>, 68. Sarūpāna mekasesvasakiṃ.</p>\r\n<p class="bodytext"><a name="para389"></a><span class="paranum">389</span>, 413. Gaṇanedasassa dvi ti catu pañca cha satta aṭṭhanavakānaṃ vī ti cattāra paññā cha sattāsanavā yosu yonañcīsamāsaṃṭhiritītuti.</p>\r\n<p class="bodytext"><a name="para390"></a><span class="paranum">390</span>, 256. </p>','\r',char(13)),'\n',char(10)),'-373-374-375-376-377-378-379-380-381-382-383-384-385-386-387-388-389-390-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',23,replace(replace('<p class="bodytext">Catūpapadassa <a name="M0.0023"></a> lopo tu’ttarapadādicassa cu copi navā.</p>\r\n<p class="bodytext"><a name="para391"></a><span class="paranum">391</span>, 423. Yadanupapannā nipātanā sijjhanti.</p>\r\n<p class="bodytext"><a name="para392"></a><span class="paranum">392</span>, 418. Dvādito ko’nekatthe ca.</p>\r\n<p class="bodytext"><a name="para393"></a><span class="paranum">393</span>, 415. Dasadasakaṃ sataṃ dasakānaṃ sataṃ sahassañca yomhi.</p>\r\n<p class="bodytext"><a name="para394"></a><span class="paranum">394</span>, 416. Yāva taduttari dasaguṇitañca.</p>\r\n<p class="bodytext"><a name="para395"></a><span class="paranum">395</span>, 417. Sakanāmehi.</p>\r\n<p class="bodytext"><a name="para396"></a><span class="paranum">396</span>, 363. Tesaṃ ṇo lopaṃ.</p>\r\n<p class="bodytext"><a name="para397"></a><span class="paranum">397</span>, 420. Vibhāge dhā ca.</p>\r\n<p class="bodytext"><a name="para398"></a><span class="paranum">398</span>, 421. Sabbanāmehi pakāravacane tu thā.</p>\r\n<p class="bodytext"><a name="para399"></a><span class="paranum">399</span>, 422. Kimimehi thaṃ.</p>\r\n<p class="bodytext"><a name="para400"></a><span class="paranum">400</span>, 364. Vuddhādisarassa vā’saṃyogantassa saṇe ca.</p>\r\n<p class="bodytext"><a name="para401"></a><span class="paranum">401</span>, 375. Māyūnamāgamo ṭhāne.</p>\r\n<p class="bodytext"><a name="para402"></a><span class="paranum">402</span>. 377. Āttañca.</p>\r\n<p class="bodytext"><a name="para403"></a><span class="paranum">403</span>, 354. Kvacādimajjhuttarānaṃ dīgharassa paccayesu ca.</p>\r\n<p class="bodytext"><a name="para404"></a><span class="paranum">404</span>, 370. Tesu vuddhi lopāgama vikāra viparitādesā ca.</p>\r\n<p class="bodytext"><a name="para405"></a><span class="paranum">405</span>, 365. Ayuvaṇṇānañcāyo vuddhi.</p>\r\n<p class="centered">Iti nāmakappe taddhitakappo aṭṭhamo kaṇḍo.</p>\r\n<p class="centered">Taddhitasuttaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">6. Ākhyāti</p>\r\n<p class="bodytext"><a name="para406"></a><span class="paranum">406</span>, 429. </p>','\r',char(13)),'\n',char(10)),'-391-392-393-394-395-396-397-398-399-400-401-402-403-404-405-406-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',24,replace(replace('<p class="bodytext">Atha <a name="M0.0024"></a> pubbāni-vibhattīnaṃ cha parassapadāni.</p>\r\n<p class="bodytext"><a name="para407"></a><span class="paranum">407</span>, 439. Parāṇuttanopadāni.</p>\r\n<p class="bodytext"><a name="para408"></a><span class="paranum">408</span>, 430. Dve dve paṭhamamajjhimuttamapurisā.</p>\r\n<p class="bodytext"><a name="para409"></a><span class="paranum">409</span>, 441. Sabbesamekābhidhāne paro puriso.</p>\r\n<p class="bodytext"><a name="para410"></a><span class="paranum">410</span>, 432. Nāmamhi payujjamānepi tulyādhikaraṇe paṭhamo.</p>\r\n<p class="bodytext"><a name="para411"></a><span class="paranum">411</span>, 436. Tumhe majjhimo.</p>\r\n<p class="bodytext"><a name="para412"></a><span class="paranum">412</span>, 437. Amhe uttamo.</p>\r\n<p class="bodytext"><a name="para413"></a><span class="paranum">413</span>, 426. Kāle.</p>\r\n<p class="bodytext"><a name="para414"></a><span class="paranum">414</span>, 428. Vattamānā paccuppanne.</p>\r\n<p class="bodytext"><a name="para415"></a><span class="paranum">415</span>, 451. Āṇātyāsiṭṭhe’nuttakāle pañcamī.</p>\r\n<p class="bodytext"><a name="para416"></a><span class="paranum">416</span>, 454. Anumatiparikappetthesu sattamī.</p>\r\n<p class="bodytext"><a name="para417"></a><span class="paranum">417</span>, 460. Apaccakkhe parokkhā’tīte.</p>\r\n<p class="bodytext"><a name="para418"></a><span class="paranum">418</span>, 456. Hiyyopabhuti paccakkhe hiyyattanī.</p>\r\n<p class="bodytext"><a name="para419"></a><span class="paranum">419</span>, 469. Samīpe’jjatanī.</p>\r\n<p class="bodytext"><a name="para420"></a><span class="paranum">420</span>, 471. Māyoge sabbakāle ca.</p>\r\n<p class="bodytext"><a name="para421"></a><span class="paranum">421</span>, 473. Anāgate bhavissantī.</p>\r\n<p class="bodytext"><a name="para422"></a><span class="paranum">422</span>, 475. Kriyātipanne’tīte kālātipatti.</p>\r\n<p class="bodytext"><a name="para423"></a><span class="paranum">423</span>, 426. Vattamānā tī anti, si tha, mi ma te ante, sevhe, e mhe.</p>\r\n<p class="bodytext"><a name="para424"></a><span class="paranum">424</span>, 450. </p>','\r',char(13)),'\n',char(10)),'-407-408-409-410-411-412-413-414-415-416-417-418-419-420-421-422-423-424-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',25,replace(replace('<p class="bodytext">Pañcamī <a name="M0.0025"></a> tu antu, hi tha, mi ma, taṃ antaṃ, ssu vho, e āmase.</p>\r\n<p class="bodytext"><a name="para425"></a><span class="paranum">425</span>, 453. Sattamī eyya eyyuṃ, eyyāsi eyyātha, eyyāmi eyyāma, etha eraṃ, etho eyyāvho, eyyaṃ eyyāmhe.</p>\r\n<p class="bodytext"><a name="para426"></a><span class="paranum">426</span>, 459. Parokkhā a u, e ttha, aṃ mha, ttha re, ttho vho, iṃ mhe.</p>\r\n<p class="bodytext"><a name="para427"></a><span class="paranum">427</span>, 455. Hiyyattanī ā ū, o ttha, aṃ mhā, ttha tthuṃ, sevhaṃ, iṃ mhase.</p>\r\n<p class="bodytext"><a name="para428"></a><span class="paranum">428</span>, 468. Ajjatanī īñaṃ ottha, iṃ mhā, ā ū, se vhaṃ, aṃ mhe.</p>\r\n<p class="bodytext"><a name="para429"></a><span class="paranum">429</span>, 472. Ssavissantī ssati ssanti, ssasi ssatha, ssāmi ssāma, ssate ssante, ssase ssavhe, ssaṃ ssāmhe.</p>\r\n<p class="bodytext"><a name="para430"></a><span class="paranum">430</span>, 474. Kālātipatti ssā ssaṃsu, sse ssatha, ssaṃ ssāmā, ssatha ssisu, ssase ssavhe, ssiṃ ssāmhase.</p>\r\n<p class="bodytext"><a name="para431"></a><span class="paranum">431</span>, 428. Hiyyattanī sattamī pañcamī vattamānā sabbadhātukaṃ.</p>\r\n<p class="centered">Iti ākhyātakappe paṭhamo kaṇḍo.</p>\r\n<p class="bodytext"><a name="para432"></a><span class="paranum">432</span>, 362. Dhātuliṅgehi parā paccayā.</p>\r\n<p class="bodytext"><a name="para433"></a><span class="paranum">433</span>, 528. Tijagupakitamānehi khachasā vā.</p>\r\n<p class="bodytext"><a name="para434"></a><span class="paranum">434</span>, 534. Bhujaghasaharasupādīhitumicchatthesu.</p>\r\n<p class="bodytext"><a name="para435"></a><span class="paranum">435</span>, 536. </p>','\r',char(13)),'\n',char(10)),'-425-426-427-428-429-430-431-432-433-434-435-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',26,replace(replace('<p class="bodytext">Āya <a name="M0.0026"></a> nāmato kattūpamānādācāre.</p>\r\n<p class="bodytext"><a name="para436"></a><span class="paranum">436</span>, 537. Īyū’pamānā ca.</p>\r\n<p class="bodytext"><a name="para437"></a><span class="paranum">437</span>, 538. Nāmamhā’tticchatthe.</p>\r\n<p class="bodytext"><a name="para438"></a><span class="paranum">438</span>, 540. Dhātūhi ṇe ṇaya ṇāpe ṇāpayā kāritāni hetvatthe.</p>\r\n<p class="bodytext"><a name="para439"></a><span class="paranum">439</span>, 539. Dhāturūpe nāmasmā ṇa yo ca.</p>\r\n<p class="bodytext"><a name="para440"></a><span class="paranum">440</span>, 445. Bhāvakammesu yo.</p>\r\n<p class="bodytext"><a name="para441"></a><span class="paranum">441</span>, 447. Tassa cavaggayakāravakārattaṃ sadhātvantassa.</p>\r\n<p class="bodytext"><a name="para442"></a><span class="paranum">442</span>, 448. Ivaṇṇāgamo vā.</p>\r\n<p class="bodytext"><a name="para443"></a><span class="paranum">443</span>, 449. Pubbarūpañca.</p>\r\n<p class="bodytext"><a name="para444"></a><span class="paranum">444</span>, 511. Tathā kattari ca.</p>\r\n<p class="bodytext"><a name="para445"></a><span class="paranum">445</span>, 433. Bhūvādito a.</p>\r\n<p class="bodytext"><a name="para446"></a><span class="paranum">446</span>, 509. Rudhādito niggahitapubbañca.</p>\r\n<p class="bodytext"><a name="para447"></a><span class="paranum">447</span>, 510. Divādito yo.</p>\r\n<p class="bodytext"><a name="para448"></a><span class="paranum">448</span>, 512. Svādito ṇuṇā uṇā ca.</p>\r\n<p class="bodytext"><a name="para449"></a><span class="paranum">449</span>, 513. Kiyādito nā.</p>\r\n<p class="bodytext"><a name="para450"></a><span class="paranum">450</span>, 517. Gahādito ppaṇhā.</p>\r\n<p class="bodytext"><a name="para451"></a><span class="paranum">451</span>, 520. Tanādito oyirā.</p>\r\n<p class="bodytext"><a name="para452"></a><span class="paranum">452</span>, 525. Curādito ṇe ṇayā.</p>\r\n<p class="bodytext"><a name="para453"></a><span class="paranum">453</span>, 444. Attanopadāni bhāve ca kammani.</p>\r\n<p class="bodytext"><a name="para454"></a><span class="paranum">454</span>, 440. Kattari ca.</p>\r\n<p class="bodytext"><a name="para455"></a><span class="paranum">455</span>, 530. </p>','\r',char(13)),'\n',char(10)),'-436-437-438-439-440-441-442-443-444-445-446-447-448-449-450-451-452-453-454-455-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',27,replace(replace('<p class="bodytext">Dhātuppaccayehi <a name="M0.0027"></a> vibhattiyo.</p>\r\n<p class="bodytext"><a name="para456"></a><span class="paranum">456</span>, 420. Kattari parassapadaṃ.</p>\r\n<p class="bodytext"><a name="para457"></a><span class="paranum">457</span>, 424. Bhūvādayo dhātavo.</p>\r\n<p class="centered">Iti ākhyātakappe dutiyo kaṇḍo.</p>\r\n<p class="bodytext"><a name="para458"></a><span class="paranum">458</span>, 461. Kvacādivaṇṇānamekassarānaṃ dvebhāvo.</p>\r\n<p class="bodytext"><a name="para459"></a><span class="paranum">459</span>, 462. Pubbo’ bbhāso.</p>\r\n<p class="bodytext"><a name="para460"></a><span class="paranum">460</span>, 506. Rasso.</p>\r\n<p class="bodytext"><a name="para461"></a><span class="paranum">461</span>, 464. Dutiyacatutthānaṃ paṭhamatatiyā.</p>\r\n<p class="bodytext"><a name="para462"></a><span class="paranum">462</span>, 467. Kavaggassa cavaggo.</p>\r\n<p class="bodytext"><a name="para463"></a><span class="paranum">463</span>, 532. Mānakitānaṃ va tattaṃ vā.</p>\r\n<p class="bodytext"><a name="para464"></a><span class="paranum">464</span>, 504. Hassa jo.</p>\r\n<p class="bodytext"><a name="para465"></a><span class="paranum">465</span>, 463. Antassivaṇṇākāro vā.</p>\r\n<p class="bodytext"><a name="para466"></a><span class="paranum">466</span>, 489. Niggahitañca.</p>\r\n<p class="bodytext"><a name="para467"></a><span class="paranum">467</span>, 533. Tato pāmānānaṃ vāmaṃ sesu.</p>\r\n<p class="bodytext"><a name="para468"></a><span class="paranum">468</span>, 492. Ṭhā tiṭṭho.</p>\r\n<p class="bodytext"><a name="para469"></a><span class="paranum">469</span>, 494. Pā pivo.</p>\r\n<p class="bodytext"><a name="para470"></a><span class="paranum">470</span>, 514. Ñāssa jājaṃnā.</p>\r\n<p class="bodytext"><a name="para471"></a><span class="paranum">471</span>, 483. Disassa passa dissa dakkhā vā.</p>\r\n<p class="bodytext"><a name="para472"></a><span class="paranum">472</span>, 531. Byañjanantassa co chappaccayesu ca.</p>\r\n<p class="bodytext"><a name="para473"></a><span class="paranum">473</span>, 529. Ko khe ca.</p>\r\n<p class="bodytext"><a name="para474"></a><span class="paranum">474</span>, 535. Harassa gī se.</p>\r\n<p class="bodytext"><a name="para475"></a><span class="paranum">475</span>, 465. </p>','\r',char(13)),'\n',char(10)),'-456-457-458-459-460-461-462-463-464-465-466-467-468-469-470-471-472-473-474-475-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',28,replace(replace('<p class="bodytext">Brūbhūnamāhabhūvā <a name="M0.0028"></a> parokkhāyaṃ.</p>\r\n<p class="bodytext"><a name="para476"></a><span class="paranum">476</span>, 442. Gamissanto ccho vā sabbāsu.</p>\r\n<p class="bodytext"><a name="para477"></a><span class="paranum">477</span>, 479. Vacassa’jjatanimhi makārā o.</p>\r\n<p class="bodytext"><a name="para478"></a><span class="paranum">478</span>, 438. Akāro dīghaṃ himimesu.</p>\r\n<p class="bodytext"><a name="para479"></a><span class="paranum">479</span>, 452. Hi lopaṃ vā.</p>\r\n<p class="bodytext"><a name="para480"></a><span class="paranum">480</span>, 490. Hotissare’ ho’he bhavissantimhi ssassa ca.</p>\r\n<p class="bodytext"><a name="para481"></a><span class="paranum">481</span>, 524. Karassa sappaccayassa kāho.</p>\r\n<p class="centered">Iti ākhyātakappe tatiyo kaṇḍo.</p>\r\n<p class="bodytext"><a name="para482"></a><span class="paranum">482</span>, 508. Dādantassaṃ mimesu.</p>\r\n<p class="bodytext"><a name="para483"></a><span class="paranum">483</span>, 527. Asaṃyogantassa vuddhi kārite.</p>\r\n<p class="bodytext"><a name="para484"></a><span class="paranum">484</span>, 542. Ghaṭādīnaṃ vā.</p>\r\n<p class="bodytext"><a name="para485"></a><span class="paranum">485</span>, 434. Aññesu ca.</p>\r\n<p class="bodytext"><a name="para486"></a><span class="paranum">486</span>, 543. Guhadusānaṃ dīghaṃ.</p>\r\n<p class="bodytext"><a name="para487"></a><span class="paranum">487</span>, 478. Vaca vasa vahādīnamukāro vassaye.</p>\r\n<p class="bodytext"><a name="para488"></a><span class="paranum">488</span>, 481. Ha vipariyayo lo vā.</p>\r\n<p class="bodytext"><a name="para489"></a><span class="paranum">489</span>, 519. Gahassa ghe ppe.</p>\r\n<p class="bodytext"><a name="para490"></a><span class="paranum">490</span>, 518. Halopo ṇhāmhi.</p>\r\n<p class="bodytext"><a name="para491"></a><span class="paranum">491</span>, 