INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',29,replace(replace('<p class="bodytext">Tissa <a name="M0.0029"></a> tissa tthitthaṃ.</p>\r\n<p class="bodytext"><a name="para495"></a><span class="paranum">495</span>, 500. Tussa tthuttaṃ.</p>\r\n<p class="bodytext"><a name="para496"></a><span class="paranum">496</span>, 497. Simhi ca.</p>\r\n<p class="bodytext"><a name="para497"></a><span class="paranum">497</span>, 477. Labhasmā īaṃnaṃ tthattaṃ.</p>\r\n<p class="bodytext"><a name="para498"></a><span class="paranum">498</span>, 480. Kusasmā dī cchi.</p>\r\n<p class="bodytext"><a name="para499"></a><span class="paranum">499</span>, 480. Dā dhātussa dajjaṃ.</p>\r\n<p class="bodytext"><a name="para500"></a><span class="paranum">500</span>, 486. Vadassa vajjaṃ.</p>\r\n<p class="bodytext"><a name="para501"></a><span class="paranum">501</span>, 443. Gamissa ghammaṃ.</p>\r\n<p class="bodytext"><a name="para502"></a><span class="paranum">502</span>, 493. Yamhi dā dhā mā ṭhā hā pā maha mathādīnamī.</p>\r\n<p class="bodytext"><a name="para503"></a><span class="paranum">503</span>, 485. Yajassādissi.</p>\r\n<p class="bodytext"><a name="para504"></a><span class="paranum">504</span>, 470. Sabbato uṃ iṃsu.</p>\r\n<p class="bodytext"><a name="para505"></a><span class="paranum">505</span>, 482. Jaramarānaṃ jīra jīyya mīyyā vā.</p>\r\n<p class="bodytext"><a name="para506"></a><span class="paranum">506</span>, 496. Sabbatthā’sassādilopo ca.</p>\r\n<p class="bodytext"><a name="para507"></a><span class="paranum">507</span>, 501. Asabbadhātuke ñā.</p>\r\n<p class="bodytext"><a name="para508"></a><span class="paranum">508</span>, 515. Yeyyassa ñāto iyā ñā.</p>\r\n<p class="bodytext"><a name="para509"></a><span class="paranum">509</span>, 516. Nāssa lopo yakārattaṃ.</p>\r\n<p class="bodytext"><a name="para510"></a><span class="paranum">510</span>, 487. Lopañcettamakāro.</p>\r\n<p class="bodytext"><a name="para511"></a><span class="paranum">511</span>, 521. Uttamokāro.</p>\r\n<p class="bodytext"><a name="para512"></a><span class="paranum">512</span>, 522. Karassākāro ca.</p>\r\n<p class="bodytext"><a name="para513"></a><span class="paranum">513</span>, 435. O ava sare.</p>\r\n<p class="bodytext"><a name="para514"></a><span class="paranum">514</span>, 491. E aya.</p>\r\n<p class="bodytext"><a name="para515"></a><span class="paranum">515</span>, 541. </p>','\r',char(13)),'\n',char(10)),'-495-496-497-498-499-500-501-502-503-504-505-506-507-508-509-510-511-512-513-514-515-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',30,replace(replace('<p class="bodytext">Te <a name="M0.0030"></a> āvāyā kārite.</p>\r\n<p class="bodytext"><a name="para516"></a><span class="paranum">516</span>, 466. Ikārāgamo asabbadhātukamhi.</p>\r\n<p class="bodytext"><a name="para517"></a><span class="paranum">517</span>, 488. Kvaci dhātuvibhattippaccayānaṃ dīghaviparītādesa lopāgamā ca.</p>\r\n<p class="bodytext"><a name="para518"></a><span class="paranum">518</span>, 446. Attanopadāni parassapadattaṃ.</p>\r\n<p class="bodytext"><a name="para519"></a><span class="paranum">519</span>, 457. Akārāgamo hiyyatthanīajjatanīkālātipattīsu.</p>\r\n<p class="bodytext"><a name="para520"></a><span class="paranum">520</span>, 502. Brūto ī timhi.</p>\r\n<p class="bodytext"><a name="para521"></a><span class="paranum">521</span>, 425. Dhātussanto lopo’ nekasarassa.</p>\r\n<p class="bodytext"><a name="para522"></a><span class="paranum">522</span>, 476. Isuyamūnamanto ccho vā.</p>\r\n<p class="bodytext"><a name="para523"></a><span class="paranum">523</span>, 526. Kāritānaṃ ṇo lopaṃ.</p>\r\n<p class="centered">Iti ākhyātakappe catuttho kaṇḍo.</p>\r\n<p class="centered">Ākhyātasuttaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">7. Kibbidhāna</p>\r\n<p class="bodytext"><a name="para524"></a><span class="paranum">524</span>, 561. Dhātuyā kammādimhi ṇo.</p>\r\n<p class="bodytext"><a name="para525"></a><span class="paranum">525</span>, 565. Saññāyama nu.</p>\r\n<p class="bodytext"><a name="para526"></a><span class="paranum">526</span>, 567. Pure dadā ca iṃ.</p>\r\n<p class="bodytext"><a name="para527"></a><span class="paranum">527</span>, 568. Sabbato ṇvutvā’vī vā.</p>\r\n<p class="bodytext"><a name="para528"></a><span class="paranum">528</span>, 577. Visa ruja padādito ṇa.</p>\r\n<p class="bodytext"><a name="para529"></a><span class="paranum">529</span>, 580. Bhāve ca.</p>\r\n<p class="bodytext"><a name="para530"></a><span class="paranum">530</span>, 584. Kvi ca.</p>\r\n<p class="bodytext"><a name="para531"></a><span class="paranum">531</span>, 589. </p>','\r',char(13)),'\n',char(10)),'-516-517-518-519-520-521-522-523-524-525-526-527-528-529-530-531-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',31,replace(replace('<p class="bodytext">Dharādīhi <a name="M0.0031"></a> rammo.</p>\r\n<p class="bodytext"><a name="para532"></a><span class="paranum">532</span>, 590. Tassīlādīsu ṇī tvā vī ca.</p>\r\n<p class="bodytext"><a name="para533"></a><span class="paranum">533</span>, 591. Sadda kudha cala maṇḍattha rucādīhi yu.</p>\r\n<p class="bodytext"><a name="para534"></a><span class="paranum">534</span>, 592. Pārādigamimhā rū.</p>\r\n<p class="bodytext"><a name="para535"></a><span class="paranum">535</span>, 593. Bhikkhādito ca.</p>\r\n<p class="bodytext"><a name="para536"></a><span class="paranum">536</span>, 594. Hanatyādīnaṃ ṇuko.</p>\r\n<p class="bodytext"><a name="para537"></a><span class="paranum">537</span>, 566. Nu niggahitaṃ padante.</p>\r\n<p class="bodytext"><a name="para538"></a><span class="paranum">538</span>, 595. Saṃhanā’ññāya vā ro gho.</p>\r\n<p class="bodytext"><a name="para539"></a><span class="paranum">539</span>, 558. Ramhi ranto rādino.</p>\r\n<p class="bodytext"><a name="para540"></a><span class="paranum">540</span>, 545. Bhāvakammesu tabbā’nīyā.</p>\r\n<p class="bodytext"><a name="para541"></a><span class="paranum">541</span>, 552. Ṇyo ca.</p>\r\n<p class="bodytext"><a name="para542"></a><span class="paranum">542</span>, 557. Karamhā ricca.</p>\r\n<p class="bodytext"><a name="para543"></a><span class="paranum">543</span>, 555. Bhūto’bba.</p>\r\n<p class="bodytext"><a name="para544"></a><span class="paranum">544</span>, 556. Vada mada gamu yuja garahākārādīhi jja mmaggayheyyāvāro vā.</p>\r\n<p class="bodytext"><a name="para545"></a><span class="paranum">545</span>, 548. Te kiccā.</p>\r\n<p class="bodytext"><a name="para546"></a><span class="paranum">546</span>, 562. Aññe kita.</p>\r\n<p class="bodytext"><a name="para547"></a><span class="paranum">547</span>, 596. Nandādīhi yu.</p>\r\n<p class="bodytext"><a name="para548"></a><span class="paranum">54