INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',32,replace(replace('<p class="bodytext">Ṇādayo <a name="M0.0032"></a> tekālikā.</p>\r\n<p class="bodytext"><a name="para551"></a><span class="paranum">551</span>, 598. Saññāyaṃ dādhāto i.</p>\r\n<p class="bodytext"><a name="para552"></a><span class="paranum">552</span>, 609. Ti kiccā’siṭṭhe.</p>\r\n<p class="bodytext"><a name="para553"></a><span class="paranum">553</span>, 599. Itthiyamatiyavo vā.</p>\r\n<p class="bodytext"><a name="para554"></a><span class="paranum">554</span>, 601. Karato ririya.</p>\r\n<p class="bodytext"><a name="para555"></a><span class="paranum">555</span>, 612. Atīte ta tavantu tāvī.</p>\r\n<p class="bodytext"><a name="para556"></a><span class="paranum">556</span>, 622. Bhāvakammesu ta.</p>\r\n<p class="bodytext"><a name="para557"></a><span class="paranum">557</span>, 606. Budhagamāditthe kattari.</p>\r\n<p class="bodytext"><a name="para558"></a><span class="paranum">558</span>, 602. Jito ina sabbattha.</p>\r\n<p class="bodytext"><a name="para559"></a><span class="paranum">559</span>, 603. Supato ca.</p>\r\n<p class="bodytext"><a name="para560"></a><span class="paranum">560</span>, 604. Īsaṃdusūhi kha.</p>\r\n<p class="bodytext"><a name="para561"></a><span class="paranum">561</span>, 636. Icchatthesu samānakattukesu tave tuṃ vā.</p>\r\n<p class="bodytext"><a name="para562"></a><span class="paranum">562</span>, 638. Arahasakkādīsu ca.</p>\r\n<p class="bodytext"><a name="para563"></a><span class="paranum">563</span>, 639. Pattavacane alamatthesu ca.</p>\r\n<p class="bodytext"><a name="para564"></a><span class="paranum">564</span>, 640. Pubbakāle’kakattukānaṃ tuna tvāna tvā vā.</p>\r\n<p class="bodytext"><a name="para565"></a><span class="paranum">565</span>, 646. Vattamāne māna’ntā.</p>\r\n<p class="bodytext"><a name="para566"></a><span class="paranum">566</span>, 574. Sāsādīhi ratthu.</p>\r\n<p class="bodytext"><a name="para567"></a><span class="p