INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',33,replace(replace('<p class="bodytext">Mānādīhi <a name="M0.0033"></a> rātu.</p>\r\n<p class="bodytext"><a name="para569"></a><span class="paranum">569</span>, 610. Āgamā tuko.</p>\r\n<p class="bodytext"><a name="para570"></a><span class="paranum">570</span>, 611. Bhabbe ika.</p>\r\n<p class="centered">Iti kibbidhānakappe dutiyo kaṇḍo.</p>\r\n<p class="bodytext"><a name="para571"></a><span class="paranum">571</span>, 624. Paccayādaniṭṭhā nipātanā sijjhanti.</p>\r\n<p class="bodytext"><a name="para572"></a><span class="paranum">572</span>, 625. Sāsadisato tassa riṭṭho ca.</p>\r\n<p class="bodytext"><a name="para573"></a><span class="paranum">573</span>, 626. Sādisanta puccha bhanja hantādīhi ṭṭho.</p>\r\n<p class="bodytext"><a name="para574"></a><span class="paranum">574</span>, 613. Vasato uttha.</p>\r\n<p class="bodytext"><a name="para575"></a><span class="paranum">575</span>, 614. Vasa vāvu.</p>\r\n<p class="bodytext"><a name="para576"></a><span class="paranum">576</span>, 607. Dha ḍha bha he hi dhaḍḍhā ca.</p>\r\n<p class="bodytext"><a name="para577"></a><span class="paranum">577</span>, 628. Bhanjato ggo ca.</p>\r\n<p class="bodytext"><a name="para578"></a><span class="paranum">578</span>, 560. Bhujādīnamanto no dvi ca.</p>\r\n<p class="bodytext"><a name="para579"></a><span class="paranum">579</span>, 629. Vaca vā vu.</p>\r\n<p class="bodytext"><a name="para580"></a><span class="paranum">580</span>, 630. Gupādīnañca.</p>\r\n<p class="bodytext"><a name="para581"></a><span class="paranum">581</span>, 616. Tarādīhi iṇṇo.</p>\r\n<p class="bodytext"><a name="para582"></a><span class="paranum">582</span>, 631. Bhidādito inna anna īṇā vā.</p>\r\n<p class="bodytext"><a name="para583"></a><span class="paranum">583</span>, 617. Susa paca sakato kkha kkā ca.</p>\r\n<p class="bodytext"><a name="para584"></a><span class="paranum">584</span>, 618. Pakkamādīhi nto ca.</p>\r\n<p c