INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',34,replace(replace('<p class="bodytext"><a name="para588"></a><span class="paranum">588</span>, Ṭhāpānamiī <a name="M0.0034"></a> ca.</p>\r\n<p class="bodytext"><a name="para589"></a><span class="paranum">589</span>, 621. Hantehi ho hassa ḷo vā adahanahānaṃ.</p>\r\n<p class="centered">Iti kibbidhānakappe tatiyo kaṇḍo.</p>\r\n<p class="bodytext"><a name="para590"></a><span class="paranum">590</span>, 579. Ṇamhi ranjassa jo bhāvakaraṇesu.</p>\r\n<p class="bodytext"><a name="para591"></a><span class="paranum">591</span>, 544. Hanassa ghāto.</p>\r\n<p class="bodytext"><a name="para592"></a><span class="paranum">592</span>, 503. Vadho vā sabbattha.</p>\r\n<p class="bodytext"><a name="para593"></a><span class="paranum">593</span>, 564. Ākārantānamāyo.</p>\r\n<p class="bodytext"><a name="para594"></a><span class="paranum">594</span>, 582. Purasamupaparīhi karotissa kha kharā vā tappaccayesu ca.</p>\r\n<p class="bodytext"><a name="para595"></a><span class="paranum">595</span>, 637. Tavetunādīsu kā.</p>\r\n<p class="bodytext"><a name="para596"></a><span class="paranum">596</span>, 551. Gamakhanahanādīnaṃ tuṃtabbādīsu na.</p>\r\n<p class="bodytext"><a name="para597"></a><span class="paranum">597</span>, 641. Sabbehi tunādīnaṃ yo.</p>\r\n<p class="bodytext"><a name="para598"></a><span class="paranum">598</span>, 643. Canantehi raccaṃ.</p>\r\n<p class="bodytext"><a name="para599"></a><span class="paranum">599</span>, 644. Disā svānasvantalopo ca.</p>\r\n<p class="bodytext"><a name="para600"></a><span class="paranum">600</span>, 645. Mahadabhehi mma yha jja bbha ddhā ca.</p>\r\n<p class="bodytext"><a name="para601"></a><span class="paranum">601</span>, 334. Taddhitasamāsakitakā nāmaṃ vā’tavetunādīsu ca.</p>\r\n<p class="bodytext"><a name="para602"></a><span class="paranum">602</span>, 6. Dumhi garu.</p>\r\n<p class="bodytext"><a name="para603"></a><span class="paranum">603</span>, 7. </p>','\r',char(13)),'\n',char(10)),'-588-589-590-591-592-593-594-595-596-597-598-599-600-601-602-603-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',35,replace(replace('<p class="bodytext">Dīgho <a name="M0.0035"></a> ca.</p>\r\n<p class="bodytext"><a name="para604"></a><span class="paranum">604</span>, 684. Akkharehi kāra.</p>\r\n<p class="bodytext"><a name="para605"></a><span class="paranum">605</span>, 547. Yathāgamamikāro.</p>\r\n<p class="bodytext"><a name="para606"></a><span class="paranum">606</span>, 642. Dadhantato yo kvaci.</p>\r\n<p class="centered">Iti kibbidhānakappe catuttho kaṇḍo.</p>\r\n<p class="bodytext"><a name="para607"></a><span class="paranum">607</span>, 578. Niggahita saṃyogādi no.</p>\r\n<p class="bodytext"><a name="para608"></a><span class="paranum">608</span>, 623. Sabbattha ge hī.</p>\r\n<p class="bodytext"><a name="para609"></a><span class="paranum">609</span>, 484. Sadassa sīdatthaṃ.</p>\r\n<p class="bodytext"><a name="para610"></a><span class="paranum">610</span>, 627. Yajassa sarassi ṭṭhe.</p>\r\n<p class="bodytext"><a name="para611"></a><span class="paranum">611</span>, 608. Hacatutthānamantānaṃ do dhe.</p>\r\n<p class="bodytext"><a name="para612"></a><span class="paranum">612</span>, 615. Ḍho ḍhakāre.</p>\r\n<p class="bodytext"><a name="para613"></a><span class="paranum">613</span>, 583. Gahassa ghara ṇe vā.</p>\r\n<p class="bodytext"><a name="para614"></a><span class="paranum">614</span>, 581. Dahassa do ḷaṃ.</p>\r\n<p class="bodytext"><a name="para615"></a><span class="paranum">615</span>, 586. Dhātvantassa lopo kvimhi.</p>\r\n<p class="bodytext"><a name="para616"></a><span class="paranum">616</span>, 587. Vidante ū.</p>\r\n<p class="bodytext"><a name="para617"></a><span class="paranum">617</span>, 633. Na ma ka rānamantānaṃ niyuttatamhi.</p>\r\n<p class="bodytext"><a name="para618"></a><span class="paranum">618</span>, 571. Na ka vatthaṃ ca jā ṇvumhi.</p>\r\n<p class="bodytext"><a name="para619"></a><span class="paranum">619</span>, 573. Karassa ca tattaṃ thusmiṃ.</p>\r\n<p class="bodytext"><a name="para620"></a><span class="paranum">620</span>, 549. Tuṃtunatabbesu vā.</p>\r\n<p class="bodytext"><a name="para621"></a><span class="paranum">621</span>, 553. </p>','\r',char(13)),'\n',char(10)),'-604-605-606-607-608-609-610-611-612-613-614-615-616-617-618-619-620-621-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',36,replace(replace('<p class="bodytext">Kāritaṃ <a name="M0.0036"></a> viya ṇānubandho.</p>\r\n<p class="bodytext"><a name="para622"></a><span class="paranum">622</span>, 570. Anakā yu ṇvūnaṃ.</p>\r\n<p class="bodytext"><a name="para623"></a><span class="paranum">623</span>, 554. Ka gā ca jānaṃ.</p>\r\n<p class="centered">Iti kibbidhānakappe pañcamo kaṇḍo.</p>\r\n<p class="centered">Kibbidhānasuttaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">8. Uṇādikappa</p>\r\n<p class="bodytext"><a name="para624"></a><span class="paranum">624</span>, 563. Kattari kitta.</p>\r\n<p class="bodytext"><a name="para625"></a><span class="paranum">625</span>, 605. Bhāvakammesu kiccattakkhatthā.</p>\r\n<p class="bodytext"><a name="para626"></a><span class="paranum">626</span>, 634. Kammani dutiyāya tto.</p>\r\n<p class="bodytext"><a name="para627"></a><span class="paranum">627</span>, 652. Khyādīhi mana ma ca to vā.</p>\r\n<p class="bodytext"><a name="para628"></a><span class="paranum">628</span>, 653. Samādīhi thamā.</p>\r\n<p class="bodytext"><a name="para629"></a><span class="paranum">629</span>, 569. Gahassu’padhasse vā.</p>\r\n<p class="bodytext"><a name="para630"></a><span class="paranum">630</span>, 654. Masussa sussa ccharaccherā.</p>\r\n<p class="bodytext"><a name="para631"></a><span class="paranum">631</span>, 655. Āpubbacarassa ca.</p>\r\n<p class="bodytext"><a name="para632"></a><span class="paranum">632</span>, 656. Ala kala salehi la yā.</p>\r\n<p class="bodytext"><a name="para633"></a><span class="paranum">633</span>, 657. Yāṇa lāṇā.</p>\r\n<p class="bodytext"><a name="para634"></a><span class="paranum">634</span>, 658. Mathissa thassa lo ca.</p>\r\n<p class="bodytext"><a name="para635"></a><span class="paranum">635</span>, 559. Pesātisaggapattakālesu kiccā.</p>\r\n<p class="bodytext"><a name="para636"></a><span class="paranum">636</span>, 659. Avassakādhamiṇesu ṇī ca.</p>\r\n<p class="bodytext"><a name="para637"></a><span class="paranum">637</span>, 0. Arahasakkādīhi tuṃ.</p>\r\n<p class="bodytext"><a name="para638"></a><span class="paranum">638</span>, 668. Vajādīhi pabbajjādayo nippajjante.</p>\r\n<p class="bodytext"><a name="para639"></a><span class="paranum">639</span>, 585. </p>','\r',char(13)),'\n',char(10)),'-622-623-624-625-626-627-628-629-630-631-632-633-634-635-636-637-638-639-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',37,replace(replace('<p class="bodytext">Kvilopo <a name="M0.0037"></a> ca.</p>\r\n<p class="bodytext"><a name="para640"></a><span class="paranum">640</span>, 0. Sacajānaṃ ka gā ṇānubandhe.</p>\r\n<p class="bodytext"><a name="para641"></a><span class="paranum">641</span>, 572. Nudādīhi yu ṇvūna manā na nā kā na na kā sakāritehi ca.</p>\r\n<p class="bodytext"><a name="para642"></a><span class="paranum">642</span>, 588. I ya ta ma kiṃ esānamantassaro dīghaṃ kvaci dusassa guṇaṃ doraṃ sa kkhī ca.</p>\r\n<p class="bodytext"><a name="para643"></a><span class="paranum">643</span>, 635. Bhyādīhi mati budhi pūjādīhi ca tto.</p>\r\n<p class="bodytext"><a name="para644"></a><span class="paranum">644</span>, 661. Vepu sī dava vamu ku dā bhūtvādīhi thuttima ṇimā nibbatte.</p>\r\n<p class="bodytext"><a name="para645"></a><span class="paranum">645</span>, 662. Akkose namhāni.</p>\r\n<p class="bodytext"><a name="para646"></a><span class="paranum">646</span>, 419. Ekādito sakissa kkhattuṃ.</p>\r\n<p class="bodytext"><a name="para647"></a><span class="paranum">647</span>, 663. Sunassunasso ṇa vānuvānūnanakhunānā.</p>\r\n<p class="bodytext"><a name="para648"></a><span class="paranum">648</span>, 664. Taruṇassa susu ca.</p>\r\n<p class="bodytext"><a name="para649"></a><span class="paranum">649</span>, 665. Yuvassuvassuvuvānanūnā.</p>\r\n<p class="bodytext"><a name="para650"></a><span class="paranum">650</span>, 651. Kāle vattamānātīte ṇvādayo.</p>\r\n<p class="bodytext"><a name="para651"></a><span class="paranum">651</span>, 647. Bhavissati gamādīhi ṇī ghiṇa.</p>\r\n<p class="bodytext"><a name="para652"></a><span class="paranum">652</span>, 648. Kriyāyaṃ ṇvu tavo.</p>\r\n<p class="bodytext"><a name="para653"></a><span class="paranum">653</span>, 307. Bhāvavācimhi catutthī.</p>\r\n<p class="bodytext"><a name="para654"></a><span class="paranum">654</span>, 649. Kammani ṇo.</p>\r\n<p class="bodytext"><a name="para655"></a><span class="paranum">655</span>, 650. Sese ssaṃ ntu mānānā.</p>\r\n<p class="bodytext"><a name="para656"></a><span class="paranum">656</span>, 666. Chadādīhi ta traṇa.</p>\r\n<p class="bodytext"><a name="para657"></a><span class="paranum">657</span>, 667. Vadādīhi ṇitto gaṇe.</p>\r\n<p class="bodytext"><a name="para658"></a><span class="paranum">658</span>, 668. Midādīhi tti tiyo.</p>\r\n<p class="bodytext"><a name="para659"></a><span class="paranum">659</span>, 669. </p>','\r',char(13)),'\n',char(10)),'-640-641-642-643-644-645-646-647-648-649-650-651-652-653-654-655-656-657-658-659-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',38,replace(replace('<p class="bodytext">Usuranjadasānaṃ <a name="M0.0038"></a> daṃsassa daḍḍho ḍhaṭhā ca.</p>\r\n<p class="bodytext"><a name="para660"></a><span class="paranum">660</span>, 670. Sūvusānamūvusānamato tho ca.</p>\r\n<p class="bodytext"><a name="para661"></a><span class="paranum">661</span>, 671. Ranjudādīhi dhadiddakirā kvaci jadalopo ca.</p>\r\n<p class="bodytext"><a name="para662"></a><span class="paranum">662</span>, 672. Paṭito hissa heraṇa hīraṇa.</p>\r\n<p class="bodytext"><a name="para663"></a><span class="paranum">663</span>, 673. Kaḍhādīhi ko.</p>\r\n<p class="bodytext"><a name="para664"></a><span class="paranum">664</span>, 674. Khādāmagamānaṃ khandhandhagandhā.</p>\r\n<p class="bodytext"><a name="para665"></a><span class="paranum">665</span>, 675. Paṭādīlyalaṃ.</p>\r\n<p class="bodytext"><a name="para666"></a><span class="paranum">666</span>, 676. Puthassa puthu pathā mo vā.</p>\r\n<p class="bodytext"><a name="para667"></a><span class="paranum">667</span>, 677. Sasvādīhi tu davo.</p>\r\n<p class="bodytext"><a name="para668"></a><span class="paranum">668</span>, 678. Cyādīhi īvaro.</p>\r\n<p class="bodytext"><a name="para669"></a><span class="paranum">669</span>, 679. Munādīhi ci.</p>\r\n<p class="bodytext"><a name="para670"></a><span class="paranum">670</span>, 680. Vidādīlyūro.</p>\r\n<p class="bodytext"><a name="para671"></a><span class="paranum">671</span>, 681. Hanādīhi nu ṇu tavo.</p>\r\n<p class="bodytext"><a name="para672"></a><span class="paranum">672</span>, 682. Kuṭādīhi ṭho.</p>\r\n<p class="bodytext"><a name="para673"></a><span class="paranum">673</span>, 683. Manupūrasuṇādīhi ussanusisā.</p>\r\n<p class="centered">Iti kibbidhānakappe uṇādikappo chaṭṭho kaṇḍo.</p>\r\n<p class="centered">Uṇādisuttaṃ niṭṭhitaṃ.</p>\r\n<p class="title">Mahākaccāyanasaddāpāṭha</p>\r\n<p class="chapter">1. Sandhikappa</p>\r\n<p class="title">Paṭhamakaṇḍa</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'-660-661-662-663-664-665-666-667-668-669-670-671-672-673-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',39,replace(replace('<p class="gatha1">Seṭṭhaṃ <a name="M0.0039"></a> tilokamahitaṃ abhivandiyaggaṃ,</p>\r\n<p class="gatha2">Buddhañca dhammamamalaṃ gaṇamuttamañca;</p>\r\n<p class="gatha3">Satthussa tassa vacanatthavaraṃ subuddhuṃ,</p>\r\n<p class="gathalast">Vakkhāmi suttahitamettha susandhikappaṃ.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Seyyaṃ jineritanayena budhā labhanti,</p>\r\n<p class="gatha2">Tañcāpi tassa vacanatthasubodhanena;</p>\r\n<p class="gatha3">Atthañca akkharapadesu amohabhāvā,</p>\r\n<p class="gathalast">Seyyatthiko padamato vividhaṃ suṇeyyaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>, 1. <span class="bld">Attho akkharasaññāto</span>.</p>\r\n<p class="bodytext">Sabbavacanānamattho akkhareheva saññāyate. Akkharavipattiyaṃ hi atthassa dunnayathā hoti, tasmā akkharakosallaṃ bahūpakāraṃ suttantesu.</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>, 2. </p>','\r',char(13)),'\n',char(10)),'-1-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',40,replace(replace('<p class="bodytext"><span class="bld">Akkharāpādayo</span> <a name="M0.0040"></a> <span class="bld">ekacattālīsaṃ</span>.</p>\r\n<p class="bodytext">Te ca kho akkharā api <span class="bld">a</span>kārādayo ekacattā līsa suttantesu sopakārā.</p>\r\n<p class="bodytext">Taṃ yathā? A ā i ī u ū e o, ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, ya ra la va sa ha ḷa aṃ, iti <span class="bld">akkharā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Attho akkharasaññāto.</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>, 3. <span class="bld">Tatthodantā sarā aṭṭha</span>.</p>\r\n<p class="bodytext">Tattha akkharesu <span class="bld">a</span>kārādīsu <span class="bld">o</span>dantā aṭṭha akkharā sarā nāma honti.</p>\r\n<p class="bodytext">Taṃ yathā? A ā i ī u ū e o, iti <span class="bld">sarā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Sarā sare lopaṃ.</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>, 4. <span class="bld">Lahumattā tayo rassā</span>.</p>\r\n<p class="bodytext">Tattha aṭṭhasu saresu lahumattā tayo sarā rassā nāma honti.</p>\r\n<p class="bodytext">Taṃ yathā? A i u, iti <span class="bld">rassā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Rassaṃ.</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>, 5. </p>','\r',char(13)),'\n',char(10)),'-3-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',41,replace(replace('<p class="bodytext"><span class="bld">Aññe</span> <a name="M0.0041"></a> <span class="bld">dīghā</span>.</p>\r\n<p class="bodytext">Tattha aṭṭhasu saresu rassehi aññe pañca sarā dīghā nāma honti.</p>\r\n<p class="bodytext">Taṃ yathā? Ā ī ū e o, iti <span class="bld">dīghā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Dīghaṃ.</p>\r\n<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>, 8. <span class="bld">Sesā byañjanā</span>.</p>\r\n<p class="bodytext">Ṭhapetvā aṭṭha sare sesā akkharā <span class="bld">ka</span>kārādayo <span class="bld">niggahita</span>ntā byañjanā nāma honti.</p>\r\n<p class="bodytext">Taṃ yathā? Ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, ya ra la va sa ha ḷa aṃ, iti <span class="bld">byañjanā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Sarā pakati byañjane.</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>, 9. <span class="bld">Vaggā pañcapañcaso mantā</span>.</p>\r\n<p class="bodytext">Tesaṃ kho byañjanānaṃ <span class="bld">ka</span>kārādayo makārantā pañcapañcaso akkharavanto vaggā nāma honti.</p>\r\n<p class="bodytext">Taṃ yathā? Ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, iti <span class="bld">vaggā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Vaggantaṃ vā vagge.</p>\r\n<p class="bodytext"><a name="para8"></a><span class="paranum">8</span>, 10. </p>','\r',char(13)),'\n',char(10)),'-6-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',42,replace(replace('<p class="bodytext"><span class="bld">Aṃ</span> <a name="M0.0042"></a> <span class="bld">iti niggahitaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Aṃ iti niggahitaṃ</span> nāma hoti.</p>\r\n<p class="bodytext">Tena kvattho? Aṃ byañjane niggahitaṃ.</p>\r\n<p class="bodytext"><a name="para9"></a><span class="paranum">9</span>, 11. <span class="bld">Parasamaññā payoge</span>.</p>\r\n<p class="bodytext">Yā ca pana paresu sakkataganthesu samaññā ghosāti vā aghosāti vā, tā payoge sati etthāpi yujjante.</p>\r\n<p class="bodytext"><span class="bld">Tattha</span> ghosā nāma-ga gha ṅa, ja jha ña, ḍa ḍha ṇa, da dha na, ba bha ma, ya ra la va ha ḷa, iti <span class="bld">ghosā</span> nāma. Aghosā nāma-ka kha, ca cha, ṭa ṭha, ta tha, pa pha, sa, iti <span class="bld">aghosā</span> nāma.</p>\r\n<p class="bodytext">Tena kvattho? Vagge ghosāghosānaṃ tatiyapaṭhamā.</p>\r\n<p class="bodytext"><a name="para10"></a><span class="paranum">10</span>, 12. <span class="bld">Pubbamadhoṭhita massaraṃ sarena viyojaye</span>.</p>\r\n<p class="bodytext">Tattha sandhiṃ kattukāmo pubbabyañjanaṃ adhoṭhitaṃ assaraṃ katvā sarañca upari katvā sarena viyojaye.</p>\r\n<p class="bodytext">Tatrāyamādi.</p>\r\n<p class="bodytext"><a name="para11"></a><span class="paranum">11</span>, 14. </p>','\r',char(13)),'\n',char(10)),'-9-10-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',43,replace(replace('<p class="bodytext"><span class="bld">Naye</span> <a name="M0.0043"></a> <span class="bld">paraṃ yutte</span>.</p>\r\n<p class="bodytext">Assaraṃ kho byañjanaṃ adhoṭhataṃ parakkharaṃ naye yutte. Tatrābhiratimiccheyya.</p>\r\n<p class="bodytext"><span class="bld">Yutte</span>tikasmā? Akkocchi maṃ avadhi maṃ, ajini maṃ ahāsi me. Ettha pana yuttaṃ na hoti.</p>\r\n<p class="centered">Iti sandhikappe paṭhamo kaṇḍo.</p>\r\n<p class="subhead">Dutiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para12"></a><span class="paranum">12</span>, 13. <span class="bld">Sarā sare lopaṃ</span>.</p>\r\n<p class="bodytext">Sarā kho sare pare lopaṃ papponti.</p>\r\n<p class="bodytext">Yassindriyāni samathaṅgatāni. No hetaṃ bhante sametāyasmā saṅghena.</p>\r\n<p class="bodytext"><a name="para13"></a><span class="paranum">13</span>, 15. <span class="bld">Vā paro asarūpā</span>.</p>\r\n<p class="bodytext">Saramhā asarūpā paro saro lopaṃ pappoti vā.</p>\r\n<p class="bodytext">Cattāro’me bhikkhave dhammā, kinnu’ māvasamaṇiyo. Vāti kasmā? Pañcindriyāni, tayassu dhammā jahitā bhavanti.</p>\r\n<p class="bodytext"><a name="para14"></a><span class="paranum">14</span>, 16. </p>','\r',char(13)),'\n',char(10)),'-12-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',44,replace(replace('<p class="bodytext"><span class="bld">Kvacāsacaṇṇaṃ</span> <a name="M0.0044"></a> <span class="bld">lutte</span>.</p>\r\n<p class="bodytext">Saro kho paro pubbasare lutte kvaci asavaṇṇaṃ pappoti.</p>\r\n<p class="bodytext">Saṅkhyaṃ nopeti vedagū, bandhusseva samāgamo.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Yassindriyāni, tathūpamaṃ dhammavaraṃ adesayi.</p>\r\n<p class="bodytext"><a name="para15"></a><span class="paranum">15</span>, 17. <span class="bld">Dīghaṃ</span>.</p>\r\n<p class="bodytext">Saro kho paro pubbasare lutte kvaci dīghaṃ pappoti. Saddhīdha vittaṃ purisassa seṭṭhaṃ, anāgārehi cūbhayaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Pañcahupāli aṅgehi samannāgato. Natthaññaṃ kiñci.</p>\r\n<p class="bodytext"><a name="para16"></a><span class="paranum">16</span>, 18. <span class="bld">Pubbo ca</span>.</p>\r\n<p class="bodytext">Pubbo ca saro parasaralope kate kvaci dīghaṃ pappoti.</p>\r\n<p class="bodytext">Kiṃsūdha vittaṃ purisassa seṭṭhaṃ, sādhūti paṭissuṇitvā, <span class="bld">kvacī</span>ti kasmā? Itissa muhuttampi.</p>\r\n<p class="bodytext"><a name="para17"></a><span class="paranum">17</span>, 19. </p>','\r',char(13)),'\n',char(10)),'-15-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',45,replace(replace('<p class="bodytext"><span class="bld">Yamedantassādeso</span><a name="M0.0045"></a>.</p>\r\n<p class="bodytext">Ekārassa antabhūtassa sare pare kvaci <span class="bld">ya</span>kārādeso hoti.</p>\r\n<p class="bodytext">Adhigato kho myāyaṃ dhammo, tyāhaṃ evaṃ vadeyyaṃ, tyāssa pahīnā honti.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Ne’nāgatā, iti nettha.</p>\r\n<p class="bodytext"><a name="para18"></a><span class="paranum">18</span>, 20. <span class="bld">Vamodudantānaṃ</span>.</p>\r\n<p class="bodytext">Okārukārānaṃ antabhūtānaṃ sare pare kvaci vakārādeso hoti.</p>\r\n<p class="bodytext">Atha khvassa, svassa hoti, bahvābādho, vatthvettha vihitaṃ niccaṃ cakkhāpāthamāgacchati.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>tikasmā? Cattāro’me bhikkhave dhammā, kinnumāva samaṇiyo.</p>\r\n<p class="bodytext"><a name="para19"></a><span class="paranum">19</span>, 22. <span class="bld">Sabbo caṃti</span>.</p>\r\n<p class="bodytext">Sabbo icceso <span class="bld">ti</span>saddo byañjano sare pare kvaci <span class="bld">ca</span>kāraṃ pappoti.</p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',46,replace(replace('<p class="bodytext">Iccetaṃ <a name="M0.0046"></a> kusalaṃ, iccassa vacanīyaṃ, paccuttaritvā, paccāharati.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Itissa muhuttampi.</p>\r\n<p class="bodytext"><a name="para20"></a><span class="paranum">20</span>, 27. <span class="bld">Do dhassa ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dha</span>iccetassa sare pare kvaci <span class="bld">da</span>kārādeso hoti.</p>\r\n<p class="bodytext">Ekamidāhaṃ bhikkhave samayaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Idheva maraṇaṃ bhavissati.</p>\r\n<p class="bodytext"><span class="bld">Vaggahaṇena dha</span>kārassa <span class="bld">ha</span>kārādeso hoti sāhu dassana mariyānaṃ.</p>\r\n<p class="bodytext">Suttavibhāgena bahudhā siyā-</p>\r\n<p class="bodytext"><span class="bld">To</span> dassa, yathā? Sugato.</p>\r\n<p class="bodytext"><span class="bld">Ṭo</span> tassa, yathā? Dukkaṭaṃ.</p>\r\n<p class="bodytext"><span class="bld">Dho</span> tassa, yathā? Gandhabbo.</p>\r\n<p class="bodytext"><span class="bld">Tro</span> ttassa, yathā? Atrajo.</p>\r\n<p class="bodytext"><span class="bld">Ko</span> gassa, yathā? Kulūpako.</p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',47,replace(replace('<p class="bodytext"><span class="bld">Lo</span> <a name="M0.0047"></a> rassa, yathā? Mahāsālo.</p>\r\n<p class="bodytext"><span class="bld">Jo</span> yassa, yathā? Gavajo.</p>\r\n<p class="bodytext"><span class="bld">Bbo</span> vassa, yathā? Kubbato.</p>\r\n<p class="bodytext"><span class="bld">Ko</span> yassa, yathā? Sake.</p>\r\n<p class="bodytext"><span class="bld">Yo</span> jassa, yathā? Niyaṃputtaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ko</span> tassa, yathā? Niyako.</p>\r\n<p class="bodytext"><span class="bld">Cco</span> ttassa, yathā bhacco.</p>\r\n<p class="bodytext"><span class="bld">Pho</span> passa, yathā? Nipphatti.</p>\r\n<p class="bodytext"><span class="bld">Kho</span> kassa, yathā? Nikkhamati. Iccevamādī yojetabbā.</p>\r\n<p class="bodytext"><a name="para21"></a><span class="paranum">21</span>, 21. <span class="bld">Ivaṇṇo yaṃ navā</span>.</p>\r\n<p class="bodytext">Pubbo ivaṇṇo sare pare <span class="bld">ya</span>kāraṃ pappoti navā. Paṭisunthāravutyassa, sabbā vityānubhūyate.</p>\r\n<p class="bodytext"><span class="bld">Navā</span>ti kasmā? Pañcahaṅgehi samannāgato, muttacāgī anuddhato.</p>\r\n<p class="bodytext"><a name="para22"></a><span class="paranum">22</span>, 28. <span class="bld">Evādissa ri pubbo ca rasso</span>.</p>\r\n<p class="bodytext">Saramhā parassa evassa ekārassa ādissa <span class="bld">ri</span>kāro hoti, pubbo ca saro rasso hoti navā.</p>','\r',char(13)),'\n',char(10)),'-21-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',48,replace(replace('<p class="bodytext">Yathariva <a name="M0.0048"></a> vasudhātalañca sabbaṃ, tathariva guṇavā supūjaniyo.</p>\r\n<p class="bodytext"><span class="bld">Navā</span>ti kasmā? Yathā eva, tathā eva.</p>\r\n<p class="centered">Iti sandhikappe dutiyo kaṇḍo.</p>\r\n<p class="subhead">Tatiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para23"></a><span class="paranum">23</span>, 26. <span class="bld">Sarā pakati byañjane</span>.</p>\r\n<p class="bodytext">Sarā kho byañjane pare pakatirūpāni honti.</p>\r\n<p class="bodytext">Manopubbaṅgamā dhammā, pamādo maccuno padaṃ, tiṇṇo pāraṅgato ahu.</p>\r\n<p class="bodytext"><a name="para24"></a><span class="paranum">24</span>, 35. <span class="bld">Sare kvaci</span>.</p>\r\n<p class="bodytext">Sarā kho sare pare kvaci pakatirūpāni honti.</p>\r\n<p class="bodytext">Ko imaṃ pathaviṃ vicessati.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Appassutāyaṃ puriso.</p>\r\n<p class="bodytext"><a name="para25"></a><span class="paranum">25</span>, 37. <span class="bld">Dīghaṃ</span>.</p>\r\n<p class="bodytext">Sarā kho byañjane pare kvaci dīghaṃ papponti.</p>','\r',char(13)),'\n',char(10)),'-23-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',49,replace(replace('<p class="bodytext">Sammā <a name="M0.0049"></a> dhammaṃ vipassato, evaṃ gāme munī care, khantī paramaṃ tapo titikkhā.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Idha modati pecca modati, patilīyati, paṭihaññati.</p>\r\n<p class="bodytext"><a name="para26"></a><span class="paranum">26</span>, 38. <span class="bld">Rassaṃ</span>.</p>\r\n<p class="bodytext">Sarā kho byañjane pare kvaci rassaṃ papponti.</p>\r\n<p class="bodytext">Bhovādināma so hoti, yathābhāvi guṇena so.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Sammāsamādhi, sāvittī chandaso mukhaṃ, upanīyati jīvitamappamāyu.</p>\r\n<p class="bodytext"><a name="para27"></a><span class="paranum">27</span>, 39. <span class="bld">Lopañca tatrākāro</span>.</p>\r\n<p class="bodytext">Sarā kho byañjane pare kvaci lopaṃ papponti. Tatra ca lope kate akārāgamo hoti.</p>\r\n<p class="bodytext">Sa sīlavā. Sa paññavā esa dhammo sanantano, sa ve kasāvamarahati, sa mānakāmopi bhaveyya, sa ve muni jātibhayaṃ adassi.</p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',50,replace(replace('<p class="bodytext"><span class="bld">Kvacī</span>ti <a name="M0.0050"></a> kasmā? So muni, eso dhammo padissati, na so kāsāvamarahati.</p>\r\n<p class="bodytext"><a name="para28"></a><span class="paranum">28</span>, 40. <span class="bld">Para dvebhāvo ṭhāne</span>.</p>\r\n<p class="bodytext">Saramhā parassa byañjanassa dvebhāvo hoti ṭhāne.</p>\r\n<p class="bodytext">Idhappamādo, purisassa jantuno, pabbajjaṃ kittayissāmi, cātuddasi, pañcaddasi, abhikkantataro cando.</p>\r\n<p class="bodytext"><span class="bld">Ṭhāne</span>ti kasmā? Idha modati pecca modati.</p>\r\n<p class="bodytext"><a name="para29"></a><span class="paranum">29</span>, 42. <span class="bld">Vagge ghosāghosānaṃ tatiyapaṭhamā</span>.</p>\r\n<p class="bodytext">Vagge kho pubbesaṃ byañjanānaṃ ghosāghosabhūtānaṃ saramhā yathāsaṅkhyaṃ tatiyapaṭhamakkharā dvebhāvaṃ gacchanti ṭhāne.</p>\r\n<p class="bodytext">Eseva cajjhānapphalo, yatraṭṭhitaṃ nappasaheyya maccu, sele yathā pabbatamuddhaniṭṭhito, cattāriṭṭhānāni naro pamatto.</p>\r\n<p class="bodytext"><span class="bld">Ṭhāne</span>ti kasmā? Idha cetaso daḷhaṃ gaṇhāti thāmasā.</p>\r\n<p class="centered">Iti sandhikappe tatiyo kaṇḍo.</p>\r\n<p class="subhead">Catutthakaṇḍa</p>\r\n<p class="bodytext"><a name="para30"></a><span class="paranum">30</span>, 58. </p>','\r',char(13)),'\n',char(10)),'-28-29-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',51,replace(replace('<p class="bodytext"><span class="bld">Aṃ</span> <a name="M0.0051"></a> <span class="bld">byañjane niggahitaṃ</span>.</p>\r\n<p class="bodytext">Niggahitaṃ kho byañjane pare aṃ iti hoti.</p>\r\n<p class="bodytext">Evaṃ vutte, taṃ sādhūti paṭissuṇitvā.</p>\r\n<p class="bodytext"><a name="para31"></a><span class="paranum">31</span>, 49. <span class="bld">Vaggantaṃ vā vagge</span>.</p>\r\n<p class="bodytext">Vaggabhūte byañjane pare niggahitaṃ kho vaggantaṃ vā pappoti.</p>\r\n<p class="bodytext">Tanniccutaṃ, dhammañcare sucaritaṃ, cirappavāsiṃ purisaṃ. Santantassa manaṃ hoti, taṅkāruṇikaṃ, evaṅkho bhikkhave sikkhitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vāggahaṇena</span>niggahitaṃ kho <span class="bld">la</span>kārādeso hoti. Puggalaṃ.</p>\r\n<p class="bodytext">Vāti kasmā? Na taṃ kammaṃ kataṃ sādhu.</p>\r\n<p class="bodytext"><a name="para32"></a><span class="paranum">32</span>, 50. <span class="bld">Ehe uṃ</span>.</p>\r\n<p class="bodytext">Ekārahakāre pare niggahitaṃ kho ñakāraṃ pappoti vā.</p>\r\n<p class="bodytext">Paccattaññeva parinibbāyissāmi, taññevettha paṭipucchissāmi, evañhi vo bhikkhave sikkhitabbaṃ. Tañhi tassa musā hoti.</p>','\r',char(13)),'\n',char(10)),'-31-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',52,replace(replace('<p class="bodytext"><span class="bld">Vā</span>ti <a name="M0.0052"></a> kasmā? Evametaṃ abhiññāya, evaṃ hoti subhāsitaṃ.</p>\r\n<p class="bodytext"><a name="para33"></a><span class="paranum">33</span>, 50. <span class="bld">Saye ca</span>.</p>\r\n<p class="bodytext">Niggahitaṃ kho <span class="bld">ya</span>kāre pare saha <span class="bld">ya</span>kārena <span class="bld">ña</span>kāraṃ pappoti vā.</p>\r\n<p class="bodytext">Saññogo, saññuttaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kasmā? Saṃyogo, saṃyuttaṃ.</p>\r\n<p class="bodytext"><a name="para34"></a><span class="paranum">34</span>, 52. <span class="bld">Madā sare</span>.</p>\r\n<p class="bodytext">Niggahitassa kho sare pare <span class="bld">ma</span>kāra<span class="bld">da</span>kārādesā honti vā.</p>\r\n<p class="bodytext">Tamahaṃ brūmi brāhmaṇaṃ, etadavoca satthā.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kasmā? Akkocchi maṃ avadhi maṃ, ajini maṃ ahāsi me.</p>\r\n<p class="bodytext"><a name="para35"></a><span class="paranum">35</span>, 34. <span class="bld">Ya va ma da na ta ra lā cāgamā</span>.</p>\r\n<p class="bodytext">Sare pare <span class="bld">ya</span>kāro <span class="bld">va</span>kāro <span class="bld">ma</span>kāro <span class="bld">da</span>kāro <span class="bld">na</span>kāro <span class="bld">ta</span>kāro <span class="bld">ra</span>kāro <span class="bld">la</span>kāro ime āgamā honti vā.</p>\r\n<p class="bodytext">Nayimassa vijjā, yathayidaṃ cittaṃ. Migī bhantā vudikkhati, sittā te lahu messati, asittā te garu messati. </p>','\r',char(13)),'\n',char(10)),'-33-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',53,replace(replace('<p class="bodytext">Asso <a name="M0.0053"></a> bhadro kasāmiva, sammadaññā vimuttānaṃ. Manasādaññā vimuttānaṃ, attadatthamabhiññāya. Ciraṃnāyati, ito nāyati. Yasmātiha bhikkhave, tasmātiha bhikkhave, ajjatagge pāṇupetaṃ. Sabbhireva samāsetha, āraggeriva sāsapo, sāsaporiva āraggā. Chaḷabhiññā, saḷāyatanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kasmā? Evaṃ mahiddhiyā esā, akkocchi maṃ avadhi maṃ, ajini maṃ ahāsi me, ajeyyo anugāmiko.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> idheva <span class="bld">ma</span>kārassa <span class="bld">pa</span>kāro hoti. Cirappavāsiṃ purisaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ka</span>kārassa ca <span class="bld">da</span>kāro hoti. Sadatthapasuto siyā.</p>\r\n<p class="bodytext"><span class="bld">Da</span>kārassa ca <span class="bld">ta</span>kāro hoti, sugato.</p>\r\n<p class="bodytext"><a name="para36"></a><span class="paranum">36</span>, 47. <span class="bld">Kvaci o byañjane</span>.</p>\r\n<p class="bodytext">Byañjane pare kvaci <span class="bld">o</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Atippago kho tāva sāvatthiyaṃ piṇḍāya carituṃ. Parosahassaṃ.</p>','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',54,replace(replace('<p class="bodytext"><span class="bld">Kvacī</span>ki <a name="M0.0054"></a> kasmā? Etha passathimaṃ lokaṃ, andhībhūto ayaṃ loko.</p>\r\n<p class="bodytext"><a name="para37"></a><span class="paranum">37</span>, 57. <span class="bld">Niggahitañca</span>.</p>\r\n<p class="bodytext">Niggahitañcāgamo hoti sare vā byañjane vā pare kvaci.</p>\r\n<p class="bodytext">Cakkhuṃudapādi, avaṃsiro, yāvañcidha bhikkhave purimaṃ jātiṃ sarāmi, aṇuṃthūlāni sabbaso, manopubbaṅgamā dhammā.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Idheva naṃ pasaṃsanti, pecca sagge pamodati, na hi etehi yānehi, gaccheyya agataṃ disaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> visaddassa ca pakāro hoti. Pacessati, vicessati vā.</p>\r\n<p class="bodytext"><a name="para38"></a><span class="paranum">38</span>, 53. <span class="bld">Kvaci lopaṃ</span>.</p>\r\n<p class="bodytext">Niggahitaṃ kho sare pare kvaci lopaṃ pappoti.</p>\r\n<p class="bodytext">Tāsāhaṃ santike, vidūnaggamiti.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Ahameva nūna bālo etamatthaṃ viditvāna.</p>\r\n<p class="bodytext"><a name="para39"></a><span class="paranum">39</span>, 54. </p>','\r',char(13)),'\n',char(10)),'-37-38-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',55,replace(replace('<p class="bodytext"><span class="bld">Byañjane</span> <a name="M0.0055"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Niggahitaṃ kho byañjane pare kvaci lopaṃ pappoti.</p>\r\n<p class="bodytext">Ariyasaccānadassanaṃ, etaṃ buddhānasāsanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Etaṃ maṅgalamuttamaṃ, taṃ vo vadāmi bhaddante.</p>\r\n<p class="bodytext"><a name="para40"></a><span class="paranum">40</span>, 55. <span class="bld">Paro vāsaro</span>.</p>\r\n<p class="bodytext">Niggahitamhā paro saro lopaṃ pappoti vā.</p>\r\n<p class="bodytext">Bhāsitaṃ abhinandunti, uttattaṃva, yathābījaṃva, yathādhaññaṃva.</p>\r\n<p class="bodytext">Vāti kasmā? Ahameva nūna bālo, etadahosi.</p>\r\n<p class="bodytext"><a name="para41"></a><span class="paranum">41</span>, 56. <span class="bld">Byañjano ca visaññogo</span>.</p>\r\n<p class="bodytext">Niggahitamhā parasmiṃ sare lutte yadi byañjano sasaññogo visaññogo hoti.</p>\r\n<p class="bodytext">Evaṃsa te āsavo, pupphaṃsā uppajji.</p>\r\n<p class="bodytext"><span class="bld">Lutte</span>ti kasmā? Evamassa vidūnaggamiti.</p>','\r',char(13)),'\n',char(10)),'-40-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',56,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇena</span> <a name="M0.0056"></a> tiṇṇaṃ byañjanānamantare ye sarūpā, tesampi lopo hoti. Agyāgāraṃ, paṭisanthāravutyassa.</p>\r\n<p class="centered">Iti sandhikappe catuttho kaṇḍo.</p>\r\n<p class="subhead">Pañcamakaṇḍa</p>\r\n<p class="bodytext"><a name="para42"></a><span class="paranum">42</span>, 32. <span class="bld">Gosare puthassāgamo kvaci</span>.</p>\r\n<p class="bodytext"><span class="bld">Putha</span>iccetassa ante sare pare kvaci <span class="bld">ga</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Puthage va.</p>\r\n<p class="bodytext"><a name="para43"></a><span class="paranum">43</span>, 33. <span class="bld">Pāssa canto rasso</span>.</p>\r\n<p class="bodytext"><span class="bld">Pā</span>iccetassa ante sare pare kvaci <span class="bld">ga</span>kārāgamo hoti, anto ca saro rasso hoti.</p>\r\n<p class="bodytext">Pageva vutyassa.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Pā eva vutyassa.</p>\r\n<p class="bodytext"><a name="para44"></a><span class="paranum">44</span>, 24. <span class="bld">Abbho abhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Abhi</span>iccetassa sare pare <span class="bld">abbhā</span>deso hoti. Abbhudīritaṃ, abbhuggacchati.</p>\r\n<p class="bodytext"><a name="para45"></a><span class="paranum">45</span>, 25. </p>','\r',char(13)),'\n',char(10)),'-42-43-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',57,replace(replace('<p class="bodytext"><span class="bld">Ajjho</span> <a name="M0.0057"></a> <span class="bld">adhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Adhi</span>iccetassa sare pare <span class="bld">ajjhā</span>deso hoti.</p>\r\n<p class="bodytext">Ajjhokāse, ajjhāgamā.</p>\r\n<p class="bodytext"><a name="para46"></a><span class="paranum">46</span>, 26. <span class="bld">Te na vā ivaṇṇe</span>.</p>\r\n<p class="bodytext">Te ca kho <span class="bld">abhiadhi</span>iccete ivaṇṇe pare <span class="bld">abbho ajjho</span>itivuttarūpā naṃ honti vā.</p>\r\n<p class="bodytext">Abhicchitaṃ, adhīritaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kasmā? Abbhīritaṃ, ajjhiṇamutto.</p>\r\n<p class="bodytext"><a name="para47"></a><span class="paranum">47</span>, 23. <span class="bld">Atissa cantassa</span>.</p>\r\n<p class="bodytext"><span class="bld">Ati</span>iccetassa antabhūtassa <span class="bld">ti</span>saddassa ivaṇṇe pare ‘‘sabbo caṃ tī’’ti vuttarūpaṃ na hoti.</p>\r\n<p class="bodytext">Atīsigaṇo, atīritaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ivaṇṇe</span>ti kasmā? Accantaṃ.</p>\r\n<p class="bodytext"><a name="para48"></a><span class="paranum">48</span>, 43. <span class="bld">Kvaci paṭi patissa</span>.</p>\r\n<p class="bodytext"><span class="bld">Pati</span>iccetassa sare vā byañjane vā pare kvaci <span class="bld">paṭi</span>ādeso hoti.</p>\r\n<p class="bodytext">Paṭaggi dātabbo, paṭihaññati.</p>','\r',char(13)),'\n',char(10)),'-46-47-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',58,replace(replace('<p class="bodytext"><span class="bld">Kvacī</span>ti <a name="M0.0058"></a> kasmā? Paccantimesu janapadesu, patilīyati, patirūpadesavāso ca.</p>\r\n<p class="bodytext"><a name="para49"></a><span class="paranum">49</span>, 44. <span class="bld">Puthassu byañjane</span>.</p>\r\n<p class="bodytext"><span class="bld">Putha</span>iccetassa anto saro byañjane pare <span class="bld">u</span>kāro hoti.</p>\r\n<p class="bodytext">Puthujjano, puthubhūtaṃ.</p>\r\n<p class="bodytext"><span class="bld">Antaggahaṇena</span> aputhassāpi sare pare antassa <span class="bld">u</span>kāro hoti, manuññaṃ.</p>\r\n<p class="bodytext"><a name="para50"></a><span class="paranum">50</span>, 45. <span class="bld">O avassa</span>.</p>\r\n<p class="bodytext"><span class="bld">Ava</span>iccetassa byañjane pare kvaci <span class="bld">o</span>kāro hoti.</p>\r\n<p class="bodytext">Andhakārena onaddhā.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kasmā? Avasussatu me sarīre maṃsalohitaṃ.</p>\r\n<p class="bodytext"><a name="para51"></a><span class="paranum">51</span>, 59. <span class="bld">Anupadiṭṭhānaṃ vuttayogato</span>.</p>\r\n<p class="bodytext">Anupadiṭṭhānaṃ upasagganipātānaṃ sarasandhīhi byañjanasandhīhi vuttasandhīhi ca yathāyogaṃ yojetabbaṃ.</p>','\r',char(13)),'\n',char(10)),'-49-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',59,replace(replace('<p class="bodytext">Pāpanaṃ<a name="M0.0059"></a>, parāyaṇaṃ, upāyanaṃ, upāhanaṃ, nyāyogo, nigupadhi, anubodho, duvūpasantaṃ, suvūpasantaṃ, dvālayo, svālayo, durākhyātaṃ, svākhyāto, udīritaṃ, samuddiṭṭhaṃ, viyaggaṃ, vijjhaggaṃ, byaggaṃ, avayāgamanaṃ, anveti, anupaghāto, anacchariyaṃ, pariyesanā, parāmāso, evaṃ sare ca honti.</p>\r\n<p class="bodytext">Pariggaho, paggaho, pakkamo, parakkamo, nikkamo, nikkasāvo, nillayanaṃ, dullayanaṃ, dumbhikkhaṃ, dubbuttaṃ, sandiṭṭhaṃ, duggaho, viggaho, niggato, abhikkamo, paṭikkamo, evaṃ <span class="bld">byañjane</span> ca. Sesā sabbe yojetabbā.</p>\r\n<p class="centered">Iti sandhikappe pañcamo kaṇḍo.</p>\r\n<p class="centered">Sandhikappo niṭṭhito.</p>\r\n<p class="chapter">2. Nāmakappa</p>\r\n<p class="subhead">Paṭhamakaṇḍa</p>\r\n<p class="bodytext"><a name="para52"></a><span class="paranum">52</span>, 60. </p>','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',60,replace(replace('<p class="bodytext"><span class="bld">Jinavacanayuttaṃ</span> <a name="M0.0060"></a> <span class="bld">hi</span>.</p>\r\n<p class="bodytext">‘‘Jinavacanayuttaṃ hi’’ iccetaṃ adhikāratthaṃ veditabbaṃ.</p>\r\n<p class="bodytext"><a name="para53"></a><span class="paranum">53</span>, 61. <span class="bld">Liṅgañca nippajjate</span>.</p>\r\n<p class="bodytext">Yathā yathā jinavacanayuttaṃ hi liṅgaṃ, tathā tathā idha liṅgañca nippajjate.</p>\r\n<p class="bodytext">Taṃ yathā? Eso no satthā, brahmā attā, sakhā, rājā.</p>\r\n<p class="bodytext"><a name="para54"></a><span class="paranum">54</span>, 62. <span class="bld">Tato ca vibhattiyo</span>.</p>\r\n<p class="bodytext">Tato jinavacanayuttehi liṅgehi vibhattiyo parā honti.</p>\r\n<p class="bodytext"><a name="para55"></a><span class="paranum">55</span>, 63. <span class="bld">Si yo, aṃ yo, nā hi, sa naṃ, smā hi, sa naṃ, smiṃ su</span>.</p>\r\n<p class="bodytext">Kā ca pana tāyo vibhattiyo? Si, yo iti paṭhamā, aṃ, yoiti dutiyā, nā hi iti tatiyā, sa, naṃiti catutthī, smā, hi iti pañcamī, sa, naṃ iti chaṭṭhī, smiṃ, su iti sattamī.</p>','\r',char(13)),'\n',char(10)),'-53-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',61,replace(replace('<p class="bodytext"><span class="bld">Vibhatti</span>iccanena <a name="M0.0061"></a> kvattho? Amhassa mamaṃ savibhattissa se.</p>\r\n<p class="bodytext"><a name="para56"></a><span class="paranum">56</span>, 64. <span class="bld">Tadanuparodhena</span>.</p>\r\n<p class="bodytext">Yathā yathā tesaṃ jinavacanānaṃ anuparodho. Tathā tathā idha liṅgañca nippajjate.</p>\r\n<p class="bodytext"><a name="para57"></a><span class="paranum">57</span>, 71. <span class="bld">Ālapane si ga sañño</span>.</p>\r\n<p class="bodytext">Ālapanatthe si gasañño hoti.</p>\r\n<p class="bodytext">Bhoti ayye, bhoti kaññe, bhoti kharādiye.</p>\r\n<p class="bodytext"><span class="bld">Ālapane</span>ti kimatthaṃ? Sā ayyā.</p>\r\n<p class="bodytext"><span class="bld">Sī</span>ti kimatthaṃ? Bhotiyo ayyāyo.</p>\r\n<p class="bodytext"><span class="bld">Gai</span>ccanena kvattho? Ghate ca.</p>\r\n<p class="bodytext"><a name="para58"></a><span class="paranum">58</span>, 29. <span class="bld">Ivaṇṇuvaṇṇā jhalā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ivaṇṇuvaṇṇā</span>iccete <span class="bld">jhala</span>saññā honti yathāsaṅkhyaṃ.</p>','\r',char(13)),'\n',char(10)),'-56-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',62,replace(replace('<p class="bodytext">Isino<a name="M0.0062"></a>, aggino, gahapatino, daṇḍino. Setuno, ketuno, bhikkhuno. Sayambhuno, abhibhuno.</p>\r\n<p class="bodytext"><span class="bld">Jhala</span>iccanena kvattho? Jhalato sassa no vā.</p>\r\n<p class="bodytext"><a name="para59"></a><span class="paranum">59</span>, 182. <span class="bld">Te itthikhyā po</span>.</p>\r\n<p class="bodytext"><span class="bld">Te ivaṇṇuvaṇṇā</span> yadā itthikhyā, tadā <span class="bld">pa</span>saññā honti.</p>\r\n<p class="bodytext">Rattiyā, itthiyā, dhenuyā, vadhuyā.</p>\r\n<p class="bodytext"><span class="bld">Itthikhyā</span>ti kimatthaṃ? Isinā, bhikkhunā.</p>','\r',char(13)),'\n',char(10)),'-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',63,replace(replace('<p class="bodytext"><span class="bld">Sa</span>iccanena <a name="M0.0063"></a> kvattho? Pato yā.</p>\r\n<p class="bodytext"><a name="para60"></a><span class="paranum">60</span>, 177. <span class="bld">Ā gho</span>.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>kāro yadā itthikhyo, tadā <span class="bld">gha</span>sañño hoti.</p>\r\n<p class="bodytext">Saddhāya, kaññāya, vīṇāya, gaṅgāya, disāya sālāya, mālāya, tulāya, dolāya, pabhāya, sobhāya, paññāya, karuṇāya nāvāya, kapālikāya.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>ti kimatthaṃ? Rattiyā, itthiyā.</p>\r\n<p class="bodytext"><span class="bld">Itthikhyo</span>ti kimatthaṃ? Satthārā desito ayaṃ dhammo.</p>\r\n<p class="bodytext"><span class="bld">Gha</span>iccanena kvattho? Ghato nādīnaṃ.</p>\r\n<p class="bodytext"><a name="para61"></a><span class="paranum">61</span>, 86. <span class="bld">Sāgamo se</span>.</p>\r\n<p class="bodytext"><span class="bld">Sa</span>kārāgamo hoti <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Purisassa, aggissa, isissa, daṇḍissa, bhikkhussa, sayambhussa, abhibhussa.</p>\r\n<p class="bodytext"><span class="bld">Se</span>ti kimattaṃ? Purisasmiṃ.</p>\r\n<p class="bodytext"><a name="para62"></a><span class="paranum">62</span>, 206. <span class="bld">Saṃsāsvekavacanesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Saṃsā</span>su ekavacanesu vibhattādesesu <span class="bld">sa</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Etissaṃ, etissā imissaṃ, imissā, tissaṃ, tissā,</p>\r\n<p class="bodytext">Tassaṃ tassā, yassaṃ, yassā, amussaṃ, amussā.</p>\r\n<p class="bodytext"><span class="bld">Saṃsāsvī</span>ti kimatthaṃ? Agginā, pāṇinā.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanesvī</span>ti kimatthaṃ? Tāsaṃ, sabbāsaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vibhattādesesvī</span>ti kimatthaṃ? Manasā, vacasā, thāmasā.</p>\r\n<p class="bodytext"><a name="para63"></a><span class="paranum">63</span>, 217. <span class="bld">Etimāsami</span>.</p>\r\n<p class="bodytext"><span class="bld">Etāimā</span>iccetesamanto saro ikāro hoti <span class="bld">saṃsā</span>su ekavacanesu vibhattādesesu.</p>\r\n<p class="bodytext">Etissaṃ, etissā, imissaṃ, imissā.</p>','\r',char(13)),'\n',char(10)),'-60-61-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',64,replace(replace('<p class="bodytext"><span class="bld">Saṃsāsvī</span>ti <a name="M0.0064"></a> kimatthaṃ? Etāya, imāya.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanesvī</span>ti kimatthaṃ? Etāsaṃ, imāsaṃ.</p>\r\n<p class="bodytext"><a name="para64"></a><span class="paranum">64</span>, 216. <span class="bld">Tassā vā</span>.</p>\r\n<p class="bodytext">Tassā itthiyaṃ vattamānassa antassa <span class="bld">ā</span>kārassa <span class="bld">i</span>kāro hoti vā <span class="bld">saṃsā</span>su ekavacanesu vibhattādesesu.</p>\r\n<p class="bodytext">Tissaṃ, tissā, tassaṃ, tassā.</p>\r\n<p class="bodytext"><a name="para65"></a><span class="paranum">65</span>, 215. <span class="bld">Tato sassa ssāya</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">tā etā imā</span>to <span class="bld">sa</span>ssa vibhattissa <span class="bld">ssāyā</span>deso hoti vā.</p>\r\n<p class="bodytext">Tissāya, etissāya, imissāya.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Tissā, etissā, imissā.</p>\r\n<p class="bodytext"><a name="para66"></a><span class="paranum">66</span>, 205. <span class="bld">Gho rassaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Gho</span> rassamāpajjate <span class="bld">saṃsā</span>su ekavacanesu vibhattādesesu.</p>\r\n<p class="bodytext">Tassaṃ, tassā, yassaṃ, yassā, sabbassaṃ, sabbassā.</p>\r\n<p class="bodytext"><span class="bld">Saṃsā</span>svīti kimatthaṃ? Tāya, sabbāya.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanesvī</span>ti kimatthaṃ? Tāsaṃ, sabbāsaṃ.</p>\r\n<p class="bodytext"><a name="para67"></a><span class="paranum">67</span>, 229. </p>','\r',char(13)),'\n',char(10)),'-64-65-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',65,replace(replace('<p class="bodytext"><span class="bld">No</span> <a name="M0.0065"></a> <span class="bld">ca dvādito naṃmhi</span>.</p>\r\n<p class="bodytext">Dviiccevamādito saṅkhyāto <span class="bld">na</span>kārāgamo hoti naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Dvinnaṃ, tinnaṃ, catunnaṃ, pañcannaṃ, channaṃ, sattannaṃ, aṭṭhannaṃ, navannaṃ, dasannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Dvādito</span>ti kimatthaṃ? Sahassānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Naṃmhī</span>ti kimatthaṃ? Dvīsu, tīsu.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇenassa</span>ñcāgamo hoti. Catassannaṃ itthīnaṃ tissannaṃ vedanānaṃ.</p>\r\n<p class="bodytext"><a name="para68"></a><span class="paranum">68</span>, 184. <span class="bld">Amā pato smiṃsmānaṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Pa</span>iccetasmā <span class="bld">smiṃsmā</span>iccetesaṃ <span class="bld">aṃā</span>ādesā honti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Matyaṃ, matiyaṃ, matyā, matiyā, nikatyaṃ. Nikatiyaṃ, nikatyā, nikatiyā, vikatyaṃ, vikatiyaṃ, vikatyā, vikatiyā, viratyaṃ, viratiyaṃ, viratyā, viratiyā, ratyaṃ, ratiyaṃ, ratyā, ratiyā, puthabyaṃ, puthaviyaṃ, puthabyā, puthaviyā, pavatyaṃ, pavatyā, pavattiyaṃ, pavattiyā.</p>\r\n<p class="bodytext"><a name="para69"></a><span class="paranum">69</span>, 186. <span class="bld">Ādito o ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ādi</span>iccetasmā <span class="bld">smiṃ</span>vacanassa <span class="bld">aṃo</span>ādesā honti vā.</p>','\r',char(13)),'\n',char(10)),'-68-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',66,replace(replace('<p class="bodytext">Ādīṃ<a name="M0.0066"></a>, ādo.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Ādismiṃ, ādimhi nāthaṃ namassitvāna,</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> aññasmāpi <span class="bld">smiṃ</span> vacanassa <span class="bld">ā o aṃ</span>ādesā honti. Divā ca ratto ca haranti ye bali. Bārāṇasiṃ ahu rājā.</p>\r\n<p class="bodytext"><a name="para70"></a><span class="paranum">70</span>, 30. <span class="bld">Jhalānamiyuvā sare vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Jhala</span>iccetesaṃ <span class="bld">iya uva</span>iccete ādesā honti vā sare pare yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Tiyantaṃ pacchiyāgāre, aggiyāgāre, bhikkhuvāsane nisīdati, vuthuvāsane nisīdati.</p>\r\n<p class="bodytext"><span class="bld">Sare</span>ti kimatthaṃ? Timalaṃ, tiphalaṃ, ticatukkaṃ, tidaṇḍaṃ, tilokaṃ, tinayanaṃ, tipāsaṃ, tihaṃsaṃ, tibhavaṃ, tikhandhaṃ, tipiṭakaṃ, tivedanaṃ, catuddisaṃ, puthubhūtaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Pañcahaṅgehi tīhākārehi. Cakkhāyatanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti vikappanatthaṃ, <span class="bld">i</span>kārassa <span class="bld">ayā</span>deso hoti, vatthuttayaṃ.</p>\r\n<p class="bodytext"><a name="para71"></a><span class="paranum">71</span>, 505. <span class="bld">Yavakārā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Jhalānaṃ ya</span>kāra <span class="bld">va</span>kārādesā honti sare pare yathāsaṅkhyaṃ.</p>','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',67,replace(replace('<p class="bodytext">Agyāgāraṃ<a name="M0.0067"></a>, pakkhāyatanaṃ, svāgataṃ, te mahāvīra.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> sampiṇḍanatthaṃ.</p>\r\n<p class="bodytext"><a name="para72"></a><span class="paranum">72</span>, 185. <span class="bld">Pasaññassa ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Pa</span>saññassa ca ivaṇṇassa vibhattādese sare pare <span class="bld">ya</span>kārādeso hoti.</p>\r\n<p class="bodytext">Puthabyā, ratyā, matyā.</p>\r\n<p class="bodytext"><span class="bld">Sare</span>ti kimatthaṃ? Puthaviyaṃ.</p>\r\n<p class="bodytext"><a name="para73"></a><span class="paranum">73</span>, 174. <span class="bld">Gāva se</span>.</p>\r\n<p class="bodytext"><span class="bld">Go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">āvā</span>deso hoti <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Gāvassa.</p>\r\n<p class="bodytext"><a name="para74"></a><span class="paranum">74</span>, 169. <span class="bld">Yosu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">āvā</span>deso hoti <span class="bld">yo</span>iccetesu paresu.</p>\r\n<p class="bodytext">Gāvo gacchanti, gāvo passanti, gāvī gacchanti, gāvī passanti.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> kimatthaṃ? <span class="bld">Nāsmāsmiṃ</span>su vacanesu <span class="bld">āvā</span> deso hoti.</p>\r\n<p class="bodytext">Gāvena, gāvā, gāve, gāvesu.</p>\r\n<p class="bodytext"><a name="para75"></a><span class="paranum">75</span>, 170. </p>','\r',char(13)),'\n',char(10)),'-72-73-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',68,replace(replace('<p class="bodytext"><span class="bld">Avamhi</span> <a name="M0.0068"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">āvaava</span>iccete ādesā honti aṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Gāvaṃ, gavaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena sā</span>disesesu pubbuttavacanesu <span class="bld">go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">avā</span>deso hoti.</p>\r\n<p class="bodytext">Gavassa, gavo, gavena, gavā, gave, gavesu.</p>\r\n<p class="bodytext"><a name="para76"></a><span class="paranum">76</span>, 171. <span class="bld">Āvassu vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Āva</span>iccetassa <span class="bld">gāvā</span>desassa anta sarassa <span class="bld">u</span>kārādeso hoti vā aṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Gāvuṃ, gāvaṃ.</p>\r\n<p class="bodytext"><span class="bld">Āvasse</span>ti kimatthaṃ? Gāvo tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para77"></a><span class="paranum">77</span>, 175. <span class="bld">Tato namaṃ patimhā lutte ca samāse</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">go</span>saddato naṃvacanassa <span class="bld">aṃ</span>ādeso hoti, <span class="bld">go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">avā</span>deso hoti <span class="bld">pati</span>mhi pare alutte ca samāse.</p>\r\n<p class="bodytext">Gavapati.</p>','\r',char(13)),'\n',char(10)),'-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',69,replace(replace('<p class="bodytext"><span class="bld">Alutte</span>ti <a name="M0.0069"></a> kimatthaṃ? Gopati.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> asamāsepi naṃvacanassa aṃādeso hoti, <span class="bld">go</span>iccetassa <span class="bld">o</span>kārassa <span class="bld">avā</span>deso hoti.</p>\r\n<p class="bodytext">Gavaṃ.</p>\r\n<p class="bodytext"><a name="para78"></a><span class="paranum">78</span>, 3. <span class="bld">Osare ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Goi</span>ccetassa <span class="bld">o</span>kārassa <span class="bld">avā</span>deso hoti samāse ca sare pare.</p>\r\n<p class="bodytext">Gavassakaṃ, gaveḷakaṃ, gavājinaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> uvaṇṇaiccevamantānaṃ liṅgānaṃ <span class="bld">uvaavaurā</span>desā honti <span class="bld">smiṃyo</span>iccetesu kvaci.</p>\r\n<p class="bodytext">Bhuvi, pasavo, guravo, caturo.</p>\r\n<p class="bodytext"><span class="bld">Sareti</span> kimatthaṃ? Godhano, govindo.</p>\r\n<p class="bodytext"><a name="para79"></a><span class="paranum">79</span>, 46. <span class="bld">Tabbiparītūpapade byañjane ca</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">ava</span>saddassa yadā upapade tiṭṭhamānassa tassa <span class="bld">o</span>kārassa viparīto hoti byañjane pare.</p>\r\n<p class="bodytext">Uggate sūriye, uggacchati, uggahetvā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ. Avasāne, avakiraṇe, avakirati.</p>\r\n<p class="bodytext"><a name="para80"></a><span class="paranum">80</span>, 173. </p>','\r',char(13)),'\n',char(10)),'-78-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',70,replace(replace('<p class="bodytext"><span class="bld">Goṇa</span> <a name="M0.0070"></a> <span class="bld">naṃmhi vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">go</span>saddassa <span class="bld">goṇā</span>deso hoti vā naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Goṇānaṃ sattannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ?</p>\r\n<p class="bodytext">Gonañce taramānānaṃ, ujuṃ gacchati puṅgavo.</p>\r\n<p class="bodytext">Sabbā gāvī ujuṃ yanti, nette ujuṃ gate sati.</p>\r\n<p class="bodytext"><span class="bld">Yogavibhāgena</span> aññatrāpi <span class="bld">goṇā</span>deso hoti. Goṇabhūtānaṃ.</p>\r\n<p class="bodytext"><a name="para81"></a><span class="paranum">81</span>, 172. <span class="bld">Suhināsu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Suhinā</span>iccetesu sabbassa <span class="bld">go</span>saddassa <span class="bld">goṇā</span>deso hoti vā.</p>\r\n<p class="bodytext">Goṇesu, goṇehi, goṇebhi, goṇena.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Gosu, gohi, gobhi, gavena.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena syā</span>disesesu pubbuttaravacanesupi <span class="bld">goṇa gu gavayā</span>desā honti. Goṇo, goṇā, goṇaṃ, goṇe, goṇassa, goṇamhā. Goṇamhi, gunnaṃ, gavayehi, gavayebhi.</p>\r\n<p class="bodytext"><a name="para82"></a><span class="paranum">82</span>, 149. </p>','\r',char(13)),'\n',char(10)),'-81-82-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',71,replace(replace('<p class="bodytext"><span class="bld">Aṃmo</span> <a name="M0.0071"></a> <span class="bld">niggahitaṃ jhalapehi</span>.</p>\r\n<p class="bodytext"><span class="bld">Aṃ</span>vacanassa <span class="bld">ma</span>kārassa ca <span class="bld">jhalapa</span>iccetehi niggahitaṃ hoti</p>\r\n<p class="bodytext">Aggiṃ, isiṃ, gahapatiṃ, daṇḍiṃ, mahesiṃ, bhikkhuṃ, paṭuṃ, sayambhuṃ, abhibhuṃ, rattiṃ, itthiṃ, vadhuṃ, pulliṅgaṃ, pumbhāvo, puṅkokilo.</p>\r\n<p class="bodytext"><span class="bld">Aṃmo</span>ti kimatthaṃ? Agginā, pāṇinā, bhikkhunā, rattiyā, itthiyā, vadhuyā.</p>\r\n<p class="bodytext"><span class="bld">Jhalapehīti</span> kimatthaṃ? Sukhaṃ, dukkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Punārambha haṇaṃ</span> vibhāsānivattanatthaṃ. Aggiṃ, paṭuṃ, buddhiṃ, vadhuṃ.</p>\r\n<p class="bodytext"><a name="para83"></a><span class="paranum">83</span>, 67. <span class="bld">Saralopo’ mādesa paccayādimhi saralope tu pakati</span>.</p>\r\n<p class="bodytext">Saralopo hoti amādesapaccayādimhi sara lope tu pakati hoti.</p>\r\n<p class="bodytext">Purisaṃ, purise, pāpaṃ, pāpe, pāpiyo, pāpiṭṭho.</p>\r\n<p class="bodytext"><span class="bld">Amādesapaccayādimhī</span>ti ki tthaṃ? Appamādo amataṃ padaṃ.</p>\r\n<p class="bodytext"><span class="bld">Saralopeti</span> kimatthaṃ? Purisassa, daṇḍinaṃ.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇa</span>mavadhāraṇatthaṃ. Bhikkhunī, gahapatānī.</p>','\r',char(13)),'\n',char(10)),'-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',72,replace(replace('<p class="bodytext"><span class="bld">Pakatiggahaṇasāmatthena</span> <a name="M0.0072"></a> puna sandhibhāvo ca hoti. Seyyo, seṭṭho, jeyyo, jeṭṭho.</p>\r\n<p class="bodytext"><a name="para84"></a><span class="paranum">84</span>, 144. <span class="bld">Aghorassamekavacanayosvapi ca</span>.</p>\r\n<p class="bodytext">Agho saro rassamāpajjate ekavacanayoiccetesu.</p>\r\n<p class="bodytext">Itthiṃ, itthiyo, itthiyā. Vadhuṃ, vadhuyo, vadhuyā. Daṇḍiṃ, daṇḍino, daṇḍinā. Sayambhuṃ, sayambhuvo, sayambhunā.</p>\r\n<p class="bodytext"><span class="bld">Agho</span>ti kimatthaṃ? Kaññaṃ, kaññāyo, kaññāya.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanayosvī</span>ti kimatthaṃ? Itthīhi, sayambhūhi.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ. Nadiṃ, nadiyo, nadiyā.</p>\r\n<p class="bodytext"><span class="bld">Apiggahaṇena</span> na rassamāpajjate. Itthī, bhikkhunī.</p>\r\n<p class="bodytext"><a name="para85"></a><span class="paranum">85</span>, 150. <span class="bld">Na sismimanapuṃsakāni</span>.</p>\r\n<p class="bodytext"><span class="bld">Si</span>smiṃ anapuṃsakāni liṅgāni na rassamāpajjante. Itthī, bhikkhunī, vadhū, daṇḍī, sayambhū.</p>\r\n<p class="bodytext"><span class="bld">Sismiṃ</span>nti kimatthaṃ? Bhoti itthi, bhoti vadhu, bho daṇḍi, bho sayambhu.</p>','\r',char(13)),'\n',char(10)),'-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',73,replace(replace('<p class="bodytext"><span class="bld">Anapuṃsakānī</span>ti <a name="M0.0073"></a> kimatthaṃ? Sukhakāri dānaṃ, sukhakāri sīlaṃ, sīghayāyi cittaṃ.</p>\r\n<p class="bodytext"><a name="para86"></a><span class="paranum">86</span>, 227. <span class="bld">Ubhādito naminnaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ubha</span>iccevamādito saṅkhyāto <span class="bld">naṃ</span>vacanassa <span class="bld">innaṃ</span> hoti.</p>\r\n<p class="bodytext">Ubhinnaṃ, duvinnaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ubhādito</span>ti kimatthaṃ? Ubhayesaṃ.</p>\r\n<p class="bodytext"><a name="para87"></a><span class="paranum">87</span>, 231. <span class="bld">Iṇṇamiṇṇannaṃ tīhi saṅkhyāhi</span>.</p>\r\n<p class="bodytext">Naṃvacanassa <span class="bld">iṇṇaṃ iṇṇannaṃ</span> iccete ādesā honti tīhi saṅkhyāhi.</p>\r\n<p class="bodytext">Tiṇṇaṃ, tiṇṇannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Tīhī</span>ti kimatthaṃ? Dvinnaṃ.</p>\r\n<p class="bodytext"><a name="para88"></a><span class="paranum">88</span>, 147. <span class="bld">Yosu katanikāralopesu dīghaṃ</span>.</p>\r\n<p class="bodytext">Sabbe sarā <span class="bld">yo</span>su katanikāralopesu dīghamāpajjante.</p>\r\n<p class="bodytext">Aggī, bhikkhū, rattī, yāgū, aṭṭhī, aṭṭhīni, āyū, āyūni, sabbāni, yāni, tāni, kāni, katamāni, etāni, apūni, imāni.</p>','\r',char(13)),'\n',char(10)),'-86-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',74,replace(replace('<p class="bodytext"><span class="bld">Yosvī</span>ti <a name="M0.0074"></a> kimatthaṃ? Aggi, bhikkhu, ratti, yāgu, sabbo, yo, so, ko, amuko.</p>\r\n<p class="bodytext"><span class="bld">Katanikāralopesvī</span>ti kimatthaṃ? Itthiyo, vadhuyo, sayambhuvo.</p>\r\n<p class="bodytext"><span class="bld">Punārambhaggahaṇaṃ</span> kimatthaṃ? Niccadīpanatthaṃ. Aggī, bhikkhū, rattī, yāni, tāni, katamāni.</p>\r\n<p class="bodytext"><a name="para89"></a><span class="paranum">89</span>, 87. <span class="bld">Sunaṃhisu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Sunaṃhi</span>iccetesu sabbe sarā dīghamāpajjante.</p>\r\n<p class="bodytext">Aggīsu, aggīnaṃ, aggīhi, rattīsu, rattīnaṃ, rattīhi. Bhikkhūsu, bhikkhūnaṃ, bhikkhūhi. Purisānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>tī kimatthaṃ? Agginā, pāṇinā, daṇḍinā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ. Sukhettesu brahmacārisu, dhammamakkhāsi bhagavā bhikkhunaṃ datvā sakehi pāṇibhi.</p>\r\n<p class="bodytext"><a name="para90"></a><span class="paranum">90</span>, 252. <span class="bld">Pañcādīnamattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Pañcā</span>dīnaṃ saṅkhyānaṃ anto attamāpajjate <span class="bld">sunaṃhi</span>iccetesu.</p>\r\n<p class="bodytext">Pañcasu, pañcannaṃ, pañcahi, chasu, channaṃ, chahi, sattasu, sattannaṃ, sattahi, aṭṭhasu, aṭṭhannaṃ, aṭṭhahi, navasu, navannaṃ, navahi, dasasu, dasannaṃ, dasahi.</p>\r\n<p class="bodytext"><span class="bld">Pañcādīnamī</span>ti kimatthaṃ? Dvīsu, dvinnaṃ, dvīhi.</p>','\r',char(13)),'\n',char(10)),'-89-90-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',75,replace(replace('<p class="bodytext"><span class="bld">Attamitibhāvaniddeso</span> <a name="M0.0075"></a> ubhayassāgamanatthaṃ, anto <span class="bld">u</span>kāro <span class="bld">a</span>ttamāpajjate. Catassannaṃ itthīnaṃ. Tissannaṃ vedanānaṃ.</p>\r\n<p class="bodytext"><a name="para91"></a><span class="paranum">91</span>, 194. <span class="bld">Patissinīmhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Pati</span>ssanto <span class="bld">a</span>ttamāpajjate inīmhi paccaye pare.</p>\r\n<p class="bodytext">Gahapatānī.</p>\r\n<p class="bodytext"><span class="bld">Inīmhī</span>ti kimatthaṃ? Gahapati.</p>\r\n<p class="bodytext"><a name="para92"></a><span class="paranum">92</span>, 100. <span class="bld">Ntussanto yosuca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ntu</span>paccayassa anto <span class="bld">a</span>ttamāpajjate <span class="bld">sunaṃhiyo</span>iccetesu paresu.</p>\r\n<p class="bodytext">Guṇavantesu, guṇavantānaṃ, guṇavantehi, guṇavantā, guṇavante.</p>\r\n<p class="bodytext"><span class="bld">Ntusse</span>ti kimatthaṃ? Isīnaṃ.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Guṇavā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> aññesu vacanesu <span class="bld">a</span>ttañca hoti. Guṇavantasmiṃ, guṇavantena.</p>\r\n<p class="bodytext"><span class="bld">Antaggahaṇena ntu</span>paccayassa anto attamāpajjate, <span class="bld">yo</span>nañca ikāro hoti. Guṇavanti.</p>\r\n<p class="bodytext"><a name="para93"></a><span class="paranum">93</span>, 106. </p>','\r',char(13)),'\n',char(10)),'-91-92-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',76,replace(replace('<p class="bodytext"><span class="bld">Sabbassa</span> <a name="M0.0076"></a> <span class="bld">vā aṃsesu</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa attaṃ hoti vā <span class="bld">aṃsa</span>iccetesu.</p>\r\n<p class="bodytext">Satimaṃ bhikkhu, satimantaṃ bhikkhuṃ vā, bandhumaṃ rājānaṃ, bandhumantaṃ rājānaṃ vā, satimassa bhikkhuno, satimato bhikkhuno vā, bandhumassa rañño suṅkaṃ, bandhumato rañño vā suṅkaṃ deti.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Satimā bhikkhu, bandhumā rājā.</p>\r\n<p class="bodytext"><a name="para94"></a><span class="paranum">94</span>, 105. <span class="bld">Simhi vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ntu</span>paccayassa antassa attaṃ hoti vā <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Himavanto pabbato.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Himavā pabbato.</p>\r\n<p class="bodytext"><a name="para95"></a><span class="paranum">95</span>, 145. </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',77,replace(replace('<p class="bodytext"><span class="bld">Aggissini</span><a name="M0.0077"></a>.</p>\r\n<p class="bodytext"><span class="bld">Aggi</span>ssantassa i<span class="bld">ni</span> hoti vā <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Purato aggini, pacchato aggini, dakkhiṇato aggini, vāmato aggini.</p>\r\n<p class="bodytext"><span class="bld">Vāti</span> kimatthaṃ? Aggi.</p>\r\n<p class="bodytext"><a name="para96"></a><span class="paranum">96</span>, 148. <span class="bld">Yosvakatarasso jho</span>.</p>\r\n<p class="bodytext"><span class="bld">Yo</span>su akatarasso <span class="bld">jho</span> attamāpajjate.</p>\r\n<p class="bodytext">Aggayo munayo, isayo, gahapatayo.</p>\r\n<p class="bodytext"><span class="bld">Yosvī</span>ti kimatthaṃ? Aggīsu.</p>\r\n<p class="bodytext"><span class="bld">Akatarasso</span>ti kimatthaṃ? Daṇḍino.</p>\r\n<p class="bodytext"><span class="bld">Jho</span>ti kimatthaṃ? Rattiyo.</p>\r\n<p class="bodytext"><a name="para97"></a><span class="paranum">97</span>, 156. <span class="bld">Vevosu lo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Vevo</span>iccetesu akatarasso <span class="bld">lo a</span>ttamāpajjate.</p>\r\n<p class="bodytext">Bhikkhave, bhikkhavo, hetave, hetavo.</p>\r\n<p class="bodytext"><span class="bld">Akatarasso</span>ti kimatthaṃ? Sayambhuvo, vessabhuvo, parābhibhuvo.</p>\r\n<p class="bodytext"><span class="bld">Vevosvī</span>ti kimatthaṃ? Hetunā, ketunā, setunā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para98"></a><span class="paranum">98</span>, 186. <span class="bld">Mātulādīnamānattamīkāre</span>.</p>\r\n<p class="bodytext"><span class="bld">Mātula</span>iccevamādīnaṃ anto <span class="bld">āna</span>ttamāpajjate <span class="bld">ī</span>kāre paccaye pare.</p>','\r',char(13)),'\n',char(10)),'-96-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',78,replace(replace('<p class="bodytext">Mātulānī<a name="M0.0078"></a>, ayyakānī, varuṇānī.</p>\r\n<p class="bodytext"><span class="bld">Īkāre</span>ti kimatthaṃ? Bhikkhunī, rājinī, jālinī, gahapatānī.</p>\r\n<p class="bodytext"><span class="bld">Ānattaggahaṇena nadī</span>iccetassa <span class="bld">dī</span>saddassa <span class="bld">jjojjā</span> ādesā honti saha vibhattiyā <span class="bld">yonāsa</span>iccetesu. Najjo sandanti, najjā kataṃ taraṅgaṃ, najjā nerañjarāya tīre.</p>\r\n<p class="bodytext"><a name="para99"></a><span class="paranum">99</span>, 81. <span class="bld">Smāhismiṃnaṃmhābhimhivā</span>.</p>\r\n<p class="bodytext">Sabbato liṅgato <span class="bld">smāhismiṃ</span> iccetesaṃ <span class="bld">mhābhimhi</span>iccete ādesā honti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Purisamhā, purisasmā, purisebhi, purisehi, purisamhi, purisasmiṃ.</p>\r\n<p class="bodytext"><span class="bld">Smāhismiṃna</span>miti kimatthaṃ? Vaṇṇavantaṃ agandhakaṃ viruḷhapupphaṃ, mahantaṃ chattaṃ mahāchattaṃ, mahantaṃ dhajaṃ mahādhajaṃ.</p>\r\n<p class="bodytext"><a name="para100"></a><span class="paranum">100</span>, 214. <span class="bld">Na timehi katākārehi</span>.</p>\r\n<p class="bodytext"><span class="bld">Ta ima</span>iccetehi katākārehi <span class="bld">smāsmiṃ naṃmhāmhi</span>iccete ādesā neva honti.</p>\r\n<p class="bodytext">Asmā ṭhānā bhayaṃ uppajjati, asmiṃ ṭhāne bhayaṃ tiṭṭhati, asmā, asmiṃ.</p>','\r',char(13)),'\n',char(10)),'-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',79,replace(replace('<p class="bodytext"><span class="bld">Katākārehī</span>ti <a name="M0.0079"></a> kimatthaṃ? Tamhā, tamhi, imamhā, imamhi.</p>\r\n<p class="bodytext"><a name="para101"></a><span class="paranum">101</span>, 80. <span class="bld">Suhisvakāro e</span>.</p>\r\n<p class="bodytext"><span class="bld">Suhi</span>iccetesu akāro ettamāpajjate.</p>\r\n<p class="bodytext">Sabbesu, yesu, tesu, kesu, purisesu, imesu, kusalesu, tumhesu, amhesu, sabbehi, yehi, tehi, kehi, purisehi, imehi, kusalehi, tumhehi, amhehi.</p>\r\n<p class="bodytext"><a name="para102"></a><span class="paranum">102</span>, 202. <span class="bld">Sabbanāmānaṃ naṃmhi ca</span>.</p>\r\n<p class="bodytext">Sabbesaṃ sabbanāmānaṃ anto akāro ettamāpajjate naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Sabbesaṃ, sabbesānaṃ, yesaṃ, yesānaṃ, tesaṃ, tesānaṃ, imesaṃ, imesānaṃ, kesaṃ, kesānaṃ, itaresaṃ, itaresānaṃ, katamesaṃ, katamesānaṃ. <span class="bld">Sabbanāmāna</span>miti kimatthaṃ? Buddhānaṃ bhagavantānaṃ āciṇṇasamāciṇṇo.</p>\r\n<p class="bodytext"><span class="bld">Akāro</span>ti kimatthaṃ? Amūsaṃ, amūsānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Naṃmhī</span>ti kimatthaṃ? Sabbe, ime.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span> manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para103"></a><span class="paranum">103</span>, 79. </p>','\r',char(13)),'\n',char(10)),'-101-102-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',80,replace(replace('<p class="bodytext"><span class="bld">Ato</span> <a name="M0.0080"></a> <span class="bld">nena</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato <span class="bld">nā</span>vacanassa <span class="bld">enā</span>deso hoti.</p>\r\n<p class="bodytext">Sabbena, yena, tena, kena, anena, purisena, rūpena.</p>\r\n<p class="bodytext"><span class="bld">Ato</span>ti kimatthaṃ? Muninā, amunā, bhikkhunā.</p>\r\n<p class="bodytext"><span class="bld">Nā</span>ti kimatthaṃ? Tasmā.</p>\r\n<p class="bodytext"><a name="para104"></a><span class="paranum">104</span>, 66. <span class="bld">So</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato <span class="bld">si</span>vacanassa <span class="bld">o</span>kārādeso hoti.</p>\r\n<p class="bodytext">Sabbo, yo, so, ko, amuko, puriso.</p>\r\n<p class="bodytext"><span class="bld">Sī</span>ti kimatthaṃ? Purisānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ato</span>ti kimatthaṃ? Sayambhū.</p>\r\n<p class="bodytext"><a name="para105"></a><span class="paranum">105</span>, 0. <span class="bld">So vā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato <span class="bld">nā</span>vacanassa <span class="bld">so</span>ādeso hoti vā.</p>\r\n<p class="bodytext">Atthaso dhammaṃ jānāti, byañjanaso atthaṃ jānāti, akkharaso, suttaso, padaso, yasaso. Upāyaso, sabbaso, thāmaso, ṭhānaso.</p>','\r',char(13)),'\n',char(10)),'-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',81,replace(replace('<p class="bodytext"><span class="bld">Vā</span>ti <a name="M0.0081"></a> kimatthaṃ? Pādena vā pādārahena vā atirekapādena vā yo bhikkhu theyyacittena parassa bhaṇḍaṃ gaṇhāti, so bhikkhu pārājiko hoti asaṃvāso.</p>\r\n<p class="bodytext"><a name="para106"></a><span class="paranum">106</span>, 313. <span class="bld">Dīghorehi</span>.</p>\r\n<p class="bodytext"><span class="bld">Dīghaora</span>iccetehi <span class="bld">smā</span>vacanassa <span class="bld">so</span>ādeso hoti vā.</p>\r\n<p class="bodytext">Dīghaso, oraso, dīghamhā, oramhā.</p>\r\n<p class="bodytext"><span class="bld">Dīghorehi</span>ti kimatthaṃ? Saramhā, vacanamhā.</p>\r\n<p class="bodytext"><a name="para107"></a><span class="paranum">107</span>, 69. <span class="bld">Sabbayonīnamāe</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato sabbesaṃ <span class="bld">yonīnaṃāe</span> ādesā honti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Purisā, purise, rūpā, rūpe.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Aggayo, munayo, isayo.</p>\r\n<p class="bodytext"><span class="bld">Yonīna</span>nti kimatthaṃ? Purisassa, rūpassa.</p>\r\n<p class="bodytext"><span class="bld">Akārato</span>ti kimatthaṃ? Daṇḍino, aṭṭhīni, aggī pajjalanti, munī caranti.</p>\r\n<p class="bodytext"><a name="para108"></a><span class="paranum">108</span>, 90. <span class="bld">Smāsmiṃnaṃvā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato sabbesaṃ <span class="bld">smāsmiṃ</span>iccetesaṃ <span class="bld">ā e</span> ādesā honti vā yathāsaṅkhyaṃ.</p>','\r',char(13)),'\n',char(10)),'-106-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',82,replace(replace('<p class="bodytext">Purisā<a name="M0.0082"></a>, purisasmā, purise, purisasmiṃ.</p>\r\n<p class="bodytext"><span class="bld">Akārato</span>ti kimatthaṃ? Daṇḍinā, daṇḍismiṃ, bhikkhunā, bhikkhusmiṃ.</p>\r\n<p class="bodytext"><a name="para109"></a><span class="paranum">109</span>, 304. <span class="bld">Āya catutthekavacanassatu</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato catutthekavacanassa <span class="bld">āyā</span>deso hoti vā.</p>\r\n<p class="bodytext">Atthāya hitāya sukhāya devamanussānaṃ buddho loke uppajjati.</p>\r\n<p class="bodytext"><span class="bld">Ato</span>ti kimatthaṃ? Isissa.</p>\r\n<p class="bodytext"><span class="bld">Catutthī</span>ti kimatthaṃ? Purisassa mukhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanasse</span>ti kimatthaṃ? Purisānaṃ dadāti.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Dātā hoti samaṇassa vā brāhmaṇassa vā.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇenattha</span>ñca hoti. Atthatthaṃ, hitatthaṃ, sukhatthaṃ.</p>\r\n<p class="bodytext"><a name="para110"></a><span class="paranum">110</span>, 201. <span class="bld">Tayo neva ca sabbanāmehi</span>.</p>\r\n<p class="bodytext">Tehi sabbanāmehi <span class="bld">a</span>kārantehi <span class="bld">smāsmiṃ sa</span>iccetesaṃ tayo <span class="bld">ā e āyā</span>desā neva honti.</p>','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',83,replace(replace('<p class="bodytext">Sabbasmā<a name="M0.0083"></a>, sabbasmiṃ, sabbassa. Yasmā, yasmiṃ, yassa. Tasmā, tasmiṃ, tassa. Kasmā, kasmiṃ, kassa. Imasmā, imasmiṃ, imassa.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmehī</span>ti kimatthaṃ? Pāpā, pāpe, pāpāya.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para111"></a><span class="paranum">111</span>, 179. <span class="bld">Ghato nādīnaṃ</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">gha</span>to <span class="bld">nā</span>dīnamekavacanānaṃ vibhattigaṇānaṃ <span class="bld">āyā</span>deso hoti.</p>\r\n<p class="bodytext">Kaññāya kataṃ kammaṃ, kaññāya dīyate, kaññāya nissaṭaṃ vatthaṃ. Kaññāya pariggaho, kaññāya patiṭṭhitaṃ sīlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ghato</span>ti kimatthaṃ? Rattiyā, itthiyā, dhenuyā, vadhuyā.</p>\r\n<p class="bodytext"><span class="bld">Nādīna</span>miti kimatthaṃ? Kaññaṃ passati, vijjaṃ, vīṇaṃ, gaṅgaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanāna</span>miti kimatthaṃ? Sabbāsu, yāsu, tāsu, kāsu, imāsu, pabhāsu.</p>\r\n<p class="bodytext"><a name="para112"></a><span class="paranum">112</span>, 183. <span class="bld">Pato yā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">pa</span>to <span class="bld">nā</span>dīnamekavacanānaṃ vibhattigaṇānaṃ <span class="bld">yā</span>ādeso hoti.</p>','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',84,replace(replace('<p class="bodytext">Rattiyā<a name="M0.0084"></a>, itthiyā, deviyā, dhenuyā, yāguyā, vadhuyā.</p>\r\n<p class="bodytext"><span class="bld">Nādīna</span>miti kimatthaṃ? Rattī, rattiṃ, itthī, itthiṃ.</p>\r\n<p class="bodytext"><span class="bld">Pato</span>ti kimatthaṃ? Kaññāya, vīṇāya, gaṅgāya, pabhāya, sobhāya.</p>\r\n<p class="bodytext"><span class="bld">Ekavacanāna</span>miti kimatthaṃ? Rattīnaṃ, itthīnaṃ.</p>\r\n<p class="bodytext"><a name="para113"></a><span class="paranum">113</span>, 132. <span class="bld">Sakhato gasse vā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">sakha</span>to <span class="bld">ga</span>ssa <span class="bld">a</span>kāra <span class="bld">ā</span>kāra <span class="bld">i</span>kāra <span class="bld">ī</span>kāra <span class="bld">e</span>kārādesā honti vā.</p>\r\n<p class="bodytext">Bho sakha, bho sakhā, bho sakhi, bho sakhī, bho sakhe.</p>\r\n<p class="bodytext"><a name="para114"></a><span class="paranum">114</span>, 178. <span class="bld">Ghate ca</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">gha</span>to <span class="bld">ga</span>ssa <span class="bld">e</span>kārādeso hoti.</p>\r\n<p class="bodytext">Bhoti ayye, bhoti kaññe, bhoti kharādiye.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ, sanniṭṭhānaṃ.</p>\r\n<p class="bodytext"><a name="para115"></a><span class="paranum">115</span>, 181. <span class="bld">Na ammādito</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">ammā</span>dito <span class="bld">ga</span>ssa <span class="bld">e</span>kārattaṃ na hoti.</p>','\r',char(13)),'\n',char(10)),'-113-114-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',85,replace(replace('<p class="bodytext">Bhoti <a name="M0.0085"></a> ammā, bhoti annā, bhoti ambā, bhoti tātā.</p>\r\n<p class="bodytext"><span class="bld">Ammādito</span>ti kimatthaṃ? Bhoti kaññe.</p>\r\n<p class="bodytext"><a name="para116"></a><span class="paranum">116</span>, 157. <span class="bld">Akatarassā lato yvālapanassa vevo</span>.</p>\r\n<p class="bodytext">Tasmā akatarassā lato <span class="bld">yvā</span>lapanassa <span class="bld">vevo</span>ādesā honti.</p>\r\n<p class="bodytext">Bhikkhave, bhikkhavo, hetave, hetavo.</p>\r\n<p class="bodytext"><span class="bld">Akatarassā</span>ti kimatthaṃ? Sayambhuvo.</p>\r\n<p class="bodytext"><span class="bld">Lato</span>ti kimatthaṃ? Nāgiyo, dhenuyo, yāguyo.</p>\r\n<p class="bodytext"><span class="bld">Ālapanasse</span>ti kimatthaṃ? Te hetavo, te bhikkhavo.</p>\r\n<p class="bodytext"><a name="para117"></a><span class="paranum">117</span>, 124. <span class="bld">Jhalato sassano vā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">jhala</span>to sassa vibhattissa <span class="bld">no</span> ādeso hoti vā.</p>\r\n<p class="bodytext">Aggino, aggissa, sakhino, sakhissa, daṇḍino, daṇḍissa, bhikkhuno, bhikkhussa, sayambhuno, sayambhussa.</p>\r\n<p class="bodytext"><span class="bld">Sasse</span>ti kimatthaṃ? Isinā, bhikkhunā.</p>\r\n<p class="bodytext"><span class="bld">Jhalato</span>ti kimatthaṃ? Purisassa.</p>\r\n<p class="bodytext"><a name="para118"></a><span class="paranum">118</span>, 146. </p>','\r',char(13)),'\n',char(10)),'-116-117-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',86,replace(replace('<p class="bodytext"><span class="bld">Ghapato</span> <a name="M0.0086"></a> <span class="bld">ca yonaṃ lopo</span>.</p>\r\n<p class="bodytext">Tehi <span class="bld">ghapajhala</span>iccetehi <span class="bld">yonaṃ</span> lopo hoti vā.</p>\r\n<p class="bodytext">Kaññā, kaññāyo. Rattī, rattiyo, itthī, itthiyo, yāgū, yāguyo, vadhū, vadhuyo. Aggī, aggayo. Bhikkhū, bhikkhavo. Sayambhū, sayambhuvo. Aṭṭhī, aṭṭhīni, āyū, āyūni.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para119"></a><span class="paranum">119</span>, 155. <span class="bld">Lato vokāro ca</span>.</p>\r\n<p class="bodytext">Tasmā lato <span class="bld">yonaṃ vo</span>kāro hoti vā.</p>\r\n<p class="bodytext">Bhikkhavo, bhikkhū, sayambhuvo, sayambhū.</p>\r\n<p class="bodytext"><span class="bld">Kāraggahaṇaṃ</span> kimatthaṃ? <span class="bld">Yonaṃ no</span> ca hoti. Jantuno.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ, amū purisā tiṭṭhanti, amū purise passatha.</p>\r\n<p class="centered">Iti nāmakappe paṭhamo kaṇḍo.</p>\r\n<p class="subhead">Dutiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para120"></a><span class="paranum">120</span>, 243. <span class="bld">Amhassa mamaṃ sapibhattissa se</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">amha</span>saddassa savibhattissa <span class="bld">mamaṃ</span> ādeso hoti <span class="bld">se</span> vibhattimhi.</p>','\r',char(13)),'\n',char(10)),'-119-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',87,replace(replace('<p class="bodytext">Mamaṃ <a name="M0.0087"></a> dīyate purisena, mamaṃ pariggaho.</p>\r\n<p class="bodytext"><a name="para121"></a><span class="paranum">121</span>, 233. <span class="bld">Mayaṃ yomhi paṭhame</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">amha</span>saddassa savibhattissa <span class="bld">mayaṃ</span>ādeso hoti <span class="bld">yo</span>mhi paṭhame.</p>\r\n<p class="bodytext">Mayaṃ gacchāma, mayaṃ dema.</p>\r\n<p class="bodytext"><span class="bld">Amhasse</span>ti kimatthaṃ? Purisā tiṭṭhanti.</p>\r\n<p class="bodytext"><span class="bld">Yomhī</span>ti kimatthaṃ? Ahaṃ gacchāmi.</p>\r\n<p class="bodytext"><span class="bld">Paṭhame</span>ti kimatthaṃ? Amhākaṃ passasi tvaṃ.</p>\r\n<p class="bodytext"><a name="para122"></a><span class="paranum">122</span>, 99. <span class="bld">Ntussa nto</span>.</p>\r\n<p class="bodytext">Sabbasseva ntupaccayassa savibhattissa <span class="bld">nto</span>ādeso hoti <span class="bld">yo</span>mhi paṭhame.</p>\r\n<p class="bodytext">Guṇavanto tiṭṭhanti.</p>\r\n<p class="bodytext"><span class="bld">Ntusse</span>ti kimatthaṃ? Sabbe sattā gacchanti.</p>\r\n<p class="bodytext"><span class="bld">Paṭhame</span>ti kimatthaṃ? Guṇavante passanti janā.</p>\r\n<p class="bodytext"><a name="para123"></a><span class="paranum">123</span>, 103. <span class="bld">Ntassa se vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">ntassā</span>deso hoti vā <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Sīlava<span class="bld">nta</span>ssa jhāyino, sīlavato jhāyino vā.</p>\r\n<p class="bodytext"><span class="bld">Se</span>ti kimatthaṃ? Sīlavā tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para124"></a><span class="paranum">124</span>, 98. </p>','\r',char(13)),'\n',char(10)),'-121-122-123-124-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',88,replace(replace('<p class="bodytext"><span class="bld">Ā</span> <a name="M0.0088"></a> <span class="bld">simhi</span>.</p>\r\n<p class="bodytext">Sabbasseva<span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">ā</span>ādeso hoti <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Guṇavā, paññavā, sīlavā, balavā, dhanavā, matimā, satimā, dhitimā.</p>\r\n<p class="bodytext"><span class="bld">Ntusse</span>ti kimatthaṃ? Puriso tiṭṭhati.</p>\r\n<p class="bodytext"><span class="bld">Si</span>mhīti kimatthaṃ? Sīlavanto tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para125"></a><span class="paranum">125</span>, 198. <span class="bld">Aṃ napuṃsake</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">aṃ</span>ādeso hoti <span class="bld">si</span>mhi vibhattimhi napuṃsake vattamānassa.</p>\r\n<p class="bodytext">Guṇavaṃ cittaṃ tiṭṭhati, rucimaṃ pupphaṃ virocati.</p>\r\n<p class="bodytext"><span class="bld">Simhi</span>ti kimatthaṃ? Vaṇṇavantaṃ a<span class="bld">ga</span>ndhakaṃ virūḷhapupphaṃ passasi tvaṃ.</p>\r\n<p class="bodytext"><a name="para126"></a><span class="paranum">126</span>, 101. <span class="bld">Avaṇṇā ca ge</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">aṃ avaṇṇā</span> ca honti <span class="bld">ge</span> pare.</p>\r\n<p class="bodytext">Bho guṇavaṃ, bho guṇava, bho guṇavā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para127"></a><span class="paranum">127</span>, 102. </p>','\r',char(13)),'\n',char(10)),'-125-126-127-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',89,replace(replace('<p class="bodytext"><span class="bld">To</span> <a name="M0.0089"></a> <span class="bld">ti tā sa smiṃ nāsu</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">totitā</span>ādesā honti vā <span class="bld">sasmiṃ nā</span>iccetesu yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Guṇavato, guṇavantassa, guṇavati, guṇavantasmiṃ, guṇavatā, guṇavantena, satimato, satimantassa, satimati, satimantasmiṃ, satimatā, satimantena.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Guṇavā. Satimā.</p>\r\n<p class="bodytext"><a name="para128"></a><span class="paranum">128</span>, 104. <span class="bld">Naṃmhi taṃ vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa savibhattissa <span class="bld">taṃ</span>ādeso hoti vā naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Guṇavataṃ, guṇavantānaṃ, satimataṃ, satimantānaṃ.</p>\r\n<p class="bodytext">Naṃ<span class="bld">mhī</span>ti kimatthaṃ? Guṇavanto tiṭṭhanti, satimanto tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para129"></a><span class="paranum">129</span>, 222. <span class="bld">Imassidamaṃsisu napuṃsake</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ima</span>saddassa savibhattissa <span class="bld">idaṃ</span>ādeso hoti vā <span class="bld">aṃsi</span>su napuṃsake vattamānassa.</p>\r\n<p class="bodytext">Idaṃ cittaṃ passasi, idaṃ cittaṃ tiṭṭhati, imaṃ cittaṃ passasi. Imaṃ cittaṃ tiṭṭhati.</p>','\r',char(13)),'\n',char(10)),'-128-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',90,replace(replace('<p class="bodytext"><span class="bld">Nathuṃsake</span>ti <a name="M0.0090"></a> kimatthaṃ? Imaṃ purisaṃ passasi. Ayaṃ puriso tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para138"></a><span class="paranum">138</span>, 225. <span class="bld">Amussāduṃ</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">amu</span>saddassa savibhattissa <span class="bld">a</span>duṃādeso hoti <span class="bld">aṃsi</span>su napuṃsake vattamānassa.</p>\r\n<p class="bodytext">Aduṃ pupphaṃ passasi, aduṃ pupphaṃ virocati.</p>\r\n<p class="bodytext"><span class="bld">Napuṃsake</span>ti kimatthaṃ? Amuṃ rājānaṃ passasi, asu rājā tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para131"></a><span class="paranum">131</span>, 0. <span class="bld">Itthipumanapuṃsakasaṅkhyaṃ</span>.</p>\r\n<p class="bodytext">‘‘Itthipumanapuṃsakasaṅkhyaṃ’’ iccetaṃ adhikāratthaṃ veditabbaṃ.</p>\r\n<p class="bodytext"><a name="para132"></a><span class="paranum">132</span>, 228. <span class="bld">Yosu dvinnaṃ dve ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dvinnaṃ</span> saṅkhyānaṃ itthipumanapuṃsake vattamānānaṃ savibhattīnaṃ <span class="bld">dve</span> hoti <span class="bld">yo</span>iccetesu.</p>\r\n<p class="bodytext">Dve itthiyo, dve dhammā. Dve rūpāni.</p>\r\n<p class="bodytext"><span class="bld">Yo</span>svīti kimatthaṃ? Dvīsu.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena duve dvaya ubha ubhaya duvi</span> ca honti <span class="bld">yonāana</span>miccetesu. Duve samaṇā. Duve </p>','\r',char(13)),'\n',char(10)),'-138-131-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',91,replace(replace('<p class="bodytext">brāhmaṇā<a name="M0.0091"></a>, duve janā, dvayena, dvayaṃ, ubhinnaṃ, ubhayesaṃ duvinnaṃ.</p>\r\n<p class="bodytext"><a name="para133"></a><span class="paranum">133</span>, 230. <span class="bld">Ti catunnaṃ tisso catasso tayo cattāro tīṇi cattāri</span>.</p>\r\n<p class="bodytext"><span class="bld">Ticatunnaṃ</span> saṅkhyānaṃ itthipumanapuṃsake vattamānānaṃ savibhattīnaṃ <span class="bld">tisso catasso tayo cattāro tīṇi cattāri</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">yo</span>iccetesu.</p>\r\n<p class="bodytext">Tisso vedanā catasso disā, tayo janā, jane, cattāro purisā, purise, tīṇi āyatanāni, cattāri ariyasaccāni.</p>\r\n<p class="bodytext"><span class="bld">Yosvī</span>ti kimatthaṃ? Tīsu, catūsu.</p>\r\n<p class="bodytext"><a name="para134"></a><span class="paranum">134</span>, 251. <span class="bld">Pañcādīnamakāro</span>.</p>\r\n<p class="bodytext"><span class="bld">Pañcā</span>dīnaṃ saṅkhyānaṃ itthipumanapuṃsake vattamānānaṃ savibhattissa antassa sarassa <span class="bld">a</span>kāro hoti <span class="bld">yo</span>iccetesu.</p>\r\n<p class="bodytext">Pañca, pañca, cha, cha, satta, satta, aṭṭha, aṭṭha, nava, nava, dasa, dasa.</p>\r\n<p class="bodytext"><span class="bld">Pañcādīna</span>miti kimatthaṃ? Dve, tayo.</p>\r\n<p class="bodytext"><a name="para135"></a><span class="paranum">135</span>, 118. </p>','\r',char(13)),'\n',char(10)),'-133-134-135-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',92,replace(replace('<p class="bodytext"><span class="bld">Rājassa</span> <a name="M0.0092"></a> <span class="bld">rañño rājino se</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">rāja</span>saddassa savibhattissa <span class="bld">rañño rājino</span>iccete ādesā honti <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Rañño, rājino.</p>\r\n<p class="bodytext"><span class="bld">Se</span>ti kimatthaṃ? Raññā.</p>\r\n<p class="bodytext"><a name="para136"></a><span class="paranum">136</span>, 119. <span class="bld">Raññaṃ naṃmhi vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">rāja</span>saddassa savibhattissa <span class="bld">raññaṃ</span>ādeso hoti vā <span class="bld">naṃ</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Raññaṃ, rājūnaṃ idaṃ raṭṭhaṃ.</p>\r\n<p class="bodytext"><a name="para137"></a><span class="paranum">137</span>, 116. <span class="bld">Nāmhiraññā vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">rāja</span>saddassa savibhattissa <span class="bld">raññā</span>ādeso hoti vā <span class="bld">nā</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Tena raññā kataṃ. Rājena vā kataṃ.</p>\r\n<p class="bodytext"><span class="bld">Nāmhī</span>ti kimatthaṃ? Rañño santakaṃ.</p>\r\n<p class="bodytext"><a name="para138"></a><span class="paranum">138</span>, 121. <span class="bld">Smiṃmhi raññe rājini</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">rāja</span>saddassa savibhattissa <span class="bld">raññerājini</span>iccete ādesā honti <span class="bld">smiṃ</span>mhivibhattimhi.</p>\r\n<p class="bodytext">Raññe, rājini sīlaṃ tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para139"></a><span class="paranum">139</span>, 245. </p>','\r',char(13)),'\n',char(10)),'-136-137-138-139-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',93,replace(replace('<p class="bodytext"><span class="bld">Tumhākaṃ</span> <a name="M0.0093"></a> <span class="bld">tayimayi</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span> saddānaṃ savibhattīnaṃ <span class="bld">tayi mayi</span>iccete ādeso honti yathāsaṅkhyaṃ <span class="bld">smiṃ</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Tayi, mayi.</p>\r\n<p class="bodytext"><span class="bld">Smiṃmhī</span>ti kimatthaṃ? Tvaṃ bhavasi, ahaṃ bhavāmi.</p>\r\n<p class="bodytext"><a name="para140"></a><span class="paranum">140</span>, 232. <span class="bld">Tvamahaṃ simhi ca</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">tvaṃ ahaṃ</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Tvaṃ, ahaṃ.</p>\r\n<p class="bodytext"><span class="bld">Simhi</span>ti kimatthaṃ? Tayi, mayi.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena tuvaṃ</span> ca hoti. Tuvaṃ satthā.</p>\r\n<p class="bodytext"><a name="para141"></a><span class="paranum">141</span>, 241. <span class="bld">Tava mamase</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">tavamama</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Tava, mama.</p>\r\n<p class="bodytext"><span class="bld">Se</span>ti kimatthaṃ? Tayi, mayi.</p>\r\n<p class="bodytext"><a name="para142"></a><span class="paranum">142</span>, 242. </p>','\r',char(13)),'\n',char(10)),'-140-141-142-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',94,replace(replace('<p class="bodytext"><span class="bld">Tuyhaṃ</span> <a name="M0.0094"></a> <span class="bld">mayhañca</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">tuyhaṃ mayhaṃ</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">se</span> vibhattimhi.</p>\r\n<p class="bodytext">Tuyhaṃ, mayhaṃ dhanaṃ dīyate.</p>\r\n<p class="bodytext"><span class="bld">Se</span>ti kimatthaṃ? Tayā, mayā.</p>\r\n<p class="bodytext"><a name="para143"></a><span class="paranum">143</span>, 235. <span class="bld">Taṃ mamaṃmhi</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">taṃ ma</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">aṃ</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Taṃ, maṃ.</p>\r\n<p class="bodytext"><span class="bld">Aṃmhī</span>ti kimatthaṃ? Tayā mayā.</p>\r\n<p class="bodytext"><a name="para144"></a><span class="paranum">144</span>, 234. <span class="bld">Tavaṃ mamañca navā</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">tavaṃ mamaṃ</span>iccete ādesā honti navā yathāsaṅkhyaṃ <span class="bld">aṃ</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Tavaṃ, mamaṃ passati.</p>\r\n<p class="bodytext"><span class="bld">Navā</span>ti kimatthaṃ? Taṃ, maṃ passati.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para145"></a><span class="paranum">145</span>, 238. <span class="bld">Nāmhī tayā mayā</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ <span class="bld">tayā mayā</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">nā</span>mhi vibhattimhi.</p>','\r',char(13)),'\n',char(10)),'-143-144-145-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',95,replace(replace('<p class="bodytext">Tayā<a name="M0.0095"></a>, mayā kataṃ.</p>\r\n<p class="bodytext"><span class="bld">Nāmhi</span>ti kimatthaṃ? Tumhehi, amhehi.</p>\r\n<p class="bodytext"><a name="para146"></a><span class="paranum">146</span>, 236. <span class="bld">Tumhassa tuvaṃ tvamaṃmhi</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">tumha</span>saddassa savibhattissa <span class="bld">tuvaṃ tvaṃ</span> iccete ādesā honti aṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Kaliṅgarassa tuvaṃ maññe, kaṭṭhassa tvaṃ maññe.</p>\r\n<p class="bodytext"><a name="para147"></a><span class="paranum">147</span>, 246. <span class="bld">Padato dutiyā catutthī chaṭṭhīsu vono</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumhaamha</span>saddānaṃ savibhattīnaṃ yadā padasmā paresaṃ <span class="bld">vo no</span>ādesā honti navā yathāsaṅkhyaṃ <span class="bld">dutiyā catutthīchaṭṭhī</span>iccetesu bahuvacanesu.</p>\r\n<p class="bodytext">Pahāya vo bhikkhave gamissāmi, mā no ajja vikantiṃsu, rañño sūdā mahānase, evaṃ <span class="bld">dutiyatthe</span>.</p>\r\n<p class="bodytext">Dhammaṃ vo bhikkhave desessāmi, saṃvibhajetha no rajjena, evaṃ <span class="bld">catutthyatthe</span>.</p>\r\n<p class="bodytext">Tuṭṭhosmi vo bhikkhave pakatiyā, satthā no bhagavā anuppatto, evaṃ <span class="bld">chaṭṭhyatthe</span>.</p>','\r',char(13)),'\n',char(10)),'-146-147-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',96,replace(replace('<p class="bodytext"><span class="bld">Navā</span>ti <a name="M0.0096"></a> kimatthaṃ? Eso amhākaṃ satthā.</p>\r\n<p class="bodytext"><span class="bld">Tumhamhāka</span>miti kimatthaṃ? Ete isayo passasi.</p>\r\n<p class="bodytext"><span class="bld">Padato</span>ti kimatthaṃ? Tumhākaṃ satthā.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Gacchatha tumhe.</p>\r\n<p class="bodytext"><a name="para148"></a><span class="paranum">148</span>, 247. <span class="bld">Temekavacanesu ca</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ yadā padasmā paresaṃ <span class="bld">te me</span> ādesā honti yathāsaṅkhyaṃ <span class="bld">catu</span>tthī<span class="bld">chaṭṭhī</span>iccetesu ekavacanesu.</p>\r\n<p class="bodytext">Dadāmi te gāmavarāni pañca, dadāhi me gāmavaraṃ, idaṃ te raṭṭhaṃ, ayaṃ me putto.</p>\r\n<p class="bodytext"><span class="bld">Padato</span>ti kimatthaṃ? Tava ñāti, mama ñāti.</p>\r\n<p class="bodytext"><a name="para149"></a><span class="paranum">149</span>, 148. <span class="bld">Na aṃmhi</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumha amha</span>saddānaṃ savibhattīnaṃ yadā padasmā paresaṃ <span class="bld">te me</span>ādesā na honti <span class="bld">aṃ</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Passeyya taṃ vassasataṃ arogaṃ, so maṃ bravīti.</p>\r\n<p class="bodytext"><a name="para150"></a><span class="paranum">150</span>, 249. </p>','\r',char(13)),'\n',char(10)),'-148-149-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',97,replace(replace('<p class="bodytext"><span class="bld">Vā</span> <a name="M0.0097"></a> <span class="bld">tatiye ca</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumhaamha</span>saddānaṃ savibhattīnaṃ yadā padasmā paresaṃ <span class="bld">teme</span>ādesā honti vā yathāsaṅkhyaṃ tatiyekavacane pare.</p>\r\n<p class="bodytext">Kataṃ te pāpaṃ, kataṃ me pāpaṃ, kataṃ tayā pāpaṃ, kataṃ mayā pāpaṃ.</p>\r\n<p class="bodytext"><span class="bld">Padato</span>ti kimatthaṃ? Tayā kataṃ, mayā kataṃ.</p>\r\n<p class="bodytext"><a name="para151"></a><span class="paranum">151</span>, 250. <span class="bld">Bahuvacanesu vono</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">tumhaamha</span>saddānaṃ savibhattīnaṃ yadā padasmā paresaṃ <span class="bld">vono</span>ādesā honti yathāsaṅkhyaṃ tatiyābahuvacanesu paresu.</p>\r\n<p class="bodytext">Kataṃ vo kammaṃ, kataṃ no kammaṃ.</p>\r\n<p class="bodytext"><span class="bld">Padato</span>ti kimatthaṃ? Tumhehi kataṃ, amhehi kataṃ.</p>\r\n<p class="bodytext"><span class="bld">Bahuvacanaggahaṇena yo</span>mhi paṭhame <span class="bld">vo no</span>ādesā honti. Gāmaṃ vo gaccheyyātha, gāmaṃ no gaccheyyāma.</p>\r\n<p class="bodytext"><a name="para152"></a><span class="paranum">152</span>, 236. <span class="bld">Pumantassā simhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa savibhattissa <span class="bld">ā</span>ādeso hoti <span class="bld">si</span>mhi vibhattimhi.</p>','\r',char(13)),'\n',char(10)),'-151-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',98,replace(replace('<p class="bodytext">Pumā <a name="M0.0098"></a> tiṭṭhati.</p>\r\n<p class="bodytext"><span class="bld">Si</span>mhīti kimatthaṃ? Pumāno tiṭṭhanti.</p>\r\n<p class="bodytext"><span class="bld">Antaggahaṇena maghava yuva</span>iccevamādīnamantassasavibhattissa <span class="bld">ā</span>ādeso hoti. Maghavā, yuvā.</p>\r\n<p class="bodytext"><a name="para153"></a><span class="paranum">153</span>, 138. <span class="bld">Amālapanekavacane</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa savibhattissa <span class="bld">aṃ</span>ādeso hoti ālapanekavacane pare.</p>\r\n<p class="bodytext">He pumaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ālapane</span>ti kimatthaṃ? Pumā.</p>\r\n<p class="bodytext"><span class="bld">Ekavacane</span>ti kimatthaṃ? He pumāno.</p>\r\n<p class="bodytext"><a name="para154"></a><span class="paranum">154</span>, 0. <span class="bld">Samāse ca vibhāsā</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa samāse ca aṃādeso hoti vibhāsā samāse kate.</p>\r\n<p class="bodytext">Itthī ca pumā ca napuṃsakaṃ ca itthipumanapuṃsakāni. Itthipumanapuṃsakānaṃ samūho itthipumanapuṃsakasamūho.</p>\r\n<p class="bodytext"><span class="bld">Vibhāsā</span>ti kimatthaṃ? Itthipumanapuṃsakāni.</p>\r\n<p class="bodytext"><a name="para155"></a><span class="paranum">155</span>, 137. </p>','\r',char(13)),'\n',char(10)),'-153-154-155-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',99,replace(replace('<p class="bodytext"><span class="bld">Yosvāno</span><a name="M0.0099"></a>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa savibhattissa <span class="bld">āno</span>ādeso hoti <span class="bld">yo</span>su vibhattīsu.</p>\r\n<p class="bodytext">Pumāno, he pumāno.</p>\r\n<p class="bodytext"><span class="bld">Yosvī</span>ti kimatthaṃ? Pumā.</p>\r\n<p class="bodytext"><a name="para156"></a><span class="paranum">156</span>, 142. <span class="bld">Āne smiṃmhi vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa savibhattissa <span class="bld">āne</span> ādeso hoti vā <span class="bld">smiṃmhi</span> vibhattimhi.</p>\r\n<p class="bodytext">Pumāne, pume vā.</p>\r\n<p class="bodytext"><a name="para157"></a><span class="paranum">157</span>, 140. <span class="bld">Hivibhattimhi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa <span class="bld">hi</span>vibhattimhi ca <span class="bld">āne</span>ādeso hoti.</p>\r\n<p class="bodytext">Pumānehi, pumānebhi.</p>\r\n<p class="bodytext">Puna <span class="bld">vibhattiggahaṇaṃ</span> kimatthaṃ? <span class="bld">Savibhatti</span>ggahaṇanivattanatthaṃ. Pumānehi.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena maghava yuva</span>iccevamādīnamantassa <span class="bld">āna</span>ādeso hoti <span class="bld">si yo aṃyo</span> iccetesu vibhattīsu, <span class="bld">pumakammathāmanta</span>ssa <span class="bld">cu</span>kāro hoti <span class="bld">sasmā</span> su vibhattīsu. Maghavāno, maghavānā. Maghavānaṃ, </p>','\r',char(13)),'\n',char(10)),'-156-157-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',100,replace(replace('<p class="bodytext">maghavāne<a name="M0.0100"></a>. Yuvāno, yuvānā, yuvānaṃ, yuvāne, pumuno, pumunā. Kammuno, kammunā, thāmuno, thāmunā.</p>\r\n<p class="bodytext"><a name="para158"></a><span class="paranum">158</span>, 143. <span class="bld">Susmimā vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa <span class="bld">su</span>iccetasmiṃvibhattimhi <span class="bld">ā</span>ādeso hoti vā.</p>\r\n<p class="bodytext">Pumāsu, pumesu vā.</p>\r\n<p class="bodytext"><a name="para156"></a><span class="paranum">156</span>, 139. <span class="bld">Unāmhi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccevamantassa <span class="bld">āu</span>ādesā honti vā <span class="bld">nāmhi</span> vibhattimhi.</p>\r\n<p class="bodytext">Pumānā, pumunā, pumena vā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para160"></a><span class="paranum">160</span>, 167. <span class="bld">A kammantassa ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Kamma</span>iccevamantassa ca <span class="bld">ua</span> ādesā honti vā <span class="bld">nāmhi</span> vibhattimhi.</p>\r\n<p class="bodytext">Kammunā, kammanā, kammena vā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena maghavayuva</span>iccevamantassa <span class="bld">ā</span>āde so hoti kvaci <span class="bld">nāsu</span>iccetesu vibhattīsu. Maghavānā, maghavāsu, maghavesu, maghavena vā. Yuvānā, yuvāsu, yuvesu, yuvena vā.</p>\r\n<p class="centered">Iti nāmakappe dutiyo kaṇḍo.</p>\r\n<p class="subhead">Tatiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para161"></a><span class="paranum">161</span>, 244. </p>','\r',char(13)),'\n',char(10)),'-158-156-160-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',101,replace(replace('<p class="bodytext"><span class="bld">Tumha’mhehi</span> <a name="M0.0101"></a> <span class="bld">namākaṃ</span>.</p>\r\n<p class="bodytext">Tehi <span class="bld">tumhaamhe</span>hi naṃvacanassa <span class="bld">ākaṃ</span> hoti.</p>\r\n<p class="bodytext">Tumhākaṃ, amhākaṃ.</p>\r\n<p class="bodytext">Namiti kimatthaṃ? Tumhehi, amhehi.</p>\r\n<p class="bodytext"><a name="para162"></a><span class="paranum">162</span>, 237. <span class="bld">Vā yvappaṭhamo</span>.</p>\r\n<p class="bodytext">Tehi <span class="bld">tumhaamhe</span>hi <span class="bld">yo</span> appaṭhamo <span class="bld">ākaṃ</span>hoti vā.</p>\r\n<p class="bodytext">Tumhākaṃ passāmi, tumhe passāmi vā. Amhākaṃ passasi, amhe passasi vā.</p>\r\n<p class="bodytext"><span class="bld">Yo</span>ti kimatthaṃ? Tumhehi, amhehi.</p>\r\n<p class="bodytext"><span class="bld">Appaṭhamo</span>ti kimatthaṃ? Gacchatha tumhe, gacchāma mayaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vātivikappanatthena yonaṃ aṃ ānaṃ</span> honti. Tumhaṃ tumhānaṃ. Amhaṃ, amhānaṃ.</p>\r\n<p class="bodytext"><a name="para163"></a><span class="paranum">163</span>, 240. <span class="bld">Sassaṃ</span>.</p>\r\n<p class="bodytext">Tehi <span class="bld">humhaamhe</span>hi <span class="bld">sa</span>ssa vibhattissa <span class="bld">aṃ</span> ādeso hoti vā.</p>\r\n<p class="bodytext">Tumhaṃ dīyate, tava dīyate. Tumhaṃ pariggaho, tava pariggaho. Amhaṃ dīyate, mama dīyate. Amhaṃ pariggaho, mama pariggaho.</p>','\r',char(13)),'\n',char(10)),'-162-163-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',102,replace(replace('<p class="bodytext"><span class="bld">Sasse</span>ti <a name="M0.0102"></a> kimatthaṃ? Tumhesu, amhesu.</p>\r\n<p class="bodytext"><a name="para164"></a><span class="paranum">164</span>, 200. <span class="bld">Sabbanāma’kārate paṭhamo</span>.</p>\r\n<p class="bodytext">Sabbesaṃ sabbanāmānaṃ <span class="bld">a</span>kārato <span class="bld">yo</span> paṭhamo <span class="bld">e</span>ttamāpajjate.</p>\r\n<p class="bodytext">Sabbe, ye, te, ke, tumhe, amhe, ime.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmā</span>ti kimatthaṃ? Devā, asurā, nāgā, gandhabbā, manussā.</p>\r\n<p class="bodytext"><span class="bld">Akārato</span>ti kimatthaṃ? Amū purisā tiṭṭhanti.</p>\r\n<p class="bodytext"><span class="bld">Yo</span>ti kimatthaṃ? Sabbo, yo, so, ko, ayaṃ.</p>\r\n<p class="bodytext"><span class="bld">Paṭhamaggahaṇaṃ</span> uttarasuttatthaṃ.</p>\r\n<p class="bodytext"><a name="para165"></a><span class="paranum">165</span>, 208. <span class="bld">Dvandaṭṭhā vā</span>.</p>\r\n<p class="bodytext">Tasmā sabbanāma’kārato dvandaṭṭhā <span class="bld">yo</span> paṭhamo <span class="bld">e</span>ttamāpajjate vā.</p>\r\n<p class="bodytext">Katarakatame, katarakatamā vā.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmā</span>ti kimatthaṃ? Devāsuranāga gandhabbamanussā.</p>\r\n<p class="bodytext"><span class="bld">Dvandaṭṭhā</span>ti kimatthaṃ te, sabbe.</p>\r\n<p class="bodytext"><a name="para166"></a><span class="paranum">166</span>, 209. </p>','\r',char(13)),'\n',char(10)),'-164-165-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',103,replace(replace('<p class="bodytext"><span class="bld">Nāññaṃ</span> <a name="M0.0103"></a> <span class="bld">sabbanāmikaṃ</span>.</p>\r\n<p class="bodytext">Sabbanāmikānaṃ dvandaṭṭhe nāññaṃ kāriyaṃ hoti,</p>\r\n<p class="bodytext">Pubbāparānaṃ, pubbuttarānaṃ, adharuttarānaṃ.</p>\r\n<p class="bodytext"><a name="para167"></a><span class="paranum">167</span>, 210. <span class="bld">Bahubbīhimhi ca</span>.</p>\r\n<p class="bodytext">Bahubbīhimhi ca samāse sabbanāmavidhānañca nāññaṃ kāriyaṃ hoti.</p>\r\n<p class="bodytext">Piyapubbāya, piyapubbānaṃ, piyapubbe, piyapubbassa.</p>\r\n<p class="bodytext"><span class="bld">Ce</span>ti kimatthaṃ? Sabbanāmavidhānaṃ hoti, dakkhiṇa pubbassaṃ, dakkhiṇapubbassā, uttarapubbassaṃ, uttarapubbassā.</p>\r\n<p class="bodytext"><a name="para168"></a><span class="paranum">168</span>, 203. <span class="bld">Sabbato naṃ saṃ sānaṃ</span>.</p>\r\n<p class="bodytext">Sabbato sabbanāmato naṃvacanassa <span class="bld">saṃsā</span>naṃiccete ādesā honti.</p>\r\n<p class="bodytext">Sabbesaṃ, sabbesānaṃ, sabbāsaṃ, sabbāsānaṃ. Yesaṃ, yesānaṃ, yāsaṃ, yāsānaṃ. Tesaṃ, tesānaṃ, tāsaṃ, tāsānaṃ. Kesaṃ, kesānaṃ, kāsaṃ, kāsānaṃ. Imesaṃ, imesānaṃ, imāsaṃ, imāsānaṃ. Amūsaṃ, amūsānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Na</span>miti kimatthaṃ? Sabbassa, yassa, tassa, kassa. Evaṃ sabbattha.</p>\r\n<p class="bodytext"><a name="para169"></a><span class="paranum">169</span>, 117. </p>','\r',char(13)),'\n',char(10)),'-167-168-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',104,replace(replace('<p class="bodytext"><span class="bld">Rājassa</span> <a name="M0.0104"></a> <span class="bld">rāju sunaṃhisu ca</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">rāja</span>saddassa <span class="bld">rāju</span>ādeso hoti <span class="bld">sunaṃhi</span>iccetesu.</p>\r\n<p class="bodytext">Rājūsu, rājūnaṃ, rājūhi, rājūbhi.</p>\r\n<p class="bodytext"><span class="bld">Sunaṃhisū</span>ti kimatthaṃ? Rājā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ. Rājesu, rājānaṃ, rājehi rājebhi.</p>\r\n<p class="bodytext"><a name="para170"></a><span class="paranum">170</span>, 220. <span class="bld">Sabbassimasse vā</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ima</span>saddassa <span class="bld">e</span>kāro hoti vā <span class="bld">sunaṃhi</span>iccetesu.</p>\r\n<p class="bodytext">Esu, imesu, esaṃ, imesaṃ, ehi, ebhi, imehi, imebhi.</p>\r\n<p class="bodytext"><span class="bld">Imasse</span>ti kimatthaṃ? Etesu, etesaṃ, etehi, etebhi.</p>\r\n<p class="bodytext"><a name="para171"></a><span class="paranum">171</span>, 219. <span class="bld">Animi nāmhi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>saddassa sabbasseva <span class="bld">ana imi</span>iccete ādesā honti nāmhi vibhattimhi.</p>\r\n<p class="bodytext">Anena dhammadānena. Sukhitā hotu sā pajā.</p>\r\n<p class="bodytext">Iminā buddhapūjena, patvāna amataṃ padaṃ.</p>\r\n<p class="bodytext"><span class="bld">Nāmhī</span>ti kimatthaṃ? Imesu, imesaṃ, imehi, imebhi.</p>\r\n<p class="bodytext"><a name="para172"></a><span class="paranum">172</span>, 218. </p>','\r',char(13)),'\n',char(10)),'-170-171-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',105,replace(replace('<p class="bodytext"><span class="bld">Anapuṃsakassā</span> <a name="M0.0105"></a> <span class="bld">yaṃ simhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>saddassa sabbasseva anapuṃsakassa <span class="bld">ayaṃ</span>ādeso hoti <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Ayaṃ puriso, ayaṃ itthī.</p>\r\n<p class="bodytext"><span class="bld">Anapuṃsakasse</span>ti kimatthaṃ? Idaṃ cittaṃ tiṭṭhati.</p>\r\n<p class="bodytext"><span class="bld">Simhi</span>ti kimatthaṃ? Imaṃ purisaṃ passasi tvaṃ.</p>\r\n<p class="bodytext"><a name="para173"></a><span class="paranum">173</span>, 223. <span class="bld">Amussa mo saṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Amu</span>saddassa anapuṃsakassa <span class="bld">ma</span>kāro <span class="bld">sa</span>kāramāpajjate vā <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Asu rājā, asu itthī, amuko rājā, amukā itthī.</p>\r\n<p class="bodytext"><span class="bld">Anapuṃsakasse</span>ti kimatthaṃ? Aduṃ pupphaṃ virocati.</p>\r\n<p class="bodytext"><span class="bld">Amusse</span>ti kimatthaṃ? Ayaṃ puriso tiṭṭhati.</p>\r\n<p class="bodytext"><span class="bld">Simhi</span>ti kimatthaṃ? Amhaṃ purisaṃ passasi.</p>\r\n<p class="bodytext"><a name="para174"></a><span class="paranum">174</span>, 211. <span class="bld">Etatesaṃ to</span>.</p>\r\n<p class="bodytext"><span class="bld">Eta ta</span>iccetesaṃ anapuṃsakānaṃ <span class="bld">ta</span>kāro <span class="bld">sa</span>kāramāpajjate <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Eso puriso, esā itthī, so puriso, sā itthī.</p>','\r',char(13)),'\n',char(10)),'-173-174-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',106,replace(replace('<p class="bodytext"><span class="bld">Etatesa</span>miti <a name="M0.0106"></a> kimatthaṃ? Itaro puriso, itarā itthī.</p>\r\n<p class="bodytext"><span class="bld">Anapuṃsakāna</span>miti kimatthaṃ? Etaṃ cittaṃ, etaṃ rūpaṃ. Taṃ cittaṃ, taṃ rūpaṃ.</p>\r\n<p class="bodytext"><a name="para157"></a><span class="paranum">157</span>, 212. <span class="bld">Tassa vā nattaṃ sabbattha</span>.</p>\r\n<p class="bodytext"><span class="bld">Ta</span>ssa sabbanāmassa <span class="bld">ta</span>kārassa <span class="bld">na</span>ttaṃ hoti vā sabbattha liṅgesu.</p>\r\n<p class="bodytext">Nāya, tāya, naṃ, taṃ, ne, te, nesu, tesu, namhi, tamhi, nāhi, tāhi, nābhi, tābhi.</p>\r\n<p class="bodytext"><a name="para176"></a><span class="paranum">176</span>, 213. <span class="bld">Sasmāsmiṃsaṃsāsvattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ta</span>ssa sabbanāmassa <span class="bld">ta</span>kārassa sabbasseva <span class="bld">a</span>ttaṃ hoti vā <span class="bld">sasmāsmiṃ saṃsā</span>iccetesu sabbattha liṅgesu.</p>\r\n<p class="bodytext">Assa, tassa, asmā, tasmā, asmiṃ, tasmiṃ, assaṃ, tassaṃ, assā, tassā.</p>\r\n<p class="bodytext"><span class="bld">Takārasse</span>ti kimatthaṃ? Amussaṃ, amussā.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Nesu, tesu.</p>\r\n<p class="bodytext"><a name="para177"></a><span class="paranum">177</span>, 221. <span class="bld">Imasaddassa</span> ca.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>saddassa ca sabbasseva <span class="bld">a</span>ttaṃ hoti vā <span class="bld">sasmāsmiṃ saṃ sā</span>iccetesu sabbattha liṅgesu.</p>','\r',char(13)),'\n',char(10)),'-157-176-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',107,replace(replace('<p class="bodytext">Assa<a name="M0.0107"></a>, imassa, asmā, imasmā, asmiṃ, imasmiṃ, assaṃ, imissaṃ, assā, imissā.</p>\r\n<p class="bodytext"><span class="bld">Imasaddasse</span>ti kimatthaṃ? Etissaṃ, etissā.</p>\r\n<p class="bodytext"><a name="para178"></a><span class="paranum">178</span>, 22. <span class="bld">Sabbato ko</span>.</p>\r\n<p class="bodytext">Sabbato sabbanāmato <span class="bld">ka</span>kārāgamo hoti vā <span class="bld">si</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Sabbako, yako, sako, amuko, asuko.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Sabbo, yo, so, ko.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmato</span>ti kimatthaṃ? Puriso.</p>\r\n<p class="bodytext">Puna <span class="bld">sabbatoggahaṇena</span> aññasmāpi <span class="bld">ka</span>kārāgamo hoti, hīnako, potako.</p>\r\n<p class="bodytext"><a name="para179"></a><span class="paranum">179</span>, 204. <span class="bld">Yapato smiṃsānaṃ saṃsā</span>.</p>\r\n<p class="bodytext">Sabbato sabbanāmato <span class="bld">ghapa</span>saññato <span class="bld">smiṃsa</span>iccetesaṃ <span class="bld">saṃsā</span> ādesā honti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Sabbassaṃ, sabbassā, sabbāyaṃ, sabbāya, imissaṃ, imissā, imāyaṃ, imāya, amussaṃ, amussā, amuyaṃ, amuyā.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmato</span>ti kimatthaṃ? Itthiyaṃ, itthiyā.</p>\r\n<p class="bodytext"><span class="bld">Smiṃsāna</span>miti kimatthaṃ? Amuyo.</p>\r\n<p class="bodytext"><a name="para180"></a><span class="paranum">180</span>, 207. </p>','\r',char(13)),'\n',char(10)),'-178-179-180-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',108,replace(replace('<p class="bodytext"><span class="bld">Netāhi</span> <a name="M0.0108"></a> <span class="bld">smimāya yā</span>.</p>\r\n<p class="bodytext">Etehi sabbanāmehi <span class="bld">gha</span>pasaññehi <span class="bld">smiṃ</span>vacanassa neva <span class="bld">āya yā</span>ādesā honti.</p>\r\n<p class="bodytext">Etissaṃ, etāyaṃ, imissaṃ, imāyaṃ, amussaṃ, amuyaṃ.</p>\r\n<p class="bodytext"><span class="bld">Smiṃ</span>nti kimatthaṃ? Tāya itthiyā mukhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Etāhī</span>ti kimatthaṃ? Kaññāya, vīṇāya, gaṅgāya, kapālikāya.</p>\r\n<p class="bodytext"><a name="para181"></a><span class="paranum">181</span>, 95. <span class="bld">Manogaṇādito smiṃnānamiā</span>.</p>\r\n<p class="bodytext">Tasmā manogaṇādito <span class="bld">smiṃnā</span>iccetesaṃ <span class="bld">i</span>kāra<span class="bld">ā</span>kārādesā honti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Manasi, manusmiṃ, sirasi, sirasmiṃ, manasā, manena, vacasā, vacena, sirasā, sirena, sarasā, sarena, tapasā, tapena, vayasā, vayena, yasasā, yasena, tejasā, tejena, urasā, urena, thāmasā, thāmena.</p>\r\n<p class="bodytext"><span class="bld">Smiṃnāna</span>miti kimatthaṃ? Mano, siro, tamo, tapo, tejo.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇena</span> aññāsmāpi <span class="bld">smiṃnā</span>naṃ <span class="bld">i</span>kāra<span class="bld">ā</span>kārādesā honti, bilasi, bilasā, padasi, padasā.</p>\r\n<p class="bodytext"><a name="para182"></a><span class="paranum">182</span>, 97. </p>','\r',char(13)),'\n',char(10)),'-181-182-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',109,replace(replace('<p class="bodytext"><span class="bld">Sassa</span> <a name="M0.0109"></a> <span class="bld">co</span>.</p>\r\n<p class="bodytext">Tasmā manogaṇādito <span class="bld">sa</span>ssa ca <span class="bld">o</span>kāro hoti.</p>\r\n<p class="bodytext">Manaso, thāmaso, tapaso.</p>\r\n<p class="bodytext"><a name="para183"></a><span class="paranum">183</span>, 48. <span class="bld">Etesamo lope</span>.</p>\r\n<p class="bodytext">Etesaṃ manogaṇādīnaṃ anto <span class="bld">o</span>ttamāpajjate vibhattilope kate.</p>\r\n<p class="bodytext">Manomayaṃ, ayomayaṃ, tejosamena, tapoguṇena, siroruhena.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇaṃ</span>kimatthaṃ? Aññesamanto <span class="bld">o</span>ttamāpajjate. Āposamena, vāyosamena.</p>\r\n<p class="bodytext"><span class="bld">Lope</span>ti kimatthaṃ? Padayā, tapasā, yasasā, vacasā, manasā, evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para184"></a><span class="paranum">184</span>, 96. <span class="bld">Sa sare vāgamo</span>.</p>\r\n<p class="bodytext">Eteheva manogaṇādīhi vibhattādese sare pare <span class="bld">sa</span>kārāgamo hoti vā.</p>\r\n<p class="bodytext">Manasā, vacasā, manasi, vacasi.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Manena, tejena, vasena,</p>','\r',char(13)),'\n',char(10)),'-183-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',110,replace(replace('<p class="bodytext"><span class="bld">Sare</span>ti <a name="M0.0110"></a> kimatthaṃ? Mano, tejo, yaso.</p>\r\n<p class="bodytext">Puna <span class="bld">ādiggahaṇena</span> aññasmimpi paccaye pare <span class="bld">sa</span>kārāgamo hoti. Mānasikaṃ, vācasikaṃ.</p>\r\n<p class="bodytext"><a name="para185"></a><span class="paranum">185</span>, 112. <span class="bld">Santasaddassa so te bo cante</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">sa</span>ntasaddassa <span class="bld">sa</span>kārādeso hoti <span class="bld">bha</span>kāre pare, ante ca <span class="bld">ba</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Sabbhireva samāsetha,</p>\r\n<p class="bodytext">Sabbhikubbetha santhavaṃ.</p>\r\n<p class="bodytext">Sataṃ saddhammamaññāya,</p>\r\n<p class="bodytext">Seyyo hoti na pāpiyo.</p>\r\n<p class="bodytext">Jīranti ve rājarathā sucittā.</p>\r\n<p class="bodytext">Atho sarīrampi jaraṃ upeti.</p>\r\n<p class="bodytext">Satañca dhammo na jaraṃ upeti,</p>\r\n<p class="bodytext">Santo have sabbhi pavedayanti.</p>\r\n<p class="bodytext">Sabbhūto, sabbhāvo.</p>\r\n<p class="bodytext"><span class="bld">Bhe</span>ti kimatthaṃ? Santehi pūjito bhagavā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> kvaci <span class="bld">sa</span>kārasseva pasiddhatthaṃ. Sakkāro, sakkato.</p>\r\n<p class="bodytext"><a name="para196"></a><span class="paranum">196</span>, 107. </p>','\r',char(13)),'\n',char(10)),'-185-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',111,replace(replace('<p class="bodytext"><span class="bld">Simhigacchantādīnaṃ</span> <a name="M0.0111"></a> <span class="bld">ntasaddo aṃ</span>.</p>\r\n<p class="bodytext">Simhi <span class="bld">gacchantā</span>dīnaṃ ntasaddo <span class="bld">a</span>māpajjate vā.</p>\r\n<p class="bodytext">Gacchaṃ, gacchanto, mahaṃ, mahanto, caraṃ, caranto, khādaṃ, khādanto.</p>\r\n<p class="bodytext"><span class="bld">Gacchantādīnami</span>ti kimatthaṃ? Anto, danto, vanto, santo.</p>\r\n<p class="bodytext"><a name="para187"></a><span class="paranum">187</span>, 108. <span class="bld">Sesesu ntuva</span>.</p>\r\n<p class="bodytext"><span class="bld">Gacchantā</span>dīnaṃntasaddo<span class="bld">ntu</span>ppaccayova daṭṭhabbo sesesu vibhattippaccayesu.</p>\r\n<p class="bodytext">Gacchato, mahato, gacchati, mahati, gacchatā, mahatā.</p>\r\n<p class="bodytext"><span class="bld">Sesesū</span>ti kimatthaṃ? Gacchaṃ, mahaṃ, caraṃ, khādaṃ.</p>\r\n<p class="bodytext"><a name="para188"></a><span class="paranum">188</span>, 115. <span class="bld">Brahmatta sakha rājādito amānaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Brahma atta sakha rāja</span>iccevamādito <span class="bld">aṃ</span>vacanassa <span class="bld">ānaṃ</span> hoti vā.</p>\r\n<p class="bodytext">Brahmānaṃ, brahmaṃ, attānaṃ, attaṃ, sakhānaṃ, sakhaṃ, rājānaṃ, rājaṃ.</p>\r\n<p class="bodytext"><span class="bld">A</span>miti kimatthaṃ? Rājā.</p>\r\n<p class="bodytext"><a name="para189"></a><span class="paranum">189</span>, 113. </p>','\r',char(13)),'\n',char(10)),'-187-188-189-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',112,replace(replace('<p class="bodytext"><span class="bld">Syā</span> <a name="M0.0112"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Brahma atta sakha rāja</span>iccevamādito <span class="bld">si</span>vacanassa <span class="bld">ā</span> ca hoti.</p>\r\n<p class="bodytext">Brahmā, attā, sakhā, rājā, ātumā.</p>\r\n<p class="bodytext"><a name="para190"></a><span class="paranum">190</span>, 114. <span class="bld">Yonamāno</span>.</p>\r\n<p class="bodytext"><span class="bld">Brahmaatta sakha rāja</span>iccevamādito <span class="bld">yonaṃ āno</span>ādeso hoti.</p>\r\n<p class="bodytext">Brahmāno, attāno, sakhāno, rājāno, ātumāno.</p>\r\n<p class="bodytext"><a name="para191"></a><span class="paranum">191</span>, 130. <span class="bld">Sakhato cāyo no</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">sakha</span>to ca <span class="bld">yo</span>naṃ <span class="bld">āyo no</span> ādesā honti.</p>\r\n<p class="bodytext">Sakhāyo, sakhino.</p>\r\n<p class="bodytext"><span class="bld">Yona</span>miti kimatthaṃ? Sakhā.</p>\r\n<p class="bodytext"><a name="para192"></a><span class="paranum">192</span>, 135. <span class="bld">Smime</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">sakha</span>to <span class="bld">smiṃ</span>vacanassa <span class="bld">e</span>kāro hoti. Sakhe.</p>\r\n<p class="bodytext"><a name="para193"></a><span class="paranum">193</span>, 122. </p>','\r',char(13)),'\n',char(10)),'-190-191-192-193-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',113,replace(replace('<p class="bodytext"><span class="bld">Brahmato</span> <a name="M0.0113"></a> <span class="bld">gassa ca</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">brahma</span>to <span class="bld">ga</span>ssa ca <span class="bld">e</span>kāro hoti. He brahme.</p>\r\n<p class="bodytext"><a name="para194"></a><span class="paranum">194</span>, 131. <span class="bld">Sakhantassi no nā naṃ sesu</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">sakha</span>ntassa ikāro hoti <span class="bld">nonānaṃ</span>saiccetesu.</p>\r\n<p class="bodytext">Sakhino, sakhinā, sakhīnaṃ, sakhissa.</p>\r\n<p class="bodytext"><span class="bld">Etesvī</span>ti kimatthaṃ? Sakhārehi.</p>\r\n<p class="bodytext"><a name="para195"></a><span class="paranum">195</span>, 134. <span class="bld">Āro himhi vā</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">sakha</span>ntassa <span class="bld">āro</span> hoti vā <span class="bld">himhi</span> vibhattimhi. Sakhārehi, sakhehi.</p>\r\n<p class="bodytext"><a name="para196"></a><span class="paranum">196</span>, 133. <span class="bld">Sunamaṃsu vā</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">sakha</span>ntassa <span class="bld">āro</span> hoti vā <span class="bld">sunaṃ aṃ</span>iccetesu.</p>\r\n<p class="bodytext">Sakhāresu, sakhesu, sakhārānaṃ, sakhīnaṃ, sakhāraṃ, sakhaṃ.</p>\r\n<p class="bodytext"><a name="para197"></a><span class="paranum">197</span>, 125. <span class="bld">Brahmato tu smiṃ ni</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">brahma</span>to <span class="bld">smiṃ</span>vacanassa <span class="bld">ni</span>ādeso hoti. Brahmani.</p>','\r',char(13)),'\n',char(10)),'-194-195-196-197-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',114,replace(replace('<p class="bodytext"><span class="bld">Tuggahaṇena</span> <a name="M0.0114"></a> abrahmatopi <span class="bld">smiṃ</span> vacanassa <span class="bld">ni</span> hoti. Kammani, cammani, muddhani.</p>\r\n<p class="bodytext"><a name="para198"></a><span class="paranum">198</span>, 123. <span class="bld">Uttaṃ sanāsu</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">brahma</span>saddassa anto uttamāpajjate <span class="bld">sanā</span>iccetesu.</p>\r\n<p class="bodytext">Brahmuno, brahmunā.</p>\r\n<p class="bodytext"><span class="bld">Sanāsū</span>ti kimatthaṃ? Brahmā.</p>\r\n<p class="bodytext"><a name="para199"></a><span class="paranum">199</span>, 158. <span class="bld">Satthupitādīnamā sismiṃsilopoca</span>.</p>\r\n<p class="bodytext"><span class="bld">Satthupitu</span>ādīnamanto <span class="bld">a</span>ttamāpajjate <span class="bld">si</span>smiṃ, <span class="bld">si</span>lopo ca hoti.</p>\r\n<p class="bodytext">Satthā, pitā, mātā, bhātā, kattā.</p>\r\n<p class="bodytext"><span class="bld">Sismi</span>nti kimatthaṃ? Satthussa, pitussa, mātussa, bhātussa, kattussa.</p>\r\n<p class="bodytext"><a name="para200"></a><span class="paranum">200</span>, 159. <span class="bld">Aññesvārattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Satthupitu</span>ādīnamanto aññesu vacanesu <span class="bld">ārattamā</span>pajjate.</p>\r\n<p class="bodytext">Satthāraṃ, pitaraṃ, mātaraṃ, bhātaraṃ, kattāraṃ, satthārehi, pitarehi, mātarehi, bhātarehi, kattārehi.</p>','\r',char(13)),'\n',char(10)),'-198-199-200-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',115,replace(replace('<p class="bodytext"><span class="bld">Aññesvī</span>ti <a name="M0.0115"></a> kimatthaṃ? Satthā, pitā, mātā, bhātā, kattā.</p>\r\n<p class="bodytext"><a name="para201"></a><span class="paranum">201</span>, 163. <span class="bld">Vā naṃmhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Satthupitu</span>ādīnamanto <span class="bld">āra</span>ttamāpajjate vā naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Satthārānaṃ, pitarānaṃ, mātarānaṃ, bhātarānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Satthānaṃ, pitūnaṃ, mātūnaṃ, bhātūnaṃ.</p>\r\n<p class="bodytext"><a name="para202"></a><span class="paranum">202</span>, 164. <span class="bld">Satthunattañca</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">satthu</span>saddassa anto <span class="bld">a</span>ttamāpajjate vā naṃmhi vibhattimhi.</p>\r\n<p class="bodytext">Satthānaṃ, pitānaṃ, mātānaṃ, bhātānaṃ, kattānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Satthārānaṃ, pitarānaṃ, mātarānaṃ, bhātarānaṃ, dhītarānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> aññesampi saṅgahaṇatthaṃ.</p>\r\n<p class="bodytext"><a name="para203"></a><span class="paranum">203</span>, 162. <span class="bld">Usasmiṃ salopo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Satthupitu</span>iccevamādīnamantassa <span class="bld">u</span>ttaṃ hoti vā <span class="bld">sa</span>smiṃ, <span class="bld">sa</span>lopo ca.</p>','\r',char(13)),'\n',char(10)),'-201-202-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',116,replace(replace('<p class="bodytext">Satthu<a name="M0.0116"></a>, satthussa, satthuno dīyate, pariggaho vā. Pitu, pitussa, pituno dīyate, pariggaho vā. Bhātu, bhātussa, bhātuno dīyate, pariggaho vā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> dutiyasampiṇḍanatthaṃ.</p>\r\n<p class="bodytext"><a name="para204"></a><span class="paranum">204</span>, 167. <span class="bld">Sakkamandhātādīnañca</span>.</p>\r\n<p class="bodytext"><span class="bld">Sakkamandhātu</span>iccevamādīnamanto <span class="bld">u</span>ttamāpajjate sasmiṃ, salopo ca hoti.</p>\r\n<p class="bodytext">Sakkamandhātu iva assa rājino vibhavo. Evaṃ kattu, gantu, dātu iccevamādī.</p>\r\n<p class="bodytext">Punā<span class="bld">rambhaggahaṇaṃ</span> kimatthaṃ? Niccadīpanatthaṃ. Sakkamandhātu.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> dutiyasampiṇḍanatthaṃ.</p>\r\n<p class="bodytext"><a name="para205"></a><span class="paranum">205</span>, 160. <span class="bld">Tato yonamo tu</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">ā</span>rādesato sabbesaṃ <span class="bld">yo naṃ o</span>kārādeso hoti.</p>\r\n<p class="bodytext">Satthāro, pitaro, mātaro, bhātaro, kattāro, vattāro.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇena</span> aññasmāpi <span class="bld">yo</span>naṃ <span class="bld">o</span>kāro hoti. Caturo janā, gāvo, ubho purisā.</p>\r\n<p class="bodytext"><a name="para206"></a><span class="paranum">206</span>, 165. </p>','\r',char(13)),'\n',char(10)),'-204-205-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',117,replace(replace('<p class="bodytext"><span class="bld">Tato</span> <a name="M0.0117"></a> <span class="bld">smimi</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">ārā</span>desato <span class="bld">smiṃ</span>vacanassa <span class="bld">i</span>kārādeso hoti.</p>\r\n<p class="bodytext">Satthari, pitari, mātari, dhītari, bhātari, kattari, vattari.</p>\r\n<p class="bodytext">Puna <span class="bld">tatogahaṇena</span> aññasmāpi <span class="bld">smiṃ</span>vacanassa <span class="bld">i</span>kāro hoti. Bhuvi.</p>\r\n<p class="bodytext"><a name="para207"></a><span class="paranum">207</span>, 161. <span class="bld">Nā ā</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">ārā</span>desato <span class="bld">nā</span>vacanassa <span class="bld">ā</span>ādeso hoti.</p>\r\n<p class="bodytext">Satthārā, pitarā, mātarā, bhātarā, dhītarā, kattārā, vattārā.</p>\r\n<p class="bodytext"><a name="para208"></a><span class="paranum">208</span>, 166. <span class="bld">Āro rassamikāre</span>.</p>\r\n<p class="bodytext"><span class="bld">Ārā</span>deso rassamāpajjate <span class="bld">i</span>kāre pare.</p>\r\n<p class="bodytext">Satthari, pitari, mātari, dhītari, kattari, vattari.</p>\r\n<p class="bodytext"><a name="para209"></a><span class="paranum">209</span>, 168. <span class="bld">Pitādīnamasimhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Pitā</span>dīna <span class="bld">mārā</span>deso rassamāpajjate asimhi vibhattimhi.</p>\r\n<p class="bodytext">Pitarā, mātarā, bhātarā, mītarā pitaro, mātaro, bhātaro, dhītaro.</p>','\r',char(13)),'\n',char(10)),'-207-208-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',118,replace(replace('<p class="bodytext"><span class="bld">Asimhiggahaṇaṃ</span> <a name="M0.0118"></a> <span class="bld">to</span>mhi pare ikārādesañāpanatthaṃ. Mātito, pitito, bhātito, duhitito.</p>\r\n<p class="bodytext"><a name="para210"></a><span class="paranum">210</span>, 239. <span class="bld">Tayātayīnaṃ takāro tvattaṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Tayātayi</span> iccetesaṃ <span class="bld">ta</span>kāro <span class="bld">tva</span>ttamāpajjate vā.</p>\r\n<p class="bodytext">Tvayā, tayā, tvayi, tayi.</p>\r\n<p class="bodytext"><span class="bld">Etesami</span>ti kimatthaṃ? Tuvaṃ, tavaṃ.</p>\r\n<p class="centered">Iti nādhakappe tatiyo kaṇḍo.</p>\r\n<p class="subhead">Catutthakaṇḍa</p>\r\n<p class="bodytext"><a name="para211"></a><span class="paranum">211</span>, 126. <span class="bld">Attanto hismi’manattaṃ</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">atta</span>no anto <span class="bld">ana</span>ttamāpajjate <span class="bld">hi</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Attanehi, attanebhi.</p>\r\n<p class="bodytext"><span class="bld">Attanto</span>ti kimatthaṃ? Rājehi, rājebhi.</p>\r\n<p class="bodytext"><span class="bld">Hismi</span>nti kimatthaṃ? Attano.</p>\r\n<p class="bodytext"><span class="bld">Anattamitibhāvaniddesena atta</span>saddassa <span class="bld">sakā</span>deso hoti sabbāsu vibhattīsu. Sako, sakā, sakaṃ, sake.</p>\r\n<p class="bodytext"><a name="para212"></a><span class="paranum">212</span>, 329. </p>','\r',char(13)),'\n',char(10)),'-210-211-212-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',119,replace(replace('<p class="bodytext"><span class="bld">Tato</span> <a name="M0.0119"></a> <span class="bld">smiṃni</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">atta</span>to <span class="bld">smiṃ</span>vacanassa <span class="bld">ni</span> hoti. Attani.</p>\r\n<p class="bodytext"><a name="para213"></a><span class="paranum">213</span>, 127. <span class="bld">Sassa no</span>.</p>\r\n<p class="bodytext">Tato<span class="bld">atta</span>to <span class="bld">sa</span>ssa vibhattissa <span class="bld">no</span> hoti, attano.</p>\r\n<p class="bodytext"><a name="para214"></a><span class="paranum">214</span>, 128. <span class="bld">Smā nā</span>.</p>\r\n<p class="bodytext">Tato <span class="bld">atta</span>to <span class="bld">smā</span> vacanassa <span class="bld">nā</span> hoti. Attanā.</p>\r\n<p class="bodytext">Puna <span class="bld">tatogahaṇena</span> tassa <span class="bld">atta</span>no <span class="bld">ta</span>kārasseva <span class="bld">ra</span>kāro hoti sabbesu vacanesu. Atrajo, atrajaṃ.</p>\r\n<p class="bodytext"><a name="para215"></a><span class="paranum">215</span>, 141. <span class="bld">Jhalato ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Jhala</span>iccetehi <span class="bld">smā</span>vacanassa <span class="bld">nā</span> hoti.</p>\r\n<p class="bodytext">Agginā, daṇḍinā, bhikkhunā, sayambhunā.</p>\r\n<p class="bodytext"><span class="bld">Smā</span>ti kimatthaṃ? Aggayo, munayo, isayo.</p>\r\n<p class="bodytext"><a name="para216"></a><span class="paranum">216</span>, 180. <span class="bld">Ghapato smiṃ yaṃ vā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">ghapa</span>to <span class="bld">smiṃ</span> vacanassa <span class="bld">yaṃ</span> hoti vā.</p>','\r',char(13)),'\n',char(10)),'-213-214-215-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',120,replace(replace('<p class="bodytext">Kaññāyaṃ<a name="M0.0120"></a>, kaññāya. Rattiyaṃ, rattiyā. Itthiyaṃ, itthiyā. Yāguyaṃ, yāguyā. Vakhuyaṃ, vadhuyā.</p>\r\n<p class="bodytext"><a name="para217"></a><span class="paranum">217</span>, 199. <span class="bld">Yonaṃ ni napuṃsakehi</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">yo</span>naṃ <span class="bld">ni</span> hoti vā napuṃsakehi liṅgehi.</p>\r\n<p class="bodytext">Aṭṭhīni, aṭṭhī, āyūni, āyū.</p>\r\n<p class="bodytext"><span class="bld">Napuṃsakehī</span>ti kimatthaṃ? Itthiyo.</p>\r\n<p class="bodytext"><a name="para218"></a><span class="paranum">218</span>, 196. <span class="bld">Ato niccaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">A</span>kārantehi napuṃsakaliṅgehi <span class="bld">yo</span>naṃ <span class="bld">ni</span> hoti niccaṃ.</p>\r\n<p class="bodytext">Yāni, yāni. Tāni, tāni. Kāni, kāni. Bhayāni, bhayāni. Rūpāni, rūpāni.</p>\r\n<p class="bodytext"><a name="para219"></a><span class="paranum">219</span>, 196. <span class="bld">Siṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">A</span>kārantehi napuṃsakaliṅgehi <span class="bld">si</span>vacanassa <span class="bld">aṃ</span> hoti niccaṃ.</p>\r\n<p class="bodytext">Sabbaṃ, yaṃ, taṃ, kaṃ, rūpaṃ.</p>\r\n<p class="bodytext"><a name="para220"></a><span class="paranum">220</span>, 74. <span class="bld">Sesato lopaṃ pasipi</span>.</p>\r\n<p class="bodytext">Tato niddiṭṭhehi ligehi sesakhatā <span class="bld">gasi</span>iccete lopamāpajjante.</p>','\r',char(13)),'\n',char(10)),'-217-218-219-220-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',121,replace(replace('<p class="bodytext">Bhoti <a name="M0.0121"></a> itthi, sā itthī. Bho daṇḍi, so daṇḍī. Bho sattha, so satthā. Bho rāja, so rājā. <span class="bld">Sesato</span>ti kimatthaṃ? Puriso gacchati.</p>\r\n<p class="bodytext"><span class="bld">Gasī</span>ti kimatthaṃ? Itthiyā, satthussa.</p>\r\n<p class="bodytext"><a name="para221"></a><span class="paranum">221</span>, 282. <span class="bld">Sabbāsamāvuso pasagganipātādīhi ca</span>.</p>\r\n<p class="bodytext">Sabbāsaṃ vibhattīnaṃ ekavacanabahuvacanānaṃ paṭhamā dutiyātatiyā catutthī pañcamī chaṭṭhī sattamīnaṃ lopo hoti, <span class="bld">āvuso upasagga nipāta</span>iccevamādīhi ca,</p>\r\n<p class="bodytext">Tvaṃ panāvuso, tumhe panāvuso, padaso dhammaṃ vāceyya, vihāraṃ sve upagaccheyya.</p>\r\n<p class="bodytext">Pa, parā, ni, nī, u, du, saṃ, vi, ava, anu, pari, adhi, abhi, pati, su, ā, ati, api, apa, upa, pahāro, parābhavo, nihāro, nīhāro, uhāro, duhāro, saṃhāro, vihāro, avahāro, anuhāro, parihāro, adhihāro, abhihāro, patihāro, suhāro, āhāro, atihāro, apihāro, apahāro, upahāro, evaṃ vīsati <span class="bld">upasaggehi</span> ca.</p>\r\n<p class="bodytext">Yathā, tathā, evaṃ, khalu, kho, tatra, atho, atha, hi, tu ca, vā, vo, haṃ, abhaṃ, alaṃ, eva, ho aho, </p>','\r',char(13)),'\n',char(10)),'-221-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',122,replace(replace('<p class="bodytext">he<a name="M0.0122"></a>, ahe, re, are, evamādīhi <span class="bld">nipātehi</span> ca yojetabbāni.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇama</span> vadhāraṇatthaṃ.</p>\r\n<p class="bodytext"><a name="para222"></a><span class="paranum">222</span>, 342. <span class="bld">Pumassa liṅgādīsu samāsesu</span>.</p>\r\n<p class="bodytext"><span class="bld">Puma</span>iccetassa anto lopamāpajjate liṅgādīsu parapadesu samāsesu.</p>\r\n<p class="bodytext">Pulliṅgaṃ, pumbhāvo, puṅkokilo.</p>\r\n<p class="bodytext"><span class="bld">Pumasse</span>ti kimatthaṃ? Itthiliṅgaṃ, napuṃsakaliṅgaṃ.</p>\r\n<p class="bodytext"><span class="bld">Liṅgādīsū</span>ti kimatthaṃ? Pumitthī.</p>\r\n<p class="bodytext"><span class="bld">Samāsesū</span>ti kimatthaṃ? Pumassa liṅgaṃ.</p>\r\n<p class="bodytext"><a name="para223"></a><span class="paranum">223</span>, 188. <span class="bld">Aṃ yamīto pasaññato</span>.</p>\r\n<p class="bodytext"><span class="bld">Aṃ</span> vacanassa <span class="bld">yaṃ</span> hoti vā <span class="bld">ī</span>to pasaññato.</p>\r\n<p class="bodytext">Itthiyaṃ, itthiṃ.</p>\r\n<p class="bodytext"><span class="bld">Pasaññato</span>ti kimatthaṃ? Daṇḍinaṃ, bhoginaṃ.</p>\r\n<p class="bodytext"><span class="bld">A</span>miti kimatthaṃ? Itthīhi.</p>\r\n<p class="bodytext"><a name="para224"></a><span class="paranum">224</span>, 153. <span class="bld">Naṃ jhato katarassā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">jha</span>to katarassā <span class="bld">aṃ</span> vacanassa <span class="bld">naṃ</span> hoti.</p>','\r',char(13)),'\n',char(10)),'-222-223-224-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',123,replace(replace('<p class="bodytext"><span class="bld">Daṇḍinaṃ</span><a name="M0.0123"></a>, bhoginaṃ.</p>\r\n<p class="bodytext"><span class="bld">Jhato</span>ti kimatthaṃ? Vessabhuṃ.</p>\r\n<p class="bodytext"><span class="bld">Katarassā</span>ti kimatthaṃ? Kucchiṃ.</p>\r\n<p class="bodytext"><a name="para225"></a><span class="paranum">225</span>, 151. <span class="bld">Yonaṃ no</span>.</p>\r\n<p class="bodytext">Sabbesaṃ <span class="bld">yo</span>naṃ <span class="bld">jha</span>to katarassā <span class="bld">no</span> hoti.</p>\r\n<p class="bodytext">Daṇḍino bhogino, he daṇḍino, he bhogino.</p>\r\n<p class="bodytext"><span class="bld">Katarassā</span>ti kimatthaṃ? Aggayo, munayo, isayo.</p>\r\n<p class="bodytext"><span class="bld">Jhato</span>ti kimatthaṃ? Sayambhuno.</p>\r\n<p class="bodytext"><span class="bld">Yona</span>nti kimatthaṃ? Daṇḍinā, bhoginā.</p>\r\n<p class="bodytext"><a name="para226"></a><span class="paranum">226</span>, 154. <span class="bld">Smiṃni</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">jha</span>to katarassā <span class="bld">smiṃ</span>vacanassa niāde so hoti.</p>\r\n<p class="bodytext">Daṇḍini, bhogini.</p>\r\n<p class="bodytext"><span class="bld">Katarassā</span>ti kimatthaṃ? Byādhimhi.</p>\r\n<p class="bodytext"><a name="para227"></a><span class="paranum">227</span>, 270. <span class="bld">Kissa kaveca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetassa <span class="bld">ko</span> ca hoti <span class="bld">va</span>paccaye pare.</p>','\r',char(13)),'\n',char(10)),'-225-226-227-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',124,replace(replace('<p class="bodytext">Kva <a name="M0.0124"></a> gatosi tvaṃ devānaṃ piyatissa.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> avapaccaye parepi <span class="bld">ko</span> ca hoti. Ko taṃninditu marahati, kathaṃ bodhayituṃ dhammaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ve</span>ti kimatthaṃ? Kuto āgatosi tvaṃ.</p>\r\n<p class="bodytext"><a name="para228"></a><span class="paranum">228</span>, 272. <span class="bld">Kuhiṃ haṃ su ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetassa <span class="bld">ku</span> hoti <span class="bld">hiṃ haṃ</span>iccetesu ca. Kuhiṃ gacchasi, kulaṃ gacchasi.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena hiñcanaṃdācanaṃ</span> paccayesu paresu aññatthāpi <span class="bld">ku</span> hoti. Kuhiñcanaṃ, kudācanaṃ.</p>\r\n<p class="bodytext"><a name="para229"></a><span class="paranum">229</span>, 226. <span class="bld">Sesesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetassa <span class="bld">ko</span> hoti sesesu vibhattipaccayesu paresu.</p>\r\n<p class="bodytext">Ko pakāro kathaṃ, kaṃ pakāraṃ kathaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para230"></a><span class="paranum">230</span>, 262. <span class="bld">Trabhothesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetassa <span class="bld">ku</span> hoti <span class="bld">tratotha</span> iccetesu ca.</p>\r\n<p class="bodytext">Kutra, kuto, kuttha.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ,</p>\r\n<p class="bodytext"><a name="para231"></a><span class="paranum">231</span>, 263. </p>','\r',char(13)),'\n',char(10)),'-228-229-230-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',125,replace(replace('<p class="bodytext"><span class="bld">Sabbassetassa</span><a name="M0.0125"></a>, <span class="bld">kāro vā</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">eta</span>saddassa <span class="bld">a</span>kāro hoti vā <span class="bld">totha</span>iccetesu.</p>\r\n<p class="bodytext">Ato, attha, etto, ettha.</p>\r\n<p class="bodytext"><a name="para232"></a><span class="paranum">232</span>, 267. <span class="bld">Tre niccaṃ</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">eta</span>saddassa <span class="bld">a</span>kāro hoti niccaṃ <span class="bld">tra</span>paccaye pare.</p>\r\n<p class="bodytext">Atra.</p>\r\n<p class="bodytext"><a name="para233"></a><span class="paranum">233</span>, 264. <span class="bld">E tothesu ca</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">eta</span>saddassa ekāro hoti vā <span class="bld">totha</span>iccetesu.</p>\r\n<p class="bodytext">Etto, ato, ettha, attha.</p>\r\n<p class="bodytext"><a name="para234"></a><span class="paranum">234</span>, 265. <span class="bld">Imassi thaṃ dāni ha to dhesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>saddassa sabbasseva <span class="bld">i</span>kāro hoti <span class="bld">thaṃ dānihato dha</span>iccetesu.</p>\r\n<p class="bodytext">Itthaṃ, idāni, iha, ito, idha.</p>\r\n<p class="bodytext"><a name="para235"></a><span class="paranum">235</span>, 281. </p>','\r',char(13)),'\n',char(10)),'-232-233-234-235-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',126,replace(replace('<p class="bodytext"><span class="bld">Adhunāmhi</span> <a name="M0.0126"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Imasaddassa sabbasseva <span class="bld">a</span>kāro hoti <span class="bld">dhunā</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Adhunā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ.</p>\r\n<p class="bodytext"><a name="para236"></a><span class="paranum">236</span>, 280. <span class="bld">Eta rahimhi</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ima</span>saddassa <span class="bld">etā</span>deso hoti <span class="bld">rahi</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Etarahi.</p>\r\n<p class="bodytext"><a name="para237"></a><span class="paranum">237</span>, 176. <span class="bld">Itthiyamato āpaccayo</span>.</p>\r\n<p class="bodytext"><span class="bld">I</span>tthiyaṃ vattamānāya <span class="bld">a</span>kārato <span class="bld">ā</span>paccayo hoti.</p>\r\n<p class="bodytext">Sabbā, yā, sā, kā. Katarā.</p>\r\n<p class="bodytext"><a name="para238"></a><span class="paranum">238</span>, 187. <span class="bld">Nadādito vā ī</span>.</p>\r\n<p class="bodytext"><span class="bld">Nadā</span>dito vāa<span class="bld">nadā</span>ditovā itthiyaṃ vattamānāya īpaccayo hoti.</p>\r\n<p class="bodytext">Nadī, mahī, kumārī, taruṇī, sakhī, itthī.</p>\r\n<p class="bodytext"><a name="para239"></a><span class="paranum">239</span>, 190. </p>','\r',char(13)),'\n',char(10)),'-236-237-238-239-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',127,replace(replace('<p class="bodytext"><span class="bld">Ṇava</span> <a name="M0.0127"></a> <span class="bld">ṇika ṇeyya ṇa ntuhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇava ṇika ṇeyya ṇa ntu</span>iccetehi itthiyaṃ vattamānehi <span class="bld">ī</span>paccayo hoti.</p>\r\n<p class="bodytext">Māṇavī, paṇḍavī, nāvikī, venateyyī, kunteyyī, gotamī, guṇavatī, sāmāvatī.</p>\r\n<p class="bodytext"><a name="para240"></a><span class="paranum">240</span>, 193. <span class="bld">Pati bhikkhurājīkārantehi inī</span>.</p>\r\n<p class="bodytext"><span class="bld">Pati bhikkhu rājī</span>kārantehi itthiyaṃ vattamānehi <span class="bld">inī</span>paccayo hoti.</p>\r\n<p class="bodytext">Gahapatānī, bhikkhunī, rājinī, hatthinī, daṇḍinī, medhāvinī, tapassinī.</p>\r\n<p class="bodytext"><a name="para241"></a><span class="paranum">241</span>, 191. <span class="bld">Ntussa tamīkāre</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">ntu</span>paccayassa <span class="bld">ta</span>kāro hoti vā <span class="bld">ī</span>kāre pare.</p>\r\n<p class="bodytext">Guṇavatī, guṇavantī, kulavatī, kulavantī, satimatī. Satimantī, mahatī, mahantī, gottamatī, gottamantī.</p>\r\n<p class="bodytext"><a name="para242"></a><span class="paranum">242</span>, 192. <span class="bld">Bhavato bhoto</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">bhavanta</span>saddassa <span class="bld">bhotā</span>deso hoti <span class="bld">ī</span>kāre itthigate pare.</p>\r\n<p class="bodytext">Bhoti ayye, bhoti kaññe, bhoti kharādiye.</p>\r\n<p class="bodytext"><a name="para243"></a><span class="paranum">243</span>, 110. </p>','\r',char(13)),'\n',char(10)),'-240-241-242-243-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',128,replace(replace('<p class="bodytext"><span class="bld">Bhoge</span> <a name="M0.0128"></a> <span class="bld">tu</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">bhavanta</span>saddassa <span class="bld">bho</span>ādeso hoti <span class="bld">ge</span> pare.</p>\r\n<p class="bodytext">Bho purisa, bho aggi, bho rāja, bho sattha, bho daṇḍi, bho sayambhu.</p>\r\n<p class="bodytext"><span class="bld">Ge</span>ti kimatthaṃ? Bhavatā, bhavaṃ.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇena</span> aññasmimpi vacane sabbassa <span class="bld">bhavanta</span>saddassa <span class="bld">bhonta bhante bhonto bhadde bhotā bho to</span>iccete ādesā honti.</p>\r\n<p class="bodytext">Bhonta, bhante, bhonto, bhadde, bhotā, bhoto.</p>\r\n<p class="bodytext"><a name="para244"></a><span class="paranum">244</span>, 72. <span class="bld">Akārapitādyantānamā</span>.</p>\r\n<p class="bodytext"><span class="bld">A</span>kāro ca <span class="bld">pitā</span>dīnamanto ca attamāpajjate <span class="bld">ge</span> pare.</p>\r\n<p class="bodytext">Bho purisā, bho rājā, bho pitā, bho mātā, bho satthā.</p>\r\n<p class="bodytext"><a name="para245"></a><span class="paranum">245</span>, 152. <span class="bld">Jhalapā rassaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Jhalapa</span>iccete rassamāpajjante <span class="bld">ge</span> pare.</p>\r\n<p class="bodytext">Bho daṇḍi, bho sayambhu, bhoti itthi, bhotivadhu,</p>\r\n<p class="bodytext"><a name="para246"></a><span class="paranum">246</span>, 73. </p>','\r',char(13)),'\n',char(10)),'-244-245-246-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',129,replace(replace('<p class="bodytext"><span class="bld">Ākāro</span> <a name="M0.0129"></a> <span class="bld">vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>kāro rassamāpajjate vā <span class="bld">ge</span> pare.</p>\r\n<p class="bodytext">Bho rāja, bho rājā, bho atta, bho attā, bho sakha, bho sakhā, bho sattha, bho satthā.</p>\r\n<p class="centered">Iti nāmakappe catuttho kaṇḍo.</p>\r\n<p class="subhead">Pañcamakaṇḍa</p>\r\n<p class="bodytext"><a name="para247"></a><span class="paranum">247</span>, 261. <span class="bld">Tvādayo vibhattisaññāyo</span>.</p>\r\n<p class="bodytext"><span class="bld">To</span>ādi yesaṃ paccayānaṃ, te honti tvādayo. Te paccayā <span class="bld">tvā</span>dayo vibhattisaññāva daṭṭhabbā.</p>\r\n<p class="bodytext">Sabbato, yato, tato, kuto, ato, ito, sabbadā, yadā, tadā, kadā, idha, idāni.</p>\r\n<p class="bodytext"><a name="para248"></a><span class="paranum">248</span>, 260. <span class="bld">Kvaci to pañcamyatthe</span>.</p>\r\n<p class="bodytext">Kvaci <span class="bld">to</span>paccayo hoti pañcamyatthe.</p>\r\n<p class="bodytext">Sabbato, yato, tato, kuto, ato, ito.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Sabbasmā, imasmā.</p>\r\n<p class="bodytext"><a name="para249"></a><span class="paranum">249</span>, 266. </p>','\r',char(13)),'\n',char(10)),'-247-248-249-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',130,replace(replace('<p class="bodytext"><span class="bld">Tra</span> <a name="M0.0130"></a> <span class="bld">tha sattamiyā sabbanāmehi</span>.</p>\r\n<p class="bodytext"><span class="bld">Tratha</span>iccete paccayā honti sattamyatthe sabba nāmehi.</p>\r\n<p class="bodytext">Sabbatra, sabbattha, yatra, yattha, tatra, tattha.</p>\r\n<p class="bodytext"><a name="para250"></a><span class="paranum">250</span>, 268. <span class="bld">Sabbato dhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Sabba</span>iccetasmā <span class="bld">dhi</span>paccayo hoti kvaci sattamyatthe. Sabbadhi, sabbasmiṃ.</p>\r\n<p class="bodytext"><a name="para251"></a><span class="paranum">251</span>, 269. <span class="bld">Kiṃ smā vo</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetasmā <span class="bld">va</span>paccayo hoti sattamyatthe.</p>\r\n<p class="bodytext">Kva gatosi tva devānaṃpiyatissa.</p>\r\n<p class="bodytext"><a name="para252"></a><span class="paranum">252</span>, 271. <span class="bld">Hiṃ haṃ hiñcanaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ki</span>miccetasmā <span class="bld">hiṃhaṃhiñcanaṃ</span>iccete paccayā honti sattamyatthe.</p>\r\n<p class="bodytext">Kuhiṃ, kulaṃ, kuhiñcanaṃ.</p>\r\n<p class="bodytext"><a name="para253"></a><span class="paranum">253</span>, 273. <span class="bld">Tamhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Tamhā ca hi haṃ</span>iccete paccayā honti sattamyatthe. Tahiṃ, tahaṃ.</p>','\r',char(13)),'\n',char(10)),'-250-251-252-253-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',131,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> <a name="M0.0131"></a> <span class="bld">hiñcana</span>ggahaṇanivattanatthaṃ.</p>\r\n<p class="bodytext"><a name="para254"></a><span class="paranum">254</span>, 274. <span class="bld">Imasmā ha dhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>smā <span class="bld">hadha</span>iccete paccayā honti sattamyatthe. Iha, idha.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>mavadhāraṇatthaṃ.</p>\r\n<p class="bodytext"><a name="para255"></a><span class="paranum">255</span>, 275. <span class="bld">Yato hiṃ</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">ya</span>to <span class="bld">hiṃ</span>paccayo hoti sattamyatthe. Yahiṃ.</p>\r\n<p class="bodytext"><a name="para256"></a><span class="paranum">256</span>, 0. <span class="bld">Kāle</span>.</p>\r\n<p class="bodytext">‘‘Kāle’’iccetaṃ adhikāratthaṃ veditabbaṃ.</p>\r\n<p class="bodytext"><a name="para257"></a><span class="paranum">257</span>, 279. <span class="bld">Kiṃsabbaññekayakuhidādācanaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Kiṃ sabbaañña eka ya ku</span>iccetehi <span class="bld">dā dācanaṃ</span>iccete paccayā honti kāle sattamyatthe.</p>\r\n<p class="bodytext">Kadā, sabbadā, aññadā, ekadā, yadā, kudācanaṃ.</p>\r\n<p class="bodytext"><a name="para258"></a><span class="paranum">258</span>, 278. <span class="bld">Tamhā dāni ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ta</span>iccetasmā <span class="bld">dāni dā</span>iccete paccayā honti, kāle sattamyatthe.</p>','\r',char(13)),'\n',char(10)),'-254-255-256-257-258-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',132,replace(replace('<p class="bodytext">Tadāni<a name="M0.0132"></a>, tadā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para259"></a><span class="paranum">259</span>, 279. <span class="bld">Imasmā rahi dhunā dāni ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ima</span>smā <span class="bld">rahi dhunā dāni</span>iccete paccayā honti kāle sattamyatthe.</p>\r\n<p class="bodytext">Terahi, adhunā, idāni.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para260"></a><span class="paranum">260</span>, 277. <span class="bld">Sabbassa so dāmhi vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Sabba</span>iccetassa <span class="bld">sa</span>kārādeso hoti vā <span class="bld">dā</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Sadā, sabbadā.</p>\r\n<p class="bodytext"><a name="para261"></a><span class="paranum">261</span>, 369. <span class="bld">Avaṇṇo ye lopañca</span>.</p>\r\n<p class="bodytext">Avaṇṇo <span class="bld">ye</span> paccaye pare lopamāpajjate.</p>\r\n<p class="bodytext">Bāhussaccaṃ, paṇḍiccaṃ, vepullaṃ, kāruññaṃ, kosallaṃ, sāmaññaṃ, sohajjaṃ.</p>\r\n<p class="bodytext"><a name="para262"></a><span class="paranum">262</span>, 391. <span class="bld">Vuḍḍhassa jo iyiṭṭhesu</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">vuḍḍha</span>saddassa <span class="bld">jo</span>ādeso hoti <span class="bld">iya iṭṭha</span>iccetesu paccayesu.</p>','\r',char(13)),'\n',char(10)),'-259-260-261-262-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',133,replace(replace('<p class="bodytext">Jeyyo<a name="M0.0133"></a>, jeṭṭho.</p>\r\n<p class="bodytext"><a name="para263"></a><span class="paranum">263</span>, 392. <span class="bld">Pasatthassa so ca</span>.</p>\r\n<p class="bodytext">Sabbasseva <span class="bld">pasattha</span>saddassa <span class="bld">so</span>ādeso hoti, <span class="bld">jā</span>deso ca <span class="bld">iyaiṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Seyyo, seṭṭho, jeyyo, jeṭṭho.</p>\r\n<p class="bodytext"><a name="para264"></a><span class="paranum">264</span>, 393. <span class="bld">Antikassa nedo</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">antika</span>saddassa <span class="bld">nedā</span>deso hoti <span class="bld">iya iṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Nediyo, nediṭṭho.</p>\r\n<p class="bodytext"><a name="para265"></a><span class="paranum">265</span>, 394. <span class="bld">Bāḷhassa sādho</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">bāḷha</span>saddassa <span class="bld">sādhā</span>deso hoti <span class="bld">iya iṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Sādhiyo, sādhiṭṭho.</p>\r\n<p class="bodytext"><a name="para266"></a><span class="paranum">266</span>, 395. <span class="bld">Appassa kaṇa</span>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">appa</span>saddassa <span class="bld">kaṇa</span> ādeso hoti <span class="bld">iya iṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Kaṇiyo, kaṇiṭṭho.</p>\r\n<p class="bodytext"><a name="para267"></a><span class="paranum">267</span>, 396. </p>','\r',char(13)),'\n',char(10)),'-263-264-265-266-267-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',134,replace(replace('<p class="bodytext"><span class="bld">Yuvānañca</span><a name="M0.0134"></a>.</p>\r\n<p class="bodytext">Sabbassa <span class="bld">yuva</span>saddassa <span class="bld">kaṇa</span> ādeso hoti <span class="bld">iya iṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Kaniyo, kaniṭṭho.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span>manukaḍḍhanatthaṃ.</p>\r\n<p class="bodytext"><a name="para268"></a><span class="paranum">268</span>, 397. <span class="bld">Vantumantu vīnañca lopo</span>.</p>\r\n<p class="bodytext"><span class="bld">Vantumantuvī</span>iccetesaṃ paccayānaṃ lopo hoti <span class="bld">iyaiṭṭha</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Guṇiyo, guṇiṭṭho, satiyo, satiṭṭho, medhiyo, medhiṭṭho.</p>\r\n<p class="bodytext"><a name="para269"></a><span class="paranum">269</span>, 401. <span class="bld">Yavataṃ ta la ṇa dakārānaṃ byañjanāni ca la ña jakārattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ya</span>kāravantānaṃ <span class="bld">ta la ṇa da</span>kārānaṃ byañjanāni <span class="bld">ca la ña ja</span>kārattamāpajjante yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Bāhussaccaṃ, paṇḍiccaṃ, vepullaṃ, kāruññaṃ, kosallaṃ, nepuññaṃ, sāmaññaṃ, sohajjaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ya va ta</span>miti kimatthaṃ? Tiṇadalaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ta la ṇa dakārāna</span>miti kimatthaṃ? Ālasyaṃ, ārogyaṃ.</p>','\r',char(13)),'\n',char(10)),'-268-269-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',135,replace(replace('<p class="bodytext"><span class="bld">Byañjanāna</span>miti <a name="M0.0135"></a> kimatthaṃ? Maccunā.</p>\r\n<p class="bodytext"><span class="bld">Kāraggahaṇaṃ</span> kimatthaṃ? <span class="bld">Ya</span>kārassa <span class="bld">ma</span>kārādesañāpanatthaṃ. Opammaṃ.</p>\r\n<p class="bodytext"><a name="para270"></a><span class="paranum">270</span>, 120. <span class="bld">Amha tumhanturāja brahmatta sakhasatthu pitādīhismā nāva</span>.</p>\r\n<p class="bodytext"><span class="bld">Amha tumhanturāja brahma atta sakha satthu pitu</span>iccevamādīhi <span class="bld">smā</span>vacanaṃ nāva daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Mayā, tayā, guṇavatā, raññā, brahmunā, attanā, sakhinā, satthārā, pitarā, mātarā, bhātarā, dhītarā, kattārā, vattārā.</p>\r\n<p class="bodytext"><span class="bld">Etehī</span>ti kimatthaṃ? Purisā.</p>\r\n<p class="centered">Iti nāmakappe pañcamo kaṇḍo</p>\r\n<p class="centered">Nāmakappo niṭṭhito.</p>\r\n<p class="chapter">3. Kārakakappa</p>\r\n<p class="subhead">Chaṭṭhakaṇḍa</p>\r\n<p class="bodytext"><a name="para271"></a><span class="paranum">271</span>, 88, 308. </p>','\r',char(13)),'\n',char(10)),'-270-271-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',136,replace(replace('<p class="bodytext"><span class="bld">Yasmā</span> <a name="M0.0136"></a> <span class="bld">dapeti bhayamādatte vā tadapādānaṃ</span>.</p>\r\n<p class="bodytext">Yasmā vā apeti, yasmā vā bhayaṃ jāyate, yasmā vā ādatte, taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Gāmā apenti munayo, nagarā niggato rājā, corā bhayaṃ jāyate, ācariyupajjhāyehi sikkhaṃ gaṇhāti sisso.</p>\r\n<p class="bodytext"><span class="bld">Apādāna</span>miccanena kvattho? Apādāne pañcamī.</p>\r\n<p class="bodytext"><a name="para272"></a><span class="paranum">272</span>, 309. <span class="bld">Dhātunā mānamupasaggayogādvīsvapi ca</span>.</p>\r\n<p class="bodytext">Dhātunāmānaṃ payoge ca upasaggayogādīsvapi ca taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Dhātūnaṃ payoge</span> tāva <span class="bld">ji</span>iccetassa dhātussa <span class="bld">parā</span>pubbassa payoge yo asaho, so apādānasañño hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Buddhasmā parājenti aññatitthiyā.</p>','\r',char(13)),'\n',char(10)),'-272-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',137,replace(replace('<p class="bodytext"><span class="bld">Bhū</span>iccetassa <a name="M0.0137"></a> dhātussa papubbassa payoge yato acchinnappabhavo, so apādānasañño hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Himavatā pabhavanti pañca mahānadiyo, anavatattamhā pabhavanti mahāsarā, aciravatiyā pabhavanti kunnadiyo.</p>\r\n<p class="bodytext"><span class="bld">Nāmappayoge</span>pi taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Urasmā jāto putto, bhūmito niggato raso, ubhato sujāto putto mātito ca pitito ca.</p>\r\n<p class="bodytext"><span class="bld">Upasaggayoge</span> taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Apasālāya āyanti vāṇijā, ābrahmalokā saddo abbhuggacchati upari pabbatā devo vassati, buddhasmā pati sāriputto dhammadesanāya bhikkhū ālapati temāsaṃ, ghatamassa telasmā pati dadāti, uppalamassa padumasmā pati dadāti, kanakamassa hiraññasmā pati dadāti.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇena</span> kārakamajjhepi pañcamīvibhatti hoti. Ito pakkhasmā vijjhati migaṃ luddako, kosā vijjhati kuñjaraṃ, māsasmā bhuñjati bhojanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Apiggahaṇena</span> nipātapayogepi pañcamīvibhatti hoti dutiyā ca tatiyā ca. Rahitā mātujā puññaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',138,replace(replace('<p class="bodytext">katvā <a name="M0.0138"></a> dānaṃ deti, rahitā mātujaṃ, rahitā mātujena vā. Rite saddhammā kuto sukhaṃ labhati, rite saddhammaṃ, rite saddhammena vā. Te bhikkhū nānā kulā pabbajitā, vinā saddhammā natthañño koci nātho loke vijjati, vinā saddhammaṃ, vinā saddhammena vā. Vinā buddhasmā, vinā buddhaṃ, vinā buddhena vā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> aññatthāpi pañcamīvibhatti hoti. Yatohaṃ bhagini ariyāya jātiyā jāto. Yato sarāmi attānaṃ, yato pattosmi viññutaṃ, yatvādhikaraṇamenaṃ cakkhundriyaṃ asaṃvutaṃ viharantaṃ abhijjhādomanassā pāpakā akusalā dhammā anvāsaveyyuṃ.</p>\r\n<p class="bodytext"><a name="para273"></a><span class="paranum">273</span>, 310. <span class="bld">Rakkhaṇatthānamicchitaṃ</span>.</p>\r\n<p class="bodytext">Rakkhaṇatthānaṃ dhātūnaṃ payoge yaṃ icchitaṃ, taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext">Kāke rakkhanti taṇḍulā, yavā paṭisedhenti gāvo.</p>\r\n<p class="bodytext"><a name="para274"></a><span class="paranum">274</span>, 311. <span class="bld">Yena vā’ dassanaṃ</span>.</p>\r\n<p class="bodytext">Yena vā adassanamicchitaṃ, taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext">Upajjhāyā antaradhāyati sisso, mātarā ca pitarā ca antaradhāyati putto.</p>','\r',char(13)),'\n',char(10)),'-273-274-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',139,replace(replace('<p class="bodytext"><span class="bld">Vā</span>ti <a name="M0.0139"></a> kimatthaṃ? Sattamīvibhatyattaṃ. Jetavane antaradhāyati bhagavā.</p>\r\n<p class="bodytext"><a name="para275"></a><span class="paranum">275</span>, 312. <span class="bld">Dūranti kaddha kāla nimmāna tvālopa disāyogavibhattārappayoga suddhappamocana hetu vivittappamāṇa pubbayogabandhana guṇavacana pañha kathana thokākattūsu ca</span>.</p>\r\n<p class="bodytext">Dūratthe, antikatthe, addhanimmāne, kālanimmāne, tvālope, disāyoge, vibhatte, ārappayoge, suddhe, pamocane, hetvatthe, vivittatthe, pamāṇe, pubbayoge, bandhanatthe, guṇavacane, pañhe, kathane, thoke, akattari ca iccetesvatthesu, payogesu ca, taṃ kārakaṃ apādānasaññaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Dūratthe</span>tāva – kīva dūro ito naḷakāragāmo, dūrato vā gamma, ārakā te moghapurisā imasmā dhammavinayā. Dutiyā ca tatiyā ca, dūraṃ gāmaṃ āvato, dūrena gāmena vā āgato. Ārakā imaṃ dhammavinayaṃ, anena dhammavinayena vā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Antikatthe</span> – antikaṃ gāmā, āsannaṃ gāmā, samīpaṃ gāmā, samīpaṃ saddhammā. Dutiyā ca tatiyā ca, antikaṃ gāmaṃ, antikaṃ gāmena vā. Āsannaṃ gāmaṃ, āsannaṃ gāmena vā. Samīpaṃ gāmaṃ. Samīpaṃ gāmena vā. Samīpaṃ saddhammaṃ, samīpaṃ saddhammena vā iccevamādi.</p>','\r',char(13)),'\n',char(10)),'-275-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',140,replace(replace('<p class="bodytext"><span class="bld">Addhanimmāne</span> <a name="M0.0140"></a> – ito mathurāya catūsu yojanesu saṅkassaṃ nāma nagaraṃ atthi, tattha bahū janā vasanti iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Kālanimmāne</span> – ito bhikkhave ekanavutikappe vipassī nāma bhagavā loke udapādi, ito tiṇṇaṃ māsānaṃ accayena parinibbāyissati iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Tvālope</span> kammādhikaraṇesu – pāsādā saṅkameyya, pāsādaṃ abhiruhitvā vā. Pabbatā saṅkameyya, pabbataṃ abhiruhitvā vā. Hatthikkhandhā saṅkameyya, hatthikkhandhaṃ abhiruhitvā vā. Āsanā vuṭṭhaheyya. Āsane nisīditvā vā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Disāyoge</span> – avicito yāva uparibhavaggamantare bahū sattanikāyā vasanti, yato khemaṃ tato bhayaṃ, puratthimato, dakkhiṇato, pacchimato, uttarato aggī pajjalanti, yato assosuṃ bhagavantaṃ, uddhaṃ pādatalā adho kesamatthakā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Vibhatte</span> – yato paṇītataro vā visiṭṭhataro vā natthi. Chaṭṭhī ca, channavutīnaṃ pāsaṇḍānaṃ, dhammānaṃ pavaraṃ yadidaṃ sugatavinayo iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ārappayoge</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',141,replace(replace('<p class="bodytext">– <a name="M0.0141"></a> gāmadhammā vasaladhammā asaddhammā ārati virati paṭivirati, pāṇātipātā veramaṇī iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Suddhe</span> – lobhaniyehi dhammehi suddho asaṃsaṭṭho, mātito ca pitito ca suddho asaṃsaṭṭho anupakuddho agarahito iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Pamocane</span> – parimutto dukkhasmāti vadāmi, muttosmi mārabandhanā, na te muccanti maccunā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Hetvatthe</span> – kasmā hetunā, kena hetunā, kissa hetunā, kasmā nu tumhaṃ daharā na mīyare, kasmā idheva maraṇaṃ bhavissati iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Vivittatthe</span> – vivitto pāpakā dhammā, vivicceva kāmehi vivicca akusalehi dhammehi iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Pamāṇe</span> – dīghaso navavidatthiyo sugatavidatthiyā pamāṇikā kāretabbā, majjhimassa purisassa aḍḍha teḷasahatthā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Pubbayoge</span> – pubbeva sammodhā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Bandhanatthe</span> – satasmā bandho naro. Tatiyā ca, satena bandho naro raññā iṇatthena iccevamādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',142,replace(replace('<p class="bodytext"><span class="bld">Guṇavacane</span> <a name="M0.0142"></a> – puññāya sugatiṃ yanti, cāgāya vipulaṃ dhanaṃ, paññāya vimuttimano, issariyāya janaṃ rakkhati rājā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Pañhe</span> tvālope kammādhikaraṇesu – abhidhammā pucchanti, abhidhammaṃ sutvā, abhidhamme ṭhatvā vā. Vinayā pucchanti, vinayaṃ sutvā, vinaye ṭhatvā vā. Dutiyā ca tatiyā ca, abhidhammaṃ, abhidhammena vā. Vinayaṃ, vinayena vā. Eva suttā, geyyā, gāthāya, veyyākaraṇā, udānā, itivuttakā, jātakā, abbhutadhammā, vedallā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Kathane</span> tvālope kammādhikaraṇesu – abhidhammā kathayanti, abhidhammaṃ sutvā, abhidhamme ṭhatvā vā. Vinayā kathayanti, vinayaṃ sutvā, vinaye ṭhatvā vā. Dutiyā ca tatiyā ca, abhidhammaṃ, abhidhammena vā. Vinayaṃ vinayena vā. Evaṃ suttā, geyyā, gāthāya, veyyākaraṇā, udānā, itivuttakā, jātakā, abbhutadhammā, vedallā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Thoke</span> – thokā muccanti. Appamattakā muccanti, kicchā muccanti. Tatiyā ca. Thokena, appamattakena, kicchena vā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Akattarica</span> – kammassa katattā upacitattā ussannattā vipulattā cakkhuviññāṇaṃ uppannaṃ hoti iccevamādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',143,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇena</span> <a name="M0.0143"></a> sesesupi ye mayā nopadiṭṭhā apādānapayogikā, te payogavicakkhaṇehi yathāyogaṃ yojetabbā.</p>\r\n<p class="bodytext"><a name="para276"></a><span class="paranum">276</span>, 302. <span class="bld">Yassa dātukāmo rocate dhārayate vā taṃ sampadānaṃ</span>.</p>\r\n<p class="bodytext">Yassa vā dātukāmo yassa vārocate, yassa vā dhārayate, taṃ kārakaṃ sampadānasaññaṃ hoti.</p>\r\n<p class="bodytext">Samaṇassa cīvaraṃ dadāti, samaṇassa rocate saccaṃ, devadattassa suvaṇṇacchattaṃ dhārayate yaññadatto.</p>\r\n<p class="bodytext"><span class="bld">Sampadāna</span>miccanena kvattho? Sampadāne catutthī.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti vikappanatthaṃ, dhātunāmānaṃ payoge vā upasaggappayoge vā nipātappayoge vā sati atthavikappanatthaṃ <span class="bld">vā</span>ti padaṃ payujjati.</p>\r\n<p class="bodytext"><a name="para277"></a><span class="paranum">277</span>, 303. <span class="bld">Silāgha hanu ṭhā sapa dhāra piha kudha duhissosūya rādhikkha paccāsuṇa anupatigiṇa pubbakattārocanattha tadattha tumatthālamattha maññānādarappāṇini, gatyatthakammani, āsīsattha sammuti bhiyya sattamyatthesu ca</span>.</p>\r\n<p class="bodytext">Silāgha hanu ṭhā sapa dhāra piha kudha duha issaiccetesaṃ dhātūnaṃ payoge, usūyatthānañca payoge, rādhikkhappayoge, paccāsuṇaanupatigiṇānaṃ pubbakattari, </p>','\r',char(13)),'\n',char(10)),'-276-277-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',144,replace(replace('<p class="bodytext">ārocanatthe<a name="M0.0144"></a>, tadatthe, tumatthe, alamatthe, maññatippayoge anādare appāṇini, gatyatthānaṃ dhātūnaṃ kammani, āsīsatthe ca sammuti bhiyya sattamyatthesu ca, taṃ kārakaṃ sampadānasaññaṃ hoti.</p>\r\n<p class="bodytext"><span class="bld">Silāghappayoge</span> tāva – buddhassa silāghate, dhammassa silāghate, saṅghassa silāghate, sakaṃupajjhāyassa silāghate, tava silāghate mama silāghate iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Hanuppayoge</span> – hanute tuyhameva, hanute mayhameva iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ṭhāpayoge</span> – upatiṭṭheyya sakyaputtānaṃ vaḍḍhakī, bhikkhussa bhuñjantassa pānīyena vā vidhūpanena vā upatiṭṭheyya bhikkhunī iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Sapappayoge</span> – tuyhaṃ sapate, mayhaṃ sapate iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Dhārappayoge</span> – suvaṇṇaṃ te dhārayate iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Pihappayoge</span> – buddhassa aññatitthiyā pihayanti, devā dassanakāmā te, yato icchāmi bhaddantassa, samiddhānaṃ pihayanti daliddā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Kudhaduhaissausūyappayoge</span> – kodhayati deva dattassa, tassa kujjha mahāvīra, mā raṭṭhaṃ vinassa idaṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',145,replace(replace('<p class="bodytext">Duhayati <a name="M0.0145"></a> disānaṃ megho, titthiyā samaṇānaṃ issayanti guṇagiddhena, titthiyā samaṇānaṃ issayanti lābhagiddhena, dujjanā guṇavantānaṃ usūyanti guṇagiddhena, kā usūyā vijānataṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Rādha ikkha</span> iccetesaṃ dhātūnaṃ payoge yassa akathitassa pucchanaṃ kammavikkhyāpanatthañca, taṃ kārakaṃ sampadānasaññaṃ hoti, dutiyā ca.</p>\r\n<p class="bodytext">Ārādhohaṃ rañño, ārādhohaṃ rājānaṃ, kyāhaṃ ayyānaṃ aparajjhāmi, kyāhaṃ ayye aparajjhāmi, cakkhuṃ janassa dassanāya taṃ viya maññe, āyasmato upālittherassa upasampadāpekkho upatisso, āyasmantaṃ vā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Paccāsuṇa anupatigiṇānaṃ pubbakattari suṇo</span>tissa <span class="bld">paccā</span>yoge yassa kammuno pubbassa yo kattā, so sampadānasañño hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Bhagavā bhikkhū etadavoca.</p>\r\n<p class="bodytext"><span class="bld">Bhikkhū</span>ti <span class="bld">akathīta kammaṃ, etanti kathitakammaṃ</span>. Yassa kammuno pubbassa yo kattā, so‘bhagavā’ti ‘‘yo karoti sa kattā’’ti suttavacanena kattusañño. Evaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',146,replace(replace('<p class="bodytext">yassa <a name="M0.0146"></a> kammuno pubbassa yo kattā, so sampadānasañño hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Te bhikkhū bhagavato paccassosuṃ, āsuṇanti buddhassa bhikkhū.</p>\r\n<p class="bodytext"><span class="bld">Giṇa</span>ssa <span class="bld">anupati</span>yoge yassa kammuno pubbassa yo kattā, so sampadānasañño hoti.</p>\r\n<p class="bodytext">Taṃ yathā? Bhikkhu janaṃ dhammaṃ sāveti, tassa bhikkhuno jano anugiṇāti, tassa bhikkhuno jano patigiṇāti.</p>\r\n<p class="gatha1">Yo vadeti sa‘kattā’ti,</p>\r\n<p class="gatha2">Vuttaṃ ‘kamma’nti vuccati;</p>\r\n<p class="gatha3">Yo paṭiggāhako tassa,</p>\r\n<p class="gathalast">‘Sampadānaṃ’ vijāniyā.</p>\r\n<p class="unindented">Iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ārocanatthe</span> – ārocayāmi vo bhikkhave, āmantayāmi vo bhikkhave, paṭivedayāmi vo bhikkhave, ārocayāmi te mahārāja, āmantayāmi te mahārāja, paṭivedayāmi te mahārāja iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Tadatthe</span> – ūnassa pāripūriyā taṃ cīvaraṃ nikkhipitabbaṃ. Buddhassa atthāya, dhammassa atthāya, saṅghassa atthāya, jīvitaṃ pariccajāmi iccevamādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',147,replace(replace('<p class="bodytext"><span class="bld">Tumatthe</span> <a name="M0.0147"></a> -lokānukampāya atthāya hitāya sukhāya devamanussānaṃ buddho loke uppajjati. Bhikkhūnaṃ phāsuvihārāya vinayo paññatto iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Alamatthappayoge-ala</span>miti arahati paṭikkhittesu. Alaṃ me buddho, alaṃ me rajjaṃ, alaṃ bhikkhu pattassa, alaṃ mallo mallassa, arahati mallo mallassa. <span class="bld">Paṭikkhitte</span> alaṃ te rūpaṃ karaṇīyaṃ, alaṃ me hiraññasuvaṇṇena iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Maññatippayoge anādare appāṇini</span>-kaṭṭhassa tuvaṃ maññe, kaliṅgarassa tuvaṃ maññe.</p>\r\n<p class="bodytext"><span class="bld">Anādare</span>ti kimatthaṃ? Suvaṇṇaṃ viya taṃ maññe.</p>\r\n<p class="bodytext"><span class="bld">Appāṇinī</span>ti kimatthaṃ? Gadrabhaṃ tuvaṃ maññe iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Gatyatthakammani</span>-gāmassa pādena gato, nagarassa pādena gato, appo saggāya gacchati, saggassa gamanena vā, mūlāya paṭikasseyya saṅgho. Dutiyā ca, gāmaṃ pādena gato, nagaraṃ pādena gato, appo saggaṃ gacchati, saggaṃ gamanena vā, mūlaṃ paṭikasseyya saṅgho iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Āsīsatthe</span>-āyasmato dīghāyuko hotu, bhaddaṃ bhavato hotu, kusalaṃ bhavato hotu, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',148,replace(replace('<p class="bodytext">anāmayaṃ <a name="M0.0148"></a> bhavato hotu, sukhaṃ bhavato hotu, svāgataṃ bhavato hotu, attho bhavato hotu, hitaṃ bhavato hotu iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Sammutippayoge</span>– aññatra saṅghasammutiyā bhikkhussa vippavatthuṃ na vaṭṭati, sādhu sammuti me tassa bhagavato dassanāya iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Bhiyyappayoge</span> – bhiyyoso mattāyaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Sattamyatthe</span> – tuyhañcassa āvi karomi, tassa me sakko pāturahosi iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Atthaggahaṇena</span> bahūsu akkharappayogesu dissati.</p>\r\n<p class="bodytext">Taṃ yathā? Upamaṃ mata karissāmi, dhammaṃ vo desessāmi.</p>\r\n<p class="bodytext"><span class="bld">Sāratthe</span> ca – desetu bhante bhagavā dhammaṃ bhikkhūnaṃ. Tassa phāsu vihārāya hoti, etassa pahiṇeyya, yathā no bhagavā byākareyya, tathāpi tesaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',149,replace(replace('<p class="bodytext">byākarissāma<a name="M0.0149"></a>, kappati samaṇānaṃ āyogo, amhākaṃ maṇinā attho, kimattho me buddhena, seyyo me attho, bahūpakārā bhante mahāpajā patigotamī bhagavato, bahūpakārā bhikkhave mātāpitaro puttānaṃ iccevamādi.</p>\r\n<p class="bodytext">Sesesu akkharappayogesupi aññepi payogā payogavicakkhaṇehi yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ vikappanatthavāggahaṇānukaḍḍhanatthaṃ</span>. Ye keci saddā sampadānappayogikā mayā nopadiṭṭhā, tesaṃ gahaṇatthaṃ idha vikappīyati vāsaddo.</p>\r\n<p class="bodytext">Taṃ yathā? Bhikkhusaṅghassa pabhū ayaṃ bhagavā, desassa pabhū ayaṃ rājā. Khettassa pabhū ayaṃ gahapati, araññassa pabhū ayaṃ luddako iccevamādi. Kvaci dutiyā tatiyā pañcamī chaṭṭhī sattamyatthesu ca.</p>\r\n<p class="bodytext"><a name="para178"></a><span class="paranum">178</span>, 320. <span class="bld">Yodhāro tamokāsaṃ</span>.</p>\r\n<p class="bodytext">Yo ādhāro, taṃ okāsasaññaṃ hoti. <span class="bld">Svādhāro catubbidho</span> byāpiko, opasilesiko, vesayiko sāmīpiko cāti.</p>','\r',char(13)),'\n',char(10)),'-178-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',150,replace(replace('<p class="bodytext">Tattha <a name="M0.0150"></a> <span class="bld">byāpiko</span> tāva–jalesu khīraṃ tiṭṭhati, tilesu telaṃ, ucchūsu raso.</p>\r\n<p class="bodytext"><span class="bld">Opasilesiko</span>–pariyaṅke rājā seti, āsane upaviṭṭho saṅgho.</p>\r\n<p class="bodytext"><span class="bld">Vesayiko</span>–bhūmīsu manussā caranti, antalikkhe vāyū vāyanti. Ākāse sakuṇā pakkhandanti.</p>\r\n<p class="bodytext"><span class="bld">Sāmīpiko</span>–vane hatthino caranti, gaṅgāyaṃ ghoso tiṭṭhati, vaje gāvo duhanti, sāvatthiyaṃ viharati jetavane.</p>\r\n<p class="bodytext"><span class="bld">Okāsa</span>miccanena kvattho? Okāse sattamī.</p>\r\n<p class="bodytext"><a name="para279"></a><span class="paranum">279</span>, 292. <span class="bld">Yena vā kayirate taṃ karaṇaṃ</span>.</p>\r\n<p class="bodytext">Yena vā kayirate, yena vā passati, yena vā suṇāti, taṃ kārakaṃ karaṇasaññaṃ hoti.</p>\r\n<p class="bodytext">Dattena vihiṃ lunāti, vāsiyā kaṭṭhaṃ tacchati, pharasunā rukkhaṃ chindati, kudālena pathaviṃ khaṇati, satthena kammaṃ karoti. Cakkhunā rūpaṃ passati.</p>\r\n<p class="bodytext"><span class="bld">Karaṇami</span>ccanena kvattho? Karaṇe tatiyā.</p>\r\n<p class="bodytext"><a name="para280"></a><span class="paranum">280</span>, 285. </p>','\r',char(13)),'\n',char(10)),'-279-280-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',151,replace(replace('<p class="bodytext"><span class="bld">Yaṃ</span> <a name="M0.0151"></a> <span class="bld">karoti taṃ kammaṃ</span>.</p>\r\n<p class="bodytext">Yaṃ vā karoti, yaṃ vā passati, yaṃ vā suṇāti, taṃ kārakaṃ kammasaññaṃ hoti.</p>\r\n<p class="bodytext">Chattaṃ karoti, rathaṃ karoti, rūpaṃ passati, saddaṃ suṇāti, kaṇṭakaṃ maddati, visaṃ gilati.</p>\r\n<p class="bodytext"><span class="bld">Kamma</span>miccanena kvattho? Kammatthe dutiyā.</p>\r\n<p class="bodytext"><a name="para281"></a><span class="paranum">281</span>, 294. <span class="bld">Yo karoti sa kattā</span>.</p>\r\n<p class="bodytext">Yo karoti, so kattusañño hoti.</p>\r\n<p class="bodytext">Ahinā daṭṭho naro, garuḷena hato nāgo. Buddhena jito māro, upaguttena māro bandho.</p>\r\n<p class="bodytext"><span class="bld">Kattu</span>iccanena kvattho? Kattari ca.</p>\r\n<p class="bodytext"><a name="para282"></a><span class="paranum">282</span>, 295. <span class="bld">Yo kāreti sa hetu</span>.</p>\r\n<p class="bodytext">Yo kattāraṃ kāreti, so hetusañño hoti, kattā ca.</p>\r\n<p class="bodytext">So puriso taṃ purisaṃ kammaṃ kāreti so puriso tena purisena kammaṃ kāreti, so puriso tassa purisassa kammaṃ kāreti. Evaṃ hāreti pāṭheti pāceti, dhāreti.</p>\r\n<p class="bodytext"><span class="bld">Hetu</span>iccanena kvattho? Dhātūhi ṇe ṇaya ṇāpe ṇāpayā kāritāni hetvatthe.</p>\r\n<p class="bodytext"><a name="para283"></a><span class="paranum">283</span>, 316. </p>','\r',char(13)),'\n',char(10)),'-281-282-283-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',152,replace(replace('<p class="bodytext"><span class="bld">Yassa</span> <a name="M0.0152"></a> <span class="bld">vā pariggaho taṃ sāmī</span>.</p>\r\n<p class="bodytext">Yassa vā pariggaho, taṃ sāmīsaññaṃ hoti.</p>\r\n<p class="bodytext">Tassa bhikkhuno paṭivīso, tassa bhikkhuno patto, tassa bhikkhuno cīvaraṃ, attano mukhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Sāmī</span>iccanena kvattho? Sāmismiṃ chaṭṭhī.</p>\r\n<p class="bodytext"><a name="para284"></a><span class="paranum">284</span>, 283. <span class="bld">Liṅgatthe paṭhamā</span>.</p>\r\n<p class="bodytext">Liṅgatthābhidhānamatte paṭhamāvibhatti hoti.</p>\r\n<p class="bodytext">Puriso, purisā, eko, dve, ca, vā, he, ahe, re, are.</p>\r\n<p class="bodytext"><a name="para285"></a><span class="paranum">285</span>, 70. <span class="bld">Ālapane ca</span>.</p>\r\n<p class="bodytext">Ālapanatthā dhike liṅgatthābhidhānamatte ca paṭhamāvibhatti hoti.</p>\r\n<p class="bodytext">Bho purisa, bhavanto purisā, bho rāja, bhavanto rājāno, he sakhe, he sakhino.</p>\r\n<p class="bodytext"><a name="para286"></a><span class="paranum">286</span>, 291. <span class="bld">Karaṇe tatiyā</span>.</p>\r\n<p class="bodytext">Karaṇakārake tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Agginā kuṭiṃ jhāpeti, manasā ce paduṭṭhena, manasā ce pasannena, kāyena kammaṃ karoti.</p>\r\n<p class="bodytext"><a name="para287"></a><span class="paranum">287</span>, 299. </p>','\r',char(13)),'\n',char(10)),'-284-285-286-287-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',153,replace(replace('<p class="bodytext"><span class="bld">Sahādiyoge</span> <a name="M0.0153"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Sahādiyogatthe ca tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Sahāpi gaggena saṅgho uposathaṃ kareyya, vināpi gaggena, mahatā bhikkhusaṅghena saddhiṃ, sahassena samaṃ mitā.</p>\r\n<p class="bodytext"><a name="para288"></a><span class="paranum">288</span>, 293. <span class="bld">Kattari ca</span>.</p>\r\n<p class="bodytext">Kattari ca tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Raññā hato poso, yakkhena dinno varo, ahinā daṭṭho naro.</p>\r\n<p class="bodytext"><a name="para289"></a><span class="paranum">289</span>, 297. <span class="bld">Hetvatthe ca</span>.</p>\r\n<p class="bodytext">Hetvatthe ca tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Annena vasati, dhammena vasati, vijjāya vasati, sakkārena vasati.</p>\r\n<p class="bodytext"><a name="para290"></a><span class="paranum">290</span>, 298. <span class="bld">Sattamyatthe ca</span>.</p>\r\n<p class="bodytext">Sattamyatthe ca tatiyāvibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-288-289-290-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',154,replace(replace('<p class="bodytext">Tena <a name="M0.0154"></a> kālena, tena samayena. (Yena kālena, yena samayena,) tena kho pana samayena.</p>\r\n<p class="bodytext"><a name="para291"></a><span class="paranum">291</span>, 299. <span class="bld">Yenaṅgavikāro</span>.</p>\r\n<p class="bodytext">Yena byādhimatā aṅgena aṅgino vikāro lakkhīyate. Tattha tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Akkhinā kāṇo, hatthena kuṇī, kāṇaṃ passati nettena, pādena khañjo, piṭṭhiyā khujjo.</p>\r\n<p class="bodytext"><a name="para292"></a><span class="paranum">292</span>, 300. <span class="bld">Visesane ca</span>.</p>\r\n<p class="bodytext">Visesanatthe ca tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">Gottena gotamo nātho, suvaṇṇena abhirūpo, tapasā uttamo.</p>\r\n<p class="bodytext"><a name="para293"></a><span class="paranum">293</span>, 301. <span class="bld">Sampadāne catutthī</span>.</p>\r\n<p class="bodytext">Sampadānakārake catutthīvibhatti hoti.</p>\r\n<p class="bodytext">Buddhassa vā dhammassa vā saṅghassa vā dānaṃ deti, dātā hoti samaṇassa vā brāhmaṇassa vā.</p>\r\n<p class="bodytext"><a name="para294"></a><span class="paranum">294</span>, 305. <span class="bld">Namoyogādīsvapi ca</span>.</p>\r\n<p class="bodytext">Namoyogādīsvapi ca catutthīvibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-291-292-293-294-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',155,replace(replace('<p class="bodytext">Namo <a name="M0.0155"></a> te buddha vīratthu, sotthi pajānaṃ, namo karohi nāgassa, svāgataṃ te mahārāja.</p>\r\n<p class="bodytext"><a name="para295"></a><span class="paranum">295</span>, 307. <span class="bld">Apādāne pañcamī</span>.</p>\r\n<p class="bodytext">Apādānakārake pañcamīvibhatti hoti.</p>\r\n<p class="bodytext">Pāpā cittaṃ nivāraye, abbhā muttova candimā, bhayā muccati so naro.</p>\r\n<p class="bodytext"><a name="para296"></a><span class="paranum">296</span>, 314. <span class="bld">Kāraṇatthe ḍha</span>.</p>\r\n<p class="bodytext">Kāraṇatthe ca pañcamīvibhatti hoti.</p>\r\n<p class="bodytext">Ananubodhā appaṭivedhā catunnaṃ ariyasaccānaṃ yathābhūtaṃ adassanā.</p>\r\n<p class="bodytext"><a name="para297"></a><span class="paranum">297</span>, 284. <span class="bld">Kammatthe dutiyā</span>.</p>\r\n<p class="bodytext">Kammatthe dutiyāvibhatti hoti.</p>\r\n<p class="bodytext">Gāvaṃ hanati, vīhayo lunāti, satthaṃ karoti, ghaṭaṃ karoti, rathaṃ karoti, dhammaṃ suṇāti, buddhaṃ pūjeti, vācaṃ bhāsatī, taṇḍulaṃ vacati, coraṃ ghāteti.</p>\r\n<p class="bodytext"><a name="para298"></a><span class="paranum">298</span>, 287. <span class="bld">Kāladdhānamaccantasaṃyoge</span>.</p>\r\n<p class="bodytext">Kāladdhānaṃ accantasaṃyoge dutiyāvibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-295-296-297-298-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',156,replace(replace('<p class="bodytext">Māsaṃ <a name="M0.0156"></a> maṃsodanaṃ bhuñjati, saradaṃ ramaṇīyā nadī, māsaṃ sajjhāyati, yojanaṃ vanarāji, yojanaṃ dīgho pabbato, kosaṃ sajjhāyati.</p>\r\n<p class="bodytext"><span class="bld">Accantasaṃyoge</span>ti kimatthaṃ? Saṃvacchare bhojanaṃ bhuñjati.</p>\r\n<p class="bodytext"><a name="para299"></a><span class="paranum">299</span>, 288. <span class="bld">Kammappavadhanīyayutte</span>.</p>\r\n<p class="bodytext"><span class="bld">Kammappavacanīya</span>yutte dutiyāvibhatti hoti.</p>\r\n<p class="bodytext">Taṃ kho pana bhavantaṃ gotamaṃ evaṃ kalyāṇo kittisaddo abbhuggato, pabbajitamanupabbajiṃ<span class="bld">su</span>.</p>\r\n<p class="bodytext"><a name="para300"></a><span class="paranum">300</span>, 286. <span class="bld">Gati buddhi bhuja paṭha hara kara sayādīnaṃ kārite vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Gati buddhi bhuja paṭha hara kara sayā</span>dīnaṃ payoge kārite dutiyāvibhatti hoti vā.</p>\r\n<p class="bodytext">Puriso purisaṃ (gāmaṃ) gāmayati, puriso purisena vā, puriso purisassa vā. Evaṃ bodhayati, bhojayati, pāṭhayati, hārayati, kārayati, sayāpayati. Evaṃ sabbattha kārite.</p>\r\n<p class="bodytext"><a name="para301"></a><span class="paranum">301</span>, 215. <span class="bld">Sāmismiṃ chaṭṭhī</span>.</p>\r\n<p class="bodytext">Sāmismiṃ chaṭṭhīvibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-299-300-301-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',157,replace(replace('<p class="bodytext">Tassa <a name="M0.0157"></a> bhikkhuno paṭivīso, tassa bhikkhuno patto, tassa bhikkhuno cīvaraṃ, attano mukhaṃ.</p>\r\n<p class="bodytext"><a name="para302"></a><span class="paranum">302</span>, 319. <span class="bld">Okāse sattamī</span>.</p>\r\n<p class="bodytext">Okāsakārake sattamīvibhatti hoti.</p>\r\n<p class="bodytext">Gambhīre odakantike, pāpasmiṃ ramati mano, bhagavati brahmacariyaṃ vussati kulaputto.</p>\r\n<p class="bodytext"><a name="para303"></a><span class="paranum">303</span>, 321. <span class="bld">Sāmissarādhipati dāyāda sakkhīpatibhū pasutakusalehi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Sāmī issara adhipati dāyāda sakkhīpatibhū pasutakusala i</span>ccetehi payoge chaṭṭhīvibhatti hoti, sattamī ca.</p>\r\n<p class="bodytext">Goṇānaṃ sāmī, goṇesu sāmī, goṇānaṃ issaro, goṇesu issaro. Goṇānaṃ adhipati, goṇesu adhipati. Goṇānaṃ dāyādo, goṇesu dāyādo. Goṇānaṃ sakkhī, goṇesu sakkhī, goṇānaṃ patibhū, goṇesu patibhū. Goṇānaṃ pasuto, goṇesu pasuto. Goṇānaṃ kusalo, goṇesu kusalo.</p>\r\n<p class="bodytext"><a name="para304"></a><span class="paranum">304</span>, 322. <span class="bld">Niddhāraṇe ca</span>.</p>\r\n<p class="bodytext">Niddhāraṇatthe ca chaṭṭhīvibhatti hoti, sattamī ca.</p>','\r',char(13)),'\n',char(10)),'-302-303-304-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',158,replace(replace('<p class="bodytext">Kaṇhā <a name="M0.0158"></a> gāvīnaṃ sampannakhīratamā, kaṇhā gāvīsu sampannakhīratamā. Sāmā nārīnaṃ dassanīyatamā, sāmā nārīsu dassanīyatamā. Manussānaṃ khattiyo sūratamo, manussesu khattiyo sūratamo. Pathikānaṃ dhāvanto sīghatamo, pathikesu dhāvanto sīghatamo.</p>\r\n<p class="bodytext"><a name="para305"></a><span class="paranum">305</span>, 323. <span class="bld">Anādare ca</span>.</p>\r\n<p class="bodytext">Anādare chaṭṭhīvibhatti hoti, sattamī ca.</p>\r\n<p class="bodytext">Rudato dārakassa pabbaji, rudantasmiṃ dārake pabbaji.</p>\r\n<p class="bodytext"><a name="para306"></a><span class="paranum">306</span>, 289. <span class="bld">Kvaci dutiyā chaṭṭhīnamatthe</span>.</p>\r\n<p class="bodytext">Chaṭṭhīnamatthe kvaci dutiyāvibhatti hoti.</p>\r\n<p class="bodytext">Apissu maṃ aggivessana tisso upamā paṭibhaṃsu.</p>\r\n<p class="bodytext"><a name="para307"></a><span class="paranum">307</span>, 290. <span class="bld">Tatiyāsattamīnañca</span>.</p>\r\n<p class="bodytext">Tatiyāsattamīnaṃ atthe ca kvaci dutiyāvibhatti hoti.</p>\r\n<p class="bodytext">Sace maṃ samaṇo gotamo ālapissati, tvañca maṃ nābhibhāsasi. Evaṃ tatiyatthe.</p>\r\n<p class="bodytext">Pubbaṇhasamayaṃ nivāsetvā, ekaṃ samayaṃ bhagavā. Evaṃ sattamyatthe.</p>\r\n<p class="bodytext"><a name="para308"></a><span class="paranum">308</span>, 317. </p>','\r',char(13)),'\n',char(10)),'-305-306-307-308-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',159,replace(replace('<p class="bodytext"><span class="bld">Chaṭṭhī</span> <a name="M0.0159"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Tatiyāsattamīnaṃ atthe ca kvaci chaṭṭhīvibhatti hoti.</p>\r\n<p class="bodytext">Kato me kalyāṇo, kataṃ me pāpaṃ. Evaṃ tatiyatthe.</p>\r\n<p class="bodytext">Kusalā naccagītassa sikkhitā cāturitthiyo, kusalo tvaṃ rathassa aṅgapaccaṅgānaṃ. Evaṃ sattamyatthe.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Yo vo ānanda mayā dhammo ca vinayo ca desito paññatto, ānando atthesu vicakkhaṇo.</p>\r\n<p class="bodytext"><a name="para309"></a><span class="paranum">309</span>, 318. <span class="bld">Dutiyāpañcamīnañca</span>.</p>\r\n<p class="bodytext">Dutiyāpañcamīnañca atthe kvaci chaṭṭhīvibhatti hoti.</p>\r\n<p class="bodytext">Tassa bhavanti vattāro, sahasā kammassa kattāro, evaṃ dutiyatthe.</p>\r\n<p class="bodytext">Assavanatā dhammassa parihāyanti, kinnu kho ahaṃ tassa sukhassa bhāyāmi, sabbe tasanti daṇḍassa, sabbe bhāyanti maccuno, bhīto catunnaṃ āsīvisānaṃ ghoravisānaṃ, bhāyāmi ghoravisassa nāgassa. Evaṃ pañcamyatthe.</p>\r\n<p class="bodytext"><a name="para310"></a><span class="paranum">310</span>, 324. </p>','\r',char(13)),'\n',char(10)),'-309-310-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',160,replace(replace('<p class="bodytext"><span class="bld">Kamma</span> <a name="M0.0160"></a> <span class="bld">karaṇa nimittatthesu sattamī</span>.</p>\r\n<p class="bodytext">Kammakaraṇanimittatthesu sattamīvibhatti hoti.</p>\r\n<p class="bodytext">Sundarāvuso ime ājīvakā bhikkhūsu abhivādenti. Evaṃ kammatthe.</p>\r\n<p class="bodytext">Hatthesu piṇḍāya caranti, pattesu piṇḍāya caranti, pathesu gacchanti. Evaṃ karaṇatthe.</p>\r\n<p class="bodytext">Dīpi cammesu haññate, kuñjaro dantesu haññate, evaṃ nimittatthe.</p>\r\n<p class="bodytext"><a name="para311"></a><span class="paranum">311</span>, 325. <span class="bld">Sampadāne ca</span>.</p>\r\n<p class="bodytext">Sampadāne ca sattamīvibhatti hoti.</p>\r\n<p class="bodytext">Saṅghe dinnaṃ mahapphala, saṅghe gotamī dehi, saṅghe te dinne ahañceva pūjito bhavissāmi.</p>\r\n<p class="bodytext"><a name="para312"></a><span class="paranum">312</span>, 326. <span class="bld">Pañcamyatthe ca</span>.</p>\r\n<p class="bodytext">Pañcamyatthe ca sattamīvibhatti hoti.</p>\r\n<p class="bodytext">Kadalīsu gaje rakkhanti.</p>\r\n<p class="bodytext"><a name="para313"></a><span class="paranum">313</span>, 327. <span class="bld">Kālabhāvesu ca</span>.</p>\r\n<p class="bodytext">Kālabhāvesu ca kattari payujjamāne sattamīvibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-311-312-313-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',161,replace(replace('<p class="bodytext">Pubbaṇhasamaye <a name="M0.0161"></a> gato, sāyanhasamaye āgato. Bhikkhūsu bhojīyamānesu gato, bhuttesu āgato. Gosuduyhamānesu gato, duddhāsu āgato.</p>\r\n<p class="bodytext"><a name="para314"></a><span class="paranum">314</span>, 328. <span class="bld">Upa’jhādhikissaravacane</span>.</p>\r\n<p class="bodytext"><span class="bld">Upaadhi</span>iccetesaṃ payoge adhikaissaravacane sattamīvibhatti hoti.</p>\r\n<p class="bodytext">Upa khāriyaṃ doṇo, upa nikkhe kahāpaṇaṃ. Adhi brahmadatte pañcālā, adhi naccesu gotamī, adhi devesu buddho.</p>\r\n<p class="bodytext"><a name="para315"></a><span class="paranum">315</span>, 329. <span class="bld">Maṇḍitu’ssukkesu tatiyā</span>.</p>\r\n<p class="bodytext"><span class="bld">Maṇḍitaussukka</span>iccetesvatthesu tatiyāvibhatti hoti, sattamī ca.</p>\r\n<p class="bodytext">Ñāṇena pasīdito, ñāṇasmiṃ vā pasīdito, ñāṇena ussukko, ñāṇasmiṃ vā ussukko tathāgato vā tathāgatagotto vā.</p>\r\n<p class="centered">Iti nāmakappe kārakakappo chaṭṭho kaṇḍo.</p>\r\n<p class="centered">Kārakakappo niṭṭhito.</p>\r\n<p class="chapter">4. Samāsakappa</p>\r\n<p class="subhead">Sattamakaṇḍa</p>\r\n<p class="bodytext"><a name="para316"></a><span class="paranum">316</span>, 331. </p>','\r',char(13)),'\n',char(10)),'-314-315-316-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',162,replace(replace('<p class="bodytext"><span class="bld">Nāmānaṃ</span> <a name="M0.0162"></a> <span class="bld">samāso yuttattho</span>.</p>\r\n<p class="bodytext">Tesaṃ nāmānaṃ payujjamānapadatthānaṃ yo yuttattho, so samāsasañño hoti.</p>\r\n<p class="bodytext">Kathinadussaṃ, āgantukabhattaṃ, jīvitindriyaṃ, samaṇabrāhmaṇā, sāriputtamoggallānā, brāhmaṇa gahapatikā.</p>\r\n<p class="bodytext"><span class="bld">Nāmāna</span>miti kimatthaṃ? Devadatto pacati, yaññadatto pacati.</p>\r\n<p class="bodytext"><span class="bld">Yuttattho</span>ti kimatthaṃ? Bhato rañño putto devadattassa.</p>\r\n<p class="bodytext"><span class="bld">Samāsa</span>iccanena kvattho? Kvaci samāsantagatānamakāranto.</p>\r\n<p class="bodytext"><a name="para317"></a><span class="paranum">317</span>, 332. <span class="bld">Tesaṃ vibhattiyo lopā ca</span>.</p>\r\n<p class="bodytext">Tesaṃ yuttatthānaṃ samāsānaṃ vibhattiyo lopā ca honti.</p>','\r',char(13)),'\n',char(10)),'-317-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',163,replace(replace('<p class="bodytext">Kathinadussaṃ<a name="M0.0163"></a>, āgantukabhattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Tesaṃgahaṇena</span> samāsataddhitākhyātakitakānaṃ vibhattipaccayapadakkharāgamā ca lopā honti. Vāsiṭṭho, venateyyo.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇa</span> mavadhāraṇatthaṃ, pabhaṅkaro, amataddado, medhaṅkaro, dīpaṅkaro.</p>\r\n<p class="bodytext"><a name="para318"></a><span class="paranum">318</span>, 333. <span class="bld">Pakati cassa sarantassa</span>.</p>\r\n<p class="bodytext">Luttāsu vibhattīsu assa sarantassa liṅgassa pakatirūpāni honti.</p>\r\n<p class="bodytext">Cakkhusotaṃ, mukhanāsikaṃ, rājaputto, rājapuriso.</p>\r\n<p class="bodytext"><a name="para319"></a><span class="paranum">319</span>, 330. <span class="bld">Upasagganipātapubbako abyayībhāvo</span>.</p>\r\n<p class="bodytext"><span class="bld">Upasagganipāta</span>pubbako samāso <span class="bld">abyayībhāva</span>sañño hoti.</p>','\r',char(13)),'\n',char(10)),'-318-319-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',164,replace(replace('<p class="bodytext">Nagarassa <a name="M0.0164"></a> samīpe pavattati kathā iti upanagaraṃ, darathānaṃ abhāvo niddarathaṃ, makasānaṃ abhāvo nimmakasaṃ, vuḍḍhānaṃ paṭipāṭi yathāvuḍḍhaṃ, ye ye vuḍḍhā vā yathāvuḍḍhaṃ, jīvassa yattako paricchedo yāvajīvaṃ, cittamadhikicca pavattanti te dhammāti adhicittaṃ, pabbatassa tiro tiropabbataṃ, sotassa pati pavattati nāvā iti patisotaṃ, pāsādassa anto antopāsādaṃ.</p>\r\n<p class="bodytext"><span class="bld">Abyayībhā</span>vamiccanena kvattho? Aṃvibhattīna makārantā abyayībhāvā.</p>\r\n<p class="bodytext"><a name="para320"></a><span class="paranum">320</span>, 335. <span class="bld">So napuṃsakaliṅgo</span>.</p>\r\n<p class="bodytext">So abyayībhāvasamāso napuṃsakaliṅgova daṭṭhabbo.</p>\r\n<p class="bodytext">Kumārīsu adhikicca pavattati kathā iti adhikumāri, vadhuyā samīpe pavattati kathā iti upavadhu, gaṅgāya samīpe pavattati kathā iti upagaṅgaṃ, maṇikāya samīpe pavattati kathā iti upamaṇikaṃ.</p>\r\n<p class="bodytext"><a name="para321"></a><span class="paranum">321</span>, 349. <span class="bld">Digussekattaṃ</span>.</p>\r\n<p class="bodytext">Digussa samāsassa ekattaṃ hoti, napuṃsakaliṅgattañca.</p>','\r',char(13)),'\n',char(10)),'-320-321-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',165,replace(replace('<p class="bodytext">Tayo <a name="M0.0165"></a> lokā tilokaṃ, tayo daṇḍā tidaṇḍaṃ, tīṇi nayanāni tinayanaṃ, tayo siṅgā tisiṅgaṃ. Catasso disā catuddisaṃ, pañca indriyāni pañcindriyaṃ.</p>\r\n<p class="bodytext"><a name="para322"></a><span class="paranum">322</span>, 359. <span class="bld">Tathā dvande pāṇi tūriya yoggasenaṅgakhuddajantuka vividhaviruddha visabhāgatthādīnañca</span>.</p>\r\n<p class="bodytext">Tathā dvande samāse <span class="bld">pāṇi tūriya yogga senaṅgakhuddajantuka vividhaviruddha visabhāgattha</span>iccevamādīnaṃ ekattaṃ hoti, napuṃsakaliṅgattañca.</p>\r\n<p class="bodytext">Taṃ yathā? Cakkhu ca sotañca cakkhusotaṃ, mukhañca nāsikā ca mukhanāsikaṃ, chavi ca maṃsañca lohitañca chavimaṃsalohitaṃ. Evaṃ <span class="bld">pāṇyaṅgatthe</span>.</p>\r\n<p class="bodytext">Saṅkho ca paṇavo ca saṅkhapaṇavaṃ, gītañca vāditañca gītavāditaṃ, daddari ca ḍiṇḍimo ca daddariḍiṇḍimaṃ. Evaṃ <span class="bld">tūriyaṅgatthe</span>.</p>\r\n<p class="bodytext">Phālo ca pācanañca phālapācanaṃ, yugañca naṅgalañca yuganaṅgalaṃ. Evaṃ <span class="bld">yoggaṅgatthe</span>.</p>\r\n<p class="bodytext">Asi ca cammañca asicammaṃ, dhanu ca kalāpo ca dhanukalāpaṃ, hatthī ca asso ca hatthiassaṃ, ratho ca pattiko ca rathapattikaṃ. Evaṃ <span class="bld">senaṅgatthe</span>.</p>','\r',char(13)),'\n',char(10)),'-322-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',166,replace(replace('<p class="bodytext">Ḍaṃsā <a name="M0.0166"></a> ca makasā ca ḍaṃsamakasaṃ, kuntho ca kipilliko ca kunthakipillikaṃ, kīṭo ca sarīsapo ca kīṭasarīsapaṃ. Evaṃ <span class="bld">khuddajantukatthe</span>.</p>\r\n<p class="bodytext">Ahi ca nakulo ca ahinakulaṃ, biḷāro ca mūsiko ca biḷāramūsikaṃ, kāko ca ulūko ca kākolūkaṃ. Evaṃ <span class="bld">vividhaviruddhatthe</span>.</p>\r\n<p class="bodytext">Sīlañca paññāṇañca sīlapaññāṇaṃ, samatho ca vipassanā ca samathavipassanaṃ, vijjā ca caraṇañca vijjācaraṇaṃ. Evaṃ <span class="bld">visabhāgatthe</span>.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇaṃ</span> kimatthaṃ? Dāsī ca dāso ca dāsidāsaṃ, itthī ca pumā ca itthipumaṃ, patto ca cīvarañca pattacīvaraṃ, chattañca upāhanā ca chattupāhanaṃ, tikañca catukkañca tikacatukkaṃ, veno ca rathakāro ca venarathakāraṃ, sākuṇiko ca māgaviko ca savakuṇikamāgavikaṃ, dīgho ca majjhimo ca dīghamajjhimaṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para323"></a><span class="paranum">323</span>, 360. <span class="bld">Vibhāsā rukkha tiṇa pasukha na dhañña janapadādīnañca</span>.</p>\r\n<p class="bodytext"><span class="bld">Rukkhatiṇa pasu dhana dhañña janapada</span>iccevamādīnaṃ vibhāsā ekattaṃ hoti, napuṃsakaliṅgattañca dvande samāse. </p>','\r',char(13)),'\n',char(10)),'-323-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',167,replace(replace('<p class="bodytext">Assattho <a name="M0.0167"></a> ca kapītano ca assatthakapītanaṃ, assatthakapītanā vā. Usīrañca bīraṇañca usīrabīraṇaṃ, usīrabīraṇā vā. Ajo ca eḷako ca ajeḷakaṃ, ajeḷakā vā. Hiraññañca suvaṇṇañca hiraññasuvaṇṇaṃ, hiraññasuvaṇṇā vā. Sāli ca yavo ca sāliyavaṃ, sāliyavā vā. Kāsī ca kosalā ca kāsikosalaṃ, kāsikosalā vā.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇaṃ</span> kimatthaṃ? Sāvajjañca anavajjañca sāvajjānavajjaṃ, sāvajjā navajjā vā. Hīnañca paṇītañca hīnapaṇītaṃ. Hīnapaṇītā vā. Kaṇho ca sukko ca kaṇhasukkaṃ, kaṇhasukkā vā.</p>\r\n<p class="bodytext"><a name="para324"></a><span class="paranum">324</span>, 339. <span class="bld">Dvipade tulyādhikaraṇe kammadhārayo</span>.</p>\r\n<p class="bodytext">Dve padāni tulyādhikaraṇāni yadā samasyante, tadā so samāso <span class="bld">kammadhāraya</span>sañño hoti.</p>','\r',char(13)),'\n',char(10)),'-324-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',168,replace(replace('<p class="bodytext">Mahanto <a name="M0.0168"></a> ca so puriso cāti mahāpuriso, kaṇho ca so sappo cāti kaṇhasappo, nīlañca taṃ uppalañcāti nīluppalaṃ, lohitañca taṃ candanañcāti lohitacandanaṃ, brāhmaṇī ca sā dārikā cāti brāhmaṇadārikā, khattiyā ca sā kaññā cāti khattiyakaññā.</p>\r\n<p class="bodytext"><span class="bld">Kammadhāraya</span>iccanena kvattho? Kammadhārayasaññe ca.</p>\r\n<p class="bodytext"><a name="para325"></a><span class="paranum">325</span>, 348. <span class="bld">Saṅkhyāpubbo digu</span>.</p>\r\n<p class="bodytext">Saṅkhyāpubbo kammadhārayasamāso <span class="bld">digu</span>sañño hoti.</p>\r\n<p class="bodytext">Tīṇi malāni timalaṃ, tīṇi phalāni tiphalaṃ, tayo lokā tilokaṃ, tayo daṇḍā tidaṇḍaṃ, catasso disā catuddisaṃ, pañca indriyāni pañcindriyaṃ, satta godāvariyo sattagodāvaraṃ.</p>\r\n<p class="bodytext"><span class="bld">Digu</span>iccanena kvattho? Digusse kattaṃ.</p>\r\n<p class="bodytext"><a name="para326"></a><span class="paranum">326</span>, 341. <span class="bld">Ubhe tappurisā</span>.</p>\r\n<p class="bodytext">Ubhe digukammadhārayasamāsā <span class="bld">tappurisa</span>saññā honti.</p>\r\n<p class="bodytext">Na brāhmaṇo abrāhmaṇo, na vasalo avasalo, na bhikkhu abhikkhu, na pañcavassaṃ apañcavassaṃ, </p>','\r',char(13)),'\n',char(10)),'-325-326-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',169,replace(replace('<p class="bodytext">na <a name="M0.0169"></a> pañcapūlī apañcapūlī, na sattagodāvaraṃasattagodāvaraṃ, na dasagavaṃ adasagavaṃ, na pañcagavaṃ apañcagavaṃ.</p>\r\n<p class="bodytext"><span class="bld">Tappurisa</span>iccanena kvattho? Attaṃ nassa tappurise.</p>\r\n<p class="bodytext"><a name="para327"></a><span class="paranum">327</span>, 351. <span class="bld">Amādayo parapadebhi</span>.</p>\r\n<p class="bodytext">Tā amādayo nāmehi parapadebhi yadā samasyante, tadā so samāso <span class="bld">tappurisa</span>sañño hoti.</p>\r\n<p class="bodytext">Bhūmiṃ gato bhūmigato, sabbarattiṃ sobhaṇo sabbarattisobhaṇo. Apāyaṃ gato apāyagato, issarena kataṃ issarakataṃ, sallena viddho sallaviddho, kathinassa dussaṃ kathinadussaṃ, āgantukassa bhattaṃ āgantukabhattaṃ, methunā apeto methunāpeto, corā bhayaṃ corabhayaṃ, rañño putto rājaputto, dhaññānaṃ rāsi dhaññarāsi, rūpe saññā rūpasaññā, saṃsāre dukkhaṃ saṃsāradukkhaṃ.</p>\r\n<p class="bodytext"><a name="para328"></a><span class="paranum">328</span>, 352. <span class="bld">Aññapadatthesu bahubbīhi</span>.</p>\r\n<p class="bodytext">Aññesaṃ padānaṃ atthesu dve nāmāni bahūni nāmāni yadā samasyante, tadā so samāso <span class="bld">bahubbīhi</span> sañño hoti.</p>','\r',char(13)),'\n',char(10)),'-327-328-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',170,replace(replace('<p class="bodytext">Āgatā <a name="M0.0170"></a> samaṇā imaṃ saṅghārāmaṃ soyaṃ āgatasamaṇo, saṅghārāmo. Jitāni indriyāni anena samaṇena soyaṃ jitindriyo, samaṇo. Dinno suṅko yassa rañño soyaṃ dinnasuṅko, rājā. Niggatā janā asmā gāmā soyaṃ niggatajano, gāmo. Chinno hatto yassa purisassa soyaṃ chinnahattho, puriso. Sampannāni sassāni yasmiṃ janapade soyaṃ sampannasasso, janapado.</p>\r\n<p class="bodytext">Nigrodhassa parimaṇḍalo nigrodhaparimaṇḍalo, nigrodhaparimaṇḍalo iva parimaṇḍalo yo rājakumāro soyaṃ nigrodhaparimaṇḍalo. Atha vā nigrodhaparimaṇḍalo iva parimaṇḍalo yassa rājakumārassa soyaṃ nigrodhaparimaṇḍalo, rājakumāro.</p>\r\n<p class="bodytext">Cakkhuno bhūto cakkhubhūto, cakkhubhūto iva bhūto yo bhagavā soyaṃ cakkhubhūto, bhagavā.</p>\r\n<p class="bodytext">Suvaṇṇassa vaṇṇo suvaṇṇavaṇṇo, suvaṇṇavaṇṇo viya vaṇṇo yassa bhagavato soyaṃ suvaṇṇavaṇṇo, bhagavā.</p>\r\n<p class="bodytext">Brahmuno saro brahmassaro, brahmassaro viya saro yassa bhagavato soyaṃ brahmassaro, bhagavā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',171,replace(replace('<p class="bodytext"><span class="bld">Sayaṃ</span> <a name="M0.0171"></a> <span class="bld">patita paṇṇa pupphaphalavāyutoyāhārā</span>ti paṇṇañca pupphañca phalañca paṇṇapupphaphalāni, sayameva patitāni sayaṃpatitāni, sayaṃpatitāni ca tāni paṇṇapupphaphalāni ceti sayaṃpatitapaṇṇapupphaphalāni vāyu ca toyañca vāyutoyāni, sayaṃpatitapaṇṇapupphaphalāni ca vāyutoyāni ca sayaṃpatitapaṇṇapupphaphalavāyutoyāni. Sayaṃpatitapaṇṇapupphaphalavāyutoyāni āhārā yesaṃ te sayaṃpatitapaṇṇapupphaphalavāyutoyāhārā, isayo. Yamettha vattabbaṃ, taṃ heṭṭhā vuttameva. Ayaṃ pana dvandakammadhārayagabbho tulyādhikaraṇapahubbīhi.</p>\r\n<p class="bodytext">Atha vā – sayaṃpatitapaṇṇapupphaphalavāyutoyehi āhārā yesaṃ te sayaṃpatitapaṇṇapupphaphalavāyutoyāhārā. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Nānā dumapatita pupphavāsita sānū</span>ti nānāpakārā dumā nānādumā, nānādumehi patitāni nānādumapati tāni, nānādumapatitāni ca tāni pupphāni ceti nānādumapatitapupphāni, nānādumapatitapupphehi vāsitā nānādumapatitapupphavāsitā, nānādumapatitapupphavāsitā sānū yassa pabbatarājassa soyaṃ nānādumapatitapupphavāsitasānu, pabbatarājā. Ayaṃ pana kammadhārayatappurisagabbho tulyādhikaraṇabahubbīhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',172,replace(replace('<p class="bodytext">Atha <a name="M0.0172"></a> vā – vāsitā sānū vāsitasānu, sāpekkhatte satipi gamakattā samāso. Nānādumapatitapupphehi vāsitasānū yassa pabbatarājassa soyaṃ nānādumapatitapupphavāsitasānu, pabbatarājā. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Byālambambudharabinducumbitakūṭo</span>ti ambuṃ dhāretīti ambudharo, ko so? Pajjunno. Vividhā ālambo byālambo, byālambo ca so ambudharo cāti byālambambudharo, byālambambudharassa bindū byālambambudharabindū, byālambambudharabindūhi cumbito byālambambudharabinducumbito, byālambamburebinducumbito kūṭo yassa pabbatarājassa soyaṃ byālambambudharabinducumbitakūṭo. Ayaṃ pana kammadhārayatappurisagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext">Atha vā – cumbito kūṭo cumbitakūṭo, sāpekkhatte satipi gamakattā samāso. Byālambambudharabindūhi cumbitakūṭo yassa pabbatarājassa soyaṃ byālambambudharabinducumbitakūṭo. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Amita bala parakkamajutī</span>ti na mitā amitā, balañca parakkamo ca juti ca balaparakkamajutiyo, amitā balaparakkamajutiyo yassa soyaṃ amitabalaparakkamajuti, ayaṃ pana kammadhārayadvandagabbho tulyādhikaraṇabahubbīhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',173,replace(replace('<p class="bodytext"><span class="bld">Pīṇorakkhaṃsa</span> <a name="M0.0173"></a> <span class="bld">bāhū</span>ti uro ca akkhañca aṃso ca bāhu ca urakkhaṃsabāhavo, pīṇā urakkhaṃsabāhavo yassa bhagavato soyaṃ pīṇorakkhaṃsabāhu. Ayaṃ pana dvandavabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Pīṇa gaṇḍa vadana thanūrujaghanā</span>ti gaṇḍo ca vadanañca thano ca ūru ca jaghanañca gaṇḍavadanathanūrujaghanā, pīṇā gaṇḍavadanathanūrujaghanā yassā sāyaṃ pīṇagaṇḍavadanathanūrujaghanā. Ayampi dvandagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Vavara surāsura garuḍa manuja bhujaga gandhabba makuṭa kūṭa cumbita selasaṅghaṭṭita caraṇo</span>ti surā ca asurā ca garuḍā ca manujā ca bhujagā ca gandhabbā ca surāsuragaruḍamanujabhujagagandhabbā, pavarā ca te surāsuragaruḍamanujabhujaga gandhabbā ceti pavarasurāsuragaruḍamanujabhujagagandhabbā, pavarasurāsuragaruḍamanujabhujagagandhabbānaṃ makuṭāni pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭāni, pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭānaṃ kūṭāni pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭāni, pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭesu cumbitā pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭacumbitā, pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭacumbitā ca te selā cāti pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭacumbitaselā, pavarasurāsura garuḍamanujabhujagagandhabbamakuṭakūṭacumbitaselehi saṅghaṭṭitā pavarasurāsura garuḍamanujabhujagagandhabbamakuṭakūṭacumbitaselasaṅghaṭṭitā, pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭacumbitaselasaṅghaṭṭitā caraṇā yassa tathāgatassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',174,replace(replace('<p class="bodytext">soyaṃ <a name="M0.0174"></a> pavarasurāsuragaruḍamanujabhujagagandhabbamakuṭakūṭacumbitaselasaṅghaṭṭitacaraṇo, tathāgato, ayaṃ pana dvandakammadhārayatappurisagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext">Atha vā–saṅghaṭṭitā caraṇā saṅghaṭṭitacaraṇā, sāpekkhatte satipi gamakattā samāso. Pavarasurāsura garuḍamanujabhujaga gandhabbamakuṭakūṭacumbitaselehi saṅghaṭṭitacaraṇā yassa tathāgatassa soyaṃ pavarasurāsuragaruḍa manujabhujaga gandhabbamakuṭakūṭacumbitaselasaṅghaṭṭita caraṇo. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Catuddiso</span>ti catasso disā yassa soyaṃ catuddiso, bhagavā.</p>\r\n<p class="bodytext"><span class="bld">Pañcacakkhū</span>ti pañca cakkhūni yassa tathāgatassa soyaṃ pañcacakkhu, tathāgato.</p>\r\n<p class="bodytext"><span class="bld">Dasabalo</span>ti dasa balāni yassa soyaṃ dasabalo, bhagavā.</p>\r\n<p class="bodytext"><span class="bld">Anantañāṇo</span>ti nassa anto anantaṃ, anantaṃ ñāṇaṃ yassa tathāgatassa soyaṃ anantañāṇo, tathāgato.</p>\r\n<p class="bodytext"><span class="bld">Amita ghana sarīro</span>ti na mitaṃ amitaṃ, ghanaṃ eva sarīraṃ ghanasarīraṃ, amitaṃ ghanasarīraṃ yassa tathāgatassa soyaṃ amitaghanasarīro, tathāgato.</p>\r\n<p class="bodytext"><span class="bld">Amita bala parakkama patto</span>ti na mitā amitā, balañca parakkamo ca balaparakkamā, amitā eva balaparakkamā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',175,replace(replace('<p class="bodytext">amitabalaparakkamā<a name="M0.0175"></a>, amitabalaparakkamā pattā yena soyaṃ amitabalaparakkamapatto, bhagavā. Ayaṃ pana kammadhārayadvandagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Matta bhamara gaṇa cumbita vikasitapupphavallināgarukkho pasobhita kandaro</span>ti mattā eva bhamarā mattabhamarā, mattabhamarānaṃ gaṇā mattabhamaragaṇā, mattabhamaragaṇehi cumbitāni mattabhamaraṇacumbitāni, vikasitāni eva pupphāni vikasitapupphāni, mattabhamaragaṇacumbitāni vikasitapupphāni yesaṃ tehi mattabhamaragaṇacumbitavikasitapupphā, valli ca nāgarukkho ca mallināgarukkhā, mattabhamaragaṇacumbitavikasitapupphā ca te vallināgarukkhā ceti mattabhamaragaṇacumbitavikasitapupphavallināgarukkhā, mattabhamaragaṇacumbitavikasitapupphavallināgarukkhehi upasobhitāni mattabhamaragaṇacumbitavikasitapupphavallināgarukkho pasobhitāni, mattabhamaragaṇacumbitavikasitapupphavallināgarukkho pasobhitāni kandarāni yassa pabbatarājassa soyaṃ mattabhamaragaṇacumbitavikasitapuppha vallināgarukkho paso bhitakandaro, pabbatarājā. Ayaṃ pana dvandakammadhārayatappurisagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext">Atha vā–upasobhitāni kandarāni upasobhitakandarāni, sāpekkhatte satipi gamakattā samāso. Mattabhamaragaṇacumbitavikasitapupphavallināgarukkhehi upasobhitakandarāni yassa pabbatarājassa soyaṃ mattabhamaragaṇacumbitavikasitapupphavallināgarukkho pasobhitakandaro, pabbatarājā. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',176,replace(replace('<p class="bodytext"><span class="bld">Nānā</span> <a name="M0.0176"></a> <span class="bld">rukkha tiṇa patita pupphopasobhita kandaro</span>ti rukkho ca tiṇañca rukkhatiṇāni, nānā pakārāni eva rukkhatiṇāni nānārukkhatiṇāni, nānārukkhatiṇehi patitāni nānārukkhatiṇapatitāni, nānārukkhatiṇapatitāni ca tāni pupphāni ceti nānārukkhatiṇapatitapupphāni, nānārukkhatiṇapatitapupphehi upasobhitāni nānārukkhatiṇapatita pupphopasobhitāni, nānārukkhatiṇapatitapupphopasobhitāni kandarāni yassa pabbatarājassa soyaṃ nānārukkhatiṇapatitapupphopasobhitakandaro, pabbatarājā. Ayaṃ pana dvandakammadhārayatappurisagabbho tulyādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext">Atha vā–upasobhitāni eva kandarāni upasobhitakandarāni, (sāpekkhatte satipi gamakattā samāso.) Nānārukkhatiṇapatitapupphehi upasobhitakandarāni yassa pabbatarājassa soyaṃ nānārukkhatiṇapatitapupphopasobhitakandaro, pabbatarājā. Ayaṃ pana bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Nānā musala phāla pabbata taru kaliṅgara sara dhanugadāsi tomarahatthā</span>ti musalo ca phālo ca pabbato ca taru ca kaliṅgaro ca saro ca dhanu ca gadā caasi ca tomaro, ca musalaphālapabbatatarukaliṅgarasaradhanugadāsitomarā, nānā pakārā eva musalaphālapabbatatarukaliṅgarasaradhanugadāsitomarā nānāmusalaphālapabbatatarukaliṅgarasaradhanugadāsitomarā, nānāmusalaphālapabbatatarukaliṅgarasaradhanugadāsitomarā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',177,replace(replace('<p class="bodytext">hatthesu <a name="M0.0177"></a> yesaṃ te nānāmusalaphālapabbatatarukaliṅgara saradhanugadāsitomarahatthā. Ayaṃ pana dvandakammadhārayagabbho bhinnādhikaraṇabahubbīhi.</p>\r\n<p class="bodytext"><span class="bld">Bahubbīhi</span> iccanena kvattho? Bahubbīhimhi ca.</p>\r\n<p class="bodytext"><a name="para329"></a><span class="paranum">329</span>, 357. <span class="bld">Nāmānaṃ samuccayo dvando</span>.</p>\r\n<p class="bodytext">Nāmānaṃ ekavibhattikānaṃ yo samuccayo, so dvandasañño hoti.</p>\r\n<p class="bodytext">Candimā ca sūriyo ca candimasūriyā, samaṇo ca brāhmaṇo ca samaṇabrāhmaṇā, sāriputto ca moggallāno ca sāriputtamoggallānā, brāhmaṇo ca gahapatiko ca brāhmaṇagahapatikā, yamo ca varuṇo ca yamavaruṇā, kuvero ca vāsavo ca kuveravāsavā.</p>\r\n<p class="bodytext"><span class="bld">Dvanda</span>iccanena kvattho? Dvandaṭṭhā vā.</p>\r\n<p class="bodytext"><a name="para330"></a><span class="paranum">330</span>, 340. <span class="bld">Mahataṃ mahā tulyādhikaraṇe pade</span>.</p>\r\n<p class="bodytext">Tesaṃ <span class="bld">mahanta</span>saddānaṃ <span class="bld">mahā</span>ādeso hoti tulyādhikaraṇe pade.</p>','\r',char(13)),'\n',char(10)),'-329-330-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',178,replace(replace('<p class="bodytext">Mahanto <a name="M0.0178"></a> ca so puriso cāti mahāpuriso, mahantī ca sā devī cāti mahādevī, mahantañca taṃ balañcāti mahābalaṃ, mahanto ca so nāgo cāti mahānāgo, mahanto ca so yaso cāti mahāyaso, mahantañca taṃ padumavanañcāti mahāpadumavanaṃ, mahantī ca sā nadī cāti mahānadī, mahanto ca so maṇi cāti mahāmaṇi, mahanto ca so gahapatiko cāti mahāgahapatiko, mahantañca taṃ dhanañcāti mahādhanaṃ, mahanto ca so puñño cāti mahāpuñño.</p>\r\n<p class="bodytext"><span class="bld">Bahuvacanaggahaṇena</span> kvaci <span class="bld">mahanta</span> saddassa <span class="bld">mahā</span>deso hoti. Mahantañca taṃ phalañcāti mahapphalaṃ, mahabbalaṃ, evaṃ mahaddhanaṃ, mahabbhayaṃ.</p>\r\n<p class="bodytext"><a name="para331"></a><span class="paranum">331</span>, 353. <span class="bld">Itthiyaṃ bhāsitapumitthī pumāva ce</span>.</p>\r\n<p class="bodytext">Itthiyaṃ tulyādhikaraṇe pade ce bhāsitapumitthī pumāva daṭṭhabbā.</p>\r\n<p class="bodytext">Dīghā jaṅghā yassa soyaṃ dīghajaṅgho, kalyāṇabhariyo, pahūtapañño.</p>','\r',char(13)),'\n',char(10)),'-331-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',179,replace(replace('<p class="bodytext"><span class="bld">Bhāsitapupe</span>ti <a name="M0.0179"></a> kimatthaṃ? Brāhmaṇabandhu ca sā bhariyā cātibrāhmaṇabandhubhariyā.</p>\r\n<p class="bodytext"><a name="para332"></a><span class="paranum">332</span>, 343. <span class="bld">Kammadhārayasaññe ca</span>.</p>\r\n<p class="bodytext">Kammadhārayasaññe ca samāse itthiyaṃ tulyādhikaraṇe pade pubbe bhāsitapumitthī ce, pumāva daṭṭhabbā. Brāhmaṇadārikā, khattiyakaññā, khattiyakumārikā.</p>\r\n<p class="bodytext"><span class="bld">Bhāsitapume</span>ti kimatthaṃ? Khattiyabandhudārikā, brāhmaṇabandhudārikā.</p>\r\n<p class="bodytext"><a name="para333"></a><span class="paranum">333</span>, 344. <span class="bld">Attaṃ nassa tappurise</span>.</p>\r\n<p class="bodytext">Nassa padassa tappurise uttarapade attaṃ hoti.</p>\r\n<p class="bodytext">Nabrāhmaṇo abrāhmaṇo, avasalo, abhikkhu, apañcavassaṃ, apañcagavaṃ.</p>\r\n<p class="bodytext"><a name="para334"></a><span class="paranum">334</span>, 345. <span class="bld">Sare ana</span>.</p>\r\n<p class="bodytext">Nassa padassa tappurise <span class="bld">ana</span> ādeso hoti sare pare.</p>\r\n<p class="bodytext">Na asso anasso, anissaro, anariyo.</p>\r\n<p class="bodytext"><a name="para335"></a><span class="paranum">335</span>, 346. </p>','\r',char(13)),'\n',char(10)),'-332-333-334-335-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',180,replace(replace('<p class="bodytext"><span class="bld">Kada</span> <a name="M0.0180"></a> <span class="bld">kussa</span>.</p>\r\n<p class="bodytext"><span class="bld">Ku</span>iccetassa <span class="bld">kada</span> hoti sare pare.</p>\r\n<p class="bodytext">Kucchitaṃ annaṃ kadannaṃ, kucchitaṃ asanaṃ kadassanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Sare</span>ti kimatthaṃ? Kucchitā dārā yesaṃ (apuññakārānaṃ) te hontīti kudārā, kujanā. Evaṃ kuputtā, kugehā, kuvatthā, kudāsā.</p>\r\n<p class="bodytext"><a name="para336"></a><span class="paranum">336</span>, 347. <span class="bld">Kā’ppatthesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ku</span>iccetassa <span class="bld">kā</span> hoti appatthesu ca.</p>\r\n<p class="bodytext">Kālavaṇaṃ, kāpupphaṃ.</p>\r\n<p class="bodytext"><span class="bld">Bahuvacana</span>ggahaṇaṃ kimatthaṃ? <span class="bld">Ku</span>iccetassa anappatatthesupi kvaci <span class="bld">kā</span> hoti, kāpurisā.</p>\r\n<p class="bodytext"><a name="para337"></a><span class="paranum">337</span>, 350. <span class="bld">Kvaci samāsantagatānamakāranto</span>.</p>\r\n<p class="bodytext">Samāsantagatānaṃ nāmānamanto saro kvaci <span class="bld">a</span>kāro hoti.</p>\r\n<p class="bodytext">Devānaṃ rājā devarājo, devarājā, devānaṃ sakhā devasakho, devasakhā, pañca ahāni pañcāhaṃ, sattāhaṃ, pañcagavaṃ, chattupāhanaṃ, upasaradaṃ, visālakkho, vimukho.</p>','\r',char(13)),'\n',char(10)),'-336-337-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',181,replace(replace('<p class="bodytext"><span class="bld">Kāra</span>ggahaṇaṃ <a name="M0.0181"></a> kimatthaṃ? <span class="bld">Ā</span>kāranta ikārantā ca honti, paccakkhā dhammā yassa soyanti paccakkhadhammā, surabhino gandho surabhigandhi, sundaro gandho sugandhi, pūtino gandho pūtigandhi, kucchito gandho kugandhi, duṭṭhu gandho yassa soyanti dugandhi, pūti eva gandho pūtigandhi.</p>\r\n<p class="bodytext"><span class="bld">Nadī</span>antā ca <span class="bld">kattu</span>antā ca <span class="bld">ka</span>paccayo hoti samāsante.</p>\r\n<p class="bodytext">Bahū nadiyo yasmiṃ soyaṃ bahunadiko, janapado. Bahavo kattāro yassa soyaṃ bahukattuko, puriso.</p>\r\n<p class="bodytext"><a name="para338"></a><span class="paranum">338</span>, 356. <span class="bld">Nadimhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Nadi</span>mhā ca <span class="bld">ka</span>paccayo hoti samāsante.</p>\r\n<p class="bodytext">Bahū nadiyo yasmiṃ soyanti bahunadiko. Bahū kantiyo yassa soyanti bahukantiko. Bahunāriko.</p>\r\n<p class="bodytext"><a name="para339"></a><span class="paranum">339</span>, 358. <span class="bld">Jāyāya tudaṃ jāni patimhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Jāyā</span>iccetāya <span class="bld">tudaṃ jāni</span>iccete ādesā honti <span class="bld">pati</span>mhi pare.</p>\r\n<p class="bodytext">Tudaṃpatī, jānipatī.</p>\r\n<p class="bodytext"><a name="para340"></a><span class="paranum">340</span>, 355. <span class="bld">Dhanumhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dhanu</span>mhā ca <span class="bld">ā</span>paccayo hoti samāsante.</p>','\r',char(13)),'\n',char(10)),'-338-339-340-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',182,replace(replace('<p class="bodytext">Gāṇḍīvo <a name="M0.0182"></a> dhanu yassa soyaṃ gāṇḍīvadhanvā.</p>\r\n<p class="bodytext"><a name="para341"></a><span class="paranum">341</span>, 336. <span class="bld">Aṃ vibhattīnamakārantā abyayībhāvā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārantā abyayībhāvasamāsā parāsaṃ vibhattīnaṃ kvaci <span class="bld">aṃ</span> hoti.</p>\r\n<p class="bodytext">Adhicittaṃ, yathāvuḍḍhaṃ, upakumbhaṃ, yāvajīvaṃ, tiropabbataṃ, tiropākāraṃ, tirokuṭṭaṃ, antopāsādaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Adhicittassa bhikkhuno.</p>\r\n<p class="bodytext"><a name="para342"></a><span class="paranum">342</span>, 337. <span class="bld">Saro rasso napuṃsake</span>.</p>\r\n<p class="bodytext">Napuṃsake vattamānassa abyayībhāvasamāsassa liṅgassa saro rasso hoti.</p>\r\n<p class="bodytext">Kumārīsu adhikicca pavattati kathā iti adhikumāri. Upavadhu, upagaṅgaṃ, upamaṇikaṃ.</p>\r\n<p class="bodytext"><a name="para343"></a><span class="paranum">343</span>, 338. <span class="bld">Aññasmā lopo ca</span>.</p>\r\n<p class="bodytext">Aññasmā abyayībhāvasamāsā anakārantā parāsaṃ vibhattīnaṃ lopo ca hoti.</p>\r\n<p class="bodytext">Adhitthi, adhikumāri, upavadhu.</p>\r\n<p class="centered">Iti nāmakappe samāsakappo sattamo kaṇḍo.</p>\r\n<p class="centered">Samāsakappo niṭṭhito.</p>\r\n<p class="chapter">5. Taddhitakappa</p>\r\n<p class="subhead">Aṭṭhamakaṇḍa</p>\r\n<p class="bodytext"><a name="para344"></a><span class="paranum">344</span>, 361. </p>','\r',char(13)),'\n',char(10)),'-341-342-343-344-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',183,replace(replace('<p class="bodytext"><span class="bld">Vāṇa’pacce</span><a name="M0.0183"></a>.</p>\r\n<p class="bodytext"><span class="bld">Ṇa</span>paccayo hoti vā ‘‘tassāpacca’’ miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Vasiṭṭhassa apaccaṃ vāsiṭṭho, vasiṭṭhassa apaccaṃ vā, vasiṭṭhassa apaccaṃ vāsiṭṭhī, vasiṭṭhassa apaccaṃ vāsiṭṭhaṃ. Evaṃ bhāradvājo, bhāradvājī, bhāradvājaṃ. Gotamo, gotamī, gotamaṃ. Vāsudevo, vāsudevī, vāsudevaṃ. Bāladevo, bāladevī, bāladevaṃ. Vesāmitto, vesāmittī vesāmittaṃ.</p>\r\n<p class="bodytext"><a name="para345"></a><span class="paranum">345</span>, 366. <span class="bld">Ṇāyana ṇāna vacchādito</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">vacchā</span>dito gottagaṇato <span class="bld">ṇāyanaṇāna</span>paccayā honti vā ‘‘tassāpacca’’miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Vacchassa apaccaṃvacchāyano, vacchāno, vacchassa apaccaṃ vā, vacchassa apaccaṃ vacchāyanī, vacchānī, vacchassa apaccaṃ vacchāyanaṃ, vacchānaṃ. Sakaṭassa apaccaṃ sākaṭāyano, </p>','\r',char(13)),'\n',char(10)),'-345-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',184,replace(replace('<p class="bodytext">sākaṭāno<a name="M0.0184"></a>, sakaṭassa apaccaṃ vā, sākaṭāyanī, sā kaṭānī, sākaṭāyanaṃ, sākaṭānaṃ. Evaṃ kaṇhāyano, kaṇhāno, kaṇhassa apaccaṃ vā. Kaṇhāyanī, kaṇhānī, kaṇhāyanaṃ, kaṇhānaṃ. Aggivessāyano, aggivessāno, aggivessāyanī, aggivessānī, aggivessāyanaṃ, aggivessānaṃ. Gacchāyano, gacchāno, gacchāyanī, gacchānī, gacchāyanaṃ, gacchānaṃ. Kappāyano, kappāno, kappāyanī, kappānī, kappāyanaṃ, kappānaṃ. Moggallāyano, moggallāno, moggallāyanī, moggallānī, moggallāyanaṃ, moggallānaṃ. Muñcāyano, muñcāno, muñcāyanī, muñcānī, muñcāyanaṃ, muñcānaṃ. Saṅghāyano, saṅghāno, saṅghāyanī, saṅghānī, saṅghāyanaṃ, saṅghānaṃ. Lomāyano, lomāno, lomāyanī, lomānī, lomāyanaṃ, lomānaṃ, sākamāyano, sākamāno, sākamāyanī, sākamānī, sākamāyanaṃ, sākamānaṃ. Nārāyano, nārāno, nārāyanī, nārānī, nārāyanaṃ, nārānaṃ. Corāyanocorāno, corāyanī, corānī, corāyanaṃ, corānaṃ, āvasālāyano, āvasālāno, āvasālāyanī, āvasālānī, āvasālāyanaṃ, āvasālānaṃ. Dvepāyano, dvepāno, dvepāyanī, dvepānī, dvepāyanaṃ, dvepānaṃ. Kuñcāyano, kuñcāno, kuñcāyanī, kuñcānī, kuñcāyanaṃ, kuñcānaṃ. Kaccāyano, kaccāno, kaccāyanī, kaccānī, kaccāyanaṃ, kaccānaṃ.</p>\r\n<p class="bodytext"><a name="para346"></a><span class="paranum">346</span>, 367. </p>','\r',char(13)),'\n',char(10)),'-346-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',185,replace(replace('<p class="bodytext"><span class="bld">Ṇeyyo</span> <a name="M0.0185"></a> <span class="bld">kattikādīhi</span>.</p>\r\n<p class="bodytext">Tehi gottagaṇehi <span class="bld">kattikā</span>dīhi <span class="bld">ṇeyya</span>paccayo hoti vā ‘‘tassāpacca’’miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Kattikāya apaccaṃ kattikeyyo, kattikāya apaccaṃ vā. Evaṃ venateyyo, rohiṇeyyo, gaṅgeyyo, kaddameyyo, nādeyyo, āleyyo, āheyo, kāmeyyo, suciyā apaccaṃ soceyyo, sāleyyo, bāleyyo, māleyyo, kāleyyo.</p>\r\n<p class="bodytext"><a name="para347"></a><span class="paranum">347</span>, 368. <span class="bld">Ato ṇi vā</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">a</span>kārato <span class="bld">ṇi</span>paccayo hoti vā ‘‘tassāpacca’’miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Dakkhassa apaccaṃ dakkhi, dakkhassa apaccaṃ vā. Duṇassa apaccaṃ doṇi, duṇassa apaccaṃ vā, evaṃ vāsavi, sakyaputti, nāṭaputti, dāsaputti, dāsavi, vāruṇi, gaṇḍi, bāladevi, pāvaki, jenadatti, buddhi, dhammi, saṅghi, kappi, anuruddhi.</p>\r\n<p class="bodytext"><span class="bld">Vāti vikappanatthena ṇika</span>paccayo hoti ‘‘tassāpacca’’miccetasmiṃ atthe. Sakyaputtassa apaccaṃ sakyaputtiko. Evaṃ nāṭaputtiko, jenadattiko.</p>\r\n<p class="bodytext"><a name="para148"></a><span class="paranum">148</span>, 371. </p>','\r',char(13)),'\n',char(10)),'-347-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',186,replace(replace('<p class="bodytext"><span class="bld">Ṇavo’</span> <a name="M0.0186"></a> <span class="bld">pakvādīhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Upaku</span>iccevamādīhi <span class="bld">ṇava</span>paccayo hoti vā ‘‘tassāpacca’’miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Upakussa apaccaṃ opakavo, upakussa apaccaṃ vā. Manuno apaccaṃ mānavo, manuno apaccaṃ vā. Bhaggussa apaccaṃ bhaggavo, bhaggussa apaccaṃ vā, paṇḍussa apaccaṃ paṇḍavo, paṇḍussa apaccaṃ vā, bahussa apaccaṃ bāhavo, bahussa apaccaṃ vā.</p>\r\n<p class="bodytext"><a name="para349"></a><span class="paranum">349</span>, 372. <span class="bld">Ṇera vidhavādito</span>.</p>\r\n<p class="bodytext">Tasmā <span class="bld">vidhavā</span>dito <span class="bld">ṇera</span>paccayo hoti vā ‘‘tassāpacca’’miccetasmiṃ atthe.</p>\r\n<p class="bodytext">Vidhavāya apaccaṃ vedhavero, vidhavāya apaccaṃ vā. Bandhukiyā apaccaṃ bandhukero, bandhukiyā apaccaṃ vā. Samaṇassa apaccaṃ sāmaṇero, samaṇassa apaccaṃ vā. Evaṃ sāmaṇerī, sāmaṇeraṃ, nāḷikero, nāḷikerī, nāḷikeraṃ.</p>\r\n<p class="bodytext"><a name="para350"></a><span class="paranum">350</span>, 373. <span class="bld">Yena vā saṃsaṭṭhaṃ tarati carati vahati ṇiko</span>.</p>\r\n<p class="bodytext">Yena vā saṃsaṭṭhaṃ, yena vā tarati, yena vā carati, yena vā vahati iccetesvatthesu <span class="bld">ṇika</span>paccayo hoti vā.</p>','\r',char(13)),'\n',char(10)),'-349-350-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',187,replace(replace('<p class="bodytext">Tilena <a name="M0.0187"></a> saṃsaṭṭhaṃ bhojanaṃ telikaṃ, tilena saṃsaṭṭhaṃ vā. Evaṃ goḷikaṃ, ghātikaṃ.</p>\r\n<p class="bodytext">Nāvāya taratīti nāviko, nāvāya tarati vā. Evaṃ oḷumpiko.</p>\r\n<p class="bodytext">Sakaṭena caratīti sākaṭiko, sakaṭena carati vā. Evaṃ pattiko, daṇḍiko, dhammiko, pādiko.</p>\r\n<p class="bodytext">Sīsena vahatīti sīsiko, sīsena vahati vā. Aṃsena vahatīti aṃsiko, aṃsena vahati vā. Evaṃ khandhiko, aṅguliko.</p>\r\n<p class="bodytext"><span class="bld">Vāti vikappanatthena</span> aññesupi <span class="bld">ṇika</span>paccayo hoti. Rājagahe vasatīti rājagahiko, rājagahe vasati vā. Rājagahe jāto rājagahiko, rājagahe jāto vā. Evaṃ māgadhiko, sāvatthiko, kāpila vatthiko, pāṭaliputtiko, vesāliko.</p>\r\n<p class="bodytext"><a name="para351"></a><span class="paranum">351</span>, 374. <span class="bld">Tamadhīte tenakatādi sannidhāna niyoga sippa bhaṇḍa jīvikatthesu ca</span>.</p>\r\n<p class="bodytext">Tamadhīte, tenakatādiatthe, tamhi sannidhānā, tattha niyutto, tamassa sippaṃ, tamassa bhaṇḍaṃ, tamassa jīvikaṃ iccetesvatthesu ca <span class="bld">ṇika</span>paccayo hoti vā.</p>','\r',char(13)),'\n',char(10)),'-351-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',188,replace(replace('<p class="bodytext">Vinayamadhīte <a name="M0.0188"></a> venayiko, vinayamadhīte vā. Evaṃ suttantiko, ābhidhammiko, veyyākaraṇiko.</p>\r\n<p class="bodytext">Kāyena kataṃ kammaṃ kāyikaṃ, kāyena kataṃ kammaṃ vā. Evaṃ vācasikaṃ, mānasikaṃ.</p>\r\n<p class="bodytext">Sarīre sannidhānā vedanā sārīrikā, sarīre sannidhānā vā, evaṃ mānasikā.</p>\r\n<p class="bodytext">Dvāre niyutto dovāriko, dvāre niyutto vā. Evaṃ bhaṇḍāgāriko, nāgariko, nāvakammiko.</p>\r\n<p class="bodytext">Vīṇā assa sippaṃ veṇiko, vīṇā assa sippaṃ vā. Evaṃ pāṇaviko, modiṅgiko, vaṃsiko.</p>\r\n<p class="bodytext">Gandho assa bhaṇḍaṃ gandhiko, gandho assa bhaṇḍaṃ vā. Evaṃ teliko, goḷiko.</p>\r\n<p class="bodytext">Urabbhaṃ hantvā jīvatīti orabbhiko, urabbhaṃ hantvā jīvati vā. Magaṃ hantvā jīvatīti māgaviko, magaṃ hantvā jīvati vā. Evaṃ sokariko, sākuṇiko.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇena</span> aññatthāpi <span class="bld">ṇika</span> paccayo yojetabbo. Jālena hato jāliko, jālena hato vā.</p>\r\n<p class="bodytext">Suttena bandho suttiko, suttena bandho vā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',189,replace(replace('<p class="bodytext">Cāpo <a name="M0.0189"></a> assa āvudho cāpiko, cāpo assa āvudho vā. Evaṃ tomariko, muggariko, mosaliko.</p>\r\n<p class="bodytext">Vāto assa ābādho vātiko, vāto assa ābādho vā. Evaṃ semhiko, pittiko.</p>\r\n<p class="bodytext">Buddhe pasanno buddhiko, buddhe pasanno vā. Evaṃ dhammiko, saṅghiko.</p>\r\n<p class="bodytext">Buddhassa santakaṃ buddhikaṃ, buddhassa santakaṃ vā. Evaṃ dhammikaṃ, saṅghikaṃ.</p>\r\n<p class="bodytext">Vatthena kītaṃ bhaṇḍaṃ vatthikaṃ, vatthena kītaṃ bhaṇḍaṃ vā. Evaṃ kumbhikaṃ, phālikaṃ, kiṃ kaṇikaṃ, sovaṇṇikaṃ.</p>\r\n<p class="bodytext">Kumbho assa parimāṇaṃ kumbhikaṃ, kumbho assa parimāṇaṃ vā.</p>\r\n<p class="bodytext">Kumbhassa rāsi kumbhikaṃ, kumbhassa rāsi vā.</p>\r\n<p class="bodytext">Kumbhaṃ arahatīti kumbhiko, kumbhaṃ arahati vā.</p>\r\n<p class="bodytext">Akkhena dibbatīti akkhiko, akkhena dibbati vā. Evaṃ sālākiko, tindukiko, ambaphaliko. Kapiṭṭhaphaliko, nāḷikeriko iccevamādi.</p>\r\n<p class="bodytext"><a name="para352"></a><span class="paranum">352</span>, 376. <span class="bld">Ṇa rāgā tasse damaññatthesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇa</span>paccayo hoti vā rāgamhā ‘‘tena rattaṃ’’ iccetasmiṃ atthe, ‘‘tassedaṃ’’ aññatthesu ca.</p>','\r',char(13)),'\n',char(10)),'-352-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',190,replace(replace('<p class="bodytext">Kasāvena <a name="M0.0190"></a> rattaṃ vatthaṃ kāsāvaṃ, kasāvena rattaṃ vatthaṃ vā. Evaṃ kosumbhaṃ, hāliddaṃ, pāṭaṅgaṃ, rattaṅga, mañjiṭṭhaṃ, kuṅkumaṃ.</p>\r\n<p class="bodytext">Sūkarassa idaṃ maṃsaṃ sokaraṃ, sūkarassa idaṃ maṃsaṃ vā. Evaṃ māhisaṃ.</p>\r\n<p class="bodytext">Udumbarassa avidūre pavattaṃ vimānaṃ odumbaraṃ, udumbarassa avidūre pavattaṃ vimānaṃ vā.</p>\r\n<p class="bodytext">Vidisāya avidūre nivāso vediso, vidisāya avidūre nivāso vā.</p>\r\n<p class="bodytext">Mathurāya jāto māthuro, mathurāya jāto vā.</p>\r\n<p class="bodytext">Mathurāya āgato māthuro, mathurāya āgato vā.</p>\r\n<p class="bodytext">Kattikāya niyutto māso kattiko, kattikāya niyutto māso vā. Evaṃ māgasiro, phusso, māgho, phagguno, citto, vesākho, jeṭṭho, āsaḷho, sāvaṇo, bhaddo, assayujo.</p>\r\n<p class="bodytext">Na vuddhi nīlapītādo, paccaye saṇakārake.</p>\r\n<p class="bodytext"><span class="bld">Pha</span>kāro <span class="bld">phussa</span>saddassa, ‘‘siro’’ti sirasaṃ vade.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',191,replace(replace('<p class="bodytext">Sikkhānaṃ <a name="M0.0191"></a> samūho sikkho, bhikkhānaṃ samūho bhikkho. Evaṃ kāpoto, māyūro, kokilo.</p>\r\n<p class="bodytext">Buddhe, assa devatā buddho. Evaṃ bhaddo, māro, māhindo, vessavaṇo, yāmo, somo, nārāyaṇo.</p>\r\n<p class="bodytext">Saṃvaccharamadhīte saṃvaccharo. Evaṃ mohutto, nemitto, aṅgavijjo, veyyākaraṇo, chando, bhāsso, cando.</p>\r\n<p class="bodytext">Vasādānaṃ visayo deso vāsādo. Evaṃ kumbho, sākunto, ātisāro.</p>\r\n<p class="bodytext">Udumbarā asmiṃ padese santīti odumbaro. Sāgarehi nibbatto sāgaro. Sāgalamassa nivāso sāgalo. Mathurā assa nivāso māthuro. Mathurāya issaro māthuro. Iccevamādayo yojetabbā.</p>\r\n<p class="bodytext"><a name="para353"></a><span class="paranum">353</span>, 378. <span class="bld">Jātādīnamimiyā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Jāta</span>iccevamādīnamatthe <span class="bld">imaiya</span>paccayā honti.</p>\r\n<p class="bodytext">Pacchā jāto pacchimo. Evaṃ antimo, majjhimo, purimo, uparimo, heṭṭhimo, gopphimo. Bodhisattajātiyā jāto bodhisattajātiyo, </p>','\r',char(13)),'\n',char(10)),'-353-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',192,replace(replace('<p class="bodytext">evaṃ <a name="M0.0192"></a> assajātiyo, hatthijātiyo, manussajātiyo.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇena</span> niyuttatthāditopi tadassatthāditopi <span class="bld">ima iya ika</span> iccete paccayā honti. Anto niyutto antimo. Evaṃ antiyo, antiko.</p>\r\n<p class="bodytext">Putto assa atthi, tasmiṃ vā vijjatīti puttimo. Evaṃ puttiyeva, puttiko, kappimo, kappiyo, kappiko.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena kiya</span>paccayo hoti niyuttatthe. Jātiyaṃ niyutto jātikiyo, andhe niyutto andhakiyo, jātiyā andho jaccandho, jaccandhe niyutto jaccandhakiyo.</p>\r\n<p class="bodytext"><a name="para354"></a><span class="paranum">354</span>, 379. <span class="bld">Samūhatthe kaṇa ṇā</span>.</p>\r\n<p class="bodytext">Samūhatthe <span class="bld">kaṇa ṇa</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Rājaputtānaṃ samūho rājaputtako. Evaṃ rājaputto, mānussako, mānusso, māyūrako, māyūro, māhiṃsako, māhiṃso.</p>\r\n<p class="bodytext"><a name="para355"></a><span class="paranum">355</span>, 380. <span class="bld">Gāma jana bandhu sahāyādīhitā</span>.</p>\r\n<p class="bodytext"><span class="bld">Gāma jana bandhu sahāya</span>iccevamādīhi <span class="bld">tā</span>paccayo hoti samūhatthe.</p>','\r',char(13)),'\n',char(10)),'-354-355-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',193,replace(replace('<p class="bodytext">Gāmānaṃ <a name="M0.0193"></a> samūho gāmatā. Evaṃ janatā, bandhutā, sahāyatā, nagaratā.</p>\r\n<p class="bodytext"><a name="para356"></a><span class="paranum">356</span>, 381. <span class="bld">Tadassa ṭhānamiyo ca</span>.</p>\r\n<p class="bodytext">‘‘Tadassa ṭhāna’’ miccetasmiṃ atthe <span class="bld">iya</span>paccayo hoti.</p>\r\n<p class="bodytext">Madanassa ṭhānaṃ madaniyaṃ, bandhanassa ṭhānaṃ bandhaniyaṃ, mucchanassa ṭhānaṃ mucchaniyaṃ, evaṃ rajaniyaṃ, kamaniyaṃ, gamaniyaṃ, dussaniyaṃ, dassaniyaṃ.</p>\r\n<p class="bodytext"><a name="para357"></a><span class="paranum">357</span>, 382. <span class="bld">Upamatthāyitattaṃ</span>.</p>\r\n<p class="bodytext">Upamatthe <span class="bld">āyitatta</span>paccayo hoti.</p>\r\n<p class="bodytext">Dhūmo viya dissati aduṃ vanaṃ tadidaṃ dhūmāyitattaṃ, timiraṃ viya dissati aduṃ vanaṃ tadidaṃ timirāyitattaṃ.</p>\r\n<p class="bodytext"><a name="para358"></a><span class="paranum">358</span>, 383. <span class="bld">Tannissitatthe lo</span>.</p>\r\n<p class="bodytext">‘‘Tannissitatthe, tadassa ṭhāna’’miccetasmiṃ atthe ca <span class="bld">la</span>paccayo hoti.</p>','\r',char(13)),'\n',char(10)),'-356-357-358-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',194,replace(replace('<p class="bodytext">Duṭṭhu <a name="M0.0194"></a> nissitaṃ duṭṭhullaṃ, vedaṃ nissitaṃ vedallaṃ, duṭṭhu ṭhānaṃ duṭṭhullaṃ, vedassa ṭhānaṃ vedallaṃ.</p>\r\n<p class="bodytext"><a name="para359"></a><span class="paranum">359</span>, 384. <span class="bld">Ālu tabbahule</span>.</p>\r\n<p class="bodytext"><span class="bld">Ālu</span>paccayo hoti tabbahulatthe.</p>\r\n<p class="bodytext">Abhijjhā assa pakati abhijjhālu, abhijjhā assa bahulā vā abhijjhālu. Evaṃ sītālu, dhajālu, dayālu.</p>\r\n<p class="bodytext"><a name="para360"></a><span class="paranum">360</span>, 387. <span class="bld">Ṇya tta tā bhāve tu</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇyattatā</span>iccete paccayā honti bhāvatthe.</p>\r\n<p class="bodytext">Alasassa bhāvo ālasyaṃ, arogassa bhāvo ārogyaṃ. Paṃsukūlikassa bhāvo paṃsukūlikattaṃ, anodarikassa bhāvo anodarikattaṃ. Saṅgaṇikārāmassa bhāvo saṅgaṇikārāmatā, niddārāmassa bhāvo niddārāmatā.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇena ttana</span>paccayo hoti. Puthujjanattanaṃ, vedanattanaṃ.</p>\r\n<p class="bodytext"><a name="para361"></a><span class="paranum">361</span>, 388. </p>','\r',char(13)),'\n',char(10)),'-359-360-361-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',195,replace(replace('<p class="bodytext"><span class="bld">Ṇa</span> <a name="M0.0195"></a> <span class="bld">visamādīhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇa</span>paccayo hoti <span class="bld">visamādī</span>hi ‘‘tassa bhāvo’’iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Visamassa bhāvo vesamaṃ, sucissa bhāvo socaṃ.</p>\r\n<p class="bodytext"><a name="para362"></a><span class="paranum">362</span>, 389. <span class="bld">Ramaṇīyādito kaṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Ramaṇīya</span>iccevamādito <span class="bld">kaṇa</span> paccayo hoti ‘‘tassa bhāvo’’iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Ramaṇīyassa bhāvo rāmaṇīyakaṃ, manuññassa bhāvo mānuññakaṃ.</p>\r\n<p class="bodytext"><a name="para363"></a><span class="paranum">363</span>, 390. <span class="bld">Visese taratamisikiyiṭṭhā</span>.</p>\r\n<p class="bodytext">Visesatthe <span class="bld">tara tama isika iya iṭṭha</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Sabbe ime pāpā, ayamimesaṃ visesena pāpoti pāpataro. Evaṃ pāpatamo, pāpisiko, pāpiyo, pāpiṭṭho.</p>\r\n<p class="bodytext"><a name="para364"></a><span class="paranum">364</span>, 398. <span class="bld">Tadassatthīti vī ca</span>.</p>\r\n<p class="bodytext">‘‘Tadassatthi’’iccetasmiṃ atthe <span class="bld">vī</span>paccayo hoti.</p>','\r',char(13)),'\n',char(10)),'-362-363-364-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',196,replace(replace('<p class="bodytext">Medhā <a name="M0.0196"></a> yassa atthi, tasmiṃ vā vijjatītimedhāvī. Evaṃ māyāvī.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena so</span>paccayo hoti. Sumedhā yassa atthi, tasmiṃ vā vijjatīti sumedhaso.</p>\r\n<p class="bodytext"><a name="para365"></a><span class="paranum">365</span>, 399. <span class="bld">Tapādito sī</span>.</p>\r\n<p class="bodytext"><span class="bld">Tapā</span>dito <span class="bld">sī</span>paccayo hoti ‘‘tadassatthi’’iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Tapo yassa atthi tasmiṃ vā vijjatīti tapassī. Evaṃ yasassī, tejassī.</p>\r\n<p class="bodytext"><a name="para266"></a><span class="paranum">266</span>, 400. <span class="bld">Daṇḍādito ikaī</span>.</p>\r\n<p class="bodytext"><span class="bld">Daṇḍā</span>dito <span class="bld">ika ī</span>iccete paccayā honti ‘‘tadassatthi’’iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Daṇḍo yassa atthi, tasmiṃ vā vijjatīti daṇḍiko, daṇḍī. Evaṃ māliko, mālī.</p>\r\n<p class="bodytext"><a name="para367"></a><span class="paranum">367</span>, 401. <span class="bld">Madhvādito ro</span>.</p>\r\n<p class="bodytext"><span class="bld">Madhu</span>iccevamādito <span class="bld">ra</span>paccayo hoti ‘‘tadassatthi’’iccetasmiṃ atthe.</p>','\r',char(13)),'\n',char(10)),'-365-266-367-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',197,replace(replace('<p class="bodytext">Madhu <a name="M0.0197"></a> yassa atthi, tasmiṃ vā vijjatīti madhuro. Evaṃ kuñjaro, muggaro, mukharo, susiro, (sīsaro, sukaro, suṅkaro), subharo, suciro, ruciro.</p>\r\n<p class="bodytext"><a name="para368"></a><span class="paranum">368</span>, 402. <span class="bld">Guṇādito vantu</span>.</p>\r\n<p class="bodytext"><span class="bld">Guṇa</span>iccevamādito <span class="bld">vantu</span>paccayo hoti ‘‘tadassatthi’’iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Guṇo yassa atthi, tasmiṃ vā vijjatīti guṇavā. Evaṃ yasavā, dhanavā, paññavā, balavā, bhagavā.</p>\r\n<p class="bodytext"><a name="para369"></a><span class="paranum">369</span>, 403. <span class="bld">Satyādīhi mantu</span>.</p>\r\n<p class="bodytext"><span class="bld">Sati</span>iccevamādīhi <span class="bld">mantu</span>paccayohoti ‘‘tadassatthi’’ iccetasmiṃ atthe.</p>\r\n<p class="bodytext">Sati yassa atthi, tasmiṃ vā vijjatīti satimā, evaṃ jutimā, rucimā, thutimā, dhitimā, matimā, bhāṇumā.</p>\r\n<p class="bodytext"><a name="para370"></a><span class="paranum">370</span>, 405. <span class="bld">Saddhāditoṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Saddhā</span>iccevamādito <span class="bld">ṇa</span> paccayo hoti ‘‘tadassatthi’’iccetasmiṃ atthe.</p>','\r',char(13)),'\n',char(10)),'-368-369-370-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',198,replace(replace('<p class="bodytext">Saddhā <a name="M0.0198"></a> yassa atthi, tasmiṃ vā vijjatībhi saddho, evaṃ pañño, amaccharo.</p>\r\n<p class="bodytext"><a name="para371"></a><span class="paranum">371</span>, 404. <span class="bld">Āyussukārāsa mantumhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Āyu</span>ssa anto <span class="bld">u</span>kāro <span class="bld">asā</span>deso hoti<span class="bld">mantumhi</span> paccaye pare.</p>\r\n<p class="bodytext">Āyu assa atthi, tasmiṃ vā vijjatīti āyasmā.</p>\r\n<p class="bodytext"><a name="para372"></a><span class="paranum">372</span>, 385. <span class="bld">Tappakativacane mayo</span>.</p>\r\n<p class="bodytext">Tappakativacanatthe <span class="bld">maya</span>paccayo hoti.</p>\r\n<p class="bodytext">Suvaṇṇena pakataṃ kammaṃ sovaṇṇamayaṃ. Evaṃ rūpiyamayaṃ jatumayaṃ, rajatamayaṃ, iṭṭhakamayaṃ, ayomayaṃ, mattikā mayaṃ, dārumayaṃ, gomayaṃ.</p>\r\n<p class="bodytext"><a name="para373"></a><span class="paranum">373</span>, 406. <span class="bld">Saṅkhyāpūraṇe mo</span>.</p>\r\n<p class="bodytext">Saṅkhyāpūraṇatthe <span class="bld">ma</span>paccayo hoti.</p>\r\n<p class="bodytext">Pañcannaṃ pūraṇo pañcamo, evaṃ sattamo, aṭṭhamo, navamo, dasamo.</p>\r\n<p class="bodytext"><a name="para374"></a><span class="paranum">374</span>, 408. </p>','\r',char(13)),'\n',char(10)),'-371-372-373-374-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',199,replace(replace('<p class="bodytext"><span class="bld">Sa</span> <a name="M0.0199"></a> <span class="bld">chassavā</span>.</p>\r\n<p class="bodytext"><span class="bld">Cha</span>ssa sakārādeso hoti vā saṅkhyāpūraṇatthe.</p>\r\n<p class="bodytext">Channaṃ pūraṇo saṭṭho, chaṭṭho vā.</p>\r\n<p class="bodytext"><a name="para375"></a><span class="paranum">375</span>, 412. <span class="bld">Ekādito dasassī</span>.</p>\r\n<p class="bodytext"><span class="bld">Ekā</span>dito <span class="bld">dasa</span>ssa ante <span class="bld">ī</span>paccayo hoti vā saṅkhyāpūraṇatthe.</p>\r\n<p class="bodytext">Eko ca dasa ca ekādasa, ekādasannaṃ pūraṇī ekādasī. Pañcaca dasa ca pañcadasa, pañcadasannaṃ pūraṇī pañcadasī. Cattāro ca dasa ca catuddasa, catuddasannaṃ pūraṇī cātuddasī.</p>\r\n<p class="bodytext"><span class="bld">Pūraṇe</span>ti kimatthaṃ? Ekādasa, pañcadasa.</p>\r\n<p class="bodytext"><a name="para376"></a><span class="paranum">376</span>, 257. <span class="bld">Dase so niccañca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dasa</span>sadde pare niccaṃ <span class="bld">cha</span>ssa <span class="bld">so</span> hoti.</p>\r\n<p class="bodytext">Soḷasa.</p>\r\n<p class="bodytext"><a name="para377"></a><span class="paranum">377</span>, 0. <span class="bld">Ante niggahitañca</span>.</p>\r\n<p class="bodytext">Tāsaṃ saṅkhyānaṃ ante niggahitāgamo hoti.</p>\r\n<p class="bodytext">Pañcadasi, cātuddasi.</p>\r\n<p class="bodytext"><a name="para378"></a><span class="paranum">378</span>, 414. </p>','\r',char(13)),'\n',char(10)),'-375-376-377-378-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',200,replace(replace('<p class="bodytext"><span class="bld">Ti</span> <a name="M0.0200"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Tāsaṃ saṅkhyānaṃ ante <span class="bld">ti</span>kārāgamo hoti. Vīsati, tiṃsati.</p>\r\n<p class="bodytext"><a name="para379"></a><span class="paranum">379</span>, 258. <span class="bld">La darānaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Da</span>kāra <span class="bld">ra</span>kārānaṃ saṅkhyānaṃ <span class="bld">la</span>kārādeso hoti. Soḷasa, cattālīsaṃ.</p>\r\n<p class="bodytext"><a name="para380"></a><span class="paranum">380</span>, 255. <span class="bld">Vīsati dasesu bā dvissa tu</span>.</p>\r\n<p class="bodytext"><span class="bld">Vīsati dasa</span>iccetesu <span class="bld">dvissa bā</span> hoti.</p>\r\n<p class="bodytext">Bāvīsatindriyāni, bārasa manussā.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇena dvissa du di do</span> ādesā ca honti. Durattaṃ, dirattaṃ, diguṇaṃ, dohaḷinī.</p>\r\n<p class="bodytext"><a name="para381"></a><span class="paranum">381</span>, 254. <span class="bld">Ekādito dassa ra saṅkhyāne</span>.</p>\r\n<p class="bodytext"><span class="bld">Ekā</span>dito <span class="bld">dasa</span>ssa <span class="bld">da</span>kārassa <span class="bld">ra</span>kāro hoti vā saṅkhyāne.</p>\r\n<p class="bodytext">Ekārasa, ekādasa, bārasa, dvādasa.</p>\r\n<p class="bodytext"><span class="bld">Saṅkhyāne</span>ti kimatthaṃ? Dvādasāyatanāni.</p>\r\n<p class="bodytext"><a name="para382"></a><span class="paranum">382</span>, 259. </p>','\r',char(13)),'\n',char(10)),'-379-380-381-382-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',201,replace(replace('<p class="bodytext"><span class="bld">Aṭṭhādito</span> <a name="M0.0201"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Aṭṭha</span>iccevamādito ca <span class="bld">dasa</span>saddassa <span class="bld">da</span>kārassa <span class="bld">ra</span>kārādeso hoti vā saṅkhyāne.</p>\r\n<p class="bodytext">Aṭṭhārasa, aṭṭhadasa.</p>\r\n<p class="bodytext"><span class="bld">Aṭṭhādito</span>ti kimatthaṃ? Pañcadasa, soḷasa.</p>\r\n<p class="bodytext"><span class="bld">Saṅkhyāne</span>ti kimatthaṃ? Aṭṭhadasiko.</p>\r\n<p class="bodytext"><a name="para383"></a><span class="paranum">383</span>, 253. <span class="bld">Dvekaṭṭhānamākāro vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Dvi eka aṭṭha</span>iccetesamanto <span class="bld">ā</span>kāro hoti vā saṅkhyāne.</p>\r\n<p class="bodytext">Dvādasa, ekādasa, aṭṭhārasa.</p>\r\n<p class="bodytext"><span class="bld">Saṅkhyāne</span>ti kimatthaṃ? Dvidanto, ekadanto, ekacchanno, aṭṭhatthambho.</p>\r\n<p class="bodytext"><a name="para384"></a><span class="paranum">384</span>, 407. <span class="bld">Catucchehī tha ṭhā</span>.</p>\r\n<p class="bodytext"><span class="bld">Catu cha</span>iccetehi <span class="bld">tha ṭha</span>iccete paccayā honti saṅkhyāpūraṇatthe.</p>\r\n<p class="bodytext">Catuttho, chaṭṭho.</p>\r\n<p class="bodytext"><a name="para385"></a><span class="paranum">385</span>, 409. </p>','\r',char(13)),'\n',char(10)),'-383-384-385-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',202,replace(replace('<p class="bodytext"><span class="bld">Dvitīhi</span> <a name="M0.0202"></a> <span class="bld">tiyo</span>.</p>\r\n<p class="bodytext"><span class="bld">Dvi ti</span>iccetehi <span class="bld">tiya</span>paccayo hoti saṅkhyāpūraṇatthe.</p>\r\n<p class="bodytext">Dutiyo, tatiyo.</p>\r\n<p class="bodytext"><a name="para386"></a><span class="paranum">386</span>, 410. <span class="bld">Tiye dutāpi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dvi ti</span>iccetesaṃ <span class="bld">du ta</span>iccete ādesā honti <span class="bld">tiya</span>paccaye pare.</p>\r\n<p class="bodytext">Dutiyo, tatiyo.</p>\r\n<p class="bodytext"><span class="bld">Apiggahaṇena</span> aññesupi <span class="bld">dvi</span>iccetassa <span class="bld">du</span>ādeso hoti. Durattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena dvi</span>iccetassa <span class="bld">di</span>kāro hoti. Dirattaṃ, diguṇaṃ saṅghāṭiṃ pārupetvā.</p>\r\n<p class="bodytext"><a name="para387"></a><span class="paranum">387</span>, 411. <span class="bld">Tesamaḍḍhūpapadena aḍḍhuḍḍha divaḍḍha diyaḍḍhaḍḍhatiyā</span>.</p>','\r',char(13)),'\n',char(10)),'-386-387-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',203,replace(replace('<p class="bodytext">Tesaṃ <a name="M0.0203"></a> <span class="bld">catuttha dutiya tatiyānaṃ</span> aḍḍhūpapadānaṃ <span class="bld">aḍḍhaḍḍhudivaḍḍha diyaḍḍha aḍḍhatiyā</span>desā honti, <span class="bld">aḍḍhū</span>papadena sahanippajjante.</p>\r\n<p class="bodytext">Aḍḍhena catuttho aḍḍhuḍḍho, aḍḍhena dutiyo divaḍḍho, aḍḍhena dutiyo diyaḍḍho, aḍḍhena tatiyo aḍḍhatiyo.</p>\r\n<p class="bodytext"><a name="para388"></a><span class="paranum">388</span>, 68. <span class="bld">Sarūpānamekasesvasakiṃ</span>.</p>\r\n<p class="bodytext">Sarūpānaṃ padabyañjanānaṃ ekaseso hoti asakiṃ. Puriso ca puriso ca purisā.</p>\r\n<p class="bodytext"><span class="bld">Sarūpāna</span>miti kimatthaṃ? Hatthī ca asso ca ratho ca pattiko ca hatthiassarathapattikā,</p>\r\n<p class="bodytext"><span class="bld">Asaki</span>nti kimatthaṃ? Puriso.</p>\r\n<p class="bodytext"><a name="para389"></a><span class="paranum">389</span>, 413. <span class="bld">Gaṇane dasassa dviticatupañcachasattaaṭṭhanavakānaṃ vī ti cattāra paññā cha sattāsanavā yosu, yonañcīsamāsaṃ ṭhi ri tī tuti</span>.</p>\r\n<p class="bodytext">Gaṇane <span class="bld">dasa</span>ssa dvika tika catukka pañcaka chakka sattakaaṭṭhaka navakānaṃ sarūpānaṃ katekasesānaṃ yathāsaṅkhyaṃ <span class="bld">vī ti cattāra paññā cha satta asa nava</span>iccādesā honti asakiṃ <span class="bld">yo</span>su, <span class="bld">yo</span>nañca <span class="bld">īsaṃ āsaṃ ṭhi ri ti īti uti</span>iccādesā honti. Pacchā puna nippajjante.</p>','\r',char(13)),'\n',char(10)),'-388-389-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',204,replace(replace('<p class="bodytext">Vīsaṃ<a name="M0.0204"></a>, tiṃsaṃ, cattālīsaṃ, paññāsaṃ, saṭṭhi, sattari, sattati, asīti, navuti.</p>\r\n<p class="bodytext"><span class="bld">Asaki</span>nti kimatthaṃ? Dasa.</p>\r\n<p class="bodytext"><span class="bld">Gaṇane</span>ti kimatthaṃ? Dasadasakā purisā.</p>\r\n<p class="bodytext"><a name="para390"></a><span class="paranum">390</span>, 256. <span class="bld">Catūpapadassa lopo tuttarapadādicassa cucopi navā</span>.</p>\r\n<p class="bodytext"><span class="bld">Catū</span>papadassa gaṇane pariyāpannassa <span class="bld">tu</span>kārassa lopo hoti, uttarapadādi<span class="bld">ca</span>kārassa <span class="bld">cuco</span>pi ādesā honti navā.</p>\r\n<p class="bodytext">Cuddasa, coddasa, catuddasa.</p>\r\n<p class="bodytext"><span class="bld">Apiggahaṇena</span> anupapadassāpi padādi<span class="bld">ca</span>kārassa. Lopohoti navā, <span class="bld">ca</span>ssa <span class="bld">cuco</span>pi honti. Tālīsaṃ, cattālīsaṃ, cuttālīsaṃ, cottālīsaṃ.</p>\r\n<p class="bodytext"><a name="para391"></a><span class="paranum">391</span>, 423. <span class="bld">Yadanupapannā nipātanā sijjhanti</span>.</p>\r\n<p class="bodytext">Ye saddā aniddiṭṭhalakkhaṇā, akkharapadabyañjanato, </p>','\r',char(13)),'\n',char(10)),'-390-391-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',205,replace(replace('<p class="bodytext">itthipumanapuṃsakaliṅgato<a name="M0.0205"></a>, nāmupasagganipātato, abyayībhāvasamāsataddhitākhyātato, gaṇanasaṅkhyākālakārakappayogasaññāto, sandhipakativuddhilopāgamavikāraviparitato, vibhattivibhajanato ca, te nipātanā sijjhanti.</p>\r\n<p class="bodytext"><a name="para392"></a><span class="paranum">392</span>, 418. <span class="bld">Dvādito ko’nekattheca</span>.</p>\r\n<p class="bodytext">Dviiccevamādito <span class="bld">ka</span>paccayo hoti anekatthe ca, nipātanā sijjhanti.</p>\r\n<p class="bodytext">Satassa dvikaṃ dvisataṃ, satassa tikaṃ tisataṃ, satassa catukkaṃ catusataṃ, satassa pañcakaṃ pañcasataṃ, satassa chakkaṃ chasataṃ, satassa sattakaṃ sattasataṃ, satassa aṭṭhakaṃ aṭṭhasataṃ, satassa navakaṃ navasataṃ, satassa dasakaṃ dasasataṃ, sahassaṃ hoti.</p>\r\n<p class="bodytext"><a name="para393"></a><span class="paranum">393</span>, 415. <span class="bld">Dasadasakaṃ sataṃ dasakānaṃ sataṃ sahassañca yomhi</span>.</p>\r\n<p class="bodytext">Gaṇane pariyāpannassa <span class="bld">dasa</span>dasakassa <span class="bld">sataṃ</span> hoti, <span class="bld">sata</span>dasakassa <span class="bld">sahassaṃ</span> hoti <span class="bld">yo</span>mhi pare.</p>\r\n<p class="bodytext">Sataṃ, sahassaṃ.</p>\r\n<p class="bodytext">Dvikādīnaṃ taduttarapadānañca nippajjante yathāsaṅkhyaṃ. Satassa dvikaṃ (tadidaṃ hoti) dvisataṃ, evaṃ tisataṃ, </p>','\r',char(13)),'\n',char(10)),'-392-393-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',206,replace(replace('<p class="bodytext">catusataṃ<a name="M0.0206"></a>, pañcasataṃ, chasataṃ, sattasataṃ, aṭṭhasataṃ, navasataṃ, dasasataṃ, sahassaṃ hoti.</p>\r\n<p class="bodytext"><a name="para394"></a><span class="paranum">394</span>, 416. <span class="bld">Yāva taduttari dasaguṇitañca</span>.</p>\r\n<p class="bodytext">Yāva tāsaṃ saṅkhyānaṃ uttari dasaguṇitañca kātabbaṃ.</p>\r\n<p class="bodytext">Taṃ yathā? Dasassa gaṇanassa dasaguṇitaṃ katvā sataṃ hoti, satassa dasaguṇitaṃ katvā sahassaṃ hoti, sahassassa dasaguṇitaṃ katvā dasasahassaṃ hoti, dasasahassassa dasaguṇitaṃ katvāsatasahassaṃ hoti, satasahassassa dasaguṇitaṃ katvādasasatasahassaṃ hoti, dasasatasahassassadasaguṇitaṃ katvā koṭi hoti, koṭisatasahassassa sataguṇitaṃ katvā pakoṭi hoti. Evaṃ sesāpi yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> visesanatthaṃ.</p>\r\n<p class="bodytext"><a name="para395"></a><span class="paranum">395</span>, 417. <span class="bld">Sakanāmehi</span>.</p>\r\n<p class="bodytext">Yāsaṃ pana saṅkhyānaṃ aniddiṭṭhanāmadheyyānaṃ sakehi sakehi nāmehi nippajjante.</p>\r\n<p class="bodytext">Satasahassānaṃ sataṃ koṭi, koṭisatasahassānaṃ sataṃ pakoṭi, pakoṭisatasahassānaṃ sataṃ </p>','\r',char(13)),'\n',char(10)),'-394-395-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',207,replace(replace('<p class="bodytext">koṭipakoṭi<a name="M0.0207"></a>, koṭipakoṭisatasahassānaṃ sataṃ nahutaṃ, nahutasatasahassānaṃ sataṃ ninnahutaṃ, ninnahutasatasahassānaṃ sataṃ akkhobhiṇī. Tathā bindu, abbudaṃ, nirabbudaṃ, ahahaṃ, ababaṃ, aṭaṭaṃ, sogandhikaṃ, uppalaṃ, kumudaṃ, padumaṃ, puṇḍarikaṃ, kathānaṃ, mahākathānaṃ, asaṅkhyeyyaṃ.</p>\r\n<p class="bodytext"><a name="para396"></a><span class="paranum">396</span>, 363. <span class="bld">Tesaṃ ṇo lopaṃ</span>.</p>\r\n<p class="bodytext">Tesaṃ paccayānaṃ <span class="bld">ṇo</span> lopamāpajjate.</p>\r\n<p class="bodytext">Gotamassa apaccaṃ gotamo. Evaṃ vāsiṭṭho. Venateyyo, ālasyaṃ, ārogyaṃ.</p>\r\n<p class="bodytext"><a name="para397"></a><span class="paranum">397</span>, 420. <span class="bld">Vibhāge dhā ca</span>.</p>\r\n<p class="bodytext">Vibhāgatthe ca <span class="bld">dhā</span>paccayo hoti.</p>\r\n<p class="bodytext">Ekena vibhāgena ekadhā. Evaṃ dvidhā, tidhā, catudhā, pañcadhā, chadhā.</p>\r\n<p class="bodytext"><span class="bld">Ce</span>ti kimatthaṃ? <span class="bld">So</span>paccayo hoti. Suttaso, byañjanaso, padaso.</p>\r\n<p class="bodytext"><a name="para398"></a><span class="paranum">398</span>, 421. </p>','\r',char(13)),'\n',char(10)),'-396-397-398-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',208,replace(replace('<p class="bodytext"><span class="bld">Sabbanāmehi</span> <a name="M0.0208"></a> <span class="bld">pakāravacane tu thā</span>.</p>\r\n<p class="bodytext">Sabbanāmehi pakāravacanatthe <span class="bld">thā</span>paccayo hoti.</p>\r\n<p class="bodytext">So pakāro tathā, taṃ pakāraṃ tathā, tena pakārena tathā, tassa pakārassa tathā, tasmā pakārā tathā, tassa pakārassa tathā, tasmiṃ pakāre tathā. Evaṃ yathā, sabbathā, aññathā, itarathā.</p>\r\n<p class="bodytext"><span class="bld">Tuggahaṇaṃ</span> kimatthaṃ? <span class="bld">Tatthā</span>paccayo hoti. So pakāro tathatthā. Evaṃ yathatthā. Sabbathatthā, aññathatthā, itarathatthā.</p>\r\n<p class="bodytext"><a name="para399"></a><span class="paranum">399</span>, 422. <span class="bld">Kimimehi thaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Kiṃ ima</span>iccetehi thaṃpaccayo hoti pakāravacanatthe.</p>\r\n<p class="bodytext">Ko pakāro kathaṃ, kaṃ pakāraṃ kathaṃ, kena pakārena kathaṃ, kassa pakārassa kathaṃ, kasmā pakārā kathaṃ. Kassa pakārassa kathaṃ, kasmiṃ pakāre kathaṃ, ayaṃ pakāro itthaṃ, imaṃ pakāraṃ itthaṃ, iminā pakārena itthaṃ, imassa pakārassa itthaṃ, imasmā pakārā itthaṃ, imassa pakārassa itthaṃ, <span class="bld">i</span>masmiṃ pakāre itthaṃ.</p>\r\n<p class="bodytext"><a name="para400"></a><span class="paranum">400</span>, 364. </p>','\r',char(13)),'\n',char(10)),'-399-400-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',209,replace(replace('<p class="bodytext"><span class="bld">Vuddhādisarassa</span> <a name="M0.0209"></a> <span class="bld">vā’saṃyogantassa saṇe ca</span>.</p>\r\n<p class="bodytext">Ādisarassa vā asaṃyogantassa ādibyañjanassa vā sarassa vuddhi hoti saṇakārake paccaye pare.</p>\r\n<p class="bodytext">Ābhidhammiko, venateyyo, vāsiṭṭho, ālasyaṃ, ārogyaṃ.</p>\r\n<p class="bodytext"><span class="bld">Asaṃyogantasse</span>ti kimatthaṃ? Bhaggavo, manteyyo, kunteyyo.</p>\r\n<p class="bodytext"><a name="para401"></a><span class="paranum">401</span>, 375. <span class="bld">Māyūnamāgamo ṭhāne</span>.</p>\r\n<p class="bodytext"><span class="bld">Iu</span>iccetesaṃ ādibhūtānaṃ mā vuddhi hoti. Tesu ca <span class="bld">e o</span> vuddhāgamo hoti ṭhāne.</p>\r\n<p class="bodytext">Byākaraṇamadhīte veyyākaraṇiko, nyāyamadhīte neyyāyiko, byāvacchassa apaccaṃ veyyāvaccho, dvāre niyutto dovāriko.</p>\r\n<p class="bodytext"><a name="para402"></a><span class="paranum">402</span>, 377. </p>','\r',char(13)),'\n',char(10)),'-401-402-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',210,replace(replace('<p class="bodytext"><span class="bld">Āttañca</span><a name="M0.0210"></a>.</p>\r\n<p class="bodytext"><span class="bld">Iu</span>iccetesaṃ <span class="bld">ā</span>ttañca hoti, <span class="bld">ri</span>kārāgamo ca ṭhāne.</p>\r\n<p class="bodytext">Isissa bhāvo ārisyaṃ, iṇassa bhāvo āṇyaṃ, usabhassa bhāvo āsabhaṃ, ujuno bhāvo ajjavaṃ, iccevamādī yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Yūna</span>miti kimatthaṃ? Apāyesu jāto āpāyi ko.</p>\r\n<p class="bodytext"><span class="bld">Ṭhāne</span>ti kimatthaṃ? Vematiko, opanayiko, opamāyiko, opāyiko.</p>\r\n<p class="bodytext"><a name="para403"></a><span class="paranum">403</span>, 354. <span class="bld">Kvacādimajjhuttarānaṃ dīgharassā paccayesu ca</span>.</p>\r\n<p class="bodytext">Kvaci ādimajjhauttaraiccetesaṃ dīgharassā honti paccayesu ca apaccayesu ca.</p>\r\n<p class="bodytext"><span class="bld">Ādidīgho</span> tāva – pākāro, nīvāro, pāsādo, pākaṭo, pātimokkho, pāṭikaṅkho iccevamādi.</p>','\r',char(13)),'\n',char(10)),'-403-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',211,replace(replace('<p class="bodytext"><span class="bld">Majjhedīgho</span> <a name="M0.0211"></a> tāva – aṅgamāgadhiko, orabbhamāgavīko iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaradīgho</span> tāva – khantī paramaṃ tapo titikkhā, añjanā giri, koṭarā vanaṃ, aṅgulī iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ādirasso</span> tāva – pageva iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjherasso</span> tāva – sumedhaso suvaṇṇadharehi iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttararasso</span> tāva – bhovādi nāma so hoti, yathābhāvi guṇena so iccevamādi. Aññepi yathājinavacanānuparodhena yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> apaccayesu cāti atthaṃ samucceti,</p>\r\n<p class="bodytext"><a name="para404"></a><span class="paranum">404</span>, 370. <span class="bld">Tesu vuddhilopāgamavikāraviparītādesā ca</span>.</p>\r\n<p class="bodytext">Tesu ādimajjhuttaresu yathājinavacanānuparodhena kvaci vuddhi hoti, kvaci lopo hoti, kvaci āgamo hoti, kvaci vikāro hoti, kvaci viparīto hoti, kvaci ādeso hoti.</p>\r\n<p class="bodytext"><span class="bld">Ādivuddhi</span> tāva – ābhidhammiko, venateyyo iccevamādi.</p>','\r',char(13)),'\n',char(10)),'-404-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',212,replace(replace('<p class="bodytext"><span class="bld">Majjhevuddhi</span> <a name="M0.0212"></a> tāva – sukhaseyyaṃ, sukhakāri dānaṃ, sukhakāri sīlaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaravuddhi</span> tāva – kāliṅgo, māgadhiko, paccakkhadhammā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ādilopo</span> tāva – tālīsaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjhelopo</span> tāva – kattukāmo, kumbhakāraputto, vedallaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaralopo</span> tāva – bhikkhu, bhikkhunī iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ādiāgamo</span> tāva – vutto bhagavatā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjheāgamo</span> tāva – sasīlavā, sapaññavāiccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaraāgamo</span> tāva – vedallaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ādivikāro</span> tāva – ārisyaṃ, āṇyaṃ, āsabhaṃ, ajjavaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjhevikāro</span> tāva – varārisyaṃ, parārisyaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaravikāro</span> tāva – yāni, tāni, sukhāni iccevamādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',213,replace(replace('<p class="bodytext"><span class="bld">Ādiviparīto</span> <a name="M0.0213"></a> tāva – uggate sūriye uggacchati iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjheviparīto</span> tāva – samuggacchati, samuggate sūriye iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaraviparīto</span> tāva – digu, diguṇaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Ādiādeso</span> tāva – yūnaṃ iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Majjheādeso</span> tāva – nyāyogā iccevamādi.</p>\r\n<p class="bodytext"><span class="bld">Uttaraādeso</span> tāva – sabbaseyyo, sabbaseṭṭho, cittaṃ iccevamādi. Evaṃ yathājinavacanānuparodhena sabbattha yojetabbā.</p>\r\n<p class="bodytext"><a name="para405"></a><span class="paranum">405</span>, 365. <span class="bld">Ayuvaṇṇānañcāyo vuddhi</span>.</p>\r\n<p class="bodytext"><span class="bld">A i</span>ti akāro, <span class="bld">i ī</span>iti ivaṇṇo, <span class="bld">u ū</span>iti uvaṇṇo, tesaṃ akāraivaṇṇuvaṇṇānaṃ <span class="bld">ā e o</span>vuddhiyo honti yathāsaṅkhyaṃ, <span class="bld">ā ī ū</span>vuddhi ca.</p>\r\n<p class="bodytext">Ābhidhammiko, venateyyo, oḷumpiko.</p>\r\n<p class="bodytext">Puna <span class="bld">vuddhi</span>ggahaṇaṃ kimatthaṃ? Uttarapadavuddhibhāvatthaṃ, aṅgamagadhehi āgatāti aṅgamāgadhikā. Nigamajanapadesu </p>','\r',char(13)),'\n',char(10)),'-405-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',214,replace(replace('<p class="bodytext">jātāti <a name="M0.0214"></a> negamajānapadā. Purimajanapadesu jātāti porimajānapadā. Sattāhe niyuttoti sattāhikā, catuvijje niyuttoti cātuvijjikā. Iccevamādī yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Vuddhi</span>iccanena kvattho? Vuddhādisarassa vā’saṃyogantassa saṇe ca.</p>\r\n<p class="centered">Iti nāmakappe taddhitakappo aṭṭhamo kaṇḍo.</p>\r\n<p class="centered">Taddhitakappo niṭṭhito.</p>\r\n<p class="chapter">6. Ākhyātakappa</p>\r\n<p class="subhead">Paṭhamakaṇḍa</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',215,replace(replace('<p class="gatha1">Ākhyātasāgaramathajjatanītaraṅgaṃ<a name="M0.0215"></a>,</p>\r\n<p class="gatha2">Dhātujjalaṃ vikaraṇāgamakālamīnaṃ;</p>\r\n<p class="gatha3">Lopānubandhariyamatthavibhāgatīraṃ,</p>\r\n<p class="gathalast">Dhīrā taranti kavino puthubuddhināvā.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Vicittasaṅkhāraparikkhitaṃ imaṃ,</p>\r\n<p class="gatha2">Ākhyātasaddaṃ vipulaṃ asesato;</p>\r\n<p class="gatha3">Paṇamya sambuddhamanantagocaraṃ,</p>\r\n<p class="gathalast">Sugocaraṃ yaṃ vadato suṇātha me.</p>\r\n<p class="bodytext">(Ga)</p>\r\n<p class="gatha1">Adhikāre maṅgale ceva, nipphanne cāvadhāraṇe;</p>\r\n<p class="gathalast">Anantare ca pādāne, <span class="bld">atha</span>saddo pavattati.</p>\r\n<p class="bodytext"><a name="para416"></a><span class="paranum">416</span>, 429. <span class="bld">Atha pubbāni vibhattīnaṃ cha parassapadāni</span>.</p>\r\n<p class="bodytext">Atha sabbāsaṃ vibhattīnaṃ yāni yāni pubbakāni <span class="bld">cha</span> padāni, tāni tāni <span class="bld">parassapada</span>saññāni honti.</p>\r\n<p class="bodytext">Taṃ yathā? <span class="bld">Ti anti, si tha, mi ma</span>.</p>\r\n<p class="bodytext"><span class="bld">Parassapada</span>miccanena kvattho? Kattari parassapadaṃ.</p>\r\n<p class="bodytext"><a name="para407"></a><span class="paranum">407</span>, 439. </p>','\r',char(13)),'\n',char(10)),'-416-407-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',216,replace(replace('<p class="bodytext"><span class="bld">Parāṇyattanopadāni</span><a name="M0.0216"></a>.</p>\r\n<p class="bodytext">Sabbāsaṃ vibhattīnaṃ yāni yāni parāni <span class="bld">cha</span> padāni. Tāni tāni <span class="bld">attanopada</span>saññāni honti.</p>\r\n<p class="bodytext">Taṃ yathā? <span class="bld">Te ante, se vhe, e mhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Attanopada</span>miccanena kvattho? Attanopadāni bhāve ca kammani.</p>\r\n<p class="bodytext"><a name="para408"></a><span class="paranum">408</span>, 431. <span class="bld">Dve dve paṭhama majjhimuttamapurisā</span>.</p>\r\n<p class="bodytext">Tāsaṃ sabbāsaṃ vibhattīnaṃ parassapadānaṃ, attanopadānañca <span class="bld">dve dve</span> padāni <span class="bld">paṭhamamajjhimuttamapurisa</span>saññāni honti.</p>\r\n<p class="bodytext">Taṃ yathā? <span class="bld">Ti anti</span> iti paṭhamapurisā, <span class="bld">si tha</span> iti majjhimapurisā, <span class="bld">mi ma</span> iti uttamapurisā. Attanopadānampi <span class="bld">te ante</span> iti paṭhamapurisā, <span class="bld">se vhe</span> iti majjhimapurisā, <span class="bld">e mhe</span> iti uttamapurisā. Evaṃ sabbattha.</p>\r\n<p class="bodytext"><span class="bld">Paṭhamamajjhimuttamapurisa</span>miccanena kvattho? Nāmamhi payujjamānepi tulyādhikaraṇe paṭhamo, tumhe majjhimo, amhe uttamo.</p>\r\n<p class="bodytext"><a name="para409"></a><span class="paranum">409</span>, 441. <span class="bld">Sabbesamekābhidhāne paro puriso</span>.</p>\r\n<p class="bodytext">Sabbesaṃ tiṇṇaṃ paṭhamamajjhimuttama purisānaṃ ekābhidhāne <span class="bld">paro puriso</span> gahetabbo.</p>','\r',char(13)),'\n',char(10)),'-408-409-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',217,replace(replace('<p class="bodytext">So <a name="M0.0217"></a> ca paṭhati, tvañca paṭhasi, <span class="bld">tumhe paṭhatha</span>. So ca pacati, tvañca pacasi. <span class="bld">Tumhe pacatha</span>. Evaṃ sesāsu vibhattīsu <span class="bld">paro puriso</span> yojetabbo.</p>\r\n<p class="bodytext"><a name="para410"></a><span class="paranum">410</span>, 432. <span class="bld">Nāmamhi payujjamānepi tulyādhikaraṇe paṭhamo</span>.</p>\r\n<p class="bodytext"><span class="bld">Nāma</span>mhi payujjamānepi appayujjamānepi tulyādhikaraṇe <span class="bld">paṭhamapuriso</span> hoti.</p>\r\n<p class="bodytext">So gacchati, te gacchanti.</p>\r\n<p class="bodytext">Appayujjamānepi – gacchati, gacchanti.</p>\r\n<p class="bodytext"><span class="bld">Tulyādhikaraṇe</span>ti kimatthaṃ? Tena haññase tvaṃ devadattena.</p>\r\n<p class="bodytext"><a name="para411"></a><span class="paranum">411</span>, 436. <span class="bld">Tumhe majjhimo</span>.</p>\r\n<p class="bodytext"><span class="bld">Tumhe</span> payujjamānepi appayujjamānepi tulyādhikaraṇe <span class="bld">majjhimapuriso</span> hoti.</p>\r\n<p class="bodytext">Tvaṃ yāsi, tumhe yātha.</p>\r\n<p class="bodytext">Appayujjamānepi – yāsi, yātha.</p>\r\n<p class="bodytext"><span class="bld">Tulyādhikaraṇe</span>ti kimatthaṃ? Tayā paccate odano.</p>\r\n<p class="bodytext"><a name="para412"></a><span class="paranum">412</span>, 437. </p>','\r',char(13)),'\n',char(10)),'-410-411-412-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',218,replace(replace('<p class="bodytext"><span class="bld">Amhe</span> <a name="M0.0218"></a> <span class="bld">uttamo</span>.</p>\r\n<p class="bodytext"><span class="bld">Amhe</span> payujjamānepi appayujjamānepi tulyādhikaraṇe <span class="bld">uttamapuriso</span> hoti.</p>\r\n<p class="bodytext">Ahaṃ yajāmi, mayaṃ yajāma.</p>\r\n<p class="bodytext">Appayujjamānepi – yajāmi, yajāma.</p>\r\n<p class="bodytext"><span class="bld">Tulyādhikaraṇe</span>ti kimatthaṃ? Mayā ijjate buddho.</p>\r\n<p class="bodytext"><a name="para413"></a><span class="paranum">413</span>, 427. <span class="bld">Kāle</span>.</p>\r\n<p class="bodytext">‘‘Kāle’’ iccetaṃ adhikāratthaṃ veditabbaṃ.</p>\r\n<p class="bodytext"><a name="para414"></a><span class="paranum">414</span>, 428. <span class="bld">Vattamānā paccuppanne</span>.</p>\r\n<p class="bodytext"><span class="bld">Paccuppanne</span> kāle <span class="bld">vattamānā</span>vibhatti hoti.</p>\r\n<p class="bodytext">Pāṭaliputtaṃ gacchati, sāvatthiṃ pavisati.</p>\r\n<p class="bodytext"><a name="para415"></a><span class="paranum">415</span>, 451. <span class="bld">Āṇatyā siṭṭhe’nuttakāle pañcamī</span>.</p>\r\n<p class="bodytext"><span class="bld">Āṇatyatthe</span> ca <span class="bld">āsīsatthe</span> ca anuttakāle <span class="bld">pañcamī</span> vibhatti hoti.</p>\r\n<p class="bodytext">Karotu kusalaṃ, sukhaṃ te hotu.</p>\r\n<p class="bodytext"><a name="para416"></a><span class="paranum">416</span>, 454. <span class="bld">Anumatiparikappatthesu sattamī</span>.</p>\r\n<p class="bodytext"><span class="bld">Anumatyatthe</span> ca <span class="bld">parikappatthe</span> ca anuttakāle <span class="bld">sattamī</span> vibhatti hoti.</p>','\r',char(13)),'\n',char(10)),'-413-414-415-416-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',219,replace(replace('<p class="bodytext">Tvaṃ <a name="M0.0219"></a> gaccheyyāsi, kimahaṃ kareyyāmi.</p>\r\n<p class="bodytext"><a name="para417"></a><span class="paranum">417</span>, 460. <span class="bld">Apaccakkhe parokkhātīte</span>.</p>\r\n<p class="bodytext"><span class="bld">Apaccakkhe atīte</span> kāle <span class="bld">parokkhā</span>vibhatti hoti.</p>\r\n<p class="bodytext">Supine kilamāha, evaṃ kila porāṇāhu.</p>\r\n<p class="bodytext"><a name="para418"></a><span class="paranum">418</span>, 456. <span class="bld">Hiyyopabhuti paccakkhe hiyyattanī</span>.</p>\r\n<p class="bodytext"><span class="bld">Hiyyopabhuti atīte</span> kāle paccakkhe vā apaccakkhe vā <span class="bld">hiyyattanī</span> vibhatti hoti.</p>\r\n<p class="bodytext">So agamā maggaṃ, te agamū maggaṃ.</p>\r\n<p class="bodytext"><a name="para419"></a><span class="paranum">419</span>, 469. <span class="bld">Samīpe’jjatanī</span>.</p>\r\n<p class="bodytext"><span class="bld">Ajjappabhuti atīte</span> kāle paccakkhe vā apaccakkhe vā samīpe <span class="bld">ajjatanī</span>vibhatti hoti.</p>\r\n<p class="bodytext">So maggaṃ agamī, te maggaṃ agamuṃ.</p>\r\n<p class="bodytext"><a name="para420"></a><span class="paranum">420</span>, 471. <span class="bld">Māyoge sabbakāle ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Hiyyattanīajjatanī</span>iccetā vibhattiyo yadā <span class="bld">mā</span>yogā, tadā <span class="bld">sabbakāle</span> ca honti.</p>\r\n<p class="bodytext">Mā gamā, mā vacā, mā gamī, mā vacī.</p>','\r',char(13)),'\n',char(10)),'-417-418-419-420-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',220,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇena</span> <a name="M0.0220"></a> pañcamīvibhattipi hoti. Mā gacchāhi.</p>\r\n<p class="bodytext"><a name="para421"></a><span class="paranum">421</span>, 473. <span class="bld">Anāgate bhavissantī</span>.</p>\r\n<p class="bodytext"><span class="bld">Anāgate</span> kāle <span class="bld">bhavissantī vibhatti</span> hoti.</p>\r\n<p class="bodytext">So gacchissati, karissati. Te gacchissanti, karissanti.</p>\r\n<p class="bodytext"><a name="para422"></a><span class="paranum">422</span>, 475. <span class="bld">Kriyātipanne’tīte kālātipatti</span>.</p>\r\n<p class="bodytext"><span class="bld">Kriyātipannamatte atīte</span> kāle <span class="bld">kālātipatti</span> vibhatti hoti.</p>\r\n<p class="bodytext">So ce taṃ yānaṃ alabhissā, agacchissā. Te ce taṃ yānaṃ alabhissaṃsu, agacchissaṃsu.</p>\r\n<p class="bodytext"><a name="para423"></a><span class="paranum">423</span>, 426. <span class="bld">Vattamānā ti anti, si tha, mi ma, te ante, se vhe, e mhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Vattamānā</span> iccesā saññā hoti ti anti, si tha, mi ma, te ante, se vhe, e mhe iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vattamānā</span> iccanena kvattho? Vattamānā paccuppanne.</p>\r\n<p class="bodytext"><a name="para424"></a><span class="paranum">424</span>, 450. <span class="bld">Pañcamī tu antu, hitha, mima, taṃ antaṃ, ssu vho, e āmase</span>.</p>','\r',char(13)),'\n',char(10)),'-421-422-423-424-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',221,replace(replace('<p class="bodytext"><span class="bld">Pañcamī</span>iccesā <a name="M0.0221"></a> saññā hoti tu antu, hi tha, mima, taṃ antaṃ, ssu vho, e āmase iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pañcamī</span>iccanena kvattho? Āṇatyāsiṭṭhe, nuttakāle pañcamī.</p>\r\n<p class="bodytext"><a name="para425"></a><span class="paranum">425</span>, 453. <span class="bld">Sattamī eyya eyyuṃ, eyyā si eyyā tha, eyyāmi eyyāma, etha eraṃ, etho eyyāvho, eyyaṃ eyyāmhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Sattamī</span> iccesā saññā hoti eyya eyyuṃ, eyyāsi eyyātha, eyyāmi eyyāma, etha eraṃ, etho, eyyāvho, eyyaṃ eyyāmhe iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Sattamī</span> iccanena kvattho? Anumatiparikappatthesu sattamī.</p>\r\n<p class="bodytext"><a name="para426"></a><span class="paranum">426</span>, 459. <span class="bld">Parokkhā au ettha, aṃmha, tthare, ttho vho, iṃ mhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Parokkhā</span> iccesā saññā hoti a u, ettha, aṃ mha, ttha re ttho vho, iṃ mhe iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Parokkhā</span> iccanena kvattho? Apaccakkhe parokkhātīte.</p>\r\n<p class="bodytext"><a name="para427"></a><span class="paranum">427</span>, 455. </p>','\r',char(13)),'\n',char(10)),'-425-426-427-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',222,replace(replace('<p class="bodytext"><span class="bld">Hiyyattanī</span> <a name="M0.0222"></a> <span class="bld">āū, ottha, aṃmhā, tthatthuṃ, se vhaṃ, iṃ mhase</span>.</p>\r\n<p class="bodytext"><span class="bld">Hiyyattanī</span> iccesā saññā hoti ā ū, o ttha, aṃ mhā, ttha tthuṃ, se vhaṃ, iṃ mhase iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Hiyyattanī</span> iccanena kvattho? Hiyyopabhuti paccakkhe hiyyattanī.</p>\r\n<p class="bodytext"><a name="para428"></a><span class="paranum">428</span>, 468. <span class="bld">Ajjatanī ī uṃ, o ttha, iṃ mhā, ā ū, se vhaṃ, aṃ mhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Ajjatanī</span> iccesā saññā hoti ī uṃ, o ttha, iṃmhā, ā ū, sevhaṃ, aṃ mhe iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ajjatanī</span> iccanena kvattho? Samīpejjatanī.</p>\r\n<p class="bodytext"><a name="para429"></a><span class="paranum">429</span>, 472. <span class="bld">Bhavissantī ssati ssanti, ssasi ssatha, ssāmi ssāma, ssate ssante, ssase ssavhe, ssaṃ ssāmhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhavissantī</span> iccesā saññā hoti ssati ssanti, ssasi ssatha, ssāmi ssāma, ssate ssante, ssase ssavhe, ssaṃ ssāmhe iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Bhavissantī</span> iccanena kvattho? Anāgate bhavissantī.</p>\r\n<p class="bodytext"><a name="para430"></a><span class="paranum">430</span>, 373. </p>','\r',char(13)),'\n',char(10)),'-428-429-430-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',223,replace(replace('<p class="bodytext"><span class="bld">Kālātipatti</span> <a name="M0.0223"></a> <span class="bld">ssā ssaṃsu, sse ssatha, ssaṃ ssāmhā, ssatha sissu, ssase ssavhe, ssiṃ ssāmhase</span>.</p>\r\n<p class="bodytext"><span class="bld">Kālātipatti</span> iccesā saññā hoti ssā ssaṃsu, sse ssatha, ssaṃ ssāmhā, ssatha ssisu, ssase ssavhe, ssiṃ ssāmhase iccetesaṃ <span class="bld">dvādasannaṃ</span> padānaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kālātipatti</span> iccanena kvattho? Kriyātipanne’ tīte kālātipatti.</p>\r\n<p class="bodytext"><a name="para431"></a><span class="paranum">431</span>, 458. <span class="bld">Hiyyattanī sattamī pañcamī vattamānā sabbadhātukaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Hiyyattanādayo catasso</span> vibhattiyo <span class="bld">sabbadhātuka</span> saññā honti.</p>\r\n<p class="bodytext">Agamā, gaccheyya, gacchatu, gacchati.</p>\r\n<p class="bodytext"><span class="bld">Sabbadhātuka</span> iccanena kvattho? Ikārāgamo asabbadhātukamhi.</p>\r\n<p class="centered">Iti ākhyātakappe paṭhamo kaṇḍo.</p>\r\n<p class="subhead">Dutiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para432"></a><span class="paranum">432</span>, 362. </p>','\r',char(13)),'\n',char(10)),'-431-432-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',224,replace(replace('<p class="bodytext"><span class="bld">Dhātuliṅgehi</span> <a name="M0.0224"></a> <span class="bld">parā paccayā</span>.</p>\r\n<p class="bodytext">Dhātuliṅgaiccetehi parā paccayā honti.</p>\r\n<p class="bodytext">Karoti, gacchati. Yo koci karoti, taṃ añño ‘‘karohi karohi’’ iccevaṃ bravīti, atha vā karontaṃ payojayati = kāreti. Saṅgho pabbatamiva attānamācarati = pabbatāyati. Taḷākaṃ samuddamiva attānamācarati = samuddāyati, saddo cicciṭamiva attānamācarati = cicciṭāyati, vasiṭṭhassa apaccaṃ vāsiṭṭho. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para433"></a><span class="paranum">433</span>, 528. <span class="bld">Tija gupa kita māne hi kha cha sā vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Tija gupa kita mānu</span> iccetehi dhātūhi <span class="bld">kha cha sa</span> iccete paccayā honti vā.</p>\r\n<p class="bodytext">Titikkhati, jigucchati, tikicchati, vīmaṃsati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Tejati, gopati, māneti.</p>\r\n<p class="bodytext"><a name="para434"></a><span class="paranum">434</span>, 534. <span class="bld">Bhuja ghasa hara su pādīhi tumicchatthesu</span>.</p>','\r',char(13)),'\n',char(10)),'-433-434-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',225,replace(replace('<p class="bodytext"><span class="bld">Bhuja</span> <a name="M0.0225"></a> <span class="bld">ghasa hara su pā</span>iccevamādīhi dhātūhi <span class="bld">tumicchatthesu kha cha sa</span>iccete paccayā honti vā.</p>\r\n<p class="bodytext">Bhottumicchati=bubhukkhati, ghasitumicchati=jighacchati, haritumicchati=jigīsati, sotumicchati=sussusati, pātumicchati=pivāsati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Bhottumicchati.</p>\r\n<p class="bodytext"><span class="bld">Tumicchatthesū</span>ti kimatthaṃ? Bhuñjati.</p>\r\n<p class="bodytext"><a name="para435"></a><span class="paranum">435</span>, 536. <span class="bld">Āya nāmato kattūpamānādācāre</span>.</p>\r\n<p class="bodytext">Nāmato kattūpamānā ācāratthe <span class="bld">āya</span>paccayo hoti.</p>\r\n<p class="bodytext">Saṅgho pabbatamiva attānamācarati = pabbatāyati, taḷākaṃ samuddamiva attānamācarati = samuddāyati, saddo cicciṭamiva attānamācarati = cicciṭāyati. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para436"></a><span class="paranum">436</span>, 537. <span class="bld">Īyūpamānā ca</span>.</p>\r\n<p class="bodytext">Nāmato upamānā ācāratthe ca <span class="bld">īya</span>paccayo hoti.</p>','\r',char(13)),'\n',char(10)),'-435-436-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',226,replace(replace('<p class="bodytext">Achattaṃ <a name="M0.0226"></a> chattamiva ācarati =chattīyati, aputtaṃ puttamiva ācarati=puttīyati.</p>\r\n<p class="bodytext"><span class="bld">Upamānā</span>ti kimatthaṃ? Dhammaṃ ācarati.</p>\r\n<p class="bodytext"><span class="bld">Ācāre</span>ti kimatthaṃ? Achattaṃ chattamiva rakkhati. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para437"></a><span class="paranum">437</span>, 538. <span class="bld">Nāmamhā’tticchatthe</span>.</p>\r\n<p class="bodytext">Nāmamhā attano icchatthe <span class="bld">īya</span>paccayo hoti.</p>\r\n<p class="bodytext">Attano pattamicchati = pattīyati. Evaṃ vatthīyati, parikkhārīyati, cīvarīyati, dhanīyati, ghaṭīyati.</p>\r\n<p class="bodytext"><span class="bld">Atticchatthe</span>ti kimatthaṃ? Aññassa pattamicchati. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para438"></a><span class="paranum">438</span>, 540. <span class="bld">Dhātūhi ṇe ṇaya ṇāpe ṇāpayā kāritāni hetvatthe</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi <span class="bld">ṇeṇaya ṇāpe ṇāpaya</span>iccete paccayā honti <span class="bld">kārita</span>saññā ca hetvatthe.</p>\r\n<p class="bodytext">Yo koci karoti, taṃ añño ‘‘karohi karohi’’ iccevaṃ bravīti, atha vā karontaṃ </p>','\r',char(13)),'\n',char(10)),'-437-438-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',227,replace(replace('<p class="bodytext">payojayati <a name="M0.0227"></a> = kāreti, kārayati, kārāpeti, kārā payati. Ye keci karonti, te aññe ‘‘karotha karotha’’ iccevaṃ bruvanti = kārenti, kārayanti, kārāpenti, kārāpayanti. Yo koci pacati, taṃ añño ‘‘pacāhi pacāhi’’iccevaṃ bravīti, atha vā pacantaṃ payojayati = pāceti, pācayati, pācāpeti, pācāpayati. Ye keci pacanti, te aññe ‘‘pacatha pacatha’’ iccevaṃ bruvanti = pācenti, pācayanti, pācāpenti. Pācāpayanti. Evaṃ bhaṇeti, bhaṇayati, bhaṇāpeti, bhaṇāpayati. Bhaṇenti, bhaṇayanti, bhaṇāpenti, bhaṇāpayanti. Tathariva aññepi yojetabbā.</p>\r\n<p class="bodytext"><span class="bld">Hetvatthe</span>ti kimatthaṃ? Karoti, pacati.</p>\r\n<p class="bodytext"><span class="bld">Atthaggahaṇena ala</span>paccayo hoti, jotalati.</p>\r\n<p class="bodytext"><a name="para439"></a><span class="paranum">439</span>, 539. <span class="bld">Dhāturūpe nāmasmā ṇayo ca</span>.</p>\r\n<p class="bodytext">Tasmā nāmasmā <span class="bld">ṇaya</span>paccayo hoti <span class="bld">kārita</span>sañño ca dhāturūpe sati.</p>\r\n<p class="bodytext">Hatthinā atikkamati maggaṃ = atihatthayati, vīṇāya upagāyati gītaṃ = upavīṇayati, daḷhaṃ karoti </p>','\r',char(13)),'\n',char(10)),'-439-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',228,replace(replace('<p class="bodytext">vīriyaṃ <a name="M0.0228"></a> = daḷhayati, visuddhā hoti ratti = visuddhayati.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena āra āla</span>iccete paccayā honti. Santaṃ karoti = santārati, upakkamaṃ karoti = upakkamālati.</p>\r\n<p class="bodytext"><a name="para440"></a><span class="paranum">440</span>, 445. <span class="bld">Bhāvakammesu yo</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi bhāvakammesu <span class="bld">ya</span>paccayo hoti.</p>\r\n<p class="bodytext">Ṭhīyate, bujjhate, paccate, labbhate, karīyate, yujjate, uccate.</p>\r\n<p class="bodytext"><span class="bld">Bhāvakammesū</span>ti kimatthaṃ? Karoti, pacati, paṭhati.</p>\r\n<p class="bodytext"><a name="para441"></a><span class="paranum">441</span>, 447. <span class="bld">Tassa cavaggayakāravakārattaṃ sadhātvantassa</span>.</p>\r\n<p class="bodytext">Tassa <span class="bld">ya</span>paccayassa <span class="bld">ca</span>vaggayakāravakārattaṃ hoti dhātūnaṃ antena saha yathāsambhavaṃ.</p>\r\n<p class="bodytext">Vuccate, vuccante, uccate, uccante, paccate, paccante. Majjate, majjante, yujjate, yujjante. Bujjhate, bujjhante, kujjhate, kujjhante, ujjhate, ujjhante. Haññate, haññante. Kayyate, kayyante. Dibbate, dibbante.</p>\r\n<p class="bodytext"><a name="para442"></a><span class="paranum">442</span>, 448. </p>','\r',char(13)),'\n',char(10)),'-440-441-442-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',229,replace(replace('<p class="bodytext"><span class="bld">Ivaṇṇāgamo</span> <a name="M0.0229"></a> <span class="bld">vā</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi <span class="bld">ya</span>mhi paccaye, pare ivaṇṇāgamo hoti vā.</p>\r\n<p class="bodytext">Karīyate, karīyati, gacchīyate, gacchīyati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Kayyate.</p>\r\n<p class="bodytext"><a name="para443"></a><span class="paranum">443</span>, 449. <span class="bld">Pubbarūpañca</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi <span class="bld">ya</span>paccayo pubbarūpamāpajjate vā.</p>\r\n<p class="bodytext">Vuḍḍhate, phallate, dammate, sakkate, labbhate, dissate.</p>\r\n<p class="bodytext"><a name="para444"></a><span class="paranum">444</span>, 501. <span class="bld">Tathā kattari ca</span>.</p>\r\n<p class="bodytext">Yathā heṭṭhā bhāvakammesu <span class="bld">ya</span>paccayassa ādeso hoti tathā kattaripi <span class="bld">ya</span>paccayassa ādeso kātabbo.</p>\r\n<p class="bodytext">Bujjhati, vijjhati, maññati, sibbati.</p>\r\n<p class="bodytext"><a name="para445"></a><span class="paranum">445</span>, 433. <span class="bld">Bhūvādito a</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhū</span>iccevamādito dhātugaṇato <span class="bld">a</span>paccayo hoti kattari.</p>','\r',char(13)),'\n',char(10)),'-443-444-445-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',230,replace(replace('<p class="bodytext">Bhavati<a name="M0.0230"></a>, paṭhati, pacati, jayati.</p>\r\n<p class="bodytext"><a name="para446"></a><span class="paranum">446</span>, 509. <span class="bld">Rudhādito niggahitapubbañca</span>.</p>\r\n<p class="bodytext"><span class="bld">Rudha</span>iccevamādito dhātugaṇato <span class="bld">a</span>paccayo hoti kattari, pubbe niggahitāgamo hoti.</p>\r\n<p class="bodytext">Rundhati, chindati, bhindati.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena i ī e o</span>iccete paccayā honti niggahitapubbañca.</p>\r\n<p class="bodytext">Rundhiti, rundhīti, rundheti, rundhoti, sumbhoti, parisumbhoti.</p>\r\n<p class="bodytext"><a name="para447"></a><span class="paranum">447</span>, 510. <span class="bld">Divādito yo</span>.</p>\r\n<p class="bodytext"><span class="bld">Divu</span>iccevamādito dhātugaṇato <span class="bld">ya</span>paccayo hoti kattari.</p>\r\n<p class="bodytext">Dibbati, thibbati, yujjhati, vijjhati, bujjhati.</p>\r\n<p class="bodytext"><a name="para448"></a><span class="paranum">448</span>, 512. <span class="bld">Svādito ṇu ṇā uṇā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Su</span>iccevamādito dhātugaṇato <span class="bld">ṇu ṇā u ṇā</span>iccete paccayā honti kattari.</p>','\r',char(13)),'\n',char(10)),'-446-447-448-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',231,replace(replace('<p class="bodytext">Abhisuṇoti<a name="M0.0231"></a>, abhisuṇāti, saṃvuṇoti, saṃvuṇāti, āvuṇoti, āvuṇāti, pāpuṇoti, pāpuṇāti.</p>\r\n<p class="bodytext"><a name="para449"></a><span class="paranum">449</span>, 513. <span class="bld">Kiyādito nā</span>.</p>\r\n<p class="bodytext"><span class="bld">Kī</span>iccevamādito dhātugaṇato <span class="bld">nā</span>paccayo hoti kattari.</p>\r\n<p class="bodytext">Kiṇāti, jināti, dhunāti, munāti, lunāti, punāti.</p>\r\n<p class="bodytext"><a name="para450"></a><span class="paranum">450</span>, 517. <span class="bld">Gahādito ppa ṇhā</span>.</p>\r\n<p class="bodytext"><span class="bld">Gaha</span>iccevamādito dhātugaṇato <span class="bld">ppaṇhā</span>iccete paccayā honti kattari.</p>\r\n<p class="bodytext">Gheppati, gaṇhāti.</p>\r\n<p class="bodytext"><a name="para451"></a><span class="paranum">451</span>, 520. <span class="bld">Tanādito oyirā</span>.</p>\r\n<p class="bodytext"><span class="bld">Tanu</span>iccevamādito dhātugaṇato <span class="bld">o yira</span>iccete paccayā honti kattari.</p>','\r',char(13)),'\n',char(10)),'-449-450-451-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',232,replace(replace('<p class="bodytext">Tanoti<a name="M0.0232"></a>, tanohi, karoti, karohi, kayi rati, kayirāhi.</p>\r\n<p class="bodytext"><a name="para452"></a><span class="paranum">452</span>, 525. <span class="bld">Curādito, ṇe ṇayā</span>.</p>\r\n<p class="bodytext"><span class="bld">Cura</span>iccevamādito dhātugaṇato <span class="bld">ṇe ṇaya</span>iccete paccayā honti kattari, kāritasaññā ca.</p>\r\n<p class="bodytext">Coreti, corayati, cinteti, cintayati, manteti, mantayati.</p>\r\n<p class="bodytext"><a name="para453"></a><span class="paranum">453</span>, 444. <span class="bld">Attanopadāni bhāve ca kammani</span>.</p>\r\n<p class="bodytext">Bhāve ca kammani ca attanopadāni honti.</p>\r\n<p class="bodytext">Uccate, uccante, majjate, majjante, yujjate, yujjante, kujjhate, kujjhante, labbhate, labbhante, kayyate, kayyante.</p>\r\n<p class="bodytext"><a name="para454"></a><span class="paranum">454</span>, 440. <span class="bld">Kattari ca</span>.</p>\r\n<p class="bodytext">Kattari ca attanopadāni honti.</p>\r\n<p class="bodytext">Maññate, rocate, socate, bujjhate, jāyate.</p>\r\n<p class="bodytext"><a name="para455"></a><span class="paranum">455</span>, 530. <span class="bld">Dhātuppaccayehi vibhattiyo</span>.</p>\r\n<p class="bodytext"><span class="bld">Dhātuniddiṭṭhehi</span> paccayehi <span class="bld">khādikārita</span>ntehi vibhattiyo honti.</p>','\r',char(13)),'\n',char(10)),'-452-453-454-455-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',233,replace(replace('<p class="bodytext">Titikkhati<a name="M0.0233"></a>, jigucchati, vīmaṃsati, samuddāyati, puttīyati, kāreti, pāceti.</p>\r\n<p class="bodytext"><a name="para456"></a><span class="paranum">456</span>, 430. <span class="bld">Kattari parassapadaṃ</span>.</p>\r\n<p class="bodytext">Kattari parassapadaṃ hoti.</p>\r\n<p class="bodytext">Karoti, pacati, paṭhati, gacchati.</p>\r\n<p class="bodytext"><a name="para457"></a><span class="paranum">457</span>, 424. <span class="bld">Bhūvādayo dhātavo</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhū</span>iccevamādayo ye saddagaṇā, te dhātusaññā honti.</p>\r\n<p class="bodytext">Bhavati, bhavanti, carati, caranti, pacati, pacanti, cintayati, cintayanti, hoti, honti, gacchati, gacchanti.</p>\r\n<p class="centered">Iti ākhyātakappe dutiyo kaṇḍo.</p>\r\n<p class="subhead">Tatiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para458"></a><span class="paranum">458</span>, 461. <span class="bld">Kvacādivaṇṇānamekassarānaṃ dvebhāvo</span>.</p>\r\n<p class="bodytext">Ādibhūtānaṃ vaṇṇānaṃ ekassarānaṃ kvaci dvebhāvo hoti.</p>','\r',char(13)),'\n',char(10)),'-456-457-458-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',234,replace(replace('<p class="bodytext">Titikkhati<a name="M0.0234"></a>, jigucchati, tikicchati, vīmaṃsati, bubhukkhati, pivāsati, daddallati, dadāti, jahāti, caṅkamati.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Kampati, calati.</p>\r\n<p class="bodytext"><a name="para459"></a><span class="paranum">459</span>, 462. <span class="bld">Pubbo’bbhāso</span>.</p>\r\n<p class="bodytext">Dvebhūtassa dhātussa yo pubbo, so <span class="bld">abbhāsa</span>sañño hoti.</p>\r\n<p class="bodytext">Dadhāti, dadāti, babhūva.</p>\r\n<p class="bodytext"><a name="para460"></a><span class="paranum">460</span>, 506. <span class="bld">Rasso</span>.</p>\r\n<p class="bodytext">Abbhāse vattamānassa sarassa rasso hoti. Dadhāti, jahāti.</p>\r\n<p class="bodytext"><a name="para461"></a><span class="paranum">461</span>, 464. <span class="bld">Dutiyacatutthānaṃ paṭhamatatiyā</span>.</p>\r\n<p class="bodytext">Abbhāsagatānaṃ dutiyacatutthānaṃ paṭhamatatiyā honti.</p>\r\n<p class="bodytext">Ciccheda, bubhukkhati, babhūva, dadhāti.</p>\r\n<p class="bodytext"><a name="para462"></a><span class="paranum">462</span>, 476. <span class="bld">Kavaggassa cavaggo</span>.</p>\r\n<p class="bodytext">Abbhāse vattamānassa <span class="bld">ka</span>vaggassa <span class="bld">ca</span> vaggo hoti.</p>','\r',char(13)),'\n',char(10)),'-459-460-461-462-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',235,replace(replace('<p class="bodytext">Cikicchati<a name="M0.0235"></a>, jigucchati, jighacchati, jigīsati, jaṅgamati, caṅkamati.</p>\r\n<p class="bodytext"><a name="para463"></a><span class="paranum">463</span>, 532. <span class="bld">Mānakitānaṃ vatattaṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Mānakita</span>iccetesaṃ dhātūnaṃ abbhāsagatānaṃ <span class="bld">va</span>kāra <span class="bld">ta</span>kārattaṃ hoti vā yathāsaṅkhyaṃ.</p>\r\n<p class="bodytext">Vīmaṃsati, tikicchati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Cikicchati.</p>\r\n<p class="bodytext"><a name="para464"></a><span class="paranum">464</span>, 504. <span class="bld">Hassa jo</span>.</p>\r\n<p class="bodytext">Abbhāse vattamānassa <span class="bld">ha</span>kārassa <span class="bld">jo</span> hoti.</p>\r\n<p class="bodytext">Jahāti, juhvati, juhoti, jahāra.</p>\r\n<p class="bodytext"><a name="para465"></a><span class="paranum">465</span>, 463. <span class="bld">Antassivaṇṇākāro vā</span>.</p>\r\n<p class="bodytext">Abbhāsassa antassa ivaṇṇo hoti, <span class="bld">a</span>kāro vā.</p>\r\n<p class="bodytext">Jigucchati, pivāsati, vīmaṃsati, jighacchati, babhūva, dadhāti.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Bubhukkhati.</p>\r\n<p class="bodytext"><a name="para466"></a><span class="paranum">466</span>, 489. </p>','\r',char(13)),'\n',char(10)),'-463-464-465-466-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',236,replace(replace('<p class="bodytext"><span class="bld">Niggahitañca</span><a name="M0.0236"></a>.</p>\r\n<p class="bodytext">Abbhāsassa ante niggahitāgamo hoti vā.</p>\r\n<p class="bodytext">Caṅkamati, cañcalati, jaṅgamati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Pivāsati, daddallati.</p>\r\n<p class="bodytext"><a name="para467"></a><span class="paranum">467</span>, 533. <span class="bld">Tato pāmānānaṃ vā maṃ sesu</span>.</p>\r\n<p class="bodytext">Tato abbhāsato <span class="bld">pāmāna</span>iccetesaṃ dhātūnaṃ <span class="bld">vāmaṃ</span>iccete ādesā honti yathāsaṅkhyaṃ <span class="bld">sa</span>paccaye pare.</p>\r\n<p class="bodytext">Pivāsati, vīmaṃsati.</p>\r\n<p class="bodytext"><a name="para468"></a><span class="paranum">468</span>, 462. <span class="bld">Ṭhā tiṭṭho</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṭhā</span>iccetassa dhātussa <span class="bld">tiṭṭhā</span>deso hoti vā.</p>\r\n<p class="bodytext">Tiṭṭhati, tiṭṭhatu, tiṭṭheyya, tiṭṭheyyuṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Ṭhāti.</p>\r\n<p class="bodytext"><a name="para469"></a><span class="paranum">469</span>, 494. <span class="bld">Pā pivo</span>.</p>\r\n<p class="bodytext"><span class="bld">Pā</span>iccetassa dhātussa <span class="bld">pi</span>vādeso hoti vā.</p>\r\n<p class="bodytext">Pivati, pivatu, piveyya, piveyyuṃ.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Pāti.</p>\r\n<p class="bodytext"><a name="para470"></a><span class="paranum">470</span>, 514. </p>','\r',char(13)),'\n',char(10)),'-467-468-469-470-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',237,replace(replace('<p class="bodytext"><span class="bld">Ñāssa</span> <a name="M0.0237"></a> <span class="bld">jā jaṃ nā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ñā</span>iccetassa dhātussa <span class="bld">jā jaṃ nā</span>ādesā honti vā.</p>\r\n<p class="bodytext">Jānāti, jāneyya, jāniyā, jaññā, nāyati.</p>\r\n<p class="bodytext"><a name="para471"></a><span class="paranum">471</span>, 483. <span class="bld">Disassa passa dissa dakkhā vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Disa</span>iccetassa dhātussa <span class="bld">passa dissa dakkha</span>iccete ādesā honti vā.</p>\r\n<p class="bodytext">Passati, dissati, dakkhati, adakkha.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Addasa.</p>\r\n<p class="bodytext"><a name="para472"></a><span class="paranum">472</span>, 531. <span class="bld">Byañjanantassa co chapaccayesu ca</span>.</p>\r\n<p class="bodytext">Byañjanantassa dhātussa <span class="bld">co</span> hoti <span class="bld">cha</span>paccayesu paresu.</p>\r\n<p class="bodytext">Jigucchati, tikicchati, jighacchati.</p>\r\n<p class="bodytext"><a name="para473"></a><span class="paranum">473</span>, 529. <span class="bld">Ko khe ca</span>.</p>\r\n<p class="bodytext">Byañjanantassa dhātussa <span class="bld">ko</span> hoti <span class="bld">kha</span>paccaye pare.</p>','\r',char(13)),'\n',char(10)),'-471-472-473-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',238,replace(replace('<p class="bodytext">Titikkhati<a name="M0.0238"></a>, bubhukkhati.</p>\r\n<p class="bodytext"><a name="para474"></a><span class="paranum">474</span>, 535. <span class="bld">Harassa gīse</span>.</p>\r\n<p class="bodytext"><span class="bld">Hara</span>iccetassa dhātussa sabbasseva <span class="bld">gī</span>ādeso hoti <span class="bld">sa</span>paccaye pare.</p>\r\n<p class="bodytext">Jigīsati.</p>\r\n<p class="bodytext"><a name="para475"></a><span class="paranum">475</span>, 565. <span class="bld">Brūbhūnamāhabhūvā parokkhāyaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Brūbhū</span>iccetesaṃ dhātūnaṃ <span class="bld">āhabhūva</span>iccete ādesā honti yathāsaṅkhyaṃ parokkhāyaṃ vibhattiyaṃ.</p>\r\n<p class="bodytext">Āha, āhu, babhūva, babhūvu.</p>\r\n<p class="bodytext"><span class="bld">Parokkhāya</span>miti kimatthaṃ? Abracuṃ.</p>\r\n<p class="bodytext"><a name="para476"></a><span class="paranum">476</span>, 442. <span class="bld">Gamissanto ccho vā sabbāsu</span>.</p>\r\n<p class="bodytext"><span class="bld">Gamu</span>iccetassa dhātussa anto <span class="bld">ma</span>kāro <span class="bld">ccho</span> hoti vā sabbāsu paccayavibhattīsu.</p>\r\n<p class="bodytext">Gacchamāno, gacchanto. Gacchati, gameti. Gacchatu, gametu. Gaccheyya. Gameyya. Agacchā, agamā. Agacchī, agamī. Gacchissati, gamissati. Agacchissā, agamissā.</p>','\r',char(13)),'\n',char(10)),'-474-475-476-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',239,replace(replace('<p class="bodytext"><span class="bld">Gamisse</span>ti <a name="M0.0239"></a> kimatthaṃ? Icchati.</p>\r\n<p class="bodytext"><a name="para477"></a><span class="paranum">477</span>, 479. <span class="bld">Vacassajjatanimhi makāro o</span>.</p>\r\n<p class="bodytext"><span class="bld">Vaca</span>iccetassa dhātussa <span class="bld">a</span>kāro <span class="bld">o</span>ttamāpajjate ajjatanimhi vibhattimhi.</p>\r\n<p class="bodytext">Avoca. Avocuṃ.</p>\r\n<p class="bodytext"><span class="bld">Ajjatanimhī</span>ti kimatthaṃ? Avaca, avacū.</p>\r\n<p class="bodytext"><a name="para478"></a><span class="paranum">478</span>, 438. <span class="bld">Akāro dīghaṃ hi mi mesu</span>.</p>\r\n<p class="bodytext"><span class="bld">A</span>kāro dīghamāpajjate <span class="bld">himima</span>iccetesu vibhattīsu.</p>\r\n<p class="bodytext">Gacchāhi, gacchāmi, gacchāma, gacchāmhe.</p>\r\n<p class="bodytext"><span class="bld">Mikāraggahaṇena</span> hivibhattimhi <span class="bld">a</span>kāro kvaci na dīghamāpajjate. Gacchahi.</p>\r\n<p class="bodytext"><a name="para479"></a><span class="paranum">479</span>, 452. <span class="bld">Ha lopaṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Hi</span>vibhatti lopamāpajjate vā.</p>\r\n<p class="bodytext">Gaccha, gacchāhi, gama, gamāhi, gamaya, gamayāhi.</p>\r\n<p class="bodytext"><span class="bld">Hī</span>ti kimatthaṃ? Gacchati, gamayati.</p>\r\n<p class="bodytext"><a name="para480"></a><span class="paranum">480</span>, 490. <span class="bld">Hotissarehohe bhavissantimhissassa ca</span>.</p>','\r',char(13)),'\n',char(10)),'-477-478-479-480-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',240,replace(replace('<p class="bodytext"><span class="bld">Hū</span>iccetassa <a name="M0.0240"></a> dhātussa saro <span class="bld">e ha oha e</span>ttamāpajja te bhavissantimhi, <span class="bld">ssa</span>ssa ca lopo hoti vā.</p>\r\n<p class="bodytext">Hehiti, hehinti, hohiti, hohinti, heti, henti, hehissati, hehissanti, hohissati, hohissanti, hessati, hessanti.</p>\r\n<p class="bodytext"><span class="bld">Hū</span>ti kimatthaṃ? Bhavissati, bhavissanti.</p>\r\n<p class="bodytext"><span class="bld">Bhavissantimhī</span>ti kimatthaṃ? Hoti.</p>\r\n<p class="bodytext"><a name="para481"></a><span class="paranum">481</span>, 524. <span class="bld">Karassa sapaccayassa kāho</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetassa dhātussa sapaccayassa <span class="bld">kāhā</span>deso hoti vā bhavissantimhi vibhattimhi, <span class="bld">sa</span>ssa ca niccaṃ lopo hoti.</p>\r\n<p class="bodytext">Kāhati, kāhiti, kāhasi, kāhisi, kāhāmi, kāhāma.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Karissati, karissanti.</p>\r\n<p class="bodytext"><span class="bld">Sapaccayaggahaṇena</span> aññehipi bhavissantiyā vibhattiyā <span class="bld">khāmi khāma chāmi chāma</span>iccādayo ādesā honti. Vakkhāmi, vakkhāma, vacchāmi, vacchāma.</p>\r\n<p class="centered">Iti ākhyātakappe tatiyo kaṇḍo.</p>\r\n<p class="subhead">Catutthakaṇḍa</p>\r\n<p class="bodytext"><a name="para482"></a><span class="paranum">482</span>, 508. </p>','\r',char(13)),'\n',char(10)),'-481-482-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',241,replace(replace('<p class="bodytext"><span class="bld">Dādantassaṃ</span> <a name="M0.0241"></a> <span class="bld">mi mesu</span>.</p>\r\n<p class="bodytext"><span class="bld">Dā</span>iccetassa dhātussa antassa <span class="bld">aṃ</span> hoti <span class="bld">mima</span>iccetesu.</p>\r\n<p class="bodytext">Dammi, damma.</p>\r\n<p class="bodytext"><a name="para483"></a><span class="paranum">483</span>, 527. <span class="bld">Asaṃyogantassa vuddhi kārite</span>.</p>\r\n<p class="bodytext">Asaṃyogantassa dhātussa kārite vuddhi hoti.</p>\r\n<p class="bodytext">Kāreti, kārenti, kārayati, kārayanti, kārāpeti, kārāpenti, kārāpayati, kārāpayanti.</p>\r\n<p class="bodytext"><span class="bld">Asaṃyogantasse</span>ti kimatthaṃ? Cintayati, mantayati.</p>\r\n<p class="bodytext"><a name="para484"></a><span class="paranum">484</span>, 542. <span class="bld">Ghaṭādīnaṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ghaṭā</span>dīnaṃ dhātūnaṃ asaṃyogantānaṃ vuddhi hoti vā kārite.</p>\r\n<p class="bodytext">Ghāṭeti, ghaṭeti, ghāṭayati, ghaṭayati, ghāṭāpeti, ghaṭāpeti, ghāṭāpayati, ghaṭāpayati, gāmeti, gameti, gāmayati, gamayati, gāmāpeti, gamāpeti. Gāmāpayati, gamāpayati.</p>\r\n<p class="bodytext"><span class="bld">Ghaṭādīnami</span>ti kimatthaṃ? Kāreti.</p>\r\n<p class="bodytext"><a name="para485"></a><span class="paranum">485</span>, 434. </p>','\r',char(13)),'\n',char(10)),'-483-484-485-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',242,replace(replace('<p class="bodytext"><span class="bld">Aññesu</span> <a name="M0.0242"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Aññesu ca paccayesu sabbesaṃ dhātūnaṃ asaṃyogantānaṃ vuddhi hoti.</p>\r\n<p class="bodytext">Jayati, hoti, bhavati.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> ṇupaccayassāpi vuddhi hoti. Abhisuṇoti.</p>\r\n<p class="bodytext"><a name="para486"></a><span class="paranum">486</span>, 543. <span class="bld">Guha dusānaṃ dīghaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Guha dusa</span>iccetesaṃ dhātūnaṃ saro dīghamāpajjate kārite.</p>\r\n<p class="bodytext">Gūhayati, dūsayati.</p>\r\n<p class="bodytext"><a name="para487"></a><span class="paranum">487</span>, 478. <span class="bld">Vaca vasa vahādīnamukāro vassa ye</span>.</p>\r\n<p class="bodytext"><span class="bld">Vaca vasa vaha</span>iccevamādīnaṃ dhātūnaṃ vakārassa <span class="bld">u</span>kāro hoti <span class="bld">ya</span>paccaye pare.</p>\r\n<p class="bodytext">Uccate, vuccati, gussati, vuyhati.</p>\r\n<p class="bodytext"><a name="para488"></a><span class="paranum">488</span>, 481. <span class="bld">Ha vipariyayo lo vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ha</span>kārassa vipariyayo hoti <span class="bld">ya</span>paccaye pare, <span class="bld">ya</span>paccayassa ca <span class="bld">lo</span> hoti vā.</p>\r\n<p class="bodytext">Vulhati, vuyhati.</p>\r\n<p class="bodytext"><a name="para489"></a><span class="paranum">489</span>, 519. </p>','\r',char(13)),'\n',char(10)),'-486-487-488-489-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',243,replace(replace('<p class="bodytext"><span class="bld">Gahassa</span> <a name="M0.0243"></a> <span class="bld">ghe ppe</span>.</p>\r\n<p class="bodytext"><span class="bld">Gaha</span>iccetassa dhātussa sabbassa <span class="bld">ghe</span>kāro hoti ppapaccaye pare.</p>\r\n<p class="bodytext">Gheppati.</p>\r\n<p class="bodytext"><a name="para490"></a><span class="paranum">490</span>, 518. <span class="bld">Halo po ṇhāmhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Gaha</span>iccetassa dhātussa <span class="bld">ha</span>kārassa lopo hoti <span class="bld">ṇhā</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Gaṇhāti.</p>\r\n<p class="bodytext"><a name="para491"></a><span class="paranum">491</span>, 523. <span class="bld">Karassa kāsattamajjatanimhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetassa dhātussa sabbassa <span class="bld">kāsa</span>ttaṃ hoti vā ajjatanimhi vibhattimhi.</p>\r\n<p class="bodytext">Akāsi, akāsuṃ. Akari, akaruṃ.</p>\r\n<p class="bodytext"><span class="bld">Kāsattamitibhāvaniddesena</span> aññatthāpi <span class="bld">sā</span>gamo hoti. Ahosi, adāsi.</p>\r\n<p class="bodytext"><a name="para492"></a><span class="paranum">492</span>, 499. <span class="bld">Asasmā mimānaṃ mhimhā’ ntalopo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>iccetāya dhātuyā <span class="bld">mima</span>iccetesaṃ vibhattīnaṃ <span class="bld">mhimhā</span>desā honti vā, dhātvantassa lopo ca.</p>','\r',char(13)),'\n',char(10)),'-490-491-492-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',244,replace(replace('<p class="bodytext">Amhi<a name="M0.0244"></a>, amha, asmi, asma.</p>\r\n<p class="bodytext"><a name="para493"></a><span class="paranum">493</span>, 498. <span class="bld">Thassa tthattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>iccetāya dhātuyā <span class="bld">tha</span>ssa vibhattissa <span class="bld">ttha</span>ttaṃ hoti, dhātvantassa lopo ca.</p>\r\n<p class="bodytext">Attha.</p>\r\n<p class="bodytext"><a name="para494"></a><span class="paranum">494</span>, 495. <span class="bld">Tissa tthittaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>iccetāya dhātuyā <span class="bld">ti</span>ssa vibhattissa <span class="bld">tthi</span>ttaṃ hoti, dhātvantassa lopo ca.</p>\r\n<p class="bodytext">Atthi.</p>\r\n<p class="bodytext"><a name="para495"></a><span class="paranum">495</span>, 500. <span class="bld">Tussa tthuttaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>iccetāya dhātuyā <span class="bld">tu</span>ssa vibhattissa <span class="bld">tthu</span>ttaṃ hoti, dhātvantassa lopo ca.</p>\r\n<p class="bodytext">Atthu.</p>\r\n<p class="bodytext"><a name="para496"></a><span class="paranum">496</span>, 497. <span class="bld">Simhi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>sseva dhātussa <span class="bld">si</span>mhi vibhattimhi antassa lopo ca hoti.</p>\r\n<p class="bodytext">Ko nu tvamasi mārisa.</p>\r\n<p class="bodytext"><a name="para497"></a><span class="paranum">497</span>, 477. </p>','\r',char(13)),'\n',char(10)),'-493-494-495-496-497-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',245,replace(replace('<p class="bodytext"><span class="bld">Labhasmā</span> <a name="M0.0245"></a> <span class="bld">ī iṃnaṃ ttha tthaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Labha</span>iccetāya dhātuyā ī iṃnaṃ vibhattīnaṃ <span class="bld">ttha tthaṃ</span>ādesā honti, dhātvantassa lopo ca.</p>\r\n<p class="bodytext">Alattha, alatthaṃ.</p>\r\n<p class="bodytext"><a name="para498"></a><span class="paranum">498</span>, 480. <span class="bld">Kusasmā dī cchi</span>.</p>\r\n<p class="bodytext"><span class="bld">Kusa</span>iccetāya dhātuyā <span class="bld">ī</span>vibhattissa <span class="bld">cchi</span>hoti, dhātvantassa lopo ca.</p>\r\n<p class="bodytext">Akkocchi.</p>\r\n<p class="bodytext"><a name="para499"></a><span class="paranum">499</span>, 507. <span class="bld">Dādhātussa dajjaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Dā</span>iccetassa dhātussa sabbassa <span class="bld">dajjā</span>deso hoti vā.</p>\r\n<p class="bodytext">Dajjāmi, dajjeyya, dadāmi, dadeyya.</p>\r\n<p class="bodytext"><a name="para500"></a><span class="paranum">500</span>, 486. <span class="bld">Vadassa vajjaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Vada</span>iccetassa dhātussa sabbassa <span class="bld">vajjā</span>deso hoti vā.</p>\r\n<p class="bodytext">Vajjāmi, vajjeyya, vadāmi, vadeyya.</p>\r\n<p class="bodytext"><a name="para501"></a><span class="paranum">501</span>, 443. </p>','\r',char(13)),'\n',char(10)),'-498-499-500-501-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',246,replace(replace('<p class="bodytext"><span class="bld">Gamissa</span> <a name="M0.0246"></a> <span class="bld">ghammaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Gamu</span>iccetassa dhātussa sabbassa <span class="bld">ghammā</span>deso hoti vā.</p>\r\n<p class="bodytext">Ghammatu. Ghammāhi, ghammāmi.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Gacchatu, gacchāhi, gacchāmi.</p>\r\n<p class="bodytext"><a name="para502"></a><span class="paranum">502</span>, 493. <span class="bld">Yamhi dā dhā mā ṭhā hā pā mahamathādīnamī</span>.</p>\r\n<p class="bodytext"><span class="bld">Ya</span>mhi paccaye pare <span class="bld">dā dhā mā ṭhā hā pā maha matha</span> iccevamādīnaṃ dhātūnaṃ anto <span class="bld">ī</span>kāramāpajjate.</p>\r\n<p class="bodytext">Dīyati, dhīyati, mīyati, ṭhīyati, hīyati, pīyati, mahīyati, mathīyati.</p>\r\n<p class="bodytext"><a name="para503"></a><span class="paranum">503</span>, 485. <span class="bld">Yajassādissi</span>.</p>\r\n<p class="bodytext"><span class="bld">Yaja</span>iccetassa dhātussa ādissa <span class="bld">i</span>kārādeso hoti <span class="bld">ya</span>paccaye pare.</p>\r\n<p class="bodytext">Ijjate mayā buddho.</p>\r\n<p class="bodytext"><a name="para504"></a><span class="paranum">504</span>, 470. <span class="bld">Sabbato uṃ iṃsu</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi uṃvibhattissa iṃsuādeso hoti.</p>','\r',char(13)),'\n',char(10)),'-502-503-504-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',247,replace(replace('<p class="bodytext">Upasaṅkamiṃsu<a name="M0.0247"></a>, nisīdiṃsu.</p>\r\n<p class="bodytext"><a name="para505"></a><span class="paranum">505</span>, 482. <span class="bld">Jara marānaṃ jīra jiyya miyyā vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Jara mara</span> iccetesaṃ dhātūnaṃ <span class="bld">jīra jiyya miyyā</span>desā honti vā.</p>\r\n<p class="bodytext">Jīrati, jīranti, jiyyati, jiyyanti, miyyati, miyyanti, marati, maranti.</p>\r\n<p class="bodytext"><a name="para506"></a><span class="paranum">506</span>, 496. <span class="bld">Sabbatthā’ sassādilopo ca</span>.</p>\r\n<p class="bodytext">Sabbattha vibhattipaccayesu <span class="bld">asa</span>iccetassa dhātussa ādissa lopo hoti vā.</p>\r\n<p class="bodytext">Siyā, santi, sante, samāno.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Asi.</p>\r\n<p class="bodytext"><a name="para507"></a><span class="paranum">507</span>, 501. <span class="bld">Asabbadhātuke bhū</span>.</p>\r\n<p class="bodytext"><span class="bld">Asa</span>sseva dhātussa <span class="bld">bhū</span>hoti vā asabbadhātuke.</p>\r\n<p class="bodytext">Bhavissati. Bhavissanti.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Āsuṃ.</p>\r\n<p class="bodytext"><a name="para508"></a><span class="paranum">508</span>, 515. </p>','\r',char(13)),'\n',char(10)),'-505-506-507-508-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',248,replace(replace('<p class="bodytext"><span class="bld">Eyyassa</span> <a name="M0.0248"></a> <span class="bld">ñāto iyā ñā</span>.</p>\r\n<p class="bodytext"><span class="bld">Eyya</span>ssa vibhattissa <span class="bld">ñā</span>iccetāya dhātuyā parassa <span class="bld">iyā ñā</span>ādesā honti vā.</p>\r\n<p class="bodytext">Jāniyā, jaññā.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Jāneyya.</p>\r\n<p class="bodytext"><a name="para509"></a><span class="paranum">509</span>, 516. <span class="bld">Nāssa lopo yakārattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ñā</span>iccetāya dhātuyā parassa <span class="bld">nā</span>paccayassa lopo hoti vā, <span class="bld">ya</span>kārattañca.</p>\r\n<p class="bodytext">Jaññā, nāyati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Jānāti.</p>\r\n<p class="bodytext"><a name="para510"></a><span class="paranum">510</span>, 487. <span class="bld">Lopañcettamakāro</span>.</p>\r\n<p class="bodytext"><span class="bld">A</span>kārapaccayo lopamāpajjate, ettañca hoti vā.</p>\r\n<p class="bodytext">Vajjemi, vademi, vajjāmi, vadāmi.</p>\r\n<p class="bodytext"><a name="para511"></a><span class="paranum">511</span>, 521. <span class="bld">Uttamokāro</span>.</p>\r\n<p class="bodytext"><span class="bld">O</span>kārapaccayo uttamāpajjate vā.</p>\r\n<p class="bodytext">Kurute, karoti.</p>','\r',char(13)),'\n',char(10)),'-509-510-511-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',249,replace(replace('<p class="bodytext"><span class="bld">Okāro</span>ti <a name="M0.0249"></a> kimatthaṃ? Hoti.</p>\r\n<p class="bodytext"><a name="para512"></a><span class="paranum">512</span>, 522. <span class="bld">Karassākāro ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetassa dhātussa <span class="bld">a</span>kāro <span class="bld">u</span>ttamāpajjate vā.</p>\r\n<p class="bodytext">Kurute, taroti, tubbanti, kayirati.</p>\r\n<p class="bodytext"><span class="bld">Karasse</span>ti kimatthaṃ? Sarati, marati.</p>\r\n<p class="bodytext"><a name="para513"></a><span class="paranum">513</span>, 435. <span class="bld">O ava sare</span>.</p>\r\n<p class="bodytext"><span class="bld">O</span>kārassa dhātvantassa sare pare <span class="bld">avā</span>deso hoti.</p>\r\n<p class="bodytext">Cavati. Bhavati.</p>\r\n<p class="bodytext"><span class="bld">O</span>ti kimatthaṃ? Jayati.</p>\r\n<p class="bodytext"><a name="para514"></a><span class="paranum">514</span>, 491. <span class="bld">E aya</span>.</p>\r\n<p class="bodytext"><span class="bld">E</span>kārassa dhātvantassa sare pare <span class="bld">ayā</span>deso hoti.</p>\r\n<p class="bodytext">Nayati, jayati.</p>\r\n<p class="bodytext"><a name="para515"></a><span class="paranum">515</span>, 541. </p>','\r',char(13)),'\n',char(10)),'-512-513-514-515-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',250,replace(replace('<p class="bodytext"><span class="bld">Te</span> <a name="M0.0250"></a> <span class="bld">āvāyā kārite</span>.</p>\r\n<p class="bodytext">Te <span class="bld">o e</span>iccete <span class="bld">āva āyā</span>dese pāpuṇanti kārite.</p>\r\n<p class="bodytext">Lāveti, nāyeti.</p>\r\n<p class="bodytext"><span class="bld">Yogavibhāgena</span> aññasmimpi <span class="bld">e</span>kārassa <span class="bld">āyā</span>deso hoti. Gāyati, gāyanti.</p>\r\n<p class="bodytext"><a name="para516"></a><span class="paranum">516</span>, 466. <span class="bld">Ikārāgamo asabbadhātukamhi</span>.</p>\r\n<p class="bodytext">Sabbasmiṃ asabbadhātukamhi <span class="bld">i</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Gamissati, karissati, labhissati, pacissati.</p>\r\n<p class="bodytext"><span class="bld">Asabbadhātukamhī</span>ti kimatthaṃ? Gacchati, karoti, labhati, pacati.</p>\r\n<p class="bodytext"><a name="para517"></a><span class="paranum">517</span>, 488. <span class="bld">Kvaci dhātuvibhattipaccayānaṃ dīgha viparītādesalopāgamā ca</span>.</p>\r\n<p class="bodytext">Idha ākhyāte aniddiṭṭhesu sādhanesu kvaci dhātuvibhattipaccayānaṃ dīghaviparītādesalopāgamaiccetāni kāriyāni jinavacanānurūpāni kātabbāni.</p>','\r',char(13)),'\n',char(10)),'-516-517-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',251,replace(replace('<p class="bodytext">Jāyati<a name="M0.0251"></a>, kareyya, jāniyā, siyā, kare, gacche, jaññā, vakkhetha, dakkhetha, dicchati, agacchi, agacchuṃ, ahosi, ahesuṃ iccevamādīni aññānipi sādhanāni yojetabbāni.</p>\r\n<p class="bodytext"><a name="para518"></a><span class="paranum">518</span>, 446. <span class="bld">Attanopadāni parassapadattaṃ</span>.</p>\r\n<p class="bodytext">Attanopadāni kvaci parassapadattamāpajjante.</p>\r\n<p class="bodytext">Vuccati, labbhati, paccati.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Karīyate, labbhate, paccate.</p>\r\n<p class="bodytext"><a name="para519"></a><span class="paranum">519</span>, 457. <span class="bld">Akārāgamo hiyyattanīajjatanīkālātipattīsu</span>.</p>\r\n<p class="bodytext">Kvaci <span class="bld">a</span>kārāgamo hoti <span class="bld">hiyyattanī ajjatanīkālātipatti</span>iccetāsu vibhattīsu.</p>\r\n<p class="bodytext">Agamā, agamī, agamissā.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Gamā, gamī, gamissā.</p>\r\n<p class="bodytext"><a name="para520"></a><span class="paranum">520</span>, 502. <span class="bld">Brūto ī timhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Brū</span>iccetāya dhātuyā <span class="bld">ī</span>kārāgamo hoti <span class="bld">ti</span>mhi vibhattimhi.</p>\r\n<p class="bodytext">Bravīti.</p>\r\n<p class="bodytext"><a name="para521"></a><span class="paranum">521</span>, 425. </p>','\r',char(13)),'\n',char(10)),'-518-519-520-521-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',252,replace(replace('<p class="bodytext"><span class="bld">Dhātussanto</span> <a name="M0.0252"></a> <span class="bld">lopo’nekasarassa</span>.</p>\r\n<p class="bodytext">Dhātussa anto kvaci lopo hoti anekasarassa.</p>\r\n<p class="bodytext">Gacchati, sarati, marati.</p>\r\n<p class="bodytext"><span class="bld">Anekasarasse</span>ti kimatthaṃ? Pāti, yāti, vāti.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Mahīyati, mathīyati.</p>\r\n<p class="bodytext"><a name="para522"></a><span class="paranum">522</span>, 476. <span class="bld">Isuyamūnamanto ccho vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Isu yamu i</span>ccetesaṃ dhātūnaṃ anto <span class="bld">ccho</span> hoti vā. Icchati, niyacchati.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Esati, niyamati.</p>\r\n<p class="bodytext"><a name="para523"></a><span class="paranum">523</span>, 526. <span class="bld">Kāritānaṃ ṇo lopaṃ</span>.</p>\r\n<p class="bodytext">Kāritaiccetesaṃ paccayānaṃ <span class="bld">ṇo</span> lopamāpajjate.</p>\r\n<p class="bodytext">Kāreti, kārayati, kārāpeti, kārāpayati.</p>\r\n<p class="gatha1">Sāsanatthaṃ samuddiṭṭhaṃ, mayākhyātaṃ samāsato;</p>\r\n<p class="gathalast">Sakaṃ buddhivisesena, cintayantu vicakkhaṇā.</p>\r\n<p class="centered">Iti ākhyātakappe catuttho kaṇḍo.</p>\r\n<p class="centered">Ākhyātakappo niṭṭhito.</p>\r\n<p class="chapter">7. Kibbidhānakappa</p>\r\n<p class="subhead">Paṭhamakaṇḍa</p>\r\n<p class="bodytext">(Ka)</p>','\r',char(13)),'\n',char(10)),'-522-523-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',253,replace(replace('<p class="gatha1">Buddhaṃ <a name="M0.0253"></a> ñāṇasamuddaṃ, sabbaññuṃ lokahetu’khīṇamatiṃ;</p>\r\n<p class="gathalast">Vanditvā pubbamahaṃ, vakkhāmi sasādhanaṃ hi kitakappaṃ.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Sādhanamūlaṃ hi payogaṃ,</p>\r\n<p class="gatha2">Āhu payogamūlamatthañca;</p>\r\n<p class="gatha3">Atthesu visāradamatayo,</p>\r\n<p class="gathalast">Sāsanassudharā jinassa matā.</p>\r\n<p class="bodytext">(Ga)</p>\r\n<p class="gatha1">Andho desakavikalo,</p>\r\n<p class="gatha2">Ghatamadhutelāni bhājanena vinā;</p>\r\n<p class="gatha3">Naṭṭho naṭṭhāni yathā,</p>\r\n<p class="gathalast">Payogavikalo tathā attho.</p>\r\n<p class="bodytext">(Gha)</p>\r\n<p class="gatha1">Tasmā saṃrakkhaṇatthaṃ, munivacanatthassa dullabhassāhaṃ;</p>\r\n<p class="gathalast">Vakkhāmi sissakahitaṃ, kitakappaṃ sādhanena yutaṃ.</p>\r\n<p class="bodytext"><a name="para524"></a><span class="paranum">524</span>, 561. <span class="bld">Dhātuyā kammādimhi ṇo</span>.</p>\r\n<p class="bodytext">Dhātuyā kammādimhi <span class="bld">ṇa</span>paccayo hoti.</p>\r\n<p class="bodytext">Kammaṃ karotīti kammakāro, evaṃ kumbhakāro, mālākāro, kaṭṭhakāro, rathakāro, </p>','\r',char(13)),'\n',char(10)),'-524-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',254,replace(replace('<p class="bodytext">rajatakāro<a name="M0.0254"></a>, suvaṇṇakāro, pattaggāho, tantavāyo, dhaññamāyo, dhammakāmo, dhammacāro.</p>\r\n<p class="bodytext"><a name="para525"></a><span class="paranum">525</span>, 565. <span class="bld">Saññāyama nu</span>.</p>\r\n<p class="bodytext">Saññāyamabhidheyyāyaṃ dhātuyā kammādimhi <span class="bld">a</span>kārapaccayo hoti, nāmamhi ca <span class="bld">nu</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Ariṃ dametīti arindamo, rājā. Vessaṃ taratīti vessantaro, rājā. Taṇhaṃ karotīti taṇhaṅkaro, bhagavā. Medhaṃ karotīti medhaṅkaro, bhagavā. Saraṇaṃ karotīti saraṇaṅkaro, bhagavā. Dīpaṃ karotīti dīpaṅkaro, bhagavā.</p>\r\n<p class="bodytext"><a name="para526"></a><span class="paranum">526</span>, 567. <span class="bld">Pure dadā ca iṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Pura</span>sadde ādimhi <span class="bld">dada</span>iccetāya dhātuyā <span class="bld">a</span>kārapaccayo hoti, <span class="bld">pura</span>saddassa <span class="bld">a</span>kārassa ca iṃ hoti.</p>\r\n<p class="bodytext">Pure dānaṃ adāsīti purindado devarājā.</p>\r\n<p class="bodytext"><a name="para527"></a><span class="paranum">527</span>, 568. <span class="bld">Sabbato ṇvu tvāvī vā</span>.</p>\r\n<p class="bodytext">Sabbato dhātuto kammādimhi vā akammādimhi vā <span class="bld">a</span>kāra <span class="bld">ṇvu tu āvī</span>iccete paccayā honti.</p>','\r',char(13)),'\n',char(10)),'-525-526-527-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',255,replace(replace('<p class="bodytext">Taṃ <a name="M0.0255"></a> karotīti takkaro, hitaṃ karotīti hitakaro, vineti ettha, etenāti vā vinayo nissāya naṃ vasatīti nissayo.</p>\r\n<p class="bodytext"><span class="bld">Ṇvumhi</span> – rathaṃ karotīti rathakārako, annaṃ, dadātīti annadāyako, vineti satteti vināyako, karotīti kārako, dadātīti dāyako, netīti nāyako.</p>\r\n<p class="bodytext"><span class="bld">Tumhi</span> – taṃ karotīti takkattā, tassa kattāti vā takkattā. Bhojanaṃ dadātīti bhojanadātā, bhojanassa dātāti vā bhojanadātā. Karotīti kattā. Saratīti saritā.</p>\r\n<p class="bodytext"><span class="bld">Āvīmhi</span> – bhayaṃ passatīti bhayadassāvī iccevamādi.</p>\r\n<p class="bodytext"><a name="para528"></a><span class="paranum">528</span>, 577. <span class="bld">Visa ruja padādito ṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Visa ruja pada</span>iccevamādīhi dhātūhi <span class="bld">ṇa</span> paccayo hoti.</p>\r\n<p class="bodytext">Pavisatīti paveso, rujatīti rogo, uppajjatīti uppādo, phusatīti phasso, ucatīti oko, bhavatīti bhāvo, ayatīti āyo, sammā bujjhatīti sambodho, viharatīti vihāro.</p>\r\n<p class="bodytext"><a name="para529"></a><span class="paranum">529</span>, 580. </p>','\r',char(13)),'\n',char(10)),'-528-529-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',256,replace(replace('<p class="bodytext"><span class="bld">Bhāve</span> <a name="M0.0256"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext">Bhāvatthābhidheyye sabbadhātūhi <span class="bld">ṇa</span>paccayo hoti.</p>\r\n<p class="bodytext">Paccate, pacanaṃ vā pāto, cajate, cajanaṃ vā cāgo, evaṃ yāgo, yogo, bhāgo, paridāho.</p>\r\n<p class="bodytext"><a name="para530"></a><span class="paranum">530</span>, 584. <span class="bld">Kvi ca</span>.</p>\r\n<p class="bodytext">Sabbadhātūhi <span class="bld">kvi</span>paccayo hoti.</p>\r\n<p class="bodytext">Sambhavatīti sambhū, visesena bhavatīti vibhū, bhujena gacchatīti bhujago, saṃ attānaṃ khanati, saṃ saṭṭhu khanatīti vā saṅkho.</p>\r\n<p class="bodytext"><a name="para531"></a><span class="paranum">531</span>, 589. <span class="bld">Dharādīhi rammo</span>.</p>\r\n<p class="bodytext"><span class="bld">Dhara</span>iccevamādīhi dhātūhi <span class="bld">ramma</span>paccayo hoti.</p>\r\n<p class="bodytext">Dharati tenāti dhammo, karīyate tanti kammaṃ.</p>\r\n<p class="bodytext"><a name="para532"></a><span class="paranum">532</span>, 590. <span class="bld">Tassīlādīsu ṇītvāvī ca</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi <span class="bld">tassīlā</span>dīsvatthesu <span class="bld">ṇī tu āvī</span> iccete paccayā honti.</p>\r\n<p class="bodytext">Piyaṃ pasaṃsituṃ sīlaṃ yassa rañño, so hoti rājā piyapasaṃsī, brahmaṃ carituṃ sīlaṃ yassa puggalassa </p>','\r',char(13)),'\n',char(10)),'-530-531-532-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',257,replace(replace('<p class="bodytext">so <a name="M0.0257"></a> hoti puggalo brahmacārī, pasayha pavattituṃ sīlaṃ yassa rañño, so hoti rājā pasayhapavatthā, bhayaṃ passituṃ sīlaṃ yassa samaṇassa, so hoti samaṇo bhayadassāvī iccevamādi.</p>\r\n<p class="bodytext"><a name="para533"></a><span class="paranum">533</span>, 591. <span class="bld">Sadda ku dha cala maṇḍattharudhādīhiyu</span>.</p>\r\n<p class="bodytext">Sadda kudha cala maṇḍatthehi ca <span class="bld">rucā</span>dīhi ca dhātūhi <span class="bld">yu</span>paccayo hoti <span class="bld">tassīlā</span>dīsvatthesu.</p>\r\n<p class="bodytext">Ghosanasīlo ghosano, bhāsanasīlo bhāsano. Evaṃ viggaho kātabbo. Kodhano, dosano, calano, kampano, phandano, maṇḍano, vibhūsano, rocano, jotano, vaḍḍhano.</p>\r\n<p class="bodytext"><a name="para534"></a><span class="paranum">534</span>, 562. <span class="bld">Pārādigamimhā rū</span>.</p>\r\n<p class="bodytext"><span class="bld">Gamu</span>iccetamhā dhātumhā <span class="bld">pāra</span>saddādimhā <span class="bld">rū</span>paccayo hoti <span class="bld">tassīlā</span>dīsvatthesu.</p>\r\n<p class="bodytext">Bhavassa pāraṃ bhavapāraṃ, bhavapāraṃ gantuṃ sīlaṃ yassa purisassa, so hoti puriso bhavapāragū.</p>\r\n<p class="bodytext"><span class="bld">Tassīlādīsvī</span>ti kimatthaṃ? Pāraṅgato.</p>\r\n<p class="bodytext"><span class="bld">Pārādigamimhā</span>ti kimatthaṃ? Anugāmī.</p>\r\n<p class="bodytext"><a name="para535"></a><span class="paranum">535</span>, 593. </p>','\r',char(13)),'\n',char(10)),'-533-534-535-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',258,replace(replace('<p class="bodytext"><span class="bld">Bhikkhādito</span> <a name="M0.0258"></a> <span class="bld">ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhikkha</span>iccevamādīhi dhātūhi rūpaccayo hoti <span class="bld">tassīlā</span>dīsvatthesu.</p>\r\n<p class="bodytext">Bhikkhanasīlo yācanasīlo bhikkhu, vijānanasīlo viññū.</p>\r\n<p class="bodytext"><a name="para536"></a><span class="paranum">536</span>, 594. <span class="bld">Tanatyādīnaṃ ṇuko</span>.</p>\r\n<p class="bodytext"><span class="bld">Hana</span>tyādīnaṃ dhātūnaṃ ante <span class="bld">ṇuka</span>paccayo hoti <span class="bld">tassīlā</span>dīsvatthesu.</p>\r\n<p class="bodytext">Āhananasīlo āghātuko, karaṇasīlo kāruko.</p>\r\n<p class="bodytext"><a name="para537"></a><span class="paranum">537</span>, 566. <span class="bld">Nu niggahitaṃ padante</span>.</p>\r\n<p class="bodytext">Padante <span class="bld">nu</span>kārāgamo niggahitamāpajjate.</p>\r\n<p class="bodytext">Ariṃ dametīti arindamo, rājā. Vessaṃ taratīti vessantaro, rājā. Pabhaṃ karotīti pabhaṅkaro, bhagavā.</p>\r\n<p class="bodytext"><a name="para538"></a><span class="paranum">538</span>, 595. <span class="bld">Saṃhanāññāya vā ro gho</span>.</p>\r\n<p class="bodytext"><span class="bld">Saṃ</span>pubbāya <span class="bld">hana</span>iccetāya dhātuyā, aññāya vā dhātuyā <span class="bld">ra</span>paccayo, <span class="bld">hana</span>ssa ca <span class="bld">gho</span> hoti.</p>','\r',char(13)),'\n',char(10)),'-536-537-538-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',259,replace(replace('<p class="bodytext">Samaggaṃ <a name="M0.0259"></a> kammaṃ samupagacchatīti saṅgho, samantato nagarassa māhire khaññatīti parikhā, antaṃ karotīti antako.</p>\r\n<p class="bodytext"><span class="bld">Saṃ</span>iti kimatthaṃ? Upahananaṃ upaghāto.</p>\r\n<p class="bodytext"><a name="para539"></a><span class="paranum">539</span>, 558. <span class="bld">Ramhi ranto rādino</span>.</p>\r\n<p class="bodytext"><span class="bld">Ra</span>mhi paccaye pare sabbo dhātvanto rakārādi lopo hoti.</p>\r\n<p class="bodytext">Antako, pāragū, satthā, diṭṭho iccevamādi.</p>\r\n<p class="bodytext"><a name="para540"></a><span class="paranum">540</span>, 545. <span class="bld">Bhāvakammesu tabbānīyā</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhāvakamma</span>iccetesvatthesu <span class="bld">tabba anīya</span>iccete paccayā honti sabbadhātūhi.</p>\r\n<p class="bodytext">Bhavitabbaṃ, bhavanīyaṃ, āsitabbaṃ, āsanīyaṃ, pajjitabbaṃ, pajjanīyaṃ, kattabbaṃ, karaṇīyaṃ, gantabbaṃ, gamanīyaṃ.</p>\r\n<p class="bodytext"><a name="para541"></a><span class="paranum">541</span>, 552. <span class="bld">Ṇyo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhāvakamme</span>su sabbadhātūhi ṇyapaccayo hoti.</p>\r\n<p class="bodytext">Kattabbaṃ kāriyaṃ, jetabbaṃ jeyyaṃ, netabbaṃ neyyaṃ, iccevamādi.</p>','\r',char(13)),'\n',char(10)),'-539-540-541-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',260,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇena</span> <a name="M0.0260"></a> <span class="bld">teyya</span>paccayo hoti. Ñātabbaṃ ñāteyyaṃ, daṭṭheyyaṃ, patteyyaṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para542"></a><span class="paranum">542</span>, 557. <span class="bld">Karamhā ricca</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetamhā dhātumhā <span class="bld">ricca</span>paccayo hoti <span class="bld">bhāvakamme</span>su.</p>\r\n<p class="bodytext">Kattabbaṃ kiccaṃ.</p>\r\n<p class="bodytext"><a name="para543"></a><span class="paranum">543</span>, 555. <span class="bld">Bhūto’bba</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhū</span>iccetāya dhātuyā <span class="bld">ṇya</span>paccayassa <span class="bld">ū</span>kārena saha <span class="bld">abbā</span>deso hoti <span class="bld">bhāvakamme</span>su.</p>\r\n<p class="bodytext">Bhavitabbo bhabbo, bhavitabbaṃ bhabbaṃ.</p>\r\n<p class="bodytext"><a name="para544"></a><span class="paranum">544</span>, 556. <span class="bld">Vada mada gamu yuja garahākārādīhi jja mma gga yheyyā gāro vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Vada mada gamu yuja garahā</span>kārantaiccevamādīhi dhātūhi ṇyapaccayassa yathāsaṅkhyaṃ <span class="bld">jja mma gga yha eyyā</span>desā honti vā dhātvantena saha, <span class="bld">gara</span>ssaṃ ca <span class="bld">gāro</span> hoti bhāvakammesu.</p>\r\n<p class="bodytext">Vattabbaṃ vajjaṃ, madanīyaṃ majjaṃ, gamanīyaṃ gammaṃ, yojanīyaṃ yoggaṃ, garahitabbaṃ gārayhaṃ, dātabbaṃ deyyaṃ, pātabbaṃ peyyaṃ, </p>','\r',char(13)),'\n',char(10)),'-542-543-544-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',261,replace(replace('<p class="bodytext">hātabbaṃ <a name="M0.0261"></a> heyyaṃ, mātabbaṃ meyyaṃ, ñātabbaṃ ñeyyaṃ, iccevamādi.</p>\r\n<p class="bodytext"><a name="para545"></a><span class="paranum">545</span>, 548. <span class="bld">Te kiccā</span>.</p>\r\n<p class="bodytext">Ye paccayā <span class="bld">tabbā</span>dayo <span class="bld">ricca</span>ntā, te <span class="bld">kicca</span>saññāti veditabbā.</p>\r\n<p class="bodytext"><span class="bld">Kicca</span>saññāya kiṃpayojanaṃ? Bhāvakammesu kiccattakhatthā.</p>\r\n<p class="bodytext"><a name="para546"></a><span class="paranum">546</span>, 562. <span class="bld">Aññe kita</span>.</p>\r\n<p class="bodytext">Aññe paccayā <span class="bld">kita</span> eva saññā honti.</p>\r\n<p class="bodytext"><span class="bld">Kita</span> saññāya kiṃpayojanaṃ? Kattari kita.</p>\r\n<p class="bodytext"><a name="para547"></a><span class="paranum">547</span>, 596. <span class="bld">Nandādīhi yu</span>.</p>\r\n<p class="bodytext"><span class="bld">Nandā</span>dīhi dhātūhi <span class="bld">yu</span>paccayo hoti <span class="bld">bhāvakamme</span>su.</p>\r\n<p class="bodytext">Nandīyate nandanaṃ, ninditabbaṃ vā nandanaṃ, gahaṇīyaṃ gahaṇaṃ, caritabbaṃ caraṇaṃ, evaṃ sabbattha yojetabbā.</p>\r\n<p class="bodytext"><a name="para548"></a><span class="paranum">548</span>, 597. <span class="bld">Kattukaraṇapadesesu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Kattukaraṇapadesa</span>iccetesvatthesu ca <span class="bld">yu</span>paccayo hoti.</p>','\r',char(13)),'\n',char(10)),'-545-546-547-548-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',262,replace(replace('<p class="bodytext"><span class="bld">Kattari</span> <a name="M0.0262"></a> tāva – rajaṃ haratīti rajoharaṇaṃ toyaṃ.</p>\r\n<p class="bodytext"><span class="bld">Karaṇe</span> tāva – karoti tenāti karaṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Padese</span> tāva – tiṭṭhanti tasminti ṭhānaṃ. Evaṃ sabbattha yojetabbā.</p>\r\n<p class="bodytext"><a name="para549"></a><span class="paranum">549</span>, 550. <span class="bld">Rahādito ṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Ra</span>kāra<span class="bld">ha</span>kārādyantehi dhātūhi <span class="bld">anā</span>desassa <span class="bld">na</span>ssa <span class="bld">ṇo</span> hoti.</p>\r\n<p class="bodytext">Karoti tenāti karaṇaṃ, pūreti tenāti pūraṇaṃ. Gahaṇīyaṃ tenāti gahaṇaṃ. Evamaññepi yojetabbā.</p>\r\n<p class="centered">Iti kibbidhānakappe paṭhamo kaṇḍo.</p>\r\n<p class="subhead">Dutiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para550"></a><span class="paranum">550</span>, 546. <span class="bld">Ṇādayo tekālikā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇā</span>dayo paccayā <span class="bld">yu</span>paccayantā <span class="bld">tekālikā</span>ti veditabbā.</p>\r\n<p class="bodytext">Kumbhaṃ karoti akāsi karissatīti kumbhakāro, karoti akāsi karissati tenāti karaṇaṃ. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para551"></a><span class="paranum">551</span>, 598. </p>','\r',char(13)),'\n',char(10)),'-549-550-551-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',263,replace(replace('<p class="bodytext"><span class="bld">Saññāyaṃ</span> <a name="M0.0263"></a> <span class="bld">dā dhāto i</span>.</p>\r\n<p class="bodytext">Saññāyamabhidheyyāyaṃ <span class="bld">dā dhā</span>to <span class="bld">i</span>paccayo hoti.</p>\r\n<p class="bodytext">Paṭhamaṃ ādīyatīti ādi, udakaṃ dadhātīti udami, mahodakāni dadhātīti mahodadhi, vālāni dadhāti tasminti vāladhi, sammā dhīyatīti sandhi.</p>\r\n<p class="bodytext"><a name="para552"></a><span class="paranum">552</span>, 609. <span class="bld">Ti kita cāsiṭṭhe</span>.</p>\r\n<p class="bodytext">Saññāyamabhidheyyāyaṃ sabbadhātūhi tipaccayo hoti, <span class="bld">kita</span> ca āsiṭṭhe.</p>\r\n<p class="bodytext">Jino janaṃ bujjhatūti jinabuddhi, dhanaṃ assa bhavatūti dhanabhūti, bhavatūti bhūto, bhavatūti bhāvo, dhammo janaṃ dadātūti dhammadinno, vaḍḍhatūti vaḍḍhamāno. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para553"></a><span class="paranum">553</span>, 599. <span class="bld">Itthiyamatiyavo vā</span>.</p>\r\n<p class="bodytext">Itthiyamabhidheyyāyaṃ sabbadhātūhi <span class="bld">a</span>kāra <span class="bld">ti yu</span> iccete paccayā honti vā.</p>\r\n<p class="bodytext">Jīratītī jarā, maññatīti mati, cetayatīti cetanā, vedayatīti vedanā. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para554"></a><span class="paranum">554</span>, 601. </p>','\r',char(13)),'\n',char(10)),'-552-553-554-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',264,replace(replace('<p class="bodytext"><span class="bld">Karato</span> <a name="M0.0264"></a> <span class="bld">ririya</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>to itthiyamanitthiyaṃ vā abhikheyyāyaṃ <span class="bld">rirīya</span>paccayo hoti vā.</p>\r\n<p class="bodytext">Kattabbā kiriyā, karaṇīyaṃ kiriyaṃ.</p>\r\n<p class="bodytext"><a name="para555"></a><span class="paranum">555</span>, 612. <span class="bld">Atīte tatavantutāvī</span>.</p>\r\n<p class="bodytext">Atīte kāle sabbadhātūhi <span class="bld">tatavantutāvī</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Huto, hutavā, hutāvī. Vusito, vusitavā, vusitāvī. Bhutto, bhuttavā, bhuttāvī.</p>\r\n<p class="bodytext"><a name="para556"></a><span class="paranum">556</span>, 622. <span class="bld">Bhāvakammesu ta</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhāvakamme</span>su atīte kāle <span class="bld">ta</span>paccayo hoti sabbadhātūhi.</p>\r\n<p class="bodytext"><span class="bld">Bhāve</span> tāva – tassa gītaṃ, naccaṃ, naṭṭaṃ, hasitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kammani</span> tāva – tena bhāsitaṃ, desitaṃ.</p>\r\n<p class="bodytext"><a name="para557"></a><span class="paranum">557</span>, 606. <span class="bld">Budhagamāditthe kattari</span>.</p>\r\n<p class="bodytext"><span class="bld">Budhagamu</span>iccevamādīhi dhātūhi tadatthe gamyamāne <span class="bld">ta</span>paccayo hoti kattari sabbakāle.</p>','\r',char(13)),'\n',char(10)),'-555-556-557-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',265,replace(replace('<p class="bodytext">Sabbe <a name="M0.0265"></a> saṅkhatāsaṅkhate dhamme bujjhati abujjhi bujjhissatīti buddho, saraṇaṅgato, samathaṅgato, amathaṅgato, jānāti ajāni jānissatīti ñāto, iccevamādi.</p>\r\n<p class="bodytext"><a name="para558"></a><span class="paranum">558</span>, 602. <span class="bld">Jito ina sabbattha</span>.</p>\r\n<p class="bodytext"><span class="bld">Ji</span>iccetāya dhātuyā <span class="bld">ina</span>paccayo hoti sabbakāle kattari.</p>\r\n<p class="bodytext">Pāpake akusale dhamme jināti ajini jinissatīti jino.</p>\r\n<p class="bodytext"><a name="para559"></a><span class="paranum">559</span>, 603. <span class="bld">Supato ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Supa</span>iccetāya dhātuyā <span class="bld">ina</span>paccayo hoti kattari, bhāve ca.</p>\r\n<p class="bodytext">Supatīti supinaṃ, supīyate supinaṃ.</p>\r\n<p class="bodytext"><a name="para560"></a><span class="paranum">560</span>, 604. <span class="bld">Īsaṃdusūhi kha</span>.</p>\r\n<p class="bodytext"><span class="bld">Īsaṃdusu</span>saddādīhi sabbadhātūhi <span class="bld">kha</span>paccayo hoti.</p>\r\n<p class="bodytext">Īsassayo, dussayo, sussayo bhavatā, īsakkaraṃ, dukkaraṃ, sukaraṃ, bhavatā.</p>\r\n<p class="bodytext"><a name="para561"></a><span class="paranum">561</span>, 636. </p>','\r',char(13)),'\n',char(10)),'-558-559-560-561-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',266,replace(replace('<p class="bodytext"><span class="bld">Icchatthesu</span> <a name="M0.0266"></a> <span class="bld">samānakattukesu tave tuṃ vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Icchatthe</span>su samānakattukesu sabbadhātūhi<span class="bld">tavetuṃ</span>iccete paccayā honti sabbakāle kattari.</p>\r\n<p class="bodytext">Puññāni kātave, saddhammaṃ sotu micchati.</p>\r\n<p class="bodytext"><a name="para562"></a><span class="paranum">562</span>, 638. <span class="bld">Arahasakkādīsu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Arahasakkādī</span>su ca atthesu sabbadhātūhi <span class="bld">tuṃ</span>paccayo hoti.</p>\r\n<p class="bodytext">Ko taṃ ninditumarahati, sakkā jetuṃ dhanena vā. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para563"></a><span class="paranum">563</span>, 639. <span class="bld">Pattavacane alamatthesu ca</span>.</p>\r\n<p class="bodytext">Pattavacane <span class="bld">alamatthe</span>su sabbadhātūhi <span class="bld">tuṃ</span>paccayo hoti.</p>\r\n<p class="bodytext">Alameva dānāni dātuṃ, alameva puññāni kātuṃ.</p>\r\n<p class="bodytext"><a name="para564"></a><span class="paranum">564</span>, 640. <span class="bld">Pubbakāle’ kakattukānaṃ tuna tvāna tvāvā</span>.</p>\r\n<p class="bodytext">Pubbakāle ekakattukānaṃ dhātūnaṃ <span class="bld">tunatvāna tvā</span>iccete paccayā honti vā.</p>','\r',char(13)),'\n',char(10)),'-562-563-564-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',267,replace(replace('<p class="bodytext">Kātuna <a name="M0.0267"></a> kammaṃ gacchati, akātuna puññaṃ kilissati, sattā sutvāna dhammaṃ modanti, ripuṃ jitvāna vasati, dhammaṃ sutvāna’ssa etadahosi, ito sutvāna amutra kathayanti, sutvā jānissāma. Evaṃ sabbattha yojetabbā.</p>\r\n<p class="bodytext"><a name="para565"></a><span class="paranum">565</span>, 646. <span class="bld">Vattamāne mānantā</span>.</p>\r\n<p class="bodytext"><span class="bld">Vattamāne</span> kāle sabbadhātūhi <span class="bld">mānaanta</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Saramāno rodati. Gacchanto gaṇhāti.</p>\r\n<p class="bodytext"><a name="para566"></a><span class="paranum">566</span>, 574. <span class="bld">Sāsādīhi ratthu</span>.</p>\r\n<p class="bodytext"><span class="bld">Sāsa</span>iccevamādīhi dhātūhi <span class="bld">ratthu</span>paccayo hoti.</p>\r\n<p class="bodytext">Sāsatīti satthā, sāsati hiṃsatīti vā satthā.</p>\r\n<p class="bodytext"><a name="para567"></a><span class="paranum">567</span>, 575. <span class="bld">Pātitoritu</span>.</p>\r\n<p class="bodytext"><span class="bld">Pā</span>iccetāya dhātuyā <span class="bld">ritu</span>paccayo hoti.</p>\r\n<p class="bodytext">Pāti puttanti pitā.</p>\r\n<p class="bodytext"><a name="para568"></a><span class="paranum">568</span>, 576. </p>','\r',char(13)),'\n',char(10)),'-565-566-567-568-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',268,replace(replace('<p class="bodytext"><span class="bld">Mānādīhi</span> <a name="M0.0268"></a> <span class="bld">rātu</span>.</p>\r\n<p class="bodytext"><span class="bld">Māna</span>iccevamādīhi dhātūhi <span class="bld">rātu</span>paccayo hoti, <span class="bld">ritu</span> paccayo ca.</p>\r\n<p class="bodytext">Dhammena puttaṃ mānetīti mātā, pubbe bhāsatīti bhātā, mātāpitūhi dhārīyatīti dhītā.</p>\r\n<p class="bodytext"><a name="para569"></a><span class="paranum">569</span>, 610. <span class="bld">Āgamā tuko</span>.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>iccādimhā <span class="bld">gami</span>to <span class="bld">tuka</span>paccayo hoti.</p>\r\n<p class="bodytext">Āgacchatīti āgantuko, bhikkhu.</p>\r\n<p class="bodytext"><a name="para570"></a><span class="paranum">570</span>, 611. <span class="bld">Bhabbe ika</span>.</p>\r\n<p class="bodytext"><span class="bld">Gamu</span>iccetamhā dhātumhā <span class="bld">ika</span>paccayo hoti bhabbe. Gamissati gantuṃ bhabboti gamiko, bhikkhu.</p>\r\n<p class="centered">Iti kibbidhānakappe dutiyo kaṇḍo.</p>\r\n<p class="subhead">Tatiyakaṇḍa</p>\r\n<p class="bodytext"><a name="para571"></a><span class="paranum">571</span>, 624. <span class="bld">Paccayādaniṭṭhā nipātanā sijjhanti</span>,</p>\r\n<p class="bodytext"><span class="bld">Saṅkhyānāmasamāsataddhitākhyātakitakappamhi</span> sappaccayā ye saddā aniṭṭhaṅgatā, te sādhanena nirakkhitvā sakehi sakehi nāmehi nipātanā sijjhanti.</p>','\r',char(13)),'\n',char(10)),'-569-570-571-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',269,replace(replace('<p class="bodytext"><span class="bld">Saṅkhyāyaṃ</span> <a name="M0.0269"></a> tāva – <span class="bld">eka</span>ssa <span class="bld">etā</span> hoti, <span class="bld">dasa</span>ssa ca <span class="bld">da</span>kārassa <span class="bld">ra</span>kārādeso hoti. Eko ca dasa ca ekārasa.</p>\r\n<p class="bodytext"><span class="bld">Dvi</span>ssa <span class="bld">bā</span> hoti, <span class="bld">dasa</span>ssa ca <span class="bld">da</span>kārassa <span class="bld">ra</span>kārādeso hoti, dve ca dasa ca bārasa.</p>\r\n<p class="bodytext"><span class="bld">Dvi</span>ssa <span class="bld">bā</span> hoti, <span class="bld">dasa</span>ssa ca <span class="bld">vīsaṃ</span> hoti. Dve ca vīsañca bāvīsaṃ.</p>\r\n<p class="bodytext"><span class="bld">Cha</span>ssa <span class="bld">so</span> hoti, <span class="bld">dasa</span>ssa ca <span class="bld">da</span>kārassa <span class="bld">ḷo</span> hoti, cha ca dasa ca soḷasa.</p>\r\n<p class="bodytext">Chaāyatanamhi <span class="bld">cha</span>ssa <span class="bld">saḷo</span> hoti, saḷāyatanaṃ. Evaṃ sesā saṅkhyā kātabbā.</p>\r\n<p class="bodytext"><span class="bld">Nāmike</span> tāva-<span class="bld">ima samāna apara</span>iccetehi jjajju paccayā honti, <span class="bld">ima samāna</span>saddānañca <span class="bld">a</span>kāra<span class="bld">sa</span>kārādesā honti. Imasmiṃ kāle ajja, ajju, samāne kāle sajja, sajju, aparasmiṃ kāle aparajja, aparajju.</p>\r\n<p class="bodytext"><span class="bld">Samāse</span> tāva – bhūmigato, apāyagato, issarakataṃ. Sallaviddho, kathinadussaṃ, corabhayaṃ, dhaññarāsi, saṃsāradukkhaṃ, pubbāparaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',270,replace(replace('<p class="bodytext"><span class="bld">Taddhite</span> <a name="M0.0270"></a> tāva – vāsiṭṭho, bhāradvājo, bhaggavo, paṇḍavo, kāleyyo.</p>\r\n<p class="bodytext"><span class="bld">Ākhyāte</span> tāva – ‘‘asa bhāve’’ti dhātuto vattamānesu ekavacanabahuvacanesu ekavacanassa <span class="bld">ti</span>ssa <span class="bld">sso</span> hoti antena saha, bahuvacanassa <span class="bld">anti</span>ssa <span class="bld">ssu</span> hoti antena saha. Evamassa vacanīyo, evamassu vacanīyā.</p>\r\n<p class="bodytext">Āṇattiyaṃ <span class="bld">hi</span>ssa <span class="bld">ssu</span> hoti vā, gacchassu, gacchāhi.</p>\r\n<p class="bodytext"><span class="bld">Kitake</span> tāva – <span class="bld">vada hana</span>iccevamādīhi dhātūhi <span class="bld">ka</span>paccayo hoti, <span class="bld">vada</span>ssa ca <span class="bld">vādo</span> hoti, <span class="bld">hana</span>ssa ca <span class="bld">ghāto</span> hoti. Vādako, ghātako.</p>\r\n<p class="bodytext"><span class="bld">Naṭa</span>dhātuto <span class="bld">ta</span>pañcayassa <span class="bld">cca ṭṭā</span>desā honti antena saha. Naccaṃ, naṭṭaṃ. Iccevamādayo nipātanā sijjhanti.</p>\r\n<p class="bodytext"><a name="para572"></a><span class="paranum">572</span>, 625. <span class="bld">Sāsa disato tassa riṭṭho ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Sāsa disa</span>iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">riṭṭhā</span>deso hoti ṭhāne.</p>\r\n<p class="bodytext">Anusiṭṭho so mayā, diṭṭhaṃ me rūpaṃ.</p>','\r',char(13)),'\n',char(10)),'-572-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',271,replace(replace('<p class="bodytext"><span class="bld">Caggahaṇena</span> <a name="M0.0271"></a> kiccatakārassa ca <span class="bld">tuṃ</span> paccayassa <span class="bld">ca raṭṭharaṭṭhuṃ</span>ādesā honti. Dassanīyaṃ daṭṭhabbaṃ. Daṭṭhuṃ vihāraṃ gacchanti samaṇānaṃ.</p>\r\n<p class="bodytext"><a name="para573"></a><span class="paranum">573</span>, 626. <span class="bld">Sādisanta puccha bhanja ha</span>nsā<span class="bld">dīhiṭṭho</span>.</p>\r\n<p class="bodytext"><span class="bld">Sa</span>kāranta <span class="bld">puccha bha</span>nja <span class="bld">ha</span>nsa iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa sahādibyañjanena <span class="bld">ṭṭhā</span>deso hoti ṭhāne.</p>\r\n<p class="bodytext">Tuṭṭho, ahinā daṭṭho naro, mayā puṭṭho, bhaṭṭho, pabhaṭṭho, haṭṭho, pahaṭṭho, yiṭṭho. Evamaññepi dhātavo sabbattha yojetabbā.</p>\r\n<p class="bodytext"><a name="para574"></a><span class="paranum">574</span>, 613. <span class="bld">Vasato uṭṭha</span>.</p>\r\n<p class="bodytext"><span class="bld">Vasa</span>iccetamhā dhātumhā <span class="bld">ta</span>kārapaccayassa sahādibyañjanena <span class="bld">uṭṭhā</span>deso hoti ṭhāne.</p>\r\n<p class="bodytext">Vassaṃvuṭṭho.</p>\r\n<p class="bodytext"><a name="para575"></a><span class="paranum">575</span>, 614. <span class="bld">Vassa vā vu</span>.</p>\r\n<p class="bodytext"><span class="bld">Vasa</span>sseva dhātussa <span class="bld">ta</span>paccaye pare <span class="bld">va</span>kārassa <span class="bld">u</span>kārādeso hoti vā.</p>\r\n<p class="bodytext">Vusitaṃ brahmacariyaṃ, uṭṭho. Vuṭho vā.</p>\r\n<p class="bodytext"><a name="para576"></a><span class="paranum">576</span>, 607. </p>','\r',char(13)),'\n',char(10)),'-573-574-575-576-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',272,replace(replace('<p class="bodytext"><span class="bld">Dha</span> <a name="M0.0272"></a> <span class="bld">ḍha bha ye hi dha ḍhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Dha ḍha bha ha</span>iccevamantehi dhātūhi <span class="bld">ta</span>kārapaccayassa yathākkamaṃ <span class="bld">dha ḍhā</span>desā honti.</p>\r\n<p class="bodytext">Yathā? Buddho bhagavā, vaḍḍho bhikkhu, laddhaṃ me patthacīvaraṃ, agginā daḍḍhaṃ vanaṃ.</p>\r\n<p class="bodytext"><a name="para577"></a><span class="paranum">577</span>, 628. <span class="bld">Bhanjato gvo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Bha</span>njato dhātumhā <span class="bld">ta</span>kārapaccayassa <span class="bld">ggo</span> ādeso hoti sahādibyañjanena.</p>\r\n<p class="bodytext">Bhaggo.</p>\r\n<p class="bodytext"><a name="para578"></a><span class="paranum">578</span>, 560. <span class="bld">Bhujādīnamanto no dvi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhuja</span>iccevamādīnaṃ dhātūnaṃ anto no hoti, <span class="bld">ta</span>paccayassa <span class="bld">ca</span> dvitāvo hoti.</p>\r\n<p class="bodytext">Bhutto, bhuttāvī, catto, satto, ratto, yutto, vivitto.</p>\r\n<p class="bodytext"><a name="para579"></a><span class="paranum">579</span>, 629. <span class="bld">Vaca vāvu</span>.</p>','\r',char(13)),'\n',char(10)),'-577-578-579-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',273,replace(replace('<p class="bodytext"><span class="bld">Vaca</span>iccetassa <a name="M0.0273"></a> dhātvassa vakārassa <span class="bld">u</span>kārādeso hoti anto <span class="bld">ca</span>kāro no hoti, <span class="bld">ta</span>paccayassa ca dvebhāvo hoti vā.</p>\r\n<p class="bodytext">Vuttaṃ bhagavatā, uttaṃ vā.</p>\r\n<p class="bodytext"><a name="para580"></a><span class="paranum">580</span>, 630. <span class="bld">Gupādīnañca</span>.</p>\r\n<p class="bodytext"><span class="bld">Gupa</span>iccevamādīnaṃ dhātūnaṃ anto ca byañjano no hoti, <span class="bld">ta</span>paccayassa ca dvebhāvo hoti.</p>\r\n<p class="bodytext">Sugutto, catto, litto, santatto, utto, vivitto, sitto. Evamaññepi yojetabbā.</p>\r\n<p class="bodytext"><a name="para581"></a><span class="paranum">581</span>, 616. <span class="bld">Tarādīhi iṇṇo</span>.</p>\r\n<p class="bodytext"><span class="bld">Tara</span>iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">iṇṇā</span>deso hoti, anto ca byañjano no hoti.</p>\r\n<p class="bodytext">Taratīti tiṇṇo, uttaratīti uttiṇṇo, saṃpūratīti sampuṇṇo, turatīti tuṇṇo, parijīratīti parijiṇṇo, ākiratīti ākiṇṇo.</p>\r\n<p class="bodytext"><a name="para582"></a><span class="paranum">582</span>, 631. </p>','\r',char(13)),'\n',char(10)),'-580-581-582-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',274,replace(replace('<p class="bodytext"><span class="bld">Bhidādito</span> <a name="M0.0274"></a> <span class="bld">inna anna īṇāvā</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhidi</span>iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">inna anna īṇā</span>desā honti vā, anto ca byañjano no hoti.</p>\r\n<p class="bodytext">Bhinditabboti bhinno, chindīyatīti chinno, ucchindīyitthāti ucchinno, dīyatīti dinno, nisīdatīti nisinno, suṭṭhu chādīyatīti suchanno, khidatīti khinno, rodatīti runno, khīṇā jāti.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Bhijjatīti bhitti.</p>\r\n<p class="bodytext"><a name="para583"></a><span class="paranum">583</span>, 617. <span class="bld">Susa paca sakato kkha kkā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Susa paca saka</span>iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">kkhakkā</span>desā honti, anto ca byañjano no hoti.</p>\r\n<p class="bodytext">Sussatīti sukkhaṃ, kaṭṭhaṃ, paccatīti pakkaṃ, phalaṃ. Sakati samattheti, pūjetīti vā sakko, sujampati.</p>\r\n<p class="bodytext"><a name="para584"></a><span class="paranum">584</span>, 618. <span class="bld">Pakkamādīhi nto ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Pakkama</span>iccevamādīhi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">nto</span>ādeso hoti, anto ca no hoti.</p>','\r',char(13)),'\n',char(10)),'-583-584-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',275,replace(replace('<p class="bodytext">Pakkamatīti <a name="M0.0275"></a> pakkanto, vibbhamatīti vibbhanto, saṅkanto, khanto, santo, danto, vanto.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇaṃ</span> kimatthaṃ? Teheva dhātūhi <span class="bld">ta</span>paccayassanti hoti. Anto ca no hoti. Kanti, khanti. Evaṃ sabbattha.</p>\r\n<p class="bodytext"><a name="para585"></a><span class="paranum">585</span>, 619. <span class="bld">Janādīnamā timhi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Jana</span>iccevamādīnaṃ dhātūnaṃ antassa byañjanassa <span class="bld">ā</span>ttaṃ hoti <span class="bld">ta</span>paccaye pare, <span class="bld">ti</span>mhi ca.</p>\r\n<p class="bodytext">Ajanīti jāto, jananaṃ jāti.</p>\r\n<p class="bodytext"><span class="bld">Timhī</span>ti kimatthaṃ? Aññasmimpi paccaye pare <span class="bld">ā</span>kāranivattanatthaṃ. Janitvā, janitā, janituṃ, janitabbaṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para586"></a><span class="paranum">586</span>, 600. <span class="bld">Gama khana hana ramādīnamanto</span>.</p>\r\n<p class="bodytext"><span class="bld">Gama khana hana ramu</span>iccevamādīnaṃ dhātūnaṃ anto byañjano no hoti vā <span class="bld">ta</span>paccaye pare <span class="bld">ti</span>mhi ca.</p>\r\n<p class="bodytext">Sundaraṃ nibbānaṃ gacchatīti sugato. Sundaraṃ nibbānaṃ gacchatīti sugati, khataṃ, khati. Upahataṃ, upahati. Rato, rati, mato, mati.</p>','\r',char(13)),'\n',char(10)),'-585-586-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',276,replace(replace('<p class="bodytext"><span class="bld">Vā</span>ti <a name="M0.0276"></a> kimatthaṃ? Ramato, ramati.</p>\r\n<p class="bodytext"><a name="para587"></a><span class="paranum">587</span>, 632. <span class="bld">Rakāro ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ra</span>kāro ca dhātūnamantabhūto no hoti <span class="bld">ta</span>paccaye, pare <span class="bld">ti</span>mhi ca.</p>\r\n<p class="bodytext">Pakārena karīyatīti pakato, paṭhamaṃ karīyatīti pakati, visarīyatīti visato, visati.</p>\r\n<p class="bodytext"><a name="para588"></a><span class="paranum">588</span>, 620. <span class="bld">Ṭhāpānami ī ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṭhā pā</span>iccetesaṃ dhātūnaṃ antassa <span class="bld">ā</span>kārassa <span class="bld">i</span>kāra <span class="bld">ī</span>kārādesā honti yathāsaṅkhyaṃ <span class="bld">ta</span>paccaye pare, <span class="bld">ti</span>mhi ca.</p>\r\n<p class="bodytext">Yatra ṭhito, ṭhiti, pīto, pīti.</p>\r\n<p class="bodytext"><a name="para589"></a><span class="paranum">589</span>, 621. <span class="bld">Hantehi ho hassa ḷo vā adahanahānaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ha</span>kārantehi dhātūhi <span class="bld">ta</span>paccayassa <span class="bld">ha</span>kārādeso hoti, <span class="bld">ha</span>kārassa dhātvantassa <span class="bld">ḷo</span> hoti vā <span class="bld">adahanahānaṃ</span>.</p>\r\n<p class="bodytext">Āruhitthāti āruḷho. Gāḷho, bāḷho. Mūḷo.</p>','\r',char(13)),'\n',char(10)),'-587-588-589-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',277,replace(replace('<p class="bodytext"><span class="bld">Adahanahāna</span>miti <a name="M0.0277"></a> kimatthaṃ? Dayhatīti daḍḍho, saṃsuṭṭhu nayhatīti sannaddho.</p>\r\n<p class="centered">Iti kibbidhānakappe tatiyo kaṇḍo.</p>\r\n<p class="subhead">Catutthakaṇḍa</p>\r\n<p class="bodytext"><a name="para590"></a><span class="paranum">590</span>, 579. <span class="bld">Ṇamhiranjassa jo bhāvakaraṇesu</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇa</span>mhi paccaye pare ranjaiccetassa dhātussa antabhūtassa njakārassa <span class="bld">jo</span>ādeso hoti <span class="bld">bhāvakaraṇesu</span>.</p>\r\n<p class="bodytext">Rañjanaṃ rāgo, ranjanti etenāti rāgo.</p>\r\n<p class="bodytext"><span class="bld">Bhāvakaraṇesū</span>ti kimatthaṃ? Ranjatīti raṅgo.</p>\r\n<p class="bodytext"><a name="para591"></a><span class="paranum">591</span>, 544. <span class="bld">Hanassa ghāto</span>.</p>\r\n<p class="bodytext"><span class="bld">Hana</span>iccetassa dhātussa sabbassa <span class="bld">ghātā</span>deso hoti <span class="bld">ṇa</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Upahanatīti upaghāto, gāvo hanatīti goghātako.</p>\r\n<p class="bodytext"><a name="para592"></a><span class="paranum">592</span>, 503. </p>','\r',char(13)),'\n',char(10)),'-590-591-592-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',278,replace(replace('<p class="bodytext"><span class="bld">Vadho</span> <a name="M0.0278"></a> <span class="bld">vā sabbattha</span>.</p>\r\n<p class="bodytext"><span class="bld">Hana</span>iccetassa dhātussa <span class="bld">vadhā</span>deso hoti vā sabbattha ṭhānesu.</p>\r\n<p class="bodytext">Hanatīti vadho, vadhako, avadhi, ahani vā.</p>\r\n<p class="bodytext"><a name="para593"></a><span class="paranum">593</span>, 564. <span class="bld">Ākārantānamāyo</span>.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>kārantānaṃ dhātūnaṃ antassa <span class="bld">ā</span>kārassa <span class="bld">āyā</span>deso hoti <span class="bld">ṇa</span>mhi paccaye pare.</p>\r\n<p class="bodytext">Dadātīti dāyako, dānaṃ dātuṃ sīlaṃ yassāti dānadāyī, majjaṃ dātuṃ sīlaṃ yassāti majjadāyī, nagaraṃ yātuṃ sīlaṃ <span class="bld">ya</span>ssāti nagarayāyī.</p>\r\n<p class="bodytext"><a name="para594"></a><span class="paranum">594</span>, 582. <span class="bld">Pura samupa parīhi karotissa kha kharā vā tappaccayesuca</span>.</p>\r\n<p class="bodytext"><span class="bld">Pura saṃ upa pari</span>iccetehi karotissa dhātussa <span class="bld">kha kharā</span>desā honti vā <span class="bld">ta</span>paccaye pare, <span class="bld">ṇa</span>mhi ca.</p>\r\n<p class="bodytext">Pure karīyatīti purakkhato, sammā karīyatīti saṅkhato, upagantvā karīyatīti upakkhato, parisamantato karotīti parikkhāro, saṃkarīyatīti saṅkhāro.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Upagantvā karotīti upakāro.</p>\r\n<p class="bodytext"><a name="para595"></a><span class="paranum">595</span>, 637. </p>','\r',char(13)),'\n',char(10)),'-593-594-595-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',279,replace(replace('<p class="bodytext"><span class="bld">Tave</span> <a name="M0.0279"></a> <span class="bld">tunādīsu kā</span>.</p>\r\n<p class="bodytext"><span class="bld">Tave tuna</span>iccevamādīsu paccayesu karotissa dhātussa <span class="bld">kā</span>ādeso hoti vā.</p>\r\n<p class="bodytext">Kātave, kātuṃ, kattuṃ vā, kātuna, kattuna vā.</p>\r\n<p class="bodytext"><a name="para596"></a><span class="paranum">596</span>, 551. <span class="bld">Gama khana hanādīnaṃ tuṃ tabbādīsu na</span>.</p>\r\n<p class="bodytext"><span class="bld">Gama khana hana</span>iccevamādīnaṃ dhātūnaṃ antassa <span class="bld">na</span>kāro hoti vā <span class="bld">tuṃ tabbā</span>dīsu paccayesu.</p>\r\n<p class="bodytext">Gantuṃ, gamituṃ, gantabbaṃ, gamitabbaṃ. Khantuṃ. Khanituṃ, khantabbaṃ, khanitabbaṃ. Hantuṃ, hanituṃ, hantabbaṃ. Hanitabbaṃ. Mantuṃ, manituṃ, mantabbaṃ, manitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Ādiggahaṇaṃ</span> kimatthaṃ? <span class="bld">Tuna</span>ggahaṇatthaṃ. Gantuna, khantuna, hantuna, mantuna.</p>\r\n<p class="bodytext"><a name="para597"></a><span class="paranum">597</span>, 641. <span class="bld">Sabbehi tunādīnaṃ yo</span>.</p>\r\n<p class="bodytext">Sabbehi dhātūhi <span class="bld">tunā</span>dīnaṃ paccayānaṃ <span class="bld">ya</span>kārādeso hoti vā.</p>\r\n<p class="bodytext">Abhivandiya, abhivanditvā, ohāya, ohitvā, upanīya, upanetvā, passiya, passitvā, uddissa, uddisitvā, ādāya, ādiyitvā.</p>\r\n<p class="bodytext"><a name="para598"></a><span class="paranum">598</span>, 643. </p>','\r',char(13)),'\n',char(10)),'-596-597-598-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',280,replace(replace('<p class="bodytext"><span class="bld">Canantehi</span> <a name="M0.0280"></a> <span class="bld">raccaṃ</span>.</p>\r\n<p class="bodytext">Cakāranakārantehi dhātūhi <span class="bld">tunā</span>dīnaṃ paccayānaṃ <span class="bld">raccā</span>deso hoti vā.</p>\r\n<p class="bodytext">Vivicca, āhacca, uhacca.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Hantvā.</p>\r\n<p class="bodytext"><a name="para599"></a><span class="paranum">599</span>, 644. <span class="bld">Disā svāna svāntalopo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Disa</span>iccetāya dhātuyā <span class="bld">tunā</span>dīnaṃ paccayānaṃ <span class="bld">svānasvā</span>desā honti, antalopo ca.</p>\r\n<p class="bodytext">Disvāna, disvā.</p>\r\n<p class="bodytext"><a name="para600"></a><span class="paranum">600</span>, 645. <span class="bld">Ma ha da bhehi mma yha jja bbha ddhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ma ha da bha</span> iccevamantehi dhātūhi <span class="bld">tunā</span>dīnaṃ paccayānaṃ <span class="bld">mma yha jja bbha ddhā</span> ādesā honti vā antalopo ca.</p>\r\n<p class="bodytext">Āgamma, āgamitvā, okkamma. Okkamitvā, paggayha, paggaṇhitvā, uppajja, uppajjitvā, ārabbha, ārabhitvā, āraddha, ārabhitvā.</p>\r\n<p class="bodytext"><a name="para601"></a><span class="paranum">601</span>, 334. </p>','\r',char(13)),'\n',char(10)),'-599-600-601-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',281,replace(replace('<p class="bodytext"><span class="bld">Taddhitasamāsakitakā</span> <a name="M0.0281"></a> <span class="bld">nāmaṃ vā’ tave tunādīsu ca</span>.</p>\r\n<p class="bodytext">Taddhitasamāsakitakaiccevamantā saddānāmaṃva daṭṭhabbā <span class="bld">tave tuna tvāna tvā</span>dipaccayante vajjetvā.</p>\r\n<p class="bodytext">Vāsiṭṭho, pattadhammo, kumbhakāro iccevamādi.</p>\r\n<p class="bodytext"><a name="para602"></a><span class="paranum">602</span>, 6. <span class="bld">Dumhi garu</span>.</p>\r\n<p class="bodytext">Dumhi akkhare yo pubbo akkharo, so garukova daṭṭhabbo.</p>\r\n<p class="bodytext">Bhitvā, chitvā, datvā, hutvā.</p>\r\n<p class="bodytext"><a name="para603"></a><span class="paranum">603</span>, 7. <span class="bld">Dīgho ca</span>.</p>\r\n<p class="bodytext">Dīgho ca saro garukova daṭṭhabbo.</p>\r\n<p class="bodytext">Āhāro, nadī, vadhū, te dhammā, opanayiko.</p>\r\n<p class="bodytext"><a name="para604"></a><span class="paranum">604</span>, 684. <span class="bld">Akkharehi kāra</span>.</p>\r\n<p class="bodytext">Akkharatthehi akkharābhidheyyehi <span class="bld">kāra</span>paccayo hoti payoge sati.</p>\r\n<p class="bodytext">A eva akāro, ā eva ākāro, ya eva yakāro.</p>\r\n<p class="bodytext"><a name="para605"></a><span class="paranum">605</span>, 647. </p>','\r',char(13)),'\n',char(10)),'-602-603-604-605-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',282,replace(replace('<p class="bodytext"><span class="bld">Yathāgamamikāro</span><a name="M0.0282"></a>.</p>\r\n<p class="bodytext">Yathāgamaṃ sabbadhātūhi sabbapaccayesu <span class="bld">i</span>kārāgamo hoti.</p>\r\n<p class="bodytext">Kāriyaṃ, bhavitabbaṃ, janitabbaṃ, viditabbaṃ, karitvā, icchitaṃ.</p>\r\n<p class="bodytext"><a name="para606"></a><span class="paranum">606</span>, 642. <span class="bld">Dadhantato yo kvaci</span>.</p>\r\n<p class="bodytext"><span class="bld">Da</span>kāradhakārantāya dhātuyā yathāgamaṃ <span class="bld">ya</span>kārāgamo hoti kvaci <span class="bld">tunā</span>dīsu paccayesu.</p>\r\n<p class="bodytext">Buddho loke uppajjitvā, dhammaṃ bujjhitvā.</p>\r\n<p class="bodytext"><span class="bld">Dadhantato</span>ti kimatthaṃ? Labhitvā.</p>\r\n<p class="bodytext"><span class="bld">Kvacī</span>ti kimatthaṃ? Uppādetvā.</p>\r\n<p class="centered">Iti kibbidhānakappe catuttho kaṇḍo.</p>\r\n<p class="subhead">Pañcamakaṇḍa</p>\r\n<p class="bodytext"><a name="para607"></a><span class="paranum">607</span>, 578. <span class="bld">Niggahita saṃyogādino</span>.</p>\r\n<p class="bodytext">Saṃyogādibhūto <span class="bld">na</span>kāro niggahitamāpajjate.</p>\r\n<p class="bodytext">Raṅgo, bhaṅgo, saṅgo.</p>\r\n<p class="bodytext"><a name="para608"></a><span class="paranum">608</span>, 623. </p>','\r',char(13)),'\n',char(10)),'-606-607-608-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',283,replace(replace('<p class="bodytext"><span class="bld">Sabbattha</span> <a name="M0.0283"></a> <span class="bld">ge gī</span>.</p>\r\n<p class="bodytext"><span class="bld">Ge</span>iccetassa dhātussa <span class="bld">gī</span>ādeso hoti sabbattha ṭhāne.</p>\r\n<p class="bodytext">Gītaṃ gāyati.</p>\r\n<p class="bodytext"><a name="para609"></a><span class="paranum">609</span>, 484. <span class="bld">Sadassa sīdattaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Sada</span>iccetassa dhātussa <span class="bld">sīdā</span>deso hoti sabbattha ṭhāne.</p>\r\n<p class="bodytext">Nisinno, nisīdati.</p>\r\n<p class="bodytext"><a name="para610"></a><span class="paranum">610</span>, 627. <span class="bld">Yajassa sarassi ṭṭhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Yaja</span>iccetassa dhātussa sarassa <span class="bld">i</span>kārādeso hoti ṭṭhe pare.</p>\r\n<p class="bodytext">Yiṭṭho, yiṭṭhā.</p>\r\n<p class="bodytext"><span class="bld">Ṭṭhe</span>ti kimatthaṃ? Yajanaṃ.</p>\r\n<p class="bodytext"><a name="para611"></a><span class="paranum">611</span>, 608. <span class="bld">Hacatutthānamantānaṃ do dhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Ha</span>catutthānaṃ dhātvantānaṃ <span class="bld">do</span> ādeso hoti <span class="bld">dhe</span> pare.</p>\r\n<p class="bodytext">Sannaddho, kuddho, yuddho, siddho, laddho, āraddho.</p>\r\n<p class="bodytext"><a name="para612"></a><span class="paranum">612</span>, 615. </p>','\r',char(13)),'\n',char(10)),'-609-610-611-612-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',284,replace(replace('<p class="bodytext"><span class="bld">Ḍo</span> <a name="M0.0284"></a> <span class="bld">ḍhakāre</span>.</p>\r\n<p class="bodytext">Hacatutthānaṃ dhātvantānaṃ <span class="bld">ḍo</span> ādeso hoti ḍhakāre pare.</p>\r\n<p class="bodytext">Dayhatīti daḍḍho, vaḍḍhatīti vuḍḍho.</p>\r\n<p class="bodytext"><span class="bld">Ḍhakāre</span>ti kimatthaṃ? Dāho.</p>\r\n<p class="bodytext"><a name="para613"></a><span class="paranum">613</span>, 583. <span class="bld">Gahassa ghara ṇe vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Gaha</span>iccetassa dhātussa sabbassa <span class="bld">gharā</span>deso hoti vā <span class="bld">ṇa</span>paccaye pare.</p>\r\n<p class="bodytext">Gharaṃ, gharāni.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Gāho.</p>\r\n<p class="bodytext"><a name="para614"></a><span class="paranum">614</span>, 581. <span class="bld">Dahassa do ḷaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Daha</span>iccetassa dhātussa <span class="bld">da</span>kāro ḷattamāpajjate vā <span class="bld">ṇa</span>paccaye pare.</p>\r\n<p class="bodytext">Paridahanaṃ pariḷāho.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Paridāho.</p>\r\n<p class="bodytext"><a name="para615"></a><span class="paranum">615</span>, 586. <span class="bld">Dhātvantassa lopo kvimhi</span>.</p>\r\n<p class="bodytext">Dhātvantassa byañjanassa lopo hoti kvimhi paccaye pare.</p>','\r',char(13)),'\n',char(10)),'-613-614-615-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',285,replace(replace('<p class="bodytext">Bhujena <a name="M0.0285"></a> gacchatīti, bhujago. Urena gacchatīti urago, turago, saṅkho.</p>\r\n<p class="bodytext"><a name="para616"></a><span class="paranum">616</span>, 587. <span class="bld">Vidante ū</span>.</p>\r\n<p class="bodytext"><span class="bld">Vida</span>iccetassa dhātussa ante <span class="bld">ū</span>kārāgamo hoti kvimhi paccaye pare.</p>\r\n<p class="bodytext">Lokaṃ vidati jānātīti lokavidū.</p>\r\n<p class="bodytext"><a name="para617"></a><span class="paranum">617</span>, 633. <span class="bld">Na ma ka rānamantānaṃ niyuttatamhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Na</span>kāra <span class="bld">ma</span>kāra <span class="bld">ka</span>kāra <span class="bld">ra</span>kārānaṃ dhātvantānaṃ lopo na hoti <span class="bld">i</span>kārayutte <span class="bld">ta</span>paccaye pare.</p>\r\n<p class="bodytext">Hanibhuṃ, gamito, ramito, sakito, sarito, karitvā.</p>\r\n<p class="bodytext"><span class="bld">Iyuttatamhī</span>ti kimatthaṃ? Gato, sato.</p>\r\n<p class="bodytext"><a name="para618"></a><span class="paranum">618</span>, 571. <span class="bld">Na ka gattaṃ ca jāṇvumhi</span>.</p>\r\n<p class="bodytext"><span class="bld">Ca</span>kāra <span class="bld">ja</span>kārā <span class="bld">ka</span>kāra <span class="bld">ga</span>kārattaṃ nāpajjante ṇvumhi paccaye pare.</p>\r\n<p class="bodytext">Pacatīti pācako, yajatīti yājako.</p>\r\n<p class="bodytext"><a name="para619"></a><span class="paranum">619</span>, 573. </p>','\r',char(13)),'\n',char(10)),'-616-617-618-619-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',286,replace(replace('<p class="bodytext"><span class="bld">Karassa</span> <a name="M0.0286"></a> <span class="bld">ca tattaṃ tusmiṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetassa dhātussa antassa <span class="bld">ra</span>kārassa <span class="bld">ta</span>kārattaṃ hoti <span class="bld">tu</span>paccaye pare.</p>\r\n<p class="bodytext">Karotīti kattā, karontīti kattāro.</p>\r\n<p class="bodytext"><a name="para620"></a><span class="paranum">620</span>, 549. <span class="bld">Tuṃ tuna tabbesu vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Kara</span>iccetassa dhātussa antassa <span class="bld">ra</span>kārassa <span class="bld">ta</span>kārattaṃ hoti vā <span class="bld">tuṃ tuna tabba</span>iccetesu paccayesu.</p>\r\n<p class="bodytext">Kattuṃ, kātuṃ, kattuna. Kātuna, kattabbaṃ, kātabbaṃ.</p>\r\n<p class="bodytext"><a name="para621"></a><span class="paranum">621</span>, 553. <span class="bld">Kāritaṃ viya ṇānubandho</span>.</p>\r\n<p class="bodytext"><span class="bld">Ṇa</span>kārānubandho paccayo kāritaṃ viya daṭṭhabbo vā.</p>\r\n<p class="bodytext">Dāho, deho, vāho, bāho, cāgo, vāro, cāro, parikkhāro, dāyako, nāyako, lāvako, bhāvako, kārī, ghātī, dāyī.</p>\r\n<p class="bodytext"><span class="bld">Vā</span>ti kimatthaṃ? Upakkharo.</p>\r\n<p class="bodytext"><a name="para622"></a><span class="paranum">622</span>, 570. </p>','\r',char(13)),'\n',char(10)),'-620-621-622-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',287,replace(replace('<p class="bodytext"><span class="bld">Anakā</span> <a name="M0.0287"></a> <span class="bld">yu ṇvūnaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Yuṇvu</span>iccetesaṃ paccayānaṃ <span class="bld">ana aka</span>iccete ādesā honti.</p>\r\n<p class="bodytext">Nandanaṃ, kārako.</p>\r\n<p class="bodytext"><a name="para623"></a><span class="paranum">623</span>, 554. <span class="bld">Ka gā ca jānaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ca ja</span>iccetesaṃ dhātvantānaṃ <span class="bld">ka</span>kāragakārādesā honti <span class="bld">ṇā</span>nubandhe paccaye pare.</p>\r\n<p class="bodytext">Pāko, yogo.</p>\r\n<p class="centered">Iti kibbidhānakappe pañcamo kaṇḍo.</p>\r\n<p class="centered">Kitakappo niṭṭhito.</p>\r\n<p class="chapter">8. Uṇādikappa</p>\r\n<p class="subhead">Chaṭṭhakaṇḍa</p>\r\n<p class="bodytext"><a name="para624"></a><span class="paranum">624</span>, 563. </p>','\r',char(13)),'\n',char(10)),'-623-624-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',288,replace(replace('<p class="bodytext"><span class="bld">Kattari</span> <a name="M0.0288"></a> <span class="bld">kita</span>.</p>\r\n<p class="bodytext">Kattuiccetasmiṃ atthe kita paccayā honti.</p>\r\n<p class="bodytext">Kāru, kāruko, kārako, pācako, kattā, janitā, pacitā, netā.</p>\r\n<p class="bodytext"><a name="para625"></a><span class="paranum">625</span>, 605. <span class="bld">Bhāvakammesu kiccatta khatthā</span>.</p>\r\n<p class="bodytext">Bhāvakammaiccetesvatthesu <span class="bld">kicca tta khattha</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Upasampādetabbaṃ upasampādanīyaṃ bhavatā, sayitabbaṃ bhavatā, kattabbaṃ bhavatā, bhottabbo odano bhavatā, asitabbaṃ bhojanaṃ bhavatā, asitaṃ bhavatā, sayitaṃ bhavatā, pacitaṃ bhavatā, asitaṃ asanaṃ bhavatā, sayitaṃ sayanaṃ bhavatā, pacito odano bhavatā, kiñcissayo, īsassayo, dussayo, sussayo bhavatā.</p>\r\n<p class="bodytext"><a name="para626"></a><span class="paranum">626</span>, 634. </p>','\r',char(13)),'\n',char(10)),'-625-626-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',289,replace(replace('<p class="bodytext"><span class="bld">Kammani</span> <a name="M0.0289"></a> <span class="bld">dutiyāya tto</span>.</p>\r\n<p class="bodytext">Kammani iccetasmiṃ atthe dutiyāyaṃ vibhattiyaṃ kattari <span class="bld">tta</span>paccayo hoti.</p>\r\n<p class="bodytext">Dānaṃ dinno devadatto, sīlaṃ rakkhito devadatto, bhattaṃ bhutto devadatto, garuṃ upāsito devadatto.</p>\r\n<p class="bodytext"><a name="para627"></a><span class="paranum">627</span>, 652. <span class="bld">Khyādīhi māna ma ca to vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Khi bhī su ru hu vā dhū hi lū pī ada</span>iccevamādīhi dhātūhi <span class="bld">mana</span> paccayo hoti, <span class="bld">ma</span>ssa ca <span class="bld">to</span> hoti vā.</p>\r\n<p class="bodytext">Khīyanti upaddavā etthāti khemo, bhāyitabboti bhemo, bhāyanti etasmāti vā bhemo, raṃsiyo abhissavetīti somo, ravati gacchatīti romo, huvati juhvati etenāti homo, paṭilomavasena vāti gacchatīti vāmo, lāmakavasena vāti gacchati pavattatīti vā vāmo, dhunāti kampatīti dhūmo, seṭṭhabhāvena hinoti pavattati cittaṃ etasminti hemo, lunitabboti lomo, maṃsacammāni lunāti chindatīti vā lomo, piyanaṃ pemo, piyāyitabboti vā pemo, sukhadukkhaṃ adati bhakkhatīti attā, jātijarāmaraṇādīhi adīyate bhakkhīyateti vā attā, ātumā.</p>\r\n<p class="bodytext"><a name="para628"></a><span class="paranum">628</span>, 653. </p>','\r',char(13)),'\n',char(10)),'-627-628-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',290,replace(replace('<p class="bodytext"><span class="bld">Samādīhi</span> <a name="M0.0290"></a> <span class="bld">tha mā</span>.</p>\r\n<p class="bodytext"><span class="bld">Samu damu dara raha du hi si bhī dā yā sā ṭhābhasa</span>iccevamādīhi dhātūhi <span class="bld">tha ma</span> paccayā honti.</p>\r\n<p class="bodytext">Sametīti samatho, damatīti damatho, damanaṃ vā damatho, damitabboti vā damatho, daratīti daratho, jiṇṇabhāvaṃ rahissati gaṇhissatīti ratho, dabbasambhāre rahati gaṇhātīti vā ratho, davati gacchatīti dumo, davati vuddhi viruḷhi gacchati pavattatīti uddhaṃ vā dumo, pathavīpabbatādīsu gacchati patatīti himo, kammavācāya bandhati etthāti sīmā. Bandhitabbāti vā sīmā. Bhāyanti etasmāti bhīmo, satte avakhaṇḍenti nivārenti etenāti dāmo, mūsikādīhi khādīyati avakhaṇḍīyatīti vā dāmo, yāti gacchatīti yāmo, paresaṃ cittaṃ gaṇhituṃ samatthetīti sāmo, tiṭṭhanti etenāti thāmo, bhasati bhasmīkarīyatīti bhasmā.</p>\r\n<p class="bodytext"><a name="para629"></a><span class="paranum">629</span>, 569. <span class="bld">Gahassu’padhasse vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Gaha</span>iccetassa dhātussa upadhassa <span class="bld">a</span>kārassa etta hoti vā.</p>\r\n<p class="bodytext">Dabbasambhāra gaṇhātīti gehaṃ, gahaṃ.</p>\r\n<p class="bodytext"><a name="para630"></a><span class="paranum">630</span>, 654. </p>','\r',char(13)),'\n',char(10)),'-629-630-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',291,replace(replace('<p class="bodytext"><span class="bld">Masussa</span> <a name="M0.0291"></a> <span class="bld">sussa cchara ccherā</span>.</p>\r\n<p class="bodytext"><span class="bld">Masu</span>iccetassa pāṭipadikassa <span class="bld">su</span>ssa <span class="bld">ccharaccherā</span>desā honti.</p>\r\n<p class="bodytext">Maccharatīti maccharo, evaṃ macchero.</p>\r\n<p class="bodytext"><a name="para631"></a><span class="paranum">631</span>, 655. <span class="bld">Āpubbacarassa ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ā</span>pubbassa <span class="bld">cara</span>iccetassa dhātussa <span class="bld">cchariyaccharaccherā</span> desā honti, <span class="bld">ā</span>pubbassa ca rasso hoti.</p>\r\n<p class="bodytext">Ābhuso caritabbanti acchariyaṃ. Evaṃ accharaṃ, accheraṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena masu</span>ssa <span class="bld">su</span>ssāpi <span class="bld">cchariyā</span>deso hoti, macchariyaṃ.</p>\r\n<p class="bodytext"><a name="para632"></a><span class="paranum">632</span>, 656. <span class="bld">Ala kala salehi layā</span>.</p>\r\n<p class="bodytext"><span class="bld">Ala kala sala</span>iccetehi dhātūhi <span class="bld">la ya</span>paccayā honti.</p>\r\n<p class="bodytext">Alati samatthetīti allaṃ, kalitabbaṃ saṅkhyātabbanti kallaṃ, salati gacchati pavisatīti sallaṃ. Evaṃ alyaṃ, kalyaṃ, salyaṃ.</p>\r\n<p class="bodytext"><a name="para633"></a><span class="paranum">633</span>, 657. </p>','\r',char(13)),'\n',char(10)),'-631-632-633-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',292,replace(replace('<p class="bodytext"><span class="bld">Yāṇa</span> <a name="M0.0292"></a> <span class="bld">lāṇā</span>.</p>\r\n<p class="bodytext">Tehi <span class="bld">kala sala</span>iccetehi dhātūhi <span class="bld">yāṇa lāṇa</span>paccayā honti.</p>\r\n<p class="bodytext">Kalitabbaṃ saṅkhyātabbanti kalyāṇaṃ, gaṇato paṭikkamitvā salanti etthāti paṭisalyāṇaṃ. Evaṃ sallāṇo, paṭisallāṇo.</p>\r\n<p class="bodytext"><a name="para634"></a><span class="paranum">634</span>, 658. <span class="bld">Mathissa thassa lo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Matha</span>iccetassa dhātussa <span class="bld">tha</span>ssa <span class="bld">lā</span>desohoti. Aññamaññaṃ mathati viloḷatīti mallo, mallaṃ.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> lato <span class="bld">ko</span> ca āgamo hoti. Mallako, mallakaṃ.</p>\r\n<p class="bodytext"><a name="para635"></a><span class="paranum">635</span>, 559. <span class="bld">Pesātisaggapattakālesukiccā</span>.</p>\r\n<p class="bodytext"><span class="bld">Pesa atisagga pattakāla</span>iccetesvatthesu kiccapaccayā honti.</p>\r\n<p class="bodytext">Kattabbaṃ kammaṃ bhavatā, karaṇīyaṃ kiccaṃ bhavatā, bhottabbaṃ bhojanaṃ bhavatā, bhojanīyaṃ bhojanaṃ bhavatā, ajjhayitabbaṃ ajjheyyaṃ bhavatā, ajjhayanīyaṃ ajjheyyaṃ bhavatā.</p>\r\n<p class="bodytext"><a name="para636"></a><span class="paranum">636</span>, 659. </p>','\r',char(13)),'\n',char(10)),'-634-635-636-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',293,replace(replace('<p class="bodytext"><span class="bld">Avassakā’dhamiṇesu</span> <a name="M0.0293"></a> <span class="bld">ṇī ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Avassaka adhamiṇa</span>iccetesvatthesu <span class="bld">ṇī</span>paccayo hoti, kiccā ca.</p>\r\n<p class="bodytext"><span class="bld">Avassate</span> tāva – kārīsi me kammaṃ avassaṃ hārīsi me bhāraṃ avassaṃ.</p>\r\n<p class="bodytext"><span class="bld">Adhamiṇe</span> – dāyīsi me sataṃ iṇaṃ, dhārīsi me sahassaṃ iṇaṃ.</p>\r\n<p class="bodytext">Kiccā ca – dātabbaṃ me bhavatā sataṃ iṇaṃ. Dhārayitabbaṃ me bhavatā sahassaṃ iṇaṃ, kattabbaṃ me bhavatā gehaṃ, karaṇīyaṃ me bhavatā kiccaṃ, kāriyaṃ me bhavatā sayanaṃ.</p>\r\n<p class="bodytext"><a name="para637"></a><span class="paranum">637</span>, 0. <span class="bld">Arahasakkādīhi tuṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Araha sakka bhabba</span>iccevamādīhi payoge sati sabbadhātūhi <span class="bld">tuṃ</span>paccayo hoti.</p>\r\n<p class="bodytext">Arahā bhavaṃ vattuṃ, arahā bhavaṃ kattuṃ, sakkā bhavaṃ hantuṃ, sakkā bhavaṃ janetuṃ, janituṃ, bhavituṃ, sakkā bhavaṃ dātuṃ, sakkā bhavaṃ gantuṃ, tabbo bhavaṃ janetuṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para638"></a><span class="paranum">638</span>, 660. </p>','\r',char(13)),'\n',char(10)),'-637-638-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',294,replace(replace('<p class="bodytext"><span class="bld">Vajādīhipabbajjādayo</span> <a name="M0.0294"></a> <span class="bld">nippajjante</span>.</p>\r\n<p class="bodytext"><span class="bld">Vaja</span>iccevamādīhi dhātūhi, upasaggapaccayādīhi ca pabbajjādayo saddā nippajjante.</p>\r\n<p class="bodytext">Paṭhamameva vajitabbāti pabbajjā, iñjanaṃ ejjā, samajjanaṃ samajjā, nisīdanaṃ nisajjā, vijānanaṃ vijjā visajjanaṃ visajjā, padanaṃ pajjā, hananaṃ vajjhā, esanaṃ icchā, atiesanaṃ aticchā, sadanaṃ sajjā, sayanti etthāti seyyā, sammā cittaṃ nidheti etāyāti saddhā, caritabbā cariyā, karaṇaṃ kiriyā, rujanaṃ rucchā, padanaṃ pacchā, riñcanaṃ ricchā, tikicchanaṃ titicchā, saṃkocanaṃ saṃkucchā, madanaṃ macchā, labhanaṃ lacchā, radihabbāti racchā, radanaṃ vilekhanaṃ vāracchā, adho bhāgena gacchatīti tiracchā, tiracchāno, ajanaṃ acchā, titikkhatīti titikkhā, saha āgamanaṃ sāgacchā, duṭṭhu bhakkhanaṃ dobhacchā, duṭṭhu rosanaṃ durucchā, pucchanaṃ pucchā, muhanaṃ mucchā, vasanaṃ vacchā, kacanaṃ kacchā, saha kathanaṃ sākacchā, tudanaṃ tucchā, visanaṃ vicchā, pisanaṃ picchillā, sukhadukkhaṃ mudati bhakkhatīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',295,replace(replace('<p class="bodytext">maccho<a name="M0.0295"></a>, sattānaṃ pāṇaṃ museti cajetīti maccu, satanaṃ saccaṃ, uddhaṃ dhunāti kampatīti uddhaccaṃ, naṭanaṃ naccaṃ, nitanaṃ niccaṃ, tathanaṃ tacchaṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para639"></a><span class="paranum">639</span>, 585. <span class="bld">Kvilopo ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Kvi</span>lopo hoti, puna ca nippajjante.</p>\r\n<p class="bodytext">Vividhehi sīlādiguṇehi bhavatīti vibhū, visesena vā bhavatīti vibhū, sayaṃ attanā bhavatīti sayambhū, abhivitvā bhavatīti abhibhū, saṃ suṭṭhu dhunāti kampatīti sandhū, visesena bhāti dibbatīti vibhā, nissesena bhāti dibbatīti nibhā, pakārena bhāti dibbatīti pabhā, saha bhāsanti etthāti sabhā, ābhuso bhāti dibbatīti ābhā, bhujena kuṭilena gacchatīti bhujago, turitaturito gacchatīti turago, saṃ suṭṭhu pathaviṃ khanatīti saṅkho, visesena yamati uparamatīti viyo, suṭṭhu manati jānātīti sumo, pari samantato tanoti vitthāretīti parito iccevamādi.</p>\r\n<p class="bodytext"><a name="para640"></a><span class="paranum">640</span>, 0. <span class="bld">Sacajānaṃ kagā ṇānubandhe</span>.</p>\r\n<p class="bodytext"><span class="bld">Saca jā</span>naṃ dhātūnamantānaṃ <span class="bld">cajānaṃ kagā</span>desā honti yathāsaṅkhyaṃ <span class="bld">ṇā</span>nubandhe paccaye pare.</p>','\r',char(13)),'\n',char(10)),'-639-640-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',296,replace(replace('<p class="bodytext">Oko<a name="M0.0296"></a>, pāko, seko, soko, viveko, cāgo, yogo, bhogo, rogo, rāgo, bhāgo, bhaṅgo, raṅgo, saṅgo.</p>\r\n<p class="bodytext"><a name="para641"></a><span class="paranum">641</span>, 572. <span class="bld">Nudādīhi yuṇvūnamanānanākānanakā sakāritehi ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Nuda sūda jana su lū hu pu bhū ñā a sa samu</span>iccevamādīhi dhātūhi, <span class="bld">phanda citi āṇa</span> iccevamādīhi sakāritehi ca <span class="bld">yuṇvūnaṃ</span> paccayānaṃ <span class="bld">ana ānana aka ānanakā</span>desā honti yathāsaṅkhyaṃ kattari, bhāvakaraṇesu ca.</p>\r\n<p class="bodytext"><span class="bld">Kattari</span> tāva – panudatīti panūdano. Evaṃ sūdano, janano, savaṇo, lavano, havano, pavano, bhavano, ñāṇo, asano, samaṇo.</p>\r\n<p class="bodytext"><span class="bld">Bhāve ca</span> – panudate panūdanaṃ. Evaṃ sūdanaṃ, jananaṃ, savaṇaṃ, lavanaṃ, havanaṃ, pavanaṃ, bhavanaṃ, ñāṇaṃ, asanaṃ, samaṇaṃ, sañjānanaṃ, kuyate kānanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Kārite</span> ca – phandāpīyate phandāpanaṃ, cetāpīyate cetāpanaṃ, āṇāpīyate āṇāpanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Karaṇe</span> – nudanti anenāti nūdanaṃ, evaṃ sūdanaṃ, jananaṃ, savaṇaṃ, lavaṇaṃ, havanaṃ, pavanaṃ, bhaganaṃ, ñāṇaṃ, asanaṃ, savaṇaṃ.</p>','\r',char(13)),'\n',char(10)),'-641-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',297,replace(replace('<p class="bodytext">Puna <a name="M0.0297"></a> <span class="bld">kattari</span> – nudatīti nūdako, sūdatīti sūdadhakā, janetīti janako, suṇotīti sāvako, lunātīti lāvako, juhotīti hāvako, punātīti pāvako, bhavatīti bhāvako, jānātīti jānako, asatīti asako, upāsatīti upāsako, sametīti samako.</p>\r\n<p class="bodytext"><span class="bld">Kārite</span> tu – phandāpayatīti phandāpayako. Evaṃ āṇāpayako, cetāpayako, sañjānanako.</p>\r\n<p class="bodytext"><a name="para642"></a><span class="paranum">642</span>, 588. <span class="bld">I ya ta ma ki e sāna’mantassaro dīghaṃ kvaci du sa ssa guṇaṃ do raṃ sakkhī ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Iya ta ma ki e sa</span>iccetesaṃ sabbanāmānamanto saro dīghamāpajjate, kvaci <span class="bld">dusa</span>iccetassa dhātussa <span class="bld">u</span>kāro guṇamāpajjate, <span class="bld">da</span>kāro <span class="bld">ra</span>kāramāpajjate, dhātvantassa <span class="bld">sa</span>ssa ca <span class="bld">sa kkha ī</span>iccete ādesā honti yathāsambhavaṃ. Ete saddā sakena sakena nāmena yathānuparodhena buddhasāsane pacchā puna nippajjante.</p>\r\n<p class="bodytext">Imamiva naṃ passatīti īdiso, yamiva naṃ passatīti yādiso, tamiva naṃ passatīti tādiso, mamiva naṃ passatīti mādiso, kimiva naṃ passatīti kīdiso, etamiva naṃ passatīti ediso, samānamiva naṃ passatīti </p>','\r',char(13)),'\n',char(10)),'-642-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',298,replace(replace('<p class="bodytext">sādiso<a name="M0.0298"></a>. Imamiva naṃ passatīti īriso, yamiva naṃ passatīti yāriso, tamiva naṃ passatīti tāriso, mamiva naṃ passatīti māriso, kimiva naṃ passatīti kīriso, etamiva naṃ passatīti eriso, samānamiva naṃ passatīti sāriso. Imamiva naṃ passatīti īdikkho, yamiva naṃ passatīti yādikkho, tamiva naṃ passatīti tādikkho, evaṃ mādikkho, kīdikkho, edikkho, sādikkho. Īdī, yādī, tādī, mādī, kīdī, edī, sādī.</p>\r\n<p class="bodytext"><span class="bld">Caggahaṇena</span> tesameva saddānaṃ <span class="bld">iya</span>iccevamādīnamanto ca saro kvaci dīghatthamāhu. Īdikkho, yādikkho, tādikkho, mādikkho, kīdikkho, edikkho, sādikkho. Idiso, sadiso, sariso, sarikkho.</p>\r\n<p class="bodytext"><a name="para643"></a><span class="paranum">643</span>, 635. <span class="bld">Bhyādīhi mati budhi pūjādīhi ca tto</span>.</p>\r\n<p class="bodytext"><span class="bld">Bhī</span>iccevamādīhi dhātūhi, <span class="bld">mati. Budhi pūjā</span>dito ca <span class="bld">tta</span> paccayo hoti.</p>\r\n<p class="bodytext">Bhāyitabboti bhīto, supitabboti sutto, mijjitabbo sinehetabboti mitto, sammannitabboti sammato, saṃ suṭṭhu mānitabbo pūjetabboti sammato, sammānīyitthāti sammato, saṃkappīyateti </p>','\r',char(13)),'\n',char(10)),'-643-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',299,replace(replace('<p class="bodytext">saṅkappito<a name="M0.0299"></a>, saṃkappīyitthāti saṅkappito, sampādīyateti sampādito, sampādīyitthāti sampādito, avadhārīyateti avadhārito, avadhārīyitthāti avadhārito, bujjhitabbo ñātabboti buddho, ajjhayitabboti ito, etabbo gantabboti ito, viditabbo ñātabboti vidito, takkīyateti takkito, pūjīyateti pūjito, pūjīyitthāti pūjito, apacāyitabboti apacāyito, mānitabbo pūjetabboti mānito, apacīyateti apacito, vandīyateti vandito, vandīyitthāti vandito, sakkarīyateti sakkārito, sakkarīyitthāti sakkārito.</p>\r\n<p class="bodytext"><a name="para644"></a><span class="paranum">644</span>, 661. <span class="bld">Vepu sī dava vamu ku dā bhū hvādīhi thu ttima ṇimā nibbatte</span>.</p>\r\n<p class="bodytext"><span class="bld">Vepu sī dava vamu ku dā bhū hui</span>ccevamādīhi dhātūhi yathāsambhavaṃ <span class="bld">thu ttima ṇima</span>paccayā honti nibbattatthe.</p>\r\n<p class="bodytext">Vepanaṃ vepo, tena nibbatto vepathu, sayanaṃ sayo, tena nibbatto sayathu, davanaṃ davo, tena nibbatto davathu. Vamanaṃ vamo, tena nibbatto vamathu. Kutti karaṇaṃ, tena nibbattaṃ kuttimaṃ. Dāti dānaṃ, tena nibbattaṃ dattimaṃ. Bhūti bhavanaṃ, tena nibbattaṃ bhottimaṃ. Avahuti avahanaṃ, tena nibbattaṃ ohāvimaṃ.</p>\r\n<p class="bodytext"><a name="para645"></a><span class="paranum">645</span>, 662. </p>','\r',char(13)),'\n',char(10)),'-644-645-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',300,replace(replace('<p class="bodytext"><span class="bld">Akkose</span> <a name="M0.0300"></a> <span class="bld">namhāni</span>.</p>\r\n<p class="bodytext">Akkosaiccetasmiṃ atthe namhi paṭisedhayutte <span class="bld">āni</span>paccayo hoti dhātūhi.</p>\r\n<p class="bodytext">Na gamitabbaṃ agamāni te jamma desaṃ, na kattabbaṃ akarāṇi te jamma kammaṃ.</p>\r\n<p class="bodytext"><span class="bld">Namhī</span>ti kimatthaṃ? Vipatti te jamma, vikati te jamma.</p>\r\n<p class="bodytext"><span class="bld">Akkoseti</span> kimatthaṃ? Na gantabbā agati te.</p>\r\n<p class="bodytext"><a name="para646"></a><span class="paranum">646</span>, 419. <span class="bld">Ekādito sakissa kkhattuṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Ekā</span>dito <span class="bld">saki</span>ssa <span class="bld">kkhattuṃ</span> hoti.</p>\r\n<p class="bodytext">Ekassa padatthassa sakiṃ vāraṃ ekakkhattuṃ, dvinnaṃ padatthānaṃ sakiṃ vāraṃ dvikkhattuṃ, tiṇṇaṃ padatthānaṃ sakiṃ vāraṃ tikkhattuṃ, evaṃ catukkhattuṃ, pañcakkhattuṃ, chakkhattuṃ, sattakkhattuṃ, aṭṭhakkhattuṃ, navakkhattuṃ, dasakkhattuṃ. Iccevamādayo saddā yojetabbā.</p>\r\n<p class="bodytext"><a name="para647"></a><span class="paranum">647</span>, 663. <span class="bld">Sunassunassoṇa vānuvānūnunakhunānā</span>.</p>\r\n<p class="bodytext"><span class="bld">Suna</span>iccetassa pāṭipadikassa <span class="bld">una</span>ssa <span class="bld">oṇa vāna uvāna ūna unakha una ā ānā</span>desā honti.</p>','\r',char(13)),'\n',char(10)),'-646-647-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',301,replace(replace('<p class="bodytext">Sāmikassa <a name="M0.0301"></a> saddaṃ suṇātīti soṇo, sāmikassa saddaṃ suṇātīti svāno, evaṃ suvāno, sūno, sunakho, suno, sā, sāno.</p>\r\n<p class="bodytext"><a name="para648"></a><span class="paranum">648</span>, 664. <span class="bld">Taruṇassa susu ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Taruṇai</span>ccetassa pāṭipadikassa <span class="bld">susu</span> ādeso hoti.</p>\r\n<p class="bodytext">Susu kāḷakeso.</p>\r\n<p class="bodytext"><a name="para649"></a><span class="paranum">649</span>, 665. <span class="bld">Yuvassuvassuvuvānunūnā</span>.</p>\r\n<p class="bodytext"><span class="bld">Yuva</span>iccetassa pāṭipadikassa <span class="bld">uva</span>ssa <span class="bld">uvauvāna unaūnā</span>desā honti.</p>\r\n<p class="bodytext">Yuvā, yuvāno, yuno, yūno.</p>\r\n<p class="bodytext"><a name="para650"></a><span class="paranum">650</span>, 651. <span class="bld">Kāle vattamānātīte ṇvādayo</span>.</p>\r\n<p class="bodytext">Kāle vattamānatthe ca atītatthe ca <span class="bld">ṇu yu ta</span>paccayā honti.</p>\r\n<p class="bodytext">Akāsi, karotīti kāru, agacchi, gacchatīti, vāyu, abhavi, bhavatīti bhūtaṃ.</p>\r\n<p class="bodytext"><a name="para651"></a><span class="paranum">651</span>, 647. </p>','\r',char(13)),'\n',char(10)),'-648-649-650-651-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',302,replace(replace('<p class="bodytext"><span class="bld">Tavissati</span> <a name="M0.0302"></a> <span class="bld">gamādīhi ṇī ghiṇa</span>.</p>\r\n<p class="bodytext">Bhavissatikālatthe <span class="bld">gamu bhaja su ṭhā</span>iccevamādīhi dhātūhi <span class="bld">ṇī ghiṇa</span> paccayā honti.</p>\r\n<p class="bodytext">Āyatiṃ gamituṃ sīlaṃ yassa, so hotīti gāmī, āyatiṃ bhajituṃ sīlaṃ yassa, so hotīti bhājī, āyatiṃ passāpituṃ sīlaṃ yassa, so hotīti passāvi, āyatiṃ paṭṭhāyituṃ sīlaṃ yassa, so hotīti paṭṭhāyi.</p>\r\n<p class="bodytext"><a name="para652"></a><span class="paranum">652</span>, 648. <span class="bld">Kriyāyaṃ ṇvu tavo</span>.</p>\r\n<p class="bodytext">Kriyāyamatthe <span class="bld">ṇvu tu</span>iccete paccayā honti bhavissatikāle.</p>\r\n<p class="bodytext">‘‘Karissa’’nti kārako vajati, ‘‘bhuñjissa’’nti bhottā vajati.</p>\r\n<p class="bodytext"><a name="para653"></a><span class="paranum">653</span>, 306. <span class="bld">Bhāvavācimhi catutthī</span>.</p>\r\n<p class="bodytext">Bhāvavācimhi catutthīvibhatti hoti bhavissatikāle,</p>\r\n<p class="bodytext">Pacissate, pacanaṃ vā pāko, pākāya vajati. Bhuñjissate, bhojanaṃ vā bhogo, bhogāya vajati. Naccissate, naccanaṃ vā naccaṃ, naccāya vajati.</p>\r\n<p class="bodytext"><a name="para654"></a><span class="paranum">654</span>, 649. </p>','\r',char(13)),'\n',char(10)),'-652-653-654-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',303,replace(replace('<p class="bodytext"><span class="bld">Kammani</span> <a name="M0.0303"></a> <span class="bld">ṇo</span>.</p>\r\n<p class="bodytext">Kammani upapade <span class="bld">ṇa</span>paccayo hoti bhavissatikāle.</p>\r\n<p class="bodytext">Nagaraṃ karissati nagarakāro vajati, sāliṃ lāvissati sālilāvo vajati, dhaññaṃ vapissati dhaññavāpo vajati, bhogaṃ dadissati bhogadāyo vajati, sindhuṃ pivissati sindhupāyo vajati.</p>\r\n<p class="bodytext"><a name="para655"></a><span class="paranum">655</span>, 650. <span class="bld">Sese ssaṃ ntumānānā</span>.</p>\r\n<p class="bodytext">Sesaiccetasmiṃ atthe <span class="bld">ssaṃ ntu māna āna</span> iccete paccayā honti bhavissatikāle kammūpapade.</p>\r\n<p class="bodytext">Kammaṃ karissati kammaṃ karissaṃ, evaṃ kammaṃ karonto, kammaṃ kurumāno, kammaṃ karāno vajati. Bhojanaṃ bhuñjissati bhojanaṃ bhuñjissaṃ, evaṃ bhojanaṃ bhuñjanto, bhojanaṃ bhuñjamāno, bhojanaṃ bhuñjāno vajati. Khādanaṃ khādissati khādanaṃ khādissaṃ, evaṃ khādanaṃ khādanto, khādanaṃ khādamāno, khādanaṃ khādāno vajati. Maggaṃ carissati maggaṃ carissaṃ, evaṃ maggaṃ caranto, maggaṃ caramāno, maggaṃ carāno vajati. Bhikkhaṃ bhikkhissati bhikkhaṃ bhikkhissaṃ, evaṃ bhikkhaṃ bhikkhanto, bhikkhaṃ bhikkhamāno, bhikkhaṃ bhikkhāno vajati.</p>\r\n<p class="bodytext"><a name="para656"></a><span class="paranum">656</span>, 666. </p>','\r',char(13)),'\n',char(10)),'-655-656-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',304,replace(replace('<p class="bodytext"><span class="bld">Chavādīti</span> <a name="M0.0304"></a> <span class="bld">tatraṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Cha da ci ti su nī vi da pada tanu yata ada mada yuja vatumida mā pu ka la vara ve pu gupa dā</span> iccevamādīhi dhātūhi <span class="bld">tatraṇa</span> iccete paccayā honti yathāsambhavaṃ.</p>\r\n<p class="bodytext">Ātapaṃ chādetīti chattaṃ, chatraṃ. Ārammaṇaṃ cintetīti cittaṃ, citraṃ. Cintenti sampayuttadhammā ethenāti vā cittaṃ, citraṃ. Atthe abhissavetīti suttaṃ, sutraṃ. Atthe sūcetīti vā suttaṃ, sutraṃ. Satte netīti nettaṃ, netraṃ. Satte icchitaṭṭhānaṃ nenti etenāti vā nettaṃ, netraṃ. Pakārena vidatīti pavittaṃ, pavitraṃ. Vividhena ākārena maṅga pāpaṃ punāti, sodhetīti pavittaṃ, pavitraṃ. Sucibhāvaṃ vā pāpuṇātīti pavittaṃ, pavitraṃ. Padati pāpuṇātīti patto, patro. Āhārā patanti ettha bhājaneti pattaṃ, patraṃ. Padati pavattatīti vā pattaṃ, patraṃ. Tanoti vitthāretīti tantaṃ, tantraṃ, tanitabbaṃ vitthāretabbanti vā tantaṃ, tantra. Yatatīti yattaṃ, yatraṃ. Yatati vīriyaṃ karoti etenāti vā yattaṃ, yatraṃ. Yatanaṃ vā yattaṃ, yatraṃ. Sukhadukkhaṃ adati bhakkhatīti attā, atrā. Madatīti mattaṃ, matraṃ. Vatthuṃ yujjanti etenāti yottaṃ, yotraṃ. Vattatīti vattaṃ, vatraṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',305,replace(replace('<p class="bodytext">Midati <a name="M0.0305"></a> sinehaṃ karotīti mittaṃ. Mitraṃ. Midati sinehati etāyāti mettā, metrā. Pari samanta to sabbākārena minanti etāyāti mattā, matrā. Mānanaṃ vā mattaṃ, matraṃ. Attano kulaṃ punāti sodhetīti putto, putro. Kalitabbaṃ saṅkhyātabbanti kalattaṃ, kalatraṃ. Saṃsuṭṭhu vāreti etenāti varattaṃ, varatraṃ. Vepati kampatīti vettaṃ, vetraṃ. Gopitabbaṃ rakkhitabbanti guttaṃ. Gutraṃ, gottaṃ, gotraṃ. Dāti avakhaṇḍati etenāti dāttaṃ, dātraṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para657"></a><span class="paranum">657</span>, 667. <span class="bld">Vadādīhi ṇitto gaṇe</span>.</p>\r\n<p class="bodytext"><span class="bld">Vada cara vara</span>iccevamādīhi dhātūhi <span class="bld">ṇitta</span>paccayo hoti gaṇatthe.</p>\r\n<p class="bodytext">Vāditānaṃ gaṇo vādittaṃ. Evaṃ cārittaṃ, vārittaṃ, iccevamādi.</p>\r\n<p class="bodytext"><a name="para658"></a><span class="paranum">658</span>, 668. <span class="bld">Midādīhi ttitiyo</span>.</p>\r\n<p class="bodytext"><span class="bld">Mida pada ra</span>nja <span class="bld">tanu dhā</span>iccevamādīhi dhātūhi <span class="bld">tti ti</span> iccete paccayā honti.</p>\r\n<p class="bodytext">Midati sinehatīti metti, padati gacchatīti patti, ranjati etthāti ratti, tanoti vitthāretīti tanti, </p>','\r',char(13)),'\n',char(10)),'-657-658-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',306,replace(replace('<p class="bodytext">attano <a name="M0.0306"></a> kulaṃ tanoti vitthāretīti vā tanti, paresaṃ itthīnaṃ puttaṃ dhāretīti dhāti, khīraṃ dhāretīti vā dhāti, attano sabhāvaṃ dhāretīti vā dhāti iccevamādi.</p>\r\n<p class="bodytext"><a name="para659"></a><span class="paranum">659</span>, 669. <span class="bld">Usu ranja daṃsānaṃ daṃsassa daḍḍhoḍha ṭhā ca</span>.</p>\r\n<p class="bodytext"><span class="bld">Usu ranja daṃsa</span>iccetesaṃ dhātūnaṃ <span class="bld">daṃsa</span>ssa <span class="bld">daḍḍhā</span>deso hoti, <span class="bld">ḍha ṭha</span>paccayā ca honti.</p>\r\n<p class="bodytext">Usīyate uḍḍho, ranjanti etthāti raṭṭhaṃ, daṃsīyateti daḍḍho.</p>\r\n<p class="bodytext"><a name="para660"></a><span class="paranum">660</span>, 670. <span class="bld">Sūvusānamūvusānamatothoca</span>.</p>\r\n<p class="bodytext"><span class="bld">Sūvu asa</span>iccetesaṃ dhātūnaṃ <span class="bld">ūuasā</span>naṃ <span class="bld">atā</span>deso hoti, <span class="bld">tha</span>paccayo ca.</p>\r\n<p class="bodytext">Savati hiṃsati etenāti satthaṃ, hirottappaṃ saṃvarati etenātivatthaṃ, saddānurūpaṃ asatibhavatīti attho,</p>\r\n<p class="bodytext"><a name="para661"></a><span class="paranum">661</span>, 671. </p>','\r',char(13)),'\n',char(10)),'-659-660-661-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',307,replace(replace('<p class="bodytext"><span class="bld">Ranjudādīhi</span> <a name="M0.0307"></a> <span class="bld">dha didda kirā kvaci jadalopoca</span>.</p>\r\n<p class="bodytext"><span class="bld">Ranja uda idi cadi madi khuda chidi rudi dala susa suca vaca vaja</span> iccevamādīhi dhātūhi <span class="bld">dha da idda ka ira</span>iccete paccayā honti, kvaci <span class="bld">ja da</span> lopo ca, puna nippajjante.</p>\r\n<p class="bodytext">Rañjitabbanti randhaṃ, ranjayitthāti vā randhaṃ, attani sannissitānaṃ macchamakarānaṃ pītisomanassaṃ undati pasavati janetīti samuddo, indati paramissariyaṃ karotīti indo, indattaṃ adhipatibhāvaṃ karotīti vā indo, canditabbo icchitabboti cando, mandati hāsetīti mando, maditabbo hāsetabboti vā mando, khudati pipāsetīti khuddo, chinditabboti chiddo, rudati hiṃsatīti ruddo, dalati duggatabhāvaṃ gacchatīti daliddo, sussatīti sukkaṃ, sucatītisoko, vacitabbanti vakkaṃ, appaṭihato hutvā vajati gacchatīti vajiraṃ iccevamādi.</p>\r\n<p class="bodytext"><a name="para662"></a><span class="paranum">662</span>, 672. <span class="bld">Paṭito hissa heraṇa hīraṇa</span>.</p>\r\n<p class="bodytext"><span class="bld">Paṭi</span>iccetasmā <span class="bld">hi</span>ssa dhātussa <span class="bld">heraṇa hīraṇa</span> ādesā honti.</p>\r\n<p class="bodytext">Paṭipakkhemadditvāgacchati pavattatīti pāṭiheraṃ, pāṭihīraṃ.</p>\r\n<p class="bodytext"><a name="para663"></a><span class="paranum">663</span>, 673. </p>','\r',char(13)),'\n',char(10)),'-662-663-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',308,replace(replace('<p class="bodytext"><span class="bld">Kaḍyādīhi</span> <a name="M0.0308"></a> <span class="bld">ko</span>.</p>\r\n<p class="bodytext"><span class="bld">Kaḍi ghaḍi vaḍi karaḍi maḍi saḍi kuṭhi bhaḍi paḍi daḍi raḍi taḍi isiḍi caḍi gaḍi aḍi laḍi meḍi eraḍi khaḍi</span> iccevamādīhi dhātūhi <span class="bld">ka</span>paccayo hoti saha paccayena ca nippajjante yathāsambhavaṃ.</p>\r\n<p class="bodytext">Kaṇḍitabbo chinditabboti kaṇḍo, ghaṇḍitabbo ghaṭetabboti ghaṇḍo, vaṇḍanti etthāti vaṇḍo, karaṇḍitabbo bhājetabboti karaṇḍo, maṇḍīyate vibhūsīyate etenāti maṇḍo, saṇḍanti gumbanti etthāti saṇḍo, aṅgamaṅgāni kuṇṭhati chindatīti kuṭṭhaṃ, bhaṇḍitabbanti bhaṇḍaṃ, paṇḍati liṅgavekallabhāvaṃ gacchatīti paṇḍako. Daṇḍati āṇaṃ karoti etenāti daṇḍo, raṇḍati hiṃsatīti raṇḍo, visesena taṇḍati cāleti paresaṃ viññūnaṃ hadayaṃ kampetīti vitaṇḍo, isiṇḍati paresaṃ maddatīti isiṇḍo, caṇḍati caṇḍikkabhāvaṃ karotīti caṇḍo, gaṇḍati sannicayati samūhaṃ karoti etthāti gaṇḍo, aṇḍīyati nibbattīyatīti aṇḍo, laṇḍitabbo jigucchitabboti laṇḍo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',309,replace(replace('<p class="bodytext">meṇḍati <a name="M0.0309"></a> kuṭilabhāvaṃ gacchatīti meṇḍo, eraṇḍati rogaṃ hiṃsatīti eraṇḍo, khaṇḍitabbo chinditabboti khaṇḍo iccevamādi.</p>\r\n<p class="bodytext"><a name="para664"></a><span class="paranum">664</span>, 674. <span class="bld">Khādāmagamānaṃ kha ndha’ndha gandhā</span>.</p>\r\n<p class="bodytext"><span class="bld">Khāda ama gamu</span>iccetesaṃ dhātūnaṃ <span class="bld">khandha andha gandhā</span>desā honti, <span class="bld">ka</span>paccayo ca hoti.</p>\r\n<p class="bodytext">Jātijarāmaraṇādīhi saṃsāradukkhehi khāditabboti khandho, amati aṅgamaṅgassa rujjanabhāvaṃ gacchatīti andho, cakkhunā amati rujjatīti vā andho, taṃ taṃ ṭhānaṃ vātena gacchatīti gandho. Evaṃ khandhako, andhako, gandhako.</p>\r\n<p class="bodytext"><a name="para665"></a><span class="paranum">665</span>, 675. <span class="bld">Paṭādīhyalaṃ</span>.</p>\r\n<p class="bodytext"><span class="bld">Paṭa kala kusa kada bhaganda mekha vakka takka palla sadda mūla bila vida caḍaṃ pañca vā vasa paci maca musa gotthu puthu bahu maṅga baha kamba samba agga</span>iccevamādīhi dhātūhi pāṭi padikehi ca uttarapadesu <span class="bld">ala</span>paccayo hoti, pacchā puna nippajjante.</p>\r\n<p class="bodytext">Paṭe alanti paṭalaṃ, kale alanti kalalaṃ, pāpake akusale dhamme kusati chindatīti kusalaṃ, kusabhūte yathāsabhāvadhamme alanti vā kusalaṃ, kuse uddissa dāne alanti vā kusalaṃ, kuse sañcaye dhammasamudāye </p>','\r',char(13)),'\n',char(10)),'-664-665-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',310,replace(replace('<p class="bodytext">alanti <a name="M0.0310"></a> vā kusalaṃ, kadde madde alanti kadalaṃ, bhagande secane alanti bhagandalaṃ, bhagande muttakarīsaharaṇe alanti vā bhagandalaṃ, mekhe kaṭivicitte alanti mekhalaṃ, vakke rukkhatace alanti vakkalaṃ, takke rukkhasilese alanti takkalaṃ, palle ninnaṭṭhāne alanti pallalaṃ, sadde harite alanti saddalaṃ, mūle patiṭṭhāne alanti mulālaṃ, biḷe nissaye alanti bilālaṃ, vide vijjamāne alanti vidalaṃ, caṇḍe alanti caṇḍālo, pañcannaṃ rājūnaṃ alanti pañcālo, vā gatigandhanesu alanti vālaṃ, vā padagamane alanti vā vāḷo, vase acchādane alanti vasalo, pace vitthāre alanti pacalo, mace corakamme alanti macalo, muse theyye, muse pāṇacāge vā alanti musalo, gotte vaṃse siṅgālajātiyaṃ alanti gotthulo, puthumhi vitthāre alanti puthulo, bahumhi saṅkhyāne alanti bahulo, bahumhi vuddhimhi alanti vā bahulo, maṅgamhi gamane alanti maṅgalaṃ, bahumhi vuddhimhi alanti bahalaṃ, kambamhi sañcalane </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',311,replace(replace('<p class="bodytext">alanti <a name="M0.0311"></a> kambalaṃ. Sambamhi maṇḍale alanti sambalaṃ, agge gatikoṭille alanti aggaḷaṃ. Iccevamādayo aññepi saddā bhavanti.</p>\r\n<p class="bodytext"><a name="para666"></a><span class="paranum">666</span>, 676. <span class="bld">Puthassa puthu pathā mo vā</span>.</p>\r\n<p class="bodytext"><span class="bld">Putha</span>iccetassa pāṭipadikassa <span class="bld">puthu pathā</span>desā honti, kvaci <span class="bld">ama</span>paccayo hoti.</p>\r\n<p class="bodytext">Putha hutvā jātanti puthavī pathame jāto pathamo, pathavī, paṭhamo vā, puthu kilese janetīti puthujjano, puthu hutvā jātanti pathavī, pathavī vā.</p>\r\n<p class="bodytext"><a name="para667"></a><span class="paranum">667</span>, 677. <span class="bld">Sasvādīhi tu davo</span>.</p>\r\n<p class="bodytext"><span class="bld">Sasu dada ada mada</span>iccevamādīhi dhātūhi <span class="bld">tu du</span>iccete paccayā honti.</p>\r\n<p class="bodytext">Aññe satte sasati hiṃsatīti sattu, dukkhaṃ dadātīti daddu, dukkhena adati bhakkhati etthāti addu, dukkhaṃ adati anubhavati jano etenāti vā addu, dukkhaṃ </p>','\r',char(13)),'\n',char(10)),'-666-667-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',312,replace(replace('<p class="bodytext">bhājanaṃ <a name="M0.0312"></a> ādhāraṃ bhāvatīti vā addu, madati ummattaṃ karotīti maddu, madati maddabhāvaṃ karotīti vā maddu.</p>\r\n<p class="bodytext"><a name="para668"></a><span class="paranum">668</span>, 678. <span class="bld">Jhādīhi īvaro</span>.</p>\r\n<p class="bodytext"><span class="bld">Ci pā dhā</span>iccevamādīhi dhātūhi <span class="bld">īvara</span>paccayohoti.</p>\r\n<p class="bodytext">Cīyatīti cīvaraṃ, pivatīti pīvaro, pātabbaṃ rakkhitabbanti vā pīvaraṃ. Dhāreti dhāretvā jīvitaṃ kappetīti dhīvaro, dhīvaraṃ.</p>\r\n<p class="bodytext"><a name="para669"></a><span class="paranum">669</span>, 679. <span class="bld">Munādīhi ci</span>.</p>\r\n<p class="bodytext"><span class="bld">Muna yata agga pata kava suca ruca mahāla bhaddāla manai</span>iccevamādīhi dhātūhi, pāṭipadikehi ca ipaccayo hoti.</p>\r\n<p class="bodytext">Atthānatthaṃ munāti, ñeyyadhammaṃ lakkhaṇādivasena vā jānātīti muni, yatati vīriyaṃ karotīti yati, aggati kuṭilabhāvaṃ gacchatīti aggi, patati seṭṭho hutvā purato gacchatīti pati, kabyaṃ bandhatīti kavi, kantaṃ manāpavacanaṃ vadatīti vā kavi. Sucati parisuddhaṃ bhavatīti suci, rucati dibbatīti ruci, mahantaṃ vibhāvaṃ bhogakkhandhaṃ lātīti mahāli, bhaddaṃ yasaṃ lātīti bhaddāli, manaṃ tattha ratane nayatīti maṇi.</p>\r\n<p class="bodytext"><a name="para670"></a><span class="paranum">670</span>, 680. </p>','\r',char(13)),'\n',char(10)),'-668-669-670-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',313,replace(replace('<p class="bodytext"><span class="bld">Vidādīhyūro</span><a name="M0.0313"></a>.</p>\r\n<p class="bodytext"><span class="bld">Vida valla masa sida dukukapu maya udi khajja kura</span>iccevamādīhi dhātūhi, pāṭipadikehi ca <span class="bld">ūra</span>paccayohoti.</p>\r\n<p class="bodytext">Vidituṃ alanti vidūro, vidūraṭṭhāne jāto vedūro, vallati vallabhāvena bhavatīti vallūro, vallati aññamaññaṃ bandhatīti vā vallūro, āmasitabboti masūro, sindati siṅgārabhāvaṃ gacchatīti sindūro, sindati virocatīti vā sindūro, gamituṃ alaṃ anāsannattāti dūro, kuti saddaṃ karotīti kūro, attano gandhena aññaṃ gandhaṃ kapati hanati hiṃsatīti kappūro, kappati rogāpanayane samatthetīti vā kappūro, mahiyaṃ ravatīti mayūro, mahiyaṃ yāti gacchatīti mayūro, paṃsuṃ undati pasavatīti undūro, khajjitabbo khāditabboti khajjūro, kurati akkosatīti kurūro.</p>\r\n<p class="bodytext"><a name="para671"></a><span class="paranum">671</span>, 681. <span class="bld">Hanādīhi nu ṇutavo</span>.</p>\r\n<p class="bodytext"><span class="bld">Hana jana bhāri khanu ama ve vedhā siki hi</span> iccevamādīhi dhātūhi <span class="bld">nu ṇu ku</span>iccete paccayā honti.</p>','\r',char(13)),'\n',char(10)),'-671-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',314,replace(replace('<p class="bodytext">Bhojanaṃ <a name="M0.0314"></a> hanati hiṃsati etenāti haṇu, hanu vā. Gamanaṃ janetītī jāṇu, bhāṇu dibbatīti bhāṇu, nivāte riti gacchatītī reṇu, khaṇitabbo avadāritabboti khāṇu. Aṅgamaṅgassa rujjanabhāvaṃ vijjhanabhāvaṃ amati gacchatīti aṇu, veṇu, veti tantasantāne bhavatītī veṇu, bahisārealantivāveṇu vacchaṃ, pāyetītī dhenu, atthaṃ dhāretītī dhātu, gamanapacanādikaṃ kriyaṃ dhāretītī vā dhātu, sīyatī bandhīyatītī setu, uddhaṃ gacchati pavattatītī ketu, attano phalaṃ hinoti pavattatītī hetu.</p>\r\n<p class="bodytext"><a name="para672"></a><span class="paranum">672</span>, 682. <span class="bld">Kuṭādīhi ṭho</span>.</p>\r\n<p class="bodytext"><span class="bld">Kuṭa kusa kaṭai</span>ccevamādīhi dhātūhi, pāṭipadikehi ca <span class="bld">ṭha</span>paccayo hotī.</p>\r\n<p class="bodytext">Aṅgamaṅgaṃ kuṭati chindatītī kuṭṭhaṃ, dhaññena chādetabbo pūretabbotī koṭṭho, kaṭitabbaṃ madditabbanti, kaṭṭhaṃ.</p>\r\n<p class="bodytext"><a name="para673"></a><span class="paranum">673</span>, 683. <span class="bld">Manu pūra suṇādīhi ussa nusisā</span>.</p>\r\n<p class="bodytext"><span class="bld">Manu pūra suṇa kusu ila ala maha si ki</span> iccevamādīhi dhātūhi, pāṭipadikehi ca <span class="bld">usa nusa isa</span>iccete paccayā honti, puna nippajjante.</p>','\r',char(13)),'\n',char(10)),'-672-673-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_byakarana_gantha_sangaho_e0802n',315,replace(replace('<p class="bodytext">Kusalākusale <a name="M0.0315"></a> dhamme manati jānātīti manusso, mānuso. Kāraṇā kāraṇaṃ manati jānātīti vā manusso, mānusso. Atthānatthaṃ manati jānātīti vā manusso, mānusso. Mātāpitūnaṃ hadayaṃ pūretīti puriso, attano manorathaṃ pūretīti vā puriso, pūretīti vā poso, sasurehi suṇitabbā hiṃsitabbāti suṇisā, dvinnaṃ jānānaṃ kulasantānaṃ karotīti vā suṇisā, kucchitabbanti karīsaṃ, gabbhaṃ vimocetīti suriso, tamandhakāravidhamanena sattānaṃ bhayaṃ surati hiṃsatīti sūriyo, rogaṃ hiṃsatīti sirīso, ilati kampatīti <span class="bld">i</span>lliso, taṇhāya dubbalo hutvā ilati kampatīti vā illigho, pāpakaraṇe alati samatthetīti alaso, mahitabbo pūjetabboti mahiso, sīyati bandhīyatīti sīsaṃ, kitabbaṃ hiṃsitabbanti kisaṃ, iccevamādi.</p>\r\n<p class="centered">Iti kibbidhānakappe uṇādikappo chaṭṭho kaṇḍo.</p>\r\n<p class="centered">Uṇādikappo niṭṭhito.</p>\r\n<p class="centered"><span class="bld">Kaccāyanapakaraṇaṃ niṭṭhitaṃ</span>.</p>','\r',char(13)),'\n',char(10)),'');