INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',1,replace(replace('<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa.</p>\r\n<p class="book">Payogasiddhipāḷi</p>\r\n<p class="chapter">1. Saññādikaṇḍa</p>\r\n<p class="centered">Siddha-mbatthuṃ sammā vande</p>\r\n<p class="subsubhead">Ganthārambhakathā</p>\r\n<p class="gatha1">Siddha+miṭṭhadadaṃ <a name="M0.0001"></a> buddhaṃ, dhammaṃ maṇiṃva svatthadaṃ;</p>\r\n<p class="gathalast">Saṅghañca sādaraṃ natvā, payogasiddhi vuccate.</p>\r\n<p class="gatha1">Pabhāvo moggallānassa, byākaraṇe ca peṭake;</p>\r\n<p class="gathalast">Nisseseva kabbātitto, aho acchariyo vata.</p>\r\n<p class="unindented">Vuttañhi pubbasīhaḷācariya+pācariyehi –</p>\r\n<p class="gatha1">Yā satti pāṇine yā ca, candra+kātyāyanādisu;</p>\r\n<p class="gathalast">Sā+yaṃ muttimatī maññe, moggallāyanarūpinī-ti.</p>\r\n<p class="gatha1">Suttaṃ vutti ca teneva, katā ekena pañcikā;</p>\r\n<p class="gathalast">Tasmā+ssa sattha+maññehi, supasaṭṭhaṃ+tisundaraṃ.</p>\r\n<p class="gatha1">Tamhi ca dubbidhaṃ ñeyyaṃ, suttaṃ kammatthabhedato;</p>\r\n<p class="gathalast">Tesvā+di mītisaddena, dutiyaṃ tippakārato.</p>\r\n<p class="gatha1">Suvisadaṃ pakāsetvā, subodha+mākumārakaṃ;</p>\r\n<p class="gathalast">Byāpikāvaḷiyā kassaṃ, taṃ suṇātha samāhitāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',2,replace(replace('<p class="subhead">1. Aādayo titālīsa vaṇṇā</p>\r\n<p class="bodytext">Akārādayo <a name="M0.0002"></a> niggahītantā tecattālīsakkharā vaṇṇā nāma honti. Taṃ yathā a, ā, i, ī, u, ū, e, ऐ, o, औ, ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, ya ra lava sa ha ḷa aṃ. Tena kvattho ‘‘e ona+ma vaṇṇe’’ti. Titālīsāti vacanaṃ katthaci vaṇṇalopaṃ ñāpeti, tena ‘‘paṭisaṅkhā yoniso’’tiādi siddhaṃ.</p>\r\n<p class="bodytext">Akāro ādi mariyādabhūto yesaṃ te aādayo. Tayo ca cattālīsa ceti titālīsa, iminā nipātanena vā catabhāgalopo. Vaṇṇīyati attho etehīti vaṇṇā.</p>\r\n<p class="bodytext">Etthā+ha – ‘‘kasmā ācariyakaccāyanādīhi viya ekacattālīsakkharāna+makkharasañña+makatvā titālīsakkharānaṃ vaṇṇasaññā katā’’ti. Vuccate –</p>\r\n<p class="gatha1">Sā māgadhī mūlabhāsā, narā yāyā+dikappikā;</p>\r\n<p class="gathalast">Brahmāno cā-sutālāpā, sambuddhā cāpi bhāsareti –</p>\r\n<p class="unindented">Vacanato māgadhikānaṃ ettha, seyyo, oṭṭho, sotthi-tyādīsupi asesabyāpikānaṃ titālīsakkharānaṃva ujukānvatthappakkharaguṇikā, neva pana ‘rukkhā vanaṃ’tyādo viya avayave samudāyavohāro, ‘samuddo mayā diṭṭho’tyādo viya ca samudāye avayavavohāroti dassetuṃ paccekaṃ vaṇṇasaññā katāti.</p>\r\n<p class="bodytext">‘‘Sakkaccasavanaṃ buddhasāsanasampattī’’ti ‘‘sithiladhanitādi akkharavipattiyañhi atthassa dunnayatā hotī’’ti ca yasmā vuttaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',3,replace(replace('<p class="gatha1">Tasmā <a name="M0.0003"></a> akkharakosallaṃ, sampādeyya vicakkhaṇo;</p>\r\n<p class="gathalast">Upaṭṭhahaṃ garuṃ sammā, uṭṭhānādīhi pañcahi.</p>\r\n<p class="bodytext">Tattha akārādīna+manukkamo pane+sa ṭhānādikkamasannissito. Tathā hi ṭhāna+karaṇa+payatanehi vaṇṇā jāyanti. Tattha cha ṭhānāni kaṇṭha+tālu+muddha+danta+oṭṭha+nāsikāvasena.</p>\r\n<p class="bodytext">Tattha avaṇṇa+kavagga+hānaṃ kaṇṭho ṭhānaṃ, ivaṇṇa+cavagga+yānaṃ tālu, ṭavagga+ra+ḷānaṃ muddhā, tavagga+la+sānaṃ dantā, uvaṇṇa+pavaggānaṃ oṭṭho, e vaṇṇassa kaṇṭhatālū, o vaṇṇassa kaṇṭho+ṭṭhā, vakārassa danto+ṭṭhā, niggahītassa nāsikā, ṅa, ñña, ṇa, na, mānaṃ sakaṭhānaṃ nāsikā ca. Ettha ca –</p>\r\n<p class="gatha1">Hakāro pañcameheva, antaṭṭhāhi ca saṃyuto;</p>\r\n<p class="gathalast">Oraso iti viññeyyo, kaṇṭhajo tadasaṃyuto.</p>\r\n<p class="bodytext">Yathā avaṅhoti (avaṅa hoti., tañhi, taṇhā, pubbanho, amhe, guyhaṃ, gārayhā, ārulho, bahvakkharanti.</p>\r\n<p class="bodytext">Karaṇaṃ –</p>\r\n<p class="gatha1">Jivhāmajjhaṃ tālujānaṃ,</p>\r\n<p class="gatha2">Jivhopaggaṃ muddhajānaṃ,</p>\r\n<p class="gatha3">Jivhaggaṃ dantajānaṃ se-</p>\r\n<p class="gathalast">Sā sakaṭhānakaraṇā.</p>\r\n<p class="bodytext">Vaṇṇānaṃ uccāraṇussāho payatanaṃ, taṃ kiṃ – saṃvutādikaraṇaviseso, saṃvutatta+makārassa, vivaṭattaṃ sesasarānaṃ sakāra+hakārānañca, phuṭṭhattaṃ vaggānaṃ, īsaṃphuṭṭhattaṃ ya+ra+la+vānaṃ.</p>\r\n<p class="bodytext">Evaṃ ṭhāna+karaṇa+payatana+suti kālabhinnesu akkharesu sarā nissayā, itare nissitā. Tattha –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',4,replace(replace('<p class="gatha1">Nissayā+do <a name="M0.0004"></a> sarā vuttā, nissitā byañjanā tato;</p>\r\n<p class="gathalast">Vagge+kajā bahuttā+do, tato ṭhāna+lahukkamā.</p>\r\n<p class="bodytext">‘‘Aādayo’’ti vattate yāva ‘‘bindu niggahīta’’nti. Tañca kho ‘‘atthavasā vibhattivipariṇāmo’’ti sattamyanta+mabhisambandhīyate, ‘‘vaṇṇā’’ti vattate –</p>\r\n<p class="subhead">2. Dasā+do sarā</p>\r\n<p class="bodytext">Aādīsvā+dimhi niddiṭṭhā odantā dasa vaṇṇā sarā nāma honti. Yathā a ā, i ī, u ū, e ऐ, o औ. Saranti=sappadhānabhāvena pavattanti, byañjane vā sārentīti sarā.</p>\r\n<p class="bodytext">‘‘Dasā+do sarā’’ti vattate.</p>\r\n<p class="subhead">3. Dve dve savaṇṇā</p>\r\n<p class="bodytext">Aādīsvā+dimesu dasasu dve dve savaṇṇā nāma honti yathākkamaṃ. Yathā a āiti, i īiti, u ū iti, e eiti, o, oiti. Samānā sadisā vaṇṇā savaṇṇā, samānattañca ṭhānato. ‘‘Dve dve’’ti vattate vakkhamānesu dvīsu.</p>\r\n<p class="subhead">4. Pubbo rasso</p>\r\n<p class="bodytext">Tesveva dasasu ye dve dve savaṇṇā, tesu yo yo pubbo, so so rassasañño hoti. Yathā a i u e o. Tesu ‘saṃyogato pubbāva dissanti dve panantimā’ta dassetuṃ tattha sādhuttā tesampi idha saṅgaho, yathā ettha seyyo oṭṭho sotthi. Rassakālayogā tabbantatāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',5,replace(replace('<p class="bodytext">vā <a name="M0.0005"></a> rassā. Rassakālo nāma accharāsaṅghāto akkhinimmilanasaṅkhāto vā kālo, tena ekamatto rasso, dvimatto dīgho, aḍḍhamatto byañjano. Chandasi diyaḍḍhamattampi rassanti gaṇhanti ācariyā.</p>\r\n<p class="subhead">5. Paro dīgho</p>\r\n<p class="bodytext">Aādīsvā+dibhūtesu dasasu ye dve dve savaṇṇā, tesu yo yo paro, so so dīghasañño hoti. Taṃ yathā ā ī ū e o. Dīghakālayogā tabbantatāya vā dīghā.</p>\r\n<p class="subhead">6. Kādayo byañjanā</p>\r\n<p class="bodytext">Aādīsu kādayo niggahītapariyantā tettiṃsa byañjanā nāma honti. Yathā ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, ya ra lava sa ha ḷa aṃ iti. Byañjīyati attho etehīti byañjanā. Kakārādīsva+kāro uccāraṇattho.</p>\r\n<p class="bodytext">‘‘Kādayo’’ti vattate.</p>\r\n<p class="subhead">7. Pañcapañcakā vaggā</p>\r\n<p class="bodytext">Aādīsu kakārādayo makārantā pañcapañcakā vaggā nāma honti. Yathā ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma iti. Pañca pañca parimāṇa+mesaṃ pañcapañcakā. Vajjenti yakārādayoti vaggā. Te pana </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',6,replace(replace('<p class="bodytext">paṭhamakkharavasena <a name="M0.0006"></a> kavagga+cavaggādivohāraṃ gatā kusalattikādayo viya.</p>\r\n<p class="subhead">8. Bindu niggahītaṃ</p>\r\n<p class="bodytext">Akārādīsva+yaṃ vaṇṇo bindumatto, so niggahītasañño hoti. Rassasaraṃ nissāya gahita+muccāritaṃ niggahītaṃ, karaṇaṃ niggahetvā vā.</p>\r\n<p class="gatha1">Karaṇaṃ niggahetvāna, mukhenā+vivaṭena yaṃ;</p>\r\n<p class="gathalast">Vuccate niggahītanti, vuttaṃ bindu sarānugaṃ.</p>\r\n<p class="bodytext">Bahvakkharasaññākaraṇaṃ anvatthasaññatthaṃ, jha+lādayo tu ruḷhīsaññā.</p>\r\n<p class="gatha1">Vaṇṇā sarā savaṇṇā ca, rassā dīghā ca byañjanā;</p>\r\n<p class="gathalast">Vaggā ca niggahītanti, hoti saññāvidhikkamo.</p>\r\n<p class="centered">(Saññāvidhānaṃ.)</p>\r\n<p class="bodytext">Sandhi vuccate –</p>\r\n<p class="bodytext">Loka aggapuggalo, paññā indriyaṃ, tīṇi imāni, no hi etaṃ, bhikkhunī ovādo, mātu upaṭṭhānaṃ, sametu āyasmā, abhibhū āyatanaṃ, dhanaṃ me atthi, sabbe eva, tayo assu dhammā, asanto ettha na dissanti itī+dha sarasaññāyaṃ –</p>\r\n<p class="subhead">26. Saro lopo sare</p>\r\n<p class="bodytext">Sare saro lopanīyo hoti. Saroti kāriyīniddeso, lopoti kāriyaniddeso. Lopo= adassanaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',7,replace(replace('<p class="bodytext">anuccāraṇaṃ<a name="M0.0007"></a>, saroti jātyekavacanavasena vuttaṃ, sareti opasilesikādhārasattamī, tato vaṇṇa kāla byavadhāne kāriyaṃ na hoti, tva+masi, katamā cānanda aniccasaññāti. Evaṃ sabbasandhīsu.</p>\r\n<p class="bodytext">Vidhīti vattate.</p>\r\n<p class="subhead">14. Sattamiyaṃ pubbassa</p>\r\n<p class="bodytext">Therayaṭṭhinyāyena pavattate paribhāsā dubbalavidhino patiṭṭhābhāvato. Sattamīniddese pubbasseva vidhīti pubbasaralopo. Lokaggapuggalo, paññindriyaṃ, tīṇimāni, no hetaṃ, bhikkhunovādo, mātupaṭṭhānaṃ, sametāyasmā, abhibhāyatanaṃ, dhanaṃmatthi, sabbeva, tayassu dhammā, asante+ttha na dissanti. Pubbassa kāriyavidhānā sattamīniddiṭṭhassa paratā+va gamyateti pareti paravacanampi ghaṭate.</p>\r\n<p class="bodytext">Yassa idāni, saññā iti, chāyā iva, iti api, assamaṇī asi, cakkhu indriyaṃ, akataññū asi, ākāse iva, te api, vande ahaṃ, so ahaṃ, cattāro ime, vasalo iti, moggallāno asi bījako, kathā eva kā, pāto evātī+dha pubbassaralope sampatte ‘‘saro lopo sare’’ tveva.</p>\r\n<p class="subhead">27. Paro kvaci</p>\r\n<p class="bodytext">Saramhā paro saro kvaci lopanīyo hoti. Yassa dāni, saññāti, chāyāva, itipi, assamaṇīsi, cakkhundriyaṃ, akataññūsi, ākāseva, tepi, vandehaṃ, sohaṃ, cattārome, vasa- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',8,replace(replace('<p class="bodytext">lobhi<a name="M0.0008"></a>, moggallānosi bījako, kathāva kā, pātova. Kvacīti kiṃ, paññindriyaṃ, pañcindriyāni, sattuttamo, ekūnavīsati, yassete, sugatovādo, diṭṭhāsavo, diṭṭhogho, cakkhāyatanaṃ, taṃ kutettha labbhā. ‘‘Vivakkhāto sandhayo bhavantī’’ti ñāyā vatticchāpi idha sijjhati. Kvacītya+dhikāro sabbasandhīsu, tena nātippasaṅgo. (Lopasandhi).</p>\r\n<p class="bodytext">Saro paro veti ca vattate.</p>\r\n<p class="bodytext">Tassa idaṃ, vāta īritaṃ, na upeti, vāma ūru, ati iva aññehi, vi udakaṃ itīdha pubbassaralope –</p>\r\n<p class="subhead">29. Yuvaṇṇāna+meo luttā</p>\r\n<p class="bodytext">Luttā sarā paresaṃ ivaṇṇuvaṇṇānaṃ e+o honti vā yathākkamaṃ. Yathāsaṃkhyānuddeso samānānaṃ.</p>\r\n<p class="subhead">24. Vaṇṇaparena savaṇṇopi</p>\r\n<p class="bodytext">Vaṇṇasaddo paro yasmā, tena savaṇṇopi gayhati sayañca rūpaṃti īūnampi e+o. Sabbattha rassassa jātiniddese dīghassāpi gahaṇatthaṃ ida+māraddhaṃ. Tassedaṃ, vāteritaṃ, nopeti, vāmoru, atevaññehi, vodakaṃ. Idañca pacchimodāharaṇadvayaṃ ‘‘avaṇṇe lutte eva e+o hontī’’ti gāhassa nisedhanatthaṃ. Vātveva, tassidaṃ. Kathaṃ ‘‘paccorasmi’’nti, yogavibhāgā. Pati urasminti vibhajja ‘‘yavā sare’’ti yakāre ‘‘tavaggavaraṇā’’dinā co, ‘‘vaggalasehi te’’ti pubbarūpañca, ‘‘yuvaṇṇāna+meo’’ti (yogavibhāgā) ussa o ca. Luttāti kiṃ, dasa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',9,replace(replace('<p class="bodytext">ime <a name="M0.0009"></a> dhammā, yathā idaṃ, kusalassa upasampadā. Atippasaṅgabādhakassa kvacisaddassānuvattanato na vikappavidhi niyatā, tena upeto, aveccāti evamādīsu vikappo. Tārakitā, yassindriyāni, mahiddhiko, sabbītiyo, tenupasaṅkami, lokuttarotiādīsu vidhi ca na hoti.</p>\r\n<p class="bodytext">Paṭisanthāravutti assa, sabbavitti anubhūyate, vi añjanaṃ, vi ākato, dāsī ahaṃ, ahuvā pure, anu addhamāsaṃ, anu eti, su āgataṃ, su ākāro, du ākāro, cakkhu āpāthaṃ, bahu ābādho, pātu akāsi, na tu eva, bhū āpanalānilaṃ itī+dha ‘‘yuvaṇṇānaṃ’’ ‘‘ve’’ti ca vattate,</p>\r\n<p class="subhead">30. Yavā sare</p>\r\n<p class="bodytext">Sare pare ivaṇṇuvaṇṇānaṃ yakāra+vakārā honti vā yathākkamaṃ. Paṭisanthāravutyassa, sabbavityanubhūyate, byañjanaṃ, byākato ‘‘byañjane dīgharassā’’ti dīghe dāsyāhaṃ, ahāpure, anvaddhamāsaṃ, anveti, svāgataṃ, svākāro, dvākāro, cakkhvāpāthaṃ, bahvābādho, pātvākāsi, na tveva, bhvāpanalānilaṃ. Vātveva, viākato, sāgataṃ.</p>\r\n<p class="bodytext">Adhigato kho me ayaṃ dhammo, putto te ahaṃ, te assa pahīnā pañca, te ahaṃ, ye assa, te ajja, yāvatako assa kāyo, tāvatako assa byāmo, ko attho, atha kho assa, ahaṃ kho ajja, so ahaṃ, so ajja, so eva, yato adhikaraṇaṃ, so ahaṃ itī+dha ‘‘yavā sare’’ ‘‘ve’’ti ca vattate,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',10,replace(replace('<p class="subhead">30. Eonaṃ</p>\r\n<p class="bodytext">Eonaṃ <a name="M0.0010"></a> yakāra+vakārā honti vā sare pare yathākkamaṃ. ‘‘Byañjane dīgharassā’’ti dīghe adhigato kho myāyaṃ dhammo, putto tyāhaṃ, tyāssa pahīnā pañca, tyāhaṃ, yyassa, tyajja, yāvatakvassa kāyo, tāvatakvassa byāmo, kvattho, atha khvassa, ahaṃ khvajja, svāhaṃ, svajja, sveva, yatvādhikaraṇaṃ, svāhaṃ. Vātveva, tejja, sohaṃ. Kvaci tveva, dhanamatthi, puttāmatthi, te nāgatā, asantettha, cattāro ime.</p>\r\n<p class="bodytext">Go eḷakaṃ, go assaṃ, go ajinaṃ itī+dha ‘‘sare’’ti vattate.</p>\r\n<p class="subhead">32. Gossā+vaṅa</p>\r\n<p class="bodytext">Sare pare gossa avavādeso hoti. Sa ca ‘‘ṭānubandhānekavaṇṇā sabbassā’’ti sabbassa pasaṅge ‘antassā’’ti vattate.</p>\r\n<p class="subhead">18. Ṅa+nubandho</p>\r\n<p class="bodytext">Ṅa-kāro anubandho yassa, so anekavaṇṇopi antassa hotīti okārasseva hoti. ‘‘Saṃketo+navayavo+nubandho’’ti vacanā ṅa-kārassā+ppayogo. Uccāritānantarappadhaṃsino hi anubandhā, payojanaṃ anubandhoti saṃketo. Gaveḷakaṃ, gavāssaṃ, gavājinaṃ.</p>\r\n<p class="bodytext">Iti evā+tī+dha –</p>\r\n<p class="subhead">36. Vī+tisse+ve vā</p>\r\n<p class="bodytext">Evasadde pare itissa vo hoti vā. Sa ca –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',11,replace(replace('<p class="subhead">17. Chaṭṭhiyantassa</p>\r\n<p class="bodytext">Chaṭṭhīniddiṭṭhassa <a name="M0.0011"></a> yaṃ kāriyaṃ, ta+dantassa viññeyyanti ikārassā+deso. Ādesiṭṭhāne ādissatīti ādeso. Itveva. Aññatra yādeso, ‘‘tavaggavaraṇānaṃ ye cavaggabayañā’’ti tassa co, ‘‘vaggalasehi te’’ti yassa cakāro, icceva. Eveti kiṃ, iccāha. (Ādesasandhi).</p>\r\n<p class="bodytext">Ti aṅgulaṃ,ti aṅgikaṃ, bhū ādayo, migī bhantā udikkhatityādisandhayo vuccante. ‘‘Mayadā sare’’ti vattate.</p>\r\n<p class="subhead">45. Vanataragā cā+gamā</p>\r\n<p class="bodytext">Ete mayadā ca āgamā honti vā sare kvaci. Āgamino aniyamepi –</p>\r\n<p class="gatha1">Saroyevā+gami hoti, vanādinantu ñāpakā;</p>\r\n<p class="gathalast">Aññathā hi padādīnaṃ, yukavidhāna+manatthakaṃ.</p>\r\n<p class="bodytext">Etthā+gamā aniyatāgamīnameva bhavanti ce, yakārāgameneva ‘‘nipajja’’nti siddhe ‘‘padādīnaṃ kvacī’’ti byañjanassa yukā+gamo niratthakoti adhippāyo. Tivaṅgulaṃ, tivaṅgikaṃ, bhūvādayo, migī bhantā vudikkhati, pavuccati, pāguññavujutā. Ito nāyati, cinitvā. Yasmātiha, tasmātiha, ajjatagge. Nirantaraṃ, nirālayo, nirindhano, nirīhakaṃ, niruttaro, nirojaṃ, duratikkamo, durāgataṃ, duruttaraṃ, pāturahosi, punarāgaccheyya, punaruttaṃ, punareva, punareti, dhīratthu, pātarāso, caturaṅgikaṃ, caturārakkhaṃ, caturiddhipādapaṭilābho, caturoghanittharaṇatthaṃ, bhatturatthe, vuttiresā, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',12,replace(replace('<p class="bodytext">pathavīdhāturevesā<a name="M0.0012"></a>, nakkhattarājāriva tārakānaṃ, vijjurivabbhakuṭe, āraggeriva sāsapo, usabhoriva, sabbhireva samāsetha. Puthageva, rasse pageva. Lahumessati, gurumessati, idhamāhu, kena te idha mijjhati, bhadro kasāmiva, ākāse mabhipūjaye, ekamekassa, yena midhekacce. Bhātiyeva, hotiyeva, yathāyidaṃ, yathāyeva, māyidaṃ, nayidaṃ, nayidha, chayimāni, navayime dhammā, bodhiyāyeva, pathavīyeva dhātu, tesuyeva, teyeva, soyeva, pāṭiyekkaṃ, viyañjanaṃ, viyākāsi, pariyantaṃ, pariyādānaṃ, pariyuṭṭhānaṃ, pariyesati, pariyosānaṃ, niyāyogo. Udaggo, udayo, udāhaṭaṃ, udito, udīritaṃ, udeti, sakideva, kiñcideva, kenacideva, kismiñcideva, kocideva, sammadatto, sammadaññā vimuttānaṃ, sammadeva, yāvadatthaṃ, yāvadicchakaṃ, yāvadeva, tāvadeva, punadeva, yadatthaṃ, yadantaraṃ, tadantaraṃ, tadaṅgavimutti, etadatthaṃ, attadatthaṃ, tadatthaṃ, sadatthapasuto siyā, aññadatthu, manasādaññā vimuttānaṃ, bahudeva ratti. Vātveva, attaatthaṃ, dvādhiṭṭhitaṃ, pātuahosi. Vavatthitavibhāsattā vādhikārassa byañjanatopi, bhikkhunīnaṃ vuṭṭhāpeyya, ciraṃ nāyati, taṃyeva.</p>\r\n<p class="bodytext">Cha abhiññā, cha aṅgaṃ, cha asīti, cha aṃsā, cha āyatanaṃ itī+dha ‘‘vā sare’’ ‘‘āgamo’’ti ca vattate.</p>\r\n<p class="subhead">49. Chā ḷo</p>\r\n<p class="bodytext">Chasaddā parassa sarassa ḷakāro āgamo hoti vā. Chāti anukaraṇattā ekavacanaṃ. Chaḷabhiññā, chaḷaṅgaṃ, chaḷasīti, chaḷaṃsā, chaḷāyatanaṃ. Vātveva, chaabhiññā. (Āgamasandhi).</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',13,replace(replace('<p class="gatha1">Lopo <a name="M0.0013"></a> adassanaṃ, ṭhāniṃ, ya+māmaddiya dissati;</p>\r\n<p class="gathalast">Ādeso nāma so yā tu, asantuppatti āgamo.</p>\r\n<p class="centered">Sarasandhi.</p>\r\n<p class="bodytext">Kaññā iva, kaññāva iccādi sarasandhinisedho vuccati, pasaṅgapubbako hi paṭisedho. Pubbasarānaṃ lope sampatte ‘‘saro’’ ‘‘ve’’ti ca vattate.</p>\r\n<p class="subhead">28. Na dve vā</p>\r\n<p class="bodytext">Pubbaparassarā dvepi vā kvaci na lupyante. Kaññā iva, kaññeva, kaññāva.</p>\r\n<p class="bodytext">Sāriputta idhekacco, ehi sivaka uṭṭhehi, āyasmā ānando, gāthā abhāsi, devā ābhassarā yathā, tevijjā iddhipattā ca, bhagavā uṭṭhāyāsanā, bhagavā eta+davoca, abhivādetvā ekamantaṃ aṭṭhāsi, gantvā olokento, bhūtavādī atthavādī, yaṃ itthī arahaṃ assa, sāmāvatī āha, pāpakārī ubhayattha tappati, nadī ottharati, ye te bhikkhū appicchā, bhikkhū āmantesi, bhikkhū ujjhāyiṃsu, bhikkhū eva+māhaṃsu, imasmiṃ gāme ārakkhakā, sabbe ime, katame ekādasa, gambhīre odakantike, appamādo amataṃ padaṃ, saṅgho āgacchatu, ko imaṃ pathaviṃ vicessati, āloko udapādi, eko ekāya, cattāro oghā, are ahampi, sace imassa kāyassa, no atikkamo, aho acchariyo, atho anto ca, atha kho āyasmā, atho oṭṭhava+cittakā, tato ā- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',14,replace(replace('<p class="bodytext">mantayi <a name="M0.0014"></a> satthāti evamādayo idha kālabyavadhāneneva sijjhanti. Kvacīti kiṃ, āgatā+ttha, āgatā+mhā, katama+ssa vāro, appassutā+yaṃ puriso, camarī+va, sabbe+va, sve+va, ese+va nayo, parisuddhe+tthā+yasmanto, ne+ttha, kute+ttha labbhā, sace+sa brāhmaṇa, tathū+pamaṃ, yathā+ha, jīvhā+yatanaṃ, avijjo+gho, itthindriyaṃ, abhibhā+yatanaṃ, bhayatū+paṭṭhānaṃ, saddhī+dha vittaṃ purisassa seṭṭhaṃ. (Sarasandhinisedho)</p>\r\n<p class="bodytext">Tatra abhirati, tatra ayaṃ, buddha anussati, sa atthikā, saññāvā assa, tadā ahaṃ, yāni idha bhūtāni, gacchāmi iti, ati ito, kikī iva, bahu upakāraṃ, madhu udakaṃ, su upadhāritaṃ, sopi ayaṃ, idāni ahaṃ, sace ayaṃ, appassuto ayaṃ, itara itarena, saddhā idha vittaṃ, kamma upanissayo, tathā upamaṃ, ratti uparato, vi upasamo, lokassa iti, deva iti, vi atipatanti, vi atināmenti, saṅghāṭi api, jīvitahetu api, vijju iva, kiṃsu idha vittaṃ, sādhu iti, te assa pahīnā, so assa, madhuvā maññati bālo, evaṃ gāme muni care, khanti paramaṃ tapo titikkhā, na maṃku bhavissāmi, su akkhāto, yo ahaṃ, so ahaṃ, kāmato jāyati soko, kāmato jāyati bhayaṃ, sakko uju ca suhujuca, anupaghāto, durakkhaṃ, duramaṃ, dubharatā. Yiṭṭhaṃ vā hutaṃ vā loke, yadi vā sāvake, puggalā dhammadasā te, bhovādī nāma so hoti, yathābhāvī guṇena so, yathā idaṃ, sammā dakkhāto, parā kamo, taṇhā kkhayo, jhānassa lābhī amhi, thullaccayo itī+dha –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',15,replace(replace('<p class="subhead">33. Byañjane dīgharassā</p>\r\n<p class="bodytext">Rassa+dīghānaṃ <a name="M0.0015"></a> kvaci dīgha+rassā honti byañjane. Tatrābhirati, tatrāyaṃ, buddhānussati, sātthikā, saññāvā+ssa, tadāhaṃ, yānī+dha bhūtāni, gacchāmīti, atīto, kikīva, bahūpakāraṃ, madhūdakaṃ, sūpadhāritaṃ, sopāyaṃ, idānāhaṃ, sacāyaṃ, appassutāyaṃ, itarītarena, saddhīdha vittaṃ, kammūpanissayo, tathūpamaṃ, rattūparato, vūpasamo, lokassāti, devāti, vītipatanti, vītināmenti, saṅghāṭīpi, jīvitahetūpi, vijjūva, kiṃsūdha vittaṃ, sādhūti, tyāssa pahīnā, svāssa, madhuvā maññatī bālo, evaṃ gāme munī care, khantī paramaṃ tapo titikkhā, na maṃkū bhavissāmi, svākkhāto, yvāhaṃ, svāhaṃ, kāmato jāyatī soko, kāmato jāyatī bhayaṃ, sakko ujū ca suhujū ca, anūpaghāto, dūrakkhaṃ, dūramaṃ, dūbharatā. Yiṭṭhaṃva hutaṃva loke, yadiva sāvake, puggala dhammadasā te, bhovādi nāma so hoti, yathābhāvi guṇena so, yathayidaṃ, sammadakkhāto, parakkamo, taṇhakkhayo, jhānassa lābhimhi, vasimhi, thullaccayo. Kvacīti kiṃ, tyajja, svassa, patiliyatīti dīghanisedho, māyidaṃ, manasādaññā vimuttānaṃ, yathākkamaṃ, ārakkhātītaṃ, dīyati, sūyatīti rassakāriyanisedho. Kathaṃ yāniva antalikkheti, ‘‘dīgharassā’’ti yogavibhāgā. (Dīgha+rassasandhi).</p>\r\n<p class="bodytext">Byañjaneti vattate.</p>\r\n<p class="subhead">34. Saramhā dve</p>\r\n<p class="bodytext">Saramhā parassa byañjanassa kvaci dve rūpāni honti. Ettha ca āvuttidvivacanaṃ ṭhānedvivacananti dvīsu ṭhānedvivacanaṃ veditabbaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',16,replace(replace('<p class="bodytext">Tāni <a name="M0.0016"></a> ca pa+pati+paṭi+kama+kusa+kudha+kī+gaha+juta+ñā+si+ su+sū+sambhū+sara+sasādīnamādibyañjanānañca hoti. Idha pamādo=idhappamādo, evaṃ appamādo, vippayutto, suppasanno, sammā padhānaṃ=sammappadhānaṃ rassattaṃ. Appativattiyo, adhippatipaccayo, suppatiṭṭhito,. Appaṭipuggalo, vippaṭisāro, suppaṭipatti. Pakkamo, paṭikkamo, hetukkamo, ākamati=akkamati, evaṃ pakkamati, yathākkamaṃ. Akkosati, paṭikkosati, anukkosati, ākosati=akkosati. Akkuddho, abhikkuddho. Dhanakkīto, vikkayo, anukkayo. Paggaho, viggaho, anuggaho, candaggāho, diṭṭhiggāho. Pajjoto, vijjoto, ujjoto. Kataññū, viññū, paññāṇaṃ, viññāṇaṃ, anaññāṇaṃ. Avassayo, nissayo, samussayo. Appassuto, vissuto, bahussuto. Āsavā=assavā. Passambhento, vissambhento. Aṭṭassaro, vissarati, anussarati, anussati. Passasanto, vissasanto, muhussasanto, āsāso=assāso. Ādisaddena avissajento, vissajento, abhikkantataro, pariccajento, upaddavo, upakkileso, mittadduno, āyabyayo, abbahi iccādi.</p>\r\n<p class="bodytext">Tika+taya+tiṃsa+vatādīna+mādibyañjanassa ca. Kusalattikaṃ, pītittikaṃ, hetuttikaṃ. Lokattayaṃ, bodhittayaṃ, vatthuttayaṃ. Ekattiṃsa, dvattiṃsa, tettiṃsa, catuttiṃsa. Sīlabbataṃ, subbato. Sappītiko, samannāgato, punappunaṃ iccādi.</p>\r\n<p class="bodytext">Vatu+vaṭa+disāna+mante, yathā vattati, vaṭṭati, dassanaṃ, phasso iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',17,replace(replace('<p class="bodytext">U+du+niupasagga+ta+catu+cha+santasaddādesādīhi <a name="M0.0017"></a> paresañca. Ukaṃso=ukkaṃso, evaṃ dukkaraṃ, nikkaṅkho, uggataṃ, duccaritaṃ, nijjaṭaṃ, ujjahaṃ, uccaṅgaṃ, unnamati, dukkaro, niddaro, unnato, duppañño, nimmalo, uyyutto, dullabho, nibbatto, ussāho, nissāro. Takkaro, tajjo, tanninno, tappabhavo, tammayo, catukkaṃ, catuddisaṃ, catuppado, catubbidhaṃ, catussālaṃ, chakkaṃ, channavuti, chappadiko, chabbassāni. Sakkāro, sagguṇo, sandiṭṭhi, sappuriso, mahabbalo.</p>\r\n<p class="bodytext">Apadantaākāravajjitadīghato yakārassa ca, niyyāti, suyyati, abhibhuyya, viceyya, vineyya, dheyyaṃ, neyyaṃ, seyyo, jeyyo, veyyākaraṇo. Ākāravajjitanti kiṃ, mālāya, dolāya, samādāya.</p>\r\n<p class="bodytext">Chandānurakkhaṇe-nappajahe vaṇṇabalaṃ purāṇaṃ, ujjugatesu seyyo, gacchanti suggatiṃ. Saramhāti kiṃ, ñāyo, taṃkhaṇaṃ. Kvacitveva, nikāyo, nidānaṃ, nivāso, tato, chasaṭṭhi, upanīyati, sūyati.</p>\r\n<p class="subhead">35. Catutthadutiyesve+saṃ tatiyapaṭhamā</p>\r\n<p class="bodytext">Catutthadutiyesu paresve+saṃ catutthadutiyānaṃ tabbagge tatiyapaṭhamā honti paccāsatyā. Vagge gha+jha+ḍha+dha+bhā catutthā, kha+cha+ṭha+tha+phā dutiyā, ga+ja+ḍa+da+bā tatiyā, ka+ca+ṭa+ta+pā paṭhamā. Pa+u+du-niādīhi paresaṃ ghādīnaṃ dvibhāve tatiyapaṭhamā honti. Pagarati=paggharati, evaṃ uggharati, nigghoso, ugghāṭeti. Esova tajjhānaphalo, paṭhamajjhānaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',18,replace(replace('<p class="bodytext">abhijjhā-yati<a name="M0.0018"></a>, ujjhāyati. Daḍḍho, buḍḍho. Viddhaṃseti, uddhaṃsito, uddhāro, niddhano, niddhuto. Vibbhanto, ubbhanto, samubbhanto, dubbhikkhaṃ, nibbhayaṃ, tabbhāvo, catubbhi. Saddhā, saddhammo. Mahabbhayaṃ.</p>\r\n<p class="bodytext">Rassasarehi paresaṃ vaggadutiyānaṃ dvibhāvo ce, paṭhamā. Pañcakkhandhā, evaṃ rūpakkhandho, akkhamo, abhikkhaṇaṃ, avikkhepo, jātikkhettaṃ, dhātukkhobho, āyukkhayo. Setacchattaṃ, evaṃ sabbacchannaṃ, vicchannaṃ, bodhicchāyā, jambucchāyā, samucchedo. Tatra ṭhito=tatraṭṭhito, evaṃ thalaṭṭhaṃ, jalaṭṭhaṃ, aṭṭhitaṃ, niṭṭhitaṃ, cattāriṭṭhānāni, garuṭṭhāniyo, samuṭṭhito. Sabbatthāmena, yasatthero, pattharati, vitthāro, abhitthuto, vitthambhito, anutthunaṃ. Papphoṭeti, mahapphalaṃ, anipphalaṃ, vipphāro, paripphuṭeyya, madhupphāṇitaṃ. Ākārato, ākhāto=akkhāto, evaṃ taṇhākkhayo, āṇākkhettaṃ, saññākkhandho. Āchādayi=acchādayi, evaṃ acchindati, nāvaṭṭhaṃ, attharati, apphoṭeti. Kvaci tveva, puvakhajjakaṃ, tassa chaviādīni chinditvā, yathāṭhitaṃ, kammaphalaṃ, sīlaṃ tassa jhāyino, ye jhānappasutā dhīrā, nidhanaṃ, mahādhanaṃ. (Dvibhāvasandhi).</p>\r\n<p class="bodytext">Akaramha se te, so kho byanti kāhiti, so gacchaṃ na nivattati, eso attho, eso ābhogo, eso idāni itī+dha ‘‘ve’’ti vattate.</p>\r\n<p class="subhead">37. Eona+ma vaṇṇe</p>\r\n<p class="bodytext">Eonaṃ vaṇṇe kvaci a hoti vā. Akaramha sa te, akaramha se te, evaṃ sa kho byanti kāhiti, sa gacchaṃ na nivattati, esa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',19,replace(replace('<p class="bodytext">attho<a name="M0.0019"></a>, esa ābhogo, esa idāni. Vaṇṇeti kiṃ, amoghavacano ca so, gandhabbānaṃ adhipati, mahārājā yasassi soti. ‘‘Na sandhisamāsā vaddhassā’’ti vuttattā gāthāmajjhe sandhi na hotīti ‘‘tividhassā’’ti vuttatimhi parepi vaṇṇo paro nāma na iti. (Sarabyañjanasandhi).</p>\r\n<p class="bodytext">Ata yantaṃ, tatha yaṃ, mada yaṃ, budha yati, dhana yaṃ, seva yo, para yesanā, pokkharaṇa yo itī+dha –</p>\r\n<p class="subhead">48. Tavaggavaraṇānaṃ ye cavagga bayañā</p>\r\n<p class="bodytext">Tavaggavaraṇānaṃ cavaggabayañā honti yathākkamaṃ yakāre. ‘‘Vaggalasehi te’’ti pubbarūpaṃ. Accantaṃ, tacchaṃ, majjaṃ, bujjhati, dhaññaṃ, sebbo, payyesanā, pokkharañño. Apuccaṇḍakāyaṃ, jaccandho, yajjevaṃ, ajjhagamā, ajjhattaṃ, ajjhupagato, ajjhogāhetvā, dibbaṃ. Kvacitveva, ratyā.</p>\r\n<p class="bodytext">Saka yate, ruca yate, paca yate, aṭa yate, lupa yate, kupa yate, sala yate, phala yate, disa yate, asa yate itī+dha ‘‘ye’’ti vattate vakkhamānesu dvīsu.</p>\r\n<p class="subhead">49. Vaggalasehite</p>\r\n<p class="bodytext">Vaggalasehi parassa yakārassa kvaci te vaggalasā honti. Sakkate, ruccate, paccate, aṭṭate, luppate, kuppate, sallate, phallate, dissate, assate. Kvacitveva, kyāhaṃ.</p>\r\n<p class="bodytext">Muha yati, guha yati itī+dha –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',20,replace(replace('<p class="subhead">50. Hassa vipallāso</p>\r\n<p class="bodytext">Hassa <a name="M0.0020"></a> vipallāso hoti yakāre. Muyhati, guyhati.</p>\r\n<p class="bodytext">Bahu ābādho itī+dha ussavakāre ‘‘hassa vipallāso’’ti vattate.</p>\r\n<p class="subhead">51. Ve vā</p>\r\n<p class="bodytext">Hassa vipallāso hoti vā vakāre. Bavhābādho. Vātveva, bahvābādho.</p>\r\n<p class="subhead">52. Tathanarānaṃ ṭaṭhaṇalā</p>\r\n<p class="bodytext">Tathanarānaṃ ṭaṭhaṇalā honti vā yathākkamaṃ. Dukkataṃ=dukkaṭaṃ, evaṃ sukaṭaṃ, patthaṭo, pataṭo, uddhaṭo, visaṭo. Aṭṭhakathā. Paṇidhānaṃ, paṇipāto, paṇāmo, paṇītaṃ, pariṇato, pariṇāmo, duṇṇayo, niṇṇayo, oṇato. Paripanno=palipanno, evaṃ palibodho, pallaṅkaṃ, taluno, mahāsālo, māluto, sukhumālo. (Byañjanasandhi).</p>\r\n<p class="bodytext">Cakkhu udapādi, akkhi rujati, purima jāti, aṇu thūlāni, kattabba kusalaṃ bahuṃ, ta sampayuttā, tata sabhāvato itī+dha ‘‘ve’’ti vattate yāva ‘‘mayadā sare’’ti.</p>\r\n<p class="subhead">38. Niggahītaṃ</p>\r\n<p class="bodytext">Niggahītāgamo hoti vā kvaci. Sāmatthiyenā+gamova, sa ca rassasarasseva hoti… tassa rassasarānugatattā. Ṭhānīna+māliṅghiya gacchati pavattatīti āgamo. Cakkhuṃ udapādi, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',21,replace(replace('<p class="bodytext">akkhiṃ <a name="M0.0021"></a> rujati, purimaṃ jāti, aṇuṃ thūlāni, kattabbaṃ kusalaṃ bahuṃ, taṃsampayuttā, taṃtaṃsabhāvato. Vāggahaṇena cakkhu udapādi iccādi. Avaṃsiro, yāvañcidaṃtiādi niccaṃ… vavatthitavibhāsattā vādhikārassa, vavatthitassa lakkhaṇassā+nurodhena lakkhaṇe pavattitā vibhāsā vavatthitavibhāsā. Vāsaddo hi atthadvaye vattate katthaci vikappe, katthaci yathāvavatthitarūpapariggaheti. Yadā pacchime, tadā nicca+manicca+masantañca vidhiṃ dīpeti. Ettha pana kvacisaddassā+nuvattanā tenevā+santavidhi siddhoti vāsaddeni+taradvayaṃ. Kvaci tveva, na hi etehi, idha ceva.</p>\r\n<p class="bodytext">Saṃ rambho, saṃ ratto, saṃ rāgo, tāsaṃ ahaṃ santike, evaṃ ayaṃ, puṃ liṅgaṃ, kiṃ ahaṃ, tassa adāsiṃ ahaṃ itī+dha ‘‘niggahītā’’dhikāro ā ‘‘mayadā sare’’ti.</p>\r\n<p class="subhead">39. Lopo</p>\r\n<p class="bodytext">Niggahītassa lopo hoti vā kvaci. Dīghe sārambho saṃrambho, sāratto saṃratto, sārāgo saṃrāgo, pubbassaralope tāsāhaṃ santike, evāyaṃ, dvitte pulliṅgaṃ puṃliṅgaṃ, kyāhaṃ, tassa adāsahaṃ. Paṭisallāno, sallekho, pātukāmo, gantumano, ariyasaccāna dassanaṃ, etaṃ buddhāna sāsanaṃ, avisāhāro, cirappavāsintiādīsu niccaṃ. Kvacīti kiṃ, eva+mayaṃ, ki+mahaṃ, etaṃ maṅgala+muttamaṃ.</p>\r\n<p class="bodytext">Kataṃ iti, abhinanduṃ iti, uttattaṃ iva, cakkaṃ iva, kaliṃiva, halaṃ idāni, kiṃ idāni, tvaṃ asi, idaṃ api, uttariṃ api, dātuṃ api, sadisaṃ eva itī+dha –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',22,replace(replace('<p class="subhead">40. Parasarassa</p>\r\n<p class="bodytext">Niggahītamhā <a name="M0.0022"></a> parasarassa lopo hoti vā kvaci. Katanti, abhinandunti, uttattaṃva, cakkaṃva, kaliṃva, halaṃdāni, kiṃdāni, tvaṃsi, idampi, uttarimpi, dātumpi, sadisaṃva. Vāti kiṃ, kataṃiti, kimiti, dātumapi, sadisaṃ eva.</p>\r\n<p class="bodytext">Pupphaṃ assā uppajjati, evaṃ assa te āsavā itī+dha parassaralope –</p>\r\n<p class="subhead">53. Saṃyogādilopo</p>\r\n<p class="bodytext">Abyavahitānaṃ dvinnaṃ byañjanānaṃ ekatra ṭhiti saṃyogo, tasmiṃ saṃyoge yo ādibhūtāvayavo, tassa vā kvaci lopo hotītiādibyañjanassa lopo. Pupphaṃsā uppajjati, evaṃsa te āsavā. Tiṇṇaṃ saṃyogānaṃ visaye agyāgāraṃ, vutyassa iti hoti.</p>\r\n<p class="bodytext">Taṃ karoti, taṃ khaṇaṃ, saṃ gato, taṃ ghataṃ, dhammaṃ care, taṃ channaṃ, taṃ jātaṃ, taṃ jhānaṃ, taṃ ñāṇaṃ, taṃ ṭhānaṃ, taṃ ḍahati, taṃ tanoti, taṃ thiraṃ, taṃ dānaṃ, taṃ dhanaṃ, taṃ niccutaṃ, taṃ patto, taṃ phalaṃ, tesaṃ bodho, saṃ bhūto, taṃ mittaṃ, kiṃ kato, dātuṃ gato itī+dha –</p>\r\n<p class="subhead">41. Vagge vagganto</p>\r\n<p class="bodytext">Niggahītassa kho vagge vagganto vā hotīti nimittānussarānaṃ paccāsatyā tabbaggapañcamo hoti. Paccāsatti nāma ṭhānato āsannatā guṇato vā, guṇatoti vaṇṇasaññādiguṇato. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',23,replace(replace('<p class="bodytext">Niggahītassa <a name="M0.0023"></a> anusarīyatīti pacchā katvā sarīyatīti anussarotipi vuccati. Taṅkaroti=taṃ karoti, evaṃ taṅkhaṇaṃ, saṅgato, taṅghataṃ. Dhammañcare, tañchannaṃ, tañjātaṃ, tañjhānaṃ, taññāṇaṃ. Taṇṭhānaṃ, taṇḍahati. Tantanoti, tanthiraṃ, tandānaṃ, tandhanaṃ, tanniccutaṃ, tampatto, tamphalaṃ, tesammodho, sambhūto, tammittaṃ. Kiṅkato, dātuṅgato, taṇhaṅkaro, raṇañjaho, saṇṭhito, jutindharo, sammatotiādīsu niccaṃ.</p>\r\n<p class="bodytext">Ānantarikaṃ ya+māhu, yaṃ yadeva, paccattaṃ eva, taṃ hiitī+dha –</p>\r\n<p class="subhead">42. Yevahisu ño</p>\r\n<p class="bodytext">Ya+eva+hisaddesu niggahītassa vā ño hoti. ‘‘Vaggalasehi te’’ti yassa ñakāro. Ānantarikañña+māhu=ānantarikaṃ ya+māhu, yaññadeva=yaṃyadeva, ñassa dvitte paccattaññeva, paccattaṃ eva, tañhi, tañhi. ‘‘Abyabhicārinā byabhicārī niyamyate’’ti ñāyā evasaddasahacariyā ‘‘ya’’ iti sabbādiyasaddasseva gahaṇaṃ.</p>\r\n<p class="bodytext">Saṃyogo, saṃyojanaṃ, saṃyato, saṃyācikāya itī+dha –</p>\r\n<p class="subhead">43. Ye saṃssa</p>\r\n<p class="bodytext">Saṃsaddassa yaṃ niggahītaṃ, tassa vā ño hoti yakāre. Saññogo=saṃyogo, evaṃ saṃyojanaṃ, saññato, saññācikāya. Idha yakāramattova gayhate. Saṃssāti kiṃ, etaṃ yojanaṃ, taṃ yānaṃ, taṃ saraṇaṃ yanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',24,replace(replace('<p class="bodytext">Taṃ <a name="M0.0024"></a> eva, taṃ ahaṃ brūmi, yaṃ āhu, dhanaṃ eva, kiṃ etaṃ, nindituṃ arahati, taṃ idaṃ, yaṃ aniccaṃ, taṃ anattā, etaṃ avoca, etaṃ eva itī+dha –</p>\r\n<p class="subhead">44. Mayadā sare</p>\r\n<p class="bodytext">Niggahītassa ma ya dā honti vā sare kvaci. Tameva taṃ eva, tamahaṃ brūmi=taṃ ahaṃ brūmi, yamāhu, dhanameva, kimetaṃ, ninditumarahati. Tayidaṃ. Yadaniccaṃ, tadanattā, etadavoca, etadeva. ‘‘Mayadā’’ti yogavibhāgā buddhama saraṇama iccādi bhavati.</p>\r\n<p class="subhead">47. Tadaminādīni</p>\r\n<p class="bodytext">Tadaminādīni nippajjanti. ‘‘Ya+dalakkhaṇikaṃ, taṃ nipātanā’’ti ñāyā lakkhaṇantarena avihitā desa+lopā+gama+vipallāsā, sabbattha imināva daṭṭhabbā. Idañca paresaṃ pisodarādimiva daṭṭhabbaṃ. Phusitaṃ=jalabindu, phusita+mudara+massa pisodaraṃ. Issa akāre taṃ iminā=tadaminā, sakiṃ āgāmī=sakadāgāmī, dhassa dakāre ekaṃ idha ahaṃ=eka+midā+haṃ, vidhassa vidādeso saṃvidhāya avahāro=saṃvidāvahāro, vārisaddassa vakāre, hassa lakāre ca kate vārivāhako=valāhako, jīvanassa jīādeso, jīvanassa muto=jīmūto. Chavassa suādese, sayanassa sānādese ca kate chavassa sayanaṃ=susānaṃ. Uddhassa uduādese, khassa khalaādese ca ‘‘saramhā dve’’ti dvittādimhi ca kate uddhaṃ khaṃ assa udukkhalaṃ. Pisitassa piādese, asassa sācādese ca kate pisitāso=pisāco.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',25,replace(replace('<p class="bodytext">Mahīsaddassa <a name="M0.0025"></a> mayūādese, ravatissa rādese ca kate mahiyaṃ ravatīti mayūro. Eva+maññepi payogato+nugantabbā. Ettha ca –</p>\r\n<p class="gatha1">Vaṇṇāgamo vaṇṇavipariyāyo,</p>\r\n<p class="gatha2">Dve cā+pare vaṇṇavikāra+nāsā;</p>\r\n<p class="gatha3">Dhātussa atthātisayena yogo,</p>\r\n<p class="gathalast">Ta+duccate pañcavidhaṃ niruttaṃti.</p>\r\n<p class="bodytext">Yathā dvāre niyutto=dovārikoti okaāgamo. Hiṃsasaddassa sīhoti vipallāso. Nijako=niyakoti vikāro. Mehanassa khassa mālā mekhalāti vaṇṇalopo, ha+na+makārānaṃ lopo. Mayūroti atthe ravatissa atissayayogoti.</p>\r\n<p class="bodytext">Yathariva tathariveti nipātāva. ‘‘Jaraggavā vicintesuṃ, vara+mhākaṃ bhusāmive’’ti ettha ivasaddo evakārattho. Niggahītasandhi.</p>\r\n<p class="gatha1">Sandhissarānaṃ paṭisedhasandhi,</p>\r\n<p class="gatha2">Atho byañjana+sarabyañjanānaṃ;</p>\r\n<p class="gatha3">Sandhi ca+tho niggahītassa sandhi,</p>\r\n<p class="gathalast">Bhavanti sandhi pana pañcadhā ve.</p>\r\n<p class="centered">Iti payogasiddhiyaṃ sandhikaṇḍo paṭhamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',26,replace(replace('<p class="chapter">2. Nāmakaṇḍa</p>\r\n<p class="bodytext">Atha <a name="M0.0026"></a> nāmāni vuccante. Taṃ atthābhimukhaṃ namanato, attanā ca+tthassa nāmanato nāmaṃ, dabbābhidhānaṃ. Taṃ duvidhaṃ saliṅgā+liṅgato, anvattharuḷhito ca, tividhaṃ pumi+tthi+napuṃsakaliṅgato, rukkho, latā, vananti. Catubbidhaṃ sāmañña+guṇa+kriyā+yadicchānāmato, rukkho, nīlo, pācako, sirivaḍḍhoti. Aṭṭhavidhaṃ avaṇṇi+vaṇṇu+vaṇṇo+kāra+niggahītantapakatibhedato, ettha kiṃsaddo niggahītanto. Paccayā paṭhamaṃ karīyatīti pakati, saddo, dhātu ca.</p>\r\n<p class="bodytext">Tattha saliṅgesu tāva akārantato pulliṅgā sugatasaddā satta vibhattiyo parā yojīyante. Sugataiti ṭhite –</p>\r\n<p class="subhead">1. Dve dve+kānekesu nāmasmā siyo aṃyo nā hi sa naṃ smā hi sa naṃ smiṃ su</p>\r\n<p class="bodytext">Etesaṃ dve dve honti ekānekatthesu vattamānato nāmasmā yathākkamaṃ. Yato ime satta dukā honti, ‘‘atthavanta+madhātuka+mapaccayaṃ pāṭipadikaṃ kitaka+taddhita+samāsā ce’’ti vuttattā taṃ nāmaṃ pāṭipadikaṃ nāma. Keci sakattha+dabba+liṅga+saṅkhyā+kammādipañcakaṃ pāṭipadikanti vadanti. Tene+taṃ vuccati –</p>\r\n<p class="gatha1">Sakattha+dabba+liṅgāni, saddattha+mabravuṃ pare;</p>\r\n<p class="gathalast">Saṅkhyā+kammādikānantu, vibhatti vācakā matā.</p>\r\n<p class="gatha1">Sakattha+dabba+liṅgāni, saṅkhyā+kammādipañcakaṃ;</p>\r\n<p class="gathalast">Saddattha+mabravuṃ keci, vibhatti pana jotakāti ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',27,replace(replace('<p class="bodytext">Tato <a name="M0.0027"></a> ekamhi vattabbe ekavacanaṃ bahumhi vattabbe bahuvacanañcāti aniyamena pasaṅge ‘‘nāmasmā’’ti adhikāro.</p>\r\n<p class="subhead">37. Paṭhamā+tthamatte</p>\r\n<p class="bodytext">Nāmassā+bhidheyyamatte paṭhamāvibhatti hotīti vatticchāvasā paṭhamāye+kavacanabahuvacanāni. Si yoiti paṭhamā. Sissi+kārassā+nubandhattā appayogo. Payojanaṃ ‘‘ki+maṃsisū’’ti saṃketo, tathā aṃvacanassā+kārassa. Ettha tathāti vuttassātideso aññadīyadhammāna+maññatthapāpana+matideso. Ekamhi vattabbe paṭhamekavacanaṃ si.</p>\r\n<p class="bodytext">Atoti vattate, atoti nāmavisesanattā ‘‘vidhibbisesanantassā’’ti paribhāsato akārantato nāmasmā vidhi.</p>\r\n<p class="subhead">109. Sisso</p>\r\n<p class="bodytext">Akārantato nāmasmā sissa o hoti. Pubbasaralope sugato tiṭṭhati. Bahumhi vattabbe bahuvacanaṃ yo. Evaṃ uparipi yojetabbaṃ.</p>\r\n<p class="subhead">41. Ato yonaṃ ṭāṭe</p>\r\n<p class="bodytext">Akārantato nāmasmā paṭhamādutiyāyonaṃ ṭāṭe honti yathākkamaṃ. Ṭakārānubandhattā ‘‘ṭānubandhā+nekavaṇṇā sabbassā’’ti sabbādeso. Sugatā tiṭṭhanti.</p>\r\n<p class="bodytext">‘‘Paṭhamā+tthamatte’’ti vattate.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',28,replace(replace('<p class="subhead">38. Āmantaṇe</p>\r\n<p class="bodytext">Āmantaṇādhike <a name="M0.0028"></a> atthamatte paṭhamāvibhatti hotīti ekasmiṃ ekavacanaṃ si.</p>\r\n<p class="subhead">112. Go syā+lapane</p>\r\n<p class="bodytext">Ālapane si gasañño hoti.</p>\r\n<p class="bodytext">‘‘Lopo’’ti vattate.</p>\r\n<p class="subhead">117. Gasīnaṃ</p>\r\n<p class="bodytext">Nāmasmā ga+sīnaṃ lopo hoti. Bho sugata ciraṃ tiṭṭha.</p>\r\n<p class="bodytext">‘‘Ge’’ti vattate.</p>\r\n<p class="subhead">59. Ayunaṃ vā dīgho</p>\r\n<p class="bodytext">Aiuiccetesaṃ vā dīgho hoti ge pare tiliṅge+ti dīghe bho sugata sugatā ciraṃ tiṭṭha. Sakkate sugatāti dīghaṃ dūrālapaneyevi+cchanti, samīpālapanepi dassanato taṃ na gahetabbaṃ. Bahuvacane yossa ṭā, sugatā ciraṃ tiṭṭhatha.</p>\r\n<p class="subhead">2. Kamme dutiyā</p>\r\n<p class="bodytext">Tasmiṃ kammakārake dutiyāvibhatti hoti. Aṃyoiti dutiyā. Ettha dutiyātatiyādibhāvo vibhattisutte siyo iti paṭhamāvibhatyādīni+mupādāya vuccati, taṃ taṃ upādāya paññattattā. Dutiyekavacanaṃ aṃ, akārassā+payogo. Sugataṃ passa. Dutiyābahuvacanaṃ yo, tassa ṭe, sugate passa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',29,replace(replace('<p class="subhead">19. Kattukaraṇesu tatiyā</p>\r\n<p class="bodytext">Tasmiṃ <a name="M0.0029"></a> kattari karaṇe ca kārake tatiyāvibhatti hoti. Nā+hiiti tatiyāvibhatti. Tatiyāekavacanaṃ nā.</p>\r\n<p class="bodytext">‘‘Nāssā’’ti vattate.</p>\r\n<p class="subhead">108. Ate+na</p>\r\n<p class="bodytext">Akārantato nāmasmā parassa nāvacanassa enādeso hoti niccaṃ. Sugatena kataṃ.</p>\r\n<p class="subhead">98. Suhisva+sse</p>\r\n<p class="bodytext">Akārantassa suhisve+hoti. Sugatehi.</p>\r\n<p class="bodytext">‘‘Ve’’ti vattate.</p>\r\n<p class="subhead">95. Smāhismiṃnaṃ mhābhimhi</p>\r\n<p class="bodytext">Nāmasmā paresaṃ smāhismiṃnaṃ mhābhimhi honti yathākkamaṃti hissa bhiādese sugatebhi. Karaṇe sugatena loko puññaṃ karoti, sugatehi sugatehi vā.</p>\r\n<p class="subhead">24. Catutthī sampadāne</p>\r\n<p class="bodytext">Tasmiṃ sampadānakārake catutthī siyā. Sa+naṃiti catutthī. Catutthe+kavacanaṃ sa. Vibhattisutte ssa+naṃti dīghapāṭhena sugatassāti siddhepi ‘‘jhalā sassa no’’ tyādikāriyasuttesu ssassāti akkharagāravatā hotīti lāghavattha+mida+māraddhaṃ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',30,replace(replace('<p class="subhead">51. Suña sassa</p>\r\n<p class="bodytext">Nāmasmā <a name="M0.0030"></a> parassa sassa suñāgamo hoti. Sa ca ‘‘chaṭṭhiyā’’ti vattamāne –</p>\r\n<p class="subhead">1,20. Ñākānu bandhā+dyantā</p>\r\n<p class="bodytext">Chaṭṭhīniddiṭṭhassa ñānubandha+kānubandhā ādyantāhontīti ādyavayavo. Ukāro uccāraṇattho, ññakāro asmiṃ sutte saṃketattho. Sugatassa dānaṃ deti.</p>\r\n<p class="subhead">1,58. ‘‘Bahulaṃ’’tya+dhikāro</p>\r\n<p class="bodytext">Bahulādhikāraṃ kappadumamiva maññanti saddikā. Tañca –</p>\r\n<p class="gatha1">Kvaci pavattya+pavatti, kvaca+ññaṃ kvaci vā kvaci;</p>\r\n<p class="gathalast">Siyā bahulasaddena, vidhi sabbo yathāgamaṃti –</p>\r\n<p class="bodytext">Catubbidhaṃ bahulaṃ samikkhanti.</p>\r\n<p class="bodytext">‘‘Ato vā’’tveva,</p>\r\n<p class="subhead">44. Sassāya catutthiyā</p>\r\n<p class="bodytext">Akārantato parassa catutthiyā sassa āyo hoti vā bahulaṃ. Sugatāya. Yebhuyyena tādattheyevā+ya+māyo dissatīti ito paraṃ no+dāharīyate. Catutthībahuvacanaṃ naṃ,</p>\r\n<p class="bodytext">‘‘Dīgho’’ti vattate.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',31,replace(replace('<p class="subhead">89. Sunaṃhisu</p>\r\n<p class="bodytext">Nāmassa <a name="M0.0031"></a> dīgho hoti sunaṃhisu. Sugatānaṃ.</p>\r\n<p class="subhead">29. Pañcamya+vadhismā</p>\r\n<p class="bodytext">Etasmā avadhikārakā pañcamīvibhatti hoti. Smā+hiiti pañcamī. Pañcamyekavacanaṃ smā,</p>\r\n<p class="bodytext">‘‘Ato’’ ‘‘ṭāṭe’’ ‘‘ve’’ti ca vattate.</p>\r\n<p class="subhead">43. Smā+smiṃnaṃ</p>\r\n<p class="bodytext">Akārantato nāmasmā paresaṃ smā+smiṃnaṃ ṭā+ṭe honti vā yathākkamaṃ. Sugatā apehi sugatamhā sugatasmā vā. Pañcamībahuvacanañhi, sugatebhi sugatehi.</p>\r\n<p class="subhead">39. Chaṭṭhī sambandhe</p>\r\n<p class="bodytext">Kārakehi añño sambandho, tatra chaṭṭhīvibhatti hoti. Sa+naṃiti chaṭṭhī, chaṭṭhekavacanaṃ sa, sugatassa vihāro, chaṭṭhībahuvacanaṃ naṃ, sugatānaṃ.</p>\r\n<p class="subhead">14. Sattamyā+dhāre</p>\r\n<p class="bodytext">Ādhārakārake sattamīvibhatti hoti. Smiṃ+suiti sattamī. Sattamyekavacanaṃ smiṃ, sugate patiṭṭhitaṃ sugatamhi sugatasmiṃ vā. Sattamībahuvacanaṃ su, ‘‘su+hisva+sse’’ti e, sugatesu.</p>\r\n<p class="bodytext">Sugato, sugatā. Bho sugata, bho sugatā, bhavanto sugatā. Sugataṃ, sugate. Sugatena, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',32,replace(replace('<p class="bodytext">sugatebhi<a name="M0.0032"></a>, sugatehi. Karaṇe sugatena, sugatebhi, sugatehi. Sugatassa, sugatāya, sugatānaṃ. Sugatā, sugatamhā, sugatasmā, sugatebhi, sugatehi. Sugatassa, sugatānaṃ. Sugate, sugatamhi, sugatasmiṃ, sugatesu.</p>\r\n<p class="bodytext">Sugato sugato. Sugataṃ namati. Sugatena kato. Sugatena jito. Sugatassa dade. Sugatā vigato. Sugatassa suto. Sugate ramate. Evaṃ –</p>\r\n<p class="gatha1">Sūrā+sura+naro+raga+nāga+yakkhā,</p>\r\n<p class="gatha2">Gandhabba+kinnara+manussa+pisāca+petā;</p>\r\n<p class="gatha3">Mātaṅga+jaṅgama+turaṅga+varāha+sīhā,</p>\r\n<p class="gathalast">Byaggha+ccha+kacchapa+taraccha+miga+ssa+soṇā.</p>\r\n<p class="gatha1">Āloka+loka+nilayā+nila+cāga+yogā,</p>\r\n<p class="gatha2">Vāyāma+gāma+nigamā+gama+dhamma+kāmā;</p>\r\n<p class="gatha3">Saṅgho+gha+ghosa+paṭighā+sava+kodha+lobhā,</p>\r\n<p class="gathalast">Sārambha+thambha+mada+māna+pamāda+makkhā.</p>\r\n<p class="gatha1">Punnāga+pūga+panasā+sana+campaka+mba-</p>\r\n<p class="gatha2">Hintāla+tāla+vakula+jjuna+kiṃsukā ca;</p>\r\n<p class="gatha3">Mandāra+kunda+pucimanda+karañja+rukkhā,</p>\r\n<p class="gathalast">Ñeyyā mayūra+sakuṇa+ṇḍaja+koñca+haṃsā.</p>\r\n<p class="bodytext">Sugatasaddova, yato sabbo saddo, na saddatālitatthova, atha kho saṃyogādivasenapi atthaṃ vadanti. Tene+taṃ vuccati –</p>\r\n<p class="gatha1">Saṃyogā vippayogā ca, sāhacariyā+virodhato;</p>\r\n<p class="gathalast">Atthā pakaraṇā liṅgā, saddantarasamīpato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',33,replace(replace('<p class="gatha1">Sāmatthyo+citra+desehi<a name="M0.0033"></a>, kāla+byattā+nurūpato;</p>\r\n<p class="gathalast">Upacāra+kākubheda, sambandhehu+palakkhaṇā.</p>\r\n<p class="gatha1">Vacanā ca tadaṅgattā, padhānattātiādihi;</p>\r\n<p class="gathalast">Sadda+tthā pavibhajjante, na saddādeva kevalāti.</p>\r\n<p class="bodytext">Ettha saṃyogato tāva, ‘‘sakisorā dhenu dīyatū’’ti, kisoro assapotako, taṃsaṃyogato vaḷavā eva patīyate.</p>\r\n<p class="bodytext">Vippayogato – ‘‘akisorā ānīyatū’’ti tappaṭisedhā vaḷavā eva patīyate.</p>\r\n<p class="bodytext">Sahacaraṇato – ‘‘rāma+lakkhaṇa’’iti ubhinnaṃ sahacaraṇena rāmoti dāsarathi eva rāmo, na aññābhidhāno jāmadaganyādi. Lakkhaṇopi somitti eva, na tu yo koci lakkhaṇo.</p>\r\n<p class="bodytext">Virodhato – ‘‘rāma+jjunā’’ iti bhaggavo sahassabāhu ca aññamaññaviruddhāti te eva patīyante, na dāsarathi sabyasāci ca.</p>\r\n<p class="bodytext">Atthato – ‘‘sindhava+mānaya, pavisāmi raṇaṅgaṇa’’miti raṇaṅgaṇapaveso vāhanavisesena hotīti atthato turaṅgapatīti, na tu lavaṇavisesaṃ.</p>\r\n<p class="bodytext">Pakaraṇato-bhojanavidhimhi upasaṅkhariyamāne ‘‘sindhava+mānaye’’ti, atra hi saddantarassā+bhāvepi bhojanopakaraṇasamavāya+mālokitabhāvato lavaṇe paṭipatti, tādiso hi patthāvoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',34,replace(replace('<p class="bodytext">Liṅgato <a name="M0.0034"></a> – ‘‘devadattaṃ paṭhama+mupavesaya samārādhitaguruṃ’’ti, atra samārādhitaguruttena liṅgena tassa bāhussacca+mavagamyate, na tu yo koci devaguṇo.</p>\r\n<p class="bodytext">Sannidhānato – ‘‘ajjuno katavīriyoti’’ patīyate, no akatavīriyo ajjunoti.</p>\r\n<p class="bodytext">Sāmatthiyato – ‘‘anudarā kaññā’’ti udare asati kaññā eva natthīti tassā kisāṅgiyā majjhapadesoti patīyate.</p>\r\n<p class="bodytext">Ocitrato – ‘‘rāmasadiso+yaṃ’’ iti, atra hi rāmo payuttadāsarathismiṃ bhiyyo sādhāraṇo paricayoti dāsarathi eva patīyate, na bhaggavarāmo.</p>\r\n<p class="bodytext">Desato – ‘‘poṭṭhapā’’ iti kismiñci dese pasaṃsāvacanaṃ. Kismiñci akkosavacanaṃ.</p>\r\n<p class="bodytext">Kālato – ‘‘pace’’ti dakkhiṇāpathe katthaci pubbaṇhe yāgupāke, sāyaṇhe tu odanapāke.</p>\r\n<p class="bodytext">Byattito – ‘‘gāmassa addha’’ miti samabhāge, napuṃsakattā. ‘‘Gāmassa addho’’ti pumattena tu asamabhāge.</p>\r\n<p class="bodytext">Anurūpato – ‘‘narapati sādhu rakkhati gomaṇḍala’’miti mahīmaṇḍalapālanaṃ rājino+nurūpa+miti mahīmaṇḍalapālaneva patīti, na tu goyūtharakkhane.</p>\r\n<p class="bodytext">Upacārato – ataṃsabhāve taṃsabhāvāropana+mupacāro, sa ca tadaṭṭho, taddhammo, taṃsahacariyo, taṃsamīpoti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',35,replace(replace('<p class="bodytext">catubbidho<a name="M0.0035"></a>, tattha yathākkamaṃ mañcā ukkosanti, aggi māṇavo, yaṭṭhiṃ pavesaya, gaṅgāyaṃ vajoti.</p>\r\n<p class="bodytext">Kākuto-kākusaddo itthiyaṃ, sa ca vikāra+soka+bhīti+dhanirūpesu dissati, vattu kāyavikārā kathañci taṃ akatavāapi kenaci aññena ‘‘kiṃ tvaṃ taṃ akāsi ‘‘iti puṭṭho kopena bhamubhedā ‘‘ahaṃ katavā amhī’’ti katheti, tassa bhamubhedakriyā akriyāpaṭiññaṃ sūcayati.</p>\r\n<p class="bodytext">Sambandha to – ‘‘mātari sammā vattitabbaṃ, pitari sussūyitabbaṃ’’ iti, atra hi samātari sapitarīti sambandhisaddābhāvepi sā mātā so pitā ca assa puttassāti patīyate.</p>\r\n<p class="bodytext">Upalakkhaṇato – ‘‘kākehi rakkhitabbaṃ dadhī’’ti kākasaddo sabbesa+mupaghātakānaṃ sāmaññaṃ upalakkhetīti sunakhādisabbehipi nivārīyate.</p>\r\n<p class="bodytext">Vacanato – ‘‘dārā’’iti dārasaddo kalatte bahuvacananto, aññattha aniyatavacano.</p>\r\n<p class="bodytext">Tadaṅgattā – ‘‘sajjitaṃ bhojana’’miti vutte tapparikkhārattā tadupakaraṇa āsana, pāti, byañjanādīnaṃ sampādanampi patīyate.</p>\r\n<p class="bodytext">Padhānabhāvato – ‘‘niggacchati avaninātho’’ti rañño niggamanena tadupajīvīnampi niggamanaṃ viññāyati.</p>\r\n<p class="indent">Vuttañca –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',36,replace(replace('<p class="gatha1">Neyyanītatthasuttesu<a name="M0.0036"></a>, ñeyyaṃ saddatthamattakaṃ;</p>\r\n<p class="gathalast">Ne+ttha vattabbaatthena, suttaṃ nītatthakaṃ bhaveti.</p>\r\n<p class="bodytext">Eva+maññesampi akārantānaṃ pulliṅgānaṃ saddānaṃ rūpanayo kriyā+bhisambandho ca. Sugatasaddato yassa saddassa viseso atthi, taṃ vakkhāma. Ito paraṃ chaṭṭhiyā catutthīsamattā pañcamībahuvacanassa ca tatiyābahuvacanena samattā na tā dassiyante.</p>\r\n<p class="bodytext">Gumba si, ‘‘ato’’ ‘‘sissā’’ti ca vattate.</p>\r\n<p class="subhead">110. Kvace+vā.</p>\r\n<p class="bodytext">Akārantato nāmasmā parassa sissa e hoti vā kvaci. Gumbe gumbo, gumbā. Bho gumba gumbā, bhavanto gumbā iccādi sugatasamaṃ. Evaṃ phussitagge phussitaggo, vattabbe vattabbo iccādi. Sisso+kārassa niccattā katthaci pakkhe ekāratta+mida+māraddhanti sisso+kārapakkhe eva bhavatīti ‘‘aṃ napuṃsake’’ti a+mādesena ekārassa napuṃsakavisaye bādhitattā ‘‘bahulaṃ’’ vidhānā napuṃsakepi sukhe dukkheti kvaci hoteva.</p>\r\n<p class="bodytext">‘‘Yossa’’ ‘‘ṭe’’ti ca vattate.</p>\r\n<p class="subhead">135. Ekaccādīha+to</p>\r\n<p class="bodytext">Akārantehi ekaccādīhi yonaṃ ṭe hoti. Ekacco, ekacce. Bho ekacca ekaccā, ekacce. Ekaccaṃ, ekacce. Evaṃ esa+sa+paṭhamasaddānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',37,replace(replace('<p class="bodytext">Kodho<a name="M0.0037"></a>, kodhā. Bho kodha kodhā, kodhā. Kodhaṃ, kodhe.</p>\r\n<p class="bodytext">‘‘Nāssa’’ ‘‘sā’’ti ca vattate.</p>\r\n<p class="subhead">107. Kodhādīhi</p>\r\n<p class="bodytext">Kodhādīhi nāssa sā hoti vā. Kodhasā kodhena. Atthasā atthena. ‘‘Ye uttamatthāni tayi labhimhā’’ti atthasaddo napuṃsakaliṅgopi dissati.</p>\r\n<p class="bodytext">‘‘Smino ṭī’’ti ca vattate.</p>\r\n<p class="subhead">175. Divādito</p>\r\n<p class="bodytext">Divādīhi nāmehi smino ṭi hoti niccaṃ. Divi, evaṃ bhuvi. Etthaṭimhi niccaṃ vakārāgamo rasso ca. Ettha bhūsaddo vadhūsaddasamaṃ.</p>\r\n<p class="bodytext">‘‘Ve’’ti vattate.</p>\r\n<p class="subhead">144. Manādīhi smiṃ+saṃ+nā+smānaṃ si+so+o+sā+sā</p>\r\n<p class="bodytext">Manādīhi smi+mādīnaṃ si+so+o+sā+sā honti vā yathākkamaṃ. Mano, manā. Bho mana manā, manā. Mano, manaṃ, mane. Manasā manena, manehi manebhi. Manaso manassa, manānaṃ. Manasā manā manamhā manasmā, manehi manebhi. Manasi manamhi manasmiṃ, manesu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',38,replace(replace('<p class="gatha1">Evaṃ <a name="M0.0038"></a> vaco payo tejo,</p>\r\n<p class="gathalast">Tapo ceto tamo yaso.</p>\r\n<p class="gatha1">Ayo vayo siro saro,</p>\r\n<p class="gathalast">Uro+tye+te manādayo.</p>\r\n<p class="bodytext">Rūpasiddhiyaṃ aha+rahasaddā manādīsu paṭhitā. Ahassa āpādittā rahoti nipātattā rahasīti vibhatyantapaṭirūpakanipātattā idha na gahitā.</p>\r\n<p class="bodytext">Gacchanta si, ‘‘sissa’’ ‘‘ve’’ti ca vattate. Parato bhiyyo nānuvattayissāma, vuttiyā eva anuvattassa gamyamānattā.</p>\r\n<p class="subhead">148. Ntassaṃ</p>\r\n<p class="bodytext">Simhi ntapaccayassa aṃ hoti vā. ‘‘Sutānumitesu sutasambandhova balavā’’ti ñāyā ‘‘ntassā’’ti sutattā ntasseva aṃ, na tadantassa anumitassa saddassa. Eva+muparipi nta+ntūnaṃ ādesavidhānaṭṭhānesu. ‘‘Gasinaṃ’’ti silopo. Gacchaṃ gacchanto.</p>\r\n<p class="subhead">215. Nta+ntūnaṃ nto yomhi paṭhame</p>\r\n<p class="bodytext">Paṭhame yomhi nta+ntūnaṃ savibhattīnaṃ ntoiccādeso hoti vā. Sa ca bahulādhikārā pumeva, gacchanto gacchantā.</p>\r\n<p class="subhead">218. Ṭa+ṭā+aṃ ge</p>\r\n<p class="bodytext">Ge pare nta+ntūnaṃ savibhattīnaṃ ṭa+ṭā+aṃiccādesā niccaṃ honti bahulaṃ. Bho gaccha gacchā gacchaṃ, gacchanto gacchantā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',39,replace(replace('<p class="subhead">92. Ntassa ca ṭa vaṃ+se</p>\r\n<p class="bodytext">Aṃsesu <a name="M0.0039"></a> ntapaccayassa ṭa hoti vā ntussa ca. Vavatthitavibhāsā+yaṃ. Gacchaṃ gacchantaṃ, gacchante.</p>\r\n<p class="subhead">217. To+tā+ti+tā sa+smā+smiṃ nāsu</p>\r\n<p class="bodytext">Sa+smā+smiṃ+nāsu nta+ntūnaṃ savibhattīnaṃ to+tā+ti+tā honti vā yathākkamaṃ. Gacchatā gacchantena, gacchantehi gacchantebhi. Gacchato gacchassa gacchantassa.</p>\r\n<p class="subhead">216. Taṃ naṃmhi</p>\r\n<p class="bodytext">Naṃmhi nta+ntūnaṃ savibhattīnaṃ taṃ vā hoti. Gacchataṃ gacchantānaṃ. Gacchatā gacchantā gacchantamhā gacchantasmā. Gacchati gacchante gacchantamhi gacchanthasmiṃ, cchentesu.</p>\r\n<p class="gatha1">Evaṃ mahaṃ caraṃ tiṭṭhaṃ, dadaṃ bhuñjaṃ suṇaṃ pacaṃ;</p>\r\n<p class="gathalast">Jayaṃ jīraṃ vacaṃ pīyaṃ, saraṃ kubbaṃ japaṃ vajaṃ.</p>\r\n<p class="unindented">Iccādayo.</p>\r\n<p class="bodytext">Bhavanta si,</p>\r\n<p class="subhead">149. Bhūto</p>\r\n<p class="bodytext">Niyamasutta+midaṃ. Bhūdhātuto ntassa aṃ hoti simhi niccaṃ punabbidhānā. Bhavaṃ.</p>\r\n<p class="subhead">146. Bhavato vā bhontoga+yo+nā+se</p>\r\n<p class="bodytext">Bhavantasaddassa bhontādeso vā hoti ga+yo+nā+se. Ntoādeso, bhonto bhontā bhavanto bhavantā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',40,replace(replace('<p class="bodytext">Ge <a name="M0.0040"></a> pana bho bhonta bhontā bhava bhavā bhavaṃ, bhonto bhontā bhavanto bhavantā. Bhontādesapakkhe ṭa+ṭā+aṃādesā bahulādhikārā na honti. Bhavaṃ bhavantaṃ, bhonte bhavante. Bhotā bhontena bhavatā bhavantena, bhavantehi bhavantebhi. Bhoto bhontassa bhavato bhavassa bhavantassa, bhavataṃ bhavantānaṃ. Bhavatā iccādi gacchantasamaṃ.</p>\r\n<p class="bodytext">Bhoiti āmantaṇe nipāto, ‘‘kuto nu āgacchatha bho tayo janā’’ti bahuvacanepi dassanato. Evaṃ bhanteti. Bhaddeti bhaddasaddantarena siddhaṃ. Bhaddantaiti dassa dvibhāvena.</p>\r\n<p class="bodytext">Saṃ santo, santo santā. Bho sa sā saṃ, santo santā. Saṃ santaṃ ‘‘saṃyogādilopoti nassa lope ‘‘yaṃ yañhi rāja bhajati, santaṃ vā yadi vā asaṃ’’, sante. Satā santena.</p>\r\n<p class="subhead">145. Sato saba bhe</p>\r\n<p class="bodytext">Santasaddassa saba bhavati bhakāre. Sabbhi santehi. Niccattā santebhīti na hoti. Sato sassa santassa iccādi gacchantasamaṃ.</p>\r\n<p class="subhead">150. Mahantā+rahantānaṃ ṭā vā</p>\r\n<p class="bodytext">Simhi mahantā+rahantānaṃ ntassa ṭā vā hoti. Mahā mahaṃ mahanto, mahanto mahantā. Arahā arahaṃ, arahanto arahantā iccādi gacchantasamaṃ.</p>\r\n<p class="bodytext">Asma si,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',41,replace(replace('<p class="subhead">154. Rājādīyuvāditvā</p>\r\n<p class="bodytext">Rājādīhi <a name="M0.0041"></a> yuvādīhi ca parassa sissa ā hoti. Asmā,</p>\r\n<p class="subhead">156. Yona+māno</p>\r\n<p class="bodytext">Rājādīhi yuvādīhi ca yona+māno vā hoti. Asmāno asmā. Bho asma asmā, asmāno asmā.</p>\r\n<p class="subhead">155. Vā+mhā+naṅa</p>\r\n<p class="bodytext">Rājādīnaṃ yuvādīnañca ānaṅa hoti vā aṃmhi. Asmānaṃ asmaṃ, asmāno asme.</p>\r\n<p class="subhead">80. Nāsse+no</p>\r\n<p class="bodytext">Kammādito nāvacanassa eno vā hoti. Asmena asmanā, asmehi asmebhi. Asmassa, asmānaṃ. Asmā asmamhā asmasmā iccādi.</p>\r\n<p class="subhead">79. Kammādito</p>\r\n<p class="bodytext">Kammādito smino ni hoti vā. Asmani asme asmamhi asmasmiṃ, asmesu. Kamma camma vesma bhasma brahma atta ātuma ghamma muddhaiti kammādayo. Muddha gaṇḍivadhanva aṇima laghimādayo asmasamā. Rāja brahma sakha atta ātuma gaṇḍivadhanva asma aṇima laghimādayo rājādayo.</p>\r\n<p class="bodytext">‘‘Dhammo vā+ññatthe’’ti gaṇasuttena rājādīsu paṭṭhitattā daḷadhammo daḷadhammāti vā hoti. Yuva sā suvā maghava </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',42,replace(replace('<p class="bodytext">puma <a name="M0.0042"></a> vattahāti yuvādayo. Rājā, rājāno rājā. Bhorāja rājā, rājāno rājā. Rājānaṃ rājaṃ, rājāno rāje.</p>\r\n<p class="subhead">123. Rājassi nāmhi</p>\r\n<p class="bodytext">‘‘Sabbadattena rājinā’’ti pāṭhampati ida+māraddhaṃ. Rājassi vā hoti nāmhi. Rājinā.</p>\r\n<p class="subhead">222. Nā+smāsu raññā</p>\r\n<p class="bodytext">Nā+smāsu rājassa savibhattissa raññā hoti niccaṃ. Anekavaṇṇattā sabbassa. Raññā.</p>\r\n<p class="subhead">124. Su+naṃ+hisū</p>\r\n<p class="bodytext">Rājassa ū hoti vā su+naṃ+hisu. ‘‘Chaṭṭhiyantassāti antassa hoti. Rājūhi rājehi rājūbhi rājebhi.</p>\r\n<p class="subhead">223. Rañño+raññassa+rājino se</p>\r\n<p class="bodytext">Se rājassa savibhattissa ete ādesā honti. Rañño raññassa rājino, rājūnaṃ.</p>\r\n<p class="subhead">221. Rājassa raññaṃ</p>\r\n<p class="bodytext">Naṃmhi rājassa savibhattissa raññaṃ vā hoti. Raññaṃ. Raññā, rājūhi rājehi rājūbhi rājebhi.</p>\r\n<p class="subhead">224. Smimhi raññe+rājini</p>\r\n<p class="bodytext">Smimhi rājassa savibhattissa raññe+rājini honti niccaṃ. Raññe rājini, rājūsu rājesu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',43,replace(replace('<p class="subhead">225. Samāse vā</p>\r\n<p class="bodytext">Iti <a name="M0.0043"></a> gaṇasuttena rājassa nā+smā+smiṃsu yaṃ vuttaṃ, taṃ vā hoti. Kāsiraññā kāsirājinā kāsirājena, kāsirājūbhi kāsirājebhi kāsirājūhi kāsirājehi. Kāsirañño kāsiraññassa kāsirājino kāsirājassa. Kāsiraññā kāsirājasmā. Kāsirājūnaṃ kāsirājānaṃ. Kāsiraññe kāsirājini kāsirāje kāsirājamhi kāsirājasmiṃ, kāsirājūsu kāsirājesu.</p>\r\n<p class="bodytext">Addhā, addhāno addhā. Bho addha addhā, addhāno addhā. Addhānaṃ addhaṃ, addhāno addhe.</p>\r\n<p class="subhead">192. Puma+kamma+thāma+ddhānaṃ sa+smāsu ca</p>\r\n<p class="bodytext">Pumādīna+mu hoti vā sa+smāsu nāmhi ce+ti utte addhunā, kammādittā ‘‘nāsse+no’’ti vā eno, addhena addhanā, addhehi addhebhi. Se ukāre ca –</p>\r\n<p class="subhead">1,9. Iyuvaṇṇā jhalā nāmassa+nte</p>\r\n<p class="bodytext">Nāmaṃ pāṭipadikaṃ, tassa ante vattamānā ivaṇṇuvaṇṇā jhalasaññā honti yathākkamaṃ. I ca u ca iyu, iyu ca te vaṇṇā ceti iyuvaṇṇā, ‘‘dvandante sūyamānaṃ pacceka+mabhisambandhiya te’’ti vuttattā vaṇṇasaddaṃ pacceka+mabhisambandhiya ‘‘ivaṇṇuvaṇṇā’’ti vuttaṃ.</p>\r\n<p class="subhead">81. Jhalā sassa no</p>\r\n<p class="bodytext">Jhalato sassa no vā hoti. Addhuno addhussa addhassa, addhānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',44,replace(replace('<p class="subhead">82. Nā smāssa</p>\r\n<p class="bodytext">Jhalato <a name="M0.0044"></a> smāssa nā hoti vā, addhunā addhumhā addhusmā addhā addhamhā addhasmā. ‘‘Kammādito’’ti smino ni, addhani addhe addhamhi addhasmiṃ, addhesu. Addhasaddo ce+tthakāla+ddhānavāci, na bhāgavācī.</p>\r\n<p class="bodytext">Attā, attāno iccādi yāva dutiyā rājāva, kammādittā ene attena attanā.</p>\r\n<p class="subhead">195. Su+hisu naka</p>\r\n<p class="bodytext">Atta+ātumānaṃ su+hisu nakāgamo hoti. ‘‘Ñakānubandhā+dyantā’’ti paribhāsato kakāro antāvayavattho, attanehi atthehi attanebhi attebhi.</p>\r\n<p class="subhead">194. No+ttātumā</p>\r\n<p class="bodytext">Atta+ātumehi sassa no vā hoti. Attano attassa, attānaṃ.</p>\r\n<p class="subhead">196. Smāssa nā brahmā ca</p>\r\n<p class="bodytext">Brahmā atta+ātumehi ca parassa smāssa nā hoti niccaṃ. Attanā. Smimhi kammādittā ni, attani atte attamhi attasmiṃ, attanesu attesu. Ātumā attāva.</p>\r\n<p class="bodytext">Brahmā, brahmāno brahmā, ‘‘brahmassu vā’’ti sutte ‘‘brahmassū’’ti yogavibhāgā ānomhi brahmassa u, parassaralope brahmunotipi sijjhati. Ayañca brahmasaṃyutte dissati. Brahmā ge-</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',45,replace(replace('<p class="subhead">60. Gha+brahmādite</p>\r\n<p class="bodytext">Ākatigaṇo+yaṃ<a name="M0.0045"></a>, ākatīti jāti, jātipadhānagaṇotya+ttho. Ghasaññato brahma+kattu+isi+sakhādīhi ca gasse+vā hoti. Bho brahme brahmā, brahmāno brahmā. Brahmānaṃ brahmaṃ, brahmāno brahme.</p>\r\n<p class="subhead">191. Nāmhi</p>\r\n<p class="bodytext">Brahmassu hoti nāmhi niccaṃ. Brahmunā, brahmehi brahmebhi.</p>\r\n<p class="subhead">190. Brahmassu vā</p>\r\n<p class="bodytext">Brahmassu vā hoti sa+naṃsu. ‘‘Jhalā sassa no’’ti no. Brahmuno brahmussa brahmassa, brahmūnaṃ brahmānaṃ. ‘‘Smāssa nā brahmā ce’’ti smāssa nā, ukāre brahmunā. ‘‘Ambvādīhi’’ti smino ni, imassa ākatigaṇattā brahmassa kammādittepi ettha vuttā. Brahmani brahme brahmamhi brahmasmiṃ, brahmesu.</p>\r\n<p class="bodytext">Sakhā, rājādittā ā.</p>\r\n<p class="subhead">2,157. Āyo no ca sakhā</p>\r\n<p class="bodytext">Sakhato yona+māyo no honti vā āno ca. Sakhāyo sakhāno.</p>\r\n<p class="subhead">159. No+nā+sesvi</p>\r\n<p class="bodytext">Sakhassa i hoti niccaṃ no+nā+sesu. Sakhino.</p>\r\n<p class="subhead">161. Yo+svaṃ+hisu cā+raṅa</p>\r\n<p class="bodytext">Sakhassa vā āraṅa hoti yo+svaṃ+hisu smā+naṃsuca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',46,replace(replace('<p class="subhead">171. Āraṅasmā</p>\r\n<p class="bodytext">Āravādesato <a name="M0.0046"></a> paresaṃ yonaṃ ṭo hoti. Sakhāro sakhā. Ge tu ‘‘gha+brahmādite’’ti e, sakhe sakha sakhā. Bahuvacanaṃ paṭhamā viya. Aṃmhi ‘‘vā+mhā+naṅa’’ti ānaṅa, sakhānaṃ sakhāraṃ sakhaṃ, sakhāyo sakhāno sakhino.</p>\r\n<p class="subhead">172. Ṭo ṭe vā</p>\r\n<p class="bodytext">Āravādesamhā yonaṃ ṭo ṭe vā honti yathākkamaṃti ṭe. Aññattha ‘‘āraṅasmā’’ti ṭo. Sakhāre sakhāro sakhe. Ṭoggahaṇaṃ lāghavatthaṃ. Sakhinā, sakhārehi sakhehi. ‘‘Jhalā sassa no’’ti jhato sassa no, sakhino sakhissa, sakhārānaṃ.</p>\r\n<p class="subhead">160. Smā+naṃsu vā</p>\r\n<p class="bodytext">Sakhassa vā i hoti smā+naṃsu. Sakhīnaṃ sakhānaṃ.</p>\r\n<p class="subhead">171. Ṭā nā+smānaṃ</p>\r\n<p class="bodytext">Āravādesamhā nā+smānaṃ ṭā hoti niccaṃ. Sakhārā, bahulādhikārā sakhārasmā. ‘‘Nā smāssā’’ti nā, sakhinā sakhismā sakhā sakhamhā sakhasmā.</p>\r\n<p class="subhead">158. Ṭe smino</p>\r\n<p class="bodytext">Sakhato smino ṭe hoti niccaṃ. Sakhe, sakhāresu sakhesu. Sakhi sakhīti itthiyaṃyeva payogo dissati, tasmā ‘‘nadādito ṅī’’ti vīmhi ālapanattā vā rasso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',47,replace(replace('<p class="bodytext">Yuvādittā <a name="M0.0047"></a> ā, yuvā.</p>\r\n<p class="subhead">181. Yonaṃ no+ne vā</p>\r\n<p class="bodytext">Yuvādīhi yonaṃ no+ne vā honti yathākkamaṃ. ‘‘Yona+māno’’ti ānomhi siddhepi ‘‘dutiyassa ne’’ti ganthagāravo hotīti yathākkamaṃpati lāghavatthaṃ noggahaṇaṃ.</p>\r\n<p class="subhead">179. No+nā+nesvā</p>\r\n<p class="bodytext">No+nā+nesu yuvādīna+mā hoti. Yuvāno yuvā. Bho yuva yuvā, yuvāno yuvā. Yuvānaṃ yuvaṃ, yuvāno yuvāne yuve. Yuvānā.</p>\r\n<p class="subhead">178. Yuvādīnaṃ su+hisvā+naṅa</p>\r\n<p class="bodytext">Su+hisu yuvādīna+mānaṅa hoti. Yuvānehi yuvānebhi.</p>\r\n<p class="subhead">193. Yuvā sassi+no</p>\r\n<p class="bodytext">Yuvā sassa vā ino hoti. Yuvino yuvassa, yuvānaṃ.</p>\r\n<p class="subhead">180. Smā+smiṃnaṃ nā+ne</p>\r\n<p class="bodytext">Yuvādahi smā+smiṃnaṃ nā+ne niccaṃ honti yathākkamaṃ. Yuvānā, yuvānehi yuvānebhi. Yuvāne, yuvānesu. Maghava+puma+vattahasaddā yuvasaddasamā. Ayaṃ viseso –</p>\r\n<p class="subhead">187. Gassaṃ</p>\r\n<p class="bodytext">Pumasaddato gassa aṃ vā hoti. Pumaṃ puma pumā, pumāno pumā. Pumānaṃ pumaṃ, pumāno pumāne pume.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',48,replace(replace('<p class="subhead">185. Nāmhi</p>\r\n<p class="bodytext">Pumassā <a name="M0.0048"></a> hoti nāmhi. Pumānā. ‘‘Lakkhaṇikapaṭipadottesu paṭipadottasseva gahaṇaṃ, na lakkhaṇikassā’’ti ñāyā paṭipadottanāvibhatti eva gayhati, na nāsmāssa, katalakkhaṇikattā. Paṭipadanti ca ‘‘nāmhī’’ti nāvibhattiyā paṭipadabhūto anukaraṇasaddo. Na lakkhaṇiko nā. ‘‘Puma+kamma+thāma+ddhānaṃ vā sa+smāsu ce’’ti vā utte pumunā pumena. Pumuno pumussa pumassa. Pumunā pumā.</p>\r\n<p class="subhead">184. Pumāti</p>\r\n<p class="bodytext">Smino ne vā hoti. Pumāne pume pumamhi pumasmiṃ.</p>\r\n<p class="subhead">186. Sumhā ca</p>\r\n<p class="bodytext">Pumassa sumhi pumādīnaṃ yaṃ niccaṃ vuttaṃ, taṃ vā hotīti ānaṅa vā hoti ā ca. Pumānesu pumāsu pumesu. Vattahā, vattahāno vattahā iccādi yuvasaddasamaṃ.</p>\r\n<p class="subhead">189. Vattahā sa+naṃnaṃ no+nānaṃ</p>\r\n<p class="bodytext">Vattahā sanaṃnaṃ nonānaṃ niccaṃ honti yathākkamaṃ. Vattahāno vattahānānaṃ.</p>\r\n<p class="centered">Akārantaṃ.</p>\r\n<p class="bodytext">Sā si,</p>\r\n<p class="subhead">64. Ekavacana+yosva+ghonaṃ</p>\r\n<p class="bodytext">Gho ca o ca gho, na gho agho. ‘‘Aghonaṃ’’ti ghappaṭisedhe akate ssa+mādīsu paresu ghassa vikappena rasso, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',49,replace(replace('<p class="bodytext">ekavacanādīsu <a name="M0.0049"></a> yosu ca paresu ghassa niccena rassoti viruddhatthagahaṇanivattanattho ghapaṭisedho. Oggahaṇa+muttaratthaṃ. Ekavacane yosu ca gha+okārantavajjitānaṃ nāmānaṃ rasso hoti tiliṅgeti rasse sampatte –</p>\r\n<p class="subhead">66. Sismiṃ nā+napuṃsakassati</p>\r\n<p class="bodytext">Anapuṃsakassa rasso na hotīti simhi tu na rasso. Sā. ‘‘Pajjunnova lakkhaṇapavutti jalepi vassati, thalepi vassatī’’ti ñāyā yuvādittā sissa ā. Yosu rasse ‘‘yonaṃ no+ne vā’’ti yossa no. ‘‘No nānesvā’’ti ā, sāno. Nottābhāvapakkhe ‘‘yona+māno’’ti vādhikārassa vavatthitavibhāsattā nicca+māno, tasmā no+neabhāvapakkhe sā, seti rūpapasaṅgo na hoti. Sāno. Tathā nettābhāvapakkhe.</p>\r\n<p class="subhead">188. Sāssaṃ+se cā+naṅa</p>\r\n<p class="bodytext">Sāsaddassa ānaṅa hoti aṃ+se ge ca niccaṃ. Bho sāna sānā, sāno. Sānaṃ, sāne sāno. Sānā, sānehi sānebhi. Sānassa, sānaṃ. Sānā, sānehi sānebhi. Sāne, sānesu.</p>\r\n<p class="bodytext">Suvā yuvāva. ‘‘Ekavacanayosva+ghonaṃ’’ti rassattaṃ viseso. Ge tu –</p>\r\n<p class="subhead">130. Ge vā</p>\r\n<p class="bodytext">Aghonaṃ ge vā rasso hoti tiliṅge. Bho suva suvā.</p>\r\n<p class="centered">Ākārantaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',50,replace(replace('<p class="bodytext">Muni <a name="M0.0050"></a> silopo. Jhe kate –</p>\r\n<p class="subhead">93. Yosu jhissa pume</p>\r\n<p class="bodytext">Jhasaññassa issa yosu vā ṭa hoti pulliṅge. Munayo, jhaggahaṇaṃ kiṃ, ikārantasamudāyassa mā siyā. Iggahaṇaṃ kiṃ, īkārassa vāti. Atoti sāmaññaniddesā lakkhaṇikaakārato yonaṃ ṭāṭe sampattāpi avidhānasāmatthiyā na honti. Sāmatthiyañca aññathā anupapatti.</p>\r\n<p class="subhead">114. Lopo</p>\r\n<p class="bodytext">Jhalato yonaṃ lopo hotīti yolope –</p>\r\n<p class="subhead">88. Yolopa+nisu dīgho</p>\r\n<p class="bodytext">Yonaṃ lopenisu ca dīgho hoti. Muni. Munayoti ettha yolopo kimatthaṃ na hoti, akkena jhattassa nāsitattā. Kinti paṭhamaṃ na hoti, antaraṅgattā jhattassa. Bho muni munī, munayo munī. Muniṃ, munayo munī. Muninā, munīhi munībhi. ‘‘Yolopanisu’’ ‘‘vīmantuvantūna’’ miccādiñāpakā ikārukārānaṃ sunaṃhisu dīghassā+niccattā munihi muninaṃ munisu itipi hoti. ‘‘Jhalā sassa no’’ti no, munino munissa, munīnaṃ. ‘‘Nāsmāssā’’ti nā, muninā munimhā munismā. Munimhi munismiṃ, munīsu. ‘‘Ito kvaci sassa ṭānubandho’’ (gaṇasutta)ti brahmādīsu pāṭhā ‘‘yo ca sisso mahāmune’’ti ettha ‘‘gha+brahmādite’’ti sassa eṭa.</p>\r\n<p class="bodytext">Evaṃ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',51,replace(replace('<p class="gatha1">Joti <a name="M0.0051"></a> pāṇi gaṇṭhi muṭṭhi, kucchi vatthi sāli vīhi;</p>\r\n<p class="gathalast">Byādhi odhi bodhi sandhi, rāsi kesi sāti dīpi.</p>\r\n<p class="gatha1">Isi gini maṇi dhani, giri ravi kavi kapi;</p>\r\n<p class="gathalast">Asi masi nidhi vidhi, ahi kimi pati hari.</p>\r\n<p class="gatha1">Ari timi kali bali, jaladhi ca gahapati;</p>\r\n<p class="gatha2">Uramiti varamati, nirupadhi adhipati;</p>\r\n<p class="gathalast">Añjali sārathi atithi, samādhi udadhippabhutayo.</p>\r\n<p class="bodytext">Aggi+isīnaṃ ayaṃ viseso –</p>\r\n<p class="subhead">147. Sissā+ggito ni</p>\r\n<p class="bodytext">Aggismā sissa ni hoti vā. Aggini aggi, aggayo iccādi munisaddasamaṃ.</p>\r\n<p class="subhead">133. Ṭe sissi+sismā</p>\r\n<p class="bodytext">Isismā sissa ṭe vā hoti. Ise isi. ‘‘Gha+brahmādite’’ti gassa e vā, bho ise isi, isayo isī. Isiṃ.</p>\r\n<p class="subhead">134. Dutiyassa yossa</p>\r\n<p class="bodytext">‘‘Dutiyā yossā’’ti avatvā ‘‘dutiyassa yossā’’ti visuṃ karaṇaṃ ‘‘ekayoganiddiṭṭhāna+mapye+kadeso+nuvattate, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',52,replace(replace('<p class="bodytext">na <a name="M0.0052"></a> tve+kavibhattiyuttānaṃ’’ti ñāyā dutiyāyossāti nānuvattiya yossāti sāmaññena anuvuttiya ekaccādito paṭhamā yossāpi ṭevidhānatthaṃ. Isismā parassa dutiyā yossa ṭe vā hoti. Ise isayo isī, sesaṃ munisamaṃ.</p>\r\n<p class="bodytext">Ādi, ādayo iccādi, smimhi –</p>\r\n<p class="subhead">55. Ratyādīhi ṭo smino</p>\r\n<p class="bodytext">Ratyādīhi smino ṭo vā hoti. Ādo ādimhi ādismiṃ, ādīsu.</p>\r\n<p class="bodytext">Samāse ikārantato yo+smiṃsu viseso.</p>\r\n<p class="subhead">182. Ito+ññatthe pume</p>\r\n<p class="bodytext">Aññatthe vattamānato ikārantato nāmasmā yonaṃ no+ne vā honti yathākkamaṃ pulliṅge. Ariyavuttino ariyavuttayo ariyavuttī. Bho ariyavutti ariyavuttī, ariyavuttino ariyavuttayo ariyavuttī. Ariyavuttiṃ, ariyavuttino ariyavuttayo ariyavuttī. Ariyavuttinā iccādi tu munisaddasamaṃ.</p>\r\n<p class="subhead">183. Ne smino kvaci</p>\r\n<p class="bodytext">Aññatthe ikārantato nāmasmā smino ne vā hoti kvaci. Ariyavuttine ariyavuttimhi ariyavuttismiṃ, ariyavuttīsu. Evaṃ tomaraṅkusapāṇino sāramatino iccādi. Kvaciggahaṇā na sabbattha neādeso.</p>\r\n<p class="centered">Ikārantaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',53,replace(replace('<p class="bodytext">Daṇḍī<a name="M0.0053"></a>, silopo. ‘‘Ekavacane’’ ccādinā rasse sampatte anapuṃsakattā ‘‘sismiṃnā+napuṃsakassā’’ti nisedho. Yomhi ekavacane ca sabbattha rasso.</p>\r\n<p class="subhead">75. Yonaṃ no+ne pume</p>\r\n<p class="bodytext">Jhasaññito yonaṃ no+ne vā honti yathākkamaṃ pulliṅge. Daṇḍino.</p>\r\n<p class="subhead">115. Jantuhetvīghapehi vā</p>\r\n<p class="bodytext">Jantu+hetūhi īkārantehi gha+pasaññehi ca paresaṃ yonaṃ vā lopo hoti. ‘‘Yolopanisu dīgho’’ti dīghe daṇḍī daṇḍiyo. Ge tu ‘‘ge vā’’ti vā rasso. Bho daṇḍi daṇḍī, daṇḍino daṇḍī daṇḍiyo.</p>\r\n<p class="subhead">74. Naṃ jhīto</p>\r\n<p class="bodytext">Jhasaññīto aṃvacanassa naṃ vā hoti. Daṇḍinaṃ daṇḍiṃ, daṇḍine.</p>\r\n<p class="subhead">76. No</p>\r\n<p class="bodytext">Jhīto yonaṃ no vā hoti pulliṅge. Daṇḍino daṇḍī daṇḍiyo. Daṇḍinā, daṇḍīhi daṇḍībhi. ‘‘Jhalā sassa no’’ti nomhi kate daṇḍino daṇḍissa, daṇḍīnaṃ. ‘‘Nā smāssā’’ti smāssa nā, daṇḍinā daṇḍimhā daṇḍismā.</p>\r\n<p class="subhead">77. Smino ni</p>\r\n<p class="bodytext">Jhīto smiṃvacanassa ni hoti vā. Daṇḍini daṇḍismiṃ, daṇḍīsu, bahulādhikārā smimhi gāmaṇī+senānī+sudhīpabhutīnaṃ niādesābhāvo ca viseso. Evaṃ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',54,replace(replace('<p class="gatha1">Dhammī <a name="M0.0054"></a> saṅghī ñāṇī hatthī, cakkī pakkhī dāṭhī raṭṭhī;</p>\r\n<p class="gathalast">Chattī mālī cammī yogī, bhāgī bhogī kāmī sāmī.</p>\r\n<p class="gatha1">Dhajī gaṇī sasī kuṭṭhī, jaṭī yānī sukhī sikhī;</p>\r\n<p class="gathalast">Dantī mantī karī cāgī, kusalī musalī balī; (Vācī, rū)</p>\r\n<p class="gatha1">Pāpakārī sattughātī, mālyakārī dīghajīvī;</p>\r\n<p class="gathalast">Dhammavādī sīhanādī, bhūmisāyī sīghayāyī.</p>\r\n<p class="centered">Īkārantaṃ.</p>\r\n<p class="bodytext">Bhikkhu, silopo. Lasaññāyaṃ –</p>\r\n<p class="subhead">83. Lā yonaṃ vo pume</p>\r\n<p class="bodytext">Lato yonaṃ vo hoti vā pulliṅge.</p>\r\n<p class="subhead">94. Ve+vosu lussa</p>\r\n<p class="bodytext">Lasaññassa ussa ve+vosu ṭa hoti. Ettha ‘‘pumālapane vevo’’ tya+tra vossa sahacaritañāyā anissitattā jātivasena ‘‘lā yonaṃ’’ tyādo vo ca gayhati. Bhikkhavo. Aññatra ‘‘lopo’’ti yolopo. ‘‘Yolopanisu dīgho’’ti dīghe bhikkhū. Bho bhikkhu bhikkhū.</p>\r\n<p class="subhead">96. Pumā+lapane vevo</p>\r\n<p class="bodytext">Lasaññato uto yossā+lapane ve+vo honti vā pulliṅge. Bhikkhave bhikkhavo bhikkhū. Bhikkhuṃ, bhikkhavo bhikkhū. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',55,replace(replace('<p class="bodytext">Bhikkhunā<a name="M0.0055"></a>, bhikkhūhi bhikkhūbhi. ‘‘Jhalā sassa no’’ti lato sassa no vā, bhikkhuno bhikkhussa, bhikkhūnaṃ. ‘‘Nā smāssā’’ti lato vā smāssa nā, bhikkhunā bhikkhumhā bhikkhusmā. Bhikkhumhi bhikkhusmiṃ. Evaṃ –</p>\r\n<p class="bodytext">Setu ketu rāhu bhāṇu, saṃku ucchu veḷu maccu. (Paṅgu, rū) sindhu bandhu neru meru, sattu kāru hetu jantu. Ruru paṭu – iccādayo.</p>\r\n<p class="bodytext">Jantu+hetūnaṃ yosva+yaṃ bhedo. ‘‘Jantu+hetvī+gha+pehi vā’’ti yolope –</p>\r\n<p class="subhead">84. Jantvādito no ca</p>\r\n<p class="bodytext">Jantvādito yonaṃ no hoti vo ca pulliṅge. Jantuno jantavo jantuyo. Bho jantu jantū, jantuno, ‘‘pumālapane vevo’’ti ve+vo, jantave jantavo jantuyo. Jantuṃ, jantū jantuno jantavo jantuyo. Sesaṃ bhikkhusamaṃ.</p>\r\n<p class="bodytext">Hetu, hetū hetavo. ‘‘Yomhi vā kvacī’’ti lasaññassa ussa vā ṭādeso, hetayo. Yomhi antaraṅgattā paṭhamaṃ ṭādese kate pacchā yolopābhāvo, tathā hi antaraṅga+bāhiraṅgavidhānesva+ntaraṅgavidhiyeva balavā. Ettha ca pakatinissita+mantaraṅgaṃ, paccayanissitaṃ bāhiraṅgaṃ. Hetuyo. Bho hetu, hetū hetave hetavo hetayo hetuyo. Sesaṃ pubbasamaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',56,replace(replace('<p class="bodytext">Bahuto <a name="M0.0056"></a> naṃmhi –</p>\r\n<p class="subhead">48. Bahukatinnaṃ</p>\r\n<p class="bodytext">Naṃmhi bahuno katissa ca nuka hoti tiliṅge. Bahunnaṃ. Sesaṃ bhikkhusamaṃ.</p>\r\n<p class="bodytext">Vattu si,</p>\r\n<p class="subhead">57. Ltu+pitādīna+mā simhi</p>\r\n<p class="bodytext">Ltupaccayantānaṃ pitu+mātu+bhātu+dhītu+duhitu+jāmātu+nattu+hotu+potūnañcā hoti simhi. Vattā. ‘‘Kattari ltu+ṇakā’’ti vihitaltupaccayassa gahaṇā yato dhātuto hi so vihito, ‘‘paccayaggahaṇe yasmā so vihito, tadādino tadantassa ca gahaṇaṃ’’ti ñāyā tadavinābhāvato tadantadhātunopi gahaṇaṃti ltupaccayantānaṃti vuttaṃ.</p>\r\n<p class="subhead">162. Ltu+pitādīna+ma se</p>\r\n<p class="bodytext">Ltupaccayantānaṃ pitādīnañcā+raṅa hoti sato+ññatra. ‘‘Āraṅasmā’’ti ṭo, vattāro.</p>\r\n<p class="subhead">58. Ge a ca</p>\r\n<p class="bodytext">Geltu+pitādīnaṃ a hoti ā ca. Bho vatta vattā, vattāro. Vattāraṃ, vattāre vattāro. Nāvacanassa ‘‘ṭā nāsmānaṃ’’ti ṭā, vattārā.</p>\r\n<p class="subhead">166. Su+hisvā+raṅa</p>\r\n<p class="bodytext">Su+hisu ltu+pitādīna+māraṅa vā hoti. Vattārehi vattārebhi vattūhi vattūbhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',57,replace(replace('<p class="subhead">165. Salopo</p>\r\n<p class="bodytext">Ltu+pitādīhi <a name="M0.0057"></a> sassa lopo vā hoti. Vattu vattuno vattussa.</p>\r\n<p class="subhead">163. Naṃmhi vā</p>\r\n<p class="bodytext">Naṃmhi ltu+pitādīna+māraṅa vā hoti. Vattārānaṃ.</p>\r\n<p class="subhead">164. Ā</p>\r\n<p class="bodytext">Naṃmhi ltu+pitādīna+mā vā hoti. Vattānaṃ vattūnaṃ. Smāssa ṭā, vattārā.</p>\r\n<p class="subhead">174. Ṭi smino</p>\r\n<p class="bodytext">Āravā+desamhā smino ṭi hoti.</p>\r\n<p class="subhead">176. Rassā+raṅa</p>\r\n<p class="bodytext">Smimhi āro rasso hoti. Vattari, vattāresu vattūsu vattusu. Evaṃ –</p>\r\n<p class="gatha1">Evaṃ bhattu kattu netu, sotu ñātu jetu chettu;</p>\r\n<p class="gathalast">Bhettu dātu dhātu boddhu, viññāpetādayopi ca.</p>\r\n<p class="bodytext">Satthusaddassa pana nāmhi bahulādhikārā ‘‘ltu+pitādīna+mase’’ti vā āravādese satthārā satthunā. Sesaṃ vattusamaṃ.</p>\r\n<p class="bodytext">Simhi ā, pitā. ‘‘Ltu+pitādīna+mase’’ti āravādese –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',58,replace(replace('<p class="subhead">177. Pitādīna+manatvādīnaṃ</p>\r\n<p class="bodytext">Natvādivajjitānaṃ <a name="M0.0058"></a> pitādīna+māro rasso hoti sabbāsu vibhattīsu. ‘‘Ltu+pitādīna+mase’’ ‘‘su+hisvā+raṅa’’ ‘‘naṃmhi vā’’ti ettha vuttavibhattīsu paresu āraṅa hotīti tā vibhattiyo paṭicca sabbāsūti vuttaṃ. Pitaro. Bho pita pitā, pitaro. Pitaraṃ, pitare pitaro. Pitarā, pitarehi pitarebhi pitūhi pitūbhi. Pitu pituno pitussa, pitarānaṃ pitūnaṃ. ‘‘Pitunnaṃ’’ti naṃmhi dīghe rassa+dvittānīti vuttaṃ. ‘‘Sānuvuttaṃ suttaṃ’’ti ñāyā ‘‘bahukatinnaṃ’’ti ettha nuka-iti yogavibhāgenapi sijjhati ettha anuvattitanaṃmhi. Pañcamīchaṭṭhī tatiyācatutthīsamaṃ. Pitari, pitaresu pītūsu, rassābhāvo. Nattā, nattāro. Bho natta nattā, nattāro iccādi vattusamaṃ.</p>\r\n<p class="bodytext">Guṇavantu si,</p>\r\n<p class="subhead">151. Ntussa</p>\r\n<p class="bodytext">Simhi ntussa ṭā hoti. Guṇavā. Yomhi ‘‘ntantūnaṃ nto yomhi paṭhame’’ti savibhattissa ntussa nto hoti. Ettha ca ‘‘ntu vantu+mantā+vantu+tavantusambandhī’’ti paribhāsato ntu ca vantvādisambandhīyeva gayhate, na jantu tantādīnaṃ. Guṇavanto. Aññatra –</p>\r\n<p class="subhead">91. Yvādo ntussa</p>\r\n<p class="bodytext">Yoādīsu ntussa a hoti. Guṇavantaiti akārantā ṭā+ṭeādesā honti, guṇavantā. ‘‘Ṭaṭāaṃ ge’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',59,replace(replace('<p class="bodytext">ṭādayo<a name="M0.0059"></a>, bho guṇava guṇavā guṇavaṃ, guṇavanto guṇavantā. ‘‘Ntassa ca ṭa vaṃ+se’’ti aṃsesu ntassa ṭo vā, guṇavaṃ guṇavantaṃ, guṇavante. ‘‘To+tā+ti+tā sa+smā+smiṃ+nāsū’’ti tāādayo honti, guṇavatā guṇavantena, guṇavantehi guṇavantebhi. ‘‘Lakkhaṇikapaṭipadottesu paṭipadottasseva gahaṇaṃ, na lakkhaṇikassā’’ti ñāyā ‘‘na lakkhaṇikassā’’ti vuttabyattipakkha+manapekkhitvā ‘‘ato’’ti rassākārajātiyā pekkhitattā guṇavantenāti ‘‘atenā’’ tya+nena sijjhati. Jāti=sāmaññaṃ, byatti=viseso. Guṇavato guṇavassa guṇavantassa, ‘‘taṃ naṃmhi’’ti naṃmhī taṃ vā, guṇavataṃ guṇavantānaṃ. Guṇavatā guṇavantā guṇavantamhā guṇavantasmā. Guṇavati guṇavante guṇavantamhi guṇavantasmiṃ, guṇavantesu. ‘‘Ntassa ca ṭa vā’’ti yogavibhāgā yosu ca ntussa vā ṭādese kate yossa ṭā, ‘‘cakkhumā andhitā honti’’, ‘‘vaggumudātīriyā bhikkhu vaṇṇavā’’ iccādī honti.</p>\r\n<p class="bodytext">Evaṃ gaṇavā kulavā phalavā yasavā dhanavā sutavā bhagavā himavā balavā sīlavā paññavā iccādī.</p>\r\n<p class="subhead">153. Himavato vā o</p>\r\n<p class="bodytext">Himavato simhi ntussa o vā hoti, himavanto himavā. Sesaṃ purimasamaṃ.</p>\r\n<p class="bodytext">Āyasmantusaddo kammavācāya kvaci bahulādhikārā dvivacanena āyasmantā, tiṇṇaṃ vacanena āyasmantoti dissati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',60,replace(replace('<p class="bodytext">Evaṃ <a name="M0.0060"></a> satimā dhitimā gatimā mutimā matimā jutimā sirimā hirimā thutimā ratimā yatimā sucimā kalimā balimā kasimā rucimā buddhimā cakkhumā bandhumā hetumā setumā ketumā rāhumā bhāṇumā khāṇumā vijjumā iccādayo.</p>\r\n<p class="centered">Ukārantaṃ.</p>\r\n<p class="bodytext">Vessabhū silopo. Te ca rassābhāvova viseso. ‘‘Ekavacanayosva+ghonaṃ’’ti rasse ‘‘lā yonaṃ vo pume’’ti vo, vessabhuvo, ‘‘vevosu lussā’’ti vuttattā na ṭādeso, ‘‘lopoti yolope vessabhū. ‘‘Ge vā’’ti vā rasse bho vessabhu vessabhū, vessabhuvo vessabhū. Vessabhuṃ, vessabhuvo vessabhū. Vessabhunā iccādi bhikkhusamaṃ. Evaṃ sayambhū parābhibhū abhibhūādayo. Gotrabhū+sahabhūsaddehi pana yonaṃ ‘‘jantvādito no vā’’ti no, vo vā, gotrabhuno gotrabhuvo gotrabhū. Sahabhuno sahabhuvo sahabhū, sesaṃ vessabhūsamaṃ.</p>\r\n<p class="subhead">85. Kūto</p>\r\n<p class="bodytext">Kūpaccayantato yonaṃ no vā hoti pulliṅge. Sabbaññuno. Aññatra ‘‘lā yonaṃ vo pume’’ti na vo, ‘‘kūto’’ti jantvādīhi puthakkaraṇā. Yolope sabbaññū. Bho sabbaññu sabbaññū, sabbaññuno sabbaññū iccādi.</p>\r\n<p class="bodytext">Evaṃ maggaññū dhammaññū atthaññū kālaññū rattaññū mattaññū kataññū kathaññū viññū vidū iccādayo. Ettha ‘‘vidā kū’’ ‘‘vito </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',61,replace(replace('<p class="bodytext">ñāto’’ <a name="M0.0061"></a> ‘‘kammā’’ti tīsu suttesu kūpaccayassa gahitattā vedagūādayo rūpaccayantā na gahitā.</p>\r\n<p class="centered">Ūkārantaṃ.</p>\r\n<p class="bodytext">Go, si, silopo. Go.</p>\r\n<p class="subhead">67. Gossā+ga+si+hi+naṃsu gāva+gavā</p>\r\n<p class="bodytext">Ga+si+hi+naṃvajjitāsu vibhattīsu gosaddassa gāva+gavā honti niccaṃ.</p>\r\n<p class="subhead">170. Ubha+gohi ṭo</p>\r\n<p class="bodytext">Ubha+gohi yonaṃ ṭo hoti. Gāvo gavo. Bho go, gāvo gavo.</p>\r\n<p class="subhead">72. Gāvu+mhi</p>\r\n<p class="bodytext">Aṃvacane gossa gāvu vā hoti. Gāvuṃ gāvaṃ gavaṃ, gāvo gavo.</p>\r\n<p class="subhead">71. Nāssā</p>\r\n<p class="bodytext">Goto nāssa ā hoti vā. Ekavaṇṇattā na sabbādeso. ‘‘Pañcamiyaṃ parasse’’ti vattante ‘‘ādissā’’ti nāssa ā hoti, paralopo, gāvā gāvena gavā gavena, gohi gobhi.</p>\r\n<p class="subhead">69. Gavaṃ sena</p>\r\n<p class="bodytext">Gossa se vā gavaṃ hoti saha sena. Gavaṃ gāvassa gavassa. Kaccāyane ‘‘gavaṃ ce taramānānaṃ’’ti pāḷiṃ paṭicca naṃmhi bahuvacanameva sādhitaṃ, idha ‘‘gavaṃva siṅgino saṅgaṃ’’ti dassanato ekavacanañca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',62,replace(replace('<p class="subhead">70. Gunnañca naṃnā</p>\r\n<p class="bodytext">Naṃvacanena <a name="M0.0062"></a> saha gossa gunnaṃ hoti gavañca vā. Gunnaṃ gavaṃ gonaṃ. Gāvā gāvamhā gāvasmā gavā gavamhā gavasmā. Gāve gāvamhi gāvasmiṃ gave gavamhi gavasmiṃ.</p>\r\n<p class="subhead">68. Sumhi vā</p>\r\n<p class="bodytext">Sumhi gossa gāva+gavā honti vā. Gāvesu gavesu gosu. Gossa goṇādeso na kato, saddantarattā. Goṇasaddo hi sattasu vibhattīsu dissatīti.</p>\r\n<p class="gatha1">Usu+bhūmi+pasu+raṃsi-disā+vācā+mbu+cakkhusu;</p>\r\n<p class="gathalast">Dasasva+tthesu go vutto, lagge ca vajire iti.</p>\r\n<p class="centered">Okārantaṃ.</p>\r\n<p class="centered">Iti pulliṅgaṃ.</p>\r\n<p class="bodytext">Kaññā, silopo.</p>\r\n<p class="subhead">1,11. Ghā</p>\r\n<p class="bodytext">Itthiyaṃ vattamānassa nāmassa+nte vattamāno ākāro ghasañño hoti. ‘‘Jantu+hetvī+gha+pehi vā’’ti yolopo vā. Kaññā kaññāyo. ‘‘Gha+brahmādite’’ti gassa e vā, kaññe kaññā, yomhi kaññā kaññāyo. ‘‘Gho ssaṃ+ssā yaṃ+tiṃsū’’ti aṃmhi rasso. Kaññaṃ, kaññā kaññāyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',63,replace(replace('<p class="subhead">45. Gha+pate+kasmiṃ nādīnaṃ ya+yā</p>\r\n<p class="bodytext">Ghapato <a name="M0.0063"></a> smiṃvibhattipariyantānaṃ ekatte nādīnaṃ ya+yā honti yathākkamaṃ. Kaññāya, kaññābhi kaññāhi. Kaññāya, kaññānaṃ.</p>\r\n<p class="subhead">103. Yaṃ</p>\r\n<p class="bodytext">Ghapato smino yaṃ vā hoti. Kaññāyaṃ kaññāya. ‘‘Gha+ pate+kasmiṃ nādīnaṃ ya+yā’’ ‘‘yaṃ’’ti ca imesaṃ apavādādīnaṃ visaye mhissa ussaggattā pavatti natthīti smino bahulādhikārā ‘‘smā+hi+sminna’’ miccādinā mhikate ‘‘dasasahassimhi dhātumhī’’ti sijjhati. Kaññāsu. Evaṃ –</p>\r\n<p class="gatha1">Saddhā medhā paññā vijjā, cintā mantā taṇhā vīṇā;</p>\r\n<p class="gathalast">Icchā mucchā ejā māyā, mettā mattā sikkhā bhikkhā.</p>\r\n<p class="gatha1">Jaṅghā gīvā jīvhā vācā, chāyā āsā gaṅgā nāvā;</p>\r\n<p class="gathalast">Gāthā senā lekhā sālā, mālā velā pūjā khiḍḍā.</p>\r\n<p class="gatha1">Pipāsā vedanā saññā, cetanā tasinā pajā;</p>\r\n<p class="gathalast">Devatā vaṭṭakā godhā, balākā parisā sabhā.</p>\r\n<p class="gatha1">Ūkā sephālikā laṅkā, salākā vālukā sikhā;</p>\r\n<p class="gathalast">Visākhā visikhā sākhā, vācā vañjhā jaṭā ghaṭā.</p>\r\n<p class="gatha1">Jeṭṭhā soṇḍā vitaṇḍā ca, karuṇā vanitā latā;</p>\r\n<p class="gathalast">Kathā niddā sudhā rādhā, vāsanā siṃsapā papā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',64,replace(replace('<p class="gatha1">Pabhā <a name="M0.0064"></a> sīmā khamā chāyā, khattiyā sakkharā surā;</p>\r\n<p class="gathalast">Dolā tulā silā lilā, lāle+lā mekhalā kalā.</p>\r\n<p class="gatha1">Vaḷavā+lambusā mūsā, mañjusā sulasā disā;</p>\r\n<p class="gathalast">Nāsā juṇhā guhā īhā, lasikā vasudhādayo.</p>\r\n<p class="bodytext">‘‘Na+mmādīhī’’ti ammā+annā+ambāhi gassa ekārā+bhāve –</p>\r\n<p class="subhead">62. Rasso vā</p>\r\n<p class="bodytext">Ammādīnaṃ ge rasso vā hoti. Amma ammā iccādi. Sesaṃ kaññāva. Evaṃ annā ambā. Sabhāparisāhi smino ‘‘tiṃ sabhāparisāyā’’ti tiṃ vā hoti. ‘‘Ghossa’’ mādinā rasse sabhatiṃ sabhāyaṃ sabhāya. Parisatiṃ parisāyaṃ parisāya.</p>\r\n<p class="centered">Ākārantaṃ.</p>\r\n<p class="bodytext">Mati, yomhi –</p>\r\n<p class="subhead">1,10. Pi+tthiyaṃ</p>\r\n<p class="bodytext">Itthiyaṃ vattamānassa nāmassa+nte vattamānā ivaṇṇu+vaṇṇā pasaññā honti.</p>\r\n<p class="subhead">116. Ye passi+vaṇṇassa</p>\r\n<p class="bodytext">Pasaññassa ivaṇṇassa lopo hoti vā yakāre. ‘‘Paro kvacī’’ti anuvattitakvaciggahaṇā ye pare ca-kāra+pubbarūpā- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',65,replace(replace('<p class="bodytext">ni <a name="M0.0065"></a> na honti. Matyo. Aññatra ‘‘jantvā’’dinā yolopo, dīgho, matī matiyo. Bho mati matī, matyo matī matiyo. Matiṃ, matyo matī matiyo. Matiyā, matīhi matībhi. Matyā matiyā, matīnaṃ. Smino yaṃ, matyaṃ matiyaṃ matyā matiyā, matīsu. Evaṃ –</p>\r\n<p class="gatha1">Patti yutti vutti kitti, mutti titti khanti kanti;</p>\r\n<p class="gathalast">Santi tanti siddhi suddhi, iddhi vuddhi buddhi bodhi.</p>\r\n<p class="gatha1">Bhūmi jāti pīti suti, nandi sandhi soṇi koṭi;</p>\r\n<p class="gathalast">Diṭṭhi vuṭṭhi tuṭṭhi yaṭṭhi, pāḷi āḷi nāḷi keḷi.</p>\r\n<p class="gatha1">Sati muti gati cuti, dhiti yuvati vikati;</p>\r\n<p class="gatha2">Rati ruci rasmi asani, vasani osadhi aṅguli;</p>\r\n<p class="gathalast">Dhūli dudrabhi doṇi, aṭavi chavi iccādi.</p>\r\n<p class="subhead">55. Ratyādīhi ṭo smino</p>\r\n<p class="bodytext">Ratyādīhi smino ṭo vā hoti. Ratto ratyaṃ rattiyaṃ ratyā rattiyā, rattīsu. Sesaṃ matisamaṃ.</p>\r\n<p class="centered">Ikārantaṃ.</p>\r\n<p class="bodytext">Dāsī, silopo. ‘‘Ekavacane’’ccādinā rasso. ‘‘Ye passi+vaṇṇassā’’ti īlopo, dāsyo. ‘‘Jantvā’’ dinā yolopo. Dāsī dāsiyo. ‘‘Ge vā’’ti rasso. Bho dāsi dāsī, dāsyo dāsī dāsiyo.</p>\r\n<p class="subhead">73. Yaṃ pito</p>\r\n<p class="bodytext">Pasaññīto aṃvacanassa yaṃ vā hoti. Dāsyaṃ dāsiyaṃ dāsiṃ, dāsyo dāsī dāsiyo. ‘‘Ghapatekā’’dinā yā. Dāsyā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',66,replace(replace('<p class="bodytext">dāsiyā<a name="M0.0066"></a>, dāsīhi dāsībhi. Dāsyā dāsiyā, dāsīnaṃ. Dāsyaṃ dāsiyaṃ dāsyā dāsiyā, dāsīsu. Evaṃ –</p>\r\n<p class="gatha1">Mahī vetaraṇī vāpī, pāṭalī kadalī ghaṭī;</p>\r\n<p class="gathalast">Nārī kumārī taruṇī, vāruṇī brāhmaṇī sakhī.</p>\r\n<p class="gatha1">Gandhabbī kinnarī nāgī, devī yakkhī ajī migī;</p>\r\n<p class="gathalast">Vānarī sūkarī sīhī, haṃsī kākī ca kukkuṭī.</p>\r\n<p class="unindented">Iccādayo.</p>\r\n<p class="bodytext">Ettha ca īkāralope ññakārapubbarūpo, vetarañño vetaraṇiyo. Vetaraññaṃ vetaraṇiyaṃ vetaraṇiṃ, vetarañño vetaraṇiyo iccādi. Yosu –</p>\r\n<p class="subhead">167. Najjā dhayāsvāma</p>\r\n<p class="bodytext">Yosu nadīsaddassa āma vā hoti. Suña+naka+āma ityādi ññakāra+kakāra+makārā āgamaliṅgā. Sa ca ‘‘mānubandho sarāna+mantā paro’’ti mānubandhattā sarāna+mantā paro hotīti īkārā paro. ‘‘Yavā sare’’ti ye dassa jo, yassa ca pubbarūpaṃ, najjāyo. Vā pa-lopa+yolopesu najjo nadī nadiyo iccādi.</p>\r\n<p class="centered">Īkārantaṃ.</p>\r\n<p class="bodytext">Yāgu, yāgū yāguyo. Bho yāgu, yāgū yāguyo. Yāguṃ, yāgū yāguyo. Yāguyā, yāgūhi yāgūbhi. Yāguyā, yāgūnaṃ. Yāguyaṃ yāguyā, yāgūsu. Evaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',67,replace(replace('<p class="bodytext">dhātu+dhenu+kāsu+daddu+kaṇḍu+kacchu+rajju+kareṇu+sassu+piyaṅgu <a name="M0.0067"></a> ādayo.</p>\r\n<p class="bodytext">Ettha dhātusaddo ‘‘manodhātunā’’ti abhidhammāvatāre vuttattā pulliṅgepi dissati, taṃ sakkaṭamatena vuttanti keci.</p>\r\n<p class="bodytext">Mātā, mātaro. Bho māta mātā, mātaro. Mātaraṃ, mātare mātaro. Mātarā, nāssa yādese ‘‘ye passā’’ti yogavibhāgā palopo, anuññāto ahaṃ matyā, aññatra mātuyā, mātarehi mātarebhi mātūhi mātūbhi. Salope mātu, pasaññattā ‘no’ na hoti, matyā mātuyā, mātarānaṃ mātānaṃ mātūnaṃ. Chaṭṭhivisaye ‘‘mātussa saratī’’tipi dissati. Mātari, mātaresu mātūsu. Visesā+ññatra pitusamaṃ. Evaṃ dhītu+duhitusaddā.</p>\r\n<p class="centered">Ukārantaṃ.</p>\r\n<p class="bodytext">Rassanisedhe silope ca kate jambū. ‘‘Jantvā’’dinā yolope, jambū jambuyo. ‘‘Ge vā’’ti rasse bho jambu, jambū jambuyo. Jambuṃ, jambū jambuyo. Jambuyā, jambūhi jambūbhi. Jambuyā, jambūnaṃ. Jambuyaṃ jambuyā, jambūsu.</p>\r\n<p class="gatha1">Evaṃ vadhū ca sarabhū, sarabū sutanū camū;</p>\r\n<p class="gathalast">Vāmūrū nāganāsurū, samānā khalu jambuyā.</p>\r\n<p class="centered">Ūkārantaṃ.</p>\r\n<p class="bodytext">Go, gāvo gavo iccādi pulliṅgasamaṃ.</p>\r\n<p class="centered">Itthiliṅgaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',68,replace(replace('<p class="bodytext">Napuṃsaka<a name="M0.0068"></a>, si –</p>\r\n<p class="subhead">111. Aṃ napuṃsake</p>\r\n<p class="bodytext">Akārantato nāmasmā sissa aṃ hoti napuṃsake. Napuṃsakaṃ.</p>\r\n<p class="subhead">112. Yonaṃ ni</p>\r\n<p class="bodytext">Akārantato yonaṃ ni hoti napuṃsake. Nīnaṃ niccavidhānepi ‘‘ninaṃ vā’’ti pakkhe ṭāṭe honti, dīghe napuṃsakā napuṃsakāni. Bho napuṃsaka napuṃsakā, napuṃsakā napuṃsakāni. Napuṃsakaṃ, napuṃsake napuṃsakāni. Napuṃsakena, iccādi sugatasaddasamaṃ. Evaṃ –</p>\r\n<p class="gatha1">Puñña+pāpa+phala+rūpa+sādhanaṃ,</p>\r\n<p class="gatha2">Sota+ghāna+sukha+dukkha+kāraṇaṃ;</p>\r\n<p class="gatha3">Dāna+sīla+dhana+jhāna+locanaṃ,</p>\r\n<p class="gathalast">Mūla+kūla+bala+jāla+maṅgalaṃ.</p>\r\n<p class="gatha1">Naḷina+liṅga+mukha+ṅga+jala+mbujaṃ,</p>\r\n<p class="gatha2">Pulina+dhañña+hirañña+phalā+mataṃ;</p>\r\n<p class="gatha3">Paduma+paṇṇa+susāna+vanā+yudhaṃ,</p>\r\n<p class="gathalast">Hadaya+cīvara+vattha+kuli+ndriyaṃ.</p>\r\n<p class="gatha1">Nayana+vadana+yāno+dana+sopāna+pānaṃ,</p>\r\n<p class="gatha2">Bhavana+bhuvana+lohā+lāta+tuṇḍa+ṇḍa+pīṭhaṃ,</p>\r\n<p class="gatha3">Karaṇa+maraṇa+ñāṇā+rammaṇā+rañña+tāṇaṃ,</p>\r\n<p class="gathalast">Tagara+nagara+tīra+cchatta+chiddo+dakāni.iccādi;</p>\r\n<p class="bodytext">Ekaccaṃ,</p>\r\n<p class="subhead">136. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',69,replace(replace('<p class="subhead">Na <a name="M0.0069"></a> nissa ṭā</p>\r\n<p class="bodytext">Ekaccādīhi parassa nissa ṭā na hoti, ekaccāni. Bho ekacca, ekaccā ekaccāni. Ekaccaṃ, ekacce ekaccāni. Sesaṃ napuṃsakaṃva.</p>\r\n<p class="bodytext">Evaṃ paṭhamaṃ, paṭhamāni iccādi. Padaṃ, padā padāni iccādi napuṃsakasamaṃ. Nāsmiṃsu bhedo.</p>\r\n<p class="subhead">106. Nāssa sā</p>\r\n<p class="bodytext">Padādīhi nāssa sā hoti vā. Padasā padena.</p>\r\n<p class="subhead">105. Padādīhi</p>\r\n<p class="bodytext">Padādīhi smino si hoti vā. Padasi pade padamhi padasmiṃ, padesu. Evaṃ bilasaddo.</p>\r\n<p class="bodytext">Kammasaddato nāssa ‘‘nāsse+no’’ti eno vā, kammena, ‘‘pumakammathāmā’’dinā utte kammunā kammanā. Imināva sasmāsu uttaṃ, ussa lasaññāyaṃ sa+smānaṃ yathāyogaṃ no+nā niccaṃ, vavatthitavibhāsattā vādhikārassa. Kammuno kammassa. Kammunā kammā kammamhā kammasmā. ‘‘Kammādito’’ti smino vā nimhi kammani kamme kammamhi kammasmiṃ, sesaṃ napuṃsakasamaṃ. Camma+vesma+bhasmādayo kammasamā uttato+ññatra.</p>\r\n<p class="bodytext">Gacchanta, si. ‘‘Ntassaṃ’’ti vā aṃmhi silopo gacchaṃ. Aññatra sissa daṃ, gacchantaṃ, gacchantā gacchantāni. Bho gaccha gacchā gacchaṃ, gacchantā gacchantāni. Gacchaṃ gacchantaṃ, gacchante gacchantāni. Gacchatā gacchantene+ccādi pulliṅgasamaṃ. Evaṃ yajanta+vajantādayo.</p>\r\n<p class="centered">Akārantaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',70,replace(replace('<p class="bodytext">Aṭṭhi<a name="M0.0070"></a>, silopo.</p>\r\n<p class="subhead">113. Jhalā vā</p>\r\n<p class="bodytext">Jhalato yonaṃ ni hoti vā napuṃsake. Aṭṭhīni. ‘‘Lopo’’ti yolope dīgho, aṭṭhī. Bho aṭṭhi aṭṭhī, aṭṭhīni aṭṭhī. Aṭṭhiṃ, aṭṭhīni aṭṭhī. Aṭṭhinā iccādi munisaddasamaṃ. Evaṃ pacchi+akkhi+dadhi+satthi+vāri+acciādayo.</p>\r\n<p class="centered">Ikārantaṃ.</p>\r\n<p class="bodytext">Daṇḍi, napuṃsakattā ‘‘ekavacane’’ccādinā rasse silopo. Daṇḍīni daṇḍī. ‘‘Ge vā’’ti rasse bho daṇḍi daṇḍī, daṇḍīni daṇḍī. ‘‘Naṃ jhīto’’ti naṃ. Daṇḍinaṃ daṇḍiṃ, daṇḍīni daṇḍī. Sesaṃ pulliṅge daṇḍīsamaṃ. Evaṃ sukhakārī+sīghayāyīādayo.</p>\r\n<p class="centered">Īkārantaṃ.</p>\r\n<p class="bodytext">Cakkhu, cakkhūni cakkhū. Sesaṃ aṭṭhisamaṃ. Evaṃ āyu+vasu+dhanu+dāru+tipu+madhu+siṅgu+hiṅgu+vatthu+jatu+ambu+ assuādīni. Āyusaddato nāssa kodhādittā sāva viseso.</p>\r\n<p class="centered">Ukārantaṃ.</p>\r\n<p class="bodytext">Gotrabhu, rasse silopo. Gotrabhūni gotrabhū. Bho gotrabhu gotrabhū, gotrabhūni gotrabhū. Gotrabhuṃ, gotrabhūni gotrabhū iccādi pulliṅge vessabhūsamaṃ. Evaṃ sayambhū+abhibhū+dhammaññū ādayo.</p>\r\n<p class="centered">Ūkārantaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',71,replace(replace('<p class="gatha1">Visadā+visadākāra-vohāro+bhayamuttako<a name="M0.0071"></a>;</p>\r\n<p class="gathalast">Pumādijānane hetu-bhāvato liṅga+mīrito.</p>\r\n<p class="gatha1">Thana+kesāvatī nārī, massuvā puriso siyā;</p>\r\n<p class="gathalast">Ubhinna+mantaraṃ etaṃ, itaro+bhayamuttako.</p>\r\n<p class="gatha1">Ese+sā eta+mīti ca,</p>\r\n<p class="gatha2">Pasiddhiatthesu yesu lokassa;</p>\r\n<p class="gatha3">Thī+puma+napuṃsakānī+ti,</p>\r\n<p class="gathalast">Vuccante tāni nāmāni.</p>\r\n<p class="centered">Napuṃsakaliṅgaṃ.</p>\r\n<p class="bodytext">Atha sabbādīnaṃ rūpanayo niddisiyate,</p>\r\n<p class="bodytext">Sabba katara katama ubhaya itara añña aññatara aññatama, pubba+parā+para+dakkhiṇu+ttarā+dharāni vavatthāya+masaññāyaṃ. ‘‘Ūnapūrattha+madhikapadodāharaṇa+majjhāhāro’’ti ñāyā sabbādīsu paṭhīyanteti yojetabbaṃ. Ya tya ta eta ima amu kiṃ eka tumha amha iccete sabbādayo. Kaccāyane adassitassāpi tyasaddassa –</p>\r\n<p class="gatha1">Khiḍḍā paṇihitā tyāsu, rati tyāsu patiṭṭhitā;</p>\r\n<p class="gathalast">Bījāni tyāsu ruhanti, yadidaṃ sattā pajāyareti –</p>\r\n<p class="bodytext">Pāḷiyaṃ dissamānattā idha saṅgaho.</p>\r\n<p class="bodytext">Tattha sabbasaddo niravasesattho. Katara+katamasaddā pucchanatthā. Ubhayasaddo dviavayavasamudāyavacano. Itarasaddo vuttapaṭiyogīvacano. Aññasaddo adhikatāparavacano. Añña- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',72,replace(replace('<p class="bodytext">tara+aññatamasaddā <a name="M0.0072"></a> aniyamatthā. Pubbādayo disādivavatthāvacanā. Yasaddo aniyamattho. Tya+tasaddā parammukhavacanā. Eta+ima+amu+kiṃ iccete samīpa+accantasamīpa+dūra+pucchanatthavacanā. Ekasaddo saṃkhyādivacano. Tumha+amhasaddā para+atta niddesavacanā.</p>\r\n<p class="bodytext">Sabbo, sissa o,</p>\r\n<p class="subhead">138. Yona+meṭa</p>\r\n<p class="bodytext">Akārantehi sabbādīhi yonaṃ eṭa hoti niccaṃ. Sabbe. Bho sabba sabbā, sabbe. Sabbaṃ, sabbe. Sabbena, sabbehi sabbebhi. Evaṃ karaṇe. Sabbassa.</p>\r\n<p class="subhead">99. Sabbādīnaṃ naṃmhi ca</p>\r\n<p class="bodytext">Akārantānaṃ sabbādīnaṃ e hoti naṃmhi su+hisu ca. Ettha ādisaddo avayave, vuttañhi –</p>\r\n<p class="gatha1">Mariyādāyaṃ pakāre ca, samīpe+vayave tathā;</p>\r\n<p class="gathalast">Catūsva+tthesu medhāvī, ādisaddaṃ pakāsayeti.</p>\r\n<p class="subhead">100. Saṃ+sānaṃ</p>\r\n<p class="bodytext">Sabbādito naṃvacanassa saṃ+sānaṃ honti. Sabbesaṃ sabbesānaṃ. Sabbā sabbamhā sabbasmā. Sabbe sabbamhi sabbasmiṃ, sabbesu.</p>\r\n<p class="bodytext">Itthiyaṃ ‘‘itthiya+matvā’’ti āpaccaye tassa ghasaññā. Sesaṃ kaññāva. Sabbā, sabbā sabbāyo. Bho sabbe, sabbā sabbāyo. ‘‘Gho ssaṃ+ssā+ssāyaṃ+tiṃ sū’’-ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',73,replace(replace('<p class="bodytext">rasse <a name="M0.0073"></a> sabbaṃ, sabbā sabbāyo. Sabbāya, sabbāhi sabbābhi.</p>\r\n<p class="subhead">101. Ghapā sassa ssā vā</p>\r\n<p class="bodytext">Sabbādīnaṃ ghapato sassa ssā vā hoti. Amussāti rūpassa ‘‘ssā vā te+ti+mā+mūhī’’ti ssādesena siddhattā vāggahaṇa+muttaratthaṃ. Rasse sabbassā sabbāya, sabbāsaṃ sabbāsānaṃ. Pañcamiyaṃ sabbāya.</p>\r\n<p class="subhead">102. Smino ssaṃ</p>\r\n<p class="bodytext">Sabbādīnaṃ ghapato smino ssaṃ vā hoti. Sabbassaṃ sabbāyaṃ sabbāya, sabbāsu.</p>\r\n<p class="bodytext">Napuṃsake sabbaṃ. ‘‘Yonaṃ nī’’ti napuṃsake yossa nimhi –</p>\r\n<p class="subhead">137. Sabbādīhi</p>\r\n<p class="bodytext">Sabbādīhi parassa nissa ṭā na hoti. Dīghe sabbāni. Bho sabba sabbā, sabbāni. Sabbaṃ, sabbe sabbāni. Nādīsu pumeva. Katarakatamaubhayā tīsu liṅgesu sabbasamā. Evaṃ itaraaññasaddā. Ssā+ssaṃsu viseso.</p>\r\n<p class="subhead">52. Ssaṃ+ssā+ssāyesvi-tare+ka+ññe+ti+māna+mi</p>\r\n<p class="bodytext">Ssamādīsu itara+eka+añña+eta+ima iccetesaṃ i hoti niccaṃ. Itarissā itarāya, aññissā aññāya, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',74,replace(replace('<p class="bodytext">aññāsaṃ <a name="M0.0074"></a> aññāsānaṃ. Aññissaṃ aññāyaṃ aññāya. Aññatara+ññatamā liṅgattaye sabbasamā.</p>\r\n<p class="bodytext">Pubbo,</p>\r\n<p class="subhead">143. Pubbādīhi chahi</p>\r\n<p class="bodytext">Etehi chahi savisaye eṭa vā hotīti yossa eṭa, pubbe pubbā. Bho pubba pubbā, pubbe pubbā. Pubbaṃ, pubbe. Pubbena. Sesaṃ sabbaliṅge sabbasamaṃ. Evaṃ parādayo pañca.</p>\r\n<p class="subhead">139. Nā+ññañca nāma+ppadhānā</p>\r\n<p class="bodytext">Taṃnāmabhūtehi appadhānehi ca sabbādīhi sabbādikāriyaṃ na hoti. Te sabbā, sabbanāmā teti attho. Te piyasabbā, te atisabbā. Nāmabhūte ca aññapadatthādo appadhānavisaye ca sabbādikāriyanisedhena ‘‘paramasabbe tiṭṭhanti’’ tyādito padhānapadantato eṭaādayo honti, visesanasamāsassa uttarapadatthapadhānattā.</p>\r\n<p class="subhead">140. Tatiyatthayoge</p>\r\n<p class="bodytext">Tatiyatthena yoge ca sabbādikāriyaṃ na hoti. Māsena pubbā māsapubbā iccādi.</p>\r\n<p class="subhead">141. Catthasamāse</p>\r\n<p class="bodytext">Catthasamāsavisaye sabbādikāriyaṃ na hoti. Dakkhiṇuttarapubbānantiādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',75,replace(replace('<p class="subhead">142. Ve+ṭa</p>\r\n<p class="bodytext">Iti <a name="M0.0075"></a> catthasamāse sabbādikāriyaṃ na hoti, niccena eṭaādesappasaṅge ayaṃ sampattavibhāsā. Pubbuttare pubbuttarā. Sesaṃ sugatasamaṃ.</p>\r\n<p class="bodytext">Yo, ye. Yā, yāyo. Yaṃ, yāni iccādi sabbasamaṃ. Yādīna+mālapane rūpaṃ na sambhavati.</p>\r\n<p class="bodytext">Tya si –</p>\r\n<p class="subhead">128. Tya+te+tānaṃ tassa so</p>\r\n<p class="bodytext">Tya+te+tāna+manapuṃsakānaṃ tassa so hoti simhi. Syo, tye. Syā, tyā, tyāyo. Tyaṃ, tyāni iccādi sabbasamaṃ.</p>\r\n<p class="bodytext">So,</p>\r\n<p class="subhead">131. Tatassa no sabbāsu</p>\r\n<p class="bodytext">Tasaddassa tassa no vā hoti sabbāsu vibhattīsu. ‘‘Tya+te+tānaṃ tassā’’ti ca ettha tyādīnaṃ takāraggahaṇaṃ syā sā esā nāyoti itthiyaṃ sabbasamā hotīti. Ne te. Naṃ taṃ, ne te. Nena tene, nehi nebhi tehi tebhi.</p>\r\n<p class="subhead">132. Ṭa sasmāsmiṃssāyassaṃssāsaṃmhāmhisvi+massa ca</p>\r\n<p class="bodytext">‘‘Ṭa sa+smā+smiṃ+naṃsmi+massa cā’’ti vuttepi tesaṃ vibhattīnaṃ ādesesu ssāyādīsu paresu ‘‘tadādesā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',76,replace(replace('<p class="bodytext">tadiva <a name="M0.0076"></a> bhavantī’’ti ñāyā ṭādese siddhepi yādiādesantare pare nivattanatthaṃ ssādīnaṃ gahaṇaṃ.</p>\r\n<p class="bodytext">Sādīsvi+massa tasaddatakārassa ca ṭo vā hoti. Pubbassaralopo. Eva+muparipi. Assa nassa tassa, nesaṃ nesānaṃ tesaṃ tesānaṃ. Amhā asmā namhā nasmā tamhā tasmā. Amhi asmiṃ namhi nasmiṃ tamhi tasmiṃ, nesu tesu.</p>\r\n<p class="bodytext">Itthiyaṃ sā, nā nāyo tā tāyo. Naṃ taṃ, nā nāyo tā tāyo. Nāmhi –</p>\r\n<p class="subhead">46. Ssā vā te+ti+mā+mūhi</p>\r\n<p class="bodytext">Ghapasaññehi ta+eta+ima+amūhi ekatte nādīnaṃ ssā vā hoti. Antassarānaṃ ghapavohārena taṃsahacaritāpi saddā ‘‘kunte pavesayā’’ti ñāyā gayhantīti ‘‘ghapasaññehi ta+eta+ima+amūhī’’ti vuttaṃ. Vāṭādese assā nassā nāya.</p>\r\n<p class="subhead">53. Tāya vā</p>\r\n<p class="bodytext">Ssaṃssāssā yesu tassa vā i hoti. Tissā tassā tāya, nāhi nābhi tāhi tābhi. Sassa vā ssāmhi assā nassā tissā tassā.</p>\r\n<p class="subhead">54. Te+ti+māto sassa ssāya</p>\r\n<p class="bodytext">Tā+etā+imāto sassa ssāyo hoti vā. Assāya nassāya tissāya tassāya, ‘‘ghapate’’ccādinā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',77,replace(replace('<p class="bodytext">yādese <a name="M0.0077"></a> nāya tāya. Naṃvacanassa sa+mādese takārassa ca vā ṭādese āsaṃ nāsaṃ nāsānaṃ tāsaṃ tāsānaṃ. Sattamiyaṃ assaṃ assā nassaṃ nassā nāyaṃ nāya tissaṃ tissā tassaṃ tassā tāyaṃ tāya, nāsu tāsu.</p>\r\n<p class="bodytext">Napuṃsake naṃ taṃ, nāni tāni. Naṃ taṃ, ne nāni te tāni. Sesaṃ pumeva.</p>\r\n<p class="bodytext">‘‘Yaṃtaṃsaddā niccasambandhā’’ti ñāyā yaṃsaddena aniyamitatthaṃ taṃsaddo niyameti.</p>\r\n<p class="gatha1">Pasiddhe anubhūtatthe, pakkantavisaye tathā;</p>\r\n<p class="gathalast">Yaṃsadda+manapekkheva, taṃsaddo yujjate sadāti –</p>\r\n<p class="bodytext">Vuttattā ettheva taṃsaddo yaṃsaddaṃ nāpekkhati. Yathākkamaṃ tatri+da+mudāharaṇaṃ –</p>\r\n<p class="bodytext">(.) ‘‘Namo tassā’’ti ca, (.) ‘‘aggimpa+kkhinā…pe… ñātakārī hi so jino’’ti ca, (..) Purimagāthāya vuttamunisadda+mapekkhitvā ‘‘savāsane kilese so’’ti ca.</p>\r\n<p class="bodytext">Eso, ete. Esā, etā etāyo, etaṃ, etāni iccādi ṭa+nādesābhāvova viseso.</p>\r\n<p class="bodytext">Ima si,</p>\r\n<p class="subhead">127. Simha+napuṃsakassā+yaṃ</p>\r\n<p class="bodytext">Imasaddassa anapuṃsakassa ayaṃ hoti simhi. Ayaṃ, ime. Imaṃ, ime.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',78,replace(replace('<p class="subhead">126. Nāmha+ni+mi</p>\r\n<p class="bodytext">Imasaddassa <a name="M0.0078"></a> anitthiyaṃ nāmhi ana+imiiccādesā honti. ‘‘Ate+nā’’ti ene anena iminā. Himhi –</p>\r\n<p class="subhead">125. Imassā+nitthiyaṃ ṭe</p>\r\n<p class="bodytext">Imassā+nitthiyaṃ ṭe hoti vā su+naṃ+hisu. ‘‘Nāmaggahaṇe liṅgavisiṭṭhassāpi gahaṇaṃ’’ti ñāyā ‘anitthiyaṃ’ti itthiliṅganisedhā imassāti nāmaggahaṇavisaye liṅgavisesitassa imasaddassāpi gahaṇaṃ. Tassa phalaṃ ‘‘tatassa no sabbāsu’’ tyādo liṅgattaye kāriyasiddhi. Ehi ebhi imehi imebhi. ‘‘Ṭa sasmāsmi’’miccādinā sabbassi+massa vā ṭādese assa imassa, vā ṭe esaṃ esānaṃ imesaṃ imesānaṃ. Amhā asmā imamhā imasmā. Amhi asmiṃ imamhi imasmiṃ, esu imesu.</p>\r\n<p class="bodytext">Itthiyaṃ ayaṃ, imā imāyo. Imaṃ, imā imāyo. Nā ‘‘ssā vā te+ti+mā+mūhī’’ti ssā vā, vā ṭādese ‘‘ssa’’ miccādinā iādese ca kate assā imissā imāya, imāhi imābhi. Assā imissā assāya imissāya imāya. Naṃvacanassa sa+mādese imassa ca vā ṭādese assa ‘‘sunaṃhi sū’’ti dīghe ca kate āsaṃ, aññatra imāsaṃ imāsānaṃ. Sattamiyaṃ assaṃ imissaṃ assā imissā imāyaṃ imāya, imāsu.</p>\r\n<p class="bodytext">Napuṃsake –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',79,replace(replace('<p class="subhead">201. Imassi+daṃ vā</p>\r\n<p class="bodytext">Aṃsisu <a name="M0.0079"></a> saha tehi imassi+daṃ hoti vā napuṃsake. Idaṃ imaṃ, ime imāni. Idaṃ imaṃ, ime imāni. Anena iminā iccādi pulliṅgasamaṃ.</p>\r\n<p class="subhead">197. Ime+tāna+menā+nvādese dutiyāyaṃ</p>\r\n<p class="bodytext">Imaetasaddānaṃ kathitānukathanavisaye dutiyāya+menādeso hoti. Imaṃ bhikkhuṃ vinaya+majjhāpaya, atho enaṃ dhamma+majjhāpaya. Ime bhikkhū vinaya+majjhāpaya, atho ene dhamma+majjhāpaya. Eva+metassa ca yojanīyaṃ.</p>\r\n<p class="bodytext">Amu si,</p>\r\n<p class="subhead">129. Massā+mussa</p>\r\n<p class="bodytext">Anapuṃsakassā+mussa makārassa so hoti simhi. Asu, yo –</p>\r\n<p class="subhead">86. Lopo+musmā</p>\r\n<p class="bodytext">Niyamasutta+midaṃ, amusaddato yonaṃ lopo hoti niccaṃ pulliṅge. Dīghe amū. Jhalato yonaṃ ‘‘lopo’’ti lope siddhepi vo+pavādo+ya+mārambho.</p>\r\n<p class="gatha1">Ārambho vacanampatti, lakkhaṇaṃ yogalakkhaṇaṃ;</p>\r\n<p class="gathalast">Vākyaṃ satthañca iccādi, suttāna+mabhidhāyakā.</p>\r\n<p class="bodytext">Amuṃ, amū. Amunā, amūhi amūbhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',80,replace(replace('<p class="subhead">87. Na no sassa</p>\r\n<p class="bodytext">Amusmā <a name="M0.0080"></a> sassa no na hoti. Amussa, amūsaṃ amūsānaṃ. ‘‘Nāsmāssā’’ti lato smāssa nā, amunā amumhā amusmā. Amumhi amusmiṃ, amūsu.</p>\r\n<p class="bodytext">Itthiyaṃ asu, amū amuyo. Amuṃ, amū amuyo. Nā, ‘‘ssā vā te+ti+mā+mūhī’’ti nādyekavacanānaṃ ssā vā, amussā amuyā, amūhi amūbhi. Amussā amuyā, amūsaṃ amūsānaṃ. Sattamiyaṃ amussaṃ amussā amuyaṃ amuyā, amūsu.</p>\r\n<p class="bodytext">Napuṃsake –</p>\r\n<p class="subhead">202. Amussā+duṃ</p>\r\n<p class="bodytext">Aṃsisu saha tehi amussa aduṃ hoti vā napuṃsake. Aduṃ, silopo, amuṃ. ‘‘Jhalā vā’’ti vāniādese amūni amū. Aduṃ amuṃ, amūni amū. Amunā iccādi pulliṅgasamaṃ.</p>\r\n<p class="bodytext">‘‘Sakatte’’ti kapaccaye –</p>\r\n<p class="subhead">130. Ke vā</p>\r\n<p class="bodytext">Amu eva amukoti sakatthe kapaccaye taddhitavuttittā ‘‘ekatthatāyaṃ’’ti silope ca kate ‘‘nimittābhāve nemittikassābhāvo’’ti ñāyā nimittabhūtassa sissā+bhāve nemittikassa ‘‘massā+mussā’’ti kattabbassa sakārassa nivuttīti ‘ke vā’ti vikappena massa sakārattha+ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',81,replace(replace('<p class="bodytext">mida+māraddhaṃ<a name="M0.0081"></a>. Amussa massa ke sa hoti vā. Asuko amukā, asukā amukā. Asukaṃ amukaṃ iccādi.</p>\r\n<p class="bodytext">Kiṃ si.</p>\r\n<p class="subhead">198. Kissa ko sabbāsu</p>\r\n<p class="bodytext">Sabbāsu vibhattīsu kissa ko hoti. Sisso, ko, ke. Kaṃ, ke. Kena, kehi kebhi.</p>\r\n<p class="subhead">199. Ki sasmiṃsu vā nitthiyaṃ</p>\r\n<p class="bodytext">Anitthiyaṃ kissa ki vā hoti sasmiṃsu. Kissa kassa, kesaṃ kesānaṃ. Kamhā kasmā. Kimhi kismiṃ kamhi kasmiṃ, kesu.</p>\r\n<p class="bodytext">Itthiyaṃ vibhattīsu paresu kādese kate akārantattā majjhe āpaccayakaraṇa+maviruddhanti ā, kā, kāyo. Kaṃ, kā kāyo iccādi sabbāva.</p>\r\n<p class="bodytext">Napuṃsake –</p>\r\n<p class="subhead">200. Ki+maṃsisu saha napuṃsake</p>\r\n<p class="bodytext">Aṃsisu saha tehi kiṃ saddassa kiṃhoti napuṃsake. Kādesassa sāmaññattā ‘‘visesavihitā vidhayo sāmaññavidhayo nisedhentī’’ti ñāyā kiṃādesena kādesanivutti. Kiṃ, kāni. Kiṃ, ke kāni. Kene+ccādi pubbeva.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',82,replace(replace('<p class="bodytext">Ekasaddo <a name="M0.0082"></a> saṃkhyā+tulya+ñña+sahāyavacano. Yadā saṃkhyāvacano, tadā ekavacananto, atra ekasaddo saṃkhyeyyavāpī. Aññatra tulyādīsu bahuvacanantopi. Eko, eke. Ekā, ekā ekāyo. Ekaṃ, ekāni+ccādi sabbasamaṃ tiliṅge.</p>\r\n<p class="bodytext">Tulye eko vilāso dvinnaṃ kumārānaṃ, eke vaṇṇasaddā dvinnaṃ kumārānaṃ. Aññatthe eko ācariyo eva+māha, eke ācariyā eva+māhaṃsu. Asahāyate ekova araññaṃ pavisitvā.</p>\r\n<p class="bodytext">Eko, eke. Ekā, ekā ekāyo. Ekaṃ ekāni+ccādi sabbasamaṃ tiliṅge. Ssā+ssaṃsu pana ‘‘ssa’’mādinā i, ekissā ekāya, ekissaṃ ekāyaṃ ekāya.</p>\r\n<p class="bodytext">Idha attaparagāravavasena ekassāpi ‘‘amhākaṃ rañño’’tipi ‘‘eke ācariyā’’tipi bahuvacanassa lokena icchitattā bahulavidhānā bahuvacaneneva sijjhati.</p>\r\n<p class="bodytext">Tumha+amhasaddā aliṅgā, tathā ubha+kati+dvisaddā, pañcādayo aṭṭhārasantā ca. Tumha si, amha si.</p>\r\n<p class="subhead">212. Tumhassa tuvaṃtva+mamhi ca</p>\r\n<p class="bodytext">Amhi simhi ca tumhassa savibhattissa tuvaṃ+tvaṃ honti. Tuvaṃ tvaṃ.</p>\r\n<p class="subhead">211. Simha+haṃ</p>\r\n<p class="bodytext">Simhi amhassa savibhattissa ahaṃ hoti. Ahaṃ, yevasve+ṭa, tu mhe.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',83,replace(replace('<p class="subhead">2,209. Maya+masmā+mhassa</p>\r\n<p class="bodytext">Yosva+mhassa <a name="M0.0083"></a> savibhattissa maya+masmā vā honti yathākkamaṃ. Mayaṃ amhe.</p>\r\n<p class="subhead">227. Aṃmhi taṃ+maṃ+tavaṃ+mamaṃ</p>\r\n<p class="bodytext">Aṃmhi tumhaamhasaddānaṃ savibhattīnaṃ taṃ+maṃ+tavaṃ+mamaṃ honti yathākkamaṃ. Taṃ, maṃ, tavaṃ, mamaṃ.</p>\r\n<p class="subhead">231. Dutiyā yomhi vā</p>\r\n<p class="bodytext">Tumhaamhasaddānaṃ savibhattīnaṃ paccekaṃ ṅaṃ+ṅākaṃ vā honti yomhi dutiye, tumhaṃ tumhākaṃ tumhe, amhaṃ amhākaṃ amhe.</p>\r\n<p class="subhead">228. Nāsmāsu tayā+mayā</p>\r\n<p class="bodytext">Nāsmāsu tumhaamhasaddānaṃ savibhattīnaṃ tayā+mayā honti yathākkamaṃ. ‘‘Nāsmāsū’’ti bahuvacanesupi vibhattikkama+manapekkhitvā saddakkamena paccekaṃ dve dve honti.</p>\r\n<p class="subhead">213. Tayātayīnaṃ tva vā tassa</p>\r\n<p class="bodytext">Tumhassa tayātayīnaṃ takārassa tva hoti vā. Tvayā tayā, mayā. Tumhehi tumhebhi, amhehi amhebhi.</p>\r\n<p class="subhead">229. Tava+mama+tuyhaṃ+mayhaṃ se</p>\r\n<p class="bodytext">Se tumhaamhasaddānaṃ savibhattīnaṃ ete ādesā honti yathākkamaṃ. Tava tuyhaṃ, mama mayhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',84,replace(replace('<p class="subhead">211. Naṃsesva+smākaṃ+mamaṃ.</p>\r\n<p class="bodytext">Naṃsesva+mhassa <a name="M0.0084"></a> savibhattissa asmākaṃ+mamaṃ honti yathākkamaṃ. Mamaṃ.</p>\r\n<p class="subhead">230. Ṅaṃ+ṅākaṃ naṃmhi</p>\r\n<p class="bodytext">Naṃmhi tumhaamhasaddānaṃ savitattīnaṃ ṅaṃ+ṅākaṃ honti paccekaṃ. Tumhaṃ tumhākaṃ amhaṃ amhākaṃ asmākaṃ. Kaccāyane ekavacanassa aṃvidhānatthaṃ sutta+māraddhaṃ, ettha pana attagāravavasena aṃmhi tumhaṃ amhaṃbhi sijjhanti bahulādhikārā.</p>\r\n<p class="subhead">214. Smāmhi tvamhā</p>\r\n<p class="bodytext">Smāmhi tumhassa savitattissa tvamhā hoti vā. Tvamhā tvayā tayā, mayā.</p>\r\n<p class="subhead">226. Smimhi tumhamhānaṃ tayi+mayi</p>\r\n<p class="bodytext">Smimhi tumhaamhasaddānaṃsavibhattīnaṃ tayi+mayi hoti yathākkamaṃ. Tvayi tayi, mayi. Tumhesu.</p>\r\n<p class="subhead">203. Sumhā+mhassā+smā</p>\r\n<p class="bodytext">Amhassa asmā hoti vā sumhi. Asmāsu amhesu. Sabbādayo vutta+mapekkhantā vakkhamānaṃ vāti ida+mesaṃ lakkhaṇaṃ.</p>\r\n<p class="subhead">232. Apādādo padate+kavākyeti</p>\r\n<p class="bodytext">Adhikāro. Ettha pādo nāma gāthāya catutthaṃso, tasmā ‘‘tumhehi puññaṃ pasutaṃ anappakaṃ’’ti ettha vo na hoti. Ettha padanti vutta saddo sabhāvato –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',85,replace(replace('<p class="gatha1">Ākāsavāyuppabhavo <a name="M0.0085"></a> sarīrā,</p>\r\n<p class="gatha2">Samuccaraṃ vatta+mupeti nādo;</p>\r\n<p class="gatha3">Ṭhānantare suppaṭihaññamāno,</p>\r\n<p class="gathalast">Vaṇṇatta+māgacchati so tu saddoti –</p>\r\n<p class="bodytext">Vuttattā ekeko vaṇṇo saddo nāma, tabbaṇṇasamūho padaṃ, tappadasamūho vākyañca. Tathā hi –</p>\r\n<p class="gatha1">Vitatyantaṃ padaṃ tassa, ca yo vākyanti manvayaṃ;</p>\r\n<p class="gathalast">Upacārā vaṇṇasadda-vāccaṃ taṃ na pariccaje.</p>\r\n<p class="bodytext">Taṃ padañca –</p>\r\n<p class="gatha1">Padaṃ catubbidhaṃ vuttaṃ, nāmā+khyāto+pasaggajaṃ;</p>\r\n<p class="gathalast">Nipātajañca taññū hi, asso khalvā+bhidhāvatīti.</p>\r\n<p class="bodytext">Taṃ vākyañca –</p>\r\n<p class="bodytext">Ekākhyāto padaccayo, siyā vākyaṃ sakārakoti –</p>\r\n<p class="bodytext">Vuttaṃ. Tasmā ‘‘vibhatyantaṃ padaṃ, padasamūho vākya’’nti ca vuccati.</p>\r\n<p class="subhead">233. Yonaṃhisva+pañcamyā vo+no</p>\r\n<p class="bodytext">Apañcamiyāyonaṃhisva+pādādo vattamānānaṃ padasmā paresa+mekavākye ṭhitānaṃ tumhaamhasaddānaṃ savibhattīnaṃ vo+no honti vā yathākkamaṃ. Gāmaṃ vo gaccheyyātha, gāmaṃ tumhe gaccheyyātha. Gāmaṃ no gaccheyyāma, gāmaṃ amhe gaccheyyāma. Pahāya vo gamissāmi, mā no vikantiṃsu. Dīyate vo, dīyate tumhaṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',86,replace(replace('<p class="bodytext">Dīyate <a name="M0.0086"></a> no, dīyate amhaṃ. Tuṭṭho+smi vo pakatiyā, tuṭṭho+smi tumhaṃ. Satthā no bhagavā, eso amhākaṃ satthā. Kataṃ vo, kataṃ tumhehi. Kataṃ no, kataṃ amhehi.</p>\r\n<p class="subhead">234. Te+me nāse</p>\r\n<p class="bodytext">Nāmhi se ca apādādo vattamānānaṃ dasmā paresa+mekavākye ṭhitānaṃ tumhaamhasaddānaṃ savibhattīnaṃ te+me vā honti yathākkamaṃ. Kataṃ te, kataṃ tayā. Kataṃ me, kataṃ mayā. Dīyate te, dīyate tava. Dīyate me, dīyate mama. Dhanaṃ te, dhanaṃ tava. Dhanaṃ me, dhanaṃ mama.</p>\r\n<p class="subhead">237. Na ca+vā+hā+he+vayoge</p>\r\n<p class="bodytext">Cādīhi yoge tumhaamhasaddānaṃ vo+no, te+me na honti. Gacchāma tumhe ca mayañca, passati tumhe ca amhe ca, kataṃ tumhehi ca amhehi ca, dīyate tumhañca amhañca, dhanaṃ tumhañca amhañca, kataṃ tayā ca mayā ca, dīyate tava ca mama ca, dhanaṃ tava ca mama ca. Evaṃ vādiyogepi.</p>\r\n<p class="subhead">235. Anvādese</p>\r\n<p class="bodytext">Kathitānukathitavisaye tumhaamhasaddānaṃ ādesā niccaṃ honti punabbidhānā. Gāmo tumhaṃ pariggaho, atho janapado vo pariggaho. Anvādese atho athoti vāradvayābhāvā niccanti vuttaṃ. Athosaddo kathitasseva puna kathanato anvādesajotako.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',87,replace(replace('<p class="subhead">236. Sapubbā paṭhamantā vā</p>\r\n<p class="bodytext">Vijjamānapubbasmā <a name="M0.0087"></a> paṭhamantā paresaṃ tumhāmhānaṃ ādesā vā honti anvādese. Gāme paṭo tumhākaṃ atho nagare kambalo vo atho nagare kambalo tumhākaṃ, athoti anukathanavāradvayattā vikappo sapubbāti kiṃ, paṭo tumhākaṃ, atho kambalo vo. Paṭhamantāti kiṃ, paṭo nagare tumhākaṃ, atho kambalo gāme vo.</p>\r\n<p class="subhead">238. Dassanatthe+nālocane</p>\r\n<p class="bodytext">Dassanatthe ālovacanavajjite payujjamāne tumhaamhāna+mādesā na honti. Gāmo tumhe uddissa āgato, gāmo amhe uddissa āgato. Anālocaneti kiṃ, gāmo vo āloceti, gāmo no āloceti.</p>\r\n<p class="subhead">239. Āmantaṇaṃ pubba+masantaṃva</p>\r\n<p class="bodytext">Āmantaṇaṃ pubba+vijjamānaṃ viya hoti tumhāmhāna+mādesavisaye. Devadatta tava pariggaho. Āmantaṇanti kiṃ, kambalo te pariggaho.</p>\r\n<p class="subhead">240. Na sāmaññavacana+mekatthe</p>\r\n<p class="bodytext">Samānādhikaraṇe parato sāmaññavacana+māmantaṇaṃ ekatthe asantaṃ viya na hoti māṇavaka jaṭilaka te pariggaho. Parāmanthaṇe asatipi pubba+mupādāya ādeso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',88,replace(replace('<p class="subhead">241. Bahūsu vā</p>\r\n<p class="bodytext">Bahūsu <a name="M0.0088"></a> vattamāna+māmantaṇaṃ sāmaññavacana+mekatthe asantaṃ viya vā na hoti. ‘‘Siddhe satyārambho nīyamāya vā vikappāyavā’’ti vuttattā vikappattha+midaṃ. Brāhmaṇā guṇavanto tumhākaṃ pariggaho, brāhmaṇā guṇavanto vo pariggaho.</p>\r\n<p class="bodytext">Ubha+katisaddā bahuvacanantā. ‘‘Ubha+gohi ṭo’’ti yonaṃ ṭo, ubho. Kathaṃ ‘‘ubhayo vasemase’’ti, ṭomhi yakārāgamo. Ubho.</p>\r\n<p class="subhead">59. Suhisu+bhasso</p>\r\n<p class="bodytext">Ubhassa suhisvo hoti. Ubhohi ubhobhi.</p>\r\n<p class="subhead">50. Ubhi+nnaṃ</p>\r\n<p class="bodytext">Ubhā naṃvacanassa innaṃ hoti. Ubhinnaṃ, ubhosu.</p>\r\n<p class="subhead">168. Ṭi katimhā</p>\r\n<p class="bodytext">Katimhā yonaṃ ṭi hoti. Kati, kati. Jhato yolopapasaṅge dīghanivattanatthaṃ ṭiādeso. Katīhi katībhi. ‘‘Bahukatinnaṃ’’ti nuka, katinnaṃ, katīsu.</p>\r\n<p class="subhead">1,54. Vicchā+bhikkhaññesu dve</p>\r\n<p class="bodytext">Vicchāya+mābhikkhaññe ca dve rūpāni honti. Kriyā+guṇa+dabbehi byāpetu+micchā vicchā. Rukkhaṃ rukkhaṃ siñcanti, gāmo gāmo ramaṇīyo, gāme gāme pānīyaṃ. Ābhikkhaññaṃ=ponopuññaṃ, pacati pacati, papacati papacati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',89,replace(replace('<p class="subhead">1,55. Syādilopo pubbasse+kassa</p>\r\n<p class="bodytext">Vicchāya+mekassa <a name="M0.0089"></a> dvitte pubbassa syādilopo hoti. Ekassa ekassāti dvitte ekekassa. Kathaṃ ‘‘matthakamatthakenā’’ti, ‘‘syādilopo pubbassā’’ti yogavibhāgā, yogavibhāgā ca iṭṭhapasiddhīti.</p>\r\n<p class="subhead">1,56. Sabbādīnaṃ vītihāre</p>\r\n<p class="bodytext">Sabbādīnaṃ vītihāre dve bhavanti. Pubbassa syādilopo ca, aññassa aññassa bhojakā aññamaññassa bhojakā, evaṃ itarītarassa.</p>\r\n<p class="subhead">1,57. Yāvabodhaṃ sambhame</p>\r\n<p class="bodytext">Turitenā+pāyahetupadassanaṃ sambhamo, tasmiṃ sati vattu yāvantehi saddehi so+ttho viññāyate, tāvanto payujjante. Sappo sappo sappo, bujjhassu bujjhassu bujjhassu, bhinno bhikkhusaṅgho, bhinno bhikkhusaṅgho.</p>\r\n<p class="gatha1">Bhaye kodhe pasaṃsāyaṃ, turite kotūhala+cchare;</p>\r\n<p class="gathalast">Hāse soke pasāde ca, kare āmeḍitaṃ budho.</p>\r\n<p class="title">Saṅkhyākaṇḍa</p>\r\n<p class="bodytext">Atha saṅkhyāsaddā vuccante. Ekādayo aṭṭhārasantā saṅkhyeyyavacanā. Vīsatiādayo ‘‘bhikkhūnaṃ vīsatī’’tiādīsu saṅkhyāvacanā, ‘‘vīsati bhikkhavo’’tiādīsu saṅkhyeyyavacanā. Ekasaddo sabbādīsu vuttova. Dvādayo aṭṭhārasantā bahuvacananthāva.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',90,replace(replace('<p class="subhead">219. Yomhi dvinnaṃ duve+dve</p>\r\n<p class="bodytext">Yomhi <a name="M0.0090"></a> dvissa savibhattissa duve+dve honti paccekaṃ. Duve dve, duve dve, dvīhi dvībhi.</p>\r\n<p class="subhead">220. Duvinnaṃ naṃmhi vā</p>\r\n<p class="bodytext">Naṃmhi dvissa savibhattissa duvinnaṃ hoti vā. Duvinnaṃ, aññatra –</p>\r\n<p class="subhead">47. Naṃmhi nuka dvādīnaṃ sattarasannaṃ</p>\r\n<p class="bodytext">Dvādīnaṃ sattarasannaṃ saṅkhyānaṃ nuka hoti naṃmhi vibhattimhi. Ukāro uccāraṇattho, kakāro antāvayavattho. Ettha nāgamo vibhattissa ādyāvayavo ce, ‘‘āgamā tagguṇībhūtā taggahaṇena gayhante’’ti ñāyā nāgamopi taṃgahaṇena gayhatīti ‘‘sunaṃhisū’’ti dīghappasaṅge pakatiyā antāvayavabhūte sarantatā natthīti na dīgho. Dvinnaṃ, dvīsu.</p>\r\n<p class="subhead">207. Pume tayo+cattāro</p>\r\n<p class="bodytext">Yomhi savibhattīnaṃ ti+catunnaṃ tayo+cattāro honti yathākkamaṃ pulliṅge. Tayo, tayo, tīhi tībhi.</p>\r\n<p class="subhead">49. Ṇṇaṃ+ṇṇannaṃ tito jhā</p>\r\n<p class="bodytext">Jhasaññito tito naṃvacanassa ṇṇaṃ+ṇṇannaṃ honti. Tiṇṇaṃ tiṇṇannaṃ, tīsu.</p>\r\n<p class="bodytext">Itthiyaṃ –</p>\r\n<p class="subhead">205. Tisso catasso yomhi savibhattīnaṃ</p>\r\n<p class="bodytext">Vibhattisahitānaṃ ticatunnaṃ yomhi tisso+catasso honti itthiyaṃ yathākkamaṃ. Tisso, tisso, tīhi tībhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',91,replace(replace('<p class="subhead">204. Naṃmhi ticatunna+mitthiyaṃ tissa+catassā</p>\r\n<p class="bodytext">Naṃmhi <a name="M0.0091"></a> ticatunnaṃ tissa+catassā honti+tthiyaṃ yathākkamaṃ. Naṃmhīti catutthīchaṭṭhīnaṃ sāmaññavacanaṃ, no ce naṃsūti vadati, yathākkamaṃti saddadvayāpekkhaṃ. Eva+mīdisa+maññampi. Tissannaṃ tīsu.</p>\r\n<p class="subhead">206. Tīṇi+cattāri napuṃsake</p>\r\n<p class="bodytext">Yomhi savibhattīnaṃ ticatunnaṃ yathākkamaṃ tīṇi+cattāri honti napuṃsake. Tīṇi, tīṇi, tīhi iccādi pulliṅgeva.</p>\r\n<p class="bodytext">Catu yo,</p>\r\n<p class="subhead">208. Caturo vā catussa</p>\r\n<p class="bodytext">Catussa savibhattissa yomhi caturo hoti vā pulliṅge. Caturo cattāro. Kathaṃ ‘‘caturo nimitte nāddasāsi’’nti, liṅgavipallāsena siddhaṃ, cattāri nimittānīti attho. Vipallāso tividho liṅgavipallāso vacanavipallāso vibhattivipallāsoti. Catūhi catūbhi. Catunnaṃ, catūsu.</p>\r\n<p class="bodytext">Itthiyaṃ catasso. Catasso. Catūhi catūbhi. Catassannaṃ. Catassannaṃ. Catūsu.</p>\r\n<p class="bodytext">Napuṃsake cattāri. Cattāri. Catūhi catūbhi. Iccādi pumeva.</p>\r\n<p class="subhead">169. Ṭa pañcādīhi cuddasahi</p>\r\n<p class="bodytext">‘‘Sutte liṅgavacanamatta’’nti ñāyā ṭa-iti vuttaṃ. Pañcādīhi cuddasahi saṅkhyāhi yonaṃ ṭa hoti. Pañca. Pañca. Yonaṃ ṭā+ṭenivatthanatthaṃ ṭa-vidhānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',92,replace(replace('<p class="subhead">90. Pañcādīnaṃ cuddasanna+ma</p>\r\n<p class="bodytext">Pañcādīnaṃ <a name="M0.0092"></a> cuddasannaṃ sunaṃhisva hoti. Etta+dīghāpavādo+yaṃ. Apavādati bādhetīti apavādo. Tasmā ‘‘pakapyāpavādavisayamussaggā abhinivisante’’ti ñāyā ‘‘suhisva+sse’’ ‘‘sunaṃhisu’’ti ca ussaggā ‘‘pañcādīnaṃ cuddasanna+ma’’ iti apavādavisayaṃ na pavisanti, sāmaññattā. Eva+muparipi ‘‘pañcamiyaṃ parassa’’ ‘‘ādissā’’ti. Pañcahi pañcabhi pañcannaṃ. Pañcannaṃ. Pañcasu. Evaṃ chādayo aṭṭhārasantā.</p>\r\n<p class="bodytext">‘‘Catthe’’ti eko ca dasa cāti cattasamāse ‘‘amādī’’ti ekena adhikā dasāti tatiyāsamāse vā kate ‘‘ekatthatāya’’nti vibhattilopo. Eva+mupari ca.</p>\r\n<p class="subhead">3,102. Ekaṭṭhāna+mā</p>\r\n<p class="bodytext">Ekaaṭṭhānaṃ ā hoti dase pare.</p>\r\n<p class="subhead">3,103. Ra saṃkhyāto vā</p>\r\n<p class="bodytext">Saṃkhyāto parassa dasassa ra hoti vibhāsā. Sa ca ‘‘pañcamiyaṃ parassā’’ti vattamāne ‘‘ādissā’’ti dakārasseva hoti. Ekārasa ekādasa. Ekārasahi ekādasahi. Ekārasannaṃ ekādasannaṃ. Ekārasasu ekādasasu. Eva+mekādasaiccādipi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',93,replace(replace('<p class="subhead">3,94. Ā saṃkhyāyā satādo nāññatthe</p>\r\n<p class="bodytext">Saṃkhyāya+muttarapade <a name="M0.0093"></a> dvissā hoti asatādo nāññatthe.</p>\r\n<p class="bodytext">Āviṭṭhaliṅgattā saṃkhyāyaṃ uttarapade saliṅgeneva visesanaṃ bhavati.</p>\r\n<p class="gatha1">Suddhaṃ missañca saṃkiṇṇaṃ, upasajjanameva ca;</p>\r\n<p class="gathalast">Āviṭṭha+matha vā+byattaṃ, chadhā liṅgaṃ vivaṃyate.</p>\r\n<p class="bodytext">Ettha yathākkamaṃ rukkho latā paṇṇaṃti suddhaṃ. Ghaṭo ghaṭī, vajiro vajiraṃ, vedanā vedanaṃti missaṃ. Taṭo taṭī taṭaṃti saṃkiṇṇaṃ. Sukko paṭo, sukkā paṭi, sukkaṃ vatthaṃti upasajjanaṃ. Rājā saraṇaṃ, guṇo pamāṇaṃti āviṭṭhaṃ. Tuvaṃ ahaṃ kati pañcāti abyattaṃ. Iti liṅgaṃ veditabbaṃ. Dvādasa.</p>\r\n<p class="subhead">3,98. Bā cattālīsā do</p>\r\n<p class="bodytext">Dvissa bā vā hoti cattālīsā do nāññatthe. Rāde se bārasa.</p>\r\n<p class="subhead">3,95. Tisse</p>\r\n<p class="bodytext">Saṃkhyāya+muttarapade tissa e hota+satādo nā+ññatthe.</p>\r\n<p class="subhead">3,104. Chatīhi ḷo ca</p>\r\n<p class="bodytext">Chatīhi parassa dasassa ḷo hoti ro ca. Teḷasa terasa.</p>\r\n<p class="subhead">3,100. Catussa cuco dase</p>\r\n<p class="bodytext">Catussa cu+co honti vā dase pare. Dvitte cuddasa coddasa catuddasa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',94,replace(replace('<p class="subhead">3,99. Vīsatidasesu pañcassa paṇṇa+pannā</p>\r\n<p class="bodytext">Vīsati <a name="M0.0094"></a> sesu paresu pañcassa paṇṇa+pannā honti yathākkamaṃ. Pannarasa pañcadasa.</p>\r\n<p class="subhead">3,101. Chassa so</p>\r\n<p class="bodytext">Chassa so-icca+ya+mādeso hoti se pare. Soḷasa sorasa. Sattarasa sattadasa. ‘‘Ekaṭṭhāna+mā’’ti ā, aṭṭhārasa aṭṭhādasa.</p>\r\n<p class="bodytext">Ūnā ca sā vīsati cāti ‘‘visesana+mekatthene’’ti visesanasamāse ‘‘itthiyaṃ bhāsitapumi+tthī pume+vekatthe’’ti pumatte ca kate ekena ūnā vīsatīti tatiyāsamāse ekūnavīsati.</p>\r\n<p class="bodytext">Vīsatiādayo ānavutiyā itthiliṅge+kavacanā. Bho ekūnavīsati. Ekūnavīsatiṃ. Ekūnavīsatyā ekūnavīsatiyā. Ekūnavīsatyaṃ ekūnavīsatiyaṃ ekūnavīsatyā ekūnavīsatiyā. Matisamaṃ.</p>\r\n<p class="bodytext">Evaṃ vīsati, ekavīsati, dvāvīsati bāvīsati, tevīsati catuvīsatippabhutayo. Paṇṇaādese paṇṇavīsati pañcavīsati. Evaṃ chabbīsati, sattavīsati, aṭṭhavīsati.</p>\r\n<p class="bodytext">Ekena ūnā tiṃsati tiṃsā vāti ekūnatiṃsati ekūnatiṃsā vā. Karaṇe ekūnatiṃsāya. Ekūnatiṃsāya, ekūnatiṃsāya, ekūnatiṃsāya, ekūnatiṃsāyaṃ. Evaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',95,replace(replace('<p class="bodytext">tiṃsatiṇiṃsāvabhutayo<a name="M0.0095"></a>. Tiṃsāsaddassa pana silope ‘‘dīghasso’’ti yogavibhāgā rasso, tiṃsa. Niggahītāgamo, tiṃsaṃ. Bhotiṃse+ccādi pumeva.</p>\r\n<p class="bodytext">Cattālīsādīsupi yathāsambhavaṃ evameva. Dvitte ekattiṃsati ekattiṃsā. Rassadvitte dvattiṃsati dvattiṃsa. Evaṃ tettiṃsati tettiṃsādayo yāva ekūnacattālīsati ekūnacattālīsā. Cattālīsāya simhi cattārīsā cattālīsā cattālīsaṃ, bho cattālīse+ccādi hoti. Evaṃ ekacattārīsā.</p>\r\n<p class="subhead">3,97. Dvīssā ca</p>\r\n<p class="bodytext">Asatādo nāññatthe cattālīsādo dvissa e vā hoti ā ca. Dvecattālīsā dvecattālīsa dvecattālīsaṃ, evaṃ dvācattālīsa-iccādi. Dvecattārīsā dvecattārīsa dvecattārīsaṃ, evaṃ dvācattārīsā dvācattārīsa iccādi. Cattālīsati ekacattālīsati dvecattālīsati dvācattālīsati iccādi ekūnavīsatisamaṃ.</p>\r\n<p class="subhead">3,98. Cattālīsādo vā</p>\r\n<p class="bodytext">Asatādo nāññatthe cattālīsādo tisse hoti vā. Tecattālīsā tecattālīsa tecattālīsaṃ. Bho tecattālīse iccādi hoti. Evaṃ ticattālīsā tecattārīsā ticattārīsā iccādi, tecattālīsati ticattālīsati iccādi pubbeva.</p>\r\n<p class="bodytext">Evaṃ catucattālīsā pañcacattālīsā catucattālīsati pañcacattālīsati iccādi yāva ekūnapaññāsā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',96,replace(replace('<p class="bodytext">Paññāsā <a name="M0.0096"></a> paññāsa paññāsaṃ iccādi hoti. Evaṃ dvepaññāsā dvāpaññāsā dvipaññāsā dvepaṇṇāsā dvāpaṇṇāsā dvipaṇṇāsā. Tepaññāsā tipaññāsā tipaññāsaṃ tepaṇṇāsā tipaṇṇāsā. Catupaṇṇāsā pañcapaṇṇāsā iccādi yāva ekūnasaṭṭhi.</p>\r\n<p class="bodytext">Evaṃ saṭṭhi, ekasaṭṭhi, dvesaṭṭhi dvāsaṭṭhi dvisaṭṭhi, tesaṭṭhi tisaṭṭhi, catusaṭṭhi pañcasaṭṭhi iccādi yāva ekūnasattati.</p>\r\n<p class="bodytext">Sattati, ekasattati, dvesattati dvāsattati dvisattati, tesattati tisattati. Dvesattari dvāsattari dvisattari, catusattati, pañcasattati, catusattari, pañcasattarīti yāva ekūnāsīti.</p>\r\n<p class="bodytext">Asīti, ekāsīti, dvāsīti, yāgame dviyāsīti, teasīti tiyāsīti, caturāsīti, pañcāsīti iccādi yāva ekūnanavuti.</p>\r\n<p class="bodytext">Navuti, ekanavuti, dvenavuti dvānavuti dvinavuti, tenavuti tinavuti, catunavuti, pañcanavuti iccādi yāva ekūnasataṃ, etaṃ napuṃsakaliṅga+mekavacanantaṃ.</p>\r\n<p class="bodytext">Sataṃ, bho sata satā, sataṃ, sataṃ. Satena, karaṇe satena. Satassa, satā satamhā satasmā, satassa, sate satamhi satasmiṃ. Evaṃ ekasatato pabhuti yāva sahassaṃ.</p>\r\n<p class="bodytext">Koṭi pakoṭi koṭippakoṭi akkhobhiṇiyo itthiliṅge+kavacanantā. Vaggabhede tu sabbāsampi saṃkhyānaṃ bahuvacanopi hoteva ‘‘dvevīsatiyo jinadantā’’ ‘‘tisso vīsatiyo dinaghaṭikā’’ iccādi.</p>\r\n<p class="bodytext">Dasadasakaṃ sataṃ nāma, dasasataṃ sahassaṃ nāma, dasasahassaṃ nahutaṃ, dasanahutaṃ lakkhaṃ, satasahassantipi vuccati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',97,replace(replace('<p class="bodytext">Lakkhasataṃ <a name="M0.0097"></a> koṭi, koṭilakkhasataṃ pakoṭi, pakoṭilakkhasataṃ koṭippakoṭi, evaṃ nahutaṃ, ninnahutaṃ, akkhobhiṇī, bindu, abbudaṃ, nirabbudaṃ, ahahaṃ, ababaṃ, aṭaṭaṃ, sogaṇḍikaṃ, uppalaṃ, kumudaṃ, puṇḍarīkaṃ, padumaṃ, kathānaṃ, mahākathānaṃ, asaṅkhyeyyanti yathākkamaṃ satalakkhaguṇaṃ veditabbaṃ.</p>\r\n<p class="gatha1">Iccevaṃ ṭhānato ṭhānaṃ, satalakkhaguṇaṃ mataṃ;</p>\r\n<p class="gathalast">Koṭippabhutīnaṃ vīsa-saṅkhyānañca yathākkamanti.</p>\r\n<p class="bodytext">Athā+saṃkhyā vuccate, abyayanti ca vuccate. Taṃ pādi cādi, upasagganipātāti ca duvidhaṃ.</p>\r\n<p class="bodytext">Pa parā apa saṃ anu ava o ni du vi adhi api ati su u abhi pati pari upa ā ime vīsati pādayo. Pādayo hi jotakā, na vācakā.</p>\r\n<p class="bodytext">Tattha pa-saddo pakārā+dikamma, padhāna+ntobhāva, viyoga, tappara, bhusattha, sambhava, titti, anāvila, patthanādīsu. Yathā pakāre-paññā. Ādikamme-vippakataṃ, padhāne-paṇītaṃ, antobhāve-pakkhittaṃ, khittanti peraṇaṃ, pakkhittanti antokaraṇaṃ, dhātvatthassa bādhitattā. Vutthañhi –</p>\r\n<p class="gatha1">Dhātvatthaṃ bādhate koci, koci taṃ anuvattate;</p>\r\n<p class="gathalast">Tameva+ñño viseseti, upasaggagatī tidhāti.</p>\r\n<p class="bodytext">Ettha ca ‘‘anuruddhakā me saṅgāmayuddhe’’ti ca ime upasaggā dhātvatthaṃ visesenti nāma. Viyoge-pavāsī, tappare-pācariyo, bhusatthe-pavuddhakāyo, sambhave-himavantā gaṅgā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',98,replace(replace('<p class="bodytext">pabhavati<a name="M0.0098"></a>, tittiyaṃ-pahūta+mannaṃ, anāvile-pasanna+mudakaṃ, patthane-paṇihitaṃ.</p>\r\n<p class="bodytext">Parāiti parihāni, parājaya, gati, vikkamā+masanādīsu. Yathā parihāniyaṃ-parābhavo. Parājaye-parājito, gatiyaṃ-parāyanaṃ, vikkame-parakkamati, āmasane-aṅgassa parāmasanaṃ.</p>\r\n<p class="bodytext">Apaiti apagata, garaha, vajjana, pūjā, padussanādīsu. Yathā apagate-apamāno apeto, garahe-apagabbho, vajjane-apasālāya āyanti vāṇijā, pūjāyaṃ-vuddhāpacāyī, padussane-aparajjhanti.</p>\r\n<p class="bodytext">Saṃiti samodhāna, sammāsama, samantabhāva, saṃgata, saṃkhepa, bhusattha, saha+ppattha, pabhavā+bhimukhabhāva, vidhāna, punappuna, karaṇa, samiddhādīsu. Yathā samodhāne-sandhi, sammāsame-samādhi, sampayutto, samantabhāve-saṃkiṇṇaṃ, samullapanā, saṃgate-saṅgamo, saṃkhepe-samāso, bhusatthe-sārattho, sahatthe-saṃvāso, appatthe-samaggho, pabhave-sambhavo, abhimukhabhāve-sammukhaṃ. Saṃgate-saṃgaṇhāti, vidhāne-saṃvutaṃ, punappunakaraṇe-sandhāvati, samiddhiyaṃ-sampanno.</p>\r\n<p class="bodytext">Anuiti anugatā+nupacchinna, pacchāttha, bhusattha, sādissa, hīna, tatiyattha, lakkhaṇi+tthambhūtakkhāna, bhāga, vicchādīsu. Anugate-anveti, anupacchinne-anusayo, pacchātthe-anurathaṃ, bhusatthe-anuratto, sādisse-anurūpaṃ, hīne-anusāriputtaṃ paññavanto, tatiyatthe-nadi+manvavasitā senā, lakkhaṇerukkha+manuvijjotate vijju, itthambhūtakhyāne-sādhu devadatto </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',99,replace(replace('<p class="bodytext">mātara+manu<a name="M0.0099"></a>, bhāge-ya+dettha maṃ anu siyā, taṃ dīyatu, vicchāyaṃ-rukkhaṃ rukkhaṃ anu vijjotate cando.</p>\r\n<p class="bodytext">Avaiti adhobhāga, viyoga, paribhava, jānana, <span class="note">[suddhi]</span> nicchaya, desa, theyyādīsu. Adhobhāge-avakkhittacakkhu, viyoge-avakokilaṃ vanaṃ, paribhave-avajānanaṃ, avamaññati, jānane-avagacchati. <span class="note">[suddhiyaṃ–vodānaṃ (rūpasiddhi)]</span>, Nicchaye-avadhāraṇaṃ, dese-avakāso, theyye-avahāro.</p>\r\n<p class="bodytext">Oiti oroharaṇa, nīharaṇa, suddhiādīsu dissati. Orohaṇe-pāsādā orohati, nīharaṇe-omukkupāhano, suddhiyaṃ-odātaṃ.</p>\r\n<p class="bodytext">Niiti, nissesa, niggata, nīharaṇa,+ntopavesanā+bhāva, nisedhana, nikkhanta, pātubhāvā+vadhāraṇa, vibhajana, upamu+padhāraṇā+vasāna, cheka, nīharaṇā+varaṇādīsu. Nissese-nirutti, niggate-nikkileso, niyyāti, nīharaṇe-niddhāraṇaṃ, antopavesane-nikhāto, abhāve-nimmakkhikaṃ, nisedhe-nivāreti, nikkhante-nibbānaṃ, pātubhāve-nimmitaṃ, avadhāraṇe-nicchayo, vibhajane-niddeso. Upamāyaṃ-nidassanaṃ, upadhāraṇe-nisāmanaṃ, avasāne-niṭṭhitaṃ, cheke-nipuṇo, nīharaṇe-nīharati, issa dīgho. Āvaraṇe-nīvaraṇaṃ.</p>\r\n<p class="bodytext">Duiti asobhaṇā+bhāva, kucchitā+samiddhi, kiccha, virūpatādīsu. Asobhaṇe-duggandho, abhāve-dubbhikkhaṃ, kucchite – </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',100,replace(replace('<p class="bodytext">dukkaṭaṃ<a name="M0.0100"></a>. Asamiddhiyaṃ-dussassaṃ, kicche-dukkaraṃ, virūpatāyaṃ-dubbaṇṇo dummukho.</p>\r\n<p class="bodytext">Viiti visesa, vividha, viruddha, vigata, viyoga, virūpatādīsu. Visese-vimutti, visiṭṭho, vividhe-vimati, viruddhe-vivādo, vigate-vimalaṃ, viyoge-vippayutto, virūpatāyaṃ-virūpo.</p>\r\n<p class="bodytext">Adhiiti adhiki+ssarū+paribhāvā+dhibhavana+jjhāyā+dhiṭṭhāna, nicchaya, pāpuṇanādīsu. Adhike-adhisīlaṃ, issare-adhipati, adhi brahmadatte pañcālā, uparibhāve-adhirohati, pathaviṃ adhisessati, adhibhavane-adhibhavati, ajjhāyane-byākaraṇa+madhīte, adhiṭṭhāne-bhūmikampādiṃ adhiṭṭhāti, nicchaye-adhimokkho, pāpuṇane-bhogakkhandhaṃ adhigacchati.</p>\r\n<p class="bodytext">Apiiti sambhāvanā+pekkhā, samuccaya, garaha, pañhādīsu. Sambhāvanāyaṃ-api dibbesu kāmesu, merumpi vinivijjhitvā gaccheyya, apekkhāyaṃ-ayampi dhammo aniyato, samuccaye-itipi arahaṃ, antampi antaguṇampi ādāya, garahe-api amhākaṃ paṇḍitaka, pañhe-api bhante bhikkhaṃ labhittha.</p>\r\n<p class="bodytext">Atiiti atikkamanā+tikkantā+tisaya, bhusatthādīsu. Atikkame-atirocati amhehi, atīto, atikkante-accantaṃ, atisaye-atikusalo, bhusatthe-atikodho, ativuddhi.</p>\r\n<p class="bodytext">Suiti sobhaṇa, suṭṭhu, sammā, samiddhi, sukhatthādīsu. Sobhaṇe-sugandho, suṭṭhu+sammādatthesu-suṭṭhu gato sugato, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',101,replace(replace('<p class="bodytext">sammā <a name="M0.0101"></a> gatotipi sugato, samiddhiyaṃ-subhikkhaṃ, sukhatthe-sukaro.</p>\r\n<p class="bodytext">Uiti uggatu+ddhakamma, padhāna, viyoga, sambhava, attalābha, satti, sarūpakathanādīsu. Uggate-uggacchati, uddhakamme-āsanā uṭṭhito, ukkhepo, padhāne-uttamo, lokuttaro, viyoge-ubbhāsito, sambhave-ubbhuto, attalābhe-uppannaṃ ñāṇaṃ, sattiyaṃ-ussahati gantuṃ, sarūpakathane-uddisati suttaṃ.</p>\r\n<p class="bodytext">Abhiiti abhimukhabhāva, visiṭṭhā+dhiku+ddhakamma, kula, sāruppa, vandana, lakkhaṇi+tthambhūtakkhāna, vicchādīsu. Abhimukhabhāve-abhimukho, abhikkamati, visiṭṭhe-abhidhammo, adhike-abhivasati, uddhakamme-abhiruhati. Kule-abhijāto, sāruppe-abhirūpo, vandane-abhivādeti, lakkhaṇādīsu purimasamaṃ.</p>\r\n<p class="bodytext">Patiiti paṭigata, paṭiloma, paṭinidhi, paṭidāna, nisedha, nivattana, sādissa, paṭikaraṇā+dāna, paṭibodha, paṭicca, lakkhaṇitthambhūtakkhāna, bhāga, vicchādīsu. Paṭigate-paccakkhaṃ, paṭilome-paṭisotaṃ, paṭinidhimhi-ācariyato pati sisso, paṭidāne-telatthikassa ghataṃ paṭidadāti, nisedhe-paṭisedheti, nivattane-paṭikkamati, sādisse-paṭirūpakaṃ, paṭikaraṇe-paṭikāro, ādāne-paṭiggaṇhāti, paṭibodhe-paṭivedho, paṭicce-paccayo, lakkhaṇādīsu purimasamaṃ.</p>\r\n<p class="bodytext">Pariiti samantatobhāva, pariccheda, vajjanā+liṅgana, nivasana, pūjā, bhojanā+vajānana, dosakkhāna, lakkhaṇādīsu. Samantha- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',102,replace(replace('<p class="bodytext">tobhāve-parivuto<a name="M0.0102"></a>, paricchede-pariññeyyaṃ, vajjane-pariharati, āliṅgane-parissajati, nivasane-yo vatthaṃ paridahessati, pūjāyaṃ-paricariyā, bhojane-bhikkhuṃ parivisati, avajānane-paribhavati. Dosakkhāne-paribhāsati, lakkhaṇādīsu-rukkhaṃ pari vijjotate vijju iccādi.</p>\r\n<p class="bodytext">Upaiti upagamana, samīpū+papatti, sādissā+dhiku+paribhāvā+nasana, dosakkhāna, saññā, pubbakamma, pūjā, gayhākāra, bhusatthādīsu. Upagamane-nisinnaṃ vā upanisīdeyya, samīpe-upanagaraṃ, upapattiyaṃ-saggaṃ lokaṃ upapajjati, atha vā upapatti=yutti, yathā upapattito ikkhatīti upekkhā, sādisse-upamānaṃ, upamā, adhike-upakhāriyaṃ doṇo, uparibhāve-upasampanno, anasane-upavāso, dosakkhāne-paraṃ upavadati, saññāyaṃ-upadhā, upasaggo, pubbakamme-upakkamo, upakāro, pūjāyaṃ-buddhupaṭṭhāko, mātupaṭṭhānaṃ, gayhākāre-soceyyapaccupaṭṭhānaṃ, bhusatthe-upādānaṃ, upāyāso, upanissayoti.</p>\r\n<p class="bodytext">Āiti abhimukhabhāvu+ddhakamma+mariyādā+bhividhi, patti+cchā, parissajana, ādikamma, gahaṇa, nivāsana, samīpa, āvhānādīsu. Abhimukhabhāve-āgacchanti, uddhakamme-ārohati, mariyādāyaṃ-āpabbatā khettaṃ, abhividhimhi-ākumāraṃ yaso kaccāyanassa, pattiyaṃ-āpattiṃ āpanno, icchāyaṃ-ākaṅkhā, parissajane-āliṅganaṃ, ādikamme-ārambho, gahaṇe-ādīyati, āliṅgati, (ālimpati, rū), nivāse-āvasatho, āvāso, samīpe-āsannaṃ, āvhāne-āmanteti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',103,replace(replace('<p class="gatha1">Pa <a name="M0.0103"></a> parā+pa sa+manva+va, o ni du rabhi byā+dhisu;</p>\r\n<p class="gatha2">Ati ni ppati pari apayo, upa ā iti vīsati;</p>\r\n<p class="gathalast">Esa hi bho upasagga-vidhi+kkamato kathito.</p>\r\n<p class="bodytext">Ettha ca –</p>\r\n<p class="gatha1">Upasagga+nipātā ca, paccayā ca ime tayo;</p>\r\n<p class="gathalast">Neke+nekatthavācakā, iti neruttikā+bravuṃ.</p>\r\n<p class="subhead">118. Asaṃkhyehi sabbāsaṃ</p>\r\n<p class="bodytext">Avijjamānasaṃkhyehi parāsaṃ sabbāsaṃ vibhattīnaṃ lopo hoti. Na santi ekavacanādisaṃkhyā etesanti asaṃkhyā, tesaṃ asaṃkhyānaṃ vibhattīnaṃ bhede asatipi syādividhānaṃ ‘‘mā no ajja vikantiṃsū’’tiādo vibhatyantattā padattasiddhīti vo+noādīnaṃ siddhiyā ca tesaṃ paṭhamādiatthe dassanato ca hoti. Pahāro parābhavo apahāro saṃhāro avahāro ohāro iccādi hoti.</p>\r\n<p class="gatha1">Upecca+tthaṃ sajantīti, upasaggā hi pādayo;</p>\r\n<p class="gathalast">Cādī padādimajjhante, nipātā nipatantīti.</p>\r\n<p class="centered">Opasaggikapadaṃ.</p>\r\n<p class="bodytext">Samuccaya+vikappana+paṭisedha+pūraṇādiatthaṃ asattavācikaṃ nepātikaṃ.</p>\r\n<p class="bodytext">Ca-iti samuccayā+nvācaya+itaretarayoga+samāhārā+vadhāraṇādīsu. Vā-iti vikappanu+pamāna+samuccaya+vavatthitavibhāsādīsu. Na, no, mā, a, alaṃ, halaṃ iccete </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',104,replace(replace('<p class="bodytext">paṭisedhe<a name="M0.0104"></a>. ‘‘Dve paṭisedhā pakatiatthaṃ gamayantī’’ti ñāyā ‘‘na tesaṃ pana rūpānaṃ paccayā na ca hontī’’ti ettha na ca na honti-hontevāti pakatiatthaṃ gamayati. Alaṃ pariyatti+bhūsanesu ca.</p>\r\n<p class="bodytext">Pūraṇatthaṃ duvidhaṃ attapūraṇaṃ padapūraṇañca, tattha atha, khalu, vata, atho, assu, yagghe, hi, carahi, naṃ, taṃ, ca, tu, vā, vo, pana, have, kīva, ha, tato, yathā, sudaṃ, kho, ve, haṃ, enaṃ, (evaṃ, rū) seyyathidaṃ iccevamādi padapūraṇe. Tattha atha-iti pañhā+nantariyā+dhikārādīsu ca. Khalu-iti paṭisedhā+vadhāraṇa, pasiddhīsu ca. Vata+iti ekaṃsa, khedā+nukampa, saṃkappesu ca. Atho-iti anvādese ca. Hi-iti hetu, avadhāraṇesu ca. Tu-iti visesa, hetu, nivattanādīsu ca. Pana-iti visesepi. Have+ve iccete ekaṃsatthepi. Haṃ-visāda, vimhesupi. (Visāda, sambhavesu, rū). Seyyathidaṃti taṃ katamaṃti atthepi.</p>\r\n<p class="bodytext">Atthapūraṇaṃ duvidhaṃ vibhattiyuttaṃ avibhattiyuttañca. Atthi, sakkā, labbhā iccete paṭhamāyaṃ. Āvuso ambho hambho are hare re je iccete āmantaṇe. Divā bhiyyo (namo, rū) iccete paṭhamādutiyāsu. Sayaṃ sāmaṃ saṃ sammā kinti iccete tatiyatthe. So+to+dhāpaccayantā ca suttaso padaso aniccato dukkhato ekadhā dvidhā iccādi. Tave+tuṃ paccayantā catutthiyāva, kātave dātave, kātuṃ kāretuṃ, (dātuṃ, rū) dāpetuṃ iccādi. So+topaccayantā pañcamiyatthe, dīghaso oraso rājato vā corato vā iccādi. To satta- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',105,replace(replace('<p class="bodytext">myatthepi <a name="M0.0105"></a> tra+tthādipaccayantā ca, ekato purato pacchato passato piṭṭhito pādato sīsato aggato mūlato, yatra yattha yahiṃ tatra tattha tahiṃ iccādi. Samantā sāmantā parito abhito samantato ekajjhaṃ ekamantaṃ heṭṭhā upari uddhaṃ adho tiriyaṃ sammukhā paramukhā āvi raho tiro uccaṃ nīcaṃ anto antarā antaraṃ ajjhattaṃ bahiddhā bāhirā bāhiraṃ bahi oraṃ pāraṃ ārā ārakā pacchā pure huraṃ pecca iccete sattamiyā. Sampati āyati ajja aparajjha suve sve uttarasuve (kisu, rū) hiyyo pare (parasuve, rū) sajju sāyaṃ pāto kālaṃ kallaṃ divā rattaṃ niccaṃ satataṃ abhiṇhaṃ abhikkhaṇaṃ muhuṃ muhuttaṃ bhūtapubbaṃ purā yadā tadā (kadā, rū) iccādayo kālasattamiyaṃ. Iti vibhattiyuttāni.</p>\r\n<p class="bodytext">Avibhattiyuttesu appeva appevanāma nu iccete saṃsayatthe. Addhā aññadatthu taggha jātu kāmaṃ sasakkaṃ iccete ekaṃsatthe. Evaṃ iti avadhāraṇe. Kacci nu nanu kathaṃ (kiṃsu, rū) kiṃ iccete pucchanatthe. Evaṃ iti itthaṃ iccete nidassanatthe. Iti hetu+vākyaparisamattīsu ca. Yāva tāva yāvatā tāvatā kittāvatā ettāvatā kīva iccete paricchedanatthe. Evaṃ sāhu lahu opāyikaṃ paṭirūpaṃ āma sādhu iccete sampaṭicchanatthe. Tathā yathā yatheva tatheva evaṃ evameva evampi yathāpi seyyathāpi seyyathāpināma viya iva yathariva tathariva yathānāma tathānāma yathāhi tathāhi yathāca tathāca iccete paṭibhāgatthe. Yathāiti yoggatā+vicchā+padatthānativatti+nidassanesu ca. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',106,replace(replace('<p class="bodytext">Evaṃiti <a name="M0.0106"></a> upadesa+pañhādīsu ca. Kiñcāpiiti anuggahatthe. Dhiiti garahe. Ahoiti garaha+pasaṃsana+patthanesu. Nāmaiti garaha+pasaṃsana+saññā+pañhesu. Sādhūti pasaṃsana+yācanesu. Iṅgha handa iccete codanatthe. Sādhu suṭṭhu evametaṃti anumodane. Kiraiti anussavana+asaddheyyesu. Nunaiti anumānā+nussaraṇa+parivitakkesu. Kasmāti kāraṇapucchane. Yasmā tasmā tathā hi tena iccete kāraṇacchedanatthe. Saha saddhiṃ amā (samaṃ, rū) iti samakriyāyaṃ. Vinā rite vippayoge. Nānā puthu bahupakāre. Puthu visuṃ asaṅghāte ca. Duṭṭhu ku kucchāyaṃ (jigucchāyaṃ, rū). Puna appaṭhame. Kathaṃ cikicchatthe. Dhā kkhattuṃ so kiñcaiti saṃkhyāvibhāge. Īsakaṃ appamānesu (appamatte, rū). Saṇikaṃ mandatthe. Khippaṃ araṃ lahuṃ āsu tuṇhaṃ aciraṃ sīghatthe. Ciraṃ cirassaṃ dīghakāle. Ce yadi saṃkā+vaṭṭhāne. Dhuraṃ thirā+vadhāraṇesu (dhuvaṃ thirā+vadhāraṇesu, rū). Hā visāde. Tuṇhī abhāsane. Sacchi paccakkhe. Musā micchāalikaṃ asacce. Suvatthi āsiṭṭhe iccādi.</p>\r\n<p class="bodytext">Tuna+ktvāna+ktvāpaccayantā ussukkanatthe bhavanti. Yathā passituna passiya passitvāna passitvā, disvā disvāna dassetvā dātuna datvāna datvā upādāya dāpetvā viññāya viceyya vineyya samecca nihacca upecca ārabbha āgamma iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',107,replace(replace('<p class="gatha1">Vibhattiyā <a name="M0.0107"></a> kato bhedo, saliṅgānaṃ bhave tathā;</p>\r\n<p class="gathalast">Tumhādīnaṃ tva+liṅgesu, neva+tthi pādi+cādinaṃ.</p>\r\n<p class="bodytext">Evaṃ nāmākhyātopasaggehi vinimuttaṃ etaṃ nipātapadaṃ veditabbaṃ.</p>\r\n<p class="centered">Nepātikapadaṃ.</p>\r\n<p class="gatha1">Pulliṅgaṃ itthiliṅgañca, napuṃsaka+mathā+paraṃ;</p>\r\n<p class="gathalast">Tiliṅgañca aliṅgañca, nāmikaṃ pañcadhā ṭhitaṃ.</p>\r\n<p class="centered">Iti payogasiddhiyaṃ nāmakaṇḍo dutiyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',108,replace(replace('<p class="chapter">3. Kārakakaṇḍa</p>\r\n<p class="bodytext">Atha <a name="M0.0108"></a> vibhattīna+matthabhedā vuccante.</p>\r\n<p class="bodytext">Tattha ekampi atthaṃ kammādivasena ekattādivasena ca vibhajantīti vibhattiyo, syādayo. Tā pana paṭhamādibhedena sattavidhā. Tā yathā ‘‘paṭhamā+tthamatte’’ti paṭhamāvibhatti hoti. Sissa okāra+lopa+a-mādesesu naro itthī napuṃsakaṃ.</p>\r\n<p class="gatha1">Sakattha+dabba+liṅgāni, saṃkhyā+kammādipañcakaṃ;</p>\r\n<p class="gathalast">Nāmattho tassa sāmañña-matthamatthaṃ pavuccate.</p>\r\n<p class="bodytext">So nāmattho ca –</p>\r\n<p class="gatha1">Jāti kriyā guṇo dabbaṃ, tathā nāmanti pañcadhā;</p>\r\n<p class="gathalast">Saddassa+ttho sa saddopi, pañcadhāva+tra bhijjate.</p>\r\n<p class="bodytext">Kathaṃ jātisaddo guṇasaddotyādinā.</p>\r\n<p class="gatha1">Bhinnesva+bhinnadhī+saddā, sabalādīsu yabbalā;</p>\r\n<p class="gathalast">Vattante jāti esā+ssa, mālā sutta+mivā+nvitā.</p>\r\n<p class="gatha1">Adabbabhūtaṃ kattādi-kārakaggāmasādhiyaṃ;</p>\r\n<p class="gathalast">Padatthaṃ kattukammaṭṭhaṃ, kriya+micchanti tabbidū.</p>\r\n<p class="gatha1">Dabbassito tato bhinno, nimitto tappatītiyā;</p>\r\n<p class="gathalast">Vinassanasabhāvo ca, nigguṇova guṇo+ccate.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',109,replace(replace('<p class="bodytext">Ettha <a name="M0.0109"></a> paṭo sukkoti vinassanasabhāvo ca casaddena vipulo ākāsoti avinassanasabhāvo ca attani visuṃ guṇarahito guṇo+ccate tyattho.</p>\r\n<p class="gatha1">Yaṃ yaṃ visessate kiñci, taṃ taṃ dabba+miho+ccate;</p>\r\n<p class="gathalast">Jātyādinopya+to tādi, dabbattaṃ parikappate.</p>\r\n<p class="bodytext">Ettha gojāti assajātīti vutte jāti dabbaṃ. Nīlo guṇoti ca guṇo dabbaṃ. Pacanakriyāti ca gamanādikriyāto visesiyatīti kriyāva dabbaṃ.</p>\r\n<p class="gatha1">Nāmarūpena sadabbe, kvaci saññī kathīyate;</p>\r\n<p class="gathalast">Nāmanti taṃ yathā citto, saññā saddo tu taddhanī.</p>\r\n<p class="bodytext">Nāmassa dabbattepi cittotyādi nāmeneva pasijjhati, no ghaṭa+paṭādayova dabbattena, tasmā nāma+miti saññī kathīyate, taṃvācakattā tabbatī cittādi saññāsaddoti nicchiyateyya+dhippāyo.</p>\r\n<p class="bodytext">Ayaṃ pañcavidhopi attho saddattho ceva sāmatthiyā gamyamānattho cāti dubbidho. Tathā hi ‘‘pīno divā na bhuñjeyya’’miti bhuttinirākati saddattho. Rattibhutti tu sāmatthiyā+vagamyate. Tene+taṃ vuccati –</p>\r\n<p class="gatha1">Atthappatītiyaṃ sadda-byāpāro tividho bhave;</p>\r\n<p class="gathalast">Mukhyo lakkhaṇa+byañjana-sabhāvo cāti ettha tu.</p>\r\n<p class="gatha1">Mukhyo tu nirantaratthe, lakkhaṇo tu tirohite;</p>\r\n<p class="gathalast">Atthe+taro tu vākyassa, attheyeva pavattati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',110,replace(replace('<p class="bodytext">‘‘Mañce’’ti <a name="M0.0110"></a> nirantaratthe vattamāno mukhyo, ‘‘mañcā ugghosantī’’ti tirohitatthe vattamāno lakkhaṇo, gāthādisakalavākyassa+tthe vattamāno byañjanasabhāvo.</p>\r\n<p class="gatha1">Byāpārassa pabhedena, tidhā saddopi vācako;</p>\r\n<p class="gathalast">Lakkhaṇiko byañjakoti, tadatthopi tidhā mato.</p>\r\n<p class="gatha1">Vacco lakkhaṇiko byaṅgyo, cevaṃ saddo suvācako;</p>\r\n<p class="gathalast">Vuttakamena jātyādi-bhedena pañcadhā bhaveti.</p>\r\n<p class="subhead">38. Āmantaṇe –</p>\r\n<p class="bodytext">Ti āmantaṇādhike atthamatte paṭhamāvibhatti hoti. Bho nara, bho itthi, bho napuṃsaka.</p>\r\n<p class="gatha1">Saddenā+bhimukhīkāro, vijjamānassa vatthuno;</p>\r\n<p class="gathalast">Āmantaṇaṃ vidhātabbe, natthi ‘‘rājā bhave’’ti+daṃ.</p>\r\n<p class="bodytext">Kriyā nimittaṃ kārakantu kamma, kattu, karaṇa, sampadāna, avadhi, ādhārabhedena chabbidhaṃ, taṃ yathā –</p>\r\n<p class="subhead">2,2. Kamme dutiyā</p>\r\n<p class="bodytext">Karīyati kattukiriyāya sambandhīyatīti kammaṃ, tasmiṃ kammakārake dutiyāvibhatti hoti. Taṃ tividhaṃ nibbatti, vikati, patti bhedena, tattha nibbattikamme mātā puttaṃ vijāyati, āhāro sukhaṃ janayati, kaṭaṃ karoti datto. Vikatiyaṃ kaṭṭha+maṅgāraṃ karoti, suvaṇṇaṃ kaṭakaṃ karoti, vīhayo lunāti. Pattiyaṃ datto gharaṃ pavisati, ādiccaṃ passati, dhammaṃ suṇāti, paṇḍite payirupāsati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',111,replace(replace('<p class="bodytext">Vuttañca <a name="M0.0111"></a> –</p>\r\n<p class="gatha1">Nibbatti+vikati+patti-bhedā kammaṃ tidhā mataṃ;</p>\r\n<p class="gathalast">Kattukriyābhigammantaṃ, sukha+maṅgāraṃ+gharaṃ yathāti.</p>\r\n<p class="bodytext">Kaṭaṃ karoti vipulaṃ dassanīyanti attheva guṇayuttassa kammatā, vipulaṃ karoti, dassanīyaṃ karotīti kriyāya sambandhiyamānattā. Odano paccateti odanasaddato kammatā nappatīyate, kiñcarahi ākhyātato.</p>\r\n<p class="bodytext">Icchitakammaṃ yathā-gāvuṃ payo dohati, gomantaṃ gāvaṃ yācati, gāva+mava rundhati vajaṃ, māṇavakaṃ maggaṃ pucchati, gomantaṃ gāvaṃ bhikkhate, rukkha+mavacināti phalāni, rukkhātyattho. Sissaṃ dhammaṃ brūte. Ettha payo, gāvaṃ iccādi icchitaṃ, gāvuṃ, gomanta+miccādi anicchitaṃ. Eva+manicchitepi kaṇṭakaṃ maddati, visaṃ gilati. Yaṃ nevi+cchitaṃ, nāpi anicchitaṃ, tatrāpi gāmaṃ gacchanto rukkhamūla+mupasappati.</p>\r\n<p class="bodytext">Ādhāre adhisi+ṭhā+sānaṃ payoge ca, pathaviṃ adhisessati, gāma+madhitiṭṭhati, rukkha+majjhāsateti. Ettha pathavinti pathaviyanti attho. Eva+mabhi, nipubbavisassāpi, dhamma+mabhinivisate, dhamme vā. Tathā upa, nva+jjhā+pubbavasatissa, gāma+mupavasati, gāma+manuvasati, vihāra+madhivasati, gāma+māvasati, agāraṃ ajjhavasati, ettha gāmanti gāmetyattho. Tathā tappānā+cārepi, nadiṃ pibati, gāmaṃ carati, nadiyaṃtyattho. Sace maṃ nālapissatīti mayā saddhiṃtyattho. Vihitāva paṭi-yogepi dutiyā, paṭibhantu taṃ cunda bojjhaṅgā, tampaṭi bojjhaṅgā bhāsantūti attho. Upamā maṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',112,replace(replace('<p class="bodytext">patibhāti<a name="M0.0112"></a>, upamā maṃ upaṭṭhātityattho. Dhātunāyutte ‘‘tassa nappaṭibhātī’’ti chaṭṭhī.</p>\r\n<p class="subhead">3. Kāladdhāna+maccantasaṃyoge</p>\r\n<p class="bodytext">Kāladdhānaṃ dabba+guṇa+kriyāhi accantasaṃyoge tehi kāladdhānavācīhi dutiyā hoti. Kāle-sattāhaṃ gavapānaṃ, māsaṃ maṃsodanaṃ, saradaṃ ramaṇīyā nadī, sabbakālaṃ ramaṇīyaṃ nandanaṃ, tayo māse abhidhammaṃ deseti. Addhani-yojanaṃ vanarāji, yojanaṃ dīgho pabbato, kosaṃ sajjhāyati.</p>\r\n<p class="bodytext">Pubbanhasamayaṃ nivāsetvā, ekaṃ samayaṃ bhagavā, imaṃ rattiṃ cattāro mahārājānoti evamādīsu kālavācīhi accantasaṃyoge dutiyā, bahulaṃvidhānā vibhattivipallāso vā. Māsenā+nuvāko+dhīto, kosenā+nuvāko+dhītoti karaṇatthe tatiyā.</p>\r\n<p class="subhead">4. Gatibodhāhārasaddatthākammakabhajjādīnaṃ payojje</p>\r\n<p class="bodytext">Gatyatthānaṃ bodhatthānaṃ āhāratthānaṃ saddatthāna+makammakānaṃ bhajjādīnañca payojje kattari dutiyā hoti. Datto gamayati māṇavakaṃ gāmaṃ, yāpayati vā. Guru bodhayati māṇavakaṃ dhammaṃ, vedayati vā. Mātā bhojayati putta+modanaṃ, āsayati vā. Guru ajjhāpayati sissaṃ vedaṃ, pāṭhayati vā. Payojjato+ññatra kamme dutiyā. Poso āsayati dattaṃ, sāyayati vā. Poso aññaṃ bhajjāpeti, aññaṃ koṭṭāpeti, aññaṃ santharāpeti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',113,replace(replace('<p class="subhead">5. Harādīnaṃ vā</p>\r\n<p class="bodytext">Harādīnaṃ <a name="M0.0113"></a> payojje kattari dutiyā hoti vā. Poso hāreti bhāraṃ dattaṃ, datteneti vā. Dassayate janaṃ rājā, janeneti vā. Abhivādayate guruṃ dattaṃ, datteneti vā. Ajjhohāreti sattuṃ dattaṃ, datteneti vā. Kāreti datto dattaṃ, datteneti vā. Pakkhe sabbatra kattari tatiyā.</p>\r\n<p class="subhead">6. Na khādādīnaṃ</p>\r\n<p class="bodytext">Khādādīnaṃ payojje kattari dutiyā na hoti. Gatyatthādīsu kvaci paṭisedhattha+midaṃ. Khādayati datto dattena, ādayati dattena, avhāpayati datthena, saddāyayati dattena, nāyayati dattena, kandayati dattena.</p>\r\n<p class="subhead">7. Jhādīhi yuttā</p>\r\n<p class="bodytext">Dhiādīhi yuttato dutiyā hoti. Dhi+ratthu+maṃ putikāyaṃ, antarā ca rājagahaṃ antarā ca nālandaṃ, rājagahassa ca nālandassa ca vivarabhūte majjheti attho. Samādhāna+mantarena, mucalinda+mabhito saraṃ. Chaṭṭhyatthe+yaṃ dutiyā.</p>\r\n<p class="subhead">8. Lakkhaṇitthambhūtavicchāsva+bhinā</p>\r\n<p class="bodytext">Lakkhaṇādīsva+tthesu abhinā yuttamhā dutiyā hoti. Rukkha+mabhivijjotate cando, ettha rukkho lakkhaṇaṃ, cando lakkhitabbo, tatra abhinā rukkhassa lakkhaṇavuttitā pakāsīyatīti rukkho abhinā yutto nāma. Sādhu devadatto mātara+mabhi, mātari sādhuttaṃ pattotyattho. Rukkhaṃ rukkhaṃabhi vijjotate cando, rukkhe rukkhetyattho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',114,replace(replace('<p class="subhead">9. Patiparīhi bhāge ca</p>\r\n<p class="bodytext">Patiparīhi <a name="M0.0114"></a> yuttato lakkhaṇādīsu bhāge ca+tthe dutiyā hoti. Rukkhaṃpati vijjotate, sādhu devadatto mātaraṃpati, rukkhaṃ rukkhaṃpati tiṭṭhati, ya+dettha maṃ pati siyā, yo mama bhāgo, so dīyatutyattho. Evaṃ rukkhaṃparityādipi.</p>\r\n<p class="subhead">10. Anunāti</p>\r\n<p class="bodytext">Lakkhaṇādīsu dutiyā. Rukkhamanu vijjotate, saccakiriya+manu vassi, hetu ca lakkhaṇaṃ bhavati, sādhu devadatto mātara+manu, rukkhaṃ rukkha+manu vijjotate, yadettha maṃanu siyā.</p>\r\n<p class="subhead">11. Sahatthe</p>\r\n<p class="bodytext">Sahatthe anunā yuttamhā dutiyā. Pabbata+manu tiṭṭhati, pabbatena saha tyattho.</p>\r\n<p class="subhead">12. Hīne</p>\r\n<p class="bodytext">Hīnatthe anunā yuttamhā dutiyā. Anusāriputtaṃ paññavanto bhikkhū, sāriputtato paññāya hīnātyattho.</p>\r\n<p class="subhead">13. Upena</p>\r\n<p class="bodytext">Hīnatthe upena yuttamhā dutiyā. Upasāriputtaṃ paññavanto.</p>\r\n<p class="subhead">16. Kattukaraṇesu tatiyā</p>\r\n<p class="bodytext">Kattari karaṇe ca kārake tatiyā hoti. Jinena desito dhammo, buddhena jito māro, ahinā daṭṭho naro. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',115,replace(replace('<p class="bodytext">Yattha <a name="M0.0115"></a> kāraṇakāraṇampi kāraṇavasena vuccati, tatthāpi tatiyā, corehi gāmo daḍḍho, tiṇehi bhattaṃ siddhaṃ, saddhehi kāritā vihārā.</p>\r\n<p class="gatha1">Attapadhāno kiriyaṃ, yo nibbatteti kārako;</p>\r\n<p class="gathalast">Apayutto payutto vā, sa kattāti pavuccati.</p>\r\n<p class="bodytext">Karaṇe-taṃ pana duvidhaṃ ajjhattikabāhiravasena, yathā hatthena kammaṃ karoti, cakkhunā rūpaṃ passati, manasā dhammaṃ viññāyati. Dattena vihayo lunāti, agginā kuṭiṃ jhāpeti.</p>\r\n<p class="gatha1">Bāhirañca tathā+jjhattaṃ, karaṇaṃ duvidhaṃ yathā;</p>\r\n<p class="gathalast">Vīhiṃ lunāti dattena, nettena canda+mikkhate.</p>\r\n<p class="bodytext">Pakatiyā abhirūpo, gottena gotamo iccādi bhūdhātussa sambhavā karaṇe tatiyā. Tathā sāriputtoti nāmena vissuto, jātiyā khattiyo buddho, jātiyā sattavassiko, sippena naḷakāro so, ekūnatiṃso vayasā, evaṃ samena dhāvati, visamena dhāvati, dvidoṇena dhaññaṃ kiṇāti.</p>\r\n<p class="subhead">17. Sahatthena</p>\r\n<p class="bodytext">Sahatthena yoge tatiyā siyā. Tatiyāpi chaṭṭhīva appadhāne eva bhavati. Puttena sahā+gato, puttena saddhiṃ āgato, vitakkena saha vattati, puttena saha thūlo, nisīdi bhagavā saddhiṃ bhikkhusaṅghena, sahassena samaṃ mitā, sabbehi me piyehi manāpehi nānābhāvo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',116,replace(replace('<p class="subhead">18. Lakkhaṇe</p>\r\n<p class="bodytext">Lakkhaṇe <a name="M0.0116"></a> vattamānato tatiyā. Bhinnena sīsena paggharantena lohitena paṭīvissake ujjhāpesi, ūnapañcabandhanena pattena aññaṃ navaṃ pattaṃ cetāpeyya, tidaṇḍakena paribbājaka+maddakkhi, akkhinā kāṇo, hatthena kuṇī, pādena khañjo, piṭṭhiyā khujjo. Tena hi vikalaṅgena vikalaṅgino vikāro lakkhiyate.</p>\r\n<p class="subhead">19. Hetumhi</p>\r\n<p class="bodytext">Vāsādilakkhaṇakriyāya hetuto tatiyā. Annena vasati, vijjāya vasati, na jaccā vasalo hoti, kammunā vasalo hoti, dānena bhogavā, ācārena kulaṃ, tena pāṇi kāmadado.</p>\r\n<p class="subhead">20. Pañcamī+ṇe vā</p>\r\n<p class="bodytext">Iṇe hetumhi pañcamī vā. Satasmā baddho, satena vā.</p>\r\n<p class="subhead">21. Guṇe</p>\r\n<p class="bodytext">Paraṅgabhūte hetumhi pañcamī hoti vā. Jaḷattā baddho, jaḷattenavā, paññāya mutto, hutvā abhāvato aniccā, saṅkhāranirodhā viññāṇanirodho. Bahulaṃvidhānā sattamyatthehipi, tena samayena, kālena dhammasākacchā, puratthimena dhataraṭṭho, dakkhiṇena virūḷhako, uttarena kapilavatthu, yena bhagavā, tenupasaṅkami, so vo mama+ccayena satthāti. Māsena bhuñjati, ekāheneva bārāṇasiṃ pāvisi, navahi māsehi vihāraṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',117,replace(replace('<p class="bodytext">kārāpesi<a name="M0.0117"></a>, kahāpaṇena ūno, dhanena vikalo, asinā kalaho, ācārena nipuṇo, guḷena missakaṃ, vācāya sakhilo, maṇinā attho, dhanena attho, yojanena gacchati iccādi hetumhi karaṇe vā tatiyā. Attanāva attānaṃ sammannatīti paccatte bahulaṃvidhānā kattari tatiyā. Evaṃ tilehi khette vapatīti kammatthe, sumuttā mayaṃ tena mahāsamaṇenāti pañcamyatthe ca.</p>\r\n<p class="subhead">24. Catutthī sampadāne</p>\r\n<p class="bodytext">Acetanaṃ sacetanaṃ vā paṭiggāhakabhāvenā+pekkhitaṃ, taṃ sampadānaṃ, tañca dā-dāneti dhātuto byappena yuttameva, tattha catutthī hoti. Tañca dīyamānassa vatthuno anivāraṇa+jjhesanā+numatito tividho, yathā buddhassa pupphaṃ deti, bodhirukkhassa jalaṃ deti, yācakassa dhanaṃ deti, bhikkhūnaṃ dānaṃ deti.</p>\r\n<p class="gatha1">Anirākaraṇā+rādha-na+bbhanuññavasena hi;</p>\r\n<p class="gathalast">Sampadānaṃ tidhā vuttaṃ, rukkha+yācaka+bhikkhavo.</p>\r\n<p class="bodytext">Ādhāravivakkhāyaṃ sattamīpi, saṅghe gotami dehi, saṅghe dinne ahañceva pūjito bhavissāmi, yā palālamayaṃ mālaṃ, nārī datvāna cetiyeti.</p>\r\n<p class="subhead">25. Tādatthye</p>\r\n<p class="bodytext">Tādatthye catutthī siyā. Tadatthassa bhāve, tādatthyaṃ, tasmiṃ tadatthabhāve jotanīyeva catutthī siyā. Tadatthassa bhāvoti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',118,replace(replace('<p class="bodytext">nimittanimittīsambandhe <a name="M0.0118"></a> chaṭṭhī, tasmā chaṭṭhāpavādo+yaṃ. Samepi nimittanimittīnaṃ sambandhe nimittabhūtayūpatova catutthī, no nimittībhūtadāruto. Yūpāya dāru, pākāya vajati, buddhassatthāya jīvitaṃ pariccajāmi, neva davāya, na madāya, ūnassa pāripūriyā, atthāya hitāya sukhāya saṃvattati, lokānukampāya, phāsu vihārāya.</p>\r\n<p class="bodytext">Iccāyaṃ kaccāyane upari vakkhamānassa catutthīti sādhitattā idha chaṭṭhīti dīpanatthaṃ vuccate –</p>\r\n<p class="bodytext">Kassa sādu naruccati, mā ayasmantānampi saṅghabhedo ruccittha, khamati saṅghassa, bhatta+massa nacchādetīti chaṭṭhī sambandhavacanicchāyaṃ, na ce+vaṃ virodho siyā, sadisarūpattā, evaṃvidhesu ca sambandhassa saddikānumatattā. Tathā hi bhāgavuttiyā ‘‘upapadavibhatti chaṭṭhiyā+pavādā’’ti vuttaṃ, saddantare vihitā vibhatti upapadavibhatti.</p>\r\n<p class="bodytext">Kassa vā tvaṃ dhammaṃ rocesīti atthamatte paṭhamā, kassa vā tuyhanti avatvā kassa vā tvaṃ dhammaṃ rocesīti atthamatte paṭhamāvasena byabhicāradassanā. Eva+maññāpi viññeyyā, paratopi yathāgamaṃ.</p>\r\n<p class="bodytext">Rañño sataṃ dhāreti rañño chattaṃ dhāretīti sambandhe chaṭṭhīva. Silāgha=kathane, evaṃ rañño silāghate iccādi, thutiṃ karotītyattho. Hanu=apanayane, rañño hanute, vañcetītyattho. Upatiṭṭheyya sakyaputtānaṃ vaḍḍhakī, upagaccheyyātyattho. Sapa=akkose, mayhaṃ sapate, saccaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',119,replace(replace('<p class="bodytext">kurutetyattho<a name="M0.0119"></a>. Dhara=dhāraṇe, suvaṇṇaṃ te dhārayate, iṇaṃ te dhārayati, assa rañño nāgaṃ dhārayāma. Piha=icchāyaṃ, devāpi tassa pihayanti tādino, tesaṃ pihayanti sambuddhānaṃ satīmataṃ, pihayanti=patthenti. Tassa kujjha mahāvīra, yadi+haṃ tassa pakuppeyyaṃ, dubhayati disānaṃ megho, yo mittānaṃ na dūbhati, yo appaduṭṭhassa narassa dussati. Issa=issāyaṃ, titthiyā issayanti samaṇānaṃ. Usūya=dosāvikaraṇe, dujjanā guṇavantānaṃ ussūyanti, kā usuyā vijānataṃ. Idha+sidha+rādha+sādha=saṃsiddhiyaṃ, ārādho me rañño, rañño bhāgya+mārajjhati, kyā+haṃ ayyānaṃ aparajjhāmi, āyasmato upālittherassa upasampadāpekkho upatisso. Bhikkhū bhagavato paccassosuṃ, āsuṇanti buddhassa bhikkhū, tassa bhikkhuno janoanugiṇāti, patigiṇāti, sādhukāradānādinā taṃ ussahatītyattho. Ārocayāmi vo bhikkhave, paṭivedayāmi vo, āmantayāmi te mahārāja, dhammaṃ te desissāmi, desetu bhante bhagavā dhammaṃ bhikkhūnaṃ, yathā no bhagavā byākareyya, niruttiṃ te pavakkhāmi, alaṃ me rajjaṃ, alaṃ bhikkhu pattassa, alaṃ mallo mallassa, arahati mallo mallassa, alaṃ te idha vāsena, kiṃ me ekena tiṇṇenāti sabbattha sambandhe chaṭṭhī.</p>\r\n<p class="bodytext">Evaṃ āyu bhoto hotu, ciraṃ jīvitaṃ, bhaddaṃ, kalyāṇaṃ, atthaṃ, payojanaṃ, kusalaṃ, anāmayaṃ, hitaṃ, patthaṃ, sukhaṃ, sātaṃ, bhoto hotu, sādhu sammuti me tassa, puttassā+vikareyya guyha+matthaṃ, tassa me sakko pāturahosi, tassa pahiṇeyya, bhikkhūnaṃ dūtaṃ pāhesi, kappati samaṇānaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',120,replace(replace('<p class="bodytext">āyogo<a name="M0.0120"></a>, ekassa dvinnaṃ tiṇṇaṃ vā pahoti, upamaṃ te karissāmi, añjaliṃte paggaṇhāmi, tassa phāsu, lokassa+ttho, namo te purisājañña, sotthi tassa, samattho mallo mallassa, tassa hitaṃ, tassa sukhaṃ, svāgataṃ te mahārājāti sabbattha sambandhe chaṭṭhī.</p>\r\n<p class="subhead">26. Pañcamya+vadhismā</p>\r\n<p class="bodytext">Padatthāvadhismā pañcamīvibhatti hoti.</p>\r\n<p class="gatha1">Samepya+pagame dvinnaṃ, pubbarūpā ya+daccutaṃ;</p>\r\n<p class="gatha2">Vuccate ta+dapādānaṃ, taṃ calācalato dvidhā;</p>\r\n<p class="gathalast">Yathā+ssā dhāvatā poso, pato, rukkhāphalanti ca.</p>\r\n<p class="bodytext">Tattha calāvadhi dhāvatā assā puriso patati, acalāvadhi pabbatā otaranti vanacārakāti.</p>\r\n<p class="bodytext">Tañca avadhi visayakriyāvisesassa niddiṭṭhattā niddiṭṭhavisayaṃ, yattha apa apagamanakriyaṃ upāttaṃ=ajjhāhaṭaṃ visayaṃ katvā pavattati, taṃ upāttaṃ. Yaṃ kenaci guṇena ukkaṃsiyati, taṃ anumeyyaṃ. Yathā gāmā apenti munayo, nagarā niggato rājā, pāpā cittaṃ nivārenti. Valāhakā vijjotate, kusulato pacatīti. Ettha ca vaḷāhakā nikkhamma, kusulato apanetvāti ca pubbakriyā ajjhāharīyati. Mathurā pāṭaliputtakehi abhirūpāti anumīyati. Vuttañhi –</p>\r\n<p class="gatha1">Niddiṭṭhavisayaṃ kiñci, upāttavisayaṃ tathā;</p>\r\n<p class="gathalast">Anumeyyavisayañceti, tidhā+hu avadhiṃ budhāti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',121,replace(replace('<p class="bodytext">Bhayahetumhi-corā <a name="M0.0121"></a> bhayaṃ jāyati, taṇhāya jāyati bhayaṃ, pāpato uttasati, natthi soko kuto bhayaṃ. Akkhātari-upajjhāyā sikkhaṃ gaṇhāti, ācariyamhā adhīto suṇāti vā. Buddhasmā parājenti aññatitthiyā, parājitā bhavantītyattho. Himavatā pabhavati gaṅgā, aciravatiyā pabhavanti kunnadiyo. Urasmā jāto putto, kammato jātaṃ indriyaṃ, upajjhāyā antaradhāyati sisso, mātāpitūhi antaradhārayati putto, nilīyatītyattho. Dūratthayogekīvadūro ito naḷakāragāmo, tato have dūrataraṃ vadanti, gāmato nātidūre, ārakā te moghapurisā imasmā dhammavinayā, ārakā tehi bhagavā dūratova namassanti, addasa dūratova āgacchantaṃ. Antikatthayoge-antikaṃ gāmā, āsannaṃ gāmā, samīpaṃ gāmā. Parimāṇe-ito mathurāya catūsu yojanesu saṃkassaṃ, rājagahato pañcacattālīsayojanamatthake sāvatthi. Kālaparimāṇe-ito ekanavutikappamatthake, ito vassasatasahassassa accayena buddho loke uppajjissati. Pāsādā saṃkameyya, pāsādaṃ abhiruhitvā saṃkameyyāti attho, tathā hatthikkhandhā saṃkameyya, abhidhammā pucchanti, abhidhammaṃ sutvāti attho, āsanā vuṭṭhaheyya. Disatthavācīhi yoge-ito sā purimā disā, ito sā dakkhiṇā disā, avīcito yāva bhavaggaṃ, uddhaṃ pādatalā, adho kesamatthakā. Vibhajane-yato paṇītataro vā visiṭṭhataro vā natthi, attadanto tato varaṃ, kiñcāpi dānato sīlaṃ varaṃ, tato mayā bahutaraṃ sutaṃ, sīlameva sutā seyyo. Āratippayoge-ārati virati pāpā, pāṇātipātā <a name="M0.0122"></a> veramaṇi, adinnādānā paṭivirato, appaṭiviratā musāvādā. Suddhatthayoge-lobhanīyehi dhammehi suddho, mātuto ca pituto ca suddho anupakkuṭṭho. Pamocanatthayoge-parimutto dukkhasmāti vadāmi, mutto+smi mārabandhanā, na te muccanti maccunā, mutto+haṃ+sabbapāsehi. Vivecane-vivicceva kāmehi, vivicca akusalehi dhammehi. Pamāṇatthe-āyāmato ca vitthārato ca yojanaṃ, gambhīrato ca puthulato ca yojanaṃ candabhāgāya parimāṇaṃ, parikkhepato navayojanasataparimāṇo majjhimadeso. Pubbādiyoge-pubbeva me bhikkhave sambodhā, ito pubbe nāhosi, tato paraṃ paccantimā janapadā, tato aparena samayena. Pañhe-kuto+si tvaṃ, kuto bhavaṃ, pāṭaliputtato. Kicchā laddhanti guṇe pañcamī, kicchena me adhigatanti hetumhi karaṇe vā tatiyā, evaṃ thokā mutto, thokena muttoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',122,replace(replace('<p class="bodytext">pāṇātipātā <a name="M0.0122"></a> veramaṇi, adinnādānā paṭivirato, appaṭiviratā musāvādā. Suddhatthayoge-lobhanīyehi dhammehi suddho, mātuto ca pituto ca suddho anupakkuṭṭho. Pamocanatthayoge-parimutto dukkhasmāti vadāmi, mutto+smi mārabandhanā, na te muccanti maccunā, mutto+haṃ+sabbapāsehi. Vivecane-vivicceva kāmehi, vivicca akusalehi dhammehi. Pamāṇatthe-āyāmato ca vitthārato ca yojanaṃ, gambhīrato ca puthulato ca yojanaṃ candabhāgāya parimāṇaṃ, parikkhepato navayojanasataparimāṇo majjhimadeso. Pubbādiyoge-pubbeva me bhikkhave sambodhā, ito pubbe nāhosi, tato paraṃ paccantimā janapadā, tato aparena samayena. Pañhe-kuto+si tvaṃ, kuto bhavaṃ, pāṭaliputtato. Kicchā laddhanti guṇe pañcamī, kicchena me adhigatanti hetumhi karaṇe vā tatiyā, evaṃ thokā mutto, thokena muttoti.</p>\r\n<p class="bodytext">Kathaṃ ‘‘thokaṃ calatī’’ti, kriyāvisesane kammani dutiyā, thokaṃ calanaṃ karotītyattho, thokanti calanakriyāya visesanattā kriyāvisesanaṃ.</p>\r\n<p class="gatha1">Kriyāvisesanaṃ nāma, kammatte+kattasaṇṭhitā;</p>\r\n<p class="gathalast">Nyāyasiddhaṃ yato tasmā, tadatthaṃ na visuṃ vidhi.</p>\r\n<p class="bodytext">Nyāyasiddhaṃva=calananti yasmā bhāve ano, tasmā bhāvasse+kattā ekavacananti ñāyā ekattañca, bhāve anattā napuṃsakattañca, karotikriyāya sambandhena kammattañca sijjhatīti. Kara+bhūdhātavo ca –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',123,replace(replace('<p class="gatha1">Kāriyarūpābhidhātvatthā<a name="M0.0123"></a>, sabbe sattāya yujjare;</p>\r\n<p class="gathalast">Tato kriyā ca bhāvo ca, sāmaññaṃ tesu gamyate –</p>\r\n<p class="unindented">Ti vuttattā yujjanti.</p>\r\n<p class="bodytext">Thokatthe-thokā muccati. Sabbattha savisaye pañcamī. Ettha ‘‘vivakkhā lokikā sā ca, na sakkā anivattituṃ’’ti vuttattā aniṭṭhappasaṅgo na siyā. Mariyādāyaṃ-āpabbatā khettaṃ. Abhividhimhi-ābrahmalokā saddo abbhuggacchati, pabbataṃ vajjetvā, brahmalokamhi byāpetvāti ca attho. Ettha vajjamānasīmā mariyādā, gayhamānasīmā abhividhi.</p>\r\n<p class="subhead">27. Apaparīhi vajjane</p>\r\n<p class="bodytext">Vajjane vattamānehī apaparīhi yoge pañcamī hoti. Apasālāya āyanti vāṇijā, parisālāya āyanti vāṇijā, sālaṃ vajjetvāti attho.</p>\r\n<p class="subhead">28. Paṭinidhipatidānesu patinā</p>\r\n<p class="bodytext">Paṭinidhimhi patidāne ca vattamānena patinā yoge nāmasmā pañcamī hoti. Buddhasmā pati sāriputto, ghata+massa telasmā pati dadāti.</p>\r\n<p class="subhead">29. Rite dutiyā ca</p>\r\n<p class="bodytext">Ritesaddayoge nāmasmā dutiyā hoti pañcamī ca. Rite saddhammā, rite saddhammaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',124,replace(replace('<p class="subhead">30. Vinā+ññatra tatiyā ca</p>\r\n<p class="bodytext">Vinā+ññatrayoge <a name="M0.0124"></a> nāmasmā tatiyā dutiyā pañcamī ca. Vinā vātena, vinā vātaṃ, vinā vātasmā. Aññatra ekena piṇḍapātanīhārakena, aññatra dhammaṃ, aññatra dhammā.</p>\r\n<p class="subhead">31. Puthanānāhi</p>\r\n<p class="bodytext">Etehi yoge tatiyā hoti pañcamī ca. Bhinnayogakaraṇaṃ dutiyānivattanatthaṃ. Puthageva janena, puthageva janasmā, janena nānā, janasmā nānā.</p>\r\n<p class="subhead">39. Chaṭṭhī sambandhe</p>\r\n<p class="bodytext">Sambandhe chaṭṭhī hoti. Rañño purisoti vutte yasmā rājā dadāti, puriso gaṇhāti, tasmā rājapurisoti viññāyati. Evameva yo yassa āyatto sevakādibhāvena bhaṇḍabhāvena vā samīpa+samūhā+vayava+vikāra+kāriya+avatthā+jāti+guṇa+kriyādivasena vā, ṭhānīvasena vā, āgamīvasena vā, so tividhopi attho sambandho nāma. Vuttañhi –</p>\r\n<p class="gatha1">Kriyākārakasañjāto, asse+daṃbhāvahetuko;</p>\r\n<p class="gathalast">Sambandho nāma so attho, tattha chaṭṭhī vidhīyate.</p>\r\n<p class="gatha1">Pāratantyañhi sambandho, tattha chaṭṭhī bhave tito;</p>\r\n<p class="gathalast">Upādhi+ṭhānyā+gamito, na visessādito titoti.</p>\r\n<p class="bodytext">Upādhisaṅkhātavisesanato tāva-rañño puriso. Ettha ca brāhmaṇādisāmito nivattetīti rājā visesanaṃ, puriso </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',125,replace(replace('<p class="bodytext">tena <a name="M0.0125"></a> visesiyatīti visesso. Bhaṇḍasambandhato-pahutaṃ me dhanaṃ sakka, ekassa paṭivīso, bhikkhussa pattacīvaraṃ. Samīpato-ambavanassa avidūre, nibbānasseva santike. Samūhe-suvaṇṇassa rāsi, bhikkhūnaṃ samūho. Avayave-manussasseva te sīsaṃ, rukkhassa sākhā. Vikāre-suvaṇṇassa vikati, bhaṭṭhadhaññānaṃ sattu. Kāriye-yavassa aṅkuro, meghassa saddo, puttāpi tassa bahavo, kammānaṃ phalaṃ vipāko. Avatthāyaṃ-khandhānaṃ pātubhāvo, jarā, bhedo vā. Jātiyaṃ-manussassa bhāvo, manussānaṃ jāti. Guṇe-suvaṇṇassa vaṇṇo, vaṇṇo na khīyetha tathāgatassa buddhassa guṇaghoso, pupphānaṃ gandho, phalānaṃ raso, cittassa phusanā, sippikānaṃ sataṃ natthi, tilānaṃ muṭṭhi, tesaṃ samāyogo, sandhino vimokkho, tathāgatassa paññāpāramiṃ ārabbha, sukhaṃ te, dukkhaṃ te, cetaso parivitakko udapādi, paññāya paṭubhāvo, rūpassa lahutā, mudutā vā, upacayo vā. Kriyāsambandhe-pādassa ukkhepanaṃ, avakkhepanaṃ vā, hatthassa samiñjanaṃ, dānaṃ, pasāraṇaṃ, dhātūnaṃ gamanaṃ, ṭhānaṃ, nisajjā, sayanaṃ vā, tathāgatassa nāmagottādi, tassa kāraṇaṃ, tassa mātāpitaro, tassa purato pāturahosi, nagarassa dakkhiṇato, vassānaṃ tatiye māse, na tassa upamā, kuverassa bali iccādi. Api ca –</p>\r\n<p class="gatha1">Gāvassa jāti, dhavalo, gati, siṅgaṃ, nāmanti+dha;</p>\r\n<p class="gathalast">Dabbassāpi ca jātyādi, visessā honti kāmato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',126,replace(replace('<p class="gatha1">Gottañca <a name="M0.0126"></a> sabalodissa, pāka+mannassa sukkatā;</p>\r\n<p class="gathalast">Paṭassa, siṅgaṃmeṇḍassa, nā+ññesaṃti visessate.</p>\r\n<p class="bodytext">Ṭhānito-yuvaṇṇāna+meo luttā. Āgamito-suña sassa. Sāmiyoge-devāna+mindo, migānaṃ rājā. Rujādiyoge-devadattassa rujati, tassa rogo uppajji, mahāsenāpatīnaṃ ujjhāpetabbaṃ, rajakassa vatthaṃ dadāti, musāvādassa ottappaṃ iccādi. Yajassa yoge-pupphassa buddhassa yajati, pupphenātyattho. Evaṃ ghatassa aggiṃ juhati. Suhitatthe-pattaṃ odanassa pūretvā, pūraṃ nānāpakārassa asucino, pūraṃ hiraññasuvaṇṇassa, pūrati bālo pāpassa. Kitakappayoge-bahulaṃvidhānā chaṭṭhī, rañño sammato, pūjito, sakkato, apacito, mānito vā, amataṃ tesaṃ bhikkhave aparibhuttaṃ, yesaṃ kāyagatā sati aparibhuttā, sādhusammato bahujanassa, suppaṭividdhā buddhānaṃ dhammadhātu, dhammassa gutto medhāvī. Kammatthe-tassa bhavanti vattāro, sahasā kammassa kattāro, amatassa dātā, bhinnānaṃ sandhātā, sahitānaṃ anuppadātā, bodhetā pajāya, kammassa kārako natthi, vipākassa ca vedako, avisaṃvādako lokassa, catunnaṃ mahābhūtānaṃ upādāya pasādo. Sarati+cchāyoge-mātu sarati, na tesaṃ koci sarati, sattānaṃ kammapaccayā, puttassa icchati. Karotissa (yoge) – udakassa patikurute, kaṇḍassa patikurute, abhisaṅkharotītyattho. Pañcamiyatthe parihānibhayatthayogepi chaṭṭhī, assavanatāya dhammassa pariyāyanti, kinnu kho ahaṃ tassa sukhassa bhāyāmi, sabbe tasanti daṇḍassa, sabbe </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',127,replace(replace('<p class="bodytext">bhāyanti <a name="M0.0127"></a> maccuno, bhīto catunnaṃ āsīvisānaṃ ghoravisānaṃ iccādi.</p>\r\n<p class="subhead">40. Tulyatthena vā tatiyā</p>\r\n<p class="bodytext">Tulyatthena yoge vā chaṭṭhī hoti tatiyā ca. Pitu tulyo, pitarā vā, mātu tulyo, mātarā vā.</p>\r\n<p class="subhead">32. Sattamyā+dhāre</p>\r\n<p class="bodytext">Kattukammaṭṭhānaṃ nisajja+pacanādikriyānaṃ yo ādhāro, taṃ ādhārakārakaṃ nāma. Kathaṃ kaṭe nisīdati devadatto, thāliyaṃ odanaṃ pacati, devadatta+taṇḍulānaṃ kattu+kammānaṃ dhāraṇato tadaṭṭhaāsana+pacanakriyaṃ kaṭa+thāliyo dhārenti nāma, tasmiṃ sattamīvibhatti hoti.</p>\r\n<p class="bodytext">So byāpiko, opasilesiko, vesayiko, sāmīpikoti catubbidho. Tattha yo ādheyyassa nissesādhārabhūto, so byāpiko, yathā tilesu telaṃ atthi, khīresu jalaṃ, dadhimhi sappi. Paccekasiddhānaṃ bhāvānaṃ yaṃ ādheyyabhāvena upasilesanaṃ allīyanaṃ atthi, so opasilesiko. Yathā āsane nisinno saṅgho, thāliyaṃ odanaṃ pacati, ghaṭe udaka+matthi, dūre ṭhito, samīpe ṭhito. Yattha samīpe samīpīvohāraṃ katvā tadāyattatādīpanatthaṃ ādhārabhāvo upacarīyati, taṃ sāmīpikaṃ, yathā gaṅgāyaṃ ghoso, gaṅgāya samīpe vajotyattho. Sāvatthiyaṃ viharati jetavane. Yattha aññathābhāvavasena desantarāvacchedavasena vā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',128,replace(replace('<p class="bodytext">ādhāraparikappo<a name="M0.0128"></a>, so vesayito. Yathā ākāse sakuṇā caranti, bhūmiyaṃ manussā, jale macchā, pāsādesu patito, pāpasmiṃ ramatī mano, pasanno buddhasāsane, paññāya sādhu, vinaye nipuṇo, mātari sādhu, pitari nipuṇo.</p>\r\n<p class="gatha1">Kiriyā kattu+kammaṭṭhā, ādhārīyati yena so;</p>\r\n<p class="gathalast">Ādhāro catudhā vutto, byāpakādippabhedato.</p>\r\n<p class="gatha1">Byāpako tilakhīrādi, kaṭo opasilesiko;</p>\r\n<p class="gathalast">Sāmīpiko tu gaṅgādi, ākāso visayo mato.</p>\r\n<p class="subhead">33. Nimitte</p>\r\n<p class="bodytext">Nimittatthe sattamī hoti. Ajinamhi haññate dīpi, kuñjaro dantesu haññate, aṇumattesu vajjesu bhayadassāvī, sampajānamusāvāde pācittiyaṃ. Sabbattha nimityattho.</p>\r\n<p class="subhead">34. Yabbhāvo bhāvalakkhaṇaṃ</p>\r\n<p class="bodytext">Yassa bhāvo bhāvantarassa lakkhaṇaṃ bhavati, tato sattamī. Kāle gāvīsu duyhamānāsugato, duddhāsu āgato. Etthagamanakriyāya dohanakriyācihanaṃ, āgamanakriyāya duddhākriyācihanaṃ, eva+muparipi. Pubbaṇhasamaye gato, sāyanhasamaye āgato, jāyamāne kho sāriputta bodhisatte ayaṃ dasasahassī lokadhātu sampakampi.</p>\r\n<p class="gatha1">Pāsāṇā sakkharā ceva, kaṭhalā khāṇukaṇṭakā;</p>\r\n<p class="gathalast">Sabbe maggā vivajjanti, gacchante lokanāyake.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',129,replace(replace('<p class="unindented">Imasmiṃ <a name="M0.0129"></a> sati idaṃ hoti iccādi. ‘‘Akāle vassatī tassa, kāle tassa na vassatī’’ti visayasattamī.</p>\r\n<p class="subhead">35. Chaṭṭhī cā+nādare</p>\r\n<p class="bodytext">Yassa bhāvo bhāvantarassa lakkhaṇaṃ bhavati, tato chaṭṭhī hoti sattamī cā+nādare gamyamāne. Akāmakānaṃ mātāpitūnaṃ rudantānaṃ pabbaji, mātāpitūsu rudantesu vā. ‘‘Ākoṭayanto so neti, sivirājassa pekkhate’’, ‘‘maccu ādāya gacchati, pekkhamāne mahājane’’.</p>\r\n<p class="bodytext">Gunnaṃ sāmīti sambandhe chaṭṭhī, gosu sāmīti visayasattamī. Evaṃ gunnamissaro, gosvi+ssaro, gunnaṃ adhipati, gosu adhipati, gunnaṃ dāyādo, gosu dāyādo, gunnaṃ sakkhī, gosu sakkhī, gunnaṃ patibhū, gosu patibhū, gunnaṃ pasuto, gosu pasuto, kusalā naccagītassa, kusalā naccagīte, āyutto kaṭakaraṇassa, āyutto kaṭakaraṇe. Tathā ādhāravacanicchāyaṃ sattamī, bhikkhūsu abhivādenti, muddhani cumbitvā, bāhāsu gahetvā, hatthesu piṇḍāya caranti, kadalīsu gaje rakkhanti, ñāṇasmiṃ pasanno, ñāṇasmiṃ ussukkoti visayasattamī.</p>\r\n<p class="subhead">36. Yato niddhāraṇaṃ</p>\r\n<p class="bodytext">Jātiguṇakriyāhi samudāyate+kadesassa puthakkaraṇaṃ niddhāraṇaṃ, yato taṃ karīyati, tato chaṭṭhīsattamiyo honti. Sālayo sūkadhaññānaṃ pathyatamā, sūkadhaññesu sālayo pathyatamā. Kaṇhā gāvīnaṃ sampannakhīratamā, kaṇhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',130,replace(replace('<p class="bodytext">gāvīsu <a name="M0.0130"></a> sampannakhīratamā. Gacchataṃ dhāvanto sīghatamā, gacchantesu dhāvanto sīghatamā.</p>\r\n<p class="subhead">14. Sattamyā+dhikye</p>\r\n<p class="bodytext">Ādhikye atthe upena yuttamhā sattamī hoti. Upakhāriyaṃ doṇo, khāriyā doṇo adhikotyattho. Tathā upanikkhe kahāpaṇaṃ.</p>\r\n<p class="subhead">15. Sāmitthe+dhinā</p>\r\n<p class="bodytext">Sāmibhāvatthe adhinā yuttamhā sattamī hoti. Adhibrahmadatte pañcālā, adhipañcālesu brahmadatto, brahmadattissarā pañcālāti attho. Adhidevesu buddho, sammutidevādīhi buddho adhikotyattho.</p>\r\n<p class="bodytext">Ettha ca yathāvuttesu atthesu ayaṃ bhedo –</p>\r\n<p class="bodytext">Kārakaṃ sādhakaṃ nibbattakaṃ kriyānimittanti atthato ekameva, tañca daṇḍo, dhavalo, pacanaṃ, citto, goti dabba+guṇa+kriyā+nāma+jātibhedena pañcavidhampi kamma+kattādivasena chabbidhaṃ, taṃ sattikārakaṃ, tadādhārabhūtadabbādayo tatraṭṭhakārakaṃ, na mukhyato. Mukhyato ce honti, tesaṃ sattidabbānaṃ aññamaññabyāvaṭarūpattā yo ādhāro, so ādhāroyeva, kismiñcikāle karaṇaṃ vā katturūpabhūto vā na hoti, tasmā thāli pacati, thāliyā pacati, thāliyaṃ pacatītyādinā ekassa vatthuno kattu+karaṇā+dhārabhedo na siyā. Sattipakkhe pana dabbādīnaṃ anekasattiyā ādhārattā sattiyā dabbādayo vivakkhitā taṃ taṃ kārakaṃ hotīti dabbādīnaṃ abhedepi kārakabhedo yujjate. Vuttañhi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',131,replace(replace('<p class="gatha1">Vicittakattuādīhi<a name="M0.0131"></a>, saṃyogā ekavatthuno;</p>\r\n<p class="gathalast">Nānāttaṃ yujjate nāṭya-bhedena naṭakassi+vāti.</p>\r\n<p class="bodytext">Thāli vatthuto ekā cedapi sattikārakabhūtakattu+karaṇā+dhārādīnaṃ bhedena bheda+mupayāti, kimiva rāma+rāvaṇādivesadhārīnaṭako tesaṃ nāmavasena rāmo, rāvaṇoti bheda+mupayāti, ta+mivāti adhippāyo.</p>\r\n<p class="bodytext">Puna+rapi –</p>\r\n<p class="gatha1">Yathe+kopi paṭo sutta, pītādiguṇasaṃyuto;</p>\r\n<p class="gathalast">Sukko paṭoti pītoti, bhedaṃ yātye+vameva+yaṃ.</p>\r\n<p class="bodytext">Ettha ca sattibhūtakattādayo mukhyakārakaṃ, taṃyogena dabbabhūtathālī guṇakārakaṃ, te ca kathaṃ kriyāya kārakā honti. Kattā attanā patiṭṭhitāya hasati+naccaticcādikriyāya nimittaṃ hoti. Kammañca attanā patiṭṭhitakriyāya tadatthabhūtaindhanādīni pavattentaṃ ‘‘odanaṃ pacatī’’tyādo nimittaṃ. ‘Kaṭṭhehi paccatī’tyādo kaṭṭhāni jalanakriyāya sādhetabbapākassa aṅgabhāvena nimittaṃ. ‘Pharasunā chindatī’tyādo pharasu ca kaṭṭhānaṃ dvidhāpavattiyā nimittaṃ. Kammañca kriyāya sambandhīyamānabyāpye sati nimittabhāvena kriyāya nimittaṃ. Tathā sampadānā+vadhi+ādhārānaṃ ‘gāvo dadāti’ ‘gāmasmā apanayati’ ‘thāliyaṃ pacatī’tyādīsu godānā+panayana+pacanakriyānaṃ nimittattā kriyāya nimittāni honti. Tesaṃ yathāsakaṃ kriyāya pavattako kattā, tasmā sa eva padhāno kattāti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',132,replace(replace('<p class="bodytext">vohāraṃ <a name="M0.0132"></a> labhati, aññesaṃ karaṇādīnaṃ kattubhāve satipi appadhānattā taṃ na labhati.</p>\r\n<p class="bodytext">Codakena vuttañhi –</p>\r\n<p class="gatha1">Nanu sāmagyamīnāyaṃ, kriyāsiddhi kathaṃ vada;</p>\r\n<p class="gathalast">Ekassa kattuno eva, sabbesaṃ kattutaṃ vinā.</p>\r\n<p class="gatha1">Sabbesaṃ karaṇādīnaṃ, kattutāya viyogato;</p>\r\n<p class="gathalast">Karaṇādīnaṃ abhāvattā, kārakaṃ na hi chabbidhanti.</p>\r\n<p class="bodytext">Vuccate –</p>\r\n<p class="gatha1">Yadyapya+tthi hi kattuttaṃ, sabhāvā karaṇādisu;</p>\r\n<p class="gathalast">Kriyāsiddhyā tathāpye+ta+mappadhānaṃ paraṅgato.</p>\r\n<p class="bodytext">Etaṃ sabhāvato upalabbhamānaṃ karaṇādīsu kattuttaṃ paresaṃ karaṇādīnaṃ jalana+dhāraṇādikriyāya aṅgaṃ upāyati appadhānanti adhippāyo.</p>\r\n<p class="gatha1">Kārakaṃ chabbidhaṃ saññā-vasā chabbīsatividhaṃ;</p>\r\n<p class="gathalast">Pabhedā sattadhā kammaṃ, kattā pañcavidho bhave.</p>\r\n<p class="gatha1">Karaṇaṃ duvidhaṃ hoti, sampadānaṃ tidhā mataṃ;</p>\r\n<p class="gathalast">Apādānaṃ pañcavidhaṃ, ādhāro tu catubbidho.</p>\r\n<p class="bodytext">Vibhattiyo pana –</p>\r\n<p class="gatha1">Paccatta+mupayogañca, karaṇaṃ sampadāniyaṃ;</p>\r\n<p class="gathalast">Nissakkaṃ sāmivacanaṃ, bhumma+mālapana+ṭṭhamaṃti.</p>\r\n<p class="centered">Iti payogasiddhiyaṃ kārakakaṇḍo tatiyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',133,replace(replace('<p class="chapter">4. Samāsakaṇḍa</p>\r\n<p class="bodytext">Atha <a name="M0.0133"></a> nāmānameva aññamaññasambandhīnaṃ samāsoti nāmanissitattā, sayañca nāmikattā nāmānantaraṃ samāso vuccate.</p>\r\n<p class="subhead">1. Syādi syādine+katthanti</p>\r\n<p class="bodytext">Ida+madhikataṃ veditabbaṃ. Pubbe vuttavidhiggahaṇañāyena syādīti tadantassa gahaṇaṃ. So ca bhinnatthānaṃ nāmāna+mekatthībhāvo samāsoti vuccate.</p>\r\n<p class="subhead">2. Asaṅkhyaṃ vibhatti+sampatti+samīpa+sākalyā+bhāva+yathā+ pacchā+yugapadatthe</p>\r\n<p class="gatha1">Pubbassa+tthaparaṃ yassa, aññatthaparamañca yaṃ;</p>\r\n<p class="gathalast">Napuṃsakaṃ bhave yañca, ta+dāsaṃkhya+mihe+ssate.</p>\r\n<p class="gatha1">Satthantare pasiddhaṃ yaṃ, abyayībhāvanāmato;</p>\r\n<p class="gathalast">Upakumbhaṃ tiṭṭhagu ca, pātameghaṃti taṃ yathā.</p>\r\n<p class="bodytext">Asaṅkhyaṃ syādyantaṃ vibhatyādīna+matthe vattamānaṃ syādyantena sahe+katthaṃ bhavati. ‘‘Aviggaho niccasamāso, padantaraviggaho ce’’ti padantaraviggaho. Itthīsu tathā pavattāti viggayha vibhatyatthe samāse kate –</p>\r\n<p class="subhead">2,119. Ekatthatāyaṃ</p>\r\n<p class="bodytext">Eko attho yassa pakatipaccayādisamudāyassa so ekattho. Tassa bhāvo pavattinimittaṃ ekatthatā, īyādi+ṇādi+samāsavidhānaṃ, tasmiṃ sati syādilopo hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',134,replace(replace('<p class="subhead">9. Taṃ napuṃsakanti</p>\r\n<p class="bodytext">Napuṃsakaliṅge <a name="M0.0134"></a> ca ‘‘pubbasmā+mādito’’ti sabbavibhattīnaṃ lope ca kate adhitthi tiṭṭhati, bho adhitthi, adhitthi passa, adhitthi kataṃ, adhitti carati, adhitthi dehi, adhitthi apehi, adhitthi āyattaṃ, adhitthi patiṭṭhitaṃ. Evaṃ adhikumāri, antabhūtassa appadhānassa ghapassa ‘‘ghapassa+ntassā+ppadhānassā’’ti passa rasso.</p>\r\n<p class="bodytext">Saha=sampannaṃ brahmaṃ sabrahmaṃ. Vuttanayena ettha ca upari ca samāsādayo honti, ettha ‘‘akāle satatthe’’ti sahassa sādeso. Syādimhi kate ‘‘pubbasmā+mādito’’ti syādīnaṃ lope ca sampatte ‘‘nā+to+mapañcamiyā’’ti apañcamiyā paṭisedho ca akārantaasaṃkhyasamāsato parāsaṃ sabbavibhattīnaṃ aṃādeso ca hoti. Eva+muparipi.</p>\r\n<p class="bodytext">Bhikkhānaṃ samiddhi subhikkhaṃ, ettha ghassa rasso. Subhikkhaṃ tiṭṭhati bho subhikkhaṃ, subhikkhaṃ passa. Nā+smiṃsu ‘‘vā tatiyā sattamīnaṃ’’ti vikappena aṃ, subhikkhaṃ subhikkhena vā kataṃ, subhikkhaṃ subhikkhena vā carati, subhikkhaṃ dehi. Pañcamiyaṃ a+mabbhāvā subhikkhā apagaccha, subhikkha+māyattaṃ, subhikkhaṃ subhikkhe vā patiṭṭhitaṃ.</p>\r\n<p class="bodytext">Samīpatthe-kumbhassa samīpaṃ upakumbhaṃ, evaṃ upanagaraṃ, bho upakumbhaṃ iccādi.</p>\r\n<p class="bodytext">Sākalye-tiṇampi asesetvāti satiṇaṃ, tiṇampi asesetvā ajjhoharaṇīya+majjhoharatītyattho. Sesaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',135,replace(replace('<p class="bodytext">sabrahmasamaṃ<a name="M0.0135"></a>. Aggimpi asesetvāti sāggi, aggiganthaṃpi asesetvā adhītetyattho. Vāraggahaṇaṃ adhitthisamaṃ.</p>\r\n<p class="bodytext">Abhāvo sambandhībhedā bahuvidho, tatra iddhābhāvevigatā iddhi=vibhūti saddikānanti dussaddikaṃ. Atthābhāvemakkhikānaṃ abhāvo nimmakkhikaṃ, niddarathaṃ, nimmasakaṃ. Atikkamābhāve-atigatāni tiṇāni nittiṇaṃ. Upabhogasambandhīvattamānakālassa abhāve-atigataṃ lahupāvuraṇaṃ atilahupāvuraṇaṃ, lahupāvuraṇassa nā+yaṃ upabhogatā loti attho.</p>\r\n<p class="bodytext">Yathāttho+nekavidho, tatra yoggatāyaṃ-yoggaṃ rūpa+manurūpaṃ. Vicchāyaṃ-addhamāsaṃ addhamāsaṃ anu anvaddhamāsaṃ, evaṃ paccattaṃ. Atthānativattiyaṃ-sattiṃ anatikkamma yathāsatti. Evaṃ yathākkamaṃ, yathābalaṃ. Bahulādhikārā yā yā parisā yathāparisā. Sadisatthe-kikhiyā kaṇhavicchitadhenuyā sadiso sakikhi, sahassa sādeso. Ānupubbiyaṃ-jeṭṭhānukkamena anujeṭṭhaṃ.</p>\r\n<p class="bodytext">Pacchāatthe-rathassa pacchā anurathaṃ.</p>\r\n<p class="bodytext">Yugapadatthe-cakkena saha=ekakālaṃ sacakkaṃ, cakkena ekakkhaṇe nidhetīti attho.</p>\r\n<p class="subhead">3. Yathā na tulye</p>\r\n<p class="bodytext">Yathāsaddo tulyatthe vattamāno syādyantena sahe+kattho na bhavati. Yathā devadatto, tathā yaññadatto. Ettha upamānabhūto yathāsaddo ‘tathā yaññadatto’ti upameyya+mapekkhati, tasmā ‘‘sāpekkha+masamatthaṃ bhavatī’’ti ñāyā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',136,replace(replace('<p class="bodytext">asamāse <a name="M0.0136"></a> ‘‘na tulye’’ti paṭisedho kimattha+miti ce. Yasmā ‘‘yathā devadatto’’ti samudāyameva upamānaṃ bhavati, na visuṃ yathāsaddo, tasmā samudāyasseva upameyyasāpekkhatte na asāmatthiyatā na visuṃ yathāsaddassa, tasmā sādisse pattasamāsassa paṭisedhattha+midaṃ. Tulyattheti vattabbe tulyaṃ vinā tulyatā natthīti tulyattheti avatvā tulyeti vuttaṃ.</p>\r\n<p class="gatha1">Yathākathañci sādissaṃ, ñāyate yattha sambhavaṃ;</p>\r\n<p class="gathalast">Upamā nāma sā tassā, papañco bahudhā bhave.</p>\r\n<p class="subhead">4. Yāvā+vadhāraṇe</p>\r\n<p class="bodytext">Yāvasaddo avadhāraṇe vattamāno syādyantena sahe+kattho bhavati. Avadhāraṇaṃ=ettakatāparicchedo. Yāvantāni amattāni=bhājanāni yāvāmattaṃ, iminā samāse kate sesaṃ pubbasamaṃ. Jīvassa yattako paricchedo yāvajīvaṃ. Yāvatāyukaṃ, ‘‘sakatthe’’ti kapaccayo. Yattakena attho yāvadatthaṃ.</p>\r\n<p class="subhead">5. Payyapā bahi tiro pure pacchā vā pañcamyā</p>\r\n<p class="bodytext">Pariādayo pañcamyantena sahe+katthā honti vā. Pari pabbatā paripabbataṃ. Vāssa vākyavikappatthattā pari pabbatā iccādayopi honti. Apa pabbatā apapabbataṃ, ā pāṭaliputtā āpāṭaliputtaṃ, bahi gāmā bahigāmaṃ, tiro pabbatā tiropabbataṃ, pure bhattā purebhattaṃ, pacchā bhattā pacchābhattaṃ, sabbaṃ upakumbhasamaṃ, iminā samāso viseso. Eva+muparisuttepi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',137,replace(replace('<p class="bodytext">Vātya+dhikāro <a name="M0.0137"></a> –</p>\r\n<p class="subhead">6. Samīpā+yāmesva+nu</p>\r\n<p class="bodytext">‘‘Asaṃkhya’’miccādinā niccasamāsassa vikappatthaṃ samīpaggahaṇaṃ. Anusaddo sāmīpye āyāme ca vattamāno syādyantena sahe+kattho vā hoti. Ettha samīpaggahaṇassa bhāvappadhānattā sāmīpyameva gamyateti sāmīpyeti vuttaṃ. Vanassa anu=samīpaṃ anuvanaṃ, gaṅgāya anu=āyāmo anugaṅgaṃ, gaṅgāya anu vā bārāṇasī.</p>\r\n<p class="subhead">7. Tiṭṭhagvādīni</p>\r\n<p class="bodytext">Tiṭṭhaguādīni aññatthena siddhāpi asmiṃ asaṃkhyasamāse nipātiyanti. Tiṭṭhanti gāvo yasmiṃ kāleti tiṭṭhantasadda+gosaddehi paṭhamāyomhi kate ‘‘vā+nekaññatthe’’ti samāso, iminā nipātanā lope napuṃsakatthe ca kate simhi gossa ‘‘gossu’’ti ukāre ‘‘pubbasmā+mādito’’ti vibhattilopo. Evaṃ vahantī gāvo yasmiṃ kāleti vahaggu iccādi. Velāppakāsakapātoādīnampi ettheva saṅgaho, pāto nahānanti sattamīamādisamāse pātanahānaṃ, evaṃ sāyanahānaṃ, pātakālaṃ, sāyakālaṃ, pātameghaṃ, sāyameghaṃ, pātamaggaṃ, sāyamaggaṃ, ettha ‘‘eona+ma vaṇṇe’’ti akāro, niggahītassa lopo ca hoti.</p>\r\n<p class="subhead">8. Ore+pari+paṭi+pāre+majjhe+heṭṭhu+ddhā+dho+nto vā chaṭṭhiyā.</p>\r\n<p class="bodytext">Orādayo saddā chaṭṭhiyantena sahe+katthā vā honti. Oraṃ gaṅgāya oregaṅgā, upari sikharassa uparisikharaṃ, paṭi=mukhaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',138,replace(replace('<p class="bodytext">sotassa <a name="M0.0138"></a> paṭisotaṃ, pāraṃ yamunāya pāreyamunaṃ, majjhaṃ gaṅgāya majjhegaṅgaṃ, heṭṭhā pāsādassa heṭṭhāpāsādaṃ, uddhaṃ gaṅgāya uddhagaṅgaṃ, adho gaṅgāya adhogaṅgaṃ, anto pāsādassa antopāsādaṃ. Iminā nipātanāva niggahītalope ca ekāre ca kate oreccādi hoti.</p>\r\n<p class="subhead">10. A+mādi</p>\r\n<p class="bodytext">A+mādisyādyantaṃ syādyantena saha bahula+mekattaṃ hoti.</p>\r\n<p class="gatha1">Uttarassa padassa+ttho, padhānaṃ liṅga+massa ca;</p>\r\n<p class="gathalast">Dutiyantādipadekattho, bahudhā taṃ vibhajjate.</p>\r\n<p class="gatha1">Paresa+missate tañca, bhiyyo tappurisā+khyayā;</p>\r\n<p class="gathalast">Taṃ yathā+tra rājāpaccaṃ, katthacīti+mitīdisaṃ.</p>\r\n<p class="bodytext">A+mādyantānaṃ kārakānaṃ akārakānañca samāso katthacimeva vā hoti. Tañca bahulaṃvidhānenāti daṭṭhabbaṃ.</p>\r\n<p class="bodytext">Tattha dutiyātappuriso amādi gata+nissitā+tītā+tikkanta+pattā+pannādīhi bhavati. Saraṇaṃ gatoti samāse kate ‘‘ekatthatāyaṃ’’ti vibhattilopādi upari sabbattha pubbasamaṃ. Saraṇagato, saraṇagatā. Saraṇagatā, saraṇagatāyo. Saraṇagataṃ kulaṃ, saraṇagatāni kulāni iccādi. Araññagato, bhūmigato. Dhammaṃ nissito dhammanissito, atthanissito. Bhavaṃ atīto bhavātīto, kālātīto. Pamāṇaṃ atikkantaṃ pamāṇātikkantaṃ, lokātikkantaṃ. Sukhaṃ patto sukhappatto, dukkhappatto. Sotaṃ āpanno sotāpanno, nirodhasamāpanno, maggappaṭipanno. Rathaṃ āruḷho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',139,replace(replace('<p class="bodytext">rathāruḷho<a name="M0.0139"></a>. Sabbarattiṃ sobhaṇo sabbarattisobhaṇo, muhuttasukhaṃ. Akārakānaṃ samāso accantasaṃyoge. Vuttiyevo+papadasamāse, tassa niccattā. Yathā kammaṃ karotīti kammakāro, kumbhakāro, atthaṃ kāmetīti atthakāmo, dhammakāmo, dhammaṃ dhāretīti dhammadharo, vinayadharo. Sānaṃ pacatīti sapāko, tantaṃ vāyatīti tantavāyo, varaṃ āharatīti varāharo. Nta+māna+ktavantehi vākyameva. Dhammaṃ suṇanto, dhammaṃ suṇamāno, odanaṃ bhuttavā.</p>\r\n<p class="bodytext">Tatiyātappuriso kitaka+pubba+sadisa+samo+nattha+kalaha+nipuṇa+missa+sakhilādīhi. Buddhena bhāsito buddhabhāsito dhammo, evaṃ jinadesito. Satthārā vaṇṇito satthuvaṇṇito. Viññūhi garahito viññugarahito, viññuppasattho, issarakataṃ, sayaṃ kataṃ, sukehi āhaṭaṃ sukāhaṭaṃ, raññā hato rājahato, rājapīḷito. Agginā daḍḍho aggidaḍḍho, sappena daṭṭho sappadaṭṭho, sallehi viddho sallaviddho, icchāya pakato icchāpakato, sīlasampanno. Evaṃ sukhasahagataṃ, ñāṇasampayuttaṃ, mittasaṃsaggo, piyavippayogo, jātithaddho, guṇahīno, guṇavuddho, catuvaggakaraṇīyaṃ, catuvaggādikattabbaṃ. Kākehi peyyā kākapeyyā, nadī. Kvaci vuttiyeva, urasā gacchatīti urago, pādena pivatīti pādapo. Kvaci vākyameva, pharasunā chinnavā, kākehi pātabbā, dassanena pahātabbā. Pubbādiyoge-māsena pubbo māsapubbo. Evaṃ mātusadiso, mātusamo. Ekūnavīsati, sīlavikalo, asikalaho, vācānipuṇo, yāvakālikasammissaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',140,replace(replace('<p class="bodytext">vācāsakhilo<a name="M0.0140"></a>. Satthārā sadiso satthusadiso, satthukappo, puññena atthiko puññatthiko, guṇādhiko. Dadhinā upasittaṃ bhojanaṃ dadhibhojanaṃ, guḷena saṃsaṭṭho odano guḷodano. Kārakasambandho kriyāya kato, upasittādikriyānaṃ apaññāyanepi vuttiyevo+pasittādikriyāna+mākhyāpanato natthā+yuttatthatā. Evaṃ khīrodano. Assena yutto ratho assaratho, maggacittaṃ, jambuyā paññāto lakkhito dīpo jambudīpo, ekena adhikā dasa ekādasa, jātiyā andho jaccandho, pakatiyā medhāvī pakatimedhāvī iccādi.</p>\r\n<p class="bodytext">Catutthītappuriso tadattha+attha+hita+deyyādīhi. Tadatthe-kathinassa dussaṃ kathinadussaṃ, kathinacīvarassāti attho. Evaṃ cīvaradussaṃ, cīvaramūlaṃ, yāguyā atthāya taṇḍulā yāgutaṇḍulā, bhattataṇḍulā, saṅghassa atthāya bhattaṃ saṅghabhattaṃ, āgantukabhattaṃ, evaṃ gamikabhattaṃ, pāsādāya dabbaṃ pāsādadabbaṃ. Ettha cā+yaṃ niccasamāso, tassa tiliṅgatā ca-bhikkhusaṅghassa attho vihāro bhikkhusaṅghattho vihāro, bhikkhusaṅghatthā yāgu, bhikkhusaṅghatthaṃ cīvaraṃ. Yassa attho yadattho, yadatthā, yadatthaṃ. Evaṃ tadattho, tadatthā, tadatthaṃ. Tathā lokahito. Buddhassa deyyaṃ buddhadeyyaṃ pupphaṃ. Saṅghadeyyaṃ cīvaraṃ. Idha na hoti ‘‘saṅghassa dātabbaṃ’’.</p>\r\n<p class="bodytext">Pañcamītappuriso apagamana+bhaya+virati+mocanatthādīhi. Methunasmā apeto methunāpeto, evaṃ palāpagato, nagaraniggato, piṇḍapātapaṭikkanto, kāmato nikkhantaṃ kāmanikkhantaṃ, rukkhaggā patito rukkhaggapatito, sāsanaccuto, āpatti- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',141,replace(replace('<p class="bodytext">vuṭṭhānaṃ<a name="M0.0141"></a>, dharaṇītalaggato, sabbabhavehi nissaṭo sabbabhavanissaṭo. Bhayatādiyoge-rājato bhayaṃ rājabhayaṃ, corabhayaṃ, amanussabhayaṃ, aggibhayaṃ, pāpabhīto, pāpabhīruko. Akattabbato virati akattabbavirati, evaṃ kāyaduccaritavirati, vacīduccaritavirati. Bandhanā mutto bandhanamutto, vanamutto, bandhanamokkho. Kammasamuṭṭhitaṃ, ukkaṭṭhukkaṭṭhaṃ, omakomakaṃ. Kvaci vuttiyeva, kammato jātaṃ kammajaṃ, evaṃ cittajaṃ, utujaṃ, āhārajaṃ. Idha na hoti ‘pāsādā patito’.</p>\r\n<p class="bodytext">Chaṭṭhītappuriso rañño putto rājaputto, evaṃ rājapuriso, ācariyapūjako, buddhasāvako, buddharūpaṃ, jinavacanaṃ, samuddaghoso, dhaññānaṃ rāsi dhaññarāsi, pupphagandho, phalaraso, kāyassa lahutā kāyalahutā. Maraṇassati, rukkhamūlaṃ, ayassa patto ayopatto, ettha ‘‘manādyāpādīna+mo maye ce’’ti o. Evaṃ suvaṇṇakaṭāhaṃ, pānīyathālakaṃ, sappikumbho, devānaṃ rājā devarājā. Pumassa liṅgaṃ pulliṅgaṃ, ‘‘puṃ pumassavā’’ti pumassa puṃ, niggahītalopo, lassa dvibhāvo ca. Hatthipadaṃ, itthirūpaṃ, bhikkhunisaṅgho, jambusākhā, ettha īkārūkārānaṃ rasso. Bahulādhikārā nta+māna+niddhāriya+pūraṇa+bhāva+tittatthehi na hoti. Mamā+nukubbaṃ, mamā+nukurumāno, gunnaṃ kaṇhā sampannakhīratamā, sissānaṃ pañcamo, paṭassa suttatā. Kvaci hoteva ‘vattamānasāmīpyaṃ’. Brāhmaṇassa sukkā dantāti sāpekkhatāya na hoti. Phalānaṃ titto, phalāna+māsito, phalānaṃ suhito. ‘‘Brāhmaṇassa uccaṃ gehaṃ’’ti sāpekkhatāya na hoti. ‘‘Rañño pāṭaliputtakassa dhanaṃ’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',142,replace(replace('<p class="bodytext">dhanasambandhe <a name="M0.0142"></a> chaṭṭhīti pāṭaliputtakena sambandhābhāvā na hoti. ‘‘Rañño go ca asso ca puriso cā’’ti bhinnatthatāya vākyameva. Rañño gavassapurisā rājagavassapurisāti vutti hoteva, ekatthībhāvā. Sambandhīsaddānaṃ pana niccasāpekkhattepi gamakattā samāso, gamakattampi hi samāsassa nibandhanaṃ, yathā devadattassa gurukulaṃ, bhagavato sāvakasaṅghotiādi.</p>\r\n<p class="bodytext">Sattamītappuriso rūpe saññā rūpasaññā, evaṃ rūpasañcetanā, saṃsāradukkhaṃ. Cakkhumhi sannissitaṃ viññāṇaṃ cakkhuviññāṇaṃ. Dhamme rato dhammarato, dhammābhirati, dhammaruci, dhammagāravo, dhammesu nirutti dhammanirutti, dānādhimutti, bhavantarakataṃ. Dassane assādo dassanassādo. Araññe vāso araññavāso, vikālabhojanaṃ, kālavassaṃ, vanapupphaṃ, vanamahiso, gāmasūkaro, samuddamacchā, āvāṭakacchapo, āvāṭamaṇḍūko, kūpamaṇḍūko, titthanāvā. Itthīsu dhutto itthidhutto, akkhadhutto. Chāyāyaṃ sukkho chāyāsukkho, aṅgārapakkaṃ, cārakavano. Kvaci vuttiyeva, vane caratīti vanacarako, kucchimhi sayantīti kucchisayā, thale tiṭṭhatīti thalaṭṭho, jalaṭṭho, pabbataṭṭho, maggaṭṭho. Paṅke jātaṃ paṅkajaṃ, saroruha+miccādi. Idha na hoti, bhojane mattaññutā, indriyesu guttadvāratā, āsane nisinno, āsane nisīditabbaṃ.</p>\r\n<p class="subhead">11. Visesana+mekatthena</p>\r\n<p class="bodytext">Visesanaṃ syādyantaṃ visessena syādyantena samānādhikaraṇenasahe+katthaṃ hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',143,replace(replace('<p class="gatha1">Samānatthe <a name="M0.0143"></a> pade yattha, bhedyabhedakavācake;</p>\r\n<p class="gathalast">Visesanasamāso+yaṃ, visessatthapadhānato.</p>\r\n<p class="gatha1">Visessagata+meva+tra, liṅga+metaṃ paraṃ tato;</p>\r\n<p class="gathalast">Kammadhāraya+micce+sa, samāso+ññehi saññito.</p>\r\n<p class="bodytext">Sutte visessenāti avuttepi visesanassa sambandhīsaddattā sāmatthiyato labbhamānaākaḍḍhitasaddaṃ pati ‘‘visessenā’’ti vuttaṃ. Vuttañca –</p>\r\n<p class="gatha1">Sāmaññavatthu yā vattha+ntarato tu visesiya;</p>\r\n<p class="gathalast">Ekappakāre ṭhapanā, visesana+mitī+ritaṃ.</p>\r\n<p class="gatha1">Ekappakāragaṃ vatthu, visessanti pavuccati;</p>\r\n<p class="gatha2">Padāni yāni yāneva, sambandha+mupayanti+ha;</p>\r\n<p class="gathalast">Gamyate kāmacārena, visesana+visessatāti.</p>\r\n<p class="bodytext">Ettha ca uppaladabbaṃ rattuppalādito visesayatīti nīlasaddo visesanaṃ. Tena visesiyatīti uppalasaddo visessaṃ. Api ca bhamara+ṅgārādisāmañña nīlatthato visesiyatīti nīlaṃ visessaṃ. Na vatthādīnaṃ, uppalassevāti visesanato uppalaṃ visesanaṃti kāmacāreneti vuttaṃ. Api ca –</p>\r\n<p class="gatha1">Parito ayantya+nena+tthā, pariyāyoti vuccati;</p>\r\n<p class="gathalast">Govācāti pavutte tu, vācattho tu visesanaṃ.</p>\r\n<p class="gatha1">Visesse dissamānā yā, liṅga+saṃkhyā+vibhattiyo;</p>\r\n<p class="gathalast">Tulyādhikaraṇe bhiyyo, kattabbā tā visesaneti –</p>\r\n<p class="bodytext">Vuttattā mahanto+ccādīsu samānaliṅgādayo daṭṭhabbā. Bhiyyoti kiṃ, devā pamāṇaṃ iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',144,replace(replace('<p class="bodytext">So <a name="M0.0144"></a> ca chabbidho visesanapubbapado, visesanuttarapado, visesanobhayapado, upamānuttarapado, sambhāvanāpubbapado, avadhāraṇapubbapadoti.</p>\r\n<p class="bodytext">Tattha visesanapubbapade tāva-mahanto ca so puriso cāti vākye iminā suttena samāso. ‘‘Ṭa nta+ntūnaṃ’’ti ntassa ṭādese dīgho hoti, mahāpuriso, mahāpurisā iccādi.</p>\r\n<p class="bodytext">Vākye tulyādhikaraṇabhāva pakāsanatthaṃ ca+ta-saddapayogo. Vuttiyantu samāseneva tappakāsanato na tappayogo. Eva+maññatrāpi vuttatthāna+mappayogo. Evaṃ mahāvīro, mahāmuni. Mahantañca taṃ balañcāti mahābalaṃ, mahabbhayaṃ. Santo ca so puriso cāti sappuriso. Tathā pubbapuriso, aparapuriso, paṭhamapuriso, majjhimapuriso, uttamapuriso, parapuriso, setahatthī, kaṇhasappo, nīluppalaṃ, rattuppalaṃ, lohitacandanaṃ. Kvaci na hoti, puṇṇo mantānīputto, citto gahapati. Pumā ca so kokilo cāti puṅkokilo, uttarapade pumassa puṃ hoti. Evaṃ punnāgo.</p>\r\n<p class="bodytext">Khattiyā ca sā kaññā cāti khattiyakaññā.</p>\r\n<p class="subhead">67. Itthiyaṃ bhāsitapumi+tthī pumeve+kattheti</p>\r\n<p class="bodytext">Bhāsitapumā itthī pumeva hotīti pumbhāvā itthipaccayānaṃ nivatti hoti. Evaṃ rattalatā, dutiyabhikkhā. Brāhmaṇī ca sā dārikā cāti brāhmaṇadārikā, nāgamāṇavikā. Itthiyanti kiṃ, kumāriratanaṃ, samaṇipadumaṃ. Bhāsitapumāti kiṃ, gaṅgānadī, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',145,replace(replace('<p class="bodytext">taṇhānadī<a name="M0.0145"></a>, pathavīdhātu. Puratthimo ca so kāyo cāti puratthimakāyo. Ettha ca kāyekadese kāyasaddo. Evaṃ pacchimakāyo, uparimakāyo, heṭṭhimakāyo, sabbakāyo, navāvāso, kataranikāyo, hetupaccayo. Jīvitappadhānaṃ navakaṃ jīvitanavaka+miccādi.</p>\r\n<p class="bodytext">Visesanuttarapade therā+cariya+paṇḍitā visesanaṃ parañca bhavati. Yathā sāriputto ca so thero cāti sāriputtatthero, evaṃ mahāmoggallānatthero, mahākassapatthero, buddhaghosācariyo, dhammapālācariyo, ācariyaguttilo vā. Mahosadho ca so paṇḍito cāti mahosadhapaṇḍito, evaṃ vidhurapaṇḍito.</p>\r\n<p class="bodytext">Visesanobhayapade yathā-sītañca taṃ uṇhañcāti sītuṇhaṃ, siniddho ca so uṇho cāti siniddhuṇho māso. Khañjo ca so khujjo cāti khañjakhujjo, evaṃ andhabadhiro, katākataṃ, chiddāvachiddaṃ, uccāvacaṃ, chinnabhinnaṃ, gatapaccāgataṃ. Kvaci pubbakālassāpi paranipāto, vāsito ca so litto cāti littavāsito, evaṃ naggamūsito, sittasammaṭṭho, bhaṭṭhaluñjito.</p>\r\n<p class="bodytext">Upamanuttarapade upamānabhūtaṃ visesanaṃ paraṃ bhavati, yathā sīhoti vutte upacaritā+nupacaritasīhānaṃ sāmaññappatītiyaṃ munisaddo viseseti. Ettha ca –</p>\r\n<p class="bodytext">Upamāno+pameyyānaṃ, sadhammattaṃ siyo+pamā.</p>\r\n<p class="bodytext">Sā ca vatthu+vaṇṇa+ākārānaṃ sāmyena hoti. Sīhova sīho, muni ca so sīho cāti munisīho, munivasabho, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',146,replace(replace('<p class="bodytext">munipuṅgavo<a name="M0.0146"></a>, buddhanāgo, buddhādicco. Raṃsī viya raṃsī, saddhammo ca so raṃsī cāti saddhammaraṃsī, evaṃ vinayasāgaro. Puṇḍarikamiva puṇḍariko, samaṇo ca so puṇḍariko cāti samaṇapuṇḍariko, samaṇapadumo. Cando viya cando, mukhañca taṃ cando cāti mukhacando, mukhapadumaṃ iccādi.</p>\r\n<p class="bodytext">Sambhāvanāpubbapade yathā-dhammoti buddhi dhammabuddhi, evaṃ dhammasaññā, dhammasaṅkhāto, dhammasammato, pāṇasaññitā, asubhasaññā, aniccasaññā, dhātusaññā, attasaññā, attadiṭṭhi iccādi.</p>\r\n<p class="bodytext">Avadhāraṇapubbapade yathā-guṇo eva dhanaṃ guṇadhanaṃ, evaṃ saddhādhanaṃ, sīladhanaṃ, paññāratanaṃ, cakkhu eva indriyaṃ cakkhundriyaṃ, evaṃ cakkhāyatanaṃ, cakkhudhātu, cakkhudvāraṃ, rūpārammaṇa+miccādi.</p>\r\n<p class="gatha1">Visesana+visessehi, kriyāya ca sahe+rito;</p>\r\n<p class="gathalast">Tesaṃ bhāvaṃ vivecetā, nipāto byavacchindati.</p>\r\n<p class="gatha1">Ayoga+maññayogañca, accantāyoga+mevi+ti;</p>\r\n<p class="gathalast">Vivakkhāto payuttopi, evattho ñāyate yato.</p>\r\n<p class="gatha1">Byavacchedaphalaṃ vākyaṃ, tato citto dhanuddharo;</p>\r\n<p class="gathalast">Pāttho dhanuddharo nīlu+ppala+matthīti taṃ yathā.</p>\r\n<p class="bodytext">Ettha nipātoti eva-itinipāto, appayuttopi evasaddo evaṃ yojetabbo – ‘‘citto dhanuddharo evā’’ti visesanena yutto ayogavivacchedako, dhanunā yoge patiṭṭhāpanato ‘‘pāttho eva dhanuddharo’’ti visessena yutto aññayogavivacchedako, dhanuddharattassa pātthasaṃkhātaajjune eva patiṭṭhāpanato. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',147,replace(replace('<p class="bodytext">‘‘Nīluppala+matthevā’’ti <a name="M0.0147"></a> kriyāya yutto accantāyogavivacchedako, nīluppalassa sabbhāveyeva patiṭṭhāpanato.</p>\r\n<p class="subhead">21. Saṃkhyādi</p>\r\n<p class="bodytext">Ekatthe samāhāre saṃkhyādi napuṃsakaliṅgaṃ bhavati. Tayo lokā samāhaṭā=cittena sampiṇḍitā, tiṇṇaṃ lokānaṃ samāhāroti vā vākye visesanasamāse kate iminā napuṃsakattaṃ bhavati. Samāhārasse+kattā ekavacanameva, tilokaṃ, bho tiloka, tilokaṃ, tilokena iccādi. Evaṃ tayo daṇḍā tidaṇḍaṃ, tīṇi malāni samāhaṭāni, tiṇṇaṃ malānaṃ samāhāroti vā timalaṃ, tilakkhaṇaṃ, catusaccaṃ, pañcasikkhāpadaṃ, chaṭṭhāyatanaṃ, sattāhaṃ, aṭṭhasīlaṃ, navalokuttaraṃ, dasasīlaṃ, satayojanaṃ. Dve rattiyo samāhaṭā dvirattaṃ.</p>\r\n<p class="subhead">12. Naña</p>\r\n<p class="bodytext">Naūccetaṃ syādyantaṃ syādyantena sahe+katthaṃ hoti. Ññakāro ‘‘ṭa nañassā’’ti visesanattho ‘pāmanaputtādīsu nassa ṭo mā hotū’ti. Na brāhmaṇo abrāhmaṇo, ‘‘ṭa nañassā’’ti nassa ṭādeso. Ñña-kāro ettheva visesanattho.</p>\r\n<p class="gatha1">Na-nisedho sato yutto, desādiniyamaṃ vinā;</p>\r\n<p class="gathalast">Asato vā+phalo tasmā, katha+mabrāhmaṇotice.</p>\r\n<p class="gatha1">Nisedhatthānuvādena, paṭisedhavidhi kvaci;</p>\r\n<p class="gathalast">Parassa micchāñāṇattā+khyāpanāyo+papajjate.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',148,replace(replace('<p class="bodytext">Duvidho <a name="M0.0148"></a> ca+ssa nassa attho pasajjapaṭisedha+pariyudāsavasena. Tattha yo ‘‘asūrikapassārājadārā’’tiādīsu viya uttarapadatthassa sabbadā abhāvaṃ dīpeti, so pasajjapaṭisedhavācī nāma. Yo pana ‘‘abrāhmaṇa+mānayā’’tiādīsu viya uttarapadatthaṃ pariyudāsitvā paṭikkhipitvā taṃsadise vatthumhi kāriyaṃ paṭipādayati, so pariyudāsavācī nāma. Vuttañca –</p>\r\n<p class="gatha1">Pasajjapaṭisedhassa, lakkhaṇaṃ vatthunatthitā;</p>\r\n<p class="gathalast">Vatthuto+ññatra yā vutti, pariyudāsalakkhaṇaṃ.</p>\r\n<p class="gatha1">Yatra abrāhmaṇādīsu, vatthuṃ pariyudassati;</p>\r\n<p class="gathalast">Takriyāyuttarājādiṃ, vade so pariyudāsako.</p>\r\n<p class="gatha1">Pasajjapaṭisedho tu, vatthantara+manādiya;</p>\r\n<p class="gathalast">Kiñcivatthunisedhassa, pasaṅgo na bhaveyya so.</p>\r\n<p class="gatha1">Tadañño ca taṃviruddho,</p>\r\n<p class="gathalast">Tadabhāvo ca naññattho.</p>\r\n<p class="bodytext">Tadaññatthe – abrāhmaṇo, brāhmaṇato añño taṃsadisoti vuttaṃ hoti. Evaṃ amanusso, assamaṇo, na byākatā abyākatā dhammā. Tabbiruddhatthe-na kusalā akusalā, kusalapaṭipakkhāti attho. Evaṃ alobho, amitto, ayaṃ pariyudāsanayo. Tadabhāve-na katvā akatvā, akātuna puññaṃ, akaronto, abhāvo bhavati. Ayaṃ pasajjapaṭisedhanayo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',149,replace(replace('<p class="bodytext">Ettha <a name="M0.0149"></a> ca ubhosu pariyudāse brāhmaṇā añño brāhmaṇadhamme appatiṭṭhito khattiyādi brāhmaṇasadisova abrāhmaṇoti vutte patīyate. Itarasmiṃ pana pakkhe kenaci saṃsayanimittena khattiyādo brāhmaṇoti vuttassa micchāñāṇanivutti karīyati ‘‘brāhmaṇo+yaṃ na bhavati abrāhmaṇo’’ti, brāhmaṇattajjhāsito na bhavatītyattho. Tattha sadisattaṃ vinā micchāñāṇāsambhavā payogasāmatthiyā ca sadisapaṭipatti, taggatā ca liṅga+saṅkhyā bhavanti. Atoyeva uccate ‘‘naññivayutta+maññasadisādhikaraṇe, kathā hi atthasampaccayo’’ti.</p>\r\n<p class="subhead">75. Ana sareti</p>\r\n<p class="bodytext">Nañasaddassa sare ana, na asso anasso, na ariyo anariyo. Evaṃ anissaro, aniṭṭho, anāsavo. Na ādāya anādāya, anoloketvā icchādi. Bahulādhikārā ayuttatthehi kehici hoti. Puna na gīyantīti apunageyyā gāthā, anokāsaṃ kāretvā, amūlāmūlaṃ gantvā, acandamullokikāni mukhāni, asaddhabhojī, alavaṇabhojī.</p>\r\n<p class="subhead">13. Kupādayo nicca+masyādividhimhi</p>\r\n<p class="bodytext">Kusaddo pādayo ca syādyantena sahe+katthā honti niccaṃ syādividhivisayato+ññattha. Ettha abyabhicāripādisahacaraṇatthena kuiti nipātova, na pathavīvācako kusaddo. Syādividhivisayo nāma ‘‘lakkhaṇitthambhūtā’’ dinā patiādīnaṃ visaye katadutiyā, tañca anvaddhamāsanti asaṃkhyasamāsa+miva mā hotūti ‘‘asyādividhimhī’’ti nisedho. Kucchito brāhmaṇo kubrāhmaṇo, niccasamāsattā asapadena viggaho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',150,replace(replace('<p class="subhead">107. Sare kada kussu+ttaratthe</p>\r\n<p class="bodytext">Kussu+ttaratthe <a name="M0.0150"></a> vattamānassa sarādo uttarapade kadādeso hoti. Īsakaṃ uṇhaṃ kaduṇhaṃ, kucchitaṃ annaṃ kadannaṃ, kadasanaṃ. Sareti kiṃ, kuputtā, kudārā, kudāsā, kudiṭṭhi.</p>\r\n<p class="subhead">108. Kā+ppatthe</p>\r\n<p class="bodytext">Appatthe vattamānassa kussa kā hotu+ttarapade. Appakaṃ lavaṇaṃ kālavaṇaṃ. Evaṃ kāpupphaṃ.</p>\r\n<p class="subhead">109. Purise vāti</p>\r\n<p class="bodytext">Kussa kā vā. Kucchito puriso kāpuriso, kupuriso vā. Pakaṭṭho nāyako panāyako, padhānaṃ vacanaṃ pāvacanaṃ bhusaṃ vaddhaṃ pavaddhaṃ sarīraṃ, samaṃ sammā vā ādhānaṃ samādhānaṃ, vividhā mati vimati, vividho visiṭṭho vā kappo vikappo, adhiko devo atidevo, evaṃ adhidevo, adhisīlaṃ. Sundaro gandho sugandho, kacchito gandho duggandho, suṭṭhu kataṃ sukataṃ, duṭṭhu kataṃ dukkataṃ abhi siñcanaṃ abhisekoti sananto, atisayena katvā, kataṃ pakaritvā, pakataṃ, atisayena thutaṃ atitthutaṃ, atikkamma thutaṃ atitthutaṃ, īsaṃ kaḷāro ākaḷāro, suṭṭhu baddho ābaddho.</p>\r\n<p class="bodytext">Pādayo gatādyatthe paṭhamāya</p>\r\n<p class="bodytext">Pagato ācariyo pācariyo, evaṃ pantevāsī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',151,replace(replace('<p class="bodytext">Accādayo <a name="M0.0151"></a> kantādyatthe dutiyāya</p>\r\n<p class="bodytext">Atikkanto mañcaṃ atimañco. Atimālo, ‘‘ghapassa+ntassā+ppadhānassā’’ti mālāsadde ghassa rasso. Eva+muparipi ghapānaṃ rasso.</p>\r\n<p class="bodytext">Avādayo kuṭṭhādyatthe tatiyāya</p>\r\n<p class="bodytext">Avakuṭṭhaṃ kokilāya vanaṃ avakokilaṃ, avamayūraṃ. Avakuṭṭhanti pariccattaṃ.</p>\r\n<p class="bodytext">Pariyādayo gilānādyatthe catutthiyā</p>\r\n<p class="bodytext">Parigilāno+jjhenāya pariyajjheno.</p>\r\n<p class="bodytext">Nyādayo kantādyatthe pañcamiyā</p>\r\n<p class="bodytext">Nikkhanto kosambiyā nikkosambi. Asyādividhimhīti kiṃ, rukkhaṃ pati vijjotate.</p>\r\n<p class="subhead">14. Cī kriyatthehi</p>\r\n<p class="bodytext">Cīpaccayanto kriyatthehi syādyantehi sahe+kattho hoti. Amalīnaṃ malīnaṃ karitvāti viggayha ‘‘abhūtatabbhāve karā+sa+bhūyoge vikārācī’’ti cīpaccayekate iminā samāso. Ettha ca-kāro ‘‘cī kriyatthehī’’ti visesanattho. ‘‘Pyo vā tvāssa samāse’’ti pya hoti, pa-kāro ‘‘pye sissā’’ti visesanattho. Malinīkariya.</p>\r\n<p class="subhead">15. Bhūsanā+darā+nādaresva+laṃ+sā+sāti</p>\r\n<p class="bodytext">Bhūsanādīsva+tthesva+la+mādayo saddā ekatthā honti. Alaṃ karitvā sakkaritvā asakkaritvāti viggayha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',152,replace(replace('<p class="bodytext">samāse <a name="M0.0152"></a> kate pye ca ‘‘sā sādhikarā ca cariccā’’ti cādeso pararūpañca. Alaṃkariya, sakkacca, asakkacca.</p>\r\n<p class="subhead">16. Aññecā+ti suttena samāse kate… ettha yathā dvāraṃ vivarāti vutte pakaraṇato aggala+miti viññāyati, eva+midhāpi nipātapabhāve aññe cāti sāmaññaṃ ce+ti āgamānusārena labbhamānavibhatyantapaṭirūpanipātāva viññāyanti. Aggato bhavitvā purobhuyya, antarahito hutvā tirobhūya, antaradhānaṃ katvā tirokariya, urasi katvā urasikariya, manasi katvā manasikariya, majjhe katvā majjhekariya, tuṇhī bhavitvā tuṇhī bhūya.</p>\r\n<p class="subhead">17. Vā+neka+ññatthe</p>\r\n<p class="bodytext">Anekaṃ syādyantaṃ aññassa padassa atthe ekatthaṃ vā hoti.</p>\r\n<p class="gatha1">Padantarassa yassa+ttho, padhānaṃ liṅga+massa ca;</p>\r\n<p class="gathalast">Samāso so+ya+maññattho, bahubbīhiparavhayo.</p>\r\n<p class="bodytext">So ca navavidho dvipado, bhinnādhikaraṇo, tipado, na-nipātapubbapado, sahapubbapado, upamānapubbapado, saṅkhyobhayapado, disantarālattho, byatihāralakkhaṇo cāti.</p>\r\n<p class="subhead">1. Tattha dvipado tulyādhikaraṇo kammādīsu chasu vibhatyatthesu bhavati.</p>\r\n<p class="bodytext">(Ka) tattha dutiyatthe tāva-āgatā samaṇā imaṃ saṅghārāmanti āgatasamaṇo saṅghārāmo. So ca duvidho tagguṇā+tagguṇavasena. Vuttañhi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',153,replace(replace('<p class="gatha1">Tagguṇo+tagguṇo <a name="M0.0153"></a> ce+ti,</p>\r\n<p class="gathalast">So samāso dvidhā mato.</p>\r\n<p class="gatha1">Taṃ yathā ‘nīyataṃ lamba-</p>\r\n<p class="gathalast">Kaṇṇo+’ ‘yaṃ diṭṭhasāgaro’.</p>\r\n<p class="bodytext">Tesu yattha visesanabhūto attho aññapadatthaggahaṇena gayhati, so tagguṇasaṃviññāṇo, yathā ‘lambakaṇṇa+mānayā’ti. Yattha pana na gayhati, so atagguṇasaṃviññāṇo, yathā ‘bahudhana+mānayā’ti.</p>\r\n<p class="bodytext">Idha visesanassa pubbanipāto. Ettha ca āgatasaddo ca samaṇasaddo ca attano atthe aṭṭhatvā dutiyāvibhatyatthabhūte saṅghārāmasaṅkhāte aññapadatthe vattanti, tato samāseneva kammatthassa abhihitattā puna dutiyā na hoti. Tathā āgatasamaṇā sāvatthi, āgatasamaṇaṃ jetavanaṃ. Paṭipannā addhikā yaṃ paṭipannaddhiko patho, abhirūḷhāva, ṇijāyaṃ nāvaṃ sā abhirūḷhavāṇijā nāvā.</p>\r\n<p class="bodytext">(Kha) tatiyatthe-jitāni indriyāni yena so jitindriyo samaṇo, evaṃ diṭṭhadhammo, pattadhammo, katakicco. Vijitā mārā anenāti vijitamāro bhagavā, paṭividdhasabbadhammo. Karaṇatthe-chinno rukkho yena so chinnarukkho pharasu.</p>\r\n<p class="bodytext">(Ga) catutthiyatthe-dinno suṅko yassa so dinnasuṅko rājā, dinnaṃ bhojanaṃ assāti dinnabhojano.</p>\r\n<p class="bodytext">(Gha) pañcamiyatthe-niggatā janā yasmā so niggatajano gāmo, niggato ayo=sukhaṃ yasmāti nirayo, nikkile- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',154,replace(replace('<p class="bodytext">so<a name="M0.0154"></a>. Apetaṃ viññāṇaṃ asmāti apetaviññāṇo matakāyo, apagatabhayabheravo arahā.</p>\r\n<p class="bodytext">(Ṅa) chaṭṭhiyatthe-chinnā hatthā yassa so chinnahattho. Evaṃ paripuṇṇasaṅkappo khīṇāsavo, vīto rāgo assāti vītarāgo. Dve padāni assāti dvipado, dvihattho paṭo. Tevijjoti ettha tivijjo evāti sakatthe ṇo vuddhi ca. Catuppado, pañca cakkhūni assāti pañcacakkhu bhagavā, chaḷabhiñño, ‘‘ghapassā’’dinā rassattaṃ. Navaṅgaṃ satthusāsanaṃ. Dasabalo, anantañāṇo. Tīṇi dasa parimāṇa+mesaṃti tidasā devā, idha parimāṇasaddasannimānato dasasaddo saṅkhyāne vattate. Ayaṃ paccayo etesanti idappaccayā, uttarapade ‘‘imassi+daṃ vā’’ti imassa idaṃ. Ko pabhavo assāti kiṃ pabhavo kāyo. Vigataṃ malaṃ assāti vimalo, sundaro gandho assāti sugandhaṃ candanaṃ, evaṃ susīlo, sumukho, kucchito gandho assāti duggandhaṃ kuṇapaṃ, dummukho, duṭṭhu mano assāti dummano, evaṃ dussīlo. Tapo eva dhanaṃ assāti tapodhano. Khantisaṅkhātaṃ balaṃ assāti khantibalo. Indoti nāmaṃ etassāti indanāmo.</p>\r\n<p class="bodytext">Chandajātādīsu visesanavisesitabbānaṃ yathicchitattā ubhayaṃ pubbaṃ nipatati, kamātikkame payojanābhāvā. Jāto chando assāti jātachando, evaṃ chandajāto. Sañjātapītisomanasso, pītisomanassasañjāto. Māsajāto, jātamāso. Chinnahattho, hatthachinno.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',155,replace(replace('<p class="bodytext">Dīghājaṅghā <a name="M0.0155"></a> assāti dīghajaṅgho, ettha pumbhāvo, ‘‘ghapassā’’dinā rasso ca. Tathā pahūtajivho. Mahantī paññā assāti mahāpañño. ‘‘Itthiyaṃ bhāsitapumi+tthī pume+ve+katthe’’ti vīpaccayābhāventassa ṭādeso rassattañca. Itthiyanti kiṃ, khamādhano. Bhāsitapumāti kiṃ, saddhādhuro. Paññāpakatiko, paññāvisuddhiko, ettha ‘‘ltvitthiyūhi ko’’ti ko. Gaṇḍīvadhanvāti pakatantarena siddhaṃ.</p>\r\n<p class="bodytext">Nānā=ppakārā dumā nānādumā, nānādumehi patitāni nānādumapatitāni, nānādumapatitāni ca tāni pupphāni ceti nānādumapatitapupphāni, tehi vāsitā nānādumapatitapupphavāsitā, nānādumapatitapupphavāsitāsānuyassaso nānādumapatitapupphavāsitasānu pabbato, ayaṃ visesana+amādisamāsagabbho tulyādhikaraṇaaññapadattho.</p>\r\n<p class="bodytext">(Ca) sattamyatthe-sampannāni sassāni yasmiṃ so sampannasasso janapado. Sulabho piṇḍo imasminti sulabhapiṇḍo deso. Ākiṇṇā manussā yassaṃ sā ākiṇṇamanussā rājadhānī. Bahavo tāpasā etasminti bahutāpaso assamo. Upacitaṃ maṃsalohitaṃ asminti upacitamaṃsalohitaṃ sarīraṃ. Bahavo sāmino asminti bahusāmikaṃ nagaraṃ, bahū nadiyo asminti bahunadiko, īkārantattā kapaccayo. Evaṃ bahujambukaṃ vanaṃ, bahavo kattāro asmiṃ assa vāti bahukattuko deso, evaṃ bahubhattuko, ‘‘ltvitthiyūhi ko’’ti ko.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',156,replace(replace('<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Bhinnādhikaraṇo <a name="M0.0156"></a> yathā-ekarattiṃ vāso assāti ekarattivāso, samānena janena saddhiṃ vāso assāti samānavāso puriso. Ubhato byañjana+massāti ubhatobyañjanako, vibhatyalopo ‘‘vā+ññato’’ti ko ca, chattaṃ pāṇimhi assāti chattapāṇi, evaṃ daṇḍapāṇi, satthapāṇi, vajirapāṇi, khaggahattho, pattahattho, dāne ajjhāsayo assāti dānajjhāsayo dānādhimuttiko, buddhabhattiko, saddhammagāravo iccādi.</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. Tipado yathā-parakkamenā+dhigatā sampadā yehi te parakkamādhigatasampadā mahāpurisā. Evaṃ dhammādhigatabhogā. Onīto pattato pāṇi yena so onītapattapāṇi. Sīhassa pubbaddhamiva kāyo assāti sīhapubbaddhakāyo. Mattā bahavo mātaṅgā asminti mattabahumātaṅgaṃ vanaṃ.</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. Na-nipātapubbapado yathā-natthi etassa samoti assamo, ‘‘ṭa nañassā’’ti nassa ṭo. Evaṃ appaṭipuggalo, aputtako, ahetuko, kapaccayo, eva+muparipi ñeyyaṃ. Natthi saṃvāso etenāti asaṃvāso, na vijjate vuṭṭhi etthāti avuṭṭhiko janapado, abhikkhuko vihāro. Evaṃ anuttaro ‘‘ana sare’’ti ana, evaṃ anantaṃ, anāsavo.</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>. Paṭhamātthe sahapubbapado yathā-saha hetunā vattati so sahetuko sahetu vā, ‘‘sahassa so+ññatthe’’ti sahassa so, evaṃ sappītikā, sappaccayā, sakileso, saupādāno, saparivāro sahaparivāro vā, saha mūlena uddhaṭo samūluddhaṭo rukkho.</p>','\r',char(13)),'\n',char(10)),'-2-3-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',157,replace(replace('<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>. Upamānopameyyajotakaivayutto <a name="M0.0157"></a> upamānapubbapado paṭhamāya yathā-nigrodho iva parimaṇḍalo yo so nigrodhaparimaṇḍalo. Saṅkho viya paṇḍaro ayanti saṅkhapaṇḍaro, kāko viya sūro ayanti kākasūro. Cakkhu iva bhūto ayaṃ paramatthadassanatoti cakkhubhūto bhagavā. Evaṃ atthabhūto, dhammabhūto, brahmabhūto, andhabhūto. Muñjapabbajamiva bhūtā ayaṃ muñjapabbajabhūtā kudiṭṭhi. Tantākulamiva jātā ayaṃti tantākulajātā.</p>\r\n<p class="bodytext">Chaṭṭhyatthe-suvaṇṇassa vaṇṇo viya vaṇṇo yassa so suvaṇṇavaṇṇo bhagavā, majjhapadalopo. Nāgassa gati viya gati assāti nāgagati. Evaṃ sīhagati, nāgavikkamo, sīhavikkamo, sīhahanu. Eṇissa viya jaṅghā assāti eṇijaṅgho. Brahmuno viya saro assāti brahmassaro.</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>. Vāsaddatthe saṅkhyāubhayapado yathā-dve vā tayo vā dvatti, dvattayo ca te pattā ceti dvattipattā, ‘‘tisva’’iti tisadde pare dvissa attaṃ. Dvīhaṃ vā tīhaṃ vā dvīhatīhaṃ, cha vā pañca vā vācā chappañcavācā, evaṃ sattaṭṭhamāsā, ekayojanadviyojanāni.</p>\r\n<p class="bodytext"><a name="para8"></a><span class="paranum">8</span>. Disantarālattho yathā-pubbassā ca dakkhiṇassā ca disāya yadantarālaṃ sā pubbadakkhiṇā vidisā. Ettha –</p>\r\n<p class="subhead">69. Sabbādayo vuttimatteti</p>\r\n<p class="bodytext">Itthivācakā sabbādayo vuttimatte pumeva honti. Evaṃ pubbuttarā, aparadakkhiṇā, pacchimuttarā. Pubbā ca sā dakkhiṇā ceti vā.</p>','\r',char(13)),'\n',char(10)),'-6-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',158,replace(replace('<p class="bodytext"><a name="para9"></a><span class="paranum">9</span>. Byatihāralakkhaṇo <a name="M0.0158"></a> yathā – ‘‘tattha gahetvā tena paharitvā yuddhe sarūpaṃ’’ti suttena samāse kate kesesu ca kesesu ca gahetvā yuddhaṃ pavattaṃ kesākesī, daṇḍehi ca daṇḍehi ca paharitvā yuddhaṃ pavattaṃ daṇḍādaṇḍīti hoti. Ettha ca ‘‘cī vītihāre’’ti cīpaccaye ‘‘cismiṃ’’ti ākāro, evaṃ muṭṭhāmuṭṭhī.</p>\r\n<p class="bodytext">Sobhaṇo gandho sugandho, so assa atthīti sugandhīhi atthiatthe īpaccayena siddhaṃ. Yasmā ca bhaddāya kāpilāniyā apadāne ‘‘puno pattaṃ gahetvāna, sodhayitvā sugandhinā’’ti vuttaṃ, tasmā vuttiyaṃ ikārantassa abhāvadīpanatthaṃ ‘‘sugandhi duggandhīti payogā na dissatī’’ti vuttaṃ. Sugandhināti ekavacane rasso.</p>\r\n<p class="subhead">19. Catthe</p>\r\n<p class="bodytext">Anekaṃsyādyantaṃ catthe ekatthaṃ vā hoti. Samuccayo anvācayo itarītarayogo samāhāroti casaddassa attho catubbidho.</p>\r\n<p class="bodytext">Tattha samuccayā+nvācayesu samāso na hoti, kriyāsāpekkhatāya nāmānaṃ aññamaññaṃ ayuttatthattā, yathā-cīvaraṃ piṇḍapātañca paccayaṃ sayanāsanaṃ adāsi, dānañca dehi, sīlañcarakkhāhi. Itarītarayoge samāhāre ca aññamaññāpekkhattā samāso.</p>\r\n<p class="bodytext">Ubhayatthapadhāne catthe katha+mekatthībhāvo sambhave+ti ce, vuttañhi –</p>\r\n<p class="gatha1">Sappadhānāpi yattha+tthā, mitho sāpekkhatā iva;</p>\r\n<p class="gathalast">Kriyāsambandhasāmaññā, catthe+katthaṃ ta+duccateti.</p>','\r',char(13)),'\n',char(10)),'-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',159,replace(replace('<p class="bodytext">Yasmā <a name="M0.0159"></a> ekatthībhāvepi satīyasatīyatthe padhānaṃ, tasmā idaṃ vuccate –</p>\r\n<p class="gatha1">Na+ññamaññaṃ visesenti, catthe atthā padāniva;</p>\r\n<p class="gathalast">Satthavutyī ato tesaṃ, padhānatthaṃ+bhiyujjate.</p>\r\n<p class="gatha1">Itarītarayogo ca, samāhārotya+yaṃ dvidhā;</p>\r\n<p class="gathalast">Samāso tu imaṃ aññe, jānante dvandanāmato.</p>\r\n<p class="gatha1">Itarītarayogasmiṃ+vayavatthassa sambhavo;</p>\r\n<p class="gathalast">Samudāyatirobhāvo, paraṃva liṅga+massa ca.</p>\r\n<p class="gatha1">Samudāyabbhavo yasmiṃ+vayavā ca tirohitā;</p>\r\n<p class="gathalast">Samāhārotya+yaṃ cattho, so ca hoti napuṃsake.</p>\r\n<p class="bodytext">Itarītarayogo yathā-sāriputto ca moggallāno ca sāriputtamoggallānā, bho sāriputtamoggallānā iccādi. Avayavapadhānattā bahuvacanameva. Samaṇā ca brāhmaṇā ca samaṇabrāhmaṇā, evaṃ brāhmaṇagahapatikā, khattiyabrāhmaṇā, devamanussā, candimasūriyā.</p>\r\n<p class="subhead">64. Vijjāyonisambandhīna+mā tatra cattheti</p>\r\n<p class="bodytext">Vijjāsambandhīnaṃ yonisambandhīnañca catthe ā hotīti ukārassa ā, hotā ca potā ca hotāpotāro. Evaṃ mātāpitaro.</p>\r\n<p class="subhead">65. Putteti</p>\r\n<p class="bodytext">Putte uttarapade pitādīna+mā hoti catthe. Pitā ca putto ca pitāputtā, evaṃ mātāputtā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',160,replace(replace('<p class="subhead">78. Jāyāya jayaṃ patimhi</p>\r\n<p class="bodytext">Patimhi <a name="M0.0160"></a> pare jāyāya jayaṃ hoti. Jāyā ca pati ca jayampatayo. Jānipatīti pakatantarena siddhaṃ, jāni ca pati ca jānipati. Evaṃ jampati dampatīti.</p>\r\n<p class="bodytext">Kvaci appasaraṃ pubbaṃ nipatati, yathā-cando ca sūriyo ca candasūriyā, nigamā ca janapadā ca nigamajanapadā. Evaṃ surāsuragaruḍamanujabhujagagandhabbā.</p>\r\n<p class="bodytext">Kvaci ivaṇṇu+vaṇṇantānaṃ pubbanipāto, yathā-aggidhumā, gatibuddhibhujapaṭhaharakarasayā, dhātuliṅgāni.</p>\r\n<p class="bodytext">Kvaci sarādiakārantaṃ pubbaṃ nipatati, yathā-atthadhammā, atthasaddā, saddatthā vā.</p>\r\n<p class="bodytext">Aññamaññasāpekkhānameva tirohitāvayavabhedo samudāyapadhāno samāhāro, yathā-chattañca upāhanā ca chattupāhanaṃ.</p>\r\n<p class="subhead">20. Samāhāre napuṃsakanti</p>\r\n<p class="bodytext">Samāhāre sabbattha napuṃsakaliṅgaṃ bhavati, samāhārasse+kattā ekavacanameva.</p>\r\n<p class="subhead">23. Syādīsu rassoti</p>\r\n<p class="bodytext">Napuṃsake vattamānassa syādīsu rasso. Bho chattupāhana, chattupāhanaṃ, chattupāhanena iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',161,replace(replace('<p class="bodytext">Te <a name="M0.0161"></a> ca samāhāritarītarayogā bahulaṃvidhānā niyatavisayāyeva honti, tatrā+yaṃ visayavibhāgo-niruttipiṭakāgato-pāṇi+tūriya+yogga+senaṅgānaṃ, niccaverīnaṃ, saṅkhyāparimāṇasaññānaṃ, khuddajantukānaṃ, pacanacaṇḍālānaṃ, caraṇasādhāraṇānaṃ, ekajjhāyanapāvacanānaṃ, liṅgavisesānaṃ, vividhaviruddhānaṃ, disānaṃ, nadīnañca niccasamāhārekatthaṃ bhavati.</p>\r\n<p class="bodytext">Pāṇaṅgānaṃ-cakkhu ca sotañca cakkhusotaṃ, mukhañca nāsikā ca mukhanāsikaṃ, ‘‘syādīsu rasso’’ti napuṃsake vattamānassa rasso. Hanu ca gīvā ca hanugīvaṃ, kaṇṇā ca nāsā ca kaṇṇanāsaṃ, pāṇi ca pādo ca pāṇipādaṃ, chavi ca maṃsañca lohitañca chavimaṃsalohitaṃ, nāmañca rūpañca nāmarūpaṃ, jarā ca maraṇañca jarāmaraṇaṃ.</p>\r\n<p class="bodytext">Tūriyaṅgānaṃ-alaso ca tālambaro ca alasatālambaraṃ, murajo ca gomukho ca murajagomukhaṃ, saṃkho ca paṇavo ca deṇḍimo ca, saṃkhā ca paṇavā ca deṇḍimā cāti vā saṃkhapaṇavadeṇḍimaṃ, paṇavādayo dvepi bherivisesā, maddaviko ca pāṇaviko ca maddavikapāṇavikaṃ, gītañca vāditañca gītavāditaṃ, sammañca tāḷañca sammatāḷaṃ, sammaṃti kaṃsatālaṃ, tāḷaṃti hatthatāḷaṃ.</p>\r\n<p class="bodytext">Yoggaṅgānaṃ-phālo ca pācanañca phālapācanaṃ, yugañca naṅgalañca yuganaṅgalaṃ.</p>\r\n<p class="bodytext">Senaṅgānaṃ-hatthino ca assā ca hatthiassaṃ, rathā ca pattikā ca rathapattikaṃ, asi ca satti ca tomarañca piṇḍañca asisattitomarapiṇḍaṃ, asi ca cammañca asicammaṃ, cammanti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',162,replace(replace('<p class="bodytext">saravāraṇa-phalakaṃ<a name="M0.0162"></a>. Dhanu ca kalāpo ca dhanukalāpaṃ, kalāpo=tuṇīraṃ. Paharaṇañca āvaraṇañca paharaṇāvaraṇaṃ.</p>\r\n<p class="bodytext">Niccaverīnaṃ-ahi ca nakulo ca, ahī ca nakulā cāti vā ahinakulaṃ. Evaṃ biḷāramūsikaṃ, antassa rassattaṃ. Kākolukaṃ, sappamaṇḍūkaṃ, garuḷasappaṃ, nāgasupaṇṇaṃ.</p>\r\n<p class="bodytext">Saṅkhyāparimāṇasaññānaṃ-ekakañca dukañca ekakadukaṃ. Evaṃ, dukatikaṃ, tikacatukkaṃ, catukkapañcakaṃ. Dasakañca ekādasakañca dasekādasakaṃ, ‘‘titālīsa’’ iti caka-bhāgalopaniddesena kakārassa lopo.</p>\r\n<p class="bodytext">Khuddajantukānaṃ-kīṭā ca paṭaṅgā ca kīṭapaṭaṅgaṃ, kīṭā=kapālapiṭṭhikapāṇā. Evaṃ kunthakipillikaṃ, ḍaṃsā ca makasā ca ḍaṃsamakasaṃ, makkhikā ca kipillikā ca makkhikakipillikaṃ, kīṭā ca sariṃsapā ca kīṭasariṃsapaṃ. Tattha kunthā=sukhumakipillikā.</p>\r\n<p class="gatha1">Khuddajantu anaṭṭhī vā, atha kho khuddakopi vā;</p>\r\n<p class="gathalast">Sataṃ vā pasato yesaṃ, keci ānatulā iti.</p>\r\n<p class="bodytext">Pacanacaṇḍālānaṃ-orabbhikā ca sūkarikā ca orabbhikasūkarikaṃ, evaṃ sākuntikamāgavikaṃ. Sapāko ca caṇḍālo ca sapākacaṇḍālaṃ, pukkusachavaḍāhakaṃ, venarathakāraṃ, tattha venā=tacchakā, rathakārā=cammakārā.</p>\r\n<p class="bodytext">Caraṇasādhāraṇānaṃ-atiso ca bhāradvājo ca atisabhāradvājaṃ, kaṭṭho ca kapālo ca kaṭṭhakapālaṃ, sīlañca paññāṇañca sīlapaññāṇaṃ, samatho ca vipassanā ca samathavipassanaṃ, vijjā ca caraṇañca vijjācaraṇaṃ, evaṃ nāmarūpaṃ, hirottappaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',163,replace(replace('<p class="bodytext">satisampa-jaññaṃ<a name="M0.0163"></a>, lobhamohaṃ, dosamohaṃ, ahirikānottappaṃ, thinamiddhaṃ, uddhaccakukkucca+miccādi.</p>\r\n<p class="bodytext">Ekajjhāyanapāvacanānaṃ-dīgho ca majjhimo ca dīghamajjhimaṃ, evaṃ ekuttara saṃyuttakaṃ, khandhakavibhaṅgaṃ.</p>\r\n<p class="bodytext">Liṅgavisesānaṃ-itthī ca pumā ca itthipumaṃ, dāsī ca dāso ca dāsidāsaṃ, cīvarañca piṇḍapāto ca senāsanañca gilānapaccayabhesajjaparikkhāro ca cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhāraṃ, tiṇañca kaṭṭho ca sākhā ca palāsañca tiṇakaṭṭhasākhāpalāsaṃ. ‘‘Lābhī hoti cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānaṃ’’tipi dissati.</p>\r\n<p class="bodytext">Vividhaviruddhānaṃ-kusalañca akusalañca kusalākusalaṃ, evaṃ sāvajjānavajjaṃ, hīnapaṇītaṃ, kaṇhasukkaṃ, chekapāpakaṃ, sukhadukkhaṃ, adhamuttamaṃ, paṭighānunayaṃ, chāyātapaṃ, ālokandhakāraṃ. Rattiñca divā ca rattindivaṃ, ‘‘rattindivadāragavacaturassā’’ti apaccaye kate numaāgamo. Aho ca ratti ca ahorattaṃ, ‘‘dīghā+hovassekadesehi ca ratyā’’ti apaccaye kate ‘‘manādyāpādīna+mo maye ce’’ti okāro.</p>\r\n<p class="bodytext">Disānaṃ-pubbā ca aparā ca pubbāparaṃ, evaṃ puratthimapacchimaṃ, dakkhiṇuttaraṃ, pubbadakkhiṇaṃ, pubbuttaraṃ, aparadakkhiṇaṃ, aparuttaraṃ.</p>\r\n<p class="bodytext">Nadīnaṃ-gaṅgā ca yamunā ca gaṅgāyamunaṃ, evaṃ mahīsarabhū.</p>\r\n<p class="bodytext">Tiṇa+rukkha+pasu+sakuṇa+dhana+khañña+byañjana+janapadānaṃ vā. Tiṇavisesānaṃ-usīrāni ca bīraṇāni ca usīrabīraṇaṃ usīrabīraṇāni vā. Evaṃ muñjapabbajaṃ muñjapabbajāni vā, kāsakusaṃ kāsakusā vā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',164,replace(replace('<p class="bodytext">Rukkhavisesānaṃ-assatthā <a name="M0.0164"></a> ca kapiṭṭhā ca assatthakapiṭṭhaṃ assatthakapiṭṭhā vā, evaṃ ambapanasaṃ ambapanasā, khadirapalāsaṃ khadirapalāsā, dhavāssakaṇṇaṃ dhavāssakaṇṇā, pilakkhanigrodhaṃ pilakkhanigrodhā, sākasālaṃ sākasālā.</p>\r\n<p class="bodytext">Pasuvisesānaṃ-gajā ca gavajā ca gajagavajaṃ gajagavajā vā, ajā ca eḷakā ca ajeḷakaṃ ajeḷakā, hatthī ca gāvo ca assā ca vaḷavā ca hatthīgavassavaḷavaṃ hatthīgavassavaḷavā, rassattaṃ. Evaṃ gomahisaṃ gomahisā, eṇeyyavarāhaṃ eṇeyyavarāhā, sīhabyagghataracchaṃ sīhabyagghataracchā, kukkuṭasūkaraṃ kukkuṭasūkarā, eṇeyyagomahisaṃ eṇeyyagomahisā.</p>\r\n<p class="bodytext">Sakuṇavisesānaṃ-haṃsā ca bakā ca haṃsabakaṃ haṃsabakā. Evaṃ kāraṇḍavacakkavākaṃ kāraṇḍavacakkavākā, mayūrakoñcaṃ mayūrakoñcā, sukasālikaṃ sukasālikā, bakabalākaṃ bakabalākā.</p>\r\n<p class="bodytext">Dhanānaṃ-hiraññañca suvaṇṇañca hiraññasuvaṇṇaṃ hiraññasuvaṇṇāni. Evaṃ jātarūparajataṃ jātarūparajatāni, maṇisaṅkhamuttaveḷuriyaṃ maṇisaṅkhamuttaveḷuriyā.</p>\r\n<p class="bodytext">Dhaññānaṃ-sālī ca yavā ca sāliyavaṃ sāliyavā vā. Evaṃ tilamuggamāsaṃ tilamuggamāsāni, nippāvakulatthaṃ nippāvakulatthā.</p>\r\n<p class="bodytext">Byañjanānaṃ-sāko ca suvā ca sākasuvaṃ sākasuvā. Evaṃ gabyamāhisaṃ gabyamāhisā, eṇeyyavarāhaṃ eṇeya, varāhā. Migamayūraṃ migamayūrā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',165,replace(replace('<p class="bodytext">Janapadānaṃ-kāsi <a name="M0.0165"></a> ca kosalā ca kāsikosalaṃ kāsikosalā, vajjī ca mallā ca vajjimallaṃ vajjimallā, aṅgā ca magadhā ca aṅgamagadhaṃ aṅgamagadhā, evaṃ cetivaṃsaṃ cetivaṃsā, macchasūrasenaṃ macchasūrasenā, kurupañcālaṃ kurupañcālā. Nāmañca rūpañca nāmarūpañca nāmarūpanāmarūpanti caturekapañcavokāravasena vattabbe bahulādhikārā sarūpekasesaṃ katvā nāmarūpanti vuttaṃ.</p>\r\n<p class="bodytext">Etasmiṃ ekatthībhāvakaṇḍe yaṃ vuttaṃ pubbaṃ, tadeva pubbaṃ nipatati, kamātikkame payojanassā+bhāvā. Kvaci vipallāsopi hoti, bahulādhikārato, dantānaṃ rājā rājadanto. Catthe samāhāre ‘‘sabhāparisāyā’’ti ñāpakā kvaci napuṃsakaliṅgaṃ na bhavati, ādhipaccañca parivāro ca ādhipaccaparivāro. Evaṃ chandapārisuddhi, paṭisandhipavattiyaṃ.</p>\r\n<p class="subhead">40. Samāsantva</p>\r\n<p class="bodytext">Upari aya+madhikarīyati.</p>\r\n<p class="subhead">41. Pāpādīhi bhūmiyā</p>\r\n<p class="bodytext">Pāpādīhi parā yā bhūmi, tassā samāsanto a hoti. Pāpā bhūmi yasminti pāpabhūmaṃ, jātiyā upalakkhitā bhūmi jātibhūmaṃ.</p>\r\n<p class="subhead">42. Saṃkhyāhiti</p>\r\n<p class="bodytext">Saṃkhyāhi parāya bhūmiyā a hoti. Dve bhūmiyo assa dvibhūmaṃ. Evaṃ tibhūmaṃ.</p>\r\n<p class="subhead">43. Nadīgodhāvarīnaṃ</p>\r\n<p class="bodytext">Saṃkhyāhi parāsaṃ nadīgodhāvarīnaṃ samāsanto a hoti. Pañcannaṃ nadīnaṃ samāhāro pañcanadaṃ. Evaṃ sattagodhāvaraṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',166,replace(replace('<p class="subhead">44. Asaṃkhyehi cā+ṅgulyā+nāññāsaṃkhyatthesuti</p>\r\n<p class="bodytext">Asaṃkhyehi <a name="M0.0166"></a> saṃkhyāhi ca parāya aṅguliyā samāsanto a hoti. Niggata+maṅgulīhi niraṅgulaṃ, dve aṅguliyo samāhaṭā dvaṅgulaṃ. Anaññāsaṃkhyatthesūti kiṃ, pañca aṅguliyo asmiṃ hattheti pañcaṅguli, aṅguliyā samīpaṃ upaṅguli.</p>\r\n<p class="subhead">45. Dīghāhovassekadesehi ca rattyāti</p>\r\n<p class="bodytext">Dīghādīhi parāya rattiyā a hoti. Dīghā ca sā ratti cāti dīgharattaṃ. Aho ca ratti ca ahorattaṃ, āpādittā o. Vassāsu ratti vassārattaṃ. Pubbā ca sā ratti cāti pubbarattaṃ. Evaṃ apararattaṃ, aḍḍharattaṃ, atikkanto rattiṃ atiratto. Dve rattiyo samāhaṭā dvirattaṃ. Anaññāsaṃkhyatthesu tveva, dīghā ratti asminti dīgharatti, hemanto. Rattiyā samīpaṃ uparatti. Bahulaṃvidhānā kvaci hoteva, rattiparimāṇānurūpaṃ yathārattaṃ.</p>\r\n<p class="subhead">46. Gotva+catthe cā+lope</p>\r\n<p class="bodytext">Gosaddā alopavisaye samāsanto a hoti na ce catthādīsu samāso. Rañño go rājagavo. Paramo ca so go cāti paramagavo. Pañcannaṃ gunnaṃ samāhāro pañcagavaṃ, ‘‘gossā+vaṅa’’ti avaṅa. Taṃ dhana+massāti pañcagavadhano. Dasagavaṃ.</p>\r\n<p class="subhead">47. Rattindiva+dāragava+caturassā</p>\r\n<p class="bodytext">Ete saddā a-antā nipaccante. Ratti ca divā ca rattindivaṃ, imināva numāga mā. Dārā ca gavo ca dāragavaṃ. Catasso assiyo assa caturasso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',167,replace(replace('<p class="subhead">48. Āyāme+nugavaṃ</p>\r\n<p class="bodytext">Anugavaṃti <a name="M0.0167"></a> nipaccate āyāmegamyamāne. Gavassa āyāmo anugavaṃ sakaṭaṃ, asaṅkhyasamāso.</p>\r\n<p class="subhead">49. Akkhismā+ññatthe</p>\r\n<p class="bodytext">Akkhismā samāsanto a hoti aññatthe. Visālāni akkhīni yassa so visālakkho.</p>\r\n<p class="subhead">50. Dārumya+ṅgulyā</p>\r\n<p class="bodytext">Aṅgulantā aññatthe dārumhi samāsanto a hoti. Dve aṅguliyo avayavā asseti dvaṅgulaṃ dāru, evaṃ pañcaṅgulaṃ. Aṅgulisadisāvayavaṃ dhaññādīnaṃ vikkhepakaṃ dārūti vuccate.</p>\r\n<p class="subhead">54. Uttarapade</p>\r\n<p class="bodytext">Idaṃ sabbattha adhikātabbaṃ.</p>\r\n<p class="subhead">55. Imassi+danti</p>\r\n<p class="bodytext">Uttarapade imassa idaṃ, imāya attho idamaṭṭho, thassa ṭho, idamaṭṭho assa atthīti idamaṭṭhī, idamaṭṭhino bhāvo idamaṭṭhitā. Imesaṃ paccayā idappaccayā, niggahītalopo passa ca dvibhāvo.</p>\r\n<p class="subhead">57. Ṭa ntantūnanti</p>\r\n<p class="bodytext">Ntantūnaṃ uttarapade ṭa hoti. Bhavanto patiṭṭhā amhanti bhavaṃpatiṭṭhā mayaṃ, niggahītāgamo, vagganto, yossa ṭā ca. Bhagavā mūlaṃ etesaṃti bhagavaṃmūlakā no dhammā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',168,replace(replace('<p class="subhead">58. A</p>\r\n<p class="bodytext">Iti <a name="M0.0168"></a> ntantūnaṃ a hoti. Guṇavanto patiṭṭhā mamāti guṇavantapatiṭṭho+smi.</p>\r\n<p class="subhead">60. Parassa saṃkhyāsuti</p>\r\n<p class="bodytext">Saṃkhyāsu parassa o, paro satasmā adhikā parosataṃ.</p>\r\n<p class="subhead">61. Jane puthassuti</p>\r\n<p class="bodytext">Puthassa jane u hoti. Puthagevā+yaṃ janoti puthujjano, jassa dvittaṃ.</p>\r\n<p class="subhead">62. So chassā+hā+yatane vā</p>\r\n<p class="bodytext">Ahe āyatane ca uttarapade chassa so hoti vā. Sāhaṃ chāhaṃ, saḷāyatanaṃ chaḷāyatanaṃ. (Sambaraṃ)</p>\r\n<p class="subhead">63. Ltu+pitādīna+māravaraṅa</p>\r\n<p class="bodytext">Ltupaccayantānaṃ pitādīnañca yathākkama+māravaraṅa vā hontu+ttarapade. Satthuno dassanaṃ satthāradassanaṃ, kattāraniddeso. Mātarapitaro. Vātveva, satthudassanaṃ, mātujāyo.</p>\r\n<p class="subhead">68. Kvaci paccayeti</p>\r\n<p class="bodytext">Paccaye pumabhāve atisayena byattā byattatarā, byattatamā. ‘‘Tassaṃ tatra, tāya tato, tassaṃ velāyaṃ tadā’’<span class="note">[‘‘… etthantare rūpāni ācariyasaṃgharakkhita mahāsāmittheramatena imināva ‘‘kvaci paccayeti suttena siddhāni, teneva tāni imasmiṃ sutte udāhaṭāni. ācariyamoggallānamahātheramatena pane+tāni ‘‘sabbādayo vuttimatteti suttena siddhāni. toādīnaṃ vibhatyatthe vihitapaccayattā, tadanthānañca ṇādivuttittā dvinnampi therānaṃ matā aviruddhā.]</span></p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',169,replace(replace('<p class="subhead">71. Saññāya+mudo+dakassa</p>\r\n<p class="bodytext">Saññāya+mudakassu+ttarapade <a name="M0.0169"></a> udādeso hoti. Udadhi, udapānaṃ.</p>\r\n<p class="subhead">72. Kumbhādīsu vā</p>\r\n<p class="bodytext">Kumbhādīsu+ttarapadesu udakassa udādeso vā hoti. Udakumbho, udakakumbho. Udapatto udakapatto. Udabindu udakabindu. Ākatigaṇo+yaṃ.</p>\r\n<p class="subhead">73. Sotādīsū+lopo</p>\r\n<p class="bodytext">Sotādīsu+ttarapadesu udakassa ussa lopo hoti. Dakasotaṃ, dakarakkhaso.</p>\r\n<p class="subhead">26. Itthiya+matvā</p>\r\n<p class="bodytext">Itthiyaṃ vattamānato akārantato nāmasmā āpaccayo hoti. Dhammadinnā.</p>\r\n<p class="subhead">27. Nadādito ṅī</p>\r\n<p class="bodytext">Nadādīhi itthiyaṃ vīpaccayo hoti. Nadī mahī kumārī taruṇī vāruṇī gotamī.</p>\r\n<p class="bodytext">Goto vā</p>\r\n<p class="bodytext">Gāvī go. Ākatigaṇo+yaṃ. Va-kāro ‘‘ntantūnaṃ vīmhi to vā’’ti visesanattho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',170,replace(replace('<p class="subhead">28. Yakkhāditvi+nī ca</p>\r\n<p class="bodytext">Yakkhādito <a name="M0.0170"></a> itthiyaṃ inī hoti vī ca. Yakkhinī yakkhī, nāginī nāgī, sīhinī sīhī.</p>\r\n<p class="subhead">29. Ārāmikādīhi</p>\r\n<p class="bodytext">Ārāmikādito inī hoti+tthiyaṃ. Ārāmikinī, anantarāyikinī.</p>\r\n<p class="bodytext">Saññāyaṃ mānuso mānusinī, aññatra mānusī.</p>\r\n<p class="subhead">30. Yuvaṇṇehi nī</p>\r\n<p class="bodytext">Itthiya+mivaṇṇuvaṇṇantehi nī hoti bahulaṃ. Sadāpayatapāṇinī, daṇḍinī, bhikkhunī, khattabandhunī, paracittavidunī. Mātuādito kasmā na hoti, itthipaccayaṃ vināpi itthattābhidhānato.</p>\r\n<p class="subhead">31. Ttimhā+ññatthe</p>\r\n<p class="bodytext">Ttimhā+ññattheyeva itthiyaṃ nī hoti bahulaṃ. Sā+haṃ ahiṃsāratinī, tassā muṭṭhassatiniyā, sā gāvī vacchagiddhinī. Aññattheti kiṃ, dhammarati.</p>\r\n<p class="subhead">32. Gharaṇyādayoti</p>\r\n<p class="bodytext">Gharaṇipabhutayo nīpaccayantā sādhavo honti. Ghara+massā atthīti īmhi ‘‘yuvaṇṇehi nī’’ti nī, gharaṇī. Iminā nassa ṇo, īssa attañca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',171,replace(replace('<p class="bodytext">Ācariyā <a name="M0.0171"></a> vā yalopo ca iti gaṇasuttena niyāmitattā imināva nīmhi yalopo ca, ācarinī ācariyā.</p>\r\n<p class="subhead">33. Mātulāditvā+nī bhariyāyanti</p>\r\n<p class="bodytext">Mātulādito bhariyāya+mānī hoti. Mātulānī, varuṇānī, gahapatānī, ācariyānī.</p>\r\n<p class="bodytext">Abhariyāyaṃ khattiyā vā iti gaṇasuttena niyamitattā iminā vā ānī, khattiyānī. Nadādipāṭhā bhariyāyantu ī, khattiyī.</p>\r\n<p class="subhead">34. Upamā+saṃhita+sahita+saññata+saha+sapha+vāma+lakkhaṇāditū+rutūti</p>\r\n<p class="bodytext">Ūrusaddato itthiya+mū hoti. Karabho viya ūru yassā sā karabhorū, saṃhito ūru assāti saṃhitorū, evaṃ sahitorū, saññatorū, sahorū, saphorū, vāmorū, lakkhaṇorū. Ūti yogavibhāgā brahmabandhū.</p>\r\n<p class="subhead">35. Yuvāti</p>\r\n<p class="bodytext">Yuvasaddatoti hoti+tthiyaṃ. Yuvati.</p>\r\n<p class="subhead">36. Ntantūnaṃ vīmhi to vāti</p>\r\n<p class="bodytext">Vīmhi ntantūnaṃ to vā hoti. Gacchatī gacchantī, sīlavata sīlavantī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',172,replace(replace('<p class="subhead">37. Bhavato bhototi</p>\r\n<p class="bodytext">Vīmhi <a name="M0.0172"></a> bhavato bhotādeso vā hoti. Bhotī bhavantī.</p>\r\n<p class="subhead">39. Puthussa pathavaputhavāti</p>\r\n<p class="bodytext">Vīmhi puthussa pathavaputhavā honti. Pathavī puthavī, ṭhe pathavī.</p>\r\n<p class="bodytext">Iti payogasiddhiyaṃ samāsakaṇḍo catuttho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',173,replace(replace('<p class="chapter">5. Ṇādikaṇḍa</p>\r\n<p class="gatha1">Samāso <a name="M0.0173"></a> padasaṃkhepo, padapaccayasaṃhitaṃ;</p>\r\n<p class="gathalast">Taddhitaṃ nāma hotīti, viññeyyaṃ tesa+mantaraṃ.</p>\r\n<p class="subhead">1. Ṇo vā+pacce</p>\r\n<p class="bodytext">Chaṭṭhīyantā nāmasmā vā ṇapaccayo hoti apacce+bhidheyye. Apaccasaddasambandhittena apaccavantato katasarapaccayo samatthyato chaṭṭhyantato hotīti ‘‘chaṭṭhīyantā nāmasmā’’ti sutte avuttampi vuttaṃ. Ṇādīnaṃ taddhitanti pubbācariyasaññā. Ṇakāro vuddhyattho. Eva+maññatrāpi. Ṇādivuttittā ‘‘ekatthatāyaṃ’’ti vibhattilopo.</p>\r\n<p class="subhead">124. Sarāna+mādissā+yuvaṇṇassā+eo ṇānubandhe</p>\r\n<p class="bodytext">Sarāna+mādibhūtā ye akāri+vaṇṇu+vaṇṇā, tesaṃ ā+e+o vā honti yathākkamaṃ ṇānubandheti akārassa ākāro. Ā+e+onaṃ vuddhītipi pubbācariyasaññā. Vasiṭṭhassā+paccaṃ vāsiṭṭho. Vīmhi vāsiṭṭhī. Veti vasiṭṭhassā+paccaṃti vākyassa ca vasiṭṭhāpaccaṃti samāsassa ca vikappatthaṃ. So ca vāsaddo yāva ‘‘sakatthe’’ti adhikarīyati.</p>\r\n<p class="gatha1">Napuṃsakena liṅgena, saddo+dāhu pumena vā;</p>\r\n<p class="gathalast">Niddissatīti ñātabba+mavisese pani+cchiteti –</p>\r\n<p class="bodytext">Vuttatthā apaccasaddassa napuṃsakatthepi puttaputtīnaṃ dvinnampi vācako hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',174,replace(replace('<p class="gatha1">Ṇādayo+bhidheyyaliṅgā<a name="M0.0174"></a>, apacce tva+napuṃsakā;</p>\r\n<p class="gatha2">Napuṃsake sakatthe ṇyo, bhiyyo bhāvasamūhajā;</p>\r\n<p class="gathalast">Tā tu+tthiya+masaṃkhyāne, tvādicīpaccayantakā.</p>\r\n<p class="bodytext">Bhāradvājassa apaccaṃ=putto bhāradvājo, evaṃ vessāmitto, gotamo. Ettha ca a+yuvaṇṇantābhāvā ā+e+onaṃ na vuddhi. Vāsudevassa apaccaṃ vāsudevo, baladevo. Cittakotiādīsu pana saṃyogantattā ‘‘saṃyoge kvacī’’ti kvaci na vuddhi. Upaguno apaccaṃ opagavo opagavī. Ettha ‘‘uvaṇṇassā+vaṅa sare’’ti ukārassa avaṅa.</p>\r\n<p class="bodytext">Vacchassā+paccanti viggayha pubbasuttena ṇapaccaye kate puna vacchassā+paccaṃti viggaho.</p>\r\n<p class="subhead">2. Vacchādito ṇāna+ṇāyanā</p>\r\n<p class="bodytext">Vacchādīhi apaccapaccayantehi gottādīhi ca saddehi ṇāna+ṇāyanapaccayā vā honti apacce. Vacchāno, vacchāyano, ‘‘saṃyoge kvacī’’ti na vuddhi. Katissā+paccaṃ kacco, ‘‘ṇya diccādīhī’’ti ṇyo, ‘‘lopo+vaṇṇi+vaṇṇānaṃ’’ti akāralope cavaggapubbarūpāni. Kaccassā+paccaṃ kaccāno, kaccāyano, yāgame kātiyāno. Muggassā+paccaṃ=nattādīti vākye vuddhi+ikāralopa+pubbarūpāni. Moggallassā+paccaṃti punaṇāna+ṇāyanā honti, moggallāno moggallāyano. Ime cattāro apaccapaccayantā. Gottādito yathāsakaṭassā+paccaṃ nattādīhi sakaṭāno sakaṭāyano. Kaṇhassā+paccaṃ nattādi kaṇhāno kaṇhāyano. Evaṃ aggivessāno </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',175,replace(replace('<p class="bodytext">aggivessāyano<a name="M0.0175"></a>, muñjāno muñjāyano, kuñjāno kuñjāyano. Sabbattha saṃyogattā na vuddhi. Ākatigaṇo+yaṃ.</p>\r\n<p class="subhead">3. Kattikāvidhavādīhi ṇeyyaṇerā</p>\r\n<p class="bodytext">Kattikādīhi vidhavādīhi ca ṇeyyaṇerappaccayā honti vā yathākkamaṃ. Kattikāya apaccaṃ kattikeyyo, garuḷo vinatāya=supaṇṇamātuyā apaccaṃ venateyyo. Rohiṇiyā apaccaṃ rohiṇeyyo, gaṅgāya apaccaṃ gaṅgeyyo. Evaṃ bhāgineyyo, nādeyyo, anteyyo, āheyyo, kāpeyyo, suciyā apaccaṃ soceyyo, bāleyyo. Ṇere-vidhavāya apaccaṃ vedhavero. Bandhakiyā=abhisārikāya apaccaṃ bandha-kero. Samaṇassa upajjhāyassā+paccaṃ sāmaṇero, nāḷikero iccādi.</p>\r\n<p class="subhead">4. Ṇya diccādīhi</p>\r\n<p class="bodytext">Ditippabhutīhi ṇyo hoti apacce.</p>\r\n<p class="subhead">125. Saṃyoge kvaci</p>\r\n<p class="bodytext">Saṃyogavisaye kvaci ā+e+ovuddhiyo honti ṇānubandhe.</p>\r\n<p class="subhead">131. Lopo+vaṇṇi+vaṇṇānaṃ</p>\r\n<p class="bodytext">Avaṇṇi+vaṇṇānaṃ lopo hoti yakārādo paccaye. Ditiyā=asuramātuyā apaccaṃ decco ādicco. Kuṇḍaniyā apaccaṃ koṇḍañño, nassa ññe pubbarūpaṃ. Aditīti devamātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',176,replace(replace('<p class="bodytext">Bhātuno <a name="M0.0176"></a> apaccaṃ bhātabyo, ‘‘yamhi gossa cā’’ti yamhi ussa avaṅa akāralopapubbarūpāni.</p>\r\n<p class="subhead">5. Ā ṇi</p>\r\n<p class="bodytext">Akārantato ṇi vā hota+pacce bahulaṃ. Ā=ti nāmavisesanattā a-kārantatoti vuttaṃ. Dakkhassā+paccaṃ dakkhi, datti, doṇi, vāsavi, vāruṇi. Jinadattassā+paccaṃ jenadatti, suddhodani, ānuruddhi iccādi.</p>\r\n<p class="subhead">6. Rājato ñño jātiyaṃ</p>\r\n<p class="bodytext">Rājato ñño vā hota+pacce khatthiyajātiyaṃ gamyamānāyaṃ. Rañño apaccaṃ rājañño. Jātīyanti kiṃ, rājāpaccaṃ.</p>\r\n<p class="subhead">7. Khattā yi+yā</p>\r\n<p class="bodytext">Tijātiyaṃ apacce ya+iyā honti. Khattassā+paccaṃ khatyo khattiyo. Jātiyaṃ tveva, khatti.</p>\r\n<p class="subhead">8. Manuto ssa+saṇa</p>\r\n<p class="bodytext">Manuto jātisamudāye ssa+saṇa hontu+pacce. Manuno apaccaṃ manusso mānuso, manussī mānusī. Jātiyaṃ tveva, mānavo, nassa ṇo, māṇavo.</p>\r\n<p class="subhead">9. Janapadanāmasmā khattiyā raññe ca ṇo</p>\r\n<p class="bodytext">Janapadassa yaṃ nāmaṃ, taṃ nāmasmā khattiyā apacce raññe ca ṇo hoti. Pañcālānaṃ apaccaṃ rājā vā pañcālo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',177,replace(replace('<p class="bodytext">kosalo<a name="M0.0177"></a>, māgadho, okkāko. Janapadanāmasmāti kiṃ, dāsarathi. Khattiyāti kiṃ, pañcālassa brāhmaṇassā+paccaṃ pañcāli.</p>\r\n<p class="subhead">10. Ṇya kurusivīhi</p>\r\n<p class="bodytext">Kurusivīhi apacce raññe ca ṇyo hoti. Kurūnaṃ apaccaṃ rājā vā korabyo. ‘‘Yamhi gossa cā’’timinā avaṅa, bakārapubbarūpāni. Sebbo, ilopo. (Apaccataddhitaṃ).</p>\r\n<p class="subhead">11. Ṇa rāgā tena rattaṃ</p>\r\n<p class="bodytext">Rāgavācītatiyantato ratta+micce+tasmiṃ atte ṇo hoti. Kusumbhādīhi vaṇṇantarapattaṃ rattaṃ nāma. Kasāvena rattaṃ kāsāvaṃ. Evaṃ kusumbhena rattaṃ kosumbhaṃ, hāliddaṃ, pattaṅgaṃ, mañjeṭṭhaṃ, kuṅkumaṃ. Idha na hoti nīlaṃ pītanti, guṇavacanattā ṇena vināpi dabbassā+bhidhānato.</p>\r\n<p class="subhead">12. Nakkhatteni+nduyuttena kāle</p>\r\n<p class="bodytext">Tatiyantato nakkhattā tena lakkhite kāle ṇo hoti, taṃ ce nakkhatta+minduyuttaṃ hoti. Phussena induyuttena lakkhitā puṇṇamāsī phussī ratti, phusso aho, maghāya induyuttāya lakkhitā puṇṇamāsī māghī, māgho.</p>\r\n<p class="subhead">13. Sā+ssa devatā puṇṇamāsī</p>\r\n<p class="bodytext">Seti paṭhamantā asseti chaṭṭhiyatthe ṇo hoti, yaṃ paṭhamantaṃ, sā ce devatā puṇṇamāsī vā. Sugato devatā assa sogato, māhindo, yāmo, vāruṇo. Buddho assa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',178,replace(replace('<p class="bodytext">devatāti <a name="M0.0178"></a> buddho. Phussī puṇṇamāsī assa sambandhinīti phusso māso. Evaṃ māgho, phagguno, citto, vesākho, jeṭṭhamūlo, āsāḷho, sāvaṇo, poṭṭhapādo, assayujo, kattiko, māgasiro. Puṇṇamāsī ca bhatakamāsasambandhinī na hoti, puṇṇo mā assanti nibbacanā, ato eva nipātanā ṇo, sāgamo ca.</p>\r\n<p class="subhead">14. Ta+madhīte taṃ jānāti ka+ṇikā ca</p>\r\n<p class="bodytext">Dutiyantato ta+madhīte taṃ jānātīti etesva+tthesu ṇo hoti ko ṇiko ca. Ettha casaddo ko caṇiko ca hotīti samuccino, no ṇapaccayaṃ. Byākaraṇa+madhīte jānātīti vā veyyākaraṇo, vi+ā+karaṇanti vicchijja katayādesassi+kārassa ‘‘tadādesā tadīva bhavantī’’ti ñāyā ‘‘sarāna’’miccādinā ekāre yāgamadvittāni. Evaṃ mohutto, nemitto, aṅgavijjo, vatthuvijjo, chandaso, ‘‘manā, dīnaṃ saka’’ iti sakāgamo. Kamako, padako, venayiko, suttantiko, ābhidhammiko. Dvitaggahaṇaṃ ajjhenajānane ca visuṃ visuṃ paccayavidhānatthaṃ, ajjhenavisayadassanatthaṃ, pasiddhupasaṃharaṇatthañca.</p>\r\n<p class="subhead">15. Tassa visaye dese</p>\r\n<p class="bodytext">Chaṭṭhiyantā visaye desasarūpe ṇo hoti. Vasātīnaṃ visayo deso vāsāto. Deseti kiṃ, cakkhussa visayo rūpaṃ, devadattassa visayo+nuvāko.</p>\r\n<p class="subhead">16. Nivāse tannāme</p>\r\n<p class="bodytext">Ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',179,replace(replace('<p class="bodytext">tannāme <a name="M0.0179"></a> nivāse dese ṇo hoti. Sivīnaṃ nivāso deso sebbo. Ettha ‘‘yavā sare’’ti yakāre bakārapubbarūpāni. Vāsāto.</p>\r\n<p class="subhead">17. Adūrabhaveti</p>\r\n<p class="bodytext">Ṇo, vidisāya adūrabhavaṃ vedisaṃ.</p>\r\n<p class="subhead">18. Tena nibbatteti</p>\r\n<p class="bodytext">Tatiyantā nibbattatthe ṇo hoti. Kusambena nibbattā kosambī nagaraṃ, evaṃ kākandī, mākandī, sahassena nibbattā sāhassī parikhā, hetumhi kattari karaṇe ca yathāyogaṃ tatiyā.</p>\r\n<p class="subhead">19. Ta+midha+tthi</p>\r\n<p class="bodytext">Tanti paṭhamantā idhāti sattamyatthe dese tannāme ṇo hoti, yaṃ taṃ paṭhamanta+matthi ce. Udumbarā asmiṃ dese santīti odumbaro, bādaro, pabbajo.</p>\r\n<p class="subhead">20. Tatra bhaveti</p>\r\n<p class="bodytext">Sattamyantā bhavatthe ṇo. Udake bhavo odako, oraso, jānapado, māgadho, kāpilavatthavo, kosambo, manasi bhavaṃ mānasaṃ sukhaṃ, sakāgamo. Sāraso sakuṇo, sārasī sakuṇī, sārasaṃ pupphaṃ. Mitte bhavā mettā mettī vā. Pure bhavā porī vācā. Pāvuse bhavo pāvuso megho. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',180,replace(replace('<p class="bodytext">Pāvusā <a name="M0.0180"></a> ratti, pāvusaṃ abbhaṃ. Sārado, sāradā, sāradaṃ pupphaṃ. Mādhuro jano, mādhurā gaṇikā, mādhuraṃ vatthaṃ.</p>\r\n<p class="subhead">21. Ajjādīhi tanoti</p>\r\n<p class="bodytext">Bhavatthe tano. Ajja bhavo ajjatano, svātano, hiyyatano. ‘‘Eona+ma vaṇṇe’’ti eonaṃ a hoti.</p>\r\n<p class="subhead">22. Purāto ṇo cati</p>\r\n<p class="bodytext">Bhavatthe ṇo tano ca. Ettha ṇakāro avayavo, nevā+nubandho. Purāṇo, purātano.</p>\r\n<p class="subhead">23. Amātva+ccoti</p>\r\n<p class="bodytext">Bhavatthe acco hoti. Amā=saha bhavo amacco.</p>\r\n<p class="subhead">24. Majjhāditvi+moti</p>\r\n<p class="bodytext">Bhavatthe imo, majjhe bhavo majjhimo. Evaṃ antimo, heṭṭhimo, uparimo, orimo, pārimo, pacchimo, abbhantaridho, paccantimo.</p>\r\n<p class="subhead">25. Kaṇa+ṇeyya+ṇeyyaka+yi+yāti</p>\r\n<p class="bodytext">Bhavatthe kaṇaādayo honti. Kaṇa-kusinārāyaṃ bhavo kosinārako, māgadhako, āraññako vihāro, rājagahako, kosambako, indapattako, kāpilako, bhārukacchako, nāgarako. Aṅgesu jāto aṅgako, kosalako, vedehako, kambojako, gandhārako, sovīrako, sindhavako, assako iccādi. Ṇeyya-gaṅgeyyo, pabbateyyo, vāneyyo. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',181,replace(replace('<p class="bodytext">Ṇeyyaka-kosaleyyako<a name="M0.0181"></a>, bārāṇaseyyako, campeyyako, silāya jātaṃ seleyyakaṃ, mithileyyako. Bārāṇaseyyakotyādīsu ‘‘dissanta+ññepi paccayā’’ti eyyako, evaṃ upari sutte dassitapaccayato visuṃ paccaye dassite iminā suttenāti daṭṭhabbaṃ. Ya-gammo, yamhi akāralope pubbarūpaṃ rasso ca. Dibbo. Iya-udariyo, diviyo, pañcāliyo, bodhipakkhiyo, lokiyo.</p>\r\n<p class="subhead">26. Ṇiko</p>\r\n<p class="bodytext">Sattamyantā bhavatthe ṇiko hoti. Sarade bhavo sāradiko divaso, sāradikā ratti.</p>\r\n<p class="subhead">27. Ta+massa sippaṃ sīlaṃ paṇyaṃ paharaṇaṃ payojanaṃ</p>\r\n<p class="bodytext">Paṭhamantā sippādivācakā asseti chaṭṭhyatthe ṇiko hoti. Vīṇāvādanaṃ sippa+massa veṇiko, vīṇāvādanaṃ abhedopacārena vīṇā nāma. Modiṅgiko, vaṃsiko. Paṃsukūladhāraṇaṃ sīla+massa paṃsukūliko, tecīvariko. Gandho paṇya+massa gandhiko, teliko, goḷiko, pūviko, paṇṇiko, tambuliko, loṇiko. Cāpopaharaṇa+massa cāpiko, tomariko, muggariko, mosaliko. Upadhi=kkhandhādi payojana+massa opadhikaṃ, sātikaṃ, sāhassikaṃ.</p>\r\n<p class="subhead">28. Taṃ hanta+rahati gacchatu+ñchati+carati</p>\r\n<p class="bodytext">Dutiyantā hantīti evamādīsva+tthesu ṇiko hoti. Pakkhino hantīti pakkhiko, sākuṇiko, māyūriko, macche hantīti macchiko, meniko. Mige hantīti māgaviko, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',182,replace(replace('<p class="bodytext">vakārāgamo<a name="M0.0182"></a>. Migassa ‘‘tadaminā’’dinā magavādesepi māgaviko. Orabbhiko, hāriṇiko. Sūkarikoti iko. Sata+marahatīti sātikaṃ, sandiṭṭhikaṃ, ehi passa vidhiṃ arahatīti ehipassiko. Ettha tyādyantasamudāyato anukaraṇattā vā taddhitassa abhidhānalakkhaṇattā vā bahulaṃvidhānena vā paccayo. Sāhassiko, kumbhiko, doṇiko, addhamāsiko, kahāpaṇiko, āsītikā gāthā, nāvutikā. Sahassiyoti iyo. Paradāraṃ gacchatīti pāradāriko, maggiko, paññāsayojaniko, pathiko. Badare uñchatīti bādariko, sāmākiko. Dhammaṃ caratīti dhammiko, adhammiko.</p>\r\n<p class="subhead">29. Tena kataṃ kītaṃ baddha+mabhisaṅkhataṃ saṃsaṭṭhaṃ hataṃ hanti jitaṃ jayati dibbati khanati tarati carati vahati jīvatiti</p>\r\n<p class="bodytext">Katādīsva+tthesu ṇiko. Kāyena kataṃ kāyikaṃ, vācasikaṃ, mānasikaṃti sakāgamo. Vātena kato ābādho vātito, semhiko, pittiko. Satena kītaṃ sātikaṃ, sāhassikaṃ, vatthena kītaṃ vatthikaṃ, kumbhikaṃ, sovaṇṇikaṃ, ghātikaṃ. Mūlatova paccayo, amūlavācittā devadattena kītoti na hoti, tadatthāppatītiyā. Varatthāya baddho vārattiko, āyasiko, pāsiko, suttiko. Ghatena abhisaṅkhataṃ saṃsaṭṭhaṃ vā ghātikaṃ, goḷikaṃ, dadhikaṃ, mārīcikaṃ. Jālena hato hantīti vā jāliko, bālisiko. Akkhehi jitaṃ akkhikaṃ dhanaṃ, sālākikaṃ, tindukiko, ambaphaliko. Akkhehi jayati dibbati vā akkhiko. Khaṇittiyā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',183,replace(replace('<p class="bodytext">khanatīti <a name="M0.0183"></a> khāṇittiko, kuddāliko. Devadattena jitaṃ, aṅgulyā khanatīti na hoti, tadatthānavagamā. Uḷumpena taratīti oḷumpiko, uḷumpikoti iko. Kulliko, gopucchiko, nāviko. Sakaṭena caratīti sākaṭiko, rathiko. Parappikoti iko. Khandhena vahatīti khandhiko. Aṃsiko, sīsikoti iko. Vetanena jīvatīti vetaniko, bhatiko, kayiko, vikkayiko, kayavikkayikoti iko.</p>\r\n<p class="subhead">30. Tassa saṃvattati</p>\r\n<p class="bodytext">Catutthyantā saṃvattatīti asmiṃ attheṇiko hoti. Punabbhavāya saṃvattatīti ponobbhaviko, itthiyaṃ ponobbhavikā. Lokāya saṃvattatīti lokiko. Suṭṭhu aggoti saggo, saggāya saṃvattatīti sovaggiko, sasso+vaka tadaminādīpāṭhā. Dhanāya saṃvattatīti dhaññaṃ.</p>\r\n<p class="subhead">31. Tato sambhūta+māgataṃ</p>\r\n<p class="bodytext">Pañcamyantā sambhūta+māgatanti etesva+tthesu ṇiko hoti. Mātito sambhūta+māgataṃvā+ti ettha ‘‘mātito ca bhaginiyaṃccho’’ti ‘‘mātito’’ti bhāgena ussa imhi vākyaṃ, rassadvittesu mattikaṃ, pettikaṃ. Ṇya+riyaṇa+ryapaccayāpi dissanti. Surabhito sambhūtaṃ sorabhyaṃ. Yamhi thaññaṃ. Ubhayattha ‘‘lopo+vaṇṇi+vaṇṇānaṃ’’ti lopo. Riyaṇa-pituto sambhūto pettiyo, mātiyo, ‘‘rānubandhe+ntasarādissā’’ti ulopo, mattiyo. Ryamhi-ulopo, cavaggapubbarūpāni, macco vā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',184,replace(replace('<p class="subhead">32. Tattha vasati vidito bhatto niyutto</p>\r\n<p class="bodytext">Sattamyantā <a name="M0.0184"></a> vasatītvevamādīsva+tthesu ṇiko hoti. Rukkhamūle vasatīti rukkhamūliko, āraññiko, rājagahiko, māgadhiko, sosāniko. Loke vidito lokiko. Catumahārājesu bhattā cātummahārājikā. Dvāre niyutto dovāriko, dassoka tadaminādipāṭhā. Bhaṇḍāgāriko. Ike-navakammiko, ādikammiko. Kiye-jātikiyo, andhakiyo.</p>\r\n<p class="subhead">33. Tassi+daṃ</p>\r\n<p class="bodytext">Chaṭṭhiyantā ida+micca+smiṃ atthe ṇiko hoti. Saṅghassa idaṃ saṅghikaṃ, puggalikaṃ, sakyaputtiko, nāṭaputtiko, jenadattiko. Kiye-sassa idaṃ sakiyo, parakiyo. Niyeattaniyaṃ. Ke-sako, rañño idaṃ rājakaṃ bhaṇḍaṃ.</p>\r\n<p class="subhead">34. Ṇo</p>\r\n<p class="bodytext">Chaṭṭhiyantā ida+micca+smiṃ atthe ṇo hoti. Kaccāyanassa idaṃ kaccāyanaṃ, byākaraṇaṃ, sogataṃ sāsanaṃ, māhisaṃ maṃsādi.</p>\r\n<p class="subhead">35. Gavādīhi yo</p>\r\n<p class="bodytext">Gavādīhi chaṭṭhiyantehi ida+micca+smiṃ atthe yo hoti. Gunnaṃ idaṃ gabyaṃ, avaṅa, maṃsādi. Ilope kabbaṃ. Duno idaṃ dabbaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',185,replace(replace('<p class="subhead">36. Pitito bhātari reyyaṇa</p>\r\n<p class="bodytext">‘‘Pitito <a name="M0.0185"></a> mātito’’ti tena tena suttanipāteneva ussa i. Pitusaddā tassa bhātari reyyaṇa. Pitu bhātā petteyyo.</p>\r\n<p class="subhead">37. Mātito ca bhaginiyaṃ ccho</p>\r\n<p class="bodytext">Mātuto ca pituto ca tesaṃ bhaginiyaṃ ccho hoti. Mātu bhagini mātucchā, pitu bhagini pitucchā. Kathaṃ ‘‘mātu bhātā mātulo’’ti, ‘‘mātulāditvānī’’ti nipātanā lapaccayo.</p>\r\n<p class="subhead">38. Mātāpitūsvā+maho</p>\r\n<p class="bodytext">Mātāpitūhi tesaṃ mātāpitūsvā+maho hoti. Mātu mātā mātāmahī, mātu pitā mātāmaho. Pitu mātā pitāmahī, pitu pitā pitāmaho. Na yathāsaṅkhyaṃ paccekābhisambandhato visuṃ visuṃ mātāpitusaddehi tesaṃ mātāpitunnaṃ atthe paccayo hoti.</p>\r\n<p class="subhead">39. Hite reyyaṇa</p>\r\n<p class="bodytext">Mātāpitūhi hite reyyaṇa hoti. Mātu hito matteyyo, petteyyo.</p>\r\n<p class="subhead">40. Nindā+ññāta+ppa paṭibhāga rassa dayā saññāsu ko</p>\r\n<p class="bodytext">Nindādīsva+tthesu nāmasmā ko hoti. Nindāyaṃ-nindito muṇḍo muṇḍako, evaṃ samaṇako, paṇḍitako, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',186,replace(replace('<p class="bodytext">brāhmaṇako<a name="M0.0186"></a>, veyyākaraṇako. Aññāte-kassā+yaṃ asso assako, payogasāmatthiyā sambandhivisesānāvagamo+vagamyate. Appatthe-appakaṃ telaṃ telakaṃ, ghatakaṃ. Paṭibhāgatthe-hatthī viya hatthiko, assako, balībaddhako. ‘‘Ime no hatthikā assā, balībaddhā ca no ime’’tyādipāṭhe ‘‘lopo’’ti kapaccayalopena vā abhedopacārena vā daṭṭhabbaṃ, ime ca dāruādīhi katarūpāni. Rasserasso mānuso mānusako, rukkhako, pilakkhako. Dayāyaṃ-dayito=nukampito putto puttako, vacchako. Saññāyaṃ-moro viya morako, katako, bhatako.</p>\r\n<p class="subhead">41. Ta+massa parimāṇaṃ ṇiko ca</p>\r\n<p class="bodytext">Paṭhamantā asseti asmiṃ atthe ṇiko hoti ko ca, tañce paṭhamantaṃ parimāṇaṃ bhavati. Doṇādīnaṃ parimitavīhādīnaṃ karaṇattā ‘‘parimīyantya+nenāti parimāṇa’’nti hoti. Doṇo parimāṇa+massāti doṇiko vīhi, khārasatiko, khārasahassiko, āsītiko vayo, upaḍḍhakāyo parimāṇa+massa upaḍḍhakāyikaṃ bimbohanaṃ. Pañcakaṃ, chakkaṃ.</p>\r\n<p class="subhead">42. Ya+te+tehi+ttako</p>\r\n<p class="bodytext">Yādīhi paṭhamantehi asseti chaṭṭhyatthe ttako hoti, tañce paṭhamantaṃ parimāṇaṃ bhavati. Yaṃ parimāṇa+massa yattakaṃ, tattakaṃ, ‘‘etasseṭa ttake’’ti etassa eṭa, ettakaṃ. Āvatake-yaṃ parimāṇa+massa yāvatako, tāvatako.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',187,replace(replace('<p class="subhead">43. Sabbā cā+vantu</p>\r\n<p class="bodytext">Sabbato <a name="M0.0187"></a> paṭhamantā yādīhi ca asseti chaṭṭhyatthe āvantu hoti, tañce paṭhamantaṃ parimāṇaṃ bhavati. Sabbaṃ parimāṇa+massa sabbāvantaṃ, ‘‘aṃṅaṃ napuṃsake’’ti aṃ. Yāvantaṃ, tāvantaṃ.</p>\r\n<p class="subhead">44. Kimhā rati+rīva+rīvataka+rittakā</p>\r\n<p class="bodytext">Kimhā paṭhamantā asseti chaṭṭhyatthe rati+rīva+rīvataka+rittakā honti. Kiṃsaṅkhyānaṃ parimāṇa+mesaṃ kati, rānubandhattā iṃbhāgalopo. Kīva, kīvatakaṃ, kittakaṃ. Rīvanto sabhāvato asaṃkhyo.</p>\r\n<p class="subhead">45. Sañjātaṃ tārakāditvi+toti</p>\r\n<p class="bodytext">Sañjātatthe ito. Tārakā sañjātā assa tārakitaṃ gaganaṃ, pupphāni sañjātāni assa pupphito rukkho, pallavitā latā.</p>\r\n<p class="subhead">46. Māne matto</p>\r\n<p class="bodytext">Paṭhamantā mānavuttito asseti asmiṃ atthe matto hoti. Palaṃ ummāna+massa palamattaṃ. Hattho paṇāma+massa hatthamattaṃ. Sataṃ māna+massa satamattaṃ. Doṇo parimāṇa+massa doṇamattaṃ. Abhedopacārā doṇotipi hoti. Mīyate+nenāti mānaṃ, mānassa sambandhittā chaṭṭhyantabhūtānameva vidhi hoti. Ettha ca –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',188,replace(replace('<p class="gatha1">Uddhamānantu <a name="M0.0188"></a> ummānaṃ, parimāṇantu sabbato;</p>\r\n<p class="gathalast">Pamāṇaṃ hoti āyāmo, saṃkhyāseso tu sabbato.</p>\r\n<p class="subhead">47. Taggho cu+ddhaṃ</p>\r\n<p class="bodytext">Uddhamānavuttito taggho hoti matto ca. Jaṇṇu parimāṇa+massa jaṇṇutagghaṃ, jaṇṇumattaṃ.</p>\r\n<p class="subhead">48. Ṇo ca purisāti</p>\r\n<p class="bodytext">Purisā ṇo hoti mattādayo ca. Puriso parimāṇa+massa porisaṃ, purisamattaṃ+purisatagghaṃ.</p>\r\n<p class="subhead">49. Ayu+bhadvitīhaṃ+se</p>\r\n<p class="bodytext">Ubhadvitīhi avayavavuttīhi ayo hoti. Ubho aṃsā assa ubhayaṃ, dvayaṃ, tayaṃ. Aṃsasambandhena samudāye vidhi hotīti na ubhayādito bahuvacananti ce, ‘‘rāhuno siro’’ tyādo abhedepi bhedavivakkhāya labbhamānato bahuvacanaṃ hoteva.</p>\r\n<p class="subhead">50. Saṅkhyāya saccu+tī+sā+sa+dasantāyā+dhikā+smiṃ satasahasse ḍo</p>\r\n<p class="bodytext">Satyantāya utyantāya īsantāya āsantāya dasantāya saṅkhyāya paṭhamantāya asminti sattamyatthe ḍo hoti, sā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',189,replace(replace('<p class="bodytext">ce <a name="M0.0189"></a> saṅkhyā adhikā hoti, ya+dasminti, taṃ ce sataṃ sahassaṃ satasahassaṃ vā hoti. Vīsati adhikā asmiṃ sateti vīsaṃsataṃ.</p>\r\n<p class="subhead">139. Ḍe satissa tissati</p>\r\n<p class="bodytext">Ḍe pare satyantassa tikārassa lopo hoti. Ekavīsaṃsataṃ sahassaṃ satasahassaṃ vā. Tiṃsati adhikā asmiṃ satādiketi tiṃsasataṃ ekatiṃsasataṃ iccādi. Utyantāyanavuti adhikā asmiṃ satādiketi navutaṃsataṃ sahassaṃ satasahassaṃ vā. Īsantāya-cattālīsaṃ adhikā asmiṃ sate sahasse satasahasseti cattālīsasata+miccādi. Āsantāya evaṃ, paññāsaṃsata+miccādi. Dasantāya-ekādasaṃsata+miccādi.</p>\r\n<p class="subhead">51. Tassa pūraṇe+kādasādito vā</p>\r\n<p class="bodytext">Chaṭṭhiyantāye+kādasādikāya saṃkhyāya ḍo hoti pūraṇatthe vibhāsā. Sā saṃkhyā pūriyate yena taṃ pūraṇaṃ. Ekādasannaṃ pūraṇo ekādaso. Aññatra ‘‘ma pañcādikatīhī’’ti mo, ekādasamo. Vīsatiyā pūraṇo vīso vīsatimo, tiṃso, tiṃsatimo. Vāsaddassa vavatthitavibhāsattā niccaṃ cattālīso paññāso.</p>\r\n<p class="subhead">52. Ma pañcādikatīhiti</p>\r\n<p class="bodytext">Mapaccaye pañcamo, pañcamī. Sattamo, sattamī. Aṭṭhamo, aṭṭhamī. Katimo, katimī iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',190,replace(replace('<p class="subhead">53. Sathādīna+mi cati</p>\r\n<p class="bodytext">Satādīnaṃ <a name="M0.0190"></a> mo ca antādeso i ca. Satassa pūraṇo satimo, sahassimo.</p>\r\n<p class="subhead">54. Chā ṭṭha+ṭṭhamāti</p>\r\n<p class="bodytext">Chasaddā pūraṇatthe ṭṭha+ṭṭhamā. Channaṃ pūraṇo chaṭṭho, chaṭṭhamo. Itthiyaṃ chaṭṭhī, chaṭṭhamī. ‘‘Catutthadutiyesve+saṃ tatiyapaṭhamā’’ti nipātanā pūraṇatthe dvito tiyo dvissa du ca, ticatūhi tissa a, tiya+tthā ca yathākkamaṃ, dutiyo tatiyo, catuttho.</p>\r\n<p class="subhead">55. Ekā kā+kya+sahāye</p>\r\n<p class="bodytext">‘‘Sati byabhicāre visesanaṃ sātthakaṃ’’ti ñāyā saṃkhyāto visesetuṃ ‘‘asahāye’’ti vuttaṃ. Ekasmā asahāyatthe ka+ākī honti vā. Ekova ekako, ekākī, eko vā.</p>\r\n<p class="subhead">56. Vacchādīhi tanutte taro</p>\r\n<p class="bodytext">Vacchādīnaṃ sabhāvassa tanutte=kiñcimattāvasese taro hoti. So=sako bhāvo sabhāvo attaniyapavattinimittaṃ. Susuttassa tanutte vacchataro, itthiyaṃ vacchatarī. Yobbanassa tanutte okkhataro. Okkhā=dutiyavayaṭṭhagoṇo. Assabhāvassa tanutte assataro. Gāvoti jātisāmatthiyassa tanutte usabhataro. Ettha tanuttaṃ appabalatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',191,replace(replace('<p class="subhead">57. Kimhā niddhāraṇe ratara+ratamā</p>\r\n<p class="bodytext">Kiṃsaddā <a name="M0.0191"></a> niddhāraṇe ratara+ratamā honti. Ko evāti kataro bhavataṃ devadatto, kataro bhavataṃ kaṭṭho. Katamo bhavataṃ devadatto, katamo bhavataṃ kaṭṭho. Bhāradvājānaṃ katamo+si brahme.</p>\r\n<p class="subhead">58. Tena datte li+yāti</p>\r\n<p class="bodytext">Datte+bhidheyye la+iyā honti bahulādhikārā manussasaññāyaṃ. Devena datto devalo deviyo, brahmalo brahmiyo. Sīvalo sīviyo sissa dīgho. Deva+brahma+sivāti tannāmakā manussā. (Rattamiccādianekatthataddhitaṃ).</p>\r\n<p class="subhead">59. Tassa bhāvakammesutta+tā+ttana+ṇya+ṇeyya+ṇi+ya+ṇiyā</p>\r\n<p class="bodytext">Chaṭṭhiyantā bhāve kamme ca ttādayo honti bahulaṃ. Na sabbe paccayā sabbato honti aññatratta+tāhi. Bhavanti etasmā buddhisaddāti bhāvo saddapavattinimittaṃ. Vuttañca –</p>\r\n<p class="gatha1">Hontya+smā saddabuddhīti, bhāvo taṃ saddavuttiyā;</p>\r\n<p class="gathalast">Nimittabhūtaṃ nāmañca, jāti dabbaṃ kriyā guṇoti.</p>\r\n<p class="bodytext">Nīlassa paṭassa bhāvo nīlattaṃ nīlatāti guṇo bhāvo. Ettha nīlaguṇavasena paṭe nīlasaddassa vuttiyā paṭabuddhiyā nimittaṃ bhāvo nāma. Nīlassa guṇassa bhāvo nīlattaṃ nīlatāti nīlaguṇajāti, ettha nīlaguṇajāti nimittaṃ hutvā nīlasaddassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',192,replace(replace('<p class="bodytext">guṇavuttiyā <a name="M0.0192"></a> nīlaguṇajāti nimittaṃ. Gottaṃ gotāti gojāti, ettha jātisaddānaṃ dabbavuttiyā sati jāti nimittaṃ. Pācakassa bhāvo pācakattaṃti kriyāsambandhittaṃ bhāvo, ettha pacanakriyāsambandhittaṃ bhāvo. Daṇḍittaṃ visāṇittaṃ rājapurisattaṃti daṇḍa+visāṇa+rājadabbānaṃ sambandhittaṃ bhāvo, daṇḍītyādisaddapavattiyā nimittattā.</p>\r\n<p class="bodytext">Devadattassa bhāvo devadattattaṃ, candattaṃ, sūriyattaṃti tadavatthā visesasāmaññaṃ, devadattassa bālatādiavatthābhedo, candassa kalādiavatthabhedo ca, sūriyassa mandapaṭutādiavatthābhedo ca sāmaññaṃ, tadettha nimittaṃ. Ettha vijjamānapadatthānaṃ visayabhūtasaññāsaddānaṃ pavattinimittaṃ vuttaṃ. Ākāsattaṃ abhāvattaṃti, tattha ghaṭākāsa+pīṭharākāsa, paṭābhāva+ghaṭābhāvādinā upacaritabhedasāmaññaṃ bhāvo.</p>\r\n<p class="bodytext">Ttana-puthujjanattanaṃ. Vedanāya bhāvo vedanattanaṃ, rasso. Evaṃ jāyattanaṃ, jārattanaṃ.</p>\r\n<p class="bodytext">Ṇya-alasassa bhāvo kammaṃ vā ālasyaṃ, ettha ‘‘lopo+vaṇṇi+vaṇṇānaṃ’’ti a-lope ‘‘sarāna+mādissā’’dinā ākāro, eva+mupari ca. Brāhmaṇassa bhāvo brahmaññaṃ, ññakārapubbarūpāni. Cāpalyaṃ, nepuññaṃ, pesuññaṃ, rañño bhāvo rajjaṃ, ādhipaccaṃ, dāyajjaṃ, vesammaṃ, vesamaṃ, ṇapaccayo. Sakhino bhāvo sakhyaṃ, vāṇijjaṃ, ārogyaṃ, odagyaṃ, ānaṇyaṃ, dubbalyaṃ, balyaṃ, paṇḍiccaṃ, bāhussaccaṃ, porohiccaṃ. Muṭṭhassatissa bhāvo muṭṭhassaccaṃ, ilopo. Kosallaṃ, vepullaṃ. Samānassa bhāvo sāmaññaṃ, perissaṃ, somanassaṃ, domanassaṃ, sovacassaṃ, dovacassaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',193,replace(replace('<p class="bodytext">nipakassa <a name="M0.0193"></a> bhāvo nepakkaṃ, ādhikkaṃ, dubhagassa bhāvo dobhaggaṃ, sarūpassa bhāvo sāruppaṃ, opammaṃ, sokhummaṃ, tathassa bhāvo tacchaṃ. Dummedhassa bhāvo dummejjhaṃ. Bhesajassa bhāvo bhesajjaṃ, byāvaṭassa kammaṃ veyyāvaccaṃ yathā veyyākaraṇaṃ.</p>\r\n<p class="bodytext">Ṇeyya-sucino bhāvo soceyyaṃ, ādhipateyyaṃ, kapissa bhāvo kāpeyyaṃ. Saṭhassa bhāvo kammaṃ vā sāṭheyyaṃ.</p>\r\n<p class="bodytext">Ṇa-garūnaṃ bhāvo gāravaṃ, u avaṅa. Pāṭavaṃ, ajjavaṃ, maddavaṃ, ‘‘kosajjā’’dinā ussa attaṃ dvittañca. Paramānaṃ bhāvo pāramī, vīpaccayo. Samaggānaṃ bhāvo sāmaggī.</p>\r\n<p class="bodytext">Iya-adhipatino bhāvo adhipatiyaṃ, paṇḍitiyaṃ, bahussutiyaṃ, naggiyaṃ, sūriyaṃ.</p>\r\n<p class="bodytext">Ṇiya-alasassa bhāvo kammaṃ vā ālasiyaṃ, tālusiyaṃ, mandiyaṃ, dakkhiyaṃ, porohitiyaṃ, veyyattiyaṃ.</p>\r\n<p class="bodytext">Kathaṃ rāmaṇīyakaṃti, sakatthe kantā ṇena siddhā. Kammaṃ=kriyā, tattha alasassa kammaṃ alasattaṃ alasatā alasattanaṃ ālasyaṃ ālasiyaṃ vā.</p>\r\n<p class="subhead">122. Sakattheti</p>\r\n<p class="bodytext">Sakatthepi yathāyogaṃ ttādayo honti. Yathābhūtameva yathābhuccaṃ, kāruññaṃ, pattakallaṃ. Ākāsānantameva ākāsānañcaṃ, ettha akāralopo, tassa ce pubbarūpe ca kate ‘‘tadaminā’’dinā nassa ñño ca lopo ca hoti, kāyapāguññatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',194,replace(replace('<p class="subhead">60. Bya vaddhadāsā vā</p>\r\n<p class="bodytext">Chaṭṭhiyantā <a name="M0.0194"></a> vaddhā dāsā ca byo hoti bhāvakammesu. Vaddhassa bhāvo kammaṃ vā vaddhabyaṃ, vaddhatā. Dāsassa bhāvo kammaṃ vā dāsabyaṃ, dāsyaṃ, dāsatā. Kathaṃ vaddhavaṃti, ṇe vāgamo.</p>\r\n<p class="subhead">61. Nasa yuvā bo ca vassa</p>\r\n<p class="bodytext">Chaṭṭhiyantā yuvasaddā bhāvakammesu naṇa vā hoti vassa bo ca. Yuvassa bhāvo yobbanaṃ, yuvattaṃ yuvatā vā.</p>\r\n<p class="subhead">62. Aṇvāditvi+moti</p>\r\n<p class="bodytext">Bhāve vā imo. Aṇuno bhāvo aṇimā, laghimā, mahato bhāvo mahimā. Kisassa bhāvo kasimā.</p>\r\n<p class="subhead">133. Kisa+mahata+mime kasa, mahāti</p>\r\n<p class="bodytext">Imamhi mahato maho ca kisassa kasādeso ca hoti.</p>\r\n<p class="subhead">63. Bhāvā tena nibbatteti</p>\r\n<p class="bodytext">Kriyāvācakasaddato imo, pākena nibbattaṃ pākimaṃ, sekimaṃ.</p>\r\n<p class="subhead">127. Kosajjā+jjava+pārisajja+sohajja+maddavā+rissā+sabhā+jañña+theyya+ bāhusaccā</p>\r\n<p class="bodytext">Ete saddā nipaccante ṇānubandhe. Kusītassa bhāvoti bhāve ṇyo hoti, iminā īssa akāre ca tassa je </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',195,replace(replace('<p class="bodytext">ca <a name="M0.0195"></a> kate yassa pubbarūpaṃ, kosajjaṃ. Ujuno bhāve ajjavaṃti ṇo, iminā ussa attaṃ, ‘‘uvaṇṇassā+vaṅa sare’’ti avaṅaādese jassa dvittaṃ. Parisāsu sādhūti vākye ‘‘ṇyo tattha sādhū’’ti ṇyo, iminā jāgamo ca, ‘‘byañjane dīgharassā’’ti āssa rasse ca kate jassa pubbarūpaṃ, pārisajjo. Iminā kamena vākyeneva saddasiddhi veditabbā. Suhadayova suhajjo, tassa bhāvo sohajjaṃ, iminā ayalopo. Muduno bhāvo maddavaṃ, iminā ussa attaṃ. Isino idaṃ bhāvo vā ārissaṃ, ṇyapaccaye iminā ārañāgame ‘‘lopo+vaṇṇi+vaṇṇānaṃ’’ti antaikāralopo ca. Usabhassa idaṃ bhāvo vā āsabhaṃ, iminā ussa ā. Ājānīyassa bhāvo ājaññaṃ, iminā yalope ‘‘lopo+vaṇṇi+vaṇṇānaṃ’’ti īkāralopo. Thenassa bhāvo theyyaṃ, iminā nassa yakāro. Bahussutassa bhāvo bāhusaccaṃ, iminā ussa akāro saṃyogādilopo. (Bhāvataddhitaṃ).</p>\r\n<p class="subhead">64. Tara+tami+ssiki+yi+ṭṭhā+tisaye</p>\r\n<p class="bodytext">Atisaye vattamānato honte+te paccayā. Atisayena pāpo pāpataro pāpatamo pāpissito pāpiyo pāpiṭṭho, itthiyaṃ pāpatarā iccādi. Atisayappaccayantāpi atisayappaccayo, atisayena pāpiṭṭho pāpiṭṭhataro pāpiṭṭhatamo. Evaṃ paṭutaro paṭutamo, paṭissiko, paṭiyo, paṭiṭṭho, varataro iccādi paṇītataro iccādi ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',196,replace(replace('<p class="subhead">135. Jo vuddhassi+yi+ṭṭhesu</p>\r\n<p class="bodytext">Vuddhassa <a name="M0.0196"></a> jo hoti iyaiṭṭhesu. Atisayena vuddho jeyyo jeṭṭho, jādese pubbasaralope luttā sarā issa ekāre ca yassa dvittaṃ.</p>\r\n<p class="subhead">136. Bāḷha+ntika+pasatthānaṃ sādha+neda+sā</p>\r\n<p class="bodytext">Iyaiṭṭhesu bāḷha+ntika+pasatthānaṃ sādha+neda+sā honti yathākkamaṃ. Atisayena bāḷho sādhiyo sādhiṭṭho. Atisayena antiko nediyo nediṭṭho. Atisayena pasattho seyyo seṭṭho, pubbeva sare lutte issa e dvittañca.</p>\r\n<p class="subhead">137. Kaṇa+kana+ppa+yuvānaṃ</p>\r\n<p class="bodytext">Iyaiṭṭhesu appayuvānaṃ kaṇa+kanā honti yathākkamaṃ. Atisayena appo kaṇiyo kaṇiṭṭho. Atisayena yuvā kaniyo kaniṭṭho.</p>\r\n<p class="subhead">138. Lopo vī+mantu+vantūnanti</p>\r\n<p class="bodytext">Iyaiṭṭhesu vī+mantu+vantūnaṃ lopo. Atisayena medhāvī medhiyo medhiṭṭho. Atisayena satimā satiyo satiṭṭho. Atisayena guṇavā guṇiyo guṇiṭṭho. Ettha yathākkamaṃ mantvatthe vī+mantu+vantu hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',197,replace(replace('<p class="subhead">65. Tannissite llo</p>\r\n<p class="bodytext">Llapaccayo <a name="M0.0197"></a> hoti dutiyantā tannissitatthe. Vedaṃ nissitaṃ vedallaṃ. Duṭṭhu nissitaṃ duṭṭhullaṃ. Vedanti tuṭṭhi. Ille saṅkhāraṃ nissitaṃ saṅkhārillaṃ.</p>\r\n<p class="subhead">66. Tassa vikārāvayavesu ṇa+ṇika+ṇeyya+mayā</p>\r\n<p class="bodytext">Pakatiyā uttara+mavatthantaraṃ vikāro. Chaṭṭhiyantā nāmasmā vikāre+vayave ca ṇādayo honti bahulaṃ. Ṇa-ayaso vikāro āyasaṃ bandhanaṃ, sakāgamo. Udumbarassa avayavo vikāro vā odumbaraṃ bhasmaṃ paṇṇaṃ vā. Kāpotaṃ maṃsaṃ satti vā. Ṇika-kappāsassa vikāro kappāsikaṃ vatthaṃ. Ṇeyya-eṇissa vikāro+vayavo vā eṇeyyaṃ. Kosānaṃ vikāro koseyyaṃ vatthaṃ. Maya-tiṇānaṃ vikāro tiṇamayaṃ, dārumayaṃ, naḷamayaṃ, mattikāmayaṃ, suvaṇṇamayo ratho, rūpiyamayaṃ. ‘‘Aññasmiṃ’’ti mayo, gunnaṃ karīsaṃ gomayaṃ.</p>\r\n<p class="subhead">67. Jatuto ssaṇa vā</p>\r\n<p class="bodytext">Vikārāvayavesu jatuto ssaṇa vā hoti. Ettha ‘‘vikārāvayavesū’’ti vattantesupi bahulādhikārā vikāreyeva hoti. Jatuno vikāro jātussaṃ jatumayaṃ.</p>\r\n<p class="subhead">123. Lopoti</p>\r\n<p class="bodytext">Bahulaṃ paccayalopopi. ‘‘Phalitassa rukkhassa phala+mavayavo vikāro ca, pallavitasseva pallavaṃ’’ti vuttattā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',198,replace(replace('<p class="bodytext">phalādayo <a name="M0.0198"></a> avayavā vikārā ca honti, tasmā idaṃ vuccati phalapupphamūlesu vikārāvayavesu-piyālassa phalāni piyālāni, mallikāya pupphāni mallikā, usīrassa mūlaṃ usīraṃ. Taṃsaddena vā tadabhidhānaṃ abhedopacārena, tasmā paccayalopaṃ vināpi sijjhati.</p>\r\n<p class="subhead">68. Samūhe kaṇa+ṇa+ṇikā</p>\r\n<p class="bodytext">Chaṭṭhiyantā samūhe kaṇa+ṇa+ṇikā honti. Gottapaccayantā kaṇa-rājaññānaṃ samūho rājaññakaṃ, mānusakaṃ. Ukkhādīhi ukkhānaṃ samūho okkhakaṃ, ‘‘saṃyoge kvacī’’ti okāro. Oṭṭhakaṃ. Urabbhānaṃ samūho orabbhakaṃ. Rājakaṃ, rājaputtakaṃ, hatthikaṃ, dhenukaṃ, māyūrakaṃ, kāpotakaṃ, māhisakaṃ. Ṇa-kākānaṃ samūho kākaṃ, bhikkhaṃ. Ṇika acittā-apūpānaṃ samūho āpūpikaṃ, saṃkulikaṃ.</p>\r\n<p class="subhead">69. Janādīhi tāti</p>\r\n<p class="bodytext">Samūhatthe tā. Janānaṃ samūho janatā, gajatā, bandhutā, gāmatā, sahāyatā, nāgaratā. Tāntā sabhāvato itthiliṅgā.</p>\r\n<p class="bodytext">Madanīyanti karaṇe+dhikaraṇe vā anīyena siddhaṃ. Dhūmāyitattantiktantā nāmadhātuto ttena siddhaṃ, dhūmo viya ācaratīti ‘‘kattutā+yo’’ti āye ‘‘gamanatthākammakādhāre cā’’ti ktapaccaye ūāgame ca kate dhūmāyitassa bhāvoti dhūmāyitattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',199,replace(replace('<p class="subhead">70. Iyo hiteti</p>\r\n<p class="bodytext">Hitatthe <a name="M0.0199"></a> iyo. Upādānānaṃ hitaṃ upādāniyaṃ.</p>\r\n<p class="subhead">71. Cakkhādito ssoti</p>\r\n<p class="bodytext">Hitatthe sso. Cakkhuno hitaṃ cakkhussaṃ, āyussaṃ.</p>\r\n<p class="subhead">72. Ṇyo tattha sādhu</p>\r\n<p class="bodytext">Sattamyantā tattha sādhūti asmiṃ atthe ṇyo hoti. Sādhūti kusalo yoggo hito vā. Sabhāyaṃ kusalo sabbho, ākāralopo. Parisāyaṃ sādhu pārisajjo, ‘‘kosajjā’’dinā jāgame akāravuddhi. Medhāya hitaṃ mejjhaṃ ghataṃ. Pādānaṃ hitaṃ pajjaṃ telaṃ. ‘‘Aññasmiṃ’’ti ṇyo, rathaṃ vahatīti racchā.</p>\r\n<p class="subhead">73. Kammā niya+ññāti</p>\r\n<p class="bodytext">Kammasaddā sādhvatthe niya+ññā honti. Kamme sādhu kammaniyaṃ kammaññaṃ.</p>\r\n<p class="subhead">74. Kathāditvi+koti</p>\r\n<p class="bodytext">Iko. Kathāyaṃ kusalo kathiko. Dhammakathiko, saṅgāmiko, pavāsiko, upavāsiko.</p>\r\n<p class="subhead">75. Pathādīhi ṇeyyoti</p>\r\n<p class="bodytext">Ṇeyyo. Pathe hitaṃ pātheyyaṃ, sapatismiṃ hitaṃ sāpateyyaṃ dhanaṃ. Padīpeyyaṃ telaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',200,replace(replace('<p class="subhead">76. Dakkhiṇāyā+raheti</p>\r\n<p class="bodytext">Arahatthe <a name="M0.0200"></a> ṇeyyo. Dakkhiṇaṃ arahatīti dakkhiṇeyyo.</p>\r\n<p class="subhead">77. Rāyo tumantāti</p>\r\n<p class="bodytext">Arahatthe tumantā rāyo vā. Ghātetuṃ arahatīti ghātetāyaṃ, ‘‘rānubandhe+ntasarādissā’’ti uṃlopo. Jāpetāyaṃ, pabbājetāyaṃ. Vāti kiṃ, ghātetuṃ. (Saṃkiṇṇataddhitaṃ).</p>\r\n<p class="subhead">78. Ta+mettha+ssa+tthīti mantu</p>\r\n<p class="bodytext">Paṭhamantā ettha assa atthīti etesva+tthesu mantu hoti. Gāvo ettha dese assa vā purisassa santīti gomā, gomanto iccādi guṇavantusamaṃ.</p>\r\n<p class="bodytext">Atthīti vattamānakālopādānato bhūtāhi bhavissantīhi vā gohi na gomā. Kathaṃ gomā āsi, gomā bhavissatīti. Tadāpi vattamānāhiyeva gohi gomā, āsi bhavissatīti padantarā kālantaraṃ. Itisaddato visayaniyamo. Vuttañhi –</p>\r\n<p class="gatha1">Pahūte ca pasaṃsāyaṃ, nindāyañcā+tisāyane;</p>\r\n<p class="gathalast">Niccayoge ca saṃsagge, honti+me mantuādayoti.</p>\r\n<p class="subhead">134. Āyussā+yasa mantumhi</p>\r\n<p class="bodytext">Mantumhi āyussa āyasādeso hoti. Āyu assa atthīti āyasmā. Go assoti jātisaddānaṃ dabbābhidhāna- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',201,replace(replace('<p class="bodytext">sāmatthiyā <a name="M0.0201"></a> mantvādayo na honti, tathā guṇasaddānaṃ seto paṭoti. Yesaṃ tu guṇasaddānaṃ dabbābhidhānasāmatthiyaṃ natthi, tehi honteva, buddhi assa atthīti buddhimā. ‘‘Vantva+vaṇṇā’’ti vantumhi rūpavā rasavā gandhavā saddavā. ‘‘Daṇḍāditvi+kaī vā’’ti ika+ī, rasī rasiko, rūpī rūpiko, gandhī gandhikoti.</p>\r\n<p class="subhead">79. Vantva+vaṇṇāti</p>\r\n<p class="bodytext">Vantu. Pasatthaṃ sīla+massa atthīti sīlavā. Pahutā pasatthā vā paññā assa atthīti paññavā.</p>\r\n<p class="subhead">80. Daṇḍāditvi+ka+ī vāti</p>\r\n<p class="bodytext">Ika+ī honti vā mantatthe. Bahulaṃvidhānā kutocisaddato dve honti, kuto ce+kamekaṃva. Niccayutto daṇḍo assa atthīti daṇḍiko daṇḍī. Gandhiko gandhī. Vātveva, daṇḍavā.</p>\r\n<p class="bodytext">‘‘Uttamiṇeva dhanā iko’’ti gaṇasuttena iko dhaniko. Añño dhanī dhanavā.</p>\r\n<p class="bodytext">‘‘Asannihite atthā’’ asannihito attho assa atthīti atthiko atthī. Sannihite atthavā. ‘‘Tadantā ca’’ puññattho assa atthīti puññatthiko puññatthī.</p>\r\n<p class="bodytext">‘‘Vaṇṇantā īyeva’’. Brahmavaṇṇaṃ assa atthīti brahmavaṇṇī, devavaṇṇī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',202,replace(replace('<p class="bodytext">‘‘Hatthadantehi <a name="M0.0202"></a> jātiyaṃ’’. Hattha+massa atthīti hatthī, dantī. Aññatra hatthavā dantavā.</p>\r\n<p class="bodytext">‘‘Vaṇṇato brahmacārimhi’’. Vaṇṇo assa atthīti vaṇṇiko brahmacārī.</p>\r\n<p class="bodytext">‘‘Pokkharādito dese’’. Pokkharaṃ jalaṃ padumaṃ vā assa atthīti pokkharī, ‘‘yuvaṇṇehi nī’’ti nīmhi ‘‘gharaṇyādayo’’ti īssa attaṃ, nassa ṇo ca, pokkharaṇī, uppalinī, kumudinī, bhisinī, mulālinī, sālukinī.</p>\r\n<p class="bodytext">‘‘Kvacā+desepi’’. Paduma+massa atthīti padumī paduminī paduminīpaṇṇaṃ. Desato+ññatra pokkharavā hatthī.</p>\r\n<p class="bodytext">‘‘Nāvāyi+ko’’ nāviko. Sikhī, bālī, sīlī, balī.</p>\r\n<p class="bodytext">‘‘Sukhadukkhā ī’’. Sukhī dukkhī. ‘‘Balā bāhūrupubbā ca’’. Bāhubalī, ūrubalī.</p>\r\n<p class="subhead">81. Tapādīhi ssīti</p>\r\n<p class="bodytext">Ssī. Tapo assa atthīti tapassī, yasassī, tejassī, manassī, payassī. Vātveva, yasavā.</p>\r\n<p class="subhead">82. Mukhādito roti</p>\r\n<p class="bodytext">Ro. Ninditaṃ mukha+massa atthīti mukharo. Susi=chiddaṃ assa atthīti susiro. Ūso=khāramattikā asmiṃ atthīti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',203,replace(replace('<p class="bodytext">ūsaro<a name="M0.0203"></a>. Madhuro guḷo, madhurā sakkharā, madhuraṃ khīraṃ. Khaṃ=gīvāya vivaraṃ assa atthīti kharo gadrabho. Kuñjo=hanu assa atthīti kuñjaro. Nagaro.</p>\r\n<p class="bodytext">‘‘Dantassa ca unnatadante’’ti gaṇasuttena dantassa u ca, unnataṃ danta+massa atthīti danturo.</p>\r\n<p class="subhead">83. Tuṭṭhyādīhi bhoti</p>\r\n<p class="bodytext">Bho vā. Tuṭṭhi assa atthīti tuṭṭhibho, sālibho, vālibho.</p>\r\n<p class="subhead">84. Saddhāditva</p>\r\n<p class="bodytext">Iti vā a hoti. Atisayā saddhā assa atthīti saddho, pañño, paññavā, saddhā kaññā, saddhaṃ kulaṃ.</p>\r\n<p class="subhead">85. Ṇo tapāti</p>\r\n<p class="bodytext">Ṇo, tāpaso, sakāgamo. Tāpasī.</p>\r\n<p class="subhead">86. Ālva+bhijjhādīhiti</p>\r\n<p class="bodytext">Ālu vā. Abhijjhā assa atthīti abhijjhālu, sītālu, dhajālu, dayālu, dayāvā. Abhijjhālu eva abhijjhāluko.</p>\r\n<p class="subhead">87. Picchāditvi+loti</p>\r\n<p class="bodytext">Ilo vā. Piccha+massa atthīti picchilo, picchavā. Pheṇilo, jaṭilo, tuṇḍilo. Ninditā vācā assa atthīti vācāloti parassaralopo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',204,replace(replace('<p class="subhead">88. Sīlādito voti</p>\r\n<p class="bodytext">Vo <a name="M0.0204"></a> hoti vā. Sīlavo, sīlavā. Kesavo, kesavā.</p>\r\n<p class="bodytext">‘‘Aṇṇā niccaṃ’’ aṇṇavo. ‘‘Gaṇḍīrājīhi saññāyaṃ’’ gaṇḍī=meṇḍasiṅgaṃ assa atthīti gaṇḍīvaṃ dhanu, rājīvaṃ paṅkajaṃ.</p>\r\n<p class="subhead">89. Māyā medhāhi vīti</p>\r\n<p class="bodytext">Vī. Māyāvī, medhāvī pumā. Nīmhi medhāvinī. Medhāvī kulaṃ, ‘‘ekavacanayosva+ghonaṃ’’ti simhi rasso. Evaṃ māyāvī.</p>\r\n<p class="subhead">90. Si+ssare āmyu+vāmī</p>\r\n<p class="bodytext">Issare+bhidheyye sasaddā āmī+uvāmī honti mantvatthe. Saṃ=āyattaṃ assa atthīti sāmī suvāmī. Suvāminī kaññā.</p>\r\n<p class="subhead">91. Lakkhyā ṇo a ca</p>\r\n<p class="bodytext">Lakkhīsaddā ṇo hoti mantvatthe a ca+ntassa. Lakkhī assa atthīti lakkhaṇo.</p>\r\n<p class="subhead">92. Aṅgā no kalyāṇeti</p>\r\n<p class="bodytext">Aṅgasmā no hoti. Kalyāṇāni sobhaṇāni aṅgāni assā atthīti aṅganā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',205,replace(replace('<p class="subhead">93. So lomāti</p>\r\n<p class="bodytext">Lomā <a name="M0.0205"></a> so, pahūtā lomā assa atthīti lomaso, lomasā kaññā.</p>\r\n<p class="subhead">94. Imi+yāti</p>\r\n<p class="bodytext">Ima+iyā honti, putto assa atthīti puttimo, kittimo, puttiyo, kappiyo, jaṭiyo, hānabhāgiyo, seniyo. (Atthyatthataddhitaṃ).</p>\r\n<p class="subhead">95. To pañcamyā</p>\r\n<p class="bodytext">Pañcamyantā bahulaṃ to hoti vā. Toādipaccayantā nipphannanipātā, tehi parāsaṃ vibhattīnaṃ ‘‘asaṃkhyehi sabbāsaṃ’’ti lopova. Gāmato āgacchatīti gāmasmā āgacchati, corato bhāyatīti corehi bhāyati, satthato parihīno satthā parihīno. Evaṃ purisato, rājato, aggito, hatthito, hetuto, yuttito, itthito, bhikkhunito, yāguto, jambuto, cittato, āyuto. Sabbādito-sabbato, yato, tato iccādi.</p>\r\n<p class="subhead">96. Ito+te+tto kuto</p>\r\n<p class="bodytext">Tomhi imassa ṭi nipaccate etassa ṭa+eṭa kiṃ saddassa kuttañca. Ito imasmā, ato etto etasmā, kuto kasmā.</p>\r\n<p class="subhead">97. Abhyādīhiti</p>\r\n<p class="bodytext">To. Abhito, parito, pacchato, heṭṭhato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',206,replace(replace('<p class="subhead">98. Ādyādīhiti</p>\r\n<p class="bodytext">Sattamyantehi <a name="M0.0206"></a> to hoti. Ādo ādito, majjhato, antato, piṭṭhito, passato, mukhato. Paṭhamantā yato+dakaṃ ta+dādittaṃ, yaṃ udakaṃ, tadevā+dittanti attho.</p>\r\n<p class="subhead">99. Sabbādito sattamyā tra+tthā</p>\r\n<p class="bodytext">Sabbādīhi sattamyantehi tra+tthā vā honti. Sabbatra sabbattha sabbasmiṃ, yatra yattha, tatra tattha iccādi. Bahulādhikārā na tumhaamhehi.</p>\r\n<p class="subhead">100. Katthe+ttha+kutrā+tra kve+hi+dha</p>\r\n<p class="bodytext">Etehi ‘‘sabbādito’’tiādinā tra+tthā. Sesādesā ca paccayā ca imināva nipaccante. Iminā kissa ka+ku ca, etassa ṭe+ṭā ca, vapaccaye kissa ilopo ca, ha+dhapaccayesu imassa ṭi ca nipaccate. Kasmiṃ kattha kutra kva, etasmiṃ ettha atra, imasmiṃ iha idha.</p>\r\n<p class="subhead">101. Dhi sabbā vāti</p>\r\n<p class="bodytext">Sabbasmā dhi vā. Sabbasmiṃ sabbadhi sabbatra.</p>\r\n<p class="subhead">102. Yā hinti</p>\r\n<p class="bodytext">Yasaddā hiṃ, yasmiṃ yahiṃ yatra.</p>\r\n<p class="subhead">103. Tā hañcati</p>\r\n<p class="bodytext">Tasaddā haṃ hoti hiñca. Tahaṃ tahiṃ tatra.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',207,replace(replace('<p class="subhead">104. Kuhiṃ kahanti</p>\r\n<p class="bodytext">Hiṃ <a name="M0.0207"></a> haṃ nipaccante kissa ku+kā ca. Kuhiṃ kahaṃ. Kuhiñcanaṃti nipātantaraṃ.</p>\r\n<p class="subhead">105. Sabbe+ka+ñña+ya+tehi kāle dā</p>\r\n<p class="bodytext">Etehi kāle dā hoti vā. Sabbasmiṃ kāle sabbadā, ekadā, aññadā, yadā, tadā.</p>\r\n<p class="subhead">106. Kadā kudā sadā+dhune+dāni</p>\r\n<p class="bodytext">Ete saddā nipaccante. Kasmiṃkāle kadā kudā, sabbasmiṃ kāle sadā, imasmiṃ kāle adhunā idāni.</p>\r\n<p class="subhead">107. Ajja sajjva+parajjve+tarahi karahā</p>\r\n<p class="bodytext">Pakati paccayo ādeso kālavisesoti sabba+metaṃ nipātanā labbhati. Imassa ṭo jjo cā+hani nipaccante, asmiṃ ahani ajja. Samānassa sa-bhāvo jju cā+hani. Samāne ahani sajju. Aparasmā jju, aparasmiṃ ahani aparajju. Imassa eto, kāle rahi ca, imasmiṃkāle etarahi. Kiṃ saddassa ko, raha cā+najjatane. Kasmiṃ kāle karaha.</p>\r\n<p class="subhead">108. Sabbādīhi pakāre thā</p>\r\n<p class="bodytext">Sāmaññassa bhedako viseso pakāro, tasmiṃ thāpaccayo hoti. Sabbena pakārena sabbathā, yathā, tathā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',208,replace(replace('<p class="subhead">109. Katha+mitthaṃ</p>\r\n<p class="bodytext">Ki+mimehi <a name="M0.0208"></a> thaṃpaccayo, ka+ittaṃ tesaṃ yathākkamaṃ. Kena pakārena kathaṃ, iminā pakārena itthaṃ.</p>\r\n<p class="subhead">110. Dhā saṅkhyāhiti</p>\r\n<p class="bodytext">Pakāre dhā hoti. Dvīhi pakārehi dve vā pakāre karoti dvidhā karoti, bahudhā karoti, ekaṃ rāsiṃ pañcappakāraṃ karoti pañcadhā karoti. Pañcappakāra+mekappakāraṃ karoti ekadhā karoti.</p>\r\n<p class="subhead">111. Ve+kā+jjhanti</p>\r\n<p class="bodytext">Ekasmā pakāre jjhaṃ vā hoti. Ekena pakārena ekaṃ vā pakāraṃ karoti ekajjhaṃ karoti, ekadhā karoti vā.</p>\r\n<p class="subhead">112. Dvitīhe+dhāti</p>\r\n<p class="bodytext">Edhā vā. Dvīhi pakārehi dve vā pakāre karoti dvedhā, tedhā. Dvidhā tidhā.</p>\r\n<p class="subhead">113. Tabbati jātiyo</p>\r\n<p class="bodytext">Pakāravati taṃsāmaññavācakā saddā jātiyo hoti. Paṭujātiyo, mudujātiyo.</p>\r\n<p class="subhead">114. Vārasaṅkhyāya kkhattuṃ</p>\r\n<p class="bodytext">Vārasambandhiniyā saṃkhyāya kkhattuṃ hoti. Dve vāre bhuñjati dvikkhattuṃ divasassa bhuñjati. Vāraggahaṇaṃ kiṃ, pañca bhuñjati. Saṅkhyāyāti kiṃ, pahūte vāre bhuñjati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',209,replace(replace('<p class="subhead">115. Katimhāti</p>\r\n<p class="bodytext">Kkhattuṃ <a name="M0.0209"></a> hoti. Kati vāre bhuñjati katikkhattuṃ bhuñjati.</p>\r\n<p class="subhead">116. Bahumhā dhā ca paccāsattiyaṃ</p>\r\n<p class="bodytext">Vārasambandhiniyā bahusaṃkhyāya dhā hoti kkhattuñca, vārānaṃ ce paccāsatti hoti. Bahuvāre bhuñjati bahudhā divasassa bhuñjati, bahukkhattuṃ vā. Paccāsattiyaṃti kiṃ, bahukkhattuṃ māsassa bhuñjati.</p>\r\n<p class="subhead">117. Sa kiṃ vāti</p>\r\n<p class="bodytext">Kiṃpaccayo ekassa sādeso ca nipaccate. Ekaṃ vāraṃ bhuñjati sakiṃ bhuñjati, ekakkhattuṃ vā.</p>\r\n<p class="subhead">118. So vicchā+pakāresu</p>\r\n<p class="bodytext">Vicchāyaṃ pakāre ca so hoti. Khaṇḍaṃ khaṇḍaṃ karoti khaṇḍaso karoti. Puthuppakārena puthuso. Sabbena pakārena sabbaso.</p>\r\n<p class="subhead">119. Abhūtatabbhāve karā+sa+bhūyoge vikārā cī</p>\r\n<p class="bodytext">Avatthāvato+vatthantarenā+bhūtassa tāyā+vatthāya bhāve karā+sa+bhūhi sambandhe sati vikāravācakācī hoti. Adhavalaṃ dhavalaṃ karoti dhavalī karoti. Adhavalo dhavalo siyā dhavalī siyā. Adhavalo dhavalo bhavati dhavalī bhavati. Abhūtatabbhāveti kiṃ, ghaṭaṃ karoti, dadhi atthi, ghaṭo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',210,replace(replace('<p class="bodytext">bhavati<a name="M0.0210"></a>. Karāsabhūyogeti kiṃ, adhavalo dhavalo jāyate. Vikārāti kiṃ, pakatiyā mā hotu, suvaṇṇaṃ kuṇḍalī karoti. (Nipātataddhitaṃ).</p>\r\n<p class="subhead">120. Dissanta+ññepi paccayāti</p>\r\n<p class="bodytext">Vuttato+ññepi paccayā dissanti. Vividhā mātaro vimātaroti visesanasamāso, tāsaṃ puttā vemātikāti rikaṇapaccaye rānubandhattā ulope vuddhimhi kate vemātikā. Pathaṃ gacchantīti pathāvino, āvī. Issā assa atthīti issukī, ukī. Dhuraṃ vahantīti dhorayhā, yhaṇa.</p>\r\n<p class="gatha1">Sāmaññañca viseso ca, bhāvajo ca nipātajo;</p>\r\n<p class="gathalast">Iti viññūhi viññeyyo, taddhito tu catubbidho.</p>\r\n<p class="centered">Iti payogasiddhiyaṃ ṇādikaṇḍo pañcamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',211,replace(replace('<p class="chapter">6. Tyādikaṇḍa</p>\r\n<p class="bodytext">Atha <a name="M0.0211"></a> tyādayo kriyāvācīhi dhātūhi vuccante.</p>\r\n<p class="bodytext">Kriyaṃ ācikkhatīti ākhyātanti kriyāpadassa pubbācariyasaññā. Kāla+kāraka+purisaparidīpakaṃ kriyālakkhaṇaṃ tyādyantaṃ aliṅgañca, vuttampi ce+taṃ –</p>\r\n<p class="gatha1">Yaṃ tikālaṃ tipurisaṃ, kriyāvācī tikārakaṃ;</p>\r\n<p class="gathalast">Atiliṅgaṃ dvivacanaṃ, ta+dākhyātanti vuccati.</p>\r\n<p class="subhead">14. Kriyatthā bahulaṃ –ti</p>\r\n<p class="bodytext">Ca sabbattha vattate. Kriyā attho etassāti kriyattho dhātu, so ca duvidho sakammakā+kammakavasena. Tattha yasmiṃ kriyatthe kattuvācimhā kammaṃ gavesīyate, so sakammako. Itaro akammako.</p>\r\n<p class="bodytext">Tatra sakammakā kammāpekkhaṃ kriyaṃ vadanti, yathā kaṭaṃ karoti, gāmaṃ gacchati, odanaṃ pacati. Akammakā kammanirapekkhaṃ kriyaṃ vadanti, yathā acchati seti tiṭṭhati. Kriyāti ca gamanapacanādiko asattasammato kattari kamme vā patiṭṭhito kārakasamūhasādhiyo padattho vuccati. Api ca –</p>\r\n<p class="gatha1">Karaṇaṃ bhavanaṃ cāpi, kara+bhūhi kathīyate;</p>\r\n<p class="gathalast">Tato kriyādivācattaṃ, pākādīnaṃ kathaṃ bhave.</p>\r\n<p class="bodytext">Kara+bhūdhātūhi karaṇañca bhavanañca vuccati, pāka+gamanādīhi tesaṃ kriyābhavanānaṃ vāccattaṃ kathaṃ bhavatīti vuttaṃ hoti. Punapi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',212,replace(replace('<p class="gatha1">Pākādīnañhi <a name="M0.0212"></a> vāccattaṃ, karabhūsu na yujjati;</p>\r\n<p class="gathalast">Taṃ bahutaravāccattaṃ, pākādīsu na yujjati.</p>\r\n<p class="bodytext">Taṃ nāma –</p>\r\n<p class="gatha1">Kāriyarūpā hi dhātvatthā, sattāyuttā ca te+khilā;</p>\r\n<p class="gathalast">Tato kriyā ca bhāvo ca, sāmaññaṃ tesu gamyate.</p>\r\n<p class="subhead">1. Vattamāneti anti si tha mi ma te ante se vhe e mhe.</p>\r\n<p class="bodytext">Vattamāne āraddhāparisamatte atthe vattamānato kriyatthā tyādayo honti. Kriyādhikārattā ‘‘kriyatthā’’ti vuttaṃ. Tesa+maniyame –</p>\r\n<p class="subhead">14. Pubbaparacchakkāna+mekānekesu tumhā+mha+sesesu dve dve majjhimu+ttama+paṭhamā</p>\r\n<p class="bodytext">Ekānekesu tumhaamhasaddavacanīyesu tadaññasaddavacanīyesu ca kārakesu pubbacchakkānaṃ paracchakkānañca majjhimu+ttama+paṭhamā dve dve honti yathākkamaṃ. Tatthati antīti paṭhamapuriso, ādo niddiṭṭhattā. Si tha iti majjhimapuriso, majjhe niddiṭṭhattā. Mi ma iti uttamapuriso. Uttamasaddo+yaṃ sabhāvato tipabhutīna+manta+māha. Paracchakkepi te antetiādinā evaṃ yojetabbaṃ. Evaṃ sesesu bhavissatiādīsu sattasu paccayavidhānasuttesupi yojetabbaṃ.</p>\r\n<p class="bodytext">Idāni bhūvādīna+maṭṭhagaṇānaṃ bhūvādigaṇesu bhū=sattāya+miti paṭhamadhātuto parā tyādayo yojīyante. Tattha ‘‘ekamhi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',213,replace(replace('<p class="bodytext">vattabbe <a name="M0.0213"></a> ekavacanaṃ’’ti tyādīsu parabhūtesu kattu+kamma+bhāvesuyeva kyavikaraṇalavikaraṇā hontīti ‘‘kyo bhāvakammesva+parokkhesu māna+nta+tyādīsu’’ ‘‘kattari lo’’ iccādinā tesaṃ vikaraṇānaṃ vidhānā tyādayo kattu+kamma+bhāvesveva viññāyantīti kattari timhilo. Ke te vikaraṇā –</p>\r\n<p class="gatha1">Pubbāparabhāgaṭṭhānā, bhinnadhātuvibhattiyo;</p>\r\n<p class="gathalast">Nissāya paccayā honti, ete vikaraṇā siyuṃ.</p>\r\n<p class="subhead">5,82. Yuvaṇṇāna+me, o paccaye</p>\r\n<p class="bodytext">Ivaṇṇu+vaṇṇantānaṃ kriyattānaṃ e+o honti yathākkamaṃ paccayeti ūsso+kāro.</p>\r\n<p class="subhead">5,89. Eona+maya+vā sare</p>\r\n<p class="bodytext">Sare pare eona+mayavā honti yathākkamaṃti ossa avādeso. So puriso sādhu bhavati, sā itthī sādhu bhavati, cittaṃ sādhu bhavati.</p>\r\n<p class="gatha1">Uttattā kattu ākhyāte, tatiyā na ca kattari;</p>\r\n<p class="gathalast">Paṭhamāvibhatti hoteva, atthamattaṃ apekkhiya.</p>\r\n<p class="bodytext">Satipi kriyāyekatte kattūnaṃ bahuttā ‘‘bahumhi vattabbe bahuvacanaṃ’’ti anti.</p>\r\n<p class="subhead">161. Kvaci vikaraṇānanti</p>\r\n<p class="bodytext">Vikaraṇānaṃ kvaci lopo hotīti lavikaraṇassa lopo, sesaṃ purimasadisaṃ. Te purisā bhavanti. Majjhimapuri- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',214,replace(replace('<p class="bodytext">sekavacanaṃ <a name="M0.0214"></a> si, tvaṃ bhavasi. Bahuvacanaṃ thapaccayo, tumhe bhavatha. Uttamapurisekavacanaṃ mipaccayo.</p>\r\n<p class="subhead">57. Himimesva+ssa</p>\r\n<p class="bodytext">Akārassa dīgho hoti himimesu. Ahaṃ bhavāmi. Bahuvacanaṃ mapaccayo, mayaṃ bhavāma. Evaṃ paracchekkepi-bhavate, bhavante. Bhavase, bhavavhe. Bhave, bhavamhe. Keci dīghaṃ katvā paṭhanti, ‘‘byañjane dīgharassā’’ti dīgho, bhavāmhe.</p>\r\n<p class="bodytext">Ettha ca –</p>\r\n<p class="gatha1">Lajjā sattā ṭhiti jāgaraṇaṃ,</p>\r\n<p class="gatha2">Vuddhi kkhaya jīvita maraṇaṃ;</p>\r\n<p class="gatha3">Kīḷā ruci rocate ityevaṃ,</p>\r\n<p class="gathalast">Vuttā akammakadhātu sabbe.</p>\r\n<p class="bodytext">–Ti vuttattā kammaṃ dutiyā na.</p>\r\n<p class="bodytext">Paca=pāke, akāro uccāraṇattho, eva+muparipi. Paca iti ṭhite lavikaraṇaṃ, vikaraṇalopādi purimasamaṃ. So odanaṃ pacati, te pacanti. Tvaṃ pacasi, tumhe pacatha. Ahaṃ pacāmi, mayaṃ pacāma. Paracchakke-pacate, pacante. Pacase, pacavhe. Pace, pacāmhe.</p>\r\n<p class="subhead">1,22. Vippaṭisedhe</p>\r\n<p class="bodytext">Vippaṭisedhanaṃ=aññamaññapaṭisedhanaṃ vippaṭisedho. Paṭhamamajjhimapurisānaṃ dvinnaṃ ekattha pasaṅge majjhimapurisabahuvacanaṃ thapaccayo. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',215,replace(replace('<p class="bodytext">So <a name="M0.0215"></a> ca pacati, tvañca pacasi, tumhe pacatha. Tulyabalavirodhino hi vippaṭisedhā paṭhamamajjhimauttamapurisānaṃ tiṇṇaṃ ekattha pasaṅge uttamapurisabahuvacanaṃ hoti, so ca pacati, tvañca pacasi, ahañca pacāmi mayaṃ pacāmāti bhavati.</p>\r\n<p class="bodytext">Ama+gama=gamane –</p>\r\n<p class="subhead">5,173. Gama yami+sā+sa disānaṃ vā cchaṅa</p>\r\n<p class="bodytext">Etesaṃ dhātūnaṃ cchaṅa vā hoti nta+māna+tyādīsu. So gāmaṃ gacchati. Anti –</p>\r\n<p class="subhead">74. Garupubbā rassā re+ntentīnaṃ</p>\r\n<p class="bodytext">Garupubbasmā rassā nte+ntīnaṃ re vā hoti.</p>\r\n<p class="gatha1">Akkharaniyamo chandaṃ,</p>\r\n<p class="gatha2">Garulahuniyamo bhave vutti;</p>\r\n<p class="gatha3">Dīgho saṃyogādipubbo,</p>\r\n<p class="gathalast">Rasso garu lahu tu rasso.</p>\r\n<p class="bodytext">Gacchare gacchanti. Tvaṃ gacchasi, tumhe gacchatha. Ahaṃ gacchāmi, mayaṃ gacchāma. Cchādesābhāvapakkhe ‘‘ū lasse’’ti ekāre gameti. Paralope gamenti. Gamesi, gametha. Gamemi, gamema. Paracchakkepi so gacchate, gacchare gacchante. Gacchase, gacchavhe. Gacche, gacchāmhe.</p>\r\n<p class="bodytext">Pure adhammo dibbati, purā marāmīti ca taṃsamīpe tabbohārūpacārena vattamānavacanaṃ. Vuttañhi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',216,replace(replace('<p class="gatha1">Āraddhā+niṭṭhitaṃ <a name="M0.0216"></a> kiccaṃ, vattamānanti vuccati;</p>\r\n<p class="gathalast">Vattamānasamīpañca, vattamānanti vuccati.</p>\r\n<p class="bodytext">Kimiva te –</p>\r\n<p class="gatha1">Gaṅgāca taṃsamīpañca, yato gaṅgāti ñāyate;</p>\r\n<p class="gathalast">Gaṅgāyaṃ pātu+māgaccha, ghoso gaṅgāya+mityapi.</p>\r\n<p class="gatha1">Mukhyā+mukhyatthabhedena, vattamānaṃ tato dvidhā;</p>\r\n<p class="gathalast">Mukhyañhi ruḷhi+māpannaṃ, ta+dāropā amukhyatāti.</p>\r\n<p class="bodytext">Pure+purāsaddehi vā anāgatatā gamyate, tadā tassa vattamānattā. Kālabyattayo vā eso, bhavantyeva hi kālantarepi tyādayo bāhulakā ‘‘santesu parigūhāmi’’ ‘‘kāyassa bhedā abhisamparāyaṃ, sahabyataṃ gacchati vāsavassa’’ ‘‘ativelaṃ namassissaṃ’’ti. ‘‘Kuto nu tvaṃ āgacchasi, rājagahato āgacchāmī’’tiādīsu pana paccuppannasamīpe vattamānavacanaṃ.</p>\r\n<p class="subhead">5,175. Gama vada dānaṃ ghamma vajja dajjā</p>\r\n<p class="bodytext">Gamādīnaṃ ghammādayo vā honti nta+māna+tyādīsu. Ghammati, ghammanti iccādi.</p>\r\n<p class="bodytext">Kamme –</p>\r\n<p class="gatha1">Upasaggavasā koci, akammopi sakammako;</p>\r\n<p class="gathalast">Yathā+bhibhūyate rāgo, tāpasena mahiddhināti –</p>\r\n<p class="bodytext">Vuttattā akammakato kammani anupubbā bhūdhātuto tyādayo honti.</p>\r\n<p class="subhead">5,17. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',217,replace(replace('<p class="subhead">Kyo <a name="M0.0217"></a> bhāvakammesva+parokkhesu māna+nta+tyādīsu</p>\r\n<p class="bodytext">Bhāvakammavihitesu parokkhāvajjitesu māna+nta+tyādīsu paresu kyo hoti kriyatthā. Kakāro+nubandhakāriyattho. ‘‘Na te kānubandha+nāgamesū’’ti paṭisedhā okārābhāvo. Anubhūyati sukhaṃ devadattena.</p>\r\n<p class="bodytext">Ettha ca –</p>\r\n<p class="gatha1">Ākhyātena avuttattā, kattuto tatiyā na tu;</p>\r\n<p class="gathalast">Dutiyā hoti kammassa, vuttattā paṭhamāpi+dha.</p>\r\n<p class="bodytext">Anubhūyanti sampattiyo tayā. Anubhūyasi tvaṃ devadattena. Anubhūyatha tumhe. Ahaṃ anubhūyāmi, tayā mayaṃ anubhūyāma. Dvitte anubhuyyati+ccādi. Evaṃ paracchakke.</p>\r\n<p class="bodytext">Bhāve adabbavuttino bhāvassa ekattā ekavacanameva. Tañca paṭhamapurisekavacaneyeva sambhavati, neva+ññaṃ, bahulaṃvidhānā. Bhūyati bhūyate devadattena, sampati bhavanantyattho.</p>\r\n<p class="bodytext">Pacadhātuto kamme kyo.</p>\r\n<p class="subhead">37. Kyassati</p>\r\n<p class="bodytext">Kriyatthā kyassa īña vā hoti. Ño ādyāvayavattho. Devadattena odano pacīyati paccati, reādese odanā pacīyare pacīyanti, paccare paccanti. Cavaggapubbarūpāni. Tvaṃ pacīyasi paccasi, tumhe pacīyatha paccatha. Ahaṃ pacīyāmi paccāmi, mayaṃ pacīyāma paccāma. Paracchakke devadattena odano pacīyate iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',218,replace(replace('<p class="bodytext">Gamito <a name="M0.0218"></a> kamme cchaṅa ca īñaāgame ca kate tena gāmo gacchīyati, gāmā gacchīyanti. Gacchīyasi, gacchīyatha. Gacchīyāmi, gacchīyāma. Evaṃ paracchakke. Gamīyati gammati, gamīyanti gammanti. Gamīyasi gammasi, gamīyatha gammatha, gamīyāmi gammāmi, gamīyāma gammāma paracchakkepi evaṃ. Tathā ghammīyati, ghammīyanti iccādi.</p>\r\n<p class="gatha1">Kriyatthā kattari tyādi, kammasmiñca sakammakā;</p>\r\n<p class="gathalast">Bhāve vā+kammakā kammā+vacanicchāya maññate.</p>\r\n<p class="bodytext">Tathā hi vijjamānassāpi kammāssa avacanicchāyaṃ idaṃ vuccati –</p>\r\n<p class="gatha1">Satopi na vivakkhā+ssa, asatopi ca sā bhave;</p>\r\n<p class="gathalast">Taṃ yathā+nudarā kaññā, vañjhāvaddhitako yathā.</p>\r\n<p class="gatha1">Vivakkhā lokikā esā, asakya+mativattituṃ;</p>\r\n<p class="gathalast">Katha+mesa vipariyāso, loko evā+nayujjate.</p>\r\n<p class="centered">(Vattamānapaccayanayo).</p>\r\n<p class="subhead">2. Bhavissati ssati ssanti ssati ssatha ssāmi ssāma ssate ssante ssase ssavhe ssaṃ ssāmhe</p>\r\n<p class="bodytext">Bhavissati=anāraddhe atthe vattamānato kriyatthā ssatyādayo honti.</p>\r\n<p class="subhead">35. A+ī+ssādīnaṃ byañjanassiña</p>\r\n<p class="bodytext">Kriyatthā paresaṃ aādīnaṃ īādīnaṃ ssādīnañca byañjanassi+ña hoti vibhāsā. Vavatthitavibhāsā+yaṃ. Sseti ssādīnaṃ ssatiādīnañcāvayavo gahito, ‘‘ūbyañjanassā’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',219,replace(replace('<p class="bodytext">siddhepi <a name="M0.0219"></a> tyādīsu etesamevāti niyamattho+ya+mārambho. Lassākāralope o+avādese bhavissati, bhavissanti. Bhavissasi, bhavissatha. Bhavissāmi, bhavissāma. Bhavissate, bhavissante. Bhavissase, bhavissavhe. Bhavissaṃ, bhavissāmhe.</p>\r\n<p class="bodytext">Kamme –</p>\r\n<p class="subhead">49. Kyassa sse</p>\r\n<p class="bodytext">Kyassa vā lopo hoti sse. Sukhaṃ tayā anubhavissati, anubhavissanti. Anubhavissasi, anubhavissatha. Anubhavissāmi, anubhavissāma. Aññatra anubhūyissati, anubhūyissanti iccādi. Evaṃ paracchakke. Bhāve-bhūyissati bhūyissate.</p>\r\n<p class="bodytext">Kattari-pacissati, pacissanti iccādi. Kamme-pacissati odano devadattena, pacīyissati, paccissati. Paccissanti odanā pacīyissanti, pacīyissare, paccissare, paccissanti.</p>\r\n<p class="bodytext">Gamito kattari-so gacchissati, te gacchissanti. Tvaṃ gacchissasi, tumhe gacchissatha. Ahaṃ gacchissāmi, mayaṃ gacchissāma. Gacchissate, gacchissante. Gacchissase, gacchissavhe. Gacchissaṃ, gacchissāmhe. Saggaṃ gamissati, gamissanti. Gamissasi, gamissatha. Gamissāmi, gamissāma iccādi. Kamme-gacchiyissati, gacchiyissanti iccādi. Kyalope gamissati, gamissanti iccādi. Tathā ghammissati ghammīyissati, ghammissanti, ghammīyissanti iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',220,replace(replace('<p class="subhead">3. Nāme garahāvimhayesu</p>\r\n<p class="bodytext">Nāmasadde <a name="M0.0220"></a> nipāte sati garahāyaṃ vimhaye ca gamyamāne ssatyādayo honti. Ime hi nāma kalyāṇadhammā paṭijānissanti. Ettha ‘‘jyādīhi knā’’ti knāvikaraṇe ‘‘ñāssane jā’’ti jādeso. Na hi nāma bhikkhave tassa moghapurisassa pāṇesu anuddayā bhavissati. Kathañhi nāma so bhikkhave moghapuriso sabbamattikāmayaṃ kuṭikaṃ karissati. Vimhaye-acchariyaṃ vata bho, abbhutaṃ vata bho, santena vata bho pabbajitā vihārena viharanti, yatra hi nāma saññī samāno jāgaro pañcamattāni sakaṭasatāni nissāya nissāya atikkantāni neva dakkhiti, na pana saddaṃ sossati. Acchariyaṃ andho nāma pabbata+mārohissati, badhiro nāma saddaṃ sossati. (Bhavissantipaccayanayo).</p>\r\n<p class="subhead">4. Bhūte ī uṃ o ttha iṃ mhā ā ū se vhaṃ a mhe</p>\r\n<p class="bodytext">Bhūte parisamatte atthe vattamānato kriyatthā īādayo honti. Bhūtānajjatane upari vakkhamānattā ime īādayo bhūtajjatane. ‘‘Suvo ahosi ānando’’tiādīsu bhūtasāmaññeva bhavanti.</p>\r\n<p class="gatha1">Ahassu+bhayato aḍḍha-rattaṃ vā tadupaḍḍhataṃ;</p>\r\n<p class="gatha2">Antokatvāna viññeyyo, aho ajjatano iti;</p>\r\n<p class="gathalast">Tadañño pana yo kālo, so+najjatanasaññito.</p>\r\n<p class="bodytext">Iti paṭhamapurisekavacanaṃ ī,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',221,replace(replace('<p class="subhead">15. Ā+ī+ssādīsva+ña vā</p>\r\n<p class="bodytext">Āādo <a name="M0.0221"></a> īādo ssādo ca kriyatthassa vā aña hotīti dhātuto pubbaṃ aña. ‘‘Kattari lo’’ti lo, ekāra+avādesā ca.</p>\r\n<p class="subhead">38. Eyyātha+sse+a+ā+ī+thānaṃ o+a+aṃ+ttha+ttho+vhoka</p>\r\n<p class="bodytext">Eyyāthādīnaṃ oādayo vā honti yathākkamaṃti īssattho, abhavittho, bhavittho, abhavattho, bhavattho. ‘‘Ā ī ū mhā ssā ssamhānaṃ vā’’ti īssa rassattaṃ. So abhavi bhavi, abhavī bhavī.</p>\r\n<p class="subhead">39. Uṃssiṃ+svaṃ+su</p>\r\n<p class="bodytext">Tiuṃssa iṃsu+aṃsu vā honti. Abhaviṃsu bhaviṃsu, abhavaṃsu bhavaṃsu, abhavuṃ bhavuṃ.</p>\r\n<p class="subhead">42. Ossa a+i+ttha+ttho</p>\r\n<p class="bodytext">Ossa aādayo vā honti. Abhava bhava, abhavi bhavi, iñāgame abhavittha bhavittha, abhavattha bhavattha, abhavittho bhavittho, abhavattho bhavattho.</p>\r\n<p class="subhead">43. Si</p>\r\n<p class="bodytext">Ossa si vā hoti. Abhavisi bhavisi, abhavasi bhavasi. Abhavo bhavo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',222,replace(replace('<p class="subhead">45. Mhātthāna+muña</p>\r\n<p class="bodytext">Esaṃ <a name="M0.0222"></a> uña vā hoti. Īādisambandhīnameva gahaṇaṃ. Abhavuttha bhavuttha.</p>\r\n<p class="subhead">46. Iṃssa ca siña</p>\r\n<p class="bodytext">Iṃssa ca siña vā hoti mhātthānañca bahulaṃ. Ikāraññakārā uccāraṇaādyāvayavatthā. Abhavasittha bhavasittha, abhavittha bhavittha, abhavattha bhavattha, abhavisiṃ bhavisiṃ, abhavasiṃ bhavasiṃ, abhaviṃ bhaviṃ. Uñāgame āssa rasso. Abhavumha bhavumha abhavumhā bhavumhā. Siñāgame abhavasimha bhavasimha abhavasimhā bhavasimhā abhavimha bhavimha abhavimhā bhavimhā abhavamha bhavamha abhavamhā bhavamhā. Paracchakke – ‘‘eyyāthā’’dinā tthe abhavittha bhavittha abhavattha bhavattha. Rasse abhavatha bhavatha, abhavā bhavā, abhavu bhavu. Abhavise bhavise, abhavase bhavase, abhavivhaṃ bhavivhaṃ abhavavhaṃ bhavavhaṃ, assa a+mādese abhavaṃ bhavaṃ abhava bhava, abhavimhe bhavimhe abhavamhe bhavamhe. Kamme sukhaṃ tayā anubhūyittho anvabhūyattho anubhūyattho anvabhūsi anubhūsī anvabhūyī anubhūyī, anvabhūyiṃsu anubhūyiṃsu anvabhūyaṃsu anubhūyaṃsu anvabhūyuṃ anubhūyuṃ iccādi. Bhāve-tena abhūyittho bhūyittho abhūyattho bhūyattho abhūyi bhūyi abhūyī bhūyī. Tena abhūyattha bhūyattha abhūya bhūya abhūyā bhūyā.</p>\r\n<p class="bodytext">So apacittho pacittho iccādi kattusamaṃ. Kamme-apacīyittho pacīyittho apacīyattho pacīyattho. Cavaggapubba- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',223,replace(replace('<p class="bodytext">rūpe <a name="M0.0223"></a> apaccittho paccittho apaccattho paccattho apacīyi pacīyi apacīyī pacīyī apacci pacci apaccī paccī, apacīyiṃsu pacīyiṃsu apaciṃsu paciṃsu apacīyaṃsu pacīyaṃsu apaccaṃsu paccaṃsu apacīyuṃ pacīyuṃ apaccuṃ paccuṃ iccādi kattusamaṃ, pubbarūpova viseso.</p>\r\n<p class="bodytext">So gāmaṃ agacchittho gacchittho iccādi purimasamaṃ.</p>\r\n<p class="subhead">30. Ḍaṃsassa ca ñchaṅa</p>\r\n<p class="bodytext">Ḍaṃsassa ca gamissa ca ñchaṅa vā hoti ā+īādīsu. Agañchittho gañchittho agañchattho gañchattho agañchi gañchi agañchī gañchī iccādi.</p>\r\n<p class="subhead">29. Gamissā</p>\r\n<p class="bodytext">Āādo īādo ca gamissa ā hoti vā. So agā agami gami agamittho gamittho agamattho gamattho. Īpaccaye lassākārassa ‘‘himimesva+ssā’’ti ettha ‘‘assā’’ti yogavibhāgā dīghe ‘‘dīghā īssā’’ti īssa siādeso, agamāsi agami gami agamī gamī, agamiṃsu gamiṃsu agamaṃsu gamaṃsu agamuṃ gamuṃ. Ossa aādese ajjhaguṃ. Tvaṃ ajjhagā parassa lopo. Tumhe ajjhaguttha. Ahaṃ ajjhagaṃ, mayaṃ ajjhagamha. Paracchakke – ‘‘eyyāthā’’dinā tthe so agacchittha gacchittha agañchittha gañchittha agamittha gamittha agama gama agamā gamā. Te agamū gamū, ossa āādese ajjhaguṃ. Tvaṃ agamise gamise. Tumhe agamivhaṃ. ‘‘Eyyāthā’’ dinā a+mādese ahaṃ agamaṃ gamaṃ ajjhagaṃ. Mayaṃ agamimhe gamimhe. Kammeagacchīyi agamīyi, agacchiyuṃ agamiyuṃ iccādi. Tathā aghammī ghammī iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',224,replace(replace('<p class="subhead">13. Māyoge ī+āādī</p>\r\n<p class="bodytext">Māyoge <a name="M0.0224"></a> sati īādayo āādayo ca vā honti. Sakakālato kālantarepi paccayavidhānattho+yaṃ. Mā bhavaṃ abhavittho iccādi. Vāvidhānāssatyādi+eyyādi+tvādayopi honti, mā bhavaṃ bhavissati, mā bhavaṃ bhaveyya, mā bhavaṃ bhavatu iccādi. (Īādipaccayanayo).</p>\r\n<p class="subhead">5. Anajjatane ā ūo ttha a mhā ttha tthuṃ se vhaṃ iṃ mhase</p>\r\n<p class="bodytext">Avijjamānajjatane bhūtatthe vattamānato kriyatthā āādayo honti vā. ‘‘Eyyāthā’’dinā tthe ‘‘ā ī ū mhā’’ iccādinā rasse ca so abhavattha bhavattha abhava bhava abhavā bhavā, te abhavu bhavu abhavū bhavū. O, ‘‘ossā’’tiādinā aādayo, abhava bhava abhavi bhavi abhavattha bhavattha abhavattho bhavattho, siādese abhavasi bhavasi abhavo bhavo. Tumhe abhavattha bhavattha. Ahaṃ abhavaṃ bhavaṃ abhava bhava, mayaṃ abhavamha bhavamha abhavamhā bhavamhā. So abhavattha bhavattha. Te abhavatthuṃ bhavatthuṃ. Tvaṃ abhavase bhavase. Tumhe abhavavhaṃ bhavavhaṃ. ‘‘Issa ca siña’’ti siṃ, ahaṃ abhavasiṃ bhavasiṃ abhaviṃ bhaviṃ. Mayaṃ abhavamhase bhavamhase. Kamme-tthe sukhaṃ tayā anubhūyittha, iñāgamābhāve anubhūyattha iccādi. Bhāve-abhūyattha bhūyattha iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',225,replace(replace('<p class="bodytext">So <a name="M0.0225"></a> odanaṃ apacattha pacattha iccādi kattusamaṃ. Kamme-apacīyattha pacīyattha apaccattha paccattha apacīya pacīya apacīyā pacīyā apacca pacca apaccā paccā. Apacīyu pacīyu apacīyū pacīyū apaccu paccu apaccū paccū iccādi. ‘‘Māyoge ī+āādī’’ti māyogepi āādayo, mā bhavaṃ apaccattha iccādi.</p>\r\n<p class="bodytext">Tathā agacchattha gacchattha iccādi purimasamaṃ. Tathā agamattha gamattha iccādipi aghammattha ghammattha iccādipi. (Anajjatanapaccayanayo).</p>\r\n<p class="subhead">6. Parokkhe a u e ttha a mha ttha re ttho vho i mhe</p>\r\n<p class="bodytext">Apaccakkhe bhūtānajjatane vattamānato kriyatthā aādayo honti. Akkhānaṃ=indriyānaṃ paraṃ parokkhaṃ. Tasmiṃ. ‘‘Aparokkhesū’’ti vacanā kya+lādivikaraṇā na honti.</p>\r\n<p class="subhead">5,71. Parokkhāyañca</p>\r\n<p class="bodytext">Parokkhāyaṃ paṭhama+mekassaraṃ saddarūpaṃ dve bhavati. ‘‘Bhūssa vuka’’ itiādīsu vuka, bhūva bhūva iti dvitte –</p>\r\n<p class="subhead">5,75. Lopo+nādibyañjanassa</p>\r\n<p class="bodytext">Dvitte pubbassa ādito+ññassa byañjanassa lopo hoti.</p>\r\n<p class="subhead">18. Pubbassa a</p>\r\n<p class="bodytext">Aādīsu dvitte pubbassa bhūssa a hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',226,replace(replace('<p class="subhead">5,78. Catutthadutiyānaṃ tatiyapaṭhamā</p>\r\n<p class="bodytext">Dvitte <a name="M0.0226"></a> pubbesaṃ catutthadutiyānaṃ tatiyapaṭhamā honti yathākkamaṃti bakāre so babhūva kira, te babhuvu kira. Tvaṃ babhuve kira, iña tumhe babhuvittha kira. Ahaṃ babhuva kira, mayaṃ babhuvimha kira. So babhuvittha kira, te babhuvire kira. Tvaṃ babhuvittho kira, tumhe babhuvivho kira. Ahaṃ babhuvi kira, mayaṃ babhuvimhe kira. Kamme-anupabhuva kira iccādi. Bhāvebabhuva babhuvittha kira.</p>\r\n<p class="bodytext">Papaca, papacu. Papace, iñāgame papacittha. Aññatra ‘‘saṃyogādilopo’’ti lopo, papacittha. Papaca, papacimha. Papacittha, papacire. Papacittho papacitho, papacivho. Papaci, papacimhe. Evaṃ kamme.</p>\r\n<p class="bodytext">Aādipaccaye dvibhāve anādibyañjanalope pubbassa ‘‘kavaggahānaṃ cavaggajā’’ti cavaggajakāre ‘‘byañjane dīgharassā’’ti dīghe ca kate so gāmaṃ jagāma kira, jagamu. Jagame, jagamittha. Jagama, jagamimha. Jagamittha, jagamire. Jagamittho, jagamivho. Jagami, jagamimhe. Evaṃ kamme.</p>\r\n<p class="bodytext">Mūḷhavikkhittabyāsattacittena attanāpi kriyā katā abhinibbattikāle+nupaladdhāsamānā phalenā+numīyamānā parokkhāva vatthuto. Tena uttamavisayepi payogasambhavo. (Parokkhāpaccayanayo).</p>\r\n<p class="subhead">7. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',227,replace(replace('<p class="subhead">Yyodo <a name="M0.0227"></a> vā+tipattiyaṃ ssā ssaṃsu sse ssatha ssaṃ ssamhā ssatha ssiṃsussasessavhessiṃ ssāmhase.</p>\r\n<p class="bodytext">Eyyādo visaye kriyātipattiyaṃ ssādayo honti. Vidhurapaccayopanipātato kāraṇavekallato vā kriyātipatana+manipphatti kriyātipatti. Ete ca ssādayo sāmatthiyā atītānāgatesveva honti, na vattamāne, tatra kriyātipatyasambhavā. Iñāgame ssāssa vā rasse ca sace so paṭhamavaye pabbajjaṃ alabhissa, arahā abhavissa bhavissa abhavissā bhavissā vā, te ca taṃ alabhissaṃsu, arahanto abhavissaṃsu bhavissaṃsu. ‘‘Eyyāthā’’dinā ssessa vā akāre tvaṃ abhavissa bhavisse. Tumhe abhavissatha. Ahaṃ abhavissaṃ. Rasse mayaṃ abhavissamha bhavissamha abhavissamhā bhavissamhā. Paracchakke-so abhavissatha, abhavissiṃsu. Abhavissase, abhavissavhe. Abhavissiṃ, abhavissāmhase.</p>\r\n<p class="bodytext">Kamme-tena sukhaṃ anvabhavissa, anvabhavissaṃsu kyalopo. Anvabhūyissa anvabhūyissā iccādi. Bhāve-tena abhūyissa bhūyissa abhūyissā bhūyissā. Abhūyissatha bhūyissatha.</p>\r\n<p class="bodytext">Evaṃ taṇḍulādisādhanaṃ alabhissa, odanaṃ apacissa pacissa iccādi. Kamme-tena odano apacissa pacissa apacissā pacissā apacīyissa pacīyissa apacīyissā pacīyissā apaccissa paccissa apaccissā paccissā iccādi.</p>\r\n<p class="bodytext">So agacchissa gacchissa agacchissā gacchissā iccādi. Cchādesā+bhāve agamissa gamissa agamissā gamissā iccādi. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',228,replace(replace('<p class="bodytext">Kamme-agacchissa <a name="M0.0228"></a> gacchissa agacchīyissa iccādi. Agamissa gamissa agamīyissa iccādi. Tathā aghammissa iccādi. (Kriyātipattipaccayanayo).</p>\r\n<p class="subhead">8. Hetuphalesve+yya eyyuṃ eyyāsi eyyātha eyyāmi eyyāma etha eraṃ etho eyyāvho eyyaṃ eyyāmhe.</p>\r\n<p class="bodytext">Hetubhūtāyaṃ phalabhūtāyañca kriyāyaṃ vattamānato kriyatthā eyyādayo honti vā. Lavikaraṇavuddhiādi pubbasamaṃ. Parikappe –</p>\r\n<p class="subhead">75. Eyye+yyāse+yyannaṃ ṭe</p>\r\n<p class="bodytext">Eyya+eyyāsi+eyya+miccesa+me vā hoti. So dāni kinnu kho bhave, yadi so paṭhamavaye pabbajeyya, arahā bhaveyya.</p>\r\n<p class="subhead">47. Eyyuṃssuṃ</p>\r\n<p class="bodytext">Eyyu+miccassa ñuṃ vā hoti. Sace saṃkhārā niccā bhavuṃ bhaveyyuṃ, na nirujjheyyuṃ. Yadi tvaṃ bhave bhaveyyāsi. ‘‘Eyyāthā’’ dinā assa okāre tumhe bhaveyyātho bhaveyyātha katha+mahaṃ devo bhaveyyāmi.</p>\r\n<p class="subhead">78. Eyyāmasse+mu ca</p>\r\n<p class="bodytext">Eyyāmassa emu vā hoti u ca. Kinnu kho mayaṃ bhavemu bhaveyyāmu bhaveyyāma. Paracchakke-bhavetha, bhaveraṃ. Bhavetho, bhaveyyāvho. Bhave bhaveyyaṃ, bhaveyyāmhe. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',229,replace(replace('<p class="bodytext">Kamme-sukhaṃ <a name="M0.0229"></a> tayā anubhuyye anubhuyyeyya, anubhuyyuṃ anubhūyeyyuṃ. Tena tvaṃ anubhūye anubhūyeyyāsi, tumhe anubhūyeyyātho anubhūyeyyātha. Tenā+haṃ anubhūyeyyāmi, mayaṃ anubhūyemu anubhūyeyyāmu anubhūyeyyāma. Paracchakke-anubhūyetha iccādi. Bhāve-bhūye bhūyeyya bhūyetha.</p>\r\n<p class="bodytext">Vidhimhi-so odanaṃ pace paceyya iccādi. Kamme-pacīye pacīyeyya pacce pacceyya iccādi.</p>\r\n<p class="bodytext">Anumatiyaṃ-so gāmaṃ gacche gaccheyya iccādi. Aññatra game gameyya iccādi. Tathā ghamme ghammeyya iccādi.</p>\r\n<p class="gatha1">Pāto paceyya ce bhuñje, icce+ttha pacanakriyā;</p>\r\n<p class="gathalast">Hetubhūtāti viññeyyā, phalaṃ tva+nubhavakriyā.</p>\r\n<p class="subhead">11. Sattya+rahesve+yyādī</p>\r\n<p class="bodytext">Sattiyaṃ arahate ca kriyatthā eyyādayo honti. Bhavaṃ khalu pattaṃ paceyya, bhavaṃ satto, bhavaṃ araho.</p>\r\n<p class="subhead">12. Sambhāvane vā</p>\r\n<p class="bodytext">Sambhāvane gamyamāne dhātunā vuccamāne ca eyyādayo honti vibhāsā. Api bhavaṃ gilitaṃ pāsāṇaṃ paceyya udaragginā, sambhāvemi saddahāmi bhavaṃ paceyya, bhavaṃ pacissati, bhavaṃ apaci.</p>\r\n<p class="subhead">9. Pañha+patthanā+vidhīsu</p>\r\n<p class="bodytext">Pañhādīsu kriyatthato eyyādayo honti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',230,replace(replace('<p class="gatha1">Pañhā <a name="M0.0230"></a> saṃpucchanaṃ, iṭṭhā-siṃsanaṃ yācanaṃ duve;</p>\r\n<p class="gathalast">Patthanā, bhattiyā vā+tha, na vā byāpāraṇā vidhi.</p>\r\n<p class="bodytext">Pañhe-kiṃ so bhattaṃ paceyya, udāhu byañjanaṃ. Patthanāyaṃ-aho vata so paceyya ce. Vidhimhi-bhavaṃ pattaṃ paceyya. (Eyyādipaccayanayo).</p>\r\n<p class="subhead">10. Tu antu hi tha mi ma taṃ antaṃ ssu vho e āmase</p>\r\n<p class="bodytext">Pañhapatthanāvidhisve+te honti kriyatthato. Āsiṃsanatthe-so sukhī bhavatu, te sukhitā bhavantu.</p>\r\n<p class="subhead">48. Hissa+to lopo</p>\r\n<p class="bodytext">Ato parassa hissa vā lopo hoti. Tvaṃ sukhī bhava bhavāhi, himhi dīgho. ‘‘Eyyāthā’’dinā vhoka, tumhe sukhitā bhavathavho bhavatha. Ahaṃ sukhī bhavāmi, mayaṃ sukhino bhavāma. Paracchakke-tathā bhavataṃ, bhavantaṃ. Bhavassu, bhavavho. Bhave bhavāmase. Kamme-tayā anubhūyatu, anubhūyantu iccādi. Bhāve-bhūyatu, bhūyataṃ.</p>\r\n<p class="bodytext">Āṇattiyaṃ-devadatto dāni odanaṃ pacatu, pacantu iccādi. Kamme-tena odano paccatu iccādi. Tathā so gāmaṃ gacchatu iccādi. ‘‘Ū lasse’’ti lasse+kāre gametu, gamentu iccādi. Ghammādese ghammatu, ghammantu iccādi. Kamme-gacchīyatu iccādi. Tathā gamīyatu iccādi.</p>\r\n<p class="bodytext">Vidhimhi-lassa lope lutte vuddhi, idha pabbato hotu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',231,replace(replace('<p class="bodytext">Nimantane-adhivāsetha <a name="M0.0231"></a> tumhe bhante bhagavā bhojanaṃ, vasatissa ‘‘curādito ṇī’’ti ṇimhi ‘‘assā ṇānubandhe’’ti ā, ‘‘yuvaṇṇāna+meo paccaye’’ti e ca hoti. Idha nissīdatu bhavaṃ, sadassa ‘‘jara+sadāna+mīma vā’’ti īma, manubandhattā antasarato īma.</p>\r\n<p class="bodytext">Ajjhesane-desetu bhante bhagavā dhammaṃ, ettha curādittā purimasamaṃ.</p>\r\n<p class="bodytext">Anumatiyaṃ-puccha vāsava maṃ pañhaṃ, pavisatu bhavaṃ. Ettha pucchato hissa lopo. Evaṃ nisīda.</p>\r\n<p class="bodytext">Patthanā=yācanā, tatra-dada=dāne, dadāhi me gāmavarāni pañca, dāto lassa ekāre ca ekaṃ me nayanaṃ dehi.</p>\r\n<p class="bodytext">Pattakāle-sampatto te kālo kaṭakaraṇe, kaṭaṃ karotu bhavaṃ. Ettha ‘‘tanāditvo’’ti o.</p>\r\n<p class="gatha1">Tvādī eyyādayo vatta-mānā sampati,+nāgate;</p>\r\n<p class="gathalast">Bhavissati, parokkhādi-cattāro+tītakālikā.</p>\r\n<p class="subhead">5,173. Gama yami+sā+sa disānaṃ vā cchaṅa</p>\r\n<p class="bodytext">Etesaṃ vā cchaṅa hotinta+māna+tyādīsu. So saggaṃ gacchati gameti, gacchanti iccādi. Kamme-gacchīyati gamīyati iccādi. Yama=uparame, parokkhāanajjatanarūpāni sabbattha payoga+manugamma yojetabbāni. Nipubbo, niyacchati niyamati, niyacchanti niyamanti. Saṃpubbo, ‘‘ye saṃssā’’ti ññattaṃ, saññamati, saññamanti. Kamme-niyacchīyati niyamīyati niyammati, saññamīyati vā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',232,replace(replace('<p class="bodytext">Tathā <a name="M0.0232"></a> niyacchissati, saññamissati. Niyacchi, saṃyami. Niyacchissa, saṃyamissa. Niyaccheyya, saṃyameyya. Niyacchatu, saññamatu. Isa+siṃsa=icchāyaṃ, so saggaṃ icchati, icchanti iccādi. ‘‘Lahussupantassā’’ti vuddhimhi esati, esanti iccādi. Kamme-icchīyati, ‘‘vā kvacī’’ti vuddhimhi esīyati. Pubbarūpe issati issate iccādi. Icchissati esissati iccādi. Icchi esi. Icchissa esissa. Iccheyya eseyya. Icchatu esatu iccādi. Āsa=upavesane, so āsane acchati iccādi. ‘‘Byañjane dīgharassā’’ti rasso, upapubbo, tathā upāsati. Acchissati upāsissati. Acchi upāsi. Acchissa upāsissa. Accheyya upāseyya. Acchatu upāsatu iccādi. Disa=pekkhane, dicchati dicchanti iccādi sabbaṃ purimasamaṃ. Labha=lābhe, ito paraṃ visesaṭṭhānamevavakkhāma, sabbavāro vuttānusārena gahetabbo. Labhati, labhanti iccādi. Kamme-kyassa pubbarūpādimhi kate labbhati iccādi.</p>\r\n<p class="subhead">73. Labhā iṃ+īnaṃ thaṃ thā vā</p>\r\n<p class="bodytext">Labhasmā iṃ īnaṃ thaṃ+thā vā honti. So alabhittha labhittha alabhi labhi iṃ paccaye alatthaṃ alabhiṃ iccādi.</p>\r\n<p class="subhead">26. Labha vasa+cchida bhida rudānaṃ cchaṅa</p>\r\n<p class="bodytext">Labhādīnaṃ cchaṅa hoti ssena saha. Lacchati labhissati, lacchanti labhissanti iccādi. Kriyātipattiyaṃ ssābhāgassa cchaṅādese alacchā alabhissa iccādi. Vasa=nivāse, vacchati vasissati. Kamme-bahulādhikārā ‘‘assū’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',233,replace(replace('<p class="bodytext">ukāro<a name="M0.0233"></a>, pubbarūpe vussati vussissati iccādi. Tathā avacchā avasissā. Chida=dvedhākaraṇe, checchati chindissati, ‘‘mañca rudhādīnaṃ’’ti maṃ lo ca. Lassa lopo ca. Bhida=vidāraṇe, bhecchati bhindissati. Abhecchā abhindissā. Ruda=rodane, rucchati rodissati. Arucchā arodissā iccādi.</p>\r\n<p class="bodytext">Aññapaccayepi chidassa ‘‘cchaṅa’’ iti yogavibhāgā cchaṅa, bhūte ūṃ paccaye acchecchuṃ acchindiṃsu. Vuttato aññadhātūnañca, gama=gamane, gacchaṃ gacchissaṃ. Aññapaccayepi vaca=byattavacane iccādi, kamme-bahulādhikārā tyādīsu parabhūtesupi yathāgamaṃ vacādīnañcassa ‘‘vacādīnaṃ vassuṭa vā’’ti kamme uṭa ca ‘‘assū’’ti assa u ca, tena dhammo uccati vuccati, vuccanti iccādi, yassa ca pubbarūpaṃ.</p>\r\n<p class="subhead">27. Bhuja muca vaca visānaṃ kkhaṅa</p>\r\n<p class="bodytext">Bhujādīnaṃ kkhaṅa hoti ssena saha. Vakkhati vacissati, vakkhanti vacissanti iccādi.</p>\r\n<p class="subhead">21. Īādo vacassoma</p>\r\n<p class="bodytext">Īādīsu vacassa oma hoti. Makārānubandhattā ‘‘mānubandho sarāna+mantā paro’’ti paribhāsato sarā paro. Avoci, avocuṃ, avoco iccādi. Anajjatane-avaca avacā vacā iccādi. Kriyātipattiyaṃ-avakkhā avacissā, avakkhiṃsu avacissiṃsu iccādi. Vaceyya iccādi. Kamme-vucceyya iccādi. Tathā vacatu, vuccatu iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',234,replace(replace('<p class="bodytext">Bhuja=pālanajjhohāresu<a name="M0.0234"></a>, ‘‘bhujā’’ dinā kkhavādese bhokkhati bhuñjissati.</p>\r\n<p class="bodytext">Kusa=akkose, āpubbo, tassa rasso ca, akkosati iccādi ñeyyaṃ, lavikaraṇalopavuddhiyo.</p>\r\n<p class="subhead">34. Kusa+ruhehī+ssa chi</p>\r\n<p class="bodytext">Kusā ruhā ca parassa īssa chi vā hoti. Pararūpapaṭhamakkharāni, akkocchi akkosi iccādi. Abhipubbo ruha=rohane, abhirucchi abhiruhi iccādi.</p>\r\n<p class="bodytext">Vaha=pāpuṇane, vahati, vahanti. Kamme-kye ‘‘hassa vipallāso’’ti vipariyāso, tena so vuyhati, ‘‘assū’’ti uttaṃ. Vahissati, vuyhissati. Avahi, avuyhittha, avuhi. Avahissā, vuyhīssā iccādi. Vaheyya, vuyheyya. Vahatu, vuyhatu iccādi.</p>\r\n<p class="bodytext">Jara=jīraṇe,</p>\r\n<p class="subhead">5,174. Jara marāna+mīyaṅa</p>\r\n<p class="bodytext">Jara marānaṃ īyaṅa vā hoti māna+nta+tyādīsu. Jīyati. ‘‘Jara sadāna+mīma’’ti īmaāgamo lo ca, jīrati. Kamme-jīrīyati jīyīyati. Evaṃ jīyissati jīrissati, jīyīyissati jīrīyissati. Ajīyi ajīri, ajīyīyi ajīrīyi. Ajīyissā ajīrissā, ajīyīyissā ajīrīyissā ajīyissā ajīrīyissā iccādi. Mara=pāṇacāge, īyaṅa, mīyati marati, mīyanti maranti iccādi.</p>\r\n<p class="bodytext">Disa=pekkhane,</p>\r\n<p class="subhead">5,124. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',235,replace(replace('<p class="subhead">Disassa <a name="M0.0235"></a> passa dassa dasa da dakkhāti</p>\r\n<p class="bodytext">Ete ādesā disassa vā honti. Anekavaṇṇattā sabbādeso. Passati passanti, dakkhati dakkhanti. Kamme-kye pubbarūpo, dissati dissanti, vipassīyati dakkhīyati iccādi.</p>\r\n<p class="subhead">6,69. Dakkha+kha+heti+hohīti lopo</p>\r\n<p class="bodytext">Dakkhādīhi ādesehi parassa ssassa lopo vā hoti. Dakkhati dakkhissati. Apassi passi, apassiṃsu passiṃsu apasso passo, apassittha passittha. Apassiṃ passiṃ, apassimha passimha. Tathā adassī dassī, adassiṃsu dassiṃsu iccādi. Anajjatane dasādeso, addasā addasa. Addā adda iccādi. Apassissa dakkhissa iccādi. Passeyya dakkheyya, passatu dakkhatu iccādi.</p>\r\n<p class="bodytext">Sada=visaraṇagatyāvasādanādānesu, nipubbo, ‘‘jarasadāna+mīma vā’’ti īmaāgamo. Nisīdati, nisīdanti iccādi. Bhāve-nisajjati, nisajjate.</p>\r\n<p class="bodytext">Yaja=devapūjāsaṃgatikaraṇadānesu, yajati, yajanti iccādi. Kamme –</p>\r\n<p class="subhead">5,113. Yajassa yassa ṭi+yīti</p>\r\n<p class="bodytext">Yajassa yassa ṭi+yī honti kānubandheti bahulādhikārā tyādivisayepi kye pubbarūpe ca ijjati yijjati mayā buddho. So yajissati, tena ijjissate yijjissate. So </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',236,replace(replace('<p class="bodytext">yajī<a name="M0.0236"></a>, tena ijji yijji. So yajissā, tena ijjissā yijjissā. So yajeyya, tena ijjeyya yijjeyya. So yajatu, tena ijjatu yijjatu iccādi.</p>\r\n<p class="bodytext">Vada=vacane, ‘‘gamavadā’’ dinā vadassa vajjādeso vā, lassa ‘‘ūlasse’’ti kvaci e ca, vajjati vadeti vadati, vajjenti vadenti iccādi. Kamme – ‘‘kyassā’’ti īña, vajjīyati vajjati vadīyati, vajjīyanti vajjanti vadīyanti. Vadissati, vadissanti. Avadī vadī, avadiṃsu vadiṃsu. Avadissa vadissa. Vajje vajjeyya vade vadeyya, vajjeyyuṃ vadeyyuṃ. Vajjetu vadetu vadatu iccādi yathāgamaṃ ñeyyaṃ.</p>\r\n<p class="bodytext">Kamu=padavikkhepe, ‘‘parokkhāyañcā’’ti caggahaṇena kama kama iti dvitte anādibyañjanalope lavikaraṇe ‘‘kavaggahā’’ dinā kassa ce niggahītāgame ca kate caṃkamati kamati iccādi. Cala=kampane, caṃcalati. Jala+dala=dittiyaṃ, dvitte daddallati iccādi. (Savuddhikabhūvādinayo).</p>\r\n<p class="bodytext">Hū+bhū=sattāyaṃ, tyādīsu ‘‘kattari lo’’ti lo, tassa lope ‘‘yuvaṇṇāna+meo paccaye’’ti okāro. So hoti, te honti. Hosi, hotha. Homi, homa iccādi. Bhāve-tena hūyati hūyate.</p>\r\n<p class="subhead">31. Hūssa he+hehi+hohi ssatyādo</p>\r\n<p class="bodytext">Hūssa heādayo honti ssaccādo. Hessati hehi ssati hohissati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',237,replace(replace('<p class="subhead">69. Dakkha+kha+hehi+hohīhi lopoti</p>\r\n<p class="bodytext">Dakkhādīhi <a name="M0.0237"></a> ādesehi parassa ssassa vā lopo hoti. Hehiti hehissati, hohiti hohissati. Hehinti hehissanti, hohinti hohissanti iccādi. Anajjatane īmhi añāgamo, ‘‘paro kvacī’’ti īssa lopo, so ahu. Vuddhimhi ‘‘dīghā īssa’’ iti īssa siādeso, suvo ahosi ānando.</p>\r\n<p class="subhead">41. Hūto resuṃ</p>\r\n<p class="bodytext">Hūto parassa u+miccassa resuṃ vā hoti. Rakārānubandho ‘‘rānubandhentasarādissā’’ti antasarādissa lopo. Ahesuṃ. Okāravuddhi avādese ahavuṃ. Ossa siādese ahosi.</p>\r\n<p class="subhead">46. Iṃssa ca siña</p>\r\n<p class="bodytext">Imiccassa siña hoti mhātthānañca bahulaṃ. Ñākāro ākhyāvayavattho (ukāro) ikāro uccāraṇattho. ‘‘Aīssā’’ dinā iñāgame tumhe ahosittha, ahosi. Parassaralope rasse ca ahuṃ, ahosimha, ahumha, rasso. Bhāve-abhavi, āssatthādeso, abhavittha. Anajjatane āūādipaccaye kate ‘‘yuvaṇṇāna+miya ṅuvaṅa sare’’ti uvaṅādese ahuvā, ahuvu, ahuvo, ahuvattha. Assa amādese ahuṃ ahuvaṃ, ahumha iccādi. Bhāve-tena ahuyi ahuyittha. Ahavissā, ahavissaṃsu iccādi. Bhāve-ahuyissa, ahuyissatha. Pubbassaralopo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',238,replace(replace('<p class="bodytext">heyya<a name="M0.0238"></a>, heyyuṃ iccādi. Bhāve-hūyeyya, hūyetha. Hotu, hontu iccādi. Bhāve-hūyatu, hūyataṃ. Anupubbe anubhoti iccādi sabbavāresu yojetabbaṃ. Bhāve rūpābhāvā. Kamme-anubhūyati iccādi viseso.</p>\r\n<p class="bodytext">Si=saye, lassa lopo vuddhi, seti. Ayādese sayati, senti sayanti iccādi. Kamme-atipubbo, kye ‘‘dīgho sarassā’’ti issa dīghe ca kate tena atisīyati, atisīyanti iccādi. Bhāve-tena sīyati, sīyate. Tathā bhavissatiādīsu.</p>\r\n<p class="bodytext">Nī=pāpane, dvikammako+yaṃ, ajaṃ gāmaṃ neti nayati, nenti nayanti iccādi. Kamme-nīyate gāmaṃ ajo devadattena iccādi. Tathā sesesupi yojetabbaṃ.</p>\r\n<p class="bodytext">Ṭhā=gatinivattiyaṃ –</p>\r\n<p class="subhead">5,175. Ṭhāpānaṃ tiṭṭhapivā</p>\r\n<p class="bodytext">Ṭhāpānaṃ tiṭṭhapivāhonti vā nta+māna+tyādīsu. Lassa lope tiṭṭhati, tiṭṭhanti, ṭhāti, ṭhānti.</p>\r\n<p class="subhead">5,131. Pādito ṭhāssa vā ṭhaho kvaciti</p>\r\n<p class="bodytext">Pādito parassa ṭhāssa kvaci ṭhaho hoti vā. Saṇṭhahati, saṇṭhahanti. Lasse, adhiṭṭheti, adhiṭṭhenti. Kamme –</p>\r\n<p class="subhead">5,137. Aññādissā+ssī kye</p>\r\n<p class="bodytext">Ñādito+ññassa ākārantassa kriyatthassa ī hoti kye. Upaṭhīyati, upaṭhīyanti. Ṭhahādese ‘‘kyassā’’ti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',239,replace(replace('<p class="bodytext">īña<a name="M0.0239"></a>, tena patiṭṭhahīyati, patiṭṭhahīyanti. Bhāve-īmhi ṭhīyati, ṭhīyate. Tathā papubbe patiṭṭhissati patiṭṭhahissati. Īssa simhi aṭṭhāsi, aṭṭhaṃsu, saṇṭhahi, saṇṭhahiṃsu. Patiṭṭhissa patiṭṭhahissa. Tiṭṭhe tiṭṭheyya, saṇṭhe saṇṭheyya, saṇṭheyyuṃ, saṇṭhahe saṇṭhaheyya. Tiṭṭhatu ṭhātu, saṇṭhahatu iccādi.</p>\r\n<p class="bodytext">Pā=pāne, pivādese pivati. ‘‘Tavaggavaraṇā’’ do ‘‘bayañā’’ti yogavibhāgena vassa bakāro, pibati. Kammepīyati, pīyanti iccādi.</p>\r\n<p class="bodytext">Asa=bhuvi,</p>\r\n<p class="subhead">52. Tassa thoti</p>\r\n<p class="bodytext">Atthito parassa tassa tho hoti. Pararūpe paṭhamakkharatakāre ca atthi. ‘‘Nta+māna+nti+yi+yuṃsvā+dilopo’’ti assa lopo, santi.</p>\r\n<p class="subhead">53. Sihisva+ṭa</p>\r\n<p class="bodytext">Atthissa aṭa hoti sihisu. Ṭo sabbādesattho. Tvaṃ asi. Pararūpādimhi kate idāni tumhe attha.</p>\r\n<p class="subhead">54. Mimānaṃ vā mhimhā ca</p>\r\n<p class="bodytext">Asasmā paresaṃ mimānaṃ mhimhā vā honti taṃsanniyogena asassa aṭa ca. Amhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',240,replace(replace('<p class="subhead">55. Esu sa</p>\r\n<p class="bodytext">Esu <a name="M0.0240"></a> mimesu asassa so hoti, pararūpabādhanatthaṃ. Asmi, amha asma. Bhavissatipaccaye –</p>\r\n<p class="subhead">5,129. A+ā+ssaādīsu</p>\r\n<p class="bodytext">Parokkhā a-ādo anajjatana ā-ādo kriyātipattissā-ādo bhavissatissatyādo ca atthissa bhu hoti. Ādesavidhānaṃ asassāpayogatthaṃ, kismiñci paccayavisese, tasmā asitabbantiādi na bhavati. Bhavissati, bhavissanti. Abhavā, abhavu. Kriyātipattiyaṃ bhuādese abhavissa, abhavissaṃsu iccādi. Eyyādimhi –</p>\r\n<p class="subhead">50. Atthite+yyādicchannaṃ sa+su+sa+satha+saṃ+sāma</p>\r\n<p class="bodytext">Asa=bhuvi+ccasmā paresaṃ eyyādicchannaṃ sādayo honti yathākkamaṃ. Pararūpe so assa, te assu. Tvaṃ assa, tumhe assatha. Ahaṃ assaṃ, mayaṃ assāma. Tvādimhi so atu, assa lope santhu, ayādese tvaṃ ahi, tumhe attha. Amhi, amha, sādese asmi asma. Bahulādhikārā ajjatane āsi, āsiṃsu, āsuṃ iccādīpi honti.</p>\r\n<p class="bodytext">Brū=vacane,</p>\r\n<p class="subhead">36. Brūto tissīña</p>\r\n<p class="bodytext">Brūto parassa tissa īña vā hoti. Vuddhiavādese lassa lope bravīti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',241,replace(replace('<p class="subhead">5,97. Na brūsso</p>\r\n<p class="bodytext">Brūssa <a name="M0.0241"></a> o na hoti byañjane. Brūti. Uvaṅādese brūvanti.</p>\r\n<p class="subhead">20. Tya+ntīnaṃ ṭa+ṭū</p>\r\n<p class="bodytext">Āhā paresaṃ ti+antīnaṃ ṭa+ṭū honti. Ṭakārā sabbādesatthā. Atoyeva āhā paresaṃ tiantīnaṃ ṭaṭūvidhānañāpakā tiantīsu brūssa āho. Āha, āhu. Brūsi, brūtha. Brūmi, brūma iccādi. Bravissati. Abravi abravī, abravuṃ. Anajjatane abravā, abravu. Parokkhāyaṃ –</p>\r\n<p class="subhead">16. Aādīsvā+ho brūssa</p>\r\n<p class="bodytext">Brūssa āho hoti parokkhāaādīsu. Supine kira so āha, te āhu iccādi. Abravissa. Brave braveyya. Vuddhippaṭisedhe brūtu, bravantu iccādi.</p>\r\n<p class="bodytext">Hana=hiṃsāyaṃ, timhi vikaraṇalope so hanati hanti. Te hananti iccādi. Kamme-ye nassa ññe pubbarūpaṃ, tena haññati, haññanti haññare. Tathā hanissati iccādi.</p>\r\n<p class="subhead">67. Hanā cha+khā</p>\r\n<p class="bodytext">Hanā ssāssa cha+khā vā honti. Paṭihaṃkhāmi paṭihanissāmi. Haṃchema hanissāma. Ahani, ahaniṃsūtiādi sabbattha yojetabbaṃ. Huvādayo</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',242,replace(replace('<p class="bodytext">Hu=havane<a name="M0.0242"></a>. Tyādīsu lo. ‘‘Parokkhāyañcā’’ti caggahaṇena hu huti dvittaṃ, ‘‘kavaggahānaṃ cavaggajā’’ti hassa jo, lalopo vuddhi ca, juhoti aggiṃ, aññatra ‘‘yavā sare’’ti vakāro, juhvati. Juhonti. Juhvanti. Juhosi juhvasi, juhotha juhvatha. Iccādi. Kamme – ‘‘dīgho sarassā’’ti dīghe hūyati tena aggi iccādi. Juhissati, juhissanti. Ajuhavi ajuhosi, ajuhavuṃ ajuhaviṃsu ajuhavaṃsu ajuhosuṃ. Ajuhissa, ajuhissaṃssu. Juhe juheyya. Juhotu, juhontu iccādi.</p>\r\n<p class="bodytext">Hā=cāge, pure viya dvebhāvachādesalope ‘‘rasso pubbassā’’ti pubbassa āssa rasso. Jahāti iccādi. Kamme – ‘‘aññādissā+ssī kye’’ti kye āssa ī, hīyati iccādi. Jahissati. Ajahāsi, ‘‘dīghā īssa’’ iti si, pajahi, ajahiṃsu ajahaṃsu, pajahiṃsu pajahuṃ. Kamme-pahīyi pahīyittha. Ssādi+eyyādi+tvādīsupi ñeyyā.</p>\r\n<p class="bodytext">Dā=dāne, dvibhāvarassattāni, lalopo, dadāti, dadanti iccādi. ‘‘Gamavadā’’ dinā dajjādese dajjati, dajjanti iccādi. Lasse+kāre dānaṃ deti iccādi.</p>\r\n<p class="subhead">22. Dāssa daṃ vā mimesva+dvitte</p>\r\n<p class="bodytext">Advitte vattamānassa dāssa daṃ hoti vā mimesu. Vaggantaṃ, dammi demi, damma dema. Īkāre dīyati iccādi. Anāgatatthe iñāgame saraṇopādi, dadissati, dadissanti. Dajjissati, dajjissanti. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',243,replace(replace('<p class="bodytext">Lavikaraṇe <a name="M0.0243"></a> dassati, dassanti. Ajjatane-adadi, adadiṃsu, adajji, adajjiṃsu, adāsi, adaṃsu. Anajjatane-adadā, adadu. Kālātipattiyaṃ-adadissa adajjissa, le adassa, dassaṃsu. Eyyādimhi-dade dadeyya dajje dajjeyya. ‘‘Ṭā’’ti bahulādhikārā dajjādesā parassa eyyassa ṭā, dajjā, dajjuṃ dajjeyyaṃ dadeyyuṃ. Tvādīsu-dadātu, dadantu, dajjatu, dajjantu, lasse+kāre detu, dentu iccādi. Sabbattha kammepi yojanīyaṃ.</p>\r\n<p class="bodytext">Dhā=dhāraṇe, tyādimhi caggahaṇena dvitte pubbākārassa rassatte lalope ‘‘catutthadutiyānaṃ tatiyapaṭhamā’’ti pubbadhakārassa dakāre ca kate dadhāti, dadhanti. Pipubbo, ‘‘tadaminā’’ dinā āssa lopo, ‘‘dhāssa ho’’ti dvitte parassa dhāssa hakāro, dvāraṃ pidahati, pidahanti. Lasse+kāre nidheti, nidhenti. Kamme-vidhīyati, vidhīyanti iccādi. Dhassati, pidahissati, paridahessati. Adhāsi, pidahi. Adhassa, pidahissa. Dadhe dadheyya, pidahe pidaheyya. Dadātu, pidahatu, nidhetu, nidhentu iccādi. (Juhotyādayo).</p>\r\n<p class="gatha1">Bhūvādi ca juhādi ca, huvādidhātavo ci+me;</p>\r\n<p class="gathalast">Savuddhikabhūvādīhi, tidhā bhedaṃ upenti te.</p>\r\n<p class="centered">(Bhūvādinayo.)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',244,replace(replace('<p class="bodytext">Adhunā <a name="M0.0244"></a> vikaraṇapabhedapakāsanatthaṃ rudhādīnaṃ aṭṭhagaṇānaṃ kānici rūpāni udāhariyante –</p>\r\n<p class="bodytext">Rudha=āvaraṇe, tyādayo honti. Eva+muparipi sabbagaṇesu.</p>\r\n<p class="subhead">5,19. Mañca rudhādīnaṃ</p>\r\n<p class="bodytext">Rudhādiko aparokkhesu kattuvihitamāna+nta+tyādīsu lo hoti mañca+ntasarā paro. Niggahītassa vaggantaṃ, ‘‘lahussupantassā’’ti saṃyogattā na vuddhi. So maggaṃ rundhati, rundhanti iccādi. Kamme-nipubbo kyo dhassa cavagga+pubbarūpa+tatiyakkharajakārā, tena maggo nirujjhati iccādi. Iñāgame rundhissati, nirujjhissati. Arundhi, arundhiṃsu. Nirujjhittho, nirujjhi, nirujjhiṃsu. Arundhissa, arundhissaṃsu, nirujjhissa, nirujjhissaṃsu. Rundhe rundheyya, nirujjhe nirujjheyya. Rundhatu, rundhantu. Tvaṃ rundha rundhāhi. Nirujjhatu, nirujjhantu iccādi.</p>\r\n<p class="bodytext">Chida=dvedhākaraṇe, chindati, chindanti. Kamme-chijjati, chijjanti. Bhavissatipaccaye – ‘‘labha vasacchidā’’ dinā cchaṅādese checchati chindissati. Kamme-pubbarūpaṃ, chijjissati, chijjissanti. Achindi chindi, acchejji, acchejjiṃsu. Acchindissā achijjissā. Chinde chindeyya, chijje chijjeyya, chindatu, chindantu, chijjatu, chijjantu iccādi.</p>\r\n<p class="bodytext">Bhuja=pālanajjhohāresu, bhuñjati, bhuñjanti iccādi. Bhavissatimhi ‘‘bhuja+muca+vaca+visānaṃ kkhaṅa’’ti dhātvantena saha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',245,replace(replace('<p class="bodytext">ssassa <a name="M0.0245"></a> kkhaṅādese vuddhi, bhokkhati, bhuñjissati, bhokkhanti, bhuñjissanti iccādi.</p>\r\n<p class="bodytext">Muca=mocane, muccati, muccanti iccādi. Kamme-muccati, muccanti iccādi. (Rudhādinayo).</p>\r\n<p class="bodytext">Diva=kīḷā vijigiṃsā vohāra juti thuti gatīsu,</p>\r\n<p class="subhead">5,21. Divādīhi yaka</p>\r\n<p class="bodytext">Divādīhi kattari lavisaye yaka hoti. Kakāro kānubandhakāriyattho, eva+mupari ca. Vassa bakāre pubbarūpaṃ, so jutaṃ dibbati, dibbanti iccādi. Kamme-kye tena dibbati, dibbanti iccādi. Dibbissati, tena dibbissati. Adibbi dibbi. Tena adibbi dibbi. Adibbissa. Tena adibbissa. Dibbe dibbeyya. Tena dibbe dibbeyya. Dibbatu. Tena dibbatu iccādi.</p>\r\n<p class="bodytext">Siva=tantusantāne, sibbati, sibbanti iccādi. Pada=gamane, upubbo, cavaggapubbarūpāni, uppajjati, uppajjare uppajjanti iccādi. Kammekye tena paṭipajjate iccādi. Bhāve-uppajjate tayā. Budha=avagamane, jhakārapubbarūpāni yakkharesu. Dhammaṃ bujjhati, bujjhare bujjhanti iccādi. Kamme-mayā dhammo bujjhate iccādi. Yudha=sampahāre, yujjhati+ccādi. Naha=bandhane, ‘‘hassa vipallāso’’ti ye vipallāso, sannayhati+ccādi. Mana=ñāṇe, ññakāra+pubbarūpāni, maññati+ccādi. Tusa=tuṭṭhimhi, pubbarūpe tussati+ccādi. Sama=upasame, sammati+ccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',246,replace(replace('<p class="bodytext">Jana=janane<a name="M0.0246"></a>, ‘‘janissā’’ti ā hoti kānubandhe bahulādhikārā, so jāyati, jāyanti+ccādi. Kamme-janituṃ payojetīti ‘‘payojakabyāpāre ṇāpi ce’’ti ṇimhi ‘‘aññatrāpī’’ti vuddhipaṭisedhe kyamhi ‘‘dīgho sarassā’’ti dīghe ca kate tena janīyati, janīyanti iccādi. So jāyissati. Tena janissati. So ajāyi, tena ajanīyi. So ajāyissa, tena ajanīyissa. So jāye jāyeyya, tena janīyeyya. So jāyatu, tena janīyatu iccādi. (Divādinayo).</p>\r\n<p class="bodytext">Tuda=byathane,</p>\r\n<p class="subhead">5,22. Tudādīhi ko</p>\r\n<p class="bodytext">Tudādīhi kattari lavisaye ko hoti. Tudati, tudanti iccādi. Kamme-tudyati tujjati, tudyare tudyanti, tujjare tujjanti+ccādi. Tathā tudissati tujjissati. Atudi, atudiṃsu, atujji. Atudissa atujjissa, tudeyya tujjeyya. Tudatu, tudantu, tujjatu iccādi.</p>\r\n<p class="bodytext">Visa=pavesane, papubbo. So gāmaṃ pavisati iccādi. Kamme-pavisīyati iccādi. Pavisissati. Pavisīyissati. Pāvisi, īmhi ‘‘bhujamucā’’ dinā yogavibhāgā kkhaṅa. Pāvisi, pavisi pāvekkhi, pathaviṃ pāvisiṃsu pavisiṃsu. Pāvisīyi. Pāvisissa pavisissa. Pāvisīyissa. Pavise paviseyya, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',247,replace(replace('<p class="bodytext">vikaraṇalopo <a name="M0.0247"></a> pavisīyeyya. Pavisatu, pavisīyatu iccādi. Nudati. Disa=uccāraṇe, uddissati, likhati, tusati iccādi. (Tudādinayo).</p>\r\n<p class="bodytext">Ji=jaye,</p>\r\n<p class="subhead">5,23. Jyādīhi knā</p>\r\n<p class="bodytext">Jyādīhi kattari lavisaye knā hoti. Na vuddhi. Kilese jināti, jinanti. Tena kileso jīyati, jīyanti, kye dīgho. Jinissati, jinissanti. Ajini jini. Ajīyi, ajīyittha. Ajinissa. Ajīyissa. Jineyya. Jinātu iccādi. Ci=caye, tathā cināti, cinanti iccādi.</p>\r\n<p class="bodytext">Ñā=avabodhane,</p>\r\n<p class="subhead">5,120. Ñāssa ne jā</p>\r\n<p class="bodytext">Ñādhātussa jā hoti nakāre. Vijānāti.</p>\r\n<p class="subhead">61. Ñāssa sanāssa nāyo timhi</p>\r\n<p class="bodytext">Sanāssa ñāssa nāyo vā hoti timhi. Nāyati, vijānāti iccādi. Kamme-viññāyati iccādi. Kye ‘‘āsse cā’’ti ettha ‘‘āsse’’ti yogavibhāgā āssa e hoti, yassa dvitte ñeyyati, ñeyyanti iccādi. Vijānissati, vijānissanti. Kamme – ‘‘kyassa sse’’ti kyalope rasse ca kate ñāssati, ñāssanti, viññāyissati, paññāyissati.</p>\r\n<p class="subhead">65. Ssassa hi kamme</p>\r\n<p class="bodytext">Ñāto parassa ssassa hi vā hoti kamme, paññāyihiti, paññāyihinti. Añāgame samajāni, saṃjāni, saṃjāniṃsu. Samajā- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',248,replace(replace('<p class="bodytext">niṃsu<a name="M0.0248"></a>. Kamme-paññāyi, paññāyiṃsu. Ajānissa. Kamme-aññāyissa.</p>\r\n<p class="subhead">63. Eyyassi+yāñā vā</p>\r\n<p class="bodytext">Ñāto parassa eyyassa iyā+ñā honti vā. Jāniyā.</p>\r\n<p class="subhead">62. Ñāmhi jaṃ</p>\r\n<p class="bodytext">Ñādese sanāssa ñāssa jaṃ vā hoti. Jaññā, jāneyya, jāneyyuṃ. Kamme-paññāyeyya, paññāyeyyuṃ. Vijānātu, vijānanthu. Kamme-viññāyatu iccādi.</p>\r\n<p class="bodytext">Mā=māne, ‘‘ā ī ssādīnaṃ byañjanassiña’’ iti iñaiti yogavibhāgā iñāgame pubbasaralopo, mināti, minanti. Kamme-mīyati iccādi. Lū=chedane, ‘‘kṇāknāsu rasso’’ti dhātussa rasso, lunāti, lunanti. Kamme-lūyati iccādi. Dhu=kampane, dhunāti iccādi. (Jyādinayo).</p>\r\n<p class="bodytext">Kī=dabbavinīmaye,</p>\r\n<p class="subhead">24. Kyādīhi kṇā</p>\r\n<p class="bodytext">Kyādīhi lavisaye kṇā hoti. ‘‘Kṇāknāsu rasso’’ti rasse kiṇāti, kiṇanti iccādi. Kamme-vikkīyati, vikkīyanti. Vikkiṇissati, vikkiṇissanti. Vikkīyissati, vikkīyissanti. Akiṇi, vikkiṇi, vikkīyi. Akiṇissa, vikkiṇissa. Vikkiṇe vikkiṇeyya.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',249,replace(replace('<p class="bodytext">Vikkīye<a name="M0.0249"></a>. Vikkīyeyya. Vikkiṇātu, vikkiṇantu, vikkīyatu, vikkīyantu iccādi. Su=savane, suṇāti, suṇanti iccādi.</p>\r\n<p class="bodytext">Saka=sattiyaṃ,</p>\r\n<p class="subhead">5,121. Sakā+pānaṃ kuka+ku ṇe</p>\r\n<p class="bodytext">Saka+āpānaṃ kuka+kuiccete āgamā honti ṇe. Sakkuṇāti, sakkuṇanti. Sakkunāti, sakkunanti.</p>\r\n<p class="subhead">53. Sse vā</p>\r\n<p class="bodytext">Sakasmā kṇāssa kkho vā hoti sse. Sakkhissati, sakkhissanti.</p>\r\n<p class="subhead">58. Sakā kṇāssa kha īādo</p>\r\n<p class="bodytext">Sakasmā kṇāssa kho vā hoti īādīsu. Asakkhi sakkhi, sakkhiṃsu. Asakkhissa, asakkhissaṃsu. Sakkuṇe sakkuṇeyya. Sakkuṇātu, sakkuṇantu.</p>\r\n<p class="bodytext">Apa=pāpuṇane, papubbo, ‘‘sakā+pānaṃ kuka+ku ṇe’’ti kukate sampatti pāpuṇāti, pāpuṇanti iccādi. Kammepāpīyati, pāpīyanti. Pāpuṇissati, pāpuṇissanti. Kamme-pāpīyissati, pāpīyissanti. Pāpuṇi, pāpuṇiṃsu. Kamme-pāpīyi, pāpīyittha. Apāpuṇissa. Kamme-apāpīyissa. Pāpuṇe, pāpuṇeyya. Kamme-pāpīyeyya. Pāpuṇātu, pāpuṇantu. Kamme-pāpīyatu iccādi. (Kyādinayo).</p>\r\n<p class="bodytext">Su=savane,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',250,replace(replace('<p class="subhead">25. Svādīhi kṇo</p>\r\n<p class="bodytext">Suādīhi <a name="M0.0250"></a> lavisaye kṇo hoti. Kānubandhattā na vuddhi. Dhammaṃ suṇoti. Parassaralope suṇonti. Kamme-kye dīghe sūyati, sūyanti. Dvitte suyyati suyyanti+ccādi. Iñāgame suṇissati, suṇissanti. Kamme-kyalope vuddhi. Sossati, sossanti iccādi. Asuṇi suṇi, asuṇiṃsu suṇiṃsu.</p>\r\n<p class="subhead">60. Tesu suto kṇo+kṇānaṃ roṭa</p>\r\n<p class="bodytext">Tesu īādīsu suto paresaṃ kṇo+kṇānaṃ roṭa vā hoti. Rakāro anubandho. Ṭo sabbādesattho. Ussa lope dvittaṃ īssa si ca. Assosi, assosiṃsu. ‘‘Iṃssa ca siña’’ti yogavibhāgā siña, assosiṃsu, paccassosuṃ. Asuyi, asuyittha. Asuṇissa, tena asuyissa. Suṇe suṇeyya, suṇeyyaṃ. Tena sūye sūyeyya. Suṇātu, suṇantu. Sūyatu, sūyantu iccādi.</p>\r\n<p class="bodytext">Gi=sadde, giṇoti. Giṇanti. Tvaṃ giṇosi. Giṇissati, giṇissanti. Agiṇissa. Giṇeyya. Giṇotu iccādi. Vu=saṃvaraṇe, āvuṇoti, āvuṇanti. Āvuṇosi iccādi. Sesesupi yojetabbaṃ. (Svādinayo).</p>\r\n<p class="bodytext">Tana=vitthāre,</p>\r\n<p class="subhead">26. Tanāditvo</p>\r\n<p class="bodytext">Tanādito kattari lavisaye o hoti. Kittiṃ tanoti, tanonti iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',251,replace(replace('<p class="subhead">76. Ovikaraṇassu paracchakke</p>\r\n<p class="bodytext">Ovikaraṇassa <a name="M0.0251"></a> u hoti paracchakkavisaye. Tanute, tanvante. Tanuse, tanuvhe. Tanve, tanvamhe. Kamme –</p>\r\n<p class="subhead">5,138. Tanassā vā</p>\r\n<p class="bodytext">Tanassa vā ā hoti kye. Papubbo, patāyati. Pataññati, patāyare patāyanti pataññare pataññanti+ccādi. Tathā vikaraṇalope iñāgame ca tanissati, tanissanti. Patāyissati. Atani, ataniṃsu. Atanissa. Patāyissa. Taneyya, taneyyuṃ. Tanotu, tanontu iccādi.</p>\r\n<p class="bodytext">Kara=karaṇe,</p>\r\n<p class="subhead">5,177. Karassa sossa kubba+kuru+kayirā</p>\r\n<p class="bodytext">Karassa sa okārassa kubbādayo vā honti nta+māna+tyādīsu. Kubbati, kubbanti. Karoti, karonti. Karosi, karotha.</p>\r\n<p class="subhead">23. Karassa sossa kuṃ</p>\r\n<p class="bodytext">Karassa sa okārassa kuṃ vā hoti mimesu. Kummi karomi. Kumma karoma. Paracchakke kurute kubbate, kubbante iccādi. Kayirati, kayiranti iccādi. Kamme-kyassa dvitte īssa rasso, kariyyati, karīyati vā iccādi. ‘‘Tavaggavaraṇā’’ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',252,replace(replace('<p class="bodytext">dinā <a name="M0.0252"></a> ye rassa yakāre kayyati, kayyanti. Bahulādhakārā kamme kvaci iminā kayirādese tena kayirati, kayiranti iccādi. Bhavissatimhi –</p>\r\n<p class="subhead">25. Hāssa cā+haṅa ssena</p>\r\n<p class="bodytext">Karassa sossa hāssa ca āhaṅa vā hoti ssena saha. Kāhati, kāhanti iccādi. Iñāgame kāhiti, kāhinti iccādi. Āhaṅādesābhāvapakkhe karissati, karissanti iccādi. Ajjatane –</p>\r\n<p class="subhead">24. Kā īādīsu</p>\r\n<p class="bodytext">Karassa sa okārassa kā hoti vā īādīsu.</p>\r\n<p class="subhead">44. Dīghā īssa</p>\r\n<p class="bodytext">Dīghato parassa īssa si vā hoti. Akāsi. Īlope akā, akaṃsu. Ossa siādese akāsi. Siñāgame akāsittha. Akāsi, akāsiṃ, akāsimha. Aññatra akarikari, akariṃsu kariṃsu akaṃsu iccādi. Tena akarīyi iccādi. Akarissa iccādi. Kamme-akarīyissa iccādi. Eyyādimhi ‘‘kvaci vikaraṇānaṃ’’ti ovikaraṇalope karekareyya, kareyyuṃ iccādi. Kubbe kubbeyya iccādi.</p>\r\n<p class="subhead">71. Ṭā</p>\r\n<p class="bodytext">Kayirā parassa eyyassa ṭā hoti. So kayirā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',253,replace(replace('<p class="subhead">70. Kayire+yyasse+yyumādīnaṃ</p>\r\n<p class="bodytext">Kayirā <a name="M0.0253"></a> parassa eyyumādīnaṃ eyyassa lopo hoti. Kayiruṃ. Tvaṃ kayirāsi, kayirātha. Kayirāmi, kayirāma.</p>\r\n<p class="subhead">72. Ethassā</p>\r\n<p class="bodytext">Kayirā parasse+thassa ā hotītiādissa essa ā hoti. Kayirātha dhīro. Kamme-kariyeyya, kariyeyyu+miccādi. Karotu kurutu vā, kubbantu karontu iccādi. Paracchakke-kurutaṃ, kubbantaṃ. Kurussu karassu iccādi. ‘‘Eona+ma vaṇṇe’’ti akāro. Kuruvho, kubbe, kubbāmase. Kamme-karīyatu iccādi.</p>\r\n<p class="subhead">5,133. Karotissa kho</p>\r\n<p class="bodytext">Pādito parassa karassa kvaci kha hoti. Abhisaṅkharoti iccādi. Sabbattha yojetabbaṃ. ‘‘Tadaminā’’dinā khādese abhisaṃkhāsīti viseso. Saka=sattiyaṃ, sakkoti iccādi. Apa=pāpuṇane, papubbo, pappoti iccādi. (Tanādinayo).</p>\r\n<p class="bodytext">Cura=theyye,</p>\r\n<p class="subhead">1,15. Curādito ṇi</p>\r\n<p class="bodytext">Curādīhi kriyatthehi sakatthe ṇi paro hoti. ‘‘Ṇiṇāpyāpīhi vā’’ti vikappena lo. Dhanaṃ corayati coreti iccādi. Kamme-corīyati iccādi. Corayissati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',254,replace(replace('<p class="bodytext">coressati <a name="M0.0254"></a> iccādi. Kamme-corīyissati iccādi. Acorayi corayi acoresi coresi iccādi. Acorayissa iccādi. Kamme-acorīyissa iccādi. Coraye corayeyya iccādi. Coretu corentu iccādi.</p>\r\n<p class="bodytext">Cinta=cintāyaṃ, saṃyogattā na vuddhi, cinteti cintayati, cintenti. Kamme-cintīyati, cintīyanti iccādi. Manta=guttabhāsane, manteti mantayati iccādi. Pāla=rakkhaṇe, so dhammaṃ pāleti pālayati. Tena pālīyati+ccādi. (Curādinayo).</p>\r\n<p class="gatha1">Bhūvādi ca rudhādi ca, divādi ca tudādayo;</p>\r\n<p class="gathalast">Jyādī kiyādī svādī ca, tanādī ca curādayo.</p>\r\n<p class="centered">(Vikaraṇavidhānaṃ).</p>\r\n<p class="subhead">5,1. Tija+mānehi kha+sā khamā+vīmaṃsāsu</p>\r\n<p class="bodytext">Khantiyaṃ tijā, vīmaṃsāyaṃ mānā ca kha+sapaccayā honti yathākkamaṃ. Tija=nisāne, akārassā+payogo. Khe ‘‘lahussupantassā’’ti patte+kārassa ‘‘aññatrāpī’’ti paṭisedho. Yakāravajjitabyañjanassa ‘‘pararūpa+mayakāre byañjane’’ti pararūpañca. ‘‘Catutthadutiyā’’ dinā paṭhamakkharakakāre ca ‘‘khachasāna+mekassaro+di dve’’ti tikkha tikkha iti dvibhāve ‘‘lopo+nādibyañjanassā’’tiādito+ññassa byañjanassa lope ca kate titikkhādhātuto tyādipaccaya+lavikaraṇāni. Titikkhati iccādi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',255,replace(replace('<p class="bodytext">purimasamaṃ<a name="M0.0255"></a>. Kamme-titikkhīyati iccādi. Ito paraṃ kammodāharaṇaṃ na karissāma.</p>\r\n<p class="bodytext">Māna=pūjāyaṃ+timasmā sapaccaya, dvittādimhi kate ‘‘mānassa vī parassa ca maṃ’’ti pubbamānassa vī ca paramānassa mañca hoti. Vīmaṃsadhātuto tipaccayādimhi kate vīmaṃsati iccādi. Titikkhissati, vīmaṃsissati iccādi. Eva+muparipi ajjatanādīsupi yojetabbaṃ. Payojakattā ṇi, ‘‘ṇiṇāpyāpīhi vā’’ti lavikaraṇaṃ, tejayati tejeti. Tathā curādittā na lavikaraṇaṃ, mānayati māneti.</p>\r\n<p class="subhead">5,2. Kitā tikicchā+saṃsayesu cho</p>\r\n<p class="bodytext">Tikicchāyaṃ saṃsaye ca vattamānā kitā cho hoti. Kita=nivāse, chapaccaye pubbeva pararūpādimhi ca kate ‘‘kitassā+saṃsaye vā’’ti dvitte pubbassa tiādese tikicchadhātuto tyādayo. Tikicchati, tikicchanti+ccādi. Vipubbato kitā chappaccayādimhi kate ‘‘kavaggahānaṃ cavaggajā’’ti dvitte pubbassa kassa co. Vicikicchati, vicikicchanti+ccādi. Payojakattā ketayati+ccādi purimasamaṃ.</p>\r\n<p class="subhead">5,3. Nindāyaṃ gupa+badhā bassa bho ca</p>\r\n<p class="bodytext">Nindāyaṃ gupa+badhehi cho hoti bassa bho ca. Gupa=rakkhane+tīmasmā chappaccaye ‘‘aññatrāpī’’ti ottābhāve ca pararūpādimhi ‘‘kavaggahā’’ dinā gassa je ca kate ‘‘gupissussā’’ti dvitte pubbassa ussa i hoti. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',256,replace(replace('<p class="bodytext">Tyādi+lavikaraṇādimhi <a name="M0.0256"></a> jigucchati iccādi. Nindāyaṃ-badha=bandhane+tīmasmā chappaccaye ca imināva bassa bhakāre ca pararūpe paṭhamakkhare bhacchabhacchaiti dvitte anādibyañjanassa lope ‘‘catutthadutiyānaṃ tatiyapaṭhamā’’ti dvitte pubbassa bhassa bakāre ‘‘khachasesvassī’’ti ikāre dīghe ca kate bībhacchadhātuto tyādayo honti. Bībhacchati, bībhacchanti iccādi. Aññatra gopeti+ccādi.</p>\r\n<p class="subhead">5,4. Tuṃsmā lopo ci+cchāyaṃ te</p>\r\n<p class="bodytext">Tumantato icchāya+matthe te kha+sa+chā honti bahulaṃ, tuṃpaccayassa lopo ca hoti, sutattā. Bhuja=pālana+jjhohāresu. Bhojanāyāti viggayha ‘‘tuṃ+tāye’’ccādinā tuṃpaccaye ‘‘lahussupantassā’’ti okāre pararūpe ca kate bhottu+micchatīti viggayha iminā khapaccaye tuṃpaccayassa iminā ca lope ‘‘nimittābhāve nemittikassāpi ca abhāvo’’ti ñāyā pararūpaokārānaṃ abhāve khassa pararūpa+khakārādimhi ca kate bhukkha bhukkha iti dvitte tatiyabakāro hoti, bubhukkhadhātuto tyādayo honti, bubhukkhati, bubhukkhanti iccādi.</p>\r\n<p class="bodytext">Ji=jaye, jayanāya iti viggayha pure viya tumādimhi kate jetu+micchatīti viggayha sappaccaye jisa jisa iti dvitte anādibyañjanalope dvitte parassa jissa ‘‘jiharānaṃ giṃ’’ti giṃ. Jigiṃsadhātuto tyādīsu jigiṃsati, jigiṃsanti iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',257,replace(replace('<p class="bodytext">Ghasa=adane<a name="M0.0257"></a>, ghasitu+micchati chappaccayādimhi pure viya kate ghassa ‘‘kavaggahā’’dinā jhe jhassa ‘‘catutthadutiyā’’ dinā jakāre ‘‘khachasesvassī’’ti ikāre ca kate jighacchadhātuto tyādayo honti. Jighacchati, jighacchanti+ccādi.</p>\r\n<p class="subhead">5,5. Īyo kammā</p>\r\n<p class="bodytext">Icchākammato icchāya+matthe īyapaccayo hoti. Putta+micchatīti īyapaccaye īyādivuttittā ‘‘ekatthatāyaṃ’’ti vibhattilopo. Puttīyati, puttīyanti+ccādi.</p>\r\n<p class="subhead">5,6. Upamānā+cāre</p>\r\n<p class="bodytext">Kammato upamānā ācāratthe īyo hoti. Putta+mivā+carati puttīyati sissaṃ, puttīyanti+ccādi.</p>\r\n<p class="subhead">5,7. Ādhārāti</p>\r\n<p class="bodytext">Īyo hoti. Kuṭiya+mivā+carati kuṭīyati pāsāde. Pāsādevā+carati pāsādīyati kuṭiyaṃ, pāsādīyanti+ccādi.</p>\r\n<p class="subhead">5,8. Kattutā+yo</p>\r\n<p class="bodytext">Kattuto+pamānā ācāratthe āyo hoti. Pabbato ivā+carati sīlādiguṇayogatoti pabbatāyati yogī, pabbatāyadhātuto tyādayo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',258,replace(replace('<p class="subhead">5,9. Cyatthe</p>\r\n<p class="bodytext">Kattuto <a name="M0.0258"></a> abhūtatabbhāve āyo hoti bahulaṃ. Bhusoti paṭhamantato abhuso bhuso bhavatīti bhusāyati, bhusāyanti iccādi. ‘‘Vicchābhikkhaññesu dve’’ti ābhikkhaññatthe dvitte apaṭapaṭā paṭapaṭā bhavatīti āye paṭapaṭāyati, paṭapaṭāyanti+ccādi. Alohito lohito bhavati lohitāyati.</p>\r\n<p class="subhead">5,10. Saddādīni karoti</p>\r\n<p class="bodytext">Saddādīhi dutiyantehi karotīti asmiṃ atthe āyo hoti. Saddaṃ karoti saddāyati. Evaṃ verāyati, kalahāyati, dhūpāyati+ccādi.</p>\r\n<p class="subhead">5,11. Namotva+sso</p>\r\n<p class="bodytext">Namoiccasmā karotīti asmiṃ atthe asso hoti. Namo karotīti asmiṃ atthe assapaccaye tyādayo honti, tathāgataṃ namassati, namassanti iccādi.</p>\r\n<p class="subhead">5,12. Dhātvatthe nāmasmi</p>\r\n<p class="bodytext">Nāmasmā dhātvatthe bahula+mi hoti. Hatthinā atikkamatīti ipaccaye lavikaraṇa+ekāra+ayādesesu katesu atihatthayati. Evaṃ vīṇāya upagāyati upavīṇāyati, vinayaṃ daḷhaṃ karoti daḷhayati, visuddhā hoti ratti visuddhāyati, kusalaṃ pucchati kusalāyati+ccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',259,replace(replace('<p class="subhead">5,13. Saccādīhā+pi</p>\r\n<p class="bodytext">Saccādīhi <a name="M0.0259"></a> dhātvatthe āpi hoti. Sacca+mācikkhatīti āpimhi tyādipaccaye ‘‘ṇi+ṇāpyā+dīhi vā’’ti lavikaraṇa+e+ayādesā. Saccāpayati, saccāpeti iccādi. Attha+mācikkhati atthāpayati. Evaṃ vedāpayati. Sukkhaṃ karotīti sukkhāpayati sukkhāpeti iccādi.</p>\r\n<p class="subhead">5,16. Payojakabyāpāre ṇāpi ca</p>\r\n<p class="bodytext">Kattāraṃ yo payojeti, tassa byāpāre kriyatthā ṇi+ṇāpī honti bahulaṃ.</p>\r\n<p class="gatha1">Ṇipaccayo uvaṇṇantā, āto ṇāpeva hoti+ha;</p>\r\n<p class="gathalast">Dve dve+ko hoti vā sese, bahulaṃtyanuvuttiyā.</p>\r\n<p class="gatha1">Akammakāpi honteva, ṇi+ṇāpyantā sakammakā;</p>\r\n<p class="gathalast">Sakammakā dvikammā+ssu, dvikammā ca tikammakā.</p>\r\n<p class="gatha1">Tasmā kattari kamme ca, ṇi+ṇāpīnaṃ tu sambhavo;</p>\r\n<p class="gathalast">Na bhāve suddhakattā tu, kammaṃ hoti payojake.</p>\r\n<p class="gatha1">Nayādīnaṃ padhānañca, apadhānaṃ duhādinaṃ;</p>\r\n<p class="gathalast">Suddhakattā ṇi+ṇāpīsu, kamma+makkhyāta gocaraṃ.</p>\r\n<p class="bodytext">Bhavituṃ payojayatīti atthe iminā ṇippaccayo. Ṇakāro ṇānubandhakāriyattho. ‘‘Yuvaṇṇāna+me+o paccaye’’ti okāre ‘‘āyā+vā ṇānubandhe’’ti ṇānubandhe āvādeso. Tyādimhi ‘‘ṇi+ṇāpyā+pīhi vā’’ti lavikaraṇe ekāre ‘‘eona+mayavā sare’’ti ayādeso. So samādhiṃ bhāvayati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',260,replace(replace('<p class="bodytext">bhāveti<a name="M0.0260"></a>, bhāvayanti bhāventi+ccādi. Kamme-tena samādhi bhāvīyati+ccādi. Ettha ‘‘dīgho sarassā’’ti kye ikārassa dīgho. Bhāvayissati bhāvessati, bhāvayissanti bhāvessanti+ccādi. Ajjatane īssa simhi abhāvesi bhāvesi, abhāvayi bhāvayi, abhāvayiṃsu bhāvayiṃsu. Parassaralope abhāvesuṃ bhāvesuṃ, abhāvayaṃsu bhāvayaṃsu, abhāvayuṃ bhāvayuṃ iccādi. Kamme-abhāvīyi bhāvīyi iccādi. Abhāvissa abhāvayissa, abhāvissaṃsu abhāvayissaṃsu. Kamme-abhāvayissaṃ iccādi. Bhāve bhāveyya iccādi. Kamme-bhāvīyeyya iccādi. Bhāvayatu bhāvetu iccādi. Kamme-bhāvīyatu iccādi.</p>\r\n<p class="bodytext">Pacituṃ payojetīti atthe ṇi+ṇāpī honti. So devadattena odanaṃ pācayati pāceti iccādi, tathā pācāpayati pācāpeti+ccādi. Kamme-so tena devadattena odano pācīyati pācāpīyati+ccādi, bhavissatyādīsupi yojetabbaṃ.</p>\r\n<p class="bodytext">Gantuṃ payojetīti atthe so taṃ purisaṃ gāmaṃ gamayati gameti gacchāpayati gacchāpeti+ccādi. Kamme-tena so gāmaṃ gamīyati gacchāpīyati+ccādi.</p>\r\n<p class="bodytext">Guha=saṃvaraṇe, guhituṃ payojetīti ṇimhi ‘‘guhissa sare’’ti dīgho. Gūhayati, gūhayanti iccādi.</p>\r\n<p class="bodytext">Disa+dusa=appītiyaṃ, dusituṃ payojetīti ṇimhi ‘‘ṇimhi dīgho dusassā’’ti dīghe dūsayati+ccādi.</p>\r\n<p class="bodytext">Tathā icchantaṃ payojayati icchāpayati icchāpeti, esayati eseti. Niyacchantaṃ payojayati niyamayati niyameti. Tathā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',261,replace(replace('<p class="bodytext">āsayati <a name="M0.0261"></a> āseti, acchāpayati acchāpeti. Lābhayati lābheti, evaṃ vāsayati vāseti, vāsāpayati vāsāpeti. Vāhayati vāheti, vāhāpayati vāhāpeti+ccādi. Evaṃ jīrayati, mārayati, dassayati iccādi. Hū=sattāyaṃ, pahontaṃ payojayati pahāvayati pahāveti iccādi. Sāyayati sāyāpayati sāyāpeti. Nāyāpayati nāyāpeti. Patiṭṭhāpayati patiṭṭhāpeti. Rasse patiṭṭhapeti. Hantuṃ payojayatīti ṇi+ṇāpī, ‘‘hanassa ghāto ṇānubandhe’’ti ghātādese ghātayati ghāteti. Tathā juhāvayati juhāveti, jahāpayati jahāpeti. Hāpayati hāpeti. Dāpayati dāpeti. Vidhāpayati vidhāpeti, pidahāpayati pidahāpeti. (Bhūvādinayo).</p>\r\n<p class="bodytext">Idāni rudhādiaṭṭhagaṇā dassīyante-rodhayati rodheti. Devayati deveti. Todayati todeti. Jayāpayati jayāpeti. Vikkayati vikkayāpeti. Sāvayati sāveti. Vitānayati vitāneti. Corāpayati corāpeti iccādi.</p>\r\n<p class="gatha1">Khādīhi paccayantehi, api honti ṇi+ṇāpayo;</p>\r\n<p class="gathalast">Ṇi+ṇāpinā+nakānānaṃ, dassanañcettha sādhanaṃ.</p>\r\n<p class="bodytext">Titikkhantaṃ payojayati titikkheti titikkhāpeti, tikicchayati tikiccheti tikicchāpayati tikicchāpeti. Evaṃ bubhukkheti bubhukkhāpeti. Pabbatāyayati. Puttīyayati iccādi. (Khādipaccayanayo).</p>\r\n<p class="bodytext">Iti payogasiddhiyaṃ tyādikaṇḍo chaṭṭho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',262,replace(replace('<p class="chapter">7. Khādikaṇḍa</p>\r\n<p class="bodytext">Atha <a name="M0.0262"></a> dhātūhiyeva bhāva+kamma+kattu+karaṇādisādhanasahitaṃ khādividhānaṃ ārabhīyate –</p>\r\n<p class="bodytext">‘‘Tijamānehi khasā khamāvīmaṃsāsu’’ iccādīhi paccayavidhānañca pararūpadvittādikāriyañca tyādikaṇḍe vuttanayeneva ñātabbaṃ. Titikkhanaṃ titikkhā, ‘‘itthiya+maṇaktikayakayā ca’’ iti suttena apaccayo ca ‘‘itthiya+matvā’’ti āpaccayo ca hoti. Tathā vīmaṃsanaṃ vīmaṃsā. ‘‘Kitā tikicchāsaṃsayesu cho’’ti chappaccayādimhi kate tikicchanaṃ tikicchā, vicikicchanaṃ vicikicchā. Gupa=gopane, badha=bandhaneti imehi dhātūhi ‘‘nindāyaṃ gupa+badhā bassa bho ca’’ iti chapaccayādimhi ca dvitte parabakārassa iminā bhakāre ca kate apaccayādi hoti. Jigucchanaṃ jigucchā, bībhacchanaṃ bībhacchā. ‘‘Tuṃsmā lopo ci+cchāyaṃ te’’ iti icchāya+matthe kha+sa+chappaccayā honti. Bhuja=pālanajjhohāresu, bubhukkhanaṃ bubhukkhā. Ji=jaye, jigiṃsanaṃ, jigiṃsā. Ghasa=adane, jighacchanaṃ jighacchā.</p>\r\n<p class="subhead">27. Bhāvakammesu tabbānīyā</p>\r\n<p class="bodytext">Tabbaanīyā kriyatthā pare bhāvakammesu bhavanti, bahulaṃvidhānā kattukaraṇādīsupi. Bhū=sattāyaṃ, ‘‘yuvaṇṇāna+me opaccaye’’ti okāre ‘‘ūbyañjanassā’’ti ūāgamo, ññakāro ādyāvayavattho. Ossa ‘‘eona+ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',263,replace(replace('<p class="bodytext">mayavā <a name="M0.0263"></a> sare’’ti avādeso, bhūyateti bhavitabbaṃ bhavatā bhavanīyaṃ. Bhāvasse+kattā ekavacanameva, tañca napuṃsakaliṅgaṃ.</p>\r\n<p class="gatha1">Tabbādyabhihito bhāvo,</p>\r\n<p class="gathalast">Dabbamiva pakāsatīti-bahuvacanañca hoti.</p>\r\n<p class="bodytext">Kamme-abhipubbo, abhibhūyate abhibhūyittha abhibhūyissateti abhibhavitabbo kodho paṇḍitena, abhibhavitabbā taṇhā, abhibhavitabbaṃ dukkhaṃ. Evaṃ abhibhavanīyo abhibhavanīyā abhibhavanīyaṃ, kamme abhidheyyasseva liṅgavacanāni.</p>\r\n<p class="gatha1">Visessaliṅgātabbādī, tatthā+do pañca bhāvajā;</p>\r\n<p class="gathalast">Napuṃsake siyuṃ bhāve, kto cā+no akattari.</p>\r\n<p class="gatha1">Bhāvasmiṃ ghaṇa pume evaṃ, iyuvaṇṇā gahādijo;</p>\r\n<p class="gathalast">Apaccayopi vā+saṃkhyā, tu+māditvantakā siyuṃ.</p>\r\n<p class="bodytext">Ito paraṃ upasaggapubbatā ca kālattayassa vākyagahaṇañca vuttanayena ñātabbaṃ, tasmā anurūpavākyameva dassayissāma –</p>\r\n<p class="bodytext">Āsa=upavesane, āsane āsitabbaṃ tayā āsanīyaṃ. Kamme-upāsīyatīti upāsitabbo guru upāsanīyo. Sī=saye, e+ayādesā, sayanaṃ sayitabbaṃ sayanīyaṃ tayā. Atisīyatīti atisayitabbo kaṭo te atisayanīyo. Pada=gamane, ‘‘padādīnaṃ kvacī’’ti yuka, kakāro kānubandhakāriyattho, ukāro uccāraṇattho, dassajo pubbarūpañca, uppajjanaṃ uppajjitabbaṃ uppajjanīyaṃ. Paṭipajjīyatīti paṭipajjitabbo maggo paṭipajjanīyo. Budha=ñāṇe, bujjhateti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',264,replace(replace('<p class="bodytext">bujjhitabbo <a name="M0.0264"></a> dhammo bujjhanīyo. Su=savane, sūyateti sotabbo dhammo, ññimhi nāgame ‘‘tathanarānaṃ ṭaṭhaṇalā’’ti ṇe ca kate ‘‘na te kānubandha+nāgamesū’’ti okārābhāvo. Suṇitabbo, savanīyo. Kara=karaṇe –</p>\r\n<p class="subhead">95. Pararūpa+mayakāre byañjane</p>\r\n<p class="bodytext">Kriyatthāna+mantabyañjanassa pararūpaṃ hoti yakārato aññasmiṃ byañjane. Karīyatīti kattabbo dhammo, kattabbā pūjā, kattabbaṃ kusalaṃ.</p>\r\n<p class="subhead">119. Tuṃ+tuna+tabbesu vā</p>\r\n<p class="bodytext">Tumādīsu karassā hoti vā. Kātabbaṃ hitaṃ.</p>\r\n<p class="subhead">171. Rā nassa ṇo</p>\r\n<p class="bodytext">Rantato kriyatthā paccayanakārassa ṇa hoti. Karaṇīyo.</p>\r\n<p class="bodytext">Bhara=bharaṇe, bharīyatīti bharitabbo bharaṇīyo. Gaha=upādāne ‘‘maṃ vā rudhādīnaṃ’’ti antasarā paromaṃ vā hoti. Makāro+nubandho. ‘‘Ṇo niggahītassā’’ti niggahītassa ṇo, saṃgayhatīti saṃgaṇhitabbo saṃgaṇhanīyo, ‘‘tathanarā’’dinā ṇakāre gahaṇīyo. Rama=kīḷāyaṃ, ramīyatīti ramaṇīyo vihāro. Āpa=pāpuṇane, ‘‘sakāpānaṃ kukakū ṇe’’ti ettha ‘sakāpānaṃ kuka+kū’ti yogavibhāgā ku, ūāgame nāgamassa ṇe ca kate pāpīyatīti pāpuṇitabbo. Pararūpe pattabbo, pāpuṇanīyo, pāpanīyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',265,replace(replace('<p class="subhead">96. Manānaṃ niggahītaṃ</p>\r\n<p class="bodytext">Makāra+nakārantānaṃ <a name="M0.0265"></a> kriyatthānaṃ niggahītaṃ hotya+yakāre byañjane. Vaggantaṃ, gamiyatīti gantabbo. Gamitabbaṃ gamanīyaṃ. Khana=khaṇa=avadāraṇe, niggahītaṃ vaggantattañca, khaññateti khantabbaṃ āvāṭaṃ khanitabbaṃ khananīyaṃ. Hana=hiṃsāyaṃ, haññateti hantabbaṃ hanitabbaṃ hananīyaṃ. Mana=ñāṇe, maññateti mantabbo manitabbo. ‘‘Padādīnaṃ kvacī’’ti yuka, cavaggādimhi kate maññitabbaṃ maññanīyaṃ.</p>\r\n<p class="bodytext">Pūja=pūjāyaṃ, ‘‘curādito ṇī’’ti ṇimhi ñukārassa guruttā okārāvutti ‘‘yuvaṇṇāna+meo paccaye’’ti ekāre pūjetabbo ūmhi ayādese pūjayitabbo pūjanīyo bhagavā.</p>\r\n<p class="bodytext">Kattari-yā=pāpuṇane, nīyatīti niyyāniyo maggo, gacchantīti gamanīyā bhogā. Karaṇe-naha=soceyye, nahāyantya+nenāti nahānīyaṃ cuṇṇaṃ. Sampadāne-dā=dāne, saṃ+papubbo, sammā padīyate assāti sampadāniyo brāhmaṇo.</p>\r\n<p class="subhead">28. Ghyaṇa</p>\r\n<p class="bodytext">Bhāvakammesu kriyatthā paro ghyaṇa hoti bahulaṃ. Ūmhi kattabbaṃ kāriyaṃ. Hara=haraṇe, harīyatīti hāriyaṃ. Bhara=bharaṇe, bharitabbaṃ bhāriyaṃ. Labha=lābhe, ‘‘vaggalasehi te’’ti pubbarūpabhakāre ‘‘catutthadutiyā’’ dinā tatiyakkhare ca kate labhitabbaṃ labbhaṃ.</p>\r\n<p class="bodytext">Vaca=byattavacane,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',266,replace(replace('<p class="subhead">98. Kagā cajānaṃ ghānubandhe</p>\r\n<p class="bodytext">Ghānubandhe <a name="M0.0266"></a> cakāra+jakārantānaṃ kriyatthānaṃ ka+gā honti yathākkamaṃ.</p>\r\n<p class="subhead">84. Assā ṇānubandhe</p>\r\n<p class="bodytext">Ṇakārānubandhe paccaye pare upantassa akārassa ā hoti. Vacanaṃ vuccateti vākyaṃ. Bhaja=sevāyaṃ, bhajanīyaṃ bhāgyaṃ, jassa gakāro. Ci=caye, cayanaṃ cīyatīti vā ceyyaṃ. Yassa dvittaṃ.</p>\r\n<p class="subhead">5,122. Nito cissa cho</p>\r\n<p class="bodytext">Nito parassa cissa cho hotītiādicakārassa cho. Vinicchayatīti viniccheyyaṃ, nāgame vinicchinitabbaṃ. E+ayādesesu vinicchetabbaṃ vinicchanīyaṃ. Nī=pāpane, nīyatīti neyyo neyyā neyyaṃ, netabbaṃ.</p>\r\n<p class="subhead">29. Āsse+ca</p>\r\n<p class="bodytext">Ākārantato kriyatthā ghyaṇa hoti bhāvakammesu āssa e ca. Dā=dāne, dātabbaṃ deyyaṃ. Pā=pāne, pīyatīti peyyaṃ. Mā=māne, mīyatīti meyyaṃ. Ñā=avabodhane, ñāyatīti ñeyyaṃ ñātabbaṃ, ūmhi nāgamo ‘‘ñāssa ne+jā’’ti ñāssa jādese jānitabbaṃ, vijāniyaṃ. Khā=pakathane, saṃkhātabbaṃ saṃkheyyaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',267,replace(replace('<p class="subhead">30. Vadādīhi yo</p>\r\n<p class="bodytext">Vadādīhi <a name="M0.0267"></a> kriyatthehi yo hoti bahulaṃ bhāvakammesu. Vada=vacane, vadanaṃ vajjatīti vā vajjaṃ. Mada=ummāde, madanaṃ majjate anenāti vā majjaṃ. Gamanaṃ gammateti vā gammaṃ. Gada=vacane, gajjate gadanīyaṃ vāti gajjaṃ. Pada=gamane, pajjanīyaṃ pajjaṃ gāthā. Ada+khāda=bhakkhane, khajjatīti khajjaṃ khādanīyaṃ. Dama=damane, dammateti dammo damanīyo.</p>\r\n<p class="bodytext">Bhujā+nneti gaṇasuttena anne vattabbe yapaccayo.</p>\r\n<p class="subhead">83. Lahussupantassati</p>\r\n<p class="bodytext">Lahubhūtassa upantassa iyuvaṇṇassa eo honti yathākkamaṃ. Yassa pubbarūpe bhuñjitabboti bhojjo odano, bhojjā yāgu.</p>\r\n<p class="subhead">31. Kicca ghacca bhacca bhabba leyyā</p>\r\n<p class="bodytext">Ete saddā yapaccayantā nipaccante. Kara=karaṇe, iminā nipātanā ye kicādese ca kate pubbarūpaṃ, kattabbaṃ kiccaṃ. Hana=hiṃsāyaṃ, ghaccādesādimhi kate hananaṃ haññateti vā ghaccaṃ. Bhara=bharaṇe, bhaccādesādimhi kate bharaṇīyo bhacco. Bhū=sattāyaṃ, yamhi okāre iminā avādese bhavatīti bhabbo. Liha=assādane, yamhi iminā hassa yakāre lehitabbaṃ leyyaṃ, ekāravuddhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',268,replace(replace('<p class="subhead">32. Guhādīhi yaka</p>\r\n<p class="bodytext">Guhādīhi <a name="M0.0268"></a> kriyatthehi bhāvakammesu yaka hoti. Guha=saṃvaraṇe, ‘‘lahussupantassā’’ti sampattassa okārassa ‘‘na te kānubandhanāgamesū’’ti paṭisedho. ‘‘Hassa vipallāso’’ti vipallāse guhanaṃ guhitabbaṃ guyhaṃ. Duha=papūraṇe, dohanaṃ duyhatīti vā duyhaṃ. Sāsa=anusiṭṭhiyaṃ –</p>\r\n<p class="subhead">117. Sāsassa sisa vā</p>\r\n<p class="bodytext">Sāsassa sisa vā hoti kānubandhe. Pubbarūpaṃ, sāsīyatīti sisso.</p>\r\n<p class="bodytext">Siddhā eve+te tabbādayo pesā+tisagga+ppattakālesu gamyamānesupi, sāmaññena vidhānato. Pesanaṃ – ‘‘kattabba+midaṃ bhavatā’’ti āṇāpanaṃ ajjhesanañca. Atisaggo nāma ‘‘ki+midaṃ mayā kattabbaṃ’’ti puṭṭhassa ‘‘pāṇo na hantabbo’’tiādinā paṭipattidassanamukhena kattabbassa anuññā. Pattakālo nāma sampattasamayo. Yo kiccakaraṇasamayaṃ upaparikkhitvā karoti, tassa samayārocanaṃ, na tattha ajjhesana+matthi. Bhotā khalu kaṭo kattabbo karaṇīyo kāriyo kicco’’ evaṃ tvayā kaṭo kattabbo, bhoto hi patto kālo kaṭakaraṇe.</p>\r\n<p class="bodytext">Evaṃ uddhamuhuttepi vattamānato pesādīsu siddhā eva. Tathā arahe kattari sattivisiṭṭhe ca patīyamāne, āvassakā+dhamīṇatāvisiṭṭhe ca bhāvādo siddhā. Uddhaṃ muhuttato-bhotā kaṭo kattabbo. Bhotā rajjaṃ kātabbaṃ, bhavaṃ araho. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',269,replace(replace('<p class="bodytext">Bhotā <a name="M0.0269"></a> bhāro vahitabbo, bhavaṃ sakko. Bhotā avassaṃ kaṭo kattabbo. Bhotā nikkho dātabbo.</p>\r\n<p class="subhead">33. Kattari ltu+ṇkā</p>\r\n<p class="bodytext">Kattari kārake kriyatthā ltu+ṇkā honti. Kara=karaṇe, pararūpe ‘‘ltu+pitādīna+mā simhī’’ti ā silopo ca. Karotīti kattā. Evaṃ bharatīti bhattā. Haratīti hattā. Bhidatīti bhettā, ekāro, ūmhi bheditā. Chindatīti chettā. Bhojanassa dātā bhojanadātā. Sandhātīti sandhātā. Vacatīti vattā. Okārapararūpesu bhuñjatīti bhottā. ‘‘Padādīnaṃ kvacī’’ti yukāgamo, bujjhatīti bujjhitā. Jānātīti ñātā. Chindatīti chetā. Suṇātīti sotā. ‘‘Ū+lasse’’ti ūssa ekāre gaṇhātīti gahetā. Bhavatīti bhavitā. Saratīti saritā. ‘‘Manānaṃ niggahītaṃ’’ti massa niggahīte vaggante ca gacchatīti gantā. Nakārantānampi niggahītaṃ, khanatīti khantā. Sanatīti santā. Maññatīti mantā. Pāletīti pālayitā pāletā, ettha curādittā ṇi.</p>\r\n<p class="bodytext">Ṇiṇāpīsu-bhāvayatīti bhāvayitā bhāvetā. Evaṃ sārayitā sāretā, dāpayitā dāpetā, hāpayitā hāpetā, nirodhayitā nirodhetā, bodhayitā bodhetā, ñāpayitā ñāpetā, sāvayitā sāvetā, gāhayitā gāhetā, kārayitā kāretā, kārāpayitā kārāpetā iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',270,replace(replace('<p class="bodytext">Ṇkapaccaye-ṇakāro <a name="M0.0270"></a> vuddhyattho. Rathaṃ karotīti rathakārako, ‘‘assā ṇānubandhe’’ti ā amādisamāso ca. Annaṃ dadātīti annadāyako. ‘‘Adhātussa kā+syādito ghe+ssī’’ti ghe pare assa iādeso, annadāyikā, annadāyakaṃ kulaṃ. ‘‘Āssā+ṇāpimhi yuka’’ itiṇāpito+ññatra yuka. Lokaṃ netīti lokanāyako, ekāre ‘‘āyā+vā ṇānubandhe’’ti āyādeso. Evaṃ vinetīti vināyako.</p>\r\n<p class="bodytext">Akammupapade-karotīti kārako, evaṃ dāyako nāyako, okāre āvādese suṇātīti sāvako. Pure viya ikāre sāvikā. Lunātīti lāvako. Pu=pavane, punātīti pāvako. Bhavatīti bhāvako, upāsatīti upāsako. Gaṇhātīti gāhako pāvako, yājako. Vadha=hiṃsāyaṃ, vadhetīti vadhako, ‘‘aññatrāpī’’ti vuddhipaṭisedho. ‘‘Hanassa ghāto ṇānubandhe’’ti ghātādeso, hanatīti ghātako. ‘‘Maṃ vā rudhādīnaṃ’’ti maṃ, rundhako, guruttā na vuddhi. Tathā bhuñjatīti bhuñjako. Āyassa rasse kiṇātīti kayako. Pāletīti pālako. Pūjetīti pūjako.</p>\r\n<p class="bodytext">Khādīsu-titikkhatīti titikkhako. Vīmaṃsatīti vīmaṃsako iccādi. Panudatīti panūdako, ‘‘byañjane’’ccādinā dīgho. ‘‘Bhītvā+nako’’ti ettha ‘ānako’ti yogavibhāgā ānako, āssa rasse nakārāgame ca ‘‘ñāssa ne jā’’ti jādeso, jānanako. Ṇāpimhi –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',271,replace(replace('<p class="bodytext">Aṇa-iti <a name="M0.0271"></a> daṇḍakadhātu, āṇāpetīti āṇāpako. Tathā saññāpako, patiṭṭhāpako. Saṃ+pa pubbo āpa=pāpuṇane, nibbānaṃ sampāpetīti nibbānasampāpako. Kārāpako, kārāpikā iccādi.</p>\r\n<p class="bodytext">Bahulaṃvidhānā kammepi-pādehi harīyatīti pādahārako. Cupa=mandagamane, gale cuppatīti galacopako.</p>\r\n<p class="bodytext">Siddhova ltu arahādīsu ‘‘bhavaṃ khalu kaññāya pariggahāraho’’ti (pariggahitā). Sīlatthe-upādānasīloti upādātā. Sādhu gacchatīti gantā. Muṇḍanadhammā muṇḍanācārāti muṇḍayitāro, ettha ‘‘dhātvatthe nāmasmī’’ti imhi ūāgame e+ayādese muṇḍayitusaddamhi āraṅādese ca kate yossa ṭo.</p>\r\n<p class="subhead">34. Āvī</p>\r\n<p class="bodytext">Kriyatthā āvī hoti bahulaṃ kattari. ‘‘Disassa passa+dassa=dasa+da+dakkhā’’ti dassādeso, bhayaṃ passatīti bhayadassāvī. Nīmhi bhayadassāvinī. Bhayadassāvi cittaṃ. Appavisayatāñāpanatthaṃ bhinnayogakaraṇaṃ. Sāmaññavihitattā sīlādīsu ca hoteva.</p>\r\n<p class="subhead">35. Āsiṃsāya+mako</p>\r\n<p class="bodytext">Āsiṃsāyaṃ gammamānāyaṃ kriyatthā ako hoti kattari. Jīva=pāṇadhāraṇe, jīvatūti jīvako. Nanda=samiddhiyaṃ, nandatūti nandako. Bhavatūti bhavako.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',272,replace(replace('<p class="subhead">36. Karā ṇano</p>\r\n<p class="bodytext">Karato <a name="M0.0272"></a> kattari ṇa no hoti. Karotīti kāraṇaṃ. Kattarīti kiṃ, karoti anenāti karaṇaṃ.</p>\r\n<p class="subhead">37. Hāto vīhi+kālesu</p>\r\n<p class="bodytext">Hāto vīhismiṃ kāle ca ṇano hoti. ‘‘Āssā’’tyādinā yuka, jahanti udakaṃti hāyanā vīhayo. Jahāti bhāve padattheti hāyano saṃvaccharo. Vīhikālesūti kiṃ, jahātīti hātā.</p>\r\n<p class="subhead">38. Vidā kū</p>\r\n<p class="bodytext">Vidasmā kū hoti kattari. Kakāro ‘‘kūto’’ti visesanattho. Vidatīti vidū, lokavidū.</p>\r\n<p class="subhead">39. Vito ñāto</p>\r\n<p class="bodytext">Vipubbā ñāicca+smā kū hoti kattari. Vijānātīti viññū.</p>\r\n<p class="subhead">40. Kammā</p>\r\n<p class="bodytext">Kammato parā ñāicca+smā kū hoti kattari. Sabbaṃ jānātīti sabbaññū. Evaṃ mattaññū, dhammaññū, atthaññū kālaññū, kataññū iccādi. (Bhikkhūti pana ‘‘bharādi’’ ṇvādisuttena siddhaṃ).</p>\r\n<p class="subhead">41. Kvacaṇa</p>\r\n<p class="bodytext">Kammato parā kriyatthā kvaci aṇa hoti kattari. Kumbhaṃ karotīti kumbhakāro, amādisamāso. Itthiyaṃ kumbhakārī. Evaṃ kammakāro, māsākāro, kaṭṭhakāro, rathakāro </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',273,replace(replace('<p class="bodytext">suvaṇṇakāro<a name="M0.0273"></a>, suttakāro, vuttikāro, ṭīkākāro. Saraṃ lunātīti saralāvoti o+avādesā. Mante ajjhāyatīti manthajjhāyo, i=ajjhenagatīsu, adhipubbo, e+ayādesā, adhino issa yakāra+cavaggādayo ca.</p>\r\n<p class="bodytext">Bahulādhikārā iha na hoti ‘‘ādiccaṃ passati, himavantaṃ suṇoti, gāmaṃ gacchati’’. Kvacīti kiṃ, kammakaro, ettha apaccayo.</p>\r\n<p class="subhead">42. Gamā rū</p>\r\n<p class="bodytext">Kammato parā gamā rū hoti kattari. Rānubandhattā amabhāgalopo. Vedaṃ gacchatīti vedagū, evaṃ pāragū.</p>\r\n<p class="bodytext">Sāmaññavidhānato sīlādīsupi hoti. Bhavapāraṃ gacchati sīlenāti bhavapāragū. Antagamanasīlo antagū, evaṃ addhagū.</p>\r\n<p class="subhead">43. Samāna+ñña+bhavanta+yāditū+pamānā disā kamme rī+rikkha+kā</p>\r\n<p class="bodytext">Samānādīhi yādīhi co+pamānehi parā disā kammakārake rī+rikkha+kā honti. ‘‘Syādi syādine+katthaṃ’’ti samāse ‘‘rānubandhe+ntasarādissā’’ti disassa isabhāgalope ‘‘rīrikkhakesū’’ti samānassa sādese ca samāno viya dissatīti sadī, sadikkho. Ke – ‘‘na te kānubandhanāgamesū’’ti ettābhāvo, sadiso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',274,replace(replace('<p class="subhead">125. Samānā ro rī+rikkha+kesu</p>\r\n<p class="bodytext">Samānasaddato <a name="M0.0274"></a> parassa disassa ra hoti vā rī+rikkha+kesūti pakkhe dassa rādese sarī, sarikkho, sariso.</p>\r\n<p class="subhead">3,86. Sabbādīna+mā</p>\r\n<p class="bodytext">Rī+rikkha+kesu sabbādīna+mā hoti. Añño viya dissatīti aññādī, aññādikkho, aññādiso.</p>\r\n<p class="subhead">3,87. Nta+ki+mi+mānaṃ ṭā+kī+ṭī</p>\r\n<p class="bodytext">Rī+rikkha+kesu nta+kiṃ+imasaddānaṃ ṭā+kī+ṭī honti yathākkamaṃ. Ṭakārā sabbādesatthā. Bhavādī bhavādikkho bhavādiso, kīdī kīdikkho kīdiso, ayamiva dissatīti īdī īdikkho īdiso. Ākāre yādī yādikkho yādiso, tyādī tyādikkho tyādiso iccādi.</p>\r\n<p class="subhead">3,88. Tumhāmhānaṃ tāme+kasmiṃ</p>\r\n<p class="bodytext">Rī+rikkha+kesu tumhāmhānaṃ tāmā honte+kasmiṃ yathākkamaṃ. Tvaṃ viya dissati, ayaṃ viya dissatīti tādī mādī iccādi. Ekasminti kiṃ, tumhādiso amhādiso.</p>\r\n<p class="subhead">3,89. Taṃ+ma+maññatra</p>\r\n<p class="bodytext">Rīrikkhakantato+ññasmiṃ uttarapade tumhāmhāna+mekasmiṃ taṃ+maṃ honti yathākkamaṃ. Tvaṃ dīpo esaṃ, ahaṃ dīpo esaṃti aññapadatthe taṃdīpā maṃdīpā. Tvaṃ saraṇa+mesaṃ, ahaṃ saraṇa+ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',275,replace(replace('<p class="bodytext">mesanti <a name="M0.0275"></a> taṃsaraṇā maṃsaraṇā. Tayā yogo tayyogo, mayā yogo mayyogoti amādisamāse niggahītalopo.</p>\r\n<p class="subhead">3,90. Ve+tasse+ṭa</p>\r\n<p class="bodytext">Rī+rikkha+kesu etassa eṭa vā hoti. Edī etādī, edikkho etādikkho, ediso etādiso.</p>\r\n<p class="subhead">44. Bhāvakārake sva+ghaṇa gha kā</p>\r\n<p class="bodytext">Bhāve kārake ca kriyatthā a ghaṇa gha kā honti bahulaṃ.</p>\r\n<p class="bodytext">Apaccayo-paggaṇhanaṃ paggaho, evaṃ niggaho, dhammaṃ dhāretīti dhammadharo, evaṃ vinayadharo. Tathā taṃ karotīti takkaro, dvittaṃ. Evaṃ hitakaro, divasakaro, dinakaro, divākaro, nisākaro, dhanuṃ gaṇhīti dhanuggaho. Evaṃ kavacaggaho. Dada=dāne, sabbakāmaṃ dadātīti sabbakāmadado, sabbadado. Āto ‘‘parokkhāyañcā’’ti caggahaṇena dvitte ‘‘rasso pubbassā’’ti rasse ca annaṃ dadātīti annadado, evaṃ dhanado. Saṃpubbo dhā=dhāraṇe, sabbaṃ sandhahatīti sabbasandho. Nī=pāpane, vipubbo, vinesi vineti vinessati etena etthāti vā vinayo, e+ayādesā. Nayanaṃ nayo. Si=sevāyaṃ, nipubbo, nissīyatīti nissayo. Si=saye, anusayi anuseti anusessatīti anusayo. I=gatimhi, patipubbo, paṭicca etasmā phala+metīti paccayo. Saṃ+upubbo, dāgame </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',276,replace(replace('<p class="bodytext">samudayo<a name="M0.0276"></a>. Ci=caye, vinicchayate+nena vinicchayanaṃ vā vinicchayo, ‘‘nito cissa cho’’ti cissa cho. Uccayanaṃ uccayo, saṃcayo. Khi=khaye, khayanaṃ khayo. Ji=jaye, vijayanaṃ vijayo, jayo. Kī=dabbavinīmaye, vikkayanaṃ vikkayo, kayo. Lī=silesane, allīyanti etthāti ālayo, layo. (Ivaṇṇantā).</p>\r\n<p class="bodytext">Āsuṇantīti assavā, āssa rasso. Paṭissavanaṃ paṭissavo. Su=passavane, ābhavaggā savantīti āsavā. Ru=saddo ravatīti ravo. Bhavatīti bhavo. Pabhavati etasmāti pabhavo. Lū=chedane, lavanaṃ lavo. (Uvaṇṇantā).</p>\r\n<p class="bodytext">Cara=caraṇe, saṃcaraṇaṃ saṃcaro. Dara=vidāraṇe, ādaranaṃ ādaro. Āgacchati āgamanaṃti vā āgamo. Sappa=gamane, sappatīti sappo. Dibbatīti devo. Pakkamanaṃ pakkamatīti vā pakkamo, evaṃ vikkamo. Cara=caraṇe, vanaṃ caratīti vanacaro. Kāmo avacarati etthāti kāmāvacaro loko, kāmāvacarā paññā, kāmāvacaraṃ cittaṃ. Gāvo caranti etthāti gocaro. Pādena pivatīti pādapo. Evaṃ kacchapo. Sirasmiṃ ruhatīti siroruho, manādittā o. Guhāyaṃ sayatīti guhāsayaṃ cittaṃ, evaṃ kucchisayā vātā. Pabbate tiṭṭhatīti pabbataṭṭho puriso, pabbataṭṭhā nadī, pabbataṭṭhaṃ bhasmaṃ. Evaṃ thalaṭṭhaṃ jalaṭṭhaṃ.</p>\r\n<p class="bodytext">Kicchatthe dumhi akicchatthe su+īsaṃ+sukha upapadesu-dukkhena karīyati karaṇaṃ vā dukkaraṃ. Evaṃ dussayo, dukkhena bharīyatīti dubbharo mahiccho. Evaṃ durakkhaṃ cittaṃ, duddaso dhammo, duranubodho dhammo. Īsaṃ sayatīti īsaṃsayo, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',277,replace(replace('<p class="bodytext">evaṃ <a name="M0.0277"></a> sukhasayo. Īsaṃ karīyatīti īsakkaraṃ kammaṃ. Sukhena karīyatīti sukaraṃ pāpaṃ bālena. Evaṃ subharo appiccho, sudassaṃ paravajjaṃ, subodha+miccādi. Sabbattha pādiamādisamāsā.</p>\r\n<p class="bodytext">Ghaṇa-bhavatīti bhāvo, o+āvādesā. Aya=iti daṇḍakadhātu, ayati itoti āyo, āharatīti āhāro. Upahanatīti upaghāto, ‘‘hanassa ghāto ṇānubandhe’’ti ghātādeso. Rañjatīti rāgo, ‘‘kagā cajānaṃ ghānubandhe’’ti jassa go. Rañjanti anenāti rāgo. ‘‘Assā ṇānubandhe’’ti ā, pajjate+nenāti pādo. Tuda=byathane, patujjate+nenāti patodo. Jarīyati anenāti jāro, evaṃ dāro. Bhajīyatīti bhāgo. Evaṃ bhāro. Labbhatīti lābho. Vi+opubbo, voharīyatīti vohāro. Diyyatīti dāyo, yuka. Vihaññati etasmāti vighāto. Viharanti etthāti vihāro. Āramanti etasminti ārāmo. Pacanaṃ vā pāko, cassa ko. Cajanaṃ cāgo. Yajanaṃ yāgo. Rajanaṃ rāgo.</p>\r\n<p class="subhead">127. Ana+ghaṇasvā+parīhi ḷo</p>\r\n<p class="bodytext">Ā+parīhi parassa dahassa ḷo hota+na+ghaṇasu. Paridahanaṃ pariḷāho. Evaṃ dāho. Bhañjanaṃ saṅgo. Evaṃ saṅgo. Saṃkharanaṃ saṃkhāro, ‘‘karotissa kho’’ti kassa kho. Evaṃ parikkhāro. ‘‘Purasmā’’ti karassa kho, purekkhāro, ettaṃ tadaminādipāṭhā. Evaṃ upakāro, gāho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',278,replace(replace('<p class="bodytext">Gha-vacatīti <a name="M0.0278"></a> vako. Sica=paggharaṇe, secanaṃ seko. Evaṃ soko, eovuddhiyo. Yuñjanaṃ yogo.</p>\r\n<p class="bodytext">Ka-pī=tappane, pīnetīti pīyo, kānubandhattā na vuddhi, ‘‘yuvaṇṇāna+miyaṅuvaṅa sare’’ti iyaṅa. Khipa=peraṇe, khipatīti khipo. Bhuñjantya+nenāti bhujo. Yudha=sampahāre āyujjhanti anenāti āyudhaṃ.</p>\r\n<p class="subhead">45. Dādhātvi</p>\r\n<p class="bodytext">Dādhāto bahula+mi hoti bhāvakārakesu. Dā=dāne, ādiyatītiādi. Evaṃ upādi. Dhā=dhāraṇe, udakaṃ dadhātīti udadhi, ‘‘saññāya+mudo+dakassā’’ti udakassa udādeso. Jalaṃ dhiyate asminti jaladhi. Vālāni dhīyanti asminti vāladhi. Sandhīyati sandhātīti vā sandhi. Dhīyatīti dhi. Vidhīyati vidhāti vidhānaṃ vā vidhi. Sammā samaṃ vā cittaṃ ādadhātīti samādhi.</p>\r\n<p class="subhead">46. Vamādīhya+thu</p>\r\n<p class="bodytext">Vamādīhi bhāvakārakesva+thu hoti. Vama=uggiraṇe, vamanaṃ vamīyatīti vā vamathu. Vepa+kampa=calane, vepanaṃ vepathu.</p>\r\n<p class="subhead">47. Kvi</p>\r\n<p class="bodytext">Kriyatthā kvi hoti bahulaṃ bhāvakārakesu. Kakāro kānubandhakāriyattho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',279,replace(replace('<p class="subhead">159. Kvissa</p>\r\n<p class="bodytext">Kriyatthā <a name="M0.0279"></a> parassa kvissa lopo hoti. Sambhavatīti sambhū. Evaṃ vibhavatīti vibhū, abhibhū, sayambhū. Tathā dhu=kampane, sandhunātīti sandhu. Vibhātīti vibhā. Pabhātīti pabhā. Saṃgamma bhāsanti etthāti sabhā, ‘‘kvimhi lopo+ntabyañjanassā’’ti antabyañjanassa lopo. Bhujena gacchatīti bhujago. Evaṃ urago. Turaṃ=sīghaṃ gacchatīti turaṅgo. Khena gacchatīti khago. Vihāyase gacchatīti vihago, tadaminādipāṭhā vihādeso. Na gacchatīti nago. Evaṃ ago, ‘‘nago vā+ppāṇinī’’ti vikappena nañasamāse ṭādesanisedho. Jana=janane, kammato jātoti kammajo, amādisamāso, kammajo vipāko, kammajā paṭisandhi, kammajaṃ rūpaṃ. Evaṃ cittajaṃ, utujaṃ, āhārajaṃ. Attajo putto, vārimhi jāto vārijo. Evaṃ thalajo. Paṅkajaṃ. Jalajaṃ. Aṇḍajaṃ. Sarasijaṃ, upapadasamāse bahulaṃvidhānā vibhatyalope ‘‘manādīhī’’tiādinā siādeso. Dvikkhattuṃ jāto dvijo, ‘‘tadaminā’’ dinā kkhattuṃlopo. Pacchā jāto anujo. Sañjānātīti saññā. Pajānātīti paññā. Evaṃ patiṭṭhātīti patiṭṭhā. Jhā=cintāyaṃ, parasampattiṃ abhijjhāyatīti abhijjhā. Hitesitaṃ upaṭṭhāpetvā jhāyatīti upajjhā. So eva upajjhāyo, ‘‘sakatthe’’ti yo. Sammā jhāyanti etthāti saṃjhā. Kvidantā dhātvatthaṃ na jahanti, liṅgatthaṃ paṭipādayanti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',280,replace(replace('<p class="subhead">48. Ano</p>\r\n<p class="bodytext">Kriyatthā <a name="M0.0280"></a> bhāvakārakesu ano hoti. Nanda=samiddhiyaṃ, bhāve-nandiyate nandanaṃ. Kamme-anandīyittha nandīyati nandīyissati nanditabbanti vā nandanaṃ vanaṃ. Gahanaṃ gahanīyaṃ vā gahaṇaṃ, ‘‘tathanarā’’ dinā nassa ṇo. Gaṇhanaṃ, niggahītassa no. Caritabbaṃ caraṇaṃ. Bhuyate bhavanaṃ. Huyate havanaṃ. Rundhitabbaṃ rundhanaṃ rodhanaṃ vā. Bhuñjitabbaṃ bhuñjanaṃ bhojanaṃ vā. Bujjhitabbaṃ bujjhanaṃ, ‘‘padādīnaṃ kvacī’’ti yuka. Bodhanaṃ vā. Suti suyyati vā savanaṃ. Pāpīyatīti pāpuṇanaṃ, ‘‘sakāpānaṃ kuka+kū’’ti yogavibhāgā kuāgame nāgame ca tassa ṇo ca. Pālīyatīti pālanaṃ iccādi.</p>\r\n<p class="bodytext">Kattari-rajaṃ haratīti rajoharaṇaṃ toyaṃ. Vijānātīti viññāṇaṃ. Ghā=gandhopādāne, ghāyatīti ghānaṃ. Jhā=cintāyaṃ, jhāyatīti jhānaṃ. Karoti anenāti kāraṇaṃ, dīgho. Viākarīyanti etenāti byākaraṇaṃ. Pūrati+nenāti pūraṇaṃ. Dīyate+nenāti dānaṃ. Pamīyate+nenāti pamāṇaṃ. Vuccate+nenāti vacanaṃ. Panudati panujjate+nenāti vā panudanaṃ. Sūda=pagaraṇe, sūdati sujjate+nenāti vā sūdano. Suṇāti suyate+neneti vā savanaṃ. Luyati luyate+neneti vā lavanaṃ. Evaṃ nayanaṃ. Punāti puyate+neneti vā pavano. Sametīti samaṇo samanaṃ vā. Tathā bhāveti bhāvīyati etāyāti vā bhāvanaṃ. Evaṃ pācanaṃ pācāpanaṃ iccādi. ‘‘Ana+saṇasvā parīhi ḷo’’ti ḷo, āḷāhanaṃ.</p>\r\n<p class="bodytext">Adhikaraṇe-tiṭṭhati asinti ṭhānaṃ. Evaṃ sayanaṃ, senaṃ vā āsanaṃ. Adhikarīyati etthāti adhikaraṇaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',281,replace(replace('<p class="bodytext">Sampadānāpādānesu-sammā <a name="M0.0281"></a> padīyate yassa taṃ sampadānaṃ. Apecca etasmā ādadātīti apādānaṃ. Bahulādhikārā calanādīhipi sīlasādhudhammesupi ano, calati sīlenāti calano evaṃ jalano, kodhano, kopano. Maṇḍa=bhusane, maṇḍeti sīlenāti maṇḍano. Evaṃ bhūsano. ‘‘Aññātrapī’’ti okāranisedho.</p>\r\n<p class="subhead">49. Itthiya+ma+ṇa+kti+ka+yaka+yā ca</p>\r\n<p class="bodytext">Itthiliṅge bhāve kārake ca kriyatthā aādayo honti ano ca bahulaṃ.</p>\r\n<p class="bodytext">A-jara=vayohāniyaṃ, jirati jiraṇaṃ vā jarā. ‘‘Itthiya+ma+tvā’’ti āpaccayo. Paṭisambhijjatīti paṭisambhidā. Paṭipajjati etāyāti paṭipadā. Evaṃ sampadā, āpadā. Upādīyatīti upādā. Ikkha+cakkha=dassane, upaikkhatīti upekkhā. ‘‘Yuvaṇṇāna+meo luttā’’ti ekāro. Cintanaṃ cintā. Sikkha=vijjopādāne, sikkhanaṃ sikkhīyantīti vā sikkhā. Evaṃ bhikkhā. Icchanaṃ icchā. ‘‘Gamayami’’ccādinā cchaṅādeso. Puccha=pucchane, pucchanaṃ pucchā. Midha+medha=saṅgame, apaṭhitadhātu, medhanaṃ medhā. Evaṃ gudha=pariveṭhane, godhanaṃ godhā. Titikkhanaṃ titikkhā. Evaṃ vīmaṃsā, jigucchā, pipāsā, puttiyā. Īhanaṃ īhā.</p>\r\n<p class="bodytext">Ṇa-karaṇaṃ kārā, ‘‘assā ṇānubandhe’’ti ā hoti, eva+muparipi. Haraṇaṃ hārā muttāvali. Taraṇaṃ tārā, dharaṇaṃ dhārā. Araṇaṃ ārā.</p>\r\n<p class="bodytext">Yathākathañci saddamhi, ruḷhiyā atthanicchayo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',282,replace(replace('<p class="bodytext">Kti-sambhuvanaṃ <a name="M0.0282"></a> sambhuti. ‘‘Na te’’ccādinā na vuddhi. Savanaṃ suti. Nayanaṃ nayati etāyāti vā nīti. Maññatīti mati, ‘‘gamādirānaṃ lopontabyañjanassā’’ti gamādittā nalopo. Gamanaṃ gantabbāti vā gati. Upahananaṃ upahati. Ramanti etāya ramanaṃ vā rati. Tananaṃ tati. Niyamanaṃ niyati. Bhuñjanaṃ bhutti. Yuñjanaṃ yutti, pararūpaṃ. Evaṃ samāpajjanaṃ samāpajjateti samāpatti. Sampatti. Yaja=devapūjāsaṅgatikaraṇadānesu, yajanaṃ iṭṭhi, ‘‘yajassa yassa ṭiyī’’ti ṭiādeso, ‘‘pucchādito’’ti tassa ṭho, pararūpapaṭhamakkharā ca. Sāsanaṃ siṭṭhi, ‘‘sāsassa sisa vā’’ti sisa, sānantarassa tassa ṭho’’ti ṭhādeso. Bhedanaṃ bhijjateti vā bhitti. Bhaja=sevāyaṃ, bhajanaṃ bhatti. Tana=vitthāre, tanotīti tanti, nassa niggahītādi.</p>\r\n<p class="bodytext">Ka-guhantī etthāti guhā, okāranivutti. Rujatīti rujā. Modanti etāyāti mudā nāma muditā.</p>\r\n<p class="bodytext">Yaka-vida=ñāṇe, vidanaṃ vidanti etāyāti vā vijjā, dassa je pubbarūpaṃ. Yajanaṃ ijjā, ṭiādeso.</p>\r\n<p class="bodytext">Ya-sayanti etthāti seyyā, dvittaṃ. Aja=vaja=gamane, samajanaṃ samajanti etthāti vā samajjā. Papubbo, pabbajanaṃ pabbajjā. Tavaggavaraṇā’’dimhi ‘‘cavaggabayañā’’ti yogavibhāgena vassa be dvittaṃ. Ūmhi paricaraṇaṃ paricariyā. Jāgaraṇaṃ jāgariyā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',283,replace(replace('<p class="bodytext">Ana-payojake <a name="M0.0283"></a> kāriyadhātuto kattuṃ payojanaṃ kāraṇaṃ, ekāranisedho nassa ṇo ca. Evaṃ harituṃ payojanaṃ hāraṇaṃ. Vida=anubhave, vitti vedayatīti vā vedanā. Vanda=abhivādanathutīsu, vandanaṃ vandanā. Upāsanaṃ upāsanā. Cita=saṃcetanāyaṃ, cetayatīti cetanā. Desiyatīti desanā. Bhāviyatīti bhāvanā.</p>\r\n<p class="subhead">50. Jāhāhi ni</p>\r\n<p class="bodytext">Jā=vayohānimhi, hā=cāge, imehi itthiyaṃ ni hoti. Jānaṃ=vayaparipāko jāni. Hānaṃ hāni.</p>\r\n<p class="subhead">51. Karā ririyo</p>\r\n<p class="bodytext">Karato ririyo hoti+tthiyaṃ. Rānubandhattā aralope karaṇaṃ kiriyā. ‘‘Kriyā’’ti ‘‘tuṃtāye’’ccādimhi ‘‘kriyāyaṃ’’ti yogavibhāgā riyaramhi aralopo, rikāro kakāre+nubandho hoti.</p>\r\n<p class="subhead">52. I+ki+tī sarūpe</p>\r\n<p class="bodytext">Dhātussa sarūpe+bhidheyye ete honti. Vacaicca+yaṃ dhātu eva vaci. Evaṃ yudhi. ‘‘Karotissa kho’’ti vikaraṇassa ñāpitattā ‘‘kattari lo’’ti lo, pacati. Akāro kakāroti ghaṇantena kārasaddena chaṭṭhīsamāso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',284,replace(replace('<p class="subhead">53. Sīlā+bhikkhaññā+vassakesu ṇī</p>\r\n<p class="bodytext">Kriyatthā <a name="M0.0284"></a> ṇī hoti sīlādīsu. Saṃsa=pasaṃsane, piyapubbo, piyaṃ pasaṃsati sīlenāti piyapasaṃsī rājā. Atha vā piyaṃ pasaṃsati sīlena vā dhammena vā tasmiṃ sādhu vāti piyapasaṃsī, piyapasaṃsanī, piyapasaṃsi kulaṃ, āvuddhimhi tathā saccavādī, dhammavādī. Sīghayāyīti ‘‘assā+ṇāpimhī yuka’’ iti yuka. Pāpakārī, mālakārī iccādi. Uṇhaṃ bhuñjati sīlenāti uṇhabhojī, ‘‘lahussupantassā’’ti okāro.</p>\r\n<p class="bodytext">Ābhikkhaññe-punappuna khīraṃ pivatīti khīrapāyī, yuka. Avassaṃ karotīti avassakārī. ‘‘Syādi syādine+katthaṃ’’ti samāse vibhattilope ca kate ‘‘lopo’’ti niggahītalopo. Satandāyīti ettha bahulaṃvidhānā vibhattialope amādisamāsapaṭisedhe ca kate vaggantaṃ.</p>\r\n<p class="bodytext">Aññasmiṃ atthepi ‘‘ṇī’’ti yogavibhāgena siddhaṃ. Sādhukārī, brahmacārī, assaddhabhojī. Paṇḍitaṃ attānaṃ maññatīti paṇḍitamānī, bahussutadhārī iccādi.</p>\r\n<p class="bodytext">Sādhukaraṇaṃ sādhukāro, so assa atthīti sādhukārīti ghaṇantā ī.</p>\r\n<p class="subhead">54. Thāvari+ttara bhaṅgura bhidura bhāsura bhassarā</p>\r\n<p class="bodytext">Ete saddā nipaccante sīle gamyamāne. Iminā nipātanā varapaccayo ca thāssa tho ca, tiṭṭhati sīlenāti thāvaro. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',285,replace(replace('<p class="bodytext">I=ajjhenagatīsu<a name="M0.0285"></a>, ttarapaccayo, gacchati sīlenāti ittaro. Bhañja=omaddane, bhajjate sayameva bhajjati vā attanā attānanti bhaṅguro, kamme kattari vā gurapaccayo. Bhijjate sayameva bhindati vā attānanti bhidūro, ettha kūrapaccayo, kakāro+nubandho. Parabhañjanavisayesupi udāharaṇesu na hoti. Tatthāpi keci ‘‘dosandhakārabhidūro’’ti idaṃ sandhāya icchanti. Bhāsati dippatīti bhāsuro, urapaccayo. Sarapaccaye bhassaro, ‘‘byañjane dīgharassā’’ti rasso. (Tekālikappaccayā).</p>\r\n<p class="subhead">55. Kattari bhūte ktavantu+ktāvī</p>\r\n<p class="bodytext">Bhūte atthe vattamānato kriyatthā ktavantu+ktāvī honti kattari. Vijinīti vijitavā vijitāvī, kānubandhattā na vuddhi. Guṇavantu daṇḍisamaṃ. Hu=havane, aggiṃ ahavīti hutavā hutāvī, hutāvinī hutavānī. ‘‘Bhūte’’ti yāva ‘‘āhāratthā’’ti adhikāro.</p>\r\n<p class="subhead">56. Kto bhāvakammesuti</p>\r\n<p class="bodytext">Bhāve kamme ca bhūte kto hoti. Asanaṃ āsitaṃ bhavatā, ñi. Karīyitthāti kato kaṭo bhavatā, ‘‘gamādirānaṃ’’ tyādinā ralopo. Eva+muparipi.</p>\r\n<p class="subhead">57. Kattari cā+rambhe</p>\r\n<p class="bodytext">Kriyārambhe kattarikto hoti yathāpattañca, pakarīti pakato bhavaṃ kaṭaṃ. Pakarīyitthāti pakato kaṭo bhavatā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',286,replace(replace('<p class="bodytext">Pasupīti <a name="M0.0286"></a> pasutto bhavaṃ. Pasupīyitthāti pasuttaṃ bhavatā, pararūpaṃ.</p>\r\n<p class="subhead">58. Ṭhā+sa vasa silisa sī ruha jara janīhi</p>\r\n<p class="bodytext">Ṭhādīhi kattari kto hoti yathāpattañca. Upaṭṭhāsīti upaṭṭhīto bhavaṃ guruṃ. Upaṭṭhīyitthāti upaṭṭhito guru bhotā, ‘‘ṭhāssī’’ti ṭhāssa i. Evaṃ upāsīti upāsito. Upāsīyitthāti upāsito. ‘‘Ñi byañjanassā’’ti ñi. Anuvusi anuvusīyitthāti vā anuvusīto. ‘‘Assū’’ti assa ukāro. Āpubbo silisa=āliṅgane, āsilesi āsilesīyitthāti vā āsiliṭṭho. ‘‘Sānantarassa tassa ṭho’’ti ṭhe pararūpaṃ paṭhamakkharañca. Adhisayi adhisīyitthāti vā adhisayito khaṭopikaṃ bhavaṃ, adhisayitā khaṭopikā bhotā. Evaṃ āruhi āruhīyitthāti vā āruḷho rukkhaṃ bhavaṃ, āruḷho rukkho bhotā, ‘‘ruhādīhi ho ḷa cā’’ti tassa hakāro pubbahakārassa ca ḷo. Anujīri anujīrīyitthāti vā anujiṇṇo vasaliṃ bhavaṃ, anujiṇṇā vasalī bhotā, ‘‘tarādīhi riṇṇo’’ti ktassa riṇṇādeso ‘‘rānubandhe’’tyādinā arabhāgassa lopo ca. Anvajāyi anvajāyitthāti vā anujāto māṇavako māṇavikaṃ, anujātā māṇavikā māṇavakena, ‘‘janissā’’ti nassa ā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',287,replace(replace('<p class="subhead">59. Gamanatthā+kammakā+dhāre ca</p>\r\n<p class="bodytext">Gamanatthato <a name="M0.0287"></a> akammakato ca kriyatthā ādhāre kto hoti kattari ca yathāpattañca. Yā=pāpuṇane, yātavanto asminti yātaṃ ṭhānaṃ. Yātavanto yātā, yānaṃ yātaṃ. Ettha santampi kammaṃ anicchitaṃ. Yāyitāti yāto patho. ‘‘Ye gatyatthā, te buddhyatthā. Ye buddhyatthā, te gatyatthā’’ti vuttattā tathalakkhaṇaṃ yāthāvato āgato abhisambuddhoti tathāgato itipi hoti. Āsitavanto asminti āsitaṃ. Āsitavanto āsitā. Āsana+māsitaṃ.</p>\r\n<p class="subhead">60. Āhāratthā</p>\r\n<p class="bodytext">Ajjhohāratthā ādhāre kto hoti yathāpattañca. Bhuttavanto+sminti bhuttaṃ. Evaṃ pītaṃ, ‘‘gāpāna+mī’’ti ī. Bhuñjanaṃ bhuttaṃ pānaṃ pītaṃ. Bhuñjīyitthāti bhutto odano. Evaṃ pītaṃ udakaṃ. Akattatthaṃ bhinnayogakaraṇaṃ. Bahulādhikārā kattaripi ‘‘apiviṃsūti pītā gāvo’’ti hoteva. Sida=pāke, passijjīti passanno, ‘‘gamanattha’’ dinā kto, ‘‘bhidā’’ dinā tassa no pararūpañca. Amaññitthāti mato. Icchīyitthāti iṭṭho. Abujjhitāti buddho, ‘‘dho dhahabhehī’’ti tassa dho. Pūjīyitthāti pūjito, curādittā ṇi. Evaṃ sīlīyitthāti sīlito. Rakkhīyitthāti rakkhito. Khamīyitthāti khanto, massa niggahītaṃ. Akkocchīyitthāti akkuṭṭho, āssa rasso. Rusa=rose, arosīti ruṭṭho, ‘‘gamanatthā’’ dinā kattari kto. Ñimhi rusito. Hara=haraṇe, abhi+ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',288,replace(replace('<p class="bodytext">vi+āpubbo<a name="M0.0288"></a>, abhibyāharīyitthāti abhibyāhaṭo. Daya=dānagatihiṃsādānesu, adayīyitthāti dayito. Hasa=ālikye, ahasīti haṭṭho. Kāmīyitthāti kanto, ‘‘ṇiṇāpīnaṃ tesū’’ti ettha ‘‘ṇiṇāpīnaṃ’’ti yogavibhāgā curādiṇilopo. Saṃyamīti saṃyato. Nañapubbo, na marīti amato, nañasamāse nassa ṭo.</p>\r\n<p class="bodytext">Kaṭṭhaṃ dukkhaṃ āpannoti phalabhūte dukkhe kaṭṭhasaddassa vattanato phalassa bhāvittā bhūteyeva kto. Kasa=gatihiṃsāvilekhanesu, akasi=hiṃsīti kaṭṭhaṃ.</p>\r\n<p class="subhead">150. Bhidādito no kta+ktavantūnaṃ</p>\r\n<p class="bodytext">Bhidādīhi paresaṃ kta+ktavantūnaṃ tassa no hoti. Ākatigaṇo+yaṃ. ‘‘Kto bhāvakammesū’’ti kamme upari sabbattha kto. Bhijjitthoti bhinno, tassa ne pararūpaṃ. Abhindīti bhinnavā, ‘‘kattari bhūte’’ ccādinā ktavantu. Eva+muparipi. Chijjitthāti chinno. Achindīti chinnavā. Chada=apavāraṇe, chādīyitthāti channo. Achādayīti channavā, curādittā ṇi, tassa ‘‘ṇiṇāpīnaṃ tesū’’ti ettha ‘‘ṇi’’ iti yogavibhāgā lopo. Khida=asahane, khijjīti khinno khinnavā, kattari. Eva+muparipi. Uppajjīti uppanno uppannavā. Sida=pāke, asijjīti sinno sinnavā. Sada=visaraṇagatya vasādanādānesu, sidīti sanno sannavā. Pīnīti pīno pīnavā. Sū=pasave, sūnīti sūno. Pasavīti sūnavā. Dī=khaye, dīyīti dīno dīnavā. Ḍī+lī=ākāsagamane, ḍīyīti ḍīno ḍīnavā. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',289,replace(replace('<p class="bodytext">Līyi <a name="M0.0289"></a> leyi alīyīti līno līnavā. Aluyīti lūno lūnavā.</p>\r\n<p class="subhead">151. Dātvi+nno</p>\r\n<p class="bodytext">Dāto kta+ktavantūnaṃ tassa inno hoti. Adāyitthāti dinno. Addīti dinnavā.</p>\r\n<p class="subhead">152. Kirādīhi ṇoti</p>\r\n<p class="bodytext">Ṇo. Upari sabbattha kta+ktavantūnaṃti yojetabbaṃ. Kira=vikiraṇe, akirīyitthāti kiṇṇo. Akirīti kiṇṇavā. Apurīti puṇṇo puṇṇavā. Akhīyitthāti khīṇo. Akhīyīti khīṇavā.</p>\r\n<p class="subhead">153. Tarādīhi riṇṇoti</p>\r\n<p class="bodytext">Tassa riṇṇo. Rakāro antasarādilopattho. Atarīti tiṇṇo tiṇṇavā. Ajīrīti jiṇṇo jiṇṇavā. Paricīyitthāti ciṇṇo. Paricīyīti ciṇṇavā. Ettha cissa vikappavidhānattā pana paricita+upacitādayopi siddhā eva.</p>\r\n<p class="subhead">154. Go bhañjādīhiti</p>\r\n<p class="bodytext">Bhañjādito tassa ga hoti. Abhañjīti bhaggo bhaggavā. ‘‘Gamanatthā’’ dinā kattari ktapaccaye tassa iminā gakāre pararūpaṃ niggahītalopo ca. Laga=saṅge, alagīti laggo laggavā. Mujja=mujjane, nimujjīti nimuggo nimuggavā, ettha saṃyogādilopo. Vīja+bhayacalanesu, saṃpubbo, saṃvijjīti saṃviggo saṃviggavā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',290,replace(replace('<p class="subhead">155. Susā khoti</p>\r\n<p class="bodytext">Susato <a name="M0.0290"></a> paresaṃ kta+ktavantūnaṃ tassa kho hoti. Susa=sose. Sussīti sukkho sukkhavā.</p>\r\n<p class="subhead">156. Pacā ko</p>\r\n<p class="bodytext">Pacā paresaṃ kta+ktavantūnaṃ tassa ko hoti. Paccīti pakko. Pacīti pakkavā.</p>\r\n<p class="subhead">157. Mucā vāti</p>\r\n<p class="bodytext">Mucā paresaṃ kta+ktavantūnaṃ tassa ko vā hoti. Muca=mocane, muccīti mukko mutto. Amucīti mukkavā muttavā. Sakko-tiṇvādīsu ‘‘i bhī kā’’ dinā siddhaṃ. Asakkhīti sakko. Kta+ktavantūsu satto sattavātveva hoti.</p>\r\n<p class="subhead">106. Muha+bahānañca te kānubandhe+tve</p>\r\n<p class="bodytext">Muha+bahānaṃ duhissa ca dīgho hoti takārādo kānubandhe tvāna+tvāvajjite. Muha=vecitte, baha+braha+brūha vuddhiyaṃ. Muyhitta bayhitthāti mūḷho bāḷho, ‘‘gamanatthā’’ dinā akammakattā kattari kte dīgho ‘‘ruhādīhi ho ḷa cā’’ti tassa ho ca hassa ḷo ca hoti. Evaṃ gūḷho. ‘‘Kānubandhe+tve’’ti yāva ‘‘sāsassa sisa vā’’ tya+dhikāro.</p>\r\n<p class="subhead">107. Vahassu+ssa</p>\r\n<p class="bodytext">Vahassa ussa dīgho hoti te. Vuyhitthāti vūḷho, ‘‘assū’’ti ukāre iminā dīgho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',291,replace(replace('<p class="subhead">108. Dhāssa hi</p>\r\n<p class="bodytext">Dhā=dhāraṇe+timassa <a name="M0.0291"></a> hi hoti vā te. Nidhīyitthāti nihito. Nidahīti nihitavā nihitāvī.</p>\r\n<p class="subhead">109. Gamādirānaṃ lopo+ntassa</p>\r\n<p class="bodytext">Gamādīnaṃ rakārantānañca antassa lopo hoti te. Agamīti gato. Khaññitthāti khato, kamme. Evaṃ haññitthāti hato. Tana=vitthāre, taññitthāti tato. Yama=uparame, saṃyamīti saññato, ‘‘gamanatthā’’ dinā kattari kto, ‘‘ye saṃssā’’ti niggahītassa ño pubbarūpañca. Evaṃ aramīti rato. Karīyitthāti kato.</p>\r\n<p class="subhead">110. Vacādīnaṃ vassu+ṭa vā</p>\r\n<p class="bodytext">Vacādīnaṃ vassa uṭa vā hoti kānubandhe atve. Vuccitthāti uttaṃ. Assa u vuttaṃ, ubhayattha pararūpaṃ. Vasa=nivāse, vasanaṃ avasi vasiṃsu etthāti vā utthaṃ vutthaṃ, ‘‘gamanatthā’’ dinā kto, ‘‘sāsa vasa saṃsa saṃsā tho’’ti tassa tho.</p>\r\n<p class="subhead">112. Vaddhassa vā</p>\r\n<p class="bodytext">Vaddhassa assa vā u hoti kānubandhe atve. Avaddhīti vuddho vaddho, kattari kte ‘‘dho dha+habhehī’’ti tassa dho, tatiyakkharado ca saṃyogādilopo ca. Vuttīti ‘‘sabbādayo vuttimatte’’ti yogavibhāgā assa u. Vattītipi yathālakkhaṇaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',292,replace(replace('<p class="subhead">113. Yajassa yassa ṭi+yī</p>\r\n<p class="bodytext">Yajassa <a name="M0.0292"></a> yassa ṭi+yī honti kānubandhe atve. Yajanaṃti kto, ‘‘pucchādito’’ti tassa ṭhe pararūpādimhi kate iṭṭhaṃ yiṭṭhaṃ.</p>\r\n<p class="subhead">114. Ṭhāssi</p>\r\n<p class="bodytext">Ṭhāssa i hoti kānubandhe atve. Aṭṭhāsīti ṭhito.</p>\r\n<p class="subhead">115. Gā+pāna+mī</p>\r\n<p class="bodytext">Gā+pāna+mī hoti kānubandhe atve. Gānaṃ gītaṃ. Pānaṃ pītaṃ. Bahulādhikārā pitvā.</p>\r\n<p class="subhead">116. Janissā</p>\r\n<p class="bodytext">Janissa ā hoti kānubandhe atve. Ajanīti jāto, ‘‘chaṭṭhiyantassā’’ti antassa ā hoti.</p>\r\n<p class="subhead">117. Sāsassa sisa vā</p>\r\n<p class="bodytext">Sāsassa sisa hoti kānubandhe atve. Sāsanaṃti kto, tassa ‘‘sāsa vasa saṃsa sasā tho’’ti the pararūpaṃ paṭhamakkharo ca, ‘‘ta+tha+na+rā’’ dinā tassa ṭo. Thassa ṭho, siṭṭhaṃ. Aññatra satthaṃ. ‘‘Guhādīhi yaka’’ iti yakapaccaye sāsīyatīti sisso. Umhi sāsiyo.</p>\r\n<p class="subhead">140. Sānantarassa tassa ṭho</p>\r\n<p class="bodytext">Sakārantā kriyatthā parassā+nantarassa tassa ṭha hoti. Tussīti tuṭṭho, ‘‘gamanatthā’’ dinā kte iminā tassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',293,replace(replace('<p class="bodytext">ṭhe <a name="M0.0293"></a> pararūpādi. Evaṃ tuṭṭhavā. Tabba+ktīsu tosanaṃ tuṭṭhabbaṃ, ‘‘aññatrāpī’’ti okārābhāvo. Evaṃ tuṭṭhi, kānubandhattā na vuddhi.</p>\r\n<p class="subhead">141. Kasassi+ma ca vā</p>\r\n<p class="bodytext">Kasasmā parassā+nantarassa tassa ṭha hoti kasassa vā ima ca. Kasīyitthāti kiṭṭhaṃ. Kiṭṭhādiṃ viya duppasuṃ, kamme kto. Imābhāve kaṭṭhaṃ, akaṭṭhapākimaṃ sāliṃ.</p>\r\n<p class="subhead">142. Dhasto+trastā</p>\r\n<p class="bodytext">Ete saddā nipaccante. Dhaṃsa+dhaṃsane, dhaṃsīyitthāti dhasto, bindulopo. Utrasīti otrasto.</p>\r\n<p class="subhead">143. Pucchādito</p>\r\n<p class="bodytext">Pucchādīhi parassā+nantarassa tassa ṭha hoti. Pucchīyitthāti puṭṭho. Bhajja=pāke, bhajīyitthāti bhaṭṭho, saṃyogādilopo. Yajitthāti yiṭṭho, ‘‘yajassa yassa ṭi+yī’’ti yi.</p>\r\n<p class="subhead">144. Sāsa vasa saṃsa sasā tho</p>\r\n<p class="bodytext">Etehi parassā+nantarassa tassa tha hoti. Sāsanaṃ sāsīyitthāti vā satthaṃ. Vasanaṃ vutthaṃ, ‘‘assū’’ti u. Pasaṃsanaṃ pasaṃsīyitthāti vā pasatthaṃ. Sasa=gati+hiṃsā+pāṇanesu, sasanaṃ gamanaṃ hiṃsanaṃ jīvanañca satthaṃ. Anusāsīyitthāti anusiṭṭho, tthassa ṭṭho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',294,replace(replace('<p class="subhead">145. Dho dha+ha+tehi</p>\r\n<p class="bodytext">Dhakāra <a name="M0.0294"></a> hakāra bhakārantehi parassa tassa dha hoti. Vaddhitthāti kamme kte tassa dhakārādimhi ca ‘‘vaddhassa vā’’ti ukāre kate saṃyogādilopo, vuddho. Duyhitthāti duddhaṃ. Labhīyitthāti laddhaṃ.</p>\r\n<p class="subhead">146. Dahā ḍho</p>\r\n<p class="bodytext">Dahā parassā+nantarassa tassa ḍha hoti. Dayhitthāti daḍḍho, pararūpādimhi kate ‘‘catutthadutiye’’ ccādinā ḍakāro.</p>\r\n<p class="subhead">147. Bahassu+ma ca</p>\r\n<p class="bodytext">Bahā parassā+nantarassa tassa ḍha hoti bahassu+ma ca ḍhasanniyogena. Baha+braha+brūha=vuddhiyaṃ. Abahīti buḍḍho.</p>\r\n<p class="subhead">148. Ruhādīhi ho ḷa ca</p>\r\n<p class="bodytext">Ruhādīhi parassā+nantarassa tassa ha hoti ḷo ca+ntassa. Āruhīti āruḷho. Guyhitthāti guḷho. Abahīti bāḷho. Dvīsu ‘‘muhabahā’’ dinā dīgho.</p>\r\n<p class="subhead">149. Muhā vāti</p>\r\n<p class="bodytext">Ktatassa hakāro antassa vā ḷo ca. Amohīti muḷho, muddho, tassa dho. (Atītakālikapaccayavidhānaṃ).</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',295,replace(replace('<p class="subhead">64. Nto kattari vattamāne</p>\r\n<p class="bodytext">Vattamānatthe <a name="M0.0295"></a> vattamānato kriyatthā nto hoti kattari. Tiṭṭhatīti tiṭṭhanto, ‘‘kattari lo’’ti lo, ‘‘ṭhāpānaṃ tiṭṭhapivā’’ti tiṭhādeso. Kattari māna+nta+tyādisu imināva lo.</p>\r\n<p class="subhead">65. Mānoti</p>\r\n<p class="bodytext">Kattari māno. Tiṭṭhamāno.</p>\r\n<p class="subhead">66. Bhāvakammesuti</p>\r\n<p class="bodytext">Bhāvakammesu māno. Ṭhānaṃ ṭhīyamānaṃ. ‘‘Kyo bhāvakammesva+parokkhesu māna+nta+tyādīsū’’ti bhāve kamme ca kyo. ‘‘Kyassā’’ti īma. Pacīyatīti paccamā no. Kye pubbarūpaṃ.</p>\r\n<p class="subhead">67. Te ssapubbā+nāgate</p>\r\n<p class="bodytext">Anāgate atthe vattamānato kriyatthā tentamānā ssapubbā honti, le āssa lopo, ṭhassatīti ṭhassanto ṭhassamāno. Ṭhīyissatīti ṭhīyissamānaṃ, kye ī, ‘‘ñibyañjanassā’’ti ñi. Assa tyādivisayattā ‘‘āīssā’’ dinā iña na hoti, aīhi sahacaritattā ssāssa. Paccissatīti paccissamāno odano. ‘‘Rā nassa ṇo’’ti ṇe patte –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',296,replace(replace('<p class="subhead">5,172. Na nta+māna+tyādīnaṃ</p>\r\n<p class="bodytext">Rantato <a name="M0.0296"></a> paresaṃ nta+māna+tyādīnaṃ nassa ṇo na hoti. Karotīti karonto kurumāno, ‘‘tanāditvo’’ti o.</p>\r\n<p class="subhead">5,173. Gama yami+sāsa disānaṃ vā cchaṅa</p>\r\n<p class="bodytext">Etesaṃ vā cchaṅa hoti nta+māna+tyādīsu. Ṅa-nubandhattā antassa hoti, gacchanto gacchamāno. Yama=uparame, yacchanto yacchamāno. Icchanto icchamāno. Āsa=upavesane, acchanto acchamāno, ‘‘byañjane’’ccādinā rasso. Disa=atisajjane, dicchanto dicchamāno. Vavatthitavibhāsattā vāsaddassa aññapaccayesu ca kvaci, icchīyatīti icchitabbaṃ, icchanaṃ icchā, ‘‘itthiya+maṇā’’ dinā appaccayo. Icchitaṃ, icchitabbaṃ, icchituṃ. Aññesañca yogavibhāgā, pavecchanto.</p>\r\n<p class="subhead">174. Jara+marāna+mīyaṅa</p>\r\n<p class="bodytext">Etesa+mīyaṅa vā hoti nta+māna+tyādīsu. Jīyanto. ‘‘Jarasadā’’ diccādinā īma. Jīranto. Jīyamāno jīramāno. Mīyanto maranto, mīyamāno maramāno.</p>\r\n<p class="subhead">175. Ṭhāpānaṃ tiṭṭhapivā</p>\r\n<p class="bodytext">Ṭhāpānaṃ tiṭṭhapivā honti vā ntādīsu. Tiṭṭhanto, tiṭṭhamāno. Pivanto pivamāno.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',297,replace(replace('<p class="subhead">176. Gama+vada+dānaṃ ghamma+vajja+dajjā</p>\r\n<p class="bodytext">Gamādīnaṃ <a name="M0.0297"></a> ghammādayo vā honti ntādīsu. Ghammanto gacchanto, vajjanto vadanto, dajjanto dadanto.</p>\r\n<p class="subhead">177. Karassa sossa kubba kuru kayirā</p>\r\n<p class="bodytext">Karassa saokārassa kubbādayo vā honti ntādīsu. Kubbanto kayiranto karonto, evaṃ kubbamāno iccādi. Aññatra ‘‘mānassa massā’’ti mānassa massa lope karāṇo. ‘‘Sossā’’ti vuttattā kattariyeva.</p>\r\n<p class="subhead">178. Gahassa gheppo</p>\r\n<p class="bodytext">Gahassa vā gheppo hoti ntādīsu. Gheppanto gheppamāno.</p>\r\n<p class="subhead">179. Ṇo niggahītassa</p>\r\n<p class="bodytext">Gahassa niggahītassa ṇo hoti, ‘‘maṃ vā rudhādīnaṃ’’ti maṃ, gaṇhitabbaṃ, gaṇhituṃ, gaṇhanto.</p>\r\n<p class="subhead">130. Nta+māna+nti+yi+yuṃsvā+dilopoti</p>\r\n<p class="bodytext">Ādilopo. Asa=bhuvi, bhavatīti santo samāno.</p>\r\n<p class="subhead">131. Pādito ṭhāssa vā ṭhaho kvaciti</p>\r\n<p class="bodytext">Ṭhāssa pādīsu ṭhaho. Saṇṭhahanto. Santiṭṭhanto.</p>\r\n<p class="bodytext">(Vattamānapaccayantanayo).</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',298,replace(replace('<p class="subhead">61. Tuṃ tāye tave bhāve bhavissati kriyāyaṃ tadatthāyaṃ</p>\r\n<p class="bodytext">Bhavissatiatthe <a name="M0.0298"></a> vattamānato kriyatthā bhāve tumādayo honti kriyāyaṃ tadatthāyaṃ patīyamānāyaṃ. Karaṇāya gacchati kātuṃ gacchati. ‘‘Tuṃtunatabbesu vā’’ti karassa vā ā hoti, pubbarūpe kattāye. ‘‘Karassā tave’’ti niccaṃ ā, kātave. Nipātattā ‘‘asaṅkhyehi sabbāsaṃ’’ti catutthīsassa lopo. Eva+mupari tunādīsu. Kattuṃ kāmetīti kattukāmo, abhisaṅkharitu+mākaṅkhati. Saddhammaṃ suṇituṃ, ñimhi nāgame tassa ṇo, nāgamattā na vuddhi. Sotave sotuṃ suṇituṃ vā patthehi. Evaṃ anubhavituṃ, pacituṃ. Gantuṃ, ‘‘manānaṃ niggahītaṃ’’ti niggahītaṃ. Gamituṃ, khantuṃ, khanituṃ, hantuṃ, hanituṃ, mantuṃ, manituṃ, harituṃ, anussarituṃ icchati. Tathā tudituṃ, pavisituṃ, uddisituṃ, bhottuṃ, sayituṃ, netuṃ, juhotuṃ, pajahituṃ, pahātuṃ, dātuṃ, roddhuṃ, rundhituṃ, bhottuṃ, bhuñjītuṃ, chettuṃ, chindituṃ, sibbituṃ, ūmhi vassa bo, dvittaṃ, buddhuṃ, tassa dho. Bujjhituṃ, yuka. Jānituṃ ñā=avabodhane. Janituṃ. Jetuṃ jinituṃ. Pattuṃ pāpuṇituṃ. Ketuṃ kiṇituṃ. Vinicchetuṃ, issa e, vinicchinituṃ. Gahetuṃ gaṇhituṃ. Corituṃ coretuṃ corayituṃ, pāletuṃ pālayituṃ. Payojake-bhāvetuṃ bhāvayituṃ, kāretuṃ kārayituṃ kārāpetuṃ kārāpayituṃ icchati+ccādi.</p>\r\n<p class="bodytext">Evaṃ kriyatthakriyāyaṃ gamyamānāyaṃ, yathā suboddhuṃ vakkhāmi, evaṃ daṭṭhuṃ gacchati, gantu+mārabhati, gantuṃ payojeti, dassetu+māha iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',299,replace(replace('<p class="bodytext">Arahasakkādīsupi <a name="M0.0299"></a> siddhaṃ, tathā kāla samayavelāsu. Ko taṃ ninditu+marahati, rājā bhavitu+marahati, araho bhavaṃ vattuṃ, sakkā jetuṃ dhanena vā, sakkā laddhuṃ, bhassa dho. Kattuṃ sakkhissati. Bhabbo niyāmaṃ okkamituṃ, abhabbo kātuṃ. Anucchaviko bhavaṃ dānaṃ paṭiggahetuṃ, idaṃ kātuṃ anurūpaṃ, dānaṃ dātuṃ yuttaṃ, dātuṃ vattuñca labhati. Evaṃ vaṭṭati bhāsituṃ, chindituṃ na ca kappati iccādi. Tathākālo bhuñjituṃ, samayo bhuñjituṃ, velā bhuñjituṃ.</p>\r\n<p class="bodytext">Alamatthepi siddhaṃ, alameva dānāni dātuṃ, alameva puññāni kātuṃ.</p>\r\n<p class="subhead">62. Paṭisedhe+laṃkhalūnaṃ ktuna ktvāna ktvā vā</p>\r\n<p class="bodytext">Alaṃ+khalusaddānaṃ paṭisedhanatthānaṃ payoge ktunādayo vā honti bhāve. Alaṃpubbo, su=savane, alaṃ savanaṃ katvā khalu savanaṃ katvāti alaṃ sotuna khalu sotuna alaṃ sutvāna khalu sutvāna alaṃ sutvā khalu sutvā. Nipātattā silopo.</p>\r\n<p class="subhead">63. Pubbe+kakattukānaṃ</p>\r\n<p class="bodytext">Eko kattā yesaṃ byāpārānaṃ, tesu yo pubbo, tadatthato kriyatthā tunādayo honti bhāve. ‘‘Tuṃtunā’’ dinā karassa ā, so kātuna kammaṃ gacchati, akātuna puññaṃ kilissanti. Ralope kammaṃ katvāna bhadrakaṃ, puññāni katvā saggaṃ gacchati. Abhisaṅkharaṇaṃ katvā abhisaṅkharitvā karitvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',300,replace(replace('<p class="bodytext">vā<a name="M0.0300"></a>. Tathā sibbitvā chādayitvā jānitvā dhammaṃ sutvā sutvāna dhammaṃ modati, suṇitvā, patvā pāpuṇitvā, kinitvā, jetvā jinitvā jitvā, coretvā corayitvā, pūjetvā pūjayitvā, tathā mettaṃ bhāvetvā bhāvayitvā, vihāraṃ kāretvā kārayitvā kārāpetvā kārāpayitvā saggaṃ gamissanti+ccādi. Pubbeti kiṃ, bhuñjati ca pacati ca. ‘‘Apatvā nadiṃ pabbato, atikkamma pabbataṃ nadī’’tiādīsu bhūdhātussa sambhavā ekakattukatā pubbakālatā ca gamyate. ‘‘Bhutvā bhutvā gacchatī’’ti imināva siddhaṃ, ābhikkhaññantu dvibbacanāva gamyate. Kathaṃ ‘‘jīvaggāhaṃ agāhayi, kāyappacālakaṃ gacchatī’’ti, ghaṇantena kriyāvisesanena siddhaṃ, yathā odanapākaṃ sayatīti.</p>\r\n<p class="subhead">164. Pyo vā tvāssa samāse</p>\r\n<p class="bodytext">Tvāssa vā pyo hoti samāse. Pakāro ‘‘pye sissā’’ti visesanattho. Abhipubbo pādisamāso, abhivadanaṃ katvā abhivādiya bhāsissaṃ. Tathā abhibhuyya, dvittarassāni. Si=saye –</p>\r\n<p class="subhead">88. Pye sissā</p>\r\n<p class="bodytext">Sissa ā hoti pyādese. Nissayanaṃ katvā nissāya. Pubbarūpe vibhajja vibhajiya. Disa=atisajjane, uddissa. Pavissa pavisiya pavisitvā, upanaya, atiseyya atisayitvā, ohāya ohitvā. Ādāya, dāssi+yaṅa, ādiya. Paṭṭhāya, viceyya, viññāya vijānitvā, samāseti kiṃ, patvā. Kvacā+ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',301,replace(replace('<p class="bodytext">samāsepi <a name="M0.0301"></a> bahulādhikārā, lataṃ dantehi chindiya. Tathā bhuñjitvā.</p>\r\n<p class="subhead">165. Tuṃ+yānā</p>\r\n<p class="bodytext">Tvāssa vā tuṃ+yānā honti samāse kvaci. Abhiharaṇaṃ katvā abhihaṭṭhuṃ, abhiharitvā, pādisamāso, ‘‘pucchādito’’ti tassa ṭho pararūpādi ca. Anumodanaṃ katvā anumodiyāna anumoditvā vā, kānubandhepi ‘‘vā kvacī’’ti vikappattā ‘‘lahussupantassā’’ti okāro. Kvacā+samāsepi bahulādhikārā, daṭṭhuṃ, disato tvāssa tumādese disassa dasa. Aññatra ‘‘disā vāna+vā sa cā’’ti tvāssa vāna+vā sa honti, disvā. Evaṃ labhanaṃ katvā laddhā dhanaṃtiādīsu tassa dho bhavati.</p>\r\n<p class="bodytext">Iti payogasiddhiyaṃ khādikaṇḍo sattamo.</p>\r\n<p class="title">Payogasiddhi nigamanaṃ</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',302,replace(replace('<p class="gatha1">Ye <a name="M0.0302"></a> nantatantaratanākaramanthanena,</p>\r\n<p class="gatha2">Manthācalollasitañāṇavarena laddhā;</p>\r\n<p class="gatha3">Sārāmatā+tisukhitā sukhayanti ca+ññe,</p>\r\n<p class="gathalast">Te me jayanti guravo guravo guṇehi.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Yassa sādhuguṇubbhūta-kitti sabbatta patthaṭā;</p>\r\n<p class="gathalast">Moggallāno mahāpañño, jayatī so ha sabbadā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Paramappicchatā+neka-santosūpasamesinaṃ;</p>\r\n<p class="gathalast">Sucisalekhavuttīnaṃ, sadā+raññanivāsinaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Sāsanujotakārīna+māceratta+mupāgataṃ;</p>\r\n<p class="gathalast">Udumbaragirīkhyātā+yatanaṃ yatipuṅgavaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Medhaṅkaroti ākhyāta-nāmadheyyaṃ tapodhanaṃ;</p>\r\n<p class="gathalast">Theraṃ thiradayā medhā-nidhānaṃ sādhu pūjitaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Sissaṃ sahāya+māgamma, kalyāṇamitta+mattano;</p>\r\n<p class="gathalast">Sodhetuṃ sāsanaṃ satthu, parakkama+makāsi yo.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Saṅgharakkhitanāmena, mahātherena dhīmatā;</p>\r\n<p class="gathalast">Nivāsabhūtenā+neka-guṇāna+ppicchatādinaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Moggallānabyākaraṇa-payogakkamasādhakā;</p>\r\n<p class="gathalast">Ettāvatā katā esā, payogasiddhi niṭṭhitā.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-4-5-6-7-8-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',303,replace(replace('<p class="gatha1">Teneva <a name="M0.0303"></a> racitā sādhu, sāsanodayakārinā;</p>\r\n<p class="gathalast">Khuddasikkhāya ṭīkā ca, tathā sambandhacintanā.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Susaddasiddhiṃ yo yoga-nicchayaṃ sabbhi vaṇṇitaṃ;</p>\r\n<p class="gathalast">Akā subodhālaṅkāraṃ, tathā sambandhacintanaṃ.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Satthasañcitapuññena, nibbānasādhakaṃ hitaṃ;</p>\r\n<p class="gathalast">Sādhento lokanāthassa, saddhammo tiṭṭhataṃ ciranti.</p>\r\n<p class="centered">Iti saṅgharakkhitamahāsāmitherapādaviracitā</p>\r\n<p class="centered">Payogasiddhi niṭṭhitā.</p>\r\n<p class="bodytext">(Visesalakkhaṇaṃ). Yathāvuttānaṃ pana payogasiddhinigamana gāthānaṃ ekādasannaṃ pubbeyeva vakkhamānā imā gāthāyo dissanti porāṇe sīhaḷamūlapayogasiddhipāṭhe. Tā ca gāthāyo pacchā sīhaḷamūlapayogasiddhisaṃsodhakena medhaṅkaroti garūhi gahitanāmadheyyena therena pakkhittāti veditabbā, tasmā mayaṃ tā gāthāyo sabbapacchāyeva imasmiṃ-ṭhāne ṭhapema. Tā pana gāthāyo katamāti ce –</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> Cāgavikkamasaddhānu-sampannaguṇasāmino;</p>\r\n<p class="gathalast">Parakkamanarindassa, sīhaḷindassa dhīmato.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Atrajenā+nujātena, bhūpālakulaketunā;</p>\r\n<p class="gathalast">Disantapatthaṭodāra-vikkamena yasassinā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Bhuvanekabhujavhena, mahārājena dhīmatā;</p>\r\n<p class="gathalast">Catupaccayadānena, satataṃ samupaṭṭhito.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-11-1-2-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_80',304,replace(replace('<p class="gatha1">‘‘Jambudoṇī’’ti <a name="M0.0304"></a> vikhyātā –</p>\r\n<p class="gatha2">Vāse nivasato sato;</p>\r\n<p class="gatha3">Sumaṅgalamahatthera –</p>\r\n<p class="gathalast">Sāmino sucivuttino.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Vaṃse visuddhe saṃjāto,</p>\r\n<p class="gatha2">Panthasenāsane rato;</p>\r\n<p class="gatha3">Pariyattimahāsindhu –</p>\r\n<p class="gathalast">Niyyāmakadhurandharo.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Appicchādiguṇūpeto, jinasāsanamāmako;</p>\r\n<p class="gathalast">Vaneratamahatthero, medhaṅkarasamavhayo.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Pāṭavatthāya bhikkhūnaṃ, vinaye suvisārado;</p>\r\n<p class="gathalast">Payogasiddhiṃ sodhayi, sadā sappaññagocaraṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Imaṃ likhitapuññena, mettekyaṃ upasaṃkami;</p>\r\n<p class="gathalast">Patiṭṭhahitvā saraṇe, suppatiṭṭhāmi sāsaneti.</p>\r\n<p class="centered">Payogasiddhisiddhaṃ.</p>','\r',char(13)),'\n',char(10)),'-5-5-6-7-');