INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',1,replace(replace('<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa.</p>\r\n<p class="book">Bālāvatāra</p>\r\n<p class="subhead">Paṇāma</p>\r\n<p class="bodytext">[Ka]</p>\r\n<p class="gatha1">Sabbaṃ niruttipathapāragataṃ sabuddhaṃ,</p>\r\n<p class="gatha2">Buddhaṃ tilokatilakaṃ hatapāpadhammaṃ;</p>\r\n<p class="gatha3">Dhammaṃ vimuttisukhadaṃ vihatāghasaṃghaṃ,</p>\r\n<p class="gathalast">Saṃghaṃ ca niccamabhivandiya dakkhiṇeyyaṃ.</p>\r\n<p class="bodytext">[Kha]</p>\r\n<p class="gatha1">Vuddhippattosmi muddho mama varagaravo sīlapaññādisobhe,</p>\r\n<p class="gatha2">Candādicceva suddhe varajinaṭhapite sāsanābbhe patīte;</p>\r\n<p class="gatha3">Nissāyevā tipemā paṇamiya sirasā niccamesaṃ saritvā,</p>\r\n<p class="gathalast">Pādambhoje guṇagge hataduritamalo ānubhāvena tassā.</p>\r\n<p class="bodytext">[Ga]</p>\r\n<p class="gatha1">Porāṇa sīhaḷa padattha vinicchayañca,</p>\r\n<p class="gatha2">Sabbampi māgadhaniruttinayaṃ pasatthaṃ;</p>\r\n<p class="gatha3">Aññañca nekavidha sakkata saddasatthaṃ,</p>\r\n<p class="gathalast">Pāramparābhata matañca nisamma sammā.</p>\r\n<p class="bodytext">[Gha]</p>\r\n<p class="gatha1">Bālāvatāra varamāgadha saddasatthe,</p>\r\n<p class="gatha2">Dubbodha nekapadaattha vinicchayena;</p>\r\n<p class="gatha3">Atthāya ādhunika bālaparamparāya,</p>\r\n<p class="gathalast"><span class="bld">Bālāvatāra varagaṇṭhipadaṃ</span> karissaṃ.</p>\r\n<p class="bodytext">[Ṅa]</p>\r\n<p class="gatha1">Niccaṃ ye mettha bālā varahadayayutā suṭṭhu nikkhittanettā,</p>\r\n<p class="gatha2">Puṇṇe nānānayānaṃ suratarusadise dhīrapāsaṃsiye ve;</p>\r\n<p class="gatha3">Gambhīraṃ duttaraṃ te jinavacanudadhiṃ tiṇṇathāmā bhaveyyuṃ,</p>\r\n<p class="gathalast">Laddhopāyā ca candaṃ taditarapacuraṃ sakkataṃ sotukāmā.</p>\r\n<p class="bodytext">[Ca]</p>\r\n<p class="gatha1">Ganthanipphattiyā sesa – dukkarattaṃ yathāvato;</p>\r\n<p class="gatha2">Jānanti kusalā dhīrā, nekasatthantarādisu;</p>\r\n<p class="gathalast">Ganthesu guṇadosampi, teyeva vidurā sadā.</p>\r\n<p class="bodytext">[Cha]</p>\r\n<p class="gatha1">Tasmā ettha pamādādi – dosaleso bhave yadi;</p>\r\n<p class="gathalast">Porāṇāceraladdhīhi, vilomaṃ vā bhaveyya ce.</p>\r\n<p class="bodytext">[Ja]</p>\r\n<p class="gatha1">Ganthantaraṃ vigāhetvā, vicāretvā punappunaṃ;</p>\r\n<p class="gathalast">Yuttimeva ca gaṇhantu, hutvā vīmaṃsabuddhikāti.</p>\r\n<p class="subhead">Paṇāma</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Buddhaṃ <a name="M0.0001"></a> tidhā bhivanditvā, buddhambuja vilocanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Bālāvatāraṃ</span> bhāsissaṃ, bālānaṃ buddhivuddhiyā.</p>\r\n<p class="chapter">1. Sandhi kaṇḍa</p>\r\n<p class="title">Saññā</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. <span class="bld">Akkharāpādayo ekacattālīsaṃ.</span></p>','\r',char(13)),'\n',char(10)),'-1-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',6,replace(replace('<p class="bodytext">Akkharāpi <a name="M0.0006"></a> akārādayo ekacattālīsaṃ suttantopakārā. Taṃ yathā-a ā-i ī-u ū-e o, ka kha ga gha ṅa, ca cha ja jha ña, ṭa ṭha ḍa ḍha ṇa, ta tha da dha na, pa pha ba bha ma, ya ra la va sa ha ḷa aṃ-iti.</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. <span class="bld">Tatthodantāsarā aṭṭha.</span></p>','\r',char(13)),'\n',char(10)),'-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',8,replace(replace('<p class="bodytext">Tattha <a name="M0.0008"></a> akkharesu okārantā aṭṭha sarā nāma. Tattheti vattate.</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. <span class="bld">Lahumattā tayo rassā.</span></p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',9,replace(replace('<p class="bodytext">Tattha <a name="M0.0009"></a> saresu lahumattā a, i, u iti tayo rassā.</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>. <span class="bld">Aññe dīghā.</span></p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',10,replace(replace('<p class="bodytext">Tattha <a name="M0.0010"></a> saresu rassehaññe dīghā.</p>\r\n<p class="bodytext">Saṃyogato pubbe eo rassā ivoccante, anantarā byañjanā saṃyogo. Ettha, seyyo, oṭṭho, sotthi.</p>\r\n<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>. <span class="bld">Sesā byañjanā.</span></p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',12,replace(replace('<p class="bodytext">Sare <a name="M0.0012"></a> ṭhapetvā sesā kādayo niggahītantā byañjanā.</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>. <span class="bld">Vaggā pañcapañcaso mantā.</span></p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',13,replace(replace('<p class="bodytext">Byañjanānaṃ <a name="M0.0013"></a> kādayo makārantā pañcapañcaso akkharavanto vaggā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',16,replace(replace('<p class="bodytext"><a name="para8"></a><span class="paranum">8</span>. Vaggānaṃ <a name="M0.0016"></a> paṭhamadutiyā so cāghoso. Ḷantāññe ghosā. Ghosāghosasaññā ca ‘‘parasamaññā payoge’’ti saṅgahītā. Evaṃ liṅga, sabbanāma, pada, upasagga, nipāta, taddhita, ākhyāta, kammappavacanīyādisaññā ca.</p>\r\n<p class="bodytext"><a name="para9"></a><span class="paranum">9</span>. <span class="bld">Aṃiti niggahītaṃ.</span></p>','\r',char(13)),'\n',char(10)),'-8-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',18,replace(replace('<p class="bodytext">Aṃiti <a name="M0.0018"></a> akārato paraṃ yo bindu sūyate, <span class="bld">taṃ nigga</span>hītaṃ nāma.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',19,replace(replace('<p class="bodytext">Bindu <a name="M0.0019"></a> cūḷāmaṇākāro, niggahītanti vuccate.</p>\r\n<p class="bodytext">Kevalassā ppayogattā, akāro sannidhīyate.</p>\r\n<p class="bodytext"><a name="para10"></a><span class="paranum">10</span>. A, kavagga, hā kaṇḍajā, i, cavagga, yā tālujā, u, pavaggā oṭṭhajā, ṭavagga, ra, ḷā muddhajā, tavagga, la, sā dantajā, e kaṇṭhatālujo, o kaṇṭhoṭṭhajo, vo dantoṭṭhajo.</p>\r\n<p class="centered">Saññā</p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',21,replace(replace('<p class="title">Sarasandhi</p>\r\n<p class="bodytext"><a name="para11"></a><span class="paranum">11</span>. Loka <a name="M0.0021"></a> aggoityasmiṃ – ‘‘pubbamadhoṭhita massaraṃ sarena viyojaye’’ti pubbabyañjanaṃ sarato puthakkātabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Sarā sare lopaṃ</span>.</p>\r\n<p class="bodytext">Anantare sare pare sarā lopaṃ papponti.</p>','\r',char(13)),'\n',char(10)),'-11-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',22,replace(replace('<p class="bodytext">‘‘Nare <a name="M0.0022"></a> paraṃ yutte’’ti assaro byañjano parakkharaṃ netabbo lokaggo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',24,replace(replace('<p class="bodytext">Saretyasmiṃ <a name="M0.0024"></a> opasilesiko kāsasattamī, tato vaṇṇakālabyavadhāne kāriyaṃ na hoti. Yathā-maṃ ahā sīti, ‘‘pamādamanuyuñjantī’’tyādigāthāyaṃ ‘janā appamāda’nti ca. Evaṃ sabbasandhīsu.</p>\r\n<p class="bodytext">Anantaraṃ parassa sarassa lopaṃ vakkhati, tasmānena pubbassa lopo ñāyati, teneva sattamīniddiṭṭhassa paratāpi gamyate.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',26,replace(replace('<p class="bodytext"><a name="para12"></a><span class="paranum">12</span>. Saretyadhikāro. <a name="M0.0026"></a> Pana ime pana imetīha-sarā lopaṃ itveva.</p>\r\n<p class="bodytext"><span class="bld">Vā paro asarūpo</span>.</p>\r\n<p class="bodytext">Asamānarūpāsaramhā paro saro vā lupyate, paname, panime.</p>','\r',char(13)),'\n',char(10)),'-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',27,replace(replace('<p class="bodytext"><a name="para13"></a><span class="paranum">13</span>. Bandhussa <a name="M0.0027"></a> iva, na upetītīdha –</p>\r\n<p class="bodytext"><span class="bld">Kvacāsavaṇṇaṃ lutte</span>.</p>\r\n<p class="bodytext">Sare lutte parasarassa kvaci asavaṇṇo hotīti i u iccetesaṃ ṭhānāsannā e o. Bandhusseva. Nopeti.</p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',29,replace(replace('<p class="bodytext"><a name="para14"></a><span class="paranum">14</span>. Tatra <a name="M0.0029"></a> ayaṃ, yāni idha, bahu upakāraṃ, saddhā idha, tathā upamantyetasmiṃ –</p>\r\n<p class="bodytext"><span class="bld">Dīghaṃ</span>.</p>\r\n<p class="bodytext">Sare lutte paro saro kvaci ṭhānāsannaṃ dīghaṃ yāti. Tatrāyaṃ, yānīdha, bahūpakāraṃ, saddhīdha, tathūpamaṃ.</p>\r\n<p class="bodytext"><a name="para15"></a><span class="paranum">15</span>. Kiṃsu idhetyatra –</p>\r\n<p class="bodytext"><span class="bld">Pubbo ca</span>.</p>\r\n<p class="bodytext">Sare lutte pubbo ca kvaci dīghaṃ yāti. Kiṃ sūdha.</p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',30,replace(replace('<p class="bodytext"><a name="para16"></a><span class="paranum">16</span>. Te <a name="M0.0030"></a> ajja, te ahaṃtettha –</p>\r\n<p class="bodytext"><span class="bld">Yamedantassādeso</span>.</p>\r\n<p class="bodytext">Sare pare antassa ekārassa kvaci yo ādeso hoti, tyajja, ‘‘dīgha’’nti byañjane pare kvaci dīgho, tyāhaṃ.</p>\r\n<p class="bodytext">Kvacīti kiṃ. Nettha.</p>','\r',char(13)),'\n',char(10)),'-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',33,replace(replace('<p class="bodytext"><a name="para17"></a><span class="paranum">17</span>. So <a name="M0.0033"></a> assa, anu etityettha –</p>\r\n<p class="bodytext"><span class="bld">Vamodudantānaṃ</span>.</p>\r\n<p class="bodytext">Sare pare anto kārukārānaṃ kvaci vo ādeso hoti. Svassa, anveti.</p>\r\n<p class="bodytext">Kvacīti kiṃ. Tayassu, sametāyasmā.</p>','\r',char(13)),'\n',char(10)),'-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',34,replace(replace('<p class="bodytext"><a name="para18"></a><span class="paranum">18</span>. Idha <a name="M0.0034"></a> ahaṃ tīdha –</p>\r\n<p class="bodytext"><span class="bld">Do dhassa ca</span>.</p>\r\n<p class="bodytext">Sare pare dhassa kvaci do hoti. Dīghe – idāhaṃ. Kvacīti kiṃ. Idheva. Cakārena byañjanepi, idha bhikkhave.</p>\r\n<p class="bodytext"><a name="para19"></a><span class="paranum">19</span>. Pati antuṃ, vutti assetīha –</p>\r\n<p class="bodytext"><span class="bld">Ivaṇṇo yannavā</span>.</p>\r\n<p class="bodytext">Sare pare ivaṇṇassa yo navā hoti. Kata yakārassa tissa ‘‘sabbo cantī’’ti kvaci cādese ‘‘paradvebhāvo ṭhāne’’ti sarato parabyañjanassa ṭhānāsannavasā dvittaṃ. Paccantaṃ, vutyassa.</p>\r\n<p class="bodytext">Navāti kiṃ. Paṭaggi,</p>\r\n<p class="bodytext">Ettha ‘‘kvaci paṭi patisse’’ti patissa paṭi, vaṇṇaggahaṇaṃ sabbattha rassadīgha saṅgahaṇatthaṃ.</p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',36,replace(replace('<p class="bodytext"><a name="para20"></a><span class="paranum">20</span>. Yathā <a name="M0.0036"></a> evetīha –</p>\r\n<p class="bodytext"><span class="bld">Evādissa ri pubbo ca rasso</span>.</p>\r\n<p class="bodytext">Sarato parassa evassādiekāro rittaṃ navā yāti. Pubbo ca ṭhānāsannaṃ rassaṃ. Yathariva. Yatheva.</p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',37,replace(replace('<p class="bodytext"><a name="para21"></a><span class="paranum">21</span>. Na <a name="M0.0037"></a> imassa, ti aṅgikaṃ, lahu essati, attha atthaṃ, ito āyati, tasmā iha, sabbhi eva, cha abhiññā, putha eva, pā evatīha vā tveva –</p>\r\n<p class="bodytext"><span class="bld"> Ya va ma da na ta ra ḷā cāgamā</span>.</p>\r\n<p class="bodytext">Sare pare yādayo āgamā vā honti, cakārena go ca. Nayimassa, tivaṅgikaṃ, lahumessati, attadatthaṃ, itonāyati, tasmātiha, sabbhireva, chaḷabhiñā, puthageva, ‘‘rassa’’nti byañjane pare kvaci rasso. Pageva.</p>\r\n<p class="bodytext">Vāti kiṃ. Cha abhiññā, putha eva, pā eva.</p>\r\n<p class="bodytext">Ettha ‘‘sare kvacī’’ti sarānaṃ pakati hoti, sassarūpameva, na vikārotyattho.</p>','\r',char(13)),'\n',char(10)),'-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',39,replace(replace('<p class="bodytext"><a name="para22"></a><span class="paranum">22</span>. Abhi <a name="M0.0039"></a> uggatotyatra –</p>\r\n<p class="bodytext">‘‘Abbho abhī’’ti abhissa abbho. Abbhuggato.</p>\r\n<p class="centered">Sarasandhi</p>\r\n<p class="title">Byañjanasandhi</p>\r\n<p class="bodytext"><a name="para23"></a><span class="paranum">23</span>. Byañjanetyadhikāro. Kvacītveva. So bhikkhu, kacci nu tvaṃ, jānema tantīha –</p>\r\n<p class="bodytext"><span class="bld">Lopañca tatrākāro</span>.</p>\r\n<p class="bodytext">Byañjane pare sarānaṃ kvaci lopohoti, tatra lutte ṭhāne akārāgamo, cakārena okārukārāpi. Sabhikkhu kaccino tvaṃ, jānemu taṃ.</p>\r\n<p class="bodytext">Kvacīti kiṃ, somuni.</p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',40,replace(replace('<p class="bodytext"><a name="para24"></a><span class="paranum">24</span>. Ughoso<a name="M0.0040"></a>, ākhātantīha – dvebhāve ṭhāne itveva.</p>\r\n<p class="bodytext"><span class="bld">Vagge ghosāghosānaṃ tatiyapaṭhamā</span>.</p>\r\n<p class="bodytext">Vagge ghosāghosānaṃ catutthadutiyānaṃ tabbagge tatiyapaṭhamā honti yathāsaṅkhyaṃ yutte ṭhāne, ugghoso, rasse akkhātaṃ.</p>','\r',char(13)),'\n',char(10)),'-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',41,replace(replace('<p class="bodytext"><a name="para25"></a><span class="paranum">25</span>. Para <a name="M0.0041"></a> sahassaṃ, atippakhotīha – ‘‘kvaci o byañjane’’ti okārāgamo. Parosahassaṃ. Gāgame ca, atippagokho.</p>\r\n<p class="bodytext"><a name="para26"></a><span class="paranum">26</span>. Ava naddhātyatra – ‘‘o avasse’’ti kvaci avassa o. Onaddhā.</p>\r\n<p class="bodytext">Kvacīti kiṃ. Avasussatu.</p>\r\n<p class="centered">Byañjanasandhi.</p>','\r',char(13)),'\n',char(10)),'-25-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',42,replace(replace('<p class="title">Niggahītasandhi</p>\r\n<p class="bodytext"><a name="para27"></a><span class="paranum">27</span>. Niggahītantyadhikāro<a name="M0.0042"></a>. Kiṃ kato, saṃ jāto, saṃ ṭhito, taṃ dhanaṃ, taṃ mittantiha –</p>\r\n<p class="bodytext"><span class="bld">Vaggantaṃ vā vagge</span>.</p>\r\n<p class="bodytext">Vaggabyañjane pare bindussa tabbagganto vā hoti. Kiṅkato, sañjāto, saṇṭhito, tandhanaṃ, tammittaṃ.</p>\r\n<p class="bodytext">Vāti kiṃ. Na taṃ kammaṃ.</p>\r\n<p class="bodytext">Vākāreneva le lo ca. Pulliṅgaṃ.</p>','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',43,replace(replace('<p class="bodytext"><a name="para28"></a><span class="paranum">28</span>. Vātyadhikāro<a name="M0.0043"></a>. Evaṃ assa, etaṃ avocetīha –</p>\r\n<p class="bodytext"><span class="bld">Madā sare</span>.</p>\r\n<p class="bodytext">Sare pare binduno ma dā vā honti. Evamassa, etadavoca.</p>\r\n<p class="bodytext">Vāti kiṃ. Maṃ ajini.</p>\r\n<p class="bodytext"><a name="para29"></a><span class="paranum">29</span>. Taṃ eva, taṃ hītīha –</p>\r\n<p class="bodytext"><span class="bld">Eheñaṃ</span>.</p>\r\n<p class="bodytext">Ekāre, he ca pare binduno ño vā hoti. Dvitte – taññeva, tameva. Tañhi, taṃ hi.</p>\r\n<p class="bodytext"><a name="para30"></a><span class="paranum">30</span>. Saṃyogotīha –</p>\r\n<p class="bodytext"><span class="bld">Saye ca</span>.</p>\r\n<p class="bodytext">Yakāre pare tena saha binduno ño vā hoti. Dvitte – saññogo, saṃyogo.</p>\r\n<p class="bodytext"><a name="para31"></a><span class="paranum">31</span>. Cakkhu aniccaṃ, ava sirotīha - āgamo, kvacitveva.</p>\r\n<p class="bodytext"><span class="bld">Niggahītañca</span>.</p>\r\n<p class="bodytext">Sare, byañjane vā pare kvaci bindvāgamo hoti. Cakkhuṃaniccaṃ, avaṃsiro.</p>','\r',char(13)),'\n',char(10)),'-28-29-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',44,replace(replace('<p class="bodytext"><a name="para32"></a><span class="paranum">32</span>. Vidūnaṃ <a name="M0.0044"></a> aggaṃ, tāsaṃ ahaṃtīha –</p>\r\n<p class="bodytext">‘‘Kvaci lopaṃ’’ti sare bindulopo, vidūnaggaṃ. Dīghetāsāhaṃ.</p>\r\n<p class="bodytext"><a name="para33"></a><span class="paranum">33</span>. Buddhānaṃ sāsanaṃ, saṃ rāgotīha –</p>\r\n<p class="bodytext">‘‘Byañjane ce’’ti bindulopo, buddhānasāsanaṃ. Dīghesārāgo.</p>\r\n<p class="bodytext"><a name="para34"></a><span class="paranum">34</span>. Bījaṃ ivetīha –</p>\r\n<p class="bodytext"><span class="bld">Paro vā saro</span>.</p>\r\n<p class="bodytext">Binduto paro saro vā lupyate, bījaṃva.</p>\r\n<p class="bodytext"><a name="para35"></a><span class="paranum">35</span>. Evaṃ assetīha –</p>\r\n<p class="bodytext"><span class="bld">Byañjano ca visaññogo</span>.</p>\r\n<p class="bodytext">Binduto pare sare lutte saṃyogo byañjano vinaṭṭhasaṃyogo hotīti pubbasalopo. Evaṃsa.</p>\r\n<p class="centered">Niggahītasandhi.</p>\r\n<p class="title">Vomissaka sandhī</p>\r\n<p class="bodytext"><a name="para36"></a><span class="paranum">36</span>. <span class="bld">Anupadiṭṭhānaṃ vuttayogato.</span></p>','\r',char(13)),'\n',char(10)),'-32-33-34-35-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',45,replace(replace('<p class="bodytext">Idhāniddiṭṭhā <a name="M0.0045"></a> sandhayo vuttānusārena ñe yyā, yathā – yadi evaṃ, bodhi aṅgātīha – yādese iminā suttena dayakārasaṃyogassa jo, dhayakārasaṃyogassa jho, dvitte – yajjevaṃ, bojjhaṅgā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',46,replace(replace('<p class="bodytext"><a name="para37"></a><span class="paranum">37</span>. Asadisasaṃyoge <a name="M0.0046"></a> ekasarūpatā ca.</p>\r\n<p class="bodytext">Pari esanātīha – yādese rakārassa yo, payyesa nā.</p>','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',47,replace(replace('<p class="bodytext"><a name="para38"></a><span class="paranum">38</span>. Vaṇṇānaṃ <a name="M0.0047"></a> bahuttaṃ, viparītatā ca.</p>\r\n<p class="bodytext">Sarati, iti eva, sā itthī, busaṃ eva, bahu ābādho, adhi abhavi, sukhaṃ, dukkhaṃ, jīvotīha –</p>\r\n<p class="bodytext">Māgamo sakāre akārassa u ca, sumarati.</p>\r\n<p class="bodytext">Issa vo, itveva.</p>\r\n<p class="bodytext">Paralope ākārassa o, sotthī.</p>','\r',char(13)),'\n',char(10)),'-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',48,replace(replace('<p class="bodytext">Mādese<a name="M0.0048"></a>, pubbadīghe ca ekārassa i. Busāmiva.</p>\r\n<p class="bodytext">Vādese havakāravipariyayo. Bahvābādho.</p>\r\n<p class="bodytext">Adhissa kvaci addho, dīghe-addhābhavi.</p>\r\n<p class="bodytext">Binduno, okārassa ca e. Sukhe, dukkhe, jīve.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',50,replace(replace('<p class="bodytext"><a name="para39"></a><span class="paranum">39</span>. Radānaṃ <a name="M0.0050"></a> ḷo, paṭibodho, pariḷāho.</p>\r\n<p class="bodytext"><a name="para40"></a><span class="paranum">40</span>. Sare, byañjane vā pare binduno kvaci mo. Mama abhāsi, buddhama saraṇaṃ, pubbe mo paraṃ na netabbo ayuttattā.</p>\r\n<p class="bodytext"><a name="para41"></a><span class="paranum">41</span>. Binduto parasarāna maññassaratāpi.</p>\r\n<p class="bodytext">Taṃ iminā, evaṃ imaṃ, kiṃ ahaṃ tīha-issa a. Tadaminā.</p>\r\n<p class="bodytext">Issa u, akārassa ca e, bindulopādo. Evumaṃ, kehaṃ.</p>\r\n<p class="bodytext"><a name="para42"></a><span class="paranum">42</span>. Vākyasukhuccāraṇatthaṃ, chandahānitthañca vaṇṇalopopi.</p>\r\n<p class="bodytext">Paṭisaṅkhāya yonisotīha – pubbayalopo, paṭisaṅkhāyoniso.</p>','\r',char(13)),'\n',char(10)),'-39-40-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',51,replace(replace('<p class="bodytext"><a name="para43"></a><span class="paranum">43</span>. Alābūnityādo <a name="M0.0051"></a> akāralopo. Lābūni sīdanti, silā plavanti.</p>\r\n<p class="bodytext"><a name="para44"></a><span class="paranum">44</span>. Vutyabhedāya vikāropi.</p>\r\n<p class="bodytext">Akaramhase tetyādo sakāre garuno ekārassa iminā lahuakāro, akaramhasa te kiccaṃ.</p>','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',52,replace(replace('<p class="bodytext"><a name="para45"></a><span class="paranum">45</span>. Akkharaniyamo <a name="M0.0052"></a> chandaṃ, garulahuniyamo bhave vutti,</p>\r\n<p class="bodytext">Dīgho, saṃyogādipubbo rasso ca garu, lahu tu rasso. Yathā- ā, assa, aṃ, a.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-45-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',53,replace(replace('<p class="gatha1">Evamaññāpi <a name="M0.0053"></a> viññeyyā, saṃhitā tantiyā hitā;</p>\r\n<p class="gathalast">Saṃhitāti ca vaṇṇānaṃ, sannidhabyavadhānato.</p>\r\n<p class="centered">Vomissakasandhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',55,replace(replace('<p class="chapter">2. Nāmakaṇḍa</p>\r\n<p class="title">Pulliṅga</p>\r\n<p class="bodytext"><a name="para47"></a><span class="paranum">47</span>. ‘‘Jinavacanayuttaṃ <a name="M0.0055"></a> hī’’ti sabbatthādhikāro.</p>\r\n<p class="bodytext"><span class="bld">Liṅgañca nipaccate</span>.</p>\r\n<p class="bodytext">Dhātuppaccayavibhattivajjitamatthayuttaṃ saddarūpaṃ liṅgaṃ nāma, jinavacanayoggaṃ liṅgaṃ idha ṭhapīyati nipphādīyati ca.</p>','\r',char(13)),'\n',char(10)),'-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',56,replace(replace('<p class="bodytext"><a name="para48"></a><span class="paranum">48</span>. Buddhaiti <a name="M0.0056"></a> ṭhite –</p>\r\n<p class="bodytext"><span class="bld">Tato ca vibhattiyo</span>.</p>\r\n<p class="bodytext">Tasmā liṅgā parā vibhattiyo honti. Cakārena tāsaṃ ekavacanādipaṭhamādisaññā ca.</p>\r\n<p class="bodytext">‘‘Si yo aṃ yo nā hi sa naṃ smā hi sa naṃ smiṃ sū’’ ti vibhattiyo. Si yo iti paṭhamā, aṃ yo iti dutiyā, nā hi iti tatiyā, sa naṃ iti catutthī, smā hi iti pañcamī, sa naṃ iti chaṭṭhī, smiṃ su iti sattamī.</p>\r\n<p class="bodytext"><span class="bld">Liṅgatthe paṭhamā</span>.</p>','\r',char(13)),'\n',char(10)),'-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',59,replace(replace('<p class="bodytext">Yo <a name="M0.0059"></a> kammakattādivattantaramappatto sassarūpaṭṭho suddho, so <span class="bld">liṅgattho</span> nāma, tassābhidhānamatte paṭhamāvibhatti hoti. Tassāpaniyame ekamhi vattabbe ekavacanaṃ si, vuccate nenetivacanaṃ, ekassatthassa vacanaṃ ekavacanaṃ. Evaṃ bahuvacanaṃ.</p>\r\n<p class="bodytext">Atotveva.</p>\r\n<p class="bodytext"><span class="bld">So</span>.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',60,replace(replace('<p class="bodytext">Akārantā <a name="M0.0060"></a> parassa sissa o hoti.</p>\r\n<p class="bodytext"><span class="bld">Saralopo mādesappaccayādimhi saralope tu pakati</span>.</p>\r\n<p class="bodytext">Aṃādīsu paresu sarassa lopo hoti, tasmiṃ kate tu kvacādinā asavaṇṇe patte pakati hoti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',61,replace(replace('<p class="bodytext">Naye <a name="M0.0061"></a> paraṃ yutte. Evamupari saralopādi. Buddho.</p>\r\n<p class="bodytext">Bahumhi vattabbe bahuvacanaṃ yo.</p>\r\n<p class="bodytext">Ato vātveva.</p>\r\n<p class="bodytext"><span class="bld">Sabbayonīnamāe</span>.</p>\r\n<p class="bodytext">Akārantā paresaṃ paṭhamadutiyāyonīnaṃ yathāsaṅkhyaṃ āe vā honti. Buddhā.</p>\r\n<p class="bodytext">Vāti kiṃ. Aggayo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',62,replace(replace('<p class="bodytext"><a name="para49"></a><span class="paranum">49</span>. Liṅgatthe <a name="M0.0062"></a> paṭhamātveva.</p>\r\n<p class="bodytext"><span class="bld">Ālapane ca</span>.</p>\r\n<p class="bodytext">Abhimukhīkaraṇamālapanaṃ, tadadhike liṅgatthe paṭhamā hoti.</p>\r\n<p class="bodytext">‘‘Ālapane si gasañño’’ti sissa gasaññā. Geitveva.</p>\r\n<p class="bodytext"><span class="bld">Akārā pitādyantānamā</span>.</p>\r\n<p class="bodytext">Ge pare akāro pitusatthuattarājādīnamanto ca āttaṃ yāti.</p>\r\n<p class="bodytext">‘‘Ākāro vā’’ti ge pare ākārassa rasso vā.</p>\r\n<p class="bodytext"><span class="bld">Sesato lopaṃ gasīpi</span>.</p>\r\n<p class="bodytext">So siṃ syā ca sakhāto gassevātyādiniddiṭṭhehaññe avaṇṇivaṇṇuvaṇṇokārantā sesā, tehi pare gasī lupyante. He - buddha, buddhā. Yo - buddhā.</p>\r\n<p class="bodytext"><a name="para50"></a><span class="paranum">50</span>. <span class="bld">Kammatthe dutiyā.</span></p>','\r',char(13)),'\n',char(10)),'-49-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',63,replace(replace('<p class="bodytext">Yaṃ <a name="M0.0063"></a> karoti, taṃ kammaṃ nāma. Tattha dutiyā hoti. Aṃbuddhaṃ. Yossa e-buddhe.</p>\r\n<p class="bodytext"><a name="para51"></a><span class="paranum">51</span>. Tatiyātveva.</p>\r\n<p class="bodytext"><span class="bld">Kattari ca</span>.</p>\r\n<p class="bodytext">Yo karoti, sa kattā nāma. Tattha tatiyā hoti. Nā.</p>\r\n<p class="bodytext"><span class="bld">Ato nena</span>.</p>\r\n<p class="bodytext">Akārā paro nā enaṃ yāti. Buddhena.</p>\r\n<p class="bodytext">Hi.</p>\r\n<p class="bodytext"><span class="bld">Suhisvakāro e</span>.</p>','\r',char(13)),'\n',char(10)),'-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',64,replace(replace('<p class="bodytext">Suhisu <a name="M0.0064"></a> paresvakārassa e hoti.</p>\r\n<p class="bodytext"><span class="bld">Smāhisminnaṃ mhābhimhi vā</span>.</p>\r\n<p class="bodytext">Sabbasaddehi paresaṃ smāhisminnaṃ yathāsaṅkhyaṃ mhābhimhiiccete vā honti. Buddhebhi, buddhehi.</p>\r\n<p class="bodytext"><a name="para52"></a><span class="paranum">52</span>. <span class="bld">Karaṇe tatiyā.</span></p>','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',65,replace(replace('<p class="bodytext">Yena <a name="M0.0065"></a> vā kayirate, taṃ karaṇaṃ nāma. Tattha tatiyā hoti. Sabbaṃ kattusamaṃ.</p>\r\n<p class="bodytext"><a name="para53"></a><span class="paranum">53</span>. <span class="bld">Sampadāne catutthī.</span></p>\r\n<p class="bodytext">Yassa dātukāmo rocate, dhārayate vā, taṃ sampadānaṃ nāma. Tattha catutthī hoti. Sa.</p>\r\n<p class="bodytext">Ato vātveva.</p>\r\n<p class="bodytext"><span class="bld">Āya catutthekavacanassa tu</span>.</p>\r\n<p class="bodytext">Akārā parassa catutthekavacanassa āyo vā hoti. Buddhāya.</p>','\r',char(13)),'\n',char(10)),'-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',66,replace(replace('<p class="bodytext">‘‘Sāgamo <a name="M0.0066"></a> se’’ti se sakārāgamo. Buddhassa. Naṃ.</p>\r\n<p class="bodytext">Dīghantveva.</p>\r\n<p class="bodytext"><span class="bld">Sunaṃhisu ca</span>.</p>\r\n<p class="bodytext">Sunaṃhisu paresu sarādīnaṃ dīgho hoti. Casaddena kvaci na. Buddhānaṃ.</p>\r\n<p class="bodytext"><a name="para54"></a><span class="paranum">54</span>. <span class="bld">Apādāne pañcamī.</span></p>\r\n<p class="bodytext">Yasmādapeti, bhayamādatte vā, tadapādānaṃ nāma, tattha pañcamī hoti. Smā.</p>\r\n<p class="bodytext">Ato āetveva.</p>\r\n<p class="bodytext"><a name="para55"></a><span class="paranum">55</span>. <span class="bld">Smāsminnaṃ vā.</span></p>\r\n<p class="bodytext">Akārā paresaṃ smāsminnaṃ āe vā honti. Buddhā, buddhamhā, buddhasmā. Buddhebhi, buddhehi.</p>\r\n<p class="bodytext"><a name="para56"></a><span class="paranum">56</span>. <span class="bld">Sāmismiṃ chaṭṭhī.</span></p>','\r',char(13)),'\n',char(10)),'-54-55-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',67,replace(replace('<p class="bodytext">Yassa <a name="M0.0067"></a> vā pariggaho, taṃ sāmī nāma. Tattha chaṭṭhī hoti. Buddhassa. Buddhānaṃ.</p>\r\n<p class="bodytext"><a name="para57"></a><span class="paranum">57</span>. <span class="bld">Okāse sattamī.</span></p>\r\n<p class="bodytext">Yodhāro, tamokāsaṃ nāma. Tattha sattamī hoti. Smiṃ-buddhe, buddhamhi, buddhasmiṃ. Su-buddhesu.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Buddho buddha sukhaṃ dadāti sarato buddhaṃ tato dukkaraṃ,</p>\r\n<p class="gatha2">Kiṃ buddhena mahiddhayopi munayo buddhena jātāsukhī;</p>\r\n<p class="gatha3">Buddhasseva manaṃ dade padamahaṃ buddhā labheyyāccutaṃ,</p>\r\n<p class="gathalast">Buddhassiddhi na kiṃ kare bhavabhave bhattyatthu buddhe mama.</p>','\r',char(13)),'\n',char(10)),'-57-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',68,replace(replace('<p class="bodytext"><a name="para59"></a><span class="paranum">59</span>. Ito <a name="M0.0068"></a> paraṃ tatiyāpañcamīnañca catutthīchaṭṭhīnañca sarūpattā pañcamīchaṭṭhiyo bhīyo upekkhante.</p>\r\n<p class="bodytext"><a name="para60"></a><span class="paranum">60</span>. Atta, si.</p>\r\n<p class="bodytext">Brahmattasakharājādito tveva.</p>\r\n<p class="bodytext"><span class="bld">Syā ca</span>.</p>\r\n<p class="bodytext">Brahmādito sissa ā hoti. Attā.</p>\r\n<p class="bodytext">‘‘Yonamāno’’ti brahmādito yonaṃ ānottaṃ. Attāno.</p>\r\n<p class="bodytext"><a name="para61"></a><span class="paranum">61</span>. He - atta, attā. Yo, attāno.</p>','\r',char(13)),'\n',char(10)),'-59-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',69,replace(replace('<p class="bodytext"><a name="para62"></a><span class="paranum">62</span>. ‘‘Brahmattasakharājādito <a name="M0.0069"></a> amāna’’nti brahmādito aṃvacanassa ānaṃ vā hoti. Attānaṃ, attaṃ. Attāno.</p>\r\n<p class="bodytext"><a name="para63"></a><span class="paranum">63</span>. Attena, attanā. Pakkhe-jinavacanānurodhena enābhāvo.</p>','\r',char(13)),'\n',char(10)),'-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',70,replace(replace('<p class="bodytext">‘‘Attānto <a name="M0.0070"></a> hismimanattaṃ’’ti himhi attantassa ano. Attanehi. Evaṃ karaṇe.</p>\r\n<p class="bodytext"><a name="para64"></a><span class="paranum">64</span>. ‘‘Sassa no’’ti nokāro. Attano. Attānaṃ.</p>\r\n<p class="bodytext"><a name="para65"></a><span class="paranum">65</span>. Amhatumhantu rājabrahmattasakha satthupitādīhi smā nāva.</p>\r\n<p class="bodytext">Amhādito smā nā iva hoti. Attanā.</p>\r\n<p class="bodytext"><a name="para66"></a><span class="paranum">66</span>. ‘‘Tato sminnī’’ti smino ni. Attani. ‘‘Anatta’’nti bhāvaniddesena sumhi ca ano. Attanesu.</p>','\r',char(13)),'\n',char(10)),'-64-65-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',71,replace(replace('<p class="bodytext"><a name="para67"></a><span class="paranum">67</span>. Rājā <a name="M0.0071"></a> attāva. Nā.</p>\r\n<p class="bodytext">Savibhattissa rājassetveva.</p>\r\n<p class="bodytext"><span class="bld">Nāmhi raññā vā</span>.</p>\r\n<p class="bodytext">Nāmhi savibhattissa rājasaddassa raññā vā hoti. Raññā, rājena.</p>\r\n<p class="bodytext"><span class="bld">Rājassa rāju sunaṃhisu ca</span>.</p>\r\n<p class="bodytext">Sunaṃ hisu paresu rājassa rāju hoti, cakārena kvaci na.</p>','\r',char(13)),'\n',char(10)),'-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',72,replace(replace('<p class="bodytext">‘‘Sunaṃhisu <a name="M0.0072"></a> ceti’’ dīghe - rājūbhi, rājūhi, rājebhi, rājehi.</p>\r\n<p class="bodytext"><a name="para68"></a><span class="paranum">68</span>. Savibhattissetyadhikāro.</p>\r\n<p class="bodytext">‘‘Rājassa rañño rājino se’’ti se rañño rājino honti. Rañño, rājino.</p>\r\n<p class="bodytext">‘‘Raññaṃ namhi vā’’ti namhi raññaṃ vā. Raññaṃ, rājūnaṃ, rājānaṃ.</p>','\r',char(13)),'\n',char(10)),'-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',73,replace(replace('<p class="bodytext"><a name="para69"></a><span class="paranum">69</span>. Smāssanātulyattā-nāmhi <a name="M0.0073"></a> raññā vā. Raññā, rājamhā, rājasmā.</p>\r\n<p class="bodytext"><a name="para70"></a><span class="paranum">70</span>. ‘‘Smimhi raññe rājinī’’ti smimhi raññe rājini honti. Raññe, rājini. Rājūsu, rājesu.</p>\r\n<p class="bodytext"><a name="para71"></a><span class="paranum">71</span>. Guṇavantu, si.</p>\r\n<p class="bodytext">Savibhattissa ntussetveva.</p>\r\n<p class="bodytext">‘‘Ā simhī’’ti simhi savibhattissa ntussa ā. Guṇavā.</p>\r\n<p class="bodytext">Yomhi paṭhametveva.</p>\r\n<p class="bodytext"><span class="bld">Ntussa nto</span>.</p>\r\n<p class="bodytext">Paṭhame yomhi savibhattissa ntussa ntokāro hoti. Guṇavanto.</p>\r\n<p class="bodytext">Sunaṃhisu attaṃtveva.</p>\r\n<p class="bodytext"><span class="bld">Ntussanto yosu ca</span>.</p>\r\n<p class="bodytext">Sunaṃhisu, yosu, cakārena aññesupi paresu ntussanto attaṃ yāti. Guṇavantā.</p>','\r',char(13)),'\n',char(10)),'-69-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',74,replace(replace('<p class="bodytext"><a name="para72"></a><span class="paranum">72</span>. Savibhattissetyadhikāro<a name="M0.0074"></a>. Aṃitveva.</p>\r\n<p class="bodytext"><span class="bld">Avaṇṇo ca ge</span>.</p>\r\n<p class="bodytext">Ge pare savibhattissa ntussa aṃaā honti. Heguṇavaṃ,guṇava, guṇavā. Yo - guṇavanto, guṇavantā.</p>\r\n<p class="bodytext"><a name="para73"></a><span class="paranum">73</span>. Attaṃ – guṇavantaṃ. Guṇavante.</p>','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',75,replace(replace('<p class="bodytext"><a name="para74"></a><span class="paranum">74</span>. ‘‘Totitā <a name="M0.0075"></a> sasmiṃ nāsvī’’ti savibhattissa ntussa nāmhi tā, se tokāro, smimhi ti ca vā. Guṇavatā, guṇavantena. Guṇavantebhi, guṇavantehi.</p>\r\n<p class="bodytext"><a name="para75"></a><span class="paranum">75</span>. Guṇavato, guṇavantassa.</p>\r\n<p class="bodytext">‘‘Namhi taṃ vā’’ti namhi ntussa taṃ vā. Guṇavataṃ, guṇavantānaṃ. Smā nāva.</p>\r\n<p class="bodytext"><a name="para76"></a><span class="paranum">76</span>. Guṇavati, guṇavante, guṇavantamhi, guṇavantasmiṃ, guṇavantesu.</p>\r\n<p class="bodytext"><a name="para77"></a><span class="paranum">77</span>. Gacchanta, si.</p>\r\n<p class="bodytext">‘‘Simhi gacchantādīnaṃ ntasaddo a’’nti ntasaddassa aṃvā, silopo. Gacchaṃ, sissa o – gacchanto.</p>\r\n<p class="bodytext">Gacchantādīnaṃ ntasaddotveva.</p>\r\n<p class="bodytext"><span class="bld">Sesesuntuva</span>.</p>\r\n<p class="bodytext">Vuttaṃ hitvā sesesu gacchantādīnaṃ ntasaddo ntu iva daṭṭhabbo. Gacchanto, gacchantāiccādi.</p>\r\n<p class="bodytext">Sesaṃ guṇavantusamaṃ.</p>','\r',char(13)),'\n',char(10)),'-74-75-76-77-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',77,replace(replace('<p class="bodytext"><a name="para78"></a><span class="paranum">78</span>. Gacchantādayo <a name="M0.0077"></a> nāma antappaccayantā.</p>\r\n<p class="bodytext"><a name="para79"></a><span class="paranum">79</span>. Aggi, silopo.</p>\r\n<p class="bodytext">‘‘Ivaṇṇuvaṇṇā jhalā’’ti ivaṇṇuvaṇṇānaṃ yathāsaṅkhyaṃ jhalasaññā.</p>\r\n<p class="bodytext">Jhalato vātveva.</p>\r\n<p class="bodytext"><span class="bld">Ghapato ca yonaṃ lopo</span>.</p>\r\n<p class="bodytext">Ghapajhalato yonaṃ lopo vā hoti.</p>\r\n<p class="bodytext"><span class="bld">Yosu katanikāralopesu dīghaṃ</span>.</p>\r\n<p class="bodytext">Kato nikāro lopo ca yesaṃ tesu yosu sarānaṃ dīgho hoti. Aggī. Pakkhe-attantveva.</p>\r\n<p class="bodytext"><span class="bld">Yo svakatarasso jho</span>.</p>\r\n<p class="bodytext">Yosu akatarasso jho attaṃ yāti. Aggayo. Tathālapane.</p>\r\n<p class="bodytext"><a name="para80"></a><span class="paranum">80</span>. <span class="bld">Aṃ mo niggahītaṃ jhalapehi.</span></p>\r\n<p class="bodytext">Jhalapato aṃ mo ca binduṃ yanti. Aggiṃ. Aggī, aggayo.</p>\r\n<p class="bodytext"><a name="para81"></a><span class="paranum">81</span>. Agginā. Dīghe-aggībhi, aggīhi.</p>\r\n<p class="bodytext"><a name="para82"></a><span class="paranum">82</span>. ‘‘Jhalato sassa no vā’’ti sassa nottaṃ vā. Aggino, aggissa. Aggīnaṃ.</p>\r\n<p class="bodytext"><a name="para83"></a><span class="paranum">83</span>. ‘‘Jhalato ce’’ti smāssa nā. Agginā.</p>\r\n<p class="bodytext"><a name="para84"></a><span class="paranum">84</span>. Aggimhi, aggismiṃ. Aggīsu.</p>\r\n<p class="bodytext"><a name="para85"></a><span class="paranum">85</span>. Ādi aggīva. Smiṃno pana ‘‘ādito o ce’’ti aṃ, o ca vā. Ādi, ādo, ādimhi, ādismiṃ. Ādīsu.</p>','\r',char(13)),'\n',char(10)),'-78-79-80-81-82-83-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',79,replace(replace('<p class="bodytext"><a name="para86"></a><span class="paranum">86</span>. Daṇḍī<a name="M0.0079"></a>, si.</p>\r\n<p class="bodytext">‘‘Agho rassa’’mādinā rasse sampatte ‘‘na sismimanapuṃsakānī’’ti simhi anapuṃsakānaṃ na rasso. Silopo, daṇḍī, yolope – daṇḍī. Pakkhe –</p>\r\n<p class="bodytext"><span class="bld">Agho rassamekavacanayosvapi ca</span>.</p>\r\n<p class="bodytext">Ekavacanayosu jhalapā rassaṃ yanti.</p>\r\n<p class="bodytext">Jhato katarassātveva.</p>\r\n<p class="bodytext"><span class="bld">Yonaṃ no</span>.</p>\r\n<p class="bodytext">Katassā jhato yonaṃ nottaṃ hoti. Daṇḍino.</p>\r\n<p class="bodytext"><a name="para87"></a><span class="paranum">87</span>. ‘‘Jhalapā rassa’’nti ge pare jhalapānaṃ rasso. Hedaṇḍi. Daṇḍī, daṇḍino.</p>','\r',char(13)),'\n',char(10)),'-86-87-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',80,replace(replace('<p class="bodytext"><a name="para88"></a><span class="paranum">88</span>. Vā <a name="M0.0080"></a> aṃitveva.</p>\r\n<p class="bodytext">‘‘Naṃ jhato katarassā’’ti aṃiccassa naṃ vā. Daṇḍinaṃ, daṇḍiṃ. Daṇḍī, daṇḍino.</p>\r\n<p class="bodytext"><a name="para89"></a><span class="paranum">89</span>. Daṇḍinā. Daṇḍībhi, daṇḍīhi.</p>\r\n<p class="bodytext"><a name="para90"></a><span class="paranum">90</span>. Daṇḍino, daṇḍissa. Daṇḍīnaṃ.</p>\r\n<p class="bodytext"><a name="para91"></a><span class="paranum">91</span>. Jhato katarassātveva.</p>\r\n<p class="bodytext">‘‘Sminnī’’ti smino ni. Daṇḍini. Daṇḍīsu.</p>\r\n<p class="bodytext"><a name="para92"></a><span class="paranum">92</span>. Bhikkhu, silopo.</p>\r\n<p class="bodytext">Vā yonaṃtveva.</p>\r\n<p class="bodytext">‘‘Lato vo kāro ce’’ti lato yonaṃ vottaṃ vā.</p>\r\n<p class="bodytext">Attaṃ akatarassotveva.</p>\r\n<p class="bodytext"><span class="bld">Vevosu lo ca</span>.</p>\r\n<p class="bodytext">Vevosu akatarasso lo attaṃ yāti. Bhikkhavo, pakkhe – yolopa dīghā. Bhikkhū.</p>\r\n<p class="bodytext"><a name="para93"></a><span class="paranum">93</span>. He-bhikkhu.</p>\r\n<p class="bodytext">‘‘Akatarassā lato yvālapanassa vevo’’ti ālapane yossa vevokārā, attaṃ. Bhikkhave, bhikkhavo, bhikkhū.</p>\r\n<p class="bodytext"><a name="para94"></a><span class="paranum">94</span>. Bhikkhuṃ. Bhikkhavo, bhikkhū. Sesaṃ aggīva.</p>\r\n<p class="bodytext"><a name="para95"></a><span class="paranum">95</span>. Evaṃ jantu. Jantū, jantavo.</p>','\r',char(13)),'\n',char(10)),'-88-89-90-91-92-93-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',81,replace(replace('<p class="bodytext">‘‘Lato <a name="M0.0081"></a> vokāro ce’’tīha kāraggahaṇena yonaṃ nottaṃ, cakārena kvaci vononamabhāvova viseso. Jantuno, jantuyo.</p>\r\n<p class="bodytext"><a name="para96"></a><span class="paranum">96</span>. Satthu, si.</p>\r\n<p class="bodytext">‘‘Satthupitādīnamā sismiṃ silopo ce’’ti satthādyantassa ā, silopo ca. Satthā.</p>\r\n<p class="bodytext">Satthupitādīnantyadhikāro.</p>\r\n<p class="bodytext"><span class="bld">Aññesvārattaṃ</span>.</p>\r\n<p class="bodytext">Sitoññesu satthādyantassa āro hoti.</p>\r\n<p class="bodytext"><span class="bld">Tato yonamo tu</span>.</p>\r\n<p class="bodytext">Tato ārato yonaṃ o hoti. Satthāro.</p>\r\n<p class="bodytext"><a name="para97"></a><span class="paranum">97</span>. He-sattha, satthā. Satthāro.</p>\r\n<p class="bodytext"><a name="para98"></a><span class="paranum">98</span>. Satthāraṃ. Satthāre, satthāro.</p>\r\n<p class="bodytext"><a name="para99"></a><span class="paranum">99</span>. ‘‘Nā ā’’ti ārato nāssa ā. Satthārā. Satthārebhi, satthārehi.</p>\r\n<p class="bodytext"><a name="para100"></a><span class="paranum">100</span>. <span class="bld">U sasmiṃ salopo ca.</span></p>','\r',char(13)),'\n',char(10)),'-96-97-98-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',82,replace(replace('<p class="bodytext">Se <a name="M0.0082"></a> satthādyantassa u hoti salopo ca vā. Satthu, satthuno, satthussa.</p>\r\n<p class="bodytext">‘‘Vā namhī’’ti namhi āro vā. Satthārānaṃ.</p>\r\n<p class="bodytext">‘‘Satthunāttañce’’ti namhi satthādyantassa attaṃ vā. Dīghesatthānaṃ.</p>\r\n<p class="bodytext"><a name="para101"></a><span class="paranum">101</span>. ‘‘Tato smimī’’ti ārato smino i. ‘‘Āro rassamīkāre’’ti imhi ārassa rasso. Satthari. Satthāresu. Evaṃ nattādi.</p>','\r',char(13)),'\n',char(10)),'-101-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',83,replace(replace('<p class="bodytext"><a name="para102"></a><span class="paranum">102</span>. Pitā <a name="M0.0083"></a> sattheva. ‘‘Pitādīnamasimhī’’ti sitoññesu ārassa rassova viseso. Pitaro.</p>\r\n<p class="bodytext">Namhi – pitūnantipi hoti. Evaṃ bhātuppabhutayo.</p>\r\n<p class="bodytext"><a name="para103"></a><span class="paranum">103</span>. Abhibhū. Rasse-abhibhuvo. Yolopeabhibhū. Sesaṃ bhikkhūva, rassova viseso.</p>','\r',char(13)),'\n',char(10)),'-102-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',84,replace(replace('<p class="bodytext"><a name="para104"></a><span class="paranum">104</span>. Evaṃ <a name="M0.0084"></a> sabbaññū. Pubbeva yonaṃ nokāro ca. Sabbaññuno, sabbaññū.</p>\r\n<p class="bodytext"><a name="para105"></a><span class="paranum">105</span>. Go.</p>\r\n<p class="bodytext">Gāvaitveva.</p>\r\n<p class="bodytext">‘‘Yosu ce’’ti gosaddokārassa āvo, ‘‘tato yonamo tu’’tīha tusaddena yonaṃ o. Gāvo. Tathālapane.</p>\r\n<p class="bodytext"><a name="para106"></a><span class="paranum">106</span>. <span class="bld">Avamhi ca.</span></p>\r\n<p class="bodytext">Amhi pare gosaddokārassa āvaavā honti, casaddena hinaṃvajjitesu sesesupi.</p>\r\n<p class="bodytext"><a name="para107"></a><span class="paranum">107</span>. ‘‘Āvassu vā’’ti amhi āvantassa uttaṃ vā, gāhvaṃ, gāvaṃ, gavaṃ. Yo-gāvo. Gāvena, gavena. Gobhi, gohi.</p>\r\n<p class="bodytext"><a name="para108"></a><span class="paranum">108</span>. ‘‘Gāva se’’ti se ossa āvo. Gāvassa, gavassa. ‘‘Tato na’’mādo cakārena naṃiccassa aṃ, ossa avo ca. Gavaṃ.</p>','\r',char(13)),'\n',char(10)),'-104-105-106-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',85,replace(replace('<p class="bodytext"><a name="para109"></a><span class="paranum">109</span>. ‘‘Suhināsu <a name="M0.0085"></a> ce’’tīha cakārena gossa gu ca. Dvitte-gunnaṃ, gonaṃ. Gāvā, gavā, gāvamhā, gavamhā, gāvasmā, gavasmā. Gobhi, gohi.</p>\r\n<p class="bodytext"><a name="para110"></a><span class="paranum">110</span>. Gāve, gave, gāvamhi, gavamhi, gāvasmiṃ, gavasmiṃ. Gāvesu, gavesu, gosu.</p>\r\n<p class="centered">Pulliṅgā.</p>','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',86,replace(replace('<p class="title">Itthiliṅga</p>\r\n<p class="bodytext"><a name="para111"></a><span class="paranum">111</span>. Kaññā<a name="M0.0086"></a>. Silopo.</p>\r\n<p class="bodytext">‘‘Ā gho’’ti itthiyaṃ ākārassa ghasaññā. Yo lope – kaññā. Pakkhe – kaññāyo.</p>\r\n<p class="bodytext"><a name="para112"></a><span class="paranum">112</span>. ‘‘Ghate ce’’ti gassa e. He-kaññe. Kaññā, kaññāyo.</p>\r\n<p class="bodytext"><a name="para113"></a><span class="paranum">113</span>. Kaññaṃ. Kaññā, kaññāyo.</p>\r\n<p class="bodytext"><a name="para114"></a><span class="paranum">114</span>. Āya ekavacanassetveva.</p>\r\n<p class="bodytext">‘‘Ghato nādīna’’nti nādekavacanānamāyo. Kaññāya. Kaññābhi, kaññāhi.</p>\r\n<p class="bodytext"><a name="para115"></a><span class="paranum">115</span>. Kaññāya. Kaññānaṃ.</p>\r\n<p class="bodytext"><a name="para116"></a><span class="paranum">116</span>. <span class="bld">Ghapato smiṃ yaṃ vā.</span></p>\r\n<p class="bodytext">Ghapehi smino yaṃ vā hoti. Kaññāyaṃ, kaññāya. Kaññāsu.</p>','\r',char(13)),'\n',char(10)),'-111-112-113-114-115-116-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',87,replace(replace('<p class="bodytext"><a name="para117"></a><span class="paranum">117</span>. Ratti<a name="M0.0087"></a>, silopo.</p>\r\n<p class="bodytext">‘‘Te itthikhyā po’’ti itthiyamivaṇṇuvaṇṇānaṃ pasaññā. Yolopadīghā. Rattī. Pakkhe – rattiyo. Tathālapane.</p>\r\n<p class="bodytext"><a name="para118"></a><span class="paranum">118</span>. Rattiṃ, rattī. Rattiyo.</p>\r\n<p class="bodytext"><a name="para119"></a><span class="paranum">119</span>. Ekavacanassa nādīnantveva.</p>\r\n<p class="bodytext">‘‘Pato yā’’ti nādekavacanānaṃ yā. Rattiyā. Rattībhi, rattīhi.</p>\r\n<p class="bodytext"><a name="para120"></a><span class="paranum">120</span>. Rattiyā. Rattīnaṃ.</p>\r\n<p class="bodytext"><a name="para121"></a><span class="paranum">121</span>. Rattiyaṃ, rattiyā. Rattīsu.</p>','\r',char(13)),'\n',char(10)),'-117-118-119-120-121-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',88,replace(replace('<p class="bodytext"><a name="para122"></a><span class="paranum">122</span>. Nadī<a name="M0.0088"></a>. Sesaṃ rattīva. Aghattā rassova viseso.</p>\r\n<p class="bodytext"><a name="para123"></a><span class="paranum">123</span>. Yāgu rattīva.</p>\r\n<p class="bodytext"><a name="para124"></a><span class="paranum">124</span>. Mātu, dhītu, duhitvādayo piteva.</p>\r\n<p class="bodytext"><a name="para125"></a><span class="paranum">125</span>. Jambū nadīva.</p>\r\n<p class="bodytext">Kaññaiti ṭhite –</p>\r\n<p class="bodytext"><span class="bld">Itthiyamato āpaccayo</span>.</p>\r\n<p class="bodytext">Itthiyaṃ vattamānā akārantato āpaccayo hoti. Saralopapakalyādi. Kaññā.</p>\r\n<p class="bodytext">‘‘Dhātuppaccayavibhatti vajjitamatthavaṃ liṅga’’nti vacanato paccayantassāliṅgattā taddhitādisutte cakārena nāmamiva kate – syādi. Evaṃ īinīsu.</p>\r\n<p class="bodytext">Evaṃ ajā, eḷakā, kokilā, assā, mūsikā, balākā, mandā, jarāiccādi.</p>\r\n<p class="bodytext"><a name="para126"></a><span class="paranum">126</span>. Itthiyantyadhikāro.</p>\r\n<p class="bodytext"><span class="bld">Nadādito vā ī</span>.</p>','\r',char(13)),'\n',char(10)),'-122-123-124-125-126-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',89,replace(replace('<p class="bodytext">Itthiyaṃ <a name="M0.0089"></a> nadādito vā anadādito vā ī hoti. Nadī, nagarī, kumārī, brāhmaṇī, taruṇī, kukkuṭī, itthī iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',90,replace(replace('<p class="bodytext">‘‘Mātulādīnamānattamīkāre’’ti <a name="M0.0090"></a> īmhi mātulādyantassa āno. Mātulānītyādi.</p>\r\n<p class="bodytext">Anadādito vā ī. Sakhī, hatthī.</p>\r\n<p class="bodytext"><span class="bld">Bhavato bhoto</span>.</p>\r\n<p class="bodytext">Īmhi bhavantassa bhoto hoti. Bhotī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',91,replace(replace('<p class="bodytext"><a name="para127"></a><span class="paranum">127</span>. ‘‘Ṇa <a name="M0.0091"></a> va ṇikaṇeyya ṇantūhī’’ti ī. Mānavī, nāvikī, venateyyī, gotamī.</p>\r\n<p class="bodytext"><a name="para128"></a><span class="paranum">128</span>. ‘‘Ntussa tamīkāre’’ti ntussa to vā. Guṇavatī, guṇavantī. Dhitimatī, dhitimantī.</p>\r\n<p class="bodytext">Ntassa ntubyapadeso. Mahatī, mahantī.</p>\r\n<p class="bodytext"><a name="para129"></a><span class="paranum">129</span>. <span class="bld">Patibhikkhurājīkārantehi inī.</span></p>','\r',char(13)),'\n',char(10)),'-127-128-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',92,replace(replace('<p class="bodytext">Patyādīhi <a name="M0.0092"></a> īkārantehi ca itthiyaṃ inī hoti.</p>\r\n<p class="bodytext">‘‘Patissinīmhī’’ti patyantassa atte saralopādo tukārena lopābhāvo. ‘‘Vā paro asarūpā’’ti ilopo, dīgho ca. Gahapatānī, bhikkhunī, rājinī, medhāvinī, tapassinī, dhammacāriṇī, bhayadassāvinī, bhuttāvinītyādi.</p>\r\n<p class="centered">Itthiliṅgā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',93,replace(replace('<p class="title">Napuṃsakaliṅga</p>\r\n<p class="bodytext"><a name="para130"></a><span class="paranum">130</span>. Citta<a name="M0.0093"></a>, si.</p>\r\n<p class="bodytext">Napuṃsakehi ato niccanteva.</p>\r\n<p class="bodytext"><span class="bld">Siṃ</span>.</p>\r\n<p class="bodytext">Akāranteti napuṃsakehi sissa nicaṃ aṃ hoti. Cittaṃ.</p>\r\n<p class="bodytext">Yonaṃ ni napuṃsakehitveva.</p>\r\n<p class="bodytext"><span class="bld">Ato niccaṃ</span>.</p>\r\n<p class="bodytext">Akārantehi na puṃsakehi yonaṃ niccaṃ ni hoti, nissa ā. Cittā. Pakkhe – yosvādinā dīghe – cittāni.</p>\r\n<p class="bodytext"><a name="para131"></a><span class="paranum">131</span>. Galope – he – citta. Cittā, cittāni.</p>','\r',char(13)),'\n',char(10)),'-130-131-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',94,replace(replace('<p class="bodytext"><a name="para132"></a><span class="paranum">132</span>. Cittaṃ<a name="M0.0094"></a>. Nissa e – citte, cittāni. Sesaṃ buddhova.</p>\r\n<p class="bodytext"><a name="para133"></a><span class="paranum">133</span>. Mana, si, manaṃ.</p>\r\n<p class="bodytext">Nā vātveva.</p>\r\n<p class="bodytext"><a name="para134"></a><span class="paranum">134</span>. <span class="bld">Manogaṇādito sminnānamiā.</span></p>\r\n<p class="bodytext">Manādito sminnānaṃ iā vā honti.</p>\r\n<p class="bodytext"><span class="bld">Sa sare vāgamo</span>.</p>\r\n<p class="bodytext">Vibhatyādese sare pare manādito sāgamo vā hoti. Manasā.</p>\r\n<p class="bodytext"><a name="para135"></a><span class="paranum">135</span>. <span class="bld">Sassa co</span></p>\r\n<p class="bodytext">Manādito sassa o hoti, casaddena smāssa ā ca. Manaso, manasā.</p>','\r',char(13)),'\n',char(10)),'-132-133-134-135-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',95,replace(replace('<p class="bodytext"><a name="para136"></a><span class="paranum">136</span>. Manasi<a name="M0.0095"></a>. Sesaṃ cittaṃva.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> Manaṃ siraṃ uraṃ tejaṃ, rajaṃ ojaṃ vayaṃ payaṃ;</p>\r\n<p class="gathalast">Yasaṃ tapaṃ vacaṃ cetaṃ, evamādi manogaṇo.</p>','\r',char(13)),'\n',char(10)),'-136-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',96,replace(replace('<p class="bodytext"><a name="para138"></a><span class="paranum">138</span>. Guṇavantu<a name="M0.0096"></a>, si.</p>\r\n<p class="bodytext">‘‘Aṃ napuṃsake’’ti simhi savibhattissa ntussa aṃ. Guṇavaṃ. Ntussa tte – guṇavantāni.</p>\r\n<p class="bodytext"><a name="para139"></a><span class="paranum">139</span>. Yotoññaṃ pumeva. Evaṃ gacchaṃ.</p>\r\n<p class="bodytext"><a name="para140"></a><span class="paranum">140</span>. Aṭṭhi.</p>\r\n<p class="bodytext">Vātveva.</p>\r\n<p class="bodytext">‘‘Yonanni napuṃsakehī’’ti yonaṃ ni vā. Aṭṭīni. Jhattā yolope – aṭṭhī. Tathālapane.</p>\r\n<p class="bodytext"><a name="para141"></a><span class="paranum">141</span>. Aṭṭhiṃ, aṭṭhīni, aṭṭhī. Sesaṃ aggīva.</p>\r\n<p class="bodytext"><a name="para142"></a><span class="paranum">142</span>. Daṇḍī, si.</p>\r\n<p class="bodytext">Aghattā rasso, daṇḍi. Yotoññaṃ pumeva.</p>\r\n<p class="bodytext"><a name="para143"></a><span class="paranum">143</span>. Āyu aṭṭhīva.</p>\r\n<p class="centered">Napuṃsakaliṅgā.</p>','\r',char(13)),'\n',char(10)),'-138-139-140-141-142-143-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',98,replace(replace('<p class="title">Pumitthiliṅga</p>\r\n<p class="bodytext"><a name="para144"></a><span class="paranum">144</span>. Pumitthiliṅgā <a name="M0.0098"></a> – ghaṭa, kaṭa, yaṭṭhi, muṭṭhi, sindhu, reṇuppabhutayo dvipada catuppada jātivācino ca.</p>\r\n<p class="bodytext">Yathā – ghaṭo, īpaccaye-ghaṭī. Eso yaṭṭhi, esā yaṭṭhiccādi.</p>\r\n<p class="bodytext">Dvipadajātivācino yathā – khattiyo. Āpaccaye- khattiyā, samaṇo, īmhi-samaṇīiccādi.</p>\r\n<p class="bodytext">Catuppadajātivācino yathā – gajo, ā-gajā, byaggho, īmhi - byagghīiccādi.</p>','\r',char(13)),'\n',char(10)),'-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',100,replace(replace('<p class="title">Pumanapuṃsakaliṅga</p>\r\n<p class="bodytext"><a name="para145"></a><span class="paranum">145</span>. Pumanapuṃsakaliṅgā <a name="M0.0100"></a> – dhamma, kamma, brahma, kusuma saṅgama, paduma, assama, vihāra, sarīra, suvaṇṇa, vaṇṇa, kahāpaṇa, bhavana, bhuvana, yobbana, bhusana, āsana, sayana, odana, ākāsa, upavāsa, māsa, divasa, rasa, thala, phala, raṭṭha ambu, madhvādayo.</p>\r\n<p class="title">Itthinapuṃsakaliṅga</p>\r\n<p class="bodytext"><a name="para146"></a><span class="paranum">146</span>. Itthinapuṃsakaliṅgā – nagara, accippamukhā.</p>','\r',char(13)),'\n',char(10)),'-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',101,replace(replace('<p class="title">Sabbaliṅga</p>\r\n<p class="bodytext"><a name="para147"></a><span class="paranum">147</span>. Sabbaliṅgā <a name="M0.0101"></a> – taṭa puṭa patta maṇḍala kalasā dayo, nāmīkatā, sabbanāmāni ca. Yathā – taṭo, īmhi – taṭī, taṭamiccādi.</p>\r\n<p class="bodytext">Nāmīkatā yathā – devadatto, ā – devadattā devadattamiccādi.</p>','\r',char(13)),'\n',char(10)),'-147-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',102,replace(replace('<p class="title">Sabbanāma</p>\r\n<p class="bodytext"><a name="para148"></a><span class="paranum">148</span>. Sabba<a name="M0.0102"></a>, katara, katama, ubhaya, itara, añña, aññatara, aññatama, pubba, para, apara, dakkhiṇa, uttara, eka, ya, ta, eta, ima, amu, kiṃ, tumha, amha-iti sabbanāmāni.</p>\r\n<p class="bodytext">Sabbo buddhova. Ayaṃ viseso.</p>\r\n<p class="bodytext">Yotveva.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmakārate paṭhamo</span>.</p>','\r',char(13)),'\n',char(10)),'-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',103,replace(replace('<p class="bodytext">Sabbādīnamakārato <a name="M0.0103"></a> paro paṭhamo yo ettaṃ yāti sabbe.</p>\r\n<p class="bodytext">‘‘Tayo neva ca sabbanāmehī’’ti nisedhā sasmāsminnaṃ āya ā e na honti. Sabbassa.</p>\r\n<p class="bodytext"><span class="bld">Sabbato naṃ saṃsānaṃ</span>.</p>\r\n<p class="bodytext">Sabbādito naṃiccassa saṃsānaṃ honti.</p>\r\n<p class="bodytext">Akāro eitveva.</p>\r\n<p class="bodytext"><span class="bld">Sabbanāmānaṃ namhi ca</span>.</p>\r\n<p class="bodytext">Namhi sabbādīnamakārassa e hoti. Sabbesaṃ, sabbesānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',104,replace(replace('<p class="bodytext"><a name="para149"></a><span class="paranum">149</span>. Itthiyaṃ <a name="M0.0104"></a> ā, sabbā kaññāva. Ayaṃ viseso. Vātveva.</p>\r\n<p class="bodytext"><span class="bld">Ghapato smiṃsānaṃ saṃsā</span>.</p>\r\n<p class="bodytext">Ghapasaññāto sabbādito smiṃsānaṃ saṃsā vā honti.</p>\r\n<p class="bodytext">‘‘Saṃsāsvekavacanesu ce’’ti sāgamo.</p>\r\n<p class="bodytext"><span class="bld">Gho rassaṃ</span>.</p>\r\n<p class="bodytext">Ekavacanasaṃsāsu gho rassaṃ yāti. Sabbassā, sabbāya. Sabbāsaṃ, sabbāsānaṃ. Sabbassaṃ, sabbāyaṃ. Sabbāsu.</p>\r\n<p class="bodytext"><span class="bld">Netāhi smimāyayā</span>.</p>\r\n<p class="bodytext">Ghapaññāhi sabbādīhi smino āya yā na honti.</p>','\r',char(13)),'\n',char(10)),'-149-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',105,replace(replace('<p class="bodytext"><a name="para150"></a><span class="paranum">150</span>. Napuṃsake <a name="M0.0105"></a> – sabbaṃ. Sabbāni. Evaṃ dutiyā.</p>\r\n<p class="bodytext">Sabbādayo napuṃsake tatiyādīsu sakasakapumasamā. Evaṃ ya - saddantā.</p>\r\n<p class="bodytext"><a name="para151"></a><span class="paranum">151</span>. Pubbaparāparehi tu smino ‘‘yadanupapannā nipātanā sijjhantī’’ti anitthiyaṃ e vā. Pubbe, pubbasmiṃiccādi.</p>\r\n<p class="bodytext">Ekasaddo saṅkhyātulyaññāsahāyattho. Yadā saṅkhyattho, tadekavacano, aññattha sabbavacano ca.</p>\r\n<p class="bodytext">Yādīnamālapanaṃ natthi.</p>\r\n<p class="bodytext"><a name="para152"></a><span class="paranum">152</span>. Ta, si.</p>\r\n<p class="bodytext">Simhi saṃ anapuṃsakassetveva.</p>\r\n<p class="bodytext"><span class="bld">Etatesaṃ to</span>.</p>\r\n<p class="bodytext">Simhi anapuṃsakānaṃ etataiccetesaṃ takārassa sa hoti. So.</p>\r\n<p class="bodytext"><span class="bld">Tassa vā nattaṃ sabbattha</span>.</p>','\r',char(13)),'\n',char(10)),'-150-151-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',106,replace(replace('<p class="bodytext">Tiliṅgesu <a name="M0.0106"></a> sabbādīnaṃ takārassa no vā hoti. Ne, te. Sesaṃ sabbasamaṃ, nattaṃva viseso.</p>\r\n<p class="bodytext"><a name="para153"></a><span class="paranum">153</span>. Itthiyaṃ – sā, nā, nāyo, tā, tāyoiccādi.</p>\r\n<p class="bodytext">Vātveva.</p>\r\n<p class="bodytext"><span class="bld">Tato sassa ssāya</span>.</p>\r\n<p class="bodytext">Tāetāimāhi sassa ssāyo vā hoti.</p>\r\n<p class="bodytext">Saṃsāsvekavacanesu iitveva.</p>\r\n<p class="bodytext"><span class="bld">Tassā vā</span>.</p>\r\n<p class="bodytext">Ekavacanasaṃsāsu tāsaddassa ā ittaṃ vā yāti. Tissāya, tissā, tassā, tāya. Tāsaṃ, tāsānaṃ. Tissaṃ, tassaṃ, tāyaṃ. Tāsu.</p>','\r',char(13)),'\n',char(10)),'-153-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',107,replace(replace('<p class="bodytext"><a name="para154"></a><span class="paranum">154</span>. Napuṃsake <a name="M0.0107"></a> – taṃiccādi.</p>\r\n<p class="bodytext"><a name="para155"></a><span class="paranum">155</span>. Eso. Sesaṃ sabbasamaṃ.</p>\r\n<p class="bodytext"><a name="para156"></a><span class="paranum">156</span>. Itthiyaṃ – esā.</p>\r\n<p class="bodytext">Saṃsāsvekavacanesvetveva.</p>\r\n<p class="bodytext"><span class="bld">Etimāsami</span>.</p>\r\n<p class="bodytext">Ekavacanasaṃsāsu etāimānamantassa i hoti. Etissāya, etisā, etāya. Etāsaṃ, etāsānaṃ. Etissaṃ, etassaṃ, etāyaṃ. Etāsu. Sesaṃ sabbāva.</p>\r\n<p class="bodytext"><a name="para157"></a><span class="paranum">157</span>. Napuṃsake – etaṃ iccādi.</p>\r\n<p class="bodytext"><a name="para158"></a><span class="paranum">158</span>. Ima, si.</p>\r\n<p class="bodytext">‘‘Anapuṃsakassāyaṃ simhī’’ti imassa ayaṃ. Silopo. Ayaṃ, ime. Imaṃ, ime.</p>\r\n<p class="bodytext">‘‘Animi nāmhi ce’’ti imassa ano, imi ca. Anena, iminā.</p>\r\n<p class="bodytext">‘‘Sabbassimasse vā’’ti sunaṃhisu evā. Ehi, imehi. Vā smāsasmiṃsaṃsāsvattantveva.</p>\r\n<p class="bodytext"><span class="bld">Imasaddassa ca</span>.</p>','\r',char(13)),'\n',char(10)),'-154-155-156-157-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',108,replace(replace('<p class="bodytext">Sasmāsmiṃsaṃsāsu <a name="M0.0108"></a> imassa attaṃ vā hoti. Assa, imassa. Esaṃ, esānaṃ, imesaṃ, imesānaṃ. Asmā, imamhā, imasmā. Ehi, imehi. Asmiṃ, imamhi, imasmiṃ. Esu, imesu.</p>\r\n<p class="bodytext">Attapakkhe – ‘‘na timehi katākārehī’’ti smāsminnaṃ mhāmhi na sijjhante.</p>\r\n<p class="bodytext"><a name="para159"></a><span class="paranum">159</span>. Itthiyaṃ – ayaṃ. Sesaṃ etāva, saṃsāsvattaṃva viseso.</p>\r\n<p class="bodytext"><a name="para160"></a><span class="paranum">160</span>. Napuṃsake – savibhattissa vātveva.</p>\r\n<p class="bodytext">‘‘Imassidamaṃsisu napuṃsake’’ti imassa idaṃ vā. Idaṃ, imaṃ ime, imāni. Evaṃ dutiyā.</p>','\r',char(13)),'\n',char(10)),'-159-160-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',109,replace(replace('<p class="bodytext"><a name="para161"></a><span class="paranum">161</span>. Amu<a name="M0.0109"></a>, si.</p>\r\n<p class="bodytext">Vā anapuṃsakassa simhitveva.</p>\r\n<p class="bodytext">‘‘Amussa mo saṃ’’ti massa so vā, silopo. Asu.</p>\r\n<p class="bodytext">‘‘Sabbato ko’’ti sabbanāmato kāgamo. ‘‘So’’ti o, amuko, pakkhe – amu. Amū, amuyo.</p>','\r',char(13)),'\n',char(10)),'-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',110,replace(replace('<p class="bodytext">Pubbeva <a name="M0.0110"></a> yonaṃ vokāro na. Amuṃ. Amū, amuyo. Sesaṃ bhikkhūva sabbādikāriyāññatra.</p>\r\n<p class="bodytext"><a name="para162"></a><span class="paranum">162</span>. Itthiyaṃ – asu. Sesaṃ yāgusamaṃ. Visesoyaṃ – amussā, amuyā. Amūsaṃ, amūsānaṃ. Amussaṃ, amuyaṃ. Amūsu.</p>\r\n<p class="bodytext"><a name="para163"></a><span class="paranum">163</span>. Napuṃsake – savibhattissa aṃsisu napuṃsaketveva.</p>\r\n<p class="bodytext">‘‘Amussāduṃ’’ti aduṃ. Aduṃ. Amū, amūni. Evaṃ dutiyā.</p>\r\n<p class="bodytext"><a name="para164"></a><span class="paranum">164</span>. ‘‘Sesesu ce’’ti sabbattha kissa ko. Ko, kā, kaṃ iccādi. Liṅgattaye sabbasamo.</p>','\r',char(13)),'\n',char(10)),'-162-163-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',112,replace(replace('<p class="bodytext"><a name="para165"></a><span class="paranum">165</span>. Tumha<a name="M0.0112"></a>, si, amha, si.</p>\r\n<p class="bodytext">Savibhattissa tumhamhānantyadhikāro.</p>\r\n<p class="bodytext"><span class="bld">Tvamahaṃ simhi ca</span>.</p>\r\n<p class="bodytext">Simhi savibhattīnaṃ tumhamhānaṃ tvaṃ ahaṃ honti. Casaddena tumhassa tuvaṃ ca. Tvaṃ, tuvaṃ, ahaṃ. Yo – tumhe.</p>\r\n<p class="bodytext">‘‘Mayaṃ yomhi paṭhame’’ti amhassa mayaṃ hoti. Mayaṃ.</p>\r\n<p class="bodytext"><a name="para166"></a><span class="paranum">166</span>. ‘‘Taṃmamamhī’’ti amhi taṃ maṃ honti.</p>\r\n<p class="bodytext">‘‘Tavaṃ mamañca navā’’ti amhi tavaṃ mamañca navā.</p>\r\n<p class="bodytext">‘‘Tumhassa tuvaṃ tvamamhī’’ti tumhassa tuvaṃ, tvañca. Taṃ, tavaṃ, tuvaṃ, tvaṃ, maṃ, mamaṃ.</p>\r\n<p class="bodytext">Ākantveva.</p>\r\n<p class="bodytext">‘‘Vā yoppaṭhamo’’ti dutiyāyossa ākaṃ vā. Tumhākaṃ, tumhe. Amhākaṃ, amhe.</p>\r\n<p class="bodytext"><a name="para167"></a><span class="paranum">167</span>. ‘‘Nāmhi tayā mayā’’ti nāmhi tayā mayā honti. ‘‘Tayātayīnaṃ takāro tvattaṃvā’’ti tassa tvo vā. Tvayā, tayā, mayā. Tumhehi, amhehi.</p>','\r',char(13)),'\n',char(10)),'-165-166-167-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',113,replace(replace('<p class="bodytext"><a name="para168"></a><span class="paranum">168</span>. ‘‘Tava <a name="M0.0113"></a> mama se’’ti se tava mama honti.</p>\r\n<p class="bodytext">‘‘Tuyhaṃ mayhaṃ ce’’ti se tuyhaṃ mayhañca.</p>\r\n<p class="bodytext">‘‘Sassaṃ’’ti sassa aṃ vā.</p>\r\n<p class="bodytext">‘‘Amhassa mamaṃ savibhattissa se’’ti se amhassa mamaṃ ca. Tava, tuyhaṃ, tumhaṃ, mama, mayhaṃ, amhaṃ, mamaṃ.</p>\r\n<p class="bodytext">‘‘Tumhamhehi namākaṃ’’ti naṃvacanassa ākaṃ. Tumhākaṃ, amhākaṃ. Smānāva.</p>\r\n<p class="bodytext"><a name="para169"></a><span class="paranum">169</span>. ‘‘Tumhāmhānaṃ tayi mayī’’ti smimhi tayi mayi honti. Tve kate - tvayi, tayi, mayi, tumhesu, amhesu. Liṅgattaye samaṃ.</p>\r\n<p class="bodytext"><a name="para170"></a><span class="paranum">170</span>. Navātveva.</p>\r\n<p class="bodytext"><span class="bld">Padato dutiyā catutthī chaṭṭhīsu vono</span>.</p>\r\n<p class="bodytext">Atthajjotakā vaṇṇā <span class="bld">padaṃ,</span> dutiyā catutthī chaṭṭhī bahuvacanesu paresu padasmā paresaṃ savibhattīnaṃ tumhāmhānaṃ vonokārā navā honti.</p>','\r',char(13)),'\n',char(10)),'-168-169-170-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',114,replace(replace('<p class="bodytext">Rakkhatu <a name="M0.0114"></a> vo, passatu no, dadāti vo, dadāhi no, saddhā vo, satthā no.</p>\r\n<p class="bodytext">Navāti kiṃ, eso amhākaṃ satthā.</p>\r\n<p class="bodytext"><a name="para171"></a><span class="paranum">171</span>. Padatotyadhikāro.</p>\r\n<p class="bodytext">‘‘Temekavacanesu ce’’ti catutthīchaṭṭhekavacanesu te me honti. Dadāmi te, dadāhi me, idaṃ te, ayaṃ me.</p>','\r',char(13)),'\n',char(10)),'-171-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',115,replace(replace('<p class="bodytext">‘‘Naamhī’’ti <a name="M0.0115"></a> amhi nisedho. Passetha taṃ, ajini maṃ.</p>\r\n<p class="bodytext">‘‘Vā tatiye ce’’ti tatiyekavacane te me vā honti. Kataṃ te tayā vā. Kataṃ me mayā vā.</p>\r\n<p class="bodytext">‘‘Bahuvacanesu vono’’ti tatiyābahuvacanesu vo no honti. Bahuvacane paṭhame yomhi ca. Kataṃ vo, kataṃ no, gāmaṃ vo gaccheyyātha, gāmaṃ no gaccheyyāma.</p>\r\n<p class="title">Saṅkhyā</p>\r\n<p class="bodytext"><a name="para172"></a><span class="paranum">172</span>. Saṅkhyā vuccate.</p>\r\n<p class="bodytext">Ekasaddo sabbanāmesu vutto.</p>','\r',char(13)),'\n',char(10)),'-172-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',117,replace(replace('<p class="bodytext"><a name="para173"></a><span class="paranum">173</span>. Dvādayo <a name="M0.0117"></a> aṭṭhārasantā bahuvacanantā.</p>\r\n<p class="bodytext">Savibhattissa, itthipumanapuṃsakasaṅkhyanti cādhikāro.</p>\r\n<p class="bodytext">‘‘Yosu dvinnaṃ dve ce’’ti dvissa dve hoti. Dve, dve, dvībhi, dvīhi.</p>\r\n<p class="bodytext"><span class="bld">No ca dvādito namhi</span>.</p>\r\n<p class="bodytext">Namhi dvādito nakārāgamo hoti. Dvinnaṃ, dvīsu, liṅgattaye samaṃ.</p>\r\n<p class="bodytext"><a name="para174"></a><span class="paranum">174</span>. <span class="bld">Ticatunnaṃ tisso catasso tayo cattāro tīṇi cattāri</span>.</p>\r\n<p class="bodytext">Yosu itthipumanapuṃsakesu savibhattīnaṃ ticatunnaṃ tisso catasso ādayo honti. Tayo, tayo, tībhi, tīhi, tinnaṃ.</p>\r\n<p class="bodytext">‘‘Iṇṇamiṇṇannaṃ tīhi saṅkhyāhī’’ti tisaddato naṃiccassa iṇṇaṃ iṇṇannaṃ ca. Tiṇṇaṃ, tiṇṇannaṃ, tīsu.</p>','\r',char(13)),'\n',char(10)),'-173-174-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',118,replace(replace('<p class="bodytext"><a name="para175"></a><span class="paranum">175</span>. Itthiyaṃ-tisso<a name="M0.0118"></a>, tisso, tībhi, tīhi.</p>\r\n<p class="bodytext">‘‘No cā’’do cakārena namhi ssaṃāgamo, vaggantetissanaṃ, tīsu.</p>\r\n<p class="bodytext"><a name="para176"></a><span class="paranum">176</span>. Napuṃsake-tīṇi, tīṇi, tīhi.</p>\r\n<p class="bodytext"><a name="para177"></a><span class="paranum">177</span>. Cattāro.</p>\r\n<p class="bodytext">‘‘Osare ce’’tīha cakārena yosu ussa uro. ‘‘Tato yonamotū’’tīha tukārena yonaṃ o. Caturo. Evaṃ dutiyā. Catūhi, catunnaṃ, catūsu.</p>','\r',char(13)),'\n',char(10)),'-175-176-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',119,replace(replace('<p class="bodytext"><a name="para178"></a><span class="paranum">178</span>. Itthiyaṃ<a name="M0.0119"></a>, catasso, catasso, catūhi. Pubbeva ssaṃāgamo. Yadādinā ussa attaṃ. Catassannaṃ, catūsu.</p>\r\n<p class="bodytext"><a name="para179"></a><span class="paranum">179</span>. Napuṃsake, cattāri, cattāri.</p>\r\n<p class="bodytext"><a name="para180"></a><span class="paranum">180</span>. ‘‘Pañcādīna makāro’’ti yosu savibhattissa pañcādyantassa attaṃ. Pañca, pañca.</p>\r\n<p class="bodytext">‘‘Pañcādīna mattaṃ’’ti sunaṃhisu pañcādyantassa attaṃ. Edīghānamapavādoyaṃ. Pañcahi, pañcannaṃ, pañcasu. Liṅgattaye samaṃ.</p>\r\n<p class="bodytext"><a name="para181"></a><span class="paranum">181</span>. Evaṃ cha satta aṭṭha nava dasādayo aṭṭhārasantā.</p>','\r',char(13)),'\n',char(10)),'-178-179-180-181-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',120,replace(replace('<p class="bodytext"><a name="para182"></a><span class="paranum">182</span>. Vīsatyādayo <a name="M0.0120"></a> ānavutiyā itthiliṅgā ekavacanantā, vīsati rattīva. Evaṃ tiṃsati.</p>\r\n<p class="bodytext">Cattālīsaṃ paññāsaṃ saddehi parāsaṃ sabbāsaṃ vibhattīnaṃ ‘‘sabbāsamā’’dotīha ādisaddena lopo, saṭṭhi vīsatī va. Evaṃ sattati asīti navuti.</p>\r\n<p class="bodytext">Sataṃ napuṃsakamekavacanantaṃ. Evaṃ sahassādi.</p>\r\n<p class="bodytext">Koṭi vīsatīva.</p>','\r',char(13)),'\n',char(10)),'-182-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',122,replace(replace('<p class="bodytext"><a name="para183"></a><span class="paranum">183</span>. Rāsibhede <a name="M0.0122"></a> tu sabbattha bahuvacanampi. Yathā-dvevīsatiyo buddhadantā. Tisso vīsatiyo dinaghaṭikā. Evamaññatra.</p>\r\n<p class="bodytext">Eseso etantippasiddhi, lokassa hoti yatthatthesu.</p>\r\n<p class="bodytext">Thīpumanapuṃsakānityuccante, tānimāni lokenātthā.</p>\r\n<p class="title">Aliṅga</p>\r\n<p class="bodytext"><a name="para184"></a><span class="paranum">184</span>. Aliṅgā vuccante.</p>\r\n<p class="bodytext"><span class="bld">Kvaci to pañcamyatthe</span>.</p>\r\n<p class="bodytext">Liṅgato pañcamyatthe kvaci toppaccayo hoti.</p>\r\n<p class="bodytext">‘‘Tvādayo vibhattisaññāyo’’ti toppabhutidānyantānaṃ vibhattisaññā. Tasmā tadantānampi vibhatyantattā padattaṃ siddhanti na puna vibhatti. Corasmā corato. Evaṃ pitito, ettha ‘‘pitādīna masimhi’’ tyatrāsimhiggahaṇena tomhi pitādīnaṃ ussa i.</p>\r\n<p class="bodytext"><span class="bld">Imassi thandānihatodhesu ca</span>.</p>\r\n<p class="bodytext">Thaṃādīsu paresu imassa i hoti. Ito.</p>\r\n<p class="bodytext">‘‘Sabbassetassākāro vā’’ti tothesvetassa attaṃ vā, ato, etto. Pakkhe- ‘‘saralopā’’dinā akāralopo.</p>','\r',char(13)),'\n',char(10)),'-183-184-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',126,replace(replace('<p class="bodytext">‘‘Tratothesu <a name="M0.0126"></a> ce’’ti kissa ku. Kuto.</p>\r\n<p class="bodytext">‘‘Kvaci to’’ti suttadvidhākaraṇena sattamyatthe ca to hoti, ādismiṃ, ādito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',127,replace(replace('<p class="bodytext"><a name="para185"></a><span class="paranum">185</span>. ‘‘Tratha <a name="M0.0127"></a> sattamiyā sabbanāmehī’’ti sattamyatthe trathappaccayā honti. Sabbasmiṃ, sabbatra, sabbattha, dvittaṃ. Evaṃ atra, attha. Ettha ‘‘tre nicca’’nti pubbe etassa a. Kutra, kuttha.</p>\r\n<p class="bodytext">‘‘Sesesu ce’’ti kādese-kattha.</p>\r\n<p class="bodytext"><a name="para186"></a><span class="paranum">186</span>. ‘‘Kismā vo ce’’ti vappaccayo. ‘‘Kissa ka vece’’ti ko, kakārākāralopo. Kva.</p>\r\n<p class="bodytext"><a name="para187"></a><span class="paranum">187</span>. ‘‘Hiṃ haṃ hiñcana’’nti kasmā hiṃ ādipaccayā. ‘‘Ku hiṃ haṃsu ce’’ti kissa ku. Cakārena hiñcanaṃ dācanaṃsu ca. Kuhiṃ, kuhaṃ, kuhiñcanaṃ.</p>\r\n<p class="bodytext"><a name="para188"></a><span class="paranum">188</span>. ‘‘Tamhā ce’’ti hiṃhaṃ. Tahiṃ, tahaṃ.</p>\r\n<p class="bodytext"><a name="para189"></a><span class="paranum">189</span>. ‘‘Yato hiṃ’’ti hiṃ. Yahiṃ.</p>','\r',char(13)),'\n',char(10)),'-185-186-187-188-189-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',128,replace(replace('<p class="bodytext"><a name="para190"></a><span class="paranum">190</span>. ‘‘Imasmā <a name="M0.0128"></a> hadhā ce’’ti hadhā. Iha, idha.</p>\r\n<p class="bodytext"><a name="para191"></a><span class="paranum">191</span>. ‘‘Sabbato dhī’’ti dhi. Sabbadhi.</p>\r\n<p class="bodytext"><a name="para192"></a><span class="paranum">192</span>. Kāletyadhikāro.</p>\r\n<p class="bodytext">‘‘Kiṃsabbaññekayakuhi dādācana’’nti kiṃ ādito dā, dācanaṃ ca. Kasmiṃ kāle kadā, kudācanaṃ.</p>\r\n<p class="bodytext">‘‘Sabbassa so dāmhi vā’’ti sabbassa so vā. Sadā, sabbadā.</p>\r\n<p class="bodytext"><a name="para193"></a><span class="paranum">193</span>. ‘‘Tamhā dāni ce’’ti dāni, dā ca. Tadāni, tadā.</p>\r\n<p class="bodytext"><a name="para194"></a><span class="paranum">194</span>. Yadādinā imasaddā, samānāparehi ca yathāsaṅkhyaṃ jja jjuppaccayā, ima, samānānaṃ a, sā ca. Ajja, sajju, aparajju.</p>','\r',char(13)),'\n',char(10)),'-190-191-192-193-194-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',129,replace(replace('<p class="bodytext"><a name="para195"></a><span class="paranum">195</span>. ‘‘Imasmā <a name="M0.0129"></a> rahidhunādāni ce’’ti rahyādippaccayā. ‘‘Eta rahimhī’’ti imassa eto. Etarahi.</p>\r\n<p class="bodytext">‘‘A dhunāmhi ce’’ti imassa a. Adhunā, idāni.</p>\r\n<p class="bodytext"><a name="para196"></a><span class="paranum">196</span>. Lopaṃ itveva.</p>\r\n<p class="bodytext"><span class="bld">Sabbāsamāvusopasagganipātādīhi ca</span>.</p>\r\n<p class="bodytext">Etehi parā sabbā vibhattī lupyante. Tvaṃ āvuso, tumhe āvuso.</p>\r\n<p class="title">Upasagganipāta</p>\r\n<p class="bodytext"><a name="para197"></a><span class="paranum">197</span>. Upasagganipātā vuccante.</p>','\r',char(13)),'\n',char(10)),'-195-196-197-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',130,replace(replace('<p class="bodytext">Pa <a name="M0.0130"></a> parā ni nī u du saṃ vi ava anu pari adhi abhi pati su ā ati api apa upa ete vīsatyupasaggā.</p>\r\n<p class="bodytext">Ca na va vā mā hi dhi ci ku tu nu ce re he sve ve vo kho no to yaṃ naṃ taṃ kiṃ handa kira eva kīva yāva tāva vata vatha atha aṅga iṅgha taggha āma nāma nūna puna pana āha saha sakkā labbhā heṭṭhā ārā dūrā divā navā vinā nānā addhā mudhā micchā pacchā āvi sakkhi sacci sacchi bahi yadi iti kinti atthi sotthi khalu nanu kimu assu yagghe sace have suve are pure namo tiro adho atho aho raho hīyo bhīyo anto pāto sudaṃ kallaṃ evaṃ dhuvaṃ alaṃ halaṃ sayaṃ sāyaṃ samaṃ sāmaṃ kāmaṃ pāraṃ oraṃ ciraṃ huraṃ ahaṃ sahaṃ uccaṃ nīcaṃ sakiṃ saddhiṃ, athavā antarā ārakā bāhirā bahiddhā yāvatā tāvatā samantā sāmantā āmantā sammukhā carahi tarahi sampati āyati upari yāvade tāvade tiriyaṃ sanikaṃ sasakkaṃ ettāvatā parammukhā kittāvatā etarahi aññadatthu seyyathidaṃ appevanāma bhīyosomattāya iccādayo nipātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',134,replace(replace('<p class="bodytext"><a name="para198"></a><span class="paranum">198</span>. Sadisā <a name="M0.0134"></a> ye tiliṅgesu, sabbāsu ca vibhattīsu.</p>\r\n<p class="bodytext">Vacanesu ca sabbesu, te nipātāti kittitā.</p>','\r',char(13)),'\n',char(10)),'-198-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',135,replace(replace('<p class="bodytext">Yathā <a name="M0.0135"></a> – uccaṃ rukkho, latā, gharaṃ vā, uccaṃ rukkho. He rukkha, rukkhaṃ, rukkhena, rukkhassa, rukkhasmā, rukkhe vā iccādi. Uccaṃ rukkho, rukkhā vā iccādi. Evaṃ latā, gharāni.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> Ubhayesu vibhatyatta -</p>\r\n<p class="gatha2">Kriyadesa samaya disāguṇatthehi;</p>\r\n<p class="gatha3">Sabbāpi yathāyogaṃ,</p>\r\n<p class="gathalast">Vibhattiyoññehi tuppaṭhamā.</p>\r\n<p class="bodytext">Taṃ yathā – adhiantosaddehi sattamī. Sayaṃsaddā tatiyā, chaṭṭhī ca. Namosaddā paṭhamā, dutiyā ca. Pāraṃsaddā sattamī. Divāsaddā paṭhamā, dutiyā, sattamī ca. Heṭṭhāsaddā sattamī. Uccaṃsaddā sabbāpi. Pasaddā ca casaddā ca paṭhamā, hesaddā ālapane paṭhamā. Tathāññehipi.</p>','\r',char(13)),'\n',char(10)),'-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',136,replace(replace('<p class="bodytext"><a name="para200"></a><span class="paranum">200</span>. Upasaggā <a name="M0.0136"></a> sabbepi saddantarena saha payujjante. Nipātā tu keci visumpi. Yathā – pahāro, paharati, sā ca so ca bhāsati vā karoti vā, sotthityādi.</p>','\r',char(13)),'\n',char(10)),'-200-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',137,replace(replace('<p class="bodytext"><a name="para201"></a><span class="paranum">201</span>. Ekekaliṅgaṃ <a name="M0.0137"></a> dviliṅgaṃ, tiliṅgaṃ cāpyaliṅgikaṃ.</p>\r\n<p class="bodytext">Catudheti nāmaṃ nāmaṃ, namatyatthanti kittitaṃ.</p>\r\n<p class="bodytext">Nāmikaṃ.</p>','\r',char(13)),'\n',char(10)),'-201-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',138,replace(replace('<p class="chapter">3. Samāsakaṇḍa</p>\r\n<p class="title">Samāsalakkhaṇādi</p>\r\n<p class="bodytext"><a name="para202"></a><span class="paranum">202</span>. Nāmānaṃ <a name="M0.0138"></a> samāso yuttatthotyadhikāro. <span class="bld">Samāso</span>ti bhinnatthānaṃ padāna mekatthatā. <span class="bld">Yuttattho</span>ti aññamaññasambandhattho.</p>','\r',char(13)),'\n',char(10)),'-202-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',142,replace(replace('<p class="bodytext">Vibhāsātyadhikātabbaṃ <a name="M0.0142"></a> vākyatthaṃ.</p>\r\n<p class="title">Kammadhārayasamāsa</p>\r\n<p class="bodytext"><a name="para203"></a><span class="paranum">203</span>. ‘‘Mahanto ca so vīro cā’’ti vākye –</p>\r\n<p class="bodytext"><span class="bld">Dvipade tulyādhikaraṇe kammadhārayo</span>.</p>','\r',char(13)),'\n',char(10)),'-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',143,replace(replace('<p class="bodytext">Bhinnappavattinimittā <a name="M0.0143"></a> saddā ekasmiṃ vatthuni pavattā tulyādhikaraṇā, visesanavisesassabhūtā samānādhikaraṇā dve padā yadā samasyante, tadā so samāso kammadhārayo</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',144,replace(replace('<p class="bodytext">Nāma<a name="M0.0144"></a>, idha vā samāsasuttāni saññādvārena samāsavidhāyakāni.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',146,replace(replace('<p class="bodytext">Aggahitavisesanā <a name="M0.0146"></a> buddhi visessamhi na uppajjatīti visesanaṃ pubbaṃ hoti, samāseneva tulyādhikaraṇattassa vuttattā tappakāsanatthaṃ payuttā samāsato atirittā ca so iccete ‘‘vuttaṭṭhānamappayogo’’ti ñāyā nappayujjante. Evamaññatra.</p>\r\n<p class="bodytext"><span class="bld">Tesaṃ vibhattiyo lopā ca</span>.</p>\r\n<p class="bodytext">Tesaṃ yuttatthānaṃ samāsānaṃ pubbuttarapadānaṃ vibhattī lupyante, cakārena kvaci na.</p>\r\n<p class="bodytext">Tato mahanta vīra iti ca rūpappasaṅge –</p>\r\n<p class="bodytext"><span class="bld">Pakati cassa sarantassa</span>.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',147,replace(replace('<p class="bodytext">Vibhattīsu <a name="M0.0147"></a> luttāsu sarantassa pubbabhūtassa, parabhūtassa ca assa samāsapadassa pakati hotītīha luttākārā punānīyante.</p>\r\n<p class="bodytext">Tato mahanta vīraiti ṭhite –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',149,replace(replace('<p class="bodytext">‘‘Mahataṃ <a name="M0.0149"></a> mahā tulyādhikaraṇe pade’’ti mahantassa mahā.</p>\r\n<p class="bodytext"><span class="bld">Taddhita samāsa kitakā nāmaṃvātavetunādīsu ca</span>.</p>\r\n<p class="bodytext">Taddhitādayo nāmaṃ iva daṭṭhabbā taveppabhutipaccaye vajjetvā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',150,replace(replace('<p class="bodytext">Tato <a name="M0.0150"></a> vatticchāya syādi. Mahāvīro, mahāvīrāiccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',152,replace(replace('<p class="bodytext"><a name="para204"></a><span class="paranum">204</span>. Kammadhārayo <a name="M0.0152"></a> dvando ca, tappuriso ca lābhino.</p>\r\n<p class="bodytext">Tayo parapade liṅgaṃ, bahubbīhi padantare.</p>\r\n<p class="bodytext"><a name="para205"></a><span class="paranum">205</span>. Rattā ca sā paṭī cāti rattapaṭī, mahantī ca sā saddhā cāti mahāsaddhā. Ettha ‘‘kammadhārayasaññe ce’’ti pubbapade pumeva kate āīpaccayānaṃ nivutti.</p>\r\n<p class="bodytext"><a name="para206"></a><span class="paranum">206</span>. Nīlañca taṃ uppalañcāti nīluppalaṃ, satthīva satthi, satthi ca sā sāmā cāti satthisāmā. Mukhameva cando mukhacando.</p>','\r',char(13)),'\n',char(10)),'-204-205-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',154,replace(replace('<p class="bodytext">Visesanavisessānaṃ <a name="M0.0154"></a> yathecchattā kvaci visesanaṃ paraṃ hoti, khattiyabhūtoiccādi, icchā ca yathātanti.</p>\r\n<p class="title">Ubhe tappurisa samāsa</p>\r\n<p class="bodytext"><a name="para207"></a><span class="paranum">207</span>. Nasaddā si, tassa lopo. Na suro asuro.</p>\r\n<p class="bodytext">Ettha kammadhāraye kate – ‘‘ubhe tappurisā’’ti tappurisasaññā. ‘‘Attannassa tappurise’’ti nassa a. Na asso anasso. Ettha ‘‘sare ana’’ti nassa ana.</p>','\r',char(13)),'\n',char(10)),'-207-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',155,replace(replace('<p class="bodytext"><a name="para208"></a><span class="paranum">208</span>. ‘‘Nāmānaṃ <a name="M0.0155"></a> samāso’’ti sutte dvidhākate ayuttatthānampi kvaci samāso. Na puna geyyā apunageyyā gāthetyādi. Ettha geyyena sambandho na-saddo ayuttatthenāpi punena yogavibhāgabalā samasyate.</p>','\r',char(13)),'\n',char(10)),'-208-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',156,replace(replace('<p class="title">Digusamāsa</p>\r\n<p class="bodytext"><a name="para209"></a><span class="paranum">209</span>. Tayo <a name="M0.0156"></a> lokā samāhaṭā tilokaṃ.</p>\r\n<p class="bodytext">Ettha ‘‘saṅkhyāpubbo digū’’ti kammadhārayassa digusaññā. ‘Digussekattaṃ’’ti ekattaṃ, napuṃsakattañca.</p>','\r',char(13)),'\n',char(10)),'-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',157,replace(replace('<p class="title">Suddhatappurisasamāsa</p>\r\n<p class="bodytext"><a name="para210"></a><span class="paranum">210</span>. Tappurisā <a name="M0.0157"></a> tveva.</p>\r\n<p class="bodytext"><span class="bld">Amādayo parapadehi</span>.</p>\r\n<p class="bodytext">Dutiyantādayo parapadehi nāmehi yadā samasyante, tadā so samāso tappuriso nāma.</p>','\r',char(13)),'\n',char(10)),'-210-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',158,replace(replace('<p class="bodytext">Gāmaṃ <a name="M0.0158"></a> gato gāmagato.</p>\r\n<p class="bodytext">‘‘Passa vāsiṭṭha gāmaṃ, gato tisso sāvatthiṃ’’tya trāyuttatthatāya na samāso. Tathā ññatra ñeyyaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',159,replace(replace('<p class="bodytext"><a name="para211"></a><span class="paranum">211</span>. Raññā <a name="M0.0159"></a> hato rājahato.</p>\r\n<p class="bodytext">Kiccantehi bhīyo adhikatthavacane.</p>\r\n<p class="bodytext">Tabba, anīya, ṇya, teyya, riccappaccayā <span class="bld">kiccā</span>. Thutinindatthamajjhāropitatthaṃ vacanaṃ <span class="bld">adhikatthavacanaṃ</span>. Soṇaleyyo kūpoiccādi. Soṇehi yathā liyhate, tathā puṇṇattā <span class="bld">thuti</span>. Tehi ucchiṭṭhattā <span class="bld">nindā</span> ca.</p>\r\n<p class="bodytext">Dadhinā upasittaṃ bhojanaṃ dadhibhojanaṃ, samāsapadeneva upasittakriyāya kathanā natthetthāyuttatthatā. Upasittasaddāppayogo pubbeva.</p>','\r',char(13)),'\n',char(10)),'-211-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',160,replace(replace('<p class="bodytext"><a name="para212"></a><span class="paranum">212</span>. Karaṇe <a name="M0.0160"></a> tu-asinā kalaho asikalaho.</p>\r\n<p class="bodytext"><a name="para213"></a><span class="paranum">213</span>. Buddhassa deyyaṃ buddhaddeyyaṃ, parassapadaṃ, ettha vibhatyalopo. Evaṃ attanopadamiccādi.</p>','\r',char(13)),'\n',char(10)),'-212-213-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',161,replace(replace('<p class="bodytext"><a name="para214"></a><span class="paranum">214</span>. Corasmā <a name="M0.0161"></a> bhayaṃ corabhayaṃ. Evaṃ baddhanamuttoccādi.</p>\r\n<p class="bodytext"><a name="para215"></a><span class="paranum">215</span>. Rañño putto rājaputto.</p>\r\n<p class="bodytext">‘‘Brāhmaṇassa kaṇhā dantā’’ iccatra dantāpekkhā chaṭṭhīti kaṇhena sambandhābhāvā na samāso. Yadā tu kaṇhā ca te dantā ceti kammadhārayo, tadā chaṭṭhī kaṇhadantāpekkhāti brāhmaṇakaṇhadantāti samāso hoteva.</p>','\r',char(13)),'\n',char(10)),'-214-215-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',162,replace(replace('<p class="bodytext"><a name="para216"></a><span class="paranum">216</span>. ‘‘Rañño <a name="M0.0162"></a> māgadhassa dhana’’ ntyatra raññoti chaṭṭhī dhana mapekkhate, na māgadhaṃ. Rājā eva māgadhasaddena vuccateti bhedābhāvā sambandhābhāvoti tulyādhikaraṇena māgadhena saha rājā na samasyate. Dviṭṭho hi sambandho.</p>','\r',char(13)),'\n',char(10)),'-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',163,replace(replace('<p class="bodytext">Rañño <a name="M0.0163"></a> asso puriso ce’’ tya tra rañño asso, rañño puriso ti ca paccekaṃ sambandhato sāpekkhatā atthīti na samāso. ‘‘Asso ca puriso cā’’ti dvande kate tu rājassapurisāti hoteva, aññānapekkhattā.</p>\r\n<p class="bodytext">‘‘Rañño garuputto’’ iccatra rājāpekkhinopi garuno</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',164,replace(replace('<p class="bodytext">Puttena <a name="M0.0164"></a> saha samāso, gamakattā. Gamakattampi samāsassa nibandhanaṃ. Tattha garuno puttoti viggaho, evamaññatra.</p>\r\n<p class="bodytext"><a name="para217"></a><span class="paranum">217</span>. Rūpe saññā rūpasaññā.</p>\r\n<p class="bodytext">Kvaci nindāyaṃ - kūpe maṇḍūko viya kūpamaṇḍūko. Evaṃ nagarakāko iccādi. Atropamāya nindā gamyate.</p>\r\n<p class="bodytext">Antevāsiko tyādo vibhattyalopo.</p>\r\n<p class="title">Bahubbīhisamāsa</p>\r\n<p class="bodytext"><a name="para218"></a><span class="paranum">218</span>. <span class="bld">Aññapadatthesu bahubbīhi.</span></p>','\r',char(13)),'\n',char(10)),'-217-218-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',165,replace(replace('<p class="bodytext">Appaṭhamantāna <a name="M0.0165"></a> maññesaṃ padānaṃ atthesu dve vā bahūni vā nāmāni yadā samasyante, tadā so samāso bahubbīhi nāma.</p>\r\n<p class="bodytext">Āgatā samaṇā yaṃ sā āgatasamaṇo, vihāro.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',166,replace(replace('<p class="bodytext"><a name="para219"></a><span class="paranum">219</span>. Jitāni <a name="M0.0166"></a> indriyāni yena so jitindriyo, bhagavā. Āhito aggi yena so āhitaggi. Agyāhito vātyādo yathecchaṃ visesanassa paratā.</p>\r\n<p class="bodytext"><a name="para220"></a><span class="paranum">220</span>. Karaṇe tu-chinno rukkho yena so chinnarukkho, pharasu.</p>\r\n<p class="bodytext"><a name="para221"></a><span class="paranum">221</span>. Dinno suṅko yassa so dinnasuṅko, rājā.</p>\r\n<p class="bodytext"><a name="para222"></a><span class="paranum">222</span>. Niggatā janā yasmā so niggatajano, gāmo.</p>','\r',char(13)),'\n',char(10)),'-219-220-221-222-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',167,replace(replace('<p class="bodytext"><a name="para223"></a><span class="paranum">223</span>. Dasa <a name="M0.0167"></a> balāni yassa so dasabalo, bhagavā. Natthi samo yassa so asamo. Ettha ‘‘attannassā’’ti yogavibhāgena nassa a.</p>\r\n<p class="bodytext">Pahūtā jivhā yassa so pahūtajivho, mahantī paññā yassa so mahāpañño. Dvīsu ‘‘itthiyambhāsitapumitthīpumāva ce’’ti pumbhāvātidesā pubbuttarapadesu āīppaccayānamabhāvo.</p>','\r',char(13)),'\n',char(10)),'-223-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',169,replace(replace('<p class="bodytext"><a name="para224"></a><span class="paranum">224</span>. ‘‘Kvaci <a name="M0.0169"></a> samāsantagatānamakāranto’’ti antassa attaṃ. Kāraggahaṇena ā i ca. Itthiyamivaṇṇantā, tvantehi ca kappaccayopi. Yathā - visālaṃ akkhi yassa so visālakkho, paccakkhadhammā, silopo. Sobhano gandho yassa so <a name="M0.0171"></a> sugandhi. Bahukantiko, bahunadiko, samuddo. Ettha yadādinā rasso. Bahukattuko. Mattā bahavo mātaṅgā yasmiṃ taṃ mattabahumātaṅgaṃ, vanaṃ.</p>','\r',char(13)),'\n',char(10)),'-224-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',171,replace(replace('<p class="bodytext">so <a name="M0.0171"></a> sugandhi. Bahukantiko, bahunadiko, samuddo. Ettha yadādinā rasso. Bahukattuko. Mattā bahavo mātaṅgā yasmiṃ taṃ mattabahumātaṅgaṃ, vanaṃ.</p>\r\n<p class="centered">Tulyādhikaraṇo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',172,replace(replace('<p class="bodytext"><a name="para225"></a><span class="paranum">225</span>. Suvaṇṇassa <a name="M0.0172"></a> viya vaṇṇo yassa so suvaṇṇavaṇṇo. Vajiraṃ pāṇimhi yassa so vajirapāṇi. Urasi lomāni yassa so urasilomo. Ettha vibhatyalopo.</p>\r\n<p class="bodytext">‘‘Atthesū’’ti bahuttaggahaṇena kvaci paṭhamantānampi. Saha hetunā yo vattate so sahetuko, ‘‘yadā’’ dinā sahassa so.</p>','\r',char(13)),'\n',char(10)),'-225-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',173,replace(replace('<p class="bodytext"><a name="para226"></a><span class="paranum">226</span>. Satta <a name="M0.0173"></a> vā aṭṭha vā sattaṭṭha, māsā, etthaññapadattho vā saddassattho. Dakkhiṇassā ca pubbassā ca disāya yaṃ antarālaṃ, sā dakkhiṇapubbā, disā.</p>\r\n<p class="centered">Bhinnādhikaraṇo.</p>\r\n<p class="bodytext">Appaṭhamantānanti kiṃ, desito buddhena yo dhammo.</p>\r\n<p class="title">Dvandasamāsa</p>\r\n<p class="bodytext"><a name="para227"></a><span class="paranum">227</span>. <span class="bld">Nāmānaṃ samuccayo dvando.</span></p>','\r',char(13)),'\n',char(10)),'-226-227-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',177,replace(replace('<p class="bodytext">Samuccayo<a name="M0.0177"></a>, ti piṇḍīkaraṇaṃ ekavibhattikānaṃ nāmānaṃ yo samuccayo, so dvando nāma, idaṃ suttaṃ bahuvacanavisayaṃ.</p>\r\n<p class="bodytext">Cando ca sūriyo ca candasūriyā. Tiṭṭhanti tyādi-</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',178,replace(replace('<p class="bodytext">Kriyāsambandhasāmaññato <a name="M0.0178"></a> atthetthāpekatthatā, evaṃ naranāriyo, akkharapadāni.</p>\r\n<p class="bodytext"><a name="para228"></a><span class="paranum">228</span>. <span class="bld">Tathā dvande pāṇi turiya yogga senaṅga khuddajantuka vividha viruddha visabhāgatthādīnañca.</span></p>\r\n<p class="bodytext">Vividhenākārena viruddhā <span class="bld">vividhaviruddhā</span>, sabhāgā sadisā, vividhā ca te sabhāgā ceti <span class="bld">visabhāgā</span>. Yathā digusamāse, tathā dvande pāṇyaṅgatthādīnaṃ ekattaṃ, napuṃsakattañca hoti.</p>\r\n<p class="bodytext">Cakkhusotaṃ, gītavāditaṃ, yuganaṅgalaṃ, hatthassaṃ, asicammaṃ, ḍaṃsamakasaṃ, kokālūkaṃ.</p>','\r',char(13)),'\n',char(10)),'-228-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',182,replace(replace('<p class="bodytext">Nāmarūpaṃ<a name="M0.0182"></a>, nāmaṃ namanalakkhaṇaṃ, rūpaṃ ruppanalakkhaṇaṃ. Evamete dhammā lakkhaṇato vividhā, paramatthato sabhāgā ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',183,replace(replace('<p class="bodytext">Ādisaddenāññatthāpi<a name="M0.0183"></a>. Yathā - bhinnaliṅgānaṃ - itthipumaṃ. Yadādinā rasso, dāsidāsaṃ, pattacīvaraṃ. Gaṅgāsoṇaṃ.</p>\r\n<p class="bodytext">Saṅkhyāparimāṇānaṃ - tikacatukkaṃ.</p>\r\n<p class="bodytext">Sippīnaṃ - veṇarathakāraṃ.</p>\r\n<p class="bodytext">Luddakānaṃ - sākuntika māgavikaṃ.</p>\r\n<p class="bodytext">Appāṇijātīnaṃ - ārasatthi.</p>\r\n<p class="bodytext">Ekajjhāyanabrāhmaṇānaṃ - kaṭhakālāpaṃ iccādi.</p>\r\n<p class="bodytext"><a name="para229"></a><span class="paranum">229</span>. <span class="bld">Vibhāsā rukkha tiṇa pasu dhana dhañña janapadādīnañca.</span></p>','\r',char(13)),'\n',char(10)),'-229-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',185,replace(replace('<p class="bodytext">Dvande <a name="M0.0185"></a> rukkhādīnaṃ ekattaṃ napuṃsakattañca vā hoti.</p>\r\n<p class="bodytext">Dhavakhadiraṃ, dhavakhadirā, muñjapabbajaṃ, muñjapabbajā, ajeḷakaṃ, ajeḷakā, hiraññasuvaṇṇaṃ, hiraññasuvaṇṇāni, sāliyavaṃ, sāliyavā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',187,replace(replace('<p class="bodytext">Kāsikosalaṃ <a name="M0.0187"></a> kāsikosalā.</p>\r\n<p class="bodytext">Ādisaddena aññesupi vā. Yathā – niccavirodhīnamaddabbānaṃ - kusalākusalaṃ, kusalākusalāni.</p>\r\n<p class="bodytext">Sakuṇīnaṃ - bakabalākaṃ, bakabalākā.</p>\r\n<p class="bodytext">Byañjanānaṃ - dadhighataṃ, dadhighatāni.</p>\r\n<p class="bodytext">Disānaṃ - pubbāparaṃ, pubbāparā iccādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',189,replace(replace('<p class="title">Abyayībhāvasamāsa</p>\r\n<p class="bodytext"><a name="para230"></a><span class="paranum">230</span>. Adhisaddā <a name="M0.0189"></a> smiṃ, tassa lopo. Adhisaddena tulyādhikaraṇattā itthisaddāpismiṃ. Niccasamāsattā ādhārabhūtāyamitthiyanti padantarena viggaho. Adhi itthiyanti ṭhite –</p>\r\n<p class="bodytext"><span class="bld">Upasagganipātapubbako abyayībhāvo</span>.</p>\r\n<p class="bodytext">Upasaggādipubbako saddo vibhatyatthādīsu samāso hoti, abyayībhāvasañño ca.</p>\r\n<p class="bodytext">‘‘So napuṃsakaliṅgo’’ti abyayībhāvo napuṃsakaliṅgo, yadādinā ekavacano ca.</p>','\r',char(13)),'\n',char(10)),'-230-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',191,replace(replace('<p class="bodytext">‘‘Saro <a name="M0.0191"></a> rasso napuṃsake’’ti rasso.</p>\r\n<p class="bodytext"><span class="bld">Aññasmā lopo ca</span>.</p>\r\n<p class="bodytext">Anakārantā abyayībhāvā parā sabbā vibhattī lujjare. Adhitthi, vibhattīnamattho ādhārādi.</p>\r\n<p class="bodytext">Idhādhisaddo ādhārevattate, adhitthiiccetaṃ padaṃ itthiya miccetamatthaṃ vadati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',193,replace(replace('<p class="bodytext">Samīpaṃ <a name="M0.0193"></a> nagarassa upanagaraṃ. ‘‘Aṃvibhattīnamakārantabyayībhāvā’’ti vibhattīnaṃ kvaci aṃ.</p>\r\n<p class="bodytext">Kvacīti kiṃ. Upanagare.</p>\r\n<p class="bodytext">Abhāvo makkhikānaṃ nimmakkhikaṃ rasso. Anupubbo therānaṃ anutheraṃ, anatikkamma sattiṃ yathāsatti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',195,replace(replace('<p class="bodytext">Ye <a name="M0.0195"></a> ye buḍḍhā yathābuḍḍhaṃ, vicchāyaṃ.</p>\r\n<p class="bodytext">Yattako paricchedo jīvassa yāvajīvaṃ, avadhāraṇe.</p>\r\n<p class="bodytext">Ā pabbatā khettaṃ āpabbataṃ khettaṃ, mariyādāyaṃ, vajjamānā sīmā mariyādā, pabbataṃ vinātyattho.</p>\r\n<p class="bodytext">Ā jalantā sītaṃ ājalantaṃ sītaṃ, abhividhimhi, gayhamānā sīmā abhividhi, jalantena sahetyattho.</p>\r\n<p class="bodytext">Āsaddayoge ‘‘dhātunāmā’’dinā apādānavidhāneneva vākyampi siddhaṃ. Tathāññatra.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',197,replace(replace('<p class="bodytext"><a name="para231"></a><span class="paranum">231</span>. ‘‘Uttamo <a name="M0.0197"></a> vīro pavīro’’ iccādo pana pubbapadatthappadhānattābhāvābyayībhāvābhāvo kammadhārayoeva. Evaṃ visiṭṭho dhammo abhidhammo. Kucchitaṃ annaṃ kadannaṃ. Etttha <a name="M0.0198"></a> ‘‘kada kussā’’ti sare kussa kadādeso.</p>','\r',char(13)),'\n',char(10)),'-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',198,replace(replace('<p class="bodytext">Etttha <a name="M0.0198"></a> ‘‘kada kussā’’ti sare kussa kadādeso.</p>\r\n<p class="bodytext">Appakaṃ lavaṇaṃ kālavaṇaṃ, ettha ‘‘kāppatthesu cā’’ti kussa kā, bahuvacanenāññatrāpi kvaci. Kucchito puriso kāpuriso, kupuriso vā, evamasurādi.</p>\r\n<p class="gatha1">Pubbaparūbhayamaññapadattha - ppadhānābyayībhāva samāso;</p>\r\n<p class="gathalast">Kammadhārayaka tappurisā dve, dvendo ca bahubbīhi ca ñeyyā.</p>\r\n<p class="centered">Samāso.</p>\r\n<p class="chapter">4. Taddhitakaṇḍa</p>\r\n<p class="title">Apaccataddhita</p>\r\n<p class="bodytext"><a name="para232"></a><span class="paranum">232</span>. <span class="bld">Vāṇapacce.</span></p>','\r',char(13)),'\n',char(10)),'-232-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',200,replace(replace('<p class="bodytext">Chaṭṭhantā <a name="M0.0200"></a> saddā ‘‘tassāpacca’’miccasmiṃ atthe ṇo vā hoti. Vāti vākyatthaṃ. Ṇenevāpaccatthassa vuttattā apaccasaddāppayogo.</p>\r\n<p class="bodytext">‘‘Tesaṃ vibhatyā’’ do tesaṃgahaṇena vibhattilopo. Tathottaratra.</p>\r\n<p class="bodytext">‘‘Tesaṃ ṇo lopaṃ’’ti paccayānaṃ ṇassa lopo.</p>\r\n<p class="bodytext">‘‘Vuddhādisarassa vā saṃyogantassa saṇe ce’’ti saṇakāre pare asaṃyogantassādisarassa vuddhi.</p>\r\n<p class="bodytext">Tassāpaniyame –</p>\r\n<p class="bodytext"><span class="bld">Ayuvaṇṇānañcāyo vuddhi</span>.</p>\r\n<p class="bodytext">Akārivaṇṇuvaṇṇānaṃ āeovuddhiyo honti, casaddena kvaci na.</p>\r\n<p class="bodytext">Saralopādi, taddhitattā nāmamiva kate syādi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',202,replace(replace('<p class="bodytext">Taddhitābhidheyyaliṅga–vibhattivacanā <a name="M0.0202"></a> siyuṃ.</p>\r\n<p class="bodytext">Samūhabhāvajā bhīyo, sakatthe ṇyo napuṃsake.</p>\r\n<p class="bodytext">Tā tutthiyaṃ nipātā te, dhāmithaṃpaccayantakā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',203,replace(replace('<p class="bodytext">Vasiṭṭhassāpaccaṃ <a name="M0.0203"></a> poso vāsiṭṭho, itthī vāsiṭṭhī, napuṃsakaṃ vāsiṭṭhaṃ. Vikappavidhānato taddhitena samāsassāccantaṃ bādhāyā bhāvā vasiṭṭhā paccantipi hoti.</p>\r\n<p class="bodytext">Napuṃsakena vāpīti, saddasatthavidū viduṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',205,replace(replace('<p class="bodytext"><a name="para233"></a><span class="paranum">233</span>. Vā <a name="M0.0205"></a> apacceti cādhikāro.</p>\r\n<p class="bodytext"><span class="bld">Ṇāyana ṇāna vacchādito</span>.</p>\r\n<p class="bodytext">Vacchādito gottagaṇato ṇāyano ṇāno ca vā hoti.</p>\r\n<p class="bodytext">Apaccaṃ paputtappabhuti gottaṃ. Kaccassāpaccaṃ kaccāyano, kaccāno vā. Saṃyogantattā na vuddhi.</p>','\r',char(13)),'\n',char(10)),'-233-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',206,replace(replace('<p class="bodytext"><a name="para234"></a><span class="paranum">234</span>. ‘‘Ṇeyyo <a name="M0.0206"></a> kattikādīhī’’ti ṇeyyo, vinatāya apaccaṃ venateyyo vinateyyo vā. Na pakkhe vuddhi, ṇeyyoti yogavibhāgena ‘‘tassa dīyate’’ tyatthepi ṇeyyo, dakkhiṇā dīyate yassa so dakkhiṇeyyo.</p>\r\n<p class="bodytext"><a name="para235"></a><span class="paranum">235</span>. <span class="bld">Ato ṇi vā.</span></p>','\r',char(13)),'\n',char(10)),'-234-235-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',207,replace(replace('<p class="bodytext">Akārantato <a name="M0.0207"></a> apacce ṇi vā hoti, puna vāsaddena ṇiko, akārantā anakārantā ca bopi.</p>\r\n<p class="bodytext">Dakkhi, sakyaputtiko, maṇḍabbo, bhātubbo. Dvittaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',208,replace(replace('<p class="bodytext"><a name="para236"></a><span class="paranum">236</span>. ‘‘Ṇavo <a name="M0.0208"></a> pagvādīhī’’ ti ṇavo. Manuno apaccaṃ māṇavo.</p>','\r',char(13)),'\n',char(10)),'-236-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',209,replace(replace('<p class="bodytext"><a name="para237"></a><span class="paranum">237</span>. ‘‘Ṇera <a name="M0.0209"></a> vidhavādito’’ti ṇero, sāmaṇero.</p>','\r',char(13)),'\n',char(10)),'-237-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',210,replace(replace('<p class="title">Saṃsaṭṭhādianekatthataddhita</p>\r\n<p class="bodytext"><a name="para238"></a><span class="paranum">238</span>. ‘‘Yena <a name="M0.0210"></a> vā saṃsaṭṭhaṃ tarati carati vahati ṇiko’’ti ṇiko. Vākārena nekatthenekapaccayā ca. Ghatena saṃsaṭṭho ghātiko, odano. Uḷūpena taratīti oḷūpiko, uḷūpiko vā, na pakkhe vuddhi.</p>\r\n<p class="bodytext">Sakaṭena caratīti sākaṭiko. Sīsena vahatīti sīsiko, na vuddhi.</p>\r\n<p class="bodytext">Itthiliṅgato eyyako, ṇako ca. Campāyaṃ jāto campeyyako. Evaṃ bārāṇaseyyako. Ṇako – kusinārāyaṃ vasatīti kosinārako. Janapadato ṇako ca – magadhesu vasati, tesaṃ issaro vā māgadhako.</p>','\r',char(13)),'\n',char(10)),'-238-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',212,replace(replace('<p class="bodytext">Tajjātiyā <a name="M0.0212"></a> visiṭṭhatthe ājānīyo. Assajātiyā visiṭṭho assājānīyo. Ño - agganti jānitabbaṃ aggaññaṃ, dvittaṃ.</p>\r\n<p class="bodytext"><a name="para239"></a><span class="paranum">239</span>. <span class="bld">Tamadhīte tena katādisannidhānaniyogasippabhaṇḍa jīvikatthesu ca.</span></p>','\r',char(13)),'\n',char(10)),'-239-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',213,replace(replace('<p class="bodytext">Taṃ <a name="M0.0213"></a> adhīte iccādīsvatthesu ādisaddena hatādīsu ca ṇiko vā hoti. Abhidhammamadhīteti ābhidhammiko, abhidhammiko vā, na pakkhe vuddhi. Vacasā kataṃ kammaṃ vācasikaṃ. Evaṃ mānasikaṃ, ettha –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',214,replace(replace('<p class="bodytext">‘‘Sa <a name="M0.0214"></a> sare vāgamo’’tīhānuvattitādisaddena sāgamo.</p>\r\n<p class="bodytext">Sarīre sannidhānā vedanā sārīrikā. Dvāre niyutto dovāriko, ettha- ‘‘māyūnamāgamo ṭhāne’’ti vakārato pubbe okārāgamo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',215,replace(replace('<p class="bodytext"><span class="bld">Sippa</span>nti <a name="M0.0215"></a> gītādikalā, vīṇā assa sippanti veṇiko, atra vīṇeti vīṇāvādanaṃ. Gandho assa bhaṇḍanti gandhiko, mage hantvā jīvatīti māgaviko, vakārāgamo. Jālena hato jāliko, suttena baddho suttiko, cāpo assa āyudhanti cāpiko, vāto assa ābādho atthīti vā vātiko, buddhe pasanno buddhiko, vatthena kītaṃ bhaṇḍaṃ vatthikaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',217,replace(replace('<p class="bodytext">Kumbho <a name="M0.0217"></a> assa parimāṇaṃ, ta marahati, tesaṃ rāsi vā kumbhiko. Akkhena dibbatīti akkhiko, magadhesu vasati, jātoti vā māgadhiko iccādi.</p>\r\n<p class="bodytext"><a name="para240"></a><span class="paranum">240</span>. <span class="bld">Ṇa rāgā tena rattaṃ tassedamaññatthesu ca.</span></p>','\r',char(13)),'\n',char(10)),'-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',218,replace(replace('<p class="bodytext">Tena <a name="M0.0218"></a> rattaṃ tyādyatthesu ṇo vā hoti. Kasāvena rattaṃ kāsāvaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',219,replace(replace('<p class="bodytext">Evaṃ <a name="M0.0219"></a> nīlaṃ pītamiccādi. Na vuddhi, mahisassa idaṃ māhisaṃ, siṅgaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',220,replace(replace('<p class="bodytext">Evaṃ <a name="M0.0220"></a> rājaporisaṃ, ettha ‘‘ayuvaṇṇānañcā’’ do puna vuddhiggahaṇena uttarapadassa vuddhi. Magadhehi āgato, tatra jāto, tesaṃ issaro, te assa nivāsoti vā māgadho, kattikādīhi yutto kattiko, māso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',222,replace(replace('<p class="bodytext">Buddho <a name="M0.0222"></a> assa devatāti buddho. Byākaraṇaṃ avecca adhīteti veyyākaraṇo. Ettha ‘‘māyūnamā’’dinā yakārato pubbe e āgamo, yassa dvittaṃ. Sagarehi nibbatto sāgaroiccādi.</p>\r\n<p class="bodytext"><a name="para241"></a><span class="paranum">241</span>. <span class="bld">Jātādīnamimiyā ca.</span></p>','\r',char(13)),'\n',char(10)),'-241-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',224,replace(replace('<p class="bodytext">Jātādīsu <a name="M0.0224"></a> imo iyo ca hoti, casaddena kiyo ca. Pacchā jāto pacchimo, manussajātiyā jāto manussajātiyo. Ante niyutto antimo, antiyo.</p>\r\n<p class="bodytext">Evaṃ andhakiyo. Putto assa atthīti puttimo, puttiyo. Evaṃ kappiyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',225,replace(replace('<p class="bodytext"><a name="para242"></a><span class="paranum">242</span>. ‘‘Tadassaṭṭhānamīyo <a name="M0.0225"></a> ce’’ti īyo, cakārena hitādyatthepi, bandhanassa ṭhānaṃ bandhanīyaṃ, caṅkamanassa hitaṃ caṅkamanīyaṃ.</p>','\r',char(13)),'\n',char(10)),'-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',226,replace(replace('<p class="bodytext"><a name="para243"></a><span class="paranum">243</span>. ‘‘Ālu <a name="M0.0226"></a> tabbahule’’ti ālu. Abhijjhābahulo abhijjhālu.</p>\r\n<p class="title">Visesataddhita</p>\r\n<p class="bodytext"><a name="para244"></a><span class="paranum">244</span>. <span class="bld">Visese taratamissikiyiṭṭhā.</span></p>\r\n<p class="bodytext">Atisayatthe tarādayo honti.</p>','\r',char(13)),'\n',char(10)),'-243-244-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',227,replace(replace('<p class="bodytext">Ayametesaṃ <a name="M0.0227"></a> atisayena pāpoti pāpataro, pāpatamo, pāpissiko, pāpiyo, pāpiṭṭho vā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',228,replace(replace('<p class="bodytext">‘‘Vuddhassa <a name="M0.0228"></a> jo iyiṭṭhesū’’ti vuddhassa jādese – ‘‘saralopā’’do pakatiggahaṇena pakatyabhāvā issa e. Jeyyo, jeṭṭho.</p>\r\n<p class="bodytext">Evaṃ ‘‘pasatthassa so ce’’ti sādese seyyo, seṭṭho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',230,replace(replace('<p class="title">Assatthitaddhita</p>\r\n<p class="bodytext"><a name="para245"></a><span class="paranum">245</span>. ‘‘Tadassatthīti <a name="M0.0230"></a> vī ce’’ti vī. Medhā assa atthīti medhāvī.</p>','\r',char(13)),'\n',char(10)),'-245-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',231,replace(replace('<p class="bodytext"><a name="para246"></a><span class="paranum">246</span>. Evaṃ <a name="M0.0231"></a> ‘‘tapādito sī’’ti sī, dvittaṃ, tapassī.</p>','\r',char(13)),'\n',char(10)),'-246-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',232,replace(replace('<p class="bodytext"><a name="para247"></a><span class="paranum">247</span>. ‘‘Daṇḍādito <a name="M0.0232"></a> ika ī’’ti iko, ī ca. Daṇḍiko, daṇḍī.</p>\r\n<p class="bodytext"><a name="para248"></a><span class="paranum">248</span>. ‘‘Guṇādito vantū’’ti vantu. Guṇavā, paññavā. Yadādinā rasso.</p>','\r',char(13)),'\n',char(10)),'-247-248-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',233,replace(replace('<p class="bodytext"><a name="para249"></a><span class="paranum">249</span>. ‘‘Satyādīhi <a name="M0.0233"></a> mantū’’ti mantu. Satimā, bhānumā.</p>','\r',char(13)),'\n',char(10)),'-249-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',235,replace(replace('<p class="bodytext"><a name="para250"></a><span class="paranum">250</span>. ‘‘Āyussukārāsmantumhī’’ti <a name="M0.0235"></a> ussa asa. Āyasmā.</p>\r\n<p class="bodytext"><a name="para251"></a><span class="paranum">251</span>. ‘‘Saddhādito ṇa’’ iti ṇo. Saddho.</p>','\r',char(13)),'\n',char(10)),'-250-251-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',236,replace(replace('<p class="bodytext"><a name="para252"></a><span class="paranum">252</span>. ‘‘Tappakativacane <a name="M0.0236"></a> mayo’’ti mayo. Suvaṇṇena pakataṃ sovaṇṇamayaṃ, suvaṇṇamayaṃ vā. Pakkhe - yadādinā vuddhi.</p>\r\n<p class="bodytext"><span class="bld">Etesamo lope</span>.</p>\r\n<p class="bodytext">Vibhattilope manādīnamantassa o hoti. Manomayaṃ.</p>','\r',char(13)),'\n',char(10)),'-252-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',237,replace(replace('<p class="title">Saṅkhyātaddhita</p>\r\n<p class="bodytext"><a name="para253"></a><span class="paranum">253</span>. Saṅkhyāpūraṇe <a name="M0.0237"></a> tyadhikāro.</p>\r\n<p class="bodytext">‘‘Dvitīhi tiyo’’ti tiyo, ‘‘tiye dutāpi ce’’ti dvitīnaṃ dutā. Dvinnaṃ pūraṇo dutiyo, evaṃ tatiyo.</p>\r\n<p class="bodytext"><a name="para254"></a><span class="paranum">254</span>. ‘‘Catucchehi thaṭhā’’ti thaṭhā. Catuttho, chaṭṭho.</p>\r\n<p class="bodytext"><a name="para255"></a><span class="paranum">255</span>. <span class="bld">Tesamaḍḍhūpapadena aḍḍhuḍḍha divaḍḍha diyaḍḍhāḍḍhatiyā.</span></p>','\r',char(13)),'\n',char(10)),'-253-254-255-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',238,replace(replace('<p class="bodytext">Catuttha <a name="M0.0238"></a> dutiya tatiyānaṃ aḍḍhūpapadena saha aḍḍhuḍḍha divaḍḍha diyaḍḍhāḍḍhatiyā honti.</p>\r\n<p class="bodytext">Aḍḍhena catuttho aḍḍhuḍḍho, aḍḍhena dutiyo divaḍḍho, diyaḍḍho vā, aḍḍhena tatiyo aḍḍhatiyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',239,replace(replace('<p class="bodytext"><a name="para256"></a><span class="paranum">256</span>. ‘‘Saṅkhyāpūraṇe <a name="M0.0239"></a> mo’’ti mo, pañcamo. Itthiyaṃ pañcannaṃ pūraṇī pañcamī.</p>\r\n<p class="bodytext">Eko ca dasa cāti dvande kate –</p>\r\n<p class="bodytext"><span class="bld">Dvekaṭṭhānamākāro vā</span>.</p>\r\n<p class="bodytext">Saṅkhyāne uttarapade dviekaaṭṭhaiccetesa mantassa ā vā hoti. Ekādasa pañcīva. Evaṃ dvādasa.</p>\r\n<p class="bodytext">Yadādinā tissa teādese ‘‘ekādito dassa ra saṅkhyāne’’ti dasasadde dassa ro. Terasa.</p>','\r',char(13)),'\n',char(10)),'-256-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',241,replace(replace('<p class="bodytext"><a name="para257"></a><span class="paranum">257</span>. ‘‘Catūpapadassa <a name="M0.0241"></a> lopo tuttarapadādicassa cucopi navā’’ti catusadde tussa lopo cassa cu ca. Cuddasa.</p>\r\n<p class="bodytext">‘‘Dase so niccañce’’ti chassa soādese – ‘‘ḷa darānaṃ’’ti dasasadde dassa ḷo. Soḷasa, aṭṭhārasa.</p>','\r',char(13)),'\n',char(10)),'-257-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',242,replace(replace('<p class="bodytext"><a name="para258"></a><span class="paranum">258</span>. ‘‘Vīsati <a name="M0.0242"></a> dasesu bā dvissa tū’’ti dvissa bā. Bāvīsati, ekādasannaṃ pūraṇo ekādasamo.</p>\r\n<p class="bodytext"><a name="para259"></a><span class="paranum">259</span>. ‘‘Ekādito dasassī’’ti itthiyaṃ ī. Ekādasī iccādi.</p>\r\n<p class="bodytext">‘‘Dvādito konekatthe ce’’ti ko, dve parimāṇāni asseti dvikaṃ. Evaṃ tikādi.</p>','\r',char(13)),'\n',char(10)),'-258-259-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',243,replace(replace('<p class="bodytext"><a name="para260"></a><span class="paranum">260</span>. ‘‘Samūhatthe <a name="M0.0243"></a> kaṇṇā’’ti kaṇa ca, ṇo ca. Manussānaṃ samūho mānussako, mānusso vā.</p>\r\n<p class="bodytext">Ṇe kate – ‘‘jhalānamiyuvā sare vā’’ tīha vākārena issa ayādese – dvayaṃ, tayaṃ. Evaṃ ‘‘gāmajanabandhusahāyādīhi tā’’ti tā. Gāmatā, nāgaratā.</p>\r\n<p class="title">Bhāvataddhita</p>\r\n<p class="bodytext"><a name="para261"></a><span class="paranum">261</span>. <span class="bld">Ṇyattatā bhāve tu.</span></p>\r\n<p class="bodytext">Bhāvatthe ṇyattatā honti. Tusaddena ttano ca. Sakatthādīsupi ṇyo, sakatthe tā ca.</p>\r\n<p class="hangnum"><a name="para262"></a><span class="paranum">262</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-260-261-262-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',245,replace(replace('<p class="gatha1">Hontyasmā <a name="M0.0245"></a> saddañāṇāni,</p>\r\n<p class="gatha2">Bhāvo sā saddavuttiyā;</p>\r\n<p class="gatha3">Nimittabhūtaṃ nāmañca,</p>\r\n<p class="gathalast">Jāti dabbaṃ kriyā guṇo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',246,replace(replace('<p class="bodytext"><a name="para263"></a><span class="paranum">263</span>. Yathā <a name="M0.0246"></a> – candassa bhāvo candattaṃ. Iha nāmavasā candasaddo candaddabbe vattate, nimittassa rūpānugatañca ñāṇaṃ. Evaṃ manussattanti manussajātivasā. Yadādinā īssa rasse – daṇḍittanti daṇḍaddabbasambandhā. Pācakattanti pacanakriyāsambandhā. Nīlattanti nīlaguṇavasā.</p>\r\n<p class="bodytext">Evaṃ ṇyādīsupi yathāyogaṃ ñeyyaṃ. Ṇyo.</p>\r\n<p class="bodytext"><span class="bld">Avaṇṇo ye lopañca</span>.</p>','\r',char(13)),'\n',char(10)),'-263-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',247,replace(replace('<p class="bodytext">Ye <a name="M0.0247"></a> pare avaṇṇo lupyate, cakārena ikāropi.</p>\r\n<p class="bodytext">Yavataṃ talaṇadakārānaṃ byañjanāni calaña jakāratta’’nti yakārayuttānaṃ tādīnaṃ cādayo, kāraggahaṇena sakapabhamādito parayakārassa pubbena saha kvaci pubbarūpañca, dvittaṃ. Paṇḍiccaṃ, kosallaṃ, sāmaññaṃ, sohajjaṃ, porissaṃ, nepakkaṃ, sāruppaṃ, osabbhaṃ, opammaṃ.</p>\r\n<p class="bodytext"><span class="bld">Āttañca</span>.</p>\r\n<p class="bodytext">Iuiccetesaṃ ā hoti, rikārāgamo ca ṭhāne.</p>\r\n<p class="bodytext">Saralopādinā ilopo. Isino bhāvo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',248,replace(replace('<p class="bodytext">Ārissaṃ<a name="M0.0248"></a>. Evaṃ mudutā, arahatā, ntassa yadādinā lopo.</p>\r\n<p class="bodytext">Puthujjanattanaṃ, akiñcanameva ākiñcaññaṃ, kuṇḍaniyā apaccaṃ koṇḍañño, ettha vuddhādo vākārena saṃyogantassāpi vuddhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',250,replace(replace('<p class="bodytext">Padāya <a name="M0.0250"></a> hitaṃ pajjaṃ, dhanāyaṃ saṃvattanikaṃ dhaññaṃ, satito sambhūtaṃ saccaṃ, ilopo, tīsu na vuddhi. Devo eva devatā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',251,replace(replace('<p class="bodytext"><a name="para264"></a><span class="paranum">264</span>. ‘‘Ṇa <a name="M0.0251"></a> visamādīhī’’ti bhāve ṇo. Vesamaṃ. Ujuno bhāvo ajjavaṃ. Ettha ussa ātte parūkārassa yadādinā avo.</p>','\r',char(13)),'\n',char(10)),'-264-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',252,replace(replace('<p class="bodytext"><a name="para265"></a><span class="paranum">265</span>. ‘‘Ramaṇīyādito <a name="M0.0252"></a> kaṇti kaṇa. Mānaññakaṃ.</p>','\r',char(13)),'\n',char(10)),'-265-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',254,replace(replace('<p class="title">Abyayataddhita</p>\r\n<p class="bodytext"><a name="para266"></a><span class="paranum">266</span>. ‘‘Vibhāge <a name="M0.0254"></a> dhā ce’’ti dhā, cakārena soppaccayo ca. Ekena vibhāgena ekadhā, nipātattā silopo. Padavibhāgena padaso.</p>\r\n<p class="bodytext"><a name="para267"></a><span class="paranum">267</span>. ‘‘Sabbanāmehi pakāravacane tu thā’’ti thā. Tukārena thattā ca. Sabbo pakāro, sabbena pakārena vā sabbathā. Evaṃ aññathattā.</p>','\r',char(13)),'\n',char(10)),'-266-267-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',255,replace(replace('<p class="bodytext"><a name="para268"></a><span class="paranum">268</span>. ‘‘Kimimehi <a name="M0.0255"></a> tha’’nti thaṃ, kādese-kathaṃ. Iādese-itthaṃ, thanti yogavibhāgena thaṃ-bahutthaṃ.</p>\r\n<p class="bodytext"><a name="para269"></a><span class="paranum">269</span>. Amalinaṃ malinaṃ karotītyādyatthe-abhūtatabbhāve gamyamāne karabhūyoge sati nāmato yadādinā īppaccayo, malinīkaroti setaṃ. Abhasmano bhasmano karaṇanti bhasmīkaraṇaṃ kaṭṭhassa. Amalino malino bhavatīti malinībhavati seto. Īppaccayantopi nipāto. Abhūtatabbhāveti kiṃ, ghaṭaṃ karoti, ghaṭo bhavati.</p>','\r',char(13)),'\n',char(10)),'-268-269-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_84',256,replace(replace('<p class="bodytext">Karabhūyogeti <a name="M0.0256"></a> kiṃ, amalino malino jāyate.</p>\r\n<p class="bodytext">Avatthāvatovatthayā, bhūtassaññāya vatthuno.</p>\r\n<p class="bodytext">Tāyāvatthāya bhavanaṃ, abhūtatabbhavaṃ viduṃ.</p>\r\n<p class="bodytext">Taddhito.</p>','\r',char(13)),'\n',char(10)),'');