INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="nikaya">Suttantapiṭaka</p>\r\n<p class="book">Saṃyuttanikāye</p>\r\n<p class="chapter">Sagāthāvaggapāḷi</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',1,replace(replace('<p class="bodytext">paṭhamamahāsaṃgītikāle <a name="M1.0001"></a> āvuso dhammasaṃgāhakā mahākassapādayo mahātheravarā porāṇasaṃgītikārā paṭhamaṃ vinayapiṭakaṃ saṃgāyitvā tadanantaraṃ suttantapiṭakaṃ saṃgāyantā dīghanikāyañca majjhimanikāyañca saṃgāyitvā tadanantaraṃ kiṃnāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamamahāsaṃgītikāle bhante dhammasaṃgāhakā mahākassapādayo porāṇatheravarā paṭhamaṃ vinayapiṭakaṃ saṃgāyitvā tadanantaraṃ suttantapiṭakaṃ saṃgāyantā dīghanikāyañca majjhimanikāyañca saṃgāyitvā tadanantaraṃ sattahi ca suttasahassehi sattahi ca suttasatehi dvāsaṭṭhiyā ca suttehi paṭimaṇḍitaṃ bhāṇavārasataparimāṇaṃ saṃyuttanikāyaṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',2,replace(replace('<p class="bodytext">saṃyuttanikāye <a name="M1.0002"></a> ca āvuso sagāthāvaggo nidānavaggo khandhavaggo saḷāyatanavaggo mahāvaggoti pañcasaṃyuttappakaraṇāni, tesu paṭhamaṃ kataraṃ saṃyuttappakaraṇaṃ te saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcasu bhante saṃyuttappakaraṇesu paṭhamaṃ sagāthāvaggasaṃyuttappakaraṇaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',3,replace(replace('<p class="bodytext">sagāthāvaggepi <a name="M1.0003"></a> āvuso devatāsaṃyuttādivasena ekādasasaṃyuttāni, tesu paṭhamaṃ kataraṃ saṃyuttaṃ te saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekādasasu bhante saṃyuttesu paṭhamaṃ devatāsaṃyuttaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> devatāsaṃyuttepi āvuso naḷavaggādayo aṭṭha vaggā, oghataraṇasuttādīni ca ekāsīti suttāni, tesu paṭhamaṃ kataraṃ vaggaṃ katarañca suttaṃ sagāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhasu bhante vaggesu paṭhamaṃ naḷavaggaṃ ekāsītiyā ca suttesu paṭhamaṃ oghataraṇasuttaṃ saṃgāyiṃsu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',4,replace(replace('<p class="bodytext">Sādhu <a name="M1.0004"></a> sādhu āvuso, mayampi dāni tatoyeva paṭṭhāya saṃgītipubbaṅgamāni pucchanavissajjanakiccāni āvahituṃ samārabhāra.</p>\r\n<p class="title">Devatāsaṃyutta</p>\r\n<p class="subhead">Oghataraṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā jānatā passatā arahatā sammāsambuddhena oghataraṇasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devataṃ ārabbha bhāsitaṃ, aññatarā bhante devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṃ jetavanaṃ obhāsetvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsi, ekamantaṃ ṭhitā kho sā devatā bhagavantaṃ etadavoca ‘‘kathaṃ nu tvaṃ mārisa oghamatarī’’ti,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',5,replace(replace('<p class="bodytext">Tasmiṃ <a name="M1.0005"></a> bhante vatthusmiṃ ‘‘apatiṭṭhaṃ khvāhaṃ āvuso anāyūhaṃ oghamatariṃ’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Accentisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',8,replace(replace('<p class="bodytext">accentisuttaṃ <a name="M1.0008"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devataṃ ārabbha bhāsitaṃ. Aññatarā bhante devatā bhagavantaṃ etadavoca–</p>\r\n<p class="gatha1">‘‘Accenti kālā tarayanti rattiyo,</p>\r\n<p class="gatha2">Vayoguṇā anupubbaṃ jahanti,</p>\r\n<p class="gatha3">Etaṃ bhayaṃ maraṇe pekkhamāno,</p>\r\n<p class="gathalast">Puññāni kayirātha sukhāvahānī’’ti–</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ –</p>\r\n<p class="gatha1">‘‘Accenti kālā tarayanti rattiyo,</p>\r\n<p class="gatha2">Vayoguṇā anupubbaṃ jahanti,</p>\r\n<p class="gatha3">Etaṃ bhayaṃ maraṇe pekkhamāno,</p>\r\n<p class="gathalast">Lokāmisaṃ pajahe santipekkho’’ti –</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sattisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',10,replace(replace('<p class="bodytext">sattisuttaṃ <a name="M1.0010"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devataṃ ārabbha bhāsitaṃ, aññatarā bhante devatā bhagavantaṃ etadavoca–</p>\r\n<p class="gatha1">‘‘Sattiyā viya omaṭṭho, ḍayhamānova matthake;</p>\r\n<p class="gathalast">Kāmarāgappahānāya, sato bhikkhu paribbaje’’ti–</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',11,replace(replace('<p class="gatha1">Sattiyā <a name="M1.0011"></a> viya omaṭṭho ḍayhamānova matthake;</p>\r\n<p class="gathalast">Sakkāyadiṭṭhippahānāya, sato bhikkhu paribbajeti;</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="gatha1">Sattiyā viya omaṭṭho, ḍayhamānova matthake;</p>\r\n<p class="gathalast">Kāmarāgappahānāya, sato bhikkhu paribbaje –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',12,replace(replace('<p class="gatha1">Sattiyā <a name="M1.0012"></a> viya omaṭṭho, ḍayhamānova matthake,</p>\r\n<p class="gathalast">Sakkāyadiṭṭhippamānāya, sato bhikkhu paribbaje –</p>\r\n<p class="subhead">Jaṭāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> jaṭāsuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devataṃ ārabbha bhāsitaṃ, aññatarā bhante devatā bhavagantaṃ etadavoca–</p>\r\n<p class="gatha1">‘‘Anto jaṭā bahijaṭā, jaṭāya jaṭitā pajā;</p>\r\n<p class="gathalast">Taṃ taṃ gotama pucchāmi, ko imaṃ vijaṭaye jaṭa’’nti–</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ –</p>\r\n<p class="gatha1">Sīle patiṭṭhāya naro sapañño, cittaṃ paññañca bhāvayaṃ;</p>\r\n<p class="gathalast">Ātāpī nipako bhikkhu, so imaṃ vijaṭaye jaṭaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',13,replace(replace('<p class="gatha1">Yesaṃ <a name="M1.0013"></a> rāgo ca doso ca, avijjā ca virājitā;</p>\r\n<p class="gathalast">Khīṇāsavā arahanto, tesaṃ vijaṭitā jaṭāti;</p>\r\n<p class="bodytext">Evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',14,replace(replace('<p class="gatha1">Sīle <a name="M1.0014"></a> patiṭṭhāya naro sapañño, cittaṃ paññañca bhāvayaṃ;</p>\r\n<p class="gathalast">Ātāpī nipako bhikkhu, so imaṃ vijaṭaye jaṭaṃ–</p>\r\n<p class="gatha1">Yesaṃ rāgo ca doso ca, avijjā ca virājitā;</p>\r\n<p class="gathalast">Khīṇāsavā arahanto, tesaṃ vijaṭitā jaṭā–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',15,replace(replace('<p class="gatha1">Yattha <a name="M1.0015"></a> nāmañca rūpañca, asesaṃ uparujjhati;</p>\r\n<p class="gathalast">Paṭighaṃ rūpasaññā ca, etthesā chijjate jaṭā.</p>\r\n<p class="subhead">Accharāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> accharāsuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devaputtaṃ ārabbha bhāsitaṃ. Aññataro bhante devaputto bhagavantaṃ etadavoca–</p>\r\n<p class="gatha1">‘‘Accharāgaṇasaṅghaṭṭhaṃ, pisāca gaṇasevitaṃ;</p>\r\n<p class="gathalast">Vanantaṃ mohanaṃ nāma, kathaṃ yātrā bhavissatī’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Ujuko nāma so maggo, abhayā nāma sā disā,</p>\r\n<p class="gathalast">Ratho akūjano nāma, dhammacakkehi saṃyuto.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',16,replace(replace('<p class="gatha1">Hirī <a name="M1.0016"></a> tassa apālambo, satyassa parivāraṇaṃ;</p>\r\n<p class="gathalast">Dhammāhaṃ sārathiṃ brūmi, sammādiṭṭhi purejavaṃ.</p>\r\n<p class="gatha1">Yassa etādisaṃ yānaṃ, itthiyā purisassa vā;</p>\r\n<p class="gathalast">Sa ve etena yānena, nibbānasseva santike’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',17,replace(replace('<p class="gatha1">Accharāgaṇasaṅghuṭṭhaṃ<a name="M1.0017"></a>, pisācagaṇasevitaṃ;</p>\r\n<p class="gathalast">Vanantaṃ mohanaṃ nāma, kathaṃ yātrā bhavissati–</p>\r\n<p class="subhead">Jarāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',19,replace(replace('<p class="bodytext">jarāsuttaṃ <a name="M1.0019"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ devataṃ ārabbha bhāsitaṃ. Aññatarā bhante devatā bhagavantaṃ etadavoca–</p>\r\n<p class="gatha1">‘‘Kiṃ su yāva jarā sādhu, kiṃ su sādhu patiṭṭhitaṃ;</p>\r\n<p class="gathalast">Kiṃ su narānaṃ ratanaṃ, kiṃ su corehi dūhara’’nti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Sīlaṃ yāva jarā sādhu, saddhā sādhu patiṭṭhitā;</p>\r\n<p class="gathalast">Paññā narānaṃ ratanaṃ, puññaṃ corehi dūhara’’nti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Kiṃ su yāva jarā sādhu.</p>\r\n<p class="bodytext">Kiṃ su sādhu patiṭṭhitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',20,replace(replace('<p class="bodytext">Kiṃ <a name="M1.0020"></a> su narānaṃ ratanaṃ.</p>\r\n<p class="bodytext">Kiṃ su corehi dūharaṃ.</p>\r\n<p class="title">Devaputtasaṃyutta</p>\r\n<p class="subhead">Subrahmasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā jānatā passatā arahatā sammāsambuddhena subrahmasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ katañca bhāsitaṃ. Kīdiso ca tattha dhammapaṭiggāhakassa dhammassavanānisaṃso adhigato.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',21,replace(replace('<p class="bodytext">rājagahe <a name="M1.0021"></a> bhante subrahmadevaputtaṃ ārabbha bhāsitaṃ. Subrahmā bhante devaputto maraṇabhayabhīto bhagavantaṃ upasaṅkamitvā etadavoca–</p>\r\n<p class="gatha1">‘‘Niccaṃ utrastamidaṃ cittaṃ, niccaṃ ubbiggamidaṃ mano;</p>\r\n<p class="gatha2">Anuppannesu kicchesu, atho uppatitesu ca;</p>\r\n<p class="gathalast">Sace atthi anutrastaṃ, taṃ me akkhāhi pucchito’’ti–</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ.</p>\r\n<p class="gatha1">‘‘Nāññatra bojjā tapasā, nāññatrindriyasaṃvarā;</p>\r\n<p class="gathalast">Nāññatra sabbanissaggā, sotthiṃ passāmi pāṇina’’nti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ. Desanāpariyosāne ca bhante subrahmassa devaputtassa tasmiṃyeva āsane virajaṃ vītamalaṃ dhammacakkhuṃ udapādi, ‘‘yaṃkiñci samudayadhammaṃ, sabbaṃ taṃ nirodhadhamma’’nti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',22,replace(replace('<p class="gatha1">Niccaṃ <a name="M1.0022"></a> utrastamidaṃ cittaṃ, niccaṃ ubbiggamidaṃ mano;</p>\r\n<p class="gatha2">Anuppannesu kicchesu, atho uppatitesu ca;</p>\r\n<p class="gathalast">Sace atthi anutrasta, taṃ me akkhāhi pucchito-hu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',23,replace(replace('<p class="gatha1">Nāññatra <a name="M1.0023"></a> bojjā tapasā, nāññatrindriyasaṃvarā;</p>\r\n<p class="gathalast">Nāññatra sabbanissaggā, sotthiṃ passāmi pāṇinaṃ–</p>\r\n<p class="subhead">Rohitassasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',24,replace(replace('<p class="bodytext">rohitassasuttaṃ <a name="M1.0024"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rohitassaṃ devaputtaṃ ārabbha bhāsitaṃ. Rohitasso bhante devaputto bhagavantaṃ etadavoca ‘‘yattha nu kho bhante na jāyati na jīyati na mīyati na cavati na upapajjati, sakkā nu kho so bhante gamanena lokassa anto ñātuṃ vā daṭṭhuṃ vā pāpuṇituṃ vā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yattha kho āvuso na jāyati na jīyati na mīyati na cavati na upapajjati, nāhaṃ taṃ gamanena lokassa antaṃ ñāteyyaṃ aṭṭheyyaṃ patteyyanti vadāmī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Kosalasaṃyutta</p>\r\n<p class="subhead">Daharasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',26,replace(replace('<p class="bodytext">daharasuttaṃ <a name="M1.0026"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃ pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavatā saddhiṃ sammodi, ekamantaṃ nisinno kho bhante rājā pasenadi kosalo bhagavantaṃ etadavoca ‘‘bhavampi no gotamo ‘anuttaraṃ sammāsambodhiṃ abhisambuddho’ti paṭijānātī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yañhi taṃ mahārāja sammā vadamānovadeyya ‘anuttaraṃ sammāsambodhiṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',27,replace(replace('<p class="bodytext">abhisambuddho’ti<a name="M1.0027"></a>, mameva taṃ sammā vadamāno vadeyyā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',29,replace(replace('<p class="gatha1">Tasmā <a name="M1.0029"></a> hi paṇḍito poso, sampassaṃ atthamattano;</p>\r\n<p class="gatha2">Bhujaṅgamaṃ pāvakañca, khattiyañca yasassinaṃ;</p>\r\n<p class="gathalast">Bhikkhuñca sīlasampannaṃ, sammadeva samācare–</p>\r\n<p class="subhead">Attarakkhitasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> attarakkhitasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃyeva pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo bhagavantaṃ upasaṅkamitvā ‘‘idha mayhaṃ bhante rahogatassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',30,replace(replace('<p class="bodytext">paṭisallīnassa <a name="M1.0030"></a> evaṃ cetaso parivitakko udapādī’’ti evamādikaṃ vacanaṃ avoca. Bhagavā ca bhante evametaṃ mahārāja evametaṃ mahārājāti evamādinā vibhajitvā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',32,replace(replace('<p class="gatha1">Kāyena <a name="M1.0032"></a> saṃvaro sādhu, sādhu vācāya saṃvaro;</p>\r\n<p class="gatha2">Manasā saṃvaro sādhu, sādhu, sabbattha saṃvaro;</p>\r\n<p class="gathalast">Sabbattha saṃvuto lajjī, rakkhitoti pavuccati.</p>\r\n<p class="subhead">Appakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> appakasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃyeva pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo bhagavantaṃ upasaṅkamitvā ‘‘idha mayhaṃ bhante rahogatassa paṭisallīnassa evaṃ cetaso parivitakko udapādi appakā te sattā lokasmiṃ, ye uḷāre uḷāre bhoge labhitvā na ceva majjanti na ca pamajjanti, na ca kāmesu gedhaṃ āpajjantī’’ti evamādikaṃ vacanaṃ avoca. Bhagavā ca bhante ‘‘evametaṃ mahārāja evametaṃ mahārājā’’ti evamādinā appakasuttaṃ bhāsitaṃ.</p>\r\n<p class="subhead">Mallikāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',34,replace(replace('<p class="bodytext">mallikāsuttaṃ <a name="M1.0034"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃyeva pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo mallikaṃ deviṃ etadavoca ‘‘atthi nu kho te mallike kocañño attanā piyataro’’ti. Tasmiṃ vatthusmiṃ sabbādisā anuparigamma cetasāti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',35,replace(replace('<p class="bodytext">Sabbā <a name="M1.0035"></a> disā anuparigamma cetasā, nevajjhagā piyataramattanā kvaci. Evaṃ piyo puthu attā paresaṃ, tasmā na hiṃse paramattakāmo–</p>\r\n<p class="subhead">Sattajaṭilasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',36,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0036"></a> bhagavatā…pe… sammāsambuddhena sattajaṭilasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃ pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo acirapakkantesu sattasu ca jaṭilesu sattasu ca nigaṇṭhesu sattasu ca acelakesu sattasu ca ekasāṭakesu sattasu ca paribbājakesu yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi, ekamantaṃ nisinno kho bhante rājā pasenadi kosalo bhagavantaṃ etadavoca ‘‘ye te bhante loke arahanto vā arahattamaggaṃ vā samāpannā, ete tesaṃ aññatarā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘dujjānaṃ kho etaṃ mahārāja tayā gihinā kāmabhoginā puttasambādhasayanaṃ ajjhāvasantena kāsiṇacandanaṃ paccanubhontena mālāgandhavilepanaṃ dhārayantena jātarūparajataṃ sādiyantena ‘‘ime vā arahanto, ime vā arahattamaggaṃ samāpannā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',40,replace(replace('<p class="gatha1">Na <a name="M1.0040"></a> vaṇṇarūpena naro sujāno, na vissase ittaradassanena;</p>\r\n<p class="gathalast">Susaññatānañhi viyañjanena, asaññatā lokamimaṃ caranti.</p>\r\n<p class="gatha1">Patirūpako mattikākuṇḍalova, lohaḍḍhamāsova suvaṇṇachanno;</p>\r\n<p class="gathalast">Caranti loke parivārachannā, anto asuddhā bahisobhamānā-hū</p>\r\n<p class="subhead">Pañcarājasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',41,replace(replace('<p class="bodytext">pañcarājasuttaṃ <a name="M1.0041"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃyeva pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo bhagavantaṃ etadavoca ‘‘kiṃ nu kho bhante kāmānaṃ agga’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘manāpapariyantaṃ khvāhaṃ mahārāja pañcasu kāmaguṇesu agganti vadāmī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',42,replace(replace('<p class="bodytext">Idha <a name="M1.0042"></a> bhante amhākaṃ pañcannaṃ rājūnaṃ pañcahi kāmaguṇehi samappitānaṃ samaṅgībhūtānaṃ paricārayamānānaṃ ayamantarākathā udapādiṃ ‘kiṃ nu kho kāmānaṃ agga’nti–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',43,replace(replace('<p class="bodytext">Padumaṃ <a name="M1.0043"></a> yathā kokanadaṃ sugandhaṃ, pāto siyā phullamavītagandhaṃ. Aṅgīrasaṃ passa virocamānaṃ, tapantamādiccamivantalikkhe–</p>\r\n<p class="subhead">Dutiyaaputtakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',44,replace(replace('<p class="bodytext">dutiyaaputtakasuttaṃ <a name="M1.0044"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante rājānaṃyeva pasenadiṃ kosalaṃ ārabbha bhāsitaṃ. Rājā bhante pasenadi kosalo bhagavantaṃ upasaṅkamitvā ‘‘idha bhante sāvatthiyaṃ seṭṭhi gahapati kālaṅkato, tamahaṃ aputtakaṃ sāpateyyaṃ rājantepuraṃ atiharitvā āgacchāmī’’ti evamādikaṃ vacanaṃ avoca. Bhagavā ca bhante ‘‘evametaṃ mahārāja, evametaṃ mahārāja, bhūtapubbaṃ so mahārāja seṭṭhi gahapati taggarasikhiṃ nāma paccekasambuddhaṃ piṇḍapātena paṭipādesī’’ti evamādinā tassa seṭṭhissa gahapatissa atītaṃ vatthuṃ āharitvā pariyosāne catūhi gāthāhi bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',47,replace(replace('<p class="gatha1">Dhaññaṃ <a name="M1.0047"></a> dhanaṃ rajataṃ jātarūpaṃ, pariggahaṃ vāpi yadatthi kiñci,</p>\r\n<p class="gathalast">Dāsā kammakarā pessā, ye cassa anujīvino.</p>\r\n<p class="gatha1">Sabbaṃ nādāya gantabbaṃ, sabbaṃ nikkhippagāminaṃ;</p>\r\n<p class="gathalast">Yañca karoti kāyena, vācāya udacetasā.</p>\r\n<p class="gatha1">Tañhi tassa sakaṃ hoti, taṃva ādāya gacchati;</p>\r\n<p class="gathalast">Taṃvassa anugaṃ hoti, chāyāva anapāyinī.</p>\r\n<p class="gatha1">Tasmā kareyya kalyāṇaṃ, nicayaṃ samparāyikaṃ;</p>\r\n<p class="gathalast">Puññāni paralokasmiṃ, patiṭṭhā honti pāṇinaṃ –</p>\r\n<p class="title">Mārasaṃyutta</p>\r\n<p class="subhead">Tapokammasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',48,replace(replace('<p class="bodytext">tapokammasuttaṃ <a name="M1.0048"></a> panāvuso bhagavatā jinena kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uruvelāyaṃ bhante māraṃ pāpimantaṃ ārabbha bhāsitaṃ. Māro bhante pāpimā bhagavantaṃ upasaṅkamitvā gāthāya ajjhabhāsi.</p>\r\n<p class="gatha1">‘‘Tapokammā apakkamma, yena sujjhanti māṇavā;</p>\r\n<p class="gathalast">Asuddho maññasi suddho, suddhimaggā aparaddho’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Anattasaṃhitaṃ ñatvā, yaṃ kiñci amaraṃ tapaṃ;</p>\r\n<p class="gatha2">Sabbaṃ natthāvahaṃ hoti, phiyārittaṃva dhammani;</p>\r\n<p class="gatha3">Sīlaṃ samādhi paññañca, maggaṃ bodhāya bhāvayaṃ;</p>\r\n<p class="gathalast">Pattosmi paramaṃ suddhiṃ, nihato tvamasi antakā’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā jinena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',49,replace(replace('<p class="gatha1">Tapokammā <a name="M1.0049"></a> apakkamma, ye na sujjhanti māṇavā;</p>\r\n<p class="gathalast">Asuddho maññasi suddho, suddhimaggā aparaddho–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',50,replace(replace('<p class="gatha1">Anatthasaṃhitaṃ <a name="M1.0050"></a> utvā, yaṃkiñci amaraṃ tapaṃ;</p>\r\n<p class="gathalast">Sabbaṃ natthāvahaṃ hoti, phiyārittaṃva dhammani;</p>\r\n<p class="subhead">Hatthirājavaṇṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',51,replace(replace('<p class="bodytext">hatthirājavaṇṇasuttaṃ <a name="M1.0051"></a> panāvuso bhavagatā jinena kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uruvelāyaṃ bhante mārakaṃyeva pāpimantaṃ ārabbha bhāsitaṃ. Māro bhante pāpimā bhagavato bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo mahantaṃ hatthirājavaṇṇaṃ abhinimminitvā yena bhagavā tenupasaṅkami.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Saṃsaraṃ dīghamaddhānaṃ, vaṇṇaṃ katvā subhāsubhaṃ;</p>\r\n<p class="gathalast">Alaṃ te tena pāpima, nihato tvamasi antakā’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā jinena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',52,replace(replace('<p class="gatha1">Saṃsaraṃ <a name="M1.0052"></a> dīghamaddhānaṃ, vaṇṇaṃ katvā subhāsubhaṃ,</p>\r\n<p class="gathalast">Alaṃ te tena pāpima, nihato tvamasi antaka –</p>\r\n<p class="title">Bhikkhunīsaṃyutta</p>\r\n<p class="subhead">Āḷavikāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> idāni āvuso bhikkhunī saṃyuttaṃ pucchāmi, yaṃ tassa bhagavato arahato sammāsambuddhassa sāvikāhi therīhi attano</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',53,replace(replace('<p class="bodytext">Ca <a name="M1.0053"></a> buddhasāsanassa ca yathābhūtaṃ guṇaṃ pakāsetvā bhāsitaṃ. Tatthāvuso paṭhamaṃ porāṇakehi mahākassapādīhi dhammasaṃgāhakehi theravarehi saṃgītaṃ āḷavikāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante māraṃ pāpimantaṃ ārabbha āḷavikāya bhikkhuniyā bhāsitaṃ. Māro bhante pāpimā āḷavikāya bhikkhuniyā bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo vivekamhā cāvetukāmo āḷavikaṃ bhikkhuniṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">‘‘Natthi nissaraṇaṃ loke, kiṃ vivekena kāhasi;</p>\r\n<p class="gathalast">Bhuñjassa kāmaratiyo, māhu pacchānutāpinī’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Atthi nissaraṇaṃ loke, paññāya me suphussitaṃ;</p>\r\n<p class="gatha2">Pamattabandhu pāpima, na tvaṃ jānāsi taṃ padaṃ;</p>\r\n<p class="gatha3">Sattisūlūpamā kāmā, khandhāsaṃ adhikuṭṭanā;</p>\r\n<p class="gathalast">Yaṃ tvaṃ kāmaratiṃ brūsi, arahi mayha sā ahū’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante āḷavikāya bhikkhuniyā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',54,replace(replace('<p class="gatha1">Natthi <a name="M1.0054"></a> nissaraṇaṃ loke, kiṃ vivekena kāhasi;</p>\r\n<p class="gathalast">Bhuñjassu kāmaratiyo, māhu pacchānutāpinī-hu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',55,replace(replace('<p class="gatha1">Atthi <a name="M1.0055"></a> nissaraṇaṃ loke, paññāya me suphussitaṃ;</p>\r\n<p class="gathalast">Pamattabandhu pāpima, na tvaṃ jānāsi taṃ padaṃ.</p>\r\n<p class="gatha1">Sattisūlūpamā kāmā, khandhāsaṃ adhikuṭṭanā;</p>\r\n<p class="gathalast">Yaṃ tvaṃ kāmaratiṃ brūsi, arati mayha sā ahu–</p>\r\n<p class="subhead">Somāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',56,replace(replace('<p class="bodytext">somāsuttaṃ <a name="M1.0056"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante andhavane māraṃ pāpimantaṃ ārabbha somāya bhikkhuniyā bhāsitaṃ. Māro bhante pāpimā somāya bhikkhuniyā bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo samādhimhā cāvetukāmo somaṃ bhikkhuniṃ gāthāya ajjhabhāsi.</p>\r\n<p class="gatha1">‘‘Yaṃ taṃ isīhi pattabbaṃ, ṭhānaṃ durabhisambhavaṃ;</p>\r\n<p class="gathalast">Na taṃ dvaṅgulapaññāya, sakkā pappotumitthiyā’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Itthibhāvo kiṃ kayirā, cittamhi susamāhite;</p>\r\n<p class="gatha2">Ñāṇamhi vattamānamhi, sammā dhammaṃ vipassato;</p>\r\n<p class="gatha3">Yassa nūna siyā evaṃ, itthāhaṃ purisoti vā;</p>\r\n<p class="gathalast">Kiñci vā pana aññasmi, taṃ māro vattumarahatī’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante somāya bhikkhuniyā ariyasāvikāya bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',57,replace(replace('<p class="gatha1">Yaṃ <a name="M1.0057"></a> taṃ isīhi pattabbaṃ, ṭhānaṃ durabhisambhavaṃ;</p>\r\n<p class="gathalast">Na taṃ dvaṅgulapaññāya, sakkā pappotumitthiyā–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',58,replace(replace('<p class="gatha1">Itthi <a name="M1.0058"></a> bhāvo kiṃ kayirā, cittamhi susamāhite;</p>\r\n<p class="gathalast">Ñāṇamhi vatta mānamhi, sammā dhammaṃ vipassato.</p>\r\n<p class="gatha1">Yassa nūna siyā evaṃ, itthāhaṃ purisoti vā;</p>\r\n<p class="gathalast">Kiñci vā pana aññasmi, taṃ māro vattumarahati.</p>\r\n<p class="subhead">Kisāgotamīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',59,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0059"></a> kisāgotamīsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante andhavane māraṃ pāpimantaṃ ārabbha kisāgotamiyā bhikkhuniyā theriyā bhāsitaṃ. Māro bhante pāpimā kisāgotamiyā bhikkhuniyā bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo samādhimhā cāvetukāmo kisāgotamiṃ bhikkhuniṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">‘‘Kiṃ nu tvaṃ mataputtāva, ekamāsi rudammukhī;</p>\r\n<p class="gathalast">Vanamajjhagatā ekā, purisaṃ nu gavesasī’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Accantaṃ mataputtāmhi, purisā ekadantikā;</p>\r\n<p class="gathalast">Na socāmi na rodāmi, na taṃ bhāyāmi āvuso.</p>\r\n<p class="gatha1">Sabbattha vihatā nandī, tamokkhandho padālito;</p>\r\n<p class="gathalast">Jetvāna maccuno senaṃ, viharāmi anāsavā’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante kisāgotamiyā bhikkhuniyā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',60,replace(replace('<p class="gatha1">Kiṃ <a name="M1.0060"></a> nu tvaṃ mataputtāva, ekamāsi rudammukhī;</p>\r\n<p class="gathalast">Vanamajjhagatā ekā, purisaṃ nu gasesasi-hu–</p>\r\n<p class="gatha1">Accantaṃ mataputtāmhi, purisā etadantikā;</p>\r\n<p class="gathalast">Na socāmi na rodāmi, na taṃ bhāyāmi āvuso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',61,replace(replace('<p class="gatha1">Sabbattha <a name="M1.0061"></a> vihatā nandī, tamokkhandho padālito;</p>\r\n<p class="gathalast">Jetvāna maccuno senaṃ, viharāmi anāsavā.</p>\r\n<p class="subhead">Uppalavaṇṇāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tatthāvuso porāṇehi saṃgītikāramahātheravarehi saṃgītaṃ uppalavaṇṇasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante andhavane māraṃ pāpimantaṃ ārabbha uppalavaṇṇāya bhikkhuniyā bhāsitaṃ. Māro bhante pāpimā uppalavaṇṇāya bhikkhuniyā bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo samādhimhā cāvetukāmo uppalavaṇṇaṃ bhikkhuniṃ gāthāya ajjhabhāsi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',62,replace(replace('<p class="bodytext">‘‘Supupphitaggaṃ <a name="M1.0062"></a> upagamma bhikkhuni, ekā tuvaṃ tiṭṭhasi sālamūle. Na catthi te dutiyā vaṇṇadhātu, bāle na tvaṃ bhāyasi dhuttakāna’’nti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Sataṃ sahassānipi dhuttakānaṃ,</p>\r\n<p class="gatha2">Idhāgatā tādisakā bhaveyyuṃ;</p>\r\n<p class="gatha3">Lomaṃ na iñjāmi na santasāmi,</p>\r\n<p class="gathalast">Na māra bhāyāmi tamekikāpi.</p>\r\n<p class="gatha1">Esā antaradhāyāmi, kucchiṃ vā pavisāmi te;</p>\r\n<p class="gathalast">Pakhumantarikāyampi, tiṭṭhantiṃ maṃ na dakkhasi.</p>\r\n<p class="gatha1">Cittasmiṃ vasībhūtāmhi, iddhipādā subhāvitā;</p>\r\n<p class="gathalast">Sabbabandhanamuttāmhi, na taṃ bhāyāmi āvuso’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante uppalavaṇṇāya bhikkhuniyā bhāsitaṃ.</p>\r\n<p class="subhead">Cālāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',64,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0064"></a> cālāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante andhavane māraṃyeva pāpimantaṃ ārabbha cālāya bhikkhuniyā ariyasāvikāya bhāsitaṃ. Māro bhante pāpimā cālaṃ bhikkhuniṃ upasaṅkamitvā etadavoca ‘‘kiṃ nu tvaṃ bhikkhuni na rocesī’’ti. Jātiṃ khvāhaṃ āvuso na rocemī’’ti.</p>\r\n<p class="gatha1">Kiṃ nu jātiṃ na rocesi, jāto kāmāni bhuñjati;</p>\r\n<p class="gathalast">Ko nu taṃ idamādapayi, jātiṃ mā roca bhikkhunīti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Jātassa maraṇaṃ hoti, jāto dukkhāni phussati;</p>\r\n<p class="gathalast">Bandhaṃ vadhaṃ pariklesaṃ, tasmā jātiṃ na rocaye.</p>\r\n<p class="gatha1">Buddho dhammamadesesi, jātiyā samatikkamaṃ;</p>\r\n<p class="gathalast">Sabbadukkhappahānāya, so maṃ sacce nivesayi.</p>\r\n<p class="gatha1">Ye ca rūpūpagā sattā, ye ca arūpaṭṭhāyino;</p>\r\n<p class="gathalast">Nirodhaṃ appajānantā, āgantāro punabbhavanti.</p>\r\n<p class="bodytext">Evaṃ kho bhante cālāya bhikkhuniyā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',65,replace(replace('<p class="gatha1">Kiṃ <a name="M1.0065"></a> nu jātiṃ na rocesi, jāto kāmāni bhuñjati;</p>\r\n<p class="gathalast">Ko nu taṃ idamādapayi, jātiṃ mā roca bhikkhunī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',66,replace(replace('<p class="gatha1">Jātassa <a name="M1.0066"></a> maraṇaṃ hoti, jāto dukkhāni phussati;</p>\r\n<p class="gathalast">Bandhaṃ vadhaṃ pariklesaṃ, tasmā jātiṃ na rocaye.</p>\r\n<p class="gatha1">Buddho dhammamadesesi, jātiyā samatikkamaṃ,</p>\r\n<p class="gathalast">Sabbadukkhappahānāya, so maṃ sacce nivesayi.</p>\r\n<p class="subhead">Selāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',67,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0067"></a> selāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante māraṃyeva pāpimantaṃ ārabbbha selāya bhikkhuniyā ariyasāvikāya bhāsitaṃ. Māro bhante pāpimā selāya bhikkhuniyā bhayaṃ chambhitattaṃ lomahaṃsaṃ uppādetukāmo samādhimhā cāvetukāmo selaṃ bhikkhuniṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">Kenidaṃ pakataṃ bimbaṃ, kvanu bimbassa kārako;</p>\r\n<p class="gathalast">Kvanu bimbaṃ samuppannaṃ, kvanu bimbaṃ nirujjhatīti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Nayidaṃ attakataṃ bimbaṃ, nayidaṃ parakataṃ aghaṃ;</p>\r\n<p class="gathalast">Hetuṃ paṭicca sambhūtaṃ, hetubhaṅgā nirujjhatīti.</p>\r\n<p class="bodytext">Evamādinā bhante selāya bhikkhuniyā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',68,replace(replace('<p class="gatha1">Kenidaṃ <a name="M1.0068"></a> pakataṃ bimbaṃ, kvanu bimbassa kārako;</p>\r\n<p class="gathalast">Kvanu bimbaṃ samuppannaṃ, kvanu bimbaṃ nirujjhati-hu–</p>\r\n<p class="gatha1">Nayidaṃ attakataṃ bimbaṃ, nayidaṃ parakataṃ aghaṃ;</p>\r\n<p class="gathalast">Hetuṃ paṭicca sambhūtaṃ, hetubhaṅgā nirujjhati.</p>\r\n<p class="subhead">Vajirāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',69,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0069"></a> porāṇakehi mahākassapādīhi dhammasaṃgāhakatheravarehi saṃgītaṃ vajirāsuttaṃ kattha kaṃ ārabbha kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante māraṃ pāpimantaṃ ārabbha vajirāya theriyā bhikkhuniyā bhāsitaṃ. Māro bhante pāpimā purimanayeneva vajiraṃ bhikkhuniṃ gāthāya ajjhabhāsi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',70,replace(replace('<p class="gatha1">Kenāyaṃ <a name="M1.0070"></a> pakato satto, kuvaṃ sattassa kārako,</p>\r\n<p class="gathalast">Kuvaṃ satto samuppanno, kuvaṃ satto nirujjhatīti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Kiṃ nu sattoti paccesi, māradiṭṭhigataṃ nu te;</p>\r\n<p class="gathalast">Suddhasaṅkhārapuñjoyaṃ, nayidha sattupalabbhati.</p>\r\n<p class="gatha1">Yathā hi aṅgasambhārā, hoti saddo ratho iti;</p>\r\n<p class="gathalast">Evaṃ khandhesu santesu, hoti sattoti sammuti.</p>\r\n<p class="gatha1">Dukkhameva hi sambhoti, dukkhaṃ tiṭṭhati veti ca,</p>\r\n<p class="gathalast">Nāññatra dukkhā sambhoti, nāññaṃ dukkhā nirujjhatīti.</p>\r\n<p class="bodytext">Evaṃ kho bhante vajirāya bhikkhuniyā bhāsitaṃ.</p>\r\n<p class="gatha1">Kenāyaṃ pakato satto, kuvaṃ sattassakārako;</p>\r\n<p class="gathalast">Kuvaṃ satto samuppanno, kuvaṃ satto nirujjhati-hu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',71,replace(replace('<p class="gatha1">Kiṃ <a name="M1.0071"></a> nu sattoti paccesi, māra diṭṭhigataṃ nu te;</p>\r\n<p class="gathalast">Suddhasaṅkhārapuñjoyaṃ, nayidha sattupalabbhati.</p>\r\n<p class="title">Brahmasaṃyutta</p>\r\n<p class="subhead">Gāravasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',72,replace(replace('<p class="bodytext">brahmasaṃyutte <a name="M1.0072"></a> āvuso porāṇehi mahākassapatherādīhi dhammasaṃgāhakatheravarehi dutiyaṃ saṃgītaṃ gāravasuttaṃ kattha kathañca samuppannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uruvelāyaṃ bhante samuppannaṃ. Bhagavā bhante uruvelāyaṃ viharati najjā nerañjarāya tīre ajapālanigrodhamūle paṭhamābhisambuddho. Atha kho bhagavato rahogatassa paṭisallīnassa evaṃ cetaso parivitakko udapādi ‘‘dukkhaṃ kho agāravo viharati appatisso, kaṃ nu khvāhaṃ samaṇaṃ vā brāhmaṇaṃ vā sakkatvā garuṃ katvā upanissāya vihareyya’’nti.</p>\r\n<p class="bodytext">Atha kho bhante bhagavato etadahosi ‘‘aparipuṇṇassa kho sīlakkhandhassa pāripūriyā aññaṃ samaṇaṃ vā brāhmaṇaṃ vā sakkatvā garuṃ katvā upanissāya vihareyyuṃ, na kho panāhaṃ passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya attanā sīlasampannataraṃ aññaṃ samaṇaṃ vā</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',73,replace(replace('<p class="bodytext">Brāhmaṇaṃ <a name="M1.0073"></a> vā, yamahaṃ sakkatvā garuṃ katvā upanissāya vihareyyuṃ.</p>\r\n<p class="gatha1">Aparipuṇṇassa kho samādhikkhandhassa;</p>\r\n<p class="gatha2">Aparipuṇṇassa kho paññākkhandhassa;</p>\r\n<p class="gathalast">Aparipuṇṇassa kho vimuttikkhandhassa.</p>\r\n<p class="bodytext">Aparipuṇṇassa kho vimuttiñāṇadassanakkhandhassa pāripūriyā aññaṃ samaṇaṃ vā brāhmaṇaṃ vā sakkatvā garuṃ katvā upaninissāya vihareyyaṃ. Na kho panāhaṃ passāmi sadevake loke samārake sabrahmake sassamaṇabrāhmaṇiyā pajāya sadevamanussāya attanā vimuttiñāṇadassanasampannataraṃ aññaṃ samaṇaṃ vā brāhmaṇaṃ vā, yamahaṃ sakkatvā garuṃ katvā upanissāya vihareyyaṃ. Yaṃnūnāhaṃ yvāyaṃ dhammo mayā abhisambuddho, tameva dhammaṃ sakkatvā garuṃ katvā upanissāya vihareyya’’nti. Evaṃ kho bhante bhagavato cetaso parivitakkavasena uppannaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',75,replace(replace('<p class="gatha1">Ye <a name="M1.0075"></a> ca atītā sambuddhā, ye ca buddhā anāgatā;</p>\r\n<p class="gatha2">Yo cetarahi sambuddho, bahūnaṃ sokanāsano;</p>\r\n<p class="gatha3">Sabbe saddhammagaruno, vihaṃsu viharanti ca;</p>\r\n<p class="gathalast">Tathāpi viharissanti, esā buddhāna dhammatā.</p>\r\n<p class="gatha1">Tasmā hi attakāmena, mahattamabhikaṅkhatā;</p>\r\n<p class="gathalast">Saddhammo garukātabbo, saraṃ buddhāna sāsanaṃ.</p>\r\n<p class="subhead">Aññaratabrahmasutta</p>\r\n<p class="bodytext">Pucchā- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',76,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0076"></a> porāṇakehi mahākassapādīhi dhammasaṃgāhakatheravarehi paṭhamaṃ saṃgītaṃ aññatarabrahmasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> brahmaloke bhante aññataraṃ brahmānaṃ ārabbha āyasmatā mahāmoggallānattherena bhāsitaṃ. Aññatarassa bhante brahmuno evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti ‘‘natthi so samaṇo vā brāhmaṇo vā, yo idha āgaccheyyā’’ti. Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Ajjāpi te āvuso sā diṭṭhi, yā te diṭṭhi pure ahu;</p>\r\n<p class="gatha2">Passasi vītivattantaṃ, brahmaloke pabhassaranti;</p>\r\n<p class="gathalast">Evaṃ kho bhante āyasmatā mahāmoggallānattherena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',78,replace(replace('<p class="gatha1">Ajjāpi <a name="M1.0078"></a> te āvuso sā diṭṭhi, yā te diṭṭhi pure ahu;</p>\r\n<p class="gathalast">Passasi vītivattanta, brahmaloke pabhassaraṃ-hu–</p>\r\n<p class="gatha1">Na me mārisa sā diṭṭhi, yā me diṭṭhi pure ahu;</p>\r\n<p class="gatha2">Passāmi vītivattantaṃ, brahmaloke pabhassaraṃ;</p>\r\n<p class="gathalast">Svāhaṃ ajja kathaṃ vajjaṃ, ahaṃ niccomhi sassato hu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',79,replace(replace('<p class="gatha1">Tevijjā <a name="M1.0079"></a> iddhipattā ca, cetopariyāya kovidā;</p>\r\n<p class="gathalast">Khīṇāsavā arahanto, bahū buddhassa sāvakā-hu–</p>\r\n<p class="subhead">Aruṇavatīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',80,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0080"></a> aruṇavatīsuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘bhūtapubbaṃ bhikkhave rājā ahosi aruṇavā nāma. Rañño kho pana bhikkhave aruṇavato aruṇavatī nāma rājadhānī ahosī’’ti evamādinā bhante bhagavatā aruṇavatī suttaṃ bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',83,replace(replace('<p class="gatha1">Ārambhatha <a name="M1.0083"></a> nikkamatha, yuñjatha buddhasāsane;</p>\r\n<p class="gatha2">Dhunātha maccuno senaṃ, naḷāgāraṃva kuñjaro;</p>\r\n<p class="gatha3">Yo imasmiṃ dhammavinaye, appamatto vihassati;</p>\r\n<p class="gathalast">Pahāya jāti saṃsāraṃ, dukkhassantaṃ karissati-hu–</p>\r\n<p class="title">Brāhmaṇasaṃyutta</p>\r\n<p class="subhead">Dhanañjānīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',84,replace(replace('<p class="bodytext">brāhmaṇasaṃyutte <a name="M1.0084"></a> āvuso paṭhamaṃ saṃgītaṃ dhanañjānīsuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante bhāradvājagottaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Bhāradvājagotto bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā gāthāya ajjhabhāsi.</p>\r\n<p class="gatha1">‘‘Kiṃ su chetvā sukhaṃ seti,</p>\r\n<p class="gatha2">Kiṃ su chetvā na socati;</p>\r\n<p class="gatha3">Kissassu ekadhammassa,</p>\r\n<p class="gathalast">Vadhaṃ rocesi gotamā’’ti–</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Kodhaṃ chetvā sukhaṃ seti;</p>\r\n<p class="gatha2">Kodhaṃ chetvā na socati;</p>\r\n<p class="gatha3">Kodhassa visamūlassa,</p>\r\n<p class="gatha2">Madhuraggassa brāhmaṇa;</p>\r\n<p class="gatha3">Vadhaṃ ariyā pasaṃsanti,</p>\r\n<p class="gathalast">Tañhi chetvā na socatī’’ti;</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',86,replace(replace('<p class="gatha1">Kiṃ <a name="M1.0086"></a> su chetvā sukhaṃseti, kiṃ su chetvā na socati;</p>\r\n<p class="gathalast">Kissassu ekadhammassa, vadhaṃ rocesi gotama–</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',87,replace(replace('<p class="bodytext">imañca <a name="M1.0087"></a> panāvuso dhammadesanaṃ sutvā bhāradvājagotto brāhmaṇo imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi, kīvattakañca atthaṃ sampādesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā bhāradvājagotto brāhmaṇo ‘‘abhikkantaṃ bho gotama, abhikkantaṃ bho gotama, seyyathāpi bho gotama nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya cakkhumanto rūpāni dakkhantī’’ti evamādinā bhante imasmiṃ dhammavinaye pasanno pasannākāramakāsi. Imañca pana bhante dhammadesanaṃ sutvā bhāradvājagotto brāhmaṇo bhagavato santike pabbajjaṃ upasampadaṃ yācitvā acirūpasampanno yāva arahattaṃ sampādesi.</p>\r\n<p class="subhead">Akkosasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',88,replace(replace('<p class="bodytext">akkosakabhāradvājasuttaṃ <a name="M1.0088"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante akkosakabhāradvājaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Akkosakabhāradvājo bhante brāhmaṇo bhagavantaṃ upasaṅkamitvā asabbhāhi pharusāhi vācāhi bhagavantaṃ akkosati paribhāsati. Tasmiṃ bhante vatthusmiṃ ‘‘taṃ kiṃ maññasi brāhmaṇa, api nukho te āgacchanti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',89,replace(replace('<p class="bodytext">mittāmaccā <a name="M1.0089"></a> ñātisālohitā atitiyo’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',91,replace(replace('<p class="gatha1">Akkodhassa <a name="M1.0091"></a> kuto kodho,</p>\r\n<p class="gatha2">Dantassa samajīvino;</p>\r\n<p class="gatha3">Sammadaññā vimuttassa,</p>\r\n<p class="gathalast">Upasantassa tādino.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',92,replace(replace('<p class="gatha1">Tasseva <a name="M1.0092"></a> tena pāpiyo,</p>\r\n<p class="gatha2">Yo kuddhaṃ paṭikujjhati;</p>\r\n<p class="gatha3">Kuddhaṃ appaṭikujjhanto,</p>\r\n<p class="gathalast">Saṅgāmaṃ jeti dujjayaṃ.</p>\r\n<p class="gatha1">Ubhinnamatthaṃ carati,</p>\r\n<p class="gatha2">Attano ca parassa ca;</p>\r\n<p class="gatha3">Paraṃ saṅkupitaṃ ñatvā,</p>\r\n<p class="gathalast">Yo sato upasammati.</p>\r\n<p class="gatha1">Ubhinnaṃ tikicchantānaṃ,</p>\r\n<p class="gatha2">Attano ca parassa ca;</p>\r\n<p class="gatha3">Janā maññanti bāloti,</p>\r\n<p class="gathalast">Ye dhammassa akovidā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',93,replace(replace('<p class="bodytext">imañca <a name="M1.0093"></a> panāvuso dhammadesanaṃ sutvā akkosakabhāradvājo brāhmaṇo imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi. Kīvattakañca atthaṃ sampādesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā akkosakabhāradvājo brāhmaṇo ‘‘abhikkantaṃ bho gotama, abhikkantaṃ bho gotamā’’ti evamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi. Yāva arahattā ca pana mahantaṃ atthaṃ sampādesi.</p>\r\n<p class="subhead">Bahudhītarasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',95,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0095"></a> bhagavatā jānatā…pe… sammāsambuddhena brāhmaṇasaṃyutte bahudhītarasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante aññatarasmiṃ vanasaṇḍe aññataraṃ bhāradvājagottaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Aññataro bhante bhāradvājagotto brāhmaṇo goṇe naṭṭhe pariyesanto bhagavato santike imā gāthāyo abhāsi.</p>\r\n<p class="gatha1">‘‘Na hi nūnimassa samaṇassa, balībaddā catuddasa;</p>\r\n<p class="gatha2">Ajjasaṭṭhiṃ na dissanti, tenāyaṃ samaṇo sukhī;</p>\r\n<p class="gatha3">Na hi nūnimassa samaṇassa, tilākhettasmi pāpakā;</p>\r\n<p class="gathalast">Ekapaṇṇā dupaṇṇā ca, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(Peyyāla)</p>\r\n<p class="gatha1">Na hi nūnimassa samaṇassa, paccūsamhi iṇāyikā;</p>\r\n<p class="gathalast">Detha dethāti codenti, tenāyaṃ samaṇo sukhī’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, balībaddā catuddasa;</p>\r\n<p class="gatha2">Ajjasaṭṭhiṃ na dissanti, tenāhaṃ brāhmaṇā sukhī;</p>\r\n<p class="gatha3">Na hi mayhaṃ brāhmaṇa, tilākhettasmi pāpakā;</p>\r\n<p class="gathalast">Ekapaṇṇā dupaṇṇā ca, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(Peyyāla)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, paccūsamhi iṇāyikā;</p>\r\n<p class="gathalast">Detha dethāti codenti, tenāhaṃ brāhmaṇā sukhīti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">(1)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',96,replace(replace('<p class="gatha1">Na <a name="M1.0096"></a> hi nūnimassa samaṇassa, balībaddā catuddasa;</p>\r\n<p class="gathalast">Ajjasaṭṭhiṃ na dissanti, tenāhaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(2)</p>\r\n<p class="gatha1">Na hi nūnimassa samaṇassa, tilākhettasmi pāpakā;</p>\r\n<p class="gathalast">Ekapaṇṇā dupaṇṇā ca, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(3)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',97,replace(replace('<p class="gatha1">Na <a name="M1.0097"></a> hi nūnimassa samaṇassa, tucchakoṭṭhasmi mūsikā;</p>\r\n<p class="gathalast">Ussoḷhikāya naccanti, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(4)</p>\r\n<p class="gatha1">Na hi nūnimassa samaṇassa, santhāro sattamāsiko;</p>\r\n<p class="gathalast">Uppāṭakehi sañchanno, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(5)</p>\r\n<p class="gatha1">Na hi nūnimassa samaṇassa, vidhavā satta dhītaro;</p>\r\n<p class="gathalast">Ekaputtā duputtā ca, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(6)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',98,replace(replace('<p class="gatha1">Na <a name="M1.0098"></a> hi nūnimassa samaṇassa, piṅgalā tilakāhatā;</p>\r\n<p class="gathalast">Sottaṃ pādena bodheti, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(7)</p>\r\n<p class="gatha1">Na hi nūnimassa samaṇassa, paccūsami iṇāyikā;</p>\r\n<p class="gathalast">Detha dethāti codenti, tenāyaṃ samaṇo sukhī.</p>\r\n<p class="bodytext">(1)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',99,replace(replace('<p class="gatha1">Na <a name="M1.0099"></a> hi mayhaṃ brāhmaṇa, balībaddā catuddasa;</p>\r\n<p class="gathalast">Ajjasaṭṭhiṃ na dissanti, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(2)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, tilākhettasmi pāpakā;</p>\r\n<p class="gathalast">Ekapaṇṇā dupaṇṇā ca, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(3)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, tucchakoṭṭhasmi mūsikā;</p>\r\n<p class="gathalast">Ussoḷhīkāya naccanti, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(4)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, santhāro sattamāsiko;</p>\r\n<p class="gathalast">Uppāṭakehi sañchanno, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(5)</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',100,replace(replace('<p class="gatha1">Na <a name="M1.0100"></a> hi mayhaṃ brāhmaṇa, vidhavā satta dhītaro;</p>\r\n<p class="gathalast">Ekaputtā duputtā ca, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(6)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, piṅgalā tilakāhatā;</p>\r\n<p class="gathalast">Sottaṃ pādena bodheti, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext">(7)</p>\r\n<p class="gatha1">Na hi mayhaṃ brāhmaṇa, paccūsamhi iṇāyikā;</p>\r\n<p class="gathalast">Detha dethāti codenti, tenāhaṃ brāhmaṇā sukhī.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',101,replace(replace('<p class="bodytext">imañca <a name="M1.0101"></a> panāvuso dhammadesanaṃ sutvā so bhāradvājagotto brāhmaṇo imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi. Kīva mahantañca imasmiṃ dhammavinaye atthaṃ sampādesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā so bhāradvājagotto brāhmaṇo ‘‘abhikkantaṃ bho gotama abhikkantaṃ bho gotama, seyyathāpi bhogotama, nikkujjitaṃ vā ukkujjeyya, paṭicchannaṃ vā vivareyya, mūḷhassa vā maggaṃ ācikkheyya, andhakāre vā telapajjotaṃ dhāreyya, cakkhumanto rūpāni dukkhantī’’ti evamādinā imasmi dhammavinaye pasanno pasannākāramakāsi. Bhagavato ca santike ‘‘labheyyāhaṃ bhoto gotamassa santike pabbajaṃ, labheyyaṃ upasampada’’nti evamādinā pabbajjaṃ upasampadaṃ yācitvā acirūpasampanno yāva arahattā mahantaṃ visesaṃ sampādesi.</p>\r\n<p class="subhead">Kasibhāradvājasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',104,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0104"></a> ekādasamaṃ mahākassapādīhi porāṇadhammasaṃgāhakatherehi saṃgītaṃ kasibhāradvājasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> magadhesu bhante dakkhiṇāgirismiṃ ekanāḷāyaṃ brāhmaṇagāme kasibhāradvājaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Kasibhāradvājo bhante brāhmaṇo bhagavantaṃ gāthāya ajjhabhāsi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',105,replace(replace('<p class="bodytext">‘‘Kassako <a name="M1.0105"></a> paṭijānāsi, na ca passāmi te kasiṃ. Kassako pucchito brūhi, kathaṃ jānemu taṃ kasi’’nti. Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Saddhā bījaṃ tapo vuṭṭhi, paññā me yuganaṅgalaṃ;</p>\r\n<p class="gathalast">Hirī īsā mano yottaṃ, sati me phālapācanaṃ.</p>\r\n<p class="gatha1">Kāyagutto vacīgutto, āhāre udare yato;</p>\r\n<p class="gathalast">Saccaṃ karomi niddānaṃ, soraccaṃ me pamocanaṃ.</p>\r\n<p class="gatha1">Vīriyaṃ me dhuradhorayhaṃ, yogakkhemādhivāhanaṃ;</p>\r\n<p class="gathalast">Gacchati anivattantaṃ, yattha gantvā na socati.</p>\r\n<p class="gatha1">Evamesā kasī kaṭṭhā, sā hoti amatapphalā;</p>\r\n<p class="gathalast">Etaṃ kasiṃ kasitvāna, sabbadukkhā pamuccatī’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',106,replace(replace('<p class="gatha1">Kassako <a name="M1.0106"></a> paṭijānāsi, na ca passāmi te kasiṃ;</p>\r\n<p class="gathalast">Kassako pucchito brūhi, kathaṃ jānemu taṃ kasiṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',107,replace(replace('<p class="gatha1">Saddhā <a name="M1.0107"></a> bījaṃ tapo vuṭṭhi, paññā me yuganaṅgalaṃ;</p>\r\n<p class="gathalast">Hirī īsā mano yottaṃ, sati me phālapācanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',109,replace(replace('<p class="bodytext">imañca <a name="M1.0109"></a> panāvuso dhammadesanaṃ sutvā so kasibhāradvājo brāhmaṇo imasmiṃ dhammavinaye pasanno kīdisaṃ pasannākāramakāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imañca pana bhante dhammadesanaṃ sutvā kasibhāradvājo brāhmaṇo ‘‘abhikkantaṃ bho gotama, abhikkantaṃ bho gotamā’’tievamādinā imasmiṃ dhammavinaye pasanno pasannākāramakāsi.</p>\r\n<p class="bodytext">Seyyathāpi bho gotama ukkujjitaṃ vā nikkujjeyya.</p>\r\n<p class="subhead">Udayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',110,replace(replace('<p class="bodytext">udayasuttaṃ <a name="M1.0110"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante udayaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Udayo bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘pakaṭṭhakoyaṃ samaṇo gotamo punappunaṃ āgacchatī’’ti. Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Punappunañceva vapanti bījaṃ, punappunaṃ vassati devarājā;</p>\r\n<p class="gathalast">Punappunaṃ khettaṃ kasanti kassakā, punappunaṃ dhaññamupeti raṭṭhaṃ.</p>\r\n<p class="bodytext">Evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',111,replace(replace('<p class="gatha1">Punappunaṃ <a name="M1.0111"></a> ceva vapanti bījaṃ, punappunaṃ vassati deva rājā;</p>\r\n<p class="gathalast">Punappunaṃ khettaṃ kasanti kassakā, punappunaṃ dhaññamupeti raṭṭhaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',112,replace(replace('<p class="gatha1">Punappunaṃ <a name="M1.0112"></a> yācakā yācayanti, punappunaṃ dānapatī dadanti;</p>\r\n<p class="gathalast">Punappunaṃ dānapatī daditvā, punappunaṃ saggamupeti ṭhānaṃ.</p>\r\n<p class="gatha1">Punappunaṃ khīranikā duhanti, punappunaṃ vaccho upeti mātaraṃ;</p>\r\n<p class="gathalast">Punappunaṃ kilamati phandati ca, punappunaṃ gabbhamupeti mando.</p>\r\n<p class="gatha1">Punappunaṃ jāyati mīyati ca, punappunaṃ sivathikaṃ haranti;</p>\r\n<p class="gathalast">Maggañca laddhā apunabbhavāya, na punappunaṃ jāyati bhūripañño-hu–</p>\r\n<p class="subhead">Mātuposakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',113,replace(replace('<p class="bodytext">mātuposakasuttaṃ <a name="M1.0113"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante mātuposakaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Mātuposako bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘ahañhi bho gotama dhammena bhikkhaṃ pariyesāmi, dhammena bhikkhaṃ pariyesitvā mātāpitaro posemi, kaccāhaṃ bho gotama evaṃkārī kiccakārī homī’’ti, tasmiṃ bhante vatthusmiṃ ‘‘taggha tvaṃ brāhmaṇa evaṃkārī kiccakārī hosi, yo kho brāhmaṇa dhammena bhikkhaṃ pariyesati, dhammena bhikkhaṃ pariyesitvā mātāpitaro poseti, bahuṃ so puññaṃ pasavatī’’ti evamādinā bhante bhagavatā bhāsitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',114,replace(replace('<p class="gatha1">Yo <a name="M1.0114"></a> mātaraṃ vā pitaraṃ vā, macco dhammena posati;</p>\r\n<p class="gatha2">Tāya naṃ pāricariyāya, mātāpitūsu paṇḍitā;</p>\r\n<p class="gathalast">Idheva naṃ pasaṃsanti, pecca sagge pamodati–</p>\r\n<p class="subhead">Khomadussasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',115,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0115"></a> bhagavatā…pe… sammāsambuddhena brāhmaṇasaṃyutte khomadussasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante khomadusse nāma sakyānaṃ nigame khomadussake brāhmaṇagahapatike ārabbha bhāsita, khomadussakā bhante brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ ‘‘ke ca muṇḍakā samaṇakā, ke ca sabhādhammaṃ jānissantī’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Nesā sabhā yattha na santi santo,</p>\r\n<p class="gatha2">Santo na te ye na vadanti dhammaṃ;</p>\r\n<p class="gatha3">Rāgañca dosañca pahāya mohaṃ,</p>\r\n<p class="gathalast">Dhammaṃ vadantā ca bhavanti santo’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',116,replace(replace('<p class="gatha1">Nesā <a name="M1.0116"></a> sabhā yattha nasanti santo,</p>\r\n<p class="gatha2">Santo na te ye na vadanti dhammaṃ;</p>\r\n<p class="gatha3">Rāgañca dosañca pahāya mohaṃ,</p>\r\n<p class="gathalast">Dhammaṃ vadantā ca bhavanti santo–</p>\r\n<p class="title">Vaṅgīsasaṃyutta</p>\r\n<p class="subhead">Ānandasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',117,replace(replace('<p class="bodytext">vaṅgīsasaṃyutte <a name="M1.0117"></a> panāvuso catutthaṃ saṃṅgītaṃ ānandasuttaṃ katthakaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ vaṅgīsattheraṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Āyasmatā bhante vaṅgīso āyasmantaṃ ānandaṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">‘‘Kāmarāgena ḍayhāmi, cittaṃ me pariḍayhati;</p>\r\n<p class="gathalast">Sādhu nibbāpanaṃ brūhi, anukampāya gotamā’’ti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Saññāya vipariyesā, cittaṃ te pariḍayhati;</p>\r\n<p class="gatha2">Nimittaṃ parivajjehi, subhaṃ rāgūpasaṃhitaṃ;</p>\r\n<p class="gatha3">Saṅkhāre parato passa, dukkhato mā ca attato;</p>\r\n<p class="gatha2">Nibbāpehi mahārāgaṃ, mā ḍayhittho punappunaṃ;</p>\r\n<p class="gatha3">Asubhāya cittaṃ bhāvehi, ekaggaṃ susamāhitaṃ;</p>\r\n<p class="gatha2">Sati kāyagatā tyatthu, nibbidābahulo bhava;</p>\r\n<p class="gatha3">Animittañca bhāvehi, mānānusayamujjaha;</p>\r\n<p class="gathalast">Tato mānābhisamayā, upasanto carissasī’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',118,replace(replace('<p class="gatha1">Kāmarāgena <a name="M1.0118"></a> ḍayhāmi, cittaṃ me pariḍayhati;</p>\r\n<p class="gathalast">Sādhu nibbāpanaṃ brūhi, anukampāya gotama–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',119,replace(replace('<p class="gatha1">Saññāya <a name="M1.0119"></a> vipariyesā, cittaṃ te pariḍayhati;</p>\r\n<p class="gathalast">Nimittaṃ parivajjehi, subhaṃ rāgūpasaṃhitaṃ.</p>\r\n<p class="gatha1">Saṅkhāre paratopassa, dukkhato mā ca attato;</p>\r\n<p class="gathalast">Nibbāpehi mahārāgaṃ, mā ḍayhittho punappunaṃ.</p>\r\n<p class="gatha1">Asubhāya cittaṃ bhāvehi, ekaggaṃ susamāhitaṃ;</p>\r\n<p class="gathalast">Sati kāyagatā tyatthu, nibbidābahulo bhava.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',120,replace(replace('<p class="gatha1">Animittañca <a name="M1.0120"></a> bhāvehi, mānānusayamujjaha;</p>\r\n<p class="gathalast">Tato mānābhisamayā, upasanto carissasi.</p>\r\n<p class="subhead">Vaṅgīsasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattha āvuso dvādasamaṃ vaṅgīsasuttaṃ kattha kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmatā vaṅgīsattherena acira arahattappattena vimuttisukhapaṭisaṃvedinā–</p>\r\n<p class="gatha1">‘‘Kāveyyamattā vicarimha pubbe, gāmāgāmaṃ purāpuraṃ,</p>\r\n<p class="gatha2">Athaddasāma sambuddhaṃ, saddhā no upapajjatha;</p>\r\n<p class="gatha3">So me dhamma madesesi, khandhāyatana dhātuyo;</p>\r\n<p class="gatha2">Tassāhaṃ dhammaṃ sutvāna, pabbajiṃ anagāriyaṃ;</p>\r\n<p class="gatha3">Bahunnaṃ vata atthāya, bodhiṃ ajjhagamā muni;</p>\r\n<p class="gatha2">Bhikkhūnaṃ bhikkhūnīnañca, ye niyāmagataddasā;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',121,replace(replace('<p class="gatha3">Svāgataṃ <a name="M1.0121"></a> vata me āsi, mama buddhassa santike;</p>\r\n<p class="gatha2">Tisso vijjā anuppattā, kataṃ buddhassa sāsanaṃ;</p>\r\n<p class="gatha3">Pubbe nivāsaṃ jānāmi, dibbacakkhuṃ visodhitaṃ;</p>\r\n<p class="gathalast">Tevijjo iddhipattomhi, cetopariyāya kovido.</p>\r\n<p class="bodytext">Evaṃ kho bhante udānavasena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',124,replace(replace('<p class="gatha1">Kāveyya <a name="M1.0124"></a> mattā vicarimha pubbe; Gāmāgāmaṃpurāpuraṃ,</p>\r\n<p class="gathalast">Athaddasāma sambuddhaṃ, saddhā no upapajjatha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',125,replace(replace('<p class="gatha1">So <a name="M1.0125"></a> me dhammamadesesi, khandhāyatana dhātuyo;</p>\r\n<p class="gathalast">Tassāhaṃ dhammaṃ sutvāna, pabbajiṃ anagāriyaṃ.</p>\r\n<p class="gatha1">Bahunnaṃ vata atthāya, bodhiṃ ajjhagamā muni;</p>\r\n<p class="gathalast">Bhikkhūnaṃ bhikkhunī nañca, ye niyāmagataddasā.</p>\r\n<p class="gatha1">Svāgataṃ vata me āsi, mama buddhassa santike;</p>\r\n<p class="gathalast">Tasso vijjā anuppattā, kataṃ buddhassa sāsanaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',126,replace(replace('<p class="gatha1">Pubbenivāsaṃ <a name="M1.0126"></a> jānāmi; Dibbacakkhu visodhitaṃ;</p>\r\n<p class="gathalast">Tevijjo iddhipattomhi, cetopariyāyakovido.</p>\r\n<p class="title">Vanasaṃyutta</p>\r\n<p class="subhead">Ānandasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> vanasaṃyutte panāvuso pañcamaṃ saṃgītaṃ ānandasuttaṃ kadā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante aññatarasmiṃ vanasaṇḍe aciraparinibbute bhagavati āyasmantaṃ ānandaṃ ārabbha tasmiṃ vanasaṇḍe adhivatthāya devatāya bhāsitaṃ. Āyasmā bhante ānando aciraparinibbute bhagavati ativelaṃ gihisaññattibahulo viharati, tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Rukkhamūlagahanaṃ pasakkiya,</p>\r\n<p class="gatha2">Nibbānaṃ hadayasmiṃ opiya;</p>\r\n<p class="gatha3">Jhāya gotama mā pamādo,</p>\r\n<p class="gathalast">Kiṃ te biḷibiḷikā karissatī’’ti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',127,replace(replace('<p class="bodytext">Evaṃ <a name="M1.0127"></a> kho bhante vanasaṇḍe adhivatthāya devatāya āyasmato ānandattherassa anukampikāya atthakāmāya bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',128,replace(replace('<p class="gatha1">Rukkhamūla <a name="M1.0128"></a> gahanaṃ pasakkiya, nibbānaṃ hadayasmiṃ opiya;</p>\r\n<p class="gathalast">Jhāya gotama mā pamādo, kiṃ te biḷibiḷikā karissati-hu–</p>\r\n<p class="title">Yakkhasaṃyutta</p>\r\n<p class="subhead">Indakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',129,replace(replace('<p class="bodytext">yakkhasaṃyutte <a name="M1.0129"></a> panāvuso paṭhamaṃ saṃgītaṃ indakasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante indakūṭe pabbate indakassa yakkhassa bhavane indakaṃ yakkhaṃ ārabbha bhāsitaṃ. Indako bhante yakkho attavādo bhagavantaṃ upasaṅkamitvā gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">‘‘Rūpaṃ na jīvanti vadanti buddhā,</p>\r\n<p class="gatha2">Kathaṃ nvayaṃ vindatimaṃ sarīraṃ;</p>\r\n<p class="gatha3">Kutassa aṭṭhīyakapiṇḍamebhi,</p>\r\n<p class="gathalast">Kathaṃ nvayaṃ sajjati gabbharasmi’’nti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Paṭhamaṃ kalalaṃ hoti, kalalā hoti abbudaṃ,</p>\r\n<p class="gatha2">Abbudā jāyate pesi, pesi nibbattatī ghano;</p>\r\n<p class="gatha3">Ghanā pasākhā jāyanti, kesā lomā nakhāpica;</p>\r\n<p class="gatha2">Yañcassa bhuñjatī mātā, annaṃ pānañca bhojanaṃ;</p>\r\n<p class="gathalast">Tena so tattha yāpeti, mātukucchigatonaro’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āḷavakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',132,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0132"></a> bhagavatā…pe… sammāsambuddhena yakkhasaṃyutte āḷavakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante āḷavakassa yakkhassa bhavane āḷavakaṃ yakkhaṃ ārabbha bhāsitaṃ. Āḷavako bhante yakkho bhagavantaṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">Kiṃ sūdhavittaṃ purisassa seṭṭhaṃ,</p>\r\n<p class="gatha2">Kiṃ su suciṇṇaṃ sukhamāvahāti;</p>\r\n<p class="gatha3">Kiṃ su have sādutaraṃ rasānaṃ,</p>\r\n<p class="gathalast">Kathaṃ jīviṃ jīvitāmāhu seṭṭhanti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">Saddhīca vittaṃ purisassa seṭṭhaṃ,</p>\r\n<p class="gatha2">Dhammo suciṇṇo sukhamāvahāti;</p>\r\n<p class="gatha3">Saccaṃ have sādutaraṃ rasānaṃ,</p>\r\n<p class="gathalast">Paññājīviṃ jīvitamāhu seṭṭhanti.</p>\r\n<p class="bodytext">Evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',134,replace(replace('<p class="bodytext">Na <a name="M1.0134"></a> khvāhaṃ taṃ āvuso nikkhamissāmi, yaṃ te karaṇīyaṃ, taṃ karohi,</p>\r\n<p class="bodytext">Pañhaṃ taṃ samaṇa pucchissāmi, sace me na byākarissasi, cittaṃ vā te khipissāmi, hadayaṃ vā te phālessāmi, pādesu vā gahetvā pāragaṅgāya khipissāmi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',135,replace(replace('<p class="gatha1">Kiṃ <a name="M1.0135"></a> sūkha vittaṃ purisassa seṭṭhaṃ, kiṃ su suciṇṇaṃ sukhamāvahāti;</p>\r\n<p class="gathalast">Kiṃ su have sādubharaṃ rasānaṃ, kathaṃ jīviṃ jīvitamāhu seṭṭhaṃ–</p>\r\n<p class="gatha1">Saddhīdha vittaṃ purisassa seṭṭhaṃ, dhammo suciṇṇo sukhamāvahāti;</p>\r\n<p class="gathalast">Saccaṃ have sādutaraṃ rasānaṃ, paññājīviṃ jīvitamāhu seṭṭhaṃ;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',136,replace(replace('<p class="gatha1">Kathaṃsu <a name="M1.0136"></a> tarati oghaṃ, kathaṃsu tarati aṇṇavaṃ;</p>\r\n<p class="gathalast">Kathaṃsu dukkha macceti, kathaṃsu parisujjhati.</p>\r\n<p class="gatha1">Saddhāya tarati oghaṃ, appamādena aṇṇavaṃ;</p>\r\n<p class="gathalast">Vīriyena dukkha macceti, paññāya parisujjhati–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',137,replace(replace('<p class="gatha1">Kathaṃsu <a name="M1.0137"></a> labhate paññaṃ, kathaṃsu vindate dhanaṃ;</p>\r\n<p class="gatha2">Kathaṃsu kittiṃ pappoti, kathaṃ mittāni ganthati;</p>\r\n<p class="gathalast">Asmā lokā paraṃ lokaṃ, kathaṃ pecca na socati–</p>\r\n<p class="gatha1">Saddahāno arahataṃ, dhammaṃ nibbānapattiyā;</p>\r\n<p class="gatha2">Sussūsaṃ labhate paññaṃ, appamatto vicakkhaṇo;</p>\r\n<p class="gatha3">Patirūpakārī dhuravā, uṭṭhātā vindate dhanaṃ;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',138,replace(replace('<p class="gatha2">Saccena <a name="M1.0138"></a> kittiṃ pappoti, dadaṃ mittāni ganthati;</p>\r\n<p class="gatha3">Asmā lokā paraṃ lokaṃ, evaṃ pecca na socati;</p>\r\n<p class="gatha2">Yassete caturo dhammā, saddhassa gharamesino;</p>\r\n<p class="gathalast">Saccaṃ dhammo dhiti cāgo, sa ve peccana socati–</p>\r\n<p class="title">Sakkasaṃyutta</p>\r\n<p class="subhead">Vatapadasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',139,replace(replace('<p class="bodytext">sakkasaṃyutte <a name="M1.0139"></a> panāvuso vatapadasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha sakkassa bhikkhave devānamindassa pubbe manussabhūtassa satta vatapadāni samattāni samādinnāni ahesuṃ, yesaṃ samādinnattā sakko sakkattaṃ ajjhagāti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',141,replace(replace('<p class="bodytext">Sakkassa <a name="M1.0141"></a> bhikkhave devānamindassa pubbe manussabhūtassa imāni satta vatapadāni samattāni samādinnāni ahesuṃ.</p>\r\n<p class="gatha1">Mātāpettibharaṃ jantuṃ, kule jeṭṭhāpacāyinaṃ;</p>\r\n<p class="gatha2">Saṇhaṃ sakhilasambhāsaṃ, pesuṇeyyappahāyinaṃ;</p>\r\n<p class="gatha3">Maccheravinaye yuttaṃ, saccaṃ kodhābhibhuṃ naraṃ;</p>\r\n<p class="gathalast">Taṃ ve devā tāvatiṃsā, āhu ‘‘sappuriso’’iti.</p>\r\n<p class="subhead">Daliddasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> daliddasuttaṃ panāvuso kattha kaṃ ārabbha kathañca bhāsitaṃ. Vissajjanā. Rājagahe bhante sambahule bhikkhū ārabbha ‘‘bhūtapubbaṃ bhikkhave aññataro puriso imasmiṃyeva rājagahe </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',142,replace(replace('<p class="bodytext">manussadaliddo <a name="M1.0142"></a> ahosi manussakapaṇo manussavarāko, so tathāgatappavedite dhammavinaye saddhaṃ samādiyi, sīlaṃ, sutaṃ, cāgaṃ, paññaṃ samādiyī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',144,replace(replace('<p class="gatha1">Yassa <a name="M1.0144"></a> saddhā tathāgate, acalā suppatiṭṭhitā;</p>\r\n<p class="gathalast">Sīlañca yassa kalyāṇaṃ, ariyakantaṃ pasaṃsitaṃ.</p>\r\n<p class="subhead">Yajamānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yajamānasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',145,replace(replace('<p class="bodytext">rājagahe <a name="M1.0145"></a> bhante sakkaṃ devānamindaṃ ārabbha bhāsitaṃ. Sakko bhante devānamindo bhagavantaṃ gāthāya ajjhabhāsi–</p>\r\n<p class="gatha1">‘‘Yajamānānaṃ manussānaṃ, puññapekkhāna pāṇinaṃ;</p>\r\n<p class="gathalast">Karotaṃ opadhikaṃ puññaṃ, kattha dinnaṃ mahapphala’’nti.</p>\r\n<p class="bodytext">Tasmiṃ bhante vatthusmiṃ–</p>\r\n<p class="gatha1">‘‘Cattāro ca paṭipannā, cattāroca phale ṭhitā;</p>\r\n<p class="gatha2">Esa saṅgho ujubhūto, paññāsīlasamāhito;</p>\r\n<p class="gatha3">Yajamānānaṃ manussānaṃ, puññapekkhāna pāṇinaṃ;</p>\r\n<p class="gathalast">Karotaṃ opadhikaṃ puññaṃ, saṅghe dinnaṃ mahapphalaṃ’’nti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',146,replace(replace('<p class="gatha1">Yajamānānaṃ <a name="M1.0146"></a> manussānaṃ, puññapekkhāna pāṇinaṃ,</p>\r\n<p class="gathalast">Karotaṃ opadhitaṃ puññaṃ, kattha dinnaṃ mahapphalaṃ–</p>\r\n<p class="gatha1">Cattāro ca paṭipannā, cattāro ca phale ṭhitā;</p>\r\n<p class="gatha2">Esa saṅgho ujubhūto, paññāsīlasamāhito;</p>\r\n<p class="gatha3">Yajamānānaṃ manussānaṃ, puññapekkhāna pāṇinaṃ;</p>\r\n<p class="gathalast">Karotaṃ opadhikaṃ puññaṃ, saṅghe dinnaṃ mahapphalaṃ–</p>\r\n<p class="subhead">Gahaṭṭhavandanāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',147,replace(replace('<p class="bodytext">gahaṭṭhavandanāsuttaṃ <a name="M1.0147"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘bhūtapubbaṃ bhikkhave sakko devānamindo mātaliṃ saṅgāhakaṃ āmantesī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',148,replace(replace('<p class="gatha1">Taṃ <a name="M1.0148"></a> namassanti tevijjā, sabbe bhummāca khattiyā;</p>\r\n<p class="gatha2">Cattāro ca mahārājā, tidasāca yasassino;</p>\r\n<p class="gathalast">Athako nāma so yakkho, yaṃ tvaṃ sakka namassasi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',149,replace(replace('<p class="gatha1">Ahañca <a name="M1.0149"></a> sīlasampanne, cirarattasamāhite;</p>\r\n<p class="gathalast">Sammāpabbajite vande, brahmacariyaparāyane.</p>\r\n<p class="gatha1">Ye gahaṭṭhā puññakarā, sīlavanto upāsakā;</p>\r\n<p class="gathalast">Dhammena dāraṃ posenti, te namassāmi mātali.</p>\r\n<p class="chapter">Nidānavaggapāḷi</p>\r\n<p class="title">Nidānasaṃyutta</p>\r\n<p class="subhead">Paṭiccasamuppādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',151,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0151"></a> bhagavatā…pe… sammāsambuddhena nidānavaggasaṃyutte paṭhamaṃ saṃgītaṃ paṭiccasamuppādasuttaṃ kattha taṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante pañcasate janapadavāsino bhikkhū ārabbha bhāsitaṃ. Pañcasatā bhante janapadavāsino bhikkhū sabbe ugghāṭitaññuno dhutaṅgadharā āraddhavīriyā yuttayogā vipassakā saṇhaṃ sukhumaṃ suññataṃ paccayākāradhammadesanaṃ patthayamānā bhagavantaṃ abhivādetvā bhagavantaṃ parivāretvā nisīdiṃsu. Tasmiṃ vatthusmiṃ ‘‘paṭiccasamuppādaṃ vo bhikkhave desessāmi, taṃ suṇātha sādhukaṃ manasi karotha, bhāsissāmī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',152,replace(replace('<p class="bodytext">Katamo <a name="M1.0152"></a> ca bhikkhave paṭiccasamuppādo. Avijjāpaccayā bhikkhave saṅkhārā, saṅkhārapaccayā viññāṇaṃ (peyyāla) jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāsā sambhavanti, evametassa kevalassa dukkhandhassa samudayo hoti. Ayaṃ vuccati bhikkhave paṭiccasamuppādo.</p>\r\n<p class="bodytext">Avijjāya tveva asesavirāganirodho saṅkhāranirodho (peyyāla) jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti, evametassa kevalassa dukkhakkhandhassa nirodho hoti –</p>\r\n<p class="subhead">Vibhaṅgasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',154,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0154"></a> dutiyaṃ saṃgītaṃ vibhaṅgasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante pañcasate janapadavāsino vipañcitaññuno bhikkhū ārabbha bhāsitaṃ. Pañcasatā bhante janapadavāsikā bhikkhū vipañcitaññuno dhutaṅgadharā āraddhavīriyā yuttayogā vissakā saṇhaṃ sukhumaṃ suññatapaṭisaṃyuttaṃ paccayākāradhammadesanaṃ patthayamānā bhagavantaṃ parivāretvā nisīdiṃsu. Tasmiṃ vatthusmiṃ ‘‘paṭicca samuppādaṃ vo bhikkhave dedessāmi vibhajissāmi, taṃ suṇātha sādhukaṃ manasikarotha, bhāsissāmī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Pañcaverabhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',156,replace(replace('<p class="bodytext">pañcaverabhayasuttaṃ <a name="M1.0156"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante anāthapiṇḍikaṃ gahapatiṃ ārabbha ‘‘yato kho gahapati ariyasāvakassa pañca bhayāni verāni vūpasantāni honti, catūhi ca sotāpattiyaṅgehi samannāgato hoti, ariyo cassa ñāyo paññāya sudiṭṭho hoti suppaṭividdho, so ākaṅkhamāno attanāva attānaṃ byākareyya khīṇanirayomi khīṇatiracchānayoni khīṇapettivisayo khīṇāpāyaduggativinipāto, sotāpannohamasmi avinipātadhammo niyato sambodhiparāyano’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',157,replace(replace('<p class="bodytext">kathañcāvusotattha <a name="M1.0157"></a> bhagavatā ariyasāvakassa pañcannaṃ bhayānaṃ verānaṃ vūpasantatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',158,replace(replace('<p class="bodytext">‘‘katamāni <a name="M1.0158"></a> pañca bhayāni verāni vūpasantāni honti. Yaṃ gahapati pāṇātipātī pāṇātipātapaccayā diṭṭhadhammikampi bhayaṃ veraṃ pasavati, samparāyikampi bhayaṃ veraṃ pasavati, cetasikampi dukkhaṃ domanassaṃ paṭisaṃvedayati. Pāṇātipātā ṭiviratassa evaṃ taṃ bhayaṃ veraṃ vūpasantaṃ hotī’’ti evamādinā bhante tattha bhagavatā ariyasāvakassa pañcannaṃ bhayānaṃ verānaṃ vūpasantatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',159,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0159"></a> tattha bhagavatā ariyasāvakassa catūhi sotāpattiyaṅgehi samannāgatatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamehi catūhi sotāpattiyaṅgehi samannāgato hoti. Idha gahapati ariyasāvako buddhe aveccappasādena samannāgato hoti ‘‘itipiso bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaropurisadammasārathi satthādevamanussānaṃ buddho bhagavā’’ti evamādinā bhante tattha bhagavatā ariyasāvakassa catūhi sotāpattiyaṅgehi samannāgatatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',161,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0161"></a> tattha bhagavatā ariyasāvakena ariyassa ñāyassa paññāya sudiṭṭhatā suppaṭividdhatā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo cassa ariyo ñāyo paññāya sudiṭṭho hoti suppaṭividdho. Idha gahapati ariyasāvako paṭiccasamuppādaññeva sādhukaṃ yoniso manasi karoti ‘‘iti imasmiṃ sati idaṃ hoti, imasmiṃ asati idaṃ na hoti, imassuppādā idaṃ uppajjati, imassa nirodhā idaṃ nirujjhati, yadidaṃ avijjāpaccayā (peyyāla) evametassa kevalassa dukkhakkhandhassa nirodho hotī’’ti, evaṃ kho bhante bhagavatā tattha ariyasāvakena ariyassa ñāyassa paññāya sudiṭṭhatā suppaṭividdhatā pakāsitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',162,replace(replace('<p class="bodytext">Yato <a name="M1.0162"></a> kho gahapati ariyasāvakassa imāni pañca bhayāni verāni vūpasantāni honti. Imehi catūhi sotāpattiyaṅgehi samannāgato hoti. Ayañcassa ariyo ñāyo paññāya sudiṭṭho hoti suppaṭividdho–</p>\r\n<p class="subhead">Dukkhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',163,replace(replace('<p class="bodytext">dukkhasuttaṃ <a name="M1.0163"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘dukkhassa bhikkhave samudayañca atthaṅgamañca desessāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',164,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0164"></a> tattha bhagavatā dukkhasamudayo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave dukkhassa samudayo. Cakkhuñca paṭicca rūpe ca uppajjati cakkhuviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā. Ayaṃ kho bhikkhave dukkhassa samudayo. Sotañca paṭicca sadde ca uppajjati sotaviññāṇaṃ…pe… ghānañca paṭicca gandhe ca…pe… jivhañca paṭicca rase ca…pe… kāyañca paṭicca phoṭṭhabbe ca…pe… manañca paṭicca dhamme ca uppajjati manoviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā. Ayaṃ kho bhikkhave dukkhassa samudayoti evaṃkho bhante tattha bhagavatā dukkhassa samudayo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',165,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0165"></a> tattha bhagavatā dukkhassa atthaṅgamo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave dukkhassa atthaṅgamo. Cakkhuñca paṭicca rūpe ca uppajjati cakkhuviññāṇaṃ, tiṇṇaṃ saṅgati phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā, tassāyeva taṇhāya asesavirāganirodhā upādānanirodho upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti. Evametassa kevalassa dukkhakkhandhassa nirodho hotīti evamādinā bhante tattha bhagavatā dukkhassa atthaṅgamo pakāsito.</p>\r\n<p class="subhead">Puttamaṃsūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',166,replace(replace('<p class="bodytext">puttamaṃsūpamasuttaṃ <a name="M1.0166"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule adhunā pabbajite bhikkhū ārabbha bhāsitaṃ. Bhagavato ca bhante bhikkhusaṅghassa ca mahālābhasakkāro udapādi, ekacce ca bhante bhikkhū navā acirapabbajitā kulaputtā āhāraṃ apaccavekkhitvā paribhuñjiṃsu. Tasmiṃ bhante vatthusmiṃ ‘‘cattārome bhikkhave āhārā bhūtānaṃ vā sattānaṃ ṭhitiyā sambhavesīnaṃ vā anuggahāya. Katame cattāro kabaḷīkāro āhāro oḷāriko vā sukhumo vā phasso dutiyo manosañcetanā tatiyā viññāṇaṃ catuttha’’nti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',168,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0168"></a> tattha kabaḷīkārassa āhārassa daṭṭhabbākāro bhagavatā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi bhikkhave dve jāyampatikā parittaṃ sambalaṃ ādāya kantāramaggaṃ paṭipajjeyyuṃ. Tesamassa ekaputtako piyo manāpoti evamādinā bhante tattha </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',169,replace(replace('<p class="bodytext">bhagavatā <a name="M1.0169"></a> kabaḷīkārassa āhārassa daṭṭhabbākāro pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',172,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0172"></a> tattha bhagavatā phassāhārassa daṭṭhabbākāro pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi bhikkhave gāvī niccammā kuṭṭaṃ ce nissāya tiṭṭheyya, ye kuṭṭanissitā pāṇā, te naṃ khādeyyuṃ. Rukkhaṃ ce nissāya tiṭṭheyya. Udakaṃ ce nissāya tiṭṭheyya. Ākāsaṃ ce nissāya tiṭṭheyya, ye ākāsanissitā pāṇā, te naṃ khādeyyunti evamādinā bhante tattha bhagavatā phassassa āhārassa daṭṭhabbākāro vitthāretvā pakāsito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',173,replace(replace('<p class="bodytext">Evameva <a name="M1.0173"></a> khvāhaṃ bhikkhave phassāhāro daṭṭhabboti vadāmi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',174,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0174"></a> tattha bhagavatā manosañcetanāhārassa daṭṭhabbākāro pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi bhikkhave aṅgārakāsu sādhikaporisā puṇṇā aṅgārānaṃ vītaccikānaṃ vītadhūmānaṃ. Atha puriso āgaccheyya jīvitukāmo amaritukāmo sukha kāmo dukkhappaṭikūloti evamādinā bhante tattha bhagavatā manosañcetanāhārassa daṭṭhabbākāro vitthārena pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',176,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0176"></a> tattha bhagavatā viññāṇāhārassa daṭṭhabbākāro pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> seyyathāpi bhikkhave coraṃ āgucāriṃ gahetvā rañño dasseyyuṃ ‘‘ayaṃ te devacoro āgucārī, imassa yaṃ icchasi, taṃ daṇḍaṃ paṇehī’’ti evamādinā bhante tattha bhagavatā viññāṇāhārassa daṭṭhabbākāro pakāsito.</p>\r\n<p class="subhead">Susimaparibbājakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',179,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0179"></a> bhagavatā…pe… sammāsambuddhena susimaparibbājaka suttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ susimaṃ ārabbha bhāsitaṃ. Āyasmā bhante susimo yāvatako bhikkhūhi saddhiṃ ahosi kathāsallāpo, taṃ sabbaṃ bhagavato ārocesi. Tasmiṃ vatthusmiṃ ‘‘pubbe kho susima dhammaṭṭhitiñāṇaṃ, pacchā nibbāne ñāṇa’’nti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',184,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0184"></a> tattha bhagavā āyasmato susimattherassa sampatiarahattapattassa anuyogavasena paṭipucchitvā paṭipucchitvā uttari dhammadesanaṃ vitthāretvā desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> jātipaccayā jarāmaraṇanti susima passasīti. Evaṃ bhante. Bhavapaccayā jātīti susima passasīti. Evaṃ bhanteti evamādinā bhante bhagavā tattha āyasmato susimassa sampatiarahattapattassa uttaripi anuyogavasena paṭipucchitvā paṭipucchitvā vitthārato dhammaṃ desesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',186,replace(replace('<p class="bodytext">Tassa <a name="M1.0186"></a> me bhante bhagavā accayaṃ accayato paṭiggaṇhātu āyatiṃsaṃvarāya–</p>\r\n<p class="title">Abhisamayasaṃyutta</p>\r\n<p class="subhead">Nakhasikhāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',187,replace(replace('<p class="bodytext">abhisamayasaṃyutte <a name="M1.0187"></a> panāvuso paṭhamaṃ saṃgītaṃ nakhasikhāsuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ, yo vāyaṃ mayā paritto nakhasikhāyaṃ paṃsuāropito, ayaṃ vā mahāpathavī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Dhātusaṃyutta</p>\r\n<p class="subhead">Caṅkamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',189,replace(replace('<p class="bodytext">dhātusaṃyutte <a name="M1.0189"></a> panāvuso caṅkamasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha ‘‘dhātusova bhikkhave sattā saṃsandanti samenti, hīnādhimuttikā hīnādhimuttikehi saddhiṃ saṃsandanti samenti, kalyāṇādhimuttikā kalyāṇādhimuttikehi saddhiṃ saṃsandanti samentī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Assaddhasaṃsandanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',192,replace(replace('<p class="bodytext">assaddhasaṃsandanasuttaṃ <a name="M1.0192"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘dhātusova bhikkhave sattā saṃsandanti samenti, assaddhā assaddhehi saddhiṃ saṃsandanti samenti, ahirikā ahirikehi saddhiṃ saṃsandanti samenti, anottappino. Appassutā. Kusītā. Muṭṭhassatino. Duppaññā duppaññehi saddhiṃ saṃsandanti samentī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Anamataggasaṃyutta</p>\r\n<p class="subhead">Pathavīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',194,replace(replace('<p class="bodytext">anamataggasaṃyutte <a name="M1.0194"></a> āvuso dutiyaṃ saṃgītaṃ pathavīsuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘anamataggoyaṃ bhikkhave saṃsāro, pubbā koṭi na paññāyati avijjānīvaraṇānaṃ sattānaṃ taṇhāsaṃyojanānaṃ sandhāvataṃ saṃsarataṃ. Seyyathāpi bhikkhave puriso imaṃ mahāpathaviṃ kolaṭṭhimattaṃ kolaṭṭhimattaṃ mattikāguḷikaṃ karitvā nikkhipeyya ‘ayaṃ me piyā, tassa me pitu ayaṃ pitā’ti. Apariyādinnāva bhikkhave tassa purisassa pitupitaro assu. Athāyaṃ mahāpathavī parikkhayaṃ pariyādānaṃ gaccheyya. Taṃ kissahetu, anamataggoyaṃ bhikkhave saṃsāro, pubbā koṭi na paññāyati avijjānīvaraṇānaṃ sattānaṃ taṇhā saṃyojanānaṃ sandhāvataṃ saṃsarataṃ’’ evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',195,replace(replace('<p class="bodytext">Evaṃ <a name="M1.0195"></a> dīgharattaṃ vo bhikkhave dukkhaṃ paccanubhūtaṃ tibbaṃ paccanubhūtaṃ byasanaṃ paccanubhūtaṃ kaṭasī vaḍḍhitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',196,replace(replace('<p class="gatha1">Alameva <a name="M1.0196"></a> sabbasaṅkhāresu nibbindituṃ,</p>\r\n<p class="gathalast">Alaṃ virajjituṃ, alaṃ vimuccituṃ,</p>\r\n<p class="subhead">Assusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tatthāvuso tatiyaṃ saṃgītaṃ assusuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahuleyeva bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ, yaṃ vā vo iminā dīghena addhunā sandhāvataṃ saṃsarataṃ amanāpasampayogā manāpavippayogā kandantānaṃ rodantānaṃ assu passannaṃ paggharitaṃ, yaṃ vā catūsu mahāsamuddesu udaka’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sāsapasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',198,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0198"></a> chaṭṭhaṃ saṃgītaṃ sāsapasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha bhāsitaṃ. Aññataro bhante bhikkhu bhagavantaṃ etadavoca ‘‘kīvadīgho nu kho bhante kappo’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘dīgho kho bhikkhu kappo, so na sukaro saṅkhātuṃ ettakāni vassāni itivā, (peyyāla) ettakāni vassasatasahassāni itivāti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Gaṅgāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',200,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0200"></a> sammāsambuddhena anamataggasaṃyutte gaṅgāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante aññataraṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Aññataro bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘kīva-</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',201,replace(replace('<p class="bodytext">Bahukā <a name="M1.0201"></a> nu kho bho gotama kappā abbhatītā atikkantā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘bahukā kho brāhmaṇa kappā abbhatītā atikkantā, te na sukarā saṅkhātuṃ ‘ettakā kappā’ iti vā ‘ettakāni kappasatāni’ iti vā ‘ettakāni kappasahassāni’ itivā ‘ettakāni kappasatasahassāni’ iti vā’’ti eva mādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Puggalasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',203,replace(replace('<p class="bodytext">puggalasuttaṃ <a name="M1.0203"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha ‘‘anamataggoyaṃ bhikkhave saṃsāro, pubbākoṭi na paññāyati avijjā nīvaraṇānaṃ sattānaṃ taṇhāsaṃyojanānaṃ sandhāvataṃ saṃsarataṃ. Ekapuggalassa bhikkhave kappaṃ sandhāvato saṃsarato siyā evaṃ mahā aṭṭhikaṅkalo aṭṭhipuñjo aṭṭhirāsi, yathā yaṃ vepullo pabbato. Sace saṃhārako assa, sambhatañca na vinassetyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Duggatasutta, sukhitasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',204,replace(replace('<p class="bodytext">tatthāvuso <a name="M1.0204"></a> duggatasuttañca sukhitasuttañca bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘anamataggoyaṃ bhikkhave saṃsāro, pubbā koṭi na paññāyati avijjānīvaraṇānaṃ sattānaṃ taṇhāsaṃyojanānaṃ sandhāvataṃ saṃsarataṃ. Yaṃ bhikkhave passeyyātha duggataṃ durūpetaṃ, niṭṭhamettha gantabbaṃ ‘amhehipi evarūpaṃ paccanubhūtaṃ iminā dīghena addhunā’ti, yaṃ bhikkhave passeyyātha sukhitaṃ susajjitaṃ,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',205,replace(replace('<p class="bodytext">Niṭṭhamettha <a name="M1.0205"></a> gantabbaṃ, ‘‘amhehipi paccanubhūtaṃ iminā dīghena addhunā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tiṃsamattasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',206,replace(replace('<p class="bodytext">tiṃsamattasuttaṃ <a name="M1.0206"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante tiṃsamatte pāveyyake bhikkhū ārabbha ‘‘anamataggoyaṃ bhikkhave saṃsāro, pubbākoṭi na paññāyati avijjānīvaraṇānaṃ sattānaṃ taṇhāsaṃyojanānaṃ sandhāvataṃ saṃsarataṃ. Taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ, yaṃ vā vo iminā dīghena addhunā sandhāvataṃ saṃsarataṃ sīsacchinnānaṃ lohitaṃ passannaṃ paggharitaṃ, yaṃ vā catūsu mahāsamuddesu udaka’’nti evamādinā bhavagatā bhāsitaṃ.</p>\r\n<p class="subhead">Mātusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',209,replace(replace('<p class="bodytext">mātusuttādīni <a name="M1.0209"></a> panāvuso chasuttāni bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘anamataggoyaṃ bhikkhave saṃsāro, pubbā koṭi na paññāyati avijjānīvaraṇānaṃ sattānaṃ taṇhāsaṃyojanānaṃ sandhāvataṃ saṃsarataṃ. Na so bhikkhave satto sulabharūpo, yo na mātābhūtapubbo iminā dīghena addhunā. Yo na pitābhūtapubbo. Yo na bhātābhūtapubbo. Yo na bhaginibhūtapubbo. Yo na puttabhūtapubbo. Yo na </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',210,replace(replace('<p class="bodytext">dhītābhūtapubbo <a name="M1.0210"></a> iminā dīghena addhunā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Kassapasaṃyutta</p>\r\n<p class="subhead">Candūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',211,replace(replace('<p class="bodytext">kassapasaṃyutte <a name="M1.0211"></a> panāvuso candūpamasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘candūpamā bhikkhave kulāni upasaṅkamatha apakasseva kāyaṃ apakassa cittaṃ niccanavakā kulesu appagabbhā. Seyyathāpi bhikkhave puriso jarudapānaṃ vā olokeyya pabbatavisamaṃ vā nadīviduggaṃ vā apakasseva kāyaṃ apakassa cittaṃ. Evameva kho bhikkhave candūpamā kulāni upasaṅkamatha apakasseva kāyaṃ apakassa cittaṃ niccanavakā kulesu appagabbhā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',213,replace(replace('<p class="bodytext">Taṃ <a name="M1.0213"></a> kiṃ maññatha bhikkhave, kathaṃ rūpā bhikkhu arahati kulāni upasaṅkamituṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',214,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0214"></a> tattha bhagavatā parisuddhāparisuddha dhammadesanaṃ dassetvā bhikkhūnaṃ ovādo dinno.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘taṃ kiṃ maññatha bhikkhave, kathaṃ rūpassa bhikkhuno aparisuddhā dhammadesanā hoti, kathaṃ rūpassa bhikkhuno parisuddhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',215,replace(replace('<p class="bodytext">dhammadesanā <a name="M1.0215"></a> hotī’’ti evamādinā parisuddhāparisuddhadhammadesanā vitthārato dassetvā ‘‘kassapena vā hi vo bhikkhave ovadissāmi yo vā panassa kassapasadiso, ovaditehi ca pana vo tathattāya paṭipajjitabba’’nti. Evaṃ kho bhante bhagavato bhikkhūnaṃ ovādo dinno.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',216,replace(replace('<p class="bodytext">Yo <a name="M1.0216"></a> hi koci bhikkhave bhikkhu evaṃcitto paresaṃ dhammaṃ deseti ‘‘aho vata me dhammaṃ suṇeyyuṃ, sutvā ca pana dhammaṃ pasīdeyyuṃ, pasannā ca me pasannākāraṃ kareyyuṃ–</p>\r\n<p class="bodytext">Svākkhāto bhagavatā dhammo sandiṭṭhiko akāliko ehi passiko opaneyyiko paccattaṃ veditabbo viññūhi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',217,replace(replace('<p class="bodytext">Kassapena <a name="M1.0217"></a> vā hi vo bhikkhave ovadissāmi yo vā panassa kassapasadiso, ovaditehi ca pana vo tathattāya paṭipajjitabbaṃ–</p>\r\n<p class="subhead">Kulūpakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',218,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0218"></a> bhagavatā…pe… sammāsambuddhena kassapasaṃyutte catutthaṃ kulūpakasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, kathaṃ rūpo bhikkhu arahati kulūpako hotuṃ, kathaṃ rūpo bhikkhu na arahati kulūpako hotu’’nti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',222,replace(replace('<p class="bodytext">Kassapena <a name="M1.0222"></a> vā hi vo bhikkhave ovadissāmi yo vā panassa kassapasadiso, ovaditehi ca pana vo tathattāya paṭipajjitabbaṃ.</p>\r\n<p class="subhead">Dutiya ovādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',223,replace(replace('<p class="bodytext">tatthevāvuso <a name="M1.0223"></a> bhagavatā dutiya ovādasuttaṃ kattha kena saddhiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmatā mahākassapena saddhiṃ ‘‘ovada kassapa bhikkhū, karohi kassapa bhikkhūnaṃ dhammiṃ kathaṃ, ahaṃ vā kassapa bhikkhū ovadeyyaṃ tvaṃ vā, ahaṃ vā bhikkhūnaṃ dhammiṃ kathaṃ kareyyaṃ tvaṃ vā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',224,replace(replace('<p class="bodytext">Yassa <a name="M1.0224"></a> kassaci bhante saddhā natthi kusalesu dhammesu, hirī natthi. Ottappaṃ natthi. Vīriyaṃ natthi. Paññā natthi kusalesu dhammesu.</p>\r\n<p class="subhead">Tatiya ovādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',227,replace(replace('<p class="bodytext">tatthevāvuso <a name="M1.0227"></a> bhagavatā tatiyaovādasuttaṃ kattha kena saddhiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmatāyeva mahākassapena saddhiṃ ‘‘ovada kassapa bhikkhū. Karohi kassapa bhikkhūnaṃ dhammiṃ kathaṃ, ahaṃ vā kassapa bhikkhū ovadeyyaṃ tvaṃ vā. Ahaṃ vā bhikkhūnaṃ dhammaṃ kathaṃ kareyyaṃ tvaṃ vā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',228,replace(replace('<p class="bodytext">Dubbacā <a name="M1.0228"></a> kho bhante etarahi bhikkhū dovacassakaraṇehi dhammehi samannāgatā akkhamā appadakkhiṇaggāhino anusāsaniṃ–</p>\r\n<p class="subhead">Saddhammappatirūpakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',231,replace(replace('<p class="bodytext">tatthevāvuso <a name="M1.0231"></a> bhagavatā pariyosānaṃ saddhammappatirūpakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃyeva mahākassapaṃ ārabbha bhāsitaṃ. Āyasmā bhante mahākassapo bhagavantaṃ etadevoca ‘‘ko nu kho bhante hetu ko paccayo, yena pubbe appatarāni ceva sikkhāpadāni ahesuṃ, bahutarā ca bhikkhū aññāya saṇṭhahiṃsu. Ko pana bhante hetu ko paccayo, yenetarahi bahutarāni ceva sakkhāpadāni appatarā ca bhikkhū aññāya saṇṭhahantī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘evañcetaṃ kassapa hoti sattesu hāyamānesu saddhamme antaradhāyamāne bahutarāniceva </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',232,replace(replace('<p class="bodytext">sikkhāpadāni <a name="M1.0232"></a> honti, appatarāca bhikkhū aññāya saṇṭhahantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Lābhasakkārasaṃyutta</p>\r\n<p class="subhead">Mīḷhakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',235,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0235"></a> sammāsambuddhena lābhasakkāraṃyutte pañcamaṃ saṃgītaṃ mīḷhakasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘dāruṇo bhikkhave lābhasakkārasiloko pharuso antarāyiko anuttarassa yogakkhemassa adhigamāya. Seyyathāpi bhikkhave mīḷhakā gūthādī gūthapūrā puṇṇā gūthassa, purato cassa mahāgūthapuñjo, sā tena aññā mīḷhakā atimaññeyya ‘‘ahamhi gūthādī gūthapūrā puṇṇā gūthassa, purato ca myāyaṃ mahāgūthapuñjo’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',237,replace(replace('<p class="bodytext">Taṃ <a name="M1.0237"></a> hi tassa bhikkhave moghapurisassa hoti dīgharattaṃ ahitāya dukkhāya.</p>\r\n<p class="bodytext">Evaṃ dāruṇo kho bhikkhave lābhasakkārasiloko.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',238,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0238"></a> bhikkhave evaṃ sikkhitabbaṃ, ‘‘uppannaṃ lābhasakkārasi lokaṃ pajahissāma, na ca no uppanno lābhasakkārasiloko cittaṃ pariyādāya ṭhassabhī’’ti, evañhi kho bhikkhave sikkhitabbaṃ.</p>\r\n<p class="subhead">Ekaputtakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattho āvuso porāṇakehi dhammasaṃgāhakehi saṃgītaṃ ekaputtakasuttaṃ kattha kaṃ ārabbhaṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘dāruṇo bhikkhave lābhasakkārasiloko kaṭuko pharuso antarāyiko anuttarassa yogakkhemassa adhigamāya, saddhā bhikkhave upāsikā ekaputtakaṃ piyaṃ manāpaṃ evaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',239,replace(replace('<p class="bodytext">sammāāyācamānā <a name="M1.0239"></a> āyāceyya tādiso tāta bhavāhi, yādiso citto ca gahapati hatthako ca āḷavako’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',240,replace(replace('<p class="bodytext">Esā <a name="M1.0240"></a> bhikkhave tulā etaṃ pamāṇaṃ mama sāvakānaṃ upāsakānaṃ yadidaṃ citto ca gahapati hatthako ca āḷavako.</p>\r\n<p class="bodytext">Sace kho tvaṃ tāta agārasmā anagāriyaṃ pabbajasi, tādiso tāta bhavāhi, yādisā sāriputtamoggallānā.</p>\r\n<p class="bodytext">Mā ca kho tvaṃ tāta sekhaṃ appattamānasaṃ lābhasakkārasiloko anupāpuṇātu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',241,replace(replace('<p class="bodytext">Evaṃ <a name="M1.0241"></a> dāruṇo bhikkhave lābhasakkārasiloko kaṭuko pharuso antarāyiko anuttarassa yogakkhemassa adhigamāya.</p>\r\n<p class="subhead">Ekavītusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',242,replace(replace('<p class="bodytext">tattheva <a name="M1.0242"></a> āvuso porāṇakehi dhammasaṃgāyakehi saṃgītaṃ ekadhītusuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha ‘‘dāruṇo bhikkhave lābhasakkārasiloko kaṭuko pharuso antarāyiko anuttarassa yogakkhemassa adhigamāya. Saddhā bhikkhave upāsikā ekaṃ dhītaraṃ piyaṃ manāpaṃ evaṃ sammā āyācamānā āyāceyya ‘tādisā ayye bhavāhi, yādisā khujjuttarā ca upāsikā veḷukaṇḍakiyā ca nandamātā’ti. Esā bhikkhave tulā etaṃ pamāṇaṃ mama sāvikānaṃ upāsikānaṃ, yadidaṃ khujjuttarā ca upāsikā veḷukaṇḍakiyā ca nandamātā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',243,replace(replace('<p class="bodytext">Sace <a name="M1.0243"></a> kho tvaṃ ayye agārasmā anagāriyaṃ pabbajasi, bhādisā ayye bhavāhi, yādisā khemā ca bhikkhunī uppalavaṇṇā ca–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',244,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0244"></a> bhikkhave evaṃ sikkhitabbaṃ.</p>\r\n<p class="title">Rāhulasaṃyutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',245,replace(replace('<p class="bodytext">rāhulasaṃyuttaṃ <a name="M1.0245"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ rāhulaṃ ārabbha bhāsitaṃ. Āyasmā bhante rāhulo bhagavantaṃ etadavoca ‘‘sādhu me bhante bhagavā saṃkhittena dhammaṃ desetu, yamahaṃ bhagavato dhammaṃ sutvā eko vūpakaṭṭho appamatto ātāpī pahitatto vihareyya’’nti, tasmiṃ bhante vatthusmiṃ ‘‘taṃ kiṃ maññasi rāhula, cakkhuniccaṃ vā aniccaṃ vāti. Aniccaṃ bhante. Yaṃ panāniccaṃ, dukkhaṃ vā taṃ sukhaṃ vāti. Dukkhaṃ bhante. Yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ ‘etaṃ mama, eso hamasmi, eso me attā’ti. No hetaṃ bhante’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Lakkhaṇasaṃyutta</p>\r\n<p class="subhead">Aṭṭhisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',247,replace(replace('<p class="bodytext">lakkhaṇasaṃyutte <a name="M1.0247"></a> panāvuso porāṇakehi dhammasaṃgāhakattherehi paṭhamaṃ saṃgītaṃ aṭṭhisuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantañca lakkhaṇaṃ āyasmantañca mahāmoggallānaṃ ārabbha bhāsitaṃ. Āyasmā bhante lakkhaṇo bhagavatā sammukhe āyasmantaṃ mahāmoggallānaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',248,replace(replace('<p class="bodytext">etadavoca <a name="M1.0248"></a> ‘‘idhāyasmā mahāmoggallāno gijjhakūṭā pabbatā orohanto aññatarasmiṃ padese sitaṃ pātvākāsi, ko nu kho āvuso moggallāna hetu, ko paccayo sitassa pātukammāyā’’ti. Āyasmā ca bhante mahāmoggallāno ‘‘idhāhaṃ āvuso gijjhakūṭā pabbatā orohanto addasaṃ aṭṭhikasaṅkhalikaṃ vehāsaṃ gacchantiṃ’’ti evamādinā ārocesi. Tasmiṃ bhante vatthusmiṃ ‘‘cakkhubhūtā vata bhikkhave sāvakā viharanti, ñāṇabhūtā vata bhikkhave sāvakā viharanti, yatrahi nāma sāvako evarūpaṃ ñassati vā dakkhati vā sakkhiṃ vā karissatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',250,replace(replace('<p class="gatha1">Acchariyaṃ <a name="M1.0250"></a> vata bho, abbhutaṃ vata bho;</p>\r\n<p class="gathalast">Evarūpopi nāma satto bhavissati.</p>\r\n<p class="subhead">Piṇḍasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',251,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0251"></a> jānatā…pe… sammāsambuddhena lakkhaṇasaṃyutte tatiyaṃ saṃgītaṃ piṇḍasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantañca lakkhaṇaṃ āyasmantañca mahāmoggallānaṃ ārabbha bhāsitaṃ. Āyasmā bhante lakkhaṇo bhaggavato sammukhe āyasmantaṃ mahāmoggallānaṃ etadavoca ‘‘idhāyasmā mahāmoggallāno gijjhakūṭā pabbatā orohanto aññatarasmiṃ padese sitaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',252,replace(replace('<p class="bodytext">pātvākāsi<a name="M1.0252"></a>, ko nukho āvuso moggallānahetu, ko paccayo sitassa pātukammāyā’’ti. Āyasmā ca bhante mahāmoggallāno ‘‘idhāhaṃ āvuso gijjhakūṭā pabbatā orohanto addasaṃ maṃsapiṇḍaṃ vehāsaṃ gacchantaṃ, tamenaṃ gijjhāpi kākāpi kulalāpi anupatitvā anupatitvā vitacchenti virājenti. Sā sudaṃ aṭṭassaraṃ karotī’’ti evamādinā ārocesi. Tasmiṃ bhante vatthusmiṃ ‘‘cakkhubhūtā vata bhikkhave sāvakā viharanti, ñāṇabhūtā vata bhikkhave sāvakā viharanti, yatrahi nāma sāvako evarūpaṃ ñassati vā dakkhati vā sakkhiṃ vā karissatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Asilomasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',255,replace(replace('<p class="bodytext">tattheva <a name="M1.0255"></a> āvuso porāṇakehi dhammasaṃgāhakehi mahātherehi saṃgītaṃ asilomasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantañca lakkhaṇaṃ āyasmantañca mahāmoggallānaṃ ārabbha bhāsitaṃ. Āyasmā bhante lakkhaṇo bhagavato sammukhe āyasmantaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',256,replace(replace('<p class="bodytext">mahāmoggallānaṃ <a name="M1.0256"></a> etadavoca ‘‘ko nu kho āvuso moggallāna hetu, ko paccayo sitassa pātukammāyā’’ti. Āyasmā ca bhante mahāmoggallānatthero ‘‘idhāhaṃ āvuso gijjhakūṭā pabbatā orohanto addasaṃ asilomaṃ vehāsaṃ gacchantaṃ’’ti evamādinā ārocesi. Tasmiṃ bhante vatthusmiṃ cakkhubhūtā vata bhikkhave sāvakā viharanti, ñāṇabhūtā vata bhikkhave sāvakā viharanti. (Peyyāla) eso bhikkhave satto imasmiṃyeva rājagahe sūkariko ahosī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sūcilomasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',258,replace(replace('<p class="bodytext">tattheva <a name="M1.0258"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītaṃ sūcilomasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante rājagahe āyasmantañca lakkhaṇaṃ āyasmantañca mahāmoggallānaṃ ārabbha bhāsitaṃ. Āyasmā bhante lakkhaṇo bhagavato sammukhe tatheva avoca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',259,replace(replace('<p class="bodytext">Āyasmā <a name="M1.0259"></a> ca bhante mahāmoggallāno tatheva ārocesi. Tasmiṃ bhante vatthusmiṃ ‘‘cakkhubhūtā vata bhikkhave sāvakā viharanti, ñāṇabhūtā vata bhikkhave sāvakā viharanti, (peyyāla) eso bhikkhave satto imasmiṃyeva rājagahe sūto ahosī’’ti evamādinā bhagavatā bhāsitā.</p>\r\n<p class="subhead">Pāpabhikkhusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',261,replace(replace('<p class="bodytext">tattheva <a name="M1.0261"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītāni pāpabhikkhusuttādīni pañcasuttāni bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe āyasmantañca lakkhaṇaṃ āyasmantañca mahāmoggallānaṃ ārabbha bhāsitāni. Āyasmā bhante lakkhaṇo bhagavato sammukhe pubbe vuttanayeneva ārocesi. Āyasmā ca bhante mahāmoggallāno ‘‘idhāhaṃ āvuso gijjhakūṭā pabbatā orohanto addasaṃ bhikkhuṃ vehāsaṃ gacchantaṃ. Addasaṃ bhikkhuniṃ vehāsaṃ gacchantiṃ. Addasaṃ sikkhamānaṃ vehāsaṃ gacchantiṃ. Addasaṃ sāmaṇeraṃ vehāsaṃ gacchantaṃ. Addasaṃ sāmaṇeriṃ vehāsaṃ gacchantiṃ. Tassā saṅghāṭipi ādittā sampajjalitā sajotibhūtā, pattopi āditto sampajjalito sajotibhūto, kāyabandhanampi ādittaṃ sampajjalitaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',262,replace(replace('<p class="bodytext">sajotibhūtaṃ<a name="M1.0262"></a>, kāyopi āditto sampajjalito sajotibhūto, sā sudaṃ aṭṭassaraṃ karotī’’ti evamādinā ārocesi. Tasmiṃ bhante vatthusmiṃ ‘‘cakkhubhūtā vata bhikkhave sāvakā viharanti ñāṇabhūtā vata bhikkhave sāvakā viharanti. (Peyyāla). Eso bhikkhave bhikkhu kassapassasammāsambuddhassa pāvacane pāpabhikkhu ahosi. Esā bhikkhave bhikkhunī kassapassa sammāsambuddhassa pāvacane pāpabhikkhunī ahosi. Esā bhikkhave sikkhamānā kassapassa sammāsambuddhassa pāvacane pāpasikkhamānā ahosi. Eso bhikkhave sāmaṇero kassapassasammāsambuddhassa pāvacane pāpasāmaṇero ahosi. Esā bhikkhave sāmaṇerī kassapassa sammāsambuddhassa pāvacane pāpasāmaṇerī ahosī’’ti evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="title">Opammasaṃyutta</p>\r\n<p class="subhead">Nakhasikhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',264,replace(replace('<p class="bodytext">opammasaṃyutte <a name="M1.0264"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītaṃ nakhasikhasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ, yo cāyaṃ mayā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',265,replace(replace('<p class="bodytext">paritto <a name="M1.0265"></a> nakhasikhāyaṃ paṃsu āropito, yā cāyaṃ mahāpathavī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',266,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0266"></a> bhikkhave evaṃ sikkhitabbaṃ.</p>\r\n<p class="subhead">Āṇisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītaṃ āṇisuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha ‘‘bhūtapubbaṃ bhikkhave dasārahānaṃ ānako nāma mudiṅgo ahosi, tassa dasārahā ānake ghaṭite aññaṃ āṇiṃ odahiṃsu, ahu kho so bhikkhave samayo yaṃ ānakassa mudiṅgassa porāṇaṃ pokkharaphalakaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',267,replace(replace('<p class="bodytext">antaradhāyi<a name="M1.0267"></a>, āṇisaṅghāṭova avasissī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Bhikkhusaṃyutta</p>\r\n<p class="subhead">Navasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',269,replace(replace('<p class="bodytext">bhikkhusaṃyutte <a name="M1.0269"></a> āvuso bhagavatā navasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ navaṃ bhikkhuṃ ārabbha bhāsitaṃ. Aññataro bhante navo bhikkhu pacchābhattaṃ piṇḍapātapaṭikkanto vihāraṃ pavisitvā apposukko tuṇhībhūto saṅkasāyabhi, na bhikkhūnaṃ veyyāvaccaṃ karoti cīvarakārasamaye tasmiṃ bhante vatthusmiṃ ‘‘mā kho tumhe bhikkhave etassa bhikkhuno ujjhāyitthā eso kho bhikkhave bhikkhu catunnaṃ jhānānaṃ ābhicetasikānaṃ diṭṭhadhamma sukhavihārānaṃ nikāmalābhī akicchalābhī akasiralābhī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',270,replace(replace('<p class="bodytext">Ahampi <a name="M1.0270"></a> kho bhante sakaṃ kiccaṃ karomi,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',271,replace(replace('<p class="bodytext">Eso <a name="M1.0271"></a> kho bhikkhave bhikkhu catunnaṃ jhānānaṃ ābhicetasikānaṃ diṭṭhadhammasukhavihārānaṃ nikāmalābhī akicchalābhī akasiralābhī –</p>\r\n<p class="gatha1">Nayidaṃ sithilamārabbha, nayidaṃ appena thāmasā;</p>\r\n<p class="gatha2">Nibbānaṃ adhigantabbaṃ, sabbadukkhappamocanaṃ;</p>\r\n<p class="gatha3">Ayañca daharo bhikkhu, ayamuttamapuriso;</p>\r\n<p class="gathalast">Dhāreti antimaṃ dehaṃ, jetvā māraṃ savāhiniṃ–</p>\r\n<p class="chapter">Khandhavaggasaṃyuttapāḷi</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="subhead">Nakulapitusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',1,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0001"></a> bhagavatā jānatā…pe… sammāsambuddhena khandhavaggasaṃyutte paṭhamaṃ nakulapitusuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhaggesu bhante susumāragire bhesakaḷāvane migadāye nakulapitaraṃ gahapatiṃ ārabbha bhāsitaṃ. Nakulapitā bhante gahapati bhagavantaṃ ‘‘ahamasmi bhante jiṇṇo vuḍḍho mahallako addhagato vayoanuppatto āturakāyo abhikkhaṇātaṅko, aniccadassāvī kho panāhaṃ bhante bhagavato manobhāvanīyānañca bhikkhūnaṃ, ovadatu maṃ bhante bhagavā, anusāsatu maṃ bhante bhagavā, yaṃ mamassa dīgharattaṃ hitāya sukhāyā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘evametaṃ gahapati, evametaṃ gahapati, āturo hāyaṃ gahapati kāyo aṇḍabhūto pariyonaddho, yo hi gahapati imaṃ kāyaṃ pariharanto muhuttampi ārogyaṃ paṭijāneyya, kimaññatra bālyā. Tasmātiha te gahapati evaṃ sikkhitabbaṃ ‘āturakāyassa me sato cittaṃ anāturaṃ bhavissatī’ti. Evañhi te gahapati sikkhitabba’’nti. Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',3,replace(replace('<p class="bodytext">Tasmātiha <a name="M2.0003"></a> te gahapati evaṃ sikkhitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',4,replace(replace('<p class="bodytext">tañcāvuso <a name="M2.0004"></a> bhagavatā saṃkhittena bhāsitaṃ vitthārena kena kathañca vibhattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kho bhante bhagavatā saṃkhittena desitaṃ ‘‘kathañca gahapati āturakāyo ceva hoti āturacitto ca. Idha gahapati assutavā puthujjano ariyānaṃ adassāvī ariyadhammassa akovido ariyadhamme avinīto, sappurisānaṃ adassāvī sappurisadhammassa akovido sappurisadhamme avinīto rūpaṃ attato samanupassati rūpavantaṃ vā attānaṃ, attani vā rūpaṃ, rūpasmiṃ vā attānaṃ, ‘‘ahaṃ rūpaṃ mama rūpa’’nti pariyuṭṭhaṭṭhāyī hoti. Tassa ‘‘ahaṃ rūpaṃ mama rūpa’’nti pariyuṭṭhaṭṭhāyino taṃ rūpaṃ vipariṇamati aññathā hoti. Tassa rūpavipariṇāmaññathābhāvā uppajjanti sokaparidevadukkhadomanassupāyāsā’’ti evamādinā bhante āyasmatā sāriputtena dhammasenāpatinā vitthārena vibhattaṃ.</p>\r\n<p class="subhead">Aniccasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',9,replace(replace('<p class="bodytext">tattheva <a name="M2.0009"></a> āvuso dutiyavagge porāṇakehi dhammasaṃgāhaka mahātherehi paṭhamaṃ saṃgītaṃ aniccasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘rūpaṃ bhikkhaveaniccaṃ, vedanā aniccā, saññā aniccā, saṅkhārā aniccā, viññāṇaṃ aniccaṃ. Evaṃ passaṃ bhikkhave sutavā ariyasāvako rūpasmimpi nibbindati, vedanāyapi nibbindati, saññāyapi nibbindati, saṅkhāresupi nibbindati, viññāṇasmimpi nibbindati, nibbindaṃ virajjati, virāgā vimuccati, vimuttasmiṃ ‘vimutta’miti ñāṇaṃ hotī’’ti. Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dukkhaanattasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',10,replace(replace('<p class="bodytext">dukkhaanattasuttāni <a name="M2.0010"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha ‘‘rūpaṃ bhikkhave dukkhaṃ, vedanā dukkhā, saññā dukkhā, saṅkhārā dukkhā, viññāṇaṃ dukkhaṃ. Rūpaṃ bhikkhave anattā, vedanā anattā, saññā anattā, saṅkhārā anattā, viññāṇaṃ anattā. Evaṃ passaṃ bhikkhave sutavā ariyasāvako rūpasmimpi nibbindati, vedanāyapi nibbindati, saññāyapi nibbindati, saṅkhāresupi nibbindati, viññāṇasmimpi nibbindati, nibbindaṃ virajjati, virāgā vimuccati, vimuttasmiṃ ‘vimutta’miti ñāṇaṃ hoti, khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyāti pajānātī’’ti evaṃ kho bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Bhārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',11,replace(replace('<p class="bodytext">bhārasuttaṃ <a name="M2.0011"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha ‘‘bhārañca vo bhikkhave desessāmi bhārahārañca bhārādānañca bhāranikkhepanañca, taṃ suṇātha. Katamo ca bhikkhavebhāro, ‘pañcupādānakkhandhā’ tissa vacanīyaṃ. Katame pañca, rūpupādānakkhandho vedanupādānakkhandho saññupādānakkhandho saṅkhārupādānakkhandho viññāṇupādānakkhandho. Ayaṃ vuccati bhikkhave bhāro’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',13,replace(replace('<p class="gatha1">Bhārā <a name="M2.0013"></a> have pañcakkhandhā, bhārahāro ca puggalo;</p>\r\n<p class="gatha2">Bhārādānaṃ dukhaṃ loke, bhāranikkhepanaṃ sukhaṃ;</p>\r\n<p class="gatha3">Nikkhipitvā garuṃ bhāraṃ, aññaṃ bhāraṃ anādiya;</p>\r\n<p class="gathalast">Samūlaṃ taṇhamabbuyha, nicchāto parinibbuto.</p>\r\n<p class="subhead">Natumhākasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> na tumhākasuttaṃ panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhu ārabbha ‘‘yaṃ bhikkhave natumhākaṃ taṃ pajahatha, taṃ vo pahīnaṃ hitāya sukhāyabhavissatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Anattalakkhaṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',15,replace(replace('<p class="bodytext">anattalakkhaṇasuttaṃ <a name="M2.0015"></a> panāvuso bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante isipatane migadāye pañcavaggiye bhikkhū ārabbha ‘‘rūpaṃ bhikkhave anattā, rūpañca hidaṃ bhikkhave attā abhavissa, nayidaṃ rūpaṃ ābādhāya saṃvatteyya, labbhetha ca rūpe evaṃ me rūpaṃ hotu, evaṃ me rūpaṃ mā ahosī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Yamakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> theravagge panāvuso tatiyaṃ saṃgītaṃ yamakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ yamakattheraṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Āyasmato bhante yamakattherassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ hoti ‘‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi, yathā khīṇāsavo bhikkhu kāyassa bhedā ucchijjati vinassati, na hoti paraṃmaraṇā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘taṃ kiṃ maññasi āvuso yamaka, rūpaṃ niccaṃ vā aniccaṃ vā’’ti evamādinā bhante āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',18,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0018"></a> tattha āyasmā sāriputtatthero dhammasenāpati āyasmato yamakattherassa paṭividdhasaccassa diṭṭhisampannassa anuyogavattajhāpanavasena paṭipucchitvā uttari dhammadesanaṃ vitthārena desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kiṃ maññasi āvuso yamaka, rūpaṃ ‘tathāgato’ti samanupassasīti. ‘No hetaṃ āvuso’. Vedanaṃ. Saññaṃ. Saṅkhāre. Viññāṇaṃ ‘tathāgato’ti samanupassasīti. ‘No hetaṃ āvuso’ti evamādinā bhante āyasmā sāriputtatthero dhammasenāpati āyasmato yamakattherassa anuyogavattajhāpanavasena paṭipucchitvā paṭipucchitvā uttari dhammadesanaṃ pavattesi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',20,replace(replace('<p class="bodytext">Taṃ <a name="M2.0020"></a> kiṃ maññasi āvuso yamaka, rūpaṃ, vedanaṃ, saññaṃ, saṅkhāre, viññāṇaṃ ‘tathāgato’ti samanupassasi–</p>\r\n<p class="subhead">Vakkalisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',22,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0022"></a> bhagavatā…pe… sammāsambuddhena khandhavaggasaṃyutte theravagge pañcamaṃ vakkalisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ vakkaliṃ theraṃ ārabbha bhāsitaṃ. Āyasmā bhante vakkalithero ābādhiko dukkhito bāḷhagilāno bhagavantaṃ etadavoca ‘‘cirapaṭikāhaṃ bhante bhagavantaṃ dassanāya upasaṅkamitukāmo, natthi ca me kāyasmiṃ tāvatikā balamattā, yāvatāhaṃ bhagavantaṃ dassanāya upasaṅkameyya’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘alaṃ vakkali, kiṃ te iminā pūtikāyena diṭṭhena, yo kho vakkali dhammaṃ passati, so maṃ passati, yo maṃ passati, so dhammaṃ passati. Dhammañhi vakkali passanto maṃ passati, maṃ passanto dhammaṃ passati. Taṃ kiṃ maññasi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',23,replace(replace('<p class="bodytext">vakkali<a name="M2.0023"></a>, rūpaṃ niccaṃ vā aniccaṃ vā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',26,replace(replace('<p class="bodytext">‘‘Ahaṃ <a name="M2.0026"></a> vakkali, kiṃ te iminā pūtikāyena diṭṭhena’’ –</p>\r\n<p class="bodytext">‘‘Taṃ kiṃ maññasi vakkali, rūpaṃ niccaṃ vā aniccaṃ vā’’ –</p>\r\n<p class="bodytext">Aniccaṃ bhante.</p>\r\n<p class="bodytext">‘‘Yaṃ panāniccaṃ, dukkhaṃ vā taṃ sukhaṃ vā’’–</p>\r\n<p class="bodytext">Dukkhaṃ bhante.</p>\r\n<p class="bodytext">Yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ ‘‘etaṃ mama, esohamasmi, eso me attā’’–</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',27,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0027"></a> punapi bhagavā bhikkhū pesetvā āyasmato vakkalittherassa paggahavacanaṃ ārocāpesi. Sopi kathaṃ attano pavattiṃ bhagavato paccārocāpesi. Kathañcassa abhisamparāyo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> suṇāvuso tvaṃ vakkali bhagavato vacanaṃ dvinnañca devatānaṃ, imaṃ āvuso rattiṃ dve devatāyo abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṃ gijjhakūṭaṃ obhāsetvā yena</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',28,replace(replace('<p class="bodytext">Bhagavā <a name="M2.0028"></a> tenupasaṅkamiṃsu, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhaṃsu, ekamantaṃ ṭhitā kho āvuso ekā devatā bhagavantaṃ etadavoca ‘vakkali bhante bhikkhu vimokkhāya cetetī’ti. Aparā devatā bhagavantaṃ etadavoca ‘so hi nūna bhante suvimutto vimuccissatī’ti. Bhagavā ca taṃ āvuso vakkali evamāha ‘mā bhāyi vakkali, mā bhāyi vakkali, apāpakaṃ te maraṇaṃ bhavissati, apāpikā kālakiriyā’ti. Evaṃ kho bhante bhagavā punapi bhikkhū pesetvā āyasmato vakkalittherassa paggahavacanaṃ ārocāpesi. Sopi bhante āyasmā tenahāvuso mama vacanena bhagavato pāde sirasā vandatha ‘‘vakkali bhante bhikkhu ābādhiko dukkhito bāḷhagilāno, so bhagavato pāde sirasā vandatī’’ti, evañca vadetha ‘‘rūpaṃ aniccaṃ, tāhaṃ bhante na kaṅkhāmi, yadaniccaṃ taṃ dukkhanti navicikicchāmi, yadaniccaṃ dukkhaṃ vipariṇāmadhammaṃ, natthi me tattha chando vā rāgo vā pemaṃvāti na vicikicchāmī’’ti evamādinā bhagavato attano pavattiṃ paccārocāpesi. So hi bhante āyasmā acirapakkantesu tesu bhikkhūsu satthaṃ āharitvā vedanaṃ vikkhambhetvā mūlakammaṭṭhānaṃ ādāya sampajāno arahattaṃ sacchikatvā kālamakāsi. Evaṃ kho bhante tassa āyasmato abhisamparāyo ahosi.</p>\r\n<p class="subhead">Assajisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',33,replace(replace('<p class="bodytext">assajisuttaṃ <a name="M2.0033"></a> panāvuso bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ assajiṃ ārabbha bhāsitaṃ. Āyasmā bhante assaji ābādhiko dukkhito bāḷhagilāno bhagavantaṃ etadavoca ‘‘pubbe khvāhaṃ bhante gelaññe passambhetvā passambhetvā kāyasaṅkhāre viharāmi, sohaṃ samādhiṃ nappaṭilabhāmi. Tassa mayhaṃ bhante taṃ samādhiṃ appaṭilabhato evaṃ hoti no cassāhaṃ parihāyāmī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘ye te assaji samaṇabrāhmaṇā samādhisārakā samādhisāmaññā, tesaṃ taṃ samādhiṃ appaṭilabhataṃ evaṃ hoti ‘no cassu mayaṃ parihāyāmā’ti. Taṃ kiṃ maññasi assaji, rūpaṃ niccaṃ vā aniccaṃ vā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Khemakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',36,replace(replace('<p class="bodytext">khemakasuttaṃ <a name="M2.0036"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante sambahule there bhikkhū ārabbha āyasmatā khemakattherena bhāsitaṃ. Sambahulā bhante therā bhikkhū āyasmantaṃ dāsakaṃ pesetvā catukkhattuṃ āyasmantaṃ khemakaṃ ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ gelaññakāraṇañca dhammañca </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',37,replace(replace('<p class="bodytext">pucchiṃsu<a name="M2.0037"></a>, tasmiṃ bhante vatthusmiṃ ‘‘pañcime āvuso upādānakkhandhā vuttā bhagavatā. Seyyathidaṃ, rūpupādānakkhandho vedanupādānakkhandho saññupādānakkhandho saṅkhārupādānakkhandho viññāṇupādānakkhandho, imesu khvāhaṃ āvuso pañcasu upādānakkhandhesu na kiñci attaṃ vā attaniyaṃ vā samanupassāmī’’ti evamādinā bhante āyasmatā khemakattherena bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',40,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0040"></a> tattha āyasmā khemako uttari dhammadesanaṃ pavaḍḍhetvā vitthārena therānaṃ bhikkhūnaṃ desesi. Kīdiso ca nesaṃ dhammadesakadhammappaṭiggāhakānaṃ dhammadesanāya ānisaṃso adhigato.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catutthe bhante vāre āyasmā khemako ‘‘alaṃ āvuso dāsaka kiṃ imāya sandhāvanikāya, āharāvuso daṇḍaṃ, ahameva therānaṃ santikaṃ gamissāmī’’ti daṇḍamolubbha āyasmā khemako yena therā bhikkhū tenupasaṅkami, upasaṅkamitvā therehi bhikkhūhi pucchito ‘‘nakhvāhaṃ āvuso rūpaṃ asmīti vadāmi, napi aññatra rūpā asmīti vadāmi. Na khvāhaṃ āvuso vedanaṃ asmīti vadāmi, napi aññatra vedanā asmīti vadāmī’’ti evamādinā bhante āyasmā khemako uttaripi dhammadesanaṃ pavaḍḍhetvā vitthārena desesi. Imasmiñca pana bhante veyyākaraṇasmiṃ bhaññamāne saṭṭhimattānaṃ therānaṃ bhikkhūnaṃ anupādāya āsavehi cittāni vimucciṃsu āyasmatoca khemakassa. Evaṃ kho bhante tesaṃ therānaṃ dhammadesakadhammappaṭiggāhakānaṃ dhammābhisamayo ahosi.</p>\r\n<p class="subhead">Channasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',44,replace(replace('<p class="bodytext">channasuttaṃ <a name="M2.0044"></a> panāvuso kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃ channattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante channatthero āyasmantaṃ ānandaṃ etadavoca ‘‘ovadatu maṃ āyasmā ānando, anusāsatu maṃ āyasmā ānando, karotu me āyasmā ānando dhammiṃ kathaṃ, yathāhaṃ dhammaṃ passeyya’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘sammukhā me taṃ āvuso channa bhagavato sutaṃ, sammukhā paṭiggahitaṃ kaccānagottaṃ bhikkhuṃ ovadantassa, dvayanissito khvāyaṃ kaccāna loko yebhuyyena atthitañceva natthitañca, lokasamudayaṃ kho kaccāna yathābhūtaṃ sammappaññāya passato yā loke natthitā, sā na hotī’’ti evamādinā bhante āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',46,replace(replace('<p class="bodytext">Ovadatu <a name="M2.0046"></a> maṃ āyasmā ānando, anusāsatu maṃ āyasmā ānando.</p>\r\n<p class="subhead">Pupphasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',47,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M2.0047"></a> sammāsambuddhena khandhavaggasaṃyutte pupphavagge pupphasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘nāhaṃ bhikkhave lokena vivadāmi, lokova mayā vivadati, na bhikkhave </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',48,replace(replace('<p class="bodytext">dhammavādī <a name="M2.0048"></a> kenaci lokasmiṃ vivadati, yaṃ bhikkhave natthi sammataṃ loke paṇḍitānaṃ, ahampitaṃ ‘‘natthī’’ti vadāmi, yaṃ bhikkhave atthisammataṃ loke paṇḍitānaṃ, ahampitaṃ ‘‘atthī’’ti vadāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Nāhaṃ bhikkhave lokena vivadāmi.</p>\r\n<p class="bodytext">Na bhikkhave dhammavādī kenaci lokasmiṃ vivadati.</p>\r\n<p class="subhead">Pheṇapiṇḍūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',51,replace(replace('<p class="bodytext">tattheva <a name="M2.0051"></a> āvuso pheṇapiṇḍūpamasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ayujjhāyaṃ bhante gaṅgāya nadiyā tīre sambahule bhikkhū ārabbha ‘‘seyyathāpi bhikkhave ayaṃ gaṅgā nadī mahantaṃ pheṇapiṇḍaṃ āvaheyya, tamenaṃ cakkhumā puriso passeyya nijjhāyeyya yoniso upaparikkheyya, tassa taṃ passato nijjhāyato yoniso upaparikkhato rittakaññeva khāyeyya, tucchakaññeva khāyeyya, asārakaññeva khāyeyya, kiṃ siyā bhikkhave pheṇapiṇḍe sāro. Evameva kho bhikkhave yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ ajjhattaṃvā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā, taṃ bhikkhu passati nijjhāyati yoniso upaparikkhati, tassa taṃ passato nijjhāyato yoniso upaparikkhato rittakaññeva khāyati, tucchakaññeva khāyati, asārakaññeva khāyati, kiñhi siyā bhikkhave rūpe sāro’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',54,replace(replace('<p class="gatha1">Pheṇapiṇḍūpamaṃ <a name="M2.0054"></a> rūpaṃ, vedanā pubbuḷupamā;</p>\r\n<p class="gatha2">Marīcikūpamā saññā, saṅkhārā kadalūpamā;</p>\r\n<p class="gathalast">Māyūpamañca viññāṇaṃ, desitādiccabandhunā;</p>\r\n<p class="gatha1">Yathā yathā nijjhāyati, yoniso upaparikkhati;</p>\r\n<p class="gathalast">Rittakaṃ tucchakaṃ hoti, yo naṃ passati yoniso.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',55,replace(replace('<p class="gatha1">Imañca <a name="M2.0055"></a> kāyaṃ ārabbha, bhūripaññena desitaṃ;</p>\r\n<p class="gathalast">Pahānaṃ tiṇṇaṃ dhammānaṃ, rūpaṃ passatha chaḍḍitaṃ.</p>\r\n<p class="gatha1">Āyu usmāca viññāṇaṃ, yadā kāyaṃ jahantimaṃ;</p>\r\n<p class="gathalast">Apaviddho tadā seti, parabhattaṃ acetanaṃ.</p>\r\n<p class="gatha1">Etādisāyaṃ santāno, māyāyaṃ bālalāpinī;</p>\r\n<p class="gathalast">Vadhako esa akkhāto, sāro ettha na vijjati.</p>\r\n<p class="gatha1">Evaṃ khandhe avekkheyya, bhikkhu āraddhavīriyo;</p>\r\n<p class="gathalast">Divā vā yadi vā rattiṃ, sampajāno paṭissato.</p>\r\n<p class="gatha1">Jaheyya sabbasaṃyogaṃ, kareyya saraṇattano;</p>\r\n<p class="gathalast">Careyyādittasīsova, patthayaṃ accutaṃ padaṃ.</p>\r\n<p class="subhead">Dutiyadhammakatikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso dhammakathikavagge dutiyadhammakathikasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha bhāsitaṃ. Aññatamo bhante bhikkhu bhagavantaṃ etadavoca ‘‘dhammakathiko dhammakathikoti bhante vuccati, kittāvatā nu kho bhante dhammakathiko hoti, kittāvatā dhammānudhammappaṭipanno hoti, kittāvatā diṭṭhadhammanibbānappatto hotī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘rūpassa ce bhikkhu nibbidāya virāgāya nirodhāya dhammaṃ deseti, ‘dhammakathiko bhikkhū’ti alaṃ vacanāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sīlavantasutta, sutavantasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',57,replace(replace('<p class="bodytext">tattheva <a name="M2.0057"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītaṃ sīlavantasuttañca sutavantasuttañca kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',58,replace(replace('<p class="bodytext">bārāṇasiyaṃ <a name="M2.0058"></a> bhante āyasmantaṃ mahākoṭṭhikaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Āyasmā bhante mahākoṭṭhiko sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā sāriputto tenupasaṅkami, upasaṅkamitvā āyasmantaṃ sāriputtaṃ etadavoca ‘‘sīlavatāvuso sāriputta bhikkhunā katame dhammā yoniso manasikātabbā. Sutāvatāvuso sāriputta bhikkhunā katame dhammā yoniso manasikātabbā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘sīlavatāvuso koṭṭhika bhikkhunā, sutāvatāvuso koṭṭhika bhikkhunā pañcupādānakkhandhā aniccato dukkhato rogato gaṇḍato sallato aghato ābādhato parato palokato suññato anattato yoniso manasikātabbā’’ti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="title">Rādhasaṃyutta</p>\r\n<p class="subhead">Sattasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',61,replace(replace('<p class="bodytext">rādhasaṃyutte <a name="M2.0061"></a> panāvuso dutiyaṃ sattasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ rādhattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante rādhatthero bhagavantaṃ etadavoca ‘‘satto sattoti bhante vuccati, kittāvatā nu kho bhante sattoti vuccatī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘rūpe kho rādha yo chando yo rāgo yā nandī yā taṇhā, tatra satto tatra visatto, tasmā sattoti vuccatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Diṭṭhisaṃyutta</p>\r\n<p class="subhead">Soattāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',63,replace(replace('<p class="bodytext">diṭṭhisaṃyutte <a name="M2.0063"></a> panāvuso tatiyaṃ saṃgītaṃ soattā suttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘kismiṃ nu kho bhikkhave sati kiṃ upādāya kiṃ abhinivissa evaṃ diṭṭhi uppajjati so attā so loko so pecca bhavissāmi nicco dhuvo sassato avipariṇāmadhammo’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Natthidinnasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',65,replace(replace('<p class="bodytext">tattheva <a name="M2.0065"></a> āvuso pañcamaṃ natthidinnasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha ‘‘kismiṃ nu kho bhikkhave sati kiṃ upādāya kiṃ abhinivissa evaṃ diṭṭhi uppajjati natthi dinnaṃ, natthi yiṭṭhaṃ, natthi hutaṃ, natthi sukatadukkaṭānaṃ kammānaṃ phalaṃ vipāko’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Okkantasaṃyutta</p>\r\n<p class="subhead">Cakkhusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',66,replace(replace('<p class="bodytext">okkantasaṃyutte <a name="M2.0066"></a> panāvuso paṭhamaṃ cakkhusuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',67,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0067"></a> bhante sambahule bhikkhū ārabbha ‘‘cakkhuṃ bhikkhave aniccaṃ vipariṇāmi aññathābhāvi. Sotaṃ. Ghānaṃ. Jivhaṃ. Kāyo. Mano anicco vipariṇāmī aññathābhāvī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Sāriputtasaṃyutta</p>\r\n<p class="subhead">Sucimukhīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',68,replace(replace('<p class="bodytext">sāriputtasaṃyutte <a name="M2.0068"></a> panāvuso dasamaṃ sucimukhīsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sucimukhiṃ paribbājikaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Sucimukhī bhante paribbājikā āyasmantaṃ sāriputtaṃ etadavoca ‘‘kiṃ nu kho samaṇa adhomukho bhuñjasī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘na khvāhaṃ bhagini adhomukho bhuñjāmī’’ti evamādinā paṭikkhipitvā ‘‘ye hi keci bhagini samaṇabrāhmaṇā vatthuvijjā tiracchānavijjāya micchājīvena jīvikaṃ kappenti, ime vuccanti bhagini samaṇabrāhmaṇā adhomukhā bhuñjantī’’ti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',69,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0069"></a> nu kho samaṇa adho mukho bhuñjasi.</p>\r\n<p class="bodytext">Na khvāhaṃ bhagini adhomukho bhuñjāmi.</p>\r\n<p class="bodytext">Tena hi samaṇa ubbhamukho bhuñjasi.</p>\r\n<p class="bodytext">Na khvāhaṃ bhagini ubbhamukho bhuñjāmi.</p>\r\n<p class="chapter">Saḷāyatanavaggasaṃyuttapāḷi</p>\r\n<p class="subhead">Ajjhattāniccasutta, bāhirasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',73,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0073"></a> bhagavatā…pe… sammāsambuddhena saḷāyatanavaggasaṃyutte paṭhamaṃ ajjhattāniccasuttañca catutthaṃ bāhirāniccasuttañca kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘cakkhuṃ bhikkhave aniccaṃ, yadaniccaṃ taṃ dukkhaṃ, yaṃ dukkhaṃ tadanattā, yadanattā taṃ ‘netaṃ mama, nesohamasmi, na meso attā’ti evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabba’’nti evamādināca. ‘‘Rūpā bhikkhave aniccā. Yadaniccaṃ taṃ dukkhaṃ, yaṃ dukkhaṃ tadanattā, yadanattā taṃ netaṃ mama, nesohamasmi, na meso attā’’ti ca evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Sabbavagga</p>\r\n<p class="subhead">Ādittasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',74,replace(replace('<p class="bodytext">tatthāvuso <a name="M2.0074"></a> sabbavagge chaṭṭhaṃ ādittasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',75,replace(replace('<p class="bodytext">gayāyaṃ <a name="M2.0075"></a> bhante gayāsīse purāṇajaṭilaṃ bhikkhusahassaṃ ārabbha ‘‘sabbaṃ bhikkhave ādittaṃ, kiñca bhikkhave sabbaṃ ādittaṃ. Cakkhu bhikkhave ādittaṃ, rūpā ādittā, cakkhuviññāṇaṃ ādittaṃ, cakkhusamphasso āditto, yampidaṃ cakkhusamphassapaccayā uppajjati, vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃvā, tampi ādittaṃ. Kena ādittaṃ. Rāgagginā dosagginā mohagginā ādittaṃ, jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittanti vadāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Migajālavagga</p>\r\n<p class="subhead">Paṭhama migajālasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',76,replace(replace('<p class="bodytext">migajālavagge <a name="M2.0076"></a> āvuso paṭhamamigajālasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',77,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0077"></a> bhante āyasmantaṃ migajālattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante migajālatthero bhagavantaṃ etadavoca ‘‘ekavihārī ekavihārīti bhante vuccati, kittāvatānukho bhante ekavihārī hoti, kittāvatāca pana sadutiyavihārī hotī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘santi kho migajāla cakkhuviññeyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā, tañce bhikkhu abhinandati abhivadati ajjhosāya tiṭṭhati, tassa taṃ abhinandato abhivadato ajjhosāya tiṭṭhato uppajjati nandī, nandiyā sati sārāgo hoti, sārāge sati saṃyogo hoti, nandisaṃyojanasaṃyutto kho migajāla bhikkhu sadutiyavihārīti vuccatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhama chaphassāyatanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',79,replace(replace('<p class="bodytext">tattheva <a name="M2.0079"></a> navamaṃ saṃgītaṃ paṭhamachaphassāyatanasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha ‘‘yo hi koci vikkhave bhikkhu channaṃ phassāyatanānaṃ samudayañca atthaṅgamañca assādañca ādīnavañca nissaraṇañca yathābhūtaṃ nappajānāti, avusitaṃ tena brahmacariyaṃ, ārakā so imasmā dhammavinayā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',80,replace(replace('<p class="bodytext">Ahaṃ <a name="M2.0080"></a> hi bhante channaṃ phassāyatanānaṃ samudayañca atthaṅgamañca assādañca ādīnavañca nissaraṇañca yathābhūtaṃ nappajānāmi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',81,replace(replace('<p class="bodytext">Cakkhuṃ <a name="M2.0081"></a> etaṃ mama, esohamasmi, eso me attāti samanupassasi.</p>\r\n<p class="bodytext">No hetaṃ bhante.</p>\r\n<p class="bodytext">Ettha ca te bhikkhu cakkhu netaṃ mama, nesohamasmi, na meso attāti evametaṃ yathābhūtaṃ sammappaññāya sudiṭṭhaṃ bhavissati, esevanto dukkhassa.</p>\r\n<p class="title">Gilānavagga</p>\r\n<p class="subhead">Paṭhama gilānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',82,replace(replace('<p class="bodytext">gilānavagge <a name="M2.0082"></a> panāvuso paṭhamaṃ gilānasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ navaṃ bhikkhuṃ gilānaṃ ābādhitaṃ dukkhitaṃ bāḷhagilānaṃ ārabbha bhāsitaṃ. Aññataro bhante bhikkhu gilāno ābādhiko dukkhito bāḷhagilāno bhagavantaṃ etadavoca ‘‘nakhvāhaṃ bhante sīlavisuddhatthaṃ bhagavatā dhammaṃ desitaṃ ājānāmī’’ti. Tasmiṃ bhante vatthusmiṃ taṃ bhikkhuṃ paṭipucchitvā tassa ca vacanaṃ sādhukāraṃ datvā ‘‘rāgavirāgattho hi bhikkhu mayā dhammo desito, taṃ kiṃ maññasi bhikkhu, cakkhu niccaṃ vā aniccaṃ vāti. Aniccaṃ bhante. Yaṃ panāniccaṃ dukkhaṃ vā taṃ sukhaṃ vāti, dukkhaṃ bhante’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',84,replace(replace('<p class="bodytext">Na <a name="M2.0084"></a> me bhante khamanīyaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',85,replace(replace('<p class="bodytext">Sādhu <a name="M2.0085"></a> kho tvaṃ bhikkhu rāgavirāgatthaṃ mayā dhammaṃ desitaṃ ājānāsi,</p>\r\n<p class="title">Channavagga</p>\r\n<p class="subhead">Puṇṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',87,replace(replace('<p class="bodytext">channavagge <a name="M2.0087"></a> panāvuso pañcamaṃ saṃgītaṃ puṇṇasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ puṇṇattheraṃ ārabbha bhāsitaṃ, āyasmā bhante puṇṇatthero bhagavantaṃ etadavoca ‘‘sādhu me bhante bhagavā saṃkhittena dhammaṃ desetu, yamahaṃ bhagavato dhammaṃ sutvā eko vūpakaṭṭho appamatto ātāpī pahitatto vihareyya’’nti. Tasmiṃ vatthusmiṃ ‘‘santi kho puṇṇa cakkhuviññeyyā rūpā iṭṭhā kantā manāpā piyarūpā kāmūpasaṃhitā rajanīyā, tañce bhikkhu abhinandati abhivadati ajjhosāya tiṭṭhati, tassa taṃ abhinandato abhivadato ajjhosāya tiṭṭhato uppajjati nandī, nandī samudayo dukkhasamudayo puṇṇāti vadāmī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',89,replace(replace('<p class="bodytext">evañcāvuso <a name="M2.0089"></a> bhagavā āyasmato puṇṇattherassa saṃkhittena ovādaṃ datvā kathañca naṃ paṭipucchi, kathañca so bhagavato ārocesi, kathañcassāyasmato puṇṇattherassa abhisamparāyo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> evaṃ kho bhante bhagavā āyasmato puṇṇattherassa saṃkhittena ovādaṃ datvā ‘‘iminā tvaṃ puṇṇa mayā saṃkhittena ovādena ovadito katamasmiṃ janapade viharissasī’’ti taṃ āyasmantaṃ puṇṇattheraṃ paṭipucchi. So ca bhante āyasmā ‘‘atthi bhante sunāparanto nāma janapado, tatthāhaṃ viharissāmī’’ti evamādinā bhagavato ārocesi. So hi bhante āyasmā sunāparante janapade vasitvā teneva antaravassena tisso vijjā sacchākāsi. Teneva antaravassena parinibbāyi. Evaṃ kho bhante tassa āyasmato abhisamparāyo ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',92,replace(replace('<p class="bodytext">Santi <a name="M2.0092"></a> kho tassa bhagavato sāvakā kāyena ca jīvitena ca aṭṭīyamānā harāyamānā jigucchamānā satthahārakaṃ pariyesanti.</p>\r\n<p class="bodytext">Sakkhissasi kho tvaṃ puṇṇa iminā damūpasamena samannāgato sunāparasantasmiṃ janapade vatthuṃ.</p>\r\n<p class="title">Saḷavagga</p>\r\n<p class="subhead">Mālukyaputtasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',93,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0093"></a> bhagavatā…pe… sammāsambuddhena saḷavagge dutiyaṃ mālukyaputtasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',94,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0094"></a> bhante āyasmantaṃ mālukyaputtattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante mālukyaputtatthero bhagavantaṃ etadavoca ‘‘sādhu me bhante bhagavā saṃkhittena dhammaṃ desetu, yamahaṃ bhagavato dhammaṃ sutvā eko vūpakaṭṭho appamatto ātāpī pahitatto vihareyya’’nti. Tasmiṃ vatthusmiṃ ‘‘ettha dāni mālukyaputta kiṃ dahare bhikkhū vakkhāmāti’’ādinā theraṃ apasādetvā ceva ussādetvā ca ‘‘taṃ kiṃ maññasi mālukyaputta, ye te cakkhuviññeyyā rūpā adiṭṭhā adiṭṭhapubbā, na ca passasi, na ca te hoti passeyyanti, atthi te tattha chando vā rāgo vā evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',97,replace(replace('<p class="bodytext">imasmiṃ <a name="M2.0097"></a> ca kho panāvuso bhagavatā ovāde saṃkhittena bhāsite so āyasmā mālukyaputtatthero bhagavantaṃ kiṃ avoca kathañcassa bhagavā anuññāsi. Kīdiso cassāyasmato mālukyaputtattherassa dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ kho bhante ovāde bhagavatā saṃkhittena bhāsite āyasmā mālukyaputtatthero bhagavantaṃ etadavoca ‘‘imassa khvāhaṃ bhante bhagavatā saṃkhittena bhāsitassa vitthārena atthaṃ ājānāmi.</p>\r\n<p class="gatha1">Rūpaṃ disvā muṭṭhā, piyaṃ nimittaṃ manasi karoto;</p>\r\n<p class="gathalast">Sārattacitto vedeti, tañca ajjhosa tiṭṭhati.</p>\r\n<p class="gatha1">Tassa vaḍḍhanti vedanā, anekā rūpasambhavā;</p>\r\n<p class="gatha2">Abhijjhā ca vihesā ca, cittamassūpahaññati;</p>\r\n<p class="gathalast">Evaṃ ācinato dukkhaṃ, ārā nibbāna vuccati;</p>\r\n<p class="subhead">Peyyāla</p>\r\n<p class="gatha1">Na so rajjati dhammesu, dhammaṃ ñatvā paṭissato;</p>\r\n<p class="gathalast">Virattacitto vedeti, tañca nājjhosa tiṭṭhati.</p>\r\n<p class="gatha1">Yathāssa jānato dhammaṃ, sevato cāpi vedanaṃ;</p>\r\n<p class="gatha2">Khīyati nopacīyati, evaṃ so caratī sato;</p>\r\n<p class="gathalast">Evaṃ apacinato dukkhaṃ, santike nibbāna vuccatīti.</p>\r\n<p class="bodytext">Imassa khvāhaṃ bhante bhagavatā saṃkhittena bhāsitassa evaṃ vitthārena atthaṃ ājānāmīti. Bhagavā ca bhante ‘‘sādhu sādhu mālukyaputta, sādhu kho tvaṃ mālukyaputta mayā saṃkhittena bhāsitassa vitthārena atthaṃ ājānāsī’’ti sādhukāraṃ datvā–</p>\r\n<p class="gatha1">‘‘Rūpaṃ disvā sati muṭṭho, piyaṃ nimittaṃ manasi karoto;</p>\r\n<p class="gathalast">Sārattacitto vedeti, tañca ajjhosa tiṭṭhatī’’ti–</p>\r\n<p class="bodytext">Ādinā</p>\r\n<p class="bodytext">Therassa vacanaṃ samanuññāsi. So ca bhante āyasmā mālukyaputtatthero eko vūpakaṭṭho appamatto ātāpī </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',98,replace(replace('<p class="bodytext">pahitatto <a name="M2.0098"></a> viharanto nacirasseva yassatthāya kulaputtā sammadeva agārasmā anagāriyaṃ pabbajanti, tadanuttaraṃ brahmacariyapariyosānaṃ diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihāsi, ‘‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’’ti abbhaññāsi. Aññataroca panāyasmā mālukyaputto arahataṃ ahosi. Evaṃ kho bhante tassa āyasmato abhisamparāyo ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',100,replace(replace('<p class="gatha1">Rūpaṃ <a name="M2.0100"></a> disvā sati muṭṭhā, piyaṃ nimittaṃ manasi karoto;</p>\r\n<p class="gathalast">Sārattacitto vedeti, tañca ajjhosa tiṭṭhati.</p>\r\n<p class="subhead">Pamādavihārīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',101,replace(replace('<p class="bodytext">tattheva <a name="M2.0101"></a> āvuso catutthaṃ pamādavihārīsuttaṃ bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘pamādavihāriñca vo bhikkhave desessāmi appamādavihāriñca, taṃ suṇātha. Kathañca bhikkhave pamādavihārī hoti. Cakkhundriyaṃ asaṃvutassa bhikkhave viharato cittaṃ byāsiñcati cakkhuviññeyyesu rūpesu, tassa byāsittacittassa pāmojjaṃ na hoti, pāmojje asati pīti na hoti, pītiyā passaddhi na hoti, passaddhiyā asati dukkhaṃ hoti, dukkhino cittaṃ na samādhiyati, asamāhi te citte dhammā na pātubhavanti, dhammānaṃ apātubhāvā pamādavihārītveva saṅkhaṃ gacchatī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Lokakāmaguṇavagga</p>\r\n<p class="subhead">Rāhulovādasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',104,replace(replace('<p class="bodytext">lokakāmaguṇavagge <a name="M2.0104"></a> āvuso aṭṭhamaṃ rāhulovādasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante āyasmantaṃ rāhulaṃ ārabbha ‘‘taṃ kiṃ maññasi rāhula, cakkhu niccaṃ vā aniccaṃ vāti. Aniccaṃ bhante. Yaṃ panāniccaṃ, dukkhaṃ vā taṃ sukhaṃvāti. Dukkhaṃ bhante. Yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ kallaṃ nu taṃ samanupassituṃ ‘etaṃ mama, esohamasmi, eso me attā’ti. No hetaṃ bhante’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',105,replace(replace('<p class="bodytext">Yaṃnūnāhaṃ <a name="M2.0105"></a> rāhulaṃ uttariṃ āsavānaṃ khaye vineyyaṃ.</p>\r\n<p class="bodytext">Gaṇhāhi rāhula nisīdanaṃ.</p>\r\n<p class="title">Gahapativagga</p>\r\n<p class="subhead">Bhāradvājasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',107,replace(replace('<p class="bodytext">gahapativagge <a name="M2.0107"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi catutthaṃ saṃgītaṃ bhāradvājasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante rājānaṃ udenaṃ ārabbha āyasmatā piṇḍolabhāradvājattherena bhāsitaṃ. Rājā bhante udeno āyasmantaṃ piṇḍolabhāradvājaṃ etadavoca ‘‘ko nu kho bho bhāradvāja hetu ko paccayo, yenime daharā bhikkhū susū kāḷakesā bhadrena yobbanena samannāgatā paṭhamena vayasā anikīḷitāvino kāmesu yāvajīvaṃ paripuṇṇaṃ parisuddhaṃ brahmacariyaṃ caranti, addhānañca āpādentī’’ti. Tasmiṃ vatthusmiṃ ‘‘vuttaṃ kho</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',108,replace(replace('<p class="bodytext">Etaṃ <a name="M2.0108"></a> mahārāja tena bhagavatā jānatā passatā arahatā sammāsambuddhena etha tumhe bhikkhave mātumattīsu mātucittaṃ upaṭṭhapetha. Bhaganimattīsu bhaginicittaṃ upaṭṭhapetha, dhītumattīsu dhītucittaṃ upaṭṭhapethā’’ti evamādinā bhante āyasmatā piṇḍola bhāradvājattherena bhāsitaṃ.</p>\r\n<p class="subhead">Verahaccānisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',112,replace(replace('<p class="bodytext">tattheva <a name="M2.0112"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi dasamaṃ saṃgītaṃ verahaccānisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kāmaṇḍāyaṃ bhante verahaccānigottaṃ brāhmaṇiṃ ārabbha āyasmatā udāyittherena bhāsitaṃ. Verahaccānīgottā bhante brāhmaṇī āyasmantaṃ udāyittheraṃ etadavoca ‘‘kismiṃ nu kho bhante sati arahanto sukhadukkhaṃ paññapenti, kismiṃ asati arahanto sukhadukkhaṃ na paññapentī’’ti. Tasmiṃ vatthusmiṃ ‘‘cakkhusmiṃ kho bhagini </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',113,replace(replace('<p class="bodytext">sati <a name="M2.0113"></a> arahanto sukhadukkhaṃ paññapenti, cakkhusmiṃ asati arahanto sukhadukkhaṃ na paññapentī’’ti evamādinā bhante āyasmatā udāyittherena bhāsitaṃ.</p>\r\n<p class="title">Devadahavagga</p>\r\n<p class="subhead">Khaṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',115,replace(replace('<p class="bodytext">devadahavagge <a name="M2.0115"></a> āvuso khaṇasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante devadahe nāma sakyānaṃ nigame sambahule bhikkhū ārabbha ‘‘lābhā vo bhikkhave, suladdhaṃ vo bhikkhave, khaṇo vo paṭiladdho brahmacariyavāsāyā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Samuddavagga</p>\r\n<p class="subhead">Bāḷisikopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',117,replace(replace('<p class="bodytext">samuddavagge <a name="M2.0117"></a> panāvuso tatiyaṃ bāḷisikopamasuttaṃ bhagavatā kattha kaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',118,replace(replace('<p class="bodytext">rājagahe <a name="M2.0118"></a> bhante jīvakambavane sambahule bhikkhū ārabbha ‘‘seyyathāpi bhikkhave bāḷisiko āmisagatabaḷisaṃ gambhīre udakarahade pakkhipeyyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Ādittapariyāyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',120,replace(replace('<p class="bodytext">saḷāyatanavaggasaṃyutte <a name="M2.0120"></a> samuddavagge panāvuso porāṇakehi dhammasaṃgāhakamahātherehi saṃgītaṃ aṭṭhamaṃ ādittapariyāyasuttaṃ bhagavatā kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha ‘‘ādittapariyāyaṃ vo bhikkhave dhammapariyāyaṃ desessāmi, taṃ suṇātha. Katamo ca bhikkhave ādittapariyāyo dhammapariyāyo. Varaṃ bhikkhave tattāya ayosalākāya ādittāya sampajjalitāya sajotibhūtāya cakkhundriyaṃ sampalimaṭṭhaṃ, natveva cakkhuviññeyyesu rūpesu anubyañjanaso nimittaggāho’’tiādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Āsīvisavagga</p>\r\n<p class="subhead">Paṭhamadārukkhandhopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',126,replace(replace('<p class="bodytext">āsīvisavagge <a name="M2.0126"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi catutthaṃ saṃgītaṃ paṭhamadārukkhandhopamasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante gaṅgāya nadiyā tīre sambahule bhikkhū ārabbha ‘‘passatha no tumhe bhikkhave amhaṃ mahantaṃ dārukkhandhaṃ gaṅgāya nadiyā sotena vuyhamāna’’nti evamādinā bhagavatā bhāsitā.</p>\r\n<p class="subhead">Upacāravacanaṃ</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',129,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0129"></a> tattha bhagavatā orimatīrādīnaṃ upacāravacanānaṃ attho vitthārena vibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> orimaṃ tīranti kho bhikkhu channetaṃ ajjhattikānaṃ āyatanānaṃ adhivacanaṃ. Pārimaṃ tīranti kho bhikkhu channetaṃ bāhirānaṃ āyatanānaṃ adhivacanaṃ. Majjhe saṃsīdoti kho bhikkhu nandirāgassetaṃ adhivacanaṃ. Thale ussādoti kho bhikkhu asmimānassetaṃ adhivacananti evamādinā bhante bhagavatā tattha orimatīrādīnaṃ upacāravacanānaṃ attho vitthārena vibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',131,replace(replace('<p class="bodytext">imasmiṃ <a name="M2.0131"></a> kho pana āvuso sutte bhagavatā bhāsite visesato kassa kīdiso attho kathañca paṭiladdho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ bhante sutte bhagavatā bhāsite visesato nandassa gopālakassa pabbajjā ca upasampadā ca yāva arahattāca visesato attho adhigato.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',132,replace(replace('<p class="bodytext">Labheyyāhaṃ <a name="M2.0132"></a> bhante bhagavato santike pabbajjaṃ, labheyyaṃ upasampadaṃ.</p>\r\n<p class="bodytext">Tena hi tvaṃ nanda sāmikānaṃ gāvo niyyātehi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',133,replace(replace('<p class="bodytext">Gamissanti <a name="M2.0133"></a> bhante gāvo vacchagiddhiniyo.</p>\r\n<p class="subhead">Kiṃsukopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',134,replace(replace('<p class="bodytext">tattheva <a name="M2.0134"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi aṭṭhamaṃ saṃgītaṃ kiṃsukopamasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ nigrodhārāme aññataraṃ kārakaṃ bhikkhuṃ ārabbha bhāsitaṃ. Aññataro bhante kārako bhikkhu cattāro khāṇāsave bhikkhū ñāṇadassanavisuddhaṃ pucchitvā asantuṭṭho tesaṃ bhikkhūnaṃ pañhāveyyākaraṇena yena bhagavā tenupasaṅkami. Upasaṅkamitvā bhagavantaṃ etadavoca ‘‘kittāvatā nu kho bhante bhikkhuno dassanaṃ suvisuddhaṃ hotī’’ti. Tasmiṃ vatthusmiṃ ‘‘seyyathāpi bhikkhu purisassa kiṃsuko adiṭṭhapubbo assa, so yenaññataro puriso kiṃsukassa dassāvī tenupasaṅkameyyā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Chappāṇakopamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',140,replace(replace('<p class="bodytext">tattheva <a name="M2.0140"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi dasamaṃ saṃgītaṃ chappāṇakopamasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesuyeva bhante sambahule bhikkhū ārabbha ‘‘seyyathāpi, bhikkhave puriso arugatto pakkagatto saravanaṃ paviseyyā’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',141,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0141"></a> tattha bhagavatā asaṃvaro vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathañca bhikkhave asaṃvaro hoti, idha bhikkhave bhikkhu cakkhunā rūpaṃ disvā piyarūpe rūpe adhimuccati, appiyarūpe rūpe byāpajjati, anupaṭṭhitakāyassati ca viharati parittacetaso. Tañca cetovimuttiṃ paññāvimuttiṃ yathābhūtaṃ nappajānāti, yatthassa te uppannā pāpakā akusalā dhammā aparisesā nirujjhantīti evamādinā bhante bhagavatā tattha asaṃvaro vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',143,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0143"></a> tattha bhagavatā saṃvaro vibhajitvā vitthārena desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathañca bhikkhave saṃvaro hoti, idha bhikkhave bhikkhu cakkhunā rūpaṃ disvā piyarūpe rūpe nādhimuccati, appiyarūpe rūpe na byāpajjatīti, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',144,replace(replace('<p class="bodytext">evamādinā <a name="M2.0144"></a> bhante bhagavatā tattha saṃvaro vibhajitvā vitthārena desito.</p>\r\n<p class="title">Vedanāsaṃyutta</p>\r\n<p class="subhead">Daṭṭhabbasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',147,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0147"></a> bhagavatā…pe… sammāsambuddhena vedanāsaṃyutte pañcamaṃ daṭṭhabbasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ nigrodhārāme sambahule bhikkhū ārabbha ‘‘tisso imā bhikkhave vedanā. Katamā tisso, sukhā vedanā dukkhāvedanā adukkhamasukhā vedanā. Sukhā bhikkhave vedanā dukkhato daṭṭhabbā, dukkhā vedanā sallato daṭṭhabbā, adukkhamasukhā vedanā aniccato daṭṭhabbā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',148,replace(replace('<p class="bodytext">Sukhā <a name="M2.0148"></a> bhikkhave vedanā dukkhato daṭṭhabbā.</p>\r\n<p class="gatha1">Yo sukhaṃ dukkhato addasa, dukkhamaddakkhi sallato;</p>\r\n<p class="gathalast">Adukkhamasukhaṃ santaṃ, addakkhinaṃ aniccato.</p>\r\n<p class="gatha1">Sa ve sammaddaso bhikkhu, parijānāti vedanā;</p>\r\n<p class="gatha2">So vedanā pariññāya, diṭṭhe dhamme anāsavo;</p>\r\n<p class="gathalast">Kāyassa bhedā dhammaṭṭho, saṅkhyaṃ nopeti vedagū.</p>\r\n<p class="subhead">Sallasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',149,replace(replace('<p class="bodytext">tattheva <a name="M2.0149"></a> āvuso chaṭṭhaṃ sallasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ nigrodhārāme sambahule bhikkhū ārabbha ‘‘assutavā bhikkhave puthujjano sukhampi vedanaṃ vedayati, dukkhampi vedanaṃ vedayati, adukkhamasukhampi vedanaṃ vedayati. Sutavā bhikkhave ariyasāvako sukhampi vedanaṃ vedayati, dukkhampi vedanaṃ vedayati, adukkhamasukhampi vedanaṃ vedayati. Tatra bhikkhave ko viseso, ko adhippayāso, kiṃ nānākaraṇaṃ sutavato ariyabhāvakassa assutavatā puthujjanenā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Jambukhādakasaṃyutta</p>\r\n<p class="subhead">Nibbānapañhāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',154,replace(replace('<p class="bodytext">jambukhādakasaṃyutte <a name="M2.0154"></a> āvuso paṭhamaṃ nibbānapañhāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> magadhesu bhante nālakagāmake jambukhādakaṃ paribbājakaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Jambukhādako bhante paribbājako āyasmantaṃ sāriputtaṃ etadavoca ‘‘nibbānaṃ nibbānanti āvuso sāriputta vuccati, katamaṃ nukho āvuso nibbāna’’nti. Tasmiṃ bhante vatthusmiṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',155,replace(replace('<p class="bodytext">‘‘yo <a name="M2.0155"></a> kho āvuso rāgakkhayo dosakkhayo mohakkhayo, idaṃ vuccati nibbāna’’nti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="subhead">Dhammavādīpañhāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',157,replace(replace('<p class="bodytext">tattheva <a name="M2.0157"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi tatiyaṃ saṃgītaṃ dhammavādīpañhāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> magadhesuyeva bhante nālakagāme jambukhādakaṃ paribbājakaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Jambukhādako bhante paribbājako āyasmantaṃ sāriputtaṃ etadavoca ‘‘ke nu kho āvuso sāriputta loke dhammavādino, ke loke suppaṭipannā, ke loke sugatā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘ye kho āvuso rāgappahānāya dhammaṃ desenti, dosappahānāya dhammaṃ desenti, mohappahānāya dhammaṃ desenti. Te loke dhammavādino’’ti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',158,replace(replace('<p class="bodytext">Ke <a name="M2.0158"></a> loke suppaṭipannā.</p>\r\n<p class="bodytext">Ke loke sugatā.</p>\r\n<p class="subhead">Dukkarapañhāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',161,replace(replace('<p class="bodytext">tattheva <a name="M2.0161"></a> porāṇakehi dhammasaṃgāhaka mahātherehi pariyosānasuttabhāvena saṃgītaṃ dukkarapañhāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> magadhesuyeva nālakagāme jambukhādakaṃ paribbājakaṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ. Jambukhādako bhante paribbājako āyasmantaṃ sāriputtaṃ etadavoca ‘‘kiṃ nu kho āvuso sāriputta imasmiṃ dhammavinaye dukkara’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘pabbajjā kho āvuso imasmiṃ dhammavinaye dukkarā’’ti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="title">Moggallānasaṃyutta</p>\r\n<p class="subhead">Sakkasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',163,replace(replace('<p class="bodytext">moggallānasaṃyutte <a name="M2.0163"></a> āvuso dasamaṃ saṃgītaṃ sakkasuttaṃ kattha kaṃ ārabbha kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> devesu bhante tāvatiṃsesu sakkaṃ devānamindaṃ ārabbha ‘‘sādhu kho devānaminda buddhasaraṇagamanaṃ hoti, buddhasaraṇagamanahetu kho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',164,replace(replace('<p class="bodytext">devānaminda <a name="M2.0164"></a> evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjantī’’ti evamādinā bhante āyasmatā mahāmoggallānattherena bhāsitaṃ.</p>\r\n<p class="title">Cittasaṃyutta</p>\r\n<p class="subhead">Nigaṇṭhanāṭaputtasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',168,replace(replace('<p class="bodytext">cittasaṃyutte <a name="M2.0168"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi aṭṭhamaṃ saṃgītaṃ nigaṇṭhanāṭaputtasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> macchikāsaṇḍe bhante nigaṇṭhaṃ nāṭaputtaṃ ārabbha cittena gahapatinā bhāsitaṃ. Nigaṇṭho bhante nāṭaputto cittaṃ gahapatiṃ etadavoca ‘‘saddahasi tvaṃ gahapati samaṇassa gotamassa atthi avitakko avicāro samādhi, atthi vitakkavicārānaṃ nirodho’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘na khvāhaṃ ettha bhante bhagavato saddhāya gacchāmi, atthi avitakko avicāro samādhi, atthi vitakkavicārānaṃ nirodho’’ti evamādinā cittena gahapatinā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',169,replace(replace('<p class="bodytext">Idaṃ <a name="M2.0169"></a> bhavanto passantu.</p>\r\n<p class="bodytext">Yāva ujuko cāyaṃ citto gahapati.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',170,replace(replace('<p class="bodytext">‘‘Ahaṃ <a name="M2.0170"></a> kho bhante yāvadeva ākaṅkhāmi, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharāmi’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',171,replace(replace('<p class="bodytext">Idaṃ <a name="M2.0171"></a> bhavanto passantu.</p>\r\n<p class="subhead">Acelakassapasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',172,replace(replace('<p class="bodytext">tattheva <a name="M2.0172"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi navamaṃ saṃgītaṃ acelakassapasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante macchikāsaṇḍe acelaṃ kassapaṃ ārabbha cittena gahapatinā bhāsitaṃ. Acelo bhante kassapo cittaṃ gahapatiṃ etadavoca ‘‘imehi pana te gahapati tiṃsamattehi vassehi atthi koci uttarimanussadhammo alamariyañāṇadassanaviseso adhigato phāsuvihāro’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘gihinopi siyā bhante ahañhi bhante yāvadeva ākaṅkhāmi, vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharāmī’’ti evamādinā cittena gahapatinā bhāsitaṃ.</p>\r\n<p class="title">Gāmaṇisaṃyutta</p>\r\n<p class="subhead">Caṇḍasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',175,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0175"></a> bhagavatā…pe… sammāsambuddhena gāmaṇisaṃyutte paṭhamaṃ caṇḍasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',176,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0176"></a> bhante caṇḍaṃ gāmaṇiṃ ārabbha bhāsitaṃ. Caṇḍo bhante gāmaṇi bhagavantaṃ etadavoca ‘‘ko nu kho bhante hetu ko paccayo, yena midhekacco caṇḍo caṇḍotveva saṅkhaṃ gacchati. Ko pana bhante hetu ko paccayo, yena midhekacco sorato soratotveva saṅkhaṃ gacchatī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘idha gāmaṇi ekaccassa rāgo appahīno hoti, rāgassa appahīnattā pare kopenti, parehi kopiyamāno kopaṃ pātukaroti, so caṇḍotveva saṅkhaṃ gacchatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Khettūpamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',178,replace(replace('<p class="bodytext">tattheva <a name="M2.0178"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi sattamaṃ saṃgītaṃ khettūpamasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nāḷandāyaṃ bhante pāvārikambavane asibandhakaputtaṃ gāmaṇiṃ ārabbha bhāsitaṃ. Asibandhakaputto bhante gāmaṇi bhagavantaṃ etadavoca ‘‘nanu bhante bhagavā sabbapāṇabhūtahitānukampī viharatīti. Evaṃ gāmaṇi tathāgato sabbapāṇabhūtahitānukampī viharatīti. Atha kiñcarahi bhante bhagavā ekaccānaṃ sakkaccaṃ dhammaṃ deseti, ekaccānaṃ no </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',179,replace(replace('<p class="bodytext">tathā <a name="M2.0179"></a> sakkaccaṃ dhammaṃ desetī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘tena hi gāmaṇi taññevettha paṭipucchissāmi, yathā te khameyya, tathā naṃ byākareyyāsī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Saṅkhadhamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',183,replace(replace('<p class="bodytext">tattheva <a name="M2.0183"></a> āvuso porāṇakehi dhammasaṃgāhaka mahātherehi aṭṭhamaṃ saṃgītaṃ saṅkhadhamasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nāḷandāyaṃyeva bhante pāvārikambavane asibandhakaputtaṃ gāmaṇiṃ nigaṇṭhasāvakaṃ ārabbha bhāsitaṃ. Bhagavā bhante asibandhakaputtaṃ gāmaṇiṃ nigaṇṭhasāvakaṃ etadavoca ‘‘kathaṃ nu kho gāmaṇi nigaṇṭho nāṭaputto sāvakānaṃ dhammaṃ desetī’’ti. Evaṃ kho bhante nigaṇṭho nāṭaputto sāvakānaṃ dhammaṃ deseti ‘‘yo koci pāṇaṃ atipāteti, sabbo so āpāyiko nerayiko. (Peyyāla) yaṃbahulaṃ yaṃbahulaṃ viharati, tena tena nīyatī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yaṃbahulaṃ yaṃbahulañca gāmaṇi viharati, tena tena nīyati, evaṃ sante na koci āpāyiko nerayiko bhavissati, yathā nigaṇṭhassa nāṭaputtassa vacana’’nti evaṃ kho bhagavatā saṃkhittena bhāsitaṃ.</p>\r\n<p class="subhead">Yaṃ bahulavāda</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',185,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0185"></a> tattha bhagavatā yaṃbahulavāde doso pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kiṃ maññasi gāmaṇi, yo so puriso pāṇātipātī rattiyā vā divasassa vā samayāsamayaṃ upādāya katamo bahutaro samayo yaṃ vāso pāṇamatipāteti. Yaṃ vā so pāṇaṃ nātipātetīti evamādinā bhante tattha bhagavatā asibandhakaṃ nigaṇṭhanāṭaputtassa sāvakaṃ gāmaṇiṃ pucchitvā pucchitvā yaṃbahulavāde doso pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',188,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0188"></a> tattha bhagavatā anekaṃsa vipākakammassa ekaṃ savipākavāde doso pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha gāmaṇi ekacco satthā evaṃvādī hoti evaṃdiṭṭhi yo koci pāṇamatipāteti, sabbo so āpāyiko nerayikoti evamādinā bhante tattha bhagavatā anekaṃsa kammavipākassa ekaṃsavipākavāde doso vitthāretvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',189,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0189"></a> tattha bhagavatā yathādhammasāsane guṇovibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',190,replace(replace('<p class="bodytext">idha <a name="M2.0190"></a> pana gāmaṇi tathāgato loke uppajjati arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaropurisa dammasārathi satthādevamanussānaṃ buddho bhagavā, so anekapariyāyena pāṇātipātaṃ garahati vigarahati. ‘‘Pāṇātipātā viramathā’’ti cāhāti evamādinā bhante bhagavatā tattha yathādhammasāsane guṇo vitthāretvā pakāsito.</p>\r\n<p class="subhead">Bhadrakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',193,replace(replace('<p class="bodytext">tattheva <a name="M2.0193"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi ekādasamaṃ saṃgītaṃ bhadrakasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mallesu bhante uruvelakappenāma mallānaṃ nigame bhadrakaṃ gāmaṇiṃ ārabbha bhāsitaṃ. Bhadrako bhante gāmaṇi bhagavantaṃ etadavoca ‘‘sādhu me bhante bhagavā dukkhassa samudayañca atthaṅgamañca desetū’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘ahañce te gāmaṇi atītamaddhānaṃ ārabbha dukkhassa samudayañca atthaṅgamañca deseyyaṃ ‘evaṃ ahosi atītamaddhāna’nti. Tatra te siyā kaṅkhā siyā vimatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',194,replace(replace('<p class="gatha1">Sādhu <a name="M2.0194"></a> me bhante bhagavā dukkhassa</p>\r\n<p class="gathalast">Samudayañca atthaṅgamañca desetu–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',195,replace(replace('<p class="bodytext">‘‘Taṃ <a name="M2.0195"></a> kiṃ maññasi gāmaṇi, atthi uruvelakappe manussā’’ –</p>\r\n<p class="subhead">Rāsiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',199,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0199"></a> bhagavatā…pe… sammāsambuddhena gāmaṇisaṃyutte dvādasamaṃ saṃgītaṃ rāsiyasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mallesu bhante uruvelakappenāma mallānaṃ nigame rāsiyaṃ gāmaṇiṃ ārabbha bhāsitaṃ. Rāsiyo bhante gāmaṇi bhagavantaṃ upasaṅkamitvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',200,replace(replace('<p class="bodytext">etadadoca <a name="M2.0200"></a> ‘‘sutaṃ me bhante ‘samaṇo gotamo sabbaṃ tapaṃ garahati, sabbaṃ tamassiṃ lūkhajīviṃ ekaṃsena upavadati upakkosatī’’ti. Ye te bhante evamāhaṃsu ‘samaṇo gotamo sabbaṃ tapaṃ garahati, sabbaṃ tapassiṃ lūkhajīviṃ ekaṃsena upavadati upakkosatī’’ti. Kacci te bhante bhagavato vuttavādino, na ca bhagavantaṃ abhūtena abbhācikkhanti, dhammassa cānudhammaṃ byākarontī’’ti. Tasmiṃ bhante vatthusmiṃ ye te gāmaṇi evamāhaṃsu ‘‘samaṇo gotamo sabbaṃ tapaṃ garahati, sabbaṃ tapassiṃ lūkhajīviṃ ekaṃsena upavadati upakkosatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',202,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0202"></a> tattha bhagavatā garahitabbāgarahitabbā kāmabhogino vitthārena vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tayo kho me gāmaṇi kāmabhogino santo saṃvijjamānā lokasmiṃ. Katame tayo. Idha gāmaṇi ekacco kāmabhogī </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',203,replace(replace('<p class="bodytext">adhammena <a name="M2.0203"></a> bhoge pariyesati sāhasena, adhammena bhoge pariyesitvā sāhasena na attānaṃ sukheti na pīṇeti, na saṃvibhajati na puññāni karotīti evamādinā bhante tattha bhagavatā garahitabbāgarahitabbā kāmabhogino vitthārena vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',207,replace(replace('<p class="bodytext">kathañcāvuso <a name="M2.0207"></a> tattha bhagavatā garahitabbāgarahitabbā tapassino lūkhajīvino vitthārena vibhajitvā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tayo me gāmaṇi tapassino lūkhajīvino santo saṃvijjamānā lokasmiṃ. Katame tayo. Idha gāmaṇī ekacco tapassī lūkhajīvī saddhā agārasmā anagāriyaṃ pabbajito hoti ‘‘appeva nāma kusalaṃ dhammaṃ adhigaccheyyaṃ, appeva nāma uttarimanussadhammā alamariyañāṇadassanavisesaṃ sacchikareyya’’nti, so attānaṃ ātāpeti paritāpeti, kusalañca dhammaṃ nādhigacchati, uttari ca manussadhammā alamariyañāṇadassanavisesaṃ na sacchikarotīti evamādinā bhante tattha bhagavatā garahitabbāgarahitabbā tapassino lūkhajīvino vitthārena vibhajitvā pakāsitā.</p>\r\n<p class="title">Abyākatasaṃyutta</p>\r\n<p class="subhead">Khemāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',209,replace(replace('<p class="bodytext">abyākatasaṃyutte <a name="M2.0209"></a> panāvuso porāṇakehi dhammasaṃgāhaka mahātherehi paṭhamaṃ saṃgītaṃ khemāsuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ. Kathañca taṃ bhagavatāpi puna bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> antarā ca bhante sāvatthiṃ antarā ca sāketaṃ toraṇavatthusmiṃ padese rājānaṃ pasenadiṃ kosalaṃ ārabbha khemāya bhikkhuniyā bhāsitaṃ. Rājā bhante pasenadi kosalo yena khemā bhikkhunī tenupasaṅkami, upasaṅkamitvā abhivādetvā ekamantaṃ nisīdi, ekamantaṃ nisinno kho bhante rājā pasenadikosalo khemaṃ bhikkhuniṃ etadavoca ‘‘kiṃ nukho ayye hoti tathāgato paraṃmaraṇā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘abyākataṃ kho etaṃ mahārāja bhagavatā hoti tathāgato paraṃ maraṇā’’ti evamādinā khemāya bhikkhuniyā bhāsitaṃ. Bhagavatā ca bhante aparena samayena raññā pasenadikosalena puṭṭhena evameva puna bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',211,replace(replace('<p class="bodytext">Kiṃ <a name="M2.0211"></a> panayye nahoti tathāgato paraṃ maraṇā.</p>\r\n<p class="subhead">Kutūhalasālāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',215,replace(replace('<p class="bodytext">tattheva <a name="M2.0215"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi navamaṃ saṃgītaṃ kutūhalasālāsuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante vacchagottaṃ paribbājakaṃ ārabbha bhāsitaṃ. Vacchagotto bhante paribbājako bhagavantaṃ upasaṅkamitvā ‘‘purimāni bho gotama divasāni purimatarāni sambahulānaṃ nānātitthiyānaṃ samaṇabrāhmaṇānaṃ paribbājakānaṃ kutūhalasālāyaṃ sannisinnānaṃ sannipatitānaṃ ayamantarakathā udapādī’’ti evamādikaṃ vacanaṃ avoca. Tasmiṃ bhante vatthusmiṃ ‘‘alañhi te vaccha kaṅkhituṃ, alaṃ vicikicchituṃ. Kaṅkhanīye ca pana te ṭhāne vicikicchā uppannā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="chapter">Mahāvaggasaṃyuttapāḷi</p>\r\n<p class="title">Avijjāvagga</p>\r\n<p class="subhead">Upaḍḍhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',219,replace(replace('<p class="bodytext">mahāvaggasaṃyutte <a name="M2.0219"></a> panāvuso porāṇakehi dhammasaṃgāhakamahātherehi dutiyaṃ saṃgītaṃ upaḍḍhasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante sakkare nāma sakyānaṃ nigame āyasmantaṃ ānandattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante ānandatthero bhagavantaṃ etadavoca ‘‘upaḍḍhamidaṃ bhante brahmacariyaṃ yadidaṃ kalyāṇamittatā kalyāṇasahāyatā kalyāṇasampavaṅkatā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘mā hevaṃ ānanda, mā hevaṃ ānanda, sakalamevidaṃ ānanda brahmacariyaṃ yadidaṃ kalyāṇamittatā kalyāṇasahāyatā kalyāṇasampavaṅkatā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',220,replace(replace('<p class="bodytext">Mamaṃ <a name="M2.0220"></a> hi ānanda kalyāṇamittaṃ āgamma jātidhammā sattā jātiyā parimuccanti.</p>\r\n<p class="title">Bojjhaṅgasaṃyutta</p>\r\n<p class="subhead">Kuṇḍaliyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',221,replace(replace('<p class="bodytext">bojjhaṅgasaṃyutte <a name="M2.0221"></a> panāvuso porāṇakehi dhammasaṃgāhakamahātherehi chaṭṭhaṃ saṃgītaṃ kuṇḍaliyasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sākete bhante añjanavane migadāye kuṇḍaliyaṃ paribbājakaṃ ārabbha bhāsitaṃ. Kuṇḍaliyo bhante paribbājako bhagavantaṃ upasaṅkamitvā ‘‘ahamasmi bho gotama ārāmanissayī parisāvacaro, tassa mayhaṃ bho gotama pacchābhattaṃ bhuttapātarāsassa ayamācāro hoti ārāmena ārāmaṃ uyyānena uyyānaṃ anucaṅkamāmi anuvicarāmi, so tattha passāmi eke samaṇabrāhmaṇe itivādappamokkhānisaṃsañceva kathaṃ kathante upārambhānisaṃsañca, bhavaṃ pana gotamo kimānisaṃso viharatī’’ti etaṃ vacanaṃ avoca. Tasmiṃ bhante vatthusmiṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',222,replace(replace('<p class="bodytext">‘‘vijjāvimuttiphalānisaṃso <a name="M2.0222"></a> kho kuṇḍaliya tathāgato viharatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhama gilānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',224,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0224"></a> bhagavvatā jānatā…pe… sammāsambuddhena bojjhaṅgasaṃyutte dutiye gilānavagge paṭhamagilānasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante pippaliguhāyaṃ āyasmantaṃ mahākassapattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante mahākassapatthero pippaliguhāyaṃ viharati ābādhiko dukkhito bāḷhagilāno. Atha kho bhante bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā mahākassapo tenupasaṅkami, upasaṅkamitvā paññatte āsane nisīdi, nisajja kho bhagavā āyasmantaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',225,replace(replace('<p class="bodytext">mahākassapaṃ <a name="M2.0225"></a> etadavoca– ‘‘kacci te kassapa khamanīyaṃ, kacci yāpanīyaṃ, kacci dukkhā vedanā paṭikkamanti no abhikkamanti, paṭikkamosānaṃ paññāyati no abhikkamoti. Na me bhante khamanīyaṃ na yāpanīyaṃ, bāḷhā me dukkhā vedanā abhikkamanti no paṭikkamanti, abhikkamosānaṃ paññāyati no paṭikkamo’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘sattime kassapa bojjhaṅgā mayā sammadakkhātā bhāvitā bahulīkatā abhiññāya sambodhāya nibbānāya saṃvattantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Udāyisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',227,replace(replace('<p class="bodytext">tattheva <a name="M2.0227"></a> āvuso tatiye udāyivagge porāṇakehi dhammasaṃgāhakamahātherehi dasamaṃ saṃgītaṃ udāyisuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sumbhesu bhante setakenāma sumbhānaṃ nigame āyasmantaṃ udāyittheraṃ ārabbha bhāsitaṃ. Āyasmā bhante udāyitthero bhagavantaṃ upasaṅkamitvā ‘‘acchariyaṃ bhante, abbhutaṃ bhante, yāva bahukatañca me bhante bhagavati pemañca gāravo ca hirī ca ottappañcāti evamādikaṃ vacanaṃ avoca. Tasmiṃ bhante vatthusmiṃ ‘‘sādhu sādhu udāyi eso hi te udāyi maggo paṭiladdho. Yo te bhāvito bahulīkato, tathā tathā viharantaṃ tathattāya upanessati, yathā tvaṃ ‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’ti pajānissatī’’ti. Evaṃ kho bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Satipaṭṭhānasaṃyutta</p>\r\n<p class="subhead">Satisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',230,replace(replace('<p class="bodytext">satipaṭṭhānasaṃyutte <a name="M2.0230"></a> āvuso porāṇakehi dhammasaṃgāhaka mahātherehi dutiyaṃ saṃgītaṃ satisuttaṃ bhagavatā kattha kaṃ kārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante ambapālivane sambahule bhikkhū ārabbha ‘‘sato bhikkhave bhikkhu vihareyya sampajāno ayaṃ vo amhākaṃ anusāsanī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sālasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',232,replace(replace('<p class="bodytext">tattheva <a name="M2.0232"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi catutthaṃ saṃgītaṃ sālasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante sālāyanāma kesalānaṃ brāhmaṇagāme sambahule bhikkhū ārabbha ‘‘ye te bhikkhave bhikkhū navā acirapabbajitā adhunāgatā imaṃ dhammavinayaṃ, te vo bhikkhave bhikkhū catunnaṃ satipaṭṭhānānaṃ bhāvanāya samādapetabbā nivesetabbā patiṭṭhāpetabbā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sakuṇagghisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',234,replace(replace('<p class="bodytext">tattheva <a name="M2.0234"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi chaṭṭhaṃ saṃgītaṃ sakuṇagghisuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘bhūtapubbaṃ bhikkhave sakuṇagghi lāpaṃ sakuṇaṃ sahasā ajjhappattā aggahesi. Atha kho bhikkhave lāpo sakuṇo sakuṇagghiyā hariyamāno evaṃ paridevasi mayamevamha alakkhikā, mayaṃ appapuññā, ye mayaṃ agocare carimha paravisaye, sacejja mayaṃ gocare careyyāma sake pettike visaye, na myāhaṃ sakuṇagghi alaṃ abhavissa yadidaṃ yuddhāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',235,replace(replace('<p class="bodytext">Mayamevamha <a name="M2.0235"></a> alakkhikā, mayaṃ appapuññā.</p>\r\n<p class="bodytext">Ko pana te lāpa gocaro sako pettiko visayo.</p>\r\n<p class="bodytext">Yadidaṃ naṅgalakaṭṭhakaraṇaṃ leḍḍuṭṭhānaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',236,replace(replace('<p class="bodytext">Ehi <a name="M2.0236"></a> kho dāni me sakuṇagghi, ehi kho dāni me sakuṇagghi,</p>\r\n<p class="bodytext">(Dāruguḷoviya vinivattitvā tasseva leḍḍussa antare paccupādi. Aṭṭhakathā)</p>\r\n<p class="bodytext">Evañhi taṃ bhikkhave hoti yo agocare carati paravisaye.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',237,replace(replace('<p class="bodytext">Tasmātiha <a name="M2.0237"></a> bhikkhave mā agocare carittha paravisaye.</p>\r\n<p class="bodytext">Gocare bhikkhave caratha sake pettike visaye.</p>\r\n<p class="bodytext">Gocare bhikkhave carataṃ sake pettike visaye na lacchati māro otāraṃ, na lacchati māro ārammaṇaṃ.</p>\r\n<p class="subhead">Bhikkhunupassayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',238,replace(replace('<p class="bodytext">tattheva <a name="M2.0238"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi dasamaṃ saṃgītaṃ bhikkhunupassayasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ ānandattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante ānando yāvattako ahosi bhikkhunīhi saddhiṃ kathāsallāpo taṃ sabbaṃ bhagavato ārocesi. Tasmiṃ bhante vatthusmiṃ ‘‘evametaṃ ānanda, evametaṃ ānanda, yohi koci ānanda bhikkhu vā bhikkhunī vā catūsu satipaṭṭhānesu suppatiṭṭhitacitto viharati, tassetaṃ pāṭikaṅkhaṃ uḷāraṃ pubbenāparaṃ visesaṃ sañjānissatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Cundasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',242,replace(replace('<p class="bodytext">tattheva <a name="M2.0242"></a> āvuso dutiye nālandavagge porāṇakehi dhammasaṃgāhakamahātherehi tatiyaṃ saṃgītaṃ cundasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃyeva ānandattheraṃ ārabbha bhāsitaṃ. Āyasmā bhante ānandatthero cundena samaṇuddesena saddhiṃ yena bhagavā tenupasaṅkami. Upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. Ekamantaṃ nisinno kho bhante āyasmā ānando bhagavantaṃ etadavoca ‘‘ayaṃ bhante cundo samaṇuddeso evamāha ‘āyasmā bhante sāriputto parinibbuto, idamassa pattacīvara’nti. Api ca me bhante madhurakajāto viya kāyo, disāpi me na pakkhāyanti, dhammāpi maṃ nappaṭibhanti ‘āyasmā sāriputto parinibbuto’ti sutvā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘kiṃ nu kho te ānanda sāriputto sīlakkhandhaṃ vā ādāya parinibbuto, samādhikkhandhaṃ vā, paññākkhandhaṃvā, vimuttikkhandhaṃvā, vimuttiñāṇadassanakkhandhaṃ vā ādāya parinibbuto’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',243,replace(replace('<p class="bodytext">Ayaṃ <a name="M2.0243"></a> bhante cundosamaṇuddeso evamāha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',245,replace(replace('<p class="bodytext">Tasmātihānanda <a name="M2.0245"></a> attadīpā viharatha attasaraṇā anaññasaraṇā dhammadīpā dhammasaraṇā anaññasaraṇā.</p>\r\n<p class="subhead">Bāhiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',246,replace(replace('<p class="bodytext">tattheva <a name="M2.0246"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi pañcamaṃ saṃgītaṃ bāhiyasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ bāhiyaṃ ārabbha bhāsitaṃ. Āyasmā bhante bāhiyo bhagavantaṃ etadavoca ‘‘sādhu me bhante bhagavā saṃkhittena dhammaṃ desetu, yamahaṃ bhagavato dhammaṃ sutvā eko vūpakaṭṭho appamatto ātāpī pahitatto vihareyya’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘tasmātiha tvaṃ bāhiya ādimeva visodhehi kusalesu dhammesu. Ko cādikusalānaṃ dhammānaṃ, sīlañca suvisuddhaṃ diṭṭhica ujukā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Sīlaṭṭhitivagga</p>\r\n<p class="subhead">Ciraṭṭhitisutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',248,replace(replace('<p class="bodytext">tattheva <a name="M2.0248"></a> āvuso tatiye sīlaṭṭhitivagge porāṇakehi dhammasaṃgāhakamahātherehi dutiyaṃ saṃgītaṃ ciraṭṭhitisuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pāṭaliputte bhante āyasmantaṃ bhaddaṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Āyasmā bhante bhaddo sāyanhasamayaṃ paṭisallānā vuṭṭhito yenāyasmā ānando tenupasaṅkami, upasaṅkamitvā āyasmantaṃ ānandaṃ etadavoca ‘‘ko nu kho āvuso ānanda hetu ko paccayo, yena tathāgate parinibbute saddhammo na ciraṭṭhitiko hoti, ko panāvuso ānanda hetu ko paccayo, yena tathāgate parinibbute saddhammo ciraṭṭhitiko hotī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘sādhu sādhu āvuso bhadda, bhaddako kho te āvuso bhadda ummaṅgo’’ti evamādinā āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',249,replace(replace('<p class="bodytext">Bhaddako <a name="M2.0249"></a> te āvuso bhadda ummaṅgo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',250,replace(replace('<p class="bodytext">‘‘Catunnaṃ <a name="M2.0250"></a> kho āvuso satipaṭṭhānānaṃ abhāvitattā abahulīkatattā tathāgate parinibbute saddhammo na ciraṭṭhitiko hotī’’ –</p>\r\n<p class="subhead">Sirivaḍḍhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tassāvuso bhagavatā arahato sammāsambuddhassa pāvacanasamudayabhūtāya mahāvaggapāḷiyā satipaṭṭhānasaṃyutte sīlaṭṭhitivagge navamaṃ sirivaḍḍhasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sirivaḍḍhaṃ gahapatiṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Sirivaḍḍho bhante gahapati ābādhiko hoti dukkhito bāḷhagilāno. Athakho bhante āyasmā ānando yenasirivaḍḍhassa gahapatissa nivesanaṃ tenupasaṅkami, upasaṅkamitvā paññatte āsane </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',251,replace(replace('<p class="bodytext">nisīdi<a name="M2.0251"></a>. Nisajja kho āyasmā ānando sirivaḍḍhaṃ gahapatiṃ etadavoca ‘‘kacci te gahapati khamanīyaṃ, kacci yāpanīyaṃ, kacci dukkhā vedanā paṭikkamanti no abhikkamanti, paṭikkamosānaṃ paññāyati no abhikkamo’’ti. Na me bhante khamanīyaṃ, na yāpanīyaṃ, bāḷhā me dukkhā vedanā abhikkamanti no paṭikkamanti, abhikkamosānaṃ paññāyati no paṭikkamo’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘tasmātiha te gahapati evaṃ sikkhitabbaṃ ‘kāye kāyānupassī viharissāmi ātāpī sampajāno satimā vineyya loke abhijjhā domanassaṃ. Vedanāsu. Citte. Dhammesu dhammānupassī viharissāmi ātāpī sampajāno satimā vineyya loke abhijjhā domanassa’nti. Evañhi te gahapati sikkhitabba’’nti. Evaṃ kho āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>\r\n<p class="title">Iddhipādasaṃyutta</p>\r\n<p class="subhead">Moggallānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',254,replace(replace('<p class="bodytext">iddhipādasaṃyutte <a name="M2.0254"></a> āvuso catutthaṃ moggallānasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha bhāsitaṃ. Sambahulā bhante bhikkhū heṭṭhāmigāramātupāsāde viharanti uddhatā unnaḷā capalā mukharā vikiṇṇavācā muṭṭhassatino asampajānā asamāhitā bhantacittā pākatindriyā. Tasmiṃ bhante vatthusmiṃ āyasmantaṃ mahāmoggallānaṃ te bhikkhū saṃvejetvā ‘‘kiṃ nu tumhe bhikkhave saṃviggā lomahaṭṭhajātā ekamantaṃ ṭhitā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',256,replace(replace('<p class="bodytext">Acchariyaṃ <a name="M2.0256"></a> vata bho, abbhutaṃ vata bho.</p>\r\n<p class="bodytext">Nivātañca vata.</p>\r\n<p class="bodytext">Ayañca migāramātupāsādo gambhīranemo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',257,replace(replace('<p class="bodytext">Tumheva <a name="M2.0257"></a> kho bhikkhave saṃvejetukāmena moggallānena bhikkhunā pādaṅguṭṭhakena migāramātupāsādo saṅkampito sampakampito sampadhālito.</p>\r\n<p class="bodytext">Taṃ kiṃ maññatha bhikkhave, katamesaṃ dhammānaṃ bhāvitattā bahulīkatattā moggallāno bhikkhu evaṃ mahiddhiko evaṃ mahānubhāvo.</p>\r\n<p class="title">Anuruddhasaṃyutta</p>\r\n<p class="subhead">Bāḷhagilānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',259,replace(replace('<p class="bodytext">anuruddhasaṃyutte <a name="M2.0259"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi dasamaṃ saṃgītaṃ bāḷhaginānasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha āyasmatā anuruddhattherena bhāsitaṃ. Sambahulā bhante bhikkhū āyasmantaṃ anuruddhaṃ ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ etadavocuṃ ‘‘katamenāyasmato anuruddhassa vihārena viharato uppannā sārīrikā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhantī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘catūsu kho me āvuso satipaṭṭhānesu suppatiṭṭhitacittassa viharato uppannā sārīrikā dukkhā vedanā cittaṃ na pariyādāya tiṭṭhantī’’ti evamādinā āyasmatā anuruddhattherena bhāsitaṃ.</p>\r\n<p class="title">Ānāpānasaṃyutta</p>\r\n<p class="subhead">Mahākappinasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',261,replace(replace('<p class="bodytext">ānāpānasaṃyutte <a name="M2.0261"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi sattamaṃ saṃgītaṃ mahākappinasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘passatha no tumhe bhikkhave etassa bhikkhuno kāyassa iñjitattaṃ vā phanditabbaṃ vā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Icchānaṅgalasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',263,replace(replace('<p class="bodytext">tattheva <a name="M2.0263"></a> āvuso dutiyavagge paṭhamaṃ icchānaṅgalasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> icchānaṅgale bhante sambahule bhikkhū ārabbha ‘‘sace kho bhikkhave aññatitthiyā paribbājakā evaṃ puccheyyuṃ ‘katame nāvuso vihārena samaṇo gotamo vassāvāsaṃ bahulaṃ vihāsī’ti. Evaṃ puṭṭhā tumhe bhikkhave tesaṃ aññatitthiyānaṃ paribbājakānaṃ evaṃ byākareyyātha ānāpānassatisamādhinā kho āvuso bhagavā vassāvāsaṃ bahulaṃ vihāsī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Sotāpattisaṃyutta</p>\r\n<p class="subhead">Cakkavattirājasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',266,replace(replace('<p class="bodytext">sotāpattisaṃyutte <a name="M2.0266"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi paṭhamaṃ saṃgītaṃ cakkavattirājasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘kiñcāpi bhikkhave rājā cakkavattī catunnaṃ dīpānaṃ issariyādhipaccaṃ rajjaṃ kāretvā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjati devānaṃ tāvatiṃsānaṃ sahabyataṃ, so tattha nandane vane accharāsaṅghaparivuto dibbehi ca pañcahi kāmaguṇehi samappito samaṅgībhūto paricāreti so catūhi dhammehi asamannāgato, atha kho so aparimuttova nirayā, aparimutto tiracchānayoniyā, aparimutto pettivisayā, aparimutto apāyaduggativinipātā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dīghāvu upāsakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',268,replace(replace('<p class="bodytext">tattheva <a name="M2.0268"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi tatiyaṃ saṃgītaṃ dīghāvuupāsakasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',269,replace(replace('<p class="bodytext">rājagahe <a name="M2.0269"></a> bhante dīghāvuṃ upāsakaṃ ārabbha bhāsitaṃ. Bhagavā bhante dīghāvuṃ upāsakaṃ ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ etadavoca ‘‘kacci te dīghāvu khamanīyaṃ, kacci yāpanīyaṃ, kacci dukkhā vedanā paṭikkamanti no abhikkamanti, paṭikkamosānaṃ paññāyati no abhikkamo’’ti. Na me bhante khamanīyaṃ, na yāpanīyaṃ bāḷhā me dukkhā vedanā abhikkamanti no paṭikkamanti, abhikkamosānaṃ paññāyati no paṭikkamoti. Tasmiṃ bhante vatthusmiṃ ‘‘tasmātiha te dīghāvu evaṃ sikkhitabbaṃ buddhe aveccappasādena samannāgato bhavissāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',271,replace(replace('<p class="bodytext">Yānimāni <a name="M2.0271"></a> bhante bhagavatā cattāri sotāpattiyaṅgāni desitāni, saṃvijjante te dhammā mayi–</p>\r\n<p class="bodytext">Tasmātiha tvaṃ dīghāvu imesu catūsu sotāpattiyaṅgesu patiṭṭhāya cha vijjābhāgiye dhamme uttari bhāveyyāsi–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',272,replace(replace('<p class="bodytext">Mā <a name="M2.0272"></a> tvaṃ tāta dīghāvu evaṃ manasākāsi.</p>\r\n<p class="bodytext">Iṅgha tvaṃ tāta dīghāvu yadeva te bhagavā āha, tadeva tvaṃ sādhukaṃ manasikarohi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',273,replace(replace('<p class="bodytext">Paṇḍito <a name="M2.0273"></a> bhikkhave dīghāvu upāsako.</p>\r\n<p class="bodytext">Paccapādi dhammassānudhammaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',274,replace(replace('<p class="bodytext">Na <a name="M2.0274"></a> ca maṃ dhammādhikaraṇaṃ viheṭhesi.</p>\r\n<p class="bodytext">Dīghāvu bhikkhave upāsako pañcannaṃ orambhāgiyānaṃ saṃyojanānaṃ parikkhayā opapātiko tattha parinibbāyī anāvattidhammo tasmā lokā.</p>\r\n<p class="subhead">Veḷudvāreyyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… sammāsambuddhena sotāpattisaṃyutte sattamaṃ saṃgītaṃ veḷudvāreyyasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante veḷudvāre nāma kosalānaṃ brāhmaṇa gāme veḷudvāreyyake brāhmaṇagahapatike ārabbha bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',275,replace(replace('<p class="bodytext">Veḷudvāreyyakā <a name="M2.0275"></a> bhante brāhmaṇagahapatikā bhagavantaṃ etadavocuṃ ‘‘mayaṃ bho gotama evaṃkāmā evaṃchandā evaṃadhippāyā puttasambādhasayanaṃ ajjhāvaseyyāma…pe… jātarūparajataṃ sādiyeyyāma, kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjeyyāma, tesaṃ no bhavaṃ gotamo amhākaṃ evaṃkāmānaṃ evaṃchandānaṃ evaṃadhippāyānaṃ tathā dammaṃ desetu, yathā mayaṃ puttasambādhasayanaṃ ajjhāvaseyyāma…pe… kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjeyyāmā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘attūpanāyikaṃ vo gahapatayo dhammapariyāyaṃ desessāmi, taṃ suṇātha sādhukaṃ manasi karotha, bhāsissāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Saraṇānivagga</p>\r\n<p class="subhead">Paṭhama mahānāmasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',280,replace(replace('<p class="bodytext">tattheva <a name="M2.0280"></a> āvuso tatiye saraṇānivagge porāṇakehi dhammasaṃgāhakamahātherehi paṭhamaṃ saṃgītaṃ paṭhamamahānāmasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',281,replace(replace('<p class="bodytext">sakkesu <a name="M2.0281"></a> bhante kapilavatthusmiṃ nigrodhārāme mahānāmaṃ sakkaṃ ārabbha bhāsitaṃ. Mahānāmo bhante sakko bhagavantaṃ etadavoca ‘‘idaṃ bhante kapilavatthu iddhañceva phītañca bāhujaññaṃ ākiṇṇamanussaṃ sambādhabyūhaṃ, so khvāhaṃ bhante bhagavantaṃ vā payirupāsitvā manobhāvanīye vā bhikkhū sāyanhasamayaṃ kapilavatthuṃ pavisanto bhantenapi hatthinā samāgacchāmi, bhantenapi assena samāgacchāmi, bhantenapi rathena samāgacchāmi, bhantenapi sakaṭena samāgacchāmi, bhantenapi purisena samāgacchāmi, tassa mayhaṃ bhante tasmiṃ samaye mussateva bhagavantaṃ ārabbha sati, mussati dhammaṃ ārabbha sati, mussati saṅghaṃ ārabbha sati, tassa mayhaṃ bhante evaṃ hoti imamhi cāhaṃ samaye kālaṃ kareyyaṃ, kā mayhaṃ gati, ko abhisamparāyo’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘mā bhāyi mahānāma, mā bhāyi mahānāma, apāpakaṃ te maraṇaṃ bhavissati apāpikā kālaṃ kiriyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dutiya saraṇānisakkasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',284,replace(replace('<p class="bodytext">tattheva <a name="M2.0284"></a> āvuso dhammasaṃgāhakamahātherehi pañcamaṃ saṃgītaṃ dutiyasaraṇānisakkasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ nigrodhārāme mahānāmaṃyeva sakkaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext">Mahānāmo bhante sakko bhagavantaṃ etadavoca ‘‘idha bhante saraṇāni sakko kālaṅkato, so bhagavatā byākato ‘sotāpanno avinipātadhammo niyato sambodhiparāyaṇo’ti. Tatra sudaṃ bhante sambahulā sakkā saṅgamma samāgamma ujjhāyanti khīyanti vipācenti ‘acchariyaṃ vata bho, abbhutaṃ vata</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',285,replace(replace('<p class="bodytext">Bho<a name="M2.0285"></a>, ettha dāni ko na sotāpanno bhavissati, yatra hi nāma saraṇāni sakko kālaṅkato, so bhagavatā byākato ‘sotāpanno avinipātadhammo niyato sambodhiparāyaṇo’ti, saraṇāni sakko sikkhāya aparipūrakārī ahosī’’ti tasmiṃ bhante vatthusmiṃ ‘‘yo so mahānāma dīgharattaṃ upāsako buddhaṃ saraṇaṃ gato, dhammaṃ saraṇaṃ gato, saṅghaṃ saraṇaṃ gato, so kathaṃ vinipātaṃ gaccheyyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhama anāthapiṇḍikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',289,replace(replace('<p class="bodytext">tattheva <a name="M2.0289"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi chaṭṭhaṃ saṃgītaṃ paṭhamaanāthapiṇḍikasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante anāthapiṇḍikaṃ gahapatiṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="bodytext">Āyasmā bhante sāriputtatthero āyasmatā ānandattherena pacchāsamaṇena saddhiṃ yena anāthapiṇḍikassa gahapatissa nivesanaṃ tenupasaṅkami, upasaṅkamitvā paññatte āsane nisīdi, nisajja kho āyasmā sāriputto anāthapiṇḍikaṃ gahapatiṃ ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ etadavoca ‘‘kacci te gahapati khamanīyaṃ kacci yāpanīyaṃ, kacci dukkhā vedanā paṭikkamanti, no abhikkamanti, paṭikkamosānaṃ paññāyati, no abhikkamo’’ti, na me bhante khamanīyaṃ, na yāpanīyaṃ, bāḷhā me dukkhā vedanā abhikkamanti, no paṭikkamanti, abhikkamosānaṃ paññāyati, no paṭikkamoti. Tasmiṃ bhante vatthusmiṃ ‘‘yathā rūpena kho gahapati buddhe appasādena samannāgato assutavā puthujjano kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ niyaraṃ upapajjati tathārūpo te buddhe appasādo natthi, atthi ca kho te gahapati buddhe aveccappasādo ‘itipi so bhagavā arahaṃ sammāsambuddho…pe… bhagavā’ti tañca pana te buddhe aveccappasādaṃ attani samanupassato ṭhānaso vedanā paṭippassambheyyā’’ti evamādinā āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',293,replace(replace('<p class="gatha1">Yassa <a name="M2.0293"></a> saddhā tathāgate, acalā suppatiṭṭhitā;</p>\r\n<p class="gathalast">Sīlañca yassa kalyāṇaṃ, ariyakantaṃ pasaṃsitaṃ.</p>\r\n<p class="gatha1">Saṅghe pasādo yassatthi, ujubhūtañca dassanaṃ;</p>\r\n<p class="gathalast">Adaliddoti taṃ āhu, amoghaṃ tassa jīvitaṃ.</p>\r\n<p class="gatha1">Tasmā saddhañca sīlañca, pasādaṃ dhammadassanaṃ;</p>\r\n<p class="gathalast">Anuyuñjetha medhāvī, saraṃ buddhānasāsanaṃ.</p>\r\n<p class="title">Puññābhisandavagga</p>\r\n<p class="subhead">Mahānāmasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso puññābhisandavaggo porāṇakehi dhammasaṃgāhakamahātherehi sattamaṃ saṃgītaṃ mahānāmasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',294,replace(replace('<p class="bodytext">sakkesu <a name="M2.0294"></a> bhante kapilavatthusmiṃ mahānāmaṃ sakkaṃ ārabbha bhāsitaṃ.</p>\r\n<p class="bodytext">Mahānāmo bhante sakko bhagavantaṃ etadavoca ‘‘kittāvatā nu kho bhante upāsako hotī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yato kho mahānāma buddhaṃ saraṇaṃ gato hoti, dhammaṃ saraṇaṃ gato hoti, saṅghaṃ saraṇaṃ gato hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Kittāvatā nu kho bhante upāsako hoti.</p>\r\n<p class="subhead">Kāḷigodhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',297,replace(replace('<p class="bodytext">tattheva <a name="M2.0297"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi navamaṃ saṃgītaṃ kāḷigodhasuttaṃ bhagavatā kattha kaṃ kārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kāḷigodhaṃ nāma sākiyāniṃ ārabbha ‘‘catūhi kho godhe dhammehi samannāgatā ariyasāvikā sotāpannā hoti avinipātadhammā niyatā sambodhiparāyaṇā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',300,replace(replace('<p class="bodytext">Lābhā <a name="M2.0300"></a> te godhe, suladdhaṃ te godhe, sotāpattiphalaṃ tayā godhe –</p>\r\n<p class="subhead">Nandiyasakkasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso jānatā…pe… sammāsambuddhena sotāpattisaṃyutte puññābhisandavagge dasamaṃ nandiyasakkasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante kapilavatthusmiṃ nigrodhārāme nandiyaṃ sakkaṃ ārabbha bhāsitaṃ. Nandiyo bhante sakko bhagavantaṃ etadavoca ‘‘yasseva nu kho bhante ariyasāvakassa cattāri sotāpattiyaṅgāni sabbena sabbaṃ sabbathāsabbaṃ natthi, sveva nu kho bhante ariyasāvako pamādavihārī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yassa kho nandiya cattāri sotāpattiyaṅgāni sabbenasabbaṃ sabbathāsabbaṃ natthi, tamahaṃ bāhiro puthujjanapakkhe ṭhitoti vadāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="title">Saccasaṃyutta</p>\r\n<p class="subhead">Tiracchānakathāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',304,replace(replace('<p class="bodytext">saccasaṃyutte <a name="M2.0304"></a> āvuso porāṇakehi dhammasaṃgāyakamahātherehi dasamaṃ saṃgītaṃ tiracchānakathāsuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha mā bhikkhave anekavihitaṃ tiracchānakathaṃ katheyyātha. Seyyathidaṃ, rājakathaṃ corakathaṃ mahāmattakathaṃ senākathaṃ bhayakathaṃ yuddhakathaṃ annakathaṃ pānakathaṃ vatthakathaṃ sayanakathaṃ evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',305,replace(replace('<p class="bodytext">Idaṃ <a name="M2.0305"></a> dukkhanti katheyyātha.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',306,replace(replace('<p class="bodytext">Tasmātiha <a name="M2.0306"></a> bhikkhave idaṃ dukkhanti yogo karaṇīyo…pe… ayaṃ dukkhanirodhagāminī paṭipadāti yogo karaṇīyo.</p>\r\n<p class="subhead">Dhammacakkapavattanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso dhammacakkappavattanavagge porāṇakehi dhammasaṃgāhakamahātherehi paṭhamaṃ saṃgītaṃ dhammacakkapavattanasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante isipatane migadāye pañcavaggiye bhikkhū ārabbha ‘‘dve me bhikkhave antā pabbajitena nasevitabbā. Katame dve, yo cāyaṃ kāmesu kāmasukhallikānuyogo hīno gammo pothujjaniko anariyo anatthasaṃhito, yo cāyaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',307,replace(replace('<p class="bodytext">attakilamathānuyogo <a name="M2.0307"></a> dukkho anariyo anatthasaṃhito, ete kho bhikkhave ubho ante anupagamma majjhimāpaṭipadā tathāgatena abhisambuddhā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āsavakkhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',308,replace(replace('<p class="bodytext">tattheva <a name="M2.0308"></a> āvuso koṭigāmavagge porāṇakehi dhammasaṃgāhakamahātherehi pañcamaṃ saṃgītaṃ āsavakkhayasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘jānatohaṃ bhikkhave passato āsavānaṃ khayaṃ vadāmi, no ajānato apassato. Kiñca bhikkhave jānato passato āsavānaṃ khayo hoti, idaṃ dukkhanti bhikkhave jānato passato āsavānaṃ khayo hoti, ayaṃ dukkhasamudayoti, ayaṃ dukkhanirodhoti, ayaṃ dukkhanirodhagāminīpaṭipadāti jānato passato āsavānaṃ khayo hoti. Evaṃ kho bhikkhave jānato evaṃ passato āsavānaṃ khayo hoti. Tasmātiha bhikkhave ‘‘idaṃ dukkha’’nti yogo karaṇīyo, ayaṃ dukkhasamudayoti, ayaṃ dukkhanirodhoti, ayaṃ dukkhanirodhagāminīpaṭipadāti yogo karaṇīyo’’ti evaṃ kho bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sīsapāvanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',309,replace(replace('<p class="bodytext">tattheva <a name="M2.0309"></a> āvuso sīsapāvanavagge porāṇakehi dhammasaṃgāhakamahātherehi paṭhamaṃ saṃgītaṃ sīsapāvanasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',310,replace(replace('<p class="bodytext">kosambiyaṃ <a name="M2.0310"></a> bhante sīsapāvane sambahule bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, katamaṃ nukho bahutaraṃ yāni vā mayā parittāni sīsapāpaṇṇāni pāṇinā gahitāni yadidaṃ upari sīsapāvane’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Pañcagatipeyyālavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',312,replace(replace('<p class="bodytext">tattheva <a name="M2.0312"></a> āvuso porāṇakehi dhammasaṃgāhakamahātherehi pariyosānabhāvena saṃgīto pañcagatipeyyālavagge, bhagavatā kattha kaṃ ārabbha kathañca bhāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante sambahule bhikkhū ārabbha ‘‘taṃ kiṃ maññatha bhikkhave, katamaṃ nu kho bahutaraṃ, yo vāyaṃ mayā paritto nakhasikhāyaṃ paṃsu āropito, ayaṃ vā mahāpathavī’’ti evamādinā bhagavatā bhāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',315,replace(replace('<p class="bodytext">ke <a name="M2.0315"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhā ca bhante puthujjanakalyāṇakā ca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',316,replace(replace('<p class="bodytext">ke <a name="M2.0316"></a> āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_85',317,replace(replace('<p class="bodytext">kassa <a name="M2.0317"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kena āvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>','\r',char(13)),'\n',char(10)),'');