INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="nikaya">Suttantapiṭaka</p>\r\n<p class="chapter">Aṅguttaranikāye</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',1,replace(replace('<p class="bodytext">paṭhamamahāsaṃgītikāle <a name="M1.0001"></a> āvuso dhammasaṃgāhakā mahākassapādayo mahātheravarā porāṇasaṃgītikārā paṭhamaṃ vinayapiṭakaṃ saṃgāyitvā suttantapiṭake ca dīghamajjhimasaṃyuttasaṅkhāte tayo nikāye saṃgāyitvā tadanantaraṃ kiṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamamahāsaṃgītikāle bhante dhammasaṃgāhakā mahākassapādayo mahātheravarā porāṇasaṃgītikārā paṭhamaṃ vinayaṃ saṃgāyitvā suttantapiṭake ca dīghamajjhimasaṃyuttasaṅkhāte tayo nikāye saṃgāyitvā tadanantaraṃ navahi ca suttasahassehi pañcahi ca suttasattehi sattapaññāsāya ca suttehi paṭimaṇḍitaṃ vīsatibhāṇavārasataparimāṇaṃ aṅguttaranikāyaṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',2,replace(replace('<p class="bodytext">aṅguttaranikāyepi <a name="M1.0002"></a> āvuso ekakanipāto dukanipāto tikanipātotiādinā nipātapakaraṇaparicchedavasena ekādasavidhā. Tattha kataraṃ nipātapakaraṇaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṅguttaranikāye bhante ekādasasu nipātapakaraṇaparicchedesu ekakanipātaṃ paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="subhead">1. Rūpādivagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',3,replace(replace('<p class="bodytext">ekakanipātepi <a name="M1.0003"></a> āvuso rūpādivaggo nīvaraṇappahānavaggo akammaniyavaggotiādinā vaggabhedavasena bahuvidhā. Tattha kataraṃ vaggaṃ paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekakanipāte bhante rūpādivaggo nīvaraṇappahānavaggotiādinā vīsatiyā vaggesu rūpādivaggaṃ paṭhamaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhu sādhu āvuso, mayampi dāni āvuso tatoyeva paṭṭhāya saṃgāhanatthāya saṃgītipubbaṅgamāni dhammapucchanavissajjanakiccāni āvahituṃ samārabhāma. Tenāvuso bhagavatā jānatā passatā arahatā sammāsambuddhena aṅguttaranikāye ekakanipāte rūpādivagge purimāni pañca suttāni kattha kaṃ ārabbha kathañca bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',4,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0004"></a> bhante sambahule bhikkhū ārabbha rūpādigarukānaṃ pañcannaṃ purisānaṃ ajjhāsayavasena ‘‘nāhaṃ bhikkhave aññaṃ ekarūpampi samanupassāmi, yaṃ evaṃ purisassa cittaṃ pariyādāya tiṭṭhati, yathayidaṃ bhikkhave itthirūpaṃ, itthirūpaṃ bhikkhave purisassa cittaṃ pariyādāya tiṭṭhatī’’ti evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="bodytext">Nāhaṃ bhikkhave aññaṃ ekarūpampi samanupassāmi, yaṃ evaṃ purisassa cittaṃ pariyādāya tiṭṭhati, yathayidaṃ bhikkhave itthirūpaṃ, itthirūpaṃ bhikkhave purisassa cittaṃ pariyādāya tiṭṭhati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',5,replace(replace('<p class="bodytext">tattheva <a name="M1.0005"></a> āvuso pacchimāni pañca suttāni bhagavatā kattha kaṃ ārabbha kathañca bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃyeva bhante sāvatthiyaṃ sambahule bhikkhū ārabbha rūpādigarukānaṃ pañcannaṃ itthīnaṃ ajjhāsayavasena ‘‘nāhaṃ bhikkhave aññaṃ ekarūpampi samanupassāmi, yaṃ evaṃ itthiyā cittaṃ pariyādāya tiṭṭhati, yathayidaṃ bhikkhave purisarūpaṃ, purisarūpaṃ bhikkhave itthiyā cittaṃ pariyādāya tiṭṭhatī’’ti evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="bodytext">Nāhaṃ bhikkhave aññaṃ ekarūpampi samanupassāmi, yaṃ evaṃ itthiyā cittaṃ pariyādāya tiṭṭhati, yathayidaṃ bhikkhave purisarūpaṃ, purisarūpaṃ bhikkhave itthiyā cittaṃ pariyādāya tiṭṭhati.</p>\r\n<p class="subhead">Nītatthavasena, neyyatthavasena</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',6,replace(replace('<p class="bodytext">imasmiṃ <a name="M1.0006"></a> āvuso rūpādivagge bhagavatā nītatthavasena kiṃ kathitaṃ, neyyatthavasena pana kiṃ ñāpitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ bhante rūpādivagge bhagavatā nītatthavasena vaṭṭaṃ kathitaṃ. Neyyatthavasena pana vivaṭṭampi ñāpitaṃ.</p>\r\n<p class="subhead">2. Nīvaraṇappahānavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',7,replace(replace('<p class="bodytext">dutiyo <a name="M1.0007"></a> pana āvuso nīvaraṇappahānavaggo bhagavatā kattha kathañca bhāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yena anuppanno vā kāmacchando uppajjati, uppanno vā kāmacchando bhiyyobhāvāya vepullāya saṃvattati, yathayidaṃ bhikkhave subhanimittaṃ, subhanimittaṃ bhikkhave ayoniso manasikaroto anuppannoceva kāmacchando uppajjati, uppanno ca kāmacchando bhiyyobhāvāya vepullāya saṃvattatī’’ti evamādinā bhagavatā bhāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',8,replace(replace('<p class="bodytext">imasmiṃ <a name="M1.0008"></a> pana āvuso dutiye nīvaraṇappahānavagge bhagavatā kiṃ kathitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ pana bhante dutiye nīvaraṇappahānavagge bhagavatā vaṭṭampi vivaṭṭampi kathitaṃ.</p>\r\n<p class="subhead">3. Akammaniyava</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',9,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0009"></a> sammāsambuddhena aṅguttaranikāye ekakanipāte tatiye akammaniyavagge kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tatiye bhante akammaniyavagge ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yaṃ evaṃ abhāvitaṃ akammaniyaṃ hoti, yathayidaṃ bhikkhave cittaṃ, cittaṃ bhikkhave abhāvitaṃ akammaniyaṃ hoti. Nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yaṃ evaṃ bhāvitaṃ kammaniyaṃ hoti, yathayidaṃ bhikkhave cittaṃ. Cittaṃ bhikkhave bhāvitaṃ kammaniyaṃ hotī’’ti evamādikā bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">4. Adantavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',10,replace(replace('<p class="bodytext">catutthe <a name="M1.0010"></a> panāvuso adantavagge bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catutthe bhante adantavagge ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yaṃ evaṃ adantaṃ mahato anatthāya saṃvattati, yathayidaṃ bhikkhave cittaṃ, cittaṃ bhikkhave adantaṃ mahato anatthāya saṃvattati. Nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yaṃ evaṃ dantaṃ mahato atthāya saṃvattati, yathayidaṃ bhikkhave cittaṃ, cittaṃ bhikkhave dantaṃ mahato atthāya saṃvattatī’’ti evamādikā bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">5. Paṇihitaacchavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',11,replace(replace('<p class="bodytext">pañcame <a name="M1.0011"></a> āvuso paṇihitaacchavagge bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcame bhante paṇihitaacchavagge ‘‘seyyathāpi bhikkhave sālisūkaṃ vā yavasūkaṃ vā micchāpaṇihitaṃ hatthena vā pādena vā akkantaṃ hatthaṃ vā pādaṃ vā bhecchati, lohitaṃ vā uppādessatīti netaṃ ṭhāna vijjati. Taṃ kissahetu, micchāpaṇihitattā bhikkhave sūkassa. Evameva kho bhikkhave so vata bhikkhu micchāpaṇihitena cittena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',12,replace(replace('<p class="bodytext">avijjaṃ <a name="M1.0012"></a> bhecchati, vijjaṃ uppādessati, nibbānaṃ sacchikarissatīti netaṃ ṭhānaṃ vijjati, taṃ kissa hetu, micchāpaṇihitattā bhikkhave cittassā’’ti evamādikā bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">6. Accharāsaṅghātavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',13,replace(replace('<p class="bodytext">accharāsaṅghātavagge <a name="M1.0013"></a> āvuso paṭhama dutiyasuttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> accharāsaṅghātavagge bhante bhagavatā ‘‘pabhassaramidaṃ bhikkhave cittaṃ, tañca kho āgantukehi upakkilesehi upakkiliṭṭhaṃ, taṃ assutavā puthujjano yathābhūtaṃ nappajānāti, tasmā ‘assutavato puthujjanassa cittabhāvanā natthī’ti vadāmi. Pabhassaramidaṃ bhikkhave cittaṃ, tañca kho āgantukehi upakkilesehi vippamuttaṃ, taṃ subhavā ariyasāvako yathābhūtaṃ pajānāti. Tasmā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',14,replace(replace('<p class="bodytext">‘sutapato <a name="M1.0014"></a> ariyasāvakassa cittabhāvanā atthī’ti vadāmī’’ti. Evaṃ kho bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Tatiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',15,replace(replace('<p class="bodytext">tattheva <a name="M1.0015"></a> āvuso tatiyasuttādīni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante tatiyasuttādīni pañcasuttāni ‘‘accharāsaṅghātamattampi ce bhikkhave bhikkhu mettācittaṃ āsevati, bhāveti, manasikaroti. Ayaṃ vuccati bhikkhave bhikkhu arittajjhāno viharati satthusāsanakaro ovādapatikaro amoghaṃ raṭṭhapiṇḍaṃ bhuñjati, ko pana vādo ye naṃ bahulīkaronti. Yekeci bhikkhave dhammā akusalā akusalabhāgiyā akusalapakkhikā, sabbete manopubbaṅgamā, mano tesaṃ dhammānaṃ paṭhamaṃ uppajjati, anvadeva akusalādhammā, yekeci bhikkhave dhammā kusalā kusalabhāgiyā kusalapakkhikā, sabbete manopubbaṅgamā, mano tesaṃ dhammānaṃ paṭhamaṃ uppajjati, anvadeva kusalā dhammā’’ti evaṃ kho bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Aṭṭhamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',17,replace(replace('<p class="bodytext">tattheva <a name="M1.0017"></a> āvuso aṭṭhama navama dasamasuttāni ca vīriyārambhādivagge dasasuttāni ca kalyāṇamittādivagge paṭhamasuttañca bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante aṭṭhama navama dasamasuttāni ca vīriyārambhādivagge dasasuttāni ca kalyāṇamittādivagge paṭhamasuttañca ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yena anuppannā vā akusalā dhammā uppajjanti, uppannā vā kusalā dhammā parihāyanti, yathayidaṃ bhikkhave pamādo, pamattassa bhikkhave anuppannā ceva akusalā dhammā uppajjanti, uppannā ca kusalā dhammā parihāyantī’’ti evamādinā bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Dutiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',19,replace(replace('<p class="bodytext">kalyāṇamittādivagge <a name="M1.0019"></a> āvuso dutiya tatiyasuttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kalyāṇamittādivagge bhante dutiya tatiyasuttāni ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yena anuppannā vā akusalā dhammā uppajjanti, uppannā vā kusalā dhammā parihāyanti, yathayidaṃ bhikkhave anuyogo akusalānaṃ dhammānaṃ ananuyogo kusalānaṃ dhammāna’’nti evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Chaṭṭhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',20,replace(replace('<p class="bodytext">tattheva <a name="M1.0020"></a> āvuso chaṭṭhasuttādīni ca pamādādivagge paṭhamasuttañca bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante chaṭṭhasuttādīni ca pamādādivagge paṭhamasuttañca ‘‘appamattikā esā bhikkhave parihāni yadidaṃ ñātiparihāni. Etaṃ patikiṭṭhaṃ bhikkhave parihānīnaṃ yadidaṃ paññāparihānīti. Appamattikā esā bhikkhave vuddhi yadidaṃ ñātivuddhi. Etadaggaṃ bhikkhave vuddhīnaṃ yadidaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',21,replace(replace('<p class="bodytext">paññāvuddhi<a name="M1.0021"></a>. Tasmātiha bhikkhave evaṃ sikkhitabbaṃ ‘paññāvuddhiyā vaddhissāmā’ti. Evañhi vo bhikkhave sikkhitabba’’nti evamādinā bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Pamādādivagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',22,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0022"></a> sammāsambuddhena pamādādivagge dutiyasuttādīni kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yo evaṃ mahato anatthāya saṃvattati, yathayidaṃ bhikkhave pamādo. Pamādo bhikkhave mahato anatthāya saṃvattati. Nāhaṃ bhikkhave aññaṃ ekadhammampi, yo evaṃ mahato atthāya saṃvattati, yathayidaṃ bhikkhave appamādo, appamādo bhikkhave mahato anatthāya saṃvattatīti evamādinā bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Dutiya pamādādivagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',24,replace(replace('<p class="bodytext">dutiye <a name="M1.0024"></a> panāvuso pamādādivagge paṭhamasuttādīni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiye bhante pamādādivagge ajjhattikaṃ bhikkhave aṅganti karitvā nāññaṃ ekaṅgampi samanupassāmi, yaṃ evaṃ mahato anatthāya saṃvattati, yathayidaṃ bhikkhave pamādo. Pamādo bhikkhave mahato anatthāya saṃvattati. Ajjhattikaṃ bhikkhave aṅganti karitvā nāññaṃ ekaṅgampi samanupassāmi, yaṃ evaṃ mahato atthāya saṃvattati, yathayidaṃ bhikkhave appamādo. Appamādo bhikkhave mahato atthāya saṃvattatīti evamādinā bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Dasasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',26,replace(replace('<p class="bodytext">tattheva <a name="M1.0026"></a> āvuso tettiṃsamādīnica dasasuttāni adhammavagge ca dasasuttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante tettiṃsamādīni dasasuttāni ca adhammavagge dasasuttāni ca ye te bhikkhave bhikkhū adhammaṃ ‘‘dhammo’’ti dīpenti, te bhikkhave bhikkhū bahujanaahitāya paṭipannā bahujanaasukhāya bahuno janassa anatthāya ahitāya dukkhāya devamanussānaṃ. Bahuñca te bhikkhave bhikkhū apuññaṃ pasavanti, te cimaṃ saddhammaṃ antaradhāpentīti evamādinā ca, ye te bhikkhave bhikkhū adhammaṃ ‘‘adhammo’’ti dīpenti, te bhikkhave bhikkhū bahujanahitāya paṭipannā bahujanasukhāya bahujano janassa atthāya hitāya sukhāya devamanussānaṃ. Bahuñca te bhikkhave bhikkhū puññaṃ pasavanti, te cimaṃ saddhammaṃ ṭhapentīti evamādinā ca bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Ekapuggalavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',29,replace(replace('<p class="bodytext">ekapuggalavagge <a name="M1.0029"></a> āvuso bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekapuggalavagge bhante bhagavatā ekapuggalo bhikkhave loke uppajjamāno uppajjati bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ. Katamo ekapuggalo, tathāgato arahaṃ sammāsambuddho, ayaṃ kho bhikkhave ekapuggalo loke uppajjamāno uppajjati bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānanti evamādikā bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">Etadaggavagga</p>\r\n<p class="subhead">Aññāsikoṇḍañña vatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',30,replace(replace('<p class="bodytext">etadavagge <a name="M1.0030"></a> āvuso āgatesu ekacattālīsāya theresu āyasmā aññāsikoṇḍaññatthero kathaṃ bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> etadaggavagge bhante āgatesu ekacattālīsāya mahātheresu ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ rattaññūnaṃ yadidaṃ aññāsikoṇḍañño’’ti, evaṃ kho bhante āyasmā aññāsikoṇḍaññatthero bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="subhead">Aggasāvakavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',36,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0036"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatesu ekacattālīsāya mahātheresu āyasmā ca sāriputtatthero dhammasenāpati paṭhamo aggasāvako āyasmā ca mahāmoggallānatthero dutiyo aggasāvako kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante sāriputtatthero dhammasenāpati ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ mahāpaññānaṃ yadidaṃ sāriputto’’ti. Āyasmā pana mahāmoggallānatthero ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ iddhimantānaṃ yadidaṃ mahāmoggallāno’’ti. Evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',40,replace(replace('<p class="gatha1">Idaṃ <a name="M1.0040"></a> te āsanaṃ vīra, paññattaṃ tavanucchaviṃ;</p>\r\n<p class="gathalast">Mama cittaṃ pasādento, nisīda pupphamāsane.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',44,replace(replace('<p class="bodytext">Ye <a name="M1.0044"></a> vata loke arahanto vā arahattamaggaṃ vā samāpannā, ayaṃ tesaṃ bhikkhu aññataro.</p>\r\n<p class="bodytext">Yaṃnūnāhaṃ imaṃ bhikkhuṃ upasaṅkamitvā pañhaṃ puccheyyaṃ.</p>\r\n<p class="gatha1">Ye dhammā hetuppabhavā,</p>\r\n<p class="gatha2">Tesaṃ hetuṃ tathāgato;</p>\r\n<p class="gatha3">Āha tesañca yo nirodho,</p>\r\n<p class="gathalast">Evaṃvādī mahāsamaṇo –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',46,replace(replace('<p class="bodytext">Labheyyāma <a name="M1.0046"></a> mayaṃ bhante bhagavato santike pabbajjaṃ, labheyyāma upasampadaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',47,replace(replace('<p class="bodytext">Etadaggaṃ <a name="M1.0047"></a> bhikkhave mama sāvakānaṃ bhikkhūnaṃ mahāpaññānaṃ yadidaṃ sāriputto.</p>\r\n<p class="bodytext">Etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ iddhimantānaṃ yadidaṃ mahāmoggallāno.</p>\r\n<p class="subhead">Mahākassapavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',48,replace(replace('<p class="bodytext">āyasmā <a name="M1.0048"></a> pana āvuso mahākassapatthero paṭhamamahāsaṃgītikāle pāmokkhasaṅghanāyakabhūto bhagavatā kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante mahākassapatthero paṭhamamahāsaṃgītikāle saṅghapāmokkhabhūto ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ dhutavādānaṃ yadidaṃ mahākassapo’’ti. Evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="subhead">Anuruddhattheravatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',51,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0051"></a> sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatesu ekacattālīsāya mahātheresu āyasmā anuruddhatthero kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',52,replace(replace('<p class="bodytext">āyasmā <a name="M1.0052"></a> bhante anuruddhatthero ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ dibbacakkhukānaṃ yadidaṃ anuruddho’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="subhead">Sīvalivatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',54,replace(replace('<p class="bodytext">āyasmā <a name="M1.0054"></a> pana āvuso sīvalitthero bhagavatā kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante sīvalitthero ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ lābhīnaṃ yadidaṃ sīvalī’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',56,replace(replace('<p class="bodytext">Sammāsambuddho <a name="M1.0056"></a> vata so bhagavā, yo imassa evarūpassa dukkhassa pahānāya dhammaṃ deseti.</p>\r\n<p class="bodytext">Suppaṭipanno vata tassa bhagavato sāvakasaṅgho, yo imassa evarūpassa dukkhassa pahānāya paṭipanno.</p>\r\n<p class="bodytext">Susukhaṃ vata nibbānaṃ, yatthidaṃ evarūpaṃ dukkhaṃ na saṃvijjati –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',58,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0058"></a> bhikkhave mama sāvakānaṃ bhikkhūnaṃ lābhīnaṃ yadidaṃ sīvali’’ hu –</p>\r\n<p class="subhead">Ānandātheravatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> āyasmā pana āvuso ānandatthero dhammabhaṇḍāgāriko paṭhamamahāsaṃgītikāle dhammavissajjakatherabhūto kathaṃ bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',59,replace(replace('<p class="bodytext">āyasmā <a name="M1.0059"></a> bhante ānandatthero dhammabhaṇḍāgāriko paṭhamamahāsaṃgītikāle dhammavissajjakabhūto ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ bahussutānaṃ yadidaṃ ānando. Etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ satimantānaṃ yadidaṃ ānando. Etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ gatimantānaṃ yadidaṃ ānando. Etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ dhitimantānaṃ yadidaṃ ānando. Etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ upaṭṭhākānaṃ yadidaṃ ānando’’ti evaṃ kho bhante bhagavatā pañcasu etadaggaṭṭhānesu ṭhapito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',61,replace(replace('<p class="gatha1">Satasahassena <a name="M1.0061"></a> me kītaṃ,</p>\r\n<p class="gatha2">Satasahassena māpitaṃ;</p>\r\n<p class="gatha3">Sobhanaṃ nāma uyyānaṃ;</p>\r\n<p class="gathalast">Paṭiggaṇha mahāmuni –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',64,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0064"></a> bhikkhave mama sāvakānaṃ bhikkhūnaṃ bahussutānaṃ. Satimantānaṃ. Gatimantānaṃ. Dhitimantānaṃ. Upaṭṭhākānaṃ yadidaṃ ānando’’ –</p>\r\n<p class="subhead">Upālivatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',65,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0065"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatesu ekacattālīsāya mahātheresu āyasmā upālitthero paṭhamamahāsaṃgītikāle vinayavissajjakabhūto kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante upālitthero paṭhamamahāsaṃgītikāle vinayavissajjakabhūto ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ bhikkhūnaṃ vinayadharānaṃ yadidaṃ upālī’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="gatha1">Vinayo vāsayo mayhaṃ,</p>\r\n<p class="gatha2">Vinayo ṭhānacaṅkamaṃ;</p>\r\n<p class="gatha3">Kappemi vinaye vāsaṃ,</p>\r\n<p class="gathalast">Vinayo mama gocaro.</p>\r\n<p class="subhead">Mahāpajāpatigotamītherīvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',68,replace(replace('<p class="bodytext">etadaggavagge <a name="M1.0068"></a> āvuso āgatāsu terasasu therīsu mahāpajāpati gotamītherī kathaṃ bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',69,replace(replace('<p class="bodytext">etadaggavagge <a name="M1.0069"></a> bhante āgatāsu terasasu therīsu mahāpajāpatigotamītherī ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ rattaññūnaṃ yadidaṃ mahāpajāpati gotamī’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',73,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0073"></a> bhikkhave mama sāvikānaṃ bhikkhunīnaṃ rattaññūnaṃ yadidaṃ mahāpajāpatigotamī’’ –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> khemātherī pana āvuso aggasāvikā bhagavatā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> khemātherī bhante paṭhamā aggasāvikā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ mahāpaññānaṃ yadidaṃ khemā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="gatha1">‘‘Ye rāgarattānupatanti sotaṃ,</p>\r\n<p class="gatha2">Sayaṃkataṃ makkaṭova jālaṃ,</p>\r\n<p class="gatha3">Etampi chetvāna vajanti dhīrā;</p>\r\n<p class="gathalast">Anapekkhino sabbadukkhaṃ pahāya’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',77,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0077"></a> bhikkhave mama sāvikānaṃ bhikkhunīnaṃ mahāpaññānaṃ yadidaṃ khemā’’ –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> uppalavaṇṇā pana āvuso therī dutiyaaggasāvikā bhagavatā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uppalavaṇṇā bhante therī dutiyaaggasāvikā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ iddhimantīnaṃ yadidaṃ uppalavaṇṇā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext">‘‘Etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ iddhimantīnaṃ yadidaṃ uppalavaṇṇā’’ –</p>\r\n<p class="subhead">Paṭācārātherībhikkhunīmavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatāsu terasasu therīsu paṭācārānāma therī kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> etadaggavagge bhante āgatāsu terasasu therikāsu paṭācārā therī ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ vinayadharānaṃ yadidaṃ paṭācārā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="gatha1">‘‘Ubho puttā kālaṅkatā,</p>\r\n<p class="gatha2">Panthe mayhaṃ patī mato;</p>\r\n<p class="gatha3">Mātāpitā ca bhātā ca,</p>\r\n<p class="gathalast">Ekacitakasmiṃ ḍayhare’’ –</p>\r\n<p class="gatha1">‘‘Catūsu samuddesu jalaṃ parittakaṃ,</p>\r\n<p class="gatha2">Tato bahuṃ assujalaṃ anappakaṃ;</p>\r\n<p class="gatha3">Dukkhena phuṭṭhassa narassa socanā,</p>\r\n<p class="gathalast">Kiṃ kāraṇā amma tuvaṃ pamajjasi’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',83,replace(replace('<p class="gatha1">‘‘Na <a name="M1.0083"></a> santi puttā tāṇāya,</p>\r\n<p class="gatha2">Na pitā nāpi bandhavā;</p>\r\n<p class="gatha3">Antakenādhipannassa,</p>\r\n<p class="gatha2">Natthi ñātīsu tāṇatā;</p>\r\n<p class="gatha3">Etamatthavasaṃ ñatvā, paṇḍito sīlasaṃvuto;</p>\r\n<p class="gathalast">Nibbānagamanaṃ maggaṃ, khippameva visodhaye’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',84,replace(replace('<p class="gatha1">‘‘Yo <a name="M1.0084"></a> ca vassasataṃ jīve,</p>\r\n<p class="gatha2">Apassaṃ udayabbayaṃ;</p>\r\n<p class="gatha3">Ekāhaṃ jīvitaṃ seyyo,</p>\r\n<p class="gathalast">Passato udayabbayaṃ’’ –</p>\r\n<p class="bodytext">‘‘Etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ vinayadharānaṃ yadidaṃ paṭācārā’’ –</p>\r\n<p class="subhead">Dhammadinnātherībhikkhunīmavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',85,replace(replace('<p class="bodytext">dhammadinnā <a name="M1.0085"></a> pana āvuso therī bhagavatā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dhammadinnā bhante therī ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ dhammakathikānaṃ yadidaṃ dhammadinnā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',88,replace(replace('<p class="gatha1">‘‘Yassa <a name="M1.0088"></a> pure ca pacchā ca,</p>\r\n<p class="gatha2">Majjheca natthi kiñcanaṃ;</p>\r\n<p class="gatha3">Akiñcanaṃ anādānaṃ,</p>\r\n<p class="gathalast">Tamahaṃ brūmi brāhmaṇaṃ’’ –</p>\r\n<p class="bodytext">‘‘Etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ dhammakathikānaṃ yadidaṃ dhammadinnā’’ –</p>\r\n<p class="subhead">Yasodharātherībhikkhunīmavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhaddakaccānānāma āvuso yasodharātherī bhagavatā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',89,replace(replace('<p class="bodytext">bhaddakaccānā <a name="M1.0089"></a> bhante yasodharātherī bhagavatā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ bhikkhunīnaṃ mahābhiññapattānaṃ yadidaṃ bhaddakaccānā’’ti. Evaṃ kho bhante etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',90,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0090"></a> bhikkhave mama sāvikānaṃ bhikkhunīnaṃ mahābhiññapattānaṃ yadidaṃ bhaddakaccānā’’ –</p>\r\n<p class="subhead">Tapussabhallikavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',91,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M1.0091"></a> āvuso etadaggavagge āgatesu ekādasasu upāsakesu tapussabhallikānāma vāṇijā bhagavatā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tapussabhallikā bhante vāṇijā ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ upāsakānaṃ paṭhamaṃ saraṇaṃ gacchantānaṃ yadidaṃ tapussabhallikā vāṇijā’’ti, evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',93,replace(replace('<p class="bodytext">‘‘Ete <a name="M1.0093"></a> bhante mayaṃ bhagavantaṃ saraṇaṃ gacchāma dhammañca, upāsake no bhagavā dhāretu ajjatagge pāṇupete saraṇaṃ gate’’ hu –</p>\r\n<p class="bodytext">‘‘Etadaggaṃ bhikkhave mama sāvakānaṃ upāsakānaṃ paṭhamaṃ saraṇaṃ gacchantānaṃ yadidaṃ tapussabhallikā vāṇijā’’ –</p>\r\n<p class="subhead">Anāthapiṇḍakavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',94,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0094"></a> bhagavatā arahatā…pe… sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatesu ekādasasu upāsakesu sudatto gahapati anāthapiṇḍiko kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> etadaggavagge bhante āgatesu ekādasasu upāsakesu sudatto gahapati anāthapiṇḍiko ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ upāsakānaṃ dāyakānaṃ yadidaṃ sudatto gahapati anāthapiṇḍiko’’ti. Evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',97,replace(replace('<p class="bodytext">Etadaggaṃ <a name="M1.0097"></a> bhikkhave mama sāvakānaṃ upāsakānaṃ dāyakānaṃ yadidaṃ sudatto gahapati anāthapiṇḍiko–</p>\r\n<p class="subhead">Sūrambaṭṭhaupāsakāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sūrambaṭṭho nāma āvuso upāsako bhagavatā kathaṃ etadaggaṭṭhāne ṭhapito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sūrambaṭṭho upāsako ‘‘etadaggaṃ bhikkhave mama sāvakānaṃ upāsakānaṃ aveccappasannānaṃ yadidaṃ sūrambaṭṭho’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',100,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0100"></a> bhikkhave mama sāvakānaṃ upāsakānaṃ aveccappasannānaṃ yadidaṃ sūrambaṭṭho’’ hu –</p>\r\n<p class="subhead">Sujātā upāsikāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',101,replace(replace('<p class="bodytext">etadaggavagge <a name="M1.0101"></a> āvuso āgatāsu dasasu upāsikāsu sujātānāma upāsikā seniyadhītā kathaṃ bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sujātā bhante seniyadhītā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ upāsikānaṃ paṭhamaṃ saraṇaṃ gacchantīnaṃ yadidaṃ sujātā seniyadhītā’’ti, evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',104,replace(replace('<p class="bodytext">‘‘Etā <a name="M1.0104"></a> mayaṃ bhante bhagavantaṃ saraṇaṃ gacchāma dhammañca bhikkhusaṅghañca, upāsikāyo no bhagavā dhāretu ajjatagge pāṇupetā saraṇaṃ gatā’’ hu –</p>\r\n<p class="bodytext">‘‘Etadaggaṃ bhikkhave mama sāvikānaṃ upāsikānaṃ paṭhamaṃ saraṇaṃ gacchantīnaṃ yadidaṃ sujātā seniyadhītā’’ –</p>\r\n<p class="subhead">Visākhā upāsikāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',105,replace(replace('<p class="bodytext">visākhā <a name="M1.0105"></a> pana āvuso upāsikā kathaṃ bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> visākhā bhante upāsikā migāramātā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ upāsikānaṃ dāyikānaṃ yadidaṃ visākhā migāramātā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',107,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0107"></a> bhikkhave mama sāvikānaṃ upāsikānaṃ dāyikānaṃ yadidaṃ visākhā migāramātā’’ hu –</p>\r\n<p class="subhead">Khujjuttarā upāsikāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',108,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0108"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye ekakanipāte etadaggavagge āgatāsu dasasu upāsikāsu khujjuttarānāma upāsikā kathaṃ etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> etadaggavagge bhante āgatāsu dasasu upāsikāsu khujjuttarā upāsikā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ upāsikānaṃ bahussutānaṃ yadidaṃ khujjuttarā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',111,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0111"></a> bhikkhave mama sāvakānaṃ upāsikānaṃ bahussutānaṃ yadidaṃ khujjuttarā’’ hu –</p>\r\n<p class="subhead">Kāḷīupāsikāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kāḷīnāma āvuso upāsikā kuraragharikā kathaṃ bhagavatā etadaggaṭṭhāne ṭhapitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kāḷī bhante upāsikā kuraragharikā ‘‘etadaggaṃ bhikkhave mama sāvikānaṃ anussavappasannānaṃ yadidaṃ kāḷī upāsikā kuraragharikā’’ti evaṃ kho bhante bhagavatā etadaggaṭṭhāne ṭhapitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',112,replace(replace('<p class="bodytext">‘‘Etadaggaṃ <a name="M1.0112"></a> bhikkhave mama sāvikānaṃ upāsikānaṃ anussavappasannānaṃ yadidaṃ kāḷī upāsikā kuraragharikā’’ –</p>\r\n<p class="subhead">Aṭṭhānapāḷi</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',113,replace(replace('<p class="bodytext">sakalāpi <a name="M1.0113"></a> āvuso aṭṭhānapāḷi bhagavatā kathaṃ bhāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhānametaṃ bhikkhave anavakāso, yaṃ diṭṭhisampanno puggalo kañcisaṅkhāraṃ niccato upagaccheyya, netaṃ ṭhānaṃ vijjati. Ṭhānañca kho etaṃ bhikkhave vijjati, yaṃ puthujjano kañcisaṅkhāraṃ niccato upagaccheyya, ṭhānametaṃ vijjatīti evamādinā bhante bhagavatā sakalāpi aṭṭhānapāḷi bhāsitā.</p>\r\n<p class="subhead">Ekadhammapāḷi paṭhamavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',114,replace(replace('<p class="bodytext">ekadhammapāḷiyaṃ <a name="M1.0114"></a> āvuso paṭhamavagge kīdisī dhammadesanā bhagavatā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekadhammapāḷiyaṃ bhante paṭhamavagge ‘‘ekadhammo bhikkhave bhāvito bahulīkato ekantanibbidāya virāgāya nirodhāya upasamāya abhiññāya sambodhāya nibbānāya saṃvattati. Katamo ekadhammo, buddhānussati, ayaṃ kho bhikkhave ekadhammo bhāvito bahulīkato ekantanibbidāya virāgāya nirodhāya upasamāya abhiññāya sambodhāya nibbānāya saṃvattatī’’ti evamādikā bhagavatā dhammadesanā desitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',115,replace(replace('<p class="bodytext">dutiyavagge <a name="M1.0115"></a> pana āvuso bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',116,replace(replace('<p class="bodytext">dutiyavagge <a name="M1.0116"></a> bhante bhagavatā ‘‘nāhaṃ bhikkhave aññaṃ ekadhammampi samanupassāmi, yena anuppannā vā akusalā dhammā uppajjanti, uppannā vā akusalā dhammā bhiyyobhāvāya vepullāya saṃvattanti, yathayidaṃ bhikkhave micchādiṭṭhi. Micchādiṭṭhikassa bhikkhave anuppannā ceva akusalā dhammā uppajjanti, uppannā ca akusalā dhammā bhiyyobhāvāya vepullāya saṃvattantī’’ti. Evamādikā bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">Tatiyavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',117,replace(replace('<p class="bodytext">tatiyavagge <a name="M1.0117"></a> pana āvuso pañcamādīni aṭṭha suttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tatiyavagge bhante pañcamādīni aṭṭhasuttāni ‘‘durakkhāte bhikkhave dhammavinaye yo ca samādapeti, yañca samādapeti, yo ca samādapito tathattāya paṭipajjati. Sabbete bahuṃ apuññaṃ pasavanti. Taṃkissahetu, durakkhābhattā bhikkhave dhammassa. Svākkhāte bhikkhave dhammavinaye yo ca samādapeti, yañca samādapeti, yo ca samādapito, tathattāya paṭipajjati, sabbete bahuṃ puññaṃ pasavanti. Taṃkissahetu svākkhātattā bhikkhave dhammassā’’ti evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Apara accharāsaṅghātavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',120,replace(replace('<p class="bodytext">aparaaccharāsaṅghātavagge <a name="M1.0120"></a> pana āvuso bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aparaaccharāsaṅghātavagge bhante ‘‘accharāsaṅghātamattampi ce bhikkhave bhikkhu paṭhamaṃ jhānaṃ bhāveti, ayaṃ vuccati bhikkhave bhikkhu arittajjhāno viharati satthusāsanakaro ovādapatikaro amoghaṃ raṭṭhapiṇḍaṃ bhuñjati, ko pana vādo ye naṃ bahulīkarontī’’ti evamādikā bhante bhagavatā dhammadesanāyo desitā.</p>\r\n<p class="subhead">Kāyagatāsativagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',122,replace(replace('<p class="bodytext">sakalopi <a name="M1.0122"></a> āvuso kāyagatāsativaggo bhagavatā kathaṃ bhāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yassa kassaci bhikkhave mahāsamuddo cetasā phuṭo, antogadhā tassa kunnadiyo yākāci samuddaṅgamā, evameva bhikkhave yassa kassaci kāyagatāsati bhāvitā bahulīkatā, antogadhā tassa kusalā dhammā yekeci vijjābhāgiyā’’ti evamādinā bhante bhagavatā sakalopi kāyagatāsativaggo bhāsito.</p>\r\n<p class="subhead">Amatavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',123,replace(replace('<p class="bodytext">sakalopi <a name="M1.0123"></a> āvuso amatavaggo bhagavatā kathaṃ bhāsato.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> amataṃ te bhikkhave na paribhuñjanti, ye kāyagatāsatiṃ na paribhuñjanti. Amataṃ te bhikkhave paribhuñjanti, ye kāyagatāsatiṃ paribhuñjanti. Amataṃ te bhikkhave aparibhuttaṃ, yesaṃ kāyagatāsati aparibhuttā. Amataṃ te bhikkhave paribhuttaṃ, yesaṃ kāyagatāsati paribhuttāti evamādinā bhante bhagavatā sakalopi amatavaggo bhāsito.</p>\r\n<p class="subhead">Kammakaraṇavagga, vajjasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',125,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0125"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye dukanipāte kammakaraṇavagge paṭhamaṃ vajjasuttaṃ kattha kassa kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulānaṃ bhikkhūnaṃ ‘‘dvemāni bhikkhave vajjāni. Katamāni dve diṭṭhadhammikañca vajjaṃ samparāyikañca vajja’’nti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',127,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0127"></a> bhikkhave evaṃ sikkhitabbaṃ ‘‘diṭṭhadhammikassa vajjassa bhāyissāma, samparāyikassa vajjassa bhāyissāma, vajjabhīruno bhavissāma vajjabhayadassāvino’’ti. Evañhi kho bhikkhave sikkhitabbaṃ.</p>\r\n<p class="subhead">Padhānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',128,replace(replace('<p class="bodytext">tattheva <a name="M1.0128"></a> āvuso dutiyaṃ padhānasuttaṃ bhagavatā kattha kassa kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahulānaṃ bhikkhūnaṃ ‘‘dvemāni bhikkhave padhānāni durabhisambhavāni lokasmiṃ. Katamāni dve, yañca gihīnaṃ agāraṃ ajjhāvasataṃ cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārānuppādanatthaṃ padhānaṃ, yañca agārasmā anagāriyaṃ pabbajitānaṃ sabbūpadhipaṭinissaggattaṃ padhānaṃ. Imāni kho bhikkhave dve padhānāni durabhisambhavāni lokasmi’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tapanīyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',130,replace(replace('<p class="bodytext">tattheva <a name="M1.0130"></a> āvuso tapanīyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dveme bhikkhave dhammā tapanīyā. Katame dve, idha bhikkhave ekaccassa kāyaduccaritaṃ kataṃ hoti, akataṃ hoti kāya sucaritaṃ, vacīduccaritaṃ kataṃ hoti, akataṃ hoti vacīsucaritaṃ, manoduccaritaṃ kataṃ hoti, akataṃ hoti manosucaritaṃ. So ‘‘kāyaduccaritaṃ me kata’’nti tappati, ‘‘akataṃ me kāyasucarita’’nti tappati. Vacīduccaritaṃ (pa) ‘‘manoduccaritaṃ me kata’’nti tappati, ‘‘akataṃ me manosucarita’’nti tappati. Ime kho bhikkhave dve dhammā tapanīyāti. Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Atapanīyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',131,replace(replace('<p class="bodytext">catutthaṃ <a name="M1.0131"></a> pana āvuso atapanīyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catutthaṃ bhante atapanīyasuttaṃ ‘‘dveme bhikkhave dhammā atapanīyā. Katame dve, idha bhikkhave ekaccassa kāyasucaritaṃ kataṃ hoti, akataṃ hoti kāyaduccaritaṃ. Vacīsucaritaṃ kataṃ hoti, akataṃ hoti vacīduccaritaṃ. Manosucaritaṃ kataṃ hoti, akataṃ hoti manoduccaritaṃ. So ‘kāyasucaritaṃ me kata’nti na tappati, ‘akataṃ me kāyaduccarita’nti natappati. ‘Vacīsucaritaṃ…pe… manosucaritaṃ me kata’nti na tappati. ‘Akataṃ me manoduccarita’nti na tappati. Ime kho bhikkhave dve dhammā atapanīyā’’ti. Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Upaññātasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',132,replace(replace('<p class="bodytext">pañcamaṃ <a name="M1.0132"></a> pana āvuso upaññātasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcamaṃ bhante upaññātasuttaṃ ‘‘dvinnāhaṃ bhikkhave dhammānaṃ upaññāsiṃ. Yā ca asantuṭṭhitā kusalesu dhammesu, yā ca appaṭivānitā padhānasmiṃ. Appaṭivāni sudāhaṃ bhikkhave padahāmi ‘kāmaṃ-taco ca nhāru ca aṭṭhi ca avasissatu upasussatu sarīre maṃsalohitaṃ, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',133,replace(replace('<p class="bodytext">yaṃ <a name="M1.0133"></a> taṃ purisathāmena purisavīriyena purisaparakkamena pattabbaṃ, na taṃ apāpuṇitvā vīriyassa saṇṭhānaṃ bhavissatī’’ti. Evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Cariyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',134,replace(replace('<p class="bodytext">navamaṃ <a name="M1.0134"></a> pana āvuso cariyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> navamaṃ bhante cariyasuttaṃ ‘‘dveme bhikkhave dhammā sukkā lokaṃ pālenti. Katame dve, hirīca ottappañca. Ime kho bhikkhave dve sukkā dhammā lokaṃ na pāleyyuṃ, nayidha paññāyetha ‘mātā’tivā ‘mātucchā’tivā ‘mātulānī’tivā ‘ācariyabhariyā’tivā ‘garūnaṃ dārā’ti’’vāti, evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Adhikaraṇavagga, sattamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',136,replace(replace('<p class="bodytext">adhikaraṇavagge <a name="M1.0136"></a> pana āvuso sattamasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante jāṇussoṇiṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Jāṇussoṇi bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘ko nu kho bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃmaraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjantī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘katattā ca brāhmaṇa akatattā ca evamidhekacce sattā kāyassa bhedā paraṃmaraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjantī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',137,replace(replace('<p class="bodytext">Ko <a name="M1.0137"></a> nu kho bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti.</p>\r\n<p class="bodytext">‘‘Katattāca brāhmaṇa akatattāca evamidhekacce sattā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjanti’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',138,replace(replace('<p class="bodytext">Ko <a name="M1.0138"></a> pana bho gotama hetu ko paccayo, yena midhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjanti –</p>\r\n<p class="bodytext">Katattā ca brāhmaṇa akatattā ca evamidhekacce sattā kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjanti.</p>\r\n<p class="bodytext">Na kho ahaṃ imassa bhoto gotamassa saṃkhittena bhāsitassa vitthārena atthaṃ avibhattassa vitthārena atthaṃ –</p>\r\n<p class="bodytext">Sādhu me bhavaṃ gotamo tathā dhammaṃ desetu.</p>\r\n<p class="subhead">Aṭṭhamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',140,replace(replace('<p class="bodytext">tattheva <a name="M1.0140"></a> āvuso aṭṭhamasuttaṃ bhagavatā kattha kassa kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmato ānandassa ‘‘ekaṃsenāhaṃ ānanda akaraṇīyaṃ vadāmi kāyaduccaritaṃ manoduccarita’’nti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',141,replace(replace('<p class="bodytext">‘‘Ekaṃsenāhaṃ <a name="M1.0141"></a> ānanda karaṇīyaṃ vadāmi kāyasucaritaṃ vacīsucaritaṃ manosucaritaṃ’’ –</p>\r\n<p class="subhead">Navamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',142,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0142"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye dukanipāte adhikaraṇavagge navamasuttaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',143,replace(replace('<p class="bodytext">akusalaṃ <a name="M1.0143"></a> bhikkhave pajahatha, sakkā bhikkhave akusalaṃ pajahituṃ. Nocedaṃ bhikkhave sakkā abhavissa akusalaṃ pajahituṃ, nāhaṃ evaṃ vadeyyaṃ ‘‘akusalaṃ bhikkhave pajahathā’’ti. Yasmā ca kho bhikkhave sakkā akusalaṃ pajahituṃ, tasmāhaṃ evaṃ vadāmi ‘‘akusalaṃ bhikkhave pajahathā’’ti. Akusalañca hidaṃ bhikkhave pahīnaṃ ahitāya dukkhāya saṃvatteyya, nāhaṃ evaṃvadeyyaṃ ‘‘akusalaṃ bhikkhave pajahathā’’ti. Yasmā ca kho bhikkhave akusalaṃ pahīnaṃ hitāya sukhāya saṃvattati, tasmāhaṃ evaṃ vadāmi ‘‘akusalaṃ bhikkhave pajahathā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dasamasutta, ekādasamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',145,replace(replace('<p class="bodytext">tattheva <a name="M1.0145"></a> āvuso dasamaekādasamasuttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante dasamaekādasamasuttāni ‘‘dveme bhikkhave dhammā saddhammassa sammosāya antaradhānāya saṃvattanti. Katame dve, dunnikkhittañca padabyañjanaṃ attho ca dunnīto, dunnikkhittassa bhikkhave padabyañjanassa atthopi dunnayo hoti. Ime kho bhikkhave dve dhammā saddhammassa sammosāya antaradhānāya saṃvattantī’’ti ca. ‘‘Dveme bhikkhave dhammā saddhammassa ṭhitiyā asammosāya anantaradhānāya saṃvattanti. Katame dve, sunikkhittañca padabyañjanaṃ attho ca sunīto, sunikkhittassa bhikkhave padabyañjanassa atthopi sunayo hoti. Ime kho bhikkhave dve dhammā saddhammassa ṭhitiyā asammosāya anantaradhānāya saṃvattantī’’ti ca. Evaṃ kho bhante bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Bālavagga</p>\r\n<p class="subhead">Dutiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',147,replace(replace('<p class="bodytext">bālavagge <a name="M1.0147"></a> pana āvuso dutiyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bālavagge bhante dutiyaṃ suttaṃ ‘‘dveme bhikkhave tathāgataṃ abbhācikkhanti. Katame dve, duṭṭho vā dosantaro saddho vā duggahitena. Ime kho bhikkhave dve tathāgataṃ abbhācikkhantī’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tatiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',148,replace(replace('<p class="bodytext">tattheva <a name="M1.0148"></a> āvuso tatiyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante tatiyasuttaṃ bhagavatā ‘‘dveme bhikkhave tathāgataṃ abbhācikkhanti. Katame dve, yo ca abhāsitaṃ alapitaṃ tathāgatena ‘bhāsitaṃ lapitaṃ tathāgatenā’ti dīpeti. Yo ca bhāsitaṃ lapitaṃ tathāgatena ‘abhāsitaṃ alapitaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',149,replace(replace('<p class="bodytext">tathāgatenāti’ <a name="M1.0149"></a> dīpeti. Ime kho bhikkhave dve tathāgataṃ abbhācikkhanti. Dveme bhikkhave tathāgataṃ nābbhācikkhanti. Katame dve, yo ca abhāsitaṃ alapitaṃ tathāgatena ‘abhāsitaṃ alapitaṃ tathāgatenā’ti dīpeti. Yo ca bhāsitaṃ lapitaṃ tathāgatena ‘bhāsitaṃ lapitaṃ tathāgatenā’ti dīpeti. Ime kho bhikkhave dve tathāgataṃ nābbhācikkhantī’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Catutthavagga, pañcamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',150,replace(replace('<p class="bodytext">tattheva <a name="M1.0150"></a> āvuso catutthapañcamasuttāni bhagavatā kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante catutthapañcamasuttāni ‘‘dveme bhikkhave tathāgataṃ abbhācikkhanti. Katame dve, yo ca neyyatthaṃ suttantaṃ ‘nītattho </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',151,replace(replace('<p class="bodytext">suttanto’ti <a name="M1.0151"></a> dīpeti, yo ca nītatthaṃ suttantaṃ ‘neyyattho suttanto’ti dīpetī’’ti, evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Samacittavagga, paṭhamasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',153,replace(replace('<p class="bodytext">samacittavagge <a name="M1.0153"></a> āvuso paṭhamasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samacittavagge bhante paṭhamasuttaṃ ‘‘asappurisabhūmiñca vo bhikkhave desessāmi sappurisabhūmiñca, taṃ suṇātha sādhukaṃ manasikarotha, bhāsissāmī’’ti, evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dutiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',156,replace(replace('<p class="bodytext">tattheva <a name="M1.0156"></a> āvuso dutiyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante dutiyasuttaṃ ‘‘dvinnāhaṃ bhikkhave na suppatikāra vadāmi. Katamesaṃ dvinnaṃ, mātu ca pitu cā’’ti, evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Samacittasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',157,replace(replace('<p class="bodytext">tattheva <a name="M1.0157"></a> āvuso pañcamaṃ samacittasuttaṃ kattha kassa kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulānaṃ bhikkhūnaṃ ‘‘ajjhattasaṃyojanañca āvuso puggalaṃ desessāmi bahiddhāsaṃyojanañca, taṃsuṇātha</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',158,replace(replace('<p class="bodytext">Sādhukaṃ <a name="M1.0158"></a> manasikarothā’’ti, evamādinā bhante āyasmatā sāriputtattherena dhammasenāpatinā bhāsitaṃ.</p>\r\n<p class="subhead">Ovāda</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',161,replace(replace('<p class="bodytext">kathañca <a name="M1.0161"></a> āvuso tattha bhagavā ovādaṃ adāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante bhagavā ‘‘tasmātiha sāriputta evaṃ sikkhitabbaṃ ‘santindriyā bhavissāma santamānasā’ti. Evañhi vo sāriputta sikkhitabba’’nti evamādinā ovādamadāsi.</p>\r\n<p class="bodytext">‘‘Eso bhante āyasmā sāriputto pubbārāme migāramātupāsā de bhikkhūnaṃ ajjhattasaṃyojanañca puggalaṃ deseti bahiddhā saṃyojanañca’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',162,replace(replace('<p class="bodytext">Sādhu <a name="M1.0162"></a> bhante bhagavā yena āyasmā sāriputto tenupasaṅkamatu anukampaṃ upādāya.</p>\r\n<p class="bodytext">Tā kho pana sāriputta devatā dasapi hutvā vīsampi hutvā tiṃsampi hutvā cattālīsampi hutvā paññāsampi hutvā saṭṭhipi hutvā āraggakoṭinitudanamattepi tiṭṭhanti, na ca aññamaññaṃ byābādhenti–</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',163,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0163"></a> sāriputta evaṃ sikkhitabbaṃ santindriyā bhavissāma santamānasā.</p>\r\n<p class="subhead">Parisavagga</p>\r\n<p class="subhead">Uttānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',165,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0165"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye dukanipāte parisavagge paṭhamaṃ uttānasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamaṃ bhante uttānasuttaṃ ‘‘dvemā sikkhave parisā, katamā dve, uttānā ca parisā gambhīrā ca parisā, katamā ca bhikkhave uttānā parisā, idha bhikkhave yassaṃ parisāyaṃ bhikkhū uddhatā honti unnaḷā capalā mukharā vikiṇṇavācā muṭṭhassatī asampajānā asamāhitā vibbhantacittā pākatindriyā. Ayaṃ vuccati bhikkhave uttānā parisā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Soparisattaka</p>\r\n<p class="bodytext">Etadaggaṃ bhikkhave imāsaṃ dvinnaṃ parisānaṃ yadidaṃ gambhīrā parisā.</p>\r\n<p class="subhead">Anaggavatīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',167,replace(replace('<p class="bodytext">tattheva <a name="M1.0167"></a> āvuso tatiyaṃ anaggavatīsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante tatiyaṃ anaggavatīsuttaṃ ‘‘dvemā bhikkhave parisā. Katamā dve, anaggavatī ca parisā aggavatī ca parisā. Katamā ca bhikkhave anaggavatī parisā, idha bhikkhave yassaṃ parisāyaṃ therā bhikkhū bāhulikā honti sāthalikā, okkamane pubbaṅgamā, paviveke nikkhittadhurā, na vīriyaṃ ārabhanti appattassa pattiyā anadhigatassa adhigamāya asacchikabhassa sacchikiriyāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Okkācitavinītasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',170,replace(replace('<p class="bodytext">tattheva <a name="M1.0170"></a> āvuso chaṭṭhaṃ okkācitavinītasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante chaṭṭhaṃ okkācitavinītasuttaṃ ‘‘dvemā bhikkhave parisā. Katamā dve, okkācitavinītā parisā no paṭipucchāvinītā, paṭipucchā vinītā parisā no okkācitavinītā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Puggalavagga</p>\r\n<p class="subhead">Asantasannivāsasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',173,replace(replace('<p class="bodytext">puggalavagge <a name="M1.0173"></a> āvuso ekādasamaṃ asantasannivāsasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puggalavagge bhante ekādasamaṃ asantasannivāsasuttaṃ ‘‘asantasannivāsañca vo bhikkhave desessāmi santasannivāsañca, taṃ suṇātha, sādhukaṃ manasikarotha bhāsissāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sukhavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',176,replace(replace('<p class="bodytext">sukhavagge <a name="M1.0176"></a> pana āvuso bhagavatā kīdisī dhammadesanāyo desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sukhavagge bhante bhagavatā ‘‘dvemāni bhikkhave sukhāni. Katamāni dve, gihisukhañca pabbajitasukhañca, imāni kho bhikkhave dve sukhāni. Etadaggaṃ bhikkhave imesaṃ dvinnaṃ sukhānaṃ yadidaṃ pabbajitasukha’’nti evamādikā dhammadesanāyo desitā.</p>\r\n<p class="subhead">Āyācanavagga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',178,replace(replace('<p class="bodytext">āyācanavagge <a name="M1.0178"></a> pana āvuso bhagavatā paṭhamādīni cattāri suttāni kathaṃ bhāsitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyācanavagge bhante paṭhamādīni cattāri suttāni ‘‘saddho bhikkhave bhikkhu evaṃ sammā āyācamāno āyāceyya tādiso homi, yādisā sāriputtamoggallānā’’ti, evamādinā bhagavatā bhāsitāni.</p>\r\n<p class="subhead">Avaṇṇārahasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',180,replace(replace('<p class="bodytext">tattheva <a name="M1.0180"></a> āvuso pañcamaṃ avaṇṇārahasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante pañcamaṃ avaṇṇārahasuttaṃ ‘‘dvīhi bhikkhave dhammehi samannāgato bālo abyatto asappuriso etaṃ upahataṃ attānaṃ pariharati, sāvajjo ca hoti sānuvajjo ca viññūnaṃ, bahuñca apuññaṃ pasavatī’’ti, evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Appasādanīyasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',182,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0182"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye dukanipāte āyācanavagge chaṭṭhaṃ appasādanīyasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chaṭṭhaṃ bhante appasādanīyasuttaṃ ‘‘dvīhi bhikkhave dhammehi samannāgato bālo abyatto asappuriso etaṃ upahataṃ attānaṃ pariharati, sāvajjo ca hoti sānuvajjo ca viññūnaṃ, bahuñca apuññaṃ pasavati. Katamehi dvīhi, ananuvicca apariyogāhetvā appasādanīye ṭhāne pasādaṃ upadaṃseti, ananuvicca apariyo gāhetvā pasādanīye ṭhāne appasādaṃ upadaṃsetī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Mātāpitusutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',184,replace(replace('<p class="bodytext">tattheva <a name="M1.0184"></a> āvuso sattamaṃ mātāpitusuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante sattamaṃ mātāpitusuttaṃ ‘‘dvīsu bhikkhave micchā paṭipajjamāno bālo abyatto asappuriso etaṃ upahataṃ attānaṃ pariharati, sāvajjo ca hohi sānuvajjo ca viññūnaṃ, bahuñca apuññaṃ pasavati. Katamesu dvīsu, mātari ca pitari cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tathāgatasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',185,replace(replace('<p class="bodytext">tattheva <a name="M1.0185"></a> āvuso aṭṭhamaṃ tathāgatasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',186,replace(replace('<p class="bodytext">tattheva <a name="M1.0186"></a> bhante aṭṭhamaṃ tathāgatasuttaṃ ‘‘dvīsu bhikkhave micchāpaṭipajjamāno bālo abyatto asappuriso etaṃ upahataṃ attānaṃ pariharati, sāvajjo ca hoti sānuvajjo ca viññūnaṃ, bahuñca apuññaṃ pasavati. Katamesu dvīsu, tathāgate ca tathāgatasāvake cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tikanipāta</p>\r\n<p class="subhead">Bālavagga, bhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',189,replace(replace('<p class="bodytext">tikanipāte <a name="M1.0189"></a> pana āvuso paṭhamaṃ bhayasuttaṃ bhagavatā kattha kassa kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulānaṃ ‘‘yāni kānici bhikkhave bhayāni uppajjanti, sabbāni tāni bālato uppajjanti no paṇḍitato. Ye keci uppaddavā uppajjanti, sabbete bālato uppajjanti no paṇḍitato. Yekeci upasaggā uppajjanti, sabbete bālato uppajjanti no paṇḍitato’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Cintīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',190,replace(replace('<p class="bodytext">tattheva <a name="M1.0190"></a> āvuso tatiyaṃ cintīsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante cintīsuttaṃ ‘‘tīṇimāni bhikkhave bālassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',191,replace(replace('<p class="bodytext">bālalakkhaṇāni <a name="M1.0191"></a> bālanimittāni bālāpadānāni. Katamāni tīṇi, idha bhikkhave bālo duccintitacintī ca hoti dubbhāsitabhāsī ca dukkaṭakammakārī cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Accayasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',193,replace(replace('<p class="bodytext">tattheva <a name="M1.0193"></a> āvuso catutthaṃ accayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante catutthaṃ accayasuttaṃ ‘‘tīhi bhikkhave dhammehi samannāgato bālo veditabbo. Katamehi tīhi, accayaṃ accayato disvā yathādhammaṃ nappaṭikaroti, parassa kho pana accayaṃ desentassa yathādhammaṃ nappaṭiggaṇhātī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Malasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',195,replace(replace('<p class="bodytext">tattheva <a name="M1.0195"></a> āvuso dasamaṃ malasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante dasamaṃ malasuttaṃ ‘‘tīhi bhikkhave dhammehi samannāgato tayo male appahāya yathābhataṃ nikkhitto evaṃ niraye. Katamehi tīhi, dussīlo ca hoti, dussīlyamalañcassa appahīnaṃ hoti. Issukī ca hoti, issāmalañcassa appahīnaṃ hoti. Maccharī ca hoti, maccharamalañcassa appahīnaṃ hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Rathakāravagga, ñātasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',197,replace(replace('<p class="bodytext">rathakāravagge <a name="M1.0197"></a> pana āvuso paṭhamaṃ ñātasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamaṃ bhante ñātasuttaṃ ‘‘tīhi bhikkhave dhammehi samannāgato ñāto bhikkhu bahujanaahitāya paṭipanno hoti bahujana dukkhāya bahuno janassa anatthāya ahitāya dukkhāya devamanussānaṃ. Katamehi tīhi, ananulomike kāyakamme samādapeti, ananulomike vacīkamme samādapeti, ananulomikesu dhammesu samādapetī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āsaṃsasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',199,replace(replace('<p class="bodytext">tattheva <a name="M1.0199"></a> āvuso tatiyaṃ āsaṃsasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante tatiyaṃ āsaṃsasuttaṃ ‘‘tayome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo nirāso āsaṃso’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sacetanasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',203,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0203"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte rathakāravagge pañcamaṃ sacetanasuttaṃ kattha kassa kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante sambahulānaṃ bhikkhūnaṃ ārabbha ‘‘bhūtapubbaṃ bhikkhave rājā ahosi sacetano nāma, atha kho bhikkhave rājā sacetano rathakāraṃ āmantesi ito me samma rathakāra channaṃ māsānaṃ paccayena saṅgāmo bhavissati sakkhissasi me samma rathakāra navaṃ cakkayugaṃ kātu’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Apaṇṇakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',208,replace(replace('<p class="bodytext">tattheva <a name="M1.0208"></a> āvuso chaṭṭhaṃ apaṇṇakasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chaṭṭhaṃ bhante apaṇṇakasuttaṃ ‘‘tīhi bhikkhave dhammehi samannāgato bhikkhu apaṇṇakapaṭipadaṃ paṭipanno hoti, yoni cassa āraddhā hoti āsavānaṃ khayāya. Katamehi tīhi, idha bhikkhave bhikkhu indriyesu guttadvāro hoti, bhojane mattaññū hoti, jāgariyamanuyutto hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Devalokasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',210,replace(replace('<p class="bodytext">tattheva <a name="M1.0210"></a> āvuso aṭṭhamaṃ devalokasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante aṭṭhamaṃ devalokasuttaṃ ‘‘sace vo bhikkhave aññatitthiyā paribbājakā evaṃ puccheyyuṃ ‘‘devalokūpapattiyā āvuso samaṇe gotame brahmacariyaṃ vussathā’ti. Nanu tumhe </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',211,replace(replace('<p class="bodytext">bhikkhave <a name="M1.0211"></a> evaṃ puṭṭhā aṭṭīyeyyātha harāyeyyātha jiguccheyyāthā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhama pāpaṇikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',212,replace(replace('<p class="bodytext">tattheva <a name="M1.0212"></a> āvuso navamaṃ paṭhamapāpaṇikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante navamaṃ paṭhamapāpaṇikasuttaṃ ‘‘tīhi bhikkhave aṅgehi samannāgato pāpaṇiko abhabbo anadhigataṃ vā bhogaṃ adhigantuṃ, adhigataṃ vā bhogaṃ phātiṃ kātuṃ. Katamehi tīhi, idha bhikkhave pāpaṇiko pubbaṇhasamayaṃ na sakkaccaṃ kammantaṃ adhiṭṭhātī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Puggalavagga</p>\r\n<p class="subhead">Gilānasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',215,replace(replace('<p class="bodytext">puggalavagge <a name="M1.0215"></a> pana āvuso dutiyaṃ gilānasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puggalavagge bhante dutiyaṃ gilānasuttaṃ ‘‘tayome bhikkhave gilānā santo saṃvijjamānā lokasmiṃ. Katame tayo, idha bhikkhave ekacco gilāno labhanto vā sappāyāni bhojanāni alabhanto vā sappāyāni bhojanāni, labhanto vā sappāyāni bhesajjāni alabhanto vā sappāyāni bhesajjāni, labhanto vā patirūpaṃ upaṭṭhākaṃ alabhanto vā patirūpaṃ upaṭṭhākaṃ vuṭṭhāti tamhā ābādhā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Bahukārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',217,replace(replace('<p class="bodytext">tattheva <a name="M1.0217"></a> āvuso catutthaṃ bahukārasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',218,replace(replace('<p class="bodytext">tattheva <a name="M1.0218"></a> bhante catutthaṃ bahukārasuttaṃ ‘‘tayo me bhikkhave puggalā puggalassa bahukārā. Katame tayo, yaṃ bhikkhave puggalaṃ āgamma puggalo buddhaṃ saraṇaṃ gato hoti, dhammaṃ saraṇaṃ gato hoti, saṅghaṃ saraṇaṃ gato hoti. Ayaṃ bhikkhave puggalo imassa puggalassa bahukāro’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Jigucchitabbasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',220,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0220"></a> bhagavatā jānatā passatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte puggalavagge sattamaṃ jigucchitabbasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattamaṃ bhante jigucchitabbasuttaṃ ‘‘tayome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo, atthi bhikkhave puggalo jigucchitabbo na sevitabbo na bhajitabbo na payirupāsitabbo, atthi bhikkhave puggalo ajjhupekkhitabbo na sevitabbo na bhajitabbo na payirupāsitabbo, atthi bhikkhave puggalo sevitabbo bhajitabbo payirupāsitabbo’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',221,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0221"></a> tattha bhagavatā jigucchitabbo puggalo nasevitabbo nabhajitabbo na payirupāsitabbo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘katamo ca bhikkhave puggalo jigucchitabbo na sevitabbo na bhajitabbo na payirupāsitabbo. Idha bhikkhave ekacco dussīlo hoti pāpadhammo asucisaṅkassarasamācāro paṭicchannakammanto assamaṇo samaṇapaṭiñño abrahmacārī brahmacāripaṭiñño antopūti avassuto kasambujāto, evarūpo bhikkhave puggalo jigucchitabbo na sevitabbo na bhajitabbo na payirupāsitabbo’’ti evamādinā bhante bhagavatā jigucchitabbo na sevitabbo na bhajitabbo na payirupāsitabbo puggalo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',223,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0223"></a> panāvuso tattha bhagavatā ajjhupekkhitabbo puggalo na sevitabbo na bhajitabbo na payirupāsitabbo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave puggalo ajjhupekkhitabbo na sevitabbo na bhajitabbo na payirupāsitabbo, idha bhikkhave ekacco puggalo kodhano hoti upāyāsabahulo, appampi vutto samāno abhisajjati kuppati byāpajjati patitthīyati, kopañca dosañca appaccayañca pātukarotī’’ti evamādinā bhante bhagavatā tattha ajjhupekkhitabbo na sevitabbo na bhajitabbo na payirupāsitabbo puggalo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',224,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0224"></a> panāvuso tattha bhagavatā sevitabbo puggalo bhajitabbo payirupāsitabbo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ‘‘katamo ca bhikkhave puggalo sevitabbo bhajitabbo payirupāsitabbo, idha bhikkhave ekacco puggalo sīlavā hoti kalyāṇadhammo, evarūpo bhikkhave puggalo sevitabbo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',225,replace(replace('<p class="bodytext">bhajitabbo <a name="M1.0225"></a> payirupāsitabbo’’ti evamādinā bhante tattha bhagavatā sevitabbo puggalo bhajitabbo payirupāsitabbo pakāsito.</p>\r\n<p class="subhead">Gūthabhāṇīsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',226,replace(replace('<p class="bodytext">tattheva <a name="M1.0226"></a> āvuso puggalavagge aṭṭhamaṃ gūthabhāṇīsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante puggalavagge aṭṭhamaṃ gūthabhāṇīsuttaṃ ‘‘tayo me bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo, gūthabhāṇī pupphabhāṇī madhubhāṇī’’ti evaṃ kho bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Gūthabhāṇīpuggala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',227,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0227"></a> tattha bhagavatā gūthabhāṇīpuggalo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave puggalo gūthabhāṇī, idha bhikkhave ekacco puggalo sabhaggato vā parisaggato vā ñātimajjhagatovā pūgamajjhagatovā rājakulamajjhagatovā abhinīto sakkhipuṭṭho ‘‘ehambho purisa yaṃ jānāsi, taṃ vadehī’’ti. So ajānaṃ vā āha ‘‘jānāmī’’ti, jānaṃ vā āha ‘‘na jānāmī’’ti, apassaṃ vā āha ‘‘passāmī’’ti, passaṃ vā āha ‘‘na passāmī’’ti. Iti attahetu vā parahetu vā āmisakiñcikkhahetu vā sampajānamusā bhāsitā hoti. Ayaṃ vuccati bhikkhave puggalo gūthabhāṇīti, evaṃ kho bhante tattha bhagavatā gūthabhāṇīpuggalo pakāsito.</p>\r\n<p class="subhead">Pupphabhāṇīpuggala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',228,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0228"></a> panāvuso tattha bhagavatā pupphabhāṇīpuggalo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave puggalo pupphabhāṇī, idha bhikkhave ekacco puggalo sabhaggato vā parisaggato vā ñātimajjhagato vā pūgamajjhagatovā rājakulamajjhagato vā abhinīto sakkhipuṭṭho ‘‘ehambho purisa yaṃ pajānāsi, taṃ vadehī’’ti. So ajānaṃ vā āha ‘‘na jānāmī’’ti, jānaṃ vā āha ‘‘jānāmī’’ti, apassaṃ vā āha ‘‘na passāmī’’ti, passaṃ vā āha ‘‘passāmī’’ti, iti attahetu vā parahetu vā āmisakiñcikkhahetu vā na sampajānamusā bhāsitā hoti, ayaṃ vuccati bhikkhave puggalo pupphabhāṇīti, evaṃ kho bhante tattha bhagavatā pupphabhāṇīpuggalo pakāsito.</p>\r\n<p class="subhead">Madhubhāṇīpuggala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',229,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0229"></a> panāvuso tattha bhagavatā madhubhāṇī puggalo pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> katamo ca bhikkhave puggalo madhubhāṇī, idha bhikkhave ekacco puggalo pharusaṃ vācaṃ pahāya pharusāya vācāya paṭivirato</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',230,replace(replace('<p class="bodytext">Hotīti <a name="M1.0230"></a> evamādinā bhante bhagavatā tattha madhubhāṇī puggalo pakāsito.</p>\r\n<p class="subhead">Andhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',231,replace(replace('<p class="bodytext">tattheva <a name="M1.0231"></a> āvuso puggalavagge navamaṃ andhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante puggalavagge navamaṃ andhasuttaṃ ‘‘tayome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo andho ekacakkhu dvicakkhū’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kīdiso āvuso puggalo tattha bhagavatā andho akkhāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yassa bhante bhogesu ceva dhammesu ca paññācakkhu natthi, ediso bhante puggalo tattha bhagavatā andho akkhāto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',232,replace(replace('<p class="bodytext">kīdiso <a name="M1.0232"></a> pana āvuso puggalo tattha bhagavatā ekacakkhu akkhāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yassa bhante bhogesuyeva paññācakkhu atthi na dhammesu. Īdiso bhante puggalo tattha bhagavatā ekacakkhu akkhāto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',233,replace(replace('<p class="bodytext">kīdiso <a name="M1.0233"></a> pana āvuso puggalo tattha bhagavatā dvicakkhu akkhāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yassa bhante bhogesu ceva dhammesu ca paññācakkhu atthi, īdiso bhante puggalo tattha bhagavatā dvicakkhu akkhāto.</p>\r\n<p class="subhead">Avakujjasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',234,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0234"></a> bhagavatā jānatā passatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte puggalavagge dasamaṃ avakujjasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasamaṃ bhante avakujjasuttaṃ ‘‘tayo me bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo, avakujjapañño puggalo ucchaṅgapañño puggalo puthupañño puggalo. Katamo ca bhikkhave avakujjapañño puggalo. Idha bhikkhave ekacco puggalo ārāmaṃ gantā hoti abhikkhaṇaṃ bhikkhūnaṃ santike dhammassavanāya, tassa bhikkhū dhammaṃ desentiādikalyāṇaṃ majjhekalyāṇaṃ pariyosānakalyāṇaṃ sātthaṃ sabyañjanaṃ kevalaparipuṇṇaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',235,replace(replace('<p class="bodytext">parisuddhaṃ <a name="M1.0235"></a> brahmacariyaṃ pakāsenti. So tasmiṃ āsane nisinno tassā kathāya nevādiṃ manasikaroti, na majjhaṃ manasikaroti, na pariyosānaṃ manasikaroti, vuṭṭhitopi tamhā āsanā tassā kathāya nevādiṃ manasikaroti, na majjhaṃ manasikaroti, na pariyosānaṃ manasikarotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Devadūtavagga, sabrahmakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',239,replace(replace('<p class="bodytext">devadūtavagge <a name="M1.0239"></a> pana āvuso paṭhamaṃ sabrahmakasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> devadūtavagge bhante paṭhamaṃ sabrahmakasuttaṃ ‘‘sabrahmakāni bhikkhave tāni kulāni, yesaṃ puttānaṃ mātāpitaro ajjhāgāre pūjitā honti. Sapubbacariyakāni bhikkhave tāni kulāni, yesaṃ puttānaṃ mātāpitaro ajjhāgāre pūjitā honti. Āhuneyyāni bhikkhave tāni kulāni, yesaṃ puttānaṃ mātāpitaro ajjhāgāre pūjitā honti. Brahmāti bhikkhave mātāpitūnaṃ etaṃ adhivacanaṃ. Pubbācariyāti bhikkhave mātāpitūnaṃ etaṃ adhivacanaṃ. Āhuneyyāti bhikkhave etaṃ mātāpitūnaṃ adhivacanaṃ. Taṃ kissa hetu, bahukārā bhikkhave mātāpitaro puttānaṃ āpādakā posakā imassa lokassa dassetāro’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',241,replace(replace('<p class="gatha1">Brahmāti <a name="M1.0241"></a> mātāpitaro, pubbācariyāti vuccare;</p>\r\n<p class="gatha2">Āhuneyyā ca puttānaṃ, pajāya anukampakā;</p>\r\n<p class="gatha3">Tasmā hi ne namasseyya, sakkareyya ca paṇḍito;</p>\r\n<p class="gatha2">Annena atha pānena, vatthena sayanena ca;</p>\r\n<p class="gatha3">Ucchādanena nhāpanena, pādānaṃ dhovanena ca;</p>\r\n<p class="gathalast">Tāya naṃ pāricariyāya, mātāpitūsu paṇḍitā.</p>\r\n<p class="bodytext">Idheva naṃ pasaṃsanti, pecca sagge pamodatīti –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso pañcamaṃ hatthakasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcamaṃ bhante hatthakasuttaṃ āḷaviyaṃ hatthakaṃ āḷavakaṃ ārabbha bhāsitaṃ. Hatthako bhante āḷavako bhagavantaṃ etadavoca ‘‘kacci bhante bhagavā sukhamasayitthā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘evaṃ kumāra sukhamasayittha ye ca pana loke sukhaṃ senti, ahaṃ tesaṃ aññataro’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',242,replace(replace('<p class="bodytext">‘‘Kacci <a name="M1.0242"></a> bhante bhagavā sukhamasayittha’’.</p>\r\n<p class="bodytext">‘‘Evaṃ kumāra sukhamasayittha, ye ca pana loke sukhaṃ senti, ahaṃ tesaṃ aññataro’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',244,replace(replace('<p class="bodytext">‘‘Evaṃ <a name="M1.0244"></a> kumāra sukhamasayittha, ye ca pana loke sukhaṃ senti, ahaṃ tesaṃ aññataro’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',247,replace(replace('<p class="gatha1">‘‘Sabbadā <a name="M1.0247"></a> ve sukhaṃ seti,</p>\r\n<p class="gatha2">Brāhmaṇo parinibbuto;</p>\r\n<p class="gatha3">Yo na limpati kāmesu,</p>\r\n<p class="gathalast">Sītibhūto nirūpadhi;</p>\r\n<p class="gatha1">Sabbā āsattiyo chetvā,</p>\r\n<p class="gatha2">Vineyya hadaye daraṃ;</p>\r\n<p class="gatha3">Upasanto sukhaṃ seti;</p>\r\n<p class="gathalast">Santiṃ pappuyya cetaso’’ hu –</p>\r\n<p class="subhead">Devadūtasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte devadūtavagge chaṭṭhaṃ devadūtasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',248,replace(replace('<p class="bodytext">chaṭṭhaṃ <a name="M1.0248"></a> bhante devadūtasuttaṃ ‘‘tīṇimāni bhikkhave devadūtāni. Katamāni tīṇi, idha bhikkhave ekacco kāyena duccaritaṃ carati, vācāya duccaritaṃ carati, manasā duccaritaṃ carati. So kāyena duccaritaṃ caritvā vācāya duccaritaṃ caritvā manasā duccaritaṃ caritvā kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati. Tamenaṃ bhikkhave nirayapālā nānābāhāsu gahetvā yamassa rañño dassenti-ayaṃ deva puriso amatteyyo apetteyyo asāmañño abrahmañño, na kule jeṭṭhapacāyī, imassa devo daṇḍaṃ paṇetū’’ti. Evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',251,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0251"></a> tattha bhagavatā yamassa rañño paṭhamadevadūta samanuyuñjanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tamenaṃ bhikkhave yamo rājā paṭhamaṃ devadūtaṃ samanuyuñjati samanugāhati samanubhāsati ‘‘ambho purisa na tvaṃ addasa manussesu paṭhamaṃ devadūtaṃ pātubhūta’’nti evamādinā bhante tattha bhagavatā yamassa rañño paṭhamadevadūtasamanuyuñjanā pakāsitā.</p>\r\n<p class="bodytext">‘‘Ambho purisa na tvaṃ addasa manussesu paṭhamaṃ devadūtaṃ pātubhūtaṃ’’.</p>\r\n<p class="subhead">Dutiya devadūta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',252,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0252"></a> panāvuso tattha bhagavatā yamassa rañño dutiyadevadūta samanuyuñjanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tamenaṃ bhikkhave yamo rājā paṭhamaṃ devadūtaṃ samanuyuñjitvā samanugāhitvā samanubhāsitvā dutiyaṃ devadūtaṃ samanuyuñjati samanugāhati samanubhāsati ‘‘ambho purisa na tvaṃ addasa manussesu dutiyaṃ devadūtaṃ pātubhūta’’nti evamādinā bhante tattha bhagavatā yamassa rañño dutiyā devadūtasamanuyuñjanā pakāsitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',255,replace(replace('<p class="bodytext">‘‘Ambho <a name="M1.0255"></a> purisa na tvaṃ addasa manussesu dutiya devadūtaṃ pātubhūtaṃ’’.</p>\r\n<p class="bodytext">‘‘Ambho purisa na tvaṃ addasa manussesu itthīṃ vā purisaṃ vā ābādhikaṃ dukkhitaṃ bāḷhagilānaṃ sake muttakarīse palipannaṃ semānaṃ aññehi vuṭṭhāpiyamānaṃ aññehi saṃvesiyamānaṃ’’ –</p>\r\n<p class="subhead">Tatiya devadūta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',256,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0256"></a> panāvuso tattha bhagavatā yamassa rañño tatiyadevadūta samanuyuñjanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tamenaṃ bhikkhave yamo rājā dutiyaṃ devadūtaṃ samanuyuñjitvā samanugāhitvā samanubhāsitvā tatiyaṃ devadūtaṃ samanuyuñjati samanugāhati samanubhāsati ‘‘ambho purisa natvaṃ manussesu tatiyaṃ devadūtaṃ pātubhūtanti’’ evamādinā bhante tattha bhagavatā yamassa rañño tatiyā devadūtasamanuyuñjanā pakāsitā.</p>\r\n<p class="bodytext">‘‘Ambho purisa na tvaṃ addasa manussesu tatiyaṃ devadūtaṃ pātubhūtaṃ’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',257,replace(replace('<p class="bodytext">‘‘Ambho <a name="M1.0257"></a> purisa na tvaṃ addasa manussesu itthiṃ vā purisaṃ vā ekāhamataṃ vā dvīhamataṃ vā tīhamataṃ vā uddhumātakaṃ vinīlakaṃ vipubbakajātaṃ’’ –</p>\r\n<p class="bodytext">‘‘Nāsakkhissaṃ bhante pamādassaṃ bhante’’ –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',258,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0258"></a> panāvuso tattha bhagavatā nerayikassa sattassa niraya dukkhapaṭisaṃvedanā pakāsitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tamenaṃ bhikkhave ‘‘yamo rājā tatiyaṃ devadūtaṃ samanuyuñjitvā samanugāhitvā samanubhāsitvā tuṇhī hoti. Tamenaṃ bhikkhave nirayapālā pañcavidhabandhanaṃ nāma kāraṇaṃ karonti, tattaṃ ayokhilaṃ hatthe gamenti, tattaṃ ayokhilaṃ dutiyasmiṃ hatthe gamenti, tattaṃ ayokhilaṃ pāde gamenti, tattaṃ ayokhilaṃ dutiyasmiṃ pāde gamenti, tattaṃ ayokhilaṃ majjheurasmiṃ gamenti. So tattha dukkhā tibbā kharā kaṭukā vedanā vedayati, na ca tāva kālaṃ karoti, yāva na taṃ pāpakammaṃ byantīhotī’’ti evamādinā bhante tattha bhagavatā nerayikassa nirayadukkhapaṭisaṃvedanā pakāsitā.</p>\r\n<p class="subhead">Saṃvejanīyakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',261,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0261"></a> panāvuso tattha bhagavatā saṃvejanīyakathā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhūtapubbaṃ bhikkhave yamassa rañño etadahosi ‘‘ye kira bho loke pāpakāni kammāni karonti, te evarūpā vividhā kammakaraṇā karīyanti, ahovatāhaṃ manussattaṃ labheyyaṃ, tathāgato ca loke uppajjeyya arahaṃ sammāsambuddho’’ti evamādinā bhante tattha bhagavatā saṃvejanīyakathā kathitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',262,replace(replace('<p class="bodytext">‘‘Ye <a name="M1.0262"></a> kira bho loke pāpakāni kammāni karonti’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',263,replace(replace('<p class="gatha1">‘‘Coditā <a name="M1.0263"></a> devadūtehi, ye pamajjanti māṇavā;</p>\r\n<p class="gathalast">Te dīgharattaṃ socanti, hīnakāyūpagā narā.</p>\r\n<p class="gatha1">Ye ca kho devadūtehi, santo sappurisā idha;</p>\r\n<p class="gathalast">Coditā na pamajjanti, ariyadhamme kudācanaṃ.</p>\r\n<p class="gatha1">Upādāne bhayaṃ disvā, jātimaraṇasambhave;</p>\r\n<p class="gathalast">Anupādā vimuccanti, jātimaraṇasaṅkhaye.</p>\r\n<p class="gatha1">Te appamattā sukhino, diṭṭhadhammābhinibbutā;</p>\r\n<p class="gathalast">Sabbaverabhayātītā, sabbadukkhaṃ upaccaguṃ’’ hu –</p>\r\n<p class="subhead">Catumahārājasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte devadūtavagge aṭṭhamaṃ catumahārājasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhamaṃ bhante catumahārājasuttaṃ ‘‘aṭṭhamiyaṃ bhikkhave pakkhassa catunnaṃ mahārājānaṃ amaccā pārisajjā imaṃ lokaṃ anuvicaranti kacci bahū manussā manussesu matteyyā petteyyā sāmaññā brahmaññā kule jeṭṭhapacāyino, uposathaṃ upavasanti paṭijāgaronti, puññāni karontī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',265,replace(replace('<p class="bodytext">Appakā <a name="M1.0265"></a> kho mārisā manussā manussesu matteyyā petteyyā sāmaññā brahmaññā kule jeṭṭhapacāyino, uposathaṃ upavasanti paṭijāgaronti, puññāni karonti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',266,replace(replace('<p class="bodytext">‘‘Dibbā <a name="M1.0266"></a> vata bho kāyā parihāyissanti, paripūrissanti asura kāyā’’ –</p>\r\n<p class="bodytext">Bahū kho mārisā manussā manussesu matteyyā petteyyā sāmaññā brahmaññā kule jeṭṭhapacāyino, uposathaṃ upavasanti paṭijāgaronti, puññāni karonti.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',267,replace(replace('<p class="bodytext">‘‘Dibbā <a name="M1.0267"></a> vata bho kāyā paripūrissanti, parihāyissanti asura kāyā’’ –</p>\r\n<p class="subhead">Ādhipateyyāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso dasamaṃ ādhipateyyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante dasamaṃ ādhipateyyasuttaṃ ‘‘tīṇimāni bhikkhave ādhipateyyāni. Katamāni tīṇi, attādhipateyyaṃ lokādhipateyyaṃ dhammādhipateyyaṃ. Katamañca bhikkhave attādhipateyyaṃ, idha bhikkhave bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā iti paṭisañcikkhatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Brāhmaṇavagga, paribbājakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',272,replace(replace('<p class="bodytext">brāhmaṇavagge <a name="M1.0272"></a> āvuso catutthaṃ paribbājakasuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> brāhmaṇavagge bhante catutthaṃ paribbājakasuttaṃ aññataraṃ brāhmaṇa paribbājakaṃ ārabbha bhāsitaṃ. Aññataro bhante brāhmaṇaparibbājako bhagavantaṃ etadavoca ‘‘sanniṭṭhiko dhammo sandiṭṭhiko dhammoti bho gotama vuccati, kittāvatānu kho bho gotama sandiṭṭhiko dhammo hoti akāliko ehipassiko opaneyyiko paccattaṃ veditabbo viññūhī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘ratto kho brāhmaṇa rāgena abhibhūto pariyādinnacitto attabyābādhāyapi ceteti, parabyābādhāyapi ceteti, ubhaya byābādhāyapi ceteti, cetasikampi dukkhaṃ domanassaṃ paṭisaṃvedeti, rāge pahīne nevattabyābādhāyapi ceteti, naparabyābādhāyapi ceteti, naubhayabyābādhāyapi ceteti, nacetasikaṃ dukkhaṃ domanassaṃ paṭisaṃvedetī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Vacchagottasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',275,replace(replace('<p class="bodytext">tattheva <a name="M1.0275"></a> āvuso sattamaṃ vacchagottasuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattamaṃ bhante vacchagottasuttaṃ vacchagottaṃ paribbājakaṃ ārabbha bhāsitaṃ. Vacchagotto bhante paribbājako bhagavantaṃ etadavoca ‘‘sutaṃ metaṃ bho gotama samaṇo gotamo evamāha ‘‘mayhameva dānaṃ dātabbaṃ, nāññesaṃ dānaṃ dātabba. (Peyyāla) anabbhakkhā tukāmāhi mayaṃ bhavantaṃ gotama’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘ye te vaccha evamāhaṃsu samaṇo gotamo evamāha mayhameva dānaṃ dātabbaṃ, nāññesaṃ dātabba’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Saṅgāravasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',278,replace(replace('<p class="bodytext">tattheva <a name="M1.0278"></a> āvuso dasamaṃ saṅgāravasuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',279,replace(replace('<p class="bodytext">dasamaṃ <a name="M1.0279"></a> bhante saṅgāravasuttaṃ saṅgāravaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Saṅgāravo bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘mayamassu bho gotama brāhmaṇā nāma yaññaṃ yajāmapi yajāpemapi, tatra bho gotama yo ceva yajati yo ca yajāpeti, sabbe te anekasārīrikaṃ puññappaṭipadaṃ paṭipannā honti, yadidaṃ yaññādhikaraṇaṃ, yopanāyaṃ bho gotama yassa vā kulā agārasmā anagāriyaṃ pabbajito ekamattānaṃ dameti, ekamattānaṃ sameti, ekamattānaṃ parinibbāpeti, evamassāyaṃ ekasārīrikaṃ puññappaṭipadaṃ paṭipanno hoti, yadidaṃ pabbajjādhikaraṇa’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘tenahi brāhmaṇa taññevettha paṭipucchissāmi, yathā te khameyya, tathā naṃ byākareyyāsī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',282,replace(replace('<p class="bodytext">‘‘Iccāyapi <a name="M1.0282"></a> bho gotama evaṃ bhante anekasārīrikā puññappaṭipadā hoti’’ –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattheva āvuso dutiya anusandhimhi bhagavatā kīdisī dhammadesanā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante dutiye anusandhimhi tividhā pāṭihāriyā paṭisaṃyuttā dhammadesanā bhagavatā desitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',283,replace(replace('<p class="bodytext">‘‘Seyyathāpi <a name="M1.0283"></a> bhavaṃ gotamo bhavañcānando, ete me pujjā ete me pāsaṃsā’’ –</p>\r\n<p class="bodytext">‘‘Seyyathāpi bhavaṃ gotamo bhavañcānando, ete me pujjā ete me pāsaṃsā’’ –</p>\r\n<p class="subhead">Mahāvagga, venāgapurasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',287,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0287"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanimāte mahāvagge tatiyaṃ venāgapurasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante venāgapure nāma brāhmaṇānaṃ gāme venāgapurikaṃ vacchagottaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Venāgapuriko bhante vacchagotto brāhmaṇo bhagavantaṃ etadavoca ‘‘acchariyaṃ bho gotama, abbhutaṃ bho gotama, yāvañcidaṃ bhoto gotamassa vippasannāni indriyāni, parisuddho chavivaṇṇo pariyodāto (peyyāla) evarūpānaṃ nūna bhavaṃ gotamo uccāsayana mahāsayanānaṃ nikāmalābhī akicchalābhī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘yāni kho pana tāni brāhmaṇa uccāsayana mahāsayanāni. Seyyathidaṃ, āsandi pallaṅko gonako </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',288,replace(replace('<p class="bodytext">cittako <a name="M1.0288"></a> paṭikā paṭalikā tūlikā vikatikā uddalomī ekantalomī kaṭṭissaṃ koseyyaṃ kuṭṭakaṃ hatthattharaṃ assattharaṃ rathattharaṃ ajinappaveṇī kadalimigapavarapaccattharaṇaṃ sauttaracchadaṃ ubhatolohitakūpadhānaṃ. Dullabhāni tāni pabbajitānaṃ, laddhā ca pana nakappantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">‘‘Acchariyaṃ bho gotama abbhutaṃ bho gotama’’.</p>\r\n<p class="subhead">Dibba uccāsayanamahāsayana</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',290,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0290"></a> tattha bhagavatā dibbaṃ uccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idhāhaṃ brāhmaṇa yaṃ gāmaṃ vā nigamaṃ vā upanissāya viharāmi, so pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya tameva gāmaṃ vā nigamaṃ vā piṇḍāya pavisāmi, so pacchābhattaṃ piṇḍapātapaṭikkanto vanantaññeva pavisāmīti evamādinā bhante tattha bhagavatā dibbaṃ uccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="subhead">Brahma uccāsayanamahāsayana</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathañcāvuso tattha bhagavatā brahmaṃ uccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idhāhaṃ brāhmaṇaṃ yaṃ gāmaṃ vā nigamaṃ vā upanissāya viharāmi, so pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya tameva gāmaṃ vā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',293,replace(replace('<p class="bodytext">nigamaṃ <a name="M1.0293"></a> vā piṇḍāya pavisāmīti evamādinā bhante tattha bhagavatā brahmauccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="bodytext">Katamaṃ pana taṃ bho gotama brahmauccāsayanamahāsayanaṃ.</p>\r\n<p class="subhead">Ariya uccāsayanamahāsayana</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',294,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0294"></a> tattha bhagavatā ariyaṃ uccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',295,replace(replace('<p class="bodytext">idhāhaṃ <a name="M1.0295"></a> brāhmaṇa yaṃ gāmaṃ vā nigamaṃ vā upanissāya viharāmi, so pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya tameva gāmaṃ vā nigamaṃ vā piṇḍāya pavisāmi. So pacchābhattaṃ piṇḍapāta paṭikkanto vanantaññeva pavisāmīti evamādinā bhante tattha bhagavatā ariyaṃ uccāsayanamahāsayanaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',297,replace(replace('<p class="bodytext">tattheva <a name="M1.0297"></a> āvuso mahāvagge pañcamaṃ kesamuttisuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante kesamuttenāma kālāmānaṃ nigame kesamuttiye kālāme ārabbha bhāsitaṃ. Kesamuttiyā bhante kālāmā bhagavantaṃ etadavocuṃ ‘‘santi bhante eke samaṇa brāhmaṇā kesamuttaṃ āgacchanti, te sakaṃyeva vādaṃ dīpenti, jotenti, parappavādaṃ pana khuṃsenti vambhenti paribhavanti, omakkhiṃ karonti, aparepi bhante eke samaṇabrāhmaṇā kesamuttaṃ āgacchanti, tepi sakaṃyeva vādaṃ dīpenti jotenti, parappavādaṃ pana khuṃsenti vambhenti paribhavanti, omakkhiṃ karonti. Tesaṃ no bhante amhākaṃ hoteva kaṅkhā, hoti vicikicchā ko su nāma imesaṃ bhavataṃ samaṇabrāhmaṇānaṃ saccaṃ āha, ko musā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘alañhi vo kālāmā kaṅkhituṃ alaṃ vicikicchituṃ, kaṅkhīyeva pana vo ṭhāne vicikicchā uppannā’’ti eva mādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Uposatasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',303,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0303"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte mahāvagge dasamaṃ uposathasuttaṃ kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante visākhaṃ migāramātaraṃ ārabbha ‘‘tayo kho me visākhe uposathā. Katame tayo. Gopālakuposatho nigaṇṭhuposatho ariyuposatho’’ti evaṃ kho bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Handa kuto nu tvaṃ visākhe āgacchasi divā divassa.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',304,replace(replace('<p class="bodytext">‘‘Tayo <a name="M1.0304"></a> kho me visākhe uposathā. Katame tayo, gopālakuposatho nigaṇṭhuposatho ariyuposatho’’ –</p>\r\n<p class="subhead">Gopālakaupuggala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathañcāvuso tattha bhagavatā gopālakuposatho pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathañca visākhe gopālakuposatho hoti, seyyathāpi visākhe gopālako sāyanhasamaye sāmikānaṃ gāvo niyyātetvā iti paṭisañcikkhati ‘‘ajja kho gāvo amukasmiñca amukasmiñca padese cariṃsu, amukasmiñca amukasmiñca padese pānīyāni piviṃsu.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',305,replace(replace('<p class="bodytext">Sve <a name="M1.0305"></a> dāni gāvo amukasmiñca amukasmiñca padese carissanti, amukasmiñca amukasmiñca padese pānīyāni pivissantī’’ti evamādinā bhante tattha bhagavatā gopālakuposatho pakāsito.</p>\r\n<p class="subhead">Nigaṇṭhaupuggala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',306,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0306"></a> tattha bhagavatā nigaṇṭhuposatho pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathañca visākhe nigaṇṭhuposatho hoti, atthi visākhe nigaṇṭhānāma samaṇajātikā, te sāvakaṃ evaṃ samādapenti, ‘‘ehitvaṃ ambhopurisa ye puratthimāya disāya pāṇā paraṃ yojanasataṃ, tesu daṇḍaṃ nikkhipāhi, ye pacchimāya disāya. Ye uttarāya disāya. Ye dakkhiṇāya disāya pāṇā paraṃ yojanasataṃ, tesu daṇḍaṃ nikkhipāhī’’ti evamādinā bhante tattha bhagavatā nigaṇṭhuposatho pakāsito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',307,replace(replace('<p class="bodytext">Ehi <a name="M1.0307"></a> tvaṃ ambho purisa.</p>\r\n<p class="bodytext">Ehi tvaṃ ambho purisa sabbacelāni nikkhipitvā evaṃ vadehi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',308,replace(replace('<p class="bodytext">Nāhaṃ <a name="M1.0308"></a> kvacani kassaci kiñcanatasmiṃ, na ca mama kvacani katthaci kiñcana tatthi.</p>\r\n<p class="subhead">Ariyā uposatho</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',309,replace(replace('<p class="bodytext">kathañcāvuso <a name="M1.0309"></a> tattha bhagavatā ariyuposatho vitthārena vibhajitvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathañca visākhe ariyuposatho hoti, upakkiliṭṭhassa visākhe cittassa upakkamena pariyodapanā hoti. Kathañca visākhe upakkiliṭṭhassa cittassa upakkamena pariyodapanā hoti. Idha visākhe ariyasāvako tathāgataṃ anussarati ‘‘itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā’’ti. Tassa tathāgataṃ anussarato cittaṃ pasīdati, pāmojjaṃ uppajjati, ye cittassa upakkilesā te pahīyantīti evamādinā bhante tattha bhagavatā ariyo uposatho vitthāretvā pakāsito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',316,replace(replace('<p class="bodytext">evaṃ <a name="M1.0316"></a> upavutthassa pana āvuso ariyuposathassa kathaṃ mahapphalatā mahānisaṃsatā vuttā bhagavatā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kīvamahapphalā hoti, kīvamahānisaṃso, kīvamahājutiko, kīvamahāvipphāro, seyyathāpi visākhe yo imesaṃ soḷasannaṃ mahājanapadānaṃ pahūtarattajanapadānaṃ issariyādhipaccaṃ rajjaṃ kāreyya, seyyathidaṃ ‘‘aṅgānaṃ magadhānaṃ kāsīnaṃ kosalānaṃ vajjīnaṃ mallānaṃ cetīnaṃ vaṅgānaṃ kurūnaṃ pañcālānaṃ macchānaṃ sūrasenānaṃ assakānaṃ avantīnaṃ gandhārānaṃ kambojānaṃ, aṭṭhaṅgasamannāgatassa uposathassa etaṃ kalaṃ nāgghati soḷasi’’nti evamādinā bhante tattha bhagavatā evaṃ upavutthassa ariyuposathassa mahapphalatā mahānisaṃsatā vuttā.</p>\r\n<p class="subhead">Ānandavagga</p>\r\n<p class="subhead">Ājīvakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',319,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M1.0319"></a> āvuso tikanipāte ānandavagge dutiyaṃ ājīvakasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante aññataraṃ ājīvakasāvakaṃ gahapatiṃ ārabbha āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ. Aññataro bhante ājīvakasāvako gahapati āyasmantaṃ ānandaṃ etadavoca ‘‘kesaṃ no bhante ānanda dhammo svākkhāto, ke loke suppaṭipannā, ke loke sukatā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘tena hi gahapati taññevettha paṭipucchissāmi, yathā te khameyya, tathā naṃ byākareyyāsī’’ti evamādinā āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',321,replace(replace('<p class="bodytext">‘‘Kesaṃ <a name="M1.0321"></a> no bhante ānanda dhammo svākkhāto, ke loke suppaṭipannā, ke loke sukatā’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',322,replace(replace('<p class="bodytext">‘‘Taṃ <a name="M1.0322"></a> kiṃ maññasi gahapati’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',323,replace(replace('<p class="bodytext">‘‘Iti <a name="M1.0323"></a> kho gahapati tayāvetaṃ byākataṃ’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',324,replace(replace('<p class="bodytext">‘‘Abhikkantaṃ <a name="M1.0324"></a> bhante, abhikkantaṃ bhante, seyyathāpi bhante nikkujjitaṃ vā ukkujjeyya’’ –</p>\r\n<p class="subhead">Gandhajātasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',325,replace(replace('<p class="bodytext">tattheva <a name="M1.0325"></a> āvuso navamaṃ gandhajātasuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante ānandaṃ ārabbha bhāsitaṃ. Āyasmā bhante ānando bhagavantaṃ etadavoca ‘‘tīṇimāni bhante gandhajātāni, yesaṃ anuvātaṃyeva gandho gacchati no paṭivātaṃ…pe… atthi nu kho bhante kiñci gandhajātaṃ, yassa anuvātampi gandho gacchati paṭivātampi gandho gacchati, anuvātapaṭivātampi gandho gacchatī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘atthānanda kiñci gandhajātaṃ, yassa anuvātampi gandho gacchati, paṭivātampi gandho gacchati, anuvātapaṭivātampi gandho gacchatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',327,replace(replace('<p class="gatha1">Na <a name="M1.0327"></a> pupphagandho paṭivātameti;</p>\r\n<p class="gatha2">Na candanaṃ tagaramallikā vā;</p>\r\n<p class="gatha3">Satañca gandho paṭivātameti;</p>\r\n<p class="gathalast">Sabbā disā sappuriso pavāyati;</p>\r\n<p class="subhead">Gadrabhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',328,replace(replace('<p class="bodytext">samaṇavagge <a name="M1.0328"></a> āvuso dutiyaṃ gadrabhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samaṇavagge bhante dutiyaṃ gadrabhasuttaṃ ‘‘seyyathāpi bhikkhave gadrabho gogaṇaṃ piṭṭhito piṭṭhito anubandho hoti ‘ahampi dammo ahampi dammo’ti, tassa na tādiso vaṇṇo hoti seyyathāpi gunnaṃ, na tādiso saro hoti seyyathāpi gunnaṃ, na tādisaṃ padaṃ hoti seyyathāpi gunnaṃ, so gogaṇaṃyeva piṭṭhito piṭṭhito anubandho hoti ahampi dammo ahampi dammo’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',329,replace(replace('<p class="bodytext">Tasmātiha <a name="M1.0329"></a> bhikkhave evaṃ sikkhitabbaṃ.</p>\r\n<p class="bodytext">‘‘Tibbo no chando bhavissati adhisīlasikkhāsamādāne…pe… adhipaññāsikkhāsamādāne’’ –</p>\r\n<p class="subhead">Saṅkavāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',330,replace(replace('<p class="bodytext">tattheva <a name="M1.0330"></a> āvuso ekādasamaṃ saṅkavāsuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosalesu bhante saṅkavāyaṃ nāma kosalānaṃ nigame kassapagottaṃ nāma bhikkhuṃ ārabbha bhāsitaṃ. Kassapa gotto bhante bhikkhu bhagavati manopadūsitvā bhagavato santike accayaṃ accayato desesi. Tasmiṃ bhante vatthusmiṃ ‘‘thero cepi kassapa bhikkhu na sikkhākāmo na sikkhāsamādānassa vaṇṇavādī. Ye caññe bhikkhū na sikkhākāmā, te ca na sikkhāya samādapeti, ye caññe bhikkhū sikkhākāmā tesañca na vaṇṇaṃ bhaṇati bhūtaṃ tacchaṃ kālenā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Loṇakapallavagga</p>\r\n<p class="subhead">Accāyikasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',334,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M1.0334"></a> sammāsambuddhena aṅguttaranikāye tikanipāte loṇakapallavagge paṭhamaṃ accāyikasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> loṇakapallavagge bhante paṭhamaṃ accāyikasuttaṃ ‘‘tīṇimāni bhikkhave kassakassa gahapatissa accāyikāni karaṇīyāni. Katamāni tīṇi, idha bhikkhave kassako gahapati sīghaṃ sīghaṃ khettaṃ sukaṭṭhaṃ karoti sumatikataṃ. Sīghaṃ sīghaṃ khettaṃ sukaṭṭhaṃ karitvā sumatikataṃ sīghaṃ sīghaṃ bījāni patiṭṭhāpeti. Sīghaṃ sīghaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',335,replace(replace('<p class="bodytext">bījāni <a name="M1.0335"></a> patiṭṭhāpetvā sīghaṃ sīghaṃ udakaṃ abhinetipi apanetipi. Imāni kho bhikkhave tīṇi kassakassa gahapatissa accāyikāni karaṇīyāni’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sambodhavagga</p>\r\n<p class="subhead">Ruṇṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',337,replace(replace('<p class="bodytext">sambodhavagge <a name="M1.0337"></a> panāvuso pañcamaṃ ruṇṇasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambodhavagge bhante pañcamaṃ ruṇṇasuttaṃ ‘‘ruṇṇamidaṃ bhikkhave ariyassa vinaye yadidaṃ gītaṃ, ummattakamidaṃ bhikkhave ariyassa vinaye yadidaṃ naccaṃ, komārakamidaṃ bhikkhave ariyassa vinaye yadidaṃ ativelaṃ dantavidaṃsakahasitaṃ. Tasmātiha bhikkhave setughāto gīte setughāto nacce, alaṃ vo dhammappamoditānaṃ sataṃ sitaṃ sitamattāyā’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Arakkhīkasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',338,replace(replace('<p class="bodytext">tattheva <a name="M1.0338"></a> āvuso sattamaṃ arakkhitasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante anātapiṇḍikaṃ gahapatiṃ ārabbha ‘‘citte gahapati arakkhite kāyakammampi arakkhitaṃ hoti, vacīkammampi. Manokammampi arakkhitaṃ hoti. Tassa arakkhitakāyakammantassa arakkhitavacīkammantassa arakkhitamanokammantassa kāyakammampi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',339,replace(replace('<p class="bodytext">avassutaṃ <a name="M1.0339"></a> hoti, vacīkammampi avassutaṃ hoti, manokammampi avassutaṃ hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āpāyikavagga</p>\r\n<p class="subhead">Apaṇṇakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',341,replace(replace('<p class="bodytext">āpāyikavagge <a name="M1.0341"></a> āvuso chaṭṭhaṃ apaṇṇakasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āpāyikavagge bhante chaṭṭhaṃ apaṇṇakasuttaṃ ‘‘tisso imā bhikkhave vipattiyo. Katamā tisso sīlavipatti, cittavipatti, diṭṭhivipatti. Katamā ca bhikkhave sīlavipatti, idha bhikkhave ekacco pāṇātipātī hoti…pe… samphappalāpī hoti. Ayaṃ vuccati bhikkhave sīlavipattī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kusināravagga</p>\r\n<p class="subhead">Kusinārasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',344,replace(replace('<p class="bodytext">kusināravagge <a name="M1.0344"></a> panāvuso paṭhamaṃ kusinārasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kusinārāyaṃ bhante baliharaṇe vanasaṇḍe sambahule bhikkhū ārabbha ‘‘idha bhikkhave bhikkhu aññataraṃ gāmaṃ vā nigamaṃ vā upanissāya viharati, tamenaṃ gahapati vā gahapatiputto vā upasaṅkamitvā svātanāya bhattena nimantetī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Hatthakasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',347,replace(replace('<p class="bodytext">tattheva <a name="M1.0347"></a> āvuso pañcamaṃ hatthakasuttaṃ bhagavatā kattha kena saddhiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante hatthakena devaputtena saddhiṃ ‘‘ye te hatthaka dhammā pubbe manussabhūtassa pavattino ahesuṃ, apinu te te dhammā etarahi pavattino’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Anuruddhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',350,replace(replace('<p class="bodytext">tattheva <a name="M1.0350"></a> āvuso aṭṭhamaṃ anuruddhasuttaṃ kaṃ ārabbha kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhamaṃ bhante anuruddhasuttaṃ āyasmantaṃ anuruddhattheraṃ ārabbha āyasmatā sāriputtattherena dhammasenāpatinā ‘‘yaṃ kho te āvuso anuruddhaṃ evaṃ hoti ‘ahaṃ dibbena cakkhunā visuddhena atikkantamānusakena sahassaṃ lokaṃ volokemī’ti, idaṃ te mānasmiṃ’’ti evamādinā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',352,replace(replace('<p class="bodytext">‘‘Sādhu <a name="M1.0352"></a> vatāyasmā anuruddho ime tayo dhamme pahāya ime tayo dhamme amanasikaritvā amatāya dhātuyā cittaṃ upasaṃharatu’’ –</p>\r\n<p class="subhead">Kusināravagga</p>\r\n<p class="subhead">Paṭicchannasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',353,replace(replace('<p class="bodytext">tenāvuso <a name="M1.0353"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye tikanipāte kusināravagge navamaṃ paṭicchannasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> navamaṃ bhante paṭicchannasuttaṃ ‘‘tīṇimāni bhikkhave paṭicchannāni āvahanti no vivaṭāni. Katamāni tīṇi, mātugāmo bhikkhave paṭicchanno āvahati no vivaṭo, brāhmaṇānaṃ bhikkhave mantā paṭicchannā āvahanti no vivaṭā, micchādiṭṭhi bhikkhave paṭicchannā āvahati no vivaṭā. Imāni kho bhikkhave tīṇi paṭicchannāni āvahanti no vivaṭānī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Lekhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',355,replace(replace('<p class="bodytext">tattheva <a name="M1.0355"></a> āvuso dasamaṃ lekhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasamaṃ bhante lekhasuttaṃ ‘‘tayome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame tayo, pāsāṇalekhūpamo puggalo pathavilekhūpamo puggalo udakalekhūpamo puggalo. Katamo ca bhikkhave pāsāṇa lekhūpamo puggalo, idha bhikkhave ekacco puggalo abhiṇhaṃ kujjhati, so ca khvassa kodho dīgharattaṃ anuseti. Seyyathāpi bhikkhave pāsāṇalekhā na khippaṃ lujjati vātena vā udakena vā, ciraṭṭhitikā hoti, evameva kho bhikkhave idhekacco puggalo abhiṇhaṃ kujjhati, so ca khvassa kodho dīgharattaṃ anuseti. Ayaṃ vuccati bhikkhave pāsāṇalekhūpamo puggalo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Yodhājīvavagga</p>\r\n<p class="subhead">Kesakambalasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',358,replace(replace('<p class="bodytext">yodhājīvavagge <a name="M1.0358"></a> pana āvuso pañcamaṃ kesakambalasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yodhājīvavagge bhante pañcamaṃ kesakambalasuttaṃ ‘‘seyyathāpi bhikkhave yāni kānici tantāvutānaṃ vatthānaṃ, kesakambalo tesaṃ paṭikiṭṭho akkhāyati. Kesakambalo bhikkhave sīte sīto uṇhe uṇho dubbaṇṇo duggandho dukkhasamphasso. Evameva kho bhikkhave yānikānici puthusamaṇabrāhmaṇavādānaṃ, makkhalivādo tesaṃ paṭikiṭṭho akkhāyatīti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Maṅgalavagga</p>\r\n<p class="subhead">Vandanāsutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',360,replace(replace('<p class="bodytext">maṅgalavagge <a name="M1.0360"></a> panāvuso navamaṃ vandanāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> maṅgalavagge bhante navamaṃ vandanāsuttaṃ ‘‘tisso imā bhikkhave vandanā. Katamā tisso, kāyena vācāya manasā. Imā kho bhikkhave tisso vandanā’’ti, evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">‘‘Tisso imā bhikkhave vandanā. Katamā tisso, kāyena vācāya manasā. Imā kho bhikkhave tisso vandanā’’.</p>\r\n<p class="subhead">Pubbaṇhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',361,replace(replace('<p class="bodytext">tattho <a name="M1.0361"></a> āvuso dasamaṃ pubbaṇhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasamaṃ bhante pubbaṇhasuttaṃ ‘‘ye bhikkhave sattā pubbaṇhasamayaṃ kāyena sucaritaṃ caranti, vācāya sucaritaṃ caranti, manasā sucaritaṃ caranti. Supubbaṇho bhikkhave tesaṃ sattānaṃ. Ye bhikkhave sattā majjhanhikasamayaṃ…pe… sāyanhasamayaṃ kāyena sucaritaṃ caranti, vācāya sucaritaṃ caranti, manasā sucaritaṃ caranti. Susāyanho bhikkhave tesaṃ sattāna’’nti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',362,replace(replace('<p class="gatha1">Sunakkhattaṃ <a name="M1.0362"></a> sumaṅgalaṃ, suppabhātaṃ suhuṭṭhitaṃ;</p>\r\n<p class="gathalast">Sukhaṇo sumuhutto ca, suyiṭṭhaṃ brahmacārisu.</p>\r\n<p class="subhead">Bhaṇḍagāmavagga</p>\r\n<p class="subhead">Anubuddhasutta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',1,replace(replace('<p class="bodytext">catukkanipāte <a name="M2.0001"></a> pana āvuso bhaṇḍagāmavagge paṭhamaṃ anubuddhasuttaṃ bhagavatā sattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vajjīsu bhante bhaṇḍagāme sambahule bhikkhū ārabbha ‘‘catunnaṃ bhikkhave dhammānaṃ ananubodhā appaṭivedhā evamidaṃ dīghamaddhānaṃ sandhāvitaṃ saṃsaritaṃ mamañceva tumhākañca. Katamesaṃ catunnaṃ, ariyassa bhikkhave sīlassa ananubodhā appaṭivedhā evamidaṃ dīghamaddhānaṃ sandhāvitaṃ saṃsaritaṃ mamañceva tumhākañca. Ariyassa bhikkhave samādhissa…. Ariyāya bhikkhave paññāya…. Ariyāya bhikkhave vimuttiyā ananubodhā appaṭivedhā evamidaṃ dīghamaddhānaṃ sandhāvitaṃ saṃsaritaṃ mamañceva tumhākañcā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Appassutasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',3,replace(replace('<p class="bodytext">tattheva <a name="M2.0003"></a> āvuso chaṭṭhaṃ appassutasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattha bhante chaṭṭhaṃ appassutasuttaṃ ‘‘cattārome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, appassuto sutena anupapanno, appassuto sutena upapanno, bahussuto sutena anupapanno, bahussuto sutena upapanno’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Caravagga</p>\r\n<p class="subhead">Carasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',7,replace(replace('<p class="bodytext">caravagge <a name="M2.0007"></a> pana āvuso paṭhamaṃ carasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> caravagge bhante paṭhamaṃ carasuttaṃ ‘‘carato cepi bhikkhave bhikkhuno uppajjati kāmavitakko vā byāpādavitakko vā vihiṃsāvibhatto vā, taṃ ce bhikkhu adhivāseti nappajahati na vinodeti na byantīkaroti na anabhāvaṃ gametī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Uruvelavagga</p>\r\n<p class="subhead">Lokasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',9,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M2.0009"></a> sammāsambuddhena aṅguttaranikāye catukkanipāte uruvelavagge tatiyaṃ lokasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> uruvelavagge bhante tatiyaṃ lokasuttaṃ ‘‘loko bhikkhave tathāgatena abhisambuddho, lokasmā tathāgato visaṃyutto, lokasamudayo bhikkhave tathāgatena abhisambuddho, lokasamudayo tathāgatassa pahīno, lokanirodho bhikkhave tathāgatena abhisambuddho lokanirodho tathāgatassa sacchikato, lokanirodhagāminī paṭipadā bhikkhave tathāgatena abhisambuddhā, lokanirodhagāmini paṭipadā tathāgatassa bhāvitā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',12,replace(replace('<p class="gatha1">Danto <a name="M2.0012"></a> damayataṃ seṭṭho,</p>\r\n<p class="gatha2">Santo samayataṃ isi;</p>\r\n<p class="gatha3">Mutto mocayataṃ aggo,</p>\r\n<p class="gathalast">Tiṇṇo tārayataṃ varo–</p>\r\n<p class="gatha1">Sabbaṃ lokaṃ abhiññāya,</p>\r\n<p class="gatha2">Sabbaṃ loke yathātathaṃ;</p>\r\n<p class="gatha3">Sabbaṃ lokaṃ visaṃyutto,</p>\r\n<p class="gathalast">Sabbaloke anūpayo.</p>\r\n<p class="gatha1">Sa ve sabbābhibhū dhīro,</p>\r\n<p class="gatha2">Sabbaganthappamocano;</p>\r\n<p class="gatha3">Puṭṭha’ssa paramā santi,</p>\r\n<p class="gathalast">Nibbānaṃ akuto bhayaṃ.</p>\r\n<p class="gatha1">Esa khīṇāsavo buddho,</p>\r\n<p class="gatha2">Anīgho chinnasaṃsayo;</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',13,replace(replace('<p class="gatha3">Sabbakammakkhayaṃ <a name="M2.0013"></a> patto,</p>\r\n<p class="gathalast">Vimutto upadhisaṅkhaye;</p>\r\n<p class="gatha1">Esa so bhagavā buddho,</p>\r\n<p class="gatha2">Esa sīho anuttaro;</p>\r\n<p class="gatha3">Sadevakassa lokassa,</p>\r\n<p class="gathalast">Brahmacakkaṃ pavattayī.</p>\r\n<p class="gatha1">Iti devā manussā ca,</p>\r\n<p class="gatha2">Ye buddhaṃ saraṇaṃ gatā;</p>\r\n<p class="gatha3">Saṅgamma taṃ namassanti,</p>\r\n<p class="gathalast">Mahantaṃ vītasāradaṃ.</p>\r\n<p class="gatha1">Danto damayataṃ seṭṭho,</p>\r\n<p class="gatha2">Santo samayataṃ isi;</p>\r\n<p class="gatha3">Mutto mocayataṃ aggo,</p>\r\n<p class="gathalast">Tiṇṇo tārayataṃ varo.</p>\r\n<p class="gatha1">Iti hetaṃ namassanti,</p>\r\n<p class="gatha2">Mahantaṃ vītasāradaṃ;</p>\r\n<p class="gatha3">Sadevakasmiṃ lokasmiṃ,</p>\r\n<p class="gathalast">Natthi me paṭipuggalo–</p>\r\n<p class="subhead">Brahmacariyasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> tattheva āvuso pañcamaṃ brahmacariyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> pañcamaṃ bhante brahmacariyasuttaṃ ‘‘nayidaṃ bhikkhave brahmacariyaṃ vussati janakuhanatthaṃ, na janalapanatthaṃ, na </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',14,replace(replace('<p class="bodytext">lābhasakkārasilokānisaṃsuttaṃ<a name="M2.0014"></a>, na itivādappamokkhānisaṃsatthaṃ, na ‘iti maṃ jano jānātū’ti. Atha kho idaṃ bhikkhave brahmacariyaṃ vussati saṃvaratthaṃ pahānatthaṃ virāgatthaṃ nirodhattha’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Ariyavaṃsasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',15,replace(replace('<p class="bodytext">tattheva <a name="M2.0015"></a> āvuso aṭṭhamaṃ ariyavaṃsasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante aṭṭhamaṃ ariyavaṃsasuttaṃ ‘‘cattārome bhikkhave ariyavaṃsā aggaññā rattaññā vaṃsaññā porāṇā, asaṃkiṇṇā asaṃkiṇṇapubbāna saṃkīyissanti, appaṭikuṭṭhā samaṇehi brāhmaṇehi viññūhi. Katame cattāro, idha bhikkhave bhikkhu santuṭṭho hoti itarītarena cīvarena, itarītaracīvarasantuṭṭhiyā ca vaṇṇavādī, na ca cīvarahetu anesanaṃ appatirūpaṃ āpajjati, aladdhā ca cīvaraṃ na paritassati, laddhā ca cīvaraṃ agadhito amucchito anajjhopanno ādīnavadassāvī nissaraṇapañño paribhuñjati, tāya ca pana itarītaracīvarasantuṭṭhiyā nevattānukkaṃseti no paraṃ vambheti. Yo hi tattha dakkho analaso sampajāno paṭissabho, ayaṃ vuccati bhikkhave bhikkhu porāṇe aggaññe ariyavaṃse ṭhito’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Cakkavagga</p>\r\n<p class="subhead">Cakkasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',19,replace(replace('<p class="bodytext">cakkavagge <a name="M2.0019"></a> pana āvuso paṭhamaṃ cakkasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> cakkavagge bhante paṭhamaṃ cakkasuttaṃ ‘‘cattārimāni bhikkhave cakkāni, yehi samannāgatānaṃ devamanussānaṃ catucakkaṃ vattati, yehi samannāgatā devamanussā nacirasseva mahantattaṃ vepullattaṃ pāpuṇanti bhogesu. Katamāni cattāri, patirūpadesavāso sappurisāvassayo attasammāpaṇidhi pubbe ca katapuññatā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Saṅgahasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',21,replace(replace('<p class="bodytext">tattheva <a name="M2.0021"></a> āvuso dutiyaṃ saṅgahasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante dutiyaṃ saṅgahasuttaṃ ‘‘cattārimāni bhikkhave saṅgahavatthūni. Katamāni cattāri, dānaṃ peyyavajjaṃ atthacariyā samānattatā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sīhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',23,replace(replace('<p class="bodytext">bhattheva <a name="M2.0023"></a> āvuso tatiyaṃ sīhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante tatiyaṃ sīhasuttaṃ ‘‘sīho bhikkhave migarājā sāyanusamayaṃ āsayā nikkhamati, āsayā nikkhamitvā vijambhati, vijambhitvā samantā catuddisā anuviloketi, samantā catuddisā anuviloketvā tikkhattuṃ sīhanādaṃ nadati, tikkhattuṃ sīhanādaṃ naditvā gocarāya pakkamatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Doṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',25,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0025"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye catukkanipāte cakkavagge chaṭṭhaṃ doṇasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',26,replace(replace('<p class="bodytext">antarā <a name="M2.0026"></a> ca bhante ukkaṭṭhaṃ antarā ca setabyaṃ doṇaṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Doṇo bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘devo no bhavaṃ bhavissatī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘na kho ahaṃ brāhmaṇa devo bhavissāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',27,replace(replace('<p class="bodytext">‘‘Acchariyaṃ <a name="M2.0027"></a> vata bho, abbhutaṃ vata bho, na vatimāni manussabhūtassa padāni bhavissanti’’.</p>\r\n<p class="bodytext">‘‘Devo no bhavaṃ bhavissati’’.</p>\r\n<p class="bodytext">‘‘Na kho ahaṃ brāhmaṇa devo bhavissami’’.</p>\r\n<p class="bodytext">‘‘Gandhabbo no bhavaṃ bhavissati’’.</p>\r\n<p class="bodytext">‘‘Na kho ahaṃ brāhmaṇa gandhabbo bhavissāmi’’.</p>\r\n<p class="subhead">Ujjayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',30,replace(replace('<p class="bodytext">tattheva <a name="M2.0030"></a> āvuso navamaṃ ujjayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> navamaṃ bhante ujjayasuttaṃ ‘‘na kho ahaṃ brāhmaṇa sabbaṃ yaññaṃ vaṇṇemi, na panāhaṃ brāhmaṇa sabbaṃ yaññaṃ na vaṇṇemi. Yathārūpe kho brāhmaṇa yaññe gāvo haññanti, ajeḷakā haññanti, kukkuṭasūkarā haññanti, vividhā pāṇā saṅghātaṃ āpajjantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',31,replace(replace('<p class="bodytext">‘‘Bhavampi <a name="M2.0031"></a> no gotamo yaññaṃ vaṇṇeti’’.</p>\r\n<p class="subhead">Rohitassavagga</p>\r\n<p class="subhead">Samādhibhāvanāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',33,replace(replace('<p class="bodytext">rohitassavagge <a name="M2.0033"></a> pana āvuso paṭhamaṃ samādhibhāvanāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> rohitassavagge bhante paṭhamaṃ samādhibhāvanāsuttaṃ ‘‘catasso imā bhikkhave samādhibhāvanā, katamā catasso, atthi bhikkhave samādhibhāvanā bhāvitā bahulīkatā diṭṭhadhammasukhavihārāya saṃvattati, atthi bhikkhave samādhibhāvanā bhāvitā bahulīkatā ñāṇadassanappaṭilābhāya saṃvattati, atthi bhikkhave samādhibhāvanā bhāvitā bahulīkatā satisampajaññāya saṃvattati, atthi bhikkhave samādhibhāvanā bhāvitā bahulīkatā āsavānaṃ khayāya saṃvattatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Pañhabyākaraṇasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',37,replace(replace('<p class="bodytext">tattheva <a name="M2.0037"></a> āvuso dutiyaṃ pañhabyākaraṇasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante dutiyaṃ pañhabyākaraṇasuttaṃ ‘‘cattārimāni bhikkhave pañhabyākaraṇāni. Katamāni cattāri, atthi bhikkhave pañho ekaṃsabyākaraṇīyo, atthi bhikkhave pañho vibhajjabyākaraṇīyo, atthi bhikkhave pañho paṭipucchābyākaraṇīyo, atthi bhikkhave pañho ṭhapanīyo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Puññābhisandavagga</p>\r\n<p class="subhead">Paṭhamasaṃvāsasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',39,replace(replace('<p class="bodytext">puññābhisandavagge <a name="M2.0039"></a> pana āvuso tatiyaṃ saṃvāsasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> puññābhisandavagge bhante tatiyaṃ saṃvāsasuttaṃ antarā ca madhuraṃ antarā ca verañjaṃ sambahule gahapatayo ca gahapatāniyo ca ārabbha ‘‘cattārome gahapatayo saṃvāsā. Katame cattāro, chavo chavāya saddhiṃ saṃvasati, chavo deviyā saddhiṃ saṃvasati, devo chavāya saddhiṃ saṃvasati, devo deviyā saddhiṃ saṃvasatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhamasamajīvīsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',43,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0043"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye catukkanipāte puññābhisandavagge pañcamaṃ samajīvisuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> pañcamaṃ bhante samajīvisuttaṃ ‘‘ākaṅkheyyuṃ ce gahapatayo ubho jānipatayo diṭṭhe ceva dhamme aññamaññaṃ passituṃ, abhisamparāyañca aññamaññaṃ passituṃ, ubhova assu samasaddhā samasīlā samacāgā samapaññā, te diṭṭhe ceva dhamme aññamaññaṃ passanti, abhisamparāyañca aññamaññaṃ passantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Pattakammavagga</p>\r\n<p class="subhead">Ānaṇyasukhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',46,replace(replace('<p class="bodytext">pattakammavagge <a name="M2.0046"></a> pana āvuso dutiyaṃ ānaṇyasukhasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',47,replace(replace('<p class="bodytext">pattakammavagge <a name="M2.0047"></a> bhante dutiyaṃ ānaṇyasukhasuttaṃ sāvatthiyaṃ anāthapiṇḍikaṃ gahapatiṃ ārabbha ‘‘cattārimāni gahapati sukhāni adhigamanīyāni gihinā kāmabhoginā kālena kālaṃ samayena samayaṃ upādāya. Katamāni cattāri, atthisukhaṃ bhogasukhaṃ anaṇyasukhaṃ anavajjasukha’’nti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',50,replace(replace('<p class="gatha1">Ānaṇyasukhaṃ <a name="M2.0050"></a> ñatvāna, atho atthisukhaṃ paraṃ;</p>\r\n<p class="gatha2">Bhuñjaṃ bhogasukhaṃ macco, tato paññā vipassati;</p>\r\n<p class="gatha3">Vipassamāno jānāti, ubho bhāge sumedhaso;</p>\r\n<p class="gathalast">Anavajjasukhassetaṃ, kalaṃ nāgghati soḷasiṃ–</p>\r\n<p class="subhead">Appaṇṇakavagga</p>\r\n<p class="subhead">Sappurisasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> apaṇṇakavagge pana āvuso tatiyaṃ sappurisasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',51,replace(replace('<p class="bodytext">apaṇṇakavagge <a name="M2.0051"></a> bhante tatiyaṃ sappurisasuttaṃ ‘‘catūhi bhikkhave dhammehi samannāgato asappuriso veditabbo. Katamehi catūhi, idha bhikkhave asappuriso yo hoti parassa avaṇṇo taṃ apuṭṭhopi pātu karoti ko pana vādo puṭṭhassā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',55,replace(replace('<p class="bodytext"><span class="bld">Adhunāgatavadhukāsamena <a name="M2.0055"></a> cetasā viharissāma’’ –</span></p>\r\n<p class="subhead">Acinteyyasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> tattheva āvuso sattamaṃ acinteyyasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante acinteyyasuttaṃ ‘‘cattārimāni bhikkhave acinteyyāni na cintetabbāni, yāni cintento ummādassa vighātassa bhāgī assa. Katamāni cattāri, buddhānaṃ bhikkhave buddhavisayo acinteyyo na cintetabbo, yaṃ cintento ummādassa vighātassa bhāgī assā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Macalavagga</p>\r\n<p class="subhead">Tamotamasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',57,replace(replace('<p class="bodytext">macalavagge <a name="M2.0057"></a> pana āvuso pañcamaṃ tamotamasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> macalavagge bhante pañcamaṃ tamotamasuttaṃ ‘‘cattārome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, tamotamaparāyaṇo, tamojotiparāyaṇo, jotitamaparāyaṇo, jotijotiparāyaṇo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Asuravagga</p>\r\n<p class="subhead">Asurasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',61,replace(replace('<p class="bodytext">tenāvuso <a name="M2.0061"></a> bhagavatā…pe… sammāsambuddhena aṅguttaranikāye catukkanipāte asuravagge paṭhamaṃ asurasuttaṃ kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> asuravagge bhante paṭhamaṃ asurasuttaṃ ‘‘cattārome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, asuro asuraparivāro, asuro devaparivāro, devo asuraparivāro, devo devaparivāro’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Samādhisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',64,replace(replace('<p class="bodytext">tattheva <a name="M2.0064"></a> āvuso catutthaṃ samādhisuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> catutthaṃ bhante samādhisuttaṃ ‘‘cattārome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, idha bhikkhave ekacco puggalo lābhī hoti ajjhattaṃ cetosamathassa na lābhī adhipaññādhammavipassanāya. Idha pana bhikkhave puggalo lābhī hoti adhipaññādhammavipassanāya, na lābhī ajjhattaṃ cetosamathassa. Idha pana bhikkhave ekacco puggalo na ceva lābhī hoti ajjhattaṃ cetosamathassa, na ca lābhī adhipaññādhammavipassanāya. Idha pana bhikkhave ekacco puggalo lābhī ceva hoti ajjhattaṃ cetosamathassa, lābhī ca adhipaññādhammavipassanāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Rāgavinayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',68,replace(replace('<p class="bodytext">tattheva <a name="M2.0068"></a> āvuso chaṭṭhaṃ rāgavinayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> chaṭṭhaṃ bhante rāgavinayasuttaṃ ‘‘cattārome bhikkhave puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, attahitāya paṭipanno no parahitāya, parahitāya paṭipanno no attahitāya, nevattahitāya paṭipanno no parahitāya, attahitāya ceva paṭipanno parahitāya cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Valāhakavagga</p>\r\n<p class="subhead">Paṭhamavalāhakasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',71,replace(replace('<p class="bodytext">valāhakavagge <a name="M2.0071"></a> pana āvuso paṭhamaṃ valāhakasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘cattārome bhikkhave valāhakā. Katame cattāro, gajjitā no vassitā, vassitā no gajjitā, neva gajjitā no vassitā, gajjitā ca vassitā ca. Ime kho bhikkhave cattāro valāhakā. Evameva kho bhikkhave cattāro valāhakūpamā puggalā santo saṃvijjāmānā lokasmiṃ. Katame cattāro, gajjitā no vassitā, vassitā no gajjitā, neva gajjitā no vassitā, gajjitā ca vassitā cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Udakarahadasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',73,replace(replace('<p class="bodytext">tattheva <a name="M2.0073"></a> āvuso catutthaṃ udakarahadasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> catuttha bhante udakarahadasuttaṃ ‘‘cattārome bhikkhave udakarahadā. Katame cattāro, uttāno gambhīrobhāso, gambhīro uttānobhāso, uttāno uttānobhāso, gambhīro gambhīrobhāso, ime kho bhikkhave cattāro udakarahadā. Evameva kho bhikkhave cattāro udakarahadūpamā puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, uttāno gambhīrobhāso, gambhīro uttānobhāso, uttāno uttānobhāso, gambhīro gambhīrobhāso’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Mūsikasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',76,replace(replace('<p class="bodytext">tattheva <a name="M2.0076"></a> āvuso sattamaṃ mūsikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante mūsikasuttaṃ ‘‘catasso imā bhikkhave mūsikā. Katamā catasso, gādhaṃ kattā no vasitā, vasitā no gādhaṃ kattā, neva gādhaṃ kattā no vasitā, gādhaṃ kattā ca vasitā ca. Imā kho bhikkhave catasso mūsikā. Evameva kho bhikkhave cattāro mūsikūpamā puggalā santo saṃvijjamānā lokasmiṃ. Katame cattāro, gādhaṃ kattā no vasitā, vasitā no gādhaṃ kattā, neva gādhaṃ kattā no vasitā, gādhaṃ kattā ca vasitā cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Balībaddhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',79,replace(replace('<p class="bodytext">saṃgītāpi <a name="M2.0079"></a> āvuso aṅguttaranikāyato kānici suttāni uddharitvā paṭipucchissāmi bahujanassa sutavuḍḍhiyā. Saṃgīte āvuso aṅguttaranikāye catukkanipāte valāhakavagge aṭṭhamaṃ balībaddhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> valāhakavagge bhante aṭṭhamaṃ balībaddhasuttaṃ ‘‘cattārome bhikkhave balībaddhā. Katame cattāro, sagavacaṇḍo no paragavacaṇḍo, paragavacaṇḍo no sagavacaṇḍo, sagavacaṇḍo ca paragavacaṇḍo ca, neva sagavacaṇḍo no paragavacaṇḍo. Ime kho bhikkhave cattāro balībaddhā. Evameva kho bhikkhave cattāro balībaddhūpamā puggalā santo saṃvijjamānā lokasmi’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kesisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',82,replace(replace('<p class="bodytext">kesivagge <a name="M2.0082"></a> pana āvuso paṭhamaṃ kesisuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> kesiṃ bhante assadammasārathiṃ ārabbha bhāsitaṃ. Kesi bhante assadammasārathi bhagavantaṃ etadavoca ‘‘bhagavā pana bhante anuttaro purisadammasārathi, kathaṃ pana bhante bhagavā purisadammaṃ vinetī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘ahaṃ kho kesi purisadammaṃ saṇhenapi vinemi, pharusenapi vinemi, saṇhapharusenapi vinemi. Tatridaṃ kesi saṇhasmiṃ – iti kāyasucaritaṃ iti kāyasucaritassa vipāko, iti vacīsucaritaṃ iti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',83,replace(replace('<p class="bodytext">vacīsucaritassa <a name="M2.0083"></a> vipāko, iti manosucaritaṃ iti manosucaritassa vipāko, iti devā iti manussā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">‘‘Tvaṃ khosi kesi paññāto assadammasārathīti, kathaṃ pana tvaṃ kesi assadammasārathi’’ –</p>\r\n<p class="bodytext">‘‘Ahaṃ kho bhante assadammaṃ saṇhenapi vinemi, pharusenapi vinemi, saṇhapharusenapi vinemi’’ –</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',84,replace(replace('<p class="bodytext">‘‘Sace <a name="M2.0084"></a> te kesi assadammo saṇhena vinayaṃ na upeti, pharusena vinayaṃ na upeti, saṇhapharusena vinayaṃ na upeti, kinti naṃ karosi’’ –</p>\r\n<p class="bodytext">‘‘Bhagavā pana bhante anuttaro purisadammasārathi, kathaṃ pana bhante bhagavā purisadammaṃ vineti’’ –</p>\r\n<p class="bodytext">Na 0.0087 kho bhante bhagavato pāṇātipāto kappati, atha ca pana bhagavā evamāha ‘‘hanāmi naṃ kesī’’ti.</p>\r\n<p class="subhead">Attānuvādasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',89,replace(replace('<p class="bodytext">bhayavagge <a name="M2.0089"></a> pana āvuso paṭhamaṃ attānuvādasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> bhayavagge bhante paṭhamaṃ attānuvādasuttaṃ ‘‘cattārimāni bhikkhave bhayāni. Katamāni cattāri, attānuvādabhayaṃ parānuvādabhayaṃ daṇḍabhayaṃ duggatibhaya’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dhammakathikasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',92,replace(replace('<p class="bodytext">idāni <a name="M2.0092"></a> āvuso bahujanassa sutavuḍḍhiyā saṃgītā aṅguttaranikāyatopi kānici suttāni uddharitvā paṭipucchissāmi, saṃgīte āvuso aṅguttaranikāye catukkanipāte puggalavagge navamaṃ dhammakathikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> puggalavagge bhante navamaṃ dhammakathikasuttaṃ ‘‘cattārome bhikkhave dhammakathikā. Katame cattāro, idha bhikkhave ekacco dhammakathiko appañca bhāsati asahitañca, parisā cassa na kusalā hoti sahitāsahitassa. Evarūpo bhikkhave dhammakathiko evarūpāya parisāya dhammakathikotveva saṅkhaṃ gacchatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Rogasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',95,replace(replace('<p class="bodytext">indriyavagge <a name="M2.0095"></a> pana āvuso sattamaṃ rogasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante rogasuttaṃ ‘‘dveme bhikkhave rogā. Katame dve, kāyiko ca rogo, cetasiko ca rogo. Dissanti bhikkhave sattā kāyikena rogena ekampi vassaṃ ārogyaṃ paṭijānamānā dvepi vassāni tīṇipi vassāni cattāripi vassāni pañcapi vassāni dasapi vassāni vīsampi vassāni tiṃsampi vassāni cattārīsampi vassāni paññāsampi vassāni ārogyaṃ paṭijānamānā vassasatampi bhiyyopi ārogyaṃ paṭijānamānā. Te bhikkhave sattā sudullabhā lokasmiṃ, ye cetasikena rogena muhuttampi ārogyaṃ paṭijānanti aññatra khīṇāsavehī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Cattārome 0.0096 bhikkhave pabbajitassa rogā.</p>\r\n<p class="subhead">Yuganaddhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',98,replace(replace('<p class="bodytext">paṭipadāvagge <a name="M2.0098"></a> āvuso dasamaṃ yuganaddhasuttaṃ kattha kaṃ ārabbha kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> kosambiyaṃ bhante sambahule bhikkhū ārabbha ‘‘yo hi koci āvuso bhikkhu vā bhikkhunī vā mama santike arahattappattiṃ byākaroti, sabbo so catūhi maggehi etesaṃ vā aññatarenā’’ti evamādinā bhante āyasmatā ānandattherena dhammabhaṇḍāgārikena bhāsitaṃ.</p>\r\n<p class="subhead">Pāṭibhogasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',101,replace(replace('<p class="bodytext">brāhmaṇavagge <a name="M2.0101"></a> pana āvuso dutiyaṃ pāṭibhogasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> brāhmaṇavagge bhante dutiyaṃ pāṭibhogasuttaṃ ‘‘catunnaṃ bhikkhave dhammānaṃ natthi koci pāṭibhogo samaṇo vā brāhmaṇo vā devo vā māro vā brahmā vā koci vā lokasmi’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sutasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',103,replace(replace('<p class="bodytext">tattheva <a name="M2.0103"></a> āvuso tatiyaṃ sutasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> rājagahe bhante vassakāraṃ brāhmaṇaṃ magadhamahāmattaṃ ārabbha bhāsitaṃ. Vassakāro bhante brāhmaṇo magadhamahāmatto bhagavantaṃ etadavoca ‘‘ahañhi bho gotama evaṃvādī evaṃdiṭṭhi yo koci diṭṭhaṃ bhāsati ‘evaṃ me diṭṭha’nti, natthi tato doso. Yo koci sutaṃ bhāsati ‘evaṃ me suta’nti, natthi tato doso’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘nāhaṃ brāhmaṇa sabbaṃ diṭṭhaṃ ‘bhāsitabba’nti vadāmi, na panāhaṃ brāhmaṇa sabbaṃ diṭṭhaṃ ‘na bhāsitabba’nti vadāmi, nāhaṃ brāhmaṇa sabbaṃ sutaṃ ‘bhāsitabba’nti vadāmi, na panāhaṃ brāhmaṇa sabbaṃ sutaṃ ‘na bhāsitabba’nti vadāmī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Abhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',106,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0106"></a> āvuso catukkanipāte brāhmaṇavagge catutthaṃ abhayasuttaṃ bhagavatā kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> brāhmaṇavagge bhante catutthaṃ abhayasuttaṃ jāṇussoṇiṃ brāhmaṇaṃ ārabbha bhāsitaṃ. Jāṇussoṇi bhante brāhmaṇo bhagavantaṃ etadavoca ‘‘natthi yo maraṇadhammo samāno na bhāyati, na santāsaṃ āpajjati maraṇassā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘atthi brāhmaṇa maraṇadhammo samāno bhāyati, santāsaṃ āpajjati maraṇassa. Atthi pana brāhmaṇa maraṇadhammo samāno na bhāyati, na santāsaṃ āpajjati maraṇassā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sotānugatasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',113,replace(replace('<p class="bodytext">mahāvagge <a name="M2.0113"></a> pana āvuso paṭhamaṃ sotānugatasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> mahāvagge bhante paṭhamaṃ sotānugatasuttaṃ ‘‘sotānugatānaṃ bhikkhave dhammānaṃ vacasā paricitānaṃ manasānupekkhitānaṃ diṭṭhiyā suppaṭividdhānaṃ cattāro ānisaṃsā pāṭikaṅkhā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',115,replace(replace('<p class="bodytext">Ayaṃ <a name="M2.0115"></a> vā so dhammavinayo, yatthāhaṃ pubbe brahmacariyaṃ acariṃ.</p>\r\n<p class="subhead">Bhaddiyasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',118,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0118"></a> āvuso catukkanipāte mahāvagge tatiyaṃ bhaddiyasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> vesāliyaṃ bhante bhaddiyaṃ licchavi ārabbha bhāsitaṃ. Bhaddiyo bhante licchavi bhagavantaṃ etadavoca ‘‘sutaṃ metaṃ bhante ‘māyāvī samaṇo gotamo āvaṭṭaniṃ māyaṃ jānāti, yāya aññatitthiyānaṃ sāvake āvaṭṭetī’ti. Ye te bhante evamāhaṃsu ‘māyāvī samaṇo gotamo āvaṭṭaniṃ māyaṃ jānāti, yāya aññatitthiyānaṃ sāvake āvaṭṭetī’ti…pe… </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',119,replace(replace('<p class="bodytext">anabbhakkhātukāmā <a name="M2.0119"></a> hi mayaṃ bhante bhagavanta’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘etha tumhe bhaddiya mā anussavena mā paramparāya mā itikirāya mā piṭakasampadānena mā takkahetu mā nayahetu mā ākāravitakkena mā diṭṭhinijjhānakkhantiyā mā bhabbarūpatāya mā samaṇo no garūti. Yadā tumhe bhaddiya attanāva jāneyyātha ‘ime dhammā akusalā, ime dhammā sāvajjā, ime dhammā viññū garahitā, ime dhammā samattā samādinnā ahitāya dukkhāya saṃvattantī’ti. Atha tumhe bhaddiya pajaheyyāthā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',126,replace(replace('<p class="bodytext">imissā <a name="M2.0126"></a> ca pana āvuso desanāya desitāya bhaddiyassa licchavissa kīdiso dhammasavanānisaṃso adhigato. Kathañca naṃ bhagavā anuyuñjitvā taṃ vacanaṃ viniveṭhesi.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> imissā bhante dhammadesanāya desitāya bhaddiyassa licchavissa sotāpattiphalasaṅkhāto dhammasavanānisaṃso adhigato. Api nu tāhaṃ bhaddiya evaṃ avacaṃ ‘‘ehi me tvaṃ bhaddiya sāvako hohi, ahaṃ satthā bhavissāmī’’ti evamādinā ca naṃ bhante bhagavā paṭipucchitvā anuyuñjitvā taṃ vacanaṃ viniveṭhesi.</p>\r\n<p class="subhead">Mallikādevīsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',129,replace(replace('<p class="bodytext">tattheva <a name="M2.0129"></a> āvuso sattamaṃ mallikādevīsuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sāvatthiyaṃ bhante mallikādeviṃ ārabbha bhāsitaṃ. Mallikā bhante devī bhagavantaṃ etadavoca ‘‘ko nu kho bhante hetu ko paccayo, yena midhekacco mātugāmo dubbaṇṇā ca hoti durūpā supāpikā dassanāya, daliddā ca hoti appassakā appabhogā appesakkhā ca…pe… ko pana bhante hetu ko paccayo, yena midhekacco mātugāmo abhirūpā ca hoti dassanīyā pāsādikā paramāya vaṇṇapokkharatāya samannāgatā, aḍḍhā ca hoti mahaddhanā mahābhogā mahesakkhā cā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘idha mallike mātugāmo kodhanā hoti upāyāsabahulā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dasakammasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',134,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0134"></a> āvuso catukkanipāte sappurisavagge catutthaṃ dasakammasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',135,replace(replace('<p class="bodytext">sappurisavagge <a name="M2.0135"></a> bhante catutthaṃ dasakammasuttaṃ ‘‘asappurisañca vo bhikkhave desessāmi asappurisena asappurisatarañca sappurisañca sappurisena sappurisatarañca, taṃ suṇātha sādhukaṃ manasi karothā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Parisāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',137,replace(replace('<p class="bodytext">tattheva <a name="M2.0137"></a> āvuso parisāvagge paṭhamaṃ parisāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> parisāvagge bhante paṭhamaṃ parisāsuttaṃ ‘‘cattārome bhikkhave parisadūsanā. Katame cattāro, bhikkhu bhikkhave dussīlo pāpadhammo parisadūsano, bhikkhunī bhikkhave dussīlā pāpadhammā parisadūsanā, upāsako bhikkhave dussīlo pāpadhammo parisadūsano, upāsikā bhikkhave dussīlā pāpadhammā parisadūsanā. Ime kho bhikkhave cattāro parisadūsanā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sekhabalavagga</p>\r\n<p class="subhead">Saṃkhittasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',139,replace(replace('<p class="bodytext">pañcakanipāte <a name="M2.0139"></a> pana āvuso paṭhame sekhabalavagge paṭhamaṃ saṃkhittasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha ‘‘pañcimāni bhikkhave sekhabalāni. Katamāni pañca, saddhābalaṃ hirībalaṃ ottappabalaṃ vīriyabalaṃ paññābalaṃ. Imāni kho bhikkhave pañca sekhabalānī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dukkhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',140,replace(replace('<p class="bodytext">tattheva <a name="M2.0140"></a> āvuso tatiyaṃ dukkhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tatiyaṃ bhante dukkhasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato bhikkhu diṭṭheva dhamme dukkhaṃ viharati savighātaṃ saupāyāsaṃ sapariḷāhaṃ kāyassa ca bhedā paraṃ maraṇā duggati pāṭikaṅkhā. Katamehi pañcahi, idha bhikkhave bhikkhu asaddho hoti ahiriko hoti, anottappī hoti, kusīto hoti, duppañño hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Samāpattisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',142,replace(replace('<p class="bodytext">tattheva <a name="M2.0142"></a> āvuso chaṭṭhaṃ samāpattisuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> chaṭṭhaṃ bhante samāpattisuttaṃ ‘‘na tāva bhikkhave akusalassa samāpatti hoti, yāva saddhā paccupaṭṭhitā hoti kusalesu dhammesu. Yato ca kho bhikkhave saddhā antarahitā hoti asaddhiyaṃ pariyuṭṭhāya tiṭṭhati, atha akusalassa samāpatti hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kāmasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',143,replace(replace('<p class="bodytext">tattheva <a name="M2.0143"></a> āvuso sattamaṃ kāmasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante kāmasuttaṃ ‘‘yebhuyyena bhikkhave sattā kāmesu laḷitā. Asitabyābhaṅgiṃ bhikkhave kulaputto ohāya agārasmā anagāriyaṃ pabbajito hoti, saddhāpabbajito kulaputtoti alaṃ vacanāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Vimuttāyatanasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',146,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0146"></a> pañcakanipāte pañcaṅgikavagge chaṭṭhaṃ vimuttāyatanasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> pañcaṅgikavagge bhante chaṭṭhaṃ vimuttāyatanasuttaṃ ‘‘pañcimāni bhikkhave vimuttāyatanāni, yattha bhikkhuno appamattassa ātāpino pahitattassa viharato avimuttaṃ vā cittaṃ vimuccati, aparikkhīṇā vā āsavā parikkhayaṃ gacchanti, ananuppattaṃ vā anuttaraṃ yogakkhemaṃ anupāpuṇātī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Caṅkamasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',150,replace(replace('<p class="bodytext">tattheva <a name="M2.0150"></a> āvuso navamaṃ caṅkamasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> navamaṃ bhante caṅkamasuttaṃ ‘‘pañcime bhikkhave caṅkame ānisaṃsā. Katame pañca, addhānakkhamo hoti, padhānakkhamo hoti, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',151,replace(replace('<p class="bodytext">appābādho <a name="M2.0151"></a> hoti, asitaṃ pītaṃ khāyitaṃ sāyitaṃ sammā pariṇāmaṃ gacchati, caṅkamādhigato samādhi ciraṭṭhitiko hoti. Ime kho bhikkhave pañca caṅkame ānisaṃsā’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sumanasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',152,replace(replace('<p class="bodytext">sumanavagge <a name="M2.0152"></a> pana āvuso paṭhamaṃ sumanasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sāvatthiyaṃ bhante sumanaṃ rājakumāriṃ ārabbha bhāsitaṃ. Sumanā bhante rājakumārī bhagavantaṃ etadavoca ‘‘idhassu bhante bhagavato dve sāvakā samasaddhā samasīlā samapaññā eko dāyako eko adāyako. Te kāyassa bhedā paraṃ maraṇā sugatiṃ saggaṃ lokaṃ upapajjeyyuṃ. Devabhūtānaṃ pana nesaṃ bhante siyā viseso siyā nānākaraṇa’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘yo so sumane dāyako, so amhaṃ adāyakaṃ devabhūto samāno pañcahi ṭhānehi adhigaṇhāti, dibbena āyunā dibbena vaṇṇena dibbena sukhena dibbena yasena dibbena ādhipateyyena. Yo so sumane dāyako, so amhaṃ adāyakaṃ devabhūto samāno imehi pañcahi ṭhānehi adhigaṇhātī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',156,replace(replace('<p class="gatha1">‘‘Yathāpi <a name="M2.0156"></a> cando vimalo, gacchaṃ ākāsadhātuyā;</p>\r\n<p class="gatha2">Sabbe tārāgaṇe loke, ābhāya atirocati;</p>\r\n<p class="gatha3">Tatheva sīlasampanno, saddho purisapuggalo;</p>\r\n<p class="gathalast">Sabbe maccharino loke, cāgena atirocati’’–</p>\r\n<p class="subhead">Uggahasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> tattheva āvuso tatiyaṃ uggahasuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tatiyaṃ bhante uggahasuttaṃ bhaddiye uggahaṃ meṇḍakanattāraṃ ārabbha bhāsitaṃ. Uggaho bhante meṇḍakanattā bhagavantaṃ etadavoca ‘‘imā me bhante kumāriyo patikulāni gamissanti, ovadatu tāsaṃ bhante bhagavā, anusāsatu tāsaṃ bhante bhagavā, yaṃ tāsaṃ assa dīgharattaṃ hitāya sukhāyā’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘tasmātiha kumāriyo evaṃ sikkhitabbaṃ’’ yassa vo mātāpitaro bhattuno dassanti atthakāmā hitesino anukampakā anukampaṃ upādāya, tassa bhavissāma </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',157,replace(replace('<p class="bodytext">pubbuṭṭhāyino <a name="M2.0157"></a> pacchānipātiniyo kiṃ kārapaṭissāviniyo manāpacāriniyo piyavādiniyo’ti. Evañhi vo kumāriyo sikkhitabba’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sīhasenāpatisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',160,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0160"></a> āvuso pañcakanipāte sumanavagge catutthaṃ sīhasenāpatisuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> vesāliyaṃ bhante sīhaṃ senāpatiṃ ārabbha bhāsitaṃ. Sīho bhante senāpati bhagavantaṃ etadavoca ‘‘sakkā nu kho bhante sandiṭṭhikaṃ dānaphalaṃ paññāpetu’’nti. Tasmiṃ bhante vatthusmiṃ ‘‘sakkā sīha, dāyako sīha dānapati bahuno janassa piyo hoti manāpo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kāladānasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',164,replace(replace('<p class="bodytext">tattheva <a name="M2.0164"></a> āvuso chaṭṭhaṃ kāladānasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',165,replace(replace('<p class="bodytext">chaṭṭhaṃ <a name="M2.0165"></a> bhante kāladānasuttaṃ ‘‘pañcimāni bhikkhave kāladānāni. Katamāni pañca, āgantukassa dānaṃ deti, gamikassa dānaṃ deti, gilānassa dānaṃ deti, dubbhikkhe dānaṃ deti, yāni tāni navasassāni navaphalāni, tāni paṭhamaṃ sīlavantesu patiṭṭhāpeti. Imāni kho bhikkhave pañca kāladānānī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Bhojanasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',166,replace(replace('<p class="bodytext">tattheva <a name="M2.0166"></a> āvuso sattamaṃ bhojanasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante bhojanasuttaṃ ‘‘bhojanaṃ bhikkhave dadamāno dāyako paṭiggāhakānaṃ pañca ṭhānāni deti. Katamāni pañca, āyuṃ deti, vaṇṇaṃ deti, sukhaṃ deti, balaṃ deti, paṭibhānaṃ deti. Āyuṃ kho pana datvā āyussa bhāgī hoti dibbassa vā mānusassa vā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Puttasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',167,replace(replace('<p class="bodytext">tattheva <a name="M2.0167"></a> āvuso navamaṃ puttasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> navamaṃ bhante puttasuttaṃ ‘‘pañcimāni bhikkhave ṭhānāni sampassanto mābhāpitaro puttaṃ icchanti kule jāyamānaṃ. Katamāni pañca,</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',168,replace(replace('<p class="bodytext">Bhato <a name="M2.0168"></a> vā no bharissati, kiccaṃ vā no karissati, kulavaṃso ciraṃ ṭhassati, dāyajjaṃ paṭipajjissatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Nāradasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',169,replace(replace('<p class="bodytext">muṇḍarājavagge <a name="M2.0169"></a> panāvuso dasamaṃ nāradasuttaṃ kattha kaṃ ārabbha kismiṃ vatthusmiṃ kena kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> pāṭaliputte bhante muṇḍaṃ rājānaṃ ārabbha bhāsitaṃ. Muṇḍassa bhante rañño bhaddā devī kālaṅkatā hoti piyā manāpā. So bhaddāya deviyā kālaṅkatāya piyāya manāpāya neva nhāyati na vilimpati na bhattaṃ bhuñjati, na kammantaṃ payojeti, rattindivaṃ bhaddāya deviyā sarīre ajjhomucchito. Tasmiṃ bhante vatthusmiṃ ‘‘pañcimāni mahārāja alabbhanīyāni ṭhānāni samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmi’’nti evamādinā bhante āyasmatā nāradattherena rañño pasenadissa kosalassa bhagavatā desitaniyāmena desitaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',175,replace(replace('<p class="bodytext">Ko <a name="M2.0175"></a> nāmo ayaṃ bhante dhammapariyāyo.</p>\r\n<p class="bodytext">Taggha bhante sokasallaharaṇo.</p>\r\n<p class="subhead">Samayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',176,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0176"></a> āvuso pañcakanipāte nīvaraṇavagge catutthaṃ samayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> nīvaraṇavagge bhante catutthaṃ samayasuttaṃ ‘‘pañcime bhikkhave asamayā padhānāya. Katame pañca, idha bhikkhave bhikkhu jiṇṇo hoti jarāyābhibhūto. Ayaṃ bhikkhave paṭhamo asamayo padhānāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Ṭhānasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',180,replace(replace('<p class="bodytext">tattheva <a name="M2.0180"></a> āvuso sattamaṃ ṭhānasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> sattamaṃ bhante ṭhānasuttaṃ ‘‘pañcimāni bhikkhave ṭhānāni abhiṇhaṃ paccavekkhitabbāni itthiyā vā purisena vā gahaṭṭhena vā pabbajitena vā. Katamāni pañca, jarādhammomhi jaraṃ anatītoti abhiṇhaṃ paccavekkhitabbaṃ itthiyā vā purisena vā gahaṭṭhena vā pabbajitena vā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dhammavihārīsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',185,replace(replace('<p class="bodytext">yodhājīvavagge <a name="M2.0185"></a> panāvuso tatiyaṃ dhammavihārisuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> yodhājīvavagge bhante tatiyaṃ dhammavihārisuttaṃ ‘‘idha bhikkhu bhikkhu dhammaṃ pariyāpuṇāti suttaṃ geyyaṃ veyyākaraṇaṃ gāthaṃ udānaṃ dutivuttakaṃ jātakaṃ abbhutadhammaṃ vedallaṃ, so tāya dhammapariyattiyā divasaṃ atināmeti, riñcati paṭisallānaṃ, nānuyuñjati ajjhattaṃ cetosamathaṃ. Ayaṃ vuccati bhikkhu bhikkhu pariyattibahulo no dhammavihārī’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="bodytext">Dhammavihārī dhammavihārīti bhante vuccati, kittāvatā nu kho bhante bhikkhu dhammavihārī hoti.</p>\r\n<p class="subhead">Dutiyaanāgatabhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',188,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0188"></a> āvuso pañcakanipāte yodhājīvavagge aṭṭhamaṃ dutiyaanāgatabhayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> yodhājīvavagge bhante aṭṭhamaṃ dutiyaanāgatabhayasuttaṃ ‘‘pañcimāni bhikkhave anāgatabhayāni sampassamānena alameva bhikkhunā appamattena ātāpinā pahitattena viharituṃ appattassa pattiyā anadhigatassa adhigamāya asacchikatassa sacchikiriyāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tatiyaanāgatabhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',193,replace(replace('<p class="bodytext">tattheva <a name="M2.0193"></a> āvuso navamaṃ tatiyaanāgatabhayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante navamaṃ katiyaanāgatabhayasuttaṃ ‘‘pañcimāni bhikkhave anāgatabhayāni etarahi asamuppannāni āyatiṃ samuppajjissanti, tāni vo paṭibujjhitabbāni, paṭibujjhitvā ca tesaṃ pahānāya vāyamitabbaṃ. Katamāni pañca, bhavissanti bhikkhave bhikkhū anāgatamaddhānaṃ abhāvitakāyā abhāvitasīlā abhāvitacittā abhāvitapaññā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Catutthaanāgatabhayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',199,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0199"></a> āvuso pañcakanipāte yodhājīvavagge dasamaṃ catutthaanāgatabhayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> yodhājīvavagge bhante dasamaṃ catutthaanāgatabhayasuttaṃ ‘‘pañcimāni bhikkhave anāgatabhayāni etarahi asamuppannāni āyatiṃ samuppajjissanti, tāni vo paṭibujjhitabbāni, paṭibujjhitvā ca tesaṃ pahānāya vāyamitabba’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kakudhavagga</p>\r\n<p class="subhead">Sīhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',202,replace(replace('<p class="bodytext">kakudhavagge <a name="M2.0202"></a> āvuso navamaṃ sīhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> kakudhavagge bhante navamaṃ sīhasuttaṃ ‘‘sīho bhikkhave migarājā sāyanhasamayaṃ āsayā nikkhamati, āsayā nikkhamitvā vijambhati, vijambhitvā samantā catuddisaṃ anuviloketi, samantā catuddisaṃ anuviloketvā tikkhattuṃ sīhanādaṃ nadatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Andhakavindavagga</p>\r\n<p class="subhead">Kulūpakasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',204,replace(replace('<p class="bodytext">andhakavindavagge <a name="M2.0204"></a> pana āvuso ‘‘paṭhamaṃ kulūpakasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> andhakavindavagge bhante paṭhamaṃ kulūpakasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato kulūpako bhikkhu kulesu appiyo ca hoti amanāpo ca agaru ca abhāvanīyo cā’’ti evamādinā bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Andhakavindasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',206,replace(replace('<p class="bodytext">tattheva <a name="M2.0206"></a> āvuso catutthaṃ andhakavindasuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> magadhesu bhante andhakavinde āyasmantaṃ ānandaṃ ārabbha ‘‘ye te ānanda bhikkhū navā acirapabbajitā adhunāgatā imaṃ dhammavinayaṃ, te vo ānanda bhikkhū pañcasu dhammesu samādapetabbā nivesetabbā patiṭṭhāpetabbā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Gilānavagga</p>\r\n<p class="subhead">Satisūpaṭṭhitasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',208,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0208"></a> āvuso pañcakanipāte gilānavagge dutiyaṃ satisūpaṭṭhitasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> gilānavagge bhante dutiyaṃ satisūpaṭṭhitasuttaṃ ‘‘yo hi koci bhikkhave bhikkhu vā bhikkhunī vā pañca dhamme bhāveti, pañca dhamme bahulīkaroti, tassa dvinnaṃ phalānaṃ aññataraṃ phalaṃ pāṭikaṅkhaṃ ‘diṭṭheva dhamme aññā, sati vā upādisese anāgāmitā’. Katame pañca, idha bhikkhave bhikkhuno ajjhattaññeva satisūpaṭṭhitā hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Upaṭṭhākasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',209,replace(replace('<p class="bodytext">tattheva <a name="M2.0209"></a> āvuso tatiyaṃ upaṭṭhākasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',210,replace(replace('<p class="bodytext">tatiyaṃ <a name="M2.0210"></a> bhante upaṭṭhākasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato gilāno dūpaṭṭhāko hoti. Katamehi pañcahi, asappāyakārī hoti, sappāye mattaṃ na jānāti, bhesajjaṃ nappaṭisevitā hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Anāyussāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',212,replace(replace('<p class="bodytext">tattheva <a name="M2.0212"></a> āvuso pañcamaṃ anāyussāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> pañcamaṃ bhante anāyussāsuttaṃ ‘‘pañcime bhikkhave dhammā anāyussā. Katame pañca, asappāyakārī hoti, sappāye mattaṃ na jānāti, apariṇatabhojī ca hoti, akālacārī ca hoti, abrahmacārī ca. Ime kho bhikkhave pañca dhammā anāyussā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Samaṇasukhasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',213,replace(replace('<p class="bodytext">tattheva <a name="M2.0213"></a> āvuso aṭṭhamaṃ samaṇasukhasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> aṭṭhamaṃ bhante samaṇasukhasuttaṃ ‘‘pañcimāni bhikkhave samaṇadukkhāni. Katamāni pañca, idha bhikkhave bhikkhu asantuṭṭho hoti itarītarena cīvarena, asantuṭṭho hoti itarītarena piṇḍapātena, asantuṭṭho hoti itarītarena senāsanena, asantuṭṭho hoti itarītarena gilānapaccayabhesajjaparikkhārena, anabhirato ca brahmacariyaṃ carati. Imāni kho bhikkhave pañca samaṇadukkhānī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Byasanasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',215,replace(replace('<p class="bodytext">tattheva <a name="M2.0215"></a> āvuso dasamaṃ byasanasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> dasamaṃ bhante byasanasuttaṃ ‘‘pañcimāni bhikkhave byasanāni. Katamāni pañca, ñātibyasanaṃ bhogabyasanaṃ rogabyasanaṃ sīlabyasanaṃ diṭṭhibyasana’’nti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Rājavagga</p>\r\n<p class="subhead">Patthanāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',217,replace(replace('<p class="bodytext">rājavagge <a name="M2.0217"></a> pana āvuso chaṭṭhaṃ patthanāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> rājavagge bhante chaṭṭhaṃ patthanāsuttaṃ ‘‘pañcahi bhikkhave aṅgehi samannāgato rañño khattiyassa muddhāvasittassa jeṭṭho putto oparajjaṃ patthetī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sappurisadānasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',221,replace(replace('<p class="bodytext">tikaṇḍakīvagge <a name="M2.0221"></a> āvuso aṭṭhamaṃ sappurisadānasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tikaṇḍakīvagge bhante aṭṭhamaṃ sappurisadānasuttaṃ ‘‘pañcimāni bhikkhave sappurisadānāni. Katamāni pañca, saddhāya dānaṃ deti, sakkaccaṃ dānaṃ deti, kālena dānaṃ deti, anuggahitacitto dānaṃ deti, attānañca parañca anupahacca dānaṃ detī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Saddhammavagga</p>\r\n<p class="subhead">Paṭhamasammattaniyāmasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',224,replace(replace('<p class="bodytext">saddhammavagge <a name="M2.0224"></a> pana āvuso paṭhamaṃ sammattaniyāmasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> saddhammavagge bhante paṭhamaṃ sammattaniyāmasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato sunakkhopi saddhammaṃ abhabbo niyāmaṃ okkamituṃ kusalesu dhammesu sammattaṃ. Katamehi pañcahi, kathaṃ paribhoti, kathikaṃ paribhoti, attānaṃ paribhotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Paṭhamasaddhammasammosasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',226,replace(replace('<p class="bodytext">tattheva <a name="M2.0226"></a> āvuso catutthaṃ paṭhamasaddhammasammosasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante catutthaṃ paṭhamasaddhammasammosasuttaṃ ‘‘pañcime bhikkhave dhammā saddhammassa sammosāya antaradhānāya saṃvattanti. Katame pañca, idha bhikkhave bhikkhū na sakkaccaṃ dhammaṃ suṇanti, na sakkaccaṃ dhammaṃ pariyāpuṇanti, na sakkaccaṃ dhammaṃ dhārenti, na sakkaccaṃ dhātānaṃ dhammānaṃ atthaṃ upaparikkhanti, na sakkaccaṃ atthamaññāya dhammamaññāya dhammānudhammaṃ paṭipajjanti. Ime kho bhikkhave pañca dhammā saddhammassa sammosāya antaradhānāya saṃvattantī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dutiyasaddhammasammosasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',228,replace(replace('<p class="bodytext">tattheva <a name="M2.0228"></a> āvuso pañcamaṃ dutiyasaddhammasammosasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante pañcamaṃ dutiyasaddhammasammosasuttaṃ ‘‘pañcime bhikkhave dhammā saddhammassa sammosāya antaradhānāya saṃvattanti. Katame pañca, idha bhikkhave bhikkhū dhammaṃ na pariyāpuṇanti suttaṃ geyyaṃ veyyākaraṇaṃ gāthaṃ udānaṃ itivuttakaṃ jātakaṃ abbhutadhammaṃ vedallaṃ. Ayaṃ bhikkhave paṭhamo dhammo saddhammassa sammosāya antaradhānāya saṃvattatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Tatiyasaddhammasammosasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',231,replace(replace('<p class="bodytext">tattheva <a name="M2.0231"></a> āvuso chaṭṭhaṃ tatiyasaddhammasammosasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante chaṭṭhaṃ tatiyasaddhammasammosasuttaṃ ‘‘pañcime bhikkhave dhammā saddhammassa sammosāya antaradhānāya saṃvattanti. Katame pañca, idha bhikkhave bhikkhū duggahitaṃ suttantaṃ pariyāpuṇanti dunnikkhittahi padabyañjanehi. Dunnikkhittassa bhikkhave padabyañjanassa atthopi dunnayo hoti. Ayaṃ bhikkhave paṭhamo dhammo saddhammassa sammosāya antaradhānāya saṃvattatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Dukkatāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',237,replace(replace('<p class="bodytext">aṅguttaranikāye <a name="M2.0237"></a> āvuso pañcakanipāte saddhammavagge sattamaṃ dukkathāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> saddhammavagge bhante sattamaṃ dukkathāsuttaṃ ‘‘pañcannaṃ bhikkhave puggalānaṃ kathā dukkathā puggale puggalaṃ upanidhāya. Katamesaṃ pañcannaṃ, assaddhassa bhikkhave saddhākathā dukkathā, dussīlassa sīlakathā dukkathā, appassutassa bāhusaccakathā dukkathā, maccharissa cāgakathā dukkathā, duppaññassa paññākathā dukkathā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Udāyīsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',240,replace(replace('<p class="bodytext">tattheva <a name="M2.0240"></a> āvuso navamaṃ udāyisuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> kosambiyaṃ bhante āyasmantaṃ udāyiṃ ārabbha bhāsitaṃ. Āyasmā bhante udāyī mahatiyā gīhiparisāya </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',241,replace(replace('<p class="bodytext">parivuto <a name="M2.0241"></a> dhammaṃ desento nisinno hoti. Addasā kho āyasmā ānando āyasmantaṃ udāyiṃ mahatiyā gihiparisāya parivutaṃ dhammaṃ desentaṃ nisinnaṃ. Disvā yena bhagavā tenupasaṅkami, upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi. Ekamantaṃ nisinno kho āyasmā ānando bhagavantaṃ etadavoca ‘‘āyasmā bhante udāyī mahatiyā gihiparisāya parivuto dhammaṃ desetī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘na kho ānanda sukaraṃ paresaṃ dhammaṃ desetuṃ, paresaṃ ānanda dhammaṃ desentena pañca dhamme ajjhattaṃ upaṭṭhāpetvā paresaṃ dhammo desetabbo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āghātavagga</p>\r\n<p class="subhead">Paṭhamaāghātapaṭivinayasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',243,replace(replace('<p class="bodytext">āghātavagge <a name="M2.0243"></a> pana āvuso paṭhamaṃ āghātapaṭivinayasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> āghātavagge bhante paṭhamaṃ āghātapaṭivinayasuttaṃ ‘‘pañcime bhikkhave āghātapaṭivinayā, yattha bhikkhuno uppanno āghāto sabbaso paṭivinetabbo. Katame pañca, yasmiṃ bhikkhave puggale āghāto jāyetha, mettā tasmiṃ puggale bhāvetabbā, evaṃ tasmiṃ puggale āghāto paṭivinetabbo’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Upāsakavagga</p>\r\n<p class="subhead">Caṇḍālasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',245,replace(replace('<p class="bodytext">upāsakavagge <a name="M2.0245"></a> pana āvuso pañcamaṃ caṇḍālasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> upāsakavagge bhante pañcamaṃ caṇḍālasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato upāsako upāsakacaṇḍālo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',246,replace(replace('<p class="bodytext">ca <a name="M2.0246"></a> hoti upāsakamalañca upāsakapatikuṭṭho cā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Pītisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',247,replace(replace('<p class="bodytext">tattheva <a name="M2.0247"></a> āvuso chaṭṭhaṃ pītisuttaṃ bhagavatā kattha kaṃ ārabbha kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> chaṭṭhaṃ bhante pītisuttaṃ sāvatthiyaṃ anāthapiṇḍikaṃ gahapatiṃ ārabbha ‘‘tumhe kho gahapati bhikkhusaṅghaṃ paccupaṭṭhitā cīvara piṇḍapāta senāsana </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',248,replace(replace('<p class="bodytext">gilānapaccayabhesajjaparikkhārena<a name="M2.0248"></a>. Na kho gahapati tāvatakeneva tuṭṭhi karaṇīyā ‘‘mayaṃ bhikkhusaṅghaṃ paccupaṭṭhitā cīvarapiṇḍapātasenāsanagilāna paccaya bhesajjaparikkhārenā’’ti. Tasmātiha gahapati evaṃ sikkhitabbaṃ ‘‘kindi mayaṃ kālena kālaṃ pavivekaṃ pītiṃ upasampajja vihareyyāmā’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Vaṇijjāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',251,replace(replace('<p class="bodytext">tattheva <a name="M2.0251"></a> āvuso sattamaṃ vaṇijjāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante sattamaṃ vaṇijjāsuttaṃ ‘‘pañcimā bhikkhave vaṇijjā upāsakena akaraṇīyā. Katamā pañca, satthavaṇijjā sattavaṇijjā maṃsavaṇijjā majjavaṇijjā visavaṇijjā. Imā kho bhikkhave pañca vaṇijjā upāsakena akaraṇīyā’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Gavesīsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',252,replace(replace('<p class="bodytext">tattheva <a name="M2.0252"></a> āvuso dasamaṃ gavesīsuttaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ kathañca bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> dasamaṃ bhante gavesīsuttaṃ kosalesu āyasmantaṃ ānandattheraṃ dhammabhaṇḍāgārikaṃ ārabbha bhāsitaṃ. Āyasmā bhante ānandatthero dhammabhaṇḍāgāriko bhagavantaṃ etadavoca ‘‘ko nu kho bhante hetu ko paccayo bhagavato sitassa pātukammāya, na akāraṇena tathāgatā sitaṃ pātukarontī’’ti. Tasmiṃ bhante vatthusmiṃ ‘‘bhūtapubbaṃ ānanda imasmiṃ padese nagaraṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',253,replace(replace('<p class="bodytext">ahosi <a name="M2.0253"></a> iddhañceva phītañca bahujanaṃ ākiṇṇamanussaṃ, taṃ kho panānanda nagaraṃ kassapo bhagavā arahaṃ sammāsambuddho upanissāya vihāsī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Vācāsutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',260,replace(replace('<p class="bodytext">brāhmaṇavagge <a name="M2.0260"></a> āvuso aṭṭhamaṃ vācāsuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> brāhmaṇavagge bhante aṭṭhamaṃ vācāsuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgatā vācā subhāsitā hoti no dubbhāsitā, anavajjā ca ananuvajjā ca viññūnaṃ. Katamehi pañcahi, kālena bhāsitā hoti, saccā ca bhāsitā hoti, saṇhā ca bhāsitā hoti, atthasaṃhitā ca bhāsitā hoti, mettācittena ca bhāsitā hoti, imehi kho bhikkhave pañcahi aṅgehi samannāgatā vācā subhāsitā hoti no dubbhāsitā, anavajjā ca ananuvajjā ca viññūna’’nti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kulasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',261,replace(replace('<p class="bodytext">tattheva <a name="M2.0261"></a> āvuso navamaṃ kulasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante navamaṃ kulasuttaṃ ‘‘yaṃ bhikkhave sīlavanto pabbajitā kulaṃ upasaṅkamanti, tattha manussā pañcahi ṭhānehi bahuṃ puññaṃ pasavanti. Katamehi pañcahi, yasmiṃ bhikkhave samaye sīlavante pabbajite kulaṃ upasaṅkamante manussā disvā cittāni pasādenti, saggasaṃvattanikaṃ bhikkhave taṃ kulaṃ tasmiṃ samaye paṭipadaṃ paṭipannaṃ hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Kimilavagga</p>\r\n<p class="subhead">Dhammassavanasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',263,replace(replace('<p class="bodytext">kimilavagge <a name="M2.0263"></a> pana āvuso dutiyaṃ dhammassavanasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> kimilavagge bhante dutiyaṃ dhammassavanasuttaṃ ‘‘pañcime bhikkhave ānisaṃsā dhammassavane. Katame pañca, assutaṃ suṇāti, sutaṃ pariyodāpeti, kaṅkhaṃ vitarati, diṭṭhiṃ ujuṃ karoti, cittamassa pasīdati, ime kho bhikkhave pañca ānisaṃsā dhammassavane’’ti evaṃ kho bhante bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Akkosakavagga</p>\r\n<p class="subhead">Akkhantisutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',264,replace(replace('<p class="bodytext">akkosakavagge <a name="M2.0264"></a> pana āvuso pañcamaṃ akkhantisuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> akkosakavagge bhante pañcamaṃ akkhantisuttaṃ ‘‘pañcime bhikkhave ādīnavā akkhantiyā. Katame pañca, bahuno </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',265,replace(replace('<p class="bodytext">janassa <a name="M2.0265"></a> appiyo hoti amanāpo, verabahulo ca hoti, vajjabahulo ca, sammūḷho kālaṃ karoti, kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjatī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Apāsādikasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',266,replace(replace('<p class="bodytext">tattheva <a name="M2.0266"></a> āvuso aṭṭhamaṃ apāsādikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante aṭṭhamaṃ apāsādikasuttaṃ ‘‘pañcime bhikkhave ādīnavā apāsādike. Katame pañca, appasannā nappasīdanti, pasannānañca ekaccānaṃ aññathattaṃ hoti, satthusāsanaṃ akataṃ hoti, pacchimā janatā diṭṭhānugatiṃ āpajjati, cittamassa nappasīdati. Ime kho bhikkhave pañca ādīnavā apāsādike’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Āvāsikavagga</p>\r\n<p class="subhead">Āvāsikasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',268,replace(replace('<p class="bodytext">āvāsikavagge <a name="M2.0268"></a> panāvuso paṭhamaṃ āvāsikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> āvāsikavagge bhante paṭhamaṃ āvāsikasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato āvāsiko bhikkhu abhāvanīyo hoti. Katamehi pañcahi, na ākappasampanno hoti na vattasampanno, na bahussuto hoti na sutadharo, na paṭisallekhitā hoti na paṭisallānārāmo, na kalyāṇavāco hoti na kalyāṇavākkaraṇo, duppañño hoti jaḷo eḷamūgo. Imehi kho bhikkhave pañcahi dhammehi samannāgato āvāsiko bhikkhu abhāvanīyo hotī’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Avaṇṇārahasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',270,replace(replace('<p class="bodytext">tattheva <a name="M2.0270"></a> āvuso sattamaṃ avaṇṇārahasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> tattheva bhante sattamaṃ avaṇṇārahasuttaṃ ‘‘pañcahi bhikkhave dhammehi samannāgato āvāsiko bhikkhu yathābhataṃ nikkhitto evaṃ niraye. Katamehi pañcahi, ananuvicca apariyogāhetvā avaṇṇārahassa vaṇṇaṃ bhāsati, ananuvicca apariyogāhetvā vaṇṇārahassa avaṇṇaṃ bhāsati, āvāsamaccharī hoti āvāsapaligedhī, kulamaccharī hoti kulapaligedhi, saddhādeyyaṃ vinipāteti. Imehi kho bhikkhave pañcahi dhammehi samannāgato </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',271,replace(replace('<p class="bodytext">āvāsiko <a name="M2.0271"></a> bhikkhu yathābhataṃ nikkhitto evaṃ niraye’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Duccaritavagga</p>\r\n<p class="subhead">Paṭhamaduccaritasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',273,replace(replace('<p class="bodytext">duccaritavagge <a name="M2.0273"></a> pana āvuso paṭhamaṃ duccaritasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> duccaritavagge bhante paṭhamaṃ duccaritasuttaṃ ‘‘pañcime bhikkhave ādīnavā duccarite. Katame pañca, attāpi attānaṃ upavadati, anuvicca viññū garahanti, pāpako kittisaddo abbhuggacchati, sammūḷho kālaṃ karoti, kāyassa bhedā paraṃ maraṇā apāyaṃ duggatiṃ vinipātaṃ nirayaṃ upapajjati. Ime kho bhikkhave pañca ādīnavā duccarite’’ti evamādinā bhagavatā bhāsitaṃ.</p>\r\n<p class="subhead">Sivathikasutta</p>\r\n<p class="bodytext"><span class="bld">Pu –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',274,replace(replace('<p class="bodytext">tattheva <a name="M2.0274"></a> āvuso navamaṃ sivathikasuttaṃ bhagavatā kathaṃ bhāsitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vi –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_60',275,replace(replace('<p class="bodytext">tattheva <a name="M2.0275"></a> bhante navamaṃ sivathikasuttaṃ ‘‘pañcime bhikkhave ādīnavā sivathikāya. Katame pañca, asuci, duggandhā, sappaṭibhayā, vāḷānaṃ amanussānaṃ āvāso, bahuno janassa ārodanā. Ime kho bhikkhave pañca ādīnavā sivathikāyā’’ti evamādinā bhagavatā bhāsitaṃ.</p>','\r',char(13)),'\n',char(10)),'');