INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Vinayapiṭaka</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="subhead">Okāsa dānakathā</p>\r\n<p class="bodytext">Suṇātu <a name="M1.0001"></a> me bhante saṅgho, mahatī ayaṃ parisā, evarūpāya parisāya na sukarā sabbaso kathaṃ sāvetuṃ, tasmā āyasmato ca javanattherassa pakhukkūnagaravāsino āyasmato ca vicittasārābhivaṃsassa tipiṭakadhara dhammabhaṇḍāgārikassa vinayapucchanabhārañca vinayavissajjana bhārañca āvahituṃ okāsaṃ dammi, karotha tumhe āvuso chaṭṭhasaṃgītipubbaṅgamāni vinayapucchana vinayavissajjana kiccāni yathādhammaṃ yathāvinayaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',2,replace(replace('<p class="centered">Aggamahāpaṇḍita <a name="M1.0002"></a> bhadantajavana mahātherena</p>\r\n<p class="bodytext">Ṭhapitā</p>\r\n<p class="centered">Pucchaka sammuti ñātti</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho, yadi saṅghassa pattakallaṃ, ahaṃ āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ vinayaṃ puccheyyaṃ.</p>\r\n<p class="centered">Tipiṭakadhara dhammabhaṇḍāgārika</p>\r\n<p class="bodytext">Bhadantavicittasārābhivaṃsena</p>\r\n<p class="centered">Ṭhapitā vissajjaka sammuti ñātti</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho, yadi saṅghassa pattakallaṃ, ahaṃ āyasmatā javanattherena saṅghanāyaka mahātherassa paṭinidhibhūtena vinayaṃ puṭṭho vissajjeyyaṃ.</p>\r\n<p class="subhead">Paṭhama pārājika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',3,replace(replace('<p class="bodytext">yaṃ <a name="M1.0003"></a> tena āvuso bhagavatā jānatā passatā arahatā sammāsambuddhena paṭhamaṃ pārājikaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sudinnaṃ bhante kalandaputtaṃ ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sudindo bhante kalandaputto purāṇadutiyikāya methunaṃ dhammaṃ paṭisevi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi āvuso tattha paññatti anupaññatti anuppannapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekā bhante paññatti dve anupaññattiyo, anuppannapaññatti tasmiṃ natthi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',4,replace(replace('<p class="bodytext">sabbatthapaññattinukho <a name="M1.0004"></a> āvuso padesapaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbatthapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhāraṇapaññattinukho āvuso asādhāraṇapaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sādhāraṇapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ekatopaññattinukho āvuso ubhatopaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ubhatopaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pañcannaṃ āvuso pātimokkhuddesānaṃ katthogadhaṃ kattha pariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',5,replace(replace('<p class="bodytext">nidānogadhaṃ <a name="M1.0005"></a> bhante nidānapariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katamena āvuso uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiyena bhante uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sīlavipatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sammatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',6,replace(replace('<p class="bodytext">kā <a name="M1.0006"></a> āvuso paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na evarūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu, ayaṃ bhante paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kati āvuso atthavase paṭicca bhagavatā paṭhamaṃ pārājikaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante atthavase paṭicca bhagavatā paṭhamaṃ pārājikaṃ paññattaṃ, 1 - saṅghasuṭṭhutāya, 2 - saṅghaphāsutāya, 3 - dummaṅkūnaṃ puggalānaṃ niggahāya, 4 - pesalānaṃ bhikkhūnaṃ phāsuvihārāya, 5 - diṭṭhadhammikānaṃ āsavānaṃ saṃvarāya, 6 - samparāyikānaṃ āsavānaṃ paṭighātāya, 7 - appasannānaṃ pasādāya, 8 - pasannānaṃ bhiyyobhāvāya, 9 - saddhammaṭṭhitiyā, 10 - vinayānuggahāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',7,replace(replace('<p class="bodytext">ke <a name="M1.0007"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhāca bhante puthujjanakalyāṇakāca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',8,replace(replace('<p class="bodytext">kassa <a name="M1.0008"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="centered">Saṃgāyanatthāya saṅghassa ñāpanaṃ</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho ahaṃ bhante āyasmantaṃ vicitthasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ paṭhamassa pārājikassa nidānampi pucchiṃ, puggalampi pucchiṃ, vatthumpi pucchiṃ, paññatti anupaññattiādīnipi pucchiṃ, puṭṭho puṭṭhoca so āyasmāvicittasārābhivaṃso vissajjesi, itihidaṃ bhante paṭhamaṃ pārājika sikkhāpadaṃ nimmalaṃ suparisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsambuddhassa, tasmā yathāpure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariya saṅkhātassa buddhasāsanassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ dhammavinayaṃ saṅgāyiṃsuceva anusaṅgāyiṃsuca, evameva mayampi dāni sabbeva chaṭṭhasaṃgītimahādhammasabhāpariyāpannā idaṃ paṭhama pārājika sikkhāpadaṃ ekatosajjhāyanavasena saṃgāyeyyāma.</p>\r\n<p class="centered">Revatābhi paṇḍitadhaja sāsanavaṃsa mahādhammarājaguruno</p>\r\n<p class="centered">Abhidhajamahāraṭṭhagurunoca saṅghanāyaka mahātherassa</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',10,replace(replace('<p class="subhead">Okāsa dānakathā</p>\r\n<p class="bodytext">Suṇātu <a name="M1.0010"></a> me bhante saṅgho, mahatī ayaṃ parisā, evarūpāya parisāya na sukarā sabbaso kathaṃ sāvetuṃ, tasmā āyasmatoca sobhanassa sāsanari sārāmādhivāsino āyasmatoca vicittasārābhivaṃsassa tipiṭakadhara dhammabhaṇḍāgārikassa vinayapucchanabhārañca vinayavissajjanabhārañca āvahituṃ okāsaṃ dammi, karotha tumhe āvuso chaṭṭhasaṃgītipubbaṅgamāni vinayapucchana vinayavissajjana kiccāni yathādhammaṃ yathāvinayaṃ.</p>\r\n<p class="centered">Aggamahāpaṇḍita bhadanta sobhanattherena</p>\r\n<p class="centered">Ṭhapitā pucchaka sammuti ñātti</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho, yadi saṅghassa pattakallaṃ, ahaṃ āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ vinayaṃ puccheyyaṃ.</p>\r\n<p class="centered">Tipiṭakadhara dhammabhaṇḍāgārika</p>\r\n<p class="bodytext">Bhadantavicittasārābhivaṃsena</p>\r\n<p class="centered">Ṭhapitā vissajjaka sammuti ñātti</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',11,replace(replace('<p class="bodytext">Suṇātu <a name="M1.0011"></a> me bhante saṅgho, yadisaṅghassa pattakallaṃ, ahaṃ āyasmatā sobhanattherena saṅghanāyaka mahātherassa paṭinidhibhūtena vinayaṃ puṭṭho vissajjeyyaṃ.</p>\r\n<p class="subhead">Dutiya pārājika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',12,replace(replace('<p class="bodytext">yaṃ <a name="M1.0012"></a> tena āvuso bhagavatā jānatā passatā arahatā sammāsambuddhena dutiyaṃ pārājikaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dhaniyaṃ bhante kumbhakāraputtaṃ ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dhaniyo bhante kumbhakāraputto rañño dārūni adinnaṃ ādiyi, tasmiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',13,replace(replace('<p class="bodytext">atthi <a name="M1.0013"></a> āvuso tattha paññatti anupaññatti anuppannapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekā bhante paññatti ekā anupaññatti anuppannapaññatti tasmiṃ natthi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yo pana bhikkhu adinnaṃ theyyasaṅkhātaṃ ādiyeyya, yathārūpe adinnādāne rājāno coraṃ gahetvā haneyyuṃvā bandheyyuṃvā pabbājeyyuṃvā corosi bālosi muḷhosi thenosīti tathārūpaṃ bhikkhu adinnaṃ ādiyamāno ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',14,replace(replace('<p class="bodytext">kā <a name="M1.0014"></a> āvuso tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> gāmāvā araññāvāti ayaṃ bhante tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yo pana bhikkhu gāmāvā araññāvā adinnaṃ theyyasaṅkhātaṃ ādiyeyya, kathārūpe adinnādāne rājāno coraṃ gahetvā haneyyuṃvā bandheyyuṃvā pabbājeyyuṃvā corosi bālosi mūḷhosi thenosīti tathārūpaṃ bhikkhu adinnaṃ ādiyamāno ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',15,replace(replace('<p class="bodytext">sabbatthapaññatti <a name="M1.0015"></a> nukho āvuso padesapaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbatthapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhāraṇapaññattinukho āvuso asādhāraṇapaññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sādhāraṇapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ekatopaññattinukho āvuso ubhatopaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ubhatopaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',16,replace(replace('<p class="bodytext">tasmiṃ <a name="M1.0016"></a> āvuso dutiya pārājike adinnaṃ ādiyanto kati āpattiyo āpajjati vibhajitvā vissajjehi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> adinnaṃ bhante ādiyanto tisso āpattiyo āpajjati, pañca māsakaṃvā atireka pañca māsakaṃvā agghanakaṃ adinnaṃ theyyasaṅkhātaṃ ādiyati āpatti pārājikassa, ūnapañcamāsakaṃvā atirekamāsakaṃvā adinnaṃ theyyasaṅkhātaṃ ādiyati āpatti thullaccayassa, māsakaṃvā ūnamāsakavā agghanakaṃ adinnaṃ theyyasaṅkhātaṃ ādiyati āpatti dukkaṭassa, adinnaṃ bhante ādiyanto imā tisso āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',17,replace(replace('<p class="bodytext">kesaṃ <a name="M1.0017"></a> āvuso anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasannaṃ bhante puggalānaṃ anāpatti, sakasaññissa, vissāsaggāhena gaṇhantassa, tāvakālikaṃ gaṇhantassa, petapariggahaṃ gaṇhantassa, tiracchānagatapariggahaṃ gaṇhantassa, paṃsukūlasaññissa, ummattakassa, khittacittassa, vedanāṭṭassa, ādikammikassāti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pañcannaṃ āvuso pātimokkhuddesānaṃ katthogadhaṃ katthapariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nidānogadhaṃ bhante nidānapariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',18,replace(replace('<p class="bodytext">katamena <a name="M1.0018"></a> āvuso uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiyena bhante uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sīlavipatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sattannaṃ āvuso āpattikkhandhānaṃ katamo āpattikkhandho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pārājikāpattikkhandho bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> channaṃ āvuso āpattisamuṭṭhānānaṃ katihi samuṭṭhānehi samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīhi bhante samuṭṭhānehi samuṭṭhāti, siyā kāyatoca cittatoca samuṭṭhāti, na vācāto, siyā vācātoca</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',19,replace(replace('<p class="bodytext">Cittatoca <a name="M1.0019"></a> samuṭṭhāti, na kāyato, siyā kāyatoca vācātoca cittatoca samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso adhikaraṇānaṃ katamaṃ adhikaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āpattādhikaraṇaṃ bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sattannaṃ āvuso samathānaṃ katihi samathehi sammati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dvihi bhante samathehi sammati sammukhāvinayenaca paṭiññāta karaṇenaca.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',20,replace(replace('<p class="bodytext">ko <a name="M1.0020"></a> āvuso tattha vinayo, ko tattha abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante vinayo vibhatti abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kiṃāvuso tattha pātimokkhaṃ kiṃ tattha adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante pātimokkhaṃ, vibhatti adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',21,replace(replace('<p class="bodytext">kā <a name="M1.0021"></a> āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sampatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na evarūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu ayaṃ bhante tattha paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kati āvuso atthavase paṭicca bhagavatā dutiyaṃ pārājikaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante atthavase paṭicca bhagavatā dutiyaṃ pārājikaṃ paññattaṃ. Saṅghasuṭṭhutāya saṅghaphāsutāya dummaṅkūnaṃ puggalānaṃ niggahāya pesalānaṃ bhikkhūnaṃ phāsuvihārāya diṭṭhadhammikānaṃ āsavānaṃ saṃvarāya samparāyikānaṃ āsavānaṃ paṭighātāya appasannānaṃ pasādāya pasannānaṃ bhiyyobhāvāya saddhammaṭṭhitiyā vinayānuggahāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',22,replace(replace('<p class="bodytext">ko <a name="M1.0022"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhāca bhante puthujjanakalyāṇakāca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',23,replace(replace('<p class="bodytext">kattha <a name="M1.0023"></a> āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavatā bhante vacanaṃ arahato sammāsammuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',24,replace(replace('<p class="bodytext">kenāvuso <a name="M1.0024"></a> ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nu kho āvuso tattha upanetabbaṃ vā apanetabbaṃ vā akkharapadapaccābhaṭṭhaṃ vā viraddhapadabyañjanaṃ vā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="centered"><span class="bld">‘‘Saṃgāyanatthāya saṅghassa ñāpanaṃ’’</span></p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',25,replace(replace('<p class="bodytext">Suṇātu <a name="M1.0025"></a> me bhante saṅgho ahaṃ bhante āyasmantaṃ vicittasārābhivaṃsa tipiṭakadharadhammabhaṇḍāgārikaṃ dutiyassa pārājikassa nidānampi pucchiṃ, puggalampi pucchiṃ, vatthumpi pucchiṃ, paññattimpi pucchiṃ, anupaññattimpi pucchiṃ, āpattimpi pucchiṃ, anāpattimpi pucchiṃ, aññānipi īdisāni pucchitabbaṭṭhānāni anekāni pucchiṃ, puṭṭho puṭṭhoca so āyasmā vicittasārābhivaṃso vissajjesi. Iti hidaṃ bhante idaṃ dutiyaṃ pārājika sikkhāpadaṃ nimmalaṃ, suparisuddhaṃ, tasseva bhagavato vacanaṃ arahato sammāsambuddhassa, tasmā yathā pure mahākassa pādayo mahātheravarā porāṇasaṃgītikārā brahmacariyasaṅkhātassa buddhasāsanassa ciraṭṭhitiyā, bahujanahitāya, bahujanasukhāya lokānukampāya atthāya, hitāya, sukhāya, devamanussānaṃ dhammavinayaṃ saṅgāyiṃsuceva anusaṅgāyiṃsuca, evameva mayampi dāni sabbeva chaṭṭhasaṃgītimahādhammasabhāpariyāpannā idaṃ dutiya pārājika sikkhāpadaṃ ekato gaṇasajjhāyaṃ katvā samaggā sammodamānā hutvā saṅgāyeyyāma.</p>\r\n<p class="subhead">Tatiya pārājika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',27,replace(replace('<p class="bodytext">yaṃ <a name="M1.0027"></a> tena āvuso bhagavatā (pa) sammāsambuddhena tatiyaṃ pārājikaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahulā bhante bhikkhu aññamaññaṃ jīvitā voropesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',28,replace(replace('<p class="bodytext">atthi <a name="M1.0028"></a> nukho āvuso tattha paññatti anupaññatti anuppannapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekā bhante paññatti, ekā anupaññatti, anuppannapaññatti tasmiṃ natthi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yopana bhikkhu sañcicca manussaviggahaṃ jīvitā voropeyya, satthahārakaṃvāssa pariyeseyya ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> maraṇavaṇṇaṃ vā saṃvaṇṇeyya maraṇāya vā samādapeyya ambhopurisa kiṃ tuyhiminā pāpakena dujjīvitena matante jīvitā</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',29,replace(replace('<p class="bodytext">Seyyāti <a name="M1.0029"></a> iti cittamano cittasaṅkappo anekapariyāyena maraṇavaṇṇaṃ vā saṃvaṇṇeyya maraṇāya vā samādapeyyāti ayaṃ bhante tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yo pana bhikkhu sañcicca manussaviggahaṃ jīvitā voropeyya, satthahārakaṃvāssa pariyeseyya, maraṇavaṇṇaṃ vā saṃvaṇṇeyya, maraṇāya vā samādapeyya, ambho purisa kiṃ tuyhiminā pāpakena dujjīvitena matante jīvitā seyyoti iti cittamano cittasaṅkappo anekapariyāyena maraṇavaṇṇaṃvā saṃvaṇṇeyya, maraṇāya vā samādapeyya, ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',30,replace(replace('<p class="bodytext">sabbatthapaññatti <a name="M1.0030"></a> nukho āvusopadesapaññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbatthapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhāraṇapaññattinukho āvuso asādhāraṇapaññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sādhāraṇapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',31,replace(replace('<p class="bodytext">ekatopaññatti <a name="M1.0031"></a> nukho āvuso ubhatopaññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ubhatopaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tasmiṃ āvuso tatiya pārājike sañcicca manussaviggahaṃ jīvitā voropento kati āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sañcissa bhante manussaviggahaṃ jīvitā voropento tisso āpattiyo āpajjati, manussaṃ odissa papātaṃ khaṇati papatitvā marissatīti āpatti dukkaṭassa, papatite dukkhā vedanā uppajjati āpatti thullaccayassa, marati āpatti pārājikassa, sañcicca bhante manussaviggahaṃ jīvitā voropento imā tisso āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',32,replace(replace('<p class="bodytext">kesaṃ <a name="M1.0032"></a> āvuso anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattannaṃ bhante puggalānaṃ anāpatti, asañcicca mārentassa, ajānantassa, na maraṇādhippāyassa, ummattakassa, khittacittassa, vedanāṭṭassa, ādikammikassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pañcannaṃ āvuso pātimokkhuddesānaṃ katthogadhaṃ kattha pariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nidānogadhaṃ bhante nidānapariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',33,replace(replace('<p class="bodytext">katamena <a name="M1.0033"></a> āvuso uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiyena bhante uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sīlavipatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sattannaṃ āvuso āpattikkhandhānaṃ katamo āpattikkhandho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pārājikāpattikkhandho bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> channaṃ āvuso āpattisamuṭṭhānānaṃ katihi samuṭṭhānehi samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> channaṃ bhante āpattisamuṭṭhānānaṃ tīhi samuṭṭhānehi samuṭṭhāti, siyā kāyatoca cittatoca samuṭṭhāti, na </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',34,replace(replace('<p class="bodytext">vācato<a name="M1.0034"></a>, siyā vācatoca cittatoca samuṭṭhāti, na kāyato, siyā kāyatoca vācatoca cittatoca samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso adhikaraṇānaṃ katamaṃ adhikaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āpattādhikaraṇaṃ bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',35,replace(replace('<p class="bodytext">sattannaṃ <a name="M1.0035"></a> āvuso samathānaṃ katihi samathehi sammati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattānaṃ bhante samathānaṃ dvīhi samathehi sammati, sammukhā vinayenaca paṭiññātakaraṇenaca.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ko tattha āvuso vinayo ko tattha abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante vinayo vibhatti abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kiṃ āvuso tattha pātimokkhaṃ kiṃ adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante pātimokkhaṃ vibhatti adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',36,replace(replace('<p class="bodytext">kā <a name="M1.0036"></a> āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sampatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso tattha paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na evarūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu ayaṃ bhante tattha paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',37,replace(replace('<p class="bodytext">kati <a name="M1.0037"></a> āvuso atthavase paṭicca bhagavatā tatiyaṃ pārājikaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante atthavase paṭicca bhagavatā tatiyaṃ pārājikaṃ paññattaṃ saṅghasuṭṭhutāya saṅghaphāsutāya (pa) vinayānuggahāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',38,replace(replace('<p class="bodytext">ke <a name="M1.0038"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhāca bhante puthujjanakalyāṇakāca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',39,replace(replace('<p class="bodytext">kassa <a name="M1.0039"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthinukho āvuso tattha kiñci upanetabbaṃ vā apanetabbaṃ vā akkharapadapaccābhaṭṭhaṃ vā viraddhapadabyañjanaṃ vā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="centered"><span class="bld">Saṃgāyanatthāya saṅghassa ñāpanaṃ</span></p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',40,replace(replace('<p class="bodytext">Suṇātu <a name="M1.0040"></a> me bhante saṅgho, ahaṃ bhante āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ tatiyassa pārājikassa nidānampi pucchiṃ, puggalampi pucchiṃ, vatthumpi pucchiṃ, paññattimpi pucchiṃ, anupaññattimpi pucchiṃ, āpattimpi pucchiṃ, anāpattimpi pucchiṃ, aññānipi evarūpāni pucchitabbaṭṭhānāni anekāni pucchiṃ, puṭṭho puṭṭhoca so āyasmā vicittasārābhivaṃso vissajjesi, itihidaṃ bhante tatiyapārājikasikkhāpadaṃ nimmalaṃ suparisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsammuddhassa, tasmā yathā pure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariyasaṅkhātassa buddhasāsanassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ dhammavinayaṃ saṅgāyiṃsuceva anusaṅgāyiṃsuca, evameva mayampi dāni sabbeva chaṭṭhasaṃgītimahādhammasabhāpariyāpannā idaṃ tatiyapārājikasikkhāpadaṃ ekato gaṇasajjhāyaṃ katvā samaggā sammodamānā hutvā saṃgāyeyyāma.</p>\r\n<p class="subhead">Catuttha pārājika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',42,replace(replace('<p class="bodytext">yaṃ <a name="M1.0042"></a> tena āvuso bhagavatā jānatā passatā…pe… sammāsambuddhena catutthaṃ pārājikaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vaggumudātīriye bhante bhikkhū ārabbha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vaggumudātīriyā bhante bhikkhū gihīnaṃ aññamaññassa uttarimanussadhammassa vaṇṇaṃ bhāsiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',43,replace(replace('<p class="bodytext">atthi <a name="M1.0043"></a> āvuso tattha paññatti anupaññatti anuppannapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekā bhante paññatti ekā anupaññatti anuppannapaññatti tasmiṃ natthi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katamā āvuso tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yo pana bhikkhu anabhijānaṃ uttarimanussadhammaṃ uttarimanussadhammaṃ attupanāyikaṃ alamariyañāṇadassanaṃ samudācareyya, itijānāmi itipassāmīti, tato aparena samayena samanuggāhīya mānovā asamanuggāhīya mānovā āpanno visuddhāpekkho evaṃ vadeyya ajānamevaṃ āvuso avacaṃ jānāmi apassaṃ passāmi tucchaṃ musāvilapinti ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',44,replace(replace('<p class="bodytext">katamā <a name="M1.0044"></a> āvuso tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aññatra adhimānāti ayaṃ bhante tattha anupaññatti.</p>\r\n<p class="bodytext">Honti ye te ānanda bhikkhū adiṭṭhe diṭṭhasaññino apatte pattasaññino anadhigate adhigatasaññino asacchikate sacchikatasaññano adhimānena aññaṃ byākaronti, tañca kho etaṃ abbohārikaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',45,replace(replace('<p class="bodytext">katamā <a name="M1.0045"></a> āvuso tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yopana bhikkhu anabhijānaṃ uttarimanussadhammaṃ attupanāyikaṃ alamariyañāṇadassanaṃ samudācareyya, iti jānāmi iti passāmīti, tato aparena samayena samanuggāhīyamānovā asamanuggāhīyamānovā āpanno visuddhāpekkho evaṃ vadeyya ajānamevaṃ āvuso avacaṃjānāmi apassaṃ passāmi tucchaṃ musā vilapinti, aññatra adhimānā ayampi pārājiko hoti asaṃvāsoti ayaṃ bhante tattha paripuṇṇa paññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',46,replace(replace('<p class="bodytext">sabbatthapaññatti <a name="M1.0046"></a> nukho āvuso padesapaññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbatthapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhāraṇapaññatti nukho āvuso asādhāraṇapaññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sādhāraṇapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ekatopaññatti nukho āvuso ubhato paññatti nukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ubhatopaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',47,replace(replace('<p class="bodytext">tasmiṃ <a name="M1.0047"></a> āvuso catuttha pārājike asantaṃ abhūtaṃ uttarimanussadhammaṃ ullapanto kati āpattiyo āpajjati, vibhajitvā vissajjehi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃ bhante catutthapārājike asantaṃ abhūtaṃ uttarimanussadhammaṃ ullapanto tisso āpattiyo āpajjati, asantaṃ abhūtaṃ uttarimanussadhammaṃ ullapati āpattipārājikassa, yo te vihāre vasati so bhikkhu arahāti bhaṇati paṭivijānantassa āpatti thullaccayassa, appaṭivijānantassa āpatti dukkaṭassa, uttarimanussadhammaṃ bhante ullapanto imā tisso āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',48,replace(replace('<p class="bodytext">kesaṃ <a name="M1.0048"></a> āvuso anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> channaṃ bhante puggalānaṃ anāpatti adhimānena bhaṇantassa anullapanādhippāyassa ummattakassa khittacittassa vedanāṭṭassa ādikammikassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',49,replace(replace('<p class="bodytext">pañcannaṃ <a name="M1.0049"></a> āvuso pātimokkhuddesānaṃ katthogadhaṃ kattha pariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> nidānogadhaṃ bhante nidānapariyāpannaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katamena āvuso uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiyena bhante uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sīlavipatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',50,replace(replace('<p class="bodytext">sattannaṃ <a name="M1.0050"></a> āvuso āpattikkhandhānaṃ katamo āpattikkhandho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pārājikāpattikkhandho bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> channaṃ āvuso āpattisamuṭṭhānānaṃ katihi samuṭṭhānehi samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> channaṃ bhante āpattisamuṭṭhānānaṃ tīhi samuṭṭhānehi samuṭṭhāti, siyā kāyatoca cittatoca samuṭṭhāti, na vācato, siyā vācatoca cittatoca samuṭṭhāti, na kāyato, siyā kāyatoca vācatoca cittatoca samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',51,replace(replace('<p class="bodytext">catunnaṃ <a name="M1.0051"></a> āvuso adhikaraṇānaṃ katamaṃ adhikaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āpattādhikaraṇaṃ bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sattannaṃ āvuso samathānaṃ katihi samathehi sammati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattannaṃ bhante samathānaṃ dvīhi samathehi sammati, sammukhāvinayenaca paṭiññātakaraṇenaca.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ko tattha āvuso vinayo, ko tattha abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante vinayo, vibhatti abhivinayo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',52,replace(replace('<p class="bodytext">kiṃ <a name="M1.0052"></a> āvuso tattha pātimokkhaṃ, kiṃ adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paññatti bhante pātimokkhaṃ, vibhatti adhipātimokkhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sampatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',53,replace(replace('<p class="bodytext">kā <a name="M1.0053"></a> āvuso tattha paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na evarūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu ayaṃ bhante tattha paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kati āvuso atthavase paṭicca bhagavatā catutthaṃ pārājikaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante atthavase paṭicca bhagavatā catutthaṃ pārājikaṃ paññattaṃ, saṅghasuṭṭhutāya saṅghaphāsutāya…pe… vinayānuggahāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',55,replace(replace('<p class="bodytext">ke <a name="M1.0055"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhāca bhante puthujjanakalyāṇakāca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',56,replace(replace('<p class="bodytext">kassa <a name="M1.0056"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso tattha kiñci upanetabbaṃ vā apanetabbaṃ vā akkharapadapaccābhaṭṭhaṃ vā viraddhapadabyañjanaṃ vā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="chapter">Saṅghādisesa sikkhāpucchā</p>\r\n<p class="subhead">1. Sukkavissaṭṭhisaṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',57,replace(replace('<p class="bodytext">yaṃ <a name="M1.0057"></a> tena āvuso bhagavatā jānatā passatā arahatā sammāsambuddhena upakkamitvā asuciṃ mocentassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante seyyasakaṃ ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante seyyasako hatthena upakkamitvā asuciṃ mocesi, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',59,replace(replace('<p class="bodytext">atthi <a name="M1.0059"></a> āvuso tattha paññatti anupaññatti anuppannapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekā bhante paññatti, ekā anupaññatti, anuppannapaññatti tasmiṃ natthi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katamā āvuso tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sañcetanikā sukkavissaṭṭhi saṅghādisesoti ayaṃ bhante tattha mūlapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katamā panāvuso tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aññatra supinantāti ayaṃ bhante tattha anupaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',60,replace(replace('<p class="bodytext">katamā <a name="M1.0060"></a> panāvuso tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sañcetanikā sukkavissaṭṭhi aññatrasupinantā saṅghādisoti ayaṃ bhante tattha paripuṇṇapaññatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sādhāraṇa paññattinukho āvuso asādhāraṇa paññattinukho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asādhāraṇapaññatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tattha āvuso upakkamitvā asuciṃ mocento kati āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> upakkamitvā bhante asuciṃ mocento tisso āpattiyo āpajjati, ceteti upakkamati muccati āpatti</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',61,replace(replace('<p class="bodytext">Saṅghādisesassa<a name="M1.0061"></a>, ceteti upakkamati na muccati āpatti thullaccayassa, payoge dukkaṭaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kesaṃ āvuso anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> channaṃ bhante anāpatti supinantena mocentassa na mocanādhippāyassa ummattakassa khittacittassa vedanāṭṭassa ādikammikassa imesaṃ kho bhante channaṃ anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',62,replace(replace('<p class="bodytext">pañcannaṃ <a name="M1.0062"></a> āvuso pātimokkhuddesānaṃ katamena uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcannaṃ bhante pātimokkhuddesānaṃ tatiyena uddesena uddesaṃ āgacchati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> catunnaṃ āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catunnaṃ bhante vipattīnaṃ sīlavipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',63,replace(replace('<p class="bodytext">kā <a name="M1.0063"></a> āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sampatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na eva rūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu ayaṃ bhante paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kati āvuso atthavase paṭicca bhagavatā upakkamitvā asuciṃ mocentassa saṅghādiseso paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',64,replace(replace('<p class="bodytext">dasa <a name="M1.0064"></a> bhante atthavase paṭicca bhagavatā upakkamitvā asuciṃ mocentassa saṅghādiseso paññatto saṅghasuṭṭhutāya…pe… vinayānuggahāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',65,replace(replace('<p class="bodytext">kassa <a name="M1.0065"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso tattha sanidāne sapadabhājanīye savinītavatthuke upanetabbaṃ vā apanetabbaṃ vā akkharapadapaccābhaṭṭhaṃ vā viraddhapadabyañjanaṃ vā.</p>\r\n<p class="centered"><span class="bld">Saṃgāyanatthāya saṅghassa ñāpanaṃ</span></p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',66,replace(replace('<p class="bodytext">Suṇātu <a name="M1.0066"></a> me bhante saṅgho āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ paṭhamassa saṅghādisesa sikkhāpadassa nidānampi pucchiṃ, puggalampi pucchiṃ, vatthumpi pucchiṃ, paññattimpi pucchiṃ, anupaññattimpi pucchi, āpattimpi pucchiṃ, anāpattimpi pucchiṃ, aññānipi evarūpāni anekāni pucchitabbaṭṭhānāni pucchiṃ, puṭṭho puṭṭho ca so āyasmā vicittasārābhivaṃso vissajjesi, itihidaṃ bhante paṭhama saṅghādisesa sikkhāpadaṃ saparivāraṃ nimmalaṃ suparisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsambuddhassa. Tasmā yathāpure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariyassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ <a name="M1.0067"></a> dhammavinayaṃ saṃgāyiṃsuceva anusaṃgāyiṃsuca, evameva mayampi sabbeva chaṭṭhasaṃgītimahādhammasabhā pariyāpannā idaṃ paṭhamaṃ saṅghādisesa sikkhāpadaṃ nidānato paṭṭhāya ekaccānaṃ vacasā sajjhāyavasena ekaccānaṃ manasā manasikaraṇavasenaca ekato gaṇasajjhāyaṃ katvā saṃgāyeyyāma.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',67,replace(replace('<p class="bodytext">devamanussānaṃ <a name="M1.0067"></a> dhammavinayaṃ saṃgāyiṃsuceva anusaṃgāyiṃsuca, evameva mayampi sabbeva chaṭṭhasaṃgītimahādhammasabhā pariyāpannā idaṃ paṭhamaṃ saṅghādisesa sikkhāpadaṃ nidānato paṭṭhāya ekaccānaṃ vacasā sajjhāyavasena ekaccānaṃ manasā manasikaraṇavasenaca ekato gaṇasajjhāyaṃ katvā saṃgāyeyyāma.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',68,replace(replace('<p class="bodytext">yaṃ <a name="M1.0068"></a> tena āvuso bhagavatā…pe… sammāsambuddhena mātukāmena saddhiṃ kāyasaṃsaggaṃ samāpajjantassa ca, mātugāmaṃ duṭṭhullāhi vācāhi obhāsantassa ca, mātugāmassa santike attakāma pāricariyāya vaṇṇaṃ bhāsantassa ca, sañcarittaṃ samāpajjantassa ca saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',69,replace(replace('<p class="bodytext">kaṃ <a name="M1.0069"></a> āvuso ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante udāyittheraṃ ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante udāyitthero mātugāmena saddhiṃ kāyasaṃsaggaṃ samāpajji, āyasmāyeva bhante udāyitthero mātugāmaṃ duṭṭhullāhi vācāhi obhāsi, āyasmāyeva bhante udāyitthero mātugāmassa santike attakāma pāricariyāya vaṇṇaṃ abhāsi, āyasmāyeva bhante udāyitthero sañcarittaṃ samāpajjiṃ, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">6. Kuṭikāra saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',70,replace(replace('<p class="bodytext">yaṃ <a name="M1.0070"></a> tena āvuso…pe… sammāsambuddhena saññācikāya kuṭiṃ kārāpentassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante paññattho.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷavike bhante bhikkhū ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',71,replace(replace('<p class="bodytext">kismiṃ <a name="M1.0071"></a> āvuso vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷavikā bhante bhikkhū saññācikāyo kuṭiyo kārāpesuṃ, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">7. Vihārakāra saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso…pe… sammāsambuddhena mahallakaṃ vihāraṃ kārāpentassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',72,replace(replace('<p class="bodytext">kaṃ <a name="M1.0072"></a> āvuso ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante channattheraṃ ārabbha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃ āvuso vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmā bhante channo vihāravatthuṃ sodhento aññataraṃ cetiyarukkhaṃ chedāpesi gāmapūjitaṃ nigamapūjitaṃ nagara pūjitaṃ janapadapūjitaṃ raṭṭhapūjitaṃ, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">8-9. Duṭṭhadosa saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso bhagavatā…pe… sammāsambuddhena bhikkhuṃ amūlakena pārājikena dhammena anuddhaṃsentassa ca, bhikkhuṃ aññabhāgiyassa adhikaraṇassa kiñcidesaṃ lesamattaṃ upādāya pārājikena dhammena anuddhaṃsentassa ca saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',73,replace(replace('<p class="bodytext">rājagahe <a name="M1.0073"></a> bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mettiyabhūmajakā bhante bhikkhū ārabbha paññatto, mettiyabhūmajakā bhante bhikkhū āyasmantaṃ dabbaṃ mallaputtaṃ amūlakena pārājikena dhammena anuddhaṃsesuṃ, teyeva bhante mettiyabhūmajakā bhikkhū āyasmantaṃ dabbaṃ mallaputtaṃ aññabhāgiyassa adhikaraṇassa kiñcidesaṃ lesamattaṃ upādāya pārājikena dhammena anuddhaṃsesuṃ, tasmiṃ bhante vatthusmiṃ, paññatto.</p>\r\n<p class="subhead">10. Saṅghabhedaka saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',74,replace(replace('<p class="bodytext">yaṃ <a name="M1.0074"></a> tena āvuso bhagavatā…pe… sammāsambuddhena saṅghabhedakassa bhikkhuno yāvatatiyaṃ samanubhāsanāya appaṭinissajjantassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',75,replace(replace('<p class="bodytext">kaṃ <a name="M1.0075"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattho.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> devadattaṃ bhante ārabbha paññatto.</p>\r\n<p class="bodytext">Devadatto bhante samaggassa saṅghassa bhedāya parakkami, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">11. Bhedānuvattaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',77,replace(replace('<p class="bodytext">yaṃ <a name="M1.0077"></a> tena āvuso bhagavatā…pe… sammāsambuddhena bhedānuvattakānaṃ bhikkhūnaṃ yāvatatiyaṃ samanubhāsanāya appaṭinissajjantānaṃ saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññatto, sambahulā bhante bhikkhū devadattassa saṅghābhedāya parakkamantassa anuvattakā ahesuṃ vaggavādakā, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">12. Dubbaca, saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',78,replace(replace('<p class="bodytext">yaṃ <a name="M1.0078"></a> tena āvuso bhagavatā…pe… sammāsambuddhena dubbacassa bhikkhuno yāvatatiyaṃ samanubhāsanāya appaṭinissajjantassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',79,replace(replace('<p class="bodytext">kaṃ <a name="M1.0079"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante channaṃ ārabbha paññatto, āyasmā bhante channo bhikkhūhi sahadhammikaṃ vuccamāno attānaṃ avacanīyaṃ akāsi, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">13. Kuladūsaka saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',80,replace(replace('<p class="bodytext">yaṃ <a name="M1.0080"></a> tena āvuso bhagavatā…pe… sammāsambuddhena kuladūsakassa bhikkhuno yāvatatiyaṃ samanubhāsanāya appaṭinissajjantassa saṅghādiseso kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',81,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0081"></a> bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ ārabbha āvuso kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> assaji punabbasuke bhante bhikkhū ārabbha paññatto, assajipunabbasukā bhante bhikkhū saṅghena pabbājanīyakammakatā chandagāmitā dosagāmitā mohagāmitā bhayagāmitā pāpesuṃ, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="subhead">Aniyata sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',85,replace(replace('<p class="bodytext">yaṃ <a name="M1.0085"></a> tena āvuso bhagavatā…pe… sammāsambuddhena paṭhamo ca aniyato dutiyo ca aniyato kattha paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante udāyiṃ ārabbha paññatto, āyasmā bhante udāyī mātugāmena saddhiṃ ekoekāya raho paṭicchanne āsane alaṃkammaniye nisajjaṃ kappesi, tasmiñca vatthusmiṃ, āyasmāyeva bhante udāyī mātugāmena saddhiṃ raho nisajjaṃ akāsi, tasmiñca bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',86,replace(replace('<p class="bodytext">kassa <a name="M1.0086"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso ettha kocipi viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="centered">Saṃgāyanatthāya saṅghassañāpanaṃ</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',87,replace(replace('<p class="bodytext">Suṇātu <a name="M1.0087"></a> me bhante saṅgho, ahaṃ bhante āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ paṭhama duṭṭhadosa sikkhāpadato paṭṭhāya pucchitabbāni anekāni ṭhānāni pucchiṃ, so ca āyasmā puṭṭho puṭṭho vissajjeti, itihidaṃ bhante sabbaṃ sikkhāpadaṃ nimmalaṃ suparisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsambuddhassa, tasmā yathāpure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariyassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ saṃgāyiṃsuceva anusaṃgāyiṃsuca, evameva mayampi dāni sabbeva chaṭṭhasaṃgīti mahādhammasabhāpariyāpannā imāni sikkhāpadāni ekato gaṇasajjhāyaṃ katvā saṃgāyeyyāma.</p>\r\n<p class="chapter">Nissaggipācittiya</p>\r\n<p class="subhead">1. Cīvaravagga 1. paṭhamakathina sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',89,replace(replace('<p class="bodytext">yaṃ <a name="M1.0089"></a> tena āvuso bhagavatā…pe… sammāsambuddhena atirekacīvaraṃ dasāhaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū atirekacīvaraṃ dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Cīvaravagga 2. udosita sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',90,replace(replace('<p class="bodytext">ekarattaṃ <a name="M1.0090"></a> āvuso ticivarena vippavasantassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū bhikkhūnaṃ hatthe cīvaraṃ nikkhipitvā santaruttarena janapadacārikaṃ pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Cīvaravagga 3. tatiyakathina sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',91,replace(replace('<p class="bodytext">akālacīvaraṃ <a name="M1.0091"></a> āvuso paṭiggahetvā māsaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū akālacīvaraṃ paṭiggahetvā māsaṃ atikkāmesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Cīvaravagga 4. purāṇacīvara sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',92,replace(replace('<p class="bodytext">aññātikāya <a name="M1.0092"></a> āvuso bhikkhuniyā purāṇacīvaraṃ dhovāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante udāyiṃ ārabbha paññattaṃ, āyasmā bhante udāyī aññātikāya bhikkhuniyā purāṇacīvaraṃ dhovāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Cīvaravagga 5. cīvarapaṭiggahaṇa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',93,replace(replace('<p class="bodytext">aññātikāya <a name="M1.0093"></a> āvuso bhikkhuniyā hatthato cīvaraṃ paṭiggaṇhantassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante udāyiṃ ārabbha paññattaṃ, āyasmā bhante udāyī aññātikāya bhikkhuniyā hatthato cīvaraṃ paṭiggahesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Cīvaravagga 6. aññātakaviññatti sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',95,replace(replace('<p class="bodytext">aññātakaṃ <a name="M1.0095"></a> āvuso gahapatiṃvā gahatāniṃvā cīvaraṃ viññāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto aññātakaṃ seṭṭhiputtaṃ cīvaraṃ viññāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. Cīvaravagga 7. tatuttari sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',96,replace(replace('<p class="bodytext">aññātakaṃ <a name="M1.0096"></a> āvuso gahapatiṃ vā gahapatāniṃ vā tatuttari cīvaraṃ viññāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū na mattaṃ jānitvā bahumpi cīvaraṃ viññāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',97,replace(replace('<p class="bodytext">pubbe <a name="M1.0097"></a> appavāritassa āvuso aññātakaṃ gahapatiṃ vā gahatāniṃ vā upasaṅkamitvā cīvare vikappaṃ āpajjantassa ca aññātake gahapatike upasaṅkamitvā cīvare vikappaṃ āpajjantassa ca nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto pubbe appavārito aññātakaṃ gahapatikaṃ upasaṅkamitvā cīvare vikappaṃ āpajji. Soyeva bhante āyasmā upanando sakyaputto pubbe appavārito aññātake gahapatike upasaṅkamitvā cīvare vikappaṃ āpajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Cīvaravagga 10. rāja sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atirekatikkhattuṃ āvuso codanāya atireka chakkhattuṃ ṭhānena cīvaraṃ abhinipphādentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',99,replace(replace('<p class="bodytext">kaṃ <a name="M1.0099"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto upāsakena ajjaṇho bhante āgamehīti vuccamāno nāgamesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga, 1. kosiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kosiyamissakaṃ āvuso santhakaṃ kārāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',100,replace(replace('<p class="bodytext">kaṃ <a name="M1.0100"></a>0 āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kosiyakārake upasaṅkamitvā evamahaṃsu bahū āvuso kosakārake pacatha amhākampi dassatha mayampi icchāma kosiyamissakaṃ santakaṃ kātunti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 2. suddhakāḷaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> suddhakāḷakānaṃ āvuso eḷakalomānaṃ santhataṃ kārāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante paññatthaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',101,replace(replace('<p class="bodytext">kaṃ <a name="M1.0101"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū suddhakāḷakānaṃ eḷakalomānaṃ santhataṃ kārāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 3. dvebhāga sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādiyitvā āvuso tulaṃ odātānaṃ tulaṃ gocariyānaṃ navaṃ santhataṃ kārāpentassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',102,replace(replace('<p class="bodytext">kaṃ <a name="M1.0102"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū thokaṃyeva odātaṃ ante ādiyitvā tatheva suddhakāḷakānaṃ eḷakalomānaṃ santhataṃ kārāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 4. chabbassa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso bhagavatā…pe… sammāsambuddhena anuvassaṃ kārāpentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',103,replace(replace('<p class="bodytext">kaṃ <a name="M1.0103"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū anuvassaṃ santhataṃ kārāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',104,replace(replace('<p class="bodytext">atthi <a name="M1.0104"></a> nukho āvuso ettha koci viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="centered">Saṃgāyanatthāya saṅghassa ñāpanaṃ</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho, ahaṃ āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ imesaṃ nissaggiyapācittiyasikkhāpadānaṃ yāva chabbassasikkhāpadaṃ nidānādīni pucchitabbāni anekāni ṭhānāni pucchiṃ, puṭṭhopuṭṭho ca so āyasmā vicittasārābhivaṃso vissajjesi, itihidaṃ bhante imāni sikkhāpadāni nimmalāni suparisuddhānī tasseva bhagavato vacanāni arahato sammāsambuddhassa, tasmā yathāpure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariyassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ dhammavinayaṃ saṃgāyiṃsu ceva anusaṃgāyiṃsu ca, evameva mayampi dāni sabbeva chaṭṭhasaṃgīti mahādhammasabhā pariyāpannā chabbassa nissaggiya sikkhāpadato paṭṭhāya imāni nissaggiyasikkhāpadāni ekato gaṇasajjhāyaṃ katvā saṃhāyeyyāma.</p>\r\n<p class="subhead">2. Kosiyavagga 5. nisīdanasanthata sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',105,replace(replace('<p class="bodytext">yaṃ <a name="M1.0105"></a> tena āvuso bhagavatā…pe… sammāsambuddhena anādiyitvā purāṇasanthatassa sāmantā sugatavidattiṃ navaṃ nisīdanasanthataṃ kārāpentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',106,replace(replace('<p class="bodytext">kaṃ <a name="M1.0106"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū santhatāni ujjhitvā āraññikaṃ piṇḍapātikaṅgaṃ paṃsukūlakaṅgaṃ samādiyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 6. eḷakaloma sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> eḷakalomāni āvuso paṭiggahetvā tiyojanaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',107,replace(replace('<p class="bodytext">kaṃ <a name="M1.0107"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aññataraṃ bhante bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu eḷakalomāni paṭiggahetvā tiyojanaṃ atikkāmesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 7. eḷakalomadhovāpana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',108,replace(replace('<p class="bodytext">aññātikāya <a name="M1.0108"></a> āvuso bhikkhuniyā eḷakalomāni dhovāpentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū aññātikāhi bhikkhūnīhi eḷakalomāni dhovāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Kosiyavagga 8. rūpiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',109,replace(replace('<p class="bodytext">rūpiyaṃ <a name="M1.0109"></a> āvuso paṭiggaṇhantassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto rūpiyaṃ paṭiggahesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',110,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0110"></a> āvuso tattha paṭipajjanto rūpiyaṃ paṭiggaṇhāti nāma.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sayaṃ bhante gaṇhāti, aññaṃ vā gaṇhāpesi, idaṃ ayyassa hotūti upanikkhittaṃ sādiyati, evaṃ kho bhante paṭipajjanto bhikkhu rūpiyaṃ paṭiggaṇhāti nāma.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso tattha paṭipajjanto bhikkhu rūpiyaṃ na paṭiggaṇhāti nāma.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',111,replace(replace('<p class="bodytext">yathā <a name="M1.0111"></a> bhante rājasikkhāpade yathā ca bhante meṇḍakasikkhāpade āgacchati, tathā bhante paṭipajjanto bhikkhu rūpiyaṃ na paṭiggaṇhātināma.</p>\r\n<p class="subhead">2. Kosiyavagga 9. rūpiyasaṃvohāra sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',112,replace(replace('<p class="bodytext">rūpiyasaṃvohāraṃ <a name="M1.0112"></a> āvuso samāpajjantassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū nānappakārakaṃ rūpiyasaṃvohāraṃ samāpajjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Kosiyavagga, 10. kayavikkaya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> nānappakārakaṃ āvuso kayavikkayaṃ āpajjantassa nissaggiyaṃ pācittiyaṃ bhagavatā kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto paribbājakena saddhi kayavikkayaṃ samāpajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso tattha paṭipajjanto bhikkhu kayavikkayaṃ samāpajjatināma.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> iminā imaṃ dehi iminā imaṃ āhara iminā imaṃ parivattehi iminā imaṃ cetāmehīti ajjhācarati, evaṃ kho bhante paṭipajjanto kayavikkayaṃ samāpajjatināma.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',114,replace(replace('<p class="bodytext">kathaṃ <a name="M1.0114"></a> āvuso tattha paṭipajjanto bhikkhu na kayavikkayaṃ samāpajjatināma.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yo bhante agghaṃ pucchati, yo ca bhante kappiyakārakassa ācikkhati, yo ca bhante amhākañca idaṃ atthi amhākañca iminā ca iminā ca atthoti bhaṇati, evaṃ kho bhante paṭipajjanto bhikkhu na kayavikkayaṃ samāpajjatināma.</p>\r\n<p class="subhead">3. Pattavagga, 1. patta sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atirekapattaṃ āvuso dasāhaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',115,replace(replace('<p class="bodytext">kaṃ <a name="M1.0115"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū atirekapattaṃ dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para514"></a><span class="paranum">514</span> 3. Pattavagga, 2. ūnapañcabandhana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ūnapañcabandhanena pattena aññaṃ navaṃ pattaṃ cetāpentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'-514-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',116,replace(replace('<p class="bodytext">kaṃ <a name="M1.0116"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū appamattakenapi bhinnena appamattakenapi khaṇḍena vilikhitamattenapi bahū patte viyyāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext">Na bhikkhave patto viññāpetabbo, yo viññāpeyya āpatti dukkaṭassa.</p>\r\n<p class="subhead">3. Pattavagga 3. bhesajja sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',117,replace(replace('<p class="bodytext">bhesajjāni <a name="M1.0117"></a> āvuso paṭiggahetvā sattāhaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha ārabbha paññattaṃ, sambahulā bhante bhikkhū bhesajjāni paṭiggahetvā sattāhaṃ atikkāmesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavagga 4. vassikasāṭika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',118,replace(replace('<p class="bodytext">atirekamāse <a name="M1.0118"></a> sese gimāne āvuso vassikasāṭikacīvaraṃ pariyesantassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',119,replace(replace('<p class="bodytext">kaṃ <a name="M1.0119"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū atirekamāse sese gimhāne vassikasāṭikacīvaraṃ pariyesiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavagga 5. cīvaraacchindana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhussa āvuso sāmaṃ cīvaraṃ datvā kupitena anattamanena acchindantassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',120,replace(replace('<p class="bodytext">kaṃ <a name="M1.0120"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto bhikkhussa sāmaṃ cīvaraṃ datvā kupito anattamano acchindi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavaggo, 6. suttaviññatti sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sāmaṃ āvuso suttaṃ viññāpetvā tantavāyehi cīvaraṃ vāyāpentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kaṃ āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū sāmaṃ suttaṃ viññāpetvā tantavāyehi cīvaraṃ vāyāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavagga, 7. mahāpesakāra sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',122,replace(replace('<p class="bodytext">pubbe <a name="M1.0122"></a> appavāritassa āvuso aññātakassa gahapatikassa tantavāye upasaṅkamitvā cīvare vikappaṃ āpajjantassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthi yaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',123,replace(replace('<p class="bodytext">kaṃ <a name="M1.0123"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmantaṃ bhante upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto pubbe appavārito aññātakassa gahapatikassa tantavāye upasaṅkamitvā cīvare vikappaṃ āpajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavaggo, 8. accekacīvara sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> acceka cīvaraṃ āvuso paṭiggahetvā cīvarakālasamayaṃ atikkāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',124,replace(replace('<p class="bodytext">kaṃ <a name="M1.0124"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū accekacīvaraṃ paṭiggahetvā cīvarakālasamayaṃ atikkāmesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavaggo, 9. sāsaṅka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tiṇṇaṃ āvuso cīvarānaṃ aññataraṃ cīvaraṃ antaraghare nikkhipitvā atirekachārattaṃ vippavasantassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',125,replace(replace('<p class="bodytext">kaṃ <a name="M1.0125"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū tiṇṇaṃ cīvarānaṃ aññataraṃ cīvaraṃ antaraghare nikkhipitvā atirekachārattaṃ vippavasiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Pattavaggo, 10. pariṇata sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso bhagavatā passatā arahatā sammāsambuddhena jānaṃ saṅghikaṃ lābhaṃ atthano pariṇāmentassa nissaggiyaṃ pācittiyaṃ kattha paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',126,replace(replace('<p class="bodytext">kaṃ <a name="M1.0126"></a> āvuso ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū jānaṃ saṅghikaṃ lābhaṃ pariṇataṃ attano pariṇāmesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',127,replace(replace('<p class="bodytext">jānaṃ <a name="M1.0127"></a> āvuso saṅghikaṃ lābhaṃ pariṇataṃ attano pariṇāmento, kati āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> jānaṃ bhante saṅghikaṃ lābhaṃ pariṇataṃ attano pariṇāmento dve āpattiyo āpajjati, pariṇāmeti payoge dukkaṭaṃ, pariṇāmite nissaggiyaṃ pācittiyaṃ, jānaṃ bhante saṅghikaṃ lābhaṃ pariṇataṃ attano pariṇāmento imā dve āpattiyo āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',128,replace(replace('<p class="bodytext">kassa <a name="M1.0128"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso tattha kocipi viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',129,replace(replace('<p class="subhead">Saṃgāyanatthāya ñāpanaṃ</p>\r\n<p class="bodytext">Suṇātu <a name="M1.0129"></a> me bhante saṅgho, ahaṃ bhante imesaṃ soḷasannaṃ nissaggiyapācittiyasikkhāpadānaṃ nidānādīni anekāni pucchitabbāni āyasmantaṃ vicittasārābhivaṃsaṃ tipiṭakadhara dhammabhaṇḍāgārikaṃ pucchiṃ, so ca puṭṭho puṭṭho vissajjeti, itihidaṃ bhante sikkhāpadasañjātaṃ nimmalaṃ su parisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsambuddhassa, tasmā yathā pure mahākassapādayo mahāthera varā porāṇasaṃgītikārā brahmacariyasaṅkhātassa buddhasāsanassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lokānukampāya atthāya hitāya sukhāya devamanussānaṃ dhammavinayaṃ saṃgāyiṃsuceva anusaṃgāyiṃsuca, evameva mayampi dāni chaṭṭhasaṃgīti mahādhammasabhāpariyāpannā imāni sikkhāpadāni yathānuppattaṭṭhānato paṭṭhāya ekato gaṇasajjhāyaṃ katvā saṃgāyeyyāma.</p>\r\n<p class="subhead">Pācittiyapāḷi</p>\r\n<p class="subhead">1. Musāvādavagga, 1. musāvāda sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso bhagavatā jānatā passatā arahatā sammāsambuddhena sampajānamusāvāde pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ, hatthakaṃ sakyaputtaṃ ārabbha paññattaṃ, hatthako bhante sakyaputto titthiyehi saddhiṃ sallapanto avajānitvā paṭijānāti paṭijānitvā avajānāti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo, 2-3. omasavāda, pesuñña sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> omasavāde pācittiyañca āvuso bhikkhupesuññe pācittiyañca bhagavatā katthapaññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',132,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0132"></a> bhante paññattaṃ, chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūhi saddhiṃ bhaṇḍantā omasiṃsu tasmiṃca vatthusmiṃ, chabbaggiyā bhante bhikkhū bhikkhūnaṃ bhaṇḍanajātānaṃ kalahajātānaṃ vivādāpannānaṃ pesuññaṃ upasaṃhariṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavagga, 4. padasodhamma sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anupasampannaṃ āvuso padaso vācentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paññattaṃ, chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū upāsake padaso dhammaṃ vācesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo 5. sahaseyya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anupasampannena āvuso uttaridirattatirattaṃ sahaseyyaṃ kappentassa pācittiyaṃ kattha paññattaṃ, kaṃārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante paññattaṃ, sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū anupasampannena sahaseyyaṃ kappesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',134,replace(replace('<p class="bodytext">kesaṃ <a name="M1.0134"></a> āvuso ettha anupasampannena sahaseyyaṃ kappentānampi anāpatti hoti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekādasannaṃ bhante puggalānaṃ anupasampannena sahaseyyaṃ kappentānaṃ anāpatti, dvetisso rattiyo vasatī, ūnakadve tisso rattiyo vasati, dverattiyo vasitvā tatiyāyarattiyā purāruṇā nikkhamitvā puna vasati, sabbacchanne sabbaaparicchanne, sabbaparicchanne sabbaacchanne, yebhuyyena acchanne yebhuyyena aparicchanne, anupasampanne nipanne bhikkhu nisīdati, bhikkhunipanne anupasampanno nisīdati, ubhovā nisīdanti, ummattakassa, ādikammikassa, imesaṃkho bhante ekādasannaṃ puggalānaṃ anupasampannena sahaseyyaṃ kappentānaṃ anāpatti.</p>\r\n<p class="subhead">1. Musāvādavaggo, 6. dutiya sahaseyya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',135,replace(replace('<p class="bodytext">mātugāmena <a name="M1.0135"></a> āvuso sahaseyyaṃ kappentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ anuruddhaṃ ārabbha paññattaṃ, āyasmā bhante anuruddho mātugāmena sahaseyyaṃ kappesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo, 7. dhammadesanā sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',136,replace(replace('<p class="bodytext">mātugāmassa <a name="M1.0136"></a> āvuso uttari chappañcavācāhi dhammaṃ desentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ udāyiṃ ārabbha paññattaṃ, āyasmā bhante udāyī mātugāmassa dhammaṃdeseti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo, 8. bhūtārocana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',137,replace(replace('<p class="bodytext">anupasampannassa <a name="M1.0137"></a> āvuso uttarimanussadhammaṃ bhūtaṃ ārocentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante vaggumudātīriye bhikkhū ārabbha paññattaṃ, vaggumudātīriyā bhante bhikkhū gihīnaṃ aññamaññassa uttarimanussadhammassa vaṇṇaṃ bhāsiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo, 9. duṭṭhullārocana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',138,replace(replace('<p class="bodytext">bhikkhussa <a name="M1.0138"></a> āvuso duṭṭhullāpattiṃ anupasampannassa ārocentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhussa duṭṭhullāpattiṃ anupasampannassa ārocesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Musāvādavaggo, 10. pathavīkhaṇana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',139,replace(replace('<p class="bodytext">pathaviṃkhaṇantassa <a name="M1.0139"></a> āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante āḷavike bhikkhū ārabbha paññattaṃ, āḷavikā bhante bhikkhū pathaviṃkhaṇiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavaggo, 1. bhūtagāma sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',140,replace(replace('<p class="bodytext">bhūtagāmapātabyatāya <a name="M1.0140"></a> pācittiyaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante āḷavike bhikkhū ārabbha paññattaṃ, āḷavikā bhante bhikkhū rukkhaṃ chindiṃ su, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. Bhūtagāmavagga, 2. aññavādaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> aññavādake vihesake pācittiyaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',141,replace(replace('<p class="bodytext">kosambiyaṃ <a name="M1.0141"></a> bhante āyasmantaṃ channaṃ ārabbha paññattaṃ, āyasmā bhante channo saṅghamajjhe āpattiyā anuyuñjīyamāno aññenaññaṃ paṭicari, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 3. ujjhāpanaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ujjhāpanake khiyyanake pācittiyaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante mettiyabhūmajake bhikkhū ārabbha paññattaṃ, mettiyabhūmajakā bhante bhikkhū āyasmantaṃ dabbaṃ mallaputtaṃ bhikkhū ujjhāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 4. paṭhama senāsana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',142,replace(replace('<p class="bodytext">saṅghikaṃ <a name="M1.0142"></a> āvuso mañcaṃvā pīṭhaṃvā bhisiṃvā kocchaṃvā ajjhokāse santharitvā anuddharitvā anāpucchā pakkamantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū saṅghikaṃ senāsanaṃ ajjhokāse santharitvā anuddharitvā anāpucchā pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 5. dutiya senāsana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',143,replace(replace('<p class="bodytext">saṅghike <a name="M1.0143"></a> āvuso vihāre seyyaṃ santharitvā anuddharitvā anāpucchā pakkamantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sattarasavaggiye bhikkhū ārabbha paññattaṃ, sattarasavaggiyā bhante bhikkhū saṅghike vihāre seyyaṃ santharitvā anuddharitvā anāpucchā pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',144,replace(replace('<p class="bodytext">kassa <a name="M1.0144"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavatā bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',145,replace(replace('<p class="bodytext">atthi <a name="M1.0145"></a> nukho āvuso ettha kocipi viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="subhead">Saṅghassa ñāpanaṃ</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho, ahaṃ bhante āyasmantaṃ vicittasārābhivaṃsaṃ imesaṃ pannarasannaṃ sikkhāpadānaṃ nidānādīni pucchitabbāni anekāni ṭhānāni pucchiṃ, so ca puṭṭho puṭṭho vissajjeti, iti hidaṃ bhante nimmalaṃ suparisuddhaṃ tasseva bhagavato vacanaṃ arahato sammāsambuddhassa, tasmā yathāpure mahākassapādayo mahātheravarā porāṇasaṃgītikārā brahmacariyassa ciraṭṭhitiyā bahujanahitāya bahujanasukhāya lonukampāya atthāya hitāya sukhāya devamanussānaṃ dhammavinayaṃ saṃgāyiṃsuceva anusaṃgāyiṃsuca, evameva mayampi dāni chaṭṭhasaṃgītimahādhammasabhā pariyāpannā yathānuppaṭṭhānato paṭṭhāya imāni sikkhāpadāni ekato gaṇasajjhāyaṃ katvā saṃgāyeyyāma.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 6. anupakhajja sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',146,replace(replace('<p class="bodytext">yaṃ <a name="M1.0146"></a> tena āvuso…pe… sammāsambuddhena saṅghike vihāre jānaṃ pubbupagataṃ bhikkhuṃ anupakhajja seyyaṃ kappentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū there bhikkhū anupakhajja seyyaṃ kappesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 7. nikaḍḍhana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',147,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0147"></a> āvuso kupitena anattamanena saṅghikā vihārā nikkaḍḍhentassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kupitā anattamanā bhikkhū saṅghikāvihārā nikkaḍḍhiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 8. vehāsakuṭi sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> uparivehāsakuṭiyā āvuso āhaccapādakaṃ mañcaṃvā pīṭhaṃvā abhinisīdantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',148,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0148"></a> bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu saṅghike vihāre uparivehāsa kuṭiyā āhaccapādakaṃ mañcaṃ sahasā abhinisīdi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 9. mahallakavihāra sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dvatti pariyāye āvuso adhiṭṭhahitvā tatuttari adhiṭṭhahantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃ channattheraṃ ārabbha paññattaṃ, āyasmā bhante chando katapariyositaṃ vihāraṃ punappunaṃ chādāpesi punappunaṃ lepāpesi, atibhārito vihāro paripati, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Bhūtagāmavagga, 10. sappāṇaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',149,replace(replace('<p class="bodytext">jānaṃ <a name="M1.0149"></a> āvuso sappāṇakaṃ udakaṃ tiṇaṃvā mattikaṃvā siñcantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante āḷavike bhikkhū ārabbha paññattaṃ, āḷavikā bhante bhikkhū jānaṃ sappāṇakaṃ udakaṃ tiṇampi mattikampi siñciṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 1. ovāda sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',150,replace(replace('<p class="bodytext">asammatena <a name="M1.0150"></a> āvuso bhikkhuniyo ovadantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū asammatā bhikkhuniyo ovadiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 2. atthaṅgata sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthaṅgate āvuso sūriye bhikkhuniyo ovadantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ cūḷapanthakaṃ ārabbha paññattaṃ, āyasmā bhante cūḷapanthako atthaṅgate sūriye bhikkhuniyo ovadi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 3-4. bhikkhunupassaya, āmisa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',151,replace(replace('<p class="bodytext">bhikkhunupassayaṃ <a name="M1.0151"></a> āvuso upasaṅkamitvā bhikkhuniyo ovadantassaca āmisahetu therā bhikkhū bhikkhuniyo ovadantīti bhaṇantassaca pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhikkhunupassayaṃ upasaṅkamitvā bhikkhuniyo ovadantassa pācittiyaṃ bhante sakkesu chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhunupassayaṃ upasaṅkamitvā bhikkhuniyo ovadiṃsu, āmisahetu therā bhikkhū bhikkhuniyo ovadantīti bhaṇantassa pācittiyaṃ sāvatthiyaṃ chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū āmisahetu therā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',152,replace(replace('<p class="bodytext">bhikkhū <a name="M1.0152"></a> bhikkhuniyo ovadantīti bhaṇiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 5. cīvaradāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',153,replace(replace('<p class="bodytext">aññātikāya <a name="M1.0153"></a> āvuso bhikkhuniyā cīvaraṃ dentassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu aññātikāya bhikkhuniyā cīvaraṃ adāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 6. cīvarasibbana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> aññātikāya āvuso bhikkhuniyā cīvaraṃ sibbentassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ udāyiṃ ārabbha paññattaṃ, āyasmā bhante udāyī aññātikāya bhikkhuniyā cīvaraṃ sibbesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 7. saṃvidhāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',154,replace(replace('<p class="bodytext">bhikkhunayā <a name="M1.0154"></a> āvuso saddhiṃ saṃvidhāya ekaddhānamaggaṃ paṭipajjantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhunīhi saddhiṃ saṃvidhāya ekaddhānamaggaṃ paṭipajjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 8. nāvābhiruhana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',155,replace(replace('<p class="bodytext">bhikkhuniyā <a name="M1.0155"></a> āvuso saddhiṃ saṃvidhāya ekaṃ nāvaṃ abhiruhantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūnīhi saddhiṃ saṃvidhāya ekaṃ nāvaṃ abhiruhiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 9. paripācita sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> jānaṃ āvuso bhikkhuniparipācitaṃ piṇḍapātaṃ bhuñjantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha paññattaṃ, devadatto bhante jānaṃ bhikkhuniparipācitaṃ piṇḍapātaṃ bhuñji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Ovādavagga, 10. rahonisajja sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',156,replace(replace('<p class="bodytext">bhikkhuniyā <a name="M1.0156"></a> saddhiṃ āvuso eko ekāya raho nisajjaṃ kappentassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ udāyiṃ ārabbha paññattaṃ, āyasmā bhante udāyī bhikkhuniyā saddhiṃ eko ekāya raho nisajjaṃ kappesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 1. āvasathapiṇḍa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',157,replace(replace('<p class="bodytext">tatuttari <a name="M1.0157"></a> āvuso āvasathapiṇḍaṃ bhuñjantassa pācittiyaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū anuvasitvā anuvasitvā āvasathapiṇḍaṃ paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 2. gaṇabhojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> gaṇabhojane pācittiyaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha paññattaṃ, devadatto bhante sapariso kulesu viññāpetvā bhuñji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga 3. paramparabhojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',158,replace(replace('<p class="bodytext">parampara <a name="M1.0158"></a> bhojane pācittiyaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū aññatra nimantitā aññatra bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 4. kāṇamātu sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',159,replace(replace('<p class="bodytext">dvattipattapūraṃ <a name="M1.0159"></a> pūvaṃ paṭiggahetvā tatuttari paṭiggaṇhantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū na mattaṃ jānitvā pūvaṃ paṭiggahesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso ettha kocipi viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="subhead">4. Bhojanavagga, 5. paṭhamapavāraṇā sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',160,replace(replace('<p class="bodytext">yaṃ <a name="M1.0160"></a> tena sāvuso…pe… sammāsambuddhena bhuttāvinā parivātena anatirittaṃ khādanīyaṃvā bhojanīyaṃvā bhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū bhuttāvī pavāritā aññatra bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 6. dutiya pavāraṇā sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',161,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0161"></a> āvuso bhuttāviṃ pavāritaṃ anatirittena khādanīyena bhojanīyena abhihaṭṭhuṃ pavārentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu bhikkhuṃ bhuttāviṃ pavāritaṃ anatirittena bhojanīyena abhihaṭṭhuṃ pavāresi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 7. vikālabhojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> vikāle āvuso khādanīyaṃ vā bhojanīyaṃ vā bhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sattarasavaggiye bhikkhū ārabbha paññattaṃ, sattarasavaggiyā bhante bhikkhū vikāle bhojanaṃ bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 8. sannidhikāraka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sannidhikārakaṃ āvuso khādanīyaṃ vā bhojanīyaṃ vā bhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ belaṭṭhasīsaṃ ārabbha paññattaṃ, āyasmā bhante belaṭṭhasīso sannidhikārakaṃ bhojanaṃ bhuñji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 9. paṇītabhojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> paṇīta bhojanāni āvuso attano atthāya viññāpetvā bhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',163,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0163"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū paṇītabhojanāni attano atthāya viññāpetvā bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Bhojanavagga, 10. dantapona sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> adinnaṃ āvuso mukhadvāraṃ āhāraṃ āharantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu adinnaṃ mukhadvāraṃ āhāraṃ āhari, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga, 1. acelaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',164,replace(replace('<p class="bodytext">acelakassavā <a name="M1.0164"></a> āvuso paribbājakassavā paribbājikāyavā sahatthā khādanīyaṃvā bhojanīyaṃvā dentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante āyasmantaṃ ānandaṃ ārabbha paññattaṃ, āyasmā bhante ānando aññatarissā paribbājikāya ekaṃ maññamāno dve pūve adāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga, 2. uyyojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',165,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0165"></a> ehāvuso gāmaṃvā nigamaṃvā piṇḍāya pavisissāmāti tassa dāpetvāvā adāpetvāvā uyyojentassa āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto bhikkhu ehāvuso gāmaṃ piṇḍāya pavisissāmāti tassa adāpetvā uyyojesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga, 3. sabhojana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sabhojane kule anupakhajja nisajjaṃ kappentassa āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',166,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0166"></a> bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto sabhojane kule anupakhajja nisajjaṃ kappesi, tasmiṃ bhantevatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga 4-5. rahopaṭicchanna, rahonisajja sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mātugāmena saddhiṃ raho paṭicchanne āsane nisajjaṃ kappentassaca āvuso mātugāmena saddhiṃ eko ekāya raho nisajjaṃ kappentassaca pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto mātugāmena saddhiṃ raho paṭicchanne āsane nisajjaṃ kappesi, mātugāmena saddhiṃ eko ekāya raho nisajjaṃ kappesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga, 6. cāritta sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',167,replace(replace('<p class="bodytext">nimantitena <a name="M1.0167"></a> āvuso sabhattena santaṃ bhikkhuṃ anāpucchā purebhattaṃvā pacchābhattaṃvā kulesu cārittaṃ āpajjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto nimantito sabhatto samāno purebhattaṃ pacchābhattaṃ kulesu cārittaṃ āpajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga, 7. mahānāma sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tatuttari āvuso bhesajjaṃ viññāpentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',168,replace(replace('<p class="bodytext">sakkesu <a name="M1.0168"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū mahānāmena sakkena ajjaṇho bhante āgametāti vuccamānā nāgamesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Acelakavagga 8-9-10. uyyuttasenā caso sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> uyyuttaṃ āvuso senaṃ dassanāya gacchantassa ca atirekatirattaṃ senāya vasantassa ca uyyodhikaṃ gacchantassa ca pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū uyyuttaṃ senaṃ dassanāya agamaṃsu, teyeva bhante chabbaggiyā bhikkhū atirekatirattaṃ senāya vasiṃsu, teyeva bhante chabbaggiyā bhikkhū uyyodhikaṃ agamaṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 1. surāpāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',169,replace(replace('<p class="bodytext">surāmerayapāne <a name="M1.0169"></a> āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃ sāgataṃ ārabbha paññattaṃ, āyasmā bhante sāgato majjaṃ pivi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 2. aṅgulipatodaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',170,replace(replace('<p class="bodytext">aṅgulipatodake <a name="M1.0170"></a> āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhuṃ aṅgulipatodakena hāsesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 3. hasadhamma sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> udake hasadhamme āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sattarasavaggiye bhikkhū ārabbha paññattaṃ, sattarasavaggiyā bhante bhikkhū aciravatiyā nadiyā udake kīḷiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 4. anādariya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',171,replace(replace('<p class="bodytext">anādariya <a name="M1.0171"></a> āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃ channaṃ ārabbha paññattaṃ, āyasmā bhante channo āpattiyā anuyuñjiyamāno anādariyaṃ akāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 5. bhiṃsāpana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',172,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0172"></a> bhiṃsāpentassa āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhuṃ bhiṃsāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 6. jotika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> jotiṃ āvuso samādahitvā visibbentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhaggesu bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū jotiṃ samādapetvā visibbesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 7. nahāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',173,replace(replace('<p class="bodytext">orenaddhamāsaṃ <a name="M1.0173"></a> āvuso nahāyantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū rājānampi passitvā na mattaṃ jānitvā nahāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',174,replace(replace('<p class="bodytext">kassa <a name="M1.0174"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso ettha kocipi viruddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho…pe… saṃgāyeyyāma.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',175,replace(replace('<p class="bodytext">yaṃ <a name="M1.0175"></a> tena āvuso…pe… sammāsambuddhena anādiyitvā tiṇṇaṃ dubbaṇṇakaraṇānaṃ aññataraṃ dubbaṇṇakaraṇaṃ navaṃ cīvaraṃ paribhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū attano cīvaraṃ na sañjāniṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Surāpānavagga, 10. cīvaraapanidhāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhussa āvuso pattaṃ vā cīvaraṃ vā nisīdanaṃ vā sūcigharaṃ vā kāyabandhanaṃ vā apanidhentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',176,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0176"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūnaṃ pattampi cīvarampi apanidhesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Sappāṇakavagga, 1. sañcicca sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sañcicca āvuso pāṇaṃ jīvitā voropentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ udāyiṃ ārabbha paññattaṃ āyasmā bhante udāyī sañcicca pāṇaṃ jīvitā voropesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Sappāṇakavagga, 2. sappāṇaka sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',177,replace(replace('<p class="bodytext">jānaṃ <a name="M1.0177"></a> āvuso sappāṇakaṃ udakaṃ paribhuñjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū jānaṃ sappāṇakaṃ udakaṃ paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Sappāṇakavagga, 6. theyyasattha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> jānaṃ āvuso theyyasatthena saddhiṃ saṃvidhāya ekaddhānamaggaṃ paṭipajjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu jānaṃ theyyasatthena saddhiṃ saṃvidhāya ekaddhānamaggaṃ paṭipajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Sappāṇakavagga, 7. saṃvidhāna sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',178,replace(replace('<p class="bodytext">mātugāmena <a name="M1.0178"></a> saddhiṃ āvuso saṃvidhāya ekaddhānamaggaṃ paṭipajjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu mātugāmena saddhiṃ saṃvidhāya ekaddhānamaggaṃ paṭipajji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 1. sahadhammika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhūhi āvuso sahadhammikaṃ vuccamānena na tāvāhaṃ āvuso etasmiṃ sikkhāpade sikkhissāmi, yāva na aññaṃ bhikkhuṃ byattaṃ vinayadharaṃ paripucchissāmīti bhaṇantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',179,replace(replace('<p class="bodytext">kosambiyaṃ <a name="M1.0179"></a> bhante āyasmantaṃ channaṃ ārabbha paññattaṃ, āyasmā bhante channo bhikkhūhi sahadhammikaṃ vuccamāno na tāvāhaṃ āvuso etasmiṃ sikkhāpade sikkhissāmi, yāva na aññaṃ bhikkhuṃ byattaṃ vinayadharaṃ paripucchissāmīti bhaṇi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 2. vilekhana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> vinayaṃ vivaṇṇentassa āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū vinayaṃ vivaṇṇesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 3. mohana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',180,replace(replace('<p class="bodytext">mohanake <a name="M1.0180"></a> āvuso pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū mohesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',181,replace(replace('<p class="bodytext">bhikkhussa <a name="M1.0181"></a> āvuso kupitena anattamanena pahāraṃ dentassa ca talasattikaṃ uggirantassa ca pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kupitā anattamanā sattarasavaggiyānaṃ bhikkhūnaṃ pahāraṃ adaṃsu, talasattikañca uggiriṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 7. sañcicca sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhussa āvuso sañcicca kukkuccaṃ upadahantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūnaṃ sañcicca kukkuccaṃ upadahiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 8. upassuti sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',182,replace(replace('<p class="bodytext">bhikkhūnaṃ <a name="M1.0182"></a> āvuso bhaṇḍanajātānaṃ kalahajātānaṃ vivādā pannānaṃ upassutiṃ tiṭṭhantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūnaṃ bhaṇḍanajātānaṃ kalaha jātānaṃ vivādāpannānaṃ upassutiṃ tiṭṭhahiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 9. kammapaṭibāhana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dhammikānaṃ āvuso kammānaṃ chandaṃ datvā pacchā khīyanadhammaṃ āpajjantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',183,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0183"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū dhammikānaṃ kammānaṃ chandaṃ datvā pacchā khīyanadhammaṃ āpajjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga 10. chandaṃ adatvāgamana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> saṅghe āvuso vinicchakathāya vattamānāya chandaṃ adatvā uṭṭhāyāsanā pakkamantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante chabbaggiyo bhikkhu saṅghe vinicchayakathāya vattamānāya chandaṃ adatvā uṭṭhāyāsanā pakkāmi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Sahadhammikavagga, 12. pariṇāmana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',184,replace(replace('<p class="bodytext">jānaṃ <a name="M1.0184"></a> āvuso saṅghikaṃ lābhaṃ pariṇataṃ puggalassa pariṇāmentassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū jānaṃ saṅghikaṃ lābhaṃ pariṇataṃ puggalassa pariṇāmesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Ratanavagga, 1. antepura sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pubbe appaṭisaṃviditena āvuso rañño antepuraṃ pavisantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',185,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0185"></a> bhante āyasmantaṃ ānandaṃ ārabbha paññattaṃ, āyasmā bhante ānando pubbe appaṭisaṃvidito rañño antepuraṃ pāvisi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Ratanavagga, 2. ratana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ratanaṃ āvuso uggaṇhantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu ratanaṃ uggahesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Ratanavagga, 3. vikālagāmappavisana sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',186,replace(replace('<p class="bodytext">santaṃ <a name="M1.0186"></a> āvuso bhikkhuṃ anāpucchā vikāle gāmaṃ pavisantassa pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū santaṃ bhikkhuṃ anāpucchā vikāle gāmaṃ pavisiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="title">Pāṭidesanīya</p>\r\n<p class="subhead">3. Tatiyapāṭidesanī sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',187,replace(replace('<p class="bodytext">sekkhasammatesu <a name="M1.0187"></a> āvuso kulesu khādanīyaṃ vā bhojanīyaṃ vā sahatthā paṭiggahetvā bhuñjantassa pāṭidesanīyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū namattaṃ jānitvā paṭiggahesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">Sekhiyakaṇḍa</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',188,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0188"></a> āvuso paṭicca purato vā pacchato vā olambentena nivāsentassaca pārupantassaca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū purato ca pacchato ca olambantā nivāsesuṃceva pārupiṃsuca, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca kāyaṃ vivaritvā antaraghare gacchantassa ca nisīdantassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggīye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kāyaṃ vivaritvā antaraghare gacchiṃsu ceva nisīdiṃsuca, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',189,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0189"></a> āvuso paṭicca hatthaṃ vā pādaṃ vā kīḷāpentena antaraghare gacchantassa ca nisīdantassa ca, tahaṃ tahaṃ olokentena antaraghare gacchantassa ca nisīdantassa ca, ukkhittakāya antaraghare gacchantassa ca nisīdantassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū hatthampi pādampi kīḷāpentā tahaṃ tahaṃpi olokentā antaraghare gacchiṃsuceva nisīdiṃsu ca, teyeva bhante chabbaggiyā bhikkhū ukkhittakāya antaraghare gacchiṃsuceva nisīdiṃsuca, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',190,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0190"></a> āvuso paṭicca ujjagghikāya vā uccāsaddaṃ mahāsaddaṃ karontena vā antaraghare gacchantassaca nisīdantassaca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū mahāhasitaṃ hasantā ca uccāsaddaṃ mahāsaddaṃ karontā ca antaraghare gacchiṃsuceva nisīdiṃsuca, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca kāyappacālakaṃ vā bāhuppa cālakaṃ vā sīsappacālakaṃ vā khambhakatena vā oguṇṭhitena vā antaraghare gacchantassa ca nisīdantassa ca ukkuṭikāya kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kāyappacālakaṃ bāhuppacālakaṃ sīsappacālakaṃ karontā ca khambhīkatā ca oguṇṭhitā ca antaraghare gacchiṃsuceva nisīdiṃsuca, ukkuṭikāyaca antaraghare gacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',191,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0191"></a> āvuso paṭicca pallatthikāya antaraghare nisinnassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū pallatthikāya antaraghare nisīdiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',192,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0192"></a> āvuso paṭicca asakkaccaṃ piṇḍapātaṃ paṭiggaṇhantassa ca tahaṃ tahaṃ olokentena piṇḍapātaṃ paṭiggaṇhantassaca sūpaññeva bahuṃ paṭiggaṇhantassaca thūpīkataṃ piṇḍapātaṃ paṭiggaṇhantassaca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū asakkaccaṃ piṇḍapātaṃ paṭiggahesuṃ, tahaṃ tahaṃ olokentā piṇḍapātaṃ paṭiggahesuṃ, sūpaññeva bahuṃ paṭiggahesuṃ, thūpīkataṃ piṇḍapātaṃ paṭiggahesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',193,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0193"></a> āvuso paṭicca āsakkaccaṃ piṇḍapātaṃ bhuñjantassaca tahaṃ tahaṃ olokentana piṇḍapātaṃ bhuñjantassa ca tahaṃ tahaṃ omasitvā piṇḍapātaṃ bhuñjantassa ca sūpaññeva bahuṃ bhuñjantassa ca thūpakato omadditvā piṇḍapātaṃ bhuñjantassa ca sūpaṃ vā byañjanaṃ vā odanena paṭicchādentassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū asakkaccaṃ piṇḍapātaṃ bhuñjiṃsu, tahaṃ tahaṃ olokentā piṇḍapātaṃ bhuñjiṃsu, teyeva bhante chabbaggiyā bhikkhū tahaṃ tahaṃ omasitvā piṇḍapātaṃ bhuñjiṃsu, teyeva bhante chabbaggiyā bhikkhū bahuṃ sūpaṃ bhuñjiṃsu, teyeva bhante chabbaggiyā bhikkhū thūpakato omadditvā piṇḍapātaṃ bhuñjiṃsu, teyeva bhante chabbaggiyā bhikkhū sūpampi byañjanampi odanena paṭicchādesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',194,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0194"></a> āvuso paṭicca sūpaṃvā odanaṃvā agilānena attano atthāya viññāpetvā bhuñjantassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū sūpampi odanampi attano atthāya viññāpetvā bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca ujjhānasaññinā paresaṃ pattaṃ olokentassa ca atimahantaṃ kabaḷaṃ karontassa ca dīghaṃ ālopaṃ karontassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',195,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0195"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū ujjhānasaññino paresaṃ pattaṃ olokesuṃ, teyeva bhante chabbaggiyā bhikkhū atimahantaṃ kabaḷaṃ kariṃsu, teyeva bhante chabbaggiyā bhikkhū dīghaṃ ālopaṃ kariṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca anāhaṭe kabaḷe mukhadvāraṃ vivarantassa ca bhuñjamāne sabbaṃ hatthaṃ mukhe pakkhipantassa ca sakabaḷena mukhena byāharantassa ca piṇḍukkhepakaṃ piṇḍapātaṃ bhuñjantassa ca kabaḷāvacchedakaṃ piṇḍapātaṃ bhuñjantassa ca avagaṇḍakārakaṃ piṇḍapātaṃ bhuñjantassa ca hatthaniddhunakaṃ sitthāvakārakaṃ jivhānicchārakaṃ capucapukārakaṃ piṇḍapātaṃ bhuñjantassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū anāhaṭe kabaḷe mukhadvāraṃ vivariṃsu, bhuñjamānā sabbaṃ hatthaṃ mukhe pakkhipiṃsu, sakabaḷena mukhena byāhariṃsu, piṇḍukkhepakaṃ bhuñjiṃsu, kabaḷāvacchedakaṃ bhuñjiṃsu, avagaṇḍakārakaṃ bhuñjiṃsu, hatthaniddhunakaṃ sitthāvakārakaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',196,replace(replace('<p class="bodytext">jivhānicchārakaṃ <a name="M1.0196"></a> capucapukārakaṃ bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca surusurukārakaṃ bhuñjantassaca hatthanillehakaṃvā pattanillehakaṃ vā oṭṭhanillehakaṃ vā bhuñjantassa ca sāmisena hatthena pānīyathālakaṃ paṭiggaṇhantassa ca sasitthakaṃ pattadhovanaṃ antaraghare caḍḍentassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> suru surukārakaṃ bhante bhuñjantassa dukkaṭaṃ kosambiyaṃ paññattaṃ, sambahule bhante bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū surusurukārakaṃ khīraṃ piviṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ. </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',197,replace(replace('<p class="bodytext">Hatthanillehakaṃ <a name="M1.0197"></a> bhuñjantassa ca pattanillehakaṃ bhuñjantassa ca oṭṭhanillehakaṃ bhuñjantassa dukkaṭaṃ sāvatthiyaṃ paññattaṃ, chabbaggiye bhante bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū hatthanillehakaṃ bhuñjiṃsu, pattanillehakaṃ bhuñjiṃsu, oṭṭhanillehakaṃ bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext">Sāmisena hatthena pānīyathālakaṃ paṭiggaṇhantassa ca sasitthakaṃ pattadhovanaṃ antaraghare chaḍḍentassa ca dukkaṭaṃ bhaggesu paññattaṃ, sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhikkhū sāmisena hatthena pānīyathālakaṃ paṭiggahesuṃ, teyeva bhante sambahulā bhikkhū sasitthakaṃ pattadhovanaṃ antaraghare chaḍḍesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',198,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0198"></a> āvuso paṭicca chattapāṇissa dhammaṃ desentassa ca daṇḍapāṇissa vā satthapāṇissa vā āvudhapāṇissa vā dhammaṃ desentassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū chattapāṇissa daṇḍapāṇissa satthapāṇissa āvudhapāṇissa dhammaṃ desesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> anādariyaṃ āvuso paṭicca pādukāruḷhassa ca upahanāruḷhassa ca yānagatassa ca sayanagatassa ca dhammaṃ desentassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū pādukāruḷhassa upahanāruḷhassa yānagatassa sayanagatassa dhammaṃ desesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',199,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0199"></a> āvuso paṭicca pallatthikāya nisinnassa ca veṭhitasīsassa ca oguṇṭhitasīsassa ca chamāyaṃ nisīditvā āsane nisinnassa ca nīce āsane nisīditvā ucce āsane nisinnassa ca ṭhitena nisinnassa ca pacchato gacchantena purato gacchantassa ca uppathena gacchantena pathena gacchantassa ca dhammaṃ desentassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū pallatthikāya nisinnassa ca veṭhitasīsassa ca oguṇṭhitasīsassa ca chamāyaṃ nisīditvā āsane nisinnassa ca nīce āsane nisīditvā ucce āsane nisinnassa ca ṭhitānisinnassa ca pacchato gacchantā purato gacchantassa ca uppathena gacchantā pathena gacchantassa ca dhammaṃ desesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',200,replace(replace('<p class="bodytext">anādariyaṃ <a name="M1.0200"></a> āvuso paṭicca ṭhitena uccāraṃ vā passāvaṃ vā karontassa ca harite vā udake vā uccāraṃ vā passāvaṃ vā kheḷaṃ vā karontassa ca dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū ṭhitā uccārampi passāvampi akaṃsu, teyeva bhante chabbaggiyā bhikkhū haritepi udakepi uccārampi passāvampi kheḷampi akaṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="title">Bhikkhunīvibhaṅga</p>\r\n<p class="subhead">5. Pañcama pārājika sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',202,replace(replace('<p class="bodytext">bhikkhunīnaṃ <a name="M1.0202"></a> āvuso pañcama pārājikaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sundarīnandaṃ bhikkhuniṃ ārabbha paññattaṃ, sundarīnandā bhante bhikkhunī avassutā avassutassa purisapuggalassa kāyasaṃsaggaṃ sādiyi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">Bhikkhunīvibhaṅga, saṅghādisesa sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ussayavādikāya āvuso bhikkhuniyā aḍḍaṃ karontiyā saṅghādiseso kattha paññatto, kaṃ ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante tullanandaṃ bhikkhuniṃ ārabbha paññatto, thullanandā bhante bhikkhunī ussayavādikā vihari, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',203,replace(replace('<p class="bodytext">coriṃ <a name="M1.0203"></a> āvuso vuṭṭhāpentiyā saṅghādiseso kattha paññatto, kaṃ ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññatto, thullanandā bhante bhikkhunī coriṃ vuṭṭhāpesi, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',204,replace(replace('<p class="bodytext">ekāya <a name="M1.0204"></a> āvuso gāmantaraṃ gacchantiyā saṅghādiseso kattha paññatto, kaṃ ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññatto, aññatarā bhante bhikkhunī ekā gāmantaraṃ gacchi, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> avassutāya āvuso bhikkhuniyā avassutassa purisapuggalassa hatthato khādanīyaṃvā bhojanīyaṃvā sahatthā paṭiggahetvā bhuñjantassa saṅghādiseso kattha paññatto, kaṃ ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sundarīnandaṃ bhikkhuniṃ ārabbha paññatto, sundarīnandā bhante bhikkhunī avassutā avassutassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',205,replace(replace('<p class="bodytext">purisapuggalassa <a name="M1.0205"></a> hatthato āmisaṃ paṭiggahesi tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kupitāya āvuso anattamanāya bhikkhuniyā yāvatatiyaṃ samanubhāsantiyā na paṭinisajjantiyā saṅghādiseso kattha paññatto, kaṃ ārabbha kismiṃ vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante caṇḍakāḷiṃ bhikkhuniṃ ārabbha paññatto, caṇḍakāḷī bhante bhikkhunī kupitā anattamanā evaṃ abhaṇi, buddhaṃ paccācikkhāmi, dhammaṃ paccācikkhāmi, saṅghaṃ paccācikkhāmi, sikkhaṃ paccācikkhāmi, kinnumāva samaṇiyo yā samaṇiyo sakyadhītaro, santaññāpi samaṇiyo lajjiniyo kukkuccikā sikkhākāmā, tāsāhaṃ santike brahmacariyaṃ carissāmīti, tasmiṃ bhante vatthusmiṃ paññatto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',206,replace(replace('<p class="bodytext">kassa <a name="M1.0206"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> atthi nukho āvuso ettha kocipi viraddhadoso.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> natthi bhante.</p>\r\n<p class="title">Bhikkhunī vibhaṅgapācittiya</p>\r\n<p class="subhead">1. Lasuṇavagga, 1. paṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',207,replace(replace('<p class="bodytext">yaṃ <a name="M1.0207"></a> tena āvuso bhagavatā…pe… sammāsambuddhena lakkhaṇaṃ khādantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī na mattaṃ jānitvā bahuṃ lasuṇaṃ harāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Lasuṇavagga, 6. chaṭṭha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhussa āvuso bhuñjamānassa annena vā pānena vā vidhūpanena vā upatiṭṭhantiyā bhikkhuniyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',208,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0208"></a> bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī bhikkhussa bhuñjamānassa pānīyena vidhūpanena ca upatiṭṭhati, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Lasuṇavagga, 8. aṭṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> uccāraṃvā āvuso passāvaṃvā saṅkāraṃvā vighāsaṃvā thirokuṭṭe chaḍḍentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī uccāraṃ tirokuṭṭe chaḍḍesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">1. Lasuṇavagga, 10. dasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',209,replace(replace('<p class="bodytext">naccaṃ <a name="M1.0209"></a> vā āvuso gītaṃ vā vāditaṃ vā dassanāya gacchantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo naccampi gītampi vāditampi dassanāya agamaṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',210,replace(replace('<p class="bodytext">bhikkhupana <a name="M1.0210"></a> āvuso naccaṃ vā gītaṃ vā vāditaṃ vā dassanāya gacchanto kaṃ nāma āpattiṃ āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dukkaṭaṃ bhante āpattiṃ āpajjati.</p>\r\n<p class="subhead">1. Paṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> rattandhakāre āvuso apadīpe purisena saddhiṃ ekenekāya santiṭṭhantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī rattandhakāre apadīpe purisena saddhiṃ ekenekā santiṭṭhi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Andhakāravagga, 2-3. dutiya, tatiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',211,replace(replace('<p class="bodytext">paṭicchanne <a name="M1.0211"></a> vā āvuso okāse ajjhokāse vā purisena saddhiṃ ekenekāya santiṭṭhantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī paṭicchanne okāse ajjhokāse ca purisena saddhiṃ ekenekā santiṭṭhi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Andhakāravagga, 8. aṭṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> duggahitena āvuso dūpadhāritena paraṃ ujjhāpentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī duggahitena dūpadhāritena paraṃ ujjhāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Andhakāravagga, 9. navama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',212,replace(replace('<p class="bodytext">attānaṃ <a name="M1.0212"></a> vā āvuso paraṃ vā nirayena vā brahmacariyena vā abhisapantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante caṇḍakāḷiṃ bhikkhuniṃ ārabbha paññattaṃ, caṇḍakāḷī bhante bhikkhunī attānampi parampi nirayenapi brahmacariyenapi abhisapi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">2. Andhakāravagga, 10. dasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',213,replace(replace('<p class="bodytext">attānaṃ <a name="M1.0213"></a> āvuso vadhitvā vadhitvā rodantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante caṇḍakāḷiṃ bhikkhuniṃ ārabbha paññattaṃ, caṇḍakāḷī bhante bhikkhunī bhikkhunīhi saddhiṃ bhaṇḍitvā attānaṃ vadhitvā vadhitvā rodi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Naggavagga, 3. tatiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',214,replace(replace('<p class="bodytext">bhikkhuniyā <a name="M1.0214"></a> āvuso cīvaraṃ visibbetvā vā visibbāpetvā vā neva sibbentiyā na sibbāpanāya ussukkaṃ karontiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī bhikkhuniyā cīvaraṃ visibbāpetvā neva sibbesi na sibbāpanāya ussukkaṃ akāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">3. Naggavagga, 8. aṭṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',215,replace(replace('<p class="bodytext">agārikassa <a name="M1.0215"></a> vā āvuso paribbājakassa vā paribbājikāya vā samaṇacīvaraṃ dentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī agārikassa samaṇacīvaraṃ adāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 1-2. paṭhama, dutiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dvinnaṃ āvuso bhikkhunīnaṃ ekamañce vā ekattharaṇapāvuraṇe vā tuvaṭṭentīnaṃ pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo dve ekamañce tuvaṭṭesuṃ, ekattharaṇapāvuraṇe tuvaṭṭesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 3. tatiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',216,replace(replace('<p class="bodytext">bhikkhuniyā <a name="M1.0216"></a> āvuso sañcicca aphāsuṃ karontiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī bhikkhuniyā sañcicca aphāsuṃ akāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 4. catuttha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',217,replace(replace('<p class="bodytext">dukkhitaṃ <a name="M1.0217"></a> āvuso sahajīviniṃ neva upaṭṭhentiyā na upaṭṭhāpanāya ussukkaṃ karontiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃyeva bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī dukkhitaṃ sahajīviniṃ nevaupaṭṭhesi na upaṭṭhāpanāya ussukkaṃ akāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 5. pañcama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',218,replace(replace('<p class="bodytext">bhikkhuniyā <a name="M1.0218"></a> āvuso upassayaṃ datvā kupitāya anattamanāya nikkaḍḍhantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃyeva bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī bhikkhuniyā upassayaṃ datvā kupitā anattamanā nikkaḍḍhi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 6. chaṭṭha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',219,replace(replace('<p class="bodytext">saṃsaṭṭhāya <a name="M1.0219"></a> āvuso bhikkhuniyā yāvatatiyaṃ samanubhāsanāya na paṭinissajjantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante caṇḍakāḷiṃ bhikkhuniṃ ārabbha paññattaṃ, caṇḍakāḷī bhante bhikkhunī saṃsaṭṭhā vihari, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 7-8. sattama aṭṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',220,replace(replace('<p class="bodytext">antoraṭṭhe <a name="M1.0220"></a> vā āvuso tiroraṭṭhe vā sāsaṅkasammate sappaṭibhaye asatthikāya cārikaṃ carantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo antoraṭṭhepi tiroraṭṭhepi sāsaṅkasammate sappaṭibhaye asatthikāyo cārikaṃ cariṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 9. navama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> antovassaṃ āvuso cārikaṃ carantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo antovassaṃ cārikaṃ pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">4. Tuvaṭṭavagga, 10. dasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',221,replace(replace('<p class="bodytext">vassaṃ <a name="M1.0221"></a> vuṭṭhāya āvuso bhikkhuniyā cārikaṃ na pakkamantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahulā bhikkhuniyo ārabbhaṃ paññattaṃ, sambahulā bhante bhikkhuniyo vassaṃvuṭṭhā cārikaṃ na pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',222,replace(replace('<p class="bodytext">kassa <a name="M1.0222"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante vacanaṃ.</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho…pe… saṃgāyeyyāma.</p>\r\n<p class="subhead">5. Cittāgāravagga, 1. paṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> yaṃ tena āvuso bhagavatā…pe… sammāsambuddhena rājāgāraṃ vā cittāgāraṃ vā ārāmaṃ vā uyyānaṃ vā pokkharaṇiṃ vā dassanāya gacchantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',223,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0223"></a> bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo rājāgārampi cittāgārampi dassanāya gacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Cittāgāravagga, 4. catuttha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> gihiveyyāvaccaṃ āvuso karontiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyogihi veyyāvaccaṃ akaṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Cittāgāravagga, 6. chaṭṭha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',224,replace(replace('<p class="bodytext">agārikassa <a name="M1.0224"></a> vā āvuso paribbājakassa vā paribbājikāya vā sahatthā khādanīyaṃ bhojanīyaṃ dentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī agārikassa sahatthā khādanīyampi bhojanīyampi adāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">5. Cittāgāravagga, 9-10. navama, dasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tiracchānavijjaṃ āvuso pariyāpuṇantiyā ca vācentiyā ca pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo tiracchānavijjaṃ pariyāpuṇiṃsuceva vācesuñca, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',225,replace(replace('<p class="bodytext">bhikkhu <a name="M1.0225"></a> pana āvuso tādisaṃ tiracchānavijjaṃ pariyāpuṇanto ca vācento ca kiṃ nāma āpattiṃ āpajjati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dukkaṭaṃ bhante āpattiṃ āpajjati.</p>\r\n<p class="subhead">6. Ārāmavagga, 1. paṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> jānaṃ āvuso sabhikkhukaṃ ārāmaṃ anāpucchā pavisantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo ārāmaṃ anāpucchā pavisiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 2. dutiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',226,replace(replace('<p class="bodytext">bhikkhuṃ <a name="M1.0226"></a> āvuso akkosantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo āyasmantaṃ upāliṃ akkosiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 3. tatiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',227,replace(replace('<p class="bodytext">caṇḍikatāya <a name="M1.0227"></a> āvuso gaṇaṃ paribhāsantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī caṇḍikatāya gaṇaṃ paribhāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 5. pañcama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',228,replace(replace('<p class="bodytext">kulaṃ <a name="M1.0228"></a> āvuso maccharāyantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuniṃ ārabbha paññattaṃ, aññatarā bhante bhikkhunī kulaṃ maccharāyi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 6. chaṭṭha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',229,replace(replace('<p class="bodytext">abhikkhuke <a name="M1.0229"></a> āvuso āvāse vassaṃ vasantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo abhikkhuke āvāse vassaṃ vasiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 7. sattama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',230,replace(replace('<p class="bodytext">vassaṃ <a name="M1.0230"></a> vuṭṭhāya āvuso bhikkhuniyā ubhatosaṅghe tīhi ṭhānehi na pavārentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo vassaṃ vuṭṭhā bhikkhusaṅghaṃ na pavāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 8. aṭṭhama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ovādāya vā āvuso saṃvāsāya vā nagacchantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo ovādaṃ nagacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">6. Ārāmavagga, 9. navama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',231,replace(replace('<p class="bodytext">uposathampi <a name="M1.0231"></a> āvuso na pucchantiyā ovādampi na yācantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo uposathaṃ na pucchiṃsu, ovādampi na yāciṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Gabbhinīvagga, 1-2. paṭhama, dutiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> gabbhiniṃ vā āvuso pāyantiṃ vā vuṭṭhāpentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',232,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M1.0232"></a> bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo gabbhiniṃ vuṭṭhāpesuṃ, pāyantiṃ vuṭṭhāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">7. Gabbhinīvagga, 3. tatiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dve vassāni āvuso chasu dhammesu asikkhitasikkhaṃ sikkhamānaṃ vuṭṭhāpentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo dve vassāni chasu dhammesu asikkhitasikkhaṃ sikkhamānaṃ vuṭṭhāpesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Kumārībhūtavagga, 9. navama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',233,replace(replace('<p class="bodytext">purisasaṃsaṭṭhaṃ <a name="M1.0233"></a> āvuso kumārakasaṃsaṭṭhaṃ caṇḍiṃ sokāvāsaṃ sikkhamānaṃ vuṭṭhāpentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī purisasaṃsaṭṭhaṃ kumārakasaṃsaṭṭhaṃ caṇḍiṃ sokāvāsaṃ caṇḍakāḷiṃ sekkhamānaṃ vuṭṭhāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">8. Kumārībhūtavagga, 10. dasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',234,replace(replace('<p class="bodytext">mātāpitūhi <a name="M1.0234"></a> vā āvuso sāmikena vā ananuññātaṃ sikkhamānaṃ vuṭṭhāpentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante thullanandaṃ bhikkhuniṃ ārabbha paññattaṃ, thullanandā bhante bhikkhunī mātāpitūhipi sāmikenapi ananuññātaṃ sikkhamānaṃ vuṭṭhāpesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Chattupāhanavagga, 1-2. paṭhama, dutiya sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> chattupāhanaṃ āvuso dhārentiyā ca yānena yāyantiyā ca pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo chattupāhanaṃ dhāresuṃ, yānena yāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Chattupāhanavagga, 4. catuttha sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',235,replace(replace('<p class="bodytext">itthālaṅkāraṃ <a name="M1.0235"></a> āvuso dhārentiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo itthālaṅkāraṃ dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhu pana āvuso purisālaṅkāraṃ dhārento kiṃ nāma āpattiṃ āpajjeyya.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dukkaṭaṃ bhante āpattiṃ āpajjeyya.</p>\r\n<p class="subhead">9. Chattupāhanavagga, 5. pañcama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',236,replace(replace('<p class="bodytext">gandhavaṇṇakena <a name="M1.0236"></a> āvuso nahāyantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo gandhavaṇṇakena nahāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Chattupāhanavagga, 11. ekādasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhussa āvuso purato anāpucchā āsane nisīdantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo bhikkhussa purato anāpucchā āsane nisīdiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">9. Chattupāhanavagga, 12. dvādasama sikkhāpucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',237,replace(replace('<p class="bodytext">anokāsakataṃ <a name="M1.0237"></a> āvuso bhikkhuṃ pañhaṃ pucchantiyā pācittiyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahulā bhikkhuniyo ārabbha paññattaṃ, sambahulā bhante bhikkhuniyo anokāsakataṃ bhikkhuṃ pañhaṃ pucchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sappiṃ vā āvuso telaṃ vā madhuṃ vā phāṇitaṃ vā macchaṃ vā maṃsaṃ vā khīraṃ vā dadhiṃ vā viññāpetvā bhuñjantiyā pāṭidesanīyaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyā bhikkhuniyo ārabbha paññattaṃ, chabbaggiyā bhante bhikkhuniyo sappimpi telampi madhumpi phāṇitampi macchampi maṃsampi khīrampi dadhimpi viññāpetvā bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',238,replace(replace('<p class="bodytext">catunnaṃ <a name="M1.0238"></a> āvuso vipattīnaṃ katamā vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ācāravipatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> channaṃ āvuso āpattisamuṭṭhānānaṃ katihi samuṭṭhānehi samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catūhi bhante samuṭṭhānehi samuṭṭhāti, siyā kāyato samuṭṭhāti, navācato nacittato, siyā kāyato ca vācato ca samuṭṭhāti, nacittato, siyā kāyato ca cittato ca samuṭṭhāti, navācato, siyā kāyato ca vācato ca cittato ca samuṭṭhāti, imehi bhante catūhi samuṭṭhānehi samuṭṭhāti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',239,replace(replace('<p class="bodytext">kā <a name="M1.0239"></a> āvuso vipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> asaṃvaro bhante vipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kā āvuso sampatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṃvaro bhante sampatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',240,replace(replace('<p class="bodytext">kā <a name="M1.0240"></a> āvuso paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> na evarūpaṃ karissāmīti yāvajīvaṃ āpāṇakoṭikaṃ samādāya sikkhati sikkhāpadesu, ayaṃ bhante paṭipatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>\r\n<p class="bodytext">Suṇātu me bhante saṅgho…pe… saṃgāyeyyāma.</p>\r\n<p class="chapter">Mahāvaggapāḷi</p>\r\n<p class="subhead">Saṃgāyanassa pucchā vissajjanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',1,replace(replace('<p class="bodytext">ubhato <a name="M2.0001"></a> vibhaṅgānantaraṃ āvuso porāṇakehi saṃgītikāramahātherehi kaṃ nāma pāvacanaṃ saṃgītaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ubhato vibhaṅgānantaraṃ bhante porāṇakehi saṃgītikāramahātherehi khandhakā saṃgītā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> te pana āvuso khandhakā vaggabhedena katividhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',2,replace(replace('<p class="bodytext">te <a name="M2.0002"></a> pana bhante khandhakā duvidhā vaggabhedena mahāvaggo cūḷavaggoti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahāvagge āvuso kati khandhakā pariyāpannā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mahāvagge bhante dasakhandhakā pariyāpannā, seyyathidaṃ, mahākhandhako uposathakkhandhako vassūpanāyikakkhandhako pavāraṇākkhandhako cammakkhandhako bhesajjakkhandhako kathinakkhandhako cīvarakkhandhako campeyyakkhandhako kosambakakkhandhakoti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> mahākhandhake āvuso bahū kathāyo, katamā tāsaṃ ādikathā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mahākhandhake bhante bodhikathā ādi.</p>\r\n<p class="subhead">Mahākhandhaka</p>\r\n<p class="subhead">Mahābodhipucchā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',3,replace(replace('<p class="bodytext">tasmātiha <a name="M2.0003"></a> āvuso tato paṭṭhāya taṃ pucchissāmi, bhagavā āvuso paṭhamābhisambuddhakāle kattha kīvacīraṃ kenākārena vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavā bhante paṭhamābhisambuddho bodhirukkhamūle sattāhaṃ ekapallaṅkena vihāsi vimuttisukhaṃ paṭisaṃvedī.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tadā āvuso bhagavā kīdisaṃ dhammaṃ manasikatvā kīdisaṃ udānaṃ udānesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tadā bhante bhagavā rattiyā paṭhamaṃ yāmaṃ rattiyā majjhimaṃ yāmaṃ rattiyā pacchimaṃ yāmaṃ paṭiccasamuppādaṃ anulomaṃ paṭilomaṃ manasikatvā tīṇi udānāni udānesi.</p>\r\n<p class="subhead">Ajapāla</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',4,replace(replace('<p class="bodytext">tassa <a name="M2.0004"></a> āvuso sattāhassa accayena bhagavā kattha vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tassa bhante sattāhassa accayena bhagavā ajapāla nigrodhamūle vihāsi, sattāhaṃ ekapallaṅkena vihāsi vimuttisukhaṃ paṭisaṃvedī.</p>\r\n<p class="subhead">Mucalindā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tassapi āvuso sattāhassa accayena bhagavā kattha vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',5,replace(replace('<p class="bodytext">tassa <a name="M2.0005"></a> bhante sattāhassa accayena bhagavā mucalindamūle sattāhaṃ ekapallaṅkena nisīdi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tassapi āvuso sattāhassa accayena bhagavā kattha vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tassapi bhante sattāhassa accayena bhagavā rājāyatanamūle sattāhaṃ ekapallaṅkena nisīdi vimuttisukhaṃ paṭisaṃvedī.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',6,replace(replace('<p class="bodytext">tassapi <a name="M2.0006"></a> āvuso sattāhassa accayena bhagavā kattha vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tassa bhante sattāhassa accayena bhagavā punadeva ajapālanigrodhamūle vihāsi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tasmiṃ pana āvuso ajapālanigrodhe viharantassa bhagavato kathaṃ cetaso parivitakko udapādi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃ pana bhante ajapālanigrodhe viharantassa bhagavato rahogatassa paṭisallīnassa dhammagambhīratāpaṭisaṃyutto cetaso parivitakko udapādi.</p>\r\n<p class="indent">Adhigato kho myāyaṃ dhammo gambhīro duddaso aranubodho santo paṇīto atakkāvacarā nipuṇo paṇḍitavedanīyo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',7,replace(replace('<p class="bodytext">Ālayarāmākho <a name="M2.0007"></a> panāyaṃ pajā ālayaratā ālayasammuditā,</p>\r\n<p class="subhead">Sahampati brahmā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso tadā dhammadesanāya brahmayācanā ca bhagavato paṭiññāca ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> desetu bhante bhagavā dhammaṃ, desetu sugato dhammaṃ, santi sattā apparajakkhajātikā assavanatā dhammassa parihāyanti, bhavissanti dhammassa aññātāroti iti bhante brahmuno dhammadesanāya yācanā ahosi,</p>\r\n<p class="gatha1">‘‘Apārutā tesaṃ amatassa dvārā,</p>\r\n<p class="gatha2">Ye sotavanto pamuñcantu saddhaṃ;</p>\r\n<p class="gatha3">Vihiṃsasaññī paguṇaṃ nabhāsiṃ,</p>\r\n<p class="gathalast">Dhammaṃ paṇītaṃ manujesu brahme’’ti.</p>\r\n<p class="bodytext">Evaṃ kho bhante bhagavato dhammadesanāya paṭiññā ca ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',8,replace(replace('<p class="bodytext">Nassati <a name="M2.0008"></a> vata loko…</p>\r\n<p class="bodytext">Desetu bhante bhagavā dhammaṃ…</p>\r\n<p class="gatha1">Apārutā tesaṃ amatassa dvārā,</p>\r\n<p class="gathalast">Ye sotavanto pamuñcantu saddhaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',9,replace(replace('<p class="bodytext">evaṃ <a name="M2.0009"></a> kho āvuso bhagavā dhammadesanāya paṭiññaṃ katvā paṭhamaṃ dhammaṃ desento kattha kīdisaṃ dhammaṃ kassa desesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante isipatane migadāye pañcavaggiya bhikkhupamukhānaṃ brahmagaṇānaṃ dhammacakkapavattanasuttaṃ desesi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',10,replace(replace('<p class="bodytext">tasmiṃ <a name="M2.0010"></a> kho pana āvuso dhammacakkapavattanasutte desiyamāne manussaloke kassa dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmato bhante koṇḍaññassa dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ nu kho āvuso imasmiṃ buddhasāsane paṭhamaṃ pabbajjā ca upasampadā ca ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> imasmiṃ bhante buddhasāsane paṭhamaṃ ehibhikkhupasampadā ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',11,replace(replace('<p class="indent">Labheyyāhaṃ <a name="M2.0011"></a> bhante bhagavato santike pabbajaṃ, labheyyaṃ upasampadaṃ.</p>\r\n<p class="indent">Ehi bhikkhu svākhāto dhammo, cara brahmacariyaṃ sammā dukkhassa antakiriyāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> itaresaṃ pana āvuso catunnaṃ pañcavaggiyānaṃ kadā dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pāṭipade bhante divase āyasmato vappassa, dutiye bhante divase āyasmato bhaddiyassa, tatiye bhante divase āyasmato mahānāmassa, catutthe bhante divase āyasmato assajissa dhammābhisamayo ahosi.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',12,replace(replace('<p class="bodytext">sabbesampi <a name="M2.0012"></a> āvuso tesaṃ pañcavaggiyānaṃ bhikkhūnaṃ kadā arahattapatti hoti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbesampi bhante tesaṃ pañcavaggiyānaṃ bhikkhūnaṃ pakkhassa pañcamiyaṃ anattalakkhaṇasutte desiyamāne arahattapatti hoti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kadā āvuso bhagavatā bhikkhū tattha tattha cārikaṃ caritvā dhammadesanatthāya pesitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yasappamukhānaṃ bhante catupaññāsa sahāyakānaṃ pabbajitakāle bhagavatā bhikkhū tattha tattha cārikaṃ caritvā dhammadesanatthāya pesitā.</p>\r\n<p class="subhead">Saraṇagamana</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',13,replace(replace('<p class="bodytext">kadā <a name="M2.0013"></a> āvuso bhagavatā tīhi saraṇagamanehi pabbajjūpa sampadā anuññāto ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yasappamukhānaṃ bhante catupaññāsāya gihi sahāyakānaṃ pabbajitvā tattha tattha cārikaṃ caritvā dhammadesanatthāya pesitakāle bhagavatā tīhi saraṇagamanehi pabbajjā ca upasampadā ca anuññātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',14,replace(replace('<p class="indent">Anujānāmi <a name="M2.0014"></a> bhikkhave tumheva dāni tāsu tāsu disāsu tesu tesu janapadesu pabbājetha upasampādetha.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhagavatā āvuso bhikkhūnaṃ ārāmo paṭiggaṇhituṃ kattha kismiṃ vatthusmiṃ anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavatā bhante bhikkhūnaṃ ārāmaṃ paṭiggaṇhituṃ rājagahe anuññāto, rājā bhante māgadho seniyo bimbisāro buddhappamukhassa bhikkhusaṅghassa veḷuvanaṃ uyyānaṃ adāsi, tasmiṃ bhante vatthusmiṃ anuññāto.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',15,replace(replace('<p class="indent">Etāhaṃ <a name="M2.0015"></a> bhante veḷuvanaṃ uyyānaṃ buddhappamukhassa bhikkhusaṅghassa dammi –</p>\r\n<p class="bodytext">Anujānāmi bhikkhave ārāmaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',16,replace(replace('<p class="bodytext">kathañca <a name="M2.0016"></a> āvuso dvinnaṃ aggasāvakānaṃ pabbajjūpasampadā ahosi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dvinnaṃ bhante aggasāvakānaṃ ehi bhikkhupasampadā ahosi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',17,replace(replace('<p class="bodytext">Vippasannāni <a name="M2.0017"></a> kho te āvuso indriyāni.</p>\r\n<p class="bodytext">Kaṃsi tvaṃ āvuso uddissa pabbajito.</p>\r\n<p class="bodytext">Ko vā te satthā.</p>\r\n<p class="bodytext">Kassa vā tvaṃ dhammaṃ rocesi.</p>\r\n<p class="bodytext">Atthāvuso mahāsamaṇo sakyaputto sakyakulā pabbajito.</p>\r\n<p class="bodytext">Kiṃ vādī panāyasmato satthā kimakkhāyī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',18,replace(replace('<p class="gatha1">Ye <a name="M2.0018"></a> dhammā hetuppabhavā, tesaṃ hetuṃ tathāgato;</p>\r\n<p class="gathalast">Āha tesañca yo nirodho, evaṃ vādī mahāsamaṇo.</p>\r\n<p class="bodytext">Alaṃ āvuso mā agamittha.</p>\r\n<p class="bodytext">Ete bhikkhave dve sahāyakā āgacchanti kolito upatisso ca etaṃ me sāvakayugaṃ bhavissati aggaṃ bhaddayugaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',19,replace(replace('<p class="bodytext">Labheyyāma <a name="M2.0019"></a> mayaṃ bhante bhagavato santike pabbajjaṃ labheyyāma upasampadaṃ.</p>\r\n<p class="bodytext">Etha bhikkhavo svākhāto dhammo, caratha brahmacariyaṃ sammā dukkhassa antakiriyāya.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> upajjhāyo āvuso gaṇhituṃ bhagavatā kattha anuññāto, kaṃ ārabbha kismiṃ vatthusmiṃ anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññāto, sambahulā bhante bhikkhū anupajjhāyakā anācariyakā anovadiyamānā ananusāsiyamānā dunnivatthā duppārutā anokappasampannā piṇḍāya cariṃsu, tasmiṃ bhante vatthusmiṃ anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> upajjhāyamhi āvuso na sammāvattantassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',21,replace(replace('<p class="bodytext">rājagahe <a name="M2.0021"></a> bhante sambahule saddhivihārike ārabbha paññattaṃ, sambahulā bhante saddhivihārikā upajjhāyamhi na sammāvattiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kadā āvuso bhagavatā tīhi saraṇagamanehi upasampadaṃ paṭikkhipitvā ñāticatutthena kammena upasampadā anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rādhabrāhmaṇassa bhante pabbajitakāle bhagavatā tīhi saraṇagamanehi upasampadaṃ paṭikkhipitvā ñātticatutthena kammena upasampadā anuññātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',22,replace(replace('<p class="bodytext">Konu <a name="M2.0022"></a> kho bhikkhave tassa brāhmaṇassa adhikāraṃ sarati.</p>\r\n<p class="bodytext">Ahaṃ kho bhante tassa brāhmaṇassa adhikāraṃ sarāmi.</p>\r\n<p class="bodytext">Sādhu sādhu sāriputta, kataññuno hi sāriputta sappurisā katavedino.</p>\r\n<p class="bodytext">Kathāhaṃ bhante taṃ brāhmaṇaṃ upasampādemi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',23,replace(replace('<p class="bodytext">Yāsā <a name="M2.0023"></a> bhikkhave mayā tīhi saraṇagamanehi upasampadāanuññātā, taṃ ajjatagge paṭikkhipāmi.</p>\r\n<p class="bodytext">Anujānāmi bhikkhave ñātticatutthena kammena upasampādetuṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ayācitakaṃ āvuso upasampadāpekkhaṃ upasampādentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu upasampannasamanantaraṃ anācāraṃ acari, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',24,replace(replace('<p class="bodytext">catunnaṃ <a name="M2.0024"></a> āvuso nissayānaṃ ācikkhaṇā bhagavatā kattha anuññātā, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante aññataraṃ bhikkhuṃ ārabbha anuññātā, aññataro bhante bhikkhu udarassa kāraṇā pabbaji, tasmiṃ bhante vatthusmiṃ anuññātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',25,replace(replace('<p class="bodytext">Ehi <a name="M2.0025"></a> dāni āvuso piṇḍāya carissāma.</p>\r\n<p class="bodytext">Kathañhi nāma bhikkhu evaṃ svākkhāte dhammavinaye udarassa kāraṇā pabbajissati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',26,replace(replace('<p class="bodytext">ūnadasavassena <a name="M2.0026"></a> āvuso upasampādentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ upasenaṃ vaṅgantaputtaṃ ārabbha paññattaṃ, āyasmā bhante upaseno vaṅgantaputto ekavasso saddhivihārikaṃ upasampādesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',27,replace(replace('<p class="bodytext">bālena <a name="M2.0027"></a> āvuso abyattena upasampādentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū ‘‘dasavassamhā dasavassamhā’’ti bālā abyattā upasampādesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',28,replace(replace('<p class="bodytext">ācariyo <a name="M2.0028"></a> āvuso gaṇhituṃ bhagavatā kattha anuññāto, kaṃ ārabbha kismiṃ vatthusmiṃ anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññāto, sambahulā bhante bhikkhū upajjhāyesu pakkantesupi vibbhantesupi kālaṅkatesupi pakkhasaṅkantesupi anācariyakā anovadiyamānā ananusāsiyamānā dunnivatthā duppārutā anākappasampannā piṇḍāya cariṃsu, tasmiṃ bhante vatthusmiṃ anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',29,replace(replace('<p class="bodytext">pañcahi <a name="M2.0029"></a> āvuso ābādhehi phuṭṭhaṃ pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū pañcahi ābādhehi phuṭṭhaṃ pabbājesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',30,replace(replace('<p class="bodytext">Sādhu <a name="M2.0030"></a> bhante ayyā pañcahi ābādhehi phuṭṭhaṃ na pabbājeyyuṃ…</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> rājabhaṭaṃ āvuso pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū rājabhaṭe pabbājesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',31,replace(replace('<p class="bodytext">dhajabandhaṃ <a name="M2.0031"></a> āvuso coraṃ pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagaheyeva bhante aṅgulimālaṃ coraṃ ārabbha paññattaṃ, aṅgulimālo bhante coro bhikkhūsu pabbajito hoti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kārabhedakaṃ āvuso coraṃ pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',32,replace(replace('<p class="bodytext">rājagaheyeva <a name="M2.0032"></a> bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū kārabhedakacoraṃ pabbājesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> iṇāyikaṃ āvuso pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagaheyeva bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū iṇāyikaṃ pabbājesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',33,replace(replace('<p class="bodytext">dāsaṃ <a name="M2.0033"></a> āvuso pabbajentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagaheyeva bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū dāsaṃ pabbājesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhaṇḍukammāya āvuso apalokanaṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagaheyeva bhante aññataraṃ kammārabhaṇḍuputtaṃ ārabbha anuññātaṃ aññataro bhante kammārabhaṇḍuputto mātāpitūhi saddhiṃ bhaṇḍitvā bhikkhūsu pabbajito hoti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',34,replace(replace('<p class="bodytext">ūnavīsativassassa <a name="M2.0034"></a> āvuso puggalassa upasampadā bhagavatā kattha paṭikkhittā, kaṃ ārabbha kismiṃ vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',35,replace(replace('<p class="bodytext">rājagahe <a name="M2.0035"></a> bhante sambahule bhikkhū ārabbha paṭikkhittā, sambahulā bhante bhikkhū ūnavīsativassaṃ puggalaṃ upasampādesuṃ, tasmiṃ bhante vatthusmiṃ paṭikkhittā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',36,replace(replace('<p class="bodytext">Na <a name="M2.0036"></a> bhikkhave jānaṃ ūnavīsativasso puggalo upasampādetabbo, youpasampādeyya, yathādhammo kāretabbo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',37,replace(replace('<p class="bodytext">mātāpitūhi <a name="M2.0037"></a> āvuso ananuññātaṃ puttaṃ pabbājentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sakkesu bhante paññattaṃ, rājā bhante suddhodano bhagavantaṃ upasaṅkamitvā varaṃ yāci, sādhu bhante ayyāananuññātaṃ mātāpitūhi puttaṃ pabbājeyyunti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',39,replace(replace('<p class="bodytext">Na <a name="M2.0039"></a> bhikkhave ananuññāto mātāpitūhi putto pabbājetabbo, yo pabbājeyya, āpattidukkaṭassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> theyyasaṃvāsakassa āvuso upasampadā bhagavatā kattha paṭikkhittā, kaṃ ārabbha kismiṃ vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ purāṇakulaputtaṃ ārabbha paṭikkhittā, aññataro bhante purāṇakulaputto khīṇa kolañño sāmaṃ pattacīvaraṃ paṭiyādetvā kesamassuṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',40,replace(replace('<p class="bodytext">ohāretvā <a name="M2.0040"></a> kāsāyāni vatthāni acchādetvā ārāmaṃ gantvā bhikkhūhi saddhiṃ saṃvasi, bhante vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',41,replace(replace('<p class="bodytext">tiracchānagatassa <a name="M2.0041"></a> āvuso upasampadā bhagavatā kattha paṭikkhittā, kaṃ ārabbha kismiṃ vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ nāgaṃ ārabbha paṭikkhittā, aññataro bhante nāgo māṇavakavaṇṇena bhikkhū upasaṅkamitvā pabbajjaṃ yāci, taṃ bhikkhū pabbājesuṃ upasampādesuṃ, tasmiṃ bhante vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',42,replace(replace('<p class="bodytext">mātughātakassa <a name="M2.0042"></a> ca āvuso pitughātakassa ca upasampadā bhagavatā kattha paṭikkhittā, kaṃ ārabbha kismiṃ vatthusmiṃ paṭikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',43,replace(replace('<p class="bodytext">sāvatthiyaṃyeva <a name="M2.0043"></a> bhante aññataraṃ māṇavakaṃ ārabbha paṭikkhittā, aññataro ca bhante māṇavako aññataro ca māṇavako mātaraṃ jīvitā voropesi pitaraṃ jīvitā voropesi, tasmiṃ bhante vatthusmiṃ paṭikkhittā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',44,replace(replace('<p class="bodytext">Mātughātako <a name="M2.0044"></a> bhikkhave anupasampanno naupasampādetabbo, upasampanno nāsetabbo, pitughātako bhikkhave anupasampanno naupasampādetabbo, upasampanno nāsetabbo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> apattacīvarakaṃ vā āvuso yācitapattacīvarakaṃ vā upasampādentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū apattacīvarakaṃ upasampādesuṃ, yācitakena pattacīvarena upasampādesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',45,replace(replace('<p class="bodytext">upasampadaṃ <a name="M2.0045"></a> pucchissaṃ, sanidānaṃ sauddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> upasampadaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante dve āpattiyo.</p>\r\n<p class="subhead">2. Uposathakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',46,replace(replace('<p class="bodytext">cātuddase <a name="M2.0046"></a> ca āvuso pannarase ca pakkhassa ca aṭṭhamiyā sannipatitvā dhammaṃ bhāsituṃ bhagavatā kattha anuññātaṃ, kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātā, sambahulā bhante bhikkhū cātuddesa pannarase aṭṭhamiyā ca pakkhassa sannipatitvā tuṇhī nisīdiṃsu, tasmiṃ bhante vatthusmiṃ anuññātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',47,replace(replace('<p class="bodytext">Anujānāmi <a name="M2.0047"></a> bhikkhave cātuddase pannarase aṭṭhamiyā ca pakkhassa sannipatituṃ.</p>\r\n<p class="bodytext">Nanu nāma sannipatitehi dhammo bhāsitabbo.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kadā āvuso bhikkhūnaṃ pātimokkhuddeso bhagavatā anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yadā bhante bhagavato rahogatassa paṭisallīnassa evaṃ cetaso parivitakko udapādi ‘‘yaṃnūnāhaṃ yāni mayā bhikkhūnaṃ paññattāni sikkhāpadāni, tāni nesaṃ pātimokkhuddesaṃ anujāneyyaṃ, so nesaṃ bhavissati </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',48,replace(replace('<p class="bodytext">uposathakamma’’nti <a name="M2.0048"></a> tadā bhante bhagavatā bhikkhūnaṃ pātimokkhuddeso anuññāto.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sīmaṃ āvuso sammanituṃ bhagavatā kattha anuññātaṃ, kismiñca vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante anuññātaṃ, rājagahe bhante bhagavati viharati bhikkhūnaṃ etadahosi ‘‘bhagavatā paññattaṃ ettāvatā sāmaggī, yāvatā ekāvāsoti kittāvatānukho ekāvāso hotī’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',49,replace(replace('<p class="bodytext">kati <a name="M2.0049"></a> āvuso uposathā divasavasena ca kārakapuggalavasena ca kātabbākāravasena ca.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> divasavasena bhante tayo uposathā, puggalavasena ca bhante tayo uposathā, kātabbākāravasena ca bhante tayo uposathā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',50,replace(replace('<p class="bodytext">kati <a name="M2.0050"></a> āvuso uposathakammāni tesu ca kīdisaṃ uposathakammaṃ bhagavatā anuññātaṃ, kīdisaṃ ananuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cattārimāni bhante uposathakammāni, adhammena vaggaṃ uposathakammaṃ, adhammena samaggaṃ uposathakammaṃ, dhammena vaggaṃ uposathakammaṃ, dhammena samaggaṃ uposathakammaṃ. Tatra bhante yadidaṃ adhammena vaggaṃ uposathakammaṃ, adhammena samaggaṃ uposathakammaṃ dhammena vaggaṃ uposathakammaṃ, evarūpaṃ bhante uposathakammaṃ ananuññātaṃ, tatra bhante yadidaṃ dhammena samaggaṃ uposathakammaṃ, evarūpaṃ bhante uposathakammaṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',51,replace(replace('<p class="bodytext">uposathaṃ <a name="M2.0051"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> uposathaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">3. Vassupanāyikakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> vassūpanāyikaṃ āvuso bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahulā bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū hemantampi gimhampi vassampi cārikaṃ cariṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> katipanāvuso vassūpanāyikā bhagavatā anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dvemā bhante vassūpanāyikā bhagavatā anuññātā purimikā pacchimikā, aparajjugatāya bhante āsaḷhiyā purimikā upagantabbā, māsagatāya bhante āsaḷhiyā pacchimikā upagantabbā, imā kho bhante dve vassūpanāyikā bhagavatā anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',54,replace(replace('<p class="bodytext">vassaṃ <a name="M2.0054"></a> āvuso upagantvā purimaṃ vā temāsaṃ pacchimaṃ vā temāsaṃ avasitvā cārikaṃ pakkamantassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū vassaṃ upagantvā antarā vassaṃ cārikaṃ pakkamiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> sattāha karaṇīyena āvuso pahite gantuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū udenena upāsakena pahite na gacchiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',56,replace(replace('<p class="bodytext">sattāhakaraṇīyena <a name="M2.0056"></a> āvuso sattannaṃ apahitepi gantuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññatarassa bhikkhuno mātugilāna vatthusmiṃ anuññātaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',57,replace(replace('<p class="bodytext">Anujānāmi <a name="M2.0057"></a> bhikkhave sattannaṃ sattāhakaraṇīyena apahitepi gantuṃ, pageva pahite.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kismiṃci āvuso antarāye sati antovassaṃ pakkamituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū kosalesu janapadesu vassaṃ upagacchiṃsu, te vassūpagatā vāḷehi ubbāḷhā ahesuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',58,replace(replace('<p class="bodytext">rukkha <a name="M2.0058"></a> susire vā āvuso rukkhaviṭabhiyā vā ajjhokāse vā vassaṃ upagacchantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū rukkhasusirepi rukkhaviṭabhiyāpi ajjhokāsepi vassaṃ upagacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',59,replace(replace('<p class="bodytext">asenāsanikena <a name="M2.0059"></a> āvuso vassaṃ upagacchantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tasmiṃyeva bhante sāvatthiyaṃ sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū asenāsanikā vassaṃ upagacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> chavakuṭikāya vā āvuso chatte vā cāṭiyā vā vassaṃ upagacchantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃyeva bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū chavakuṭikāyapi chattepi cāṭiyāpi vassaṃ upagacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',60,replace(replace('<p class="bodytext">paṭissavaṃ <a name="M2.0060"></a> āvuso visaṃ vādentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto rañño pasenadissa kosalassa vassaṃ vāsaṃ paṭissuṇitvā visaṃvādesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',61,replace(replace('<p class="bodytext">vassūpanāyikaṃ <a name="M2.0061"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vassūpanāyikaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">4. Pavāraṇākkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',63,replace(replace('<p class="bodytext">vassaṃ <a name="M2.0063"></a> vuṭṭhānaṃ āvuso bhikkhūnaṃ tīhi ṭhānehi pavārituṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū sandiṭṭhā sambhattā antovassaṃ neva ālapiṃsu na sallapiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',64,replace(replace('<p class="bodytext">Anujānāmi <a name="M2.0064"></a> bhikkhave vassaṃ vuṭṭhānaṃ bhikkhūnaṃ tīhi ṭhānehi pavāretuṃ, diṭṭheva vā sutena vā parisaṅkāya vā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kati āvuso pavāraṇā divasavasena ca puggalavasena ca kātabbākāravasena ca.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> divasavasena bhante tisso pavāraṇā, tathā puggalavasena kātabbākāravasenaca.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',65,replace(replace('<p class="bodytext">katīnaṃ <a name="M2.0065"></a> āvuso saṅghe pavāretuṃ bhagavatā anuññātaṃ, katīnaṃ panāvuso aññamaññaṃ pavāretuṃ bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcannaṃ bhante bhikkhūnaṃ saṅghe pavāretuṃ bhagavatā anuññātaṃ, catunnaṃ vā bhante tiṇṇaṃ vā dvinnaṃ vā aññamaññaṃ pavāretuṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ekena panāvuso vassaṃvuṭṭhena bhikkhunā kathaṃ paṭipajjitabbanti bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekena pana bhante bhikkhunā ‘‘ajja me pavāraṇā’’ti adhiṭṭhātabbanti bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',66,replace(replace('<p class="bodytext">pavāraṇaṃ <a name="M2.0066"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pavāraṇaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">5. Cammakkhandhaka</p>\r\n<p class="subhead">Soṇamathera vatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',67,replace(replace('<p class="bodytext">upāhanaṃ <a name="M2.0067"></a> āvuso bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ soṇaṃ ārabbha anuññātaṃ, āyasmato bhante soṇassa accāraddhavīriyassa caṅkamato pādā bhijjiṃsu, caṅkamo lohitena phuṭo ahosi, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',70,replace(replace('<p class="bodytext">sabbanīlikā <a name="M2.0070"></a> vā āvuso sabbapītikā vā sabbalohitikā vā sabbamañjiṭṭhikā vā sabbakaṇhā vā sabbamahāraṅgarattā vā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',71,replace(replace('<p class="bodytext">sabbamahānāmarattā <a name="M2.0071"></a> vā upāhanāyo dhārentassa dukkaṭaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū sabbanīlikāyo upāhanāyo dhāresuṃ, sabbapītikāyo sabbalohitikāyo sabbamañjiṭṭhikāyo sabbakaṇhāyo sabbamahāraṅgarattā sabbamahānāmarattāyopi upāhanāyo dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',72,replace(replace('<p class="bodytext">khallakabandhā <a name="M2.0072"></a> vā āvuso puṭabandhā vā pāliguṇṭhimā vā tūlapuṇṇikā vā tittirapattikā vā meṇḍavisāṇavaddhikā vā ajavisāṇavaddhikā vā vicchikāḷikā vā morapiñcha parisibbikā vā citrā vā upāhanāyo dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū khallakabandhā upāhanāyopi dhāresuṃ, puṭabandhā upāhanāyopi dhāresuṃ, pāliguṇṭhimā upāhanāyopi dhāresuṃ, tūlapuṇṇikā upāhanāyopi dhāresuṃ, tittirapattikā upāhanāyopi dhāresuṃ, meṇḍavisāṇavaddhikā upāhanāyopi dhāresuṃ, ajavisāṇavaddhikā upāhanāyopi dhāresuṃ, vicchikāḷikā upāhanāyopi dhāresuṃ, morapiñcha parisibbitā upāhanāyo dhāresuṃ, citrā upāhanāyopi dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',73,replace(replace('<p class="bodytext">kaṭṭhapādukāyo <a name="M2.0073"></a> āvuso dhārentassa dukkaṭaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggīyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū rattiyā paccūsasamayaṃ paccuṭṭhāya kaṭṭhapādukāyo abhiruhitvā ajjhokāse caṅkamanti uccāsaddā mahāsaddā khaṭakhaṭasaddā anekavihitaṃ tiracchānakathaṃ kathentā, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',74,replace(replace('<p class="bodytext">yānena <a name="M2.0074"></a> āvuso yāyantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū yānena yāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',75,replace(replace('<p class="bodytext">gilānassa <a name="M2.0075"></a> āvuso yānaṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ gilānaṃ bhikkhuṃ ārabbha anuññātaṃ, aññataro bhante bhikkhu kosalesu janapade sāvatthiṃ gacchanto bhagavantaṃ dassanāya antarāmagge gilāno kukkuccāyanto yānaṃ nābhiruhi, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',76,replace(replace('<p class="bodytext">uccāsayanamahāsayanāni <a name="M2.0076"></a> āvuso dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū uccāsayanamahāsayanāni dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> gocammāni āvuso dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ pāpabhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu pāṇātipāte samādapesi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',78,replace(replace('<p class="bodytext">paccantimesu <a name="M2.0078"></a> āvuso janapadesu vinayadharapañcamena gaṇena upasampadā ca guṇaṅguṇūpahanañca dhuvanahānañca cammāni attharaṇāni ca nissīmagatānaṃ cīvaradānañcāti imāni pañca bhagavatā kattha anuññātāni, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ soṇaṃ kuṭikaṇṇaṃ ārabbha anuññātā, āyasmā bhante soṇo kuṭikaṇṇo etāni pañcavatthūni āyasmato mahākaccānassa vacanena bhagavantaṃ yāci, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',81,replace(replace('<p class="bodytext">cammasaññuttaṃ <a name="M2.0081"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cammasaññuttaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">6. Bhesajjakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',82,replace(replace('<p class="bodytext">pañca <a name="M2.0082"></a> āvuso bhesajjāni paṭiggahetvā kālepi vikālepi paribhuñjituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulānaṃ bhante bhikkhūnaṃ sāradikena ābādhena puṭṭhānaṃ yāgupi na sammā pariṇāmaṃ gacchi, bhattampi bhuttaṃ na sammā pariṇāmaṃ gacchi, te tena kisā ahesuṃ lūkhā dubbaṇṇā uppaṇḍuppaṇḍukajātā dhamanisanthatagattā, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',83,replace(replace('<p class="bodytext">mūlādibhesajjāni <a name="M2.0083"></a> āvuso bhagavatā kattha anuññātāni, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule gilāne bhikkhū ārabbha anuññātāni, sambahulānaṃ gilānānaṃ bhikkhūnaṃ mūlehi bhesajjehi attho ahosi, tasmiṃ bhante vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> antovuṭṭhaṃvā āvuso antopakkaṃ vā sāmaṃpakkaṃ vā paribhuñjantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ ānandaṃ ārabbha paññattaṃ, āyasmā bhante ānando sāmaṃ tilampi taṇḍulampi muggampi viññāpetvā anto vāsetvā anto sāmaṃ paci, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">Suppiyā vatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',84,replace(replace('<p class="bodytext">manussamaṃsaṃ <a name="M2.0084"></a> āvuso paribhuñjantassa thullaccayañca appaṭivekkhitvā maṃsaṃ paribhuñjantassa dukkaṭañca bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu appaṭivekkhitvā manussamaṃsaṃ bhuñji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',87,replace(replace('<p class="bodytext">Na <a name="M2.0087"></a> bhikkhave manussamaṃsaṃ paribhuñjitabbaṃ, yo paribhuñjeyya, āpatti thullaccayassa, na ca bhikkhave appaṭivekkhitvā maṃsaṃ paribhuñjitabbaṃ, yo paribhuñjeyya āpatti dukkaṭassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> hatthimaṃsaṃ vā āvuso assamaṃsaṃ vā paribhuñjantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū hatthimaṃsampi assamaṃsampi paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',88,replace(replace('<p class="bodytext">sunakkhamaṃsaṃ <a name="M2.0088"></a> vā āvuso ahimaṃsaṃ vā bhuñjantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū sunakkhamaṃsampi ahimaṃsampi paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',89,replace(replace('<p class="bodytext">sīhamaṃsaṃ <a name="M2.0089"></a> vā āvuso byagghamaṃsaṃ vā dīpimaṃsaṃ vā acchamaṃsaṃ vā taracchamaṃsaṃ vā bhuñjantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃyeva bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū sīhamaṃsampi byagghamaṃsampi dīpimaṃsampi acchamaṃsampi taracchamaṃsampi paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',90,replace(replace('<p class="bodytext">gilānassa <a name="M2.0090"></a> āvuso guḷaṃ vā agilānassa guḷodakaṃ vā bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū gilānasseva bhagavatā guḷo anuññāto no agilānassāti kukkuccāyantā guḷaṃ na bhuñjiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',91,replace(replace('<p class="bodytext">jānaṃ <a name="M2.0091"></a> āvuso uddissa kataṃ maṃsaṃ paribhuñjantassa dukkaṭaṃ paññapetvā tikoṭiparisuddhaṃ maṃsaṃ bhagavatā kattha anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante sīhassa seṭāpatino vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Meṇḍaka vatthu</span></p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',94,replace(replace('<p class="bodytext">pātheyyaṃ <a name="M2.0094"></a> āvuso pariyesituñca kappiyakārakānaṃ hatthe upanikkhittahiraññato nibbattaṃ kappiyapaccayaṃ sādituñca bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',95,replace(replace('<p class="bodytext">antarā <a name="M2.0095"></a> ca bhante bhaddiyaṃ antarā ca aṅguttarāpaṃ meṇḍakaṃ gahapatiṃ ārabbha anuññātaṃ, meṇḍako bhante gahapati bhagavantaṃ yāci ‘‘santi bhante maggā kantārā appodakā appabhakkhā na sukarā apātheyyena gantuṃ, sādhu bhante bhagavā bhikkhūnaṃ pātheyyaṃ anujānātū’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="subhead">Keṇiya vatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',96,replace(replace('<p class="bodytext">aṭṭha <a name="M2.0096"></a> āvuso pānāni bhagavatā kattha anuññātāni, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āpaṇe bhante sambahule bhikkhū ārabbha anuññātāni, sambahulā bhante bhikkhū kukkuccāyantā pānāni na paṭiggaṇhiṃsu, tasmiṃ bhante vatthusmiṃ anuññātāni.</p>\r\n<p class="subhead">Rojamallā vatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',97,replace(replace('<p class="bodytext">sabbañca <a name="M2.0097"></a> āvuso ḍākaṃ sabbañca piṭṭhakhādanīyaṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kusinārāyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū kukkuccāyantā ḍākañca piṭṭhakhādanīyañca na paṭiggahesuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',98,replace(replace('<p class="bodytext">akappiyaṃ <a name="M2.0098"></a> āvuso samādapentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ātumāyaṃ bhante aññataraṃ vuḍḍhapabbajitaṃ ārabbha paññattaṃ, aññataro bhante vuḍḍhapabbajito akappiye samādapeti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',99,replace(replace('<p class="bodytext">saṅghikāni <a name="M2.0099"></a> vā āvuso puggalikāni vā bījāni puggalikāya vā saṅghikāya vā bhūmiyā ropitāni bhāgaṃ datvā paribhuñjituṃ bhagavatā kattha anuññātaṃ, kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante anuññātaṃ, sāvatthiyaṃ bhante bhagavati viharati saṅghikānipi bījāni puggalikāya bhūmiyā ropiyiṃsu, puggalikānipi bījāni saṅghikāya bhūmiyā ropiyiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',100,replace(replace('<p class="bodytext">vinayamahāpadesasaṅkhātāni <a name="M2.0100"></a> āvuso cattāri kappiyākappiyaanulomāni bhagavatā kattha paññattāni, kaṃ ārabbha kismiṃ vatthusmiṃ paññattāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattāni, sambahulānaṃ bhante bhikkhūnaṃ kismiñci kismiñciṭhāne kukkuccaṃ uppajji, tasmiṃ bhante vatthusmiṃ paññattāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',101,replace(replace('<p class="bodytext">yāvakālikādīhi <a name="M2.0101"></a> saṃsaṭṭhānaṃ āvuso yāmakālikādīnaṃ paribhogakālamariyādaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulānaṃ bhante bhikkhūnaṃ etadahosi ‘‘kappati nukho yāvakālikena yāmakālikaṃ sattāhakālikaṃ yāvajīvikaṃ, na nukho kappati. Kappatinukho yāmakālikena sattāhakālikaṃ yāvajīvikaṃ, na nukho kappati. Kappati nukho sattāhakālikena yāvajīvikaṃ, na nukho kappatī’’ti. Tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',102,replace(replace('<p class="bodytext">bhesajjakaṃ <a name="M2.0102"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhesajjakaṃ vissajjissaṃ sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">7. Kathinakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',104,replace(replace('<p class="bodytext">vassaṃvuṭṭhānaṃ <a name="M2.0104"></a> āvuso bhikkhūnaṃ kathinaṃ attharituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante tiṃsamatte pāveyyake bhikkhū ārabbha anuññātaṃ, tiṃsamattā bhante pāveyyakā bhikkhū sāvatthiṃ āgacchantā bhagavantaṃ dassanāya upakaṭṭhāya vassūpanāyikāya nāsakkhiṃsu sāvatthiyaṃ vassūpanāyikaṃ sambhāvetuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',105,replace(replace('<p class="bodytext">kathinakaṃ <a name="M2.0105"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kathinakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante na katamā āpatti.</p>\r\n<p class="subhead">8. Cīvarakkhandhaka</p>\r\n<p class="subhead">Jīvakavatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',106,replace(replace('<p class="bodytext">gahapaticīvaraṃ <a name="M2.0106"></a> āvuso sādiyituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante jīvakaṃ komārabhaccaṃ ārabbha anuññātaṃ, jīvako bhante komārabhacco bhikkhūnaṃ gahapaticīvaraṃ anujānituṃ bhagavantaṃ yāci, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',107,replace(replace('<p class="bodytext">cha <a name="M2.0107"></a> āvuso rajanāni bhagavatā kattha anuññātāni, kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātāni, sambahulā bhante bhikkhū chakaṇenapi paṇḍumattikāyapi cīvaraṃ rajiṃsu, cīvaraṃ dubbaṇṇaṃ hoti, tasmiṃ bhante vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',108,replace(replace('<p class="bodytext">acchinnakāni <a name="M2.0108"></a> āvuso cīvarāni dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū acchinnakāni cīvarāni dhāresuṃ dantakasāvāni, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kadā āvuso bhagavā anuññāsi chinnakaṃ saṅghāṭiṃ chinnakaṃ uttarāsaṅgaṃ chinnakaṃ antaravāsakaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dakkhiṇāgirito bhante pacchāgatakāle bhagavā anuññāsi chinnakaṃ saṅghāṭiṃ chinnakaṃ uttarāsaṅgaṃ chinnakaṃ antaravāsakaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',110,replace(replace('<p class="bodytext">aggaḷaṃ <a name="M2.0110"></a> ca āvuso tunnañca ovaṭṭikañca kaṇḍusakañca daḷhīkammañca bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bārāṇasiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha anuññātaṃ, aññataro bhante bhikkhu aggaḷaṃ acchupesi, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',111,replace(replace('<p class="bodytext">vassikasāṭikā <a name="M2.0111"></a> ca āvuso āgantukabhattañca gamikabhattañca gilānabhattañca gilānupaṭṭhākabhattañca gilānabhesajjañca dhuvayāgu ca bhikkhunisaṅghassa udakasāṭikācāti imā aṭṭha bhagavatā kattha anuññātā, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante visākhaṃ migāramātaraṃ ārabbha anuññātā, visākhā bhante migāramātā bhagavantaṃ aṭṭhavarāni yāci, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',114,replace(replace('<p class="bodytext">bhagavatā <a name="M2.0114"></a> āvuso mātāpitūnaṃ dānaṃ anujānitvā saddhādeyyaṃ vinipātentassa dukkaṭaṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññatarassa bhante bhikkhuno bahuṃ cīvaraṃ uppannaṃ ahosi, so ca taṃcīvaraṃ mātāpitūnaṃ dātukāmo ahosi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',115,replace(replace('<p class="bodytext">Mātāpitaroti <a name="M2.0115"></a> kho bhikkhave dadamāne kiṃ vadeyyāma, anujānāmi bhikkhave mātāpitūnaṃ dātuṃ, na ca bhikkhave saddhādeyyaṃ vinipātetabbaṃ, yo vinipāteyya āpatti dukkaṭassa –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> aññatra vassaṃ vuṭṭhena āvuso aññatra cīvarabhāgaṃ sādiyantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto aññatra vassaṃ vuṭṭho aññatra cīvarabhāgaṃ sādiyi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',117,replace(replace('<p class="bodytext">anāthakaṃ <a name="M2.0117"></a> āvuso bhikkhuṃ gilānaṃ na upaṭṭhahantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, bhagavati bhante sāvatthiyaṃ viharati aññatarassa bhikkhuno kucchivikārābādho ahosi, so sake muttakarīse palipanno sayi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',119,replace(replace('<p class="bodytext">naggiyaṃ <a name="M2.0119"></a> āvuso titthiyasamādānaṃ samādiyantassa thullaccayaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu naggiyaṃ titthiyasamādānaṃ samādiyi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',120,replace(replace('<p class="bodytext">sabbanīlakāni <a name="M2.0120"></a> vā āvuso cīvarāni sabbapītakāni vā sabbalohitakāni vā sabbamañjiṭṭhakāni vā sabbakaṇhānivāti evarūpāni cīvarāni dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',121,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0121"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū sabbanīlakādīni cīvarāni dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',122,replace(replace('<p class="bodytext">kañcukaṃ <a name="M2.0122"></a> vā āvuso veṭhanaṃ vā dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kañcukampi dhāresuṃ veṭhanampi dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> cīvarasaññuttaṃ pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cīvarasaññuttaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">6. Campeyyakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',124,replace(replace('<p class="bodytext">suddhaṃ <a name="M2.0124"></a> āvuso bhikkhuṃ anāpattikaṃ avatthusmiṃ akāraṇe ukkhipantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> campāyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, bhagavati bhante campāyaṃ viharati sambahulā bhikkhū kāsīsu vāsabhagāme suddhaṃ kassapagottaṃ bhikkhuṃ anāpattikaṃ avatthusmiṃ akāraṇe ukkhipiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',125,replace(replace('<p class="bodytext">campeyyakaṃ <a name="M2.0125"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> campeyyakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">10. Kosambakakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',127,replace(replace('<p class="bodytext">bhedagarukehi <a name="M2.0127"></a> bhikkhave bhikkhūhi na so bhikkhu āpattiyā adassane ukkhipitabboti ca, bhedagarukena bhikkhave bhikkhunā paresampi saddhāya āpattidesetabbāti ca āvuso ayaṃ sāmaggirasaovādo bhagavatā kattha dinno, kaṃ ārabbha kismiṃ vatthusmiṃ dinno.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante sambahule bhikkhū ārabbha dinno, bhagavati bhante kosambiyaṃ viharati aññataro bhikkhu āpattiṃ āpanno ahosi, so tassā āpattiyā āpatti diṭṭhi ahosi, aññe bhikkhū tassā āpattiyā anāpatti diṭṭhino ahesuṃ. So aparena samayena tassā āpattiyā anāpattidiṭṭhi ahosi, aññe bhikkhū tassā āpattiyā āpattidiṭṭhino ahesuṃ. Atha kho te bhante bhikkhū sāmaggiṃ labhitvā taṃ bhikkhuṃ āpattiyā adassane ukkhipiṃsu, tasmiṃ bhante vatthusmiṃ dinno.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',130,replace(replace('<p class="bodytext">saṅghabhedassa <a name="M2.0130"></a> āvuso mūlabhūte bhikkhumhi ukkhittake osārite tassa vatthussa vūpasamāya saṅghasāmaggiñca kātuṃ sāmaggī uposathañca kātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante teyeva ukkhittakānuvattake bhikkhū ārabbha anuññātaṃ, te bhante ukkhittakānuvattakā bhikkhū taṃ ukkhittakaṃ bhikkhuṃ osāretvā yena ukkhepakā bhikkhū tenupasaṅkamiṃsu upasaṅkamitvā te ukkhepake bhikkhū ekadavocuṃ ‘‘yasmiṃ āvuso vatthusmiṃ ahosi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',131,replace(replace('<p class="bodytext">saṅghassa <a name="M2.0131"></a> bhaṇḍanaṃ kalaho viggaho vivādo saṅghabhedo saṅgharāji saṅghavavatthānaṃ saṅghanānākaraṇaṃ, so eso bhikkhuṃ āpanno ca ukkhitto ca passi ca osārito ca, handa mayaṃ āvuso tassa vatthussa vūpasamāya saṅghasāmaggiṃ karomā’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',132,replace(replace('<p class="bodytext">kosambakaṃ <a name="M2.0132"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="chapter">Cūḷavaggapāḷi</p>\r\n<p class="subhead">1. Kambakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',133,replace(replace('<p class="bodytext">bhaṇḍanādikārakassa <a name="M2.0133"></a> āvuso bhikkhuno tajjanīyakammaṃ kātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante paṇḍukalohitake bhikkhū ārabbha anuññātaṃ, paṇḍukalohitakā bhante bhikkhū attanā bhaṇḍanakārakā kalahakārakā vivādakārakā bhassakārakā saṅghe adhikaraṇakārakā, yepi caññe bhikkhū bhaṇḍanakārakā kalahakārakā vivādakārakā bhassakārakā saṅghe adhikaraṇakārakā te upasaṅkamitvā evamāhaṃsu ‘‘mā kho tumhe āyasmanto eso ajesi, balavābalavaṃ paṭimantetha, tumhe tena paṇḍitatarā ca byattatarā ca bahussutatarā ca alamattatarā ca, mā cassa bhāyittha, mayampi tumhākaṃ pakkhā bhavissāmā’’ti, tena anuppannāni ceva bhaṇḍanāni uppajjanti, uppannāni ca bhaṇḍanāni bhiyyobhāvāya vepullāya saṃvattanti. Tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',134,replace(replace('<p class="bodytext">tajjanīyakammakatassa <a name="M2.0134"></a> āvuso bhikkhuno katisu vattesu sammā vattantassa taṃ kammaṃ paṭippassambhetuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',135,replace(replace('<p class="bodytext">aṭṭhārasasu <a name="M2.0135"></a> bhante vattesu sammā vattantassa tajjanīyakammakatassa bhikkhuno taṃ kammaṃ paṭippassambhetuṃ bhagavatā sāvatthiyaṃ anuññātaṃ, teyeva paṇḍukalohitakā bhikkhū saṅghena tajjanīyakammakatā sammā vattitvā saṅghaṃ upasaṅkamitvā yāciṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',136,replace(replace('<p class="bodytext">bālassa <a name="M2.0136"></a> āvuso abyattassa āpattibahulassa anapadānassa gihisaṃsaṭṭhassa ananulomikehi gihisaṃsaggehi niyassakammaṃ kātuṃ kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ seyyasakaṃ ārabbha paññattaṃ, āyasmā bhante seyyasako bālo ahosi abyatto āpattibahulo anapadāno gihisaṃsaṭṭho vihāsi ananulomikehi gihisaṃsaggehi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',137,replace(replace('<p class="bodytext">niyassakammakatassa <a name="M2.0137"></a> āvuso bhikkhuno katisu vattesu sammā vattantassa taṃ kammaṃ paṭippassambhetuṃ bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhārasasu bhante vattesu sammā vattantassa niyassa kammakatassa bhikkhuno taṃ kammaṃ paṭippassambhetuṃ bhagavatā anuññātaṃ, na upasampādetabbaṃ, na nissayo dātabbo, na sāmaṇero upaṭṭhāpetabbo, na bhikkhunovādakasammuti sāditabbā, sammatenapi bhikkhuniyo na ovaditabbā, evamādīsu bhante aṭṭhārasasu vattesu sammā vattantassa niyassakammakatassa bhikkhuno taṃ kammaṃ paṭippassambhetuṃ bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',138,replace(replace('<p class="bodytext">kuladūsakassa <a name="M2.0138"></a> āvuso pāpasamācārassa bhikkhuno pabbājanīyakammaṃ kātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante assajipunabbasuke bhikkhū ārabbha anuññātaṃ, assajipunabbasukā bhante bhikkhū kīṭāgirismiṃ kuladūsakā ahesuṃ pāpasamācārā, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',140,replace(replace('<p class="bodytext">paṭisāraṇīyakammaṃ <a name="M2.0140"></a> āvuso pucchāmi, saddhaṃ āvuso pasannaṃ gahapatiṃ dāyakaṃ kārakaṃ saṅghupaṭṭhākaṃ hīnena khuṃsentassa </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',141,replace(replace('<p class="bodytext">bhikkhuno <a name="M2.0141"></a> paṭisāraṇīyakammaṃ kātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ sudhammaṃ ārabbha anuññātaṃ, bhagavati bhante sāvatthiyaṃ viharati āyasmā sudhammo macchikāsaṇḍe cittaṃ gahapatiṃ saddhaṃ pasannaṃ dāyakaṃ kārakaṃ saṅghupaṭṭhākaṃ hīnena khuṃsesi hīnena vambhesi, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',142,replace(replace('<p class="bodytext">Tenahi <a name="M2.0142"></a> bhikkhave saṅgho sudhammassa bhikkhuno paṭisāraṇīyakammaṃ karotu ‘‘citto te gahapati khamāpetabbo’’ti –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',143,replace(replace('<p class="bodytext">paṭisāraṇīyakammakatassa <a name="M2.0143"></a> āvuso bhikkhuno anudūtaṃ dātuñca tena anudūtena saddhiṃ gantvā yathākhuṃsitaṃ gahapatiṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',144,replace(replace('<p class="bodytext">khamāpetuṃ <a name="M2.0144"></a> ca bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃyeva sudhammaṃ ārabbha anuññātaṃ, āyasmā bhante sudhammo macchikāsaṇḍaṃ gantvā maṅkubhūto nāsakkhi cittaṃ gahapatiṃ khamāpetuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',145,replace(replace('<p class="bodytext">paṭisāraṇīyakammakatassa <a name="M2.0145"></a> āvuso bhikkhuno katisu vattesu sammā vattantassa taṃ kammaṃ paṭippassambhetuṃ bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhārasasu bhante vattesu sammā vattantassa paṭisāraṇīya kammakatassa bhikkhuno taṃ kammaṃ paṭippassambhetuṃ bhagavatā anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',146,replace(replace('<p class="bodytext">āpattiṃ <a name="M2.0146"></a> āvuso āpajjitvā taṃ āpattiṃ passituṃ vā paṭikātuṃ vā na icchantassa bhikkhuno āpattiyā adassane vā appaṭikamme vā ukkhepanīyakammaṃ kātuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃ channattheraṃ ārabbha paññattaṃ, āyasmā bhante channo āpattiṃ āpajjitvā na icchi taṃ āpattiṃ passituṃ vā paṭikātuṃ vā, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',147,replace(replace('<p class="bodytext">āpattiyā <a name="M2.0147"></a> āvuso adassanena vā appaṭikamme vā ukkhepanīya kammakatena bhikkhunā katisu vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tecattālīsāya bhante vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',148,replace(replace('<p class="bodytext">ukkhepanīyakammakatassa <a name="M2.0148"></a> āvuso bhikkhuno sammā vattantassa taṃ kammaṃ paṭippassambhetuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kosambiyaṃ bhante āyasmantaṃyeva channaṃ ārabbha anuññātaṃ, āyasmā bhante channo saṅghena āpattiyā adassane vā appaṭikamme vā ukkhepanīyakammakato sammā vattesi lomaṃpātesi netthāraṃ vattesi saṅghaṃ upasaṅkamitvā tassa kammassa paṭippassaddhiyā yāci, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',149,replace(replace('<p class="bodytext">pāpikaṃ <a name="M2.0149"></a> āvuso diṭṭhiṃ gahetvā taṃ diṭṭhiṃ na paṭinissajjantassa pāpikāya diṭṭhiyā appaṭinissagge ukkhepanīyakammaṃ kātuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante ariṭṭhaṃ bhikkhuṃ gaddhabādhipubbaṃ ārabbha paññattaṃ, bhagavati bhante sāvatthiyaṃ viharati ariṭṭhassa nāma bhikkhuno gaddhabādhipubbassa evarūpaṃ pāpakaṃ diṭṭhigataṃ uppannaṃ ahosi ‘‘tathāhaṃ bhagavatā dhammaṃ desitaṃ ājānāmi, yathāyeme antarāyikā dhammā vuttā bhagavatā, te </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',150,replace(replace('<p class="bodytext">paṭisevato <a name="M2.0150"></a> nālaṃ antarāyāyā’’ti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pāpikāya āvuso diṭṭhiyā appaṭinissagge ukkhepanīyakammakatena bhikkhunā katisu vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',151,replace(replace('<p class="bodytext">tecattālīsāya <a name="M2.0151"></a> bhante vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kammakkhandhakaṃ pucchissaṃ, sanidāni saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kammakkhandhakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">2. Pārivāsikakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',152,replace(replace('<p class="bodytext">pārivāsikassa <a name="M2.0152"></a> āvuso pakatattānaṃ bhikkhūnaṃ abhivādanādīni sādiyantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante pārivāsike bhikkhū ārabbha paññattaṃ, pārivāsikā bhante bhikkhū sādiyiṃsu pakatattānaṃ bhikkhūnaṃ abhivādanādīni, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',153,replace(replace('<p class="bodytext">pārivāsikena <a name="M2.0153"></a> āvuso bhikkhunā katisu vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pārivāsikena bhante bhikkhunā catunavutiyā vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pārivāsikaṃ pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pārivāsikaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">3. Samuccayakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',154,replace(replace('<p class="bodytext">samuccayaṃ <a name="M2.0154"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samuccayaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">4. Samathakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',155,replace(replace('<p class="bodytext">asammukhībhūtānaṃ <a name="M2.0155"></a> āvuso bhikkhūnaṃ tajjanīyaṃ vā niyassaṃ vā pabbājanīyaṃ vā paṭisāraṇīyaṃ vā ukkhepanīyaṃ vā kammaṃ karontassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū asammukhībhūtānaṃ bhikkhūnaṃ kammaṃ akaṃsu tajjanīyampi niyassampi pabbājanīyampi paṭisāraṇīyampi ukkhepanīyampi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',156,replace(replace('<p class="bodytext">sativepullapattassa <a name="M2.0156"></a> āvuso bhikkhuno sativinayaṃ dānaṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante mettiyabhūmajake bhikkhū ārabbha anuññātaṃ mettiyabhūmajakā bhante bhikkhū āyasmantaṃ dabbaṃ mallaputtaṃ amūlikāya sīlavipattiyā anuddhaṃsesuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',161,replace(replace('<p class="bodytext">katipanāvuso <a name="M2.0161"></a> ettha dhammikāni sativinayadānāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcimāni bhante dhammikāni sativinayassa dānāni, suddho hoti bhikkhu anāpattiko, anuvadanti ca naṃ, yācati ca, tassa saṅgho sativinayaṃ deti dhammena samaggena, imāni kho bhante pañca dhammikāni sati vinayassa dānāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',162,replace(replace('<p class="bodytext">amūḷhassa <a name="M2.0162"></a> āvuso bhikkhuno amūḷhavinayaṃ dātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū gaggaṃ bhikkhuṃ ummattakena </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',163,replace(replace('<p class="bodytext">cittavipariyāsakatena <a name="M2.0163"></a> ajjhāciṇṇena āpattiyā codesuṃ ‘‘saratā yasmā evarūpiṃ āpattiṃ āpajjitā’’ti, so evaṃ vadeti ‘‘ahaṃ kho āvuso ummattako ahosi cittavipariyāsakato, tena me ummattakena cittavipariyāsakatena bahuṃ assāmaṇakaṃ ajjhāciṇṇaṃ bhāsitaparikkantaṃ, nāhaṃ taṃ sarāmi, mūḷhena me etaṃ kata’’nti. Evampi naṃ vuccamānā codenteva ‘‘saratā yasmā evarūpiṃ āpattiṃ āpajjitā’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',164,replace(replace('<p class="bodytext">kati <a name="M2.0164"></a> āvuso adhammikāni amūḷhavinayassa dānāni, vibhajitvā kathehi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīṇi bhante adhammikāni amūḷhavinayassa dānāni, idha bhante bhikkhu āpattiṃ āpanno hoti, tamenaṃ codeti saṅgho vā sambahulā vā ekapuggalo vā ‘‘saratāyasmā evarūpiṃ āpattiṃ āpajjitā’’ti, so saramānova evaṃ vadeti ‘‘na kho ahaṃ āvuso sarāmi evarūpiṃ āpattiṃ āpajjitā’’ti. Añño saramānova evaṃ vadeti ‘‘sarāmi kho ahaṃ āvuso yathā supinantenā’’ti. Añño anummattakova ummattakālayaṃ karoti ‘‘ahampi kho evaṃ karomi, tumhepi evaṃ karotha, mayhampi etaṃ kappati, tumhākampetaṃ kappatī’’ti. Esaṃ saṅgho amūḷhavinayaṃ deti, adhammikaṃ bhante amūḷhavinayassa dānaṃ. Imāni kho bhante tīṇi adhammikāni amūḷhavinayassa dānāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',166,replace(replace('<p class="bodytext">kati <a name="M2.0166"></a> āvuso dhammikāni amūḷhavinayassa dānāni, vibhajitvā kathehi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tīṇi bhante dhammikāni amūḷhavinayassa dānāni, idha bhante bhikkhu ummattako hoti cittavipariyāsakato tena ummattakena cittavipariyāsakatena bahuṃ assāmaṇakaṃ ajjhāciṇṇaṃ hoti bhāsitaparikkantaṃ, tamenaṃ codeti saṅgho vā sambahulā vā ekapuggalo vā ‘‘saratāyasmā evarūpiṃ āpattiṃ āpajjitā’’ti, so assaramānova evaṃ vadeti ‘‘na kho ahaṃ āvuso sarāmi evarūpiṃ āpattiṃ āpajjitā’’ti. Aññopi assaramānova evaṃ vadeti ‘‘sarāmi kho ahaṃ āvuso yathā supinantenā’’ti. Aññopi ummattakova ummattakālayaṃ karoti ‘‘ahampi evaṃ karomi, tumhepi evaṃ karotha, mayhampi etaṃ kappati, tumhākampetaṃ kappatī’’ti. Esaṃ bhante tiṇṇaṃ bhikkhūnaṃ saṅgho amūḷhavinayaṃ deti, dhammikaṃ bhante amūḷhavinayassa dānaṃ. Imāni kho bhante tīṇi dhammikāni amūḷhavinayassa dānāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',167,replace(replace('<p class="bodytext">appaṭiññāya <a name="M2.0167"></a> āvuso bhikkhūnaṃ tajjanīyādīni kammāni karontassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',168,replace(replace('<p class="bodytext">rājagahe <a name="M2.0168"></a> bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū appaṭiññāya bhikkhūnaṃ kammāni akaṃsu tajjanīyādīni, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso adhammikaṃ hoti paṭiññātakaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aññathā bhante āpattiṃ āpajjantassa aññathā paṭijānantassa yathā so paṭijānāti, tathā saṅgho kāreti, evaṃ kho bhante adhammikaṃ hoti paṭiññātakaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',169,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0169"></a> āvuso dhammikaṃ hoti paṭiññātakaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yaṃ bhante āpattiṃ āpajjantassa tameva paṭijānantassa teneva saṅgho kāreti, evaṃ kho bhante dhammikaṃ paṭiññātakaraṇaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',170,replace(replace('<p class="bodytext">yebhuyyasikāya <a name="M2.0170"></a> āvuso adhikaraṇaṃ vūpasametuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',171,replace(replace('<p class="bodytext">rājagahe <a name="M2.0171"></a> bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū bhaṇḍanajātā kalahajātā vivādāpannā aññamaññaṃ mukhasattīhi vitudantā vihariṃsu, na sakkonti taṃ adhikaraṇaṃ vūpasametuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso adhikaraṇaṃ yebhuyyasikāya vūpasametabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcahi bhante aṅgehi samannāgato bhikkhu salākaggāhāpako sammannitabbo, tena bhante </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',172,replace(replace('<p class="bodytext">salākaggāhāpakena <a name="M2.0172"></a> salākā gāhātabbā yathā bahutarā bhikkhū dhammavādino vadanti tathā taṃ adhikaraṇaṃ vūpasametabbaṃ, evaṃ kho bhante yebhuyyasikāya adhikaraṇaṃ vūpasametabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',173,replace(replace('<p class="bodytext">kati <a name="M2.0173"></a> āvuso adhammikā salākaggāhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante adhammikā salākaggāhā, oramattakañca adhikaraṇaṃ hoti, na ca gatigataṃ hoti, na ca saritasāritaṃ hoti, jānāti ‘adhammavādī bahutarā’ti, appeva nāma adhammavādī bahutarā assūti, jānāti ‘saṅgho bhijjissatī’ti, appeva nāma saṅgho bhijjeyyāti, adhammena gaṇhanti, vaggā gaṇhanti, na ca yathādiṭṭhiyā gaṇhanti, ime kho bhante dasa adhammikā kalākaggāhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',175,replace(replace('<p class="bodytext">kati <a name="M2.0175"></a> āvuso dhammikā salākaggāhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasa bhante dhammikā salākaggāhā vuttavipariyāyena.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pāpussannassa āvuso bhikkhussa tassa pāpiyasikā kammaṃ kātuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante upavāḷaṃ bhikkhuṃ ārabbha paññattaṃ, upavāḷo bhante bhikkhu saṅghamajjhe āpattiyā anuyuñjiyamāno avajānitvā paṭijānāti, paṭijānitvā avajānāti, aññenaññaṃ paṭicarati, sampajānamusā bhāsati, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',176,replace(replace('<p class="bodytext">kati <a name="M2.0176"></a> āvuso dhammikāni tassapāpiyasikā kammassa karaṇāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcimāni bhante dhammikāni tassapāpiyasikā kammassa karaṇāni. Asuci ca hoti, alajjī ca, sānuvādo ca, tassa saṅgho tassapāpiyasikā kammaṃ karoti dhammena samaggena, imāni kho bhante pañca dhammikāni tassa pāpiyasikā kammassa karaṇāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',177,replace(replace('<p class="bodytext">tassa <a name="M2.0177"></a> pāpiyasikākammakatena āvuso bhikkhunā katisu vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tassa pāpiyasikākammakatena bhante bhikkhunā aṭṭhārasasu vattesu sammā vattitabbaṃ. Na upasampādetabbaṃ, na nissayo dātabbo, na sāmaṇero upaṭṭhāpetabbo, evamādīsu bhante aṭṭhārasasu vattesu sammā vattitabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',178,replace(replace('<p class="bodytext">kati <a name="M2.0178"></a> āvuso adhikaraṇāni samathehi vūpasametabbāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cattārimāni bhante adhikaraṇāni samathehi vūpasametabbāni, vivādādhikaraṇaṃ anuvādādhikaraṇaṃ āpattādhikaraṇaṃ kiccādhikaraṇaṃ, imāni bhante cattāri adhikaraṇāni samathehi vūpasametabbāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',179,replace(replace('<p class="bodytext">kiṃ <a name="M2.0179"></a> āvuso vivādādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cha bhante vivādamūlāni vivādādhikaraṇassa mūlaṃ, tīṇipi akusalamūlāni vivādādhikaraṇassa mūlaṃ, tīṇipi kusalamūlāni vivādādhikaraṇassa mūlaṃ, idaṃ bhante vivādādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',180,replace(replace('<p class="bodytext">kiṃ <a name="M2.0180"></a> panāvuso anuvādādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cha bhante anuvādamūlāni anuvādādhikaraṇassa mūlaṃ, tīṇipi akusalamūlāni anuvādādhikaraṇassa mūlaṃ, tīṇipi kusalamūlāni anuvādādhikaraṇassa mūlaṃ, kāyopi anuvādādhikaraṇassa mūlaṃ, vācāpi anuvādādhikaraṇassa mūlaṃ, idaṃ kho bhante anuvādādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',181,replace(replace('<p class="bodytext">kiṃ <a name="M2.0181"></a> panāvuso āpattādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cha bhante āpattisamuṭṭhānā āpattādhikaraṇassa mūlaṃ, atti bhante āpatti kāyato samuṭṭhāti na vācato na cittato, atthi bhante āpatti vācato samuṭṭhāti na kāyato na cittato, atthi bhante āpatti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',182,replace(replace('<p class="bodytext">kāyato <a name="M2.0182"></a> ca vācato ca samuṭṭhāti na cittato, atthi bhante āpatti kāyato ca cittato ca samuṭṭhāti na vācato, atthi bhante āpatti vācato ca cittato ca samuṭṭhāti na kāyato, atthi bhante āpatti kāyato ca vācato ca cittato ca samuṭṭhāti, imāni bhante cha āpatti samuṭṭhānāni āpattādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',183,replace(replace('<p class="bodytext">kiṃ <a name="M2.0183"></a> panāvuso kiccādhikaraṇassa mūlaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kiccādhikaraṇassa bhante ekaṃ mūlaṃ saṅgho.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> samathaṃ āvuso pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samathaṃ bhante vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante dve āpattiyo.</p>\r\n<p class="subhead">5. Khuddakavatthukkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',184,replace(replace('<p class="bodytext">nahāyantena <a name="M2.0184"></a> āvuso bhikkhunā rukkhe vā thambhe vā kuṭṭe vā kāyaṃ ugghaṃsentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū nahāyamānā rukkhepi thambhepi kuṭṭepi kāyaṃ ugghaṃsesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> gandhabbahatthakena vā āvuso kuruvindakasuttiyā vā mallakena vā nahāyantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū gandhabbahatthakenapi </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',185,replace(replace('<p class="bodytext">kuruvindakasuttiyāpi <a name="M2.0185"></a> mallakenapi nahāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> pāṇinā āvuso parikammaṃ kātuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū piṭṭhiparikammaṃ kātuṃ kukkuccāyiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',186,replace(replace('<p class="bodytext">vallikaṃ <a name="M2.0186"></a> vā āvuso pāmaṅgaṃ vā kaṇṭhasuttakaṃ vā kaṭisuttakaṃ vā ovaṭṭikaṃ vā kāyuraṃ vā hatthābharaṇaṃ vā aṅgulimuddikaṃ vā dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū vallikādīni dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',187,replace(replace('<p class="bodytext">dīghe <a name="M2.0187"></a> āvuso kese dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū dīghe kese dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',188,replace(replace('<p class="bodytext">kocchenavā <a name="M2.0188"></a> āvuso phaṇakenavā hatthaphaṇakenavā kese osaṇṭhentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kocchādīhi kese osaṇṭhesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',189,replace(replace('<p class="bodytext">ādāse <a name="M2.0189"></a> vā āvuso udakapatte vā mukhanimittaṃ olokentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū ādāsepi udakapattepi mukhanimittaṃ olokesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',190,replace(replace('<p class="bodytext">ābādhapaccayā <a name="M2.0190"></a> panāvuso ādāse vā udakapatte vā mukhanimittaṃ oloketuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante aññataraṃ bhikkhuṃ ārabbha anuññātaṃ, bhagavati bhante rājagahe viharati aññatarassa bhikkhuno mukhe vaṇo ahosi, so bhikkhū etadavoca ‘‘kīdiso me āvuso vaṇo’’ti, bhikkhū evamāhaṃsu ‘‘ediso te āvuso vaṇo’’ti, so na saddahati, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',191,replace(replace('<p class="bodytext">mukhālepanādīni <a name="M2.0191"></a> āvuso karontassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū mukhālepanādīni akaṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',192,replace(replace('<p class="bodytext">naccaṃ <a name="M2.0192"></a> vā āvuso gītaṃ vā vāditaṃ vā dassanāya gacchantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante naccampi gītampi vāditampi dassanāya gacchiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathaṃ āvuso naccādīni passantassa āpatti hoti, kathaṃ pana anāpatti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> naccaṃ vā bhante gītaṃ vā vāditaṃ vā dassanāya gacchati āpatti dukkaṭassa, yattha ṭhito passati vā suṇāti vā āpatti </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',193,replace(replace('<p class="bodytext">dukkaṭassa<a name="M2.0193"></a>, dassanūpacāraṃ vijahitvā punappunaṃ passati āpatti dukkaṭassa, ārāme ṭhitassa passato anāpatti, vihārato pana vihāraṃ passissāmīti gacchato āpattiyeva, yattha ṭhito passati vā suṇāti vā āpatti dukkaṭassa, āsanasālāya nisinno passati anāpatti, passissāmīti vuṭṭhahitvā gacchati āpatti dukkaṭassa, yattha ṭhito passati vā suṇāti vā āpatti dukkaṭassa, patipathaṃ gacchanto passati anāpatti, gīvaṃ parivattetvā passato pana āpatti bhante.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',194,replace(replace('<p class="bodytext">āyatakena <a name="M2.0194"></a> āvuso gītassarena dhammaṃ gāyantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū āyatakena gītassarena dhammaṃ gāyiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',196,replace(replace('<p class="bodytext">dārupattaṃ <a name="M2.0196"></a> āvuso dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante āyasmantaṃ piṇḍolabhāradvājaṃ ārabbha paññattaṃ, āyasmā bhante piṇḍolabhāradvājo chavassa dārupattassa kāraṇā gihīnaṃ uttarimanussadhammaṃ iddhipāṭihāriyaṃ dasseti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',197,replace(replace('<p class="bodytext">Na <a name="M2.0197"></a> ca bhikkhave dārupatto dhāretabbo, yo dhāreyya āpatti dukkaṭassa –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhagavatā āvuso uccāvace patte paṭikkhipitvā ayo patto bhūmipattoti imeyeva dve pattā kattha anuññātā, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha anuññātā, chabbaggiyā bhante bhikkhū uccāvace patte dhāresuṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',198,replace(replace('<p class="bodytext">sovaṇṇamayā <a name="M2.0198"></a> rūpiyamayā, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',199,replace(replace('<p class="bodytext">sodakaṃ <a name="M2.0199"></a> āvuso pattaṃ paṭisāmentassa ca otāpentassaca uṇhe pattaṃ nidahantassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū sodakaṃ pattaṃ paṭisāmesuṃ, otāpesuṃ, uṇhe pattaṃ nidahiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',200,replace(replace('<p class="bodytext">chavasīsapattaṃ <a name="M2.0200"></a> āvuso dhārentassa ca sabbapaṃsukūlikassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu sabbapaṃsukūliko ahosi, so chavasīsassa pattaṃ dhāresi, aññatarā itthī passitvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',201,replace(replace('<p class="bodytext">bhītā <a name="M2.0201"></a> vissaramakāsi ‘‘abhuṃ me pisāco vatāya’’nti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> calakāni vā āvuso aṭṭhikāni vā ucchiṭṭhodakaṃ vā pattena nīharantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū calakānipi aṭṭhikānipi ucchiṭṭho dakampi pattena nīhariṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',202,replace(replace('<p class="bodytext">upāhanatthavikā <a name="M2.0202"></a> āvuso bhagavatā kattha anuññātā, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> antarā ca bhante rājagahaṃ antarā ca vesāliṃ aññataraṃ bhikkhuṃ ārabbha anuññātā, aññataro bhante bhikkhu upāhanāyo kāyabandhanena bandhitvā gāmaṃ piṇḍāya pāvisi, aññataro upāsako taṃ bhikkhuṃ abhivādento upāhanāyo sīsena ghaṭṭeti, so bhikkhu maṅku ahosi, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',203,replace(replace('<p class="bodytext">addhānamaggappaṭipannena <a name="M2.0203"></a> āvuso parissāvanaṃ yāciyamānena na dadantassa ca appaṭissāvanakena addhānaṃ paṭipajjantassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> antarā ca bhante rājagahaṃ antarā ca vesāliṃ aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu parissāvanaṃ yāciyamāno na adāsi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',205,replace(replace('<p class="bodytext">pupphābhikiṇṇe <a name="M2.0205"></a> āvuso sayane sayantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū pupphābhikiṇṇesu sayanesu sayiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ekabhājane vā āvuso bhuñjantānaṃ ekathālake vā pivantānaṃ ekattharaṇapāvuraṇānaṃ vā tuvaṭṭānaṃ dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante chabbaggiyeva bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū ekabhājanepi bhuñjiṃsu, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',206,replace(replace('<p class="bodytext">ekathālakepi <a name="M2.0206"></a> piviṃsu, ekamañcakepi tuvaṭṭesuṃ, ekattharaṇāpi tuvaṭṭesuṃ, ekapāvuraṇāpi tuvaṭṭesuṃ, ekattharaṇapāvuraṇāpi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> cāmaribījaniṃ āvuso paṭikkhipitvā tisso bījaniyo bhagavatā kattha anuññātā, kismiñca vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante anuññātā, sāvatthiyaṃ bhante bhagavati viharati saṅghassa cāmaribījanī uppannā ahosi, bhagavato etamatthaṃ ārocesuṃ, tasmiṃ bhante vatthusmiṃ anuññātā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',207,replace(replace('<p class="bodytext">Na <a name="M2.0207"></a> bhikkhave cāmaribījanī dhāretabbā, yo dhāreyya, āpatti dukkaṭassa. Anujānāmi bhikkhave tisso bījaniyo vākamayaṃ usīramayaṃ morapiñchā mayaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dīghe āvuso nakhe dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',208,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0208"></a> bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu dīghe nakhe dhāresi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',209,replace(replace('<p class="bodytext">kattarikāya <a name="M2.0209"></a> āvuso kese chedāpentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū kattarikāya kese chindiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',210,replace(replace('<p class="bodytext">dīghe <a name="M2.0210"></a> āvuso nāsikālome dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū dīghāni nāsikālomāni dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> uccāvacā āvuso kaṇṇamalaharaṇiyo paṭikkhipitvā dasa kaṇṇamalaharaṇiyo bhagavatā kattha anuññātā, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha anuññātā, chabbaggiyā bhante bhikkhū uccāvacā kaṇṇamalaharaṇiyo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',211,replace(replace('<p class="bodytext">dhāresuṃ <a name="M2.0211"></a> sovaṇṇamayaṃ rūpiyamayaṃ, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> saṅghāṭipallatthikāya nisīdantassa āvuso dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū saṅghāṭipallatthikāya nisīdiṃsu, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',212,replace(replace('<p class="bodytext">saṅghāṭiyā <a name="M2.0212"></a> pattā lujjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> akāyabandhanena āvuso gāmaṃ pavisantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ bhikkhuṃ ārabbha paññattaṃ, aññataro bhante bhikkhu akāyabandhano gāmaṃ piṇḍāya pāvisi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',213,replace(replace('<p class="bodytext">gihinivatthaṃ <a name="M2.0213"></a> āvuso nivāsentassa ca, gihipārutaṃ pārupantassa ca, saṃvelliyaṃ nivāsentassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū gihinivatthaṃ nivāsiṃsu, gihipārutampi pārupiṃsu, saṃvelliyampi nivāsiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',214,replace(replace('<p class="bodytext">ubhato <a name="M2.0214"></a> kājaṃ āvuso dhārentassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū ubhato kājaṃ dhāresuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',215,replace(replace('<p class="bodytext">lokāyataṃ <a name="M2.0215"></a> āvuso pariyāpuṇantassa ca vācentassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū lokāyataṃ pariyāpuṇiṃsupi vācesumpi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',217,replace(replace('<p class="bodytext">tiracchānavijjaṃ <a name="M2.0217"></a> āvuso pariyāpuṇantassa ca vācentassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū tiracchānavijjaṃ pariyāpuṇiṃsupi vācesumpi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> khipite āvuso ‘‘jīvā’’tivadantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',218,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0218"></a> bhante sambahule bhikkhū ārabbha paññattaṃ, bhagavā bhante mahatiyā parisāya parivuto dhammaṃ desento khipi, bhikkhū ‘‘jīvatu bhante bhagavā, jīvatu sugatoti’’ uccāsaddaṃ mahāsaddaṃ akaṃsu, tena saddena dhammakathā antarā ahosi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ārāme āvuso passāvavaccānaṃ tahaṃ tahaṃ karaṇaṃ paṭikkhipitvā ekamantaṃ kātuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū ārāme tahaṃ tahaṃ passāvaṃ akaṃsu, tahaṃ tahaṃ vaccaṃ akaṃsu, ārāmo dussi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">Khuddakavatthukkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',220,replace(replace('<p class="bodytext">khuddakavatthuṃ <a name="M2.0220"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> khuddakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">6. Senāsanakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',221,replace(replace('<p class="bodytext">pañca <a name="M2.0221"></a> āvuso leṇāni bhagavatā kattha anuññātāni, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha anuññātāni, sambahulā bhante bhikkhū bhagavantaṃ upasaṅkamitvā etadavocuṃ ‘‘rājagahako bhante seṭṭhī vihāre kārāpetukāmo, kathaṃ nu kho bhante paṭipajjitabba’’nti, tasmiṃ bhante vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',222,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0222"></a> āvuso vihārā paṭhamaṃ uppannā, kathañca te patiṭṭhāpi tā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',223,replace(replace('<p class="bodytext">ekāheneva <a name="M2.0223"></a> bhante rājagahakena seṭṭhinā chaṭṭhivihārā patiṭṭhāpitā, te idha bhante saṭṭhivihārā buddhappamukhassa āgatānāgatassa cātuddisassa saṅghassa patiṭṭhāpitā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',224,replace(replace('<p class="bodytext">vihāre <a name="M2.0224"></a> āvuso paṭibhānakammaṃ paṭikkhipitvā mālākammādīni bhagavatā kattha anuññātāni, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante chabbaggiye bhikkhū ārabbha anuññātāni. Chabbaggiyā bhante bhikkhū vihāre paṭibhānacittaṃ kārāpesuṃ itthirūpakaṃ purisarūpakaṃ, tasmiṃ bhante vatthusmiṃ anuññātāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',225,replace(replace('<p class="bodytext">yathāvuḍḍhaṃ <a name="M2.0225"></a> āvuso abhivādanādīni ca aggāsanādīni ca anujānitvā saṅghikaṃ yathāvuḍḍhaṃ paṭibāhantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> antarā ca bhante vesāliṃ antarā ca sāvatthiṃ chabbaggiyānaṃ bhikkhūnaṃ antevāsike bhikkhū ārabbha paññattaṃ, chabbaggiyānaṃ bhante bhikkhūnaṃ antevāsikā bhikkhū buddhappamukhassa saṅghassa purato purato gantvā vihāre paṭiggahesuṃ, seyyāyo paṭiggahesuṃ ‘‘idaṃ amhākaṃ upajjhāyānaṃ bhavissati, idaṃ amhākaṃ ācariyānaṃ bhavissati, idaṃ amhākaṃ bhavissatī’’ti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',227,replace(replace('<p class="bodytext">ekena <a name="M2.0227"></a> āvuso dve paṭibāhantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante āyasmantaṃ upanandaṃ sakyaputtaṃ ārabbha paññattaṃ, āyasmā bhante upanando sakyaputto eko dve paṭibāhi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',228,replace(replace('<p class="bodytext">navakena <a name="M2.0228"></a> āvuso uddisantena samake vā āsane nisīdituṃ uccatare vā dhammagāravena, therena pana bhikkhunā uddisāpentena samake vā āsane nisīdituṃ nīcatare vā dhammagāravena bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū āyasmato upālissa santike vinayaṃ pariyāpuṇiṃsu, āyasmā bhante upāli ṭhitakova uddisati therānaṃ bhikkhūnaṃ gāravena, tattha bhante therā ceva bhikkhū kilamiṃsu āyasmā ca upāli kilami, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',230,replace(replace('<p class="bodytext">tivassantarena <a name="M2.0230"></a> āvuso saha nisīdituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulānaṃ bhante bhikkhūnaṃ etadahosi ‘‘kittāvatānu kho samānāsaniko hotī’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',231,replace(replace('<p class="bodytext">mañce <a name="M2.0231"></a> ca āvuso pīṭhe ca dvinnaṃyeva nisīdituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule samānāsanike ārabbha paññattaṃ, sambahulā bhante bhikkhū samānāsanikā mañce nisīditvā mañcaṃ bhindiṃsu, pīṭhe nisīditvā pīṭhaṃ bhindiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',232,replace(replace('<p class="bodytext">dīghāsane <a name="M2.0232"></a> panāvuso asamānāsanikehipi nisīdituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū dīghāsane asamānāsanikehi saha nisīdituṃ kukkuccāyiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',233,replace(replace('<p class="bodytext">pañca <a name="M2.0233"></a> āvuso avissajjiyāni bhagavatā kattha paññattāni, kaṃ ārabbha kismiṃ vatthusmiṃ paññattāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha paññattāni, sambahulā bhante bhikkhū saṅghikaṃ senāsanaṃ vissajjesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',234,replace(replace('<p class="bodytext">pañca <a name="M2.0234"></a> āvuso avebhaṅgiyāni bhagavatā kattha paññattāni, kaṃ ārabbha kismiṃ vatthusmiṃ paññattāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> kīṭāgirismiṃ bhante assajipunabbasuke bhikkhū ārabbha paññattāni, assajipunabbasukā bhante bhikkhū saṅghikaṃ senāsanaṃ vibhajiṃsu, tasmiṃ bhante vatthusmiṃ paññattāni.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',236,replace(replace('<p class="bodytext">aññatra <a name="M2.0236"></a> paribhogaṃ āvuso aññatra paribhuñjantassa dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āḷaviyaṃ bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū aññatarassa upāsakassa vihāraparibhogaṃ senāsanaṃ aññatra paribhuñjiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> adhotehi ca āvuso allehi ca pādehi senāsanaṃ akkamantassa ca saupāhanena senāsanaṃ akkamantassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',237,replace(replace('<p class="bodytext">āḷaviyaṃ <a name="M2.0237"></a> bhante sambahule bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū adhotehi pādehi senāsanaṃ akkamiṃsu, allehi ca pādehi senāsanaṃ akkamiṃsu, saupāhanāpi senāsanaṃ akkamiṃsu, senāsanaṃ dussi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> parikammakatāya āvuso bhūmiyā niṭṭhubhantassa ca parikammakataṃ bhittiṃ apassayantassa ca dukkaṭaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',238,replace(replace('<p class="bodytext">āḷaviyaṃ <a name="M2.0238"></a> bhante sambahuleva bhikkhū ārabbha paññattaṃ, sambahulā bhante bhikkhū parikammakatāya bhūmiyā niṭṭhubhiṃsu, parikammakataṃ bhittiṃ apassayiṃsu, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> senāsanakkhandhakaṃ pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',239,replace(replace('<p class="bodytext">senāsanakkhandhakaṃ <a name="M2.0239"></a> vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante tisso āpattiyo.</p>\r\n<p class="subhead">7. Saṅghatedakakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',240,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0240"></a> āvuso channaṃ sakyakumārānaṃ saha upālikappakena pabbajjā ahosi, kathañca nesaṃ viseso udapādi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavā bhante upāliṃ kappakaṃ paṭhamaṃ pabbājesi, pacchā te sakyakumāre, atha bhante āyasmā bhaddiyo teneva antaravassena tisso vijjā sacchākāsi, āyasmā anuruddho dibbacakkhuṃ uppādesi, āyasmā ānando sotāpattiphalaṃ sacchākāsi, devadatto pothujjanikaṃ iddhiṃ abhinipphādesi. Evaṃ kho bhante channaṃ sakyakumārānaṃ upālikappakena saha pabbajjā ahosi, evañca pana bhante tesaṃ visesādhigamo ahosi.</p>\r\n<p class="subhead">Pakāsanīya</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',241,replace(replace('<p class="bodytext">pakāsanīyakammaṃ <a name="M2.0241"></a> āvuso kātuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha paññattaṃ, devadatto bhante ‘‘sarājikāya maṃ bhagavā parisāya kheḷāsakavādena apasādeti, sāriputtamoggallāneva ukkaṃsatī’’ti kupito anattamano bhagavati āghātaṃ bandhi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="subhead">Pakāsanīya</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',243,replace(replace('<p class="bodytext">devadattassa <a name="M2.0243"></a> āvuso pakāsanīyakammaṃ kārāpetvā kathaṃ bhagavā pakāsetuṃ āṇāpesi.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavā bhante devadattassa pakāsanīyakammaṃ kātuṃ paññapetvā āyasmantaṃ sāriputtaṃ āmantesi ‘‘tena hi tvaṃ sāriputta devadattaṃ rājagahe pakāsehī’’ti, evaṃ kho bhante devadattassa pakāsanīyakammaṃ katvā devadattaṃ rājagahe pakāsetuṃ bhagavā āṇāpesi.</p>\r\n<p class="subhead">Devadatta</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',244,replace(replace('<p class="bodytext">kathaṃ <a name="M2.0244"></a> āvuso devadattena duṭṭhacittena vadhakacittena tathā gatassa ruhiraṃ uppādetvā paṭhamaṃ ānantariyaṃ kammaṃ upacitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavā bhante gijjhakūṭassa pabbatassa chāyāyaṃ caṅkami, atha bhante devadatto gijjhakūṭaṃ pabbataṃ āruhitvā mahatiṃ silaṃ pavijjhi ‘‘imāya samaṇaṃ gotamaṃ jīvitā voropessāmī’’ti, atha kho bhante dve pabbatakūṭāni samāgantvā taṃ silaṃ sampaṭicchiṃsu, tato papatikā uppatitvā bhagavato pāde ruhiraṃ uppādesi. Evaṃ kho bhante devadattena duṭṭhena vadhakacittena tathāgatassa ruhiraṃ uppādetvā paṭhamaṃ ānantariyakammaṃ upacitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',245,replace(replace('<p class="bodytext">kulesu <a name="M2.0245"></a> āvuso tikabhojanaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha paññattaṃ, devadatto bhante parihīnalābhasakkāro sapariso kulesu viññāpetvā viññāpetvā bhuñji, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',246,replace(replace('<p class="bodytext">alaṃ <a name="M2.0246"></a> devadatta, mā te rucci saṅghabhedo, garuko kho devadatta saṅghabhedotiādiko āvuso ovādo bhagavatā kattha dinno, kismiṃ vatthusmiṃ dinno.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃ ārabbha dinno, devadatto bhante saṅghabhedāya parakkami cakkabhedāya, tasmiṃ bhante vatthusmiṃ dinno.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',247,replace(replace('<p class="bodytext">Alaṃ <a name="M2.0247"></a> devadatta, mā te rucci saṅghabhedo, garuko kho devadatta saṅghabhedo –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ‘‘sukaraṃ sādhunā sādhuṃ, sādhuṃ pāpena dukkaraṃ. Pāpaṃ pāpena sukaraṃ, pāpamariyehi dukkara’’nti- </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',248,replace(replace('<p class="bodytext">āvuso <a name="M2.0248"></a> idaṃ udānaṃ bhagavatā kattha udānitaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ udānitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante devadattaṃyeva ārabbha udānitaṃ, āyasmā bhante ānando pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya rājagahaṃ piṇḍāya pāvisi, atha kho bhante devadatto yenāyasmā ānando tenupasaṅkami, upasaṅkamitvā āyasmantaṃ ānandaṃ etadavoca ‘‘ajjatagge dānāhaṃ āvuso ānanda aññatreva bhagavatā aññatreva bhikkhusaṅghā uposathaṃ karissāmi saṅghakammaṃ karissāmī’’ti, tasmiṃ bhante vatthusmiṃ udānitaṃ.</p>\r\n<p class="gatha1">Sukaraṃ sādhunā sādhuṃ, sādhuṃ pāpena dukkaraṃ;</p>\r\n<p class="gathalast">Pāpaṃ pāpena sukaraṃ, pāpamariyehi dukkaraṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',249,replace(replace('<p class="bodytext">bhedānuvattakānaṃ <a name="M2.0249"></a> āvuso bhikkhūnaṃ thullaccayaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rājagahe bhante sambahule bhikkhū ārabbha paññattaṃ, āyasmā bhante sāriputto bhagavantaṃ upasaṅkamitvā etadavoca ‘‘sādhu bhante bhedānuvattakā bhikkhū puna upasampajjeyyu’’nti, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',252,replace(replace('<p class="bodytext">‘‘Sādhu <a name="M2.0252"></a> bhante bhedānuvattakā bhikkhū puna upasampajjeyyu’’nti –</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',253,replace(replace('<p class="bodytext">kittāvatā <a name="M2.0253"></a> nu kho āvuso saṅgharāji hoti no ca saṅghabhedo, kittāvatā ca pana saṅgharāji ceva hoti saṅghabhedo ca.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ekato bhante eko hoti, ekato dve catuttho anussāveti, salākaṃ gāheti ‘‘ayaṃ dhammo ayaṃ vinayo idaṃ satthusāsanaṃ imaṃ gaṇhatha imaṃ rocethāti’’, eteneva bhante nayena catunnaṃ vā pañcannaṃ vā channaṃ vā sattannaṃ vā aṭṭhannaṃ vā saṅgharāji hoti, no ca saṅghabhedo, ekato bhante cattāro honti, ekato cattāro, navamo anussāveti, salākaṃ gāheti ‘‘ayaṃ dhammo ayaṃ vinayo idaṃ satthusāsanaṃ imaṃ gaṇhatha imaṃ rocethā’’ti, evaṃ kho bhante saṅgharāji ceva hoti saṅghabhedo ca. Evaṃ kho bhante saṅgharāji hoti, no ca saṅghabhedo, evañca pana bhante saṅgharāji ceva hoti saṅghabhedo ca.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',254,replace(replace('<p class="bodytext">kittāvatā <a name="M2.0254"></a> nu kho āvuso saṅgho bhinno hoti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhante bhikkhū adhammādiṃ dhammādīnīti dīpenti, te imehi aṭṭhārasahi vatthūhi apakassanti, avapakassanti, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',255,replace(replace('<p class="bodytext">āveniṃ <a name="M2.0255"></a> uposathaṃ karonti, āveniṃ pavāraṇaṃ karonti, āveniṃ saṅghakammaṃ karonti, evaṃ kho bhante saṅgho bhinno hoti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kittāvatā nu kho āvuso saṅgho samaggo hoti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> idha bhante bhikkhū adhammaṃ adhammoti dīpenti, dhammaṃ dhammoti dīpenti, avinayaṃ avinayoti dīpenti…pe… duṭṭhullaṃ āpattiṃ duṭṭhullā āpattīti dīpenti, aduṭṭhullaṃ āpattiṃ aduṭṭhullā āpattīti dīpenti, te imehi aṭṭhārasahi vatthūhi na apakassanti, na avapakassanti, na āveniṃ uposathaṃ karonti, na āveniṃ pavāraṇaṃ karonti, na āveniṃ saṅghakammaṃ karonti, ettāvatā kho bhante saṅgho samaggo hoti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',256,replace(replace('<p class="bodytext">samaggaṃ <a name="M2.0256"></a> āvuso saṅghaṃ bhinditvā kiṃ so pasavati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samaggaṃ kho bhante saṅghaṃ bhinditvā kappaṭṭhitikaṃ kibbisaṃ pasavati, kappaṃ nirayamhi paccati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',257,replace(replace('<p class="bodytext">bhinnaṃ <a name="M2.0257"></a> kho āvuso saṅghaṃ samaggaṃ katvā kiṃ so pasavati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhinnaṃ kho bhante saṅghaṃ samaggaṃ katvā brahmaṃ puññaṃ pasavati, kappaṃ saggamhi modati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',258,replace(replace('<p class="bodytext">saṅghabhedaṃ <a name="M2.0258"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> saṅghabhedaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante dve āpattiyo.</p>\r\n<p class="subhead">8. Vattakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',259,replace(replace('<p class="bodytext">āgantukānaṃ <a name="M2.0259"></a> āvuso bhikkhūnaṃ vattaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule āgantuke bhikkhū ārabbha paññattaṃ, āgantukā bhante bhikkhū saupāhanāpi ārāmaṃ pavisiṃsu, chattapaggahitāpi ārāmaṃ pavisiṃsu, oguṇṭhitāpi ārāmaṃ pavisiṃsu, sīsepi cīvaraṃ karitvā ārāmaṃ pavisiṃsu, pānīyenapi pāde doviṃsu, vuḍḍhatarepi āvāsike bhikkhū nābhivādesuṃ, napi senāsanaṃ pucchiṃsu, tasmiṃ bhante vatthusmiṃ ‘‘tena hi bhikkhave āgantukānaṃ bhikkhūnaṃ vattaṃ paññapessāmi, yathā āgantukehi bhikkhūhi sammā vattitabbaṃ, āgantukena bhikkhave bhikkhunā idāni ārāmaṃ pavisissāmīti upāhanā omuñcitvā nīcaṃ katvā papphoṭetvā gahetvā chattaṃ apanāmetvā sīsaṃ vivaritvā sīse cīvaraṃ khandhe katvā sādhukaṃ ataramānena ārāmo pavisitabbo’’ evamādinā bhante āgantukānaṃ bhikkhūnaṃ vattaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',261,replace(replace('<p class="bodytext">āvāsikānaṃ <a name="M2.0261"></a> āvuso bhikkhūnaṃ vattaṃ bhagavatā kattha kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ, kathañca pana paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule āvāsike bhikkhū ārabbha paññattaṃ, sambahulā bhante āvāsikā bhikkhū āgantuke bhikkhū disvā neva āsanaṃ paññapesuṃ, na pādodakaṃ pādapīṭhaṃ pādakathalikaṃ upanikkhipiṃsu, na paccuggantvā pattacīvaraṃ paṭiggahesuṃ, na senāsanaṃ paññapesuṃ, tasmiṃ bhante vatthusmiṃ paññattaṃ, tasmiṃ bhante vatthusmiṃ –</p>\r\n<p class="bodytext">‘‘Āvāsikena bhikkhave bhikkhunā āgantukaṃ bhikkhuṃ vuḍḍhataraṃ disvā āsanaṃ paññapetabbaṃ, pādodakaṃ pādapīṭhaṃ pādakathalikaṃ upanikkhipitabbaṃ, paccuggantvā pattacīvaraṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',262,replace(replace('<p class="bodytext">paṭiggahetabbaṃ <a name="M2.0262"></a> pānīyena pucchitabbo’’ evamādinā bhante āvāsikānaṃ bhikkhūnaṃ vattaṃ bhagavatā paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',263,replace(replace('<p class="bodytext">gamikānaṃ <a name="M2.0263"></a> āvuso bhikkhūnaṃ vattaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ, kathañcapana paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule gamike bhikkhū ārabbha paññattaṃ, sambahulā bhante gamikā bhikkhū dārubhaṇḍaṃ mattikābhaṇḍaṃ appaṭisāmetvā dvāravātapānaṃ vivaritvā senāsanaṃ anāpucchā pakkamiṃsu, tasmiṃ bhante vatthusmiṃ ‘‘gamikena bhikkhave bhikkhunā dārubhaṇḍaṃ paṭisāmetvā dvāravātapānaṃ thaketvā senāsanaṃ āpucchā pakkamitabbaṃ’’ evamādinā bhante ākārena bhagavatā gamikānaṃ bhikkhūnaṃ vattaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',264,replace(replace('<p class="bodytext">bhattagge <a name="M2.0264"></a> āvuso anumodituṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',265,replace(replace('<p class="bodytext">sāvatthiyaṃ <a name="M2.0265"></a> bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū bhattagge nānumodiṃsu, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> bhikkhūnaṃ āvuso bhattaggavattaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū dunnivatthā duppārutā anākappasampannā bhattaggaṃ gacchiṃsu, tasmiṃ bhante vatthusmiṃ ‘‘sace ārāme kālo ārocito hoti, timaṇḍalaṃ paṭicchādentena parimaṇḍalaṃ nivāsetvā kāyabandhanaṃ bandhitvā saguṇaṃ katvā saṅghāṭiyo pārupitvā gaṇṭhikaṃ paṭimuñcitvā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',266,replace(replace('<p class="bodytext">dhovitvā <a name="M2.0266"></a> pattaṃ gahetvā sādhukaṃ ataramānena gāmo pavisitabbo’’ti evamādinā bhante bhattaggavattaṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',267,replace(replace('<p class="bodytext">bhikkhūnaṃ <a name="M2.0267"></a> āvuso senāsanavattaṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ, kathañca taṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante chabbaggiye bhikkhū ārabbha paññattaṃ, chabbaggiyā bhante bhikkhū bhikkhūsu ajjhokāse cīvaraṃ karontesu paṭivāte aṅgaṇe senāsanaṃ papphoṭesuṃ, tasmiṃ bhante vatthusmiṃ ‘‘yasmiṃ vihāre viharati, sace so vihāro uklāpo hoti, sace ussahati sodhetabbo’’ti evamādinā bhante bhikkhūnaṃ senāsanavattaṃ bhagavatā paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> samācāraṃ pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> samācāraṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">9. Pātimokkhaṭṭhapanakkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',270,replace(replace('<p class="bodytext">sāpattikena <a name="M2.0270"></a> āvuso pātimokkhaṃ suṇantassa pātimokkhaṃ ṭhapetuṃ bhagavatā kattha paññattaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante aññataraṃ puggalaṃ dussīlaṃ pāpadhammaṃ ārabbha paññattaṃ, aññataro bhante puggalo dussīlo pāpadhammo tadahuposathe saṅghamajjhe nisinno ahosi, tasmiṃ bhante vatthusmiṃ paññattaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',272,replace(replace('<p class="bodytext">ṭhapanaṃ <a name="M2.0272"></a> āvuso pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> ṭhapanaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante ekā āpatti.</p>\r\n<p class="subhead">10. Bhikkhunikkhandhaka</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',273,replace(replace('<p class="bodytext">aṭṭhahi <a name="M2.0273"></a> āvuso garudhammehi upasampadā bhagavatā kattha anuññātā, kassa anuññātā, kismiñca vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante mahāpajāpatiyā gotamiyā anuññātā, mahāpajāpati bhante gotamī sūnehi pādehi rajokiṇṇena gattena dukkhī dummanā assumukhī rudamānā bahidvārakoṭṭhake aṭṭhāsi, atha kho bhante āyasmā ānando mahāpajāpatiṃ gotamiṃ taṃ kāraṇaṃ pucchitvā bhagavantaṃ catukkhattuṃ yāci mātugāmassa tathāgatappavedite dhammavinaye āgārasmā anāgāriyaṃ pabbajjaṃ, tasmiṃ bhante vatthusmiṃ anuññātā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',275,replace(replace('<p class="bodytext">bhikkhūhi <a name="M2.0275"></a> āvuso bhikkhuniyo upasampādetuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> vesāliyaṃ bhante mahāpajāpatiṃ gotamiṃ ārabbha anuññātaṃ, mahāpajāpati bhante gotamī bhagavantaṃ etadavoca ‘‘kathāhaṃ bhante imāsu sākiyānīsu paṭipajjāmī’’ti, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',276,replace(replace('<p class="bodytext">ekato <a name="M2.0276"></a> upasampannāya āvuso bhikkhunisaṅghe visuddhāya bhikkhusaṅghe upasampādetuṃ bhagavatā kattha anuññātaṃ, kaṃ ārabbha kismiṃ vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sāvatthiyaṃ bhante sambahule bhikkhū ārabbha anuññātaṃ, sambahulā bhante bhikkhū bhikkhunīnaṃ antarāyike dhamme pucchiṃsu, upasampadāpekkhāyo vitthāyiṃsu, maṅkū ahesuṃ, na sakkhiṃsu vissajjetuṃ, tasmiṃ bhante vatthusmiṃ anuññātaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',277,replace(replace('<p class="bodytext">bhikkhunikkhandhakaṃ <a name="M2.0277"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhikkhunikkhandhakaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante dve āpattiyo.</p>\r\n<p class="subhead">11. Pañcasatikakkhandhaka</p>\r\n<p class="subhead">Paṭhama saṃgāyanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',279,replace(replace('<p class="bodytext">pañcasatikaṃ <a name="M2.0279"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcasatikaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante na katamā āpatti.</p>\r\n<p class="subhead">12. Sattasatikakkhandhaka</p>\r\n<p class="subhead">Dutiya saṃgāyanā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',280,replace(replace('<p class="bodytext">sattasatikaṃ <a name="M2.0280"></a> pucchissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ āvuso kati āpattiyo.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattasatikaṃ vissajjissaṃ, sanidānaṃ saniddesaṃ. Samukkaṭṭhapadānaṃ bhante na katamā āpatti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',281,replace(replace('<p class="bodytext">kassa <a name="M2.0281"></a> āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekkhā ca bhante puthujjanakalyāṇakā ca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_77',282,replace(replace('<p class="bodytext">ke <a name="M2.0282"></a> āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha āvuso ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sikkhākāmesu bhante ṭhitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kenāvuso ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paramparāya bhante ābhataṃ.</p>','\r',char(13)),'\n',char(10)),'');