INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',1,replace(replace('<p class="bodytext">paṭhamamahāsaṃgītikāle <a name="M0.0001"></a> āvuso dhammasaṃgāhakā mahākassapādayo mahātheravarā porāṇasaṃgītikārā paṭhamaṃ vinayapiṭakaṃ saṃgāyitvā suttantapiṭake ca dīghamajjhimasaṃyuttaaṅguttarasaṃkhāte cattāro mahānikāye saṃgāyitvā tadanantaraṃ kiṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamamahāsaṃgītikāle bhante dhammasaṃgāhakā mahākassapādayo mahātheravarā porāṇasaṃgītikārā paṭhamaṃ vinayapiṭakaṃ saṃgāyitvā suttantapiṭake ca dīghamajjhimasaṃyuttaaṅguttarasaṃkhāte cattāro nikāye saṃgāyitvā tadanantaraṃ abhidhammapiṭakaṃ nāma pāvacanaṃ saṃgāyiṃsu.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',2,replace(replace('<p class="bodytext">imissampi <a name="M0.0002"></a> āvuso chaṭṭhasaṃgītiyaṃ sakalañceva vinayapiṭakaṃ saṃgītaṃ, te ca cattāro mahānikāyā. Kālo dāni āvuso sampatto abhidhammapiṭakaṃ saṃgāyituṃ, tasmāhaṃ taṃ tattha pucchitabbāni pucchissāmi. Abhidhammo nāmesa āvuso kenaṭṭhena abhidhammoti vuccati.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dhammātireka dhammavisesaṭṭhena bhante abhidhammoti vuccati.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',5,replace(replace('<p class="bodytext">so <a name="M0.0005"></a> panesa āvuso abhidhammo kena kattha kadā ca adhigato.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> so kho bhante abhidhammo sabbaññubuddhena mahābodhimaṇḍamūle vesākhapuṇṇamiyaṃ yathābhūtaṃ adhigato.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kattha panesa āvuso abhidhammo bhagavatā kadā ca vicito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> so kho bhante abhidhammo bhagavatā mahābodhimaṇḍe ratanagharasattāhe vicito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',7,replace(replace('<p class="bodytext">kattha <a name="M0.0007"></a> panesa āvuso abhidhammo bhagavatā kadā kassatthāya ca desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> so kho bhante abhidhammo bhagavatā devesu tāvatiṃsesu pāricchattakamūlamhi paṇḍukambalasilāyaṃ abhisambodhito sattame vasse mātaraṃ pamukhaṃ katvā dasahi cakkavāḷasahassehi āgamma sannisinnānaṃ devatānaṃ caturoghanittharaṇatthāya anto vassaṃ desito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',8,replace(replace('<p class="bodytext">kenesa <a name="M0.0008"></a> āvuso abhidhammo paṭhamaṃ manussaloke patiggahito, kassa ca puna tena desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> āyasmatā bhante sāriputtattherena dhammasenāpatinā esa abhidhammo paṭhamaṃ manussaloke patiggahito, teneva bhante āyasmatā sāriputtattherena attano saddhivihārikānaṃ pañcannaṃ bhikkhusatānaṃ desito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',11,replace(replace('<p class="bodytext">ke <a name="M0.0011"></a> āvuso sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sekhā ca bhante puthujjanā kalyāṇakā ca sikkhanti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> ke āvuso sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> arahanto bhante sikkhitasikkhā.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',12,replace(replace('<p class="bodytext">ke <a name="M0.0012"></a> āvuso dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> yesaṃ bhante vattati, te dhārenti.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kassa āvuso vacanaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> bhagavato bhante vacanaṃ arahato sammāsambuddhassa.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',13,replace(replace('<p class="bodytext">kenāvuso <a name="M0.0013"></a> ābhataṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paraṃparāya bhante ābhataṃ.</p>\r\n<p class="centered">Dhammasaṅgaṇī</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',14,replace(replace('<p class="bodytext">so <a name="M0.0014"></a> panesa āvuso abhidhammo dhammasaṅgaṇivibhaṅgādipakaraṇaparicchedavasena sattavidho. Tesu paṭhamaṃ bhagavatā kataraṃ pakaraṇaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> paṭhamaṃ bhante dhammasaṅgaṇipakaraṇaṃ bhagavatā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> dhammasaṅgaṇiyaṃ āvuso atthi mātikā, atthi padabhājanīyaṃ. Tesu mātikaṃ nikkhipantena bhagavatā kataṃ nikkhittā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> mātikaṃ bhante nikkhipantena bhagavatā ‘‘kusalā dhammā akusalā dhammā abyākatā dhammā. Sukhāya vedanāya sampayuttā dhammā dukkhāya vedanāya sampayuttā dhammā adukkhamasukhāya vedanāya sampayuttā dhammā. Vipākā dhammā vipākadhammadhammā nevavipākanavipākadhammadhammā’’ti evamādinā dvāvīsatiyā tikānaṃ vasena. ‘‘Hetū dhammā na hetū dhammā. Sahetukā dhammā ahetukā dhammā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',15,replace(replace('<p class="bodytext">Hetusampayuttā <a name="M0.0015"></a> dhammā hetuvippayuttā dhammā. Hetū ceva dhammā sahetukā ca sahetukā ceva dhammā na ca hetū. Hetū ceva dhammā hetusampayuttā ca hetusampayuttā ceva dhammā na ca hetū. Na hetū kho pana dhammā sahetukāpi ahetukāpī’’ti evamādinā dukasatānañca vasena mātikā nikkhittā.</p>\r\n<p class="subhead">Kāmāvacarakusala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',16,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0016"></a> āvuso dhammasaṅgaṇiyaṃ padabhājanīye cittuppādakaṇḍe kusalā dhammātipadassa atthaṃ vibhajantena bhagavatā kathaṃ kāmāvacarakusalaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante dhammasaṅgaṇipakaraṇe cittuppādakaṇḍe kusalā dhammāti padassa atthaṃ vibhajantena bhagavatā ‘‘katame dhammā kusalā. Yasmiṃ samaye kāmāvacaraṃ kusalaṃ cittaṃ uppannaṃ hoti somanassasahagataṃ ñāṇasampayuttaṃ rūpārammaṇaṃ vā saddārammaṇaṃ vā gandhārammaṇaṃ vā rasārammaṇaṃ vā phoṭṭhabbārammaṇaṃ vā dhammārammaṇaṃ vā yaṃ yaṃ vā panārabbha. Tasmiṃ samaye phasso hoti vedanā hoti saññā hoti cetanā hoti cittaṃ hoti vitakko hoti vicāro hoti pīti hoti sukhaṃ hoti cittassekaggatā hotī’’ti evamādinā kāmāvacarakusalaṃ tīhi mahāvārehi vibhajitvā desitaṃ.</p>\r\n<p class="subhead">Rūpāvacarakusala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',18,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0018"></a> āvuso dhammasaṅgaṇīpāḷiyaṃ cittuppādakaṇḍe kusalā dhammāti padassa atthaṃ vibhajantena bhagavatā kathaṃ rūpāvacarakusalañca arūpāvacarakusalañca vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> rūpāvacarakusalañca arūpāvacarakusalañca bhante ‘‘katame dhammā kusalā. Yasmiṃ samaye rūpūpapattiyā maggaṃ bhāveti vivicceva kāmehi vivicca akusalehi dhammehi savitakkaṃ savitakkaṃ vivekajaṃ pītisukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati pathavīkasiṇa’’nti evamādinā bhagavatā vitthārato desitaṃ.</p>\r\n<p class="subhead">Lokuttarākusala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',21,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0021"></a> āvuso dhammasaṅgaṇīpāḷiyaṃ cittuppādakaṇḍe kusalā dhammāti padassa atthaṃ vibhajantena bhagavatā kathaṃ lokuttarakusalaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante dhammasaṅgaṇiyaṃ cittuppādakaṇḍe kusalā dhammāti padassa atthaṃ vibhajantena bhagavatā ‘‘katame dhammā kusalā. Yasmiṃ samaye lokuttaraṃ jhānaṃ bhāveti niyyānikaṃ apacayagāmiṃ diṭṭhigatānaṃ pahānāya paṭhamāya bhūmiyā pattiyā vivicceva kāmehi…pe… paṭhamaṃ jhānaṃ upasampajja viharati dukkhapaṭipadaṃ dandhābhiñña’’nti evamādinā bhagavatā lokuttarakusalaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="subhead">Akusala</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',23,replace(replace('<p class="bodytext">tattheva <a name="M0.0023"></a> āvuso akusalā dhammāti mātikāpadassa atthaṃ vibhajantena bhagavatā kathaṃ akusalaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattheva bhante akusalā dhammāti mātikāpadassa atthaṃ vibhajantena bhagavatā ‘‘katame dhammā akusalā. Yasmiṃ samaye akusalaṃ cittaṃ uppannaṃ hoti somanassasahagataṃ diṭṭhigatasampayuttaṃ rūpārammaṇaṃ vā saddhārammaṇaṃ vā gandhārammaṇaṃ vā rasārammaṇaṃ vā phoṭṭhabbārammaṇaṃ vā dhammārammaṇaṃ vā yaṃ yaṃ vā panārabbha. Tasmiṃ samaye phasso hoti vedanā hoti saññā hoti cetanā hoti cittaṃ hotī’’ti evamādinā akusalaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="subhead">Abyākata</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> abhidhammapiṭake āvuso dhammasaṅgaṇīpāḷiyaṃ cittuppādakaṇḍarūpakaṇḍesu abyākatā dhammāti mātikāpadassa atthaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',24,replace(replace('<p class="bodytext">vibhajantena <a name="M0.0024"></a> bhagavatā kathaṃ abyākatā dhammā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante dhammasaṅgaṇiyaṃ abyākatā dhammāti padassa atthaṃ vibhajantena bhagavatā ‘‘katame dhammā abyākatā. Yasmiṃ samaye kāmāvacarassa kusalassa kammassa katattā upacitattā vipākaṃ cakkhuviññāṇaṃ uppannaṃ hoti upekkhāsahagataṃ rūpārammaṇaṃ. Tasmiṃ samaye phasso hoti vedanā hoti saññā hoti cetanā hoti cittaṃ hotī’’ti evamādinā ca cittuppādakaṇḍe, rūpakaṇḍe ca ‘‘katame dhammā abyākatā. Kusalākusalānaṃ dhammānaṃ vipākā kāmāvacarā rūpāvacarā arūpāvacarā apariyāpannā vedanākkhandho saññākkhandho saṅkhārakkhandho viññāṇakkhandho. Ye ca dhammā kiriyā neva kusalā nākusalā na ca kammavipākā. Sabbañca rūpaṃ asaṅkhatā ca dhātu. Ime dhammā abyākatā’’ti evamādinā ca abyākatā dhammā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Vibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',26,replace(replace('<p class="bodytext">sattasu <a name="M0.0026"></a> āvuso abhidhammappakaraṇesu vibhaṅgappakaraṇe bhagavatā kati vibhaṅgā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante sattasu pakaraṇesu dutiye vibhaṅgappakaraṇe khandhavibhaṅgo āyatanavibhaṅgo dhātuvibhaṅgo saccavibhaṅgo indriyavibhaṅgo paṭiccasamuppādavibhaṅgo satipaṭṭhānavibhaṅgo sammappadhānavibhaṅgo iddhipādavibhaṅgo bojjhaṅgavibhaṅgo maggaṅgavibhaṅgo jhānavibhaṅgo appamaññāvibhaṅgo sikkhāpadavibhaṅgo paṭisambhidāvibhaṅgo ñāṇavibhaṅgo khuddakavatthuvibhaṅgo dhammahadayavibhaṅgoti bhagavatā aṭṭhārasa vibhaṅgā desitā.</p>\r\n<p class="subhead">Khandhavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tesu āvuso aṭṭhārasasu vibhaṅgesu paṭhame khandhavibhaṅge bhagavatā kathaṃ khandhā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',27,replace(replace('<p class="bodytext">tesu <a name="M0.0027"></a> bhante aṭṭhārasasu vibhaṅgesu paṭhame khandhavibhaṅge ‘‘pañcakkhandhā rūpakkhandho vedanākkhandho saññākkhandho saṅkhārakkhandho viññāṇakkhandho. Tattha katamo rūpakkhandho, yaṃkiñci rūpaṃ atītānāgatapaccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā, tadekajjhaṃ abhisaññūhitvā abhisaṅkhipitvā ayaṃ vuccati rūpakkhandho’’ti evamādinā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi bhagavatā khandhā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Āyatanavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',28,replace(replace('<p class="bodytext">dutiye <a name="M0.0028"></a> pana āvuso āyatanavibhaṅge bhagavatā kathaṃ āyatanāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dutiye bhante āyatanavibhaṅge ‘‘dvādasāyatanāni cakkhāyatanaṃ rūpāyatanaṃ sotāyatanaṃ saddāyatanaṃ ghānāyatanaṃ gandhāyatanaṃ jivhāyatanaṃ rasāyatanaṃ kāyāyatanaṃ phoṭṭhabbāyatanaṃ manāyatanaṃ dhammāyatanaṃ. Cakkhuṃ aniccaṃ dukkhaṃ anattā vipariṇāmadhammaṃ, rūpā aniccā dukkhā anattā vipariṇāmadhammā’’ti evamādinā bhagavatā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīheva āyatanāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Dhātuvibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',29,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0029"></a> āvuso vibhaṅgappakaraṇe tatiye dhātuvibhaṅge bhagavatā kathaṃ dhātuyo vitthārena vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tatiye bhante dhātuvibhaṅge ‘‘cha dhātuyo pathavīdhātu āpodhātu tejodhātu vāyodhātu ākāsadhātu viññāṇadhātū’’ti bhante suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi bhagavatā dhātuyo vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Saccavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',31,replace(replace('<p class="bodytext">catutthe <a name="M0.0031"></a> pana āvuso saccavibhaṅge bhagavatā kathaṃ saccāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> catutthe bhante saccavibhaṅge ‘‘cattāri ariyasaccāni dukkhaṃ ariyasaccaṃ, dukkhasamudayaṃ ariyasaccaṃ, dukkhanirodhaṃ ariyasaccaṃ, dukkhanirodhagāminī paṭipadā ariyasaccaṃ. Tattha katamaṃ dukkhaṃ ariyasaccaṃ, jātipi dukkhā, jarāpi dukkhā, maraṇampi dukkhaṃ, sokaparidevadukkhadomanassupāyāsāpi dukkhā, appiyehi sampayogo dukkho, piyehi vippayogo dukkho, yampicchaṃ na labhati, tampi dukkhaṃ, saṃkhittena pañcupādānakkhandhā dukkhā’’ti evamādinā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi bhagavatā saccāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Indriyavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',32,replace(replace('<p class="bodytext">pañcame <a name="M0.0032"></a> pana āvuso indriyavibhaṅge bhagavatā kathaṃ indriyāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pañcame pana bhante indriyavibhaṅge ‘‘bāvīsatindriyāni cakkhundriyaṃ sotindriyaṃ ghānindriyaṃ jivhindriyaṃ kāyindriyaṃ manindriyaṃ…pe… aññātāvindriya’’nti evamādinā abhidhammabhājanīyapañhāpucchakasaṅkhātehi dvīhi mahānayehi bhagavatā indriyāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Paṭiccasamuppāda vibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',33,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0033"></a> āvuso vibhaṅgappakaraṇe chaṭṭhe paṭiccasamuppādavibhaṅge bhagavatā kathaṃ paṭiccasamuppādo vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chaṭṭhe bhante paṭiccasamuppādavibhaṅge ‘‘avijjāpaccayā saṅkhārā…pe… samudayo hotī’’ti evamādinā bhagavatā paṭiccasamuppādo suttantabhājanīyaabhidhammabhājanīyasaṅkhātehi dvīhi mahānayehi vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Satipaṭṭhānavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',34,replace(replace('<p class="bodytext">sattame <a name="M0.0034"></a> pana āvuso satipaṭṭhānavibhaṅge bhagavatā kathaṃ satipaṭṭhānā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sattame bhante satipaṭṭhānavibhaṅge ‘‘cattāro satipaṭṭhānā idha bhikkhu ajjhattaṃ kāye kāyānupassī viharati, bahiddhā kāye kāyānupassī viharati, ajjhattabahiddhā kāye kāyānupassī viharati ātāpī sampajāno satimā vineyya loke abhijjhādomanassa’’nti evamādinā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi bhagavatā satipaṭṭhānā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Sammappadhānavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',35,replace(replace('<p class="bodytext">aṭṭhame <a name="M0.0035"></a> pana āvuso sammappadhānavibhaṅge bhagavatā kathaṃ sammappadhānā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> aṭṭhame bhante sammappadhānavibhaṅge ‘‘cattāro sammappadhānā idha bhikkhu anuppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ anuppādāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, uppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, anuppannānaṃ kusalānaṃ dhammānaṃ uppādāya chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahati, uppannānaṃ kusalānaṃ dhammānaṃ ṭhitiyā asammosāya bhiyyobhāvāya vepullāya bhāvanāya pāripūriyā chandaṃ janeti vāyamati vīriyaṃ ārabhati cittaṃ paggaṇhāti padahatī’’ti evamādinā bhagavatā sammappadhānā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Iddhipādavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',36,replace(replace('<p class="bodytext">navame <a name="M0.0036"></a> pana āvuso iddhipādavibhaṅge bhagavatā kathaṃ iddhipādā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> navame bhante iddhipādavibhaṅge ‘‘cattāro iddhipādā idha bhikkhu chandasamādhipadhānasaṅkhārasamannāgataṃ iddhipādaṃ bhāveti…pe… vīmaṃsāsamādhipadhānasaṅkhārasamannāgataṃ iddhipādaṃ bhāvetī’’ti evamādinā tīhi mahānayehi bhagavatā iddhipādā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Bojjhaṅgavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',37,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0037"></a> āvuso vibhaṅgappakaraṇe dasame bojjhaṅgavibhaṅge bhagavatā kathaṃ bojjhaṅgā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dasame bhante bojjhaṅgavibhaṅge ‘‘satta bojjhaṅgā satisambojjhaṅgo dhammavicayasambojjhaṅgo vīriyasambojjhaṅgo pītisambojjhaṅgo passaddhisambojjhaṅgo samādhisambojjhaṅgo upekkhāsambojjhaṅgo. Tattha katamo satisambojjhaṅgo, idha bhikkhu satimā hoti paramena satinepakkena samannāgato cirakatampi cirabhāsitampi saritā hoti anussaritā, ayaṃ vuccati satisambojjhaṅgo’’ti evamādinā bhagavatā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi nayehi bojjhaṅgā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Maggaṅgavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',38,replace(replace('<p class="bodytext">ekādasame <a name="M0.0038"></a> pana āvuso maggaṅgavibhaṅge bhagavatā kathaṃ maggaṅgā vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',39,replace(replace('<p class="bodytext">ekādasame <a name="M0.0039"></a> bhante maggaṅgavibhaṅge ‘‘ariyo aṭṭhaṅgiko maggo. Seyyathidaṃ, sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto sammāājīvo sammāvāyāmo sammāsati sammāsamādhī’’ti evamādinā bhagavatā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi maggaṅgā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Jhānavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',40,replace(replace('<p class="bodytext">dvādasamo <a name="M0.0040"></a> pana āvuso jhānavibhaṅgo bhagavatā kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dvādasamo bhante jhānavibhaṅgo ‘‘idha bhikkhu pātimokkhasaṃvarasaṃvuto viharati ācāragocarasampanno aṇumattesu vajjesu bhayadassāvī samādāya sikkhati sikkhāpadesu indriyesu guttadvāro bhojane mattaññū pubbarattāpararattaṃ jāgariyānuyogamanuyutto’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Appamaññāvibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',41,replace(replace('<p class="bodytext">terasame <a name="M0.0041"></a> pana āvuso appamaññāvibhaṅge bhagavatā kathaṃ appamaññāyo vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> terasame bhante appamaññāvibhaṅge ‘‘catasso appamaññāyo idha bhikkhu mettāsahagatena cetasā ekaṃ disaṃ pharitvā viharati, tathā dutiyaṃ. Tathā tatiyaṃ. Tathā catutthaṃ. Iti uddhamadho tiriyaṃ sabbadhi sabbattatāya sabbāvantaṃ lokaṃ mettāsahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjena pharitvā viharatī’’ti evamādinā bhagavatā suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi tīhi mahānayehi appamaññāyo vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Sikkhāpadavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',42,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0042"></a> āvuso vibhaṅgappakaraṇe cuddasame sikkhāpadavibhaṅge bhagavatā kathaṃ sikkhāpadāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> cuddasame bhante sikkhāpadavibhaṅge ‘‘pañca sikkhāpadāni pāṇātipātā veramaṇī sikkhāpadaṃ, adinnādānā veramaṇī sikkhāpadaṃ, kāmesumicchācārā veramaṇī sikkhāpadaṃ, musāvādā veramaṇī sikkhāpadaṃ, surāmerayamajjapamādaṭṭhānā veramaṇī sikkhāpadaṃ. Tattha katamaṃ pāṇātipātā veramaṇī sikkhāpadaṃ, yasmiṃ samaye kāmāvacaraṃ kusalaṃ cittaṃ uppannaṃ hoti somanassasahagataṃ ñāṇasampayuttaṃ pāṇātipātā viramantassā’’ti evamādinā abhidhammabhājanīyapañhāpucchakasaṅkhātehi dvīhi mahānayehi bhagavatā sikkhāpadāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Paṭisambhidāvibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',43,replace(replace('<p class="bodytext">pannarasame <a name="M0.0043"></a> pana āvuso paṭisambhidāvibhaṅge bhagavatā kathaṃ paṭisambhidāyo vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> pannarasame bhante paṭisambhidāvibhaṅge ‘‘catasso paṭisambhidā atthapaṭisambhidā dhammapaṭisambhidā niruttipaṭisambhidā paṭibhānapaṭisambhidā. Atthe ñāṇaṃ atthapaṭisambhidā, dhamme ñāṇaṃ dhammapaṭisambhidā, tatra dhammaniruttābhilāpe ñāṇaṃ niruttipaṭisambhidā, ñāṇesu ñāṇaṃ paṭibhānapaṭisambhidā’’ti evamādinā bhagavatā catasso paṭisambhidāyo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',44,replace(replace('<p class="bodytext">suttantabhājanīyaabhidhammabhājanīyapañhāpucchakasaṅkhātehi <a name="M0.0044"></a> tīhi mahānayehi vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Ñāṇavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> soḷasame pana āvuso ñāṇavibhaṅge bhagavatā kathaṃ ñāṇāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> soḷasame bhante ñāṇavibhaṅge ‘‘ekavidhena ñāṇavatthu pañcaviññāṇā na hetū, ahetukā, hetuvippayuttā, </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',45,replace(replace('<p class="bodytext">sappaccayā<a name="M0.0045"></a>, saṅkhatā, arūpā, lokiyā, sāsavā’’ti evamādinā ekakato paṭṭhāya yāva dasakā bhagavatā ñāṇāni vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Khuddakavatthuvibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> abhidhammapiṭake āvuso vibhaṅgappakaraṇe sattarasamo khuddakavatthuvibhaṅgo bhagavatā kathaṃ desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante dutiye vibhaṅgappakaraṇe sattarasamo khuddakavatthuvibhaṅgo ‘‘jātimado, gottamado, ārogyamado, yobbanamado, jīvitamado’’ti evamādinā ekakato paṭṭhāya yāva aṭṭhasatataṇhāvicaritaṃ bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Dhammahadayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',46,replace(replace('<p class="bodytext">tattha <a name="M0.0046"></a> āvuso aṭṭhārasamo dhammahadayavibhaṅgo bhagavatā kathaṃ vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',47,replace(replace('<p class="bodytext">aṭṭhārasamo <a name="M0.0047"></a> bhante dhammahadayavibhaṅgo ‘‘kati khandhā, kati āyatanāni, kati dhātuyo, kati saccāni, kati indriyāni, kati hetū, kati āhārā, kati phassā, kati vedanā, kati saññā, kati cetanā, kati cittāni. Pañcakkhandhā, dvādasāyatanāni, aṭṭhārasa dhātuyo, cattāri saccāni, bāvīsatindriyāni, nava hetū, cattāro āhārā, satta phassā, satta vedanā, satta saññā, satta cetanā, satta cittānī’’ti evamādinā sabbasaṅgāhakādīhi dasahi vārehi bhagavatā vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> kathañcāvuso tattha bhagavatā uppādakakammaāyuppamāṇavāro vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> dānaṃ datvā sīlaṃ samādiyitvā uposathakammaṃ katvā kattha upapajjanti. Dānaṃ datvā, sīlaṃ samādiyitvā uposathakammaṃ </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',48,replace(replace('<p class="bodytext">katvā <a name="M0.0048"></a> appekacce khattiyamahāsālānaṃ sahabyataṃ upapajjanti, appekacce brāhmaṇamahāsālānaṃ, appekacce gahapatimahāsālānaṃ, appekacce cātumahārājikānaṃ devānaṃ, appekacce tāvatiṃsānaṃ devānaṃ, appekacce yāmānaṃ devānaṃ, appekacce tusitānaṃ devānaṃ sahabyataṃ upapajjantīti evamādinā bhante tattha bhagavatā uppādakakammaāyuppamāṇavāro vitthārena vibhajitvā desito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',51,replace(replace('<p class="gatha1">Ukkhittā <a name="M0.0051"></a> puññatejena, kāmarūpagatiṃ gatā;</p>\r\n<p class="gathalast">Bhavaggatampi sampattā, punāgacchanti duggatiṃ –</p>\r\n<p class="subhead">Dhātukathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',52,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0052"></a> bhagavatā aṭṭhārasahi buddhadhammehi samannāgatena abhidhamme aṭṭhārasahi vibhaṅgehi paṭimaṇḍitaṃ dutiyaṃ vibhaṅgappakaraṇaṃ desetvā tadanantaraṃ dhātukathaṃ nāma tatiyaṃ pakaraṇaṃ kathaṃ vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tatiyaṃ bhante dhātukathaṃ nāma abhidhammapiṭakaṃ ‘‘saṅgaho asaṅgaho, saṅgahitena asaṅgahitaṃ, asaṅgahitena saṅgahitaṃ, saṅgahitena saṅgahitaṃ, asaṅgahitena asaṅgahitaṃ. Sampayogo vippayogo, sampayuttena vippayuttaṃ, vippayuttena sampayuttaṃ, sampayuttena sampayuttaṃ, vippayuttena vippayuttaṃ. Saṅgahitena sampayuttaṃ vippayuttaṃ, sampayuttena saṅgahitaṃ asaṅgahitaṃ, asaṅgahitena sampayuttaṃ vippayuttaṃ, vippayuttena saṅgahitaṃ asaṅgahita’’nti evamādinā pañca mātikāyo paṭhamaṃ nikkhipitvā saṅgahāsaṅgahādīhi cuddasahi nayehi bhagavatā vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="subhead">Puggalapaññatti</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',54,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0054"></a> bhagavatā sabbadhātukusalena abhidhammapiṭake tatiyaṃ dhātukathāpakaraṇaṃ desetvā tadanantaraṃ catutthaṃ puggalapaññattippakaraṇaṃ kathaṃ vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante catutthaṃ puggalapaññattippakaraṇaṃ ‘‘cha paññattiyo khandhapaññatti āyatanapaññatti dhātupaññatti saccapaññatti indriyapaññatti puggalapaññatti. Kittāvatā khandhānaṃ khandhapaññatti, yāvatā pañcakkhandhā, rūpakkhandho vedanākkhandho saññākkhandho saṅkhārakkhandho viññāṇakkhandho. Ettāvatā khandhānaṃ khandhapaññattī’’ti evamādinā khandhāyatanadhātusaccaindriyapaññattiyo saṃkhittena uddisitvā puggalapaññattiṃ ca ekakadukatikacatukkapañcakachakkasattakaaṭṭhakanavakadasakavasena vitthārato vibhajitvā bhagavatā desitā.</p>\r\n<p class="subhead">Kathāvatthu</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',56,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0056"></a> bhagavatā sabbaparamatthapaññattidhammakusalena abhidhammapiṭake catutthaṃ puggalapaññattippakaraṇaṃ desetvā tadanantaraṃ pañcamaṃ kathāvatthuppakaraṇaṃ kathaṃ desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> sabbaparamatthapaññattidhammakusalena bhante bhagavatā loke aggapuggalena abhidhammapiṭake catutthaṃ puggalapaññattippakaraṇaṃ desetvā tadanantaraṃ kathāvatthudesanāya vāre sampatte ‘‘anāgate mama sāvako moggaliputtatissatthero nāma uppannaṃ sāsanamalaṃ sodhetvā tatiyasaṅgītiṃ karonto bhikkhusaṅghassa majjhe nisinno sakavāde pañca suttasatāni paravāde pañcāti suttasahassaṃ samodhānetvā, imaṃ pakaraṇaṃ bhājessatī’’ti tassokāsaṃ karontena puggalavāre aṭṭhamukhā vādayuttiṃ ādiṃ katvā sabbakathāmaggesu asampuṇṇabhāṇavāratthāya tantiyā mātikā nikkhepamattena pañcamaṃ kathāvatthuppakaraṇaṃ saṃkhepato desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',57,replace(replace('<p class="bodytext">adhunā <a name="M0.0057"></a> pākaṭaṃ panāvuso vitthāraṃ kathāvatthuppakaraṇaṃ kadā kena kattha kathañca vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> taṃ kho bhante bhagavato nayamukhaṃ nissāya āyasmatā mahāmoggaliputtatissattherena tatiyasaṃgītikāle pāṭaliputte asokārāme saṭṭhibhikkhusatasahassānaṃ samāgame bhagavatā dinnanayavasena tathāgatena ṭhapitamātikaṃ vibhajantena sakavāde pañcasuttasatāni paravāde pañcāti suttasahassaṃ āharitvā idaṃ paravādamathanaṃ adhunā pākaṭaṃ kathāvatthuppakaraṇaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',62,replace(replace('<p class="bodytext">kati <a name="M0.0062"></a> āvuso tattha kathāyo tena āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> tattha bhante vācanāmaggato dve ca satāni sattavīsati ca kathāyo āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>\r\n<p class="subhead">Puggalakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',63,replace(replace('<p class="bodytext">kathañcāvuso <a name="M0.0063"></a> tattha paṭhamā puggalakathā āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> puggalo upalabbhati saccikaṭṭhaparamatthenāti, āmantā. Yo saccikaṭṭho paramattho, tato so puggalo upalabbhati saccikaṭṭhaparamatthenāti, na hevaṃ vattabbe. Ājānāhi niggahaṃ hañci puggalo upalabbhati saccikaṭṭhaparamatthena, tena vata re vattabbe ‘‘yo saccikaṭṭho paramattho, tato so puggalo upalabbhati saccikaṭṭhaparamatthenā’’ti. Yaṃ tattha vadesi ‘‘vattabbe kho ‘puggalo upalabbhati saccikaṭṭhaparamatthena’, no ca vattabbe ‘yo saccikaṭṭho paramattho, tato so puggalo upalabbhati saccikaṭṭhaparamatthenā’’ti, micchāti evamādinā bhante tattha paṭhamā puggalakathā pañca bhāṇavāramattāya tantiyā āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>\r\n<p class="subhead">Aññāṇakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',64,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0064"></a> āvuso kathāvatthuppakaraṇe dutiyavagge dutiyā aññāṇakathā āyasmatā mahāmoggaliputtatissattherena kathaṃ vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante pañcame kathāvatthuppakaraṇe dutiyavagge dutiyā aññāṇakathā ‘‘atthi arahato aññāṇanti, āmantā. Atthi arahato avijjā avijjogho avijjāyogo avijjānusayo avijjāpariyuṭṭhānaṃ avijjāsaṃyojanaṃ avijjānīvaraṇanti, na hevaṃ vattabbe. Natthi arahato avijjā avijjogho avijjāyogo avijjānusayo avijjāpariyuṭṭhānaṃ avijjāsaṃyojanaṃ avijjānivaraṇanti, āmantā. Hañci natthi arahato avijjā avijjogho avijjāyogo avijjānusayo avijjāpariyuṭṭhānaṃ avijjāsaṃyojanaṃ avijjānīvaraṇaṃ, no ca vata re vattabbe ‘‘atthi arahato aññāṇa’’nti evamādinā āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā bhāsitā.</p>\r\n<p class="subhead">Asaññakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',67,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0067"></a> āvuso pañcame kathāvatthuppakaraṇe tatiyavagge ekādasamā asaññakathā āyasmatā mahāmoggaliputtatissattherena kathaṃ vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante pañcame kathāvatthuppakaraṇe tatiyavagge ekādasamā asaññakathā ‘‘asaññasattesu saññā atthīti, āmantā. Saññābhavo saññāgati saññāsattāvāso saññāsaṃsāro saññāyoni saññattabhāvapaṭilābhoti, na hevaṃ vattabbe’’ti evamādinā āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',68,replace(replace('<p class="bodytext">Asaññasattesu <a name="M0.0068"></a> saññā atthi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',69,replace(replace('<p class="bodytext">Saññābhavo <a name="M0.0069"></a> saññāgati saññāsattāvāso saññāsaṃsāro saññāyoni saññattabhāvapaṭilābho.</p>\r\n<p class="bodytext">Na hevaṃ vattabbe.</p>\r\n<p class="bodytext">Nanu asaññabhavo asaññagati asaññasattāvāso asaññasaṃsāro asaññayoni asaññattabhāvapaṭilābho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',70,replace(replace('<p class="bodytext">Hañci <a name="M0.0070"></a> asaññabhavo asaññagati asaññasattāvāso asaññasaṃsāro asaññayoni asaññattabhāvapaṭilābho, no ca vata re vattabbe asaññasattesu saññā atthi.</p>\r\n<p class="bodytext">Asaññasattesu saññā atthi.</p>\r\n<p class="bodytext">Pañcavokārabhavo gati sattāvāso saṃsāro yoni attabhāvapaṭilābho.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',71,replace(replace('<p class="bodytext">Nanu <a name="M0.0071"></a> ekavokārabhavo gati sattāvāso saṃsāro yoni attabhāvapaṭilābho.</p>\r\n<p class="bodytext">Hañci ekavokārabhavo gati sattāvāso saṃsāro yoni attabhāvapaṭilābho, no ca vata re vattabbe asaññasattesu saññā atthi.</p>\r\n<p class="subhead">Chaṭṭhavagga</p>\r\n<p class="subhead">Pathavīdhātusanidassanātiādikathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',72,replace(replace('<p class="bodytext">chaṭṭhavagge <a name="M0.0072"></a> pana āvuso aṭṭhamā pathavīdhātu sanidassanātiādikathā āyasmatā mahāmoggaliputtatissattherena kathaṃ vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> chaṭṭhavagge bhante aṭṭhamā pathavīdhātu sanidassanātiādikathā ‘‘pathavīdhātu sanidassanāti āmantā. Rūpaṃ rūpāyatanaṃ rūpadhātu nīlaṃ pītakaṃ lohitakaṃ odātaṃ cakkhuviññeyyaṃ cakkhusmiṃ paṭihaññati cakkhussa āpāthaṃ āgacchatīti, na hevaṃ vattabbe’’ti evamādinā āyasmatā mahāmoggali puttatissattherena vitthārato vibhajitvā kathitā.</p>','\r',char(13)),'\n',char(10)),'');
