INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',75,replace(replace('<p class="bodytext">Tena <a name="M0.0075"></a> hi na vattabbaṃ cakkhuñca paṭicca pathavīdhātuñca uppajjati cakkhuviññāṇaṃ.</p>\r\n<p class="subhead">Antarābhavakathā</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> abhidhammapiṭake āvuso pañcame kathāvatthuppakaraṇe aṭṭhamavagge dutiyā antarābhavakathā āyasmatā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',76,replace(replace('<p class="bodytext">mahāmoggaliputtatissattherena <a name="M0.0076"></a> kathaṃ vitthārato vibhajitvā kathitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante pañcame kathāvatthuppakaraṇe aṭṭhamavagge dutiyā antarābhavakathā ‘‘atthi antarābhavoti, āmantā. Kāmabhavoti, na hevaṃ vattabbe…pe… atthi antarābhavoti, āmantā. Rūpabhavoti, na hevaṃ vattabbe…pe… atthi antarābhavoti, āmantā. Arūpabhavoti, na hevaṃ vattabbe’’ti evamādinā āyasmatā mahāmoggaliputtatissattherena vitthārato vibhajitvā kathitā.</p>','\r',char(13)),'\n',char(10)),'');
