INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',124,replace(replace('<p class="bodytext">abyākatassa <a name="M0.0124"></a> dhammassa hetupaccayena paccayo’’ti evamādinā pucchāvārena ca. ‘‘Kusalo dhammo kusalassa dhammassa hetupaccayena paccayo, kusalā hetū sampayuttakānaṃ khandhānaṃ hetupaccayena paccayo. Kusalo dhammo abyākatassa dhammassa hetupaccayena paccayo, kusalā hetū cittasamuṭṭhānānaṃ rūpānaṃ hetupaccayena paccayo’’ti evamādinā vissajjanāvārena ca ‘‘hetuyā satta, ārammaṇe nava, adhipatiyā dasa, anantare samanantare satta, sahajāte nava, aññamaññe tīṇi, nissaye terasa, upanissaye nava, purejāte pacchājāte āsevane tīṇi, kamme satta, vipāke ekaṃ. Āhāre indriye jhāne magge satta, sampayutte tīṇi, vippayutte pañca, atthiyā terasa, natthiyā vigate satta, avigate terasā’’ti evamādinā saṅkhyāvārena ca paccayānulomapaccayapaccanīyapaccayānulomapaccanīyapaccayapaccanīyānulomasaṅkhātehi catūhi nayehi bhagavatā vitthārato vibhajitvā desito.</p>','\r',char(13)),'\n',char(10)),'');
