INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',129,replace(replace('<p class="bodytext">‘‘Kusalo <a name="M0.0129"></a> dhammo kusalassa dhammassa hetupaccayena paccayo, kusalā hetū sampayattakānaṃ khandhānaṃ hetupaccayena paccayo. Kusalo dhammo abyākatassa dhammassa hetupaccayena paccayo, kusalā hetū cittasamuṭṭhānaṃ rūpānaṃ hetupaccayena paccayo. Kusalo dhammo kusalassa ca abyākatassa ca dhammassa hetupaccayena paccayo, kusalā hetū sampayuttakānaṃ khandhānaṃ cittasamuṭṭhānānañca rūpānaṃ hetupaccayena paccayo’’.</p>\r\n<p class="subhead">Ārammaṇapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso…pe… sammāsambuddhena abhidhammapiṭake sattasu pakaraṇesu sattame paṭṭhānamahāpakaraṇe catuvīsatiyā paṭṭhānesu paṭhame dhammānulomatikapaṭṭhāne dvāvīsatiyā tikesu paṭhame kusalattike sattasu mahāvāresu sattame pañhāvāre catūsu nayesu paṭhame paccayānulomanaye pucchāvissajjanāsaṅkhyāvārasaṅkhātesu tīsu vāresu dutiye vissajjanāvāre hetupaccayavibhaṅgādīsu catuvīsatiyā vibhaṅgesu dutiyo ārammaṇapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',130,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0130"></a> bhante sattasu pakaraṇesu sattame anantanayasamantapaṭṭhāne mahāpakaraṇe catuvīsatiyā paṭṭhānesu paṭhame dhammānulomatikapaṭṭhāne dvāvīsatiyā tikesu paṭhame kusalattike sattasu mahāvāresu sattame pañhāvāre catūsu nayesu paṭhame paccayānulomanaye tīsu ca vāresu dutiye vibhaṅgavāre hetupaccayavibhaṅgādīsu catuvīsatiyā vibhaṅgesu dutiyo ārammaṇapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa ārammaṇapaccayena paccayo, dānaṃ datvā sīlaṃ samādiyitvā uposathakammaṃ katvā taṃ paccavekkhati, pubbe suciṇṇāni paccavekkhati, jhānā vuṭṭhahitvā jhānaṃ paccavekkhati, sekkhā gotrabhuṃ paccavekkhanti, vodānaṃ paccavekkhanti, sekkhā maggā vuṭṭhahitvā maggaṃ paccavekkhanti, sekkhā vā puthujjanā vā kusalaṃ aniccato dukkhato anattato vipassanti, cetopariyañāṇena kusalacittasamaṅgissa cittaṃ jānanti, ākāsānañcāyatanakusalaṃ viññāṇañcāyatanakusalassa ārammaṇapaccayena paccayo. Ākiñcaññāyatanakusalaṃ nevasaññānāsaññāyatanakusalassa ārammaṇapaccayena paccayo. Kusalā khandhā iddhividhañāṇassa, cetopariyañāṇassa, pubbenivāsānussatiñāṇassa, yathākammupagañāṇassa, anāgataṃsañāṇassa ārammaṇapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Adhipatipaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',133,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0133"></a> āvuso sattasu pakaraṇesu sattame paṭṭhānamahāpakaraṇe catuvīsatiyā paṭṭhānesu paṭhame dhammānulomatikapaṭṭhānesu bāvīsatiyā tikesu paṭhame kusalattike sattasu mahāvāresu sattame pañhāvāre catūsu nayesu paṭhame paccayānulomanaye pucchāvissajjanāsaṅkhyāsaṅkhātesu tīsu vāresu dutiye vissajjanāsaṅkhāte vibhaṅgavāre hetupaccayavibhaṅgādīsu catuvīsatiyā vibhaṅgesu tatiyo adhipatipaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante sattasu pakaraṇesu sattame anantanayasamantapaṭṭhāne mahāpakaraṇe catuvīsatiyā paṭṭhānesu paṭhame dhammānulomatikapaṭṭhāne bāvīsatiyā tikesu paṭhame kusalattike sattasu mahāvāresu sattame pañhāvāre catūsu nayesu paṭhame paccayānulomanaye pucchāvissajjanāsaṅkhyāsaṅkhātesu tīsu vāresu dutiye vissajjanāsaṅkhāte vibhaṅgavāre hetupaccayavibhaṅgādīsu catuvīsatiyā vibhaṅgesu tatiyo adhipatipaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa adhipatipaccayena paccayo. Ārammaṇādhipati, sahajātādhipati. Ārammaṇādhipati–dānaṃ datvā sīlaṃ samādiyitvā uposathakammaṃ katvā taṃ garuṃ katvā paccavekkhati. Pubbe suciṇṇāni garuṃ katvā paccavekkhati, jhānā vuṭṭhahitvā jhānaṃ garuṃ katvā paccavekkhati, sekkhā gotrabhuṃ garuṃ katvā paccavekkhanti, vodānaṃ garuṃ katvā paccavekkhanti, sekkhā maggā vuṭṭhahitvā maggaṃ garuṃ katvā paccavekkhanti. Sahajātādhipati–kusalādhipati sampayuttakānaṃ khandhānaṃ adhipatipaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Anantarapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',137,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0137"></a> bhagavatā…pe… catuttho anantarapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… catuttho anantarapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa anantarapaccayena paccayo, purimā purimā kusalā khandhā pacchimānaṃ pacchimānaṃ kusalānaṃ khandhānaṃ anantarapaccayena paccayo. Anulomaṃ gotrabhussa, anulomaṃ vodānassa, gotrabhu maggassa, vodānaṃ maggassa anantarapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Sahajātapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',138,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M0.0138"></a> chaṭṭho sahajātapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',139,replace(replace('<p class="bodytext">abhidhammapiṭake <a name="M0.0139"></a> bhante…pe… chaṭṭho sahajātapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa sahajātapaccayena paccayo, kusalo eko khandho tiṇṇannaṃ khandhānaṃ sahajātapaccayena paccayo, tayo khandhā ekassa khandhassa sahajātapaccayena paccayo, dve dvinnaṃ khandhānaṃ sahajātapaccayena paccayo. Kusalo dhammo abyākatassa dhammassa sahajātapaccayena paccayo, kusalā khandhā cittasamuṭṭhānānaṃ rūpānaṃ sahajātapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Upanissayapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',140,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0140"></a> bhagavatā…pe… navamo upanissayapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… navamo upanissayapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa upanissayapaccayena paccayo. Ārammaṇūpanissayo, anantarūpanissayo, pakatūpanissayo. Ārammaṇūpanissayo–dānaṃ datvā sīlaṃ samādiyitvā uposathakammaṃ katvā kaṃ garuṃ katvā paccavekkhati…pe…</p>\r\n<p class="bodytext">Pakatūpanissayo – </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',141,replace(replace('<p class="bodytext">saddhaṃ <a name="M0.0141"></a> upanissāya dānaṃ deti sīlaṃ samādiyati uposathakammaṃ karoti, jhānaṃ uppādeti, vipassanaṃ uppādeti, maggaṃ uppādeti, abhiññaṃ uppādeti, samāpattiṃ uppādetī’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Purejātapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',144,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M0.0144"></a> dasamo purejātapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… dasamo purejātapaccayavibhaṅgo ‘‘abyākato dhammo abyākatassa dhammassa purejātapaccayena paccayo. Ārammaṇapurejātaṃ, vatthupurejātaṃ. Ārammaṇapurejātaṃ–arahā cakkhuṃ aniccato dukkhato anattato vipassati. Ghānaṃ, jivhaṃ, kāyaṃ, rūpe, sadde, gandhe, rase, phoṭṭhabbe, vatthuṃ aniccato dukkhato anattato vipassati. Dibbena cakkhunā rūpaṃ passati, dibbāya sotadhātuyā saddaṃ suṇāti, rūpāyatanaṃ cakkhuviññāṇassa, saddāyatanaṃ sotaviññāṇassa, gandhāyatanaṃ ghānaviññāṇassa, rasāyatanaṃ jivhāviññāṇassa, phoṭṭhabbāyatanaṃ kāyaviññāṇassa purejātapaccayena paccayo. Vatthupurejātaṃ–cakkhāyatanaṃ cakkhuviññāṇassa, sotāyatanaṃ sotaviññāṇassa, ghānāyatanaṃ ghānaviññāṇassa, jivhāyatanaṃ jivhāviññāṇassa, kāyāyatanaṃ kāyaviññāṇassa, vatthu vipākābyākatānaṃ kriyābyākatānaṃ khandhānaṃ purejātapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Kammapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',147,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0147"></a> bhagavatā…pe… terasamo kammapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… terasamo kammapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa kammapaccayena paccayo, kusalā cetanā sampayuttakānaṃ khandhānaṃ kammapaccayena paccayo. Kusalo dhammo abyākatassa dhammassa kammapaccayena paccayo. Sahajātā, nānākkhaṇikā. Sahajātā–kusalā cetanā cittasamuṭṭhānānaṃ rūpānaṃ kammapaccayena paccayo. Nānākkhaṇikā–kusalā cetanā vipākānaṃ khandhānaṃ kaṭattā ca rūpānaṃ kammapaccayena paccayo. Kusalo dhammo kusalassa ca abyākatassa ca dhammassa kammapaccayena paccayo. Kusalā cetanā sampayuttakānaṃ khandhānaṃ cittasamuṭṭhānānañca rūpānaṃ kammapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Āhārapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',150,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M0.0150"></a> pannarasamo āhārapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… pannarasamo āhārapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa āhārapaccayena paccayo. Kusalā āhārā sampayuttakānaṃ khandhānaṃ āhārapaccayena paccayo. Kusalo dhammo abyākatassa dhammassa āhārapaccayena paccayo. Kusalā āhārā cittasamuṭṭhānānaṃ rūpānaṃ āhārapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Indriyapaccayavibhaṅga</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',151,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0151"></a> bhagavatā…pe… soḷasamo indriyapaccayavibhaṅgo kathaṃ vitthārena vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… soḷasamo indriyapaccayavibhaṅgo ‘‘kusalo dhammo kusalassa dhammassa indriyapaccayena paccayo, kusalo indriyā sampayuttakānaṃ khandhānaṃ indriyapaccayena paccayo. Kusalo dhammo abyākatassa dhammassa indriyapaccayena paccayo, kusalā indriyā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',152,replace(replace('<p class="bodytext">cittasamuṭṭhānānaṃ <a name="M0.0152"></a> rūpānaṃ indriyapaccayena paccayo’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>\r\n<p class="subhead">Saṅkhyāvāra</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',154,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M0.0154"></a> pucchāvissajjanāsaṅkhyāvārasaṅkhātesu tīsu vāresu tatiyo saṅkhyāvāro kathaṃ vitthārato vibhajitvā desito.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… tatiyo saṅkhyāvāro ‘‘hetuyā satta, ārammaṇe nava, adhipatiyā dasa, anantara samanantare satta, sahajāte nava, aññamaññe tīṇi, nissaye terasa, upanissaye nava, purejāte pacchājāte āsevane </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',155,replace(replace('<p class="bodytext">tīṇi<a name="M0.0155"></a>, kamme satta, vipāke ekaṃ, āhāre indriye jhāne magge satta, sampayutte tīṇi, vippayutte pañca, atthiyā terasa, natthiyā vigate satta, avigate terasā’’ti evamādinā bhagavatā vitthārato vibhajitvā desito.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',156,replace(replace('<p class="bodytext">Hetu <a name="M0.0156"></a> sahajāta nissaya atthi avigatanti satta, hetu sahajāta aññamañña nissaya atthi avigatanti tīṇi, hetu sahajāta aññamañña nissaya sampayutta atthi avigatanti tīṇi …</p>\r\n<p class="subhead">Vedanātika paṭiccavāra</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> tenāvuso bhagavatā…pe… paṭhamaṃ kusalattikaṃ vitthārato vibhajitvā tadanantaraṃ avasesā vedanāttikādayo ekavīsatitikā kathaṃ vitthārato vibhajitvā desitā.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… paṭhamaṃ kusalattikaṃ vitthārato vibhajitvā tadanantaraṃ avasesā vedanāttikādayo </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',157,replace(replace('<p class="bodytext">ekavīsatitikā <a name="M0.0157"></a> ‘‘sukhāya vedanāya sampayuttaṃ dhammaṃ paṭicca sukhāya vedanāya sampayutto dhammo uppajjati hetupaccayā. Sukhāya vedanāya sampayuttaṃ ekaṃ khandhaṃ paṭicca dve khandhā, dve khandhe paṭicca eko khandho, paṭisandhikkhaṇe sukhāya vedanāya sampayuttaṃ ekaṃ khandhaṃ paṭicca dve khandhā, dve khandhe paṭicca eko khandho’’ti evamādinā ca. Vipākaṃ dhammaṃ paṭicca vipāko dhammo uppajjati hetupaccayā. Vipākaṃ ekaṃ khandhaṃ paṭicca tayo khandhā, tayo khandhe paṭicca eko khandho, dve khandhe paṭicca dve khandhā, paṭisandhikkhaṇe vipākaṃ ekaṃ khandhaṃ paṭicca tayo khandhā, tayo khandhe paṭicca eko khandho, dve khandhe paṭicca dve khandhā. Vipākaṃ dhammaṃ paṭicca nevavipākanavipākadhammadhammo uppajjati hetupaccayā, vipāke khandhe paṭicca cittasamuṭṭhānarūpaṃ, paṭisandhikkhaṇe vipāke khandhe paṭicca kaṭattārūpaṃ, khandhe paṭicca vatthū’’ti evamādinā ca ekekasmiṃ tike sattahi sattahi mahāvārehi ekekasmiñca mahāvāre catūhi catūhi nayehi bhagavatā vitthārato vibhajitvā desitā.</p>\r\n<p class="subhead">Dukapaṭṭhāna</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',158,replace(replace('<p class="bodytext">tenāvuso…pe… <a name="M0.0158"></a> tikapaṭṭhānādīsu chasu antogadhapaṭṭhānādīsu paṭhamaṃ tikapaṭṭhānaṃ desetvā tadanantaraṃ dukapaṭṭhānaṃ kathaṃ vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… tikapaṭṭhānādīsu chasu antogadhapaṭṭhānādīsu paṭhamaṃ tikapaṭṭhānaṃ vibhajitvā tadanantaraṃ dukapaṭṭhānaṃ ‘‘hetuṃ dhammaṃ paṭicca hetu dhammo uppajjati hetupaccayā, alobhaṃ paṭicca adoso amoho, adosaṃ paṭicca alobho amoho, amohaṃ paṭicca alobho adoso. Lobhaṃ paṭicca moho, mohaṃ paṭicca lobho, dosaṃ paṭicca moho, mohaṃ paṭicca doso’’ti evamādinā ca. Saraṇaṃ dhammaṃ paṭicca saraṇo dammo uppajjati hetupaccayā, saraṇaṃ ekaṃ khandhaṃ paṭicca tayo khandhā, tayo khandhe paṭicca eko khandho, dve khandhe paṭicca dve khandhā. Saraṇaṃ dhammaṃ paṭicca araṇo dhammo uppajjati hetupaccayā, saraṇo khandhe paṭicca cittasamuṭṭhānaṃ rūpaṃ. Saraṇaṃ dhammaṃ paṭicca saraṇo ca araṇo ca dhammā uppajjanti hetupaccayā. Saraṇaṃ ekaṃ khandhaṃ paṭicca tayo khandhā cittasamuṭṭhānañca rūpaṃ, tayo khandhe paṭicca eko khandho cittasamuṭṭhānañca rūpaṃ, dve khandhe paṭicca dve khandhā </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',159,replace(replace('<p class="bodytext">cittasamuṭṭhānañca <a name="M0.0159"></a> rūpa’’nti evamādinā ca. Ekekasmiṃ duke sattahi sattahi mahāvārehi ekekasmiñca mahāvāre catūhi catūhi nayehi bhagavatā vitthārato vibhajitvā desitaṃ.</p>\r\n<p class="bodytext">Saraṇaṃ dhammaṃ paṭicca saraṇo dhammo uppajjati hetupaccayā, saraṇaṃ ekaṃ khandhaṃ paṭicca tayo khandhā, tayo khandhe paṭicca eko khandho, dve</p>','\r',char(13)),'\n',char(10)),'');
