INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',161,replace(replace('<p class="bodytext">tadavasesā <a name="M0.0161"></a> dukatikapaṭṭhānādayo ‘‘hetuṃ kusalaṃ dhammaṃ paṭicca hetu kusalo dhammo uppajjati hetupaccayā. Hetuṃ kusalaṃ dhammaṃ paṭicca nahetu kusalo dhammo uppajjati hetupaccayā. Hetuṃ kusalaṃ dhammaṃ paṭicca hetu kusalo ca nahetu kusalo ca dhammā uppajjanti hetupaccayā, nahetuṃ kusalaṃ dhammaṃ paṭicca nahetu kusalo dhammo uppajjati hetupaccayā, nahetuṃ kusalaṃ dhammaṃ paṭicca hetu kusalo dhammo uppajjati hetupaccayā, nahetuṃ kusalaṃ dhammaṃ paṭicca hetu kusalo ca nahetu kusalo ca dhammā uppajjanti hetupaccayā’’ti evamādinā vitthārato vibhajitvā desitāni.</p>\r\n<p class="subhead">Dhammapaccanīyapaṭṭhāna</p>\r\n<p class="bodytext"><span class="bld">Pucchā –</span> </p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_buddha_vandana_e0906n',163,replace(replace('<p class="bodytext">tenāvuso <a name="M0.0163"></a> bhagavatā…pe… paṭhamaṃ dhammānulomapaṭṭhānaṃ vibhajitvā tadanantaraṃ dutiyaṃ dhammapaccanīyapaṭṭhānaṃ kathaṃ vibhajitvā desitaṃ.</p>\r\n<p class="bodytext"><span class="bld">Vissajjanā –</span> abhidhammapiṭake bhante…pe… paṭhamaṃ dhammānulomapaṭṭhānaṃ vibhajitvā tadanantaraṃ dutiyaṃ dhammapaccanīyapaṭṭhānaṃ ‘‘nakusalaṃ dhammaṃ paṭicca nakusalo dhammo uppajjati hetupaccayā. Akusalābyākataṃ ekaṃ khandhaṃ paṭicca akusalābyākatā tayo khandhā cittasamuṭṭhānañca rūpaṃ. Tayo khandhe paṭicca eko khandho cittasamuṭṭhānañca rūpaṃ. Dve khandhe paṭicca dve khandhā cittasamuṭṭhānañca rūpaṃ. Nakusalaṃ dhammaṃ paṭicca naakusalo dhammo uppajjati hetu paccayā. Vipākābyākataṃ kiriyābyākataṃ ekaṃ khandhaṃ paṭicca tayo khandhā cittasamuṭṭhānañca rūpaṃ. Tayo khandhe paṭicca eko khandho cittasamuṭṭhānañca rūpaṃ. Dve khandhe paṭicca dve khandhā </p>','\r',char(13)),'\n',char(10)),'');
