INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Kavidappaṇanīti</p>\r\n<p class="chapter">Mātikā</p>\r\n<p class="gatha1">Yathādhammikarājūnaṃ, amaccā ca purohitā;</p>\r\n<p class="gathalast">Nītisatthaṃ sunissāya, nicchayanti vinicchayaṃ.</p>\r\n<p class="gatha1">Aṅgāni vedā cattāro, mīmaṃsānyāya vitthāro;</p>\r\n<p class="gathalast">Dhammasatthaṃ purāṇañca, vijjā hetā catuddasa.</p>\r\n<p class="gatha1">Āyubbedo manubbedo, gandhabbo ceti te tayo;</p>\r\n<p class="gathalast">Atthasatthaṃ catutthañca, vijjāhyāṭṭharasa matā.</p>\r\n<p class="gatha1">Sutisamutisaṅkhyā ca, rogānīti visesakā;</p>\r\n<p class="gathalast">Gandhabbā gaṇikā ceva, dhanubbedā ca pūraṇā.</p>\r\n<p class="gatha1">Tikicchā itihāso ca, jotimāyā ca chandati;</p>\r\n<p class="gathalast">Ketumantā ca saddā ca, sippāṭṭhārasakā ime.</p>\r\n<p class="gatha1">Damo daṇḍo itikhyāto, taṭṭhādaṇḍo mahīpati;</p>\r\n<p class="gathalast">Tassa nīti daṇḍanīti, nayanānīti vuccati.</p>\r\n<p class="gatha1">Daṇḍena nīyate cedaṃ, daṇḍaṃ nayati vā puna;</p>\r\n<p class="gathalast">Daṇḍanīti itikhyāto, tilokā nati vattate.</p>\r\n<p class="gatha1">Nānāsatthoddhataṃ vakkhe, rājanīti samuccayaṃ;</p>\r\n<p class="gathalast">Sabbabījamiduṃ satthaṃ, cāṇakya sārasaṅgahaṃ.</p>\r\n<p class="gatha1">Mūlasuttaṃ pavakkhāmi, cāṇakyena yathoditaṃ;</p>\r\n<p class="gathalast">Yassaṃ viññātamattena, mūḷho bhavati paṇḍito.</p>\r\n<p class="gatha1">Mittalābho suhadabhedo, viggaho sandhireva ca;</p>\r\n<p class="gathalast">Pañcatandrā tathāññasmā, ganthā kassiyalikhyate.</p>\r\n<p class="bodytext">Lokanītimhā –</p>\r\n<p class="bodytext">(1) Paṇḍitakaṇḍa. (2) Sujanakaṇḍa. (3) Bāladujjana kaṇḍa. (4) Mittakaṇḍa. (5) Itthikaṇḍa. (6) Rājakaṇḍa. (7) Pakiṇṇaka kaṇḍa-</p>\r\n<p class="bodytext">Lokanīti –</p>\r\n<p class="gatha1">Paṇḍito sujano kaṇḍo, dujjano mittaitthī ca;</p>\r\n<p class="gathalast">Rājapakiṇṇako cāti, sattakaṇḍe vibhūsino.</p>\r\n<p class="gatha1">Cakkindābhisirināyaṃ, sodhito kāsike sāke;</p>\r\n<p class="gathalast">Chanotyaṃ dutiyāsaḷhe, kāḷasattama ādihe.</p>\r\n<p class="gatha1">Lokanītiṃ pavakkhāmi, nānāsatthasamuddhaṭaṃ;</p>\r\n<p class="gathalast">Māgadheneva saṅkhepaṃ, vanditvā ratanattayaṃ.</p>\r\n<p class="gatha1">Nīti loke purisassa sāro,</p>\r\n<p class="gatha2">Mātā pitā ācariyo mitto;</p>\r\n<p class="gatha3">Tasmā hi nītiṃ puriso vijaññā,</p>\r\n<p class="gathalast">Ñāṇīmahā hoti bahussuto.</p>\r\n<p class="bodytext">Mahārahanīti –</p>\r\n<p class="bodytext">(1) Paṇḍitakathā. (2) Sambhedakathā. (3) Mittakathā. (4) Nāyaka kathā. (5) Itthikathā</p>\r\n<p class="gatha1">Mahāraha rahaṃsakya-muniṃ nīvaraṇā taṇhā;</p>\r\n<p class="gathalast">Muttaṃ muttaṃ sudassanaṃ, vande bodhivaraṃ varaṃ.</p>\r\n<p class="gatha1">Nītidha jantūnaṃ sāro, mittācariyā ca pitaro;</p>\r\n<p class="gathalast">Nītimā subuddhibyatto, sutavā atthadassimā.</p>\r\n<p class="bodytext">Dhammanīti –</p>\r\n<p class="bodytext">(1) Ācariyakathā (2) sippakathā (3) paññākathā (4) sutakathā (5) kathānakathā (6) dhanakathā (7) desakathā (8) nissayakathā (9) mittakathā (10) dujjanakathā (11) sujanakathā (12) balakathā (13) itthikathā (14) yuttakathā (15) dāsakathā (16) gharāvāsakathā (17) kātabbakathā (18) akātabbakathā (19) ñātabbakathā (20) alaṅkārakathā (21) rājadhammakathā (22) upasevakakathā (23) dukkhādimissakakathā (24) pakiṇṇakakathā</p>\r\n<p class="gatha1">Cakkāticakkacakkindo, devātidevādevindo,</p>\r\n<p class="gathalast">Brahmāti brahmabrahmindo, jino pūretu me bhāvaṃ.</p>\r\n<p class="gatha1">Ciraṃ tiṭṭhatu lokamhi, dhaṃsakaṃ sabbapāṇinaṃ;</p>\r\n<p class="gathalast">Mahāmohatamaṃ jayaṃ, jotantaṃ jinasāsanaṃ.</p>\r\n<p class="gatha1">Vanditvā ratanaṃ seṭṭhaṃ, nissāya pubbake garu;</p>\r\n<p class="gathalast">Nītidhammaṃ pavakkhāmi, sabbaloka sukhāvahaṃ.</p>\r\n<p class="gatha1">Ācariyo ca sippañca, paññāsutakathādhanaṃ;</p>\r\n<p class="gathalast">Desañca nissayo mittaṃ, dujjano sujano balaṃ.</p>\r\n<p class="gatha1">Itthī putto ca dāso ca, gharāvāso katākato;</p>\r\n<p class="gatha2">Ñātabbo ca alaṅkāro, rājadhammā pasevako;</p>\r\n<p class="gathalast">Dukkhādimissako ceva, pakiṇṇakāti mātikā.</p>\r\n<p class="bodytext">Rājanīti –</p>\r\n<p class="gatha1">Sīhā ekaṃ bakā ekaṃ, sikkhe cattāri kukkuṭā;</p>\r\n<p class="gathalast">Pañca kākā rājā nāma, cha sunakkhā tīṇi gadrabhā.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> Mahākammaṃ khuddakaṃ vā, yaṃ kammaṃ kātumicchati;</p>\r\n<p class="gathalast">Sabbārambhena kātabbaṃ, sīhā ekaṃ tadā bhave.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Indriyāni susaṃyama, bakova paṇḍito bhave;</p>\r\n<p class="gathalast">Desaka lomapannāni, sabbakammāni sādhaye.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Pubbaṭṭhānañca yuddhañca, saṃvibhāgañca bandhu hi;</p>\r\n<p class="gathalast">Thiyā akkamma bhuttañca, sikkhe cattāri kukkuṭo.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Guyhe methunaṃ pekkhitvā, bhojanaṃ ñātisaṅgaho;</p>\r\n<p class="gathalast">Vilokā pekkhanālasyaṃ, pañca sikkheyya vāyasā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Anālassaṃtisavantāso, suniddhā suppabodhanā;</p>\r\n<p class="gathalast">Daḷhabhatti ca sūrañca, cha etesvānato guṇo.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Khinnova vahate bhāraṃ, sītuṇhañca na cintayī;</p>\r\n<p class="gathalast">Santuṭṭho ca bhave niccaṃ, tīṇi sikkheyya gadrabhā.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Vīsati tāni guṇāni, careyya iha paṇḍito;</p>\r\n<p class="gathalast">Vijeyya ripū sabbepi, tejassī so bhavissati.</p>\r\n<p class="bodytext">(1) Paṇḍitakaṇḍa (2) sujanakaṇḍa (3) bāladujjanakaṇḍa (4) mittakaṇḍa (5) rājakaṇḍa (6) nāyakakaṇḍa (7) puttakaṇḍa (8) vejjācariyakaṇḍa (9) dāsakakaṇḍa (10) itthikaṇḍa (11) pakiṇṇakakaṇḍa</p>\r\n<p class="gatha1">Kavidappaṇanītiṃyo, vācuggataṃ karoti ce;</p>\r\n<p class="gathalast">Bhuvanamajjhe eso hi, viññū paṇḍitajātiko.</p>\r\n<p class="subhead">Kavidappaṇanīti</p>\r\n<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="chapter">Ratanattayapaṇāma</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-2-3-4-5-6-7-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',1,replace(replace('<p class="gatha1">Mahākavivaraṃ <a name="M0.0001"></a> buddhaṃ, dhammañca tena sevitaṃ;</p>\r\n<p class="gathalast">Saṅghaṃ niraṅgaṇañcāpi, vandāmi sirasā daraṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Karissāmi samāsena, nānāsattha samuddhaṭaṃ;</p>\r\n<p class="gathalast">Hitāya kavinaṃ nītiṃ, kavidappaṇanāmakaṃ.</p>\r\n<p class="chapter">Paṇḍitakaṇḍa</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',2,replace(replace('<p class="gatha1">Nīti <a name="M0.0002"></a> sāro manussānaṃ, mitto ācariyopi ca;</p>\r\n<p class="gathalast">Mātā pitā ca nītimā, sutavā ganthakārako.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',3,replace(replace('<p class="gatha1">Alasassa <a name="M0.0003"></a> kuto sippaṃ, asippassa kuto dhanaṃ;</p>\r\n<p class="gatha2">Adhanassa kuto mittaṃ, amittassa kuto sukhaṃ;</p>\r\n<p class="gathalast">Asukhassa kuto puññaṃ, apuññassa kuto varaṃ.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',4,replace(replace('<p class="gatha1">Sucintitacintī <a name="M0.0004"></a> ceva, subhāsitabhāsīpi ca;</p>\r\n<p class="gathalast">Sukatakammakārī ca, paṇḍito sādhumānuso.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',5,replace(replace('<p class="gatha1">Kaviheraññakā <a name="M0.0005"></a> katvā, uttattaṃ satthakañcanaṃ;</p>\r\n<p class="gathalast">Bhūsanaṃ gajjapajjādiṃ, karonti ca manoharaṃ.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',6,replace(replace('<p class="gatha1">Bahuṃ <a name="M0.0006"></a> lahuñca gahaṇaṃ, sammūpadhāraṇampi ca;</p>\r\n<p class="gathalast">Gahita asammussanaṃ, etaṃ suviññulakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Ajarāmaraṃva pañño, vijjamatthañca cintaye;</p>\r\n<p class="gathalast">Gahito iva kesesu, maccunā dhammamācare.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-8-9-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',7,replace(replace('<p class="gatha1">Sippasamaṃ <a name="M0.0007"></a> dhanaṃ natthi, sippaṃ corā na gaṇhare;</p>\r\n<p class="gathalast">Idha loke sippaṃ mittaṃ, paraloke sukhāvahaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',8,replace(replace('<p class="gatha1">Bhuñjanatthaṃ <a name="M0.0008"></a> kathanatthaṃ, mukhaṃ hotīti no vade;</p>\r\n<p class="gathalast">Yaṃ vātaṃ vā mukhāruḷhaṃ, vacanaṃ paṇḍito naro.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Dummedhehi pasaṃsā ca, viññūhi garahā ca yā;</p>\r\n<p class="gathalast">Garahāva seyyo viññūhi, yañce bālappasaṃsanā.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',9,replace(replace('<p class="gatha1">Acintiye <a name="M0.0009"></a> sāṭṭhakathe, paṇḍito jinabhāsite;</p>\r\n<p class="gathalast">Upadesaṃ sadā gaṇhe, garuṃ sammā upaṭṭhahaṃ.</p>\r\n<p class="gatha1">Tasmā sāṭṭhakathe dhīro, gambhīre jinabhāsite;</p>\r\n<p class="gathalast">Upadesaṃ sadā gaṇhe, garuṃ sammā upaṭṭhahaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',10,replace(replace('<p class="gatha1">Garūpadesahīno <a name="M0.0010"></a> hi, atthasāraṃ na vindati;</p>\r\n<p class="gathalast">Atthasāravihīno so, saddhammā parihāyati.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Garūpadesalābhī ca, atthasārasamāyuto;</p>\r\n<p class="gathalast">Saddhammaṃ paripālento, saddhammasmā na hāyati.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',11,replace(replace('<p class="gatha1">Sabbadabbesu <a name="M0.0011"></a> vijjeva, dabbamāhu anuttaraṃ;</p>\r\n<p class="gathalast">Ahārattā anagghattā, akkhayattā ca sabbadā.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Appakenapi medhāvī, pābhatena vicakkhaṇo;</p>\r\n<p class="gathalast">Samuṭṭhāpeti attānaṃ, aṇuṃ aggiṃva sandhamaṃ.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',12,replace(replace('<p class="gatha1">Paṇḍite <a name="M0.0012"></a> ca guṇā sabbe, mūḷhe dosā hi kevalaṃ;</p>\r\n<p class="gathalast">Tasmā mūḷhasahassesu, pañño eko visesiyate.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',13,replace(replace('<p class="gatha1">Bālā <a name="M0.0013"></a> issanti dummedhā, guṇī niddosakārino;</p>\r\n<p class="gathalast">Garuko paṇḍito etasa-missaṃ tehyavidvā samo.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',14,replace(replace('<p class="gatha1">Manuññameva <a name="M0.0014"></a> bhāseyya, nāmanuññaṃ kudācanaṃ;</p>\r\n<p class="gatha2">Manuññaṃ bhāsamānassa, garuṃ bhāraṃ udaddhari;</p>\r\n<p class="gathalast">Dhanañca naṃ alābhesi, tena cattamano ahu.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',15,replace(replace('<p class="gatha1">Vijjā <a name="M0.0015"></a> dadāti vinayaṃ, vinayā yāti pattataṃ;</p>\r\n<p class="gathalast">Pattattā dhanaṃ pappoti, dhanā dhammaṃ tato sukhaṃ.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> Ye vuḍḍhamapacayanti, narā dhammassa kovidā;</p>\r\n<p class="gathalast">Diṭṭheva dhamme pāsaṃsā, samparāye ca suggatiṃ.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-21-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',16,replace(replace('<p class="gatha1">Mātariva <a name="M0.0016"></a> paradāresu, paradabbesu ledduṃva;</p>\r\n<p class="gathalast">Attanīva sabbabhūtesu, yo passati so paṇḍito.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',17,replace(replace('<p class="gatha1">Āsīsetheva <a name="M0.0017"></a> puriso, na nibbindeyya paṇḍito;</p>\r\n<p class="gathalast">Anavajjesu kammesu, pasaṃsitesu sādhubhi.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',18,replace(replace('<p class="gatha1">Āsīsetheva <a name="M0.0018"></a> puriso, na nibbindeyya paṇḍito;</p>\r\n<p class="gathalast">Passāmi vohaṃ attānaṃ, yathā icchiṃ tathā ahu.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Vāyametheva puriso, na nibbindeyya paṇḍito;</p>\r\n<p class="gathalast">Puññakriyavatthūsu, pasaṃsitesu viññubhi.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',19,replace(replace('<p class="gatha1">Loke <a name="M0.0019"></a> ussāhavantānaṃ, janānaṃ kimasādhiyaṃ;</p>\r\n<p class="gathalast">Sāgarepi mahāsetuṃ, kapiyūthehi bandhati.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Kiṃ kulena visālena, guṇahīno tu yo naro;</p>\r\n<p class="gathalast">Akulinopi satthañño, devatāhipi pujjate.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',20,replace(replace('<p class="gatha1">Ukkaṭṭhe <a name="M0.0020"></a> sūramicchanti, mantīsu akutūhalaṃ;</p>\r\n<p class="gathalast">Piyañca annapānamhi, atthe jāte ca paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',21,replace(replace('<p class="gatha1">Rūpayobbannasampannā<a name="M0.0021"></a>, visālakulasambhavā;</p>\r\n<p class="gathalast">Vijjāhīnā na sobhante, niggandhā iva kiṃ sukā.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',22,replace(replace('<p class="gatha1">Vutyaṃ <a name="M0.0022"></a> visadañāṇassa, ñāto attho tarassana;</p>\r\n<p class="gathalast">Sūrappabhāya ādāso, chāyaṃ disse na mākare.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Aveyyākaraṇo tvandho, badhiro kosavajjito;</p>\r\n<p class="gathalast">Sāhiccarahito paṅgu, mūgo takkavivajjito.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',23,replace(replace('<p class="gatha1">Dhīro <a name="M0.0023"></a> ca vividhānaññū, paresaṃ vivarānugū;</p>\r\n<p class="gathalast">Sabbāmitte vasīkatvā, kosiyova sukhī siyā.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',24,replace(replace('<p class="gatha1">Mahātejopi <a name="M0.0024"></a> tejoyaṃ, mattikaṃ na muduṃ kare;</p>\r\n<p class="gathalast">Āpo āpesi mudukaṃ, sādhuvācāva kakkhaḷaṃ.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Kottho puttena jātena, yo na vidū na dhammiko;</p>\r\n<p class="gathalast">Kāṇena cakkhunā kiṃ vā, cakkhu pīḷeva kevalaṃ.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-33-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',25,replace(replace('<p class="gatha1">Mudunāva <a name="M0.0025"></a> ripuṃ jeti, mudunā jeti dāruṇaṃ;</p>\r\n<p class="gathalast">No na siddhaṃ mudu kiñci, tato ca mudunā jaye.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> Sajāto yena jātena, yāti vaṃso samunnatiṃ;</p>\r\n<p class="gathalast">Parivattinisaṃsāre, mato ko vā na jāyate.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-35-36-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',26,replace(replace('<p class="gatha1">Dāne <a name="M0.0026"></a> tapasi sūre ca, yassa na patthito yaso;</p>\r\n<p class="gathalast">Vijjāya matthalābhe ca, kevalaṃ adhikovaso.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Varo eko guṇī putto, na ca mūḷhasatānyapi;</p>\r\n<p class="gathalast">Eko cando tamo hanati, na ca tārāgaṇo tathā.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> Puññatitthakato yena, tapo kvāpi sudukkaro;</p>\r\n<p class="gathalast">Tassa putto bhave vasso, samiddho dhammiko suddhe.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Lālaye pañcavassāni, dasavassāni tālaye;</p>\r\n<p class="gathalast">Pattetu soḷase vasse, puttaṃ mittaṃva ācare.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-37-38-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',28,replace(replace('<p class="gatha1">Lālane <a name="M0.0028"></a> bahavo dosā, tālane bahavo guṇā;</p>\r\n<p class="gathalast">Tasmā puttañca sissañca, tālaye na tu lālaye.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Māgadhā pākatā ceva, sakkatavohāropi ca;</p>\r\n<p class="gathalast">Etesu kovido pañño, dhīro pāḷiṃ visodhaye.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',29,replace(replace('<p class="gatha1">Sakkataṃ <a name="M0.0029"></a> pākatañceva-pabhaṃso ca pisācikī;</p>\r\n<p class="gathalast">Māgadhī sorasenīva, cha bhāsā parikittitā.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',30,replace(replace('<p class="gatha1">Candanaṃ <a name="M0.0030"></a> sītalaṃ loke, candikā sītalā tato;</p>\r\n<p class="gathalast">Candana candikātopi, vākyaṃ sādhu subhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Pattakāloditaṃ appaṃ, vākyaṃ subhāsitaṃ bhave;</p>\r\n<p class="gathalast">Khuditassa kadannampi, bhuttaṃ sādurasaṃ siyā.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',31,replace(replace('<p class="gatha1">Satthakāpi <a name="M0.0031"></a> bahūvācā, nādarā bahubhāṇino;</p>\r\n<p class="gathalast">Sopakāramudāsinā, nanu diṭṭhaṃ nadījalaṃ.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> Pāsāṇachattaṃ garukaṃ, tato devānācikkhanā;</p>\r\n<p class="gathalast">Tato vuḍḍhānamovādo, tato buddhassa sāsanaṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',32,replace(replace('<p class="gatha1">Tūlaṃ <a name="M0.0032"></a> sallahukaṃ loke, tato capalajātiko;</p>\r\n<p class="gathalast">Tatonosāvako tato, yati dhammapamādako.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Paṇḍitassa pasaṃsāya, daṇḍo bālena dīyate;</p>\r\n<p class="gathalast">Paṇḍito paṇḍiteneva, vaṇṇitova suvaṇṇito.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Satesu jāyate sūro, sahassesu ca paṇḍito;</p>\r\n<p class="gathalast">Vuttā satasahassesu, dātā bhavati vā na vā.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> Vidvattañca rājattañca, neva tulyaṃ kadācipi;</p>\r\n<p class="gathalast">Sadese pūjito rājā, vidvā sabbattha pūjito.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-49-50-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',34,replace(replace('<p class="gatha1">Sataṃ <a name="M0.0034"></a> dīghāyukaṃ sabba-sattānaṃ sukhakāraṇaṃ;</p>\r\n<p class="gathalast">Asataṃ pana sabbesaṃ, dukkhahetu na saṃsayo.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> Paṇḍite sujane sante, sabbepi sujanā janā;</p>\r\n<p class="gathalast">Jātekasmiṃ sāragandhe, sabbe gandhamayā dumā.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Attāva yadi vinīto, nijassitā mahājanā;</p>\r\n<p class="gathalast">Vinītaṃ yanti sabbepi, ko taṃ nāseyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Sarīrassa guṇānañca, dūramaccantamantaraṃ;</p>\r\n<p class="gathalast">Sarīraṃ khaṇaviddhaṃsī, kappantaṭṭhāyino guṇā.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> Ambuṃ pivanti no najjo, rukkho khādati no phalaṃ;</p>\r\n<p class="gathalast">Megho kvacipi no sassaṃ, paratthāya sataṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Saccaṃ punapi saccanti, bhujamukkhippa muccate;</p>\r\n<p class="gathalast">Sakattho natthi nattheva, parassattha makubbato.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-52-53-54-55-56-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',37,replace(replace('<p class="gatha1">Sataṃ <a name="M0.0037"></a> pharusavācāhi, na yāti vikatiṃ mano;</p>\r\n<p class="gathalast">Tiṇukkāhi na sakkāva, tāpetuṃ sāgare jalaṃ.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Selo yathā ekaghano, vātena na samīrati;</p>\r\n<p class="gathalast">Evaṃ nindāpasaṃsāsu, na samiñjanti paṇḍitā.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',38,replace(replace('<p class="gatha1">Dhammatthakāmamokkhānaṃ<a name="M0.0038"></a>, yassekopi na vijjati;</p>\r\n<p class="gathalast">Ajagalathanasseva, tassa jāti niratthakā.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Na kammamapi cintetvā, caje uyyogamattano;</p>\r\n<p class="gathalast">Anuyyogena telāni, tilehi na sakkā laddhuṃ.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-60-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',39,replace(replace('<p class="gatha1">Yathā <a name="M0.0039"></a> hyekena cakkena, na rathassa pati bhave;</p>\r\n<p class="gathalast">Evaṃ purisakārena, vinā kammaṃ na sijjhati.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> Uyyāmena hi sijjhanti, kāriyāni na manorathaṃ;</p>\r\n<p class="gathalast">Na hi suttassa sīhassa, pavisanti migāmukhe.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-62-63-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',40,replace(replace('<p class="gatha1">Mātāpitu <a name="M0.0040"></a> katābhyāso, guṇitameti bālako;</p>\r\n<p class="gathalast">Na gabbhajātimattena, putto bhavati paṇḍito.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> Mātā sattu pitā verī, yena bālo na pāṭhito;</p>\r\n<p class="gathalast">Na sobhate sabhāmajjhe, haṃsamajjhe bako yathā.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-64-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',41,replace(replace('<p class="gatha1">Kāco <a name="M0.0041"></a> kañcanasaṃsaggo, dhatte marakatiṃ jutiṃ;</p>\r\n<p class="gathalast">Tathā sabbhisannidhānā, mūḷho yāti pavīṇataṃ.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> Tasmā akkharakosallaṃ, sammādeyya hitatthiko;</p>\r\n<p class="gathalast">Upaṭṭhahaṃ garuṃ sammā, uṭṭhānādīhi pañcahi.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-66-67-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',42,replace(replace('<p class="gatha1">Uṭṭhānā <a name="M0.0042"></a> upaṭṭhānā, ca, sussūsā pāricarīyā;</p>\r\n<p class="gathalast">Sakkaccaṃ sippuggahaṇā, garuṃ ārādhaye budho.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-68-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',43,replace(replace('<p class="gatha1">Kābyasattha <a name="M0.0043"></a> vinodena, kālo gacchati dhīmataṃ;</p>\r\n<p class="gathalast">Byasanena ca mūḷhānaṃ, nidāya kalahena vā.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> Cha dosā puriseneha, hātabbā bhūtimicchatā;</p>\r\n<p class="gathalast">Niddātandī bhayaṃ kodho, ālasyaṃ dīghasuttatā.</p>','\r',char(13)),'\n',char(10)),'-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',44,replace(replace('<p class="gatha1">Niddāsīlī <a name="M0.0044"></a> sabhāsīlī, anuṭṭhātā ca yo naro;</p>\r\n<p class="gathalast">Alaso kodhapaññāṇo, taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Nigguṇesupi sattesu, dayā kubbanti sādhavo;</p>\r\n<p class="gathalast">Na hi saṃharate jutiṃ, cando caṇḍālavesme.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',45,replace(replace('<p class="gatha1">Yatra <a name="M0.0045"></a> vidvajjano natthi, sīlāghyo tatra appadhipi;</p>\r\n<p class="gathalast">Niratthapādame dese, eraṇḍopi dumāyate.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Ṭhānabhaṭṭhā na sobhante, dantā kesā nakhā narā;</p>\r\n<p class="gathalast">Itiviññāya matimā, saṭṭhānaṃ na pariccaje.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',46,replace(replace('<p class="gatha1">Paropadese <a name="M0.0046"></a> paṇḍiccaṃ, sabbesaṃ sukarañhi kho;</p>\r\n<p class="gathalast">Dhamme sayamanuṭṭhānaṃ, kassacisumahattano.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> Appamādaṃ pasaṃsanti, puññakiriyāsu paṇḍitā;</p>\r\n<p class="gathalast">Appamatto ubho atthe, adhiggaṇhāti paṇḍito.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-74-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',47,replace(replace('<p class="gatha1">Nidhīnaṃva <a name="M0.0047"></a> pavattāraṃ, yaṃ passe vajjadassinaṃ;</p>\r\n<p class="gatha2">Niggayhavādiṃ medhāviṃ, tādisaṃ paṇḍitaṃ bhaje;</p>\r\n<p class="gathalast">Tādisaṃ bhajamānassa, seyyo hoti na pāpiyo.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',48,replace(replace('<p class="gatha1">Muhuttamapi <a name="M0.0048"></a> ce viññū, paṇḍitaṃ payirupāsati;</p>\r\n<p class="gathalast">Khippaṃ dhammaṃ vijānāti, jivhā sūparasaṃ yathā.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Dullabho purisājañño, na so sabbattha jāyati;</p>\r\n<p class="gathalast">Yattha so jāyatī dhīro, taṃ kulaṃ sukha medhati.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',49,replace(replace('<p class="gatha1">Tagarañca <a name="M0.0049"></a> palāsena, yo naro upanayhati;</p>\r\n<p class="gathalast">Pattāpi surati vāyanti, evaṃ dhīrūpasevanā.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Nipuṇe sutameseyya, vicinitvā sutatthiko;</p>\r\n<p class="gathalast">Bhattaṃ ukkhaliyaṃ pakkaṃ, bhājanepi tathā bhave.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',50,replace(replace('<p class="gatha1">Appakaṃ <a name="M0.0050"></a> nātimaññeyya, citte sutaṃ nidhāpaye;</p>\r\n<p class="gathalast">Vammikodakabindūva, cirena paripūrati.</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',51,replace(replace('<p class="gatha1">Gacchaṃ <a name="M0.0051"></a> kipilliko yāti, yojanānaṃ satānipi;</p>\r\n<p class="gathalast">Agacchaṃ venayyoapi, padamekaṃ na gacchati.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Sele sele na maṇikaṃ, gaje gaje na muttikaṃ;</p>\r\n<p class="gathalast">Vane vane na candanaṃ, ṭhāne ṭhāne na paṇḍito.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',52,replace(replace('<p class="gatha1">Paṇḍito <a name="M0.0052"></a> sutasampanno, yattha atthīti ce suto;</p>\r\n<p class="gathalast">Mahussāhena taṃ ṭhānaṃ, gantabbaṃva sutesinā.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Potthakesu ca yaṃ sippaṃ, parahatthesu yaṃ dhanaṃ;</p>\r\n<p class="gathalast">Yathākicce samuppanne, na taṃ sippaṃ na taṃ dhanaṃ.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-85-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',53,replace(replace('<p class="gatha1">Uppalena <a name="M0.0053"></a> jalaṃ jaññā, kiriyāya kulaṃ naro;</p>\r\n<p class="gathalast">Byattippamāṇa vācāya, jaññā tiṇena medaniṃ.</p>\r\n<p class="gatha1">Jalappamāṇaṃ kumudamālaṃ,</p>\r\n<p class="gatha2">Kulappamāṇaṃ vinayopamāṇaṃ;</p>\r\n<p class="gatha3">Byattippamāṇaṃ kathitavākyaṃ,</p>\r\n<p class="gathalast">Pathaviyā pamāṇaṃ tiṇamilātaṃ –</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',54,replace(replace('<p class="gatha1">Appassuto <a name="M0.0054"></a> sutaṃ appaṃ, bahuṃ maññati mānavā;</p>\r\n<p class="gathalast">Sindhudakamapassanto, kūpe toyaṃva maṇḍuko.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Paṭhame sippaṃ gaṇheyya, eseyya dutiye dhanaṃ;</p>\r\n<p class="gathalast">Careyya tatiye dhammaṃ, esā janāna dhammatā.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',55,replace(replace('<p class="gatha1">Sussūsā <a name="M0.0055"></a> suttavaddhanī, sutaṃ paññāya vaddhanaṃ;</p>\r\n<p class="gathalast">Paññāya atthaṃ jānāti, attho ñāto sukhāvaho.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',56,replace(replace('<p class="gatha1">Natthi <a name="M0.0056"></a> vijjāsamaṃ mittaṃ, na ca byādhisamo ripu;</p>\r\n<p class="gathalast">Na ca atthasamaṃ pemaṃ, na ca kammasamaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Vinā satthaṃ na gaccheyya, sūro saṅgāmabhūmiyaṃ;</p>\r\n<p class="gathalast">Paṇḍitvaddhagū vāṇijo, videsagamano tathā.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-90-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',57,replace(replace('<p class="gatha1">Dhananāsaṃ <a name="M0.0057"></a> manotāpaṃ, ghare duccaritāni ca;</p>\r\n<p class="gathalast">Vañcanañca avamānaṃ, paṇḍito na pakāsaye.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> Anavhāyaṃ gamayanto, apucchā bahubhāsako;</p>\r\n<p class="gathalast">Attaguṇaṃ pakāsanto, tividho hīnapuggalo.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',58,replace(replace('<p class="gatha1">Haṃso <a name="M0.0058"></a> majjhe na kākānaṃ, sīho gunnaṃ na sobhate;</p>\r\n<p class="gathalast">Gadrabhamajjhe turaṅgo, bālamajjheva paṇḍito.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Pattānurūpakaṃ vākyaṃ, sabhāvānurūpaṃ piyaṃ;</p>\r\n<p class="gathalast">Attānurūpakaṃ kodhaṃ, yo jānāti sa paṇḍito.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',59,replace(replace('<p class="gatha1">Apparūpo <a name="M0.0059"></a> bahuṃbhāso, appapañño pakāsako;</p>\r\n<p class="gathalast">Appapūro ghaṭo khobhe, appakhīrā gāvī cale.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Na titti rājā dhanamhi, paṇḍitopi subhāsite;</p>\r\n<p class="gathalast">Cakkhupi piyadassane, na titti sāgaro jale.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-97-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',60,replace(replace('<p class="gatha1">Hīnaputto <a name="M0.0060"></a> rājamacco, bālaputto ca paṇḍito;</p>\r\n<p class="gathalast">Adhanassa dhanaṃbahu, purisānaṃ na maññatha.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',61,replace(replace('<p class="gatha1">Yo <a name="M0.0061"></a> sisso sippalobhena, bahuṃ gaṇhāti taṃ sippaṃ;</p>\r\n<p class="gathalast">Mūgova supinaṃ passaṃ, kathetumpi na ussahe.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Na bhijjetuṃ kumbhakāro, sobhetuṃ kumbha ghaṭati;</p>\r\n<p class="gathalast">Na khipituṃ apāyesu, sissānaṃ vuḍḍhikāraṇā.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',62,replace(replace('<p class="gatha1">Adhanassa <a name="M0.0062"></a> rasaṃkhādo, abalassa hato naro;</p>\r\n<p class="gathalast">Appaññassa vākyakaro, ummattaka samāhikho.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> Ekenāpi surukkhena, pupphitena sugandhinā;</p>\r\n<p class="gathalast">Vāsitaṃ kānanaṃ sabbaṃ, suputtena kulaṃ yathā.</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-101-102-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',63,replace(replace('<p class="gatha1">Iṇakattā <a name="M0.0063"></a> pitā sattu, mātā ca byabhicārinī;</p>\r\n<p class="gathalast">Bhariyā rūpavatī sattu, putto sattu apaṇḍito.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> Guṇadosamasatthaññū, jano vibhajate kathaṃ;</p>\r\n<p class="gathalast">Adhikāro kimandhassa, rūpabhedopaladdhiyaṃ.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-103-104-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',64,replace(replace('<p class="gatha1">Sabbattha <a name="M0.0064"></a> satthatoyeva, guṇadosavicecanaṃ;</p>\r\n<p class="gathalast">Yaṃ karoti vināsatthaṃ, sāhasaṃ kimatodhikaṃ.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',65,replace(replace('<p class="gatha1">Nihīyati <a name="M0.0065"></a> puriso nihīnasevī,</p>\r\n<p class="gatha2">Na ca hāyetha kadāci tulyasevī;</p>\r\n<p class="gatha3">Seṭṭhamupanamaṃ udeti khippaṃ,</p>\r\n<p class="gathalast">Tasmā attano uttariṃ bhaje.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',66,replace(replace('<p class="gatha1">Paccuppannañca <a name="M0.0066"></a> yo dhammaṃ, tattha tattha vipassati;</p>\r\n<p class="gathalast">Asaṃhīraṃ asaṃkuppaṃ, taṃ vidvā manubrūhaye.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Chandā dosā bhayā mohā, yo dhammaṃ nātivattati;</p>\r\n<p class="gathalast">Āpūrati tassa yaso, sukkapakkheva candimā.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',67,replace(replace('<p class="gatha1">Paṇḍito <a name="M0.0067"></a> sīlasampanno, saṇho ca paṭibhānavā;</p>\r\n<p class="gathalast">Nivātavutti athaddho, tādiso labhate yasaṃ.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',68,replace(replace('<p class="gatha1">Dullabhaṃ <a name="M0.0068"></a> pākatikaṃ vākyaṃ, dullabho khemakaro suto;</p>\r\n<p class="gathalast">Dullabhā sadisī jāyā, dullabho sajano piyo.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> Atthaṃ mahantamāpajja, vijjaṃ sampattimeva ca;</p>\r\n<p class="gathalast">Careyyāmānathaddho yo, paṇḍito so pavuccati.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-110-111-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',69,replace(replace('<p class="gatha1">Sutasanniccayā <a name="M0.0069"></a> dhīrā, tuṇhībhūtā apucchitā;</p>\r\n<p class="gathalast">Puṇṇāsubhāsitenāpi, ghaṇṭādī ghaṭṭitā yathā.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> Apuṭṭho paṇḍito bherī, pajjunno hoti pucchito;</p>\r\n<p class="gathalast">Bālo puṭṭho apuṭṭho ca, bahuṃ vikatthate sadā.</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-112-113-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',70,replace(replace('<p class="gatha1">Parūpavāde <a name="M0.0070"></a> badhiro, paravajje alocano;</p>\r\n<p class="gathalast">Paṅgulo aññanārīsu, dussatakke acetano.</p>\r\n<p class="gatha1">Cakkhumāssa yathā andho, sotavā badhiro yathā;</p>\r\n<p class="gatha2">Paññavāssa yathāmūgo, balavā dubbaloriva;</p>\r\n<p class="gathalast">Atha atthe samuppanne, sayetha matasāyitaṃ.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-114-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',71,replace(replace('<p class="gatha1">Pāpamitte <a name="M0.0071"></a> vivajjetvā, bhajeyyuttamapuggalaṃ;</p>\r\n<p class="gathalast">Ovāde cassa tiṭṭheyya, patthento acalaṃ sukhaṃ.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> Atisītaṃ atiuṇhaṃ, atisāyamidaṃ ahu;</p>\r\n<p class="gathalast">Iti vissaṭṭhakammante, atthā accenti māṇave.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-115-116-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',72,replace(replace('<p class="gatha1">Yo <a name="M0.0072"></a> ca sītañca uṇhañca, tiṇābhiyyo na maññati;</p>\r\n<p class="gathalast">Karaṃ purisakiccāni, so sukhaṃ na vihāyati.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-117-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',73,replace(replace('<p class="gatha1">Yasmiṃdese <a name="M0.0073"></a> na sammāno, na piyo na ca bandhavo;</p>\r\n<p class="gathalast">Na ca vijjāgamo koci, na tattha divasaṃ vase.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> Dhanavā sutavā rājā, nadī vajjo ime pañca;</p>\r\n<p class="gathalast">Yattha dese na vijjanti, na tattha divasaṃ vase.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-118-119-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',74,replace(replace('<p class="gatha1">Nabhassa <a name="M0.0074"></a> bhūsanaṃ cando, nārīnaṃ bhūsanaṃ pati;</p>\r\n<p class="gathalast">Chamāya bhūsanaṃ rājā, vijjā sabbassa bhūsanaṃ.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> Sukhatthiko sace vijjaṃ, vijjatthiko caje sukhaṃ;</p>\r\n<p class="gathalast">Sukhatthino kuto vijjā, kuto vijjatthino sukhaṃ.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-120-121-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',75,replace(replace('<p class="gatha1">Khaṇena <a name="M0.0075"></a> kaṇena ceva, vijjāmatthañca sādhaye;</p>\r\n<p class="gathalast">Khaṇacāge kuto vijjā, kaṇacāge kato dhanaṃ.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> Ācariyā pādamādatte, pādaṃ sisso sajānanā;</p>\r\n<p class="gathalast">Pādaṃ sabrahmacārīhi, pādaṃ kālakkamena ca.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-122-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',76,replace(replace('<p class="gatha1">Dhammo <a name="M0.0076"></a> jaye no adhammo, saccaṃ jayati nāsaccaṃ;</p>\r\n<p class="gathalast">Khamā jayati no kodho, devo jayati nāsūro.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Hatthassa bhūsanaṃ dānaṃ, saccaṃ kaṇṭhassa bhūsanaṃ;</p>\r\n<p class="gathalast">Sotassa bhūsanaṃ satthaṃ, bhūsane kiṃ payojanaṃ.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-124-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',77,replace(replace('<p class="gatha1">Videsetu <a name="M0.0077"></a> dhanaṃ vijjā, byasanesu dhanaṃ mati;</p>\r\n<p class="gathalast">Paraloke dhanaṃ dhammo, sīlaṃ sabbattha ve dhanaṃ.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',78,replace(replace('<p class="gatha1">Padose <a name="M0.0078"></a> dīpako cando, pabhāte dīpako ravi;</p>\r\n<p class="gathalast">Tiloke dīpako dhammo, suputto kuladīpako.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> Vidvā eva vijānāti, vidvajjanaparissamaṃ;</p>\r\n<p class="gathalast">Na hi vañjhā vijānāti, guruṃ pasavavedanaṃ.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-127-128-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',79,replace(replace('<p class="gatha1">Yassa <a name="M0.0079"></a> natthi sayaṃ paññā, satthaṃ tassa karoti kiṃ;</p>\r\n<p class="gathalast">Locanehi vihīnassa, dappaṇo kiṃ karissati.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> Kiṃ karissanti vattāro, sotaṃ yattha na vijjate;</p>\r\n<p class="gathalast">Naggakapaṇake dese, rajako kiṃ karissati.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-129-130-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',80,replace(replace('<p class="gatha1">Mūḷhasidhassāpadesena, <a name="M0.0080"></a> kunārībharaṇena ca;</p>\r\n<p class="gathalast">Khalasattūhi saṃyogā, paṇḍitopyāvasīdati.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> Natthi attasamaṃ pemaṃ, natthi dhaññasamaṃ dhanaṃ;</p>\r\n<p class="gathalast">Natthi paññāsamā ābhā, vuṭṭhi ve paramā sarā.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-131-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',81,replace(replace('<p class="gatha1">Bhujaṅgamaṃ <a name="M0.0081"></a> pāvakañca khattiyañca yasassinaṃ;</p>\r\n<p class="gathalast">Bhikkhuñca sīlasampannaṃ, sammadeva samācare.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> Tasmā hi paṇḍito poso, sampassaṃ atthamattano;</p>\r\n<p class="gathalast">Buddhe dhamme ca saṅghe ca, dhīro saddhaṃ nivesaye.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-133-134-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',82,replace(replace('<p class="gatha1">Guṇo <a name="M0.0082"></a> seṭṭhaṅgataṃ yāti, na ucce sayane vase;</p>\r\n<p class="gathalast">Pāsādasikhare vāso, kāko kiṃ garuḷo siyā.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> Anāgataṃ bhayaṃ disvā, dūrato parivajjaye;</p>\r\n<p class="gathalast">Āgatañca bhayaṃ disvā, abhīto hoti paṇḍito.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> Asajjāya malāmantā, anuṭṭhānamalā gharā;</p>\r\n<p class="gathalast">Malaṃ vaṇṇassa kosajjaṃ, pamādo rakkhato malaṃ.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-135-136-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',83,replace(replace('<p class="gatha1">Anupubbena <a name="M0.0083"></a> medhāvī, thokaṃ thokaṃ khaṇe khaṇe;</p>\r\n<p class="gathalast">Kammāro rajatasseva, niddhame malamattano.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-138-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',84,replace(replace('<p class="gatha1">Yañhi <a name="M0.0084"></a> kayirā tañhi vade, yaṃ na kayirā na taṃ vade;</p>\r\n<p class="gathalast">Akarontaṃ bhāsamānaṃ, parijānanti paṇḍitā.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> Visamaṃ sabhayaṃ ativāto, paṭicchannaṃ devanissitaṃ;</p>\r\n<p class="gathalast">Pantho ca saṅgāmo titthaṃ, aṭṭhete parivajjiyā.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-139-140-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',85,replace(replace('<p class="gatha1">Rattoduṭṭho <a name="M0.0085"></a> ca muḷho ca, mānī luddho tathālaso;</p>\r\n<p class="gathalast">Ekacintī ca bālo ca, ete atthavināsakā.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> Ratto duṭṭho ca mūḷho ca, bhīru āmisagaruko;</p>\r\n<p class="gathalast">Itthī soṇḍo paṇḍako ca, navamo dārakopi ca.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> Navate puggalā loke, ittarā calitā calā;</p>\r\n<p class="gathalast">Etehi mantitaṃ guyhaṃ, khippaṃ bhavati pākaṭaṃ.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-141-142-143-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',86,replace(replace('<p class="gatha1">Yo <a name="M0.0086"></a> niruttiṃ na sikkheyya, sikkhanto piṭakattayaṃ;</p>\r\n<p class="gathalast">Pade pade vikaṅkheyya, vane andhagajo yathā.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',87,replace(replace('<p class="gatha1">Suttaṃ <a name="M0.0087"></a> dhātu gaṇoṇvādi, nāmaliṅgānusāsanaṃ;</p>\r\n<p class="gathalast">Yassa tiṭṭhati jivhagge, sabyākaraṇakesarī.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Saddatthalakkhaṇe bhedī, yo yo nicchitalakkhaṇe;</p>\r\n<p class="gathalast">So so ñātumakicchena, pahoti piṭakattaye.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',88,replace(replace('<p class="gatha1">Yo <a name="M0.0088"></a> saddasatthakusalo kusalo nighaṇḍu,</p>\r\n<p class="gatha2">Chando alaṅkatisu niccakatābhiyogo;</p>\r\n<p class="gatha3">So yaṃ kavittavikalopi kavīsu saṅkhyaṃ,</p>\r\n<p class="gathalast">Moggayha vindati hi kitti’ mamandarūpaṃ.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-147-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',89,replace(replace('<p class="gatha1">Sukkhopi <a name="M0.0089"></a> candanataru na jahāti gandhaṃ,</p>\r\n<p class="gatha2">Nāgo gato naramukhe na jahāti līḷaṃ;</p>\r\n<p class="gatha3">Yantagato madhurasaṃ na jahāti ucchu,</p>\r\n<p class="gathalast">Dukkhopi paṇḍitajano na jahāti dhammaṃ.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> Dhanadhaññappayogesu, tathā vijjāgamesu ca;</p>\r\n<p class="gathalast">Āhāre byavahāre ca, cattalajjo sadā bhave.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-148-149-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',90,replace(replace('<p class="gatha1">Sābhāvikī <a name="M0.0090"></a> ca paṭibhā, sutañca bahunimmalaṃ;</p>\r\n<p class="gathalast">Amando cābhiyogoyaṃ, hetu hotiha bandhane.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-150-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',91,replace(replace('<p class="gatha1">Jaheyya <a name="M0.0091"></a> pāpake mitte, bhajeyya paṇḍite jane;</p>\r\n<p class="gathalast">Sādhavo abhiseveyya, suṇeyya dhammamuttamaṃ.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> Kalyāṇakārī kalyāṇaṃ, pāpakārī ca pāpakaṃ;</p>\r\n<p class="gathalast">Yādisaṃ vappate bījaṃ, tādisaṃ harate phalaṃ.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-151-152-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',92,replace(replace('<p class="gatha1">Chando <a name="M0.0092"></a> nidānaṃ gāthānaṃ, akkharā tāsaṃ viyañjanaṃ;</p>\r\n<p class="gathalast">Nāmasannissitā gāthā, kavi gāthānamāsayo.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-153-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',93,replace(replace('<p class="gatha1">Tasmā <a name="M0.0093"></a> hi paṇḍito poso, sampassaṃ hitamattano;</p>\r\n<p class="gathalast">Paññavantaṃbhipūjeyya, cetiyaṃ viya sādaro.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> Dhīraṃ passe suṇe dhīraṃ, dhīrena sahasaṃvase;</p>\r\n<p class="gathalast">Dhīrenallāpasallāpaṃ, taṃ kare tañca rocaye.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-154-155-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',94,replace(replace('<p class="gatha1">Nayaṃ <a name="M0.0094"></a> nayati medhāvī, adhurāyaṃ na yuñjati;</p>\r\n<p class="gatha2">Sunayo seyyaso hoti, sammā vutto na kuppati;</p>\r\n<p class="gathalast">Vinayaṃ so pajānāti, sādhu tena samāgamo.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> Sace labhetha nipakaṃ sahāyaṃ,</p>\r\n<p class="gatha2">Saddhiṃ caraṃ sādhuvihāri dhīraṃ;</p>\r\n<p class="gatha3">Abhibhuyya sabbāni parissayāni,</p>\r\n<p class="gathalast">Careyya tenattamano satimā.</p>\r\n<p class="hangnum"><a name="para157"></a><span class="paranum">157</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-156-157-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',95,replace(replace('<p class="gatha1">No <a name="M0.0095"></a> ce labhetha nipakaṃ sahāyaṃ,</p>\r\n<p class="gatha2">Saddhiṃ caraṃ sādhuvihāri dhīraṃ;</p>\r\n<p class="gatha3">Rājāva raṭṭhaṃ vijitaṃ pahāya,</p>\r\n<p class="gathalast">Eko care mātaṅgaraññeva nāgo.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',96,replace(replace('<p class="gatha1">Sokaṭṭhānasahassāni<a name="M0.0096"></a>, bhayaṭṭhānasatāni ca;</p>\r\n<p class="gathalast">Divase divase mūḷha-māvisanti na paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> Jalabindunipātena, cirena pūrate ghaṭo;</p>\r\n<p class="gathalast">Tathā sakalavijjānaṃ, dhammassa ca dhanassa ca.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-159-160-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',97,replace(replace('<p class="gatha1">Paṇḍitā <a name="M0.0097"></a> dukkhaṃ patvāna, na bhavanti visādino;</p>\r\n<p class="gathalast">Pavissa rāhuno mukhaṃ, kiṃ no deti puna sasī.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> Javena assaṃ jānanti, vāhena ca balibaddhaṃ;</p>\r\n<p class="gathalast">Duhena dhenuṃ jānanti, bhāsamānena paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-161-162-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',98,replace(replace('<p class="gatha1">Manasā <a name="M0.0098"></a> cintitaṃ kammaṃ, vacasā na pakāsaye;</p>\r\n<p class="gathalast">Aññalakkhitakāriyassa, yato siddhi na jāyate.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> Anabhyāse visaṃ vijjā, ajiṇṇe bhojanaṃ visaṃ;</p>\r\n<p class="gathalast">Visaṃ sabhā daliddassa, vuddhassa taruṇī visaṃ.</p>\r\n<p class="gatha1">Cattāro pañca ālope, ābhutvā udakaṃ pive;</p>\r\n<p class="gathalast">Alaṃ phāsuvihārāya, pahitattassa bhikkhuno.</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> Yassa eso pasutopi, guṇavā pujjate naro;</p>\r\n<p class="gathalast">Dhanu vaṃsavisuddhopi, nigguṇo kiṃ karissati.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-163-164-165-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',100,replace(replace('<p class="gatha1">Issī <a name="M0.0100"></a> dayī asaṃtuṭṭho, kodhano niccasaṅkīto;</p>\r\n<p class="gathalast">Parabhāgyopajīvī ca, chaḷete dukkhabhāgino.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> Sumahantāni sattānī, dhārayantā bahussutā;</p>\r\n<p class="gathalast">Chettāro saṃsayānañca, kaliṃ yanti lobhamohitā.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-166-167-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',101,replace(replace('<p class="gatha1">Nadītīre <a name="M0.0101"></a> khate kūpe, araṇītālavaṇṭake;</p>\r\n<p class="gathalast">Na vade dakādī natthīti, mukhe ca vacanaṃ tathā.</p>\r\n<p class="hangnum"><a name="para168"></a><span class="paranum">168</span>.</p>\r\n<p class="gatha1"> Sabbaṃ suṇāti sotena, sabbaṃ passati cakkhunā;</p>\r\n<p class="gathalast">Na ca diṭṭhaṃ sutaṃ dhīro, sabbaṃ uccitu marahati.</p>\r\n<p class="hangnum"><a name="para169"></a><span class="paranum">169</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-168-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',102,replace(replace('<p class="gatha1">Bālādapi <a name="M0.0102"></a> gahetabbaṃ, yuttamuttamanīsibhi;</p>\r\n<p class="gathalast">Ravissāvisaye kiṃ na, padīpassa pakāsanaṃ.</p>\r\n<p class="hangnum"><a name="para170"></a><span class="paranum">170</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-170-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',103,replace(replace('<p class="gatha1">Tasmā <a name="M0.0103"></a> hi paṇḍito poso, sampassaṃ attamattano;</p>\r\n<p class="gathalast">Yoniso vicine dhammaṃ, evaṃ tattha visujjhati.</p>\r\n<p class="hangnum"><a name="para171"></a><span class="paranum">171</span>.</p>\r\n<p class="gatha1"> Kiṃ tena jātujātena, mātuyobbannahārinā;</p>\r\n<p class="gathalast">Ārohati na yo saka-vaṃsaagge dhajo yathā.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> Sammā upaparikkhitvā, akkharesu padesu ca;</p>\r\n<p class="gathalast">Coraghāto siyā sisso, guru coraṭṭakārako.</p>\r\n<p class="hangnum"><a name="para173"></a><span class="paranum">173</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-171-172-173-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',104,replace(replace('<p class="gatha1">Adantadamanaṃ <a name="M0.0104"></a> satthaṃ, khalānaṃ kurute madaṃ;</p>\r\n<p class="gathalast">Cakkhusaṅkhārakaṃ tejaṃ, ulūkānaṃmivandhakaṃ.</p>\r\n<p class="hangnum"><a name="para174"></a><span class="paranum">174</span>.</p>\r\n<p class="gatha1"> Narattaṃ dullabhaṃ loke, vijjā tatra sudullabhā;</p>\r\n<p class="gathalast">Kavittaṃ dullabhaṃ tatra, satti tatra sudullabhā.</p>\r\n<p class="hangnum"><a name="para175"></a><span class="paranum">175</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-174-175-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',105,replace(replace('<p class="gatha1">Yebhuyyena <a name="M0.0105"></a> hi sattānaṃ, vināse paccupaṭṭhite;</p>\r\n<p class="gathalast">Anayo nayarūpena, buddhimāgamma tiṭṭhati.</p>\r\n<p class="chapter">Sujanakaṇḍa</p>\r\n<p class="hangnum"><a name="para176"></a><span class="paranum">176</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-176-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',106,replace(replace('<p class="gatha1">Saddhāsīlādidhammehi, <a name="M0.0106"></a> sappanno seṭṭhamānuso;</p>\r\n<p class="gathalast">Vutto buddhādisantehi, sādhusappuriso iti.</p>\r\n<p class="gatha1">Saddādhanaṃ sīladhanaṃ, hirīottappiyaṃ dhanaṃ;</p>\r\n<p class="gathalast">Sutadhanañca cāgo ca, paññā ve sattamaṃ dhanaṃ.</p>\r\n<p class="gatha1">Yassa ete dhanā atthi, itthiyā purisassa vā;</p>\r\n<p class="gathalast">Adaliddoti taṃ āhu, amoghaṃ tassa jīvitaṃ.</p>\r\n<p class="gatha1">Tasmā saddhañca sīlañca, pasādaṃ dhammadassanaṃ;</p>\r\n<p class="gathalast">Anuyuñjetha medhāvī, saraṃ buddhānasāsanaṃ.</p>\r\n<p class="hangnum"><a name="para177"></a><span class="paranum">177</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-177-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',107,replace(replace('<p class="gatha1">Sabbhireva <a name="M0.0107"></a> samāsetha, sabbhi kubbetha santhavaṃ;</p>\r\n<p class="gathalast">Sataṃ saddhammamaññāya, seyyo hoti na pāpiyo.</p>\r\n<p class="hangnum"><a name="para178"></a><span class="paranum">178</span>.</p>\r\n<p class="gatha1"> Caja dujjanasaṃsaggaṃ, bhaja sādhusamāgamaṃ;</p>\r\n<p class="gathalast">Kara puññamahorattaṃ, sara niccamaniccataṃ.</p>\r\n<p class="hangnum"><a name="para179"></a><span class="paranum">179</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-178-179-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',108,replace(replace('<p class="gatha1">Yo <a name="M0.0108"></a> ve kataññū katavedī dhīro,</p>\r\n<p class="gatha2">Kalyāṇamitto daḷhabhatti ca hoti;</p>\r\n<p class="gatha3">Dukkhitassa sakkacca karoti kiccaṃ,</p>\r\n<p class="gathalast">Tathāvidhaṃ sappurisaṃ vadanti.</p>\r\n<p class="hangnum"><a name="para180"></a><span class="paranum">180</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-180-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',109,replace(replace('<p class="gatha1">Mātāpettibharaṃ <a name="M0.0109"></a> jantuṃ, kule jeṭṭhāpacāyinaṃ;</p>\r\n<p class="gathalast">Saṇhaṃ sakhilasambhāsaṃ, pesuṇeyyappahāyinaṃ.</p>\r\n<p class="hangnum"><a name="para181"></a><span class="paranum">181</span>.</p>\r\n<p class="gatha1"> Maccheravinaye yuttaṃ, saccaṃ kodhābhibhuṃ naraṃ;</p>\r\n<p class="gathalast">Taṃ ve devā tāvatiṃsā, āhu ‘‘sappuriso’’iti.</p>\r\n<p class="hangnum"><a name="para182"></a><span class="paranum">182</span>.</p>\r\n<p class="gatha1"> Kulajāto kulaputto, kulavaṃsasurakkhato;</p>\r\n<p class="gathalast">Attanā dukkhappattopi, hīnakammaṃ na kāraye.</p>\r\n<p class="hangnum"><a name="para183"></a><span class="paranum">183</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-181-182-183-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',110,replace(replace('<p class="gatha1">Udeyya <a name="M0.0110"></a> bhāṇu pacchime, nameyya meruaddipi;</p>\r\n<p class="gatha2">Sītalaṃ yadi naraggi, pabbatagge ca uppalaṃ;</p>\r\n<p class="gathalast">Vikase na viparitā, sādhuvācā kudācanaṃ.</p>\r\n<p class="hangnum"><a name="para184"></a><span class="paranum">184</span>.</p>\r\n<p class="gatha1"> Sukhā rukkhassa chāyāva, tato ñātimātāpitu;</p>\r\n<p class="gathalast">Tato ācerassa rañño, tato buddhassanekadhā.</p>\r\n<p class="hangnum"><a name="para185"></a><span class="paranum">185</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-184-185-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',111,replace(replace('<p class="gatha1">Bhamarā <a name="M0.0111"></a> pupphamicchanti, guṇamicchanti sajjanā;</p>\r\n<p class="gathalast">Makkhikā pūtimicchanti, dosamicchanti dujjanā.</p>\r\n<p class="hangnum"><a name="para186"></a><span class="paranum">186</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-186-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',112,replace(replace('<p class="gatha1">Mātuhīno <a name="M0.0112"></a> dubbhāso hi, pituhīno dukkiriyo;</p>\r\n<p class="gathalast">Ubho mātupituhīnā, dubbhāsā ca dukkiriyā.</p>\r\n<p class="hangnum"><a name="para187"></a><span class="paranum">187</span>.</p>\r\n<p class="gatha1"> Mātuseṭṭho subhāso hi, pituseṭṭho sukiriyo;</p>\r\n<p class="gathalast">Ubhomātu pituseṭṭhā, subhāsā ca sukiriyā.</p>\r\n<p class="hangnum"><a name="para188"></a><span class="paranum">188</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-187-188-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',113,replace(replace('<p class="gatha1">Sunakho <a name="M0.0113"></a> sunakhaṃ disvā, dantaṃ dasseti hiṃsituṃ;</p>\r\n<p class="gathalast">Dujjano sujanaṃ disvā, rosayaṃ hiṃsamicchati.</p>\r\n<p class="hangnum"><a name="para189"></a><span class="paranum">189</span>.</p>\r\n<p class="gatha1"> Na ca vegena kiccāni, kattabbāni kudācanaṃ;</p>\r\n<p class="gathalast">Sahasā kāritaṃ kammaṃ, bālo pacchānutappati.</p>\r\n<p class="hangnum"><a name="para190"></a><span class="paranum">190</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-189-190-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',114,replace(replace('<p class="gatha1">Kodhaṃ <a name="M0.0114"></a> vadhitvā na kadāci socati,</p>\r\n<p class="gatha2">Makkhappahānaṃ isayo vaṇṇayanti;</p>\r\n<p class="gatha3">Sabbesaṃ vuttaṃ pharusaṃ khametha,</p>\r\n<p class="gathalast">Etaṃ khantiṃ uttamamāhu santo.</p>\r\n<p class="hangnum"><a name="para191"></a><span class="paranum">191</span>.</p>\r\n<p class="gatha1"> Dukkho nivāso sambādhe, ṭhāne asucīsaṅkate;</p>\r\n<p class="gathalast">Tato arimhi appiye, tatopi akataññunā.</p>\r\n<p class="hangnum"><a name="para192"></a><span class="paranum">192</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-191-192-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',115,replace(replace('<p class="gatha1">Ovadeyyā’nusāseyya<a name="M0.0115"></a>, asabbhā ca nivāraye;</p>\r\n<p class="gathalast">Satañhi so piyo hoti, asataṃ hotiappiyo.</p>\r\n<p class="hangnum"><a name="para193"></a><span class="paranum">193</span>.</p>\r\n<p class="gatha1"> Uttamattanivātena, kakkhaḷaṃ mudunā jaye;</p>\r\n<p class="gathalast">Nīcaṃ appakadānena, vāyāmena samaṃ jaye.</p>\r\n<p class="hangnum"><a name="para194"></a><span class="paranum">194</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-193-194-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',116,replace(replace('<p class="gatha1">Na <a name="M0.0116"></a> visaṃ visamiccāha, dhanaṃ saṅghassa uccate;</p>\r\n<p class="gathalast">Visaṃ ekaṃva hanati, hanati saṅghassa sabbaṃ.</p>\r\n<p class="hangnum"><a name="para195"></a><span class="paranum">195</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-195-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',117,replace(replace('<p class="gatha1">Dhanamappampi <a name="M0.0117"></a> sādhūnaṃ, kūpe vāriva nissayo;</p>\r\n<p class="gathalast">Bahuṃapi asādhūnaṃ, na ca vāriva aṇṇave.</p>\r\n<p class="hangnum"><a name="para196"></a><span class="paranum">196</span>.</p>\r\n<p class="gatha1"> Apattheyyaṃ na pattheyya, acinteyyaṃ na cintaye;</p>\r\n<p class="gathalast">Dhammameva sucinteyya, kālaṃ moghaṃ na icchaye.</p>\r\n<p class="hangnum"><a name="para197"></a><span class="paranum">197</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-196-197-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',118,replace(replace('<p class="gatha1">Acintitampi <a name="M0.0118"></a> bhavati, cintitampi vinassati;</p>\r\n<p class="gathalast">Na hi cintāmayā bhogā, itthiyā purisassa vā.</p>\r\n<p class="hangnum"><a name="para198"></a><span class="paranum">198</span>.</p>\r\n<p class="gatha1"> Asantassa piyo hoti, sante na kurute piyaṃ;</p>\r\n<p class="gathalast">Asataṃ dhammaṃ roceti, taṃ parābhavato mukhaṃ.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-198-199-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',119,replace(replace('<p class="gatha1">Guṇā <a name="M0.0119"></a> kubbanti dūtattaṃ, dūrepi vasataṃ sataṃ;</p>\r\n<p class="gathalast">Ketake gandhaṃ ghāyitvā, gacchanti bhamarā sayaṃ.</p>\r\n<p class="hangnum"><a name="para200"></a><span class="paranum">200</span>.</p>\r\n<p class="gatha1"> Pubbajātikataṃ kammaṃ, taṃ kammamīti kathyate;</p>\r\n<p class="gathalast">Tasmā purisākārenaṃ, yataṃ kare atandito.</p>\r\n<p class="hangnum"><a name="para201"></a><span class="paranum">201</span>.</p>\r\n<p class="gatha1"> Mattikapiṇḍato kattā, kurute yaṃ yadicchati;</p>\r\n<p class="gathalast">Evamattakataṃ kammaṃ, māṇavo paṭipajjate.</p>\r\n<p class="hangnum"><a name="para202"></a><span class="paranum">202</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-200-201-202-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',121,replace(replace('<p class="gatha1">Uṭṭhāyoṭṭhāya <a name="M0.0121"></a> bodheyyaṃ, mahabbhaya mupaṭṭhitaṃ;</p>\r\n<p class="gathalast">Maraṇabyādhisokānaṃ, kimajja nipatissati.</p>\r\n<p class="hangnum"><a name="para203"></a><span class="paranum">203</span>.</p>\r\n<p class="gatha1"> Pāṇā yathāttanobhiṭṭhā, bhūtānamapi te tathā;</p>\r\n<p class="gathalast">Attopamena bhūtesu, dayaṃ kubbanti sādhavo.</p>','\r',char(13)),'\n',char(10)),'-203-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',122,replace(replace('<p class="gatha1">Sabbe <a name="M0.0122"></a> tasanti daṇḍassa, sabbe bhāyanti maccuno;</p>\r\n<p class="gathalast">Attānaṃ upamaṃ katvā, na haneyya na ghātaye.</p>\r\n<p class="hangnum"><a name="para204"></a><span class="paranum">204</span>.</p>\r\n<p class="gatha1"> Bālo vā yadi vā vuddho, yuvā vā gehamāgato;</p>\r\n<p class="gathalast">Tassa pūjā vidhātabbā, sabbassābhyāgato garu.</p>\r\n<p class="hangnum"><a name="para205"></a><span class="paranum">205</span>.</p>\r\n<p class="gatha1"> Ākiṇṇopi asantehi, asaṃsaṭṭhova bhaddako;</p>\r\n<p class="gathalast">Bahunā sannajātena, gacchena ubbattenidha.</p>\r\n<p class="chapter">Bāladujjanakaṇḍa</p>\r\n<p class="hangnum"><a name="para206"></a><span class="paranum">206</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-204-205-206-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',124,replace(replace('<p class="gatha1">Kāyaduccaritādīhi<a name="M0.0124"></a>, sampanno pāpamānujo;</p>\r\n<p class="gathalast">Bāloti lokanāthena, kittito dhammasāminā.</p>\r\n<p class="hangnum"><a name="para207"></a><span class="paranum">207</span>.</p>\r\n<p class="gatha1"> Ducintitacintī ceva, dubbhāsitabhāsīpi ca;</p>\r\n<p class="gathalast">Dukkaṭakammakārī ca, pāpako bālamānujo.</p>\r\n<p class="hangnum"><a name="para208"></a><span class="paranum">208</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-207-208-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',125,replace(replace('<p class="gatha1">Atipiyo <a name="M0.0125"></a> na kātabbo, khalo kotuhalaṃ karo;</p>\r\n<p class="gathalast">Sirasā vahamānopi, aḍḍhapūro ghaṭo yathā.</p>\r\n<p class="hangnum"><a name="para209"></a><span class="paranum">209</span>.</p>\r\n<p class="gatha1"> Sappo duṭṭho khalo duṭṭho, sappā duṭṭhataro khalo;</p>\r\n<p class="gathalast">Mantosadhehi taṃ sappaṃ, khalaṃ kenupasammati.</p>\r\n<p class="hangnum"><a name="para210"></a><span class="paranum">210</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-209-210-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',126,replace(replace('<p class="gatha1">Yo <a name="M0.0126"></a> bālo maññati bālyaṃ, paṇḍito vāpi tenaso;</p>\r\n<p class="gathalast">Bālo ca paṇḍitamānī, sa ve bāloti vuccati.</p>\r\n<p class="hangnum"><a name="para211"></a><span class="paranum">211</span>.</p>\r\n<p class="gatha1"> Madhūva maññati bālo, yāva pāpaṃ na paccati;</p>\r\n<p class="gathalast">Yadā ca paccati pāpaṃ, atha dukkhaṃ nigacchati.</p>\r\n<p class="hangnum"><a name="para212"></a><span class="paranum">212</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-211-212-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',127,replace(replace('<p class="gatha1">Na <a name="M0.0127"></a> sādhu balavā bālo, sāhasā vindate dhanaṃ;</p>\r\n<p class="gathalast">Kāyassa bhedā duppañño, nirayaṃ sopapajjati.</p>\r\n<p class="hangnum"><a name="para213"></a><span class="paranum">213</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-213-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',128,replace(replace('<p class="gatha1">Ghare <a name="M0.0128"></a> duṭṭho mūsiko ca, vane duṭṭho ca vānaro;</p>\r\n<p class="gathalast">Sakuṇe ca duṭṭho kāko, nareduṭṭho ca brāhmaṇo.</p>\r\n<p class="hangnum"><a name="para214"></a><span class="paranum">214</span>.</p>\r\n<p class="gatha1"> Dīghā jāgarato ratti, dīghaṃ santassa yojanaṃ;</p>\r\n<p class="gathalast">Dīgho bālānasaṃsāro, saddhammaṃ avijānataṃ.</p>\r\n<p class="hangnum"><a name="para215"></a><span class="paranum">215</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-214-215-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',129,replace(replace('<p class="gatha1">Tilamattaṃ <a name="M0.0129"></a> paresañca, appadosañca passati;</p>\r\n<p class="gathalast">Nāḷikerampi sadosaṃ, khalajāto na passati.</p>\r\n<p class="hangnum"><a name="para216"></a><span class="paranum">216</span>.</p>\r\n<p class="gatha1"> Nattadosaṃ pare jaññā, jaññā dosaṃ parassatu;</p>\r\n<p class="gathalast">Guyho kummāva aṅgāni, paradosañca lakkhaye.</p>\r\n<p class="hangnum"><a name="para217"></a><span class="paranum">217</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-216-217-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',130,replace(replace('<p class="gatha1">Luddhaṃ <a name="M0.0130"></a> atthena gaṇheyya, thaddhaṃ añjalikammunā;</p>\r\n<p class="gathalast">Chandānuvattiyā mūḷhaṃ, yathābhūtena paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para218"></a><span class="paranum">218</span>.</p>\r\n<p class="gatha1"> Yathā udumbarapakkā, bahi rattakā eva ca;</p>\r\n<p class="gathalast">Antokimila sampuṇṇā, evaṃ dujjanahadayā.</p>\r\n<p class="hangnum"><a name="para219"></a><span class="paranum">219</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-218-219-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',131,replace(replace('<p class="gatha1">Yāvajīvampi <a name="M0.0131"></a> ce bālo, paṇḍitaṃ payirupāsati;</p>\r\n<p class="gathalast">Na so dhammaṃ vijānāti, dabbī sūparasaṃ yathā.</p>\r\n<p class="hangnum"><a name="para220"></a><span class="paranum">220</span>.</p>\r\n<p class="gatha1"> Carañce nādhigaccheyya, seyyaṃ sadisamattano;</p>\r\n<p class="gathalast">Ekacariyaṃ daḷhaṃ kayirā, natthi bāle sahāyatā.</p>\r\n<p class="hangnum"><a name="para221"></a><span class="paranum">221</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-220-221-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',132,replace(replace('<p class="gatha1">Ajātamatamūḷhānaṃ<a name="M0.0132"></a>, varamādayo na cantimo;</p>\r\n<p class="gathalast">Sakiṃ dukkhakarāvāda-yontimo tu pade pade.</p>\r\n<p class="hangnum"><a name="para222"></a><span class="paranum">222</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-222-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',133,replace(replace('<p class="gatha1">Dujjanena <a name="M0.0133"></a> samaṃ veraṃ, sakhyañcāpi na kāraye;</p>\r\n<p class="gathalast">Uṇho dahati caṅgāro, sīto kaṇhāyate karaṃ.</p>\r\n<p class="hangnum"><a name="para223"></a><span class="paranum">223</span>.</p>\r\n<p class="gatha1"> Dujjano piyavādī ca, netaṃ vissāsakāraṇaṃ;</p>\r\n<p class="gathalast">Madhu tiṭṭhati jivhagge, hadaye halāhalaṃ visaṃ.</p>\r\n<p class="hangnum"><a name="para224"></a><span class="paranum">224</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-223-224-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',134,replace(replace('<p class="gatha1">Dujjano <a name="M0.0134"></a> parihātabbo, vijjāyālaṅkatopi ca;</p>\r\n<p class="gathalast">Maṇinā bhūsito sappo, kimeso na bhayaṅkaro.</p>\r\n<p class="hangnum"><a name="para225"></a><span class="paranum">225</span>.</p>\r\n<p class="gatha1"> Nāḷikerasamākārā, dissantepi hi sajjanā;</p>\r\n<p class="gathalast">Aññe badarikākārā, bahireva manoharā.</p>\r\n<p class="hangnum">  </p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-225-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',135,replace(replace('<p class="gatha1">Yathāpi <a name="M0.0135"></a> pana sapakkā, bahi kaṇṭakameva ca;</p>\r\n<p class="gathalast">Anto amatasampuṇṇā, evaṃ sujanahadayā.</p>\r\n<p class="gatha1">Yathā udumbarapakkā, bahi rattakameva ca;</p>\r\n<p class="gathalast">Evaṃ kimilasampuṇā, evaṃ dujjanahadayā.</p>\r\n<p class="hangnum"><a name="para226"></a><span class="paranum">226</span>.</p>\r\n<p class="gatha1"> Dosabhīto anārambho, taṃ kā purisalakkhaṇaṃ;</p>\r\n<p class="gathalast">Kohyajiṇṇabhayā nanu, bhojanaṃ parihīyate.</p>\r\n<p class="hangnum"><a name="para227"></a><span class="paranum">227</span>.</p>\r\n<p class="gatha1"> Payopānaṃ bhujaṅgānaṃ, kevalaṃ visavaḍḍhanaṃ;</p>\r\n<p class="gathalast">Upadeso hi mūḷhānaṃ, pakopāya na santiyā.</p>\r\n<p class="hangnum"><a name="para228"></a><span class="paranum">228</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-226-227-228-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',136,replace(replace('<p class="gatha1">Na <a name="M0.0136"></a> ṭhātabbaṃ na gantabbaṃ, dujjanena samaṃ kvaci;</p>\r\n<p class="gathalast">Dujjano hi dukkhaṃ deti, na so sukhaṃ kadācipi.</p>\r\n<p class="hangnum"><a name="para229"></a><span class="paranum">229</span>.</p>\r\n<p class="gatha1"> Abaddhā tattha bajjhanti, yattha bālā pabhāsare;</p>\r\n<p class="gathalast">Baddhāpi tattha muccanti, yattha dhīrā pabhāsare.</p>\r\n<p class="hangnum"><a name="para230"></a><span class="paranum">230</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-229-230-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',137,replace(replace('<p class="gatha1">Parittaṃ <a name="M0.0137"></a> dārumāruyha, yathā sīde mahaṇṇave;</p>\r\n<p class="gatha2">Evaṃ kusitamāgamma, sādhu jīvipi sīdati;</p>\r\n<p class="gathalast">Tasmā taṃ parivajjeyya, kusītaṃ hīnavīriyaṃ.</p>\r\n<p class="hangnum"><a name="para231"></a><span class="paranum">231</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',138,replace(replace('<p class="gatha1">Saddhāsīlādisampanno<a name="M0.0138"></a>, sumitto sādhumānuso;</p>\r\n<p class="gathalast">Tādisaṃ mittaṃ seveyya, vuddhikāmo vicakkhaṇo.</p>\r\n<p class="hangnum"><a name="para232"></a><span class="paranum">232</span>.</p>\r\n<p class="gatha1"> Dānādiguṇaseṭṭhehi, miditabbo mitto hi kho;</p>\r\n<p class="gathalast">Tādisaṃ avaṅkeneva, manasā bhajeyya sudhī.</p>\r\n<p class="hangnum"><a name="para333"></a><span class="paranum">333</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-232-333-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',139,replace(replace('<p class="gatha1">Hitakkaro <a name="M0.0139"></a> paro bandhu, bandhupi ahito paro;</p>\r\n<p class="gathalast">Ahito dehajo byādhi, hitaṃ araññamosadhaṃ.</p>\r\n<p class="hangnum"><a name="para234"></a><span class="paranum">234</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-234-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',140,replace(replace('<p class="gatha1">Parokkhe <a name="M0.0140"></a> kiccahantāraṃ, paccakkhe piyavādinaṃ;</p>\r\n<p class="gathalast">Vajjaye tādisaṃ mittaṃ, visakumbhaṃ payomukhaṃ.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> Dhanahīne caje mitto, puttadārā sahodarā;</p>\r\n<p class="gathalast">Dhanavanteva sevanti, dhanaṃ loke mahāsakhā.</p>\r\n<p class="hangnum"><a name="para236"></a><span class="paranum">236</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-135-236-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',141,replace(replace('<p class="gatha1">Jāniyā <a name="M0.0141"></a> pesane bhacce, bandhave byasanāgame;</p>\r\n<p class="gathalast">Mittañca āpadikāle, bhariyañca vibhavakkhaye.</p>\r\n<p class="hangnum"><a name="para237"></a><span class="paranum">237</span>.</p>\r\n<p class="gatha1"> Ussave byasane ceva, dubbhikkhe sattuviggahe;</p>\r\n<p class="gathalast">Rājadvāre susāne ca, yo tiṭṭhati so bandhavā.</p>\r\n<p class="hangnum"><a name="para238"></a><span class="paranum">238</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-237-238-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',142,replace(replace('<p class="gatha1">Na <a name="M0.0142"></a>2 vissase amittassa, mittañcāpi na vissase;</p>\r\n<p class="gathalast">Kadāci kupite mitte, sabbadosaṃ pakāsati.</p>\r\n<p class="hangnum"><a name="para239"></a><span class="paranum">239</span>.</p>\r\n<p class="gatha1"> Mātā mittaṃ pitā ceti, sabhāvā taṃ tayaṃ hitaṃ;</p>\r\n<p class="gathalast">Kammakaraṇato caññe, bhavanti hitabuddhiyo.</p>\r\n<p class="hangnum"><a name="para240"></a><span class="paranum">240</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-239-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',143,replace(replace('<p class="gatha1">Āpadāsu <a name="M0.0143"></a> mittaṃ jaññā, yuddhe sūraṃ iṇe suciṃ;</p>\r\n<p class="gathalast">Bhariyaṃ khīṇesu vittesu, byasanesu ca bandhavaṃ.</p>\r\n<p class="hangnum"><a name="para241"></a><span class="paranum">241</span>.</p>\r\n<p class="gatha1"> Sakiṃ duṭṭhañca yo mittaṃ, puna sandhātumicchati;</p>\r\n<p class="gathalast">Sa maccumupagaṇhāti, gabbhaṃ assatarī yathā.</p>\r\n<p class="hangnum"><a name="para242"></a><span class="paranum">242</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-241-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',144,replace(replace('<p class="gatha1">Iṇaseso <a name="M0.0144"></a> aggiseso, rogaseso tatheva ca;</p>\r\n<p class="gathalast">Punappunaṃ vivaḍḍhanti, tasmā sesaṃ na kāraye.</p>\r\n<p class="hangnum"><a name="para243"></a><span class="paranum">243</span>.</p>\r\n<p class="gatha1"> Padumaṃva mukhaṃ yassa, vācā candanasītalā;</p>\r\n<p class="gathalast">Tādisaṃ no paseveyya, hadaye tu halāhalaṃ.</p>\r\n<p class="hangnum"><a name="para244"></a><span class="paranum">244</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-243-244-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',145,replace(replace('<p class="gatha1">Na <a name="M0.0145"></a> seve pharusaṃ sāmiṃ, na ca seveyya macchariṃ;</p>\r\n<p class="gathalast">Tato apaggaṇha sāmiṃ, neva niggahitaṃ tato.</p>\r\n<p class="hangnum"><a name="para245"></a><span class="paranum">245</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-245-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',146,replace(replace('<p class="gatha1">Kudesañca <a name="M0.0146"></a> kumittañca, kukulañca kubandhavaṃ;</p>\r\n<p class="gathalast">Kudārañca kudāsañca, dūrato parivajjaye.</p>\r\n<p class="hangnum"><a name="para246"></a><span class="paranum">246</span>.</p>\r\n<p class="gatha1"> Sītavāco bahumitto, pharuso appamittako;</p>\r\n<p class="gathalast">Upamā ettha ñātabbā, sūriyacandarājūnaṃ.</p>\r\n<p class="hangnum"><a name="para247"></a><span class="paranum">247</span>.</p>\r\n<p class="gatha1"> Ahitā paṭisedho ca, hitesu ca payojanaṃ;</p>\r\n<p class="gathalast">Byasane apariccāgo, itidaṃ mittalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para248"></a><span class="paranum">248</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-246-247-248-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',147,replace(replace('<p class="gatha1">Piyo <a name="M0.0147"></a> garu bhāvanīyo, vattā ca vacanakkhamo;</p>\r\n<p class="gatha2">Gambhīrañca kathaṃ kattā, no caṭṭhāne niyojako;</p>\r\n<p class="gathalast">Tādisaṃ mittaṃ seveyya, bhūtikāmo vicakkhaṇo.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',148,replace(replace('<p class="gatha1">Piyo <a name="M0.0148"></a> garu bhāva nīyo, vattā ca vacanakkhamo;</p>\r\n<p class="gathalast">Gambhirañca kathaṃ kattā, no caṭṭhāne niyojako.</p>\r\n<p class="gatha1">Yamhi etāni ṭhānāni, saṃvijjantidha puggale;</p>\r\n<p class="gatha2">So mitto mittakāmena, atthakāmānukampato;</p>\r\n<p class="gathalast">Api nāsiyamānena, bhajitabbo tathāvidho.</p>\r\n<p class="hangnum"><a name="para249"></a><span class="paranum">249</span>.</p>\r\n<p class="gatha1"> Orasaṃ katasambandhaṃ, tathā vaṃsakkamāgataṃ;</p>\r\n<p class="gathalast">Rakkhako byasanehi, mittaṃ ñeyyaṃ catubbidhaṃ.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> Piyo garu bhāvaniyo, vattā ca vacanakkhamo;</p>\r\n<p class="gatha2">Gambhirañca kathaṃ kattā, na caṭṭhāne niyojako;</p>\r\n<p class="gathalast">Taṃ mittaṃ mittakāmena, yāvajīvampi seviyaṃ.</p>\r\n<p class="gatha1">Piyo garu bhāvanīyo, vattā ca vacanakkhamo;</p>\r\n<p class="gatha2">Gambhirañca kathaṃ kattā, no caṭṭhāne niyojako;</p>\r\n<p class="gathalast">Tādisaṃ mittaṃ seveyya, bhūtikāmo vicakkhaṇo.</p>\r\n<p class="chapter">Rājakaṇḍa</p>\r\n<p class="hangnum"><a name="para251"></a><span class="paranum">251</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-249-250-251-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',150,replace(replace('<p class="gatha1">Mahājanaṃ <a name="M0.0150"></a> yo rañjeti, catūhipi vatthūhi vā;</p>\r\n<p class="gathalast">Rājāti vuccate loke, iti sallakkhaye vidvā.</p>\r\n<p class="hangnum"><a name="para252"></a><span class="paranum">252</span>.</p>\r\n<p class="gatha1"> Dānañca atthacariyā, piyavācā attasamaṃ;</p>\r\n<p class="gathalast">Saṅgahā caturo ime, munindena pakāsitā.</p>','\r',char(13)),'\n',char(10)),'-252-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',151,replace(replace('<p class="gatha1">Dānampi <a name="M0.0151"></a> atthacariyatañca, piyavāditañca samānattatañca;</p>\r\n<p class="gathalast">Kariyacariyasusaṅgahaṃ bahūnaṃ, anavamatena guṇena yāti saggaṃ;</p>\r\n<p class="gatha1">Dānañca peyyavajjañca, atthacariyā ca yā idha;</p>\r\n<p class="gatha2">Samānattatā ca dhammesu, tattha tattha yathārahaṃ;</p>\r\n<p class="gathalast">Ete kho saṅgahā loke, rathassāṇīva yāyato.</p>\r\n<p class="hangnum"><a name="para253"></a><span class="paranum">253</span>.</p>\r\n<p class="gatha1"> Dānaṃ sīlaṃ pariccāgaṃ, ajjavaṃ maddavaṃ tapaṃ;</p>\r\n<p class="gatha2">Akkodhaṃ avihiṃsañca, khantī ca avirodhanaṃ;</p>\r\n<p class="gathalast">Dasete dhamme rājāno, appamattena dhārayyuṃ.</p>\r\n<p class="hangnum"><a name="para254"></a><span class="paranum">254</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-253-254-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',152,replace(replace('<p class="gatha1">Ekayāmaṃ <a name="M0.0152"></a> saye rājā, dviyā maññeva paṇḍito;</p>\r\n<p class="gathalast">Gharāvāso tiyāmova, catuyāmo tu yācako.</p>\r\n<p class="hangnum"><a name="para255"></a><span class="paranum">255</span>.</p>\r\n<p class="gatha1"> Aputtakaṃ gharaṃ suññaṃ, raṭṭhaṃ suññaṃ arājakaṃ;</p>\r\n<p class="gathalast">Asippassa mukhaṃ suññaṃ, sabbasuññaṃ daliddattaṃ.</p>\r\n<p class="hangnum"><a name="para256"></a><span class="paranum">256</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-255-256-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',153,replace(replace('<p class="gatha1">Dhanamicche <a name="M0.0153"></a> vāṇijeyya, vijjamicche bhaje sutaṃ;</p>\r\n<p class="gathalast">Puttamicche taruṇitthiṃ, rājāmaccaṃ icchāgate.</p>\r\n<p class="hangnum"><a name="para257"></a><span class="paranum">257</span>.</p>\r\n<p class="gatha1"> Pakkhīnaṃ balamākāso, macchānamudakaṃ balaṃ;</p>\r\n<p class="gathalast">Dubbalassa balaṃ rājā, kumārānaṃ rudaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para258"></a><span class="paranum">258</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-257-258-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',154,replace(replace('<p class="gatha1">Khamā <a name="M0.0154"></a> jāgariyuṭṭhānaṃ, saṃvibhāgo dayikkhaṇā;</p>\r\n<p class="gathalast">Nāyakassa guṇā ete, icchitabbā sataṃ sadā.</p>\r\n<p class="hangnum"><a name="para259"></a><span class="paranum">259</span>.</p>\r\n<p class="gatha1"> Sakiṃ vadanti rājāno, sakiṃ samaṇabrāhmaṇā;</p>\r\n<p class="gathalast">Sakiṃ sappurisā loke, esa dhammo sanantano.</p>\r\n<p class="hangnum"><a name="para260"></a><span class="paranum">260</span>.</p>\r\n<p class="gatha1"> Alaso gīhi kāmabhogī na sādhu,</p>\r\n<p class="gatha2">Asaññato pabbajito na sādhu;</p>\r\n<p class="gatha3">Rājā na sādhu anisammakārī,</p>\r\n<p class="gathalast">Yo paṇḍito kodhano taṃ na sādhu.</p>\r\n<p class="hangnum"><a name="para261"></a><span class="paranum">261</span>.</p>\r\n<p class="gatha1"> Āyaṃ khayaṃ sayaṃ jaññā, katākataṃ sayaṃ jaññā;</p>\r\n<p class="gathalast">Niggahe niggahārahaṃ, paggahe paggahārahaṃ.</p>\r\n<p class="hangnum"><a name="para262"></a><span class="paranum">262</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-259-260-261-262-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',156,replace(replace('<p class="gatha1">Mātā <a name="M0.0156"></a> puttakataṃ pāpaṃ, sissakataṃ guru tathā;</p>\r\n<p class="gathalast">Rājā raṭṭhakataṃ pāpaṃ, rājakataṃ purohito.</p>\r\n<p class="hangnum"><a name="para263"></a><span class="paranum">263</span>.</p>\r\n<p class="gatha1"> Akkodhena jine kodhaṃ, asādhuṃ sādhunā jine;</p>\r\n<p class="gathalast">Jine kadariyaṃ dānena, saccenālikavādinaṃ.</p>\r\n<p class="hangnum"><a name="para264"></a><span class="paranum">264</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-263-264-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',157,replace(replace('<p class="gatha1">Adantadamanaṃ <a name="M0.0157"></a> dānaṃ, dānaṃ sabbatthasādhakaṃ;</p>\r\n<p class="gathalast">Dānena piyavācāya, unnamanti namanti ca.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',158,replace(replace('<p class="gatha1">Adantadamanaṃ <a name="M0.0158"></a> dānaṃ, adānaṃ dantadūsakaṃ;</p>\r\n<p class="gathalast">Dānena piyavācāya, unnamanti namanti ca.</p>\r\n<p class="hangnum"><a name="para265"></a><span class="paranum">265</span>.</p>\r\n<p class="gatha1"> Dānaṃ sinehabhesajjaṃ, maccheraṃ dosanosadhaṃ;</p>\r\n<p class="gathalast">Dānaṃ yasassībhesajjaṃ, maccheraṃ kapaṇosadhaṃ.</p>\r\n<p class="hangnum"><a name="para266"></a><span class="paranum">266</span>.</p>\r\n<p class="gatha1"> Na raññā samakaṃ bhuñje, kāmabhogaṃ kudācanaṃ;</p>\r\n<p class="gatha2">Ākappaṃ rasabhuttaṃ vā, mālāgandhavilepanaṃ;</p>\r\n<p class="gathalast">Vatthasabbamalaṅkāraṃ, na raññā sadisaṃ kare.</p>\r\n<p class="hangnum"><a name="para267"></a><span class="paranum">267</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-265-266-267-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',159,replace(replace('<p class="gatha1">Na <a name="M0.0159"></a> me rājā sakhā hoti, na rājā hoti samako;</p>\r\n<p class="gathalast">Eso sāmiko mayhanti, citte niṭṭhaṃ saṇṭhāpaye.</p>\r\n<p class="hangnum"><a name="para268"></a><span class="paranum">268</span>.</p>\r\n<p class="gatha1"> Nātidūre bhaje rañño, naccāsanne pavātake;</p>\r\n<p class="gatha2">Ujuke nātininne ca, na bhaje uccamāsane;</p>\r\n<p class="gathalast">Cha dose vajje sevako, aggīva saṃyato tiṭṭhe.</p>','\r',char(13)),'\n',char(10)),'-268-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',160,replace(replace('<p class="gatha1">Na <a name="M0.0160"></a> pacchato na purato, nāpi āsannadūrato;</p>\r\n<p class="gatha2">Na kacche nopi paṭivāte, na cāpi oṇatuṇṇate;</p>\r\n<p class="gathalast">Ime dose vivajjetvā, ekamantaṃ ṭhitā ahu –</p>\r\n<p class="hangnum"><a name="para269"></a><span class="paranum">269</span>.</p>\r\n<p class="gatha1"> Jappena mantena subhāsitena,</p>\r\n<p class="gatha2">Anuppadānena paveṇiyā vā;</p>\r\n<p class="gatha3">Yathā yathā yattha labhetha atthaṃ,</p>\r\n<p class="gathalast">Tathā tathā tattha parakkameyya.</p>\r\n<p class="hangnum"><a name="para270"></a><span class="paranum">270</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-269-270-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',161,replace(replace('<p class="gatha1">Kassako <a name="M0.0161"></a> vāṇijo macco, samaṇo sutasīla vā;</p>\r\n<p class="gathalast">Tesu vipulajātesu, raṭṭhampi vipulaṃ siyā.</p>\r\n<p class="hangnum"><a name="para271"></a><span class="paranum">271</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-271-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',162,replace(replace('<p class="gatha1">Tesu <a name="M0.0162"></a> dubbalajātesu, raṭṭhampi dubbalaṃ siyā;</p>\r\n<p class="gathalast">Tasmā saraṭṭhaṃ vipulaṃ, dhāraye raṭṭhabhāravā.</p>\r\n<p class="hangnum"><a name="para272"></a><span class="paranum">272</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-272-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',163,replace(replace('<p class="gatha1">Mahārukkhassa <a name="M0.0163"></a> phalino, āmaṃ chindati yo phalaṃ;</p>\r\n<p class="gathalast">Rasañcassa na jānāti, bījañcassa vinassati.</p>\r\n<p class="hangnum"><a name="para273"></a><span class="paranum">273</span>.</p>\r\n<p class="gatha1"> Mahārukkhūpamaṃ raṭṭhaṃ, adhammena pasāsati;</p>\r\n<p class="gathalast">Rasañcassa na jānāti, raṭṭhañcassa vinassati.</p>\r\n<p class="hangnum"><a name="para274"></a><span class="paranum">274</span>.</p>\r\n<p class="gatha1"> Mahārukkhassa phalino, pakkaṃ chindati yo phalaṃ;</p>\r\n<p class="gathalast">Rasañcassa vijānāti, bījañjassa na nassati.</p>\r\n<p class="hangnum"><a name="para275"></a><span class="paranum">275</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-273-274-275-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',164,replace(replace('<p class="gatha1">Mahārukkhūpamaṃ <a name="M0.0164"></a> raṭṭhaṃ, dhammena yo pasāsati;</p>\r\n<p class="gathalast">Rasañcassa vijānāti, raṭṭhañcassa na nassati.</p>\r\n<p class="chapter">Nāyaka kaṇḍa</p>\r\n<p class="hangnum"><a name="para276"></a><span class="paranum">276</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-276-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',165,replace(replace('<p class="gatha1">Anāyakā <a name="M0.0165"></a> vinassanti, nassanti bahunāyakā;</p>\r\n<p class="gathalast">Thīnāyakā vinassanti, nassanti susunāyakā.</p>\r\n<p class="hangnum"><a name="para277"></a><span class="paranum">277</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-277-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',166,replace(replace('<p class="gatha1">Gavaṃ <a name="M0.0166"></a> ce taramānānaṃ, ujuṃ gacchati puṅgavo;</p>\r\n<p class="gathalast">Sabbā gāvī ujuṃ yanti, nette ujuṃ gate sati.</p>\r\n<p class="hangnum"><a name="para278"></a><span class="paranum">278</span>.</p>\r\n<p class="gatha1"> Evameva manussesu, yo hoti seṭṭhasammato;</p>\r\n<p class="gatha2">So sace dhammaṃ carati, pageva itarā pajā.</p>\r\n<p class="gatha1">Gavaṃ ce taramānānaṃ, ujuṃ gacchati puṅgavo;</p>\r\n<p class="gathalast">Sabbā gāvī ujuṃyanti, nette ujuṃ gate sati.</p>','\r',char(13)),'\n',char(10)),'-278-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',167,replace(replace('<p class="gatha1">Evameva <a name="M0.0167"></a> manussesu, yo hoti seṭṭhasammato;</p>\r\n<p class="gatha2">So sace dhammaṃ carati, pageva itarā pajā;</p>\r\n<p class="gathalast">Sabbaṃ raṭṭhaṃ sukhaṃ seti, rājā ce hoti dhammiko.</p>\r\n<p class="hangnum"><a name="para279"></a><span class="paranum">279</span>.</p>\r\n<p class="gatha1"> Nodayāha vināsāya, bahunāyakatā bhusaṃ;</p>\r\n<p class="gathalast">Milāyanti vinassanti, padmānyakkehi sattahi.</p>\r\n<p class="hangnum"><a name="para280"></a><span class="paranum">280</span>.</p>\r\n<p class="gatha1"> Sutārakkho abhiyogo, kulārakkho vattaṃ bhave;</p>\r\n<p class="gathalast">Vijjā hi kulaputtassa, nāyakassāpamādako.</p>\r\n<p class="chapter">Puttakaṇḍa</p>\r\n<p class="hangnum"><a name="para281"></a><span class="paranum">281</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-279-280-281-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',168,replace(replace('<p class="gatha1">Abhijātaṃ <a name="M0.0168"></a> anujātaṃ, puttamicchanti paṇḍitā;</p>\r\n<p class="gathalast">Avajātaṃ na icchanti, yo hoti kulagandhano.</p>\r\n<p class="hangnum"><a name="para282"></a><span class="paranum">282</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-282-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',170,replace(replace('<p class="gatha1">Pañcaṭṭhānāni <a name="M0.0170"></a> sampassaṃ, puttamicchanti paṇḍitā;</p>\r\n<p class="gathalast">Bhato vā no bharissati, kiccaṃ vā no karissati.</p>\r\n<p class="hangnum"><a name="para283"></a><span class="paranum">283</span>.</p>\r\n<p class="gatha1"> Kulavaṃso ciraṃ tiṭṭhe, dāyajjaṃ paṭipajjati;</p>\r\n<p class="gathalast">Atha vā pana petānaṃ, dakkhinaṃ anupadassati.</p>\r\n<p class="hangnum"><a name="para284"></a><span class="paranum">284</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-283-284-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',171,replace(replace('<p class="gatha1">Bahuputte <a name="M0.0171"></a> pitā eko, avassaṃ poseti sadā;</p>\r\n<p class="gathalast">Bahuputtā na sakkonti, posetuṃ pitarekakaṃ.</p>\r\n<p class="hangnum"><a name="para285"></a><span class="paranum">285</span>.</p>\r\n<p class="gatha1"> Puttaṃ vā bhātaraṃ duṭṭhu, anusāseyya no jahe;</p>\r\n<p class="gathalast">Kinnu chejjaṃ karaṃ pādaṃ, littaṃ asucinā siyā.</p>\r\n<p class="chapter">Vejjācariya kaṇḍa</p>\r\n<p class="hangnum"><a name="para286"></a><span class="paranum">286</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-285-286-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',173,replace(replace('<p class="gatha1">Āyubedakatābhyāso<a name="M0.0173"></a>, sabbesaṃ piyadassano;</p>\r\n<p class="gathalast">Ariyasīlaguṇopeto, esa vejjo vidhīyate.</p>\r\n<p class="hangnum"><a name="para287"></a><span class="paranum">287</span>.</p>\r\n<p class="gatha1"> Nānāganthajānanañca, sudiṭṭhakammasampadā;</p>\r\n<p class="gathalast">Dakkhatā hatthasīghatā, pasādasūrasattitā.</p>\r\n<p class="hangnum"><a name="para288"></a><span class="paranum">288</span>.</p>\r\n<p class="gatha1"> Sābhāvikataṅkhaṇika-ñāṇasubhāsitāpi ca;</p>\r\n<p class="gathalast">Ussāho dabbo sabbatā, vejjācerassa lakkhaṇaṃ;</p>\r\n<p class="hangnum"><a name="para289"></a><span class="paranum">289</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-287-288-289-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',174,replace(replace('<p class="gatha1">Kiliṭṭhavatthaṃ <a name="M0.0174"></a> kodho ca, atimānañca gammatā;</p>\r\n<p class="gathalast">Animantitagamanaṃ, ete pañca vivajjiyā.</p>\r\n<p class="hangnum"><a name="para290"></a><span class="paranum">290</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-290-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',175,replace(replace('<p class="gatha1">Diṭṭhakammatā <a name="M0.0175"></a> socañca, dakkhatā viditāgamo;</p>\r\n<p class="gathalast">Cattāro subhisakkassa, suguṇā viññunā matā.</p>\r\n<p class="hangnum"><a name="para291"></a><span class="paranum">291</span>.</p>\r\n<p class="gatha1"> Rujāya jayalakkhaṇaṃ, raso ca bhesajjampi ca;</p>\r\n<p class="gathalast">Tilakkhaṇabhedo ceva, viññeyyo bhisakkena ve.</p>\r\n<p class="chapter">Dāsaka kaṇḍa</p>\r\n<p class="hangnum"><a name="para292"></a><span class="paranum">292</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-291-292-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',176,replace(replace('<p class="gatha1">Antojāto <a name="M0.0176"></a> dhanakkīto, dāsabyopagato sayaṃ;</p>\r\n<p class="gathalast">Dāsākaramarānīto-ccevaṃ te catudhā siyuṃ.</p>\r\n<p class="hangnum"><a name="para293"></a><span class="paranum">293</span>.</p>\r\n<p class="gatha1"> Pubbuṭṭhā pacchānipātī, dinnassa ādāyīpi ca;</p>\r\n<p class="gathalast">Sukatakammakaro ca, kittivaṇṇaharopi ca.</p>\r\n<p class="hangnum"><a name="para294"></a><span class="paranum">294</span>.</p>\r\n<p class="gatha1"> Dāsā 0.0177 pañceva corayya-sakhāñātyattasādisā;</p>\r\n<p class="gathalast">Tathā viññūhi viññeyyā, mittadārā ca bandhavā.</p>\r\n<p class="chapter">Itthikaṇḍa</p>\r\n<p class="hangnum"><a name="para295"></a><span class="paranum">295</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-293-294-295-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',178,replace(replace('<p class="gatha1">Āsā <a name="M0.0178"></a> lokitthiyo nāma, velā tāsaṃ na vijjati;</p>\r\n<p class="gatha2">Sārattā ca pagabbhā ca, sikhī sabbaghaso yathā;</p>\r\n<p class="gathalast">Tasmā tāyo hitvāna, brūheyya vivekaṃ sudhī.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',179,replace(replace('<p class="gatha1">Āsā <a name="M0.0179"></a> lokitthiyo nāma, velā tāsaṃ na vijjati;</p>\r\n<p class="gatha2">Sāratthā ca pagabbhā ca, sikhī sabbaghaso yathā;</p>\r\n<p class="gathalast">Tā hitvā pabbajissāmi, vivekamanubrūhayaṃ.</p>\r\n<p class="hangnum"><a name="para296"></a><span class="paranum">296</span>.</p>\r\n<p class="gatha1"> Loke hi aṅganā nāma, kodhanā mittabhedikā;</p>\r\n<p class="gathalast">Pisukā akataññū ca dūrato parivajjaye.</p>\r\n<p class="hangnum"><a name="para297"></a><span class="paranum">297</span>.</p>\r\n<p class="gatha1"> Yathā nadī ca pantho ca, pānāgāraṃ sabhā papā;</p>\r\n<p class="gathalast">Evaṃ lokitthiyo nāma, nāsaṃ kujjhanti paṇḍitā.</p>\r\n<p class="hangnum"><a name="para298"></a><span class="paranum">298</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-296-297-298-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',180,replace(replace('<p class="gatha1">Sabbā <a name="M0.0180"></a> nadī vaṅkagatī, sabbe kaṭṭhamayā vanā;</p>\r\n<p class="gathalast">Sabbitthiyo kare pāpaṃ, labhamāne nivātake.</p>\r\n<p class="hangnum"><a name="para299"></a><span class="paranum">299</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-299-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',181,replace(replace('<p class="gatha1">Ghaṭakumbhasamā <a name="M0.0181"></a> nārī, tatthaṅgārasamo pumā;</p>\r\n<p class="gathalast">Tasmā ghatañca aggiñca, nekatra ṭhapaye budho.</p>\r\n<p class="hangnum"><a name="para300"></a><span class="paranum">300</span>.</p>\r\n<p class="gatha1"> Itthīnañca dhanaṃ rūpaṃ, purisānaṃ vijjā dhanaṃ;</p>\r\n<p class="gathalast">Bhikkhūnañca dhanaṃ sīlaṃ, rājānañca dhanaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para301"></a><span class="paranum">301</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-300-301-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',182,replace(replace('<p class="gatha1">Pañcāratyā <a name="M0.0182"></a> sugandhabbā, sattāratyā dhanuggahā;</p>\r\n<p class="gathalast">Ekamāsā subhariyā, aḍḍhamāsā sissā malā.</p>\r\n<p class="hangnum"><a name="para302"></a><span class="paranum">302</span>.</p>\r\n<p class="gatha1"> Jiṇṇe annaṃ pasaṃseyya, dārañca gatayobbane;</p>\r\n<p class="gathalast">Raṇapunāgate sūraṃ, sassañca gehamāgate.</p>\r\n<p class="hangnum"><a name="para303"></a><span class="paranum">303</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-302-303-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',183,replace(replace('<p class="gatha1">Dvitipati <a name="M0.0183"></a> nārī ceva, vihāradviti bhikkhu ca;</p>\r\n<p class="gathalast">Sakuṇo dvitipāto ca, katamāyābahutarā.</p>\r\n<p class="hangnum"><a name="para304"></a><span class="paranum">304</span>.</p>\r\n<p class="gatha1"> Ratti vinā na candimā, vīcivinā ca sāgaro;</p>\r\n<p class="gathalast">Haṃsavinā pokkharaṇī, pativinā kaññā sobhe.</p>\r\n<p class="hangnum"><a name="para305"></a><span class="paranum">305</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-304-305-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',184,replace(replace('<p class="gatha1">Asantuṭṭhā <a name="M0.0184"></a> yatī naṭṭhā, santuṭṭhāpi ca patthi vā;</p>\r\n<p class="gathalast">Salajjā gaṇikā naṭṭhā, nillajjā ca kulitthiyo.</p>\r\n<p class="hangnum"><a name="para306"></a><span class="paranum">306</span>.</p>\r\n<p class="gatha1"> Corīnaṃ bahubuddhīnaṃ, yāsu saccaṃ sudullabhaṃ;</p>\r\n<p class="gathalast">Thīnaṃ bhāvo durājāno, macchassevo’dake gataṃ.</p>\r\n<p class="hangnum"><a name="para307"></a><span class="paranum">307</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-306-307-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',185,replace(replace('<p class="gatha1">Analā <a name="M0.0185"></a> mudusambhāsā, duppūrā tā nadīsamā;</p>\r\n<p class="gathalast">Sīdanti naṃ viditvāna, ārakā parivajjaye.</p>\r\n<p class="hangnum"><a name="para308"></a><span class="paranum">308</span>.</p>\r\n<p class="gatha1"> Āvaṭṭanī mahāmāyā, brahmacariyavikopanā;</p>\r\n<p class="gathalast">Sīdanti naṃ viditvāna, ārakā parivajjaye.</p>\r\n<p class="hangnum"><a name="para309"></a><span class="paranum">309</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-308-309-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',186,replace(replace('<p class="gatha1">Itthīpi <a name="M0.0186"></a> hi ekacciyā, seyyā posa janādhipa;</p>\r\n<p class="gathalast">Medhāvinī sīlavatī, sassudevā patibbatā;</p>\r\n<p class="hangnum"><a name="para310"></a><span class="paranum">310</span>.</p>\r\n<p class="gatha1"> Tassā yo jāyati poso,</p>\r\n<p class="gatha2">Sūro hosa disampati;</p>\r\n<p class="gatha3">Tādisā subhagiyā putto,</p>\r\n<p class="gathalast">Rajjampi anusāsati.</p>\r\n<p class="hangnum"><a name="para311"></a><span class="paranum">311</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-310-311-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',187,replace(replace('<p class="gatha1">Sallape <a name="M0.0187"></a> asihatthena, pisācenāpi sallape;</p>\r\n<p class="gatha2">Āsīvisampi āsīde, yena daṭṭho na jīvati;</p>\r\n<p class="gathalast">Na tveva eko ekāya, mātugāmena sallape.</p>\r\n<p class="hangnum"><a name="para312"></a><span class="paranum">312</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-312-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',188,replace(replace('<p class="gatha1">Na <a name="M0.0188"></a> hi sabbesu ṭhānesu, puriso hoti paṇḍito;</p>\r\n<p class="gathalast">Itthīpi paṇḍitā hoti, tattha tattha vicakkhaṇā.</p>\r\n<p class="hangnum"><a name="para313"></a><span class="paranum">313</span>.</p>\r\n<p class="gatha1"> Na hi sabbesu ṭhānesu, puriso hoti paṇḍito;</p>\r\n<p class="gathalast">Itthīpi paṇḍitā hoti, lahuṃ atthavicintikā.</p>\r\n<p class="hangnum"><a name="para314"></a><span class="paranum">314</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-313-314-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',189,replace(replace('<p class="gatha1">Kūpodakaṃ <a name="M0.0189"></a> vaṭacchāyā, sāmāthī iṭṭhakālayaṃ;</p>\r\n<p class="gathalast">Sītakāle bhave uṇhaṃ, uṇhakāle ca sītalaṃ.</p>\r\n<p class="hangnum"><a name="para315"></a><span class="paranum">315</span>.</p>\r\n<p class="gatha1"> Itthiyo ekacciyāpi, seyyā vuttāva muninā;</p>\r\n<p class="gathalast">Bhaṇḍānaṃ uttamā itthī, aggūpaṭṭhāyikā iti.</p>\r\n<p class="chapter">Pakiṇṇakakaṇḍa</p>\r\n<p class="hangnum"><a name="para316"></a><span class="paranum">316</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-315-316-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',191,replace(replace('<p class="gatha1">Kulasīlaguṇopeto<a name="M0.0191"></a>, sabbadhammaparāyaṇo;</p>\r\n<p class="gathalast">Pavīṇo pesanājjhakkho, dhammajjhakkho vidhīyate.</p>\r\n<p class="hangnum"><a name="para317"></a><span class="paranum">317</span>.</p>\r\n<p class="gatha1"> Vedavedaṅgatatvañño, jappahomaparāyaṇo;</p>\r\n<p class="gathalast">Āsīvādavacoyutto, esa rājapurohito.</p>','\r',char(13)),'\n',char(10)),'-317-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',192,replace(replace('<p class="gatha1">Kappo <a name="M0.0192"></a> byākaraṇaṃ joti – satthaṃ sikkhā nirutti ca;</p>\r\n<p class="gathalast">Chandoviciti cetāni, vedaṅgāni vadanti cha.</p>\r\n<p class="hangnum"><a name="para318"></a><span class="paranum">318</span>.</p>\r\n<p class="gatha1"> Sakiṃ vuttagahitatto, lahuhattho jitakkharo;</p>\r\n<p class="gathalast">Sabbasatthasamālokī, pakaṭṭho nāma lekhako.</p>\r\n<p class="hangnum"><a name="para319"></a><span class="paranum">319</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-318-319-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',193,replace(replace('<p class="gatha1">Samattanītisatthañño<a name="M0.0193"></a>, vāhane pūritassamo;</p>\r\n<p class="gathalast">Sūravīraguṇopeto, senājhakkho vidhīyate.</p>\r\n<p class="hangnum"><a name="para320"></a><span class="paranum">320</span>.</p>\r\n<p class="gatha1"> Sudhī vākyapaṭu, pañño, paracittopalakkhaṇo;</p>\r\n<p class="gathalast">Dhīro yathātthavādī ca, esa dūto vidhīyate.</p>\r\n<p class="hangnum"><a name="para321"></a><span class="paranum">321</span>.</p>\r\n<p class="gatha1"> Puttanattaguṇopeto, satthañño rasapācako;</p>\r\n<p class="gathalast">Sūro ca kathino ceva, sūpakāro sa vuccate.</p>\r\n<p class="hangnum"><a name="para322"></a><span class="paranum">322</span>.</p>\r\n<p class="gatha1"> Iṅgitākāratattañño, balavāpiyadassano;</p>\r\n<p class="gathalast">Appamādī sadā dakkho, patīhāro sa uccate.</p>\r\n<p class="hangnum"><a name="para323"></a><span class="paranum">323</span>.</p>\r\n<p class="gatha1"> Itthimisse kuto sīlaṃ, maṃsabhakkhe kuto dayā;</p>\r\n<p class="gatha2">Surāpāne kuto saccaṃ, mahālobhe kuto lajjā;</p>\r\n<p class="gathalast">Mahātande kuto sippaṃ, mahākodhe kuto dhanaṃ.</p>\r\n<p class="hangnum"><a name="para324"></a><span class="paranum">324</span>.</p>\r\n<p class="gatha1"> Surāyogo velālo ca, samajjacaraṇaṅgato;</p>\r\n<p class="gathalast">Khiḍḍā dhutto pāpamitto, alaso bhoganāsakā.</p>\r\n<p class="hangnum"><a name="para325"></a><span class="paranum">325</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-320-321-322-323-324-325-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',196,replace(replace('<p class="gatha1">Jīvantāpi <a name="M0.0196"></a> matā pañca, byāsena parikittitā;</p>\r\n<p class="gathalast">Dukkhito byādhiti mūḷho, iṇavā niccasevako.</p>\r\n<p class="hangnum"><a name="para326"></a><span class="paranum">326</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-326-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',197,replace(replace('<p class="gatha1">Niddāluko <a name="M0.0197"></a> pamādo ca, sukhito rogavālaso;</p>\r\n<p class="gathalast">Kāmuko kammārāmo ca, sattete satthavajjitā.</p>\r\n<p class="hangnum"><a name="para327"></a><span class="paranum">327</span>.</p>\r\n<p class="gatha1"> Goṇāhi sabbagihīnaṃ, posakā bhogadāyako;</p>\r\n<p class="gathalast">Tasmā hi mātāpitūva, mānaye sakkareyya ca.</p>\r\n<p class="hangnum"><a name="para328"></a><span class="paranum">328</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-327-328-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',198,replace(replace('<p class="gatha1">Yathā <a name="M0.0198"></a> mātā pitā bhātā, aññevāpi ca ñātakā;</p>\r\n<p class="gathalast">Gāvo no paramā mittā, yāsu jāyanti osadhā.</p>\r\n<p class="hangnum"><a name="para329"></a><span class="paranum">329</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-329-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',199,replace(replace('<p class="gatha1">Annadā <a name="M0.0199"></a> baladā cetā, vaṇṇadā sukhadā tathā;</p>\r\n<p class="gathalast">Etamatthavasaṃ ñatvā, nāsu gāvo haniṃsu te.</p>\r\n<p class="hangnum"><a name="para330"></a><span class="paranum">330</span>.</p>\r\n<p class="gatha1"> Ye ca khādanti gomaṃsaṃ, mātumaṃsaṃva khādare;</p>\r\n<p class="gathalast">Matesu tesu gijjhānaṃ, dade sote ca vāhaye.</p>\r\n<p class="hangnum"><a name="para331"></a><span class="paranum">331</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-330-331-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',200,replace(replace('<p class="gatha1">Dviguṇo <a name="M0.0200"></a> thīnamāhāro, buddhicāpi catugguṇo;</p>\r\n<p class="gathalast">Chagguṇo hoti vāyāmo, kāmotvaṭṭhaguṇo bhave.</p>\r\n<p class="hangnum"><a name="para332"></a><span class="paranum">332</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-332-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',201,replace(replace('<p class="gatha1">Na <a name="M0.0201"></a> loke sobhate mūḷho, kevalattapasaṃsako;</p>\r\n<p class="gathalast">Api sampihite kūpe, katavijjo pakāsate.</p>\r\n<p class="hangnum"><a name="para333"></a><span class="paranum">333</span>.</p>\r\n<p class="gatha1"> Kosajjaṃ bhayato disvā, vīriyārambhañca khemato;</p>\r\n<p class="gathalast">Āraddhavīriyā hotha, esā buddhānusāsanī.</p>\r\n<p class="hangnum"><a name="para334"></a><span class="paranum">334</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-333-334-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',202,replace(replace('<p class="gatha1">Vivādaṃ <a name="M0.0202"></a> bhayato disvā, avivādañca khemato;</p>\r\n<p class="gathalast">Samaggā sakhilā hotha, esā buddhānusāsanī.</p>\r\n<p class="hangnum"><a name="para335"></a><span class="paranum">335</span>.</p>\r\n<p class="gatha1"> Pamādaṃ bhayato disvā, appamādañca khemato;</p>\r\n<p class="gathalast">Bhāvethaṭṭhaṅgikaṃ maggaṃ, esā buddhānusāsanī.</p>\r\n<p class="hangnum"><a name="para336"></a><span class="paranum">336</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-335-336-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',203,replace(replace('<p class="gatha1">Garahā <a name="M0.0203"></a> ca pasaṃsā ca, aniccā tāvakālikā;</p>\r\n<p class="gatha2">Appakācekadesāva, na tā ikkheyya paṇḍito;</p>\r\n<p class="gathalast">Dhammādhammaṃva ikkheyya, atthānatthaṃ hitāhitaṃ.</p>\r\n<p class="chapter">Kavidappaṇanīti</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',204,replace(replace('<p class="gatha1">Pakhukkūpuraseṭṭhassa<a name="M0.0204"></a>, pacchime āsi vissuto;</p>\r\n<p class="gathalast">Catugāvutadesamhī, kanarayagāmo susobhano.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',205,replace(replace('<p class="gatha1">Dvino <a name="M0.0205"></a> dviveka sākamhi, tamhi jātena jātiyā;</p>\r\n<p class="gathalast">Laṅkābhārataādīsu, vuṭṭhapubba sutesinā.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Visutārāma sīhānaṃ, sikkhitena tipeṭakaṃ;</p>\r\n<p class="gathalast">Santike navavassāni, saṃgītikiccakārinā.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Dakkhiṇārāma vāsīnaṃ, santikepi suviññunaṃ;</p>\r\n<p class="gathalast">Sikkhitena sattavīsa-vassitvāna yasassinā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',206,replace(replace('<p class="gatha1">Sundare <a name="M0.0206"></a> puraseṭṭhamhi, sundare visute subhe;</p>\r\n<p class="gathalast">Sundare jotipālamhi, vasatā gaṇavācinā.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Nissāya peṭake ceva, anekanīti potthake;</p>\r\n<p class="gathalast">Bahule ganthaseṭṭhepi, katoyaṃ vidhumānito.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-6-7-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',207,replace(replace('<p class="gatha1">Tiṭṭhataṃ <a name="M0.0207"></a> ayaṃ me gantho, susāro yāva sāsanaṃ;</p>\r\n<p class="gathalast">Tiṭṭhateva sutesīnaṃ, susāraṃ supakāsayaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Anena suvisiṭṭhena, puññenaññena kammunā;</p>\r\n<p class="gathalast">Manisibhigurūheva, gaccheyyaṃ amataṃ sivaṃ.</p>','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',210,replace(replace('<p class="bodytext">‘‘Aṅgārino <a name="M0.0210"></a> dāni dumā bhadante’’</p>\r\n<p class="gatha1">‘‘Yathāpi rammako māso, gimhānaṃ hoti brāhmaṇa;</p>\r\n<p class="gathalast">Ate‘va’ññehi māsehi, dumapumphehi sobhati’’.</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',211,replace(replace('<p class="gatha1">‘‘Vanappagumbe <a name="M0.0211"></a> yathaphussitagge,</p>\r\n<p class="gathalast">Gimhāna māse paṭhamasmiṃ gimhe’’ –</p>\r\n<p class="centered"> Namo tassa bhagavato arahato sambāsambuddhassa</p>','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',212,replace(replace('<p class="chapter">Paṇāma paṭiññā</p>\r\n<p class="gatha1">Vatthuttayaṃ <a name="M0.0212"></a> namasitvā, ācere kavipuṅgave;</p>\r\n<p class="gathalast">Kassaṃ dvādasamāsānaṃ, bandhaṃ tammāsavasikaṃ.</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',213,replace(replace('<p class="gatha1">Cittasammatamāso <a name="M0.0213"></a> hi, atevaññehi sobhati;</p>\r\n<p class="gathalast">Rammakamāso rammamāso, teneva vohāro bhavi.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> Tasmiṃ sucittamāsamhi, nāgadumā supupphare;</p>\r\n<p class="gathalast">Pupphanti asanadumā, vāyanti kānane hi ve.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Saṅkanta mahussavopi, tamhi māsamhi vattate;</p>\r\n<p class="gathalast">Gandhodakehi aññoññaṃ, siñcamānā sumodare.</p>','\r',char(13)),'\n',char(10)),'-2-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',214,replace(replace('<p class="gatha1">Yathāpi <a name="M0.0214"></a> rammako māso, gimhānaṃ hoti brāhmaṇa;</p>\r\n<p class="gathalast">Ate‘va’ññehi māsehi, dumapupphehi sobhati.</p>\r\n<p class="gatha1">Sambuddho cittamāsassa, kāḷapakkhe uposathe;</p>\r\n<p class="gatha2">Pātoyeva samādāya, pavaraṃ pattacīvaraṃ;</p>\r\n<p class="gathalast">Anukampāya nāgānaṃ, nāgadīpamupāgami.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> Vesākhavhayamāso tu, suvisiṭṭho supākaṭo;</p>\r\n<p class="gathalast">Lokagganāthaṃ paṭicca, santehi abhilakkhito.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-4-5-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',215,replace(replace('<p class="gatha1">Tamhi <a name="M0.0215"></a> vesākhamāsamhi, campakāpi supupphare;</p>\r\n<p class="gathalast">Bodhiṃ dakehi siñcitvā, sajjanā sampamodare.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> Vanesuva potakāpi, pakkhandanti disodisaṃ;</p>\r\n<p class="gathalast">Vikūjantā sabhāsāya, janasotarasāyanaṃ.</p>','\r',char(13)),'\n',char(10)),'-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',216,replace(replace('<p class="gatha1">Dutiye <a name="M0.0216"></a> divase bhatta-kāle ārocite jino;</p>\r\n<p class="gathalast">Ramme vesākhamāsamhi, puṇṇāmāyaṃ munissaro.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Jeṭṭhasammatamāsopi, sogatajanabbhantare;</p>\r\n<p class="gathalast">Vikhyāto lakkhañño ceva, jeṭṭhena saṃyuto hi ve.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> Tasmiṃhi jeṭṭhamāsamhi, sumanā vanamallikā;</p>\r\n<p class="gathalast">Pupphanti ca pavāyanti, sabbajanamanoharā.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Parikkhaṇāsusabhāpi, abhavi mranamāmaṇḍale;</p>\r\n<p class="gathalast">Khetale jeṭṭhajotipi, pajjali tasmiñhi ve.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> Āsāḷho nāma māsopi, atīva visiṭṭho bhavi;</p>\r\n<p class="gathalast">Paṭisandhiṃ gaṇhi buddho, tasmiñhi muni sudhī.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Nikkhamipi ca sambuddho, dhammacakkaṃ pavattayi;</p>\r\n<p class="gathalast">Upasampadakammampi, karonti tasmiṃpi hi.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> Punnāgadumā pupphanti, pavāyanti disodisaṃ;</p>\r\n<p class="gathalast">Ādicco tiṭṭhati tamhi, uttarāyānakoṭiyaṃ.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-9-10-11-12-13-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',219,replace(replace('<p class="gatha1">Sīhe <a name="M0.0219"></a> sāvaṇamāsamhi, salākadānamuttamaṃ;</p>\r\n<p class="gathalast">Denti sādhavo mānusā, saddahantā vatthuttayaṃ.</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> Pupphanti kaṭeruhāpi, tamhi sāvaṇamāsake;</p>\r\n<p class="gathalast">Khe savaṇanakkhattampi, atīva jotayī hi ve.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Vassabbhantarabhūte ca, samaṇā sugatorasā;</p>\r\n<p class="gathalast">Māse vācanauggaṇha-kammaṃkaṃsu sukhāsayā.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-14-15-16-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',221,replace(replace('<p class="gatha1">Kaññārāsisammatehi<a name="M0.0221"></a>, poṭṭhapādasumāsake;</p>\r\n<p class="gathalast">Nadīsu dakapūritā, kaṭapatthatasādisā.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> Nāvāmahāussavampi, karonti mānujā tadā;</p>\r\n<p class="gathalast">Kīḷanti sampamodanti, vijite nara nāriyo.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Kañcanayamadumāpi, vikasanti tadā hi ve;</p>\r\n<p class="gathalast">Megho thokaṃ thokaṃ himaṃ, vassati patatipi ca.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-17-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',222,replace(replace('<p class="gatha1">Vassike <a name="M0.0222"></a> assayujimhi, vikasanti anekadhā;</p>\r\n<p class="gathalast">Padumādidakajāni, pupphāni manuññāni ve.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Mahāpadīpapantīhi, sakalamranamābhūtale;</p>\r\n<p class="gathalast">Pūjenti lokagganāthaṃ, sādhavo sogatājanā.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> Tapodhanā vicaranti, vassaṃvuṭṭhā disodisaṃ;</p>\r\n<p class="gathalast">Sādhavo dānasoṇḍāva, sītāyanti sukhanti ca.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-22-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',223,replace(replace('<p class="gatha1">Kattikamāsaseṭṭhepi<a name="M0.0223"></a>, sampamodanti mānujā;</p>\r\n<p class="gathalast">Kosītakīpupphāni ca, vikasanti vāyanti ca.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> Kathinamahādānampi, dadanti sādhavo janā;</p>\r\n<p class="gathalast">Tadā candakiraṇopi, atīva pajjoto ahu.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Ahosi himapāto ca, uttaravāto pavāyati;</p>\r\n<p class="gathalast">Kattikajotichaṇopi, ahosi tasmiñhi ve.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-23-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',224,replace(replace('<p class="gatha1">Dhanurāsīmāgasira<a name="M0.0224"></a>, māse hemantasammate;</p>\r\n<p class="gathalast">Sattidharasupūjāvha, sabhāpi sampavattitā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Devasammatapupphāni, manuññarucirānipi;</p>\r\n<p class="gathalast">Pupphanti tamhi māsamhi, himapāto ahosi ca.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Vīhayo honti pakkā ca, khettesu mranamābhūtale;</p>\r\n<p class="gathalast">Migasiranakkhattampi, joteti ākāsaṅgaṇe.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',225,replace(replace('<p class="gatha1">Makāre <a name="M0.0225"></a> phussamāsepi, pupphanti pavāyanti ca;</p>\r\n<p class="gathalast">Sunīlavallipupphāni, janamanoharānipi.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',226,replace(replace('<p class="gatha1">Senābyūhampi <a name="M0.0226"></a> karonti, bhūpālā mranamāraṭṭhikā;</p>\r\n<p class="gathalast">Saparisā udikkhanti, hatthiassādiādayo.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> Tamhisī atisītalampi, dakkhiṇāyanakoṭiyaṃ;</p>\r\n<p class="gathalast">Aṭṭhāpuṇṇamadinamhi, sūriyo lokamānito.</p>\r\n<p class="gatha1">Bodhito navame māse, phussapuṇṇamiyaṃ jino;</p>\r\n<p class="gathalast">Laṃkādīpaṃ visodhetuṃ, laṅkādīpamupāgami.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-30-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',227,replace(replace('<p class="gatha1">Kumbhesu <a name="M0.0227"></a> māghamāsehi, tūladumā supupphare;</p>\r\n<p class="gathalast">Pumtālā madhurarasaṃ, mānujānaṃ dadanti ca.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Yāgumahāussavopi, pākaṭo mranamābhūtale;</p>\r\n<p class="gathalast">Avasesasu meghopi, thanayaṃ abhivassati.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> Naranārī manuññāni, padarāni paṇḍāni ca;</p>\r\n<p class="gathalast">Pucimandadumā nava-pattāni dhārentipi ca.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-32-33-34-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1001n',228,replace(replace('<p class="gatha1">Mine <a name="M0.0228"></a> phagguṇamāsamhi, surabhigandhikā subhā;</p>\r\n<p class="gathalast">Pupphanti vanamhi dumā, navapattehi sobhare.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> Dakkhiṇadesato tamhi, vāto pavāyati hi ve;</p>\r\n<p class="gathalast">Vāḷukapiṭṭhe vāluka-thūpe katvāna pūjayyuṃ.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Pathamagimha māsamhi, nānādumāti pupphare;</p>\r\n<p class="gathalast">Tena sabbampi vipinaṃ, vicittaṃ dassaniyañhi ve.</p>\r\n<p class="gatha1">Visute jotipālamhi, visutamhi niketane;</p>\r\n<p class="gathalast">Vasatā nekaganthānaṃ, lekhakena kato ayaṃ.</p>','\r',char(13)),'\n',char(10)),'-35-36-');