INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',1,replace(replace('<p class="centered"> Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="book">Mahārahanīti</p>\r\n<p class="chapter">Paṇḍitakathā</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-1-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',1,replace(replace('<p class="gatha1">Mahāraharahaṃ <a name="M0.0001"></a> sakya,</p>\r\n<p class="gatha2">Muniṃ nīvaraṇā taṇhā;</p>\r\n<p class="gatha3">Muttaṃ muttaṃ sudassanaṃ,</p>\r\n<p class="gathalast">Vande bodhivaraṃ varaṃ.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-2-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',2,replace(replace('<p class="gatha1">Muninā <a name="M0.0002"></a> munināgena,</p>\r\n<p class="gatha2">Duṭṭhapabbajjitā jitā;</p>\r\n<p class="gatha3">Yathā yathāasaddhamma-</p>\r\n<p class="gathalast">Puraṇā puraṇādayo.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> Tathā tathāgatovādā,</p>\r\n<p class="gatha2">Nugate lokanītiyaṃ;</p>\r\n<p class="gatha3">Katayogena vidūnā,</p>\r\n<p class="gathalast">Jeyyovānattha kārino.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-3-4-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',3,replace(replace('<p class="gatha1">Nitīdha <a name="M0.0003"></a> jantunaṃ sāro,</p>\r\n<p class="gatha2">Mittācariyāca pītarā;</p>\r\n<p class="gatha3">Nītimā subuddhi byatto,</p>\r\n<p class="gathalast">Sutavā atthadassimā.</p>\r\n<p class="hangnum"><a name="para5"></a><span class="paranum">5</span>.</p>\r\n<p class="gatha1"> Kaviheraññakā katvā,</p>\r\n<p class="gatha2">Suttattaṃ satthakañcanaṃ;</p>\r\n<p class="gatha3">Gajjapajjādyālaṅkāraṃ,</p>\r\n<p class="gathalast">Karontīdha manorammaṃ.</p>\r\n<p class="hangnum"><a name="para6"></a><span class="paranum">6</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-5-6-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',4,replace(replace('<p class="gatha1">Vutyaṃ <a name="M0.0004"></a> visadañāṇassa,</p>\r\n<p class="gatha2">Ñāto atthotarassa na;</p>\r\n<p class="gatha3">Surappabhāya ādāso,</p>\r\n<p class="gathalast">Chāyaṃ disse na mākare.</p>\r\n<p class="hangnum"><a name="para7"></a><span class="paranum">7</span>.</p>\r\n<p class="gatha1"> Mahātejopi tejoyaṃ,</p>\r\n<p class="gatha2">Mattikaṃ namudukaṃkaro;</p>\r\n<p class="gatha3">Āpo āpeti mudukaṃ,</p>\r\n<p class="gathalast">Sādhuvācāca kakkhaḷaṃ.</p>\r\n<p class="hangnum"><a name="para8"></a><span class="paranum">8</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-7-8-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',5,replace(replace('<p class="gatha1">Mudunāva <a name="M0.0005"></a> ripuṃ jeti,</p>\r\n<p class="gatha2">Mudunā jeti dāruṇaṃ;</p>\r\n<p class="gatha3">Nosiddhaṃ mudunā kiñci,</p>\r\n<p class="gathalast">Yatvato mudunā jaye.</p>\r\n<p class="hangnum"><a name="para9"></a><span class="paranum">9</span>.</p>\r\n<p class="gatha1"> Candanaṃ sītalaṃ loke,</p>\r\n<p class="gatha2">Tato candaṃva sītalaṃ;</p>\r\n<p class="gatha3">Candanacandasītamhā,</p>\r\n<p class="gathalast">Vākyaṃ sādhusubhāsitaṃ.</p>\r\n<p class="hangnum"><a name="para10"></a><span class="paranum">10</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-9-10-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',6,replace(replace('<p class="gatha1">Pattakalloditaṃ <a name="M0.0006"></a> appaṃ,</p>\r\n<p class="gatha2">Vākyaṃ subhāsitaṃ bhave;</p>\r\n<p class="gatha3">Khudditassa kadannaṃpi,</p>\r\n<p class="gathalast">Suttaṃ sāduraso siyā.</p>\r\n<p class="hangnum"><a name="para11"></a><span class="paranum">11</span>.</p>\r\n<p class="gatha1"> Satthakāpi bahūvācā,</p>\r\n<p class="gatha2">Nādarā bahubhāṇino;</p>\r\n<p class="gatha3">Sopakāramudāsīnaṃ,</p>\r\n<p class="gathalast">Nanudiṭṭhaṃ nadījalaṃ.</p>\r\n<p class="hangnum"><a name="para12"></a><span class="paranum">12</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-11-12-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',7,replace(replace('<p class="gatha1">Saṃsāravisarukkhassa<a name="M0.0007"></a>,</p>\r\n<p class="gatha2">Dvayamevāmataṃphalaṃ;</p>\r\n<p class="gatha3">Subhāsitassasārādo,</p>\r\n<p class="gathalast">Sādhūhirasamāgamo.</p>\r\n<p class="hangnum"><a name="para13"></a><span class="paranum">13</span>.</p>\r\n<p class="gatha1"> Pāsāṇachattaṃ garukaṃ,</p>\r\n<p class="gatha2">Tato devānacikkhanā;</p>\r\n<p class="gatha3">Tato vuḍḍhānamovādo,</p>\r\n<p class="gathalast">Tato buddhassa sāsanaṃ;</p>\r\n<p class="hangnum"><a name="para14"></a><span class="paranum">14</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-13-14-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',8,replace(replace('<p class="gatha1">Tulaṃ <a name="M0.0008"></a> sallahukaṃ loke,</p>\r\n<p class="gatha2">Tato capalajātiko;</p>\r\n<p class="gatha3">Tatonovādiko tato,</p>\r\n<p class="gathalast">Yati dhammepamādako.</p>\r\n<p class="hangnum"><a name="para15"></a><span class="paranum">15</span>.</p>\r\n<p class="gatha1"> Aha gacchati hāyantī,</p>\r\n<p class="gatha2">Sattānamidha jīvitaṃ;</p>\r\n<p class="gatha3">Tasmāhi māpamattatvaṃ,</p>\r\n<p class="gathalast">Gacchantu jinasāsane.</p>\r\n<p class="hangnum"><a name="para16"></a><span class="paranum">16</span>.</p>\r\n<p class="gatha1"> Paṇḍitassa pasaṃsāya,</p>\r\n<p class="gatha2">Daṇḍobālena dīyate;</p>\r\n<p class="gatha3">Paṇḍito paṇḍiteneva,</p>\r\n<p class="gathalast">Vaṇṇitova suvaṇṇito.</p>\r\n<p class="hangnum"><a name="para17"></a><span class="paranum">17</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-15-16-17-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',9,replace(replace('<p class="gatha1">Satesu <a name="M0.0009"></a> jāyate sūro,</p>\r\n<p class="gatha2">Sahassesuca paṇḍito;</p>\r\n<p class="gatha3">Vuttā satasahassesu,</p>\r\n<p class="gathalast">Dātā bhavativā navā.</p>\r\n<p class="hangnum"><a name="para18"></a><span class="paranum">18</span>.</p>\r\n<p class="gatha1"> Bhūpattaṃca paṇḍitattañca,</p>\r\n<p class="gatha2">Nevatulyaṃ kadācipi;</p>\r\n<p class="gatha3">Sadesepūjito rājā,</p>\r\n<p class="gathalast">Buddho sabbatthapūjito.</p>\r\n<p class="hangnum"><a name="para19"></a><span class="paranum">19</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-18-19-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',10,replace(replace('<p class="gatha1">Kvātibhāro <a name="M0.0010"></a> samatthānaṃ,</p>\r\n<p class="gatha2">Kiṃdūro byavahārinaṃ;</p>\r\n<p class="gatha3">Kovideso savijjānaṃ,</p>\r\n<p class="gathalast">Koparo piyavādinaṃ.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> Nanu te yevasantā no,</p>\r\n<p class="gatha2">Sāgarā nakulācalā;</p>\r\n<p class="gatha3">Appampi mariyādaṃ ye,</p>\r\n<p class="gathalast">Saṃvaṭṭevi jahantino.</p>\r\n<p class="hangnum"><a name="para21"></a><span class="paranum">21</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-21-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',11,replace(replace('<p class="gatha1">Palayetītamariyādā<a name="M0.0011"></a>,</p>\r\n<p class="gatha2">Bhavantikira sāgarā;</p>\r\n<p class="gatha3">Mariyādātikaṃ sādhu,</p>\r\n<p class="gathalast">Naicchanti palayepi.</p>\r\n<p class="hangnum"><a name="para22"></a><span class="paranum">22</span>.</p>\r\n<p class="gatha1"> Sataṃ dīghāyukaṃ sabba,</p>\r\n<p class="gatha2">Sattānaṃ sukhakāraṇaṃ;</p>\r\n<p class="gatha3">Asataṃpana sabbesaṃ,</p>\r\n<p class="gathalast">Dukkhahetu nasaṃsayo.</p>\r\n<p class="hangnum"><a name="para23"></a><span class="paranum">23</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-22-23-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',12,replace(replace('<p class="gatha1">Pāmokkhe <a name="M0.0012"></a> sujane sante,</p>\r\n<p class="gatha2">Sabbepi sujanā janā;</p>\r\n<p class="gatha3">Jātekasmiṃsāragandhe,</p>\r\n<p class="gathalast">Sabbe gandhamayā dumā.</p>\r\n<p class="hangnum"><a name="para24"></a><span class="paranum">24</span>.</p>\r\n<p class="gatha1"> Attanā yadi ekena,</p>\r\n<p class="gatha2">Vinatena mahājanā;</p>\r\n<p class="gatha3">Vinayaṃ yanti sabbepi,</p>\r\n<p class="gathalast">Ko taṃ nāseyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para25"></a><span class="paranum">25</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-24-25-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',13,replace(replace('<p class="gatha1">Sarīrassa <a name="M0.0013"></a> guṇānañca,</p>\r\n<p class="gatha2">Dūramaccanta mantaraṃ;</p>\r\n<p class="gatha3">Sarīraṃ khaṇaviddhaṃsi,</p>\r\n<p class="gathalast">Kappantiṭṭhāyino guṇā.</p>\r\n<p class="hangnum"><a name="para26"></a><span class="paranum">26</span>.</p>\r\n<p class="gatha1"> Yadi niccamaniccena,</p>\r\n<p class="gatha2">Nimalaṃ malavāhinā;</p>\r\n<p class="gatha3">Yo so kāyena labhetha,</p>\r\n<p class="gathalast">Kiṃnaladdhaṃ bhave nukiṃ.</p>\r\n<p class="hangnum"><a name="para27"></a><span class="paranum">27</span>.</p>\r\n<p class="gatha1"> Dhammatthakāmamokkhānaṃ,</p>\r\n<p class="gatha2">Pāṇaṃ saṃsiddhikāraṇaṃ;</p>\r\n<p class="gatha3">Tannighātā kinnahataṃ,</p>\r\n<p class="gathalast">Rakkhanā kinnarakkhitaṃ.</p>\r\n<p class="hangnum"><a name="para28"></a><span class="paranum">28</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-26-27-28-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',14,replace(replace('<p class="gatha1">Attā <a name="M0.0014"></a> bandhu manussassa,</p>\r\n<p class="gatha2">Ripu attāva jantunaṃ;</p>\r\n<p class="gatha3">Attāva niyato ñāti,</p>\r\n<p class="gathalast">Attāva niyato ripu.</p>\r\n<p class="hangnum"><a name="para29"></a><span class="paranum">29</span>.</p>\r\n<p class="gatha1"> Attanoparicāgena,</p>\r\n<p class="gatha2">Yaṃsitamanurakkhanaṃ;</p>\r\n<p class="gatha3">Karonti sajjanāyeva,</p>\r\n<p class="gathalast">Na taṃ nītimatā mataṃ.</p>\r\n<p class="hangnum"><a name="para30"></a><span class="paranum">30</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-29-30-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',15,replace(replace('<p class="gatha1">Tiṇāni <a name="M0.0015"></a> bhūmirudakaṃ,</p>\r\n<p class="gatha2">Catutthī vākyasuṭṭhutā;</p>\r\n<p class="gatha3">Etāni hi sataṃgehe,</p>\r\n<p class="gathalast">Nochindante kadācipi.</p>\r\n<p class="hangnum"><a name="para31"></a><span class="paranum">31</span>.</p>\r\n<p class="gatha1"> Ambuṃ pivantino najjo,</p>\r\n<p class="gatha2">Rukkho khādatino phalaṃ;</p>\r\n<p class="gatha3">Megho kvacipi no sassaṃ,</p>\r\n<p class="gathalast">Paratthāya sataṃdhanaṃ.</p>\r\n<p class="hangnum"><a name="para32"></a><span class="paranum">32</span>.</p>\r\n<p class="gatha1"> Sataṃ pharusavācāhi,</p>\r\n<p class="gatha2">Nayāti vikatiṃ mano;</p>\r\n<p class="gatha3">Tiṇukkāhi nasakkāva,</p>\r\n<p class="gathalast">Tāpetuṃ sāgare jalaṃ.</p>\r\n<p class="hangnum"><a name="para33"></a><span class="paranum">33</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-31-32-33-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',16,replace(replace('<p class="gatha1">Sampatyaṃ <a name="M0.0016"></a> mahattaṃceto,</p>\r\n<p class="gatha2">Bhavatyuppalakomalaṃ;</p>\r\n<p class="gatha3">Vippatyañca mahāsele,</p>\r\n<p class="gathalast">Silāsaṅghādakakkaso.</p>\r\n<p class="hangnum"><a name="para34"></a><span class="paranum">34</span>.</p>\r\n<p class="gatha1"> Atthaṃ mahantamāpajja,</p>\r\n<p class="gatha2">Vijjaṃ sampattimevaca;</p>\r\n<p class="gatha3">Careyyāmānathaddhoyo,</p>\r\n<p class="gathalast">Paṇḍito so pavuccati.</p>\r\n<p class="hangnum"><a name="para35"></a><span class="paranum">35</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',17,replace(replace('<p class="gatha1">Nālabbhamabhipatthenti<a name="M0.0017"></a>,</p>\r\n<p class="gatha2">Naṭṭhampi nacasocare;</p>\r\n<p class="gatha3">Vippatyañca namuyhanti,</p>\r\n<p class="gathalast">Yenarā teva paṇḍito.</p>\r\n<p class="hangnum"><a name="para36"></a><span class="paranum">36</span>.</p>\r\n<p class="gatha1"> Gaṇṭhiṭṭhāne ekapade,</p>\r\n<p class="gatha2">Nātimaññeyya paṇḍito;</p>\r\n<p class="gatha3">Kiṃ akko veḷupabbhāro,</p>\r\n<p class="gathalast">Timahādīpabhānudo.</p>\r\n<p class="hangnum"><a name="para37"></a><span class="paranum">37</span>.</p>\r\n<p class="gatha1"> Guṇadosesumekena,</p>\r\n<p class="gatha2">Natthi koci vivajjito;</p>\r\n<p class="gatha3">Sukhumālassa padumassa,</p>\r\n<p class="gathalast">Naḷā bhavati kakkasā.</p>\r\n<p class="hangnum"><a name="para38"></a><span class="paranum">38</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-36-37-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',18,replace(replace('<p class="gatha1">Sumahantāni <a name="M0.0018"></a> satthāni,</p>\r\n<p class="gatha2">Dhārayanto bahussutā;</p>\r\n<p class="gatha3">Chettāro saṃsayānantu,</p>\r\n<p class="gathalast">Kilante lobhamohatā.</p>\r\n<p class="hangnum"><a name="para39"></a><span class="paranum">39</span>.</p>\r\n<p class="gatha1"> Dosampi saguṇe disvā,</p>\r\n<p class="gatha2">Guṇavādī vadantino;</p>\r\n<p class="gatha3">Na loko vijjamānampi,</p>\r\n<p class="gathalast">Cande passati lañchanaṃ.</p>\r\n<p class="hangnum"><a name="para40"></a><span class="paranum">40</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-39-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',19,replace(replace('<p class="gatha1">Nipuṇe <a name="M0.0019"></a> sutameseyya,</p>\r\n<p class="gatha2">Vicinitvā sutatthiko;</p>\r\n<p class="gatha3">Bhattaṃ hukkhaliyaṃ pakkaṃ,</p>\r\n<p class="gathalast">Bhājanaṃpi tathā bhave.</p>\r\n<p class="hangnum"><a name="para41"></a><span class="paranum">41</span>.</p>\r\n<p class="gatha1"> Guṇā kubbanti dūtattaṃ,</p>\r\n<p class="gatha2">Dūrepi vasataṃ sataṃ;</p>\r\n<p class="gatha3">Ketake gandha ghāsāya,</p>\r\n<p class="gathalast">Gacchanti bhamarā sayaṃ.</p>\r\n<p class="hangnum"><a name="para42"></a><span class="paranum">42</span>.</p>\r\n<p class="gatha1"> Paṇḍito sutasampanno,</p>\r\n<p class="gatha2">Yatthaatthīti cesuto;</p>\r\n<p class="gatha3">Mahussāhena taṃṭhānaṃ,</p>\r\n<p class="gathalast">Gantabbaṃ sutaresinā.</p>\r\n<p class="hangnum"><a name="para43"></a><span class="paranum">43</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-41-42-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',20,replace(replace('<p class="gatha1">Sabbasutaṃ <a name="M0.0020"></a> madhīyetha,</p>\r\n<p class="gatha2">Hīnamukkaṭṭha majjhimaṃ;</p>\r\n<p class="gatha3">Sabbassaatthaṃ jāneyya,</p>\r\n<p class="gatha2">Naca sabbaṃ payojaye;</p>\r\n<p class="gatha3">Hoti tādisakokālo,</p>\r\n<p class="gathalast">Yattha atthāvahaṃ sutaṃ.</p>\r\n<p class="hangnum"><a name="para44"></a><span class="paranum">44</span>.</p>\r\n<p class="gatha1"> Ṭhito cirattanaṃ nīre,</p>\r\n<p class="gatha2">Sakkharo nantaraṃsuto;</p>\r\n<p class="gatha3">Dabbho nīrantike jāto,</p>\r\n<p class="gathalast">Sabbakālantaraṃsuto.</p>\r\n<p class="hangnum"><a name="para45"></a><span class="paranum">45</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-44-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',21,replace(replace('<p class="gatha1">Vasuṃ <a name="M0.0021"></a> gaṇhanti dūraṭṭhā,</p>\r\n<p class="gatha2">Pabbate ratanocite;</p>\r\n<p class="gatha3">Na milakkhā samīpaṭṭhā,</p>\r\n<p class="gathalast">Evaṃ bālā bahussute.</p>\r\n<p class="hangnum"><a name="para46"></a><span class="paranum">46</span>.</p>\r\n<p class="gatha1"> Hiraññena migānaṃva,</p>\r\n<p class="gatha2">Susīlaṃpi asīlino;</p>\r\n<p class="gatha3">Adhammikassa dhammena,</p>\r\n<p class="gathalast">Bālānaṃpi sutena kiṃ.</p>\r\n<p class="hangnum"><a name="para47"></a><span class="paranum">47</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-46-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',22,replace(replace('<p class="gatha1">Sutena <a name="M0.0022"></a> hadayaṭṭhena,</p>\r\n<p class="gatha2">Khalo nevasusīlavā;</p>\r\n<p class="gatha3">Madhunā koṭaraṭṭhena,</p>\r\n<p class="gathalast">Nimbo kiṃ madhurāyate.</p>\r\n<p class="hangnum"><a name="para48"></a><span class="paranum">48</span>.</p>\r\n<p class="gatha1"> Sakiṃpi viññu dhīrena,</p>\r\n<p class="gatha2">Karoti sahasaṅgamaṃ;</p>\r\n<p class="gatha3">Attatthañca paratthañca,</p>\r\n<p class="gathalast">Nibbānantaṃ sukhaṃ labhe.</p>\r\n<p class="hangnum"><a name="para49"></a><span class="paranum">49</span>.</p>\r\n<p class="gatha1"> Haṃso majjhe nakākānaṃ,</p>\r\n<p class="gatha2">Sīho gunnaṃ nasobhati;</p>\r\n<p class="gatha3">Gadrabhānaṃ turaṅgono,</p>\r\n<p class="gathalast">Bālānañca napaṇḍito.</p>\r\n<p class="hangnum"><a name="para50"></a><span class="paranum">50</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-48-49-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',23,replace(replace('<p class="gatha1">Ākiṇṇopi <a name="M0.0023"></a> asabbhīva,</p>\r\n<p class="gatha2">Asaṃsaṭṭhova bhaddako;</p>\r\n<p class="gatha3">Bahunā sannajātena,</p>\r\n<p class="gathalast">Gacche nummattakenidha.</p>\r\n<p class="hangnum"><a name="para51"></a><span class="paranum">51</span>.</p>\r\n<p class="gatha1"> Nadītire khatekūpe,</p>\r\n<p class="gatha2">Araṇītālavaṇṭake;</p>\r\n<p class="gatha3">Navade āpādi natthīti,</p>\r\n<p class="gathalast">Mukheca vacanaṃ tathā.</p>\r\n<p class="hangnum"><a name="para52"></a><span class="paranum">52</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-51-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',24,replace(replace('<p class="gatha1">Sutasanniccayā <a name="M0.0024"></a> dhīrā,</p>\r\n<p class="gatha2">Tuṇhibhūtā apucchitā;</p>\r\n<p class="gatha3">Puṇṇā subhāsitenāpi,</p>\r\n<p class="gathalast">Ghaṇḍādyaghaṭitāyathā.</p>\r\n<p class="hangnum"><a name="para53"></a><span class="paranum">53</span>.</p>\r\n<p class="gatha1"> Apuṭṭho paṇḍito bherī,</p>\r\n<p class="gatha2">Pajjunno hoti pucchito;</p>\r\n<p class="gatha3">Bālo puṭṭho apuṭṭhoca,</p>\r\n<p class="gathalast">Bahuṃ vikattate sadā.</p>\r\n<p class="hangnum"><a name="para54"></a><span class="paranum">54</span>.</p>\r\n<p class="gatha1"> Sampannaguṇā laṅkāro,</p>\r\n<p class="gatha2">Sabbasatta hitāvaho;</p>\r\n<p class="gatha3">Nacareyya parattatthaṃ,</p>\r\n<p class="gathalast">Kuto so paṇḍito bhave.</p>\r\n<p class="hangnum"><a name="para55"></a><span class="paranum">55</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-53-54-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',25,replace(replace('<p class="gatha1">Saparatthaṃ <a name="M0.0025"></a> care dhīro,</p>\r\n<p class="gatha2">Asakkonto sakaṃ care;</p>\r\n<p class="gatha3">Tathāpica asakkonto,</p>\r\n<p class="gathalast">Pāpāttānaṃ viyojaye.</p>\r\n<p class="hangnum"><a name="para56"></a><span class="paranum">56</span>.</p>\r\n<p class="gatha1"> Bālecummattake bhūpe,</p>\r\n<p class="gatha2">Gurumātāpitūsvapi;</p>\r\n<p class="gatha3">Nadosaṃ kariyā pañño,</p>\r\n<p class="gathalast">Saṅgheca jeṭṭhabhātari.</p>\r\n<p class="hangnum"><a name="para57"></a><span class="paranum">57</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-56-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',26,replace(replace('<p class="gatha1">Atthanāsaṃ <a name="M0.0026"></a> manotāpaṃ,</p>\r\n<p class="gatha2">Ghareduccaritānica;</p>\r\n<p class="gatha3">Vañcanaṃ avamānañca,</p>\r\n<p class="gathalast">Matimā napakāsaye.</p>\r\n<p class="hangnum"><a name="para58"></a><span class="paranum">58</span>.</p>\r\n<p class="gatha1"> Paradānaṃ janettīva,</p>\r\n<p class="gatha2">Leṭṭuva paradabbakaṃ;</p>\r\n<p class="gatha3">Attāva sabbabhūtāni,</p>\r\n<p class="gathalast">Yo passati so passati.</p>\r\n<p class="hangnum"><a name="para59"></a><span class="paranum">59</span>.</p>\r\n<p class="gatha1"> Parūpavāde padhiro,</p>\r\n<p class="gatha2">Paravajjesvalocano;</p>\r\n<p class="gatha3">Paṅgulo aññanārīsu,</p>\r\n<p class="gathalast">Dussakappesvacetano.</p>\r\n<p class="hangnum"><a name="para60"></a><span class="paranum">60</span>.</p>\r\n<p class="gatha1"> Sabbaṃ suṇāti sotena,</p>\r\n<p class="gatha2">Sabbaṃ passati cakkhunā;</p>\r\n<p class="gatha3">Nacadiṭṭhaṃ sutaṃ dhīro,</p>\r\n<p class="gathalast">Sabbaṃ uccitumarahati.</p>\r\n<p class="hangnum"><a name="para61"></a><span class="paranum">61</span>.</p>\r\n<p class="gatha1"> Cakkhumassa yathāandho,</p>\r\n<p class="gatha2">Sotavā padhiroyathā;</p>\r\n<p class="gatha3">Paññāvāssa yathāmūgo,</p>\r\n<p class="gatha2">Balavā dubbaloriva;</p>\r\n<p class="gatha3">Atha atthesamuppanne,</p>\r\n<p class="gathalast">Sayetha matasāyitaṃ.</p>\r\n<p class="hangnum"><a name="para62"></a><span class="paranum">62</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-58-59-60-61-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',28,replace(replace('<p class="gatha1">Pāpamitte <a name="M0.0028"></a> vivajjetvā,</p>\r\n<p class="gatha2">Bhajeyyu ttamapuggalaṃ;</p>\r\n<p class="gatha3">Ovādecassa tiṭṭheyya,</p>\r\n<p class="gathalast">Patthento acalaṃsukhaṃ.</p>\r\n<p class="hangnum"><a name="para63"></a><span class="paranum">63</span>.</p>\r\n<p class="gatha1"> Sususā sutavaḍḍhanaṃ,</p>\r\n<p class="gatha2">Sutaṃ paññāyavaḍḍhanaṃ;</p>\r\n<p class="gatha3">Paññāya atthaṃ jānāti,</p>\r\n<p class="gathalast">Ñāto attho sukhāvaho.</p>\r\n<p class="hangnum"><a name="para64"></a><span class="paranum">64</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-63-64-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',29,replace(replace('<p class="gatha1">Pāmojja <a name="M0.0029"></a> karaṇaṃ ṭhānaṃ,</p>\r\n<p class="gatha2">Pasaṃsāvahanaṃsukhaṃ;</p>\r\n<p class="gatha3">Phalānisaṃso bhāveti,</p>\r\n<p class="gathalast">Vahanto porissaṃdhuraṃ.</p>\r\n<p class="hangnum"><a name="para65"></a><span class="paranum">65</span>.</p>\r\n<p class="gatha1"> Atisītaṃ atiuṇhaṃ,</p>\r\n<p class="gatha2">Atisāya midaṃahu;</p>\r\n<p class="gatha3">Iti visaṭṭha kammante,</p>\r\n<p class="gathalast">Khaṇā accanti māṇave.</p>\r\n<p class="hangnum"><a name="para66"></a><span class="paranum">66</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-65-66-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',30,replace(replace('<p class="gatha1">Yoca <a name="M0.0030"></a> sītañca uṇhañca,</p>\r\n<p class="gatha2">Tiṇātiyo namaññati;</p>\r\n<p class="gatha3">Karaṃ purisa kiccāni,</p>\r\n<p class="gathalast">So sukhā navihāyati.</p>\r\n<p class="hangnum"><a name="para67"></a><span class="paranum">67</span>.</p>\r\n<p class="gatha1"> Yasalābhaṃ jigīsantaṃ,</p>\r\n<p class="gatha2">Naraṃ vajjenti dūrato;</p>\r\n<p class="gatha3">Apatthetvāna te tasmā,</p>\r\n<p class="gathalast">Taṃmaggaṃ maggaye budho.</p>\r\n<p class="hangnum"><a name="para68"></a><span class="paranum">68</span>.</p>\r\n<p class="gatha1"> Desamosajja gacchanti,</p>\r\n<p class="gatha2">Sīhā sappurisā gajā;</p>\r\n<p class="gatha3">Tattheva nidhanaṃ yanti,</p>\r\n<p class="gathalast">Kākā kāpurisā migā.</p>\r\n<p class="hangnum"><a name="para69"></a><span class="paranum">69</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-67-68-69-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',31,replace(replace('<p class="gatha1">Yasmiṃdese <a name="M0.0031"></a> nasammāro,</p>\r\n<p class="gatha2">Napiti nacabandhavā;</p>\r\n<p class="gatha3">Nacavijjāgamo koci,</p>\r\n<p class="gathalast">Natattha vasatī vasse.</p>\r\n<p class="hangnum"><a name="para70"></a><span class="paranum">70</span>.</p>\r\n<p class="gatha1"> Dhanavā gaṇako rājā,</p>\r\n<p class="gatha2">Nadī vejjā imejanā;</p>\r\n<p class="gatha3">Yattha pañca navijjante,</p>\r\n<p class="gathalast">Na tatthavasatī vase.</p>\r\n<p class="hangnum"><a name="para71"></a><span class="paranum">71</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-70-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',32,replace(replace('<p class="gatha1">Asanaṃ <a name="M0.0032"></a> bhayamantānaṃ,</p>\r\n<p class="gatha2">Maccānaṃ maraṇaṃ bhayaṃ;</p>\r\n<p class="gatha3">Uttamānantusabbesaṃ,</p>\r\n<p class="gathalast">Avamānaṃ paraṃ bhayaṃ.</p>\r\n<p class="hangnum"><a name="para72"></a><span class="paranum">72</span>.</p>\r\n<p class="gatha1"> Amānanā yattha siyā,</p>\r\n<p class="gatha2">Santānaṃ avamānanā;</p>\r\n<p class="gatha3">Hīnasamānanāvāpi,</p>\r\n<p class="gathalast">Na tatthavasati vase.</p>\r\n<p class="hangnum"><a name="para73"></a><span class="paranum">73</span>.</p>\r\n<p class="gatha1"> Yatthālasoca dakkhoca,</p>\r\n<p class="gatha2">Sūro bhīruca pūjito;</p>\r\n<p class="gatha3">Na santā tattha vassanti,</p>\r\n<p class="gathalast">Avisesakarena ko.</p>\r\n<p class="hangnum"><a name="para74"></a><span class="paranum">74</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-72-73-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',33,replace(replace('<p class="gatha1">Dukkho <a name="M0.0033"></a> nivāso sambādhe,</p>\r\n<p class="gatha2">Ṭhāne asucisaṅkaṭe;</p>\r\n<p class="gatha3">Tato rimhipiyānante,</p>\r\n<p class="gathalast">Tatoca akataññunaṃ.</p>\r\n<p class="hangnum"><a name="para75"></a><span class="paranum">75</span>.</p>\r\n<p class="gatha1"> Siṅgīnaṃ pañcahatthena,</p>\r\n<p class="gatha2">Satena vājinaṃ caje;</p>\r\n<p class="gatha3">Hatthīnantu sahassena,</p>\r\n<p class="gathalast">Desacāgena dujjane.</p>\r\n<p class="hangnum"><a name="para76"></a><span class="paranum">76</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-75-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',34,replace(replace('<p class="gatha1">Caje <a name="M0.0034"></a> ekaṃ kulassatthaṃ,</p>\r\n<p class="gatha2">Gāmassatthaṃ kulaṃ caje;</p>\r\n<p class="gatha3">Gāmaṃ janapadassatthaṃ;</p>\r\n<p class="gathalast">Attatthaṃ pathavī caje.</p>\r\n<p class="hangnum"><a name="para77"></a><span class="paranum">77</span>.</p>\r\n<p class="gatha1"> Calatyekenapādena,</p>\r\n<p class="gatha2">Tiṭṭhatyekena paṇḍito;</p>\r\n<p class="gatha3">Asamikhyaparaṃṭhānaṃ,</p>\r\n<p class="gathalast">Pubbamāyanaṃnacaje.</p>\r\n<p class="hangnum"><a name="para78"></a><span class="paranum">78</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-77-78-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',35,replace(replace('<p class="gatha1">Ṭhānabhaṭṭhā <a name="M0.0035"></a> nasobhanti,</p>\r\n<p class="gatha2">Dantākesāna khānarā;</p>\r\n<p class="gatha3">Matimā itiviññāya,</p>\r\n<p class="gathalast">Saṭhānaṃ nacaje lahuṃ.</p>\r\n<p class="hangnum"><a name="para79"></a><span class="paranum">79</span>.</p>\r\n<p class="gatha1"> Caje dhanaṃ aṅgavarassahetu,</p>\r\n<p class="gatha2">Aṅgaṃ caje jivitaṃ rakkhamāno;</p>\r\n<p class="gatha3">Aṅgaṃ dhanaṃ jivitañcāpi sabbaṃ,</p>\r\n<p class="gathalast">Caje naro dhamma manussaranto.</p>\r\n<p class="hangnum"><a name="para80"></a><span class="paranum">80</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-79-80-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',36,replace(replace('<p class="gatha1">Sotaṃ <a name="M0.0036"></a> suteneva na kuṇḍalena,</p>\r\n<p class="gatha2">Dānena pāṇī natu kaṅkaṇena;</p>\r\n<p class="gatha3">Ābhāti kāyo purisuttamassa,</p>\r\n<p class="gathalast">Paropakārena na candanena.</p>\r\n<p class="chapter">Sambhedakathā</p>\r\n<p class="hangnum"><a name="para81"></a><span class="paranum">81</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-81-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',37,replace(replace('<p class="gatha1">Sattha <a name="M0.0037"></a> kappavicārena,</p>\r\n<p class="gatha2">Kālo gacchati dhīmataṃ;</p>\r\n<p class="gatha3">Byasanena asādhūnaṃ,</p>\r\n<p class="gathalast">Niddāya kalahenavā.</p>\r\n<p class="hangnum"><a name="para82"></a><span class="paranum">82</span>.</p>\r\n<p class="gatha1"> Sokaṭhānasahassāni,</p>\r\n<p class="gatha2">Bhayaṭhānasatānica;</p>\r\n<p class="gatha3">Divasedivase muḷhaṃ,</p>\r\n<p class="gathalast">Āvīsanti napaṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para83"></a><span class="paranum">83</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-82-83-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',38,replace(replace('<p class="gatha1">Atidīgho <a name="M0.0038"></a> mahāmuḷho,</p>\r\n<p class="gatha2">Majjhimoca vicakkhaṇo;</p>\r\n<p class="gatha3">Vāsudevaṃ purakkhitvā,</p>\r\n<p class="gathalast">Sabbevāmanakā saṭṭhā.</p>\r\n<p class="hangnum"><a name="para84"></a><span class="paranum">84</span>.</p>\r\n<p class="gatha1"> Na loke sobhate muḷho,</p>\r\n<p class="gatha2">Kevalattapasaṃsako;</p>\r\n<p class="gatha3">Api sampihite kūpe,</p>\r\n<p class="gathalast">Katavijjo pakāsate.</p>\r\n<p class="hangnum"><a name="para85"></a><span class="paranum">85</span>.</p>\r\n<p class="gatha1"> Madantadamanaṃ satthaṃ,</p>\r\n<p class="gatha2">Khalānaṃ kurute madaṃ;</p>\r\n<p class="gatha3">Cakkhusaṅkhārakaṃ tejaṃ,</p>\r\n<p class="gathalast">Uluṅkāna mivandhakaṃ.</p>\r\n<p class="hangnum"><a name="para86"></a><span class="paranum">86</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-84-85-86-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',39,replace(replace('<p class="gatha1">Atyappamapi <a name="M0.0039"></a> sādhūnaṃ,</p>\r\n<p class="gatha2">Silālekheva tiṭṭhate;</p>\r\n<p class="gatha3">Jalelekheva nīcānaṃ,</p>\r\n<p class="gathalast">Yaṃ kataṃ taṃpi nassati.</p>\r\n<p class="hangnum"><a name="para87"></a><span class="paranum">87</span>.</p>\r\n<p class="gatha1"> Dabbamappaṃpi sādhūnaṃ,</p>\r\n<p class="gatha2">Jalaṃ kūpeva nissayo;</p>\r\n<p class="gatha3">Bahuttaṃpi asādhūnaṃ,</p>\r\n<p class="gathalast">Naca vāriva aṇṇave.</p>\r\n<p class="hangnum"><a name="para88"></a><span class="paranum">88</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-87-88-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',40,replace(replace('<p class="gatha1">Duṭṭhacitto <a name="M0.0040"></a> panāhissa,</p>\r\n<p class="gatha2">Kodho pāsāṇalikkhito;</p>\r\n<p class="gatha3">Kujjhitabbe sujanassa,</p>\r\n<p class="gathalast">Jalelekhā ciraṭṭhito.</p>\r\n<p class="hangnum"><a name="para89"></a><span class="paranum">89</span>.</p>\r\n<p class="gatha1"> Bālassa jīvitaṃ pāpaṃ,</p>\r\n<p class="gatha2">Mitarassi taraṃ bhave;</p>\r\n<p class="gatha3">Janakāyassa rājāva,</p>\r\n<p class="gathalast">Rājadhammova rājunaṃ.</p>\r\n<p class="hangnum"><a name="para90"></a><span class="paranum">90</span>.</p>\r\n<p class="gatha1"> Sappādānaṃ balī sīho,</p>\r\n<p class="gatha2">Puḷāvako tato kippīlikā,</p>\r\n<p class="gatha3">Narā tato balī rājā,</p>\r\n<p class="gathalast">Sabbesa mantako balī.</p>\r\n<p class="hangnum"><a name="para91"></a><span class="paranum">91</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-89-90-91-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',41,replace(replace('<p class="gatha1">Bhūpoṇṇava <a name="M0.0041"></a> ggi thī sippi,</p>\r\n<p class="gatha2">Abhijjhālucapuggalo;</p>\r\n<p class="gatha3">Etesaṃpi mahicchānaṃ,</p>\r\n<p class="gathalast">Mahicchitā aniccatā.</p>\r\n<p class="hangnum"><a name="para92"></a><span class="paranum">92</span>.</p>\r\n<p class="gatha1"> Niddāluko asantuṭṭho,</p>\r\n<p class="gatha2">Akataññūca bhīruko;</p>\r\n<p class="gatha3">Sakkuṇantina sācāraṃ,</p>\r\n<p class="gathalast">Sikkhituṃ te kadācipi.</p>\r\n<p class="hangnum"><a name="para93"></a><span class="paranum">93</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-92-93-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',42,replace(replace('<p class="gatha1">Niddālu <a name="M0.0042"></a> kāmarāmoca,</p>\r\n<p class="gatha2">Sukhito bhogavālaso;</p>\r\n<p class="gatha3">Nicchanto kammarāmo ca,</p>\r\n<p class="gathalast">Sattete satthavajjitā.</p>\r\n<p class="hangnum"><a name="para94"></a><span class="paranum">94</span>.</p>\r\n<p class="gatha1"> Samiddho dhanadhaññena,</p>\r\n<p class="gatha2">Kaṭṭho dakatiṇaggihi;</p>\r\n<p class="gatha3">Sabbato duggatenaṭṭho,</p>\r\n<p class="gathalast">Tasmā nadukkataṃ kare.</p>\r\n<p class="hangnum"><a name="para95"></a><span class="paranum">95</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-94-95-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',43,replace(replace('<p class="gatha1">Puññapāpaphalaṃ <a name="M0.0043"></a> yo ce,</p>\r\n<p class="gatha2">Nasaddhahati saccato;</p>\r\n<p class="gatha3">So ve khippaṃva attānaṃ,</p>\r\n<p class="gathalast">Ādāsatalamānaye.</p>\r\n<p class="hangnum"><a name="para96"></a><span class="paranum">96</span>.</p>\r\n<p class="gatha1"> Samparāyikamatthaṃ yo,</p>\r\n<p class="gatha2">Nasaddhahati cepiso;</p>\r\n<p class="gatha3">Āvāse sagga gāmīnaṃ,</p>\r\n<p class="gathalast">Mākkabhe kiṃnapassati.</p>\r\n<p class="hangnum"><a name="para97"></a><span class="paranum">97</span>.</p>\r\n<p class="gatha1"> Mahantaṃ vaṭṭarukkhādiṃ,</p>\r\n<p class="gatha2">Khuddabījaṃ bahūphalaṃ;</p>\r\n<p class="gatha3">Sakkhikatvā udikkheyya,</p>\r\n<p class="gathalast">Puññapāpakaro naro.</p>\r\n<p class="hangnum"><a name="para98"></a><span class="paranum">98</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-96-97-98-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',44,replace(replace('<p class="gatha1">Yassa <a name="M0.0044"></a> sallahukaṃ hoti,</p>\r\n<p class="gatha2">Gurusakkāramānanaṃ;</p>\r\n<p class="gatha3">Tassa sallahukāyeva,</p>\r\n<p class="gathalast">Vijjāsampattisampadā.</p>\r\n<p class="hangnum"><a name="para99"></a><span class="paranum">99</span>.</p>\r\n<p class="gatha1"> Upajjhācariyānañca,</p>\r\n<p class="gatha2">Mātāpitūnamevaca;</p>\r\n<p class="gatha3">Sakkaccaṃ yonupaṭṭhāti,</p>\r\n<p class="gathalast">Suto tassapitādiso.</p>\r\n<p class="hangnum"><a name="para100"></a><span class="paranum">100</span>.</p>\r\n<p class="gatha1"> Dese dese kulānīca,</p>\r\n<p class="gatha2">Desedese ca bandhavo;</p>\r\n<p class="gatha3">Tādisaṃ sahajā yattha,</p>\r\n<p class="gathalast">Desaṃ passāminevatu.</p>\r\n<p class="hangnum"><a name="para101"></a><span class="paranum">101</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-99-100-101-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',45,replace(replace('<p class="gatha1">Puttaṃvā <a name="M0.0045"></a> bhātaraṃ duṭṭhaṃ,</p>\r\n<p class="gatha2">Anusāseyya nojahe;</p>\r\n<p class="gatha3">Kiṃnu chejjaṃ karaṃpādaṃ,</p>\r\n<p class="gathalast">Littaṃ asucinā siyā;</p>\r\n<p class="hangnum"><a name="para102"></a><span class="paranum">102</span>.</p>\r\n<p class="gatha1"> Bahuputte pitāeko,</p>\r\n<p class="gatha2">Avassaṃ poseti sadā;</p>\r\n<p class="gatha3">Bahuputtā nasakkonti,</p>\r\n<p class="gathalast">Posetuṃ pitarekakaṃ.</p>\r\n<p class="hangnum"><a name="para103"></a><span class="paranum">103</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-102-103-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',46,replace(replace('<p class="gatha1">Atijātamanujātaṃ<a name="M0.0046"></a>,</p>\r\n<p class="gatha2">Puttamicchanti paṇḍitā;</p>\r\n<p class="gatha3">Avajātaṃ naicchanti,</p>\r\n<p class="gathalast">Yohoti kulagandhano.</p>\r\n<p class="hangnum"><a name="para104"></a><span class="paranum">104</span>.</p>\r\n<p class="gatha1"> Pañcaṭhānāni sampassaṃ,</p>\r\n<p class="gatha2">Puttamicchanti paṇḍitā;</p>\r\n<p class="gatha3">Jātovā no bharissati,</p>\r\n<p class="gathalast">Kiccaṃvā no karissati.</p>\r\n<p class="hangnum"><a name="para105"></a><span class="paranum">105</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-104-105-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',47,replace(replace('<p class="gatha1">Kulavaṃso <a name="M0.0047"></a> ciraṃ tiṭṭhe,</p>\r\n<p class="gatha2">Dāyajjaṃ paṭipaccati;</p>\r\n<p class="gatha3">Athavāpana petānaṃ,</p>\r\n<p class="gathalast">Dakkhiṇaṃnupadassati.</p>\r\n<p class="hangnum"><a name="para106"></a><span class="paranum">106</span>.</p>\r\n<p class="gatha1"> Antojāto dhanakkīto,</p>\r\n<p class="gatha2">Dāsabyopagato sayaṃ;</p>\r\n<p class="gatha3">Dāsākaramarānīto,</p>\r\n<p class="gathalast">Ccevante catudhā siyuṃ.</p>\r\n<p class="hangnum"><a name="para107"></a><span class="paranum">107</span>.</p>\r\n<p class="gatha1"> Dāsā pañceva corayya-</p>\r\n<p class="gatha2">Sakhañātyattasadisā;</p>\r\n<p class="gatha3">Tathā viññūhi viññeyyā,</p>\r\n<p class="gathalast">Mittādārāca bandhavā.</p>\r\n<p class="hangnum"><a name="para108"></a><span class="paranum">108</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-106-107-108-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',48,replace(replace('<p class="gatha1">Tayova <a name="M0.0048"></a> paṇḍitā satthe,</p>\r\n<p class="gatha2">Ahamevātivādīca,</p>\r\n<p class="gatha3">Ahamapiti vādīca,</p>\r\n<p class="gathalast">Nāhantica imetayo.</p>\r\n<p class="hangnum"><a name="para109"></a><span class="paranum">109</span>.</p>\r\n<p class="gatha1"> Vinā satthaṃ nagaccheyya,</p>\r\n<p class="gatha2">Sūro saṅkāmabhūmiyaṃ;</p>\r\n<p class="gatha3">Paṇḍitaddhagū vāṇijjo,</p>\r\n<p class="gathalast">Videsagamano tathā.</p>\r\n<p class="hangnum"><a name="para110"></a><span class="paranum">110</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-109-110-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',49,replace(replace('<p class="gatha1">Sammā <a name="M0.0049"></a> upaparikkhitvā,</p>\r\n<p class="gatha2">Akkharesu padesuca;</p>\r\n<p class="gatha3">Coraghāto siyā sisso,</p>\r\n<p class="gathalast">Guru coraṭṭakārako.</p>\r\n<p class="hangnum"><a name="para111"></a><span class="paranum">111</span>.</p>\r\n<p class="gatha1"> Sādhuttaṃ sujanasamāgamā khalānaṃ,</p>\r\n<p class="gatha2">Sādhūnaṃ nakhalasamāgamā khalattaṃ;</p>\r\n<p class="gatha3">Āmodaṃkusumabhavaṃdadhātibhūmi,</p>\r\n<p class="gathalast">Bhūgandhaṃ naca kusumāni dhārayanti.</p>\r\n<p class="hangnum"><a name="para112"></a><span class="paranum">112</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-111-112-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',50,replace(replace('<p class="gatha1">Saṭṭhena <a name="M0.0050"></a> mittaṃ kalusena dhammaṃ,</p>\r\n<p class="gatha2">Paropatāpena samiddhibhāvaṃ;</p>\r\n<p class="gatha3">Sukhena vijjaṃ pharusena nāriṃ,</p>\r\n<p class="gathalast">Icchanti ye te navapaṇḍitāva.</p>\r\n<p class="chapter">Mittakathā</p>\r\n<p class="hangnum"><a name="para113"></a><span class="paranum">113</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-113-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',51,replace(replace('<p class="gatha1">Katvāna <a name="M0.0051"></a> kusalaṃkammaṃ,</p>\r\n<p class="gatha2">Katvācā kusalaṃ pure;</p>\r\n<p class="gatha3">Sukhitadukkhitā honti,</p>\r\n<p class="gathalast">So bālo yo napassati.</p>\r\n<p class="hangnum"><a name="para114"></a><span class="paranum">114</span>.</p>\r\n<p class="gatha1"> Sayaṃkatena pāpena,</p>\r\n<p class="gatha2">Aniṭṭhaṃ labhate phalaṃ;</p>\r\n<p class="gatha3">Te me so me janentīti,</p>\r\n<p class="gathalast">Punāguṃ kurute jaḷo.</p>\r\n<p class="hangnum"><a name="para115"></a><span class="paranum">115</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-114-115-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',52,replace(replace('<p class="gatha1">Kālakhepena <a name="M0.0052"></a> hāpeti,</p>\r\n<p class="gatha2">Dānasīlādikaṃ jaḷo;</p>\r\n<p class="gatha3">Athirampi thiraṃ maññe,</p>\r\n<p class="gathalast">Attānaṃ sassatīsamaṃ.</p>\r\n<p class="hangnum"><a name="para116"></a><span class="paranum">116</span>.</p>\r\n<p class="gatha1"> Bālova pāpakaṃ katvā,</p>\r\n<p class="gatha2">Nataṃchaṭṭetumussahe;</p>\r\n<p class="gatha3">Kiṃ byagghatādi gacchanto,</p>\r\n<p class="gathalast">Padaṃ makkhetumussahe.</p>\r\n<p class="hangnum"><a name="para117"></a><span class="paranum">117</span>.</p>\r\n<p class="gatha1"> Paranassanato naṭṭho,</p>\r\n<p class="gatha2">Pureva paranāsako;</p>\r\n<p class="gatha3">Sīghaṃvā dassanaṃ yāti,</p>\r\n<p class="gathalast">Tiṇaṃ pāsādajjhāpakaṃ.</p>\r\n<p class="hangnum"><a name="para118"></a><span class="paranum">118</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-116-117-118-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',53,replace(replace('<p class="gatha1">Bhojanā <a name="M0.0053"></a> methunā niddā,</p>\r\n<p class="gatha2">Gave poseca vijjati;</p>\r\n<p class="gatha3">Vijjā viseso posassa,</p>\r\n<p class="gathalast">Tatohīno gosamo bhave.</p>\r\n<p class="hangnum"><a name="para119"></a><span class="paranum">119</span>.</p>\r\n<p class="gatha1"> Muḷhasisso padesena,</p>\r\n<p class="gatha2">Kunārībharaṇenaca;</p>\r\n<p class="gatha3">Khalasatthūhisaṃyogā,</p>\r\n<p class="gathalast">Paṇḍitopyavasīdati.</p>\r\n<p class="hangnum"><a name="para120"></a><span class="paranum">120</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-119-120-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',54,replace(replace('<p class="gatha1">Dvecimekaṇṭakā <a name="M0.0054"></a> tikkhā,</p>\r\n<p class="gatha2">Sarīrasobhitā kāme;</p>\r\n<p class="gatha3">Nidhano yoca kāmeti,</p>\r\n<p class="gathalast">Yocakuppatyanissāro.</p>\r\n<p class="hangnum"><a name="para121"></a><span class="paranum">121</span>.</p>\r\n<p class="gatha1"> Nidhanocāpi kāmeti,</p>\r\n<p class="gatha2">Dubbalo kalahaṃpiyo;</p>\r\n<p class="gatha3">Mandasattho vivādatthi,</p>\r\n<p class="gathalast">Tividhaṃ muḷhalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para122"></a><span class="paranum">122</span>.</p>\r\n<p class="gatha1"> Appasuto sute appe,</p>\r\n<p class="gatha2">Bahuṃmaññati mānavo;</p>\r\n<p class="gatha3">Sindhudakama passanto,</p>\r\n<p class="gathalast">Kūpetoyaṃva maṇḍuko.</p>\r\n<p class="hangnum"><a name="para123"></a><span class="paranum">123</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-121-122-123-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',55,replace(replace('<p class="gatha1">Tadamināpi <a name="M0.0055"></a> jānāti,</p>\r\n<p class="gatha2">Sobbhesu padaresuca;</p>\r\n<p class="gatha3">Sunantā yanti kusumbhā,</p>\r\n<p class="gathalast">Tuṇhīyanti mahodadhi.</p>\r\n<p class="hangnum"><a name="para124"></a><span class="paranum">124</span>.</p>\r\n<p class="gatha1"> Yaṃ ūnakaṃ taṃ sunati,</p>\r\n<p class="gatha2">Yaṃ pūraṃ santameva taṃ;</p>\r\n<p class="gatha3">Aḍḍhakumbhūpamo bālo,</p>\r\n<p class="gathalast">Rahato pūrova paṇḍito.</p>\r\n<p class="hangnum"><a name="para125"></a><span class="paranum">125</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-124-125-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',56,replace(replace('<p class="gatha1">Budhehibhāsamānopi<a name="M0.0056"></a>,</p>\r\n<p class="gatha2">Khalo bahutaketavo;</p>\r\n<p class="gatha3">Ghaṃsiyamāno aṅgāro,</p>\r\n<p class="gathalast">Nilamattaṃ nagacchati.</p>\r\n<p class="hangnum"><a name="para126"></a><span class="paranum">126</span>.</p>\r\n<p class="gatha1"> Cārutā paradārāya,</p>\r\n<p class="gatha2">Dhanaṃ lokatappatiyā;</p>\r\n<p class="gatha3">Pasutaṃ sādhunāsāya,</p>\r\n<p class="gathalast">Khale khalatarā guṇā.</p>\r\n<p class="hangnum"><a name="para127"></a><span class="paranum">127</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-126-127-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',57,replace(replace('<p class="gatha1">Ito <a name="M0.0057"></a> hassataraṃ loke,</p>\r\n<p class="gatha2">Kiñci tassa navijjati;</p>\r\n<p class="gatha3">Dujjanotica yaṃāha,</p>\r\n<p class="gathalast">Sajjanaṃ dujjano sayaṃ.</p>\r\n<p class="hangnum"><a name="para128"></a><span class="paranum">128</span>.</p>\r\n<p class="gatha1"> Rogaṇḍacaṅkuro tejo,</p>\r\n<p class="gatha2">Visamassataro ghaṇo;</p>\r\n<p class="gatha3">Avināsiya samanti,</p>\r\n<p class="gathalast">Na khaloca sanissayaṃ.</p>\r\n<p class="hangnum"><a name="para129"></a><span class="paranum">129</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-128-129-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',58,replace(replace('<p class="gatha1">Navinā <a name="M0.0058"></a> paravādena,</p>\r\n<p class="gatha2">Ramante dujjanā khalu;</p>\r\n<p class="gatha3">Na sā sabbarase bhutvā,</p>\r\n<p class="gathalast">Vinā vacce natussati.</p>\r\n<p class="hangnum"><a name="para130"></a><span class="paranum">130</span>.</p>\r\n<p class="gatha1"> Tappate yāti sandhānaṃ,</p>\r\n<p class="gatha2">Davo bhavati vanataṃ;</p>\r\n<p class="gatha3">Muduṃ dujjanacittaṃ kiṃ,</p>\r\n<p class="gathalast">Lohena upamīyate.</p>\r\n<p class="hangnum"><a name="para131"></a><span class="paranum">131</span>.</p>\r\n<p class="gatha1"> Tasmā dujjanasaṃsaggaṃ,</p>\r\n<p class="gatha2">Āsīvisa mivoragaṃ;</p>\r\n<p class="gatha3">Ārakā parivajjeyya,</p>\r\n<p class="gathalast">Bhūtikāmo vicakkhaṇo.</p>\r\n<p class="hangnum"><a name="para132"></a><span class="paranum">132</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-130-131-132-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',59,replace(replace('<p class="gatha1">Dujjanena <a name="M0.0059"></a> hi saṃsaggo,</p>\r\n<p class="gatha2">Sattutāpi nayujjate;</p>\r\n<p class="gatha3">Tatto dahati aṅgāro,</p>\r\n<p class="gathalast">Santātu kālataṃkaro.</p>\r\n<p class="hangnum"><a name="para133"></a><span class="paranum">133</span>.</p>\r\n<p class="gatha1"> Dujjano vajjanīyova,</p>\r\n<p class="gatha2">Vijjāyālaṅkatopi ce;</p>\r\n<p class="gatha3">Maṇinā laṅkato santo,</p>\r\n<p class="gathalast">Sappo kiṃ nabhayaṅkaro.</p>\r\n<p class="hangnum"><a name="para134"></a><span class="paranum">134</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-133-134-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',60,replace(replace('<p class="gatha1">Aggino <a name="M0.0060"></a> dahato dāyaṃ,</p>\r\n<p class="gatha2">Sakhābhavati māluto;</p>\r\n<p class="gatha3">Sova dīpaṃtu nāseti,</p>\r\n<p class="gathalast">Khale nattheva mittatā.</p>\r\n<p class="hangnum"><a name="para135"></a><span class="paranum">135</span>.</p>\r\n<p class="gatha1"> Nasmase katapāpamhi,</p>\r\n<p class="gatha2">Nasmase alikavādini;</p>\r\n<p class="gatha3">Nasmasettatthapaññamhi,</p>\r\n<p class="gathalast">Atisantepi nasmase.</p>\r\n<p class="hangnum"><a name="para136"></a><span class="paranum">136</span>.</p>\r\n<p class="gatha1"> Māretuṃ kittakā sakkā,</p>\r\n<p class="gatha2">Dujjanā gagaṇūpamā;</p>\r\n<p class="gatha3">Māritā kodhacittetu,</p>\r\n<p class="gathalast">Mārito honti dujjanā.</p>\r\n<p class="hangnum"><a name="para137"></a><span class="paranum">137</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-135-136-137-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',61,replace(replace('<p class="gatha1">Bhūmi <a name="M0.0061"></a> kaṇṭakasaṃkiṇṇā,</p>\r\n<p class="gatha2">Chādituṃ nevasakyate;</p>\r\n<p class="gatha3">Upāhana mattakena,</p>\r\n<p class="gathalast">Channā bhavati medanī.</p>\r\n<p class="hangnum"><a name="para138"></a><span class="paranum">138</span>.</p>\r\n<p class="gatha1"> Sattā sadāpasevanti,</p>\r\n<p class="gatha2">Sodakaṃ vāpiādikaṃ;</p>\r\n<p class="gatha3">Sabhogaṃ sadhanañcevaṃ,</p>\r\n<p class="gathalast">Tucchāce te jahanti te.</p>\r\n<p class="hangnum"><a name="para139"></a><span class="paranum">139</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-138-139-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',62,replace(replace('<p class="gatha1">Dhanahīnaṃ <a name="M0.0062"></a> caje mitto,</p>\r\n<p class="gatha2">Puttadārā sahodarā;</p>\r\n<p class="gatha3">Atthavantaṃva sevanti,</p>\r\n<p class="gathalast">Attho loke mahāsakhā.</p>\r\n<p class="hangnum"><a name="para140"></a><span class="paranum">140</span>.</p>\r\n<p class="gatha1"> Na rūpinī na paññāṇo,</p>\r\n<p class="gatha2">Nakulīno padhānatā;</p>\r\n<p class="gatha3">Kāle vipattisampatte,</p>\r\n<p class="gathalast">Dhanimāva visesatā.</p>\r\n<p class="hangnum"><a name="para141"></a><span class="paranum">141</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-140-141-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',63,replace(replace('<p class="gatha1">Kalyāṇamittaṃ <a name="M0.0063"></a> kantāraṃ,</p>\r\n<p class="gatha2">Yuddhaṃ sabhāyaṃ bhāsituṃ;</p>\r\n<p class="gatha3">Asatthā gantu micchanti,</p>\r\n<p class="gathalast">Muḷhā tecaturojanā.</p>\r\n<p class="hangnum"><a name="para142"></a><span class="paranum">142</span>.</p>\r\n<p class="gatha1"> Ahitā paṭisedhoca,</p>\r\n<p class="gatha2">Hitesuca payojanaṃ;</p>\r\n<p class="gatha3">Byasanesva pariccāgo,</p>\r\n<p class="gathalast">Saṅkhepā mittalakkhaṇaṃ.</p>\r\n<p class="hangnum"><a name="para143"></a><span class="paranum">143</span>.</p>\r\n<p class="gatha1"> Āture byasanepatte,</p>\r\n<p class="gatha2">Dubbhikkhe sattu viggahe;</p>\r\n<p class="gatha3">Rājadvāre susāneca,</p>\r\n<p class="gathalast">Yo tiṭṭhati sabandhavo.</p>\r\n<p class="hangnum"><a name="para144"></a><span class="paranum">144</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-142-143-144-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',64,replace(replace('<p class="gatha1">So <a name="M0.0064"></a> bandhu yo hiteyutto,</p>\r\n<p class="gatha2">So pitā yo tu posako;</p>\r\n<p class="gatha3">So ñāti yatra visāso,</p>\r\n<p class="gathalast">Sā bhariyā yatra nibbūti.</p>\r\n<p class="hangnum"><a name="para145"></a><span class="paranum">145</span>.</p>\r\n<p class="gatha1"> Hitesino sumittoca,</p>\r\n<p class="gatha2">Viññūca dullabhā janā;</p>\r\n<p class="gatha3">Yatho sadhaṃca sāduṃca,</p>\r\n<p class="gathalast">Rogahārīca sujano.</p>\r\n<p class="hangnum"><a name="para146"></a><span class="paranum">146</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-145-146-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',65,replace(replace('<p class="gatha1">Agaruko <a name="M0.0065"></a> anālasso,</p>\r\n<p class="gatha2">Asaṭṭhoca saccavā suci;</p>\r\n<p class="gatha3">Aluddho atthakāmoca,</p>\r\n<p class="gathalast">Ediso suhaduttamo.</p>\r\n<p class="hangnum"><a name="para147"></a><span class="paranum">147</span>.</p>\r\n<p class="gatha1"> Yo dhuvāni pariccajja,</p>\r\n<p class="gatha2">Adhūvānyupasevati;</p>\r\n<p class="gatha3">Dhuvāpi tassa nassanti,</p>\r\n<p class="gathalast">Adhuvesu kathāvakā.</p>\r\n<p class="hangnum"><a name="para148"></a><span class="paranum">148</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-147-148-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',66,replace(replace('<p class="gatha1">Luddhamatthenagaṇheyya<a name="M0.0066"></a>,</p>\r\n<p class="gatha2">Thaddha mañjalikammunā;</p>\r\n<p class="gatha3">Chandānuvattiyā muḷhaṃ,</p>\r\n<p class="gathalast">Yathābhūtena paṇḍitaṃ.</p>\r\n<p class="hangnum"><a name="para149"></a><span class="paranum">149</span>.</p>\r\n<p class="gatha1"> Uttamaṃ pāṇipātena,</p>\r\n<p class="gatha2">Sūraṃ bhedena vijaye;</p>\r\n<p class="gatha3">Nicaṃ dabbapadānena,</p>\r\n<p class="gathalast">Vikkamena samaṃ jaye.</p>\r\n<p class="hangnum"><a name="para150"></a><span class="paranum">150</span>.</p>\r\n<p class="gatha1"> Yassayassa hi yobhāvo,</p>\r\n<p class="gatha2">Tenatena hi taṃnaraṃ;</p>\r\n<p class="gatha3">Anupavissa medhāvī,</p>\r\n<p class="gathalast">Khippamattavasaṃ naye.</p>\r\n<p class="hangnum"><a name="para151"></a><span class="paranum">151</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-149-150-151-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',67,replace(replace('<p class="gatha1">Yena <a name="M0.0067"></a> icchati sambandhaṃ,</p>\r\n<p class="gatha2">Tena tīṇi nakāraye;</p>\r\n<p class="gatha3">Vivāda matthasambandhaṃ,</p>\r\n<p class="gathalast">Parokkhe dāradassanaṃ.</p>\r\n<p class="hangnum"><a name="para152"></a><span class="paranum">152</span>.</p>\r\n<p class="gatha1"> Accābhikkhaṇasaṃsaggā,</p>\r\n<p class="gatha2">Asammosaraṇenaca;</p>\r\n<p class="gatha3">Etena mittā jīranti,</p>\r\n<p class="gathalast">Akāle yācanāyaca.</p>\r\n<p class="hangnum"><a name="para153"></a><span class="paranum">153</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-152-153-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',68,replace(replace('<p class="gatha1">Tasmā <a name="M0.0068"></a> nābhikkhaṇaṃ gacche,</p>\r\n<p class="gatha2">Nacagacche cirāciraṃ;</p>\r\n<p class="gatha3">Kālena yācaṃ yāceyya,</p>\r\n<p class="gathalast">Evaṃ mittā najīraye.</p>\r\n<p class="hangnum"><a name="para154"></a><span class="paranum">154</span>.</p>\r\n<p class="gatha1"> Ete bhiyyo samaṃ yanti,</p>\r\n<p class="gatha2">Sandhi tesaṃ najīrati;</p>\r\n<p class="gatha3">Yo adhippannaṃ sahati,</p>\r\n<p class="gathalast">Yoca jānāti desanaṃ.</p>\r\n<p class="hangnum"><a name="para155"></a><span class="paranum">155</span>.</p>\r\n<p class="gatha1"> Sace santā vivādanti,</p>\r\n<p class="gatha2">Khippaṃ sandhiraye puna;</p>\r\n<p class="gatha3">Bālā pattāva bhijjanti,</p>\r\n<p class="gathalast">Na te samathamajjhaguṃ.</p>\r\n<p class="hangnum"><a name="para156"></a><span class="paranum">156</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-154-155-156-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',69,replace(replace('<p class="gatha1">Yenamittena <a name="M0.0069"></a> saṃsaggo,</p>\r\n<p class="gatha2">Yogakkhemo vihīyati;</p>\r\n<p class="gatha3">Pubbeva jjhābhavantassa,</p>\r\n<p class="gathalast">Rakkhe akkhiva paṇḍito.</p>\r\n<p class="hangnum"><a name="para157"></a><span class="paranum">157</span>.</p>\r\n<p class="gatha1"> Yenamittena saṃsaggā,</p>\r\n<p class="gatha2">Yogakkhemo pavaḍḍhati;</p>\r\n<p class="gatha3">Kareyyattasamaṃ vutti,</p>\r\n<p class="gathalast">Sabbakiccesu paṇḍito.</p>\r\n<p class="hangnum"><a name="para158"></a><span class="paranum">158</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-157-158-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',70,replace(replace('<p class="gatha1">Guṇo <a name="M0.0070"></a> sabbaññutulyopi,</p>\r\n<p class="gatha2">Sīdate ko anissayo;</p>\r\n<p class="gatha3">Anaggha mapimāṇikkaṃ,</p>\r\n<p class="gathalast">Hemaṃ nissāya sobhate.</p>\r\n<p class="hangnum"><a name="para159"></a><span class="paranum">159</span>.</p>\r\n<p class="gatha1"> Pabbepabbe kamenucchu,</p>\r\n<p class="gatha2">Visesarasivaggato;</p>\r\n<p class="gatha3">Tathā sumittiko sādhu,</p>\r\n<p class="gathalast">Viparitova dujjano.</p>\r\n<p class="hangnum"><a name="para160"></a><span class="paranum">160</span>.</p>\r\n<p class="gatha1"> Teneva muninā vuttaṃ,</p>\r\n<p class="gatha2">Yekecilokiyādhammā;</p>\r\n<p class="gatha3">Tathā nibbānagāmīno,</p>\r\n<p class="gathalast">Santi lokuttarādhammā.</p>\r\n<p class="hangnum"><a name="para161"></a><span class="paranum">161</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-159-160-161-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',71,replace(replace('<p class="gatha1">Kalyāṇamitta <a name="M0.0071"></a>1 māgamma,</p>\r\n<p class="gatha2">Sabbe te honti pāṇinaṃ;</p>\r\n<p class="gatha3">Tasmā kalyāṇamittesu,</p>\r\n<p class="gathalast">Kātabbo ādaro sadā.</p>\r\n<p class="hangnum"><a name="para162"></a><span class="paranum">162</span>.</p>\r\n<p class="gatha1"> Yo ve kataññū katavediko dhiro,</p>\r\n<p class="gatha2">Kalyāṇamitto daḷhabhattica hoti;</p>\r\n<p class="gatha3">Dukkhitassa sakkaccaṃ kareti kiccaṃ,</p>\r\n<p class="gathalast">Tathāvidhaṃ sappurisāti vadanti.</p>\r\n<p class="hangnum"><a name="para163"></a><span class="paranum">163</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-162-163-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',72,replace(replace('<p class="gatha1">Yassahi <a name="M0.0072"></a> dhammaṃ puriso vijaññā,</p>\r\n<p class="gatha2">Yecassa kaṅkhaṃ vinayanti santo;</p>\r\n<p class="gatha3">Tañhissa dīpañca parāyanañca,</p>\r\n<p class="gathalast">Na tena mittaṃ jirayetha pañño.</p>\r\n<p class="chapter">Nāyakakathā</p>\r\n<p class="hangnum"><a name="para164"></a><span class="paranum">164</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-164-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',73,replace(replace('<p class="gatha1">Kassako <a name="M0.0073"></a> vāṇijomacco,</p>\r\n<p class="gatha2">Samaṇo sutasīlavā;</p>\r\n<p class="gatha3">Tesu vipulajātesu,</p>\r\n<p class="gathalast">Raṭṭhaṃpi vipulaṃ siyā.</p>\r\n<p class="hangnum"><a name="para165"></a><span class="paranum">165</span>.</p>\r\n<p class="gatha1"> Tesu dubbalajātesu,</p>\r\n<p class="gatha2">Raṭṭhaṃpi dubbalaṃ siyā;</p>\r\n<p class="gatha3">Tasmā raṭṭhaṃpi vipulaṃ,</p>\r\n<p class="gathalast">Dhāraye raṭṭhasāravā.</p>\r\n<p class="hangnum"><a name="para166"></a><span class="paranum">166</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-165-166-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',74,replace(replace('<p class="gatha1">Mahārukkhassa <a name="M0.0074"></a> phalino,</p>\r\n<p class="gatha2">Āmaṃ chindati yo phalaṃ;</p>\r\n<p class="gatha3">Rasañcassa najānāti,</p>\r\n<p class="gathalast">Bījañcassa vinassati.</p>\r\n<p class="hangnum"><a name="para167"></a><span class="paranum">167</span>.</p>\r\n<p class="gatha1"> Mahārukkhupamaṃ raṭṭhaṃ,</p>\r\n<p class="gatha2">Yo adhammena sāsati;</p>\r\n<p class="gatha3">Rasañcassa najānāti,</p>\r\n<p class="gathalast">Raṭṭhañcāpi vinassati.</p>\r\n<p class="hangnum"><a name="para168"></a><span class="paranum">168</span>.</p>\r\n<p class="gatha1"> Mahārukkhassa phalino,</p>\r\n<p class="gatha2">Pakkaṃ chindati yo phalaṃ;</p>\r\n<p class="gatha3">Rasañcassa vijānāti,</p>\r\n<p class="gathalast">Bījañcassa nanassati.</p>\r\n<p class="hangnum"><a name="para169"></a><span class="paranum">169</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-167-168-169-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',75,replace(replace('<p class="gatha1">Mahārukkhupamaṃ <a name="M0.0075"></a> raṭṭhaṃ,</p>\r\n<p class="gatha2">Dhammena yo pasāsati;</p>\r\n<p class="gatha3">Rasañcassa vijānāti,</p>\r\n<p class="gathalast">Raṭṭhañcāpi nanassati.</p>\r\n<p class="hangnum"><a name="para170"></a><span class="paranum">170</span>.</p>\r\n<p class="gatha1"> Janappadañca yorājā,</p>\r\n<p class="gatha2">Adhammena pasāsati;</p>\r\n<p class="gatha3">Sabbo sadhihi sorājā,</p>\r\n<p class="gathalast">Viruddho hoti khattiyo.</p>\r\n<p class="hangnum"><a name="para171"></a><span class="paranum">171</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-170-171-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',76,replace(replace('<p class="gatha1">Tatheva <a name="M0.0076"></a> nigame hiṃsaṃ,</p>\r\n<p class="gatha2">Ye yuttā kayavikkaye;</p>\r\n<p class="gatha3">Suṅkadānabalīkāre,</p>\r\n<p class="gathalast">Sa kosena virujjhati.</p>\r\n<p class="hangnum"><a name="para172"></a><span class="paranum">172</span>.</p>\r\n<p class="gatha1"> Mahāravarakhettesu,</p>\r\n<p class="gatha2">Saṅga, me katanissame;</p>\r\n<p class="gatha3">Ussite hiṃsayaṃ rājā,</p>\r\n<p class="gathalast">Sabalena virujjhati.</p>\r\n<p class="hangnum"><a name="para173"></a><span class="paranum">173</span>.</p>\r\n<p class="gatha1"> Tatheva isayo hiṃso,</p>\r\n<p class="gatha2">Saṃyame brahmacāriyo;</p>\r\n<p class="gatha3">Adhammacāri khattīyo,</p>\r\n<p class="gathalast">So saggena virujjhati.</p>\r\n<p class="hangnum"><a name="para174"></a><span class="paranum">174</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-172-173-174-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',77,replace(replace('<p class="gatha1">Sayaṃkatā <a name="M0.0077"></a> naparena,</p>\r\n<p class="gatha2">Mahānajjo juvakatā;</p>\r\n<p class="gatha3">Issarena tathā raññā,</p>\r\n<p class="gathalast">Saraṭṭhe adhipaccatā.</p>\r\n<p class="hangnum"><a name="para175"></a><span class="paranum">175</span>.</p>\r\n<p class="gatha1"> Pāpaṃvāpi hi puññaṃvā,</p>\r\n<p class="gatha2">Padhāno yaṃ karotice;</p>\r\n<p class="gatha3">Lokopevaṃ karotyeva,</p>\r\n<p class="gathalast">Taṃ vijāneyya paṇḍito.</p>\r\n<p class="hangnum"><a name="para176"></a><span class="paranum">176</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-175-176-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',78,replace(replace('<p class="gatha1">Kacchapīnañca <a name="M0.0078"></a> macchīnaṃ,</p>\r\n<p class="gatha2">Kukkuṭīnañca dhenūnaṃ;</p>\r\n<p class="gatha3">Puttaposā yathāhoti,</p>\r\n<p class="gathalast">Tathā maccesu rājūnaṃ.</p>\r\n<p class="hangnum"><a name="para177"></a><span class="paranum">177</span>.</p>\r\n<p class="gatha1"> Anāyakā vinassanti,</p>\r\n<p class="gatha2">Nassanti bahunāyakā;</p>\r\n<p class="gatha3">Thīnāyakā vinassanti,</p>\r\n<p class="gathalast">Nassanti susunāyakā.</p>\r\n<p class="hangnum"><a name="para178"></a><span class="paranum">178</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-177-178-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',79,replace(replace('<p class="gatha1">Bahuvo <a name="M0.0079"></a> yattha netāro,</p>\r\n<p class="gatha2">Sabbe paṇḍitamānino;</p>\r\n<p class="gatha3">Sabbe mahattamicchanti,</p>\r\n<p class="gathalast">Kataṃ nesaṃ vinassati.</p>\r\n<p class="hangnum"><a name="para179"></a><span class="paranum">179</span>.</p>\r\n<p class="gatha1"> Nodayāya vināsāya,</p>\r\n<p class="gatha2">Bahunāyakatā bhūsaṃ;</p>\r\n<p class="gatha3">Nomilanti vinassanti,</p>\r\n<p class="gathalast">Padmā nyakkehisattahi.</p>\r\n<p class="hangnum"><a name="para180"></a><span class="paranum">180</span>.</p>\r\n<p class="gatha1"> Asantuṭṭho yatī naṭṭho,</p>\r\n<p class="gatha2">Santuṭṭhoca mahīpati;</p>\r\n<p class="gatha3">Salajjā gaṇikā naṭṭhā,</p>\r\n<p class="gathalast">Nilajjātu kulaṅganā.</p>\r\n<p class="hangnum"><a name="para181"></a><span class="paranum">181</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-179-180-181-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',80,replace(replace('<p class="gatha1">Na <a name="M0.0080"></a> gaṇassa ggato gacche,</p>\r\n<p class="gatha2">Siddhe kamme samaṃphalaṃ;</p>\r\n<p class="gatha3">Kammavippatti cehoti,</p>\r\n<p class="gathalast">Mukharo tatra haññate.</p>\r\n<p class="hangnum"><a name="para182"></a><span class="paranum">182</span>.</p>\r\n<p class="gatha1"> Padhiro ca tapassīni,</p>\r\n<p class="gatha2">Sūro raṇavaṇo tathā;</p>\r\n<p class="gatha3">Majjapo patithī rājā,</p>\r\n<p class="gathalast">Ete nasaddahāmahaṃ.</p>\r\n<p class="hangnum"><a name="para183"></a><span class="paranum">183</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-182-183-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',81,replace(replace('<p class="gatha1">Jāneyya <a name="M0.0081"></a> pesane bhaccaṃ,</p>\r\n<p class="gatha2">Bandhavāpi bhayāgate;</p>\r\n<p class="gatha3">Āpadāsu tathā mittaṃ,</p>\r\n<p class="gathalast">Dārañca vibhavakkhaye.</p>\r\n<p class="hangnum"><a name="para184"></a><span class="paranum">184</span>.</p>\r\n<p class="gatha1"> Raṇā paccāgataṃ sūraṃ,</p>\r\n<p class="gatha2">Dhanañca gharamāgataṃ;</p>\r\n<p class="gatha3">Jiṇṇa mannaṃ pasaṃseyya,</p>\r\n<p class="gathalast">Dārañca gatayobbanaṃ.</p>\r\n<p class="hangnum"><a name="para185"></a><span class="paranum">185</span>.</p>\r\n<p class="gatha1"> Saddhāpemesu santesu,</p>\r\n<p class="gatha2">Nagaṇe māsakaṃ sataṃ;</p>\r\n<p class="gatha3">Saddhāvemesva santesu,</p>\r\n<p class="gathalast">Māsakaṃpi sataṃ gaṇe.</p>\r\n<p class="hangnum"><a name="para186"></a><span class="paranum">186</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-184-185-186-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',82,replace(replace('<p class="gatha1">Adantadamanaṃ <a name="M0.0082"></a> dānaṃ,</p>\r\n<p class="gatha2">Dānaṃ sabbatthasādhakaṃ;</p>\r\n<p class="gatha3">Dānena piyavācena,</p>\r\n<p class="gathalast">Unnamanti namantica.</p>\r\n<p class="hangnum"><a name="para187"></a><span class="paranum">187</span>.</p>\r\n<p class="gatha1"> Dānaṃ sinehabhesajjaṃ,</p>\r\n<p class="gatha2">Maccheraṃ dussanosadhaṃ;</p>\r\n<p class="gatha3">Dānaṃ yasassabhosajjaṃ,</p>\r\n<p class="gathalast">Maccheraṃ kappanosadhaṃ.</p>\r\n<p class="hangnum"><a name="para188"></a><span class="paranum">188</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-187-188-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',83,replace(replace('<p class="gatha1">Dubbhikkhe <a name="M0.0083"></a> annadātaṃca,</p>\r\n<p class="gatha2">Subhikkheca hiraññadaṃ;</p>\r\n<p class="gatha3">Bhayecābhayatādānaṃ,</p>\r\n<p class="gathalast">Saggepi bahu maññate.</p>\r\n<p class="hangnum"><a name="para189"></a><span class="paranum">189</span>.</p>\r\n<p class="gatha1"> Sataṃcakkhu sataṃkaṇṇā,</p>\r\n<p class="gatha2">Nāyakassa sutā sadā;</p>\r\n<p class="gatha3">Tathāpi andhapadhīro;</p>\r\n<p class="gathalast">Esā nāyakadhammatā.</p>\r\n<p class="hangnum"><a name="para190"></a><span class="paranum">190</span>.</p>\r\n<p class="gatha1"> Khamā jāgariyu ṭṭhānaṃ,</p>\r\n<p class="gatha2">Saṃvibhāgo dayikkhaṇā;</p>\r\n<p class="gatha3">Nāyakassa guṇāete,</p>\r\n<p class="gathalast">Icchitabbā hitatthinā.</p>\r\n<p class="hangnum"><a name="para191"></a><span class="paranum">191</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-189-190-191-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',84,replace(replace('<p class="gatha1">Paribhūto <a name="M0.0084"></a> mudū hoti,</p>\r\n<p class="gatha2">Atitikkhoca veravā;</p>\r\n<p class="gatha3">Etañcaubhayaṃ ñatvā,</p>\r\n<p class="gathalast">Anumajjhaṃ samācare.</p>\r\n<p class="hangnum"><a name="para192"></a><span class="paranum">192</span>.</p>\r\n<p class="gatha1"> Nekantamudunā sakkā,</p>\r\n<p class="gatha2">Ekantatikhiṇenavā;</p>\r\n<p class="gatha3">Attaṃ mahante ṭhapetuṃ,</p>\r\n<p class="gathalast">Tasmā ubhaya mācare.</p>\r\n<p class="hangnum"><a name="para193"></a><span class="paranum">193</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-192-193-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',85,replace(replace('<p class="gatha1">Piṭṭhito <a name="M0.0085"></a> kkaṃ niseveyya,</p>\r\n<p class="gatha2">Kucchinātu hutāsanaṃ;</p>\r\n<p class="gatha3">Sāmikaṃ sabbabhāvena,</p>\r\n<p class="gathalast">Paralokaṃ amāyāya.</p>\r\n<p class="hangnum"><a name="para194"></a><span class="paranum">194</span>.</p>\r\n<p class="gatha1"> Naseve pharusaṃsāmiṃ,</p>\r\n<p class="gatha2">Taṃpi sevena macchariṃ;</p>\r\n<p class="gatha3">Tato paggaṇhakaṃseve,</p>\r\n<p class="gathalast">Seve niggaṇhakaṃ tato;</p>\r\n<p class="hangnum"><a name="para195"></a><span class="paranum">195</span>.</p>\r\n<p class="gatha1"> Na sā rājā yo ajeyyaṃ jināti,</p>\r\n<p class="gatha2">Na so sakhāyo sakhāraṃ jināti;</p>\r\n<p class="gatha3">Na sā bhariyā patino virodhati,</p>\r\n<p class="gathalast">Na te vuttā ye nabharanti jiṇṇakaṃ.</p>\r\n<p class="hangnum"><a name="para196"></a><span class="paranum">196</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-194-195-196-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',86,replace(replace('<p class="gatha1">Na <a name="M0.0086"></a> sā sabhā yattha nasanti santo,</p>\r\n<p class="gatha2">Na te santo ye navadanti dhammaṃ;</p>\r\n<p class="gatha3">Rāgañca dosañca pahāya mohaṃ,</p>\r\n<p class="gathalast">Dhammaṃ bhaṇantāva bhavanti santo.</p>\r\n<p class="hangnum"><a name="para197"></a><span class="paranum">197</span>.</p>\r\n<p class="gatha1"> Sutassa rakkhā satatābhiyogo;</p>\r\n<p class="gatha2">Kulassa vattaṃ purisassa vijjā;</p>\r\n<p class="gatha3">Rañño pamādo pasamodhanassa,</p>\r\n<p class="gathalast">Thīnantu jānāmi na jātu rakkhaṃ.</p>\r\n<p class="chapter">Itthikathā</p>\r\n<p class="hangnum"><a name="para198"></a><span class="paranum">198</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-197-198-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',89,replace(replace('<p class="gatha1">Namātarā <a name="M0.0089"></a> dhītuyā vāpi,</p>\r\n<p class="gatha2">Bhagīniyā vicakkhaṇo;</p>\r\n<p class="gatha3">Navivittāsane mante,</p>\r\n<p class="gathalast">Nārī māyāvinīnanu.</p>\r\n<p class="hangnum"><a name="para199"></a><span class="paranum">199</span>.</p>\r\n<p class="gatha1"> Vijjutānañca lolattaṃ,</p>\r\n<p class="gatha2">Satthānañcatitikkhaṇaṃ;</p>\r\n<p class="gatha3">Sīghataṃ vāyutejānaṃ,</p>\r\n<p class="gathalast">Anukubbanti nāriyo.</p>\r\n<p class="hangnum"><a name="para200"></a><span class="paranum">200</span>.</p>\r\n<p class="gatha1"> Dviguṇo thīna māhāro,</p>\r\n<p class="gatha2">Buddhicāpi catugguṇo;</p>\r\n<p class="gatha3">Chaguṇo hoti vāyāmo,</p>\r\n<p class="gathalast">Kāmotva ṭṭha guṇobhave.</p>\r\n<p class="hangnum"><a name="para201"></a><span class="paranum">201</span>.</p>\r\n<p class="gatha1"> Ekamekāyaitthīyā,</p>\r\n<p class="gatha2">Aṭṭhaṭṭhapatino siyuṃ;</p>\r\n<p class="gatha3">Sūrāca balavantoca,</p>\r\n<p class="gatha2">Sabbākāmarasāharā;</p>\r\n<p class="gatha3">Kareyya navame chandaṃ,</p>\r\n<p class="gathalast">Unnattā hi napūrati.</p>\r\n<p class="hangnum"><a name="para202"></a><span class="paranum">202</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-199-200-201-202-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',90,replace(replace('<p class="gatha1">Lapanti <a name="M0.0090"></a> saddhimaññena,</p>\r\n<p class="gatha2">Passantaññaṃ savibbhamā;</p>\r\n<p class="gatha3">Cittakaṃ cintayantaññaṃ,</p>\r\n<p class="gathalast">Nārīnaṃnāma ko piyo.</p>\r\n<p class="hangnum"><a name="para203"></a><span class="paranum">203</span>.</p>\r\n<p class="gatha1"> Jivhāsahassiko yo hi,</p>\r\n<p class="gatha2">Jīve vassasataṃ naro;</p>\r\n<p class="gatha3">Tena nikammuno vutto,</p>\r\n<p class="gathalast">Thīdoso kiṃkhayaṃgato.</p>\r\n<p class="hangnum"><a name="para204"></a><span class="paranum">204</span>.</p>\r\n<p class="gatha1"> Aggi āpo thiyo muḷho,</p>\r\n<p class="gatha2">Sappo rājakulānica;</p>\r\n<p class="gatha3">Payatanāpagantabbo,</p>\r\n<p class="gathalast">Maccupāṇaharāniti.</p>\r\n<p class="hangnum"><a name="para205"></a><span class="paranum">205</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-203-204-205-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',91,replace(replace('<p class="gatha1">Itthīnaṃ <a name="M0.0091"></a> dujjanā nañca,</p>\r\n<p class="gatha2">Visāsonopapajjate;</p>\r\n<p class="gatha3">Vīsassa siṅgino roga,</p>\r\n<p class="gathalast">Nadīrājakulassaca.</p>\r\n<p class="hangnum"><a name="para206"></a><span class="paranum">206</span>.</p>\r\n<p class="gatha1"> Satthaṃ sunicchitamatīpi vicintanīyaṃ,</p>\r\n<p class="gatha2">Svārādhitopya vanipo parisaṅkanīyo;</p>\r\n<p class="gatha3">Hatthaṅgatāpi yuvatīparirakkhanīyā,</p>\r\n<p class="gathalast">Satthāvanīpayuvatīsu kutovasitthaṃ.</p>\r\n<p class="hangnum"><a name="para207"></a><span class="paranum">207</span>.</p>\r\n<p class="gatha1"> Ayuttakammāraddhanaṃ virodho,</p>\r\n<p class="gatha2">Saṅghassa yuddhañca mahābalehi;</p>\r\n<p class="gatha3">Vissāsakammaṃ pamādāsu niccaṃ,</p>\r\n<p class="gathalast">Dvārānimaccussa vadanti paññā.</p>\r\n<p class="hangnum"><a name="para208"></a><span class="paranum">208</span>.</p>\r\n<p class="gatha1"> Vātaṃ jālena naro parāmase,</p>\r\n<p class="gatha2">Osiñce sāgaraṃ ekapāṇinā;</p>\r\n<p class="gatha3">Sakenatālena janeyya ghosaṃ,</p>\r\n<p class="gathalast">Yo sabbabhāvaṃ pamadāsu osaje.</p>\r\n<p class="hangnum"><a name="para209"></a><span class="paranum">209</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-206-207-208-209-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',93,replace(replace('<p class="gatha1">Itthīpi <a name="M0.0093"></a> hi ekacciyā,</p>\r\n<p class="gatha2">Seyyā vuttāva muninā;</p>\r\n<p class="gatha3">Bhaṇḍāna muttamaṃ itthī,</p>\r\n<p class="gathalast">Aggupaṭṭhāyikātica.</p>\r\n<p class="hangnum"><a name="para210"></a><span class="paranum">210</span>.</p>\r\n<p class="gatha1"> Yo naṃ bharati sabbadā,</p>\r\n<p class="gatha2">Niccaṃ ātāpi ussuko;</p>\r\n<p class="gatha3">Sabbakāmaharaṃ posaṃ,</p>\r\n<p class="gathalast">Bhattāraṃ nātimaññati.</p>\r\n<p class="hangnum"><a name="para211"></a><span class="paranum">211</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-210-211-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',94,replace(replace('<p class="gatha1">Nācāpi <a name="M0.0094"></a> sotthi bhattāraṃ,</p>\r\n<p class="gatha2">Issācārena rosaye;</p>\r\n<p class="gatha3">Bhattuca garunosabbe,</p>\r\n<p class="gathalast">Paṭipūjeti paṇḍito.</p>\r\n<p class="hangnum"><a name="para212"></a><span class="paranum">212</span>.</p>\r\n<p class="gatha1"> Uṭṭhāyikā anālāsā,</p>\r\n<p class="gatha2">Saṅgahitaparijjanā;</p>\r\n<p class="gatha3">Bhattu manāpaṃ carati,</p>\r\n<p class="gathalast">Bhattakaṃ anurakkhati.</p>\r\n<p class="hangnum"><a name="para213"></a><span class="paranum">213</span>.</p>\r\n<p class="gatha1"> Evaṃ vattati yānārī,</p>\r\n<p class="gatha2">Bhattuchandavasānugā;</p>\r\n<p class="gatha3">Manāpānāma tedevā,</p>\r\n<p class="gathalast">Yattha sā upapajjati.</p>\r\n<p class="hangnum"><a name="para214"></a><span class="paranum">214</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-212-213-214-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',95,replace(replace('<p class="gatha1">Kokilānaṃ <a name="M0.0095"></a> saro rūpaṃ,</p>\r\n<p class="gatha2">Nārīrūpaṃ patibbataṃ;</p>\r\n<p class="gatha3">Vijjā rūpa marūpānaṃ,</p>\r\n<p class="gathalast">Khamā rūpaṃ tapassīnaṃ.</p>\r\n<p class="hangnum"><a name="para215"></a><span class="paranum">215</span>.</p>\r\n<p class="gatha1"> Āneya kulajaṃ pañño,</p>\r\n<p class="gatha2">Virūpamapi kaññakaṃ;</p>\r\n<p class="gatha3">Hīnāyapi surūpāya,</p>\r\n<p class="gathalast">Vivāhaṃsadisaṃ kare.</p>\r\n<p class="hangnum"><a name="para216"></a><span class="paranum">216</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-215-216-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',96,replace(replace('<p class="gatha1">Visamhāmatamādeyya<a name="M0.0096"></a>,</p>\r\n<p class="gatha2">Amajjamhāpikañcanaṃ;</p>\r\n<p class="gatha3">Nicamhāpyuttamāvijjā,</p>\r\n<p class="gathalast">Ratanitthīpidukulā.</p>\r\n<p class="hangnum"><a name="para217"></a><span class="paranum">217</span>.</p>\r\n<p class="gatha1"> Bālitthī makkhikātuṇḍaṃ,</p>\r\n<p class="gatha2">Isīnañca kamaṇḍaluṃ;</p>\r\n<p class="gatha3">Setambu phalaṃ tambulaṃ,</p>\r\n<p class="gathalast">Nocchiṭṭhamupajāyate.</p>\r\n<p class="hangnum"><a name="para218"></a><span class="paranum">218</span>.</p>\r\n<p class="gatha1"> Bālakko paṇṇadhūmoca,</p>\r\n<p class="gatha2">Vuddhitthī pallalodakaṃ;</p>\r\n<p class="gatha3">Āyukkhayakaraṃ niccaṃ,</p>\r\n<p class="gathalast">Rattoca dadhibhojanaṃ.</p>\r\n<p class="hangnum"><a name="para219"></a><span class="paranum">219</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-217-218-219-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',97,replace(replace('<p class="gatha1">Thiyo <a name="M0.0097"></a> seveyya naccantaṃ,</p>\r\n<p class="gatha2">Sāduṃ bhuñjeyyanāhitaṃ;</p>\r\n<p class="gatha3">Pūjaye mānaye vuddhe,</p>\r\n<p class="gathalast">Guruṃ māyāya nobhaje.</p>\r\n<p class="hangnum"><a name="para220"></a><span class="paranum">220</span>.</p>\r\n<p class="gatha1"> Ācāro kula mākhyāti,</p>\r\n<p class="gatha2">Desa mākhyāti bhāsitaṃ;</p>\r\n<p class="gatha3">Sambhavo pema mākhyāti,</p>\r\n<p class="gathalast">Deha mākhyātibhojanaṃ.</p>\r\n<p class="hangnum"><a name="para221"></a><span class="paranum">221</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-220-221-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',98,replace(replace('<p class="gatha1">Dehītivacanādvārā<a name="M0.0098"></a>,</p>\r\n<p class="gatha2">Dehaṭṭhāpañcadevatā;</p>\r\n<p class="gatha3">Sajja niyanti dhī kitti,</p>\r\n<p class="gathalast">Hirī sirī matīpica.</p>\r\n<p class="hangnum"><a name="para222"></a><span class="paranum">222</span>.</p>\r\n<p class="gatha1"> Dehītivacanaṃ dukkhaṃ,</p>\r\n<p class="gatha2">Natthītivacanaṃ tathā;</p>\r\n<p class="gatha3">Vākyaṃdehītinatthīti,</p>\r\n<p class="gathalast">Mābhaveyya bhavebhave.</p>\r\n<p class="hangnum"><a name="para223"></a><span class="paranum">223</span>.</p>\r\n<p class="gatha1"> Bodhayanti nayācanti,</p>\r\n<p class="gatha2">Dehīti pacchimājanā;</p>\r\n<p class="gatha3">Passa vatthumadānassa,</p>\r\n<p class="gathalast">Mābhavatūti īdiso.</p>\r\n<p class="hangnum"><a name="para224"></a><span class="paranum">224</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-222-223-224-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',99,replace(replace('<p class="gatha1">Mahā <a name="M0.0099"></a> atyappakaṃ yāti,</p>\r\n<p class="gatha2">Niguṇe guṇavāpiha;</p>\r\n<p class="gatha3">Aṭṭhānaṭṭheyyabhāvena,</p>\r\n<p class="gathalast">Gajindoiva dabbake.</p>\r\n<p class="hangnum"><a name="para225"></a><span class="paranum">225</span>.</p>\r\n<p class="gatha1"> Mahantaṃ nissayaṃkatvā,</p>\r\n<p class="gatha2">Khuddopyatimahā bhave;</p>\r\n<p class="gatha3">Hemapabbatamāpajja,</p>\r\n<p class="gathalast">Sovaṇṇākira pakkhino.</p>\r\n<p class="hangnum"><a name="para226"></a><span class="paranum">226</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-225-226-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',100,replace(replace('<p class="gatha1">Bahūna <a name="M0.0100"></a> mappasārānaṃ,</p>\r\n<p class="gatha2">Ekibhāvo hidujjayo;</p>\r\n<p class="gatha3">Tiṇena vaṭṭate rajju,</p>\r\n<p class="gathalast">Tāya nāgopi bandhate.</p>\r\n<p class="hangnum"><a name="para227"></a><span class="paranum">227</span>.</p>\r\n<p class="gatha1"> Asahāyo samatthopi,</p>\r\n<p class="gatha2">Tejasi kiṃ karissati;</p>\r\n<p class="gatha3">Nivātasaṇṭhito aggi,</p>\r\n<p class="gathalast">Sayamevūpasammati.</p>\r\n<p class="hangnum"><a name="para228"></a><span class="paranum">228</span>.</p>\r\n<p class="gatha1"> Khantuṃ tapanajotejo,</p>\r\n<p class="gatha2">Sakkāhoti na vaṇṇajo;</p>\r\n<p class="gatha3">Bhūpādīhi katodaṇḍo,</p>\r\n<p class="gathalast">Sakkāhoti na bhaccajo.</p>\r\n<p class="hangnum"><a name="para229"></a><span class="paranum">229</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-227-228-229-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',101,replace(replace('<p class="gatha1">Thīsaṃsagge <a name="M0.0101"></a> kutosuddho,</p>\r\n<p class="gatha2">Maṃsabhakkhe kutodayā;</p>\r\n<p class="gatha3">Surāpāṇe kutosaccaṃ,</p>\r\n<p class="gathalast">Pakodhamhi kutotapo.</p>\r\n<p class="hangnum"><a name="para230"></a><span class="paranum">230</span>.</p>\r\n<p class="gatha1"> Thīyā guyhaṃ nasaṃseyya,</p>\r\n<p class="gatha2">Amittassaca paṇḍito;</p>\r\n<p class="gatha3">Yocā missena saṃhīro,</p>\r\n<p class="gathalast">Hadayattheno yonaro.</p>\r\n<p class="hangnum"><a name="para231"></a><span class="paranum">231</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-230-231-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',102,replace(replace('<p class="gatha1">Guyhamattha <a name="M0.0102"></a></p>\r\n<p class="gatha2">Sambodhayati yonaro;</p>\r\n<p class="gatha3">Mantabhedabhayā tassa,</p>\r\n<p class="gathalast">Dāsabhūto titikkhati.</p>\r\n<p class="hangnum"><a name="para232"></a><span class="paranum">232</span>.</p>\r\n<p class="gatha1"> Vahe amitta khandhena,</p>\r\n<p class="gatha2">Yāvakāle anāgate;</p>\r\n<p class="gatha3">Tamheva cāgate kāle,</p>\r\n<p class="gathalast">Bhinde ghaṭami vuppale.</p>\r\n<p class="hangnum"><a name="para233"></a><span class="paranum">233</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-232-233-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',103,replace(replace('<p class="gatha1">Khalaṃ <a name="M0.0103"></a> sālaṃ pasuṃ khettaṃ,</p>\r\n<p class="gatha2">Gantvācassa abhikkhaṇaṃ;</p>\r\n<p class="gatha3">Mitaṃ dhaññaṃ nidhāpeyya,</p>\r\n<p class="gathalast">Mitañca pācaye ghare.</p>\r\n<p class="hangnum"><a name="para234"></a><span class="paranum">234</span>.</p>\r\n<p class="gatha1"> Kodhaṃ lobhaṃ madaṃ mānaṃ,</p>\r\n<p class="gatha2">Tandiṃ missaṃ pamattakaṃ;</p>\r\n<p class="gatha3">Soṇṭhaṃniddhālukaṃ makkhaṃ,</p>\r\n<p class="gathalast">Maccherañca jahe budho.</p>\r\n<p class="hangnum"><a name="para235"></a><span class="paranum">235</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-234-235-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',104,replace(replace('<p class="gatha1">Kodho <a name="M0.0104"></a> abbhantare jāto,</p>\r\n<p class="gatha2">Dhūvaṃ nāseti kodhanaṃ;</p>\r\n<p class="gatha3">Vatthālaṅkārapuṇṇāyaṃ,</p>\r\n<p class="gathalast">Mañjūsāyaṃ sikhīyathā.</p>\r\n<p class="hangnum"><a name="para236"></a><span class="paranum">236</span>.</p>\r\n<p class="gatha1"> Uppajjate sace kodho;</p>\r\n<p class="gatha2">Āvajje kakacūpamaṃ;</p>\r\n<p class="gatha3">Uppajje ce rase taṇhā,</p>\r\n<p class="gathalast">Puttamaṃsūpamaṃ sare.</p>\r\n<p class="hangnum"><a name="para237"></a><span class="paranum">237</span>.</p>\r\n<p class="gatha1"> Guṇa maddhisamaṃ makkhe,</p>\r\n<p class="gatha2">Parena kalahe sati;</p>\r\n<p class="gatha3">Addhisamaṃ pakāseti,</p>\r\n<p class="gathalast">Aṇumattaṃvavajjakaṃ.</p>\r\n<p class="hangnum"><a name="para238"></a><span class="paranum">238</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-236-237-238-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',105,replace(replace('<p class="gatha1">Tasseva <a name="M0.0105"></a> tena pāpīyo,</p>\r\n<p class="gatha2">Yo kuddhaṃ paṭikujjhati;</p>\r\n<p class="gatha3">Kuddha mapaṭikujjhanto,</p>\r\n<p class="gathalast">Saṅgāmaṃ jeti dujjayaṃ.</p>\r\n<p class="hangnum"><a name="para239"></a><span class="paranum">239</span>.</p>\r\n<p class="gatha1"> Rāgonāma manosallaṃ,</p>\r\n<p class="gatha2">Guṇatthavaratakkaro;</p>\r\n<p class="gatha3">Rāhu vijjāsasaṅkassa,</p>\r\n<p class="gathalast">Tapodhanahutāsano.</p>\r\n<p class="hangnum"><a name="para240"></a><span class="paranum">240</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-239-240-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',106,replace(replace('<p class="gatha1">Pamādo <a name="M0.0106"></a> jāyate madā,</p>\r\n<p class="gatha2">Pamādā jāyate khayo;</p>\r\n<p class="gatha3">Khayā padosā jāyanti,</p>\r\n<p class="gathalast">Madaṃ kiṃ najahe budho.</p>\r\n<p class="hangnum"><a name="para241"></a><span class="paranum">241</span>.</p>\r\n<p class="gatha1"> Namanti phalinorukkhā,</p>\r\n<p class="gatha2">Namanti vibudhājanā;</p>\r\n<p class="gatha3">Sukkhakaṭṭhañca muḷhoca,</p>\r\n<p class="gathalast">Bhijjateva nanamanti.</p>\r\n<p class="hangnum"><a name="para242"></a><span class="paranum">242</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-241-242-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',107,replace(replace('<p class="gatha1">Ṭhāne <a name="M0.0107"></a> vuddhāna mokāsaṃ,</p>\r\n<p class="gatha2">Dade vuddhāpacāyiko;</p>\r\n<p class="gatha3">Nanu tālo ajīvopi,</p>\r\n<p class="gathalast">Samīpa ññe paronato.</p>\r\n<p class="hangnum"><a name="para243"></a><span class="paranum">243</span>.</p>\r\n<p class="gatha1"> Garukātabbaposesu,</p>\r\n<p class="gatha2">Niccavuttiṃ karoti yo;</p>\r\n<p class="gatha3">Nicattaṃ so pahantāna,</p>\r\n<p class="gathalast">Uttamatte patiṭṭhati.</p>\r\n<p class="hangnum"><a name="para244"></a><span class="paranum">244</span>.</p>\r\n<p class="gatha1"> Yattha posaṃ najānanti,</p>\r\n<p class="gatha2">Jātiyā vinayena vā;</p>\r\n<p class="gatha3">Na tattha mānaṃ kariyā,</p>\r\n<p class="gathalast">Vasa maññātakejane.</p>\r\n<p class="hangnum"><a name="para245"></a><span class="paranum">245</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-243-244-245-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',108,replace(replace('<p class="gatha1">Aññātavāsaṃ <a name="M0.0108"></a> vasatā,</p>\r\n<p class="gatha2">Jātavedasamenāpi;</p>\r\n<p class="gatha3">Khamitabbaṃ sapaññena,</p>\r\n<p class="gathalast">Api dāsassa tajjitaṃ.</p>\r\n<p class="hangnum"><a name="para246"></a><span class="paranum">246</span>.</p>\r\n<p class="gatha1"> Dhanadhaññāpayogesu,</p>\r\n<p class="gatha2">Tathā vijjāgamesuca;</p>\r\n<p class="gatha3">Dūteca byavahāreca,</p>\r\n<p class="gathalast">Cattalajjo sadā bhave.</p>\r\n<p class="hangnum"><a name="para247"></a><span class="paranum">247</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-246-247-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',109,replace(replace('<p class="gatha1">Nahi <a name="M0.0109"></a> koci kate kicce,</p>\r\n<p class="gatha2">Kattāraṃ sammapekkhate;</p>\r\n<p class="gatha3">Tasmā sabbānikammāni,</p>\r\n<p class="gathalast">Sāvasesāni kāraye.</p>\r\n<p class="hangnum"><a name="para248"></a><span class="paranum">248</span>.</p>\r\n<p class="gatha1"> Iṇaseso aggiseso,</p>\r\n<p class="gatha2">Sattuseso tayoime;</p>\r\n<p class="gatha3">Punappunaṃpi vaḍḍhanti,</p>\r\n<p class="gathalast">Tasmā sesaṃ nakāraye.</p>\r\n<p class="hangnum"><a name="para249"></a><span class="paranum">249</span>.</p>\r\n<p class="gatha1"> Natthi vijjāsamaṃ vittaṃ,</p>\r\n<p class="gatha2">Natthi byādhisamo ripu;</p>\r\n<p class="gatha3">Natthi attasamaṃ pemaṃ,</p>\r\n<p class="gathalast">Natthi kammaparaṃ balaṃ.</p>\r\n<p class="hangnum"><a name="para250"></a><span class="paranum">250</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-248-249-250-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',110,replace(replace('<p class="gatha1">Attanā <a name="M0.0110"></a> kurute lakkhī,</p>\r\n<p class="gatha2">Alakkhīcāpi attanā;</p>\r\n<p class="gatha3">Nahi lakkhī alakkhīca,</p>\r\n<p class="gathalast">Añño aññassa kurute.</p>\r\n<p class="hangnum"><a name="para251"></a><span class="paranum">251</span>.</p>\r\n<p class="gatha1"> Sayaṃ āyaṃ vayaṃ raññā,</p>\r\n<p class="gatha2">Sayaṃ jaññā katākataṃ;</p>\r\n<p class="gatha3">Attanāva bhavakkheyya,</p>\r\n<p class="gathalast">Katāni akatānica.</p>\r\n<p class="hangnum"><a name="para252"></a><span class="paranum">252</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-251-252-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',111,replace(replace('<p class="gatha1">Upakāraṃ <a name="M0.0111"></a> hiteneva,</p>\r\n<p class="gatha2">Sattunā sattu muddhare;</p>\r\n<p class="gatha3">Pādalaggaṃ karaṭṭhena,</p>\r\n<p class="gathalast">Kaṇṭakena kaṇṭakaṃ.</p>\r\n<p class="hangnum"><a name="para253"></a><span class="paranum">253</span>.</p>\r\n<p class="gatha1"> Name namantassa bhaje bhajantaṃ,</p>\r\n<p class="gatha2">Kiccāni krupassa kareyya kiccaṃ;</p>\r\n<p class="gatha3">Nā natthakāmassa kareyya atthaṃ,</p>\r\n<p class="gathalast">Asambhajantaṃpi nasambhajeyya.</p>\r\n<p class="hangnum"><a name="para254"></a><span class="paranum">254</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-253-254-');
INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_62',112,replace(replace('<p class="gatha1">Caje <a name="M0.0112"></a> cajantaṃ navataṃ kariyā,</p>\r\n<p class="gatha2">Apetacittena nasambhajeyya;</p>\r\n<p class="gatha3">Dijo dumaṃ khīṇaphala mañatvā,</p>\r\n<p class="gathalast">Aññaṃ sapekkheyya mahāti loko.</p>','\r',char(13)),'\n',char(10)),'');