INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',45,replace(replace('<p class="gatha1">Māyācetā <a name="M0.0045"></a> marīcī ca,</p>\r\n<p class="gatha2">Soko rogo upaddavo;</p>\r\n<p class="gatha3">Kharā ca bandhanācetā,</p>\r\n<p class="gathalast">Maccupāso guhāsayo.</p>\r\n<p class="bodytext">(Kha)</p>\r\n<p class="gatha1">Balavanto dubbalā honti,</p>\r\n<p class="gatha2">Thāmavantopi hāyare;</p>\r\n<p class="gatha3">Cakkhumā andhakā honti,</p>\r\n<p class="gathalast">Mātugāmavasaṃgatā.</p>\r\n<p class="bodytext">(Ga)</p>','\r',char(13)),'\n',char(10)),'');
