INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_niti_gantha_sangaho_e1006n',129,replace(replace('<p class="bodytext">Aṭṭhahi <a name="M0.0129"></a> bhikkhave aṅgehi sampannāgatassa upāsakassa patto nikujjitabbo. Bhikkhūnaṃ alābhāya parisakkati, bhikkhūnaṃ anatthāya parisakkati, bhikkhūnaṃ anāvāsāya parisakkati, bhikkhūnaṃ akkosati paribhāsati, bhikkhūbhikkhūhi bhedeti, buddhassa avaṇṇaṃ bhāsati, dhammassa avaṇṇaṃ bhāsati, saṅghassa avaṇṇaṃ bhāsati, anujānāmi bhikkhave imehi aṭṭhahi aṅgehi sampannāgatassa upāsakassa pattaṃ nikujjituṃ.</p>\r\n<p class="hangnum"><a name="para20"></a><span class="paranum">20</span>.</p>\r\n<p class="gatha1"> </p>','\r',char(13)),'\n',char(10)),'-20-');
