INSERT INTO books VALUES('annya_sadda_56','annya','annya_sadda','Moggallāna  vuttivivaraṇapañcikā.',1,1,1);
INSERT INTO tocs VALUES('annya_sadda_56','Moggallāna  vuttivivaraṇapañcikā.','chapter',1);
