INSERT INTO books VALUES('annya_sadda_28','annya','annya_sadda','Padamañjarī',1,1,1);
DELETE FROM tocs where book_id='annya_sadda_28';
INSERT INTO tocs VALUES('annya_sadda_28','Padamañjarī','chapter',1);
