INSERT INTO pages (bookid, page, content, paranum) VALUES('annya_sadda_28',1,replace(replace('<p class="chapter">Padamañjarī <a name="O0.0001"></a></p>\r\n<p class="centered">Namo tassa bhagavato arahato sammāsambuddhassa</p>\r\n<p class="gatha1">Niruttikāyodadhipāragaṃ jinaṃ,</p>\r\n<p class="gatha2">Visuddhadhammañca gaṇaṃ anuttaraṃ;</p>\r\n<p class="gatha3">Tidhā namitvā padamañjarī mayā,</p>\r\n<p class="gathalast">Karīyato bālakabuddhivuddhiyāti.</p>\r\n<p class="centered">Atha akārantapulliṅgo buddhasaddo vuccate.</p>\r\n<p class="unindented">Buddho devamanussānaṃ dhammaṃ desesi</p>\r\n<p class="unindented">Buddhā devamanussānaṃ dhammaṃ adesiṃsu</p>\r\n<p class="unindented">Bho buddha tvampikho maṃ pālaya</p>\r\n<p class="unindented">Bho buddhā tumhepi kho maṃ pāletha.</p>\r\n<p class="unindented">Buddhaṃ bhagavantaṃ sakkaccaṃ vandāmahaṃ</p>\r\n<p class="unindented">Buddhe bhagavante sakkaccaṃ vandāma mayaṃ</p>\r\n<p class="unindented">Buddhena bhagavatā dhammo desīyate</p>\r\n<p class="unindented">Buddhehi bhagavantehi dhammo desito</p>\r\n<p class="unindented">Buddhena bhagavatā mahājano sucarati</p>\r\n<p class="unindented">Buddhehi bhagavantehi sivapadaṃ yanti</p>\r\n<p class="unindented">Buddhassa bhagavato pupphaṃ yajati</p>\r\n<p class="unindented">Buddhānaṃ bhagavataṃ pupphāni yajati</p>\r\n<p class="unindented">Buddhā bhagavatā sivapadaṃ labheyya</p>\r\n<p class="unindented">Buddhehi bhagavantehi pabhā niccharati</p>\r\n<p class="unindented">Buddhassa bhagavato iddhi kiṃ na kare</p>\r\n<p class="unindented">Buddhānaṃ bhagavantānaṃ iddhipāṭihāriyāni</p>\r\n<p class="unindented">Buddhe bhagavante mahājano pasīdati.</p>\r\n<p class="unindented">Buddhesu bhagavantesu manaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="centered">Akārantapulliṅgo attasaddo vuccate.</p>\r\n<p class="unindented">Attā saṃsāradukkhaṃ pāpuṇāti</p>\r\n<p class="unindented">Attāno sukhadukkhaṃ pāpuṇanti</p>\r\n<p class="unindented">Bho atta sukhadukkhaṃ anubhosi</p>\r\n<p class="unindented">Bho attano sukhadukkhaṃ anubhotha</p>\r\n<p class="unindented">Attānaṃ passati buddho bhagavā</p>\r\n<p class="unindented">Attāno <a name="O0.0002"></a> passati sammā sambuddho</p>\r\n<p class="unindented">Attanā sukhadukkhaṃ anubhuttaṃ</p>\r\n<p class="unindented">Attanehi kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Attanā saṃsārasukhaṃ nāma natthi</p>\r\n<p class="unindented">Attanehi bhavasukhaṃ nāma natthi</p>\r\n<p class="unindented">Attano attāva patiṭṭhā siyā</p>\r\n<p class="unindented">Attānaṃ kammameva patiṭṭhā siyā</p>\r\n<p class="unindented">Attanāpi mahabbhayaṃ uppajjati</p>\r\n<p class="unindented">Attanehi mahabbhayāni jāyanti</p>\r\n<p class="unindented">Attano mātāpitaro honti</p>\r\n<p class="unindented">Attānaṃ pañcagatiyo honti</p>\r\n<p class="unindented">Attani sabbaṃ balaṃ harati rogo</p>\r\n<p class="unindented">Attanesu sabbaṃ balaṃ haranti rogā.</p>\r\n<p class="centered">Iti dutiyo pāṭho.</p>\r\n<p class="centered">Akāranta pulliṅgo rājasaddo vuccate.</p>\r\n<p class="unindented">Rājāpi mahājanaṃ toseti</p>\r\n<p class="unindented">Rājāno mahājanaṃ tosenti</p>\r\n<p class="unindented">Bho rājā mahājanaṃ pālaya</p>\r\n<p class="unindented">Bho rājāno mahājanaṃ pāletha</p>\r\n<p class="unindented">Rājānaṃ passati mahājano</p>\r\n<p class="unindented">Rājāno passanti mahājanā</p>\r\n<p class="unindented">Raññā kārīyate mahāpāsādo</p>\r\n<p class="unindented">Rājūhi kārāpitā mahāvihārā</p>\r\n<p class="unindented">Raññā mahājano sukhaṃ carati</p>\r\n<p class="unindented">Rājūhī mahājanā sukhaṃ caranti</p>\r\n<p class="unindented">Rañño paṇṇākāraṃ deti mahājano</p>\r\n<p class="unindented">Rājūnaṃ paṇṇākāraṃ denti mahājanā</p>\r\n<p class="unindented">Raññā mahabbhayaṃ uppajjati</p>\r\n<p class="unindented">Rājūhi mahabbhayāni uppajjanti</p>\r\n<p class="unindented">Rañño vappamaṅgalaṃ hoti</p>\r\n<p class="unindented">Rājūnaṃ sabbābharaṇāni honti</p>\r\n<p class="unindented">Raññe mahājano pana pasīdati</p>\r\n<p class="unindented">Rājusu mahājanā pana pasīdanti.</p>\r\n<p class="centered">Iti tatiyo pāṭho.</p>\r\n<p class="centered">Akārantapulliṅgo guṇavattusaddo vuccate.</p>\r\n<p class="unindented">Guṇavā puriso sundaraṃ nibbāṇaṃ gato</p>\r\n<p class="unindented">Guṇavanto <a name="O0.0003"></a> purisā sundaraṃ nibbāṇaṃ gatā</p>\r\n<p class="unindented">Bho guṇavaṃ purisa tvampi dānaṃ dehi</p>\r\n<p class="unindented">Bho guṇavanto purisā tumhe dānaṃ detha</p>\r\n<p class="unindented">Guṇavantaṃ purisaṃ dhammaṃ bodheti ācariyo</p>\r\n<p class="unindented">Guṇavante purise dhammaṃ pāṭhayati ācariyo</p>\r\n<p class="unindented">Guṇavantena purisena kārito vihāro</p>\r\n<p class="unindented">Guṇavantehi purisehi kāritā vihārā</p>\r\n<p class="unindented">Guṇavantena purisena loko sucarati</p>\r\n<p class="unindented">Guṇavantehi purisehi lokā sucaranti</p>\r\n<p class="unindented">Guṇavato purisassa sakkāraṃ karoti</p>\r\n<p class="unindented">Guṇavataṃ purisānaṃ sakkāraṃ karonti</p>\r\n<p class="unindented">Guṇavatā purisasmā bhayaṃ nūppajjati</p>\r\n<p class="unindented">Guṇavantehi purisehi bhayāni nūppajjanti</p>\r\n<p class="unindented">Guṇavato purisassa kittisaddo aṅguggacchati</p>\r\n<p class="unindented">Guṇavataṃ purisānaṃ guṇaghoso hoti</p>\r\n<p class="unindented">Guṇavante purise me ramati mato</p>\r\n<p class="unindented">Guṇavantesu purisesu sappuriso pasīdati.</p>\r\n<p class="centered">Iti catuttho pāṭho.</p>\r\n<p class="centered">Akārantapulliṅgo gacchantasaddo vuccate.</p>\r\n<p class="unindented">Gacchaṃ yaññadatto purisaṃ bhāraṃ hārayati</p>\r\n<p class="unindented">Gacchantā yaññadattā purisaṃ kammaṃ kārayanti</p>\r\n<p class="unindented">Bho gacchaṃ yaññadatta tvaṃ maṃ pālaya</p>\r\n<p class="unindented">Bho gacchantā yaññadattā maṃ pāletha</p>\r\n<p class="unindented">Gacchantaṃ yaññadattaṃ kambalaṃyācayati dvijo</p>\r\n<p class="unindented">Gacchante yaññadatte kambalaṃ yācayati dvijo</p>\r\n<p class="unindented">Gacchatā yaññadattena rukkho pupphāni avacīyate</p>\r\n<p class="unindented">Gacchantehi yaññadattehi rukkho pupphāni avacito</p>\r\n<p class="unindented">Gacchatā yaññadattena koci maggaṃ jānāti</p>\r\n<p class="unindented">Gacchantehi yaññadattehi kecimaggaṃ jānanti</p>\r\n<p class="unindented">Gacchato yaññadattassa chattaṃ dhārayate</p>\r\n<p class="unindented">Gacchataṃ yaññadattānaṃ chattāni dhārayante</p>\r\n<p class="unindented">Gacchatā yaññadattasmā bhayaṃ nūppajjati</p>\r\n<p class="unindented">Gacchantehi yaññadattehi bhayāni nūppajjanti</p>\r\n<p class="unindented">Gacchato yaññadattassa chatto hoti</p>\r\n<p class="unindented">Gacchataṃ yaññadattānaṃ ābharaṇāni honti</p>\r\n<p class="unindented">Gacchante yaññadatte koci pasīdati</p>\r\n<p class="unindented">Gacchantesu yaññadattesu keci pasīdanti.</p>\r\n<p class="centered">Iti <a name="O0.0004"></a> pañcamo pāṭho.</p>\r\n<p class="centered">Ikārantapulliṅgo aggisaddo vuccate.</p>\r\n<p class="unindented">Aggi pana kaṭṭhamaṅgāraṃ karoti</p>\r\n<p class="unindented">Aggayo kaṭṭhamaṅgāraṃ karonti</p>\r\n<p class="unindented">Bho aggi tvaṃ sītaṃ vinodehi</p>\r\n<p class="unindented">Bho aggī tumhe sītaṃ vinodetha</p>\r\n<p class="unindented">Aggiṃ nibbāpeti yo koci</p>\r\n<p class="unindented">Aggayo nibbāpenti ye keci</p>\r\n<p class="unindented">Agginā koci akāro daḍḍho</p>\r\n<p class="unindented">Aggīhi keci agārā daḍḍhā</p>\r\n<p class="unindented">Agginā kiñci āgāraṃ jhāpeti</p>\r\n<p class="unindented">Aggīhi keci agāre jhāpeti</p>\r\n<p class="unindented">Aggino upādānaṃ dadāti brāhmaṇo</p>\r\n<p class="unindented">Aggīnaṃ upādānaṃ dadanti brāhmaṇā</p>\r\n<p class="unindented">Agginā dhūmo apeti niccaṃ</p>\r\n<p class="unindented">Aggīhi’dhūmāpi apenti niccaṃ</p>\r\n<p class="unindented">Aggino āloko ca hoti</p>\r\n<p class="unindented">Aggīnaṃ ālokāpi honti</p>\r\n<p class="unindented">Aggimhi yo koci pasīdati</p>\r\n<p class="unindented">Aggīsu ye keci pasīdanti.</p>\r\n<p class="centered">Iti chaṭṭho pāṭho.</p>\r\n<p class="centered">Ikārantapulliṅgo ādisaddo vuccate.</p>\r\n<p class="unindented">Ādi bandhīyate samaggena saṅghena</p>\r\n<p class="unindented">Ādayo bandhīyante samaggehi bhikkhuhi</p>\r\n<p class="unindented">Bho ādi tvaṃ dīghakālaṃ pavattehi</p>\r\n<p class="unindented">Bho ādī tumhe dīghakālaṃ pavattetha</p>\r\n<p class="unindented">Ādiṃ passati samaggo saṅgho ca</p>\r\n<p class="unindented">Ādayo passati bhikkhu saṅgho ca</p>\r\n<p class="unindented">Ādinā parisuddhena pana bhūyate</p>\r\n<p class="unindented">Ādīhi parisuddhehi pana bhūyate</p>\r\n<p class="unindented">Ādinā samaggo saṅgho sucarati</p>\r\n<p class="unindented">Ādīhi bhikkhu saṅgho sucarati</p>\r\n<p class="unindented">Ādino koci upakaraṇaṃ deti</p>\r\n<p class="unindented">Ādīnaṃ keci upakaraṇaṃ denti</p>\r\n<p class="unindented">Ādinā samaggo saṅgho apeti</p>\r\n<p class="unindented">Ādīhi bhikkhu’saṅgho apeti</p>\r\n<p class="unindented">Ādissa <a name="O0.0005"></a> upakārikā kho pana hoti</p>\r\n<p class="unindented">Ādīnaṃ upakārikāyopi honti</p>\r\n<p class="unindented">Ādimhi samaggo saṅgho nisīdati</p>\r\n<p class="unindented">Ādīsū bhikkhu saṅghopi nisīdati.</p>\r\n<p class="centered">Iti sattamo pāṭho.</p>\r\n<p class="centered">Īkārantapulliṅgo daṇḍīsaddo vuccate.</p>\r\n<p class="unindented">Daṇḍī purisopi daṇḍaṃ chaḍḍetu</p>\r\n<p class="unindented">Daṇḍino purisā daṇḍaṃ chaḍḍentu</p>\r\n<p class="unindented">Bho daṇḍī purisa daṇḍaṃ chaḍḍehi</p>\r\n<p class="unindented">Bho daṇḍino purisā daṇḍaṃ chaḍḍetha</p>\r\n<p class="unindented">Daṇḍiṃ purisaṃ kammaṃ kārayati puriso</p>\r\n<p class="unindented">Daṇḍī purise kamme kārenti purisā</p>\r\n<p class="unindented">Daṇḍinā purisena daṇḍo chaḍḍīyatu</p>\r\n<p class="unindented">Daṇḍīhi purisehi daṇḍā chaḍḍīyantu</p>\r\n<p class="unindented">Daṇḍinā purisena puriso tiṭṭhati</p>\r\n<p class="unindented">Daṇḍīhi purisehi purisā tiṭṭhanti</p>\r\n<p class="unindented">Daṇḍino purisassa cittaṃ na ruccati</p>\r\n<p class="unindented">Daṇḍīnaṃ purisānaṃ cittaṃ na ruccati</p>\r\n<p class="unindented">Daṇḍinā purisasmā bhayaṃ uppajjati</p>\r\n<p class="unindented">Daṇḍīhi purisehi bhayāni uppajjanti</p>\r\n<p class="unindented">Daṇḍino purisassa pariggaho hoti</p>\r\n<p class="unindented">Daṇḍīnaṃ purisānaṃ pariggahā honti</p>\r\n<p class="unindented">Daṇḍismiṃ purise cittaṃ na ramati</p>\r\n<p class="unindented">Daṇḍīsu purisesu ekacco nappasīdati.</p>\r\n<p class="centered">Iti aṭṭhamo pāṭho.</p>\r\n<p class="centered">Ukārantapulliṅgo bhikkhusaddo vuccate.</p>\r\n<p class="unindented">Bhikkhu mahārājānaṃ dhammaṃ bhaṇati</p>\r\n<p class="unindented">Bhikkhū mahārājānaṃ dhammaṃ bhaṇanti</p>\r\n<p class="unindented">Bho bhikkhu paṇītaṃ dhammaṃ desehi</p>\r\n<p class="unindented">Bho bhikkhū paṇītaṃ dhammaṃ desetha</p>\r\n<p class="unindented">Bhikkhuṃ sakkaccaṃ paṇamāmahaṃ</p>\r\n<p class="unindented">Bhikkhū sakkaccaṃ paṇamāma mayaṃ</p>\r\n<p class="unindented">Bhikkhunā saddhammo desīyate</p>\r\n<p class="unindented">Bhikkhūhi saddhammo sudesito</p>\r\n<p class="unindented">Bhikkhunā loko saggaṃ gacchati</p>\r\n<p class="unindented">Bhikkhūhi mahājanā saggaṃ tacchanti</p>\r\n<p class="unindented">Bhikkhussa <a name="O0.0006"></a> dānaṃ deti sappuriso</p>\r\n<p class="unindented">Bhikkhūnaṃ dānaṃ denti sappurisā</p>\r\n<p class="unindented">Bhikkhunā saggaṃ labheyya saṅo</p>\r\n<p class="unindented">Bhikkhūhi saggaṃ labheyyuṃ sappurisā</p>\r\n<p class="unindented">Bhikkhuno pattacīvarampi bhavati</p>\r\n<p class="unindented">Bhikkhūnaṃ pattacīvarāni bhavanti</p>\r\n<p class="unindented">Bhikkhusmiṃ me ramati mano</p>\r\n<p class="unindented">Bhikkhūsu saddho sappuriso pasīdati.</p>\r\n<p class="centered">Iti navamo pāṭho.</p>\r\n<p class="centered">Ukārantapulliṅgo jantusaddo vuccate.</p>\r\n<p class="unindented">Jantu devadattaṃ kaṭaṃ kārāpeti</p>\r\n<p class="unindented">Jantuno devadatte kaṭe kārāpenti</p>\r\n<p class="unindented">Bhojantu tvampi devadattaṃ kaṭaṃ kāresi</p>\r\n<p class="unindented">Bho jantuno devadatte kaṭe kāretha.</p>\r\n<p class="unindented">Jantumpetaṃ kaṭaṃ kāremi teneva kaṭe vā</p>\r\n<p class="unindented">Jantupete kaṭaṃ kārema teneva kaṭe vā</p>\r\n<p class="unindented">Jantunā puriso vihāraṃ vihārevā kārāpīyate</p>\r\n<p class="unindented">Jantūhi puriso vihāraṃ vihārevā kārito</p>\r\n<p class="unindented">Jantunā riyena yo koci sukhaṃ pāpuṇati</p>\r\n<p class="unindented">Jantūhi ariyehi ye keci sukhaṃ pāpuṇanti</p>\r\n<p class="unindented">Jantuno ariyassa dhanaṃ dadāti dhanavanto</p>\r\n<p class="unindented">Jantunamariyānaṃ dhanaṃ dadanti dhanavantā</p>\r\n<p class="unindented">Jantunāriyamhā antaradhāyati yo koci</p>\r\n<p class="unindented">Jantūhi ariyehi antaradhāyanti ye keci</p>\r\n<p class="unindented">Jantuno sakalassa phalaṃvipāko hoti</p>\r\n<p class="unindented">Jantūnaṃ sakalānaṃ pañcagatiyo honti</p>\r\n<p class="unindented">Jantumhi ariye yo koci pasīdati</p>\r\n<p class="unindented">Jantusu ariyesu ye keci pasīdanti.</p>\r\n<p class="centered">Iti dasamo pāṭho.</p>\r\n<p class="centered">Ukārantapulliṅgo satthusaddo vuccate.</p>\r\n<p class="unindented">Satthā devamanussānaṃ dhammadesanaṃ akāsi</p>\r\n<p class="unindented">Satthāro devamanussānaṃ dhammadesanaṃ akāsuṃ</p>\r\n<p class="unindented">Bho satthā tvaṃ sadevakaṃ lokaṃ pālaya</p>\r\n<p class="unindented">Bho satthāro sadevakaṃ lokaṃ pāletha</p>\r\n<p class="unindented">Satthāraṃ dhammarājānaṃ sakkaccaṃ paṇamā mahaṃ</p>\r\n<p class="unindented">Satthāre dhammarāje sakkaccaṃ paṇamāma mayaṃ</p>\r\n<p class="unindented">Satthārā <a name="O0.0007"></a> dhammarājena sīvaṃ bodhīyate loko</p>\r\n<p class="unindented">Satthārehi dhammarājehi sivaṃ bodhito loko</p>\r\n<p class="unindented">Satthārā dhammarājena accutaṃ padaṃ gacchati</p>\r\n<p class="unindented">Satthārehi dhammarājehi accutaṃ padaṃ labheyya</p>\r\n<p class="unindented">Satthu dhammarājassa pupphāni yajati loko</p>\r\n<p class="unindented">Satthānaṃ dhammarājānaṃ pupphāni yajati loko</p>\r\n<p class="unindented">Satthārā dhammarājasmā parājenti aññatitthiyā</p>\r\n<p class="unindented">Satthārehi dhammarājehi charaṃsiyo niccharanti</p>\r\n<p class="unindented">Satthuno dhammarājassa caraṇaṃ paṇamāmyahaṃ</p>\r\n<p class="unindented">Satthānaṃ dhammarājānaṃ pāde sirasā paṇāma</p>\r\n<p class="unindented">Satthari dhammarāje ko bhattiṃ na ghaṭīyati</p>\r\n<p class="unindented">Satthāresu dhammarājesu bhatti bhavabhave atthu.</p>\r\n<p class="centered">Iti ekādasamo pāṭho.</p>\r\n<p class="centered">Ukārantapulliṅgo nantusaddo vuccate.</p>\r\n<p class="unindented">Tattā pitāmahaṃ bhojanaṃ bhojayati</p>\r\n<p class="unindented">Nattāro pitāmahaṃ bhojanaṃ bhojayanti</p>\r\n<p class="unindented">Bho nattā tvampi sippaṃ uggaṇhāhi</p>\r\n<p class="unindented">Bho nattāro tumhe sippāni uggaṇhātha</p>\r\n<p class="unindented">Nattāraṃ sippaṃ pāṭheti ācariyo</p>\r\n<p class="unindented">Nattāre sippāni pāṭhenti ācariyā</p>\r\n<p class="unindented">Nattārā rukkho pupphāni avacīyate</p>\r\n<p class="unindented">Nattārehi rukkho pupphāni avacito</p>\r\n<p class="unindented">Nattārā pitāmaho sukhī jāto</p>\r\n<p class="unindented">Nattārehi pittāmahā sukhījātā</p>\r\n<p class="unindented">Nattussa khettavatthuṃ dadāti pitāmaho</p>\r\n<p class="unindented">Nattārānaṃ khettavatthuṃ dadanti pitāmahā</p>\r\n<p class="unindented">Nattārā pitāmaho apeto hoti</p>\r\n<p class="unindented">Nattārehi pitāmaho apeto hoti</p>\r\n<p class="unindented">Nantuno vatthābharanaṃ pana hoti</p>\r\n<p class="unindented">Nattārānaṃ kho pana vatthābharanāni honti</p>\r\n<p class="unindented">Nattari yo koci pitāmaho nappasīdati</p>\r\n<p class="unindented">Nattāresu ye keci pitāmahā nappasīdanti.</p>\r\n<p class="centered">Iti dvādasamo pāṭho.</p>\r\n<p class="centered">Ukāranta pulliṅgo pitusaddo vuccate.</p>\r\n<p class="unindented">Pitāpi puttaṃ bhojanaṃ bhojayati</p>\r\n<p class="unindented">Pitaro putte bhojanaṃ bhojayanti</p>\r\n<p class="unindented">Bhopitā <a name="O0.0008"></a> tvampi bhojanaṃ bhojehi</p>\r\n<p class="unindented">Bho pitaro tumhe bhojanaṃ bhojetha</p>\r\n<p class="unindented">Pitaraṃ sakkaccaṃ poseti putto</p>\r\n<p class="unindented">Pitare sakkaccaṃ posenti puttā</p>\r\n<p class="unindented">Pitarā putto kusalaṃ kārāpīyate</p>\r\n<p class="unindented">Pitarehi putto kusalaṃ kārāpīyate</p>\r\n<p class="unindented">Pitarā puttopi sukhaṃ pāpuṇāti</p>\r\n<p class="unindented">Pitarehi bhaginiyo sukhaṃ pāpuṇanti</p>\r\n<p class="unindented">Pitussa annapānaṃdeti putto</p>\r\n<p class="unindented">Pitarānaṃ annapānaṃ denti puttā</p>\r\n<p class="unindented">Pitarā antaradhāyāti putto</p>\r\n<p class="unindented">Pituno pāde abhivandati putto</p>\r\n<p class="unindented">Pitarānaṃ pāde abhivandati puttā</p>\r\n<p class="unindented">Pitari putto trajo pasīdati</p>\r\n<p class="unindented">Pitaresu puttoraso pasīdati.</p>\r\n<p class="centered">Iti terasamo pāṭho.</p>\r\n<p class="centered">Ukāranta pulliṅgo bhātusaddo vuccate.</p>\r\n<p class="unindented">Bhātā bhaginiṃ kusalaṃ kārāpayati</p>\r\n<p class="unindented">Bhātaro bhaginī kusalāni kārāpayanti</p>\r\n<p class="unindented">Bho bhātā tvampi kusalaṃ karohi</p>\r\n<p class="unindented">Bho bhātaro tumhe kusalāni karotha</p>\r\n<p class="unindented">Bhātaraṃ kammaṃ kārayati pubbajo</p>\r\n<p class="unindented">Bhātare kamme kārayanti pubbajā</p>\r\n<p class="unindented">Bhātarā saṅgho bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Bhātarehi saṅgho bhattaṃ bhojāpito</p>\r\n<p class="unindented">Bhātarā bhaginīpi sukhaṃ pāpuṇāti</p>\r\n<p class="unindented">Bhātarehi bhaginiyo sukhaṃ pāpuṇanti</p>\r\n<p class="unindented">Bhātussa vatthañca dadāti pubbajo</p>\r\n<p class="unindented">Bhātarānaṃ vatthāni dadanti pubbajā</p>\r\n<p class="unindented">Bhātarā antaradhāyati bhaginipi</p>\r\n<p class="unindented">Bhātarehi antaradhāyanti bhaginiyo</p>\r\n<p class="unindented">Bhātuno khettavatthūni pana vijjanti</p>\r\n<p class="unindented">Bhātarānaṃ khettavatthūni pana vijjanti</p>\r\n<p class="unindented">Bhātari jeṭṭho sammā pasīdati</p>\r\n<p class="unindented">Bhātaresu jeṭṭhā sammā pasīdanti.</p>\r\n<p class="centered">Iti cuddasamo pāṭho.</p>\r\n<p class="centered">Ūkāranta <a name="O0.0009"></a> pulliṅgo abhibhūsaddo vuccate.</p>\r\n<p class="unindented">Abhibhū tathāgato dhammadesanaṃ akāsi</p>\r\n<p class="unindented">Abhibhū tathāgatā dhammadesanaṃ akāsuṃ</p>\r\n<p class="unindented">Bho abhibhū tathāgata dhammaṃ desehi</p>\r\n<p class="unindented">Bho abhibhū tathāgatā dhammaṃ desetha</p>\r\n<p class="unindented">Abhibhuṃ tathāgataṃ sirasā namāmyahaṃ</p>\r\n<p class="unindented">Abhibhuvo tathāgate sirasā namāma mayaṃ</p>\r\n<p class="unindented">Abhibhunā tathāgatena dhammo desito</p>\r\n<p class="unindented">Abhibhūhi tathāgatehi dhammā desitā</p>\r\n<p class="unindented">Abhibhunā tathāgatena munayo sukhījātā</p>\r\n<p class="unindented">Abhibhūhi tathāgatehi lokā sukhījātā</p>\r\n<p class="unindented">Abhibhūno tathāgatassa pupphāni yajati</p>\r\n<p class="unindented">Abhibhūnaṃ tathāgatānaṃ pupphāni yajanti</p>\r\n<p class="unindented">Abhibhunā tathāgatamhā pabhā niccharati</p>\r\n<p class="unindented">Abhibhūhi tathāgatehi pabhāyo niccharanti</p>\r\n<p class="unindented">Abhibhuno tathāgatassa pāde paṇamāmi</p>\r\n<p class="unindented">Abhibhūnaṃ tathāgatānaṃ caraṇaṃ paṇamāma</p>\r\n<p class="unindented">Abhibhumhi tathāgate me ramati mano</p>\r\n<p class="unindented">Abhibhūsu tathāgatesu manaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti pañcadasamo pāṭho.</p>\r\n<p class="centered">Ūkāranta pulliṅgo sabbaññusaddo vuccate.</p>\r\n<p class="unindented">Sabbaññu lokanātho dhammaṃ deseti</p>\r\n<p class="unindented">Sabbaññu lokanāthā dhammaṃ desenti</p>\r\n<p class="unindented">Bho sabbaññu lokanātha dhammaṃ desehi</p>\r\n<p class="unindented">Bho sabbaññu lokanāthā dhammaṃ desetha</p>\r\n<p class="unindented">Sabbaññuṃ lokanāthaṃ passati mahājano</p>\r\n<p class="unindented">Sabbaññuno lokanāthe passati mahājano</p>\r\n<p class="unindented">Sabbaññunā lokanāthena dhammo desīyate</p>\r\n<p class="unindented">Sabbaññūhi lokanāthehi dhammo desito</p>\r\n<p class="unindented">Sabbaññunā lokanāthena sivapadaṃ yanti</p>\r\n<p class="unindented">Sabbaññūhi lokanāthehi lokā sukhījātā</p>\r\n<p class="unindented">Sabbaññuno lokanāthassa jīvitaṃ pariccajāmi</p>\r\n<p class="unindented">Sabbaññūnaṃ lokanāthānaṃ jīvitaṃ pariccajāmi</p>\r\n<p class="unindented">Sabbaññunā lokanāthasmā sivapadaṃ labheyya</p>\r\n<p class="unindented">Sabbaññūhi lokanāthehi sivapadi labheyyuṃ</p>\r\n<p class="unindented">Sabbaññuno <a name="O0.0010"></a> lokanāthassa caraṇaṃ vandāmi</p>\r\n<p class="unindented">Sabbañuñūnaṃ lokanāthānaṃ pāde vandāma</p>\r\n<p class="unindented">Sabbaññusmiṃ lokanāthe loko pasīdati</p>\r\n<p class="unindented">Sabbaññūsu lokanāthesu lokā pasīdanti.</p>\r\n<p class="centered">Iti solasamo pāṭho.</p>\r\n<p class="centered">Okāranta pulliṅgo gosaddo vuccate.</p>\r\n<p class="unindented">Go usabho ujuṃ gacchati</p>\r\n<p class="unindented">Gāvo usabhā ujuṃ gacchanti</p>\r\n<p class="unindented">Bho go usabhā ujuṃ gacchāhi</p>\r\n<p class="unindented">Gāvuṃ vajaṃ rundhati gopālo</p>\r\n<p class="unindented">Gāvo vaje rundhati gopālā</p>\r\n<p class="unindented">Gāvena sakaṭo ānīyate</p>\r\n<p class="unindented">Gohi sakaṭā ānīyante</p>\r\n<p class="unindented">Gāvena gomiko jīvikaṃ kappeti</p>\r\n<p class="unindented">Gohi gomikā jīvikaṃ kappenti</p>\r\n<p class="unindented">Gāvassa tiṇaṃ dadāti gopālo</p>\r\n<p class="unindented">Gonaṃ tiṇaṃ dadanti gopālā</p>\r\n<p class="unindented">Gāvā usabhasmā bhayaṃ upjajjati</p>\r\n<p class="unindented">Gohi usabhehi bhayāni jāyanti</p>\r\n<p class="unindented">Gāvassa usabhassa dhavalo guṇo</p>\r\n<p class="unindented">Gavaṃ usabhānaṃ guṇā pamāṇaṃ</p>\r\n<p class="unindented">Bhāve usabhe gomiko pasīdati</p>\r\n<p class="unindented">Gosu usabhesu gomikā pasīdanti.</p>\r\n<p class="centered">Iti sattadasamo pāṭho.</p>\r\n<p class="centered">Iti padamañjariyā pulliṅganāmānaṃ.</p>\r\n<p class="centered">Paṭhamo paricchedo.</p>\r\n<p class="centered">Atha akārantetthiliṅgo kaññāsaddo vuccate.</p>\r\n<p class="unindented">Kaññā dāsiṃ kammaṃ kārāpayati</p>\r\n<p class="unindented">Kaññāyo dāsī kamme kārāpayanti</p>\r\n<p class="unindented">Bho kaññe tvampi kusalaṃ karohi</p>\r\n<p class="unindented">Bho kaññāyo tumhe kusalaṃ karotha</p>\r\n<p class="unindented">Kaññaṃ kusalaṃ kārāpeti mātā</p>\r\n<p class="unindented">Kaññāyo kusalāni kārenti mātāpitaro</p>\r\n<p class="unindented">Kaññāya <a name="O0.0011"></a> tilānipi bhajjīyante</p>\r\n<p class="unindented">Kaññāhi dhaññānipi bhajjīyante</p>\r\n<p class="unindented">Kaññāya koci puriso sucarati</p>\r\n<p class="unindented">Kaññāhi keci purisā sucaranti</p>\r\n<p class="unindented">Kaññāya ābharaṇāni dadāti puriso</p>\r\n<p class="unindented">Kaññānaṃ ābharaṇāni dadāti puriso</p>\r\n<p class="unindented">Kaññāya apeti koci puriso</p>\r\n<p class="unindented">Kaññāhi apenti keci purisā</p>\r\n<p class="unindented">Kaññāya vatthābharaṇānipi honti</p>\r\n<p class="unindented">Kaññānaṃ vatthābharaṇānipi honti</p>\r\n<p class="unindented">Kaññāyaṃ koci puriso pasīdati</p>\r\n<p class="unindented">Kaññāsu keci purisā pasīdanti.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="centered">Ikāranta itthiliṅgo rattisaddo vuccate.</p>\r\n<p class="unindented">Ratti juṇhā sammā virocati</p>\r\n<p class="unindented">Rattiyo juṇhāyo virocanti</p>\r\n<p class="unindented">Bho ratti juṇhā sammā viroca</p>\r\n<p class="unindented">Bho rattiyo juṇhāyo virocatha</p>\r\n<p class="unindented">Rattiṃ na oloketvā dhammaṃ suṇomi</p>\r\n<p class="unindented">Rattiyo na oloketvā dhammaṃ suṇoma</p>\r\n<p class="unindented">Rattiyā yo koci maggo rundhīyati</p>\r\n<p class="unindented">Rattīhi ye keci maggā rundhīyanti</p>\r\n<p class="unindented">Rattiyā corajeṭṭho corayati</p>\r\n<p class="unindented">Rattīhi corajeṭṭhā corayanti</p>\r\n<p class="unindented">Rattiyā dīpaṃ dadāti dīpakāle</p>\r\n<p class="unindented">Rattīnaṃ dīpaṃ dadanti dīpakāle</p>\r\n<p class="unindented">Rattiyā bhojanā appaṭivirato</p>\r\n<p class="unindented">Rattīhi bhojanehi appaṭivirato</p>\r\n<p class="unindented">Rattiyā ghanāndhakāropi hoti</p>\r\n<p class="unindented">Rattīnaṃ ghanāndhakārāpi honti</p>\r\n<p class="unindented">Rattiyaṃ sūriyo na pātubhavati</p>\r\n<p class="unindented">Rattīsu uhuṅkārā gocaraṃ gaṇhanti.</p>\r\n<p class="centered">Iti dutiyo pāṭho.</p>\r\n<p class="centered">Īkāranta itthiliṅgo nadīsaddo vuccate.</p>\r\n<p class="unindented">Nadī avicchedappavatti sandati</p>\r\n<p class="unindented">Nadiyo <a name="O0.0012"></a> avicchedappavattī sandantī</p>\r\n<p class="unindented">Bho nadī avicchedappavatti jalaṃ dada</p>\r\n<p class="unindented">Bho nadī avicchedappavattī jalaṃ dadatha</p>\r\n<p class="unindented">Nadiṃ avicchedappavattiṃ passati</p>\r\n<p class="unindented">Nadiyo avicchedappavattiyo passati</p>\r\n<p class="unindented">Nadiyā āpo niccaṃ vuyhate</p>\r\n<p class="unindented">Nadīhi āpo niccaṃ vuyhate</p>\r\n<p class="unindented">Nadiyā khettaṃ vapati kassako</p>\r\n<p class="unindented">Nadīhi khettāni vapanti kassakā</p>\r\n<p class="unindented">Nadiyā visaṃ dadāti koci bālo</p>\r\n<p class="unindented">Nadīnaṃ visaṃ dadanti keci bālā</p>\r\n<p class="unindented">Nadiyā pabhavanti kunnadiyo</p>\r\n<p class="unindented">Nadiyā kho pana mahogho bhavati</p>\r\n<p class="unindented">Nadīnaṃ kho pana mahogho bhavati</p>\r\n<p class="unindented">Nadiyaṃ macchasamūho pana vicarati</p>\r\n<p class="unindented">Nadīsu macchakacchapādayo vicaranti.</p>\r\n<p class="centered">Iti tatiyo pāṭho.</p>\r\n<p class="centered">Ukāranta itthiliṅgo yāgusaddo vuccate.</p>\r\n<p class="unindented">Yāgu paccate yaññadattena</p>\r\n<p class="unindented">Yāguyo paccante yaññadattehi</p>\r\n<p class="unindented">Bho yāgu tvaṃ pana khudaṃ bhana</p>\r\n<p class="unindented">Bho yāguyo tumhe khudaṃ bhanatha</p>\r\n<p class="unindented">Yāguṃ pibati yo koci jano</p>\r\n<p class="unindented">Yāguyo pibanti ye keci janā</p>\r\n<p class="unindented">Yāguyā pana udaraggi haññati</p>\r\n<p class="unindented">Yāgūhi pana udaraggī haññanti</p>\r\n<p class="unindented">Yāguyā koci rogo vupasammati</p>\r\n<p class="unindented">Yāgūhi keci rogā vupasammanti</p>\r\n<p class="unindented">Yāguyā pana lavaṇaṃ dadāti sūdo</p>\r\n<p class="unindented">Yāgūnaṃ lavaṇāni dadanti sūdā</p>\r\n<p class="unindented">Yāguyā kho pana dhūmo apeti</p>\r\n<p class="unindented">Yāgūhi kho pana dhūmā apenti</p>\r\n<p class="unindented">Yāguyā kho pana uṇho vijjati</p>\r\n<p class="unindented">Yāgūnaṃ kho pana uṇhā vijjanti.</p>\r\n<p class="unindented">Yāguyaṃ pana sitthāni honti</p>\r\n<p class="unindented">Yāgūsu pana sitthāni honti.</p>\r\n<p class="centered">Iti <a name="O0.0013"></a> catuttho pāṭho.</p>\r\n<p class="centered">Ukāranta itthiliṅgo mātusaddo vuccate.</p>\r\n<p class="unindented">Mātā puttaṃ bhojanaṃ bhojayati</p>\r\n<p class="unindented">Mātaro putte bhojanaṃ bhojayanti</p>\r\n<p class="unindented">Bho mātā tvaṃ pana ciraṃ jīva</p>\r\n<p class="unindented">Bho mātaro tumhe ciraṃ jīvatha</p>\r\n<p class="unindented">Mātaraṃ poseti puttotrajopi</p>\r\n<p class="unindented">Mātare posenti puttotrajāpi</p>\r\n<p class="unindented">Mātarā putto bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Mātarehi putto bhattaṃ bhojāpito</p>\r\n<p class="unindented">Mātarā puttopi sukhaṃ pāpuṇāti</p>\r\n<p class="unindented">Mātarehi puttāpi sukhaṃ pāpuṇanti</p>\r\n<p class="unindented">Mātuyā annaṃ dadāti puttopi</p>\r\n<p class="unindented">Mātarānaṃ vatthāni dadanti puttāpi</p>\r\n<p class="unindented">Mātarā pana antaradhāyati putto</p>\r\n<p class="unindented">Mātarehi antaradhāyanti puttā</p>\r\n<p class="unindented">Mātuyā pana puttāpi bahavo honti</p>\r\n<p class="unindented">Mātarānaṃ puttāpi bahavo honti</p>\r\n<p class="unindented">Mātari pana oraso putto pasīdati</p>\r\n<p class="unindented">Mātaresu ye keci puttā pasīdanti.</p>\r\n<p class="centered">Iti pañcamo pāṭho.</p>\r\n<p class="centered">Ūkāranta itthiliṅgo jambusaddo vuccate.</p>\r\n<p class="unindented">Jambū pana anubhuttā tathāgatena</p>\r\n<p class="unindented">Jambuyo anubhuttāyo tathāgatehi</p>\r\n<p class="unindented">Bho jambū jambonadampi dehi</p>\r\n<p class="unindented">Bho jambuyo jambonadampi detha</p>\r\n<p class="unindented">Jambuṃ pana passati yo koci</p>\r\n<p class="unindented">Jambuyo passanti ye keci</p>\r\n<p class="unindented">Jambuyā udaraggi pana paṭihaññate</p>\r\n<p class="unindented">Jambūhi udaraggī pana paṭihaññante</p>\r\n<p class="unindented">Jambuyā kho pana yo koci jīvati</p>\r\n<p class="unindented">Jambūhi kho pana ye keci jīvanti</p>\r\n<p class="unindented">Jambuyā pana silāghate yo koci</p>\r\n<p class="unindented">Jambūnaṃ pana silāghate mahājano</p>\r\n<p class="unindented">Jambuyā <a name="O0.0014"></a> kho pana jambonadaṃ jāyati</p>\r\n<p class="unindented">Jambūhi kho pana jambonadaṃ uppajjati</p>\r\n<p class="unindented">Jambuyā pana madhurarasojā hoti</p>\r\n<p class="unindented">Jambūnaṃ madhurarasojāyo honti</p>\r\n<p class="unindented">Jambuyaṃ kho pana jambonadaṃ atthi</p>\r\n<p class="unindented">Jambusu pana jambonadāni vijjanti.</p>\r\n<p class="centered">Iti chaṭṭho pāṭho.</p>\r\n<p class="centered">Iti padamañjariyā itthiliṅganāmānaṃ.</p>\r\n<p class="centered">Dutiyo paricchedo.</p>\r\n<p class="centered">Atha akārantapuṃsakaliṅgo cittasaddo vuccate.</p>\r\n<p class="unindented">Cittaṃ attano santānaṃ vijānāti</p>\r\n<p class="unindented">Cittāni attano sattānaṃ vijānanti</p>\r\n<p class="unindented">Bho citta attano santānaṃ vijānāhi</p>\r\n<p class="unindented">Bho cittāni attano santānaṃ vijānātha</p>\r\n<p class="unindented">Cittaṃ saññamessanti ye keci janā</p>\r\n<p class="unindented">Cittāni saññamessanti ye keci</p>\r\n<p class="unindented">Cittena sabbopi jano nīyyate</p>\r\n<p class="unindented">Cittehi sabbopi jano nīyyate</p>\r\n<p class="unindented">Cittena saṃkilissati mānavo</p>\r\n<p class="unindented">Cittehi visujjhati kocimānavo</p>\r\n<p class="unindented">Cittassa ovādaṃ deti koci jano</p>\r\n<p class="unindented">Cittānaṃ ovādaṃ denti keci janā</p>\r\n<p class="unindented">Cittasmā ārammaṇaṃ uppajjati</p>\r\n<p class="unindented">Cittehi ārammaṇāni uppajjanti</p>\r\n<p class="unindented">Cittassa aniccadhammassa vasamanvagū</p>\r\n<p class="unindented">Cittānaṃ parivitakko udapādi</p>\r\n<p class="unindented">Citte arakkhite kāyakammaṃ arakkhitaṃ</p>\r\n<p class="unindented">Cittesu guttesu kāyakammaṃ rakkhitaṃ.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="centered">Akārantanapuṃsakaliṅgo manasaddo vuccate.</p>\r\n<p class="unindented">Manaṃ attano santānaṃ maññati</p>\r\n<p class="unindented">Manāni attano sattānaṃ maññanti</p>\r\n<p class="unindented">Bho mana attano santānaṃ maññāhi</p>\r\n<p class="unindented">Bho <a name="O0.0015"></a> manāni attano santānaṃ maññatha</p>\r\n<p class="unindented">Manaṃ pasādetvā saggaṃ gamissāmi</p>\r\n<p class="unindented">Mane pasādetvā saggaṃ gamissāma</p>\r\n<p class="unindented">Manena kusalākusalakammaṃ kataṃ</p>\r\n<p class="unindented">Manehi kusalākusalakammāni katāni</p>\r\n<p class="unindented">Manasā dhammaṃ vijānāti yogāvacaro</p>\r\n<p class="unindented">Manehi dhammaṃ vijānanti yogāvacarā</p>\r\n<p class="unindented">Manaso paduṭṭhassa ovādaṃ dadāti</p>\r\n<p class="unindented">Manānaṃ padūṭṭhānaṃ ovādaṃ dadanti</p>\r\n<p class="unindented">Manasāpana ārammanaṃ uppajjati</p>\r\n<p class="unindented">Manehi ārammaṇāni uppajjanti</p>\r\n<p class="unindented">Manaso aniccadhammassa vasamanvagū</p>\r\n<p class="unindented">Manānaṃ pana parivitakko udapādi</p>\r\n<p class="unindented">Mane arakkhite kāyakammaṃ arakkhitaṃ</p>\r\n<p class="unindented">Manesu guttesu kāyakammaṃ rakkhitaṃ.</p>\r\n<p class="centered">Iti dutiyo pāṭho.</p>\r\n<p class="centered">Akārantanapuṃsakaliṅgo guṇavantusaddo vuccate.</p>\r\n<p class="unindented">Guṇavaṃ kulaṃ pana puññaṃ karoti</p>\r\n<p class="unindented">Guṇavantā kulāni puññaṃ karonti</p>\r\n<p class="unindented">Bho guṇavaṃ kulaṃ puññaṃ karohi</p>\r\n<p class="unindented">Bho guṇavantā kulāni puññaṃ karotha</p>\r\n<p class="unindented">Guṇavantaṃ kulaṃ passati yo koci</p>\r\n<p class="unindented">Guṇavante kule passanti ye keci</p>\r\n<p class="unindented">Guṇavantena kulena vihāro kārito</p>\r\n<p class="unindented">Guṇavantehi kulehi vihārā kāritā</p>\r\n<p class="unindented">Guṇavantena kulena loko sucarati</p>\r\n<p class="unindented">Guṇavantehi kulehi lokā sucaranti</p>\r\n<p class="unindented">Guṇavato kulassa dhanaṃ dadāti dhanavā</p>\r\n<p class="unindented">Guṇavataṃ kulānaṃ dhanaṃ dadanti dhanavantā</p>\r\n<p class="unindented">Guṇavatā kulamhā na apeti yo koci</p>\r\n<p class="unindented">Guṇavantehi kulehi na apenti ye keci</p>\r\n<p class="unindented">Guṇavato kulassa guṇaghoso hoti</p>\r\n<p class="unindented">Guṇavantānaṃ kulānaṃ guṇaghosā honti</p>\r\n<p class="unindented">Guṇavante kulepi me ramati mano</p>\r\n<p class="unindented">Guṇavantesu kulesu manaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti <a name="O0.0016"></a> tatiyo pāṭho.</p>\r\n<p class="centered">Akārantanapuṃsakaliṅgo gaccantasaddo vuccate.</p>\r\n<p class="unindented">Gacchaṃ guṇavaṃ sundaraṃ nibbāṇaṃ gacchati</p>\r\n<p class="unindented">Gacchantā guṇavantā nibbāṇaṃ gacchanti</p>\r\n<p class="unindented">Bho gacchaṃ guṇavaṃ tvaṃ pana sugatiṃ gacchāhi</p>\r\n<p class="unindented">Bho gacchantā guṇavantā sugatiṃ gacchatha</p>\r\n<p class="unindented">Gacchantaṃ guṇavantaṃ passati ekacco</p>\r\n<p class="unindented">Gavchante guṇavante passati ekacco</p>\r\n<p class="unindented">Gacchatā guṇavantena satthaṃ sūyate</p>\r\n<p class="unindented">Gacchantehi guṇavantehi pupphaṃ gayhate</p>\r\n<p class="unindented">Gacchatā guṇavantena loko sucarati</p>\r\n<p class="unindented">Gacchantehi guṇavantehi sukhaṃ pāpuṇāti</p>\r\n<p class="unindented">Gacchato guṇavantassa anugiṇāti jano</p>\r\n<p class="unindented">Gacchataṃ guṇavantānaṃ patigiṇāti jano</p>\r\n<p class="unindented">Gacchatā guṇavantamhā apeti ekacco</p>\r\n<p class="unindented">Gaccantehi guṇavantehi apenti ekacce</p>\r\n<p class="unindented">Gacchato guṇavantassa mātāpitaro</p>\r\n<p class="unindented">Gacchataṃ guṇavantānaṃ nāmagottādi</p>\r\n<p class="unindented">Gacchante guṇavante me ramani mano</p>\r\n<p class="unindented">Gacchantesu guṇavantesu manaṃ patiṭhitaṃ.</p>\r\n<p class="centered">Iti catuttho pāṭho.</p>\r\n<p class="centered">Ikārantanapuṃsakaliṅgo aṭṭhisaddo vuccate.</p>\r\n<p class="unindented">Aṭṭhi saṅkhalikaṃ sarīraṃ paṭikkūlaṃ hoti</p>\r\n<p class="unindented">Aṭṭhini puñjakitāni paṭikkūlāni honti</p>\r\n<p class="unindented">Bho aṭṭhi saṅkhalikaṃ tvaṃ aniccato passa</p>\r\n<p class="unindented">Bho aṭṭhini setāni aniccato passatha</p>\r\n<p class="unindented">Aṭṭhiṃ samaṃsalohitaṃ asubhato passati</p>\r\n<p class="unindented">Aṭṭhini puñjakitāni aniccato passati</p>\r\n<p class="unindented">Aṭṭhinā kāyena yaṃ kiñci rūpaṃ nimmitaṃ</p>\r\n<p class="unindented">Aṭṭhīhi kāyehi yaṃ kiñci rūpaṃ nimmitaṃ</p>\r\n<p class="unindented">Aṭṭhinā nimittena bhikkhū maggaṃ bhāveti</p>\r\n<p class="unindented">Aṭṭhīhi nimittehi bhikkhū maggaṃ bhāventi</p>\r\n<p class="unindented">Aṭṭhino kāyassa ovādaṃ deti ekacco</p>\r\n<p class="unindented">Aṭṭhīnaṃ kāyānaṃ ovādi denti ekacce</p>\r\n<p class="unindented">Aṭṭhimhā <a name="O0.0017"></a> kāyasmā apeti yogāvacaro</p>\r\n<p class="unindented">Aṭṭhīhi kāyehi apenti yogāvacarā</p>\r\n<p class="unindented">Aṭṭhino kāyassa pariggaho hoti</p>\r\n<p class="unindented">Aṭṭhīnaṃ kāyānaṃ pariggaho hoti</p>\r\n<p class="unindented">Aṭṭhinī kāye yogāvacaro nappasīdati</p>\r\n<p class="unindented">Aṭṭhīsu kāyesu yogāvacarā nappasīdanti.</p>\r\n<p class="centered">Iti pañcamo pāṭho.</p>\r\n<p class="centered">Īkārantanapuṃsakaliṅgo daṇḍisaddo vuccate.</p>\r\n<p class="unindented">Daṇḍī pana purisaṃ kammaṃ kārāpayati</p>\r\n<p class="unindented">Daṇḍīni purisaṃ kammaṃ kārāpayanti</p>\r\n<p class="unindented">Bho daṇḍī tvaṃ pana kammaṃ karohi</p>\r\n<p class="unindented">Bho daṇḍīni tumhe kammaṃ karotha</p>\r\n<p class="unindented">Daṇḍiṃ daṇḍakammaṃ kārayati amacco</p>\r\n<p class="unindented">Daṇḍīni daṇḍakamme kārayanti amaccā</p>\r\n<p class="unindented">Daṇḍinā jano daṇḍakammaṃ vedīyate</p>\r\n<p class="unindented">Daṇḍīhi jano daṇḍakammaṃ vedito</p>\r\n<p class="unindented">Daṇḍinā yo koci pana santajjeti</p>\r\n<p class="unindented">Daṇḍīhi ye keci pana santajjenti</p>\r\n<p class="unindented">Daṇḍino daṇḍakammaṃ deti amacco</p>\r\n<p class="unindented">Daṇḍīnaṃ daṇḍakammaṃ denti amaccā</p>\r\n<p class="unindented">Daṇḍinā apeti yo koci puriso</p>\r\n<p class="unindented">Daṇḍīhi apenti ye keci purisā</p>\r\n<p class="unindented">Daṇḍino yo koci pariggaho hoti</p>\r\n<p class="unindented">Daṇḍīnaṃ ye keci pariggahā honti</p>\r\n<p class="unindented">Daṇḍini pana me mano na ramati</p>\r\n<p class="unindented">Daṇḍīsu kho pana me manā na ramanti.</p>\r\n<p class="centered">Iti chaṭṭho pāṭho.</p>\r\n<p class="centered">Ukāranta napuṃsakaliṅgo āyusaddo vuccate.</p>\r\n<p class="unindented">Āyu cassā pana parikkhiṇo ahosi</p>\r\n<p class="unindented">Āyūni pana tesaṃ parikkhīṇā ahesuṃ</p>\r\n<p class="unindented">Bho āyu tvaṃ pana jīvitaṃ pālehi</p>\r\n<p class="unindented">Bho āyūni tumhe jīvitaṃ pālathe</p>\r\n<p class="unindented">Āyuṃ arūpadhammaṃ passati sammā sambuddho</p>\r\n<p class="unindented">Āyūni arūpadhamme passati lokanātho</p>\r\n<p class="unindented">Āyunā arūpadhammena jīvitaṃ pavattitaṃ</p>\r\n<p class="unindented">Āyūhi <a name="O0.0018"></a> arūpadhammehi jīvitaṃ pavattitaṃ</p>\r\n<p class="unindented">Āyunā arūpadhammena loko jīvati</p>\r\n<p class="unindented">Āyūhi arūpadhammehi loko jīvanti</p>\r\n<p class="unindented">Āyuno ruccati sabbopi jano</p>\r\n<p class="unindented">Āyūnaṃ ruccanti sabbepi janā</p>\r\n<p class="unindented">Āyūnā kho pana apeti jīvitampi</p>\r\n<p class="unindented">Āyūhi kho pana apenti jīvitāni</p>\r\n<p class="unindented">Āyūno pana parihāro hoti sabbadā</p>\r\n<p class="unindented">Āyūnaṃ pana parihāro hoti sabbadā</p>\r\n<p class="unindented">Āyūmhi kho pana manaṃ patiṭhitaṃ sabbadā</p>\r\n<p class="unindented">Āyūsu kho pana manaṃ patiṭṭhitaṃ sabbadā.</p>\r\n<p class="centered">Iti sattamo pāṭho.</p>\r\n<p class="centered">Iti padamañjariyā napuṃsakaliṅganāmānaṃ.</p>\r\n<p class="centered">Tatiyo paricchedo.</p>\r\n<p class="gatha1">Ito paraṃ pavakkhāmi sabbanāmaṃca tassamaṃ;</p>\r\n<p class="gathalast">Nāmaṃca yojitaṃ nānā nāmeheva visesato.</p>\r\n<p class="gatha1">Yāni honti tiliṅgāni anukūlāni yāni ca;</p>\r\n<p class="gathalast">Tiliṅgānaṃ visesena padānetāni nāmato.</p>\r\n<p class="gatha1">Sabbasādhāraṇā kāni nāmānicceva atthato;</p>\r\n<p class="gathalast">Sabbanāmāni vuccanti sattavīsati saṅkhato.</p>\r\n<p class="gatha1">Tesu kānici rūpehi sesāññehica yujjare;</p>\r\n<p class="gathalast">Kānici pana saheva etesaṃ lakkhaṇaṃ idaṃ.</p>\r\n<p class="gatha1">Etasmā lakkhaṇā muttaṃ napadaṃ sabbanāmikaṃ;</p>\r\n<p class="gathalast">Tasmātītādayo saddā guṇanāmāni vuccareti.</p>\r\n<p class="centered">Atha pulliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Sabbo sotari nāvāhi mahātitthe mahājano;</p>\r\n<p class="gathalast">Sabbe antaradhāyanti satamāyugate sati.</p>\r\n<p class="gatha1">Bho sabbā bhuta kalyāṇaṃ karohi kusalā sadā;</p>\r\n<p class="gathalast">Bho sabbe purisā bhaddaṃ karotha kusalaṃ sadā.</p>\r\n<p class="gatha1">Sabbaṃ bhaṇḍaṃ samodhāya tuṭṭhacitto mahīpati;</p>\r\n<p class="gathalast">Sabbe bhojāpayī te tu sā nakhīyittha bhojanaṃ.</p>\r\n<p class="gatha1">Sabbena <a name="O0.0019"></a> sādhulokena anubhuttaṃ subhaṃ phalaṃ;</p>\r\n<p class="gathalast">Sabbehi sādhujantuhi anubhuttaṃ kammaphalaṃ.</p>\r\n<p class="gatha1">Sabbena puññakammena pappoti vipulaṃ sukhaṃ;</p>\r\n<p class="gathalast">Sabbehi guṇavantehi papponti vipulaṃ sukhaṃ.</p>\r\n<p class="gatha1">Sabbassa bhikkhusaṅghassa mahādānaṃ dadanti ca;</p>\r\n<p class="gathalast">Sabbesaṃ sīlavantānaṃ dānaṃ denti mahājanā.</p>\r\n<p class="gatha1">Sabbasmā sādhulokasmā apentīti dubuddhino;</p>\r\n<p class="gathalast">Sabbehi bhagavantehi niccharanti charaṃsiyo.</p>\r\n<p class="gatha1">Sabbassa puññakammassa vipāko hoti sabbadā;</p>\r\n<p class="gathalast">Sabbesaṃ silavantānaṃ sīlagandho anuttaro.</p>\r\n<p class="gatha1">Sabbasmiṃ buddhadhamme ca sadā ramati me mano;</p>\r\n<p class="gathalast">Sabbesu ca vihāresu thūpe kāresi khattiyoti.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Sabbā alaṅkatā laṅkā therassa viya āsi ca;</p>\r\n<p class="gathalast">Sabbā te phāsukā bhaggā gahakūṭaṃ visaṃ khitaṃ.</p>\r\n<p class="gatha1">Bho sabbe ca paje tvampi dānaṃ dadāhi sabbadā;</p>\r\n<p class="gathalast">Bho sabbāyo pajā tumhe sīlaṃ rakkhatha sabbadā.</p>\r\n<p class="gatha1">Sabbaṃ diṭṭhiṃ jahitvāna sammādiṭṭhiṃca bhāvaye;</p>\r\n<p class="gathalast">Sabbāyo diṭṭhiyo hantvā khemaṃ gacchanti paṇḍitā.</p>\r\n<p class="gatha1">Sabbassā assu kaññāya niccaṃ kammaṃ karīyyate;</p>\r\n<p class="gathalast">Sabbāhi cāpi itthīhi pāpakammaṃ karīyyate.</p>\r\n<p class="gatha1">Sabbassā pana vijjāya jīvantīti mahājanā;</p>\r\n<p class="gathalast">Sabbāhi ca nadīheva khettaṃ vapati kassako.</p>\r\n<p class="gatha1">Sabbassā assu kaññāya cittaṃ nadeti paṇḍito;</p>\r\n<p class="gathalast">Sabbāsānaṃ nadīnaṃca visaṃ nadeti paṇḍito.</p>\r\n<p class="gatha1">Sabbassā pana taṇhāya vimuttassa natthi bhayaṃ;</p>\r\n<p class="gathalast">Sabbāhi pana kaññāhi abhirūpāṅganā ayaṃ.</p>\r\n<p class="gatha1">Sabbassā assu kaññāya ābharaṇaṃ manoramaṃ;</p>\r\n<p class="gathalast">Sabbāsaṃ pana gaṅgānaṃ mahogho hoti sabbadā.</p>\r\n<p class="gatha1">Sabbassā neva kaññāya cittaṃ ramati paṇḍito;</p>\r\n<p class="gathalast">Sabbāsu ceva gaṅgāsu macchā caranti sabbadāti.</p>\r\n<p class="centered">Iti <a name="O0.0020"></a> dutiyo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Sabbaṃ puññañhi nissesaṃ manussatte samijjhati;</p>\r\n<p class="gathalast">Sabbāni assu cittāni sayamevapi bhijjare.</p>\r\n<p class="gatha1">Bho sabbā bhūta kalyāṇaṃ puññaṃ karohi sabbadā;</p>\r\n<p class="gathalast">Bho sabbānica bhūtāni puññaṃ karotha sabbadā.</p>\r\n<p class="gatha1">Sabbaṃ bhaṇḍaṃ samādāya pāraṃ tiṇṇosi brāhmaṇo;</p>\r\n<p class="gathalast">Sabbāni assu puññāni katvāna tidivaṃ gato.</p>\r\n<p class="gatha1">Sabbena pana bhūtena anubhuttaṃ kammaphalaṃ;</p>\r\n<p class="gathalast">Sabbehi guṇavantehi puññakammaṃ karīyyate.</p>\r\n<p class="gatha1">Sabbena puññakammena pappoti vipulaṃ sukhaṃ;</p>\r\n<p class="gathalast">Sabbehi guṇavantehi sucaranti bahujjanā.</p>\r\n<p class="gatha1">Sabbassa guṇavantassa dānaṃ dadeyya paṇḍito;</p>\r\n<p class="gathalast">Sabbesaṃ sīlavantānaṃ dānaṃ dadeyya paṇḍito.</p>\r\n<p class="gatha1">Sabbasmā pāpakammasmā cittaṃ pana nivāraye;</p>\r\n<p class="gathalast">Sabbehi balavantehi apentīti keci janā.</p>\r\n<p class="gatha1">Sabbassa pāpakammassa vipāko hoti kibbisaṃ;</p>\r\n<p class="gathalast">Sabbesaṃ puññakammānaṃ vipāko hoti sobhano.</p>\r\n<p class="gatha1">Sabbasmiṃ puññakammeca sadā ramati me mano;</p>\r\n<p class="gathalast">Sabbesu sīlavantesu pasīdati mahājano.</p>\r\n<p class="centered">Iti tatiyo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Pubbo kālo atikkanto ahosi</p>\r\n<p class="unindented">Pubbe kālā ca atikkantā ahesuṃ</p>\r\n<p class="unindented">Bho pubba kāla atikkanto abhavi</p>\r\n<p class="unindented">Bho pubbe kālā atikkantā abhavittha</p>\r\n<p class="unindented">Pubbaṃ kālaṃ passati lokanātho</p>\r\n<p class="unindented">Pubbe kāle passati lokavidū</p>\r\n<p class="unindented">Pubbenācariyena sisso bodhīyī.</p>\r\n<p class="unindented">Pubbehi ācariyehi sissā bodhīyiṃsu</p>\r\n<p class="unindented">Pubbenācariyena sisso sukhījāto</p>\r\n<p class="unindented">Pubbehi ācariyehi sissā sukhījāti</p>\r\n<p class="unindented">Pubbassācariyassa sakkāraṃ akarī</p>\r\n<p class="unindented">Pubbesaṃ <a name="O0.0021"></a> ācariyānaṃ sakkāraṃ akaruṃ</p>\r\n<p class="unindented">Pubbācariyasmā antaradhāyī antevāsiko</p>\r\n<p class="unindented">Pubbehi ācariyehi antaradhāyiṃsu antevāsikā</p>\r\n<p class="unindented">Pubbassācariyassa antevāsikā bahavo</p>\r\n<p class="unindented">Pubbesaṃ ācariyānaṃ guṇaghosā ahesuṃ</p>\r\n<p class="unindented">Pubbe dīpaṅkaro nāma satthā udapādi</p>\r\n<p class="unindented">Pubbesu aṭṭhavīsati cakkavattirājāno ahesuṃ.</p>\r\n<p class="centered">Iti catuttho pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Pubbā yā kāci kaññā bahuṃ puññaṃ akarī</p>\r\n<p class="unindented">Pubbā yā kāci kaññāyo bahuṃ puññaṃ akaruṃ</p>\r\n<p class="unindented">Bho pubbe kaññe bahuṃ puññaṃ akaro</p>\r\n<p class="unindented">Bho pubbā kaññāyo bahuṃ puññaṃ akarittha</p>\r\n<p class="unindented">Pubbaṃ yaṃ kiṃci kaññaṃ puññaṃ kārāpayī</p>\r\n<p class="unindented">Pubbā yā kāci kaññāyo puññe kārāpayī</p>\r\n<p class="unindented">Pubbāya yāya kāyaci kaññāya puññaṃ kataṃ</p>\r\n<p class="unindented">Pubbāhi yāhi kāhici kaññāhi puññāni katāni</p>\r\n<p class="unindented">Pubbāya yāya kāyaci kaññāya koci anucarī</p>\r\n<p class="unindented">Pubbāhi yāhi kāhici kaññāhi keci anucariṃsu</p>\r\n<p class="unindented">Pubbāya yāya kāyaci kaññāya ābharaṇaṃ adadī</p>\r\n<p class="unindented">Pubbāsaṃ yāsaṃci kaññānaṃ ābharaṇāti adadiṃsu</p>\r\n<p class="unindented">Pubbāya yāya kāyaci kaññāya koci puriso apeto.</p>\r\n<p class="unindented">Pubbāhi yāya kāhici kaññāhi keci purisā apetā</p>\r\n<p class="unindented">Pubbāya yāya kāyaci kaññāya mātāpitaro ahesuṃ</p>\r\n<p class="unindented">Pubbāsaṃ yāsaṃ kāsaṃci kaññānaṃ vatthābharaṇāni</p>\r\n<p class="unindented">Pubbāyaṃ yāya kāyaci kaññāyaṃ cittaṃ patiṭṭhitaṃ</p>\r\n<p class="unindented">Pubbāsu yāsu kāsuci kaññāsu cittaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti pañcamo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Pubbaṃ yaṃ kiṃci pana bahuṃ puccaṃ akarī</p>\r\n<p class="unindented">Pubbāni yāni kānici bahuṃ puññaṃ akaruṃ</p>\r\n<p class="unindented">Bho pubba bhūta tvaṃ bahuṃ puññaṃ akaro</p>\r\n<p class="unindented">Bho pubbāni bhūtāni bahuṃ puññaṃ akarittha</p>\r\n<p class="unindented">Pubbaṃ yaṃ kiṃci puññaṃ kārāpayī ekacco</p>\r\n<p class="unindented">Pubbāni yāni kānici puññe kārāpayiṃsu ekacce</p>\r\n<p class="unindented">Pubbena <a name="O0.0022"></a> yena kenaci vihāro kārāpito</p>\r\n<p class="unindented">Pubbehi yehi kehici vihārā kārāpitā</p>\r\n<p class="unindented">Pubbena yena kenaci puriso sukhījāto</p>\r\n<p class="unindented">Pubbehi yehi kehici purisā sukhījātā</p>\r\n<p class="unindented">Pubbassa yassa kassaci silāghate ekacco</p>\r\n<p class="unindented">Pubbesaṃ yesaṃ kesaṃci silāghate ekacco</p>\r\n<p class="unindented">Pubbā yasmā kasmāca ekacco apeto</p>\r\n<p class="unindented">Pubbehi yehi kehici ekacce apetā</p>\r\n<p class="unindented">Pubbassa yassa kassaci pariggaho ahosi</p>\r\n<p class="unindented">Pubbesaṃ yesaṃ kesaṃci pariggahā ahesuṃ</p>\r\n<p class="unindented">Pubbe yasmiṃ kasmiṃci cittaṃ patiṭṭhitaṃ</p>\r\n<p class="unindented">Pubbesu yesu kesuci cittaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti chaṭṭho pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Eko puriso devadattaṃ odanaṃ pāceti</p>\r\n<p class="unindented">Eke purisā devadattaṃ odanaṃ pācenti</p>\r\n<p class="unindented">Ekaṃ sissaṃ dhammaṃ pāṭheti ācariyo</p>\r\n<p class="unindented">Eke sisse dhammaṃ pāṭhenti ācariyā</p>\r\n<p class="unindented">Ekena garunā sisso dhammaṃ bodhāpīyate</p>\r\n<p class="unindented">Ekehi garūhi sisso dhammaṃ bodhāpito</p>\r\n<p class="unindented">Ekena garunā antevāsiko sukhījāto</p>\r\n<p class="unindented">Ekehi garūhi antevāsikā sukhījātā</p>\r\n<p class="unindented">Ekassa garuno sakkāraṃ karoti sisso</p>\r\n<p class="unindented">Ekesaṃ garūnaṃ sakkāraṃ karonti sissā</p>\r\n<p class="unindented">Ekamhā garunā sikkhaṃ gaṇhāti sisso</p>\r\n<p class="unindented">Ekehi garūhi sikkhaṃ gaṇhanti sissā</p>\r\n<p class="unindented">Ekassa garuno kho pana parikkhāro hoti</p>\r\n<p class="unindented">Ekesaṃ garūnaṃ kho pana guṇaghoso hoti</p>\r\n<p class="unindented">Ekamhi garusmiṃ pana sisso pasīdati</p>\r\n<p class="unindented">Ekesu garūsu pana sissā pasīdanti.</p>\r\n<p class="centered">Iti sattamo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Ekā kaññā pana devadattaṃ kambalaṃ yācate</p>\r\n<p class="unindented">Ekā kaññāyo devadattaṃ kambalaṃ yācante</p>\r\n<p class="unindented">Ekaṃ kaññaṃ odanaṃ pācāpayati puriso</p>\r\n<p class="unindented">Ekā <a name="O0.0023"></a> kaññāyo odanaṃ pācāpayanti purisā</p>\r\n<p class="unindented">Ekāya kaññāya odano pacitvā bhujjate</p>\r\n<p class="unindented">Ekāhi kaññāhi odano pacitvā bhutto</p>\r\n<p class="unindented">Ekāya kaññāya sukhaṃ pāpuṇāti ekacco</p>\r\n<p class="unindented">Ekāhi kaññāhi sukhaṃ pāpuṇanti ekacce</p>\r\n<p class="unindented">Ekāya kaññāya ābharaṇaṃ deti puriso</p>\r\n<p class="unindented">Ekāsaṃ kaññānaṃ ābharaṇāni denti purisā</p>\r\n<p class="unindented">Ekāya kaññāya bhayaṃ uppajjati silavataṃ</p>\r\n<p class="unindented">Ekāhi kaññāhi bhayāni uppajjanti sīlavataṃ</p>\r\n<p class="unindented">Ekāya kaññāya pana vatthābharaṇaṃ hoti</p>\r\n<p class="unindented">Ekāsaṃ kaññānaṃ pana vatthābharaṇāni honti</p>\r\n<p class="unindented">Ekāyaṃ kaññāyaṃ yo kocipasīdati</p>\r\n<p class="unindented">Ekāsu kaññāsu ye keci pasīdanti.</p>\r\n<p class="centered">Iti aṭṭhamo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Ekaṃ kulaṃ pana bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Ekāni kulāni bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Ekaṃ kulaṃ puññaṃ kārāpeti guṇavā</p>\r\n<p class="unindented">Ekāni kulāni puññaṃ kārāpeti guṇavā</p>\r\n<p class="unindented">Ekena kulena bhikkhu bhattaṃ bhojāpito</p>\r\n<p class="unindented">Ekehi kulehi bhikkhū bhattaṃ bhojāpitā</p>\r\n<p class="unindented">Ekena kulena sukhaṃ pāpuṇāti bhikkhu</p>\r\n<p class="unindented">Ekehi kulehi sukhaṃ pāpuṇanti bhikkhū</p>\r\n<p class="unindented">Ekassa kulassa pana usūyati dujjano</p>\r\n<p class="unindented">Ekesaṃ kulānaṃ pana usūyanti dujjanā</p>\r\n<p class="unindented">Ekasmā kulamhā yo koci pabbajito</p>\r\n<p class="unindented">Ekehi kulehi ye keci pabbajitā</p>\r\n<p class="unindented">Ekassa kulassa pana nāma gottādi</p>\r\n<p class="unindented">Ekesaṃ kulānaṃ pana nāma gottādayo</p>\r\n<p class="unindented">Ekasmiṃ kulamhi yo koci pasīdati</p>\r\n<p class="unindented">Ekesu kulesu ye keci pasīdanti.</p>\r\n<p class="centered">Iti navamo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Yo koci taṃ purisaṃ odanaṃ pācāpeti</p>\r\n<p class="unindented">Ye keci taṃ purisaṃ odanaṃ pācāpenti</p>\r\n<p class="unindented">Yaṃ kiṃci dāsaṃ gāmaṃ gamayati sāmiko</p>\r\n<p class="unindented">Ye <a name="O0.0024"></a> keci dāse gāmaṃ gamayati sāmiko</p>\r\n<p class="unindented">Yena kenaci sūdena odano pācāpīyate</p>\r\n<p class="unindented">Yehi kehici sūdehi odano pācāpito</p>\r\n<p class="unindented">Yena kenaci sukhaṃ pāpuṇāti bhikkhusaṅgho</p>\r\n<p class="unindented">Yehi kehici sukhaṃ pāpuṇāti bhikkhusaṅgho</p>\r\n<p class="unindented">Yassa kassaci dānaṃ deti saddho sappuriso</p>\r\n<p class="unindented">Yesaṃ kesaṃci dānaṃ denti sappurisā</p>\r\n<p class="unindented">Yasmā kasmāci garuṇā antaradhāyati sisso</p>\r\n<p class="unindented">Yehi kehici garūhi antaradhāyanti sissā</p>\r\n<p class="unindented">Yassa kassaci bhikkhuno pāde vandāmi</p>\r\n<p class="unindented">Yesaṃ kesaṃci bhikkhūnaṃ pāde vandāma</p>\r\n<p class="unindented">Yasmiṃ kasmiṃci āsane nisīdati koci</p>\r\n<p class="unindented">Yesu kesuci āsanesu nisīdanti keci.</p>\r\n<p class="centered">Iti dasamo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Yā kāci vanitā pana dāsiṃ kammaṃ kārāpeti</p>\r\n<p class="unindented">Yā kāci vanitāyo dāsī kamme kārāpenti</p>\r\n<p class="unindented">Yaṃ kiṃci vanitaṃ puññaṃ kārāpeti puriso</p>\r\n<p class="unindented">Yā kāci vanitāyo puññaṃ kārenti purisā</p>\r\n<p class="unindented">Yāya kāyaci vanitāya sāmi bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Yāhi kāhici vanitāhi sāmi bhattaṃ bhojāpito</p>\r\n<p class="unindented">Yāya kāyaci vanitāya sukhaṃ pāpuṇāti sāmiko</p>\r\n<p class="unindented">Yāhi kāhici vanitāhi sukhaṃ pāpuṇanti sāmikā</p>\r\n<p class="unindented">Yāya kāyaci vanitāya ābharaṇaṃ deti sāmiko</p>\r\n<p class="unindented">Yāsaṃ kāsaṃci vanitānaṃ ābharaṇaṃ deti sāmiko</p>\r\n<p class="unindented">Yāya kāyaci vanitāya apeti yo koci</p>\r\n<p class="unindented">Yāhi kāhici vanitāhi apeti yo koci</p>\r\n<p class="unindented">Yāya kāyaci vanitāya puttāpi bahavo</p>\r\n<p class="unindented">Yāsaṃ kāsaṃci vanitānaṃ ābharaṇāni honti</p>\r\n<p class="unindented">Yāyaṃ kāyaṃci vanitāyaṃ me cittaṃ na ramati</p>\r\n<p class="unindented">Yāsu kāsuci vanitāsu me cittāni na ramanti.</p>\r\n<p class="centered">Iti ekādasamo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Yaṃ kiṃci kulaṃ pana bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Yāni kānici kulāni bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Yaṃ kiṃci bahuṃ puññaṃ kārāpeti koci</p>\r\n<p class="unindented">Yāni <a name="O0.0025"></a> kānici bahuṃ puññaṃ kārenti keci</p>\r\n<p class="unindented">Yena kenaci kulena sakkāro kato</p>\r\n<p class="unindented">Yehi kehici kulehi sakkārā katā</p>\r\n<p class="unindented">Yena kenaci kulena ekacco jīvati</p>\r\n<p class="unindented">Yehi kehici kulehi ekacce jīvanti</p>\r\n<p class="unindented">Yassa kassaci kulassa upakāraṃ akāsi</p>\r\n<p class="unindented">Yesaṃ kesaṃci kulānaṃ upakāraṃ akāsuṃ</p>\r\n<p class="unindented">Yasmā kasmāci kulamhā apeti ekacco</p>\r\n<p class="unindented">Yehi kehici kulehi apenti ekacce</p>\r\n<p class="unindented">Yassa kassaci kulassa nāma gottādi</p>\r\n<p class="unindented">Yesaṃ kesaṃci kulānaṃ nāma gottādayo</p>\r\n<p class="unindented">Yasmiṃ kasmiṃci kule ekacco pasīdati</p>\r\n<p class="unindented">Yesu kesuci kulesu ekacce pasīdanti.</p>\r\n<p class="centered">Iti dvādasamo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">So sūdajeṭṭho sūdena odanaṃ pāceti</p>\r\n<p class="unindented">Te sūdajeṭṭhā sūdehi odanaṃ pācenti</p>\r\n<p class="unindented">Taṃ yaññadattaṃ kambalaṃ yācate brāhmaṇo</p>\r\n<p class="unindented">Te yaññadattena kambalaṃ yācante brāhmaṇā</p>\r\n<p class="unindented">Tena brāhmaṇena gahapati dhanaṃ yācīyate</p>\r\n<p class="unindented">Tehi sissehi garu satthaṃ pucchīyate</p>\r\n<p class="unindented">Tena pupphena buddhaṃ yajati sappuriso</p>\r\n<p class="unindented">Tehi pupphehi buddhaṃ yajanti sappurisā</p>\r\n<p class="unindented">Tassa bhikkhussa dānaṃ deti sappuriso</p>\r\n<p class="unindented">Tesaṃ yācakānaṃ dhanaṃ dadāti dhanavā</p>\r\n<p class="unindented">Tamhā himavatā pabhavanti pañcamahā nadiyo</p>\r\n<p class="unindented">Tehi lobhanīyehi dhammehi suddho asaṃsaṭṭho</p>\r\n<p class="unindented">Tassa buddhassa pacchato pacchato anubandhiṃsu</p>\r\n<p class="unindented">Tesaṃ bhikkhūnaṃyeva pattacīvarāni honti</p>\r\n<p class="unindented">Tasmiṃ āsaneyeva nisīdati bhikkhu</p>\r\n<p class="unindented">Tesu bhikkhūsu pana me mano ramati.</p>\r\n<p class="centered">Iti terasamo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Sā khattiyakaññā pana bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Tā khattiyakaññāyo bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Taṃ khattīyakaññaṃ puññaṃ kārāpeti rājā</p>\r\n<p class="unindented">Tā <a name="O0.0026"></a> khattiyakaññāyo puññaṃ kārāpeti rājā</p>\r\n<p class="unindented">Tāya khattiyakaññāya puññaṃ kārāpīyate</p>\r\n<p class="unindented">Tāhi khattiyakaññāhi puññaṃ kārāpito</p>\r\n<p class="unindented">Tāya khattiyakaññāya mahājano sucarati</p>\r\n<p class="unindented">Tāhi khattiyakaññāhi mahājanā sucaranti</p>\r\n<p class="unindented">Tāya khattiyakaññāya upatiṭṭheyya amacco</p>\r\n<p class="unindented">Tāsaṃ khattiyakaññānaṃ upatiṭṭheyyuṃ amaccā</p>\r\n<p class="unindented">Tāya khattiyakaññāya pana bhayaṃ uppajjati</p>\r\n<p class="unindented">Tāhi khattiyakaññāhi pana bhayāni uppajjanti</p>\r\n<p class="unindented">Tāya khattiyakaññāya pana vatthābharaṇāni</p>\r\n<p class="unindented">Tāsaṃ khattiyakaññānaṃ vatthābharaṇāni honti</p>\r\n<p class="unindented">Tāyaṃ khattiya kaññāyaṃ pasīdati yo koci</p>\r\n<p class="unindented">Tāsu khattiyakaññāsu pasīdanti ye keci.</p>\r\n<p class="centered">Iti cuddasamo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Taṃ kulaṃ niccaṃ bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Tāni kulāni pana bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Taṃ kulaṃ uddissa puññaṃ karoti koci</p>\r\n<p class="unindented">Tāni kulāni uddissa puññāni karonti keci</p>\r\n<p class="unindented">Tena kulena puññakammaṃ kārāpīyate</p>\r\n<p class="unindented">Tehi kulehi puññakammaṃ kārāpito</p>\r\n<p class="unindented">Tena kulena bahujjano sucarati</p>\r\n<p class="unindented">Tehi kulehi bahujjanā sucaranti</p>\r\n<p class="unindented">Tassa kulassa upakārañca akāsi</p>\r\n<p class="unindented">Tesaṃ kulānaṃ upakārañca akaṃsu</p>\r\n<p class="unindented">Tasmā kulamhā yo koci apeti</p>\r\n<p class="unindented">Tehi kulehi ye keci apenti</p>\r\n<p class="unindented">Tassa kulassa mahābhogo hoti</p>\r\n<p class="unindented">Tesaṃ kulānaṃ mahaddhano ca hoti</p>\r\n<p class="unindented">Tamhi kulasmiṃ pasīdati yo koci</p>\r\n<p class="unindented">Tesu kulesu pasīdanti ye keci.</p>\r\n<p class="centered">Iti pañcadasamo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Eso sisso ca garuṃ dhammaṃ pucchati</p>\r\n<p class="unindented">Ete sissā ca garuṃ dhammaṃ pucchanti</p>\r\n<p class="unindented">Etaṃ sissaṃ dhammaṃ bodhayati garu</p>\r\n<p class="unindented">Ete <a name="O0.0027"></a> sisse dhammaṃ bodhayanti garū</p>\r\n<p class="unindented">Etena garunā sisso dhammaṃ bodhāpīyate</p>\r\n<p class="unindented">Etehi garūhi sisso dhammaṃ bodhāpito</p>\r\n<p class="unindented">Etena garunaṃ sukhaṃ pāpuṇāti sisso</p>\r\n<p class="unindented">Etehi garūhi sukhaṃ pāpuṇanti sissā</p>\r\n<p class="unindented">Etassa garuno sakkāraṃ karoti sisso</p>\r\n<p class="unindented">Etesaṃ garūnaṃ sakkāraṃ karonti sissā</p>\r\n<p class="unindented">Etasmā garunā pana antaradhāyati sisso</p>\r\n<p class="unindented">Etehi garūhi pana antaradhāyanti sissā</p>\r\n<p class="unindented">Etassa garuno antevāsikā bahavo</p>\r\n<p class="unindented">Etesaṃ garūnaṃ antevāsikā bahavo</p>\r\n<p class="unindented">Etasmiṃ garumhi pasīdati antevāsiko</p>\r\n<p class="unindented">Etesu garūsu pasīdanti antevāsikā.</p>\r\n<p class="centered">Iti soḷasamo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Esā vanitā pana dāsiṃ kammaṃ kārāpeti.</p>\r\n<p class="unindented">Etā vanitāyo dāsiṃ kammaṃ kārāpenti</p>\r\n<p class="unindented">Etaṃ vanitaṃ puññaṃ kārayati puriso</p>\r\n<p class="unindented">Etā vanitāyo puññaṃ kārayanti purisā</p>\r\n<p class="unindented">Etāya vanitāya odano pacitvā bhujjate</p>\r\n<p class="unindented">Etāhi vanitāhi odano pacitvā bhutto</p>\r\n<p class="unindented">Etāya vanitāya koci jīvikaṃ kappeti</p>\r\n<p class="unindented">Etāhi vanitāhi keci jīvikaṃ kappenti</p>\r\n<p class="unindented">Etissā vanitāya pilandhanaṃ deti puriso</p>\r\n<p class="unindented">Etāsaṃ vanitānaṃ pilandhanaṃ denti purisā</p>\r\n<p class="unindented">Etāya vanitāya pana yo koci apeti</p>\r\n<p class="unindented">Etāhi vanitāhi pana ye keci apenti</p>\r\n<p class="unindented">Etissā vanitāya pana vatthābharaṇāni honti</p>\r\n<p class="unindented">Etāsānaṃ vanitānaṃ nāma gottādayo</p>\r\n<p class="unindented">Etissaṃ vanitāyaṃ abhiramati ekacco</p>\r\n<p class="unindented">Etāsu vanitāsu abhiramanti ekacce.</p>\r\n<p class="centered">Iti sattadasamo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Etaṃ kulaṃ pana bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Etāni kulāni bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Etaṃ kulaṃ pana passati yo koci</p>\r\n<p class="unindented">Etāni <a name="O0.0028"></a> kulāni pana passanti ye keci</p>\r\n<p class="unindented">Etena kulena saṅgho bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Etehi kulehi saṅgho bhattaṃ bhojāpīto</p>\r\n<p class="unindented">Etena kulena pana jano sukhaṃ pāpuṇāti</p>\r\n<p class="unindented">Etehi kulehi pana janā sukhaṃ pāpuṇanti</p>\r\n<p class="unindented">Etassa kulassa kho pana dhanaṃ dadāti dhanavā</p>\r\n<p class="unindented">Etesaṃ kulānaṃ pana dhanaṃ dadanti dhanavantā</p>\r\n<p class="unindented">Etasmā kulamhā kho pana ekacco na apeti</p>\r\n<p class="unindented">Etehi kulehi kho pana ekacce na apenti</p>\r\n<p class="unindented">Etassa kulassa pana mahābhogo hoti</p>\r\n<p class="unindented">Etesaṃ kulānaṃ pana mahābhogā honti</p>\r\n<p class="unindented">Etasmiṃ kulamhi pana me mano ramati</p>\r\n<p class="unindented">Etesu kulesu me manāni na ramanti.</p>\r\n<p class="centered">Iti aṭṭhādasamo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Ayaṃ jano pana taṃ purisaṃ puññaṃ kārāpeti</p>\r\n<p class="unindented">Ime janā te purise puññaṃ kārāpenti</p>\r\n<p class="unindented">Imaṃ janā puññaṃ kārāpeti sappuriso</p>\r\n<p class="unindented">Ime jane puññaṃ kārāpenti sappurisā</p>\r\n<p class="unindented">Iminā janena kammaphalaṃ anubhūyate</p>\r\n<p class="unindented">Imehi janehi kammaphalāni anubhuttāni</p>\r\n<p class="unindented">Iminā janena yo koci sucarati</p>\r\n<p class="unindented">Imehi janehi ye keci sucaranti</p>\r\n<p class="unindented">Imassa janassa sakkārañca karoti</p>\r\n<p class="unindented">Imesaṃ janānaṃ sakkāraṃ karonti</p>\r\n<p class="unindented">Imamhā janamhā viññutaṃ pattosmi</p>\r\n<p class="unindented">Imehi janehi bhayāni uppajjanti</p>\r\n<p class="unindented">Imassa janassa khettavatthūni honti</p>\r\n<p class="unindented">Imesaṃ janānañca pahūtadhanadhaññāni</p>\r\n<p class="unindented">Imamhi janasmiṃ pasīdati yo koci</p>\r\n<p class="unindented">Imesu janesu pasīdanti ye keci.</p>\r\n<p class="centered">Iti ekūnavīsatimo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Ayaṃ kaññā pana bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Imā kaññāyo bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Imaṃ kaññaṃ puññaṃ kārāpeti mātā</p>\r\n<p class="unindented">Imā kaññāyo puññaṃ kārāpeti mātā</p>\r\n<p class="unindented">Imāya <a name="O0.0029"></a> kaññāya pana tilāni bhajjīyante</p>\r\n<p class="unindented">Imāhi kaññāhi pana dhaññāni bhajjīyante</p>\r\n<p class="unindented">Imāya kaññāya sukhaṃ pāpuṇāti mātā</p>\r\n<p class="unindented">Imāhi kaññāhi sukhaṃ pāpuṇāti mātā</p>\r\n<p class="unindented">Imissā kaññāya ābharaṇaṃ deti sāmi</p>\r\n<p class="unindented">Imāsaṃ kaññānaṃ ābharaṇaṃ deti sāmi</p>\r\n<p class="unindented">Imāya kaññāya pana apeti yo koci</p>\r\n<p class="unindented">Imāhi kaññāhi pana apeti yo koci</p>\r\n<p class="unindented">Imissā kaññāya pana ābharaṇāni honti</p>\r\n<p class="unindented">Imāsaṃ kaññānaṃ pana ābharaṇāni honti</p>\r\n<p class="unindented">Imissaṃ kaññāyaṃ pana cittaṃ patiṭṭhitaṃ</p>\r\n<p class="unindented">Imāsu kaññāsu cittaṃ pana napatiṭṭhitaṃ.</p>\r\n<p class="centered">Iti vīsatimo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Imaṃ cittaṃ pana attano santānaṃ vijānāti</p>\r\n<p class="unindented">Imāni cittāni attano santānaṃ vijānanti</p>\r\n<p class="unindented">Imaṃ cittaṃ saññamessanti ye keci</p>\r\n<p class="unindented">Ime citte saññamessanti ye keci</p>\r\n<p class="unindented">Iminā cittena sabbopi jano nīyyati</p>\r\n<p class="unindented">Imehi cittehi sabbe janā nīyyanti</p>\r\n<p class="unindented">Iminā cittena yo koci saṃkilissati</p>\r\n<p class="unindented">Imehi cittehi yo koci visujjhati</p>\r\n<p class="unindented">Imassa cittassa ovādaṃ deti yo koci</p>\r\n<p class="unindented">Imesaṃ cittānaṃ ovādaṃ denti ye keci</p>\r\n<p class="unindented">Imasmā cittamhā ārammaṇaṃ uppajjati</p>\r\n<p class="unindented">Imehi cittehi ārammaṇāni uppajjanti</p>\r\n<p class="unindented">Imassa cittassa aniccadhammassa vasaṃ anvagū</p>\r\n<p class="unindented">Imesānaṃ cittānaṃ parivitakko udapādi</p>\r\n<p class="unindented">Asmiṃ citte arakkhite kāyakammaṃ arakkhitaṃ</p>\r\n<p class="unindented">Imesu cittesu guttesu kāyakammaṃ rakkhitaṃ.</p>\r\n<p class="centered">Iti ekavīsatimo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Asu mahārājā catuhi saṅgahavatthūhi janaṃ toseti</p>\r\n<p class="unindented">Amū mahārājā catūhisaṅgahavatthūhi janaṃ tosenti</p>\r\n<p class="unindented">Amuṃ mahārājānaṃ sakkaccaṃ upasaṅkamati mahājano</p>\r\n<p class="unindented">Amūyo mahārāje sakkaccaṃ upasaṅkamati mahājano</p>\r\n<p class="unindented">Amunā <a name="O0.0030"></a> mahārājenapi mahāpāsādo kārāpīyate</p>\r\n<p class="unindented">Amūhi mahārājehi mahāvihāropi kārāpito</p>\r\n<p class="unindented">Amunā mahārājena kho pana mahājano sucarati</p>\r\n<p class="unindented">Amūhi mahārājehi kho pana mahājano sucarati</p>\r\n<p class="unindented">Amussa mahārājassa paṇṇākāraṃ deti mahājano</p>\r\n<p class="unindented">Amūsānaṃ mahārājānaṃ paṇṇākāraṃ denti mahājanā</p>\r\n<p class="unindented">Amusmā mahārājamhā pana mahabbhayaṃ uppajjati</p>\r\n<p class="unindented">Amūhi mahārājehi mahabbhayāni uppajjanti</p>\r\n<p class="unindented">Amūssa mahārājassa vappamaṅgalañca hoti</p>\r\n<p class="unindented">Amūsānaṃ mahārājānaṃ vappamaṅgalāni honti</p>\r\n<p class="unindented">Amusmiṃ mahārāje kho pana mahājano pasīdati</p>\r\n<p class="unindented">Amūsu mahārājesu kho pana mahājanā pasīdanti.</p>\r\n<p class="centered">Iti dvevīsatimo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Asu upāsikā pana sakkaccaṃ dhammaṃ suṇāti</p>\r\n<p class="unindented">Amuyo upāsikāyo sakkaccaṃ dhammaṃ suṇanti</p>\r\n<p class="unindented">Amuṃ upāsikaṃ puññaṃ kārāpeti saddho</p>\r\n<p class="unindented">Amuyo upāsikāyo puññaṃ kārāpeti saddho</p>\r\n<p class="unindented">Amuyā upāsikāya bhikkhu bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Amūhi upāsikāhi saṅgho bhattaṃ bhojāpito</p>\r\n<p class="unindented">Amuyā upāsikāya yo koci pana sucarati</p>\r\n<p class="unindented">Amūhi upāsikāhi ye keci pana sucaranti</p>\r\n<p class="unindented">Amussā upāsikāya dānaṃ deti sappuriso</p>\r\n<p class="unindented">Amūsaṃ upāsikānaṃ dānaṃ denti sappurisā</p>\r\n<p class="unindented">Amuyā upāsikāyapi yo koci apeti</p>\r\n<p class="unindented">Amūhi upāsikāhi ye keci pana apenti</p>\r\n<p class="unindented">Amussā upāsikāya puttāpi bahavo honti</p>\r\n<p class="unindented">Amūsānaṃ upāsikānaṃ parisāpi bahavo</p>\r\n<p class="unindented">Amussaṃ upāsikāyaṃ yo koci pasīdati</p>\r\n<p class="unindented">Amūsu upāsikāsu pana ye keci pasīdanti.</p>\r\n<p class="centered">Iti tevīsatimo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Aduṃ dhanavaṃ niccaṃ bahuṃ puññaṃ pasavati</p>\r\n<p class="unindented">Amūni dhanavantāni bahuṃ puññaṃ pasavanti</p>\r\n<p class="unindented">Aduṃ dhanavantaṃ puññaṃ kārāpeti saddho</p>\r\n<p class="unindented">Amūni dhanavantāni puññaṃ kārāpeti saddho</p>\r\n<p class="unindented">Amunā <a name="O0.0031"></a> dhanavantena bhikkhu bhattaṃ bhojāpīyate</p>\r\n<p class="unindented">Amūhi dhanavantehi bhikkhu bhattaṃ bhojāpito</p>\r\n<p class="unindented">Amunā dhanavantena mahājanopi sukhījāto</p>\r\n<p class="unindented">Amūhi dhanavattehi mahājanopi sukhījāto</p>\r\n<p class="unindented">Amuno dhanavantassa suvaṇṇachattaṃ dhārayate</p>\r\n<p class="unindented">Amūsaṃ dhanavantānaṃ upatiṭṭheyya ekacco</p>\r\n<p class="unindented">Amusmā dhanavantamhā lābhasakkāraṃ labheyya</p>\r\n<p class="unindented">Amūhi dhanavantehi lābhasakkārāni labheyyuṃ</p>\r\n<p class="unindented">Amuno dhanavantassa mahāparivāro atthi</p>\r\n<p class="unindented">Amūsānaṃ dhanavantānaṃ mahāparivārā vijjanti</p>\r\n<p class="unindented">Amumhi dhanavantasmiṃ yo koci pasīdati</p>\r\n<p class="unindented">Amūsu dhanavantesu ye keci pasīdanti.</p>\r\n<p class="centered">Iti catuvīsatimo pāṭho.</p>\r\n<p class="centered">Pulliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Kohi nāma budho loke vasaṃ kodhassa gacchati;</p>\r\n<p class="gathalast">Ke hitvā mānusaṃ dehaṃ dibbaṃ yogaṃ upaccaguṃ.</p>\r\n<p class="gatha1">Kaṃsi tvaṃ assu uddissa pabbajito ca āvuso;</p>\r\n<p class="gathalast">Keci puññāni katvāna kittakā tidivaṃ gatā.</p>\r\n<p class="gatha1">Kenāyaṃ pakato satto kuvaṃ sattassa kārako;</p>\r\n<p class="gathalast">Kehidaṃ pakataṃ bimbaṃ kvannu bibbassa kārako.</p>\r\n<p class="gatha1">Kena te tādiso vaṇṇo kena te idha mijjhati;</p>\r\n<p class="gathalast">Kehi me puññakammehi mamaṃ rakkhanti devatā.</p>\r\n<p class="gatha1">Kassa cābhirato satto sabbadukkhā pamuccati;</p>\r\n<p class="gathalast">Kesaṃ divāca rattoca sadā puññaṃ pavaḍḍhati.</p>\r\n<p class="gatha1">Kasmā naparidevesi evarūpe mahabbhaye;</p>\r\n<p class="gathalast">Kehi nāma ariyehi puthageva jano ayaṃ.</p>\r\n<p class="gatha1">Kassa tvaṃ dhammamaññāya vācaṃ bhasayi īdisaṃ;</p>\r\n<p class="gathalast">Kesaṃ te dhammamaññāya acchiduṃ bhavabhandhanaṃ.</p>\r\n<p class="gatha1">Kamhi kāle tayā vīra patthitā bodhimuttamā;</p>\r\n<p class="gathalast">Kesuddhānesu muninda sāvako paritibbutoti.</p>\r\n<p class="centered">Iti pañcavīsatimo pāṭho.</p>\r\n<p class="centered">Itthiliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Kā ca suphassayaṃ dānaṃ mañcapīṭhādikaṃ adā;</p>\r\n<p class="gathalast">Kāyo nānāvidhaṃ puññaṃ katvāna tidivaṃ gatā.</p>\r\n<p class="gatha1">Kaṃ <a name="O0.0032"></a> bhāvanañca bhāveti kaṃ sīlaṃ paripālayī;</p>\r\n<p class="gathalast">Kā nāma dāsiyo kamme kārāpayati sāmiko.</p>\r\n<p class="gatha1">Kāya upāsikāyassu dhammo ca sūyate sadā;</p>\r\n<p class="gathalast">Kāhi ca sīlavatīhi dhammo ca sūyate sadā.</p>\r\n<p class="gatha1">Kāya vijjāya me putto pāpuṇāti idaṃ sukhaṃ;</p>\r\n<p class="gathalast">Kāhi sikkhāhi me puttā pāpuṇanti idaṃ sukhaṃ.</p>\r\n<p class="gatha1">Kassā upāsikāyassu dānaṃ dadeyya dhanavā;</p>\r\n<p class="gathalast">Kāsaṃ upāsikānañca dānaṃ dadeyya guṇavā.</p>\r\n<p class="gatha1">Kāya gaṅgāya sabbāca pabhavantīti kunnadī;</p>\r\n<p class="gathalast">Kāhi ca pana nadīhi pabhavanti mahānadī.</p>\r\n<p class="gatha1">Kassā kho pana gaṅgāya mahogho hoti sabbadā;</p>\r\n<p class="gathalast">Kāsaṃ kho pana kaññānaṃ ābharaṇā bhavanti ca.</p>\r\n<p class="gatha1">Kassaṃ nadiṃ pana macchā niccaṃ vicaranti sadā;</p>\r\n<p class="gathalast">Kāsu gaṅgāsu kho macchā niccaṃ vicaranti sadā.</p>\r\n<p class="centered">Iti chabbīsatimo pāṭho.</p>\r\n<p class="centered">Napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="gatha1">Kiṃ te jaṭāhi dummedha kiṃ te ajinasāṭiyā;</p>\r\n<p class="gathalast">Kāni cittāni jāyanti kathaṃ jānema taṃ mayaṃ.</p>\r\n<p class="gatha1">Kiṃ tvaṃ attavasaṃ disvā mama dajjāsimaṃ dhanaṃ;</p>\r\n<p class="gathalast">Kāni puññāni katvāna kittakā tidivaṃ gatā.</p>\r\n<p class="gatha1">Kenassu nīyati loko kenassu parikassati;</p>\r\n<p class="gathalast">Kehi me puññakammassa nāntamevañca dissati.</p>\r\n<p class="gatha1">Kenāsi dummano tāta purisaṃ kannu yocasi;</p>\r\n<p class="gathalast">Kehi pupphehi sakkaccaṃ saddho yajati gotamaṃ.</p>\r\n<p class="gatha1">Kassa so kayirā mettiṃ tamāhu cariyaṃ budhā;</p>\r\n<p class="gathalast">Kesaṃ dānavaraṃ etaṃ dātabbañca sadādarā.</p>\r\n<p class="gatha1">Kasmā coro ahu me tvaṃ iti rājā apucchitaṃ;</p>\r\n<p class="gathalast">Kehi nāmapi hetūhi jāyantīti ime janā.</p>\r\n<p class="gatha1">Kassa te dhammamaññāya acchiduṃ bhavabhandhanaṃ;</p>\r\n<p class="gathalast">Kesaṃ majjhagato bhāti candova nabhamajjhago.</p>\r\n<p class="gatha1">Kasmiṃ me sivayo kuddhā nāhaṃ passāmi dukkaṭaṃ;</p>\r\n<p class="gathalast">Kesu puññesu yojeti sakhīnaṃ so sakhāhitoti.</p>\r\n<p class="centered">Iti <a name="O0.0033"></a> sattavīsatimo pāṭho.</p>\r\n<p class="centered">Iti padamañjariyā sabbanāmānaṃ.</p>\r\n<p class="centered">Catuttho paricchedo.</p>\r\n<p class="centered">Atha aliṅgatumhāmhasaddā vuccante.</p>\r\n<p class="unindented">Tvaṃ pana puriso puññaṃ karohi</p>\r\n<p class="unindented">Tumhe purisā puññaṃ karotha</p>\r\n<p class="unindented">Tuvaṃ pana purisaṃ puññaṃ kārāpeti</p>\r\n<p class="unindented">Tumhe purise puññāni kārāpeti</p>\r\n<p class="unindented">Tayā purisena odano paccate</p>\r\n<p class="unindented">Tumhehi purisehi odanā paccante</p>\r\n<p class="unindented">Tayā purisena ekacco jīvati</p>\r\n<p class="unindented">Tumhehi purisehi ekacce jīvanti</p>\r\n<p class="unindented">Tuyhaṃ purisassa dhanaṃ deti dhanī</p>\r\n<p class="unindented">Tumhākaṃ purisānaṃ dhanaṃ deti dhanī</p>\r\n<p class="unindented">Tayā purisamhā apeti yo koci</p>\r\n<p class="unindented">Tumhehi purisehi apenti ye keci</p>\r\n<p class="unindented">Tuyhaṃ purisassa nāma gottādayo</p>\r\n<p class="unindented">Tumhākaṃ purisānaṃ pariggaho</p>\r\n<p class="unindented">Tayi purisasmiṃ koci pasīdati</p>\r\n<p class="unindented">Tumhesu purisesu keci pasīdanti.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="unindented">Tvaṃ itthīpi odanaṃ bhutvā gacchāhi</p>\r\n<p class="unindented">Tumhe itthiyo odanaṃ bhutvā gacchatha</p>\r\n<p class="unindented">Tuvaṃ itthiṃ puññaṃ kārayati puriso</p>\r\n<p class="unindented">Tumhe itthiyo puññāni kārenti purisā</p>\r\n<p class="unindented">Tayā itthiyāpi dhaññaṃ bhajjate</p>\r\n<p class="unindented">Tumhehi itthīhi dhaññāni bhajjante</p>\r\n<p class="unindented">Tayā itthiyā dukkhaṃ pāpuṇāti koci</p>\r\n<p class="unindented">Tumhehi itthīhi sukhaṃ pāpuṇāti sāmi</p>\r\n<p class="unindented">Tuyhaṃ itthiyā ābharaṇaṃ deti sāmi</p>\r\n<p class="unindented">Tumhākaṃ itthīnaṃ ābharaṇaṃ deti sāmi</p>\r\n<p class="unindented">Tayā itthiyā apeti yo koci</p>\r\n<p class="unindented">Tumhehi itthīhi apenti ye keci</p>\r\n<p class="unindented">Tuyhaṃ itthiyā puttāpi bahavo honti</p>\r\n<p class="unindented">Tumhākaṃ itthīnaṃ vatthābharaṇāni honti</p>\r\n<p class="unindented">Tvayi itthiyā pana manaṃ patiṭṭhitaṃ</p>\r\n<p class="unindented">Tumhesu itthīsu pana manāni patiṭṭhitāni.</p>\r\n<p class="centered">Iti <a name="O0.0034"></a> dutiyo pāṭho.</p>\r\n<p class="unindented">Tvaṃ cittaṃ pana ārammaṇaṃ cintesi</p>\r\n<p class="unindented">Tumhe cittāni ārammaṇaṃ cintetha</p>\r\n<p class="unindented">Tvaṃ cittaṃ saññamessanti ye keci</p>\r\n<p class="unindented">Tumhe cittāni saññamessanti ye keci</p>\r\n<p class="unindented">Tayā cittena kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Tumhehi cittehi kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Tayā cittena buddhaṃ sarati sappuriso</p>\r\n<p class="unindented">Tumhehi cittehi buddhaṃ saranti sappurisā</p>\r\n<p class="unindented">Tuyhaṃ cittassa ovādaṃ deti koci</p>\r\n<p class="unindented">Tumhākaṃ cittānaṃ ovādaṃ denti keci</p>\r\n<p class="unindented">Tayā cittamhā kho pana bhayaṃ uppajjati</p>\r\n<p class="unindented">Tumhehi cittehi pana bhayāni uppajjanti</p>\r\n<p class="unindented">Tuyhaṃ cittassa parivitakko udapādi</p>\r\n<p class="unindented">Tumhākaṃ cittānaṃ parivitakko udapādi</p>\r\n<p class="unindented">Tayi citte kho pana kusalacittaṃ patiṭhitaṃ</p>\r\n<p class="unindented">Tumhesu cittesu kusalacittāni patiṭṭhitāni.</p>\r\n<p class="centered">Iti tatiyo pāṭho.</p>\r\n<p class="unindented">Ahaṃ purisopi puññaṃ karomi</p>\r\n<p class="unindented">Mayaṃ purisā pana puññaṃ karoma</p>\r\n<p class="unindented">Mamaṃ purisaṃ puññaṃ kārāpeti</p>\r\n<p class="unindented">Amhe purise puññāni kārāpeti</p>\r\n<p class="unindented">Mayā purisena kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Amhehi purisehi kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Mayā purisena ekacco sucarati</p>\r\n<p class="unindented">Amhehi purisehi ekacce sucaranti</p>\r\n<p class="unindented">Amhaṃ purisassa dhanaṃ dadāti dhanavā</p>\r\n<p class="unindented">Amhākaṃ purisānaṃ phalaṃ dadāti phalavā</p>\r\n<p class="unindented">Mayā purisasmā apeti ekacco</p>\r\n<p class="unindented">Amhehi purisehi bhayāni uppajjanti</p>\r\n<p class="unindented">Amhaṃ purisassa nāma gottādi</p>\r\n<p class="unindented">Amhākaṃ purisānaṃ pariggaho hoti</p>\r\n<p class="unindented">Mayi purisasmiṃ pasīdati yo koci</p>\r\n<p class="unindented">Amhesu purisesu ekacco sūratamo.</p>\r\n<p class="centered">Iti catuttho pāṭho.</p>\r\n<p class="unindented">Ahaṃ kaññā pana puññaṃ karomi</p>\r\n<p class="unindented">Mayaṃ kaññāyo puññāni karoma</p>\r\n<p class="unindented">Mamaṃ kaññaṃ puññaṃ kārāpeti</p>\r\n<p class="unindented">Amhe <a name="O0.0035"></a> kaññāyo puññaṃ kārāpeti</p>\r\n<p class="unindented">Mayā kaññāya odano paccate</p>\r\n<p class="unindented">Amhehi kaññāhi odano paccate</p>\r\n<p class="unindented">Mayā kaññāya sukhadukkhā pāpuṇāti</p>\r\n<p class="unindented">Amhehi kaññāhi sukhadukkhaṃ pāpuṇāti</p>\r\n<p class="unindented">Amhaṃ kaññāya ābharaṇaṃ deti</p>\r\n<p class="unindented">Amhākaṃ kaññānaṃ ābharaṇaṃ deti</p>\r\n<p class="unindented">Mayā kaññāya ayaṃ kaññā hīṇā</p>\r\n<p class="unindented">Amhehi kaññāhi ayaṃ kaññā adhikā</p>\r\n<p class="unindented">Amhaṃ kaññāya vatthābharaṇāni honti</p>\r\n<p class="unindented">Amhākaṃ kaññānaṃ pariggaho hoti</p>\r\n<p class="unindented">Mayi kaññāyaṃ me manaṃ napatiṭṭhitaṃ</p>\r\n<p class="unindented">Amhesu kaññāsu me manaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti pañcamo pāṭho.</p>\r\n<p class="unindented">Ahaṃ cittaṃ pana ārammaṇaṃ vijānāhi</p>\r\n<p class="unindented">Mayaṃ cittāni ārammaṇaṃ vijānātha</p>\r\n<p class="unindented">Mamaṃ cittaṃ saññamessanti ekacce</p>\r\n<p class="unindented">Amhe cittāni saññamessanti keci</p>\r\n<p class="unindented">Mayā cittena kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Amhehi cittehi kammaphalaṃ anubhuttaṃ</p>\r\n<p class="unindented">Mayā cittena buddhaṃ sarati sappuriso</p>\r\n<p class="unindented">Amhehi cittehi buddhaṃ sarati saddho</p>\r\n<p class="unindented">Mamaṃ cittassa ovādaṃ deti koci</p>\r\n<p class="unindented">Amhākaṃ cittānaṃ ovādaṃ denti keci</p>\r\n<p class="unindented">Mayā cittasmā pana bhayaṃ uppajjati</p>\r\n<p class="unindented">Amhehi cittehi bhayāni uppajjanti</p>\r\n<p class="unindented">Mamaṃ cittassa parivitakko udapādi</p>\r\n<p class="unindented">Amhākaṃ cittānaṃ parivitakko hoti</p>\r\n<p class="unindented">Mayi citte pana kusalacittaṃ patiṭṭhitaṃ</p>\r\n<p class="unindented">Amhesu cittesu kusalacittāni patiṭṭhitāni.</p>\r\n<p class="centered">Iti chaṭṭho pāṭho.</p>\r\n<p class="unindented">Gāmaṃ vo pana gaccheyyātha</p>\r\n<p class="unindented">Gāmaṃ no pana gaccheyyāma</p>\r\n<p class="unindented">Pahāya vo bhikkhave gamissāmi</p>\r\n<p class="unindented">Mā no ajja pana vikantisu</p>\r\n<p class="unindented">Katameva te pana taṃ kammaṃ</p>\r\n<p class="unindented">Katameva <a name="O0.0036"></a> me pana taṃ kammaṃ</p>\r\n<p class="unindented">Katameva vo pana kusalakammaṃ</p>\r\n<p class="unindented">Katameva no pana kusalakammaṃ</p>\r\n<p class="unindented">Dadāmi te pana gāmavarāni pañca</p>\r\n<p class="unindented">Dadāhi me gāmavaraṃ tvampi</p>\r\n<p class="unindented">Dhammaṃ vo bhikkhave desissāmi</p>\r\n<p class="unindented">Saṃvibhājetha no rajjena</p>\r\n<p class="unindented">Manussasseva te sīsaṃ pana</p>\r\n<p class="unindented">Pahūtaṃ me pana dhanaṃ sakka</p>\r\n<p class="unindented">Tuṭṭhosmiyā vo pana pakatiyā</p>\r\n<p class="unindented">Satthā no bhagavā anuppatto.</p>\r\n<p class="gatha1">Vo no te meti rūpāni padāni padato yato;</p>\r\n<p class="gathalast">Tato nāmikapantīsu natū vuttāni tāni me.</p>\r\n<p class="gatha1">Paccatte upayoge ca karaṇe sampadāniye;</p>\r\n<p class="gathalast">Sāmissa vacane ceva vo no saddo pavattati.</p>\r\n<p class="gatha1">Karaṇe sampadāne ca sāmiatthe ca āgato;</p>\r\n<p class="gathalast">Te me saddoti viññeyyo viññunā nayadassināti.</p>\r\n<p class="centered">Iti sattamo pāṭho.</p>\r\n<p class="centered">Iti padamañjariyā aliṅgasabbanāmānaṃ.</p>\r\n<p class="centered">Pañcamo paricchedo.</p>\r\n<p class="unindented">Imāni pulliṅgarūpāni vuccante</p>\r\n<p class="unindented">Dve mahārājāno rajjaṃ kārenti</p>\r\n<p class="unindented">Dvepi mahārājāno upasaṅkamati</p>\r\n<p class="unindented">Dvīhi mahārājehi saṅgāmo kato</p>\r\n<p class="unindented">Dvīhi mahārājehi raṭṭhavāsino jīvanti</p>\r\n<p class="unindented">Dvinnaṃ mahārājānaṃ pannākāraṃ deti</p>\r\n<p class="unindented">Dvīhi mahārājehi bhayāni uppajjanti</p>\r\n<p class="unindented">Dvinnaṃ mahārājānaṃ pariggaho hoti</p>\r\n<p class="unindented">Dvīsu mahārājesu manāni patiṭṭhitāni.</p>\r\n<p class="centered">Imāni itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Dve kaññāyo puññāni karonti</p>\r\n<p class="unindented">Dve kaññāyo puññāni kārāpeti</p>\r\n<p class="unindented">Dvīhi kaññāhi puññāni katāni</p>\r\n<p class="unindented">Dvīhi kaññāhi sukhadukkhaṃ pāpuṇāti</p>\r\n<p class="unindented">Dvinnaṃ kaññānaṃ ābharaṇāni deti</p>\r\n<p class="unindented">Dvīhi <a name="O0.0037"></a> kaññāhi ayaṃ kaññā adhikā</p>\r\n<p class="unindented">Dvinnaṃ kaññānaṃ vatthābharaṇaṃ hoti</p>\r\n<p class="unindented">Dvīsu kaññāsu manāni patiṭṭhitāni</p>\r\n<p class="centered">Imāni napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Dve kulāni dānādikusalaṃ karonti</p>\r\n<p class="unindented">Dve kulāni dānādikusalaṃ kārāpeti</p>\r\n<p class="unindented">Dvīhi kulehi saṅgho bhattaṃ bhojāpito</p>\r\n<p class="unindented">Dvīhi kulehi sukhaṃ pāpuṇāti saṅgho</p>\r\n<p class="unindented">Dvinnaṃ kulānaṃ sakkāraṃ karonti keci</p>\r\n<p class="unindented">Dvīhi kulehi apeti ekacco puggalo</p>\r\n<p class="unindented">Dvinnaṃ kulānaṃ nāma gottādayo</p>\r\n<p class="unindented">Dvīsu kulesu me cittaṃ patiṭṭhitaṃ.</p>\r\n<p class="centered">Iti paṭhamo pāṭho.</p>\r\n<p class="centered">Imāni pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Tayo purisā pana vihāraṃ karonti</p>\r\n<p class="unindented">Tayo purise upagacchati ekacco</p>\r\n<p class="unindented">Tīhi purisehi vihāro kārāpito</p>\r\n<p class="unindented">Tīhi purisehi jīvanti ye keci</p>\r\n<p class="unindented">Tiṇṇannaṃ purisānaṃ dhanaṃ deti dhanavā</p>\r\n<p class="unindented">Tīhi purisehi bhayāni uppajjanti</p>\r\n<p class="unindented">Tiṇṇannaṃ purisānaṃ pariggaho hoti</p>\r\n<p class="unindented">Tīsu purisesu pasīdati yo koci.</p>\r\n<p class="centered">Imāni itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Tisso itthiyo puññāni karonti</p>\r\n<p class="unindented">Tisso itthiyo puññāni kārāpeti</p>\r\n<p class="unindented">Tīhi itthīhi saṅgho bhattaṃ bhojāpito</p>\r\n<p class="unindented">Tīhi itthīhi jīvanti ekacco puriso</p>\r\n<p class="unindented">Tissannaṃ itthīnaṃ ābharaṇāni deti</p>\r\n<p class="unindented">Tīhi itthīhi apeti ekacco puriso</p>\r\n<p class="unindented">Tissannaṃ itthīnaṃ ābharaṇāni honti</p>\r\n<p class="unindented">Tīsu itthīsu pasīdati ekacco puriso.</p>\r\n<p class="centered">Imāni napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Tīni kulāni puññāni karonti</p>\r\n<p class="unindented">Tīni kulāni puññāni kārāpeti</p>\r\n<p class="unindented">Tīhi kulehi puññāni kariyyante</p>\r\n<p class="unindented">Tīhi kulehi jīvanti ye keci</p>\r\n<p class="unindented">Tiṇṇannaṃ <a name="O0.0038"></a> kulānaṃ dhanaṃ deti dhanavā</p>\r\n<p class="unindented">Tīhi kulehi bhayāni na uppajjanti</p>\r\n<p class="unindented">Tiṇṇannaṃ kulānaṃ mahābhogo hoti</p>\r\n<p class="unindented">Tīsu kulesu pasīdati ekacco puriso.</p>\r\n<p class="centered">Iti dutiyo pāṭho.</p>\r\n<p class="centered">Imāni pulliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Cattāro mahārājā rajjaṃ karonti</p>\r\n<p class="unindented">Cattāro mahārāje upagacchati ekacco</p>\r\n<p class="unindented">Catūhi mahārājehi puññāni kariyyante</p>\r\n<p class="unindented">Catūhi mahārājehi jīvanti mahājanā</p>\r\n<p class="unindented">Catunnaṃ mahārājānaṃ pannākāraṃ denti</p>\r\n<p class="unindented">Catūhi mahārājehi bhayāni uppajjanti</p>\r\n<p class="unindented">Catunnaṃ mahārājānaṃ ābharaṇaṃ hoti</p>\r\n<p class="unindented">Catūsu mahārājesu pasīdati mahājano.</p>\r\n<p class="centered">Imāni itthiliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Catasso kaññāyo puññāni karonti</p>\r\n<p class="unindented">Catasso kaññāyo puññāni kārāpeti</p>\r\n<p class="unindented">Catūhi kaññāhi saṅgho bhattaṃ bhojāpito</p>\r\n<p class="unindented">Catūhi kaññāhi vadhaṃ pāpuṇanti purisā</p>\r\n<p class="unindented">Catassannaṃ kaññānaṃ ābharaṇāni denti</p>\r\n<p class="unindented">Catūhi kaññāhi ayaṃ kaññā adhikā</p>\r\n<p class="unindented">Catassannaṃ kaññānaṃ ābharaṇaṃ hoti</p>\r\n<p class="unindented">Catūsu kaññāsu pasīdati ekacco puriso.</p>\r\n<p class="centered">Imāni napuṃsakaliṅgarūpāni vuccante.</p>\r\n<p class="unindented">Cattāri kulāni bahuṃ puññaṃ karonti</p>\r\n<p class="unindented">Cattāri kulāni upagacchanti ekacce</p>\r\n<p class="unindented">Catūhi kulehi vihāro kārāpīyate</p>\r\n<p class="unindented">Catūhi kulehi jīvanti ekacce purisā</p>\r\n<p class="unindented">Catunnaṃ kulānaṃ sakkāraṃ karonti</p>\r\n<p class="unindented">Catūhi kulehi apenti ekacce</p>\r\n<p class="unindented">Catunnaṃ kulānaṃ nāma gottādayo</p>\r\n<p class="unindented">Catusu kulesu pasīdati mahājano.</p>\r\n<p class="centered">Iti tatiyo pāṭho.</p>\r\n<p class="unindented">Pañca mahābhūtā tiṭṭhanti</p>\r\n<p class="unindented">Pañca mahābhūte passati</p>\r\n<p class="unindented">Pañcahi <a name="O0.0039"></a