INSERT INTO books VALUES('annya_sadda_10','annya','annya_sadda','niruttidīpanīpāṭha',1,563,563);
INSERT INTO pages VALUES(54408,'annya_sadda_10',1,replace(replace('\r\n<p class="centered">. namo tassa bhagavato arahato sammāsambuddhassa .</p>\r\n<p class="book">niruttidīpanīpāṭha</p>\r\n<p class="subsubhead">ganthārambha</p>\r\n<p class="hangnum"><a name="para1"></a><span class="paranum">1</span>.</p>\r\n\r\n<p class="gatha1"> caturāsītisahassa<a name="M0.0001"></a>, dhammakkhandhāpabhaṅkarā.</p>\r\n<p class="gathalast">lokamhi yassa jotanti, nantavaṇṇapabhassarā.</p>\r\n<p class="hangnum"><a name="para2"></a><span class="paranum">2</span>.</p>\r\n<p class="gatha1"> anantavaṇṇaṃ sambuddhaṃ, vande niruttipāraguṃ.</p>\r\n<p class="gathalast">saddhammañcassa saṅghañca, visuddhavaṇṇabhājanaṃ.</p>\r\n<p class="hangnum"><a name="para3"></a><span class="paranum">3</span>.</p>\r\n<p class="gatha1"> moggallāno mahāñāṇī, niruttāraññakesarī.</p>\r\n<p class="gathalast">nadi byākaraṇaṃnādaṃ, sogatāraññabyāpanaṃ.</p>\r\n<p class="hangnum"><a name="para4"></a><span class="paranum">4</span>.</p>\r\n<p class="gatha1"> tassatthaṃ dīpayissāmi, nānārāsiṃvibhājayaṃ.</p>\r\n<p class="gathalast">ogāyha saddasatthāni, navaṅgaṃ satthusāsananti.</p>\r\n','\r',char(13)),'\n',char(10)),'-1-2-3-4-');
INSERT INTO pages VALUES(54409,'annya_sadda_10',2,replace(replace('\r\n<p class="chapter">1. sandhikaṇḍa</p>\r\n<p class="subhead">saññārāsi</p>\r\n<p class="subhead">garusaññārāsi</p>\r\n\r\n<p class="bodytext">vaṇṇo<a name="M0.0002"></a>, saro, savaṇṇo, dīgho, rasso, byañjano, vaggo, niggahītaṃ.</p>\r\n<p class="bodytext"><a name="para1"></a><span class="paranum">1</span>. <span class="bld">aādayo titālīsaṃ</span> <span class="note">[titālīsa (bahūsu)]</span> <span class="bld">vaṇṇā</span> <span class="note">[ka. 2; rū. 2; nī. 1, 2]</span>.</p>\r\n<p class="bodytext">aādayo bindantā tecattālīsakkharā <span class="bld">vaṇṇā</span> nāma honti.</p>\r\n<p class="bodytext">a, ā, i, <span class="bld">ī,</span> u, ū, eta, e, ota, o. ka, kha, ga, gha, ṅa, ca, cha, ja, jha, ña, ṭa, ṭha, ḍa, ḍha, ṇa, ta, tha, da, dha, na, pa, pha, ba, bha, ma, ya, ra, la,va sa, ha, ḷa, aṃ. atthaṃ vaṇṇenti pakāsentīti vaṇṇā, <span class="bld">akkharā</span>ti ca vuccanti, nāmapaññattirūpattā nakkharanti khayavayaṃ na gacchantīti akkharā. "nāmagottaṃ na jīratī"ti <span class="note">[saṃ. ni. 1.76]</span> hi vuttaṃ.</p>\r\n<p class="bodytext"><a name="para2"></a><span class="paranum">2</span>. <span class="bld">dasādo sarā</span> <span class="note">[ka. 3; rū. 3; nī. 3]</span>.</p>\r\n<p class="bodytext">tesu vaṇṇesu ādimhi dasa vaṇṇā <span class="bld">sarā</span> nāma honti. sayameva laddhasarūpā hutvā rājanti virocantīti sarā.</p>\r\n<p class="bodytext"><a name="para3"></a><span class="paranum">3</span>. <span class="bld">dve dve savaṇṇā</span>.</p>\r\n<p class="bodytext">tesu saresu dve dvesarā <span class="bld">savaṇṇā</span> nāma honti.</p>\r\n<p class="bodytext">a, ā avaṇṇo, i, ī ivaṇṇo, u, ū uvaṇṇo, eta, e etavaṇṇo, ota, o otavaṇṇo. samāno vaṇṇo suti etesanti savaṇṇā, <span class="bld">sarūpā</span>ti ca vuccanti, samānaṃ rūpaṃ suti etesanti sarūpā.</p>\r\n<p class="bodytext"><a name="para4"></a><span class="paranum">4</span>. <span class="bld">pubbo rasso</span> <span class="note">[ka. 4; rū. 4; nī. 4.22]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-1-2-3-4-');
INSERT INTO pages VALUES(54410,'annya_sadda_10',3,replace(replace('\r\n<p class="bodytext">dvīsu <a name="M0.0003"></a> dvīsu savaṇṇesu yo yo pubbo hoti, so so <span class="bld">rasso</span> nāma hoti. rassena kālena vattabbāti rassā, rassakālo nāma akkhidalānaṃ ummisananimmisanasamakālo.</p>\r\n<p class="bodytext">tattha eta, ota iti dve ekapadasaṃyoge pare kvaci labbhanti. eṭṭhi, seṭṭho, oṭṭho, sotthi.</p>\r\n<p class="bodytext">ekapadasaṃyogeti kiṃ? padantarasaṃyoge pare rassā mā hontūti. maṃ ce tvaṃ nikhaṇaṃ vane, <span class="note">[jā. 2.22.5]</span> putto tyāhaṃ mahārāja <span class="note">[jā. 1.1.7]</span>.</p>\r\n<p class="bodytext">kvacīti kiṃ? ekapadasaṃyogepi vaggantesu vā ya, ra, la, vesu vā paresu rassā mā hontūti. enti, senti, eyya, bhāseyya, meṇḍo, soṇḍo.</p>\r\n<p class="bodytext"><a name="para5"></a><span class="paranum">5</span>. <span class="bld">paro dīgho</span> <span class="note">[ka. 5; rū. 5; nī. 5]</span>.</p>\r\n<p class="bodytext">dvīsu dvīsu savaṇṇesu yo yo paro hoti, so so <span class="bld">dīgho</span> nāma hoti. dīghena kālena vattabbāti dīghā, dīghakālo nāma rassehi diguṇakālo.</p>\r\n<p class="bodytext"><a name="para6"></a><span class="paranum">6</span>. <span class="bld">kādayo byañjanā</span> <span class="note">[ka. 6; rū. 8; nī. 6]</span>.</p>\r\n<p class="bodytext">tesu vaṇṇesu kādayo bindantā vaṇṇā <span class="bld">byañjanā</span> nāma honti. atthaṃ byañjayantīti byañjanā. te pana suddhā addhamattikā, rassayuttā diyaddhamattikā, dīghayuttā tiyaddhamattikā.</p>\r\n<p class="bodytext"><a name="para7"></a><span class="paranum">7</span>. <span class="bld">pañcapañcakā vaggā</span> <span class="note">[ka. 7; rū 9; nī. 7]</span>.</p>\r\n<p class="bodytext">tesu byañjanesu kādi-mantā pañcabyañjanapañcakā <span class="bld">vaggā</span> nāma honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-5-6-7-');
INSERT INTO pages VALUES(54411,'annya_sadda_10',4,replace(replace('\r\n<p class="bodytext">kādi <a name="M0.0004"></a> pañcako kavaggo, cādi ca vaggo, ṭādi ṭavaggo, tādi tavaggo, pādi pavaggo. sesā <span class="bld">avaggā</span>ti siddhaṃ. vaṇṇuddese ekaṭṭhānikānaṃ byañjanānaṃ vagge samūhe niyuttāti vaggā.</p>\r\n<p class="bodytext"><a name="para8"></a><span class="paranum">8</span>. <span class="bld">bindu niggahītaṃ</span> <span class="note">[ka. 8; rū. 10; nī. 8]</span>.</p>\r\n<p class="bodytext">ante bindumatto vaṇṇo <span class="bld">niggahītaṃ</span> nāma. niggayha gayhati uccāriyatīti niggahītaṃ.</p>\r\n<p class="centered">garusaññārāsi niṭṭhito.</p>\r\n<p class="subhead">byañjanavuttirāsi</p>\r\n<p class="bodytext">ṭhānaṃ, karaṇaṃ, payatanaṃ <span class="note">[rū. 2 (piṭṭhe); nī 6 (piṭṭhe); 23 (suttaṅke)]</span>.</p>\r\n<p class="bodytext">cha <span class="bld">ṭhānāni</span> – kaṇṭhaṭṭhānaṃ, tāluṭṭhānaṃ, muddhaṭṭhānaṃ, dantaṭṭhānaṃ, oṭṭhaṭṭhānaṃ, nāsikaṭṭhānaṃ. tesu byattaṃ vadantena yattha "akkha"nti vuccati, taṃ kaṇṭhaṭṭhānaṃ. yattha "iccha"nti, taṃ tāluṭṭhānaṃ. yattha "raṭṭha"nti, taṃ muddhaṭṭhānaṃ. yattha "sattha"nti, taṃ dantaṭṭhānaṃ. yattha "puppha"nti vuccati, taṃ oṭṭhaṭṭhānaṃ. nāsapadeso nāsikaṭṭhānaṃ.</p>\r\n<p class="bodytext">katthaci pana uraṭṭhānaṃ, siraṭṭhānaṃ, jivhāmūlaṭṭhānantipi āgataṃ. tattha siraṭṭhānaṃ nāma muddhaṭṭhānameva. jivhāmūlaṭṭhānaṃ pana sabbavaṇṇānaṃ sādhāraṇanti vadanti.</p>\r\n<p class="bodytext"><span class="bld">karaṇaṃ</span> catubbidhaṃ – jivhāmūlaṃ, jivhopaggaṃ, jivhaggaṃ, sakaṭṭhānanti.</p>\r\n<p class="bodytext"><span class="bld">payatanaṃ</span> catubbidhaṃ – saṃvuṭaṃ, vivaṭaṃ, phuṭṭhaṃ, īsaṃphuṭṭhanti. tattha karaṇānaṃ sakasakaṭṭhānehi saddhiṃ saṃvaraṇādiko visesākāro saṃvuṭādi nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'-8-');
INSERT INTO pages VALUES(54412,'annya_sadda_10',5,replace(replace('\r\n<p class="bodytext">tattha kaṇṭhapadesānaṃ aññamaññaṃ saṅghaṭṭanena uppannā avaṇṇa, kavagga, hakārā <span class="bld">kaṇṭhajā</span> nāma. tālumhi jivhāmajjhasaṅghaṭṭanena uppannā ivaṇṇa, cavagga, yakārā <span class="bld">tālujā</span> nāma. mukhabbhantaramuddhamhi <a name="M0.0005"></a> jivhopaggasaṅghaṭṭanena uppannā ṭavagga, ra, ḷakārā <span class="bld">muddhajā</span> nāma. upari dantapantiyaṃ jivhaggasaṅghaṭṭanena uppannā tavagga, la, sakārā <span class="bld">dantajā</span> nāma. oṭṭhadvayasaṅghaṭṭanena uppannā uvaṇṇa, pavaggā <span class="bld">oṭṭhajā</span> nāma. niggahītaṃ <span class="bld">nāsikajaṃ</span> nāma. pañcavaggantā pana nāsikaṭṭhānepi sakaṭṭhānepi jāyanti. ekāro <span class="bld">kaṇṭhatālujo</span>. okāro <span class="bld">kaṇṭhoṭṭhajo</span>. vakāro <span class="bld">dantoṭṭhajo</span>. apica ivaṇṇuvaṇṇā kaṇṭhepi jāyantiyeva. yadā hakāro vaggantehi vā ya, ra, la, vehi vā yutto hoti, tadā <span class="bld">urajo</span>ti vadanti. pañho, tuṇhi, nhāto, vimhito, gayhate, vulhate, avhānaṃ.</p>\r\n<p class="bodytext">kaṇṭhaṃ saṃvaritvā uccārito akāro <span class="bld">saṃvuṭo</span> nāma. sakasakaṭṭhāna, karaṇāni vivaritvā uccāritā sesasarā ca sa, hakārā ca <span class="bld">vivaṭā</span> nāma. tāniyeva gāḷhaṃ phusāpetvā uccāritā pañcavaggā <span class="bld">phuṭṭhā</span> nāma. thokaṃ phusāpetvā uccāritā ya, ra, la, vā <span class="bld">īsaṃphuṭṭhā</span> nāma. tattha oṭṭhajesu tāva pavaggaṃ vadantānaṃ oṭṭhadvayassa gāḷhaṃ phusanaṃ icchitabbaṃ. kasmā? phuṭṭhapayatanikattā pavaggassa. uvaṇṇaṃ vadantānaṃ pana oṭṭhadvayassa vivaraṇaṃ icchitabbaṃ. kasmā? vivaṭapayatanikattā uvaṇṇassa. esa nayo sesesu sabbesūti.</p>\r\n<p class="bodytext">cūḷaniruttiyaṃ pana sabbe rassasarā saṃvuṭā nāma, sabbedīghasarā vivaṭā nāmāti vuttaṃ. tathā saddasāratthajāliniyaṃ, katthaci sakkaṭaganthe ca. idaṃ yuttataraṃ. aññaṭṭhānikabyañjanehi yuttā sarā attano ṭhāna, karaṇāni jahantāpi payatanaṃ na jahanti. tasmā nānāvaṇṇānaṃ saṃsagge payatanānaṃ saṃsaggabhedopi veditabboti. tattha "suṇātu me"ti vadanto yadi ṇā-kāraṃ jivhaggena dantaṭṭhāne katvā vadeyya, dantajo nā-kāro eva bhaveyya. tu-kārañca jivhopaggena muddhaṭṭhāne katvā vadeyya, muddhajo ṭu-kāro eva bhaveyya. evañca sati akkharavipatti nāma siyā. esa nayo sesesu muddhajadantajesu. tasmā kammavācaṃ sāventehi nāma ṭhāna, karaṇa, payatanesu suṭṭhu kusalehi bhavitabbanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54413,'annya_sadda_10',6,replace(replace('\r\n<p class="gatha1">sithilañca<a name="M0.0006"></a>, dhanitañca, dīghaṃ, rassaṃ, garuṃ, lahuṃ.</p>\r\n<p class="gathalast">niggahītaṃ, vimuttañca, sambandhañca, vavatthitaṃ <span class="note">[nī. 9, 10, 11, 12, 13, 14, 15, 16-19, 20, 21 suttesu passitabbaṃ]</span>.</p>\r\n<p class="bodytext">mudunā vacīpayogena vattabbā vaggapaṭhama, tatiya, pañcamā <span class="bld">sithilā</span> nāma. thaddhena vacīpayogena vattabbā vaggadutiya, catutthā <span class="bld">dhanitā</span> nāma. <span class="bld">dīgha, rassā</span> pubbe vuttā. dīghā ceva saṃyogapubbā ca niggahītantā ca <span class="bld">garukā</span> nāma. sesā <span class="bld">lahukā</span> nāma. yathā saddasahito vāto mukhachiddena bahi anikkhamma nāsasotābhimukho hoti, tathā mukhaṃ avivaṭaṃ katvā vattabbaṃ byañjanaṃ <span class="bld">niggahītaṃ</span> nāma. tena yuttāni sabbabyañjanāni niggahītantāni nāma. sesā <span class="bld">vimuttā</span> nāma. padasandhivasena vattabbaṃ <span class="bld">sambandhaṃ</span> nāma. padacchedaṃ katvā vattabbaṃ <span class="bld">vavatthitaṃ</span> nāma.</p>\r\n<p class="centered">byañjanavuttirāsi niṭṭhito.</p>\r\n<p class="subhead">lahusaññārāsi</p>\r\n<p class="bodytext">jho, lo, po, gho, go.</p>\r\n<p class="bodytext"><a name="para9"></a><span class="paranum">9</span>. <span class="bld">yuvaṇṇā</span> <span class="note">[iyuvaṇṇā (bahūsu)]</span> <span class="bld">jhalā nāmassante</span> <span class="note">[ka. 58; rū. 29; nī. 205]</span>.</p>\r\n<p class="bodytext">anitthiliṅgassa nāmassa ante ivaṇṇuvaṇṇā kamena jhalasaññā honti.</p>\r\n<p class="bodytext"><a name="para10"></a><span class="paranum">10</span>. <span class="bld">pitthiyaṃ</span> <span class="note">[ka. 59; rū. 182; nī. 206]</span>.</p>\r\n<p class="bodytext">itthiliṅge nāmassante ivaṇṇuvaṇṇā pasaññā honti.</p>\r\n<p class="bodytext"><a name="para11"></a><span class="paranum">11</span>. <span class="bld">ghā</span> <span class="note">[ka. 60; rū. 177; nī. 207]</span>.</p>\r\n<p class="bodytext">gho</p>\r\n','\r',char(13)),'\n',char(10)),'-9-10-11-');
INSERT INTO pages VALUES(54414,'annya_sadda_10',7,replace(replace('\r\n<p class="bodytext"><a name="M0.0007"></a>, ā iti dvipadaṃ. itthiliṅge ākāro ghasañño hoti.</p>\r\n<p class="bodytext"><a name="para12"></a><span class="paranum">12</span>. <span class="bld">go syālapane</span> <span class="note">[ka. 57; rū. 71; nī. 214]</span>.</p>\r\n<p class="bodytext">ālapane si gasañño hoti.</p>\r\n<p class="centered">lahusaññārāsi niṭṭhito.</p>\r\n<p class="subhead">saṅketarāsi</p>\r\n<p class="bodytext"><a name="para13"></a><span class="paranum">13</span>. <span class="bld">vidhi visesanaṃ yaṃ tassa</span> <span class="note">[caṃ. 1.1.6; pā. 1.1.72; vidhibbisesanantassa (bahūsu)]</span>.</p>\r\n<p class="bodytext">sutte yaṃ visesanaṃ dissati, tassa vidhi ñātabbo.</p>\r\n<p class="bodytext">‘ato yonaṃ ṭāṭe’. narā, nare. yonanti visesanaṃ. ṭāṭeti vidhi.</p>\r\n<p class="bodytext"><a name="para14"></a><span class="paranum">14</span>. <span class="bld">sattamiyaṃ pubbassa</span> <span class="note">[rū. 8 (piṭṭhe); caṃ. 1.1.7; pā. 1.1.66]</span>.</p>\r\n<p class="bodytext">sattamīniddese pubbavaṇṇasseva vidhi ñātabbo.</p>\r\n<p class="bodytext">‘saro lopo sare’. lokaggo <span class="note">[apa. thera 1.12.57]</span>.</p>\r\n<p class="bodytext"><a name="para15"></a><span class="paranum">15</span>. <span class="bld">pañcamiyaṃ parassa</span> <span class="note">[caṃ. 1.1.8; pā. 1.1.67]</span>.</p>\r\n<p class="bodytext">pañcamīniddese parasseva vidhi ñātabbo.</p>\r\n<p class="bodytext">‘ato yonaṃ ṭāṭe’. narā, nare.</p>\r\n<p class="bodytext"><a name="para16"></a><span class="paranum">16</span>. <span class="bld">ādissa</span> <span class="note">[caṃ. 1.1.9; pā. 1.1.54]</span>.</p>\r\n<p class="bodytext">parassa sissamāno <span class="note">[dissamāno (mū)]</span> vidhi ādivaṇṇassa ñātabbo.</p>\r\n','\r',char(13)),'\n',char(10)),'-12-13-14-15-16-');
INSERT INTO pages VALUES(54415,'annya_sadda_10',8,replace(replace('\r\n<p class="bodytext">‘ra <a name="M0.0008"></a> saṅkhyāto vā’. terasa.</p>\r\n<p class="bodytext"><a name="para17"></a><span class="paranum">17</span>. <span class="bld">chaṭṭhiyantassa</span> <span class="note">[caṃ. 1.1.10; pā. 1.1.52]</span>.</p>\r\n<p class="bodytext">chaṭṭhīniddese tadantassa vidhi ñātabbo.</p>\r\n<p class="bodytext">‘rājassi nāmhi’. rājinā.</p>\r\n<p class="bodytext"><a name="para18"></a><span class="paranum">18</span>. <span class="bld">ṅānubandho</span> <span class="note">[caṃ. 1.1.11; pā. 1.1.53]</span>.</p>\r\n<p class="bodytext">ṅānubandho ādeso chaṭṭhīniddiṭṭhassa antassa ñātabbo.</p>\r\n<p class="bodytext">‘gossāvaṅa’. gavassaṃ.</p>\r\n<p class="bodytext"><a name="para19"></a><span class="paranum">19</span>. <span class="bld">ṭānubandhonekavaṇṇo sabbassa</span> <span class="note">[caṃ. 1.1.12; pā. 1.1.55; ṭānubandhānekavaṇṇā sabbassa (bahūsu)]</span>.</p>\r\n<p class="bodytext">yo ca ṭānubandho ādeso, yo ca anekavaṇṇo ādeso, tadubhayaṃ chaṭṭhīniddiṭṭhassa sabbasseva vaṇṇasamudāyassa ñātabbaṃ.</p>\r\n<p class="bodytext">ṭānubandhe tāva –</p>\r\n<p class="bodytext">‘imassānitthiyaṃ ṭe’. esu.</p>\r\n<p class="bodytext">anekavaṇṇe –</p>\r\n<p class="bodytext">‘animi nāmhi’. anena, iminā.</p>\r\n<p class="bodytext"><a name="para20"></a><span class="paranum">20</span>. <span class="bld">ñakānubandhā ādyantā</span> <span class="note">[caṃ. 1.1.13; pā. 1.1.46]</span>.</p>\r\n<p class="bodytext">ñānubandho āgamo ca kānubandho āgamo ca kamena chaṭṭhīniddiṭṭhassa ādimhi ca ante ca ñātabbo.</p>\r\n<p class="bodytext">ñānubandhe –</p>\r\n<p class="bodytext">‘brūto tissiña’. braviti.</p>\r\n','\r',char(13)),'\n',char(10)),'-17-18-19-20-');
INSERT INTO pages VALUES(54416,'annya_sadda_10',9,replace(replace('\r\n<p class="bodytext">kānubandhe <a name="M0.0009"></a> –</p>\r\n<p class="bodytext">‘bhūssa vuka’. babhuva.</p>\r\n<p class="bodytext"><a name="para21"></a><span class="paranum">21</span>. <span class="bld">mānubandho sarānamantā paro</span> <span class="note">[caṃ. 1.1.14; pā. 1.1.47]</span>.</p>\r\n<p class="bodytext">mānubandho āgamo sarānaṃ antasaramhā paro hoti.</p>\r\n<p class="bodytext">‘najjāyo svāma’. najjāyo sandanti. ‘maṃ vā rudhādīnaṃ’. rundhati. ‘jara sadānamīma vā’. jīrati, sīdati.</p>\r\n<p class="bodytext">imasmiṃ byākaraṇe anekasaratā nāma nadī, purisa iccādīsu liṅgapadesu eva atthi, gamu, pacaiccādīsu dhātupadesu natthi. sabbadhātuyo byañjanantā eva honti, dhātvantalopakiccaṃ natthi. tasmā najjāyoti ettha ī-kāro antasaro nāma. tato ‘najjāyo svāma’ iti suttena ā-kārāgamo. rundhatīti ettha pana u-kāro antasaro nāma, tato "maṃ vā rudhādīna"nti suttena bindāgamo. evaṃ jīrati, sīdati iccādīsu. mrammapotthakesu pana "mānubandho padānamantā paro"ti pāṭho, so sīhaḷapotthakehi na sameti.</p>\r\n<p class="bodytext"><a name="para22"></a><span class="paranum">22</span>. <span class="bld">vippaṭisedhe</span> <span class="note">[caṃ. 1.1.16; pā. 1.4.2]</span>.</p>\r\n<p class="bodytext">samānavisayānaṃ dvinnaṃ vidhīnaṃ aññamaññapaṭisedharahite ṭhāne yebhuyyena paro vidhi okāsaṃ labhati.</p>\r\n<p class="bodytext">cattārome bhikkhave dhammā <span class="note">[aṅguttaranikāye]</span> -ettha cattārimeti pubbalope sampatte paralopo okāsaṃ labhati.</p>\r\n<p class="bodytext"><a name="para23"></a><span class="paranum">23</span>. <span class="bld">saṅketo nāvayavonubandho</span> <span class="note">[caṃ. 1.1.5]</span>.</p>\r\n<p class="bodytext">yo vaṇṇo payogassa avayavo na hoti, suttesu saṅketamatto hoti, so <span class="bld">anubandho</span> nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'-21-22-23-');
INSERT INTO pages VALUES(54417,'annya_sadda_10',10,replace(replace('\r\n<p class="bodytext">‘gossāva’<a name="M0.0010"></a>. gavassaṃ-etena padarūpavidhāne anubandho upayogaṃ na gacchatīti ñāpeti.</p>\r\n<p class="bodytext"><a name="para24"></a><span class="paranum">24</span>. <span class="bld">vaṇṇaparena savaṇṇopi</span>.</p>\r\n<p class="bodytext">vaṇṇasaddo paro etasmāti vaṇṇaparo, vaṇṇaparena rassasarena savaṇṇopi gayhati sayañca, avaṇṇoti vutte ā-kāropi gayhati a-kāro cāti vuttaṃ hoti. evaṃ ivaṇṇuvaṇṇesu.</p>\r\n<p class="bodytext"><a name="para25"></a><span class="paranum">25</span>. <span class="bld">ntuvantumantāvantutavantusambandhī</span> <span class="note">[ntu vantumantvāvantutavantu sambandhī (bahūsu)]</span>.</p>\r\n<p class="bodytext">ntuiti vutte vantu, mantu, āvantu, tavantūnaṃ sambandhībhūto ntukāro gayhati.</p>\r\n<p class="bodytext">‘ntantūnaṃ nto yomhi paṭhame’. guṇavanto, satimanto, yāvanto, bhuttavanto.</p>\r\n<p class="centered">saṅketarāsi niṭṭhito.</p>\r\n<p class="subhead">sandhividhāna</p>\r\n<p class="bodytext">atha sandhividhānaṃ dīpiyate.</p>\r\n<p class="bodytext">lopo, dīgho, rasso, vuddhi, ādeso, āgamo, dvibhāvo, vipallāso.</p>\r\n<p class="subhead">loparāsi</p>\r\n<p class="bodytext"><a name="para26"></a><span class="paranum">26</span>. <span class="bld">saro lopo sare</span> <span class="note">[ka. 12; rū. 13; nī. 30]</span>.</p>\r\n<p class="bodytext">luppatīti lopo. sare pare sarūpo vā asarūpo vā pubbo saro lopo hoti.</p>\r\n<p class="bodytext">sarūpe tāva –</p>\r\n','\r',char(13)),'\n',char(10)),'-24-25-26-');
INSERT INTO pages VALUES(54418,'annya_sadda_10',11,replace(replace('\r\n<p class="bodytext">avaṇṇe-lokaggo <a name="M0.0011"></a> <span class="note">[apa. thera 1.12.57]</span>, bhavāsavo, <span class="note">[dha. sa. 1102]</span> avijjāsavo <span class="note">[dha. sa. 1102]</span>, avijjānusayo <span class="note">[vibha. 949]</span>.</p>\r\n<p class="bodytext">ivaṇṇe-munindo, munīrito, varavādīrito, itthindriyaṃ <span class="note">[vibha. 219]</span>.</p>\r\n<p class="bodytext">uvaṇṇe-bahūpakāro <span class="note">[jā. 1.22.588]</span>, bahukā ūmi bahūmi, sarabhuyā ūmi sarabhūmi, sarabhuyā udakaṃ sarabhūdakaṃ.</p>\r\n<p class="bodytext">asarūpe –</p>\r\n<p class="bodytext">sotindriyaṃ <span class="note">[vibha. 219]</span>, kāmupādānaṃ, bhavesanā <span class="note">[dī. ni. 3.305]</span>, bhavogho <span class="note">[dha. sa. 1156]</span>, so tuṇhassa <span class="note">[pārā. 381]</span>, diṭṭhānusayo <span class="note">[vibha. 949]</span>, diṭṭhupādānaṃ, diṭṭhekaṭṭhaṃ, diṭṭhogho <span class="note">[dha. sa. 1156]</span>, mudindriyaṃ <span class="note">[mahāva. 9]</span>, puttā matthi <span class="note">[dha. pa. 62]</span>, urassa dukkho <span class="note">[pāci. 402]</span>, asantettha na dissanti <span class="note">[dha. pa. 304]</span> iccādi.</p>\r\n<p class="centered">iti pubbaloparāsi.</p>\r\n<p class="bodytext"><a name="para27"></a><span class="paranum">27</span>. <span class="bld">paro kvaci</span> <span class="note">[ka. 13; rū. 15; nī. 31]</span>.</p>\r\n<p class="bodytext">pubbasaramhā sarūpo vā asarūpo vā paro saro kvaci lopo hoti.</p>\r\n<p class="bodytext">sarūpe tāva –</p>\r\n','\r',char(13)),'\n',char(10)),'-27-');
INSERT INTO pages VALUES(54419,'annya_sadda_10',12,replace(replace('\r\n<p class="bodytext">taṃ kadāssu bhavissati <span class="note">[jā. 2.22.144 ādayo; taṃ kudassu]</span>, kudāssu nāma dummedho, dukkhassantaṃ karissati <span class="note">[dha. pa. 376]</span>, yadāssa sīlaṃ paññañca <span class="note">[jā. 2.22.1474]</span>, tadāssu kaṇhaṃ yuñjanti <span class="note">[jā. 1.1.29]</span> -kaṇhanti mahākaṇhagoṇaṃ, taṇhāssa vippahīnā, māssu <a name="M0.0012"></a> kujjha rathesabha, satthahārakaṃ vāssa pariyeseyya <span class="note">[pārā. 167, 171]</span>, āgatāttha tumhe, sotukāmāttha tumhe, māyyo evarūpamakāsi, papaṃ avinduṃ <span class="note">[jā. 1.1.2]</span> -pavaḍḍhaṃ āpaṃ labhiṃsūtyattho, nālaṃ kabaḷaṃ padātave <span class="note">[jā. 1.1.27]</span> -pa+ādātaveti chedo, gaṇhituntattho, ruppatīti rūpaṃ, bujjhatīti buddhā-dīgho, aggīva tappati, itthīva gacchati, nadīva sandati, mātupaṭṭhānaṃ, pitupaṭṭhānaṃ, yete dhammā ādikalyāṇā <span class="note">[cūḷava. 399]</span> iccādi.</p>\r\n<p class="bodytext">asarūpe –</p>\r\n<p class="bodytext">itissa <span class="note">[pāci. 465]</span>, itipi <span class="note">[pārā. 1]</span>, assamaṇīsi <span class="note">[pārā. 135]</span>, akataññūsi <span class="note">[dha. pa. 383]</span>, vandehaṃ, sohaṃ, yassadāni <span class="note">[mahāva. 242]</span>, chāyāva anapāyinī <span class="note">[dha. pa. 2]</span>, mādisesu kathāva kā, kinnumāva samaṇiyo madhuvā maññati bālo <span class="note">[dha. pa. 69]</span>, cakkhundriyaṃ <span class="note">[vibha. 219]</span>, dveme bhikkhave dhammā <span class="note">[a. ni. 2.3]</span>, tayome bhikkhave dhammā <span class="note">[a. ni. 3.17]</span> iccādi.</p>\r\n<p class="bodytext">kvacīti kiṃ? katamā cānanda aniccasaññā <span class="note">[a. ni. 10.60]</span>.</p>\r\n<p class="centered">iti paraloparāsi.</p>\r\n<p class="bodytext"><a name="para28"></a><span class="paranum">28</span>. <span class="bld">na dve vā</span>.</p>\r\n<p class="bodytext">dve pubbaparasarā kvaci lopā na honti vā.</p>\r\n<p class="bodytext">appamādo amataṃ padaṃ <span class="note">[dha. pa. 21]</span>, ko imaṃ pathaviṃvicessati <span class="note">[dha. pa. 44]</span>.</p>\r\n<p class="bodytext">kvacitveva? sotindriyaṃ <span class="note">[vibha. 219]</span>, cakkhundriyaṃ <span class="note">[vibha. 219]</span>,</p>\r\n','\r',char(13)),'\n',char(10)),'-28-');
INSERT INTO pages VALUES(54420,'annya_sadda_10',13,replace(replace('\r\n<p class="bodytext">vāti kiṃ<a name="M0.0013"></a>? komaṃ vasaliṃ parāmasissati.</p>\r\n<p class="bodytext">ito paṭṭhāya yāvasandhikaṇḍāvasānā yuttaṭṭhānesu sabbattha kvacisaddo, vāsaddo ca vattante. tattha kvacisaddo nānāpayogaṃ dasseti. vāsaddo ekapayogassa nānārūpaṃ dasseti. lopanisedho.</p>\r\n<p class="bodytext"><a name="para29"></a><span class="paranum">29</span>. <span class="bld">parasarassa</span>.</p>\r\n<p class="bodytext">niggahītamhā parasarassa kvaci lopo hoti vā.</p>\r\n<p class="bodytext">tvaṃsi <span class="note">[pe. va. 47; jā. 2.22.764]</span>, candaṃva vimalaṃ suddhaṃ <span class="note">[dha. pa. 413; su. ni. 642]</span>, cakkaṃva vahato padaṃ <span class="note">[dha. pa. 1]</span>, halaṃdāni pakāsituṃ <span class="note">[mahāva. 8]</span>, kinti vadeyyaṃ, cīvaranti, pattanti, bhikkhunti.</p>\r\n<p class="centered">iti saraloparāsi.</p>\r\n<p class="bodytext"><a name="para30"></a><span class="paranum">30</span>. <span class="bld">saṃyogādi lopo</span>.</p>\r\n<p class="bodytext">saṃyogassa ādibhūto byañjano kvaci lopo hoti vā.</p>\r\n<p class="bodytext">pupphaṃsā uppajjati <span class="note">[pārā. 36]</span> – idha pubbasuttena saralopo, evaṃsa te āsavā pahīnā honti <span class="note">[ma. ni. 1.24]</span>, sace bhutto bhaveyyāhaṃ, sājīvo garahito mama <span class="note">[mi. pa. 6.1.5]</span> - assa+ājīvoti chedo, bhaveyyāti attho.</p>\r\n<p class="bodytext">tīsu byañjanesu sarūpānaṃ dvinnaṃ ādibyañjanassa lopo – agyāgāraṃ <span class="note">[pāci. 326]</span>, agyāhito, vutyassa, vityānubhūyate, ekasataṃ khatyā <span class="note">[jā. 2.22.594]</span>, ratyo, ratyā, ratyaṃ, sakvāhaṃ mārisa devānamindo <span class="note">[saṃ. ni. 1.268]</span>, iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-29-30-');
INSERT INTO pages VALUES(54421,'annya_sadda_10',14,replace(replace('\r\n<p class="bodytext">sarūpānanti <a name="M0.0014"></a> kiṃ? titthyā puthuso vadanti <span class="note">[su. ni. 897]</span>, catutthyantaṃ, chaṭṭhuntaṃ, cakkhvābādhaṃ, vatthvettha.</p>\r\n<p class="centered">iti byañjanaloparāsi.</p>\r\n<p class="bodytext"><a name="para31"></a><span class="paranum">31</span>. <span class="bld">lopo</span>.</p>\r\n<p class="bodytext">niggahītassa kvaci lopo hoti vā.</p>\r\n<p class="bodytext">sare pare tāva –</p>\r\n<p class="bodytext">evāhaṃ cintayitvāna <span class="note">[bu. vaṃ. 2.27]</span>, pupphadānaṃ adāsahaṃ-adāsiṃ+ahanti chedo, bindulopo, puna pubbasaralopo, tuyhatthāya mahāmuni, tuyhevetaṃ dukkaṭaṃ <span class="note">[dī. ni. 2.178]</span>, tāsāhaṃ santike <span class="note">[pāci. 709]</span>, tesāhaṃ evaṃ vadāmi, pañcannetaṃ dhammānaṃ adhivacanaṃ, channetaṃ dhammānaṃ adhivacanaṃ, samaṇa tveva pucchāmi <span class="note">[jā. 2.22.253 samaṇa teva]</span>, brāhmaṇa tveva pucchāmi <span class="note">[jā. 2.22.258 brāhmaṇa teva]</span> -tvaṃ+evāti chedo, vidūnaggamiti.</p>\r\n<p class="bodytext">byañjane pare –</p>\r\n<p class="bodytext">taṃ tuyhamūle paṭidesemi.</p>\r\n<p class="bodytext">gāthāyaṃ –</p>\r\n<p class="bodytext">ariyasaccānadassanaṃ <span class="note">[khu. pā. 5.11]</span>, etaṃ buddhāna sāsanaṃ <span class="note">[dha. pa. 183]</span>, khandhānañca paṭipāṭi, dhātuāyatanāna ca <span class="note">[dha. sa. aṭṭha. nidānakathā]</span>.</p>\r\n<p class="bodytext">māgame pare –</p>\r\n','\r',char(13)),'\n',char(10)),'-31-');
INSERT INTO pages VALUES(54422,'annya_sadda_10',15,replace(replace('\r\n<p class="bodytext">garuḷo uragāmiva <span class="note">[jā. 1.4.124 supaṇṇo]</span>, dhammo arahatāmiva <span class="note">[dī. ni. 2.348]</span>, āloko passatāmiva <span class="note">[su. ni. 769]</span>, bako kakkaṭakāmiva <span class="note">[jā. 1.1.38]</span>, nabhaṃ tārakitāmiva <span class="note">[jā. 2.22.1989 tārācitāmiva]</span>, padumaṃ <a name="M0.0015"></a> hatthagatāmiva <span class="note">[jā. 2.22.2336]</span> -etesu māgame bindulopo, byañjane pubbasaradīgho ca.</p>\r\n<p class="bodytext">tathā saṃupasaggassa bindulope antasaradīgho –</p>\r\n<p class="bodytext">sārāgo, sāratto, avisāhāro, sārambho, sāraddhā, sāketaṃ nagaraṃ, sādhāraṇaṃ, saṃ assa atthīti sāmī.</p>\r\n<p class="bodytext">samāse tumantamhi niccaṃ –</p>\r\n<p class="bodytext">kattukāmo, gantukāmo iccādi.</p>\r\n<p class="centered">iti binduloparāsi.</p>\r\n<p class="bodytext"><a name="para32"></a><span class="paranum">32</span>. <span class="bld">syādilopo pubbassekassa</span>.</p>\r\n<p class="bodytext">vicchāyaṃ ekassa vibhatyantassa padassa dvitte kate pubbapadassa syādilopo hoti.</p>\r\n<p class="bodytext">ekekaṃ, ekekāni, ekekena, ekekassa iccādi.</p>\r\n<p class="bodytext">māgame –</p>\r\n<p class="bodytext">ekamekaṃ, ekamekāni iccādi.</p>\r\n<p class="centered">iti syādiloparāsi.</p>\r\n<p class="bodytext">appavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para33"></a><span class="paranum">33</span>. <span class="bld">tadaminādīni</span> <span class="note">[caṃ. 5.2.1.27; pā. 6.3.109; mu. 2.34; kā. 2.27]</span>.</p>\r\n<p class="bodytext">mahāvuttisuttamidaṃ, tadaminādīni padarūpāni iminā nipātanena sijjhantīti attho.</p>\r\n<p class="bodytext">saralopo byañjane –</p>\r\n','\r',char(13)),'\n',char(10)),'-32-33-');
INSERT INTO pages VALUES(54423,'annya_sadda_10',16,replace(replace('\r\n<p class="bodytext">lābu=alābu<a name="M0.0016"></a>, pidhānaṃ=apidhānaṃ, dvāraṃ pidahitvā=apidahitvā, gini=aggini, ratnaṃ=ratanaṃ, nhānaṃ=nahānaṃ, asnāti=asanāti, hanti=hanati, hanti kuddhā puthujjano <span class="note">[a. ni. 7.64]</span>, phalaṃ ve kadaliṃ hanti <span class="note">[a. ni. 4.68]</span>, sakkāro kāpurisaṃ hanti. katthaci bahuvacanampi dissati. vikkosamānā tibbāhi, hanti nesaṃ varaṃ varaṃ <span class="note">[jā. 2.22.2370]</span>.</p>\r\n<p class="bodytext">ivaṇṇalope –</p>\r\n<p class="bodytext">ārāmarukkhacetyāni=cetiyāni <span class="note">[dha. pa. 188]</span>, athatthekasataṃ khatyā <span class="note">[jā. 2.22.594]</span> =khattiyā, tithyā puthuso vadanti <span class="note">[su. ni. 897]</span>. tithyā puthuso niviṭṭhā <span class="note">[su. ni. 898]</span> =titthiyā. viddhasto=viddhaṃsito, utrasto=utrāsito, sneho=sineho, klesavatthūni=kilesavatthūni, kriyā=kiriyā, plavanti=pilavanti.</p>\r\n<p class="bodytext">uvaṇṇalope –</p>\r\n<p class="bodytext">paddhāni=padumāni, usmā=usumā iccādi.</p>\r\n<p class="bodytext">saṃyogādibyañjanalopo ca –</p>\r\n<p class="bodytext">puttānañhi vadho dukho, mātigho labhate dukhaṃ, appassādā kāmā dukhā, natthi kāmaparaṃ dukhaṃ <span class="note">[jā. 2.19.118]</span>, sekho=sekkho, apekhā=apekkhā, upasampadāpekho=upasampadāpekkho <span class="note">[mahāva. 70]</span> iccādi.</p>\r\n<p class="bodytext">sarena saha byañjanalopo –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54424,'annya_sadda_10',17,replace(replace('\r\n<p class="bodytext">paṭisaṅkhā yoniso <span class="note">[a. ni. 6.58]</span>, akkhāti=akkhāyati, gandhaṃ ghāti=ghāyati, abhiññā=abhiññāya, pariññā=pariññāya, adhiṭṭhā=adhiṭṭhāya, patiṭṭhā=patiṭṭhāya, āvīkatā hissa phāsu <span class="note">[mahāva. 134]</span><a name="M0.0017"></a>, assavanatā dhammassa parihāyanti <span class="note">[mahāva. 8]</span>, vipāko tadārammaṇatā uppajjati <span class="note">[paṭṭhā. 3.1.98]</span>, dasāhaparamatā dhāretabbaṃ <span class="note">[pārā. 462]</span>, nāyaṃ brāhmaṇabhojanatthā, tilodano hehiti <span class="note">[jā. 1.8.1]</span>, visasenova gārayho, yassatthā rukkharopakā <span class="note">[jā. 1.3.54]</span> =visasenovāti evaṃnāmako rājā eva, yassatthā dūramāyanti <span class="note">[jā. 1.3.28]</span> – yassāti udarassa, pitu atthā candavatī <span class="note">[jā. 1.9.66]</span>, upādārūpaṃ, anupādā vimutto, saddhāpabbajito, upanidhāpaññatti. saṃvidhāvahāro, yāti=yāyati, vāti=vāyati, nibbāti=nibbāyati, nibbanti=nibbāyanti, pahāti=pahāyati, sappatisso=sappatissayo, suhado=suhadayo=sabbattha yalopo,</p>\r\n<p class="bodytext">mukharo=mukhakharo, vācākaraṇo=vākkaraṇo, vācāpatho=byappatho=vāssa byattaṃ, rassattañca, evaṃ byākho=evaṃ viya kho=vissa byattaṃ, dīgho ca yalopo ca.</p>\r\n<p class="bodytext">lolupo, momuho, kukkuco, susukho, roruvoiccādīsu pana atisayatthadīpanatthaṃ padadvittaṃ katvā pubbapadesu akkharalopo.</p>\r\n<p class="bodytext">padalopo ādimajjhantesu –</p>\r\n<p class="bodytext">datto=devadatto, assehi yutto ratho=assaratho, rūpabhavo=rūpaṃ, arūpabhavo=arūpaṃ iccādi.</p>\r\n<p class="centered">loparāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54425,'annya_sadda_10',18,replace(replace('\r\n<p class="subhead">dīgha, rassarāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0018"></a> dīgha, rassā dīpiyante.</p>\r\n<p class="bodytext"><a name="para34"></a><span class="paranum">34</span>. <span class="bld">sesā dīghā</span>.</p>\r\n<p class="bodytext">pakkhittamidaṃ suttaṃ. luttehi vā ādesakatehi vā vaṇṇehi sesā rassasarā kvaci dīghā honti vā.</p>\r\n<p class="bodytext">pubbalutte tāva –</p>\r\n<p class="bodytext">tatrāyamādi bhavatiṃ <span class="note">[dha. pa. 375]</span>, tatrābhiratimiccheyya <span class="note">[dha. pa. 88; saṃ. ni. 5.198]</span>, buddhānussati, saddhīdha vittaṃ purisassa seṭṭhaṃ <span class="note">[su. ni. 183]</span>, anāgārehi cūbhayaṃ <span class="note">[dha. pa. 404; su. ni. 633]</span>, dhammūpasaṃhitā <span class="note">[dī. ni. 2.349]</span>, tathūpamaṃ dhammavaraṃ adesayi <span class="note">[khu. pā. 6.13]</span>, tesaṃ vūpasamo sukho <span class="note">[dī. ni. 2.221]</span> iccādi.</p>\r\n<p class="bodytext">paralutte –</p>\r\n<p class="bodytext">ajitāti bhagavā avoca <span class="note">[su. ni. 1039, 1041]</span>, sumedho sājāto cāti, ruppatīti rūpaṃ <span class="note">[saṃ. ni. 3.79]</span>, bujjhatīti buddhā, sādhūtipatissuṇitvā <span class="note">[dha. pa. aṭṭha. 1.4 kāḷayakkhinīvatthu]</span>, kiṃsūdha vittaṃ purisassa seṭṭhaṃ <span class="note">[su. ni. 184]</span> iccādi.</p>\r\n<p class="bodytext">bindulutte –</p>\r\n<p class="bodytext">tāsāhaṃ <span class="note">[pāci. 709]</span>, tesāhaṃ <span class="note">[jā. 2.22.313]</span>.</p>\r\n<p class="bodytext">ādesesu –</p>\r\n<p class="bodytext">myāyaṃdhammo <span class="note">[mahāva. 7]</span>, svāhaṃ <span class="note">[pe. va. 485]</span>, vityānubhūyate iccādi.</p>\r\n<p class="bodytext">yadipi imāni rūpāni byañjane uparisuttena sijjhanti, luttādesesu pana niccamiva dīghasiddhiñāpanatthaṃ idaṃ suttaṃ pakkhittanti daṭṭhabbaṃ.</p>\r\n<p class="bodytext"><a name="para35"></a><span class="paranum">35</span>. <span class="bld">byañjane dīgharassā</span> <span class="note">[ka. 25, 26; nī. 35, 36, 64, 71, 165, 179]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-34-35-');
INSERT INTO pages VALUES(54426,'annya_sadda_10',19,replace(replace('\r\n<p class="bodytext">byañjane <a name="M0.0019"></a> pare rassadīghānaṃ kvaci dīgha, rassā honti vā. tattha dīghavidhi nāma gāthāvasena vā āgamavasena vā vacanasukhavasena vā buddhisukhavasena vā hoti.</p>\r\n<p class="bodytext">tattha gāthāvasena tāva –</p>\r\n<p class="bodytext">madhuvāmaññati bālo <span class="note">[dha. pa. 69]</span>, khantī ca sovacassatā <span class="note">[khu. pā. 5.10]</span>, evaṃ gāme munī care <span class="note">[dha. pa. 49]</span>, sakko ujū ca suhujū ca <span class="note">[khu. pā. 9.1]</span> iccādi.</p>\r\n<p class="bodytext">āgame –</p>\r\n<p class="bodytext">uragāmiva <span class="note">[jā. 1.7.30]</span>, arahatāmiva <span class="note">[dī. ni. 2.348]</span>, passatāmiva <span class="note">[su. ni. 769]</span> iccādi. gāthāvasenātipi yujjati.</p>\r\n<p class="bodytext">vacanasukhañca buddhisukhañca purime sesadīghepi labbhati.</p>\r\n<p class="bodytext">tattha vacanasukhe –</p>\r\n<p class="bodytext">chārattaṃ mānattaṃ <span class="note">[cūḷava. 97]</span>, pakaṭṭhaṃ vacanaṃ pāvacanaṃ, pāsādo, pākāro, pāvassi megho, nagaraṃ pāvisi, pāvekkhi, pārisuddhi, pāṭipado, caturāsītisahassāni <span class="note">[dha. sa. aṭṭha. 584]</span> iccādi.</p>\r\n<p class="bodytext">buddhisukhaṃ nāma padacchedañāṇasukhaṃ. tattha –</p>\r\n<p class="bodytext">sātthaṃ sabyañjanaṃ <span class="note">[pārā. 1]</span>, sātthikā dhammadesanā, cakkhumāssa yathā andho iccādi.</p>\r\n<p class="bodytext">bindulutte pana sārāgo, sāratto iccādīni pubbe uddhaṭāniyeva.</p>\r\n<p class="centered">iti dīgharāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54427,'annya_sadda_10',20,replace(replace('\r\n<p class="bodytext">rassasandhimhi <a name="M0.0020"></a> gāthāvasena tāva –</p>\r\n<p class="bodytext">yiṭṭhaṃvahutaṃva loke <span class="note">[dha. pa. 108]</span>, bhovādi nāma so hoti <span class="note">[dha. pa. 396; su. ni. 625]</span>, yathābhāvi guṇena so iccādi.</p>\r\n<p class="bodytext">āgame ya, ra, dāgamesu pubbarasso –</p>\r\n<p class="bodytext">yathayidaṃ <span class="note">[a. ni. 1.1-4]</span>, tathayidaṃ, yathariva amhākaṃ bhagavā, tathariva bhikkhusaṅgho, sammadeva samācare <span class="note">[saṃ. ni. 1.112]</span>, sammadakkhāto mayā satisambojjhaṅgo <span class="note">[saṃ. ni. 5.194]</span> iccādi.</p>\r\n<p class="bodytext">saṃyogarasso nāma bahulaṃ labbhati –</p>\r\n<p class="bodytext">akkamo, parakkamo, akkhāto, aññā, aññindriyaṃ, aññātaṃ bhagavā, aññātaṃ sugata, attharaṇaṃ, apphoṭeti, alliyati, acchindati, assādo, ābhassaro, pabhassaro, sabbaññutaññāṇaṃ, jhānassa lābhimhi vasimhi <span class="note">[pārā. 203-204]</span>.</p>\r\n<p class="bodytext">tā, topaccayesupi rasso –</p>\r\n<p class="bodytext">kataññutā, atthaññutā, dhammaññutā, kaññato, nadito, vadhuto.</p>\r\n<p class="bodytext">samāse –</p>\r\n<p class="bodytext">itthipumaṃ, itthiliṅgaṃ, itthibhāvo, sabbaññubuddhā iccādi.</p>\r\n<p class="centered">iti rassarāsi.</p>\r\n<p class="centered">dīgha, rassarāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54428,'annya_sadda_10',21,replace(replace('\r\n<p class="subhead">vuddhirāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0021"></a> vuddhisandhi dīpiyate.</p>\r\n<p class="bodytext"><a name="para36"></a><span class="paranum">36</span>. <span class="bld">yuvaṇṇānameo luttā</span> <span class="note">[ka. 14; rū. 16; nī. 34]</span>.</p>\r\n<p class="bodytext">luttā pubbasaramhā vā parasaramhā vā sesānaṃ ivaṇṇuvaṇṇānaṃ kamena e, oādesā honti vā.</p>\r\n<p class="bodytext">paraivaṇṇe –</p>\r\n<p class="bodytext">bandhusseva samāgamo, ateva me acchariyaṃ <span class="note">[jā. 2.22.1988]</span>, vāteritaṃ, jineritaṃ.</p>\r\n<p class="bodytext">parauvaṇṇe –</p>\r\n<p class="bodytext">gaṅgodakaṃ, pattaṃ vodakaṃ katvā <span class="note">[cūḷava. 376]</span>, saṅkhyaṃ <span class="note">[su. ni. 754]</span> nopeti vedagū <span class="note">[mahāni. 6]</span>, udakomiva jātaṃ.</p>\r\n<p class="bodytext">kriyāpadesu –</p>\r\n<p class="bodytext">veti, apeti, upeti, apekkhā, upekkhā iccādi.</p>\r\n<p class="bodytext">pubbaivaṇṇe –</p>\r\n<p class="bodytext">rathesabho, janesabho, munelayo iccādi- tattha rathīnaṃ āsabho jeṭṭhakoti rathesabho, rathīnanti rathavantānaṃ ratharuḷhānaṃ yodhānanti attho. janīnaṃ āsabho janesabho, janīnanti janavantānaṃ issarānaṃ. munīnaṃ ālayo vihāro munelayo.</p>\r\n<p class="bodytext">pubbauvaṇṇe –</p>\r\n<p class="bodytext">sundarā itthī sotthi, sundaro attho yassāti sotthi, rassattaṃ, maṅgalaṃ.</p>\r\n<p class="centered">vuddhirāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-36-');
INSERT INTO pages VALUES(54429,'annya_sadda_10',22,replace(replace('\r\n<p class="subhead">ādesasandhi</p>\r\n\r\n<p class="bodytext">athādesasandhi <a name="M0.0022"></a> dīpiyate.</p>\r\n<p class="bodytext"><a name="para37"></a><span class="paranum">37</span>. <span class="bld">yavā sare</span> <span class="note">[ka. 18, 19, 21, 45; nī. 44, 46, 47, 51, 58]</span>.</p>\r\n<p class="bodytext">sare pare ivaṇṇuvaṇṇānaṃ ya, vādesā honti vā.</p>\r\n<p class="bodytext">ivaṇṇe –</p>\r\n<p class="bodytext">paṭisanthāravutyassa <span class="note">[dha. pa. 376]</span>, sabbā vityānubhūyate, kyāhaṃ aparajjhāmi <span class="note">[pārā. 383]</span> – idha paṭhamaṃ bindhulopo, sutā ca paṇḍitātyattha, sutā ca paṇḍitātyamhā <span class="note">[jā. 2.21.149]</span>, ñāto senāpatītyāhaṃ <span class="note">[jā. 2.21.94]</span>, iccetaṃ kusalaṃ <span class="note">[pārā. 411]</span>, iccassa vacanīyaṃ <span class="note">[dīghanikāye]</span>, paccuttaritvā, paccāharati <span class="note">[pārā. 305-307]</span>, pacceti, paccayo, acceti, accayo <span class="note">[dī. ni. 1.251]</span>, accāyaṃ majjhimo khaṇḍo <span class="note">[jā. 1.7.33]</span> – atireko ayaṃ majjhimo khaṇḍotyattho, apuccaṇḍatā – aputiaṇḍatātyattho, jaccandho, jaccaghānako, jacceḷako, abbhuggacchati, abbheti, abbhokāso <span class="note">[dī. ni. 1.191]</span>, ajjhokāso ajjhāgamā iccādi.</p>\r\n<p class="bodytext">vātveva? itissa muhuttampi <span class="note">[pāci. 465]</span>, atisigaṇo, adhīritaṃ.</p>\r\n<p class="bodytext">ettha ca iccetantiādīsu iminā suttena iti, pati, atiputi, jāti, abhi, adhisaddānaṃ ivaṇṇassa yattaṃ, ‘tavagga, varaṇāna’.nti suttena yamhi tavaggassa cattaṃ, ‘vagga, la, sehi te’ti suttena yassa pubbarūpattaṃ, abhi, adhisaddesu pana ‘catutthadutiyesvesa’.nti suttena vaggacatutthānaṃ tatiyattaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-37-');
INSERT INTO pages VALUES(54430,'annya_sadda_10',23,replace(replace('\r\n<p class="bodytext">uvaṇṇe <a name="M0.0023"></a> –</p>\r\n<p class="bodytext">cakkhvābādhamāgacchati, pātvākāsi <span class="note">[ma. ni. 2.308]</span>, vatthvettha vihitaṃ niccaṃ, dvākāro <span class="note">[mahāva. 9]</span>, madhvāsavo <span class="note">[pāci. 328]</span>, anvayo, anveti, svākkhāto <span class="note">[mahāva. 26, 62]</span>, svākāro <span class="note">[mahāva. 9]</span>, bahvābādho iccādi.</p>\r\n<p class="bodytext"><a name="para38"></a><span class="paranum">38</span>. <span class="bld">eonaṃ</span> <span class="note">[ka. 17, 18; rū. 19, 20; nī. 43, 44]</span>.</p>\r\n<p class="bodytext">sare pare e, onaṃ ya, vādeso hoti vā.</p>\r\n<p class="bodytext">kyassa byapathayo assu <span class="note">[su. ni. 967]</span>, kyassu idha gocarā <span class="note">[su. ni. 967]</span> -ke+assa puggalassāti attho, byapathayoti vacanapathā, yathā nāmaṃ tathā jhassa-ce+assāti chedo, tyāhaṃ evaṃ vadeyyaṃ <span class="note">[ma. ni. 1.30]</span>, tyassa pahīnā honti, putto tyāhaṃ mahārāja <span class="note">[jā. 1.1.7]</span>, pabbatyāhaṃ gandhamādane <span class="note">[jā. 2.22.397]</span>, adhigato kho myāyaṃ dhammo <span class="note">[mahāva. 7]</span>, yyassa vippaṭisārajā, yyassa te honti anatthakāmā, yyassu maññāmi samaṇe-ettha ca avisiṭṭhepi vacanasadde ye+assāti padacchedabuddhisukhatthaṃ ‘yyassā’ti potthakāropanaṃ yujjatiyeva, yathā taṃ? ‘yadāssa sīlaṃ paññañca’ <span class="note">[jā. 2.22.1474]</span> iccādīsu viya, kvatthosi jīvitena me, yāvatakvassa kāyo, tāvatakvassa byāmo <span class="note">[dī. ni. 2.35]</span>, atha khvassa, atthi khvetaṃ brāhmaṇa, yatvādhikaraṇaṃ <span class="note">[dī. ni. 1.213]</span>, yvāhaṃ, svāhaṃ iccādi.</p>\r\n<p class="bodytext">vātveva? so ahaṃ vicarissāmi <span class="note">[su. ni. 194]</span>, so ahaṃ bhante.</p>\r\n<p class="bodytext"><a name="para39"></a><span class="paranum">39</span>. <span class="bld">yuvaṇṇānamiyaṅuvaṅa</span> <span class="note">[moga. 5-136 (yuvaṇṇāna miya ṅu va ṅa sare)]</span>.</p>\r\n<p class="bodytext">sare pare ivaṇṇuvaṇṇantānaṃ padānaṃ iyaṅa, uvaṅādesā honti vā. ṅānubandho antādesattho. evaṃ sabbattha.</p>\r\n','\r',char(13)),'\n',char(10)),'-38-39-');
INSERT INTO pages VALUES(54431,'annya_sadda_10',24,replace(replace('\r\n<p class="bodytext">idha <a name="M0.0024"></a> ekekassa padassa rūpadvayaṃ vuccate.</p>\r\n<p class="bodytext">ivaṇṇe –</p>\r\n<p class="bodytext">tiyantaṃ tyantaṃ – tattha tiyantanti iminā suttena siddhaṃ, tyantanti ‘yavā sare’ti suttena. evaṃ sesesu. aggiyāgāre agyāgāre, catutthiyatthe <span class="note">[mahāva. 37]</span> catutthyatthe, pañcamiyatthe pañcamyatthe, pathaviyākāso pathabyākāso, viyañjanaṃ byañjanaṃ, viyākato byākato, viyākaṃsu byākaṃsu, viyatto byatto, viyūḷho byūḷho, dhammaṃ adhiyeti ajjheti, patiyeti pacceti pattiyāyati vā, pariyaṅko pallaṅko, vipariyāso vipallāso, idha ekarūpaṃ hoti – pariyatti, pariyatto, pariyāyo, pallaṅkoiccādīsu parisadde rassa lattaṃ katvā issa ‘yavā sare’ti yatte kate yassa pubbarūpattaṃ.</p>\r\n<p class="bodytext">uvaṇṇe –</p>\r\n<p class="bodytext">bhikkhuvāsane, sayambhuvāsane, idhapi rūpadvayaṃ labbhati – duvaṅgikaṃ=dvaṅgikaṃ, bhuvādigaṇo=bhvādigaṇo iccādi.</p>\r\n<p class="bodytext"><a name="para40"></a><span class="paranum">40</span>. <span class="bld">vitisseve vā</span> <span class="note">[rū. 33 (piṭṭhe)]</span>.</p>\r\n<p class="bodytext">evasadde pare itisaddassa i-kārassa vo hoti vā.</p>\r\n<p class="bodytext">itveva coro aṅgulimālo, samuddotveva saṅkhyaṃ <span class="note">[udā. 45]</span> gacchati, mahāudakakkhandhotveva saṅkhyaṃ gacchati, mahāsammatotveva paṭhamaṃ akkharaṃ nibbattaṃ <span class="note">[dī. ni. 3.131]</span>, isigilitveva samaññā ahosi <span class="note">[ma. ni. 3.133]</span>.</p>\r\n<p class="bodytext">vāti kiṃ? iccevattho, samuddoteva saṅkhyaṃ gacchati.</p>\r\n','\r',char(13)),'\n',char(10)),'-40-');
INSERT INTO pages VALUES(54432,'annya_sadda_10',25,replace(replace('\r\n<p class="bodytext">suttavibhattena <a name="M0.0025"></a> evasadde pare aññati-kārassa vattaṃ. vilapatveva so dijo <span class="note">[jā. 1.6.103]</span>, anusetvevassa kāmarāgo, anusetvevassa rūparāgo – anuseti+eva+assāti chedo, hotveva kāriyasanniṭṭhānaṃ, hoteva vā.</p>\r\n<p class="bodytext"><a name="para41"></a><span class="paranum">41</span>. <span class="bld">eonama vaṇṇe</span> <span class="note">[ka. 27; rū 39; nī. 66, 163-4]</span>.</p>\r\n<p class="bodytext">sarabyañjanabhūte vaṇṇe pare e, onaṃ attaṃ hoti vā. tattha essa attaṃ yebhuyyena ma, dāgamesveva hoti.</p>\r\n<p class="bodytext">akaramhasa te kiccaṃ <span class="note">[jā. 1.4.29]</span> – akaramhasetyattho, disvā yācakamāgate <span class="note">[jā. 1.7.58; 2.22.2261]</span>, disvā paṇḍitamāgate <span class="note">[jā. 2.22.783]</span>, yamāhu natthi vīriyanti <span class="note">[jā. 2.18.162]</span> – ye+āhutyattho. kadassu – ke+assu, yadeva te jātinissitā, tadeva te jarānissitāye+eva, te+evāti chedo, sve bhavo svātanaṃ <span class="note">[pārā. 22]</span> – byañjane dīgho.</p>\r\n<p class="bodytext">omhi –</p>\r\n<p class="bodytext">sa sīlavā <span class="note">[dha. pa. 84]</span>, sa paññavā, sa ve kāsāvamarahati <span class="note">[dha. pa. 10]</span>, esa attho, esa dhammo <span class="note">[dha. pa. 5]</span>, dinnamāsi janindena <span class="note">[jā. 2.22.2161 (dinnamhāti janindena)]</span> – dinno+āsīti chedo, maggamatthi <span class="note">[vibha. aṭṭha. 189]</span> – maggo+atthi, aggamakkhāyati <span class="note">[a. ni. 1.47]</span>, paccayākārameva ca <span class="note">[vibha. aṭṭha. 225]</span>, saṅgho pabbatamiva, saddo cicciṭamiva, hiyyo bhavo hiyyattanaṃ, pāto asanaṃ pātarāso, pātamanusiṭṭho, kakusandha koṇāgamano, thera vādānamuttamo – kakusandhoti ca theroti ca chedo, theravādoti attho.</p>\r\n','\r',char(13)),'\n',char(10)),'-41-');
INSERT INTO pages VALUES(54433,'annya_sadda_10',26,replace(replace('\r\n<p class="bodytext">suttavibhattena <a name="M0.0026"></a> animittepi hoti. tuvañca dhanusekha ca <span class="note">[jā. 1.16.239]</span>, paccayamahāpadeso hesa, ekakoṭṭhāso esa, abhilāpamattabhedo esa iccādi.</p>\r\n<p class="bodytext"><a name="para42"></a><span class="paranum">42</span>. <span class="bld">gossāvaṅa</span> <span class="note">[ka. 22, 78; rū. 28; nī. 52, 229]</span>.</p>\r\n<p class="bodytext">sare pare gossa antassa avaṅa hoti.</p>\r\n<p class="bodytext">go ca asso ca gavāssaṃ.</p>\r\n<p class="bodytext">suttavibhattena byañjanepi. sagavacaṇḍo <span class="note">[a. ni. 4.108]</span>, paragavacaṇḍo.</p>\r\n<p class="bodytext">appavidhānamuccate.</p>\r\n<p class="bodytext">mahāvuttinā avaṇṇassa uttaṃ, ottañca –</p>\r\n<p class="bodytext">puthujjano, puthubhūto-puthūti vā eko pāṭipadiko, puthunā puthunītipi dissati, apekkhiyāno apekkhiyānaapekkhitvātyattho. evaṃ anumodiyāno, marīcikūpamaṃ abhisambuddhāno, mā maṃ pisācā khādantu, jīva tvaṃ saradosataṃ <span class="note">[jā. 1.2.9]</span>, rattidivova so dibbo, mānusaṃ saradosataṃ-vassasatantyattho, anuyanti disodisaṃ <span class="note">[dī. ni. 3.281]</span>, sampatanti disodisaṃ-taṃ taṃ disantyattho, parosataṃ, parosahassaṃ, aññoññaṃ aññamaññaṃ, ponopuññaṃ punappunaṃ, ponobbhavikā taṇhā-punoti vā eko nipāto, puno tassa mahe sino, puno pattaṃ gahetvāna, na ca dāni puno atthi, mama tuyhañca saṅgamo, na puno amatākāraṃ, passissāmi mukhaṃ tava <span class="note">[apa. therī 2.2.235]</span>.</p>\r\n<p class="bodytext">ivaṇṇassa attaṃ, uttaṃ, ettañca –</p>\r\n','\r',char(13)),'\n',char(10)),'-42-');
INSERT INTO pages VALUES(54434,'annya_sadda_10',27,replace(replace('\r\n<p class="bodytext">tadamināpetaṃ pariyāyena veditabbaṃ-taṃ etaṃ atthajātaṃ iminā pariyāyena veditabbanti attho, sakiṃ āgacchati sīlenāti <a name="M0.0027"></a> sakadāgāmī, itthiyā bhāvo itthattaṃ, evumaṃ – evaṃ+imanti chedo, tvaṃ no satthā mahāmune, atthadhammavidū ise.</p>\r\n<p class="bodytext">uvaṇṇassa ittaṃ, ottañca –</p>\r\n<p class="bodytext">mātito <span class="note">[dī. ni. 1.303]</span>, pitito, mātipakkho, pitipakkho, mātigho <span class="note">[jā. 2.19.118]</span>, pitigho, mattikaṃ dhanaṃ <span class="note">[pārā. 34]</span>, pettikaṃ dhanaṃ, api no lacchasi, kacci no tumhe yāpetha, kathaṃ no tumhe yāpetha, sotukāmattha no tumhe bhikkhave, na no samaṃ atthi <span class="note">[khu. pā. 6.3]</span>, na hi no saṅkarantena <span class="note">[ma. ni. 3.272]</span>, natthi no koci pariyāyo <span class="note">[jā. 1.5.110 (na hi no koci pariyāyo)]</span> – imesu tīsu nusaddo ekaṃsatthe, sosito sotatto ceva <span class="note">[jā. 1.1.94 sotatto sosindo ceva; ma. ni. 1.157]</span> – suṭṭhu sītalo suṭṭhu santattotyattho, jambunadiyā jātaṃ jambonadaṃ.</p>\r\n<p class="bodytext">essa ittaṃ –</p>\r\n<p class="bodytext">okandāmasi bhūtāni, pabbatāni vanāni ca <span class="note">[jā. 2.22.2173]</span> – avakandāmasetyattho, yaṃ karomasi brahmuno, tadajja tuyhaṃ dassāma <span class="note">[dī. ni. 2.370]</span>, idha hemantagimhisu <span class="note">[dha. pa. 286]</span>, buddhapaccekabuddhisu, cetehi cetaputtihi <span class="note">[cariyā. 1.106]</span> – cetaputtehi saddhintyattho.</p>\r\n<p class="bodytext">ossa uttaṃ –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54435,'annya_sadda_10',28,replace(replace('\r\n<p class="bodytext">manuññaṃ, na tenatthaṃ abandhisu <span class="note">[jā. 1.6.7]</span> – so tena vacanena atthaṃ na abandhi na labhītyattho. avhāyantu su yuddhena <span class="note">[jā. 2.22.871]</span> – so <a name="M0.0028"></a> pahāravacanena maṃ avhayantotyattho. api nu hanukā santā <span class="note">[jā. 1.1.146]</span> – no hanukā ekantaṃ khinnā dukkhapattātyattho.</p>\r\n<p class="bodytext">vikārasandhipi ādesasandhirūpattā idha saṅgayhati.</p>\r\n<p class="centered">iti sarādesarāsi.</p>\r\n<p class="bodytext">kavaggato paṭṭhāya vaggāvaggabyañjanānaṃ ādeso dīpiyate.</p>\r\n<p class="bodytext"><a name="para43"></a><span class="paranum">43</span>. <span class="bld">vaggalasehi te</span>.</p>\r\n<p class="bodytext">pañcavaggehi ca la, sehi ca parassa ya-kārassa kvaci te eva vagga la, sā honti vā yathākkamaṃ, ya-kāro pubbarūpattaṃ āpajjatīti vuttaṃ hoti.</p>\r\n<p class="bodytext">nipaccatīti nipako, nipakassa bhāvo nepakkaṃ, vipāko eva vepakkaṃ, vattabbanti vākkaṃ, vākyaṃ vā. pamukhe sādhu pāmokkhaṃ – yassa pubbarūpatte kate ādidutiyassa paṭhamattaṃ, subhagassa bhāvo sobhaggaṃ, dobhaggaṃ, bhuñjitabbanti bhoggaṃ, yuñjitabbanti yoggaṃ, kukkucabhāvo kukkuccaṃ, vattabbanti vāccaṃ, vuccate, paccate, vaṇijānaṃ kammaṃ vāṇijjaṃ, bhuñjitabbanti bhojjaṃ, yuñjitabbanti yojjaṃ.</p>\r\n<p class="bodytext"><a name="para44"></a><span class="paranum">44</span>. <span class="bld">tavaggavaraṇānaṃ ye cavaggabayañā</span> <span class="note">[ka. 269, 41; nī. 104, 106, 119, 121-5]</span>.</p>\r\n<p class="bodytext">ādesabhūte vā vibhattibhūte vā paccayabhūte vā ya-kāre pare tavaggānaṃva, ra, ṇānañca cavagga, ba, ya, ñādesā honti vā yathākkamaṃ.</p>\r\n<p class="bodytext">pokkharañño, pokkharaññā, pokkharaññaṃ, samaṇassa bhāvo sāmaññaṃ, evaṃ brahmaññaṃ, iccetaṃ kusalaṃ <span class="note">[pārā. 411]</span> iccādīni ‘yavā sare’ti sutte udāhaṭāni.</p>\r\n','\r',char(13)),'\n',char(10)),'-43-44-');
INSERT INTO pages VALUES(54436,'annya_sadda_10',29,replace(replace('\r\n<p class="bodytext">paṇḍitassa <a name="M0.0029"></a> bhāvo paṇḍiccaṃ, santassa bhāvo saccaṃ, tathassa bhāvotacchaṃ, yajjevaṃ-yadi+evaṃ, najjo, najjā, najjaṃ, suhadassa bhāvo sohajjaṃ, vattabbanti vajjaṃ, jhānaṃ upasampajja viharati <span class="note">[dha. sa. 160]</span>, upasampajjati, ajjhokāso, bojjhaṅgo, bojjhā, bodhiyā vā, bujjhitabbanti bojjhaṃ, bujjhati, ponopuññaṃ, thanato sambhūtaṃ thaññaṃ.</p>\r\n<p class="bodytext">pavagge yassa pubbarūpaṃ –</p>\r\n<p class="bodytext">vappate, luppate, abbhuggato, abbhokāso, usabhassa bhāvo osabbhaṃ, labhiyateti labbhaṃ, labbhate, gāme hitaṃ gammaṃ, opammaṃ, sokhummaṃ, āgamma, upagamma, gamiyateti gammo, evaṃ dammo, rammo, gammate, rammate.</p>\r\n<p class="bodytext">‘tavagga varaṇāna.’nti iminā suttena yamhi rassa yattaṃ –</p>\r\n<p class="bodytext">kayyate kariyate, ayyo ariyo.</p>\r\n<p class="bodytext">‘vaggalasehi te’ti lato yassa pubbarūpaṃ –</p>\r\n<p class="bodytext">pallaṅko, vipallāso, kosallaṃ, pattakallaṃ.</p>\r\n<p class="bodytext">‘tavaggavaraṇāna.’nti yamhi vassa battaṃ –</p>\r\n<p class="bodytext">puthabyā, puthabyaṃ, bhātu apaccaṃ bhātabyo, korabyo, dive bhavaṃ dibbaṃ dibyaṃ.</p>\r\n<p class="bodytext">‘vaggalasehi te’ti sato yassa pubbarūpaṃ –</p>\r\n<p class="bodytext">rahasi bhavaṃ rahassaṃ, somanassaṃ, domanassaṃ, sovacassaṃ, dovacassaṃ-manogaṇattā majjhe sāgamo, bhāsitabbanti bhassaṃ, ādissa=ādisitvā, uddissa=uddisitvā, upavassa=upavasitvā, samphussa=samphusitvā, tussati, dussati, nassatiiccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54437,'annya_sadda_10',30,replace(replace('\r\n<p class="bodytext"><a name="para45"></a><span class="paranum">45</span>. <span class="bld">tathanarānaṃ</span> <a name="M0.0030"></a> <span class="bld">ṭaṭhaṇalā</span> <span class="note">[rū. 3 (piṭṭhe)]</span>.</p>\r\n<p class="bodytext">tādīnaṃ ṭādiādesā honti vā.</p>\r\n<p class="bodytext">tassa ṭattaṃ –</p>\r\n<p class="bodytext">paṭihaññati, paṭaggi dātabbo, paṭicchanno, paṭippanno, byāvaṭo, udāhaṭo, dukkaṭaṃ iccādi.</p>\r\n<p class="bodytext">thassa ṭhattaṃ –</p>\r\n<p class="bodytext">pīḷanaṭṭho <span class="note">[paṭi. ma. 1.17]</span>, saṅkhataṭṭho <span class="note">[paṭi. ma. 1.17]</span>, aṭṭhiṃkatvā suṇeyya <span class="note">[jā. 2.17.92]</span>, aṭṭhakathā iccādi.</p>\r\n<p class="bodytext">nassa ṇattaṃ –</p>\r\n<p class="bodytext">gāmaṃ netīti gāmaṇi, senaṃ netīti senāṇi, paṇidhi, paṇidhānaṃ, paṇihitaṃ, paṇāmo, pariṇāmo, oṇāmo, uṇṇāmo, karaṇīyaṃ, karaṇaṃ, ñāṇaṃ, tāṇaṃ, pamāṇaṃ, saraṇaṃ, gahaṇaṃ iccādi.</p>\r\n<p class="bodytext">rassa lattaṃ –</p>\r\n<p class="bodytext">paligho, palibodho, palipanno, pallaṅko, taluṇo taruṇo, kalunaṃ paridevayi <span class="note">[jā. 2.22.2151]</span>, mahāsālo, aṭṭhasālinī, nayasālinī iccādīni.</p>\r\n<p class="bodytext">idāni mahāvuttividhānamuccate.</p>\r\n<p class="bodytext">kassa khattaṃ –</p>\r\n<p class="bodytext">nikkhamati, nikkhanto, nekkhamo, rājakiccaṃ karotīti khattā, kattā vā.</p>\r\n<p class="bodytext">dattañca –</p>\r\n<p class="bodytext">sadatthapasuto siyā <span class="note">[dha. pa. 166]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-45-');
INSERT INTO pages VALUES(54438,'annya_sadda_10',31,replace(replace('\r\n<p class="bodytext">yattañca <a name="M0.0031"></a> –</p>\r\n<p class="bodytext">sayaṃ raṭṭhaṃ hitvāna, pupphadānaṃ dadātīti pupphadāniyo pupphadāniko, sippalivane vasatīti sippalivaniyo sippalivaniko, kumāriyā kumārikā.</p>\r\n<p class="bodytext">khassa gattaṃ –</p>\r\n<p class="bodytext">eḷamūgo eḷamūkho.</p>\r\n<p class="bodytext">gassa kattaṃ –</p>\r\n<p class="bodytext">lujjatīti loko, ārogyaṃ abhisajjetīti bhisakko, kulūpako kulūpago, khīrūpako khīrūpago, gīvūpakaṃ gīvūpagaṃ.</p>\r\n<p class="bodytext">ghassa hattaṃ –</p>\r\n<p class="bodytext">sīghajavatāya sīho.</p>\r\n<p class="bodytext">cassa chattaṃ –</p>\r\n<p class="bodytext">vinicchayo, acchariyaṃ, macchariyaṃ, raṃsiyo niccharanti-nigacchantīti attho.</p>\r\n<p class="bodytext">chassa sattaṃ –</p>\r\n<p class="bodytext">atthi sāhassa jīvitaṃ <span class="note">[jā. 2.22.314]</span> -chāhaṃ+assāti chedo, saḷāyatanaṃ.</p>\r\n<p class="bodytext">jassa dattaṃ –</p>\r\n<p class="bodytext">parasenaṃ jinātītipassenadī-mahāvuttinā saralopo, rassa pararūpaṃ.</p>\r\n<p class="bodytext">yattañca –</p>\r\n<p class="bodytext">nissāya jāyatīti niyo, niyako vā, niyaṃ puttaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54439,'annya_sadda_10',32,replace(replace('\r\n<p class="bodytext">ñassaṇattaṃ – <a name="M0.0032"></a></p>\r\n<p class="bodytext">paṇṇatti paññatti, paṇṇāsaṃ paññāsaṃ. paṇṇavīsati pañcavīsati.</p>\r\n<p class="bodytext">nattañca –</p>\r\n<p class="bodytext">nāmamattaṃ na nāyati, animittā na nāyare <span class="note">[visuddhi. aṭṭha. 1.228]</span> – na paññāyantīti attho.</p>\r\n<p class="bodytext">tassa kattaṃ –</p>\r\n<p class="bodytext">niyako niyato.</p>\r\n<p class="bodytext">thattañca –</p>\r\n<p class="bodytext">nitthiṇṇo, nittharaṇaṃ, netthāraṃ.</p>\r\n<p class="bodytext">nattañca –</p>\r\n<p class="bodytext">jino, pino, lino, paṭisallino, paḷino, malino, supino, pahīno, dhuno, puno, luno, āhunaṃ, pāhunaṃ.</p>\r\n<p class="bodytext">dassa ḍattaṃ –</p>\r\n<p class="bodytext">chavaḍāho, disāḍāho, kāyaḍāho.</p>\r\n<p class="bodytext">ḷattañca –</p>\r\n<p class="bodytext">pariḷāho, āgantvā chavaṃ dahanti etthāti āḷahanaṃ, susānaṃ.</p>\r\n<p class="bodytext">tattañca –</p>\r\n<p class="bodytext">sugato, tathāgato, kusito, udati pasavatīti utu.</p>\r\n<p class="bodytext">dhassa dattaṃ –</p>\r\n<p class="bodytext">ekamidāhaṃ bhikkhave samayaṃ <span class="note">[ma. ni. 1.501]</span> -idhāti vā nipāto.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54440,'annya_sadda_10',33,replace(replace('\r\n<p class="bodytext">hattañca <a name="M0.0033"></a> –</p>\r\n<p class="bodytext">sāhu dassanamariyānaṃ <span class="note">[dha. pa. 206]</span>, saṃhitaṃ, vihitaṃ, pihitaṃ, abhihitaṃ, sannihitaṃ, paṇihitaṃ, saddahati, vidahati, pidahati.</p>\r\n<p class="bodytext">nassa uttaṃ –</p>\r\n<p class="bodytext">upaññāso=upanyāso, ñāyo=nyāyo-niccaṃ eti phalaṃ etenāti ñāyo, ñeyyaṃ=neyyaṃ.</p>\r\n<p class="bodytext">yattañca –</p>\r\n<p class="bodytext">thenassa kammaṃ theyyaṃ, therādhinanti therādheyyaṃ, pātimokkhaṃ, parādheyyakaṃ dukkhaṃ.</p>\r\n<p class="bodytext">passa phattaṃ –</p>\r\n<p class="bodytext">nipphajjati, nipphatti, nipphannaṃ.</p>\r\n<p class="bodytext">battañca –</p>\r\n<p class="bodytext">sambahulaṃ=sampahulaṃ, bahusanto na bharati <span class="note">[su. ni. 98]</span> =pahu santo na bharati.</p>\r\n<p class="bodytext">bhassa phattaṃ –</p>\r\n<p class="bodytext">anantaṃ sabbatopaphaṃ <span class="note">[dī. ni. 1.499]</span>.</p>\r\n<p class="bodytext">massa pattaṃ –</p>\r\n<p class="bodytext">cirappavāsiṃ <span class="note">[dha. pa. 219]</span>, hatthippabhinnaṃ <span class="note">[dha. pa. 326]</span>.</p>\r\n<p class="bodytext">yassa vattaṃ –</p>\r\n<p class="bodytext">dīghāvu kumāro <span class="note">[mahāva. 459]</span> =dīghāyu kumāro, āyuṃ dhāretīti āvudhaṃ=āyudhaṃ, āyu assa atthīti atthe ‘āvuso’ti nipāto, kasāvo=kasāyo, kāsāvaṃ=kāsāyaṃ, sāliṃ lunātīti sālilāyako, tiṇalāyako.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54441,'annya_sadda_10',34,replace(replace('\r\n<p class="bodytext">lassa rattaṃ <a name="M0.0034"></a> –</p>\r\n<p class="bodytext">nīlaṃ jalaṃ etthāti nerañjarā, jalaṃ gaṇhituṃ alanti arañjaro, sassataṃ pareti, ucchedaṃ pareti-paletīti attho.</p>\r\n<p class="bodytext">vassa pattaṃ –</p>\r\n<p class="bodytext">pipāsati pivāsati.</p>\r\n<p class="bodytext">battañca –</p>\r\n<p class="bodytext">byākato, byatto, byañjanaṃ, sīlabbataṃ, nibbānaṃ, nibbutaṃ, dibbaṃ, dibbati, sibbati, kubbati, kubbanto, krubbati, krubbanto, asevitabbattā vāretabboti bālo, pabbajito, pabbajjā iccādi.</p>\r\n<p class="bodytext">sassa chattaṃ –</p>\r\n<p class="bodytext">ucchiṭṭhaṃ-avasiṭṭhantyattho, "dibbā saddā niccharanti, raṃsiyo <span class="note">[vi. va. 730]</span> niccharantī"ti etthāpi sassa chattaṃ icchanti.</p>\r\n<p class="bodytext">tattañca –</p>\r\n<p class="bodytext">uttiṭṭhapattaṃ upanāmenti <span class="note">[mahāva. 64]</span>, "uttiṭṭhe nappamajjeyyā" ti ettha pana uddissa tiṭṭhanaṃ uttiṭṭhanti attho. "uddissa ariyā tiṭṭhanti, esā ariyāna yācanā <span class="note">[jā. 1.7.59]</span>"ti vuttaṃ.</p>\r\n<p class="bodytext">hassa ghattaṃ –</p>\r\n<p class="bodytext">niccaṃ dahati etthāti nidāgho, laghu lahu.</p>\r\n<p class="bodytext">ḷassa ḍattaṃ –</p>\r\n<p class="bodytext">garuḍo garuḷo.</p>\r\n<p class="centered">iti byañjanādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54442,'annya_sadda_10',35,replace(replace('\r\n<p class="bodytext">missakādeso <a name="M0.0035"></a> vuccate.</p>\r\n<p class="bodytext">avassa uttaṃ –</p>\r\n<p class="bodytext">uddhammo, ubbinayo, uppatho, ummaggo, uññā avaññā, uññātaṃ avaññātaṃ, ujjhānasaññī.</p>\r\n<p class="bodytext">ottañca –</p>\r\n<p class="bodytext">onaddhā, okāso, ovādo, olokanaṃ iccādi.</p>\r\n<p class="bodytext">vassa ottaṃ –</p>\r\n<p class="bodytext">uposatho – upavasathoti ṭhiti, nonītaṃ navanītaṃ, nivatthakoco nivatthakavaco, ko te balaṃ mahārāja, ko nu te rathamaṇḍalaṃ <span class="note">[jā. 2.22.1880]</span> – kvati attho. ko te diṭṭho vā suto vā, vānaro dhammiko iti, ko nume gotamasāvakā gatā-kva nu+imeti chedo, soṇṇaṃ suvaṇṇaṃ iccādi.</p>\r\n<p class="bodytext">kussa kruttaṃ –</p>\r\n<p class="bodytext">krubbati kubbati.</p>\r\n<p class="bodytext">ttassa trattaṃ –</p>\r\n<p class="bodytext">atrajo putto, khetrajo putto attajo, khettajo, gotrabhū, vatrabhū, citraṃ, vicitraṃ, cittaṃ, vicittaṃ, utrastamidaṃ cittaṃ <span class="note">[saṃ. ni. 1.98]</span>, utrāsī palāyī <span class="note">[saṃ. ni. 1.249]</span>, yātrā ca me bhavissati <span class="note">[ma. ni. 1.23]</span> iccādi.</p>\r\n<p class="bodytext">dassa drattaṃ –</p>\r\n<p class="bodytext">indriyaṃ, sukho udayo yassāti sukhudrayaṃ, dukkhudrayaṃ kammaṃ <span class="note">[ma. ni. 2.109]</span>, pathavī undriyyati <span class="note">[saṃ. ni. 1.158]</span> – bhijjatītyattho, mittadrubbho mittaddubbho.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54443,'annya_sadda_10',36,replace(replace('\r\n<p class="bodytext">ddassa drattaṃ <a name="M0.0036"></a> –</p>\r\n<p class="bodytext">bhadraṃ bhaddaṃ, asso bhadro <span class="note">[dha. pa. 143]</span>, sadā bhadrāni passati <span class="note">[dī. ni. 2.153]</span>, sabbe bhadrāni passantu <span class="note">[jā. 1.2.105]</span>, bhadrāni bhadrāni yānāni yojetvā, ludraṃ <span class="note">[dī. ni. 2.43]</span> luddaṃ.</p>\r\n<p class="bodytext">bassa brattaṃ –</p>\r\n<p class="bodytext">brahāvanaṃ, brahantaṃ vā vanappatiṃ <span class="note">[jā. 1.1.14]</span>, brahmā, brāhmaṇo – bāhitapāpattā arahā brāhmaṇoti vuccati, brahmuno apaccanti jātibrāhmaṇo vuccati.</p>\r\n<p class="bodytext">va, vīnaṃ byattaṃ –</p>\r\n<p class="bodytext">byayo=vayo-vināsotyattho, kiccākiccesu byāvaṭo=vāvaṭo, paṅke byasanno=visanno, byamhito=vimhito, byamhaṃ=vimānaṃ-mānassa mhattaṃ.</p>\r\n<p class="bodytext">kkhassa cchattaṃ –</p>\r\n<p class="bodytext">acchi=akkhi, sacchi=sakkhi-saha akkhinā vattatīti atthe nipāto, paccakkhanti attho. nibbānaṃ sacchikaroti <span class="note">[mi. pa. 5.3.12]</span>, macchikā=makkhikā, lacchī=lakkhī-sirīti attho.</p>\r\n<p class="bodytext">mahāvuttinā akkharasaṃkhittaṃ hoti –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54444,'annya_sadda_10',37,replace(replace('\r\n<p class="bodytext">ācero ācariyo, na mātāpitarasaṃvaḍḍho, anācerakule vasaṃ <span class="note">[jā. 1.1.9]</span>, āceramhi susikkhitā <span class="note">[jā. 1.7.82]</span>, brahmacero brahmacariyo, tiṇhaṃ tikhiṇaṃ, taṇhā tasiṇā, suṇhā suṇisā, abhiṇhaṃ abhikkhaṇaṃ, paṇho pubbaṇho, paṇhe vajjho mahosadho <span class="note">[jā. 1.15.324]</span>, surāmerayo-surāmereyyo, surāmereyyapānāni, yo naro anuyuñjati <span class="note">[dha. pa. 247]</span>. kammadhārayo= kammadhāreyyo<a name="M0.0037"></a>, pāṭihīraṃ pāṭiheraṃ pāṭihāriyaṃ, accheraṃ acchariyaṃ, maccheraṃ maccharaṃ macchariyaṃ iccādi.</p>\r\n<p class="bodytext">akkharavaḍḍhipi hoti –</p>\r\n<p class="bodytext">ekacciyo ekacceyyo ekacco, mātiyo macco, kiccayaṃ kiccaṃ, paṇḍitiyaṃ paṇḍiccaṃ, suvāmi sāmi, suvāmini sāmini, suvakehi puttehi sakehi puttehi, sattavo satto, tvañca uttamasattavo <span class="note">[jā. 2.21.76]</span>, evaṃ uttamasattavo <span class="note">[jā. 2.21.79]</span> iccādi.</p>\r\n<p class="centered">iti missakādesarāsi.</p>\r\n<p class="bodytext">bindādeso dīpiyate.</p>\r\n<p class="bodytext"><a name="para46"></a><span class="paranum">46</span>. <span class="bld">vagge vagganto</span> <span class="note">[ka. 31; rū. 49; nī. 138-9]</span>.</p>\r\n<p class="bodytext">vaggabyañjane pare niggahītassa sakavaggantabyañjanādeso hoti vā.</p>\r\n<p class="bodytext">dīpaṅkaro, saṅkhāro, saṅgaho, sañcāro, sañjāto, saṇṭhitaṃ, attantapo, parantapo, amatandado, purindado, sandhi, sannidhi, sampatti, sambuddhā, sambhavo, sambhāro, sambhinno, sammato iccādīsu niccaṃ, taṅkaro, taṃkaro iccādīsu aniccaṃ, buddhaṃ saraṇaṃ gacchāmi <span class="note">[khu. pā. 1.saraṇattaya]</span>, na taṃ kammaṃ kataṃ sādhu iccādīsu <span class="note">[dha. pa. 67]</span> natthi.</p>\r\n<p class="bodytext">mahāvuttividhānamuccate.</p>\r\n<p class="bodytext">vaggāvaggesu byañjanesu paresu niggahītaṃ pararūpaṃ gacchati –</p>\r\n','\r',char(13)),'\n',char(10)),'-46-');
INSERT INTO pages VALUES(54445,'annya_sadda_10',38,replace(replace('\r\n<p class="bodytext">sakkaroti, sakkato, sakkāro, sakkaccaṃ, takkattā, takkaro, takkhaṇaṃ taṅkhaṇaṃ taṃ khaṇaṃ, taggatikaṃ taṃ gatikaṃ, tanninno, tappoṇo, tappabbhāro, tappadhāno, etapparamo, yagguṇo yaṃguṇo, talleṇā, malleṇā, sallekho, paṭisallīno, tabbaṇṇanā taṃvaṇṇanā, tassamo taṃsamo, idappaccayatā<a name="M0.0038"></a>, cirappavāsiṃ, hatthippabhinnaṃ iccādi. imasmiṃ ganthe ekattha siddhampi taṃ taṃ rūpaṃ tattha tattha punappunampi vidhiyyati ñāṇavicittatthaṃ.</p>\r\n<p class="bodytext"><a name="para47"></a><span class="paranum">47</span>. <span class="bld">mayadā sare</span> <span class="note">[ka. 34, 35; rū. 34, 52; nī. 142-5]</span>.</p>\r\n<p class="bodytext">sare pare niggahītassa kvaci ma, ya, dā honti vā.</p>\r\n<p class="bodytext">tattha dādeso ya, ta, etasaddehi napuṃsake dissati –</p>\r\n<p class="bodytext">yadabravi <span class="note">[jā. 1.2.143]</span>, tadaniccaṃ <span class="note">[ma. ni. 2.19]</span>, etadavoca satthā <span class="note">[su. ni. dvayatānupassanāsutta]</span>.</p>\r\n<p class="bodytext">samāse pana dādeso tiliṅge dissati –</p>\r\n<p class="bodytext">yadanantaraṃ, tadanantaraṃ, etadatthā kathā <span class="note">[a. ni. 2.68]</span>. etadatthā mantanā <span class="note">[a. ni. 2.68]</span> -tattha yassa atthassa vā yassa padassa vā yassā kathāya vā anantaraṃ yadanantaraṃ.</p>\r\n<p class="bodytext">kvacitveva? yametaṃ vārijaṃ pupphaṃ, adinnaṃ upasiṅghasi <span class="note">[jā. 1.6.115]</span>.</p>\r\n<p class="bodytext">mādeso ya, ta, etasaddehi pumitthiliṅgesu dissati –</p>\r\n<p class="bodytext">yamāhu devesu sujampatīti <span class="note">[jā. 1.15.54]</span>, tamatthaṃ pakāsento satthā <span class="note">[jā. aṭṭha. 1.20.35]</span>, etamatthaṃ viditvā <span class="note">[mahāva. 2-3]</span>.</p>\r\n<p class="bodytext">aññasaddehi pana dve ādesā tiliṅge dissanti –</p>\r\n<p class="bodytext">sakadāgāmī, evametamabhiññāya <span class="note">[su. ni. 1221]</span> iccādi.</p>\r\n<p class="bodytext">yādeso idaṃsadde pare tasaddamhā eva kvaci dissati –</p>\r\n<p class="bodytext">tayidaṃ na sādhu <span class="note">[jā. 2.22.279]</span>, tayidaṃ na suṭṭhu <span class="note">[jā. 2.22.279]</span>.</p>\r\n<p class="bodytext"><a name="para48"></a><span class="paranum">48</span>. <span class="bld">yevahisu ño</span></p>\r\n','\r',char(13)),'\n',char(10)),'-47-48-');
INSERT INTO pages VALUES(54446,'annya_sadda_10',39,replace(replace('\r\n<p class="bodytext">ya<a name="M0.0039"></a>, eva, hisaddesu paresu niggahītassa ño hoti. yassa pubbarūpattaṃ.</p>\r\n<p class="bodytext">ānantarikaññamāhu <span class="note">[khu. pā. 6.5]</span> – ānantarikaṃ + yaṃ + āhūti chedo, yaññadeva-yaṃ + yaṃ + eva, taññeva taṃ+eva, purisaññeva, paccattaññeva, tañhi, purisañhi, atthasañhito atthasaṃhito, dhammasañhito dhammasaṃhito.</p>\r\n<p class="bodytext"><a name="para49"></a><span class="paranum">49</span>. <span class="bld">ye saṃssa</span> <span class="note">[ka. 33; rū. 51; nī. 141]</span>.</p>\r\n<p class="bodytext">yamhi pare saṃ upasaggassa niggahītassa ño hoti. yassa pubbarūpattaṃ.</p>\r\n<p class="bodytext">saññogo saṃyogo, saññutto saṃyutto. saṃyojanaṃ saṃyojanaṃ, saññamo saṃyamo, saññato saṃyato, saññamati saṃyamati, saññācikā saṃyācikā kuṭiṃ <span class="note">[pārā. 348]</span> iccādi.</p>\r\n<p class="centered">iti bindādesarāsi.</p>\r\n<p class="centered">ādesasandhirāsi niṭṭhito.</p>\r\n<p class="subhead">āgamasandhi</p>\r\n<p class="bodytext">athāgamasandhi dīpiyate.</p>\r\n<p class="bodytext">mahāvuttinā sarāgamo –</p>\r\n<p class="bodytext">a –</p>\r\n','\r',char(13)),'\n',char(10)),'-49-');
INSERT INTO pages VALUES(54447,'annya_sadda_10',40,replace(replace('\r\n<p class="bodytext">paṇṇasālaṃ amāpetvā <span class="note">[jā. 2.22.1913]</span>, paṇṇasālaṃ amāpiya <span class="note">[jā. 1.1.148]</span> – māpetvā iccevattho, na cāpi apunappunaṃ, hatthibondiṃ pavekkhāmi <span class="note">[jā. 1.1.148]</span> -punappunaṃ iccevattho<a name="M0.0040"></a>, natthi loke anāmataṃ <span class="note">[jā. 1.2.31]</span> – amata pubbaṃ ṭhānanti attho, anavajjaṃ, anamataggo, jaccandho, jaccabadhiro, jaccamūgo, jaccapaṇḍako.</p>\r\n<p class="bodytext">ā –</p>\r\n<p class="bodytext">aḍḍhe ājāyare kule <span class="note">[saṃ. ni. 1.49]</span>, manussesu paccājāto, āpūrati tassa yaso <span class="note">[pari. 386]</span>.</p>\r\n<p class="bodytext">i –</p>\r\n<p class="bodytext">dhammikathaṃ katvā <span class="note">[pārā. 39]</span>, sarantā sapanti gacchantīti sarisapā.</p>\r\n<p class="bodytext">ī –</p>\r\n<p class="bodytext">kabaḷīkāro, manasīkāro, manasīkaroti, tappākaṭīkaroti, dūrībhūto, abyayībhāvo.</p>\r\n<p class="bodytext">u –</p>\r\n<p class="bodytext">ñātiparijinassa bhāvo ñātipārijuññaṃ, evaṃ bhogapārijuññaṃ- parijinassāti parihānassa, parikkhayassa.</p>\r\n<p class="bodytext">o –</p>\r\n<p class="bodytext">parosataṃ, saradosataṃ, disodisaṃ <span class="note">[dha. pa. 42]</span> iccādi.</p>\r\n<p class="bodytext">‘atippago kho tāva piṇḍāya caritu’ <span class="note">[dī. ni. 3.1]</span> nti ettha pātottho pagosaddo eva.</p>\r\n<p class="centered">iti sarāgamarāsi.</p>\r\n<p class="bodytext"><a name="para50"></a><span class="paranum">50</span>. <span class="bld">vanataragācāgamā</span></p>\r\n<p class="bodytext">sare pareva na, ta, ra, gā ca ma, ya, dā ca āgamā honti.</p>\r\n<p class="bodytext"><span class="bld">go, to, do, no, mo, yo, ro, vo,</span></p>\r\n','\r',char(13)),'\n',char(10)),'-50-');
INSERT INTO pages VALUES(54448,'annya_sadda_10',41,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0041"></a> go –</p>\r\n<p class="bodytext">ariyehi puthagevāyaṃ janoti puthujjano <span class="note">[mahāniddese]</span>, idha pana pagosaddo eva, pageva vutyassa, pageva manussitthiyāti <span class="note">[pārā. 55]</span>.</p>\r\n<p class="bodytext">to –</p>\r\n<p class="bodytext">ajjatagge <span class="note">[dī. ni. 1.250]</span>, tasmātiha <span class="note">[ma. ni. 1.29]</span>, katamo nāma so rukkho, yassa tevaṃ gataṃ phalaṃ <span class="note">[jā. 2.18.10]</span> -tevanti evaṃ.</p>\r\n<p class="bodytext">do –</p>\r\n<p class="bodytext">udaggo, udabbahi, udapādi, udayo, udāhaṭo, udito, udīrito, dubhato vuṭṭhānaṃ <span class="note">[paṭisambhidāmagge; visuddhimagge]</span>, dubhayāni viceyya paṇḍarāni <span class="note">[su. ni. 531]</span>, todeyya, kappā dubhayo <span class="note">[su. ni. 1131]</span> – dve isayoti attho. kiñcideva, kocideva, kismiñcideva, yāvadeva, tāvadeva, valutte-yāvade, tāvadeti siddhaṃ, punadeva, sakideva, sammadeva-dāgame rasso, sammadakkhāto <span class="note">[saṃ. ni. 5.195]</span>, sammadaññā vimutto <span class="note">[ma. ni. 2.234]</span>, bahudeva rattiṃ <span class="note">[a. ni. 3.101]</span>, ahudeva bhayaṃ <span class="note">[dī. ni. 1.159]</span> iccādi.</p>\r\n<p class="bodytext">no –</p>\r\n<p class="bodytext">ito nāyati, ciraṃ nāyati, kamme sādhu kammaniyaṃ kammaññaṃ, attano idaṃ attaniyaṃ, addhānaṃ khamatīti addhaniyaṃ, lobhassa hitaṃ lobhaniyaṃ lobhaneyyaṃ, dosaniyaṃ dosaneyyaṃ, mohaniyaṃ mohaneyyaṃ, oghaniyaṃ, yoganiyaṃ, ganthaniyaṃ, niddhunanaṃ, niddhunanako, sañjānanaṃ, sañjānanako, saññāpanako iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54449,'annya_sadda_10',42,replace(replace('\r\n<p class="bodytext">mo <a name="M0.0042"></a> –</p>\r\n<p class="bodytext">lahumessati <span class="note">[dha. pa. 369]</span>, garumessati, maggamatthi <span class="note">[vibha. aṭṭha. 189]</span>, aggamakkhāyati <span class="note">[a. ni. 4.34]</span>, uragāmiva <span class="note">[jā. 1.7.30]</span>, arahatāmiva <span class="note">[dī. ni. 2.348]</span> iccādīni. tathā kena te idha mijjhati <span class="note">[pe. va. 181]</span>, rūpāni manupassati <span class="note">[dha. sa. aṭṭha. 596]</span>, ākāse mabhipūjaye, aññamaññassa <span class="note">[ma. ni. 3.40]</span>, ekamekassa <span class="note">[pārā. aṭṭha. 1.23]</span>, samaṇamacalo <span class="note">[a. ni. 4.87]</span>, adukkhamasukhā vedanā <span class="note">[saṃ. ni. 4.250]</span> iccādi.</p>\r\n<p class="bodytext">yo –</p>\r\n<p class="bodytext">nayimassa vijjā mayamatthi <span class="note">[jā. 1.3.25]</span>, yathayidaṃ <span class="note">[a. ni. 1.21-22]</span>, tathayidaṃ, chayime dhammā <span class="note">[a. ni. 6.11]</span>, navayime dhammā <span class="note">[a. ni. 9.9]</span>, dasayime dhammā <span class="note">[a. ni. 10.16]</span>, mamayidaṃ, soyeva, teyeva, taṃyeva taññeva, tehiyeva, tesaṃyeva tesaññeva, tasmiyeva, buddhāyeva, buddhesuyeva, bodhiyāyeva kāraṇā <span class="note">[cariyā. 1.65]</span>, hotiyeva, atthiyeva iccādi. tiyantaṃ, aggiyāgāre, catutthīyatthe iccādīni ivaṇṇantarūpāni yāgamenāpi sijjhantiyeva.</p>\r\n<p class="bodytext">ro –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54450,'annya_sadda_10',43,replace(replace('\r\n<p class="bodytext">nirantaraṃ, niratthakaṃ, nirāhāro, nirābādho, nirālayo, nirindhano aggi, nirīhakaṃ, nirudakaṃ, nirutti, niruttaro, nirūmikā nadī, nirojaṃ, duratikkamo, durabhisambhavo, durāsadā buddhā <span class="note">[apa. thera 1.40.270]</span>, durākhyāto dhammo <span class="note">[dī. ni. 3.166]</span>, durāgataṃ, duruttaṃ vacanaṃ <span class="note">[a. ni. 5.140]</span>, pāturahosi <span class="note">[mahāva. 8]</span>, pāturahu <span class="note">[jā. 1.14.202]</span>, pāturahesuṃ <span class="note">[a. ni. 3.71]</span>, pātarāso, punareti, dhīratthu <span class="note">[jā. 1.1.13]</span>, caturaṅgikaṃ jhānaṃ <span class="note">[dha. sa. 168]</span>, caturārakkhā, caturāsītisahassāni, caturiddhilābho, <a name="M0.0043"></a> caturoghā, vuddhiresā <span class="note">[dī. ni. 1.251]</span>, pathavīdhāturevesā <span class="note">[ma. ni. 3.348-349]</span>, āpodhāturevesā <span class="note">[ma. ni. 3.350]</span>, sabbhireva samāsetha, nakkhattarājāriva tārakānaṃ, vijjuriva abbhakūṭe, āraggeriva, usabhoriva <span class="note">[su. ni. 29]</span>, yathariva, tathariva <span class="note">[dī. ni. 1.263]</span> -rāgame rasso. ettha ca yathā "atiriva kallarūpā <span class="note">[su. ni. 688]</span>, ativiya lābhaggayasaggapatto, paraṃviya mattāya" iccādīsu iva, viyasaddā evatthe vattanti, tathā "yathariva, tathariva, varamhākaṃ bhusāmiva <span class="note">[jā. 1.3.108]</span>, netaṃ ajjatanāmiva" iccādīsu ivasaddo evatthe vattati.</p>\r\n<p class="bodytext">vo –</p>\r\n<p class="bodytext">duvaṅgulaṃ, duvaṅgikaṃ, tivaṅgulaṃ, tivaṅgikaṃ, pāguññavujutā, vusitaṃ, vuttaṃ, vuccate, āsanā vuṭṭhāti <span class="note">[pāci. 547]</span>, vuṭṭhānaṃ, vuṭṭhahitvā, bhikkhuvāsane, puthuvāsane, sayambhuvāsane iccādīni uvaṇṇantarūpāni vāgamenāpi sijjhantiyeva.</p>\r\n<p class="bodytext"><a name="para51"></a><span class="paranum">51</span>. <span class="bld">chā ḷo</span>.</p>\r\n<p class="bodytext">sare pare chamhā ḷāgamo hoti.</p>\r\n<p class="bodytext">chaḷaṅgaṃ, chaḷāyatanaṃ, chaḷāsītisahassāni <span class="note">[pe. va. 374]</span>, atthassa dvārā pamukhā chaḷete <span class="note">[jā. 1.1.84]</span>, chaḷevānusayā honti <span class="note">[abhidhammatthasaṅgaha]</span>, chaḷabhiññā mahiddhikā <span class="note">[bu. vaṃ. 3.5]</span>.</p>\r\n<p class="bodytext">mahāvuttividhānamuccate.</p>\r\n','\r',char(13)),'\n',char(10)),'-51-');
INSERT INTO pages VALUES(54451,'annya_sadda_10',44,replace(replace('\r\n<p class="bodytext">sare <a name="M0.0044"></a> pare manādīhi sāgamo –</p>\r\n<p class="bodytext">manasikāro, mānasiko, cetasiko, abyaggamanaso naro <span class="note">[a. ni. 1.30]</span>, putto jāto acetaso, ure bhavo oraso iccādi.</p>\r\n<p class="bodytext">sare pare bahulaṃ hāgamo –</p>\r\n<p class="bodytext">māhevaṃ ānanda <span class="note">[dī. ni. 2.95]</span>, nohetaṃ bhante <span class="note">[dī. ni. 1.185-186]</span>, nohidaṃ bho gotama <span class="note">[dī. ni. 1.263]</span>, nahevaṃ vattabbe <span class="note">[kathā. 1]</span>, hevaṃ vattabbe, hevaṃ vadati, ujū ca suhujūca <span class="note">[khu. pā. 9.1]</span>, suhuṭṭhitaṃ sukhaṇo iccādi.</p>\r\n<p class="centered">iti byañjanāgamarāsi.</p>\r\n<p class="bodytext"><a name="para52"></a><span class="paranum">52</span>. <span class="bld">niggahītaṃ</span> <span class="note">[ka. 35; rū. 21 (piṭṭhe); nī. 56]</span>.</p>\r\n<p class="bodytext">niggahītaṃ kvaci āgataṃ hoti vā.</p>\r\n<p class="bodytext">upavassaṃ kho pana <span class="note">[pārā. 653]</span>, navaṃ pana bhikkhunā cīvaralābhena <span class="note">[pāci. 368]</span>, appamādo amataṃ padaṃ <span class="note">[dha. pa. 21]</span>, cakkhuṃ udapādi <span class="note">[mahāva. 15]</span>, aṇuṃthūlāni <span class="note">[dha. pa. 265]</span>, kattabbaṃ kusalaṃ bahuṃ <span class="note">[dha. pa. 53]</span>, avaṃsirā patanti <span class="note">[jā. 1.11.35]</span>, yadattho, tadattho, etadattho, takkattā, takkaro iccādīni pubbe vuttāneva, tathā taṃsampayutto, tabbohāro, tabbahulo iccādi.</p>\r\n<p class="centered">iti bindāgamarāsi.</p>\r\n<p class="bodytext">mahāvuttinā padānaṃ ante gata, jāta, anta saddā āgamā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-52-');
INSERT INTO pages VALUES(54452,'annya_sadda_10',45,replace(replace('\r\n<p class="bodytext">rūpagataṃ <a name="M0.0045"></a> <span class="note">[ma. ni. 2.133]</span> vedanāgataṃ <span class="note">[ma. ni. 2.133]</span>, saññāgataṃ <span class="note">[ma. ni. 2.133]</span>, gūthagataṃ <span class="note">[ma. ni. 2.119]</span>, muttagataṃ <span class="note">[ma. ni. 2.119]</span>, diṭṭhigataṃ <span class="note">[mahāva. 66]</span>, atthajātaṃ <span class="note">[pārā. aṭṭha. 1.paṭhamamahāsaṅgītikathā]</span>, dhammajātaṃ, suttanto <span class="note">[kathā. 226]</span>, vananto, sammākammanto, micchākammanto iccādi.</p>\r\n<p class="centered">āgamasandhirāsi niṭṭhito.</p>\r\n<p class="subhead">dvibhāvasandhi</p>\r\n<p class="bodytext">atha dvibhāvasandhi dīpiyate.</p>\r\n<p class="bodytext">dvibhāvo tividho. tattha pakkamo, parakkamo iccādi <span class="bld">byañjanadvittaṃ</span> nāma. rukkhaṃ rukkhaṃ siñcati iccādi <span class="bld">vibhatyantapadadvittaṃ</span> nāma. titikkhā, tikicchā, jagamā, jagamu iccādi <span class="bld">dhātupadadvittaṃ</span> nāma.</p>\r\n<p class="bodytext"><a name="para53"></a><span class="paranum">53</span>. <span class="bld">saramhā dve</span> <span class="note">[ka. 28; rū. 40; nī. 67]</span>.</p>\r\n<p class="bodytext">saramhā parassa byañjanassa kvaci dve rūpāni honti.</p>\r\n<p class="bodytext">tattha saramhā pa, pati, paṭīnaṃ passa dvittaṃ –</p>\r\n<p class="bodytext">appamādo, idhappamādo, vippayutto, sammappadhānaṃ, appativattiyaṃ dhammacakkaṃ <span class="note">[mahāva. 17]</span>, suppatiṭṭhito, appaṭipuggalo, vippaṭisāro, suppaṭipanno iccādi.</p>\r\n<p class="bodytext">saramhāti kiṃ? sampayutto.</p>\r\n<p class="bodytext">kī, kudha, kamu, kusa, gaha, juta, ñā, si, su, sambhu, sara, sasa iccādīnaṃ dhātūnañca, u, du, nipubbānaṃ padānañca ādibyañjanassa dvittaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-53-');
INSERT INTO pages VALUES(54453,'annya_sadda_10',46,replace(replace('\r\n<p class="bodytext">kī <a name="M0.0046"></a> –</p>\r\n<p class="bodytext">vikkināti, vikkayo, dhanakkīto.</p>\r\n<p class="bodytext">kudha –</p>\r\n<p class="bodytext">akkuddhā, akkodho.</p>\r\n<p class="bodytext">kamu –</p>\r\n<p class="bodytext">abhikkamati, abhikkamo, abhikkanto, akkamati, akkamo, akkanto, parakkamati, parakkamo, vikkamati, vikkamo, okkamati, okkanto.</p>\r\n<p class="bodytext">kusa –</p>\r\n<p class="bodytext">akkosati, akkoso.</p>\r\n<p class="bodytext">gaha –</p>\r\n<p class="bodytext">paggaṇhāti, paggaho, viggaho, pariggaho, anuggaho.</p>\r\n<p class="bodytext">juta –</p>\r\n<p class="bodytext">ujjotati, vijjotati.</p>\r\n<p class="bodytext">ñā –</p>\r\n<p class="bodytext">aññā, paññā, abhiññā, pariññā, viññāṇaṃ, sabbaññutaññāṇaṃ, rattaññū, atthaññū, dhammaññū.</p>\r\n<p class="bodytext">si –</p>\r\n<p class="bodytext">atissayo, bhūmassito, gehassito.</p>\r\n<p class="bodytext">su –</p>\r\n<p class="bodytext">appassuto, bahussuto, vissuto, assavo, anassavo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54454,'annya_sadda_10',47,replace(replace('\r\n<p class="bodytext">sambhu <a name="M0.0047"></a> –</p>\r\n<p class="bodytext">passambhati, passaddhi, passaddhā.</p>\r\n<p class="bodytext">sara –</p>\r\n<p class="bodytext">anussarati, anussati, anussaro.</p>\r\n<p class="bodytext">sasa –</p>\r\n<p class="bodytext">assasati, assasanto, assāso, passāso.</p>\r\n<p class="bodytext">saja –</p>\r\n<p class="bodytext">vissajjeti, vissajjanto, vissaggo.</p>\r\n<p class="bodytext">caja –</p>\r\n<p class="bodytext">pariccajati, pariccajanto, pariccāgo iccādi.</p>\r\n<p class="bodytext">upubbe –</p>\r\n<p class="bodytext">ukkaṃsati, ukkaṃso, uggaho, uccāreti, uccāro, uccayo, samuccayo, ujjalo, samujjalo, uṇṇamati, uttarati iccādi.</p>\r\n<p class="bodytext">dupubbe –</p>\r\n<p class="bodytext">dukkaṭaṃ, dukkaraṃ, duggati, duccaritaṃ, duttaro, duddamo, dunnayo, dupposo, dubbalo, dummaggo, dullabho iccādi.</p>\r\n<p class="bodytext">nipubbe –</p>\r\n<p class="bodytext">nikkamo, nikkhanto, niggato, niccoro, nijjaro, niddoso, nippāpo, nimmito, nimmāno, niyyānaṃ, nillolo, nibbānaṃ, nissayo iccādi.</p>\r\n<p class="bodytext">tika, taya, tiṃsānaṃ tassa dvittaṃ –</p>\r\n<p class="bodytext">kusalattikaṃ, vedanattikaṃ, vatthuttayaṃ, ratanattayaṃ, dvattiṃsaṃ, tettiṃsaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54455,'annya_sadda_10',48,replace(replace('\r\n<p class="bodytext">catu<a name="M0.0048"></a>, chehi parabyañjanassa dvittaṃ –</p>\r\n<p class="bodytext">catubbidhaṃ, catuddasa, catuddisaṃ, catuppadaṃ, chabbidhaṃ, channavuti.</p>\r\n<p class="bodytext">vā tveva? catusaccaṃ, chasataṃ.</p>\r\n<p class="bodytext">santassa satte parabyañjanassa niccaṃ dvittaṃ –</p>\r\n<p class="bodytext">sajjano, sappuriso, saddhammo, santassa bhāvo sattā, sabbhāvo.</p>\r\n<p class="bodytext">vassa batte bassa dvittaṃ –</p>\r\n<p class="bodytext">sīlabbataṃ, nibbānaṃ, nibbutaṃ iccādi pubbe vuttameva.</p>\r\n<p class="bodytext">vatu, vaṭu iccādīnaṃ antabyañjanassa dvittaṃ –</p>\r\n<p class="bodytext">vattati, pavattati, nivattati, saṃvattati, vaṭṭati, vivaṭṭati.</p>\r\n<p class="bodytext">saṃmhā anuno nassa dvittaṃ –</p>\r\n<p class="bodytext">samannāgato, samannāhāro, samannesati.</p>\r\n<p class="bodytext">aññatrapi –</p>\r\n<p class="bodytext">sīmaṃ sammanneyya <span class="note">[mahāva. 139]</span>, sīmaṃ sammannituṃ <span class="note">[mahāva. 138]</span>, sīmaṃ sammannati <span class="note">[mahāva. 139]</span>, sampaṭicchannaṃ, cīvaracetāpannaṃ, catunnaṃ, pañcannaṃ.</p>\r\n<p class="centered">iti sadisadvittarāsi.</p>\r\n<p class="bodytext"><a name="para54"></a><span class="paranum">54</span>. <span class="bld">catutthadutiyesvesaṃ tatiyapaṭhamā</span> <span class="note">[ka. 44, 29; rū. 24; nī. 57, 68, 74, 77-8, 80, 82-3, 91, 122]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-54-');
INSERT INTO pages VALUES(54456,'annya_sadda_10',49,replace(replace('\r\n<p class="bodytext">vagge catuttha, dutiyesu paresu kamena tatiya, paṭhamā esaṃ catuttha, dutiyānaṃ dvibhāvaṃ gacchanti, dutiyabhāvaṃ gacchantīti attho. ‘saramhā dve’ti suttena vā ‘vaggalasehi te’iccādīhi vā dutiya, catutthānampi sadisatte jāte puna iminā suttena <a name="M0.0049"></a> ādidutiyassa paṭhamattaṃ, ādicatutthassa tatiyattañca jātaṃ.</p>\r\n<p class="bodytext">tattha kavagge –</p>\r\n<p class="bodytext">ākkhātaṃ, pakkhittaṃ, pakkhepo, rūpakkhandho, vedanākkhandho, dhātukkhobho, āyukkhayo, nakkhamati.</p>\r\n<p class="bodytext">‘vaggalasehi te’ti suttavidhāne –</p>\r\n<p class="bodytext">pamukhe sādhu pāmokkhaṃ, paggharati, ugghosati, nigghoso.</p>\r\n<p class="bodytext">cavagge –</p>\r\n<p class="bodytext">acchādeti, acchindati-saṃyoge rassattaṃ, pacchādeti, pacchindati, setacchattaṃ, rukkhacchāyā, tathassa bhāvo tacchaṃ, rathassa hitā racchā, pajjhāyati, ujjhāyati, nijjhāyati, paṭhamajjhānaṃ, dutiyajjhānaṃ, ajjhokāso, bojjhaṅgo, dummedhassa bhāvo dummejjhaṃ, bujjhati, bujjhitabbaṃ, bojjhaṃ, paṭivijjha, paṭivijjhiya, paṭivijjhitvā iccādi.</p>\r\n<p class="bodytext">ṭavagge –</p>\r\n<p class="bodytext">yatraṭṭhitaṃ, tatraṭṭhito, uṭṭhito, niṭṭhito, thalaṭṭho, jalaṭṭho, vuḍḍho iccādi.</p>\r\n<p class="bodytext">tavagge –</p>\r\n<p class="bodytext">sumanatthero, yasatthero, avatthā, avatthānaṃ, vitthāro, abhitthuto, vitthambhito, uddharati, uddharaṇaṃ, uddhaṭaṃ, niddhāreti, niddhāraṇaṃ, niddhāritaṃ, niddhano, niddhuto, niddhāto iccādi.</p>\r\n<p class="bodytext">pavagge –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54457,'annya_sadda_10',50,replace(replace('\r\n<p class="bodytext">vippharati, vippharaṇaṃ, vipphāro, apphoṭeti, mahapphalaṃ, nipphalaṃ, madhupphāṇitaṃ, vibbhamati, vibbhamo, ubbhataṃ, nibbhayaṃ, dubbharo, sabbhāvo<a name="M0.0050"></a>, usabhassa bhāvo osabbhaṃ, labbhati, ārabbho, ārabbha, ārabbhitvā iccādi.</p>\r\n<p class="bodytext">idhapi u, du, nito parapadānaṃ ādibyañjanassa dvittaṃ visesato icchanti.</p>\r\n<p class="centered">iti visadisadvittarāsi.</p>\r\n<p class="bodytext"><a name="para55"></a><span class="paranum">55</span>. <span class="bld">vicchābhikkhaññesu dve</span> <span class="note">[caṃ. 6.3-1; pā. 8.1.1, 4]</span>.</p>\r\n<p class="bodytext">vicchāyaṃ abhikkhaññe ca anekatthassa ekapadassa dve rūpāni honti. bhinne atthe kriyāya vā dabbena vā guṇena vā byāpituṃ icchā <span class="bld">vicchā</span>. punappunakriyā <span class="bld">abhikkhaññaṃ</span>.</p>\r\n<p class="bodytext">vicchāyaṃ tāva –</p>\r\n<p class="bodytext">rukkhaṃ rukkhaṃ siñcati. gāme gāme sataṃkumbhā, gāmo gāmo ramaṇiyo, gehe gehe issaro, rasaṃ rasaṃ bhakkhayati, kriyaṃ kriyaṃ ārabhate.</p>\r\n<p class="bodytext">ānupubbiyepi vicchāva gamyate –</p>\r\n<p class="bodytext">mūle mūle thūlā, agge agge sukhumā, jeṭṭhaṃ jeṭṭhaṃ anupavesetha, imesaṃ devasikaṃ māsakaṃ māsakaṃ dehi, mañjūsakarukkho pupphaṃ pupphaṃ pupphati, ime janā pathaṃ pathaṃ accenti, sabbe ime aḍḍhā, katarā katarā imesaṃ aḍḍhatā, katamā katamā imesaṃ aḍḍhatā.</p>\r\n<p class="bodytext">abhikkhaññe –</p>\r\n<p class="bodytext">bhattaṃ pacati pacati, apuññaṃ pasavati pasavati, bhutvā bhutvā nippajjanti, paṭaṃ paṭaṃ karoti, paṭapaṭāyati, ekamekaṃ, ekamekāni iccādīsu vicchāsu pubbapade syādilopo.</p>\r\n<p class="bodytext"><a name="para56"></a><span class="paranum">56</span>. <span class="bld">sabbādīnaṃ vītihāre</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-55-56-');
INSERT INTO pages VALUES(54458,'annya_sadda_10',51,replace(replace('\r\n<p class="bodytext">atikkamma <a name="M0.0051"></a> haraṇaṃ atihāro, na atihāro vītihāro, aññamaññassa antoyeva haraṇantiattho, vītihāratthe gamyamāne sabbādīnaṃ sabbanāmānaṃ dve rūpāni honti, pubbassekassa ca syādilopo.</p>\r\n<p class="bodytext">ime dve janā aññamaññassa upakārakā, itarītarassa upakārakā, aññamaññaṃ passanti, aññamaññassa denti, aññamaññassa apenti, aññamaññassa dhanaṃ, aññamaññe nissitā.</p>\r\n<p class="bodytext"><a name="para57"></a><span class="paranum">57</span>. <span class="bld">yāvatātāvaṃ sambhame</span> <span class="note">[caṃ. 6.3.14; pā. 8.1.12; yāvabodhaṃ sambhame (bahūsu)]</span>.</p>\r\n<p class="bodytext">yaṃ parimāṇamassāti yāvaṃ. taṃ parimāṇamassāti tāvaṃ. punappunaṃ bhamanaṃ pavattanaṃ <span class="bld">sambhamo</span>. turitena vacīpayogena taṃ taṃ upāyadīpanaṃ sambhamo, āmeḍitameva vuccati, sambhame gamyamāne yāvatā yattakena padena so attho paññāyati, tattakaṃ padaṃ payujjate, dvikkhattuṃ vā tikkhattuṃ vā taduttari vā udīriyatetyattho. yathābodhaṃ sambhametipi pāṭho, soyevattho.</p>\r\n<p class="bodytext">bhaye, kodhe, pasaṃsāyaṃ, turite, kotūhale’cchare.</p>\r\n<p class="bodytext">hāse, soke, pasāde ca, kare āmeḍitaṃ budho.</p>\r\n<p class="bodytext">tattha bhaye –</p>\r\n<p class="bodytext">sappo sappo, coro coro –</p>\r\n<p class="bodytext">kodhe –</p>\r\n<p class="bodytext">vijjha vijjha, pahara pahara.</p>\r\n<p class="bodytext">pasaṃsāyaṃ –</p>\r\n<p class="bodytext">sādhu sādhu.</p>\r\n','\r',char(13)),'\n',char(10)),'-57-');
INSERT INTO pages VALUES(54459,'annya_sadda_10',52,replace(replace('\r\n<p class="bodytext">turite <a name="M0.0052"></a> –</p>\r\n<p class="bodytext">gaccha gaccha.</p>\r\n<p class="bodytext">kotūhale –</p>\r\n<p class="bodytext">āgaccha āgaccha.</p>\r\n<p class="bodytext">acchare –</p>\r\n<p class="bodytext">aho buddhā aho buddhā.</p>\r\n<p class="bodytext">hāse –</p>\r\n<p class="bodytext">abhikkamatha vāseṭṭhā abhikkamatha vāseṭṭhā <span class="note">[dī. ni. 2.210]</span>.</p>\r\n<p class="bodytext">soke –</p>\r\n<p class="bodytext">kahaṃ ekaputtaka kahaṃ ekaputtaka <span class="note">[saṃ. ni. 4.120]</span>.</p>\r\n<p class="bodytext">pasāde –</p>\r\n<p class="bodytext">abhikkantaṃ bho gotama abhikkantaṃ bho gotama <span class="note">[ma. ni. 2.106]</span> iccādi. tikkhattuṃudānaṃ udānesi "namo tassa bhagavato" <span class="note">[ma. ni. 2.357]</span> iccādi.</p>\r\n<p class="centered">iti padavākyadvittarāsi.</p>\r\n<p class="centered">dvibhāvasandhirāsi niṭṭhito.</p>\r\n<p class="subhead">vipallāsasandhi</p>\r\n<p class="bodytext">atha vipallāsasandhi dīpiyate.</p>\r\n<p class="bodytext">padakkharānaṃ pubbāparavipariyāyo <span class="bld">vipallāso</span>.</p>\r\n<p class="bodytext"><a name="para58"></a><span class="paranum">58</span>. <span class="bld">hassa vipallāso</span>.</p>\r\n<p class="bodytext">yamhi pare hassa pubbāparavipallāso hoti vā.</p>\r\n<p class="bodytext">dayhati, saṅgayhati, sannayhati, vuyhati, duyhati, muyhati.</p>\r\n','\r',char(13)),'\n',char(10)),'-58-');
INSERT INTO pages VALUES(54460,'annya_sadda_10',53,replace(replace('\r\n<p class="bodytext">vātveva<a name="M0.0053"></a>? saṅgaṇhiyati, evaṃ saṅgayha saṅgaṇhitvā, āruyha āruhitvā, ogāyha ogāhitvā. pasayha pasahitvā.</p>\r\n<p class="bodytext"><a name="para59"></a><span class="paranum">59</span>. <span class="bld">ve vā</span> <span class="note">[rū. 40 (piṭṭhe)]</span>.</p>\r\n<p class="bodytext">vamhi pare hassa vipallāso hoti vā.</p>\r\n<p class="bodytext">bavhābādho bahvābādho, bavhettha nhāyatī jano <span class="note">[udā. 9]</span> =bahvettha nhāyatī jano.</p>\r\n<p class="bodytext">mahāvuttividhānaṃ vuccate.</p>\r\n<p class="bodytext">ya, rānaṃ vipallāso –</p>\r\n<p class="bodytext">kuṭi me kayirati <span class="note">[pārā. 358]</span>, vacanaṃ payirudāhāsi, garuṃ payirūpāsati, vandāmi te ayyire pasannacitto <span class="note">[jā. 2.17.54]</span> -yassa dvittaṃ.</p>\r\n<p class="bodytext">niggahītassa vipallāso –</p>\r\n<p class="bodytext">nirayamhi apaccisuṃ <span class="note">[jā. 2.22.60]</span>, te me asse ayācisuṃ <span class="note">[jā. 2.22.1863]</span>. imā gāthā abhāsisuṃ.</p>\r\n<p class="bodytext">sarānampi vipallāso –</p>\r\n<p class="bodytext">haññayyevāpi koci naṃ <span class="note">[jā. 2.22.1193]</span> – haññeyyāti ṭhiti, amūlamūlaṃ gantvā-mūlamūlaṃ agantvāti attho. evaṃ paratra. anokāsaṃ kārāpetvā <span class="note">[pārā 389]</span>, animittaṃ katvā, saddhaṃ na bhuñjatīti asaddhabhoji, disvā padamanuttiṇṇaṃ <span class="note">[jā. 1.1.20]</span> – uttiṇṇaṃ adisvāti attho.</p>\r\n<p class="bodytext">padānampi vipallāso –</p>\r\n<p class="bodytext">navaṃ pana bhikkhunā cīvaralābhena, nāgakaññā caritaṃ gaṇena <span class="note">[jā. 1.15.268]</span> -nāgakaññāgaṇena caritanti ṭhiti.</p>\r\n<p class="centered">iti vipallāsarāsi.</p>\r\n<p class="bodytext"><a name="para60"></a><span class="paranum">60</span>. <span class="bld">bahulaṃ</span> <span class="note">[caṃ. 1.1.130; pā. 3.3.113]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-59-60-');
INSERT INTO pages VALUES(54461,'annya_sadda_10',54,replace(replace('\r\n<p class="bodytext">sandhividhānaṃ <a name="M0.0054"></a> nāma bahulaṃ hoti, yebhuyyena hotīti attho. adhikārasuttaṃ. yāvaganthapariyosānā yuttaṭṭhānesu sabbattha vattate. etena sabbasaddasuttesu aniṭṭhanivatti ca iṭṭhapariggaho ca kato hoti.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānadīpaniyaṃ</p>\r\n<p class="centered">sandhikaṇḍo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54462,'annya_sadda_10',55,replace(replace('\r\n<p class="chapter">2. nāmakaṇḍa</p>\r\n<p class="subhead">vibhattirāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0055"></a> liṅgamhā syādivibhattividhānaṃ dīpiyate.</p>\r\n<p class="bodytext">liṅgaṃ, nāmaṃ, pāṭipadikanti atthato ekaṃ, dabbābhidhānassa purisādikassa pakatirūpassetaṃ nāmaṃ. tañhi sattannaṃ vibhattīnaṃ vasena vibhāgaṃ patvā kiñci visadarūpaṃ hoti, kiñci avisadarūpaṃ, kiñci majjhimarūpanti evaṃ tividhena liṅgarūpena yuttattā <span class="bld">liṅga</span>nti vuccati.</p>\r\n<p class="bodytext">tadeva kiñci saddaliṅgānurūpaṃ, kiñci atthaliṅgānurūpañca pariṇamantaṃ pavattati, tasmā <span class="bld">nāma</span>nti ca vuccati.</p>\r\n<p class="bodytext">tadeva dhātu, paccaya, vibhattipadehi cevasaddapadatthakapadehi ca ‘visuṃ bhūtaṃ pada’nti katvā <span class="bld">pāṭipadika</span>nti ca vuccati.</p>\r\n<p class="bodytext">tattha <span class="bld">dhātupadaṃ</span> nāma brū, bhū, hūiccādi.</p>\r\n<p class="bodytext"><span class="bld">paccayapadaṃ</span> nāma ṇa, tabba, anīya iccādi.</p>\r\n<p class="bodytext"><span class="bld">vibhattipadaṃ</span> nāma si, yo, aṃ, yo,ti, anti iccādi.</p>\r\n<p class="bodytext"><span class="bld">saddapadatthakapadā</span>ni nāma rājassa, sakhassa, pumassa iccādīni. ettha ca rājassaiccādīni saddasutte saddapadatthakāni honti, payoge atthapadatthakāni. dhātupaccayavibhattipadāni pana niccaṃ saddapadatthakāni eva honti, saddasuttesveva ca labbhanti, na payogeti, idaṃ dvinnaṃ nānattaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54463,'annya_sadda_10',56,replace(replace('\r\n<p class="bodytext">yadievaṃ bhussa, brussa, bhūto, hūto, ṇe, tabbe, simhi, timhi iccādinā tehi kathaṃ vibhattuppatti hotīti? anukaraṇapadāni nāma tāni atthissa, karotissa iccādīni viya, tasmā tāni ca rājassa iccādīni ca anukaraṇaliṅgabhāvena ettha saṅgayhanti, na ekantaliṅgabhāvenāti. evañca katvā ‘dhātu- paccaya<a name="M0.0056"></a>, vibhattivajjitamatthavaṃ liṅga’nti avocuṃ. tattha atthavanti atthapadatthakaṃ vuccati, rājassaiccādikaṃ saddapadatthakaṃ vivajjeti, etena atthapadatthake sati taddhita, samāsa, kitakapadānampi ekantaliṅgabhāvaṃ sādhenti. na hi tesaṃ liṅganāmabyapadesakiccaṃ atthi, yāni ca nāmassa visesanāni bhavituṃ arahanti, tāni upasagga, nipātapadāni tvāntādipadāni ca idha visesananāmabhāvena saṅgayhantīti.</p>\r\n<p class="bodytext"><a name="para61"></a><span class="paranum">61</span>. <span class="bld">dve dvekānekesu nāmasmā si yo aṃyo nā hi sa naṃ smāhi sanaṃsmiṃsu</span> <span class="note">[ka. 55; rū. 63; nī. 200]</span>.</p>\r\n<p class="bodytext">ekasmiṃ atthe ca anekesu atthesu ca pavattā nāmasmā dve dve si, yo.pe.. smiṃ, suvibhattiyo honti.</p>\r\n<p class="bodytext">vibhajantīti vibhattiyo, ekamekaṃ pakatināmapadaṃ nānārūpavibhāgavasena kattu, kammādinānāatthavibhāgavasena ekatta, bahuttasaṅkhyāvibhāgavasena ca vibhajantīti attho. si, lo iti <span class="bld">paṭhamā</span> nāma.pe.. smiṃ, su iti <span class="bld">sattamī</span> nāma. dvīsu dvīsu pubbaṃ pubbaṃ ‘ekasmiṃ atthe pavattaṃ vacana’nti <span class="bld">ekavacanaṃ</span> nāma. paraṃ paraṃ ‘anekesu atthesu pavattaṃ vacana’nti <span class="bld">anekavacanaṃ</span> nāma. <span class="bld">bahuvacana</span>nti ca <span class="bld">puthuvacana</span>nti ca etassa nāmaṃ. sabbamidaṃ iminā suttena siddhaṃ.</p>\r\n<p class="bodytext"><a name="para62"></a><span class="paranum">62</span>. <span class="bld">paṭhamātthamatte</span> <span class="note">[ka. 284; rū. 65; nī. 577; caṃ. 2.1.93; pā. 2.3.46]</span>.</p>\r\n<p class="bodytext">kattu, kammādikaṃ bāhiratthaṃ anapekkhitvā liṅgatthamatte pavattā nāmasmā paṭhamāvibhatti hoti.</p>\r\n<p class="bodytext">ayaṃ mama puriso, ime mama purisā.</p>\r\n<p class="bodytext"><a name="para63"></a><span class="paranum">63</span>. <span class="bld">āmantane</span> <span class="note">[ka. 285; rū. 70; nī. 578; caṃ. 2.1.94; pā. 2.3.47]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-61-62-63-');
INSERT INTO pages VALUES(54464,'annya_sadda_10',57,replace(replace('\r\n<p class="bodytext"><span class="bld">āmantanaṃ</span> <a name="M0.0057"></a> vuccati ālapanaṃ. āmantanavisaye liṅgatthamatte pavattā nāmasmā paṭhamāvibhatti hoti.</p>\r\n<p class="bodytext">bho purisa, bhonto purisā.</p>\r\n<p class="bodytext"><a name="para64"></a><span class="paranum">64</span>. <span class="bld">kamme dutiyā</span> <span class="note">[ka. 297; rū. 76, 284; nī. 580; caṃ. 2.1.43; pā. 1.4.49-51]</span>.</p>\r\n<p class="bodytext">nāmasmā kammatthe dutiyāvibhatti hoti.</p>\r\n<p class="bodytext">purisaṃ passati, purise passanti.</p>\r\n<p class="bodytext"><a name="para65"></a><span class="paranum">65</span>. <span class="bld">kattukaraṇesu tatiyā</span> <span class="note">[ka. 286, 288; rū. 83; nī. 591, 594; caṃ. 2.1.62, 63; pā. 2.3.18]</span>.</p>\r\n<p class="bodytext">nāmasmā kattari ca karaṇe ca tatiyāvibhatti hoti.</p>\r\n<p class="bodytext">purisena kataṃ, purisehi kataṃ, purisena kulaṃ sobhati, purisehi kulaṃ sobhati.</p>\r\n<p class="bodytext"><a name="para66"></a><span class="paranum">66</span>. <span class="bld">catutthī sampadāne</span> <span class="note">[ka. 293; rū 85, 301; nī. 605; caṃ. 2.1.73; pā. 2.3.13]</span>.</p>\r\n<p class="bodytext">nāmasmā sampadānatthe catutthīvibhatti hoti.</p>\r\n<p class="bodytext">purisassa deti, purisānaṃ deti.</p>\r\n<p class="bodytext"><a name="para67"></a><span class="paranum">67</span>. <span class="bld">pañcamyāvadhismiṃ</span> <span class="note">[ka. 295; rū. 89, 307; nī. 607 caṃ. 2.1.81; pā. 2.3.28; 1.4.24 pañcamyavadhismā (bahūsu)]</span>.</p>\r\n<p class="bodytext"><span class="bld">avadhi</span> vuccati apādānaṃ. nāmasmā avadhiatthe pañcamīvibhatti hoti.</p>\r\n<p class="bodytext">purisasmā apeti, purisehi apeti.</p>\r\n','\r',char(13)),'\n',char(10)),'-64-65-66-67-');
INSERT INTO pages VALUES(54465,'annya_sadda_10',58,replace(replace('\r\n<p class="bodytext"><a name="para68"></a><span class="paranum">68</span>. <span class="bld">chaṭṭhī</span> <a name="M0.0058"></a> <span class="bld">sambandhe</span> <span class="note">[ka. 30; rū. 92, 315; nī. 609; caṃ. 2.1.95; pā. 2.3.50]</span>.</p>\r\n<p class="bodytext">nāmasmā sambandhatthe chaṭṭhīvibhatti hoti.</p>\r\n<p class="bodytext">purisassa dhanaṃ, purisānaṃ dhanaṃ.</p>\r\n<p class="bodytext"><a name="para69"></a><span class="paranum">69</span>. <span class="bld">sattamyādhāre</span> <span class="note">[ka. 312; rū. 94, 319; nī. 630; caṃ. 2.1.88; pā. 2.3.36; 1.3.45]</span>.</p>\r\n<p class="bodytext">nāmasmā ādhāratthe sattamīvibhatti hoti.</p>\r\n<p class="bodytext">purisasmiṃ tiṭṭhati, purisesu tiṭṭhati.</p>\r\n<p class="centered">vibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">itthipaccayarāsi</p>\r\n<p class="bodytext"><a name="para70"></a><span class="paranum">70</span>. <span class="bld">itthiyamatvā</span> <span class="note">[ka. 237; rū. 176; nī. 466; caṃ. 2.3.15; pā. 4.1.4]</span>.</p>\r\n<p class="bodytext">itthiyaṃ+ato+āti chedo. akārantanāmamhā itthiyaṃ āpaccayo hoti.</p>\r\n<p class="bodytext">abhāsitapumehi kehici saññāsaddehi niccaṃ –</p>\r\n<p class="bodytext">kaññā, paññā, saññā, nāvā, sālā, taṇhā, icchā, bhikkhā, sikkhā, gīvā, jivhā, vīsā, tiṃsā, cattālīsā, paññāsā iccādi.</p>\r\n<p class="bodytext">bhāsitapumehipi sabbanāmehi tabbā, nīya, tapaccayantehi ca niccaṃ –</p>\r\n<p class="bodytext">sabbā, katarā, katamā, anubhavitabbā, anubhavanīyā, gatā, jātā, bhūtā, hūtā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-68-69-70-');
INSERT INTO pages VALUES(54466,'annya_sadda_10',59,replace(replace('\r\n<p class="bodytext">aññehi <a name="M0.0059"></a> pana aniccaṃ –</p>\r\n<p class="bodytext">kalyāṇā, kalyāṇī, sundarā, sundarī, sobhaṇā, sobhaṇī, kumbhakārā, kumbhakārī, kumbhakārinī, atthakāmā, atthakāmī, atthakāminī, paribbājikā, paribbājikinī, ekākā, ekākinī, dīpanā, dīpanī iccādi.</p>\r\n<p class="bodytext">suttavibhattena samāse mātu, dhītu iccādīhi āpaccayo hoti. nandamātā, uttaramātā, devadhītā, rājadhītā, assamaṇī hoti asakyadhītarā iccādi.</p>\r\n<p class="bodytext">ettha ca ‘itthiya’nti katthaci saddamattassa vā, katthaci atthamattassa vā itthibhāve jotetabbeti attho. evañca sati itthipaccayāpi syādayo viya jotakamattā eva honti, na vācakāti siddhaṃ hoti.</p>\r\n<p class="bodytext"><a name="para71"></a><span class="paranum">71</span>. <span class="bld">nadādīhi ṅī</span> <span class="note">[ka. 238; rū. 187; nī. 467; nadādito vī (bahūsu)]</span>.</p>\r\n<p class="bodytext">nadādīhi itthiyaṃ ṭīhoti. ṅānubandho ‘ntantūnaṃvīmhi to vā’ti ettha visesanattho.</p>\r\n<p class="bodytext">nadī, mahī, itthī, kumārī, taruṇī, vāseṭṭhī, gotamī, kaccānī, kaccāyanī, māṇavī, sāmaṇerī, nāvikī, pañcamī, chaṭṭhī, catuddasī, pañcadasī, sahassī, dasasahassī, satasahassī, kumbhakārī, mālakārī, cakkhukaraṇī, ñāṇakaraṇī, dhammadīpanī iccādi.</p>\r\n<p class="bodytext"><a name="para72"></a><span class="paranum">72</span>. <span class="bld">ntantūnaṃ ṅīmhi to vā</span> <span class="note">[ka. 239, 241; rū. 190, 191; nī. 468, 471]</span>.</p>\r\n<p class="bodytext">nta, ntūnaṃ to hoti vā ṅīmhi pare.</p>\r\n<p class="bodytext">gacchatī, gacchantī, satī, santī, bhavissatī, bhavissantī, guṇavatī, guṇavantī, satimatī, satimantī, sabbāvatī, sabbāvantī, yāvatī, yāvantī, tāvatī, tāvantī, bhuttavatī, bhuttavantī.</p>\r\n<p class="bodytext"><a name="para73"></a><span class="paranum">73</span>. <span class="bld">goto vā</span> <span class="note">[ka. 238; rū. 187; nī. 467]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-71-72-73-');
INSERT INTO pages VALUES(54467,'annya_sadda_10',60,replace(replace('\r\n<p class="bodytext">gosaddamhā <a name="M0.0060"></a> itthiyaṃ vī hoti vā.</p>\r\n<p class="bodytext">gāvī.</p>\r\n<p class="bodytext">vāti kiṃ? gokāṇā pariyantacārinīti pāḷi. tattha kāṇāti andhā.</p>\r\n<p class="bodytext"><a name="para74"></a><span class="paranum">74</span>. <span class="bld">yakkhādīhinī ca</span> <span class="note">[ka. 238, 240; rū. 287, 193; nī. 467, 469; yakkhāditvinī ca (bahūsu)]</span>.</p>\r\n<p class="bodytext">yakkhādīhi akārantehi itthiyaṃ vī ca hoti inī ca.</p>\r\n<p class="bodytext">yakkhī, yakkhinī, nāgī, nāginī, mahiṃsī, mahiṃsinī, migī, miginī, sīhī, sīhinī, dīpī, dīpinī, byagghī, byagghinī, kākī, kākinī, kapotī, kapotinī, mānusī, mānusinī iccādi.</p>\r\n<p class="bodytext"><a name="para75"></a><span class="paranum">75</span>. <span class="bld">ārāmikādīhi</span> <span class="note">[ka. 240; rū. 193; nī. 469]</span>.</p>\r\n<p class="bodytext">ārāmikādīhi akārantehi itthiyaṃ inī hoti.</p>\r\n<p class="bodytext">ārāmikinī, antarāyikinī, nāvikinī, olumbikinī, paṃsukūlikinī, paribbājikinī, rājinī, ekākinī iccādi.</p>\r\n<p class="bodytext">saññāyaṃ –</p>\r\n<p class="bodytext">mānusinī mānusā vā, aññatra mānusī sampatti.</p>\r\n<p class="bodytext"><a name="para76"></a><span class="paranum">76</span>. <span class="bld">gharaṇyādayo</span> <span class="note">[ka. 240; rū. 193; nī. 469]</span>.</p>\r\n<p class="bodytext">gharaṇīiccādayo itthiyaṃ nīpaccayantā sijjhanti.</p>\r\n<p class="bodytext">gharaṇī, vetraṇī, pokkharaṇī-esu nassa ṇattaṃ. ācarinīyalopo, ācariyā vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-74-75-76-');
INSERT INTO pages VALUES(54468,'annya_sadda_10',61,replace(replace('\r\n<p class="bodytext"><a name="para77"></a><span class="paranum">77</span>. <span class="bld">mātulāditvānī</span> <a name="M0.0061"></a> <span class="bld">bhariyāyaṃ</span> <span class="note">[ka. 98; rū. 189; nī. 261]</span>.</p>\r\n<p class="bodytext">mātulādīhi akārantehi bhariyāyaṃ ānī hoti.</p>\r\n<p class="bodytext">mātubhātā mātulo, tassa bhariyā mātulānī, evaṃ varuṇānī, gahapatānī, ācariyānī, khattiyānī.</p>\r\n<p class="bodytext">‘bahulā’dhikārā khattiyī khattiyā ca.</p>\r\n<p class="bodytext"><a name="para78"></a><span class="paranum">78</span>. <span class="bld">yuvaṇṇehi nī</span>.</p>\r\n<p class="bodytext">ivaṇṇantehi uvaṇṇantehi ca itthiyaṃ nī hoti.</p>\r\n<p class="bodytext">chattapāṇinī, daṇḍapāṇinī, daṇḍinī, chattinī, hatthinī, mālinī, māyāvinī, medhāvinī, piyapasaṃsinī, brahmacārinī, bhayadassāvinī, atthakāminī, hitacārinī, bhikkhunī, khattiyabandhunī, paṭunī, paracittavidunī, mattaññunī, atthaññunī, dhammaññunī iccādi.</p>\r\n<p class="bodytext"><a name="para79"></a><span class="paranum">79</span>. <span class="bld">timhāññatthe</span> <span class="note">[ktimhāññatte (bahūsu), moggallāne 31 suttaṅke]</span>.</p>\r\n<p class="bodytext">aññapadatthasamāse tipaccayantamhā itthiyaṃ nī hoti.</p>\r\n<p class="bodytext">ahiṃsāratinī, dhammaratinī, vacchagiddhinī, puttagiddhinī, muṭṭhassatinī, micchādiṭṭhinī, sammādiṭṭhinī, attaguttinī iccādi.</p>\r\n<p class="bodytext">aññattheti kiṃ? dhamme rati dhammarati.</p>\r\n<p class="bodytext"><a name="para80"></a><span class="paranum">80</span>. <span class="bld">yuvāti</span>.</p>\r\n<p class="bodytext">yuvato itthiyanti hoti.</p>\r\n<p class="bodytext">yuvati.</p>\r\n<p class="bodytext">ettha ca ‘ti’ iti suttavibhattena vīsa, tiṃsatopiti hoti vā. vīsati, vīsaṃ, tiṃsati, tiṃsaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-77-78-79-80-');
INSERT INTO pages VALUES(54469,'annya_sadda_10',62,replace(replace('\r\n<p class="bodytext"><a name="para81"></a><span class="paranum">81</span>. <span class="bld">upamā</span> <a name="M0.0062"></a> <span class="bld">saṃhita sahita saññata saha sapha vāmalakkhaṇāditūrutvū</span> <span class="note">[caṃ. 2.3.79; pā. 4.1.69, 70 tūrutū (bahūsu)]</span>.</p>\r\n<p class="bodytext">lakkhaṇādito+ūruto+ūti chedo.</p>\r\n<p class="bodytext">aññapadatthasamāse upamādipubbā ūrusaddamhā itthiyaṃ ū hoti.</p>\r\n<p class="bodytext">nāgassa nāsā viya ūrū yassāti nāganāsūrū, saṃhitā sambandhā ūrū yassāti saṃhitorū, sahitā ekabaddhā ūrū yassāti sahitorū, saññatā alolā ūrū yassāti saññatorū, ūruyā <span class="note">[ūrunā?]</span> saha vattatīti sahorū, sapho vuccati khuro, saṃsiliṭṭhatāya saphabhūtā ūrū yassāti saphorū, vāmā sundarā ūrū yassāti vāmorū, lakkhaṇasampannā ūrū yassāti lakkhaṇorū.</p>\r\n<p class="bodytext">suttavibhattena brahmabandhūti sijjhati.</p>\r\n<p class="bodytext">"sace maṃ nāganāsūrū, olokeyya pabhāvatī"ti <span class="note">[jā. 2.20.14]</span> ca "ekā tuvaṃ tiṭṭhasi sahitūrū"ti <span class="note">[jā. 1.16.297]</span> ca "saññatūrū mahāmāyā, kumāri cārudassanā"ti <span class="note">[jā. 2.17.109]</span> ca "vāmorū saja maṃ bhadde"ti <span class="note">[dī. ni. 2.348]</span> ca "kāraṇaṃ nappajānāmi, sammatto lakkhaṇūruyā"ti <span class="note">[dī. ni. 2.348]</span> ca "gārayhassaṃ brahmabandhuyā"ti <span class="note">[jā. 2.22.2109]</span> ca pāḷipadāni dissanti.</p>\r\n<p class="bodytext">tattha ‘sajā’ti āliṅgāhi, ‘gārayhassa’nti ahaṃ gārayho bhaveyyaṃ.</p>\r\n<p class="bodytext">ettha ca tāpaccayantā sabhāvaitthiliṅgā eva – lahutā, mudutā, gāmatā, janatā, devatā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-81-');
INSERT INTO pages VALUES(54470,'annya_sadda_10',63,replace(replace('\r\n<p class="bodytext">tathā tipaccayantā – gati, mati, ratti, sati, tuṭṭhi, diṭṭhi, iddhi, siddhi iccādi, tathā yāgu, dhātu, dhenu, kaṇḍu, kacchu, mātu, dhītu<a name="M0.0063"></a>, duhitu iccādi, jambū, vadhū, camū, sutanū, sarabū iccādi ca. sakkataganthesu pana sutanū, sarabū iccādīsupi ūpaccayaṃ vidahanti.</p>\r\n<p class="bodytext">tattha itthiliṅgabhūtā sabbe ‘ivaṇṇuvaṇṇā pitthiya’nti suttena niccaṃ pasaññā honti. ‘ākāro ca ghā’ti suttena niccaṃ ghasañño.</p>\r\n<p class="centered">itthipaccayarāsi niṭṭhito.</p>\r\n<p class="subhead">ākārantitthiliṅgarāsi</p>\r\n<p class="bodytext">itthiliṅgaṃ chabbidhaṃ ākārantaṃ, ikārantaṃ, īkārantaṃ, ukārantaṃ, ūkārantaṃ, okārantaṃ. tattha kaññāsaddamhā atthamatte paṭhamā.</p>\r\n<p class="bodytext"><a name="para82"></a><span class="paranum">82</span>. <span class="bld">gasīnaṃ</span> <span class="note">[ka. 220; rū. 74; nī. 447]</span>.</p>\r\n<p class="bodytext">kenaci suttena aladdhavidhīnaṃ gasīnaṃ lopo hotīti silopo.</p>\r\n<p class="bodytext">kaññā tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para83"></a><span class="paranum">83</span>. <span class="bld">jantuhetvīghapehi vā</span> <span class="note">[ka. 118; rū. 146; nī. 293]</span>.</p>\r\n<p class="bodytext">jantu, hetūhi ca punnapuṃsakesu īkārantehi ca ghato ca pasaññehi ivaṇṇuvaṇṇehi ca yonaṃ lopo hoti vā.</p>\r\n<p class="bodytext">kaññā tiṭṭhanti, kaññāyo tiṭṭhanti.</p>\r\n<p class="bodytext">āmantanatthe paṭhamā, ‘gosyālapane’ti gasaññā.</p>\r\n<p class="bodytext"><a name="para84"></a><span class="paranum">84</span>. <span class="bld">ghabrahmāditve</span> <span class="note">[ka. 114, 193; rū. 122, 178; nī. 288; ghabrahmādite (bahasu)]</span>.</p>\r\n<p class="bodytext">ghato ca brahmādito ca gassa e hoti vā. ādisaddena isi, muni, revatī, kattu, khattuiccāditopi.</p>\r\n','\r',char(13)),'\n',char(10)),'-82-83-84-');
INSERT INTO pages VALUES(54471,'annya_sadda_10',64,replace(replace('\r\n<p class="bodytext">bhoti <a name="M0.0064"></a> kaññe, bhoti kaññā, bhotiyo kaññāyo, bhotī kaññāyo, "uṭṭhehi puttika pabbajjā dukkarā puttika" iti therīpāḷi <span class="note">[therīgā. 465]</span>, tasmā ge pare mahāvuttinā rassopi yujjati. kusajātake "na me akāsi vacanaṃ, atthakāmāya puttike"tipi <span class="note">[jā. 2.20.47]</span> atthi.</p>\r\n<p class="bodytext">kammatthe dutiyā, ‘saro lopo sare’ti pubbasaralopo.</p>\r\n<p class="bodytext">kaññaṃ passati, kaññā passati, kaññāyo passati.</p>\r\n<p class="bodytext">kattari tatiyā.</p>\r\n<p class="bodytext"><a name="para85"></a><span class="paranum">85</span>. <span class="bld">ghapatekasmiṃ nādīnaṃ yayā</span> <span class="note">[ka. 111, 112; rū. 179, 183 nī. 283, 284]</span>.</p>\r\n<p class="bodytext">ghato ca pasaññehi ivaṇṇuvaṇṇehi ca ekatte pavattānaṃ nādīnaṃ pañcannaṃ ekavacanānaṃ kamena ya, yā honti.</p>\r\n<p class="bodytext">kaññāya kataṃ, kaññāhi kataṃ. ettha ca ghatopi yāādeso dissati. "te ca tattha nisīditvā, tassa rukkhassa chāyayā" <span class="note">[jā. 1.14.182]</span> ti ca "samantā parivāriṃsu, tassa rukkhassa chāyayā" <span class="note">[jā. 1.14.189]</span> ti ca pāḷi, tathā "sakkharopamayā vade" <span class="note">[saccasaṅkhepa 176 gāthā]</span>, "bāladārakalīlayā"ti <span class="note">[vibhāvinī 154]</span> ca dissanti. mahāvuttinā ghassa rasso.</p>\r\n<p class="bodytext"><a name="para86"></a><span class="paranum">86</span>. <span class="bld">smāhismiṃnaṃ mhābhimhi vā</span> <span class="note">[ka. 99; rū. 81]</span>.</p>\r\n<p class="bodytext">tesaṃ kamena mhā, bhi, mhi honti vā. ete ādesā gāthāsu bahulaṃ dissanti.</p>\r\n<p class="bodytext">kaññāhi kataṃ, kaññābhi kataṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-85-86-');
INSERT INTO pages VALUES(54472,'annya_sadda_10',65,replace(replace('\r\n<p class="bodytext">sampadāne <a name="M0.0065"></a> catutthī, kaññāya deti, kaññānaṃ deti, kaññāya ābhataṃ vatthaṃ, kaññānaṃ ābhataṃ vatthaṃ.</p>\r\n<p class="bodytext">apādāne pañcamī, kaññāya apeti, kaññamhā apetirassattaṃ, kaññāhi kaññābhi apeti.</p>\r\n<p class="bodytext">sambandhe chaṭṭhī, kaññāya santakaṃ, kaññānaṃ santakaṃ.</p>\r\n<p class="bodytext">okāse sattamī, kaññāya tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para87"></a><span class="paranum">87</span>. <span class="bld">yaṃ</span> <span class="note">[ka. 116; rū. 180; nī. 443]</span>.</p>\r\n<p class="bodytext">ghato ca pasaññehi ivaṇṇuvaṇṇehi ca smiṃno yaṃ hoti vā.</p>\r\n<p class="bodytext">kaññāyaṃ tiṭṭhati, kaññāya tiṭṭhati, kaññāsu tiṭṭhati.</p>\r\n<p class="bodytext">saddhā medhā paññā vijjā, cintā mantā vīṇā taṇhā.</p>\r\n<p class="bodytext">icchā mucchā ejā māyā, mettā mattā sikkhā bhikkhā.</p>\r\n<p class="bodytext">jaṅghā gīvā jivhā vācā, chāyā āsā gaṅgānāvā.</p>\r\n<p class="bodytext">gāthā senā lekhā sākhā, mālā velā pūjā khiḍḍā.</p>\r\n<p class="bodytext">pipāsā vedanā saññā, cetanā tasiṇāpajā.</p>\r\n<p class="bodytext">devatā vaṭṭakā godhā, balākā parisā sabhā.</p>\r\n<p class="bodytext">ūkā sephālikā laṅkā, salākā vālikā sikhā.</p>\r\n<p class="bodytext">visākhā visikhā sākhā, gacchā vañjhā jaṭā ghaṭā.</p>\r\n<p class="bodytext">jeṭṭhā soṇḍā vitaṇḍā ca, varuṇā vanitā latā.</p>\r\n<p class="bodytext">kathā niddā sudhā rādhā, vāsanā sīsapā papā.</p>\r\n<p class="bodytext">pabhā sīmā khamā jāyā, khattiyā sakkharā surā.</p>\r\n<p class="bodytext">dolā tulā silā līlā, lāle’lā mekhalā kalā.</p>\r\n','\r',char(13)),'\n',char(10)),'-87-');
INSERT INTO pages VALUES(54473,'annya_sadda_10',66,replace(replace('\r\n<p class="bodytext">vaḷavā’ <a name="M0.0066"></a> lambusā mūsā, mañjūsā sulasā disā.</p>\r\n<p class="bodytext">nāsā juṇhā guhā īhā, lasikā vasudhādayo.</p>\r\n<p class="bodytext"><a name="para88"></a><span class="paranum">88</span>. <span class="bld">nambādīhi</span> <span class="note">[nambādīhi (bahūsu)]</span>.</p>\r\n<p class="bodytext">gasaññehi amba, anna, ammaiccetehi gassa e na hoti.</p>\r\n<p class="bodytext"><a name="para89"></a><span class="paranum">89</span>. <span class="bld">rasso vā</span>.</p>\r\n<p class="bodytext">ambādīnaṃ rasso hoti vā ge pare.</p>\r\n<p class="bodytext">bhoti amba, bhoti ambā, bhoti anna, bhoti annā, bhoti amma, bhoti ammā, sesaṃ kaññāsamaṃ.</p>\r\n<p class="bodytext">ettha visesavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para90"></a><span class="paranum">90</span>. <span class="bld">ti sabhāparisāya</span>.</p>\r\n<p class="bodytext">sabhāparisāhi smiṃnoti hoti. ‘gho ssaṃssāssāya tīsū’ti suttena timhi rasso.</p>\r\n<p class="bodytext">sabhati, sabhāya, sabhāyaṃ, sabhāsu, parisati, parisāya, parisāyaṃ, parisāsu, tamaddasa mahābrahmā, nisinnaṃ samhi parisati, iti bhagavā tasmiṃ parisati suvaṇṇavaṇṇaṃ kāyaṃ vivari <span class="note">[ma. ni. 1.359]</span>.</p>\r\n<p class="bodytext">nandamātā, rājadhītāiccādīsu ‘ghabrahmāditve’ti ghassa ettaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-88-89-90-');
INSERT INTO pages VALUES(54474,'annya_sadda_10',67,replace(replace('\r\n<p class="bodytext">acchariyaṃ nandamāte, abbhutaṃ nandamāte <span class="note">[a. ni. 7.53]</span>, bhoti devadhīte, bhoti sakyadhītare-mahāvuttinā samāse syādīsu ārattaṃ rassattañca. ltupaccayantā pana yebhuyyena tīsu liṅgesu samānarūpā honti, "atthadhammaṃ paripucchitā ca uggahetā <a name="M0.0067"></a> ca dhammānaṃ sotā ca payirūpāsitā cā"ti therīpāḷi. tathā kvaci gacchantādisaddāpi. tamokhandhaṃ padālayaṃ, evaṃ dubbhāsitaṃ bhaṇaṃ iccādi-tattha padālayanti padālayantī, bhaṇanti bhaṇantīti attho.</p>\r\n<p class="bodytext">vīsā, tiṃsā, cattālīsā, paññāsā iccete saṅkhyārāsimhi āgamissanti.</p>\r\n<p class="centered">ākārantitthiliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">ikārantitthiliṅgarāsi</p>\r\n<p class="bodytext">‘gasīna’nti silopo. ratti tiṭṭhati, rattiyo tiṭṭhanti.</p>\r\n<p class="bodytext">‘jantuhetvā’disuttena yolope –</p>\r\n<p class="bodytext"><a name="para91"></a><span class="paranum">91</span>. <span class="bld">yolopanīsu dīgho</span> <span class="note">[ka. 88; rū. 147; nī. 245]</span>.</p>\r\n<p class="bodytext">tiliṅge yonaṃ lope ca niādese ca rassānaṃ dīgho hoti.</p>\r\n<p class="bodytext">rattī tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para92"></a><span class="paranum">92</span>. <span class="bld">ye passivaṇṇassa</span>.</p>\r\n<p class="bodytext">vibhattibhūte vibhattādesabhūte ca yakāre pare pasaññassa ivaṇṇassa lopo hoti. gāthāsuyeva idaṃ vidhānaṃ daṭṭhabbaṃ.</p>\r\n<p class="bodytext">ratyo tiṭṭhanti <span class="note">[rū. 84 piṭṭhe]</span> -sandhivasena āditakāralopo.</p>\r\n<p class="bodytext"><a name="para63"></a><span class="paranum">63</span>. <span class="bld">ayunaṃ vā dīgho</span> <span class="note">[ka. 88; rū. 147; nī. 245]</span>.</p>\r\n<p class="bodytext">ge pare tiliṅge aiunaṃ dīgho hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-91-92-63-');
INSERT INTO pages VALUES(54475,'annya_sadda_10',68,replace(replace('\r\n<p class="bodytext">he <a name="M0.0068"></a> rattī, he ratti. bahuvacane he rattī, he rattiyo, he ratyo.</p>\r\n<p class="bodytext">aṃvacane ‘paro kvacī’ti suttena parasare lutte niggahītaṃ pubbe ivaṇṇuvaṇṇesu tiṭṭhati.</p>\r\n<p class="bodytext">rattiṃ, tathā itthiṃ, dhenuṃ, vadhuṃ, aggiṃ, daṇḍiṃ, bhikkhuṃ, sayambhuṃ iti. rattiyaṃ, ‘bujjhassu jinabodhiya’nti pāḷi <span class="note">[bu. vaṃ. 2.182]</span>, rattinaṃ vā, ‘yāvanto purisassatthaṃ, guyhaṃ jānanti mantina’ntipāḷi <span class="note">[jā. 1.15.335]</span>.</p>\r\n<p class="bodytext">rattī, rattiyo, ratyo-‘ghapatekasmiṃ nādīnaṃ yayā’ti nādīnaṃ yā hoti, rattiyā, yakāre pare ivaṇṇalopo, ratyā.</p>\r\n<p class="bodytext"><a name="para94"></a><span class="paranum">94</span>. <span class="bld">sunaṃhisu</span> <span class="note">[ka. 89; rū. 87; nī. 246]</span>.</p>\r\n<p class="bodytext">su, naṃ, hisu rassānaṃ dīgho hoti.</p>\r\n<p class="bodytext">rattīhi, rattībhi, rattiyā, ratyā, rattīnaṃ, rattiyā, ratyā, rattīhi, rattībhi, rattiyā, ratyā, rattīnaṃ, rattiyā, ratyā, rattiyaṃ, ratyaṃ, rattīsu.</p>\r\n<p class="bodytext">ettha garū su, naṃ, hisu dīghattaṃ aniccaṃ icchanti, taṃ gāthāsu yujjati.</p>\r\n<p class="bodytext">patti yutti vutti kitti, mutti titti khanti kanti.</p>\r\n<p class="bodytext">santi tanti siddhi suddhi, iddhi vuddhi buddhi bodhi.</p>\r\n<p class="bodytext">bhūmi jāti pīti suti, nandi sandhi sāṇi koṭi.</p>\r\n<p class="bodytext">diṭṭhi vuḍḍhi tuṭṭhi yaṭṭhi, pāḷi āḷi nāḷi keḷi.</p>\r\n<p class="bodytext">sati mati gati muti, dhīti yuvati vikati.</p>\r\n<p class="bodytext">rati ruci rasmi asani vasani osadhi aṅguli dhūli dudrabhi</p>\r\n','\r',char(13)),'\n',char(10)),'-94-');
INSERT INTO pages VALUES(54476,'annya_sadda_10',69,replace(replace('\r\n<p class="bodytext">doṇi <a name="M0.0069"></a> aṭavi chaviādayo rattādi.</p>\r\n<p class="bodytext">ettha visesavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para95"></a><span class="paranum">95</span>. <span class="bld">rattādīhi ṭo smiṃno</span> <span class="note">[ka. 69; rū. 186; nī. 218, 219; rattyādīhi ṭo smino (bahūsu) ratyādīhi (katthaci)]</span>.</p>\r\n<p class="bodytext">rattisadda, ādisaddehi smiṃno ṭo hoti vā.</p>\r\n<p class="bodytext">divā ca ratto ca <span class="note">[khu. pā. 6.2; jā. 1.9.92]</span>, ādo, ādimhi, pādādo, pādādimhi, gāthādo, gāthādimhi-ādisaddo pana pulliṅgoyeva, rattiṃ bhojanaṃ bhuñjati, ādiṃ tiṭṭhatīti ādhāratthe dutiyāva, ratyo amoghā gacchanti <span class="note">[jā. 2.22.105]</span>, tiṇalatāni osadhyo <span class="note">[jā. 2.22.2174]</span>, tato ratyā vivasāne <span class="note">[jā. 2.22.1689]</span>, na jaccā vasalo hoti, na jaccā hoti brāhmaṇo <span class="note">[su. ni. 142]</span> -jaccāti jātiyā, na nikatyā sukhamedhati <span class="note">[jā. 1.1.38]</span>, khantyā bhiyyo na vijjati <span class="note">[saṃ. ni. 1.250]</span>.</p>\r\n<p class="bodytext">nāññatra bojjhā tapasā <span class="note">[saṃ. ni. 1.98]</span>, yatheva khalatī bhūmyā, bhūmyāyeva patiṭṭhati <span class="note">[jā. 2.22.1522]</span>, mahāvuttinā māti, pitisaddā nādīhi saddhiṃ matyā, petyāti sijjhanti, matyā ca petyā ca etaṃ jānāmimātito pititoti attho, matyā ca petyā ca kataṃ susādhu <span class="note">[jā. 2.18.61]</span> -katanti kataṃ nāmaṃ, susādhūti atisundaraṃ. ‘anuññāto ahaṃ matyā, sañcatto pitarā aha’nti <span class="note">[jā. 2.22.29]</span> pāḷipadāni. ‘mātīnaṃ dohaḷo nāma janinda vuccatī’ <span class="note">[jā. 2.22.1347]</span> ti ca pāḷi, vīsati, tiṃsati, saṭṭhi, sattati, asīti, navuti, koṭi, pakoṭi iccete saṅkhyārāsimhi āgamissanti.</p>\r\n<p class="centered">ikārantitthiliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">īkārantitthiliṅgarāsi</p>\r\n<p class="bodytext"><a name="para96"></a><span class="paranum">96</span>. <span class="bld">simhi nānapuṃsakassa</span> <span class="note">[ka. 85; rū. 150; nī. 239 moga-du. 66; sismiṃ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-95-96-');
INSERT INTO pages VALUES(54477,'annya_sadda_10',70,replace(replace('\r\n<p class="bodytext">simhi <a name="M0.0070"></a> pare anapuṃsakassa pumitthīnaṃ dīghassa rasso na hoti.</p>\r\n<p class="bodytext">itthī tiṭṭhati, itthī tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para97"></a><span class="paranum">97</span>. <span class="bld">ekavacanayosvaghonaṃ</span> <span class="note">[ka. 84; rū. 144; nī. 237, 238]</span>.</p>\r\n<p class="bodytext">gho ca o ca gho, na gho agho. ekavacanesu ca yosu ca paresu gha, ovajjitānaṃ sabbesaṃ dīghānaṃ rasso hoti.</p>\r\n<p class="bodytext">itthiyo tiṭṭhanti, ithyo tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para98"></a><span class="paranum">98</span>. <span class="bld">ge vā</span> <span class="note">[ka. 245, 246; rū. 152, 73; nī. 476-9]</span>.</p>\r\n<p class="bodytext">ge pare gha, ovajjitānaṃ sabbesaṃ dīghānaṃ rasso hoti vā.</p>\r\n<p class="bodytext">bhoti itthi, bhoti itthī, bhotiyo itthī, bhotiyo itthiyo, bhotiyo ithyo, itthiṃ passati.</p>\r\n<p class="bodytext"><a name="para99"></a><span class="paranum">99</span>. <span class="bld">yaṃ pīto</span> <span class="note">[ka. 223; rū. 188; nī. 450]</span>.</p>\r\n<p class="bodytext">yo pasañño īkāro, tato aṃvacanassa yaṃ hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-97-98-99-');
INSERT INTO pages VALUES(54478,'annya_sadda_10',71,replace(replace('\r\n<p class="bodytext">itthiyaṃ passati, ettha ca yanti suttavibhattena "bujjhassu jinabodhiya"nti <span class="note">[bu. vaṃ. 2.182]</span> sijjhati. itthī passati, itthiyo passati, ithyo <a name="M0.0071"></a> passati, itthiyā, ithyā, itthīhi, itthībhi, itthiyā, ithyā, itthīnaṃ, itthiyā, itthimhā, ithyā, itthīhi, itthībhi, itthiyā, ithyā, itthīnaṃ, itthiyā, ithyā, itthiyaṃ, ithyaṃ, itthimhi, itthīsu.</p>\r\n<p class="bodytext">nadī sandati, nadī sandanti, nadiyo sandanti.</p>\r\n<p class="bodytext">ivaṇṇalope sandhisuttena yakāre pare tavaggassa cavaggo, yassa ca pubbarūpaṃ <span class="note">[ka. 98; rū. 87 piṭṭhe; nī. 104; 262-3-4]</span>. najjo sandanti <span class="note">[ka. 98; rū. 87 piṭṭhe; nī. 104; 262-3-4]</span>, nādyekavacanesu najjā kataṃ, najjā deti, najjā apeti, najjā santakaṃ, najjā tiṭṭhati, najjaṃ tiṭṭhati, sesarūpāni itthisadisāni.</p>\r\n<p class="bodytext">evaṃ gacchatī gacchantī, satī santī, asatī asantī, mahatī mahantī, brahmatī brantī, bhotī bhontī, bhavissatī bhavissantī, gamissatī gamissantī, guṇavatī guṇavantī, sīlavatī sīlavantī, satimatī satimantī, sirimatī sirimantī, katavatī katavantī, bhuttāvatī bhuttāvantī, sabbāvatī sabbāvantī, yāvatī yāvantī, tāvatī tāvantī. kamhi āgame rasso, yāvatikā, tāvatikā.</p>\r\n<p class="bodytext">gāvī, yakkhī, yakkhinī, ārāmikinī, daṇḍapāṇinī, daṇḍinī, bhikkhunī, paracittavidunī, muṭṭhassatinī, gharaṇī, pokkharaṇī, ācarinī, mātulānī, gahapatānī iccādayo. nadādi.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para100"></a><span class="paranum">100</span>. <span class="bld">najjā yosvāma</span> <span class="note">[nī. 262]</span>.</p>\r\n<p class="bodytext">yosu paresu nadiyā ante āmaāgamo hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-100-');
INSERT INTO pages VALUES(54479,'annya_sadda_10',72,replace(replace('\r\n<p class="bodytext">najjāyo <a name="M0.0072"></a> sandanti <span class="note">[saṃ. ni. 3.224]</span>, najjāyo supatitthāyo <span class="note">[jā. 2.22.1414]</span> ti pāḷi, nimijātake pana najjonupariyāyati, nānāpupphadumāyutāti ca najjo cānupariyātīti <span class="note">[jā. 2.22.537]</span> ca pāḷi, tattha mahāvuttinā sissa ottaṃ.</p>\r\n<p class="bodytext">uṭṭhehi revate supāpakamme <span class="note">[vi. va. 863]</span>, dāsā ca dāsyo ca, anujīvino <span class="note">[jā. 1.10.101]</span>, bārāṇasyaṃ mahārāja, kākarājā nivāsako <span class="note">[jā. 1.3.124]</span>, bārāṇasyaṃ ahu rājā <span class="note">[jā. 1.16.178]</span>, rañño mano ummādantyā niviṭṭho, ummādantyā ramitvāna, sivirājā tato siyaṃ <span class="note">[jā. 2.18.70]</span>, dārakeva ahaṃ nessaṃ. brāhmaṇyā paricārake <span class="note">[jā. 2.22.2111]</span>. tathā yosu pokkharañño. nādīsu pathabyā, puthabyā, pokkharaññā. smiṃmhi pathabyā, pathabyaṃ, puthabyā, puthabyaṃ, pokkharaññā, pokkharaññaṃ, vetraññā, vetraññaṃ <span class="note">[ve traraññā, (nissaya)]</span> iccādīni dissanti.</p>\r\n<p class="centered">īkārantitthiliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">ukārantitthiliṅgarāsi</p>\r\n<p class="bodytext">silopo, dhenu gacchati, dhenuyo gacchanti, yolope dīgho, dhenū gacchanti, bhoti dhenu, bhoti dhenū, bhotiyo dhenuyo, bhotiyo dhenū, dhenuṃ passati, dhenuyo passati, dhenū passati, dhenuyā, dhenūhi, dhenūbhi, dhenuyā, dhenūnaṃ, dhenuyā, dhenumhā, dhenūhi, dhenūbhi, dhenuyā, dhenūnaṃ, dhenuyā, dhenuyaṃ, dhenumhi, dhenūsu.</p>\r\n<p class="bodytext">evaṃ yāgu, kāsu, daddu, kaṇḍu, kacchu, rajju, kareṇu, piyaṅgu, sassu iccādayo. dhenvādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54480,'annya_sadda_10',73,replace(replace('\r\n<p class="bodytext"><span class="bld">dhātu</span>saddo <a name="M0.0073"></a> pana pāḷinaye itthiliṅgo, saddasatthanaye pumitthiliṅgo.</p>\r\n<p class="bodytext">mātu, dhītu, duhitusaddā itthi liṅgā, tesaṃ rūpaṃ pitādigaṇe āgamissati.</p>\r\n<p class="centered">ukārantitthiliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">ūkārantitthiliṅgarāsi</p>\r\n<p class="bodytext">vadhū gacchati, vadhū gacchanti, yosu rasso, vadhuyo gacchanti, bhoti vadhu, bhoti vadhū, bhotiyo vadhū, vadhuyo, vadhuṃ, vadhū, vadhuyo, vadhuyā, vadhūhi, vadhūbhi, vadhuyā, vadhūnaṃ, vadhuyā, vadhumhā, vadhūhi, vadhūbhi, vadhuyā, vadhūnaṃ, vadhuyā, vadhuyaṃ, vadhūsu. evaṃ jambū, sarabhū, sutanū, nāganāsūrū, saṃhitorū, vāmorū, lakkhaṇūrū, brahmabandhū, bhū, camū iccādayo. vadhādi.</p>\r\n<p class="bodytext">sāhaṃ gantvā manussattaṃ, vadaññū vītamaccharāti <span class="note">[vi. va. 634]</span> ca kodhanā akataññū cāti <span class="note">[jā. 1.1.63]</span> ca pāḷiyo, tasmā nīpaccayaṃ vināpi kvaci ūkārantakitakasaddā itthiliṅgā bhavanti.</p>\r\n<p class="centered">ūkārantitthiliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">okārantarāsi</p>\r\n<p class="bodytext">gosaddo dviliṅgo. tassa rūpāni kānici dviyatthavasena itthiyampi vattanti pumepi vattanti missakepi vattanti, kānici itthiyaṃ kānici pume. idha pana sabbāni yāni samodhānetvā dīpiyante.</p>\r\n<p class="bodytext">silopo, gogacchati-ettha ca goti abhinnasaddaliṅgattā goṇotipi yujjati, gāvītipi yujjati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54481,'annya_sadda_10',74,replace(replace('\r\n<p class="bodytext"><a name="para101"></a><span class="paranum">101</span>. <span class="bld">gossāgasihinaṃsu</span> <a name="M0.0074"></a> <span class="bld">gāvagavā</span> <span class="note">[ka. 73-5; rū. 169, 170, 174; nī. 224]</span>.</p>\r\n<p class="bodytext">ga, si, hi, naṃvajjitāsu vibhattīsu gosaddassa gāva, gavādesā honti.</p>\r\n<p class="bodytext"><a name="para102"></a><span class="paranum">102</span>. <span class="bld">ubhagohi ṭo</span> <span class="note">[ka. 205; rū. 160; nī. 421]</span>.</p>\r\n<p class="bodytext">ubha, gohi yonaṃ ṭo hoti.</p>\r\n<p class="bodytext">gāvo, gavo, he go, he gāvo, he gavo, gāvaṃ, gavaṃ.</p>\r\n<p class="bodytext"><a name="para103"></a><span class="paranum">103</span>. <span class="bld">gāvumhi</span> <span class="note">[ka. 76; rū 171, 226]</span>.</p>\r\n<p class="bodytext">aṃmhi gossa gāvu hoti vā.</p>\r\n<p class="bodytext">gāvuṃ, gāvo, gavo, gāvena, gavena.</p>\r\n<p class="bodytext"><a name="para104"></a><span class="paranum">104</span>. <span class="bld">nāssā</span>.</p>\r\n<p class="bodytext">gossa gāva, gavādesato nāvacanassa ā hoti vā.</p>\r\n<p class="bodytext">gāvā, gavā, gohi, gobhi, gāvassa, gavassa.</p>\r\n<p class="bodytext"><a name="para105"></a><span class="paranum">105</span>. <span class="bld">gavaṃ sena</span>.</p>\r\n<p class="bodytext">sena saha gossa gavaṃ hoti vā.</p>\r\n<p class="bodytext">gavaṃ, gonaṃ.</p>\r\n<p class="bodytext"><a name="para106"></a><span class="paranum">106</span>. <span class="bld">gunnañca naṃnā</span> <span class="note">[ka. 81; rū. 172; nī. 230]</span>.</p>\r\n<p class="bodytext">naṃnā saha gossa gunnañca hoti gavañca.</p>\r\n<p class="bodytext">gunnaṃ, gavaṃ, gāvasmā, gavasmā, gāvamhā, gavamhā, gāvā, gavā, gohi, gobhi, gāvassa, gavassa, gavaṃ, gonaṃ, gunnaṃ, gavaṃ, gāvasmiṃ, gāvamhi, gāve, gavasmiṃ, gavamhi, gave, gosu, gāvesu, gavesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-101-102-103-104-105-106-');
INSERT INTO pages VALUES(54482,'annya_sadda_10',75,replace(replace('\r\n<p class="bodytext">yosu <a name="M0.0075"></a> gāva, gavādese kate ato yonaṃ ṭā, ṭe ca honti, usabhā rukkhā gāviyo gavā ca <span class="note">[jā. 1.1.77]</span>. balagavā dammagavā vā gaṅgāya pāraṃ agamiṃsu. athāpare patāresi balagāve dammagāve <span class="note">[ma. ni. 1.352 (thokaṃ visadisaṃ)]</span> ti pāḷipadāni.</p>\r\n<p class="bodytext">ettha ca gāvo no paramā mittā, yāsu jāyanti osadhā <span class="note">[su. ni. 298]</span> ti ca, gavā khīraṃ, khīramhā dadhi, dadhimhā navanītaṃ, navanītamhā sappi, sappimhā sappimaṇḍoti ca itthiyaṃ vattanti. gāviyo gavāti ca balagavā dammagavā balagave dammagaveti <span class="note">[ma. ni. 1.351]</span> ca gāvuṃ vā te dammi gāviṃ vā te dammīti ca gavaṃva siṅgino siṅganti <span class="note">[jā. 1.12.39]</span> ca pume bhavanti. atittheneva gāvo patāresi, atha kho tā gāvo majjhe gaṅgāya anayabyasanaṃ āpajjiṃsū <span class="note">[ma. ni. 1.350]</span> ti ca annadā baladā cetā, vaṇṇadā sukhadā ca tā, etamatthavasaṃ ñatvā, nāssu gāvo haniṃsu teti <span class="note">[su. ni. 298]</span> ca bhaddavasena itthiyaṃ atthavasena missake vattanti. gunnaṃ ce taramānānaṃ, gavaṃ ce taramānānaṃ, ujuṃ gacchati puṅgavo, sabbā tā ujuṃ gacchantīti <span class="note">[jā. 1.4.135; 2.18.104]</span> ca missake eva. balagava, dammagavasaddā jaraggava, puṅgava, sagava, paragava, dāragavasaddā viya akārantā samāsasaddātipi yujjati.</p>\r\n<p class="bodytext">missakaṭṭhānesu pana itthibahulattā tā gāvo etā gāvotiādinā itthiliṅgameva dissati.</p>\r\n<p class="centered">iti okārantarāsi.</p>\r\n<p class="centered">itthiliṅgarāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54483,'annya_sadda_10',76,replace(replace('\r\n<p class="subhead">pulliṅgarāsi<a name="M0.0076"></a></p>\r\n<p class="subhead"><span class="bld">akāranta pulliṅgapurisādirāsi</span></p>\r\n<p class="bodytext">atha pulliṅgāni dīpiyante.</p>\r\n<p class="bodytext">sattavidhaṃ pulliṅgaṃ – adantaṃ, ādantaṃ, idantaṃ, īdantaṃ, udantaṃ, ūdantaṃ, odantaṃ.</p>\r\n<p class="bodytext"><a name="para107"></a><span class="paranum">107</span>. <span class="bld">sisso</span> <span class="note">[ka. 104; rū. 66; nī. 272]</span>.</p>\r\n<p class="bodytext">ato sissa o hoti pume.</p>\r\n<p class="bodytext">puriso tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para108"></a><span class="paranum">108</span>. <span class="bld">ato yonaṃ ṭāṭe</span> <span class="note">[ka. 107; rū. 69; nī. 275, 277]</span>.</p>\r\n<p class="bodytext">ato paṭhamāyonaṃ dutiyāyonañca kamena ṭā, ṭe honti puṃ, napuṃsakesu. ṭānubandho sabbādesattho.</p>\r\n<p class="bodytext">purisā tiṭṭhanti.</p>\r\n<p class="bodytext">‘gasīna’nti silopo, bho purisa, ‘ayunaṃ vā dīgho’ti dīgho, bho purisā, bhonto purisā, purisaṃ, purise.</p>\r\n<p class="bodytext"><a name="para109"></a><span class="paranum">109</span>. <span class="bld">atena</span> <span class="note">[ka. 103; rū. 79; nī. 271]</span>.</p>\r\n<p class="bodytext">ato nāvacanassa enādeso hoti puṃ, napuṃsakesu.</p>\r\n<p class="bodytext">purisena.</p>\r\n<p class="bodytext"><a name="para110"></a><span class="paranum">110</span>. <span class="bld">suhisvasse</span> <span class="note">[ka. 101; rū. 80; nī. 68]</span>.</p>\r\n<p class="bodytext">su, hisu paresu assa e hoti puṃ, napuṃsakesu.</p>\r\n<p class="bodytext">purisehi, purisebhi.</p>\r\n','\r',char(13)),'\n',char(10)),'-107-108-109-110-');
INSERT INTO pages VALUES(54484,'annya_sadda_10',77,replace(replace('\r\n<p class="bodytext"><a name="para111"></a><span class="paranum">111</span>. <span class="bld">suu</span> <a name="M0.0077"></a> <span class="bld">sassa</span> <span class="note">[ka. 61; rū. 86; nī. 208]</span>.</p>\r\n<p class="bodytext">sassa ādimhi sāgamo hoti. ukāro uccāraṇattho, ñānubandho ādimhīti dīpanattho.</p>\r\n<p class="bodytext">purisassa, ‘sunaṃhisū’ti dīgho. purisānaṃ, purisasmā, purisamhā.</p>\r\n<p class="bodytext"><a name="para112"></a><span class="paranum">112</span>. <span class="bld">smāsmiṃnaṃ</span> <span class="note">[ka. 108; rū. 90; nī. 276]</span>.</p>\r\n<p class="bodytext">ato smā, smiṃnaṃ kamena ṭā, ṭe honti puṃ, napuṃsakesu.</p>\r\n<p class="bodytext">purisā, purisehi, purisebhi, purisassa, purisānaṃ, purisasmiṃ, purisamhi, purise, purisesu.</p>\r\n<p class="bodytext">evaṃ buddhā, dhammo, saṅgho, sakko, devo, satto, naro, goṇo, puṅgavo, jaraggavo, sagavo, paragavo, rājagavo, mātugāmo, orodho, dāroiccādi.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para113"></a><span class="paranum">113</span>. <span class="bld">kvace vā</span> <span class="note">[nī. 277]</span>.</p>\r\n<p class="bodytext">ato sissa kvaci e hoti vā puṃ, napuṃsakesu.</p>\r\n<p class="bodytext">pume tāva –</p>\r\n<p class="bodytext">vanappagumbe yatha phussitagge <span class="note">[khu. pā. 6.13; su. ni. 236]</span>, "ke gandhabbe rakkhase ca nāge, ke kimpurise cāpi mānuse. ke paṇḍite sabbakāmadade. dīgharattaṃ bhattā me bhavissati" <span class="note">[jā. 2.22.1352]</span>. natthi attakāre natthi parakāre natthi purisakāre <span class="note">[dī. ni. 1.168]</span>, eke ekatthe, same samabhāge, nahevaṃ vattabbe <span class="note">[kathā. 1]</span>, ke chave siṅgāle, ke chave pāthikaputte <span class="note">[dī. ni. 3.29-31]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-111-112-113-');
INSERT INTO pages VALUES(54485,'annya_sadda_10',78,replace(replace('\r\n<p class="bodytext">napuṃsake <a name="M0.0078"></a> pana –</p>\r\n<p class="bodytext">bhogavatī nāma mandire, nagare nimmite kañcanamaye <span class="note">[jā. 2.22.1370]</span> iccādi.</p>\r\n<p class="bodytext">vāti kiṃ? vanappagumbo.</p>\r\n<p class="bodytext">kvacīti kiṃ? puriso.</p>\r\n<p class="bodytext">mahāvuttinā paṭhamāyonañca kvaci ṭe hoti. bāle ca paṇḍite ca sandhāvitvā saṃsaritvā dukkhassantaṃ karissanti <span class="note">[dī. ni. 1.168]</span>, kvaci yonaṃ pakati hoti, vane vāḷamigā ceva, acchakokataracchayo, bahūhi paripanthayo <span class="note">[jā. 2.22.255]</span>, kyassa byapathayo assu iccādi.</p>\r\n<p class="bodytext"><a name="para114"></a><span class="paranum">114</span>. <span class="bld">divādito</span> <span class="note">[ka. 206; rū. 165]</span>.</p>\r\n<p class="bodytext">divādīhi smiṃno ṭi hoti.</p>\r\n<p class="bodytext">divi-devaloketyattho.</p>\r\n<p class="bodytext">ādisaddena asa bhuvi, niccaṃ vāgamo. ayyasaddamhā mahāvuttinā ālapane ga, yonaṃ ṭo hoti vā. bho ayyo ayya vā, bhonto ayyo ayyā vā. sesaṃ purisasamaṃ.</p>\r\n<p class="centered">purisādirāsi niṭṭhito.</p>\r\n<p class="subhead">manogaṇarāsi</p>\r\n<p class="bodytext">mano, manā, bho mana, bho manā, bhonto manā.</p>\r\n<p class="bodytext"><a name="para115"></a><span class="paranum">115</span>. <span class="bld">manādīhi smiṃsaṃnāsmānaṃ sisoosāsā</span> <span class="note">[ka. 181-2, 184; rū. 95-97; nī. 373-4, 376-7]</span>.</p>\r\n<p class="bodytext">tehi smiṃ, sa, aṃ, nā, smānaṃ kamena si, so, o, sā, sā honti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-114-115-');
INSERT INTO pages VALUES(54486,'annya_sadda_10',79,replace(replace('\r\n<p class="bodytext">manaṃ<a name="M0.0079"></a>, mano, mane, manena, manasā, manehi, manebhi, manassa, manaso, manānaṃ, manasmā, manamhā, manasā, manā, manehi, manebhi, manassa, manaso, manānaṃ, manasmiṃ, manamhi, manasi, mane, manesu.</p>\r\n<p class="bodytext">tamo, tapo, tejo, siro, uro, vaco, rajo, ojo, ayo, payo, vayo, saro, yaso, ceto, chando, radhatā, aho iccādi <span class="bld">manogaṇo</span>.</p>\r\n<p class="bodytext">idaṃ <span class="bld">manogaṇalakkhaṇaṃ</span>. kriyākamme odanto, nādīnaṃ sāditā, samāsataddhitamajjhe odanto cāti.</p>\r\n<p class="bodytext">yo ve dassanti vatvāna, adāne kurute mano <span class="note">[jā. 1.15.61]</span>, kassapassa vaco sutvā, tapo idha pakrubbati <span class="note">[saṃ. ni. 1.204]</span>, ceto paricca jānāti <span class="note">[dī. ni. 1.242]</span>, siro te bādhayissāmi iccādi.</p>\r\n<p class="bodytext">manasā ce pasannena <span class="note">[dha. pa. 2]</span>, vippasannena cetasā <span class="note">[jā. 2.22.551]</span>, vacasā manasā ceva, vandā me te tathāgate <span class="note">[parittapāḷi āṭānāṭiyasutta]</span>. ekūnatiṃso vayasā <span class="note">[dī. ni. 2.214]</span>, tejasā yasasā jalaṃ <span class="note">[vi. va. 857]</span>, tapasā uttamo satto, ghatena vā bhuñjassu payasā vā, vandāmi sirasā pāde <span class="note">[jā. 2.20.68]</span>, ye etā upasevanti, chandasā vā dhanena vā <span class="note">[jā. 2.21.350]</span>, urasā panudissāmi <span class="note">[jā. 2.22.1833]</span>, ayasā paṭikujjhito <span class="note">[a. ni. 3.36]</span> iccādi.</p>\r\n<p class="bodytext">na mayhaṃ manaso piyo <span class="note">[jā. 1.10.11]</span>, cetaso parivitakko udapādi <span class="note">[pārā. 18]</span>, cetaso samannāhāro, sādhu khalu payaso pānaṃ, sāvittī chandaso mukhaṃ <span class="note">[ma. ni. 2.400]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54487,'annya_sadda_10',80,replace(replace('\r\n<p class="bodytext">sādhukaṃ <a name="M0.0080"></a> manasi karotha <span class="note">[dī. ni. 2.3]</span>, etamatthaṃ cetasi sannidhāya, sirasi añjaliṃ katvā <span class="note">[apa. thera 1.41.82]</span>, urasilomo, pāpaṃ akāsi rahasi iccādi.</p>\r\n<p class="bodytext">manodhātu, manomayaṃ, tamokhandhaṃ padālayi, tapodhano, tejodhātu, siroruhā kesā, saroruhaṃ padumaṃ, rajoharaṇaṃ vatthaṃ, ojoharaṇā sākhā, ayopatto, vayoguṇā anupubbaṃ jahanti, yasodharā devī, cetoyuttā dhammā, chandovicitipakaraṇaṃ, rahogato cintesi, ahorattānamaccaye <span class="note">[saṃ. ni. 1.112]</span> iccādi.</p>\r\n<p class="bodytext">mahāvuttinā ahamhā smiṃno ni ca u ca hoti, tadahani, tadahu. rahamhā smiṃno o hoti, mātugāmena saddhiṃ eko ekāya raho nisīdati <span class="note">[pārā. 452]</span>, raho tiṭṭhati, raho manteti.</p>\r\n<p class="centered">manogaṇarāsi niṭṭhito.</p>\r\n<p class="subhead">manādigaṇarāsi</p>\r\n<p class="bodytext"><a name="para116"></a><span class="paranum">116</span>. <span class="bld">kodhādīhi</span>.</p>\r\n<p class="bodytext">etehi nāvacanassa sā hoti vā.</p>\r\n<p class="bodytext">kodhasā, kodhena, atthasā, atthena.</p>\r\n<p class="bodytext"><a name="para117"></a><span class="paranum">117</span>. <span class="bld">nāssa sā</span> <span class="note">[ka. 181; rū. 95; nī. 373]</span>.</p>\r\n<p class="bodytext">padādīhi nāvacanassa sā hoti vā.</p>\r\n<p class="bodytext">padasā, padena, bilasā, bilena.</p>\r\n','\r',char(13)),'\n',char(10)),'-116-117-');
INSERT INTO pages VALUES(54488,'annya_sadda_10',81,replace(replace('\r\n<p class="bodytext"><a name="para118"></a><span class="paranum">118</span>. <span class="bld">padādīhi</span> <a name="M0.0081"></a> <span class="bld">si</span>.</p>\r\n<p class="bodytext">padādīhi smiṃno si hoti vā.</p>\r\n<p class="bodytext">padasi, pade, bilasi, bile.</p>\r\n<p class="bodytext">tattha kodhādiko pulliṅgo, padādiko napuṃsako. tattha keci saddā samāsa, taddhitamajjhe odantā honti <span class="note">[ka. 183; rū. 48; nī. 375]</span>, āpodhātu, āpomayaṃ, vāyodhātu, vāyomayaṃ, jīva tvaṃ saradosataṃ <span class="note">[jā. 1.2.9]</span>, anuyanti disodisaṃ <span class="note">[dī. ni. 3.281]</span> iccādi.</p>\r\n<p class="bodytext">keci nāssa sādesaṃ labhanti, kodhasā usunā vijjhi <span class="note">[jā. 2.22.352]</span>, daḷhaṃ gaṇhāhi thāmasā <span class="note">[jā. 1.7.30]</span>, padasāva agamāsi, mākāsi mukhasā pāpaṃ, saccena danto damasā upeto-damasāti indriyadamanena, suciṃ paṇītaṃ rasasā upetaṃ <span class="note">[jā. 1.7.18]</span>, vegasā gantvāna, āyusā ekaputtamanurakkhe <span class="note">[khu. pā. 9.7]</span> iccādi.</p>\r\n<p class="bodytext">keci smiṃno syādesaṃ labhanti, padasi, bilasi iccādi.</p>\r\n<p class="bodytext">kehici mahāvuttinā nā, smānaṃ so hoti, atthaso, akkharaso, suttaso, byañjanaso, hetuso, yoniso, upāyaso, ṭhānaso, dīghaso, oraso, bahuso, puthuso, mattaso, bhāgaso iccādi.</p>\r\n<p class="bodytext">"padaso dhammaṃ vāceyya <span class="note">[pāci. 45]</span>, bilaso vibhajitvā nisinno assa" <span class="note">[dī. ni. 2.378]</span> iccādīsu pana vicchāyaṃ sopaccayo.</p>\r\n<p class="bodytext">yadā pana samāsante mahāvuttinā syādīsu vibhattīsu sāgamo hoti, tadā purisādigaṇopi hoti, byāsattamanaso, abyaggamanaso <span class="note">[a. ni. 3.29]</span>, putto jāto acetaso <span class="note">[jā. 2.22.4]</span>, sumedhaso <span class="note">[a. ni. 4.62]</span>, bhūrimedhaso <span class="note">[su. ni. 1137]</span> iccādi.</p>\r\n<p class="centered">iti manādigaṇarāsi.</p>\r\n<p class="subhead">guṇavādigaṇarāsi</p>\r\n<p class="bodytext"><a name="para119"></a><span class="paranum">119</span>. <span class="bld">ntussa</span> <span class="note">[ka. 124; rū. 98; nī. 299]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-118-119-');
INSERT INTO pages VALUES(54489,'annya_sadda_10',82,replace(replace('\r\n<p class="bodytext">simhi <a name="M0.0082"></a> ntussa ṭā hoti.</p>\r\n<p class="bodytext">guṇavā tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para120"></a><span class="paranum">120</span>. <span class="bld">yvādo ntussa</span> <span class="note">[ka. 92; rū. 100; nī. 249]</span>.</p>\r\n<p class="bodytext">yoādīsu ntussa attaṃ hoti.</p>\r\n<p class="bodytext">guṇavantā tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para121"></a><span class="paranum">121</span>. <span class="bld">ntantūnaṃ nto yomhi paṭhame</span> <span class="note">[ka. 92; rū. 100; nī. 249]</span>.</p>\r\n<p class="bodytext">paṭhame yomhi savibhattīnaṃ nta, ntūnaṃ nto hoti.</p>\r\n<p class="bodytext">guṇavanto tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para122"></a><span class="paranum">122</span>. <span class="bld">ṭaṭāaṃ ge</span> <span class="note">[ka. 126; rū. 101; nī. 301-2]</span>.</p>\r\n<p class="bodytext">ge pare savibhattīnaṃ nta, ntūnaṃ ṭa, ṭā, aṃ honti.</p>\r\n<p class="bodytext">bho guṇava, bho guṇavā, bho guṇavaṃ, bhonto guṇavantā, bhonto guṇavanto, guṇavantaṃ, guṇavante, guṇavantena.</p>\r\n<p class="bodytext"><a name="para123"></a><span class="paranum">123</span>. <span class="bld">totātitā sasmāsmiṃnāsu</span> <span class="note">[ka. 127, 187; rū. 102, 108; nī. 303, 386]</span>.</p>\r\n<p class="bodytext">sa, smā, smiṃ, nāsu savibhattīnaṃ nta, ntūnaṃ kamena to, tā,ti, tā honti vā.</p>\r\n<p class="bodytext">guṇavatā, guṇavantehi, guṇavantebhi, guṇavantassa, guṇavato.</p>\r\n','\r',char(13)),'\n',char(10)),'-120-121-122-123-');
INSERT INTO pages VALUES(54490,'annya_sadda_10',83,replace(replace('\r\n<p class="bodytext"><a name="para124"></a><span class="paranum">124</span>. <span class="bld">naṃmhi</span> <a name="M0.0083"></a> <span class="bld">taṃ vā</span> <span class="note">[ka. 128; rū. 104; nī. 304]</span>.</p>\r\n<p class="bodytext">naṃmhi savibhattīnaṃ nta, ntūnaṃ taṃ hoti vā.</p>\r\n<p class="bodytext">guṇavantānaṃ, guṇavataṃ, guṇavantasmā, guṇavantamhā, guṇavantā, guṇavatā, guṇavantehi, guṇavantebhi, guṇavantassa, guṇavato, guṇavantānaṃ, guṇavataṃ, guṇavantasmiṃ, guṇavantamhi, guṇavati, guṇavante, guṇavantesu.</p>\r\n<p class="bodytext">evaṃ bhagavā, sīlavā, paññavā, balavā, dhanavā, vaṇṇavā, bhogavā, sutavā iccādi. ettha ca ālapane bhagavāti niccaṃ dīgho.</p>\r\n<p class="bodytext">sabbāvā, sabbāvanto, sabbāvantaṃ, sabbāvante, sabbāvantena, sabbāvatā, sabbāvantehi.pe.. sabbāvantesu.</p>\r\n<p class="bodytext">evaṃ yāvā, yāvanto, tāvā, tāvanto, ettāvā, ettāvanto, kiṃvā, kiṃvanto, kittāvā, kittāvanto iccādi. tathā bhojanaṃ bhuttavā, bhuttavanto, dhammaṃ buddhavā, buddhavanto, kammaṃ katavā, katavanto iccādi ca.</p>\r\n<p class="bodytext">satimā, satimantā, satimanto, bho satima, bho satimā, bho satimaṃ, bhonto satimantā, bhonto satimanto, satimantaṃ, satimante, satimantena, satimatā, satimantehi, satimantebhi, satimantassa, satimato, satimantānaṃ, satimataṃ, satimantasmā, satimantamhā, satimantā, satimatā, satimantehi, satimantebhi, satimantassa, satimato, satimantānaṃ, satimataṃ, satimantasmiṃ, satimantamhi, satimati, satimante, satimantesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-124-');
INSERT INTO pages VALUES(54491,'annya_sadda_10',84,replace(replace('\r\n<p class="bodytext">evaṃ matimā, gatimā, pāpimā, jātimā, bhāṇumā, āyumā, āyasmā, sirimā, hirimā, dhitimā, kittimā, iddhimā, jutimā<a name="M0.0084"></a>, mutimā, thutimā, buddhimā, cakkhumā, bandhumā, gomā iccādi.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext"><a name="para125"></a><span class="paranum">125</span>. <span class="bld">himavato vā o</span> <span class="note">[ka. 94; rū. 105; nī. 252]</span>.</p>\r\n<p class="bodytext">simhi himavantasaddassa o hoti vā. ‘gasīna’nti lopo.</p>\r\n<p class="bodytext">himavanto pabbato <span class="note">[dha. pa. 304]</span>, himavā pabbato.</p>\r\n<p class="bodytext"><a name="para126"></a><span class="paranum">126</span>. <span class="bld">ntassa ca ṭa vaṃse</span> <span class="note">[ka. 93; rū. 106; nī. 251]</span>.</p>\r\n<p class="bodytext">aṃ, sesu ntassa ca ntussa ca sabbassa ṭa hoti vā.</p>\r\n<p class="bodytext">"ajjhogāhetvā himava"nti <span class="note">[apa. thera 2.47.59]</span> pāḷi. satimaṃ, bandhumaṃ, guṇavassa, satimassa, bandhumassa.</p>\r\n<p class="bodytext">mahāvuttinā kvaci simhi ge ca pare ntussa attaṃ hoti, "atulo nāma nāmena, paññavanto jutindharo"ti <span class="note">[bu. vaṃ. 21.10]</span> ca "gatimanto satimanto, dhitimanto ca yo isī"ti <span class="note">[theragā. 1052]</span> ca "cakkhumanto mahāyaso"ti ca "tuyhaṃ pitā mahāvīra, paññavanta jutindharā"ti <span class="note">[apa. therī 2.2.389]</span> ca pāḷī.</p>\r\n<p class="bodytext">paṭhamāyomhi kvaci ntussa ṭa hoti, vaggumudātīriyā pana bhikkhū vaṇṇavā honti <span class="note">[pārā. 194]</span>, etha tumhe āvuso sīlavā hotha <span class="note">[a. ni. 5.114]</span>, cakkhumā andhakā honti, ye itthīnaṃ vasaṃ gatā <span class="note">[jā. aṭṭha. 2.3.36]</span>, saṃsuddhapaññā kusalā mutimā bhavanti <span class="note">[su. ni. 887 (saṃsuddhapaññā kusalā mutīmā)]</span>.</p>\r\n<p class="centered">iti guṇavādigaṇarāsi.</p>\r\n<p class="subhead">gacchantādigaṇarāsi</p>\r\n<p class="bodytext"><a name="para127"></a><span class="paranum">127</span>. <span class="bld">ntassaṃ simhi</span> <span class="note">[ka. 186; rū. 107; nī. 382-4; ‘tassaṃ’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-125-126-127-');
INSERT INTO pages VALUES(54492,'annya_sadda_10',85,replace(replace('\r\n<p class="bodytext">simhi <a name="M0.0085"></a> ntassa aṃ hoti vā. silopo.</p>\r\n<p class="bodytext">gacchaṃ, gacchanto, gacchantā, gacchanto, bho gaccha, bho gacchā, bho gacchaṃ, bhonto gacchantā, bhonto gacchanto, gacchantaṃ, gacchante, gacchantena, gacchatā, gacchantehi, gacchantebhi, gacchantassa, gacchato, gacchantānaṃ, gacchataṃ, gacchantasmā, gacchantamhā, gacchantā, gacchatā, gacchantehi, gacchantebhi, gacchantassa, gacchato, gacchantānaṃ, gacchataṃ, gacchantasmiṃ, gacchantamhi, gacchati, gacchante, gacchantesu.</p>\r\n<p class="bodytext">evaṃ karaṃ, kubbaṃ, caraṃ, cavaṃ, jayaṃ, jahaṃ, jānaṃ, jiraṃ, dadaṃ, dahaṃ, juhaṃ, suṇaṃ, pacaṃ, saraṃ, bhuñjaṃ, muñcaṃ, sayaṃ, saraṃ, haraṃ, tiṭṭhaṃ, bhavissaṃ, karissaṃ, gamissaṃ iccādi.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext">‘ntassa ca ṭa vaṃse’ti aṃ, sesu ntassa ṭattaṃ, yaṃ yañhi rāja bhajati, santaṃ vā yadi vā asaṃ. sīlavantaṃ visīlaṃ vā, vasaṃ tasseva gacchati <span class="note">[jā. 1.15.181]</span>. kiccānukrubbassa kareyya kiccaṃ <span class="note">[jā. 1.2.145]</span> – anukrubbassāti puna karontassa.</p>\r\n<p class="bodytext">mahāvuttinā paṭhamāyomhi ca savibhattissa ntassa aṃ hoti, api nu tumhe ekantasukhaṃ lokaṃ jānaṃ passaṃ viharatha <span class="note">[dī. ni. 1.425]</span>, kasaṃ khettaṃ bījaṃ vapaṃ, dhanaṃ vindanti māṇavā <span class="note">[therīgā. 112]</span>, bharanti mātāpitaro, pubbe katamanussaraṃ <span class="note">[a. ni. 5.39]</span>.</p>\r\n<p class="bodytext"><a name="para128"></a><span class="paranum">128</span>. <span class="bld">mahantārahantānaṃ ṭā vā</span> <span class="note">[nī. 387, 712]</span>.</p>\r\n<p class="bodytext">simhi etesaṃ ntassa ṭā hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-128-');
INSERT INTO pages VALUES(54493,'annya_sadda_10',86,replace(replace('\r\n<p class="bodytext">mahā<a name="M0.0086"></a>, mahaṃ, mahanto, mahantā, mahanto, bho maha, bho mahā, bho mahaṃ, bhonto mahantā, bhonto mahanto, mahantaṃ.</p>\r\n<p class="bodytext">‘ntassa ca ṭa vaṃse’ti aṃmhi ntassa ṭattaṃ, "sumahaṃ puraṃ, parikkhipissa"nti <span class="note">[jā. 2.22.792]</span> pāḷi-suṭṭhu mahantaṃ bārāṇasipuranti attho. sesaṃ gacchantasamaṃ.</p>\r\n<p class="bodytext">arahā tiṭṭhati. ‘ntassaṃ simhī’ti simhi ntassa aṃ, arahaṃ sugato loke <span class="note">[saṃ. ni. 1.161]</span>, arahaṃ sammāsambuddhā <span class="note">[pārā. 1]</span>, arahantā, arahanto, arahantaṃ, arahante, arahantena, arahatā, arahantehi, arahantebhi, arahantassa, arahato, arahantānaṃ, arahataṃ iccādi.</p>\r\n<p class="bodytext">mahāvuttinā brahmantassa ca ntassa ṭā hoti simhi, brahā, brahanto, brahantā, brahanto, brahantaṃ, brahante iccādi.</p>\r\n<p class="bodytext">"sā parisā mahā hoti, sā senā dissate mahā"ti <span class="note">[jā. 2.22.771]</span> ca "mahā bhante bhūmicālo"ti <span class="note">[a. ni. 8.70]</span> ca "mahā te upāsaka pariccāgo"ti <span class="note">[jā. aṭṭha. 4.13.akittijātakavaṇṇanā]</span> ca "mahā me bhayamāgata"nti ca "bārāṇasirajjaṃ nāma mahā"ti <span class="note">[jā. aṭṭha. 1.1.mahāsīlavajātakavaṇṇanā]</span> ca "mahāssa honti parivārā brāhmaṇagahapatikā, mahāssa honti parivārā bhikkhū bhikkhuniyo"ti <span class="note">[dī. ni. 3.204]</span> ca "mahā vahanti dudiṭṭhiṃ, saṅkappā rāganissitā"ti ca pāḷī. atra mahāsaddo nipātapaṭirūpakopi siyā.</p>\r\n<p class="bodytext"><a name="para129"></a><span class="paranum">129</span>. <span class="bld">bhūto</span>.</p>\r\n<p class="bodytext">bhūdhātusiddhato ntassa aṃ hoti simhi. suddhe niccaṃ, upapade aniccaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-129-');
INSERT INTO pages VALUES(54494,'annya_sadda_10',87,replace(replace('\r\n<p class="bodytext">bhavaṃ <a name="M0.0087"></a> tiṭṭhati, sampattiṃ anubhavaṃ, anubhavanto, taṇhaṃ abhibhavaṃ, abhibhavanto, dukkhaṃ paribhavaṃ, paribhavanto tiṭṭhati, bhavantā, bhavanto, he bhavanta, he bhavantā, he bhavanto, he bhava, he bhavā, he bhavaṃ. "kacci bhavaṃ abhiramasi araññe"ti <span class="note">[jā. 2.18.18]</span> pāḷi.</p>\r\n<p class="bodytext">he bhavantā, he bhavanto, bhavantaṃ, bhavante, bhavantena, bhavatā, bhavantehi, bhavantebhi, bhavantassa, bhavato, bhavantānaṃ, bhavataṃ, bhavantasmā, bhavantamhā, bhavantā, bhavatā, bhavantehi, bhavantebhi, bhavantassa, bhavato, bhavantānaṃ, bhavataṃ, bhavantasmiṃ, bhavantamhi, bhavati, bhavante, bhavantesu.</p>\r\n<p class="bodytext"><a name="para130"></a><span class="paranum">130</span>. <span class="bld">bhavato vā bhonto gayonāse</span> <span class="note">[ka. 243; rū. 8, 110; nī. 484]</span>.</p>\r\n<p class="bodytext">ga, yo, nā, sesu bhavantassa bhonto hoti vā. suttavibhattena aṃ, hi, naṃ, smādīsu ca.</p>\r\n<p class="bodytext">bhontā, bhonto, he bhonta, he bhontā, he bhonto, bhontaṃ, bhonte, bhontena, bhotā, bhontehi, bhontebhi, bhontassa, bhoto, bhontānaṃ, bhotaṃ, bhontasmā, bhontamhā, bhontā, bhotā, bhontehi, bhontebhi, bhontassa, bhoto, bhontānaṃ, bhotaṃ, bhontasmiṃ, bhontamhi, bhoti, bhonte, bhontesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-130-');
INSERT INTO pages VALUES(54495,'annya_sadda_10',88,replace(replace('\r\n<p class="bodytext">bho, bhanteti dve vuddhiatthe siddhā āmantanatthe nipātā eva, tehi paraṃ ga, yonaṃ lopo, ito bho sugatiṃ gaccha <span class="note">[itivu. 83]</span>, ummujjabho puthusile, kuto nu āgacchatha bho tayo janā <span class="note">[jā. 1.9.87]</span>, passatha bho imaṃ kulaputtaṃ, ehi bhante khamāpehi, so te bhikkhū khamāpesi "khamatha bhante"ti. tathā bhaddante, bhaddantāti dve "tuyhaṃ bhaddaṃ hotu<a name="M0.0088"></a>, tumhākaṃ bhaddaṃ hotū"ti atthe siddhā āmantananipātāva, "bhaddante"ti te bhikkhū bhagavato paccassosuṃ <span class="note">[saṃ. ni. 1.249]</span>, taṃ vo vadāmi bhaddante, yāvantettha samāgatā <span class="note">[jā. 1.7.108]</span>. bhaddanta, bhadantasaddā pana purisādigaṇikā eva.</p>\r\n<p class="bodytext">santasaddo pana sappurise vijjamāne samāne ca pavatto idha labbhati. sameti asatā asaṃ <span class="note">[jā. 1.2.16]</span>. saṃ, santo, santā, santo, bhosanta, bhosantā, bhosa, bho sā, bho saṃ vā, bhonto santā, bhonto santo. yaṃ yañhi rāja bhajati, santaṃ vā yadi vā asaṃ <span class="note">[jā. 1.15.180]</span>. sante, santena, satā.</p>\r\n<p class="bodytext"><a name="para131"></a><span class="paranum">131</span>. <span class="bld">sato sabba bhe</span> <span class="note">[ka. 185; rū. 112; nī. 378]</span>.</p>\r\n<p class="bodytext">bhe pare santassa sabaādeso hoti vā.</p>\r\n<p class="bodytext">santehi, santebhi, sabbhi, santassa, sato, santānaṃ, sataṃ, santasmā, santamhā, santā, satā, santehi, santebhi, sabbhi, santassa, sato, santānaṃ, sataṃ, santasmiṃ, santamhi, sati, sante, santesu. santo sappurisā loke, dūresanto pakāsenti <span class="note">[dha. pa. 304]</span>, cattāro puggalā santo saṃvijjamānā lokasmiṃ <span class="note">[a. ni. 4.85]</span>, pahusanto na bharati <span class="note">[su. ni. 91]</span>.</p>\r\n<p class="bodytext">khede nirodhe ca pavatto santo purisādigaṇādiko, dīghaṃ santassa yojanaṃ <span class="note">[dha. pa. 61]</span>, santā honti samitā niruddhā iccādi.</p>\r\n<p class="centered">iti gacchantādigaṇarāsi.</p>\r\n<p class="subhead">rājādiyuvādigaṇarāsi</p>\r\n<p class="bodytext"><a name="para132"></a><span class="paranum">132</span>. <span class="bld">rājādiyuvādīhā</span> <span class="note">[ka. 189; rū. 113; nī. 390-1]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-131-132-');
INSERT INTO pages VALUES(54496,'annya_sadda_10',89,replace(replace('\r\n<p class="bodytext">rājādīhi <a name="M0.0089"></a> yuvādīhi ca sissa ā hoti.</p>\r\n<p class="bodytext">rājā gacchati.</p>\r\n<p class="bodytext"><a name="para133"></a><span class="paranum">133</span>. <span class="bld">yonamāno</span> <span class="note">[ka. 190; rū. 114; nī. 392]</span>.</p>\r\n<p class="bodytext">rājādīhi yuvādīhi ca yonaṃ āno hoti vā.</p>\r\n<p class="bodytext">rājāno.</p>\r\n<p class="bodytext">vāti kiṃ? caturo ca mahārājā.</p>\r\n<p class="bodytext">bho rāja, bho rājā, bhonto rājāno.</p>\r\n<p class="bodytext"><a name="para134"></a><span class="paranum">134</span>. <span class="bld">vaṃmhānaṅa</span> <span class="note">[ka. 188; rū. 115; nī. 393]</span>.</p>\r\n<p class="bodytext">rājādīnaṃ yuvādīnañca ānaṅa hoti vā aṃmhi.</p>\r\n<p class="bodytext">rājānaṃ, rājaṃ, rājāno, caturo ca mahārāje <span class="note">[pe. va. 11]</span>.</p>\r\n<p class="bodytext"><a name="para135"></a><span class="paranum">135</span>. <span class="bld">nāsmāsu raññā</span> <span class="note">[ka. 137, 270; rū. 116, 120; nī. 316, 542]</span>.</p>\r\n<p class="bodytext">nā, smāsu rājassa raññā hoti vā.</p>\r\n<p class="bodytext">raññā, rājena.</p>\r\n<p class="bodytext"><a name="para136"></a><span class="paranum">136</span>. <span class="bld">rājassi nāmhi</span> <span class="note">[nī. 316]</span>.</p>\r\n<p class="bodytext">nāmhi rājassa i hoti.</p>\r\n<p class="bodytext">rājinā.</p>\r\n<p class="bodytext"><a name="para137"></a><span class="paranum">137</span>. <span class="bld">sunaṃhisvu</span> <span class="note">[ka. 169; rū. 117; nī. 357]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-133-134-135-136-137-');
INSERT INTO pages VALUES(54497,'annya_sadda_10',90,replace(replace('\r\n<p class="bodytext">su<a name="M0.0090"></a>, naṃ, hisu rājassa uhoti vā.</p>\r\n<p class="bodytext">rājūhi, rājūbhi, rājehi, rājebhi.</p>\r\n<p class="bodytext"><a name="para138"></a><span class="paranum">138</span>. <span class="bld">raññoraññassarājino se</span> <span class="note">[ka. 139; rū. 118; nī. 314]</span>.</p>\r\n<p class="bodytext">separe savibhattissa rājassa rañño, raññassa, rājino honti vā.</p>\r\n<p class="bodytext">rañño, raññassa, rājino.</p>\r\n<p class="bodytext">vāti kiṃ? rājassa.</p>\r\n<p class="bodytext">rājūnaṃ, rājānaṃ.</p>\r\n<p class="bodytext"><a name="para139"></a><span class="paranum">139</span>. <span class="bld">rājassa raññaṃ</span> <span class="note">[ka. 136; rū. 119; nī. 315]</span>.</p>\r\n<p class="bodytext">naṃmhi rājassa raññaṃ hoti vā.</p>\r\n<p class="bodytext">raññaṃ, rājasmā, rājamhā, raññā, rājūhi, rājūbhi, rājehi, rājebhi, rañño, raññassa, rājino, rājassa vā, rājūnaṃ, rājānaṃ, raññaṃ.</p>\r\n<p class="bodytext"><a name="para140"></a><span class="paranum">140</span>. <span class="bld">smiṃmhi raññerājini</span> <span class="note">[ka. 138; rū. 121; nī. 317]</span>.</p>\r\n<p class="bodytext">smiṃmhi savibhattissa rājassa raññe, rājini honti vā.</p>\r\n<p class="bodytext">raññe, rājini, rājasmiṃ, rājamhi, rājūsu, rājesu.</p>\r\n<p class="bodytext"><a name="para141"></a><span class="paranum">141</span>. <span class="bld">samāse vā</span>.</p>\r\n<p class="bodytext">samāsaṭṭhāne sabbe te ādesā vikappena honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-138-139-140-141-');
INSERT INTO pages VALUES(54498,'annya_sadda_10',91,replace(replace('\r\n<p class="bodytext">cattāro mahārājā <span class="note">[dī. ni. 2.336]</span>, cattāro mahārājāno <span class="note">[a. ni. 3.37]</span>, devarājānaṃ, devarājaṃ, devarājāno, devarāje, cattāro ca mahārāje<a name="M0.0091"></a>, maṇimhi passa nimmitaṃ <span class="note">[jā. 2.22.1394]</span>, kāsiraññā, kāsirājena, devarājūhi, devarājehi, kāsirañño, kāsirājassa, devarājūnaṃ, devarājānaṃ.pe.. kāsiraññe, kāsirāje, devarājūsu, devarājesu.</p>\r\n<p class="bodytext">mahāvuttinā rājato yonaṃ ino hoti, "samantapāsādikā nāma, soḷasāsiṃsu rājino, ekūnatiṃse kappamhi, ito soḷasarājino <span class="note">[apa. thera 1.12.54-55 (ekūnatiṃsakappamhi, ito soḷasarājāno)]</span>, kusarājaṃ mahabbalaṃ <span class="note">[jā. 2.20.67]</span>, sālarājaṃva pupphitaṃ <span class="note">[apa. thera 1.42.86]</span>, uḷurājaṃva sobhitaṃ, caturo ca mahārāje <span class="note">[pe. va. 11]</span>, yudhañcayo anuññāto, sabbadattena rājinā <span class="note">[jā. 1.11.81]</span>, tadā adāsi maṃ tāto, bimbisārassa rājino <span class="note">[apa. therī. 2.2.326]</span>, nikkhamante mahārāje, pathavī sampakampatha" iccādīni pāḷipadāni.</p>\r\n<p class="bodytext">brahmā, brahmāno, bho brahma, bho brahmā. ‘ghabrahmāditve’ti gassa ettaṃ, bho brahme, bhonto brahmāno, brahmānaṃ, brahmaṃ, brahmāno.</p>\r\n<p class="bodytext"><a name="para142"></a><span class="paranum">142</span>. <span class="bld">nāmhi</span> <span class="note">[ka. 198; rū. 123; nī. 410]</span>.</p>\r\n<p class="bodytext">nāmhi brahmassa uhoti vā.</p>\r\n<p class="bodytext">brahmunā, brahmena, brahmehi, brahmebhi.</p>\r\n<p class="bodytext"><a name="para143"></a><span class="paranum">143</span>. <span class="bld">brahmassu vā</span> <span class="note">[ka. 198; rū. 123; nī. 410]</span>.</p>\r\n<p class="bodytext">sa, naṃsu brahmassa u hoti vā.</p>\r\n<p class="bodytext"><a name="para144"></a><span class="paranum">144</span>. <span class="bld">jhalā sassa no</span> <span class="note">[ka. 117; rū. 124; nī. 292]</span>.</p>\r\n<p class="bodytext">jha, lato sassa no hoti.</p>\r\n<p class="bodytext">brahmuno, brahmassa, brahmūnaṃ, brahmānaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-142-143-144-');
INSERT INTO pages VALUES(54499,'annya_sadda_10',92,replace(replace('\r\n<p class="bodytext"><a name="para145"></a><span class="paranum">145</span>. <span class="bld">smā</span> <a name="M0.0092"></a> <span class="bld">nāva brahmā ca</span> <span class="note">[ka. 270; rū. 120; nī. 542]</span>.</p>\r\n<p class="bodytext">attā’tumehi ca brahmato ca smāssa nā viya rūpaṃ hoti.</p>\r\n<p class="bodytext">brahmunā, brahmasmā, brahmamhā, brahmuno, brahmassa, brahmūnaṃ, brahmānaṃ. ‘kammādito’ti suttena smiṃno ni hoti, brahmasmiṃ, brahmamhi, brahmani, brahme, brahmesu.</p>\r\n<p class="bodytext">attā, attāno, bho atta, bho attā, bhonto attāno, attānaṃ, attaṃ, attāno. ‘nāsseno’ti vikappena nāssa enattaṃ, attanā, attena.</p>\r\n<p class="bodytext"><a name="para146"></a><span class="paranum">146</span>. <span class="bld">suhisvanaka</span> <span class="note">[ka. 211; rū. 126; nī. 439;. suhisunaka (bahūsu)]</span>.</p>\r\n<p class="bodytext">su, hisu attā’tumānaṃ anto anaka hoti.</p>\r\n<p class="bodytext">attanehi, attanebhi, attehi, attebhi.</p>\r\n<p class="bodytext"><a name="para147"></a><span class="paranum">147</span>. <span class="bld">nottātumā</span> <span class="note">[ka. 213; rū. 127; nī. 440]</span>.</p>\r\n<p class="bodytext">attā’tumato sassa no hoti.</p>\r\n<p class="bodytext">attano, attassa, attānaṃ, attasmā, attamhā, attā, attanā, attanehi, attanebhi, attehi, attebhi, attano, attassa, attānaṃ, attasmiṃ, attamhi, attani, atte, attesu, attanesu.</p>\r\n<p class="bodytext">samāse pana purisādirūpaṃ hoti, pahito attā etenāti pahitatto, pahitattā, pahitattaṃ, pahitatte, pahitattena, pahitattehi, pahitattebhi, pahitattassa, pahitattānaṃ, pahitattasmā, pahitattamhā, pahitattā, pahitattehi, pahitattebhi, pahitattassa, pahitattānaṃ, pahitattasmiṃ, pahitattamhi, pahitatte, pahitattesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-145-146-147-');
INSERT INTO pages VALUES(54500,'annya_sadda_10',93,replace(replace('\r\n<p class="bodytext">ātumā<a name="M0.0093"></a>, ātumāno, ātumānaṃ, ātumaṃ, ātumāno, ātumanā, ātumena, ātumanehi, ātumanebhi, ātumano, ātumassa, ātumānaṃ iccādi.</p>\r\n<p class="bodytext">sakhā tiṭṭhati.</p>\r\n<p class="bodytext"><a name="para148"></a><span class="paranum">148</span>. <span class="bld">āyo no ca sakhā</span> <span class="note">[ka. 191; rū. 130; nī. 394]</span>.</p>\r\n<p class="bodytext">sakhato yonaṃ āyo ca no ca honti vā āno ca.</p>\r\n<p class="bodytext">sakhāno, sakhāyo.</p>\r\n<p class="bodytext"><a name="para149"></a><span class="paranum">149</span>. <span class="bld">nonāsesvi</span> <span class="note">[ka. 194; rū. 131; nī. 407]</span>.</p>\r\n<p class="bodytext">no, nā, sesu sakhantassa i hoti vā.</p>\r\n<p class="bodytext">sakhino.</p>\r\n<p class="bodytext">suttavibhattena ttapaccayamhi ittaṃ, "sakhittaṃ kareyya, sakhittaṃ na kareyyā"ti <span class="note">[theragā. 1017 (sakhitaṃ)]</span> pāḷī.</p>\r\n<p class="bodytext"><a name="para150"></a><span class="paranum">150</span>. <span class="bld">yosvaṃhismānaṃsvāraṅa</span> <span class="note">[ka. 195-6; rū. 133-4; nī. 408-9; yosvaṃhisucāraṅa (bahūsu)]</span>.</p>\r\n<p class="bodytext">yosu aṃ, hi, smā, naṃsu sakhantassa āraṅa hoti. ‘ṭoṭe vā’ti suttena ārādesato yonaṃ kamena ṭo, ṭe honti.</p>\r\n<p class="bodytext">sakhāro tiṭṭhanti. ‘ghabrahmāditve’ti gassa vikappena ettaṃ, bho sakha, bho sakhā, bho sakhe, hare sakhā kissa maṃ jahāsi <span class="note">[jā. 1.6.94]</span>.</p>\r\n<p class="bodytext">"sakhi, sakhīti dvayaṃ itthiyaṃ siddha"nti vuttiyaṃ vuttaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-148-149-150-');
INSERT INTO pages VALUES(54501,'annya_sadda_10',94,replace(replace('\r\n<p class="bodytext">bhonto <a name="M0.0094"></a> sakhāno, bhonto sakhāyo, bhonto sakhino, bhonto sakhāro, sakhānaṃ, sakhāraṃ, sakhaṃ, sakhāno, sakhāyo, sakhino, sakhāre, sakhāro, sakhinā, sakhārena, sakhena, sakhārehi, sakhārebhi, sakhehi, sakhebhi, sakhissa, sakhino, sakhārānaṃ, sakhānaṃ.</p>\r\n<p class="bodytext"><a name="para151"></a><span class="paranum">151</span>. <span class="bld">smānaṃsu vā</span> <span class="note">[ka. 194, 170; rū. 120, 131; nī. 407, 542]</span>.</p>\r\n<p class="bodytext">smā, naṃsu sakhantassa i hoti vā.</p>\r\n<p class="bodytext">sakhīnaṃ, sakhismā, sakhimhā, sakhā, sakhinā, sakhārasmā, sakhāramhā, sakhārā, sakhārehi, sakhārebhi, sakhehi, sakhebhi, sakhissa, sakhino, sakhārānaṃ, sakhānaṃ, sakhīnaṃ.</p>\r\n<p class="bodytext"><a name="para152"></a><span class="paranum">152</span>. <span class="bld">ṭe smiṃno</span> <span class="note">[ka. 192; rū. 135]</span>.</p>\r\n<p class="bodytext">sakhato smiṃno ṭe hoti. niccatthamidaṃ suttaṃ.</p>\r\n<p class="bodytext">sakhe, sakhāresu, sakhesu. "netādisā sakhā honti, labbhā me jīvato sakhā"ti <span class="note">[jā. 1.7.9]</span> pāḷi. purisādinayena yonaṃ vidhi.</p>\r\n<p class="bodytext">samāse pana sabbaṃ purisādirūpaṃ labbhati, "sabbamitto sabbasakho, pāpamitto pāpasakho"ti <span class="note">[dī. ni. 3.253]</span> ca pāḷi. pāpasakho, pāpasakhā, pāpasakhaṃ, pāpasakhe, pāpasakhena, pāpasakhehi, pāpasakhebhi.pe.. pāpasakhasmiṃ, pāpasakhamhi, pāpasakhe, pāpasakhesu.</p>\r\n<p class="bodytext">yuvā gacchati.</p>\r\n','\r',char(13)),'\n',char(10)),'-151-152-');
INSERT INTO pages VALUES(54502,'annya_sadda_10',95,replace(replace('\r\n<p class="bodytext"><a name="para153"></a><span class="paranum">153</span>. <span class="bld">yonaṃ</span> <a name="M0.0095"></a> <span class="bld">none vā</span> <span class="note">[ka. 155, 157; rū. 137, 140; nī. 335, 343]</span>.</p>\r\n<p class="bodytext">yuva, pumādīhi paṭhamā, dutiyāyonaṃ kamena no, ne honti vā.</p>\r\n<p class="bodytext"><a name="para154"></a><span class="paranum">154</span>. <span class="bld">nonānesvā</span> <span class="note">[ka. 157; rū. 140; nī. 343]</span>.</p>\r\n<p class="bodytext">no, nā, nesu yuvādīnaṃ anto ā hoti vā.</p>\r\n<p class="bodytext">yuvāno, yuvānā, yuvā, he yuva, he yuvā, he yuvāno, he yuvā vā, yuvānaṃ, yuvaṃ, yuvāne, yuve, yuvena, yuvānā.</p>\r\n<p class="bodytext"><a name="para155"></a><span class="paranum">155</span>. <span class="bld">yuvādīnaṃ suhisvānaṅa</span> <span class="note">[ka. 157; rū. 140; nī. 337-9, 343]</span>.</p>\r\n<p class="bodytext">yuva, pumādīnaṃ anto ānaṅa hoti vā su, hisu.</p>\r\n<p class="bodytext">yuvānehi, yuvehi, yuvānebhi, yuvebhi, yuvassa.</p>\r\n<p class="bodytext"><a name="para156"></a><span class="paranum">156</span>. <span class="bld">yuvā sassino</span>.</p>\r\n<p class="bodytext">yuvato sassa ino hoti vā.</p>\r\n<p class="bodytext">yuvino, yuvānaṃ, yuvasmā, yuvamhā.</p>\r\n<p class="bodytext"><a name="para157"></a><span class="paranum">157</span>. <span class="bld">smāsmiṃnaṃ nāne</span> <span class="note">[ka. 156-7-8; rū. 140-2-3]</span>.</p>\r\n<p class="bodytext">yuva, pumādīhi smā, smiṃnaṃ nā, ne honti vā. ‘nonānesvā’ti nāmhi āttaṃ.</p>\r\n<p class="bodytext">yuvānā, yuvānehi, yuvānebhi, yuvehi, yuvebhi, yuvassa, yuvino, yuvānaṃ, yuvasmiṃ, yuvamhi, yuve, yuvāne, yuvānesu, yuvesu.</p>\r\n<p class="bodytext">rūpasiddhiyaṃ pana "maghava, yuvādīnamantassa ānādeso hoti vā sabbāsu vibhattīsū"ti <span class="note">[rū. 140; nī. 343]</span> vuttaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-153-154-155-156-157-');
INSERT INTO pages VALUES(54503,'annya_sadda_10',96,replace(replace('\r\n<p class="bodytext">pumā<a name="M0.0096"></a>, pumāno, he puma, he pumā.</p>\r\n<p class="bodytext"><a name="para158"></a><span class="paranum">158</span>. <span class="bld">gassaṃ</span> <span class="note">[ka. 153; rū. 138; nī. 333]</span>.</p>\r\n<p class="bodytext">pumato gassa aṃ hoti vā.</p>\r\n<p class="bodytext">he pumaṃ, he pumāno, pumānaṃ, pumaṃ, pumāne, pume.</p>\r\n<p class="bodytext"><a name="para159"></a><span class="paranum">159</span>. <span class="bld">nāmhi</span> <span class="note">[ka. 159; rū. 139; nī. 340]</span>.</p>\r\n<p class="bodytext">nāmhi pumantassa ā hoti vā.</p>\r\n<p class="bodytext">pumānā, pumena.</p>\r\n<p class="bodytext"><a name="para160"></a><span class="paranum">160</span>. <span class="bld">pumakammathāmaddhānaṃ vā sasmāsu ca</span> <span class="note">[ka. 157, 159; rū. 139, 140; nī. 338, 140]</span>.</p>\r\n<p class="bodytext">nāmhi ca sa, smāsu ca puma, kamma, thāmaddhānaṃ anto u hoti vā.</p>\r\n<p class="bodytext">pumunā, pumānehi, pumānebhi, pumehi, pumebhi, pumassa, pumuno, pumānaṃ, pumasmā, pumamhā, pumānā, pumunā, pumānehi, pumānebhi, pumehi, pumebhi, pumuno, pumassa, pumānaṃ, pumasmiṃ, pumamhi, pume.</p>\r\n<p class="bodytext"><a name="para161"></a><span class="paranum">161</span>. <span class="bld">pumā</span> <span class="note">[ka. 156; rū. 142; nī. 336]</span>.</p>\r\n<p class="bodytext">pumato smiṃno ne hoti vā. ‘nonānesvā’ti pumantassa āttaṃ.</p>\r\n<p class="bodytext">pumāne.</p>\r\n<p class="bodytext"><a name="para162"></a><span class="paranum">162</span>. <span class="bld">sumhā ca</span> <span class="note">[ka. 158; rū. 143; nī. 339]</span>.</p>\r\n<p class="bodytext">sumhi pumantassa ā ca hoti āne ca.</p>\r\n<p class="bodytext">pumānesu, pumāsu, pumesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-158-159-160-161-162-');
INSERT INTO pages VALUES(54504,'annya_sadda_10',97,replace(replace('\r\n<p class="bodytext">si<a name="M0.0097"></a>, yonaṃ purisādividhi ca hoti, "yathā balākayonimhi, na vijjati pumo sadā <span class="note">[apa. thera 1.1.511]</span>, soḷasitthisahassānaṃ, na vijjati pumo tadā <span class="note">[cariyā 3.49]</span>, itthī hutvā svajja pumomhi devo <span class="note">[dī. ni. 2.354]</span>, thiyo tassa pajāyanti, na pumā jāyare kule"ti <span class="note">[jā. 1.8.54]</span> pāḷī.</p>\r\n<p class="bodytext">maghavasaddo yuvasaddasadisoti rūpasiddhiyaṃ <span class="note">[rū. 66]</span> vuttaṃ, guṇavādigaṇikoti saddanītiyaṃ <span class="note">[nī. pada. 220]</span> icchito. aghanti dukkhaṃ pāpañca vuccati, na aghaṃ maghaṃ, sukhaṃ puññañca, magho iti purāṇaṃ nāmaṃ assa atthīti maghavāti <span class="note">[saṃ. ni. 1.259]</span> attho pāḷiyaṃ dissati.</p>\r\n<p class="bodytext">thāmasaddo purisādigaṇo, thāmena, thāmunā, thāmassa, thāmuno, thāmasmā, thāmamhā, thāmā, thāmunā, thāmassa, thāmuno. sesaṃ purisasamaṃ.</p>\r\n<p class="bodytext">addhā vuccati kālo. nādyekavacanesu-dīghena addhunā, addhanā, addhena, dīghassa addhuno, addhussa, addhassa, addhunā, addhumhā, addhusmā, addhā, addhamhā, addhasmā, addhuno, addhussa, addhassa, addhani, addhe, addhamhi, addhasminti cūḷamoggallāne āgataṃ. sesaṃ yuvasadisaṃ.</p>\r\n<p class="bodytext">upaddhavācako addhasaddo idha na labbhati, ekaṃsatthavācako ca nipāto eva. "addhānamaggappaṭipanno"tiādīsu addhānasaddo pana visuṃ siddhā napuṃsakaliṅgova.</p>\r\n<p class="bodytext">muddhasadde "muddhā te phalatu sattadhā, muddhā me phalatu sattadhā" iccādīsu <span class="note">[jā. 1.16.295; dha. pa. aṭṭha. 1.tissattheravatthu]</span> siro vuccati, "pabbatamuddhaniṭṭhito" iccādīsu <span class="note">[dī. ni. 2.70]</span> matthakaṃ vuccati, tadubhayaṃ idha labbhati, smiṃvacane muddhanīti siddhaṃ, sesaṃ yuvasamaṃ. bālavācako pana purisanayo. hatthamuṭṭhivācako itthiliṅgo.</p>\r\n<p class="bodytext">asmā vuccati pāsāṇo, usmā vuccati kāyaggi, bhismā vuccati bhayānako mahākāyo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54505,'annya_sadda_10',98,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0098"></a> asmasadde "taṃ te paññāya bhindāmi, āmaṃ pakkaṃva asmanā <span class="note">[su. ni. 445]</span>, mā tvaṃ cande khali asmanī"ti pāḷī. sesaṃ yuvasamaṃ. usmā, bhismāsaddāpi yuvasadisāti vadanti.</p>\r\n<p class="bodytext">cūḷamoggallāne muddha, gāṇḍīvadhanva, aṇima, laghimādayo ca asmasadisāti vuttaṃ.</p>\r\n<p class="bodytext">yattha suttavidhānaṃ na dissati, tattha mahāvuttinā vā suttavibhattena vā rūpaṃ vidhiyati.</p>\r\n<p class="centered">iti rājādiyuvādigaṇarāsi.</p>\r\n<p class="centered">akārantapulliṅgaṃ niṭṭhitaṃ.</p>\r\n<p class="subhead">ākārantapulliṅgarāsi</p>\r\n<p class="bodytext">‘gasīna’nti silopo, sā tiṭṭhati.</p>\r\n<p class="bodytext">‘ekavacanayosvaghona’nti yosu ca ekavacanesu ca rasso, ‘ato yona’miccādinā vidhānaṃ, sā tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para163"></a><span class="paranum">163</span>. <span class="bld">sāssaṃse cānaṅa</span>.</p>\r\n<p class="bodytext">aṃ, sesu ge ca sāsaddassa ānaṅa hoti.</p>\r\n<p class="bodytext">bho sāna, bhonto sā, saṃ, sānaṃ, se, sena, sāhi, sābhi, sassa, sānassa, sānaṃ, sasmā, samhā, sā, sāhi, sābhi, sassa, sānassa, sānaṃ, sasmiṃ, samhi, se, sāsu.</p>\r\n<p class="bodytext">atha vā ‘sāssaṃse cānaṅa’iti sutte casaddo avuttasamuccayatthopi hotītikatvā sito sesāsu vibhattīsupi ānaṅa hoti vā, mahāvuttinā ca ānādesato yonaṃ o.</p>\r\n','\r',char(13)),'\n',char(10)),'-163-');
INSERT INTO pages VALUES(54506,'annya_sadda_10',99,replace(replace('\r\n<p class="bodytext">sā <a name="M0.0099"></a> gacchati, sāno gacchanti, sā vā, he sa, he sā, he sāna, he sā, he sāno, saṃ, sānaṃ, se, sāne iccādi.</p>\r\n<p class="bodytext">saddanītirūpaṃ vuccate –</p>\r\n<p class="bodytext">sā tiṭṭhati, sā tiṭṭhanti, sāno tiṭṭhanti, bho sā, bhonto sā, sāno, sānaṃ, sāne, sānā, sānehi, sānebhi, sāssa, sānaṃ, sānā, sānehi, sānebhi, sāssa, sānaṃ, sāne, sānesūti <span class="note">[nīti. pada. 211]</span>.</p>\r\n<p class="bodytext">vattahā vuccati satto <span class="note">[sakko (amarakosa, 1-145 gāthāyaṃ)]</span>.</p>\r\n<p class="bodytext"><a name="para164"></a><span class="paranum">164</span>. <span class="bld">vattahā sanaṃnaṃ nonānaṃ</span>.</p>\r\n<p class="bodytext">vattahato sassa no hoti, naṃvacanassa nānaṃ hoti.</p>\r\n<p class="bodytext">vattahāno deti, vattahānānaṃ deti. sesaṃ yuvasaddasamaṃ.</p>\r\n<p class="bodytext">saddanītiyaṃ pana nā, sesu vattahinā, vattahinoti <span class="note">[nīti. pada. 219; (tattha nāmhi vattahānāti dissati)]</span> vuttaṃ.</p>\r\n<p class="bodytext">daḷhadhammā, daḷhadhammā, daḷhadhammāno. "sikkhitā daḷhadhammino"tipi <span class="note">[saṃ. ni. 1.209]</span> pāḷi. bho daḷhadhammā, bhonto daḷhadhammā, daḷhadhammāno, daḷhadhammino, daḷhadhammānaṃ, daḷhadhammāne, daḷhadhamminā, daḷhadhammehi. sesaṃ purisasamaṃ. evaṃ paccakkhadhammāti. vivaṭacchadasadde pana nāmhi ittaṃ natthi, sesaṃ daḷhadhammasamaṃ. pāḷiyaṃ pana "daḷhadhammoti vissuto"ti <span class="note">[jā. 2.22.300]</span> ca "loke vivaṭacchado"ti <span class="note">[dī. ni. 1.258]</span> ca diṭṭhattā ete saddā purisarūpā akārantāpi yujjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-164-');
INSERT INTO pages VALUES(54507,'annya_sadda_10',100,replace(replace('\r\n<p class="bodytext">vuttasirā vuccati navavoropitakeso, vuttasirā brāhmaṇo, vuttasirā, vuttasirāno, vuttasirānaṃ, vuttasirāne, vuttasirānā, <a name="M0.0100"></a> vuttasirānehi. sesaṃ purisasamaṃ. pāḷiyaṃ pana "kāpaṭiko māṇavo vuttasiro"tipi <span class="note">[ma. ni. 2.426]</span> dissati.</p>\r\n<p class="bodytext">rahā vuccati pāpadhammo. rahā, rahā, rahino, rahānaṃ, rahine, rahinā, rahinehi, rahinebhi, rahassa, rahino, rahānaṃ.pe.. rahāne, rahānesūti <span class="note">[nīti. pada. 217]</span> sabbaṃ saddanītiyaṃ vuttaṃ, idha pana mahāvuttinā siddhaṃ.</p>\r\n<p class="centered">iti ākārantapulliṅgarāsi.</p>\r\n<p class="subhead">ikārantapulliṅgarāsi</p>\r\n<p class="bodytext">‘gasīna’nti lopo, muni gacchati.</p>\r\n<p class="bodytext"><a name="para165"></a><span class="paranum">165</span>. <span class="bld">lopo</span> <span class="note">[ka. 118; rū. 146; nī. 293]</span>.</p>\r\n<p class="bodytext">jha, lato yonaṃ lopo hoti. ‘yolopanīsu dīgho’ti dīgho.</p>\r\n<p class="bodytext">munī gacchanti.</p>\r\n<p class="bodytext"><a name="para166"></a><span class="paranum">166</span>. <span class="bld">yosu jhissa pume</span> <span class="note">[ka. 96; rū. 148; nī. 259]</span>.</p>\r\n<p class="bodytext">pulliṅge yosu jhasaññassa i-kārassa ṭa hoti vā.</p>\r\n<p class="bodytext">munayo gacchanti.</p>\r\n<p class="bodytext">jhissāti kiṃ? rattiyo, daṇḍino.</p>\r\n<p class="bodytext">pumeti kiṃ? aṭṭhīni.</p>\r\n<p class="bodytext">bho muni, ‘ayunaṃ vā dīgho’ti dīgho, bho munī, bhonto munī, bhonto munayo, muniṃ, munī, munayo, muninā, munīhi, munībhi, munissa, munino, munīnaṃ, munismā, munimhā.</p>\r\n','\r',char(13)),'\n',char(10)),'-165-166-');
INSERT INTO pages VALUES(54508,'annya_sadda_10',101,replace(replace('\r\n<p class="bodytext"><a name="para167"></a><span class="paranum">167</span>. <span class="bld">nā</span> <a name="M0.0101"></a> <span class="bld">smāssa</span> <span class="note">[ka. 215; rū. 41; nī. 442]</span>.</p>\r\n<p class="bodytext">jha, lato smāssa nā hoti vā.</p>\r\n<p class="bodytext">muninā, munīhi, munībhi, munissa, munino, munīnaṃ, munismiṃ, munimhi, munīsu.</p>\r\n<p class="bodytext">isi gacchati, isī, isayo, bho isi, bho isī, bhonto isī, bhonto isayo iccādi.</p>\r\n<p class="bodytext">aggi jalati, aggī, aggayo, bho aggi, bho aggī, bhonto aggī, bhonto aggayo iccādi.</p>\r\n<p class="bodytext">evaṃ kucchi, muṭṭhi, gaṇṭhi, maṇi, pati, adhipati, gahapati, senāpati, narapati, yati, ñāti, sāti, vatthi, atithi, sārathi, bondi, ādi, upādi, nidhi, vidhi, odhi, byādhi, samādhi, udadhi, upadhi, nirupadhi, dhani, senāni, kapi, dīpi, kimi, timi, ari, hari, giri, kali, bali, sāli, añjali, kavi, ravi, asi, masi, kesi, pesi, rāsi, ahi, vīhiiccādayo.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext">mahāvuttinā akatarassehipi kehici jhasaññehi yonaṃ no hoti, "cha munino agāramunino, anagāramunino, sekhamunino, asekhamunino, paccekamunino, munimunino"ti <span class="note">[mahāni. 14]</span> ca "ñāṇupapannā munino vadantī"ti ca "ekamekāya itthiyā, aṭṭhaṭṭha patino siyu"nti ca <span class="note">[jā. 2.21.344]</span> "patino kiramhākaṃ visiṭṭhanārīna" <span class="note">[vi. va. 323]</span> nti ca "haṃsādhipatino ime"ti <span class="note">[jā. 2.21.38]</span> ca suttapadāni dissanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-167-');
INSERT INTO pages VALUES(54509,'annya_sadda_10',102,replace(replace('\r\n<p class="bodytext">gāthāsu ‘ghabrahmāditve’ti munito gassa ettañca hoti, porohicco tavaṃ mune <span class="note">[apa. thera 1.1.540]</span>, dhammadasso tavaṃ mune <span class="note">[apa. thera 1.1.540]</span>, ciraṃ <a name="M0.0102"></a> jīva mahāvīra, kappaṃ tiṭṭha mahāmune <span class="note">[apa. thera 1.2.168]</span>, paṭiggaṇha mahāmune <span class="note">[apa. thera 1.41.83]</span>. tuyhatthāya mahāmuneti <span class="note">[apa. thera 1.3.345]</span>.</p>\r\n<p class="bodytext">tehiyeva aṃvacanassa nañca hoti, tamāhu ekaṃ muninaṃ carantaṃ <span class="note">[su. ni. 210]</span>, muninaṃ monapathesu sikkhamānaṃ <span class="note">[jā. 1.8.44]</span>, pitaraṃ puttagiddhinaṃ <span class="note">[jā. 2.22.2377]</span>, sabbakāmasamiddhinaṃ <span class="note">[jā. 1.13.103]</span>.</p>\r\n<p class="bodytext">isisadde pana –</p>\r\n<p class="bodytext"><a name="para168"></a><span class="paranum">168</span>. <span class="bld">ṭe sissisismā</span> <span class="note">[ṭe sissasmā (mūlapāṭhe)]</span>.</p>\r\n<p class="bodytext">isimhā sissa ṭe hoti vā.</p>\r\n<p class="bodytext">yo no’jja vinaye kaṅkhaṃ, atthadhammavidū ise <span class="note">[jā. 2.22.1164]</span>. ‘ghabrahmāditve’ti gassa ettañca hoti, nisīdāhi mahāise <span class="note">[jā. 2.20.114]</span>, tvaṃ no’si saraṇaṃ ise <span class="note">[jā. 2.22.1326]</span>, putto uppajjataṃ ise <span class="note">[jā. 1.14.104]</span>.</p>\r\n<p class="bodytext"><a name="para169"></a><span class="paranum">169</span>. <span class="bld">dutiyassa yossa</span>.</p>\r\n<p class="bodytext">isimhā dutiyassa yossa ṭe hoti vā.</p>\r\n<p class="bodytext">samaṇe brāhmaṇe vande, sampannacaraṇe ise <span class="note">[jā. 1.16.314]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-168-169-');
INSERT INTO pages VALUES(54510,'annya_sadda_10',103,replace(replace('\r\n<p class="bodytext">samāse pana mahesi gacchati, mahesī gacchanti, mahesayo, mahesino. aṃvacane mahesinanti sijjhati. "saṅgāyiṃsu mahesayo <span class="note">[vi. va. ganthārambhakathā pe. va. ganthārambhakathā]</span>, vānamuttā mahesayo"ti <span class="note">[abhidhammatthasaṅgahe 113 piṭṭhe]</span> ca "na taṃ sammaggatā yaññaṃ, upayanti mahesino, etaṃ sammaggatā yaññaṃ, upayanti mahesino <span class="note">[saṃ. ni. 1.120]</span>, pahantā mahesino kāme, yena tiṇṇā mahesino"ti ca "mahesiṃ vijitāvina"nti <span class="note">[ma. ni. 2.459]</span> ca "saṅghañcāpi mahesinaṃ, kuñjaraṃva mahesinaṃ, upagantvā mahesinaṃ <a name="M0.0103"></a> <span class="note">[bu. vaṃ. 9.1]</span>, khippaṃ passa mahesinaṃ <span class="note">[jā. 2.19.70]</span>, katakiccaṃ mahesina"nti <span class="note">[jā. 2.19.102]</span> ca suttapadāni dissanti.</p>\r\n<p class="bodytext">aggisadde –</p>\r\n<p class="bodytext"><a name="para170"></a><span class="paranum">170</span>. <span class="bld">sissaggito ni</span> <span class="note">[ka. 95; rū. 145; nī. 254; ‘sissāggito ni’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">aggito sissa ni hoti vā.</p>\r\n<p class="bodytext">aggi jalati, aggini jalati, aggī jalanti, aggayo iccādi.</p>\r\n<p class="bodytext">pāḷiyaṃ pana "aggi, gini, agginī"ti tayo aggipariyāyā dissanti – "rāgaggi, dosaggi, mohaggī"ti <span class="note">[a. ni. 7.46]</span> ca "channā kuṭi āhito gini, vivaṭā kuṭi nibbuto gini <span class="note">[su. ni. 19]</span>, mahāgini sampajjalito <span class="note">[theragā. 702 (thokaṃ visadisaṃ)]</span>, yasmā so jāyate ginī"ti <span class="note">[jā. 1.10.58]</span> ca "agginiṃ sampajjalitaṃ pavisantī"ti <span class="note">[su. ni. 675]</span> ca. tesaṃ visuṃ visuṃ rūpamālā labbhati.</p>\r\n<p class="bodytext">seṭṭhi, pati, adhipati, senāpati, atithi, sārathisaddehi ca yonaṃ no hoti, aṃvacanassa naṃ hoti vā, seṭṭhino, seṭṭhinaṃ, patino, patinaṃ, adhipatino, adhipatinaṃ, senāpatino, senāpatinaṃ, atithino, atithinaṃ, sārathino, sārathinanti. gahapatayo, jānipatayo iccādīni niccarūpāni dissanti.</p>\r\n<p class="bodytext">ādisadde –</p>\r\n<p class="bodytext">‘ratthyādīhi ṭo smiṃno’ti smiṃno ṭo hoti, ādismiṃ, ādimhi, ādo, gāthādo, pādādo. "ādiṃ, gāthādiṃ, pādādiṃ" iccādīsu pana ādhāratthe dutiyā eva "imaṃ rattiṃ, imaṃ divasaṃ, purimaṃ disaṃ, pacchimaṃ disaṃ, taṃ khaṇaṃ, taṃ layaṃ, taṃ muhuttaṃ" iccādīsu viya.</p>\r\n<p class="bodytext">idāni samāse jhissa ṭādesābhāvo vuccati.</p>\r\n','\r',char(13)),'\n',char(10)),'-170-');
INSERT INTO pages VALUES(54511,'annya_sadda_10',104,replace(replace('\r\n<p class="bodytext"><a name="para171"></a><span class="paranum">171</span>. <span class="bld">itoññatthe</span> <a name="M0.0104"></a> <span class="bld">pume</span>.</p>\r\n<p class="bodytext">pume aññapadatthasamāse i-kāramhā paṭhamā, dutiyāyonaṃ kamena no, ne honti vā. suttavibhattena uttarapadatthasamāsepi kvaci yonaṃ no, ne honti.</p>\r\n<p class="bodytext">paṭhamāyomhi –</p>\r\n<p class="bodytext">micchādiṭṭhino, sammādiṭṭhino, muṭṭhassatino, upaṭṭhitassatino, asāre sāramatino <span class="note">[dha. pa. 11]</span>, nimmānaratino devā, ye devā vasavattino <span class="note">[saṃ. ni. 1.168]</span>, aṭṭhete cakkavattino, dhamme dhammānuvattino <span class="note">[saṃ. ni. 5.34]</span>, saggaṃ sugatino yanti <span class="note">[dha. pa. 126]</span>, tomara’ṅkusapāṇino <span class="note">[jā. 2.22.223]</span>, daṇḍamuggarapāṇino, ariyavuttino, nipakā santavuttino iccādi.</p>\r\n<p class="bodytext">dutiyāyomhi –</p>\r\n<p class="bodytext">muṭṭhassatine, upaṭṭhitassatine, ariyavuttine, tomara’ṅkusapāṇine <span class="note">[jā. 2.22.190]</span> iccādi.</p>\r\n<p class="bodytext">vātveva? micchādiṭṭhī janā gacchanti, micchādiṭṭhī jane passati.</p>\r\n<p class="bodytext">garū pana "tomara’ṅkusapāṇayo, atthe visāradamatayo"ti <span class="note">[ka. 253]</span> rūpāni idha icchanti.</p>\r\n<p class="bodytext">aññattheti kiṃ? micchādiṭṭhiyo dhammā, micchādiṭṭhiyo dhamme.</p>\r\n<p class="bodytext">pumeti kiṃ? micchādiṭṭhiniyo itthiyo, micchādiṭṭhīni kulāni.</p>\r\n<p class="bodytext"><a name="para172"></a><span class="paranum">172</span>. <span class="bld">ne smiṃno kvaci</span> <span class="note">[nī. 453]</span>.</p>\r\n<p class="bodytext">pume aññatthe ito smiṃno kvaci ne hoti.</p>\r\n<p class="bodytext">kataññumhi ca posamhi, sīlavante ariyavuttine <span class="note">[jā. 1.10.78]</span>. sabbakāmasamiddhine kule, chattapāṇine, daṇḍapāṇine, tomara’ṅkusapāṇine <span class="note">[jā. 2.22.190]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-171-172-');
INSERT INTO pages VALUES(54512,'annya_sadda_10',105,replace(replace('\r\n<p class="bodytext">suttavibhattena <a name="M0.0105"></a> ītopi smiṃno kvaci ne hoti, mātaṅgasmiṃ yasassine <span class="note">[jā. 2.19.96]</span>, devavaṇṇine, brahmavaṇṇine, arahantamhi tādine <span class="note">[theragā. 1182]</span> iccādi.</p>\r\n<p class="centered">ikārantapulliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">īkārantapulliṅgarāsi</p>\r\n<p class="bodytext">īkārante ‘simhi nā’napuṃsakassā’ti suttena simhi rassattaṃ natthi, ‘ge vā’ti ge pare vikappena rasso, yosu ca aṃ, nā, sa, smā, smiṃ su ca ‘ekavacanayosvaghona’nti niccaṃ rasso, daṇḍī gacchati. ‘jantu hetu’ iccādisuttena vikappena yonaṃ lopo, daṇḍī gacchanti.</p>\r\n<p class="bodytext">pakkhe –</p>\r\n<p class="bodytext"><a name="para173"></a><span class="paranum">173</span>. <span class="bld">yonaṃ none pume</span> <span class="note">[ka. 225; rū. 151; nī. 452, 453]</span>.</p>\r\n<p class="bodytext">pume jhasaññamhā ī-kārato paṭhamā, dutiyāyonaṃ kamena no, ne honti vā.</p>\r\n<p class="bodytext">daṇḍino gacchanti, bhodaṇḍi, bho daṇḍī, bhonto daṇḍino, daṇḍiṃ.</p>\r\n<p class="bodytext"><a name="para174"></a><span class="paranum">174</span>. <span class="bld">naṃ jhīto</span> <span class="note">[ka. 224; rū. 153; nī. 451]</span>.</p>\r\n<p class="bodytext">pume jhasaññamhā ī-kārato aṃvacanassa naṃ hoti vā.</p>\r\n<p class="bodytext">daṇḍinaṃ.</p>\r\n<p class="bodytext"><a name="para175"></a><span class="paranum">175</span>. <span class="bld">no vā</span> <span class="note">[’no’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">pume jhīto dutiyāyossa no hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-173-174-175-');
INSERT INTO pages VALUES(54513,'annya_sadda_10',106,replace(replace('\r\n<p class="bodytext">daṇḍī<a name="M0.0106"></a>, daṇḍino, daṇḍine, daṇḍinā, daṇḍīhi, daṇḍībhi, daṇḍissa, daṇḍino, daṇḍīnaṃ, daṇḍismā, daṇḍimhā, daṇḍinā, daṇḍīhi, daṇḍībhi, daṇḍissa, daṇḍino, daṇḍīnaṃ, daṇḍismiṃ, daṇḍimhi.</p>\r\n<p class="bodytext"><a name="para176"></a><span class="paranum">176</span>. <span class="bld">smiṃno niṃ</span> <span class="note">[ka. 226; rū. 154; nī. 416]</span>.</p>\r\n<p class="bodytext">jhīto smiṃno ni hoti vā.</p>\r\n<p class="bodytext">daṇḍini.</p>\r\n<p class="bodytext">‘ne smiṃno kvacī’ti vibhattasuttena smiṃno ne ca hoti, daṇḍine, daṇḍīsu.</p>\r\n<p class="bodytext">evaṃ cakkī, pakkhī, sukhī, sikhī, cāgī, bhāgī, bhogī, yogī, saṅghī, vācī, dhajī, bhajī, kuṭṭhī, raṭṭhī, dāṭhī, ñāṇī, pāṇī, gaṇī, guṇī, cammī, dhammī, sīghayāyī, pāpakārī, brahmacārī, māyāvī, medhāvī, bhuttāvī, bhayadassāvī, yasassī, tejassī, chattī, pattī, dantī, mantī, sattughātī, sīhanādī, sāmī, piyappasaṃsī. atthadassī, dhammadassī iccādayo. gāmaṇī, senānī, sudhī iccādīsu pana smiṃno nittaṃ natthi.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext">mahāvuttinā yosu jhī-kārassapi kvaci ṭattaṃ hoti,</p>\r\n<p class="gatha1">"haṃsā koñcā mayūrā ca, hatthayo pasadā migā.</p>\r\n<p class="gathalast">sabbe sīhassa bhāyanti, natthi kāyasmiṃ tulyatā <span class="note">[jā. 1.2.103]</span>.</p>\r\n<p class="bodytext">purisālū ca hatthayo, saññatā brahmacārayo <span class="note">[a. ni. 6.37]</span>, apace brahmacārayo"ti dissanti. tattha ‘hatthayo’ti hatthino, ‘purisālū’ti purisalolā balavāmukhayakkhiniyo, ‘brahmacārayo’ti brahmacārino, ‘apace’ti pūjeyya.</p>\r\n','\r',char(13)),'\n',char(10)),'-176-');
INSERT INTO pages VALUES(54514,'annya_sadda_10',107,replace(replace('\r\n<p class="bodytext">sussapi <a name="M0.0107"></a> kvaci nesu hoti, susukhaṃ vata jīvāma, verinesu averino <span class="note">[dha. pa. 197]</span>, verinesu manussesu, viharāma averino. tattha ‘verinesū’ti verīcittavantesu.</p>\r\n<p class="bodytext">samāsepi paṭhamāyossa nottaṃ, dutiyāyossa nottaṃ nettañca hoti. tattha dve nottāni pākaṭāni. nettaṃ pana vuccate, "assamaṇe samaṇamānine <span class="note">[a. ni. 8.10]</span>, nare pāṇātipātine <span class="note">[itivu. 93]</span>, mañjuke piyabhāṇine <span class="note">[jā. 2.22.1721]</span>, māladhārine <span class="note">[jā. 2.22.1727]</span>, kāsikuttamadhārine <span class="note">[jā. 2.22.195]</span>, vaṇṇavante yasassine <span class="note">[dī. ni. 2.282]</span>, cāpahatthe kalāpine, ubho bhassaravaṇṇine <span class="note">[jā. 2.21.111]</span>, brāhmaṇe devavaṇḍine, samuddharati pāṇine <span class="note">[apa. therī 2.3.137]</span>, evaṃ jarā ca maccu ca, adhivattanti pāṇine"ti <span class="note">[saṃ. ni. 1.136]</span> dissanti. tattha ‘bhassaravaṇṇine’ti pabhassaravaṇṇavante. smiṃno nette pana "mātaṅgasmiṃ yasassine" iccādīni <span class="note">[jā. 2.19.96]</span> pubbe vuttāneva.</p>\r\n<p class="centered">īkārantapulliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">ukārantapulliṅgarāsi</p>\r\n<p class="subhead">bhikkhādigaṇarāsi</p>\r\n<p class="bodytext">‘gasīna’nti silopo, bhikkhu. yonaṃ lope dīgho, bhikkhū.</p>\r\n<p class="bodytext">pakkhe –</p>\r\n<p class="bodytext"><a name="para177"></a><span class="paranum">177</span>. <span class="bld">lā yonaṃ vo pume</span> <span class="note">[ka. 119; rū. 155; nī. 294]</span>.</p>\r\n<p class="bodytext">pume lasaññehi uvaṇṇehi yonaṃ vo hoti vāti yonaṃ vo.</p>\r\n','\r',char(13)),'\n',char(10)),'-177-');
INSERT INTO pages VALUES(54515,'annya_sadda_10',108,replace(replace('\r\n<p class="bodytext"><a name="para178"></a><span class="paranum">178</span>. <span class="bld">vevosu</span> <a name="M0.0108"></a> <span class="bld">lussa</span> <span class="note">[ka. 97; rū. 156; nī. 260]</span>.</p>\r\n<p class="bodytext">pume ve, vosu paresu lasaññassa u-kārassa ṭa hoti.</p>\r\n<p class="bodytext">bhikkhavo.</p>\r\n<p class="bodytext">lussāti kiṃ? sayambhuvo.</p>\r\n<p class="bodytext">bho bhikkhu, bho bhikkhū, bhonto bhikkhū.</p>\r\n<p class="bodytext"><a name="para179"></a><span class="paranum">179</span>. <span class="bld">pumālapane vevo</span> <span class="note">[ka. 116; rū. 157; nī. 291]</span>.</p>\r\n<p class="bodytext">pume ālapane lasaññamhā u-kārato yossa ve, vo honti vā.</p>\r\n<p class="bodytext">bhonto bhikkhave, atha kho bhagavā bhikkhū āmantesi "bhikkhavo"ti <span class="note">[ma. ni. 1.1]</span>, devakāyā abhikkantā, te vijānātha bhikkhavo <span class="note">[dī. ni. 2.334]</span>, bhikkhuṃ, bhikkhū, bhikkhavo, bhikkhunā. ‘sunaṃhisū’ti dīgho, bhikkhūhi, bhikkhūbhi, bhikkhussa, bhikkhuno, bhikkhūnaṃ, bhikkhusmā, bhikkhumhā, bhikkhunā, bhikkhūhi, bhikkhūbhi, bhikkhussa, bhikkhuno, bhikkhūnaṃ, bhikkhusmiṃ, bhikkhumhi, bhikkhūsu.</p>\r\n<p class="bodytext">evaṃ maṅku, maccu, ucchu, paṭu, bhāṇu, setu, ketu, sattu, sindhu, bandhu, kāru, neru, meru, ruru, veḷu iccādayo.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext">hetu, jantu, kurusaddesu ‘jantuhetu’ iccādisuttena vikappena yonaṃ lopo, hetu dhammo, hetū dhammā, atīte hetavo pañca.</p>\r\n<p class="bodytext"><a name="para180"></a><span class="paranum">180</span>. <span class="bld">yomhi vā kvaci</span> <span class="note">[ka. 97; rū. 156; nī. 260]</span>.</p>\r\n<p class="bodytext">yosu lasaññino u-kārassa kvaci ṭa hoti vā.</p>\r\n<p class="bodytext">atīte hetayo pañca.</p>\r\n','\r',char(13)),'\n',char(10)),'-178-179-180-');
INSERT INTO pages VALUES(54516,'annya_sadda_10',109,replace(replace('\r\n<p class="bodytext">vāti <a name="M0.0109"></a> kiṃ? hetuyo.</p>\r\n<p class="bodytext">kvacīti kiṃ? bhikkhavo.</p>\r\n<p class="bodytext">bho hetu, bho hetū, bhonto hetū, hetavo, hetayo, hetuyo vā, hetuṃ, hetū, hetavo, hetayo, hetuyo vā. sesaṃ bhikkhusamaṃ.</p>\r\n<p class="bodytext">jantu gacchati, jantū, jantayo, jantuyo vā.</p>\r\n<p class="bodytext"><a name="para181"></a><span class="paranum">181</span>. <span class="bld">jantādito no ca</span> <span class="note">[ka. 119; rū. 155; nī. 294; ‘jantvādito’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">pume jantādito yonaṃ no ca hoti vo ca.</p>\r\n<p class="bodytext">jantuno, jantavo, bhojantu, bhojantū, bhonto jantū, jantayo, jantuyo, jantuno, jantavo, jantuṃ, jantū, jantayo, jantuyo, jantuno, jantavo. sesaṃ bhikkhusamaṃ.</p>\r\n<p class="bodytext">kuru, kurū, kurayo, kuruyo, kuruno, kuravoti sabbaṃ jantusamaṃ.</p>\r\n<p class="bodytext">"ajjeva taṃ kurayo pāpayātu <span class="note">[jā. 2.22.1632]</span>, nandanti taṃ kurayo dassanena <span class="note">[jā. 2.22.1641]</span>, ajjeva taṃ kurayo pāpayāmī"ti <span class="note">[jā. 2.22.1634]</span> dissanti.</p>\r\n<p class="bodytext">mahāvuttinā latopi aṃvacanassa kvaci naṃ hoti, "kimatthinaṃ bhikkhunaṃ āhu, bhikkhunamāhu maggadesiṃ, bhikkhunamāhu maggajīviṃ, buddhaṃ ādiccabandhuna"nti dissanti, tathā "roganiḍḍaṃ pabhaṅgunaṃ, bhogānañca pabhaṅgunaṃ <span class="note">[dha. pa. 139]</span>, viññāṇañca virāguna"nti ca. tattha ‘kimatthina’nti kiṃsabhāvaṃ, ‘maggadesi’nti maggaṃ desentaṃ, ‘maggajīvi’nti magge jīvantaṃ, ‘roganiḍḍa’nti rogānaṃ kulāvakabhūtaṃ, ‘pabhaṅguna’nti pabhijjanasīlaṃ, ‘virāguna’nti virajjanasīlaṃ. katthaci paṭhamantampi dissati, tattha nāgamo.</p>\r\n<p class="centered">iti bhikkhādigaṇarāsi.</p>\r\n<p class="subhead">satthādigaṇarāsi</p>\r\n<p class="subhead">satthādirāsi</p>\r\n<p class="bodytext"><a name="para182"></a><span class="paranum">182</span>. <span class="bld">ltupitādīnamā simhi</span> <span class="note">[ka. 299; rū. 158; nī. 411]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-181-182-');
INSERT INTO pages VALUES(54517,'annya_sadda_10',110,replace(replace('\r\n<p class="bodytext">simhi <a name="M0.0110"></a> pare ltupaccayantānaṃ satthu, kattuiccādīnaṃ pitādīnañca u-kāro ā hoti. ‘gasīna’nti lopo.</p>\r\n<p class="bodytext">satthā.</p>\r\n<p class="bodytext"><a name="para183"></a><span class="paranum">183</span>. <span class="bld">ltupitādīnamase</span> <span class="note">[ka. 200; rū. 159; nī. 412]</span>.</p>\r\n<p class="bodytext">samhā aññasmiṃ vibhattigaṇe pare ltu, pitādīnaṃ u-kāro āraṅa hoti.</p>\r\n<p class="bodytext"><a name="para184"></a><span class="paranum">184</span>. <span class="bld">āraṅsmā</span> <span class="note">[ka. 205; rū. 160; nī. 421]</span>.</p>\r\n<p class="bodytext">āraṅto yonaṃ ṭo hoti.</p>\r\n<p class="bodytext">satthāro.</p>\r\n<p class="bodytext"><a name="para185"></a><span class="paranum">185</span>. <span class="bld">ge a ca</span> <span class="note">[ka. 246; rū. 73; nī. 476, 478-9]</span>.</p>\r\n<p class="bodytext">ge pare ltu, pitādīnaṃ u-kāro hoti a ca ā ca. bhosattha, bho satthā, bhonto satthāro, satthāraṃ.</p>\r\n<p class="bodytext"><a name="para186"></a><span class="paranum">186</span>. <span class="bld">ṭoṭe vā</span> <span class="note">[ka. 205; rū. 260; nī. 421]</span>.</p>\r\n<p class="bodytext">āraṅto yonaṃ kamena ṭo, ṭe honti vā. ettha ca vāsaddo sakhasadde vikappanattho tattha vidhyantarasabbhāvā. puna ṭoggahaṇaṃ lahubhāvatthaṃ.</p>\r\n<p class="bodytext">satthāro, satthāre.</p>\r\n','\r',char(13)),'\n',char(10)),'-183-184-185-186-');
INSERT INTO pages VALUES(54518,'annya_sadda_10',111,replace(replace('\r\n<p class="bodytext"><a name="para187"></a><span class="paranum">187</span>. <span class="bld">ṭā</span> <a name="M0.0111"></a> <span class="bld">nāsmānaṃ</span> <span class="note">[ka. 207, 270; rū. 161, 120; nī. 42, 542]</span>.</p>\r\n<p class="bodytext">āraṅto nā, smānaṃ ṭā hoti.</p>\r\n<p class="bodytext">satthārā, satthārehi, satthārebhi.</p>\r\n<p class="bodytext"><a name="para188"></a><span class="paranum">188</span>. <span class="bld">lopo</span> <span class="note">[ka. 203; rū. 162; nī. 418]</span>.</p>\r\n<p class="bodytext">ltu, pitādīhi salopo hoti vā.</p>\r\n<p class="bodytext">satthu, satthussa, satthuno.</p>\r\n<p class="bodytext"><a name="para189"></a><span class="paranum">189</span>. <span class="bld">naṃmhi vā</span> <span class="note">[ka. 201; rū. 163; nī. 416]</span>.</p>\r\n<p class="bodytext">naṃmhi pare ltu, pitādīnaṃ u-kāro āraṅa hoti vā. imesaṃ mahānāma tiṇṇaṃ satthārānaṃ ekā niṭṭhā udāhu puthu niṭṭhāti <span class="note">[a. ni. 3.127]</span>. satthūnaṃ.</p>\r\n<p class="bodytext"><a name="para190"></a><span class="paranum">190</span>. <span class="bld">ā</span> <span class="note">[ka. 202; rū. 164; nī. 417]</span>.</p>\r\n<p class="bodytext">naṃmhi pare ltu, pitādīnaṃ u-kāro ā hoti vā.</p>\r\n<p class="bodytext">satthānaṃ, satthārā, satthārehi, satthārebhi, satthu, satthussa, satthuno, satthūnaṃ, satthārānaṃ, satthānaṃ.</p>\r\n<p class="bodytext"><a name="para191"></a><span class="paranum">191</span>. <span class="bld">ṭi smiṃno</span> <span class="note">[ka. 206; rū. 165; nī. 422]</span>.</p>\r\n<p class="bodytext">āraṅto smiṃno ṭi hoti.</p>\r\n<p class="bodytext"><a name="para192"></a><span class="paranum">192</span>. <span class="bld">rassāraṅa</span> <span class="note">[ka. 208; rū. 166; nī. 424]</span>.</p>\r\n<p class="bodytext">smiṃmhi pare āraṅkato rasso hoti.</p>\r\n<p class="bodytext">satthari, satthāresu.</p>\r\n','\r',char(13)),'\n',char(10)),'-187-188-189-190-191-192-');
INSERT INTO pages VALUES(54519,'annya_sadda_10',112,replace(replace('\r\n<p class="bodytext">bahulādhikārā <a name="M0.0112"></a> nā, smāsu satthunāti ca sumhi satthūsūti ca siddhaṃ. "dhammarājena satthunā, pūjaṃ labbhati bhattusū"ti <span class="note">[jā. 2.22.1517]</span> pāḷi. ‘bhattusū’ti sāmīsu, ‘bhattāsū’tipi pāṭho.</p>\r\n<p class="bodytext">‘ltupitādīnamase’ti aseggahaṇena tomhi āraṅa hoti <span class="note">[nī. 414]</span>, "satthārato satthāraṃ gacchanti, satthārato satthāraṃ ghaṭentī"ti <span class="note">[mahāni. 27]</span> pāḷi.</p>\r\n<p class="bodytext">evaṃ kattā, bhattā, gantā, jetā, janetā, chettā, cheditā, viññātā, viññāpetā, uṭṭhātā, uṭṭhāpetā, taritā, tāretā, dātā, dāpetā, sandhātā, sandhāpetā, netā, nettā, posetā, bhettā, yātā, vattā, setā, hantā, sakamandhātā, mahāmandhātā iccādayo.</p>\r\n<p class="bodytext">visesavidhānamuccate.</p>\r\n<p class="bodytext">mahāvuttinā yonaṃ ā hoti, avitakkitā gabbhamupavajanti <span class="note">[jā. 1.13.138 (visadisaṃ)]</span>, te bhikkhū brūhetā suññāgārānaṃ.</p>\r\n<p class="bodytext">amaccavācīhi kattu, khattusaddehi gassa ettaṃ, uṭṭhehi katte taramāno <span class="note">[jā. 2.22.1690]</span>, natthi bho khatte paroloko <span class="note">[dī. ni. 2.408]</span>.</p>\r\n<p class="bodytext">ge pare āraṅa ca hoti, pucchāma kattāra anomapañña, "kattāraṃ anomapañña"ntipi <span class="note">[jā. 1.10.28]</span> yujjati.</p>\r\n<p class="bodytext">aṃmhi pare pubbassaralopo ca hoti, anukampakaṃ pāṇasamampi bhattuṃ jahanti itthiyo. "so patīto pamuttena, bhattunā bhattugāravo"ti <span class="note">[jā. 2.21.48]</span> diṭṭhattā kattunā, gantunā iccādīnipi yujjanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54520,'annya_sadda_10',113,replace(replace('\r\n<p class="bodytext">nettumhā <a name="M0.0113"></a> smiṃno ettaṃ <span class="note">[nī. 430]</span>, nette ujuṃ gate sati <span class="note">[jā. 1.4.133]</span>, ete saddā tīsu liṅgesu samānarūpā honti, kattā itthī, kattā puriso, kattā kulaṃ iccādi.</p>\r\n<p class="centered">iti satthādirāsi.</p>\r\n<p class="subhead">pitādirāsi</p>\r\n<p class="bodytext">pitā gacchati.</p>\r\n<p class="bodytext"><a name="para193"></a><span class="paranum">193</span>. <span class="bld">pitādīnamanattādīnaṃ</span> <span class="note">[ka. 209; rū. 168; nī. 425; ‘pitādīnamanatvādīnaṃ’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">nattādivajjitānaṃ pitādīnaṃ āraṅakato rasso hoti.</p>\r\n<p class="bodytext">pitaro, bho pita, bho pitā, bhonto pitaro. pitaraṃ, pituṃ vā. "mātuṃ pituñca vanditvā"ti <span class="note">[jā. 2.22.1859]</span> dissati.</p>\r\n<p class="bodytext">pitaro, pitare, pitarā, pitunā, pitarehi, pitarebhi, pitūhi, pitūbhi, pitu, pitussa, pituno, pitūnaṃ, pitarānaṃ, pitarā, pitunā, pitarehi, pitarebhi, pitūhi, pitūbhi, pitu, pitussa, pituno, pitūnaṃ, pitarānaṃ, pitānaṃ, pitusmiṃ, pitumhi, pitari, pitaresu, pitūsu.</p>\r\n<p class="bodytext">anaṇo ñātinaṃ hoti, devānaṃ pitunañca so <span class="note">[jā. 2.22.126]</span>, mātāpitūnaṃ accayena, dhammaṃ cara mahārāja, mātāpitūsu khattiya <span class="note">[jā. 2.17.39]</span>.</p>\r\n<p class="bodytext">evaṃ bhātā, bhātaro, jāmātā, jāmātaroiccādi.</p>\r\n<p class="bodytext">anattādīnanti kiṃ? nattā, nattāro, nattāraṃ, nattāro, nattāre iccādi. tathā panattusaddopi.</p>\r\n','\r',char(13)),'\n',char(10)),'-193-');
INSERT INTO pages VALUES(54521,'annya_sadda_10',114,replace(replace('\r\n<p class="bodytext">mātu<a name="M0.0114"></a>, dhītu, duhitusaddā pana itthiliṅgā eva, mātā, mātaro, bhoti māta, bhoti mātā, bhoti māte vā, "acchariyaṃ nandamāte, abbhutaṃ nandamāte"ti <span class="note">[a. ni. 7.53]</span> dissati. ‘ghabrahmāditve’ti gassa ettaṃ. bhotiyo mātaro, mātaraṃ, mātuṃ, mātaro, mātare, mātuyā, mātarā, mātarehi, mātarebhi, mātūhi, mātūbhi, mātu, mātussa, mātuyā. "mātussa sarati, pitussa saratī"ti <span class="note">[rū. 169; nī. 160 piṭṭhe]</span> satthe dissati. "buddhamātussa sakkāraṃ, karotu sugatoraso"ti <span class="note">[apa. therī. 2.2.259]</span> ca dissati. mātūnaṃ, mātānaṃ, mātarānaṃ. pañcamīrūpaṃ tatiyāsamaṃ. chaṭṭhīrūpaṃ catutthīsamaṃ. mātusmiṃ, mātumhi, mātari, mātuyā, mātuyaṃ, mātaresu, mātūsu. evaṃ dhītu, duhitusaddā.</p>\r\n<p class="bodytext">visesavidhimhi gāthāsu mahāvuttinā mātu, pitusaddehi nādīnaṃ pañcannaṃ ekavacanānaṃ yā hoti, smiṃno pana yañca hoti, antalopo ca. matyā kataṃ, matyā deti, matyā apeti, matyā dhanaṃ, matyā ṭhitaṃ. matyaṃ ṭhitaṃ. evaṃ petyā kataṃiccādi, idha vuddhi.</p>\r\n<p class="bodytext">anuññāto ahaṃ matyā, sañcatto pitarā ahaṃ <span class="note">[jā. 2.22.29]</span>. matyā ca petyā ca kataṃ susādhu <span class="note">[jā. 2.18.61]</span>, ahañhi jānāmi janinda etaṃ, matyā ca petyā ca <span class="note">[jā. 2.18.59]</span>, sabbaṃ pubbepi vuttameva.</p>\r\n<p class="bodytext">satthu, pitādīnaṃ samāse vidhānaṃ samāsakaṇḍe āgamissati.</p>\r\n<p class="centered">iti pitādirāsi.</p>\r\n<p class="centered">iti satthādigaṇarāsi.</p>\r\n<p class="centered">ukārantapulliṅgarāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54522,'annya_sadda_10',115,replace(replace('\r\n<p class="subhead">ūkārantapulliṅgarāsi</p>\r\n\r\n<p class="bodytext">‘gasīna’nti <a name="M0.0115"></a> lopo, sayambhū gacchati. ‘lopo’ti yonaṃ lopo, sayambhū gacchanti.</p>\r\n<p class="bodytext">pakkhe –</p>\r\n<p class="bodytext">‘jantādito no cā’ti yonaṃ vo, no, sayambhuvo, sayambhuno.</p>\r\n<p class="bodytext">‘ge vā’ti ge pare vikappena rasso, bho sayambhu, bho sayambhū, bhonto sayambhū, sayambhuvo, sayambhuno.</p>\r\n<p class="bodytext">‘ekavacanayosvaghona’nti amādīsu ekavacanesu niccaṃ rasso, sayambhuṃ, gāthāyaṃ ‘sayambhuna’ntipi yujjati.</p>\r\n<p class="bodytext">sayambhū, sayambhuvo, sayambhuno. sayambhunā, sayambhūhi, sayambhūbhi, sayambhussa, sayambhuno, sayambhūnaṃ. sayambhusmā, sayambhumhā, sayambhunā, sayambhūhi, sayambhūbhi, sayambhussa, sayambhuno, sayambhūnaṃ. sayambhusmiṃ, sayambhumhi, sayambhūsu.</p>\r\n<p class="bodytext">evaṃ abhibhū, parābhibhū, vessabhū, gotrabhū, vatrabhū iccādi. sesesu pana yonaṃ no eva labbhati, cittasahabhuno dhammā <span class="note">[dha. sa. dukamātikā 61]</span>.</p>\r\n<p class="bodytext"><a name="para194"></a><span class="paranum">194</span>. <span class="bld">kūto</span> <span class="note">[ka. 119; rū. 155; nī. 294]</span>.</p>\r\n<p class="bodytext">pume kūpaccayantehi yonaṃ no hoti vā.</p>\r\n<p class="bodytext">sabbaññū, sabbaññuno. sesaṃ suviññeyyaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-194-');
INSERT INTO pages VALUES(54523,'annya_sadda_10',116,replace(replace('\r\n<p class="bodytext">viññū<a name="M0.0116"></a>, vadaññū, atthaññū, dhammaññū, mattaññū, vidū. idha kūsaddena rūpaccayantāpi gayhanti, vedagū, pāragū. tathā bhīrū, pabhaṅgū, virāgūiccādi ca.</p>\r\n<p class="centered">ūkārantapulliṅgarāsi niṭṭhito.</p>\r\n<p class="bodytext">okāranto pana gosaddo eva, so pubbe vuttoyeva.</p>\r\n<p class="centered">pulliṅgarāsi niṭṭhito.</p>\r\n<p class="subhead">napuṃsakaliṅgarāsi</p>\r\n<p class="bodytext"><span class="bld">akārantanapuṃsaka cittādirāsi</span></p>\r\n<p class="bodytext">atha napuṃsakaliṅgaṃ dīpiyate. taṃ pana pañcavidhaṃ adantaṃ, idantaṃ īdantaṃ, udantaṃ, ūdantanti.</p>\r\n<p class="bodytext"><a name="para195"></a><span class="paranum">195</span>. <span class="bld">aṃ napuṃsake</span> <span class="note">[ka. 125; rū. 198; nī. 300]</span>.</p>\r\n<p class="bodytext">napuṃsake ato sissa aṃ hoti.</p>\r\n<p class="bodytext">cittaṃ.</p>\r\n<p class="bodytext"><a name="para196"></a><span class="paranum">196</span>. <span class="bld">yonaṃ ni</span> <span class="note">[ka. 218; rū. 196; nī. 445]</span>.</p>\r\n<p class="bodytext">napuṃsake ato yonaṃ ni hoti. ‘yolopanīsū’ti nimhi dīgho.</p>\r\n<p class="bodytext">cittāni.</p>\r\n<p class="bodytext"><a name="para197"></a><span class="paranum">197</span>. <span class="bld">nīnaṃ vā</span> <span class="note">[ka. 107; rū. 69; nī. 275; ‘nīna vā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ato nīnaṃ ṭā, ṭe honti vā.</p>\r\n<p class="bodytext">cittā, he citta, he cittā, he cittāni, he cittā vā, cittaṃ, cittāni, citte, cittena. sesaṃ purisasamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-195-196-197-');
INSERT INTO pages VALUES(54524,'annya_sadda_10',117,replace(replace('\r\n<p class="bodytext">evaṃ <a name="M0.0117"></a> dakaṃ, udakaṃ, sukhaṃ, dukkhaṃ, mukhaṃ, aṅgaṃ, liṅgaṃ, siṅgaṃ, aghaṃ, saccaṃ, naccaṃ, rajjaṃ, pajjaṃ, ambujaṃ, dhaññaṃ, thaññaṃ, araññaṃ, puññaṃ, kiliṭṭhaṃ, piṭṭhaṃ, bhaṇḍaṃ, tuṇḍaṃ, ñāṇaṃ, tāṇaṃ, leṇaṃ, karaṇaṃ, caraṇaṃ, chattaṃ, khettaṃ, nettaṃ, amataṃ, sotaṃ, pīṭhaṃ, vatthaṃ, padaṃ, gadaṃ, āvudhaṃ, kānanaṃ, ghānaṃ, jhānaṃ, dānaṃ, dhanaṃ, vanaṃ, pāpaṃ, dumaṃ, hadayaṃ, cīraṃ, cīvaraṃ, kulaṃ, mūlaṃ, balaṃ, maṅgalaṃ, bhisaṃ, sīsaṃ, lohaṃiccādayo.</p>\r\n<p class="centered">iti cittādirāsi.</p>\r\n<p class="subhead">kammādirāsi</p>\r\n<p class="bodytext">kammasadde –</p>\r\n<p class="bodytext"><a name="para198"></a><span class="paranum">198</span>. <span class="bld">nāsseno</span> <span class="note">[ka. 103; rū. 79; nī. 271]</span>.</p>\r\n<p class="bodytext">kammādīhi nāssa eno hoti vā.</p>\r\n<p class="bodytext">kammena, kammanā.</p>\r\n<p class="bodytext">‘pumakammathāmaddhāna’nti suttena nā, smāsu uttaṃ, kammunā, kammassa, kammuno, kammasmā, kammamhā, kammanā, kammunā, kammassa, kammuno.</p>\r\n<p class="bodytext"><a name="para199"></a><span class="paranum">199</span>. <span class="bld">kammādito</span> <span class="note">[ka. 197; rū. 125; nī. 404]</span>.</p>\r\n<p class="bodytext">kammādīhi smiṃno ni hoti vā.</p>\r\n<p class="bodytext">kammasmiṃ, kammamhi, kammani, kamme. sesaṃ cittasamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-198-199-');
INSERT INTO pages VALUES(54525,'annya_sadda_10',118,replace(replace('\r\n<p class="bodytext">kamma camma ghamma asma vesma addha muddha aha brahma attaātumā kammādi. kammani, cammani. "kiṃ chando kimadhippāyo, eko sammasi ghammanī"ti <span class="note">[jā. 1.16.1]</span> ca "kiṃ patthayaṃ mahābrahme, eko sammasi ghammanī"ti <span class="note">[jā. 1.13.83]</span> ca "mā tvaṃ cande khali asmanī"ti ca "tamaddasa mahābrahmā, nisinnaṃ samhi vesmanī"ti ca dissanti. addhani<a name="M0.0118"></a>, muddhani, ahani, brahmani, attani, ātumani, sabbametaṃ pubbepi vuttameva ca. tattha ‘sammasī’ti acchasi, ‘ghammanī’ti gimhakāle ātape vā, ‘asmanī’ti pāsāṇe, ‘vesmanī’ti ghare.</p>\r\n<p class="centered">iti kammādirāsi.</p>\r\n<p class="bodytext"><a name="para200"></a><span class="paranum">200</span>. <span class="bld">aṃ napuṃsake</span> <span class="note">[ka. 125; rū. 198; nī. 300; ‘aṃṅaṃ napuṃsake’ (bahūsu)?]</span>.</p>\r\n<p class="bodytext">napuṃsake simhi ntussa aṃ hoti vā. silopo.</p>\r\n<p class="bodytext">guṇavaṃ kulaṃ.</p>\r\n<p class="bodytext">pakkhe –</p>\r\n<p class="bodytext">simhi mahāvuttinā ntussa anto a hoti, tato sissa aṃ hoti, guṇavantaṃ kulaṃ.</p>\r\n<p class="bodytext">‘yvādo ntussā’ti yvādīsu ntussantassa attaṃ, guṇavantāni, guṇavantā, he guṇava, he guṇavā, he guṇavantāni, he guṇavantā, guṇavaṃ, guṇavantaṃ, guṇavantāni, guṇavante, guṇavantena, guṇavatā kulena. sabbaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext">satimaṃ kulaṃ, satimantaṃ kulaṃ iccādi.</p>\r\n<p class="bodytext">gacchaṃ kulaṃ, gacchantaṃ kulaṃ, gacchantāni kulāni iccādi.</p>\r\n<p class="centered">iti akārantanapuṃsakarāsi.</p>\r\n<p class="subhead">ikārantanapuṃsakarāsi</p>\r\n<p class="bodytext">aṭṭhi tiṭṭhati, aṭṭhī tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para201"></a><span class="paranum">201</span>. <span class="bld">jhalā vā</span> <span class="note">[ka. 217; rū. 199; nī. 444]</span>.</p>\r\n<p class="bodytext">napuṃsake jha, lato yonaṃ ni hoti vā. ‘yolopanīsū’ti dīgho.</p>\r\n','\r',char(13)),'\n',char(10)),'-200-201-');
INSERT INTO pages VALUES(54526,'annya_sadda_10',119,replace(replace('\r\n<p class="bodytext">aṭṭhīni<a name="M0.0119"></a>, he aṭṭhi, he aṭṭhī, he aṭṭhīni, he aṭṭhī vā, aṭṭhiṃ, aṭṭhinaṃ, aṭṭhīni, aṭṭhī, aṭṭhinā, aṭṭhīhi, aṭṭhībhi. sesaṃ munisamaṃ.</p>\r\n<p class="bodytext">samāsepi sammādiṭṭhi kulaṃ, sammādiṭṭhīni kulāni iccādi, yonaṃ no, ne natthi.</p>\r\n<p class="bodytext">smiṃmhi sammādiṭṭhismiṃ, sammādiṭṭhimhi, sammādiṭṭhini, sammādiṭṭhine kule, ariyavuttine kule iti vattabbaṃ.</p>\r\n<p class="bodytext">evaṃ akkhi, acchi, satthi, dadhi, vāri iccādayo.</p>\r\n<p class="centered">iti ikārantanapuṃsakarāsi.</p>\r\n<p class="subhead">īkārantanapuṃsakarāsi</p>\r\n<p class="bodytext">īkārante ‘ekavacanayosvaghona’nti suttena simhi rasso, daṇḍi kulaṃ, daṇḍīni kulāni, yonaṃ lope daṇḍī.</p>\r\n<p class="bodytext">‘ge vā’ti rasso, he daṇḍi, he daṇḍī vā, he daṇḍīni, he daṇḍī, daṇḍiṃ, daṇḍinaṃ, daṇḍīni, daṇḍī, daṇḍinā, daṇḍīhi, daṇḍībhi. pulliṅgasamaṃ.</p>\r\n<p class="bodytext">samāsepi sīghayāyi cittaṃ, sīghayāyīni, sīghayāyī, he sīghayāyi, he sīghayāyī vā, he sīghayāyīni, he sīghayāyī, sīghayāyiṃ, sīghayāyinaṃ, sīghayāyīni iccādi.</p>\r\n<p class="bodytext">evaṃ sukhakāri dānaṃ, cakkī, pakkhī, sukhī, sikhī iccādayo kulasambandhino ca veditabbā.</p>\r\n<p class="centered">iti īkārantanapuṃsakarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54527,'annya_sadda_10',120,replace(replace('\r\n<p class="subhead">ukārantanapuṃsakarāsi</p>\r\n\r\n<p class="bodytext">āyu <a name="M0.0120"></a> tiṭṭhati, ‘jhalā vā’ti yonaṃ nitte lope ca dīgho, āyūni, āyū, he āyu, he āyū, he āyūni, he āyū, āyuṃ, āyunaṃ, āyūni, āyū. sesaṃ bhikkhusamaṃ.</p>\r\n<p class="bodytext">āyusaddo pulliṅgepi vattati, "punarāyu ca me laddhā <span class="note">[dī. ni. 2.369]</span>, āyuñca vo kīvatako nu samma <span class="note">[jā. 1.15.205]</span>, āyu nu khīṇo maraṇañca santike, na cāyu khīṇo maraṇañca dūre"ti pāḷipadāni.</p>\r\n<p class="bodytext">evaṃ cakkhu, hiṅgu, siggu, jatu, vatthu, matthu, madhu, dhanu, tipu, dāru, vasu, assu iccādayo.</p>\r\n<p class="bodytext"><a name="para202"></a><span class="paranum">202</span>. <span class="bld">ambādīhi</span> <span class="note">[ka. 217; rū. 199; nī. 444; ‘ambādīhi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ambu, paṃsuiccādīhi smiṃno ni hoti vā.</p>\r\n<p class="bodytext">phalaṃ patati ambuni, pupphaṃ yathā paṃsuni ātape gataṃ. sesaṃ āyusamaṃ. citragu, vahagu, digu iccādayopi ukārantapakatikā evāti.</p>\r\n<p class="centered">iti ukārantanapuṃsakarāsi.</p>\r\n<p class="subhead">ūkārantanapuṃsakarāsi</p>\r\n<p class="bodytext">simhi rasso, gotrabhu ñāṇaṃ, gotrabhūni, gotrabhū, he gotrabhu, he gotrabhū, he gotrabhūni, hegotrabhū, gotrabhuṃ, gotrabhunaṃ, gotrabhūni, gotrabhū. sesaṃ pulliṅgasamaṃ. evaṃ sayambhu ñāṇaṃ, abhibhu jhānaṃ iccādi.</p>\r\n<p class="centered">iti ūkārantanapuṃsakarāsi.</p>\r\n<p class="centered">napuṃsakaliṅgarāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-202-');
INSERT INTO pages VALUES(54528,'annya_sadda_10',121,replace(replace('\r\n<p class="subhead">sabbādirāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0121"></a> sabbanāmāni dīpiyante.</p>\r\n<p class="bodytext">sabba, katara, katama, ubhaya, itara, añña, aññatara, aññatama, pubba, para, apara, dakkhiṇa, uttara, adhara, ya, ta, tya, eta, ima, amu, kiṃ, eka, ubha, dvi,ti, catu, tumha, amha imāni aṭṭhavīsati sabbanāmāni nāma. sabbesaṃ liṅgatthānaṃ sādhāraṇāni nāmāni sabbanāmāni.</p>\r\n<p class="bodytext">tattha <span class="bld">sabba</span>saddo sakalattho.</p>\r\n<p class="bodytext"><span class="bld">katara, katama</span>saddā pucchanatthā.</p>\r\n<p class="bodytext"><span class="bld">ubhaya</span>saddo dvinnaṃ avayavānaṃ samudāyattho.</p>\r\n<p class="bodytext"><span class="bld">itara</span>saddo ekato vuttassa paṭiyogīvacano.</p>\r\n<p class="bodytext"><span class="bld">añña</span>saddo yathādhigatamhā aparavacano.</p>\r\n<p class="bodytext"><span class="bld">aññatara, aññatama</span>saddā aniyamatthā.</p>\r\n<p class="bodytext"><span class="bld">pubbā</span>dayo saddā disā, kālādivavatthānavacanā.</p>\r\n<p class="bodytext"><span class="bld">ya</span>saddo aniyamatthavacano.</p>\r\n<p class="bodytext"><span class="bld">ta, tya</span>saddā parammukhe dūravacanā.</p>\r\n<p class="bodytext"><span class="bld">eta</span>saddo parammukhe samīpavacano, sammukhe dūravacano. aṭṭhakathāyaṃ pana "eteti cakkhupathaṃ atikkamitvā dūragate sandhāyāhā"ti <span class="note">[jā. aṭṭha 4.15.104]</span> vuttaṃ, tasmā tasaddatthepi vattati.</p>\r\n<p class="bodytext"><span class="bld">ima</span>saddo sammukhe samīpavacano.</p>\r\n<p class="bodytext"><span class="bld">amu</span>saddo dūravacano. samīpa, dūratā ca parikappabuddhivasenāpi hoti.</p>\r\n<p class="bodytext"><span class="bld">kiṃ</span>saddo pucchanattho.</p>\r\n<p class="bodytext"><span class="bld">eka</span>saddo saṅkhyattho aññattho ca.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54529,'annya_sadda_10',122,replace(replace('\r\n<p class="bodytext"><span class="bld">ubha</span>saddo <a name="M0.0122"></a> dvisaddapariyāyo.</p>\r\n<p class="bodytext">tattha tyasaddopi bahulaṃ dissati. khiḍḍā paṇihitā tyāsu, rati tyāsu patiṭṭhitā, bījāni tyāsu ruhanti <span class="note">[jā. 2.21.120]</span>, kathaṃ nu vissase tyamhi <span class="note">[jā. 1.16.288]</span>, atha vissasate tyamhiiccādi <span class="note">[jā. 2.22.1474]</span>.</p>\r\n<p class="bodytext">‘itthiyamatvā’ti āpaccayo, ghasañño, silopo, sabbā itthī, sabbā, sabbāyo, he sabbe, he sabbā, he sabbāyo, sabbaṃ, sabbā, sabbāyo, sabbāya, sabbāhi, sabbābhi, sabbāya.</p>\r\n<p class="bodytext"><a name="para203"></a><span class="paranum">203</span>. <span class="bld">ghapāsassa ssā vā</span> <span class="note">[ka. 179, 62; rū. 204, 206; nī. 365, 209]</span>.</p>\r\n<p class="bodytext">gha, pasaññehi sabbanāmehi sassa ssā hoti vā.</p>\r\n<p class="bodytext"><a name="para204"></a><span class="paranum">204</span>. <span class="bld">ghossaṃssāssāyaṃtiṃsu</span> <span class="note">[ka. 66; rū. 205; nī. 213]</span>.</p>\r\n<p class="bodytext">ssamādīsu gho rasso hoti.</p>\r\n<p class="bodytext">sabbassā.</p>\r\n<p class="bodytext"><a name="para205"></a><span class="paranum">205</span>. <span class="bld">saṃsānaṃ</span> <span class="note">[ka. 168; rū. 203; nī. 353, 368]</span>.</p>\r\n<p class="bodytext">sabbādīhi naṃvacanassa saṃ, sānaṃ honti.</p>\r\n<p class="bodytext">sabbāsaṃ, sabbāsānaṃ, sabbāya, sabbāhi, sabbābhi, sabbāya, sabbassā, sabbāsaṃ, sabbāsānaṃ, sabbāya, sabbāyaṃ.</p>\r\n<p class="bodytext"><a name="para206"></a><span class="paranum">206</span>. <span class="bld">smiṃno ssaṃ</span> <span class="note">[ka. 179, 62; rū. 204, 206; nī. 365, 209]</span>.</p>\r\n<p class="bodytext">sabbādīhi smiṃno ssaṃ hoti vā.</p>\r\n<p class="bodytext">sabbassaṃ, sabbāsu.</p>\r\n','\r',char(13)),'\n',char(10)),'-203-204-205-206-');
INSERT INTO pages VALUES(54530,'annya_sadda_10',123,replace(replace('\r\n<p class="bodytext">saddanītiyaṃ <a name="M0.0123"></a> nā, smā, smiṃnampi ssādeso vutto <span class="note">[nī. 366]</span>. "tassā kumārikāya saddhiṃ <span class="note">[pārā. 443]</span>, kassāhaṃ kena hāyāmī"ti <span class="note">[pārā. 290]</span> pāḷi. idha pana suttavibhattena sādhiyati. sabbassā kataṃ, sabbassā apeti, sabbassā ṭhitaṃ.</p>\r\n<p class="bodytext">sabbo puriso.</p>\r\n<p class="bodytext"><a name="para207"></a><span class="paranum">207</span>. <span class="bld">yonameṭa</span> <span class="note">[ka. 164; rū. 200; nī. 347]</span>.</p>\r\n<p class="bodytext">akārantehi sabbādīhi yonaṃ eṭa hoti.</p>\r\n<p class="bodytext">sabbe purisā.</p>\r\n<p class="bodytext">atotveva? sabbā itthiyo, amū purisā.</p>\r\n<p class="bodytext">he sabba, he sabbā, he sabbe, sabbaṃ, sabbe, sabbena.</p>\r\n<p class="bodytext"><a name="para208"></a><span class="paranum">208</span>. <span class="bld">sabbādīnaṃ naṃmhi ca</span> <span class="note">[ka. 102; rū. 202; nī. 270]</span>.</p>\r\n<p class="bodytext">naṃmhi ca su, hisu ca sabbādīnaṃ assa e hoti.</p>\r\n<p class="bodytext">sabbehi, sabbebhi, sabbassa, sabbesaṃ, sabbesānaṃ, sabbasmā, sabbamhā, sabbehi, sabbebhi, sabbassa, sabbesaṃ, sabbesānaṃ, sabbasmiṃ, sabbamhi, sabbesu.</p>\r\n<p class="bodytext">cūḷaniruttiyaṃ pana smā, smiṃnaṃ ā, ettaṃ vuttaṃ, sabbā apeti, sabbe patiṭṭhitanti. "sabbā ca savati, sabbathā savatī"ti ca "tyāhaṃ mante paratthaddhā"ti <span class="note">[jā. 2.22.835]</span> ca pāḷī. tattha ‘tyāha’nti te+ahaṃ, tasmiṃ manteti attho.</p>\r\n','\r',char(13)),'\n',char(10)),'-207-208-');
INSERT INTO pages VALUES(54531,'annya_sadda_10',124,replace(replace('\r\n<p class="bodytext">sabbanāmehi catutthiyā āyādesopi dissati, "yāya no anukampāya, amhe pabbājayī muni. so no attho <a name="M0.0124"></a> anuppatto"ti <span class="note">[theragā. 176]</span> ca "yāyeva kho panatthāya āgaccheyyātha, tameva atthaṃ sādhukaṃ manasi kareyyāthā"ti ca <span class="note">[dī. ni. 1.263]</span> "neva mayhaṃ ayaṃ nāgo, alaṃ dukkhāya kāyacī"ti <span class="note">[jā. 2.22.870]</span> ca pāḷī.</p>\r\n<p class="bodytext">sabbaṃ cittaṃ.</p>\r\n<p class="bodytext"><a name="para209"></a><span class="paranum">209</span>. <span class="bld">sabbādīhi</span>.</p>\r\n<p class="bodytext">sabbādīhi nissa ṭā na hoti.</p>\r\n<p class="bodytext">sabbāni, sabbaṃ, sabbāni. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext">bahulādhikārā kvaci nissa ṭā, ṭepi honti. pāḷiyaṃ pana nissa ṭā, ṭepi dissanti- "yā pubbe bodhisattānaṃ, pallaṅkavaramābhuje. nimittāni padissanti, tāni ajja padissare <span class="note">[bu. vaṃ. 2.82]</span>. kiṃ māṇavassa ratanāni atthi, ye taṃ jinanto hare akkhadhutto"ti <span class="note">[jā. 2.22.1390]</span>. evaṃ katara, katamasaddāpi ñeyyā.</p>\r\n<p class="bodytext">ubhayasadde itthi, pumesu ubhayā, ubhayoti paṭhamekavacanarūpaṃ appasiddhaṃ. mahāvuttinā yonaṃ ṭo vā hoti, ubhayo itthiyo, ubhayaṃ itthiṃ, ubhayo itthiyo, ubhayāya, ubhayāhi, ubhayābhi. sesaṃ sabbasamaṃ.</p>\r\n<p class="bodytext">ubhayo purisā, ubhaye purisā, ubhayaṃ, ubhayo, ubhaye, ubhayena, ubhayehi, ubhayebhi, ubhayassa, ubhayesaṃ, ubhayesānaṃ. sabbasamaṃ.</p>\r\n<p class="bodytext">ubhayaṃ kulaṃ tiṭṭhati, ubhayāni, ubhayaṃ, ubhayāni. sabbasamaṃ. "ekarattena ubhayo, tuvañca dhanusekha ca <span class="note">[jā. 1.16.239]</span>, todeyya, kappā ubhayo, idhekarattiṃ ubhayo vasema, ubhaye devamanussā, ubhaye vasāmase"ti pāḷi.</p>\r\n<p class="bodytext"><a name="para210"></a><span class="paranum">210</span>. <span class="bld">ssaṃssāssāyesitarekaññetimānami</span> <span class="note">[ka. 63; rū. 217; nī. 210; ‘ssaṃssāssāyesvitarekaññebhimānami’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-209-210-');
INSERT INTO pages VALUES(54532,'annya_sadda_10',125,replace(replace('\r\n<p class="bodytext">ssamādīsu <a name="M0.0125"></a> itarā, ekā, aññā, etā, imāsaddānaṃ i hoti.</p>\r\n<p class="bodytext">itarissā kataṃ, itarissā deti, itarissā apeti, itarissā dhanaṃ, itarissā, itarissaṃ ṭhitaṃ. sesaṃ sabbasamaṃ.</p>\r\n<p class="bodytext">aññā, aññā, aññāyo, aññaṃ, aññā, aññāyo, aññāya, aññissā, aññāhi, aññābhi, aññāya, aññissā, aññāsaṃ, aññāsānaṃ, aññissā, aññāhi, aññābhi, aññāya, aññissā, aññāsaṃ, aññāsānaṃ, aññāya, aññissā, aññāyaṃ, aññissaṃ, aññāsu. sesaliṅgesu sabbasamaṃ.</p>\r\n<p class="bodytext">"aññatarissā itthiyā paṭibaddhacitto hotī"ti <span class="note">[pārā. 73]</span> pāḷi, idha suttavibhattena sijjhati. sesaṃ aññatara, aññatamesu sabbasamaṃ.</p>\r\n<p class="centered">iti sabbādiaṭṭhakarāsi.</p>\r\n<p class="bodytext">pubbā itthī, pubbā, pubbāyo, pubbaṃ, pubbā, pubbāyo, pubbāya, pubbassā, pubbāhi, pubbābhi, pubbāya, pubbassā, pubbāsaṃ, pubbāsānaṃ, sattamiyaṃ pubbāya, pubbassā, pubbāyaṃ, pubbassaṃ, pubbāsu.</p>\r\n<p class="bodytext"><a name="para211"></a><span class="paranum">211</span>. <span class="bld">pubbādīhi chahi</span> <span class="note">[ka. 164; rū. 200; nī. 347; caṃ. 2.1.15; pā. 1.1.34]</span>.</p>\r\n<p class="bodytext">tehi chahi yonaṃ eṭa hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-211-');
INSERT INTO pages VALUES(54533,'annya_sadda_10',126,replace(replace('\r\n<p class="bodytext">pubbe, pubbā, pare, parā, apare, aparā, dakkhiṇe, dakkhiṇā, uttare, uttarā, adhare, adharā. tattha ‘pubbe pubbā’ti puratthimadisābhāgā, tatraṭṭhakā vā atthā, purātanā vā sattā saṅkhārā ca. "pubbabuddhā, pubbadevā, pubbācariyā"tiādīsu "pubbe buddhā <a name="M0.0126"></a> pubbabuddhā, pubbā buddhā vā pubbabuddhā"tiādinā attho veditabbo. evaṃ sesesu.</p>\r\n<p class="bodytext">pubbesaṃ, pubbesānaṃ, paresaṃ, paresānaṃ, aparesaṃ, aparesānaṃ, dakkhiṇesaṃ, dakkhiṇesānaṃ, uttaresaṃ, uttaresānaṃ, adharesaṃ, adharesānaṃ. sesaṃ ñeyyaṃ.</p>\r\n<p class="bodytext">pubbādīhīti kiṃ? sabbe.</p>\r\n<p class="bodytext">chahīti kiṃ? ye, te.</p>\r\n<p class="bodytext"><a name="para212"></a><span class="paranum">212</span>. <span class="bld">nāññañca nāmappadhānā</span> <span class="note">[caṃ. 2.1.10; pā. 1.1.27-29]</span>.</p>\r\n<p class="bodytext">suddhanāmabhūtā ca samāse appadhānabhūtā ca sabbādito pubbe vuttaṃ sabbādikāriyaṃ aññañca upari vuccamānaṃ sabbādikāriyaṃ na hoti. tattha suddhanāmabhūtaṃ sabbādināma na jānātīti atthena bālavācako aññasaddo, ājānātīti atthena majjhemaggaphalañāṇavācako aññasaddo, arahattaphalañāṇavācako aññasaddo, ‘pubbo lohita’ntiādīsu pubbasaddo, atirekaparamādivācako parasaddo, disākālādito aññesu atthesu pavattā dakkhiṇu’ttarasaddā ca saṅkhyatthavācito añño ekasaddo cāti sabbametaṃ suddhanāmaṃ nāma, tato sabbādikāriyaṃ natthi.</p>\r\n<p class="bodytext">appadhāne diṭṭhapubba, gatapubba, piyapubba iccādi. tattha pubbe diṭṭho diṭṭhapubbo buddhā purisena. pubbe diṭṭho yenāti vā diṭṭhapubbo puriso buddhaṃ. evaṃ gatapubbo maggo purisena, gatapubbo vā puriso maggaṃ. piyā vuccati bhariyā, piyā pubbā purāṇā etassāti piyapubbo, piyo vuccati pati, piyo pubbo yassāti piyapubbā. etehi ca sabbādikāriyaṃ natthi.</p>\r\n<p class="bodytext"><a name="para213"></a><span class="paranum">213</span>. <span class="bld">tatiyatthayoge</span> <span class="note">[nī. 350; caṃ. 2.1.11; pā. 1.1.30]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-212-213-');
INSERT INTO pages VALUES(54534,'annya_sadda_10',127,replace(replace('\r\n<p class="bodytext">tatiyatthena <a name="M0.0127"></a> padena yoge sabbādikāriyaṃ natthi.</p>\r\n<p class="bodytext">māsena pubbānaṃ māsapubbānaṃ.</p>\r\n<p class="bodytext"><a name="para214"></a><span class="paranum">214</span>. <span class="bld">catthasamāse</span> <span class="note">[ka. 166; rū. 209; nī. 349; caṃ. 2.1.11; pā. 1.1.31]</span>.</p>\r\n<p class="bodytext">catthasamāso vuccati dvandasamāso, tasmiṃ sabbādikāriyaṃ natthi.</p>\r\n<p class="bodytext">dakkhiṇā ca uttarā ca pubbā ca dakkhiṇuttarapubbā, dakkhiṇuttarapubbānaṃ.</p>\r\n<p class="bodytext">cattheti kiṃ? dakkhiṇassā ca pubbassā ca yā antaradisāti dakkhiṇapubbā, dakkhiṇā ca sā pubbā cāti dakkhiṇapubbā, dakkhiṇapubbassā, dakkhiṇapubbassaṃ.</p>\r\n<p class="bodytext"><a name="para215"></a><span class="paranum">215</span>. <span class="bld">veṭa</span> <span class="note">[ka. 165; rū. 208; nī. 348; caṃ. 2.1.13; pā. 1.1.32]</span>.</p>\r\n<p class="bodytext">catthasamāse yonaṃ eṭa hoti vā.</p>\r\n<p class="bodytext">katarakatame, katarakatamā, itaritare, itaritarā, aññamaññe, aññamaññā, pubbapare, pubbaparā, pubbāpare, pubbāparā iccādi.</p>\r\n<p class="bodytext">imesu pubbādīsu smā, smiṃnaṃ ā, ettaṃ hoti, pubbā, pubbe, parā, pare, aparā, apare, dakkhiṇā, dakkhiṇe, uttarā, uttare, adharā, adhare.</p>\r\n<p class="centered">iti pubbādichakkarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-214-215-');
INSERT INTO pages VALUES(54535,'annya_sadda_10',128,replace(replace('\r\n<p class="bodytext">yā itthī, yā, yāyo, yaṃ, yā, yāyo, yāya, yassā, yāhi, yābhi, yāya, yassā, yāsaṃ, yāsānaṃ, yāya<a name="M0.0128"></a>, yassā, yāhi, yābhi, yāya, yassā, yāsaṃ, yāsānaṃ, yāya, yassā, yāyaṃ, yassaṃ, yāsu.</p>\r\n<p class="bodytext">yo puriso, ye, yaṃ, ye, yena, yehi, yebhi, yassa, yesaṃ, yesānaṃ, yasmā, yamhā, yehi, yebhi, yassa, yesaṃ, yesānaṃ, yasmiṃ, yamhi, yesu.</p>\r\n<p class="bodytext">yaṃ cittaṃ, yāni cittāni, yaṃ, yāni. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext"><a name="para216"></a><span class="paranum">216</span>. <span class="bld">tyatetānaṃ tassa so</span> <span class="note">[ka. 174; rū. 211; nī. 360]</span>.</p>\r\n<p class="bodytext">anapuṃsakānaṃtya, ta, etasaddānaṃ tabyañjanassa so hoti simhi. silopo.</p>\r\n<p class="bodytext">sā itthī, tā, tāyo, itthiyo, taṃ, tā, tāyo, tāya.</p>\r\n<p class="bodytext"><a name="para217"></a><span class="paranum">217</span>. <span class="bld">ssā vā tetimāmūhi</span> <span class="note">[ka. 179, 62; rū. 204, 206; nī. 365-6, 209]</span>.</p>\r\n<p class="bodytext">gha, pasaññehi tā, etā, imā, amusaddehi nādīnaṃ pañcannaṃ ekavacanānaṃ ssā hoti vā. rasso.</p>\r\n<p class="bodytext">tassā kataṃ, tāhi, tābhi, tāya, tassā.</p>\r\n<p class="bodytext"><a name="para218"></a><span class="paranum">218</span>. <span class="bld">tāssi vā</span> <span class="note">[ka. 64; rū. 216; nī. 211]</span>.</p>\r\n<p class="bodytext">ssaṃ, ssā, ssāyesu ghasaññassa tāsaddassa i hoti vā.</p>\r\n<p class="bodytext">tissā.</p>\r\n','\r',char(13)),'\n',char(10)),'-216-217-218-');
INSERT INTO pages VALUES(54536,'annya_sadda_10',129,replace(replace('\r\n<p class="bodytext"><a name="para219"></a><span class="paranum">219</span>. <span class="bld">tetimāto</span> <a name="M0.0129"></a> <span class="bld">sassa ssāya</span> <span class="note">[ka. 65; rū. 215; nī. 212]</span>.</p>\r\n<p class="bodytext">tā, etā, imāhi sassa ssāyādeso hoti vā.</p>\r\n<p class="bodytext">tassāya, tissāya, tāsaṃ, tāsānaṃ, tāya, tassā, tassāya, tissāya, tāsaṃ, tāsānaṃ, tāya, tāyaṃ, tassā, tassaṃ, tissā, tissaṃ, tāsu.</p>\r\n<p class="bodytext">so puriso, te purisā, taṃ, te, tena, tehi, tebhi, tassa, tesaṃ, tesānaṃ, tasmiṃ, tamhi, tesu.</p>\r\n<p class="bodytext">taṃ cittaṃ, tāni cittāni, taṃ, tāni. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext"><a name="para220"></a><span class="paranum">220</span>. <span class="bld">tassa no sabbāsu</span> <span class="note">[ka. 175; rū. 212; nī. 361]</span>.</p>\r\n<p class="bodytext">yvādīsu sabbāsu vibhattīsu tassa no hoti.</p>\r\n<p class="bodytext">ne purisā, naṃ, ne, nehi, nebhi, nesaṃ, nesānaṃ, nehi, nebhi, nesaṃ, nesānaṃ, namhi, nesu.</p>\r\n<p class="bodytext">ettha ca ‘sabbāsū’ti vuttepi yā yā vibhatti labbhati, taṃ taṃ ñatvā yojetabbā.</p>\r\n<p class="bodytext">naṃ cittaṃ, nehi, nebhi. pulliṅgasamaṃ.</p>\r\n<p class="bodytext"><a name="para221"></a><span class="paranum">221</span>. <span class="bld">ṭa sasmāsmiṃssāyassaṃssāsaṃmhāmhisvimassa ca</span> <span class="note">[ka. 176; rū. 213; nī. 362]</span>.</p>\r\n<p class="bodytext">sādīsu tassa ca imassa ca ṭa hoti vā.</p>\r\n<p class="bodytext">assā itthiyā kataṃ, assā, assāya deti. saṃmhi dīgho <span class="note">[nī. 368]</span> – āsaṃ itthīnaṃ, nāsaṃ kujjhanti paṇḍitā <span class="note">[jā. 1.1.65]</span>, assā apeti, assā, assāya dhanaṃ, āsaṃ dhanaṃ, "abhikkamo sānaṃ paññāyati, no paṭikkamo"ti <span class="note">[saṃ. ni. 5.196]</span> ettha ‘sāna’nti vedanānaṃ, mahāvuttinā tassa sattaṃ. assā, assaṃ ṭhitaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-219-220-221-');
INSERT INTO pages VALUES(54537,'annya_sadda_10',130,replace(replace('\r\n<p class="bodytext">assa <a name="M0.0130"></a> purisassa, āsaṃ purisānaṃ. nevāsaṃ kesā dissanti, hatthapādā ca jālino <span class="note">[jā. 2.22.2221]</span>. asmā, amhā, assa, āsaṃ, asmiṃ, amhi.</p>\r\n<p class="bodytext">assa cittassa. pulliṅgasamaṃ.</p>\r\n<p class="bodytext">esā itthī, etā, etāyo, etaṃ, etā, etāyo, etāya, etassā, etissā kataṃ.</p>\r\n<p class="bodytext">eso puriso, ete, etaṃ, ete, etena.</p>\r\n<p class="bodytext">etaṃ cittaṃ, etāni, etaṃ, etāni, etena. sabbaṃ tasaddasamaṃ ṭhapetvā nattaṃ, ṭattañca.</p>\r\n<p class="bodytext"><a name="para222"></a><span class="paranum">222</span>. <span class="bld">simhānapuṃsakassāyaṃ</span> <span class="note">[ka. 172; rū. 218; nī. 306-7; ‘simha.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">simhi napuṃsakato aññassa imassa ayaṃ hoti. silopo.</p>\r\n<p class="bodytext">ayaṃ itthī, imā, imāyo, imaṃ, imā, imāyo, imāya, imassā, imissā, imāhi, imābhi, imāya, imassā, imassāya, imissā, imissāya, imissaṃ, assā, assāya, imāsaṃ, imāsānaṃ, āsaṃ. pañcamīrūpaṃ tatiyāsamaṃ, chaṭṭhīrūpaṃ catutthīsamaṃ. imāya, imāyaṃ, imassā, imassāya, imassaṃ, imissā, imissāya, imissaṃ, assā, assaṃ, imāsu.</p>\r\n<p class="bodytext">ayaṃ puriso, ime, imaṃ, ime.</p>\r\n<p class="bodytext"><a name="para223"></a><span class="paranum">223</span>. <span class="bld">nāmhinimi</span> <span class="note">[ka. 171; rū. 219; nī. 357; ‘nāmhanimhi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">nāmhi anitthiliṅge imassa ana, imiādesā honti.</p>\r\n<p class="bodytext">iminā, anena, imehi, imebhi.</p>\r\n<p class="bodytext"><a name="para224"></a><span class="paranum">224</span>. <span class="bld">imassānitthiyaṃ ṭe</span> <span class="note">[ka. 170; rū. 220; nī. 356]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-222-223-224-');
INSERT INTO pages VALUES(54538,'annya_sadda_10',131,replace(replace('\r\n<p class="bodytext">anitthiliṅge <a name="M0.0131"></a> imassa ṭe hoti vā su, naṃ, hisu.</p>\r\n<p class="bodytext">ehi, ebhi, imassa, assa, imesaṃ, imesānaṃ, esaṃ, esānaṃ, imasmā, imamhā, asmā, amhā, imehi, imebhi, ehi, ebhi, imassa, assa, imesaṃ, imesānaṃ, esaṃ, esānaṃ, imasmiṃ, imamhi, asmiṃ, amhi, imesu, esu.</p>\r\n<p class="bodytext">"anamhi bhadde susoṇe, kinnu jagghasi sobhane"ti <span class="note">[jā. 1.5.130 (anamhi kāle susoṇi)]</span> pāḷi- ‘anamhī’ti imasmiṃ ṭhāne, mahāvuttinā smiṃmhi anādeso.</p>\r\n<p class="bodytext">imaṃ cittaṃ.</p>\r\n<p class="bodytext"><a name="para225"></a><span class="paranum">225</span>. <span class="bld">imassidaṃ vā</span> <span class="note">[ka. 129; rū. 222; nī. 305]</span>.</p>\r\n<p class="bodytext">napuṃsake aṃ, sisu imassa tehi aṃ, sīhi saha idaṃ hoti vā.</p>\r\n<p class="bodytext">idaṃ cittaṃ, imāni cittāni, imaṃ, idaṃ, imāni, iminā, anena. sabbaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext">idha missakarūpaṃ vuccati –</p>\r\n<p class="bodytext">yā, sā itthī, yā, tā itthiyo, yaṃ, taṃ itthiṃ, yā, esā itthī, yā, etā itthiyo, yaṃ, etaṃ itthiṃ, yā, ayaṃ itthī, yā, imā itthiyo, yaṃ, imaṃ itthiṃ, yo, so puriso, ye, te purisāiccādayo.</p>\r\n','\r',char(13)),'\n',char(10)),'-225-');
INSERT INTO pages VALUES(54539,'annya_sadda_10',132,replace(replace('\r\n<p class="bodytext">"sa kho so kumāro vuddhimanvāyā"ti ettha so so kumāroti, ‘ese se eke ekatthe’ti ettha eso so eko ekatthoti vattabbaṃ. tattha pubbaṃ pubbaṃ atthapadaṃ, paraṃ paraṃ byañjanamattaṃ. "ayaṃ so sārathi etī"ti <span class="note">[jā. 2.22.51]</span> ettha pana dvepi visuṃ <a name="M0.0132"></a> visuṃ atthapadāni evāti. yaṃ, taṃ, idanti ime saddā nipātarūpāpi hutvā pāḷivākyesu sañcaranti sabbaliṅgavibhattīsu abhinnarūpāti.</p>\r\n<p class="bodytext"><a name="para226"></a><span class="paranum">226</span>. <span class="bld">imetānamenānvādese dutiyāyaṃ</span> <span class="note">[nī. 375-6 piṭṭhe; pā. 2.4.34]</span>.</p>\r\n<p class="bodytext">anvādeso vuccati anukathanaṃ, punakathanaṃ, anvādesaṭhāne ima, etānaṃ enādeso hoti dutiyāvibhattīsu.</p>\r\n<p class="bodytext">imaṃ bhikkhuṃ vinayaṃ ajjhāpehi, atho enaṃ bhikkhuṃ dhammaṃ ajjhāpehi, ime bhikkhū vinayaṃ ajjhāpehi, atho ene bhikkhū dhammaṃ ajjhāpehi, etaṃ bhikkhuṃ vinayaṃ ajjhāpehiiccādinā vattabbaṃ. tamenaṃ bhikkhave nirayapālā <span class="note">[a. ni. 3.36]</span>, yatvādhikaraṇamenaṃ bhikkhuṃ iccādīsupi <span class="note">[dī. ni. 1.213]</span> anukathanameva.</p>\r\n<p class="bodytext"><a name="para227"></a><span class="paranum">227</span>. <span class="bld">massāmussa</span> <span class="note">[ka. 173; rū. 223; nī. 359]</span>.</p>\r\n<p class="bodytext">simhi anapuṃsakassa amussa massa so hoti.</p>\r\n<p class="bodytext">asu itthī, amu vā, amū, amuyo, amuṃ, amū, amuyo, amuyā, amussā, amūhi, amūbhi, amuyā, amussā, amūsaṃ, amūsānaṃ, amuyā, amussā, amūhi, amūbhi, amuyā, amussā, amūsaṃ, amūsānaṃ, amuyā, amuyaṃ, amussā, amussaṃ, amūsu.</p>\r\n<p class="bodytext">asu puriso, amu vā.</p>\r\n<p class="bodytext"><a name="para228"></a><span class="paranum">228</span>. <span class="bld">lopomusmā</span> <span class="note">[ka. 118; rū. 146; nī. 293]</span>.</p>\r\n<p class="bodytext">amuto yonaṃ lopo hoti. vo, nopavādoyaṃ <span class="note">[ka. 119; rū. 155; nī. 294]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-226-227-228-');
INSERT INTO pages VALUES(54540,'annya_sadda_10',133,replace(replace('\r\n<p class="bodytext">amū<a name="M0.0133"></a>, amuṃ, amū, amunā, amūhi, amūbhi.</p>\r\n<p class="bodytext"><a name="para229"></a><span class="paranum">229</span>. <span class="bld">na no sassa</span>.</p>\r\n<p class="bodytext">amuto sassa no na hoti.</p>\r\n<p class="bodytext">amussa.</p>\r\n<p class="bodytext">mahāvuttinā samhi mussa duttaṃ, adussa. pāḷiyaṃ "dussa me khettapālassa, rattiṃ bhattaṃ apābhata"nti <span class="note">[jā. 1.4.62]</span> ettha gāthāvasena a-kāralopo. amūsaṃ, amūsānaṃ, amusmā, amumhā, amūhi, amūbhi, amussa, adussa, amūsaṃ, amūsānaṃ, amusmiṃ, amumhi, amūsu.</p>\r\n<p class="bodytext"><a name="para230"></a><span class="paranum">230</span>. <span class="bld">amussāduṃ</span> <span class="note">[ka. 130; rū. 225; nī. 308]</span>.</p>\r\n<p class="bodytext">napuṃsake aṃ, sisu amussa tehi saha aduṃ hoti vā.</p>\r\n<p class="bodytext">amuṃ cittaṃ, aduṃ cittaṃ, amūni, amuṃ, aduṃ, amūni. sesaṃ pulliṅgasamaṃ. ‘sakatthe’ti suttena kapaccaye kate sabbādirūpaṃ natthi. amukā kaññā, amukā, amukāyo. amuko puriso, amukā purisā. amukaṃ cittaṃ, amukāni cittāni iccādi.</p>\r\n<p class="bodytext"><a name="para231"></a><span class="paranum">231</span>. <span class="bld">ke vā</span>.</p>\r\n<p class="bodytext">ke pare amussa massa so hoti vā.</p>\r\n<p class="bodytext">asukā itthī, asukā, asukāyo. asuko puriso, asukā purisā. asukaṃ kulaṃ, asukāni kulāni. sabbaṃ kaññā, purisa, cittasamaṃ.</p>\r\n<p class="bodytext">‘itthiyamatvā’ti ettha ‘itthiyaṃ ā’ti vibhattasuttena kiṃsaddato itthiyaṃ āpaccayo.</p>\r\n<p class="bodytext"><a name="para232"></a><span class="paranum">232</span>. <span class="bld">kiṃssa ko</span> <span class="note">[ka. 227-9; rū. 270, 226; nī. 456-7-8? ‘kissa ko sabbāsu’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-229-230-231-232-');
INSERT INTO pages VALUES(54541,'annya_sadda_10',134,replace(replace('\r\n<p class="bodytext">sabbesu <a name="M0.0134"></a> vibhattipaccayesu kiṃssa ko hoti.</p>\r\n<p class="bodytext">kā itthī, kā, kāyo, kaṃ, kā, kāyo, kāya, kassā iccādi sabbasamaṃ. ko puriso, ke purisā, kaṃ, ke, kena, kehi, kebhi, kassa.</p>\r\n<p class="bodytext"><a name="para233"></a><span class="paranum">233</span>. <span class="bld">ki sasmiṃsu vānitthiyaṃ</span>.</p>\r\n<p class="bodytext">anitthiliṅge sa, smiṃsu kiṃsaddassa ki hoti vā.</p>\r\n<p class="bodytext">kissa, kesaṃ, kesānaṃ, kasmā, kamhā, kehi, kebhi, kassa, kissa, kesaṃ, kesānaṃ, kasmiṃ, kamhi, kismiṃ, kimhi, kesu.</p>\r\n<p class="bodytext"><a name="para234"></a><span class="paranum">234</span>. <span class="bld">kimaṃsisu napuṃsake</span> <span class="note">[‘kimaṃsisu saha napuṃsake’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">napuṃsake aṃ, sisu kiṃsaddassa tehi aṃsīhi saha kiṃ hoti.</p>\r\n<p class="bodytext">kiṃ cittaṃ, kāni, kiṃ, kaṃ vā, kāni. sesaṃ pulliṅgasamaṃ. idaṃ pucchanatthassa suddhakiṃsaddassa rūpaṃ.</p>\r\n<p class="bodytext">‘ci’itinipātena yutte pana ekaccatthaṃ vā appatthaṃ vā vadati. kāci itthī, kāci itthiyo, kiñci itthiṃ, kāci, kāyaci, kāhici, kāyaci, kassāci, kāsañci, kutoci, kāhici. sattamiyaṃ - kāyaci, katthaci, kāsuci.</p>\r\n<p class="bodytext">koci puriso, keci, kiñci, keci, kenaci, kehici, kassaci, kesañci, kismiñci, kimhici, katthaci, kesuci.</p>\r\n<p class="bodytext">kiñci kulaṃ, kānici kulāni, kiñci, kānici. sesaṃ pulliṅgasamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-233-234-');
INSERT INTO pages VALUES(54542,'annya_sadda_10',135,replace(replace('\r\n<p class="bodytext">puna <a name="M0.0135"></a> yasaddena yutte sakalatthaṃ vadati. yā kāci itthī, yākāci itthiyo.</p>\r\n<p class="bodytext">yo koci puriso, ye keci, yaṃ kiñci, ye keci yena kenaci, yehi kehici, yassa kassaci, yesaṃ kesañci yato kutoci, yehi kehici, yassa kassaci, yesaṃkesañci, yasmiṃ kismiñci, yamhi kimhici, yattha katthaci, yesu kesuci.</p>\r\n<p class="bodytext">yaṃ kiñcicittaṃ, yāni kānici, yaṃ kiñci, yāni kānici. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="subhead">saṅkhyārāsi</p>\r\n<p class="bodytext">ekasaddo saṅkhyatthe pavatto ekavacanantova, aññatthe pavatto ekabahuvacananto.</p>\r\n<p class="bodytext">tattha saṅkhyatthe – ekā itthī, ekaṃ, ekāya, ekissā iccādi. punnapuṃsakesu ekavacanesu purisa, cittarūpameva.</p>\r\n<p class="bodytext">aññatthe – ekā itthī, ekā itthiyo, ekaṃ, ekā, ekāya, ekissā, ekāhi, ekābhi iccādi.</p>\r\n<p class="bodytext">eko puriso, eke, ekaṃ, eke, ekena, ekehi, ekebhi, ekassa, ekesaṃ, ekesānaṃ. pulliṅga sabbasamaṃ.</p>\r\n<p class="bodytext">ekaṃ kulaṃ, ekāni kulāni, ekaṃ kulaṃ, ekāni kulāni. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext">kapaccaye pare sabbādirūpaṃ natthi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54543,'annya_sadda_10',136,replace(replace('\r\n<p class="bodytext">"ekikā sayane setu, yā te ambe avāhari <span class="note">[jā. 1.4.175]</span>. ekākinī gahaṭṭhāhaṃ, mātuyā paricoditā"ti <span class="note">[apa. therī 2.3.188]</span> pāḷi, ekako puriso, ekakaṃ, ekakena. ekakaṃ kulaṃ iccādi ekavacanantameva, ekakānaṃ bahutte vattabbe dve ekakā, dve ekake<a name="M0.0136"></a>, dvīhi ekakehīti labbhati. "pañcālo ca videho ca, ubho ekā bhavantu te"ti pāḷi. iminā nayena bahuvacanampi labbhati. ‘ekā’ti missakā.</p>\r\n<p class="bodytext">paṭisedhayutte pana anekā itthiyo, anekāsaṃ itthīnaṃ. aneke purisā, anekesaṃ purisānaṃ. anekāni kulāni, anekesaṃ kulānaṃ. pāḷiyaṃ pana "nekāni dhaññagaṇāni, nekāni khettagaṇāni, nekānaṃ dhaññagaṇānaṃ, nekānaṃ khettagaṇāna"ntipi atthi.</p>\r\n<p class="bodytext">ekacca, ekacciya, kati, bahusaddāpi idha vattabbā. ekaccā itthī, ekaccā, ekaccāyoti sabbaṃ kaññāsamaṃ.</p>\r\n<p class="bodytext">ekacco puriso.</p>\r\n<p class="bodytext"><a name="para135"></a><span class="paranum">135</span>. <span class="bld">ekaccādīhyato</span> <span class="note">[‘ekaccādīhato’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">akārantehi ekaccādīhi yonaṃ ṭe hoti.</p>\r\n<p class="bodytext">ekacce purisā, ekacce purise. sesaṃ purisasamaṃ. ādisaddena appekacca, ekatiya, ubhādayo saṅgayhanti. appekacce purisā, ekatiye purisā, ubhe purisā.</p>\r\n<p class="bodytext">ekaccaṃ cittaṃ.</p>\r\n<p class="bodytext"><a name="para236"></a><span class="paranum">236</span>. <span class="bld">na nissa ṭā</span>.</p>\r\n<p class="bodytext">ekaccādīhi nissa ṭā na hoti.</p>\r\n<p class="bodytext">ekaccāni cittāni. sesaṃ cittasamaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-135-236-');
INSERT INTO pages VALUES(54544,'annya_sadda_10',137,replace(replace('\r\n<p class="bodytext">ekacciya, ekacceyya, ekatiyasaddā kaññā, purisa, cittanayā. "itthīpi hi ekacciyā, seyyā posa janādhipa <span class="note">[saṃ. ni. 1.127]</span>. saccaṃ kirevamāhaṃsu, narā ekacciyā idha. kaṭṭhaṃ niplavitaṃ seyyo, na tvevekacciyo <a name="M0.0137"></a> naro"ti <span class="note">[jā. 1.1.73]</span> ca "parivāritā muñcare ekacceyyā"ti ca "na vissase ekatiyesū"ti ca pāḷī – tattha ‘niplavita’nti udakato ubbhataṃ.</p>\r\n<p class="bodytext">katisaddo bahuvacanantova.</p>\r\n<p class="bodytext"><a name="para237"></a><span class="paranum">237</span>. <span class="bld">ṭikatimhā</span> <span class="note">[rū. 120 piṭṭhe]</span>.</p>\r\n<p class="bodytext">katimhā yonaṃ ṭi hoti.</p>\r\n<p class="bodytext">kati itthiyo, kati purisā, kati purise, kati cittāni. katihi itthīhi, katihi purisehi, katihi cittehi.</p>\r\n<p class="bodytext"><a name="para238"></a><span class="paranum">238</span>. <span class="bld">bahukatīnaṃ</span> <span class="note">[‘bahu katinnaṃ’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">naṃmhi bahu, katīnaṃ ante nuka hoti.</p>\r\n<p class="bodytext">katinnaṃ itthīnaṃ, katinnaṃ purisānaṃ, katinnaṃ cittānaṃ, ayaṃ nāgamo bahulaṃ na hoti, ‘katinaṃ tithīnaṃ pūraṇī katimī’ti ca dissati. "bahūnaṃ vassasatānaṃ, bahūnaṃ vassasahassāna"nti ca "bahūnaṃ kusaladhammānaṃ, bahūnaṃ akusaladhammāna"nti ca "bahūnaṃ vata atthāya, uppajjiṃsu tathāgatā"ti <span class="note">[vi. va. 807]</span> ca pāḷī.</p>\r\n<p class="bodytext">katisu itthīsu, katisu purisesu, katisu cittesu.</p>\r\n<p class="bodytext">bahusadde dvīsu naṃvacanesu bahunnaṃ, bahunnanti vattabbaṃ. sesaṃ dhenu, bhikkhu, āyusadisaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-237-238-');
INSERT INTO pages VALUES(54545,'annya_sadda_10',138,replace(replace('\r\n<p class="bodytext">kapaccaye kaññā, purisa, cittasadisaṃ, bahū itthiyo, bahukā itthiyo. bahū purisā, bahavo purisā, bahukā purisā. bahūni cittāni, bahukāni cittāni iccādinā vattabbaṃ. bahūnaṃ <a name="M0.0138"></a> samudāyāpekkhane sati ekavacanampi labbhati, "bahujanassa atthāya bahujanassa hitāya, bahuno janassa atthāya hitāyā"ti <span class="note">[a. ni. 1.141]</span> pāḷi.</p>\r\n<p class="bodytext">ubhasaddo bahuvacanantova, ‘ubhagohi ṭo’ti yonaṃ ṭo, ubho itthiyo, purisā, kulāni gacchanti, ubho itthiyo, purisā, kulāni passati.</p>\r\n<p class="bodytext"><a name="para239"></a><span class="paranum">239</span>. <span class="bld">suhisubhasso</span> <span class="note">[nī. 313 (rū. 109 piṭṭhe)]</span>.</p>\r\n<p class="bodytext">su, hisu ubhassa anto o hoti.</p>\r\n<p class="bodytext">ubhohi, ubhosu.</p>\r\n<p class="bodytext"><a name="para240"></a><span class="paranum">240</span>. <span class="bld">ubhinnaṃ</span> <span class="note">[ka. 86; nīrū. 227; nī. 341]</span>.</p>\r\n<p class="bodytext">ubhamhā naṃvacanassa innaṃ hoti.</p>\r\n<p class="bodytext">ubhinnaṃ. sabbattha itthi, purisa, kulehi yojetabbaṃ.</p>\r\n<p class="bodytext"><a name="para241"></a><span class="paranum">241</span>. <span class="bld">yomhi dvinnaṃ duvedve</span> <span class="note">[ka. 132; rū. 228; ni. 310]</span>.</p>\r\n<p class="bodytext">yosu savibhattissa dvissa duve, dve honti. ‘dvinna’nti vacanaṃ dvissa bahuvacanantaniyamatthaṃ.</p>\r\n<p class="bodytext">dve itthiyo, dve purisā, dve purise, dve cittāni, duve itthiyo, duve purisā, duve purise, duve cittāni, dvīhi, dvībhi.</p>\r\n<p class="bodytext"><a name="para242"></a><span class="paranum">242</span>. <span class="bld">naṃmhi nuka dvādīnaṃ sattarasannaṃ</span> <span class="note">[ka. 67; nī. 229; nī. 214]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-239-240-241-242-');
INSERT INTO pages VALUES(54546,'annya_sadda_10',139,replace(replace('\r\n<p class="bodytext">naṃmhi <a name="M0.0139"></a> pare dvādīnaṃ aṭṭhārasantānaṃ sattarasannaṃ saṅkhyānaṃ ante nuka hoti. u-kāro uccāraṇattho. kānubandhaṃ disvā anteti ñāyati.</p>\r\n<p class="bodytext">dvinnaṃ.</p>\r\n<p class="bodytext"><a name="para243"></a><span class="paranum">243</span>. <span class="bld">duvinnaṃ naṃmhi</span> <span class="note">[ka. 132; rū. 228; nī. 244]</span>.</p>\r\n<p class="bodytext">naṃmhi savibhattissa dvissa duvinnaṃ hoti vā.</p>\r\n<p class="bodytext">duvinnaṃ, dvīhi, dvībhi, dvinnaṃ, duvinnaṃ, dvīsu. mahāvuttinā sumhi duve hoti, nāgassa duvesu dantesu nimmitā <span class="note">[vi. va. 706]</span>, cakkāni pādesu duvesu vindati <span class="note">[dī. ni. 3.205]</span>. evañca sati duvehi, duvebhītipi siddhameva hoti, ayaṃ dvisaddo ubhasaddo viya aliṅgo.</p>\r\n<p class="bodytext"><a name="para244"></a><span class="paranum">244</span>. <span class="bld">tisso catasso yomhi savibhattīnaṃ</span> <span class="note">[ka. 133; rū. 230; nī. 311]</span>.</p>\r\n<p class="bodytext">itthiyaṃ yosu savibhattīnanti, catunnaṃ tisso, catasso honti.</p>\r\n<p class="bodytext">tisso itthiyo, catasso itthiyo.</p>\r\n<p class="bodytext">mahāvuttinā hisu ca tissa, catassā honti, "tissehi catassehi parisāhi, catassehi sahito lokanāyako"ti pāḷī. tīhi, tībhi itthīhi, catūhi, catūbhi, catubbhi itthīhi.</p>\r\n<p class="bodytext"><a name="para245"></a><span class="paranum">245</span>. <span class="bld">naṃmhi ticatunnamitthiyaṃ tissacatassā</span> <span class="note">[dī. ni. 3.205]</span>.</p>\r\n<p class="bodytext">itthiyaṃ naṃmhiti, catunnaṃ tissa, catassā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-243-244-245-');
INSERT INTO pages VALUES(54547,'annya_sadda_10',140,replace(replace('\r\n<p class="bodytext">tissannaṃ <a name="M0.0140"></a> itthīnaṃ, catassannaṃ itthīnaṃ, tiṇṇaṃ itthīnaṃ, catunnaṃ itthīnaṃ, samaṇo gotamo catunnaṃ parisānaṃ sakkato hoti, catunnaṃ parisānaṃ piyo hoti manāpoti <span class="note">[dī. ni. 1.304]</span>, tissehi, catassehi, tīhi, tībhi, catūhi, catūbhi, catubbhi, tissannaṃ, catassannaṃ, tiṇṇaṃ, catunnaṃ, tīsu, catūsu.</p>\r\n<p class="bodytext">pāḷiyaṃ "catassehī"ti diṭṭhattā tissesu, catassesūtipi diṭṭhameva hoti.</p>\r\n<p class="bodytext"><a name="para246"></a><span class="paranum">246</span>. <span class="bld">pume tayo cattāro</span> <span class="note">[ka. 133; rū. 230; nī. 311]</span>.</p>\r\n<p class="bodytext">pulliṅge yosu savibhattīnanti, catunnaṃ tayo, cattāro honti.</p>\r\n<p class="bodytext">tayo purisā, tayo purise, cattāro purisā, cattāro purise.</p>\r\n<p class="bodytext"><a name="para247"></a><span class="paranum">247</span>. <span class="bld">caturo catussa</span> <span class="note">[ka. 78, 205, 31; rū. 160; nī. 234; ‘caturo vā catussa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">pume savibhattissa catusaddassa caturo hoti.</p>\r\n<p class="bodytext">caturo purisā, caturo purise. kathaṃ caturo nimitte nādassiṃ, caturo phalamuttameti? "liṅgavipallāsā"ti vuttiyaṃ vuttaṃ, tīhi, tībhi, catūhi, catūbhi, catubbhi.</p>\r\n<p class="bodytext"><a name="para248"></a><span class="paranum">248</span>. <span class="bld">iṇṇaṃiṇṇannaṃ tito jhā</span> <span class="note">[ka. 87; rū. 231; nī. 243; ‘ṇṇaṃṇṇannaṃtiko jhā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">jhasaññamhā timhā naṃvacanassa iṇṇaṃ, iṇṇannaṃ honti.</p>\r\n<p class="bodytext">tiṇṇaṃ, tiṇṇannaṃ, catunnaṃ, tīhi, tībhi, catūhi, catūbhi, catubbhi, tiṇṇaṃ, tiṇṇannaṃ, catunnaṃ, tīsu, catūsu.</p>\r\n<p class="bodytext"><a name="para249"></a><span class="paranum">249</span>. <span class="bld">tīṇicattāri napuṃsake</span> <span class="note">[ka. 133; rū. 230; nī. 311]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-246-247-248-249-');
INSERT INTO pages VALUES(54548,'annya_sadda_10',141,replace(replace('\r\n<p class="bodytext">napuṃsake <a name="M0.0141"></a> yosu savibhattīnanti, catunnaṃ tīṇi, cattāri honti.</p>\r\n<p class="bodytext">tīṇi cittāni, cattāri cittāni. sesaṃ pulliṅgasamaṃ.</p>\r\n<p class="bodytext">vacanasiliṭṭhatte pana sati visadisaliṅgavacanānampi padānaṃ aññamaññasaṃyogo hoti, cattāro satipaṭṭhānā <span class="note">[dī. ni. 3.145]</span>, cattāro sammappadhānā <span class="note">[dī. ni. 3.145]</span>, tayomahābhūtā, tayo mahābhūte <span class="note">[paṭṭhā. 1.1.58]</span>, sabbe mālā upenti maṃ <span class="note">[dhu. 3.6]</span>, sabbe kaññā upenti maṃ <span class="note">[dhu. 3.6]</span>, sabbe ratanā upenti maṃ <span class="note">[dhu. 3.6]</span>, sabbe yānā upenti maṃ <span class="note">[dhu. 3.6]</span>, avijjāya sati saṅkhārā honti, saṅkhāresu sati viññāṇaṃ hoti <span class="note">[saṃ. ni. 2.50]</span> iccādi.</p>\r\n<p class="bodytext">gāthāsu vipallāsāpi bahulaṃ dissanti, aññe dhammāni desenti, evaṃ dhammāni sutvāna, satañca dhammāni sukittitāni sutvā, atthāni cintayitvāna, uttamatthāni tayi labhimhā, kiṃ tvaṃ atthāni jānāsi, iccheyyāmi bhante sattaputtāni, siviputtāni avhaya <span class="note">[jā. 2.22.2235]</span>, puttadārāni posenti, balībaddāni soḷasa iccādi.</p>\r\n<p class="bodytext">idha sesasaṅkhyānāmāni dīpiyante.</p>\r\n<p class="bodytext"><a name="para250"></a><span class="paranum">250</span>. <span class="bld">ṭa pañcādīhi cuddasahi</span> <span class="note">[ka. 134; rū. 251; nī. 247]</span>.</p>\r\n<p class="bodytext">pañcādīhi aṭṭhārasantehi saṅkhyāsaddehi yonaṃ ṭa hoti.</p>\r\n<p class="bodytext">pañca itthiyo, pañca purisā, purise, pañca cittāni, cha itthiyo.</p>\r\n','\r',char(13)),'\n',char(10)),'-250-');
INSERT INTO pages VALUES(54549,'annya_sadda_10',142,replace(replace('\r\n<p class="bodytext">ḷāgame <a name="M0.0142"></a> pana "itthibhāvā na muccissaṃ, chaḷāni gatiyo imā"ti pāḷi.</p>\r\n<p class="bodytext">cha purisā, cha purise, cha cittāni. evaṃ satta, aṭṭha, nava, dasa, ekādasa.pe.. aṭṭhārasa.</p>\r\n<p class="bodytext"><a name="para251"></a><span class="paranum">251</span>. <span class="bld">pañcādīnaṃ cuddasannama</span> <span class="note">[ka. 90; rū. 252; nī. 247]</span>.</p>\r\n<p class="bodytext">su, naṃ, hisu pañcādīnaṃ cuddasannaṃ assa attameva hoti, na ettaṃ vā dīghattaṃ vā hoti.</p>\r\n<p class="bodytext">pañcahi, pañcannaṃ, pañcasu, chahi, channaṃ, chasu, sattahi, sattannaṃ, sattasu, aṭṭhahi, aṭṭhannaṃ, aṭṭhasu, navahi, navannaṃ, navasu, dasahi, dasannaṃ, dasasu, ekādasahi, ekādasannaṃ, ekādasasu.pe.. aṭṭhārasahi, aṭṭhārasannaṃ, aṭṭhārasasu.</p>\r\n<p class="bodytext">ete sabbe aliṅgā bahuvacanantā eva.</p>\r\n<p class="bodytext">‘itthiyamatvā’ti vīsa, tiṃsa, cattālīsa, paññāsehi āpaccayo, mahāvuttinā simhi rasso silopo ca, ‘niggahīta’nti vikappena niggahītāgamo, vikappena aṃlopo, nādīnaṃ ekavacanānaṃ yādeso, vīsa itthiyo, vīsaṃ itthiyo, vīsa purisā, vīsaṃ purisā, vīsa purise, vīsaṃ purise, vīsa cittāni, vīsaṃ cittāni, vīsāya itthīhi kammaṃ kataṃ, vīsāya purisehi kammaṃ kataṃ, vīsāya kulehi kammaṃ kataṃ, vīsāya itthīnaṃ, purisānaṃ, kulānaṃ, sattamiyaṃ vīsāya itthīsu, purisesu, kulesu.</p>\r\n','\r',char(13)),'\n',char(10)),'-251-');
INSERT INTO pages VALUES(54550,'annya_sadda_10',143,replace(replace('\r\n<p class="bodytext">tipaccaye vīsati, tiṃsatisaddāpi saṭṭhi, sattati, asīti, navutisaddā viya niccaṃ itthi liṅgekavacanantā eva, si, aṃlopo, vīsati itthiyo, vīsati purisā, purise, vīsati kulāni, vīsatiyā itthīhi, itthīnaṃ, purisehi, purisānaṃ, kulehi, kulānaṃ<a name="M0.0143"></a>, vīsatiyā, vīsatiyaṃ itthi, purisa, kulesu, evaṃ yāvanavutiyā veditabbā. vaggabhede pana sati bahuvacanampi vikappena dissati, dve vīsatiyo iccādi.</p>\r\n<p class="bodytext">sataṃ, sahassaṃ, dasasahassaṃ, satasahassaṃ, dasasatasahassanti ime napuṃsakaliṅgāyeva. saṅkhyeyyapadhāne pana itthiliṅge vattabbe sahassī, dasasahassī, satasahassīti itthiliṅgaṃ bhavati. vaggabhede pana dve satāni, tīṇi satāni, dve sahassāni, tīṇi sahassāni iccādīni bhavanti. koṭi, pakoṭi, koṭipakoṭi, akkhobhiṇīsaddā itthiliṅgā eva. sesaṃ sabbaṃ yāvaasaṅkhyeyyā napuṃsakameva.</p>\r\n<p class="bodytext">sahassaṃ <span class="bld">kāsi</span> nāma, dasasahassaṃ <span class="bld">nahutaṃ</span> nāma, satasahassaṃ <span class="bld">lakkhaṃ</span> nāma.</p>\r\n<p class="bodytext">duvidhaṃ padhānaṃ saṅkhyāpadhānaṃ, saṅkhyeyyapadhānañca. purisānaṃ vīsati hoti, purisānaṃ navuti hoti, purisānaṃ sataṃ hoti, sahassaṃ hoti iccādi <span class="bld">saṅkhyāpadhānaṃ</span> nāma, vīsati purisā, navuti purisā, sataṃ purisā, sahassaṃ purisā iccādi <span class="bld">saṅkhyeyyapadhānaṃ</span> nāma.</p>\r\n<p class="bodytext">etthapi vīsatisaddo itthiliṅgekavacano eva. sata, sahassasaddā napuṃsakekavacanā eva. saṅkhyāsaddānaṃ pana padavidhānañca guṇavidhānañca samāsakaṇḍe āgamissati.</p>\r\n<p class="centered">saṅkhyārāsi niṭṭhito.</p>\r\n<p class="bodytext"><a name="para252"></a><span class="paranum">252</span>. <span class="bld">simhāhaṃ</span> <span class="note">[ka. 149; rū. 232; nī. 319; ‘simhahaṃ’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">simhi savibhattissa amhassa ahaṃ hoti.</p>\r\n<p class="bodytext">ahaṃ gacchāmi.</p>\r\n<p class="bodytext"><a name="para253"></a><span class="paranum">253</span>. <span class="bld">mayamasmāmhassa</span> <span class="note">[ka. 121; rū. 233; nī. 296]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-252-253-');
INSERT INTO pages VALUES(54551,'annya_sadda_10',144,replace(replace('\r\n<p class="bodytext">yosu <a name="M0.0144"></a> savibhattissa amhassa kamena mayaṃ, asmā honti vā.</p>\r\n<p class="bodytext">mayaṃ gacchāma, asme passāmi.</p>\r\n<p class="bodytext">pakkhe –</p>\r\n<p class="bodytext">‘yonameṭa’ iti vidhi, amhe gacchāma.</p>\r\n<p class="bodytext"><a name="para254"></a><span class="paranum">254</span>. <span class="bld">tumhassa tuvaṃtvaṃmhi ca</span> <span class="note">[ka. 146; rū. 236; nī. 324; ‘tumhassa tuvaṃtvamamhica’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">simhi ca aṃmhi ca savibhattissa tumhassa tuvaṃ, tvaṃ honti.</p>\r\n<p class="bodytext">tuvaṃ buddhā tuvaṃ satthā, tuvaṃ mārābhibhū muni <span class="note">[theragā. 839]</span>, tvaṃ no satthā anuttaro, tumhe gacchatha, tuvaṃ passati, tvaṃ passati.</p>\r\n<p class="bodytext"><a name="para255"></a><span class="paranum">255</span>. <span class="bld">aṃmhi taṃ maṃ tavaṃ mamaṃ</span> <span class="note">[ka. 143-4; rū. 234-5; nī. 322]</span>.</p>\r\n<p class="bodytext">aṃmhi savibhattīnaṃ tumhā’mhānaṃ taṃ, maṃ, tavaṃ, mamaṃ honti.</p>\r\n<p class="bodytext">maṃ passati, mamaṃ passati, taṃ passati, tavaṃ passati, amhe passati, tumhe passati.</p>\r\n<p class="bodytext"><a name="para256"></a><span class="paranum">256</span>. <span class="bld">dutiyāyomhi vā</span> <span class="note">[ka. 162; rū. 237; nī. 345; ‘dutiye yomhi vā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">dutiyāyomhi savibhattīnaṃ tumhā’mhānaṃ ṅānubandhā aṃ, ākaṃādesā honti vā.</p>\r\n<p class="bodytext">amhaṃ, amhākaṃ passati, tumhaṃ, tumhākaṃ passati.</p>\r\n<p class="bodytext"><a name="para257"></a><span class="paranum">257</span>. <span class="bld">nāsmāsu tayāmayā</span> <span class="note">[ka. 145, 270; rū. 238, 120; nī. 323, 542]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-254-255-256-257-');
INSERT INTO pages VALUES(54552,'annya_sadda_10',145,replace(replace('\r\n<p class="bodytext">nā<a name="M0.0145"></a>, smāsu savibhattīnaṃ tumhā’mhānaṃ tayā, mayā honti.</p>\r\n<p class="bodytext">mayā kataṃ, tayā kataṃ, mayā apeti, tayā apeti.</p>\r\n<p class="bodytext"><a name="para258"></a><span class="paranum">258</span>. <span class="bld">tayātayīnaṃ tva vā tassa</span> <span class="note">[ka. 210; rū. 239; nī. 435]</span>.</p>\r\n<p class="bodytext">tayā, tayīnaṃ tassa tva hoti vā.</p>\r\n<p class="bodytext">tvayā kataṃ, tvayā apeti, amhehi kataṃ, tumhehi kataṃ.</p>\r\n<p class="bodytext"><a name="para259"></a><span class="paranum">259</span>. <span class="bld">tavamamatuyhaṃmayhaṃ se</span> <span class="note">[ka. 141-2; rū. 241-2; nī. 321]</span>.</p>\r\n<p class="bodytext">samhi savibhattīnaṃ tumhā’mhānaṃ tavādayo honti.</p>\r\n<p class="bodytext">mama dīyate, mayhaṃ dīyate, tava dīyate, tuyhaṃ dīyate.</p>\r\n<p class="bodytext"><a name="para260"></a><span class="paranum">260</span>. <span class="bld">naṃsesvasmākaṃmamaṃ</span> <span class="note">[nī. 438]</span>.</p>\r\n<p class="bodytext">naṃ, sesu savibhattissa amhassa kamena asmākaṃ, mamaṃ honti.</p>\r\n<p class="bodytext">mamaṃ dīyate, asmākaṃ dīyate.</p>\r\n<p class="bodytext"><a name="para261"></a><span class="paranum">261</span>. <span class="bld">ṅaṃṅākaṃ naṃmhi</span> <span class="note">[ka. 161; rū. 244; nī. 344]</span>.</p>\r\n<p class="bodytext">naṃmhi savibhattīnaṃ tumhā’mhānaṃ ṅānubandhā aṃ, ākaṃādesā honti vā.</p>\r\n<p class="bodytext">amhaṃ dīyate, amhākaṃ dīyate, tumhaṃ dīyate, tumhākaṃ dīyate. pañcamiyaṃ mayā, tayā, tvayā, pubbe vuttāva.</p>\r\n<p class="bodytext"><a name="para262"></a><span class="paranum">262</span>. <span class="bld">smāmhi tvamhā</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-258-259-260-261-262-');
INSERT INTO pages VALUES(54553,'annya_sadda_10',146,replace(replace('\r\n<p class="bodytext">smāmhi <a name="M0.0146"></a> savibhattissa tumhassa tvamhā hoti.</p>\r\n<p class="bodytext">tvamhā apeti, amhehi, tumhehi, mama, mamaṃ, mayhaṃ, tava, tuyhaṃ, amhaṃ, amhākaṃ, asmākaṃ, tumhaṃ, tumhākaṃ.</p>\r\n<p class="bodytext"><a name="para263"></a><span class="paranum">263</span>. <span class="bld">smiṃmhi tumhamhānaṃ tayimayi</span> <span class="note">[ka. 139; rū. 245; nī. 318]</span>.</p>\r\n<p class="bodytext">smiṃmhi savibhattīnaṃ tumhā’mhānaṃ tayi, mayi honti.</p>\r\n<p class="bodytext">tayi, mayi, tvatte tvayi, amhesu, tumhesu.</p>\r\n<p class="bodytext"><a name="para264"></a><span class="paranum">264</span>. <span class="bld">sumhāmhassāsmā</span> <span class="note">[nī. 438]</span>.</p>\r\n<p class="bodytext">sumhi amhassa asmā hoti.</p>\r\n<p class="bodytext">asmāsu.</p>\r\n<p class="bodytext">mahāvuttinā yo, hisu amhassa asmādeso, yonaṃ ettañca, asmā gacchāma, asme passati, asmāhi kataṃ, asmākaṃ dīyate, asmāhi apeti, asmākaṃ dhanaṃ, asmāsu ṭhitaṃ. "asmābhijappanti janā anekā"ti <span class="note">[jā. 1.7.68]</span> pāḷi-asme abhijappanti patthentīti attho. "asmābhi pariciṇṇosi, mettacittā hi nāyakā"ti <span class="note">[apa. therī 2.2.230]</span> therīpāḷi – ‘pariciṇṇo’ti paricārito.</p>\r\n<p class="bodytext">catutthiyaṃ asmākaṃ adhipannānaṃ, khamassu rājakuñjara <span class="note">[jā. 2.21.181]</span> – ‘adhipannāna’nti dukkhābhibhūtānaṃ.</p>\r\n<p class="bodytext">chaṭṭhiyaṃ esasmākaṃ kule dhammo <span class="note">[jā. 1.4.147]</span>, esā asmākaṃ dhammatā.</p>\r\n','\r',char(13)),'\n',char(10)),'-263-264-');
INSERT INTO pages VALUES(54554,'annya_sadda_10',147,replace(replace('\r\n<p class="bodytext">sattamiyaṃ yaṃ kiccaṃ parame mitte, katamasmāsu taṃ tayā. pattā nissaṃsayaṃ tvamhā, bhattirasmāsu yā tava <span class="note">[jā. 2.21.81]</span> – tattha ‘yaṃ <a name="M0.0147"></a> kicca’nti yaṃ kammaṃ kattabbaṃ, tava asmāsu yā bhatti, tāya mayaṃ tvamhā nissaṃsayataṃ pattāti attho.</p>\r\n<p class="bodytext"><a name="para265"></a><span class="paranum">265</span>. <span class="bld">apādādo padatekavākye</span> <span class="note">[caṃ. 6.3.15; pā. 8.1.17, 18]</span>.</p>\r\n<p class="bodytext">apādādimhi pavattānaṃ padato paresaṃ ekavākye ṭhitānaṃ tumhā’mhānaṃ vidhi hoti. adhikārasuttamidaṃ.</p>\r\n<p class="bodytext"><a name="para266"></a><span class="paranum">266</span>. <span class="bld">yonaṃhisvapañcamyā vono</span> <span class="note">[ka. 147, 151; rū. 246, 250; nī. 325, 329, 330]</span>.</p>\r\n<p class="bodytext">pañcamīvajjitesu yo, naṃ, hisu paresu apādādopavattānaṃ padato paresaṃ ekavākye ṭhitānaṃ savibhattīnaṃ tumhā’mhasaddānaṃ vo, no honti vā.</p>\r\n<p class="bodytext">gacchatha vo, gacchatha tumhe, gacchāma no, gacchāma amhe, passeyya vo, passeyya tumhe, passeyya no, passeyya amhe, dīyate vo, dīyate tumhākaṃ, dīyate no, dīyate amhākaṃ, dhanaṃ vo, dhanaṃ tumhākaṃ, dhanaṃ no, dhanaṃ amhākaṃ, kataṃ vo puññaṃ, kataṃ tumhehi puññaṃ, kataṃ no puññaṃ, kataṃ amhehi puññaṃ.</p>\r\n<p class="bodytext">apañcamyāti kiṃ? nissaṭaṃ tumhehi, nissaṭaṃ amhehi.</p>\r\n<p class="bodytext">apādādotveva? balañca bhikkhūnamanuppadinnaṃ, tumhehi puññaṃ pasutaṃ anappakaṃ <span class="note">[khu. pā. 7.12]</span>.</p>\r\n<p class="bodytext">padatotveva? tumhe gacchatha, amhe gacchāma.</p>\r\n<p class="bodytext">ekavākyetveva? devadatto tiṭṭhati gāme, tumhe tiṭṭhatha nagare.</p>\r\n<p class="bodytext">savibhattīnantveva? arahati dhammo tumhādisānaṃ.</p>\r\n<p class="bodytext"><a name="para267"></a><span class="paranum">267</span>. <span class="bld">teme nāse</span> <span class="note">[ka. 148, 150; rū. 247, 249; nī. 326, 328; caṃ. 6.3.17; pā. 8.1.21]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-265-266-267-');
INSERT INTO pages VALUES(54555,'annya_sadda_10',148,replace(replace('\r\n<p class="bodytext">nā<a name="M0.0148"></a>, sesu tādisānaṃ savibhattīnaṃ tumha, amhasaddānaṃ te, me honti vā.</p>\r\n<p class="bodytext">kataṃ te puññaṃ, kataṃ tayā puññaṃ, kataṃ me puññaṃ, kataṃ mayā puññaṃ, dinnaṃ te vatthaṃ, dinnaṃ tuyhaṃ vatthaṃ, dinnaṃ me vatthaṃ, dinnaṃ mayhaṃ vatthaṃ, idaṃ te raṭṭhaṃ, idaṃ tava raṭṭhaṃ, idaṃ me raṭṭhaṃ, idaṃ mama raṭṭhaṃ.</p>\r\n<p class="bodytext"><a name="para268"></a><span class="paranum">268</span>. <span class="bld">anvādese</span> <span class="note">[caṃ. 6.3.20; pā. 8.1.23]</span>.</p>\r\n<p class="bodytext">anvādesaṭṭhāne tumhā’mhasaddānaṃ vo, no, te, meādesā niccaṃ bhavanti punabbidhānā.</p>\r\n<p class="bodytext">gāmo tumhākaṃ pariggaho, atho nagarampi vo pariggaho. evaṃ sesesu.</p>\r\n<p class="bodytext"><a name="para269"></a><span class="paranum">269</span>. <span class="bld">sapubbā paṭhamantā vā</span> <span class="note">[‘saṃpubbā paṭhamanthā vā’ (mūlapāṭhe) caṃ. 6.1.21; pā. 8.1.26]</span>.</p>\r\n<p class="bodytext">saṃvijjati pubbapadaṃ assāti sapubbaṃ, sapubbā paṭhamantapadamhā paresaṃ savibhattīnaṃ tumhā’mhasaddānaṃ vo, no, te, meādesā vikappena honti anvādesaṭṭhānepi.</p>\r\n<p class="bodytext">gāme paṭo tumhākaṃ, atho nagare kambalaṃ vo, atho nagare kambalaṃ tumhākaṃ vā. evaṃ sesesu.</p>\r\n<p class="bodytext"><a name="para270"></a><span class="paranum">270</span>. <span class="bld">na cavāhāhevayoge</span> <span class="note">[caṃ. 6.3.22; pā. 8.1.24]</span>.</p>\r\n<p class="bodytext">ca, vā, ha, aha, evasaddehi yoge tumhā’mhānaṃ vo, no, te, meādesā na honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-268-269-270-');
INSERT INTO pages VALUES(54556,'annya_sadda_10',149,replace(replace('\r\n<p class="bodytext">gāmo <a name="M0.0149"></a> tava ca mama ca pariggaho, gāmo tava vā mama vā pariggaho iccādi.</p>\r\n<p class="bodytext">cādiyogeti kiṃ? gāmo ca te pariggaho, nagarañca me pariggaho.</p>\r\n<p class="bodytext"><a name="para271"></a><span class="paranum">271</span>. <span class="bld">dassanatthenālocane</span> <span class="note">[caṃ. 6.3.23; pā. 8.1.25]</span>.</p>\r\n<p class="bodytext">ālocanaṃ olokanaṃ, ālocanato aññasmiṃ dassanatthe payujjamāne tumhā’mhānaṃ vo, no, te, meādesā na honti.</p>\r\n<p class="bodytext">gāmo tumhe uddissa āgato, gāmo amhe uddissa āgato – ‘gāmo’ti gāmavāsī mahājano.</p>\r\n<p class="bodytext">anālocaneti kiṃ? gāmo vo passati, gāmo no passati.</p>\r\n<p class="bodytext"><a name="para272"></a><span class="paranum">272</span>. <span class="bld">āmantanapubbaṃ asantaṃva</span> <span class="note">[‘āmantaṇaṃ pubbamasantaṃva’ (bahūsu) caṃ. 6.3.24; pā. 8.1.72]</span>.</p>\r\n<p class="bodytext">āmantanabhūtaṃ pubbapadaṃ asantaṃ viya hoti, padatoti saṅkhyaṃ na gacchati.</p>\r\n<p class="bodytext">devadatta! tava pariggaho.</p>\r\n<p class="bodytext"><a name="para273"></a><span class="paranum">273</span>. <span class="bld">na sāmaññavacanamekatthe</span> <span class="note">[caṃ. 6.3.25; pā. 8.1.73]</span>.</p>\r\n<p class="bodytext">tulyādhikaraṇabhūte pade sati pubbaṃ sāmaññavacanabhūtaṃ āmantanapadaṃ asantaṃ viya na hoti, padatoti saṅkhyaṃ gacchati.</p>\r\n<p class="bodytext">māṇavaka jaṭila! te pariggaho.</p>\r\n','\r',char(13)),'\n',char(10)),'-271-272-273-');
INSERT INTO pages VALUES(54557,'annya_sadda_10',150,replace(replace('\r\n<p class="bodytext">sāmaññavacananti <a name="M0.0150"></a> kiṃ? māṇavaka devadatta! tuyhaṃ pariggaho.</p>\r\n<p class="bodytext">ekattheti kiṃ? devadatta! yaññadatta! tumhākaṃ pariggaho.</p>\r\n<p class="bodytext"><a name="para274"></a><span class="paranum">274</span>. <span class="bld">bahūsu vā</span> <span class="note">[caṃ. 6.3.26; pā. 8.1.74]</span>.</p>\r\n<p class="bodytext">bahūsu janesu pavattamānaṃ sāmaññavacanabhūtampi āmantanapadaṃ ekatthe pade sati asantaṃ viya na hoti vā.</p>\r\n<p class="bodytext">brāhmaṇā guṇavanto vo pariggaho, brāhmaṇā guṇavanto tumhākaṃ pariggaho.</p>\r\n<p class="centered">sabbādirāsi niṭṭhito.</p>\r\n<p class="subhead">vibhattipaccayantarāsi</p>\r\n<p class="bodytext">atha vibhattipaccayā dīpiyante.</p>\r\n<p class="bodytext">vibhatyatthānaṃ jotakattā vibhattiṭṭhāne ṭhitā paccayā vibhattipaccayā.</p>\r\n<p class="bodytext"><a name="para275"></a><span class="paranum">275</span>. <span class="bld">to pañcamyā</span> <span class="note">[ka. 248; rū. 260; nī. 493; caṃ. 4.3.6; pā. 5.4.45]</span>.</p>\r\n<p class="bodytext">pañcamiyā vibhattiyā atthe topaccayo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-274-275-');
INSERT INTO pages VALUES(54558,'annya_sadda_10',151,replace(replace('\r\n<p class="bodytext">tomhi dīghānaṃ rasso, kaññato, rattito, itthito, dhenuto. mahāvuttinā tomhi mātāpitūnaṃ ittaṃ, mātito, pitito, vadhuto, purisato, munito, daṇḍito<a name="M0.0151"></a>, bhikkhuto, satthārato, kattuto, gotrabhuto, sabbato, yato, tato.</p>\r\n<p class="bodytext">ima, eta, kiṃsaddehi to.</p>\r\n<p class="bodytext"><a name="para276"></a><span class="paranum">276</span>. <span class="bld">itotettokuto</span> <span class="note">[‘ito tetto kato’ (bahūsu) caṃ. 4.3.8; pā. 7.2.104]</span>.</p>\r\n<p class="bodytext">ito, ato, etto, kutoti ete saddā topaccayantā nipaccante.</p>\r\n<p class="bodytext">imamhā imehīti vā ito, etasmā etehīti vā ato, etto, kasmā kehīti vā kuto. ettha ca imamhā, imehītiādikaṃ atthavākyaṃ disvā pakatiliṅgaṃ veditabbaṃ. iminā suttena imassa ittaṃ, etassa attaṃ ettañca, ‘saramhā dve’ti esaramhā dvittaṃ, kiṃsaddassa kuttaṃ. esa nayo sesesu nipātanesu.</p>\r\n<p class="bodytext"><a name="para277"></a><span class="paranum">277</span>. <span class="bld">abhyādīhi</span> <span class="note">[pā. 5.3.9]</span>.</p>\r\n<p class="bodytext">abhiādīhi to hoti, punabbidhānā’pañcamyatthepītipi siddhaṃ.</p>\r\n<p class="bodytext">abhito gāmaṃ gāmassa abhimukheti attho.</p>\r\n<p class="bodytext">parito gāmaṃ gāmassa samantatoti attho.</p>\r\n<p class="bodytext">ubhato gāmaṃ gāmassa ubhosu passesūti attho.</p>\r\n<p class="bodytext">pacchato, heṭṭhato, uparito.</p>\r\n<p class="bodytext"><a name="para278"></a><span class="paranum">278</span>. <span class="bld">ādyādīhi</span> <span class="note">[caṃ. 4.3.9; pā. 5.4.44]</span>.</p>\r\n<p class="bodytext">ādipabhutīhi apañcamyatthepi to hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-276-277-278-');
INSERT INTO pages VALUES(54559,'annya_sadda_10',152,replace(replace('\r\n<p class="bodytext">ādito<a name="M0.0152"></a>, majjhato, purato, passato, piṭṭhito, orato, parato, pacchato, puratthimato, dakkhiṇatoiccādīsu bahulaṃ sattamyatthe dissati.</p>\r\n<p class="bodytext">tathā tatiyatthepi rūpaṃ attato samanupassati <span class="note">[saṃ. ni. 3.44]</span>, pañcakkhandhe aniccato vipassati iccādi.</p>\r\n<p class="bodytext">yatonidānaṃ <span class="note">[su. ni. 275]</span>, yatvādhikaraṇaṃ, yatodakaṃ tadādittamiccādīsu <span class="note">[jā. 1.9.58]</span> paṭhamatthe icchanti.</p>\r\n<p class="bodytext">ito ehi, ito balāke āgaccha, caṇḍo me vāyaso sakhā iccādīsu dutiyatthe.</p>\r\n<p class="bodytext">paratoghoso, nādiṭṭhā parato dosaṃ iccādīsu chaṭṭhyatthe.</p>\r\n<p class="bodytext"><a name="para279"></a><span class="paranum">279</span>. <span class="bld">sabbādito sattamyā tratthā</span> <span class="note">[ka. 249; rū. 266; nī. 494; caṃ. 4.1.10; pā. 5.3.10]</span>.</p>\r\n<p class="bodytext">sabbādināmakehi sabbanāmehi sattamiyā atthe tra, tthā honti.</p>\r\n<p class="bodytext">sabbasmiṃ sabbesūti vā sabbatra, sabbattha, sabbassaṃ sabbāsu vātipi. evaṃ kataratra, katarattha, aññatra, aññattha iccādi.</p>\r\n<p class="bodytext">yatra, yattha, tatra, tattha.</p>\r\n<p class="bodytext"><a name="para280"></a><span class="paranum">280</span>. <span class="bld">katthetthakutrātrakvehidha</span> <span class="note">[ka. 251; rū. 269; nī. 499; caṃ. 4.1.11; pā. 5.3.11, 12]</span>.</p>\r\n<p class="bodytext">kattha, ettha, kutra, atra, tva, iha, idhāti ete saddāttha, tra,va ha, dhāpaccayantā sattamyatthe sijjhanti.</p>\r\n<p class="bodytext">kasmiṃ kesūti vā kattha, kutra, kva. ‘kuva’ntipi sijjhati, "kuvaṃ sattassa kārako, kuvaṃ satto samuppanno <span class="note">[saṃ. ni. 1.171]</span>, kuvaṃ asissaṃ, kuvaṃ khādissa"nti pāḷi.</p>\r\n','\r',char(13)),'\n',char(10)),'-279-280-');
INSERT INTO pages VALUES(54560,'annya_sadda_10',153,replace(replace('\r\n<p class="bodytext">etasmiṃ <a name="M0.0153"></a> etesūti vā ettha, atra, imasmiṃ imesūti vā iha, idha.</p>\r\n<p class="bodytext"><a name="para281"></a><span class="paranum">281</span>. <span class="bld">dhi sabbā vā</span> <span class="note">[ka. 250; rū. 268; nī. 502]</span>.</p>\r\n<p class="bodytext">sabbasaddamhā sattamyatthe dhi hoti vā.</p>\r\n<p class="bodytext">namo te buddha vīra’tthu, vippamuttosi sabbadhi <span class="note">[saṃ. ni. 1.90]</span>.</p>\r\n<p class="bodytext"><a name="para282"></a><span class="paranum">282</span>. <span class="bld">yā hiṃ</span> <span class="note">[ka. 255; rū. 275; nī. 504]</span>.</p>\r\n<p class="bodytext">yamhā sattamyatthe hiṃ hoti.</p>\r\n<p class="bodytext">yahiṃ.</p>\r\n<p class="bodytext"><a name="para283"></a><span class="paranum">283</span>. <span class="bld">tā hañca</span> <span class="note">[ka. 253; rū. 273; nī. 501]</span>.</p>\r\n<p class="bodytext">tamhā sattamyatthe hiṃ hoti hañca.</p>\r\n<p class="bodytext">tahiṃ, tahaṃ. dutiyatthepi dissati "tahaṃ tahaṃ olokento gacchatī"ti.</p>\r\n<p class="bodytext"><a name="para284"></a><span class="paranum">284</span>. <span class="bld">kiṃssa kukañca</span> <span class="note">[ka. 251, 227-8-9; rū. 226, 270-1-2; nī. 500, 456-7, 460]</span>.</p>\r\n<p class="bodytext">kiṃmhā sattamyatthe hiṃ, taṃ hoti. kiṃssa kuttaṃ kattañca hoti.</p>\r\n<p class="bodytext">kuhiṃ gacchati, kuhaṃ gacchati. kahaṃ ekaputtaka kahaṃ ekaputtaka <span class="note">[saṃ. ni. 2.63]</span>. kuhiñci, kuhiñcananti dve ci, cana-nipātantā sijjhanti.</p>\r\n<p class="centered">iti sāmaññasattamyantarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-281-282-283-284-');
INSERT INTO pages VALUES(54561,'annya_sadda_10',154,replace(replace('\r\n<p class="bodytext">kālasattamyantaṃ <a name="M0.0154"></a> vuccate.</p>\r\n<p class="bodytext"><a name="para285"></a><span class="paranum">285</span>. <span class="bld">sabbekaññayatehi kāledā</span> <span class="note">[ka. 257; rū. 276; nī. 505]</span>.</p>\r\n<p class="bodytext">sabba, eka, añña, ya, tasaddehi kāle dā hoti.</p>\r\n<p class="bodytext">sabbasmiṃ kāle sabbadā, ekasmiṃ kāle ekadā, aññasmiṃ kāle aññadā, yasmiṃ kāle yadā, tasmiṃ kāle tadā.</p>\r\n<p class="bodytext"><a name="para286"></a><span class="paranum">286</span>. <span class="bld">kadākudāsadāadhunedāni</span> <span class="note">[ka. 257-8-9; rū. 276-8-9; nī. 505-6-7]</span>.</p>\r\n<p class="bodytext">etepi sattamyatthe kāle dā, dhunā, dānipaccayantā sijjhanti.</p>\r\n<p class="bodytext">kiṃsmiṃ kāle kadā, kudā, sabbasmiṃ kāle sadā, imasmiṃ kāle adhunā, idāni.</p>\r\n<p class="bodytext"><a name="para287"></a><span class="paranum">287</span>. <span class="bld">ajjasajjuparajjetarahikarahā</span> <span class="note">[ka. 259; rū. 279, 423; nī. 507]</span>.</p>\r\n<p class="bodytext">etepi kāle jja, jju, rahi, raha paccayantā sijjhanti.</p>\r\n<p class="bodytext">imasmiṃ kāle ajja, imasmiṃ divasetyattho.</p>\r\n<p class="bodytext">samāne kāle sajju-‘samāne’ti vijjamāne. na hi pāpaṃ kataṃ kammaṃ, sajju khīraṃva muccati <span class="note">[dha. pa. 71]</span>, sajjukaṃ pāhesi – tattha ‘sajjū’ti tasmiṃ divase.</p>\r\n<p class="bodytext">aparasmiṃ kāle aparajju, punadivaseti attho.</p>\r\n<p class="bodytext">imasmiṃ kāle etarahi, kiṃsmiṃ kāle karaha. kutoci, kvaci, katthaci, kuhiñci, kadāci, karahacisaddā pana ci-nipātantā honti, tathā yato kutoci, yattha katthaci, yadā kadācīti. kiñcanaṃ, kuhiñcanaṃ, kudācananti cana-nipātantāti.</p>\r\n<p class="centered">vibhattipaccayantarāsi niṭṭhito.</p>\r\n<p class="subhead">abyayapadāni</p>\r\n','\r',char(13)),'\n',char(10)),'-285-286-287-');
INSERT INTO pages VALUES(54562,'annya_sadda_10',155,replace(replace('\r\n<p class="subhead">upasaggapadarāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0155"></a> abyayapadāni dīpiyante.</p>\r\n<p class="bodytext">chabbidhāni abyayapadāni upasaggapadaṃ, nipātapadaṃ, vibhattipaccayantapadaṃ, abyayībhāvasamāsapadaṃ, abyayataddhitapadaṃ, tvādipaccayantapadanti. byayo vuccati vikāro, nānāliṅgavibhattivacanehi natthi rūpabyayo etesanti abyayā, asaṅkhyāti ca vuccanti.</p>\r\n<p class="bodytext">tattha vibhattipaccayantapadato puna vibhattuppatti nāma natthi. abyayībhāvasamāsamhi vibhattīnaṃ vidhi samāsakaṇḍe vakkhati, tasmā tāni dve ṭhapetvā sesāni cattāri idha vuccante.</p>\r\n<p class="bodytext"><a name="para288"></a><span class="paranum">288</span>. <span class="bld">asaṅkhyehi sabbāsaṃ</span> <span class="note">[caṃ. 2.1.38; pā. 2.4.82]</span>.</p>\r\n<p class="bodytext">asaṅkhyehi padehi yathārahaṃ sabbāsaṃ vibhattīnaṃ lopo hoti, kehici padehi paṭhamāya lopo, kehici padehi dutiyāya lopo.pe.. kehici sattamiyā, kehici dvinnaṃ, kehici tissannaṃ.pe.. kehici sattannanti vuttaṃ hoti.</p>\r\n<p class="bodytext">tattha āvuso, bho, bhanteiccādīhi āmantananipātehi atthi, natthi, sakkā, labbhā, siyā, siyuṃ, sādhu, tuṇhīiccādīhi ca paṭhamāya lopo.</p>\r\n<p class="bodytext">ciraṃ, cirassaṃ, niccaṃ, satataṃ, abhiṇhaṃ, abhikkhaṇaṃ, muhuttaṃ iccādīhi accantasaṃyogalakkhaṇe dutiyāya.</p>\r\n<p class="bodytext">yathā, tathā, sabbathā, sabbaso, musā, micchāiccādīhi tatiyāya.</p>\r\n<p class="bodytext">kātuṃ, kātave iccādīhi catutthiyā.</p>\r\n','\r',char(13)),'\n',char(10)),'-288-');
INSERT INTO pages VALUES(54563,'annya_sadda_10',156,replace(replace('\r\n<p class="bodytext">samantā<a name="M0.0156"></a>, samantato, dīghaso, orasoiccādīhi pañcamiyā.</p>\r\n<p class="bodytext">pure, purā, pacchā, uddhaṃ, upari, adho, heṭṭhā, antarā, anto, raho, āvi, hiyyo, suveiccādīhi sattamiyā lopo.</p>\r\n<p class="bodytext">namosaddamhā "namo te buddha vīra’tthū"ti ettha paṭhamāya. "namo karohi nāgassā"ti ettha dutiyāya.</p>\r\n<p class="bodytext">sayaṃsaddamhā "kusūlo sayameva bhijjate"ti ettha paṭhamāya. "sayaṃ kataṃ sukhadukkha"nti <span class="note">[dī. ni. 3.191, 193]</span> ettha tatiyāya, iccādinā yathārahavibhāgo veditabbo.</p>\r\n<p class="bodytext">iti, evaṃsaddehi payogānurūpaṃ sattannaṃ vibhattīnaṃ lopaṃ icchanti.</p>\r\n<p class="bodytext">upasaggehipi atthānurūpaṃ taṃtaṃvibhattilopo.</p>\r\n<p class="bodytext">rūpasiddhiyaṃ pana "tehi paṭhamekavacanameva bhavatī"ti <span class="note">[rū. 131 (piṭṭhe)]</span> vuttaṃ.</p>\r\n<p class="bodytext">tattha "abhikkamati, abhidhammo" iccādīsu dhātuliṅgāni upecca tesaṃ atthaṃ nānāppakāraṃ karontā sajjanti saṅkharontīti upasaggā. te hi kvaci tadatthaṃ visiṭṭhaṃ karonti "jānāti, pajānāti, sañjānāti, avajānāti, abhijānāti, parijānāti, susīlo, dussīlo, suvaṇṇo, dubbaṇṇo, surājā, durājā" iccādīsu.</p>\r\n<p class="bodytext">kvaci tadatthaṃ nānāppakāraṃ katvā vibhajjanti "gacchati, āgacchati, uggacchati, ogacchati"iccādīsu.</p>\r\n<p class="bodytext">kvaci tadatthaṃ bādhetvā tappaṭiviruddhe vā tadaññasmiṃ vā atthe tāni yojenti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54564,'annya_sadda_10',157,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0157"></a> tappaṭiviruddhe –</p>\r\n<p class="bodytext">jeti, parājeti, omuñcati, paṭimuñcati, gilati, uggilati, nimmujjati, ummujjati, dhammo, uddhammoiccādi.</p>\r\n<p class="bodytext">tadaññasmiṃ –</p>\r\n<p class="bodytext">dadāti, ādadāti, dadhāti, vidheti, pidheti, nidheti, sandhiyati, saddahati, abhidhātiiccādi.</p>\r\n<p class="bodytext">kvaci pana padasobhaṇaṃ katvā tadatthaṃ anuvattanti, "vijjati, saṃvijjati, labhati, paṭilabhati" iccādi.</p>\r\n<p class="bodytext">te vīsati honti-pa, ā, u, o, du, ni, vi, su, saṃ, ati, adhi, anu, apa, api, abhi, ava, upa, pati, parā, pari.</p>\r\n<p class="bodytext">kaccāyane pana osaddo avakāriyamattanti taṃ aggahetvā nīsaddaṃ gaṇhāti, idha pana nīsaddo nissa dīghamattanti taṃ aggahetvā osaddaṃ gaṇhāti.</p>\r\n<p class="bodytext">tattha pa –</p>\r\n<p class="bodytext">pakāratthe-paññā. ādikamme-vippakataṃ. padhāne-paṇītaṃ. issariye-pabhū. antobhāve-pakkhittaṃ, passāso. viyoge-pavāso. tappare-pācariyo. tadanubandhe-putto, paputto, nattā, panattā. bhusatthe-pavaḍḍho. sambhave-pabhavati. tittiyaṃ-pahutaṃ annaṃ. anāvile-pasanno. patthanāyaṃ-paṇidhānaṃ.</p>\r\n<p class="bodytext">ā –</p>\r\n<p class="bodytext">abhimukhe-āgacchati. uddhaṃkamme-ārohati. mariyādāyaṃ-āpabbatā khettaṃ. abhividhimhi-ābrahmalokā kittisaddo. pattiyaṃ-āpanno. icchāyaṃ-ākaṅkhā. parissajane-āliṅgati. ādikamme-ārambho. gahaṇe-ādīyati. nivāse-āvasatho. samīpe-āsannaṃ. avhāne-āmantanaṃ.</p>\r\n<p class="bodytext">u –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54565,'annya_sadda_10',158,replace(replace('\r\n<p class="bodytext">uggate-uggacchati<a name="M0.0158"></a>. uddhaṃkamme-uṭṭhāti. padhāne-uttaro. viyoge-upavāso. sambhave-ubbhūto. atthalābhe-rūpassa uppādo. sattiyaṃ-ussahati gantuṃ. sarūpakhyāne-uddeso.</p>\r\n<p class="bodytext">o –</p>\r\n<p class="bodytext">antobhāve-ocarako, orodho. adhokamme-okkhitto. niggahe-ovādo. antare, dese ca-okāso. pātubhāve-opapātiko. yesu atthesu avasaddo vattati, tesupi osaddo vattati.</p>\r\n<p class="bodytext">du –</p>\r\n<p class="bodytext">asobhaṇe-duggandho. abhāve-dubbhikkhaṃ, dussīlo, duppañño. kucchite-dukkaṭaṃ. asamiddhiyaṃ-dusassaṃ. kicche-dukkaraṃ. virūpe-dubbaṇṇo, dummukho.</p>\r\n<p class="bodytext">ni –</p>\r\n<p class="bodytext">nissese-nirutti. niggate-niyyānaṃ. nīharaṇe-niddhāraṇaṃ. antopavesane-nikhāto. abhāve-nimmakkhikaṃ. nisedhe-nivāreti. nikkhante-nibbānaṃ. pātubhāve-nimmitaṃ. avadhāraṇe-vinicchayo. vibhajjane-niddeso. upamāyaṃ-nidassanaṃ. upadhāraṇe-nisāmeti. avasāne-niṭṭhitaṃ. cheke-nipuṇo.</p>\r\n<p class="bodytext">vi –</p>\r\n<p class="bodytext">visese-vipassati. vividhe-vicittaṃ. viruddhe-vivādo. vigate-vimalo. viyoge-vippayutto. virūpe-vippaṭisāro.</p>\r\n<p class="bodytext">su –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54566,'annya_sadda_10',159,replace(replace('\r\n<p class="bodytext">sobhaṇe-suggati<a name="M0.0159"></a>. sundare-sumano. sammāsaddatthe-sugato. samiddhiyaṃ-subhikkhaṃ. sukhatthe-sukaro.</p>\r\n<p class="bodytext">saṃ –</p>\r\n<p class="bodytext">samodhāne-sandhi. sammā, samatthesu-samādhi, sampayutto. samantabhāve-saṃkiṇṇo. saṅgate-samāgamo, saṅkhepe-samāso. bhusatthe-sāratto. sahatthe-saṃvāso, sambhogo. appatthe-samaggho. pabhave-sambhavo. abhimukhe-sammukhaṃ. saṅgahe-saṅgayhati. pidahane-saṃvuto. punappunakamme-sandhāvati, saṃsarati. samiddhiyaṃ-sampanno.</p>\r\n<p class="bodytext">ati –</p>\r\n<p class="bodytext">atikkame-atirocati, accayo, atīto. atikkante-accantaṃ. atissaye-atikusalo. bhusatthe-atikodho. antokamme-mañcaṃ vā pīṭhaṃ vā atiharitvā ṭhapeti.</p>\r\n<p class="bodytext">adhi –</p>\r\n<p class="bodytext">adhike-adhisīlaṃ. issare-adhipati, adhibrahmadatte pañcālā. uparibhāve-adhiseti. paribhavane-adhibhūto. ajjhāyane-ajjheti, byākaraṇamadhīte. adhiṭṭhāne-navakammaṃ adhiṭṭhāti, cīvaraṃ adhiṭṭhāti, iddhivikubbanaṃ adhiṭṭhāti. nicchaye-adhimuccati. pāpuṇane-bhogakkhandhaṃ adhigacchati, amataṃ adhigacchati.</p>\r\n<p class="bodytext">anu –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54567,'annya_sadda_10',160,replace(replace('\r\n<p class="bodytext">anugate-anveti. anuppacchinne-anusayo. pacchāsaddatthe-anurathaṃ. punappunabhāve-anvaḍḍhamāsaṃ, anusaṃvaccharaṃ. yogyabhāve-anurūpaṃ<a name="M0.0160"></a>.kaniṭṭhabhāve-anubuddhā, anuthero. sesaṃ kārakakaṇḍe vakkhati.</p>\r\n<p class="bodytext">apa –</p>\r\n<p class="bodytext">apagate-apeti, apāyo. garahe-apagabbho, apasaddo. vajjane-apasālāya āyanti. pūjāyaṃ-vuḍḍha-mapacāyanti. padussane-aparajjhati.</p>\r\n<p class="bodytext">api –</p>\r\n<p class="bodytext">sambhāvane-apipabbataṃ bhindeyya, merumpi vinivijjheyya. apekkhāyaṃ-ayampi dhammo aniyato. samuccaye-itipi arahaṃ, chavimpi dahati, cammampi dahati, maṃsampi dahati. garahāyaṃ-api amhākaṃ paṇḍitaka. pucchāyaṃ-api bhante bhikkhaṃ labhittha, api nu tumhe sotukāmāttha.</p>\r\n<p class="bodytext">abhi –</p>\r\n<p class="bodytext">abhimukhe-abhikkanto. visiṭṭhe-abhiññā. adhike-abhidhammo. uddhaṃkamme-abhirūhati. kule-abhijāto. sāruppe-abhirūpo. vandane-abhivādeti. sesaṃ kārakakaṇḍe vakkhati.</p>\r\n<p class="bodytext">ava –</p>\r\n<p class="bodytext">adhobhāge-avakkhitto. viyoge-avakokilaṃ vanaṃ. paribhave-avajānāti. jānane-avagacchati. suddhiyaṃ-vodāyati, vodānaṃ. nicchaye-avadhāraṇaṃ. dese-avakāso. theyye-avahāro.</p>\r\n<p class="bodytext">upa –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54568,'annya_sadda_10',161,replace(replace('\r\n<p class="bodytext">upagame-upanisīdati. samīpe-upacāro, upanagaraṃ. upapattiyaṃ-saggaṃ lokaṃ upapajjati. sadise-upamāṇaṃ, upameyyaṃ. adhike-upakhāriyaṃ doṇo. uparibhāve – upasampanno<a name="M0.0161"></a>, upacayo. anasane-upavāso. dosakkhāne-paraṃ upavadati. saññāyaṃ-upadhā, upasaggo. pubbakamme-upakkamo, upahāro. pūjāyaṃ-buddhaṃ upaṭṭhāti. gayhākāre-paccupaṭṭhānaṃ. bhusatthe-upādānaṃ, upāyāso, upanissayo.</p>\r\n<p class="bodytext">pati –</p>\r\n<p class="bodytext">patigate-paccakkhaṃ. paṭilome-paṭisotaṃ. paṭiyogimhi-paṭipuggalo. nisedhe-paṭisedho. nivatte-paṭikkamati. sadise-paṭirūpakaṃ. paṭikamme-rogassa paṭikāro. ādāne-paṭiggaṇhāti. paṭibodhe-paṭivedho. paṭicce-paccayo. sesaṃ kārakakaṇḍe vakkhati.</p>\r\n<p class="bodytext">parā –</p>\r\n<p class="bodytext">parihāniyaṃ-parābhavo. parājaye-parājito. gatiyaṃ-parāyanaṃ. vikkame-parakkamo. āmasane-parāmasanaṃ.</p>\r\n<p class="bodytext">pari –</p>\r\n<p class="bodytext">samantabhāve-parivuto, parikkhitto, parikkhāro. paricchede-pariññeyyaṃ, parijānāti. vajjane-pariharati. parihāro. āliṅgane-parissajati. nivāsane-vatthaṃ paridahati. pūjāyaṃ-pāricariyā. bhojane-parivisati. abhibhave-paribhavati. dosakkhāne-paribhāsati. sesaṃ kārakakaṇḍe vakkhati.</p>\r\n<p class="bodytext">nīsaddo pana nīharaṇa, nīvaraṇādīsu vattati, nīharaṇaṃ, nīvaraṇaṃiccādi.</p>\r\n<p class="centered">iti upasaggapadarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54569,'annya_sadda_10',162,replace(replace('\r\n<p class="subhead">nipātapadarāsi</p>\r\n\r\n<p class="bodytext">niccaṃ <a name="M0.0162"></a> ekarūpena vākyapathe patantīti <span class="bld">nipātā</span>. padānaṃ ādi, majjhā’vasānesu nipatantīti nipātātipi vadanti.</p>\r\n<p class="bodytext">asatvavācakā cādisaddā nipātā nāma. te pana vibhattiyuttā, ayuttā cāti duvidhā honti. tattha vibhattiyuttā pubbe dassitā eva. cādayo ayuttā nāma. te pana anekasatappabhedā honti. nighaṇṭusatthesu gahetabbāti.</p>\r\n<p class="subhead">abyayataddhitapaccayapadarāsi</p>\r\n<p class="bodytext">abyayataddhitapaccayantā nāma yathā, tathā, ekadhā, ekajjhaṃ, sabbaso, kathaṃ, itthaṃ iccādayo. tehi tatiyālopo.</p>\r\n<p class="subhead">tvādipaccayantapadarāsi</p>\r\n<p class="bodytext">tvādipaccayantā nāma katvā, katvāna, kātuna, kātuṃ, kātave, dakkhitāye, hetuye, ādāya, upādāya, viceyya, vineyya, sakkacca, āhacca, upasampajja, samecca, avecca, paṭicca, aticca, āgamma, ārabbhaiccādayo. tesu tvā, tvānantehi paṭhamālopo. tuṃ, tave, tāye, tuyepaccayantehi catutthīlopoti.</p>\r\n<p class="bodytext">dhātavo paccayā ceva, upasagganipātakā.</p>\r\n<p class="bodytext">anekatthāva te paṭi-sambhidā ñāṇagocarā.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānadīpaniyā</p>\r\n<p class="centered">nāmakaṇḍo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54570,'annya_sadda_10',163,replace(replace('\r\n<p class="chapter">3. kārakakaṇḍa<a name="M0.0163"></a></p>\r\n<p class="subhead">paṭhamāvibhattirāsi</p>\r\n<p class="bodytext">atha nāmavibhattīnaṃ atthabhedā vuccante.</p>\r\n<p class="bodytext">kasmiṃ atthe paṭhamā?</p>\r\n<p class="bodytext"><a name="para289"></a><span class="paranum">289</span>. <span class="bld">paṭhamatthamatte</span> <span class="note">[caṃ. 2.1.93; pā. 2.3.46]</span>.</p>\r\n<p class="bodytext">nāmassa abhidheyyamatte paṭhamāvibhatti hoti.</p>\r\n<p class="bodytext">rukkho, mālā, dhanaṃ.</p>\r\n<p class="bodytext">ettha ca mattasaddena kattu, kammādike vibhatyatthe nivatteti. tasmā atthamattanti liṅgatthoyeva vuccati.</p>\r\n<p class="bodytext">tattha anuccārite sati suṇantassa avidito attho līnattho nāma. taṃ līnamatthaṃ gameti bodhetīti <span class="bld">liṅgaṃ,</span> uccāritapadaṃ.</p>\r\n<p class="bodytext">taṃ pana pakatiliṅgaṃ, nipphannaliṅganti duvidhaṃ. tattha vibhattirahitaṃ pakatiliṅgaṃ idhādhippetaṃ liṅga, vibhattīnaṃ visuṃ visuṃ vibhāgaṭṭhānattā.</p>\r\n<p class="bodytext">līnaṃ aṅganti liṅgaṃ. tattha ‘līna’nti apākaṭaṃ. ‘aṅga’nti avayavo. <span class="bld">liṅgaṃ, nāmaṃ, pāṭipadika</span>nti atthato ekaṃ.</p>\r\n<p class="bodytext">liṅgassa attho paramattho, paññattiatthoti duvidho. tathā visesanattho, visesyatthoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-289-');
INSERT INTO pages VALUES(54571,'annya_sadda_10',164,replace(replace('\r\n<p class="bodytext">tattha visesanattho nāma sakattho, tassa tassa saddassa paṭiniyato pāṭipuggalikatthoti vuttaṃ hoti. soyeva tasmiṃ tasmiṃ atthe ādimhi sadduppattiyā cirakālañca saddapavattiyā nibaddhakāraṇattā nimittatthoti ca vuccati<a name="M0.0164"></a>. so suti, jāti, guṇa, dabba, kriyā, nāma, sambandhavasena sattavidho hoti.</p>\r\n<p class="bodytext">visesyattho nāma sāmaññattho, bahunimittānaṃ sādhāraṇatthoti vuttaṃ hoti, soyeva taṃtaṃnimittayogā nemittakatthoti ca vuccati, so jāti, guṇa, dabba, kriyā, nāmavasena pañcavidho. go, sukko, daṇḍī, pācako, tissoti.</p>\r\n<p class="bodytext">tattha gosaddo yadā jātimattaṃ vadati go jātīti, tadā suti visesanaṃ. yadā dabbaṃ vadati go gacchatīti, tadā suti ca jāti ca visesanaṃ.</p>\r\n<p class="bodytext">sukkasaddo yadā guṇamattaṃ vadati sukko guṇoti, tadā suti visesanaṃ. yadā guṇavisesaṃ vadati gossa sukkoti, tadā suti ca guṇajāti ca visesanaṃ. yadā guṇavantaṃ dabbaṃ vadati sukko goti, tadā suti ca guṇajāti ca guṇaviseso ca sambandho ca visesanaṃ.</p>\r\n<p class="bodytext">daṇḍīsaddo yadā jātimattaṃ vadati daṇḍī jātīti, tadā suti ca dabbañca visesanaṃ. yadā dabbavantaṃ dabbaṃ vadati daṇḍī purisoti, tadā suti ca dabbañca jāti ca sambandho ca visesanaṃ.</p>\r\n<p class="bodytext">pācakasaddo yadā jātimattaṃ vadati pācako jātīti, tadā suti ca kriyā ca visesanaṃ. yadā kriyānipphādakaṃ dabbaṃ vadati pācako purisoti, tadā suti ca kriyā ca jāti ca kriyākārakasambandho ca visesanaṃ.</p>\r\n<p class="bodytext">tissasaddo yadā nāmamattaṃ vadati tisso nāmanti, tadā suti visesanaṃ. yadā nāmavantaṃ dabbaṃ vadati, tisso bhikkhūti, tadā suti ca nāmajāti ca sambandho ca visesanaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54572,'annya_sadda_10',165,replace(replace('\r\n<p class="bodytext">sabbattha yaṃ yaṃ vadatīti vuttaṃ, taṃ taṃ visesyanti ca dabbanti ca veditabbaṃ. ettha ca suti nāma saddasabhāvā eva hoti, saddapakkhikā <a name="M0.0165"></a> eva. sabbo saddo paṭhamaṃ sattābhidhāyakoti ca ñāse vuttaṃ. tasmā sabbattha sutiṭṭhāne sattā eva yuttā vattunti. sattāti ca tassa tassa atthassa vohāramattenapi loke vijjamānatā vuccati, taṃ taṃ saddaṃ suṇantassa ca ñāṇaṃ taṃtadatthassa atthitāmattaṃ sabbapaṭhamaṃ jānāti, tato paraṃ jātisadde jātiṃ jānāti. guṇasadde guṇanti evamādi sabbaṃ vattabbaṃ. liṅga, saṅkhyā, parimāṇānipi visesanatthe saṅgayhanti.</p>\r\n<p class="bodytext">tattha liṅgaṃ nāma ye itthi, purisānaṃ liṅga, nimitta, kuttā’kappā nāma abhidhamme vuttā, ye ca napuṃsakānaṃ liṅga, nimitta, kuttā’kappā nāma avuttasiddhā, ye ca saddesu ceva atthesu ca visadā’visadākāra, majjhimākārā sandissanti, sabbametaṃ liṅgaṃ nāma.</p>\r\n<p class="bodytext">evaṃ visesana, visesyavasena duvidho attho pubbe vuttassa saddaliṅgassa attho nāma, so saliṅgo, sasaṅkhyo, saparimāṇo cāti tividho hoti.</p>\r\n<p class="bodytext">tattha saliṅgo yathā? saññā, phasso, cittaṃ. kaññā, puriso, kulaṃ. mālā, rukkho, dhananti.</p>\r\n<p class="bodytext">sasaṅkhyo yathā? eko, dve, tayo, bahū iccādi.</p>\r\n<p class="bodytext">saparimāṇo yathā? vidatthi, hattho, doṇo, āḷhakaṃ iccādi.</p>\r\n<p class="bodytext">api ca suddhā, saṃsaṭṭhoti duvidho liṅgattho. tattha kammādisaṃsaggarahito suddhā nāma. so saliṅgo, sasaṅkhyo, saparimāṇo, upasaggattho, nipātattho, pāṭi-padikatthoti chabbidho. atthi, sakkā, labbhāiccādi idha pāṭipadikaṃ nāma. tuna, tvāna, tvā, tave, tuṃ, khattuṃpaccayantāpi nipātesu gayhanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54573,'annya_sadda_10',166,replace(replace('\r\n<p class="bodytext">saṃsaṭṭho <a name="M0.0166"></a> vuttasaṃsaṭṭho, avuttasaṃsaṭṭhoti duvidho. tattha vuttasaṃsaṭṭho catubbidho samāsena vuttasaṃsaṭṭho, taddhitena, ākhyātena, kitenāti. tattha samāsena vutto chakārakasambandhavasena sattavidho, bhāvena saddhiṃ aṭṭhavidho vā, tathā taddhitena vutto. ākhyātena vutto kattu, kamma, bhāvavasena tividho. kitena vutto chakāraka, bhāvavasena sattavidho. sabbo suddhā ceva vuttasaṃsaṭṭho ca paṭhamāya visayo.</p>\r\n<p class="bodytext">avuttasaṃsaṭṭhopi kattusaṃsaṭṭho, kammasaṃsaṭṭhotiādinā anekavidho. so dutiyādīnaṃ eva visayoti. ettha ca vibhattiyā vinā kevalo saddo payogaṃ nārahatīti katvā payogārahatthameva chabbidhe suddhe catubbidhe ca vuttasaṃsaṭṭhe paṭhamā payujjati, na atthajotanatthaṃ.</p>\r\n<p class="bodytext">kenaci vācakena avuttāni pana kammādīni vibhattīhi vinā viditāni na hontītikatvā atthajotanatthampi kammādīsu dutiyādayo payujjanti. tasmā atthamatteti idha desantarāvacchedake visayamatte bhummaṃ. kamme dutiyāiccādīsu pana nipphādetabbe payojane bhummanti evaṃ dvinnaṃ bhummānaṃ nānattaṃ veditabbanti.</p>\r\n<p class="bodytext"><a name="para290"></a><span class="paranum">290</span>. <span class="bld">āmantane</span> <span class="note">[ka. 285; rū. 70; nī. 578; caṃ. 2.1.94; pā. 2.3.47; āmantaṇe (bahūsu)]</span>.</p>\r\n<p class="bodytext">pageva siddhassa vatthuno nāmena vā nipātena vā attano abhimukhīkaraṇaṃ āmantanaṃ nāma. adhikāmantane atthamatte paṭhamā hoti. ettha ca āmantanapadaṃ nāma kriyāpekkhaṃ na hoti, tasmā kārakasaññaṃ na labhati.</p>\r\n<p class="bodytext">taṃ pana duvidhaṃ sādarā’nādaravasena. ehi samma, ehi jeti.</p>\r\n','\r',char(13)),'\n',char(10)),'-290-');
INSERT INTO pages VALUES(54574,'annya_sadda_10',167,replace(replace('\r\n<p class="bodytext">tathā <a name="M0.0167"></a> sajīva, nijjīvavasena, bho purisa, vadehi bho saṅkha, vadehi bho saṅkha <span class="note">[dī. ni. 2.426]</span>. ummujja bho puthusile, ummujja bho puthusileti <span class="note">[saṃ. ni. 4.358]</span>.</p>\r\n<p class="bodytext">tathā paccakkhā’paccakkhavasena, bho purisa, kahaṃ ekaputtaka, kahaṃ ekaputtakāti <span class="note">[saṃ. ni. 2.63; dha. pa. aṭṭha. 1.2]</span>.</p>\r\n<p class="bodytext">tathā niyamā’niyamavasena, bho purisa, acchariyaṃ vata bho abbhutaṃ vata bho <span class="note">[dī. ni. 2.192]</span>. yatra hi nāma saññī samānotiādi <span class="note">[dī. ni. 2.192]</span>. idaṃ āmantanaṃ nāma pageva siddhe eva hoti, na vidhātabbe, na hi pageva rājabhāvaṃ vā bhikkhubhāvaṃ vā appattaṃ janaṃ "bho rājā"ti vā "bho bhikkhū"ti vā āmantentīti.</p>\r\n<p class="centered">paṭhamāvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">dutiyāvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe dutiyā?</p>\r\n<p class="bodytext"><a name="para291"></a><span class="paranum">291</span>. <span class="bld">kamme dutiyā</span> <span class="note">[ka. 297; rū. 284; nī. 580; caṃ. 2.1.43; pā. 1.4.49-51]</span>.</p>\r\n<p class="bodytext">kammatthe dutiyā hoti. kariyateti kammaṃ, taṃ nibbattikammaṃ, vikatikammaṃ, pattikammanti tividhaṃ hoti.</p>\r\n<p class="bodytext">tattha <span class="bld">nibbattikammaṃ</span> yathā? iddhimā hatthivaṇṇaṃ māpeti, rājā nagaraṃ māpeti, mātā puttaṃ vijāyati, bījaṃ rukkhaṃ janeti, kammaṃ vipākaṃ janeti, āhāro balaṃ janeti, jano puññaṃ karoti, pāpaṃ karoti, buddhā dhammaṃ desesi, vinayaṃ paññapesi, bhikkhu jhānaṃ uppādeti, maggaṃ uppādeti iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-291-');
INSERT INTO pages VALUES(54575,'annya_sadda_10',168,replace(replace('\r\n<p class="bodytext"><span class="bld">vikatikammaṃ</span> <a name="M0.0168"></a> yathā? gehaṃ karoti, rathaṃ karoti, ghaṭaṃ karoti, paṭaṃ vāyati, odanaṃ pacati, bhattaṃ pacati, kaṭṭhaṃ aṅgāraṃ karoti, suvaṇṇaṃ kaṭakaṃ karoti, gehaṃ jhāpeti, rukkhaṃ chindati, pākāraṃ bhindati, vihayo lunāti, pāṇaṃ hanati, bhattaṃ bhuñjati iccādi.</p>\r\n<p class="bodytext"><span class="bld">pattikammaṃ</span> yathā? gāmaṃ gacchati, gehaṃ pavisati, rukkhaṃ ārohati, nadiṃ tarati, ādiccaṃ passati, dhammaṃ suṇāti, buddhaṃ vandati payirupāsati iccādi.</p>\r\n<p class="bodytext">pakatikammaṃ, vikatikammanti duvidhaṃ. suvaṇṇaṃ kaṭakaṃ karoti, kaṭṭhaṃ aṅgāraṃ karoti, purisaṃ ṭhitaṃ passati, purisaṃ gacchantaṃ passati, bhikkhuṃ passati sataṃ, sampajānaṃ, abhikkamantaṃ, paṭikkamantaṃ, ālokentaṃ, vilokentaṃ, samiñjentaṃ, pasārentaṃ.</p>\r\n<p class="bodytext">ettha ‘purisaṃ, bhikkhu’nti <span class="bld">pakatikammaṃ,</span> ‘ṭhitaṃ, sataṃ’iccādīni <span class="bld">vikatikammāni</span>.</p>\r\n<p class="bodytext">dhātukammaṃ, kāritakammanti duvidhaṃ. gāmaṃ gacchati, purisaṃ gāmaṃ gameti.</p>\r\n<p class="bodytext">dhātukammañca dvikammikadhātūnaṃ duvidhaṃ padhānakammaṃ, appadhānakammanti. ajapālo ajaṃ gāmaṃ neti, puriso bhāraṃ gāmaṃ vahati, harati, gāmaṃ sākhaṃ kaḍḍhati, gāviṃ khīraṃ dohati, brāhmaṇaṃ kambalaṃ yācati, brāhmaṇaṃ bhattaṃ bhikkhati, gāviyo vajaṃ avarundhati, bhagavantaṃ pañhaṃ pucchati, rukkhaṃ phalāni ocināti, sissaṃ dhammaṃ bravīti, bhagavā bhikkhū etadavoca <span class="note">[udā. 23 (thokaṃ visadisaṃ)]</span>, sissaṃ dhammaṃ anusāsati iccādi.</p>\r\n<p class="bodytext">ettha ca ‘ajaṃ, khīraṃ’ iccādi padhānakammaṃ nāma kattārā pariggahetuṃ iṭṭhatarattā. ‘gāmaṃ, gāviṃ’iccādi appadhānakammaṃ nāma tathā aniṭṭhatarattā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54576,'annya_sadda_10',169,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0169"></a> padhānakammaṃ kathinakammaṃ nāma, kammabhāve thirakammanti vuttaṃ hoti. appadhānakammaṃ akathinakammaṃ nāma, athirakammanti vuttaṃ hoti. tañhi kadāci sampadānaṃ hoti, kadāci apādānaṃ, kadāci sāmi, kadāci okāso. yathā – so maṃ dakāya neti, gāvito khīraṃ dohati, gāviyā khīraṃ dohahi, gāviyaṃ khīraṃ dohati iccādi.</p>\r\n<p class="bodytext">kamme dutiyāti vattate.</p>\r\n<p class="bodytext"><a name="para292"></a><span class="paranum">292</span>. <span class="bld">gatibodhāhārasaddatthā kammaka bhajjādīnaṃ payojje</span> <span class="note">[ka. 300; rū. 286; nī. 587; caṃ. 2.1.44; pā. 1.4.52]</span>.</p>\r\n<p class="bodytext">niccavidhisuttamidaṃ. gamanatthānaṃ bodhanatthānaṃ āhāratthānaṃ saddatthānaṃ akammakānaṃ bhajjādīnañca dhātūnaṃ payojje kammani dutiyā hoti. ettha ca payojjakammaṃ nāma kāritakammaṃ vuccati.</p>\r\n<p class="bodytext">puriso purisaṃ gāmaṃ gamayati, sāmiko ajapālaṃ ajaṃ gāmaṃ nayāpeti, ācariyo sissaṃ dhammaṃ bodheti, puriso purisaṃ bhattaṃ bhojeti, ācariyo sissaṃ dhammaṃ pāṭheti, puriso purisaṃ sayāpeti, acchāpeti, uṭṭhāpeti, puriso purisaṃ dhaññaṃ bhajjāpeti, koṭṭāpeti, uddharāpeti.</p>\r\n<p class="bodytext">etesamīti kiṃ? puriso purisena odanaṃ pāceti.</p>\r\n<p class="bodytext">ettha ca gamanatthādīnaṃ payojje tatiyāpi rūpasiddhiyaṃ <span class="note">[141 piṭṭhe]</span> saddanītiyañca <span class="note">[sutta-148 piṭṭhe]</span> vuttā. saddanītiyaṃ tatiyāpayogepi kammatthameva icchati. ñāsādīsu katvatthaṃ icchanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-292-');
INSERT INTO pages VALUES(54577,'annya_sadda_10',170,replace(replace('\r\n<p class="bodytext">yadā <a name="M0.0170"></a> pana paṭhamaṃ payojakaṃ añño dutiyo payojeti, tadā paṭhamo payojjo nāma. tasmiṃ tatiyāevāti vuttiyaṃ vuttaṃ. attanā vippakataṃ kuṭiṃ parehi pariyosāpeti <span class="note">[pārā. 363]</span>.</p>\r\n<p class="bodytext"><a name="para293"></a><span class="paranum">293</span>. <span class="bld">harādīnaṃ vā</span> <span class="note">[ka. 300; rū. 286; nī. 587; caṃ. 2.1.45; pā. 1.4.53]</span>.</p>\r\n<p class="bodytext">harādīnaṃ payojje kammani vikappena dutiyā hoti.</p>\r\n<p class="bodytext">sāmiko purisaṃ bhāraṃ hāreti purisena vā, purisaṃ āhāraṃ ajjhohāreti purisena vā, purisaṃ kammaṃ kāreti purisena vā, rājā purisaṃ attānaṃ dasseti purisena vā, purisaṃ buddhaṃ vandāpeti purisena vā.</p>\r\n<p class="bodytext"><a name="para194"></a><span class="paranum">194</span>. <span class="bld">na khādādīnaṃ</span> <span class="note">[ka. 300; rū. 286; nī. 587; caṃ. 2.1.47; pā. 1.4.57]</span>.</p>\r\n<p class="bodytext">khādādīnaṃ payojje kammani na dutiyā hoti.</p>\r\n<p class="bodytext">sāmiko purisena khajjaṃ khādāpeti, ada-bhakkhane, bhattaṃ ādeti, sāmiko dāsena purisaṃ avhāpeti, saddāyāpeti, kandayati, nādayati. ettha ca ‘saddāyāpetī’ti saddaṃ kārāpeti, nāmadhātu cesā. kanda, nadāpi saddatthāyeva.</p>\r\n<p class="bodytext"><a name="para295"></a><span class="paranum">295</span>. <span class="bld">vahissāniyantuke</span> <span class="note">[ka. 300; rū. 286; nī. 587; caṃ. 2.1.48; pā. 1.4.52]</span>.</p>\r\n<p class="bodytext">vahissāti dhātuniddeso i-kāro, niyāmeti payojetīti niyantā, natthi niyantā etassāti aniyantuko. yassa aññena payojakena kiccaṃ natthi, sayameva ñatvā vahati, so aniyantuko nāma, vahadhātussa tādise aniyantuke payojje kammani dutiyā na hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-293-194-295-');
INSERT INTO pages VALUES(54578,'annya_sadda_10',171,replace(replace('\r\n<p class="bodytext">sāmiko <a name="M0.0171"></a> dāsena bhāraṃ vāheti.</p>\r\n<p class="bodytext">aniyantuketi kiṃ? balībadde bhāraṃ vāheti.</p>\r\n<p class="bodytext"><a name="para296"></a><span class="paranum">296</span>. <span class="bld">bhakkhissāhiṃsāyaṃ</span> <span class="note">[ka. 300; rū. 286; nī. 587; caṃ. 2.1.49; pā. 1.4.5]</span>.</p>\r\n<p class="bodytext">bhakkhituṃ icchantassa bhakkhāpanaṃ hiṃsā nāma na hoti, anicchantassa bhakkhāpanaṃ hiṃsā nāma, bhakkhadhātussa payojje kammani ahiṃsāvisaye dutiyā na hoti.</p>\r\n<p class="bodytext">sāmiko purisena modake bhakkhāpeti.</p>\r\n<p class="bodytext">ahiṃsāyanti kiṃ? balībadde sassaṃ bhakkhāpeti. ettha ‘sassa’nti thūlataraṃ sassanti vadanti.</p>\r\n<p class="bodytext">pāḷiyaṃ "sabbesaṃ viññāpetvāna <span class="note">[apa. thera 1.1.438]</span>, tosenti sabbapāṇinaṃ <span class="note">[apa. thera 1.1.300]</span>. therassa patto dutiyassa gāhetabbo" iccādinā <span class="note">[pārā. 615]</span> payojje chaṭṭhīpi dissati.</p>\r\n<p class="bodytext"><a name="para297"></a><span class="paranum">297</span>. <span class="bld">jhādīhi yuttā</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.50; pā. 2.3.2]</span>.</p>\r\n<p class="bodytext">dhīiccādīhi nipātopasaggehi yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">dhī brāhmaṇassa hantāraṃ <span class="note">[dha. pa. 389]</span>, dhīratthu’maṃ pūtikāyaṃ <span class="note">[jā. 1.3.129]</span>, dhīratthu taṃ dhanalābhaṃ <span class="note">[jā. 1.4.36]</span>, dhīratthu bahuke kāme. tatiyāpi dissati, dhīratthu jīvitena me <span class="note">[jā. 2.17.135]</span>. antarā ca rājagahaṃ antarā ca nāḷandaṃ <span class="note">[dī. ni. 1.1]</span>, abhito gāmaṃ vasati, paritogāmaṃ vasati, nadiṃ nerañjaraṃ pati <span class="note">[su. ni. 427]</span>, etesu chaṭṭhyatthe dutiyā.</p>\r\n','\r',char(13)),'\n',char(10)),'-296-297-');
INSERT INTO pages VALUES(54579,'annya_sadda_10',172,replace(replace('\r\n<p class="bodytext">tathā <a name="M0.0172"></a> paṭibhāti maṃ bhagavā <span class="note">[udā. 45; saṃ. ni. 1.217]</span>, apissu maṃ tisso upamāyo paṭibhaṃsu <span class="note">[ma. ni. 1.374]</span>, paṭibhātu taṃ bhikkhu dhammo bhāsituṃ <span class="note">[mahāva. 258]</span>. paṭibhantu taṃ cunda bojjhaṅgā <span class="note">[saṃ. ni. 3.79]</span> – ‘ma’nti mama, ‘ta’nti tava, sampadānatthe dutiyā. ‘ma’nti mamañāṇe, ‘ta’nti tavañāṇetipi vaṇṇesuṃ. na upāyamantarena atthassa siddhi, natthi samādānamantarena sikkhāpaṭilābho, nevidha na huraṃ na ubhayamantarena, esevanto dukkhassa <span class="note">[ma. ni. 3.393; udā. 74]</span>. tattha ‘antarenā’ti nipātapadametaṃ, vajjetvātyattho. pubbena gāmaṃ, dakkhiṇena gāmaṃ, uttarena gāmaṃ, gāmassa pubbeti attho.</p>\r\n<p class="bodytext">upasaggapubbānaṃ akammakadhātūnaṃ payoge ādhāre dutiyā, pathaviṃ adhisessati, gāmaṃ adhitiṭṭhati, rukkhaṃ ajjhāvasati, mañcaṃ vā pīṭhaṃ vā abhinisīdeyya vā abhinipajjeyya vā <span class="note">[pāci. 130]</span>, gāmaṃ upavasati, gāmaṃ anuvasati, pabbataṃ adhivasati, gharaṃ āvasati, agāraṃ ajjhāvasati <span class="note">[dī. ni. 1.258; pārā. 519]</span>, uposathaṃ upavasati, kāmāvacaraṃ upapajjati, rūpāvacaraṃ upapajjati, arūpāvacaraṃ upapajjati, sakkassa sahabyataṃ upapajjati, nipannaṃ vā upanipajjeyya <span class="note">[dī. ni. 3.282]</span>, nisinnaṃ vā upanisīdeyya <span class="note">[dī. ni. 3.282]</span>, ṭhitaṃ vā upatiṭṭheyya <span class="note">[dī. ni. 3.282]</span> iccādi.</p>\r\n<p class="bodytext">tappāna, cārepi dutiyā, nadiṃ pivati, samuddaṃ pivati, gāmaṃ carati, araññaṃ carati, nadiyaṃ, gāmeti attho.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54580,'annya_sadda_10',173,replace(replace('\r\n<p class="bodytext">kāla, disāsupi ādhāre eva dutiyā, taṃ khaṇaṃ, taṃ muhuttaṃ, taṃ kālaṃ, ekamantaṃ <span class="note">[khu. pā. 5.1]</span>, ekaṃ samayaṃ <span class="note">[khu. pā. 5.1; dī. ni. 1.1]</span>, pubbaṇhasamayaṃ <span class="note">[pārā. 16]</span>, sāyanhasamayaṃ, taṃ divasaṃ, imaṃ rattiṃ <span class="note">[dī. ni. 3.285]</span>, dutiyampi, tatiyampi, catutthaṃ vā pañcamaṃ vā appeti, tato pubbaṃ, tato paraṃ, purimaṃ disaṃ <span class="note">[dī. ni. 2.336]</span>, dakkhiṇaṃ disaṃ <span class="note">[dī. ni. 2.336]</span>, pacchimaṃ disaṃ <span class="note">[dī. ni. 2.336]</span>, uttaraṃ disaṃ <span class="note">[dī. ni. 2.336]</span>, imā dasa <a name="M0.0173"></a> disāyo, katamaṃ disaṃ tiṭṭhati nāgarājā <span class="note">[jā. 1.16.104]</span>, imāsu disāsu katamāya disāya tiṭṭhati chaddantanāgarājāti atthoiccādi.</p>\r\n<p class="bodytext"><a name="para298"></a><span class="paranum">298</span>. <span class="bld">lakkhaṇitthambhūtavicchāsvabhinā</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.54; pā. 1.4.90, 91; 2.3.8]</span>.</p>\r\n<p class="bodytext">lakkhaṇādīsu atthesu pavattena abhinā yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">lakkhīyati lakkhitabbaṃ anenāti <span class="bld">lakkhaṇaṃ</span>. ayaṃ pakāro itthaṃ, īdiso visesoti attho. itthaṃ bhūto pattoti <span class="bld">itthambhūto</span>. bhinne atthe byāpituṃ icchā <span class="bld">vicchā</span>.</p>\r\n<p class="bodytext">tattha lakkhaṇe –</p>\r\n<p class="bodytext">rukkhamabhi vijjotate vijju, rukkhaṃ abhi byāpetvā vijjotateti attho, vijjobhāsena byāpito rukkho vijjuppādassa lakkhaṇaṃ saññāṇaṃ hoti.</p>\r\n<p class="bodytext">itthambhūte –</p>\r\n<p class="bodytext">sādhu devadatto mātaramabhi, mātaraṃ abhi visiṭṭhaṃ katvā sādhūti attho, devadatto sakkaccaṃ mātupaṭṭhāne aggapurisoti vuttaṃ hoti. taṃ kho pana bhavantaṃ gotamaṃ evaṃ kalyāṇo kittisaddo abbhuggato <span class="note">[pārā. 1]</span>. ettha ca ‘abbhuggato’ti abhi visiṭṭhaṃ katvā uggatoti attho, ayaṃ kittisaddo bhoto gotamassa sakalalokaggabhāvaṃ pakāsetvā uggatoti vuttaṃ hoti, kittisaddasambandhe pana tassa kho pana bhoto gotamassāti attho.</p>\r\n<p class="bodytext">vicchāyaṃ –</p>\r\n','\r',char(13)),'\n',char(10)),'-298-');
INSERT INTO pages VALUES(54581,'annya_sadda_10',174,replace(replace('\r\n<p class="bodytext">rukkhaṃ <a name="M0.0174"></a> rukkhaṃ abhi vijjotate cando, byāpetvā vijjotatetyattho.</p>\r\n<p class="bodytext">ettha ca lakkhaṇādiatthā abhisaddena jotanīyā piṇḍatthā eva, na vacanīyatthā, byāpanādiatthā eva vacanīyatthāti.</p>\r\n<p class="bodytext"><a name="para299"></a><span class="paranum">299</span>. <span class="bld">patiparīhi bhāge ca</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.55; pā. 1.4.90]</span>.</p>\r\n<p class="bodytext">lakkhaṇi’tthambhūta, vicchāsu ca bhāge ca pavattehi pati, parīhi yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">lakkhaṇe –</p>\r\n<p class="bodytext">rukkhaṃ pati vijjotate vijju, rukkhaṃ pari vijjotate vijju. tattha ‘patī’ti paṭicca, ‘parī’ti pharitvā.</p>\r\n<p class="bodytext">itthambhūte –</p>\r\n<p class="bodytext">sādhu devadatto mātaraṃ pati, sādhu devadatto mātaraṃ pari.</p>\r\n<p class="bodytext">vicchāyaṃ –</p>\r\n<p class="bodytext">rukkhaṃ rukkhaṃ pati vijjotate cando, rukkhaṃ rukkhaṃ pari vijjotate cando.</p>\r\n<p class="bodytext">bhāge –</p>\r\n','\r',char(13)),'\n',char(10)),'-299-');
INSERT INTO pages VALUES(54582,'annya_sadda_10',175,replace(replace('\r\n<p class="bodytext">taṃ dīyatu, yadettha maṃ pati siyā, taṃ dīyatu, yadettha maṃ pari siyā. tattha ‘patī’ti paṭicca, ‘parī’ti paricca, uddissāti attho, ‘ṭhapita’nti pāṭhaseso. ettha maṃ uddissa yaṃ <a name="M0.0175"></a> vatthu ṭhapitaṃ siyā, taṃ me dīyatūtyattho, etesu bahūsu bhāgesu yo mama bhāgo, so mayhaṃ dīyatūti vuttaṃ hotīti.</p>\r\n<p class="bodytext"><a name="para300"></a><span class="paranum">300</span>. <span class="bld">anunā</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.56; pā. 1.4.84, 90]</span>.</p>\r\n<p class="bodytext">lakkhaṇi’tthambhūta, vicchāsu ca bhāge ca pavattena anunā yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">lakkhaṇe –</p>\r\n<p class="bodytext">rukkhaṃ anu vijjotate vijju, rukkhaṃ anu pharitvāti attho. caturāsītisahassāni, sambuddhamanupabbajuṃ <span class="note">[bu. vaṃ. 21.5]</span>, ‘sambuddha’nti bodhisattaṃ, anu gantvā pabbajiṃsūti attho, vipassibodhisatte pabbajite sati tānipi caturāsītikulaputtasahassāni pabbajiṃsūti vuttaṃ hoti. saccakriyamanu vuṭṭhi pāvassi, ‘anū’ti anvāya, paṭiccāti attho, saccakriyāya sati saccakriyahetu devo pāvassīti vuttaṃ hoti. "hetu ca lakkhaṇaṃ bhavatī"ti vuttiyaṃ vuttaṃ. saccakriyāya sahevātipi yujjati. "saha sacce kate mayha"nti <span class="note">[cariyā. 3.82]</span> hi vuttaṃ.</p>\r\n<p class="bodytext">itthambhūte –</p>\r\n<p class="bodytext">sādhu devadatto mātaramanu. tattha ‘anū’ti anvāya paṭicca.</p>\r\n<p class="bodytext">vicchāyaṃ –</p>\r\n<p class="bodytext">rukkhaṃ rukkhaṃ anu vijjotate cando. tattha ‘anū’ti anu pharitvā.</p>\r\n<p class="bodytext">bhāge –</p>\r\n','\r',char(13)),'\n',char(10)),'-300-');
INSERT INTO pages VALUES(54583,'annya_sadda_10',176,replace(replace('\r\n<p class="bodytext">yadettha <a name="M0.0176"></a> maṃ anu siyā, taṃ dīyatu. tattha ‘anū’ti anvāya. sesaṃ vuttanayameva.</p>\r\n<p class="bodytext"><a name="para301"></a><span class="paranum">301</span>. <span class="bld">sahatthe</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.57; pā. 1.4.85]</span>.</p>\r\n<p class="bodytext">sahatthe anunā yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">pabbataṃ anu tiṭṭhati <span class="note">[pabbatamanusenā tiṭṭhati (moggallānavuttiyaṃ)]</span>. nadiṃ anvāvasitā bārāṇasī. ‘anū’ti anugantvā, nadiyā saha ābaddhā tiṭṭhatīti vuttaṃ hoti.</p>\r\n<p class="bodytext"><a name="para302"></a><span class="paranum">302</span>. <span class="bld">hīne</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.58; pā. 1.4.86]</span>.</p>\r\n<p class="bodytext">hīne pavattena anunā yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">anu sāriputtaṃ paññavanto, anugatā pacchato gatāti attho, sabbe paññavanto sāriputtato hīnāti vuttaṃ hoti.</p>\r\n<p class="bodytext"><a name="para303"></a><span class="paranum">303</span>. <span class="bld">upena</span> <span class="note">[ka. 299; rū. 288; nī. 582, 586; caṃ. 2.1.59; pā. 1.4.87]</span>.</p>\r\n<p class="bodytext">hīne upena yuttā liṅgamhā dutiyā hoti.</p>\r\n<p class="bodytext">upa sāriputtaṃ paññavanto, upecca gatā samīpe gatāti attho, hīnātveva vuttaṃ hoti.</p>\r\n<p class="bodytext">ettha ca abhiiccādayo kammappavacanīyāti saddasatthesu vuttā. tattha pakārena vuccatīti <span class="bld">pavacanīyaṃ,</span> pakāro ca lakkhaṇi’tthambhūta, vicchādiko piṇḍattho vuccati, <span class="bld">kamma</span>nti byāpanādikriyā, kammaṃ pavacanīyaṃ yehi te <span class="bld">kammappavacanīyā</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-301-302-303-');
INSERT INTO pages VALUES(54584,'annya_sadda_10',177,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0177"></a> byāpanādikriyāvisesavācīhi upasaggehi sambandhesati kammatthe dutiyā hoti, asambandhe pana ādhāra, sāmyādiatthesu hoti, lakkhaṇādayo pana sāmatthiyasiddhā piṇḍatthā evāti.</p>\r\n<p class="bodytext"><a name="para304"></a><span class="paranum">304</span>. <span class="bld">kāladdhānamaccantasaṃyoge</span> <span class="note">[ka. 298; rū. 287; nī. 581; pā. 2.3.5]</span></p>\r\n<p class="bodytext">kālassa vā addhuno vā dabba, guṇa, kriyāhi accantaṃ nirantaraṃ saṃyoge kāla’ddhānavācīhi liṅgehi paraṃ dutiyā hoti.</p>\r\n<p class="bodytext">kāle –</p>\r\n<p class="bodytext">sattāhaṃ gavapānaṃ, māsaṃ maṃsodanaṃ, saradaṃ ramaṇīyā nadī, sabbakālaṃ ramaṇīyaṃ nandanaṃ, māsaṃ sajjhāyati, vassasataṃ jīvati, tayo māse abhidhammaṃ deseti.</p>\r\n<p class="bodytext">addhāne –</p>\r\n<p class="bodytext">yojanaṃ vanarāji, yojanaṃ dīgho pabbato, kosaṃ sajjhāyati.</p>\r\n<p class="bodytext">accantasaṃyogeti kiṃ? māse māse bhuñjati, yojane yojane vihāro.</p>\r\n<p class="bodytext">ettha ca kriyāvisesanampi kattārā sādhetabbattā kammagatikaṃ hoti, tasmā tampi ‘kamme dutiyā’ti ettha kammasaddena gayhati.</p>\r\n','\r',char(13)),'\n',char(10)),'-304-');
INSERT INTO pages VALUES(54585,'annya_sadda_10',178,replace(replace('\r\n<p class="bodytext">sukhaṃ seti, dukkhaṃ seti, sīghaṃ gacchati, khippaṃ gacchati, dandhaṃ gacchati, muduṃ pacati, garuṃ essati, lahuṃ essati, sannidhikārakaṃ bhuñjati, samparivattakaṃ otāpeti, kāyappacālakaṃ gacchati <span class="note">[pāci. 590]</span>, hatthappacālakaṃ <a name="M0.0178"></a> gacchati, sīsappacālakaṃ gacchati <span class="note">[pāci. 594-595]</span>, surusurukārakaṃ bhuñjati <span class="note">[pāci. 627]</span>, avagaṇḍakārakaṃ bhuñjati <span class="note">[pāci. 622]</span>, piṇḍukkhepakaṃ bhuñjati <span class="note">[pāci. 620]</span>, hatthaniddhunakaṃ bhuñjati <span class="note">[pāci. 623]</span>, hatthanillehakaṃ bhuñjati <span class="note">[pāci. 628]</span>, candimasūriyā samaṃ pariyāyanti, visamaṃ pariyāyanti iccādi.</p>\r\n<p class="centered">dutiyāvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">tatiyāvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe tatiyā?</p>\r\n<p class="bodytext"><a name="para305"></a><span class="paranum">305</span>. <span class="bld">kattukaraṇesu tatiyā</span> <span class="note">[ka. 286, 288; rū. 291, 293; nī. 591, 594; caṃ. 2.1.62-3; pā. 2.3.18]</span>.</p>\r\n<p class="bodytext">kattari karaṇe ca tatiyā hoti. kattāti ca kārakoti ca atthato ekaṃ "karotīti <span class="bld">kattā,</span> karotīti <span class="bld">kārako"</span>ti, tasmā "kattukārako"ti vutte dvinnaṃ pariyāyasaddānaṃ vasena ayameva kriyaṃ ekantaṃ karoti, sāmī hutvā karoti, attappadhāno hutvā karotīti viññāyati, tato kriyā nāma kattuno eva byāpāro, na aññesanti ca, aññe pana kriyāsādhane kattuno upakārakattā kārakā nāmāti ca, tathā anupakārakattā akārakā nāmāti ca viññāyantīti.</p>\r\n<p class="bodytext">tattha kattā tividho sayaṃkattā, payojakakattā, kammakattāti.</p>\r\n<p class="bodytext">tattha dhātvatthaṃ sayaṃ karonto <span class="bld">sayaṃkattā</span> nāma, puriso kammaṃ karoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-305-');
INSERT INTO pages VALUES(54586,'annya_sadda_10',179,replace(replace('\r\n<p class="bodytext">paraṃ <a name="M0.0179"></a> niyojento <span class="bld">payojakakattā</span> nāma, puriso dāsaṃ kammaṃ kāreti.</p>\r\n<p class="bodytext">kammakattā nāma payojjakakattāpi vuccati, puriso dāsena kammaṃ kāreti dāsassa vā, yo ca aññena kataṃ payogaṃ paṭicca kammabhūtopi sukarattā vā kammabhāvena avattukāmatāya vā ajānanatāya vā vañcetukāmatāya vā kattubhāvena voharīyati, so <span class="bld">kammakattā</span> nāma, kusūlo sayameva bhijjati, ghaṭo sayameva bhijjati. apica sukaro vā hotu dukkaro vā, yo kammarūpakriyāpade paṭhamanto kattā, so kammakattāti vuccati. saddarūpena kammañca taṃ attharūpena kattā cāti kammakattā, kusūlo bhijjati, ghaṭo bhijjati, paccati munino bhattaṃ, thokaṃ thokaṃ ghare ghare iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54587,'annya_sadda_10',180,replace(replace('\r\n<p class="bodytext">ettha ca saddattho duvidho paramattho, paññattatthoti. tattha <span class="bld">paramattho</span> ekantena vijjamānoyeva. <span class="bld">paññattattho</span> pana koci vijjamānoti sammato. yathā? rājaputto, govisāṇaṃ, campakapupphanti. koci avijjamānoti sammato. yathā? vañjhāputto, sasavisāṇaṃ, udumbarapupphanti. saddo ca nāma vatticchāpaṭibaddhavuttī hoti, vattamāno ca saddo atthaṃ na dīpetīti natthi, saṅkete sati suṇantassa atthavisayaṃ buddhiṃ na janetīti natthīti adhippāyo. iti avijjamānasammatopi attho saddabuddhīnaṃ visayabhāvena vijjamāno eva hoti. itarathā ‘vañjhāputto’ti padaṃ suṇantassa tadatthavisayaṃ cittaṃ nāma na pavatteyyāti, saddabuddhīnañca visayabhāvena vijjamāno nāma attho saddanānātte buddhinānātte ca sati nānā hoti, visuṃ visuṃ vijjamāno nāma hotīti adhippāyo. evaṃ saddabuddhivisayabhāvena vijjamānañca nānābhūtañca atthaṃ paṭicca kārakanānāttaṃ kriyākārakanānāttañca hoti<a name="M0.0180"></a>, na pana sabhāvato vijjamānameva nānābhūtameva ca atthanti niṭṭhamettha gantabbaṃ. tasmā "saṃyogo jāyate" iccādīsu saddabuddhīnaṃ nānāttasiddhena atthanānāttena dvinnaṃ saddānaṃ dvinnaṃ atthānañca kriyākārakatāsiddhi veditabbāti.</p>\r\n<p class="bodytext">kayirate anenāti <span class="bld">karaṇaṃ,</span> kriyāsādhane kattuno sahakārīkāraṇanti vuttaṃ hoti. taṃ duvidhaṃ ajjhattikakaraṇaṃ, bāhirakaraṇanti.</p>\r\n<p class="bodytext">tattha kattuno aṅgabhūtaṃ karaṇaṃ <span class="bld">ajjhattikaṃ</span> nāma, puriso cakkhunā rūpaṃ passati, manasā dhammaṃ vijānāti, hatthena kammaṃ karoti, pādena maggaṃ gacchati, rukkho phalabhārena oṇamati.</p>\r\n<p class="bodytext">kattuno bahibhūtaṃ <span class="bld">bāhiraṃ</span> nāma, puriso yānena gacchati, pharasunā <span class="note">[parasunā (sakkataganthesu)]</span> chindati, rukkho vātena oṇamati.</p>\r\n<p class="bodytext"><a name="para306"></a><span class="paranum">306</span>. <span class="bld">sahatthena</span> <span class="note">[ka. 287; rū. 296; nī. 592; caṃ. 2.1.65; pā. 2.3.19]</span>.</p>\r\n<p class="bodytext">sahasaddassa attho yassa soti sahattho, sahatthena saddena yuttā liṅgamhā tatiyā hoti. sahasaddassa attho nāma samavāyattho.</p>\r\n<p class="bodytext">so tividho <span class="bld">dabbasamavāyo, guṇasamavāyo, kriyāsamavāyo</span>ti. puttena saha dhanavā pitā, puttena saha thūlo pitā, puttena saha āgato pitā. saha, saddhiṃ, samaṃ, nānā, vināiccādiko sahatthasaddo nāma.</p>\r\n<p class="bodytext">nisīdi bhagavā saddhiṃ bhikkhusaṅghena <span class="note">[mahāva. 59]</span>, sahassena samaṃ mitā <span class="note">[saṃ. ni. 1.32]</span>, piyehi nānābhāvo vinābhāvo <span class="note">[dī. ni. 2.183, 207]</span>, saṅgho saha vā gaggena vinā vā gaggena uposathaṃ kareyya <span class="note">[mahāva. 167]</span>.</p>\r\n<p class="bodytext"><a name="para307"></a><span class="paranum">307</span>. <span class="bld">lakkhaṇe</span> <span class="note">[rū. 147 piṭṭhe; nī. 598; caṃ. 2.1.66; pā. 2.3.21]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-306-307-');
INSERT INTO pages VALUES(54588,'annya_sadda_10',181,replace(replace('\r\n<p class="bodytext">lakkhaṇaṃ <a name="M0.0181"></a> vuccati itthambhūtalakkhaṇaṃ, tasmiṃ tatiyā hoti.</p>\r\n<p class="bodytext">assupuṇṇehi nettehi, pitaraṃ so udikkhati <span class="note">[jā. 2.22.2123]</span>. brahmabhūtena attanā viharati <span class="note">[a. ni. 3.67]</span>, asambhinnena vilepanena rājānamadakkhi, tidaṇḍakena paribbājakamadakkhi, ūnapañcabandhanena pattena aññaṃ pattaṃ cetāpeti <span class="note">[pārā. 612 (thokaṃ visadisaṃ)]</span>, bhikkhu pāsādikena abhikkantena paṭikkantena ālokitena vilokitena samiñjitena pasāritena gāmaṃ piṇḍāya pāvisi, sā kāḷī dāsī bhinnena sīsena lohitena galantena pativissakānaṃ ujjhāpesi <span class="note">[ma. ni. 1.226]</span>, ukkhittakāya antaraghare gacchanti <span class="note">[pāci. 584]</span>, pallatthikāya antaraghare nisīdanti <span class="note">[pāci. 599]</span>.</p>\r\n<p class="bodytext">aṅgavikāropi idha saṅgayhati <span class="note">[ka. 291; rū. 299; nī. 603]</span>, akkhinā kāṇaṃ passati, ‘akkhī’ti idaṃ ‘kāṇa’nti pade visesanaṃ, vikalena cakkhuaṅgena so kāṇo nāma hoti. hatthena kuṇiṃ passati, pādena khañjaṃ passati.</p>\r\n<p class="bodytext"><a name="para308"></a><span class="paranum">308</span>. <span class="bld">hetumhi</span> <span class="note">[ka. 289; rū. 297; nī. 601]</span>.</p>\r\n<p class="bodytext">hinoti pavattati phalaṃ etenāti hetu, tasmiṃ tatiyā hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-308-');
INSERT INTO pages VALUES(54589,'annya_sadda_10',182,replace(replace('\r\n<p class="bodytext">annena vasati, vijjāya sādhu, kammunā vattati loko, kammunā vattati pajā <span class="note">[ma. ni. 2.460; su. ni. 659 (vattatī pajā)]</span>. kammunā vasalo hoti, kammunā hoti brāhmaṇo <span class="note">[su. ni. 136]</span>. kenaṭṭhena <span class="note">[dha. sa. aṭṭha. nidānakathā]</span>, kena nimittena, kena vaṇṇena <a name="M0.0182"></a> <span class="note">[saṃ. ni. 1.234]</span> kena paccayena, kena hetunā <span class="note">[jā. 2.22.2097]</span>, kena kāraṇena <span class="note">[jā. aṭṭha. 4.15 mātaṅgajātakavaṇṇanā]</span> iccādi.</p>\r\n<p class="bodytext">ettha ca karaṇaṃ tividhaṃ kriyāsādhakakaraṇaṃ, visesanakaraṇaṃ, nānāttakaraṇanti.</p>\r\n<p class="bodytext">tattha <span class="bld">kriyāsādhakaṃ</span> pubbe vuttameva.</p>\r\n<p class="bodytext"><span class="bld">visesanakaraṇaṃ</span> yathā? ādicco nāma gottena, sākiyo nāma jātiyā <span class="note">[su. ni. 425]</span>. gottena gotamo nātho <span class="note">[apa. thera 1.1.253 (visadisaṃ)]</span>, sāriputtoti nāmena <span class="note">[apa. thera 1.1.251]</span>, vissuto paññavā ca so, jātiyā khattiyo buddhā <span class="note">[dī. ni. 2.92]</span>, jātiyā sattavassiko <span class="note">[mi. pa. 6.4.8]</span>, sippena naḷakāro so, ekūnatiṃso vayasā <span class="note">[dī. ni. 2.214]</span>, vijjāya sādhu, tapasā uttamo, suvaṇṇena abhirūpo, pakatiyā abhirūpo, pakatiyā bhaddako, yebhuyyena mattikā <span class="note">[pāci. 86]</span>, dhammena samena rajjaṃ kāreti, samena dhāvati, visamena dhāvati, sukhena sukhito homi, pāmojjena pamudito <span class="note">[bu. vaṃ. 2.78]</span>. dvidoṇena dhaññaṃ kiṇāti, sahassena asse vikkiṇāti, attanāva attānaṃ sammannati <span class="note">[pārā. aṭṭha. 1.paṭhamamahāsaṅgītikathā (sammanni)]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54590,'annya_sadda_10',183,replace(replace('\r\n<p class="bodytext"><span class="bld">nānāttakaraṇaṃ</span> yathā? kiṃ me ekena tiṇṇena, purisena thāmadassinā <span class="note">[bu. vaṃ. 2.56]</span>. kiṃ te jaṭāhi dummedha, kiṃ te ajinasāṭiyā <span class="note">[dha. pa. 394]</span>. alaṃ te idha vāsena <span class="note">[pārā. 436]</span>, alaṃ me buddhena <span class="note">[pārā. 52]</span>, kinnumebuddhena <span class="note">[pārā. 52]</span>, na mamattho buddhena <span class="note">[pārā. 52]</span>, maṇinā me attho <span class="note">[pārā. 344]</span>, vadeyyātha bhante yenattho, pānīyena <span class="note">[pāṇiyena (mū.)]</span> attho, mūlehi bhesajjehi attho <span class="note">[mahāva. 263]</span>, seyyena atthiko, mahagghena <a name="M0.0183"></a> atthiko, māsena pubbo, pitarā sadiso, mātarā samo, kahāpaṇena ūno, dhanena vikalo, asinā kalaho, vācāya kalaho, ācārena nipuṇo, vācāya nipuṇo, guḷena missako, tilena missako, vācāya sakhilo iccādi.</p>\r\n<p class="bodytext">tathā kammā’vadhi, ādhāra’ccantasaṃyoga, kriyāpavaggāpi nānāttakaraṇe saṅgayhanti.</p>\r\n<p class="bodytext">kamme tāva –</p>\r\n<p class="bodytext">tilehi khette vappati, tantavāyehi cīvaraṃ vāyāpeti, sunakhehi khādāpenti iccādi.</p>\r\n<p class="bodytext">avadhimhi –</p>\r\n<p class="bodytext">sumuttā mayaṃ tena mahāsamaṇena <span class="note">[cūḷava. 437]</span>, muttomhi kāsirājena, cakkhu suññaṃ attena vā attaniyena vā <span class="note">[saṃ. ni. 4.85]</span>, ottappati kāyaduccaritena <span class="note">[a. ni. 5.2]</span>, hirīyati kāyaduccaritena <span class="note">[a. ni. 5.2]</span>, jigucchati sakena kāyena, pathabyā ekarajjena, saggassa gamanena vā. sabbalokādhipaccena, sotāpattiphalaṃ varaṃ <span class="note">[dha. pa. 178]</span> iccādi.</p>\r\n<p class="bodytext">ādhāre –</p>\r\n<p class="bodytext">tena khaṇena tena layena tena muhuttena <span class="note">[mahāva. 17]</span>, tena samayena <span class="note">[pārā. 1]</span>, kālena dhammassavanaṃ <span class="note">[khu. pā. 5.9]</span>. so vo mamaccayena satthā <span class="note">[dī. ni. 2.216]</span>, tiṇṇaṃ māsānaṃ accayena <span class="note">[dī. ni. 2.168; udā. 51]</span>, pubbena gāmaṃ, dakkhiṇena gāmaṃ, puratthimena dhataraṭṭho, dakkhiṇena virūḷhako pacchimena virūpakkho <span class="note">[dī. ni. 2.336]</span>, yena bhagavā tenupasaṅkami <span class="note">[khu. pā. 5.1]</span> iccādi.</p>\r\n<p class="bodytext">accantasaṃyoge –</p>\r\n<p class="bodytext">māsena bhuñjati, yojanena dhāvati iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54591,'annya_sadda_10',184,replace(replace('\r\n<p class="bodytext">kriyāpavaggo <a name="M0.0184"></a> nāma kriyāya sīghataraṃ niṭṭhāpanaṃ, tasmiṃ jotetabbe tatiyā, ekāheneva bārāṇasiṃ pāpuṇi, tīhi māsehi abhidhammaṃ desesi, navahi māsehi vihāraṃ niṭṭhāpesi, gamanamattena labhati, oṭṭhapahaṭamattena paguṇaṃ akāsi.</p>\r\n<p class="centered">tatiyāvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">catutthīvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe catutthī?</p>\r\n<p class="bodytext"><a name="para309"></a><span class="paranum">309</span>. <span class="bld">sampadāne catutthī</span> <span class="note">[ka. 293; rū. 301; nī. 605; catutthī sampadāne (bahūsu), caṃ. 2.1.73; pā. 2.3.13]</span>.</p>\r\n<p class="bodytext">sampadāne catutthī hoti. sammā padīyate assāti sampadānaṃ, sampaṭicchakanti vuttaṃ hoti.</p>\r\n<p class="bodytext">taṃ <span class="bld">vatthusampaṭicchakaṃ, kriyāsampaṭicchaka</span>nti duvidhaṃ. bhikkhussa cīvaraṃ deti, buddhassa silāghate.</p>\r\n<p class="bodytext">puna anirākaraṇaṃ, anumati, ārādhananti tividhaṃ hoti. tattha na nirākaroti na nivāretīti <span class="bld">anirākaraṇaṃ,</span> diyyamānaṃ na paṭikkhipatīti attho. asati hi paṭikkhipane sampaṭicchanaṃ nāma hotīti. kāyacittehi sampaṭicchanākāraṃ dassetvā paṭiggaṇhantaṃ sampadānaṃ <span class="bld">anumati</span> nāma. vividhehi āyācanavacanehi parassa cittaṃ ārādhetvā sampaṭicchantaṃ <span class="bld">ārādhanaṃ</span> nāma. bodhirukkhassa jalaṃ deti, bhikkhussa annaṃ deti, yācakassa annaṃ deti.</p>\r\n<p class="bodytext">kriyāsampaṭicchakaṃ nānākriyāvasena bahuvidhaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-309-');
INSERT INTO pages VALUES(54592,'annya_sadda_10',185,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0185"></a> rocanakriyāyoge –</p>\r\n<p class="bodytext">tañca amhākaṃ ruccati ceva khamati ca <span class="note">[ma. ni. 1.179; ma. ni. 2.435]</span>, pabbajjā mama ruccati <span class="note">[jā. 2.22.43]</span>, kassa sāduṃ na ruccati, na me ruccati bhaddante, ulūkassābhisecanaṃ <span class="note">[jā. 1.3.60]</span>. gamanaṃ mayhaṃ ruccati, māyasmantānampi saṅghabhedo ruccittha <span class="note">[pārā. 418]</span>, yassāyasmato na khamati, khamati saṅghassa <span class="note">[pārā. 438]</span>, bhattaṃ mayhaṃ chādeti, bhattamassa nacchādeti <span class="note">[cūḷava. 282]</span>, tesaṃ bhikkhūnaṃ lūkhāni bhojanāni nacchādenti <span class="note">[mahāva. 261 (thokaṃ visadisaṃ)]</span>. tattha ‘chādetī’ti icchaṃ uppādetīti attho.</p>\r\n<p class="bodytext">dhāraṇappayoge –</p>\r\n<p class="bodytext">chattaggāho rañño chattaṃ dhāreti, sampatijātassa bodhisattassa devā chattaṃ dhārayiṃsu.</p>\r\n<p class="bodytext">buddhassa silāghate, thometīti attho, tuyhaṃ hanute, tuṇhibhāvena vañcetīti attho, bhikkhunī bhikkhussa bhuñjamānassa pānīyena vā vidhūpanena vā upaṭṭhāti <span class="note">[pāci. 816 (visadisaṃ)]</span>. dutiyāpi hoti, rañño upaṭṭhāti, rājānaṃ upaṭṭhāti, ahaṃ bhotiṃ upaṭṭhissaṃ <span class="note">[jā. 2.22.1934]</span>, ahaṃ taṃ upaṭṭhissāmi, mātāpituupaṭṭhānaṃ <span class="note">[khu. pā. 5.6]</span>, tuyhaṃ sapate, sapassu me vepacitti <span class="note">[saṃ. ni. 1.253]</span>, sapathampi te samma ahaṃ karomi <span class="note">[jā. 2.21.407]</span>, tava mayi saddahanatthaṃ saccaṃ karomīti attho, rañño sataṃ dhāreti, idha kulaputto na kassaci kiñci dhāreti <span class="note">[a. ni. 4.62]</span>, tassa rañño mayaṃ nāgaṃ dhārayāma. tattha ‘rañño sataṃ dhāretī’ti sataṃ balidhanaṃ vā daṇḍadhanaṃ vā nidetīti <span class="note">[nidhetīti, nidhema (keci)]</span> attho, "iṇaṃ katvā gaṇhātī"ti ca vadanti. ‘dhārayāmā’ti puna nidema <span class="note">[nidhetīti, nidhema (keci)]</span>, tuyhaṃ saddahati, mayhaṃ saddahati, saddahāsi siṅgālassa, surāpītassa brāhmaṇa <span class="note">[jā. 1.1.113]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54593,'annya_sadda_10',186,replace(replace('\r\n<p class="bodytext">devāpi <a name="M0.0186"></a> te pihayanti tādino <span class="note">[dha. pa. 94 (tassa pihayanti)]</span>, devāpi tesaṃ pihayanti, sambuddhānaṃ satīmataṃ <span class="note">[dha. pa. 181]</span>, ‘pihayantī’ti punappunaṃ daṭṭhuṃ patthentīti attho. dutiyāpi hoti, sace maṃ pihayasi, dhanaṃ piheti, hiraññaṃ piheti, suvaṇṇaṃ piheti. tatiyāpi dissati, rūpena piheti, saddena piheti iccādi.</p>\r\n<p class="bodytext">tassa kujjha mahāvīra <span class="note">[jā. 1.4.49]</span>, mā me kujjha rathesabha <span class="note">[jā. 2.22.1696 (kujjhi)]</span>, yadihaṃ tassa kuppeyyaṃ, mātu kuppati, pitu kuppati, yo appaduṭṭhassa narassa dussati <span class="note">[dha. pa. 125; su. ni. 667; jā. 1.5.94]</span>, duhayati disānaṃ megho, pūreti vināseti vāti attho, akāle vassanto hi vināseti nāma, yo mittānaṃ na dubbhati <span class="note">[jā. 2.22.19]</span>, aduṭṭhassa tuvaṃ dubbhi <span class="note">[jā. 1.16.295]</span>, mittānaṃ na dubbheyya, titthiyā issanti samaṇānaṃ, ussūyanti dujjanā guṇavantānaṃ, pativissakānaṃ ujjhāpesi <span class="note">[ma. ni. 1.226 (ujjhāpesi)]</span>, mā tumhe tassa ujjhāyittha <span class="note">[udā. 26 (visadisaṃ)]</span>, mahārājānaṃ ujjhāpetabbaṃ viravitabbaṃ vikkanditabbaṃ, kyāhaṃ ayyānaṃ aparajjhāmi <span class="note">[pārā. 383]</span> ayye vā, rañño aparajjhati rājānaṃ vā, ārādho me rājā hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54594,'annya_sadda_10',187,replace(replace('\r\n<p class="bodytext">pati, āpubbassa su-dhātussa anu, patipubbassa ca gī-dhātussa yoge sampadāne catutthī. tatra kho bhagavā bhikkhū āmantesi "bhikkhavo"ti, "bhaddante"ti te bhikkhū bhagavato paccassosuṃ <span class="note">[a. ni. 1.1]</span>. ettha ca pubbavākye āmantanakriyāya kattā bhagavā, so paravākye paccāsuyoge sampadānaṃ hoti, ‘paccassosu’nti bhaddanteti paṭivacanaṃ adaṃsūti attho. bhikkhū buddhassa āsuṇanti, rājā bimbisāro pilindavacchattherassa ārāmikaṃ paṭissutvā <span class="note">[mahāva. 270]</span>, amacco rañño bimbisārassa paṭissutvā <span class="note">[mahāva. 270]</span>, sampaṭicchitvāti attho, bhikkhu janaṃ dhammaṃ sāveti<a name="M0.0187"></a>, jano tassa bhikkhuno anugiṇāti paṭigiṇāti, sādhukāraṃ detīti attho.</p>\r\n<p class="bodytext">ārocanatthayoge –</p>\r\n<p class="bodytext">ārocayāmi vo bhikkhave <span class="note">[ma. ni. 1.416]</span>, paṭivedayāmi vo bhikkhave <span class="note">[ma. ni. 1.416]</span>, āmantayāmi vo bhikkhave <span class="note">[dī. ni. 2.218 (visadisaṃ)]</span>, dhammaṃ vo desessāmi <span class="note">[ma. ni. 3.105]</span>, bhikkhūnaṃ dhammaṃ deseti, yathā no bhagavā byākareyya, niruttiṃ te pavakkhāmi, ahaṃ te ācikkhissāmi, ahaṃ te kittayissāmi, bhikkhūnaṃ etadavoca.</p>\r\n<p class="bodytext"><a name="para310"></a><span class="paranum">310</span>. <span class="bld">tadatthe</span> <span class="note">[ka. 277; rū. 303; nī. 554]</span>.</p>\r\n<p class="bodytext">tassā tassā kriyāya atthoti tadattho, tadatthe sampadāne catutthī hoti.</p>\r\n<p class="bodytext"><a name="para311"></a><span class="paranum">311</span>. <span class="bld">sassāya catutthiyā</span> <span class="note">[ka. 109; rū. 304; nī. 279-80]</span>.</p>\r\n<p class="bodytext">akārantato catutthībhūtassa sassa āyo hoti vā.</p>\r\n<p class="bodytext">vinayo saṃvaratthāya, saṃvaro avippaṭisāratthāya, avippaṭisāro pāmujjatthāya, pāmujjaṃ pītatthāya, pīti passaddhatthāya, passaddhi sukhatthāya, sukhaṃ samādhatthāya, samādhi yathābhūtañāṇadassanatthāya, yathābhūtañāṇadassanaṃ nibbidatthāya, nibbidā virāgatthāya, virāgo vimuttatthāya, vimutti vimuttiñāṇadassanatthāya, vimuttiñāṇadassanaṃ anupādāparinibbānatthāya <span class="note">[pari. 366]</span>, atthāya hitāya sukhāya devamanussānaṃ <span class="note">[ma. ni. 1.50]</span>, alaṃ kukkuccāya <span class="note">[pārā. 38]</span>, alaṃ sammohāya, pākāya vajati, yuddhāya gacchati, gāmaṃ piṇḍāya pāvisiṃ <span class="note">[pāci. 902]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-310-311-');
INSERT INTO pages VALUES(54595,'annya_sadda_10',188,replace(replace('\r\n<p class="bodytext">tumatthopi <a name="M0.0188"></a> tadatthe saṅgayhati, alaṃ mitte sukhāpetuṃ, amittānaṃ dukhāya ca <span class="note">[jā. 2.17.13]</span>. lokānukampāya buddhā loke uppajjati, alaṃ phāsuvihārāya, abhiññāya sambodhāya nibbānāya saṃvattati <span class="note">[mahāva. 13]</span>.</p>\r\n<p class="bodytext">alamatthayoge –</p>\r\n<p class="bodytext">alaṃ mallo mallassa, arahati mallo mallassa, alaṃ te idha vāsena <span class="note">[pārā. 436]</span>, alaṃ te hiraññasuvaṇṇena, kiṃ me ekena tiṇṇena <span class="note">[bu. vaṃ. 2.56]</span>, kiṃ te jaṭāhi dummedha, kiṃ te ajinasāṭiyā <span class="note">[dha. pa. 394]</span>.</p>\r\n<p class="bodytext">maññanāpayoge anādare apāṇismimeva catutthī, kaṭṭhassa tuvaṃ maññe, kaliṅgarassa <span class="note">[kaḷiṅgarassa, kaḷaṅgarassa (ka.)]</span> tuvaṃ maññe, jīvitaṃ tiṇāyapi na maññati.</p>\r\n<p class="bodytext">anādareti kiṃ? suvaṇṇaṃ taṃ maññe.</p>\r\n<p class="bodytext">apāṇisminti kiṃ? gadrabhaṃ tuvaṃ maññe.</p>\r\n<p class="bodytext">gatyatthānaṃ nayanatthānañca dhātūnaṃ kammani catutthī, appo saggāya gacchati <span class="note">[dha. pa. 174]</span>, yo maṃ dakāya neti <span class="note">[jā. 1.6.97]</span>, nirayāyupakaḍḍhati <span class="note">[dha. pa. 311]</span>, mūlāya paṭikasseyya <span class="note">[cūḷava. 111]</span>.</p>\r\n<p class="bodytext">āsīsanakriyāyoge –</p>\r\n<p class="bodytext">āyu bhavato hotu, bhaddaṃ te hotu, bhaddamatthu te <span class="note">[jā. 1.8.15; jā. 2.17.1]</span>, kusalaṃ te hotu, anāmayaṃ te hotu, sukhaṃ te hotu, atthaṃ te hotu, hitaṃ te hotu, kalyāṇaṃ te hotu, svāgataṃ te hotu, sotthi te hotu sotthi gabbhassa <span class="note">[ma. ni. 2.351]</span>, maṅgalaṃ te hotu.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54596,'annya_sadda_10',189,replace(replace('\r\n<p class="bodytext">sammutiyoge <a name="M0.0189"></a> kammatthe [chaṭṭhī], itthannāmassa bhikkhuno rūpiyachaḍḍakassa sammuti <span class="note">[pārā. 590]</span>, pattagāhāpakassa sammutiiccādi <span class="note">[pārā. 614]</span>.</p>\r\n<p class="bodytext">āvikaraṇādiyoge –</p>\r\n<p class="bodytext">tuyhañcassa āvi karomi, tassa me sakko pāturahosi, tassa pahiṇeyya, bhikkhūnaṃ dūtaṃ pāhesi, kappati bhikkhūnaṃ āyogo, vaṭṭati bhikkhūnaṃ āyogo, patthodano dvinnaṃ tiṇṇaṃ nappahoti, ekassa pahoti, ekassa pariyatto, upamaṃ te karissāmi <span class="note">[ma. ni. 1.258; jā. 2.19.24]</span>, añjaliṃ te paggaṇhāmi <span class="note">[jā. 2.22.327]</span>, tathāgatassa phāsu hoti, āvikatā hissa phāsu <span class="note">[mahāva. 134]</span>, lokassa attho, lokassa hitaṃ, maṇinā me attho <span class="note">[pārā. 344]</span>, na mamattho buddhena <span class="note">[pārā. 52]</span>, namatthu buddhānaṃ namatthu bodhiyā <span class="note">[jā. 1.2.17]</span>, vipassissa ca namatthu <span class="note">[dī. ni. 3.277]</span>, namo karohi nāgassa <span class="note">[ma. ni. 1.249]</span>, namo te purisājañña, namo te purisuttama <span class="note">[apa. thera 1.2.129]</span>. sotthi pajānaṃ <span class="note">[dī. ni. 1.274]</span>, suvatthi pajānaṃ iccādi.</p>\r\n<p class="centered">catutthīvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">pañcamīvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe pañcamī?</p>\r\n<p class="bodytext"><a name="para312"></a><span class="paranum">312</span>. <span class="bld">pañcamyāvadhismiṃ</span> <span class="note">[ka. 295; rū. 307; nī. 607; caṃ. 2.1.81; pā. 2.3.28; 1.4.24]</span>.</p>\r\n<p class="bodytext">avadhiyati vavatthiyati padattho etasmāti avadhi, tasmiṃ pañcamī hoti, avadhīti ca apādānaṃ vuccati.</p>\r\n<p class="bodytext">apanetvā ito aññaṃ ādadāti gaṇhātīti <span class="bld">apādānaṃ</span>. taṃ tividhaṃ niddiṭṭhavisayaṃ, uppāṭavisayaṃ, anumeyyavisayanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-312-');
INSERT INTO pages VALUES(54597,'annya_sadda_10',190,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0190"></a> yasmiṃ apādānavisayabhūto kriyāviseso sarūpato niddiṭṭho hoti, taṃ <span class="bld">niddiṭṭhavisayaṃ</span>. yathā? gāmā apenti munayo, nagarā niggato rājā.</p>\r\n<p class="bodytext">yasmiṃ pana so pāṭhasesaṃ katvā ajjhāharitabbo hoti, taṃ <span class="bld">uppāṭavisayaṃ</span>. yathā? valāhakā vijjotate vijju, agārasmā anagāriyaṃ pabbajitoti <span class="note">[mahāva. 30]</span>. ettha hi ‘nikkhamitvā’ti padaṃ ajjhāharitabbaṃ.</p>\r\n<p class="bodytext">yasmiṃ pana so niddiṭṭho ca na hoti, ajjhāharituñca na sakkā, atha kho atthato anumānavasena so viññeyyo hoti, taṃ <span class="bld">anumeyyavisayaṃ</span>. yathā? māthurā pāṭaliputtakehi abhirūpatarā, sīlameva sutā seyyo <span class="note">[jā. 1.5.65]</span>, mayā bhiyyo na vijjati, aggohamasmi lokassa, jeṭṭhohamasmi lokassa, seṭṭhohamasmi lokassa iccādi <span class="note">[ma. ni. 3.207]</span>. kriyaṃ vinā kārakaṃ nāma na sijjhatītikatvā ukkaṃsanakriyā ettha anumetabbā hoti. evaṃ kriyāpadarahitesu dūrayogādīsupi avinābhāvikriyānumānaṃ veditabbaṃ.</p>\r\n<p class="bodytext">puna calā’calavasena duvidhaṃ.</p>\r\n<p class="bodytext"><span class="bld">calaṃ</span> yathā? puriso dhāvatā assā patati, dve meṇḍā yujjhitvā aññamaññato apasakkanti. ettha ca yadi calaṃ siyā, kathaṃ avadhi nāma bhaveyya. accutilakkhaṇo hi avadhīti? vuccate-dve meṇḍā sakasakakriyāya calanti, itarītarakriyāya avadhī hontīti natthi ettha avadhilakkhaṇavirodhoti.</p>\r\n<p class="bodytext"><span class="bld">acalaṃ</span> yathā? gāmā apenti munayo, nagarā niggato rājā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54598,'annya_sadda_10',191,replace(replace('\r\n<p class="bodytext">puna <span class="bld">kāyasaṃsaggapubbakaṃ, cittasaṃsaggapubbaka</span>nti duvidhaṃ hoti, gāmā apenti munayo, corā bhayaṃ jāyate. ettha ca "kiṃva <a name="M0.0191"></a> dūro ito gāmo, ito sā dakkhiṇā disā <span class="note">[dī. ni. 3.279]</span>. ito ekanavutikappe"ti <span class="note">[dī. ni. 2.4]</span> ādīsu vadantassa cittasaṃsaggapubbakampi veditabbaṃ. "na mātā puttato bhāyati, na ca putto mātito bhāyati, bhayā bhīto na bhāsasī"ti <span class="note">[jā. 2.21.138]</span> pāḷi. atthi te ito bhayaṃ <span class="note">[ma. ni. 2.350]</span>, natthi te ito bhayaṃ, yato khemaṃ tato bhayaṃ <span class="note">[jā. 1.9.58]</span>, corā bhāyati, corā bhīto. chaṭṭhī ca, corassa bhāyati, corassa bhīto. dutiyā ca, "kathaṃ paralokaṃ na bhāyeyya, evaṃ paralokaṃ na bhāyeyya, bhāyasi maṃ samaṇa <span class="note">[su. ni. sūcilomasutta]</span>, nāhaṃ taṃ bhāyāmi <span class="note">[su. ni. sūcilomasutta]</span>, bhāyitabbaṃ na bhāyati, nāhaṃ bhāyāmi bhoginaṃ <span class="note">[jā. 2.22.835]</span>, na maṃ migā uttasantī"ti <span class="note">[jā. 2.22.307]</span> pāḷipadāni dissanti. tattha "bhogina’nti nāgaṃ, corā tasati uttasati corassa vā, sabbe tasanti daṇḍassa <span class="note">[dha. pa. 129]</span>, pāpato ottappati jigucchati harāyati pāpena vā.</p>\r\n<p class="bodytext">yato kiñci sippaṃ vā vijjaṃ vā dhammaṃ vā gaṇhāti, tasmiṃ akkhātari pañcamī, upajjhāyā adhīte, upajjhāyā sippaṃ gaṇhāti, dvāsīti buddhato gaṇhiṃ, dve sahassāni bhikkhuto. caturāsītisahassāni, yeme dhammā pavattino <span class="note">[theragā. 1027]</span>.</p>\r\n<p class="bodytext">yato suṇāti, tasmiṃ pañcamī, chaṭṭhī ca, ito sutvā, imassa sutvā vā, yamhā dhammaṃ vijāneyya, sammāsambuddhadesitaṃ <span class="note">[dha. pa. 392]</span>.</p>\r\n<p class="bodytext">yato labhati, tasmiṃ pañcamī, saṅghato labhati, gaṇato labhati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54599,'annya_sadda_10',192,replace(replace('\r\n<p class="bodytext">yato parājayati, yato pabhavati, yato jāyati, tasmiṃ pañcamī, buddhasmā parājayanti aññatitthiyā, pāḷiyaṃ pana parājiyoge apādānaṃ pāṭhasesavasena labbhati, tasmiṃ kho <a name="M0.0192"></a> pana saṅgāme devā jiniṃsu, asurā parājiniṃsu. ettha devehi parājiniṃsūti pāṭhaseso. "mayaṃ jitāmhā ambakāya. himavantā pabhavanti pañca mahānadiyo <span class="note">[a. ni. aṭṭha. 3.8.19]</span>, ayaṃ bhāgīrathī gaṅgā, himavantā pabhāvitā"ti pāḷi <span class="note">[apa. thera 1.1.255]</span>, corā bhayaṃ jāyate, kāmato jāyate bhayaṃ <span class="note">[dha. pa. 215]</span>, jātaṃ saraṇato bhayaṃ <span class="note">[jā. 1.1.36; 1.2.13; 1.9.56, 57, 59]</span>, yaṃkiñci bhayaṃ vā veraṃ vā upaddavo vā upasaggo vā jāyati, sabbaṃ taṃ bālato jāyati, no paṇḍitato, kāmato jāyatī soko <span class="note">[dha. pa. 214]</span>, ubhato sujāto putto <span class="note">[dī. ni. 1.311]</span>, urasmā jāto, ure jāto vā, cīvaraṃ uppajjeyya saṅghato vā gaṇato vā ñātimittato vā <span class="note">[pārā. 500 (thokaṃ visadisaṃ)]</span>.</p>\r\n<p class="bodytext">aññatthānaṃ yoge pañcamī, tato aññaṃ, tato paraṃ <span class="note">[mahāva. 346]</span>, tato aparena samayena <span class="note">[pārā. 195]</span>.</p>\r\n<p class="bodytext">upasaggānaṃ yoge pana –</p>\r\n<p class="bodytext"><a name="para313"></a><span class="paranum">313</span>. <span class="bld">apaparīhi vajjane</span> <span class="note">[ka. 272; rū. 309; nī. 558, 568; caṃ. 2.1.82; pā. 1.4.88; 2.3.10]</span>.</p>\r\n<p class="bodytext">vajjane pavattehi apa, parīhi yoge pañcamī hoti.</p>\r\n<p class="bodytext">apapabbatā vassati devo, paripabbatā vassati devo, apasālāya āyanti vāṇijā, parisālāya āyanti vāṇijā, pabbataṃ sālaṃ vajjetvāti attho. kaccāyane pana "uparipabbatā devo vassatī"ti pāṭho <span class="note">[porāṇapāṭho]</span>, paripabbatāti yutto. upariyoge pana sattamīyeva dissati – "tasmiṃ uparipabbate <span class="note">[ma. ni. 3.216; jā. 1.8.16]</span>, uparipāsāde <span class="note">[dī. ni. 2.408]</span>, uparivehāse, uparivehāsakuṭiyā"ti, <span class="note">[pāci. 130]</span> tattha pabbatassa upari uparipabbatanti attho.</p>\r\n<p class="bodytext"><a name="para314"></a><span class="paranum">314</span>. <span class="bld">paṭinidhipaṭidānesu patinā</span> <span class="note">[ka. 272; rū. 309; nī. 558, 568; caṃ. 2.1.83; pā. 2.3.11; 1.4.9.2]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-313-314-');
INSERT INTO pages VALUES(54600,'annya_sadda_10',193,replace(replace('\r\n<p class="bodytext">paṭinidhi <a name="M0.0193"></a> nāma paṭibimbaṭṭhapanaṃ, paṭidānaṃ nāma paṭibhaṇḍadānaṃ tesu pavattena patinā yoge pañcamī hoti.</p>\r\n<p class="bodytext">buddhasmā pati sāriputto dhammaṃ deseti, telasmā pati ghataṃ deti.</p>\r\n<p class="bodytext"><a name="para315"></a><span class="paranum">315</span>. <span class="bld">rite dutiyā ca</span> <span class="note">[ka. 272; rū. 309; nī. 558, 568; caṃ. 2.1.84; pā. 2.3.29]</span>.</p>\r\n<p class="bodytext">ritesaddena yoge pañcamī hoti dutiyā ca.</p>\r\n<p class="bodytext">rite saddhammā, rite saddhammaṃ.</p>\r\n<p class="bodytext"><a name="para316"></a><span class="paranum">316</span>. <span class="bld">vināññatrehi tatiyā ca</span> <span class="note">[ka. 272; rū. 309; nī. 558, 568; caṃ. 2.1.85; pā. 2.3.32; ‘vināññatra tatiyāca’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vajjane pavattehi vinā, aññatrasaddehi yoge pañcamī, dutiyā, tatiyā ca honti.</p>\r\n<p class="bodytext">vinā saddhammā, vinā saddhammaṃ, vinā saddhammena, aññatra saddhammā, aññatra saddhammaṃ, aññatra saddhammena.</p>\r\n<p class="bodytext"><a name="para317"></a><span class="paranum">317</span>. <span class="bld">puthunānāhi ca</span> <span class="note">[ka. 272; rū. 309; nī. 558, 568; caṃ. 2.1.86; pā. 2.3.32; ‘puthanānāhi ca’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vajjane pavattehi puthu, nānāsaddehi ca yoge pañcamī, tatiyā ca honti.</p>\r\n<p class="bodytext">puthageva janasmā, puthageva janena, nānā saddhammā, nānā saddhammena, piyehi manāpehi nānābhāvo vinābhāvo <span class="note">[dī. ni. 2.183; cūḷava. 437]</span>. "te bhikkhū nānākulā pabbajitā"ti ettha pana nānāppakārattho nānāsaddo, na vajjanattho, ettha ca vajjanattho nāma viyogattho asammissattho.</p>\r\n','\r',char(13)),'\n',char(10)),'-315-316-317-');
INSERT INTO pages VALUES(54601,'annya_sadda_10',194,replace(replace('\r\n<p class="bodytext">mariyādā’bhividhīsu <a name="M0.0194"></a> pavattehi āsadda, yāvasaddehi yogepi pañcamī, dutiyā ca.</p>\r\n<p class="bodytext">tattha yassa avadhino sambandhinī kriyā, taṃ bahikatvā pavattati, so <span class="bld">mariyādo</span>. yathā? āpabbatā khettaṃ tiṭṭhati āpabbataṃ vā, yāvapabbatā khettaṃ tiṭṭhati yāvapabbataṃ vā.</p>\r\n<p class="bodytext">yassa sambandhinī kriyā, taṃ antokatvā byāpetvā pavattati, so <span class="bld">abhividhi</span>. yathā? ābhavaggā bhagavato kittisaddo abbhuggato ābhavaggaṃ vā, bhavato ābhavaggaṃ dhammato āgotrabhuṃ savantīti āsavā, yāvabhavaggā yāvabhavaggaṃ vā, tāvadeva yāvabrahmalokā saddo abbhuggato.</p>\r\n<p class="bodytext">ārabbhe, sahatthe ca pañcamī, yatohaṃ bhagini ariyāya jātiyā jāto <span class="note">[ma. ni. 2.351]</span>, yato paṭṭhāyāti attho. yato sarāmi attānaṃ, yato pattosmi viññutaṃ <span class="note">[jā. 2.22.307]</span>. yato paṭṭhāya, yato pabhuti.</p>\r\n<p class="bodytext">sahatthe –</p>\r\n<p class="bodytext">saha sabbaññutaññāṇappaṭilābhā, saha parinibbānā <span class="note">[dī. ni. 2.220]</span>, saha dassanuppādā.</p>\r\n<p class="bodytext">"uppādā vā tathāgatānaṃ anuppādā vā tathāgatāna"nti <span class="note">[saṃ. ni. 2.20]</span>, ettha bhāvalakkhaṇe pañcamī.</p>\r\n<p class="bodytext">"sahatthā dānaṃ deti, sahatthā paṭiggaṇhātī"ti ettha karaṇe.</p>\r\n<p class="bodytext">"ajjatagge pāṇupetaṃ <span class="note">[dī. ni. 1.250]</span>, tadagge kho vāseṭṭha"iccādīsu <span class="note">[dī. ni. 3.130]</span>, ārabbhe sattamī.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54602,'annya_sadda_10',195,replace(replace('\r\n<p class="bodytext">"yatvādhikaraṇaṃ <a name="M0.0195"></a> <span class="note">[dī. ni. 1.213]</span>, yatonidānaṃ <span class="note">[su. ni. 275]</span>, tatonidānaṃ" iccādīsu <span class="note">[ma. ni. 1.238]</span> vākye icchite sati hetvatthe pañcamī, samāse icchite sati atthamatte pañcamī.</p>\r\n<p class="bodytext">dvinnaṃ kārakānaṃ kriyānañca majjhe pavattakāladdhānavācīhi pañcamī, luddako pakkhasmā migaṃ vijjhati, kosā kuñjaraṃ vijjhati. ettha ca luddako sakiṃ migaṃ vijjhitvā pakkhabbhantaramhi na vijjhi, pakkhe paripuṇṇe puna vijjhati, pakkhasaddo dvinnaṃ vijjhanavārānaṃ majjhe kālavācī hoti, dvepi vijjhanakriyā kārakehi saheva sijjhantīti kārakānañca majjheti vuccati. vuttiyaṃ pana "ajja bhutvā devadatto dvihe bhuñjissati, dvihā bhuñjissati, atraṭṭho’yamissāso kose lakkhaṃ vijjhati, kosā lakkhaṃ vijjhatī"ti <span class="note">[moga. 79]</span> evaṃ sattamīvasena paripuṇṇavākyampi vuttaṃ. pāḷiyaṃ "anāpatti chabbassā karoti <span class="note">[pārā. 564]</span>, atirekachabbassā karotī"ti <span class="note">[pārā. 564]</span>, "chabbassānī"tipi pāṭho.</p>\r\n<p class="bodytext">rakkhanatthānaṃ yoge –</p>\r\n<p class="bodytext">yañca vatthuṃ guttaṃ icchiyate, yato ca guttaṃ icchiyate, tattha pañcamī, yavehi gāvo rakkhati vāreti, taṇḍulā kāke rakkhati vāreti, taṃ maṃ puññā nivāresi, pāpā cittaṃ nivāraye <span class="note">[dha. pa. 116]</span>, na naṃ jāti nivāreti, duggatyā garahāya vā <span class="note">[su. ni. 141 (na ne)]</span>. rājato vā corato vā ārakkhaṃ gaṇhantu.</p>\r\n<p class="bodytext">antaradhānatthayoge –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54603,'annya_sadda_10',196,replace(replace('\r\n<p class="bodytext">yassa adassanaṃ icchiyati, tasmiṃ pañcamī, upajjhāyā antaradhāyati sisso, nilīyatīti attho. pāḷiyaṃ pana yassa adassanaṃ icchiyati, tasmiṃ chaṭṭhī eva- "antaradhāyissāmi samaṇassa <a name="M0.0196"></a> gotamassa, antaradhāyissāmi samaṇassa gotamassā"ti. "na sakkhi me antaradhāyitu"nti pāḷi, ‘antaradhāyissāmī’ti antarite acakkhuvisaye ṭhāne attānaṃ ṭhapessāmītyattho, nilīyissāmīti vuttaṃ hoti.</p>\r\n<p class="bodytext">yasmiṃ ṭhāne antaradhāyati, tasmiṃ sattamī eva dissati, atikhippaṃ loke cakkhu antaradhāyissati <span class="note">[dī. ni. 2.224 (visadisaṃ)]</span>, jetavane antaradhāyitvā, brahmaloke antaradhāyitvā, maddakucchismiṃ antaradhāyitvā, tatthevantaradhāyī <span class="note">[saṃ. ni. 1.1]</span> iccādi. "bhagavato purato antaradhāyitvā"ti etthapi tosaddo sattamyatthe eva. "sakko nimissa rañño sammukhe antarahito"ti pāḷi. ‘dhajatavane antaradhāyitvā’ti jetavane aññesaṃ adassanaṃ katvā, aññesaṃ acakkhuvisayaṃ katvāti attho. "andhakāro antaradhāyati, āloko antaradhāyati, saddhammo antaradhāyati, sāsanaṃ antaradhāyati" iccādīsu pana chaṭṭhī, sattamiyo yathāsambhavaṃ veditabbā.</p>\r\n<p class="bodytext">dūratthayoge –</p>\r\n<p class="bodytext">kiṃva dūro ito gāmo, kacci ārā pamādamhā <span class="note">[su. ni. 156]</span>, atho ārā pamādamhā <span class="note">[su. ni. 157]</span>, gāmato avidūre, ārakā te moghapurisā imasmā dhammavinayā, ārakā tehi bhagavā, kilesehi ārakāti arahaṃ, ārā so āsavakkhayā <span class="note">[dha. pa. 253]</span>. dutiyā ca tatiyā ca chaṭṭhī ca, ārakā imaṃ dhammavinayaṃ iminā dhammavinayena vā, ārakā mandabuddhīnaṃ <span class="note">[visuddhi ṭī. 1.130]</span>.</p>\r\n<p class="bodytext">dūratthe –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54604,'annya_sadda_10',197,replace(replace('\r\n<p class="bodytext">dūratova namassanti, addasā kho bhagavantaṃ dūratova āgacchantaṃ <span class="note">[dī. ni. 1.409]</span>, kinnu tiṭṭhatha ārakā, tasmā tiṭṭhāma ārakā. dutiyā ca tatiyā ca, dūraṃ gāmaṃ āgato, dūrena gāmena āgato<a name="M0.0197"></a>, dūrā gāmā āgato iccevattho, dūraṃ gāmena vā.</p>\r\n<p class="bodytext">antikatthayoge –</p>\r\n<p class="bodytext">antikaṃ gāmā, āsannaṃ gāmā, samīpaṃ gāmā. dutiyā ca tatiyā ca chaṭṭhī ca, antikaṃ gāmaṃ, antikaṃ gāmena, antikaṃ gāmassa.</p>\r\n<p class="bodytext">kāladdhānaṃ parimāṇavacane –</p>\r\n<p class="bodytext">ito mathurāya catūsu yojanesu saṅkassaṃ, rājagahato pañcacattālīsayojane sāvatthi, ito ekanavutikappe <span class="note">[dī. ni. 2.4]</span>, ito ekatiṃse kappe <span class="note">[dī. ni. 2.4]</span>, ito sattame divase, ito tiṇṇaṃ māsānaṃ accayena parinibbāyissāmi <span class="note">[dī. ni. 2.168; udā. 51]</span> iccādi.</p>\r\n<p class="bodytext">pamāṇatthe –</p>\r\n<p class="bodytext">āyāmato ca vitthārato ca yojanaṃ, parikkhepato navayojanasataparimāṇo majjhimadeso parikkhepena vā, dīghaso navavidatthiyo <span class="note">[pāci. 548]</span>, yojanaṃ āyāmena yojanaṃ vitthārena yojanaṃ ubbedhena sāsaparāsi <span class="note">[saṃ. ni. 2.129]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54605,'annya_sadda_10',198,replace(replace('\r\n<p class="bodytext">tvālopepi pañcamī. ettha ca tvālopo nāma paripuṇṇavākye laddhabbassa tvāntapadassa aparipuṇṇavākye natthi bhāvo, yañca padaṃ tvāntapade sati kammaṃ vā hoti adhikaraṇaṃ vā. taṃ tvāntapade asati padantare avadhi hoti, tasmiṃ pañcamī, pāsādā vā pāsādaṃ saṅkameyya <span class="note">[saṃ. ni. 1.132]</span>, hatthikkhandhā vā hatthikkhandhaṃ saṅkameyya <span class="note">[saṃ. ni. 1.132]</span> iccādi. ettha ca paṭhamaṃ ekaṃ pāsādaṃ abhirūhitvā puna aññaṃ pāsādaṃ saṅkameyyāti vā paṭhamaṃ ekasmiṃ pāsāde nisīditvā puna aññaṃ pāsādaṃ saṅkameyyāti vā evaṃ paripuṇṇavākyaṃ veditabbaṃ. "andhakārā vā andhakāraṃ gaccheyya, tamā vā <a name="M0.0198"></a> tamaṃ gaccheyyā"ti <span class="note">[saṃ. ni. 1.132]</span> pāḷi. tathā raṭṭhā raṭṭhaṃ vicarati, gāmā gāmaṃ vicarati, vanā vanaṃ vicarati, vihārato vihāraṃ gacchati, pariveṇato pariveṇaṃ gacchati, bhavato bhavaṃ gacchati, kulato kulaṃ gacchati iccādi. tathā vinayā vinayaṃ pucchati, abhidhammā abhidhammaṃ pucchati, vinayā vinayaṃ katheti, abhidhammā abhidhammaṃ katheti. etthapi paṭhamaṃ ekaṃ vinayavacanaṃ pucchitvā vā ekasmiṃ vinayavacane ṭhatvā vā puna aññaṃ vinayavacanaṃ pucchatīti paripuṇṇavākyaṃ veditabbaṃ. vuttiyaṃ pana "pāsādaṃ āruyha pekkhati, pāsādā pekkhati, āsane pavisitvā pekkhati, āsanā pekkhatī"ti vuttaṃ.</p>\r\n<p class="bodytext">disatthayoge disatthe ca pañcamī, ito sā purimā disā <span class="note">[dī. ni. 3.278]</span>, ito sā dakkhiṇā disā <span class="note">[dī. ni. 3.278]</span>, avīcito upari, uddhaṃ pādatalā, adho kesamatthakā <span class="note">[dī. ni. 2.377; ma. ni. 1.110]</span>.</p>\r\n<p class="bodytext">disatthe –</p>\r\n<p class="bodytext">purimato gāmassa, dakkhiṇato gāmassa, uparito pabbatassa, heṭṭhato pāsādassa, puratthimato, dakkhiṇato, yato khemaṃ, tato bhayaṃ <span class="note">[jā. 1.9.58]</span>, yato yato sammasati, khandhānaṃ udayabbayaṃ <span class="note">[dha. pa. 374]</span> iccādi.</p>\r\n<p class="bodytext">pubbādiyogepi pañcamī, pubbeva me sambodhā <span class="note">[a. ni. 3.104]</span>, ito pubbe, tato pubbe, ito parā paccantimā janapadā <span class="note">[mahāva. 259]</span>, tato pure, tato pacchā, tato uttari <span class="note">[pārā. 499]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54606,'annya_sadda_10',199,replace(replace('\r\n<p class="bodytext">vibhattatthe ca pañcamī chaṭṭhī ca. vibhatti nāma pageva visuṃbhūtassa atthassa kenaci adhikena vā hīnena vā bhāgena tadaññato puthakkaraṇaṃ, māthurā pāṭaliputtakehi abhirūpatarā, yato paṇītataro vā visiṭṭhataro vā natthi, attadanto tato varaṃ <span class="note">[dha. pa. 322]</span>, channavutīnaṃ pāsaṇḍānaṃ pavaraṃ yadidaṃ sugatavinayo<a name="M0.0199"></a>, sadevakassa lokassa, satthā loke anuttaro, aggohamasmi lokassa <span class="note">[ma. ni. 3.207]</span>, jeṭṭhohamasmi lokassa <span class="note">[ma. ni. 3.207]</span>, seṭṭhohamasmi lokassa <span class="note">[ma. ni. 3.207]</span>, paññavantā nāma sāriputtato hīnā sāriputtassa vā, tato adhikaṃ vā ūnaṃ vā na vaṭṭati iccādi.</p>\r\n<p class="bodytext">viramaṇatthayoge –</p>\r\n<p class="bodytext">āratī viratī pāpā <span class="note">[khu. pā. 5.8]</span>, pāṇātipātā veramaṇi <span class="note">[khu. pā. 2.1]</span> iccādi.</p>\r\n<p class="bodytext">suddhatthayoge –</p>\r\n<p class="bodytext">lobhanīyehi dhammehi suddhā iccādi.</p>\r\n<p class="bodytext">mocanatthayoge pañcamī tatiyā ca, so parimuccati jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi, parimutto so dukkhasmāti vadāmi <span class="note">[saṃ. ni. 3.29]</span>, mokkhanti mārabandhanā <span class="note">[dha. pa. 37]</span>, na te muccanti maccunā iccādi, sabbattha avadhiattho veditabbo.</p>\r\n<p class="bodytext">hetvatthe –</p>\r\n<p class="bodytext">kasmā hetunā, kena hetunā, kasmā nu tumhaṃ kule daharā na miyyare <span class="note">[jā. 1.10.92 (mīyare)]</span>, tasmātiha bhikkhave <span class="note">[saṃ. ni. 2.157]</span>. dutiyā tatiyā chaṭṭhī ca, kiṃkāraṇaṃ <span class="note">[jā. aṭṭha. 6.22.umaṅgajātakavaṇṇanā]</span>, yatvādhikaraṇaṃ <span class="note">[dī. ni. 1.213]</span>, yatonidānaṃ <span class="note">[su. ni. 275]</span>, tatonidānaṃ <span class="note">[ma. ni. 1.238]</span>, kena kāraṇena <span class="note">[jā. aṭṭha. 4.20.mātaṅgajātakavaṇṇanā]</span>, taṃ kissahetu <span class="note">[ma. ni. 1.2]</span>, kissa tumhe kilamatha iccādi.</p>\r\n<p class="bodytext">vivecanatthayoge –</p>\r\n<p class="bodytext">vivicceva kāmehi vivicca akusalehi dhammehi <span class="note">[dī. ni. 1.226]</span>, vivitto pāpakā dhammā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54607,'annya_sadda_10',200,replace(replace('\r\n<p class="bodytext">bandhanatthayoge <a name="M0.0200"></a> –</p>\r\n<p class="bodytext"><a name="para318"></a><span class="paranum">318</span>. <span class="bld">pañcamīṇe vā</span> <span class="note">[ka. 296; rū. 314; nī. 608; caṃ. 2.1.69; pā. 2.3.24]</span>.</p>\r\n<p class="bodytext">iṇabhūte hetumhi pañcamī hoti vā.</p>\r\n<p class="bodytext">satasmā bandho naro satena vā.</p>\r\n<p class="bodytext"><a name="para319"></a><span class="paranum">319</span>. <span class="bld">guṇe</span> <span class="note">[caṃ. 2.1.70; pā. 2.3.25]</span>.</p>\r\n<p class="bodytext">ajjhattabhūto hetu guṇo nāma, aguṇopi idha guṇotveva vuccati, tasmiṃ pañcamī hoti vā.</p>\r\n<p class="bodytext">jaḷattā bandho naro jaḷattena vā, attano bālattāyeva bandhoti attho, paññāya bandhanā mutto, vācāya marati, vācāya muccati, vācāya piyo hoti, vācāya desso, issariyā janaṃ rakkhati rājā issariyena vā, sīlato naṃ pasaṃsanti <span class="note">[a. ni. 4.6]</span> sīlena vā, hutvā abhāvato aniccā, udayabbayapīḷanato dukkhā, avijjānirodhā saṅkhāranirodho <span class="note">[udā. 2]</span>, saṅkhāranirodhā viññāṇanirodho <span class="note">[udā. 2]</span>, catunnaṃ ariyasaccānaṃ anaññā appaṭivedhā dīghamaddhānaṃ saṃsaranti <span class="note">[dī. ni. 2.186 (visadisaṃ)]</span> iccādi.</p>\r\n<p class="bodytext">pañhā, kathanesupi pañcamī, kuto bhavaṃ, ahaṃ pāṭaliputtato iccādi.</p>\r\n<p class="bodytext">thokatthepi asatvavacane pañcamī, satvaṃ vuccati dabbaṃ, thokā muccati thokena vā, muccanamattaṃ hotīti vuttaṃ hoti "nadiṃ taranto manaṃ vuḷho"ti <span class="note">[mahāva. 148]</span> ettha viya. appamattakā muccati appamattakena vā, kicchā muccati kicchena vā, kicchā laddhā piyo putto <span class="note">[jā. 2.22.353]</span>, kicchā muttā’mha dukkhasmā, yāma dāni mahosadha <span class="note">[jā. 2.22.700]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-318-319-');
INSERT INTO pages VALUES(54608,'annya_sadda_10',201,replace(replace('\r\n<p class="bodytext">asatvavacaneti <a name="M0.0201"></a> kiṃ? paccati munino bhattaṃ, thokaṃ thokaṃ ghare ghareti <span class="note">[theragā. 248 (kule kule)]</span>.</p>\r\n<p class="bodytext">"anupubbena medhāvī, thokaṃ thokaṃ khaṇe khaṇe <span class="note">[dha. pa. 239]</span>" iccādīsu kriyāvisesane dutiyā.</p>\r\n<p class="bodytext">akattaripi pañcamī, tassa kammassa katattā upacitattā ussannattā vipulattā tathāgato suppatiṭṭhitapādo hoti <span class="note">[dī. ni. 3.201]</span>. ettha ca ‘akattarī’ti akārake ñāpakahetumhīti vadanti. ñāse pana "akattarīti hetvatthe saṅgaṇhāti. yattha hi kattubuddhi sañjāyate, sova kattā na hotīti vattuṃ sakkā"ti vuttaṃ. etena kattusadiso janakahetu akattā nāmāti dīpeti, kammassa katattāiccādi ca janakahetu evāti.</p>\r\n<p class="bodytext">bhiyyatthayoge –</p>\r\n<p class="bodytext">yodha sītañca uṇhañca, tiṇā bhiyyo na maññati <span class="note">[dī. ni. 3.253]</span>, sukhā bhiyyo somanassaṃ <span class="note">[dī. ni. 2.287]</span>, khantyā bhiyyo na vijjati <span class="note">[saṃ. ni. 1.250]</span>, mayā bhiyyo na vijjati, sotukāmāttha tumhe bhikkhave bhiyyosomattāya pubbenivāsakathaṃ, attamano tvaṃ hohi paraṃ viya mattāya, ahampi attamano homi paraṃ viya mattāya.</p>\r\n<p class="centered">pañcamīvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">chaṭṭhīvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe chaṭṭhī?</p>\r\n<p class="bodytext"><a name="para320"></a><span class="paranum">320</span>. <span class="bld">chaṭṭhī sambandhe</span> <span class="note">[ka. 301; rū. 315; nī. 609; caṃ. 2.1.95; pā. 2.3.50]</span>.</p>\r\n<p class="bodytext">dvinnaṃ sambandhīnaṃ kenaci pakārena āyattabhāvo sambandho nāma, sambandhe jotetabbe visesanasambandhimhi chaṭṭhī hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-320-');
INSERT INTO pages VALUES(54609,'annya_sadda_10',202,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0202"></a> kriyākārakasañjāto assedambhāvahetuko sambandho nāmāti vuttaṃ. tattha dve sambandhino aññamaññaṃ taṃtaṃkriyaṃ karonti, taṃ disvā "ime aññamaññasambandhino"ti jānantassa dvinnaṃ kārakānaṃ dvinnaṃ kriyānañca saṃyogaṃ nissāya sambandhopi vidito hoti, evaṃ sambandho kriyākārakasañjāto, ‘imassa aya’nti evaṃ pavattabuddhiyā hetubhūtattā assedambhāvahetuko ca.</p>\r\n<p class="bodytext">tattha sambandho tividho sāmisambandho, nānāttasambandho, kriyākārakasambandhoti.</p>\r\n<p class="bodytext">tattha ‘sāmī’ti yassa kassaci visesanasambandhino nāmaṃ, tasmā visesyapadatthassa taṃtaṃvisesanabhāvena sambandho <span class="bld">sāmisambandho</span> nāma.</p>\r\n<p class="bodytext">so visesyapadatthabhedena anekavidho.</p>\r\n<p class="bodytext">tattha tassa mātā, tassa pitāiccādi <span class="bld">janakasambandho</span> nāma.</p>\r\n<p class="bodytext">tassā putto, tassā dhītā iccādi <span class="bld">jaññasambandho</span> nāma.</p>\r\n<p class="bodytext">tassa bhātā, tassa bhaginī iccādi <span class="bld">kulasambandho</span> nāma.</p>\r\n<p class="bodytext">sakko devānamindo <span class="note">[saṃ. ni. 1.248]</span> iccādi <span class="bld">sāmisambandho</span> nāma.</p>\r\n<p class="bodytext">pahūtaṃ me dhanaṃ sakka <span class="note">[jā. 1.15.72]</span>, bhikkhussa pattacīvaraṃ iccādi <span class="bld">saṃsambandho</span> nāma.</p>\r\n<p class="bodytext">ambavanassa avidūre, nibbānasseva santike <span class="note">[dha. pa. 32]</span> iccādi <span class="bld">samīpasambandho</span> nāma.</p>\r\n<p class="bodytext">suvaṇṇassa rāsi, bhikkhūnaṃ samūho iccādi <span class="bld">samūhasambandho</span> nāma.</p>\r\n<p class="bodytext">manussasseva te sīsaṃ <span class="note">[jā. 1.4.81]</span>, rukkhassa sākhā iccādi <span class="bld">avayavasambandho</span> nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54610,'annya_sadda_10',203,replace(replace('\r\n<p class="bodytext">suvaṇṇassa <a name="M0.0203"></a> bhājanaṃ, alābussa kaṭāhaṃ, bhaṭṭhadhaññānaṃ sattu iccādi <span class="bld">vikārasambandho</span> nāma.</p>\r\n<p class="bodytext">yavassa aṅkuro, meghassa saddo, pupphānaṃ gandho, phalānaṃ raso, aggissa dhūmo iccādi <span class="bld">kāriyasambandho</span> nāma.</p>\r\n<p class="bodytext">khandhānaṃ jāti, khandhānaṃ jarā, khandhānaṃ bhedo <span class="note">[saṃ. ni. 2.1]</span> iccādi <span class="bld">avatthāsambandho</span> nāma.</p>\r\n<p class="bodytext">suvaṇṇassa vaṇṇo, vaṇṇo na khīyyetha tathāgatassa <span class="note">[dī. ni. aṭṭha. 1.304]</span>, buddhassa kittisaddo, sippikānaṃ sataṃ natthi <span class="note">[jā. 1.1.113]</span>, tilānaṃ muṭṭhi iccādi <span class="bld">guṇasambandho</span> nāma.</p>\r\n<p class="bodytext">pādassa ukkhipanaṃ, hatthassa samiñjanaṃ, dhātūnaṃ gamanaṃ ṭhānaṃ iccādi <span class="bld">kriyāsambandho</span> nāma.</p>\r\n<p class="bodytext">cātumahārājikānaṃ ṭhānaṃ iccādi <span class="bld">ṭhānasambandho</span> nāma. evamādinā nayena sāmisambandho anekasahassappabhedo, so ca kriyāsambandhābhāvā kārako nāma na hoti. yadi evaṃ "pādassa ukkhipanaṃ" iccādi kriyāsambandho nāmāti idaṃ na yujjatīti? vuccate – kriyāsambandhābhāvāti idaṃ sādhakabhāvena sambandhābhāvaṃ sandhāya vuttaṃ, siddhāya pana kriyāya sambandhaṃ sandhāya kriyāsambandho nāma vuttoti.</p>\r\n<p class="bodytext"><span class="bld">nānāttasambandhe</span> pana nānāatthesu chaṭṭhī hoti. tattha ṇī, āvīpaccayānaṃ kamme niccaṃ chaṭṭhī, jhānassa lābhī, cīvarassa lābhī, dhanassa lābhī, ādīnavassa dassāvī, atthi rūpānaṃ dassāvī, atthi samavisamassa dassāvī, atthi tārakarūpānaṃ dassāvī, atthi candimasūriyānaṃ dassāvī.</p>\r\n<p class="bodytext">tu, aka, ana, ṇapaccayānaṃ yoge kvaci kammatthe chaṭṭhī.</p>\r\n<p class="bodytext">tupaccaye tāva –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54611,'annya_sadda_10',204,replace(replace('\r\n<p class="bodytext">tassa <a name="M0.0204"></a> bhavanti vattāro <span class="note">[ma. ni. 2.173]</span>, sahasā kammassa kattāro, amatassa dātā <span class="note">[ma. ni. 1.203]</span>, bhinnānaṃ sandhātā <span class="note">[dī. ni. 1.9, 164]</span>, sahitānaṃ vā anuppadātā <span class="note">[dī. ni. 1.9, 164]</span> iccādi.</p>\r\n<p class="bodytext">kvacīti kiṃ? gambhīrañca kathaṃ kattā <span class="note">[a. ni. 7.37]</span>, gādhaṃ kattā novasitā <span class="note">[a. ni. 4.107]</span>, kālena dhammīkathaṃ bhāsitā, sarasi tvaṃ evarūpaṃ vācaṃ bhāsitā, paresaṃ puññaṃ anumodetā, bujjhitā saccāni <span class="note">[mahāni. 192]</span> iccādi.</p>\r\n<p class="bodytext">akapaccaye –</p>\r\n<p class="bodytext">kammassa kārako natthi, vipākassa ca vedako <span class="note">[visuddhi 2.689]</span>, avisaṃvādako lokassa <span class="note">[dī. ni. 1.9]</span> iccādi.</p>\r\n<p class="bodytext">kvacīti kiṃ? mahatiṃ mahiṃ anusāsako, janaṃ aheṭhako, kaṭaṃ kārako, pasavo ghātako iccādi.</p>\r\n<p class="bodytext">anapaccaye –</p>\r\n<p class="bodytext">pāpassa akaraṇaṃ sukhaṃ <span class="note">[dha. pa. 61]</span>, bhārassa ukkhipanaṃ, hatthassa gahaṇaṃ, hatthassa parāmasanaṃ, aññatarassa aṅgassa parāmasanaṃ <span class="note">[pārā. 270]</span> iccādi.</p>\r\n<p class="bodytext">kvacīti kiṃ? bhagavantaṃ dassanāya <span class="note">[udā. 23]</span> iccādi.</p>\r\n<p class="bodytext">ṇapaccaye –</p>\r\n<p class="bodytext">acchariyo arajakena vatthānaṃ rāgo, agopālakena gāvīnaṃ doho, appapuññena lābhānaṃ lābho, hatthassa gāho, pattassa paṭiggāho iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54612,'annya_sadda_10',205,replace(replace('\r\n<p class="bodytext">tvāpaccayepi <a name="M0.0205"></a> kvaci kammani chaṭṭhī, alajjīnaṃ nissāya, āyasmato nissāya vacchāmi, catunnaṃ mahābhūtānaṃ upādāya pasādo <span class="note">[dha. sa. 596-599 (upādāya)]</span> iccādi.</p>\r\n<p class="bodytext">kattari ta, tavantu, tāvī, māna’ntānaṃ yoge pana kammani dutiyā eva, sukhakāmo vihāraṃ kato, gāmaṃ gato, odanaṃ bhuttavā bhuttāvī, kammaṃ kurumāno, kammaṃ karonto iccādi.</p>\r\n<p class="bodytext">kvaci chaṭṭhīpi dissati, dhammassa gutto medhāvī <span class="note">[dha. pa. 257]</span> iccādi.</p>\r\n<p class="bodytext">sara, isu, cinta, isa, dayadhātūnaṃ kammani chaṭṭhī vā, mātussa sarati, mātaraṃ sarati, pitussa sarati, pitaraṃ sarati, na rajjassa sarissasi <span class="note">[jā. 2.22.1721]</span>, na tesaṃ koci sarati, sattānaṃ kammapaccayā <span class="note">[khu. pā. 7.2]</span>, āpattipariyantaṃ nassarati, rattipariyantaṃ nassarati <span class="note">[cūḷava. 157]</span>, puttassa icchati puttaṃ vā, mātussa cinteti mātaraṃ vā, therassa ajjhesati theraṃ vā, telassa dayati telaṃ vā, rakkhatīti attho.</p>\r\n<p class="bodytext">karadhātussa abhisaṅkharaṇatthavācino kamme chaṭṭhī, udakassa paṭikurute udakaṃ vā, kaṇḍassa paṭikurute kaṇḍaṃ vā iccādi.</p>\r\n<p class="bodytext">tapaccaye pūjanatthādidhātūnaṃ kattari chaṭṭhī vā, rañño sammato raññā vā, gāmassa pūjito gāmena vā, rañño sakkato raññā vā, rañño apacito raññā vā, rañño mānito <span class="note">[dī. ni. 1.303]</span> raññā vā, tathā suppaṭividdhā buddhānaṃ dhammadhātu, amataṃ tesaṃ paribhuttaṃ, yesaṃ kāyagatāsati paribhuttā <span class="note">[a. ni. 1.603]</span>, amataṃ tesaṃ viraddhaṃ, yesaṃ kāyagatāsati viraddhā <span class="note">[a. ni. 1.603]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54613,'annya_sadda_10',206,replace(replace('\r\n<p class="bodytext">tipaccayepi <a name="M0.0206"></a> kvaci kattari chaṭṭhī vā, sobhaṇā kaccāyanassa pakati kaccāyanena vā, sobhaṇā buddhaghosassa pakati buddhaghosena vā iccādi.</p>\r\n<p class="bodytext">pūjanatthānaṃ pūraṇatthānañca karaṇe chaṭṭhī, pupphassa buddhaṃ pūjeti pupphena vā, ghatassa aggiṃ juhoti ghatena vā, pattaṃ udakassa pūretvā, pūraṃ nānāppakārassa asucino <span class="note">[dī. ni. 2.377]</span>, bālo pūrati pāpassa <span class="note">[dha. pa. 121]</span>, dhīro pūrati puññassa <span class="note">[dha. pa. 121]</span> pūrati dhaññānaṃ vā muggānaṃ vā māsānaṃ vā iccādi. tatiyā vā, khemā nāma pokkharaṇī, puṇṇā haṃsehi tiṭṭhati.</p>\r\n<p class="bodytext">tabba, rujādiyoge pana sampadāne catutthī eva, yakkhasenāpatīnaṃ ujjhāpetabbaṃ vikkanditabbaṃ viravitabbaṃ <span class="note">[dī. ni. 3.283 (visadisaṃ)]</span>, devadattassa rujjati, rajakassa vatthaṃ dadāti iccādi.</p>\r\n<p class="bodytext">bhayatthādīnaṃ apādāne bahulaṃ chaṭṭhī, kiṃ nu kho ahaṃ tassa sukhassa bhāyāmi, sabbe tasanti daṇḍassa, sabbe bhāyanti maccuno <span class="note">[dha. pa. 129]</span>, bhīto catunnaṃ āsīvisānaṃ <span class="note">[saṃ. ni. 4.238]</span>, mā bhikkhave puññānaṃ bhāyittha <span class="note">[udā. 22]</span>, saṅkhātuṃ nopi sakkomi, musāvādassa ottapaṃ <span class="note">[saṃ. ni. 1.184]</span> iccādi. tattha ‘ottapa’nti ottappanto. tathā aggohamasmi lokassa, jeṭṭhohamasmi lokassa <span class="note">[ma. ni. 3.207]</span> iccādi ca.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54614,'annya_sadda_10',207,replace(replace('\r\n<p class="bodytext">kusala, kovida, pasādatthānaṃ ādhāre chaṭṭhī, kusalā naccagītassa <span class="note">[jā. 2.22.94]</span>, kusalo tvaṃ rathassa aṅgapaccaṅgānaṃ <span class="note">[ma. ni. 2.87]</span>, amacce tāta jānāhi, dhīre atthassa kovide <span class="note">[jā. 1.17.13]</span>, narā dhammassa kovidā <span class="note">[jā. 1.1.37]</span>, maggāmaggassa kovidā, "keci iddhīsu kovidā"tipi atthi, santi yakkhā buddhassa pasannā <span class="note">[dī. ni. 3.276 (visadisaṃ)]</span>, dhammassa pasannā, saṅghassa pasannā, buddhe pasannā, dhamme pasannā, saṅghe pasannā vā. tathā <a name="M0.0207"></a> cetopariyañāṇassa, vasī homi mahāmuni. jhānassa vasimhi iccādi.</p>\r\n<p class="bodytext"><a name="para321"></a><span class="paranum">321</span>. <span class="bld">chaṭṭhī hetvatthehi</span> <span class="note">[rū. 163 piṭṭhe; nī. 650; caṃ. 2.1.71; pā. 2.3.26]</span>.</p>\r\n<p class="bodytext">hetvatthehi yoge hetumhi chaṭṭhī hoti.</p>\r\n<p class="bodytext">taṃ kissa hetu <span class="note">[ma. ni. 1.2; caṃ. 2.1.96; pā. 2.3.72]</span>, aṅgavarassa hetu, udarassa hetu, udarassa kāraṇā <span class="note">[pārā. 228]</span> iccādi.</p>\r\n<p class="bodytext"><a name="para322"></a><span class="paranum">322</span>. <span class="bld">tulyatthena vā tatiyā</span> <span class="note">[nī. 638]</span>.</p>\r\n<p class="bodytext">tulyatthena yoge chaṭṭhī hoti tatiyā vā.</p>\r\n<p class="bodytext">tulyo pitu pitarā vā, sadiso pitu pitarā vā. iti nānāttasambandho.</p>\r\n<p class="bodytext"><span class="bld">kriyākārakasambandho</span> nāma kārakānaṃ kriyāya saha sādhaka, sādhyabhāvena aññamaññāpekkhatā avinābhāvitā vuccati, na hi kriyaṃ vinā kārakaṃ nāma sijjhati, na ca kārakaṃ vinā kriyā nāma sijjhatīti, sā pana chaṭṭhīvisayo na hotīti.</p>\r\n<p class="centered">chaṭṭhīvibhattirāsi niṭṭhito.</p>\r\n<p class="subhead">sattamīvibhattirāsi</p>\r\n<p class="bodytext">kasmiṃ atthe sattamī?</p>\r\n<p class="bodytext"><a name="para323"></a><span class="paranum">323</span>. <span class="bld">sattamyādhāre</span> <span class="note">[ka. 302; rū. 313; nī. 630; caṃ. 2.1.88; pā. 1.3.45]</span>.</p>\r\n<p class="bodytext">ādhāro, okāso, adhikaraṇanti atthato ekaṃ, ādhāratthe sattamī hoti. kattukammaṭṭhaṃ kriyaṃ bhuso dhāretīti <span class="bld">ādhāro</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-321-322-323-');
INSERT INTO pages VALUES(54615,'annya_sadda_10',208,replace(replace('\r\n<p class="bodytext">kaṭe <a name="M0.0208"></a> nisīdati puriso, thāliyaṃ odanaṃ pacati. tattha kaṭo kattubhūte purise ṭhitaṃ nisīdanakriyaṃ dhāreti, thālī kammabhūte taṇḍule ṭhitaṃ pacanakriyaṃ dhāreti.</p>\r\n<p class="bodytext">so catubbidho byāpikādhāro, opasilesikādhāro, sāmīpikādhāro, vesayikādhāroti.</p>\r\n<p class="bodytext">tattha yasmiṃ ādheyyavatthu sakale vā ekadese vā byāpetvā tiṭṭhati, so <span class="bld">byāpiko</span>. yathā? tilesu telaṃ tiṭṭhati, ucchūsu raso tiṭṭhati, jalesu khīraṃ tiṭṭhati, dadhimhi sappi tiṭṭhatīti.</p>\r\n<p class="bodytext">yasmiṃ ādheyyavatthu allīyitvā vā tiṭṭhati, adhiṭṭhitamattaṃ hutvā vā tiṭṭhati, so <span class="bld">opasilesiko</span>. yathā? ukkhaliyaṃ ācāmo tiṭṭhati, ghaṭesu udakaṃ tiṭṭhati, āsane nisīdati bhikkhu, pariyaṅke rājā seti.</p>\r\n<p class="bodytext">yo pana attho ādheyyassa avatthubhūtopi tadāyattavuttidīpanatthaṃ ādhārabhāvena vohariyati, so <span class="bld">sāmīpiko</span> nāma. yathā? gaṅgāyaṃ ghoso tiṭṭhati, sāvatthiyaṃ viharati bhagavāti <span class="note">[a. ni. 1.1]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54616,'annya_sadda_10',209,replace(replace('\r\n<p class="bodytext">yo ca attho attanā vinā ādheyyassa aññatthattaṃ kriyaṃ sampādetuṃ asakkuṇeyyattā ādhārabhāvena vohariyati, yo ca ādheyyassa anaññābhimukhabhāvadīpanatthaṃ ādhārabhāvena vohariyati, so <span class="bld">vesayiko</span> nāma. yathā? ākāse sakuṇā pakkhanti, bhūmīsu manussā caranti<a name="M0.0209"></a>, udake macchā caranti, bhagavantaṃ pādesu vandati, pādesu patitvā rodati, pāpasmiṃ ramatī mano <span class="note">[dha. pa. 116]</span>, pasanno buddhasāsaneti <span class="note">[dha. pa. 368]</span>.</p>\r\n<p class="bodytext"><a name="para324"></a><span class="paranum">324</span>. <span class="bld">nimitte</span> <span class="note">[ka. 310; rū. 324; nī. 641; caṃ. 2.1.89; pā. 2.3.36]</span>.</p>\r\n<p class="bodytext">niminanti sañjānanti etenāti <span class="bld">nimittaṃ,</span> nemittakasahabhāvino saññāṇakāraṇassetaṃ nāmaṃ, tasmiṃ nimitte sattamī hoti.</p>\r\n<p class="bodytext">dīpi cammesu haññate, kuñjaro dantesu haññate, musāvāde pācittiyaṃ <span class="note">[pāci. 2]</span>, omasavāde pācittiyaṃ <span class="note">[pāci. 14]</span> iccādi.</p>\r\n<p class="bodytext"><a name="para325"></a><span class="paranum">325</span>. <span class="bld">yambhāvo bhāvalakkhaṇaṃ</span> <span class="note">[ka. 313; rū. 327; nī. 644; caṃ. 2.1.90; pā. 2.3.37; ‘yabbhā vo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">yādiso bhāvo yambhāvo, lakkhiyati etenāti lakkhaṇaṃ, bhāvantarassa lakkhaṇaṃ <span class="bld">bhāvalakkhaṇaṃ,</span> yambhāvo bhāvantarassa lakkhaṇaṃ hoti, tasmiṃ bhāve gamyamāne sattamī hoti, chaṭṭhīpi dissati.</p>\r\n<p class="bodytext">acirapakkantassa sāriputtassa brāhmaṇo kālamakāsi <span class="note">[ma. ni. 2.452 (visadisaṃ)]</span>, appamattassa te viharato itthāgāropi te appamatto viharissati <span class="note">[saṃ. ni. 1.129 (visadisaṃ)]</span> iccādi.</p>\r\n<p class="bodytext">imasmiṃ sati idaṃ hoti, imasmiṃ asati idaṃ na hoti <span class="note">[saṃ. ni. 2.21]</span>, acirapakkante bhagavati brāhmaṇo kālamakāsi, sabbe maggā vivajjanti, gacchante lokanāyake <span class="note">[ma. ni. aṭṭha. 2.22]</span>. gāvīsu duyhamānāsu gato, gāvīsu duddhāsu āgato iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-324-325-');
INSERT INTO pages VALUES(54617,'annya_sadda_10',210,replace(replace('\r\n<p class="bodytext">kvaci <a name="M0.0210"></a> paṭhamāpi bahulaṃ dissati, gacchanto so bhāradvājo, addasā accutaṃ isiṃ <span class="note">[jā. 2.22.2007 (addassa)]</span>. yāyamāno mahārājā, addā sīdantare nage <span class="note">[jā. 2.22.566]</span> iccādi.</p>\r\n<p class="bodytext">pubbaṇhasamaye gato, sāyanhasamaye āgato iccādi vesayikādhāro eva.</p>\r\n<p class="bodytext">tathā akāle vassatī tassa, kāle tassa na vassati <span class="note">[jā. 1.2.88; 1.8.48]</span>. ito satasahassamhi, kappe uppajji nāyako <span class="note">[apa. thera 2.54.28]</span> iccādi.</p>\r\n<p class="bodytext"><a name="para326"></a><span class="paranum">326</span>. <span class="bld">chaṭṭhī cānādare</span> <span class="note">[ka. 305; rū. 323; nī. 633; caṃ. 2.1.91; pā. 2.3.38]</span>.</p>\r\n<p class="bodytext">‘anādaro’ti dvinnaṃ lakkhaṇa, lakkhitabbakriyānaṃ ekappahārena pavattiyā adhivacanaṃ, anādarabhūte bhāvalakkhaṇe gamyamāne sattamī chaṭṭhī ca hoti.</p>\r\n<p class="bodytext">maccu gacchati ādāya, pekkhamāne mahājane. ākoṭayanto so neti, sivirājassa pekkhato <span class="note">[jā. 2.22.2122 (teneti)]</span>. akāmakānaṃ mātāpitūnaṃ rudantānaṃ pabbaji, anagāriyupetassa, vippamuttassa te sato. samaṇassa na taṃ sādhu, yadaññamanusocati <span class="note">[jā. 1.7.107 (yaṃ petamanusocasi)]</span>.</p>\r\n<p class="bodytext"><a name="para327"></a><span class="paranum">327</span>. <span class="bld">yato niddhāraṇaṃ</span> <span class="note">[ka. 304; rū. 322; nī. 632; caṃ. 2.1.92; pā. 2.3.41]</span>.</p>\r\n<p class="bodytext">jāti, guṇa, kriyā, nāmehi samudāyato ekadesassa puthakkaraṇaṃ <span class="bld">niddhāraṇaṃ,</span> yato taṃ niddhāraṇaṃ jāyati, tasmiṃ samudāye chaṭṭhī, sattamiyo honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-326-327-');
INSERT INTO pages VALUES(54618,'annya_sadda_10',211,replace(replace('\r\n<p class="bodytext">jātiyaṃ <a name="M0.0211"></a> tāva –</p>\r\n<p class="bodytext">manussānaṃ khattiyo sūratamo, manussesu khattiyo sūratamo.</p>\r\n<p class="bodytext">guṇe –</p>\r\n<p class="bodytext">kaṇhā gāvīnaṃ sampannakhīratamā, kaṇhāgāvīsu sampannakhīratamā.</p>\r\n<p class="bodytext">kriyāyaṃ –</p>\r\n<p class="bodytext">addhikānaṃ dhāvanto sīghatamo, addhikesu dhāvanto sīghatamo.</p>\r\n<p class="bodytext">nāme –</p>\r\n<p class="bodytext">āyasmā ānando arahataṃ aññataro, arahantesu aññataro iccādi.</p>\r\n<p class="bodytext">idha nānāttasattamī vuccate.</p>\r\n<p class="bodytext">kammatthe sattamī, bhikkhūsu abhivādenti <span class="note">[pārā. 517]</span>, puttaṃ muddhani cumbitvā, purisaṃ nānābāhāsu gahetvā <span class="note">[saṃ. ni. 2.63]</span> iccādi.</p>\r\n<p class="bodytext">atha vā ‘muddhani, bāhāsū’ti ādhāre eva bhummaṃ. yathā? rukkhaṃ mūle chindati, rukkhaṃ khandhe chindati, purisaṃ sīse paharati, bhagavantaṃ pādesu vandati.</p>\r\n<p class="bodytext">karaṇe ca sattamī, hatthesu piṇḍāya caranti <span class="note">[mahāva. 119]</span>, pattesu piṇḍāya caranti, pathesu gacchanti, sopi maṃ anusāseyya, sampaṭicchāmi matthake <span class="note">[mi. pa. 6.4.8]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54619,'annya_sadda_10',212,replace(replace('\r\n<p class="bodytext">sampadāne ca sattamī, saṅghe dinne mahapphalaṃ, saṅghe gotamī dadeyyāsi, saṅghe dinne ahañceva pūjito bhavissāmi <a name="M0.0212"></a> <span class="note">[ma. ni. 3.376]</span>, viceyya dānaṃ dātabbaṃ, yattha dinnaṃ mahapphalaṃ <span class="note">[pe. va. 329]</span>. etesu pana visayasattamīpi yujjati.</p>\r\n<p class="bodytext">apādāne ca sattamī, gadalīsu gaje rakkhantiiccādi.</p>\r\n<p class="bodytext">sāmissarādiyoge pana chaṭṭhī sattamī ca hoti, gunnaṃ sāmi, gosu sāmi, gunnaṃ issaro, gosu issaro, gunnaṃ adhipati, gosu adhipati, gunnaṃ dāyādo, gosu dāyādo, gunnaṃ sakkhi, gosu sakkhi, gunnaṃ patibhū, gosu patibhū, gunnaṃ pasuto, gosu pasuto, āyutto kaṭakaraṇassa, āyutto kaṭakaraṇeti, etesu pana sambandhe chaṭṭhī, visayādhāre sattamī. ñāṇasmiṃ pasanno, ñāṇasmiṃ ussukkoti visayādhāre sattamī. ñāṇena pasanno, ñāṇena ussukkoti karaṇe tatiyā.</p>\r\n<p class="bodytext"><a name="para328"></a><span class="paranum">328</span>. <span class="bld">sattamyādhikye</span> <span class="note">[ka. 314; rū. 328; nī. 645; caṃ. 2.1.60; pā. 2.3.9; 1.4.87]</span>.</p>\r\n<p class="bodytext">adhikabhāvatthe upena yuttā liṅgamhā sattamī hoti.</p>\r\n<p class="bodytext">upa khāriyaṃ doṇo, upa nikkhe kahāpaṇaṃ, atirekadoṇā khārī, atirekakahāpaṇaṃ nikkhanti vuttaṃ hoti.</p>\r\n<p class="bodytext"><a name="para329"></a><span class="paranum">329</span>. <span class="bld">sāmittedhinā</span> <span class="note">[caṃ. 2.1.61; pā. 2.3.9; 1.4.97]</span>.</p>\r\n<p class="bodytext">sāmibhāvatthe adhinā yuttā liṅgamhā sattamī hoti.</p>\r\n<p class="bodytext">adhi brahmadatte pañcālā, adhi pañcālesu brahmadatto, adhi devesu buddhā. tattha ‘adhi brahmadatte pañcālā’ti brahmadattissarā pañcālaraṭṭhavāsinoti vadanti, ‘pañcālā’ti vā janapadanāmattā bahuvacanaṃ, kadāci pañcālarājā brahmadatte kāsiraññe issaro, kadāci brahmadatto pañcālaraññe issaroti attho.</p>\r\n','\r',char(13)),'\n',char(10)),'-328-329-');
INSERT INTO pages VALUES(54620,'annya_sadda_10',213,replace(replace('\r\n<p class="bodytext"><a name="para330"></a><span class="paranum">330</span>. <span class="bld">sabbādito</span> <a name="M0.0213"></a> <span class="bld">sabbā</span> <span class="note">[caṃ. 2.1.72; pā. 2.3.27]</span>.</p>\r\n<p class="bodytext">hetvatthehi yoge sabbādīhi sabbanāmehi hetvatthe sabbā vibhattiyo honti.</p>\r\n<p class="bodytext">kiṃ kāraṇaṃ, kena kāraṇena <span class="note">[jā. aṭṭha. 4.15 mātaṅgajātakavaṇṇanā]</span>, kiṃ nimittaṃ, kena nimittena, kiṃ payojanaṃ, kena payojanena, kenaṭṭhena <span class="note">[dha. sa. aṭṭha. nidānakathā]</span>, kena vaṇṇena <span class="note">[saṃ. ni. 1.234]</span>, kimatthaṃ, kuto nidānaṃ <span class="note">[pārā. 42]</span>, kissa hetu <span class="note">[pārā. 39]</span>, kasmiṃ nidāne, etasmiṃ nidāne <span class="note">[pārā. 42]</span>, etasmiṃ pakaraṇe <span class="note">[pārā. 42]</span> iccādi.</p>\r\n<p class="centered">sattamīvibhattirāsi niṭṭhito.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānadīpaniyā</p>\r\n<p class="centered">kārakakaṇḍo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-330-');
INSERT INTO pages VALUES(54621,'annya_sadda_10',214,replace(replace('\r\n<p class="chapter">4. samāsakaṇḍa</p>\r\n<p class="bodytext">atha <a name="M0.0214"></a> yuttatthānaṃ syādyantapadānaṃ ekatthībhāvo vuccate. ekatthībhāvoti ca idha samāso vuccati. so ca samāso chabbidho abyayībhāvo, tappuriso, kammadhārayo, digu, bahubbīhi, dvandoti.</p>\r\n<p class="subhead">abyayībhāvasamāsa</p>\r\n<p class="bodytext">tattha abyayībhāvo paṭhamaṃ vuccate. byayo vuccati vikāro, natthi byayo etassāti abyayo, abyayo hutvā bhavatīti <span class="bld">abyayībhāvo,</span> nānāliṅga, vibhatti, vacanesu rūpavikārarahito hutvā bhavatīti attho, sabbaliṅga,-vibhatti, vacanesupi yebhuyyena ekarūpena pavattatīti vuttaṃ hoti.</p>\r\n<p class="bodytext">abyayanti vā upasagganipātānaṃ eva nāmaṃ, ayaṃ pana pakati abyayaṃ na hoti, asaṅkhyehi saha ekatthatāvasena abyayaṃ hoti, iti anabyayampi abyayaṃ bhavatīti abyayībhāvo.</p>\r\n<p class="bodytext"><a name="para331"></a><span class="paranum">331</span>. <span class="bld">syādisyādinekatthaṃ</span> <span class="note">[ka. 316; rū. 331; nī. 675; caṃ. 2.2.1; pā. 2.1.4]</span>.</p>\r\n<p class="bodytext">adhikārasuttamidaṃ. syādi vuccati syādyantapadaṃ, ‘syādinā’ti syādyantapadena, eko attho yassa taṃ ekatthaṃ, syādipadaṃ syādipadena saha ekatthaṃ hotīti attho.</p>\r\n<p class="bodytext">ettha ca ‘syādī’ti vacanena upasagga, nipātehi saddhiṃ sabbāni nāmikapadāni nāmapaṭirūpakāni ca saṅgaṇhāti, tyādyantapadāni nivatteti.</p>\r\n','\r',char(13)),'\n',char(10)),'-331-');
INSERT INTO pages VALUES(54622,'annya_sadda_10',215,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0215"></a> nāmapaṭirūpakāni nāma ‘yevāvanakadhammā’ iccādīni. tathā saññāsaddabhāvaṃ pattāni "atthipaccayo, natthipaccayo, atthikhīrā brāhmaṇī, aññāsikoṇḍañño, makkhaligosālo" iccādīsu ‘atthi’ iccādīni.</p>\r\n<p class="bodytext">‘ekattha’nti etena dvandasamāsepi padānaṃ ekakattu, ekakammādibhāvena ekatthībhāvo vutto hotīti.</p>\r\n<p class="bodytext"><a name="para332"></a><span class="paranum">332</span>. <span class="bld">asaṅkhyaṃ vibhattisampattisamīpasākalyābhāvayathāpacchāyugapadatthe</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.2; pā. 2.1.6]</span>.</p>\r\n<p class="bodytext">‘asaṅkhya’nti upasaggapadaṃ nipātapadañca vuccati. taṃ dvayampi hi ekatta, bahuttasaṅkhyaṃ paṭicca rūpavikārarahitattā ‘asaṅkhya’nti vuccati. vibhatyatthe, sampatyatthe, samīpatthe, sākalyatthe, abhāvatthe, yathātthe, pacchātthe, yugapadatthe pavattaṃ asaṅkhyaṃ nāma syādipadaṃ aññena syādipadena saha ekatthaṃ hoti. ayañca samāso anvatthavasena ‘asaṅkhyo’ti ca ‘abyayībhāvo’ti ca vuccati.</p>\r\n<p class="bodytext">vibhatyatthe tāva –</p>\r\n<p class="bodytext">adhitthi. ettha ca adhito si, tassa ‘asaṅkhyehi sabbāsa’nti suttena lopo, itthito su, ‘adhi itthīsū’ti vākyaṃ, tassa ca atthaṃ kathentena niccasamāsattā aññapadena viggaho kātabbo "itthīsu pavattā kathā"ti vā "itthīsu pavatto vacanapatho"ti vā "itthīsu pavattaṃ vacana"nti vā, tato purimasuttena ekatthasaññā, iminā suttena asaṅkhyekatthasaññā ca kariyate, ekatthasaññāya pana katāya vākyatthāya payuttānaṃ vibhattīnaṃ attho ekatthapadena vutto hoti, tadā vibhattiyo vuttatthā nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'-332-');
INSERT INTO pages VALUES(54623,'annya_sadda_10',216,replace(replace('\r\n<p class="bodytext">idāni <a name="M0.0216"></a> vuttatthānaṃ appayogārahattā lopavidhānamāha.</p>\r\n<p class="bodytext"><a name="para333"></a><span class="paranum">333</span>. <span class="bld">ekatthatāyaṃ</span> <span class="note">[ka. 316; rū. 331; nī. 675; caṃ. 2.1.39; pā. 2.4.71; 1.2.45, 46]</span>.</p>\r\n<p class="bodytext">eko attho yesaṃ tāni ekatthāni, ‘attho’ti cettha padantare kattu, kammādibhāvena vidheyyo padhānattho eva veditabbo. tathā hi ‘rājaputto’ti ettha puttasaddattho eva tathāvidheyyo hoti, na rājasaddattho, sabbañca vacanavākyaṃ nāma vidheyyatthehi eva sijjhati, no aññathā, yasmā ca ‘rājaputto’ti etaṃ puttasaddatthasseva nāmaṃ hoti, na rājasaddatthassa, tasmā eko padhānabhūto puttasaddattho eva tesaṃ dvinnaṃ saddānaṃ attho nāma hoti, na rājasaddatthoti, ekatthānaṃ bhāvo ekatthatā, ekatthībhāvoti vuttaṃ hoti. so tividho samāso, taddhito, dhātupaccayanto cāti. tissaṃ tividhāyaṃ ekatthatāyaṃ sabbāsaṃ vuttatthānaṃ syādivibhattīnaṃ lopo hotīti iminā sussa lopo. bahulādhikārattā pana aluttasamāsopi dissati.</p>\r\n<p class="bodytext"><a name="para334"></a><span class="paranum">334</span>. <span class="bld">taṃ napuṃsakaṃ</span> <span class="note">[ka. 320; rū. 335; nī. 698; caṃ. 2.2.15; pā. 2.4.18]</span>.</p>\r\n<p class="bodytext">taṃ asaṅkhyaṃ nāma ekatthaṃ napuṃsakaṃ hotīti iminā adhitthīsaddassa napuṃsakabhāvaṃ katvā tato syādyuppatti.</p>\r\n<p class="bodytext"><a name="para335"></a><span class="paranum">335</span>. <span class="bld">syādīsu rasso</span> <span class="note">[ka. 342; rū. 337; nī. 734; caṃ. 2.2.84; pā. 1.2.47]</span>.</p>\r\n<p class="bodytext">napuṃsakassa ekatthassa rasso hoti syādīsu vibhattīsūti iminā īkārassa rasso.</p>\r\n<p class="bodytext"><a name="para336"></a><span class="paranum">336</span>. <span class="bld">pubbasmāmādito</span> <span class="note">[ka. 343; rū. 338; nī. 375; caṃ. 2.1.40; pā. 1.1.41]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-333-334-335-336-');
INSERT INTO pages VALUES(54624,'annya_sadda_10',217,replace(replace('\r\n<p class="bodytext">pubbaamādi <a name="M0.0217"></a> nāma pubbapadatthapadhānabhūto asaṅkhyasamāso vuccati, tato parāsaṃ sabbāsaṃ vibhattīnaṃ lopo hoti, ādisaddena cettha paṭhamāvibhattipi gayhati. atha vā amādi vuccati tappuriso, tato pubbaṃ nāma asaṅkhyasamāso, iti amādito pubbabhūtā asaṅkhyekatthā parāsaṃ sabbāsaṃ vibhattīnaṃ lopo hotīti iminā adhitthisaddato sabbavibhattīnaṃ lopo.</p>\r\n<p class="bodytext">adhitthi tiṭṭhati, itthīsu pavattā kathā tiṭṭhatīti attho. adhitthi tiṭṭhanti, itthīsu pavattā kathāyo tiṭṭhantīti attho. esa nayo sesavibhattīsu sesavacanesu sesaliṅgesu ca. evaṃ sabbaliṅgesu sabbavibhattīsu sabbavacanesu ca ekeneva rūpena tiṭṭhati, tasmā ayaṃ samāso rūpavikārarahitattā ‘abyayībhāvo’ti vuccati.</p>\r\n<p class="bodytext">ettha ca vibhatyattho nāma "adhitthi, bahigāmaṃ, uparigaṅga" miccādīsu sampatyādīhi visesatthehi rahito kevalo vibhattīnaṃ attho vuccati. viggahe pana "kathā, pavattā" iccādīni samāsasāmatthiyena viditāni atthapadāni nāma, adhisaddassa atthapadānītipi vadanti. evaṃ adhikumāri, adhivadhu, adhijambuiccādi.</p>\r\n<p class="bodytext">attani pavatto dhammo, pavattā vādhammāti atthe vibhattīnaṃ lope kate adhiattasaddassa napuṃsakabhāvaṃ katvā tato syādyuppatti, ‘pubbasmāmādito’ti syādīnaṃ lope sampatte –</p>\r\n<p class="bodytext"><a name="para337"></a><span class="paranum">337</span>. <span class="bld">nātomapañcamiyā</span> <span class="note">[ka. 341; rū. 336; nī. 733; caṃ. 2.1.41; pā. 2.4.83; mu. 4.3.374]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-337-');
INSERT INTO pages VALUES(54625,'annya_sadda_10',218,replace(replace('\r\n<p class="bodytext">akārantamhā <a name="M0.0218"></a> asaṅkhyekatthā paraṃ sabbāsaṃ vibhattīnaṃ lopo na hoti, pañcamīvajjitānaṃ vibhattīnaṃ aṃ hoti.</p>\r\n<p class="bodytext">ajjhattaṃ dhammo jāyati, ajjhattaṃ dhammā jāyanti, ajjhattaṃ dhammaṃ passati, ajjhattaṃ dhamme passanti.</p>\r\n<p class="bodytext">apañcamiyāti kiṃ? ajjhattā apeti, ajjhattehi apeti.</p>\r\n<p class="bodytext"><a name="para338"></a><span class="paranum">338</span>. <span class="bld">vā tatiyāsattamīnaṃ</span> <span class="note">[ka. 341; rū. 336; nī. 733; caṃ. 2.1.42; pā. 2.4.84; mu. 4.3.375]</span>.</p>\r\n<p class="bodytext">akārantamhā asaṅkhyekatthā paraṃ tatiyā, sattamīnaṃ vikappena aṃ hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-338-');
INSERT INTO pages VALUES(54626,'annya_sadda_10',219,replace(replace('\r\n<p class="bodytext">ajjhattaṃ dhammena vattati ajjhattena vā, ajjhattaṃ dhammehi vattati ajjhattehi vā, ajjhattaṃ dhammassa deti, ajjhattaṃ dhammānaṃ deti, ajjhattā dhammā apeti, ajjhattehi dhammehi apeti, ajjhattaṃ dhammassa santakaṃ, ajjhattaṃ dhammānaṃ santakaṃ, ajjhattaṃ dhamme tiṭṭhati ajjhatte vā, ajjhattaṃ dhammesu tiṭṭhati ajjhattesu vā. ettha ca ‘ajjhattaṃ dhammo’ti ajjhattabhūto dhammo, ‘ajjhattaṃ dhammā’ti ajjhattabhūtā dhammā iccādinā attho veditabbo. attānaṃ adhikicca pavatto pavattāti vā vuttepi attassa ādhārabhāvo sijjhatiyeva. evaṃ adhicittaṃ, attani visuṃ visuṃ pavattaṃ pavattāni vā paccattaṃ. ettha ca "ajjhattaṃ abhinivisitvā ajjhattaṃ vuṭṭhāti, bahiddhā abhinivisitvā ajjhattaṃ vuṭṭhātī"ti <span class="note">[dha. sa. aṭṭha. 350; saṃ. ni. aṭṭha. 2.2.32]</span> pāṭho atthi, tasmā pañcamiyā aṃbhāvavajjanaṃ appakattāti daṭṭhabbaṃ. "ajjhattā dhammā, bahiddhā <a name="M0.0219"></a> dhammā"ti <span class="note">[dha. sa. tikamātikā 20]</span> pāṭho atthi, tasmā paṭhamādīnampi vikappo labbhatīti.</p>\r\n<p class="bodytext">sampattiatthe –</p>\r\n<p class="bodytext">sampannaṃ brahmaṃ sabrahmaṃ, ‘brahma’nti vedo vuccati. ettha ca ‘akāle sakatthassā’ti suttena sahasaddassa sādeso, bhikkhānaṃ samiddhi subhikkhaṃ, ‘syādīsu rasso’ti suttena katanapuṃsakassa rassattaṃ.</p>\r\n<p class="bodytext">samīpe –</p>\r\n<p class="bodytext">nagarassa samīpaṃ upanagaraṃ, kumbhassa samīpaṃ upakumbhaṃ, maṇikāya samīpaṃ upamaṇikaṃ, vadhuyā samīpaṃ upavadhu, gunnaṃ samīpaṃ upagu, ‘gossū’ti suttena ossa uttaṃ.</p>\r\n<p class="bodytext">sākalye –</p>\r\n<p class="bodytext">tiṇena saha sakalaṃ satiṇaṃ, tiṇena saddhiṃ sakalaṃ vatthuṃ ajjhoharatīti attho. sahasaddassa sādeso.</p>\r\n<p class="bodytext">abhāve –</p>\r\n<p class="bodytext">makkhikānaṃ abhāvo nimmakkhikaṃ, darathānaṃ abhāvo niddarathaṃ, bhikkhānaṃ abhāvo dubbhikkhaṃ, abhāvatthopi dusaddo atthi. yathā? dussīlo duppaññoti. ettha ca ‘sampannaṃ brahma’ntiādinā saddabyākaraṇesu atthavacanaṃ saddatthavibhāvanamattaṃ. suttantesu pana imesaṃ padānaṃ yuttaṃ abhidheyyatthaṃ ñatvā tadanurūpaṃ atthavacanampi veditabbaṃ.</p>\r\n<p class="bodytext">yathāsaddatthe –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54627,'annya_sadda_10',220,replace(replace('\r\n<p class="bodytext">rūpassa sabhāvassa yogyaṃ anurūpaṃ, attānaṃ attānaṃ paṭicca pavattaṃ paccattaṃ, aḍḍhamāsaṃ aḍḍhamāsaṃ anugataṃ anvaḍḍhamāsaṃ, gharaṃ gharaṃ anugataṃ anugharaṃ, vassaṃ vassaṃ anugataṃ anuvassaṃ, jeṭṭhānaṃ <a name="M0.0220"></a> anupubbaṃ anujeṭṭhaṃ, sattiyā anurūpaṃ yathāsatti, balassa anurūpaṃ yathābalaṃ, kamassa anurūpaṃ yathākkamaṃ. evaṃ yathāsaṅkhyaṃ, yathālābhaṃ. sotassa paṭilomaṃ paṭisotaṃ, paṭivātaṃ, paṭisaddaṃ.</p>\r\n<p class="bodytext">pacchāpadatthe –</p>\r\n<p class="bodytext">rathassa pacchā anurathaṃ.</p>\r\n<p class="bodytext">yugabhūto padattho yugapadattho, sahabhāvīatthadvayassetaṃ nāmaṃ. tattha asaniphalena saha pavattaṃ cakkaṃ sacakkaṃ, gadāvudhena yugaḷapavattaṃ vāsudevassa cakkāvudhantipi vadanti <span class="note">[yugapadatthe sacakkaṃ nidhehi, (moggallānavuttiyaṃ). cakkena yugapata dhehi sacakkaṃ, (mugdhabodhavuttiyaṃ). he bisaṇu! cakkena saha yugapadekakāle gadaṃ dhāraya. (mugdhabodhaṭīkāyaṃ 226 piṭṭhe)]</span>, sahassa sattaṃ.</p>\r\n<p class="bodytext"><a name="para339"></a><span class="paranum">339</span>. <span class="bld">yathā natulye</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.3; pā. 2.1.7]</span>.</p>\r\n<p class="bodytext">tulyato aññasmiṃ atthe pavatto yathāsaddo syādinā saha ekattho hoti <span class="note">[moggallāne aññathāvutti dassitā]</span>.</p>\r\n<p class="bodytext">yathāsatti, yathābalaṃ, yathākkamaṃ, ye ye vuḍḍhā yathāvuḍḍhaṃ, vuḍḍhānaṃ paṭipāṭi vā yathāvuḍḍhaṃ.</p>\r\n<p class="bodytext">natulyeti kiṃ? yathā devadatto, tathā yaññadatto.</p>\r\n<p class="bodytext"><a name="para340"></a><span class="paranum">340</span>. <span class="bld">yāvāvadhāraṇe</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.4; pā. 2.1.8]</span>.</p>\r\n<p class="bodytext">avadhāraṇaṃ vuccati paricchindanaṃ, avadhāraṇe pavatto yāvasaddo syādinā saha ekattho bhavati.</p>\r\n','\r',char(13)),'\n',char(10)),'-339-340-');
INSERT INTO pages VALUES(54628,'annya_sadda_10',221,replace(replace('\r\n<p class="bodytext">yattakaṃ <a name="M0.0221"></a> attho vattatīti yāvadatthaṃ, dāgamo. yattakaṃ jīvo vattatīti yāvajīvaṃ, yattakaṃ āyu vattatīti yāvatāyukaṃ, takāra, kakārā āgamā.</p>\r\n<p class="bodytext"><a name="para341"></a><span class="paranum">341</span>. <span class="bld">parāpābahitiropurepacchā vā pañcamyā</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.7; pā. 2.1.12, 13; ‘payyapā.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">pari, apa, āiccādayo saddā pañcamyantena syādinā saha ekatthā bhavanti vā.</p>\r\n<p class="bodytext">pabbatato pari samantā vassīti devo paripabbataṃ paripabbatā vā, pabbataṃ vajjetvā vassīti attho. pabbatato bahiddhā apapabbataṃ apapabbatā vā, pāṭaliputtato bahiddhā vassīti devo āpāṭaliputtaṃ āpāṭaliputtā vā, ākumārehi kaccāyanassa yaso vattatīti ākumāraṃ ākumārā vā, ābhavaggā bhagavato yaso vattatīti ābhavaggaṃ ābhavaggā vā, āpāṇakoṭiyā saraṇagamanaṃ vattatīti āpāṇakoṭikaṃ, kāgamo. gāmato bahi bahigāmaṃ bahigāmā vā, evaṃ bahinagaraṃ, bahileṇaṃ, pabbatato tiro tiropabbataṃ tiropabbatā vā, evaṃ tiropākāraṃ, tirokuṭṭaṃ. ettha ca ‘tiro’ti parabhāgo vuccati. bhattamhā pure purebhattaṃ purebhattā vā, aruṇamhā pure purāruṇaṃ purāruṇā vā, bhattassa pacchā pacchābhattaṃ pacchābhattā vā.</p>\r\n<p class="bodytext"><a name="para342"></a><span class="paranum">342</span>. <span class="bld">samīpāyāmesvanu</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.9; pā. 2.1.15, 16]</span>.</p>\r\n<p class="bodytext">samīpe āyāme ca pavatto anusaddo syādinā saha ekattho bhavati vā.</p>\r\n<p class="bodytext">vanassa samīpaṃ anuvanaṃ, asani anuvanaṃ gatā, gaṅgaṃ anuyātā anugaṅgaṃ, bārāṇasī.</p>\r\n','\r',char(13)),'\n',char(10)),'-341-342-');
INSERT INTO pages VALUES(54629,'annya_sadda_10',222,replace(replace('\r\n<p class="bodytext"><a name="para343"></a><span class="paranum">343</span>. <span class="bld">oro</span> <a name="M0.0222"></a> <span class="bld">pari paṭi pāre majjhe heṭṭhuddhādhonto vā chaṭṭhiyā</span> <span class="note">[ka. 319; rū. 330; nī. 696; caṃ. 2.2.11; pā. 2.1.18; ‘orepari.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">orādayo saddā chaṭṭhīyantena syādinā saha ekatthā bhavanti vā.</p>\r\n<p class="bodytext">ettha ca ore, pāre, majjhesaddesu ‘tadaminādīnī’ti suttena ekāro, gaṅgāya oraṃ oregaṅgaṃ, sikharassa upari uparisikharaṃ. evaṃ uparipāsādaṃ, uparimañcaṃ, uparipabbataṃ, sotassa paṭilomaṃ paṭisotaṃ. evaṃ paṭivātaṃ, yamunāya pāraṃ pāreyamunaṃ, gaṅgāya majjhaṃ majjhegaṅgaṃ, pāsādassa heṭṭhā heṭṭhāpāsādaṃ, heṭṭhāmañcaṃ, gaṅgāya uddhaṃ uddhaṃgaṅgaṃ, gaṅgāya adho adhogaṅgaṃ, pāsādassa anto antopāsādaṃ. evaṃ antogāmaṃ, antonagaraṃ, antovassaṃ.</p>\r\n<p class="bodytext">vāti kiṃ? gaṅgāoraṃ, majjhesamuddasmiṃ iccādi.</p>\r\n<p class="bodytext"><a name="para344"></a><span class="paranum">344</span>. <span class="bld">tiṭṭhagvādīni</span> <span class="note">[ka. 319; rū. 330; nī. 697; caṃ. 2.2.10; pā. 2.1.17]</span>.</p>\r\n<p class="bodytext">tiṭṭhaguiccādīni asaṅkhyekatthe sijjhanti.</p>\r\n<p class="bodytext">tiṭṭhanti gāvo yasmiṃ kāle tiṭṭhagu, vahanti gāvoyasmiṃ kāle vahagu, ‘gossū’ti suttena ossa uttaṃ. āyatiṃ yavo yasmiṃ kāleti āyatiyavo, khale yavo yasmiṃ kāleti khaleyavaṃ. pubbapade vibhattialopo. lunā yavā yasmiṃ kāleti lunayavaṃ, ettha ‘lunā’ti lāvitā, luyamānā yavā yasminti luyamānayavaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-343-344-');
INSERT INTO pages VALUES(54630,'annya_sadda_10',223,replace(replace('\r\n<p class="bodytext">tathā pāto nahānaṃ yasmiṃ kāleti pātanahānaṃ. evaṃ sāyanahānaṃ, pāto kammakaraṇakālo yasminti <a name="M0.0223"></a> pātakālaṃ. evaṃ sāyakālaṃ, pāto vassati megho yasminti pātameghaṃ. evaṃ sāyameghaṃ, pāto gantabbo maggo yasminti pātamaggaṃ. evaṃ sāyamaggaṃ iccādi. mahāvuttinā pātosaddassa pātattaṃ. ettha ca ‘tiṭṭhagu’ iccādīni viggahatthavasena aññapadatthe siddhāni viya dissanti, aññapadassa pana liṅgādīnaṃ vasena tesaṃ rūpavikāro nāma natthi, tasmā abyayarūpattā idha gahitāni, sabbañcetaṃ asaṅkhyasamāsapadaṃ nāma napuṃsakaṃ eva hoti, rassantameva hoti. sabbavibhattīnañca akārantamhā bahulaṃ aṃ hoti, ikārukārantehi lopo hoti.</p>\r\n<p class="centered">abyayībhāvasamāso niṭṭhito.</p>\r\n<p class="subhead">tappurisasamāsa</p>\r\n<p class="subhead">dutiyātappurisa</p>\r\n<p class="bodytext">atha amādisamāso vuccate, so tappurisoti ca vuccati. tassa puriso tappuriso, tappurisasaddena sadisattā ayaṃ samāso <span class="bld">tappuriso</span>ti vuccati. yathā hi tappurisasaddo visesanapadatthaṃ jahitvā visesyapadatthe tiṭṭhati, evaṃ ayaṃ samāsopīti.</p>\r\n<p class="bodytext"><a name="para345"></a><span class="paranum">345</span>. <span class="bld">amādi</span> <span class="note">[ka. 327; rū. 351; nī. 704; caṃ. 2.2.16]</span>.</p>\r\n<p class="bodytext">amādivibhattiyuttaṃ syādyantapadaṃ paṭhamantena syādyantapadena saha ekatthaṃ bhavati, ayañca samāso anvatthavasena "amādisamāso"ti ca "tappurisasamāso"ti ca vuccati. iminā amādisahitassa vākyassa amādekatthasaññaṃ katvā vuttatthānaṃ vibhattīnaṃ lopo, tato ekatthapadante syādyuppatti hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-345-');
INSERT INTO pages VALUES(54631,'annya_sadda_10',224,replace(replace('\r\n<p class="bodytext">so <a name="M0.0224"></a> pana samāso dutiyātappuriso, tatiyātappuriso, catutthītappuriso, pañcamītappuriso, chaṭṭhītappuriso, sattamītappurisoti chabbidho. ekamekasmiñcettha "niccasamāso, aniccasamāso"ti ca "luttasamāso, aluttasamāso"ti ca duvidho.</p>\r\n<p class="bodytext">tattha dutiyātappuriso kattuvācakesu gata, nissita,-atīta, atikkanta, patta, āpannaiccādīsu paresu hoti.</p>\r\n<p class="bodytext">gāmaṃ gatoti gāmagato gāmaṃ gato vā. evaṃ araññagato, bhūmigato, rājānaṃ nissitoti rājanissito. evaṃ atthanissito, dhammanissito. bhavaṃ atīto bhavātīto. evaṃ bhayātīto, kālātīto, khaṇātīto, pamāṇaṃ atikkantoti pamāṇātikkanto, sukhaṃ pattoti sukhappatto. evaṃ dukkhappatto, sotaṃ āpannoti sotāpanno. evaṃ nirodhasamāpanno, addhānamaggaṃ paṭipannoti addhānamaggappaṭipanno, rukkhaṃ ārūḷhoti rukkhārūḷho, rathārūḷho, oghaṃ tiṇṇoti oghatiṇṇo oghaṃ tiṇṇo vā iccādi.</p>\r\n<p class="bodytext">kammupapadavihitehi kitantapadehi pana niccasamāsoyeva, kumbhaṃ karotīti kumbhakāro, rathakāro, pattaṃ gaṇhātīti pattaggāho, atthaṃ kāmetīti atthakāmo, dhammakāmo, vinayaṃ dhāretīti vinayadharo, dhammadharo, brahmaṃ carati sīlenāti brahmacārī, bhavapāraṃ gacchati sīlenāti bhavapāragū, sabbaṃ jānātīti sabbaññū, atthaññū, dhammaññūiccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54632,'annya_sadda_10',225,replace(replace('\r\n<p class="bodytext">bahulādhikārattā ta, tavantu, tāvī, anta, māna, tuna, tvāna, tvā, tuṃ, tavepaccayantesu paresu samāso na hoti, vassaṃ vuttho, odanaṃ bhutto, odanaṃ bhuttavā, odanaṃ bhuttāvī, dhammaṃ suṇanto, dhammaṃ suṇamāno <a name="M0.0225"></a> dhammaṃ sotuna, dhammaṃ sutvāna, dhammaṃ sutvā, dhammaṃ sotuṃ, dhammaṃ sotave.</p>\r\n<p class="centered">iti dutiyātappuriso.</p>\r\n<p class="subhead">tatiyātappurisa</p>\r\n<p class="bodytext">tatiyātappuriso kammavācakesu kitantesu ca sampanna, sahagatādīsu ca pubba, sadisa, sama, ūnattha, kalaha, nipuṇa,-missaka, sakhilādīsu ca paresu hoti.</p>\r\n<p class="bodytext">buddhena bhāsito buddhabhāsito. evaṃ buddhadesito, buddhapaññatto, buddharakkhito, satthārā vaṇṇito satthuvaṇṇito, viññūhi garahito viññugarahito, viññupasattho, issarena kataṃ issarakataṃ, attanā kataṃ sayaṃkataṃ, parehi kataṃ paraṃkataṃ, bindāgamo. sukehi āhaṭaṃ sukāhaṭaṃ, raññā hato rājahato, rogena pīḷito rogapīḷito, agginā daḍḍho aggidaḍḍho, sappena daṭṭho sappadaṭṭho, sallena viddhā sallaviddhā, icchāya apakato abhibhūto icchāpakato.</p>\r\n<p class="bodytext">sīlena sampanno sīlasampanno. evaṃ sukhasahagataṃ, ñāṇasampayuttaṃ, mittasaṃsaggo, piyasampayogo, piyavippayogo, jātiyā andho jaccandho, guṇahīno, guṇavuḍḍho, catuvaggena saṅghena karaṇīyaṃ kammaṃ catuvaggakaraṇīyaṃ. evaṃ pañcavaggakaraṇīyaṃ, kākehi peyyā kākapeyyā, nadī.</p>\r\n<p class="bodytext">ekakkharesu parapadesu niccasamāso, urena gacchatīti urago, pādena pivatīti pādapo iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54633,'annya_sadda_10',226,replace(replace('\r\n<p class="bodytext">kvaci majjhepadalopo, guḷena saṃsaṭṭho odano guḷodano. evaṃ khīrodano, assena yutto ratho assaratho. evaṃ ājaññaratho, maggena sampayuttaṃ cittaṃ maggacittaṃ<a name="M0.0226"></a>, jambuyā paññāto dīpo jambudīpo, ekena adhikā dasa ekādasa iccādi.</p>\r\n<p class="bodytext">pubbādīsu – māsena pubbo māsapubbo, mātarā sadiso mātusadiso. evaṃ mātusamo, pitusamo, ekena ūnā vīsati ekūnavīsati, sīlena vikalo sīlavikalo, asinā kalaho asikalaho, vācāya nipuṇo vācānipuṇo. evaṃ yāvakālikasammissaṃ, vācāsakhilo, satthārā sadiso satthukappo, puññena attho puññattho, puññena atthiko puññatthiko. evaṃ seyyatthiko, mahagghatthiko, guṇena adhiko guṇādhiko iccādi.</p>\r\n<p class="bodytext">bahulādhikārā kvaci samāso na hoti, pharasunā chinnaṃ, kākehi pātabbā, dassanena pahātabbā dhammā, bhāvanāya pahātabbā dhammā iccādi.</p>\r\n<p class="centered">iti tatiyātappuriso.</p>\r\n<p class="subhead">catutthītappurisa</p>\r\n<p class="bodytext">catutthītappuriso tadatthe vā attha, hita, deyyādīsu vā paresu hoti.</p>\r\n<p class="bodytext">kathinassa dussaṃ kathinadussaṃ, kathinatthāya ābhaṭaṃ dussanti attho. evaṃ kathinacīvaraṃ, kathināya dussaṃ, kathināya cīvarantipi yujjati, kathinatthārāyāti attho. cīvarāya dussaṃ cīvaradussaṃ. evaṃ cīvaramūlaṃ <span class="note">[cīvaramūlyaṃ (rū. nī.)]</span>, saṅghassa ābhaṭaṃ bhattaṃ saṅghabhattaṃ, saṅghatthāya vā paṭiyattaṃ bhattaṃ saṅghabhattaṃ. evaṃ āgantukabhattaṃ, gamikabhattaṃ, gilānabhattaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54634,'annya_sadda_10',227,replace(replace('\r\n<p class="bodytext">saṅghassa <a name="M0.0227"></a> attho saṅghattho, lokassa hito lokahito, buddhassa deyyaṃ buddhadeyyaṃ, buddhassa paṇāmo buddhappaṇāmo. evaṃ buddhathomanā, buddhupaṭṭhānaṃ, suttassa anulomaṃ suttānulomaṃ. evaṃ suttānurūpaṃ, suttānukūlaṃ, suttānuguṇaṃ, ṭhānassa arahaṃ ṭhānārahaṃ, rañño arahaṃ rājārahaṃ. evaṃ rājagghaṃ, rājadeyyaṃ, kattuṃ kāmetīti kattukāmo. evaṃ gantukāmo, kathetukāmo, daṭṭhukāmo, sotukāmo. ettha ca tumantassa asaṅkhyattā ‘asaṅkhyehi sabbāsa’nti tato niccaṃ catutthīlopo hoti, samāse kate niccaṃ niggahītalopo ca. "saṅghassa dātabbaṃ, saṅghassa dātuṃ" iccādīsu samāso na hoti.</p>\r\n<p class="centered">iti catutthītappuriso.</p>\r\n<p class="subhead">pañcamītappurisa</p>\r\n<p class="bodytext">pañcamītappuriso apagamana, bhaya, virati, mocanādiatthesu paresu hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54635,'annya_sadda_10',228,replace(replace('\r\n<p class="bodytext">methunā apetoti methunāpeto, palāsato apagatoti palāsāpagato, nagaramhā niggatoti nagaraniggato, piṇḍapātato paṭikkantoti piṇḍapātappaṭikkanto. evaṃ gāmanikkhanto, rukkhapatito, sāsanamhā cutoti sāsanacuto, āpattiyā vuṭṭhānaṃ āpattivuṭṭhānaṃ, udakato uggato udakuggato, bhavato nissaṭo bhavanissaṭo, khandhasaṅgahato nissaṭanti khandhasaṅgahanissaṭaṃ, coramhā bhītoti corabhīto, pāpato bhāyati sīlenāti pāpabhīruko, pāpato jigucchati sīlenāti pāpajigucchī, vaṭṭato nibbindatīti vaṭṭanibbinno, kāyaduccaritato virati kāyaduccaritavirati. evaṃ vacīduccaritavirati, bandhanā mutto bandhanamutto. evaṃ bandhanamokkho, kammato samuṭṭhitaṃ kammasamuṭṭhitaṃ<a name="M0.0228"></a>. evaṃ kammajātaṃ, kammasambhūtaṃ, kammanibbattaṃ, lokato aggo lokaggo. evaṃ lokajeṭṭho, lokuttamo, sabbehi jeṭṭho sabbajeṭṭho, sabbehi kaniṭṭho sabbakaniṭṭho. evaṃ sabbahīno, sabbapacchimo, ukkaṭṭhato ukkaṭṭhoti ukkaṭṭhukkaṭṭho, omakato omakoti omakomako.</p>\r\n<p class="bodytext">kvaci niccasamāso hoti, mātito jāto mātujo. evaṃ pitujo, kammajaṃ, cittajaṃ, utujaṃ iccādi.</p>\r\n<p class="centered">iti pañcamītappuriso.</p>\r\n<p class="subhead">chaṭṭhītappurisa</p>\r\n<p class="bodytext">rañño putto rājaputto. evaṃ rājapuriso, buddhasāvako, samuddaghoso, dhaññānaṃ rāsi dhaññarāsi, mattikāya pattoti mattikāpatto, vikārasambandhe chaṭṭhī, mattikāmayapattoti attho. evaṃ suvaṇṇakaṭāhaṃ, suvaṇṇabhājanaṃ, pānīyassa thālakaṃ pānīyathālakaṃ.</p>\r\n<p class="bodytext">samāsamajjhe ī, ūnaṃ bahulaṃ rassattaṃ, daṇḍino kulaṃ daṇḍikulaṃ, hatthipadaṃ, itthirūpaṃ, nadikūlaṃ, naditīraṃ, bhikkhunīnaṃ saṅgho bhikkhunisaṅgho, jambuyā sākhā jambusākhā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54636,'annya_sadda_10',229,replace(replace('\r\n<p class="bodytext">anta, māna, niddhāraṇiya, pūraṇa, bhāva, suhitatthehi samāso na hoti, mamaṃ anukubbanto, mamaṃ anukurumāno, gunnaṃ kaṇhā sampannakhīratamā. vibhattāvadhichaṭṭhiyā pana hotiyeva, narānaṃ uttamo naruttamo, naraseṭṭho, naravaro, gaṇānaṃ uttamo gaṇuttamo, dvipadānaṃ uttamo dvipaduttamo iccādi. sissānaṃ pañcamo sisso, kappassa tatiyo bhāgo, pakkhassa aṭṭhamī, paṭassa sukkatā, rūpassa lahutā, rūpassa <a name="M0.0229"></a> mudutā, rūpassa kammaññatā. kvaci hoti, kāyalahutā, cittalahutā, buddhasubuddhatā. dhammasudhammatā, phalānaṃ suhito, phalānaṃ titto, phalānaṃ asito, karaṇatthe chaṭṭhī.</p>\r\n<p class="bodytext">"bhaṭo rañño putto devadattassā"ti ettha ‘rājaputto’ti na hoti aññamaññānapekkhattā. "devadattassa kaṇhā dantā"ti ettha ‘devadattakaṇhadantā’ti na hoti aññasāpekkhattā <span class="note">[nī. 690]</span>. aññasāpekkhattepi niccaṃ sambandhīpekkhasaddānaṃ samāso hoti vākye viya samāsepi sambandhassa viditattā. vuttañca "satipi sāpekkhatte gamakattā samāso hotī"ti <span class="note">[ka. 328; rū. 352; nī. 691]</span>, devadattagurukulaṃ, rājadāsīputto, devadāsīputto iccādi. tattha devadattassa guru devadattaguru, tassa kulaṃ devadattagurukulanti viggaho. guruno kulaṃ gurukulaṃ, devadattassa gurukulaṃ devadattagurukulantipi vadanti. "rañño māgadhassa bimbisārassa putto"ti etthapi aññasāpekkhattā ‘bimbisāraputto’ti na hoti, rañño go ca asso ca puriso cāti atthe ‘rājagavassapurisā’ti hoti dvandato pubbapadassa dvandapadehipi paccekaṃ sambandhassa viditattā. tathā dvandato parapadassapi, narānañca devānañca sārathi naradevasārathi.</p>\r\n<p class="centered">iti chaṭṭhītappuriso.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54637,'annya_sadda_10',230,replace(replace('\r\n<p class="subhead">sattamītappurisa</p>\r\n\r\n<p class="bodytext">sattamītappurise rūpe saññā rūpasaññā. ettha ca kārakānaṃ kriyāsādhanalakkhaṇattā kriyāpadeheva sambandho hoti, tasmā akriyavācakena parapadena saddhiṃ samāse jāte majjhe anurūpaṃ kriyāpadaṃ viññāyati, yathā <a name="M0.0230"></a> ‘assena yutto ratho assaratho’ iti ‘rūpe saññā’ti rūpe uppannā saññāti attho. cakkhusmiṃ viññāṇaṃ cakkhuviññāṇaṃ, dhamme rato dhammarato. evaṃ dhammaruci, dhammagāravo, dāne ajjhāsayo dānajjhāsayo. evaṃ dānādhimutti, vaṭṭe bhayaṃ vaṭṭabhayaṃ, vaṭṭadukkhaṃ, gāme sūkaro gāmasūkaro, vanamahiṃso, samuddamaccho, itthīsu dhutto itthidhutto, itthisoṇḍo.</p>\r\n<p class="bodytext">upapadakitantesu niccasamāso <span class="note">[nī. 682]</span>, vane caratīti vanacaro, kāmāvacaro, kucchimhi sayatīti kucchissayo, gabbhe setīti gabbhaseyyo, thale tiṭṭhatīti thalaṭṭho, jalaṭṭho, pabbataṭṭho, paṅke jātaṃ paṅkajaṃ. evaṃ atrajo, khetrajo iccādi. idha na hoti <span class="note">[nī. 681]</span>, bhojane mattaññutā, indriyesu guttadvāro, āsane nisinno, āsane nisīditabbaṃ.</p>\r\n<p class="centered">iti sattamītappuriso.</p>\r\n<p class="subhead">luttatappurisa</p>\r\n<p class="bodytext">tappurisapadānaṃ mahāvuttisuttena kvaci vipallāso.</p>\r\n<p class="bodytext">uparigaṅgā, heṭṭhānadī, antovihāro, antosamāpatti, haṃsānaṃ rājā rājahaṃso haṃsarājā vā, māsassa aḍḍhaṃ aḍḍhamāsaṃ, kahāpaṇassa aḍḍhaṃ aḍḍhakahāpaṇaṃ, aḍḍhamāsaṃ, rattiyā aḍḍhaṃ aḍḍharattaṃ. evaṃ pubbarattaṃ, pararattaṃ, issa attaṃ. kāyassa pubbabhāgo pubbakāyo, parakāyo, ahassa pubbo pubbaṇho, majjhaṇho, sāyanho, pubbediṭṭho diṭṭhapubbo, tathāgataṃ diṭṭhapubbo thero, tathāgato diṭṭhapubbo therena iccādi.</p>\r\n<p class="centered">iti luttatappuriso.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54638,'annya_sadda_10',231,replace(replace('\r\n<p class="subhead">aluttatappurisa</p>\r\n\r\n<p class="bodytext">idāni <a name="M0.0231"></a> aluttatappurisā vuccante.</p>\r\n<p class="bodytext">dīpaṅkaro, pabhaṅkaro, amatandado, purindado, vessantaro, attantapo, parantapo, raṇañjaho, jutindharo, vijjandharo, dassanenapahātabbadhammo, kutojo, tatojo, itojo, bhayato upaṭṭhānaṃ bhayatupaṭṭhānaṃ, kaṭattākammaṃ, kaṭattārūpaṃ, parassapadaṃ, attanopadaṃ, devānamindo, devānaṃpiyatisso, gavampatitthero, pubbenivāso, majjhekalyāṇaṃ, dūrerūpaṃ, santikerūpaṃ, dūrenidānaṃ, santikenidānaṃ, antevāsiko, janesuto, kāmesumicchācāro iccādi <span class="note">[ka. 327; rū. 351; nī. 686]</span>.</p>\r\n<p class="centered">iti aluttatappuriso.</p>\r\n<p class="bodytext">sabbo cāyaṃ amāditappuriso abhidheyyavacano parapadaliṅgo ca.</p>\r\n<p class="centered">amāditappuriso niṭṭhito.</p>\r\n<p class="subhead">kammadhārayasamāsa</p>\r\n<p class="bodytext">atha kammadhārayasaññito paṭhamātappuriso vuccate.</p>\r\n<p class="bodytext">kammamiva dvayaṃ dhāretīti <span class="bld">kammadhārayo</span>. yathā kammaṃ kriyañca payojanañca dvayaṃ dhāreti kamme sati tassa dvayassa sambhavato, tathā ayaṃ samāso ekassa atthassa dve nāmāni dhāreti imasmiṃ samāse sati ekatthajotakassa nāmadvayassa sambhavatoti <span class="note">[ka. 324; rū. 339; nī. 702]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54639,'annya_sadda_10',232,replace(replace('\r\n<p class="bodytext">api ca kattabbanti kammaṃ, dhāretabbanti dhāriyaṃ, kammañca taṃ dhāriyañcāti kammadhāriyaṃ, yaṃkiñci hitakammaṃ, kammadhāriyasaddasadisattā <a name="M0.0232"></a> sabbo cāyaṃ samāso <span class="bld">kammadhārayo</span>ti vuccati issa attaṃ katvā. yathā hi kammadhāriyasaddo ekassa atthassa dve nāmāni dhāreti, tathā ayaṃ samāsopīti. so eva uttarapadatthapadhānatāsaṅkhātena tappurisalakkhaṇena yuttattā ‘tappuriso’ti ca vuccati. bhinnapavattinimittānaṃ dvinnaṃ padānaṃ visesanavisesitabbabhāvena ekasmiṃ atthe pavatti tulyādhikaraṇatā nāma, tena tulyādhikaraṇalakkhaṇena yuttattā ‘tulyādhikaraṇasamāso’ti ca vuccati. so eva ca visesanapadavasena guṇavisesadīpanattā ‘visesanasamāso’ti ca vuccati. tasmiṃ visesanasamāse –</p>\r\n<p class="bodytext"><a name="para346"></a><span class="paranum">346</span>. <span class="bld">visesanamekatthena</span> <span class="note">[ka. 324; rū. 339; nī. 702; caṃ. 2.2.18; pā. 2.1.57]</span>.</p>\r\n<p class="bodytext">visesanabhūtaṃ syādyantapadaṃ ekatthena visesyabhūtena syādyantapadena saddhiṃ ekatthaṃ hoti.</p>\r\n<p class="bodytext">ettha ca visesīyati dabbaṃ visiṭṭhaṃ karīyati etenāti visesanaṃ. eko attho yassāti ekatthaṃ, ‘eko’ti samāno, ‘attho’ti abhidheyyattho, nemittakattho, soyeva dvinnaṃ pavattinimittānaṃ adhiṭṭhānaṭṭhena ‘adhikaraṇa’nti ca vuccati. pavattinimittānañca adhiṭṭhānatte sati padānampi adhiṭṭhānatā siddhā hoti. iti ekatthanti tulyādhikaraṇaṃ, samānādhikaraṇanti vuttaṃ hoti, tena ekatthena. ‘ekatthaṃ hotī’ti ekatthībhūtaṃ hotīti attho.</p>\r\n<p class="bodytext">so ca samāso navavidho visesanapubbapado, visesanuttarapado, visesanobhayapado, upamānuttarapado, sambhāvanāpubbapado, avadhāraṇapubbapado, nanipātapubbapado, kunipātapubbapado, pādipubbapado cāti.</p>\r\n','\r',char(13)),'\n',char(10)),'-346-');
INSERT INTO pages VALUES(54640,'annya_sadda_10',233,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0233"></a> <span class="bld">visesanapubbapado</span> yathā? mahāpuriso, mahānadī, mahabbhayaṃ. ettha ca "sā senā dissate mahā <span class="note">[jā. 2.22.771]</span>, bārāṇasirajjaṃ nāma mahā"ti <span class="note">[jā. aṭṭha. 1.1.mahāsīlavajātakavaṇṇanā]</span> pāḷi dissati. tasmā samāsepi tiliṅge nipātarūpo mahāsaddo yujjati. mahā ca so puriso cāti mahāpuriso, mahā ca sā nadī cāti mahānadī, mahā ca taṃ bhayañcāti mahabbhayaṃ, dvittaṃ saṃyoge ca rassattaṃ. mahāsaddavevacanena mahantasaddenapi vākyaṃ dassetuṃ yujjati, mahanto puriso mahāpuriso, mahantī nadī mahānadī, mahantaṃ bhayaṃ mahabbhayanti. ca, tasaddehi ca saddhiṃ paripuṇṇaṃ katvā dassetuṃ yujjati, mahanto ca so puriso cāti mahāpuriso, mahantī ca sā nadī cāti mahānadī, mahantañca taṃ bhayañcāti mahabbhayanti. mahantasaddo vā mahā hoti, ‘ṭa ntantūna’nti suttena uttarapade pare ntassa sabbassa attaṃ, mahāvuttinā dīgho ca.</p>\r\n<p class="bodytext">ettha ca dvīhi casaddehi dvinnaṃ padānaṃ sakatthanānāttaṃ dīpeti. taṃsaddena sakatthanānāttepi sakatthānaṃ adhikaraṇabhūtassa dabbatthassa ekattaṃ dīpeti. imasmiṃ byākaraṇe visuṃ rūpavidhānakiccaṃ nāma natthi, taṃtaṃsuttavidhānañca tadanurūpaṃ dassitaviggahavākyañca disvā tassa tassa siddhapadassa atthabyañjanavinicchaye ñāte rūpavidhānakiccaṃ siddhaṃ hoti, santo ca so puriso cāti sappuriso, setahatthī, nīluppalaṃ, lohitacandanaṃ.</p>\r\n<p class="bodytext">visadisaliṅga, vacanāpi saddā ekatthā honti, vinayo ca so pariyatti cāti vinayapariyatti, vinayo ca so piṭakañcāti vinayapiṭakaṃ, avijjā ca sā paccayo cāti avijjāpaccayo, avijjā ca sā nīvaraṇañcāti avijjānīvaraṇaṃ. evaṃ itthiratanaṃ, sīlañca taṃ guṇo cāti sīlaguṇo, sīlañca taṃ patiṭṭhā cāti sīlapatiṭṭhā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54641,'annya_sadda_10',234,replace(replace('\r\n<p class="bodytext">tathā <a name="M0.0234"></a> vīsati ca sā purisā cāti vīsatipurisā, satañca taṃ purisā cāti satapurisā, saṅkhārā ca te paccayo cāti saṅkhārapaccayo, aṅgā ca te janapadañcāti aṅgajanapadaṃ, magadhā ca te raṭṭhañcāti magadharaṭṭhaṃ. evaṃ kāsiraṭṭhaṃ iccādi.</p>\r\n<p class="bodytext">idha na hoti <span class="note">[rū. 341; nī. 681]</span>, puṇṇo mantānīputto, citto gahapati, sakko devarājā, brahmā sahampati iccādi.</p>\r\n<p class="bodytext">kvaci niccasamāso, kaṇhasappo, lohitamālaṃ iccādi.</p>\r\n<p class="bodytext"><span class="bld">visesanuttarapado</span> yathā? sāriputtatthero, buddhaghosācariyo, ācariyaguttilo vā, mahosadhapaṇḍito, purisuttamo, purisavaro, purisaviseso iccādi.</p>\r\n<p class="bodytext"><span class="bld">visesanobhayapado</span> yathā? chinnañca taṃ parūḷhañcāti chinnaparūḷhaṃ, sītañca taṃ uṇhañcāti sītuṇhaṃ, khañjo ca so khujjo cāti khañjakhujjo. evaṃ andhabadhiro, katañca taṃ akatañcāti katākataṃ, chiddāvachiddaṃ, chinnabhinnaṃ, sittañca taṃ sammaṭṭhañcāti sittasammaṭṭhaṃ, santassa bhāvo saccaṃ, akhemaṭṭhena dukkhañca taṃ aviparītaṭṭhena saccañcāti dukkhasaccaṃ iccādi.</p>\r\n<p class="bodytext"><span class="bld">upamānuttarapado</span> yathā? sīho viyāti sīho, muni ca so sīho cāti munisīho. evaṃ munipuṅgavo, buddhanāgo, buddhādicco, raṃsi viyāti raṃsi, saddhammo ca so raṃsi cāti saddhammaraṃsi. evaṃ vinayasāgaro, samaṇapadumo, samaṇapuṇḍarīko iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54642,'annya_sadda_10',235,replace(replace('\r\n<p class="bodytext"><span class="bld">sambhāvanāpubbapado</span> yathā? hetu hutvā paccayo hetupaccayo. evaṃ ārammaṇapaccayo, manussabhūto, devabhūto, dhammo iti saṅkhāto dhammasaṅkhāto, dhammasammato, dhammasaññito<a name="M0.0235"></a>, dhammalakkhito, eva iti saṅkhāto saddo evasaddo. evaṃ casaddo, vāsaddo, ariyabhūto saṅgho ariyasaṅgho. evaṃ buddhamuni, paccekamuni iccādi. ettha ca sambhāvanā nāma sāmaññabhūtassa uttarapadatthassa daḷhaṃ katvā thomanā sarūpavisesadīpanā, na guṇamattadīpanāti adhippāyo. garū pana "dhammo iti buddhi dhammabuddhi. evaṃ dhammasaññā, aniccasaññā, dhātusaññā, mātusaññā, pāṇasaññitā, attadiṭṭhi" iccādīnipi ettha āharanti, imāni pana "saraṇaṃ iti gato upagato saraṇaṅgato"ti padaṃ viya itiluttāni paṭhamātappurisapadāni nāma yujjantīti <span class="note">[rū. 343; nī. 702]</span>.</p>\r\n<p class="bodytext"><span class="bld">avadhāraṇapubbapado</span> yathā? guṇo eva dhanaṃ na maṇisuvaṇṇādīti guṇadhanaṃ. evaṃ saddhādhanaṃ, sīladhanaṃ, cakkhu eva dvāraṃ na gāmadvārādīti cakkhudvāraṃ. evaṃ cakkhuvatthu, cakkhundriyaṃ, cakkhāyatanaṃ, cakkhudhātu, khandhā eva bhārā khandhabhārā. ettha ca yadi bharitabbaṭṭhena bhārā nāma siyuṃ, pañcakkhandhā eva bhārā nāma siyuṃ, na sīsabhāra, aṃsabhārādayo. khandhā hi niccabhārā honti, itare tāvakālikā, khandhamūlikā cāti. evaṃ atisayatthasambhāvanatthaṃ khandhā eva bhārāti avadhāraṇavākyaṃ payujjati, na sīsabhārādīnaṃ sabbaso bhārabhāvapaṭikkhipanatthanti. evaṃ sabbattha, avijjā eva malaṃ na kaṃsamalādikanti avijjāmalaṃ, avijjā eva āsavo na madhvāsavādikoti avijjāsavo. evaṃ taṇhāsallaṃ, paññāsatthaṃ, paññāloko, paññāpajjoto, rāgaggi, dosaggi, mohaggi iccādi. garū pana "dhanaṃ viyāti dhanaṃ, saddhā eva ariyānaṃ dhanaṃ saddhādhanaṃ, satthaṃ viyāti satthaṃ, paññā eva satthaṃ paññāsattha"nti yojenti, evaṃ sati atisayasambhāvanattho na sijjhati <span class="note">[rū. 343; nī. 702]</span>.</p>\r\n<p class="bodytext"><a name="para347"></a><span class="paranum">347</span>. <span class="bld">nanipātapubbapade nau</span> <span class="note">[ka. 326; rū. 341; nī. 707]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-347-');
INSERT INTO pages VALUES(54643,'annya_sadda_10',236,replace(replace('\r\n<p class="bodytext">ñānubandho <a name="M0.0236"></a> paṭisedhamhā aññanakāra nivattanattho, nauiccetaṃ syādyantaṃ aññena syādyantena saha ekatthaṃ hoti. iminā nañe katthasaññaṃ katvā –</p>\r\n<p class="bodytext"><a name="para348"></a><span class="paranum">348</span>. <span class="bld">ṭa naussa</span> <span class="note">[ka. 333; rū. 344; nī. 717; caṃ. 2.2.20; pā. 2.2.6; "nau" (bahūsu)]</span>.</p>\r\n<p class="bodytext">uttarapade pare nauiccetassa ṭānubandho a hotīti nassa attaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-348-');
INSERT INTO pages VALUES(54644,'annya_sadda_10',237,replace(replace('\r\n<p class="bodytext">na brāhmaṇo abrāhmaṇo. ettha siyā – kiṃ vijjamānassa vāyaṃ nisedho, udāhu avijjamānassa vāti, kiñcettha – yadi vijjamānassa nisedho, evaṃ sati loke vijjamānā sabbe brāhmaṇā abrāhmaṇā nāma bhaveyyuṃ. tasmā "idha jano na brāhmaṇo, tattha jano na brāhmaṇo"tiādinā desādiniyamaṃ vinā loke vijjamānassa brāhmaṇassa nisedho na yujjati, atha loke avijjamānassa nisedho, evañca sati kiṃ avijjamānassa nisedhena nisedhanīyasseva avijjamānattāti? vuccate – taṃsadisādiatthesu tabbohārassevāyaṃ nisedho. tathā hi brāhmaṇasadise abrāhmaṇe kesañci brāhmaṇasaññā saṇṭhāti, saññānurūpañca brāhmaṇavohāro tasmiṃ pavattati, evaṃ pavattassa abrāhmaṇe brāhmaṇavohārassa ayaṃ paṭisedho hoti. yathā taṃ? lokasmiṃ bālajanānaṃ micchāsaññāvasena micchāvohāro pavattatiyeva "rūpaṃ attā, vedanā attā" iccādi, tesaṃ tassa micchābhāvakhyāpanatthaṃ paṭisedho yojiyati "rūpaṃ anattā, vedanā anattā" <span class="note">[mahāva. 20]</span> iccādi. ettāvatā suddhabrāhmaṇasaddassapi micchāvasena taṃsadise atthe pavattisambhavo siddhā hoti, nakārassa <a name="M0.0237"></a> ca tadatthajotakamattatā siddhā hoti, evañca sati uttarapadatthapadhānatāsaṅkhātaṃ tappurisalakkhaṇampi idha na virujjhati, tasmā abrāhmaṇoti brāhmaṇasadisoti vuttaṃ hoti. esa nayo tadañña, tabbiruddha, tadabhāvatthādīsu.</p>\r\n<p class="bodytext">tattha tadaññatthe –</p>\r\n<p class="bodytext">saṅkhatā dhammā asaṅkhatā dhammā <span class="note">[dha. sa. dukamātikā 8]</span>. ettha ca na saṅkhatā asaṅkhatā, saṅkhatadhammehi aññe dhammāti attho.</p>\r\n<p class="bodytext">tabbiruddhe –</p>\r\n<p class="bodytext">akusalo, kusalapaṭipakkho dhammoti attho.</p>\r\n<p class="bodytext">tadabhāve –</p>\r\n<p class="bodytext">na katvā akatvā, karaṇena sabbaso vināti attho.</p>\r\n<p class="bodytext">duvidho paṭisedho pasajjapaṭisedho, payirudāsapaṭisedho cāti.</p>\r\n<p class="bodytext">tattha attanā yuttapadatthaṃ pasajja laggetvā paṭisedhetīti <span class="bld">pasajjapaṭisedho,</span> tadabhāvamattajotako nakāro, kriyāmattanisedhoti vuttaṃ hoti. akatvā, akātuṃ, akaronto, na karoti, na kātabbaṃ iccādi.</p>\r\n<p class="bodytext">pasajjamatte aṭṭhatvā taṃsadisādike paritobhāge uggayha nisedhetabbaṃ atthaṃ asati khipati chaḍḍetīti <span class="bld">payirudāso,</span> taṃsadisādijotako, dabbanisedhoti vuttaṃ hoti. abrāhmaṇo iccādi. evaṃ asamaṇo, asakyaputtiyo, amitto, mittadhammavidhuroti attho.</p>\r\n<p class="bodytext"><a name="para349"></a><span class="paranum">349</span>. <span class="bld">ana sare</span> <span class="note">[ka. 334; rū. 345; nī. 718; caṃ. 5.2.119; pā. 6.3.105]</span>.</p>\r\n<p class="bodytext">sare pare nauiccetassa ana hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-349-');
INSERT INTO pages VALUES(54645,'annya_sadda_10',238,replace(replace('\r\n<p class="bodytext">na <a name="M0.0238"></a> ariyo anariyo, ariyadhammavimukhoti attho. na āvāso anāvāso, na issaro anissaro. na īti anīti, ‘ītī’ti upaddavo, na yutto upāyo anupāyo, na ūmi anūmi, na yuttā esanā anesanā, na yutto okāso anokāso, na atikkamma anatikkamma, anādāya, anoloketvā iccādi.</p>\r\n<p class="bodytext">bahulādhikārā ayuttatthānampi samāso hoti <span class="note">[ka. 336; rū. 347; nī. 689]</span>, puna na gāyitabbāti apunageyyā, gāthā, candaṃ na ullokentīti acandamullokikāni, mukhāni, sūriyaṃ na passantīti asūriyapassā, rājakaññā, saddhaṃ na bhuñjati sīlenāti asaddhabhojī. evaṃ alavaṇabhojī, atthaṃ na kāmentīti anatthakāmā. evaṃ ahitakāmā, okāsaṃ na kāresīti anokāsaṃkāretvā. evaṃ animittaṃkatvā, mūlamūlaṃ na gacchatīti amūlamūlaṃgantvā iccādi.</p>\r\n<p class="bodytext">"puna gāyitabbāti punageyyā, na punageyyā apunageyyā. atthaṃ kāmentīti atthakāmā, na atthakāmā anatthakāmā. atha vā na attho anattho, anatthaṃ kāmentīti anatthakāmā" iccādinā vākye yojite pana yuttasamāsā honti. garū pana "atthaṃ na kāmenti anatthameva kāmentīti anatthakāmā, hitaṃ na kāmenti ahitameva kāmentīti ahitakāmā, phāsuṃ na kāmenti aphāsumeva kāmentīti aphāsukāmā"ti yojesuṃ, dvādhippāyapadaṃ nāmetaṃ.</p>\r\n<p class="bodytext"><span class="bld">kunipātapubbapade</span> niccasamāsattā aññapadena viggaho, khuddakā nadī kunnadī, khuddako sombho kusombho, khuddakaṃ vanaṃ kubbanaṃ.</p>\r\n<p class="bodytext"><a name="para350"></a><span class="paranum">350</span>. <span class="bld">sare kada kussuttaratthe</span> <span class="note">[ka. 335; rū. 346; nī. 719]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-350-');
INSERT INTO pages VALUES(54646,'annya_sadda_10',239,replace(replace('\r\n<p class="bodytext">sarādike <a name="M0.0239"></a> uttarapade pare uttarapadatthe vattamānassa kunipātassa kadi hoti.</p>\r\n<p class="bodytext">kucchitaṃ annaṃ kadannaṃ, kucchitaṃ asanaṃ kadasanaṃ, kucchito ariyo kadariyo, maccharī.</p>\r\n<p class="bodytext">sareti kiṃ? kuputtā, kudārā, kudāsā.</p>\r\n<p class="bodytext">uttarattheti kiṃ? kucchito oṭṭho yassāti kuoṭṭho.</p>\r\n<p class="bodytext"><a name="para351"></a><span class="paranum">351</span>. <span class="bld">kāppatthe</span> <span class="note">[ka. 336; rū. 347; nī. 720]</span>.</p>\r\n<p class="bodytext">uttarapade pare uttarapadatthe ṭhitassa appatthe vattamānassa kunipātassa kā hoti vā.</p>\r\n<p class="bodytext">appakaṃ lavaṇaṃ kālavaṇaṃ. evaṃ kāpupphaṃ.</p>\r\n<p class="bodytext"><span class="bld">pādipubbapado</span> ca niccasamāsova, pakaṭṭhaṃ vacanaṃ pāvacanaṃ, dīghattaṃ, pakaṭṭhaṃ hutvā nītaṃ paṇītaṃ, pamukhaṃ hutvā dhānaṃ padhānaṃ. evaṃ paṭṭhānaṃ, vividhā mati vimati, adhiko devo adhidevo, atireko viseso vā dhammo abhidhammo, sundaro gandho sugandho, kucchito gandho duggandho. sobhaṇaṃ kataṃ sukataṃ, kucchitaṃ kataṃ dukkaṭaṃ, viparīto patho uppatho. evaṃ ummaggo, uddhammo, ubbinayoiccādi.</p>\r\n<p class="bodytext">ayampi kammadhārayasamāso abhidheyyavacano parapadaliṅgo ca.</p>\r\n<p class="centered">kammadhārayasamāso niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-351-');
INSERT INTO pages VALUES(54647,'annya_sadda_10',240,replace(replace('\r\n<p class="subhead">digusamāsa</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0240"></a> digusaṅkhāto paṭhamātappuriso vuccate.</p>\r\n<p class="bodytext">dve gāvo digu, saṅkhyāpubbattena napuṃsakekattena ca digusaddasadisattā sabbo cāyaṃ samāso <span class="bld">digū</span>ti vuccati.</p>\r\n<p class="bodytext"><a name="para352"></a><span class="paranum">352</span>. <span class="bld">saṅkhyādi</span> <span class="note">[ka. 321; rū. 349; nī. 699]</span>.</p>\r\n<p class="bodytext">samāhārekatthe saṅkhyāpubbakaṃ ekatthaṃ napuṃsakaṃ hoti, samāhāravacaneneva ekattañca siddhaṃ.</p>\r\n<p class="bodytext">dve gāvo digu, ‘gossū’ti suttena ossa uttaṃ, tayo lokā tilokaṃ, tayo lokā ñāṇasmiṃ samāhaṭā sampiṇḍitāti tilokaṃ, tiṇṇaṃ lokānaṃ samāhāroti tilokaṃ, tayo ca te lokā cāti tilokaṃ. evaṃ tibhavaṃ, tipurisaṃ, tīṇi malāni timalaṃ, tiratanaṃ, tisso saññāyotisaññaṃ, ‘syādīsu rasso’ti rassattaṃ. cattāro pathā catuppathaṃ, cattāri saccāni catusaccaṃ, catasso disā catuddisaṃ. evaṃ pañcasikkhāpadaṃ, saḷāyatanaṃ, sattāhaṃ, aṭṭhapadaṃ, navalokuttaraṃ, dasasikkhāpadaṃ, satayojanaṃ, sahassayojanaṃ iccādi.</p>\r\n<p class="bodytext">imasmiṃ <span class="bld">samāhāradigu</span>mhi sabbaṃ napuṃsakameva rassantameva ekavacanantameva cāti.</p>\r\n<p class="bodytext"><span class="bld">asamāhāradigu</span> <span class="note">[rū. 350 nī. 703]</span> yathā? eko puggalo ekapuggalo, tayo bhavā tibhavā, catasso disā catuddisā iccādi.</p>\r\n<p class="bodytext">saṅkhyāṭhāne pana <span class="note">[ka. 392; rū. 418; nī. 831]</span> dve satāni dvisataṃ. evaṃ tisataṃ, catusataṃ, pañcasataṃ, chasataṃ, sattasataṃ, aṭṭhasataṃ, navasataṃ, dasasataṃ, dvisahassaṃ, tisahassaṃ, catusahassaṃ, pañcasahassaṃ, dasasahassaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-352-');
INSERT INTO pages VALUES(54648,'annya_sadda_10',241,replace(replace('\r\n<p class="bodytext">dve <a name="M0.0241"></a> satasahassāni dvisatasahassaṃ. evaṃ "tisatasahassaṃ, catusatasahassaṃ, pañcasatasahassa"nti vā "dvisatāni, dve satāni, tisatāni, tīṇi satāni, catusatāni, cattāri satāni, dvisahassāni, dve sahassāni, tisahassāni, tīṇi sahassāni, dvisatasahassāni, dve satasahassānī"ti vā evaṃ vacanadvayañca vākyañca veditabbaṃ. evaṃ satasahassepīti.</p>\r\n<p class="bodytext">ettha siyā – digu nāma saṅkhyāpubbameva siyā, imesu ca sabbaṃ saṅkhyāpadameva hotīti? digumhi pubbaṃ saṅkhyāpadameva siyā, parapadaṃ pana saṅkhyāpadampi aññampi yujjatīti.</p>\r\n<p class="centered">digusamāso niṭṭhito.</p>\r\n<p class="subhead">bahubbīhisamāsa</p>\r\n<p class="bodytext">atha bahubbīhisamāso vuccate.</p>\r\n<p class="bodytext">bahavo vīhayo yasmiṃ dese soyaṃ bahubbīhi, tādiso gāmo vā deso vā janapado vā, bahubbīhisaddasadisattā sabbo cāyaṃ samāso <span class="bld">bahubbīhī</span>ti vuccati. yathā hi bahubbīhisaddo samāsapadatthe atikkamma gāma, desa, janapadaiccādīnaṃ aññesaṃ padānaṃ atthesu tiṭṭhati, tathā ayaṃ samāsopi. aññapadatthapadhāno hi bahubbīhisamāso.</p>\r\n<p class="bodytext">so saṅkhepena duvidho tagguṇasaṃviññāṇo, atagguṇasaṃviññāṇo cāti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54649,'annya_sadda_10',242,replace(replace('\r\n<p class="bodytext">tattha ‘guṇo’ti appadhānabhūto samāsapadānaṃ attho, so aññapadatthassa visesanabhūtattā tassa aññapadatthassa guṇoti <a name="M0.0242"></a> atthena tagguṇoti vuccati, viññātabboti viññāṇo, aññapadattho, tagguṇaṃ amuñcitvā tagguṇena saheva viññāṇo aññapadattho yasminti tagguṇasaṃviññāṇo, na tagguṇasaṃviññāṇo atagguṇasaṃviññāṇo, yattha samāsapadattho avayavabhāvena vā sahavidheyyabhāvena vā aññapadatthe antogadho hoti, so <span class="bld">tagguṇasaṃviññāṇo</span>. yathā? chinnahattho puriso, buddhappamukhassa bhikkhusaṅghassa bhattaṃ deti, saputtadāro āgato, pādayo upasaggā nāmāti.</p>\r\n<p class="bodytext">ettha ca ‘buddhappamukhassa bhikkhusaṅghassa bhattaṃ detī’ti bhikkhusaṅghassa ca bhattaṃ deti, pamukhabhūtassa buddhassa ca bhattaṃ detīti attho. ‘saputtadāro āgato’ti puttadārā ca āgatā, puriso ca āgatoti attho. ‘pādayo upasaggā nāmā’ti pa-kāro ca upasaggo nāma, parādayo ca upasaggā nāmāti attho. evaṃ yojanārahatā aññapadatthena saha samāsapadatthassa vidheyyatā nāmāti.</p>\r\n<p class="bodytext"><span class="bld">atagguṇasaṃviññāṇo</span> yathā? dinnasuṅko rājā dānaṃ deti, pabbatādīni khettāni kassati iccādi. imesu pana samāsapadattho avidheyyo, aññapadattho eva vidheyyo.</p>\r\n<p class="subhead">paṭhamābahubbīhi</p>\r\n<p class="bodytext">paṭhamābahubbīhi, dutiyābahubbīhi, tatiyābahubbīhi, catutthībahubbīhi, pañcamībahubbīhi, chaṭṭhībahubbīhi, sattamībahubbīhi cāti sattavidho.</p>\r\n<p class="bodytext">tattha <span class="bld">paṭhamābahubbīhi</span> sahapubbapada, upamānapubbapada, saṅkhyobhayapada, disantarāḷattha, byatihāralakkhaṇavasena pañcavidho.</p>\r\n<p class="bodytext">tattha –</p>\r\n<p class="bodytext"><a name="para353"></a><span class="paranum">353</span>. <span class="bld">vānekamaññatthe</span> <span class="note">[ka. 328; rū. 352; nī. 708]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-353-');
INSERT INTO pages VALUES(54650,'annya_sadda_10',243,replace(replace('\r\n<p class="bodytext">anekaṃ <a name="M0.0243"></a> syādyantapadaṃ aññapadassa atthe vikappena ekatthaṃ hoti.</p>\r\n<p class="bodytext">saha vitakkenāti savitakko, vitakkena saha yo vattatīti vā savitakko, samādhi.</p>\r\n<p class="bodytext">ettha ca ‘saha vitakkenā’ti ettha paṭhamāvibhattiyā atthabhūto aññapadattho vākyasāmatthiyena sijjhati. na hi kriyākārakarahitaṃ vākyaṃ nāma sambhavati, iminā suttena sahapada, vitakkapadānaṃ samādhisaṅkhātena aññapadatthena ekatthībhāvo hoti, ekatthībhāve ca honte vākye ṭhitānaṃ aññapadānaṃ vibhattīnañca sabbe atthā ekatthabhūtena samāsena vuttā nāma honti, aññapadāni ca vibhattiyo ca vuttatthā nāma, vuttatthānañca attharahitattā payogakiccaṃ natthi, tasmā ‘ekatthatāya’nti suttena vibhattīnaṃ lopo, evaṃ sabbasamāsesu vākyedissamānānaṃ ya, ta, eta, ima, iti, eva, iva, viya, ca, vāiccādīnaṃ aññapadānaṃ mahāvuttisuttena lopo, vibhattīnañca lope sati sarantānaṃ byañjanantānañca samāsapadānaṃ sayameva pakatibhāvo, idha pana ‘sahassa soññatthe’ti suttena sahasaddassa sattaṃ, tato syādyuppatti, savitakko samādhi, savitakkā samādhayo, savitakkā paññā, savitakkā paññāyo, savitakkaṃ jhānaṃ, savitakkāni jhānāni iccādinā sabbaliṅga, vibhatti, vacanehi yojetvā payogasiddhi veditabbā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54651,'annya_sadda_10',244,replace(replace('\r\n<p class="bodytext"><span class="bld">upamānapubbapado</span> yathā? kāyabyāmānaṃ samapamāṇattā nigrodho iva parimaṇḍalo nigrodhaparimaṇḍalo, nigrodho iva vā parimaṇḍalo yo hotīti so nigrodhaparimaṇḍalo, rājakumāro, saṅkho iva paṇḍaro saṅkhapaṇḍaro, kāko <a name="M0.0244"></a> iva sūro kākasūro, sattānaṃ paññācakkhupaṭilābhakaraṇena tesaṃ cakkhu viya bhūtoti cakkhubhūto, lokuttaradhammapaṭilābhakaraṇena tesaṃ dhammo viya bhūtoti dhammabhūto, niccasommahadayatāya brahmā viya bhūtoti brahmabhūto, andho viya bhūto ayanti andhabhūto iccādi.</p>\r\n<p class="bodytext"><span class="bld">saṅkhyobhayapado</span> yathā? dve vā tayo vā pattā dvittippattā, idha vāsaddāyeva aññapadāni nāma, aniyamabhūto tesaṃ attho aññapadattho nāma. dvīhaṃ vā tīhaṃ vā dvīhatīhaṃ, cha vā pañca vā vācā chappañcavācā. evaṃ sattaṭṭhamāsā, ekayojanadviyojanāni iccādi.</p>\r\n<p class="bodytext"><span class="bld">disantarāḷattho</span> yathā? dakkhiṇassā ca pubbassā ca yadantarāḷaṃ hoti sā dakkhiṇapubbā. evaṃ pubbuttarā, pacchimuttarā, aparadakkhiṇā, mahāvuttinā pubbapade rassattaṃ. dakkhiṇā ca sā pubbā cāti dakkhiṇapubbā iccādinā kammadhārayopi yujjati.</p>\r\n<p class="bodytext"><span class="bld">byatihāralakkhaṇe</span> <span class="note">[ka. 328; rū. 352; nī. 708]</span> –</p>\r\n<p class="bodytext"><a name="para354"></a><span class="paranum">354</span>. <span class="bld">tattha gahetvā tena paharitvā yuddhe sarūpaṃ</span>.</p>\r\n<p class="bodytext">sattamyantaṃ tatiyantañca samānarūpaṃ syādyantapadaṃ tattha gahetvā tena paharitvā yuddhe aññapadatthe ekatthaṃ hoti vā.</p>\r\n<p class="bodytext"><a name="para355"></a><span class="paranum">355</span>. <span class="bld">ṅi vītihāre</span> <span class="note">[ka. 404; rū. 370]</span>.</p>\r\n<p class="bodytext">aññapadatthavisaye kriyābyatihāre gamyamāne padante ṅānubandho ipaccayo hoti, ettha ikāro rasso eva.</p>\r\n<p class="bodytext"><a name="para356"></a><span class="paranum">356</span>. <span class="bld">ṅi smiṃca</span> <span class="note">[ka. 403; rū. 354]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-354-355-356-');
INSERT INTO pages VALUES(54652,'annya_sadda_10',245,replace(replace('\r\n<p class="bodytext">vipaccayante <a name="M0.0245"></a> uttarapade pare pubbapadantassa āttaṃ hoti.</p>\r\n<p class="bodytext">kesesu ca kesesu ca gahetvā idaṃ yuddhaṃ pavattatīti kesākesi, daṇḍehi ca daṇḍehi ca paharitvā idaṃ yuddhaṃ pavattatīti daṇḍādaṇḍi. evaṃ muṭṭhāmuṭṭhi, musalāmusali.</p>\r\n<p class="centered">iti paṭhamābahubbīhi.</p>\r\n<p class="subhead">dutiyābahubbīhi</p>\r\n<p class="bodytext">āgatā samaṇā imaṃ saṅghārāmaṃ soyaṃ āgatasamaṇo, saṅghārāmo. ettha ca samāsapadassa attho duvidho vāccattho, abhidheyyattho cāti.</p>\r\n<p class="bodytext">tattha saṅghārāmassa samaṇehi pattabbabhāvasaṅkhātā kammasatti vāccattho nāma, sattimantabhūto saṅghārāmo abhidheyyattho nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54653,'annya_sadda_10',246,replace(replace('\r\n<p class="bodytext">tattha āgatasamaṇasaddo vāccatthameva ujuṃ vadati, na abhidheyyatthaṃ, āgatasamaṇoti sutvā samaṇehi pattabbabhāvamattaṃ jānāti, saṅghārāmadabbaṃ na jānātīti vuttaṃ hoti, tasmā tassaṃ abhidheyyattho aññena saṅghārāmasaddena ācikkhiyati, vāccatthassa pana tena ujuṃ vuttattā puna vattabbābhāvato dutiyāvibhattiyā ācikkhanakiccaṃ natthi, tasmā saṅghārāmapade dutiyāvibhattisambhavo natthi, liṅgatthamattavisayā paṭhamāvibhatti eva pavattati, puna padantarasambandhe sati "saṅghārāmaṃ passati āgatasamaṇaṃ, saṅghārāmena gāmo sobhati āgatasamaṇena, saṅghārāmassa pūjeti āgatasamaṇassā"tiādinā tato sabbā vibhattiyo <a name="M0.0246"></a> pavattanti. esa nayo sabbesu vācakapadesu netabboti.</p>\r\n<p class="bodytext">āgatasamaṇā sāvatthi, āgatasamaṇaṃ jetavanaṃ, āgacchanti samaṇā imanti vā āgatasamaṇo, vihāro. ārūḷhā vānarā imaṃ rukkhanti ārūḷhavānaro, rukkho. sampattā gāmikā yaṃ gāmanti sampattagāmiko. evaṃ paviṭṭhagāmiko iccādi.</p>\r\n<p class="centered">iti dutiyābahubbīhi.</p>\r\n<p class="subhead">tatiyābahubbīhi</p>\r\n<p class="bodytext">jitāni indriyāni yenāti jitindriyo, samaṇo. diṭṭho catusaccadhammo yenāti diṭṭhadhammo. evaṃ pattadhammo, viditadhammo, pariyogāḷhadhammo, katāni catumaggakiccāni yenāti katakicco, bahuvacane sati katāni kiccāni yehi te katakiccā, arahanto. dhammena adhigatā bhogā yenāti dhammādhigatabhogo, puriso. evaṃ adhammādhigatabhogo. evaṃ kattari. karaṇe pana chinno rukkho yenāti chinnarukkho, pharasu iccādi.</p>\r\n<p class="centered">iti tatiyābahubbīhi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54654,'annya_sadda_10',247,replace(replace('\r\n<p class="subhead">catutthībahubbīhi</p>\r\n\r\n<p class="bodytext">dinno suṅko yassa rañño soyaṃ dinnasuṅko, upanītaṃ bhojanaṃ yassāti upanītabhojano, natthi tulo etassāti atulo, ‘ṭa nañssā’ti na-kārassa ṭattaṃ, natthi paṭipuggalo yassāti appaṭipuggalo, natthi sīlaṃ assāti dussīlo, natthi paṭisandhipaññā assāti duppañño, ‘ghapassantassāppadhānassā’ti suttena ghasaññassa āssa <a name="M0.0247"></a> rassattaṃ. natthi sīlaṃ assāti nissīlo, nippañño, apañño, virūpaṃ mukhaṃ assāti dummukho. evaṃ dummano, dubbaṇṇo, natthi attano uttaro adhiko yassāti anuttaro, ‘ana sare’ti suttena nassa ana.</p>\r\n<p class="bodytext">idha bāhiratthabahubbīhi nāma vuccati, sattāhaṃ parinibbutassa assāti sattāhaparinibbuto, aciraṃ parinibbutassa assāti aciraparinibbuto, māso jātassa assāti māsajāto, dvemāsajāto, eko māso abhisittassa assa raññoti ekamāsābhisitto, ekāhaṃ matassa assāti ekāhamataṃ. evaṃ dvīhamataṃ, tīhamataṃ, ekāhaṃ paṭicchannāya assāti ekāhappaṭicchannā. evaṃ dvīhappaṭicchannā, āpatti. yojanaṃ gatassa assāti yojanagato, dviyojanagato iccādi.</p>\r\n<p class="centered">iti catutthībahubbīhi.</p>\r\n<p class="subhead">pañcamībahubbīhi</p>\r\n<p class="bodytext">niggatā janā asmā gāmāti niggatajano, apagataṃ kāḷakaṃ itoti apagatakāḷako, paṭo. apagatakāḷakaṃ, vatthaṃ. apetaṃ viññāṇaṃ yamhāti apetaviññāṇaṃ, matasarīraṃ iccādi.</p>\r\n<p class="centered">iti pañcamībahubbīhi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54655,'annya_sadda_10',248,replace(replace('\r\n<p class="subhead">chaṭṭhībahubbīhi</p>\r\n\r\n<p class="bodytext">chinno hattho yassa soti chinnahattho, hatthacchinno, jāto chando yassāti jātachando, chandajāto, sañjātaṃ pītisomanassaṃ yassāti sañjātapītisomanasso, pītisomanassasañjāto, visuddhaṃ sīlaṃ yassāti visuddhasīlo<a name="M0.0248"></a>, sīlavisuddhā, mahanto kāyo yassāti mahākāyo.</p>\r\n<p class="bodytext">idha upamānapubbapado nāma vuccati, suvaṇṇassa viya vaṇṇo yassāti suvaṇṇavaṇṇo, brahmuno viya saro yassāti brahmassaro, nāgassa viya gati assāti nāgagati. evaṃ sīhagati, nāgavikkamo, sīhavikkamo, sīhassa viya hanu assāti sīhahanu, eṇissa viya jaṅghā yassāti eṇijaṅgho, usabhassa viya assa khandhoti usabhakkhandho iccādi.</p>\r\n<p class="bodytext">rūpaṃ vuccati sabhāvo, yādisaṃ rūpaṃ assāti yathārūpaṃ. evaṃ tathārūpaṃ, evaṃ rūpaṃ assāti evarūpaṃ, bindulopo. evaṃ ādi assāti evamādi. tathā iccādi, iccevamādi, īdisaṃ nāmaṃ yassāti itthannāmo, evaṃnāmo, kīdisaṃ nāmaṃ yassāti kinnāmo, ‘konāmo’ti ettha mahāvuttinā kiṃsaddassa kottaṃ.</p>\r\n<p class="bodytext">ko samudayo yassa dhammassāti kiṃsamudayo, kā jāti yassāti kiṃjātiko, kiṃnidānaṃ yassāti kiṃnidāno, kati vassāni yassāti kativasso, ko attho assāti kimatthaṃ, vacanaṃ. ‘kvattho’ti mahāvuttinā kiṃsaddassa kottaṃ, yādiso attho assāti yadattho, tādiso attho assāti tadattho, ediso attho yassa vinayassāti etadattho, vinayo. etadatthā, vinayakathā. etadatthaṃ, sotāvadhānaṃ iccādi.</p>\r\n<p class="centered">iti chaṭṭhībahubbīhi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54656,'annya_sadda_10',249,replace(replace('\r\n<p class="subhead">sattamībahubbīhi</p>\r\n\r\n<p class="bodytext">sampannāni sassāni yasmiṃ janapade soyaṃ sampannasasso, sulabhā bhikkhā yasmiṃ janapade soyaṃ subhikkho<a name="M0.0249"></a>, dullabhā bhikkhā yasminti dubbhikkho, bahavo gāmā asmiṃ janapadeti bahugāmo. evaṃ bahujano, gāmo. natthi gāmakhettaṃ yasmiṃ araññe tayidaṃ agāmakaṃ, samāsante ko. saṃvijjanti manussā yasmiṃ gāme samanusso, na vijjanti manussā yasmiṃ gāme amanusso iccādi.</p>\r\n<p class="centered">iti sattamībahubbīhi.</p>\r\n<p class="subhead">bhinnādhikaraṇabahubbīhi</p>\r\n<p class="bodytext">bhinnādhikaraṇabahubbīhi nāma vuccati, ekarattiṃ vāso assāti ekarattivāso, samānena janena saddhiṃ vāso assāti samānavāso, ubhato kammato uppannaṃ byañjanadvayaṃ assāti ubhatobyañjano, aluttasamāso. evaṃ kaṇṭhasmiṃ kāḷo assāti kaṇṭhekāḷo, urasmiṃ lomāni assāti urasilomo, yassa hatthe patto atthīti pattahattho. evaṃ asihattho, daṇḍahattho, chattaṃ pāṇimhi assāti chattapāṇi. evaṃ satthapāṇi, daṇḍapāṇi, vajirapāṇi, dāne ajjhāsayo assāti dānajjhāsayo, dānādhimuttiko, buddhesu bhatti assāti buddhabhattiko, buddhe gāravo assāti buddhagāravo, dhammagāravo iccādi.</p>\r\n<p class="subhead">tipadabahubbīhi</p>\r\n<p class="bodytext">tipadabahubbīhi nāma vuccati, parakkamena adhigatā sampadā yehi te parakkamādhigatasampadā, dhammena adhigatā bhogā yehi te dhammādhigatabhogā, oṇīto pattamhā pāṇi yena so oṇītapattapāṇi, sīhassa pubbaddhaṃ viya kāyo assāti sīhapubbaddhakāyo, mattā bahavo mātaṅgā yasmiṃ vaneti mattabahumātaṅgaṃ iccādi.</p>\r\n<p class="centered">bahubbīhisamāso niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54657,'annya_sadda_10',250,replace(replace('\r\n<p class="subhead">dvandasamāsa</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0250"></a> dvandasamāso dīpiyate.</p>\r\n<p class="bodytext">dve ca dve ca padāni dvandā, dve ca dve ca atthā vā dvandā, mahāvuttinā dvinnaṃ dvisaddānaṃ dvandādeso. dvandasaddasadisattā sabbo cāyaṃ samāso <span class="bld">dvando</span>ti vuccati.</p>\r\n<p class="bodytext">atha vā dve avayavā andiyanti bandhiyanti etthāti dvando, yugaḷassetaṃ nāmaṃ "pādadvandaṃ munindassa, vandāmi sirasāmaha"nti ettha viya, idha pana padayugaḷaṃ atthayugaḷañca gayhati. ubhayapadatthapadhāno hi dvando.</p>\r\n<p class="bodytext">ettha siyā – yadi ubhayapadatthappadhāno dvando, evañca sati dvande kathaṃ ekatthībhāvalakkhaṇaṃ siyāti? vuccate – abhinnavidheyyatthattā. vacanapathañhi patvā kattubhāvakammabhāvādiko vidheyyattho eva padānaṃ accantappadhānattho hoti vacanavākyasampattiyā padhānaṅgattā, so ca vidheyyattho dvandepi abhinno eva hoti. tathā hi "sāriputtamoggallānā gacchanti, sāriputtamoggallāne passati" iccādīsu dve atthā ekavibhattiyā visayā hutvā ekakattu, ekakammādibhāvena ekattaṃ gacchanti, evaṃ dvandepi dvinnaṃ tiṇṇaṃ bahunnaṃ vā padānaṃ ekatthībhāvalakkhaṇaṃ labbhatiyevāti.</p>\r\n<p class="bodytext"><a name="para357"></a><span class="paranum">357</span>. <span class="bld">catthe</span> <span class="note">[ka. 329; rū. 357; nī. 709]</span>.</p>\r\n<p class="bodytext">anekaṃ syādyantapadaṃ casaddassa atthe ekatthaṃ hoti vā.</p>\r\n<p class="bodytext">ettha ca samuccayo, anvācayo, itarītarayogo, samāhāroti cattāro casaddatthā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-357-');
INSERT INTO pages VALUES(54658,'annya_sadda_10',251,replace(replace('\r\n<p class="bodytext">tattha <span class="bld">samuccayo</span> yathā? cīvarañca piṇḍapātañca senāsanañca detīti. <span class="bld">anvācayo</span> yathā? dānañca deti, sīlañca rakkhatīti. ime <a name="M0.0251"></a> dve casaddatthā vākyadvande eva labbhanti, na samāsadvande padānaṃ aññamaññaṃ nirapekkhattāti vadanti. taṃ anvācaye yujjati nānākriyāpekkhattā, samuccaye pana "cīvarañca piṇḍapātañca senāsanañca detī"ti vā "cīvarapiṇḍapātasenāsanāni detī"ti vā evaṃ dvidhāpi yojetuṃ yujjatiyeva "lābhī hoti cīvarapiṇḍapātasenāsanagilānapaccayabhesajjaparikkhārāna"nti <span class="note">[ma. ni. 1.65]</span> pāḷidassanato. anvācayopi vā samāsadvande no na labbhati "mālāgandhavilepanadhāraṇamaṇḍanavibhūsanaṭṭhānā"ti <span class="note">[dī. ni. 1.10]</span> pāḷidassanato. evaṃ pana yujjeyya – casaddattho ekakriya, nānākriyāpekkhanabhedena duvidho hoti samuccayo, anvācayo cāti, tesu ca ekeko avayavappadhāna, samudāyappadhānabhedena duvidho hoti itarītarayogo, samāhāro cāti. tattha itarītarayoge avayavappadhānattā sabbavibhattīsu bahuvacanameva yujjati.</p>\r\n<p class="bodytext">dvande paṇītataraṃ pubbe nipatati. sāriputto ca moggallāno ca sāriputtamoggallānā, sāriputtamoggallāne, sāriputtamoggallānehi iccādi. evaṃ samaṇabrāhmaṇā, brāhmaṇagahapatikā, khattiyabrāhmaṇā, devamanussā, candimasūriyā.</p>\r\n<p class="bodytext">appakkhara, bahvakkharesu appakkharaṃ kvaci pubbaṃ hoti, gāmanigamā, gāmajanapadā iccādi.</p>\r\n<p class="bodytext">kvaci ivaṇṇu’vaṇṇantā pubbe honti, aggi ca dhūmo ca aggidhūmā, rattidivā, dhātuliṅgāni.</p>\r\n<p class="bodytext">avaṇṇantesu sarādipadaṃ pubbaṃ hoti, attho ca dhammo ca atthadhammā, dhammatthā vā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54659,'annya_sadda_10',252,replace(replace('\r\n<p class="bodytext">ayañca <a name="M0.0252"></a> niyamo dvipadadvandesu yebhuyyena labbhati, bahupadadvandesu na labbhati.</p>\r\n<p class="bodytext"><span class="bld">samāhāradvande</span> –</p>\r\n<p class="bodytext"><a name="para358"></a><span class="paranum">358</span>. <span class="bld">samāhāre napuṃsakaṃ</span> <span class="note">[ka. 322; rū. 359; nī. 700]</span>.</p>\r\n<p class="bodytext">catthe samāhāre ekatthapadaṃ napuṃsakaṃ hoti, ekavacanantattaṃ pana samāhāravacaneneva siddhaṃ, ayañca samāhāro pāṇyaṅgādīnaṃ dvandesu niccaṃ labbhati, rukkhatiṇādīnaṃ dvandesu vikappena labbhati.</p>\r\n<p class="bodytext">tattha niccaladdhesu tāva pāṇyaṅgadvande –</p>\r\n<p class="bodytext">cakkhu ca sotañca cakkhusotaṃ, mukhañca nāsikā ca mukhanāsikaṃ, ‘syādīsu rasso’ti rassattaṃ. hanu ca gīvā ca hanugīvaṃ. evaṃ kaṇṇanāsaṃ, chavi ca maṃsañca lohitañca chavimaṃsalohitaṃ, nāmañca rūpañca nāmarūpaṃ, jarā ca maraṇañca jarāmaraṇaṃ. bahulādhikārā kvaci vikapparūpampi dissati, hatthā ca pādā ca hatthapādaṃ, hatthapādā vā iccādi.</p>\r\n<p class="bodytext">tūriyaṅgadvande –</p>\r\n<p class="bodytext">naccañca gītañca vāditañca naccagītavāditaṃ. evaṃ sammatāḷaṃ, ‘samma’nti kaṃsatāḷaṃ, ‘tāḷa’nti hatthatāḷaṃ, saṅkho ca paṇavo ca ḍiṇḍimo ca saṅkhapaṇavaḍiṇḍimaṃ iccādi.</p>\r\n<p class="bodytext">yoggaṅgadvande –</p>\r\n<p class="bodytext">phālo ca pācanañca phālapācanaṃ, yugañca naṅgalañca yuganaṅgalaṃ iccādi.</p>\r\n<p class="bodytext">senaṅgadvande –</p>\r\n','\r',char(13)),'\n',char(10)),'-358-');
INSERT INTO pages VALUES(54660,'annya_sadda_10',253,replace(replace('\r\n<p class="bodytext">hatthino <a name="M0.0253"></a> ca assā ca hatthiassaṃ. evaṃ rathapattikaṃ, asi ca cammañca asicammaṃ. ‘camma’nti saraparittāṇaphalakaṃ, dhanu ca kalāpo ca dhanukalāpaṃ iccādi.</p>\r\n<p class="bodytext">khuddakapāṇadvande –</p>\r\n<p class="bodytext">ḍaṃsā ca makasā ca ḍaṃsamakasaṃ. evaṃ kunthakipillikaṃ <span class="note">[su. ni. 607]</span>, kīṭapaṭaṅgaṃ iccādi.</p>\r\n<p class="bodytext">niccaveridvande –</p>\r\n<p class="bodytext">ahi ca nakulo ca ahinakulaṃ, biḷāro ca mūsikā ca biḷāramūsikaṃ, rassattaṃ. kākolūkaṃ, sappamaṇḍūkaṃ, nāgasupaṇṇaṃ iccādi.</p>\r\n<p class="bodytext">sabhāgadvande –</p>\r\n<p class="bodytext">sīlañca paññāṇañca sīlapaññāṇaṃ, samatho ca vipassanā ca samathavipassanaṃ, vijjā ca caraṇañca vijjācaraṇaṃ. evaṃ satisampajaññaṃ, hiriottappaṃ, uddhaccakukkuccaṃ, thinamiddhaṃ iccādi.</p>\r\n<p class="bodytext">vividhaviruddhadvande –</p>\r\n<p class="bodytext">kusalākusalaṃ, sāvajjānavajjaṃ, kaṇhasukkaṃ, hīnapaṇītaṃ, chekabālaṃ iccādi.</p>\r\n<p class="bodytext">ekasaṅgītidvande –</p>\r\n<p class="bodytext">dīgho ca majjhimo ca dīghamajjhimaṃ, aṅguttarasaṃyuttakaṃ, khandhakavibhaṅgaṃ iccādi.</p>\r\n<p class="bodytext">saṅkhyāparimāṇadvande –</p>\r\n<p class="bodytext">ekakadukaṃ, dukatikaṃ, tikacatukkaṃ, catukkapañcakaṃ iccādi.</p>\r\n<p class="bodytext">pacanacaṇḍāladvande –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54661,'annya_sadda_10',254,replace(replace('\r\n<p class="bodytext">orabbhikā <a name="M0.0254"></a> ca sūkarikā ca orabbhikasūkarikaṃ. evaṃ sākuṇikamāgavikaṃ, sapākacaṇḍālaṃ, venarathakāraṃ, pukkusachavaḍāhakaṃ iccādi.</p>\r\n<p class="bodytext">liṅgavisabhāgadvande –</p>\r\n<p class="bodytext">itthipumaṃ, dāsidāsaṃ iccādi.</p>\r\n<p class="bodytext">disādvande –</p>\r\n<p class="bodytext">pubbā ca aparā ca pubbāparaṃ. evaṃ dakkhiṇuttaraṃ, pubbadakkhiṇaṃ, pubbuttaraṃ, aparadakkhiṇaṃ, aparuttaraṃ.</p>\r\n<p class="bodytext">nadīdvande –</p>\r\n<p class="bodytext">gaṅgāyamunaṃ, mahisarabhu, sabbattha napuṃsakattā ante dīghānaṃ rassattaṃ sattasu vibhattīsu ekavacanantañca.</p>\r\n<p class="centered">iti niccasamāhārarāsi.</p>\r\n<p class="bodytext">vikappaladdhesu <span class="note">[ka. 323; rū. 360; nī. 701]</span> tiṇavisesadvande –</p>\r\n<p class="bodytext">usīrāni ca bīraṇāni ca usīrabīraṇaṃ, usīrabīraṇā. evaṃ muñjapabbajaṃ, muñjapabbajā, kāsakusaṃ, kāsakusā.</p>\r\n<p class="bodytext">rukkhavisesadvande –</p>\r\n<p class="bodytext">khadiro ca palāso ca khadirapalāsaṃ, khadirapalāsā, dhavo ca assakaṇṇo ca dhavassakaṇṇaṃ, dhavassakaṇṇā, pilakkhanigrodhaṃ, pilakkhanigrodhā, assatthakapītanaṃ <span class="note">[kapitthanaṃ (katthaci)]</span>, assatthakapītanā, sākasālaṃ, sākasālā.</p>\r\n<p class="bodytext">pasuvisesadvande –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54662,'annya_sadda_10',255,replace(replace('\r\n<p class="bodytext">gajā <a name="M0.0255"></a> ca gavajā ca gajagavajaṃ, gajagavajā, gomahisaṃ, gomahisā, eṇeyyavarāhaṃ, eṇeyyavarāhā, ajeḷakaṃ, ajeḷakā, kukkuṭasūkaraṃ, kukkuṭasūkarā, hatthigavassavaḷavaṃ, hatthigavassavaḷavā.</p>\r\n<p class="bodytext">sakuṇavisesadvande –</p>\r\n<p class="bodytext">haṃsabilavaṃ, haṃsabilavā, kāraṇḍavacakkavākaṃ, kāraṇḍavacakkavākā, bakabalākaṃ, bakabalākā.</p>\r\n<p class="bodytext">dhanadvande –</p>\r\n<p class="bodytext">hiraññasuvaṇṇaṃ, hiraññasuvaṇṇā, maṇi ca saṅkho ca muttā ca veḷuriyañca maṇisaṅkhamuttaveḷuriyaṃ, maṇisaṅkhamuttaveḷuriyā, jātarūparajataṃ, jātarūparajatā.</p>\r\n<p class="bodytext">dhaññadvande –</p>\r\n<p class="bodytext">sāliyavaṃ, sāliyavā, tilamuggamāsaṃ, tilamuggamāsā, nipphāvakulatthaṃ, nipphāvakulatthā.</p>\r\n<p class="bodytext">byañjanānaṃ dvande –</p>\r\n<p class="bodytext">macchamaṃsaṃ, macchamaṃsā, sākasūpaṃ, sākasūpā, gabyamāhisaṃ, gabyamāhisā, eṇeyyavārāhaṃ, eṇeyyavārāhā, migamāyūraṃ, migamāyūrā.</p>\r\n<p class="bodytext">janapadadvande –</p>\r\n<p class="bodytext">kāsikosalaṃ, kāsikosalā, vajjimallaṃ, vajjimallā, cetavaṃsaṃ, cetavaṃsā, majjhañca sūrasenañca majjhasūrasenaṃ, majjhasūrasenā, kurupañcālaṃ, kurupañcālā.</p>\r\n<p class="centered">iti vikappasamāhārarāsi.</p>\r\n<p class="centered">dvandasamāso niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54663,'annya_sadda_10',256,replace(replace('\r\n<p class="subhead">visesavidhāna</p>\r\n\r\n<p class="bodytext">idāni <a name="M0.0256"></a> pubbe vuttāni avuttāni ca chasu samāsesu visesavidhānāni vuccante.</p>\r\n<p class="bodytext"><span class="bld">napuṃsakekattaṃ, samāsantarasso, pumbhāvātideso, samāsante ka, samāsante a, nānādeso, abyayo, saṅkhyā</span>.</p>\r\n<p class="subhead">napuṃsakekattarāsi</p>\r\n<p class="bodytext">tattha sabbo abyayībhāvo napuṃsakaliṅgo eva, samāhārabhūtā digu, dvandā napuṃsakā ca ekattasaṅkhyā ca.</p>\r\n<p class="bodytext"><a name="para359"></a><span class="paranum">359</span>. <span class="bld">kvacekattañca chaṭṭhiyā</span> <span class="note">[ka. 327; rū. 351; nī. 704; caṃ. 2.2.69-73; pā. 2.4.22-25]</span>.</p>\r\n<p class="bodytext">chaṭṭhīsamāse kvaci napuṃsakattaṃ ekattañca hoti.</p>\r\n<p class="bodytext">chāyā, sabhāsvevāyaṃ vidhi, salabhānaṃ chāyā salabhacchāyaṃ <span class="note">[.sabhacchāyaṃ (mūlapāṭhe)]</span>. evaṃ sakaṭacchāyaṃ, gharacchāyaṃ. idha na hoti, rukkhacchāyā, pabbatacchāyā. sabhāsadde amanussasabhāsvevāyaṃ vidhi, brahmūnaṃ sabhā brahmasabhaṃ. evaṃ devasabhaṃ, indasabhaṃ, yakkhasabhaṃ, rakkhasasataṃ. manussasabhāsu natthi, khattiyasabhā, rājasabhā iccādi.</p>\r\n<p class="bodytext">kvacīti kiṃ? rājaparisā.</p>\r\n<p class="centered">iti napuṃsakekattarāsi.</p>\r\n<p class="subhead">samāsantarassarāsi</p>\r\n<p class="bodytext">‘syādīsu rasso’ti suttena abyayībhāva, samāhāradigu, dvandānaṃ kassaci tappurisassa ca syādīsu rasso.</p>\r\n<p class="bodytext">abyayībhāve –</p>\r\n','\r',char(13)),'\n',char(10)),'-359-');
INSERT INTO pages VALUES(54664,'annya_sadda_10',257,replace(replace('\r\n<p class="bodytext">upamaṇikaṃ <a name="M0.0257"></a> adhitthi, upavadhu.</p>\r\n<p class="bodytext">samāhāradigumhi –</p>\r\n<p class="bodytext">catuddisaṃ, dasitthi, dasavadhu.</p>\r\n<p class="bodytext">samāhāradvande –</p>\r\n<p class="bodytext">mukhanāsikaṃ, hanugīvaṃ.</p>\r\n<p class="bodytext">tappurise –</p>\r\n<p class="bodytext">salabhacchāyaṃ, brahmasabhaṃ.</p>\r\n<p class="bodytext"><a name="para360"></a><span class="paranum">360</span>. <span class="bld">ghapassantassāppadhānassa</span> <span class="note">[ka. 403; rū. 354; nī. 858; caṃ. 2.2.86; pā. 1.2.48]</span>.</p>\r\n<p class="bodytext">syādīsu antabhūtassa appadhānabhūtassa ca ghapassa rasso hoti.</p>\r\n<p class="bodytext">bahubbīhimhi –</p>\r\n<p class="bodytext">bahukañño, poso, bahuitthi, kulaṃ, bahuvadhu, kulaṃ.</p>\r\n<p class="bodytext">abyayībhāve –</p>\r\n<p class="bodytext">upamaṇikaṃ, adhitthi, upavadhu.</p>\r\n<p class="bodytext">antassāti kiṃ? saddhādhano, puriso.</p>\r\n<p class="bodytext">appadhānassāti kiṃ? rājakaññā, rājakumārī, brahmabandhū.</p>\r\n<p class="bodytext"><a name="para361"></a><span class="paranum">361</span>. <span class="bld">gossu</span> <span class="note">[ka. 342; rū. 337; nī. 722; caṃ. 2.2.85; pā. 1.2.48]</span>.</p>\r\n<p class="bodytext">syādīsu antabhūtassa appadhānabhūtassa ca gossa u hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-360-361-');
INSERT INTO pages VALUES(54665,'annya_sadda_10',258,replace(replace('\r\n<p class="bodytext">tiṭṭhagu <a name="M0.0258"></a> cittagu.</p>\r\n<p class="bodytext">appadhānassāti kiṃ? rājagavo.</p>\r\n<p class="bodytext">antassāti kiṃ? gokulaṃ.</p>\r\n<p class="centered">iti samāsantarassarāsi.</p>\r\n<p class="subhead">pumbhāvātidesarāsi</p>\r\n<p class="bodytext"><a name="para362"></a><span class="paranum">362</span>. <span class="bld">itthiyaṃ bhāsitapumitthī pumāvekatthe</span> <span class="note">[ka. 331; rū. 353; nī. 714; caṃ. 5.2.29; pā. 6.3.34]</span>.</p>\r\n<p class="bodytext">‘ekatthe’ti tulyādhikaraṇe, itthiyaṃ vattamāne ekatthe uttarapade pare kadāci bhāsitapumo itthiliṅgasaddo pumā iva hoti. caturaṅgamidaṃ vidhānaṃ, pubbapadaṃ itthiliṅgañca bhāsitapumañca siyā, parapadaṃ niyatitthiliṅgañca pubbapadena ekatthañca siyāti.</p>\r\n<p class="bodytext">dīghā jaṅghā yassa so dīghajaṅgho, puriso, dīghajaṅghā, itthī, dīghajaṅghaṃ, kulaṃ.</p>\r\n<p class="bodytext">ettha ca ye saddā katthaci pulliṅgarūpā honti, katthaci itthipaccayayuttā itthiliṅgarūpā, te <span class="bld">bhāsitapumā</span> nāma. dīgho maggo, dīghā ratti, gato puriso, gatā itthī, kumāro, kumārī, brāhmaṇo, brāhmaṇī iccādi.</p>\r\n<p class="bodytext">ye pana itthipaccayayuttā niccaṃ itthiliṅgarūpā honti, te bhāsitapumā nāma na honti, kaññā, paññā, saddhā, nadī, itthī, pathavī iccādi. tathā sabhāvaitthiliṅgāpi niyatapunnapuṃsakaliṅgāpi bhāsitapumā na honti, devatā, ratti, dhenu, vadhū, sakko, devo, brahmā, ratanaṃ, saraṇaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-362-');
INSERT INTO pages VALUES(54666,'annya_sadda_10',259,replace(replace('\r\n<p class="bodytext">idha pana dīghasaddo "dīgho bālāna saṃsāro"ti <span class="note">[dha. pa. 60]</span> ādīsu bhāsitapumo, so visesyaliṅgānugatavasena idha itthipaccayayutto <a name="M0.0259"></a> itthiliṅgasaddo nāma. iminā suttena pumbhāvātidese kate tattha āpaccayo antaradhāyati, ‘ghapassantassāppadhānassā’ti suttena samāsantassa ākārassa rassattaṃ, kumārī bhariyā yassa so kumārabhariyo, īpaccayanivatti. yuvati jāyā yassa so yuvajāyo, tipaccayanivatti. brahmabandhū bhariyā yassa so brahmabandhubhariyo, ūpaccayanivatti.</p>\r\n<p class="bodytext">itthiyanti kiṃ? kumārī ratanaṃ yassa so kumārīratano, puriso, idha parapadaṃ itthiliṅgaṃ na hoti, tasmā pumbhāvātideso na kātabbo, yadi kareyya, kumāro ratanaṃ yassa kumāraratanoti evaṃ aniṭṭhattho bhaveyya.</p>\r\n<p class="bodytext">ekattheti kiṃ? kumārīsu bhatti yassa so kumārībhattiko. evaṃ samaṇībhattiko, brāhmaṇībhattiko, samāsante ko, idha parapadaṃ pubbapadena ekatthaṃ na hoti, tasmā pumbhāvātideso na kātabbo, yadi kareyya, kumāresu bhatti yassa so kumārabhattikoti evaṃ aniṭṭhattho bhaveyya.</p>\r\n<p class="bodytext">itthīti kiṃ? daṭṭhabbaṭṭhena diṭṭhi, gāmaṇikulaṃ diṭṭhi yena so gāmaṇidiṭṭhi, idha gāmaṇisaddo bhāsitapumo hoti, idha pana kulavācakattā napuṃsakaliṅge tiṭṭhati, itthipaccayo natthi, tasmā pumbhāvātidesakiccaṃ natthi.</p>\r\n<p class="bodytext">bhāsitapumoti kiṃ? saddhā pakati yassa so saddhāpakatiko. evaṃ paññāpakatiko, idha pubbapadaṃ niyatitthiliṅgattā bhāsitapumaṃ na hotīti. saddhādhano, paññādhano, saddhādhuro paññādhuro iccatra duvaṅgavekallaṃ hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54667,'annya_sadda_10',260,replace(replace('\r\n<p class="bodytext">kammadhārayamhi <span class="note">[ka. 332; rū. 343; nī. 716]</span> pana ‘ekatthe’ti padaṃ visuṃ ekaṃ aṅgaṃ na hoti anekatthassa idha asambhavato. dīghā ca sā jaṅghā cāti <a name="M0.0260"></a> dīghajaṅghā, kumārī ca sā bhariyā cāti kumārabhariyā. evaṃ khattiyakaññā, brāhmaṇakaññā, yuvati ca sā bhariyā cāti yuvabhariyā, brahmabandhū ca sā bhariyā cāti brahmabandhubhariyā.</p>\r\n<p class="bodytext">itthiyanti kiṃ? kumārī ca sā ratanañcāti kumārīratanaṃ. evaṃ samaṇīpadumaṃ.</p>\r\n<p class="bodytext">itthīti kiṃ? gāmaṇikulañca taṃ diṭṭhi cāti gāmaṇidiṭṭhi.</p>\r\n<p class="bodytext">bhāsitapumoti kiṃ? saddhāpakati, gaṅgānadī, taṇhānadī, pathavīdhātu.</p>\r\n<p class="bodytext">saññāsaddesu pana caturaṅgayuttepi vidhānaṃ na hoti, nandādevī, nandāpokkharaṇī, kāyagatāsati, paṭhamāvibhatti, dutiyāvibhatti, pañcamīvibhatti, chaṭṭhīvibhatti iccādi.</p>\r\n<p class="bodytext"><a name="para363"></a><span class="paranum">363</span>. <span class="bld">kvaci paccaye</span> <span class="note">[ka. 332; rū. 343; nī. 716; caṃ. 5.2.31; pā. 6.3.35]</span>.</p>\r\n<p class="bodytext">paccaye pare kadāci bhāsitapumo itthiliṅgasaddo kvaci pumāva hoti.</p>\r\n<p class="bodytext">byattatarā, byattatamā, ettha ca byattānaṃ itthīnaṃ atisayena byattāti byattatarā, byattatamāti attho. evaṃ paṇḍitatarā, paṇḍitatamā iccādi.</p>\r\n<p class="bodytext"><a name="para364"></a><span class="paranum">364</span>. <span class="bld">sabbādayo vuttimatte</span> <span class="note">[ka. 331; rū. 353; nī. 714; caṃ. 5.2.41; pā. 6.3.35]</span>.</p>\r\n<p class="bodytext">vuttimatte ṭhāne sabbādināmakā sabbanāmasaddā pumāva honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-363-364-');
INSERT INTO pages VALUES(54668,'annya_sadda_10',261,replace(replace('\r\n<p class="bodytext">sā pamukhā yassa so tappamukho. evaṃ tappadhāno, tāya tāhi vā sampayutto taṃsampayutto. sā eva pamukhā tappamukhā. evaṃ tappadhānā, tassā mukhaṃ tammukhaṃ, tassaṃ gāthāyaṃ <a name="M0.0261"></a> tāsu gāthāsu vā tatra, tāya gāthato tāhi vā gāthāhi tato, tassaṃ velāyaṃ tadā iccādi.</p>\r\n<p class="bodytext">ettha ca vutti nāma samāsa, taddhitā’ yādidhātupaccayanta, vibhattipaccayantānaṃ nāmaṃ.</p>\r\n<p class="centered">iti pumbhāvātidesarāsi.</p>\r\n<p class="subhead">samāsantakapaccayarāsi</p>\r\n<p class="bodytext"><a name="para365"></a><span class="paranum">365</span>. <span class="bld">ltitthiyūhi ko</span> <span class="note">[ka. 338; rū. 356; nī. 725; caṃ. 4.4.140; pā. 5.4.152]</span>.</p>\r\n<p class="bodytext">aññapadatthavisaye kattuiccādīhi ltupaccayantehi itthiyaṃ ī, ūkārantehi ca bahulaṃ kapaccayo hoti.</p>\r\n<p class="bodytext">bahavo kattāro yasmiṃ dese so bahukattuko. evaṃ bahuvattuko, bahukā nadiyo yasmiṃ dese so bahunadiko. evaṃ bahuitthiko, gāmo, bahuitthikā, sabhā, bahuitthikaṃ, kulaṃ. evaṃ bahukumārikaṃ, bahubrahmabandhuko.</p>\r\n<p class="bodytext">ettha ca ‘brahmabandhū’ti rassapadaṃ brāhmaṇaṃ vadati, dīghapadaṃ brāhmaṇiṃ vadati, kapaccaye pare dīghānaṃ mahāvuttinā rassattaṃ icchanti.</p>\r\n<p class="bodytext">bahulanti kiṃ? bahukattā, gāmo.</p>\r\n<p class="bodytext"><a name="para366"></a><span class="paranum">366</span>. <span class="bld">vāññato</span> <span class="note">[ka. 338; rū. 356; nī. 725; caṃ. 6.2.72; pā. 5.4.152]</span>.</p>\r\n<p class="bodytext">aññapadatthavisaye ltitthiyūhi aññato avaṇṇivaṇṇuvaṇṇantehi bahulaṃ kapaccayo hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-365-366-');
INSERT INTO pages VALUES(54669,'annya_sadda_10',262,replace(replace('\r\n<p class="bodytext">avaṇṇantamhā <a name="M0.0262"></a> tāva –</p>\r\n<p class="bodytext">agāmakaṃ, araññaṃ, bahugāmako, janapado, sasotako, asotako, salomako, sapakkhako, bahumālako, bahumālo, bahumāyako, bahumāyo.</p>\r\n<p class="bodytext">ivaṇṇantamhā –</p>\r\n<p class="bodytext">sundarā diṭṭhi yassa so sammādiṭṭhiko, sammādiṭṭhi, micchādiṭṭhiko, micchādiṭṭhi, matapatikā, itthī, saddhāpakatiko, paññāpakatiko, bahuhatthiko, bahudaṇḍiko.</p>\r\n<p class="bodytext">uvaṇṇantamhā –</p>\r\n<p class="bodytext">sahetuko, ahetuko, sacakkhuko, acakkhuko, sabhikkhuko, abhikkhuko, dīghāyuko, appāyuko, bahudhenuko, vajo, bahurattaññuko, bhikkhusaṅgho.</p>\r\n<p class="bodytext">itthiliṅge kamhi pare akārassa mahāvuttinā vā ‘adhātussa ke.’ti suttena vā bahulaṃ ikārattaṃ hoti, bahuputtikā, itthī, bahuputtakā vā, ekaputtikā, ekaputtakā iccādi.</p>\r\n<p class="centered">iti samāsantakapaccayarāsi.</p>\r\n<p class="subhead">samāsantaapaccayarāsi</p>\r\n<p class="bodytext"><a name="para367"></a><span class="paranum">367</span>. <span class="bld">samāsanta</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.52; pā. 5.4.68; ‘tva’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">‘samāsanto+a’ iti dvipadamidaṃ, samāsanto hutvā apaccayo hotīti attho. adhikārasuttamidaṃ.</p>\r\n<p class="bodytext"><a name="para368"></a><span class="paranum">368</span>. <span class="bld">pāpādīhi bhūmiyā</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.72; .pe.. 5.4.75; ‘godāvarīnaṃ’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-367-368-');
INSERT INTO pages VALUES(54670,'annya_sadda_10',263,replace(replace('\r\n<p class="bodytext">pāpādīhi <a name="M0.0263"></a> parāya bhūmiyā samāsanto ahoti, ‘bhūmisaddassā’ti vattabbe niyatitthiliṅgadassanatthaṃ ‘bhūmiyā’ti vuttaṃ. evaṃ aññatthapi.</p>\r\n<p class="bodytext">pāpānaṃ bhūmi pāpabhūmaṃ, pāpānaṃ uppattibhūmityattho, jātiyā bhūmi jātibhūmaṃ, satthujātaraṭṭhaṃ. evaṃ pacchābhūmaṃ, majjhimadese pacchābhāgaraṭṭhaṃ.</p>\r\n<p class="bodytext"><a name="para369"></a><span class="paranum">369</span>. <span class="bld">saṅkhyāhi</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.73; pā. 5.4.75]</span>.</p>\r\n<p class="bodytext">saṅkhyāhi parāya bhūmiyā samāsanto a hoti.</p>\r\n<p class="bodytext">dve bhūmiyo etthāti dvibhūmo, dvibhūmako, pāsādo, dvibhūmiko vā, tisso bhūmiyo etesanti vā tīsu bhūmisu pariyāpannāti vā tebhūmakā, dhammā, catubhūmakā, dhammā, tebhūmikā, catubhūmikā vā. digumhi-dve bhūmiyo dvibhūmaṃ, tisso bhūmiyo tibhūmaṃ, catasso bhūmiyo catubhūmaṃ iccādi.</p>\r\n<p class="bodytext"><a name="para370"></a><span class="paranum">370</span>. <span class="bld">nadīgodhāvarīnaṃ</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.73; pā. 5.4.75]</span>.</p>\r\n<p class="bodytext">saṅkhyāhi parāsaṃ nadī, godhāvarīnaṃ samāsanto a hoti.</p>\r\n<p class="bodytext">pañca nadiyo pañcanadaṃ, pañca vā nadiyo yasmiṃ padese so pañcanado, satta godhāvariyo sattagodhāvaraṃ.</p>\r\n<p class="bodytext"><a name="para371"></a><span class="paranum">371</span>. <span class="bld">asaṅkhyehi caṅgulyānaññāsaṅkhyatthesu</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.74; pā. 5.4.86]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-369-370-371-');
INSERT INTO pages VALUES(54671,'annya_sadda_10',264,replace(replace('\r\n<p class="bodytext">aññattho ca asaṅkhyattho ca aññāsaṅkhyatthā. tattha ‘aññattho’ti aññapadattho bahubbīhisamāso, ‘asaṅkhyattho’ti asaṅkhyatthasamāso <a name="M0.0264"></a> abyayībhāvasamāsoti vuttaṃ hoti, na aññāsaṅkhyatthā anaññāsaṅkhyatthā, aññāsaṅkhyatthavajjitesu samāsesu asaṅkhyehi upasaggehi ca saṅkhyāhi ca parāya aṅguliyā samāsanto a hoti. casaddena "sugataṅgulena, pamāṇaṅgulena" iccādīni sijjhanti.</p>\r\n<p class="bodytext">aṅgulīhi niggataṃ niraṅgulaṃ, aṅguliyo atikkantaṃ accaṅgulaṃ, ime dve amādisamāsā, dve aṅguliyo samāhaṭāti dvaṅgulaṃ.</p>\r\n<p class="bodytext">anaññāsaṅkhyatthesūti kiṃ? pañca aṅguliyo asminti pañcaṅguli, hattho. aṅguliyā samīpaṃ upaṅguli. "caturaṅgulaṃ kaṇṇaṃ osāretvā <span class="note">[mahāva. 66]</span>, aṭṭhaṅgulaṃ dantakaṭṭhaṃ, dvaṅgulaparamaṃ, caturaṅgulaparamaṃ, aṭṭhaṅgulaparama"ntiādīsu ‘aṅgula’nti akārantaṃ pamāṇavācīsaddantaraṃ.</p>\r\n<p class="bodytext"><a name="para372"></a><span class="paranum">372</span>. <span class="bld">dārumhaṅgulyā</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.97; pā. 5.4.114; ‘dārumyaṅgulyā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">dārusaṅkhāte aññapadatthe pavattāya aṅguliyā samāsanto ahoti.</p>\r\n<p class="bodytext">pañca aṅguliyo assāti pañcaṅgulaṃ, dāru. ettha ca aṅgulipamāṇāvayavo dhaññādīnaṃ mānaviseso ‘dārū’ti vuccati.</p>\r\n<p class="bodytext"><a name="para373"></a><span class="paranum">373</span>. <span class="bld">dīghāhovassekadesehi ca ratyā</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.75; pā. 5.4.87]</span>.</p>\r\n<p class="bodytext">dīgho ca aho ca vasso ca ekadeso cāti dvando, ekadeso nāma pubba, parādi, anaññāsaṅkhyatthesu dīghādīhi ca asaṅkhyehi ca saṅkhyāhi ca parāya rattiyā samāsanto a hoti. casaddena "ciraratta"nti sijjhati.</p>\r\n','\r',char(13)),'\n',char(10)),'-372-373-');
INSERT INTO pages VALUES(54672,'annya_sadda_10',265,replace(replace('\r\n<p class="bodytext">dīghā <a name="M0.0265"></a> rattiyo dīgharattaṃ, dīghā rattidivaparaṃparātyattho. aho ca ratti ca ahorattaṃ, vassena temitā ratti vassarattaṃ, rattiyā pubbaṃ pubbarattaṃ, rattiyā paraṃ pararattaṃ, rattiyā aḍḍhaṃ aḍḍharattaṃ, rattiṃ atikkanto atiratto, dve rattiyo dvirattaṃ. evaṃ tirattaṃ, caturattaṃ, pañcarattaṃ, chārattaṃ, vādhikārattā "ekarattaṃ, ekarattī"ti sijjhati.</p>\r\n<p class="bodytext">anaññāsaṅkhyatthesūti kiṃ? dīghā ratti etthāti dīgharatti, hemanto. rattiyā samīpaṃ uparatti.</p>\r\n<p class="bodytext"><a name="para374"></a><span class="paranum">374</span>. <span class="bld">go tvacatthe cālope</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.77; pā. 5.4.92]</span>.</p>\r\n<p class="bodytext">acatthe ca anaññā’saṅkhyatthesu ca pavattā gosaddamhā alopaṭṭhāne samāsanto a hoti.</p>\r\n<p class="bodytext">rañño go rājagavo, attano go sagavo, paresaṃ go paragavo, pañcagavo, pañcagavaṃ. evaṃ dasagavaṃ.</p>\r\n<p class="bodytext">alopeti kiṃ? pañcahi gohi kīto pañcagu. ettha ca tena kītoti etasmiṃ atthe taddhitapaccayassa lopo, tena ayaṃ apaccayo na hoti, ‘gossū’ti ossuttaṃ.</p>\r\n<p class="bodytext">acattheti kiṃ? gavajā ca gāvo ca gavajagavo, yomhi gossa gavattaṃ.</p>\r\n<p class="bodytext">anaññāsaṅkhyatthesūti kiṃ? cittagu, upagu.</p>\r\n<p class="bodytext"><a name="para375"></a><span class="paranum">375</span>. <span class="bld">rattidiva dāragava caturassā</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.62; pā. 5.4.77]</span>.</p>\r\n<p class="bodytext">ete tayo saddā aantā nipaccante.</p>\r\n<p class="bodytext">ratti ca divā ca rattidivaṃ, dārā ca gāvo ca dāragavaṃ, catasso assiyo yassa so caturasso, apaccayo, assissa issa attaṃ.</p>\r\n<p class="bodytext"><a name="para376"></a><span class="paranum">376</span>. <span class="bld">āyāmenugavaṃ</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.69; pā. 5.4.83]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-374-375-376-');
INSERT INTO pages VALUES(54673,'annya_sadda_10',266,replace(replace('\r\n<p class="bodytext">āyāme <a name="M0.0266"></a> gamyamāne anugavanti nipaccate.</p>\r\n<p class="bodytext">gohi anuṭṭhitaṃ sakaṭaṃ anugavaṃ.</p>\r\n<p class="bodytext">āyāmeti kiṃ? gunnaṃ pacchā anugu.</p>\r\n<p class="bodytext"><a name="para377"></a><span class="paranum">377</span>. <span class="bld">akkhismāññatthe</span> <span class="note">[ka. 337; rū. 350; nī. 722; caṃ. 4.4.96; pā. 5.4.113]</span>.</p>\r\n<p class="bodytext">aññapadatthe pavattā akkhimhā samāsanto a hoti.</p>\r\n<p class="bodytext">visālāni akkhīni yassa so visālakkho, virūpāni akkhīni yassa so virūpakkho, anekasahassāni akkhīni yassa so sahassakkho, lohitāni akkhīni yassa so lohitakkho. evaṃ nīlakkho, nīlakkhi vā. bahulādhikārā anaññatthepi, akkhīnaṃ paṭimukhaṃ paccakkhaṃ, akkhīnaṃ parabhāgo parokkhaṃ, akkhīnaṃ tirobhāgo tirokkhaṃ. akkhisaddena cettha pañcindriyāni gayhanti.</p>\r\n<p class="bodytext">mahāvuttinā kvaci samāsanto a, ā, ipaccayā honti.</p>\r\n<p class="bodytext">tattha apaccaye –</p>\r\n','\r',char(13)),'\n',char(10)),'-377-');
INSERT INTO pages VALUES(54674,'annya_sadda_10',267,replace(replace('\r\n<p class="bodytext">vāyuno sakhā vāyusakho, vāyusaṅkhāto sakhā assāti vā vāyusakho, aggi, sabbesaṃ piyā’piyamajjhattānaṃ sakhāti sabbasakho, sabbe vā sakhāyo assāti sabbasakho, mettāvihārī. "sabbamitto sabbasakho"ti <span class="note">[theragā. 648]</span> pāḷi. pāpānaṃ sakhāti pāpasakho, pāpā sakhāro yassāti vā pāpasakho. "pāpamitto pāpasakho"ti <span class="note">[dī. ni. 3.253]</span> pāḷi. pahito pesito attā yenāti pahitatto, majjhimo attā sabhāvo yassāti majjhatto, chātaṃ ajjhattasantānaṃ <span class="note">[saṇṭhānaṃ (mūlapāṭhe)]</span> yassāti chātajjhatto, suhito attā yassāti suhitatto<a name="M0.0267"></a>, yato saṃyato attā yassāti yatatto, ṭhito attā assāti ṭhitattoiccādi.</p>\r\n<p class="bodytext">āpaccaye –</p>\r\n<p class="bodytext">paccakkho dhammo yassāti paccakkhadhammā, chādetīti chado, moho, vivaṭo chado yasminti vivaṭacchadā, sammāsambuddhā.</p>\r\n<p class="bodytext">ipaccaye –</p>\r\n<p class="bodytext">sundaro gandho yassāti sugandhi, kucchito gandho yassāti duggandhi, pūtino gandho yassāti pūtigandhi, surabhino gandho surabhigandhi. "sarīrassa sugandhino, guṇagandhiyutto aha"nti pāḷi.</p>\r\n<p class="centered">iti samāsantaapaccayarāsi.</p>\r\n<p class="subhead">nānādesarāsi</p>\r\n<p class="bodytext"><a name="para378"></a><span class="paranum">378</span>. <span class="bld">uttarapade</span> <span class="note">[ka. 333-334; rū. 344-345; nī. 717-718]</span>.</p>\r\n<p class="bodytext">uttarapade pare pubbapade vidhi hotīti attho. adhikārasuttamidaṃ.</p>\r\n<p class="bodytext">"abrāhmaṇo, anariyo, abhikkhuko, ananto"iccādīsu samāse uttarapade pare na-kārassa a, ana honti.</p>\r\n<p class="bodytext"><a name="para379"></a><span class="paranum">379</span>. <span class="bld">nakhādayo</span> <span class="note">[ka. 328; rū. 352; nī. 708; caṃ. 5.2.95; pā. 6.3.75]</span>.</p>\r\n<p class="bodytext">nakhādayo saddā napakatikā sijjhanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-378-379-');
INSERT INTO pages VALUES(54675,'annya_sadda_10',268,replace(replace('\r\n<p class="bodytext">nā’ssa khamatthīti nakho, ‘kha’nti sukhaṃ, dukkhañca, nā’ssa kulamatthīti nakulo, evaṃnāmako brāhmaṇo, pumassa sakaṃ puṃsakaṃ <a name="M0.0268"></a> natthi puṃsakaṃ etasminti napuṃsako, khañjanaṃ vekallagamanaṃ khattaṃ, natthi khattaṃ etassāti nakkhattaṃ, kaṃ vuccati sukhaṃ, tabbiruddhattā akaṃ vuccati dukkhaṃ, natthi akaṃ etasminti nāko, saggo, na muñcatīti namuci, māro. na galati na cavatīti nagaraṃ, gehe vattabbe ‘agāra’nti sijjhati.</p>\r\n<p class="bodytext"><a name="para380"></a><span class="paranum">380</span>. <span class="bld">nago vā pāṇini</span> <span class="note">[ka. 333-334; rū. 344-345; nī. 717-718; caṃ. 5.2.96; pā. 6.3.77; ‘nago vāppāṇini’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">apāṇimhi nagoti sijjhati vā.</p>\r\n<p class="bodytext">na gacchantīti nagā, rukkhā. nagā, pabbatā. agā, rukkhā, agā, pabbatā.</p>\r\n<p class="bodytext">apāṇinīti kiṃ? ago vasalo kiṃ tena. ettha ‘ago’ti duggatajano, ‘vasalo’ti lāmako, ‘kiṃ tenā’ti nindāvacanaṃ, "sītenā"tipi pāṭho. evaṃ neke, aneke, nekāni, anekāni iccādi.</p>\r\n<p class="centered">iti na-rāsi.</p>\r\n<p class="bodytext"><a name="para381"></a><span class="paranum">381</span>. <span class="bld">sahassa soññatthe</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.97; pā. 6.3.82]</span>.</p>\r\n<p class="bodytext">aññapadatthe samāse uttarapade pare sahassa so hoti vā.</p>\r\n<p class="bodytext">puttena saha yo vattatīti saputto, sahaputto.</p>\r\n<p class="bodytext">aññattheti kiṃ? saha katvā, saha yujjhitvā.</p>\r\n<p class="bodytext"><a name="para382"></a><span class="paranum">382</span>. <span class="bld">saññāyaṃ</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.98; pā. 6.3.78]</span>.</p>\r\n<p class="bodytext">saññāyaṃ uttarapade pare sahassa so hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-380-381-382-');
INSERT INTO pages VALUES(54676,'annya_sadda_10',269,replace(replace('\r\n<p class="bodytext">saha <a name="M0.0269"></a> āyattaṃ sāyattaṃ, saha palāsaṃ sapalāsaṃ, agarukārassetaṃ nāmaṃ.</p>\r\n<p class="bodytext"><a name="para383"></a><span class="paranum">383</span>. <span class="bld">apaccakkhe</span> <span class="note">[ka. 404; rū. 307; nī. 859; caṃ. 5.2.99; pā. 6.3.80]</span>.</p>\r\n<p class="bodytext">apaccakkhe gamyamāne uttarapade pare sahassa so hoti.</p>\r\n<p class="bodytext">oḍḍiyati etāyāti oḍḍi, pāso. oḍḍiyā saha yo vattatīti soḍḍi, kapoto. idha ‘oḍḍi’ apaccakkhā. "sāggi kapoto"tipi pāṭho, picunā saha vattatīti sapicukā, vātamaṇḍalikā, sā ca apaccakkhā, uggantvā ākāse paribbhamantaṃ picusaṅghāṭaṃ disvā ñātabbā. "sapisācā vātamaṇḍalikā"tipi pāṭho. evaṃ sarajā, vātā, sarakkhasī, ratti.</p>\r\n<p class="bodytext"><a name="para384"></a><span class="paranum">384</span>. <span class="bld">akāle sakatthassa</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.110; pā. 6.3.81]</span>.</p>\r\n<p class="bodytext">sakatthappadhānassa sahasaddassa so hoti akāle uttarapade pare.</p>\r\n<p class="bodytext">sabrahmaṃ, sacakkaṃ.</p>\r\n<p class="bodytext">akāleti kiṃ? saha pubbaṇhaṃ, saha paraṇhaṃ, sunakkhattena saha pavattaṃ pubbaṇhaṃ, paraṇhanti attho.</p>\r\n<p class="bodytext"><a name="para385"></a><span class="paranum">385</span>. <span class="bld">ganthantādhikye</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.101; pā. 6.3.79]</span>.</p>\r\n<p class="bodytext">ganthassa anto ganthanto. yathā taṃ kaccāyanaganthassa anto uṇādikappo, adhikabhāvo ādhikyaṃ, ganthante ca ādhikye ca vattamānassa sahasaddassa so hoti uttarapade pare.</p>\r\n','\r',char(13)),'\n',char(10)),'-383-384-385-');
INSERT INTO pages VALUES(54677,'annya_sadda_10',270,replace(replace('\r\n<p class="bodytext">saha <a name="M0.0270"></a> uṇādinā’ yaṃ adhiyateti taṃ soṇādi, sakalaṃ kaccāyanaṃ adhītetyattho. saha muhuttena sakalaṃ jotiṃ adhīte samuhuttaṃ, jotīti nakkhattasatthaṃ.</p>\r\n<p class="bodytext">ādhikye – sadoṇā, khārī, sakahāpaṇaṃ, nikkhaṃ, samāsakaṃ, kahāpaṇaṃ. niccatthamidaṃ suttaṃ.</p>\r\n<p class="bodytext"><a name="para386"></a><span class="paranum">386</span>. <span class="bld">samānassa pakkhādīsu vā</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.103-4; pā. 6.3.84-86]</span>.</p>\r\n<p class="bodytext">pakkhādīsu uttarapadesu samānassa so hoti vā.</p>\r\n<p class="bodytext">samāno pakkho sahāyo sapakkho, samāno pakkho yassāti vā sapakkho, samānapakkho vā. evaṃ sajāti, samānajāti, sajanapado, saratti.</p>\r\n<p class="bodytext">samāno pati yassā sā sapati. evaṃ sanābhi, sabandhu, sabrahmacārī, sanāmo. avhayaṃ vuccati nāmaṃ, candena samānaṃ avhayaṃ yassa so candasavhayo, sagotto, indena samānaṃ gottaṃ yassa so indasagotto, sarūpaṃ, saṭṭhānaṃ. hari vuccati suvaṇṇaṃ, harinā samāno vaṇṇo yassa so harissavaṇṇo, sassa dvittaṃ. evaṃ siṅgīnikkhasavaṇṇo, savayo, sadhano, sadhammo, sajātiyo.</p>\r\n<p class="bodytext">pakkhādīsūti kiṃ? samānasīlo.</p>\r\n<p class="bodytext"><a name="para387"></a><span class="paranum">387</span>. <span class="bld">udare iye</span> <span class="note">[ka. 404; rū. 370; nī. 859; caṃ. 5.2.105; pā. 6.3.88]</span>.</p>\r\n<p class="bodytext">iyayutte udare pare samānassa so hoti vā.</p>\r\n<p class="bodytext">samāne udare jāto sodariyo, samānodariyo.</p>\r\n<p class="bodytext">iyeti kiṃ? samānodaratā.</p>\r\n','\r',char(13)),'\n',char(10)),'-386-387-');
INSERT INTO pages VALUES(54678,'annya_sadda_10',271,replace(replace('\r\n<p class="bodytext">aññesupi <a name="M0.0271"></a> samānassa so hoti, candena samānā sirī yassa taṃ candassasirīkaṃ, mukhaṃ. evaṃ padumassasirīkaṃ, vadanaṃ.</p>\r\n<p class="bodytext">mahāvuttinā santādīnañca so hoti, saṃvijjati lomaṃ assāti salomako. evaṃ sapakkhako, saṃvijjanti āsavā etesanti sāsavā, saṃvijjanti paccayā etesanti sappaccayā, saṃvijjanti attano uttaritarā dhammā etesanti sauttarā, santo puriso sappuriso. tathā sajjano, saddhammo, santassa bhāvo sabbhāvo iccādi.</p>\r\n<p class="centered">iti sa-rāsi.</p>\r\n<p class="bodytext"><a name="para388"></a><span class="paranum">388</span>. <span class="bld">imassidaṃ</span> <span class="note">[ka. 129; rū. 222; nī. 305]</span>.</p>\r\n<p class="bodytext">uttarapade pare imassa idaṃ hoti.</p>\r\n<p class="bodytext">ayaṃ attho etassāti idamatthī, samāsante ī, idamatthino bhāvo idamatthitā. ayaṃ paccayo etesanti idappaccayā, idappaccayānaṃ bhāvo idappaccayatā. "imesaṃ paccayā idappaccayā, idappaccayā eva idappaccayatā"tipi yojenti. ‘ida’nti nipātapadampi atthi, "rūpañca hidaṃ bhikkhave attā abhavissā, vedanā ca hidaṃ. saññā ca hidaṃ. saṅkhārā ca hidaṃ bhikkhave attā abhavissaṃsu" iccādi <span class="note">[mahāva. 20]</span>.</p>\r\n<p class="bodytext"><a name="para389"></a><span class="paranum">389</span>. <span class="bld">puṃ pumassa vā</span> <span class="note">[ka. 82; rū. 149]</span>.</p>\r\n<p class="bodytext">uttarapade pare pumassa puṃ hoti vā.</p>\r\n<p class="bodytext">pumassa liṅgaṃ pulliṅgaṃ, pumassa bhāvo pumbhāvo, pumā ca so kokilo cāti puṅkokilo, pumā ca so go cāti puṅgavo, ‘go tvacatthe.’ti apaccayo, napuṃsako.</p>\r\n<p class="bodytext">vāti kiṃ? pumitthī.</p>\r\n<p class="bodytext"><a name="para390"></a><span class="paranum">390</span>. <span class="bld">ṭa ntantūnaṃ</span> <span class="note">[ka. 126; rū. 101; nī. 301]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-388-389-390-');
INSERT INTO pages VALUES(54679,'annya_sadda_10',272,replace(replace('\r\n<p class="bodytext">uttarapade <a name="M0.0272"></a> pare nta, ntūnaṃ ṭa hoti vā kvaci.</p>\r\n<p class="bodytext">bhavaṃ patiṭṭho yesaṃ te bhavaṃpatiṭṭhā, bindāgamo. bhagavā mūlaṃ yesaṃ te bhagavaṃmūlakā, dhammā. evaṃ bhagavaṃpaṭisaraṇā, dhammā.</p>\r\n<p class="bodytext">bahulādhikārā tarādīsu ca paresu, mahantīnaṃ atisayena mahāti mahattarī, rattaññūnaṃ mahantassa bhāvo rattaññumahattaṃ. evaṃ jātimahattaṃ, guṇamahattaṃ, puññamahattaṃ, arahantassa bhāvo arahattaṃ.</p>\r\n<p class="bodytext"><a name="para391"></a><span class="paranum">391</span>. <span class="bld">a</span> <span class="note">[ka. 642; rū. 589; nī. 1269; caṃ. 5.2.106; pā. 6.3.89]</span>.</p>\r\n<p class="bodytext">uttarapade pare nta, ntūnaṃ a hoti.</p>\r\n<p class="bodytext">bhavantapatiṭṭhā, mayaṃ, guṇavantapatiṭṭhā, mayaṃ.</p>\r\n<p class="bodytext"><a name="para392"></a><span class="paranum">392</span>. <span class="bld">rīrikkhakesu</span> <span class="note">[ka. 642; rū. 589; nī. 1269; caṃ. 5.2.107; pā. 6.3.89-90]</span>.</p>\r\n<p class="bodytext">rī, rikkha, kapaccayantesu paresu samānassa so hoti.</p>\r\n<p class="bodytext">niccasamāsattā aññapadena viggaho, saṃvijjatīti samāno, paccakkhe viya citte upalabbhatīti attho. samāno viya so dissatīti sadī, sadikkho, sadiso, samānā viya te dissantīti sadisā.</p>\r\n<p class="bodytext"><a name="para393"></a><span class="paranum">393</span>. <span class="bld">ntakimimānaṃ ṭākīṭī</span> <span class="note">[ka. 126; rū. 101; nī. 301]</span>.</p>\r\n<p class="bodytext">tesu paresu ntapaccayantassa ca kiṃ, imasaddānañca kamena ṭā, kī, ṭī honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-391-392-393-');
INSERT INTO pages VALUES(54680,'annya_sadda_10',273,replace(replace('\r\n<p class="bodytext">bhavaṃ <a name="M0.0273"></a> viya so dissatīti bhavādī, bhavādikkho, bhavādiso, ko viya so dissatīti kīdī, kīdikkho, kīdiso, ayaṃ viya so dissatīti īdī, īdikkho, īdiso.</p>\r\n<p class="bodytext"><a name="para394"></a><span class="paranum">394</span>. <span class="bld">sabbādīnamā</span> <span class="note">[ka. 642; rū. 589; nī. 1269; caṃ. 5.2.108; pā. 6.3.91]</span>.</p>\r\n<p class="bodytext">tesu paresu sabbādināmakānaṃ ya, ta, eta, añña, amha, tumhasaddānaṃ anto ā hoti.</p>\r\n<p class="bodytext">yādī, yādikkho, yādiso, tādī, tādikkho, tādiso, etādī, etādikkho, etādiso.</p>\r\n<p class="bodytext"><a name="para395"></a><span class="paranum">395</span>. <span class="bld">vetasseṭa</span> <span class="note">[ka. 642; rū. 589; nī. 1269]</span>.</p>\r\n<p class="bodytext">tesu paresu etassa eṭa hoti vā.</p>\r\n<p class="bodytext">edī, edikkho, ediso, aññādī, aññādikkho, aññādiso, amhādī, amhādikkho, amhādiso, tumhādī, tumhādikkho, tumhādiso.</p>\r\n<p class="bodytext"><a name="para396"></a><span class="paranum">396</span>. <span class="bld">tumhamhānaṃ tāmekasmiṃ</span> <span class="note">[ka. 642; rū. 589; nī. 1269]</span>.</p>\r\n<p class="bodytext">tesu paresu ekavacane pavattānaṃ tumha’ mhasaddānaṃ tā, mā honti vā.</p>\r\n<p class="bodytext">ahaṃ viya so dissatīti mādī, mādikkho, mādiso, tvaṃ viya so dissatīti tādī, tādikkho, tādiso.</p>\r\n','\r',char(13)),'\n',char(10)),'-394-395-396-');
INSERT INTO pages VALUES(54681,'annya_sadda_10',274,replace(replace('\r\n<p class="bodytext">ekasminti kiṃ? amhe viya te dissantīti amhādino, amhādikkhā, amhādisā, tumhe viya te dissantīti tumhādino, tumhādikkhā, tumhādisā. ettha ca upamānatthasseva ekattaṃ icchīyati, tasmā ahaṃ viya te dissantīti mādisā <a name="M0.0274"></a> tvaṃ viya te dissantīti tādino, tādisātipi yujjanti. "mādisā ve jinā honti, ye pattā āsavakkhaya"nti <span class="note">[mahāva. 11]</span> pāḷi, imāni padāni upari kitakaṇḍepi āgamissanti.</p>\r\n<p class="bodytext"><a name="para397"></a><span class="paranum">397</span>. <span class="bld">taṃmamaññatra</span> <span class="note">[ka. 143; rū. 235; nī. 322]</span>.</p>\r\n<p class="bodytext">rī, rikkha, kapaccayehi aññasmiṃ uttarapade pare tumha’mhānaṃ taṃ, maṃādesā honti kvaci.</p>\r\n<p class="bodytext">tvaṃ leṇaṃ yesaṃ te taṃleṇā, ahaṃ leṇaṃ yesaṃ te maṃleṇā <span class="note">[saṃ. ni. 4.359]</span>. evaṃ taṃdīpā, maṃdīpā <span class="note">[saṃ. ni. 4.359]</span>, taṃpaṭisaraṇā, maṃpaṭisaraṇā.</p>\r\n<p class="bodytext"><a name="para398"></a><span class="paranum">398</span>. <span class="bld">manādyāpādīnamo maye ca</span> <span class="note">[ka. 183; rū. 386; nī. 375]</span>.</p>\r\n<p class="bodytext">uttarapade mayapaccaye ca pare manādīnaṃ āpādīnañca o hoti.</p>\r\n<p class="bodytext">manoseṭṭhā, manomayā, rajojallaṃ, rajomayaṃ, sabbo manogaṇo idha vattabbo, āpodhātu, āpomayaṃ. anuyanti disodisaṃ <span class="note">[dī. ni. 3.281]</span>, jīva tvaṃ saradosataṃ <span class="note">[jā. 1.2.9]</span>.</p>\r\n<p class="bodytext"><a name="para399"></a><span class="paranum">399</span>. <span class="bld">parassa saṅkhyāsu</span> <span class="note">[ka. 36; rū. 47; nī. 130]</span>.</p>\r\n<p class="bodytext">saṅkhyāsu parāsu parassa o hoti.</p>\r\n<p class="bodytext">parosataṃ, parosahassaṃ, paropaṇṇāsa dhammā, idha parasaddo "indriyaparopariyatta"nti ettha viya adhikatthavācisaddantaraṃ, na sabbanāmaṃ.</p>\r\n<p class="bodytext"><a name="para400"></a><span class="paranum">400</span>. <span class="bld">jane puthassu</span> <span class="note">[ka. 49; rū. 44; nī. 129]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-397-398-399-400-');
INSERT INTO pages VALUES(54682,'annya_sadda_10',275,replace(replace('\r\n<p class="bodytext">jane <a name="M0.0275"></a> pare puthassa u hoti.</p>\r\n<p class="bodytext">ariyehi puthagevāyaṃ janoti puthujjano. api ca pāḷinaye puthusaddoyeva bahulaṃ dissati, puthu kilese janentīti puthujjanā, puthu nānāsatthārānaṃ mukhaṃ ullokentīti puthujjanā, puthu nānāoghehi vuyhantīti puthujjanā <span class="note">[mahāni. 51, 94]</span>, saññānānāttaputhuttapabhedaṃ paṭicca taṇhānānāttaputhuttapabhedo hoti <span class="note">[dha. sa. aṭṭha. 1]</span>, iṅgha aññepi pucchassu, puthū samaṇabrāhmaṇe <span class="note">[saṃ. ni. 1.246]</span>. gāmā gāmaṃ vicarissaṃ, sāvake vinayaṃ puthū, āyatāni puthūni ca, puthunā vijjuvaṇṇinā <span class="note">[jā. 2.22.968]</span>, puthukāyā puthubhūtā iccādi. tasmā "puthageva, puthakkaraṇe" iccādīsu thussa ukārassa akāro <span class="note">[akāroti?]</span> yujjati.</p>\r\n<p class="bodytext"><a name="para401"></a><span class="paranum">401</span>. <span class="bld">so chassāhāyatanesu vā</span> <span class="note">[ka. 374; rū. 408; nī. 804]</span>.</p>\r\n<p class="bodytext">ahe ca āyatane ca pare chassa so hoti vā.</p>\r\n<p class="bodytext">cha ahāni sāhaṃ. atthi sāhassa jīvitaṃ <span class="note">[jā. 2.22.314]</span>, ‘sāhassā’ti sāhaṃ+assāti chedo. saḷāyatanaṃ.</p>\r\n<p class="bodytext">vāti kiṃ? chāhappaṭicchannā āpatti <span class="note">[cūḷava. 134]</span>, cha āyatanāni.</p>\r\n<p class="bodytext"><a name="para402"></a><span class="paranum">402</span>. <span class="bld">ltupitādīnamāraṅraṅa</span> <span class="note">[ka. 200; rū. 159; nī. 415; caṃ. 5.2.20; pā. 6.3.32]</span>.</p>\r\n<p class="bodytext">samāsuttarapade pare ltupaccayantānaṃ pitādīnañca kamena āraṅa, raṅa honti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-401-402-');
INSERT INTO pages VALUES(54683,'annya_sadda_10',276,replace(replace('\r\n<p class="bodytext">satthuno <a name="M0.0276"></a> dassanaṃ satthāradassanaṃ. evaṃ kattāraniddeso. mātā ca pitā ca mātarapitaro, mātāpitūsu saṃvaḍḍho mātāpitarasaṃvaḍḍho.</p>\r\n<p class="bodytext">vāti kiṃ? satthudassanaṃ, kattu niddeso, mātāpitaro.</p>\r\n<p class="bodytext"><a name="para403"></a><span class="paranum">403</span>. <span class="bld">vijjāyonisambandhīnamā tatra catthe</span> <span class="note">[ka. 199; rū. 158; nī. 736; caṃ. 5.2.21; pā. 6.3.25]</span>.</p>\r\n<p class="bodytext">catthasamāse vijjāsambandhīnaṃ yonisambandhīnañca ltupitādīnaṃ ā hoti tesveva paresu.</p>\r\n<p class="bodytext">mātāpitā, mātāpitaro iccādi.</p>\r\n<p class="bodytext">tatrāti kiṃ? mātuyā bhātā mātubhātā.</p>\r\n<p class="bodytext">ettha ca vijjāsippāni sikkhāpentā ācariyā sissānaṃ vijjāsambandhī mātāpitaro nāma.</p>\r\n<p class="bodytext"><a name="para404"></a><span class="paranum">404</span>. <span class="bld">putte</span> <span class="note">[ka. 199; rū. 158; nī. 736; caṃ. 5.2.22; pā. 6.3.25]</span>.</p>\r\n<p class="bodytext">catte putte pare vijjāyonisambandhīnaṃ ltupitādīnaṃ ā hoti.</p>\r\n<p class="bodytext">mātāputtā gacchanti, pitāputtā gacchanti. mahāvuttinā tesañca i hoti, mātipakkho, pitipakkho. mātigho labhate dukhaṃ <span class="note">[jā. 2.19.118]</span>, pitigho labhate dukhaṃ, māttikaṃ dhanaṃ, pettikaṃ dhanaṃ <span class="note">[pārā. 34]</span>. ettha ca mātuyā santakaṃ māttikaṃ, pituno santakaṃ pettikaṃ, dvittaṃ vuddhi ca. mātito, pitito, bhātā eva bhātiko, bhātikarājā.</p>\r\n<p class="bodytext"><a name="para405"></a><span class="paranum">405</span>. <span class="bld">jāyāya jāyaṃ patimhi</span> <span class="note">[ka. 339; rū. 358; nī. 731; ‘.jayaṃ patimhi’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-403-404-405-');
INSERT INTO pages VALUES(54684,'annya_sadda_10',277,replace(replace('\r\n<p class="bodytext">patimhi <a name="M0.0277"></a> pare jāyāsaddassa jāyaṃ hoti.</p>\r\n<p class="bodytext">puttaṃ janetīti jāyā, jāyā ca pati ca jāyampatī <span class="note">[jayampatī (bahūsu)]</span>. atha vā "jāyampatī"ti idaṃ sandhividhināva siddhaṃ, tasmā "jampatī"tipāṭho siyā yathā "devarājā sujampatī’ <span class="note">[saṃ. ni. 1.264]</span>’ti, yathā ca sakkataganthesu "dāro ca pati ca dampatī"ti. idha pana mahāvuttinā patimhi sujātāya sujaṃ hoti, dārassa ca daṃ hoti, tathā jāyā ca pati ca jampatīti niṭṭhaṃ gantabbaṃ.</p>\r\n<p class="bodytext">yañca vuttiyaṃ "jānipatīti pakatyantarena siddhaṃ, tathā dampatī"ti vuttaṃ. tattha puttaṃ janetīti jānī. jānī ca pati ca jānīpatīti yujjati. "tudampatī"ti pāṭho. kaccāyane ca ‘jāyāya tu daṃjāni patimhī’ti. tattha tusaddo padapūraṇamatte yujjati.</p>\r\n<p class="bodytext"><a name="para406"></a><span class="paranum">406</span>. <span class="bld">saññāyamudodakassa</span> <span class="note">[ka. 404; rū. 370; nī. 257; caṃ. 5.2.65; pā. 6.3.57]</span>.</p>\r\n<p class="bodytext">saññāyaṃ gamyamānāyaṃ uttarapade pare udakassa udo hoti.</p>\r\n<p class="bodytext">udakaṃ dhāretīti udadhi, mahantaṃ udakaṃ dhāretīti mahodadhi, udakaṃ pivanti etthāti udapānaṃ, udakaṃ pivanti etāyāti udapāti.</p>\r\n<p class="bodytext"><a name="para407"></a><span class="paranum">407</span>. <span class="bld">kumbhādīsu vā</span> <span class="note">[ka. 404; rū. 370; nī. 257; caṃ. 5.2.69; pā. 6.3.59]</span>.</p>\r\n<p class="bodytext">kumbhādīsu paresu udakassa udo hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-406-407-');
INSERT INTO pages VALUES(54685,'annya_sadda_10',278,replace(replace('\r\n<p class="bodytext">udakassa <a name="M0.0278"></a> kumbho udakumbho, udakakumbho. evaṃ udapatto, udakapatto, udabindu, udakabindu. mahāvuttinā syādīsupi, "nīlodā pokkharaṇī"ti pāḷi.</p>\r\n<p class="bodytext"><a name="para408"></a><span class="paranum">408</span>. <span class="bld">sotādīsu lopo</span> <span class="note">[ka. 404; rū. 370; nī. 256]</span>.</p>\r\n<p class="bodytext">sotādīsu paresu udakassa ussa lopo hoti.</p>\r\n<p class="bodytext">udakassa sotaṃ dakasotaṃ, udake rakkhaso dakarakkhaso, udakaṃ āsayo yesaṃ te dakāsayā, pāṇā. mahāvuttinā syādīsupi, "dake dakāsayā sentī"ti <span class="note">[saṃ. ni. 3.78 (thokaṃ visadisaṃ)]</span> pāḷi.</p>\r\n<p class="bodytext"><a name="para409"></a><span class="paranum">409</span>. <span class="bld">pubbāparajjasāyamajjhehāhassa ṇho</span> <span class="note">[ka. 404; rū. 370; nho (sī.)]</span>.</p>\r\n<p class="bodytext">pubbādīhi parassa ahassa ṇho hoti.</p>\r\n<p class="bodytext">pubbaṇho, aparaṇho, ajjaṇho, sāyaṇho <span class="note">[sāyanho]</span>, majjhaṇho.</p>\r\n<p class="centered">nānādesarāsi niṭṭhito.</p>\r\n<p class="subhead">abyayarāsi</p>\r\n<p class="bodytext"><a name="para410"></a><span class="paranum">410</span>. <span class="bld">kupādayo niccamasyādividhimhi</span> <span class="note">[ka. 324; rū. 339; caṃ. 2.2.24; pā. 2.2.18]</span>.</p>\r\n<p class="bodytext">syādividhito aññattha kuādayo pādayo ca saddā syādyantena saha niccaṃ ekatthā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-408-409-410-');
INSERT INTO pages VALUES(54686,'annya_sadda_10',279,replace(replace('\r\n<p class="bodytext">kucchito brāhmaṇo kubrāhmaṇo, īsakaṃ uṇhaṃ kaduṇhaṃ, pākaṭo hutvā bhavatīti pātubhūto, āvī <span class="note">[‘āvi’pi dissati]</span> hutvā bhavatīti āvībhūto, tuṇhī bhavatīti tuṇhībhūto, pamukho nāyako panāyako, pakārena karitvā pakaritvā <a name="M0.0279"></a> pakārena kataṃ pakataṃ, paṭhamaṃ vā kataṃ pakataṃ, virūpo puriso duppuriso. evaṃ dukkaṭaṃ, sobhaṇo puriso supuriso. evaṃ sukataṃ, abhidhammo, abhitthuto, bhusaṃ kaḷāro ākaḷāro, bhusaṃ bandho ābandho iccādi.</p>\r\n<p class="bodytext">pādayo pakatādyatthe paṭhamāya ekatthā honti, pakato ācariyo pācariyo. evaṃ payyako, paro antevāsī pantevāsī, paro putto paputto, paro nattā panattā.</p>\r\n<p class="bodytext">atyādayo atikkantādyatthe dutiyāya, mañcaṃ atikkanto atimañco. evaṃ atibālo, ativelā.</p>\r\n<p class="bodytext">avādayo kuṭṭhādyatthe tatiyāya, kokilāya avakuṭṭhaṃ vanaṃ avakokilaṃ, ‘avakuṭṭha’nti chaḍḍitanti vadanti. evaṃ avamayūraṃ.</p>\r\n<p class="bodytext">pariyādayo gilānādyatthe catutthiyā, ajjhāyituṃ gilāno pariyajjheno.</p>\r\n<p class="bodytext">nyādayo nikkhantādyatthe pañcamiyā, kosambiyā nikkhanto nikkosambi, vānato nikkhantaṃ nibbānaṃ.</p>\r\n<p class="bodytext">asyādividhimhīti kiṃ? rukkhaṃ pati vijjotate.</p>\r\n<p class="bodytext"><a name="para411"></a><span class="paranum">411</span>. <span class="bld">cī kriyatthehi</span> <span class="note">[caṃ. 2.2.25; pā. 1.4.60, 61]</span>.</p>\r\n<p class="bodytext">cīpaccayanto saddo kriyatthehi saddehi saha ekattho hoti.</p>\r\n<p class="bodytext">balasā kiriya balīkiriya, pākaṭīkiriya, pākaṭībhuyya, pākaṭībhaviya.</p>\r\n<p class="bodytext"><a name="para412"></a><span class="paranum">412</span>. <span class="bld">bhūsanādarānādarādīsvehi saha</span> <span class="note">[caṃ. 2.2.27; pā. 1.4.63, 64]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-411-412-');
INSERT INTO pages VALUES(54687,'annya_sadda_10',280,replace(replace('\r\n<p class="bodytext">alamādayo <a name="M0.0280"></a> saddā bhūsanādīsu atthesu etehi kriyatthehi saha ekatthā honti.</p>\r\n<p class="bodytext">bhūsanaṃ akāsīti alaṃkiriya, samaṃ ādaraṃ akāsīti sakkacca, asamaṃ anādaraṃ akāsīti asakkacca, binduno pararūpattaṃ.</p>\r\n<p class="bodytext">bhūsanādīsūti kiṃ? alaṃ bhutvā gato, ‘ala’nti pariyattaṃ, sakkatvā gato, sobhaṇaṃ katvātyattho. "kacca, kiriya" iccādinā saṃkhittarūpehi upapadena saha siddhehi eva ekatthasaññā, na "katvā" iccādinā asaṃkhittarūpehi visuṃ siddhehīti adhippāyo.</p>\r\n<p class="bodytext"><a name="para413"></a><span class="paranum">413</span>. <span class="bld">aññe ca</span> <span class="note">[ka. 324, 327; rū. 339, 351; nī. 682-688, caṃ. 2.2.30, 33, 34, 37, 44; pā 1.4.67, 71, 72, 75, 76; 3.4.63]</span>.</p>\r\n<p class="bodytext">aññe ca saddā kriyatthehi syādyantehi saha bahulaṃ ekatthā honti.</p>\r\n<p class="bodytext">atirekaṃ abhavīti parobhuyya. evaṃ tirobhuyya, parokiriya, tirokiriya, urasikiriya, manasikiriya, majjhekiriya, tuṇhībhuyya.</p>\r\n<p class="bodytext">tyādisaddāpi saññābhāvaṃ pattā nipātarūpā honti, syādirūpā ca. tasmā tepi imasmiṃ sutte saṅgayhanti.</p>\r\n<p class="bodytext">atthikhīrā gāvī, nasantiputtā itthī, atthi hutvā paccayo atthipaccayo. evaṃ natthipaccayo, ahosi eva kammaṃ ahosikammaṃ, ehi ca passa ca ehipassa, ehipassa iti vidhānaṃ arahatīti ehipassiko, ayaṃ taddhitantasamāso nāma. evaṃ parattha.</p>\r\n','\r',char(13)),'\n',char(10)),'-413-');
INSERT INTO pages VALUES(54688,'annya_sadda_10',281,replace(replace('\r\n<p class="bodytext">ehi <a name="M0.0281"></a> bhaddanteti vuttopi na etīti naehibhaddantiko <span class="note">[dī. ni. 1.394]</span>, tiṭṭha bhaddanteti vuttopi na tiṭṭhatīti natiṭṭhabhaddantiko, ehi svāgataṃ tuyhanti vadanasīlo ehisvāgatiko, ehisvāgatavādī <span class="note">[pārā. 432]</span> vā, ehi bhikkhūti vacanena siddhā upasampadā ehibhikkhūpasampadā, evaṃ porāṇā āhaṃsu vā, evaṃ pure āsiṃsu vāti evaṃ pavattaṃ vidhānaṃ ettha atthīti itihāso <span class="note">[dī. ni. 1.256]</span>, purāṇagantho, yaṃ pubbe anaññātaṃ, taṃ idāni ñassāmi iti pavattassa indriyaṃ anaññātaññassāmītindriyaṃ, asuko iti āha asuko iti āha, asukasmiṃvā ganthe iti āha asukasmiṃ ganthe iti āhāti evaṃ pavattavacanaṃ itihitihaṃ, aññāsi iti byākato koṇḍañño aññāsikoṇḍañño <span class="note">[mahāva. 8]</span> iccādi.</p>\r\n<p class="bodytext">keci pana "sacchikaroti, manasikaroti, pākaṭīkaroti, āvīkaroti, pātukaroti, alaṅkaroti, sakkaroti, pabhavati, parābhavati" iccādīnampi ekatthībhāvaṃ vadanti. tumantatvantādikāpi ettha saṅgayhanti, gantuṃ kāmetīti gantukāmo, kattukāmo, daṭṭhukāmo, gantuṃ mano etassāti gantumano, saṃvidhāya avahāro saṃvidhāvahāro, yalopo. evaṃ upādāya uppannaṃ rūpaṃ upādārūpaṃ, anupādāya vimutto anupādāvimutto, paṭiccasamuppādo, āhaccabhāsito, upahaccaparinibbāyī <span class="note">[pu. pa. 37]</span>, aveccappasādo, chakkhattuparamaṃ, sattakkhattuparamo <span class="note">[pu. pa. 31]</span> iccādi.</p>\r\n<p class="centered">abyayarāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54689,'annya_sadda_10',282,replace(replace('\r\n<p class="subhead">saṅkhyārāsi</p>\r\n\r\n<p class="bodytext">idāni <a name="M0.0282"></a> saṅkhyārāsi vuccate.</p>\r\n<p class="bodytext"><a name="para414"></a><span class="paranum">414</span>. <span class="bld">vidhādīsu dvissa du</span> <span class="note">[ka. 386; rū. 410; nī. 811]</span>.</p>\r\n<p class="bodytext">vidhādīsu paresu dvissa du hoti.</p>\r\n<p class="bodytext">dve vidhā pakārā yassāti duvidho, dve paṭṭāni yassāti dupaṭṭaṃ, cīvaraṃ, duvaṅgikaṃ, jhānaṃ iccādi.</p>\r\n<p class="bodytext"><a name="para415"></a><span class="paranum">415</span>. <span class="bld">di guṇādīsu</span> <span class="note">[ka. 386; rū. 410; nī. 811]</span>.</p>\r\n<p class="bodytext">guṇādīsu dvissa di hoti.</p>\r\n<p class="bodytext">dve guṇā paṭalā yassāti diguṇā, saṅghāṭi, dve gāvo digu, dve rattiyo dirattaṃ, dvirattaṃ vā.</p>\r\n<p class="bodytext"><a name="para416"></a><span class="paranum">416</span>. <span class="bld">tīsva</span> <span class="note">[ka. 383; rū. 253; nī. 815]</span>.</p>\r\n<p class="bodytext">tīsu paresu dvissa a hoti.</p>\r\n<p class="bodytext">dve vā tayo vā vārā dvattikkhattuṃ, dve vā tayo vā pattā dvattipattā.</p>\r\n<p class="bodytext"><a name="para417"></a><span class="paranum">417</span>. <span class="bld">ā saṅkhyāyāsatādonaññatthe</span> <span class="note">[ka. 383; rū. 253; nī. 815; caṃ. 5.2.52; pā. 6.3.47]</span>.</p>\r\n<p class="bodytext">aññapadatthavajjite samāse satādito aññasmiṃ saṅkhyāpade pare dvissa ā hoti.</p>\r\n<p class="bodytext">dve ca dasa ca dvādasa, dvīhi vā adhikā dasa dvādasa, dvāvīsati, dvattiṃsa, rassattaṃ.</p>\r\n<p class="bodytext">asatādoti kiṃ? dvisataṃ, dvisahassaṃ.</p>\r\n<p class="bodytext">anaññattheti kiṃ? dve dasa yasminti dvidasa.</p>\r\n<p class="bodytext"><a name="para418"></a><span class="paranum">418</span>. <span class="bld">tisse</span> <span class="note">[ka. 404; rū. 370; caṃ. 5.2.53; pā. 6.3.48]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-414-415-416-417-418-');
INSERT INTO pages VALUES(54690,'annya_sadda_10',283,replace(replace('\r\n<p class="bodytext">anaññatthe <a name="M0.0283"></a> asatādo saṅkhyāpade pare tissa e hoti.</p>\r\n<p class="bodytext">tayo ca dasa ca terasa, tīhi vā adhikā dasa terasa. evaṃ tevīsati, tettiṃsa.</p>\r\n<p class="bodytext"><a name="para419"></a><span class="paranum">419</span>. <span class="bld">cattālīsādo vā</span> <span class="note">[ka. 404; rū. 370; caṃ. 5.2.54; pā. 6.3.49]</span>.</p>\r\n<p class="bodytext">cattālīsādīsu paresu tissa e hoti vā.</p>\r\n<p class="bodytext">tecattālīsaṃ, ticattālīsaṃ, tepaññāsaṃ, tipaññāsaṃ, tesaṭṭhi, tisaṭṭhi, tesattati, tisattati, teasīti, tiasīti, tenavuti, tinavuti.</p>\r\n<p class="bodytext"><a name="para420"></a><span class="paranum">420</span>. <span class="bld">dvissā ca</span> <span class="note">[ka. 404; rū. 370 caṃ. 5.2.54; pā. 6.3.69]</span>.</p>\r\n<p class="bodytext">cattālīsādo dvissa e hoti vā ā ca.</p>\r\n<p class="bodytext">dvecattālīsaṃ, dvācattālīsaṃ, dvicattālīsaṃ, dvepaññāsaṃ, dvipaññāsaṃ, dvāsaṭṭhi, dvesaṭṭhi, dvāsattati, dvesattati, dvāsīti, dvānavuti, dvenavuti. vāsaddena paññāsamhi āttaṃ, asītimhi ettañca natthi.</p>\r\n<p class="bodytext"><a name="para421"></a><span class="paranum">421</span>. <span class="bld">bācattālīsādo vā</span> <span class="note">[ka. 380; rū. 255; nī. 810]</span>.</p>\r\n<p class="bodytext">acattālīsādo pare dvissa bā hoti vā.</p>\r\n<p class="bodytext">bārasa, dvādasa, bāvīsati, dvāvīsati, bāttiṃsa, dvattiṃsa.</p>\r\n<p class="bodytext">acattālīsādoti kiṃ? dvācattālīsaṃ.</p>\r\n<p class="bodytext"><a name="para422"></a><span class="paranum">422</span>. <span class="bld">catussa cuco dase</span> <span class="note">[ka. 390; rū. 256; nī. 826]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-419-420-421-422-');
INSERT INTO pages VALUES(54691,'annya_sadda_10',284,replace(replace('\r\n<p class="bodytext">dase <a name="M0.0284"></a> pare catussa cu, co honti vā.</p>\r\n<p class="bodytext">cuddasa, coddasa, catuddasa.</p>\r\n<p class="bodytext"><a name="para423"></a><span class="paranum">423</span>. <span class="bld">vīsatidasesu pañcassa paṇṇapannā</span> <span class="note">[ka. 404; rū. 370; nī. 814]</span>.</p>\r\n<p class="bodytext">esu pañcassa paṇṇa, pannā honti vā yathākkamaṃ.</p>\r\n<p class="bodytext">paṇṇavīsati, pañcavīsati, pannarasa, pañcadasa.</p>\r\n<p class="bodytext"><a name="para424"></a><span class="paranum">424</span>. <span class="bld">chassa so</span> <span class="note">[ka. 374; rū. 408; nī. 806]</span>.</p>\r\n<p class="bodytext">dase pare chassa so hoti.</p>\r\n<p class="bodytext">soḷasa.</p>\r\n<p class="bodytext"><a name="para425"></a><span class="paranum">425</span>. <span class="bld">ekaṭṭhānamā</span> <span class="note">[ka. 383; rū. 253; nī. 815]</span>.</p>\r\n<p class="bodytext">dase pare eka, aṭṭhānaṃ ā hoti.</p>\r\n<p class="bodytext">ekādasa, aṭṭhārasa.</p>\r\n<p class="bodytext"><a name="para426"></a><span class="paranum">426</span>. <span class="bld">ra saṅkhyāto vā</span> <span class="note">[ka. 381; rū. 254; nī. 812]</span>.</p>\r\n<p class="bodytext">ekādisaṅkhyamhā parassa dasassa ra hoti vā.</p>\r\n<p class="bodytext">ekārasa, ekādasa, bārasa, dvādasa, pannarasa, pañcadasa, sattarasa, sattadasa, aṭṭhārasa, aṭṭhādasa, bādese pannādese ca niccaṃ. idha na hoti, catuddasa.</p>\r\n<p class="bodytext"><a name="para427"></a><span class="paranum">427</span>. <span class="bld">chatīhi ḷo ca</span> <span class="note">[ka. 379; rū. 258; nī. 809]</span>.</p>\r\n<p class="bodytext">cha, tīhi parassa dassa ḷo hoti ro ca.</p>\r\n<p class="bodytext">soḷasa, terasa, teḷasa.</p>\r\n<p class="bodytext"><a name="para428"></a><span class="paranum">428</span>. <span class="bld">catutthatatiyānamaḍḍhuḍḍhatiyā</span> <span class="note">[ka. 387; rū. 411; nī. 819]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-423-424-425-426-427-428-');
INSERT INTO pages VALUES(54692,'annya_sadda_10',285,replace(replace('\r\n<p class="bodytext">‘aḍḍhuḍḍhatiyā’ti <a name="M0.0285"></a> aḍḍhā+uḍḍhatiyāti chedo, aḍḍhamhā paresaṃ catuttha, tatiyānaṃ uḍḍha, tiyā honti yathākkamaṃ.</p>\r\n<p class="bodytext">aḍḍhena catuttho aḍḍhuḍḍho, aḍḍhena tatiyo aḍḍhatiyo.</p>\r\n<p class="bodytext">sakatthe ṇyamhi aḍḍhateyyo.</p>\r\n<p class="bodytext"><a name="para429"></a><span class="paranum">429</span>. <span class="bld">dutiyassa saha diyaḍḍhadivaḍḍhā</span> <span class="note">[ka. 387; rū. 411; nī. 819]</span>.</p>\r\n<p class="bodytext">aḍḍhamhā parassa dutiyassa saha aḍḍhena diyaḍḍha, divaḍḍhā honti.</p>\r\n<p class="bodytext">aḍḍhena dutiyo diyaḍḍho, divaḍḍho.</p>\r\n<p class="bodytext">yathā ca eka, dvi,ti, catu, pañca, cha, satta, aṭṭha, nava, dasasaddā paccekaṃ attano atthesu nipatanti, tathā vīsati, tiṃsati, cattālīsa, paññāsa, saṭṭhi, sattati, asīti, navutisaddā paccekaṃ attano atthesu nipatanti, dasasaddassa kāriyā na honti, evaṃ satasahassasaddāpīti daṭṭhabbaṃ.</p>\r\n<p class="bodytext">tato paraṃ pana dasa sahassāni dasasahassaṃ, idaṃ ‘nahuta’nti ca vuccati, sataṃ sahassāni satasahassaṃ, idaṃ ‘lakkha’nti ca vuccati, dasa satasahassāni dasasatasahassanti evaṃ digusamāsavasena etāni padāni sijjhanti.</p>\r\n<p class="bodytext">dve satāni dvisataṃ, tīṇi satāni tisataṃ, dve sahassāni dvisahassaṃ, tīṇi sahassāni tisahassaṃ iccādīni digumhi vuttāneva.</p>\r\n','\r',char(13)),'\n',char(10)),'-429-');
INSERT INTO pages VALUES(54693,'annya_sadda_10',286,replace(replace('\r\n<p class="bodytext">gaṇanapathe pana ekaṭṭhānaṃ, dasaṭṭhānaṃ, sataṭṭhānaṃ, sahassaṭṭhānaṃ, dasasahassaṭṭhānaṃ, satasahassaṭṭhānaṃ, dasasatasahassaṭṭhānanti <a name="M0.0286"></a> imāni satta ṭhānāni kamena dasaguṇitāni honti. tattha ekaṭṭhānaṃ nāma ekaṃ, dve, tīṇi iccādi. dasaṭṭhānaṃ nāma dasa, vīsaṃ, tiṃsaṃ iccādi. sataṭṭhānaṃ nāma sataṃ, dvisataṃ, tisataṃ iccādi. sahassaṭṭhānaṃ nāma sahassaṃ, dvisahassaṃ, tisahassaṃ iccādi. evaṃ uparipi. ekamekasmiñca ṭhāne nava padāni ca nava antaranavantāni ca honti. ayaṃ mūlabhūmi nāma.</p>\r\n<p class="bodytext">taduttari koṭibhūmi nāma. tatthapi ekaṭṭhānaṃ, dasaṭṭhānaṃ, sataṭṭhānaṃiccādīni satta ṭhānāni honti. tattha mūlabhūmiyā antimaṭṭhānaṃ gahetvā dasaguṇite kate koṭibhūmiyaṃ ekaṭṭhānaṃ hoti, idhapi satta ṭhānāni kamena dasaguṇitāniyeva, idha dasakoṭisatasahassaṃ antimaṭṭhānaṃ bhavati.</p>\r\n<p class="bodytext">taduttari pakoṭibhūmi nāma. etthapi satta ṭhānāni honti. tattha koṭibhūmiyā antimaṭṭhānaṃ gahetvā dasaguṇite kate pakoṭibhūmiyaṃ ekaṭṭhānaṃ hoti, idhapi satta ṭhānāni dasaguṇitāniyeva, dasapakoṭisatasahassaṃ antimaṭṭhānaṃ, iminā nayena sabbabhūmīsu uparūpari bhūmisaṅkanti ca ṭhānabhedo ca veditabbo.</p>\r\n<p class="bodytext">ayaṃ panettha bhūmikkamo-mūlabhūmi, koṭibhūmi, pakoṭibhūmi, koṭipakoṭibhūmi, nahutabhūmi, ninnahutabhūmi, akkhobhiṇībhūmi <span class="note">[bhinī, bhanītipi dissanti]</span>, bindubhūmi, abbudabhūmi, nirabbudabhūmi, ahahabhūmi, abababhūmi, aṭaṭabhūmi, sogandhikabhūmi, uppalabhūmi, kumudabhūmi, puṇḍarīkabhūmi, padumabhūmi, kathānabhūmi, mahākathānabhūmi, asaṅkhyeyyabhūmīti ekavīsati bhūmiyo sattacattālīsasataṃ ṭhānāni ca honti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54694,'annya_sadda_10',287,replace(replace('\r\n<p class="bodytext">nirayabhūmīnaṃ kamena pana abbudabhūmi, nirabbudabhūmi, abababhūmi, aṭaṭabhūmi, ahahabhūmi, kumudabhūmi, sogandhikabhūmi, uppalabhūmi<a name="M0.0287"></a>, puṇḍarīkabhūmi, padumabhūmīti vattabbo. ettha ca yasmā pāḷibhāsāyaṃ dasasatasahassaṃ nāma sattamaṭṭhānaṃ natthi, cha ṭhānāni eva atthi, tasmā garū aṭṭhakathāsu <span class="note">[saṃ. ni. 1.181]</span> āgatanayena sattamaṭṭhānaṃ ullaṅghetvā chaṭṭhaṭṭhānato upari bhūmisaṅkantiṃ kathentā sataguṇitaṃ katvā kathenti, satasahassānaṃ sataṃ koṭi, koṭisatasahassānaṃ sataṃ pakoṭi iccādi.</p>\r\n<p class="bodytext">tattha satasahassānaṃ sataṃ nāma dasasatasahassānaṃ dasakameva hoti, tasmā tathā kathentāpi bhūmīnaṃ sabbaṭṭhānānañca dasaguṇasiddhimeva kathentīti veditabbaṃ. yasmā ca gaṇanabhūmisaṅkhāto gaṇanapatho nāma nānādesavāsīnaṃ vasena nānāvidho hoti, tasmā dīpavaṃse akkhobhiṇī, bindu, kathāna, mahākathānāni vajjetvā pāḷinayena sattarasa bhūmiyova vuttā. kaccāyane <span class="note">[ka. 394, 395; rū. 416, 417]</span> pubbe dassitā ekavīsati bhūmiyo, sakkataganthesu tato sādhikabhūmiyo, katthaci pana mahābalakkhandhapariyantā saṭṭhi bhūmiyoti āgatā.</p>\r\n<p class="bodytext">tattha sakasakabhūmito atirekavatthūni gaṇanapathavītivattāni nāma honti, yesaṃ pana mūlabhūmimattaṃ atthi, tesaṃ koṭimattānipi vatthūni gaṇanapathātikkantāni eva.</p>\r\n<p class="bodytext">api ca ‘gaṇanapathavītivatta’nti ca ‘gaṇanapathātikkanta’nti ca ‘asaṅkhyeyya’nti ca atthato ekaṃ. tasmā idhapi vīsati bhūmiyo eva anukkamena dasaguṇitā gaṇanapathā nāma honti. asaṅkhyeyyanti pana dasaguṇavinimuttā gaṇanapathātikkantabhūmi eva vuccati. mahākathānabhūmātikkantato paṭṭhāya hi anantamahāpathaviyā sabbapaṃsucuṇṇānipi idha asaṅkhyeyyabhūmiyaṃ saṅgayhanti. itarathā asaṅkhyeyyanti ca gaṇanapathabhūmīti ca viruddhametanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54695,'annya_sadda_10',288,replace(replace('\r\n<p class="bodytext">dīpavaṃse <a name="M0.0288"></a> ca "tato upari abhūmi, asaṅkhyeyyanti vuccatī"ti vuttaṃ. tattha ‘abhūmī’ti vacanena gaṇanapathabhūmi eva paṭisiddhā, na tu gaṇanapathātikkantā visuṃ asaṅkhyeyyabhūmi nāma. cariyāpiṭakasaṃvaṇṇanāyampi <span class="note">[cariyā. aṭṭha. nidānakathā]</span> ayamattho vutto. asaṅkhyeyyabhūmiyampi asaṅkhyeyyānaṃ cūḷa, mahādivasena anekappabhedo dakkhiṇavibhaṅgasuttena <span class="note">[ma. ni. 3.370]</span> dīpetabbo.</p>\r\n<p class="bodytext">saddanītiyaṃ <span class="note">[nī. 833]</span> pana pāḷinayaṃ gahetvā "vīsati abbudāni ekaṃ nirabbudaṃ nāma. vīsati nirabbudāni ekaṃ ababaṃ nāma" iccādinā nirabbudādīnaṃ saṅkhyānampi vīsatimattaguṇaṃ nāma vuttaṃ. taṃ na yujjati. nirayesu hi vīsatimattaguṇena abbuda, nirabbudādīnaṃ dasannaṃ nirayānaṃ tāni nāmāni siddhāni bhavanti. garū pana tāni nāmāni gahetvā dasaguṇasiddhesu gaṇanapathesu pakkhipiṃsu, tasmā nāmamattena sadisāni bhavanti, guṇavidhi pana visadisoeva.</p>\r\n<p class="bodytext">evañcakatvā pāḷiyampi bakabrahmāsutte <span class="note">[saṃ. ni. 1.175]</span> "sataṃ sahassānaṃ nirabbudānaṃ, āyuṃ pajānāmi tavāhaṃ brahme"ti vuttaṃ. ettha ca yadi gaṇanabhūmipathepi nirabbudānaṃ vīsatimattena abababhūmi bhaveyya, evaṃ sati nirabbudānaṃ satasahassaṃ nāma na vucceyya. jātakaṭṭhakathāyañca <span class="note">[jā. aṭṭha. 3.7.69]</span> "nirabbudasatasahassānaṃ ekaṃ ahahaṃ nāma, ettakaṃ bakassa brahmuno tasmiṃbhave avasiṭṭhaṃ āyū"ti vuttaṃ. tasmā imaṃ gaṇanabhūmipathaṃ patvā vīsatiguṇaṃ nāma natthīti siddhaṃ hoti. akkhobhiṇī, bindu, kathāna, mahākathānānipi aññato gahetvā pakkhittāni siyuṃ. evaṃ pakkhittānañca cuddasannaṃ saṅkhyānaṃ kaccāyane kamokkamatā pana pacchājātā siyāti.</p>\r\n<p class="bodytext">iti niruttidīpaniyā nāma moggallānabyākaraṇadīpaniyā</p>\r\n<p class="centered">samāsakaṇḍo catuttho.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54696,'annya_sadda_10',289,replace(replace('\r\n<p class="chapter">5. taddhita</p>\r\n<p class="bodytext">atha <a name="M0.0289"></a> taddhitavidhānaṃ dīpiyate.</p>\r\n<p class="bodytext">taddhitavutti nāma vicitrā hoti, sātisayena vicitrañāṇahitaṃ vahati, tasmā tesaṃ tesaṃ kulaputtānaṃ hitanti <span class="bld">taddhitaṃ,</span> imasmiṃ kaṇḍe sabbavidhānassa nāmaṃ. taṃ pana aṭṭhavidhaṃ hoti apaccaṃ, anekatthaṃ, assatthi, bhāvakammaṃ, parimāṇaṃ, saṅkhyā, khuddakaṃ, nānāttanti.</p>\r\n<p class="subhead">apaccarāsi</p>\r\n<p class="bodytext"><a name="para430"></a><span class="paranum">430</span>. <span class="bld">ṇo vāpacce</span> <span class="note">[ka. 344; rū. 361; nī. 752; caṃ. 2.4.16; pā. 4.1.92; ‘sarānamādissā.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">chaṭṭhyantā nāmamhā tassa apaccanti atthe vikappena ṇapaccayo hoti. vāsaddo vākya, samāsānaṃ vikappanattho, ito paraṃ anuvattate, tena sabbattha vikappavidhi sijjhati. ṇānubandho vuddhyattho, so payogaappayogī. vasiṭṭhassa apaccanti atthe iminā suttena vasiṭṭhamhā ṇapaccayo, so sāmyatthañca apaccatthañca ubhayaṃ vadati, chaṭṭhī ca apaccapadañca tena vuttatthā nāma honti, vasiṭṭhapadaṃ paccayena saha ekatthaṃ hoti, ubhayaṃ ekato hutvā puttassa nāmaṃ hotīti attho. tato ‘ekatthatāya’nti chaṭṭhiyā lopo, apaccapadaṃ pana vuttatthamattena luppati. tañhi sutte padhānabhāvena niddiṭṭhaṃ hoti, na chaṭṭhīti, mahāvuttinā vā padānaṃ lopo. evaṃ sabbattha.</p>\r\n<p class="bodytext"><a name="para431"></a><span class="paranum">431</span>. <span class="bld">padānamādissāyuvaṇṇassāeo ṇānubandhe</span> <span class="note">[ka. 405; rū. 365; nī. 860]</span>.</p>\r\n<p class="bodytext">padānaṃ ādibhūtassa akārassa ca ivaṇṇu’vaṇṇassa ca ā, e, o vuddhiyo honti ṇānubandhe paccaye pareti padādia-kārassa āvuddhi, syādyuppatti.</p>\r\n','\r',char(13)),'\n',char(10)),'-430-431-');
INSERT INTO pages VALUES(54697,'annya_sadda_10',290,replace(replace('\r\n<p class="bodytext">vāsiṭṭho, <a name="M0.0290"></a> puriso, vāsiṭṭhī, itthī, vāsiṭṭhaṃ, kulaṃ, vasiṭṭhassa putto vāsiṭṭho, vasiṭṭhassa dhītā vāsiṭṭhī, vasiṭṭhassa kulaṃ vāsiṭṭhanti evampi yojetuṃ yujjati.</p>\r\n<p class="bodytext">tattha ‘vasiṭṭhassā’ti etena gottasseva pitubhūtaṃ ādipurisaṃ vadati. kasmā? gottasaddattā. evañhi sati tasmiṃ gotte paccājātā sabbepi janā tassa apaccā nāma honti.</p>\r\n<p class="bodytext"><a name="para432"></a><span class="paranum">432</span>. <span class="bld">majjhe</span> <span class="note">[ka. 404; rū. 370; nī. 859]</span>.</p>\r\n<p class="bodytext">majjhe pavattānaṃ a, yuvaṇṇānaṃ ā, e, ovuddhiyo honti vā kvaci.</p>\r\n<p class="bodytext">vāseṭṭho, vāseṭṭhī, vāseṭṭhaṃ. vāsaddena "vasiṭṭhassa putto dhītā kula"nti vākyaṃ vā "vasiṭṭhaputto vasiṭṭhadhītā vasiṭṭhakula"nti samāsaṃ vā vikappeti. evaṃ sabbattha.</p>\r\n<p class="bodytext">bharadvājassa apaccaṃ bhāradvājo, visāmittassa <span class="note">[bhāradvājassa. vesāmittassa. (rū.)]</span> apaccaṃ vesāmitto, gotamassa apaccaṃ gotamo, kassapassa apaccaṃ kassapo, vasudevassa apaccaṃ vāsudevo. evaṃ bāladevo.</p>\r\n<p class="bodytext">upagussa apaccanti ettha ‘uvaṇṇassāvaṅa.’ti ṇānubandhe paccaye pare uvaṇṇassa avaṅa hoti. opagavo, opagavī, opagavaṃ, manuno apaccaṃ mānavo, bhagguno apaccaṃ bhaggavo, paṇḍuno apaccaṃ paṇḍavo, upavindussa apaccaṃ opavindavo iccādi.</p>\r\n<p class="bodytext"><a name="para433"></a><span class="paranum">433</span>. <span class="bld">vacchādito ṇānaṇāyanā</span> <span class="note">[ka. 345; rū. 366; nī. 754; pā. 4.1.93, 94, 162, 163]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-432-433-');
INSERT INTO pages VALUES(54698,'annya_sadda_10',291,replace(replace('\r\n<p class="bodytext">chaṭṭhuntehi <a name="M0.0291"></a> vacchādīhi gottasaddagaṇehi tassa apaccanti atthe ṇānubandhā āna, āyanapaccayā honti vā.</p>\r\n<p class="bodytext">vacchassa apaccaṃ vacchāno, vacchāyano, vacchānī, vacchāyanī, vacchānaṃ, vacchāyanaṃ. evaṃ kaccāno, kaccāyano, kātiyāno, kātiyāyano, sākaṭāno, sākaṭāyano, kaṇhāno, kaṇhāyano, moggallāno, moggallāyano, aggivessāno <span class="note">[aggivessanotipi dissati dī. ni. 1.176; ma. ni. 1.353 ādayo]</span>, aggivessāyano, muñcāno, muñcāyano, kuñcāno, kuñcāyano iccādi.</p>\r\n<p class="bodytext"><a name="para434"></a><span class="paranum">434</span>. <span class="bld">kattikāvidhavādīhi ṇeyyaṇerā</span> <span class="note">[ka. 346; rū. 367; nī. 755; pā. 4.1.120, 123, 126, 127, 128, 129, 131]</span>.</p>\r\n<p class="bodytext">chaṭṭhyantehi kattikādīhi vidhavādīhi ca tassa apaccanti atthe kamena ṇānubandhā eyya, erapaccayā honti vā.</p>\r\n<p class="bodytext">ṇeyye – kattikāya nāma devadhītāya apaccaṃ kattikeyyo, vinatāya nāma deviyā apaccaṃ venateyyo, rohiṇiyā nāma deviyā apaccaṃ rohiṇeyyo, bhaginiyā apaccaṃ bhāgineyyo, nadiyā nāma itthiyā apaccaṃ nādeyyo. evaṃ anteyyo, āheyyo, kāmeyyo, suciyā apaccaṃ soceyyo, bālāya apaccaṃ bāleyyo iccādi.</p>\r\n<p class="bodytext">ṇere – vidhavāya apaccaṃ vedhavero, vidhavā nāma matapatikā itthī. bandhukiyā apaccaṃ bandhukero, nāḷikiyā nāma itthiyā putto nāḷikero, samaṇassa upajjhāyassa putto sāmaṇero, samaṇiyā pavattiniyā dhītā sāmaṇerī iccādi.</p>\r\n<p class="bodytext"><a name="para435"></a><span class="paranum">435</span>. <span class="bld">ṇya diccādīhi</span> <span class="note">[ka. 347; rū. 368; nī. 756; caṃ. 2.4.2; pā. 4.1.85]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-434-435-');
INSERT INTO pages VALUES(54699,'annya_sadda_10',292,replace(replace('\r\n<p class="bodytext">chaṭṭhuntehi <a name="M0.0292"></a> ditiiccādīhi tassa apaccanti atthe ṇānubandho yapaccayo hoti vā.</p>\r\n<p class="bodytext"><a name="para436"></a><span class="paranum">436</span>. <span class="bld">lopovaṇṇivaṇṇānaṃ</span> <span class="note">[ka. 261; rū. 369; nī. 509]</span>.</p>\r\n<p class="bodytext">ye pare avaṇṇassa ivaṇṇassa ca lopo hotīti ṇyamhi pare avaṇṇi’vaṇṇānaṃ lopo.</p>\r\n<p class="bodytext">ditiyā nāma devadhītāya apaccaṃ decco, aditiyā apaccaṃ ādicco.</p>\r\n<p class="bodytext">tattha ivaṇṇalope ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti yamhi pare tavaggassa cavaggattaṃ, puna ‘vaggalasehi te’ti yassa pubbarūpattaṃ, kuṇḍaniyā apaccaṃ koṇḍañño.</p>\r\n<p class="bodytext"><a name="para437"></a><span class="paranum">437</span>. <span class="bld">uvaṇṇassāvaṅa sare</span> <span class="note">[ka. 348; rū. 371; nī. 757]</span>.</p>\r\n<p class="bodytext">sare pare uvaṇṇassa avaṅa hotīti uvaṇṇassa avattaṃ. ‘tavaggavaraṇāna.’nti suttena vassa battaṃ, puna ‘vaggalasehi te’ti suttena yassa pubbarūpattaṃ, bhātuno apaccaṃ bhātabyo.</p>\r\n<p class="bodytext"><a name="para438"></a><span class="paranum">438</span>. <span class="bld">ā ṇi</span> <span class="note">[ka. 347; rū. 368; nī. 756; caṃ. 2.4.19; pā. 4.1.95]</span>.</p>\r\n<p class="bodytext">rassā’kārantato apaccatthe ṇānubandho rassi’paccayo hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-436-437-438-');
INSERT INTO pages VALUES(54700,'annya_sadda_10',293,replace(replace('\r\n<p class="bodytext">dakkhassa apaccaṃ dakkhi. evaṃ doṇi, vāsavi, sakyaputti, nāṭaputti, dāsaputti, dāruno apaccaṃ dāravi <span class="note">[vicāretabbamidaṃ]</span>, varuṇassa apaccaṃ vāruṇi<a name="M0.0293"></a>. evaṃ kaṇḍi, bāladevi, pāvaki, jinadattassa apaccaṃ jenadatti, suddhādani, anuruddhi iccādi.</p>\r\n<p class="bodytext"><a name="para439"></a><span class="paranum">439</span>. <span class="bld">rājato ño jātiyaṃ</span> <span class="note">[ka. 347; rū. 368; nī. 756; caṃ. 2.4.70; pā. 4.1.137]</span>.</p>\r\n<p class="bodytext">jātiyaṃ gamyamānāyaṃ rājasaddamhā apaccatthe ñapaccayo hoti vā.</p>\r\n<p class="bodytext">rañño apaccaṃ rājañño, rājakulassa puttoti attho.</p>\r\n<p class="bodytext">jātiyanti kiṃ? rājaputto.</p>\r\n<p class="bodytext"><a name="para440"></a><span class="paranum">440</span>. <span class="bld">khattā yiyā</span> <span class="note">[ka. 347; rū. 368; nī. 756; caṃ. 2.4.69; pā. 4.1.138]</span>.</p>\r\n<p class="bodytext">jātiyaṃ gamyamānāyaṃ khattasaddamhā apaccatthe ya, iyapaccayā honti.</p>\r\n<p class="bodytext">khattakulassa apaccaṃ khatyo, khattiyo.</p>\r\n<p class="bodytext">jātiyantveva? khatti.</p>\r\n<p class="bodytext"><a name="para441"></a><span class="paranum">441</span>. <span class="bld">manuto ssasaṇa</span> <span class="note">[ka. 348; rū. 371; nī. 753; caṃ. 2.4.94, 95; pā. 4.1.161]</span>.</p>\r\n<p class="bodytext">jātiyaṃ gamyamānāyaṃ manusaddamhā apaccatthe ssa, saṇapaccayā honti.</p>\r\n<p class="bodytext">manuno apaccaṃ manusso, mānuso, manu nāma kappe ādikhattiyo mahāsammatarājā, itthiyaṃ manussī, mānusī,</p>\r\n<p class="bodytext">jātiyantveva? māṇavo.</p>\r\n<p class="bodytext"><a name="para442"></a><span class="paranum">442</span>. <span class="bld">janapadanāmasmā janakhattiyā raññe ca ṇo</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 2.4.96; pā. 4.1.168; "janapadanāmasmā khattiyā." (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-439-440-441-442-');
INSERT INTO pages VALUES(54701,'annya_sadda_10',294,replace(replace('\r\n<p class="bodytext">janavācinā <a name="M0.0294"></a> ca khattiyavācinā ca janapadanāmamhā raññe ca apacce ca ṇo hoti.</p>\r\n<p class="bodytext">pañcālānaṃ janānaṃ rājā pañcālo, pañcālassa khattiyassa apaccaṃ pañcālo. evaṃ kosalo, māgadho, okkāko <span class="note">[dī. ni. 1.267]</span>.</p>\r\n<p class="bodytext">janapadanāmasmāti kiṃ? dasaratharañño putto dāsarathi <span class="note">[dāsaraṭṭhi]</span>.</p>\r\n<p class="bodytext">janakhattiyāti kiṃ? pañcālassa brāhmaṇassa apaccaṃ pañcāli.</p>\r\n<p class="bodytext"><a name="para443"></a><span class="paranum">443</span>. <span class="bld">ṇya kurusivīhi</span> <span class="note">[ka. 346; rū. 367; nī. 755; caṃ. 2.4.101 .pe.. 4.1.172]</span>.</p>\r\n<p class="bodytext">kuru, sivisaddehi apacce raññe ca ṇyo hoti.</p>\r\n<p class="bodytext">kururañño apaccaṃ korabyo <span class="note">[jā. 1.14.228, 232, 236]</span>, kururaṭṭhavāsīnaṃ rājā korabyo, korabbo, pubbarūpattaṃ, sivirañño apaccaṃ sebyo, siviraṭṭhavāsīnaṃ rājā sebyo.</p>\r\n<p class="centered">apaccarāsi niṭṭhito.</p>\r\n<p class="subhead">anekattharāsi</p>\r\n<p class="bodytext"><a name="para444"></a><span class="paranum">444</span>. <span class="bld">ṇa rāgā tena rattaṃ</span> <span class="note">[ka. 347; rū. 368; nī. 756; caṃ. 3.1.1 .pe.. 4.2.1]</span>.</p>\r\n<p class="bodytext">rajjanti vatthaṃ etenāti rāgo, rajanavatthu, rāgavācimhā tena rattanti atthe ṇo hoti.</p>\r\n<p class="bodytext">kasāvena rattaṃ vatthaṃ kāsāvaṃ <span class="note">[dha. pa. 9]</span>, kāsāyaṃ vā. evaṃ kosumbhaṃ, haliddiyā rattaṃ hāliddaṃ, pāṭaṅgena rattaṃ pāṭaṅgaṃ, mañjiṭṭhaṃ, kuṅkumaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-443-444-');
INSERT INTO pages VALUES(54702,'annya_sadda_10',295,replace(replace('\r\n<p class="bodytext">"nīlaṃ <a name="M0.0295"></a> vatthaṃ, pītaṃ vattha"ntiādīsu pana nīla, pītādisaddā guṇasaddattā paccayena vinā guṇanissayaṃ dabbaṃ vadanti. evaṃ sabbesu guṇasadda, jātisadda, nāmasaddesu.</p>\r\n<p class="bodytext"><a name="para445"></a><span class="paranum">445</span>. <span class="bld">nakkhatteninduyuttena kāle</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.5; pā. 4.2.3]</span>.</p>\r\n<p class="bodytext">induyuttena nakkhattena lakkhite kāle tannakkhattavācīhi ṇo hoti.</p>\r\n<p class="bodytext">puṇṇacandayuttena phussanakkhattena lakkhitā phussā, ratti, phusso, aho. evaṃ māgho iccādi.</p>\r\n<p class="bodytext">nakkhattenāti kiṃ? garugahena lakkhitā ratti.</p>\r\n<p class="bodytext">induyuttenāti kiṃ? phussena lakkhito muhutto.</p>\r\n<p class="bodytext">kāleti kiṃ? phussena lakkhitā atthasiddhi.</p>\r\n<p class="bodytext"><a name="para446"></a><span class="paranum">446</span>. <span class="bld">sāssa devatā puṇṇamāsī</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.18, 19 .pe.. 4.2.21-24]</span>.</p>\r\n<p class="bodytext">sā assa devatā, sā assa puṇṇamāsīti atthe paṭhamantā ṇo hoti.</p>\r\n<p class="bodytext">buddhā assa devatāti buddhā, yo buddhaṃ attano ārakkhadevataṃ viya garuṃ katvā vicarati, nirantaraṃ vā "buddhā buddhā"ti vācaṃ nicchāreti, tassetaṃ nāmaṃ. sugato assa devatāti sogato, mahindadevo assa devatāti māhindo. evaṃ yāmo, somo, vāruṇo.</p>\r\n<p class="bodytext">phussī assa puṇṇamāsīti phusso, māso. evaṃ māgho, phagguno, citto, vesākho, jeṭṭhamūlo, āsaḷho, sāvaṇo, poṭṭhapādo, assayujo, kattiko, māgasiro.</p>\r\n<p class="bodytext">puṇṇamāsīti kiṃ? phussī assa pañcamī tithī.</p>\r\n<p class="bodytext"><a name="para447"></a><span class="paranum">447</span>. <span class="bld">tamadhīte taṃ jānāti kaṇikā ca</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 3.1.37 .pe.. 4.2.59]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-445-446-447-');
INSERT INTO pages VALUES(54703,'annya_sadda_10',296,replace(replace('\r\n<p class="bodytext">etesu <a name="M0.0296"></a> atthesu dutiyantā ṇo ca ko ca ṇiko cāti ete paccayā honti.</p>\r\n<p class="bodytext">ṇamhi – byākaraṇaṃ adhīte jānāti vā veyyākaraṇo <span class="note">[dī. ni. 1.256]</span>. pakkhe "veyyākaraṇiko, byañjanaṃ adhīte jānāti vā veyyañjaniko"ti imāni ṇikena sijjhanti.</p>\r\n<p class="bodytext">kamhi-chandaṃ adhīte jānāti vā chando. evaṃ padako <span class="note">[dī. ni. 1.256]</span>, nāmako.</p>\r\n<p class="bodytext">ṇikamhi-vinayaṃ adhīte jānāti vā venayiko, suttantiko, ābhidhammiko.</p>\r\n<p class="bodytext">ettha ca ‘veyyākaraṇo’ti pade viyyākaraṇaṃ adhīteti vākyaṃ, ‘veyañjaniko’ti pade viyañjanaṃ adhīteti. upari ‘dovāriko, sovaggika’nti padesupi ‘duvāre niyutto, suvaggāya saṃvattatī’ti vākyaṃ, atthaṃ kathentena pana duvāra, suvaggasaddānaṃ taddhitabhāve eva siddhattā dvāreti ca saggāyāti ca kathetabbo.</p>\r\n<p class="bodytext"><a name="para448"></a><span class="paranum">448</span>. <span class="bld">tassa visaye dese</span> <span class="note">[ka. 352; rū. 376; nī. 365; caṃ. 3.1.61; pā. 4.2.52, 53]</span>.</p>\r\n<p class="bodytext">tassa desarūpe visaye chaṭṭhuntā ṇo hoti.</p>\r\n<p class="bodytext">thūyamigā vasātino nāma, vasātīnaṃ visayo deso vāsāto. idha adesarūpattā ṇo na hoti, cakkhussa visayo rūpaṃ.</p>\r\n<p class="bodytext"><a name="para449"></a><span class="paranum">449</span>. <span class="bld">nivāse tannāme</span> <span class="note">[ka. 352; rū. 376; nī. 365; caṃ. 3.1.64; pā. 4.2.69]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-448-449-');
INSERT INTO pages VALUES(54704,'annya_sadda_10',297,replace(replace('\r\n<p class="bodytext">tannāmabhūte <a name="M0.0297"></a> nivāse chaṭṭhyantā ṇo hoti.</p>\r\n<p class="bodytext">sivīnaṃ nivāso deso sebyo. vasaṃ adenti bhakkhantīti vasādā, byagghā sīhā vā, vasādānaṃ nivāso deso vāsādo.</p>\r\n<p class="bodytext"><a name="para450"></a><span class="paranum">450</span>. <span class="bld">anubhave</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.65; pā. 4.2.70]</span>.</p>\r\n<p class="bodytext">samīpe bhavaṃ anubhavaṃ, "adūrabhave"tipi pāṭho, tannāme anubhave dese chaṭṭhyantā ṇo hoti.</p>\r\n<p class="bodytext">vidisāya anubhavaṃ vedisaṃ, nagaraṃ, udumbarassa anubhavaṃ odumbaraṃ, vimānaṃ.</p>\r\n<p class="bodytext"><a name="para451"></a><span class="paranum">451</span>. <span class="bld">tena nibbatte</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.66; pā. 4.2.68]</span>.</p>\r\n<p class="bodytext">tannāme tena nibbatte dese tatiyantā ṇo hoti.</p>\r\n<p class="bodytext">kusambena nāma isinā nibbattā kosambī, itthiyaṃ ī, nagarī. evaṃ kākandī, mākandī, sahassena dhanena nibbattā sāhassī, parikhā, ayañca tatiyā hetumhi kattari karaṇe ca yathāyogaṃ yujjati.</p>\r\n<p class="bodytext"><a name="para452"></a><span class="paranum">452</span>. <span class="bld">tamidhatthi</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.67; pā. 4.2.67]</span>.</p>\r\n<p class="bodytext">taṃ idha atthīti atthe tannāme dese paṭhamantā ṇo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-450-451-452-');
INSERT INTO pages VALUES(54705,'annya_sadda_10',298,replace(replace('\r\n<p class="bodytext">udumbarā <a name="M0.0298"></a> asmiṃ dese santīti odumbaro, badarā asmiṃ dese santīti bādaro. evaṃ pabbajo.</p>\r\n<p class="bodytext"><a name="para453"></a><span class="paranum">453</span>. <span class="bld">tatra bhave</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.2.48; pā. 4.2.133]</span>.</p>\r\n<p class="bodytext">tatra bhavatthe sattamyantā ṇo hoti.</p>\r\n<p class="bodytext">udake bhavo odako, maccho, urasi bhavo oraso, putto, sāgamo. nagare bhavo nāgaro. evaṃ jānapado, māgadho, kapilavatthumhi bhavo kāpilavatthavo, ‘uvaṇṇassāvaṅa.’ti ussa avattaṃ. kosambiyaṃ bhavo kosambo, mitte bhavā mettā, pure bhavā porī <span class="note">[dī. ni. 1.9]</span>, vācā, manasmiṃ bhavo mānaso. ettha pana –</p>\r\n<p class="bodytext"><a name="para454"></a><span class="paranum">454</span>. <span class="bld">manādīnaṃ saka</span> <span class="note">[ka. 404; rū. 370; nī. 859]</span>.</p>\r\n<p class="bodytext">ṇānubandhe paccaye pare manādīnaṃ ante sāgamo hotīti sabbattha manogaṇādīnaṃ ante sāgamo.</p>\r\n<p class="bodytext">mānaso, rāgo, mānasā, taṇhā, mānasaṃ, sukhaṃ. evaṃ cetaso, cetasā, cetasaṃ. kvaci mano eva mānasaṃ, ceto eva cetasotipi yujjati.</p>\r\n<p class="bodytext"><a name="para455"></a><span class="paranum">455</span>. <span class="bld">ajjādīhi tano</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.15; pā. 4.2.105]</span>.</p>\r\n<p class="bodytext">tatra bhavoti atthe ajjādīhi tano hoti.</p>\r\n<p class="bodytext">ajja bhavo ajjatano, attho, ajjattanī, vibhatti, dvittaṃ, ajjatanaṃ, hitaṃ, sve bhavo svātano, mahāvuttinā essa āttaṃ. hiyyo bhavo hiyyattano, hiyyattano, hiyyattanaṃ, ossa attaṃ dvittañca.</p>\r\n<p class="bodytext"><a name="para456"></a><span class="paranum">456</span>. <span class="bld">purāto ṇo ca</span> <span class="note">[ka. 352; rū. 376; nī. 765]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-453-454-455-456-');
INSERT INTO pages VALUES(54706,'annya_sadda_10',299,replace(replace('\r\n<p class="bodytext">tatra <a name="M0.0299"></a> bhavoti atthe purāsaddamhā ṇo ca tano ca honti.</p>\r\n<p class="bodytext">pure bhavo purāṇo, idha ṇo anubandho na hoti, porāṇo vā, purātano.</p>\r\n<p class="bodytext"><a name="para457"></a><span class="paranum">457</span>. <span class="bld">amātvacco</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.2.13; pā. 4.2.104]</span>.</p>\r\n<p class="bodytext">amāsaddamhā bhavatthe acco hoti. ‘amā’ti sahatthavācī.</p>\r\n<p class="bodytext">rājakiccesu raññā saha bhavatīti amacco <span class="note">[dī. ni. ṭī. 1.339]</span>.</p>\r\n<p class="bodytext"><a name="para458"></a><span class="paranum">458</span>. <span class="bld">majjhādīhimo</span> <span class="note">[ka. 353; rū. 378; nī. 767; caṃ. 3.2.82; pā. 4.3.8, 22; ‘majjhāditvimo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">majjhādīhi bhavatthe imo hoti.</p>\r\n<p class="bodytext">majjhe bhavo majjhimo. evaṃ antimo, heṭṭhimo, uparimo, orimo, pacchimo, abbhantarimo, paccantimo, puratthimo.</p>\r\n<p class="bodytext"><a name="para459"></a><span class="paranum">459</span>. <span class="bld">kaṇa ṇeyya ṇeyyaka yiyā</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.2.4, 5, 6 .pe.. 4.2.94, 95, 97, 119-130]</span>.</p>\r\n<p class="bodytext">bhavatthe sattamyantā ete pañca paccayā honti.</p>\r\n<p class="bodytext">kaṇa-kusinārāyaṃ bhavo kosinārako, magadhesu bhavo māgadhako, āraññako, vihāro.</p>\r\n<p class="bodytext">ṇeyya-gaṅgāyaṃ bhavo gaṅgeyyo, pabbateyyo, vane bhavo vāneyyo.</p>\r\n','\r',char(13)),'\n',char(10)),'-457-458-459-');
INSERT INTO pages VALUES(54707,'annya_sadda_10',300,replace(replace('\r\n<p class="bodytext">ṇeyyaka-kosaleyyako<a name="M0.0300"></a>, bārāṇaseyyako, campeyyako, mithileyyako, idha na vuddhi.</p>\r\n<p class="bodytext">ya-gāme bhavo gammo, dive bhavo dibbo,</p>\r\n<p class="bodytext">iya-gāmiyo, gāmiko, yassa kattaṃ, udare bhavo odariyo, odariko, dive bhavo diviyo, pañcāliyo, bodhipakkhiyo, lokiyo.</p>\r\n<p class="bodytext"><a name="para460"></a><span class="paranum">460</span>. <span class="bld">ṇiko</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 3.2.40, 41, 42; pā. 4.2.126, 127, 128]</span>.</p>\r\n<p class="bodytext">bhavatthe sattamyantā ṇiko hoti.</p>\r\n<p class="bodytext">sāradiko, divaso, sāradikā, ratti, sāradikaṃ, pupphaṃ. bhavasaddena cettha aññepi atthe upalakkheti, pabbatato pakkhandā nadī pabbateyyā, kimīnaṃ kose jātaṃ koseyyaṃ, vatthaṃ. evaṃ siveyyaṃ, bārāṇaseyyaṃ.</p>\r\n<p class="bodytext"><a name="para461"></a><span class="paranum">461</span>. <span class="bld">tamassa sippaṃ sīlaṃ paṇyaṃ paharaṇaṃ payojanaṃ</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 3.4.49-66 .pe.. 4.4.47-65]</span>.</p>\r\n<p class="bodytext">tamassa sippaṃ, tamassa sīlaṃ, tamassa paṇyaṃ, tamassa paharaṇaṃ, tamassa payojananti atthesu paṭhamantā ṇiko hoti.</p>\r\n<p class="bodytext">sippe – vīṇāvādanamassa sippaṃ veṇiko. ettha ca vīṇāsaddena vīṇāvādanaṃ vuccati. ekatthībhāvasāmatthiyañhetaṃ. evaṃ sabbattha. evaṃ modiṅgiko, pāṇaviko, vaṃsiko.</p>\r\n','\r',char(13)),'\n',char(10)),'-460-461-');
INSERT INTO pages VALUES(54708,'annya_sadda_10',301,replace(replace('\r\n<p class="bodytext">sīle-paṃsukūladhāraṇaṃ assa sīlaṃ paṃsukūliko, paṃsukūlaṃ dhāretuṃ sīlamassāti vā paṃsukūliko. sīlasaddena cettha vata, dhamma, sādhukārāpi gayhanti, paṃsukūlaṃ dhāreti sīlenāti paṃsukūlikotipi yujjati. evaṃ tecīvariko<a name="M0.0301"></a>, piṇḍaṃ piṇḍaṃ patatīti piṇḍapāto, piṇḍācārena laddhabhojanaṃ, piṇḍapātayāpanaṃ assa sīlanti piṇḍapātiko, piṇḍapātena yāpetuṃ sīlañca vatañca dhammo ca garukāro ca assāti piṇḍapātiko.</p>\r\n<p class="bodytext">‘paṇya’nti vikkeyyavatthu vuccati, gandho paṇyaṃ assāti gandhiko. evaṃ teliko, goḷiko.</p>\r\n<p class="bodytext">paharanti etenāti paharaṇaṃ, āvudhabhaṇḍaṃ, cāpo paharaṇamassāti cāpiko. evaṃ tomariko, muggariko.</p>\r\n<p class="bodytext">payojanaṃ vuccati phalaṃ, upadhi payojanamassāti opadhikaṃ <span class="note">[a. ni. 8.59]</span>, puññaṃ, sataṃ payojanamassāti sātikaṃ. evaṃ sāhassikaṃ <span class="note">[jā. 2.21.415]</span>.</p>\r\n<p class="bodytext"><a name="para462"></a><span class="paranum">462</span>. <span class="bld">taṃ hantārahati gacchatuñchati carati</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 3.4.27-43; pā. 4.4.28-46]</span>.</p>\r\n<p class="bodytext">taṃ hanti, taṃ arahati, taṃ gacchati, taṃ uñchati, taṃ caratīti atthesu dutiyantā ṇiko hoti.</p>\r\n<p class="bodytext">pakkhīhi pakkhino hantīti pakkhiko. evaṃ sākuṇiko <span class="note">[a. ni. 2.263 (sākunikotipi dissati)]</span>, māyūriko, macchehi macche hanatīti macchiko. evaṃ dhenuko, magehi mage hanatīti māgaviko, majjhe vāgamo. evaṃ hāriṇiko, ‘hariṇo’ti mago eva. sūkariko, idha na vuddhi.</p>\r\n<p class="bodytext">sataṃ arahatīti sātikaṃ. evaṃ sāhassikaṃ.</p>\r\n<p class="bodytext">paradāraṃ gacchatīti pāradāriko, paradāriko vā. evaṃ pathiko, maggiko.</p>\r\n','\r',char(13)),'\n',char(10)),'-462-');
INSERT INTO pages VALUES(54709,'annya_sadda_10',302,replace(replace('\r\n<p class="bodytext">badare <a name="M0.0302"></a> uñchati gavesatīti bādariko. evaṃ āmalakiko.</p>\r\n<p class="bodytext">dhammaṃ caratīti dhammiko. evaṃ adhammiko.</p>\r\n<p class="bodytext"><a name="para463"></a><span class="paranum">463</span>. <span class="bld">tena kataṃ kītaṃ bandhaṃ abhisaṅkhataṃ saṃsaṭṭhaṃ hataṃ hanti jitaṃ jayati dibbati khaṇati tarati carati vahati jīvati</span> <span class="note">[ka. 350; rū. 373; nī. 364; caṃ. 3.4.1-26; pā. 4.4.1-27]</span>.</p>\r\n<p class="bodytext">tena kataṃ, tena kītaṃ.pe.. tena jīvati iccatthesu tatiyantā ṇiko hoti.</p>\r\n<p class="bodytext">kāyena kataṃ kāyikaṃ. evaṃ vācasikaṃ, mānasikaṃ, vātena kato vātiko, ābādho.</p>\r\n<p class="bodytext">satena mūlena kītaṃ bhaṇḍaṃ sātikaṃ. evaṃ sāhassikaṃ.</p>\r\n<p class="bodytext">varattāya yottāya bandhito vārattiko, ayasā bandhito āyasiko, sāgamo. evaṃ pāsiko.</p>\r\n<p class="bodytext">ghatena abhisaṅkhataṃ saṃsaṭṭhaṃ vā ghātikaṃ. evaṃ goḷikaṃ, dādhikaṃ, māricikaṃ.</p>\r\n<p class="bodytext">jālena hato jāliko, maccho.</p>\r\n<p class="bodytext">jālena hantīti jāliko, jālakevaṭṭo. evaṃ bāḷisiko <span class="note">[saṃ. ni. 2.158]</span>.</p>\r\n<p class="bodytext">akkhehi jitaṃ dhanaṃ akkhikaṃ. evaṃ sālākikaṃ.</p>\r\n<p class="bodytext">akkhehi jayati dibbatīti vā akkhiko.</p>\r\n<p class="bodytext">khaṇittiyā khaṇatīti khāṇittiko. evaṃ kuddāliko, idha na vuddhi.</p>\r\n','\r',char(13)),'\n',char(10)),'-463-');
INSERT INTO pages VALUES(54710,'annya_sadda_10',303,replace(replace('\r\n<p class="bodytext">uḷumpena <a name="M0.0303"></a> taratīti oḷumpiko, uḷumpiko vā. evaṃ nāviko <span class="note">[jā. 2.20.149]</span>, gopucchiko.</p>\r\n<p class="bodytext">sakaṭena caratīti sākaṭiko. evaṃ rathiko, yāniko, daṇḍiko,</p>\r\n<p class="bodytext">khandhena vahatīti khandhiko. evaṃ aṃsiko, sīsiko, idha na vuddhi.</p>\r\n<p class="bodytext">vetanena jīvatīti vetaniko. evaṃ bhatiko, kasiko, kayiko, vikkayiko, bhatikādīsu na vuddhi.</p>\r\n<p class="bodytext"><a name="para464"></a><span class="paranum">464</span>. <span class="bld">tassa saṃvattati</span> <span class="note">[ka. 351; rū. 374; nī. 765; caṃ. 3.4.67-69; pā. 4.4.66-68]</span>.</p>\r\n<p class="bodytext">tassa saṃvattatīti atthe catutthyantā ṇiko hoti.</p>\r\n<p class="bodytext">puna bhavāya saṃvattatīti ponobbhaviko, punassa ottaṃ, bhassa dvittaṃ, ponobbhavikā <span class="note">[mahāva. 14]</span>, taṇhā, lokāya saṃvattatīti lokiko, suṭṭhu aggoti saggo, saggāya saṃvattatīti sovaggikaṃ <span class="note">[dī. ni. 1.163]</span>, puññaṃ. evaṃ diṭṭhadhammikaṃ, samparāyikaṃ.</p>\r\n<p class="bodytext"><a name="para465"></a><span class="paranum">465</span>. <span class="bld">tato sambhūtamāgataṃ</span> <span class="note">[ka. 351; rū. 374; nī. 765; caṃ. 3.3.49-51; .pe.. 4.3.77-79]</span>.</p>\r\n<p class="bodytext">tato sambhūtaṃ, tato āgataṃ iccatthesu pañcamyantā ṇiko hoti.</p>\r\n<p class="bodytext">mātito sambhūtaṃ āgataṃ vā mattikaṃ <span class="note">[pārā. 34]</span>, dvittaṃ rasso ca. evaṃ pettikaṃ. ṇya, ṇiyāpi dissanti, surabhito sambhūtaṃ sorabhyaṃ, thanato sambhūtaṃ thaññaṃ, pitito sambhūto pettiyo. evaṃ mattiyo. ṇyamhi-macco.</p>\r\n<p class="bodytext"><a name="para466"></a><span class="paranum">466</span>. <span class="bld">tattha vasati vidito bhatto niyutto</span> <span class="note">[ka. 351; rū. 374; nī. 765; caṃ. 3.4.70-75 .pe.. 4.4.69-74]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-464-465-466-');
INSERT INTO pages VALUES(54711,'annya_sadda_10',304,replace(replace('\r\n<p class="bodytext">etesvatthesu <a name="M0.0304"></a> sattamyantā ṇiko hoti.</p>\r\n<p class="bodytext">rukkhamūle vasatīti rukkhamūliko. evaṃ āraññiko, sosāniko.</p>\r\n<p class="bodytext">ettha ca vasatīti sāmaññavacanepi tassīla, tabbata, taddhamma, tassādhukāritānaṃ vasena attho veditabbo taddhitapaccayānaṃ pasiddhatthadīpakattā. na hi rukkhamūle muhuttamattaṃ vasanto rukkhamūlikoti voharīyati.</p>\r\n<p class="bodytext">loke vidito lokiko.</p>\r\n<p class="bodytext">catumahārājesu bhattā cātumahārājikā <span class="note">[saṃ. ni. 5.1081]</span>.</p>\r\n<p class="bodytext">dvāre niyutto dovāriko. evaṃ bhaṇḍāgāriko, navakammiko, idha na vuddhi. "jātikiyo, andhakiyo" iccādīsu mahāvuttinā kiyo.</p>\r\n<p class="bodytext"><a name="para467"></a><span class="paranum">467</span>. <span class="bld">tassidaṃ</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 3.3.85-102; pā. 4.3.120-133]</span>.</p>\r\n<p class="bodytext">tassa idanti atthe chaṭṭhuntā ṇiko hoti.</p>\r\n<p class="bodytext">saṅghassa ayaṃ saṅghiko, vihāro, saṅghikā, bhūmi, saṅghassa idaṃ saṅghikaṃ, bhaṇḍaṃ. evaṃ puggalikaṃ, gaṇikaṃ, mahājanikaṃ, sakyaputtassa esoti sakyaputtiko. evaṃ nāṭaputtiko, dāsaputtiko.</p>\r\n<p class="bodytext"><a name="para468"></a><span class="paranum">468</span>. <span class="bld">ṇikassiyo vā</span> <span class="note">[ka. 404; rū. 370; nī. 756]</span>.</p>\r\n<p class="bodytext">ṇikapaccayassa iyo hoti vā.</p>\r\n<p class="bodytext">sakyaputtiyo, sakyaputtiko iccādi.</p>\r\n<p class="bodytext"><a name="para469"></a><span class="paranum">469</span>. <span class="bld">ṇo</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.3.85; pā. 4.3.120]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-467-468-469-');
INSERT INTO pages VALUES(54712,'annya_sadda_10',305,replace(replace('\r\n<p class="bodytext">tassidanti <a name="M0.0305"></a> atthe chaṭṭhuntā ṇo hoti.</p>\r\n<p class="bodytext">kaccāyanassa idaṃ kaccāyanaṃ, byākaraṇaṃ. evaṃ sogataṃ, sāsanaṃ, māhiṃsaṃ, maṃsādi.</p>\r\n<p class="bodytext"><a name="para470"></a><span class="paranum">470</span>. <span class="bld">gavādīhi yo</span> <span class="note">[ka. 353; rū. 378; nī. 781]</span>.</p>\r\n<p class="bodytext">tassidanti atthe gavādīhi yo hoti.</p>\r\n<p class="bodytext"><a name="para471"></a><span class="paranum">471</span>. <span class="bld">yamhi gossa ca</span> <span class="note">[ka. 78; rū. 31; nī. 229]</span>.</p>\r\n<p class="bodytext">yavante paccaye pare gossa ca uvaṇṇānañca avaṅa hotīti avādeso. ‘tavaggavaraṇāna.’nti suttena vassa battaṃ, gunnaṃ idaṃ gabyaṃ, maṃsādi, du vuccati rukkho, tassa idaṃ dabyaṃ, dabbaṃ, mūlādi.</p>\r\n<p class="centered">anekattharāsi niṭṭhito.</p>\r\n<p class="subhead">assatthirāsi</p>\r\n<p class="bodytext"><a name="para472"></a><span class="paranum">472</span>. <span class="bld">tametthassatthīti mantu</span> <span class="note">[ka. 369; rū. 403; nī. 793; caṃ. 4.2.98; pā. 5.2.94]</span>.</p>\r\n<p class="bodytext">taṃ ettha atthi, taṃ assa atthīti atthesu paṭhamantā mantupaccayo hoti, ivaṇṇu’vaṇṇo’kārehi mantu. tattha ivaṇṇantehi niccaṃ, upaṭṭhitā sati etasmiṃ atthi, etassa vā atthīti satimā. evaṃ gatimā, matimā, dhitimā <span class="note">[saṃ. ni. 1.1952; jā. 2.22.1451]</span>, atthadassimā <span class="note">[jā. 2.22.1451]</span>, sirīmā <span class="note">[jā. aṭṭha. 1.avidūrenidānakathā]</span> iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-470-471-472-');
INSERT INTO pages VALUES(54713,'annya_sadda_10',306,replace(replace('\r\n<p class="bodytext">uvaṇṇantehi pana <a name="M0.0306"></a> –</p>\r\n<p class="bodytext"><a name="para473"></a><span class="paranum">473</span>. <span class="bld">āyussāyasa mantumhi</span> <span class="note">[ka. 371; rū. 404; nī. 797]</span>.</p>\r\n<p class="bodytext">mantumhi pare āyussa āyasādeso hoti.</p>\r\n<p class="bodytext">dīghaṃ āyu asmiṃ atthi, assa vā atthīti āyasmā <span class="note">[mahāni. 49]</span>. evaṃ cakkhumā, bandhumā, bhāṇumā iccādi.</p>\r\n<p class="bodytext">bahū gāvo asmiṃ santi, assa vā santīti gomā. candimā, pāpimāpadesu ca mahāvuttinā imantupaccayaṃ icchanti. tattha candasaṅkhātaṃ vimānaṃ assa atthīti candimā <span class="note">[dha. pa. 387]</span>, devaputto, upacārena pana vimānampi candimāti vuccati, devaputtopi candoti vuccati. ati viya pāpo ajjhāsayo assa atthīti pāpimā <span class="note">[saṃ. ni. 1.137]</span>, māro. na hi appakena pāpena pāpimāti vuccati pahūtādivasena pasiddhe eva mantādīnaṃ pavattanato. vuttañhi vuttiyaṃ –</p>\r\n<p class="bodytext">"pahūte ca pasaṃsāyaṃ, nindāyañcātisāyane.</p>\r\n<p class="bodytext">niccayoge ca saṃsagge, hontime mantuādayo"ti <span class="note">[moga. 78]</span>.</p>\r\n<p class="bodytext">tattha pahūte-gomā <span class="note">[saṃ. ni. 1.12]</span>, dhanavāti.</p>\r\n<p class="bodytext">pasaṃsāyaṃ-jātimā, guṇavāti.</p>\r\n<p class="bodytext">nindāyaṃ-valimāti.</p>\r\n<p class="bodytext">atisāyane – buddhimā, vaṇṇavāti.</p>\r\n<p class="bodytext">niccayoge-satimā, sīlavā, daṇḍīti.</p>\r\n<p class="bodytext">saṃsagge – haliddimāti.</p>\r\n','\r',char(13)),'\n',char(10)),'-473-');
INSERT INTO pages VALUES(54714,'annya_sadda_10',307,replace(replace('\r\n<p class="bodytext">tathā vijjamānehi eva satiādīhi satimā iccādayo vuccanti, na atītehi anāgatehi ca avijjamānehi, kasmā<a name="M0.0307"></a>? atthisaddena paccuppannena niddiṭṭhattā. evaṃ pana sati kathaṃ pubbepi tvaṃ satimā āsi, anāgatepi satimā bhavissasīti idaṃ siddhanti? tadapi tadā vijjamānāya eva satiyā siddhanti.</p>\r\n<p class="bodytext">go, asso, manusso iccādīsu jātisaddesu tesaṃ dabbābhidhānasamatthattā mantādayo na honti, tathā nīlo paṭo, sukko paṭoiccādīsu guṇasaddesu tisso, phussoiccādīsu nāmasaddesu ca. yesaṃ pana dabbābhidhānasāmatthiyaṃ natthi, tesveva honti, buddhimā, paññavā, rūpavā, vaṇṇavā iccādi.</p>\r\n<p class="bodytext"><a name="para374"></a><span class="paranum">374</span>. <span class="bld">imiyā</span> <span class="note">[ka. 353; rū. 378; nī. 768]</span>.</p>\r\n<p class="bodytext">paṭhamantā mantvatthe ima, iyā honti.</p>\r\n<p class="bodytext">bahavo puttā assa asmiṃ vā santīti puttimo, patthaṭā kitti assa asmiṃ vā atthīti kittimo. evaṃ phalimo, khandhimo, rukkho, puttiyo, kappimo, kappiyo, jaṭimo, jaṭiyo, thiraṃ guṇajātaṃ assa asmiṃ vā atthīti theriyo, hānabhāgo assa atthi, asmiṃ vā vijjatīti hānabhāgiyo. evaṃ ṭhitibhāgiyo, visesabhāgiyo, nibbedhabhāgiyo iccādi.</p>\r\n<p class="bodytext">ettha ca pāḷiyaṃ candimā, puttimāsaddānaṃ simhi rājādigaṇarūpaṃ dissati, rattimābhāti candimā <span class="note">[dha. pa. 387]</span>, puttehi nandati puttimāti <span class="note">[saṃ. ni. 1.12]</span>. kittimāsaddassa pana kittimassa kittimatoti rūpanti.</p>\r\n<p class="bodytext"><a name="para475"></a><span class="paranum">475</span>. <span class="bld">vantvāvaṇṇā</span> <span class="note">[ka. 368; rū. 402; nī. 792; caṃ. 6.3.35 .pe.. 8.2.9]</span>.</p>\r\n<p class="bodytext">avaṇṇabhūtā paṭhamantā mantvatthe vantu hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-374-475-');
INSERT INTO pages VALUES(54715,'annya_sadda_10',308,replace(replace('\r\n<p class="bodytext">niccasīlavasena <a name="M0.0308"></a> visuddhaṃ sīlaṃ assa atthi, asmiṃ vā vijjatīti sīlavā, pasattho guṇo assa atthi, asmiṃ vā vijjatīti guṇavā.</p>\r\n<p class="bodytext">evaṃ sabbattha padatthānurūpaṃ mantvatthaviseso vattabbo, paṭisandhisahagatā paññā assa atthīti paññavā, paccaye pare dīghānaṃ kvaci rassattaṃ. vidati etenāti vido, ñāṇaṃ, vido etassa atthi, etasmiṃ vā vijjatīti vidvā, byañjane pubbassaralopo.</p>\r\n<p class="bodytext">avaṇṇāti kiṃ? satimā, bandhumā.</p>\r\n<p class="bodytext">bahulādhikārā rasmivā, lakkhivā, yasassivā, bhayadassivā, massuvā, gāṇḍīvadhanvātipi sijjhanti. tattha gāṇḍīvadhanu assa atthi, asmiṃ vā vijjatīti gāṇḍīvadhanvā, byañjane pubbassaralopo.</p>\r\n<p class="bodytext"><a name="para476"></a><span class="paranum">476</span>. <span class="bld">daṇḍādīhikaī vā</span> <span class="note">[ka. 366; rū. 400; nī. 790; caṃ. 4.2.118-121; pā. 5.2.115-6]</span>.</p>\r\n<p class="bodytext">tehi mantvatthe ika, ī honti vā.</p>\r\n<p class="bodytext">niccaṃ gahito daṇḍo assa atthi, asmiṃ vā vijjatīti daṇḍiko, daṇḍī, daṇḍavā. evaṃ gandhiko, gandhī, gandhavā, rūpiko, rūpī, rūpavā. iṇasāmike vattabbe dhanā iko, dhaniko, aññatra dhanī, dhanavā, atthiko, atthī, atthavā.</p>\r\n<p class="bodytext">ettha ca asannihitena atthena attho assa atthīti atthiko, mahagghena atthiko mahagghatthiko. evaṃ dhanatthiko, puññatthiko, seyyatthiko, ayaṃ attho etassāti idamatthī, pāṭavena attho assāti pāṭavatthī. evaṃ chekatthī, kusalatthīiccādīni sijjhanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-476-');
INSERT INTO pages VALUES(54716,'annya_sadda_10',309,replace(replace('\r\n<p class="bodytext">vaṇṇasaddantā pana īyeva hoti, brahmuno vaṇṇo saṇṭhānaṃ assa atthīti brahmavaṇṇī. atha vā brahmuno vaṇṇo brahmavaṇṇo<a name="M0.0309"></a>, brahmavaṇṇo viya vaṇṇo yassa so brahmavaṇṇī. evaṃ brahmavacchasī, ‘vacchasa’nti sīsaṃ, taddhitantasamāsapadaṃ nāmetaṃ. evaṃ devavaṇṇī.</p>\r\n<p class="bodytext">hattha, dantādīhi jātiyaṃ ī, hatthī, dantī, gajo, dāṭhī, kesarī, sīho, aññatra hatthavā, dantavā. brahmacārimhi vattabbe vaṇṇato īyeva, vaṇṇī, aññatra vaṇṇavā. pokkharādīhi dese īyeva, pokkharaṃ vuccati kamalaṃ, pokkharaṇī, puna itthiyaṃ nī, pubbaī-kārassa attaṃ, uppalinī, kumudinī, bhisinī, muḷālinī, sālukinī, padumaṃ ettha dese atthīti padumī, tato itthiyaṃ nī, paduminī, pubbaī-kārassa rassattaṃ, sabbaṃ kamalākarassa vā kamalagacchassa vā nāmaṃ, aññatra pokkharavā hatthī, idha soṇḍā pokkharaṃ nāma.</p>\r\n<p class="bodytext">sikhī, sikhāvā, mālī, mālāvā, sīlī, sīlavā, balī, balavā. samāsantepi ī, niddāsīlī, sabhāsīlī, bāhubalī, ūrubalī. sukha, dukkhehi īyeva, sukhī, dukkhī iccādi.</p>\r\n<p class="bodytext"><a name="para477"></a><span class="paranum">477</span>. <span class="bld">tapādīhi sī</span> <span class="note">[ka. 365; rū. 399; nī. 789; caṃ. 4.2.106; pā. 5.2.102; ‘. ssī’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tapādīhi mantvatthe sī hoti vā.</p>\r\n<p class="bodytext">tapo assa asmiṃ vā vijjatīti tapassī, dvittaṃ. evaṃ yasassī, tejassī, mano assa atthīti manassī.</p>\r\n<p class="bodytext">vātveva? yasavā.</p>\r\n<p class="bodytext"><a name="para478"></a><span class="paranum">478</span>. <span class="bld">ṇo tapā</span> <span class="note">[ka. 370; rū. 405; nī. 795; caṃ. 4.2.106; pā. 5.2.103]</span>.</p>\r\n<p class="bodytext">tapamhā mantvatthe ṇo hoti.</p>\r\n<p class="bodytext">tapo assa atthīti tāpaso, itthiyaṃ tāpasī.</p>\r\n<p class="bodytext"><a name="para479"></a><span class="paranum">479</span>. <span class="bld">mukhādito ro</span> <span class="note">[ka. 367; rū. 401; nī. 791; caṃ. 4.2.110, 111; pā. 5.2.106, 107]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-477-478-479-');
INSERT INTO pages VALUES(54717,'annya_sadda_10',310,replace(replace('\r\n<p class="bodytext">mukhādīhi <a name="M0.0310"></a> mantvatthe ro hoti.</p>\r\n<p class="bodytext">asaṃyataṃ mukhaṃ assa atthīti mukharo, susi assa atthīti susiro, rukkho, ūso khāro yasmiṃ atthīti ūsaro, khārabhūmippadeso, madhu raso assa atthīti madhuro, guḷo, nagā ettha santīti nagaro, bahupabbatappadeso, "nagara"ntipi pāṭho, kuñjo vuccati hanu, kuñjaro, hatthī, uṇṇatā dantā assa santīti danturo, hatthīyeva, mahāvuttinā assa uttaṃ.</p>\r\n<p class="bodytext"><a name="para480"></a><span class="paranum">480</span>. <span class="bld">tundyādīhi bho</span> <span class="note">[ka. 364; rū. 398; nī. 387; caṃ. 4.2.148; pā. 5.2.139]</span>.</p>\r\n<p class="bodytext">tundiiccādīhi mantvatthe bho hoti vā.</p>\r\n<p class="bodytext">tundi vuccati vuddhā nābhi, tundibho, valiyo etasmiṃ atthīti valibho.</p>\r\n<p class="bodytext">vātveva? tundimā.</p>\r\n<p class="bodytext"><a name="para481"></a><span class="paranum">481</span>. <span class="bld">saddhāditva</span> <span class="note">[ka. 370; rū. 405; nī. 795; caṃ. 4.2.105; pā. 5.2.101 (saddādivha?)]</span>.</p>\r\n<p class="bodytext">saddhādīhi mantvatthe a hoti.</p>\r\n<p class="bodytext">saddhā assa atthi, asmiṃ vā vijjatīti saddhā. evaṃ pañño, sato.</p>\r\n<p class="bodytext">vātveva? paññavā, satimā.</p>\r\n<p class="bodytext"><a name="para482"></a><span class="paranum">482</span>. <span class="bld">ālvābhijjhādīhi</span> <span class="note">[ka. 359; rū. 384; nī. 779; caṃ. 4.2.157; pā. 3.2.158]</span>.</p>\r\n<p class="bodytext">abhijjhādīhi mantvatthe ālu hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-480-481-482-');
INSERT INTO pages VALUES(54718,'annya_sadda_10',311,replace(replace('\r\n<p class="bodytext">abhijjhā <a name="M0.0311"></a> adhikā assa atthīti abhijjhālu, sītaladukkhaṃ adhikaṃ assa atthīti sītālu, dhajā bahulā asmiṃ rathe santīti dhajālu, dayā bahulā assāti dayālu, purisacittaṃ bahulaṃ assāti purisālu, purisalolā itthī.</p>\r\n<p class="bodytext">vātveva? dayāvā.</p>\r\n<p class="bodytext"><a name="para483"></a><span class="paranum">483</span>. <span class="bld">picchāditvilo</span> <span class="note">[ka. 364; rū. 398; nī. 787; caṃ. 4.2.102, 103; pā. 5.2.99, 100]</span>.</p>\r\n<p class="bodytext">picchādito mantvatthe ilo hoti vā.</p>\r\n<p class="bodytext">picchaṃ tūlaṃ assa atthi, tasmiṃ vā vijjatīti picchilo, picchavā, tūlarukkho, picchilā sippali <span class="note">[sippalī, sīmbalī, semmalītipi dissati]</span>, phenilo <span class="note">[pheṇilotipi dissati]</span>, phenavā, addāriṭṭhako, jaṭilo, jaṭāvā, tāpaso, tuṇḍilo, tuṇḍavā, adhikā vācā assa atthīti vācālo, mahāvuttinā ilopo.</p>\r\n<p class="bodytext"><a name="para484"></a><span class="paranum">484</span>. <span class="bld">sīlādito vo</span> <span class="note">[ka. 364; rū. 398; nī. 787; caṃ. 4.2.113; pā. 5.2.109]</span>.</p>\r\n<p class="bodytext">sīlādīhi mantvatthe vo hoti.</p>\r\n<p class="bodytext">niccarakkhitasīlaṃ assa atthi, asmiṃ vā vijjatīti sīlavo, puriso, sīlavā, itthī, kesā atidīghā asmiṃ santīti kesavo, kesavā, aparimāṇā aṇṇā udakā asmiṃ santīti aṇṇavo, mahantaṃ balaṃ assa atthīti balavo, balavā, balavaṃ. gāṇḍī vuccati sandhi, bahavo gāṇḍī asmiṃ atthīti gāṇḍīvaṃ, dhanu <span class="note">[gaṇḍassa gaṇḍamigasiṅgassa ayaṃ gāṇḍī, so assa atthīti gāṇḍīti gāṇḍīvo. (pañcakāṭīkā). gāṇḍīmeṇḍasiṅgamassa atthīti gāṇḍīvaṃ, dhanu. (payogasiddhi). gāṇḍī ganthi, so atthi assa asmiṃ vā gāṇḍīvo ajjunadhanu, (mugdhabodhaṭīkā)]</span>, bahukā rājī assa atthīti rājīvaṃ, paṅkajaṃ.</p>\r\n<p class="bodytext"><a name="para485"></a><span class="paranum">485</span>. <span class="bld">māyāmedhāhi vī</span> <span class="note">[ka. 364; rū. 398; nī. 787; caṃ. 4.2.137; pā. 5.2.121]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-483-484-485-');
INSERT INTO pages VALUES(54719,'annya_sadda_10',312,replace(replace('\r\n<p class="bodytext">etehi <a name="M0.0312"></a> mantvatthe vī hoti.</p>\r\n<p class="bodytext">māyāvī, medhāvī.</p>\r\n<p class="bodytext"><a name="para486"></a><span class="paranum">486</span>. <span class="bld">issare āmyuvāmī</span> <span class="note">[ka. 364; rū. 398; nī. 787; caṃ. 4.2.143; pā. 5.2.126; "sissare." (bahūsu)]</span>.</p>\r\n<p class="bodytext">issarabhūte mantvatthe āmī, uvāmī honti.</p>\r\n<p class="bodytext">issariyaṭṭhānabhūtaṃ saṃ assa atthīti sāmī, suvāmī <span class="note">[su. ni. 671]</span>, itthiyaṃ sāminī, suvāminī, bindulopo, kāgame sāmiko.</p>\r\n<p class="bodytext"><a name="para487"></a><span class="paranum">487</span>. <span class="bld">lakkhyā ṇo a ca</span> <span class="note">[ka. 364; rū. 398; nī. 787; pā. 5.2.100]</span>.</p>\r\n<p class="bodytext">lakkhīmhā mantvatthe ṇo hoti, īkārassa attañca hoti.</p>\r\n<p class="bodytext">lakkhī sirī etassa atthīti lakkhaṇo.</p>\r\n<p class="bodytext"><a name="para488"></a><span class="paranum">488</span>. <span class="bld">aṅgā no kalyāṇe</span> <span class="note">[ka. 364; rū. 398; nī. 787; pā. 5.2.100]</span>.</p>\r\n<p class="bodytext">kalyāṇe vattabbe aṅgamhā mantvatthe no hoti.</p>\r\n<p class="bodytext">kalyāṇaṃ aṅgaṃ etissā itthiyā atthīti aṅganā.</p>\r\n<p class="bodytext"><a name="para489"></a><span class="paranum">489</span>. <span class="bld">so lomā</span> <span class="note">[ka. 364; rū. 398; nī. 787; caṃ. 4.2.104; pā. 5.2.100]</span>.</p>\r\n<p class="bodytext">lomamhā mantvatthe sapaccayo hoti.</p>\r\n<p class="bodytext">bahūni lomāni assa santīti lomaso. ettha ‘so’ti suttavibhāgena "sumedhaso, bhūrimedhaso" iccādīnipi sijjhanti.</p>\r\n<p class="centered">assatthirāsi niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-486-487-488-489-');
INSERT INTO pages VALUES(54720,'annya_sadda_10',313,replace(replace('\r\n<p class="subhead">bhāva, kammarāsi</p>\r\n\r\n<p class="bodytext"><a name="para490"></a><span class="paranum">490</span>. <span class="bld">tassa</span> <a name="M0.0313"></a> <span class="bld">bhāvakammesu tta tā ttana ṇya ṇeyyaṇiya ṇa iyā</span> <span class="note">[ka. 360; rū. 387; nī. 780; caṃ. 4.1.136-153; pā. 5.1.119-136]</span>.</p>\r\n<p class="bodytext">tassa bhāvo, tassa kammanti atthe chaṭṭhuntā ete aṭṭha paccayā bahulaṃ bhavanti.</p>\r\n<p class="bodytext">tattha bhavanti buddhi, saddā etasmāti bhāvo, saddānaṃ attano atthesu ādimhi uppattikāraṇaṃ, cirakālaṃ pavattikāraṇañca. tattha ādimhi pavattikāraṇaṃ <span class="bld">byappattinimittaṃ</span> nāma. cirakālaṃ pavattikāraṇaṃ <span class="bld">pavattinimittaṃ</span> nāma. tadubhayampi jāti, dabba, guṇa, kriyā, nāmavasena pañcavidhaṃ hoti.</p>\r\n<p class="bodytext">tattha "gossa bhāvo gotta"nti ettha gojāti bhāvo nāma.</p>\r\n<p class="bodytext">"daṇḍino bhāvo daṇḍitta"nti ettha daṇḍadabbaṃ bhāvo nāma.</p>\r\n<p class="bodytext">"nīlassa paṭassa bhāvo nīlatta"nti ettha nīlaguṇo bhāvo nāma.</p>\r\n<p class="bodytext">"pācakassa bhāvo pācakatta"nti ettha pacanakriyā bhāvo nāma.</p>\r\n<p class="bodytext">"tissanāmassa janassa bhāvo tissatta"nti ettha tissanāmaṃ bhāvo nāma.</p>\r\n<p class="bodytext">tattha gossa bhāvoti gosaddaṃ sutvā godabbe gobuddhiyā vā godabbaṃ disvā tasmiṃ dabbe govohārassa vā pavattikāraṇanti attho. evaṃ sesesupi yathānurūpaṃ attho veditabbo.</p>\r\n<p class="bodytext">ttamhi-gottaṃ, daṇḍittaṃ, pācakattaṃ, tissattaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-490-');
INSERT INTO pages VALUES(54721,'annya_sadda_10',314,replace(replace('\r\n<p class="bodytext">tāmhi-saṅgaṇikārāmatā<a name="M0.0314"></a>, niddārāmatā, bhassārāmatā iccādi.</p>\r\n<p class="bodytext">ttanamhi-puthujjanattanaṃ, vedanattanaṃ, jārattanaṃ, jāyattanaṃ iccādi.</p>\r\n<p class="bodytext">ṇyamhi-alasassa bhāvo ālasyaṃ, samaṇassa bhāvo sāmaññaṃ, brāhmaṇassa bhāvo brāhmaññaṃ, sīlasamādhipaññāguṇo sāmaññañca brāhmaññañca nāma. tāpasassa bhāvo tāpasyaṃ, nipuṇassa bhāvo nepuññaṃ, vipulassa bhāvo vepullaṃ, rañño bhāvo rajjaṃ, āpabbatassa khettassa bhāvo āpabbatyaṃ, dāyādassa bhāvo dāyajjaṃ, visamassa bhāvo vesammaṃ, sakhino bhāvo sakhyaṃ, vāṇijānaṃ bhāvo vāṇijjaṃ iccādi.</p>\r\n<p class="bodytext">ṇeyyamhi – sucissa bhāvo soceyyaṃ. evaṃ ādhipateyyaṃ iccādi.</p>\r\n<p class="bodytext">ṇiyamhi-ālasiyaṃ, madabhāvo madiyaṃ. evaṃ dakkhiyaṃ, purohitabhāvo porohitiyaṃ, byattassa bhāvo veyyattiyaṃ, byāvaṭassa bhāvo veyyāvaṭiyaṃ, imāni dve pubbe veyyākaraṇapadaṃ viya siddhāni.</p>\r\n<p class="bodytext">ṇamhi-garuno bhāvo gāravo, paṭubhāvo pāṭavaṃ iccādi.</p>\r\n<p class="bodytext">iyamhi-adhipatibhāvo adhipatiyaṃ, paṇḍitabhāvo paṇḍitiyaṃ, bahussutabhāvo bahussutiyaṃ, naggassa bhāvo naggiyaṃ, sūrabhāvo sūriyaṃ, vīrabhāvo vīriyaṃ iccādi.</p>\r\n<p class="bodytext">kammatthe kammaṃ nāma kriyā, alasassa kammaṃ alasattaṃ, alasatā, alasattanaṃ, ālasyaṃ, ālaseyyaṃ, ālasiyaṃ, ālasaṃ, alasiyaṃ iccādi.</p>\r\n<p class="bodytext"><a name="para491"></a><span class="paranum">491</span>. <span class="bld">bya vaddhadāsā vā</span> <span class="note">[ka. 360; rū. 387; nī. 780]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-491-');
INSERT INTO pages VALUES(54722,'annya_sadda_10',315,replace(replace('\r\n<p class="bodytext">bhāva<a name="M0.0315"></a>, kammesu vaddha, dāsehi byo hoti vā.</p>\r\n<p class="bodytext">vaddhassa bhāvo kammaṃ vā vaddhabyaṃ, vaddhatā, dāsabyaṃ, dāsatā, ‘vaddhava’nti idha ṇe pare vāgamo.</p>\r\n<p class="bodytext"><a name="para492"></a><span class="paranum">492</span>. <span class="bld">naṇa yuvā bo ca vaye</span> <span class="note">[ka. 361; rū. 388; nī. 781; caṃ. 4.1.146; pā. 5.1.130; ‘.vassa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vaye gamyamāne bhāva, kammesu yuvato naṇa hoti vā bāgamo ca.</p>\r\n<p class="bodytext">yuvassa bhāvo yobbanaṃ.</p>\r\n<p class="bodytext">vātveva? yuvattaṃ, yuvatā.</p>\r\n<p class="bodytext"><a name="para493"></a><span class="paranum">493</span>. <span class="bld">aṇvādīhimo</span> <span class="note">[ka. 360; rū. 387; nī. 780; caṃ. 4.1.139; pā. 5.1.122; ‘aṇvāditvimo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tehi bhāve imo hoti vā.</p>\r\n<p class="bodytext">aṇuno bhāvo aṇimā, laghuno bhāvo laghimā.</p>\r\n<p class="bodytext"><a name="para494"></a><span class="paranum">494</span>. <span class="bld">kassamahatamime kasamahā</span> <span class="note">[ka. 404; rū. 370; ni. 859; ‘kassaka.’?]</span>.</p>\r\n<p class="bodytext">imapaccaye pare kassa, mahantasaddānaṃ kamena kasa, mahā honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-492-493-494-');
INSERT INTO pages VALUES(54723,'annya_sadda_10',316,replace(replace('\r\n<p class="bodytext">kassakassa kammaṃ kasimā <span class="note">[(kisamahatamime kasamahā. imapaccaye pare kisa, mahanthasaddānaṃ kamena kasa, mahā honthi. kisassa bhāvo kasimā, moga. 4-133)]</span>, mahantassa bhāvo mahimā. sumanassa bhāvo somanassaṃ, ṇyamhi sāgamo, ‘vaggalasehi te’ti yassa pararūpattaṃ. evaṃ domanassaṃ, sundaraṃ vaco <a name="M0.0316"></a> etasminti suvaco, suvacassa bhāvo sovacassaṃ. evaṃ dovacassaṃ.</p>\r\n<p class="bodytext">"ārāmarāmaṇeyyakaṃ, uyyānarāmaṇeyyakaṃ, bhūmirāmaṇeyyakaṃ" iccādīsu ramitabbanti ramaṇaṃ, ramaṇaṃ ettha atthīti rāmaṇo, ārāmo, ārāmarāmaṇassa bhāvo ārāmarāmaṇeyyakaṃ, ṇeyyo, sakatthe ca ko <span class="note">[kathaṃ rāmaṇiyakatthi? sakatthe kanthā ṇena siddhaṃ, (moga. 4-59)]</span>, ārāmasampatti, ārāmasirīti vuttaṃ hoti. evaṃ sesesu.</p>\r\n<p class="centered">bhāva, kammarāsi niṭṭhito.</p>\r\n<p class="subhead">parimāṇarāsi</p>\r\n<p class="bodytext"><a name="para495"></a><span class="paranum">495</span>. <span class="bld">tamassa parimāṇaṃ ṇiko ca</span> <span class="note">[ka. 351; rū. 374; nī. 764; caṃ. 4.1.62; pā. 5.1.57, 58]</span>.</p>\r\n<p class="bodytext">taṃ assa parimāṇanti atthe paṭhamantā ṇiko hoti ko ca. parimīyate anenāti parimāṇaṃ.</p>\r\n<p class="bodytext">doṇo parimāṇamassāti doṇiko. evaṃ khāriko, kumbhiko, asītivassāni parimāṇamassāti āsītiko, vayo. evaṃ nāvutiko, upaḍḍhakāyo parimāṇamassāti upaḍḍhakāyikaṃ, bimbohanaṃ, dve parimāṇamassāti dukaṃ. evaṃ tikaṃ, catukkaṃ, pañcakaṃ, chakkaṃ, dvittaṃ. dasakaṃ, satakaṃ.</p>\r\n<p class="bodytext"><a name="para496"></a><span class="paranum">496</span>. <span class="bld">yatetehi ttako</span> <span class="note">[ka. 391; rū. 423; nī. 830; pā. 5.1.22, 23]</span></p>\r\n<p class="bodytext">tamassa parimāṇanti atthe ya, ta, etasaddehi sadisadvibhūto ttako hoti.</p>\r\n<p class="bodytext">yaṃ parimāṇamassāti yattakaṃ. evaṃ tattakaṃ.</p>\r\n<p class="bodytext"><a name="para497"></a><span class="paranum">497</span>. <span class="bld">etasseṭa ttake</span> <span class="note">[ka. 404; rū. 370; nī. 859]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-495-496-497-');
INSERT INTO pages VALUES(54724,'annya_sadda_10',317,replace(replace('\r\n<p class="bodytext">ttake <a name="M0.0317"></a> pare etasaddassa eṭa hoti.</p>\r\n<p class="bodytext">etaṃ parimāṇamassāti ettakaṃ, yāva parimāṇamassāti yāvattakaṃ. evaṃ tāvattakaṃ, etāvattakaṃ. ‘yatetehī’ti vacanena yāva, tāva, etāvāpi gayhanti.</p>\r\n<p class="bodytext"><a name="para498"></a><span class="paranum">498</span>. <span class="bld">sabbā ca ṭāvantu</span> <span class="note">[ka. 391; rū. 423; nī. 830; caṃ. 4.2.43; pā. 5.2.39; ‘sabbā cacavantu’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tamassa parimāṇanti atthe ya, te’tehi ca sabbato ca ṭāvantu hoti, etassa dvittaṃ.</p>\r\n<p class="bodytext">sabbaṃ parimāṇaṃ assāti sabbāvantaṃ, sabbāvā, attho, sabbāvanto, sabbāvantā, atthā, sabbāvati, atthe, sabbāvantesu, atthesu, itthiyaṃ sabbāvatī, sabbāvantī, parisā. evaṃ yāvā, yāvantā, yāvanto, tāvā, tāvantā, tāvanto, ettāvā, ettāvantā, ettāvanto iccādi.</p>\r\n<p class="bodytext">kvaci mahāvuttinā ekassa ta-kārassa lopo, yāvatako kāyo, tāvatako byāmo <span class="note">[dī. ni. 3.200]</span>, yāvatikā yānassa bhūmi.</p>\r\n<p class="bodytext"><a name="para499"></a><span class="paranum">499</span>. <span class="bld">kiṃmhā rati rīva rīvataka rittakā</span> <span class="note">[ka. 391; rū. 423; nī. 830; caṃ. 4.2.45; pā. 5.2.41]</span>.</p>\r\n<p class="bodytext">taṃ assa parimāṇanti atthe kiṃsaddato ete cattāro paccayā bhavanti.</p>\r\n<p class="bodytext"><a name="para500"></a><span class="paranum">500</span>. <span class="bld">rānubandhentasarādissa</span> <span class="note">[ka. 539; rū. 558; nī. 1124]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-498-499-500-');
INSERT INTO pages VALUES(54725,'annya_sadda_10',318,replace(replace('\r\n<p class="bodytext">rānubandhe <a name="M0.0318"></a> paccaye pare padantasarādissa lopo hoti. ādisaddena padantabyañjanaṃ gayhati, suttavibhattena rīvantu, rittāvantupaccayā ca honti.</p>\r\n<p class="bodytext">kiṃ parimāṇamassāti kati. pañcakkhandhā kati kusalā, kati akusalā <span class="note">[vibha. 151]</span>, kativassosi tvaṃ bhikkhu, ekavasso ahaṃ bhagavā <span class="note">[mahāva. 78]</span>, kiṃ parimāṇaṃ assāti kīvaṃ, kiṃva dūro ito gāmo. evaṃ kīvatakaṃ, kittakaṃ.</p>\r\n<p class="bodytext">rīvantumhi – kīvanto hontu yācakā <span class="note">[jā. 2.20.103]</span> ti.</p>\r\n<p class="bodytext">rittāvantumhi – kittāvatā khandhānaṃ khandhapaññatti <span class="note">[saṃ. ni. 3.82]</span>, kittāvatā nu kho bhante rūpanti vuccati, kittāvatā nu kho bhante māroti vuccati <span class="note">[saṃ. ni. 3.161]</span>.</p>\r\n<p class="bodytext">pubbasuttena ṭāvantumhi – ettāvatā khandhānaṃ khandhapaññatti, ettāvatā rūpanti vuccati, ettāvatā māroti vuccati.</p>\r\n<p class="bodytext"><a name="para501"></a><span class="paranum">501</span>. <span class="bld">māne matto</span> <span class="note">[ka. 328; rū. 352; nī. 708; caṃ. 4.2.38; pā. 5.2.37]</span>.</p>\r\n<p class="bodytext">mīyate etenāti mānaṃ, taṃ ummānaṃ, parimāṇanti duvidhaṃ, uddhaṃ mānaṃ ummānaṃ, tadaññaṃ mānaṃ parimāṇaṃ, imasmiṃ sutte pana sāmaññavacanattā duvidhampi labbhati, duvidhe māne pavattā hatthādisaddamhā tamassa parimāṇanti atthe mattapaccayo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-501-');
INSERT INTO pages VALUES(54726,'annya_sadda_10',319,replace(replace('\r\n<p class="bodytext">hattho parimāṇaṃ assāti hatthamattaṃ, dve hatthā parimāṇaṃ assāti dvihatthamattaṃ, dve aṅguliyo parimāṇaṃ assāti dvaṅgulamattaṃ. evaṃ caturaṅgulamattaṃ, vidatthimattaṃ, yojanamattaṃ<a name="M0.0319"></a>, tīṇi yojanāni parimāṇaṃ assāti tiyojanamattaṃ, nāḷimattaṃ, patthamattaṃ, doṇamattaṃ, palaṃ vuccati ummānasaṅkhāto pātiviseso, palaṃ parimāṇaṃ assāti palamattaṃ, pañcamattaṃ, pañcamattehi bhikkhusatehi saddhiṃ <span class="note">[pārā. 1]</span>, tiṃsamattaṃ, saṭṭhimattaṃ, satamattaṃ, sahassamattaṃ, koṭimattaṃ, kumbhamattaṃ, cāṭimattaṃ, hatthimattaṃ, pabbatamattaṃ iccādi.</p>\r\n<p class="bodytext">‘mattā’ti vā parimāṇavācisaddantaraṃ, hattho mattā etassāti hatthamattaṃ. evaṃ dvihatthamattaṃ, iccādinā samāsopi yujjati. abhedūpacārena pana hatthaparimāṇaṃ hatthoti katvā "dvihatthaṃ vatthaṃ, doṇo vīhi, doṇo māso"ti sijjhati.</p>\r\n<p class="bodytext"><a name="para502"></a><span class="paranum">502</span>. <span class="bld">taggho cuddhaṃ</span> <span class="note">[ka. 328; rū. 352; nī. 708; caṃ. 4.2.39; pā. 5.2.37]</span>.</p>\r\n<p class="bodytext">uddhaṃmāne pavattā saddā tamassa parimāṇanti atthe tagghapaccayo hoti matto ca.</p>\r\n<p class="bodytext">jaṇṇu parimāṇamassāti jaṇṇutagghaṃ, jaṇṇumattaṃ.</p>\r\n<p class="bodytext"><a name="para503"></a><span class="paranum">503</span>. <span class="bld">ṇo ca purisā</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 4.2.40 .pe.. 5.2.38]</span>.</p>\r\n<p class="bodytext">uddhaṃmāne pavattā purisamhā ṇo ca hoti taggho ca matto ca.</p>\r\n','\r',char(13)),'\n',char(10)),'-502-503-');
INSERT INTO pages VALUES(54727,'annya_sadda_10',320,replace(replace('\r\n<p class="bodytext">catuhattho puriso parimāṇamassāti porisaṃ, tiporisaṃ, sataporisaṃ, gambhīraṃ. evaṃ purisatagghaṃ, purisamattaṃ, uddhaṃ pasāritahatthena saddhiṃ pañcahatthaṃ purisapamāṇaṃ porisanti vadanti, "ekūnatīso vayasā"ti <span class="note">[dī. ni. 2.214 (ekūnatiṃso)]</span> ettha ekūnatīsa vassāni āyuparimāṇaṃ assāti ekūnatīso. evaṃ <a name="M0.0320"></a> vīso, tīso, cattālīso, paññāso, sahasso brahmā, dvisahasso brahmā, dasasahasso brahmā. ettha ca "sahassaparimāṇaṃ cakkavāḷaṃ assāti sahasso"-iccādinā ṇapaccayena sijjhati.</p>\r\n<p class="centered">parimāṇarāsi niṭṭhito.</p>\r\n<p class="subhead">saṅkhyārāsi</p>\r\n<p class="bodytext"><a name="para504"></a><span class="paranum">504</span>. <span class="bld">ekā kākyasahāye</span> <span class="note">[ka. 391; rū. 423; nī. 835; caṃ. 4.2.67; pā. 5.3.52]</span>.</p>\r\n<p class="bodytext">asahāyatthe ekamhā ka, ākī honti vā.</p>\r\n<p class="bodytext">asahāyo eko, ekako, ekākī, eko vā.</p>\r\n<p class="bodytext">itthiyaṃ ekikā, ekākinī, ekā vā, ‘adhātussa ke’ti suttena itthiyaṃ kamhi pare assa ittaṃ.</p>\r\n<p class="bodytext"><a name="para505"></a><span class="paranum">505</span>. <span class="bld">dviti catūhi tīyatthā</span> <span class="note">[ka. 385; rū. 409; nī. 817; tisatthā?]</span>.</p>\r\n<p class="bodytext">tehi tassa pūraṇanti atthe tīyo ca ttho ca honti.</p>\r\n<p class="bodytext"><a name="para506"></a><span class="paranum">506</span>. <span class="bld">dvitīnaṃ dutā tīye</span> <span class="note">[ka. 386, 410; nī. 818; tiye?]</span>.</p>\r\n<p class="bodytext">tīye pare dvi, tisaddānaṃ du, tādesā honti.</p>\r\n<p class="bodytext">dvinnaṃ pūraṇo dutīyo <span class="note">[chaṭṭhasaṃgīti pāṭhesu dutiyotyādinā dissanti]</span>, dvinnaṃ pūraṇī dutīyā, dvinnaṃ pūraṇaṃ dutīyaṃ. evaṃ tatīyo, tatīyā, tatīyaṃ. catunnaṃ pūraṇo catuttho, catutthī, catutthaṃ.</p>\r\n<p class="bodytext"><a name="para507"></a><span class="paranum">507</span>. <span class="bld">ma pañcādikatihi</span> <span class="note">[ka. 373; rū. 406; nī. 802; caṃ. 4.2.55; pā. 5.2.49]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-504-505-506-507-');
INSERT INTO pages VALUES(54728,'annya_sadda_10',321,replace(replace('\r\n<p class="bodytext">pañcādīhi <a name="M0.0321"></a> ca katimhā ca tassa pūraṇanti atthe mo hoti.</p>\r\n<p class="bodytext">pañcamo, pañcamī, pañcamaṃ. evaṃ sattama, aṭṭhama, navama, dasama, ekādasamādi. katinnaṃ pūraṇo katimo, katimī, tithī.</p>\r\n<p class="bodytext"><a name="para508"></a><span class="paranum">508</span>. <span class="bld">tassa pūraṇekādasādito vā</span> <span class="note">[ka. 374; rū. 412; nī. 805; caṃ. 4.2.51; pā. 5.2.48]</span>.</p>\r\n<p class="bodytext">pūrate anenāti pūraṇaṃ, ekādasādito tassa pūraṇanti atthe ṭānubandho apaccayo hoti vā.</p>\r\n<p class="bodytext">ekādasannaṃ pūraṇo ekādaso, ekādasī, ekādasaṃ, ekādasamo vā. evaṃ dvādaso, dvādasamo, teraso, terasamo, cuddaso, cuddasamo, pañcadaso, pañcadasamo, pannaraso, pannarasamo, soḷaso, soḷasamo, sattaraso, sattarasamo, aṭṭhāraso, aṭṭhārasamo.</p>\r\n<p class="bodytext"><a name="para509"></a><span class="paranum">509</span>. <span class="bld">ṭe satissa tissa</span> <span class="note">[ka. 389; rū. 413; nī. 824]</span>.</p>\r\n<p class="bodytext">ṭe pare satissa ti-kārassa lopo hotīti vīsati, tīsatīnaṃ tissa lopo.</p>\r\n<p class="bodytext">ekūnavīso, ekūnavīsatimo, vīso, vīsatimo, tīso, tīsatimo, cattālīso, cattālīsamo, paññāso, paññāsamo. saṭṭhyādito purimasuttena mo, saṭṭhimo, sattatimo, asītimo, navutimo.</p>\r\n<p class="bodytext"><a name="para510"></a><span class="paranum">510</span>. <span class="bld">satādīnami ca</span> <span class="note">[ka. 373; rū. 406; nī. 802; caṃ. 4.2.53 .pe.. 5.2.57]</span>.</p>\r\n<p class="bodytext">satādito tassa pūraṇatthe mo hoti, satādīnaṃ antassa ittañca hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-508-509-510-');
INSERT INTO pages VALUES(54729,'annya_sadda_10',322,replace(replace('\r\n<p class="bodytext">satassa <a name="M0.0322"></a> pūraṇo satimo, dvisatimo, tisatimo, sahassimo.</p>\r\n<p class="bodytext"><a name="para511"></a><span class="paranum">511</span>. <span class="bld">chā ṭṭhaṭṭhamā</span> <span class="note">[ka. 384; rū. 407; nī. 803]</span>.</p>\r\n<p class="bodytext">chamhā tassa pūraṇanti atthe ṭṭha, ṭṭhamā honti.</p>\r\n<p class="bodytext">chaṭṭho, chaṭṭhī, chaṭṭhaṃ, chaṭṭhamo, chaṭṭhamī, chaṭṭhamaṃ.</p>\r\n<p class="bodytext"><a name="para512"></a><span class="paranum">512</span>. <span class="bld">saṅkhyāya saccutīsāsadasantāyādhikāsmiṃ satasahasse ṭa</span> <span class="note">[ka. 328; rū. 352; nī. 701; caṃ. 4.2.50 .pe.. 5.2.45, 46; ‘.ḍo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">sati, uti, īsa, āsa, dasantāhi saṅkhyāhi te adhikā asmiṃ satasahasseti atthe ṭānubandho apaccayo hoti.</p>\r\n<p class="bodytext">ettha ca ‘satasahasse’ti sate vā sahasse vāti attho.</p>\r\n<p class="bodytext">tattha sahassasaddena sahassaṃ dasasahassaṃ satasahassaṃ dasasatasahassañca gayhati.</p>\r\n<p class="bodytext">dasanta, satyanta, īsanta, āsanta, utyantāti evaṃ anukkamo veditabbo.</p>\r\n<p class="bodytext">tattha dasa, ekādasato paṭṭhāya yāva aṭṭhārasā navasaṅkhyā <span class="bld">dasantā</span> nāma.</p>\r\n<p class="bodytext">vīsati, ekavīsatito paṭṭhāya yāva aṭṭhavīsatiyā navasaṅkhyā ca tīsati, ekatīsatito paṭṭhāya yāva aṭṭhatīsatiyā navasaṅkhyā ca <span class="bld">satyantā</span> nāma.</p>\r\n<p class="bodytext">cattālīsa, ekacattālīsato paṭṭhāya yāva aṭṭhacattālīsāya navasaṅkhyā <span class="bld">īsantā</span> nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'-511-512-');
INSERT INTO pages VALUES(54730,'annya_sadda_10',323,replace(replace('\r\n<p class="bodytext">paññāsa<a name="M0.0323"></a>, ekapaññāsato paṭṭhāya yāva aṭṭhapaññāsāya navasaṅkhyā <span class="bld">āsantā</span> nāma.</p>\r\n<p class="bodytext">navuti, ekanavutito paṭṭhāya yāva aṭṭhanavutiyā nava saṅkhyā <span class="bld">utyantā</span> nāma.</p>\r\n<p class="bodytext">sesā ṭṭhunta, tyantāpi idha saṅgayhanti. <span class="bld">ṭṭhuntā</span> nāma saṭṭhi,-ekasaṭṭhyādikā navasaṅkhyā. <span class="bld">tyantā</span> nāma sattati, ekasattatyādikā navasaṅkhyā ca asīti, ekāsītyādikā navasaṅkhyā ca.</p>\r\n<p class="bodytext">dasantāsu tāva – dasa adhikā yasmiṃ sate tayidaṃ dasasataṃ. evaṃ dasasahassaṃ, dasasatasahassaṃ, ekādasa adhikā yasmiṃ sate tayidaṃ ekādasasataṃ. evaṃ ekādasasahassaṃ, ekādasasatasahassaṃ. evaṃ dvādasasatamiccādīni.</p>\r\n<p class="bodytext">satyantāsu – ṭamhi ti-kāralopo, vīsati adhikā yasmiṃ sate tayidaṃ vīsasataṃ. evaṃ ekavīsasataṃ, dvāvīsasataṃ iccādi, tīsati adhikā yasmiṃ sate tayidaṃ tīsasataṃ. evaṃ ekatīsasataṃ, dvattīsasataṃ iccādi. esa nayo sahassepi.</p>\r\n<p class="bodytext">īsantāsu – cattālīsaṃ adhikā yasmiṃ sate tayidaṃ cattālīsasataṃ. evaṃ ekacattālīsasataṃ, dvecattālīsasataṃ iccādi. esa nayo sahassepi.</p>\r\n<p class="bodytext">āsantāsu – paññāsaṃ adhikā yasmiṃ sate tayidaṃ paññāsasataṃ. evaṃ ekapaññāsasataṃ, dvepaññāsasataṃ iccādi. esa nayo sahassepi.</p>\r\n<p class="bodytext">ṭṭhuntāsu – saṭṭhi adhikā yasmiṃ sate tayidaṃ saṭṭhisataṃ. evaṃ ekasaṭṭhisataṃ, dvāsaṭṭhisataṃ iccādi. esa nayo sahassepi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54731,'annya_sadda_10',324,replace(replace('\r\n<p class="bodytext">tyantāsu <a name="M0.0324"></a> – sattati adhikā, ekasattati adhikā, asīti adhikā, ekāsīti adhikā iccādinā vattabbā.</p>\r\n<p class="bodytext">utyantāsu – navuti adhikā yasmiṃ sate tayidaṃ navutisataṃ. evaṃ ekanavutisataṃ, dvenavutisataṃ iccādi. esa nayo sahassepi.</p>\r\n<p class="bodytext">atha vā <span class="bld">dasantā</span> nāma ekato paṭṭhāya dasasaṅkhyā.</p>\r\n<p class="bodytext"><span class="bld">satyantā</span> nāma ekādasato paṭṭhāya vīsasaṅkhyā.</p>\r\n<p class="bodytext"><span class="bld">īsantā</span> nāma ekatīsato paṭṭhāya dasasaṅkhyā.</p>\r\n<p class="bodytext"><span class="bld">āsantā</span> nāma ekacattālīsato paṭṭhāya dasasaṅkhyā.</p>\r\n<p class="bodytext">evaṃ <span class="bld">ṭṭhunta, tyanta, utyantā</span>pi veditabbā.</p>\r\n<p class="bodytext">eko adhiko yasmiṃ sate tayidaṃ ekasataṃ, "athetthekasataṃ khatyā, anuyantā yasassino"ti <span class="note">[jā. 2.22.594]</span> pāḷi. "dve adhikā yasmiṃ sate tayidaṃ dvisataṃ" iccādinā sabbaṃ vattabbaṃ, suvicittamidaṃ vidhānanti.</p>\r\n<p class="bodytext">yathā pana "eko ca dasa ca ekādasa, ekādhikā vā dasa ekādasā"ti sijjhati, tathā idhapi "dasa ca satañca dasasataṃ, dasādhikaṃ vā sataṃ dasasata"ntiādinā vutte sabbaṃ taṃ vidhānaṃ samāsavasena sijjhati.</p>\r\n<p class="bodytext">tattha pana "dve satāni dvisataṃ, tīṇi satāni tisata"miccādīni ca "dve sahassāni dvisahassaṃ, tīṇi sahassāni tisahassa"miccādīni ca "dve satasahassāni dvisatasahassaṃ, tīṇi satasahassāni tisatasahassa"miccādīni ca digusamāse sijjhanti.</p>\r\n<p class="bodytext"><a name="para513"></a><span class="paranum">513</span>. <span class="bld">vārasaṅkhyāyakkhattuṃ</span> <span class="note">[ka. 646; rū. 419; nī. 1282; caṃ. 4.4.5; pā. 5.4.17]</span>.</p>\r\n<p class="bodytext">vārasambandhibhūtā saṅkhyāsaddā kkhattuṃpaccayo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-513-');
INSERT INTO pages VALUES(54732,'annya_sadda_10',325,replace(replace('\r\n<p class="bodytext">dve <a name="M0.0325"></a> vārā dvikkhattuṃ. evaṃ tikkhattuṃ, catukkhattuṃ, pañcakkhattuṃ, dasakkhattuṃ, satakkhattuṃ, sahassakkhattuṃ.</p>\r\n<p class="bodytext"><a name="para514"></a><span class="paranum">514</span>. <span class="bld">katimhā</span> <span class="note">[ka. 646; rū. 419; nī. 1282; caṃ. 4.4.6; pā. 5.4.20]</span>.</p>\r\n<p class="bodytext">vārasambandhibhūtā katisaddā kkhattuṃ hoti. kati vārā katikkhattuṃ.</p>\r\n<p class="bodytext"><a name="para515"></a><span class="paranum">515</span>. <span class="bld">bahumhā dhā ca paccāsattiyā</span> <span class="note">[ka. 646; rū. 419; nī. 1282; ‘paccāsattiyaṃ’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vārasambandhibhūtā bahusaddā paccāsattiyā sati dhā ca hoti kkhattuñca.</p>\r\n<p class="bodytext">bahuvārā bahukkhattuṃ, bahusaddena anekavāraṃ upalakkheti, anekavārā anekakkhattuṃ. evaṃ bahuvārā bahudhā, anekavārā anekadhā. paccāsatti nāma vārānaṃ accāsannatā vuccati, divasassa bahukkhattuṃ bhuñjati, bahudhā bhuñjati, vārānaṃ dūrabhāve sati te paccayā na honti, māsassa bahuvāre bhuñjati.</p>\r\n<p class="bodytext"><a name="para516"></a><span class="paranum">516</span>. <span class="bld">sakiṃ vā</span> <span class="note">[ka. 646; rū. 419; nī. 1282; caṃ. 4.4.8; pā. 5.4.19]</span>.</p>\r\n<p class="bodytext">ekavāranti atthe sakinti nipaccate vā.</p>\r\n<p class="bodytext">sakiṃ bhuñjati, ekavāraṃ bhuñjati.</p>\r\n<p class="centered">saṅkhyārāsi niṭṭhito.</p>\r\n<p class="subhead">khuddakarāsi</p>\r\n','\r',char(13)),'\n',char(10)),'-514-515-516-');
INSERT INTO pages VALUES(54733,'annya_sadda_10',326,replace(replace('\r\n<p class="subhead">pakārarāsi</p>\r\n\r\n<p class="bodytext"><a name="para517"></a><span class="paranum">517</span>. <span class="bld">dhā</span> <a name="M0.0326"></a> <span class="bld">saṅkhyāhi</span> <span class="note">[ka. 397; rū. 420; nī. 836; caṃ. 4.3.20; pā. 5.3.42]</span>.</p>\r\n<p class="bodytext">saṅkhyāvācīhi pakāre dhā hoti.</p>\r\n<p class="bodytext">dvīhi pakārehi dvidhā. evaṃ tidhā, catudhā, pañcadhā, dasadhā, satadhā, sahassadhā, bahudhā, ekadhā, anekadhā.</p>\r\n<p class="bodytext"><a name="para518"></a><span class="paranum">518</span>. <span class="bld">vekā jjhaṃ</span> <span class="note">[ka. 397; rū. 420; nī. 837; caṃ. 4.3.24; pā. 5.3.46]</span>.</p>\r\n<p class="bodytext">ekamhā pakāre jjhaṃ hoti vā.</p>\r\n<p class="bodytext">ekena pakārena ekajjhaṃ, ekadhā vā.</p>\r\n<p class="bodytext"><a name="para519"></a><span class="paranum">519</span>. <span class="bld">dvitīhedhā</span> <span class="note">[ka. 404; rū. 420-370; nī. 859; caṃ. 4.3.24; pā. 5.3.46]</span>.</p>\r\n<p class="bodytext">dvitisaddehi pakāre edhā hoti vā.</p>\r\n<p class="bodytext">dvedhā, tedhā, dvidhā, tidhā vā.</p>\r\n<p class="bodytext"><a name="para520"></a><span class="paranum">520</span>. <span class="bld">sabbādīhi pakāre thā</span> <span class="note">[ka. 398; rū. 421; nī. 844; caṃ. 4.3.26; pā. 5.3.69]</span>.</p>\r\n<p class="bodytext">bahubhedo vā sāmaññassa bhedako viseso vā pakāro, sabbādīhi pakāre thā hoti.</p>\r\n<p class="bodytext">sabbena pakārena sabbathā, sabbehi pakārehi sabbathā, yādisena pakārena yathā, yādisehi pakārehi yathā. evaṃ tathā, aññathā, ubhayathā, itarathā.</p>\r\n<p class="bodytext"><a name="para521"></a><span class="paranum">521</span>. <span class="bld">kathamitthaṃ</span> <span class="note">[ka. 399; rū. 422; nī. 845; pā. 5.3.24, 25]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-517-518-519-520-521-');
INSERT INTO pages VALUES(54734,'annya_sadda_10',327,replace(replace('\r\n<p class="bodytext">ete <a name="M0.0327"></a> saddā pakāre nipaccanti.</p>\r\n<p class="bodytext">kena pakārena kathaṃ, iminā pakārena itthaṃ. iminā suttena kiṃ, imasaddehi thaṃ, tthaṃpaccaye katvā kiṃssa kattaṃ, imassa ittañca kariyati.</p>\r\n<p class="bodytext"><a name="para522"></a><span class="paranum">522</span>. <span class="bld">tabbati jātiyo</span> <span class="note">[ka. 398; rū. 421; nī. 844; caṃ. 4.3.26; pā. 5.3.69]</span>.</p>\r\n<p class="bodytext">so pakāro assa atthīti tabbā, tasmiṃ tabbati, pakāravante dabbeti attho. taṃsāmaññavācimhā tabbati jātiyapaccayo hoti.</p>\r\n<p class="bodytext">visesena paṭurūpo paṇḍito paṭujātiyo. visesena mudurūpaṃ vatthu mudujātiyaṃ.</p>\r\n<p class="bodytext"><a name="para523"></a><span class="paranum">523</span>. <span class="bld">so vīcchāppakāresu</span> <span class="note">[ka. 397; rū. 420; nī. 836; caṃ. 4.4.2 .pe.. 5.4.43]</span>.</p>\r\n<p class="bodytext">vīcchāyaṃ pakāre ca sopaccayo hoti.</p>\r\n<p class="bodytext">vīcchāyaṃ –</p>\r\n<p class="bodytext">padaṃ padaṃ vāceti padaso vāceti. khaṇḍaṃ khaṇḍaṃ karoti khaṇḍaso karoti, bilaṃ bilaṃ vibhajjati bilaso vibhajjati iccādi. ettha ca ‘padaṃ padaṃ’ iccādīsu kriyāvisesane dutiyā.</p>\r\n<p class="bodytext">pakāre –</p>\r\n<p class="bodytext">bahūhi pakārehi puthuso, sabbehi pakārehi sabbaso iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-522-523-');
INSERT INTO pages VALUES(54735,'annya_sadda_10',328,replace(replace('\r\n<p class="bodytext">"yoniso <a name="M0.0328"></a> upāyaso, ṭhānaso, hetuso, atthaso, dhammaso, suttaso, anubyañjanaso" iccādīsu pana mahāvuttinā tatiyekavacanassa sottaṃ. tathā dīghaso, oraso iccādi.</p>\r\n<p class="centered">iti pakārarāsi.</p>\r\n<p class="subhead">kularāsi</p>\r\n<p class="bodytext"><a name="para524"></a><span class="paranum">524</span>. <span class="bld">pitito bhātari reyyaṇa</span> <span class="note">[ka. 352; rū. 376; nī. 765]</span>.</p>\r\n<p class="bodytext">pitusaddamhā tassa bhātāti atthe reyyaṇa hoti.</p>\r\n<p class="bodytext">pitu bhātā petteyyo <span class="note">[a. ni. 6.44]</span>. ‘rānubandhentasarādissā’ti ussa lopo, tassa dvittaṃ.</p>\r\n<p class="bodytext"><a name="para525"></a><span class="paranum">525</span>. <span class="bld">mātito ca bhaginiyaṃ cho</span> <span class="note">[ka. 352; rū. 376; nī. 765]</span>.</p>\r\n<p class="bodytext">mātito pitito ca bhaginiyaṃ cho hoti.</p>\r\n<p class="bodytext">mātu bhaginī mātucchā <span class="note">[udā. 22]</span>, pitu bhaginī pitucchā <span class="note">[saṃ. ni. 2.243]</span>.</p>\r\n<p class="bodytext"><a name="para526"></a><span class="paranum">526</span>. <span class="bld">mātāpitūsvāmaho</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.1.60; pā. 4.2.36]</span>.</p>\r\n<p class="bodytext">mātāpitūhi tesaṃ mātāpitūsu āmaho hoti.</p>\r\n<p class="bodytext">mātu mātā mātāmahī, mātu pitā mātāmaho <span class="note">[ma. ni. 2.411]</span>, pitu mātā pitāmahī, pitu pitā pitāmaho.</p>\r\n<p class="centered">iti kularāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-524-525-526-');
INSERT INTO pages VALUES(54736,'annya_sadda_10',329,replace(replace('\r\n<p class="subhead">hita, sādhu, araharāsi</p>\r\n\r\n<p class="bodytext"><a name="para527"></a><span class="paranum">527</span>. <span class="bld">hite</span> <a name="M0.0329"></a> <span class="bld">reyyaṇa</span> <span class="note">[ka. 352; rū. 376; nī. 765]</span>.</p>\r\n<p class="bodytext">mātāpitūhi tesaṃ hite reyyaṇa hoti.</p>\r\n<p class="bodytext">mātu hito metteyyo, pitu hito petteyyo. mātāpitūsu suppaṭipanno.</p>\r\n<p class="bodytext"><a name="para528"></a><span class="paranum">528</span>. <span class="bld">iyo hite</span> <span class="note">[ka. 356; rū. 381; nī. 773]</span>.</p>\r\n<p class="bodytext">tassa hitanti atthe iyo hoti.</p>\r\n<p class="bodytext">upādānānaṃ hitaṃ upādāniyaṃ. evaṃ oghaniyaṃ, yoganiyaṃ, ganthaniyaṃ, nīvaraṇiyaṃ, suttavibhattiyā aññatthesupi iyo, samānodare sayito sodariyo.</p>\r\n<p class="bodytext"><a name="para529"></a><span class="paranum">529</span>. <span class="bld">cakkhvādito sso</span> <span class="note">[ka. 353; rū. 378; nī. 767]</span>.</p>\r\n<p class="bodytext">tassa hitanti atthe cakkhvādīhi sso hoti.</p>\r\n<p class="bodytext">cakkhussa hitaṃ cakkhussaṃ <span class="note">[a. ni. 5.208]</span>, subharūpaṃ cakkhubhesajjañca. āyuno hitaṃ āyussaṃ <span class="note">[a. ni. 5.231]</span>, āyuvaḍḍhanavidhi.</p>\r\n<p class="bodytext"><a name="para530"></a><span class="paranum">530</span>. <span class="bld">ṇyo tattha sādhu</span> <span class="note">[ka. 353; rū. 378; nī. 767; caṃ. 3.4.100, 103; pā. 4.4.98, 103, 105]</span>.</p>\r\n<p class="bodytext">tasmiṃ sādhūti atthe ṇyo hoti.</p>\r\n<p class="bodytext">sabhāyaṃ sādhu sabbho, ‘sādhū’ti kusalo yogyo hito vā. mittānaṃ hitaṃ mettaṃ. suttavibhāgā aññatrapi ṇyo, rathaṃ vahatīti racchā, rathavīthi.</p>\r\n<p class="bodytext"><a name="para531"></a><span class="paranum">531</span>. <span class="bld">kammāniyaññā</span> <span class="note">[ka. 353; rū. 378; nī. 767]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-527-528-529-530-531-');
INSERT INTO pages VALUES(54737,'annya_sadda_10',330,replace(replace('\r\n<p class="bodytext">tasmiṃ <a name="M0.0330"></a> sādhūti atthe kammamhā niya, ññā honti.</p>\r\n<p class="bodytext">kamme sādhu kammaniyaṃ, kammaññaṃ.</p>\r\n<p class="bodytext"><a name="para532"></a><span class="paranum">532</span>. <span class="bld">kathāditiko</span> <span class="note">[ka. 353; rū. 378; nī. 767; caṃ. 3.4.104; pā. 4.4.102]</span>.</p>\r\n<p class="bodytext">tattha sādhūti atthe kathādīhi iko hoti.</p>\r\n<p class="bodytext">kathāyaṃ sādhu kathiko, dhammakathāyaṃ sādhu dhammakathiko, saṅgāme sādhu saṅgāmiko, gāmavāse sādhu gāmavāsiko, upavāse sādhu upavāsiko.</p>\r\n<p class="bodytext"><a name="para533"></a><span class="paranum">533</span>. <span class="bld">pathādīhi ṇeyyo</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 3.4.105; pā. 4.4.104]</span>.</p>\r\n<p class="bodytext">tattha sādhūti atthe pathādīhi ṇeyyo hoti.</p>\r\n<p class="bodytext">pathe sādhu pātheyyaṃ, saṃ vuccati dhanaṃ, tassa pati sapati, sapatimhi sādhu sāpateyyaṃ.</p>\r\n<p class="bodytext"><a name="para534"></a><span class="paranum">534</span>. <span class="bld">dakkhiṇāyārahe</span> <span class="note">[ka. 352; rū. 376; nī. 765; caṃ. 4.1.80; pā. 5.1.69]</span>.</p>\r\n<p class="bodytext">dakkhiṇāsaddamhā arahatthe ṇeyyo hoti.</p>\r\n<p class="bodytext">dakkhiṇaṃ arahatīti dakkhiṇeyyo.</p>\r\n<p class="bodytext"><a name="para535"></a><span class="paranum">535</span>. <span class="bld">āyo tumantā</span> <span class="note">[ka. 352; rū. 376; nī. 765; ‘rāyo tumantā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tumantamhā arahatthe āyo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-532-533-534-535-');
INSERT INTO pages VALUES(54738,'annya_sadda_10',331,replace(replace('\r\n<p class="bodytext">ghātetuṃ <a name="M0.0331"></a> arahatīti ghātetāyo, jāpetuṃ arahatīti jāpetāyo, ‘jāpetu’nti hāpetuṃ, pabbājetuṃ arahatīti pabbājetāyo, mahāvuttinā āyamhi sabinduno ussa lopo.</p>\r\n<p class="centered">iti hita, sādhu, araharāsi.</p>\r\n<p class="subhead">vikatirāsi</p>\r\n<p class="bodytext"><a name="para536"></a><span class="paranum">536</span>. <span class="bld">tassa vikārāvayavesu ṇa ṇika ṇeyyamayā</span> <span class="note">[ka. 352, 351, 372; rū. 376, 374, 385; nī. 765, 764, 798; caṃ. 3.3.103; pā. 4.3.134]</span>.</p>\r\n<p class="bodytext">tassa vikāro, tassa avayavoti atthesu ṇādayo honti, pakatiyā uttari avatthantarāpatti vikāro.</p>\r\n<p class="bodytext">udumbarassa vikati odumbaraṃ, bhasmā, udumbarassa avayavo odumbaraṃ, paṇṇādi. kapotāvayavo kāpotaṃ, maṃsalohitapattādi.</p>\r\n<p class="bodytext">ṇikamhi-kappāsassa vikati kappāsikaṃ, suttaṃ vatthañca.</p>\r\n<p class="bodytext">ṇeyyamhi-eṇissa avayavo eṇeyyaṃ, maṃsaṃ. kosakimīnaṃ vikati koseyyaṃ, suttaṃ vatthañca.</p>\r\n<p class="bodytext">mayamhi-tiṇānaṃ vikati tiṇamayaṃ. evaṃ dārumayaṃ, naḷamayaṃ, mattikāmayaṃ, gunnaṃ vikati gomayaṃ, karīsaṃ.</p>\r\n<p class="bodytext"><a name="para537"></a><span class="paranum">537</span>. <span class="bld">jatuto mayaṇa vā</span> <span class="note">[ka. 372; rū. 385; nī. 798; caṃ. 3.3.108; pā. 4.3.138; jatuto saṇa vā (bahūsu)]</span>.</p>\r\n<p class="bodytext">tassa vikārāvayavesu jatuto mayaṇa hoti vā.</p>\r\n<p class="bodytext">jatuno vikāro jatumayaṃ.</p>\r\n<p class="centered">iti vikatirāsi.</p>\r\n<p class="subhead">visesarāsi</p>\r\n<p class="bodytext"><a name="para538"></a><span class="paranum">538</span>. <span class="bld">taratamissikiyiṭṭhātisaye</span> <span class="note">[ka. 363; rū. 390; nī. 786; caṃ. 4.3.45; pā. 5.3.55, 57]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-536-537-538-');
INSERT INTO pages VALUES(54739,'annya_sadda_10',332,replace(replace('\r\n<p class="bodytext">atisayatthe <a name="M0.0332"></a> ete paccayā bhavanti.</p>\r\n<p class="bodytext">pāpānaṃ atisayena pāpoti pāpataro, pāpatamo, pāpissiko, pāpiyo, pāpiṭṭho, itthiyaṃ pāpatarā, atisayatopi atisayapaccayo hoti, atisayena pāpiṭṭho pāpiṭṭhataro.</p>\r\n<p class="bodytext"><a name="para539"></a><span class="paranum">539</span>. <span class="bld">vacchādīhi tanutte taro</span> <span class="note">[ka. 363; rū. 390; nī. 786; caṃ. 4.3.74; pā. 5.3.91]</span>.</p>\r\n<p class="bodytext">vacchādīhi sabbatanubhāve taro hoti.</p>\r\n<p class="bodytext">atitaruṇo vaccho vacchataro, itthiyaṃ vacchatarī. yobbanassa tanutte yobbanapattānaṃ susuttassa tanutte atitaruṇo usabho ukkhataro, assabhāvassa tanutte taruṇaasso assataro, itthiyaṃ assatarī. sāmatthiyassa tanutte taruṇausabho usabhataro.</p>\r\n<p class="bodytext"><a name="para540"></a><span class="paranum">540</span>. <span class="bld">kiṃmhā niddhāraṇe taratamā</span> <span class="note">[ka. 363; rū. 390; nī. 786; caṃ. 4.3.77; pā. 5.3.92, 93; ‘.ratara ratamā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">kiṃsaddā niddhāraṇe gamyamāne tara, tamā honti.</p>\r\n<p class="bodytext">kataro bhavataṃ devadatto, kataro bhavataṃ yaññadatto, katamo bhavataṃ devadatto, katamo bhavataṃ yaññadatto, ‘kissa ko’ti suttena tara, tamesu kissa kattaṃ.</p>\r\n<p class="centered">iti visesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-539-540-');
INSERT INTO pages VALUES(54740,'annya_sadda_10',333,replace(replace('\r\n<p class="subhead">samūharāsi</p>\r\n\r\n<p class="bodytext"><a name="para541"></a><span class="paranum">541</span>. <span class="bld">samūhe</span> <a name="M0.0333"></a> <span class="bld">kaṇaṇaṇikā</span> <span class="note">[ka. 354; rū. 379; nī. 771; caṃ. 3.1.43-47; pā. 4.2.37-42]</span>.</p>\r\n<p class="bodytext">tassa samūhoti atthe kaṇa, ṇa, ṇikā honti.</p>\r\n<p class="bodytext">gottapaccayantehi tāva – rājaññānaṃ samūho rājaññakaṃ, mānussakaṃ.</p>\r\n<p class="bodytext">ukkhādīhi-ukkhānaṃ usabhānaṃ samūho okkhakaṃ, oṭṭhānaṃ samūho oṭṭhakaṃ, urabbhānaṃ samūho orabbhakaṃ. evaṃ rājakaṃ, rājaputtakaṃ, hatthikaṃ, dhenukaṃ, sasaṃ adenti bhakkhantīti sasādakā, tesaṃ samūho sasādakakaṃ. ikkhaṇikaṃ.</p>\r\n<p class="bodytext">ṇamhi-amittānaṃ samūho amittaṃ.</p>\r\n<p class="bodytext">ṇikamhi-apūpānaṃ samūho āpūpikaṃ, sakuṇānaṃ samūho sākuṇiko <span class="note">[saṃkulānaṃ samūho saṃkulikaṃ?]</span>.</p>\r\n<p class="bodytext"><a name="para542"></a><span class="paranum">542</span>. <span class="bld">janādīhi tā</span> <span class="note">[ka. 355; rū. 380; nī. 771; caṃ. 3.1.69; pā. 4.2.43]</span>.</p>\r\n<p class="bodytext">tassa samūhoti atthe tā hoti.</p>\r\n<p class="bodytext">janatā, rājatā, bandhutā, gāmatā, sahāyatā, nagaravāsīnaṃ samūho nāgaratā iccādi.</p>\r\n<p class="bodytext"><a name="para543"></a><span class="paranum">543</span>. <span class="bld">ayūbhadvitīhaṃse</span> <span class="note">[ka. 354; rū. 379; nī. 771; caṃ. 4.2.47, 48; pā. 5.2.43, 44]</span>.</p>\r\n<p class="bodytext">ubha, dvi, tīhi tassa aṃsatthe ayo hoti.</p>\r\n<p class="bodytext">ubho aṃsā bhāgā assāti ubhayaṃ, dve aṃsā assāti dvayaṃ, tayo aṃsā assāti tayaṃ, vatthuttayaṃ, ratanattayaṃ, dve vā tayo vā aṃsā assāti dvattayaṃ.</p>\r\n<p class="centered">iti samūharāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-541-542-543-');
INSERT INTO pages VALUES(54741,'annya_sadda_10',334,replace(replace('\r\n<p class="subhead">datta, nibbattarāsi</p>\r\n\r\n<p class="bodytext"><a name="para544"></a><span class="paranum">544</span>. <span class="bld">tena</span> <a name="M0.0334"></a> <span class="bld">datte liyā</span> <span class="note">[ka. 358, 356; rū. 383, 381; nī. 778, 773]</span>.</p>\r\n<p class="bodytext">tena dattoti atthe la, iyā honti. mahāvuttinā dinnasaddassa dattattaṃ.</p>\r\n<p class="bodytext">devena dattoti devalo <span class="note">[jā. 1.8.65]</span>. ‘devilo’tipi <span class="note">[devīlo?]</span> pāḷi. deviyo, devadatto vā.</p>\r\n<p class="bodytext">evaṃ brahmalo, brahmiyo, brahmadatto, sivena bissanudevarājena datto sīvalo, sīviyo, itthiyaṃ sīvali, sīviyi, sissa dīgho.</p>\r\n<p class="bodytext"><a name="para545"></a><span class="paranum">545</span>. <span class="bld">tena nibbatte</span> <span class="note">[ka. 353; rū. 378; nī. 767; ‘tena nibbatte imo’?]</span>.</p>\r\n<p class="bodytext">tena nibbatte imo hoti.</p>\r\n<p class="bodytext">pākena nibbattaṃ pākimaṃ, pheṇena nibbattaṃ pheṇimaṃ, veṭhanena nibbattaṃ veṭhimaṃ. evaṃ vedhimaṃ, gopphanena nibbattaṃ gopphimaṃ <span class="note">[pārā. aṭṭha. 2.431]</span>, pupphadāmaṃ, karaṇena nibbattaṃ kittimaṃ, kuttimaṃ vā, mahāvuttinā karaṇassa kittaṃ kuttañca. suttavibhattena saṃhārimaṃ, āhārimaṃ iccādīni sijjhanti.</p>\r\n<p class="centered">iti datta, nibbattarāsi.</p>\r\n<p class="subhead">la, ita, karāsi</p>\r\n<p class="bodytext"><a name="para546"></a><span class="paranum">546</span>. <span class="bld">tannissite lo</span> <span class="note">[ka. 358; rū. 383; nī. 778; ‘llo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tannissitatthe lo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-544-545-546-');
INSERT INTO pages VALUES(54742,'annya_sadda_10',335,replace(replace('\r\n<p class="bodytext">vedaṃ ñāṇaṃ nissitaṃ vedallaṃ, lassa dvittaṃ, duṭṭhu nissitaṃ duṭṭhullaṃ. "madanīyaṃ, bandhanīyaṃ, mucchanīyaṃ, rajanīyaṃ, gamanīyaṃ, dassanīyaṃ" <a name="M0.0335"></a> iccādīni karaṇe vā adhikaraṇe vā anīyapaccayena sijjhanti.</p>\r\n<p class="bodytext">‘dhūmāyitatta’ntiādīsu dhūmo viya attānaṃ ācaratīti dhūmāyitaṃ, gaganaṃ, dhūmāyitaṃ eva dhūmāyitattaṃ, sakatthe tta. evaṃ timirāyitattaṃ, nāmadhātuto <span class="note">[saṃ. ni. 3.87]</span> āyapaccayena siddhaṃ.</p>\r\n<p class="bodytext"><a name="para547"></a><span class="paranum">547</span>. <span class="bld">sañjātā tārakādvitvito</span> <span class="note">[ka. 555; rū. 612; nī. 1142; caṃ. 4.2.37 .pe.. 5.2.36; ‘sañjātaṃ.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">tārakādīhi te assa sañjātāti atthe ito hoti.</p>\r\n<p class="bodytext">tārakā sañjātā assāti tārakitaṃ, gaganaṃ. pupphāni sañjātāni assāti pupphito. evaṃ phalito, rukkho. pallavāni sañjātāni assāti pallavitā, latā. dukkhaṃ sañjātaṃ assāti dukkhito, sukhaṃ sañjātaṃ assāti sukhito.</p>\r\n<p class="bodytext">paṇḍā vuccati paññā, paṇḍā sañjātā assāti paṇḍito, daṇḍo sañjāto assāti daṇḍito. mahāvuttinā tassa natte malaṃ sañjātaṃ assāti malinaṃ. tathā pipāsā sañjātā assāti pipāsito, jighacchā sañjātā assāti jighacchito, bubhukkhā sañjātā assāti bubhukkhito, mucchā sañjātā assāti mucchito, visaññā sañjātā assāti visaññito, nindā sañjātā assāti nindito.</p>\r\n<p class="bodytext">evaṃ gabba-thambhe gabbito. dabba-pāṭave dabbito. antaraṃ sañjātaṃ assāti antarito, vaccaṃ sañjātaṃ assāti vaccito.</p>\r\n<p class="bodytext"><a name="para548"></a><span class="paranum">548</span>. <span class="bld">nindāññātappapaṭibhāgarassadayāsaññāsu ko</span> <span class="note">[ka. 391; rū. 423; nī. 835; caṃ. 4.3.62, 63, 64; pā. 5.3.73-79, 96, 97]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-547-548-');
INSERT INTO pages VALUES(54743,'annya_sadda_10',336,replace(replace('\r\n<p class="bodytext">nindādīsu <a name="M0.0336"></a> jotaniyesu nāmasmā ko hoti.</p>\r\n<p class="bodytext">nindāyaṃ – kucchito samaṇo samaṇako. evaṃ muṇḍako, assako, uddhumātakaṃ, vinīlakaṃ, vipubbakaṃ, aṭṭhikaṃ iccādi.</p>\r\n<p class="bodytext">aññāte – aññāto asso assako, kassa ayaṃ assoti vā assako iccādi.</p>\r\n<p class="bodytext">appatthe – appakaṃ telaṃ telakaṃ. evaṃ ghatakaṃ, khuddakaṃ dhanu dhanukaṃ, rathakaṃ, gāmakaṃ iccādi.</p>\r\n<p class="bodytext">paṭibhāgatthe – hatthirūpakaṃ hatthikaṃ. evaṃ assakaṃ, balībaddako iccādi.</p>\r\n<p class="bodytext">rasse-rasso manusso manussako. evaṃ rukkhako, pilakkhako iccādi.</p>\r\n<p class="bodytext">dayāyaṃ-anukampito putto puttako. evaṃ vacchako, itthikā, ambakā, kumārikā iccādi.</p>\r\n<p class="bodytext">saññāyaṃ-nāmamattena moro viya morako iccādi.</p>\r\n<p class="centered">iti la, ita, ka rāsi.</p>\r\n<p class="subhead">abhūtatabbhāvarāsi</p>\r\n<p class="bodytext"><a name="para549"></a><span class="paranum">549</span>. <span class="bld">abhūtatabbhāve karāsabhūyoge vikārācī</span> <span class="note">[ka. 391; rū. 423; nī. 835; caṃ. 4.4.35; pā. 5.4.50]</span>.</p>\r\n<p class="bodytext">pubbe tassa abhūtassa vatthuno kadāci tathā bhavanaṃ abhūtatabbhāvo, tasmiṃ abhūtatabbhāve jotaniye sati karā’sa, bhūdhātūnaṃ yoge vikāravācimhā nāmasmā cānubandho īpaccayo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-549-');
INSERT INTO pages VALUES(54744,'annya_sadda_10',337,replace(replace('\r\n<p class="bodytext">adhavalaṃ <a name="M0.0337"></a> dhavalaṃ karoti dhavalīkaroti, adhavalo dhavalo siyā dhavalīsiyā, adhavalo dhavalo bhavati dhavalībhavati. evaṃ dhavalīkāro, dhavalībhūto.</p>\r\n<p class="bodytext">abhūtatabbhāveti kiṃ? ghaṭaṃ karoti, ghaṭo atthi, ghaṭo bhavati.</p>\r\n<p class="bodytext">karāsabhūyogeti kiṃ? adhavalo dhavalo jāyate.</p>\r\n<p class="bodytext">vikārāti kiṃ? pakatiyā mā hotu, suvaṇṇaṃ kuṇḍalaṃ karoti, suvaṇṇassa kuṇḍalakaraṇaṃ nāma loke pakatirūpanti vuttaṃ hoti. evaṃ suvaṇṇaṃ kuṇḍalaṃ siyā, suvaṇṇaṃ kuṇḍalaṃ bhavatīti.</p>\r\n<p class="centered">iti abhūtatabbhāvarāsi.</p>\r\n<p class="subhead">sakattharāsi</p>\r\n<p class="bodytext"><a name="para550"></a><span class="paranum">550</span>. <span class="bld">sakatthe</span> <span class="note">[ka. 178, 360, 372; rū. 224, 387, 385, 378; nī. 364, 780, 798, 767]</span>.</p>\r\n<p class="bodytext">sakatthepi paccayā dissanti. ‘sakattho’ti sakapadattho, pakatiliṅgapadatthoti vuttaṃ hoti.</p>\r\n<p class="bodytext">hīno eva hīnako, poto eva potako, devo eva devatā, yathābhūtameva yathābhuccaṃ, karuṇā eva kāruññaṃ, pattakālameva pattakallaṃ, ākāsānantameva ākāsānañcaṃ, pāguññameva pāguññatā, kammaññameva kammaññatā, dānaṃ eva dānamayaṃ, sīlaṃ eva sīlamayaṃ. evaṃ bhāvanāmayaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-550-');
INSERT INTO pages VALUES(54745,'annya_sadda_10',338,replace(replace('\r\n<p class="bodytext">yathā ca amaccaputtā eva ‘amaccaputtiyā’ti vuccanti, evaṃ "sakyaputto eva sakyaputtiyo, asamaṇo hoti asakyaputtiyo <a name="M0.0338"></a> <span class="note">[pārā. 55]</span>, evaṃ nāṭaputtiyo, dāsaputtiyo"tipi yujjati.</p>\r\n<p class="bodytext">‘bhayadassivā, atthadassimā’ti vantu, mantupaccayāsakatthepi yujjanti. ‘brahmavaṇṇī, devavaṇṇī’ti ettha brahmuno vaṇṇo brahmavaṇṇo, brahmavaṇṇo viya vaṇṇo assa atthīti atthe sati īpaccayo paccayattho eva hoti, na sakattho. brahmavaṇṇo viya vaṇṇo yassa soyaṃ brahmavaṇṇīti atthe sati sakatthoyeva. api ca ekasmiṃ aññapadatthe dve samāsa, taddhitā vattantītipi yujjati, tathā ‘paguṇassa bhāvo pāguññatā’tiādīsu dve taddhitapaccayā bhāvattheti.</p>\r\n<p class="centered">iti sakattharāsi.</p>\r\n<p class="subhead">niddiṭṭhapaccayarāsi</p>\r\n<p class="bodytext"><a name="para551"></a><span class="paranum">551</span>. <span class="bld">aññasmiṃ</span> <span class="note">[ka. 352; rū. 376; nī. 765]</span>.</p>\r\n<p class="bodytext">pubbe niddiṭṭhā ṇādayo paccayā niddiṭṭhatthato aññesupi atthesu dissanti.</p>\r\n<p class="bodytext">magadhesu jāto māgadho, magadhesu saṃvaḍḍhito māgadho, magadhesu nivuttho māgadho, magadhānaṃ magadhesu vā issaro māgadho iccādi, ṇo.</p>\r\n<p class="bodytext">kāsiṃ agghatīti kāsiyo <span class="note">[cūḷava. 376]</span>, ‘kāsī’ti sataṃ vā sahassaṃ vā vuccati, iyo.</p>\r\n<p class="bodytext">evamaññepi paccayā yathānurūpaṃ veditabbā.</p>\r\n<p class="bodytext"><a name="para552"></a><span class="paranum">552</span>. <span class="bld">dissantaññepi paccayā</span> <span class="note">[ka. 351, 352; rū. 374, 376; nī. 764, 765]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-551-552-');
INSERT INTO pages VALUES(54746,'annya_sadda_10',339,replace(replace('\r\n<p class="bodytext">pubbe <a name="M0.0339"></a> niddiṭṭhapaccayehi aññepi paccayā niddiṭṭhesu aniddiṭṭhesu ca atthesu dissanti.</p>\r\n<p class="bodytext">visadisā <span class="note">[vividhā (moga.)]</span> mātaro vimātaro, tāsaṃ puttā vemātikā <span class="note">[netti 95]</span>, ikaṇa.</p>\r\n<p class="bodytext">pathe gacchantīti pathāvino <span class="note">[ma. ni. 2.347]</span>, aghaṃ dukkhaṃ pāpaṃ vā gacchatīti aghāvī, āvī.</p>\r\n<p class="bodytext">issā assa atthīti issukī <span class="note">[jā. 1.6.43]</span>, ukī.</p>\r\n<p class="bodytext">dhuraṃ vahantīti dhorayhā <span class="note">[a. ni. 3.58]</span>, yhaṇa.</p>\r\n<p class="bodytext">lobhassa hitā lobhaneyyā. evaṃ dosaneyyā, mohaneyyā, aneyyo.</p>\r\n<p class="bodytext">dassanaṃ arahatīti dassaneyyo. evaṃ vandaneyyo, pūjaneyyo, namassaneyyo, eyyo.</p>\r\n<p class="bodytext">oghānaṃ hitā oghaniyā, yoganiyā, ganthaniyā, kammaniyaṃ, attaniyaṃ, dassaniyaṃ, pūjaniyo, namassaniyo iccādi, aniyo.</p>\r\n<p class="bodytext">yaṃ parimāṇaṃ assāti yāvaṃ, yāvantassa bhāvo yāvatvaṃ. evaṃ tāvatvaṃ, tva.</p>\r\n<p class="bodytext">paramānaṃ uttamapurisānaṃ bhāvo kammaṃ vā pāramī, samaggānaṃ bhāvo kammaṃ vā sāmaggī, ṇī.</p>\r\n<p class="bodytext">"nāgavatā, sīhavatā, ājaññavatā" iccādīsu bhāve vantupaccayaṃ icchanti.</p>\r\n<p class="bodytext">mātu bhātā mātulo, ulo.</p>\r\n<p class="centered">iti niddiṭṭhapaccayarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54747,'annya_sadda_10',340,replace(replace('\r\n<p class="subhead">vuddhirāsi</p>\r\n\r\n<p class="bodytext">‘padānamādissāyuvaṇṇassāeo <a name="M0.0340"></a> ṇānubandhe’ti padādibhūtānaṃ akāra, ivaṇṇu’vaṇṇānaṃ ā, e, ovuddhi.</p>\r\n<p class="bodytext">ādicco, vāsiṭṭho, venateyyo, meniko, pettikaṃ, odumbaraṃ, oḷumpiko, odagyaṃ, dobhaggaṃ, sobhaggaṃ iccādi.</p>\r\n<p class="bodytext">ṇānubandheti kiṃ? nāmako, padako, purātano.</p>\r\n<p class="bodytext">‘majjhe’ti suttena padamajjhepi vuddhi, vāseṭṭho, aḍḍhateyyo iccādi.</p>\r\n<p class="bodytext"><a name="para553"></a><span class="paranum">553</span>. <span class="bld">saṃyogepi kvaci</span> <span class="note">[ka. 405; rū. 365; nī. 864; ‘pi’ (bahūsu natthi)]</span>.</p>\r\n<p class="bodytext">ṇānubandhe paccaye pare saṃyogepi kvaci vuddhi hoti.</p>\r\n<p class="bodytext">petteyyo, pettikaṃ, decco, pamukhe sādhu pāmokkhaṃ, pamuditassa bhāvo pāmojjaṃ, vattabbanti vākyaṃ, bhajitabbanti bhāgyaṃ, bhoggaṃ, yoggaṃ iccādi.</p>\r\n<p class="centered">iti vuddhirāsi.</p>\r\n<p class="subhead">loparāsi</p>\r\n<p class="bodytext">‘lopovaṇṇivaṇṇāna’nti suttena ṇyamhi pare avaṇṇi’vaṇṇānaṃ lopo.</p>\r\n<p class="bodytext">tattha avaṇṇe-paṇḍiccaṃ, tacchaṃ, dāyajjaṃ, dvidhā bhāvo dvejjhaṃ, karuṇāyeva kāruññaṃ iccādi.</p>\r\n<p class="bodytext">ivaṇṇe-adhipatissa bhāvo ādhipaccaṃ. evaṃ ādicco, koṇḍañño iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-553-');
INSERT INTO pages VALUES(54748,'annya_sadda_10',341,replace(replace('\r\n<p class="bodytext">‘uvaṇṇassāvaṅa <a name="M0.0341"></a> sare’ti sare pare uvaṇṇassa avaṅa hoti.</p>\r\n<p class="bodytext">ṇamhi-lahuno bhāvo lāghavaṃ, rāghavaṃ, jamburukkhe bhavaṃ jambavaṃ. tathā kapilavatthumhi bhavaṃ kāpilavatthavaṃ, vanaṃ. bhātuno apaccaṃ bhātabyo, gabyaṃ, dabyaṃ.</p>\r\n<p class="bodytext">‘ṭe satissā.’ti ṭamhi paccaye vīsati, tīsatīnaṃ tilopo.</p>\r\n<p class="bodytext">vīsatiyā pūraṇo vīso, ekūnavīso. evaṃ tīso, ekūnatīso.</p>\r\n<p class="bodytext">‘rānubandhentasarādissā’ti rānubandhe paccaye padantasarādīnaṃ lopo.</p>\r\n<p class="bodytext">metteyyo, petteyyo, kivaṃ, kittakaṃ, īdī, īdikkho, īdiso, āhacca, upahacca, sakkacca, adhikicca, kiriyā, vedagū, pāragū iccādi.</p>\r\n<p class="bodytext"><a name="para554"></a><span class="paranum">554</span>. <span class="bld">paccayānaṃ lopo</span> <span class="note">[ka. 391; rū. 423; nī. 830; moggallāne ‘lopo’ tveva dissati]</span>.</p>\r\n<p class="bodytext">paccayānaṃ kvaci lopo hoti.</p>\r\n<p class="bodytext">buddhe ratanaṃ paṇītaṃ, cakkhu suññaṃ attena vā attaniyena vā <span class="note">[saṃ. ni. 4.85]</span>. ettha ca ratanassa bhāvo ratanaṃ, attano bhāvo attā, attano sakassa bhāvo attaniyanti evaṃ bhāvapaccayalopo.</p>\r\n<p class="bodytext">"buddhānussati dhammānussati"ādīsu "buddhassa bhāvo buddhā, buddhassa ayaṃ guṇo buddhā"tiādinā nayena paccayalopo veditabbo.</p>\r\n<p class="bodytext"><a name="para555"></a><span class="paranum">555</span>. <span class="bld">lopo vīmantuvantūnaṃ</span> <span class="note">[ka. 268; rū. 397; nī. 518]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-554-555-');
INSERT INTO pages VALUES(54749,'annya_sadda_10',342,replace(replace('\r\n<p class="bodytext">iyi’ṭṭhesu <a name="M0.0342"></a> paresu vī, mantu, vantūnaṃ lopo hoti.</p>\r\n<p class="bodytext">medhāvīnaṃ atisayena medhāvīti medhiyo, medhiṭṭho, satimantānaṃ atisayena satimāti satiyo, satiṭṭho, guṇavantānaṃ atisayena guṇavāti guṇiyo, guṇiṭṭho.</p>\r\n<p class="centered">iti loparāsi.</p>\r\n<p class="centered">khuddakarāsi niṭṭhito.</p>\r\n<p class="subhead">nānāttarāsi</p>\r\n<p class="bodytext"><a name="para556"></a><span class="paranum">556</span>. <span class="bld">jo vuddhassiyiṭṭhesu</span> <span class="note">[ka. 262; rū. 391; nī. 513; caṃ. 4.3.50; pā. 5.3.61, 62]</span>.</p>\r\n<p class="bodytext">iyi’ṭṭhapaccayesu paresu vuddhasaddassa jo hoti.</p>\r\n<p class="bodytext">vuddhānaṃ atisayena vuddhāti jeyyo, jeṭṭho.</p>\r\n<p class="bodytext"><a name="para557"></a><span class="paranum">557</span>. <span class="bld">bāḷhantikapasatthānaṃ sādhanedasajā</span> <span class="note">[ka. 263, 264, 265; rū. 392, 393, 394; nī. 512, 514, 515; caṃ. 4.3.49, 51; pā. 5.3.60, 63; ‘. dasā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">iya, iṭṭhapaccayesu paresu bāḷha, antika, pasatthasaddānaṃ sādha, neda, sa, jādesā honti.</p>\r\n<p class="bodytext">bāḷhānaṃ atisayena bāḷhoti sādhiyo, sādhiṭṭho, antikānaṃ atisayena antikoti nediyo, nediṭṭho, pasatthānaṃ atisayena pasatthoti seyyo, seṭṭho, jeyyo, jeṭṭho.</p>\r\n<p class="bodytext"><a name="para558"></a><span class="paranum">558</span>. <span class="bld">kaṇakana appayuvānaṃ</span> <span class="note">[ka. 266, 267; rū. 395, 396; nī. 516, 517; caṃ. 4.3.53; pā. 5.3.64]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-556-557-558-');
INSERT INTO pages VALUES(54750,'annya_sadda_10',343,replace(replace('\r\n<p class="bodytext">iya<a name="M0.0343"></a>, iṭṭhapaccayesu paresu appa, yuvasaddānaṃ kaṇa, kanaādesā honti.</p>\r\n<p class="bodytext">appānaṃ navānaṃ atisayena appo navoti kaṇiyo, kaṇiṭṭho, yuvānaṃ taruṇānaṃ atisayena yuvā taruṇoti kaniyo, kaniṭṭho, kaniye vaye bhavā kaññā.</p>\r\n<p class="bodytext"><a name="para559"></a><span class="paranum">559</span>. <span class="bld">kosajjājjava pārisajja suhajja maddavārisyāsa bhājaññatheyyabāhusaccā</span> <span class="note">[ka. 360, 361; rū. 387, 388; nī. 780, 781; ‘.rissā." (bahūsu)]</span>.</p>\r\n<p class="bodytext">bhāva, kammesu ṇānubandhe paccaye pare ete saddā nipaccante. tattha ajjava, maddavā’sabhasaddā ṇamhi sijjhanti, sesā ṇyamhi.</p>\r\n<p class="bodytext">tattha ujuno bhāvo ajjavaṃ, iminā suttena ṇamhi ussa attaṃ, ‘uvaṇṇassāvaṅa.’ iti ussa avattaṃ. evaṃ muduno bhāvo maddavaṃ, usabhassa bhāvo āsabhaṃ, ussa āttaṃ. kusitassa <span class="note">[kusītassa?]</span> bhāvo kosajjaṃ, iminā ilopo, tyassa jjattaṃ, parisāsu uppanno pārisajjo <span class="note">[dī. ni. 2.215]</span>, dāgamo, suhadayova suhado, yalopo, suhadassa bhāvo sohajjaṃ <span class="note">[jā. 1.5.23]</span>, isino idaṃ ārisyaṃ, issa ārittaṃ, ājāniyassa <span class="note">[ājānīyassātipi dissati]</span> bhāvo ājaññaṃ, yalopo, puna ‘lopovaṇṇivaṇṇāna’nti ilopo, tato paraṃ sandhirūpaṃ, ājāniyo eva vā ājañño <span class="note">[jā. 1.1.24]</span>, sakatthe ṇyo, thenassa bhāvo, kammaṃ vā theyyaṃ, nassa yattaṃ, bahusutassa bhāvo bāhusaccaṃ, ussa attaṃ, puna sandhirūpaṃ.</p>\r\n<p class="bodytext"><a name="para560"></a><span class="paranum">560</span>. <span class="bld">adhātussa ke</span> <span class="note">[pāṇiniye, cande ca ‘kāka’iti pañcamyantaṃ dissate]</span> <span class="bld">syādito ghessi</span> <span class="note">[ka. 404; rū. 370; nī. 859]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-559-560-');
INSERT INTO pages VALUES(54751,'annya_sadda_10',344,replace(replace('\r\n<p class="bodytext">‘ghessī’ti <a name="M0.0344"></a> ghe+assa+i, chapadamidaṃ suttaṃ.</p>\r\n<p class="bodytext">adhātussa avayavabhūte kakāre pare pubbassa akārassa bahulaṃ i hoti kanissite ghe asyādito pare sati.</p>\r\n<p class="bodytext">bālikā, ekikā, hatthipotikā, mahallikā, kumbhakārikā, kammakārikā, annadāyikā, upāsikā, sāvikā, dhammavācikā.</p>\r\n<p class="bodytext">adhātussāti kiṃ? kulupakā- bhikkhunī, dhenupakā, khīrupakā-vacchī, idha dhātvādesopi kakāro dhātusaññaṃ labhatiyeva.</p>\r\n<p class="bodytext">keti kiṃ? vedanā, cetanā.</p>\r\n<p class="bodytext">asyāditoti kiṃ? bahuparibbājakā-rājadhānī.</p>\r\n<p class="bodytext">assāti kiṃ? bahukattukā-sālā.</p>\r\n<p class="bodytext">ettha ca bahavo paribbājakā yassā sā bahuparibbājakāti viggaho. paribbājakasaddo pakatisyādisaddo hoti, tasmā kanissitassa ghasaññassa ākārassa syādito parattā pubbassa assa ittaṃ na bhavati. yadi bhaveyya, bahukā paribbājikāyo yassanti atthappasaṅgo siyāti.</p>\r\n<p class="centered">iti nānāttarāsi niṭṭhito.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānabyākaraṇa-</p>\r\n<p class="centered">dīpaniyā taddhitakaṇḍo pañcamo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54752,'annya_sadda_10',345,replace(replace('\r\n<p class="chapter">6. ākhyātakaṇḍa</p>\r\n<p class="subhead">suddhakatturūpa</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0345"></a> dhātupaccayasaṃsiddhaṃ kāla, kāraka, purisa, saṅkhyābhedadīpakaṃ liṅgabhedarahitaṃ kriyāpadhānavācakaṃ tyādyantanāmakaṃ ākhyātapadaṃ dīpiyate.</p>\r\n<p class="bodytext">tattha kriyaṃ dhāretīti <span class="bld">dhātu</span>. sā pakatidhātu, vikatidhātu, nāmadhātuvasena tividhā.</p>\r\n<p class="bodytext">tattha bhū, hū, gamu, paca iccādi <span class="bld">pakatidhātu</span> nāma sabhāvena siddhattā.</p>\r\n<p class="bodytext">titikkha, tikiccha, bubhukkha, jighacchaiccādi <span class="bld">vikatidhātu</span> nāma saṅkhatavasena siddhattā.</p>\r\n<p class="bodytext">puttīya, pabbatāya iccādi <span class="bld">nāmadhātu</span> nāma nāmabhūtassa sato kriyavācīpaccayayogena dhātuṭṭhāne ṭhitattā.</p>\r\n<p class="bodytext">pakatidhātu ca sakammikā’kammikavasena duvidhā.</p>\r\n<p class="bodytext">tatthayā dhātu kammāpekkhaṃ kriyaṃ vadati, sā <span class="bld">sakammikā</span> nāma. gāmaṃ gacchati, odanaṃ pacati iccādi.</p>\r\n<p class="bodytext">yā kammanirapekkhaṃ kriyaṃ vadati, sā <span class="bld">akammikā</span> nāma. bhavati, hoti, tiṭṭhati, seti iccādi.</p>\r\n<p class="bodytext">sakammikā ca ekakammika, dvikammikavasena duvidhā.</p>\r\n<p class="bodytext">tattha yā ekakammāpekkhaṃ kriyaṃ vadati, sā ekakammikā nāma. gāmaṃ gacchati, odanaṃ pacati iccādi.</p>\r\n<p class="bodytext">yā padhānā’padhānavasena kammadvayāpekkhaṃ kriyaṃ vadati, sā dvikammikā nāma.</p>\r\n<p class="bodytext">sā ca nyādi, duhādivasena duvidhā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54753,'annya_sadda_10',346,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0346"></a> yā dhātu pāpanatthā hoti, sā nyādi nāma. ajaṃ gāmaṃ neti, bhāraṃ gāmaṃ vahati, sākhaṃ gāmaṃ ākaḍḍhati.</p>\r\n<p class="bodytext">sesā dvikammikā duhādi nāma. gāviṃ khīraṃ duhati, brāhmaṇaṃ kambalaṃ yācati, dāyakaṃ bhikkhaṃ bhikkhati, goṇaṃ vajaṃ rundhati, bhagavantaṃ pañhaṃ pucchati, sissaṃ dhammaṃ anusāsati, bhagavā bhikkhū etaṃ [vacanaṃ] avoca, rājā amaccaṃ vacanaṃ bravīti iccādi.</p>\r\n<p class="bodytext">tattha yadā kammasmiṃ rūpaṃ sijjhati, tadā vibhatti, paccayā nyādimhi padhānakammaṃ vadanti, duhādimhi apadhānakammaṃ, sabbadhātūsu kāritayoge kāritakammanti, sabbañcetaṃ dhātūnaṃ pakatiatthavasena vuttaṃ, anekatthattā pana dhātūnaṃ atthantaravacane vā nānupasaggayoge vā akammikāpi sakammikā honti, sakammikāpi akammikā honti.</p>\r\n<p class="bodytext">atthantaravacane tāva –</p>\r\n<p class="bodytext">vida – sattāyaṃ, dhammo vijjati, saṃvijjati.</p>\r\n<p class="bodytext">vida – ñāṇe, dhammaṃ vidati.</p>\r\n<p class="bodytext">vida – lābhe, dhanaṃ vindati.</p>\r\n<p class="bodytext">vida – anubhavane, sukhaṃ vedeti, vipākaṃ paṭisaṃvedeti <span class="note">[ma. ni. 3.303]</span>.</p>\r\n<p class="bodytext">vida – ārocane, vedayāmahaṃ bhante vedayatīti maṃ dhāretu <span class="note">[cūḷava. aṭṭha. 102]</span>, kāraṇaṃ nivedeti, dhammaṃ paṭivedeti iccādi.</p>\r\n<p class="bodytext">nānupasaggayoge –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54754,'annya_sadda_10',347,replace(replace('\r\n<p class="bodytext">pada-gatiyaṃ, maggaṃ pajjati, paṭipajjati, maggo uppajjati, nipajjati, sampajjati, bhogo bhavati, sambhavati, bhogaṃ anubhavati, taṇhaṃ <a name="M0.0347"></a> abhibhavati, paribhavati, adhibhavati, araññaṃ abhisambhavati, ajjhogāhatīti attho. gacchantaṃ magge abhisambhavati, sampāpuṇātīti attho iccādi.</p>\r\n<p class="bodytext">padānaṃ byañjanasampattiyā vā atthasampattiyā vā upakārakā vibhatti, paccayā paccayā nāma.</p>\r\n<p class="bodytext">tattha vibhattiyo tyādi, tvādiiccādinā aṭṭhavidhā bhavanti, sarūpato channavutividhā.</p>\r\n<p class="bodytext">tattha pubbachakkabhūtāni aṭṭhacattālīsarūpāni <span class="bld">parassapadāni</span> nāma. parachakkabhūtāni aṭṭhacattālīsarūpāni <span class="bld">attanopadāni</span> nāma.</p>\r\n<p class="bodytext">tattha parahitapaṭisaṃyuttesu ṭhānesu pavattibahulāni padāni parassapadāni nāma. attahitapaṭisaṃyuttesu pavattibahulāni attanopadāni nāmāti <span class="bld">eke</span>.</p>\r\n<p class="bodytext">paro vuccati kattā sabbakriyāsādhāraṇattā, attā vuccati kammaṃ sakasakakriyāsādhāraṇattā, parassa abhidhāyakāni padāni parassapadāni, attano abhidhāyakāni padāni attanopadānīti <span class="bld">aññe</span>.</p>\r\n<p class="bodytext">attā vuccati padatthānaṃ sarīrabhūtā kriyā, kattunā pana sādhyaṭṭhena kriyarūpāni bhāva, kammānipi attāti vuccanti. sādhakaṭṭhena tehi parabhūto kattā paro nāmāti <span class="bld">apare</span>.</p>\r\n<p class="bodytext">attā vuccati amhattho, paro vuccati tumha, nāmattho, pubbachakkāni parabahulattā parassapadāni nāma, parachakkāni pana rūḷhīvasena attanopadāni nāmātipi vadanti. idaṃ na yujjati parachakkesu tabbahulamattassāpi asiddhattā. pāḷibhāsaṃ pana patvā dvinnaṃ chakkānaṃ attahita, parahitesu vā tīsu kārakesu vā pavattinānāttaṃ na dissatiyeva, tasmā imasmiṃ ganthe taṃ nāmadvayaṃ na gahitanti daṭṭhabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54755,'annya_sadda_10',348,replace(replace('\r\n<p class="bodytext">paccayā <a name="M0.0348"></a> pana catubbidhā vikaraṇa, kicca, kārita, dhātupaccayavasena.</p>\r\n<p class="bodytext">tattha ye dhātusiddhāni tyādipadāni tabbādipadāni ca gaṇavibhāgavasena aññamaññaṃ visadisarūpāni karonti, te <span class="bld">vikaraṇapaccayā</span> nāma, la, ya, ṇoiccādayo.</p>\r\n<p class="bodytext">bhāva, kammavisayo kyo <span class="bld">kiccapaccayo</span> nāma.</p>\r\n<p class="bodytext">paresaṃ āṇāpanasaṅkhāte payojakabyāpāre pavattā ṇi, ṇāpipaccayā <span class="bld">kāritapaccayā</span> nāma.</p>\r\n<p class="bodytext">visuṃ taṃtaṃkriyavācībhāvena dhāturūpā kha, cha, saiccādikā paccayā <span class="bld">dhātupaccayā</span> nāma.</p>\r\n<p class="bodytext">"kāla, kāraka, purisa, saṅkhyābhedadīpaka"nti ettha atīta, paccuppannā’nāgata, kālavimuttavasena kālabhedo catubbidho.</p>\r\n<p class="bodytext">tattha hiyyattanī, ajjattanī <span class="note">[ajjatanī (bahūsu)]</span>, parokkhāti imā tisso vibhattiyo atīte kāle vattanti.</p>\r\n<p class="bodytext">vattamānā, pañcamīti dve paccuppanne.</p>\r\n<p class="bodytext">ekā bhavissantī anāgate.</p>\r\n<p class="bodytext">sattamī, kālātipattīti dve kālavimutte vattanti, ayaṃ kira porāṇiko vibhattīnaṃ kamo, so ca pāḷiyā sametiyeva.</p>\r\n<p class="bodytext">"sabbe saddhammagaruno, vihaṃsu viharanti ca.</p>\r\n<p class="bodytext">athopi viharissanti, esā buddhāna dhammatā"ti <span class="note">[a. ni. 4.21]</span> ca –</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54756,'annya_sadda_10',349,replace(replace('\r\n<p class="bodytext">"abbhatītā ca ye buddhā, vattamānā anāgatā"ti ca <span class="note">[apa. thera 1.1.588]</span> pāḷī. imasmiṃ kame pañcamī, sattamīti nāmadvayampi anvatthavasena siddhaṃ <a name="M0.0349"></a> bhavati. pacchā pana garuno vatticchāvasena vibhattīnaṃ nānākamaṃ karonti.</p>\r\n<p class="bodytext">kattu, kamma, bhāvā pana kārakabhedo nāma. tattha bhāvo duvidho sādhya, sādhanavasena visesana, visesyavasena ca. tattha dhātvatthakriyā <span class="bld">sādhyabhāvo</span> nāma. paccayatthakriyā <span class="bld">sādhanabhāvo</span> nāma.</p>\r\n<p class="bodytext">tesu sādhyabhāvo nānādhātūnaṃ vasena nānāvidho hoti. sādhanabhāvo nānādhātvatthānaṃ pavattākārasaṅkhātena ekaṭṭhena ekova hoti. so pana yathā jāti nāma anuppannapakkhe ṭhite saṅkhatadhamme uppādentī viya khāyati, tathā vohāravisayamatte ṭhite sabbadhātvatthe pātubhonte karonto viya khāyati, tasmā so sādhananti ca kārakanti ca vuccati. yathā ca jātivasena uppannā saṅkhatadhammā "cintanaṃ jātaṃ, phusanaṃ jāta" miccādinā ekantameva jātiṃ visesenti, tathā paccayatthavasena pātubhontā nānādhātvatthāpi "bhuyyate, gamyate, paccate, bhavanaṃ, gamanaṃ, pacana" miccādinā ekantameva paccayatthaṃ visesenti. vatticchāvasena pana bhāvasādhanapadesu dhātvattha, paccayatthānaṃ abhedopi vattuṃ yujjatiyeva. idha pana dvīsu bhāvesu sādhanabhāvo adhippetoti.</p>\r\n<p class="bodytext">paṭhama, majjhimu’ttamapurisā purisabhedo. ‘puriso’ti ca "yaṃkiñcāyaṃ purisapuggalo paṭisaṃvedeti sukhaṃ vā dukkhaṃ vā adukkhamasukhaṃ vā"ti <span class="note">[ma. ni. 3.1]</span> ettha attā eva vuccati, so ca attā "so karoti, so paṭisaṃvedetī"ti <span class="note">[a. ni. 6.43; visuddhi. 2.580]</span> ettha kārakoti vuccati. iti ‘puriso’ti kārako eva.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54757,'annya_sadda_10',350,replace(replace('\r\n<p class="bodytext">so ca tividho nāmattho, tumhattho, amhattho cāti. tattha attano ajjhattasantānabhūtattā amhattho uttamapuriso <a name="M0.0350"></a> nāma, sesā pana kamena paṭhamapuriso, majjhimapurisoti vuccanti. vibhattiyo pana taddīpakattā paṭhamapurisādināmaṃ labhanti. idañca nāmaṃ kārakabhede antogadhamevāti katvā imasmiṃ ganthe na gahitanti.</p>\r\n<p class="bodytext">saṅkhyābhedo duvidho ekatta, bahuttavasena.</p>\r\n<p class="bodytext">‘liṅgabhedarahita’nti "puriso gacchati, itthī gacchati, kulaṃ gacchati" iccādīsu ‘puriso’iccādīnaṃ abhidheyyapadānaṃ liṅgānugato rūpabhedo ākhyātapade natthi.</p>\r\n<p class="bodytext">‘kriyāpadhānavācaka’nti ettha kriyaṃ eva padhānato abhidhāti, na nāmapadaṃ viya dabbaṃ padhānato abhidhātīti adhippāyo.</p>\r\n<p class="bodytext">tattha kriyā nāma dhātvatthabhāvo vuccati, sā ca kālavasena atītakriyā, paccuppannakriyā, anāgatakriyā, kālavimuttakriyāti catubbidhā hoti.</p>\r\n<p class="bodytext">āṇattikriyā, āsiṭṭhakriyā, anumatikriyā, parikappakriyā, araha, sakka, vidhi, nimantanā’mantanādikriyāti bahuvidho kriyābhedoti.</p>\r\n<p class="bodytext">bhū-sattāyaṃ, santassa bhāvo sattā, tassaṃ sattāyaṃ, bhūdhātu sattāyamatthe vattate, sabbapadatthānaṃ sadda, buddhivisayabhāvena vijjamānabhāve vattatetyattho.</p>\r\n<p class="bodytext"><a name="para561"></a><span class="paranum">561</span>. <span class="bld">kriyatthā</span> <span class="note">[ka. 432, 455; rū. 362, 530; nī. 905, 936; pā. 3.1.91]</span>.</p>\r\n<p class="bodytext">adhikārasuttamidaṃ, kriyatthā paraṃ vibhatti, paccayā bhavantīti attho. kriyā attho yassāti kriyattho. pakatidhātu, vikatidhātu, nāmadhātuvasena tividho dhātu, kāritapaccayantarūpampi vikatidhātumhi saṅgayhati.</p>\r\n','\r',char(13)),'\n',char(10)),'-561-');
INSERT INTO pages VALUES(54758,'annya_sadda_10',351,replace(replace('\r\n<p class="bodytext"><a name="para562"></a><span class="paranum">562</span>. <span class="bld">vattamāneti</span> <a name="M0.0351"></a> <span class="bld">anti si tha mi ma te ante se vhe e mhe</span> <span class="note">[ka. 414; rū. 428; nī. 872; caṃ. 1.2.82; pā. 3.2.123]</span>.</p>\r\n<p class="bodytext">ārabhitvā niṭṭhaṃ anupagato bhāvo vattamāno nāma, taṃsambandhīkālopi tadūpacārena vattamānoti vuccati. vattamāne kāle kriyatthā paraṃ tyādivibhattiyo bhavanti. ayañca vibhatti tyādīti ca vattamānakālavisayattā vattamānāti ca sijjhati.</p>\r\n<p class="bodytext"><a name="para563"></a><span class="paranum">563</span>. <span class="bld">pubbāparachakkānamekānekesu tumhamhasesesu dve dve majjhimuttamapaṭhamā</span> <span class="note">[ka. 408; rū. 431; nī. 867]</span>.</p>\r\n<p class="bodytext">tumhanāmaṃ, amhanāmaṃ, tadubhayato sesanāmanti tīsu nāmesu payujjamānesu vā gamyamānesu vā ekasmiṃ vā anekesu vā atthesu pubbachakka, parachakkānaṃ dve dve majjhima, uttama, paṭhamā vibhattiyo bhavanti. "uttamanti uttaraṃ antima"nti cūḷamoggallāne vuttaṃ.</p>\r\n<p class="bodytext">ettha ca vibhattividhānamukhena taṃtaṃsaññāvidhānampi siddhaṃ hoti.</p>\r\n<p class="bodytext">kathaṃ?ti, anti, si, tha, mi, ma iti pubbachakkaṃ nāma.</p>\r\n<p class="bodytext">te, ante, se, vhe, e, mhe iti parachakkaṃ nāma.</p>\r\n<p class="bodytext">pubbachakke ca-ti, antidvayaṃ paṭhamadukaṃ nāma, si, thadvayaṃ majjhimadukaṃ nāma, mi, madvayaṃ uttamadukaṃ nāma. evaṃ parachakke.</p>\r\n<p class="bodytext">tattha tulyādhikaraṇabhūte sesanāme payujjamāne vā gamyamāne vā paṭhamadukaṃ bhavati. tathā tumhanāme majjhimadukaṃ, amhanāme uttamadukaṃ. dukesu ca ekasmiṃ atthe vattabbe ekavacanaṃ, bahumhi vattabbe bahuvacanaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-562-563-');
INSERT INTO pages VALUES(54759,'annya_sadda_10',352,replace(replace('\r\n<p class="bodytext">ettha <a name="M0.0352"></a> ca nāmānaṃ atthanissitā katvattha, kammatthā idha nāmatthāti vuccanti. kattu, kammasaṅkhāte yasmiṃ nāmatthe tyādivibhattiyo bhavanti, so nāmattho tyādivācakānaṃ eva vāccabhūto vuttattho nāma hoti, na syādivibhattīnaṃ.</p>\r\n<p class="bodytext">vuttakattu, kammādhiṭṭhānassa ca liṅgatthassa vācakaṃ nāmapadaṃ abhidheyyapadaṃ nāma, etadeva tulyādhikaraṇapadanti ca vuccati.</p>\r\n<p class="bodytext">amādayo ca atthavācakavibhattiyo etasmiṃ okāsaṃ na labhanti, liṅgatthamattajotikā paṭhamāvibhatti eva okāsaṃ labhati. evarūpāni tulyādhikaraṇabhūtāni abhidheyyapadāni sandhāya sutte ‘tumhamhasesesū’ti vuttaṃ.</p>\r\n<p class="bodytext">idañca suttaṃ suddhehi tumha’mha, sesanāmehi yuttavākye ca missakehi yuttavākye cāti dvīsu dvīsu vākyesu veditabbaṃ.</p>\r\n<p class="bodytext">tattha suddhehi yutte paccekaṃ dukāni vattanti. yathā? so gacchati, te gacchanti, tvaṃ gacchasi, tumhe gacchatha, ahaṃ gacchāmi, mayaṃ gacchāmāti.</p>\r\n<p class="bodytext">tathā suddhadvandepi. yathā? so ca so ca gacchati, gacchanti vā. te ca te ca gacchanti, so ca te ca gacchanti, tvañca tvañca gacchasi, gacchatha vā. tumhe ca tumhe ca gacchatha, tvañca tumhe ca gacchathāti.</p>\r\n<p class="bodytext">missakehi yutte dvandavākye pana ‘vippaṭisedhe’ti saṅketattā paradukāni eva okāsaṃ labhanti, tesu ca bahuvacanāni eva. yathā? so ca tvañca gacchatha, so ca ahañca gacchāma, tvañca ahañca gacchāma, so ca tvañca ahañca gacchāma. ekavacanacatukkaṃ.</p>\r\n<p class="bodytext">te ca tumhe ca gacchatha, te ca mayañca gacchāma, tumhe ca mayañca gacchāma, te ca tumhe ca mayañca gacchāma. bahuvacanacatukkaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54760,'annya_sadda_10',353,replace(replace('\r\n<p class="bodytext">so <a name="M0.0353"></a> ca tumhe ca gacchatha, so ca mayañca gacchāma, tvañca mayañca gacchāma, so ca tvañca mayañca gacchāma. ekavacanamūlacatukkaṃ.</p>\r\n<p class="bodytext">te ca tvañca gacchatha, te ca ahañca gacchāma, tumhe ca ahañca gacchāma, te ca tumhe ca ahañca gacchāma. bahuvacanamūlacatukkaṃ.</p>\r\n<p class="bodytext">api ca tvañca so ca gacchatha, ahañca so ca gacchāma, tvañca ahañca so ca gacchāma, tumhe ca so ca gacchatha, mayañca so ca gacchāma, tvañca te ca gacchatha, ahañca te ca gacchāmaiccādīnipi catukkāni veditabbāni.</p>\r\n<p class="bodytext">atrimā pāḷī – tuvañca putto suṇisā ca nattā, sammodamānā gharamāvasetha <span class="note">[jā. 1.8.7]</span>. ahañca putto suṇisā ca nattā, sammodamānā gharamāvasema <span class="note">[jā. 1.8.7]</span>.</p>\r\n<p class="bodytext">ahañca dāni āyasmā ca sāriputto bhikkhusaṅghaṃ pariharissāma <span class="note">[ma. ni. 2.160]</span>.</p>\r\n<p class="bodytext">ahañca ime ca bhikkhū samādhinā nisīdimhā.</p>\r\n<p class="bodytext">ahañca bhariyā ca dānapatī ahumhā <span class="note">[jā. 2.22.1593]</span>.</p>\r\n<p class="bodytext">ahañca sāmiko ca dānapatī ahumhā <span class="note">[jā. 2.22.1617]</span> iccādi.</p>\r\n<p class="bodytext">yaṃ pana "so ca gacchati, tvañca gacchasī"ti vattabbe "tumhe gacchathā"ti vā "so ca gacchati, ahañca gacchāmī"ti vattabbe "mayaṃ gacchāmā"ti vā vacanaṃ, taṃ pakatibahuvacanameva, na paropurisabahuvacanaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54761,'annya_sadda_10',354,replace(replace('\r\n<p class="bodytext">yañca kaccāyane – "sabbesamekābhidhāne paro puriso"ti <span class="note">[nī. 216 piṭṭhe]</span> suttaṃ, tatthapi sabbesaṃ dvinnaṃ vā tiṇṇaṃ vā missakabhūtānaṃ <a name="M0.0354"></a> nāma, tumha’mhānaṃ ekato abhidhāne missakadvandavākye paro puriso yojetabboti attho na na sambhavatīti.</p>\r\n<p class="bodytext"><a name="para564"></a><span class="paranum">564</span>. <span class="bld">kattari lo</span> <span class="note">[ka. 455; rū. 433; nī. 925]</span>.</p>\r\n<p class="bodytext">aparokkhesu māna, nta, tyādīsu paresu kriyatthā paraṃ kattari lānubandho apaccayo hoti. lānubandho ‘ūlasse’ti sutte visesanattho.</p>\r\n<p class="bodytext">etena yattha māna, nta, tyādayo kattari vattanti, tattha ayaṃ lapaccayoti lapaccayena tesaṃ kattuvācakabhāvaṃ ñāpeti, esa nayo "kyo bhāvakammesu." iccādīsupi.</p>\r\n<p class="bodytext">ettha ca vikaraṇapaccayā nāma byañjanapūraṇā eva honti, na atthapūraṇā, tasmā yasmiṃ payoge tehi vinā padarūpaṃ na sijjhati, tattheva te vattanti. yattha sijjhati, tattha na vattanti, ayampi lapaccayo dhātuto paraṃ vibhattisare vā āgamasare vā asante vattati, sante pana "pacāmi, pacāma, pacāhi, gameti, gamenti, vajjeti, vajjenti"-iccādīsu kāriyantaratthāya vattati. yattha ca paccayānaṃ lopo vihito, tattha gaṇantara, rūpantarappasaṅgapaṭisiddhāya vattati, aññattha na vattati.</p>\r\n<p class="bodytext"><a name="para565"></a><span class="paranum">565</span>. <span class="bld">yuvaṇṇānameo paccaye</span> <span class="note">[ka. 485; rū. 434; nī. 975; caṃ. 1.1.82; pā. 3.1.60]</span>.</p>\r\n<p class="bodytext">vibhatti, paccayā paccayo nāma. i, kī, khi, ci, ji iccādayo ivaṇṇā nāma. cu, ju, bhū, hū iccādayo uvaṇṇā nāma. paccaye pare ekakkharadhātvantānaṃ ivaṇṇu’vaṇṇānaṃ kamena e, ovuddhiyo honti. ‘paro kvacī’ti parasaralopo.</p>\r\n','\r',char(13)),'\n',char(10)),'-564-565-');
INSERT INTO pages VALUES(54762,'annya_sadda_10',355,replace(replace('\r\n<p class="bodytext">saṃpubbo-sambhoti<a name="M0.0355"></a>, sambhonti, sambhosi, sambhotha, sambhomi, sambhoma.</p>\r\n<p class="bodytext">anupubbo-anubhavane, so bhogaṃ anubhoti, te bhogaṃ anubhonti, tvaṃ bhogaṃ anubhosi, tumhe bhogaṃ anubhotha, ahaṃ bhogaṃ anubhomi, mayaṃ bhogaṃ anubhoma.</p>\r\n<p class="bodytext">tattha yathā "nīlo paṭo"ti ettha nīlasaddassa attho duvidho vāccattho, abhidheyyatthoti.</p>\r\n<p class="bodytext">tattha guṇasaṅkhāto sakattho vāccattho nāma.</p>\r\n<p class="bodytext">guṇanissayo dabbattho abhidheyyattho nāma.</p>\r\n<p class="bodytext">nīlasaddo pana vaccātthameva ujuṃ vadati, nīlasaddamattaṃ suṇanto nīlaguṇameva ujuṃ jānāti, tasmā "paṭo" iti padantarena nīlasaddassa abhidheyyattho ācikkhīyati.</p>\r\n<p class="bodytext">tathā "anubhotī"ti ettha tisaddassa attho duvidho vāccattho, abhidheyyatthoti.</p>\r\n<p class="bodytext">tattha kattusattisaṅkhāto sakattho <span class="bld">vāccattho</span> nāma.</p>\r\n<p class="bodytext">sattinissayo liṅgattho <span class="bld">abhidheyyattho</span> nāma.</p>\r\n<p class="bodytext">tisaddo pana vāccatthameva ujuṃ vadati, na abhidheyyatthaṃ. "anubhotī"ti suṇantosādhyakriyāsahitaṃ kattārameva ujuṃ jānāti, na kiñci dabbanti attho. tasmā "so" iti padantarena tisaddassa abhidheyyattho ācikkhīyati, vāccatthassa pana tisaddeneva ujuṃ vuttattā tatiyāvibhattiyā puna ācikkhitabbakiccaṃ natthi, liṅgatthajotanatthaṃ abhidheyyapade paṭhamāvibhatti eva pavattatīti. esa nayo sabbattha.</p>\r\n<p class="bodytext"><a name="para566"></a><span class="paranum">566</span>. <span class="bld">eonamayavā sare</span> <span class="note">[ka. 513, 514; rū. 435, 491; nī. 1027, 1028]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-566-');
INSERT INTO pages VALUES(54763,'annya_sadda_10',356,replace(replace('\r\n<p class="bodytext">sare <a name="M0.0356"></a> pare e, onaṃ kamena aya, avā honti. ya, vesu a-kāro uccāraṇattho.</p>\r\n<p class="bodytext">bhavati, bhavanti, bhavasi, bhavatha.</p>\r\n<p class="bodytext"><a name="para567"></a><span class="paranum">567</span>. <span class="bld">himimesvassa</span> <span class="note">[ka. 478; rū. 438; nī. 959]</span>.</p>\r\n<p class="bodytext">hi, mi, mesu paresu a-kārassa dīgho hoti.</p>\r\n<p class="bodytext">bhavāmi, bhavāma.</p>\r\n<p class="bodytext">parachakke – bhavate, bhavante, bhavase, bhavavhe, bhave, bhavamhe.</p>\r\n<p class="bodytext">papubbo bhū-pavattiyaṃ, nadī pabhavati.</p>\r\n<p class="bodytext">adhyā’bhi, paripubbo hiṃsāyaṃ, adhibhavati, abhibhavati, paribhavati.</p>\r\n<p class="bodytext">vipubbo vināse, pākaṭe, sobhaṇe ca, vibhavati.</p>\r\n<p class="bodytext">parāpubbo parājaye, parābhavati.</p>\r\n<p class="bodytext">abhi, saṃpubbo pattiyaṃ, ajjhogāhe ca, abhisambhavati, tathā pātubbhavati, āvibhavati iccādi.</p>\r\n<p class="centered">iti suddhakatturūpaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-567-');
INSERT INTO pages VALUES(54764,'annya_sadda_10',357,replace(replace('\r\n<p class="subhead">suddhabhāvakammarūpa</p>\r\n\r\n<p class="bodytext"><a name="para568"></a><span class="paranum">568</span>. <span class="bld">kyo</span> <a name="M0.0357"></a> <span class="bld">bhāvakammesvaparokkhesu māna nta tyādīsu</span> <span class="note">[ka. 440; rū. 445; nī. 920; caṃ. 1.1.80; pā. 3.1.67]</span>.</p>\r\n<p class="bodytext">parokkhāvajjitesu māna, ntapaccayesu tyādīsu ca paresu kriyatthā bhāvasmiṃ kammani ca kānubandho yapaccayo hoti, bahulādhikārā kvaci kattari ca.</p>\r\n<p class="bodytext">rūpaṃ vibhuyyati, so pahīyissati <span class="note">[saṃ. ni. 1.249]</span>, bhattaṃ paccati, gimhe udakaṃ chijjati, kusūlo bhijjati.</p>\r\n<p class="bodytext"><a name="para569"></a><span class="paranum">569</span>. <span class="bld">na te kānubandhanāgamesu</span>.</p>\r\n<p class="bodytext">kānubandhe nāgame ca ivaṇṇu’vaṇṇānaṃ assa ca te e, o,-ā na hontīti kyamhi vuddhi natthi.</p>\r\n<p class="bodytext">kamme-tena purisena bhogo anubhūyati, tena bhogā anubhūyanti, tena tvaṃ anubhūyasi, tena tumhe anubhūyatha, tena ahaṃ anubhūyāmi, tena mayaṃ anubhūyāma. yassa dvittaṃ rassattañca, anubhuyyati, anubhuyyanti.</p>\r\n<p class="bodytext">tattha "anubhūyatī"ti ettha kyapaccayasahitassa tisaddassa attho duvidho vāccattho, abhidheyyatthoti.</p>\r\n<p class="bodytext">tattha kammasattisaṅkhāto sakattho <span class="bld">vāccattho</span> nāma.</p>\r\n<p class="bodytext">sattinissayo liṅgattho <span class="bld">abhidheyyattho</span> nāma.</p>\r\n<p class="bodytext">tisaddo pana kyapaccayasahito vāccatthameva ujuṃ vadati, na abhidheyyatthaṃ. "anubhūyatī"ti suṇanto sādhyakriyāsahitaṃ kammasattiṃ eva ujuṃ jānāti, na kiñci dabbanti vuttaṃ hoti. sesaṃ pubbe vuttanayameva.</p>\r\n','\r',char(13)),'\n',char(10)),'-568-569-');
INSERT INTO pages VALUES(54765,'annya_sadda_10',358,replace(replace('\r\n<p class="bodytext">anubhūyate<a name="M0.0358"></a>, anubhuyyate, anubhūyante, anubhuyyante, anubhūyase, anubhūyavhe, anubhūye, anubhuyye, anubhūyamhe, anubhuyyamhe.</p>\r\n<p class="bodytext"><a name="para570"></a><span class="paranum">570</span>. <span class="bld">garupubbā rassā re ntentīnaṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ‘guru.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">garupubbamhā rassato nte, ntīnaṃ reādeso hoti.</p>\r\n<p class="bodytext">jāyare, jāyanti, jāyare, jāyante, gacchare, gacchanti, gacchare, gacchante, gamissare, gamissanti, gamissare, gamissante.</p>\r\n<p class="bodytext">garupubbāti kiṃ? pacanti, pacante.</p>\r\n<p class="bodytext">rassāti kiṃ? pācenti, pācante.</p>\r\n<p class="bodytext">ettha ca suttavibhāgena "sabbaṃ hidaṃ bhañjare kālapariyāyaṃ <span class="note">[jā. 1.15.370]</span>, muñcare bandhanasmā <span class="note">[jā. 2.22.1648]</span>, jīvare’ vāpi sussatī"ti <span class="note">[jā. 2.22.840]</span> etāni pāḷipadāni sijjhanti.</p>\r\n<p class="bodytext">tattha ‘bhañjare’ti bhijjati, ‘muñcare’ti muñcantu, ‘jīvare’ vāpī’ti jīvanto’vāpi. anubhūyare, anubhūyanti, anubhūyare, anubhūyante.</p>\r\n<p class="bodytext">bhāvo nāma bhavana, gamanādiko kriyākāro, so dhātunā eva tulyādhikaraṇabhāvena visesīyati, na nāmapadena, tasmā tattha tumha’mha, sesanāmavasena tyādidukavisesayogo nāma natthi, paṭhamadukameva tattha bhavati, dabbasseva ca tassa abyattasarūpattā saṅkhyābhedopi natthi, ekavacanameva bhavati.</p>\r\n<p class="bodytext">tena bhogaṃ anubhūyati, anubhuyyati, anubhūyate, anubhuyyate, anubhavanaṃ hotīti attho.</p>\r\n<p class="centered">iti suddhabhāvakammarūpāni.</p>\r\n','\r',char(13)),'\n',char(10)),'-570-');
INSERT INTO pages VALUES(54766,'annya_sadda_10',359,replace(replace('\r\n<p class="subhead">hetukatturūpa</p>\r\n\r\n<p class="bodytext"><a name="para571"></a><span class="paranum">571</span>. <span class="bld">payojakabyāpāre</span> <a name="M0.0359"></a> <span class="bld">ṇāpi ca</span> <span class="note">[ka. 438; rū. 540; nī. 914]</span>.</p>\r\n<p class="bodytext">yo suddhakattāraṃ payojeti, tassa payojakassa kattuno byāpāre kriyatthā ṇi ca ṇāpi ca honti. ṇānubandhā vuddhutthā.</p>\r\n<p class="bodytext">tesu ca ākārantato <span class="note">[‘ato’ (moga.)]</span> ṇāpiyeva hoti, dāpeti, dāpayati.</p>\r\n<p class="bodytext">uvaṇṇantato ṇiyeva, sāveti, sāvayati.</p>\r\n<p class="bodytext">sesato dvepi, pāceti, pācayati, pācāpeti, pācāpayati.</p>\r\n<p class="bodytext">payojakabyāpāropi kriyā evāti tadatthavācīhi ṇi,-ṇāpīhi paraṃ vibhatti, paccayā bhavanti, dhātvantassa ca ṇi, ṇāpīnañca vuddhi.</p>\r\n<p class="bodytext">so maggaṃ bhāveti, te maggaṃ bhāventi, tvaṃ maggaṃ bhāvesi, tumhe maggaṃ bhāvetha, ahaṃ maggaṃ bhāvemi, mayaṃ maggaṃ bhāvema.</p>\r\n<p class="bodytext"><a name="para572"></a><span class="paranum">572</span>. <span class="bld">āyāvā ṇānubandhe</span> <span class="note">[ka. 515; rū. 541; nī. 1029]</span>.</p>\r\n<p class="bodytext">e, onaṃ kamena āya, āvā honti sarādo ṇānubandhe paccaye pare, suttavibhattiyā aṇānubandhepi āyā’vā honti.</p>\r\n<p class="bodytext">ge-sadde, gāyati, gāyanti.</p>\r\n<p class="bodytext">apapubbo ce-pūjāyaṃ, apacāyati, apacāyanti.</p>\r\n<p class="bodytext">jhe-cintāyaṃ, jhāyati, jhāyanti, ujjhāyati, nijjhāyati iccādi.</p>\r\n<p class="bodytext"><a name="para573"></a><span class="paranum">573</span>. <span class="bld">ṇiṇāpyāpīhi ca</span> <span class="note">[".vā" (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-571-572-573-');
INSERT INTO pages VALUES(54767,'annya_sadda_10',360,replace(replace('\r\n<p class="bodytext">ṇi<a name="M0.0360"></a>, ṇāpi, āpīhi ca kattari lo hoti vā.</p>\r\n<p class="bodytext">kārayati, kārāpayati, saddāpayati.</p>\r\n<p class="bodytext">iminā asare ṭhāne ayādesato paraṃ akāro hoti, so maggaṃ bhāvayati, bhāvayanti, bhāvayasi, bhāvayatha, bhāvayāmi, bhāvayāma.</p>\r\n<p class="centered">iti hetukatturūpāni.</p>\r\n<p class="subhead">tyādi</p>\r\n<p class="bodytext">‘kyo bhāvakammesū.’ti ṇi, ṇāpipaccayantato yo.</p>\r\n<p class="bodytext"><a name="para574"></a><span class="paranum">574</span>. <span class="bld">kyassa</span> <span class="note">[ka. 442; rū. 448; nī. 922]</span>.</p>\r\n<p class="bodytext">kriyatthā parassa kyassa ādimhi īña hoti.</p>\r\n<p class="bodytext">tena maggo bhāvīyati, tena maggā bhāvīyanti, tena tvaṃ bhāvīyasi, tena tumhe bhāvīyatha, tena ahaṃ bhāvīyāmi, tena mayaṃ bhāvīyāma.</p>\r\n<p class="bodytext">rassatte-bhāviyati, bhāviyanti.</p>\r\n<p class="bodytext">dvitte-bhāviyyati, bhāviyyanti. tathā bhāvayīyati, bhāvayīyanti.</p>\r\n<p class="bodytext">rassatte-bhāvayiyati, bhāvayiyanti.</p>\r\n<p class="bodytext">dvitte-bhāvayiyyati, bhāvayiyyanti.</p>\r\n<p class="gatha1">akammikāpi yā dhātu, kārite tve’kakammikā.</p>\r\n<p class="gathalast">ekakammā dvikammā ca, dvikammā ca tikammakā.</p>\r\n','\r',char(13)),'\n',char(10)),'-574-');
INSERT INTO pages VALUES(54768,'annya_sadda_10',361,replace(replace('\r\n<p class="bodytext">iti <a name="M0.0361"></a> suddhakatturūpaṃ, suddhakammarūpaṃ, hetukatturūpaṃ, hetukammarūpanti ekadhātumhi cattāri nipphannarūpāni labbhanti.</p>\r\n<p class="bodytext">katturūpena cettha kammakatturūpampi saṅgayhati. kusūlo bhijjati, bhijjanadhammo bhijjati.</p>\r\n<p class="bodytext">kammarūpena ca kattukammarūpampi saṅgayhati. tattha yaṃ padaṃ kattuvācakaṃ samānaṃ saddarūpena kammarūpaṃ bhavati, taṃ kattukammarūpaṃ nāma, taṃ pāḷiyaṃ bahulaṃ dissati.</p>\r\n<p class="bodytext">rūpaṃ vibhāviyyati <span class="note">[mahāni. 108]</span>, atikkamiyyati, samatikkamiyyati, vītivattiyyati, nimittaṃ abhibhuyyatīti gotrabhu <span class="note">[paṭi. ma. 1.59]</span>, pavattaṃ abhibhuyyati <span class="note">[paṭi. ma. 1.59]</span>, cutiṃ abhibhuyyati, upapattiṃ abhibhuyyatīti <span class="note">[paṭi. ma. 1.59]</span> gotrabhu iccādi.</p>\r\n<p class="bodytext">tathā so pahīyethāpi nopi pahīyetha <span class="note">[saṃ. ni. 1.249]</span>, so pahīyissati <span class="note">[saṃ. ni. 1.249]</span>, nihiyyati yaso tassa <span class="note">[dī. ni. 3.246]</span>, hiyyoti hiyyati poso, pareti parihāyati <span class="note">[jā. 1.15.348]</span>, ājānīyā hasīyanti <span class="note">[jā. 2.22.2369]</span>, vidhurassa hadayaṃ dhaniyati <span class="note">[jā. 2.22.1350]</span> iccādi.</p>\r\n<p class="bodytext">yañca ‘yamhi dā dhā mā thā hā pā maha mathādīnamī’ti kaccāyane suttaṃ, taṃ kammani icchanti, kattari eva yujjati kammani ivaṇṇāgamassa sabbhāvā. saddanītiyaṃ pana "so pahīyissatī"ti padānaṃ bhāvarūpattaṃ daḷhaṃ vadati, tāni pana kattukammarūpāni evāti.</p>\r\n<p class="bodytext">ettha ca vattamānaṃ catubbidhaṃ niccapavattaṃ, pavattāvirataṃ, pavattuparataṃ, samīpavattamānanti.</p>\r\n<p class="bodytext">tattha <span class="bld">niccapavatte</span> – idhāyaṃ pabbato tiṭṭhati, candimasūriyā pariyāyanti, disā bhanti virocamānā <span class="note">[a. ni. 4.69]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54769,'annya_sadda_10',362,replace(replace('\r\n<p class="bodytext"><span class="bld">pavattāvirate</span> <a name="M0.0362"></a> – api nu te gahapati kule dānaṃ dīyatīti, dīyati me bhante kule dānaṃ <span class="note">[a. ni. 9.20]</span>.</p>\r\n<p class="bodytext">ettha ca yāva dāne saussāho, tāva yathāpavattā dānakriyā vattamānā eva nāma hoti ussāhassa aviratattā.</p>\r\n<p class="bodytext"><span class="bld">pavattuparate</span> – na khādati ayaṃ maṃsaṃ, neva pāṇaṃ hanati <span class="note">[a. ni. 3.67]</span>, na adinnaṃ ādiyati <span class="note">[a. ni. 3.67]</span>. ettha yāva tabbipakkhakriyaṃ na karoti, tāva viramaṇakriyā vattamānā eva nāma hoti.</p>\r\n<p class="bodytext"><span class="bld">samīpe</span> – atīte – kuto nu tvaṃ āgacchasi <span class="note">[saṃ. ni. 1.130]</span>, rājagahato āgacchāmīti. anāgate – dhammaṃ te desemi, sādhukaṃ suṇohi.</p>\r\n<p class="bodytext">suttavibhattena tadāyoge atītepi ayaṃ vibhatti hoti, vākacīrāni dhunanto, gacchāmi ambare tadā <span class="note">[bu. vaṃ. 2.37]</span>.</p>\r\n<p class="bodytext">yāva, pure, purāyoge anāgatepi-idheva tāva tiṭṭhāhi, yāvāhaṃ āgacchāmi, yaṃnūnāhaṃ dhammañca vinayañca saṅgāyeyyaṃ <span class="note">[pārā. aṭṭha. 1.paṭhamamahāsaṅgītikathā]</span>, pure adhammo dibbati, dhammo paṭibāhiyyati <span class="note">[cūḷava. 437]</span>, dante ime chinda purā marāmi <span class="note">[jā. 1.16.127]</span>.</p>\r\n<p class="bodytext">ekaṃsatthepi-nirayaṃ nūnagacchāmi, ettha me natthi saṃsayo <span class="note">[jā. 2.22.331]</span>. avassambhāviyatthepi-dhuvaṃ buddhā bhavāmahaṃ <span class="note">[bu. vaṃ. 2.109]</span>, dhuvaṃ buddhā bhavissati <span class="note">[bu. vaṃ. 2.81]</span> vā.</p>\r\n<p class="bodytext">aniyamatthepi-manasā ce pasannena, bhāsati vā karoti vā <span class="note">[dha. pa. 2]</span> cintetīti cittaṃ <span class="note">[dha. sa. aṭṭha. 1]</span>, phusatīti phasso <span class="note">[dha. sa. aṭṭha. 1]</span>, bujjhatīti buddhā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54770,'annya_sadda_10',363,replace(replace('\r\n<p class="bodytext">kadā <a name="M0.0363"></a> karahiyogepi-kadā gacchati, karahi gacchati, gamissati vā.</p>\r\n<p class="centered">iti tyādi.</p>\r\n<p class="subhead">tvādi</p>\r\n<p class="bodytext">atha tvādi vuccate.</p>\r\n<p class="bodytext"><a name="para575"></a><span class="paranum">575</span>. <span class="bld">tu antu hi tha mi ma taṃ antaṃ su vho e āmase</span> <span class="note">[ka. 424; rū. 450; nī. 897; caṃ. 1.3.122; pā. 3.3.162]</span>.</p>\r\n<p class="bodytext">vattamāne kāle pañha, patthanā, vidhīsu kriyatthā tvādayo honti.</p>\r\n<p class="bodytext">pañhe-dhammaṃ vā tvaṃ adhiyassu vinayaṃ vā <span class="note">[pāci. 471 (atthato sadisaṃ)]</span>.</p>\r\n<p class="bodytext">patthanāsaddena āsīsādayopi saṅgayhanti.</p>\r\n<p class="bodytext">tattha patthanāyaṃ-bhavābhave saṃsaranto, saddhā homi amaccharī.</p>\r\n<p class="bodytext">āsīsāyaṃ-etena saccavajjena, pajjunno abhivassatu <span class="note">[cariyā. 3.89]</span>, sabbe bhadrāni passantu <span class="note">[jā. 1.2.105]</span>, sabbe sattā averā hontu <span class="note">[paṭi. ma. 2.22]</span>.</p>\r\n<p class="bodytext">yācane-ekaṃ me nayanaṃ dehi <span class="note">[cariyā. 1.59]</span>.</p>\r\n<p class="bodytext">āyācane-desetu bhante bhagavā dhammaṃ <span class="note">[dī. ni. 2.68]</span>, ovadatu maṃ bhagavā <span class="note">[saṃ. ni. 3.1]</span>, anusāsatu maṃ sugata <span class="note">[saṃ. ni. 3.1]</span>, ullumpatu maṃ bhante saṅgho <span class="note">[mahāva. 71]</span>, asmākaṃ adhipannānaṃ, khamassu rājakuñjara <span class="note">[jā. 2.21.181]</span>, etha byagghā nivattavho <span class="note">[jā. 1.3.66]</span>.</p>\r\n<p class="bodytext">vidhisaddena niyojanādayopi saṅgayhanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-575-');
INSERT INTO pages VALUES(54771,'annya_sadda_10',364,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0364"></a> vidhimhi-akusalaṃ pajahatha, kusalaṃ upasampajja viharatha <span class="note">[paṭi. ma. 3.30; pārā. 19]</span>, evaṃ vitakketha, mā evaṃ vitakketha <span class="note">[paṭi. ma. 3.30]</span>.</p>\r\n<p class="bodytext">niyojane-etha bhikkhave sīlavā hotha <span class="note">[a. ni. 5.114]</span>, appamādena sampādetha <span class="note">[dī. ni. 2.185]</span>, etha gaṇhatha bandhatha <span class="note">[dī. ni. 2.342]</span>, mā vo muñcittha kiñcanaṃ <span class="note">[dī. ni. 2.342]</span>.</p>\r\n<p class="bodytext">ajjhesane-uddisatu bhante thero pātimokkhaṃ <span class="note">[mahāva. 155]</span>.</p>\r\n<p class="bodytext">āṇattiyaṃ-suṇātu me bhante saṅgho <span class="note">[pārā. 368]</span>.</p>\r\n<p class="bodytext">pesane-gacchatha tumhe sāriputtā <span class="note">[pārā. 432]</span>.</p>\r\n<p class="bodytext">pavāraṇāyaṃ-vadatu maṃ bhante saṅgho <span class="note">[mahāva. aṭṭha. 213]</span>, vadetha bhante yenattho <span class="note">[pārā. 290]</span>.</p>\r\n<p class="bodytext">anumatiyaṃ-paribhuñja vā vissajjehi vā yathāpaccayaṃ vā karohi <span class="note">[pāci. 374]</span>.</p>\r\n<p class="bodytext">varadāne-phussatī varavaṇṇābhe, varassu dasadhā vare <span class="note">[jā. 2.22.1655]</span>, icchitaṃ patthitaṃ tuyhaṃ, khippameva samijjhatu <span class="note">[a. ni. aṭṭha. 1.1.192]</span>.</p>\r\n<p class="bodytext">anuññāyaṃ-puccha vāsava maṃ pañhaṃ, yaṃ kiñci manasicchasi <span class="note">[dī. ni. 2.356]</span>.</p>\r\n<p class="bodytext">katāvakāsā pucchavho <span class="note">[su. ni. 1036]</span>.</p>\r\n<p class="bodytext">sampaṭicchane-evaṃ hotu <span class="note">[dī. ni. 2.419]</span>.</p>\r\n<p class="bodytext">akkose-muddhā te phalatu sattadhā <span class="note">[jā. 1.16.295]</span>, corā taṃ khaṇḍākhaṇḍikaṃ chindantu <span class="note">[ma. ni. aṭṭha. 1]</span>.</p>\r\n<p class="bodytext">sapathe-etena saccavajjena, putto uppajjataṃ ise <span class="note">[jā. 1.14.104]</span>, musā me bhaṇamānāya, muddhā phalatu sattadhā <span class="note">[jā. 1.14.104]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54772,'annya_sadda_10',365,replace(replace('\r\n<p class="bodytext">āmantane-etu <a name="M0.0365"></a> vessantaro rājā <span class="note">[jā. 2.22.2341]</span>, "ehi bhikkhu cara brahmacariyaṃ <span class="note">[mahāva. 28]</span>, etha bhikkhave sīlavā hotha" iccādīsupi ehi, ethasaddā āmantane tiṭṭhanti.</p>\r\n<p class="bodytext">nimantane-adhivāsetu me bhante bhagavā svātanāya bhattaṃ <span class="note">[pārā. 77]</span>.</p>\r\n<p class="bodytext">pavedane-vedayatīti maṃ saṅgho dhāretu <span class="note">[cūḷava. aṭṭha. 102]</span>, upāsakaṃ maṃ bhavaṃ gotamo dhāretu <span class="note">[dī. ni. 1.299]</span>, punārāyu ca me laddhā, evaṃ jānāhi mārisa <span class="note">[dī. ni. 2.369]</span>.</p>\r\n<p class="bodytext">pattakāle-parinibbātu bhante bhagavā, parinibbātu sugato, parinibbānakālo bhante bhagavato <span class="note">[dī. ni. 2.168]</span>, kālo kho te mahāvīra, uppajja mātukucchiyaṃ <span class="note">[bu. vaṃ. 1.67]</span>, byākarohi aggivessana na dāni te tuṇhībhāvassa kālo <span class="note">[ma. ni. 1.357]</span>.</p>\r\n<p class="bodytext">anubhotu, anubhontu, anubhohi, anubhotha, anubhomi, anubhoma, bhavatu, bhavantu, ‘himimesvassā’ti hi, mi, mesu assa dīgho, tvaṃ paṇḍito bhavāhi.</p>\r\n<p class="bodytext"><a name="para576"></a><span class="paranum">576</span>. <span class="bld">hissato lopo</span> <span class="note">[ka. 479; rū. 452; nī. 960; caṃ. 5.3.99; pā. 6.4.105]</span>.</p>\r\n<p class="bodytext">akārato parassa hissa lopo hoti.</p>\r\n<p class="bodytext">atoti kiṃ? brūhi, dehi, hohi.</p>\r\n<p class="bodytext">iminā ato hissa lopo, tvaṃ paṇḍito bhava.</p>\r\n<p class="bodytext">tumhe paṇḍitā bhavatha, bhavāmi, bhavāma.</p>\r\n<p class="bodytext">parachakke-so bhavataṃ, te bhavantaṃ, tvaṃ bhavassu, tumhe bhavavho, ahaṃ bhave, mayaṃ bhavāmase, imāni suddhakatturūpāni.</p>\r\n','\r',char(13)),'\n',char(10)),'-576-');
INSERT INTO pages VALUES(54773,'annya_sadda_10',366,replace(replace('\r\n<p class="bodytext">anubhūyatu<a name="M0.0366"></a>, anubhūyantu, anubhuyyatu, anubhuyyantu iccādi suddhakammarūpaṃ.</p>\r\n<p class="bodytext">bhāvetu, bhāventu, bhāvayatu, bhāvayantu iccādi hetukatturūpaṃ.</p>\r\n<p class="bodytext">bhāvīyatu, bhāvīyantu. rassatte-bhāviyatu, bhāviyantu. dvitte-bhāviyyatu, bhāviyyantu. tathā bhāvayīyatu, bhāvayīyantuiccādi hetukammarūpaṃ.</p>\r\n<p class="bodytext">‘eyyāthasse’iccādisuttena thassa vho, tumhe bhavavho, bhavatha vā.</p>\r\n<p class="centered">iti tvādi.</p>\r\n<p class="subhead">eyyādi</p>\r\n<p class="bodytext">atha eyyādi vuccate.</p>\r\n<p class="bodytext"><a name="para577"></a><span class="paranum">577</span>. <span class="bld">hetuphalesveyya eyyuṃ eyyāsi eyyātha eyyāmi eyyāma etha eraṃ etho eyyāvho</span> <span class="note">[eyyavho (moggallānādīsu)]</span> <span class="bld">eyyaṃ eyyāmhe vā</span> <span class="note">[ka. 416; rū. 454; nī. 880; caṃ. 1.3.120; pā. 3.3.156]</span>.</p>\r\n<p class="bodytext">aññamaññasambandhiniyā hetukriyāyañca phalakriyāyañca kriyatthā eyyādayo honti vā. hetuphalesupi kadāci aññavibhattuppattidīpanattho vāsaddo, sace so yānaṃ labhissati, gamissati, sace na labhissati, na gamissati iccādi.</p>\r\n<p class="bodytext">sace saṅkhāro nicco bhaveyya, sukho nāma bhaveyya, sace so paṇḍito bhaveyya, sukhito bhaveyya.</p>\r\n<p class="bodytext"><a name="para578"></a><span class="paranum">578</span>. <span class="bld">pañhapatthanāvidhīsu</span> <span class="note">[caṃ. 1.3.121; pā. 3.3.161]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-577-578-');
INSERT INTO pages VALUES(54774,'annya_sadda_10',367,replace(replace('\r\n<p class="bodytext">etesu <a name="M0.0367"></a> eyyādayo honti.</p>\r\n<p class="bodytext">pañhe-kinnu kho tvaṃ vinayaṃ vā adhiyyeyyāsi dhammaṃ vā.</p>\r\n<p class="bodytext">patthanāyaṃ-bhaveyyaṃ jātijātiyaṃ.</p>\r\n<p class="bodytext">vidhimhi-pāṇaṃ na haneyya, adinnaṃ na ādiyeyya, dānaṃ dadeyya, sīlaṃ rakkheyya.</p>\r\n<p class="bodytext"><a name="para579"></a><span class="paranum">579</span>. <span class="bld">sattārahesveyyādī</span> <span class="note">[ka. 416; rū. 454; nī. 881-4; caṃ. 1.3.128; pā. 3.3.169-172; satyarahesveyyādī (bahūsu)]</span>.</p>\r\n<p class="bodytext">sattiyaṃ arahatthe ca eyyādayo honti.</p>\r\n<p class="bodytext">bhavaṃ rajjaṃ kareyya, bhavaṃ rajjaṃ kātuṃ sakko, kātuṃ arahoti attho.</p>\r\n<p class="bodytext">so imaṃ vijaṭaye jaṭaṃ <span class="note">[saṃ. ni. 1.23]</span>.</p>\r\n<p class="bodytext"><a name="para580"></a><span class="paranum">580</span>. <span class="bld">sambhāvane vā</span> <span class="note">[ka. 416; rū. 854; nī. 881, 883-4; caṃ. 1.3.118-9; pā. 3.3.154-5]</span>.</p>\r\n<p class="bodytext">sambhāvanepi eyyādayo honti vā.</p>\r\n<p class="bodytext">pabbatamapi sirasā bhindeyya, bhaveyya, bhaveyyuṃ, bhaveyyāsi, bhaveyyātha, bhaveyyāmi, bhaveyyāma.</p>\r\n<p class="bodytext">parachakke-so bhavetha, te bhaveraṃ, tvaṃ bhavetho, tumhe bhaveyyāvho, ahaṃ bhaveyyaṃ, mayaṃ bhaveyyāmhe, iti suddhakatturūpāni.</p>\r\n<p class="bodytext">anubhūyeyya, anubhūyeyyuṃ. dvitte rassattaṃ, anubhuyyeyya, anubhuyyeyyuṃ iccādi suddhakammarūpaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-579-580-');
INSERT INTO pages VALUES(54775,'annya_sadda_10',368,replace(replace('\r\n<p class="bodytext">bhāveyya<a name="M0.0368"></a>, bhāveyyuṃ, bhāvayeyya, bhāvayeyyuṃ iccādi hetukatturūpaṃ.</p>\r\n<p class="bodytext">bhāvīyeyya, bhāvīyeyyuṃ. rassatte-bhāvayiyeyya, bhāvayiyeyyuṃ. dvitte-bhāviyyeyya, bhāviyyeyyuṃ. tathā bhāvayīyeyya, bhāvayīyeyyuṃ iccādi hetukammarūpaṃ.</p>\r\n<p class="bodytext"><a name="para581"></a><span class="paranum">581</span>. <span class="bld">eyyeyyāseyyaṃnaṃ ṭe</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">eyya, eyyāsi, eyyamiccetesaṃ ṭe hoti vā.</p>\r\n<p class="bodytext">atrimā pāḷī-caje mattā sukhaṃ dhīro, passe ce vipulaṃ sukhaṃ <span class="note">[dha. pa. 290]</span>. kiṃ tvaṃ sutasomā’nutappe <span class="note">[jā. 2.21.399]</span>, dhīraṃ passe suṇe dhīraṃ, dhīrena saha saṃvase <span class="note">[jā. 1.13.94]</span> iccādi.</p>\r\n<p class="bodytext">so bhave, bhaveyya, tvaṃ bhave, bhaveyyāsi, ahaṃ bhave, bhaveyyaṃ.</p>\r\n<p class="bodytext"><a name="para582"></a><span class="paranum">582</span>. <span class="bld">eyyuṃssuṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">eyyuṃssa uṃ hoti vā.</p>\r\n<p class="bodytext">atrimā pāḷī-vajjuṃ vā te na vā vajjuṃ, natthi nāsāya rūhanā <span class="note">[jā. 1.3.33]</span>, upayānāni me dajjuṃ, rājaputta tayī gateti <span class="note">[jā. 2.22.26]</span>.</p>\r\n<p class="bodytext"><a name="para583"></a><span class="paranum">583</span>. <span class="bld">eyyāmassemu ca</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">eyyāmassa emu ca hoti, antassa u ca.</p>\r\n','\r',char(13)),'\n',char(10)),'-581-582-583-');
INSERT INTO pages VALUES(54776,'annya_sadda_10',369,replace(replace('\r\n<p class="bodytext">atrimā pāḷī-kathaṃ jānemu taṃ mayaṃ <span class="note">[dī. ni. 2.318]</span>, muñcemu naṃ uragaṃ bandhanasmā <span class="note">[jā. 1.15.252]</span>, dakkhemu te nāga nivesanāni <span class="note">[jā. 1.15.254]</span>, gantvāna taṃ paṭikaremu accayaṃ, appeva naṃ putta labhemu jīvitaṃ <span class="note">[jā. 1.15.13]</span>, dajjemu kho <a name="M0.0369"></a> pañcasatāni bhoto <span class="note">[jā. 2.22.1302]</span>, pañhaṃ pucchemu mārisa <span class="note">[dī. ni. 2.354]</span>, viharemu averino <span class="note">[dī. ni. 2.357]</span>, tayājja guttā viharemu rattinti <span class="note">[jā. 1.2.18]</span>. bhaveyyāmu, bhaveyyāma.</p>\r\n<p class="bodytext">mahāvuttinā kvaci majjhe yyā-kārassa lopo, atthaṃ dhammañca pucchesi <span class="note">[jā. 1.16.150]</span>, uregaṇḍāyo bujjhesi, tāyo bujjhesi māṇava <span class="note">[jā. 2.17.132-133]</span>, yathā gatiṃ me abhisambhavetha <span class="note">[jā. 2.17.87-89]</span>, yathā gatiṃ te abhisambhavema <span class="note">[jā. 2.17.87-89]</span>, okāsaṃ sampajānātha, vane yattha vasemaseti <span class="note">[jā. 2.22.1885 ‘vasāmase’]</span>.</p>\r\n<p class="bodytext">‘eyyāthasse’iccādisuttena eyyāthassa o ca, tumhe bhaveyyātho, bhaveyyātha vā.</p>\r\n<p class="bodytext">ettha ca pubbe vuttā pañha, patthanā, vidhippabhedā idhapi yathāpayogaṃ veditabbā. pañhasaddena paripañha, paripucchā, parivitakka, parivīmaṃsādayo saṅgayhanti.</p>\r\n<p class="bodytext">paripañhe-dhammaṃ vā paṭhamaṃ saṅgāyeyyāma vinayaṃ vā.</p>\r\n<p class="bodytext">paripucchāyaṃ-vadetha bhante kimahaṃ kareyyaṃ, ko imassa attho, kathañcassa atthaṃ ahaṃ jāneyyaṃ.</p>\r\n<p class="bodytext">parivitakke-kassāhaṃ paṭhamaṃ dhammaṃ deseyyaṃ <span class="note">[dī. ni. 2.72]</span>, yaṃnūnāhaṃ dhammañca vinayañca saṅgāyeyyaṃ <span class="note">[pārā. aṭṭha. 1.paṭhamamahāsaṅgītikathā]</span>.</p>\r\n<p class="bodytext">parivīmaṃsāyaṃ-gaccheyyaṃ vā ahaṃ uposathaṃ, na vāgaccheyyaṃ <span class="note">[mahāva. 137]</span>.</p>\r\n<p class="bodytext">patthanāyaṃ-evaṃrūpo siyaṃ ahaṃ anāgatamaddhānaṃ <span class="note">[ma. ni. 3.274]</span>, ummādantyā ramitvāna, sivirājā tato siyaṃ <span class="note">[jā. 2.18.70]</span>, passeyya taṃ vassasataṃ arogaṃ <span class="note">[jā. 2.21.453]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54777,'annya_sadda_10',370,replace(replace('\r\n<p class="bodytext">āyācane-labheyyāhaṃ <a name="M0.0370"></a> bhante bhagavato santike pabbajjaṃ, labheyyaṃ upasampadaṃ <span class="note">[mahāva. 28; saṃ. ni. 2.17]</span>.</p>\r\n<p class="bodytext">vidhimhi-careyya dhammaṃ <span class="note">[jā. 1.14.63]</span>.</p>\r\n<p class="bodytext">niyojane-careyyādittasīsova, natthi maccussa nā gamo <span class="note">[saṃ. ni. 1.145]</span>.</p>\r\n<p class="bodytext">ajjhesane-yassa siyā āpatti, so āvikareyya <span class="note">[mahāva. 132]</span>, yassa nakkhamati, so bhāseyya <span class="note">[mahāva. 70]</span>.</p>\r\n<p class="bodytext">pavāraṇāyaṃ-vadeyyātha bhante yenattho <span class="note">[pārā. 290]</span>.</p>\r\n<p class="bodytext">anumatiyaṃ-taṃ jano hareyya vā daheyya vā yathāpaccayaṃ vā kareyya <span class="note">[ma. ni. 1.247]</span>.</p>\r\n<p class="bodytext">anuññāyaṃ-ākaṅkhamāno saṅgho kammaṃ kareyya <span class="note">[cūḷava. 6]</span>.</p>\r\n<p class="bodytext">āmantane-yadā te pahiṇeyyāmi, tadā eyyāsi khattiya <span class="note">[jā. 2.22.635]</span>.</p>\r\n<p class="bodytext">nimantane-idha bhavaṃ nisīdeyya.</p>\r\n<p class="bodytext">pattakāle-saṅgho uposathaṃ kareyya, pātimokkhaṃ uddiseyya <span class="note">[mahāva. 167]</span>.</p>\r\n<p class="bodytext">‘sambhāvane vā’ti vāsaddo avuttavikappanattho, tena parikappa, kriyātipannādayo saṅgayhanti.</p>\r\n<p class="bodytext">tattha parikappo duvidho bhūtā’bhūtavasena.</p>\r\n<p class="bodytext">tattha bhūtaparikappe-yo bālaṃ seveyya, sopi bālo bhaveyya.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54778,'annya_sadda_10',371,replace(replace('\r\n<p class="bodytext">abhūtaparikappe-yadā <a name="M0.0371"></a> kacchapalomānaṃ, pāvāro tividho siyā <span class="note">[jā. 1.8.78]</span>. yadā sasavisāṇānaṃ, nisseṇī sukatā siyā <span class="note">[jā. 1.8.79]</span>.</p>\r\n<p class="bodytext">kriyātipanne-sace so agāraṃ ajjhāvaseyya, rājā assa cakkavattī <span class="note">[dī. ni. 3.136]</span>.</p>\r\n<p class="centered">iti eyyādi.</p>\r\n<p class="subhead">hiyyattanī</p>\r\n<p class="bodytext">atha hiyyattanī vuccate.</p>\r\n<p class="bodytext"><a name="para584"></a><span class="paranum">584</span>. <span class="bld">anajjattane ā ū o ttha a mhā ttha tthuṃ se vhaṃ iṃ mhase</span> <span class="note">[ka. 418; rū. 456; nī. 886; caṃ. 1.2.77; pā. 3.2.111]</span>.</p>\r\n<p class="bodytext">ajjato aññasmiṃ bhūte kāle kriyatthā paraṃ āiccādayo honti.</p>\r\n<p class="bodytext"><a name="para585"></a><span class="paranum">585</span>. <span class="bld">ā ī ssādīsvau vā</span> <span class="note">[ka. 519; rū. 457; nī. 1032]</span>.</p>\r\n<p class="bodytext">āiccādīsu īiccādīsu ssādīsu ca tesaṃ ādimhi au hoti vā.</p>\r\n<p class="bodytext">so abhavā, bhavā, te abhavū, bhavū, tvaṃ abhavo, bhavo, tumhe abhavattha, bhavattha, ahaṃ abhava, bhava, mayaṃ abhavamhā, bhavamhā.</p>\r\n<p class="bodytext">parachakke-abhavattha, bhavattha, abhavatthuṃ, bhavatthuṃ, abhavase, bhavase, abhavavhaṃ, bhavavhaṃ, abhaviṃ, bhaviṃ, abhavamhase, bhavamhase.</p>\r\n','\r',char(13)),'\n',char(10)),'-584-585-');
INSERT INTO pages VALUES(54779,'annya_sadda_10',372,replace(replace('\r\n<p class="bodytext"><a name="para586"></a><span class="paranum">586</span>. <span class="bld">ā</span> <a name="M0.0372"></a> <span class="bld">ī ū mhā ssā ssāmhānaṃ vā</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">etesaṃ rasso hoti vā.</p>\r\n<p class="bodytext">so abhava, te abhavu, mayaṃ abhavamha.</p>\r\n<p class="bodytext">‘eyyāthasse’iccādinā āssa tthattaṃ, assa ca aṃ, so abhavattha, bhavattha, abhavā, bhavā vā, ahaṃ abhavaṃ, bhavaṃ, abhava, bhava vā, imāni suddhakatturūpāni.</p>\r\n<p class="bodytext">ettha ca mahāvuttinā ā-vibhattiyā thādeso bahulaṃ dissati, medanī sampakampatha <span class="note">[jā. 2.22.1672]</span>, visaññī samapajjatha <span class="note">[jā. 2.22.328]</span>, imā gāthā abhāsatha <span class="note">[jā. 2.22.328]</span>, tuccho kāyo adissatha <span class="note">[theragā. 172]</span>, nibbidā samatiṭṭhatha <span class="note">[theragā. 273]</span>, eko rahasi jhāyatha <span class="note">[jā. 1.15.286]</span> iccādi. tathā o-vibhattiyā ca, dubbheyyaṃ maṃ amaññatha iccādi.</p>\r\n<p class="centered">iti hiyyattanī.</p>\r\n<p class="subhead">ajjattanī</p>\r\n<p class="bodytext">atha ajjattanī vuccate.</p>\r\n<p class="bodytext"><a name="para587"></a><span class="paranum">587</span>. <span class="bld">bhūte ī uṃ o ttha iṃ mhā ā ū se vhaṃ aṃ mhe</span> <span class="note">[ka. 419; rū. 469; nī. 887]</span>.</p>\r\n<p class="bodytext">abhavīti bhūto, atītoti attho, bhūte kāle kriyatthā paraṃ īiccādayo honti.</p>\r\n<p class="bodytext"><a name="para588"></a><span class="paranum">588</span>. <span class="bld">a ī ssā ssatyādīnaṃ byañjanassiu</span> <span class="note">[ka. 516; rū. 466; nī. 1030; caṃ. 1.2.76; pā. 3.2.110 aīssaādīnaṃ byañjanassiu (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-586-587-588-');
INSERT INTO pages VALUES(54780,'annya_sadda_10',373,replace(replace('\r\n<p class="bodytext">aādissa īādissa ssāādissa ssatiādissa ca byañjanassa ādimhi iu hoti. ‘byañjanassā’ti etena aādimhi <a name="M0.0373"></a> pañca, īādimhi sattāti dvādasa suddhasaravibhattiyo paṭikkhipati.</p>\r\n<p class="bodytext">‘āīssādīsvau vā’iti suttena vikappena dhātvādimhi akāro.</p>\r\n<p class="bodytext">so abhavī, bhavī, te abhavuṃ, bhavuṃ, tvaṃ abhavo, bhavo, tumhe abhavittha, bhavittha, ahaṃ abhaviṃ, bhaviṃ, mayaṃ abhavimhā, bhavimhā, so abhavā, bhavā, te abhavū, bhavū, tvaṃ abhavise, bhavise, tumhe abhavivhaṃ, bhavivhaṃ, ahaṃ abhavaṃ, bhavaṃ, mayaṃ abhavimhe, bhavimhe.</p>\r\n<p class="bodytext">‘āīū’iccādinā ī, mhā, ā, ūnaṃ rassatte-so abhavi, bhavi, mayaṃ abhavimha, bhavimha, so abhava, bhava, te abhavu, bhavu.</p>\r\n<p class="bodytext"><a name="para589"></a><span class="paranum">589</span>. <span class="bld">eyyāthasseaāīthānaṃ o a aṃ ttha ttho vho vā</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ‘.vhoka’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">eyyāthādīnaṃ yathākkamaṃ oādayo honti vā.</p>\r\n<p class="bodytext">tumhe gaccheyyātho, gaccheyyātha vā, tvaṃ agacchissa, agacchisse vā, ahaṃ agamaṃ, gamaṃ, agama, gama vā, so agamittha, gamittha, agamā, gamā vā, so agamittho, gamittho, agamī, gamī vā, tumhe gacchavho, gacchatha vāti.</p>\r\n<p class="bodytext">iminā ī, ā, avacanānaṃ ttho, ttha, aṃādesā honti, so abhavittho, bhavittho, so abhavittha, bhavittha, ahaṃ abhavaṃ, bhavaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-589-');
INSERT INTO pages VALUES(54781,'annya_sadda_10',374,replace(replace('\r\n<p class="bodytext">atrimā pāḷī – īmhi-paṅko ca mā visiyittho <span class="note">[jā. 1.13.44]</span>, sañjagghittho mayā saha <span class="note">[jā. 1.16.241]</span>. āmhi-anumodittha vāsavo <span class="note">[jā. 2.22.1667]</span>, nimantayittha <a name="M0.0374"></a> vāsavo <span class="note">[jā. 2.22.1667]</span>, khubbhittha nagaraṃ tadā <span class="note">[jā. 2.22.1673]</span>, subhūtitthero gāthaṃ abhāsittha <span class="note">[theragā. 1]</span>. amhi-idhāhaṃ mallikaṃ deviṃ etadavocaṃ <span class="note">[saṃ. ni. 1.119]</span>, ajānamevaṃ āvuso avacaṃ jānāmīti <span class="note">[pārā. 195]</span>, ahaṃ kāmānaṃ vasamanvagaṃ <span class="note">[jā. 2.19.45]</span>, ajjhagaṃ amataṃ santiṃ iccādi.</p>\r\n<p class="bodytext"><a name="para590"></a><span class="paranum">590</span>. <span class="bld">uṃssiṃsvaṃsu</span> <span class="note">[ka. 504, 517; rū. 470-488; nī. 1016-1105]</span>.</p>\r\n<p class="bodytext">umiccassa iṃsu, aṃsu honti.</p>\r\n<p class="bodytext">agamiṃsu, agamaṃsu, agamuṃ. iminā uṃssa iṃsu, abhaviṃsu, bhaviṃsu.</p>\r\n<p class="bodytext"><a name="para591"></a><span class="paranum">591</span>. <span class="bld">ossa a i ttha ttho</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">ossa aiccādayo honti.</p>\r\n<p class="bodytext">tvaṃ abhava, tvaṃ abhavi, tvaṃ abhavittha, tvaṃ abhavittho.</p>\r\n<p class="bodytext">atrimā pāḷī-ossa atte-mā hevaṃ ānanda avaca <span class="note">[dī. ni. 2.95]</span>, tvameva dāni’makara, yaṃ kāmo byagamā tayi <span class="note">[jā. 1.2.167]</span>. itte-mā tvaṃ bhāyi mahārāja, mā tvaṃ bhāyi rathesabha <span class="note">[jā. 2.22.684]</span>, mā cintesi mā tvaṃ soci, yācāmi luddakaṃ ahaṃ <span class="note">[gavesitabbaṃ]</span>. tthattemāssu tiṇṇo amaññittha <span class="note">[jā. 2.22.255]</span>, mā kilittha mayā vinā <span class="note">[jā. 2.22.1713]</span>, māssu kujjhittha nāvika <span class="note">[jā. 1.6.5]</span>. tthotte-mā purāṇe amaññittho <span class="note">[theragā. 280]</span>, mā dayhittho punappunaṃ <span class="note">[saṃ. ni. 1.212]</span>, tiṇamatte asajjittho <span class="note">[jā. 1.1.89]</span>, mā tvaṃ brahmuno vacanaṃ upātivattittho <span class="note">[ma. ni. 1.502]</span>, mā tvaṃ maññittho na maṃ jānātī <span class="note">[ma. ni. 1.502]</span> ti.</p>\r\n','\r',char(13)),'\n',char(10)),'-590-591-');
INSERT INTO pages VALUES(54782,'annya_sadda_10',375,replace(replace('\r\n<p class="bodytext">tattha <a name="M0.0375"></a> ‘mā dayhittho’iccādīni parokkhāvacanenapi sijjhanti.</p>\r\n<p class="bodytext">suttavibhattena tthassa ttho hoti, taṃ vo vadāmi bhaddante, yāvantettha samāgatā <span class="note">[apa. thera 1.1.367]</span>, massu mittānaṃ dubbhittho. mittadubbho hi pāpakoti <span class="note">[jā. 1.16.222]</span>.</p>\r\n<p class="bodytext">mahāvuttinā ossa kvaci lopo, puna dānaṃ adā tuvaṃ <span class="note">[jā. 2.22.1786]</span>, mā no tvaṃ tāta adadā <span class="note">[jā. 2.22.2126]</span>, mā bhoti kupitā ahu <span class="note">[jā. 2.22.1931]</span>, māhu pacchānutāpinī <span class="note">[saṃ. ni. 1.162; therīgā. 57]</span>.</p>\r\n<p class="bodytext"><a name="para592"></a><span class="paranum">592</span>. <span class="bld">si</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">ossa si hoti vā.</p>\r\n<p class="bodytext">tvaṃ abhavasi, bhavasi, tvaṃ anubhosi.</p>\r\n<p class="bodytext"><a name="para593"></a><span class="paranum">593</span>. <span class="bld">mhātthānamuu</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">mhā, tthānaṃ ādimhi uu hoti.</p>\r\n<p class="bodytext">assosumhā, ahesumhā, avocumhā, avocuttha iccādīni dissanti.</p>\r\n<p class="bodytext">tumhe abhavuttha, bhavuttha, abhavittha, bhavittha vā, mayaṃ abhavumhā, bhavumhā, abhavimhā, bhavimhā vā.</p>\r\n<p class="bodytext"><a name="para594"></a><span class="paranum">594</span>. <span class="bld">iṃssa ca suu</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ".siu" (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-592-593-594-');
INSERT INTO pages VALUES(54783,'annya_sadda_10',376,replace(replace('\r\n<p class="bodytext">imiccassa mhā, tthānañca ādimhi suu hoti, sāgamo hotīti attho. casaddena īādīnampi ādimhi sāgamo hoti, sāgame ca sati byañjanaṃ hoti, tassa ādimhi iāgamo labbhati. tena "imā gāthā abhāsisuṃ <span class="note">[gavesitabbaṃ]</span>, te me <a name="M0.0376"></a> asse ayācisuṃ, yathābhūtaṃ vipassisuṃ" <span class="note">[jā. 2.22.1863]</span> iccādīni <span class="note">[dī. ni. 3.277]</span> sijjhanti.</p>\r\n<p class="bodytext">so bhogaṃ anubhosi, anubhavi vā, tumhe anubhosittha, anubhavittha vā, ahaṃ anubhosi, anubhaviṃ vā. mayaṃ anubhosimhā anubhavimhā vā.</p>\r\n<p class="bodytext"><a name="para595"></a><span class="paranum">595</span>. <span class="bld">eontā suṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ‘eottā suṃ" (bahūsu)]</span>.</p>\r\n<p class="bodytext">edantato odantato ca parassa uṃvacanassa suṃ hoti vā.</p>\r\n<p class="bodytext">ānesuṃ, sāyesuṃ, cintesuṃ, paccanubhosuṃ, paribhosuṃ, adhibhosuṃ, abhibhosuṃ.</p>\r\n<p class="bodytext">suttavibhattena ādantatopi ca, vihāsuṃ viharanti ca <span class="note">[saṃ. ni. 1.173]</span>, te anubhosuṃ, imāni suddhakatturūpāni.</p>\r\n<p class="bodytext">tena bhogo anvabhūyī, anubhūyī.</p>\r\n<p class="bodytext">rassatte-anvabhūyi, anubhūyi.</p>\r\n<p class="bodytext">dvitte-anvabhuyyi, anubhuyyi.</p>\r\n<p class="bodytext">tena bhogā anvabhūyuṃ, anubhūyuṃ, anvabhūyiṃsu, anubhūyiṃsu iccādi suddhakammarūpaṃ.</p>\r\n<p class="bodytext">so maggaṃ abhāvi, bhāvi, abhāvesi, bhāvesi, abhāvayi, bhāvayi, te maggaṃ abhāviṃsu, bhāviṃsu.</p>\r\n','\r',char(13)),'\n',char(10)),'-595-');
INSERT INTO pages VALUES(54784,'annya_sadda_10',377,replace(replace('\r\n<p class="bodytext">‘eontāsu’nti edantamhā suṃ. te maggaṃ abhāvesuṃ, bhāvesuṃ, abhāvayiṃsu, bhāvayiṃsu, tvaṃ maggaṃ abhāvaya, bhāvaya, abhāvayi, bhāvayi, tvaṃ maggaṃ abhāvittha, bhāvittha, abhāvayittha, bhāvayittha, tvaṃ maggaṃ abhāvayittho, bhāvayittho, tvaṃ maggaṃ <a name="M0.0377"></a> abhāvesi, bhāvesi, tumhe maggaṃ abhāvittha, bhāvittha, abhāvayittha, bhāvayittha, ahaṃ maggaṃ abhāviṃ, bhāviṃ, abhāvesiṃ, bhāvesiṃ, abhāvayiṃ, bhāvayiṃ, mayaṃ maggaṃ abhāvimhā, bhāvimhā, abhāvimha, bhāvimha, abhāvayimhā, bhāvayimhā, abhāvayimha, bhāvayimha.</p>\r\n<p class="bodytext">so maggaṃ abhāvā, bhāvā, abhāvittha, bhāvittha, abhāvayittha, bhāvayittha iccādi hetukatturūpaṃ.</p>\r\n<p class="bodytext">tena maggo abhāviyi, bhāviyi, tena maggā abhāviyiṃsu, bhāviyiṃsu iccādi hetukammarūpaṃ.</p>\r\n<p class="centered">iti ajjattanī.</p>\r\n<p class="subhead">parokkhā</p>\r\n<p class="bodytext">atha parokkhā vuccate.</p>\r\n<p class="bodytext"><a name="para596"></a><span class="paranum">596</span>. <span class="bld">parokkhe a u e tha aṃ mha ttha re ttho vho iṃ mhe</span> <span class="note">[ka. 417; rū. 460; nī. 887; caṃ. 1.2.81; pā. 3.2.115]</span>.</p>\r\n<p class="bodytext">akkhānaṃ indriyānaṃ paraṃ parokkhaṃ, apaccakkhanti attho. bhūte kāle attano parokkhakriyāya vattabbāya kriyatthā aādayo honti.</p>\r\n<p class="bodytext">mahāvuttinā gassa dīgho vā, so kira jagāma, te kira jagāmu, tvaṃ kira jagāme, tumhe kira jagāmittha, ahaṃ kira jagāmaṃ, mayaṃ kira jagāmimha iccādi.</p>\r\n<p class="bodytext">ettha ca ‘so kira jagāma’ iccādīni <span class="bld">anussavaparokkhāni</span> nāma.</p>\r\n','\r',char(13)),'\n',char(10)),'-596-');
INSERT INTO pages VALUES(54785,'annya_sadda_10',378,replace(replace('\r\n<p class="bodytext">‘ahaṃ <a name="M0.0378"></a> kira jagāmaṃ, mayaṃ kira jagāmimhā’ti idaṃ attanā gantvāpi gamanaṃ pamuṭṭhassa vā asampaṭicchitukāmassa vā <span class="bld">paṭivacanaparokkhaṃ</span> nāma.</p>\r\n<p class="bodytext"><a name="para597"></a><span class="paranum">597</span>. <span class="bld">parokkhāyañca</span> <span class="note">[ka. 458; rū. 461; nī. 939; caṃ. 5.1.3; pā. 6.1.2]</span>.</p>\r\n<p class="bodytext">parokkhamhi pubbakkharaṃ ekassaraṃ dverūpaṃ hoti, casaddena aññasmimpi dvedverūpaṃ sijjhati.</p>\r\n<p class="bodytext">caṅkamati, daddallati, dadāti, jahāti, juhoti, lolupo, momūho.</p>\r\n<p class="bodytext"><a name="para598"></a><span class="paranum">598</span>. <span class="bld">dutiyacatutthānaṃ paṭhamatatiyā</span> <span class="note">[ka. 461; rū. 464; nī. 942]</span>.</p>\r\n<p class="bodytext">dvitte pubbesaṃ dutiya, catutthānaṃ kamena paṭhama, tatiyā honti.</p>\r\n<p class="bodytext"><a name="para599"></a><span class="paranum">599</span>. <span class="bld">pubbassa a</span> <span class="note">[ka. 450; rū. 463; nī. 946; caṃ. 6.2.126; pā. 7.4.73]</span>.</p>\r\n<p class="bodytext">dvitte pubbassa bhūssa anto a hoti.</p>\r\n<p class="bodytext"><a name="para600"></a><span class="paranum">600</span>. <span class="bld">bhūssa vuka</span> <span class="note">[ka. 475; rū. 465; nī. 956; caṃ. 5.3.92; pā. 6.4.88]</span>.</p>\r\n<p class="bodytext">dvitte bhūdhātussa ante vuka hoti, vāgamo hotīti attho.</p>\r\n<p class="bodytext">"tatthappanādo tumulo babhūvā"ti <span class="note">[jā. 2.22.1437]</span> pāḷi.</p>\r\n<p class="bodytext">so kira rājā babhūva, te kira rājāno babhūvu, tvaṃ babhūve.</p>\r\n','\r',char(13)),'\n',char(10)),'-597-598-599-600-');
INSERT INTO pages VALUES(54786,'annya_sadda_10',379,replace(replace('\r\n<p class="bodytext">‘a ī ssā ssatyādīnaṃ byañjanassiu’ iti suttena byañjanādimhi iāgamo, tumhe babhūvittha, ahaṃ babhūvaṃ, mayaṃ babhūvimha<a name="M0.0379"></a>, so babhūvittha, te babhūvire, tvaṃ babhūvittho, tumhe babhūvivho, ahaṃ babhūviṃ, mayaṃ babhūvimhe, imāni suddhakatturūpāni.</p>\r\n<p class="bodytext">‘kyo bhāvakammesvaparokkhesū’ti paṭisiddhattā parokkhamhi bhāvakammesu yapaccayo na hoti, ‘tena kira bhogo anubabhūvittha, tena bhogo anubabhūvire’tiādinā yojetabbaṃ.</p>\r\n<p class="centered">iti parokkhā.</p>\r\n<p class="subhead">ssatyādi</p>\r\n<p class="bodytext">atha ssatyādi vuccate.</p>\r\n<p class="bodytext"><a name="para601"></a><span class="paranum">601</span>. <span class="bld">bhavissati ssati ssanti ssasi ssatha ssāmi ssāma ssate ssante ssase ssavhe ssaṃ ssāmhe</span> <span class="note">[ka. 421; rū. 473; nī. 892; caṃ. 1.3.2; pā. 3.3.13]</span>.</p>\r\n<p class="bodytext">bhavissatīti bhavissanto, anāgatakālo, tasmiṃ bhavissati kāle kriyatthassa tyādayo honti.</p>\r\n<p class="bodytext"><a name="para602"></a><span class="paranum">602</span>. <span class="bld">nāme garahāvimhayesu</span> <span class="note">[ka. 421; rū. 473; nī. 893; caṃ. 1.3.109, 115; pā. 3.3.143, 150]</span>.</p>\r\n<p class="bodytext">nipātanāmayoge garahāyañca vimhaye ca ssatyādayo honti, atītakālepi ssatyādīnaṃ uppattidīpanatthamidaṃ suttaṃ, anutthunana, paccānutāpa, paccānumodanādīnipi ettha saṅgayhanti.</p>\r\n<p class="bodytext">tattha garahāyaṃ-atthi nāma tāta sudinna ābhidosikaṃ kummāsaṃ paribhuñjissasi <span class="note">[pārā. 32]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-601-602-');
INSERT INTO pages VALUES(54787,'annya_sadda_10',380,replace(replace('\r\n<p class="bodytext">vimhaye-yatra <a name="M0.0380"></a> hi nāma saññī samāno pañcamattānaṃ sakaṭasatānaṃ saddaṃ na sossati <span class="note">[dī. ni. 2.192]</span>.</p>\r\n<p class="bodytext">anutthunanādīsu-na attanā paṭicodessaṃ, na gaṇassa ārocessaṃ <span class="note">[pāci. 665]</span>, na pubbe dhanamesissaṃ <span class="note">[jā. 1.12.50]</span>, iti pacchānutappati <span class="note">[jā. 1.12.53]</span>, bhūtānaṃ nāpacāyissaṃ, pahu santo na posissaṃ, paradāraṃ asevissaṃ <span class="note">[jā. 1.12.54]</span>, na pubbe payirupāsissaṃ <span class="note">[jā. 1.12.58]</span>, iti pacchānutappati <span class="note">[jā. 1.12.50]</span>, anekajātisaṃsāraṃ, sandhāvissaṃ anibbisaṃ <span class="note">[dha. pa. 153]</span>.</p>\r\n<p class="bodytext">katthaci pana gāthāvasena ekasakāralopo, mitto mittassa pāniyaṃ, adinnaṃ paribhuñjisaṃ <span class="note">[jā. 1.11.59]</span>, nirayamhi apaccisaṃ <span class="note">[therīgā. 438]</span>, gacchanto naṃ udakkhisaṃ <span class="note">[gavesitabbaṃ]</span>, yoniso paccavekkhisaṃ <span class="note">[theragā. 347]</span> iccādi.</p>\r\n<p class="bodytext">‘a ī ssā ssatyādīnaṃ byañjanassiu’ iti iāgamo, bhavissati, bhavissanti, bhavissare, bhavissasi, bhavissatha, bhavissāmi, bhavissāma, bhavissate, bhavissante, bhavissare, bhavissase, bhavissavhe, bhavissaṃ, bhavissāmhe.</p>\r\n<p class="bodytext">anubhossati, anubhossanti, anubhossare iccādi suddhakatturūpaṃ.</p>\r\n<p class="bodytext">anubhūyissati, anubhūyissanti, anubhūyissare.</p>\r\n<p class="bodytext"><a name="para603"></a><span class="paranum">603</span>. <span class="bld">kyassa sse</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">kyassa lopo hoti vā ssakāravati vibhattimhi.</p>\r\n<p class="bodytext">tena maggo gamissati, gamīyissati vā, tena maggo agamissā, agamīyissā vāti vikappena kyassa lopo.</p>\r\n','\r',char(13)),'\n',char(10)),'-603-');
INSERT INTO pages VALUES(54788,'annya_sadda_10',381,replace(replace('\r\n<p class="bodytext">tena <a name="M0.0381"></a> bhogo anubhavissati, anubhūyissati vā, tena bhogā anubhavissanti, anubhūyissanti vā iccādi suddhakammarūpaṃ.</p>\r\n<p class="bodytext">bhāvissati, ‘ūlasse’ti issa e, bhāvessati, bhāvayissatiiccādi hetukatturūpaṃ.</p>\r\n<p class="bodytext">tena maggo bhāvīyissati, maggā bhāvīyissanti iccādi hetukammarūpaṃ.</p>\r\n<p class="centered">iti ssatyādi.</p>\r\n<p class="subhead">ssādi</p>\r\n<p class="bodytext">atha ssādi vuccate.</p>\r\n<p class="bodytext"><a name="para604"></a><span class="paranum">604</span>. <span class="bld">eyyādotipattiyaṃ ssā ssaṃsu sse ssatha ssaṃ ssāmhā ssatha ssiṃsu ssase ssavhe ssiṃ ssāmhase</span> <span class="note">[ka. 422; rū. 475; nī. 895; caṃ. 1.3.107; pā. 3.3.139; eyyādo vātipattiyaṃ (bahūsu)]</span>.</p>\r\n<p class="bodytext">eyyādivisaye kriyātipattiyaṃ ssādayo bhavanti. eyyādivisayo nāma hetuphalakriyāsambhavo, tadubhayakriyāya abhāvo kriyātipatti.</p>\r\n<p class="bodytext">sā duvidhā atītā ca anāgatā ca.</p>\r\n<p class="bodytext">tattha atītāyaṃ-sace so paṭhamavaye pabbajjaṃ alabhissā, arahā abhavissā <span class="note">[‘sace pana nikkhamitvā pabbajissa, arahattaṃ pāpuṇissa’ (dhammapada aṭṭha. 1)]</span> iccādi.</p>\r\n<p class="bodytext">anāgatāyaṃ-sacāhaṃ na gamissaṃ, mahājāniyo so abhavissā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-604-');
INSERT INTO pages VALUES(54789,'annya_sadda_10',382,replace(replace('\r\n<p class="bodytext">‘āīssādīsvau <a name="M0.0382"></a> vā’iti dhātvādimhi vikappena akārāgamo,‘a īssā ssatyādīnaṃ byañjanassiu’ iti ssādīsu iāgamo, abhavissā, bhavissā, abhavissaṃsu, bhavissaṃsu, abhavisse, bhavisse, abhavissatha, bhavissatha, abhavissaṃ, bhavissaṃ, abhavissāmhā, bhavissāmhā, abhavissatha, bhavissatha, abhavissiṃsu, bhavissiṃsu, abhavissase, bhavissase, abhavissavhe, bhavissavhe, abhavissiṃ, bhavissiṃ, abhavissāmhase, bhavissāmhase.</p>\r\n<p class="bodytext">‘āīū’iccādinā ssā, ssāmhānaṃ rassatte-so abhavissa, bhavissa, mayaṃ abhavissāmha, bhavissāmha. ‘eyyāthasse’iccādinā ssessa atte-tvaṃ abhavissa, bhavissa iccādīni rūpacatukkāni yathāsambhavaṃ yojetabbāni.</p>\r\n<p class="centered">itissādi.</p>\r\n<p class="centered">bhūdhāturūpaṃ niṭṭhitaṃ.</p>\r\n<p class="centered">aṭṭhavibhattuppattirāsi niṭṭhito.</p>\r\n<p class="subhead">bhūvādigaṇa</p>\r\n<p class="subhead">sarantadhātu</p>\r\n<p class="subhead">ākārantadhāturūpa</p>\r\n<p class="bodytext">ito paṭṭhāya sarantadhātuyo sarānukkamena, byañjanantadhātuyo akkharānukkamena vuccante.</p>\r\n<p class="bodytext">kaccāyanaganthe anekassaradhātuyo idha byañjanantadhātuyo nāma. tasmā idha dhātvantasaralopakiccaṃ nāma natthi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54790,'annya_sadda_10',383,replace(replace('\r\n<p class="bodytext">khā, khyā-kathane, gā-sadde, ghā-gandhopādāne, ñā-paññāyane avabodhane ca, ṭhā-gatinivattiyaṃ, tā-pālane, thā-ṭhāne<a name="M0.0383"></a>, dā-dāne, dhā-dhāraṇe, pā-pāne, phā-vuddhiyaṃ, bhā-dittiyaṃ, mā-māne, yā-gatiyaṃ, lā-ādāne chedane ca, vā-gati, bandha, gandhanesu, sā-assādane tanukaraṇe antakammani ca, hā-cāge, nhā-soceyye.</p>\r\n<p class="bodytext">tyādyuppatti, kattarilo, mahāvuttinā sare pare ādantamhā kvaci yāgamo, akkhāti. parassaralopo, akkhāyati, kriyaṃ ākhyāti, ākhyāyati, jātiṃ akkhāhi pucchito <span class="note">[su. ni. 423]</span>, saṅgāyati, saṅgāyiṃsu mahesayo <span class="note">[vi. va. aṭṭha. ganthārambhakathā]</span>, gandhaṃ ghāyati, paññāyati, paññāyanti, paññāyatu, paññāyantu.</p>\r\n<p class="bodytext">kamme kyo, dhammo ñāyati, dhammā ñāyanti, viññāyati, viññāyanti.</p>\r\n<p class="bodytext">payojakabyāpāre-ṇāpi, ñāpeti, ñāpenti, ñāpayati, ñāpayanti.</p>\r\n<p class="bodytext">kamme-ñāpīyati, ñāpīyanti, ṭhāti, ṭhānti, opupphā padmā ṭhānti, mālāva ganthitā ṭhānti, dhajaggāneva dissare <span class="note">[jā. 2.22.1989]</span>.</p>\r\n<p class="bodytext"><a name="para605"></a><span class="paranum">605</span>. <span class="bld">ñcīlasse</span> <span class="note">[ka. 510; rū. 487; nī. 1023]</span>.</p>\r\n<p class="bodytext">ñānubandhassa īāgamassa ca kattari vihitassa lapaccayassa ca kvaci ettaṃ hotīti lassa ettaṃ.</p>\r\n<p class="bodytext">adhiṭṭheti, adhiṭṭhenti.</p>\r\n<p class="bodytext"><a name="para606"></a><span class="paranum">606</span>. <span class="bld">ṭhāpānaṃ tiṭṭhapivā</span> <span class="note">[ka. 468-9; rū. 492-4; nī. 949]</span>.</p>\r\n<p class="bodytext">ṭhā, pānaṃ tiṭṭha, pivā honti nta, māna, tyādīsu.</p>\r\n<p class="bodytext">tiṭṭhati, tiṭṭhanti.</p>\r\n<p class="bodytext"><a name="para607"></a><span class="paranum">607</span>. <span class="bld">pādito ṭhāssa vā ṭhaho kvaci</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-605-606-607-');
INSERT INTO pages VALUES(54791,'annya_sadda_10',384,replace(replace('\r\n<p class="bodytext">pādayo <a name="M0.0384"></a> upasaggā pādi nāma, pādito parassa ṭhāssa ṭhaho hoti vā kvaci.</p>\r\n<p class="bodytext">saṇṭhahati, saṇṭhahanti, saṇṭhāti, saṇṭhānti, upaṭṭhahati, upaṭṭhahanti, upaṭṭhāti, upaṭṭhānti.</p>\r\n<p class="bodytext">kamme –</p>\r\n<p class="bodytext"><a name="para608"></a><span class="paranum">608</span>. <span class="bld">aññādissi kye</span> <span class="note">[ka. 502; rū. 493; nī. 1015; ‘aññādissāsīkye’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ñādito aññassa ākārantakriyatthassa i hoti kye paramhi.</p>\r\n<p class="bodytext">adhiṭṭhīyati, adhiṭṭhīyanti, upaṭṭhīyati, upaṭṭhīyanti.</p>\r\n<p class="bodytext">aññādissāti kiṃ? ñāyati, ñāyanti, ākkhāyati, ākkhāyanti, ākhyāyati, ākhyāyanti.</p>\r\n<p class="bodytext">ṇāpimhi-patiṭṭhāpeti, patiṭṭhāpenti, patiṭṭhāpayati, patiṭṭhāpayanti.</p>\r\n<p class="bodytext">ajjhattanimhi vikappena sāgamo, aṭṭhāsi, patiṭṭhāsi, adhiṭṭhahi, adhiṭṭhāsi, adhiṭṭhesi, saṇṭhahi, saṇṭhāsi, upaṭṭhahi, upaṭṭhāsi.</p>\r\n<p class="bodytext">‘uṃssiṃsvaṃsū’ti uṃssa iṃsu, aṃsu, adhiṭṭhahiṃsu, saṇṭhahiṃsu, upaṭṭhahiṃsu. atthamentamhi sūriye, vāḷā panthe upaṭṭhahuṃ <span class="note">[jā. 2.22.2186]</span>. aṭṭhaṃsu, upaṭṭhahaṃsu.</p>\r\n<p class="bodytext">parachakke-aṭṭhā buddhassa santike <span class="note">[su. ni. 431]</span>.</p>\r\n<p class="bodytext">kamme-adhiṭṭhiyi, adhiṭṭhiyiṃsu, upaṭṭhiyi, upaṭṭhiyiṃsu.</p>\r\n','\r',char(13)),'\n',char(10)),'-608-');
INSERT INTO pages VALUES(54792,'annya_sadda_10',385,replace(replace('\r\n<p class="bodytext">ṇāpimhi-patiṭṭhāpesi<a name="M0.0385"></a>, patiṭṭhāpayi, saṇṭhāpesi, saṇṭhāpayi.</p>\r\n<p class="bodytext">‘eontāsu’nti uṃssasuṃ, saralopo, aṭṭhāsuṃ, upaṭṭhāsuṃ, patiṭṭhāpesuṃ, saṇṭhāpesuṃ, patiṭṭhāpayuṃ, saṇṭhāpayuṃ, patiṭṭhāpayiṃsu, saṇṭhāpayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-ṭhassati, ṭhassanti, rassattaṃ, upaṭṭhissati, upaṭṭhissanti, ahaṃ bhotiṃ upaṭṭhissaṃ <span class="note">[jā. 2.22.1934]</span>, saṇṭhahissati, saṇṭhahissanti.</p>\r\n<p class="bodytext">ṇāpimhi-patiṭṭhāpessati, patiṭṭhāpessanti.</p>\r\n<p class="bodytext">tā-pālane, bhayaṃ tāyati.</p>\r\n<p class="bodytext">thā-ṭhāne, avatthāti, avatthāyati, vitthāyati, vitthāyanti, mā kho vitthāsi <span class="note">[mahāva. 126]</span>.</p>\r\n<p class="bodytext">dā-dāne, ‘ūlasse’ti lassa ettaṃ, deti, denti, desi, detha, demi, dema.</p>\r\n<p class="bodytext">‘parokkhāyañcā’ti sutte casaddena dāssa dvittaṃ. ‘rasso pubbassā’ti pubbassa rassattaṃ, dadāti, dadanti, dadāsi, dadātha, dadāmi, dadāma.</p>\r\n<p class="bodytext"><a name="para609"></a><span class="paranum">609</span>. <span class="bld">dāssa daṃ vā mimesvadvitte</span> <span class="note">[ka. 482; rū. 508; nī. 972]</span>.</p>\r\n<p class="bodytext">advitte dāssa daṃ hoti vā mi, mesu, niggahītassa vaggantattaṃ.</p>\r\n<p class="bodytext">dammi, damma.</p>\r\n<p class="bodytext">advitteti kiṃ? dadāmi, dadāma.</p>\r\n<p class="bodytext"><a name="para610"></a><span class="paranum">610</span>. <span class="bld">dāssiyaṅa</span> <span class="note">[ka. 502; rū. 493; nī. 1014]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-609-610-');
INSERT INTO pages VALUES(54793,'annya_sadda_10',386,replace(replace('\r\n<p class="bodytext">pādito <a name="M0.0386"></a> parassa dāssa iyaṅa hoti kvaci.</p>\r\n<p class="bodytext">‘iyaṅa’ iti suttavibhattena aññadhātūnampi. jā-hāniyaṃ, appena bahuṃ jiyyāma <span class="note">[jā. 1.2.52]</span>, tassevā’nuvidhiyyati <span class="note">[jā. 1.2.67]</span>, vidhurassa hadayaṃ dhaniyati <span class="note">[jā. 2.22.1350]</span>, nihīyati tassa yaso <span class="note">[jā. 1.10.60; a. ni. 4.17]</span>, eko rājā vihiyyasi <span class="note">[jā. 2.22.1750]</span>, so pahīyissati <span class="note">[saṃ. ni. 1.249]</span> iccādi.</p>\r\n<p class="bodytext">ādiyati, ādiyanti, upādiyati, upādiyanti, samādiyati, samādiyanti, sikkhāpadaṃ samādiyāmi <span class="note">[khu. pā. 2.1]</span>, vattaṃ samādiyāmi <span class="note">[cūḷava. 85]</span>.</p>\r\n<p class="bodytext"><a name="para611"></a><span class="paranum">611</span>. <span class="bld">gama vada dānaṃ ghamma vajja dajjā</span> <span class="note">[ka. 499-500-1; rū. 443-486-507; nī. 1013, 1005-1006]</span>.</p>\r\n<p class="bodytext">etesaṃ ghamma, vajja, dajjā honti vā nta, māna, tyādīsu.</p>\r\n<p class="bodytext">dajjati, dajjanti, dajjasi, dajjatha, dajjāmi, dajjāma.</p>\r\n<p class="bodytext">‘ūlasse’ti lassa ettaṃ, dajjeti, dajjenti.</p>\r\n<p class="bodytext">kamme ‘aññādissi kye’ti kyamhi āssa ittaṃ, diyati, diyanti.</p>\r\n<p class="bodytext">dīghatte-dīyati, dīyanti.</p>\r\n<p class="bodytext">dvitte-diyyati, diyyanti, dajjīyati, dajjīyanti.</p>\r\n<p class="bodytext">ṇāpimhi-dāpeti, dāpenti, dāpayati, dāpayanti.</p>\r\n<p class="bodytext">pādipubbe rasso, samādapeti <span class="note">[ma. ni. 2.387; 3.276]</span>, samādapenti, samādapayati, samādapayanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-611-');
INSERT INTO pages VALUES(54794,'annya_sadda_10',387,replace(replace('\r\n<p class="bodytext">kamme-dāpīyati<a name="M0.0387"></a>, dāpīyanti, samādapīyati, samādapīyanti.</p>\r\n<p class="bodytext">eyyādimhi mahāvuttinā dajjato eyyādīnaṃ antassa eyyassa lopo vā, dānaṃ dajjā, dadeyya, dajjuṃ, dadeyyuṃ, dajjāsi, dadeyyāsi, dajjātha, dadeyyātha, dajjāmi, dadeyyāmi, dajjāma, dadeyyāma, ahaṃ dajjaṃ, dadeyyaṃ, mayaṃ dajjāmhe, dadeyyāmhe.</p>\r\n<p class="bodytext">atrimā pāḷī-dajjā sappuriso dānaṃ <span class="note">[jā. 2.22.2261]</span>, upāyanāni me dajjuṃ <span class="note">[jā. 2.22.24]</span>, mātaraṃ kena dosena, dajjāsi dakarakkhino <span class="note">[jā 1.16.227]</span>. tāni ammāya dajjesi <span class="note">[jā. 2.22.2149]</span>, chaṭṭhāhaṃ dajjamattānaṃ, neva dajjaṃ mahosadhaṃ <span class="note">[jā. 1.16.225]</span>.</p>\r\n<p class="bodytext">ajjhattanimhi-adadi, adāsi, adaduṃ, adaṃsu, adajji, adajjuṃ, tvaṃ adado. varañce me ado sakka <span class="note">[jā. 2.17.142]</span>.</p>\r\n<p class="bodytext">parachakke-so dānaṃ adā, brāhmaṇassa adā dānaṃ, sivīnaṃ raṭṭhavaḍḍhano <span class="note">[jā. 2.22.2117]</span>.</p>\r\n<p class="bodytext">ssatyādimhi-dāssati, dāssanti, dadissati, dadissanti, dajjissati, dajjissanti.</p>\r\n<p class="bodytext">dāiccassa diccha, pavecchādesampi icchanti, vipulaṃ annaṃ pānañca, samaṇānaṃ pavecchasi <span class="note">[therīgā. 272]</span>. appasmeke pavecchanti, bahuneke na dicchare <span class="note">[saṃ. ni. 1.33]</span>. devo sammā dhāraṃ pavecchatu. bhojanaṃ bhojanatthīnaṃ, sammadeva pavecchatha.</p>\r\n<p class="bodytext">dhā-dhāraṇe, sandhāti, vidhāti, nidheti, nidhenti. vidheti, vidhenti.</p>\r\n<p class="bodytext">‘parokkhāyañcā’ti casaddena dvittaṃ. ‘dutiyacatutthāna.’nti dhassa dattaṃ. ‘rasso pubbassā’ti pubbassa rasso.</p>\r\n<p class="bodytext"><a name="para612"></a><span class="paranum">612</span>. <span class="bld">dhāssa ho</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-612-');
INSERT INTO pages VALUES(54795,'annya_sadda_10',388,replace(replace('\r\n<p class="bodytext">dvitte <a name="M0.0388"></a> parassa dhāssa ho hoti.</p>\r\n<p class="bodytext">saddahāti, saddahati vā. saddahāti tathāgatassa bodhiṃ. vidahāti, nidahāti, saddahanti, vidahanti, nidahanti.</p>\r\n<p class="bodytext">‘mayadā sare’ti sutte ‘mayadā’ti suttavibhattiyā byañjanepi niggahītassa dattaṃ, kammaṃ saddahāti, kammaphalaṃ saddahāti, saddahanti, vatthaṃ paridahāti, paridahanti.</p>\r\n<p class="bodytext">kamme-sandhīyati, sandhīyanti, sandhiyyati, sandhiyyanti, vidhiyyati. navena sukhadukkhena, porāṇaṃ apidhiyyati <span class="note">[jā. 1.2.114]</span>.</p>\r\n<p class="bodytext">ṇāpimhi-nidhāpeti, nidhāpenti, nidhāpayati, nidhāpayanti.</p>\r\n<p class="bodytext">kamme-nidhāpīyati, nidhāpīyanti.</p>\r\n<p class="bodytext">eyyādimhi-saddaheyyaṃ, saddaheyyuṃ.</p>\r\n<p class="bodytext">īādimhi-saddahi, saddahiṃsu. tatra bhikkhavo samādahiṃsu <span class="note">[dī. ni. 2.332]</span>, samādahaṃsu vā.</p>\r\n<p class="bodytext">pā-pāne, pāti, pānti.</p>\r\n<p class="bodytext">‘ṭhāpānaṃ tiṭṭhapivā’ti pāssa pivo, pivati, pivanti.</p>\r\n<p class="bodytext">kamme-pīyati, pīyanti.</p>\r\n<p class="bodytext">ṇimhi-‘āssā ṇāpimhi yuka’ iti suttena ṇānubandhe āssa ante yāgamo, puttaṃ thaññaṃ pāyeti, pāyenti, pāyayati, pāyayanti.</p>\r\n<p class="bodytext">kamme-pāyīyati, pāyīyanti.</p>\r\n<p class="bodytext">ssatyādimhi-passati, passanti, pissati, pissanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54796,'annya_sadda_10',389,replace(replace('\r\n<p class="bodytext">atrimā <a name="M0.0389"></a> pāḷī-ayañhi te mayā’ruḷho, saro pissati lohitaṃ <span class="note">[jā. 2.22.1968]</span>,aggodakāni pissāmi, yūthassa purato vajaṃ <span class="note">[jā. 1.8.14]</span>. naḷena vāriṃ pissāma, na ca maṃ tvaṃ vadhissasi <span class="note">[jā. 1.1.20]</span>.</p>\r\n<p class="bodytext">bhā-dittiyaṃ, bhāti, rattimābhāti candimā <span class="note">[dha. pa. 387]</span>, disā bhanti virocamānā <span class="note">[ma. ni. 1.503]</span>, daddallamānā ābhanti <span class="note">[vi. va. 738; jā. 2.22.508]</span>, paṭibhāti, paṭibhanti, paṭibhātu, paṭibhantu taṃ cunda bojjhaṅgā <span class="note">[saṃ. ni. 5.197]</span>, tisso maṃ upamāyo paṭibhaṃsu <span class="note">[ma. ni. 1.374]</span>, ratti vibhāti, vibhāyati.</p>\r\n<p class="bodytext">mā-māne, dvittaṃ rasso ca, mamāyati, mamāyanti. yemaṃ kāyaṃ mamāyanti, andhā bālā puthujjanā <span class="note">[theragā. 575]</span>.</p>\r\n<p class="bodytext">yā-gatiyaṃ, yāti, yanti, yāyati, yāyanti, yāyanta’ manuyāyanti <span class="note">[jā. 2.22.1753]</span>, uyyāti, uyyanti, niyyāti, niyyanti dhīrā lokamhā, hitvā māraṃ savāhanaṃ. anuyāti, anuyanti, anupariyāyati, anupariyāyanti.</p>\r\n<p class="bodytext">lā-ādāne, lāti.</p>\r\n<p class="bodytext">vā-gati, gandhanesu, vāti devesu uttamo <span class="note">[dha. pa. 56]</span>, vāto vāyati, vāyanti, nibbāti, nibbanti, nibbāyati, nibbāyanti, parinibbāti, parinibbāyati.</p>\r\n<p class="bodytext">ṇāpimhi-nibbāpeti, nibbāpayati.</p>\r\n<p class="bodytext">īādimhi-parinibbāyi, parinibbāyiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-parinibbissati, parinibbissanti, parinibbāyissati, parinibbāyissanti.</p>\r\n<p class="bodytext">sā-assādana, tanukaraṇa, antakriyāsu, sāti, sāyati, sāyanti, pariyosāyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54797,'annya_sadda_10',390,replace(replace('\r\n<p class="bodytext">ṇāpimhi-osāpeti<a name="M0.0390"></a>, pariyosāpeti, osāpayati, pariyosāpayati.</p>\r\n<p class="bodytext">kamme-osāpīyati, pariyosāpīyati.</p>\r\n<p class="bodytext">hā-cāge, pahāti, pahāyati, pahāyanti.</p>\r\n<p class="bodytext">kattu, kammani kyo, īuāgamo, hiyyoti hiyyati poso <span class="note">[jā. 1.15.348]</span>, nihiyyati tassa yaso <span class="note">[a. ni. 4.17; jā. 1.10.60]</span>, tvaṃ eko avahiyyasi, dvittaṃ rasso ca.</p>\r\n<p class="bodytext"><a name="para613"></a><span class="paranum">613</span>. <span class="bld">kavaggahānaṃ cavaggajā</span> <span class="note">[ka. 462-4; rū. 467-504; nī. 943-5; caṃ. 6.2.116; pā. 7.4.62]</span>.</p>\r\n<p class="bodytext">dvitte pubbesaṃ kavagga, hānaṃ cavagga, jā honti.</p>\r\n<p class="bodytext">jahāti, pajahāti, jahanti, pajahanti.</p>\r\n<p class="bodytext">kamme-pahīyati, pahīyanti.</p>\r\n<p class="bodytext">rassatte-pahiyati, pahiyanti.</p>\r\n<p class="bodytext">dvitte-pahiyyati, pahiyyanti, pajahīyati, pajahīyanti.</p>\r\n<p class="bodytext">ṇāpimhi-hāpeti, hāpenti, hāpayati, hāpayanti, jahāpeti, jahāpenti, jahāpayati, jahāpayanti.</p>\r\n<p class="bodytext">kamme-hāpīyati, jahāpīyati.</p>\r\n<p class="bodytext">īādimhi-pahāsi, pahāsuṃ, pajahi, pajahiṃsu.</p>\r\n<p class="bodytext"><a name="para614"></a><span class="paranum">614</span>. <span class="bld">hāto ha</span> <span class="note">[ka. 480; rū. 490; nī. 961]</span>.</p>\r\n<p class="bodytext">hāto parassa ssa-kārassa ha hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-613-614-');
INSERT INTO pages VALUES(54798,'annya_sadda_10',391,replace(replace('\r\n<p class="bodytext">"hāhisi <a name="M0.0391"></a> tvaṃ jīvaloka"nti <span class="note">[jā. 1.5.36]</span> pāḷi, hāhiti, hāhinti, hāhati, hāhanti, jahissati, hissati, aññamaññaṃ vadhitvāna, khippaṃ hissāma jīvitaṃ <span class="note">[jā. 2.22.673]</span>.</p>\r\n<p class="bodytext">nhā-soceyye, nhāti, nhāyati, nhāyanti.</p>\r\n<p class="bodytext">mahāvuttinā byañjanavaḍḍhane, nahāti, nahāyati, nahāyanti.</p>\r\n<p class="centered">iti bhūvādigaṇe ākārantadhāturūpaṃ.</p>\r\n<p class="subhead">ivaṇṇantadhāturūpa</p>\r\n<p class="bodytext">i-gatiyaṃ ajjhāyane ca, khi-khaye pakāsane ca, cicaye, ji-jaye, ḍī-vehāsagatiyaṃ, nī-naye, bhī-bhaye, lī-laye, sī-saye, mhi-hāse.</p>\r\n<p class="bodytext">tyādyuppatti, kattari lo, eti, enti, esi, etha, emi, ema, veti, venti, sameti, samenti, abbheti, abbhenti, abhisameti, abhisamenti, aveti, aventi, samaveti, samaventi, apeti, apenti, upeti, upenti, anveti, anventi, acceti, accenti, pacceti, paccenti, ajjheti, ajjhenti, udeti, udenti, samudeti, samudenti, pariyeti, pariyenti, upayati, upayanti, accayati, accayanti, udayati, samudayati.</p>\r\n<p class="bodytext">etu, sametu, entu, samentu, ehi, samehi, etha, sametha, etha byagghā nivattavho, paccupetha mahāvanaṃ <span class="note">[jā. 1.3.66]</span>.</p>\r\n<p class="bodytext">mahāvuttinā idhātumhā eyyādīnaṃ ekārassa lopo, na ca apatvā dukkhassantaṃ <span class="note">[saṃ. ni. 1.107]</span>, vissāsaṃ eyya paṇḍito, yadā te pahiṇeyyāmi, tadā eyyāsi khattiya <span class="note">[jā. 2.22.635]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54799,'annya_sadda_10',392,replace(replace('\r\n<p class="bodytext">īādimhi <a name="M0.0392"></a> ‘paro kvacī’ti vibhattisaralopo, dhammaṃ abhisami, abhisamiṃsu.</p>\r\n<p class="bodytext">‘eontā su’nti uṃssa suṃ, abhisamesuṃ, abhisamayuṃ, abhisamayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi –</p>\r\n<p class="bodytext"><a name="para615"></a><span class="paranum">615</span>. <span class="bld">etismā</span> <span class="note">[ka. 480; rū. 490; nī. 961]</span>.</p>\r\n<p class="bodytext">‘etī’ti dhātuniddeso tisaddo, idhātumhā parassa ssakārassa hi hoti vā.</p>\r\n<p class="bodytext">ehiti, essati. bodhirukkhamupehiti <span class="note">[bu. vaṃ. 2.63]</span>, nerañjaramupehiti <span class="note">[bu. vaṃ. 2.63]</span>, upessati, tadā ehinti me vasaṃ <span class="note">[jā. 1.1.33]</span>, tato nibbānamehisi <span class="note">[cūḷava. 382 tassuddānaṃ]</span>, na punaṃ jātijaraṃ upehisi <span class="note">[dha. pa. 238]</span>. suttavibhattena aññadhātūhipi, kathaṃ jīvihisi tvaṃ <span class="note">[apa. thera 1.3.13]</span>, jāyihitippasādo <span class="note">[jā. 2.17.145]</span>, paññāyihinti etā daharā <span class="note">[jā. 2.17.197]</span>.</p>\r\n<p class="bodytext">ssādimhi "sace puttaṃ siṅgālānaṃ, kukkurānaṃ adāhisī"ti <span class="note">[gavesitabbaṃ]</span> pāḷi, ‘adāhisī’ti ca adadissasetyattho.</p>\r\n<p class="bodytext">khi-khaye avaṇṇapakāsane ca, khayati, khayanti.</p>\r\n<p class="bodytext">khito yāgamo, vikappena yassa dvittaṃ, kappo khīyetha, vaṇṇo na khīyetha tathāgatassa <span class="note">[dī. ni. aṭṭha. 1.304]</span>, ujjhāyanti khīyanti <span class="note">[pārā. 88]</span>, avaṇṇaṃ pakāsentīti attho. khiyyati, khiyyanti, āyu khiyyati maccānaṃ, kunnadīnaṃva odakaṃ <span class="note">[saṃ. ni. 1.146]</span>.</p>\r\n<p class="bodytext">ci-caye, samucceti, samuccayati.</p>\r\n<p class="bodytext"><a name="para616"></a><span class="paranum">616</span>. <span class="bld">nito cissa cho</span> <span class="note">[ka. 20; rū. 27; nī. 50]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-615-616-');
INSERT INTO pages VALUES(54800,'annya_sadda_10',393,replace(replace('\r\n<p class="bodytext">nito <a name="M0.0393"></a> parassa cissa ca-kārassa cho hoti.</p>\r\n<p class="bodytext">nicchayati, vinicchayati, vinicchayanti, viniccheti, vinicchenti,</p>\r\n<p class="bodytext">kamme kyo –</p>\r\n<p class="bodytext"><a name="para617"></a><span class="paranum">617</span>. <span class="bld">dīgho sarassa</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">sarantassa dhātussa dīgho hoti kyamhīti ikāru’kārānaṃ kyamhi vikappena dīgho.</p>\r\n<p class="bodytext">samuccīyati samuccīyanti, vinicchīyati, vinicchīyanti.</p>\r\n<p class="bodytext">ji-jaye, jeti, jenti, vijeti, vijenti, parājeti, parājenti, jayati, jayanti, vijayati, vijayanti, parājayati, parājayanti.</p>\r\n<p class="bodytext">kamme kyamhi dīgho, na taṃ jitaṃ sādhu jitaṃ, yaṃ jitaṃ avajīyati. taṃ kho jitaṃ sādhu jitaṃ, yaṃ jitaṃ nāvajīyati <span class="note">[jā. 1.1.70]</span>.</p>\r\n<p class="bodytext">ṇāpimhi pubbassaralopo vā, jāpeti, jāpayati. yo na hanti na ghāteti, na jināti na jāpaye <span class="note">[jā. 1.10.144]</span>. jayāpeti, jayāpayati, jayāpīyati, jayāpīyanti, jayatu, jayantu.</p>\r\n<p class="bodytext">īādimhi-ajesi, ajesuṃ, vijesi, vijesuṃ, ajayi, ajayuṃ, ajayiṃsu, vijayi, vijayuṃ, vijayiṃsu, jessati, vijessati, parājessati, jayissati, vijayissati, parājayissati.</p>\r\n<p class="bodytext">ḍī-vehāsagatiyaṃ, sakuṇo ḍeti, ḍenti <span class="note">[dī. ni. 1.215; a. ni. 4.198]</span>. pāsaṃ oḍḍeti, oḍḍenti.</p>\r\n','\r',char(13)),'\n',char(10)),'-617-');
INSERT INTO pages VALUES(54801,'annya_sadda_10',394,replace(replace('\r\n<p class="bodytext">nī-naye<a name="M0.0394"></a>, neti, nenti, vineti, vinenti, nayati, nayanti, vinayati, vinayanti.</p>\r\n<p class="bodytext">kamme-nīyati, nīyanti.</p>\r\n<p class="bodytext">dvitte-niyyati, niyyanti, niyyare.</p>\r\n<p class="bodytext">ṇāpimhi āyādesassa rasso, nayāpeti, nayāpenti, nayāpayati, nayāpayanti.</p>\r\n<p class="bodytext">kamme-nayāpīyati, nayāpīyanti.</p>\r\n<p class="bodytext">īādimhi-nesi, nesuṃ, vinesi, vinesuṃ, ānesi, ānesuṃ, anayi, nayi, anayiṃsu, nayiṃsu, ānayi, ānayiṃsu, vinayi, vinayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-nessati, nessanti, nayissati, nayissanti.</p>\r\n<p class="bodytext">bhī-bhaye, bheti. mā bhetha kiṃ socatha modathavho <span class="note">[jā. 1.12.27]</span>, vibhemi etaṃ asādhuṃ, uggatejo hi brāhmaṇo <span class="note">[jā. 2.17.103]</span>. bhāyati, bhāyanti.</p>\r\n<p class="bodytext">kārite mahāvuttinā sāgamo vā, bhīseti, bhīsayati, bhīsāpeti, bhīsāpayati. bhikkhuṃ bhīseyya vā bhīsāpeyya vā <span class="note">[pāci. 346-347]</span>.</p>\r\n<p class="bodytext">sī-saye, seti, senti, atiseti, atisenti, sayati, sayanti.</p>\r\n<p class="bodytext">kamme-atisīyati, atisīyanti.</p>\r\n<p class="bodytext">ṇāpimhi rasso, sayāpeti, sayāpayati.</p>\r\n<p class="bodytext">ṇimhi-sāyeti, sāyayati, sāyesuṃ dīnamānasā <span class="note">[apa. thera 2.54.48]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54802,'annya_sadda_10',395,replace(replace('\r\n<p class="bodytext">mhi-hāse<a name="M0.0395"></a>, umheti, umhayati, vimheti, vimhayati, na naṃ umhayate disvā, na ca naṃ paṭinandati <span class="note">[jā. 1.2.93]</span>.</p>\r\n<p class="bodytext">kamme-umhīyati, vimhīyati.</p>\r\n<p class="bodytext">kārite pubbassaralopo, sace maṃ nāganāsūrū, umhāyeyya pabhāvatī. sace maṃ nāganāsūrū, pamhāyeyya pabhāvatī <span class="note">[jā. 2.20.17]</span>.</p>\r\n<p class="centered">iti bhūvādigaṇe ivaṇṇantadhārūpaṃ.</p>\r\n<p class="subhead">uvaṇṇantadhāturūpa</p>\r\n<p class="bodytext">cu-cavane, ju-sīghagamane, thu-abhitthavane, du-gatiyaṃ upatāpe ca, bhū-sattāyaṃ, yu-missane gatiyañca, ru-sadde, brū-viyattiyaṃ vācāyaṃ, su-sandane janane ca, sū-pasavane, hu-dāne bhakkhane pūjāyaṃ sattiyañca, hū-sattāyaṃ.</p>\r\n<p class="bodytext">tyādyuppatti, ‘kattari lo’ti lapaccayo, uvaṇṇassa avādeso, ‘dīgho sarassā’ti sakammikadhātūnaṃ kyamhi dīgho.</p>\r\n<p class="bodytext">cu-cavane, cavati, cavanti.</p>\r\n<p class="bodytext">ṇimhi-cāveti, cāvayati.</p>\r\n<p class="bodytext">ju-sīghagamane, javati, javanti.</p>\r\n<p class="bodytext">thu-abhitthavane, abhitthavati, abhitthavanti.</p>\r\n<p class="bodytext">‘bahula’nti adhikatattā kyamhi kvaci vuddhi, avādeso, abhitthavīyati, abhitthavīyanti, abhitthaviyyati, abhitthaviyyanti.</p>\r\n<p class="bodytext">du-upatāpe, upaddavati, upaddavanti.</p>\r\n<p class="bodytext">bhū-sattāyaṃ, sambhoti, sambhavati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54803,'annya_sadda_10',396,replace(replace('\r\n<p class="bodytext">yu-gatiyaṃ<a name="M0.0396"></a>, yavati.</p>\r\n<p class="bodytext">ru-sadde, ravati, ravanti, viravati, viravanti.</p>\r\n<p class="bodytext">brū-viyattiyaṃ vācāyaṃ –</p>\r\n<p class="bodytext"><a name="para618"></a><span class="paranum">618</span>. <span class="bld">na brūsso</span>.</p>\r\n<p class="bodytext">byañjane pare brūssa o na hoti.</p>\r\n<p class="bodytext">brūti.</p>\r\n<p class="bodytext"><a name="para619"></a><span class="paranum">619</span>. <span class="bld">brūto tissīu</span> <span class="note">[ka. 520; rū. 502; nī. 1033; caṃ. 6.2.34; pā. 7.3.93]</span>.</p>\r\n<p class="bodytext">brūto tissa ādimhi īu hoti. īmhi pubbalopo.</p>\r\n<p class="bodytext">bravīti.</p>\r\n<p class="bodytext"><a name="para620"></a><span class="paranum">620</span>. <span class="bld">yuvaṇṇānamiyaṅauvaṅa sare</span> <span class="note">[ka. 70; rū. 30; nī. 220; .miyavuvaṅa. (bahūsu)]</span>.</p>\r\n<p class="bodytext">ivaṇṇu’vaṇṇantānaṃ dhātūnaṃ kvaci iyaṅa, uvaṅa honti sare.</p>\r\n<p class="bodytext">bruvanti, brunti vā. "ajānantā no pabruntī"ti pāḷi. brūsi, brūtha, brūmi, brūma.</p>\r\n<p class="bodytext"><a name="para621"></a><span class="paranum">621</span>. <span class="bld">tyantīnaṃ ṭaṭū</span> <span class="note">[ka. 517; rū. 488; nī. 1105; caṃ. 1.4.13; pā. 3.4.84]</span>.</p>\r\n<p class="bodytext">ti, antīsu brūssa āha hoti, tesañca ṭa, ṭū honti.</p>\r\n<p class="bodytext">so āha, te āhu.</p>\r\n<p class="bodytext">atrimā pāḷī-nibbānaṃ bhagavā āha, sabbaganthapamocanaṃ, āha tesañca yo nirodho <span class="note">[mahāva. 60]</span>, yaṃ pare sukhato āhu, tadariyā āhu dukkhato. yaṃ pare dukkhato āhu, tadariyā sukhato vidū <span class="note">[su. ni. 767]</span>. tattha ‘āhā’ti katheti. ‘āhū’ti kathenti.</p>\r\n','\r',char(13)),'\n',char(10)),'-618-619-620-621-');
INSERT INTO pages VALUES(54804,'annya_sadda_10',397,replace(replace('\r\n<p class="bodytext">brūtu<a name="M0.0397"></a>, brūvantu, brūhi maṅgalamuttamaṃ <span class="note">[khu. pā. 5.2]</span>, brūtha, brūmi, brūma.</p>\r\n<p class="bodytext">eyyādimhi-brūssa uvaṅa hoti, bruveyya, bruveyyuṃ.</p>\r\n<p class="bodytext">īādimhi sare pare brūssa ottaṃ, ossa ca avādeso, abravi, abravuṃ, abraviṃsu.</p>\r\n<p class="bodytext">uvādese-abruvi, abruvuṃ, abruviṃsu.</p>\r\n<p class="bodytext">parokkhamhi –</p>\r\n<p class="bodytext"><a name="para622"></a><span class="paranum">622</span>. <span class="bld">aādīsvāho brūssa</span> <span class="note">[ka. 475; rū. 465; nī. 956]</span>.</p>\r\n<p class="bodytext">aādīsu brūssa āha bhavati.</p>\r\n<p class="bodytext">so āha, te āhu.</p>\r\n<p class="bodytext"><a name="para623"></a><span class="paranum">623</span>. <span class="bld">ussaṃsvāhā vā</span> <span class="note">[nī. 1036]</span>.</p>\r\n<p class="bodytext">āhādesamhā parassa uvacanassa aṃsu hoti vā.</p>\r\n<p class="bodytext">te āhaṃsu, saccaṃ kirevamāhaṃsu, narā ekacciyā idha <span class="note">[jā. 1.13.123]</span>.</p>\r\n<p class="bodytext">su-sandane, nadī savati, savanti, ābhavaggā savanti.</p>\r\n<p class="bodytext">sū-pasavane, puññaṃ pasavati <span class="note">[cūḷava. 354]</span>, pasavanti.</p>\r\n<p class="bodytext">hu-pūjāyaṃ, ‘parokkhāyañcā’ti dvittaṃ, ‘kavaggahānaṃ cavaggajā’ti hassa jo, juhoti, juhonti.</p>\r\n<p class="bodytext">kamme-tena aggi hūyate.</p>\r\n<p class="bodytext">ṇimhi-juhāveti, juhāvayati.</p>\r\n<p class="bodytext">ṇāpimhi-juhāpeti, juhāpayati.</p>\r\n<p class="bodytext">hu-sattiyaṃ, pahoti, sampahoti, pahonti, sampahonti.</p>\r\n','\r',char(13)),'\n',char(10)),'-622-623-');
INSERT INTO pages VALUES(54805,'annya_sadda_10',398,replace(replace('\r\n<p class="bodytext">hū-sattāyaṃ<a name="M0.0398"></a>, hoti, honti, hotu, hontu.</p>\r\n<p class="bodytext">eyyādimhi- ‘yuvaṇṇānamiyaṅauvaṅa sare’ti dhātvantassa uvādeso, huveyya, huveyyuṃ.</p>\r\n<p class="bodytext">āādimhi-so ahuvā. vaṇṇagandhaphalūpeto, amboyaṃ ahuvā pure <span class="note">[jā. 1.2.71]</span>. ahuvā te pure sakhā <span class="note">[saṃ. ni. 1.50]</span>, te ahuvū, tvaṃ ahuvo, tumhe ahuvattha <span class="note">[ma. ni. 1.215]</span>, jhāyatha bhikkhave mā pamādāttha, mā pacchā vippaṭisārino ahuvattha, ahuva, ahuvaṃ vā, ahuvamhā. "akaramhasa te kiccaṃ, yaṃ balaṃ ahuvamhase <span class="note">[jā. 1.4.29]</span>. ahuvamheva mayaṃ pubbe, na nāhuvamhā"ti <span class="note">[ma. ni. 1.180]</span> pāḷiyo.</p>\r\n<p class="bodytext">īādimhi sāgamo, ahosi, pāturahosi.</p>\r\n<p class="bodytext">mahāvuttinā īlopo rasso ca. ahudeva bhayaṃ ahu chambhitattaṃ. ahudeva kukkuccaṃ, ahu vippaṭisāro <span class="note">[pārā. 38]</span>. ādīnavo pāturahu <span class="note">[theragā. 269]</span>, dibbo ratho pāturahu.</p>\r\n<p class="bodytext"><a name="para624"></a><span class="paranum">624</span>. <span class="bld">hūto resuṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">hūto ñuṃvacanassa resuṃ hoti. suttavibhattena mhāssa resumhā ca. ‘rānubandhentasarādissā’ti dhātvantalopo.</p>\r\n<p class="bodytext">te pubbe ahesuṃ. uvādese ahuvuṃ, pubbassaralope ahuṃ.</p>\r\n<p class="bodytext">atrimā pāḷī- sabbamhi taṃ araññamhi, yāvantettha dijā ahuṃ <span class="note">[jā. 2.22.2425]</span>. kūṭāgārasahassāni, sabbasoṇṇamayā ahuṃ <span class="note">[apa. thera 1.1.407]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-624-');
INSERT INTO pages VALUES(54806,'annya_sadda_10',399,replace(replace('\r\n<p class="bodytext">ossa <a name="M0.0399"></a> si, ittha, ttho. tvaṃ ahosi, ahuvi, ahuvittha, ahuvittho.</p>\r\n<p class="bodytext">mahāvuttinā olopo rasso, mā bhoti kupitā ahu <span class="note">[jā. 2.22.1931]</span>, māhu pacchānutāpinī <span class="note">[saṃ. ni. 1.162]</span>, ahosiṃ nu kho ahaṃ atītamaddhānaṃ, na nu kho ahosiṃ, kinnu kho ahosiṃ, kathaṃ nu kho ahosiṃ <span class="note">[saṃ. ni. 2.20; ma. ni. 1.18]</span>, ahaṃ ahuviṃ, mayaṃ ahosimhā, ahosimha vā. ahesumhā nu kho mayaṃ atītamaddhānaṃ, na nu kho ahesumhā, kinnu kho ahesumhā, kathaṃ nu kho ahesumhā <span class="note">[ma. ni. 1.407]</span>. mayaṃ pubbe ahuvimhā, ahumhā vā. "mayaṃ pubbe dānapatino ahumhā"ti <span class="note">[jā. 2.22.1617]</span> pāḷi.</p>\r\n<p class="bodytext">rassatte-ahumha.</p>\r\n<p class="bodytext">mahāvuttinā mhāssa uñca. "pañcasatā mayaṃ sabbā, tāvatiṃsupagā ahu"nti pāḷi.</p>\r\n<p class="bodytext">parachakke assa aṃ, ahaṃ pubbe ahuvaṃ, ahuva vā.</p>\r\n<p class="bodytext">‘paro kvacī’ti paralopo, ahuṃ.</p>\r\n<p class="bodytext">atrimā pāḷī- "ahaṃ kevaṭṭagāmasmiṃ, ahuṃ kevaṭṭadārako <span class="note">[apa. thera 1.39.86]</span>, cakkavattī ahuṃ rājā, jambumaṇḍassa issaro. sattakkhattuṃ mahābrahmā, vasavattī tadā ahuṃ. muddhābhisitto khattiyo, manussādhipatī ahu"nti. mayaṃ ahuvimhe.</p>\r\n<p class="bodytext">ssatyādimhi –</p>\r\n<p class="bodytext"><a name="para625"></a><span class="paranum">625</span>. <span class="bld">hūssa hehehihohi ssaccādo</span> <span class="note">[ka. 480; rū. 490; nī. 961; ‘.ssatyādo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ssatyādimhi hūdhātussa he ca hohi ca hohi ca honti.</p>\r\n','\r',char(13)),'\n',char(10)),'-625-');
INSERT INTO pages VALUES(54807,'annya_sadda_10',400,replace(replace('\r\n<p class="bodytext">hessati<a name="M0.0400"></a>, hessanti, hehissati, hehissanti, hohissati, hohissanti. buddhā hessaṃ sadevake <span class="note">[bu. vaṃ. 2.55]</span>, anāgatamhi addhāne, hessāma sammukhā imaṃ <span class="note">[bu. vaṃ. 2.74]</span>.</p>\r\n<p class="bodytext"><a name="para626"></a><span class="paranum">626</span>. <span class="bld">dakkha sakkha hehi hohīhi lopo</span> <span class="note">[ka. 480; rū. 490; nī. 961; ‘dakkhā hehihohilopo’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">etehi ādesehi ssassa lopo hoti vā. suttavibhattena aññehipi ssalopo.</p>\r\n<p class="bodytext">sakkhisi tvaṃ kuṇḍalini, maññisi khattabandhuni <span class="note">[jā. 2.17.14]</span>. na hi sakkhinti chettuṃ <span class="note">[su. ni. 28]</span>, adhammo haññiti dhammamajja <span class="note">[jā. 1.11.31]</span>, brahmadatto palāyiti iccādi.</p>\r\n<p class="bodytext">hehiti, hehinti, hohiti, hohinti.</p>\r\n<p class="bodytext">atrimā pāḷī-piyo ca me hehiti mālabhārī, ahañca naṃ mālinī ajjhupessaṃ <span class="note">[jā. 1.15.197]</span>, tilodano hehiti sādhupakko <span class="note">[jā. 1.8.2]</span>. doso pemañca hehiti <span class="note">[theragā. 719]</span>. mama tvaṃ hehisi bhariyā <span class="note">[jā. 1.14.27]</span>. tato sukhī hohisi vītarāgo. khippaṃ hohisi anāsavo <span class="note">[dī. ni. 2.207]</span> iccādi.</p>\r\n<p class="centered">iti bhūvādigaṇe uvaṇṇantadhāturūpaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-626-');
INSERT INTO pages VALUES(54808,'annya_sadda_10',401,replace(replace('\r\n<p class="subhead">edantadhāturūpa</p>\r\n\r\n<p class="bodytext">e-āgatiyaṃ gatiyañca, ke-sadde, khe-khādanu’paṭṭhānesu, ge-sadde, apapubba ce-pūjāyaṃ, jhe-cintāyaṃ dāha’jjhānesu ca, te-pālane, the-sadda, saṅghātesu, de-suddhi, niddāsu, pe-vuddhiyaṃ<a name="M0.0401"></a>, bhe-bhaye, le-chedane, ve-gatiyaṃ tantasantāne ca, se-antakriyāyaṃ, hare-lajjāyaṃ, gile-kilamane, pale-gatiyaṃ, mile-hāniyaṃ.</p>\r\n<p class="bodytext">ṇānubandhapaccayena vinā yesaṃ dhātūnaṃ āyādeso labbhati, te edantā nāma. mahāvuttinā yalope sati ādantehi samānarūpaṃ, ādantānañca yāgame sati edantehi samānarūpaṃ, tasmā ādanta, edantā yebhuyyena samānarūpā bhavanti.</p>\r\n<p class="bodytext">mahāvuttinā edantānaṃ tyādīsu tabbādīsu ca āyādeso, kvaci yalopo, e-āgatiyaṃ. ayaṃ so sārathī eti <span class="note">[jā. 2.22.51]</span>, sace enti manussattaṃ <span class="note">[saṃ. ni. 1.49]</span>, lakkhaṇaṃ passa āyantaṃ, migasaṅghapurakkhataṃ <span class="note">[jā. 1.1.11]</span>, yoga’māyanti maccuno <span class="note">[saṃ. ni. 1.20]</span>, āyāmāvuso <span class="note">[pārā. 228]</span>, āyāmānanda <span class="note">[dī. ni. 2.186; pārā. 22; ma. ni. 1.273]</span>.</p>\r\n<p class="bodytext">ettha edhātu āgaccha, gacchāmāti atthadvayaṃ vadati, yādhātuvasena āgaccha, yāmātipi atthaṃ vadanti.</p>\r\n<p class="bodytext">e-vuddhiyaṃ vā, "kāyo, apāyo, upāyo, samudāyo"tiādīsu –</p>\r\n<p class="bodytext">kucchitā dhammā āyanti vaḍḍhanti etthāti kāyo, ‘āyo’ti vuccati vaḍḍhi, tato apeto apāyo, tena upeto upāyo, avayavadhammā samudenti etthāti samudāyo, paribyattaṃ āyanti etenāti pariyāyo.</p>\r\n<p class="bodytext">ke-sadde, kāyati.</p>\r\n<p class="bodytext">kamme-kiyyati.</p>\r\n<p class="bodytext">khe-khādane, tiṇaṃ khāyati, vikkhāyati, undurā khāyanti, vikkhāyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54809,'annya_sadda_10',402,replace(replace('\r\n<p class="bodytext">khe-upaṭṭhāne<a name="M0.0402"></a>, khāyati, pakkhāyati, alakkhī viya khāyati.</p>\r\n<p class="bodytext">ge-sadde, gāyati, gāyanti.</p>\r\n<p class="bodytext">apapubba ce-pūjāyaṃ, apacāyati, ye vuddha’mapacāyanti <span class="note">[jā. 1.1.37]</span>.</p>\r\n<p class="bodytext">jhe-cintāyaṃ, jhāyati, jhāyanti, pajjhāyati, pajjhāyanti, abhijjhāyati, abhijjhāyanti.</p>\r\n<p class="bodytext">jhe-olokane, nijjhāyati, nijjhāyanti, upanijjhāyati, upanijjhāyanti, ujjhāyati, ujjhāyanti.</p>\r\n<p class="bodytext">jhe-dayhane, padīpo jhāyati, parijjhāyati.</p>\r\n<p class="bodytext">kamme-jhāyīyati.</p>\r\n<p class="bodytext">ṇāpimhi yalopo, kaṭṭhaṃ jhāpeti, jhāpenti, jhāpayati, jhāpayanti.</p>\r\n<p class="bodytext">jhe-ajjhayane saṃpubbo, mahāvuttinā niggahītassa jādeso, sajjhāyati, sajjhāyanti, mantaṃ sajjhāyati.</p>\r\n<p class="bodytext">te-pālane, tāyati, tāyanti.</p>\r\n<p class="bodytext">the-sadda, saṅghātesu, thāyati.</p>\r\n<p class="bodytext">de-suddhiyaṃ, vodāyati, vodāyanti.</p>\r\n<p class="bodytext">de-soppane, niddāyati, niddāyanti.</p>\r\n<p class="bodytext">pe-vuddhiyaṃ, appāyati, appāyanti.</p>\r\n<p class="bodytext">bhe-bhaye, bhāyati, bhāyanti, sabbe bhāyanti maccuno <span class="note">[dha. pa. 129]</span>, bhāyasi, sace bhāyatha dukkhassa <span class="note">[udā. 44]</span>, kinnu kho ahaṃ tassa sukhassa bhāyāmi <span class="note">[ma. ni. 1.381]</span>, nataṃ bhāyāmi āvuso, bhāyāma <span class="note">[apa. therī. 2.2.458]</span>.</p>\r\n<p class="bodytext">ṇāpimhi-bhayāpeti, bhāyāpeti, bhāyāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54810,'annya_sadda_10',403,replace(replace('\r\n<p class="bodytext">īādimhi-mā <a name="M0.0403"></a> bhāyi, mā soci <span class="note">[dī. ni. 2.207]</span>, mā cintayi.</p>\r\n<p class="bodytext">ssatyādimhi-bhāyissati, bhāyissanti.</p>\r\n<p class="bodytext">le-chedane, tiṇaṃ lāyati, sāliṃ lāyati, lāyanti.</p>\r\n<p class="bodytext">ve-gatiyaṃ, vāto vāyati, vātā vāyanti.</p>\r\n<p class="bodytext">ve-tantasantāne, tantaṃ vāyati, vāyanti.</p>\r\n<p class="bodytext">kamme-vāyīyati, pubbalope vīyati, viyyati.</p>\r\n<p class="bodytext">ṇāpimhi-cīvaraṃ vāyāpeti <span class="note">[pārā. 638]</span>, vāyāpenti.</p>\r\n<p class="bodytext">se-antakriyāyaṃ, osāyati, pariyosāyati, ajjhosāyati.</p>\r\n<p class="bodytext">kamme-pariyosīyati.</p>\r\n<p class="bodytext">ṇāpimhi-pariyosāpeti.</p>\r\n<p class="bodytext"><a name="para627"></a><span class="paranum">627</span>. <span class="bld">ṇiṇāpīnaṃ tesu</span>.</p>\r\n<p class="bodytext">ṇi, ṇāpīnaṃ lopo hoti tesuṇi, ṇāpīsu paresu.</p>\r\n<p class="bodytext">bhikkhu attanā vippakataṃ kuṭiṃ parehi pariyosāpeti <span class="note">[pārā. 363]</span>. ettha akammakattā dhātussa ‘kuṭi’nti ca ‘parehī’ti ca dve kammāni dvikkhattuṃ pavattehi kāritehi sijjhanti.</p>\r\n<p class="bodytext">anekassaraedantampi kiñci idha vuccati.</p>\r\n<p class="bodytext">hare-lajjāyaṃ, harāyati, harāyanti, aṭṭiyāmi harāyāmi <span class="note">[saṃ. ni. 1.165]</span>, harāyāma, harāyatīti hirī, mahāvuttinā upantassa ittaṃ.</p>\r\n<p class="bodytext">gile-rujjane, gilāyati, piṭṭhi me āgilāyati <span class="note">[cūḷava. 345; dī. ni. 3.300]</span>, gilāyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-627-');
INSERT INTO pages VALUES(54811,'annya_sadda_10',404,replace(replace('\r\n<p class="bodytext">pale-gatiyaṃ<a name="M0.0404"></a>, palāyati, palāyanti.</p>\r\n<p class="bodytext">mile-hāniyaṃ, milāyati, milāyanti.</p>\r\n<p class="bodytext">"jhānaṃ, ujjhānaṃ, nijjhānaṃ, tāṇaṃ, parittāṇaṃ, vodānaṃ, niddānaṃ, gilāno, palāto, milātanti"ādīsu yalopo.</p>\r\n<p class="centered">iti bhūvādigaṇe edantadhāturūpaṃ.</p>\r\n<p class="centered">bhūvādigaṇe sarantadhātūnaṃ tyādyantarūpāni niṭṭhitāni.</p>\r\n<p class="subhead">byañjanantadhātu</p>\r\n<p class="subhead">avuddhikarūpa</p>\r\n<p class="subhead">tudādigaṇa</p>\r\n<p class="bodytext">atha byañjanantadhāturūpāni vuccante. tāni ca avuddhika, savuddhikavasena duvidhāni honti. tattha avuddhikāni tāva vuccante.</p>\r\n<p class="bodytext"><span class="bld">khipa, guha, tuda, disa, pisa, phusa, likha, vadhādi</span>.</p>\r\n<p class="bodytext">idha dhātūnaṃ antassaro uccāraṇattho, so rūpavidhāne nappayujjate.</p>\r\n<p class="bodytext"><a name="para628"></a><span class="paranum">628</span>. <span class="bld">tudādīhi ko</span> <span class="note">[ka. 445; rū. 433; nī. 925; caṃ. 1.1.92; pā. 3.1.77]</span>.</p>\r\n<p class="bodytext">tudādīhi kānubandho apaccayo hoti. kānubandho avuddhidīpanattho.</p>\r\n<p class="bodytext">khipa-khipane, khipati, pakkhipati, ukkhipati, okkhipati, nikkhipati, vikkhipati, paṭikkhipati, saṃkhipati.</p>\r\n<p class="bodytext">kamme-khipīyati.</p>\r\n','\r',char(13)),'\n',char(10)),'-628-');
INSERT INTO pages VALUES(54812,'annya_sadda_10',405,replace(replace('\r\n<p class="bodytext">kārite-khipeti<a name="M0.0405"></a>, khipayati, khipāpeti, khipāpayati, khepeti, khepayati vā, udakaṃ khepeti, taṇhaṃ khepeti, khayāpetīti attho.</p>\r\n<p class="bodytext">guha-saṃvaraṇe, guhati, nigguhati.</p>\r\n<p class="bodytext">kamme-guhiyati.</p>\r\n<p class="bodytext">kārite gussa dīgho, gūheti, gūhayati, gūhāpeti, gūhāpayati.</p>\r\n<p class="bodytext">kamme-gūhāpīyati.</p>\r\n<p class="bodytext">ghaṭa-cetāyaṃ, ghaṭati.</p>\r\n<p class="bodytext">kamme-ghaṭīyati.</p>\r\n<p class="bodytext">kārite-ghaṭeti, ghaṭayati, ghaṭāpeti, ghaṭāpayati.</p>\r\n<p class="bodytext">tuda-byadhane, byadhanaṃ vijjhanaṃ, tudati, vitudati.</p>\r\n<p class="bodytext">assa ette-tudeti, tudenti.</p>\r\n<p class="bodytext">kamme-tudīyati.</p>\r\n<p class="bodytext">‘tavaggavaraṇānaṃ ye cavaggabayañā’ti dassa jattaṃ. ‘vaggalasehi te’ti yassa pubbarūpattaṃ, tujjati.</p>\r\n<p class="bodytext">kārite-tudeti, tudayati, tudāpeti, tudāpayati.</p>\r\n<p class="bodytext">disī-uddisane, uddisanaṃ sarūpato kathanaṃ. uddisati, pātimokkhaṃ uddisanti <span class="note">[mahāva. 152]</span>, niddisati, niddisanti, apadisati, apadisanti.</p>\r\n<p class="bodytext">kamme-uddisīyati, uddisīyanti.</p>\r\n<p class="bodytext">kārite-ācariyo sissaṃ pāḷidhammaṃ uddisāpeti, uddisāpayati, vācetīti attho. pāṭhesu pana sadvayampi dissati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54813,'annya_sadda_10',406,replace(replace('\r\n<p class="bodytext">nuda-khipane<a name="M0.0406"></a>, nudati, panudati, vinodeti, paṭivinodeti vā.</p>\r\n<p class="bodytext">kamme-panudīyati, panujjati.</p>\r\n<p class="bodytext">kārite-panudeti, panudayati, panudāpeti, panudāpayati.</p>\r\n<p class="bodytext">pisa-saṃcuṇṇe, pisati.</p>\r\n<p class="bodytext">kamme-pisīyati.</p>\r\n<p class="bodytext">kārite-piseti, pisayati, pisāpeti, pisāpayati.</p>\r\n<p class="bodytext">phusa-samphasse pattiyañca, phusati, phusanti dhīrā nibbānaṃ <span class="note">[dha. pa. 23]</span>, vajjaṃ naṃ phuseyya <span class="note">[pārā. 409; cūḷava. 344]</span>.</p>\r\n<p class="bodytext">kamme-phusīyati.</p>\r\n<p class="bodytext">kārite-phuseti, phusayati, phusāpeti, phusāpayati.</p>\r\n<p class="bodytext">likha-lekhane, likhati, sallikhati, vilikhati.</p>\r\n<p class="bodytext">kārite-likheti, sallikheti, vilikheti.</p>\r\n<p class="bodytext">vadha-hiṃsāyaṃ, vadhati, vadheti.</p>\r\n<p class="bodytext">kamme-vadhīyati.</p>\r\n<p class="bodytext">yamhi dhassa jhattaṃ, yassa pubbarūpaṃ. ‘catutthadutiyesvesaṃ tatiyapaṭhamā’ti jhassa tatiyajattaṃ, vajjhati, vajjhanti, vajjhare. ime sattā haññantu vā vajjhantu vā <span class="note">[ma. ni. 1.60]</span>.</p>\r\n<p class="bodytext">kārite-vadheti, vadhayati, vadhāpeti, vadhāpayati, sāmiko purisaṃ maggaṃ gameti, gamayati, gamāpeti, gamāpayati iccādīnipi idha vattabbāni.</p>\r\n<p class="centered">iti tudādigaṇo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54814,'annya_sadda_10',407,replace(replace('\r\n<p class="subhead">savuddhikarūpa</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0407"></a> savuddhikarūpāni vuccante.</p>\r\n<p class="bodytext">asa-bhuvi, āsa-nivāse upavesane ca, isu-icchā, kantīsu, kamu-padagamane, kusa-akkose, gamu-gatimhi, jaravayohānimhi, jana-janane, mara-pāṇacāge, yamu-uparame, ruda-assuvimocane kandane ca, ruha-janane, labha-lābhe, vaca, vada-viyattiyaṃ vācāyaṃ, vida-ñāṇe, vasa-nivāse, visapavesane, sada-gatyā’vasāne, hana-hiṃsāyaṃ, haraharaṇe.</p>\r\n<p class="bodytext">tyādyuppatti, kattari lo, asa-bhuvi, sattāyanti attho.</p>\r\n<p class="bodytext"><a name="para629"></a><span class="paranum">629</span>. <span class="bld">tassa tho</span> <span class="note">[ka. 494; rū. 495, 500; nī. 989, 991]</span>.</p>\r\n<p class="bodytext">atthito parassati, tūnaṃ tassa tho hoti.</p>\r\n<p class="bodytext">‘pararūpamayakāre byañjane’ti suttena byañjane pare dhātvantassa pararūpattaṃ, ‘catutthadutiyesvesaṃ tatiyapaṭhamā’ti suttena pararūpassa dutiyassa paṭhamattaṃ.</p>\r\n<p class="bodytext">dhanaṃ me atthi.</p>\r\n<p class="bodytext">ettha ca "atthi tvaṃ etarahi, na tvaṃ natthi, atthi ahaṃ etarahi, nāhaṃ natthi, puttā matthi dhanā matthi <span class="note">[dha. pa. 62]</span>, atthi imasmiṃ kāye kesā"ti <span class="note">[khu. pā. 3.dvitiṃsākāra]</span> ādīsu atthisaddo ākhyātapaṭirūpako kattuvācako nipāto.</p>\r\n<p class="bodytext">"atthīti kho kaccāyana ayameko anto, natthīti dutiyo anto"ti <span class="note">[saṃ. ni. 2.15]</span> ca "atthipaccayo, natthipaccayo"ti <span class="note">[paṭṭhā. 1.1.paccayuddesa]</span> ca evamādīsu nāmapaṭirūpako. tathā natthisaddo.</p>\r\n','\r',char(13)),'\n',char(10)),'-629-');
INSERT INTO pages VALUES(54815,'annya_sadda_10',408,replace(replace('\r\n<p class="bodytext">tumhi-vipassissa <a name="M0.0408"></a> ca namatthu <span class="note">[dī. ni. 3.287]</span>, namo te buddha vīratthu <span class="note">[saṃ. ni. 1.90]</span>, ettha ca "dhiratthumaṃ pūtikāya"nti ādīsu atthusaddo nipāto.</p>\r\n<p class="bodytext"><a name="para630"></a><span class="paranum">630</span>. <span class="bld">ntamānantantiyiyuṃsvādilopo</span> <span class="note">[ka. 494-5; rū. 496; nī. 1019; ‘ntamānānti.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">nta, māna, anti, antu, iyā, iyuṃsu atthissa ādilopo hoti.</p>\r\n<p class="bodytext">santi, santu.</p>\r\n<p class="bodytext"><a name="para631"></a><span class="paranum">631</span>. <span class="bld">sihīsvaṭa</span> <span class="note">[ka. 506; rū. 497; nī. 992]</span>.</p>\r\n<p class="bodytext">atthissa aṭa hoti si, hīsu.</p>\r\n<p class="bodytext">manussosi <span class="note">[mahāva. 126]</span>, purisosi <span class="note">[mahāva. 126]</span>. himhi dīgho, tvaṃ paṇḍito āhi, bhavāhīti attho. tavibhattīsu dhātvantassa pararūpaṃ, tumhe attha, kaccittha parisuddhā <span class="note">[pārā. 233]</span>. tvādimhi-mā pamādattha <span class="note">[ma. ni. 1.88, 215]</span>, tumhe samaggā attha.</p>\r\n<p class="bodytext"><a name="para632"></a><span class="paranum">632</span>. <span class="bld">mimānaṃ vā mhimhā ca</span> <span class="note">[ka. 492; rū. 499; nī. 987; ‘sīhisvaṭa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">atthito paresaṃ mi, mānaṃ mhi, mhā honti vā, atthissa ca aṭa hoti.</p>\r\n<p class="bodytext">ahaṃ pasannomhi, mayaṃ pasannāmha.</p>\r\n<p class="bodytext">tvādimhi-ahaṃ iminā puññena anāgate paññavā amhi, mayaṃ paññavanto amha.</p>\r\n<p class="bodytext"><a name="para633"></a><span class="paranum">633</span>. <span class="bld">esu sa</span> <span class="note">[ka. 492; rū. 499; nī. 987]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-630-631-632-633-');
INSERT INTO pages VALUES(54816,'annya_sadda_10',409,replace(replace('\r\n<p class="bodytext">etesu <a name="M0.0409"></a> mi, mesu atthissa sassa so hoti. pararūpanisedhanatthamidaṃ suttaṃ.</p>\r\n<p class="bodytext">ahaṃ paṇḍito asmi, mayaṃ paṇḍitā asma.</p>\r\n<p class="bodytext">tvādimhi-ahaṃ anāgate paññavā asmi, mayaṃ paññavanto asma.</p>\r\n<p class="bodytext">eyyādimhi –</p>\r\n<p class="bodytext"><a name="para634"></a><span class="paranum">634</span>. <span class="bld">atthiteyyādichannaṃ sa su sasi satha saṃsāma</span> <span class="note">[ka. 571, 517; rū. 624, 488; nī. 830, 1105; ‘atthiteyyādicchannaṃ sasusasatha saṃ sāma’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">‘atthī’ti dhātuniddeso ti-kāro, atthito paresaṃ eyyādīnaṃ channaṃ sādayo honti.</p>\r\n<p class="bodytext">evamassa vacanīyo <span class="note">[pārā. 411]</span>, evamassu vacanīyā <span class="note">[pārā 418]</span>, tvaṃ assasi, tumhe assatha, ahaṃ assaṃ, mayaṃ assāma.</p>\r\n<p class="bodytext">assunipātopi bahuṃ dissati, tayassu dhammā jahitā bhavanti <span class="note">[khu. pā. 6.10]</span>, kenassu taratī oghaṃ, kenassu tarati aṇṇavaṃ <span class="note">[saṃ. ni. 1.246]</span>. "kiṃsu chetvā sukhaṃ seti, kiṃsu chetvā na socatī"ti <span class="note">[saṃ. ni. 1.71]</span> ettha niggahītamhā saṃyogādilopo.</p>\r\n<p class="bodytext"><a name="para635"></a><span class="paranum">635</span>. <span class="bld">ādidvinnamiyāmiyuṃ</span> <span class="note">[ka. 517; rū. 488; nī. 993; ‘.miyā iyuṃ’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">atthito eyyādīsu ādimhi dvinnaṃ iyā, iyuṃ honti. ‘ntamānantantiyiyuṃsū.’tiādilopo.</p>\r\n','\r',char(13)),'\n',char(10)),'-634-635-');
INSERT INTO pages VALUES(54817,'annya_sadda_10',410,replace(replace('\r\n<p class="bodytext">so <a name="M0.0410"></a> siyā, te siyuṃ, ete dve nipātāpi honti. "vedanākkhandho siyā kusalo, siyā akusalo, siyā abyākato"ti <span class="note">[vibha. 152]</span> ādīsu ekaccoti attho.</p>\r\n<p class="bodytext">"siyā kusalaṃ dhammaṃ paṭicca kusalo dhammo uppajjeyya hetupaccayā"ti <span class="note">[paṭṭhā. 1.1.35-38]</span> ādīsu kinnūti attho.</p>\r\n<p class="bodytext">"dvādasākusalā siyu"nti <span class="note">[abhidhammatthasaṅgaha 2]</span> ādīsu bhavantīti attho.</p>\r\n<p class="bodytext">mahāvuttinā eyyuṃ, eyyamiccetesaṃ iyaṃsu, iyaṃ honti, ādilopo, siyaṃsu dve bhikkhū abhidhamme nānāvādā <span class="note">[ma. ni. 3.35]</span>, evaṃrūpo siyaṃ ahaṃ anāgatamaddhānaṃ, evaṃvedano siyaṃ, evaṃsañño siyaṃ <span class="note">[ma. ni. 3.274]</span>.</p>\r\n<p class="bodytext">ajjattanimhi –</p>\r\n<p class="bodytext"><a name="para636"></a><span class="paranum">636</span>. <span class="bld">īādo dīgho</span> <span class="note">[ka. 517; rū. 488; nī. 1001]</span>.</p>\r\n<p class="bodytext">atthissa dīgho hoti īādīsu.</p>\r\n<p class="bodytext">so āsi, te āsuṃ, āsiṃsu, tvaṃ āsi, tumhe āsittha, ahaṃ āsiṃ. tatrāpāsiṃ evaṃvaṇṇo <span class="note">[dī. ni. 1.245; ma. ni. 1.68]</span>. mayaṃ āsimha, āsimhā.</p>\r\n<p class="bodytext"><a name="para637"></a><span class="paranum">637</span>. <span class="bld">aāssādīsu</span> <span class="note">[ka. 507; rū. 501; nī. 1020; caṃ. 5.4.79; pā. 2.4.52]</span>.</p>\r\n<p class="bodytext">aādiparokkhāyañca āādihiyyattaniyañca ssādīsu bhavissanti, kālātipattīsu ca atthissa bhū hoti. idañca suttaṃ atthissa etāsaṃ vibhattīnampi sādhāraṇakaraṇatthaṃ. rūpampi bhūrūpameva.</p>\r\n','\r',char(13)),'\n',char(10)),'-636-637-');
INSERT INTO pages VALUES(54818,'annya_sadda_10',411,replace(replace('\r\n<p class="bodytext">so <a name="M0.0411"></a> babhūva, te babhūvu, so abhavā, te abhavū, so bhavissati, te bhavissanti, so abhavissā, te abhavissaṃsu iccādi.</p>\r\n<p class="bodytext"><a name="para638"></a><span class="paranum">638</span>. <span class="bld">atyādintesvatthissa bhū</span> <span class="note">[ka. 517; rū. 500; nī. 1020; caṃ. 5.4.79; pā. 2.4.52]</span>.</p>\r\n<p class="bodytext">tyādivajjitesu ntavajjitesu ca paccayesu atthissa bhū hoti. etena tyādi, tvādi, eyyādi, īādisaṅkhātāsu catūsu vibhattīsu ntapaccaye ca atthissa bhūādeso natthi, sesāsu catūsu vibhattīsu ca ntavajjitesu tabbādīsu ca atthissa bhūādeso labbhatīti veditabbo.</p>\r\n<p class="bodytext">āsa-upavesane. guruṃ upāsati, payirupāsati, upāsanti, payirupāsanti.</p>\r\n<p class="bodytext">kamme-upāsīyati, payirupāsīyati.</p>\r\n<p class="bodytext">kārite-mātā puttaṃ guruṃ upāseti, payirupāseti, upāsayati, payirupāsayati.</p>\r\n<p class="bodytext">īādimhi-upāsi, payirupāsi, upāsiṃsu, payirupāsiṃsu, upāsuṃ, payirupāsuṃ.</p>\r\n<p class="bodytext">āsa-nivāse.</p>\r\n<p class="bodytext"><a name="para639"></a><span class="paranum">639</span>. <span class="bld">gamayamisāsadisānaṃ vā cchaṅa</span> <span class="note">[ka. 476, 522; rū. 442, 476; nī. 957, 1035]</span>.</p>\r\n<p class="bodytext">gamu, yamu, isu, āsa, disānaṃ anto byañjano ṅānubandho ccho hoti vā nta, māna, tyādīsu.</p>\r\n<p class="bodytext">gacchanto, niyacchanto, icchanto, acchanto, dicchantoti iminā tyādīsu āsassa ccho, acchati, acchanti, so acchi, te acchiṃsu.</p>\r\n','\r',char(13)),'\n',char(10)),'-638-639-');
INSERT INTO pages VALUES(54819,'annya_sadda_10',412,replace(replace('\r\n<p class="bodytext">isu-esanāyaṃ<a name="M0.0412"></a>.</p>\r\n<p class="bodytext"><a name="para640"></a><span class="paranum">640</span>. <span class="bld">lahussupantassa</span> <span class="note">[ka. 485; rū. 434; nī. 975; caṃ. 6.1.105-106 .pe.. 7.3.77-78]</span>.</p>\r\n<p class="bodytext">antassa samīpe pavattatīti upanto, byañjanantadhātūnaṃ pubbassaro ‘upanto’ti vuccati, lahubhūtassa upantabhūtassa ca ivaṇṇu’vaṇṇassa e, ovuddhī honti.</p>\r\n<p class="bodytext">ira-kampane, erati, modati.</p>\r\n<p class="bodytext">lahussāti kiṃ? jīvati, dhūpati, ikkhati, sukkhati.</p>\r\n<p class="bodytext">upantassāti kiṃ? siñcati, bhuñjati, niggahītāgamena byavahitattā upanto na hoti.</p>\r\n<p class="bodytext">ivaṇṇuvaṇṇassātvevaṃ? pacati, vadati.</p>\r\n<p class="bodytext">iminā issa evuddhi hoti, esati, anvesati, pariyesati.</p>\r\n<p class="bodytext">adhipubbo isu-āyācane, ajjhesati.</p>\r\n<p class="bodytext">kamme-esīyati, pariyesīyati, anvesīyati, ajjhesīyati.</p>\r\n<p class="bodytext">kārite-eseti, esayati, esāpeti, esāpayati.</p>\r\n<p class="bodytext">isu-icchāyaṃ, cchādeso, icchati, icchanti, sampaṭicchati, sampaṭicchanti.</p>\r\n<p class="bodytext">kamme-icchīyati.</p>\r\n<p class="bodytext">kārite-icchāpeti, icchāpayati, sampaṭicchāpeti, sampaṭicchāpayati.</p>\r\n<p class="bodytext">kamme-icchāpīyati, icchāpayīyati, so icchi, te icchiṃsu, icchissati, icchissanti.</p>\r\n<p class="bodytext">kamu-vijjhane, gambhīresu ṭhānesu ñāṇaṃ kamati, na satthaṃ kamati, na visaṃ kamati, na aggi kamati <span class="note">[a. ni. 8.1]</span>, na vijjhatīti attho.</p>\r\n','\r',char(13)),'\n',char(10)),'-640-');
INSERT INTO pages VALUES(54820,'annya_sadda_10',413,replace(replace('\r\n<p class="bodytext">kamu-padagamane<a name="M0.0413"></a>, pakkamati, apakkamati, upakkamati, vikkamati, abhikkamati, paṭikkamati, atikkamati, saṅkamati, okkamati.</p>\r\n<p class="bodytext"><a name="para641"></a><span class="paranum">641</span>. <span class="bld">nito kamassa</span> <span class="note">[ka. 20; rū. 27; nī. 50]</span>.</p>\r\n<p class="bodytext">nimhā parassa kamassa kassa kho hoti. ‘ādissā’ti saṅketattā kassāti ñāyati.</p>\r\n<p class="bodytext">nikkhamati, nikkhamanti.</p>\r\n<p class="bodytext">kārite –</p>\r\n<p class="bodytext"><a name="para642"></a><span class="paranum">642</span>. <span class="bld">assā ṇānubandhe</span> <span class="note">[ka. 483; rū. 527; nī. 973]</span>.</p>\r\n<p class="bodytext">byañjanantassa dhātussa ādimhi a-kārassa āvuddhi hoti ṇānubandhe paccaye. ‘bahula’nti adhikatattā ṇāpimhi āvuddhi natthi.</p>\r\n<p class="bodytext">pakkāmeti, pakkāmayati, nikkhāmeti, nikkhāmayati, nikkhamāpeti, nikkhamāpayati.</p>\r\n<p class="bodytext">īādimhi mahāvuttinā ādidīgho vā hoti, so pakkami, pakkāmi, idaṃ vatvāna pakkāmi, maddī sabbaṅgasobhaṇā <span class="note">[jā. 2.22.1857]</span>, pakkamuṃ, pakkāmuṃ, sammodamānā pakkāmuṃ, aññamaññaṃ piyaṃ vadā <span class="note">[jā. 2.22.1868]</span>, pakkamiṃsu, pakkāmiṃsu, pakkamaṃsu, pakkāmaṃsu, pakkamissati, pakkamissanti, pakkamissare.</p>\r\n<p class="bodytext">āpubbo kusa-akkose, lahupantattā vuddhi, akkosati, akkosanti. papubbo āmantane, pakkosati, pakkosanti. vipubbo uccasadde, vikkosati, vikkosanti. paṭipubbo nīvāraṇe, paṭikkosati, paṭikkosanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-641-642-');
INSERT INTO pages VALUES(54821,'annya_sadda_10',414,replace(replace('\r\n<p class="bodytext">īādimhi <a name="M0.0414"></a> –</p>\r\n<p class="bodytext"><a name="para643"></a><span class="paranum">643</span>. <span class="bld">kusaruhīssacchi</span> <span class="note">[ka. 498; rū. 480; nī. 1114; ‘kusaruhehīssa chi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">kusato ruhato ca parassa īssa cchi hoti.</p>\r\n<p class="bodytext">akkocchi maṃ avadhi maṃ <span class="note">[dha. pa. 3-4]</span>, idañca rūpaṃ kudha, kupadhātūhipi sādhenti, evaṃ sati "akkocchi me"ti pāṭho siyā. akkosi, akkosiṃsu.</p>\r\n<p class="bodytext">gamu gatimhi, kattari lo, ‘ūlasse’ti lassa ettaṃ, gameti, gamenti. avapubbo ñāṇe, avagameti, avagamenti, adhipubbo ñāṇe lābhe ca, adhigameti, adhigamenti. vipubbo vigame, vigameti, vigamenti.</p>\r\n<p class="bodytext">kamme-gamīyati, gamiyati, gamiyyati.</p>\r\n<p class="bodytext">pubbarūpatte-gammati, gammanti, adhigammati, adhigammanti, adhigammare, adhigammate, adhigammante, adhigammare.</p>\r\n<p class="bodytext">kārite vuddhi natthi, gameti, gamayati, gamāpeti, gamāpayati.</p>\r\n<p class="bodytext">kamme-gamayīyati, gamayīyanti, gamāpīyati, gamāpīyanti.</p>\r\n<p class="bodytext">hiyyattanimhi-so agamā, gamā, te agamū, gamū, tvaṃ agamo, agama, agami, tumhe agamuttha, ahaṃ agamiṃ, gamiṃ, mayaṃ agamamhā, gamamhā, agamumhā, gamumhā, so agamattha, te agamatthuṃ, tvaṃ agamase, tumhe agamavhaṃ, ahaṃ agamaṃ, mayaṃ agamamhase.</p>\r\n<p class="bodytext">ajjhattanimhi īādīsu ikārāgamo, so agamī, gamī.</p>\r\n<p class="bodytext">rassatte-agami, gami.</p>\r\n','\r',char(13)),'\n',char(10)),'-643-');
INSERT INTO pages VALUES(54822,'annya_sadda_10',415,replace(replace('\r\n<p class="bodytext">mahāvuttinā <a name="M0.0415"></a> ākārena saha sāgamo, so agamāsi, gāmaṃ agamāsi, nagaraṃ agamāsi.</p>\r\n<p class="bodytext">‘eyyāthasse’iccādinā īssa ttho, so gāmaṃ agamittho, gamittho, te agamuṃ, gamuṃ.</p>\r\n<p class="bodytext">‘uṃssiṃsvaṃsū’ti iṃsu, aṃsu, te agamiṃsu, gamiṃsu, agamaṃsu, gamaṃsu, tvaṃ agamo, gamo.</p>\r\n<p class="bodytext">‘ossa a i ttha ttho’ ‘sī’ti suttāni, tvaṃ agama, gama, agami, gami, agamittha, gamittha, agamittho, gamittho, agamāsi, gamāsi, tumhe agamittha.</p>\r\n<p class="bodytext">‘mhātthānamuu’iti uttaṃ, tumhe agamuttha, gamuttha, ahaṃ agamiṃ, gamiṃ, agamāsiṃ, gamāsiṃ, mayaṃ agamimhā, gamimhā.</p>\r\n<p class="bodytext">rassatte-agamimha, gamimha, agamumhā, gamumhā, agamumha, gamumha, agamāsimhā, gamāsimhā, agamāsimha, gamāsimha.</p>\r\n<p class="bodytext">parachakke-so agamā, gamā.</p>\r\n<p class="bodytext">rassatte-agama, gama.</p>\r\n<p class="bodytext">‘eyyāthasse’iccādinā tthatte-so agamittha, gamittha, te agamū, gamū.</p>\r\n<p class="bodytext">rassatte-agamu, gamu, tvaṃ agamise, gamise, tumhe agamivhaṃ, gamivhaṃ, ahaṃ agama, gama, agamaṃ, gamaṃ vā, agamimhe, gamimhe.</p>\r\n<p class="bodytext">kamme-agamīyi, agammi, agamīyittho, agammittho, agamīyuṃ, gamīyuṃ, agamīyiṃsu, gamīyiṃsu, agammuṃ, gammuṃ, agammiṃsu, gammiṃsu.</p>\r\n<p class="bodytext">parachakke-agamīyittha, gamīyittha, agammittha, gammittha.</p>\r\n<p class="bodytext">kārite-agamāpayi, gamāpayi, agamāpesi, gamāpesi, agamāpayuṃ, gamāpayuṃ, agamāpayiṃsu, gamāpayiṃsu.</p>\r\n<p class="bodytext"><a name="para644"></a><span class="paranum">644</span>. <span class="bld">gamissa</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-644-');
INSERT INTO pages VALUES(54823,'annya_sadda_10',416,replace(replace('\r\n<p class="bodytext">āādimhi <a name="M0.0416"></a> īādimhi ca gamissa massa ā hoti. saralopo.</p>\r\n<p class="bodytext">so agā, te agū, tvaṃ ago, tumhe aguttha, ahaṃ agaṃ, mayaṃ agumhā.</p>\r\n<p class="bodytext">īādimhi īsaralopo, agā devāna santike <span class="note">[jā. 1.14.205]</span>, vāyaso anupariyagā <span class="note">[su. ni. 449]</span>.</p>\r\n<p class="bodytext">āpubbo āgamane, anavhito tato āgā <span class="note">[jā. 1.5.21]</span>, sopā’gā samitiṃ vanaṃ <span class="note">[dī. ni. 2.335]</span>.</p>\r\n<p class="bodytext">adhipubbo paṭilābhe, ajjhagā amataṃ santiṃ <span class="note">[vi. va. 846]</span>, taṇhānaṃ khayamajjhagā <span class="note">[dha. pa. 154]</span>.</p>\r\n<p class="bodytext">atipubbo upādhipubbo ca titikkame, nakkhattaṃ paṭimānentaṃ, attho bālaṃ upajjhagā <span class="note">[jā. 1.1.49]</span>. khaṇo ve mā upajjhagā.</p>\r\n<p class="bodytext">etāni o, avacanānaṃ lope sati tumha’mhayogepi labhanti, aguṃ, agiṃsu, agaṃsu. samantā vijjutā āguṃ <span class="note">[jā. 2.22.2264]</span>, tepā’guṃsamitiṃ vanaṃ <span class="note">[(gavesitabbaṃ)]</span>, visesaṃ ajjhagaṃsute <span class="note">[dī. ni. 2.354]</span>, asesaṃ parinibbanti, asesaṃ dukkhamajjhaguṃ <span class="note">[itivu. 93]</span>, sabbaṃ dukkhaṃ upajjhaguṃ <span class="note">[paṭi. ma. 1.236; ma. ni. 3.271]</span>. ajjhago, ajjhaga, ajjhagi, ajjhaguttha, ajjhagiṃ, ajjhagimhā, ajjhagumhā, agā, āgā, anvagā, ajjhagā, upajjhagā, agū, āgū. āgū devā yasassino <span class="note">[dī. ni. 2.340]</span>, cittassa ekadhammassa, sabbeva vasamanvagū <span class="note">[saṃ. ni. 1.61]</span>. cetā haniṃsu vedabbaṃ, sabbe te byasanamajjhagū <span class="note">[jā. 1.1.48]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54824,'annya_sadda_10',417,replace(replace('\r\n<p class="bodytext">parokkhāyaṃ <a name="M0.0417"></a> ādissa dvittaṃ, ‘kavaggahānaṃ cavaggajā’ti pubbassa cavaggo. so jagama, mahāvuttinā upantassa dīgho, kvaci assa ittaṃ, "rājā dudīpo jagāmi magga"nti <span class="note">[jā. 2.22.911; ‘rājā dudīpopi jagāma saggaṃ’]</span> pāḷi. so jagāma, te jagāmu, tvaṃ jagame.</p>\r\n<p class="bodytext">byañjanādimhi ikārāgamo, tumhe jagamittha, ahaṃ jagamaṃ, mayaṃ jagamimha, so jagamittha, te jagamire, tvaṃ jagamittho, tumhe jagamivho, ahaṃ jagamiṃ, mayaṃ jagamimhe.</p>\r\n<p class="bodytext">ssatyādimhi-gamissati, gamissanti, gamissare.</p>\r\n<p class="bodytext">parachakke-so gamissate, te gamissante, gamissare, tvaṃ gamissase, tumhe gamissavhe, ahaṃ gamissaṃ, mayaṃ gamissāmhe.</p>\r\n<p class="bodytext">ssādimhi-so agamissā, gamissā.</p>\r\n<p class="bodytext">‘āīūmhāssāssāmhānaṃ vā’ti ssā, ssāmhānaṃ rasso, agamissa, gamissa, te agamissaṃsu, gamissaṃsu, tvaṃ agamisse, gamisse.</p>\r\n<p class="bodytext">‘eyyāthasse’ iccādinā ssessa attaṃ, tvaṃ agamissa, gamissa, tumhe agamissatha, gamissatha, ahaṃ agamissaṃ, gamissaṃ, mayaṃ agamissāmhā, gamissāmhā, agamissāmha, gamissāmha.</p>\r\n<p class="bodytext">‘gama vada dānaṃ ghammavajjadajjā’ti sabbavibhattīsu gamissa ghammo, ghammati, ghammanti.</p>\r\n<p class="bodytext">kamme-ghammīyati, ghammīyanti.</p>\r\n<p class="bodytext">mahāvuttinā gagghādeso vā, tvaṃ yena yeneva gagghasi, phāsuṃyeva gagghasi <span class="note">[a. ni. 8.63]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54825,'annya_sadda_10',418,replace(replace('\r\n<p class="bodytext">‘gamayamisāsadisānaṃ <a name="M0.0418"></a> vācchaṅa’ iti suttena sabbavibhattīsu gamissa massa ccho, gacchati, gacchanti, gacchare.</p>\r\n<p class="bodytext">kamme-gacchīyati, gacchīyanti, gacchiyyati, gacchiyyanti, gacchiyyare.</p>\r\n<p class="bodytext">kārite-gacchāpeti, gacchāpayati. gacchatu, gacchantu, gaccheyya, gaccheyyuṃ.</p>\r\n<p class="bodytext">‘eyyuṃssuṃ’iti uṃttaṃ, gacchuṃ.</p>\r\n<p class="bodytext">‘eyyeyyāseyyaṃnaṃ ṭe’iti suttena eyya, eyyāsi, eyyaṃvibhattīnaṃ ettaṃ, so gacche, tvaṃ gacche, ahaṃ gacche.</p>\r\n<p class="bodytext">‘eyyāthasse’ iccādinā eyyāthassa ottaṃ, tumhe gaccheyyātho.</p>\r\n<p class="bodytext">āādimhi-agacchā, gacchā, agaccha, gaccha vā.</p>\r\n<p class="bodytext">īādimhi-agacchi, gacchi, agacchuṃ, gacchuṃ, agacchiṃsu, gacchiṃsu.</p>\r\n<p class="bodytext"><a name="para645"></a><span class="paranum">645</span>. <span class="bld">ḍaṃsassa ca ñchaṅa</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ‘chaṅa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">āādīsu īādīsu ca ḍaṃsassa ca anto byañjano ñchaṅa hoti.</p>\r\n<p class="bodytext">so agañchā, gañchā. tathā agañchū, gacchū.</p>\r\n<p class="bodytext">īādimhi-so agañchi, gañchi.</p>\r\n<p class="bodytext">tasmiṃ paṭipaviṭṭhamhi, añño āgañchi brāhmaṇo <span class="note">[su. ni. 985]</span>. khippameva upāgañchi, yattha sammati temiyo <span class="note">[jā. 2.22.73]</span>. te agañchuṃ, gañchuṃ.</p>\r\n<p class="bodytext">ssatyādimhi-gacchissati, gacchissanti, gacchissare.</p>\r\n<p class="bodytext"><a name="para646"></a><span class="paranum">646</span>. <span class="bld">labha vasa chida gama bhida rudānaṃ cchaṅa</span> <span class="note">[ka. 481; rū. 524; nī. 966, 968]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-645-646-');
INSERT INTO pages VALUES(54826,'annya_sadda_10',419,replace(replace('\r\n<p class="bodytext">ssena <a name="M0.0419"></a> saha etesaṃ cchaṅa hoti vā ssayuttāsu vibhattīsu, suttavibhattena susassa ca, "nadīva avasucchatī"ti <span class="note">[jā. 2.22.2140]</span> pāḷi.</p>\r\n<p class="bodytext">lacchati, labhissati, alacchā, alabhissā, vacchati, vasissati, avacchā, avasissā, checchati, chindissati, acchecchā, acchindissā, bhecchati, bhindissati, abhecchā, abhindissā, rucchati, rodissati, arucchā, arodissāti.</p>\r\n<p class="bodytext">iminā ssayuttāsu dvīsu vibhattīsu ssena saha gamissa massa ccho, gacchati, gacchissati, gacchanti, gacchissanti, ahaṃ gacchaṃ, gacchissaṃ.</p>\r\n<p class="bodytext">atrimā pāḷī-gacchaṃ pāraṃ samuddassa, kassaṃ purisakāriyaṃ <span class="note">[jā. 2.22.131]</span>, tassāhaṃ santikaṃ gacchaṃ, so me satthā bhavissati, sabbāni abhisambhossaṃ, gacchaññeva rathesabha <span class="note">[jā. 2.22.1832]</span>, vedhabyaṃ kaṭukaṃ loke, gacchaññeva rathesabha <span class="note">[jā. 2.22.1835]</span>.</p>\r\n<p class="bodytext">ssādimhi-agacchā, agacchissā, agacchaṃsu, agacchissaṃsu.</p>\r\n<p class="bodytext">jara-vayohānimhi –</p>\r\n<p class="bodytext"><a name="para647"></a><span class="paranum">647</span>. <span class="bld">jarasadānamīma vā</span> <span class="note">[ka. 505, 609; rū. 482, 484; nī. 1018, 1213]</span>.</p>\r\n<p class="bodytext">jara, sadānaṃ saramhā īmaāgamo hoti vāti īmaāgamo.</p>\r\n<p class="bodytext">jīrati, jīranti.</p>\r\n<p class="bodytext">kārite-jīrāpeti, jīrāpayati.</p>\r\n<p class="bodytext"><a name="para648"></a><span class="paranum">648</span>. <span class="bld">jaramarānamiyaṅa</span> <span class="note">[ka. 505; rū. 482; nī. 1018; ‘.mīyaṅa’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-647-648-');
INSERT INTO pages VALUES(54827,'annya_sadda_10',420,replace(replace('\r\n<p class="bodytext">etesaṃ <a name="M0.0420"></a> iyaṅa hoti vā nta, māna, tyādīsu.</p>\r\n<p class="bodytext">jiyati, jiyanti.</p>\r\n<p class="bodytext">dīghatte-jīyati, jīyanti.</p>\r\n<p class="bodytext">dvitte-jiyyati, jiyyanti.</p>\r\n<p class="bodytext">kārite-jiyāpeti, jiyāpayati.</p>\r\n<p class="bodytext">janī-pātubhāve, mahāvuttinā sabbavibhattīsu nassa yādeso ādidīgho ca, jāyati, upajāyati, vijāyati, jāyanti, jāyare, pajāyanti, pajāyare, upajāyanti, upajāyare.</p>\r\n<p class="bodytext">kārite vuddhi natthi, janeti, janenti, janayati, janayanti.</p>\r\n<p class="bodytext">kamme-janīyati, janīyanti, jāyatu, jāyantu, jāyeyya, jāyeyyuṃ.</p>\r\n<p class="bodytext">kārite-janeyya, janeyyuṃ, janayeyya, janayeyyuṃ.</p>\r\n<p class="bodytext">īādimhi-ajāyi, ajāyiṃsu, vijāyi, vijāyiṃsu, ajani, jani vā.</p>\r\n<p class="bodytext">kārite-ajanesi, janesi, ajanayi, janayi, ajanesuṃ, janesuṃ, ajanayuṃ, janayuṃ, ajanayiṃsu, janayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-jāyissati, vijāyissati.</p>\r\n<p class="bodytext">ssādimhi-ajāyissā, jāyissā.</p>\r\n<p class="bodytext">ḍaṃsa-ḍaṃsane, ḍaṃsati, ḍaṃsanti.</p>\r\n<p class="bodytext">kārite-ḍaṃseti, ḍaṃsayati, ḍaṃsāpeti, ḍaṃsāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54828,'annya_sadda_10',421,replace(replace('\r\n<p class="bodytext">ā<a name="M0.0421"></a>, īādīsu ‘ḍaṃsassa ca ñchaṅa’ iti suttaṃ, niggahītalopo, aḍañchā, ḍañchā, aḍañchi, ḍañchi.</p>\r\n<p class="bodytext">daha-dāhe, dahati, dahanti.</p>\r\n<p class="bodytext">kamme yamhi ‘hassa vipallāso’ti pubbāparavipallāso, agginā gāmo dayhati, dayhanti.</p>\r\n<p class="bodytext">kārite-dāheti, dāhayati, dahāpeti, dahāpayati.</p>\r\n<p class="bodytext"><a name="para649"></a><span class="paranum">649</span>. <span class="bld">dahassa dassa ḍo</span> <span class="note">[ka. 20; rū. 27; nī. 50; ‘.dasadadakkhā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">dahadhātussa dassa ḍo hoti vā.</p>\r\n<p class="bodytext">ḍahati, ḍahanti.</p>\r\n<p class="bodytext">disa-pekkhane, tyādyuppatti, kattari lo.</p>\r\n<p class="bodytext"><a name="para650"></a><span class="paranum">650</span>. <span class="bld">disassa passadassadasadadakkhā</span> <span class="note">[ka. 471; rū. 483; nī. 951]</span>.</p>\r\n<p class="bodytext">disadhātussa passa ca dassa ca dasa ca da ca dakkha cāti ete ādesā honti vā.</p>\r\n<p class="bodytext">vipassanā, vipassī bhagavā, sudassī, piyadassī, atthadassī, dhammadassī, sudassaṃ vajjamaññesaṃ <span class="note">[dha. pa. 252]</span>, sudassananagaraṃ, mahāsudassano nāma rājā <span class="note">[dī. ni. 2.242]</span>.</p>\r\n<p class="bodytext">dasādese-catusaccaddaso nātho <span class="note">[vibha. aṭṭha. suttantabhājanīyavaṇṇanā]</span>, duddaso dhammo <span class="note">[mahāva. 7; dī. ni. 2.64]</span>, attano pana duddasaṃ <span class="note">[dha. pa. 252]</span>, so ve bhikkhu dhammadasoti vuccati <span class="note">[gavesitabbaṃ]</span>. passa dhammaṃ durājānaṃ, sammuḷhettha aviddasū. daṭṭhabbaṃ, daṭṭhā, daṭṭhunti.</p>\r\n','\r',char(13)),'\n',char(10)),'-649-650-');
INSERT INTO pages VALUES(54829,'annya_sadda_10',422,replace(replace('\r\n<p class="bodytext">iminā suttena disassa sabbavibhattīsu yathārahaṃ passa, dassa, dakkhādesā honti, ā, īādīsu dasa, dādesā honti, "dissati, dissantī"ti rūpāni pana yassa pubbarūpattena idha <a name="M0.0422"></a> kamme sijjhanti, divādigaṇe kattari sijjhanti. mahāvuttinā addassa, dissādesāpi honti. atrimā pāḷī-yaṃ vāsavaṃ addassāmaṃ <span class="note">[jā. 1.6.112; ‘addasāma’]</span>, ye mayaṃ bhagavantaṃ addassāma <span class="note">[gavesitabbaṃ]</span>, api me mātaraṃ adassatha <span class="note">[ma. ni. 2.356]</span>, dissanti bālā abyattā <span class="note">[mahāva. 82]</span>, mayi ime dhammā sandissanti, ahañca imesu dhammesu sandissāmi <span class="note">[ma. ni. 3.253]</span>, nimittāni padissanti, tāni ajja padissareti <span class="note">[bu. vaṃ. 2.82]</span>, tasmā tyādīsupi "adassati, adassanti, adassasi, adassatha, adassāmi, addassāmā"ti yujjanti.</p>\r\n<p class="bodytext">kārite ṇimhi dassādeso, dasseti, dassayati, nidasseti, nidassayati, sandasseti, sandassayati.</p>\r\n<p class="bodytext">kamme-dassīyati, dassīyanti, nidassīyati, nidassīyanti, sandassīyati, sandassīyanti, dakkhati, dakkhanti.</p>\r\n<p class="bodytext">kamme-dakkhīyati, dakkhīyanti.</p>\r\n<p class="bodytext">‘gamayamisāsadisānaṃ cchaṅa’ iti cchādese- dicchati, dicchanti iccādi.</p>\r\n<p class="bodytext">passati, passanti, dakkhati, dakkhanti, passīyati, passīyanti, dakkhīyati, dakkhīyanti.</p>\r\n<p class="bodytext">yassa pubbarūpatte-dissati, dissanti, dissate, dissante, uddissate, uddissante, niddissate, niddissante, apadissate, apadissante.</p>\r\n<p class="bodytext">kārite-passāpeti, passāpayati, dakkhāpeti, dakkhāpayati.</p>\r\n<p class="bodytext">kamme-passāpīyati, dassīyati, nidassīyati, sandassīyati, dakkhāpīyati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54830,'annya_sadda_10',423,replace(replace('\r\n<p class="bodytext">passatu<a name="M0.0423"></a>, passantu, dakkhatu, dakkhantu, passeyya, passeyyuṃ, dakkheyya, dakkheyyuṃ.</p>\r\n<p class="bodytext">eyyāmassa emu ca antassa u ca honti. "kattha passemu khattiyaṃ <span class="note">[jā. 2.2.1947]</span>, dakkhemu te nivesana"nti <span class="note">[jā. 1.15.254 (.nivesanāni)]</span> pāḷi. passeyyāma, passeyyāmu, dakkheyyāma, dakkheyyāmu.</p>\r\n<p class="bodytext">āādimhi-apassā, adakkhā.</p>\r\n<p class="bodytext">dasa, dādesesu dakārassa dvittaṃ, addasā kho bhagavā <span class="note">[mahāva. 9; dī. ni. 2.69; saṃ. ni. 1.159]</span>, addasā kho āyasmā ānando <span class="note">[ma. ni. 1.364]</span>.</p>\r\n<p class="bodytext">rassatte-tamaddasa mahābrahmā, nisinnaṃ samhi vesmani <span class="note">[jā. 1.16.148]</span>. te addasū. rassatte-addasu, āmantayassu vo putte, mā te mātaramaddasu <span class="note">[jātake "āmantayassu te putte, mā te mātara maddasuṃ"]</span>.</p>\r\n<p class="bodytext">dādese-so addā.</p>\r\n<p class="bodytext">rassatte-adda. yāyamāno mahārājā, addā sīdantare nage <span class="note">[jā. 2.22.2105]</span>. yo dukkhaṃ sukhato adda, dukkhamaddakkhi sallato <span class="note">[dī. ni. 2.368]</span>.</p>\r\n<p class="bodytext">īādimhi-apassi, passi, apassī, passī, apassiṃsu, passiṃsu, apassi, passi, apassittha, passittha, apassiṃ, passiṃ, apassimhā, passimhā.</p>\r\n<p class="bodytext">dassādese-addassi, addassuṃ, addassiṃsu, addassaṃsu, addassi, addassittha, addassiṃ, addassimhā.</p>\r\n<p class="bodytext">dakkhādese-adakkhi, dakkhi iccādi.</p>\r\n<p class="bodytext">dasādese gāthāsu-addasi, addasuṃ, addasiṃsu, addasaṃsu.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54831,'annya_sadda_10',424,replace(replace('\r\n<p class="bodytext">parachakke-addasā<a name="M0.0424"></a>, addasū, ahaṃ addasaṃ, mayaṃ addassimhe. addasaṃ kāma te mūlaṃ, saṅkappā kāma jāyasi <span class="note">[jā. 1.8.39]</span>.</p>\r\n<p class="bodytext">kvaci sāgame ākārāgamo, so addasāsi, te addasāsuṃ, ahaṃ addasāsiṃ, mayaṃ adasāsimha. yaṃ addasāsiṃ sambuddhaṃ, desentaṃ dhammamuttamaṃ <span class="note">[theragā. 287]</span>. athaddasāsiṃ sambuddhaṃ, satthāramakutobhayaṃ <span class="note">[theragā. 912]</span>.</p>\r\n<p class="bodytext">mahāvuttinā iṃssa imhi hoti, pathe addasāsimhi bhojaputte <span class="note">[jā. 2.17.146]</span>.</p>\r\n<p class="bodytext">ssatyādimhi-passissati, passissanti.</p>\r\n<p class="bodytext">dakkhādese‘dakkha sakkha hehi’iccādinā ssassalopo, dakkhati, dakkhanti, dakkhiti, dakkhinti, dakkhissati, dakkhissanti.</p>\r\n<p class="bodytext">ssādimhi-apassissā, adakkhissā iccādi.</p>\r\n<p class="bodytext">mara-pāṇacāge, marati, maranti.</p>\r\n<p class="bodytext">‘jaramarānamiyaṅa’iti iyādeso, miyati, miyanti, amhaṃ daharā na miyyare <span class="note">[jā. 1.10.97]</span>.</p>\r\n<p class="bodytext">kārite ‘assā ṇānubandhe’ti āvuddhi, māreti, mārenti, mārayati, mārayanti, mārāpeti, mārāpenti, mārāpayati, mārāpayanti.</p>\r\n<p class="bodytext">yamu-uparame, yamati, yamanti. pare ca na vijānanti, mayamettha yamāmase. ettha ca ‘yamāmase’ti viramāmase, maraṇaṃ gacchāmaseti attho.</p>\r\n<p class="bodytext">saṃpubbo saṃyame, saṃyamati, saṃyamanti.</p>\r\n<p class="bodytext">niggahītassa ñādese-saññamati, saññamanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54832,'annya_sadda_10',425,replace(replace('\r\n<p class="bodytext">nipubbo <a name="M0.0425"></a> niyame, niyamati, niyamanti.</p>\r\n<p class="bodytext">‘gamayamisāsadisānaṃ vā cchaṅa’iti massa cchādeso, niyacchati, niyacchanti.</p>\r\n<p class="bodytext">kamme-niyamīyati, niyamīyanti.</p>\r\n<p class="bodytext">pubbarūpe-niyammati, niyammanti.</p>\r\n<p class="bodytext">kārite-niyāmeti, niyāmayati.</p>\r\n<p class="bodytext">ṇāpimhi na vuddhi, niyamāpeti, niyamāpayati, vavatthapetīti attho.</p>\r\n<p class="bodytext">ruda-assuvimocane, rodati, rodanti.</p>\r\n<p class="bodytext">ssatyādimhi-‘labhavasachidagamabhidānaṃ cchaṅa’iti ssena saha dassa cchādeso, sā nūna kapaṇā ammā, cirarattāya rucchati <span class="note">[jā. 2.22.2136]</span>. koñjī samuddatīreva, kapaṇā nūna rucchati <span class="note">[jā. 2.21.113]</span>. sā nūna kapaṇā ammā, ciraṃ rucchati assame <span class="note">[jā. 2.22.2138]</span>, kaṃ nva’jja chātā tasitā, uparucchanti dārakā <span class="note">[jā. 2.22.2153]</span>. rodissati, rodissanti, rucchati, rucchanti.</p>\r\n<p class="bodytext">ssādīsu-arucchā, arucchaṃsu, arodissā, arodissaṃsu.</p>\r\n<p class="bodytext">ruha-pāpuṇane, ruhati, ruhanti, āruhati, āruhanti, ārohati, ārohanti, abhiruhati, abhiruhanti, oruhati, oruhanti, orohati, orohanti.</p>\r\n<p class="bodytext">kamme-ārohīyati.</p>\r\n<p class="bodytext">‘hassa vipallāso’ti ha, yānaṃ vipariyāyo, āruyhati, oruyhati.</p>\r\n<p class="bodytext">īādimhi-ruhi, āruhi, oruhi.</p>\r\n<p class="bodytext">‘kusaruhissa cchī’ti suttaṃ, abhirucchi, abhiruhi vā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54833,'annya_sadda_10',426,replace(replace('\r\n<p class="bodytext">labha-lābhe<a name="M0.0426"></a>, labhati, labhanti.</p>\r\n<p class="bodytext">yassa pubbarūpatte ‘catutthadutiyesvesaṃ tatiyapaṭhamā’ti saṃyogādissa catutthassa tatiyattaṃ, labbhati, labbhanti, labbhare.</p>\r\n<p class="bodytext">īādimhi-alabhi, alabhiṃsu.</p>\r\n<p class="bodytext"><a name="para651"></a><span class="paranum">651</span>. <span class="bld">labhā iṃīnaṃ thaṃthā vā</span> <span class="note">[ka. 497; rū. 477; nī. 1013]</span>.</p>\r\n<p class="bodytext">labhamhā paresaṃ iṃ, īnaṃ kamena thaṃ, thā honti vā, dhātvantassa pararūpattaṃ, saṃyogādissa dutiyassa paṭhamattaṃ.</p>\r\n<p class="bodytext">ahaṃ alatthaṃ, alabhiṃ vā, so alattha, alabhi vā.</p>\r\n<p class="bodytext">mahāvuttinā uṃssa thuṃ, thaṃsu honti, mhāssa ca thamhā, thuṃmhā honti, te bhagavato santike pabbajjañca upasampadañca alatthuṃ <span class="note">[dī. ni. 2.77]</span>, satiṃ paccalatthuṃ, viparītasaññaṃ paccalatthuṃ, te satiṃ paccalatthaṃsu, agamamhā kho tava gehaṃ, tattha neva dānaṃ alatthamhā <span class="note">[ma. ni. 2.300 (thokaṃ visadisaṃ)]</span>, akkosameva alatthamhā, mayañca alatthamhā savanāya, alatthumhā vā. dhātvantassa ccho ca. tadāhaṃ pāpikaṃ diṭṭhiṃ, paṭilacchiṃ ayoniso <span class="note">[jā. 1.16.204]</span>.</p>\r\n<p class="bodytext">ssatyādimhi-‘labhavasa chida gama bhida rudānaṃ cchaṅa’iti suttena ssena saha dhātvantassa cchādeso, lacchati, labhissati, lacchanti, labhissanti, lacchasi, labhissasi, lacchatha, labhissatha, lacchāmi, labhissāmi, lacchāma, labhissāma. lacchāma putte jīvantā, arogā ca bhavāmase <span class="note">[jā. 2.22.2260]</span>.</p>\r\n<p class="bodytext">ssādimhi-alacchā, alabhissā, alacchaṃsu, alabhissaṃsu.</p>\r\n<p class="bodytext">vaca-viyattiyaṃ vācāyaṃ, vacati, vacanti.</p>\r\n<p class="bodytext">kamme-vacīyati, vacīyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-651-');
INSERT INTO pages VALUES(54834,'annya_sadda_10',427,replace(replace('\r\n<p class="bodytext">yassa <a name="M0.0427"></a> pubbarūpatte ‘assū’ti suttena ādimhi akārassa uttaṃ, vuccati, vuccanti, vuccare, vuccate, vuccante, vuccare.</p>\r\n<p class="bodytext">kārite-vāceti, vācenti, vācayati, vācayanti, vācāpeti, vācāpenti, vācāpayati, vācāpayanti.</p>\r\n<p class="bodytext">īādimhi-avaci, vaci.</p>\r\n<p class="bodytext">mahāvuttinā ākārena saha sāgamo, avacāsi, vacāsi, avacuṃ, vacuṃ, avaciṃsu, vaciṃsu, tvaṃ avaco, avaca, avaci, avacāsi, avacittha, avacittho, tumhe avacittha.</p>\r\n<p class="bodytext">‘mhāthānamuña’iti suttaṃ, tumhe avacuttha, vacuttha, ahaṃ avaciṃ, vaciṃ, avacāsiṃ, vacāsiṃ, mayaṃ avacimhā, vacimhā, avacimha, vacimha vā, mayaṃ avacumhā, vacumhā, so avacā, vacā.</p>\r\n<p class="bodytext">rassatte-avaca, avacittha, vacittha vā, ahaṃ avacaṃ, avaca, vaca vā, mayaṃ avacimhe, vacimhe.</p>\r\n<p class="bodytext"><a name="para652"></a><span class="paranum">652</span>. <span class="bld">īādo vacassoma</span> <span class="note">[ka. 477; rū. 479; nī. 958; caṃ. 6.2.69; pā. 7.4.20]</span>.</p>\r\n<p class="bodytext">īādīsu vacassa mānubandho o hoti.</p>\r\n<p class="bodytext">so avoci, te avocuṃ, avociṃsu, tvaṃ avoci, tumhe avocuttha, ahaṃ avociṃ, mayaṃ avocumhā, so avoca, rasso, bhagavā etadavoca <span class="note">[udā. 20]</span>.</p>\r\n<p class="bodytext">ssatyādimhi –</p>\r\n<p class="bodytext"><a name="para653"></a><span class="paranum">653</span>. <span class="bld">vacabhujamucavisānaṃ kkhaṅa</span> <span class="note">[ka. 481; rū. 524; nī. 963; ‘bhuja muca vaca visānaṃ kkhaṅa (bahūsu)]</span>.</p>\r\n<p class="bodytext">ssena saha vacādīnaṃ anto byañjano kkhaṅa hoti vā ssayuttāsu vibhattīsu.</p>\r\n','\r',char(13)),'\n',char(10)),'-652-653-');
INSERT INTO pages VALUES(54835,'annya_sadda_10',428,replace(replace('\r\n<p class="bodytext">vakkhati<a name="M0.0428"></a>, vacissati, vakkhanti, vakkhare, vacissanti, vacissare, vakkhasi, vakkhatha, vakkhāmi, vakkhāma, vakkhate, vakkhante, vakkhase, vakkhavhe, ahaṃ vakkhaṃ, vacissaṃ, mayaṃ vakkhāmhe, vacissāmhe.</p>\r\n<p class="bodytext">‘ssenā’ti adhikārena vinā dhātvantassa kkhādesopi labbhati, vakkhissati, vakkhissanti.</p>\r\n<p class="bodytext">ssādimhi-avakkhā, avacissā, avakkhaṃsu, avacissaṃsu.</p>\r\n<p class="bodytext">vada-viyattiyaṃ vācāyaṃ, vadati, vadanti, ovadati, ovadanti, vadasi, vadatha, vadāmi, vadāma.</p>\r\n<p class="bodytext">lassa ette-vadeti, vadenti, vadesi, vadetha, vademi, vadema.</p>\r\n<p class="bodytext">kamme-vadīyati, vadiyyati, ovadīyati, ovadiyyati.</p>\r\n<p class="bodytext">dassa cavaggatte yassa pubbarūpattaṃ, vajjati, vajjanti, ovajjati, ovajjanti.</p>\r\n<p class="bodytext">kārite-bheriṃ vādeti, vādenti, vādayati, vādayanti, guruṃ abhivādeti, abhivādenti, abhivādesi, abhivādetha, abhivādemi, abhivādema.</p>\r\n<p class="bodytext">mahāvuttinā assa ittaṃ, abhivādiyāmi, abhivādiyāma, abhivādayāmi, abhivādayāma vā, abhivandāmi, abhivandāmāti attho. vandanto hi "sukhī hotū"ti abhimaṅgalavacanaṃ vadāpeti nāma, tathāvacanañca vandanīyassa vattaṃ.</p>\r\n<p class="bodytext">ṇāpimhi na vuddhi, vadāpeti, vadāpayati.</p>\r\n<p class="bodytext">‘gamavadadānaṃ ghammavajjadajjā vā’ti vajjādeso, ‘ūlasse’ti lassa ettaṃ, vajjeti, vajjenti.</p>\r\n<p class="bodytext">kamme-vajjīyati, vajjīyanti.</p>\r\n<p class="bodytext">kārite-vajjāpeti, vajjāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54836,'annya_sadda_10',429,replace(replace('\r\n<p class="bodytext">eyyādimhi-‘eyyeyyāseyyaṃnaṃṭe’iti <a name="M0.0429"></a> eyyādīnaṃ ekavacanānaṃ ettaṃ, vade, vadeyya, vajje, vajjeyya, vadeyyuṃ, vajjeyyuṃ.</p>\r\n<p class="bodytext">vajjādese mahāvuttinā eyyassa āttaṃ, eyyumādīnaṃ eyyasaddassa lopo, so vajjā, te vajjuṃ.</p>\r\n<p class="bodytext">eyyādīnaṃ yyāsaddassa lopo vā, tvaṃ vajjāsi, vajjesi, tumhe vajjātha, vajjetha, ahaṃ vajjāmi, vajjemi, mayaṃ vajjāma, vajjema, ahaṃ vajjaṃ, mayaṃ vajjāmhe, vajjeyyāmhe.</p>\r\n<p class="bodytext">atrimā pāḷī-vajjuṃ vā te na vā vajjuṃ, natthi nāsāya rūhanā <span class="note">[jā. 1.3.33]</span>, ammaṃ ārogyaṃ vajjāsi, tvañca tāta sukhī bhava <span class="note">[jā. 2.22.2148]</span>, ammaṃ ārogyaṃ vajjātha, ayaṃ no neti brāhmaṇo <span class="note">[jā. 2.22.2174]</span> iccādi.</p>\r\n<p class="bodytext">hiyyattaniyaṃ-so avadā, vadā, avajjā, vajjā, te avadū, vadū, avajjū, vajjū.</p>\r\n<p class="bodytext">ajjattaniyaṃ-so avadi, vadi, avajji, vajji, te avaduṃ, vaduṃ, avajjuṃ, vajjuṃ, avadiṃsu, vadiṃsu, avajjiṃsu, vajjiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-vadissati, vajjissati.</p>\r\n<p class="bodytext">ssādimhi-avadissā, avajjissā iccādi.</p>\r\n<p class="bodytext">vida-ñāṇe, vidati.</p>\r\n<p class="bodytext">‘yuvaṇṇānamiyaṅauvaṅa sare’ti saramhi iyādeso, te vidiyanti.</p>\r\n<p class="bodytext">kārite-nivedeti, paṭivedeti, nivedayati, paṭivedayati, paṭivedayāmi vo bhikkhave <span class="note">[ma. ni. 1.416]</span>, jānāpemīti attho. vedayāmahaṃ bhante, vedayatīti maṃ saṅgho dhāretūti <span class="note">[cūḷava. aṭṭha. 102]</span> jānāpemi, pākaṭaṃ karomīti vā attho.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54837,'annya_sadda_10',430,replace(replace('\r\n<p class="bodytext">"vediyāmahaṃ <a name="M0.0430"></a> bhante, vediyatīti maṃ saṅgho dhāretū"tipi <span class="note">[cūḷava. aṭṭha. 102]</span> pāṭho, tattha apaccaye pare iyādeso yujjati.</p>\r\n<p class="bodytext">eyyādimhi-videyya, vidiyeyya, videyyuṃ, vidiyeyyuṃ.</p>\r\n<p class="bodytext">īādimhi-paccayānaṃ khayaṃ avedi, te viduṃ, vidiṃsu.</p>\r\n<p class="bodytext">kārite-nivedesi, nivedayi, paṭivedesi, paṭivedayi, nivedayuṃ, nivedayiṃsu, paṭivedayuṃ, paṭivedayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-vidissati, vedissati, parisuddhāti vedissāmi <span class="note">[mahāva. 134]</span> iccādi.</p>\r\n<p class="bodytext">vasa-nivāse, vasati, vasanti, nivasati, nivasanti.</p>\r\n<p class="bodytext">kamme-adhi, āpubbo, tena gāmo adhivasīyati, āvasīyati, ajjhāvasīyati.</p>\r\n<p class="bodytext">‘assū’ti suttena akārassa uttaṃ, vussati, vussanti, vussare, "bhagavati brahmacariyaṃ vussatī"ti <span class="note">[ma. ni. 1.257]</span> pāḷi.</p>\r\n<p class="bodytext">kārite-vāseti, adhivāseti, vāsayati, adhivāsayati.</p>\r\n<p class="bodytext">ṇāpimhi vuddhi natthi, vasāpeti, vasāpayati.</p>\r\n<p class="bodytext">īādimhi-avasi, vasi, avasuṃ, vasuṃ, avasiṃsu, vasiṃsu.</p>\r\n<p class="bodytext">ssatyādīsu-‘labha vasa chida gama bhida rudānaṃ cchaṅa’iti ssena saha dhātvantassa cchādeso, vacchati, vasissati, vacchanti, vasissanti, āyasmato nissāya vacchāmi <span class="note">[mahāva. 77]</span>, na te vacchāmi santike <span class="note">[jā. 2.22.1933]</span>, avacchā, avasissā, avacchaṃsu, avasissaṃsu.</p>\r\n<p class="bodytext">visa-pavisane, pavisati, pavisanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54838,'annya_sadda_10',431,replace(replace('\r\n<p class="bodytext">kamme-pavisīyati<a name="M0.0431"></a>, pavisīyanti, pavisīyate, pavisīyante.</p>\r\n<p class="bodytext">yassa pubbarūpatte-pavissati, pavissanti, pavissare, pavissate, pavissante, pavissare.</p>\r\n<p class="bodytext">kārite-paveseti, pavesayati.</p>\r\n<p class="bodytext">kamme-pavesīyati, pavesīyanti.</p>\r\n<p class="bodytext">īādimhi upasaggassa dīgho vā, pāvisi.</p>\r\n<p class="bodytext">mahāvuttinā dhātvantassa kkho hoti, pāvekkhi pathaviṃ cecco <span class="note">[jā. 1.19.98]</span>, so pāvekkhi kāsirājā <span class="note">[jā. 1.15.266]</span>, so tassa gehaṃ pāvekkhi <span class="note">[jā. 1.15.303]</span>, pāvisuṃ, pāvisiṃsu, pāvekkhiṃsu.</p>\r\n<p class="bodytext">ssatyādīsu ‘vaca bhuja muca visānaṃ kkhaṅa’iti ssena saha dhātvantassa kkho, pavekkhati, pavisissati, pavekkhanti, pavisissanti, esa bhiyyo pavekkhāmi, vammikaṃ sataporisaṃ <span class="note">[jā. 1.4.100]</span>, pāvekkhā, pavisissā, pāvekkhaṃsu, pavisissaṃsu.</p>\r\n<p class="bodytext">sada-saṃsīdane, ‘jarasadānamīma vā’tiādisaramhā īmaāgamo hoti vā, sīdati, sīdanti, lābūni sīdanti, silā plavanti <span class="note">[jā. 1.1.77]</span>, saṃsīdati, visīdati, osīdati, avasīdati.</p>\r\n<p class="bodytext">nipubbo nisajjāyaṃ, nisīdati, nisīdanti.</p>\r\n<p class="bodytext">papubbo pasāde, pasīdati, pasīdanti.</p>\r\n<p class="bodytext">kāritepi na vuddhi ādesantarattā, sīdeti, sīdayati, saṃsīdeti, saṃsīdayati, osīdeti, osīdayati, osīdāpeti, osīdāpayati, nisīdāpeti, nisīdāpayati.</p>\r\n<p class="bodytext">papubbamhi īma na hoti, pitā puttaṃ buddhe pasādeti, pasādayati, pasādenti, pasādayanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54839,'annya_sadda_10',432,replace(replace('\r\n<p class="bodytext">kamme-pasādīyati<a name="M0.0432"></a>, pasādīyanti.</p>\r\n<p class="bodytext">hana-hiṃsā, gatīsu, hanati, hananti.</p>\r\n<p class="bodytext">‘kvaci vikaraṇāna’nti suttena lavikaraṇassa lope hanti, phalaṃ ve kadaliṃ hanti, sakkāro kāpurisaṃ hanti <span class="note">[cūḷava. 335]</span>, hanti kuddhā puthujjano <span class="note">[a. ni. 7.64]</span>.</p>\r\n<p class="bodytext">mahāvuttinā kvaci dhātvantalopo, vikkosamānā tibbāhi, hanti nesaṃ varaṃ varaṃ <span class="note">[jā. 2.22.2370]</span>, luddakā migaṃ hanti, kevaṭṭā macchaṃ hanti.</p>\r\n<p class="bodytext">kamme-hanīyati, hanīyanti.</p>\r\n<p class="bodytext">dhātvantassa cavaggatte yassa pubbarūpattaṃ, haññati, haññanti.</p>\r\n<p class="bodytext">kārite –</p>\r\n<p class="bodytext"><a name="para654"></a><span class="paranum">654</span>. <span class="bld">hanassa ghāto ṇānubandhe</span> <span class="note">[ka. 591; rū. 544; nī. 1195]</span>.</p>\r\n<p class="bodytext">hanassa ghāto hoti ṇānubandhe paccaye.</p>\r\n<p class="bodytext">ghāteti, ghātayati, ghātāpeti, ghātāpayati.</p>\r\n<p class="bodytext">kamme-ghātīyati, ghātāpīyati.</p>\r\n<p class="bodytext">īādimhi-ahani, hani, ahaniṃsu, haniṃsu.</p>\r\n<p class="bodytext">kamme-ahaññi, haññi, ahaññiṃsu, haññiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi –</p>\r\n<p class="bodytext"><a name="para655"></a><span class="paranum">655</span>. <span class="bld">hanā jekhā</span> <span class="note">[ka. 481; rū. 524; nī. 967, 969? ‘.chakhā’ (bahūsu) ‘chekhā’ (katthaci)]</span>.</p>\r\n<p class="bodytext">hanamhā parassa ssakārassa je, khādesā honti vā, mahāvuttinā dhātvantassa pararūpattaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-654-655-');
INSERT INTO pages VALUES(54840,'annya_sadda_10',433,replace(replace('\r\n<p class="bodytext">hajjeti<a name="M0.0433"></a>, hanissati, hajjenti, hajjesi, hajjetha, hajjemi, hanissāmi, hajjema, hanissāma.</p>\r\n<p class="bodytext">khādese mahāvuttinā dhātvantassa vaggantattaṃ, paṭihaṅkhati, paṭihanissati, paṭihaṅkhanti, paṭihaṅkhasi, paṭihaṅkhāmi, paṭihaṅkhāma, paṭihanissāma.</p>\r\n<p class="bodytext">hara-haraṇe, harati, haranti.</p>\r\n<p class="bodytext">kamme-harīyati, harīyanti.</p>\r\n<p class="bodytext">kārite-hāreti, hārayati.</p>\r\n<p class="bodytext">ṇāpimhi na vuddhi, harāpeti, harāpayati.</p>\r\n<p class="bodytext">kamme-hārīyati, harāpīyati.</p>\r\n<p class="bodytext">ā, īādīsu –</p>\r\n<p class="bodytext"><a name="para656"></a><span class="paranum">656</span>. <span class="bld">āīādīsu harassā</span>.</p>\r\n<p class="bodytext">āādīsu īādīsu ca harassa rakārassa ā hoti vā, so ahā, aharā.</p>\r\n<p class="bodytext">īādimhi-so ahāsi, ajini maṃ ahāsi me <span class="note">[dha. pa. 3-4]</span>, attānaṃ upasaṃhāsi, āsanaṃ abhihāsi, sāsane vihāsi, vihāsi purisuttamo <span class="note">[gavesitabbaṃ]</span>, dhammaṃ payirudāhāsi, ahari, hari, vihāsuṃ, āhiṃsu, vihiṃsu vā, "mā me tato mūlaphalaṃ āhaṃsū"ti <span class="note">[jā. 2.18.22]</span> pāḷi, ahāsuṃ, aharuṃ, haruṃ, ahariṃsu, hariṃsu, tvaṃ ahāsi, ahari, tumhe ahāsittha, aharittha, ahaṃ ahāsiṃ, ahariṃ, vihāsiṃ sāsane rato <span class="note">[apa. thera 1.2.84]</span>, mayaṃ ahāsimhā, aharimhā.</p>\r\n<p class="bodytext">parachakke assa tthattaṃ, so ahāsittha, aharittha.</p>\r\n','\r',char(13)),'\n',char(10)),'-656-');
INSERT INTO pages VALUES(54841,'annya_sadda_10',434,replace(replace('\r\n<p class="bodytext">ssatyādīsu <a name="M0.0434"></a> –</p>\r\n<p class="bodytext"><a name="para657"></a><span class="paranum">657</span>. <span class="bld">harassa cāhaṅa sse</span> <span class="note">[‘yāssa cāhaṅa ssena’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ssakāravatīsu vibhattīsu ssena saha harassa ca karassa ca rakārassa āhaṅa hoti vā.</p>\r\n<p class="bodytext">iu āgame-hāhiti, khārikājañca hāhiti <span class="note">[jā. 2.22.1759]</span>. hāhati vā, harissati, hāhinti, hāhanti, harissanti, hāhasi, sukhaṃ bhikkhu vihāhisi <span class="note">[dha. pa. 379]</span>. hāhatha, hāhāmi, hāhāma, harissāma.</p>\r\n<p class="bodytext">mahāvuttinā harassa dhātvantassa lopo ca, "yo imasmiṃ dhammavinaye appamatto vihassati <span class="note">[saṃ. ni. 1.185]</span>, purakkhatvā vihassāma <span class="note">[therīgā. 121]</span>, ahaṃ udakamāhissa"nti <span class="note">[jā. 2.22.1931]</span> pāḷī.</p>\r\n<p class="bodytext">ssādimhi-ahāhā, aharissā, ahāhaṃsu, aharissaṃsu.</p>\r\n<p class="bodytext">āpubba sīsa-patthanāyaṃ, āsīsati, āsīsanti, paccāsīsati, paccāsīsanti.</p>\r\n<p class="bodytext"><a name="para658"></a><span class="paranum">658</span>. <span class="bld">ādismā sarā</span> <span class="note">[caṃ. 5.1.3; pā. 6.1.2]</span>.</p>\r\n<p class="bodytext">ādibhūtā saramhā paraṃ paṭhamasaddarūpaṃ ekassaraṃ dverūpaṃ hoti, iminā sarapubbānaṃ dhātupadānaṃ padadvitte āsīsa, sīsa iti rūpadvayaṃ bhavati.</p>\r\n<p class="bodytext"><a name="para659"></a><span class="paranum">659</span>. <span class="bld">loponādibyañjanassa</span> <span class="note">[caṃ. 6.2.112; pā. 7.4.60]</span>.</p>\r\n<p class="bodytext">dvitte anādibhūtassa ekassa byañjanassa lopo hotīti purime sīsarūpe sakāralopo.</p>\r\n','\r',char(13)),'\n',char(10)),'-657-658-659-');
INSERT INTO pages VALUES(54842,'annya_sadda_10',435,replace(replace('\r\n<p class="bodytext">āsīsīsati<a name="M0.0435"></a>, āsīsīsanti iccādi.</p>\r\n<p class="bodytext">tathā ‘parokkhāyañcā’ti sutte casaddena kamādīnaṃ dhātupadānaṃ padadvitte kate ‘loponādibyañjanassā’ti purime padarūpe anādibyañjanalopo, ‘kavaggahānaṃ cavaggajā’ti sesassa kavaggassa cavaggattaṃ, ‘niggahītañcā’ti niggahītāgamo, caṅkamati, caṅkamanti, caṅkamatu, caṅkamantu, caṅkameyya, caṅkameyyuṃ iccādi.</p>\r\n<p class="bodytext">kuca-saṅkocane, caṅkocati, caṅkocanti.</p>\r\n<p class="bodytext">cala-calane, cañcalati, cañcalanti.</p>\r\n<p class="bodytext">mahāvuttinā niggahītassa pararūpatte jara-bhijjane, jajjarati jajjaranti.</p>\r\n<p class="bodytext">daḷa-dittiyaṃ, daddallati, daddallanti.</p>\r\n<p class="bodytext">muha-vecitte, momuhati, momuhanti, mahāvuttinā ussa ottaṃ.</p>\r\n<p class="bodytext">tathā ru-sadde, roruvati, roruvanti.</p>\r\n<p class="bodytext">lupa-giddhe, loluppati, loluppanti iccādi.</p>\r\n<p class="bodytext">padadvittaṃ nāma padatthānaṃ atisayatādīpanatthaṃ, vicchāyaṃ pana ponopuñña, sambhamādīsu ca dvitte anādibyañjanalopo natthi, gāmo gāmo ramaṇīyo. tathā kvaci atisayadīpanepi, rūparūpaṃ, dukkhadukkhaṃ, ajjhattajjhattaṃ, devadevo, munimuni, rājarājā, brahmabrahmā, varavaro, aggaaggo, jeṭṭhajeṭṭho, seṭṭhaseṭṭho, pasatthapasattho, uggatauggato, ukkaṭṭhukkaṭṭho, omakomako, dubbaladubbalo, abalaabalo, mahantamahanto iccādi.</p>\r\n<p class="centered">bhūvādigaṇo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54843,'annya_sadda_10',436,replace(replace('\r\n<p class="subhead">rudhādigaṇa</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0436"></a> rudhādigaṇo vuccate.</p>\r\n<p class="bodytext">‘kattarī’ti padaṃ vattate, tañca bahulādhikārā vikaraṇānaṃ kattari nibandhaṃ bhāva, kammesu anibandhaṃ vikappena pavattiṃ dīpeti, tasmā bhāva, kammesu ca kāritarūpesu ca vikaraṇānaṃ pavatti veditabbā hotīti.</p>\r\n<p class="bodytext"><span class="bld">chida, bhida, bhuja, muca, yuja, rica, rudha, lipa, vida, sica, subha</span>.</p>\r\n<p class="bodytext"><a name="para660"></a><span class="paranum">660</span>. <span class="bld">mañca rudhādīnaṃ</span> <span class="note">[ka. 446; rū. 509; nī. 926; caṃ. 1.1.93; pā. 3.1.78]</span>.</p>\r\n<p class="bodytext">rudhādīhi kriyatthehi kattari lo hoti, tesañca rudhādīnaṃ pubbantasaramhā paraṃ niggahītaṃ āgacchati, mānubandho pubbantadīpanattho, akāro uccāraṇattho, casaddena rudha, subhādīhi i, ī, e, opaccaye saṅgaṇhāti, niggahītassa vaggantattaṃ.</p>\r\n<p class="bodytext">rundhati.</p>\r\n<p class="bodytext">chida-dvidhākaraṇe, chindati, chindanti.</p>\r\n<p class="bodytext">kamme kyo, ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti yamhi dhātvantassa cavaggattaṃ, ‘vaggalasehi te’ti yassa pubbarūpattaṃ. chijjati, chijjanti.</p>\r\n<p class="bodytext">‘garupubbā rassā re ntentīna’nti catunnaṃ nte, ntīnaṃ rettaṃ, chindate, chijjate, chijjante, chijjare.</p>\r\n<p class="bodytext">iminā niggahītāgamo, chindīyati, chindīyanti, chindīyate, chindīyante.</p>\r\n<p class="bodytext">kārite-chedeti, chedayati, chedāpeti, chedāpayati, chindeti, chindayati, chindāpeti, chindāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'-660-');
INSERT INTO pages VALUES(54844,'annya_sadda_10',437,replace(replace('\r\n<p class="bodytext">īādimhi-acchindi<a name="M0.0437"></a>, chindi, acchinduṃ, chinduṃ, acchindiṃsu, chindiṃsu.</p>\r\n<p class="bodytext">mahāvuttinā dhātvantassa ccho pubbassa dvittañca, acchecchi taṇhaṃ, vivattayi saṃyojanaṃ <span class="note">[itivu. 53]</span>, "acchejjī"tipi divādipāṭho dissati, acchecchuṃ vata bho rukkhaṃ <span class="note">[jā. 2.22.1788]</span>.</p>\r\n<p class="bodytext">kamme-acchijji, chijji, acchindiyi, chindiyi.</p>\r\n<p class="bodytext">kārite-chedesi, kaṇṇanāsañca chedayi <span class="note">[jā. 1.4.49]</span>, chindesi, chindayi.</p>\r\n<p class="bodytext">ssatyādimhi-‘labhavasachidagamabhidarudānaṃ cchaṅa’iti ssena saha dhātvantassa ccho, checchati, chindissati, checchanti, chindissanti, checchasi, checchata, checchāmi, checchāma, chindissāma.</p>\r\n<p class="bodytext">ssādimhi-acchecchā, acchindissā, acchecchaṃsu, acchindissaṃsu.</p>\r\n<p class="bodytext">bhida-vidāraṇe, bhindati, bhindanti.</p>\r\n<p class="bodytext">kamme-bhijjati, bhijjanti, bhijjare, bhindiyati, bhindiyanti.</p>\r\n<p class="bodytext">kārite-bhikkhū bhikkhūhi bhedeti <span class="note">[mahāva. 107]</span>, bhedayati, bhedāpeti, bhedāpayati, bhindeti, bhindayati, bhindāpeti, bhindāpayati.</p>\r\n<p class="bodytext">kamme-bhedīyati, bhedāpīyati.</p>\r\n<p class="bodytext">īādimhi-abhindi, bhindi, abhinduṃ, bhinduṃ, abhindiṃsu, bhindiṃsu.</p>\r\n<p class="bodytext">kamme-abhijji, bhijji, abhindiyi, bhindiyi.</p>\r\n<p class="bodytext">kārite-abhedesi, bhedesi, abhedayi, bhedayi, bhedāpesi, bhedāpayi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54845,'annya_sadda_10',438,replace(replace('\r\n<p class="bodytext">ssatyādimhi-‘labhavasa.’iccādinā <a name="M0.0438"></a> ssena saha dassa ccho, bhecchati, bhindissati, bhecchanti, bhindissanti, bhecchasi, bhecchatha, bhecchāmi, bhecchāma, bhindissāma, "taṃ te paññāya bhecchāmī"ti <span class="note">[su. ni. 445]</span> pāḷi.</p>\r\n<p class="bodytext">ssādimhi-acchecchā, acchindissāiccādi.</p>\r\n<p class="bodytext">bhuja-pālana, byavaharaṇesu, bhuñjati, bhuñjanti.</p>\r\n<p class="bodytext">kamme-bhujjati, bhujjanti.</p>\r\n<p class="bodytext">kārite-bhojeti, bhojayati, bhojāpeti, bhojāpayati.</p>\r\n<p class="bodytext">kamme-bhojīyati, bhojāpīyati.</p>\r\n<p class="bodytext">ssatyādimhi-‘vacabhujamucavisānaṃ kkhaṅa’iti ssena saha dhātvantassa kkho, ādivuddhi, bhokkhati, bhuñjissati, bhokkhanti, bhokkhasi, bhokkhatha, bhokkhāmi, bhokkhāma, bhuñjissāma.</p>\r\n<p class="bodytext">ssādimhi-abhokkhā, abhuñjissā, abhokkhaṃsu, abhuñjissaṃsu iccādi.</p>\r\n<p class="bodytext">muca-mocane, muñcati, muñcanti, muñcare.</p>\r\n<p class="bodytext">kamme-muccati, muccanti, muñcīyati, muñcīyanti.</p>\r\n<p class="bodytext">kārite-mocāpeti, mocāpayati.</p>\r\n<p class="bodytext">īādimhi-amuñci, muñci, amuñciṃsu, muñciṃsu.</p>\r\n<p class="bodytext">kārite-amocesi, mocesi, amocayi, mocayi, amocesuṃ, mocesuṃ, amocayuṃ, mocayuṃ, amociṃsu, mociṃsu, amocayiṃsu, mocayiṃsu.</p>\r\n<p class="bodytext">ssatyādimhi-ssena saha cassa kkho, mokkhati, muñcissati, mokkhanti mārabandhanā <span class="note">[dha. pa. 37]</span>. na me samaṇa mokkhasi <span class="note">[saṃ. ni. 1.140]</span>. mokkhatha, mokkhāmi, mokkhāma, muñcissāma.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54846,'annya_sadda_10',439,replace(replace('\r\n<p class="bodytext">ssādimhi-amokkhā<a name="M0.0439"></a>, mokkhā, amuñcissā, muñcissā, amokkhaṃsu, mokkhaṃsu, amuñcissaṃsu, muñcissaṃsu.</p>\r\n<p class="bodytext">yuja-yoge, yuñjati buddhasāsane, ārabhatīti attho, yuñjanti, pamādamanuyuñjanti, bālā dummedhino janā <span class="note">[dha. pa. 26]</span>. yuñjasi, yuñjatha buddhasāsane <span class="note">[saṃ. ni. 1.185]</span>. yuñjāmi, yuñjāma.</p>\r\n<p class="bodytext">kamme-yuñjīyati, yuñjīyanti.</p>\r\n<p class="bodytext">kārite-yojeti, payojeti, niyojeti, uyyojeti, yojayati, payojayati, niyojayati, uyyojayati.</p>\r\n<p class="bodytext">kamme-yojīyati, payojīyati, niyojīyati, uyyojīyati.</p>\r\n<p class="bodytext">rudha-āvaraṇe, rundhati, rundhiti, rundhīti, rundheti, rundhoti, rundhanti, orundhati, avarundhati, rundhāpeti, rundhāpayati, avarodheti, avarodhayati, uparodheti, uparodhayati, rodhāpeti, rodhāpayati.</p>\r\n<p class="bodytext">kamme-avarodhīyati iccādi.</p>\r\n<p class="bodytext">lipa-limpane, limpati, limpanti.</p>\r\n<p class="bodytext">kamme-limpīyati.</p>\r\n<p class="bodytext">kārite-limpeti, limpayati, limpāpeti, limpāpayati, lepeti, lepayati, lepāpeti, lepāpayati iccādi.</p>\r\n<p class="bodytext">vida-paṭilābhe, vindati, vindanti.</p>\r\n<p class="bodytext">kamme-vindīyati, vindīyanti.</p>\r\n<p class="bodytext">kārite-vindeti, vindayati, vindāpeti, vindāpayahi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54847,'annya_sadda_10',440,replace(replace('\r\n<p class="bodytext">īādimhi-avindi<a name="M0.0440"></a>, vindi, udaṅgaṇe tattha papaṃ avinduṃ <span class="note">[jā. 1.1.2]</span>, avindiṃsu, vindiṃsu iccādi.</p>\r\n<p class="bodytext">sica-secane, siñcati, siñcanti.</p>\r\n<p class="bodytext">kamme-siñcīyati, siñcīyanti.</p>\r\n<p class="bodytext">kārite-siñceti, siñcayati, siñcāpeti, siñcāpayati, siñceyya vā siñcāpeyya vā <span class="note">[pāci. 140]</span> iccādi.</p>\r\n<p class="bodytext">subha-sampahāre, yo no gāvova sumbhati <span class="note">[jā. 2.22.2132]</span>. sumbhanti, sumbhasi, sumbhatha, bhūmiṃ sumbhāmi vegasā <span class="note">[jā. 1.5.51]</span>, sumbhāma, sumbhiti, sumbhīti, sumbheti, sumbhoti iccādi.</p>\r\n<p class="bodytext">gahadhātupi idha saṅgahitā. gaha-upādāne. ‘mañca rudhādīna’nti niggahītena saha lapaccayo.</p>\r\n<p class="bodytext"><a name="para661"></a><span class="paranum">661</span>. <span class="bld">ṇo niggahītassa</span> <span class="note">[ka. 490; rū. 518; nī. 982]</span>.</p>\r\n<p class="bodytext">gahadhātumhi āgatassa niggahītassa ṇo hoti. mahāvuttinā vikappena lassa dīgho.</p>\r\n<p class="bodytext">gaṇhāti, gaṇhati vā, gaṇhanti, gaṇhasi, gaṇhatha, gaṇhāmi, gaṇhāma.</p>\r\n<p class="bodytext">kamme-gaṇhīyati, gaṇhīyanti.</p>\r\n<p class="bodytext">‘hassa vipallāso’ti ha, yānaṃ vipariyāyo, gayhati, gayhanti, gayhare.</p>\r\n<p class="bodytext">kārite-gāheti, gāhayati, gāhāpeti, gāhāpayati iccādi.</p>\r\n<p class="bodytext"><a name="para662"></a><span class="paranum">662</span>. <span class="bld">gahassa gheppo</span> <span class="note">[ka. 489; rū. 519; nī. 901]</span>.</p>\r\n<p class="bodytext">nta, māna, tyādīsu gahassa gheppādeso hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-661-662-');
INSERT INTO pages VALUES(54848,'annya_sadda_10',441,replace(replace('\r\n<p class="bodytext">gheppati<a name="M0.0441"></a>, gheppanti.</p>\r\n<p class="bodytext">kamme-gheppīyati, gheppīyanti.</p>\r\n<p class="bodytext">īādimhi-agaṇhi, gaṇhi.</p>\r\n<p class="bodytext">mahāvuttinā niggahītalopo, iuāgamassa ettaṃ, aggahesi, paṭiggahesi, anuggahesi, aggaṇhiṃsu, gaṇhiṃsu. aggahesuṃ, paṭiggahesuṃ, anuggahesuṃ.</p>\r\n<p class="bodytext">ssatyādimhi-gaṇhissati, gahessati, gaṇhissanti, gahessanti iccādi.</p>\r\n<p class="centered">rudhādigaṇo niṭṭhito.</p>\r\n<p class="subhead">divādigaṇa</p>\r\n<p class="bodytext">atha divādigaṇo vuccate.</p>\r\n<p class="bodytext">idha dhātūnaṃ kamo antakkharavasena vattabbo sabbaso sadisarūpattā.</p>\r\n<p class="bodytext"><span class="bld">muca, vica, yuja, luja, vija, gada, pada, mada, vida, idha, kudha, gidha, budha, yudha, vidha, sidha, sudha mana, hana, kupa, dīpa, lupa, vapa, supa, divu, sivu, tasa, tusa, disa, dusa, sisa, susa, daha, naha, muha</span>.</p>\r\n<p class="bodytext"><a name="para663"></a><span class="paranum">663</span>. <span class="bld">divādīhi yaka</span> <span class="note">[ka. 447; rū. 510; nī. 928; caṃ. 1.1.87; pā. 3.1.69]</span>.</p>\r\n<p class="bodytext">divādīhi kriyatthehi kattari kānubandho yapaccayo hoti.</p>\r\n<p class="bodytext">dibbati.</p>\r\n<p class="bodytext">muca-muttiyaṃ, yassa pubbarūpattaṃ, muccati, vimuccati. akammakattā suddhakammarūpaṃ na labbhati.</p>\r\n','\r',char(13)),'\n',char(10)),'-663-');
INSERT INTO pages VALUES(54849,'annya_sadda_10',442,replace(replace('\r\n<p class="bodytext">kārite-moceti<a name="M0.0442"></a>, mocayati, mocāpeti, mocāpayati.</p>\r\n<p class="bodytext">kamme-mocīyati, mocāpīyati, muccatu, dukkhā muccantu.</p>\r\n<p class="bodytext">ssatyādimhi dhātvantassa kkho, mokkhati, mokkhanti.</p>\r\n<p class="bodytext">vica-viveke, viviccati, viviccanti.</p>\r\n<p class="bodytext">kārite-viveceti, vivecayati, vivecāpeti, vivecāpayati.</p>\r\n<p class="bodytext">kamme-vivecīyati, vivecāpīyati iccādi.</p>\r\n<p class="bodytext">yuja-yuttiyaṃ, yujjati, yujjanti.</p>\r\n<p class="bodytext">luja-vināse, lujjati, lujjanti.</p>\r\n<p class="bodytext">vija-bhaya, calanesu, saṃvijjati, saṃvijjanti.</p>\r\n<p class="bodytext">kārite-saṃvejeti, saṃvejayati, saṃvejenti, saṃvejayanti iccādi.</p>\r\n<p class="bodytext">gada-gajjane, megho gajjati, gajjanti.</p>\r\n<p class="bodytext">pada-gatimhi, uppajjati, uppajjanti, nipajjati, vipajjati, sampajjati, āpajjati, samāpajjati, paṭipajjati.</p>\r\n<p class="bodytext">kamme-tena āpatti āpajjati, jhānaṃ samāpajjati, maggo paṭipajjati.</p>\r\n<p class="bodytext">kyamhi parepi yaka hoti, tena āpatti āpajjīyati. jhānaṃ samāpajjīyati, maggo paṭipajjīyati.</p>\r\n<p class="bodytext">kārite-uppādeti, uppādayati, nipphādeti, nipphādayati. sampādeti, sampādayati, āpādeti, āpādayati, paṭipādeti, paṭipādayati, paṭipajjāpeti, paṭipajjāpayati.</p>\r\n<p class="bodytext">kamme-uppādīyati, nipphādīyati, sampādīyati, āpādīyati, paṭipādīyati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54850,'annya_sadda_10',443,replace(replace('\r\n<p class="bodytext">uppajjatu<a name="M0.0443"></a>, uppajjantu, uppajjeyya, uppajjeyyuṃ, kinti nu kho saddhivihārikassa patto uppajjiyetha, cīvaraṃ uppajjiyetha, parikkhāro uppajjiyethāti <span class="note">[mahāva. 67]</span> imāni pana kattu, kammarūpāni.</p>\r\n<p class="bodytext">īādimhi-uppajji, nipajji, vipajji, sampajji, āpajji, samāpajji, paṭipajji.</p>\r\n<p class="bodytext"><a name="para664"></a><span class="paranum">664</span>. <span class="bld">kvaci vikaraṇānaṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">vikaraṇānaṃ kvaci lopo hoti.</p>\r\n<p class="bodytext">cakkhuṃ udapādi, ñāṇaṃ udapādi, vijjā udapādi, āloko udapādi <span class="note">[saṃ. ni. 5.1081]</span> iccādi, uppajjiṃsu, nipajjiṃsu.</p>\r\n<p class="bodytext">mada-ummāde, majjati, majjanti.</p>\r\n<p class="bodytext">vida-sattāyaṃ, vijjati, saṃvijjati.</p>\r\n<p class="bodytext">idha-samiddhiyaṃ, ijjhati, samijjhati.</p>\r\n<p class="bodytext">kudha-kope, kujjhati, kujjhanti.</p>\r\n<p class="bodytext">budha-avagamane, bujjhati, sambujjhati.</p>\r\n<p class="bodytext">paṭipubbo niddakkhaye vikasane ca, paṭibujjhati.</p>\r\n<p class="bodytext">kamme-tena dhammo bujjhati, dhammā bujjhanti, bujjhare, bujjhīyati, bujjhīyanti.</p>\r\n<p class="bodytext">kārite-bodheti, bodhayati, bodhāpeti, bodhāpayati, bujjhāpeti, bujjhāpayati.</p>\r\n<p class="bodytext">yudha-sampahāre, mallo mallena saddhiṃ yujjhati, dve senā yujjhanti, dve meṇḍā yujjhanti, dve usabhā yujjhanti, dve hatthino yujjhanti, dve kukkuṭā yujjhanti.</p>\r\n<p class="bodytext">kamme-yujjhīyati, yujjhīyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-664-');
INSERT INTO pages VALUES(54851,'annya_sadda_10',444,replace(replace('\r\n<p class="bodytext">kārite-yodheti<a name="M0.0444"></a>, yodhayati, yujjhāpeti, yujjhāpayati, "yodhetha māraṃ paññāvudhenā"ti <span class="note">[dha. pa. 40]</span> pāḷi.</p>\r\n<p class="bodytext">vidha-tāḷane, sarena migaṃ vijjhati, dhammaṃ paṭivijjhati, paṭivijjhanti.</p>\r\n<p class="bodytext">kamme kattusadisampi rūpaṃ hoti, tena dhammo paṭivijjhati, dhammā paṭivijjhanti, paṭivijjhīyati, paṭivijjhīyanti.</p>\r\n<p class="bodytext">kārite-vedheti, vedhayati, paṭivedheti, paṭivedhayati, iccādi.</p>\r\n<p class="bodytext">sidha-saṃsiddhiyaṃ, sijjhati, sijjhanti, sijjhare.</p>\r\n<p class="bodytext">kārite mahāvuttinā issa āttaṃ, sādheti, sādhayati, sādhenti, sādhayanti.</p>\r\n<p class="bodytext">kamme-sādhīyati, sādhīyanti iccādi.</p>\r\n<p class="bodytext">sudha-suddhiyaṃ, sujjhati, sujjhanti, visujjhati, parisujjhati.</p>\r\n<p class="bodytext">kārite-sodheti, sodhayati.</p>\r\n<p class="bodytext">mana-maññanāyaṃ, maññati, avamaññati, atimaññati, maññanti, avamaññanti, atimaññanti iccādi.</p>\r\n<p class="bodytext">hana-vighāta, saṅghātesu, haññati, vihaññati, haññanti, vihaññanti iccādi.</p>\r\n<p class="bodytext">kupa-kope, paro parassa kuppati, kucchivāto kuppati, rogo kuppati, paṭikuppati, tejodhātu pakuppati <span class="note">[ma. ni. 1.305]</span>.</p>\r\n<p class="bodytext">kārite-kopeti, kopayati iccādi.</p>\r\n<p class="bodytext">dīpa-dittiyaṃ, dippati, dippanti, pure adhammo dippati <span class="note">[cūḷava. 437]</span>.</p>\r\n<p class="bodytext">kamme-dīpīyati, dīpīyanti.</p>\r\n<p class="bodytext">kārite garupantattā na vuddhi, dīpeti, dīpayati, dīpenti, dīpayanti iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54852,'annya_sadda_10',445,replace(replace('\r\n<p class="bodytext">lupa-adassane<a name="M0.0445"></a>, luppati, luppanti.</p>\r\n<p class="bodytext">kārite-lopeti, lopayati iccādi.</p>\r\n<p class="bodytext">vapa-bījanikkhepe, vappati, vappanti iccādi.</p>\r\n<p class="bodytext">supa-suppane, suppati, suppanti.</p>\r\n<p class="bodytext">mahāvuttinā ādivuddhi, soppati, soppanti.</p>\r\n<p class="bodytext">samu-upasame nivāse ca, sammati, visammati, upasammati, vūpasammati, assame sammati, yattha sammati temiyo <span class="note">[jā. 2.22.73]</span>, sammanti. kārite na vuddhi, sameti, vūpasameti iccādi.</p>\r\n<p class="bodytext">divu-kīḷāyaṃ vijigīsāyaṃ byavahāre thuti, kanti, gati, sattīsu ca, ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti yamhi vassa battaṃ, ‘vaggalasehi te’ti yassa battaṃ, dibbati, dibbanti iccādi.</p>\r\n<p class="bodytext">sivu-saṃsibbane, sibbati, sibbanti, sibbeyya vā sibbāpeyya vā <span class="note">[pāci. 176]</span> iccādi.</p>\r\n<p class="bodytext">tasa-santāse, tassati.</p>\r\n<p class="bodytext">mahāvuttinā tassa trattaṃ, utrassati, ubbijjatīti attho. tassati, paritassati, pipāsatīti attho.</p>\r\n<p class="bodytext">kārite-tāseti, tāsayati iccādi.</p>\r\n<p class="bodytext">tusa-pītimhi, tussati, santussati.</p>\r\n<p class="bodytext">kammepi-tussati, santussati, tussīyati.</p>\r\n<p class="bodytext">kārite-toseti, tosayati iccādi.</p>\r\n<p class="bodytext">disa-paññāyane, dissati, padissati, sandissati. dissanti bālā abyattā <span class="note">[mahāva. 76]</span>, nimittāni padissanti <span class="note">[bu. vaṃ. 2.82]</span>, ime dhammā mayi sandissanti, ahañca imesu dhammesu sandissāmi <span class="note">[ma. ni. 3.253 (thokaṃ visadisaṃ)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54853,'annya_sadda_10',446,replace(replace('\r\n<p class="bodytext">dusa-paṭighāte<a name="M0.0446"></a>, dussati. dosaneyyesu dussati. padussati, dussanti, padussanti.</p>\r\n<p class="bodytext">kārite dīgho, dūseti, dūsayati.</p>\r\n<p class="bodytext">kamme-dūsīyati iccādi.</p>\r\n<p class="bodytext">sisa-asabbayoge, sissati, avasissati. sarīrāni avasissanti.</p>\r\n<p class="bodytext">kārite-seseti, sesayati iccādi.</p>\r\n<p class="bodytext">susa-sussane, sussati. aṭṭhi ca nhāru ca cammañca avasissatu, upasussatu me sarīre maṃsalohitaṃ <span class="note">[ma. ni. 2.184 (thokaṃ visadisaṃ)]</span> iccādi.</p>\r\n<p class="bodytext">daha-dāhe, ha, yānaṃ vipariyāyo, dayhati, dayhanti, ekacitakamhi dayhare.</p>\r\n<p class="bodytext">kārite-dāheti, dāhayati iccādi.</p>\r\n<p class="bodytext">naha-bandhane, sannayhati, sannayhanti iccādi.</p>\r\n<p class="bodytext">muha-muyhane, muyhati, sammuyhati, sammuyhāmi, pamuyhāmi. sabbā muyhanti me disā <span class="note">[jā. 2.22.2185]</span>.</p>\r\n<p class="bodytext">kārite-moheti, mohayati iccādi.</p>\r\n<p class="centered">divādigaṇo niṭṭhito.</p>\r\n<p class="subhead">svādigaṇa</p>\r\n<p class="bodytext">atha svādigaṇo vuccate.</p>\r\n<p class="bodytext"><span class="bld">gi, ci, mi, vu, su, hi, āpa, saka</span>.</p>\r\n<p class="bodytext">‘kā’ti vattate.</p>\r\n<p class="bodytext"><a name="para665"></a><span class="paranum">665</span>. <span class="bld">svādito kṇo</span> <span class="note">[ka. 448; rū. 512; nī. 929; caṃ. 1.1.95; pā. 3.1.74; ‘svādīhi.’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-665-');
INSERT INTO pages VALUES(54854,'annya_sadda_10',447,replace(replace('\r\n<p class="bodytext">svādīhi <a name="M0.0447"></a> kriyatthehi paraṃ kānubandhā ṇā, ṇo iti dve paccayā honti.</p>\r\n<p class="bodytext"><a name="para666"></a><span class="paranum">666</span>. <span class="bld">na te kānubandhanāgamesu</span>.</p>\r\n<p class="bodytext">ivaṇṇu’vaṇṇānaṃ akārassa ca te e, o, ā na honti kānubandhanāgamesu paresūti vuddhipaṭisedho.</p>\r\n<p class="bodytext">suṇāti, suṇoti.</p>\r\n<p class="bodytext">gi-sadde, giṇāti, giṇoti, anugiṇāti, paṭigiṇāti.</p>\r\n<p class="bodytext">pubbassaralopo, anugiṇanti, paṭigiṇanti.</p>\r\n<p class="bodytext">ci-caye, mahāvuttinā ṇassa nattaṃ, vaḍḍhakī pākāraṃ cināti, cinoti, ācināti, ācinoti, apacināti, apacinoti, viddhaṃsetīti attho.</p>\r\n<p class="bodytext">pubbassaralopo, cinanti, ācinanti, apacinanti.</p>\r\n<p class="bodytext">kamme-cīyati, ācīyati, apacīyati, cinīyati, ācinīyati, apacinīyati.</p>\r\n<p class="bodytext">kārite-cayāpeti, cayāpayati, cināpeti, cināpayati iccādi.</p>\r\n<p class="bodytext">mi-pakkhepe, miṇāti, miṇoti, mināti, minoti vā.</p>\r\n<p class="bodytext">vu-saṃvaraṇe, saṃvuṇāti, saṃvuṇoti, āvuṇāti, āvuṇoti.</p>\r\n<p class="bodytext">su-savane, suṇāti, suṇoti, suṇanti, suṇāsi, suṇosi.</p>\r\n','\r',char(13)),'\n',char(10)),'-666-');
INSERT INTO pages VALUES(54855,'annya_sadda_10',448,replace(replace('\r\n<p class="bodytext">rassatte-suṇasi <a name="M0.0448"></a> nāga <span class="note">[mahāva. 126]</span>. suṇātha, suṇotha, suṇāmi, suṇomi, suṇāma, suṇoma.</p>\r\n<p class="bodytext">kamme ‘dīgho sarassā’ti kyamhi dīgho, sūyati, suyyati, sūyanti, suyyanti, suṇīyati, suṇīyanti.</p>\r\n<p class="bodytext">kārite-sāveti, anusāveti, sāvayati, anusāvayati, suṇāpeti, suṇāpayati.</p>\r\n<p class="bodytext">kamme-sāvīyati, anusāvīyati.</p>\r\n<p class="bodytext">suṇātu, suṇantu, suyyatu, suyyantu, sāvetu, sāventu, suṇe, suṇeyya, suṇeyyuṃ, sūyeyya, suyyeyya, sūyeyyuṃ, suyyeyuṃ, sāveyya, sāveyyuṃ.</p>\r\n<p class="bodytext">īādimhi-asuṇi, suṇi.</p>\r\n<p class="bodytext"><a name="para667"></a><span class="paranum">667</span>. <span class="bld">tesu suto kṇokṇānaṃ roṭa</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">tesu īādīsu ssakāravantesu ca vacanesu sudhātuto paresaṃ kṇo, kṇānaṃ roṭa hoti, rānubandho sabbādesadīpanattho. ‘rānubandhentasarādissā’ti suttena ādisarassa lopo, dvittaṃ.</p>\r\n<p class="bodytext">assosi, assosuṃ, assosi, assosittha, assosiṃ, assosimhā, assosumhā, assosittha iccādi.</p>\r\n<p class="bodytext">ssatyādimhi-suṇissati, sossati, suṇissanti, sossanti, suṇissasi, sossasi, suṇissatha, sossatha, suṇissāmi, sossāmi, suṇissāma, sossāma. evaṃ parachakke.</p>\r\n<p class="bodytext">ssādimhi-asuṇissā, asossā, asuṇissaṃsu, sossaṃsu iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-667-');
INSERT INTO pages VALUES(54856,'annya_sadda_10',449,replace(replace('\r\n<p class="bodytext">papubbo <a name="M0.0449"></a> hi-pesane, pahiṇāti, pahiṇoti, pahiṇanti.</p>\r\n<p class="bodytext">īādimhi-dūtaṃ pahiṇi, pahiṇiṃsu, ‘kvaci vikaraṇāna’nti vikaraṇalopo, mahāvuttinā passa dīgho, dūtaṃ pāhesi <span class="note">[pārā. 297; mahāva. 198]</span>, pāhesuṃ iccādi.</p>\r\n<p class="bodytext">āpa-pāpuṇane papubbo –</p>\r\n<p class="bodytext"><a name="para668"></a><span class="paranum">668</span>. <span class="bld">sakāpānaṃ kukku kṇe</span> <span class="note">[ka. 517; rū. 488; nī. 1105; ".kukakū ṇe" (bahūsu)]</span>.</p>\r\n<p class="bodytext">saka, āpadhātūnaṃ kānubandhā kukāra, ukārā kamena āgamā honti kṇamhi paccaye.</p>\r\n<p class="bodytext">pāpuṇoti, pāpuṇanti, sampāpuṇanti.</p>\r\n<p class="bodytext">paripubbo pariyattiyaṃ, pariyāpuṇāti, pariyāpuṇanti.</p>\r\n<p class="bodytext">saṃpubbo-samāpuṇāti, parisamāpuṇāti, niṭṭhānaṃ gacchatīti attho.</p>\r\n<p class="bodytext">kṇeti kiṃ? pappoti.</p>\r\n<p class="bodytext">kamme-pāpīyati, pāpīyanti.</p>\r\n<p class="bodytext">kārite-pāpeti, pāpayati, pāpenti, pāpayanti.</p>\r\n<p class="bodytext">kamme-pāpīyati, pāpīyanti.</p>\r\n<p class="bodytext">īādimhi-pāpuṇi, pāpuṇiṃsu iccādi.</p>\r\n<p class="bodytext">saka-sattiyaṃ, sakkuṇoti, sakkuṇāti.</p>\r\n<p class="bodytext">kṇeti kiṃ? sakkoti, sakkuṇanti.</p>\r\n<p class="bodytext">īādimhi-asakkuṇi, sakkuṇi, asakkuṇiṃsu, sakkuṇiṃsu.</p>\r\n<p class="bodytext"><a name="para669"></a><span class="paranum">669</span>. <span class="bld">sakā kṇāssa kho īādo</span> <span class="note">[‘.ṇāssa kha." (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-668-669-');
INSERT INTO pages VALUES(54857,'annya_sadda_10',450,replace(replace('\r\n<p class="bodytext">sakamhā <a name="M0.0450"></a> parassa kṇāssa kho hoti īādimhi.</p>\r\n<p class="bodytext">asakkhi, sakkhi, asakkhiṃsu, sakkhiṃsu iccādi.</p>\r\n<p class="bodytext"><a name="para670"></a><span class="paranum">670</span>. <span class="bld">sse vā</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">sakamhā parassa kṇassa kho hoti vā sse pare.</p>\r\n<p class="bodytext">sakkhissati, sakkuṇissati, sakkhissanti, sakkuṇissanti, sakkhissasi, sakkhissatha, sakkhissāmi, sakkhissāma, sakkuṇissāma.</p>\r\n<p class="bodytext">‘dakkha sakkha hehi hohīhi lopo’ti ssassa vikappena lopo, sakkhiti, sakkhissati, sakkhinti, sakkhissanti.</p>\r\n<p class="bodytext">ssādimhi-asakkhissā, sakkhissā, asakkuṇissā, sakkuṇissā iccādi.</p>\r\n<p class="centered">svādigaṇo niṭṭhito.</p>\r\n<p class="subhead">kyādigaṇa</p>\r\n<p class="bodytext">atha kyādigaṇo vuccate.</p>\r\n<p class="bodytext"><span class="bld">kī, ji, ñā, dhū, pu, bhū, mā, mū, lū</span>.</p>\r\n<p class="bodytext"><a name="para671"></a><span class="paranum">671</span>. <span class="bld">kyādīhi kṇā</span> <span class="note">[ka. 449; rū. 513; nī. 930; caṃ. 1.1.101 .pe.. 3.1.81]</span>.</p>\r\n<p class="bodytext">kīiccādīhi kriyatthehi paraṃ kattari kānubandho ṇāpaccayo hoti.</p>\r\n<p class="bodytext"><a name="para672"></a><span class="paranum">672</span>. <span class="bld">kṇāknāsu rasso</span> <span class="note">[ka. 517; rū. 488; nī. 1105; caṃ. 6.1.108; pā. 7.3.80]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-670-671-672-');
INSERT INTO pages VALUES(54858,'annya_sadda_10',451,replace(replace('\r\n<p class="bodytext">etesu <a name="M0.0451"></a> dīghadhātūnaṃ rasso hoti.</p>\r\n<p class="bodytext">kī-dabbavinimaye, kiṇāti, kiṇanti, vikkiṇāti, vikkiṇanti.</p>\r\n<p class="bodytext">kamme-kīyati, kiyyati, vikkīyati, vikkiyyati, vikkiyyanti.</p>\r\n<p class="bodytext">kārite-vikkāyeti, vikkāyayati, kīṇāpeti, kīṇāpayati iccādi.</p>\r\n<p class="bodytext"><a name="para673"></a><span class="paranum">673</span>. <span class="bld">jyādīhi knā</span> <span class="note">[ka. 449; rū. 513; nī. 930]</span>.</p>\r\n<p class="bodytext">jiiccādīhi kattari kānubandho nāpaccayo hoti.</p>\r\n<p class="bodytext">jināti, jinanti.</p>\r\n<p class="bodytext">kamme-jīyati, jiyyati, jinīyati, jiniyyati.</p>\r\n<p class="bodytext">kārite-jayāpeti, jayāpayati, parājeti, parājayati, parājenti, parājayanti, jināpeti, jināpayati, ajini, jini, ajiniṃsu, jiniṃsu, jinissati, jinissanti iccādi.</p>\r\n<p class="bodytext">ñā-avabodhane.</p>\r\n<p class="bodytext"><a name="para674"></a><span class="paranum">674</span>. <span class="bld">ñāssa ne jā</span> <span class="note">[ka. 470; rū. 514; nī. 950; caṃ. 6.1.107; pā. 7.3.70, 79]</span>.</p>\r\n<p class="bodytext">nāpaccaye pare ñāssa jā hoti.</p>\r\n<p class="bodytext">jānāti, pajānāti, ājānāti, sañjānāti, vijānāti, abhijānāti, parijānāti, paṭijānāti, jānanti.</p>\r\n<p class="bodytext">kamme-ñāyati, paññāyati, aññāyati, saññāyati, viññāyati, abhiññāyati, pariññāyati, paṭiññāyati, ñāyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-673-674-');
INSERT INTO pages VALUES(54859,'annya_sadda_10',452,replace(replace('\r\n<p class="bodytext">kārite-ñāpeti<a name="M0.0452"></a>, ñāpayati, ñāpenti, ñāpayanti, jānāpeti, jānāpayati, jānāpenti, jānāpayanti.</p>\r\n<p class="bodytext">kamme-ñāpīyati, saññāpīyati, jānāpīyati.</p>\r\n<p class="bodytext"><a name="para675"></a><span class="paranum">675</span>. <span class="bld">ñāssa sanāssa nāyo timhi</span> <span class="note">[ka. 509; rū. 516; nī. 1022]</span>.</p>\r\n<p class="bodytext">nāsahitassa ñāssa nāyo hoti timhi, suttavibhattiyā anti, antesu ca.</p>\r\n<p class="bodytext">nāyati. viceyya viceyya atthe panāyatīti kho bhikkhave vipassīti vuccati <span class="note">[dī. ni. 2.41-44]</span>. nāyanti. "animittā na nāyare"ti <span class="note">[visuddhi 1.174]</span> pāḷi.</p>\r\n<p class="bodytext">eyyādimhi-jāneyya, jāneyyuṃ, jāneyyāsi, jāneyyātha, jāneyyāmi, jāneyyāma.</p>\r\n<p class="bodytext">utte-jāneyyāmu.</p>\r\n<p class="bodytext">eyyāmassa emutte "kathaṃ jānemu taṃ maya"nti <span class="note">[jā. 2.22.7]</span> pāḷi.</p>\r\n<p class="bodytext"><a name="para676"></a><span class="paranum">676</span>. <span class="bld">eyyassiyāñā vā</span> <span class="note">[ka. 508; rū. 515; nī. 1021]</span>.</p>\r\n<p class="bodytext">ñāto eyyassa iyā, ñā honti, vāsaddena eyyumādīnampi ñū, ñāsi, ñātha, ñāmi, ñāmādesā honti, eyyamiccassa ñañca.</p>\r\n<p class="bodytext">jāniyā.</p>\r\n<p class="bodytext"><a name="para677"></a><span class="paranum">677</span>. <span class="bld">ñāmhi jaṃ</span> <span class="note">[ka. 470; rū. 514; nī. 950]</span>.</p>\r\n<p class="bodytext">ñādese pare sanāssa ñāssa jaṃ hoti.</p>\r\n<p class="bodytext">jaññā, vijaññā.</p>\r\n','\r',char(13)),'\n',char(10)),'-675-676-677-');
INSERT INTO pages VALUES(54860,'annya_sadda_10',453,replace(replace('\r\n<p class="bodytext">suttavibhattena <a name="M0.0453"></a> ñūādīsupi jaṃ hoti. "pāpaṃ katvā mā maṃ jaññūti icchati <span class="note">[su. ni. 127; vibha. 894 ‘jaññā’ti]</span>, vivekadhammaṃ ahaṃ vijaññaṃ <span class="note">[gavesitabbaṃ]</span>, jaññāmu ce sīlavantaṃ vadaññu"nti <span class="note">[jā. 2.22.1301]</span> pāḷī. ‘jaññāsi, jaññātha, jaññāmi, jaññāmā’tipi yujjati.</p>\r\n<p class="bodytext"><a name="para678"></a><span class="paranum">678</span>. <span class="bld">īssatyādīsu knālopo</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">knāssa lopo hoti vā īādimhi ssatyādimhi ca.</p>\r\n<p class="bodytext">aññāsi, abbhaññāsi, ajāni, aññāsuṃ, aññaṃsu, abbhaññaṃsu, jāniṃsu, aññāsi, abbhaññāsi, ajāni, aññittha, jānittha, aññāsiṃ, abbhaññāsiṃ, ajāniṃ, jāniṃ, aññāsimhā, ajānimhā, jānimhā, ñāssati, jānissati, ñāssanti, jānissanti.</p>\r\n<p class="bodytext">kamme-viññāyissati, viññāyissanti.</p>\r\n<p class="bodytext"><a name="para679"></a><span class="paranum">679</span>. <span class="bld">ssassa hi kamme</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">ñāto ssassa hi hoti vā kamme.</p>\r\n<p class="bodytext">paññāyihi. "paññāyihinti etā, daharā"ti <span class="note">[jā. 2.17.197]</span> pāḷi. paññāyissati, paññāyissanti iccādi.</p>\r\n<p class="bodytext">dhū-vidhunane, knāmhi rasso, dhunāti, dhunanti.</p>\r\n<p class="bodytext">kamme-dhunīyati, dhunīyanti.</p>\r\n<p class="bodytext">kārite-dhunāpeti, dhunāpayati.</p>\r\n<p class="bodytext">pu-sodhane, punāti, punanti.</p>\r\n<p class="bodytext">bhū-pattiyaṃ, rasso, abhisambhunāti, abhisambhunanti. nāssa ṇatte-abhisambhuṇāti, abhisambhuṇanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-678-679-');
INSERT INTO pages VALUES(54861,'annya_sadda_10',454,replace(replace('\r\n<p class="bodytext">mā-parimāṇe<a name="M0.0454"></a>, mahāvuttinā dhātvantassa ittaṃ, mināti, nimmināti.</p>\r\n<p class="bodytext">kamme-upamīyati, upamīyanti, nimmīyati, nimmīyanti.</p>\r\n<p class="bodytext">kārite-nagaraṃ māpeti, māpayati, māpīyati, māpīyanti, nimmini, nimminiṃsu, māpesi, māpayi, māpesuṃ, māpayuṃ, nimminissati, nimminissanti, māpessati, māpessanti iccādi.</p>\r\n<p class="bodytext">mū-bandhane, munāti.</p>\r\n<p class="bodytext">lū-chedane, rassattaṃ, lunāti, lunanti.</p>\r\n<p class="bodytext">kamme-lūyati, lūyanti.</p>\r\n<p class="bodytext">kārite-lāvayati, lāvayanti.</p>\r\n<p class="bodytext">kamme-lāvīyati iccādi.</p>\r\n<p class="centered">kyādigaṇo niṭṭhito.</p>\r\n<p class="subhead">tanādigaṇa</p>\r\n<p class="bodytext">atha tanādigaṇo vuccate.</p>\r\n<p class="bodytext"><span class="bld">āpa, kara, tana, saka</span>.</p>\r\n<p class="bodytext"><a name="para680"></a><span class="paranum">680</span>. <span class="bld">tanāditvo</span> <span class="note">[ka. 451; rū. 520; nī. 932; caṃ. 1.1.97; pā. 3.1.79]</span>.</p>\r\n<p class="bodytext">tanādīhi paraṃ opaccayo hoti.</p>\r\n<p class="bodytext">tanoti.</p>\r\n<p class="bodytext">āpa-pāpuṇane papubbo. dhātvantassa dvittaṃ rasso ca, pappoti, papponti.</p>\r\n<p class="bodytext">kamme-pāpīyati, pāpīyanti.</p>\r\n<p class="bodytext">kārite-pāpeti, pāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'-680-');
INSERT INTO pages VALUES(54862,'annya_sadda_10',455,replace(replace('\r\n<p class="bodytext">kamme-pāpīyati <a name="M0.0455"></a> pāpīyanti iccādi.</p>\r\n<p class="bodytext">kara-karaṇe, karoti, karonti.</p>\r\n<p class="bodytext">kamme-karīyati, karīyanti.</p>\r\n<p class="bodytext">‘tavaggavaraṇānaṃ ye cavaggabayañā’ti yamhi dhātvantassa yattaṃ, kayyati, kayyanti, kayyare, kayyate, kayyante.</p>\r\n<p class="bodytext">kārite-kāreti, kārayati, kārenti, kārayanti, kārāpeti, kārāpayati, kārāpenti, kārāpayanti.</p>\r\n<p class="bodytext"><a name="para681"></a><span class="paranum">681</span>. <span class="bld">karassa sossa kuṃ</span> <span class="note">[ka. 511; rū. 521; nī. 1124]</span>.</p>\r\n<p class="bodytext">o-kārasahitassa karassa kuṃ hoti mi, mesu paresu.</p>\r\n<p class="bodytext">kummi, kumma, "bhattu apacitiṃ kummi <span class="note">[jā. 1.3.126]</span>, dhammassāpacitiṃ kummī"ti <span class="note">[jā. 2.22.1752]</span> pāḷī.</p>\r\n<p class="bodytext"><a name="para682"></a><span class="paranum">682</span>. <span class="bld">karotissa kho</span> <span class="note">[ka. 594; rū. 582; nī. 1198]</span>.</p>\r\n<p class="bodytext">pādito parassa karadhātussa kvaci kho hoti.</p>\r\n<p class="bodytext">saṅkharoti, saṅkharonti, abhisaṅkharoti, abhisaṅkharonti.</p>\r\n<p class="bodytext">kamme-saṅkharīyati, saṅkharīyanti.</p>\r\n<p class="bodytext">kārite-saṅkhāreti, saṅkhārayati.</p>\r\n<p class="bodytext">ṇāpimhi na vuddhi, saṅkharāpeti, saṅkharāpayati.</p>\r\n<p class="bodytext">kamme-saṅkhārīyati, saṅkharāpīyati.</p>\r\n<p class="bodytext"><a name="para683"></a><span class="paranum">683</span>. <span class="bld">karassa sossa kubbakurukayirā</span> <span class="note">[ka. 511-2; rū. 521-2; nī. 1077-8-9-10; caṃ. 5.2.103; pā. 6.4.110]</span>.</p>\r\n<p class="bodytext">o-kārasahitassa karassa kubba, kuru, kayirā honti vā nta, māna, tyādīsu, mahāvuttinā vikappena kussa kruttaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-681-682-683-');
INSERT INTO pages VALUES(54863,'annya_sadda_10',456,replace(replace('\r\n<p class="bodytext">kubbati <a name="M0.0456"></a> kubbanti, krubbati, krubbanti.</p>\r\n<p class="bodytext">parachakke-kubbate, krubbate, kubbante, krubbante, kurute, kayirati, kayiranti, kayirasi, kayiratha, kayirāmi, kayirāma, kayirate, kayirante.</p>\r\n<p class="bodytext">karotu, saṅkharotu, kubbatu, krubbatu, kurutu, agghaṃ kurutu no bhavaṃ <span class="note">[dī. ni. 2.318]</span>, kayiratu.</p>\r\n<p class="bodytext">kareyya, saṅkhareyya, kubbeyya, krubbeyya, kayireyya.</p>\r\n<p class="bodytext"><a name="para684"></a><span class="paranum">684</span>. <span class="bld">ṭā</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">kayirādesato parassa eyyavibhattissa ṭānubandho ā hoti vā.</p>\r\n<p class="bodytext">so puññaṃ kayirā, puññaṃ ce puriso kayirā <span class="note">[dha. pa. 118]</span>, kayirā naṃ punappunaṃ <span class="note">[dha. pa. 118]</span>.</p>\r\n<p class="bodytext"><a name="para685"></a><span class="paranum">685</span>. <span class="bld">kayireyyasseyyumādīnaṃ</span> <span class="note">[ka. 517; rū. 488; nī. 1083-4-5-6-7]</span>.</p>\r\n<p class="bodytext">kayirādesato parassa eyyuṃādīnaṃ eyyasaddassa lopo hoti.</p>\r\n<p class="bodytext">kayiruṃ, kayireyyuṃ, kayirāsi, kayireyyāsi, kayirātha, kayireyyātha, kayirāmi, kayireyyāmi, kayirāma, kayireyyāma.</p>\r\n<p class="bodytext"><a name="para686"></a><span class="paranum">686</span>. <span class="bld">ethassā</span> <span class="note">[ka. 517; rū. 488; nī. 1082]</span>.</p>\r\n<p class="bodytext">kayirādesato parassa ethassa e-kārassa ā hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-684-685-686-');
INSERT INTO pages VALUES(54864,'annya_sadda_10',457,replace(replace('\r\n<p class="bodytext">so <a name="M0.0457"></a> kayirātha, dīpaṃ kayirātha paṇḍito <span class="note">[dha. pa. 28]</span>, kayirā ce kayirāthenaṃ <span class="note">[dha. pa. 313; saṃ. ni. 1.89]</span>.</p>\r\n<p class="bodytext">īādimhi-akari, kari, saṅkhari, abhisaṅkhari, akubbi, kubbi, akrubbi, krubbi, akayiri, kayiri, akaruṃ, karuṃ, saṅkharuṃ, abhi, saṅkharuṃ, akariṃsu, kariṃsu, saṅkhariṃsu, abhisaṅkhariṃsu, akubbiṃsu, kubbiṃsu, akrubbiṃsu, krubbiṃsu, akayiriṃsu, kayiriṃsu, akayiruṃ, kayiruṃ.</p>\r\n<p class="bodytext"><a name="para687"></a><span class="paranum">687</span>. <span class="bld">kā īādīsu</span> <span class="note">[ka. 491; rū. 523; nī. 983]</span>.</p>\r\n<p class="bodytext">īādīsu saokārassa karassa kā hoti vā.</p>\r\n<p class="bodytext"><a name="para688"></a><span class="paranum">688</span>. <span class="bld">dīghā īssa</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">ā, e, ūdīghehi parassa īvacanassa si hoti vā.</p>\r\n<p class="bodytext">aṭṭhāsi, adāsi, vadesi, vajjesi, bhāvesi, kāresianubhosi, ahosi iccādi.</p>\r\n<p class="bodytext">so akāsi, te akaṃsu, gāthāyaṃ "akaṃsu satthuvacana"nti <span class="note">[jā. 2.22.564]</span> pāḷi. akāsuṃ, tvaṃ akāsi. mā tumhe evarūpaṃ akattha <span class="note">[gavesitabbaṃ]</span>, mākattha pāpakaṃ kammaṃ, āvī vā yadi vā raho <span class="note">[udā. 44]</span>. akāsittha, ahaṃ akāsiṃ, mayaṃ akāsimhā, akamhā. bhogesu vijjamānesu, dīpaṃ nākamha attano <span class="note">[jā. 1.4.53]</span>. so akā. "tato ekasataṃ khatye, anuyante bhavaṃ akā"ti <span class="note">[jā. 2.20.94]</span> pāḷi. akāsittha vā, ahaṃ akaṃ, akaraṃ vā. "tassāhaṃ vacanaṃ nākaṃ, pitu vuddhassa bhāsita"nti <span class="note">[jā. 2.17.134]</span> pāḷi.</p>\r\n<p class="bodytext">kārite-so kāresi, kārayi, kārāpesi, kārāpayi, te kāresuṃ, kārayuṃ, kārāpesuṃ, kārāpayuṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-687-688-');
INSERT INTO pages VALUES(54865,'annya_sadda_10',458,replace(replace('\r\n<p class="bodytext">karissati <a name="M0.0458"></a> saṅkharissati, kubbissati, krubbissati, kayirissati iccādi.</p>\r\n<p class="bodytext">"harassa cāhaṅa sse’ti ssena saha karassa rakārassa āhaṅa hoti, kāhati, kāhanti, kathaṃ kāhanti dārakā <span class="note">[jā. 2.22.1849]</span>.</p>\r\n<p class="bodytext">iñāgame-kāhiti, kāhinti iccādi. kāhasi, kāhatha. kāhāmi kusalaṃ bahuṃ <span class="note">[jā. 1.4.56]</span>, kāhāma puññasañcayaṃ <span class="note">[apa. thera 1.1.401]</span>.</p>\r\n<p class="bodytext">‘āīādīsū’ti sutte yogavibhāgena ssatyādīsupi kā hoti, saṃyoge rassattaṃ, kassati, kassanti, kassasi, kassatha, kassāmi, kassāma, kassaṃ purisakāriyaṃ <span class="note">[jā. 2.22.131]</span>.</p>\r\n<p class="bodytext">ssādimhi-akāhā, akarissā iccādi.</p>\r\n<p class="bodytext">tanu-vitthāre, tanoti.</p>\r\n<p class="bodytext">parassaralopo-tanonti.</p>\r\n<p class="bodytext"><a name="para689"></a><span class="paranum">689</span>. <span class="bld">ovikaraṇassu parachakke</span> <span class="note">[ka. 511; rū. 521; nī. 1024]</span>.</p>\r\n<p class="bodytext">parachakke pare ovikaraṇassa u hoti.</p>\r\n<p class="bodytext">tanute, tanunte.</p>\r\n<p class="bodytext">‘yavā sare’ti ussa vatte-tanvante.</p>\r\n<p class="bodytext"><a name="para690"></a><span class="paranum">690</span>. <span class="bld">pubbachakke vā kvaci</span> <span class="note">[ka. 511; rū. 521; nī. 1024]</span>.</p>\r\n<p class="bodytext">pubbachakke ovikaraṇassa kvaci u hoti vā.</p>\r\n<p class="bodytext">tanuti, kurutu.</p>\r\n<p class="bodytext">kvacīti kiṃ? karoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-689-690-');
INSERT INTO pages VALUES(54866,'annya_sadda_10',459,replace(replace('\r\n<p class="bodytext">vāti <a name="M0.0459"></a> kiṃ? tanoti.</p>\r\n<p class="bodytext">kamme-tanīyati, taññati.</p>\r\n<p class="bodytext"><a name="para691"></a><span class="paranum">691</span>. <span class="bld">tanassā vā</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">tanadhātussa na-kārassa ā hoti vā kyamhi.</p>\r\n<p class="bodytext">tāyati, tāyanti, patāyati, patāyanti. "ito’dāni patāyanti, sūciyo balisāni cā"ti <span class="note">[jā. 1.6.84]</span> pāḷi. tāyate, tāyante.</p>\r\n<p class="bodytext">saka-sattiyaṃ, sakkoti, sakkonti, sakkosi, sakkotha, sakkomi, sakkoma.</p>\r\n<p class="centered">tanādigaṇo niṭṭhito.</p>\r\n<p class="subhead">curādigaṇa</p>\r\n<p class="bodytext">atha curādigaṇo vuccate.</p>\r\n<p class="bodytext"><span class="bld">āpa, kamu, gaṇa, ghaṭa, cinta, ceta, cura, dhara, pāla, pūja, manta, māna, vida</span>.</p>\r\n<p class="bodytext"><a name="para692"></a><span class="paranum">692</span>. <span class="bld">curādīhiṇi</span> <span class="note">[ka. 452; rū. 525; nī. 933; caṃ. 1.1.45; pā. 3.1.25; ‘curādito ṇi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">curādīhi kriyatthehi paraṃ sakatthe ṇipaccayo hoti.</p>\r\n<p class="bodytext">coreti, corayati.</p>\r\n<p class="bodytext">vipubbo āpa-byāpane, byāpeti, byāpayati.</p>\r\n<p class="bodytext">kamu-icchāyaṃ, kāmeti, kāmayati, kāmenti, kāmayanti, nikāmeti, nikāmayati, nikāmenti, nikāmayanti.</p>\r\n<p class="bodytext">kamme-kāmīyati, kāmīyanti.</p>\r\n<p class="bodytext">kārite ṇāpi eva, kāmāpeti, kāmāpayati.</p>\r\n','\r',char(13)),'\n',char(10)),'-691-692-');
INSERT INTO pages VALUES(54867,'annya_sadda_10',460,replace(replace('\r\n<p class="bodytext">kamme-kāmāpīyati<a name="M0.0460"></a>, kāmāpayīyati.</p>\r\n<p class="bodytext">gaṇa-saṅkhyāne, gaṇa, ghaṭānaṃ tudādittā na vuddhi, gaṇeti, gaṇayati.</p>\r\n<p class="bodytext">ghaṭa-cetāyaṃ, ghaṭeti, ghaṭayati.</p>\r\n<p class="bodytext">cinta-cintāyaṃ, garupantattā na vuddhi, cinteti, cintayati.</p>\r\n<p class="bodytext">kamme-cintīyati, cintīyanti.</p>\r\n<p class="bodytext">kārite-cintāpeti, cintāpayati.</p>\r\n<p class="bodytext">kamme-cintāpīyati, cintāpayīyati.</p>\r\n<p class="bodytext">īādimhi-cintesi, cintayi, cinteyuṃ, cintayuṃ, cintesi, cintayi, cintayittha, cintesiṃ, cintayiṃ, cintesimhā, cintayimhā.</p>\r\n<p class="bodytext">ceta-cetāyaṃ, ceteti, cetayati, cetenti, cetayanti.</p>\r\n<p class="bodytext">cura-theyye, coreti, corayati, corenti, corayanti.</p>\r\n<p class="bodytext">dhara-dhāraṇe, dhāreti, dhārayati, dhārenti, dhārayanti.</p>\r\n<p class="bodytext">pāla-pālane, pāleti, pālayati, pālenti, pālayanti.</p>\r\n<p class="bodytext">pūja-pūjāyaṃ, garupantattā na vuddhi, pūjeti, pūjayati, pūjenti, pūjayanti.</p>\r\n<p class="bodytext">manta-guttabhāsane, manteti, mantayati, mantenti, mantayanti.</p>\r\n<p class="bodytext">āpubbo kathane āmantane ca. āmantayāmi vo bhikkhave <span class="note">[dī. ni. 2.218]</span>, bhagavā bhikkhū āmantesi <span class="note">[dī. ni. 2.208]</span>.</p>\r\n<p class="bodytext">nipubbo-nimantane, nimanteti, nimantayati.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54868,'annya_sadda_10',461,replace(replace('\r\n<p class="bodytext">īādimhi-mantesi<a name="M0.0461"></a>, mantayi, āmantesi, āmantayi, nimantesi, nimantayi, mantesuṃ, mantayuṃ, mantayiṃsu rahogatā <span class="note">[jā. 2.22.1918]</span>, mantessati, āmantessati, nimantessati, mantayissati, āmantayissati, nimantayissati iccādi.</p>\r\n<p class="bodytext">māna-pūjāyaṃ, māneti, mānayati, mānenti, mānayanti iccādi.</p>\r\n<p class="bodytext">vida-anubhavane, vedeti, vedayati, paṭisaṃvedeti, paṭisaṃvedayati.</p>\r\n<p class="bodytext">paṭi, ni, papubbo ācikkhane, paṭivedeti, paṭivedayati, nivedeti, nivedayati, pavedeti, pavedayati iccādi.</p>\r\n<p class="centered">curādigaṇo niṭṭhito.</p>\r\n<p class="centered">vikaraṇapaccayarāsi niṭṭhito.</p>\r\n<p class="subhead">sāmañña kha, cha, sarāsi</p>\r\n<p class="bodytext">atha dhātupaccayā vuccante.</p>\r\n<p class="bodytext">kriyāvācībhāvena dhāturūpā paccayā <span class="bld">dhātupaccayā,</span> kriyatthapaccayāti vuttaṃ hoti, tasmā tehipi sabbesaṃ tyādi, tabbādivibhatti, paccayānaṃ sambhavo.</p>\r\n<p class="bodytext"><span class="bld">tija, māna, kita, gupa, badha</span>.</p>\r\n<p class="bodytext"><a name="para693"></a><span class="paranum">693</span>. <span class="bld">tijamānehi khasā khamāvīmaṃsāsu</span> <span class="note">[ka. 433; rū. 528; nī. 906-9; caṃ. 1.1.17, 28; pā. 3.1.5]</span>.</p>\r\n<p class="bodytext">khamāyaṃ vīmaṃsāyañca pavattehi tija, mānadhātūhi paraṃ kamena kha, sapaccayā honti.</p>\r\n<p class="bodytext">tija-khamāyaṃ, iminā khapaccayo.</p>\r\n<p class="bodytext"><a name="para694"></a><span class="paranum">694</span>. <span class="bld">khachasānamekassaraṃ dve</span> <span class="note">[ka. 458; rū. 461; nī. 939; caṃ. 5.1.1; pā. 6.1.1, 9; ‘.mekasarodi.’ (bahūsu)]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-693-694-');
INSERT INTO pages VALUES(54869,'annya_sadda_10',462,replace(replace('\r\n<p class="bodytext">kha<a name="M0.0462"></a>, cha, sapaccayantānaṃ dhāturūpānaṃ paṭhamaṃ saddarūpaṃ ekassaraṃ dverūpaṃ hotīti ‘tija, tija’iti dvirūpe kate ‘loponādibyañjanassā’ti anādibyañjanabhūtassa ja-kārassa lopo, ‘pararūpamayakāre byañjane’ti dhātvantajakārassa pararūpattaṃ. ‘catutthadutiyesvesaṃ tatiyapaṭhamā’ti saṃyogādissa khassa kattaṃ, ‘titikkha’iti dhātupaccayantarūpaṃ, tato tyādyuppatti.</p>\r\n<p class="bodytext">titikkhati, titikkhanti.</p>\r\n<p class="bodytext">kamme-titikkhīyati.</p>\r\n<p class="bodytext">kārite-titikkheti, titikkhayati, titikkhāpeti, titikkhāpayati.</p>\r\n<p class="bodytext">kamme-titikkhāpīyati, titikkhāpīyanti.</p>\r\n<p class="bodytext">titikkhatu, titikkhantu, titikkheyya, titikkheyyuṃ iccādi.</p>\r\n<p class="bodytext">khamāyanti kiṃ? tija-nisāne, tejeti, tejenti.</p>\r\n<p class="bodytext">kārite-tejeti. "samuttejeti sampahaṃsetī"ti <span class="note">[ma. ni. 3.276]</span> pāḷi.</p>\r\n<p class="bodytext">māna-vīmaṃsāyaṃ, tato sapaccayo. ‘khachasānamekassaraṃ dve’ti ‘māna, māna’iti dvirūpe kate –</p>\r\n<p class="bodytext"><a name="para695"></a><span class="paranum">695</span>. <span class="bld">mānassa vī parassa ca maṃ</span> <span class="note">[ka. 463-7; rū. 532-3; nī. 944]</span>.</p>\r\n<p class="bodytext">dvitte pubbassa mānassa vī hoti, parassa ca sabbassa mānassa maṃ hoti.</p>\r\n<p class="bodytext">vīmaṃsati, vīmaṃsanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-695-');
INSERT INTO pages VALUES(54870,'annya_sadda_10',463,replace(replace('\r\n<p class="bodytext">kamme-vīmaṃsīyati<a name="M0.0463"></a>, vīmaṃsīyanti.</p>\r\n<p class="bodytext">kārite-vīmaṃseti, vīmaṃsayati, vīmaṃsāpeti, vīmaṃsāpayati.</p>\r\n<p class="bodytext">kamme-vīmaṃsāpīyati, vīmaṃsāpīyanti.</p>\r\n<p class="bodytext">vīmaṃsāyanti kiṃ? māna-pūjāyaṃ, māneti, sammāneti, abhimāneti, pūjetīti attho.</p>\r\n<p class="bodytext">kita-rogāpanayane saṃsaye ca.</p>\r\n<p class="bodytext"><a name="para696"></a><span class="paranum">696</span>. <span class="bld">kitā tikicchāsaṃsayesu cho</span> <span class="note">[ka. 433; rū. 528; nī. 906-9; caṃ. 1.1.18; pā. 3.1.5 kā]</span>.</p>\r\n<p class="bodytext">tikicchāyaṃ saṃsaye ca pavattakitadhātuto paraṃ cho hoti.</p>\r\n<p class="bodytext">‘kita, kita’ iti dvirūpe kate –</p>\r\n<p class="bodytext"><a name="para697"></a><span class="paranum">697</span>. <span class="bld">kitassāsaṃsayeti vā</span> <span class="note">[ka. 463; rū. 532; nī. 944]</span>.</p>\r\n<p class="bodytext">saṃsayamhā aññasmiṃ tikicchatthe pavattassa kitadhātussa dvitte pubbassa kitassati hoti vā. ‘pararūpamayakāre byañjane’ti pararūpattaṃ, saṃyogādissa cakārattaṃ.</p>\r\n<p class="bodytext">tikicchati, tikicchanti. devāpi naṃ tikicchanti, mātāpettibharaṃ naraṃ <span class="note">[jā. 2.22.408]</span>.</p>\r\n<p class="bodytext">kamme-tikicchīyati, tikicchīyanti.</p>\r\n<p class="bodytext">kārite-tikiccheti, tikicchayati, tikicchāpeti, tikicchāpayati.</p>\r\n<p class="bodytext">vāsaddena tikārābhāve ‘kavaggahānaṃ cavaggajā’ti dvitte pubbassa cavaggo, cikicchati, cikicchanti, cikicchīyati, cikicchīyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-696-697-');
INSERT INTO pages VALUES(54871,'annya_sadda_10',464,replace(replace('\r\n<p class="bodytext">asaṃsayeti <a name="M0.0464"></a> kiṃ? vicikicchati, vicikicchanti.</p>\r\n<p class="bodytext">tikicchattha, saṃsayatthato aññasmiṃ atthe –</p>\r\n<p class="bodytext">kita-ñāṇe nivāse ca, ketati, saṃketati, niketati.</p>\r\n<p class="bodytext">gupa-nindāyaṃ.</p>\r\n<p class="bodytext"><a name="para698"></a><span class="paranum">698</span>. <span class="bld">nindāyaṃ gupabadhā bassa bho ca</span> <span class="note">[ka. 433; rū. 528; caṃ. 1.1.19, 20; pā. 3.1.5, 6 kā]</span>.</p>\r\n<p class="bodytext">nindāyaṃ pavattehi gupa, badhehi paraṃ chapaccayo hoti, bassa ca bho hoti. dvirūpe kate anādibyañjanalopo.</p>\r\n<p class="bodytext"><a name="para699"></a><span class="paranum">699</span>. <span class="bld">gupissi</span> <span class="note">[ka. 465; rū. 463; nī. 946; ‘gupissussa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">gupissa dvitte pubbassa u-kārassa i hoti. gassa cavaggattaṃ, dhātvantassa pararūpattaṃ, saṃyogādissa paṭhamattaṃ.</p>\r\n<p class="bodytext">jigucchati, jigucchanti.</p>\r\n<p class="bodytext">kamme-jigucchīyati, jigucchīyanti.</p>\r\n<p class="bodytext">kārite-jiguccheti, jigucchayati, jigucchāpeti, jigucchāpayati. jigucchatu, jigucchantu iccādi.</p>\r\n<p class="bodytext">nindāyanti kiṃ? gupa-saṃvaraṇe, gopeti, gopayati.</p>\r\n<p class="bodytext">badha-nindāyaṃ, dvirūpādimhi kate –</p>\r\n<p class="bodytext"><a name="para700"></a><span class="paranum">700</span>. <span class="bld">khachasessi</span> <span class="note">[ka. 465; rū. 463; nī. 946; ‘khachasesvassi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">dvitte pubbassa assa i hoti kha, cha, sesūti assa ittaṃ, parabakārassa ca bhattaṃ, dhātvantassa pararūpādi.</p>\r\n<p class="bodytext">bibhacchati, virūpo hotīti attho. bibhacchanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-698-699-700-');
INSERT INTO pages VALUES(54872,'annya_sadda_10',465,replace(replace('\r\n<p class="bodytext">nindāyanti <a name="M0.0465"></a> kiṃ? badha-bandhana, hiṃsāsu, bādheti, bādhayati. vātaṃ jālena bādhesi <span class="note">[jā. 1.12.8]</span>.</p>\r\n<p class="bodytext">kamme-bādhīyati, bādhīyanti, bajjhati, bajjhanti.</p>\r\n<p class="centered">iti sāmañña kha, cha, sarāsi.</p>\r\n<p class="subhead">tumicchatthe khachasarāsi</p>\r\n<p class="bodytext"><a name="para701"></a><span class="paranum">701</span>. <span class="bld">tuṃsmā lopo cicchāyaṃ te</span> <span class="note">[ka. 434; rū. 534; nī. 910; caṃ. 1.1.22; pā. 3.1.7]</span>.</p>\r\n<p class="bodytext">tumantehi icchatthe te kha, cha, sā honti, tuṃpaccayassa ca lopo hoti. idañca suttaṃ tumicchatthasambhave sati sabbadhātupadehipi kha, cha, sānaṃ pavattidīpanatthaṃ. tena tumicchatthe sa, chapaccaye katvā ‘ū byañjanassā’ti sa, chānaṃ ādimhi īāgamaṃ katvā "aputtaṃ puttamiva ācarituṃ icchati puttīyīsati, pabbato viya attānaṃ ācarituṃ icchati pabbatāyīsati, dātuṃ icchati dicchati" iccādīni sijjhanti.</p>\r\n<p class="bodytext"><span class="bld">bhuja, ghasa, hana, ji, hara, pā, su</span>.</p>\r\n<p class="bodytext">bhuñjituṃ icchatīti atthe-bhujato khapaccayo, tuṃpaccayalopo, dvittaṃ, pubbassa anādilopo, pararūpatte saṃyogādissa paṭhamattaṃ, pubbassa bhassa battaṃ, bubhukkhati, bubhukkhanti, bubhukkhīyati, bubhukkhīyanti, bubhukkheti, bubhukkhayati, bubhukkhāpeti, bubhukkhāpayati, bubhukkhāpīyati, bubhukkhāpīyanti, bubhukkhatu, bubhukkhantu, bubhukkheyya, bubhukkheyyuṃ, bubhukkhi, bubhukkhiṃsu, bubhukkhissati, bubhukkhissanti, bubhukkhissā, bubhukkhissaṃsu.</p>\r\n<p class="bodytext">ghasa-adane, ghasituṃ icchatīti atthe – chapaccayo, dvittādi, pubbassa ghassa gattaṃ, gassa jattaṃ, assa ittaṃ, jighacchati, jighacchanti, jighacchīyati, jighacchīyanti, jighaccheti, jighacchāpeti iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-701-');
INSERT INTO pages VALUES(54873,'annya_sadda_10',466,replace(replace('\r\n<p class="bodytext">hana-hiṃ <a name="M0.0466"></a> sāyaṃ, hantuṃ icchatīti atthe – chapaccayo, dvittādi, ‘kavaggahānaṃ cavaggajā’ti pubbassa hassa jo, assa ittaṃ.</p>\r\n<p class="bodytext"><a name="para702"></a><span class="paranum">702</span>. <span class="bld">parassa ghaṃ se</span>.</p>\r\n<p class="bodytext">dvitte parassa hanassa ghaṃ hoti se pare.</p>\r\n<p class="bodytext">jighaṃsati, jighaṃsanti.</p>\r\n<p class="bodytext">ji-jaye, jetuṃ icchatīti atthe – sapaccayo, dvittaṃ.</p>\r\n<p class="bodytext"><a name="para703"></a><span class="paranum">703</span>. <span class="bld">jiharānaṃ gī</span> <span class="note">[ka. 462, 474; rū. 467, 535; nī. 943-954]</span>.</p>\r\n<p class="bodytext">ji, harānaṃ dvitte parassa jissa harassa ca gī hoti se pare.</p>\r\n<p class="bodytext">jigīsati, jigīsanti, vijigīsati, vijigīsanti.</p>\r\n<p class="bodytext">hara-haraṇe, dvittādi, parassa gī, pubbassa hassa jo, assa ittaṃ, jigīsati, harituṃ icchatīti attho, jigīsanti.</p>\r\n<p class="bodytext">pā-pāne, pivituṃ icchatīti atthe – sapaccayo, dvittaṃ, ‘rasso pubbassā’ti rasso, assa ittaṃ, pipāsati, pipāsanti, pipāsīyati, pipāsīyanti.</p>\r\n<p class="bodytext">su-savane, sotuṃ icchatīti atthe – dvitte parassa dvittaṃ, sussusati <span class="note">[sussūsati (bahūsu)]</span>, sussusanti, sussusīyati, sussusīyanti, sussuseti, sussusayati, sussusāpeti, sussusāpayati, sussusāpīyati, sussusāpīyanti, sussusatu, sussusantu iccādi.</p>\r\n<p class="bodytext">titikkhituṃ icchatīti atthe – titikkhato sapaccayo, sapaccayaparattā puna dvittappasaṅge –</p>\r\n<p class="bodytext"><a name="para704"></a><span class="paranum">704</span>. <span class="bld">na puna</span> <span class="note">[caṃ. 5.1.6]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-702-703-704-');
INSERT INTO pages VALUES(54874,'annya_sadda_10',467,replace(replace('\r\n<p class="bodytext">sakiṃ <a name="M0.0467"></a> dvitte kate puna dvittaṃ na āpajjatīti puna dvittābhāvo, ‘ū byañjanassā’ti ū āgamo.</p>\r\n<p class="bodytext">titikkhisati, titikkhisanti iccādi. evaṃ tikicchituṃ icchatīti tikicchisati, tikicchisanti, cikicchisati, cikicchisanti iccādi.</p>\r\n<p class="centered">iti tumicchatthe kha, cha, sa rāsi.</p>\r\n<p class="subhead">nāmadhāturāsi</p>\r\n<p class="bodytext">puttaṃ icchatīti atthe –</p>\r\n<p class="bodytext"><a name="para705"></a><span class="paranum">705</span>. <span class="bld">īyo kammā</span> <span class="note">[ka. 437; rū. 538; nī. 912; caṃ. 1.1.23, 24; pā. 3.1.8, 9]</span>.</p>\r\n<p class="bodytext">kammatthā nāmapadamhā icchatthe īyo hotīti kammabhūtā puttasaddato icchāyaṃ īyo, ‘puttīya’iti dhātupaccayantarūpaṃ, tato tyādyuppatti.</p>\r\n<p class="bodytext">puttīyati, puttīyanti, puttīyeti, puttīyayati, puttīyāpeti, puttīyāpayati, puttīyāpīyati, puttīyāpīyanti. evaṃ cīvarīyati, cīvarīyanti, pattīyati, pattīyanti, parikkhārīyati, parikkhārīyanti iccādi.</p>\r\n<p class="bodytext">aputtaṃ sissaṃ puttamiva ācaratīti atthe –</p>\r\n<p class="bodytext"><a name="para706"></a><span class="paranum">706</span>. <span class="bld">upamāṇācāre</span> <span class="note">[ka. 436; rū. 537; nī. 912; caṃ. 1.1.25; pā. 3.1.10]</span>.</p>\r\n<p class="bodytext">upamīyati upametabbo attho etenāti upamānaṃ, upamānabhūtā kammapadato ācāratthe īyo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-705-706-');
INSERT INTO pages VALUES(54875,'annya_sadda_10',468,replace(replace('\r\n<p class="bodytext">puttīyati<a name="M0.0468"></a>, puttīyanti sissaṃ.</p>\r\n<p class="bodytext">kamme-aputtopi putto viya ācarīyati puttīyīyati, puttīyīyanti, puttīyeti, puttīyayati, puttīyāpeti, puttīyāpayati, puttīyāpīyati, puttīyāpīyanti. evaṃ sissīyati, sissīyanti.</p>\r\n<p class="bodytext">kuṭiyaṃ viya pāsāde ācaratīti atthe –</p>\r\n<p class="bodytext"><a name="para707"></a><span class="paranum">707</span>. <span class="bld">ādhārā</span> <span class="note">[ka. 436; rū. 537; nī. 912; caṃ. 1.1.26; pā. 3.1.10]</span>.</p>\r\n<p class="bodytext">upamānabhūtā ādhārabhūtā ca nāmamhā ācāratthe īyo hoti.</p>\r\n<p class="bodytext">kuṭīyati, kuṭīyanti pāsāde, nadiyaṃ viya samudde ācarati nadīyati, nadīyanti iccādi.</p>\r\n<p class="bodytext">araññe viya nagare ācarati araññīyati, araññīyanti nagare. evaṃ gehīyati vihāre.</p>\r\n<p class="bodytext">lokadhammehi akampanīyaṭṭhena pabbato viya attānaṃ ācaratīti atthe –</p>\r\n<p class="bodytext"><a name="para708"></a><span class="paranum">708</span>. <span class="bld">kattutāyo</span> <span class="note">[ka. 435; rū. 536; nī. 911; caṃ. 1.1.27; pā. 3.1.11]</span>.</p>\r\n<p class="bodytext">upamānabhūtā kattubhūtā ca nāmamhā ācāratthe āyo hotīti pabbatasaddato āyo. tato tyādyuppatti.</p>\r\n<p class="bodytext">pabbatāyati saṅgho, pabbatāyanti, cicciṭo viya attānaṃ ācarati cicciṭāyati, saddo. evaṃ paṭapaṭāyati, kaṭakaṭāyati, dhūmadhūmāyati, dhūpāyati, sandhūpāyati.</p>\r\n','\r',char(13)),'\n',char(10)),'-707-708-');
INSERT INTO pages VALUES(54876,'annya_sadda_10',469,replace(replace('\r\n<p class="bodytext">abhusampi <a name="M0.0469"></a> bhusaṃ bhavatīti atthe –</p>\r\n<p class="bodytext"><a name="para709"></a><span class="paranum">709</span>. <span class="bld">jhatthe</span> <span class="note">[ka. 435; rū. 536; nī. 911; caṃ. 1.1.30; pā. 3.1.12, 13]</span>.</p>\r\n<p class="bodytext">cīpaccayassa attho abbhūtatabbhāvo <span class="bld">jhattho</span> nāma. kattuto jhatthe āyo hoti.</p>\r\n<p class="bodytext">bhusāyati, bhusāyanti, apaṭopi paṭo bhavati paṭāyati, paṭāyanti, alohitampi lohitaṃ bhavati lohitāyati. evaṃ nīlāyati, kamalāyati, candāyati, candanāyati, kañcanāyati, vajirāyati.</p>\r\n<p class="bodytext">kattutotveva? bhusaṃ karoti.</p>\r\n<p class="bodytext">saddaṃ karotīti atthe –</p>\r\n<p class="bodytext"><a name="para710"></a><span class="paranum">710</span>. <span class="bld">saddādīhi karoti</span> <span class="note">[ka. 435; rū. 536; nī. 911; caṃ. 1.1.36; pā. 3.1.17, 18; ‘saddādīni’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">saddādīhi dutiyantehi nāmehi karotyatthe āyo hoti.</p>\r\n<p class="bodytext">saddāyati, saddāyanti, veraṃ karoti verāyati, verāyanti, kalahaṃ karoti kalahāyati, kalahāyanti, mettaṃ karoti mettāyati, mettāyanti, karuṇaṃ karoti karuṇāyati, karuṇāyanti, muditaṃ karoti muditāyati, muditāyanti, upekkhaṃ karoti upekkhāyati, upekkhāyanti, kukkuccaṃ karoti kukkuccāyati, kukkuccāyanti, piyaṃ karoti piyāyati, piyāyanti, paccayaṃ saddahanaṃ karoti pattiyāyati, pattiyāyanti, taṇhaṃ karoti taṇhāyati, taṇhāyanti, taṇhīyati, taṇhīyanti vā, karotyatthe īyo. mama idanti karoti mamāyati, mamāyanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-709-710-');
INSERT INTO pages VALUES(54877,'annya_sadda_10',470,replace(replace('\r\n<p class="bodytext">namo <a name="M0.0470"></a> karotīti atthe –</p>\r\n<p class="bodytext"><a name="para711"></a><span class="paranum">711</span>. <span class="bld">namotvasso</span> <span class="note">[caṃ. 1.1.37; pā. 3.1.19]</span>.</p>\r\n<p class="bodytext">namosaddato karotyatthe asso hoti.</p>\r\n<p class="bodytext">namassati, namassanti.</p>\r\n<p class="bodytext">samānaṃ sadisaṃ karotīti atthe –</p>\r\n<p class="bodytext"><a name="para712"></a><span class="paranum">712</span>. <span class="bld">dhātvatthe nāmasmi</span> <span class="note">[pā. 3.1.21, 25]</span>.</p>\r\n<p class="bodytext">dhātvattho vuccati yā kāci kriyā. nāmasmā dhātvatthe i hoti. ‘yuvaṇṇānameo paccaye’ti issa ettaṃ.</p>\r\n<p class="bodytext">samāneti, samānenti.</p>\r\n<p class="bodytext">‘eonamayāvā sare’ti essa ayādeso. ‘ṇiṇāpyāpīhi vā’ti ettha vāsaddena lapaccayo, samānayati, samānayanti, piṇaṃ karoti piṇeti, piṇayati, kusalaṃ pucchati kusaleti, kusalayati, visuddhaṃ hoti visuddheti, visuddhayati, vīṇāya upagāyati upavīṇeti, upavīṇāyati, paññāya atikkamati atipaññeti, atipaññāyati, vaccaṃ karoti vacceti, vaccayati, muttaṃ karoti mutteti, muttayati, balena pīḷeti baleti, balayati.</p>\r\n<p class="bodytext">assa itte-balīyati, balīyanti. "abalānaṃ balīyantī"ti pāḷi.</p>\r\n<p class="bodytext">saccaṃ karotīti atthe –</p>\r\n<p class="bodytext"><a name="para713"></a><span class="paranum">713</span>. <span class="bld">saccādīhāpi</span> <span class="note">[saṃyuttanikāye; rū. 540; nī. 914; pā. 3.1.25]</span>.</p>\r\n<p class="bodytext">saccādīhi nāmehi dhātvatthe āpi hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-711-712-713-');
INSERT INTO pages VALUES(54878,'annya_sadda_10',471,replace(replace('\r\n<p class="bodytext">saccāpeti<a name="M0.0471"></a>, saccāpenti, atthavibhāgaṃ karoti atthāpeti, atthāpenti, bedasatthaṃ karoti bedāpeti, bedāpenti, sukkhaṃ karoti sukkhāpeti, sukkhāpenti, sukhaṃ karoti sukhāpeti, sukhāpenti, dukkhaṃ karoti dukkhāpeti, dukkhāpenti, uṇhaṃ karoti uṇhāpeti, uṇhāpenti iccādi.</p>\r\n<p class="bodytext">aputtaṃ puttamiva ācarati puttīyati, puttīyituṃ icchatīti atthe ‘tuṃsmā lopo cicchāyaṃ te’ti sapaccayo.</p>\r\n<p class="bodytext"><a name="para714"></a><span class="paranum">714</span>. <span class="bld">yathiṭṭhaṃ syādino</span> <span class="note">[ka. 458; rū. 461; nī. 939; caṃ. 5.1.8; pā. 6.1.3]</span>.</p>\r\n<p class="bodytext">icchīyatīti iṭṭhaṃ, yaṃ yaṃ iṭṭhaṃ yathiṭṭhaṃ. "yamiṭṭha"ntipi pāṭho. syādyantassa yathiṭṭhaṃ ekassaraṃ ādibhūta’maññaṃ vā dverūpaṃ hoti, na tyādissa viya ādibhūtamevāti attho. ‘ū byañjanassā’ti ū āgamo.</p>\r\n<p class="bodytext">ādimhi dvitte-pupputtīyisati.</p>\r\n<p class="bodytext">majjhe dvitte-puttittīyisati.</p>\r\n<p class="bodytext">akamalaṃ kamalaṃ bhavati kamalāyati, kamalāyituṃ icchatīti kakamalāyisati, kamamalāyisati, kamalalāyisati iccādi.</p>\r\n<p class="centered">iti nāmadhāturāsi.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānadīpaniyā</p>\r\n<p class="centered">tyādikaṇḍo nāma ākhyātakaṇḍo</p>\r\n<p class="centered">niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'-714-');
INSERT INTO pages VALUES(54879,'annya_sadda_10',472,replace(replace('\r\n<p class="chapter">7. kitakaṇḍa</p>\r\n<p class="subhead">dhātvantavikārarāsi</p>\r\n<p class="subhead">visaṃyogarūparāsi</p>\r\n<p class="bodytext">atha <a name="M0.0472"></a> dhātupaccayasaṃsiddhaṃ kāla, kāraka, liṅga, saṅkhyā, kriyābhedadīpakaṃ dabbappadhānavācakaṃ kitakapadaṃ dīpiyate.</p>\r\n<p class="bodytext">tattha atītādayo kālabhedo nāma.</p>\r\n<p class="bodytext">kattā ca kammañca karaṇañca sampadānañca apādānañca adhikaraṇañca bhāvo cāti satta sādhanāni kārakabhedo nāma.</p>\r\n<p class="bodytext">itthiliṅgādīni liṅgabhedo nāma.</p>\r\n<p class="bodytext">ekatta, bahuttabhedo saṅkhyābhedo nāma.</p>\r\n<p class="bodytext">tassīlakriyā, taddhammakriyā, tassādhukārakriyā, attamānakriyā, abhikkhaññakriyā, arahakriyā, sakkakriyā, pesanakriyā, atisaggakriyā, pattakālārocanakriyā, avassambhāvīkriyādayo kriyābhedo nāma.</p>\r\n<p class="bodytext">"gamanaṃ bhavati, pacanaṃ jānāti" iccādīsu paccayatthabhūto bhāvo nāma sādhanarūpo hoti. jāti viya saṅkhatadhammassa dhātvatthabhūtāya sādhyakriyāya sādhakattā liṅgattayayutto ca hoti, kriyā, kāro, karaṇanti siddhattā saṅkhyābhedayutto ca hoti-nānādhātvatthānañca kattu, kammānañca kālādīnañca bhedena sarūpabhedasabbhavato, tasmā sopi dabbe eva saṅgayhatīti katvā ‘dabbappadhānavācaka’nti vuttaṃ. sesaṃ sabbaṃ tyādikaṇḍe bhāvasādhanavinicchaye vuttameva.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54880,'annya_sadda_10',473,replace(replace('\r\n<p class="bodytext">‘bahula’nti <a name="M0.0473"></a> ca ‘kriyatthā’ti ca vattante –</p>\r\n<p class="bodytext"><a name="para715"></a><span class="paranum">715</span>. <span class="bld">kattari bhūte kta ktavantu ktāvī</span> <span class="note">[ka. 555; rū. 612; nī. 1142; caṃ. 1.2.66 .pe.. 3.2.102]</span>.</p>\r\n<p class="bodytext">abhavīti bhūto, atīto, bhūte vattabbe kriyatthā kattari kānubandhā ta, tavantu, tāvīpaccayā honti, kānubandhā ‘na te kānubandhanāgamesū’ti sutte visesanatthā.</p>\r\n<p class="bodytext"><a name="para716"></a><span class="paranum">716</span>. <span class="bld">kto bhāvakammesu</span> <span class="note">[ka. 556; rū. 622; nī. 1143; caṃ. 1.2.67 .pe.. 3.2.102; 3.4.70]</span>.</p>\r\n<p class="bodytext">bhūte vattabbe kriyatthā bhāva, kammesu kānubandho tapaccayo hoti.</p>\r\n<p class="bodytext">abhavīti bhūto-puriso, bhūtā-itthī, bhūtaṃ-kulaṃ, kārite dhātuto ṇānubandhānaṃ paṭhamaṃ sampattattā ‘na te kānubandhanāgamesū’ti paṭisedho na pāpuṇāti, ‘yuvaṇṇānameo paccaye’ti ovuddhi, ‘āvāyā ṇānubandhe’ti ossa āvattaṃ, tato tapaccayo.</p>\r\n<p class="bodytext"><a name="para717"></a><span class="paranum">717</span>. <span class="bld">ñī byañjanassa</span> <span class="note">[ka. 605; rū. 547; nī. 1210]</span>.</p>\r\n<p class="bodytext">byañjanādipaccayassa ādimhi ñānubandho īkāro āgacchati.</p>\r\n<p class="bodytext">kattari-abhāvayitthāti bhāvito-puriso, bhāvitāitthī, bhāvitaṃ-kulaṃ.</p>\r\n<p class="bodytext">kamme-anubhūyitthāti anubhūto-bhogo, anubhūtāsampatti, anubhūtaṃ-sukhaṃ.</p>\r\n<p class="bodytext">kārite-bhāvīyitthāti bhāvito-maggo, bhāvitāpaṭipadā, bhāvitaṃ-cittaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-715-716-717-');
INSERT INTO pages VALUES(54881,'annya_sadda_10',474,replace(replace('\r\n<p class="bodytext">tavantu<a name="M0.0474"></a>, tāvīsu-abhavīti bhūtavā-puriso, bhūtavantī, bhūtavatī-itthī, bhūtavaṃ-kulaṃ, guṇavantusamaṃ. bhūtāvī-puriso. bhūtāvinī-itthī, bhūtāvi-cittaṃ, daṇḍī, daṇḍinīsamaṃ. puriso bhogaṃ anubhūto, purisena bhogo anubhūto.</p>\r\n<p class="bodytext">ettha ca kitapaccayānaṃ attho duvidho vāccattho, abhidheyyattho cāti sabbaṃ tyādikaṇḍe vuttanayena veditabbaṃ.</p>\r\n<p class="bodytext">purimesu pana chasu sādhanesu paccayānaṃ abhidheyyattho padantarena ācikkhīyati, bhāvasādhane pana attano dhātunā eva.</p>\r\n<p class="bodytext">tattha ca kattusatti, kammasatti, karaṇasatti, sampadānasatti, apādānasatti, adhikaraṇasattisaṅkhātaṃ vāccatthaṃ ujuṃ vadantā kitapaccayā attano abhidheyyapadena samānaliṅga, vibhatti, saṅkhyāyuttā hutvā vadanti.</p>\r\n<p class="bodytext">taṃ yathā? –</p>\r\n<p class="bodytext">kattari tāva-puriso bhogaṃ anubhūto, purisā bhogaṃ anubhūtā.pe.. purisesu bhogaṃ anubhūtesu, itthī bhogaṃ anubhūtā, itthiyo bhogaṃ anubhūtāyo.pe.. itthīsu bhogaṃ anubhūtāsu, kulaṃ bhogaṃ anubhūtaṃ, kulāni bhogaṃ anabhūtāni.pe.. kulesu bhogaṃ anubhūtesu.</p>\r\n<p class="bodytext">kamme-bhogo purisena anubhūto, bhogā purisena anubhūtā.pe.. bhogesu purisena anubhūtesu, sampatti purisena anubhūtā, sampattiyo purisena anubhūtāyo.pe.. sampattīsu purisena anubhūtāsu, sukhaṃ purisena anubhūtaṃ, sukhāni purisena anubhūtāni.pe.. sukhesu purisena anubhūtesu. esa nayo karaṇādīsupi.</p>\r\n<p class="bodytext">evaṃ kitavācakā attano abhidheyyapadena samānaliṅga, vibhatti, saṅkhyāyuttā hutvā taṃ taṃ sādhanaṃ vadanti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54882,'annya_sadda_10',475,replace(replace('\r\n<p class="bodytext">‘itthiyamaṇatikayakyā <a name="M0.0475"></a> cā’ti itthiyaṃ tipaccayo, anubhavanaṃ, anubhūyate vā anubhūti. "tissassa anubhūti, phussassa anubhūti" iccādikā bahū anubhūtiyopi sijjhanti, tasmā "anubhūti, anubhūtiyo, anubhūtiṃ, anubhūtiyo.pe.. anubhūtīsū"ti yujjati.</p>\r\n<p class="bodytext"><a name="para503"></a><span class="paranum">503</span> 718. <span class="bld">kattari ltuṇakā</span> <span class="note">[ka. 527, 530; rū. 568, 590; nī. 1109, 1114; caṃ. 1.1.139; pā. 3.1.133, 134]</span>.</p>\r\n<p class="bodytext">kattukārake kriyatthā ltu, ṇakā honti, lānubandho tussa kattari nibandhanattho, ‘ltupitādīnamā’ti visesanattho ca.</p>\r\n<p class="bodytext">anubhavatīti anubhūtā, anubhūtāro, satthusamaṃ.</p>\r\n<p class="bodytext">sāmaññavidhānattā arahatthe sattiatthe tassīla, taddhamma, tassādhukāra, attamānesu ca kālattaye ca bhavanti.</p>\r\n<p class="bodytext">arahatthe-brahmaṇo brahmaṇiyā pariggahitā.</p>\r\n<p class="bodytext">sattiatthe-bhagavā anuppannassa maggassa uppādetā.</p>\r\n<p class="bodytext">tassīlādīsu-pasayhapavattā.</p>\r\n<p class="bodytext">attamāne-attānaṃ paṇḍitaṃ maññatīti paṇḍitamānitā.</p>\r\n<p class="bodytext"><a name="para719"></a><span class="paranum">719</span>. <span class="bld">pubbekakattukānaṃ</span> <span class="note">[ka. 564; rū. 640; nīti. 1150-6; caṃ. 1.3.131; pā. 3.4.21]</span>.</p>\r\n<p class="bodytext">yāsaṃ visesana, visesyānaṃ dvinnaṃ pubbā’parakriyānaṃ kattā ekova hoti. tāsu pubbakriyāyaṃ bhāvatthe tuna, tvāna, tvāpaccayā honti. ‘eonamayavā sare’ti īkāre pare ovuddhiyā avattaṃ.</p>\r\n<p class="bodytext">bhogaṃ anubhavituna, anubhutvāna, anubhutvā.</p>\r\n','\r',char(13)),'\n',char(10)),'-503-719-');
INSERT INTO pages VALUES(54883,'annya_sadda_10',476,replace(replace('\r\n<p class="bodytext">ekakattukānanti <a name="M0.0476"></a> kiṃ? devadatto bhuñji, yaññadatto gacchati.</p>\r\n<p class="bodytext">pubbeti kiṃ? pacchā bhuñjati, paṭhamaṃ pacati.</p>\r\n<p class="bodytext">bahulādhikārā samānā’parakriyāsupi nānākattukāsupi tunādayo bhavanti. thakkacca daṇḍo patati, dvāraṃ saṃvaritvā nikkhamati, puriso sīhaṃ disvā bhayaṃ uppajjatīti.</p>\r\n<p class="bodytext">yasmiṃ vākye aparakriyāpadaṃ na dissati. yathā? pabbataṃ atikkamma nadī, atikkamma nadiṃ pabbato, ye te santā vimokkhā atikkamma rūpe arūpāti, tatthapi sattākriyā viññāyateva sabbapadatthānaṃ sattānātivattanato. aparakriyārahite asamānakattuke ca vākye paṭhamantayogassa diṭṭhattā kattaripi tunādīnaṃ sambhavo yutto.</p>\r\n<p class="bodytext"><a name="para720"></a><span class="paranum">720</span>. <span class="bld">paṭisedhālaṃkhalūnaṃ tuna tvāna tvā vā</span> <span class="note">[paṭisedhelaṃkhalūnaṃ tunattvāna ttvā vā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">paṭisedhatthānaṃ alaṃ, khalūnaṃ yoge tunādayo honti vā.</p>\r\n<p class="bodytext">alaṃ bhutvā, khalu bhutvā, alaṃ bhuttena, khalu bhuttena vā.</p>\r\n<p class="bodytext"><a name="para721"></a><span class="paranum">721</span>. <span class="bld">tuṃtāyetave bhāve bhavissatikriyāyaṃ tadatthāyaṃ</span> <span class="note">[ka. 561-2-3; rū. 636, 638-9; nī. 1148-9]</span>.</p>\r\n<p class="bodytext">tassā tassā kriyāya atthabhūtāya bhavissamānakriyāya gamyamānāya bhāvatthe tuṃ, tāye, tavepaccayā bhavanti. suttapadavaḍḍhanena tuyepaccayopi.</p>\r\n','\r',char(13)),'\n',char(10)),'-720-721-');
INSERT INTO pages VALUES(54884,'annya_sadda_10',477,replace(replace('\r\n<p class="bodytext">anubhavituṃ gacchati, anubhavitāye gacchati, anubhavitave gacchati, anubhavituṃ icchati, kāmeti, sakkoti, jānāti. tathā kālo anubhavituṃ, samayo anubhavituṃ, velā anubhavituṃ<a name="M0.0477"></a>. tathā anubhavituṃ mano, anubhavituṃ soko, cakkhu daṭṭhuṃ, sotaṃ sotuṃ, mano viññātuṃ, hattho kātuṃ, pādo gantuṃ, dhanu yujjhituṃ, jaḷo vattuṃ, mando gantuṃ, alaso kattunti.</p>\r\n<p class="bodytext">ettha ca "kālo anubhavitu"ntiādīsu sattāvasena hetukriyā sijjhati, tasmā "anubhavituṃ kālo bhavatī"tiādinā attho veditabbo.</p>\r\n<p class="bodytext">ime panettha tāye, tuyepayogā – āgatāmha imaṃ dhammasamayaṃ, dakkhitāye aparājitasaṅghaṃ <span class="note">[dī. ni. 2.332]</span>. alañhi te jagghitāye, mamaṃ disvāna edisaṃ <span class="note">[jā. 1.5.137]</span>. ko tādisaṃ arahati khāditāye <span class="note">[jā. 1.16.92]</span>, atthi hehiti so maggo, na so sakkā na hotuye <span class="note">[bu. vaṃ. 2.9 ‘hetuye’]</span>, arahasi naṃ yācituye tuvampi, arahasi no jānituye katāni iccādi.</p>\r\n<p class="bodytext"><a name="para722"></a><span class="paranum">722</span>. <span class="bld">bhāvakammesu tabbānīyā</span> <span class="note">[ka. 540; rū. 545; nī. 1125]</span>.</p>\r\n<p class="bodytext">bhāve kammani ca tabba, anīyā honti. suttapadavaḍḍhanena tabya, tāya, teyyapaccayāpi honti.</p>\r\n<p class="bodytext">anubhavitabbo-bhogo, anubhavitabbā-sampatti, anubhavitabbaṃ-sukhaṃ.</p>\r\n<p class="bodytext">bahulādhikārā kattādīsvapi bhavanti, tapantīti tapanīyāpāpadhammā, upaṭṭhātīti upaṭṭhānīyo-sisso. pavuccati etenāti pavacanīyo-upajjhāyo, niyyanti etenāti niyyānīyo, so eva niyyāniko.</p>\r\n<p class="bodytext">sinā-soceyye, sināyanti etenāti sinānīyaṃcuṇṇaṃ, dīyate assāti dānīyo-brāhmaṇo. sammā vattati etthāti sammāvattanīyo-guru.</p>\r\n','\r',char(13)),'\n',char(10)),'-722-');
INSERT INTO pages VALUES(54885,'annya_sadda_10',478,replace(replace('\r\n<p class="bodytext">idha <a name="M0.0478"></a> gāthā vuccati –</p>\r\n<p class="gatha1">arahatthe ca sakkatthe, pattakāle ca pesane.</p>\r\n<p class="gathalast">tabbādayo atisagge, avassādhamiṇesu ca.</p>\r\n<p class="bodytext">tattha "araha sakka visiṭṭhe kattarī"ti vuttiyaṃ vuttaṃ, tasmā bhavatīti bhabbo, bhavituṃ arahatīti attho, majjatīti majjaṃ, madanīyaṃ, majjituṃ sakkotīti attho, evampi yujjati.</p>\r\n<p class="bodytext">pattakāle-kattabbo bhavatā kaṭo, esa kālo kaṭakaraṇassāti dīpeti.</p>\r\n<p class="bodytext">pesane-gantabbo bhavatā gāmo, gacchatu bhavaṃ gāmanti dīpeti.</p>\r\n<p class="bodytext">atisajjanaṃ sambodhanaṃ atisaggo, upadeso ceva vidhi ca. tattha kattabbā’kattabbassa kammassa ācikkhaṇaṃ <span class="bld">upadeso,</span> dānaṃ dātabbaṃ, sīlaṃ rakkhitabbaṃ, pāṇo na hantabbo, adinnaṃ na ādātabbaṃ <span class="note">[dī. ni. 3.85]</span>. kattabbā’kattabbākāradassanaṃ <span class="bld">vidhi,</span> sakkaccaṃ dānaṃ dātabbaṃ, no asakkaccaṃ.</p>\r\n<p class="bodytext">avassake-gamanīyo abhisamparāyo, avassaṃ gantabboti attho.</p>\r\n<p class="bodytext">yaṃ iṇaṃ adentassa daṇḍo āgacchati, idaṃ adhamiṇaṃ nāma, sataṃ me dātabbaṃ bhavatāti.</p>\r\n<p class="bodytext">ime panettha tabya, tāya, teyyapayogā – na brāhmaṇe addhike tiṭṭhamāne, gantabyamāhu dvipadinda seṭṭha <span class="note">[jā. 1.10.13 (gantabba)]</span>. bhūtagāmapātabyatā, kāmesu pātabyatā <span class="note">[pāci. 90]</span>, alajjitāye lajjanti <span class="note">[dha. pa. 316]</span>, lajjitāye na lajjare. ghātetāyaṃ vā ghātetuṃ, pabbājetāyaṃ vā pabbājetuṃ <span class="note">[ma. ni. 1.357]</span>, ñāteyyaṃ, diṭṭheyyaṃ, patteyyaṃ, viddheyyaṃ maṃ amaññatha <span class="note">[jā. 2.22.297]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54886,'annya_sadda_10',479,replace(replace('\r\n<p class="bodytext">ta,ti<a name="M0.0479"></a>, tu, tavantu, tāvī, tvā, tvāna, tuna, tuṃ, tave, tāye, tuye, tabba. ime takārapaccayā nāma.</p>\r\n<p class="bodytext"><span class="bld">kara, khanu, gā, gamu, jana, ṭhā, tanu, thara, dhā, dhara, namu, pā, phara, bhara, mana, mara, ramu, sara, hara, hana</span>.</p>\r\n<p class="bodytext"><a name="para723"></a><span class="paranum">723</span>. <span class="bld">gamādirānaṃ lopontassa</span> <span class="note">[ka. 586-7; rū. 600, 632; nī. 1190, 1191]</span>.</p>\r\n<p class="bodytext">gamādīnaṃ makāra, nakārantānaṃ rakārantānañca dhātūnaṃ antassa lopo hoti kānubandhe tapaccaye pare tvādivajjite.</p>\r\n<p class="bodytext">kara-karaṇe, karīyitthāti kato-vihāro, katāgūhā, kataṃ-gehaṃ, sakkarīyitthāti sakkato, mahāvuttinā santassa so.</p>\r\n<p class="bodytext">‘karotissa kho’ti pādito karassa kassa kho, saṅkharīyitthāti saṅkhato, abhisaṅkhato, visaṅkharittha vikirīyitthāti visaṅkhato, upakarīyittha sajjīyitthāti upakkhaṭo, ‘tathanarānaṃ ṭaṭhaṇalā’ti tassa ṭo. evaṃ dukkaṭaṃ.</p>\r\n<p class="bodytext">parito karīyitthāti parikkhato, purato karīyitthāti purakkhato, purekkhato vā, mahāvuttinā purassa ettaṃ.</p>\r\n<p class="bodytext">khanu-avadāraṇe, khaññitthāti khato-āvāṭo.</p>\r\n<p class="bodytext">gā-sadde.</p>\r\n<p class="bodytext"><a name="para724"></a><span class="paranum">724</span>. <span class="bld">gāpānamī</span> <span class="note">[ka. 588; rū. 620; nī. 1192]</span>.</p>\r\n<p class="bodytext">gā, pānaṃ anto īkāro hoti kānubandhe tapaccaye pare tvādivajjite.</p>\r\n<p class="bodytext">gāyitthāti gītaṃ, samodhānetvā gāyitthāti saṅgītopariyattidhammo.</p>\r\n','\r',char(13)),'\n',char(10)),'-723-724-');
INSERT INTO pages VALUES(54887,'annya_sadda_10',480,replace(replace('\r\n<p class="bodytext">gamu-gatimhi <a name="M0.0480"></a> agacchīti gato, agacchīyitthāti vā gato. evaṃ āgato, uggato, duggato, niggato, vigato, sugato, saṅgato, anugato, apagato, avagato, upagato, adhigato.</p>\r\n<p class="bodytext">jana-jātiyaṃ.</p>\r\n<p class="bodytext"><a name="para725"></a><span class="paranum">725</span>. <span class="bld">janissā</span> <span class="note">[ka. 585; rū. 619; nī. 1189]</span>.</p>\r\n<p class="bodytext">janissa nassa ā hoti kānubandhe tapaccaye pare tvādivajjite.</p>\r\n<p class="bodytext">ajāyitthāti jāto, dujjāto, sujāto, sañjāto, anujāto, avajāto, atijāto.</p>\r\n<p class="bodytext">suttavibhattena aññasmimpi vaṇṇe pare nassa ā hoti, puttaṃ vijāyitvā, vijāyituṃ, vijāyanaṃ, vijāyantī-itthī, vijāyamānā, puttaṃ janetīti jāyā iccādi, sabbattha mahāvuttinā sare pare yāgamo, mahāvuttinā vā sabbattha nassa yādeso ādidīgho ca.</p>\r\n<p class="bodytext">ṭhā-gatinivattiyaṃ.</p>\r\n<p class="bodytext"><a name="para726"></a><span class="paranum">726</span>. <span class="bld">ṭhāssi</span> <span class="note">[ka. 588; rū. 620; nī. 1192]</span>.</p>\r\n<p class="bodytext">ṭhāssa i hoti kānubandhe takāre tvādivajjite.</p>\r\n<p class="bodytext">aṭṭhāsīti ṭhito, uṭṭhito, niṭṭhito, saṇṭhito, adhiṭṭhito.</p>\r\n<p class="bodytext">tanu-vitthāre, ātaññitthāti ātataṃ, vitataṃ, ātatavitataṃ, tūriyabhedo.</p>\r\n<p class="bodytext">thara-santharaṇe, santharīyitthāti santhato, vitthato.</p>\r\n<p class="bodytext">dhā-dhāraṇe.</p>\r\n<p class="bodytext"><a name="para727"></a><span class="paranum">727</span>. <span class="bld">dhāssa hi</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-725-726-727-');
INSERT INTO pages VALUES(54888,'annya_sadda_10',481,replace(replace('\r\n<p class="bodytext">dhādhātussa <a name="M0.0481"></a> dhassa hi hoti kānubandhe takāre tvādivajjite.</p>\r\n<p class="bodytext">ādhīyitthāti āhito, āgyāhito, vidhīyitthāti vihito, nidhīyitthāti nihito, sandhīyitthāti saṃhito, odhīyitthāti ohito, abhidhīyitthāti abhihito, pidhīyitthāti pihito, apihito. dvitte pubbassa tatiyattaṃ, ‘dhāssa ho’ti suttena parassa hattaṃ, ādahito, vidahito, nidahito, saṃdahito, saddahito vā, sannidahito, odahito, pidahito, apidahito, paridahito.</p>\r\n<p class="bodytext">dhara-dhāraṇe, uddharīyitthāti uddhaṭo, samuddhaṭo, niddhaṭo, tassa ṭattaṃ.</p>\r\n<p class="bodytext">namu-namane, namitthāti nato, unnato, samunnato, onato, avanato.</p>\r\n<p class="bodytext">pā-pāne, ‘gāpānamī’ti īttaṃ, pīyitthāti pītaṃ.</p>\r\n<p class="bodytext">phara-pharaṇe, pharittha, pharīyitthāti vā phuṭo, vipphuṭo, samphuṭo, ophuṭo, mahāvuttinā phassa uttaṃ, tassa ṭattaṃ.</p>\r\n<p class="bodytext">bhara-dhāraṇe, bharīyitthāti bhato, ābhato, ābhaṭo vā. udakātalamubbhato, ubbhataṃ saṅghena kathinaṃ <span class="note">[mahāva. 317]</span>, sambhataṃ dhanaṃ.</p>\r\n<p class="bodytext">mana-ñāṇe, mato, mahājanena sammatoti mahāsammato, sammatā sīmā <span class="note">[mahāva. 139]</span>, anumato, abhimato.</p>\r\n<p class="bodytext">mara-pāṇacāge, maritthāti mato, kālaṅkato.</p>\r\n<p class="bodytext">ramu-kīḷāyaṃ, ramitthāti rato, abhirato.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54889,'annya_sadda_10',482,replace(replace('\r\n<p class="bodytext">ramu-uparame<a name="M0.0482"></a>, virato, paṭivirato, uparato.</p>\r\n<p class="bodytext">sara-gati, cintāsu, bahulādhikārā kālattayepi tapaccayo, sarati, asari, sarissatīti sato, anussato, patissato.</p>\r\n<p class="bodytext">hara-haraṇe, harīyitthāti hato, āhato, nihato.</p>\r\n<p class="bodytext">tassa ṭatte-āhaṭo, nihaṭo, udāhaṭo, samudāhaṭo, avahaṭo.</p>\r\n<p class="bodytext">hana-hiṃsāyaṃ, haññitthāti hato, vihato, samūhato avippavāso <span class="note">[mahāva. 145]</span>, samūhatā sīmā <span class="note">[mahāva. 146]</span>.</p>\r\n<p class="bodytext">tipaccayamhi bahulādhikārā akānubandhepi antalopo. paṭhamaṃ karīyatīti pakati, ākāro ākati, vikāro vikati, gāyanaṃ gīti, uggīti, saṅgīti, anugāyanaṃ anugīti, gamanaṃ gati, gantabbāti vā gati, gacchanti etthāti vā gati, āgamanaṃ āgati, sugati, duggati, samāgamanaṃ saṅgati, jananaṃ jāti, jāyanti etāya, etthāti vā jāti, ṭhānaṃ ṭhiti, saṇṭhiti, avaṭṭhiti, punappunaṃ tananaṃ santati, dhārenti etāyāti dhīti, mahāvuttinā īttaṃ.</p>\r\n<p class="bodytext">namanaṃ nati, unnati, samunnati, onati, avanati, bharitabbāti bhati, manati jānāti etāyāti mati, vividhā mati vimati, ramaṇaṃ rati, āramaṇaṃ ārati, viramaṇaṃ virati, abhiramaṇaṃ abhirati, paṭiviramaṇaṃ pativirati, saraṇaṃ sati, saranti etāyāti vā sati, anussati, paṭissati, upahananaṃ upahati.</p>\r\n<p class="bodytext">tavantupaccayamhi-akāsīti katavā, ahanīti hatavā.</p>\r\n<p class="bodytext">tāvīpaccayamhi-katāvī, hatāvī.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54890,'annya_sadda_10',483,replace(replace('\r\n<p class="bodytext">tvādīsu <a name="M0.0483"></a> –</p>\r\n<p class="bodytext"><a name="para728"></a><span class="paranum">728</span>. <span class="bld">tuṃtunatabbesu vā</span>.</p>\r\n<p class="bodytext">karadhātussa ra-kārassa ā hoti vā tuṃ, tuna, tabbesu. tunasaddena tvāna, tvāpi saṅgayhanti.</p>\r\n<p class="bodytext"><a name="para729"></a><span class="paranum">729</span>. <span class="bld">karassā tave</span>.</p>\r\n<p class="bodytext">karassa ra-kārassa ā hoti tavepaccayamhi.</p>\r\n<p class="bodytext">kātuṃ, kātave, kātuna, kātabbaṃ.</p>\r\n<p class="bodytext">yathā karassa, tathā mahāvuttinā harassa rūpaṃ sijjhati, hāthuṃ, hātave, hātuna. tesaṃ tuṇḍena hātūna, muñce pubbakataṃ iṇaṃ <span class="note">[jā. 1.14.10]</span>.</p>\r\n<p class="bodytext">tvāmhi āssa ittaṃ, āhitvā, soṇḍāyudakamāhitvā <span class="note">[jā. 1.10.9 (.hatvā)]</span>.</p>\r\n<p class="centered">iti visaṃyogarūparāsi.</p>\r\n<p class="subhead">sadisasaṃyogarūparāsi</p>\r\n<p class="bodytext">atha sadisasaṃyogarūparāsi vuccate.</p>\r\n<p class="bodytext">tupaccayamhi –</p>\r\n<p class="bodytext"><a name="para730"></a><span class="paranum">730</span>. <span class="bld">pararūpamayakāre byañjane</span>.</p>\r\n<p class="bodytext">yakāravajjite byañjanapaccaye pare sabbadhātūnaṃ antabyañjano pararūpaṃ āpajjate.</p>\r\n<p class="bodytext">karotīti kattā, kātuṃ arahati, kātuṃ sakkoti, karaṇasīlo, karaṇadhammo, sakkaccaṃ vā karotīti attho. bharatīti bhattā, haratīti hattā.</p>\r\n','\r',char(13)),'\n',char(10)),'-728-729-730-');
INSERT INTO pages VALUES(54891,'annya_sadda_10',484,replace(replace('\r\n<p class="bodytext">tvādīsu <a name="M0.0484"></a> ra-kārassa āttaṃ, saṃyoge pare rassattañca, katvā, katvāna.</p>\r\n<p class="bodytext">pararūpattaṃ, kattuna, kattuṃ, kattabbaṃ, bharaṇaṃ bhattuṃ, bhattave, haraṇaṃ hattuṃ, abhihattuṃ, hattave.</p>\r\n<p class="bodytext"><span class="bld">āpa, ṇāpa, khipa, gupa, caja, ñapa, ñāpa, tapa, dīpa, dhūpa, pada, bhaja, bhuja, mada, mida, yuja, rica, ranja, lipa, lupa, vaca, vatu, vada, vapa, vica, sanja, sica, sambhu, sūca, sūda, supa</span>.</p>\r\n<p class="bodytext">dhātvantabyañjanassa pararūpattaṃ, tapaccayamhi vipubbo āpabyāpane, byāpayati khippaṃ ñāṇabyāpanena byāpituṃ sakkotīti byatto, viyatto.</p>\r\n<p class="bodytext">paripubbo pariyāpuṇane pahutte ca, pariyatto.</p>\r\n<p class="bodytext">saṃpubbo paripuṇṇabhāve, samāpayitthāti samatto, parisamatto.</p>\r\n<p class="bodytext">āpubbo ṇāpa-pesane, āṇāpīyitthāti āṇatto.</p>\r\n<p class="bodytext">khipa-khipane, khipīyitthāti khitto-daṇḍo, khittā-mattikā, khittaṃ-leṭṭu. evaṃ sabbattha. pakkhitto, ukkhitto, nikkhitto, vikkhitto, okkhitto, saṃkhitto.</p>\r\n<p class="bodytext">gupa-guttiyaṃ, gopīyitthāti gutto, saṃgutto.</p>\r\n<p class="bodytext">caja-cāge, cajīyitthāti catto.</p>\r\n<p class="bodytext">upa-paññāpane, paññapīyitthāti paññatto-vinayo, paññattaṃsikkhāpadaṃ, paññattaṃ-āsanaṃ.</p>\r\n<p class="bodytext">ñāpa-ñāpane, vikatidhātu nāmesā kāritantattā, paññāpīyitthāti paññatto, saññāpīyitthāti saññatto, viññāpīyitthāti viññatto.</p>\r\n<p class="bodytext">tapa-santāpe, atappīti tatto, santatto.</p>\r\n<p class="bodytext">dīpa-dittiyaṃ, adīpitthāti ditto, paditto, āditto.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54892,'annya_sadda_10',485,replace(replace('\r\n<p class="bodytext">dhūpa-soṇḍiye<a name="M0.0485"></a>, dhūpati, adhūpi, dhūpissatīti dhutto, surādhutto, akkhadhutto <span class="note">[su. ni. 106]</span>.</p>\r\n<p class="bodytext">pada-gatiyaṃ, apajjīti patto, nipatto, sampatto.</p>\r\n<p class="bodytext">bhaja-sambhattiyaṃ, bhajatīti bhatto, sambhatto.</p>\r\n<p class="bodytext">vipubbo puthakkaraṇe, vibhajitthāti vibhatto.</p>\r\n<p class="bodytext">bhuja-pālana, byavaharaṇesu, bhuñjittha, bhuñjīyitthāti vā bhutto, paribhutto.</p>\r\n<p class="bodytext">mada-ummāde, majjitthāti matto, sammatto, pamatto, ummatto.</p>\r\n<p class="bodytext">mida-sinehane, mijjatīti mitto.</p>\r\n<p class="bodytext">yuja-yoge, yuñjatīti yutto, payutto, uyyutto, niyutto, viyutto, saṃyutto, saññutto.</p>\r\n<p class="bodytext">rica-viriñcane, riñcatīti ritto.</p>\r\n<p class="bodytext">ranja-rāge, rañjatīti ratto, sāratto, viratto.</p>\r\n<p class="bodytext">lipa-limpane, limpīyitthāti litto, ullitto, avalitto.</p>\r\n<p class="bodytext">lupa-adassane, luppatīti lutto.</p>\r\n<p class="bodytext">vaca-viyattiyaṃ vācāyaṃ.</p>\r\n<p class="bodytext"><a name="para731"></a><span class="paranum">731</span>. <span class="bld">vacādīnaṃ vassuṭa vā</span> <span class="note">[ka. 579; rū. 629; nī. 1182]</span>.</p>\r\n<p class="bodytext">vacādīnaṃ vassa uṭa hoti vā kānubandhe ta-kārapaccaye tvādivajjite.</p>\r\n','\r',char(13)),'\n',char(10)),'-731-');
INSERT INTO pages VALUES(54893,'annya_sadda_10',486,replace(replace('\r\n<p class="bodytext">vuccitthāti <a name="M0.0486"></a> utto-dhammo, uttā-kathā, uttaṃ-vacanaṃ, nirutto, niruttā, niruttaṃ, rāgamo.</p>\r\n<p class="bodytext"><a name="para732"></a><span class="paranum">732</span>. <span class="bld">assu</span>.</p>\r\n<p class="bodytext">vacādīnaṃ assa u hoti kānubandhe ta-kārapaccaye tvādivajjite.</p>\r\n<p class="bodytext">vutto-dhammo, vuttā-kathā, vuttaṃ-vacanaṃ.</p>\r\n<p class="bodytext">vatu-vattane, vattatīti vatto, pavatto, nivatto.</p>\r\n<p class="bodytext">vapa-bījanikkhepe, vapīyitthāti vuttaṃ-bījaṃ, ‘assū’ti uttaṃ.</p>\r\n<p class="bodytext">vica-vivecane, viviccitthāti vivitto.</p>\r\n<p class="bodytext">sanja-saṅge, sañjatīti satto, āsatto, visatto.</p>\r\n<p class="bodytext">sica-secane, siñcīyitthāti sitto, āsitto, avasitto, abhisitto.</p>\r\n<p class="bodytext">sūca-sūcane, atthaṃ sūcetīti suttaṃ.</p>\r\n<p class="bodytext">sūda-paggharaṇe, atthaṃ sūdatīti suttaṃ.</p>\r\n<p class="bodytext">supa-soppane, supatīti sutto iccādi.</p>\r\n<p class="bodytext">tipaccayamhi-byāpanaṃ byatti, viyatti, pariyāpuṇanaṃ pariyatti, samāpanaṃ samatti, parisamatti, āṇāpanaṃ āṇatti, gopanaṃ gutti, ñāpanaṃ ñatti, paññāpanaṃ paññatti, saññāpanaṃ saññatti, viññāpanaṃ viññatti.</p>\r\n','\r',char(13)),'\n',char(10)),'-732-');
INSERT INTO pages VALUES(54894,'annya_sadda_10',487,replace(replace('\r\n<p class="bodytext">tapa-tappane, tappanaṃ titti, mahāvuttinā assa ittaṃ. dīpanaṃ ditti, pajjanaṃ patti, āpatti, uppatti, nippatti, vipatti, sampatti, bhajanaṃ bhatti, sambhatti, bhuñjanaṃ bhutti, yuñjanaṃ yutti, riñcanaṃ ritti, niddhāretvā vuccati attho etāyāti nirutti, vuccati suttassa attho <a name="M0.0487"></a> etāyāti vutti. "vivarīyati suttassa attho etāyāti vuttī"tipi vadanti. vattanaṃ vutti, jīvitavutti, tadāyattavutti, ‘assū’ti assa uttaṃ. vivecanaṃ vivitti, sajjanaṃ satti, āsatti, visatti iccādi.</p>\r\n<p class="bodytext">tupaccayamhi-khipatīti khittā, gopetīti guttā, cajatīti cattā.</p>\r\n<p class="bodytext">idha chida, bhidādayopi vattabbā, chindatīti chettā, bhindatīti bhettā, bhajatīti bhattā, bhuñjatīti bhuttā, bhottā, yuttā, rittā, littā, luttā, vacati vadatīti vā vattā, viviccatīti vivittā, sañjatīti sattā, suppatīti suttā iccādi.</p>\r\n<p class="bodytext">tavantupaccayamhi-khipitthāti khittavā, gopitthāti guttavā, cajitthāti cattavā, chinditthāti chettavā.</p>\r\n<p class="bodytext">bhaja-puthakkaraṇe, bhājitthāti bhattavā, vibhattavā, abhuñjīti bhuttavā, ayuñjīti yuttavā iccādi.</p>\r\n<p class="bodytext">tāvīpaccayamhi-khittāvī, guttāvī, cattāvī, chettāvī, vibhattāvī, bhettāvī, bhuttāvī, yuttāvī iccādi.</p>\r\n<p class="bodytext">tvādīsu pararūpatte mahāvuttinā tiṇṇaṃ byañjanānaṃ ādibyañjanassa lopo, chetvā, chetvāna, chettuna, vibhatvā, vibhatvāna, vibhattuna, bhutvā, bhutvāna, bhuttuna iccādi.</p>\r\n<p class="bodytext">tuṃ, tavesu-chettuṃ, chettave, chetuṃ, chetave vā, ādibyañjanassa lopo. vibhattuṃ, vibhattave, bhettuṃ, bhettave, bhottuṃ, bhottave, ādivuddhi iccādi.</p>\r\n<p class="bodytext">tabbapaccaye-chettabbaṃ, chetabbaṃ vā, bhettabbaṃ, bhottabbaṃ, vuccatīti vattabbaṃ iccādi.</p>\r\n<p class="centered">iti sadisasaṃyogarūparāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54895,'annya_sadda_10',488,replace(replace('\r\n<p class="subhead">vaggantarūparāsi<a name="M0.0488"></a></p>\r\n<p class="bodytext">atha vaggantarūparāsi vuccate.</p>\r\n<p class="bodytext"><span class="bld">kamu, kilamu, khanu, khamu, gamu, tanu, timu, damu, bhamu, mana, yamu, vamu, samu, hana</span>.</p>\r\n<p class="bodytext"><a name="para733"></a><span class="paranum">733</span>. <span class="bld">manānaṃ niggahītaṃ</span>.</p>\r\n<p class="bodytext">makāra, nakārantānaṃ dhātūnaṃ anto makāro nakāro ca niggahītaṃ hoti yakāravajjite byañjane pare. ‘vagge vagganto’ti niggahītassa vaggantattaṃ.</p>\r\n<p class="bodytext">kamu-pādagamane, pakkamitthāti pakkanto, pādena akkamitthāti akkanto, ukkanto, vikkanto, nikkhanto, ‘nito kamassā’ti kassa khattaṃ, saṅkanto, okkanto, avakkanto, apakkanto, atikkanto, paṭikkanto, kassa dvittāni.</p>\r\n<p class="bodytext">kilamu-khede, kilamitthāti kilanto.</p>\r\n<p class="bodytext">timu-addabhāve, temayittāti tinto.</p>\r\n<p class="bodytext">damu-damane, damitthāti danto.</p>\r\n<p class="bodytext">bhamu-anavatthāne, bhamitthāti bhanto, vibbhanto.</p>\r\n<p class="bodytext">mana-ñāṇe, manatīti manto.</p>\r\n<p class="bodytext">vamu-uggilane, vamitthāti vanto.</p>\r\n<p class="bodytext">samu-santiyaṃ, sammatīti santo, upasanto, vūpasanto.</p>\r\n<p class="bodytext">samu-khede, sammati khijjatīti santo iccādi.</p>\r\n<p class="bodytext">tipaccayamhi-kāmanaṃ kanti, nikāmanaṃ nikanti, pakkamanaṃ pakkanti, khamanaṃ khanti, tananaṃ tanti, damanaṃ danti, bhamanaṃ bhanti, vibbhanti, mananaṃ manti, samanaṃ santi iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-733-');
INSERT INTO pages VALUES(54896,'annya_sadda_10',489,replace(replace('\r\n<p class="bodytext">tupaccaye-pakkamatīti <a name="M0.0489"></a> pakkantā, khanatīti khantā, khamatīti khantā, gacchatīti gantā, tanotīti tantā, temayatīti tintā, damayatīti dantā, bhamatīti bhantā, manatīti mantā.</p>\r\n<p class="bodytext">nipubbo yamu-niyamane, niyāmetīti niyantā, vamatīti vantā, samatīti santā, hanatīti hantā iccādi.</p>\r\n<p class="bodytext">tvādīsu-gantvā, gantvāna, gantuna, mantvā, mantvāna, mantuna, hantvā, hantvāna, hantuna iccādi.</p>\r\n<p class="bodytext">tuṃ, tavesu-pakkantuṃ, pakkantave, khananaṃ khantuṃ, khantave, gamanaṃ gantuṃ, gantave, mananaṃ mantuṃ, mantave, hananaṃ hantuṃ, hantave iccādi.</p>\r\n<p class="bodytext">tabbamhi-abhikkantabbaṃ, paṭikkantabbaṃ, khaññateti khantabbaṃ, gacchīyateti gantabbaṃ, maññateti mantabbaṃ, vamīyateti vantabbaṃ, haññateti hantabbaṃ iccādi.</p>\r\n<p class="centered">iti vaggantarūparāsi.</p>\r\n<p class="centered">dhātvantavikārarāsi niṭṭhito.</p>\r\n<p class="subhead">paccayavikārarāsi</p>\r\n<p class="subhead">kādesarāsi</p>\r\n<p class="bodytext">atha paccayavikārarāsi vuccate.</p>\r\n<p class="bodytext"><a name="para734"></a><span class="paranum">734</span>. <span class="bld">pacā ko</span> <span class="note">[ka. 583; rū. 617; nī. 1186]</span>.</p>\r\n<p class="bodytext">pacamhā ta, tavantūnaṃ tassa ko hoti.</p>\r\n<p class="bodytext">paccitthāti pakko, pakkavā.</p>\r\n','\r',char(13)),'\n',char(10)),'-734-');
INSERT INTO pages VALUES(54897,'annya_sadda_10',490,replace(replace('\r\n<p class="bodytext">bahulādhikārā <a name="M0.0490"></a> tapaccayo kālattayepi hoti, asakkhi, sakkhati, sakkhissatīti sakko, mahāvuttinā tapaccayassa ko.</p>\r\n<p class="bodytext">muca-mocane.</p>\r\n<p class="bodytext"><a name="para735"></a><span class="paranum">735</span>. <span class="bld">mucā vā</span> <span class="note">[ka. 583; rū. 617; nī. 1186]</span>.</p>\r\n<p class="bodytext">mucamhā ta, tavantūnaṃ tassa anantarassa ko hoti vā.</p>\r\n<p class="bodytext">omuccitthāti omukko, omukkavā, paṭimukko, paṭimukkavā.</p>\r\n<p class="bodytext">susa-sosane.</p>\r\n<p class="bodytext"><a name="para736"></a><span class="paranum">736</span>. <span class="bld">susā kho</span> <span class="note">[ka. 583; rū. 617; nī. 1186]</span>.</p>\r\n<p class="bodytext">susamhā ta, tavantūnaṃ tassa kho hoti.</p>\r\n<p class="bodytext">sussitthāti sukkho, sukkhavā.</p>\r\n<p class="bodytext"><a name="para737"></a><span class="paranum">737</span>. <span class="bld">go bhanjādīhi</span> <span class="note">[ka. 577; rū. 628; nī. 1180]</span>.</p>\r\n<p class="bodytext">bhanjādīhi ta, tavantūnaṃ tassa go hoti.</p>\r\n<p class="bodytext">abhañjitthāti bhaggo, bhaggavā, obhaggo, sambhaggo, palibhaggo.</p>\r\n<p class="bodytext">laga-laggane, lagitthāti laggo, laggavā, vilaggo, vilaggavā.</p>\r\n<p class="bodytext">muja-mujjane, mujjitthāti muggo, muggavā, nimmuggo, ummuggo.</p>\r\n<p class="bodytext">vija-bhaya, calanesu, saṃvijitthāti saṃviggo, saṃviggavā, ubbiggo, ubbiggavā.</p>\r\n<p class="bodytext">luja-vināse, palujitthāti paluggo, paluggavā, oluggo, oluggavā, viluggo, viluggavā iccādi.</p>\r\n<p class="centered">iti kādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-735-736-737-');
INSERT INTO pages VALUES(54898,'annya_sadda_10',491,replace(replace('\r\n<p class="subhead">ṭhādesarāsi</p>\r\n\r\n<p class="bodytext"><span class="bld">isu</span><a name="M0.0491"></a>, <span class="bld">āsa, esa, kasa, kisa, kilisa, kusa, ghusa, jusa, tusa, disa, dusa, daṃsa, nasa, pisa, pusa, puccha, phusa, bhassa, bhajja, maja, masa, musa, vassa, visa, saja, sisa, silisa, hasa, hassa, haṃsa</span>.</p>\r\n<p class="bodytext"><a name="para738"></a><span class="paranum">738</span>. <span class="bld">sānantarassa tassa ṭho</span> <span class="note">[ka. 573; rū. 626; nī. 1176 (thokaṃ visadisaṃ)]</span>.</p>\r\n<p class="bodytext">sakārantehi dhātūhi parassa anantarassa paccayatakārassa ṭho hoti, dhātvantassa pararūpattaṃ, ‘catutthadutiyesvesa’nti saṃyogādissa paṭhamattaṃ.</p>\r\n<p class="bodytext">isu-icchā, kantīsu, icchīyateti iṭṭho, pariyiṭṭho.</p>\r\n<p class="bodytext">āsa-upavesane, viparitato āsati upavīsatīti vipallaṭṭho.</p>\r\n<p class="bodytext">kasa-vappane vilekhane ca.</p>\r\n<p class="bodytext"><a name="para739"></a><span class="paranum">739</span>. <span class="bld">kasassima ca vā</span> <span class="note">[ka. 573; rū. 626; nī. 1176 (thokaṃ visadisaṃ)]</span>.</p>\r\n<p class="bodytext">kasamhā parassa paccayatakārassa ṭho hoti, kasassa ādisaramhā paraṃ ima ca hoti vā.</p>\r\n<p class="bodytext">kassitthāti kiṭṭhaṃ-sassaṃ, kaṭṭhaṃ vā.</p>\r\n<p class="bodytext">upapubbo āsanne, upakaṭṭho.</p>\r\n<p class="bodytext">vipubbo pavāse, vūpakaṭṭho.</p>\r\n<p class="bodytext">kisa-hānimhi, paṭikiṭṭho, nihīnoti attho.</p>\r\n<p class="bodytext">kilisa-vibādhane upatāpe ca, kilissatīti kiliṭṭho, saṃkiliṭṭho, upakkiliṭṭho.</p>\r\n<p class="bodytext">kusa-akkose, akkosīyitthāti akkuṭṭho. akkuṭṭho jātivādena.</p>\r\n','\r',char(13)),'\n',char(10)),'-738-739-');
INSERT INTO pages VALUES(54899,'annya_sadda_10',492,replace(replace('\r\n<p class="bodytext">ghusa-sadde<a name="M0.0492"></a>, ghosīyitthāti ghuṭṭho, saṅghuṭṭho. accharāgaṇasaṅghuṭṭhaṃ <span class="note">[saṃ. ni. 1.46]</span>. ugghuṭṭho.</p>\r\n<p class="bodytext">jusa-sevāyaṃ, jusīyitthāti juṭṭho.</p>\r\n<p class="bodytext">tusa-pītimhi, tussitthāti tuṭṭho, santuṭṭho.</p>\r\n<p class="bodytext">disa-pekkhane, passīyitthāti diṭṭho, sandiṭṭho.</p>\r\n<p class="bodytext">disī-kathane, uddisīyitthāti uddiṭṭho, niddisīyitthāti niddiṭṭho, apadisīyitthāti apadiṭṭho.</p>\r\n<p class="bodytext">dusa-dūsane, dusīyitthāti duṭṭho.</p>\r\n<p class="bodytext">daṃsa-daṃsane, daṃsīyitthāti daṭṭho, niggahītalopo.</p>\r\n<p class="bodytext">nasa-adassane, nassitthāti naṭṭho, vinaṭṭho.</p>\r\n<p class="bodytext">pisa-cuṇṇiye, pisīyitthāti piṭṭhaṃ.</p>\r\n<p class="bodytext">pusa-posane, posīyitthāti puṭṭho, parapuṭṭho.</p>\r\n<p class="bodytext">phusa-samphasse, phusīyitthāti phuṭṭho, samphuṭṭho.</p>\r\n<p class="bodytext">bhassa-kathane cavane ca, bhassitthāti bhaṭṭho, ābhaṭṭho.</p>\r\n<p class="bodytext">masa-āmasane vijjhane ca, masīyitthāti maṭṭho, āmaṭṭho, omaṭṭho, ummaṭṭho. sattiyā viya omaṭṭho <span class="note">[saṃ. ni. 1.21]</span>.</p>\r\n<p class="bodytext">musa-nassane, mussitthāti muṭṭho, pamuṭṭho, sammuṭṭho.</p>\r\n<p class="bodytext">vassa-secane, vassitthāti vuṭṭho-devo, ‘assū’ti uttaṃ.</p>\r\n<p class="bodytext">visa-pavesane, pavisitthāti paviṭṭho, niviṭṭho, upaviṭṭho.</p>\r\n<p class="bodytext">sisa-sese, avasesitthāti avasiṭṭho.</p>\r\n<p class="bodytext">vipubbo visesane, visesitthāti visiṭṭho.</p>\r\n<p class="bodytext">silisa-silesane, silissitthāti siliṭṭho.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54900,'annya_sadda_10',493,replace(replace('\r\n<p class="bodytext">hasa-hāse, <a name="M0.0493"></a> hasitthāti haṭṭho, pahaṭṭho.</p>\r\n<p class="bodytext">hassa, haṃsadhātuyo sadisā eva.</p>\r\n<p class="bodytext">puccha-pucchāyaṃ.</p>\r\n<p class="bodytext"><a name="para740"></a><span class="paranum">740</span>. <span class="bld">pucchādito</span> <span class="note">[ka. 571; rū. 626; nī. 1176]</span>.</p>\r\n<p class="bodytext">pucchādīhi parassa antarassa paccayatakārassa ṭho hoti.</p>\r\n<p class="bodytext">pucchīyitthāti puṭṭho.</p>\r\n<p class="bodytext">bhajja-bhajjane, bhajjitthāti bhaṭṭhaṃ-dhaññaṃ.</p>\r\n<p class="bodytext">maja-suddhiyaṃ, suṭṭhu majjitthāti sammaṭṭho-bhūmibhāgo.</p>\r\n<p class="bodytext">saja-saṃsaggādīsu, saṃsajjitthāti saṃsaṭṭho, visaṭṭho, nisaṭṭho, osaṭṭho.</p>\r\n<p class="bodytext">yaja-pūjāyaṃ.</p>\r\n<p class="bodytext"><a name="para741"></a><span class="paranum">741</span>. <span class="bld">yajassa yassa ṭiyī</span> <span class="note">[ka. 610; rū. 627; nī. 1215]</span>.</p>\r\n<p class="bodytext">yajassa yakārassa ṭi, yiādesā honti kānubandhe paccayatakāre tvādivajjite.</p>\r\n<p class="bodytext">yajitthāti iṭṭho, yiṭṭho.</p>\r\n<p class="bodytext">tipaccayamhi-pariyesanaṃ pariyeṭṭhi.</p>\r\n<p class="bodytext">esa-gavesane, esanaṃ eṭṭhi, pariyeṭṭhi, tussanaṃ tuṭṭhi, santuṭṭhi, dassanaṃ diṭṭhi, sandiṭṭhi, vassanaṃ vuṭṭhi, ‘assū’ti uttaṃ. visajjanaṃ visaṭṭhi.</p>\r\n<p class="bodytext">tvādīsu-disa-pekkhane, ‘disassa passadassadasādadakkhā’ti suttena disassa dasādeso, mahāvuttinā vakāralopo, daṭṭhā, daṭṭhāna, daṭṭhuna.</p>\r\n','\r',char(13)),'\n',char(10)),'-740-741-');
INSERT INTO pages VALUES(54901,'annya_sadda_10',494,replace(replace('\r\n<p class="bodytext">‘tuṃyānā’ti <a name="M0.0494"></a> tvāpaccayassa tuṃādeso. nekkhamaṃ daṭṭhu khemato <span class="note">[su. ni. 426]</span>, gāthāvasena niggahītalopo.</p>\r\n<p class="bodytext">tuṃ, tavesu-daṭṭhuṃ, daṭṭhave, pucchanaṃ puṭṭhuṃ, puṭṭhave.</p>\r\n<p class="bodytext">tabbamhi-tussitabbanti tuṭṭhabbaṃ, toṭṭhabbaṃ, passitabbanti daṭṭhabbaṃ, pucchitabbanti puṭṭhabbaṃ, phusitabbanti phoṭṭhabbaṃ.</p>\r\n<p class="centered">iti ṭhādesarāsi.</p>\r\n<p class="subhead">ḍhādesarāsi</p>\r\n<p class="bodytext"><a name="para742"></a><span class="paranum">742</span>. <span class="bld">dahā ḍho</span> <span class="note">[ka. 576; rū. 607; nī. 1179]</span>.</p>\r\n<p class="bodytext">dahamhā parassa anantarassa paccayatakārassa ḍho hoti, pararūpatte saṃyogādissa tatiyattaṃ.</p>\r\n<p class="bodytext">daha-dayhane, dayhitthāti daḍḍho.</p>\r\n<p class="bodytext"><a name="para743"></a><span class="paranum">743</span>. <span class="bld">bahassuma ca</span> <span class="note">[ka. 576; rū. 607; nī. 1179]</span>.</p>\r\n<p class="bodytext">bahamhā parassa anantarassa tassa ḍho hoti, bahassa ādisaramhā uma ca hoti.</p>\r\n<p class="bodytext">baha-vuddhiyaṃ, abahīti buḍḍho, bassa vo, vuḍḍho.</p>\r\n<p class="bodytext">timhi-bahanaṃ vuḍḍhi.</p>\r\n<p class="bodytext"><a name="para744"></a><span class="paranum">744</span>. <span class="bld">lopo vaḍḍhā tissa</span> <span class="note">[‘ttissa’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vaḍḍhamhā parassa tipaccayassa takārassa lopo hoti.</p>\r\n<p class="bodytext">vaḍḍha-vaḍḍhane, vaḍḍhanaṃ vuḍḍhi.</p>\r\n<p class="centered">iti ḍhādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-742-743-744-');
INSERT INTO pages VALUES(54902,'annya_sadda_10',495,replace(replace('\r\n<p class="subhead">ṇādesarāsi</p>\r\n\r\n<p class="bodytext"><span class="bld">kira</span> <a name="M0.0495"></a> <span class="bld">khī, cara, jara, tara, thara, pūra</span>.</p>\r\n<p class="bodytext"><a name="para745"></a><span class="paranum">745</span>. <span class="bld">kirādīhi ṇo</span>.</p>\r\n<p class="bodytext">kirādīhi paresaṃ ta, tavantūnaṃ takārassa anantarabhūtassa ṇo hoti, dhātvantassa pararūpattaṃ.</p>\r\n<p class="bodytext">kira-ākiraṇe sammissana, khipanesu ca, kiritthāti kiṇṇo, pakiṇṇo, ākiṇṇo, vikkiṇṇo, saṃkiṇṇo, samākiṇṇo.</p>\r\n<p class="bodytext">pūra-pūraṇe, pūritthāti puṇṇo, sampuṇṇo, paripuṇṇo.</p>\r\n<p class="bodytext">khī-khaye, khiyitthāti khīṇo.</p>\r\n<p class="bodytext">kiṇṇavā, puṇṇavā, khīṇavā.</p>\r\n<p class="bodytext"><a name="para746"></a><span class="paranum">746</span>. <span class="bld">tarādīhi riṇṇo</span> <span class="note">[ka. 581; rū. 616; nī. 1184]</span>.</p>\r\n<p class="bodytext">tarādīhi paresaṃ ta, tavantūnaṃ takārassa anantarabhūtassa riṇṇo hoti. ‘rānubandhentasarādissā’ti dhātvantabyañjanassa ādisarassa ca lopo.</p>\r\n<p class="bodytext">cara-gati, bhakkhanesu, carittha, carīyitthāti vā ciṇṇo, āciṇṇo, samāciṇṇo.</p>\r\n<p class="bodytext">jara-jiraṇe, jiyyitthāti jiṇṇo, anujiṇṇo, parijiṇṇo.</p>\r\n<p class="bodytext">tara-taraṇe, taritthāti tiṇṇo, uttiṇṇo, nittiṇṇo, vitiṇṇo, otiṇṇo, samotiṇṇo.</p>\r\n<p class="bodytext">thara-vitthāre, vittharitthāti vitthiṇṇo.</p>\r\n<p class="bodytext">ciṇṇavā, jiṇṇavā, tiṇṇavā, vitthiṇṇavā.</p>\r\n<p class="centered">iti ṇādesarāsi.</p>\r\n<p class="subhead">thādesarāsi</p>\r\n<p class="bodytext"><a name="para747"></a><span class="paranum">747</span>. <span class="bld">dhastoostā</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-745-746-747-');
INSERT INTO pages VALUES(54903,'annya_sadda_10',496,replace(replace('\r\n<p class="bodytext">dhasto<a name="M0.0496"></a>, utrastoti ete saddā tapaccayantā sijjhanti.</p>\r\n<p class="bodytext">dhaṃsa-viddhaṃsane, viddhaṃsatīti viddhasto, viddhaṃsito vā.</p>\r\n<p class="bodytext">tasa-santāse, utrasatīti utrasto, uttasito vā.</p>\r\n<p class="bodytext">bhasa-bhasmīkaraṇe, bhasanti bhasmiṃ karonti etenāti bhastā, bhastrā vā, kammāragaggarī, evamādīnipi idha veditabbāni.</p>\r\n<p class="bodytext"><a name="para748"></a><span class="paranum">748</span>. <span class="bld">sāsa vasa saṃsa haṃsā tho</span> <span class="note">[‘.saṃsa sasā tho’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">etehi parassa anantarassa paccayatakārassa tho hoti.</p>\r\n<p class="bodytext">sāsa-anusiṭṭhimhi.</p>\r\n<p class="bodytext"><a name="para749"></a><span class="paranum">749</span>. <span class="bld">sāsassa sisā</span> <span class="note">[‘sāsassa sisa vā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">sāsassa sisā honti kānubandhe paccayatakāre tvādivajjite, dhātvantassa pararūpattaṃ saṃyogādissa ca paṭhamattaṃ.</p>\r\n<p class="bodytext">sāsīyatīti sittho, anusāsīyatīti anusiṭṭho. "anusiṭṭho so mayā"ti <span class="note">[mahāva. 126]</span> ettha pana tthakārassa ṭṭhakāroti vuttiyaṃ vutto. taṃ taṃ atthaṃ sāsati ettha, etenāti vā satthaṃ, saddasatthaṃ, vedasatthaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-748-749-');
INSERT INTO pages VALUES(54904,'annya_sadda_10',497,replace(replace('\r\n<p class="bodytext">vasa-nivāse, ‘assū’ti uttaṃ, avasīti vuttho, vasīyitthāti vā vuttho, vassaṃ vasitthāti vassaṃvuttho, āvasīyitthāti āvutthaṃ-jetavanaṃ, nivasitthāti nivuttho, ajjhāvasitthāti ajjhāvuttho. bahulādhikārā "rukkhe adhivatthā <a name="M0.0497"></a> devatā"ti <span class="note">[pāci. 86]</span> ettha uttaṃ natthi. uposathaṃ upavasitthāti uposathaṃupavuttho, upavasīyitthāti vā upavutthouposatho, parivāsaṃ parivasitthāti parivāsaṃparivuttho, parivasīyitthāti vā parivuttho-parivāso.</p>\r\n<p class="bodytext">saṃsa-pasaṃsane, pasaṃsīyitthāti pasattho.</p>\r\n<p class="bodytext">haṃsa-pahaṃsane, haṃsīyitthāti hattho, niggahītalopo, pahattho.</p>\r\n<p class="bodytext">tipaccayamhi-anusāsanaṃ anusitthi, anusiṭṭhi vā, nivasanaṃ nivutthi.</p>\r\n<p class="bodytext">tupaccayamhi-sadevakaṃ lokaṃ sāsati anusāsatīti satthā.</p>\r\n<p class="bodytext">tavantupaccayamhi-nivasitthāti nivutthavā.</p>\r\n<p class="bodytext">tāvīmhi-nivutthāvī.</p>\r\n<p class="bodytext">tuṃ, tavesu-vasanaṃ vatthuṃ, vatthave.</p>\r\n<p class="bodytext">tabbamhi-dvāramūle vatthabbaṃ, sabhāye vatthabbaṃ.</p>\r\n<p class="bodytext">vasa-paridahane, bahulādhikārā uttaṃ natthi, vāsitabbanti vatthaṃ, nivāsīyitthāti nivatthaṃ, vatthabbaṃ, nivatthabbaṃ.</p>\r\n<p class="centered">iti thādesarāsi.</p>\r\n<p class="subhead">dhādesarāsi</p>\r\n<p class="bodytext"><span class="bld">idha, kudha, gidha, badha, budha, budhi, midha, yudha, rādha, rudha, vidha, sidha, sudha, thabhi, rabha, labha, lubha, sambhū, duha, naha, muha</span>.</p>\r\n<p class="bodytext"><a name="para750"></a><span class="paranum">750</span>. <span class="bld">dho dhabhahehi</span> <span class="note">[ka. 576; rū. 607; nī. 1179; ‘dho dhahabhehi’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">etehi parassa anantarassa tassa dho hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-750-');
INSERT INTO pages VALUES(54905,'annya_sadda_10',498,replace(replace('\r\n<p class="bodytext">idha-ijjhane<a name="M0.0498"></a>, dhātvantassa pararūpattaṃ saṃyogādissa ca tatiyattaṃ, samijjhitthāti samiddhā-mahaddhano.</p>\r\n<p class="bodytext">kudha-kope, kujjhatīti kuddhā, saṃkuddhā.</p>\r\n<p class="bodytext">gidha-gedhe, gijjhitthāti giddhā, anugiddhā, abhigiddhā.</p>\r\n<p class="bodytext">badha-bandhane, bajjhitthāti baddhā, pabaddhā, ābaddhā, nibaddhā.</p>\r\n<p class="bodytext">budha-ñāṇe jāgare vikasane ca, bujjhati jānātīti buddhā, sambuddhā, sammāsambuddhā, pabujjhati jāgarotīti vā pabuddhā, paṭibuddhā.</p>\r\n<p class="bodytext">budhi-nivāraṇe, paribundhīyatīti palibuddhā. vātapalibuddhā, pittapalibuddhā, semhapalibuddhā.</p>\r\n<p class="bodytext">midha-mijjhane, mijjhatīti middhaṃ, middhā. kapimiddhā.</p>\r\n<p class="bodytext">yudha-sampahāre, yujjhīyateti yuddhaṃ. mallayuddhaṃ, meṇḍayuddhaṃ, hatthiyuddhaṃ, kukkuṭayuddhaṃ.</p>\r\n<p class="bodytext">rādha-ārādhane, ārādhayitthāti āraddhā, abhiraddhā.</p>\r\n<p class="bodytext">vipubbo-virajjhane, viraddhā.</p>\r\n<p class="bodytext">rudha-āvaraṇe, rundhīyitthāti ruddhā, oruddhā, avaruddhā.</p>\r\n<p class="bodytext">nipubbo-nirodhe, nirujjhitthāti niruddhā.</p>\r\n<p class="bodytext">vipubbo-virodhe, virujjhitthāti viruddhā, paṭiviruddhā.</p>\r\n<p class="bodytext">anupubbo-kantiyaṃ, anuruddhā.</p>\r\n<p class="bodytext">vidha-vijjhane, vijjhitthāti viddhā. sallaviddhā.</p>\r\n<p class="bodytext">sidha-nipphattiyaṃ, sijjhitthāti siddhā.</p>\r\n<p class="bodytext">papubbo-pākaṭabhāve, pasiddhā.</p>\r\n<p class="bodytext">ni, paṭipubbo nivāraṇe, nisiddhā, paṭisiddhā.</p>\r\n<p class="bodytext">sudha-sujjhane, sujjhatīti suddhā, visuddhā, parisuddhā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54906,'annya_sadda_10',499,replace(replace('\r\n<p class="bodytext">thabhi-thambhane<a name="M0.0499"></a>, thambhatīti thaddhā, patthaddhā, upatthaddhā.</p>\r\n<p class="bodytext">rabha-ārabhe, ārabhitthāti āraddhā, ārabbhitthāti vā āraddhā, samāraddhā.</p>\r\n<p class="bodytext">labha-lābhe, alabhīti laddhā, labbhitthāti vā laddhā, paṭiladdhā, upaladdhā.</p>\r\n<p class="bodytext">lubha-giddhiyaṃ, lubbhatīti luddhā, paluddhā, viluddhā.</p>\r\n<p class="bodytext">sambhū-passaddhiyaṃ, passambhitthāti passaddhā.</p>\r\n<p class="bodytext">duha-dohane, duyhitthāti duddhā-gāvī.</p>\r\n<p class="bodytext">naha-bandhane, sannayhitthāti sannaddhā, onaddhā, avanaddhā.</p>\r\n<p class="bodytext">muha-andhabhāve, muyhatīti muddhā-bālo.</p>\r\n<p class="bodytext">tipaccayamhi-ijjhanaṃ iddhi, ijjhanti etāyāti vā iddhi, samijjhanaṃ samiddhi, gijjhanaṃ giddhi, mijjhanaṃ middhi, abhirādhanaṃ abhiraddhi, virujjhanaṃ viruddhi, paṭiviruddhi, sijjhanaṃ siddhi, saṃsiddhi, paṭisiddhi, sujjhanaṃ suddhi, visuddhi, pārisuddhi, labhanaṃ laddhi, upaladdhi, lubbhanaṃ luddhi, passambhanaṃ passaddhi, muyhanaṃ muddhi.</p>\r\n<p class="bodytext">tavantu, tāvīsu- "samiddhā, samiddhāvī"tiādinā vattabbaṃ.</p>\r\n<p class="bodytext">tvādīsu-rabha-ārabhe, āraddhā, āraddhāna.</p>\r\n<p class="bodytext">labha-lābhe, laddhā, laddhāna, paṭiladdhā, paṭiladdhāna.</p>\r\n<p class="bodytext">tuṃ, tavesu-budha-ñāṇe, buddhuṃ, buddhave, subuddhuṃ, subuddhave, boddhuṃ, boddhave, laddhuṃ, laddhave, paṭiladdhuṃ, paṭiladdhave.</p>\r\n<p class="bodytext">tabbamhi-boddhabbaṃ, laddhabbaṃ, paṭiladdhabbaṃ.</p>\r\n<p class="bodytext"><a name="para751"></a><span class="paranum">751</span>. <span class="bld">vaddhassa vā</span>.</p>\r\n<p class="bodytext">vaddhassa u hoti vā kānubandhe paccayatakāre tvādivajjite.</p>\r\n','\r',char(13)),'\n',char(10)),'-751-');
INSERT INTO pages VALUES(54907,'annya_sadda_10',500,replace(replace('\r\n<p class="bodytext">vaddhitthāti <a name="M0.0500"></a> vuddhā, vaddhā vā, vaddhanaṃ vuddhi, mahāvuttinā uttaṃ. tipaccayassa ca tassa lopo.</p>\r\n<p class="centered">iti dhādesarāsi.</p>\r\n<p class="subhead">visaṃyoganādesarāsi</p>\r\n<p class="bodytext"><span class="bld">hā, i, ci, ji, ṭi, thī, dī,pī, mi, lī, thu, dū, dhū, pū, bhū, lū, vu, su, hu, āsa, katha, kupa, pala, mala, supa, paḷa</span>.</p>\r\n<p class="bodytext"><a name="para752"></a><span class="paranum">752</span>. <span class="bld">bhidādito no kta, ktavantūnaṃ</span>.</p>\r\n<p class="bodytext">bhidādimhā paresaṃ kta, ktavantūnaṃ takārassa anantarabhūtassa no hoti.</p>\r\n<p class="bodytext">hā-cāge, hīyitthāti hīno, pahīno, nihīno, ohīno. ettha ca noādesaṃ katvā pacchā ‘ūbyañjanassā’ti īāgamo, tassa ca kvaci rasso. evaṃ parattha.</p>\r\n<p class="bodytext">adhipubbo-i-āyatte, adhicca etīti adhino.</p>\r\n<p class="bodytext">ci-caye, cayitthāti cino, ācino.</p>\r\n<p class="bodytext">ji-jaye, pañcamāre jinātīti jino.</p>\r\n<p class="bodytext">ḍi-gatiyaṃ, ḍetīti ḍino.</p>\r\n<p class="bodytext">thī-saṅghāte, thīyatīti thinaṃ.</p>\r\n<p class="bodytext">dī-khaye, anukkamena dīyati khiyyatīti dino-divaso.</p>\r\n<p class="bodytext">pī-tappane, pīnitthāti pīno.</p>\r\n<p class="bodytext">mi-pakkhepe, minātīti mino.</p>\r\n<p class="bodytext">lī-laye, līyitthāti līno, allīyitthāti allīno. nilīyitthāti nilīno, paṭilīyitthāti paṭilīno, paṭilīnacaro bhikkhu, paṭisallīyitthāti paṭisallīno.</p>\r\n','\r',char(13)),'\n',char(10)),'-752-');
INSERT INTO pages VALUES(54908,'annya_sadda_10',501,replace(replace('\r\n<p class="bodytext">thu-nitthunane<a name="M0.0501"></a>, nitthunātīti nitthuno.</p>\r\n<p class="bodytext">dū-khede, dūyateti dūno.</p>\r\n<p class="bodytext">dhū-niddhūnane, ahite dhamme dhunātīti dhuno, dhonopaññavā.</p>\r\n<p class="bodytext">pū-sodhane, punātīti puno, dantaṃ punanti etenāti dantapoṇo, nassa ṇattaṃ.</p>\r\n<p class="bodytext">bhū-vuddhiyaṃ, bhavati vaḍḍhatīti bhūno-hitarāsi.</p>\r\n<p class="bodytext">lū-chedane, lunātīti luno.</p>\r\n<p class="bodytext">vu-saṃvaraṇe, āvuṇātīti āvuṇo.</p>\r\n<p class="bodytext">su-savane, suṇātīti suno, soṇo, nassa ṇattaṃ.</p>\r\n<p class="bodytext">su-pasavane vā, pasavati vaḍḍhatīti sunaṃ-uddhumātaṃ.</p>\r\n<p class="bodytext">hu-pūjā, dānesu, āhutabbanti āhunaṃ, pāhutabbanti pāhunaṃ-dātabbavatthu.</p>\r\n<p class="bodytext">āsa-upavesane, acchatīti āsino, tuṇhī acchatīti tuṇhīmāsino.</p>\r\n<p class="bodytext">katha-thaddhe theriye ca, kathatīti kathinaṃ.</p>\r\n<p class="bodytext">kupa-kope, hirī kuppati etenāti hirīkopinaṃ.</p>\r\n<p class="bodytext">pala-gatiyaṃ, paletīti palino.</p>\r\n<p class="bodytext">mala-malinabhāve, malatīti malino, malinaṃ-vatthaṃ.</p>\r\n<p class="bodytext">supa-soppane, supatīti supino.</p>\r\n<p class="bodytext">paḷa-gatiyaṃ, paḷetīti paḷino, paḷinā jambudīpāte <span class="note">[pārā. aṭṭha. 1.tatiyasaṃgītikathā]</span>.</p>\r\n<p class="centered">iti visaṃyoganādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54909,'annya_sadda_10',502,replace(replace('\r\n<p class="subhead">sasaṃyoganādesarāsi</p>\r\n\r\n<p class="bodytext"><span class="bld">khida</span><a name="M0.0502"></a>, <span class="bld">chida, tuda, dā, nuda, pata, pada, bhida, vida, sada</span>.</p>\r\n<p class="bodytext">‘bhidādito no ktaktavantūna’nti tassa no, dhātvantassa pararūpattaṃ, khijjitthāti khinno, chijjitthāti chinno, sañchinno, tuditthāti tunno, patunno, nitunno, vitunno.</p>\r\n<p class="bodytext">nuda-khepe, nuditthāti nunno, panunno.</p>\r\n<p class="bodytext">pata-patane, patatīti panno, pannadhajo, nnassa ṇṇatterukkhapaṇṇaṃ, pattaṃ vā.</p>\r\n<p class="bodytext">pada-gatiyaṃ, pajjitthāti panno, āpanno, uppanno, nipanno, vipanno, sampanno, upapanno, samupapanno, pariyāpanno.</p>\r\n<p class="bodytext">bhida-vidāraṇe, bhijjitthāti bhinno, pabhinno, sambhinno, paribhinno, vibhinno.</p>\r\n<p class="bodytext">vida-tuṭṭhiyaṃ, nibbindatīti nibbinno.</p>\r\n<p class="bodytext">sada visaraṇa, gatyā’vasānesu, sīditthāti sanno, osanno, pasīditthāti pasanno, abhippasanno, nisīditthāti nisinno, sannisinno, ‘sadajarānamīma’iti īma, saṃyoge rasso ca.</p>\r\n<p class="bodytext">tavantumhi-khinnavā, chinnavā, sañchinnavā, tunnavā, patunnavā, panunnavā, pannavā, āpannavā, bhinnavā, sambhinnavā, sannavā, pasannavā, nisinnavā.</p>\r\n<p class="bodytext"><a name="para753"></a><span class="paranum">753</span>. <span class="bld">dātvinno</span> <span class="note">[ka. 582; rū. 631; nī. 1185]</span>.</p>\r\n<p class="bodytext">dādhātumhā paresaṃ ta, tavantūnaṃ tassa inno hoti.</p>\r\n<p class="bodytext">dīyitthāti dinno, padinno, ādinno, samādinno, upādinno, pariyādinno, nnassa ṇṇatte upādiṇṇo.</p>\r\n<p class="centered">iti sasaṃyoganādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-753-');
INSERT INTO pages VALUES(54910,'annya_sadda_10',503,replace(replace('\r\n<p class="subhead">hādesarāsi</p>\r\n\r\n<p class="bodytext"><span class="bld">ūha</span><a name="M0.0503"></a>, <span class="bld">gāhu, guha, baha, bāha, buha, muha, ruha, vaha</span>.</p>\r\n<p class="bodytext"><a name="para754"></a><span class="paranum">754</span>. <span class="bld">ruhādīhi ho ḷo ca</span> <span class="note">[ka. 589; rū. 621; nī. 1192; ‘.ḷa ca’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ruhādīhi parassa anantarabhūtassa tapaccayassa takārassa ho hoti, dhātvantassa ḷo hoti.</p>\r\n<p class="bodytext">ūha-sañcaye, byūhitthāti byūḷho, viyūḷho, paribyūḷho, devāsurasaṅgāmo samupabyūḷho ahosi <span class="note">[saṃ. ni. 1.249]</span>.</p>\r\n<p class="bodytext">gāhu-bhusatthe vilolane ca, mā gāḷhaṃ paridevayi. āgāḷhāya ceteti. gāhitthāti gāḷho, pagāḷho, āgāḷho, ogāḷho, ajjhogāḷho <span class="note">[pārā. aṭṭha. 1.1]</span>.</p>\r\n<p class="bodytext">baha-vuddhimhi.</p>\r\n<p class="bodytext"><a name="para755"></a><span class="paranum">755</span>. <span class="bld">muhabahabuhānañca te kānubandhetve</span> <span class="note">[ka. 517; rū. 488; nī. 1105; "muhabahānañca." (bahūsu)]</span>.</p>\r\n<p class="bodytext">tvādivajjite kānubandhe paccayatakāre pare muha, baha, buhānañca guhassa ca dīgho hoti.</p>\r\n<p class="bodytext">‘ruhādīhiho ḷo cā’ti dhātvantassaḷo, tapaccayassa ho, abahīti bāḷhaṃ.</p>\r\n<p class="bodytext">buha-uddharaṇe, abuhitthāti būḷho, abbūḷho, abūḷhasallo <span class="note">[su. ni. 785]</span>.</p>\r\n<p class="bodytext">muha-andhabhāve.</p>\r\n<p class="bodytext"><a name="para756"></a><span class="paranum">756</span>. <span class="bld">muhā vā</span>.</p>\r\n<p class="bodytext">muhamhā parassa anantarabhūtassa takārassa ho hoti, dhātvantassa ca ḷo hoti vā.</p>\r\n','\r',char(13)),'\n',char(10)),'-754-755-756-');
INSERT INTO pages VALUES(54911,'annya_sadda_10',504,replace(replace('\r\n<p class="bodytext">muyhitthāti <a name="M0.0504"></a> mūḷho, muddhā vā.</p>\r\n<p class="bodytext">ruha-janane, gatiyañca, ruhitthāti rūḷho, parūḷho, ārūḷho, orūḷho, virūḷho, nirūḷho.</p>\r\n<p class="bodytext">vaha-pāpane, vahitthāti vūḷho, ‘assū’ti uttaṃ.</p>\r\n<p class="bodytext">tipaccayamhi-ruhanaṃ rūḷhi, niruhanaṃ nirūḷhi, viruhanaṃ virūḷhi.</p>\r\n<p class="bodytext">tvādīsu –</p>\r\n<p class="bodytext"><a name="para757"></a><span class="paranum">757</span>. <span class="bld">pyo vā tvāssa samāse</span>.</p>\r\n<p class="bodytext">samāsaṭṭhāne tvāpaccayassa pānubandho yo hoti vā. pānubandho ‘pye sissā’ti visesanattho. ‘hassa vipallāso’ti ha, yānaṃ vipariyāyo.</p>\r\n<p class="bodytext">byuyha, paribyuyha. byūhitvā, viyūhitvā vā, vigāyha, vigāhitvā, ogāyha, ogāhitvā, ajjhogāyha, ajjhogāhitvā.</p>\r\n<p class="bodytext">bahulādhikārā asamāsepi pyo hoti, guyha, gūhitvā, niguyha, nigūhitvā, oguyha, ogūhitvā.</p>\r\n<p class="bodytext">naha-bandhane, sannayha, sannāhitvā.</p>\r\n<p class="bodytext">bāha-nivāraṇe, dīgho, bāyha, bāhitvā, paṭibāyha, paṭibāhitvā.</p>\r\n<p class="bodytext">buha-uddharaṇe papubbo, pabbuyha. samūlaṃ taṇhaṃ pabbuyha <span class="note">[saṃ. ni. 1.159 (taṇhamabbumha)]</span>.</p>\r\n<p class="bodytext">āpubbo-abbuyha, "abbuhe sallamattano"ti ādīsu viya. pamuyha, pamuyhitvā, vimuyha, vimuyhitvā, sammuyha, sammuyhitvā, āruyha, āruhitvā, ārohitvā, oruyha, orohitvā.</p>\r\n<p class="bodytext">saha-sahane, pasayha, pasahitvā vā.</p>\r\n<p class="centered">iti hādesarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'-757-');
INSERT INTO pages VALUES(54912,'annya_sadda_10',505,replace(replace('\r\n<p class="subhead">tvādivikārarāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0505"></a> tvā, tvāna, tunānaṃ vikāro vuccate.</p>\r\n<p class="bodytext"><span class="bld">i, kara, hana</span>.</p>\r\n<p class="bodytext"><a name="para758"></a><span class="paranum">758</span>. <span class="bld">ito cco</span>.</p>\r\n<p class="bodytext">idhātumhā parassa tvāssa cco hoti vā.</p>\r\n<p class="bodytext">pecca, samecca, abhisamecca, avecca, anvecca, apecca, upecca, samupecca, adhicca, aticca, paṭicca.</p>\r\n<p class="bodytext">vāti kiṃ? upetvā, samupetvā, adhiyitvā.</p>\r\n<p class="bodytext"><a name="para759"></a><span class="paranum">759</span>. <span class="bld">sādhikarā raccariccā</span> <span class="note">[ka. 598; rū. 643; nī. 1203; ‘sāsādhikarā cacariccā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">santa, adhiparā karamhā tvāssa racca, riccā honti vā, suttavibhattaṃ idha labbhati.</p>\r\n<p class="bodytext">sakkacca, ‘sakkacca’nti bindāgamo, adhikicca.</p>\r\n<p class="bodytext">vāti kiṃ? sakkatvā, sakkaritvā, adhikaritvā.</p>\r\n<p class="bodytext">suttavibhatte-attaṃ niraṃkacca piyāni sevati <span class="note">[jā. 2.21.461]</span>, abhisaṅkhacca bhojanaṃ.</p>\r\n<p class="bodytext"><a name="para760"></a><span class="paranum">760</span>. <span class="bld">hanā racco</span> <span class="note">[ka. 598; rū. 643; nī. 1203. ‘sāsādhikarā cacariccā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">hanamhā tvāssa racco hoti vā samāse. suttavibhattena haramhāpi.</p>\r\n<p class="bodytext">āhacca, uhacca, vihacca, saṃhacca, upahacca.</p>\r\n<p class="bodytext">vāti kiṃ? āhanitvā, uhanitvā, vihanitvā, saṃhanitvā.</p>\r\n','\r',char(13)),'\n',char(10)),'-758-759-760-');
INSERT INTO pages VALUES(54913,'annya_sadda_10',506,replace(replace('\r\n<p class="bodytext">haramhi-sā <a name="M0.0506"></a> no āhacca poseti <span class="note">[jā. 2.22.2334 (āhatva)]</span>, tato udakamāhacca.</p>\r\n<p class="bodytext">disa-pekkhane.</p>\r\n<p class="bodytext"><a name="para761"></a><span class="paranum">761</span>. <span class="bld">disā vānavā sa ca</span> <span class="note">[ka. 599; rū. 644; nī. 1204; ‘.sa ca’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">disamhā tvāssa vāna, vā honti vā, disassa ca sassa sa hoti, pararūpanisedhanamidaṃ.</p>\r\n<p class="bodytext">disvāna, disvā.</p>\r\n<p class="bodytext">vāti kiṃ? passitvā.</p>\r\n<p class="bodytext"><span class="bld">khā, ñā, dā, dhā, hā, ki, khi, ci, ji, nī, lī, si, bhū</span>.</p>\r\n<p class="bodytext">‘pyo vā tvāssa samāse’ti tvāssa yo, mahāvuttinā vikappena kvaci yalopo, saṃpubbo khā-ñāṇe, saṅkhāya, saṅkhā, paṭisaṅkhāya, paṭisaṅkhā, aññāya, aññā, abhiññāya, abhiññā, pariññāya, pariññā.</p>\r\n<p class="bodytext">samāseti kiṃ? ñatvā.</p>\r\n<p class="bodytext">vāti kiṃ? ājānitvā, abhijānitvā, parijānitvā.</p>\r\n<p class="bodytext">adhiṭṭhāya, adhiṭṭhā, patiṭṭhāya, patiṭṭhā.</p>\r\n<p class="bodytext">samāseti kiṃ? ṭhatvā.</p>\r\n<p class="bodytext">vāti kiṃ? adhiṭṭhahitvā, patiṭṭhahitvā. mahāvuttinā ittaṃ, upaṭṭhitvā.</p>\r\n<p class="bodytext">ādāya, upādāya, upādā.</p>\r\n<p class="bodytext">samāseti kiṃ? datvā.</p>\r\n<p class="bodytext">vāti kiṃ? ādiyitvā, samādiyitvā, ‘ū byañjanassā’ti īāgamo, ‘dāssiyaṅa’iti suttena sare pare samāse iyādeso.</p>\r\n<p class="bodytext"><a name="para762"></a><span class="paranum">762</span>. <span class="bld">tuṃyānā</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-761-762-');
INSERT INTO pages VALUES(54914,'annya_sadda_10',507,replace(replace('\r\n<p class="bodytext">tvāssa <a name="M0.0507"></a> tuñca yānañca honti kvaci samāse.</p>\r\n<p class="bodytext">bahulādhikārā gāthāyaṃ asamāsepi, nekkhamaṃ daṭṭhu khemato <span class="note">[su. ni. 426]</span>, kimabbhutaṃ daṭṭhu marū pamoditā, bindulopo.</p>\r\n<p class="bodytext">abhihattuṃ pavāreyya, "abhihaṭu"ntipi pāṭho, saṃyogādissa lopo tassa ṭattaṃ. "abhihaṭṭhu"ntipi <span class="note">[pāci. 2.243]</span> paṭhanti. byañjanaṃ na sameti.</p>\r\n<p class="bodytext">ādiyāna, upādiyāna. vidhāya, nidhāya, sandhāya, odhāya, samodhāya, vidahitvā, nidahitvā, odahitvā, samodahitvā. pahāya, vihāya, ohāya, hitvā, jahitvā.</p>\r\n<p class="bodytext">ivaṇṇesu pyassa dvittaṃ, vikkiyya, vikkiṇitvā.</p>\r\n<p class="bodytext">viceyya dānaṃ dātabbaṃ <span class="note">[pe. va. 329]</span>, ‘ūlasse’ti issa ettaṃ, vicinitvā, vineyya, vinetvā, vinayitvā, allīya, allīyitvā, paṭisallīya, paṭisallīyitvā, yāgamo.</p>\r\n<p class="bodytext"><a name="para763"></a><span class="paranum">763</span>. <span class="bld">pye sissā</span> <span class="note">[ka. 517; rū. 488; nī. 1105]</span>.</p>\r\n<p class="bodytext">pye pare sissa ā hoti.</p>\r\n<p class="bodytext">nissāya, upanissāya, apassāya, apassayitvā, avassāya, avassayitvā.</p>\r\n<p class="bodytext">vāti kiṃ? adhisetvā, adhisayitvā.</p>\r\n<p class="bodytext">bhū-sattāyaṃ, rassattaṃ, sambhuyya, vibhuyya, anubhuyya, adhibhuyya, paribhuyya, abhibhuyya.</p>\r\n','\r',char(13)),'\n',char(10)),'-763-');
INSERT INTO pages VALUES(54915,'annya_sadda_10',508,replace(replace('\r\n<p class="bodytext">samāseti <a name="M0.0508"></a> kiṃ? bhutvā, edantesu mahāvuttinā essa āttaṃ, nijjhāya, nijjhāyitvā, upanijjhāya, upanijjhāyitvā, abhijjhāya, abhijjhāyitvā.</p>\r\n<p class="bodytext">byañjanantadhātūsu ‘vaggalalehi te’ti suttena cavagga, pavagga, sakārehi yassa pubbarūpattaṃ. tavagge ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti tavaggassa cavaggo, tato yassa pubbarūpattaṃ, vipacca, paripacca, vimucca, adhimucca.</p>\r\n<p class="bodytext">mahāvuttinā yalopo dīgho ca, āpucchā, anāpucchā, vibhajja, saṃvibhajja, visajja, nisajja, paṭinisajja.</p>\r\n<p class="bodytext">ujjha-visagge, yalopo, ujjha, ujjhiya, ujjhitvā.</p>\r\n<p class="bodytext">kati-chedane, kacca, vikacca, kantitvā, vikantitvā.</p>\r\n<p class="bodytext">nikara-vañcane, nikacca kitavasseva <span class="note">[saṃ. ni. 1.35]</span>.</p>\r\n<p class="bodytext">pata-gatiyaṃ, pacca, nipacca, patitvā, nipatitvā.</p>\r\n<p class="bodytext">katha-kathane, sākaccha.</p>\r\n<p class="bodytext">pada-gatiyaṃ, pajja, āpajja, nipajja, vipajja, sampajja, upasampajja, paṭipajja.</p>\r\n<p class="bodytext">‘ū byañjanassā’ti īāgame ‘padādīnaṃ yuka’iti yāgamo, pubbarūpaṃ, pajjiya, pajjiyāna, āpajjiya, āpajjiyāna, nipajjiya, vipajjiya, sampajjiya, paṭipajjiya.</p>\r\n<p class="bodytext">āpubba sada-ghaṭṭane, āsajja naṃ tathāgataṃ <span class="note">[itivu. 89]</span>, kākova selamāsajja. chejja, chijja, chindiya, acchijja, acchindiya, vicchijja, vicchindiya, paricchijja, paricchindiya, bhejja, bhijja, sambhijja, paṭisambhijja, bhindiya, sambhindiya.</p>\r\n<p class="bodytext">budha-ñāṇe, bujjha, sambujjha, abhisambujjha, bujjhiya, bujjhiyāna, "marīcikūpamaṃ abhisambuddhāno"ti pāḷi, dādese assa ottaṃ katvā siddhā yathā ‘anupādiyāno’ti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54916,'annya_sadda_10',509,replace(replace('\r\n<p class="bodytext">vadha-hiṃsāyaṃ<a name="M0.0509"></a>, vajjha, vajjhiya.</p>\r\n<p class="bodytext">vidha-tāḷane, vijjha, vijjhiya.</p>\r\n<p class="bodytext">khana-vilekhane, khañña, khaṇiya, nāssa ṇattaṃ.</p>\r\n<p class="bodytext">pavagge –</p>\r\n<p class="bodytext">khippa, nikhippa, saṃkhippa, khipiya, saṃkhipiya.</p>\r\n<p class="bodytext">labi-avasaṃsane, yalopo, ālamba, vilamba, avalamba.</p>\r\n<p class="bodytext">lubi-santhambhane, daṇḍamolumba tiṭṭhati, jhānamolumba vattati.</p>\r\n<p class="bodytext">upubbo uddharaṇe "ullumbatu maṃ bhante saṅgho"ti <span class="note">[mahāva. 71 (ullumpatu)]</span> ādīsu viya, ullumba, ārabbha, samārabbha, labbhā, upalabbhā, dīgho.</p>\r\n<p class="bodytext">pakkamma, akkamma, vikkamma, nikkhamma, okkamma, abhikkamma, atikkamma, paṭikkamma, āgamma, saṅgamma.</p>\r\n<p class="bodytext">sama-upadhāraṇe. nisamma rāja kayirā, nānisamma disampati <span class="note">[jā. 1.4.128 (nisamma khattiyo)]</span>.</p>\r\n<p class="bodytext">samu-santiyaṃ khede ca, upasamma, vūpasamma, vissamma.</p>\r\n<p class="bodytext">kasa-kaḍḍhane, apakassa.</p>\r\n<p class="bodytext">disa-pekkhane, ādissa, uddissa, odissa, apadissa.</p>\r\n<p class="bodytext">phusa-samphasse. phussa phussa byantīkaroti <span class="note">[a. ni. 4.195]</span>.</p>\r\n<p class="bodytext">vasa-nivāse. upavassaṃ kho pana kattikatemāsapuṇṇamaṃ <span class="note">[pārā. 653 (kattikapuṇṇamaṃ)]</span>, bindāgamo.</p>\r\n<p class="bodytext">visa-pavesane, pavissa, nivissa, abhinivissa iccādi.</p>\r\n<p class="centered">iti tvādivikārarāsi.</p>\r\n<p class="centered">paccayavikārarāsi niṭṭhito.</p>\r\n<p class="subhead">pakatirūparāsi</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54917,'annya_sadda_10',510,replace(replace('\r\n<p class="subhead">tādipaccayarāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0510"></a> pakatirūparāsi vuccate.</p>\r\n<p class="bodytext"><span class="bld">ta,ti, tu, tavantu, tāvī, tvā, tvāna, tuna, tuṃ, tave, tāye, tabba</span>.</p>\r\n<p class="bodytext"><span class="bld">dā, khyā, gā, ghā, ṭā, ṭhā, tā, thā, dā, dhā, pā, phā, bhā, mā, yā, lā, vā, sā, hā</span>.</p>\r\n<p class="bodytext">akkhāto, svākkhāto, ākhyāto, vikhyāto.</p>\r\n<p class="bodytext">‘ū byañjanassā’ti kvaci īāgamo rasso ca, saṅgāyito, ghāyito, ñāto.</p>\r\n<p class="bodytext">‘jyādīhi knā’ti nāpaccayo, jānito.</p>\r\n<p class="bodytext">kārite-ñāpito, ñāpayito. puttaṃ tāyati rakkhatīti tāto-pitā. datto, dvittaṃ rassattañca. devadatto, brahmadatto, yaññadatto, dāpito, dāpayito.</p>\r\n<p class="bodytext">mahāvuttinā passa pivo, pivito.</p>\r\n<p class="bodytext">phā-vuddhiyaṃ, phito pabhātā ratti.</p>\r\n<p class="bodytext">mā-māne, mito, sammito, upamito, nimmito, yāto, lāto, vāto.</p>\r\n<p class="bodytext">mahāvuttinā vāssuttaṃ, nibbuto, parinibbuto, nibbāpito, parinibbāpito, osito, pariyosito, osāpito, pariyosāpito, pahito, pajahito, hāpito.</p>\r\n<p class="bodytext">timhi-ñatti, datti, pāti, phāti, nibbuti, parinibbuti.</p>\r\n<p class="bodytext">tumhi-saṅkhātā sabbadhammānaṃ <span class="note">[jā. 2.22.1451]</span>, akkhātāro tathāgatā <span class="note">[dha. pa. 276]</span> aññātāro bhavissanti <span class="note">[dī. ni. 2.68]</span>, ñāpetā, ñāpayitā. uṭṭhātā vindate dhanaṃ <span class="note">[saṃ. ni. 1.246]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54918,'annya_sadda_10',511,replace(replace('\r\n<p class="bodytext">‘ū lasse’ti <a name="M0.0511"></a> kvaci īssa ettaṃ, uṭṭhāpetā, samuṭṭhāpetā, aghassa tātā, tāyitā, dātā, dāpetā, sandhātā, sandhāpetā, māpitā, māpayitā, nibbāpetā, nibbāpayitā, hāpetā, hāpayitā.</p>\r\n<p class="bodytext">tavantu, tāvīsu-akkhatā, akkhātāvī iccādi.</p>\r\n<p class="bodytext">tvādīsu saṃyoge rassattaṃ, ñatvā, jānitvā, ñāpetvā, jānāpetvā, ṭhatvā.</p>\r\n<p class="bodytext">pādito ṭhāssa ṭhaho, saṇṭhahitvā, patiṭṭhahitvā.</p>\r\n<p class="bodytext">kārite kvaci rassattaṃ, ṭhapetvā, paṭṭhapetvā, patiṭṭhāpetvā, datvā, ādiyitvā, samādiyitvā, dajjitvā.</p>\r\n<p class="bodytext">dada-dāne, tvāssa pyo, yamhi dassa jo, yassa pubbarūpaṃ dīgho, dajjā, dāpetvā.</p>\r\n<p class="bodytext">pādito rasso, samādapetvā.</p>\r\n<p class="bodytext">dhā-dhāraṇe, dvittaṃ, pubbassa tatiyattaṃ rasso ca, parassa ‘dhāssa ho’ti ho, padahitvā, vidahitvā, nidahitvā, saddahitvā, odahitvā, pidahitvā, paridahitvā.</p>\r\n<p class="bodytext">kārite-ādhapetvā, sannidhāpetvā, pivitvā, pitvā vā, pāyetvā, māpetvā, osāpetvā, pariyosāpetvā, hitvā, pajahitvā, hāpetvā, pajahāpetvā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54919,'annya_sadda_10',512,replace(replace('\r\n<p class="bodytext">tuṃ, tavesu-akkhātuṃ, akkhātave, saṅkhātuṃ, saṅkhātave, ñātuṃ, ñātave, jānituṃ, jānitave, ñāpetuṃ, ñāpetave, jānāpetuṃ, jānāpetave, ṭhātuṃ, ṭhātave, saṇṭhātuṃ, saṇṭhātave, saṇṭhahituṃ, saṇṭhahitave, ṭhapetuṃ, ṭhapetave, saṇṭhāpetuṃ, saṇṭhāpetave, dātuṃ, dātave, padātuṃ, padātave, ādātuṃ, ādātave, dajjituṃ, dajjitave, dāpetuṃ, dāpetave, samādapetuṃ, samādapetave, sandhātuṃ, sandhātave, saddahituṃ, saddahetuṃ, saddahetave, nidhetuṃ, nidhetave <a name="M0.0512"></a> sandhāpetuṃ, nidhāpetuṃ, pātuṃ, pivituṃ, pātave, pivetave, mātuṃ, minituṃ, pametuṃ, upametuṃ, yātuṃ, yāyituṃ, yātave, osāyetuṃ, osāpetuṃ, pariyosāpetuṃ, hātuṃ, pahātuṃ, māradheyyaṃ pahātave <span class="note">[dha. pa. 34]</span>, jahituṃ, pajahituṃ, hāpetuṃ, pahāpetuṃ, jahāpetuṃ.</p>\r\n<p class="bodytext">tabbamhi-akkhātabbaṃ, saṅkhātabbaṃ, saṅkhyātabbaṃ, gāyitabbaṃ, ñātabbaṃ, jānitabbaṃ, ñāpetabbaṃ, jānāpetabbaṃ, ṭhātabbaṃ, ṭhapetabbaṃ, dātabbaṃ, ādātabbaṃ, samādātabbaṃ, dāpetabbaṃ, samādapetabbaṃ, vidhātabbaṃ, vidahitabbaṃ, pātabbaṃ, pivitabbaṃ, minitabbaṃ, minetabbaṃ, yātabbaṃ, lātabbaṃ, pahātabbaṃ.</p>\r\n<p class="bodytext">ivaṇṇesu vipubbo i-gatyaṃ, vīto, vītadoso vītamoho <span class="note">[saṃ. ni. 1.249]</span>, udito, samudito, dāgamo.</p>\r\n<p class="bodytext">samito, sameto, samaveto, apeto, upeto, samupeto, abhito, kīto, kayito, kiṇito, cito, cinito, ācito, upacito, sañcito, jito, parājito, ḍito, oḍḍito, nīto, ānīto, vinīto, oṇīto, paṇīto, nassa ṇattaṃ.</p>\r\n<p class="bodytext">pīto, bhīto, mito, sito, nissito, pahito.</p>\r\n<p class="bodytext">timhi-samiti, viciti, nīti, dvitte ādivuddhi, netti, saddhammanetti, bhīti.</p>\r\n<p class="bodytext">tumhi-sametā, abhisametā, vicetā, jetā, netā, vinetā, ninnetā.</p>\r\n<p class="bodytext">tavantu, tāvīsu-sametavā, sametāvī, abhisametavā, abhisametāvī iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54920,'annya_sadda_10',513,replace(replace('\r\n<p class="bodytext">tvādīsu-sametvā, upetvā, kiṇitvā, vicinitvā, jetvā, vijetvā, jinitvā, vijinitvā, parājetvā, netvā, ānetvā <a name="M0.0513"></a> vinetvā, nayitvā, ānayitvā, vinayitvā, allīyitvā, paṭisallīyitvā, sayitvā.</p>\r\n<p class="bodytext">tuṃ, tavesu-sametuṃ, upetuṃ, samupetuṃ, sametave, ketuṃ, kiṇituṃ, ketave, vicetuṃ, vicinituṃ, jetuṃ, vijetuṃ, nituṃ, ānituṃ, vinituṃ, netuṃ, ānetuṃ, vinetuṃ, nayituṃ, ānayituṃ, vinayituṃ, netave.</p>\r\n<p class="bodytext">tabbamhi-sametabbaṃ, ketabbaṃ, kiṇitabbaṃ iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu-cuto, cavito.</p>\r\n<p class="bodytext">kārite-cāvito.</p>\r\n<p class="bodytext">juto, javito, thuto, abhitthuto, abhitthavito,</p>\r\n<p class="bodytext">saṃpubbo dhu-vallabhe, sandhuto-mitto. "asaṇṭhutaṃ cirasaṇṭhutenā"tipi pāḷi.</p>\r\n<p class="bodytext">du-gatiyaṃ hiṃsāyañca, duto, upadduto.</p>\r\n<p class="bodytext">dhū-kampane, dhuto, niddhuto.</p>\r\n<p class="bodytext">bhūto, sambhūto.</p>\r\n<p class="bodytext">kārite-bhāvito, sambhāvito, vibhāvito, paribhāvito.</p>\r\n<p class="bodytext">yu-missane, saṃyuto.</p>\r\n<p class="bodytext">ru-sadde, ruto, luto, vuto, saṃvuto, susaṃvuto, suto, vissuto.</p>\r\n<p class="bodytext">hu-pūjāyaṃ, huto.</p>\r\n<p class="bodytext">timhi-cavanaṃ cuti, thavanaṃ thuti, bhūti, vibhūti, savanaṃ suti,</p>\r\n<p class="bodytext">tumhi-cavitā, cāvetā, javitā, thavitā, santhavitā, sotā, sāvetā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54921,'annya_sadda_10',514,replace(replace('\r\n<p class="bodytext">tavantu <a name="M0.0514"></a> tāvīsu-cutavā, cutāvī, cāvetavā, cāvetāvī iccādi.</p>\r\n<p class="bodytext">tvādīsu-cavitvā, cavitvāna, cavituna, javitvā, abhitthavitvā, bhutvā, anubhavitvā, bhāvetvā, bhāvayitvā, sutvā, suṇitvā, sāvetvā, sāvayitvā.</p>\r\n<p class="bodytext">tuṃ, tavesu-cavituṃ, cāvetuṃ, bhotuṃ, bhavituṃ, anubhavituṃ, bhāvetuṃ, bhāvayituṃ, sotuṃ, sāvetuṃ.</p>\r\n<p class="bodytext">hū-sattāyaṃ, hotuṃ. "yā icche puriso hotuṃ <span class="note">[jā. 2.22.1282]</span>. na so sakkā na hotuye"ti <span class="note">[bu. vaṃ. 2.9 ‘.hetuye’]</span> pāḷī.</p>\r\n<p class="bodytext">tabbamhi-cavitabbaṃ, bhavitabbaṃ, anubhavitabbaṃ, bhāvetabbaṃ, sotabbaṃ. dvitte-sottabbaṃ, sāvetabbaṃ.</p>\r\n<p class="bodytext">edantesu mahāvuttinā kvaci ekārassa ittaṃ, gāyito, apacāyito, apacito vā, ujjhāyito, nijjhāyito, abhijjhāyito.</p>\r\n<p class="bodytext">gāyanaṃ gīti, apacāyanaṃ apaciti.</p>\r\n<p class="bodytext">tumhi-gāyitā, apacāyitā, ujjhāyitā.</p>\r\n<p class="bodytext">tavantu, tāvīsu-gāyitavā, gāyitāvī iccādi.</p>\r\n<p class="bodytext">gāyitvā, jhāyitvā, abhijjhāyitvā.</p>\r\n<p class="bodytext">gāyituṃ, gāyitave, apacāyituṃ, apacāyitave, jhāyituṃ, jhāyitave, abhijjhāyituṃ, abhijjhāyitave.</p>\r\n<p class="bodytext">gāyitabbaṃ, apacāyitabbaṃ, ujjhāyitabbaṃ.</p>\r\n<p class="centered">iti ekabyañjanadhātūnaṃ pakatirūparāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54922,'annya_sadda_10',515,replace(replace('\r\n<p class="subhead">bhūvādigaṇa</p>\r\n\r\n<p class="bodytext">anekabyañjanadhātūnaṃ <a name="M0.0515"></a> pakatirūpāni tyādikaṇḍe vibhāganayena bhūvādīhi sattahi dhātugaṇehi ca kāritapaccayehi ca dhātupaccayehi ca yathālābhaṃ vibhajitvā vitthāretabbāni.</p>\r\n<p class="bodytext">atridaṃ nayadassanaṃ –</p>\r\n<p class="bodytext"><span class="bld">āsa, isa, gamu, disa</span>.</p>\r\n<p class="bodytext">āsa-upavesane, cchādesasutte ‘nta māna tyādīsū’ti adhikatattā tapaccayesucchādeso natthi, garuṃ upāsito, payirupāsito.</p>\r\n<p class="bodytext">tumhi-upāsitā, upāsetā vā, upāsitavā, upāsitāvī, upāsitvā, upāsitvāna, upāsituna. ‘pyo vā tvāssa samāse’ti pyādese-upāsiya, payirupāsiya, upasiyāna, upāsituṃ, upāsitave, upāsitabbo.</p>\r\n<p class="bodytext">isu-icchā, kantīsu, bahulādhikārā cchādeso, icchito, icchitā, icchitavā, icchitāvī, icchituṃ, icchitave, icchitabbaṃ.</p>\r\n<p class="bodytext">kārite-icchāpito, icchāpitā, icchāpitāvī, icchāpetvā, icchāpetuṃ, icchāpetave, icchāpetabbaṃ.</p>\r\n<p class="bodytext">esadhātumhi-esito, pariyesito, esitā, pariyesitā, esitavā, pariyesitavā, esitvā, pariyesitvā, esitvāna, pariyesitvāna, esituṃ, pariyesituṃ, esitave, pariyesitave, esitabbaṃ, pariyesitabbaṃ.</p>\r\n<p class="bodytext">gamu-gatimhi, gamito, gamitā, gamitavā, gamitāvī, gamitvā, gamitvāna, gamituna, gamituṃ, gamitave, gamitabbaṃ.</p>\r\n<p class="bodytext">kārite-gamāpito, gamāpetā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54923,'annya_sadda_10',516,replace(replace('\r\n<p class="bodytext">disa-pekkhane <a name="M0.0516"></a> passito, passitā, passetā vā, passitvā, passitvāna, passituna, passituṃ, passitave, passitabbaṃ.</p>\r\n<p class="bodytext">kārite-dassito, dassayito, dassitā, dassetā, dassayitā, dassitavā, dassitāvī, dassetvā, dassayitvā, dassetvāna, dassayitvāna, dassetuṃ, dassayituṃ, dassetabbaṃ.</p>\r\n<p class="bodytext">dakkhādese-dakkhito, dakkhitā, dakkhitavā, dakkhitāvī, dakkhitvā, dakkhituṃ, dakkhitave, "dakkhitāye aparājitasaṅgha"nti <span class="note">[dī. ni. 2.332]</span> pāḷi, dakkhitabbaṃ.</p>\r\n<p class="bodytext">dusa-nāse, kārite ṇipaccaye –</p>\r\n<p class="bodytext"><a name="para764"></a><span class="paranum">764</span>. <span class="bld">ṇimhi dīgho dusassa</span> <span class="note">[ka. 486; rū. 543; nī. 977]</span>.</p>\r\n<p class="bodytext">ṇimhi pare dusassa dīgho hoti.</p>\r\n<p class="bodytext">dūsito, dūsitā, dūsetā, dūsitavā, dūsitāvī, dūsetvā, dūsetvāna, dūsituna, dūsetuṃ, dūsetave.</p>\r\n<p class="bodytext">ṇimhīti kiṃ? duṭṭho.</p>\r\n<p class="centered">iti bhūvādigaṇo.</p>\r\n<p class="subhead">rudhādigaṇa</p>\r\n<p class="bodytext"><span class="bld">bhuja, yuja, chida, bhida, rudha</span>.</p>\r\n<p class="bodytext"><a name="para765"></a><span class="paranum">765</span>. <span class="bld">maṃ vā rudhādīnaṃ</span> <span class="note">[ka. 446; rū. 509; nī. 926]</span>.</p>\r\n<p class="bodytext">rudhādīnaṃ pubbantasarā paraṃ mānubandho niggahītāgamo hoti vā.</p>\r\n<p class="bodytext">bhuñjito, bhuñjitā, bhuñjitavā, bhuñjitāvī, bhuñjitvā, bhuñjitvāna, bhuñjituna, bhuñjituṃ, bhuñjitave, bhuñjitabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-764-765-');
INSERT INTO pages VALUES(54924,'annya_sadda_10',517,replace(replace('\r\n<p class="bodytext">kārite-bhojito<a name="M0.0517"></a>, bhojitā, bhojetā vā, bhojitavā, bhojitāvī, bhojetvā, bhojayitvā, bhojetuṃ, bhojetave, bhojetabbaṃ, yuñjito, anuyuñjito, bhujadhātusadisaṃ.</p>\r\n<p class="bodytext">chindito, chinditā, chindetā vā, chinditavā, chinditāvī, chinditvā, chinditvāna, chindituna. pyādese-lataṃ dantehi chindiya <span class="note">[gavesitabbaṃ]</span>, chindiyāna, sañchindiya, sañchindiyāna, chinditabbaṃ.</p>\r\n<p class="bodytext">kārite-chindāpito, chindāpetā iccādi.</p>\r\n<p class="bodytext">bhindito, bhinditā, bhindetā vā, chidadhātusadisaṃ.</p>\r\n<p class="bodytext">rundhito, rundhitā, rundhetā, rundhitavā, rundhitāvī, rundhitvā, rundhitvāna, rundhituṃ, rundhitave, rundhitabbaṃ.</p>\r\n<p class="bodytext">kārite-rundhāpito, rundhāpayito iccādi.</p>\r\n<p class="centered">iti rudhādigaṇo.</p>\r\n<p class="subhead">divādigaṇa</p>\r\n<p class="bodytext"><span class="bld">pada, budha, tusa, divu</span>.</p>\r\n<p class="bodytext"><a name="para766"></a><span class="paranum">766</span>. <span class="bld">padādīnaṃ kvaci</span>.</p>\r\n<p class="bodytext">padādīnaṃ kvaci yuka hoti, yāgamo, ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti cavaggattaṃ, ‘vaggalasehi te’ti yassa pubbarūpattaṃ.</p>\r\n<p class="bodytext">pajjito, āpajjito, paṭipajjito, paṭipajjitā, paṭipajjitavā, paṭipajjitāvī, paṭipajjitvā, paṭipajjitvāna, paṭipajjituna, paṭipajjituṃ, paṭipajjitave, paṭipajjitabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'-766-');
INSERT INTO pages VALUES(54925,'annya_sadda_10',518,replace(replace('\r\n<p class="bodytext">kārite-āpādito, uppādito, nipphādito, sampādito, paṭipādito, āpāditā, āpādetā, uppāditā, anuppannassa <a name="M0.0518"></a> maggassa uppādetā <span class="note">[apa. therī 2.3.135]</span>, nipphāditā, nipphādetā, sampāditā, sampādetā, paṭipāditā, paṭipādetā, āpādetvā, uppādetvā, nipphādetvā, sampādetvā, paṭipādetvā, āpādetuṃ, uppādetuṃ, nipphādetuṃ, sampādetuṃ, paṭipādetuṃ, āpādetabbaṃ, uppādetabbaṃ, nipphādetabbaṃ, sampādetabbaṃ, paṭipādetabbaṃ, bujjhito, bujjhitā saccānīti buddhā <span class="note">[mahāni. 192]</span>, bujjhitavā, bujjhitāvī, bujjhitvā, bujjhitvāna, bujjhituna, bujjhituṃ, bujjhitave, bujjhitabbaṃ.</p>\r\n<p class="bodytext">kārite-bodhito, bodhetā pajāyāti buddhā <span class="note">[mahāni. 192]</span>, bodhetavā, bodhetāvī, bodhayitvā, bodhayitvāna, bodhetuṃ, bodhetave, bodhetabbaṃ, tussito, santussito, tussitā, tussitavā, tussitāvī, tussitvā, tussituṃ, tussitabbaṃ.</p>\r\n<p class="bodytext">kārite-tosito, tositā, tosetā vā, tositavā, tositāvī, tosetvā, tosetuṃ, tosetabbaṃ, dibbito, dibbitā, dibbitavā, dibbitāvī, dibbitvā, dibbituṃ, dibbitabbaṃ.</p>\r\n<p class="centered">iti divādigaṇo.</p>\r\n<p class="subhead">svādigaṇa</p>\r\n<p class="bodytext"><span class="bld">su, vu, āpa</span>.</p>\r\n<p class="bodytext">suṇito, suṇitā, sotā vā, suṇitavā, suṇitāvī, suṇitvā, suṇituṃ, suṇitabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54926,'annya_sadda_10',519,replace(replace('\r\n<p class="bodytext">kārite-sāvito, sāvetā, sāvetavā, sāvetāvī, sāvetvā, sāvetuṃ, sāvetabbaṃ, saṃvuṇito, āvuṇito, saṃvuṇitā, saṃvuṇitvā, saṃvuṇituṃ, saṃvuṇitabbaṃ, pāpuṇito, pariyāpuṇito, pāpuṇitā, pariyāpuṇitā, pāpuṇitavā, pariyāpuṇitavā, pāpuṇitāvī, pariyāpuṇitāvī, pāpuṇitvā <a name="M0.0519"></a> pariyāpuṇitvā, pāpuṇituṃ, pariyāpuṇituṃ, pāpuṇitabbo, pariyāpuṇitabbo.</p>\r\n<p class="bodytext">kārite-pāpito, pāpitā, pāpetā vā, pāpetvā, pāpetuṃ iccādi.</p>\r\n<p class="centered">iti svādigaṇo.</p>\r\n<p class="bodytext">kiyādigaṇo ekabyañjanesu vutto eva.</p>\r\n<p class="subhead">tanādigaṇa</p>\r\n<p class="bodytext"><span class="bld">kara, tana</span>.</p>\r\n<p class="bodytext">‘karotissa kho’ti kassa kho, abhisaṅkharito, abhisaṅkharitā, abhisaṅkharitavā, abhisaṅkharitāvī, karitvā, karitvāna, abhisaṅkharitvā, abhisaṅkharitvāna, abhisaṅkharituṃ, abhisaṅkharitabbaṃ.</p>\r\n<p class="bodytext">kārite-kārito, kārāpito, kāritā, kāretā, kārāpitā, kārāpetā, kāritavā, kāritāvī, kāretvā, kārāpetvā, kāretuṃ, kārāpetuṃ, kāretabbaṃ, kārāpetabbaṃ.</p>\r\n<p class="bodytext">tanito, tanitvā, tanituṃ iccādi.</p>\r\n<p class="centered">iti tanādigaṇo.</p>\r\n<p class="subhead">curādigaṇa</p>\r\n<p class="bodytext"><span class="bld">kappa, cinta, cura, vida</span>.</p>\r\n<p class="bodytext">kappa-saṅkappane, kappito, saṅkappito, kappayito, saṅkappetā, saṅkappayitā, kappetavā, kappetāvī, kappetvā, kappayitvā, kappetuṃ, kappayituṃ, kappetabbaṃ, kappayitabbaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54927,'annya_sadda_10',520,replace(replace('\r\n<p class="bodytext">kārite-kappāpito <a name="M0.0520"></a> iccādi.</p>\r\n<p class="bodytext">cintito, cintayito, cintetā, cintayitā, cintitavā, cintitāvī, cintetvā, cintayitvā, cintituṃ, cintetuṃ, cintayituṃ, cintitabbaṃ, cintetabbaṃ.</p>\r\n<p class="bodytext">kārite-cintāpito iccādi.</p>\r\n<p class="bodytext">corito, corayito, coretā, corayitā iccādi.</p>\r\n<p class="bodytext">vedito, vedayito, vedetā, vedayitā iccādi.</p>\r\n<p class="centered">iti curādigaṇo.</p>\r\n<p class="bodytext"><span class="bld">titikkha, vīmaṃsa, bubhukkha, pabbatāya</span>.</p>\r\n<p class="bodytext">titikkhito, titikkhitā, titikkhitavā, titikkhitāvī, titikkhitvā, titikkhituṃ, titikkhitabbo.</p>\r\n<p class="bodytext">kārite-titikkhāpito iccādi.</p>\r\n<p class="bodytext">vīmaṃsito, vīmaṃsetā, vīmaṃsitavā, vīmaṃsitāvī, vīmaṃsitvā, vīmaṃsituṃ, vīmaṃsitabbaṃ.</p>\r\n<p class="bodytext">kārite-vīmaṃsāpito iccādi.</p>\r\n<p class="bodytext">bubhukkhito, bubhukkhitā, bubhukkhitavā, bubhukkhitāvī, bubhukkhitvā, bubhukkhituṃ, bubhukkhitabbaṃ.</p>\r\n<p class="bodytext">kārite-bubhukkhāpito iccādi.</p>\r\n<p class="bodytext">pabbatāyito, pabbatāyitā, pabbatāyitavā, pabbatāyitāvī, pabbatāyitvā, pabbatāyituṃ, pabbatāyitabbo.</p>\r\n<p class="bodytext">kārite-pabbatāyāpito iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54928,'annya_sadda_10',521,replace(replace('\r\n<p class="bodytext">evaṃ kukkuccāyito, kukkuccāyitā, kukkuccāyitavā, kukkuccāyitāvī, kukkuccāyitvā, kukkuccāyituṃ, kukkuccāyitabbaṃ<a name="M0.0521"></a>, piyāyito, piyāyitvā, piyāyituṃ iccādīni ca yojetabbāni.</p>\r\n<p class="bodytext">atha visesarāsi vuccate.</p>\r\n<p class="bodytext"><a name="para767"></a><span class="paranum">767</span>. <span class="bld">kattari cārambhe</span> <span class="note">[ka. 556-7; rū. 606, 622; nī. 1143-4]</span>.</p>\r\n<p class="bodytext">ārambho nāma ādikriyā-paṭhamārambho. kriyārambhe vattabbe kattari ca bhāva, kammesu ca kto hoti, etena paccuppannepi kriyāsantāne paṭhamārambhaṃ paṭicca atītavisayo tapaccayo vihito, yathā? bhuttāvī pavāritoti <span class="note">[pāci. 238]</span>.</p>\r\n<p class="bodytext">puriso kaṭaṃ pakato, purisena kaṭo pakato.</p>\r\n<p class="bodytext"><a name="para768"></a><span class="paranum">768</span>. <span class="bld">ṭhāsavasasilisasīruhajarajanīhi</span> <span class="note">[ka. 556; rū. 606, 622; nī. 1143-4]</span>.</p>\r\n<p class="bodytext">ṭhādīhi kattari ca bhāva, kammesu ca kto hoti.</p>\r\n<p class="bodytext">ṭhāmhi-upaṭṭhito garuṃ sisso, upaṭṭhito garu sissena.</p>\r\n<p class="bodytext">āsamhi-upāsito garuṃ sisso, upāsito garu sissena.</p>\r\n<p class="bodytext">vasamhi-anuvusito garuṃ sisso, anuvusito garu sissena.</p>\r\n<p class="bodytext">silisa-āliṅgane, āsiliṭṭho pitaraṃ putto, āsiliṭṭho pitā puttena.</p>\r\n<p class="bodytext">sīmhi-adhisayito ukkhaliṃ jano, adhisayitā ukkhali janena, uddhanaṃ āropitāti attho.</p>\r\n<p class="bodytext">ruhamhi-ārūḷho rukkhaṃ jano, ārūḷho rukkho janena.</p>\r\n<p class="bodytext">jaramhi-anujiṇṇo vasaliṃ devadatto, anujiṇṇā vasalī devadattena, anujāto māṇavikaṃ māṇavo, anujātā māṇavikā māṇavena.</p>\r\n<p class="bodytext"><a name="para769"></a><span class="paranum">769</span>. <span class="bld">gamanatthākammakādhāre ca</span> <span class="note">[ka. 556-7; rū. 606, 622; nī. 1143-4]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-767-768-769-');
INSERT INTO pages VALUES(54929,'annya_sadda_10',522,replace(replace('\r\n<p class="bodytext">gamanatthadhātūhi <a name="M0.0522"></a> akammakadhātūhi ca paraṃ ādhāre ca kattari ca bhāva, kammesu ca kto hoti.</p>\r\n<p class="bodytext">yanti etthāti yātaṃ, idaṃ tesaṃ yātaṃ. padaṃ akkamati etthāti padakkantaṃ, idaṃ tesaṃ padakkantaṃ. iha te yātā, ayaṃ tehi yāto maggo, iha tehi yātaṃ.</p>\r\n<p class="bodytext">akammakamhi-idaṃ tesaṃ āsitaṃ ṭhānaṃ, iha te āsitā, idaṃ tehi āsitaṃ ṭhānaṃ, idha tehi āsitaṃ.</p>\r\n<p class="bodytext"><a name="para770"></a><span class="paranum">770</span>. <span class="bld">āhāratthā</span> <span class="note">[ka. 556-7; rū. 606, 622; nī. 1143-4]</span>.</p>\r\n<p class="bodytext">ajjhoharaṇatthadhātuto kattari ca bhāva, kammesu ca ādhāre ca kto hoti.</p>\r\n<p class="bodytext">iha te bhuttā, asitā, pītā, khāyitā, sāyitā. imāni tehi bhuttāni, asitāni, pītāni, khāyitāni, sāyitāni. iha tesaṃ bhuttaṃ, asitaṃ, pītaṃ, khāyitaṃ, sāyitaṃ. idaṃ tesaṃ bhuttaṃ ṭhānaṃ, asitaṃ ṭhānaṃ, pītaṃ ṭhānaṃ, khāyitaṃ ṭhānaṃ, sāyitaṃ ṭhānaṃ.</p>\r\n<p class="centered">iti tādipaccayarāsi.</p>\r\n<p class="subhead">anīyapaccayarāsi</p>\r\n<p class="bodytext">‘bhāvakammesu tabbānīyā’ti anīyo, anubhuyyatīti anubhavanīyo.</p>\r\n','\r',char(13)),'\n',char(10)),'-770-');
INSERT INTO pages VALUES(54930,'annya_sadda_10',523,replace(replace('\r\n<p class="bodytext">ākārantesu parassaralopo, kvaci yāgamo, upaṭṭhānīyo, dānīyo, padahatīti padhānīyo-yogāvacaro, pātabbanti pānīyaṃ, sāyituṃ arahatīti sāyanīyaṃ, paṭisāyanīyaṃ, pahānīyaṃ, abhitthavanīyaṃ, sotabbanti savanīyaṃ, hutabbanti <a name="M0.0523"></a> havanīyaṃ, upāsanīyo, abhikkamitabboti abhikkamanīyo, rañjetīti rajjanīyo, gantabboti gamanīyo, vuccatīti vacanīyo.</p>\r\n<p class="bodytext"><span class="bld">kara, tara, thara, dhara, sara, hara</span>.</p>\r\n<p class="bodytext"><a name="para771"></a><span class="paranum">771</span>. <span class="bld">rā nassa ṇo</span> <span class="note">[ka. 549; rū. 550; nī. 1135]</span>.</p>\r\n<p class="bodytext">rakārantadhātumhā parassa paccayanakārassa ṇo hoti.</p>\r\n<p class="bodytext">kattabbanti karaṇīyaṃ, taritabbanti taraṇīyaṃ, attharitabbanti attharaṇīyaṃ, dhāretabbanti dhāraṇīyaṃ, sāretabbanti sāraṇīyaṃ, haritabbanti haraṇīyaṃ iccādi.</p>\r\n<p class="bodytext">rundhitabbanti rundhanīyaṃ, bhuñjitabbanti bhuñjanīyaṃ, bhojanīyaṃ, paribhojanīyaṃ, yojetabbanti yojanīyaṃ, dibbitabbanti dibbanīyaṃ, bujjhitabbanti bujjhanīyaṃ, pāpuṇitabbanti pāpanīyaṃ, ñāpetabbanti ñāpanīyaṃ, cintetabbanti cintanīyaṃ, vajjetabbanti vajjanīyaṃ, titikkhitabbanti titikkhanīyaṃ, vīmaṃsitabbanti vīmaṃsanīyaṃ iccādi.</p>\r\n<p class="centered">iti anīyapaccayarāsi.</p>\r\n<p class="subhead">nta, mānapaccayarāsi</p>\r\n<p class="bodytext">atha nta, mānapaccayā vuccante.</p>\r\n<p class="bodytext"><a name="para772"></a><span class="paranum">772</span>. <span class="bld">nto kattari vattamāne</span> <span class="note">[ka. 565; rū. 646; nī. 1157]</span>.</p>\r\n<p class="bodytext">vattamāno vuccati paccuppanno, vattamāne kāle kriyatthā paraṃ kattari nto hoti.</p>\r\n<p class="bodytext">bhū-sattāyaṃ, ‘kattari lo’ti apaccayo, ‘yuvaṇṇānameopaccaye’ti ovuddhi, bhavatīti bhonto-puriso, bhontaṃkulaṃ, bhontī-itthī.</p>\r\n','\r',char(13)),'\n',char(10)),'-771-772-');
INSERT INTO pages VALUES(54931,'annya_sadda_10',524,replace(replace('\r\n<p class="bodytext">puna <a name="M0.0524"></a> ‘eonamayavā sare’ti ossa avādeso, bhavaṃpuriso, bhavantaṃ-kulaṃ, bhavantī, bhavatī, bhotī vā-itthī.</p>\r\n<p class="bodytext"><a name="para773"></a><span class="paranum">773</span>. <span class="bld">māno</span> <span class="note">[ka. 565; rū. 646; nī. 1157]</span>.</p>\r\n<p class="bodytext">vattamāne kāle kriyatthā paraṃ kattari māno hoti.</p>\r\n<p class="bodytext">bhavamāno-puriso, bhavamānaṃ-kulaṃ, bhavamānā-itthī.</p>\r\n<p class="bodytext"><a name="para774"></a><span class="paranum">774</span>. <span class="bld">bhāvakammesu ca</span> <span class="note">[ka. 565; rū. 646; nī. 1157]</span>.</p>\r\n<p class="bodytext">vattamāne kāle kriyatthā paraṃ bhāva, kammesu ca māno hoti. ‘kyo bhāvakammesū.’ti yapaccayo.</p>\r\n<p class="bodytext">anubhūyateti anubhūyamāno bhogo purisena, anubhūyamānā sampatti, anubhūyamānaṃ sukhaṃ.</p>\r\n<p class="bodytext">yassa dvittaṃ, anubhuyyamāno.</p>\r\n<p class="bodytext"><a name="para775"></a><span class="paranum">775</span>. <span class="bld">te ssapubbānāgate</span>.</p>\r\n<p class="bodytext">anāgate kāle vattabbe te nta, mānapaccayā ssapubbā honti.</p>\r\n<p class="bodytext">bhavissatīti bhavissanto <span class="note">[rū. 403-piṭṭhe rūpavidhi passitabbo]</span> -puriso, bhavissantaṃ-kulaṃ, bhavissantī-vibhatti, bhavissatī vā, bhavissamāno, bhavissamānaṃ, bhavissamānā.</p>\r\n<p class="bodytext">kamme-anubhūyissamāno.</p>\r\n<p class="bodytext"><a name="para776"></a><span class="paranum">776</span>. <span class="bld">mānassa massa</span>.</p>\r\n<p class="bodytext">mānapaccayassa massa kvaci lopo hoti.</p>\r\n<p class="bodytext">nisinno vā sayāno <span class="note">[khu. pā. 9.9]</span> vā, sato sampajāno <span class="note">[dī. ni. 1.217]</span>, niccaṃ nalopo.</p>\r\n','\r',char(13)),'\n',char(10)),'-773-774-775-776-');
INSERT INTO pages VALUES(54932,'annya_sadda_10',525,replace(replace('\r\n<p class="bodytext">paññāyanto<a name="M0.0525"></a>, paññāyamāno.</p>\r\n<p class="bodytext">kamme-viññāyamāno.</p>\r\n<p class="bodytext">kārite-ñāpento, ñāpayanto, ñāpayamāno.</p>\r\n<p class="bodytext">kamme-ñāpīyamāno.</p>\r\n<p class="bodytext">kiyādigaṇe-jānanto, jānamāno.</p>\r\n<p class="bodytext">kārite-jānāpento, jānāpayamāno.</p>\r\n<p class="bodytext">kamme-jānāpīyamāno.</p>\r\n<p class="bodytext">tiṭṭhaṃ, tiṭṭhanto, tiṭṭhamāno, saṇṭhahaṃ, saṇṭhahanto, saṇṭhahamāno.</p>\r\n<p class="bodytext">kamme-upaṭṭhīyamāno.</p>\r\n<p class="bodytext">kārite-patiṭṭhāpento, patiṭṭhāpayanto, patiṭṭhāpayamāno.</p>\r\n<p class="bodytext">kamme-patiṭṭhāpīyamāno.</p>\r\n<p class="bodytext">dento, dadanto, dajjanto, samādiyanto, dadamāno, dajjamāno, samādīyamāno.</p>\r\n<p class="bodytext">kamme-dīyamāno, diyyamāno.</p>\r\n<p class="bodytext">kārite-dāpento, dāpayanto, samādapayanto, rasso.</p>\r\n<p class="bodytext">kamme-dāpīyamāno, samādāpīyamāno.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54933,'annya_sadda_10',526,replace(replace('\r\n<p class="bodytext">nidhento, nidahanto, nidahamāno, nidhiyyamāno, nidhāpento, nidhāpayanto, nidhāpayamāno, nidhāpīyamāno, yāyanto. yāyanta’manuyāyanti <span class="note">[jā. 2.22.1753]</span>, yāyamāno mahārājā, addā sīdantare nage <span class="note">[jā. 2.22.566]</span>. vāyanto, vāyamāno, nibbāyanto, parinibbāyanto, nibbāyamāno, nibbāpento, nibbāpayamāno<a name="M0.0526"></a>, nibbāpīyamāno, osāyanto, osāpento, osāpayanto, pahāyanto, pahāyamāno, jahanto, jahamāno, pahīyamāno, pahiyyamāno, jahīyamāno, hāpento, hāpayanto, hāpayamāno, jahāpento, jahāpayanto, jahāpayamāno, hāpīyamāno, jahāpīyamāno.</p>\r\n<p class="bodytext">ivaṇṇesu-vikkayanto, vikkiṇanto, vinicchayanto, vinicchinanto, nito cassa cho.</p>\r\n<p class="bodytext">ācayanto, ācinanto, jayanto, jinanto, ḍento, ḍemāno, nento, vinento, nayanto, vinayanto, nayamāno, niyyamāno, nayāpento, nayāpayamāno, sento, sayanto, semāno, sayamāno, sayāno vā, pahiṇanto, pahiṇamāno.</p>\r\n<p class="bodytext">uvaṇṇesu-cavanto, cavamāno, ṭhānā cāvanto, cāvayanto, cāvayamāno, javanto, javamāno, abhitthavanto, abhitthavamāno, abhitthavīyamāno, sandhavanto, sandhavamāno, dhunanto, niddhunanto, dhunamāno, niddhunamāno, punanto, ravanto, lunanto, āvuṇanto, pasavanto, vissavanto.</p>\r\n<p class="bodytext">kamme-suyyamāno.</p>\r\n<p class="bodytext">kārite-sāvento, sāvayanto.</p>\r\n<p class="bodytext">svādigaṇe-suṇanto, suṇamāno.</p>\r\n<p class="bodytext">kārite-suṇāpento, suṇāpayanto.</p>\r\n<p class="bodytext">hu-pūjāyaṃ, juhonto.</p>\r\n<p class="bodytext">papubbo pahutte, pahonto, sampahonto.</p>\r\n<p class="bodytext">hū-sattāyaṃ, honto.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54934,'annya_sadda_10',527,replace(replace('\r\n<p class="bodytext">edantesu-ento<a name="M0.0527"></a>. atthaṃ entamhi sūriye <span class="note">[jā. 2.22.2187 (atthaṅgatamhi)]</span>, samento, abhisamento, khāyanto, khāyamāno, gāyanto, gāyamāno, gāyīyamāno, gāyāpento, gāyāpayanto, apacāyanto, dhammaṃ apacāyamāno, jhāyanto, jhāyamāno, pajjhāyanto, ujjhāyanto, nijjhāyanto, abhijjhāyanto.</p>\r\n<p class="bodytext">kārite-jhāpento, ujjhāpento, yalopo.</p>\r\n<p class="bodytext">bhāyanto, bhāyamāno, sāliṃ lāyanto, lāyamāno, cīvaraṃ vāyanto, vāyamāno.</p>\r\n<p class="bodytext">kile-kīḷāyaṃ pemane ca, kelāyanto, kelāyamāno, cālento piyāyantoti vā attho.</p>\r\n<p class="bodytext">gile-gelaññe, gilāyanto.</p>\r\n<p class="bodytext">cine-avamaññane, cināyanto.</p>\r\n<p class="bodytext">pale-gatiyaṃ, palāyanto.</p>\r\n<p class="bodytext">mile-hāniyaṃ, milāyanto.</p>\r\n<p class="bodytext">saṅkase-nivāse, saṅkasāyanto iccādi.</p>\r\n<p class="bodytext">anekabyañjanesu-asa-bhuvi, "ntamānāntantiyiyuṃsvādilopo"ti nta, mānesu ādilopo, santo, samāno, upāsanto, upāsamāno, upāsīyamāno, icchanto, icchamāno, icchīyamāno, gacchanto, gacchamāno, gacchīyamāno.</p>\r\n<p class="bodytext">yassa pubbarūpatte-gammamāno, adhigammamāno, anāgate ssapubbo- "labha vasa chida gama bhida rudānaṃ cchaṅa"iti ssena saha dhātvantassa ccho, gacchanto, gamissanto, gacchamāno, gamissamāno, jiranto, jiramāno, jiyyanto, jiyyamāno, dahanto, dahamāno.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54935,'annya_sadda_10',528,replace(replace('\r\n<p class="bodytext">dahassa <a name="M0.0528"></a> dassa ḍo, ḍahanto, ḍahamāno.</p>\r\n<p class="bodytext">kamme-ḍayhamāno.</p>\r\n<p class="bodytext">disa-pekkhane, passanto, passamāno, passīyamāno.</p>\r\n<p class="bodytext">kārite-dassento, dassayanto, dassayamāno.</p>\r\n<p class="bodytext">labhanto, labhamāno.</p>\r\n<p class="bodytext">kamme pubbarūpaṃ, labbhamāno, upalabbhamāno.</p>\r\n<p class="bodytext">anāgate-lacchanto, labhissanto, lacchamāno, labhissamāno.</p>\r\n<p class="bodytext">maranto, maramāno, miyanto, miyamāno.</p>\r\n<p class="bodytext">yamu-uparame, niyamanto, saññamanto, saññamamāno, niyacchanto.</p>\r\n<p class="bodytext">kārite-niyāmento.</p>\r\n<p class="bodytext">rudanto, rodanto, rodamāno.</p>\r\n<p class="bodytext">anāgate-rucchanto, rodissanto, rucchamāno, rodissamāno.</p>\r\n<p class="bodytext">vacanto, vacamāno.</p>\r\n<p class="bodytext">kamme ‘assū’ti uttaṃ, vuccamāno.</p>\r\n<p class="bodytext">kārite-vācento, vācayanto, vācayamāno.</p>\r\n<p class="bodytext">kamme-vācīyamāno.</p>\r\n<p class="bodytext">anāgate-‘vaca bhuja muca visānaṃ kkhaṅa’iti ssena saha dhātvantassa kkhādeso, vakkhanto, vakkhamāno, vadanto, vadamāno, ovadanto, ovadamāno, vajjanto, vajjamāno.</p>\r\n<p class="bodytext">kamme-vadīyamāno, ovadīyamāno, ovajjamāno.</p>\r\n<p class="bodytext">kārite-bheriṃ vādento, vādayamāno.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54936,'annya_sadda_10',529,replace(replace('\r\n<p class="bodytext">vasanto <a name="M0.0529"></a> vasamāno.</p>\r\n<p class="bodytext">kamme pubbarūpattaṃ, upavassamāno.</p>\r\n<p class="bodytext">vāsento, vāsayanto.</p>\r\n<p class="bodytext">anāgate-vacchanto, vasissanto, vacchamāno, vasissamāno.</p>\r\n<p class="bodytext">pavisanto, pavisamāno.</p>\r\n<p class="bodytext">kamme-pavisīyamāno.</p>\r\n<p class="bodytext">anāgate-pavekkhanto, pavisissanto, pavekkhamāno, pavisissamāno iccādi.</p>\r\n<p class="bodytext">rudhādimhi-rundhanto, rundhamāno.</p>\r\n<p class="bodytext">kamme-rundhīyamāno.</p>\r\n<p class="bodytext">pubbarūpatte-rujjhamāno.</p>\r\n<p class="bodytext">rodhento, rodhamāno, rodhīyamāno.</p>\r\n<p class="bodytext">chindanto, chindamāno, chindīyamāno, chijjamāno, chindāpento, chindāpayanto.</p>\r\n<p class="bodytext">anāgate-checchanto, chindissanto, checchamāno, chindissamāno.</p>\r\n<p class="bodytext">bhindanto, bhindamāno, bhindīyamāno, bhijjamāno, bhecchanto, bhindissanto, bhecchamāno, bhindissamāno.</p>\r\n<p class="bodytext">bhuñjanto, bhuñjamāno.</p>\r\n<p class="bodytext">kamme-bhuñjīyamāno.</p>\r\n<p class="bodytext">pubbarūpatte-bhujjamāno.</p>\r\n<p class="bodytext">bhojento, bhojayanto, bhojayamāno, bhojīyamāno.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54937,'annya_sadda_10',530,replace(replace('\r\n<p class="bodytext">anāgate-bhokkhanto<a name="M0.0530"></a>, bhuñjissanto, bhokkhamāno, bhuñjissamāno.</p>\r\n<p class="bodytext">muñcanto, muñcamāno, muñcīyamāno, muccamāno.</p>\r\n<p class="bodytext">anāgate-mokkhanto, muñcissanto, mokkhamāno, muñcissamāno iccādi.</p>\r\n<p class="bodytext">divādimhi suddhakatturūpaṃ suddhakammarūpañca pubbarūpe sadisameva, dibbanto, dibbamāno, dibbīyamāno.</p>\r\n<p class="bodytext">pubbarūpatte-dibbamāno.</p>\r\n<p class="bodytext">chijjanto, chijjamāno, chedāpento, chedāpayamāno.</p>\r\n<p class="bodytext">bujjhanto, bujjhamāno, bujjhīyamāno, bodhento, bodhayanto, bodhayamāno.</p>\r\n<p class="bodytext">muccanto, muccamāno, mocento, mocayanto, mocayamāno, mocīyamāno.</p>\r\n<p class="bodytext">yujjanto, yujjamāno iccādi.</p>\r\n<p class="bodytext">svādimhi-suṇanto, suṇamāno.</p>\r\n<p class="bodytext">kamme-suyyamāno.</p>\r\n<p class="bodytext">kārite-sāvento, sāvayanto, sāvayamāno.</p>\r\n<p class="bodytext">pāpuṇanto, dhammaṃ pariyāpuṇanto, pariyāpuṇamāno, pāpuṇīyamāno, pāpīyamāno.</p>\r\n<p class="bodytext">kārite-pāpento, pāpayanto, pāpayamāno.</p>\r\n<p class="bodytext">pari, saṃpubbo-parisamāpento, parisamāpayanto, parisamāpayamāno, parisamāpīyamāno.</p>\r\n<p class="bodytext">sakkuṇanto, āvuṇanto iccādi.</p>\r\n<p class="bodytext">kiyādimhi-kiṇanto, kiṇāpento, vikkayanto iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54938,'annya_sadda_10',531,replace(replace('\r\n<p class="bodytext">tanādimhi-tanonto<a name="M0.0531"></a>, karonto, kubbanto, kubbamāno, krubbanto, krubbamāno, kurumāno, kayiranto, kayiramāno.</p>\r\n<p class="bodytext">kamme-karīyamāno, kayyamāno, ‘tavaggavaraṇānaṃ ye cavaggabayañā’ti dhātvantassa yādeso.</p>\r\n<p class="bodytext">saṅkharonto, abhisaṅkharonto.</p>\r\n<p class="bodytext">kārite-kārento, kārayanto, kārayamāno, kārīyamāno.</p>\r\n<p class="bodytext">sakkonto iccādi.</p>\r\n<p class="bodytext">curādimhi-corento, corayanto, corayamāno, thenento, thenayanto, thenayamāno, cintento, cintayanto, cintayamāno, cintīyamāno, cintāpento, cintāpayanto, cintāpayamāno, cintāpīyamāno iccādi.</p>\r\n<p class="bodytext">titikkhanto, titikkhamāno, titikkhīyamāno, titikkhāpento, titikkhāpayanto, titikkhāpayamāno, vīmaṃsanto, tikicchanto, cikicchanto, vicikicchanto.</p>\r\n<p class="bodytext">bhuñjituṃ icchatīti bubhukkhanto, ghasituṃ icchatīti jighacchanto, pātuṃ paribhuñjituṃ icchatīti pipāsanto, gottuṃ saṃvarituṃ icchatīti jigucchanto, harituṃ pariyesituṃ icchatīti jigīsanto, vijetuṃ icchatīti vijigīsanto.</p>\r\n<p class="bodytext">pabbato viya attānaṃ caratīti pabbatāyanto, pabbatāyamāno, piyāyanto, mettāyantoiccādīni ca yojetabbāni.</p>\r\n<p class="centered">iti nta, mānapaccayarāsi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54939,'annya_sadda_10',532,replace(replace('\r\n<p class="subhead">ṇyādipaccayarāsi</p>\r\n\r\n<p class="bodytext">atha <a name="M0.0532"></a> ṇya, ya, yakapaccayantā vuccante.</p>\r\n<p class="bodytext"><a name="para777"></a><span class="paranum">777</span>. <span class="bld">ghyaṇa</span>.</p>\r\n<p class="bodytext">bhāva, kammesu gha, ṇānubandho yapaccayo hoti. ghānubandho ‘kagācajānaṃ ghānubandhe’tiādīsu visesanattho. ṇānubandho vuddhidīpanattho. evaṃ sabbattha.</p>\r\n<p class="bodytext">anubhavitabboti anubhāviyo bhogo purisena, anubhāviyaṃ sukhaṃ, anubhāviyā sampatti.</p>\r\n<p class="bodytext"><a name="para778"></a><span class="paranum">778</span>. <span class="bld">āsse ca</span>.</p>\r\n<p class="bodytext">ādantadhātūnaṃ āssa e hoti ghyaṇamhi. casaddena ivaṇṇadhātūnaṃ āgamaīkārassa ca ettaṃ.</p>\r\n<p class="bodytext">akkhātabbaṃ kathetabbanti akkheyyaṃ.</p>\r\n<p class="bodytext">yassa dvittaṃ, saṅkhātabbanti saṅkhyeyyaṃ, saṅkhātuṃ asakkuṇeyyanti asaṅkhyeyyaṃ, gāyitabbanti geyyaṃ-sagāthakaṃ suttaṃ, ghāyituṃ arahatīti gheyyaṃ, ghāyanīyaṃ, apacāyituṃ arahatīti apaceyyaṃ, ñātuṃ arahatīti ñeyyaṃ, ājānituṃ arahatīti aññeyyaṃ, viññeyyaṃ, abhiññeyyaṃ, pariññeyyaṃ.</p>\r\n<p class="bodytext">īāgame-jāniyaṃ, vijāniyaṃ, īssa rasso.</p>\r\n','\r',char(13)),'\n',char(10)),'-777-778-');
INSERT INTO pages VALUES(54940,'annya_sadda_10',533,replace(replace('\r\n<p class="bodytext">jāneyyaṃ, vijāneyyaṃ, adhiṭṭhātabbanti adhiṭṭheyyaṃ, adhiṭṭhaheyyaṃ, dātabbanti deyyaṃ, ādātabbanti ādeyyaṃ, saddahituṃ arahatīti saddaheyyaṃ, vidhātuṃ arahatīti vidheyyaṃ, na vidheyyaṃ avidheyyaṃ-anattalakkhaṇaṃ, mārassa āṇā dahati etthāti māradheyyaṃ, maccudheyyaṃ, sannihitabbanti sannidheyyaṃ, abhidhātabbaṃ kathetabbanti abhidheyyaṃ, pidahitabbanti pidheyyaṃ, alopo, apidheyyaṃ vā, pātabbanti peyyaṃ, minetabbanti meyyaṃ, pametabbanti <a name="M0.0533"></a> pameyyaṃ, upecca minituṃ arahatīti upameyyaṃ, hātabbanti heyyaṃ, paheyyaṃ, pajaheyyaṃ.</p>\r\n<p class="bodytext">ivaṇṇesu-ajjhāyitabbanti ajjheyyaṃ, adhiyeyyaṃ, upetabbanti upeyyaṃ, vikkiṇitabbanti vikkeyyaṃ, vikkāyiyaṃ, vikkāyeyyaṃ, vikkiṇeyyaṃ vā, vicinitabbanti viceyyaṃ, vicineyyaṃ, jetabbanti jeyyaṃ, vijeyyaṃ, netabbanti neyyaṃ, vineyyaṃ, adhisayitabbanti adhiseyyaṃ, pahitabbanti pāheyyaṃ, pahiṇeyyaṃ vā.</p>\r\n<p class="bodytext">uvaṇṇesu vuddhiāvādeso, ku-sadde, kuyyatīti kāveyyaṃ.</p>\r\n<p class="bodytext">īssa abhāve vassa battaṃ rasso ca, kabyaṃ.</p>\r\n<p class="bodytext">pubbarūpatte kabbaṃ, cāvetabbanti cāveyyaṃ, javitabbanti javeyyaṃ, abhitthavitabbanti abhitthaveyyaṃ, bhavituṃ arahatīti bhabbaṃ. juhotabbanti habyaṃ-sappi.</p>\r\n<p class="bodytext">edantesu-apacāyitabbanti apaceyyaṃ, apacāyiyaṃ.</p>\r\n<p class="bodytext">ve-tantasantāne, vetabbanti veyyaṃ.</p>\r\n<p class="bodytext">vaca, bhaja, bhuja, yujādīhi ghyaṇpaccayo.</p>\r\n<p class="bodytext"><a name="para779"></a><span class="paranum">779</span>. <span class="bld">kagā cajānaṃ ghānubandhe</span> <span class="note">[ka. 623; rū. 554; nī. 1229]</span>.</p>\r\n<p class="bodytext">ca, jānaṃ dhātvantānaṃ ka, gā honti ghānubandhe paccaye pare.</p>\r\n<p class="bodytext">vattabbanti vākyaṃ, vākkaṃ, vāccaṃ, vāceyyaṃ vā.</p>\r\n<p class="bodytext">bhaja-sevāyaṃ, bhajitabbanti bhāgyaṃ, bhaggaṃ, bhuñjitabbanti bhogyaṃ, bhoggaṃ, yuñjitabbanti yogyaṃ, yoggaṃ.</p>\r\n<p class="bodytext"><a name="para780"></a><span class="paranum">780</span>. <span class="bld">vadādīhi yo</span> <span class="note">[ka. 541; rū. 552; nī. 1126]</span>.</p>\r\n<p class="bodytext">vadādīhi bhāva, kammesu bahulaṃ yo hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-779-780-');
INSERT INTO pages VALUES(54941,'annya_sadda_10',534,replace(replace('\r\n<p class="bodytext">bhuñjibhabbanti <a name="M0.0534"></a> bhojjaṃ, khāditabbanti khajjaṃ, vituditabbanti vitujjaṃ, panuditabbanti panujjaṃ, pajjitabbanti pajjaṃ, majjati etenāti majjaṃ.</p>\r\n<p class="bodytext">muda-hāse, pamodati etenāti pāmojjaṃ, vadīyatīti vajjaṃ.</p>\r\n<p class="bodytext">vadha-hiṃsāyaṃ, vadhitabbanti vajjhaṃ, vijjhitabbanti vijjhaṃ, punanti sujjhanti sattā etenāti puññaṃ, nāgamo.</p>\r\n<p class="bodytext">vihaññate vihaññaṃ, vapiyateti vappaṃ, supanaṃ soppaṃ, labhitabbanti labbhaṃ, gantabbanti gammaṃ, damituṃ arahatīti dammaṃ, ramitabbanti rammaṃ, abhirammaṃ, nisāmīyate nisammaṃ, visamīyate visammaṃ, phusīyateti phasso, ussa attaṃ.</p>\r\n<p class="bodytext">sāsitabboti sisso, ‘sāsassa sisā’ti sittaṃ.</p>\r\n<p class="bodytext"><span class="bld">gaha, guha, garaha, duha, vaha, saha</span>.</p>\r\n<p class="bodytext"><a name="para781"></a><span class="paranum">781</span>. <span class="bld">guhādīhi yaka</span> <span class="note">[ka. 541; rū. 552; nī. 1126]</span>.</p>\r\n<p class="bodytext">etehi bhāva, kammesu bahulaṃ yaka hoti, hassa vipallāso.</p>\r\n<p class="bodytext">gahetabbanti gayhaṃ, guhitabbanti guyhaṃ.</p>\r\n<p class="bodytext">garaha-nindāyaṃ, garahitabbanti gārayhaṃ, duhitabbanti duyhaṃ, vahitabbanti vayhaṃ.</p>\r\n<p class="bodytext">saha-sāhase, sahitabbanti sayhaṃ, pasayhaṃ.</p>\r\n<p class="bodytext"><a name="para782"></a><span class="paranum">782</span>. <span class="bld">kicca ghacca bhacca gabba lyā</span> <span class="note">[‘.babbaleyyā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">ete saddā yapaccayantā sijjhanti, iminā yapaccayaṃ katvā tena saha karassa kiccaṃ, hanassa ghaccaṃ, bharassa bhaccaṃ, gussa gabbaṃ, lissa lyattaṃ katvā sijjhanti.</p>\r\n','\r',char(13)),'\n',char(10)),'-781-782-');
INSERT INTO pages VALUES(54942,'annya_sadda_10',535,replace(replace('\r\n<p class="bodytext">karīyateti <a name="M0.0535"></a> kiccaṃ, kiccayaṃ vā, haññateti ghaccaṃ, haccaṃ vā, bharīyateti bhaccaṃ.</p>\r\n<p class="bodytext">gu-dabbe, guyate gabbaṃ, paṭisallīyate paṭisalyaṃ.</p>\r\n<p class="bodytext">visesavidhānaṃ –</p>\r\n<p class="bodytext">bhara-bharaṇe, bharitabbanti bhāriyaṃ, haritabbanti hāriyaṃ, bhājetabbanti bhājiyaṃ, bhājeyyaṃ, upāsitabbanti upāsiyaṃ, icchitabbanti iccheyyaṃ, adhigantabbanti adhigameyyaṃ iccādi.</p>\r\n<p class="bodytext">rundhitabbanti rundheyyaṃ, chinditabbanti chindeyyaṃ, chejjaṃ iccādi.</p>\r\n<p class="bodytext">dibbitabbanti dibbeyyaṃ, dibbaṃ, bujjhitabbanti bujjheyyaṃ, bodheyyaṃ, bojjhaṃ iccādi.</p>\r\n<p class="bodytext">sotabbanti suṇeyyaṃ, pāpuṇitabbanti pāpuṇeyyaṃ, sakkuṇitabbanti sakkuṇeyyaṃ, na sakkuṇeyyaṃ asakkuṇeyyaṃ iccādi.</p>\r\n<p class="bodytext">tanitabbanti tāneyyaṃ, taññaṃ, kātabbanti kāriyaṃ, kayyaṃ.</p>\r\n<p class="bodytext">coretabbanti coreyyaṃ, thenīyate theyyaṃ, nassa pararūpattaṃ, cintetabbanti cinteyyaṃ, na cinteyyaṃ acinteyyaṃ, acintiyaṃ, manteyyaṃ, mantiyaṃ, vediyaṃ, vedeyyaṃ iccādi.</p>\r\n<p class="bodytext">titikkheyyaṃ, vīmaṃseyyaṃ iccādi ca yojetabbāni.</p>\r\n<p class="centered">iti ṇyādipaccayarāsi.</p>\r\n<p class="subhead">aādipaccayarāsi</p>\r\n<p class="bodytext">atha a, aṇa, ghaka, ghaṇapaccayantā vuccante.</p>\r\n<p class="bodytext"><a name="para783"></a><span class="paranum">783</span>. <span class="bld">bhāvakārakesvaghaṇaghaka</span> <span class="note">[‘bhāvakārakesvaghaṇaghakā’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">bhāve chasu kārakesu ca kriyatthā paraṃ a, ghaṇa, ghakapaccayā honti kammādimhi vā akammādimhi vā.</p>\r\n<p class="bodytext"><a name="para784"></a><span class="paranum">784</span>. <span class="bld">kvacaṇa</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-783-784-');
INSERT INTO pages VALUES(54943,'annya_sadda_10',536,replace(replace('\r\n<p class="bodytext">kammupapadamhā <a name="M0.0536"></a> kriyatthā paraṃ kattari eva kvaci aṇa hoti.</p>\r\n<p class="bodytext"><span class="bld">a, aṇa, ghaka, ghaṇa</span>.</p>\r\n<p class="bodytext">amhi tāva –</p>\r\n<p class="bodytext">aggaṃ jānātīti aggañño, vaṃsaṃ jānātīti vaṃsañño, magge tiṭṭhatīti maggaṭṭho-puriso, maggaṭṭhā-itthī, maggaṭṭhaṃ-ñāṇaṃ. evaṃ phalaṭṭho, thalaṭṭho, jalaṭṭho, pabbataṭṭho, bhūmaṭṭho.</p>\r\n<p class="bodytext">go vuccati ñāṇaṃ saddo ca, gavaṃ tāyati rakkhatīti gottaṃ, parito bhayaṃ tāyati rakkhatīti parittaṃ, annaṃ detīti annado. evaṃ vatthado, vaṇṇado, yānado, sukhado, dīpado, cakkhudo, dāyaṃ ādadātīti dāyādo, pāraṃ gantuṃ detīti pārado-raso.</p>\r\n<p class="bodytext">annaṃ dadātīti annadado, dvittaṃ pubbassa rasso ca.</p>\r\n<p class="bodytext">purindado, mahāvuttinā purasadde assa ittaṃ bindāgamo ca.</p>\r\n<p class="bodytext">sabbaṃ dadātīti sabbadado, saccaṃ sandhetīti saccasandho, janaṃ sandhetīti janasandho.</p>\r\n<p class="bodytext">kaku vuccati guṇarāsi, kakuṃ sandhetīti kakusandho, gāvopāti rakkhatīti gopo-puriso, gopassa bhariyā gopī.</p>\r\n<p class="bodytext">kassaṃ vuccati khettaṃ, kassaṃ pāti rakkhatīti kassapo.</p>\r\n<p class="bodytext">bhū vuccati pathavī, bhuṃ pāti rakkhatīti bhūpo. evaṃ bhūmipo.</p>\r\n<p class="bodytext">pādena mūlena pathavīrasaṃ āporasañca pivatīti pādapo, suṭṭhu bhāti dibbatīti subho, na mamāyatīti amamo, dve anatthe lāti gaṇhātīti bālo, bahuṃ lāti gaṇhātīti bahulo, rāhu viya lāti gaṇhātīti rāhulo, ādīnaṃ</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54944,'annya_sadda_10',537,replace(replace('\r\n<p class="bodytext">dukkhaṃ <a name="M0.0537"></a> vāti bandhatītiādīnavo, aṇṇaṃ udakarāsiṃ vāti bandhatīti aṇṇavo iccādi.</p>\r\n<p class="bodytext">aṇamhi –</p>\r\n<p class="bodytext"><a name="para785"></a><span class="paranum">785</span>. <span class="bld">āssāṇāpimhi yuka</span>.</p>\r\n<p class="bodytext">ṇāpivajjite ṇānubandhe paccaye pare ādantassa dhātussa ante yuka hoti, yāgamoti attho.</p>\r\n<p class="bodytext">ñātabbo bujjhitabboti ñāyo-yutti, ñāyati amataṃ padaṃ etenāti ñāyo-ariyamaggo, paṭicca tiṭṭhatīti patiṭṭhāyo, dātabboti dāyo-āmisadāyo, dhammadāyo, khīraṃ pivatīti khīrapāyo, dhaññaṃ minātīti dhaññamāyo, vāti gacchatīti vāyo iccādi.</p>\r\n<p class="bodytext">ivaṇṇesu amhi tāva –</p>\r\n<p class="bodytext">eti pavattatīti āyo, sametīti samayo, veti vinassatīti vayo-mandādi, vigamanaṃ vinassanaṃ vayo-bhaṅgo, udayanaṃ udayo, samudayanaṃ samudayo, samudeti phalaṃ etenāti vā samudayo, aticca ayanaṃ pavattanaṃ accayo, paṭicca phalaṃ eti etasmāti paccayo, kiṇanaṃ kayo, vikkiṇanaṃ vikkayo, khīyanaṃ khayo, khīyanti etthāti vā khayo, rāgassa khayo rāgakkhayo, cayanaṃ cayo, ācayo, uccayo, samuccayo, upacayo, dhammaṃ vicinanti etenāti dhammavicayo, jayanaṃ jayo, vijayo, parājayo, niyyati etenāti nayo-vidhi, vineti ettha, etenāti vā vinayo, sukhena netabbo ñātabboti sunayo, dukkhena netabbo ñātabboti dunnayo, pātabboti payo-jalaṃ khīrañca.</p>\r\n','\r',char(13)),'\n',char(10)),'-785-');
INSERT INTO pages VALUES(54945,'annya_sadda_10',538,replace(replace('\r\n<p class="bodytext">ri-kampane, niccaṃ rayanti phandanti dukkhappattā sattā etthāti nirayo, allīyanaṃ ālayo, niliyanaṃ nilayo, sayanaṃ sayo, bhuso senti etthāti āsayo, ajjhāsayo, visesena <a name="M0.0538"></a> senti etthāti visayo, nissāya naṃ seti pavattati etthāti nissayo, upanissayo, anusetīti anusayo iccādi.</p>\r\n<p class="bodytext">aṇamhi –</p>\r\n<p class="bodytext">ayanaṃ vaḍḍhanaṃ āyo, āyamhā apeto apāyo, āyena upeto upāyo, samudeti etthāti samudāyo, samaveti etthāti samavāyo, pariyāyo, vipariyāyo, netabboti nāyo <span class="note">[ñāyo?]</span>, nīyati etenāti vā nāyo, bhūmiyaṃ setīti bhūmisāyo iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu amhi tāva –</p>\r\n<p class="bodytext">cavanaṃ cavo, javanaṃ javo, abhitthavanaṃ abhitthavo, bhusaṃ davati hiṃsatīti upaddavo, sandhavanaṃ sandhavo, mittabhāvena sandhavo mittasandhavo, bhavatīti bhāvo, vibhavanaṃ vibhavo, sambhavanaṃ sambhavo, sambhavati etasmāti vā sambhavo, adhibhavanaṃ adhibhavo, abhibhavo, paribhavo, parābhavanaṃ vinassanaṃ parābhavo, ravatīti ravo-saddo, lunanaṃ lavo, pasavatīti passāvo, āsavatīti āsavo, paṭimukhaṃ savanaṃ paṭissavo iccādi.</p>\r\n<p class="bodytext">aṇamhi –</p>\r\n<p class="bodytext">bhavanaṃ bhāvo, bhavanti sadda, buddhiyo etenāti bhāvo, sāliṃ lunātīti sālilāvo, kucchitena savati sandatīti kasāvo iccādi.</p>\r\n<p class="bodytext">edantesu aṇamhi –</p>\r\n<p class="bodytext">mahāvuttinā essa āyattaṃ, mantaṃ ajjhetīti mantajjhāyo, vajjāvajjaṃ upecca jhāyatīti upajjhāyo-thero, upajjhāyinītherī.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54946,'annya_sadda_10',539,replace(replace('\r\n<p class="bodytext">de-pālane<a name="M0.0539"></a>, attani nilīnaṃ dayati rakkhatīti dāyo, migadāyo, tantaṃ vāyatīti tantavāyo.</p>\r\n<p class="bodytext">vhe-avhāne, <span class="bld">vhī</span>yatīti vhayo-nāmaṃ, rassattaṃ, āpubbo avhayo iccādi.</p>\r\n<p class="bodytext">anekabyañjanesu amhi tāva –</p>\r\n<p class="bodytext">kamanaṃ kamo, pakkamo, abhikkamo, paṭikkamo, caṅkamati etthāti caṅkamo, hitaṃ karotīti hitakkaro, dukkhena kātabboti dukkaro-attho, dukkarā-paṭipadā, dukkaraṃ-kammaṃ, sukhena kātabboti sukaro, īsaṃ kātabboti īsakkaro, dīpaṃ karotīti dīpaṅkaro, aluttasamāso.</p>\r\n<p class="bodytext">āgacchatīti āgamo, āgamanaṃ vā āgamo, saṅgamanaṃ saṅgamo, samāgamo, paggaṇhanaṃ paggaho, saṅgaṇhanaṃ saṅgaho, saṅgayhanti ettha, etenāti vā saṅgaho, anuggaho, paṭiggaho, gāvo caranti etthāti gocaro, kāme avacaratīti kāmāvacaro, uraṃ chādetīti uracchado, jirati etenāti jaro, vessaṃ taratīti vessantaro, aluttasamāso.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54947,'annya_sadda_10',540,replace(replace('\r\n<p class="bodytext">rathe attharatīti rathattharo, assattharo, ariṃ dametīti arindamo, bhagaṃ darati bhindatīti bhagandaro, yugaṃ ravi’ndudvayaṃ dhāretīti yugandharo, dhammaṃ dhāretīti dhammadharo, pajjateti padaṃ, sikkhā eva padaṃ sikkhāpadaṃ, sukhena bharitabboti subharo, dukkhena bharitabboti dubbharo, na maratīti amaro-devo, niyamanaṃ niyamo, saṃyamanaṃ saṃyamo, sirasmiṃ ruhatīti siroruho, sukhena labbhatīti sulabho, dukkhena labbhatīti dullabho, saṃvaritabboti saṃvaro, vuccatīti vaco, subbaco, dubbaco, vāriṃ vahatīti <a name="M0.0540"></a> vārivaho, sarati gacchatīti saro, manaṃ haratīti manoharo iccādi.</p>\r\n<p class="bodytext">aṇamhi –</p>\r\n<p class="bodytext">kamu-icchā, kantīsu, kāmetīti kāmo, kāmīyatīti vā kāmo, atthaṃ kāmetīti atthakāmo, karaṇaṃ kāro, pakāro, ākāro, vikāro, upakāro, apakāro, kumbhaṃ karotīti kumbhakāro, rathakāro, mālakāro, saṅkharaṇaṃ saṅkhāro, saṅkharīyatīti vā saṅkhāro, saṅkharotīti vā saṅkhāro, parikkhāro, purakkhāro, gacchanti pavattanti kāmā etthāti gāmo, gaṇhātīti gāho, pattaṃ gaṇhātīti pattagāho, rasmiṃ gaṇhātīti rasmigāho, vicaraṇaṃ vicāro, upecca caratīti upacāro, gāmaṃ upecca caratīti gāmūpacāro, jirati hirī bhijjati etenāti jāro, kicchena taritabboti kantāro, mahāvuttinā kicchassa kattaṃ, bindāgamo, vāḷakantāro, yakkhakantāro, attharaṇaṃ atthāro, kathinassa atthāro kathinatthāro, darati bhindati kulavibhāgaṃ gacchati etena janenāti dāro, kuṃ pathaviṃ dāretīti kudāro, rassa lo, kudālo.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54948,'annya_sadda_10',541,replace(replace('\r\n<p class="bodytext">bhuso kriyaṃ dhāretīti ādhāro, pattādhāro, paṭisandhāraṇaṃ paṭisandhāro, pajjati etenāti pādo, uppajjanaṃ uppādo, paṭicca samuppajjanaṃ paṭiccasamuppādo, bharitabbo vahitabboti bhāro, sambharīyati sannicīyatīti sambhāro, bodhisambhāro, dabbasambhāro, māretīti māro, kilesamāro, khandhamāro, maccumāro, niyāmetīti niyāmo, dhammaniyāmo, kammaniyāmo, ārūhatīti āroho, rukkhaṃ ārūhatīti rukkhāroho, hatthāroho, assāroho, rathāroho, labbhatīti lābho, paṭilābho, nivaraṇaṃ nivāro, parivāretīti parivāro, vahatīti vāho, āvāho, vivāho, sarati addhānaṃ pavattatīti <a name="M0.0541"></a> sāro, virūpena paṭisaraṇaṃ punappunaṃ cintanaṃ vippaṭisāro, paharaṇaṃ pahāro, āhāro, nīhāro, vihāro, abhihāro, parihāro.</p>\r\n<p class="bodytext"><a name="para786"></a><span class="paranum">786</span>. <span class="bld">hanassa ghāto ṇānubandhe</span> <span class="note">[ka. 591; rū. 544; nī. 1195]</span>.</p>\r\n<p class="bodytext">ṇānubandhe paccaye pare hanassa ghāto hoti.</p>\r\n<p class="bodytext">hananaṃ ghāto, vihaññanaṃ vighāto, upahananaṃ upaghāto, paṭihananaṃ paṭighāto.</p>\r\n<p class="bodytext">ghakapaccaye vuddhi natthi, ‘manānaṃ niggahīta’nti dhātvānubandhassapi nassa niggahītaṃ vagganto ca, ‘kagā cajānaṃ ghānubandhe’ti ghānubandhe paccaye pare dhātvantānañca, jānaṃ ka, gā honti, nipaccatīti nipako.</p>\r\n<p class="bodytext">bhanja-bhijjane vibhāge ca, bhañjanaṃ bhaṅgo, vibhajjanaṃ vibhaṅgo, vibhajīyanti dhammā ettha, etenāti vā vibhaṅgo, khandhavibhaṅgo, dhātuvibhaṅgo.</p>\r\n<p class="bodytext">ranja-rāge, rañjanaṃ raṅgo, rañjanti sattā etthāti raṅgo.</p>\r\n<p class="bodytext">sanja-saṅge, sañjanaṃ saṅgo, pasajjanaṃ lagganaṃ pasaṅgo, āsajjatīti āsaṅgo, uttari āsaṅgo uttarāsaṅgo.</p>\r\n<p class="bodytext">saja-sajjane, atisajjanaṃ sambodhanaṃ atisaggo, gassa dvittaṃ.</p>\r\n<p class="bodytext">nissajjanaṃ nissaggo, paṭinissaggo, vissajjanaṃ vissaggo, saṃsajjanaṃ missīkaraṇaṃ saṃsaggo, yujjati etthāti yugaṃ, kaliyugaṃ, sakaṭayugaṃ, pitāmahayugaṃ, nitudanaṃ nitudo, panudanaṃ panudo, uddhaṃ bhijjatīti ubbhido, kovidatīti kovido, pakārena kujjhatīti pakudho, bujjhatīti budho-paṇḍito, muyhatīti momūho, loluppatīti loluppo, ādidvittaṃ ottañca iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'-786-');
INSERT INTO pages VALUES(54949,'annya_sadda_10',542,replace(replace('\r\n<p class="bodytext">ghaṇapaccaye-pacanaṃ <a name="M0.0542"></a> pāko, paccatīti vā pāko, vipāko, viviccanaṃ viveko, siñcanaṃ seko, abhiseko, socanaṃ soko, cajanaṃ cāgo, bhajanaṃ bhāgo, bhuñjanaṃ bhogo, saha bhogo sambhogo, paribhogo, ābhujanaṃ ābhogo, obhujanaṃ obhogo.</p>\r\n<p class="bodytext">yaja-pūjāyaṃ, yajanaṃ yāgo, āmisayāgo, dhammayāgo, yujjanaṃ yogo, payogo, āyogo, viyogo, anuyogo, upayujjitabboti upayogo, lujjatīti loko, mahāvuttinā gassa kattaṃ, kāmaloko, rūpaloko, saṃvijjanaṃ saṃvego iccādi.</p>\r\n<p class="bodytext"><a name="para787"></a><span class="paranum">787</span>. <span class="bld">anaghaṇasvāparīhi ḷo</span> <span class="note">[ka. 614; rū. 581; nī. 1219]</span>.</p>\r\n<p class="bodytext">ā, parīhi parassa dahassa ḷo hoti ana, ghaṇapaccayesu.</p>\r\n<p class="bodytext">paridayhanaṃ pariḷāho, ‘dahassa dassa ḍo’ti vikappena ḍādeso, dayhanaṃ ḍāho, dāho vā.</p>\r\n<p class="centered">iti aādipaccayarāsi.</p>\r\n<p class="subhead">anapaccayarāsi</p>\r\n<p class="bodytext">atha anapaccayantā vuccante.</p>\r\n<p class="bodytext"><a name="para788"></a><span class="paranum">788</span>. <span class="bld">ano</span>.</p>\r\n<p class="bodytext">bhāve ca chasu kārakesu ca kriyatthā anapaccayo hoti, ādantesu parassaralopo, alope yāgamo.</p>\r\n','\r',char(13)),'\n',char(10)),'-787-788-');
INSERT INTO pages VALUES(54950,'annya_sadda_10',543,replace(replace('\r\n<p class="bodytext">akkhāyate akkhānaṃ, akkhāyati etenāti vā akkhānaṃ, dhammassa akkhānanti dhammakkhānaṃ, paṭisaṅkhāyati pajānāti etenāti paṭisaṅkhānaṃ<a name="M0.0543"></a>, saha gāyanaṃ saṅgāyanaṃ, saha gāyanti sajjhāyanti etthāti vā saṅgāyanaṃ, ñāyate ñāṇaṃ, jānātīti vā ñāṇaṃ, jānanti etenāti vā ñāṇaṃ, paññāyatīti paññāṇaṃ, vijānātīti viññāṇaṃ, saññāṇaṃ, nassa ṇattaṃ.</p>\r\n<p class="bodytext">kārite-ñāpanaṃ, paññāpanaṃ, viññāpanaṃ, saññāpanaṃ.</p>\r\n<p class="bodytext">jānanaṃ, pajānanaṃ, vijānanaṃ, sañjānanaṃ, pubbassaralopo, ṭhīyate ṭhānaṃ, tiṭṭhati etthāti vā ṭhānaṃ.</p>\r\n<p class="bodytext">kārite-ṭhāpanaṃ, patiṭṭhāpanaṃ.</p>\r\n<p class="bodytext">tāyati rakkhatīti tāṇaṃ, parittāṇaṃ, nassa ṇattaṃ.</p>\r\n<p class="bodytext">avatthāyati etthāti avatthānaṃ, dīyate dānaṃ, diyyati etenāti vā dānaṃ, sammā padīyati assāti sampadānaṃ, apecca ādadāti etasmāti apādānaṃ.</p>\r\n<p class="bodytext">kārite-dāpanaṃ, samādapanaṃ.</p>\r\n<p class="bodytext">padahīyate padhānaṃ, padahanti etenāti vā padhānaṃ, ādhānaṃ, vidhānaṃ, nidhānaṃ, sannidhānaṃ.</p>\r\n<p class="bodytext">kārite-sannidhāpanaṃ.</p>\r\n<p class="bodytext">pānaṃ, paṭibhānaṃ, māṇaṃ, pamāṇaṃ, upamāṇaṃ, parimāṇaṃ, nassa ṇattaṃ.</p>\r\n<p class="bodytext">yāyati etenāti yānaṃ, uyyānaṃ, niyyānaṃ, vāyanti bhavābhavaṃ gacchanti etenāti vānaṃ, natthi vānaṃ etthāti nibbānaṃ, nibbāyanti etthāti vā nibbānaṃ.</p>\r\n<p class="bodytext">kārite-nibbāpanaṃ.</p>\r\n<p class="bodytext">avasānaṃ, osānaṃ, pariyosānaṃ, pahānaṃ, parihānaṃ.</p>\r\n<p class="bodytext">kārite-hāpanaṃ, parihāpanaṃ.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54951,'annya_sadda_10',544,replace(replace('\r\n<p class="bodytext">ivaṇṇesu-ayanaṃ <a name="M0.0544"></a> vikkayanaṃ, vikkiṇanaṃ, khayanaṃ, khiyanaṃ, khiyyanaṃ, iya, iyyādeso, cayanaṃ, cinanaṃ, ācinanaṃ, vicinanaṃ, jayanaṃ, vijayanaṃ, līyanti etthāti leṇaṃ, nassa ṇattaṃ.</p>\r\n<p class="bodytext">paṭisallīyanti etthāti paṭisallānaṃ, issa āttaṃ. seti etthāti senaṃ, sayanaṃ.</p>\r\n<p class="bodytext">kārite-sayāpanaṃ iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu-cavanaṃ, javanaṃ, abhitthavanaṃ, dhunanaṃ, viddhunanaṃ, niddhunanaṃ, bhavanaṃ, abhibhavanaṃ, lavanaṃ, lunanaṃ, savanaṃ, pasavanaṃ iccādi.</p>\r\n<p class="bodytext">edantesu-ajjhenaṃ, ajjhāyanaṃ, apacāyanaṃ, jhāyate jhānaṃ, jhāyati etenāti vā jhānaṃ, paṭhamajjhānaṃ, dutiyajjhānaṃ, ujjhānaṃ, nijjhānaṃ, abhijjhānaṃ, sālilāyanaṃ, cīvaravāyanaṃ, gilāyatīti gilāno iccādi.</p>\r\n<p class="bodytext">‘rā nassa ṇo’ti suttena rakāramhā parassa nassa ṇo, kāraṇaṃ, adhikarīyati etthāti adhikaraṇaṃ, saṅkharaṇaṃ, abhisaṅkharaṇaṃ.</p>\r\n<p class="bodytext">kārite-kārāpanaṃ.</p>\r\n<p class="bodytext">ākiraṇaṃ, vikkiraṇaṃ, caraṇaṃ, jiraṇaṃ, taraṇaṃ, kaṅkhāvitaraṇaṃ, attharaṇaṃ, āgantvā dahanti ettha matasarīranti āḷahanaṃsusānaṃ, dassa ḷo.</p>\r\n<p class="bodytext">passīyate passanaṃ, dassanaṃ, suṭṭhu passatīti sudassanorājā, suṭṭhu passitabbanti sudassanaṃ-devanagaraṃ, sandassanaṃ, nidassanaṃ, dhāraṇaṃ, uddhāraṇaṃ, niddhāraṇaṃ, ādidīgho.</p>\r\n<p class="bodytext">pūraṇaṃ, paripūraṇaṃ, pharaṇaṃ, vippharaṇaṃ.</p>\r\n<p class="bodytext">kārite-māraṇaṃ.</p>\r\n<p class="bodytext">nivāraṇaṃ, saraṇaṃ, nissaraṇaṃ, haraṇaṃ, āharaṇaṃ, nīharaṇaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54952,'annya_sadda_10',545,replace(replace('\r\n<p class="bodytext">sāmaññavidhānattā <a name="M0.0545"></a> saddattha, kujjhanattha, calanatthadhātūhi ruca, juta, vaḍḍhādidhātūhi ca tassīlādīsu ano hoti, ghosati sīlenāti ghosano, ghosati dhammenāti ghosano, ghosati sādhukārenāti ghosano, kodhano, dūsano, padūsano, kopano, calano, phandano, kampano, maṇḍano, bhūsano, vibhūsano, rocano, virocano, verocano, jotano, ujjotano, vaḍḍhano, karoti sīlenāti karaṇo. rāgo nimittakaraṇo, doso nimittakaraṇo, moho nimittakaraṇo <span class="note">[saṃ. ni. 4.349]</span> iccādi.</p>\r\n<p class="bodytext"><a name="para789"></a><span class="paranum">789</span>. <span class="bld">karā ṇano</span>.</p>\r\n<p class="bodytext">karamhā kattari ṇānubandho ano hoti.</p>\r\n<p class="bodytext">karoti attano phalanti kāraṇaṃ.</p>\r\n<p class="bodytext"><a name="para790"></a><span class="paranum">790</span>. <span class="bld">hāto vīhikālesu</span>.</p>\r\n<p class="bodytext">vīhismiṃ kāle ca vattabbe hādhātumhā kattari ṇānubandho ano hoti.</p>\r\n<p class="bodytext">hāpetīti hāyano, vīhiviseso vassañca. "kuñjaraṃ saṭṭhihāyana"nti ettha vassaṃ hāyananti vuccati.</p>\r\n<p class="centered">iti anapaccayarāsi.</p>\r\n<p class="subhead">akapaccayarāsi</p>\r\n<p class="bodytext">atha akapaccayantā vuccante.</p>\r\n<p class="bodytext"><a name="para791"></a><span class="paranum">791</span>. <span class="bld">āsīsāyamako</span> <span class="note">[‘āsiṃsāmako’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">āsīsā vuccati patthanā, āsīsāyaṃ gamyamānāyaṃ ako hoti kattari.</p>\r\n','\r',char(13)),'\n',char(10)),'-789-790-791-');
INSERT INTO pages VALUES(54953,'annya_sadda_10',546,replace(replace('\r\n<p class="bodytext">jīvatūti <a name="M0.0546"></a> jīvako, nandatūti nandako. "jinabuddhi, dhanabhūti, bhūto, dhammadinno, vaḍḍhamāno"ti ete saddā aññathā sijjhanti, jino imaṃ bujjhatūti jinabuddhi, dhanaṃ etassa bhavati vaḍḍhatīti dhanabhūti, bhavati vaḍḍhatīti bhūto, dhammena dinno dhammadinno, yathā devadatto, brahmadatto, vaḍḍhatīti vaḍḍhamānoti.</p>\r\n<p class="bodytext">‘kattari ltuṇakā’ti ṇako, so ca sāmaññavidhānattā arahe sattiyaṃ sīle dhamme sādhukāre ca sijjhati, akkhāyatīti akkhāyako, ‘āssāṇāpimhi yuka’iti yāgamo, akkhātuṃ arahati, sakkoti, akkhānamassa sīlaṃ, dhammo, akkhānaṃ sakkaccaṃ karotīti attho. kālattayepi sijjhati, pubbepi akkhāsi, ajjapi akkhāti, pacchāpi akkhāyissatīti attho. evaṃ sesesu sāmaññavidhīsu yathārahaṃ veditabbo.</p>\r\n<p class="bodytext">itthiyaṃ-‘adhātussa ke.’ti suttena akassa assa ittaṃ, akkhāyikā-itthī, akkhāyakaṃ-kulaṃ, saṅgāyako, jānātīti jānako.</p>\r\n<p class="bodytext">vikaraṇapaccayato paraṃ nāgame sati vikaraṇassa rasso, jānanako, ājānanako, vijānanako, sañjānanako.</p>\r\n<p class="bodytext">kārite-ñāpetīti ñāpako, viññāpako, saññāpako.</p>\r\n<p class="bodytext">nāgame-ñāpanako, viññāpanako, saññāpanako, adhiṭṭhātīti adhiṭṭhāyako, adhiṭṭhāpetīti adhiṭṭhāpako, detīti dāyako, dāpetīti dāpako.</p>\r\n<p class="bodytext">ṇāpimhi yāgamo natthi, samādapetīti samādapako, ubhayattha rasso. vidhetīti vidhāyako, pajahatīti pajahāyako, avahiyyatīti ohiyako, āssa ittaṃ iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54954,'annya_sadda_10',547,replace(replace('\r\n<p class="bodytext">ivaṇṇesu-ajjhetīti <a name="M0.0547"></a> ajjhāyako, mantaṃ ajjhetīti mantajjhāyako, kiṇātīti kāyako, kiṇāpetīti kāyāpako, ācinātīti ācinako, vicinako, parājayatīti pārājiko, alopo, puggalo, parājetīti pārājiko, alopo kāritalopo ca, dhammo, bhāyāpetīti bhayānako, nāgamo ādirasso ca.</p>\r\n<p class="bodytext">bhūmiyaṃ setīti bhūmisāyako, sayāpetīti sayāpako, pāhetīti pahiṇako iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu-punāti sodhetīti pāvako-aggi, bhavatīti bhāvako, vibhāvetīti vibhāvako, lunātīti lāvako, suṇātīti sāvako-puriso, sāvikā-itthī, juhotīti hāvako iccādi.</p>\r\n<p class="bodytext">apa-pāpuṇane, pāpetīti pāpako, sampāpetīti sampāpako, upāsatīti upāsako-puriso, upāsikā-itthī, upāsakaṃ-kulaṃ, karotīti kārako, kārikā, kārakaṃ, upakārako, kāretīti kārāpako, saṅkharotīti saṅkhārako, abhisaṅkhārako, khipatīti khipako, ukkhipako, nikkhipako, khepako, ukkhepako, nikkhepako, nāgamekhipanako.</p>\r\n<p class="bodytext">gaṇhātīti gāhako, gaṇhāpetīti gāhāpako. evaṃ gopako, pādamūle caratīti pādacārako, pupphaṃ ocināyatīti ocināyako, edanto dhātu.</p>\r\n<p class="bodytext">chindatīti chedako, chindako, chedāpetīti chedāpako, chindāpako, janetīti janako-puriso, janikāmātā, janakaṃ-kammaṃ, kāritalopo.</p>\r\n<p class="bodytext">jhāpa-dāhe, jhāpetīti jhāpako.</p>\r\n<p class="bodytext">ñapa-paññāpane, paññapetīti paññāpako.</p>\r\n<p class="bodytext">ṭhāpa-ṭhāne, patiṭṭhāpetīti patiṭṭhāpako.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54955,'annya_sadda_10',548,replace(replace('\r\n<p class="bodytext">ṇāpa-pesane<a name="M0.0548"></a>, āṇāpetīti āṇāpako, tudatīti tudako, santussatīti santussako, visesena passatīti vipassako, sandassetīti sandassako, dūsetīti dūsako, ādidīgho.</p>\r\n<p class="bodytext">pacatīti pācako, pācetīti pācāpako, āpādetīti āpādako, nipphādako, sampādako, paṭipajjako, paṭipādako, pūretīti pūrako, garupantattā na vuddhi.</p>\r\n<p class="bodytext">phusatīti phusako, tudādittā na vuddhi.</p>\r\n<p class="bodytext">bhājetīti bhājako, bhindatīti bhindako, bhedako, kārabhedako, bhuñjatīti bhuñjako, bhojako, gāmabhojako, bujjhatīti bujjhako, bodhako, maratīti miyyako, māretīti mārako, muñcatīti muñcako, mocako, yācatīti yācako, yajatīti yājako, yuñjatīti yuñjako, anuyuñjako, yojako, payojako, yujjhatīti yujjhako, yodhako, rundhatīti rundhako, avarodhako, vacatīti vācako, ovadatīti ovādako, ovajjako, vīṇaṃ vādetīti vīṇāvādako, bherivādako, garuṃ abhivādetīti abhivādako, vidatijānātīti vedako, vindati paṭilabhatīti vindako, anuvijjati vicāretīti anuvijjako, paṭisaṃvedetīti paṭisaṃvedako, vijjhatīti vedhako, aṭṭhiṃ vijjhatīti aṭṭhivedhako, pattaṃ vijjhatīti pattavedhako.</p>\r\n<p class="bodytext">bahulādhikārā kammepi dissati, antare vāsīyati nivāsīyatīti antaravāsako, pasīdatīti pasīdako, pasādako vā, dīpappasādako, udakappasādako, sibbatīti sibbako, sevatīti sevako, hanatīti ghātako, gāvo hanatīti goghātako, hanassa ghāto. haratīti hārako.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54956,'annya_sadda_10',549,replace(replace('\r\n<p class="bodytext">kamme <a name="M0.0549"></a> – "pādehi paharīyatīti pādapahārako"ti vuttiyaṃ vuttaṃ. titikkhatīti titikkhako, tikicchatīti tikicchako, vīmaṃsatīti vīmaṃsako, bubhukkhatīti bubhukkhako, pabbatāyatīti pabbatāyako iccādi.</p>\r\n<p class="centered">iti akapaccayarāsi.</p>\r\n<p class="subhead">ivaṇṇantarūparāsi</p>\r\n<p class="bodytext">atha ivaṇṇantarūpāni vuccante.</p>\r\n<p class="bodytext"><a name="para792"></a><span class="paranum">792</span>. <span class="bld">dādhātvi</span> <span class="note">[ka. 551; rū. 598; nī. 1138]</span>.</p>\r\n<p class="bodytext">dā, dhāhi bhāvakārakesu ipaccayo hoti.</p>\r\n<p class="bodytext">paṭhamaṃ cittena ādīyatītiādi, taṇhādiṭṭhīhi upādīyatīti upādi, khandhupādi, kilesupādi, vidhānaṃ vidhi, vidhiyyati etenāti vidhi, nidhiyyatīti nidhi, sandhiyate sandhi, abhisandhi, paṭisandhi, sannidahanaṃ sannidhi, samādhānaṃ samādhi, samādahanti etenāti samādhi, paṇidahanaṃ paṇidhi, odhi, avadhi, upanidhi, paṭinidhi, udakaṃ dahati tiṭṭhati etthāti udadhi, mahanto udadhi mahodadhi, vālāni dahanti tiṭṭhanti etthāti vāladhi.</p>\r\n<p class="bodytext"><a name="para793"></a><span class="paranum">793</span>. <span class="bld">ikitī sarūpe</span> <span class="note">[ka. 669; rū. 679; nī. 1315]</span>.</p>\r\n<p class="bodytext">dhātūnaṃ sutisaṅkhāte sarūpe vattabbe kriyatthā paraṃ i, ki, tipaccayā honti.</p>\r\n<p class="bodytext">avaṇṇupantehi i, gami, paci iccādi.</p>\r\n<p class="bodytext">uvaṇṇupantehi ki, budhi, rudhi iccādi.</p>\r\n<p class="bodytext">kehiciti, karotissa, atthissa iccādi.</p>\r\n<p class="bodytext"><a name="para794"></a><span class="paranum">794</span>. <span class="bld">sīlābhikkhaññāvassakesu ṇī</span> <span class="note">[ka. 532, 636; rū. 590, 659; nī. 1114, 1245]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-792-793-794-');
INSERT INTO pages VALUES(54957,'annya_sadda_10',550,replace(replace('\r\n<p class="bodytext">sīlaṃ <a name="M0.0550"></a> vuccati pakaticariyā, abhikkhaṇameva abhikkhaññaṃ, punappunakriyā, āyatiṃ avassaṃbhāvī avassakaṃ nāma, sīlaggahaṇena dhamma, sādhukārāpi saṅgayhanti, etesu sīlādīsu kriyāvisesesu gamyamānesu kattari ṇī hoti. ādantesu ‘āssāṇāpimhi yuka’iti yāgamo.</p>\r\n<p class="bodytext">akkhāyatīti akkhāyī, akkhāyanasīlo, akkhāyanadhammo, akkhāne sakkaccakāritā yuttoti attho. kālattayepi sijjhati sāmaññavidhānattā.</p>\r\n<p class="bodytext">avassakaṃ pana anāgatameva, dhammakkhāyī-puriso, dhammakkhāyinī-itthī, dhammakkhāyi-kulaṃ, gītaṃ abhiṇhaṃ gāyatīti gītagāyī, kappaṃ avassaṃ ṭhāssatīti kappaṭṭhāyī, saṃvaṭṭamānaṃ asaṅkhyeyyaṃ ṭhāssatīti saṃvaṭṭaṭṭhāyī. evaṃ vivaṭṭaṭṭhāyī.</p>\r\n<p class="bodytext">adinnaṃ ādadāti sīlenāti adinnādāyī. tathā dinnameva ādadātīti dinnādāyī, annaṃ dadāti sīlenāti annadāyī.</p>\r\n<p class="bodytext">dā-suppane. niddāyanasīlo niddāyī, majjaṃ pivanasīlo majjapāyī, majjaṃ abhiṇhaṃ pivatīti majjapāyī, sīghaṃ yāyanasīlo sīghayāyī, sasaṅkhārena sappayogena avassaṃ parinibbāyissatīti sasaṅkhāraparinibbāyī. tathā asaṅkhāraparinibbāyī, āyukappassa antare vemajjhe avassaṃ parinibbāyissatīti antarāparinibbāyī, āyukappapariyosānaṃ upahacca avassaṃ parinibbāyissatīti upahaccaparinibbāyī iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54958,'annya_sadda_10',551,replace(replace('\r\n<p class="bodytext">ivaṇṇesu-mantaṃ niccakālaṃ ajjhāyatīti mantajjhāyī, dhammajjhāyī, dhaññaṃ niccakālaṃ vikkiṇātīti dhaññavikkāyī, bhāyanasīlo bhāyī, bhūmiyaṃ sayanasīlo, bhūmiyaṃ vā niccakālaṃ sayatīti <a name="M0.0551"></a> bhūmisāyī, kaṇṭake apassayanasīlo kaṇṭakāpassayī iccādi.</p>\r\n<p class="bodytext">edantāpi idha vattabbā, uddhaṃ vaḍḍhanasīlo udāyī, vuddhesu apacāyanasīlo vuddhāpacāyī. evaṃ jeṭṭhāpacāyī, jhāyanasīlo, jhāyanadhammo, jhāyane sakkaccakriyāyuttoti jhāyī, niccakālaṃ jhāyatīti vā jhāyī, pajjhāyī, ujjhāyī, nijjhāyī, abhijjhāyī, bhāyanasīlo bhāyī, tiṇaṃ abhiṇhaṃ lāyatīti tiṇalāyī, tantaṃ niccakālaṃ vāyatīti tantavāyī, palāyanasīlo palāyī, na palāyī apalāyī iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu-yathābhūtaṃ atthaṃ vibhāvanasīlo vibhāvīpuriso, vibhāvinī-itthī, āyatiṃ avassaṃ bhavissatīti bhāvī, sāliṃ lunāti sīlenāti sālilāvī iccādi.</p>\r\n<p class="bodytext">byāpanasīlo byāpī, kāmeti icchati sīlenāti kāmī, dhammakāmī, atthakāmī, karaṇasīlo kārī, pāpakārī, puññakārī.</p>\r\n<p class="bodytext">avassaṃ āgamissatīti āgāmī. rassatte-āgaminīratti, āgaminī-puṇṇamāsī, ācayaṃ vaṭṭaṃ gacchati sīlenāti ācayagāmī, apacayaṃ vivaṭṭaṃ gacchati sīlenāti apacayagāmī, sakiṃ avassaṃ āgamissatīti sakadāgāmī. tathā na āgamissatīti anāgāmī.</p>\r\n<p class="bodytext">ādhānaṃ vuccati daḷhaṭṭhiti, ādhānaṃ katvā gahaṇasīlo ādhānagāhī, daḷhagāhī, dhammaṃ carati sīlenāti dhammacārī, brahmaṃ seṭṭhaṃ carati sīlenāti brahmacārī.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54959,'annya_sadda_10',552,replace(replace('\r\n<p class="bodytext">apicettha dhammo nāma kulācāradhammo, taṃ dhammaṃ carāmīti daḷhaṃ gaṇhitvā yāva na vijahati, tāva avītikkamanaṭṭhena dhammaṃ carati sīlenāti dhammacārī nāma. tathācaranto ca antarāvītikkamanīyavatthusamāyoge sati taṃ dhammaṃ apatamānaṃ katvā dhārento <a name="M0.0552"></a> saṃvaraṇaṭṭhena dhammaṃ carati dhammenāti dhammacārī nāma, tathādhārento ca taṃ dhammaṃ attukkaṃsana, paravambhanādīhi pāpadhammehi anupakkiliṭṭhañca appicchatādīhi santaguṇehi supariyodātañca karonto pariyodāpanaṭṭhena dhammaṃ carati sādhukārenāti dhammacārī nāma.</p>\r\n<p class="bodytext">brahmaṃ vuccati tato seṭṭhataraṃ sikkhāpadasīlaṃ, tampi gaṇhitvā avijahanto antarā ca apatamānaṃ katvā dhārento anupakkiliṭṭhaṃ supariyodātañca karonto tividhena atthena brahmacārī nāma, samādāna, sampatta, samucchedaviratīnaṃ vasena viyojetuṃ vaṭṭati, yo pana gaṇhanto tathā na dhāreti, dhārento vā upakkiliṭṭhaṃ karoti, so ekadesena atthena brahmacārī nāma.</p>\r\n<p class="bodytext">yo pana tividhena atthena mutto hutvā kadāci taṃ dhammaṃ carati, tassa caraṇakriyā tassīlakriyā na hoti, so dhammacārīti na vuccati, etenupāyena sesesu pāpa, kalyāṇabhūtesu tassīlapadesu atthavibhāgo veditabbo.</p>\r\n<p class="bodytext">brahmacārinī-itthī, visesena dassanasīlo vipassī, atthadassī, dhammadassī, piyadassī, sudassī, dussanasīlo dussīmāro, dhāraṇasīlo dhārī, iṇadhārī, chattadhārī, bhusaṃ nahanasīlo upanāhī, pariniṭṭhitapaccayekadesattā āyatiṃ avassaṃ uppajjissatīti uppādī, uppādino dhammā <span class="note">[dha. sa. tikamātikā 17]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54960,'annya_sadda_10',553,replace(replace('\r\n<p class="bodytext">bhara-dhāraṇe, mālaṃ niccakālaṃ bharatīti mālabhārī, bhājanasīlo bhājī, uṇhaṃ bhuñjanasīlo uṇhabhojī, attānaṃ maññati sīlenāti attamānī, attānaṃ paṇḍitaṃ maññatīti paṇḍitamānī, labhanasīlo lābhī, vacanasīlo vācī. evaṃ vādī, atthavādī, dhammavādī, yuttavādī, muttavādī, vibhajjavādī, niccaṃ vasatīti vāsī, gāmavāsī, nagaravāsī, bhāraṃ vahanasīlo bhāravāhī<a name="M0.0553"></a>, dhammaṃ paññaṃ anusarati anugacchatīti dhammānusārī. evaṃ saddhānusārī, virūpaṃ pāpapakkhaṃ paṭimukhaṃ abhiṇhaṃ sarati cintetīti vippaṭisārī, pāṇaṃ hanati sīlenāti pāṇaghātī, hanassa ghāto.</p>\r\n<p class="bodytext">haritabbaṃ sabbaṃ harati sīlenāti hārahārī iccādi.</p>\r\n<p class="bodytext">‘kagā cajāna’nti suttavibhattiyā aghānubandhepi cajānaṃ kagādeso, samaṃ vipācetīti samavepākī-udaraggi, samavepākinī-gahaṇī, upadhi phalaṃ vipaccatīti upadhivepākinī, socanasīlo sokī, sokinī-pajā, mutto hutvā cajanasīlo muttacāgī, saṃvibhājanasīlo saṃvibhāgī, kāmasukhaṃ bhuñjanasīlo kāmabhogī, visuṃ avibhattaṃ bhogaṃ bhuñjanasīlo apaṭivibhattabhogī, yuñjanasīlo yogī iccādi.</p>\r\n<p class="bodytext"><a name="para795"></a><span class="paranum">795</span>. <span class="bld">āvī</span> <span class="note">[ka. 532; rū. 590; nī. 1114]</span>.</p>\r\n<p class="bodytext">āvī hoti kattari.</p>\r\n<p class="bodytext">bhayaṃ dassanasīlo bhayadassāvī.</p>\r\n<p class="centered">iti ivaṇṇantarūparāsi.</p>\r\n<p class="subhead">uvaṇṇantarūparāsi</p>\r\n<p class="bodytext"><a name="para796"></a><span class="paranum">796</span>. <span class="bld">bhaṅgu bhīrū bhāsu assavā</span>.</p>\r\n<p class="bodytext">ete saddā mahāvuttinā sīlādīsu nipaccante.</p>\r\n<p class="bodytext">bhanja-vināse, pabhañjanasīlo pabhaṅgu-saṅkhatadhammo.</p>\r\n<p class="bodytext">bhī-bhaye, bhāyanasīlo bhīrū.</p>\r\n<p class="bodytext">bhā-dittiyaṃ, obhāsanasīlo bhāsu-pabhā, jeṭṭhavacanaṃ ādarena suṇāti sīlenāti assavo-putto, assavābhariyā.</p>\r\n<p class="bodytext"><a name="para797"></a><span class="paranum">797</span>. <span class="bld">vidā kū</span> <span class="note">[ka. 535; rū. 593; nī. 1119]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-795-796-797-');
INSERT INTO pages VALUES(54961,'annya_sadda_10',554,replace(replace('\r\n<p class="bodytext">vidamhā <a name="M0.0554"></a> kū hoti kattari.</p>\r\n<p class="bodytext">vidati sīlenāti vidū, lokavidū, paracittavidū. itthiyaṃ paracittavidunī.</p>\r\n<p class="bodytext"><a name="para798"></a><span class="paranum">798</span>. <span class="bld">vito ñāto</span> <span class="note">[ka. 535; rū. 593; nī. 1119]</span>.</p>\r\n<p class="bodytext">vipubbā ñāto kū hoti kattari.</p>\r\n<p class="bodytext">vijānanasīlo viññū.</p>\r\n<p class="bodytext"><a name="para799"></a><span class="paranum">799</span>. <span class="bld">kammā</span> <span class="note">[ka. 535; rū. 593; nī. 1119]</span>.</p>\r\n<p class="bodytext">kammupapadā ñāto kū hoti kattari.</p>\r\n<p class="bodytext">sabbaṃ jānāti sīlenāti sabbaññū, rattaññū, atthaññū, dhammaññū, kālaññū, samayaññū.</p>\r\n<p class="bodytext"><a name="para780"></a><span class="paranum">780</span>. <span class="bld">gamā rū</span> <span class="note">[ka. 534; rū. 592; nī. 1118]</span>.</p>\r\n<p class="bodytext">kammupapadā gamamhā rū hoti kattari. ‘rānubandhentasarādissā’ti sabbadhātvantalopo.</p>\r\n<p class="bodytext">pāraṃ gacchati sīlenāti pāragū, vedaṃ vuccati aggamaggañāṇaṃ, vedaṃ gacchatīti vedagū, addhānaṃ gacchatīti addhagū.</p>\r\n<p class="centered">iti uvaṇṇantarūparāsi.</p>\r\n<p class="subhead">itthiliṅgarūparāsi</p>\r\n<p class="bodytext">atha itthiliṅgarūpāni vuccante.</p>\r\n<p class="bodytext"><a name="para801"></a><span class="paranum">801</span>. <span class="bld">itthiyamaṇatikayakayā ca</span> <span class="note">[ka. 553; rū. 599; nī. 1140; ‘.kti.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">itthiliṅge vattabbe bhāvakārakesu a, ṇa,ti, ka, yaka, yapaccayā ca ano ca hoti.</p>\r\n','\r',char(13)),'\n',char(10)),'-798-799-780-801-');
INSERT INTO pages VALUES(54962,'annya_sadda_10',555,replace(replace('\r\n<p class="bodytext">ka<a name="M0.0555"></a>, a, ṇa, yaka, ya, anaiccetehi ‘itthiyamatvā’ti āpaccayo.</p>\r\n<p class="bodytext">kamhi tāva-attani nisinnaṃ gūhati saṃvaratīti guhā, attānaṃ vā paraṃ vā dūsetīti dūsā-dhuttitthī.</p>\r\n<p class="bodytext">muda-hāse, modanaṃ mudā, pamudā, sujjhati etāyāti sudhā, vasuṃ ratanaṃ dhāretīti vasudhā iccādi.</p>\r\n<p class="bodytext">amhi-saṅkhāyanti etāyāti saṅkhā. tathā saṅkhyā, pajānātīti paññā, ājānātīti aññā, sañjānātīti saññā, sañjānanti etāyāti vā saññā, sañjānanaṃ vā saññā, abhijānanaṃ abhiññā, paṭijānanaṃ paṭiññā, paricchijja jānanaṃ pariññā, paṭicca tiṭṭhati etthāti patiṭṭhā.</p>\r\n<p class="bodytext">thā-ṭhāne, avadhibhāvena ṭhāti tiṭṭhatīti avatthā, upādīyatīti upādā-paññatti, aññamaññaṃ upecca nissāya ca dhiyyatīti upanidhā-paññattiyeva, saddahanaṃ saddhā, saddahanti etāyāti vā saddhā, visiṭṭhaṃ katvā attānaṃ dahanti etāyāti vidhā-māno, bhāti dibbatīti bhā-nakkhattaṃ, pabhā, ābhā, nibhā, upamīyate upamā.</p>\r\n<p class="bodytext">jhe-cintāyaṃ, pajjhāyanaṃ pajjhā, vajjāvajjaṃ upajjhāyati pekkhatīti upajjhā, abhimukhaṃ jhāyanaṃ abhijjhā.</p>\r\n<p class="bodytext">āsa-patthanāyaṃ, āsīsanaṃ āsā, paccāsīsanaṃ paccāsā.</p>\r\n<p class="bodytext">āsa-upavesane, acchanaṃ acchā.</p>\r\n<p class="bodytext">ikkha-dassana’ṅkesu, apekkhanaṃ apekkhā, upekkhanaṃ upekkhā, upaparikkhanaṃ upaparikkhā, icchanaṃ icchā, byāpituṃ icchā vicchā.</p>\r\n<p class="bodytext">issa-ussuyyiye, issanaṃ issā.</p>\r\n<p class="bodytext">īha-byāpāre, īhanaṃ īhā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54963,'annya_sadda_10',556,replace(replace('\r\n<p class="bodytext">uchi-ucche<a name="M0.0556"></a>, ucchanaṃ ucchā.</p>\r\n<p class="bodytext">ela-kampane, elayatīti elā-doso.</p>\r\n<p class="bodytext">oja-thambhane tejane ca, ojeti taṃsamaṅgine satte saṅkhāre ca samupatthambhati samuttejetīti vā ojā.</p>\r\n<p class="bodytext">kala-saṅkhyāne, kalīyatīti kalā, khamanaṃ khamā, gajjanti etāyāti gadā, girīyati kathīyatīti girā-vācā, ghaṭīyati saṅghaṭīyati etthāti ghaṭā-yūtho, bhuso cāreti paricāretīti accharā-devī, mahāvuttinā cassa cho, jaṭatīti jaṭā, antojaṭā bahijaṭā <span class="note">[saṃ. ni. 1.23]</span>, jiyyanti etāyāti jarā, jiraṇaṃ vā jarā, āpajjati ajjhāpajjatīti āpadā, sampajjanaṃ sampadā, uparibhāvaṃ suṭṭhu pajjanti pāpuṇanti etāyāti upasampadā, paṭipajjanaṃ paṭipadā, paṭipajjanti uparivisesaṃ etāyāti vā paṭipadā, sukhappaṭipadā, dukkhappaṭipadā, paṭisaṃbhijjanti atthādīsu ñāṇappabhedaṃ gacchanti etāyāti paṭisambhidā, atthapaṭisambhidā, dhammapaṭisambhidā, niruttipaṭisambhidā, paṭibhānapaṭisambhidā.</p>\r\n<p class="bodytext">bhikkha-yācane, bhikkhīyateti bhikkhā.</p>\r\n<p class="bodytext">sikkha-ghaṭane, sikkhanaṃ sikkhā, sikkhanti ghaṭenti sekkhā janā etthāti sikkhā, adhisīlasikkhā, adhicittasikkhā, adhipaññāsikkhā iccādi.</p>\r\n<p class="bodytext">titikkhanaṃ titikkhā, vīmaṃsanaṃ vīmaṃsā, tikicchanaṃ tikicchā, vigatā tikicchā etissāti vicikicchā, bhottuṃ icchā bubhukkhā, bubhokkhā vā, ghasituṃ icchā jighacchā, pātuṃ paribhuñjituṃ icchā pipāsā, harituṃ icchā jigīsā, vijetuṃ icchā vijigīsā, hantuṃ icchā jighaṃsā iccādi.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54964,'annya_sadda_10',557,replace(replace('\r\n<p class="bodytext">ṇamhi-ara-gatiyaṃ, arati sīghaṃ vijjhamānā gacchatīti ārāvedhako, karonti nānākammakāraṇāyo etthāti kārā-addu<a name="M0.0557"></a>, jiyyanti etāyāti jārā, tarati sīghataraṃ gacchatīti tārā, bhāsanti etāyāti bhāsā, dhāreti sīghaṃ vahatīti dhārā, khaggadhārā, vuṭṭhidhārā, mayati vividhākāraṃ gacchati etāyāti māyā, likhīyate lekhā, vuccateti vācā, harati manoramaṃ pavattetīti hārā-muttāvali iccādi.</p>\r\n<p class="bodytext">yakpaccaye-saha kathanaṃ sākacchā, tathanaṃ tacchā, nipajjanaṃ nipajjā, vidati jānātīti vijjā, vidanti jānanti etāyāti vā vijjā, vijjāpaṭipakkhā avijjā, nisajjanaṃ nisajjā.</p>\r\n<p class="bodytext">idha-ijjhane, samijjhanaṃ samijjhā.</p>\r\n<p class="bodytext">sidha-nipphattiyaṃ, sijjhanaṃ sijjhā iccādi.</p>\r\n<p class="bodytext">yamhi-maja-saṃsuddhiyaṃ, sammajjanaṃ sammajjā, apecca vajanaṃ gamanaṃ pabbajjā, caraṇaṃ cariyā, paricaraṇaṃ pāricariyā, ‘ūbyañjanassā’ti īāgamo rasso ca.</p>\r\n<p class="bodytext">jāgara-niddakkhaye, jāgaraṇaṃ jāgariyā, seti etthāti seyyā, dvittaṃ.</p>\r\n<p class="bodytext">anamhi-saha gāyanti sajjhāyanti etthāti saṅgāyanā, ṭhāpīyate patiṭṭhīyate patiṭṭhānā, pāpīyate pāpanā, sampāpanā, parisamāpanā, upāsīyate upāsanā, payirūpāsanā, esīyate esanā, pariyesanā, kāmesanā, bhavesanā, brahmacariyesanā, chejjabhejjādikassa kammassa karaṇaṃ kammakāraṇā, ādivuddhi, dvattiṃsa kammakāraṇā.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54965,'annya_sadda_10',558,replace(replace('\r\n<p class="bodytext">citi-cetāyaṃ, cetenti sampayuttā dhammā etāyāti cetanā, cintīyate cintanā, ṭhapīyate ṭhapanā, dīpīyate dīpanā, vipassanti etāyāti vipassanā, sandassīyate sandassanā, desīyate desanā, desīyati etāyāti vā desanā, patthīyate patthanā, pharīyate pharaṇā, phusīyate phusanā, bhāvīyate bhāvanā, vibhāvanā, sambhāvanā, mantīyate mantanā, nimantanā<a name="M0.0558"></a>, āmantanā, punappunaṃ modanti etāyāti anumodanā, yācīyate yācanā, ādarena yācanā āyācanā, yojīyate yojanā.</p>\r\n<p class="bodytext">raca-vidhāne, racīyate racanā, āracanā, viracanā.</p>\r\n<p class="bodytext">vaṭṭa-vaṭṭane, āvaṭṭanā, vivaṭṭanā, vedīyate vedanā.</p>\r\n<p class="bodytext">vara-icchāyaṃ, pavārīyate icchāpīyate pavāraṇā, vāsīyate vāsanā, āsīsīyate āsīsanā, hiṃsīyate hiṃsanā iccādi.</p>\r\n<p class="bodytext">timhi-bahulādhikārā anitthiyampiti hoti, gāyanaṃ gīti, saha gāyanaṃ saṅgīti, duggīti, anugīti, ayaṃ amhākaṃ abbhantarimoti ñāyatīti ñāti, jānanaṃ ñatti, dvittaṃ, ṭhānaṃ ṭhiti.</p>\r\n<p class="bodytext">dā-avakhaṇḍane, dīyati etāyāti datti, dvittaṃ, dhāretīti dhāti, dahanaṃ akampanaṃ dhīti, samādahanaṃ samādhīti, mahāvuttinā āssaṃ īttaṃ, dvitte dhassa dattaṃ rasso ca, dīdhiti-raṃsi.</p>\r\n<p class="bodytext">nibbāyanaṃ nibbuti, āssa uttaṃ, saha ayanaṃ samiti, eti āgacchatīti īti-upaddavo, vicinanaṃ viciti, vijayanaṃ vijiti, nīyati ñāyati etāyāti nīti, lokanīti, dhammanīti, saddanīti, bhavaṃ netīti bhavanetti, vuddhi dvittañca, saddhammanetti, pinayatīti pīti, bhāyanaṃ bhīti, cavanaṃ cuti, javanaṃ juti, thavanaṃ thuti, abhitthuti, pavanaṃ pūti, bhavanaṃ bhūti, suṭṭhu bhavatīti subhūti, vibhavanaṃ vibhūti, suṭṭhu munanaṃ bandhanaṃ sammuti, savanaṃ suti, suyyateti vā suti, pasuti, upasuti, hūyateti huti, ānetvā hutabbāti āhuti, cāyanaṃ pūjanaṃ citi, dvitte-citti, apaciti, essa attaṃ, nijjhāyanaṃ nijjhatti.</p>\r\n','\r',char(13)),'\n',char(10)),'');
INSERT INTO pages VALUES(54966,'annya_sadda_10',559,replace(replace('\r\n<p class="bodytext">mahāvuttinā takāre karassa kuttaṃ, kriyā kutti, sarakutti, itthikuttaṃ, purisakuttaṃ, janetīti janetti, īāgamassa ettaṃ<a name="M0.0559"></a>, bandhīyateti bandhati, pajjatīti patti, padāti vā, īāgamassa āttaṃ, vasanti etthāti vasati-gehaṃ, vasanaṃ vā vasati iccādi.</p>\r\n<p class="bodytext"><a name="para802"></a><span class="paranum">802</span>. <span class="bld">jāhāhi ni</span>.</p>\r\n<p class="bodytext">etehi ni hoti.</p>\r\n<p class="bodytext">jā-hāniyaṃ, jiyyate jāni, dhanajāni, bhogajāni, mahantī jāni assāti mahājāniyo, hiyyate hāni, vaṇṇahāni, balahāni, āyuhāni, avahāni, parihāni.</p>\r\n<p class="bodytext"><a name="para803"></a><span class="paranum">803</span>. <span class="bld">karā ririyo</span> <span class="note">[ka. 554; rū. 601; nī. 1141]</span>.</p>\r\n<p class="bodytext">karamhā itthiyaṃ ririyo hoti.</p>\r\n<p class="bodytext">karīyate kiriyā, nipātanena kriyāti sijjhati.</p>\r\n<p class="centered">iti itthiliṅgarūparāsi.</p>\r\n<p class="subhead">rīrikkhādipaccayarāsi</p>\r\n<p class="bodytext"><a name="para804"></a><span class="paranum">804</span>. <span class="bld">samānaññabhavantayāditūpamānā disā kamme rīrikkhakā</span> <span class="note">[ka. 642; rū. 588; nī. 1269]</span>.</p>\r\n<p class="bodytext">samāno ca añño ca bhavanto ca yādi ca etehi upamānabhūtehi paraṃ disamhā kammerī ca rikkho ca ko cāti ete paccayā honti, rī, rikkhesu ‘rānubandhentasarādissā’ti suttena disassa antassarādīnaṃ lopo, kānubandho avuddhattho, ‘rīrikkhakesu’iccādīhi samāsasuttehi pubbapadānaṃ rūpaṃ sādhetabbaṃ.</p>\r\n<p class="bodytext"><span class="bld">ya, ta, eta, ima, kiṃ, tumha, amha, bhavanta, samāna, añña</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-802-803-804-');
INSERT INTO pages VALUES(54967,'annya_sadda_10',560,replace(replace('\r\n<p class="bodytext">yo viya dissatīti yādī, yādikkho, yādiso, yaṃ viya naṃ passantīti yādī, ye viya dissantīti yādino, itthiyaṃ-yā <a name="M0.0560"></a> viya dissatīti yādinī, yādikkhā, yādikkhī, yādisā, yādisī, yā viya dissantīti yādiniyo, yādikkhāyo, yādikkhiyo, yādisāyo, yādisiyo. evaṃ sesesupi.</p>\r\n<p class="bodytext">so viya dissatīti tādī, tādikkho, tādiso.</p>\r\n<p class="bodytext">eso viya dissatīti edī, edikkho, ediso, etādī, etādikkho, etādiso vā.</p>\r\n<p class="bodytext">ayaṃ viya dissatīti īdī, īdikkho, īdiso.</p>\r\n<p class="bodytext">ko viya dissatīti kīdī, kīdikkho, kīdiso.</p>\r\n<p class="bodytext">tvaṃ viya dissatīti tādī, tādikkho, tādiso.</p>\r\n<p class="bodytext">ahaṃ viya dissatīti mādī, mādikkho, mādiso.</p>\r\n<p class="bodytext">bahutte pana tumhe viya dissantīti tumhādī, tumhādikkho, tumhādiso.</p>\r\n<p class="bodytext">amhe viya dissantīti amhādī, amhādikkho, amhādiso.</p>\r\n<p class="bodytext">bhavaṃ viya dissatīti bhavādī, bhavādikkho, bhavādiso.</p>\r\n<p class="bodytext">samāno viya dissatīti sadī, sadikkho, sadiso.</p>\r\n<p class="bodytext">añño viya dissatīti aññādī, aññādikkho, aññādiso.</p>\r\n<p class="bodytext">itthiyaṃ-yā viya dissatīti yādisā-itthī, yādisī-itthī. tādisā-itthī, tādisī-itthī iccādi.</p>\r\n<p class="bodytext"><a name="para805"></a><span class="paranum">805</span>. <span class="bld">vamādīhi thu</span> <span class="note">[ka. 644; rū. 661; nī. 1271-3; ‘vamādīhyathu’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">vamādīhi bhāvakārakesu thu hoti.</p>\r\n<p class="bodytext">vamīyateti vamathu, davīyateti davathu iccādi.</p>\r\n<p class="bodytext"><a name="para806"></a><span class="paranum">806</span>. <span class="bld">kvi</span> <span class="note">[ka. 530; rū. 584; nī. 1112]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-805-806-');
INSERT INTO pages VALUES(54968,'annya_sadda_10',561,replace(replace('\r\n<p class="bodytext">bhāvakārakesu <a name="M0.0561"></a> kvi hoti.</p>\r\n<p class="bodytext"><a name="para807"></a><span class="paranum">807</span>. <span class="bld">kvissa</span> <span class="note">[ka. 639; rū. 585; nī. 1266]</span>.</p>\r\n<p class="bodytext">kvissa lopo hoti.</p>\r\n<p class="bodytext">ivaṇṇesu tāva-bhuso cayati vaḍḍhatīti acci, dvittaṃ rassattañca, vividhena cayati vaḍḍhatīti vīci, pañca māre jinātīti māraji, bhaddaṃ jinātīti bhaddaji. evaṃ puṇṇaji, gāmaṃ samūhaṃ netīti gāmaṇī, nassa ṇattaṃ. senaṃ netīti senānī iccādi.</p>\r\n<p class="bodytext">uvaṇṇesu-māreti cāveti cāti maccu, mahāvuttinā rassa pararūpattaṃ, vividhena javati sīghaṃ pharatīti vijju, bhavanti etthāti bhū-bhūmi, pabhavati issaraṃ karotīti pabhū, vibhavanaṃ vibhū, abhibhavatīti abhibhū, sayaṃ bhavatīti sayambhū, gottaṃ abhibhavatīti gottabhū, gotrabhū iccādi.</p>\r\n<p class="bodytext">byañjanantesu –</p>\r\n<p class="bodytext"><a name="para808"></a><span class="paranum">808</span>. <span class="bld">kvimhi lopontabyañjanassa</span> <span class="note">[ka. 615; rū. 586; nī. 1220]</span>.</p>\r\n<p class="bodytext">dhātūnaṃ antabyañjanassa lopo hoti kvimhi.</p>\r\n<p class="bodytext">antaṃ karotīti antako, nandaṃ karotīti nandako, jīvaṃ karotīti jīvako, cittaṃ vicittaṃ karotīti cittako, sukhena khamitabbanti sukhaṃ, dukkhena khamitabbanti dukkhaṃ, parito khaññateti parikkhā, saṃ attānaṃ khanatīti saṅkho, na gacchatīti ago-nago, sīsaṃ upagacchatīti sīsūpago. evaṃ gīvūpago, ninnaṭṭhānaṃ gacchatīti ninnagā-nadī, turaṃ sīghaṃ gacchatīti turaṅgo, majjhe bindāgamo.</p>\r\n','\r',char(13)),'\n',char(10)),'-807-808-');
INSERT INTO pages VALUES(54969,'annya_sadda_10',562,replace(replace('\r\n<p class="bodytext">bhujena <a name="M0.0562"></a> kuṭilena gacchatīti bhujago, urena gacchatīti urago, khena ākāsena gacchatīti khago, vehāse gacchatīti vihago, mahāvuttinā vehāsassa vihādeso.</p>\r\n<p class="bodytext">"go gacchati, gāvo gacchantī"tiādīsu pana "gocaro, godhano, gottaṃ, gotrabhū"tiādīsu ca kvimhi antabyañjanalope kvilope ca kate mahāvuttinā upantassa ottaṃ katvā goiti pakatirūpaṃ veditabbaṃ, balaṃ gaṇīyati etthāti balaggaṃ, bhattaṃ gaṇhanti etthāti bhattaggaṃ. evaṃ salākaggaṃ, uposathaggaṃ.</p>\r\n<p class="bodytext">kammena jāyatīti kammajo. evaṃ cittajo, utujo, attani jātoti atrajo, pubbe jāto pubbajo, pacchā jāto anujo, saha jāyatīti sahajo, thale jāyatīti thalajaṃ. evaṃ dakajaṃ, vārijaṃ, ambujaṃ. aṇḍe jāyatīti aṇḍajo, dvikkhattuṃ jāyatīti dvijo, saha bhāsanti etthāti sabhā, kuñje ramatīti kuñjaro iccādi.</p>\r\n<p class="bodytext"><a name="para809"></a><span class="paranum">809</span>. <span class="bld">kvimhi gho paripaccāsamohi</span> <span class="note">[ka. 538; rū. 595; nī. 1123; ‘.pacca.’ (bahūsu)]</span>.</p>\r\n<p class="bodytext">pari ca pati ca ā ca sañca o ca etehi parassa hanassa gho hoti kvimhi.</p>\r\n<p class="bodytext">parihaññatīti paligho, rassa lattaṃ. patihanatīti paṭigho, tassa ṭattaṃ. bhuso hanati bādhatīti aghaṃ-dukkhaṃ pāpañca, visesena bhuso hanatīti byaggho, diṭṭhi, sīlasāmaññena saṃhatoti saṅgho-samūho, devasaṅgho, brahmasaṅgho, migasaṅgho, ohanati adhobhāgaṃ katvā māretīti ogho, kilesogho, saṃsārogho, udakogho.</p>\r\n<p class="bodytext">suttavibhattiyā aññatopi gho hoti, mātaraṃ hanatīti mātugho. evaṃ pitugho iccādi.</p>\r\n<p class="bodytext"><a name="para810"></a><span class="paranum">810</span>. <span class="bld">ṇvādayo</span> <span class="note">[ka. 650; rū. 651; nī. 1288]</span>.</p>\r\n','\r',char(13)),'\n',char(10)),'-809-810-');
INSERT INTO pages VALUES(54970,'annya_sadda_10',563,replace(replace('\r\n<p class="bodytext">kriyatthā <a name="M0.0563"></a> bhāvakārakesu ṇuādayo honti, upari vuccamāno sabbo ṇvādikaṇḍo imassa suttassa niddeso hoti, tasmā idha kiñcimattaṃ vuccate.</p>\r\n<p class="bodytext">karotīti kāru-sippī, ayati vaḍḍhatīti āyu, ayanti vaḍḍhanti sattā etenāti vā āyu-jīvitaṃ, sobhāvisesaṃ rahanti jahanti canda, sūriyā etenāti rāhu-asurindo, hitasukhaṃ sādhetīti sādhu-sappuriso, vāyatīti vāyuvāto iccādi.</p>\r\n<p class="centered">iti rīrikkhādipaccayarāsi.</p>\r\n<p class="centered">pakatirūparāsi niṭṭhito.</p>\r\n<p class="centered">iti niruttidīpaniyā nāma moggallānadīpaniyā</p>\r\n<p class="centered">tabbādikaṇḍo nāma kitakaṇḍo niṭṭhito.</p>\r\n','\r',char(13)),'\n',char(10)),'');
