INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Morning Chanting', 'chapter', 1);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '01. First utterance of the Buddha', 'title', 1);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '02. Paṭiccasamuppāda:', 'title', 1);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 1, '<p><h2>The Book of Daily Chanting</h2></p><p><h2>International Institute of Theravāda</h2></p><p></p><p></p><p><h1>Morning Chanting</h1></p><p><h1>01. First utterance of the Buddha</h1></p><p>Anekajātisaṃsāraṃ, sandhāvissaṃ anibbisaṃ;</p><p>Gahakāraṃ gavesanto, dukkhā jāti punappunaṃ.</p><p></p><p></p><p>Gahakāraka diṭṭhosi, puna gehaṃ na kāhasi;</p><p>Sabbā te phāsukā bhaggā, gahakūṭaṃ visaṅkhitaṃ;</p><p>Visaṅkhāragataṃ cittaṃ, taṇhānaṃ khayamajjhagā”ti.</p><p></p><p><h1>02. Paṭiccasamuppāda:</h1></p><p>Iti imasmiṃ sati idaṃ hoti, imassuppādā idaṃ uppajjati.| imasmiṃ asati idaṃ na hoti, imassa nirodhā idaṃ nirujjhati.|</p><p>yadidaṃ Avijjāpaccayā saṅkhārā, saṅkhārapaccayā viññāṇaṃ, viññāṇapaccayā nāmarūpaṃ, nāmarūpapaccayā saḷāyatanaṃ, saḷāyatanapaccayā phasso, phassapaccayā vedanā, vedanāpaccayā taṇhā, taṇhāpaccayā upādānaṃ, upādānapaccayā bhavo, bhavapaccayā jāti, jātipaccayā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā sambhavanti. evametassa kevalassa dukkhakkhandhassa samudayo hoti.</p><p>“Avijjāyatveva asesavirāganirodhā saṅkhāranirodho, saṅkhāranirodhā viññāṇanirodho, viññāṇanirodhā nāmarūpanirodho, nāmarūpanirodhā saḷāyatananirodho, saḷāyatananirodhā phassanirodho, phassanirodhā vedanānirodho, vedanānirodhā taṇhānirodho, taṇhānirodhā upādānanirodho, upādānanirodhā bhavanirodho, bhavanirodhā jātinirodho, jātinirodhā jarāmaraṇaṃ sokaparidevadukkhadomanassupāyāsā nirujjhanti – evametassa kevalassa dukkhakkhandhassa nirodho hotī’ti.</p><p></p><p></p><p>Yadā have pātubhavanti dhammā;</p><p>ātāpino jhāyato brāhmaṇassa;</p><p>athassa kaṅkhā vapayanti sabbā;</p><p>yato pajānāti sahetudhammaṃ.</p><p></p><p></p><p>Yadā have pātubhavanti dhammā;</p><p>ātāpino jhāyato brāhmaṇassa;</p><p>athassa kaṅkhā vapayanti sabbā;</p><p>yato khayaṃ paccayānaṃ avedī.</p><p></p><p></p><p>Yadā have pātubhavanti dhammā;</p><p>ātāpino jhāyato brāhmaṇassa;</p><p>vidhūpayaṃ tiṭṭhati mārasenaṃ;</p><p>suriyova obhāsayamantalikkhaṃ.</p>', '-1-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', ' Recollection of the Buddha', 'title', 2);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', ' Recollection of the Dhamma', 'title', 2);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 2, '<p></p><p></p><p>------------------------</p><p></p><p>Namo tassa bhagavato arahato sammāsambuddhassa!!!</p><p>Namo tassa bhagavato arahato sammāsambuddhassa!!!</p><p>Namo tassa bhagavato arahato sammāsambuddhassa!!!</p><p></p><p>03. Invitation</p><p>yāvatā bhagavā loke - tiṭṭheyya tava sāsanaṃ;</p><p>tāvatā patigaṇhātu - pūjā lokānukampayā.</p><p>04. Oil Lamp Offering</p><p>gandhatelappa dittena - dīpena tama dhaṃsinā;</p><p>tilokadīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p></p><p></p><p>Sitthatelappadittena - dīpena tamadhaṃsinā;</p><p>tilokadīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p>05. Incense Offering</p><p>sugandhikāyavadanaṃ - anantaguṇagandhinaṃ;</p><p>sugandhinā’haṃ gandhena - pūjayāmī tathāgataṃ.</p><p>06. Drinking Water Offering</p><p>sugandhaṃ sītalaṃ kappaṃ - pasannamadhuraṃ subhaṃ;</p><p>pāṇīyametaṃ bhagavā - patigaṇhātu muttama.</p><p>07. Warm Water Offering</p><p>adhivāsetu no bhante - uṇhodakaṃ parikappitaṃ;</p><p>anukampaṃ upādāya - patigaṇhātu muttama.</p><p>08.  Flower Offering</p><p>vaṇṇa’gandha’guṇopetaṃ - etaṃ kusumasantatiṃ</p><p>pūjayāmī munindassa - sirīpāda saroruhe</p><p>pūjemi buddhaṃ kusumena nena – puññena metena labhāmi mokkhaṃ</p><p>pupphaṃ miḷāyāti yathā idaṃ me - kāyo tathā yāti vināsabhāvaṃ.</p><p>09. Recollection of the Triple Gem</p><p> <h1>Recollection of the Buddha</h1></p><p>itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā’ti.</p><p> <h1>Recollection of the Dhamma</h1></p>', '-2-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', ' Recollection of the Saṅgha', 'title', 3);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '10. Tribute to the Triple Gem', 'title', 3);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '11. Cuddasa buddhañāṇa ', 'title', 3);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '12 Caturārakkhā ', 'title', 3);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 3, '<p>Svākkhāto bhagavatā dhammo sandiṭṭhiko akāliko ehipassiko opanayiko paccattaṃ veditabbo viññūhī’ti.</p><p> <h1>Recollection of the Saṅgha</h1></p><p>Supaṭipanno bhagavato sāvakasaṅgho, ujupaṭipanno bhagavato sāvakasaṅgho, ñāyapaṭipanno bhagavato sāvakasaṅgho, sāmīcipaṭipanno bhagavato sāvakasaṅgho, yadidaṃ cattāri purisayugāni aṭṭha purisapuggalā esa bhagavato sāvakasaṅgho, āhuneyyo pāhuneyyo dakkhiṇeyyo añjalikaraṇīyo anuttaraṃ puññakkhettaṃ lokassā’ti.</p><p><h1>10. Tribute to the Triple Gem</h1></p><p>Buddhadhammā ca pacceka - buddhasaṅghā ca sāmikā</p><p>dāso vāha’smi metesaṃ - guṇaṃ ṭhātu sire sadā.</p><p>Tisaraṇaṃ tilakkhaṇūpekkhaṃ, nibbānamantimaṃ sukhaṃ</p><p>suvande sirasā niccaṃ - labhāmi tividhā mahaṃ.</p><p>Tisaraṇaṃ ca sire ṭhātu - sire ṭhātu tilakkhaṇaṃ</p><p>upekkhā ca sire ṭhātu - nibbānaṃ ṭhātu me sire.</p><p>Buddhe sakaruṇe vande - dhamme paccekasambuddhe</p><p>saṅghe ca sirasāyeva - tidhā niccaṃ namāmahaṃ.</p><p>Namāmi satthunovāda - appamāda vacanantimaṃ</p><p>sabbepi cetiye vande - upajjhācariye mamaṃ</p><p>mayhaṃ paṇāmatejena - cittaṃ pāpehi muccatanti.</p><p><h1>11. Cuddasa buddhañāṇa </h1></p><p>(Fourteen Auspicious Knowledge):</p><p>1. Dukkhe ñāṇaṃ, buddha ñāṇaṃ.</p><p>2. Dukkhasamudaye ñāṇaṃ, buddha ñāṇaṃ.</p><p>3. Dukkhanirodhe ñāṇaṃ, buddha ñāṇaṃ.</p><p>4. Dukkhanirodhagāminiyā paṭipadāya ñāṇaṃ, buddha ñāṇaṃ.</p><p>5. Atthapaṭisambhide ñāṇaṃ, buddha ñāṇaṃ.</p><p>6. Dhammapaṭisambhide ñāṇaṃ, buddha ñāṇaṃ.</p><p>7. Niruttipaṭisambhide ñāṇaṃ, buddha ñāṇaṃ.</p><p>8. Paṭibhānapaṭisambhide ñāṇaṃ, buddha ñāṇaṃ.</p><p>9. Indriyaparopariyatte ñāṇaṃ, buddha ñāṇaṃ.</p><p>10. Sattānaṃ āsayānusaye ñāṇaṃ, buddha ñāṇaṃ.</p><p>11. Yamakapāṭihāriye ñāṇaṃ, buddha ñāṇaṃ.</p><p>12. Mahākaruṇāsamāpattiyā ñāṇaṃ, buddha ñāṇaṃ.</p><p>13. Sabbaññutañāṇaṃ, buddha ñāṇaṃ.</p><p>14. Anāvaraṇañāṇaṃ, buddha ñāṇanti.</p><p>Imāni cuddasa buddha ñāṇāni. Imesaṃ aṭṭhañāṇāni sāvaka sādhāraṇāni. Cha ñāṇāni asādhāraṇāni sāvakehi, imehi cuddasa buddha ñāṇehi samannāgataṃ sammāsambuddhaṃ bhagavantaṃ sirasā namāmi.</p><p></p><p></p><p><h1>12 Caturārakkhā </h1></p><p>( Four Protective Meditations)</p>', '-3-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 4, '<p>buddhānussati mettā ca - asubhaṃ maraṇassati</p><p>iti imā caturārakkhā - bhikkhu bhāveyya sīlavā.</p><p>Anantavitthāraguṇaṃ - guṇatonussaraṃ muniṃ,</p><p>bhāveyya buddhimā bhikkhu - buddhānussatimādito.</p><p><h2>Buddhānussati</h2> </p><p>(Recollection of the Buddha)</p><p>Savāsane kilese so - eko sabbe nighātiya,</p><p>ahū susuddha santāno - pūjānañca sadāraho.</p><p>Sabbakālagate dhamme - sabbe sammāsayaṃ muni,</p><p>sabbākārena bujjhitvā - eko sabbaññutaṃ gato.</p><p>Vipassanādi vijjāhi - sīlādi caraṇehi ca,</p><p>susamiddhehi sampanno -gaganābhehi nāyako.</p><p>Sammāgato subhaṃ ṭhānaṃ - amoghavacano ca so,</p><p>tividhassāpi lokassa – ñātā niravasesato.</p><p>Anekehi guṇoghehi - sabbasattuttamo ahū,</p><p>anekehi upāyehi - naradamme damesi ca.</p><p>Eko sabbassa lokassa- sabbasattānusāsako,</p><p>bhāgya issariyādīnaṃ - guṇānaṃ paramo nidhi.</p><p>Paññāssa sabbadhammesu - karuṇā sabbajantusu,</p><p>attatthānaṃ paratthānaṃ - sādhikā guṇajeṭṭhikā.</p><p>Dayāya paramī citvā - paññāyattānamuddhari,</p><p>uddharī sabbadhamme ca - dayāyaññe ca uddhari</p><p>dissamānopi tāvassa -rūpakāyo acintiyo,</p><p>asādhāraṇañāṇaḍḍhe - dhammakāye kathāva kā’ti.</p><p></p><p><h2>Mettā bhāvanā</h2>  </p><p>(Meditation of Loving-kindness)</p><p>Attūpamāya sabbesaṃ - sattānaṃ sukha kāmataṃ,</p><p>passitvā kamato mettaṃ - sabbasattesu bhāvaye.</p><p>Sukhī bhaveyyaṃ niddukkho - ahaṃ niccaṃ ahaṃ viya,</p><p>hitā ca me sukhī hontu – majjhattātha ca verino.</p><p>Imamhi gāmakkhettamhi - sattā hontu sukhī sadā,</p><p>ato parañca rajjesu – cakkavāḷesu jantuno.</p><p>Samantā cakkavāḷesu – sattā’nantesu pāṇino,</p><p>sukhino puggalā bhūtā - attabhāvagatā siyuṃ.</p><p>Tathā itthī pumā ceva – ariyā anariyāpi ca,</p>', '-4-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Aṭṭhamahā saṃvega vatthūni', 'title', 5);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 5, '<p>devā narā apāyaṭṭhā - tathā dasadisāsu cāti.</p><p></p><p></p><p><h2>Asubha bhāvanā </h2></p><p>(Meditation on Loathsomeness)</p><p>Aviññāṇāsubhanibhaṃ - saviññāṇāsubhaṃ imaṃ,</p><p>kāyaṃ asubhato passaṃ - asubhaṃ bhāvaye satiṃ.</p><p>Vaṇṇa saṇṭhānagandhehi - āsayokāsato tathā,</p><p>paṭikkūlāni kāye me - kuṇapāni dvisoḷasa.</p><p>Patitamhāpi kuṇapā - jegucchaṃ kāya nissitaṃ,</p><p>ādhāro hi sucī tassa - kāye tu kuṇape ṭhitaṃ.</p><p>Mīḷhe kimiva kāyoyaṃ - asucimhi samuṭṭhito,</p><p>anto asuci sampuṇṇo - puṇṇavaccakuṭī viya.</p><p>Asuci sandate niccaṃ - yathā medakathālikā,</p><p>nānākimi kulāvāso - pakkacandanikā viya.</p><p>Gaṇḍabhūto roga bhūto - vaṇabhūto samussayo,</p><p>Atekiccho’tijeguccho - pabhinna kuṇapūpamo.</p><p><h2>Maraṇānussati</h2> </p><p>(Recollection of Death)</p><p>pavātadīpa tulyāya - sāyusantatiyākkhayaṃ,</p><p>parūpamāya sampassaṃ - bhāvaye maraṇassatiṃ.</p><p>Mahā sampatti sampattā - yathā sattā matā idha,</p><p>tathā ahaṃ marissāmi - maraṇaṃ mama hessati.</p><p>Uppattiyā sahevedaṃ - maraṇaṃ āgataṃ sadā,</p><p>maraṇatthāya okāsaṃ - vadhako viya esati.</p><p>Īsakaṃ anivattantaṃ - satataṃ gamanussukaṃ,</p><p>jīvitaṃ udayā atthaṃ - suriyo viya dhāvati.</p><p>Vijju bubbula ussāva - jalarājī parikkhayaṃ,</p><p>ghātakova ripū tassa - sabbatthāpi avāriyo.</p><p>Suyasatthāma puññiddhi - buddhi vuddhi jinadvayaṃ,</p><p>ghātesi maraṇaṃ khippaṃ - kā tu mādisake kathā.</p><p>Paccayānañca vekalyā - bāhirajjhattupaddavā,</p><p>Marāmoraṃ nimesāpi - maramano anukkhaṇaṃ.</p><p></p><p></p><p><h1>Aṭṭhamahā saṃvega vatthūni</h1></p>', '-5-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '14. Anumodanā gāthā (sharing merits)', 'title', 6);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 6, '<p>Bhāvetvā caturārakkhā - āvajjeyya anantaraṃ,</p><p>mahā saṃvegavatthūni - aṭṭha aṭṭhita vīriyo.</p><p>Jāti jarā vyādhi cutī apāyā - atīta appattaka vaṭṭa dukkhaṃ,</p><p>idāni āhāragaveṭṭhi dukkhaṃ - saṃvegavatthūni imāni aṭṭha.</p><p>Pāto ca sāyamapi ceva imaṃ vidhiññū</p><p>āsevate satata matta hitābhilāsi,</p><p>pappoti soti vipulaṃ hata pāripantho</p><p>seṭṭhaṃ sukhaṃ munivisiṭṭha mataṃ sukhena cāti.</p><p>13. Paritta Chanting:</p><p>i.        Metta sutta: karaṇīyamattakusalena.......</p><p>Ii.        Mora sutta (first half): udetayaṃ cakkhumā........</p><p>Iii.        Suttas (in row for each day): </p><p>A.        Monday        -        jayamaṅgala gāthā.</p><p>B.        Tuesday         -        jinapañjara paritta.</p><p>C.        Wednesday        -        aṭṭhavīsa paritta.</p><p>D.        Thursday        -        bojjhaṅga parita.</p><p>E.        Friday                -rāhulovāda gāthā.</p><p></p><p></p><p><h1>14. Anumodanā gāthā (sharing merits)</h1></p><p>Idaṃ vo ñātīnaṃ hotu sukhitā hontu ñātayo!!!         (three times)</p><p>ākāsaṭṭhā ca bhummaṭṭhā deva nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha sāsanaṃ!</p><p>ākāsaṭṭhā ca bhummaṭṭhā deva nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha desanaṃ!</p><p>ākāsaṭṭhā ca bhummaṭṭhā deva nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha sāvakaṃ!</p><p></p><p></p><p>iminā puñña kammena mā me bāla samāgamo</p><p>sataṃ samāgamo hotu yāva nibbāna pattiyā!!!         (Three times)</p><p></p><p></p><p>idaṃ me puñña kammaṃ āsavakkhayā vahaṃ hotu!</p><p>idaṃ me puñña kammaṃ āsavakkhayā vahaṃ hotu!</p><p>idaṃ me puñña kammaṃ āsavakkhayā vahaṃ hotu,</p>', '-6-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '15. Paṭipatti Pūjā adhiṭṭhāna', 'title', 7);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Evening Chanting', 'title', 7);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Sunday', 'chapter', 7);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '1. Invitation', 'title', 7);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '2. Offering lamps and candles ', 'title', 7);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '3. Offering incense ', 'title', 7);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 7, '<p>sabba dukkhā pamuñcatu!</p><p><h1>15. Paṭipatti Pūjā adhiṭṭhāna</h1></p><p>imāya dhammānudhammapaṭipattiyā buddhaṃ pūjemi!</p><p>imāya dhammānudhammapaṭipattiyā dhammaṃ pūjemi!</p><p>imāya dhammānudhammapaṭipattiyā saṅghaṃ pūjemi!</p><p>addhā imāya paṭipattiyā jāti, jarā, maraṇamhā parimuñcissāmī’ti!</p><p>16. Khamāpana Gāthā (Asking for forgiveness)</p><p>Kāyena vācā cittena - pamādena mayā kataṃ</p><p>accayaṃ khama me bhante - bhūripañña tathāgata.</p><p>Kāyena vācā cittena-pamādena mayā kataṃ</p><p>accayaṃ khama me dhamma - sandiṭṭhika akālika.</p><p>Kāyena vācā cittena - pamādena mayā kataṃ</p><p>accayaṃ khama me saṅgha - puññakkhetta anuttara.</p><p></p><p></p><p>sādhu! sādhu! sādhu!</p><p></p><p></p><p><h1>Evening Chanting</h1></p><p><h1>Sunday</h1></p><p></p><p>Namo tassa bhagavato arahato sammāsambuddhassa</p><p><h1>1. Invitation</h1></p><p>yāvatā bhagavā loke - tiṭṭheyya tava sāsanaṃ;</p><p>tāvatā patigaṇhātu - pūjā lokānukampayā.</p><p><h1>2. Offering lamps and candles</h1> </p><p>veneyyahadaye moha - tamokkhandhavinodakaṃ</p><p>dīpanetena pūjemi - paññādīpadharaṃ jinaṃ</p><p>iminā puññakammena - paññavā sīlavā sadā</p><p>bhavitvāhaṃ labhe sīghaṃ - dhammacakkhuṃ anuttaraṃ</p><p><h1>3. Offering incense </h1></p><p>desetvā pavaraṃ dhammaṃ - pāpāmagandha nāsakaṃ</p><p>sugandhinā’haṃ gandhena - pūjemi lokanāyakaṃ</p><p>4. Offering drinking water</p><p>dhammodakena sattānaṃ - pāpamala visodhakaṃ</p><p>pānīyenābhipūjemi - lokanāthaṃ dayālayaṃ</p>', '-7-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '10. Buddha-Vandanā (Homage to the Buddha):', 'title', 8);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 8, '<p>5. Offering Permissible Drinks:</p><p>tidosasamanaṃ deha - baladaṃ āyuvaḍḍhanaṃ</p><p>gilānapaccayaṃ etaṃ - paṇītaṃ madhuraṃ suciṃ</p><p>mahādayassa pūjemi - kilesāmayahārino</p><p>puññenetena pappomi - sabbasaññojanakkhayaṃ</p><p>6. Medicinal Betel</p><p>yathāvādī tathākārī - dhammavādī mahāyaso</p><p>tāmbūlaṃ patigaṇhātu – satthā loke anuttaro.</p><p>7. Offering Flower:</p><p>ananta guṇavaṇṇassa - suddhasīlasugandhino</p><p>pūjemi lokanāthassa -pupphametaṃ manoramaṃ</p><p>iminā puññakammena - bhavitvā sīlavā sadā</p><p>pappomi paramaṃ santiṃ - nibbānaṃ buddhavaṇṇitaṃ.</p><p>8. Offering Flags:</p><p>dhammaddhajadharaṃ dhīraṃ - dhajuggata mahāyasaṃ</p><p>dhajenetena pūjemi - dhammarājaṃ dhajūpamaṃ.</p><p>9. Parikkhāra pūjā (Offering requisites):</p><p>samatthaṃ appakārampi - karaṇe vipulapphalaṃ</p><p>parikkhārena pūjemi - acinteyya guṇaṃ jinaṃ.</p><p><h1>10. Buddha-Vandanā (Homage to the Buddha):</h1></p><p>itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇa sampanno sugato lokavidū anuttaro purisadamma sārathi satthā devamanussānaṃ buddho bhagavāti.</p><p>So bhagavā itipi arahaṃ, arahaṃ vata so bhagavā, taṃ bhagavantaṃ arahantaṃ sirasā namāmi. Tassa bhagavato arahato attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato arahato, sāvako hamasmi, sisso hamasmi.</p><p>Ārakattā hatattā ca - kilesārīna so munī,</p><p>hata saṃsāra cakkāro - paccayādīna cāraho,</p><p>na raho karoti pāpāni - arahaṃ taṃ namāmahaṃ.</p><p>So bhagavā itipi sammāsambuddho, sammāsambuddho vata so bhagavā, taṃ bhagavantaṃ sammāsambuddhaṃ sirasā namāmi. Tassa bhagavato sammāsambuddhassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato sammāsambuddhassa, sāvako hamasmi, sisso hamasmi.</p><p>Sammāsambuddha saddhamma - sammāsambuddha desana,</p><p>sammāsambuddha ñeyyassa - sammāsambuddha te namo.</p><p>So bhagavā itipi vijjācaraṇa sampanno, vijjācaraṇa sampanno vata so bhagavā, taṃ bhagavantaṃ vijjācaraṇa sampannaṃ sirasā namāmi. Tassa bhagavato vijjācaraṇa sampannassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato vijjācaraṇa sampannassa, sāvako hamasmi, sisso hamasmi.</p><p>Vijjācaraṇasampanno - tisso vijjā pakāsitā,</p><p>aṭṭhavijjā pañcadasa - caraṇaṃ taṃ namāmahaṃ,</p><p>so bhagavā itipi sugato, sugato vata so bhagavā, taṃ bhagavantaṃ sugataṃ sirasā namāmi. Tassa bhagavato sugatassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato sugatassa, sāvako hamasmi, sisso hamasmi.</p><p>Sugatattā sugattattā - sobhanā yassa vā gati,</p><p>gatattā sundaraṃ ṭhānaṃ - sugato nāma te namo.</p><p>So bhagavā itipi lokavidū, lokavidū vata so bhagavā, taṃ bhagavantaṃ lokaviduṃ sirasā namāmi. Tassa bhagavato lokaviduno attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato lokaviduno, sāvako hamasmi, sisso hamasmi.</p><p>Sattāvāsādi saṅkhāte - kāmarūpādike tathā,</p>', '-8-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '11. Dhamma-vandanā (Homage to the Dhamma):', 'title', 9);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 9, '<p>saṅkhāra-satta-mokāsa - loke yo vedi taṃ name.</p><p>So bhagavā itipi anuttaro purisadammasārathi, anuttaro purisadammasārathi vata so bhagavā, taṃ bhagavantaṃ anuttaraṃ purisadammasārathiṃ sirasā namāmi. Tassa bhagavato anuttarassa purisadamma sārathino attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato anuttarassa purisadammasārathino, sāvako hamasmi, sisso hamasmi.</p><p>Yo loke anuttaro deva - brahmāsuranarorage,</p><p>damesi damme purise - sārathiṃ taṃ namāmahaṃ.</p><p>So bhagavā itipi satthā devamanussānaṃ, satthā devamanussānaṃ vata so bhagavā, taṃ bhagavantaṃ satthāraṃ devamanussānaṃ sirasā namāmi. Tassa bhagavato satthuno devamanussānaṃ attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato satthuno devamanussānaṃ, sāvako hamasmi, sisso hamasmi.</p><p>Dātā tibhavasampattiṃ - netā sivapuraṃ varaṃ,</p><p>satthā devamanussānaṃ - taṃ namāmi naruttamaṃ.</p><p>So bhagavā itipi buddho, buddho vata so bhagavā, taṃ bhagavantaṃ buddhaṃ sirasā namāmi. Tassa bhagavato buddhassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato buddhassa, sāvako hamasmi, sisso hamasmi.</p><p>Catusacca mabujjhittha - bodhetā yo pajāya ca,</p><p>sāmaṃ saccāni sambujjhi - taṃ buddhaṃ paṇamāmahaṃ.</p><p>So bhagavā itipi bhagavā, bhagavā vata so bhagavā, taṃ bhagavantaṃ bhagavantaṃ sirasā namāmi. Tassa bhagavato bhagavato attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato bhagavato, sāvako hamasmi, sisso hamasmi.</p><p></p><p></p><p>Bhāgyavā bhaggavā yutto - bhagehi ca vibhattavā,</p><p>bhavesu vantagamano - bhagavā nāma te namo.</p><p></p><p></p><p>Atītā ye ca sambuddhā - ye ca buddhā anāgatā,</p><p>paccuppannā ca ye buddhā - ahaṃ vandāmi sabbadā</p><p>natthi me saraṇaṃ aññaṃ - buddho me saraṇaṃ varaṃ,</p><p></p><p></p><p>etena saccavajjena - hotu me jayamaṅgalaṃ.</p><p>Uttamaṅgena vandehaṃ - pādapaṃsuvaruttamaṃ,</p><p>buddhe yo khalito doso - buddho khamatu taṃ mamaṃ.</p><p></p><p></p><p><h1>11. Dhamma-vandanā (Homage to the Dhamma):</h1></p><p>svakkhāto bhagavatā dhammo sandiṭṭhiko akāliko ehipassiko opanayiko paccattaṃ veditabbo viññūhīti.</p><p>Svākkhāto vata tassa bhagavato so dhammo,</p><p>sandiṭṭhiko vata tassa bhagavato so dhammo,</p><p>akāliko vata tassa bhagavato so dhammo,</p><p>ehipassiko vata tassa bhagavato so dhammo,</p><p>opanayiko vata tassa bhagavato so dhammo,</p><p>paccattaṃ veditabbo viññūhi vata tassa bhagavato so dhammo,</p><p>taṃ svākkhātaṃ bhagavato dhammaṃ sandiṭṭhikaṃ akālikaṃ ehipassikaṃ opanayikaṃ paccattaṃ veditabbaṃ viññūhi sirasā namāmi. Tassa bhagavato dhammassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato dhammassa, sāvako hamasmi, sisso hamasmi.</p>', '-9-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '12. Saṅgha-vandanā (Homage to the Sangha):', 'title', 10);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 10, '<p></p><p></p><p>atītā ye ca saddhammā - ye ca dhammā anāgatā,</p><p>paccuppannā ca ye dhammā - ahaṃ vandāmi sabbadā</p><p></p><p></p><p>natthi me saraṇaṃ aññaṃ - dhammo me saraṇaṃ varaṃ,</p><p>etena saccavajjena - hotu me jayamaṅgalaṃ.</p><p></p><p></p><p>Uttamaṅgena vandehaṃ - dhammaṃ ca tividhaṃ varaṃ,</p><p>dhamme yo khalito doso - dhammo khamatu taṃ mamaṃ.</p><p>________________</p><p></p><p></p><p><h1>12. Saṅgha-vandanā (Homage to the Sangha):</h1></p><p>supaṭipanno bhagavato sāvaka saṅgho, ujupaṭipanno bhagavato sāvaka saṅgho, ñāyapaṭipanno bhagavato sāvaka saṅgho, sāmīcipaṭipanno bhagavato sāvaka saṅgho, yadidaṃ cattāri purisa yugāni aṭṭha purisa puggalā esa bhagavato sāvaka saṅgho, āhuneyyo, pāhuneyyo, dakkhiṇeyyo, añjalikaraṇīyo, anuttaraṃ puññakkhettaṃ lokassāti.</p><p>Supaṭipanno vata so bhagavato sāvakasaṅgho,</p><p>ujupaṭipanno vata so bhagavato sāvakasaṅgho,</p><p>ñāyapaṭipanno vata so bhagavato sāvakasaṅgho,</p><p>sāmīcipaṭipanno vata so bhagavato sāvakasaṅgho,</p><p>āhuneyyo vata so bhagavato sāvakasaṅgho,</p><p>pāhuneyyo vata so bhagavato sāvakasaṅgho,</p><p>dakkhiṇeyyo vata so bhagavato sāvakasaṅgho,</p><p>añjalikaraṇīyo vata so bhagavato sāvakasaṅgho,</p><p>anuttaraṃ puññakkhettaṃ lokassa vata so bhagavato sāvakasaṅgho,</p><p>taṃ bhagavato sāvaka saṅghaṃ supaṭipannaṃ ujupaṭipannaṃ ñāyapaṭipannaṃ sāmīcipaṭipannaṃ āhuneyyaṃ pāhuneyyaṃ dakkhiṇeyyaṃ añjalikaraṇīyaṃ anuttaraṃ puññakkhettaṃ lokassa sirasā namāmi.</p><p>Tassa bhagavato sāvakasaṅghassa attānaṃ pariccajāmi, pariccattoyeva me attā tassa bhagavato sāvakasaṅghassa, sāvako hamasmi, sisso hamasmi.</p><p>Ye ca saṅghā atītā ca - ye ca saṅghā anāgatā,</p><p>paccuppannā ca ye saṅghā - ahaṃ vandāmi sabbadā</p><p>natthi me saraṇaṃ aññaṃ - saṅgho me saraṇaṃ varaṃ,</p><p>etena sacca vajjena - hotu me jayamaṅgalaṃ.</p><p>Uttamaṅgena vandehaṃ - saṅghaṃ ca tividhottamaṃ,</p><p>Saṅghe yo khalito doso - saṅgho khamatu taṃ mamaṃ.</p><p></p><p></p>', '-10-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '13. Cetiya vandanā (Veneration of pagodas, etc.):', 'title', 11);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '14. Catu Saṃvejanīya-ṭhāna-vandanā:', 'title', 11);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '15.  Dhātu-Cetiya Vandanā ', 'title', 11);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '16. Veneration of all Cetiya:', 'title', 11);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 11, '<p><h1>13. Cetiya vandanā (Veneration of pagodas, etc.):</h1></p><p>sabbapāpavisuddhassa - anantaguṇa dhārino,</p><p>namāmi lokanāthassa - sādaraṃ dhātu cetiyaṃ.</p><p></p><p><h1>14. Catu Saṃvejanīya-ṭhāna-vandanā:</h1></p><p>māyā suto sugata sākiya sīhanātho - jātakkhaṇe sapadasā vabhisaṅkamitvā;</p><p>yasmiṃ udīrayi giraṃ varalumbiṇimhi - taṃ jāta cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Yasmiṃ nisajja vajirāsana bandhanena - jetvā savāsana kilesa balaṃ munindo;</p><p>sambodhi ñāṇamavagamma vihāsi sammā - taṃ bodhi cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Saṅkampayaṃ dasasahassiya loka dhātuṃ - desesi yatra bhagavā vara dhamma cakkaṃ;</p><p>barāṇasīpura samīpa vane migānaṃ - taṃ dhamma cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Katvāna loka hitamatta hitañca nātho - āsītikova upavattana kānanamhi;</p><p>yasmiṃ nipajja gatavā nirupādhisesaṃ - nibbāna cetiyamahaṃ sirasā namāmi.</p><p></p><p><h1>15.  Dhātu-Cetiya Vandanā </h1></p><p>buddhaṃ dhammaṃ ca saṅghaṃ sugatatanubhavā dhātuyo dhātugabbhe</p><p>laṅkāyaṃ jambudīpe tidasapuravare nāgaloke ca thūpe</p><p>sabbe buddhassa bimbe sakaladasadise kesalomādi dhātuṃ</p><p>vande sabbepi buddhaṃ dasabalatanujaṃ bodhicetiyaṃ sirasā namāmi.</p><p></p><p></p><p><h1>16. Veneration of all Cetiya:</h1></p><p>vandāmi cetiyaṃ sabbaṃ - sabbaṭhānesu patiṭṭhitaṃ</p><p>sārīrika dhātu mahā bodhiṃ - buddharūpaṃ sakalaṃ sadā.</p><p></p><p></p><p></p><p></p><p>……..dhamma desanā……..</p><p></p>', '-11-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '17. 18.  Āṭānāṭiya Gāthā', 'title', 12);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '19. Homage to all the Buddhas', 'title', 12);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '20. Ovāda Pātimokkha', 'title', 12);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 12, '<p></p><p>________________</p><p><h1>17. 18.  Āṭānāṭiya Gāthā</h1></p><p></p><p></p><p>vipassissa namatthu - cakkhumantassa sirīmato</p><p>sikhissapi namatthu - sabba bhūtānukampino</p><p></p><p></p><p>vessabhussa namatthu - nahātakassa tapassino</p><p>namatthu kakusandhassa - mārasenā pamaddino</p><p></p><p></p><p>koṇāgamanassa namatthu - brāhmaṇassa vusīmato</p><p>kassapassa namatthu - vippamuttassa sabbadhi</p><p></p><p></p><p>Aṅgīrasassa namatthu - sakyaputtassa sirīmato</p><p>namo tesaṃ sattannaṃ - sammāsambuddhānanti.</p><p></p><p></p><p><h1>19. Homage to all the Buddhas</h1></p><p>Paṭhavī bala sundarī - sabbaññu bodhi maṇḍalaṃ</p><p>asaṅkhyeṃ mārasenaṃ - jayo jayatu maṅgalaṃ</p><p>kakusandho koṇāgamano - kassapo gotamo munī</p><p>metteyyo pañca buddhā te - sīse me sentu sabbadā</p><p>etesaṃ ānubhāvena - yakkhā devā mahiddhikā</p><p>sabbepi sukhitā hontu - mama mittā sahāyakā</p><p>sambuddhe aṭṭhavīsaṃ ca - dvādasaṃ ca sahassake</p><p>pañcasatasahassāni - namāmi sirasādaraṃ</p><p>appakā vālukā gaṅgā - anantā nibbutā jinā</p><p>tesaṃ dhammañca saṅghañca - ādarena namāmyahaṃ</p><p>namakkārānubhāvena - hitvā sabbe upaddave</p><p>anekā antarāyāpi - vinassantu asesato.</p><p><h1>20. Ovāda Pātimokkha</h1></p><p>khantī paramaṃ tapo titikkhā - nibbānaṃ paramaṃ vadanti buddhā</p>', '-12-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '21. Vipassanā Gāthā', 'title', 13);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '22. Sharing Merits', 'title', 13);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 13, '<p>na hi pabbajito parūpaghātī - samaṇo hoti paraṃ viheṭhayanto</p><p>sabba pāpassa akaraṇaṃ - kusalassa upasampadā</p><p>sacittapariyodapanaṃ - etaṃ buddhānasāsanaṃ.</p><p>Anūpavādo anūpaghāto - pātimokkhe ca saṃvaro</p><p>mattaññutā ca bhattasmiṃ - panthañca sayanāsanaṃ</p><p>adhicitte ca āyogo - etaṃ buddhānasāsanaṃ.</p><p><h1>21. Vipassanā Gāthā</h1></p><p>sabbe saṅkhārā aniccāti - yadā paññāya passati</p><p>atha nibbindatī dukkhe - esa maggo visuddhiyā</p><p>sabbe saṅkhārā dukkhāti - yadā paññāya passati</p><p>atha nibbindatī dukkhe - esa maggo visuddhiyā</p><p>sabbe dhammā anattāti - yadā paññāya passati</p><p>atha nibbindatī dukkhe - esa maggo visuddhiyā.</p><p>Dīrgha puṇyānumodanāva hā prārthanā gāthā:</p><p><h1>22. Sharing Merits</h1></p><p>Iminā puññakammena - upajjhāyā guṇuttarā</p><p>ācariyupakārā ca - mātā pitā piyā mamaṃ</p><p></p><p></p><p>suriyo candimā rājā - guṇavantā narāpi ca</p><p>brahmā mārā ca indā ca - lokapālā ca devatā</p><p></p><p></p><p>yamo mittā manussā ca - majjhattā verikāpi ca</p><p>sabbe sattā sukhī hontu - puññāni pakatāni me.</p><p></p><p></p><p>Sukhaṃ ca tividhaṃ dentu - khippaṃ pāpetha vomataṃ</p><p>iminā puñña kammena - iminā uddisena ca</p><p></p><p></p><p>khippāhantu labhe ceva - taṇhūpādāna chedanaṃ</p><p>ye santāne hīnā dhammā - yāva nibbānato mamaṃ</p><p></p><p></p><p>nassantu sabbadāyeva - yattha jāto bhave bhave</p>', '-13-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '23. Sugatovāda (the Buddha’s Daily Admonishment):', 'title', 14);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '24. Paccavekkhaṇā:', 'title', 14);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 14, '<p>ujucitto satī pañño - sallekho viriyavāminā</p><p></p><p></p><p>mārā labhantu nokāsaṃ - kātuṃ ca viriye tu me</p><p>buddho dīpavaro nātho - dhammo nātho varuttamo</p><p></p><p></p><p>nātho paccekasambuddho - saṅgho nāthottaro mamaṃ</p><p>tesottamānubhāvena - mārokāsaṃ labhantu mā.</p><p></p><p></p><p>Bhavaggupādāya avīci heṭṭhato - etthantare sattakāyūpapannā</p><p>rūpī arūpī ca asaññisaññino - dukkhā pamuccantu phusantu nibbutiṃ</p><p></p><p></p><p>devo vassatu kālena - sassa sampattihetu ca</p><p>phīto bhavatu loko ca - rājā bhavatu dhammiko.</p><p></p><p></p><p><h1>23. Sugatovāda (the Buddha’s Daily Admonishment):</h1></p><p>appamādena bhikkhave sampādetha,</p><p>dullabho buddhuppādo lokasmiṃ,</p><p>dullabho manussattapaṭilābho,</p><p>dullabhā khaṇasampatti,</p><p>dullabhā pabbajjā,</p><p>dullabhaṃ saddhammasavanaṃ,</p><p>dullabho sappurisasaṃsevo.</p><p></p><p></p><p>Ārabhatha nikkhamatha yuñjatha buddha sāsane,</p><p>dhunātha maccuno senaṃ naḷāgāraṃva kuñjaro.</p><p>Yo imasmiṃ dhamma vinaye appamatto vihessati,</p><p>pahāya jāti saṃsāraṃ dukkhassantaṃ karissati.</p><p><h1>24. Paccavekkhaṇā:</h1></p><p>Cīvara</p><p>paṭisaṅkhā yoniso cīvaraṃ paṭisevāmi – ‘Yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya, ḍaṃsamakasavātātapasiriṃsapasamphassānaṃ paṭighātāya, yāvadeva hirikopīnapaṭicchādanatthaṃ’.</p>', '-14-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '25. Paṭipatti Pūjā adhiṭṭhāna:', 'title', 15);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '26. Khamāpana gāthā (Asking for fougiveness):', 'title', 15);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Monday/ Wednesday', 'chapter', 15);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 15, '<p>Piṇḍapāta</p><p> Paṭisaṅkhā yoniso piṇḍapātaṃ paṭisevāmi – ‘Neva davāya na madāya na maṇḍanāya na vibhūsanāya, yāvadeva imassa kāyassa ṭhitiyā yāpanāya, vihiṃsūparatiyā brahmacariyānuggahāya, iti purāṇañca vedanaṃ paṭihaṅkhāmi, navañca vedanaṃ na uppādessāmi, yātrā ca me bhavissati anavajjatā ca phāsuvihāro cāti. </p><p>Senāsana</p><p>Paṭisaṅkhā yoniso senāsanaṃ paṭisevāmi –‘Yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya, ḍaṃsamakasavātātapasarīsapasamphassānaṃ paṭighātāya, yāvadeva utuparissayavinodanaṃ paṭisallānārāmatthaṃ’.</p><p>Gilānappaccaya</p><p>Paṭisaṅkhā yoniso gilānappaccayabhesajjaparikkhāraṃ paṭisevāmi –‘Yāvadeva uppannānaṃ veyyābādhikānaṃ vedanānaṃ paṭighātāya, abyābajjhaparamatāyā’ti.</p><p><h1>25. Paṭipatti Pūjā adhiṭṭhāna:</h1></p><p>imāya dhammānudhammapaṭipattiyā buddhaṃ pūjemi!</p><p>imāya dhammānudhammapaṭipattiyā dhammaṃ pūjemi!</p><p>imāya dhammānudhammapaṭipattiyā saṅghaṃ pūjemi!</p><p>addhā imāya paṭipattiyā jāti, jarā, maraṇamhā parimuccissāmī’ti!</p><p><h1>26. Khamāpana gāthā (Asking for fougiveness):</h1></p><p>kāyena vācācittena pamādena mayā kataṃ</p><p>accayaṃ khama me bhante bhūripañña tathāgata.</p><p>Kāyena vācācittena pamādena mayā kataṃ</p><p>accayaṃ khama me dhamma sandiṭṭhika akālika.</p><p>Kāyena vācācittena pamādena mayā kataṃ</p><p>accayaṃ khama me saṅgha puññakkhetta anuttara.</p><p>Sādhu! sādhu! sādhu!</p><p></p><p><h1>Monday/ Wednesday</h1></p><p></p><p>Namo tassa bhagavato arahato sammāsambuddhassa!!!</p><p>01. Ārādhanā (Invitation) :</p><p>Yāvatā bhagavā loke - tiṭṭheyya tava sāsanaṃ;</p><p>tāvatā patigaṇhātu - pūjā lokānukampayā.</p><p>02. Padīpa pūjā (Offering Oil Lamp)</p><p>Gandhatelappadittena - dīpena tama dhaṃsinā;</p><p>tilokadīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p>Sittha telappa dittena - dīpena tama dhaṃsinā;</p><p>tileāka dīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p>03. Gandha pūjā (Offering Fragrance)</p><p>sugandhikāya vadanaṃ - ananta guṇagandhinaṃ;</p><p>sugandhināhaṃ gandhena - pūjayāmī tathāgataṃ.</p><p>04. Gandhodaka pūjā (Offering Drinking water):</p><p>sugandhaṃ sītalaṃ kappaṃ - pasanna madhuraṃ subhaṃ;</p>', '-15-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '08. Homage to the Buddha:', 'title', 16);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '09. Homage to the Dhamma:', 'title', 16);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '11. Catu Saṅvejanīyaṭhāna ', 'title', 16);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 16, '<p>pānīya metaṃ bhagavā - patigaṇhātu muttama.</p><p>05. Uṇhodaka pūjā (Warm Water):</p><p>adhivāsetu neā bhante - uṇhodakaṃ parikappitaṃ;</p><p>anukampaṃ upādāya - patigaṇhātu muttama.</p><p>06. Pānaka pūjā (Offering Allowable Drinks):</p><p>adhivāsetu no bhante- gilānapaccayaṃ imaṃ;</p><p>anukampaṃ upādāya - patigaṇhātu muttama.</p><p>07. Phuppa pūjā (Offeringn Flower):        </p><p>nirodha samāpattito vuṭṭhahitvā viya| nisinnaṃ bhagavantaṃ arahantaṃ sammāsambuddhaṃ| iminā pupphena pūjemi pūjemi pūjemi.| ayaṃ puppha pūjā buddha pacceka buddha| aggasāvaka mahāsāvaka arahantādīnaṃ sabhāvasīlaṃ| ahampi tesaṃ anuvattako homi.</p><p>Idaṃ pupphaṃ idāni vaṇṇenapi suvaṇṇaṃ | gandhenapi sugandhaṃ| saṇṭhāṇenapi susaṇṭhānaṃ| khippameva dubbaṇṇaṃ duggandhaṃ dussaṇṭhānaṃ aniccataṃ pāpuṇissati | evameva kho sabbe saṅkhārā aniccā| sabbe saṅkhārā dukkhā| sabbe dhammā anattāti.</p><p><h1>08. Homage to the Buddha:</h1></p><p>amhākaṃ bhagavato evaṃ kalyāṇo kittisaddo abbhuggato| yo pi so bhagavā asītyānubyañjana patimaṇḍita| dvattiṃsākāra mahā purisa lakkhaṇa vicitta rūpakāyo| sabbākāra parisuddha sīlakkhandhādi guṇaratana samiddhidhammakāyo| yasamahanta, puññamahanta| thāmamahanta, iddhimahanta| paññāmahantānaṃ pāragato| asamo, asamasamo| appaṭimo, appaṭisamo| appaṭibhāgo, appaṭipuggalo arahaṃ sammāsambuddho| vijjācaraṇasampanno sugato lokavidū| anuttaro purisadammasārathi| satthā devamanussānaṃ buddho bhagavā’ti.</p><p>Imehi guṇehi samannāgataṃ arahantaṃ sammāsambuddhaṃ| nibbānatthāya sirasā namāmi. | jīvitaṃ yāva nibbānaṃ buddhaṃ saraṇaṃ gacchāmi.</p><p></p><p></p><p><h1>09. Homage to the Dhamma:</h1></p><p>svakkhāto bhagavatā dhammo| sandiṭṭhiko akāliko| ehipassiko opaneyyiko| paccattaṃ veditabbo viññūhī’ti. </p><p>imehi guṇehi samannāgataṃ| sakala lokavimohaka mohadhaṃsakaṃ| pavaranīyāṇikaṃ saddhammaṃ| nibbānatthāya sirasā namāmi.| jīvitaṃ yāva nibbānaṃ dhammaṃ saraṇaṃ gacchāmi.</p><p>10. Homage to the Saṅgha:</p><p>suppaṭipanno bhagavato sāvakasaṅgho| ujupaṭipanno bhagavato sāvakasaṅgho| ñāyapaṭipanno bhagavato sāvakasaṅgho| sāmīcipaṭipanno bhagavato sāvakasaṅgho| yadidaṃ cattāri purisayugāni aṭṭha purisapuggalā| esa bhagavato sāvakasaṅgho| āhuneyyo pāhuneyyo| dakkhiṇeyyo añjalikaraṇīyo| anuttaraṃ puññakkhettaṃ lokassā’ti.</p><p>Imehi guṇehi samannāgataṃ| anaghuttama dakkhiṇeyyaṃ pavara saṅgharatanaṃ| nibbānatthāya sirasā namāmi.| jīvitaṃ yāva nibbānaṃ saṅghaṃ saraṇaṃ gacchāmi.</p><p></p><p></p><p>Monday :Tesattati ñāṇavandanā</p><p>Wednesday:  Sattasattati ñāṇāvandanā</p><p></p><p></p><p>________________</p><p><h1>11. Catu Saṅvejanīyaṭhāna </h1></p><p>māyā suto sugata sākiya sīhanātho –</p><p>jātakkhaṇe sapadasāvabhisaṅkamitvā;</p><p>yasmiṃ udīrayi giraṃ varalumbiṇimhi – </p><p>taṃ jāta cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Yasmiṃ nisajja vajirāsana bandhanena –</p>', '-16-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Iv.        Āṭānāṭiyaparitta', 'title', 17);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '14. Sugatovāda (the Buddha’s daily admonishment):', 'title', 17);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 17, '<p> jetvā savāsana kilesa balaṃ munindo;</p><p>sambodhi ñāṇamavagamma vihāsi sammā – </p><p>taṃ bodhi cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Saṅkampayaṃ dasasahassiya loka dhātuṃ - </p><p>desesi yatra bhagavā vara dhamma cakkaṃ;</p><p>barāṇasīpura samīpa vane migānaṃ - </p><p>taṃ dhamma cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Katvāna loka hitamatta hitañca nātho – </p><p>āsītikova upavattana kānanamhi;</p><p>yasmiṃ nipajja gatavā nirupādhisesaṃ -</p><p> nibbāna cetiyamahaṃ sirasā namāmi.</p><p>12. Cetiya Vandanā (veneration of pagodas, etc.):</p><p></p><p></p><p>vandāmi cetiyaṃ sabbaṃ - sabbaṭhānesu patiṭṭhitaṃ</p><p>sārīrika dhātu mahā bodhiṃ - buddharūpaṃ sakalaṃ sadā.</p><p>13. Paritta Chanting:</p><p>i.        Metta Sutta.</p><p>Ii.        Mora Paritta – Last half.</p><p>Iii.        Khandha Paritta - verses.</p><p></p><p><h1>Iv.        Āṭānāṭiyaparitta</h1></p><p>vipassissa namatthu - cakkhumantassa sirīmato</p><p>sikhissapi namatthu - sabba bhūtānukampino</p><p>vessabhussa namatthu - nahātakassa tapassino</p><p>namatthu kakusandhassa - mārasenā pamaddino</p><p>koṇāgamanassa namatthu - brāhmaṇassa vusīmato</p><p>kassapassa namatthu - vippamuttassa sabbadhi</p><p>aṅgīrasassa namatthu - sakyaputtassa sirīmato</p><p>namo tesaṃ sattannaṃ - sammāsambuddhānanti.</p><p><h1>14. Sugatovāda (the Buddha’s daily admonishment):</h1></p><p>appamādena bhikkhave sampādetha,</p>', '-17-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '16. Paccavekkhanā – Atīta/ Dātu/ Asubha', 'title', 18);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '17. Anumodanā gāthā (Sharing Merits)', 'title', 18);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 18, '<p>dullabho buddhuppādo lokasmiṃ,</p><p>dullabho manussatta paṭilābho,</p><p>dullabhā khaṇa sampatti,</p><p>dullabhā pabbajjā,</p><p>dullabhaṃ saddhamma savanaṃ,</p><p>dullabho sappurisa saṃsevo,</p><p>ārabhatha nikkhamatha yuñjatha buddha sāsane,</p><p>dhunātha maccuno senaṃ naḷāgāraṃva kuñjaro.</p><p>Yo imasmiṃ dhamma vinaye appamatto vihessati,</p><p>pahāya jāti saṃsāraṃ dukkhassantaṃ karissati.</p><p>15. Bhāvanā:</p><p><h1>16. Paccavekkhanā – Atīta/ Dātu/ Asubha</h1></p><p>Cīvara</p><p>mayāpaccavekkhitvā ajja yaṃ cīvaraṃ paribhuttaṃ taṃ – ‘Yāvadeva sītassa paṭighātāya| uṇhassa paṭighātāya| ḍaṃsamakasavātātapasiriṃsapasamphassānaṃ paṭighātāya| yāvadeva hirikopīnapaṭicchādanatthaṃ.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ cīvaraṃ| tadupabhuñjako ca puggalo dhātumattako nissatto nijjīvo suñño| sabbāni pana imāni cīvarāni, ajigucchanīyāni| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyāni jāyantī.</p><p>Piṇḍapāta</p><p> Mayāpaccavekkhitvā ajja yo piṇḍapāto paribhutto| so – ‘Neva davāya na madāya na maṇḍanāya na vibhūsanāya| yāvadeva imassa kāyassa ṭhitiyā yāpanāya| vihiṃsūparatiyā brahmacariyānuggahāya| iti purāṇañca vedanaṃ paṭihaṅkhāmi, navañca vedanaṃ na uppādessāmi| yātrā ca me bhavissati anavajjatā ca phāsuvihāro cāti.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ piṇḍapāto| tadupa bhuñjako ca puggalo dhātumattako nissatto nijjīvo suñño| sabbo panāyaṃ piṇḍapāto ajigucchanīyo| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyo jāyatī.</p><p>Senāsana</p><p>Mayāpaccavekkhitvā ajjayaṃ senāsanaṃ paribhuttaṃ| taṃ – yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya| ḍaṃsamakasavātātapasarīsapasamphassānaṃ paṭighātāya| yāvadeva utuparissayavinodaṃ paṭisallānārāmatthaṃ.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ senāsanaṃ| tadupabhuñjako ca puggalo| dhātumattako nissatto nijjīvo suñño| sabbāni pana imāni senāsanāni ajigucchanīyāni| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyāni jāyantī. |</p><p>Gilānappaccaya</p><p>Mayāpaccavekkhitvā ajjayo gilānappaccayabhesajjaparikkhāro paribhutto| so– ‘Yāvadeva uppannānaṃ veyyābādhikānaṃ vedanānaṃ paṭighātāya| abyābajjhaparamatāyā’ti.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ gilānappaccayabhesajjaparikkhāro| tadupabhuñjako ca puggalo| dhātumattako nissatto nijjīvo suñño| sabbo panāyaṃ gilānappaccayabhesajjaparikkhāro ajigucchanīyo| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyo jāyatī.</p><p></p><p></p><p><h1>17. Anumodanā gāthā (Sharing Merits)</h1></p><p>To Past relatives</p><p>idaṃ vo ñātīnaṃ hotu sukhitā hontu ñātayo!!! (Three times)</p><p>To Celestial and all Beings</p><p>ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha sāsanaṃ!</p><p>ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha desanaṃ!</p><p>ākāsaṭṭhā ca bhummaṭṭhā devā nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā ciraṃ rakkhantu sambuddha sāvakaṃ!</p>', '-18-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '18. Patthanā (wholesome wishes)', 'title', 19);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 19, '<p></p><p></p><p>kappasatasahassāni soḷasāpi asaṅkhiye,</p><p>sambharaṃ bodhisambhāre āgato jāti jātiyo,</p><p>niyato buddhabhāvāya bhaddakappe idhantime,</p><p>bodhisatto ca metteyyo puññaṃ me anumodatu!</p><p>cattāro ca mahārājā sakko cāti surāpare,</p><p>anumoditvā imaṃ puññaṃ rakkhantu jina sāsanaṃ!</p><p>asmiṃ vihāre ca ārāme adhivatthā ca devatā,</p><p>anumoditvā imaṃ puññaṃ rakkhantu jina sāsanaṃ!</p><p>sabbe sattā ca majjhattā hitā ca ahitā ca me,</p><p>anumoditvā imaṃ puññaṃ bujjhantu amataṃ padaṃ!</p><p> To Late Teachers</p><p>sīlādinā guṇagaṇena katūbhayatthā</p><p>therāsabhā mama garū tidivaṃ supattā</p><p>puññena nena pakatena samiddhacittā</p><p>santiṃ bhavantu hadayassa sukhena pattā.</p><p>To Respected Elders/Teachers</p><p>Dhammena nena matisiddha mudācitena,</p><p>buddhādi vatthu gata dhamma balena saddhiṃ,</p><p>therāsabho ………. Yatinda nātho,</p><p>dīghāyuko bhavatu sādhu samiddha citto!</p><p>dhammena nena matisiddha mudācitena,</p><p>buddhādi vatthu gata dhamma balena saddhiṃ,</p><p>therāsabhātha garugārava bhāvanīyā,</p><p>dīghāyukā bhavatha sādhu samiddha cittā!</p><p><h1>18. Patthanā (wholesome wishes)</h1></p><p>iminā puñña kammena mā me bāla samāgamo</p><p>sataṃ samāgamo hotu yāva nibbāna pattiyā!!!          (Three times)</p><p></p><p></p><p>idaṃ me puñña kammaṃ āsavakkhayā vahaṃ hotu!!! (Three times)</p><p>sabba dukkhā pamuñcatu!</p><p></p><p></p><p>imāya dhammānu dhammapaṭipattiyā buddhaṃ pūjemi!</p>', '-19-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '19. Khamāpana gāthā (asking for forgiveness)', 'title', 20);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Tuesday/ Thursday', 'chapter', 20);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 20, '<p>imāya dhammānu dhammapaṭipattiyā dhammaṃ pūjemi!</p><p>imāya dhammānu dhammapaṭipattiyā saṅghaṃ pūjemi!</p><p>addhā imāya paṭipattiyā jāti, jarā, vyādhi, maraṇamhā parimuccissāmī’ti!</p><p>________________</p><p></p><p></p><p><h1>19. Khamāpana gāthā (asking for forgiveness)</h1></p><p>kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me bhante bhūripañña tathāgata.</p><p>Kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me dhamma sandiṭṭhika akālika.</p><p>Kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me saṅgha puññakkhetta anuttara.</p><p>Sādhu! sādhu! sādhu!</p><p></p><p><h1>Tuesday/ Thursday</h1></p><p></p><p>Namo tassa bhagato arahato sammāsambuddhassa!</p><p>1. Ārādhanā (Invitation):</p><p>yāvatā bhagavā loke - tiṭṭheyya tava sāsanaṃ;</p><p>tāvatā patigaṇhātu - pūjā lokānukampayā.</p><p>yāvatā bhagavā loke - tiṭṭheyya tava sāsanaṃ;</p><p>tāvatā patigaṇhātu - pūjā lokānukampayā.</p><p>2. Padīpa pūjā (Offering Oil Lamp)</p><p>gandhatelappadittena - dīpena tama dhaṃsinā;</p><p>tileāka dīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p>Sitthatelappa dittena - dīpena tama dhaṃsinā;</p><p>tileāka dīpaṃ sambuddhaṃ - pūjayāmī tamonudaṃ.</p><p>03. Gandha pūjā (Offering Fragrance)</p><p>sugandhikāya vadanaṃ - ananta guṇagandhinaṃ;</p><p>sugandhināhaṃ gandhena - pūjayāmī tathāgataṃ.</p><p>04. Gandhodaka pūjā (Offering Drinking water):</p><p>sugandhaṃ sītalaṃ kappaṃ - pasanna madhuraṃ subhaṃ;</p><p>pānīya metaṃ bhagavā - patigaṇhātu muttama.</p><p>05. Uṇhodaka pūjā (Warm Water):</p><p>adhivāsetu neā bhante - uṇhodakaṃ parikappitaṃ;</p>', '-20-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '08. Homage to the Buddha', 'title', 21);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '09. Homage to the Dhamma', 'title', 21);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '10. Homage to the Saṅgha', 'title', 21);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '11. Aṭṭhārasa āvenika buddhaguṇa:', 'title', 21);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 21, '<p>anukampaṃ upādāya - patigaṇhātu muttama.</p><p>06. pānaka pūjā (Offering Allowable Drinks):</p><p>adhivāsetu no bhante- gilānapaccayaṃ imaṃ;</p><p>anukampaṃ upādāya - patigaṇhātu muttama.</p><p>07. Phuppa pūjā (Offeringn Flower):        </p><p>nirodha samāpattito vuṭṭhahitvā viya| nisinnaṃ bhagavantaṃ arahantaṃ sammāsambuddhaṃ| iminā pupphena pūjemi pūjemi pūjemi.| ayaṃ puppha pūjā buddha pacceka buddha| aggasāvaka mahāsāvaka arahantādīnaṃ sabhāvasīlaṃ| ahampi tesaṃ anuvattako homi.</p><p>Idaṃ pupphaṃ idāni vaṇṇenapi suvaṇṇaṃ | gandhenapi sugandhaṃ| saṇṭhāṇenapi susaṇṭhānaṃ| khippameva dubbaṇṇaṃ duggandhaṃ dussaṇṭhānaṃ aniccataṃ pāpuṇissati | evameva kho sabbe saṅkhārā aniccā| sabbe saṅkhārā dukkhā| sabbe dhammā anattāti.</p><p><h1>08. Homage to the Buddha</h1></p><p>itipi so bhagavā arahaṃ sammāsambuddho vijjācaraṇasampanno sugato lokavidū anuttaro purisadammasārathi satthā devamanussānaṃ buddho bhagavā ti.</p><p>Buddhaṃ jīvita pariyantaṃ saraṇaṃ gacchāmi.</p><p>Ye ca buddhā atītā ca - ye ca buddhā anāgatā</p><p>paccuppannā ca ye buddhā - ahaṃ vandāmi sabbadā.</p><p>Natthi me saraṇaṃ aññaṃ - buddho me saraṇaṃ varaṃ</p><p>etena sacca vajjena - hotu me jayamaṅgalaṃ.</p><p>Uttamaṅgena vandehaṃ - pāda paṃsu varuttamaṃ</p><p>buddhe yo khalito doso - buddho khamatu taṃ mamaṃ.</p><p><h1>09. Homage to the Dhamma</h1></p><p>Svākkhāto bhagavatā dhammo sandiṭṭhiko akāliko ehipassiko opanayiko paccattaṃ veditabbo viññūhī ti.</p><p>Dhammaṃ jīvita pariyantaṃ saraṇaṃ gacchāmi.</p><p>ye ca dhammā atītā ca ye ca dhammā anāgatā</p><p>paccuppannā ca ye dhammā - ahaṃ vandāmi sabbadā</p><p>natthi me saraṇaṃ aññaṃ - dhammo me saraṇaṃ varaṃ</p><p>etena sacca vajjena - hotu me jayamaṅgalaṃ</p><p>uttamaṅgena vandehaṃ - dhammaṃ ca tividhaṃ varaṃ</p><p>dhamme yo khalito doso - dhammo khamatu taṃ mamaṃ.</p><p><h1>10. Homage to the Saṅgha</h1></p><p>Supaṭipanno bhagavato sāvakasaṅgho ujupaṭipanno bhagavato sāvakasaṅgho ñāyapaṭipanno bhagavato sāvakasaṅgho sāmīcipaṭipanno bhagavato sāvakasaṅgho yadidaṃ cattāri purisayugāni aṭṭha purisapuggalā esa bhagavato sāvakasaṅgho āhuneyyo pāhuneyyo dakkhiṇeyyo añjalikaraṇīyo anuttaraṃ puññakkhettaṃ lokassā’ti.</p><p>Saṅghaṃ jīvita pariyantaṃ saraṇaṃ gacchāmi.</p><p>ye ca saṅghā atītā ca ye ca saṅghā anāgatā</p><p>paccuppannā ca ye saṅghā - ahaṃ vandāmi sabbadā</p><p>natthi me saraṇaṃ aññaṃ - saṅgho me saraṇaṃ varaṃ</p><p>etena sacca vajjena - hotu me jayamaṅgalaṃ.</p><p>Uttamaṅgena vandehaṃ - saṅghaṃ ca tividhottamaṃ</p><p>saṅghe yo khalito doso - saṅgho khamatu taṃ mamaṃ.</p><p><h1>11. Aṭṭhārasa āvenika buddhaguṇa:</h1></p><p>Atītaṃse buddhassa bhagavato appaṭihataṃ ñāṇaṃ| anāgataṃse buddhassa bhagavato appaṭihataṃ ñāṇaṃ |paccuppannaṃse buddhassa bhagavato appaṭihataṃ ñāṇaṃ |imehi tīhi dhammehi samannāgatassa buddhassa bhagavato| </p>', '-21-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '12 Catu Saṅvejanīyaṭṭhāna ', 'title', 22);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '14. Devārādhanā : ', 'title', 22);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 22, '<p>Sabbaṃ kāyakammaṃ ñāṇa pubbaṅgamaṃ ñāṇānuparivattaṃ| sabbaṃ vacīkammaṃ ñāṇa pubbaṅgamaṃ ñāṇānuparivattaṃ| sabbaṃ manokammaṃ ñāṇa pubbaṅgamaṃ ñāṇānuparivattaṃ| imehi chahi dhammehi samannāgatassa buddhassa bhagavato| </p><p>Natthi chandassa hāni | natthi dhammadesanāya hāni| natthi viriyassa hāni| natthi samādhissa hāni| natthi paññāya hāni| natthi vimuttiyā hāni. | imehi dvādasahi dhammehi samannāgatassa buddhassa bhagavato|</p><p>Natthi davā, natthi ravā| natthi apphuṭaṃ natthi vegāyitattaṃ| natthi abyāvaṭa mano, natthi appaṭisaṅkhānupekkhā. | imehi aṭṭhārasahi dhammehi samannāgataṃ| sammā sambuddhaṃ bhagavantaṃ sirasā namāmi. |</p><p><h1>12 Catu Saṅvejanīyaṭṭhāna </h1></p><p>Māyā suto sugata sākiya sīhanātho –</p><p>jātakkhaṇe sapadasā vabhisaṅkamitvā; </p><p>yasmiṃ udīrayi giraṃ varalumbiṇimhi –</p><p> taṃ jāta cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Yasmiṃ nisajja vajirāsana bandhanena –</p><p> jetvā savāsana kilesa balaṃ munindo;</p><p>sambodhi ñāṇamavagamma vihāsi sammā – </p><p>taṃ bodhi cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Saṅkampayaṃ dasasahassiya loka dhātuṃ - </p><p>desesi yatra bhagavā vara dhamma cakkaṃ;</p><p>barāṇasīpura samīpa vane migānaṃ - </p><p>taṃ dhamma cetiyamahaṃ sirasā namāmi.</p><p></p><p></p><p>Katvāna loka hitamatta hitañca nātho – </p><p>āsītikova upavattana kānanamhi;</p><p>yasmiṃ nipajja gatavā nirupādhisesaṃ - </p><p>nibbāna cetiyamahaṃ sirasā namāmi.</p><p>13.Cetiya-vandanā (Worshing pagodas, etc.):</p><p>vandāmi cetiyaṃ sabbaṃ - sabbaṭhānesu patiṭṭhitaṃ</p><p>sārīrika dhātu mahā bodhiṃ - buddharūpaṃ sakalaṃ sadā.</p><p>Paritta Chanting</p><p><h1>14. Devārādhanā : </h1></p><p>Invitation celestial beings for listening Dhamma</p><p>1. Sagge kāme ca rūpe giri sikhara taṭe| cāntalikkhe vimāne| dīpe raṭṭhe ca gāme taru vana gahaṇe| geha vatthumhi khette| bhummā cāyantu devā jalatala visame| yakkha gandhabba nāgā| tiṭṭhantā santikedaṃ munivara vacanaṃ| sādhavo me suṇantu. |</p><p>2. Samantā cakkavāḷesu atrāgacchantu devatā </p><p>Saddhammaṃ munirājassa suṇantu saggamokkhadaṃ. Dhammassavanakālo ayaṃ bhadantā. </p><p>Dhammassavanakālo ayaṃ bhadantā. </p>', '-22-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '15. Sugatovāda (the Buddha’s Daily Admonishment):', 'title', 23);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 23, '<p>Parittadhammassavanakālo ayaṃ bhadantā.</p><p></p><p>i.        Metta suttaṃ</p><p>ii.        Vatta pirita ( one sutta from two bhānavāra)</p><p>iii.        Mora paritta – (second half).</p><p>apetayaṃ cakkhumā ekarājā…….</p><p>iv.        Khandha paritta - second half</p><p>Virūpakkhehi me mettaṃ mettaṃ erāpatehi me…………</p><p></p><p>V.        Āṭānāṭiya gāthā </p><p>vipassissa namatthu - cakkhumantassa sirīmato,</p><p>sikhissapi namatthu - sabba bhūtānukampino</p><p></p><p></p><p>vessabhussa namatthu - nahātakassa tapassino,</p><p>namatthu kakusandhassa - mārasenā pamaddino,</p><p>koṇāgamanassa namatthu - brāhmaṇassa vusīmato,</p><p>kassapassa namatthu - vippamuttassa sabbadhi,</p><p></p><p></p><p>aṅgīrasassa namatthu - sakyaputtassa sirīmato,</p><p>namo tesaṃ sattannaṃ - sammāsambuddhānanti.</p><p><h1>15. Sugatovāda (the Buddha’s Daily Admonishment):</h1></p><p>Appamādena bhikkhave sampādetha,</p><p>dullabho buddhuppādo lokasmiṃ,</p><p>dullabho manussatta paṭilābho,</p><p>dullabhā khaṇa sampatti,</p><p>dullabhā pabbajjā,</p><p>dullabhaṃ saddhamma savanaṃ,</p><p>dullabho sappurisa saṃsevo,</p><p></p><p></p><p>ārabhatha nikkhamatha yuñjatha buddha sāsane,</p><p>dhunātha maccuno senaṃ naḷāgāraṃva kuñjaro.</p><p>Yo imasmiṃ dhamma vinaye appamatto vihessati,</p><p>pahāya jāti saṃsāraṃ dukkhassantaṃ karissati.</p>', '-23-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '17. Atīta paccavekkhanā:', 'title', 24);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '18. Anumodanā Gāthā (Sharing Merits)', 'title', 24);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 24, '<p>16. Bhāvanā (Meditation time):</p><p>________________</p><p></p><p></p><p></p><p></p><p><h1>17. Atīta paccavekkhanā:</h1></p><p>Cīvara</p><p>mayāpaccavekkhitvā ajja yaṃ cīvaraṃ paribhuttaṃ taṃ – ‘Yāvadeva sītassa paṭighātāya| uṇhassa paṭighātāya| ḍaṃsamakasavātātapasiriṃsapasamphassānaṃ paṭighātāya| yāvadeva hirikopīnapaṭicchādanatthaṃ.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ cīvaraṃ| tadupabhuñjako ca puggalo dhātumattako nissatto nijjīvo suñño| sabbāni pana imāni cīvarāni, ajigucchanīyāni| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyāni jāyantī.</p><p>Piṇḍapāta</p><p> Mayāpaccavekkhitvā ajja yo piṇḍapāto paribhutto| so – ‘Neva davāya na madāya na maṇḍanāya na vibhūsanāya| yāvadeva imassa kāyassa ṭhitiyā yāpanāya| vihiṃsūparatiyā brahmacariyānuggahāya| iti purāṇañca vedanaṃ paṭihaṅkhāmi, navañca vedanaṃ na uppādessāmi| yātrā ca me bhavissati anavajjatā ca phāsuvihāro cāti.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ piṇḍapāto| tadupa bhuñjako ca puggalo dhātumattako nissatto nijjīvo suñño| sabbo panāyaṃ piṇḍapāto ajigucchanīyo| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyo jāyatī.</p><p>Senāsana</p><p>Mayāpaccavekkhitvā ajjayaṃ senāsanaṃ paribhuttaṃ| taṃ – yāvadeva sītassa paṭighātāya, uṇhassa paṭighātāya| ḍaṃsamakasavātātapasarīsapasamphassānaṃ paṭighātāya| yāvadeva utuparissayavinodaṃ paṭisallānārāmatthaṃ.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ senāsanaṃ| tadupabhuñjako ca puggalo| dhātumattako nissatto nijjīvo suñño| sabbāni pana imāni senāsanāni ajigucchanīyāni| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyāni jāyantī. |</p><p>Gilānappaccaya</p><p>Mayāpaccavekkhitvā ajjayo gilānappaccayabhesajjaparikkhāro paribhutto| so– ‘Yāvadeva uppannānaṃ veyyābādhikānaṃ vedanānaṃ paṭighātāya| abyābajjhaparamatāyā’ti.</p><p>Yathāpaccayaṃ pavattamānaṃ dhātumattamevetaṃ yadidaṃ gilānappaccayabhesajjaparikkhāro| tadupabhuñjako ca puggalo| dhātumattako nissatto nijjīvo suñño| sabbo panāyaṃ gilānappaccayabhesajjaparikkhāro ajigucchanīyo| imaṃ pūtikāyaṃ patvā ativiya jigucchanīyo jāyatī.</p><p></p><p></p><p><h1>18. Anumodanā Gāthā (Sharing Merits)</h1></p><p>To Past Relatives </p><p>idaṃ vo ñātīnaṃ hotu sukhitā hontu ñātayo!!! (three times)</p><p></p><p></p><p>To Celestial Beings</p><p>ākāsaṭṭhā ca bhummaṭṭhā - deva nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā - ciraṃ rakkhantu sambuddha sāsanaṃ!</p><p></p><p></p><p>ākāsaṭṭhā ca bhummaṭṭhā - deva nāgā mahiddhikā</p><p>puññaṃ taṃ anumoditvā - ciraṃ rakkhantu sambuddha desanaṃ!</p><p></p><p></p><p>ākāsaṭṭhā ca bhummaṭṭhā - deva nāgā mahiddhikā</p>', '-24-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 25, '<p>puññaṃ taṃ anumoditvā - ciraṃ rakkhantu sambuddha sāvakaṃ!</p><p></p><p></p><p>kappasatasahassāni soḷasāpiasaṅkhiye,</p><p>sambharaṃ bodhisambhāre āgato jāti jātiyo,</p><p>niyato buddhabhāvāya bhaddakappe idhaṃtime,</p><p>bodhisatto ca metteyyo puññaṃ me anumodatu!</p><p></p><p></p><p>cattāro ca mahārājā - sakko cāti surāpare,</p><p>anumoditvā imaṃ puññaṃ - rakkhantu jina sāsanaṃ!</p><p></p><p></p><p>asmiṃ vihāre ca ārāme - adhivatthā ca devatā,</p><p>anumoditvā imaṃ puññaṃ - rakkhantu jina sāsanaṃ!</p><p></p><p></p><p>sabbe sattā ca majjhattā - hitā ca ahitā ca me,</p><p>anumoditvā imaṃ puññaṃ - bujjhantu amataṃ padaṃ!</p><p>To Late Teachers</p><p>Sīlādinā guṇagaṇena katūbhayatthā</p><p>therāsabhā mama garū tidivaṃ supattā</p><p>puññena nena pakatena samiddhacittā</p><p>santiṃ bhavantu hadayassa sukhena pattā.</p><p>To Respected Elders/ Teachers</p><p>Dhammena nena matisiddha mudācitena,</p><p>buddhādi vatthu gata dhamma balena saddhiṃ,</p><p>therāsabho janānanda yatinda nātho,</p><p>dīghāyuko bhavatu sādhu samiddha citto!</p><p></p><p></p><p>dhammena nena matisiddha mudācitena,</p><p>buddhādi vatthu gata dhamma balena saddhiṃ,</p><p>therāsabhātha garugārava bhāvanīyā,</p><p>dīghāyukā bhavatha sādhu samiddha cittā!</p><p>Patthanā (Wholesome wishes) </p>', '-25-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '19.Khamāpana gāthā (Asking for forgiveness):', 'title', 26);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Tesattatiñāṇānī', 'title', 26);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 26, '<p>iminā puñña kammena mā me bāla samāgamo</p><p>sataṃ samāgamo hotu yāva nibbāna pattiyā!!!         (three times)</p><p></p><p></p><p>idaṃ me puññakammaṃ āsavakkhayāvahaṃ hotu, !!! (three times)</p><p>sabba dukkhā pamuñjatu!</p><p></p><p></p><p>imāya dhammānu dhammapaṭipattiyā buddhaṃ pūjemi!</p><p>imāya dhammānu dhammapaṭipattiyā dhammaṃ pūjemi!</p><p>imāya dhammānu dhammapaṭipattiyā saṅghaṃ pūjemi!</p><p>addhā imāya paṭipattiyā jāti, jarā, vyādhi, maraṇamhā parimuccissāmī’ti!</p><p></p><p><h1>19.Khamāpana gāthā (Asking for forgiveness):</h1></p><p>kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me bhante bhūripañña tathāgata.</p><p></p><p></p><p>Kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me dhamma sandiṭṭhika akālika.</p><p>Kāyena vācā cittena pamādena mayā kataṃ</p><p>accayaṃ khama me saṅgha puññakkhetta anuttara.</p><p>sādhu! sādhu! sādhu!</p><p>________________</p><p></p><p><h1>Tesattatiñāṇānī</h1></p><p> </p><p></p><p>(The seventy-three kinds of knowledge)</p><p>1. Sotāvadhāne paññā sutamaye ñāṇaṃ.</p><p>2. Sutvāna saṃvare paññā sīlamaye ñāṇaṃ.</p><p>3. Saṃvaritvā samādahane paññā samādhibhāvanāmaye ñāṇaṃ.</p><p>4. Paccayapariggahe paññā dhammaṭṭhitiñāṇaṃ.</p><p>5. Atītānāgatapaccuppannānaṃ dhammānaṃ saṅkhipitvā vavatthāne paññā sammasane ñāṇaṃ.</p><p>6.paccuppannānaṃ dhammānaṃ vipariṇāmānupassane paññā udayabbayānupassane ñāṇaṃ.</p><p>7. Ārammaṇaṃ paṭisaṅkhā bhaṅgānupassane paññā vipassane ñāṇaṃ.</p>', '-26-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 27, '<p>8. Bhayatupaṭṭhāne paññā ādīnave ñāṇaṃ.</p><p>9. Muñcitukamyatāpaṭisaṅkhāsantiṭṭhanā paññā saṅkhārupekkhāsu ñāṇaṃ.</p><p>10. Bahiddhā vuṭṭhānavivaṭṭane paññā gotrabhuñāṇaṃ.</p><p>11. Dubhato vuṭṭhānavivaṭṭane paññā magge ñāṇaṃ.</p><p>12. Payogappaṭippassaddhi paññā phale ñāṇaṃ.</p><p>13. Chinnavaṭumānupassane, paññā vimuttiñāṇaṃ.</p><p>14. Tadā samudāgate dhamme passane paññā paccavekkhaṇe ñāṇaṃ.</p><p>15. Ajjhattavavatthāne paññā vatthunānatte ñāṇaṃ.</p><p>16. Bahiddhāvavatthāne paññā gocaranānatte ñāṇaṃ.</p><p>17. Cariyāvavatthāne paññā cariyānānatte ñāṇaṃ.</p><p>18. Catudhammavavatthāne paññā bhūminānatte ñāṇaṃ.</p><p>19. Navadhammavavatthāne paññā dhammanānatte ñāṇaṃ.</p><p>20. Abhiññāpaññā ñātaṭṭhe ñāṇaṃ.</p><p>21. Pariññāpaññā tīraṇaṭṭhe ñāṇaṃ.</p><p>22. Pahāne paññā pariccāgaṭṭhe ñāṇaṃ.</p><p>23. Bhāvanāpaññā ekarasaṭṭhe ñāṇaṃ.</p><p>24. Sacchikiriyāpaññā phusanaṭṭhe ñāṇaṃ.</p><p>25. Atthanānatte paññā atthapaṭisambhide ñāṇaṃ.</p><p>26. Dhammanānatte paññā dhammapaṭisambhide ñāṇaṃ.</p><p>27. Niruttinānatte paññā niruttipaṭisambhide ñāṇaṃ.</p><p>28. Paṭibhānanānatte paññā paṭibhānapaṭisambhide ñāṇaṃ.</p><p>29. Vihāranānatte paññā vihāraṭṭhe ñāṇaṃ.</p><p>30. Samāpattinānatte paññā samāpattaṭṭhe ñāṇaṃ.</p><p>31. Vihārasamāpattinānatte paññā vihārasamāpattaṭṭhe ñāṇaṃ.</p><p>32. Avikkhepaparisuddhattā āsavasamucchede paññā ānantarikasamādhimhi ñāṇaṃ.</p><p>33. Dassanādhipateyyaṃ santo ca vihārādhigamo paṇītādhimuttatāpaññā araṇavihāreñāṇaṃ.</p><p>34. Dvīhibalehi samannāgatattā tayo ca saṅkhārānaṃ paṭippassaddhiyā soḷasahiñāṇacariyāhi navahi samādhicariyāhi vasibhāvatā paññā nirodhasamāpattiyā ñāṇaṃ.</p><p>35. Sampajānassa pavattapariyādāne paññā parinibbāne ñāṇaṃ.</p><p>36. Sabbadhammānaṃ sammā samucchede nirodhe ca anupaṭṭhānatā paññā samasīsaṭṭhe ñāṇaṃ.</p><p>37. Puthunānattatejapariyodāne paññā sallekhaṭṭhe ñāṇaṃ.</p><p>38. Asallīnattapahitattapaggahaṭṭhe paññā viriyārambhe ñāṇaṃ.</p><p>39. Nānādhammepakāsanatā paññā atthasandassane ñāṇaṃ.</p><p>40.sabbadhammānaṃ ekasaṅgahatānānattekattapaṭivedhe paññā dassanavisuddhiñāṇaṃ</p><p>41. Viditattā paññā khantiñāṇaṃ.</p><p>42. Phuṭṭhattā paññā pariyogāhane ñāṇaṃ.</p><p>43. Samodahane paññā padesavihāre ñāṇaṃ.</p>', '-27-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Sattasattatiñāṇāni', 'title', 28);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 28, '<p>44. Adhipatattā paññā saññāvivaṭṭe ñāṇaṃ.</p><p>45. Nānatte paññā cetovivaṭṭe ñāṇaṃ.</p><p>46. Adhiṭṭhāne paññā cittavivaṭṭe ñāṇaṃ.</p><p>47. Suññate paññā ñāṇavivaṭṭe ñāṇaṃ.</p><p>48. Vossagge paññā vimokkhavivaṭṭe ñāṇaṃ.</p><p>49. Tathaṭṭhe paññā saccavivaṭṭe ñāṇaṃ.</p><p>50. Kāyampi cittampi ekavavatthānatā sukhasaññañca lahusaññañca adhiṭṭhānavasena       ijjhanaṭṭhe paññā iddhividhe ñāṇaṃ.</p><p>51. Vitakkavipphāravasena nānattekattasaddanimittānaṃ pariyogāhaṇe paññā         sotadhātuvisuddhiñāṇaṃ.</p><p>52. Tiṇṇaṃ cittānaṃ vipphārattā indriyānaṃ pasādavasena nānattekatta viññāṇacariyā pariyogāhaṇe paññā cetopariyañāṇaṃ.</p><p>53. Paccayappavattānaṃ dhammānaṃ nānattekattakammavipphāravasena pariyogāhane paññā pubbenivāsānussatiñāṇaṃ.</p><p>54.obhāsavasena nānattekattarūpanimittānaṃ dassanaṭṭhe paññā dibbacakkhuñāṇaṃ.</p><p>55. Catusaṭṭhiyā ākārehi tiṇṇaṃ indriyānaṃ vasībhāvatā paññā āsavānaṃ khaye ñāṇaṃ.</p><p>56. Pariññaṭṭhe paññā dukkhe ñāṇaṃ.</p><p>57. Pahānaṭṭhe paññā samudaye ñāṇaṃ.</p><p>58. Sacchikiriyaṭṭhe paññā nirodhe ñāṇaṃ.</p><p>59. Bhāvanaṭṭhe paññā magge ñāṇaṃ.</p><p>60. Dukkhe ñāṇaṃ.</p><p>61. Dukkhasamudaye ñāṇaṃ.</p><p>62. Dukkhanirodhe ñāṇaṃ.</p><p>63. Dukkhanirodhagāminiyā paṭipadāya ñāṇaṃ.</p><p>64. Atthapaṭisambhide ñāṇaṃ.</p><p>65. Dhammapaṭisambhide ñāṇaṃ.</p><p>66. Niruttipaṭisambhide ñāṇaṃ.</p><p>67. Paṭibhānapaṭisambhide ñāṇaṃ.</p><p>68. Indriyaparopariyatte ñāṇaṃ.</p><p>69. Sattānaṃ āsayānusaye ñāṇaṃ.</p><p>70. Yamakapāṭihīre ñāṇaṃ.</p><p>71. Mahākaruṇāsamāpattiyā ñāṇaṃ.</p><p>72. Sabbaññutañāṇaṃ.</p><p>73. Anāvaraṇañāṇaṃ.</p><p>Imāni tesattati ñāṇāni. Imesaṃ tesattatiyā ñāṇānaṃ sattasaṭṭhi ñāṇāni sāvakasādhāraṇāni; cha ñāṇāni asādhāraṇāni sāvakehi. Imehi tesattatiyā ñāṇehi samannāgataṃ sammāsambuddhaṃ bhagavantaṃ sirasā namāmi. </p><p>________________</p><p></p><p><h1>Sattasattatiñāṇāni</h1></p><p> </p><p>The seventy-seven subjects of knowledge</p>', '-28-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Paritta Desanā', 'title', 29);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Paṭhamo Bhānavāro', 'chapter', 29);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Devatārādhanā', 'title', 29);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '1. Dasadhammasuttaṃ', 'title', 29);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 29, '<p>1. Jātipaccayā jarāmaraṇanti ñāṇaṃ; asati jātiyā natthi jarāmaraṇanti ñāṇaṃ; atītampi addhānaṃ jātipaccayā jarāmaraṇanti ñāṇaṃ, asati jātiyā natthi jarāmaraṇanti ñāṇaṃ; anāgatampi addhānaṃ jātipaccayā jarāmaraṇanti ñāṇaṃ, asati jātiyā natthi jarāmaraṇanti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>2. Bhavapaccayā jātīti ñāṇaṃ; asati bhavasmiṃ natthi jātīti ñāṇaṃ; atītampi addhānaṃ bhavapaccayā jātīti ñāṇaṃ, asati bhavasmiṃ natthi jātīti ñāṇaṃ; anāgatampi addhānaṃ bhavapaccayā jātīti ñāṇaṃ, asati bhavasmiṃ natthi jātīti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>3. Upādānapaccayā bhavoti ñāṇaṃ; asati upādānasmiṃ natthi bhavoti ñāṇaṃ; atītampi addhānaṃ upādānapaccayā bhavoti ñāṇaṃ, asati upādānasmiṃ natthi bhavoti ñāṇaṃ; anāgatampi addhānaṃ upādānapaccayā bhavoti ñāṇaṃ, asati upādānasmiṃ natthi bhavoti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>4. Taṇhāpaccayā upādānanti ñāṇaṃ; asati taṇhāya natthi upādānanti ñāṇaṃ; atītampi addhānaṃ taṇhāpaccayā upādānanti ñāṇaṃ, asati taṇhāya natthi upādānanti ñāṇaṃ; anāgatampi addhānaṃ taṇhāpaccayā upādānanti ñāṇaṃ, asati taṇhāya natthi upādānanti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>5. Vedanāpaccayā taṇhāti ñāṇaṃ; asati vedanāya natthī taṇhāti ñāṇaṃ; atītampi addhānaṃ vedanāpaccayā taṇhāti ñāṇaṃ, asati vedanāya natthī taṇhāti ñāṇaṃ; anāgatampi addhānaṃ vedanāpaccayā taṇhāti ñāṇaṃ, asati vedanāya natthī taṇhāti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>6. Phassapaccayā vedanāti ñāṇaṃ; asati phassasmiṃ natthi vedanāti ñāṇaṃ; atītampi addhānaṃ phassapaccayā vedanāti ñāṇaṃ, asati phassasmiṃ natthi vedanāti ñāṇaṃ; anāgatampi addhānaṃ phassapaccayā vedanāti ñāṇaṃ, asati phassasmiṃ natthi vedanāti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>7. Saḷāyatanapaccayā phassoti ñāṇaṃ; asati saḷāyatanasmiṃ natthi phassoti ñāṇaṃ atītampi addhānaṃ saḷāyatanapaccayā phassoti ñāṇaṃ, asati saḷāyatanasmiṃ natthi phassoti ñāṇaṃ; anāgatampi addhānaṃ saḷāyatanapaccayā phassoti ñāṇaṃ, asati saḷāyatanasmiṃ natthi phassoti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>8. Nāmarūpapaccayā saḷāyatananti ñāṇaṃ; asati nāmarūpasmiṃ natthi saḷāyatananti ñāṇaṃ; atītampi addhānaṃ nāmarūpapaccayā saḷāyatananti ñāṇaṃ, asati nāmarūpasmiṃ natthi saḷāyatananti ñāṇaṃ; anāgatampi addhānaṃ nāmarūpapaccayā saḷāyatananti ñāṇaṃ, asati nāmarūpasmiṃ natthi saḷāyatananti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>9. Viññāṇapaccayā nāmarūpanti ñāṇaṃ; asati viññāṇasmiṃ natthi nāmarūpanti ñāṇaṃ; atītampi addhānaṃ viññāṇapaccayā nāmarūpanti ñāṇaṃ, asati viññāṇasmiṃ natthi nāmarūpanti ñāṇaṃ; anāgatampi addhānaṃ viññāṇapaccayā nāmarūpanti ñāṇaṃ, asati viññāṇasmiṃ natthi nāmarūpanti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>10. Saṅkhārapaccayā viññāṇanti ñāṇaṃ; asati saṅkhāresu natthi viññāṇanati ñāṇaṃ; atītampi addhānaṃ saṅkhārapaccayā viññāṇnti ñāṇaṃ, asati saṅkhāresu natthi viññāṇanti ñāṇaṃ; anāgatampi saṅkhārapaccayā viññāṇanti ñāṇaṃ, asati saṅkhāresu natthi viññāṇanti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ.</p><p>11. Avijjāpaccayā saṅkhārāti ñāṇaṃ, asati avijjāya natthi saṅkhārāti ñāṇaṃ; atītampi addhānaṃ avijjāpaccayā saṅkhārāti ñāṇaṃ, asati avijjāya natthi saṅkhārāti ñāṇaṃ; anāgatampi addhānaṃ avijjāpaccayā saṅkhārāti ñāṇaṃ, asati avijjāya natthi saṅkhārāti ñāṇaṃ; yampissa taṃ dhammaṭṭhitiñāṇaṃ tampi khayadhammaṃ vayadhammaṃ virāgadhammaṃ nirodhadhammanti ñāṇaṃ. </p><p>Imānī sattasattati ñāṇāni, imehi sattasattatiyā ñāṇehi samannāgataṃ sammāsambuddhaṃ bhagavantaṃ sirasā namāmi.</p><p></p><p></p><p><h1>Paritta Desanā</h1></p><p><h1>Paṭhamo Bhānavāro</h1></p><p><h1>Devatārādhanā</h1></p><p></p><p></p><p></p><p>sagge kāme ca rūpe girisikharataṭe cāntalikkhe vimāne, dīpe raṭṭhe ca gāme taru vana gahaṇe gehavatthumhi khette, bhummā cāyantu devā jalatala visame, yakkha gandhabba nāgā, tiṭṭhantā santikedaṃ munivara vacanaṃ sādhavo me suṇantu.</p><p>Samantā cakkavāḷesu, atrāgacchantu devatā</p><p>saddhammaṃ munirājassa, suṇantu saggamokkhadaṃ</p><p>dhammassavanakālo ayaṃ bhadantā</p><p>dhammassavanakālo ayaṃ bhadantā</p><p>paritta dhammassavanakālo ayaṃ bhadantā</p><p><h1>1. Dasadhammasuttaṃ</h1></p><p></p><p>(Discourse on the Ten Reflection) </p><p>bhikkhūnaṃ guṇa saṃyuttaṃ - yaṃ desesi mahā muni;</p><p>yaṃ sutvā paṭipajjanto - sabba dukkhā pamuccati;</p><p>sabba loka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| tatra kho bhagavā bhikkhū āmantesi.| bhikkhavo ti.| bhadante’ti kho te bhikkhū bhagavato paccassosuṃ.| bhagavā etadavoca.|</p><p>“Dasa ime, bhikkhave, dhammā| pabbajitena abhiṇhaṃ paccavekkhitabbā. |</p><p>katame dasa?|</p><p>   1. ‘Vevaṇṇiyamhi ajjhupagato’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;|</p>', '-29-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '   2. Maṅgalasuttaṃ ', 'title', 30);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 30, '<p>   2. ‘Parapaṭibaddhā me jīvikā’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;|</p><p>   3. ‘Añño me ākappo karaṇīyo’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;|</p><p>   4. ‘Kacci nu kho me attā sīlato na upavadatī’ti | pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;|</p><p>   5. ‘Kacci nu kho maṃ anuvicca viññū sabrahmacārī| sīlato na upavadantī’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;</p><p>   6. ‘Sabbehi me piyehi manāpehi| nānābhāvo vinābhāvo’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ.</p><p>   7. ‘Kammassakomhi kammadāyādo| kammayoni kammabandhu kammapaṭisaraṇo,| yaṃ kammaṃ karissāmi kalyāṇaṃ vā pāpakaṃ vā| tassa dāyādo bhavissāmī’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;</p><p>   8. ‘Kathambhūtassa me rattiṃ divā vītivattantī’ti | pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;</p><p>   9. ‘Kacci nu kho ahaṃ suññāgāre abhiramāmī’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ;</p><p>   10. ‘Atthi nu kho me uttari manussadhammā| alamariyañāṇadassanaviseso adhigato,| sohaṃ pacchime kāle sabrahmacārīhi puṭṭho na maṅku bhavissāmī’ti| pabbajitena abhiṇhaṃ paccavekkhitabbaṃ.</p><p></p><p></p><p>ime kho bhikkhave, dasa dhammā| pabbajitena abhiṇhaṃ paccavekkhitabbā”ti.| idamaveāca bhagavā| attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.</p><p>(A.III.325)</p><p>Dasadhammasuttaṃ niṭṭhitaṃ</p><p></p><p></p><p>   <h1>2. Maṅgalasuttaṃ</h1> </p><p>(Great discourse on blessings)</p><p>yaṃ maṅgalaṃ dvādasasu - cintayiṃsu sadevakā;</p><p>sotthānaṃ nādhigacchantī - aṭṭhatiṃsaṃ ca maṅgalaṃ;</p><p>desitaṃ deva devena - sabbapāpa vināsanaṃ;</p><p>sabba loka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. Atha kho, aññatarā devatā abhikkantāya rattiyā| abhikkantavaṇṇā kevalakappaṃ jetavanaṃ obhāsetvā| yena bhagavā tenupasaṅkami; upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsi. Ekamantaṃ ṭhitā kho sā devatā bhagavantaṃ gāthāya ajjhabhāsi –</p><p>      1. “Bahū devā manussā ca, maṅgalāni acintayuṃ;</p><p>ākaṅkhamānā sotthānaṃ, brūhi maṅgalamuttamaṃ”.</p><p>      2. “Asevanā ca bālānaṃ, paṇḍitānañca sevanā;</p><p>pūjā ca pūjanīyānaṃ, etaṃ maṅgalamuttamaṃ.</p><p>      3. “Patirūpadesavāso ca, pubbe ca katapuññatā;</p><p>attasammāpaṇidhi ca, etaṃ maṅgalamuttamaṃ.</p><p>      4. “Bāhusaccañca sippañca, vinayo ca susikkhito;</p><p>subhāsitā ca yā vācā, etaṃ maṅgalamuttamaṃ.</p><p>      5. “Mātāpitu upaṭṭhānaṃ, puttadārassa saṅgaho;</p><p>anākulā ca kammantā, etaṃ maṅgalamuttamaṃ.</p><p>      6. “Dānañca dhammacariyā ca, ñātakānañca saṅgaho;</p><p>anavajjāni kammāni, etaṃ maṅgalamuttamaṃ.</p><p>      7. “Ārati virati pāpā, majjapānā ca saṃyamo;</p>', '-30-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '      3. Ratanasuttaṃ ', 'title', 31);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 31, '<p>appamādo ca dhammesu, etaṃ maṅgalamuttamaṃ.</p><p>      8. “Gāravo ca nivāto ca, santuṭṭhi ca kataññutā;</p><p>kālena dhammassavanaṃ, etaṃ maṅgalamuttamaṃ.</p><p>      9. “Khantī ca sovacassatā, samaṇānañca dassanaṃ;</p><p>kālena dhammasākacchā, etaṃ maṅgalamuttamaṃ.</p><p>      10. “Tapo ca brahmacariyañca, ariyasaccāna dassanaṃ;</p><p>nibbānasacchikiriyā ca, etaṃ maṅgalamuttamaṃ.</p><p>      11. “Phuṭṭhassa lokadhammehi, cittaṃ yassa na kampati;</p><p>asokaṃ virajaṃ khemaṃ, etaṃ maṅgalamuttamaṃ.</p><p>      12. “Etādisāni katvāna, sabbatthamaparājitā;</p><p>sabbattha sotthiṃ gacchanti, taṃ tesaṃ maṅgalamuttama”nti.</p><p>(Khp.3)</p><p>maṅgalasuttaṃ niṭṭhitaṃ.</p><p>________________</p><p></p><p></p><p>      <h1>3. Ratanasuttaṃ</h1> </p><p>(Discourse on the Triple Gems)</p><p>koṭīsatasahassesu - cakkavāḷesu devatā;</p><p>yassānaṃ patigaṇhanti - yañca vesāliyaṃ pure;</p><p>rogāmanussa dubbhikkha - sambhūtaṃ tividhaṃ bhayaṃ;</p><p>khippamantaradhāpesi - parittaṃ taṃ bhaṇāmahe.</p><p></p><p></p><p>         1. Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe;</p><p>sabbeva bhūtā sumanā bhavantu, athopi sakkacca suṇantu bhāsitaṃ.</p><p>         2. Tasmā hi bhūtā nisāmetha sabbe, mettaṃ karotha mānusiyā pajāya;</p><p>divā ca ratto ca haranti ye baliṃ, tasmā hi ne rakkhatha appamattā.</p><p>         3. Yaṃ kiñci vittaṃ idha vā huraṃ vā, saggesu vā yaṃ ratanaṃ paṇītaṃ;</p><p>na no samaṃ atthi tathāgatena, idampi buddhe ratanaṃ paṇītaṃ;</p><p>etena saccena suvatthi hotu.</p><p>         4. Khayaṃ virāgaṃ amataṃ paṇītaṃ, yadajjhagā sakyamunī samāhito;</p><p>na tena dhammena samatthi kiñci, idampi dhamme ratanaṃ paṇītaṃ;</p><p>etena saccena suvatthi hotu.</p><p>         5. Yaṃ buddhaseṭṭho parivaṇṇayī suciṃ, samādhimānantarikaññamāhu;</p><p>samādhinā tena samo na vijjati, idampi dhamme ratanaṃ paṇītaṃ;</p>', '-31-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 32, '<p>etena saccena suvatthi hotu.</p><p>         6. Ye puggalā aṭṭha sataṃ pasatthā, cattāri etāni yugāni honti;</p><p>te dakkhiṇeyyā sugatassa sāvakā, etesu dinnāni mahapphalāni;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         7. Ye suppayuttā manasā daḷhena, nikkāmino gotamasāsanamhi;</p><p>te pattipattā amataṃ vigayha, laddhā mudhā nibbutiṃ bhuñjamānā;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         8. Yathindakhīlo paṭhaviṃsito siyā, catubbhi vātehi asampakampiyo;</p><p>tathūpamaṃ sappurisaṃ vadāmi, yo ariyasaccāni avecca passati;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         9. Ye ariyasaccāni vibhāvayanti, gambhīrapaññena sudesitāni;</p><p>kiñcāpi te honti bhusappamattā, na te bhavaṃ aṭṭhamamādiyanti;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         10. Sahāvassa dassanasampadāya, tayassu dhammā jahitā bhavanti;</p><p>sakkāyadiṭṭhī vicikicchitañca, sīlabbataṃ vāpi yadatthi kiñci.</p><p>Catūhapāyehi ca vippamutto, cha cābhiṭhānāni abhabbokātuṃ;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         11. Kiñcāpi so kamma karoti pāpakaṃ, kāyena vācā uda cetasā vā;</p><p>abhabboso tassa paṭicchadāya, abhabbatā diṭṭhapadassa vuttā;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         12. Vanappagumbe yathā phussitagge, gimhānamāse paṭhamasmiṃ gimhe;</p><p>tathūpamaṃ dhammavaraṃ adesayī, nibbānagāmiṃ paramaṃ hitāya;</p><p>idampi buddhe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         13. Varo varaññū varado varāharo, anuttaro dhammavaraṃ adesayi;</p><p>idampi buddhe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         14. Khīṇaṃ purāṇaṃ navaṃ natthi sambhavaṃ, virattacittā āyatike bhavasmiṃ;</p><p>te khīṇabījā avirūḷhichandā, nibbanti dhīrā yathāyaṃ padīpo;</p><p>idampi saṅghe ratanaṃ paṇītaṃ, etena saccena suvatthi hotu.</p><p>         15. Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe;</p><p>tathāgataṃ devamanussapūjitaṃ, buddhaṃ namassāma suvatthi hotu.</p><p>         16. Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe;</p><p>tathāgataṃ devamanussapūjitaṃ, dhammaṃ namassāma suvatthi hotu.</p><p>         17. Yānīdha bhūtāni samāgatāni, bhummāni vā yāni va antalikkhe;</p><p>tathāgataṃ devamanussapūjitaṃ, saṅghaṃ namassāma suvatthi hotu.</p><p>(Khp.4)</p><p>Ratanasuttaṃ niṭṭhitaṃ.</p>', '-32-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '4. Mettasuttaṃ ', 'title', 33);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '5. Khandhasuttaṃ            ', 'title', 33);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 33, '<p>         </p><p><h1>4. Mettasuttaṃ</h1> </p><p>(Discourse on Loving kindness) </p><p>yassānubhāvato yakkhā - neva dassenti bhiṃsanaṃ;</p><p>yamhi cevānuyuñjanto - rattiṃ divamatandito;</p><p>sukhaṃ supati sutto ca - pāpaṃ kiñci na passati;</p><p>evamādi guṇopetaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>            1. Karaṇīyamatthakusalena, yaṃ taṃ santaṃ padaṃ abhisamecca; sakko ujū ca suhujū ca, suvaco cassa mudu anatimānī.</p><p>            2. Santussako ca subharo ca, appakicco ca sallahukavutti; santindriyo ca nipako ca, appagabbho kulesu ananugiddho.</p><p>            3. Na ca khuddaṃ samācare, kiñci yena viññū pare upavadeyyuṃ; sukhino vā khemino hontu, sabbasattā bhavantu sukhitattā.</p><p>            4. Ye keci pāṇabhūtatthi, tasā vā thāvarā vā anavasesā; dīghā vā ye mahantā vā, majjhimā rassakāṇukathūlā.</p><p>            5. Diṭṭhā vā yeva adiṭṭhā, ye ca dūre vasanti avidūre; bhūtā vā sambhavesī vā, sabbasattā bhavantu sukhitattā.</p><p>            6. Na paro paraṃ nikubbetha, nātimaññetha katthaci naṃ kañci; byārosanā paṭighasaññā, nāññamaññassa dukkhamiccheyya.</p><p>            7. Mātā yathā niyaṃ puttaṃ āyusā ekaputtamanurakkhe; evampi sabbabhūtesu, mānasaṃ bhāvaye aparimāṇaṃ.</p><p>            8. Mettañca sabbalokasmi, mānasaṃ bhāvaye aparimāṇaṃ; uddhaṃ adho ca tiriyañca, asambādhaṃ averaṃ asapattaṃ.</p><p>            9. Tiṭṭhaṃ caraṃ nisinno vā, sayāno yāvatassa vigatamiddho; etaṃ satiṃ adhiṭṭheyya, brahmametaṃ vihāraṃ idhamāhu.</p><p>            10. Diṭṭhiñca anupagamma, sīlavā dassanena sampanno; kāmesu vineyya gedhaṃ, na hi jātugabbhaseyyaṃ punaretī’ti.</p><p>(Khp.10)</p><p>Mettasuttaṃ niṭṭhitaṃ.</p><p></p><p></p><p><h1>5. Khandhasuttaṃ</h1>            </p><p>(Discourse for protection of Body) </p><p>sabbāsī visajātīnaṃ - dibbamantā gadaṃ viya;</p><p>yaṃ nāsesi visaṃ ghoraṃ - sesañcāpi parissayaṃ;</p><p>āṇakkhettamhi sabbattha - sabbadā sabbapāṇinaṃ;</p><p>sabbasopi vināseti - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. Tena kho pana samayena sāvatthiyaṃ aññataro bhikkhu ahinā daṭṭho kālakato hoti. Atha kho sambahulā bhikkhū yena bhagavā tenupasaṅkamiṃsu;| upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu. Ekamantaṃ nisinnā kho te bhikkhū bhagavantaṃ etadavocuṃ| – “Idha, bhante, sāvatthiyaṃ aññataro bhikkhu ahinā daṭṭho kālaṅkato”ti.</p><p>“Naha nūna so, bhikkhave, bhikkhu cattāri ahirājakulāni mettena cittena phari.| sace hi so, bhikkhave, bhikkhu cattāri ahirājakulāni mettena cittena phareyya,| na hi so, bhikkhave, bhikkhu ahinā daṭṭho kālaṅkareyya.</p><p>“Katamāni cattāri?| virūpakkhaṃ ahirājakulaṃ, erāpathaṃ ahirājakulaṃ,| chabyāputtaṃ ahirājakulaṃ, kaṇhāgotamakaṃ ahirājakulaṃ. Naha nūna so, bhikkhave, bhikkhu imāni cattāri ahirājakulāni mettena cittena phari.| sace hi so, bhikkhave, bhikkhu imāni cattāri ahirājakulāni mettena cittena phareyya,| na hi so, bhikkhave, bhikkhu ahinā daṭṭho kālaṅkareyya.|</p><p>“Anujānāmi, bhikkhave, imāni cattāri ahirājakulāni mettena cittena pharituṃ| attaguttiyā attarakkhāya attaparittāyā”ti.</p><p>               1. “Virūpakkhehi me mettaṃ, mettaṃ erāpathehi me; chabyāputtehi me mettaṃ, mettaṃ kaṇhāgotamakehi ca.</p><p>               2. “Apādakehi me mettaṃ, mettaṃ dipādakehi me; catuppadehi me mettaṃ, mettaṃ bahuppadehi me.</p><p>               3. “Mā maṃ apādako hiṃsi, mā maṃ hiṃsi dipādako; mā maṃ catuppado hiṃsi, mā maṃ hiṃsi bahuppado.</p><p>               4. “Sabbe sattā sabbe pāṇā, sabbe bhūtā ca kevalā; sabbe bhadrāni passantu, mā kañci pāpamāgamā.</p><p>“Appamāṇo buddho, appamāṇo dhammo; appamāṇo saṅgho,| pamāṇavantāni sariṃsapāni.“Ahivicchikā satapadī, uṇṇanābhī sarabū mūsikā;| katā me rakkhā katā me parittā,| paṭikkamantu bhūtāni; sohaṃ namo bhagavato,| namo sattannaṃ sammāsambuddhāna”nti.</p>', '-33-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '6. Mettānisaṃsasuttaṃ ', 'title', 34);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '7. Mittānisaṃsasuttaṃ', 'title', 34);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 34, '<p>(A.I.383)</p><p>khandhaparittaṃ niṭṭhitaṃ</p><p></p><p><h1>6. Mettānisaṃsasuttaṃ </h1></p><p>               </p><p>(Discourse on the benefits of loving kindness)  </p><p>aggikkhandhopamaṃ sutvā - jātasaṃvegabhikkhunaṃ,</p><p>assādatthāya desesi - yaṃ parittaṃ mahāmuni,</p><p>sabbalokahitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>Mettāya, bhikkhave, cetovimuttiyā| āsevitāya bhāvitāya bahulīkatāya| yānīkatāya vatthukatāya anuṭṭhitāya paricitāya susamāraddhāya| ekādasānisaṃsā pāṭikaṅkhā.</p><p>Katame ekādasa?</p><p>Sukhaṃ supati, sukhaṃ paṭibujjhati,| na pāpakaṃ supinaṃ passati,| manussānaṃ piyo hoti, amanussānaṃ piyo hoti,| devatā rakkhanti, nāssa aggi vā visaṃ vā satthaṃ vā kamati,| tuvaṭaṃ cittaṃ samādhiyati,| mukhavaṇṇo vippasīdati,| asammūḷho kālaṃ karoti,| uttariṃ appaṭivijjhanto brahmalokūpago hoti.</p><p>Mettāya, bhikkhave, cetovimuttiyā| āsevitāya bhāvitāya bahulīkatāya| yānīkatāya vatthukatāya anuṭṭhitāya paricitāya susamāraddhāya| ime ekādasānisaṃsā pāṭikaṅkhā”ti.</p><p>(A.III.1)</p><p>mettānisaṃsa suttaṃ niṭṭhitaṃ</p><p></p><p></p><p><h1>7. Mittānisaṃsasuttaṃ</h1></p><p>                  </p><p>(Discourse on the Benefits of Friendship) </p><p>pūrento bodhisambhāre - jātho temiyajātiyaṃ,</p><p>mittānisaṃsaṃ yaṃ āha - sunandaṃ nāma sārathiṃ,</p><p>sabbaloka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>                     1. “Pahūtabhakkho bhavati, vippavuttho sakā gharā; </p><p>bahū naṃ upajīvanti, yo mittānaṃ na dūbhati.</p><p>                     2. Yaṃ yaṃ janapadaṃ yāti, nigame rājadhāniyo; </p><p>sabbattha pūjito hoti,        yo mittānaṃ na dūbhati.</p><p>                     3. “Nāssa corā pasahanti, nātimaññeti khattiyo; </p><p>sabbe amitte tarati,yo mittānaṃ na dūbhati.</p><p>                     4. “Akkuddho sagharaṃ eti, sabhāya paṭinandito; </p><p>ñātīnaṃ uttamo hoti, yo mittānaṃ na dūbhati.</p><p>                     5. “Sakkatvā sakkato hoti, garu hoti sagāravo; </p><p>vaṇṇakittibhato hoti, yo mittānaṃ na dūbhati.</p><p>                     6. “Pūjako labhate pūjaṃ, vandako paṭivandanaṃ; </p><p>yaso kittiñca pappoti, yo mittānaṃ na dūbhati.</p><p>                     7. “Aggi yathā pajjalati, devatāva virocati; </p>', '-34-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '8. Morasuttaṃ', 'title', 35);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 35, '<p>siriyā ajahito hoti,        yo mittānaṃ na dūbhati.</p><p>                     8. “Gāvo tassa pajāyanti,        khette vuttaṃ virūhati; </p><p>puttānaṃ phalamasnāti, yo mittānaṃ na dūbhati.</p><p>                     9. “Darito pabbatāto vā,        rukkhāto patito naro; </p><p>cuto patiṭṭhaṃ labhati, yo mittānaṃ na dūbhati.</p><p>                     10. “Virūḷhamūlasantānaṃ, nigrodhamiva māluto; </p><p>amittā nappasahanti,        yo mittānaṃ na dūbhati”.</p><p>(J.II.150)</p><p>mittānisaṃsa suttaṃ niṭṭhitaṃ.</p><p>          </p><p><h1>8. Morasuttaṃ</h1></p><p>(Discourse on peacock’s pretective chant) </p><p>pūrentaṃ bodhi sambhāre - nibbatto mora yoniyaṃ;</p><p>yena saṃvihitā rakkhaṃ - mahā sattaṃ vane carā;</p><p>cirassaṃ vāya mantāpi - neva sakkhiṃsu gaṇhituṃ;</p><p>brahma mantanti akkhātaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                        1. Udetayaṃ cakkhumā ekarājā,</p><p>harissavaṇṇo paṭhavippabhāso;</p><p>taṃ taṃ namassāmi harissavaṇṇaṃ paṭhavippabhāsaṃ,</p><p>tayajja guttā viharemu divasaṃ.</p><p>Ye brāhmaṇā vedagū sabbadhamme,</p><p>te me namo te ca maṃ pālayantu;</p><p>namatthu buddhānaṃ namatthu bodhiyā,</p><p>namo vimuttānaṃ namo vimuttiyā;</p><p>imaṃ so parittaṃ katvā, moro carati esanā.</p><p>                        2. Apetayaṃ cakkhumā ekarājā,</p><p>harissavaṇṇo paṭhavippabhāso;</p><p>taṃ taṃ namassāmi harissavaṇṇaṃ paṭhavippabhāsaṃ,</p><p>tayajja guttā viharemu rattiṃ.</p><p>Ye brāhmaṇā vedagū sabbadhamme,</p><p>te me namo te ca maṃ pālayantu;</p><p>namatthu buddhānaṃ namatthu bodhiyā,</p><p>namo vimuttānaṃ namo vimuttiyā;</p><p>imaṃ so parittaṃ katvā, moro vāsamakappayī”ti.</p><p>(J.I.38)</p><p>mora parittaṃ niṭṭhitaṃ. </p>', '-35-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '9. Candimasuttaṃ', 'title', 36);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '10. sūriyasuttaṃ', 'title', 36);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 36, '<p>                        </p><p><h1>9. Candimasuttaṃ</h1></p><p>(Discourse on the Moon deity) </p><p>rāhunā gahito cando - mutto yassānubhāvato,</p><p>sabbaveri bhayaṃ nāsaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| tena kho pana samayena candimā devaputto rāhunā asurindena gahito hoti.| atha kho candimā devaputto bhagavantaṃ anussaramāno| tāyaṃ velāyaṃ imaṃ gāthaṃ abhāsi.</p><p>“Namo te buddha vīratthu, vippamuttosi sabbadhi;</p><p>sambādhapaṭipannosmi, tassa me saraṇaṃ bhavā”ti.</p><p>Atha kho bhagavā candimaṃ devaputtaṃ ārabbha| rāhuṃ asurindaṃ gāthāya ajjhabhāsi –</p><p>“Tathāgataṃ arahantaṃ, candimā saraṇaṃ gato;</p><p>rāhu candaṃ pamuñcassu, buddhā lokānukampakā”ti.</p><p>Atha kho rāhu asurindo candimaṃ devaputtaṃ muñcitvā| taramānarūpo yena vepacitti asurindo| tenupasaṅkami;| upasaṅkamitvā saṃviggo lomahaṭṭhajāto| ekamantaṃ aṭṭhāsi.| ekamantaṃ ṭhitaṃ kho rāhuṃ asurindaṃ vepacitti asurindo| gāthāya ajjhabhāsi.</p><p>“Kiṃ nu santaramānova, rāhu candaṃ pamuñcasi;</p><p>saṃviggarūpo āgamma, kiṃ nu bhītova tiṭṭhasī”ti.</p><p>“Sattadhā me phale muddhā, jīvanto na sukhaṃ labhe;</p><p>buddhagāthābhigītomhi, no ce muñceyya candima”nti.</p><p>(S.I.48)</p><p>candaparittaṃ niṭṭhitaṃ.</p><p>                           </p><p><h1>10. sūriyasuttaṃ</h1></p><p>(Discourse on the Sun deity)</p><p>suriyo rāhu gahito - mutto yassānubhāvato,</p><p>sabbaveri bhayaṃ nāsaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| tena kho pana samayena sūriyo devaputto| rāhunā asurindena gahito hoti.| atha kho sūriyo devaputto bhagavantaṃ anussaramāno| tāyaṃ velāyaṃ imaṃ gāthaṃ abhāsi.</p><p>“Namo te buddha vīratthu, vippamuttosi sabbadhi;</p><p>sambādhapaṭipannosmi, tassa me saraṇaṃ bhavā”ti.</p><p>Atha kho bhagavā sūriyaṃ devaputtaṃ ārabbha| rāhuṃ asurindaṃ gāthāhi ajjhabhāsi.</p><p>“Tathāgataṃ arahantaṃ, sūriyo saraṇaṃ gato;</p><p>rāhu sūriyaṃ pamuñcassu, buddhā lokānukampakā.</p><p>“Yo andhakāre tamasi pabhaṅkaro,</p><p>verocano maṇḍalī uggatejo;</p><p>mā rāhu gilī caraṃ antalikkhe,</p><p>pajaṃ mama rāhu pamuñca sūriya”nti.</p><p>Atha kho rāhu asurindo sūriyaṃ devaputtaṃ muñcitvā| taramānarūpo yena vepacitti asurindo tenupasaṅkami;| upasaṅkamitvā saṃviggo lomahaṭṭhajāto ekamantaṃ aṭṭhāsi.| ekamantaṃ ṭhitaṃ kho rāhuṃ asurindaṃ vepacitti asurindo gāthāya ajjhabhāsi|</p><p>“Kiṃ nu santaramānova, rāhu sūriyaṃ pamuñcasi;</p><p>saṃviggarūpo āgamma, kiṃ nu bhītova tiṭṭhasī”ti.</p>', '-36-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '11. Dhajagga-Suttaṃ', 'title', 37);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Dutiyo Bhāṇavāro', 'chapter', 37);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 37, '<p>“Sattadhā me phale muddhā, jīvanto na sukhaṃ labhe;</p><p>buddhagāthābhigītomhi, no ce muñceyya sūriya”nti.</p><p>(S.I.48)</p><p>suriya parittaṃ niṭṭhitaṃ.</p><p>                     </p><p><h1>11. Dhajagga-Suttaṃ</h1></p><p>(Discourse on the Crest of Banner) </p><p>yassānussaraṇenāpi - annalikkhe’pi pāṇino,</p><p>patiṭṭhamadhigacchanti - bhūmiyā viya sabbathā,</p><p>sabbūpaddava jālamhā - yakkhacorāri sambhavā,</p><p>gaṇanā na ca muttānaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| tatra kho bhagavā bhikkhū āmantesi – “Bhikkhavo”ti.| “Bhadante”ti te bhikkhū bhagavato paccassosuṃ.| bhagavā etadavoca.</p><p>“Bhūtapubbaṃ, bhikkhave, devāsurasaṅgāmo samupabbūḷho ahosi.| atha kho, bhikkhave, sakko devānamindo deve tāvatiṃse āmantesi –</p><p>‘Sace, mārisā, devānaṃ saṅgāmagatānaṃ| uppajjeyya bhayaṃ vā chambhitattaṃ vā lomahaṃso vā,| mameva tasmiṃ samaye dhajaggaṃ ullokeyyātha.| mamañhi vo dhajaggaṃ ullokayataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissati’.</p><p>‘No ce me dhajaggaṃ ullokeyyātha,| atha pajāpatissa devarājassa dhajaggaṃ ullokeyyātha.| pajāpatissa hi vo devarājassa dhajaggaṃ ullokayataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissati’.</p><p>‘Noce pajāpatissa devarājassa dhajaggaṃ ullokeyyātha,| atha varuṇassa devarājassa dhajaggaṃ ullokeyyātha.| varuṇassa hi vo devarājassa dhajaggaṃ ullokayataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissati’.</p><p>‘No ce varuṇassa devarājassa dhajaggaṃ ullokeyyātha,| atha īsānassa devarājassa dhajaggaṃ ullokeyyātha.| īsānassa hi vo devarājassa dhajaggaṃ ullokayataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissatī’”ti.</p><p>“Taṃ kho pana, bhikkhave, sakkassa vā devānamindassa dhajaggaṃ ullokayataṃ,| pajāpatissa vā devarājassa dhajaggaṃ ullokayataṃ,| varuṇassa vā devarājassa dhajaggaṃ ullokayataṃ,| īsānassa vā devarājassa dhajaggaṃ ullokayataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā,| so pahīyethāpi nopi pahīyetha.</p><p>“Taṃ kissa hetu?| sakko hi, bhikkhave, devānamindo| avītarāgo avītadoso avītamoho| bhīrucchambhī utrāsī palāyīti.</p><p>“Ahañca kho, bhikkhave, evaṃ vadāmi| ‘Sace tumhākaṃ, bhikkhave, araññagatānaṃ vā rukkhamūlagatānaṃ vā suññāgāragatānaṃ vā| uppajjeyya bhayaṃ vā chambhitattaṃ vā lomahaṃso vā,| mameva tasmiṃ samaye anussareyyātha| – itipi so bhagavā arahaṃ sammāsambuddho| vijjācaraṇasampanno sugato lokavidū| anuttaro purisadammasārathi| satthā devamanussānaṃ buddho bhagavā’ti.| mamañhi vo, bhikkhave, anussarataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā,| so pahīyissati.</p><p>“No ce maṃ anussareyyātha, atha dhammaṃ anussareyyātha| – ‘Svākkhāto bhagavatā dhammo| sandiṭṭhiko akāliko| ehipassiko opanayako paccattaṃ veditabbo viññūhī’ti.| dhammañhi vo, bhikkhave, anussarataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissati.</p><p>“No ce dhammaṃ anussareyyātha, atha saṅghaṃ anussareyyātha| ‘Supaṭipanno bhagavato sāvakasaṅgho,| ujupaṭipanno bhagavato sāvakasaṅgho,| ñāyapaṭipanno bhagavato sāvakasaṅgho,| sāmīcipaṭipanno bhagavato sāvakasaṅgho,| yadidaṃ cattāri purisayugāni aṭṭha purisapuggalā| esa bhagavato sāvakasaṅgho,| āhuneyyo, pāhuneyyo, dakkhiṇeyyo, añjalikaraṇīyo,| anuttaraṃ puññakkhettaṃ lokassā’ti.| saṅghañhi vo, bhikkhave, anussarataṃ| yaṃ bhavissati bhayaṃ vā chambhitattaṃ vā lomahaṃso vā, so pahīyissati.</p><p>“Taṃ kissa hetu?| tathāgato hi, bhikkhave, arahaṃ sammāsambuddho| vītarāgo vītadoso vītamoho| abhīru acchambhī anutrāsī apalāyī”ti.| idamavoca bhagavā; idaṃ vatvā sugato athāparaṃ etadavoca satthā –</p><p>“Araññe rukkhamūle vā, suññāgāreva bhikkhavo;</p><p>anussaretha sambuddhaṃ, bhayaṃ tumhāka no siyā.</p><p>“No ce buddhaṃ sareyyātha, lokajeṭṭhaṃ narāsabhaṃ;</p><p>atha dhammaṃ sareyyātha, niyyānikaṃ sudesitaṃ.</p><p>“No ce dhammaṃ sareyyātha, niyyānikaṃ sudesitaṃ;</p><p>atha saṅghaṃ sareyyātha, puññakkhettaṃ anuttaraṃ.</p><p>“Evaṃ buddhaṃ sarantānaṃ, dhammaṃ saṅghañca bhikkhavo;</p><p>bhayaṃ vā chambhitattaṃ vā, lomahaṃso na hessatī”ti.</p><p>(S.I.220)</p><p>dhajaggaparittaṃ niṭṭhitaṃ.</p><p>Paṭhamabhāṇavāro niṭṭhito</p><p>________________</p><p><h1>Dutiyo Bhāṇavāro</h1></p>', '-37-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 38, '<p>                                 1. Kassapabojjhaṅgasutta </p><p>(Discourse on the Enlightenment factors chanted for Venerable Mahākassapa)</p><p>yaṃ mahā kassapatthero - parittaṃ muni santikā,</p><p>sutvā tasmiṃ khaṇeyeva - ahosi nirupaddavo,</p><p>bojjhaṅaga bala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe. Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā rājagahe viharati veḷuvane kalandakanivāpe.| tena kho pana samayena āyasmā mahākassapo| pipphaliguhāyaṃ viharati ābādhiko dukkhito bāḷhagilāno.| atha kho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito| yenāyasmā mahākassapo tenupasaṅkami;| upasaṅkamitvā paññatte āsane nisīdi.| nisajja kho bhagavā āyasmantaṃ mahākassapaṃ etadavoca| –</p><p>“Kacci te, kassapa, khamanīyaṃ kacci yāpanīyaṃ?| kacci dukkhā vedanā paṭikkamanti, no abhikkamanti;| paṭikkamosānaṃ paññāyati, no abhikkamo”ti?| “Na me, bhante, khamanīyaṃ, na yāpanīyaṃ.| bāḷhā me dukkhā vedanā abhikkamanti, no paṭikkamanti;| abhikkamosānaṃ paññāyati, no paṭikkamo”ti.|</p><p>“Sattime, kassapa, bojjhaṅgā;| mayā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattanti.</p><p>Katame satta?||</p><p>                                    1. satisambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    2. Dhammavicayasambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    3. Viriyasambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    4. Pītisambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    5. Passaddhisambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    6. Samādhisambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                    7. Upekkhāsambojjhaṅgo kho, kassapa,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati. </p><p>Ime kho, kassapa, satta bojjhaṅgā;| mayā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattantī”ti.| “Taggha, bhagava, bojjhaṅgā; taggha, sugata, bojjhaṅgā”ti.|</p><p>idamavoca bhagavā. Attamano āyasmā mahākassapo| bhagavato bhāsitaṃ abhinandi.| vuṭṭhāhi cāyasmā mahākassapo tamhā ābādhā.| tathāpahīno cāyasmato mahākassapassa| so ābādho ahosīti.</p><p>(S.III.71)</p><p>kassapabojjhaṅga parittaṃ.</p><p>                                    2. Mahāmoggallānasuttaṃ</p><p>(Discourse on the Enlightenment factors chanted for Venerable Mahāmoggallāna). </p><p>moggallānoca theroyaṃ - parittaṃ munisantikā,</p><p>sutvā tasmiṃ khaṇeyeva - ahosi nirupaddavo,</p><p>bojjhaṅaga balasaṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| rājagahe viharati veḷuvane kalandakanivāpe.| tena kho pana samayena āyasmā mahāmoggallāno| gijjhakūṭe pabbate viharati ābādhiko dukkhito bāḷhagilāno.| atha kho bhagavā sāyanhasamayaṃ paṭisallānā vuṭṭhito| yenāyasmā mahāmoggallāno tenupasaṅkami;| upasaṅkamitvā paññatte āsane nisīdi.| nisajja kho bhagavā āyasmantaṃ mahāmoggallānaṃ etadavoca.|</p><p>“Kacci te, moggallāna, khamanīyaṃ kacci yāpanīyaṃ?| kacci dukkhā vedanā paṭikkamanti, no abhikkamanti;| paṭikkamosānaṃ paññāyati, no abhikkamo”ti?|- “Na me, bhante, khamanīyaṃ, na yāpanīyaṃ.| bāḷhā me dukkhā vedanā abhikkamanti, no paṭikkamanti;| abhikkamosānaṃ paññāyati, no paṭikkamo”ti.|</p><p>“Sattime, moggallāna, bojjhaṅgā| mayā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattanti.| </p><p>katame satta?|</p><p>                                       1. satisambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       2. Dhammavicayasambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       3. Viriyasambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       4. Pītisambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       5. Passaddhisambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       6. Samādhisambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>                                       7. Upekkhāsambojjhaṅgo kho, moggallāna,| mayā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.</p><p>Ime kho, moggallāna, satta bojjhaṅgā| mayā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattantī”ti.| “Taggha, bhagavā, bojjhaṅgā; taggha, sugata, bojjhaṅgā”ti.</p>', '-38-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 39, '<p>Idamavoca bhagavā. Attamano āyasmā mahāmoggallāno| bhagavato bhāsitaṃ abhinandi.| vuṭṭhāhi cāyasmā mahāmoggallāno tamhā ābādhā.| tathāpahīno cāyasmato mahāmoggallānassa| so ābādho ahosīti.</p><p>(S.III.72)</p><p>moggallāna parittaṃ niṭṭhitaṃ.</p><p>                                       3.  Cundabojjhaṅgasuttaṃ </p><p>(Discourse on the Enlightenment factors chanted by the Venerable Cunda).</p><p>bhagavā lokanāthoyaṃ - cundattherassa santikā,</p><p>sutvā tasmiṃ khaṇeyeva - ahosi nirupaddavo,</p><p>bojjhaṅga bala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| rājagahe viharati veḷuvane kalandakanivāpe.| tena kho pana samayena| bhagavā ābādhiko hoti dukkhito bāḷhagilāno.| atha kho āyasmā mahācundo| yena bhagavā tenupasaṅkami;| upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi.| ekamantaṃ nisinnaṃ kho āyasmantaṃ mahācundaṃ bhagavā etadavoca| –</p><p>“Paṭibhantu taṃ, cunda, bojjhaṅgā”ti.</p><p>“Sattime, bhante, bojjhaṅgā| bhagavatā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattanti.|</p><p>katame satta?|</p><p>                                          1. satisambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.|</p><p>                                          2. dhammavicayasambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.|</p><p>                                          3. viriyasambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.| </p><p>                                          4. pītisambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.| </p><p>                                          5. passaddhisambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.| </p><p>                                          6. samādhisambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.| </p><p>                                          7. upekkhāsambojjhaṅgo kho, bhante,| bhagavatā sammadakkhāto bhāvito bahulīkato| abhiññāya sambodhāya nibbānāya saṃvattati.|</p><p>ime kho, bhante, satta bojjhaṅgā| bhagavatā sammadakkhātā bhāvitā bahulīkatā| abhiññāya sambodhāya nibbānāya saṃvattantī”ti.| “Taggha, cunda, bojjhaṅgā; taggha, cunda, bojjhaṅgā”ti.|</p><p>idamavocāyasmā mahā cundo.| samanuñño satthā ahosi.| vuṭṭhāhi ca bhagavā tamhā ābādhā.| tathā pahīno ca bhagavato so ābādho ahosīti.|</p><p>(S.III.73) </p><p>cunda parittaṃ niṭṭhitaṃ.</p><p>                                          4. Girimānandasuttaṃ</p><p>(Discourse on Venerable Girimānanda) </p><p>Theroyaṃ girimānando - ānandatthera santike,</p><p>sutvā tasmiṃ khaṇeyeva - ahosi nirupaddavo,</p><p>dasasaññūpa saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| tena kho pana samayena| āyasmā girimānando ābādhiko hoti dukkhito bāḷhagilāno.| atha kho āyasmā ānando yena bhagavā tenupasaṅkami;| upasaṅkamitvā bhagavantaṃ abhivādetvā ekamantaṃ nisīdi.| ekamantaṃ nisinno kho āyasmā ānando| bhagavantaṃ etadavoca –</p><p>“Āyasmā, bhante, girimānando| ābādhiko hoti dukkhito bāḷhagilāno.| sādhu, bhante, bhagavā yenāyasmā girimānando tenupasaṅkamatu anukampaṃ upādāyā”ti.| “Sace kho tvaṃ, ānanda, girimānandassa bhikkhuno| dasa saññā bhāseyyāsi,| ṭhānaṃ kho panetaṃ vijjati yaṃ girimānandassa bhikkhuno| dasa saññā sutvā so ābādho ṭhānaso paṭippassambheyya.|</p><p>katamā dasa?|</p><p>aniccasaññā, anattasaññā,| asubhasaññā, ādīnavasaññā,| pahānasaññā, virāgasaññā,| nirodhasaññā, sabbaloke anabhiratasaññā,| sabbasaṅkhāresu anicchāsaññā, ānāpānassati.|</p><p>                                             1. katamā cānanda, aniccasaññā?|</p><p>idhānanda, bhikkhu| araññagato vā rukkhamūlagato vā suññāgāragato vā| iti paṭisañcikkhati –| ‘Rūpaṃ aniccaṃ, vedanā aniccā,| saññā aniccā, saṅkhārā aniccā, viññāṇaṃ anicca’nti.| iti imesu pañcasu upādānakkhandhesu aniccānupassī viharati.| ayaṃ vuccatānanda, aniccasaññā.|</p><p>                                             2. katamā cānanda, anattasaññā?|</p><p>idhānanda, bhikkhu| araññagato vā rukkhamūlagato vā suññāgāragato vā| iti paṭisañcikkhati| – ‘Cakkhuṃ anattā, rūpaṃ anattā,| sotaṃ anattā, saddā anattā,| ghānaṃ anattā, gandhā anattā,| jivhā anattā, rasā anattā,| kāyā anattā, phoṭṭhabbā anattā,| mano anattā, dhammā anattā’ti.| iti imesu chasu ajjhattikabāhiresu āyatanesu| anattānupassī viharati.| ayaṃ vuccatānanda, anattasaññā.|</p>', '-39-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 40, '<p>                                             3. katamā cānanda, asubhasaññā?|</p><p>idhānanda, bhikkhu| imameva kāyaṃ, uddhaṃ pādatalā adho kesamatthakā tacapariyantaṃ| pūraṃ nānāppakārassa asucino paccavekkhati| – ‘Atthi imasmiṃ kāye| kesā lomā nakhā dantā taco| maṃsaṃ nahāru aṭṭhi aṭṭhimiñjā vakkaṃ| hadayaṃ yakanaṃ kilomakaṃ pihakaṃ papphāsaṃ| antaṃ antaguṇaṃ udariyaṃ karīsaṃ| pittaṃ semhaṃ pubbo lohitaṃ sedo medo| assu vasā kheḷo siṅghāṇikā lasikā mutta’nti.| iti imasmiṃ kāye asubhānupassī viharati.| ayaṃ vuccatānanda, asubhasaññā.|</p><p>                                             4. katamā cānanda, ādīnavasaññā?|</p><p>idhānanda, bhikkhu| araññagato vā rukkhamūlagato vā suññāgāragato| vā iti paṭisañcikkhati| – ‘Bahudukkho kho ayaṃ kāyo bahuādīnavo?| iti imasmiṃ kāye vividhā ābādhā uppajjanti, seyyathidaṃ |– cakkhurogo sotarogo| ghānarogo jivhārogo| kāyarogo sīsarogo| kaṇṇarogo mukharogo dantarogo| kāso sāso pināso ḍāho jaro kucchirogo| mucchā pakkhandikā sūlā visūcikā| kuṭṭhaṃ gaṇḍo kilāso soso apamāro| daddu kaṇḍu kacchu nakhasā vitacchikā| lohitaṃ pittaṃ madhumeho| aṃsā piḷakā bhagandalā| pittasamuṭṭhānā ābādhā| semhasamuṭṭhānā ābādhā| vātasamuṭṭhānā ābādhā| sannipātikā ābādhā| utupariṇāmajā ābādhā| visamaparihārajā ābādhā| opakkamikā ābādhā| kammavipākajā ābādhā| sītaṃ uṇhaṃ jighacchā pipāsā uccāro passāvo’ti.| iti imasmiṃ kāye ādīnavānupassī viharati.| ayaṃ vuccatānanda, ādīnavasaññā.|</p><p>                                             5. katamā cānanda, pahānasaññā?|</p><p>idhānanda, bhikkhu,| uppannaṃ kāmavitakkaṃ nādhivāseti, pajahati,| vinodeti, byantīkaroti, anabhāvaṃ gameti.| uppannaṃ byāpādavitakkaṃ nādhivāseti, pajahati,| vinodeti, byantīkaroti, anabhāvaṃ gameti.| uppannaṃ vihiṃsāvitakkaṃ nādhivāseti, pajahati,| vinodeti, byantīkaroti, anabhāvaṃ gameti.| uppannuppanne pāpake akusale dhamme nādhivāseti, pajahati,| vinodeti, byantīkaroti, anabhāvaṃ gameti.| ayaṃ vuccatānanda, pahānasaññā.|</p><p>                                             6. katamā cānanda, virāgasaññā?|</p><p>idhānanda, bhikkhu| araññagato vā rukkhamūlagato vā suññāgāragato vā| iti paṭisañcikkhati| – ‘Etaṃ santaṃ etaṃ paṇītaṃ yadidaṃ sabbasaṅkhārasamatho| sabbūpadhippaṭinissaggo taṇhākkhayo virāgo nibbāna’nti.| ayaṃ vuccatānanda, virāgasaññā.|</p><p>                                             7. katamā cānanda, nirodhasaññā?|</p><p>idhānanda, bhikkhu| araññagato vā rukkhamūlagato vā suññāgāragato vā| iti paṭisañcikkhati| – ‘Etaṃ santaṃ etaṃ paṇītaṃ yadidaṃ sabbasaṅkhārasamatho| sabbūpadhippaṭinissaggo taṇhākkhayo nirodho nibbāna’nti.| ayaṃ vuccatānanda, nirodhasaññā.|</p><p>                                             8. katamā cānanda, sabbaloke anabhiratasaññā?|</p><p>idhānanda, bhikkhu| ye loke upāyupādānā cetaso adhiṭṭhānābhinivesānusayā,| te pajahanto viharati anupādiyanto.| ayaṃ vuccatānanda, sabbaloke anabhiratasaññā.|</p><p>                                             9. katamā cānanda, sabbasaṅkhāresu aniccasaññā? | </p><p>idhānanda, bhikkhu| sabbasaṅkhāresu aṭṭīyati harāyati jigucchati.| ayaṃ vuccatānanda, sabbasaṅkhāresu aniccasaññā.|</p><p>                                             10. katamā cānanda, ānāpānassati? </p><p>Idhānanda, bhikkhu araññagato vā rukkhamūlagato vā suññāgāragato vā| nisīdati pallaṅkaṃ ābhujitvā| ujuṃ kāyaṃ paṇidhāya parimukhaṃ satiṃ upaṭṭhapetvā.| so satova assasati satova passasati.|</p><p>dīghaṃ vā assasanto ‘Dīghaṃ assasāmī’ti pajānāti.| dīghaṃ vā passasanto ‘Dīghaṃ passasāmī’ti pajānāti.|</p><p>rassaṃ vā assasanto ‘Rassaṃ assasāmī’ti pajānāti.| rassaṃ vā passasanto ‘Rassaṃ passasāmī’ti pajānāti.|</p><p>‘Sabbakāyapaṭisaṃvedī assasissāmī’ti sikkhati.| ‘Sabbakāyapaṭisaṃvedī passasissāmī’ti sikkhati.|</p><p>‘Passambhayaṃ kāyasaṅkhāraṃ assasissāmī’ti sikkhati.| ‘Passambhayaṃ kāyasaṅkhāraṃ passasissāmī’ti sikkhati.|</p><p>‘Pītipaṭisaṃvedī assasissāmī’ti sikkhati.| ‘Pītipaṭisaṃvedī passasissāmī’ti sikkhati.| ‘Sukhapaṭisaṃvedī assasissāmī’ti sikkhati.|</p><p>‘Sukhapaṭisaṃvedī passasissāmī’ti sikkhati.| ‘Cittasaṅkhārapaṭisaṃvedī assasissāmī’ti sikkhati.| ‘Cittasaṅkhārapaṭisaṃvedī passasissāmī’ti sikkhati.|</p><p>‘Passambhayaṃ cittasaṅkhāraṃ assasissāmī’ti sikkhati.| ‘Passambhayaṃ cittasaṅkhāraṃ passasissāmī’ti sikkhati.|</p><p>‘Cittapaṭisaṃvedī assasissāmī’ti sikkhati.| ‘Cittapaṭisaṃvedī passasissāmī’ti sikkhati.|</p><p>abhippamodayaṃ cittaṃ assasissāmī’ti sikkhati.| abhippamodayaṃ cittaṃ passasissāmī’ti sikkhati.|</p><p>samādahaṃ cittaṃ assasissāmī’ti sikkhati.| samādahaṃ cittaṃ passasissāmī’ti sikkhati.|</p><p>vimocayaṃ cittaṃ assasissāmī’ti sikkhati.| vimocayaṃ cittaṃ passasissāmī’ti sikkhati.|</p><p>aniccānupassī assasissāmī’ti sikkhati.| aniccānupassī passasissāmī’ti sikkhati.|</p><p>virāgānupassī assasissāmī’ti sikkhati.| virāgānupassī passasissāmī’ti sikkhati.|</p><p>nirodhānupassī assasissāmī’ti sikkhati.| nirodhānupassī passasissāmī’ti sikkhati.|</p><p>‘Paṭinissaggānupassī assasissāmī’ti sikkhati.| ‘Paṭinissaggānupassī passasissāmī’ti sikkhati.|</p><p>ayaṃ vuccatānanda, ānāpānassati.|</p><p>“Sace kho tvaṃ, ānanda, girimānandassa bhikkhuno| imā dasa saññā bhāseyyāsi,| ṭhānaṃ kho panetaṃ vijjati yaṃ girimānandassa bhikkhuno| imā dasa saññā sutvā so ābādho ṭhānaso paṭippassambheyyā”ti.|</p><p>atha kho āyasmā ānando| bhagavato santike imā dasa saññā uggahetvā| yenāyasmā girimānando tenupasaṅkami;| upasaṅkamitvā āyasmato girimānandassa imā dasa saññā abhāsi.| atha kho āyasmato girimānandassa dasa saññā sutvā so ābādho ṭhānaso paṭippassambhi.| vuṭṭhāhi cāyasmā girimānando tamhā ābādhā.| tathā pahīno ca panāyasmato girimānandassa so ābādho ahosī”ti.</p><p>(A.III.342)</p><p>girimānanda suttaṃ niṭṭhitaṃ </p>', '-40-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 41, '<p>                                             1. Isigilisuttaṃ </p><p>(Discourse on Sages’ Mountain)</p><p>pacceka buddha nāmāni - desento yaṃ adesayi,</p><p>ananta bala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| rājagahe viharati isigilismiṃ pabbate.| tatra kho bhagavā bhikkhū āmantesi – “Bhikkhavo”ti.| “Bhadante”ti te bhikkhū bhagavato paccassosuṃ. Bhagavā etadavoca| –</p><p>“Passatha no tumhe, bhikkhave, etaṃ vebhāraṃ pabbata”nti?| “Evaṃ, bhante”.| “Etassapi kho, bhikkhave, vebhārassa pabbatassa| aññāva samaññā ahosi aññā paññatti”.</p><p>“Passatha no tumhe, bhikkhave, etaṃ paṇḍavaṃ pabbata”nti?| “Evaṃ, bhante”.| “Etassapi kho, bhikkhave, paṇḍavassa pabbatassa| aññāva samaññā ahosi aññā paññatti”.</p><p>“Passatha no tumhe, bhikkhave, etaṃ vepullaṃ pabbata”nti?| “Evaṃ, bhante”.| “Etassapi kho, bhikkhave, vepullassa pabbatassa| aññāva samaññā ahosi aññā paññatti”.</p><p>“Passatha no tumhe, bhikkhave, etaṃ gijjhakūṭaṃ pabbata”nti?| “Evaṃ, bhante”.| “Etassapi kho, bhikkhave, gijjhakūṭassa pabbatassa| aññāva samaññā ahosi aññā paññatti”.</p><p>“Passatha no tumhe, bhikkhave, imaṃ isigiliṃ pabbata”nti?| “Evaṃ, bhante”.| “Imassa kho pana, bhikkhave, isigilissa pabbatassa| esāva samaññā ahosi esā paññatti”.</p><p>“Bhūtapubbaṃ, bhikkhave, pañca paccekabuddhasatāni| imasmiṃ isigilismiṃ pabbate ciranivāsino ahesuṃ.| te imaṃ pabbataṃ pavisantā dissanti, paviṭṭhā na dissanti.| tamenaṃ manussā disvā evamāhaṃsu |– ‘Ayaṃ pabbato| ime isī gilatī’ti; ‘Isigili isigili’ tveva samaññā udapādi.</p><p>Ācikkhissāmi, bhikkhave, paccekabuddhānaṃ nāmāni;| kittayissāmi, bhikkhave, paccekabuddhānaṃ nāmāni;| desessāmi, bhikkhave, paccekabuddhānaṃ nāmāni.| taṃ suṇātha, sādhukaṃ manasi karotha; bhāsissāmī”ti.| “Evaṃ, bhante”ti kho te bhikkhū bhagavato paccassosuṃ. Bhagavā etadavoca –</p><p>“Ariṭṭho nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>upariṭṭho nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>tagarasikhī nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>yasassī nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>sudassano nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>piyadassī nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>gandhāro nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>piṇḍolo nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>upāsabho nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>nīto nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>tatho nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi,</p><p>sutavā nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi;</p><p>bhāvitatto nāma, bhikkhave, paccekasambuddho| imasmiṃ isigilismiṃ pabbate ciranivāsī ahosi.</p><p>“Ye sattasārā anīghā nirāsā,</p><p>paccekamevajjhagamaṃsu bodhiṃ;</p><p>tesaṃ visallāna naruttamānaṃ,</p><p>nāmāni me kittayato suṇātha.</p><p>“Ariṭṭho upariṭṭho tagarasikhī yasassī,</p><p>sudassano piyadassī ca buddho;</p><p>gandhāro piṇḍolo upāsabho ca,</p><p>nīto tatho sutavā bhāvitatto.</p><p>“Sumbho subho methulo aṭṭhamo ca,</p><p>athassumegho anīgho sudāṭho;</p><p>paccekabuddhā bhavanettikhīṇā,</p>', '-41-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 42, '<p>hiṅgū ca hiṅgo ca mahānubhāvā.</p><p>“Dve jālino munino aṭṭhako ca,</p><p>atha kosalo buddho atho subāhu;</p><p>upanemiso nemiso santacitto,</p><p>sacco tatho virajo paṇḍito ca.</p><p>“Kālūpakāḷā vijito jito ca,</p><p>aṅgo ca paṅgo ca gutijjito ca;</p><p>passī jahī upadhiṃ dukkhamūlaṃ,</p><p>aparājito mārabalaṃ ajesi.</p><p>“Satthā pavattā sarabhaṅgo lomahaṃso,</p><p>uccaṅgamāyo asito anāsavo;</p><p>manomayo mānacchido ca bandhumā,</p><p>tadādhimutto vimalo ca ketumā.</p><p>“Ketumbharāgo ca mātaṅgo ariyo,</p><p>athaccuto accutagāmabyāmako;</p><p>sumaṅgalo dabbilo supatiṭṭhito,</p><p>asayho khemābhirato ca sorato.</p><p>“Durannayo saṅgho athopi uccayo,</p><p>aparo muni sayho anomanikkamo;</p><p>ānando nando upanando dvādasa,</p><p>bhāradvājo antimadehadhārī.</p><p>“Bodhi mahānāmo athopi uttaro,</p><p>kesī sikhī sundaro bhāradvājo;</p><p>tissūpatissā bhavabandhanacchidā,</p><p>upasīdarī taṇhacchido ca sīdarī.</p><p>“Buddho ahu maṅgalo vītarāgo,</p><p>usabhacchidā jāliniṃ dukkhamūlaṃ;</p><p>santaṃ padaṃ ajjhagamūpanīto,</p><p>uposatho sundaro saccanāmo.</p><p>“Jeto jayanto padumo uppalo ca,</p><p>padumuttaro rakkhito pabbato ca;</p><p>mānatthaddho sobhito vītarāgo,</p><p>kaṇho ca buddho suvimuttacitto.</p><p>“Ete ca aññe ca mahānubhāvā,</p><p>paccekabuddhā bhavanettikhīṇā;</p><p>te sabbasaṅgātigate mahesī,</p>', '-42-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '1. Dhammacakkappavattanasuttaṃ  ', 'title', 43);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 43, '<p>parinibbute vandatha appameyye”ti.</p><p>Isigilisuttaṃ niṭṭhitaṃ.</p><p>(M.III.112)</p><p>dutiyabhāṇavāro niṭṭhito</p><p> </p><p>Extra Sutta Desanā</p><p>                                                </p><p><h1>1. Dhammacakkappavattanasuttaṃ</h1>  </p><p>(Discourse on Rolling Forth the wheel of Dhamma)</p><p>evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| bārāṇasiyaṃ viharati isipatane migadāye.| tatra kho bhagavā pañcavaggiye bhikkhū āmantesi – |</p><p>“Dveme, bhikkhave, antā pabbajitena na sevitabbā.|</p><p>katame dve? Yo cāyaṃ kāmesu kāmasukhallikānuyogo| hīno gammo pothujjaniko anariyo anatthasaṃhito,|</p><p>yo cāyaṃ attakilamathānuyogo dukkho anariyo anatthasaṃhito.|</p><p>ete te, bhikkhave, ubho ante anupagamma majjhimā paṭipadā| tathāgatena abhisambuddhā| cakkhukaraṇī ñāṇakaraṇī| upasamāya abhiññāya sambodhāya| nibbānāya saṃvattati”.|</p><p>“Katamā ca sā, bhikkhave, majjhimā paṭipadā| tathāgatena abhisambuddhā| cakkhukaraṇī ñāṇakaraṇī| upasamāya abhiññāya sambodhāya| nibbānāya saṃvattati?|</p><p>ayameva ariyo aṭṭhaṅgiko maggo,| seyyathidaṃ – |</p><p>sammādiṭṭhi sammāsaṅkappo| sammāvācā sammākammanto| sammāājīvo sammāvāyāmo| sammāsati sammāsamādhi.|</p><p>ayaṃ kho sā, bhikkhave, majjhimā paṭipadā| tathāgatena abhisambuddhā| cakkhukaraṇī ñāṇakaraṇī| upasamāya abhiññāya sambodhāya| nibbānāya saṃvattati.|</p><p>“Idaṃ kho pana, bhikkhave, dukkhaṃ ariyasaccaṃ –| jātipi dukkhā, jarāpi dukkhā,| byādhipi dukkho, maraṇampi dukkhaṃ,| appiyehi sampayogo dukkho,| piyehi vippayogo dukkho,| yampicchaṃ na labhati tampi dukkhaṃ |– saṅkhittena pañcupādānakkhandhā dukkhā.| idaṃ kho pana, bhikkhave, dukkhasamudayaṃ ariyasaccaṃ |– yāyaṃ taṇhā ponobhavikā nandirāgasahagatā tatratatrābhinandinī,| seyyathidaṃ – kāmataṇhā, bhavataṇhā, vibhavataṇhā.|</p><p>idaṃ kho pana, bhikkhave, dukkhanirodhaṃ ariyasaccaṃ |– yo tassāyeva taṇhāya asesavirāganirodho| cāgo paṭinissaggo mutti anālayo.| idaṃ kho pana, bhikkhave, dukkhanirodhagāminī paṭipadā ariyasaccaṃ| – ayameva ariyo aṭṭhaṅgiko maggo, seyyathidaṃ| – sammādiṭṭhi sammāsaṅkappo sammāvācā sammākammanto| sammāājīvo sammāvāyāmo| sammāsati sammāsamādhi.|</p><p>“‘idaṃ dukkhaṃ ariyasacca’nti me, bhikkhave, pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhaṃ ariyasaccaṃ| pariññeyya’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhaṃ ariyasaccaṃ pariññāta’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.|</p><p>“‘idaṃ dukkhasamudayaṃ ariyasacca’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhasamudayaṃ ariyasaccaṃ pahātabba’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhasamudayaṃ ariyasaccaṃ pahīna’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.|</p><p>“‘idaṃ dukkhanirodhaṃ ariyasacca’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhanirodhaṃ ariyasaccaṃ sacchikātabba’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhanirodhaṃ ariyasaccaṃ sacchikata’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.|</p><p>“‘idaṃ dukkhanirodhagāminī paṭipadā ariyasacca’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| taṃ kho panidaṃ dukkhanirodhagāminī paṭipadā ariyasaccaṃ bhāvetabba’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.| ‘Taṃ kho panidaṃ dukkhanirodhagāminī paṭipadā ariyasaccaṃ bhāvita’nti me, bhikkhave,| pubbe ananussutesu dhammesu| cakkhuṃ udapādi, ñāṇaṃ udapādi,| paññā udapādi, vijjā udapādi,| āloko udapādi.|</p><p>“Yāvakīvañca me, bhikkhave, imesu catūsu ariyasaccesu| evaṃ tiparivaṭṭaṃ dvādasākāraṃ yathābhūtaṃ ñāṇadassanaṃ na suvisuddhaṃ ahosi,| neva tāvāhaṃ, bhikkhave, sadevake loke samārake sabrahmake| sassamaṇabrāhmaṇiyā pajāya sadevamanussāya| ‘Anuttaraṃ sammāsambodhiṃ abhisambuddho’ti paccaññāsiṃ.|</p><p>“Yato ca kho me, bhikkhave,| imesu catūsu ariyasaccesu| evaṃ tiparivaṭṭaṃ dvādasākāraṃ yathābhūtaṃ ñāṇadassanaṃ suvisuddhaṃ ahosi,| athāhaṃ, bhikkhave, sadevake loke samārake sabrahmake| sassamaṇabrāhmaṇiyā pajāya sadevamanussāya| ‘Anuttaraṃ sammāsambodhiṃ abhisambuddho’ti paccaññāsiṃ.|</p><p>ñāṇañca pana me dassanaṃ udapādi |– ‘Akuppā me ceto vimutti, ayamantimā jāti, natthidāni punabbhavo’”ti.|</p><p>idamavoca bhagavā. Attamanā pañcavaggiyā bhikkhū| bhagavato bhāsitaṃ abhinandunti.|</p><p>imasmiñca pana veyyākaraṇasmiṃ bhaññamāne| āyasmato koṇḍaññassa| virajaṃ vītamalaṃ dhammacakkhuṃ udapādi| – “Yaṃ kiñci samudayadhammaṃ, sabbaṃ taṃ nirodhadhamma”nti.|</p><p>pavattite ca pana bhagavatā dhammacakke| bhummā devā saddamanussāvesuṃ| – “Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā| devena vā mārena vā| brahmunā vā kenaci vā lokasmi”nti.|</p><p>bhummānaṃ devānaṃ saddaṃ sutvā| cātumahārājikā devā saddamanussāvesuṃ| – “Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā| devena vā mārena vā| brahmunā vā kenaci vā lokasmi”nti.|</p><p>cātumahārājikānaṃ devānaṃ saddaṃ sutvā tāvatiṃsā devā…pe… yāmā devā…pe… tusitā devā…pe… nimmānaratī devā…pe… paranimmitavasavattī devā…“Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā| devena vā mārena vā| brahmunā vā kenaci vā lokasmi”nti.|</p><p>paranimmitavasavattīnaṃ devānaṃ saddaṃ sutvā| brahmapārisajjā devā...pe... Brahmapurohitā devā...pe... Mahābrahmādevā...pe... Saddamanussāvesuṃ – “Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmi”nti.|</p><p>mahābrahmānaṃ devānaṃ saddaṃ sutvā| parittasubhā devā...pe... Appamāṇasubhā devā...pe... Subhakiṇṇakā devā...pe...saddamanussāvesuṃ – “Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmi”nti.|</p><p>subhakiṇṇakānaṃ devānaṃ saddaṃ sutvā parittābhā devā...pe... Appamāṇābhā devā...pe... Ābhassarā devā...pe... Vehapphalā devā...pe... Avihā devā...pe... Atappā devā...pe... Sudassā devā...pe... Sudassī devā...pe... Akaniṭṭhakā devā saddamanussāvesuṃ| – “Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appativattiyaṃ| samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmi”nti.|</p><p>itiha tena khaṇena tena muhuttena| yāva brahmalokā saddo abbhuggacchi.| ayañca dasasahassilokadhātu saṅkampi sampakampi sampavedhi,| appamāṇo ca uḷāro obhāso loke pāturahosi| atikkamma devānaṃ devānubhāvanti.|</p>', '-43-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 44, '<p>atha kho bhagavā imaṃ udānaṃ udānesi| – “Aññāsi vata, bho, koṇḍañño, aññāsi vata, bho, koṇḍañño”ti!| iti hidaṃ āyasmato koṇḍaññassa ‘Aññāsikoṇḍañño’ tveva nāmaṃ ahosīti.</p><p>(S.III.369)</p><p>dhammacakkappavattana suttaṃ niṭṭhitaṃ.</p><p>                                                   2. Mahāsamayasuttaṃ </p><p>(Discourse on the Great Congregation) </p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| sakkesu viharati kapilavatthusmiṃ mahāvane| mahatā bhikkhusaṅghena saddhiṃ| pañcamattehi bhikkhusatehi| sabbeheva arahantehi;| dasahi ca lokadhātūhi devatā| yebhuyyena sannipatitā honti bhagavantaṃ dassanāya bhikkhusaṅghañca. |</p><p>atha kho catunnaṃ suddhāvāsakāyikānaṃ devatānaṃ etadahosi |– “Ayaṃ kho bhagavā sakkesu viharati kapilavatthusmiṃ mahāvane| mahatā bhikkhusaṅghena saddhiṃ pañcamattehi bhikkhusatehi| sabbeheva arahantehi;| dasahi ca lokadhātūhi devatā| yebhuyyena sannipatitā honti| bhagavantaṃ dassanāya bhikkhusaṅghañca.| yaṃnūna mayampi yena bhagavā tenupasaṅkameyyāma;| upasaṅkamitvā bhagavato santike paccekagāthaṃ bhāseyyāmā”ti.|</p><p>atha kho tā devatā seyyathāpi nāma balavā puriso| samiñjitaṃ vā bāhaṃ pasāreyya| pasāritaṃ vā bāhaṃ samiñjeyya| evameva suddhāvāsesu devesu antarahitā bhagavato purato pāturahaṃsu.| atha kho tā devatā bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhaṃsu.| ekamantaṃ ṭhitā kho ekā devatā| bhagavato santike imaṃ gāthaṃ abhāsi |–</p><p>“Mahāsamayo pavanasmiṃ, devakāyā samāgatā;|</p><p>āgatamha imaṃ dhammasamayaṃ, dakkhitāye aparājitasaṅgha”nti.</p><p>Atha kho aparā devatā bhagavato santike imaṃ gāthaṃ abhāsi.| –</p><p>“Tatra bhikkhavo samādahaṃsu, cittamattano ujukaṃ akaṃsu;|</p><p>sārathīva nettāni gahetvā, indriyāni rakkhanti paṇḍitā”ti.</p><p>Atha kho aparā devatā bhagavato santike imaṃ gāthaṃ abhāsi| –</p><p>“Chetvā khīlaṃ chetvā palighaṃ, indakhīlaṃ ūhacca manejā;|</p><p>te caranti suddhā vimalā, cakkhumatā sudantā susunāgā”ti.</p><p>Atha kho aparā devatā bhagavato santike imaṃ gāthaṃ abhāsi |–</p><p>“Yekeci buddhaṃ saraṇaṃ gatāse, na te gamissanti apāyabhūmiṃ;|</p><p>pahāya mānusaṃ dehaṃ, devakāyaṃ paripūressantī”ti.</p><p>Atha kho bhagavā bhikkhū āmantesi |– “Yebhuyyena, bhikkhave, dasasu lokadhātūsu devatā sannipatitā honti, tathāgataṃ dassanāya bhikkhusaṅghañca.| yepi te, bhikkhave, ahesuṃ atītamaddhānaṃ arahanto sammāsambuddhā,| tesampi bhagavantānaṃ etaṃparamāyeva devatā sannipatitā ahesuṃ| seyyathāpi mayhaṃ etarahi.| yepi te, bhikkhave, bhavissanti anāgatamaddhānaṃ arahanto sammāsambuddhā,| tesampi bhagavantānaṃ etaṃparamāyeva devatā sannipatitā bhavissanti| seyyathāpi mayhaṃ etarahi.|</p><p>ācikkhissāmi, bhikkhave, devakāyānaṃ nāmāni;| kittayissāmi, bhikkhave, devakāyānaṃ nāmāni;| desessāmi, bhikkhave, devakāyānaṃ nāmāni.| taṃ suṇātha, sādhukaṃ manasikarotha, bhāsissāmī”ti.|</p><p>“Evaṃ, bhante”ti kho te bhikkhū bhagavato paccassosuṃ.</p><p>Bhagavā etadavoca –|</p><p>“Silokamanukassāmi, yattha bhummā tadassitā;</p><p>ye sitā girigabbhāraṃ, pahitattā samāhitā.</p><p>“Puthūsīhāva sallīnā, lomahaṃsābhisambhuno;</p><p>odātamanasā suddhā, vippasannamanāvilā”.</p><p>Bhiyyo pañcasate ñatvā, vane kāpilavatthave;</p><p>tato āmantayī satthā, sāvake sāsane rate.</p><p>“Devakāyā abhikkantā, te vijānātha bhikkhavo”;</p><p>te ca ātappamakaruṃ, sutvā buddhassa sāsanaṃ.</p><p>Tesaṃ pāturahu ñāṇaṃ, amanussānadassanaṃ;</p><p>appeke satamaddakkhuṃ, sahassaṃ atha sattatiṃ.</p><p>Sataṃ eke sahassānaṃ, amanussānamaddasuṃ;</p><p>appekenantamaddakkhuṃ, disā sabbā phuṭā ahu.</p><p>Tañca sabbaṃ abhiññāya, vavatthitvāna cakkhumā;</p>', '-44-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 45, '<p>tato āmantayī satthā, sāvake sāsane rate.</p><p>“Devakāyā abhikkantā, te vijānātha bhikkhavo;</p><p>ye vohaṃ kittayissāmi, girāhi anupubbaso.</p><p>“Sattasahassā te yakkhā, bhummā kāpilavatthavā.</p><p>Iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Chasahassā hemavatā, yakkhā nānattavaṇṇino;</p><p>iddhimanto jutīmanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Sātāgirā tisahassā, yakkhā nānattavaṇṇino;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Iccete soḷasasahassā, yakkhā nānattavaṇṇino;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Vessāmittā pañcasatā, yakkhā nānattavaṇṇino;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Kumbhīro rājagahiko, vepullassa nivesanaṃ;</p><p>bhiyyo naṃ satasahassaṃ, yakkhānaṃ payirupāsati;</p><p>kumbhīro rājagahiko, sopāga samitiṃ vanaṃ.</p><p>“Purimañca disaṃ rājā, dhataraṭṭho taṃ pasāsati.</p><p>Gandhabbānaṃ ādhipati, mahārājā yasassiso.</p><p>“Puttāpi tassa bahavo, indanāmā mahabbalā;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Dakkhiṇañca disaṃ rājā, virūḷho taṃ pasāsati;</p><p>kumbhaṇḍānaṃ ādhipati, mahārājā yasassiso.</p><p>“Puttāpi tassa bahavo, indanāmā mahabbalā;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Pacchimañca disaṃ rājā, virūpakkho taṃ pasāsati;</p><p>nāgānañca ādhipati, mahārājā yasassiso.</p><p>“Puttāpi tassa bahavo, indanāmā mahabbalā;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p>', '-45-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 46, '<p>“Uttarañca disaṃ rājā, kuvero taṃ pasāsati;</p><p>yakkhānañca adhipati, mahārājā yasassiso.</p><p>“Puttāpi tassa bahavo, indanāmā mahabbalā;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Purimaṃ disaṃ dhataraṭṭho, dakkhiṇena virūḷhako;</p><p>pacchimena virūpakkho, kuvero uttaraṃ disaṃ.</p><p>“Cattāro te mahārājā, samantā caturo disā;</p><p>daddallamānā aṭṭhaṃsu, vane kāpilavatthave.</p><p>“Tesaṃ māyāvino dāsā, āguṃ vañcanikā saṭhā.</p><p>Māyā kuṭeṇḍu viṭeṇḍu, viṭucca viṭuṭo saha.</p><p>“Candano kāmaseṭṭho ca, kinnighaṇḍu nighaṇḍu ca;</p><p>panādo opamañño ca, devasūto ca mātali.</p><p>“Cittaseno ca gandhabbo, naḷorājā janesabho;</p><p>āguṃ pañcasikho ceva, timbarū sūriyavaccasā.</p><p>“Ete caññe ca rājāno, gandhabbā saha rājubhi;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Athāguṃ nābhasā nāgā, vesālā sahatacchakā.</p><p>Kambalassatarā āguṃ, pāyāgā saha ñātibhi.</p><p>“Yāmunā dhataraṭṭhā ca, āgū nāgā yasassino;</p><p>erāvaṇo mahānāgo, sopāga samitiṃ vanaṃ.</p><p>“Ye nāgarāje sahasā haranti, dibbā dijā pakkhi visuddhacakkhū;</p><p>vehāsayā te vanamajjhapattā, citrā supaṇṇā iti tesa nāmāni.</p><p>“Abhayaṃ tadā nāgarājānamāsi, supaṇṇato khemamakāsi buddho; saṇhāhi vācāhi upavhayantā, nāgā supaṇṇā saraṇamakaṃsu buddhaṃ.</p><p>“Jitā vajirahatthena, samuddaṃ asurāsitā.</p><p>Bhātaro vāsavassete, iddhimanto yasassino.</p><p>“Kālakañchā mahābhismā, asurā dānaveghasā;</p><p>vepacitti sucittī ca, pahārādo namucī saha.</p><p>“Satañca baliputtānaṃ, sabbe verocanāmakā;</p><p>sannayhitvā balisenaṃ, rāhubhaddamupāgamuṃ;</p><p>samayodāni bhaddante, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Āpo ca devā paṭhavī ca, tejo vāyo tadāgamuṃ.</p><p>Varuṇā vāruṇā devā, somo ca yasasā saha.</p><p>“Mettā karuṇā kāyikā, āguṃ devā yasassino;</p><p>dasete dasadhā kāyā, sabbe nānattavaṇṇino.</p><p>“Iddhimanto jutimanto, vaṇṇavanto yasassino;</p>', '-46-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 47, '<p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Veṇḍahū ca devā sahali ca, asamā ca duve yamā;</p><p>candassūpanisā devā, candamāguṃ purakkhatvā.</p><p>“Sūriyassūpanisā devā, sūriyamāguṃ purakkhitvā;</p><p>nakkhattāni purakkhatvā, āguṃ mandavalāhakā.</p><p>“Vasūnaṃ vāsavo seṭṭho, sakkopāga purindado;</p><p>dasete dasadhā kāyā, sabbe nānattavaṇṇino.</p><p>“Iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Athāguṃ sahabhū devā, jalamaggisikhāriva;</p><p>ariṭṭhakā ca rojā ca, ummāpupphanibhāsino.</p><p>“Varuṇā sahadhammā ca, accutā ca anejakā;</p><p>sūleyyarucirā āguṃ, āguṃ vāsavanesino;</p><p>dasete dasadhā kāyā, sabbe nānattavaṇṇino.</p><p>“Iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Samānā mahāsamanā, mānusā mānusuttamā;</p><p>khiḍḍāpadūsikā āguṃ, āguṃ manopadūsikā.</p><p>“Athāguṃ harayo devā, ye ca lohitavāsino;</p><p>pāragā mahāpāragā, āguṃ devā yasassino;</p><p>dasete dasadhā kāyā, sabbe nānattavaṇṇino.</p><p>“Iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Sukkā karambhā aruṇā, āguṃ veghanasā saha;</p><p>odātagayhā pāmokkhā, āguṃ devā vicakkhaṇā.</p><p>“Sadāmattā hāragajā, missakā ca yasassino;</p><p>thanayaṃ āga pajjunno, yo disā abhivassati.</p><p>“Dasete dasadhā kāyā, sabbe nānattavaṇṇino;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p><p>“Khemiyā tusitā yāmā, kaṭṭhakā ca yasassino;</p><p>lambītakā lāmaseṭṭhā, jotināmā ca āsavā;</p><p>nimmānaratino āguṃ, athāguṃ paranimmitā.</p><p>“Dasete dasadhā kāyā, sabbe nānattavaṇṇino;</p><p>iddhimanto jutimanto, vaṇṇavanto yasassino;</p><p>modamānā abhikkāmuṃ, bhikkhūnaṃ samitiṃ vanaṃ.</p>', '-47-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 48, '<p>“Saṭṭhete devanikāyā, sabbe nānattavaṇṇino;</p><p>nāmanvayena āgañchuṃ, ye caññe sadisā saha.</p><p>“Pavutthajātimakhilaṃ, oghatiṇṇamanāsavaṃ;</p><p>dakkhemoghataraṃ nāgaṃ, candaṃva asitātigaṃ’.</p><p>“Subrahmā paramatto ca, puttā iddhimato saha.</p><p>Sanaṅkumāro tisso ca, sopāga samitiṃ vanaṃ.</p><p>“Sahassaṃ brahmalokānaṃ, mahābrahmābhitiṭṭhati;</p><p>upapanno jutimanto, bhismākāyo yasassiso.</p><p>“Dasettha issarā āguṃ, paccekavasavattino;</p><p>tesañca majjhato āga, hārito parivārito.</p><p>“Te ca sabbe abhikkante, sainde deve sabrahmake.</p><p>Mārasenā abhikkāmuṃ, passa kaṇhassa mandiyaṃ.</p><p>“‘etha gaṇhatha bandhatha, rāgena bandhamatthu vo;</p><p>samantā parivāretha, mā vo muñcittha koci naṃ’.</p><p>“Iti tattha mahāseno, kaṇha senaṃ apesayi;</p><p>pāṇinā talamāhacca, saraṃ katvāna bheravaṃ.</p><p>“Yathā pāvussako megho, thanayanto savijjuko;</p><p>tadā so paccudāvatti, saṅkuddho asayaṃvasī.</p><p>Tañca sabbaṃ abhiññāya, vavatthitvāna cakkhumā.</p><p>Tato āmantayī satthā, sāvake sāsane rate.</p><p>“Mārasenā abhikkantā, te vijānātha bhikkhavo;</p><p>te ca ātappamakaruṃ, sutvā buddhassa sāsanaṃ;</p><p>vītarāgeha pakkāmuṃ, nesaṃ lomampi iñjayuṃ.</p><p>“‘sabbe vijitasaṅgāmā, bhayātītā yasassino;</p><p>modanti saha bhūtehi, sāvakā te janesutā”ti.</p><p>(D.II.203)</p><p>mahāsamayasuttaṃ niṭṭhitaṃ.</p><p>                                                      3. āḷavakasuttaṃ</p><p>(Discourse on spirit Āḷavaka)  </p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| āḷaviyaṃ viharati āḷavakassa yakkhassa bhavane.| atha kho āḷavako yakkho yena bhagavā tenupasaṅkami; upasaṅkamitvā| bhagavantaṃ etadavoca |–</p><p>“Nikkhama, samaṇā”ti. “Sādhāvuso”ti bhagavā nikkhami.| “Pavisa, samaṇā”ti. “Sādhāvuso”ti bhagavā pāvisi.| dutiyampi kho āḷavako yakkho bhagavantaṃ etadavoca| – “Nikkhama, samaṇā”ti. “Sādhāvuso”ti bhagavā nikkhami.| “Pavisa, samaṇā”ti. “Sādhāvuso”ti bhagavā pāvisi.| tatiyampi kho āḷavako yakkho bhagavantaṃ etadavoca| – “Nikkhama, samaṇā”ti. “Sādhāvuso”ti bhagavā nikkhami.| “Pavisa, samaṇā”ti. “Sādhāvuso”ti bhagavā pāvisi.| catutthampi kho āḷavako yakkho bhagavantaṃ etadavoca| – “Nikkhama, samaṇā”ti.| “Na khvāhaṃ, āvuso, nikkhamissāmi. Yaṃ te karaṇīyaṃ taṃ karohī”ti.| “Pañhaṃ taṃ, samaṇa, pucchissāmi.| sace me na byākarissasi,| cittaṃ vā te khipissāmi, hadayaṃ vā te phālessāmi,| pādesu vā gahetvā pāragaṅgāya khipissāmī”ti.| “Na khvāhaṃ taṃ, āvuso, passāmi| sadevake loke samārake sabrahmake| sassamaṇabrāhmaṇiyā pajāya sadevamanussāya,| yo me cittaṃ vā khipeyya hadayaṃ vā phāleyya,| pādesu vā gahetvā pāragaṅgāya khipeyya.| api ca tvaṃ, āvuso, puccha yadā kaṅkhasī”ti.|</p><p>atha kho āḷavako yakkho bhagavantaṃ gāthāya ajjhabhāsi.</p><p>“Kiṃsūdha vittaṃ purisassa seṭṭhaṃ, kiṃsu suciṇṇaṃ sukhamāvahāti;</p><p>kiṃsu have sādutaraṃ rasānaṃ, kathaṃjīviṃ jīvitamāhu seṭṭha”nti.</p><p>“Saddhīdha vittaṃ purissa seṭṭhaṃ, dhammo suciṇṇo sukhamāvahāti;</p><p>saccaṃ have sādutaraṃ rasānaṃ, paññājīviṃ jīvitamāhu seṭṭha”nti.</p>', '-48-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 49, '<p>“Kathaṃsu tarati oghaṃ, kathaṃsu tarati aṇṇavaṃ;</p><p>kathaṃsu dukkhaṃ acceti, kathaṃsu parisujjhatī”.</p><p>“Saddhāya tarati oghaṃ, appamādena aṇṇavaṃ;</p><p>vīriyena dukkhaṃ acceti, paññāya parisujjhatī”.</p><p>“Kathaṃsu labhate paññaṃ, kathaṃsu vindate dhanaṃ;</p><p>kathaṃsu kittiṃ pappoti, kathaṃ mittāni ganthati;</p><p>asmā lokā paraṃ lokaṃ, kathaṃ pecca na socatī”.</p><p>“Saddahāno arahataṃ, dhammaṃ nibbānapattiyā;</p><p>sussūsā labhate paññaṃ, appamatto vicakkhaṇo.</p><p>“Patirūpakārī dhuravā, uṭṭhātā vindate dhanaṃ;</p><p>saccena kittiṃ pappoti, dadaṃ mittāni ganthati;</p><p>asmā lokā paraṃ lokaṃ, evaṃ pecca na socati.</p><p>“Yassete caturo dhammā, saddhassa gharamesino;</p><p>saccaṃ dhammo dhiti cāgo, sa ve pecca na socati.</p><p>“Iṅgha aññepi pucchassu, puthū samaṇabrāhmaṇe;</p><p>yadi saccā damā cāgā, khantyā bhiyyo na vijjatī”.</p><p>“Kathaṃ nu dāni puccheyyaṃ, puthū samaṇabrāhmaṇe;</p><p>so’haṃ ajja pajānāmi, yo attho samparāyiko.</p><p>“Atthāya vata me buddho, vāsāyāḷavimāgamā;</p><p>so’haṃ ajja pajānāmi, yattha dinnaṃ mahapphalaṃ.</p><p>“So ahaṃ vicarissāmi, gāmā gāmaṃ purā puraṃ;</p><p>namassamāno sambuddhaṃ, dhammassa ca sudhammata”nti.</p><p>Evaṃ vutte āḷavako yakkho bhagavantaṃ etadavoca| – ‘‘abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama!| seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya,| paṭicchannaṃ vā vivareyya,| mūḷhassa vā maggaṃ ācikkheyya,| andhakāre vā telapajjotaṃ dhāreyya,| cakkhumanto rūpāni dakkhintīti;| evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito.| esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi| dhammañca bhikkhusaṅghañca,| upāsakaṃ maṃ bhavaṃ gotamo dhāretu| ajjatagge pāṇupetaṃ saraṇaṃ gatanti.</p><p>(S.I.216)</p><p>āḷavakasuttaṃ niṭṭhitaṃ.</p><p></p><p></p><p>                                                         4. kasibhāradvājasuttaṃ</p><p>(Discourse on the Farmer Bhāradvāja) </p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| magadhesu viharati dakkhiṇāgirismiṃ ekanāḷāyaṃ brāhmaṇagāme.| tena kho pana samayena kasibhāradvājassa brāhmaṇassa| pañcamattāni naṅgalasatāni payuttāni honti vappakāle.| atha kho bhagavā pubbaṇhasamayaṃ nivāsetvā| pattacīvaramādāya| yena kasibhāradvājassa brāhmaṇassa kammanto tenupasaṅkami.|</p><p>tena kho pana samayena kasibhāradvājassa brāhmaṇassa| parivesanā vattati.| atha kho bhagavā yena parivesanā tenupasaṅkami; upasaṅkamitvā| ekamantaṃ aṭṭhāsi.| addasā kho kasibhāradvājo brāhmaṇo bhagavantaṃ piṇḍāya ṭhitaṃ.| disvā bhagavantaṃ etadavoca| –</p><p>“Ahaṃ kho, samaṇa, kasāmi ca vapāmi ca, kasitvā ca vapitvā ca bhuñjāmi.| tvampi samaṇa, kasassu ca vapassu ca, kasitvā ca vapitvā ca bhuñjassū”ti.|</p><p>“Ahampi kho, brāhmaṇa, kasāmi ca vapāmi ca, kasitvā ca vapitvā ca bhuñjāmī”ti.| na kho pana mayaṃ passāma bhoto gotamassa| yugaṃ vā naṅgalaṃ vā phālaṃ vā pācanaṃ vā balīvadde vā,| atha ca pana bhavaṃ gotamo evamāha| – “Ahampi kho, brāhmaṇa, kasāmi ca vapāmi ca,| kasitvā ca vapitvā ca bhuñjāmī”ti.| atha kho kasibhāradvājo brāhmaṇo| bhagavantaṃ gāthāya ajjhabhāsi| –</p><p>“Kassako paṭijānāsi, na ca passāmi te kasiṃ;</p><p>kasiṃ no pucchito brūhi, yathā jānemu te kasiṃ.</p><p>“Saddhā bījaṃ tapo vuṭṭhi, paññā me yuganaṅgalaṃ;</p>', '-49-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 50, '<p>hirī īsā mano yottaṃ, sati me phālapācanaṃ.</p><p>“Kāyagutto vacīgutto, āhāre udare yato;</p><p>saccaṃ karomi niddānaṃ, soraccaṃ me pamocanaṃ.</p><p>“Vīriyaṃ me dhuradhorayhaṃ, yogakkhemādhivāhanaṃ;</p><p>gacchati anivattantaṃ, yattha gantvā na socati.</p><p>“Evamesā kasī kaṭṭhā, sā hoti amatapphalā;</p><p>etaṃ kasiṃ kasitvāna, sabbadukkhā pamuccatī”ti.</p><p>Atha kho kasibhāradvājo brāhmaṇo| mahatiyā kaṃsapātiyā pāyāsaṃ vaḍḍhetvā| bhagavato upanāmesi| – ‘‘bhuñjatu bhavaṃ gotamo pāyasaṃ.| kassako bhavaṃ;| yaṃ hi bhavaṃ gotamo amatapphalaṃ kasiṃ kasatī’’ti.|</p><p>“Gāthābhigītaṃ me abhojaneyyaṃ,</p><p>sampassataṃ brāhmaṇa nesa dhammo;</p><p>gāthābhigītaṃ panudanti buddhā,</p><p>dhamme satī brāhmaṇa vuttiresā.</p><p>“Aññena ca kevalinaṃ mahesiṃ,</p><p>khīṇāsavaṃ kukkuccavūpasantaṃ;</p><p>annena pānena upaṭṭhahassu,</p><p>khettañhi taṃ puññapekkhassa hotī”ti.</p><p>‘‘atha kassa cāhaṃ, bho gotama, imaṃ pāyasaṃ dammī’’ti?| ‘‘na khvāhaṃ taṃ, brāhmaṇa, passāmi| sadevake loke samārake sabrahmake| sassamaṇabrāhmaṇiyā pajāya sadevamanussāya,| yassa so pāyaso bhutto sammā pariṇāmaṃ gaccheyya,| aññatra tathāgatassa vā tathāgatasāvakassa vā.| tena hi tvaṃ, brāhmaṇa, taṃ pāyasaṃ| appaharite vā chaḍḍehi| appāṇake vā udake opilāpehī’’ti.|</p><p>atha kho kasibhāradvājo brāhmaṇo| taṃ pāyasaṃ appāṇake udake opilāpesi.| atha kho so pāyaso udake pakkhitto| cicciṭāyati ciṭiciṭāyati| sandhūpāyati sampadhūpāyati| seyyathāpi nāma phālo divasasantatto udake pakkhitto| cicciṭāyati ciṭiciṭāyati sandhūpāyati sampadhūpāyati;| evameva so pāyaso udake pakkhitto| cicciṭāyati ciṭiciṭāyati sandhūpāyati sampadhūpāyati.|</p><p>atha kho kasibhāradvājo brāhmaṇo| saṃviggo lomahaṭṭhajāto| yena bhagavā tenupasaṅkami; upasaṅkamitvā| bhagavato pādesu sirasā nipatitvā bhagavantaṃ etadavoca| – ‘‘abhikkantaṃ, bho gotama, abhikkantaṃ, bho gotama!| seyyathāpi, bho gotama, nikkujjitaṃ vā ukkujjeyya,| paṭicchannaṃ vā vivareyya,|mūḷhassa vā maggaṃ ācikkheyya,| andhakāre vā telapajjotaṃ dhāreyya,| cakkhumanto rūpāni dakkhintīti;| evamevaṃ bhotā gotamena anekapariyāyena dhammo pakāsito.| esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi| dhammañca bhikkhusaṅghañca,| labheyyāmahaṃ bhoto gotamassa santike pabbajjaṃ,| labheyyaṃ upasampada’’nti.|</p><p>alattha kho kasibhāradvājo brāhmaṇo bhagavato santike pabbajjaṃ,| alattha upasampadaṃ.| acirūpasampanno kho panāyasmā bhāradvājo| eko vūpakaṭṭho appamatto ātāpī pahitatto viharanto| nacirasseva – yassatthāya kulaputtā| sammadeva agārasmā anagāriyaṃ pabbajanti,| tadanuttaraṃ – brahmacariyapariyosānaṃ| diṭṭheva dhamme sayaṃ abhiññā sacchikatvā upasampajja vihāsi.| ‘‘khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ, nāparaṃ itthattāyā’’ti abbhaññāsi.| aññataro ca kho, panāyasmā bhāradvājo arahataṃ ahosīti.|</p><p>(S.I.174)</p><p>kasībhāradvāja suttaṃ niṭṭhitaṃ.</p><p>                                                            5. Parābhavasuttaṃ </p><p>(Discourse on Downfall) </p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| atha kho aññatarā devatā abhikkantāya rattiyā| abhikkantavaṇṇā kevalakappaṃ jetavanaṃ obhāsetvā| yena bhagavā tenupasaṅkami; upasaṅkamitvā| bhagavantaṃ abhivādetvā ekamantaṃ aṭṭhāsi.| ekamantaṃ ṭhitā kho sā devatā bhagavantaṃ gāthāya ajjhabhāsi| –</p><p>“Parābhavantaṃ purisaṃ, mayaṃ pucchāma gotama;</p><p>bhagavantaṃ puṭṭhumāgamma, kiṃ parābhavato mukhaṃ”.</p><p>“Suvijāno bhavaṃ hoti, suvijāno parābhavo;</p><p>dhammakāmo bhavaṃ hoti, dhammadessī parābhavo”.</p><p>“Iti hetaṃ vijānāma, paṭhamo so parābhavo;</p><p>dutiyaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Asantassa piyā honti, sante na kurute piyaṃ;</p><p>asataṃ dhammaṃ roceti, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, dutiyo so parābhavo;</p><p>tatiyaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Niddāsīlī sabhāsīlī, anuṭṭhātā ca yo naro;</p>', '-50-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 51, '<p>alaso kodhapaññāṇo, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, tatiyo so parābhavo;</p><p>catutthaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Yo mātaraṃ pitaraṃ vā, jiṇṇakaṃ gatayobbanaṃ;</p><p>pahū santo na bharati, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, catuttho so parābhavo;</p><p>pañcamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Yo brāhmaṇaṃ samaṇaṃ vā, aññaṃ vāpi vanibbakaṃ;</p><p>musāvādena vañceti, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, pañcamo so parābhavo;</p><p>chaṭṭhamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Pahūtavitto puriso, sahirañño sabhojano;</p><p>eko bhuñjati sādūni, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, chaṭṭhamo so parābhavo;</p><p>sattamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Jātitthaddho dhanatthaddho, gottatthaddho ca yo naro;</p><p>saññātiṃ atimaññeti, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, sattamo so parābhavo;</p><p>aṭṭhamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Itthidhutto surādhutto, akkhadhutto ca yo naro;</p><p>laddhaṃ laddhaṃ vināseti, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, aṭṭhamo so parābhavo;</p><p>navamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Sehi dārehasantuṭṭho, vesiyāsu padissati;</p><p>dissati paradāresu, taṃ parābhavato mukhaṃ”.</p><p>“Itihetaṃ vijānāma, navamo so parābhavo;</p><p>dasamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Atītayobbano poso, āneti timbarutthaniṃ;</p><p>tassā issā na supati, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, dasamo so parābhavo;</p><p>ekādasamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Itthisoṇḍiṃ vikiraṇiṃ, purīsaṃ vāpi tādisaṃ;</p><p>issarīyasmiṃ ṭhāpeti, taṃ parābhavato mukhaṃ”.</p><p>“Iti hetaṃ vijānāma, ekādasamo so parābhavo;</p><p>dvādasamaṃ bhagavā brūhi, kiṃ parābhavato mukhaṃ”.</p><p>“Appabhogo mahātaṇho, khattiye jāyate kule;</p>', '-51-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 52, '<p>so ca rajjaṃ patthayati, taṃ parābhavato mukhaṃ”.</p><p></p><p></p><p>“Ete parābhave loke, paṇḍito samavekkhiya;</p><p>ariyo dassanasampanno, salokaṃ bhajate siva”nti.</p><p>(Sn.295)</p><p>parābhavasuttaṃ niṭṭhitaṃ.</p><p></p><p></p><p>                                                               6. Vasalasuttaṃ </p><p>(Discourse on the Outcaste)</p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme.| atha kho bhagavā pubbaṇhasamayaṃ nivāsetvā pattacīvaramādāya| sāvatthiṃ piṇḍāya pāvisi.| tena kho pana samayena aggikabhāradvājassa brāhmaṇassa nivesane| aggi pajjalito bhoti āhuti paggahitā.| atha kho bhagavā sāvatthiyaṃ sapadānaṃ piṇḍāya caramāno| yena aggikabhāradvājassa brāhmaṇassa nivesanaṃ tenupasaṅkami.|</p><p>addasā kho aggikabhāradvājo brāhmaṇo bhagavantaṃ dūratova āgacchantaṃ.| disvāna bhagavantaṃ etadavoca| – “Tatreva, muṇḍaka; tatreva, samaṇaka; tatreva, vasalaka tiṭṭhāhī”ti.|</p><p>evaṃ vutte, bhagavā aggikabhāradvājaṃ brāhmaṇaṃ etadavoca| – “Jānāsi pana tvaṃ, brāhmaṇa, vasalaṃ vā vasalakaraṇe vā dhamme”ti?| “Na khvāhaṃ, bho gotama, jānāmi vasalaṃ vā vasalakaraṇe vā dhamme;| sādhu me bhavaṃ gotamo tathā dhammaṃ desetu,| yathāhaṃ jāneyyaṃ vasalaṃ vā vasalakaraṇe vā dhamme”ti.| “Tena hi, brāhmaṇa, suṇāhi, sādhukaṃ manasi karohi;| bhāsissāmī”ti.| “Evaṃ, bho”ti kho aggikabhāradvājo brāhmaṇo bhagavato paccassosi.| bhagavā etadavoca| –</p><p>“Kodhano upanāhī ca, pāpamakkhī ca yo naro;</p><p>vipannadiṭṭhi māyāvī, taṃ jaññā vasalo iti.</p><p>“Ekajaṃ vā dvījaṃ vāpi, yodha pāṇāni hiṃsati;</p><p>yassa pāṇe dayā natthi, taṃ jaññā vasalo iti.</p><p>“Yo hanti parirundhati, gāmāni nigamāni ca;</p><p>niggāhako samaññāto, taṃ jaññā vasalo iti.</p><p>“Gāme vā yadi vā raññe, yaṃ paresaṃ mamāyitaṃ;</p><p>theyyā ādinnaṃ ādiyati, taṃ jaññā vasalo iti.</p><p>“Yo have iṇamādāya, cujjamāno palāyati;</p><p>na hi te iṇamatthīti, taṃ jaññā vasalo iti.</p><p>“Yo ve kiñcikkhakamyatā, panthasmiṃ vajataṃ janaṃ;</p><p>hantvā kiñcikkhamādeti, taṃ jaññā vasalo iti.</p><p>“Attahetu parahetu, dhanahetu ca yo naro;</p><p>sakkhipuṭṭho musā brūti, taṃ jaññā vasalo iti.</p><p>“Yo ñātinaṃ sakhānaṃ vā, dāresu patidissati;</p><p>sāhasā sampiyena vā, taṃ jaññā vasalo iti.</p><p>“Yo mātaraṃ pitaraṃ vā, jiṇṇakaṃ gatayobbanaṃ;</p><p>pahū santo na bharati, taṃ jaññā vasalo iti.</p><p>“Yo mātaraṃ pitaraṃ vā, bhātaraṃ bhaginiṃ sasuṃ;</p><p>bhanti roseti vācāya, taṃ jaññā vasalo iti.</p><p>“Yo atthaṃ pucchito santo, anatthamanusāsati;</p><p>paṭicchannena manteti, taṃ jaññā vasalo iti.</p>', '-52-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 53, '<p>“Yo katvā pāpakaṃ kammaṃ, mā maṃ jaññāti icchati;</p><p>yo paṭicchannakammanto, taṃ jaññā vasalo iti.</p><p>“Yo ve parakulaṃ gantvā, bhutvāna sucibhojanaṃ;</p><p>āgataṃ na paṭipūjeti, taṃ jaññā vasalo iti.</p><p>“Yo brāhmaṇaṃ vā samaṇaṃ vā, aññaṃ vāpi vaṇibbakaṃ;</p><p>musāvādena vañceti, taṃ jaññā vasalo iti.</p><p>“Yo brāhmaṇaṃ vā samaṇaṃ vā, bhattakāle upaṭṭhite;</p><p>roseti vācā na ca deti, taṃ jaññā vasalo iti.</p><p>“Asataṃ yodha pabrūti, mohena paliguṇṭhito;</p><p>kiñcikkhaṃ nijigiṃsāno, taṃ jaññā vasalo iti.</p><p>“Yo cattānaṃ samukkaṃse, pare ca mavajānati;</p><p>nihino sena mānena, taṃ jaññā vasalo iti.</p><p>“Rosako kadariyo ca, pāpiccho maccharī saṭho;</p><p>ahiriko anottāpī, taṃ jaññā vasalo iti.</p><p>“Yo buddhaṃ paribhāsati, atha vā tassa sāvakaṃ;</p><p>paribbājaṃ gahaṭṭhaṃ vā, taṃ jaññā vasalo iti.</p><p>“Yo ve anarahā santo, arahaṃ paṭijānati;</p><p>coro sabrahmake loke, eso kho vasalādhamo.</p><p>“Ete kho vasalā vutthā, mayā co ye pakāsitā;</p><p>na jaccā vasalo hoti, na jaccā hoti brāhmaṇo;</p><p>kammanā vasalo hoti, kammanā hoti brāhmaṇo.</p><p>“Tadamināpi jānātha, yathā medaṃ nidassanaṃ;</p><p>caṇḍālaputto sopāko, mātaṅgo iti vissuto.</p><p>“So yasaṃ paramaṃ patto, mātaṅgo yaṃ sudullabhaṃ;</p><p>āgañchuṃ tassupaṭṭhānaṃ, khattiyā brāhmaṇā bahū.</p><p>“So devayānamāruyha, virajaṃ so mahāpathaṃ;</p><p>kāmarāgaṃ virājetvā, brahmalokūpago ahū;</p><p>na naṃ jāti nivāresi, brahmalokūpapattiyā.</p><p>“Ajjhāyakakule jātā, brāhmaṇā mantabandhūno;</p><p>te ca pāpesu kammesu, abhiṇhamupadissare.</p><p>“Aṭṭheva dhamme gārayhā, samparāye ca duggatiṃ;</p><p>na te jāti nivāreti, duggataccā garahāya vā.</p><p>“Na jaccā vasalo hoti, na jaccā hoti brāhmaṇo;</p><p>kammanā vasalo hoti, kammanā hoti brāhmaṇo”ti.</p><p>Evaṃ vutte, aggikabhāradvājo brāhmaṇo bhagavantaṃ etadavoca| – abhikkantaṃ bho gotama, abhikkantaṃ bho gotama, seyyathāpi bho gotama nikkujjitaṃ vā ukkujjeyya paṭicchannaṃ vā vivareyya mūḷhassa vā maggaṃ ācikkheyya andhakāre vā telapajjotaṃ dhāreyya cakkhumanto rūpāni dakkhintīti, evameva bhotā gotamena anekapariyāyena dhammo pakāsito. Esāhaṃ bhavantaṃ gotamaṃ saraṇaṃ gacchāmi, dhammañca bhikkhusaṅghañca. Upāsakaṃ maṃ bhavaṃ gotamo dhāretu ajjatagge pāṇupetaṃ saraṇaṃ gatanti.</p><p>(Sn.297)</p>', '-53-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 54, '<p>vasalasuttaṃ niṭṭhitaṃ. </p><p>                                                                  7. Saccavibhaṅgasutta</p><p>(Discourse on the analysis of the Truths) </p><p>evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| bārāṇasiyaṃ viharati isipatane migadāye.| tatra kho bhagavā bhikkhū āmantesi – “Bhikkhavo”ti.| “Bhadante”ti te bhikkhū bhagavato paccassosuṃ. Bhagavā etadavoca; –</p><p>“Tathāgatena, bhikkhave, arahatā sammāsambuddhena| bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appaṭivattiyaṃ| samaṇena vā brāhmaṇena vā |devena vā mārena vā brahmunā vā kenaci vā lokasmiṃ,| yadidaṃ – catunnaṃ ariyasaccānaṃ| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ.| katamesaṃ catunnaṃ?| dukkhassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhasamudayassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhanirodhassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhanirodhagāminiyā paṭipadāya ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ.| tathāgatena, bhikkhave, arahatā sammāsambuddhena bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appaṭivattiyaṃ| samaṇena vā brāhmaṇena vā| devena vā mārena vā brahmunā vā kenaci vā lokasmiṃ,| yadidaṃ – imesaṃ catunnaṃ ariyasaccānaṃ| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ.|</p><p>“Sevatha, bhikkhave, sāriputtamoggallāne;| bhajatha, bhikkhave, sāriputtamoggallāne.| paṇḍitā bhikkhū anuggāhakā sabrahmacārīnaṃ.| seyyathāpi, bhikkhave, janetti, evaṃ sāriputto;| seyyathāpi jātassa āpādetā, evaṃ moggallāno.| sāriputto, bhikkhave, sotāpattiphale vineti,| moggallāno uttamatthe vineti. Sāriputto, bhikkhave, pahoti cattāri ariyasaccāni vitthārena ācikkhituṃ| desetuṃ paññāpetuṃ paṭṭhapetuṃ vivarituṃ vibhajituṃ uttānīkātu”nti.| idamavoca bhagavā. Idaṃ vatvāna sugato uṭṭhāyāsanā vihāraṃ pāvisi.|</p><p>tatra kho āyasmā sāriputto| acirapakkantassa bhagavato bhikkhū āmantesi| – “Āvuso, bhikkhave”ti.| “Āvuso”ti kho te bhikkhū āyasmato sāriputtassa paccassosuṃ.| āyasmā sāriputto etadavoca| –</p><p>“Tathāgatena, āvuso, arahatā sammāsambuddhena| bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appaṭivattiyaṃ samaṇena vā brāhmaṇena vā| devena vā mārena vā brahmunā vā kenaci vā lokasmiṃ,| yadidaṃ – catunnaṃ ariyasaccānaṃ| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ.| katamesaṃ catunnaṃ?| dukkhassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhasamudayassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhanirodhassa ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ,| dukkhanirodhagāminiyā paṭipadāya ariyasaccassa| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkammaṃ.|</p><p>“Katamañcāvuso, dukkhaṃ ariyasaccaṃ? |jātipi dukkhā, jarāpi dukkhā, maraṇampi dukkhaṃ,| sokaparidevadukkhadomanassupāyāsāpi dukkhā,| yampicchaṃ na labhati tampi dukkhaṃ;| saṅkhittena pañcupādānakkhandhā dukkhā.|</p><p>“Katamā cāvuso, jāti?| yā tesaṃ tesaṃ sattānaṃ| tamhi tamhi sattanikāye| jāti sañjāti okkanti abhinibbatti| khandhānaṃ pātubhāvo āyatanānaṃ paṭilābho,| ayaṃ vuccatāvuso – ‘Jāti.|</p><p>“Katamā cāvuso, jarā? |yā tesaṃ tesaṃ sattānaṃ| tamhi tamhi sattanikāye| jarā jīraṇatā khaṇḍiccaṃ pāliccaṃ valittacatā| āyuno saṃhāni indriyānaṃ paripāko,| ayaṃ vuccatāvuso – ‘Jarā’”.|</p><p>“Katamañcāvuso, maraṇaṃ?| yaṃ tesaṃ tesaṃ sattānaṃ| tamhā tamhā sattanikāyā| cuti cavanatā bhedo antaradhānaṃ| maccu maraṇaṃ kālaṅkiriyā khandhānaṃ bhedo |kaḷevarassa nikkhepo| jīvitindriyassupacchedo,| idaṃ vuccatāvuso – ‘Maraṇaṃ.’|</p><p>“Katamo cāvuso, soko?| yo kho, āvuso, aññataraññatarena byasanena samannāgatassa| aññataraññatarena dukkhadhammena phuṭṭhassa| soko socanā socitattaṃ| antosoko antoparisoko,| ayaṃ vuccatāvuso – ‘Soko.|</p><p>“Katamo cāvuso, paridevo?| yo kho, āvuso, aññataraññatarena byasanena samannāgatassa| aññataraññatarena dukkhadhammena phuṭṭhassa| ādevo paridevo ādevanā paridevanā| ādevitattaṃ paridevitattaṃ,| ayaṃ vuccatāvuso – paridevo.|</p><p>“Katamañcāvuso, dukkhaṃ?| yaṃ kho, āvuso, kāyikaṃ dukkhaṃ kāyikaṃ asātaṃ|kāyasamphassajaṃ dukkhaṃ asātaṃ vedayitaṃ,| idaṃ vuccatāvuso – ‘Dukkhaṃ’”.|</p><p>“Katamañcāvuso, domanassaṃ?| yaṃ kho, āvuso, cetasikaṃ dukkhaṃ| cetasikaṃ asātaṃ manosamphassajaṃ dukkhaṃ asātaṃ vedayitaṃ,| idaṃ vuccatāvuso ‘Domanassaṃ’.|</p><p>“Katamo cāvuso, upāyāso?| yo kho, āvuso, aññataraññatarena byasanena samannāgatassa| aññataraññatarena dukkhadhammena phuṭṭhassa| āyāso upāyāso āyāsitattaṃ upāyāsitattaṃ,| ayaṃ vuccatāvuso – ‘Upāyāso’”.|</p><p>“Katamañcāvuso, yampicchaṃ na labhati tampi dukkhaṃ?| jātidhammānaṃ, āvuso, sattānaṃ evaṃ icchā uppajjati |– ‘Aho vata, mayaṃ na jātidhammā assāma;| na ca, vata, no jāti āgaccheyyā’ti.| na kho panetaṃ icchāya pattabbaṃ.| idampi – ‘Yampicchaṃ na labhati tampi dukkhaṃ’.| jarādhammānaṃ, āvuso, sattānaṃ evaṃ icchā uppajjati |– ‘Aho vata, mayaṃ na jarādhammā assāma;| na ca, vata, no jarā āgaccheyyā’ti.| na kho panetaṃ icchāya pattabbaṃ.| idampi – ‘Yampicchaṃ na labhati tampi dukkhaṃ’.|byādhidhammānaṃ, āvuso, sattānaṃ evaṃ icchā uppajjati |– ‘Aho vata, mayaṃ na byādhidhammā assāma;| na ca, vata, no byādhi āgaccheyyā’ti.| na kho panetaṃ icchāya pattabbaṃ.| idampi – ‘Yampicchaṃ na labhati tampi dukkhaṃ’.| maraṇadhammānaṃ, āvuso, sattānaṃ evaṃ icchā uppajjati |– ‘Aho vata, mayaṃ na maraṇadhammā assāma;| na ca, vata, no maraṇaṃ āgaccheyyā’ti.| na kho panetaṃ icchāya pattabbaṃ.| idampi – ‘Yampicchaṃ na labhati tampi dukkhaṃ’.| sokaparidevadukkhadomanassupāyāsadhammānaṃ, āvuso,| sattānaṃ evaṃ icchā uppajjati |– ‘Aho vata, mayaṃ na sokaparidevadukkhadomanassupāyāsadhammā assāma;| na ca, vata, no sokaparidevadukkhadomanassupāyāsā āgaccheyyu’nti.| na kho panetaṃ icchāya pattabbaṃ.| idampi – ‘Yampicchaṃ na labhati tampi dukkhaṃ’”.|</p><p>“Katame cāvuso, saṅkhittena pañcupādānakkhandhā dukkhā?| seyyathidaṃ |– rūpupādānakkhandho,| vedanupādānakkhandho,| saññupādānakkhandho,| saṅkhārupādānakkhandho,| viññāṇupādānakkhandho.| ime vuccantāvuso – ‘Saṅkhittena pañcupādānakkhandhā dukkhā’.| idaṃ vuccatāvuso – ‘Dukkhaṃ ariyasaccaṃ’”.|</p><p>“Katamañcāvuso, dukkhasamudayaṃ ariyasaccaṃ?| yāyaṃ taṇhā ponobbhavikā| nandīrāgasahagatā tatratatrābhinandinī, seyyathidaṃ| – kāmataṇhā bhavataṇhā vibhavataṇhā,| idaṃ vuccatāvuso – ‘Dukkhasamudayaṃ ariyasaccaṃ’.|</p><p>“Katamañcāvuso, dukkhanirodhaṃ ariyasaccaṃ?| yo tassāyeva taṇhāya asesavirāganirodho| cāgo paṭinissaggo mutti anālayo,| idaṃ vuccatāvuso – ‘Dukkhanirodhaṃ ariyasaccaṃ’”.|</p><p>“Katamañcāvuso, dukkhanirodhagāminī paṭipadā ariyasaccaṃ?| ayameva ariyo aṭṭhaṅgiko maggo, seyyathidaṃ| – sammādiṭṭhi, sammāsaṅkappo,| sammāvācā, sammākammanto,| sammāājīvo, sammāvāyāmo,| sammāsati, sammāsamādhi.|</p><p>“Katamācāvuso, sammādiṭṭhi?| yaṃ kho, āvuso, dukkhe ñāṇaṃ, dukkhasamudaye ñāṇaṃ,| dukkhanirodhe ñāṇaṃ, dukkhanirodhagāminiyā paṭipadāya ñāṇaṃ,| ayaṃ vuccatāvuso – ‘Sammādiṭṭhi’”.|</p><p>“Katamo cāvuso, sammāsaṅkappo?| nekkhammasaṅkappo, abyāpādasaṅkappo,| avihiṃsāsaṅkappo,| ayaṃ vuccatāvuso – ‘Sammāsaṅkappo’”.|</p><p>“Katamā cāvuso, sammāvācā?| musāvādā veramaṇī, pisuṇāya vācāya veramaṇī,| pharusāya vācāya veramaṇī, samphappalāpā veramaṇī,| ayaṃ vuccatāvuso ‘Sammāvācā’.|</p><p>“Katamo cāvuso, sammākammanto?| pāṇātipātā veramaṇī,| adinnādānā veramaṇī,| kāmesumicchācārā veramaṇī,| ayaṃ vuccatāvuso ‘Sammākammanto.’|</p><p>“Katamo cāvuso, sammāājīvo?| idhāvuso, ariyasāvako micchāājīvaṃ pahāya |sammāājīvena jīvikaṃ kappeti,| ayaṃ vuccatāvuso – ‘Sammāājīvo’.|</p><p>“Katamo cāvuso, sammāvāyāmo?| idhāvuso, bhikkhu anuppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ anuppādāya| chandaṃ janeti vāyamati vīriyaṃ ārabhati| cittaṃ paggaṇhāti padahati,| uppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya| chandaṃ janeti vāyamati vīriyaṃ ārabhati| cittaṃ paggaṇhāti padahati,| anuppannānaṃ kusalānaṃ dhammānaṃ uppādāya| chandaṃ janeti vāyamati vīriyaṃ ārabhati| cittaṃ paggaṇhāti padahati,| uppannānaṃ kusalānaṃ dhammānaṃ| ṭhitiyā asammosāya bhiyyobhāvāya vepullāya bhāvanāya pāripūriyā| chandaṃ janeti vāyamati vīriyaṃ ārabhati| cittaṃ paggaṇhāti padahati,| ayaṃ vuccatāvuso – ‘Sammāvāyāmo’.|</p><p>“Katamā cāvuso, sammāsati?| idhāvuso, bhikkhu kāye kāyānupassī viharati| ātāpī sampajāno satimā vineyya loke abhijjhādomanassaṃ.| vedanāsu vedanānupassī viharati|…pe… citte cittānupassī viharati… dhammesu dhammānupassī viharati| ātāpī sampajāno satimā vineyya loke abhijjhādomanassaṃ,| ayaṃ vuccatāvuso – ‘Sammāsati’.|s</p><p>“Katamo cāvuso, sammāsamādhi?| idhāvuso, bhikkhu vivicceva kāmehi vivicca akusalehi dhammehi| savitakkaṃ savicāraṃ vivekajaṃ pītisukhaṃ| paṭhamaṃ jhānaṃ upasampajja viharati,| vitakkavicārānaṃ vūpasamā| ajjhattaṃ sampasādanaṃ cetaso ekodibhāvaṃ| avitakkaṃ avicāraṃ samādhijaṃ pītisukhaṃ| dutiyaṃ jhānaṃ upasampajja viharati,| pītiyā ca virāgā upekkhako ca viharati| sato ca sampajāno, sukhañca kāyena paṭisaṃvedeti| yaṃ taṃ ariyā ācikkhanti ‘Upekkhako satimā sukhavihārī’ti| tatiyaṃ jhānaṃ upasampajja viharati.| sukhassa ca pahānā dukkhassa ca pahānā,| pubbeva somanassadomanassānaṃ atthaṅgamā,| adukkhamasukhaṃ upekkhāsatipārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati.| ayaṃ vuccatāvuso – ‘Sammāsamādhi’.| idaṃ vuccatāvuso – ‘Dukkhanirodhagāminī paṭipadā ariyasaccaṃ’.|</p><p>“Tathāgatenāvuso, arahatā sammāsambuddhena| bārāṇasiyaṃ isipatane migadāye| anuttaraṃ dhammacakkaṃ pavattitaṃ appaṭivattiyaṃ| samaṇena vā brāhmaṇena vā| devena vā mārena vā brahmunā vā kenaci vā lokasmiṃ,| yadidaṃ – imesaṃ catunnaṃ ariyasaccānaṃ| ācikkhanā desanā paññāpanā paṭṭhapanā| vivaraṇā vibhajanā uttānīkamma”nti.|</p><p>idamavoca āyasmā sāriputto.| attamanā te bhikkhū āyasmato sāriputtassa bhāsitaṃ abhinandunti.|</p><p>(M.III.291)</p><p>saccavibhaṅgasuttaṃ niṭṭhitaṃ ekādasamaṃ.</p><p></p><p></p>', '-54-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Tatiyabhāṇavāro', 'chapter', 55);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 55, '<p>                                                                     8. Anattalakkhaṇasuttaṃ  </p><p>(Discourse on the Characteristics of Non-self)</p><p>evaṃ me sutaṃ ekaṃ samayaṃ bhagavā| bārāṇasiyaṃ viharati isipatane migadāye.| tatra kho bhagavā pañcavaggiye bhikkhū āmantesi – ‘‘bhikkhavo’’ti.| ‘‘bhadante’’ti te bhikkhū bhagavato paccassosuṃ. Bhagavā etadavoca –|</p><p>‘‘rūpaṃ, bhikkhave, anattā.| rūpañca hidaṃ, bhikkhave, attā abhavissa,| nayidaṃ rūpaṃ ābādhāya saṃvatteyya,| labbhetha ca rūpe – ‘Evaṃ me rūpaṃ hotu,| evaṃ me rūpaṃ mā ahosī’ti.| yasmā ca kho, bhikkhave, rūpaṃ anattā,| tasmā rūpaṃ ābādhāya saṃvattati,| na ca labbhati rūpe| – ‘Evaṃ me rūpaṃ hotu, evaṃ me rūpaṃ mā ahosī’’ti.|</p><p>‘‘vedanā bhikkhave anattā.| vedanā ca hidaṃ, bhikkhave, attā abhavissa,| nayidaṃ vedanā ābādhāya saṃvatteyya,| labbhetha ca vedanāya |– ‘Evaṃ me vedanā hotu, evaṃ me vedanā mā ahosī’ti.| yasmā ca kho, bhikkhave, vedanā anattā,| tasmā vedanā ābādhāya saṃvattati,| na ca labbhati vedanāya| – ‘Evaṃ me vedanā hotu, evaṃ me vedanā mā ahosī’’’ti.|</p><p>‘‘saññā bhikkhave anattā.| saññā ca hidaṃ, bhikkhave, attā abhavissa,| nayidaṃ saññā ābādhāya saṃvatteyya,| labbhetha ca saññāya |– ‘Evaṃ me saññā hotu, evaṃ me saññā mā ahosī’ti.| yasmā ca kho, bhikkhave, saññā anattā,| tasmā saññā ābādhāya saṃvattati,| na ca labbhati saññāya| – ‘Evaṃ me saññā hotu, evaṃ me saññā mā ahosī’’’ti.|‘‘saṅkhārā bhikkhave anattā.| saṅkhārā ca hidaṃ, bhikkhave, attā abhavissaṃsu,| nayidaṃ saṅkhārā ābādhāya saṃvatteyyuṃ,| labbhetha ca saṅkhāresu| – ‘Evaṃ me saṅkhārā hontu, evaṃ me saṅkhārā mā ahesu’nti.| yasmā ca kho, bhikkhave, saṅkhārā anattā,| tasmā saṅkhārā ābādhāya saṃvattanti,| na ca labbhati saṅkhāresu| – ‘Evaṃ me saṅkhārā hontu, evaṃ me saṅkhārā mā ahesu’’’nti.|</p><p>‘‘viññāṇaṃ anattā.| viññāṇañca hidaṃ, bhikkhave, attā abhavissa,| nayidaṃ viññāṇaṃ ābādhāya saṃvatteyya,| labbhetha ca viññāṇe |– ‘Evaṃ me viññāṇaṃ hotu, evaṃ me viññāṇaṃ mā ahosī’ti.| yasmā ca kho, bhikkhave, viññāṇaṃ anattā,| tasmā viññāṇaṃ ābādhāya saṃvattati,| na ca labbhati viññāṇe| – ‘Evaṃ me viññāṇaṃ hotu, evaṃ me viññāṇaṃ mā ahosī’’’ti.|</p><p>‘‘taṃ kiṃ maññatha, bhikkhave, rūpaṃ niccaṃ vā aniccaṃ vā’’ti?| ‘‘aniccaṃ, bhante’’.| ‘‘yaṃ panāniccaṃ dukkhaṃ vā taṃ sukhaṃ vā’’ti?| ‘‘dukkhaṃ, bhante’’.| ‘‘yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ,| kallaṃ nu taṃ samanupassituṃ| – ‘Etaṃ mama, esohamasmi, eso me attā’’’ti? |‘‘no hetaṃ, bhante’’.|</p><p>‘‘vedanā niccā vā aniccā vā’’ti?| ‘‘aniccā, bhante’’.| ‘‘yā panāniccā dukkhā vā taṃ sukhā vā’’ti?| ‘‘dukkhā, bhante’’.| ‘‘yā panāniccā dukkhā vipariṇāmadhammā,| kallaṃ nu taṃ samanupassituṃ| – ‘Etaṃ mama, esohamasmi, eso me attā’’’ti? |‘‘no hetaṃ, bhante’’.|</p><p>‘‘saññā niccā vā aniccā vā’’ti?| ‘‘aniccā, bhante’’.| ‘‘yā panāniccā dukkhā vā taṃ sukhā vā’’ti?| ‘‘dukkhā, bhante’’.| ‘‘yā panāniccā dukkhā vipariṇāmadhammā,| kallaṃ nu taṃ samanupassituṃ| – ‘Etaṃ mama, esohamasmi, eso me attā’’’ti? |‘‘no hetaṃ, bhante’’.|</p><p>‘‘saṅkhārā niccā vā aniccā vā’’ti?| ‘‘aniccā, bhante’’.| ‘‘ye panāniccā dukkhā vā te sukhā vā’’ti?| ‘‘dukkhā, bhante’’.| ‘‘ye panāniccā dukkhā vipariṇāmadhammā,| kallaṃ nu te samanupassituṃ| – ‘Etaṃ mama, esohamasmi, eso me attā’’’ti? |‘‘no hetaṃ, bhante’’.|</p><p>‘‘viññāṇaṃ niccaṃ vā aniccaṃ vā’’ti?| ‘‘aniccaṃ, bhante’’.| ‘‘yaṃ panāniccaṃ dukkhaṃ vā taṃ sukhaṃ vā’’ti?| ‘‘dukkhaṃ, bhante’’.| ‘‘yaṃ panāniccaṃ dukkhaṃ vipariṇāmadhammaṃ, kallaṃ nu taṃ samanupassituṃ| – ‘Etaṃ mama, esohamasmi, eso me attā’’’ti?| ‘‘no hetaṃ, bhante’’.|</p><p>‘‘tasmātiha, bhikkhave,| yaṃ kiñci rūpaṃ atītānāgatapaccuppannaṃ| ajjhattaṃ vā bahiddhā vā| oḷārikaṃ vā sukhumaṃ vā| hīnaṃ vā paṇītaṃ vā| yaṃ dūre santike vā,| sabbaṃ rūpaṃ| – ‘Netaṃ mama, nesohamasmi, na meso attā’ti| evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ.| yā kāci vedanā| atītānāgatapaccuppannā| ajjhattā vā bahiddhā vā| oḷārikā vā sukhumā vā| hīnā vā paṇītā vā| yā dūre santike vā,| sabbā vedanā| – ‘Netaṃ mama, nesohamasmi, na meso attā’ti| evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ.|</p><p>‘‘yā kāci saññā atītānāgatapaccuppannā| ajjhattā vā bahiddhā vā| oḷārikā vā sukhumā vā| hīnā vā paṇītā vā| yā dūre santike vā,| sabbā saññā| – ‘Netaṃ mama, nesohamasmi, na meso attā’ti| evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ.|</p><p>ye keci saṅkhārā atītānāgatapaccuppannā| ajjhattā vā bahiddhā vā| oḷārikā vā sukhumā vā| hīnā vā paṇītā vā| ye dūre santike vā|, sabbe saṅkhārā| – ‘Netaṃ mama, nesohamasmi, na meso attā’ti| evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ.|</p><p>‘‘yaṃ kiñci viññāṇaṃ atītānāgatapaccuppannaṃ| ajjhattaṃ vā bahiddhā vā| oḷārikaṃ vā sukhumaṃ vā| hīnaṃ vā paṇītaṃ vā| yaṃ dūre santike vā,| sabbaṃ viññāṇaṃ| – ‘Netaṃ mama, nesohamasmi, na meso attā’ti| evametaṃ yathābhūtaṃ sammappaññāya daṭṭhabbaṃ.|</p><p>‘‘evaṃ passaṃ, bhikkhave, sutavā ariyasāvako| rūpasmimpi nibbindati,| vedanāyapi nibbindati,| saññāyapi nibbindati,| saṅkhāresupi nibbindati,| viññāṇasmimpi nibbindati.| nibbindaṃ virajjati;| virāgā vimuccati.| vimuttasmiṃ vimuttamiti ñāṇaṃ hoti.| ‘Khīṇā jāti, vusitaṃ brahmacariyaṃ, kataṃ karaṇīyaṃ,| nāparaṃ itthattāyā’ti pajānātī’’ti.|</p><p>idamavoca bhagavā. Attamanā pañcavaggiyā bhikkhū| bhagavato bhāsitaṃ abhinanduṃti.|</p><p>imasmiñca pana veyyākaraṇasmiṃ bhaññamāne| pañcavaggiyānaṃ bhikkhūnaṃ anupādāya āsavehi cittāni vimucciṃsūti.|</p><p>(S.II.55)</p><p>anattalakkhaṇasuttaṃ niṭṭhitaṃ.</p><p> </p><p><h1>Tatiyabhāṇavāro</h1></p><p>                                                                        1. Āṭānāṭiyasuttaṃ </p><p>(The Āṭānāṭiya Discourse)</p><p>Evaṃ me sutaṃ – ekaṃ samayaṃ bhagavā| rājagahe viharati gijjhakūṭe pabbate.| atha kho cattāro mahārājā| mahatiyā ca yakkhasenāya| mahatiyā ca gandhabbasenāya| mahatiyā ca kumbhaṇḍasenāya| mahatiyā ca nāgasenāya| catuddisaṃ rakkhaṃ ṭhapetvā| catuddisaṃ gumbaṃ ṭhapetvā| catuddisaṃ ovaraṇaṃ ṭhapetvā| abhikkantāya rattiyā abhikkantavaṇṇā| kevalakappaṃ gijjhakūṭaṃ obhāsetvā| yena bhagavā tenupasaṅkamiṃsu; upasaṅkamitvā| bhagavantaṃ abhivādetvā ekamantaṃ nisīdiṃsu.| tepi kho yakkhā appekacce bhagavantaṃ abhivādetvā| ekamantaṃ nisīdiṃsu,| appekacce bhagavatā saddhiṃ sammodiṃsu,| sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu,| appekacce yena bhagavā tenañjaliṃ paṇāmetvā ekamantaṃ nisīdiṃsu,| appekacce nāmagottaṃ sāvetvā ekamantaṃ nisīdiṃsu,| appekacce tuṇhībhūtā ekamantaṃ nisīdiṃsu.|</p><p>ekamantaṃ nisinno kho vessavaṇo mahārājā| bhagavantaṃ etadavoca| – “Santi hi, bhante, uḷārā yakkhā bhagavato appasannā.| santi hi, bhante, uḷārā yakkhā bhagavato pasannā.| santi hi, bhante, majjhimā yakkhā bhagavato appasannā.| santi hi, bhante, majjhimā yakkhā bhagavato pasannā.| santi hi, bhante, nīcā yakkhā bhagavato appasannā.| santi hi, bhante, nīcā yakkhā bhagavato pasannā.| yebhuyyena kho pana, bhante, yakkhā| appasannāyeva bhagavato. Taṃ kissa hetu?|</p><p>bhagavā hi, bhante, pāṇātipātā veramaṇiyā dhammaṃ deseti,| adinnādānā veramaṇiyā dhammaṃ deseti,| kāmesumicchācārā veramaṇiyā dhammaṃ deseti,| musāvādā veramaṇiyā dhammaṃ deseti,| surāmerayamajjappamādaṭṭhānā veramaṇiyā dhammaṃ deseti.|</p><p>yebhuyyena kho pana, bhante, yakkhā| appaṭiviratāyeva pāṇātipātā,| appaṭiviratā adinnādānā,| appaṭiviratā kāmesumicchācārā,| appaṭiviratā musāvādā,| appaṭiviratā surāmerayamajjappamādaṭṭhānā.| tesaṃ taṃ hoti appiyaṃ amanāpaṃ.|</p><p>santi hi, bhante, bhagavato sāvakā araññevanapatthāni pantāni senāsanāni paṭisevanti| appasaddāni appanigghosāni| vijanavātāni manussarāhaseyyakāni paṭisallānasāruppāni.| tattha santi uḷārā yakkhā nivāsino,| ye imasmiṃ bhagavato pāvacane appasannā.| tesaṃ pasādāya| uggaṇhātu, bhante, bhagavā āṭānāṭiyaṃ rakkhaṃ| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāyā”ti.| adhivāsesi bhagavā tuṇhībhāvena.|</p><p>atha kho vessavaṇo mahārājā bhagavato adhivāsanaṃ viditvā| tāyaṃ velāyaṃ imaṃ āṭānāṭiyaṃ rakkhaṃ abhāsi. |</p><p>“Vipassissa namatthu, cakkhumantassa sirīmato;</p><p>sikhissapi namatthu, sabbabhūtānukampino.</p><p>“Vessabhussa ca namatthu, nahātakassa tapassino;</p><p>namatthu kakusandhassa, mārasenāpamaddino.</p><p>“Koṇāgamanassa namatthu, brāhmaṇassa vusīmato;</p>', '-55-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 56, '<p>kassapassa namatthu, vippamuttassa sabbadhi.</p><p>“Aṅgīrasassa namatthu, sakyaputtassa sirīmato;</p><p>yo imaṃ dhammamadesesi, sabbadukkhāpanūdanaṃ.</p><p>“Ye cāpi nibbutā loke, yathābhūtaṃ vipassisuṃ;</p><p>te janā apisuṇātha, mahantā vītasāradā.</p><p>“Hitaṃ devamanussānaṃ, yaṃ namassanti gotamaṃ;</p><p>vijjācaraṇasampannaṃ, mahantaṃ vītasāradaṃ.</p><p>“Yato uggacchati sūriyo, ādicco maṇḍalī mahā.</p><p>Yassa cuggacchamānassa, saṃvarīpi nirujjhati;</p><p>yassa cuggate sūriye, ‘Divaso’ti pavuccati.</p><p>“Rahadopi tattha gambhīro, samuddo saritodako;</p><p>evaṃ taṃ tattha jānanti, ‘Samuddo saritodako’.</p><p>“Ito ‘Sā purimā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Gandhabbānaṃ adhipati, ‘Dhataraṭṭho’ti nāmaso;</p><p>ramatī naccagītehi, gandhabbehi purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asītiṃ dasa eko ca, indanāmā mahabbalā.</p><p>“Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“Jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ.’</p><p>“Yena petā pavuccanti, pisuṇā piṭṭhimaṃsikā.</p><p>Pāṇātipātino luddā, corā nekatikā janā.</p><p>“Ito ‘Sā dakkhiṇā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p></p><p></p><p>“Kumbhaṇḍānaṃ adhipati, ‘Virūḷho’ iti nāmaso;</p><p>ramatī naccagītehi, kumbhaṇḍehi purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asīti dasa eko ca, indanāmā mahabbalā.</p><p>Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p>', '-56-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 57, '<p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p></p><p></p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“Jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ’.</p><p>“Yattha coggacchati sūriyo, ādicco maṇḍalī mahā.</p><p>Yassa coggacchamānassa, divasopi nirujjhati;</p><p>yassa coggate sūriye, ‘Saṃvarī’ti pavuccati.</p><p>“Rahadopi tattha gambhīro, samuddo saritodako;</p><p>evaṃ taṃ tattha jānanti, ‘Samuddo saritodako’.</p><p>“Ito ‘Sā pacchimā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Nāgānañca ādhipati, ‘Virūpakkho’ti nāmaso;</p><p>ramatī naccagītehi, nāgeheva purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asīti dasa eko ca, indanāmā mahabbalā.</p><p>Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“‘jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ’.</p><p>“Yena uttarakuru rammā, mahāneru sudassano.</p><p>Manussā tattha jāyanti, amamā apariggahā.</p><p>“Na te bījaṃ pavapanti, napi nīyanti naṅgalā;</p><p>akaṭṭhapākimaṃ sāliṃ, paribhuñjanti mānusā.</p><p>“Akaṇaṃ athusaṃ suddhaṃ, sugandhaṃ taṇḍulapphalaṃ;</p><p>tuṇḍikīre pacitvāna, tato bhuñjanti bhojanaṃ.</p><p>“Gāviṃ ekakhuraṃ katvā, anuyanti disodisaṃ;</p><p>pasuṃ ekakhuraṃ katvā, anuyanti disodisaṃ.</p><p>“Itthivāhanaṃ katvā, anuyanti disodisaṃ;</p><p>purisavāhanaṃ katvā, anuyanti disodisaṃ.</p>', '-57-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 58, '<p>“Kumārivāhanaṃ katvā, anuyanti disodisaṃ;</p><p>kumāravāhanaṃ katvā, anuyanti disodisaṃ.</p><p>“Yāne abhirūhitvā, sabbā disā anupariyanti; pacārā tassa rājino.</p><p>“Hatthiyānaṃ assayānaṃ, dibbaṃ yānaṃ upaṭṭhitaṃ;</p><p>pāsādā sivikā ceva, mahārājassa yasassino.</p><p>“Tassa ca nagarā ahu, antalikkhe sumāpitā;</p><p>āṭānāṭā kusināṭā parakusināṭā, nāṭapuriyā parakusitanāṭā.</p><p>“Uttarena kapivanto, janoghamaparena ca;</p><p>navanavutiyo ambaraambaravatiyo, āḷakamandā nāma rājadhānī.</p><p>“Kuverassahi kho pana, mārisa, mahārājassa visāṇā nāma rājadhānī;</p><p>tasmā kuvero mahārājā, ‘Vessavaṇo’ti pavuccati.</p><p>“Paccesanto pakāsenti, tatolā tattalā tatotalā;</p><p>ojasi tejasi tatojasī, sūro rājā ariṭṭho nemi.</p><p></p><p></p><p>“Rahadopi tattha dharaṇī nāma, yato meghā pavassanti;</p><p>vassā yato patāyanti, sabhāpi tattha bhagalavatī nāma.</p><p>“Yattha yakkhā payirupāsanti, tattha niccaphalā rukkhā;</p><p>nānā dijagaṇā yutā, mayūrakoñcābhirutā; kokilādīhi vagguhi.</p><p>“Jīvañjīvakasaddettha, atho uṭṭhavacittakā;</p><p>kuktthakā kuḷīrakā, vane pokkharasātakā.</p><p>“Sukasāḷikasaddettha, daṇḍamāṇavakāni ca;</p><p>sobhati sabbakālaṃ sā, kuveranaḷinī sadā.</p><p>“Ito ‘Sā uttarā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Yakkhānaṃ ādhipati, ‘Kuvero’ iti nāmaso;</p><p>ramatī naccagītehi, yakkhehi purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asītiṃ dasa eko ca, indanāmā mahabbalā.</p><p>“Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>‘Jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotama’”nti.</p>', '-58-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 59, '<p>“Ayaṃ kho sā, mārisa, āṭānāṭiyā rakkhā| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāya.|</p><p>“Yassa kassaci, mārisa, bhikkhussa vā bhikkhuniyā vā| upāsakassa vā upāsikāya vā| ayaṃ āṭānāṭiyā rakkhā suggahitā bhavissati samattā pariyāputā.|</p><p>taṃ ce amanusso| yakkho vā yakkhinī vā| yakkhapotako vā yakkhapotikā vā| yakkhamahāmatto vā yakkhapārisajjo vā yakkhapacāro vā,|</p><p>gandhabbo vā gandhabbī vā| gandhabbapotako vā gandhabbapotikā vā| gandhabbamahāmatto vā gandhabbapārisajjo vā gandhabbapacāro vā,| kumbhaṇḍo vā kumbhaṇḍī vā| kumbhaṇḍapotako vā kumbhaṇḍapotikā vā| kumbhaṇḍamahāmatto vā kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā,|</p><p>nāgo vā nāginī vā| nāgapotako vā nāgapotikā vā| nāgamahāmatto vā nāgapārisajjo vā nāgapacāro vā,|</p><p>paduṭṭhacitto,|</p><p>bhikkhuṃ vā bhikkhuniṃ vā| upāsakaṃ vā upāsikaṃ vā| gacchantaṃ vā anugaccheyya,| ṭhitaṃ vā upatiṭṭheyya,| nisinnaṃ vā upanisīdeyya,| nipannaṃ vā upanipajjeyya.|</p><p>na me so, mārisa, amanusso labheyya| gāmesu vā nigamesu vā| sakkāraṃ vā garukāraṃ vā.|</p><p>na me so, mārisa, amanusso labheyya| āḷakamandāya nāma rājadhāniyā vatthuṃ vā vāsaṃ vā.|</p><p>na me so, mārisa, amanusso labheyya| yakkhānaṃ samitiṃ gantuṃ.|</p><p>apissu naṃ, mārisa, amanussā| anavayhampi naṃ kareyyuṃ avivayhaṃ.|</p><p>apissu naṃ, mārisa, amanussā| attāhipi paripuṇṇāhi paribhāsāhi paribhāseyyuṃ.| apissu naṃ, mārisa, amanussā| rittaṃpissa pattaṃ sīse nikkujjeyyuṃ.|</p><p>apissu naṃ, mārisa, amanussā| sattadhāpissa muddhaṃ phāleyyuṃ.|</p><p>“Santi hi, mārisa, amanussā caṇḍā ruddhā rabhasā,| te neva mahārājānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, amanussā mahārājānaṃ avaruddhā nāma vuccanti.|</p><p>seyyathāpi, mārisa, rañño māgadhassa vijite mahācorā.| te neva rañño māgadhassa ādiyanti,| na rañño māgadhassa purisakānaṃ ādiyanti,| na rañño māgadhassa purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, mahācorā rañño māgadhassa avaruddhā nāma vuccanti.|</p><p>evameva kho, mārisa, santi hi amanussā caṇḍā ruddhā rabhasā,| te neva mahārājānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, amanussā mahārājānaṃ avaruddhā nāma vuccanti.|</p><p>yo hi koci, mārisa, amanusso| yakkho vā yakkhinī vā| yakkhapotako vā yakkhapotikā vā| yakkhamahāmatto vā yakkhapārisajjo vā yakkhapacāro vā,|</p><p>gandhabbo vā gandhabbī vā| gandhabbapotako vā gandhabbapotikā vā| gandhabbamahāmatto vā gandhabbapārisajjo vā gandhabbapacāro vā,| kumbhaṇḍo vā kumbhaṇḍī vā| kumbhaṇḍapotako vā kumbhaṇḍapotikā vā| kumbhaṇḍamahāmatto vā kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā,|</p><p>nāgo vā nāgī vā| nāgapotako vā nāgapotikā vā| nāgamahāmatto vā nāgapārisajjo vā nāgapacāro vā,|</p><p>paduṭṭhacitto| bhikkhuṃ vā bhikkhuniṃ vā| upāsakaṃ vā upāsikaṃ vā| gacchantaṃ vā anugaccheyya,| ṭhitaṃ vā upatiṭṭheyya,| nisinnaṃ vā upanisīdeyya,| nipannaṃ vā upanipajjeyya.|</p><p>imesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ| ujjhāpetabbaṃ vikkanditabbaṃ viravitabbaṃ| –</p><p>‘Ayaṃ yakkho gaṇhāti,| ayaṃ yakkho āvisati,| ayaṃ yakkho heṭheti,| ayaṃ yakkho viheṭheti,| ayaṃ yakkho hiṃsati,| ayaṃ yakkho vihiṃsati,| ayaṃ yakkho na muñcatī’ti.|</p><p>“Katamesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ? |</p><p>“Indo somo varuṇo ca, bhāradvājo pajāpati;</p><p>candano kāmaseṭṭho ca, kinnughaṇḍu nighaṇḍu ca.</p><p>“Panādo opamañño ca, devasūto ca mātali;</p><p>cittaseno ca gandhabbo, naḷo rājā janesabho.</p><p>“Sātāgiro hemavato, puṇṇako karatiyo guḷo;</p><p>sivako mucalindo ca, vessāmitto yugandharo.</p><p>“Gopālo suppagedho ca, hiri netti ca mandiyo;</p><p>pañcālacaṇḍo āḷavako, pajjanno sumano sumukho dadhīmukho;</p><p>maṇi māṇi caro dīgho, atho serissako saha.</p><p>“Imesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ| ujjhāpetabbaṃ vikkanditabbaṃ viravitabbaṃ| – ‘Ayaṃ yakkho gaṇhāti,| ayaṃ yakkho āvisati,| ayaṃ yakkho heṭheti,| ayaṃ yakkho viheṭheti,| ayaṃ yakkho hiṃsati,| ayaṃ yakkho vihiṃsati,| ayaṃ yakkho na muñcatī’ti.|</p><p>“Ayaṃ kho sā, mārisa, āṭānāṭiyā rakkhā| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāya. | handa ca dāni mayaṃ, mārisa, gacchāma| bahukiccā mayaṃ bahukaraṇīyā”ti.| “Yassadāni tumhe mahārājāno kālaṃ maññathā”ti.|</p><p>atha kho cattāro mahārājā uṭṭhāyāsanā bhagavantaṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyiṃsu.| tepi kho yakkhā uṭṭhāyāsanā,| appekacce bhagavantaṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyiṃsu.| appekacce bhagavatā saddhiṃ sammodiṃsu,| sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā tatthevantaradhāyiṃsu.|</p><p>appekacce yena bhagavā tenañjaliṃ paṇāmetvā tatthevantaradhāyiṃsu.| appekacce nāmagottaṃ sāvetvā tatthevantaradhāyiṃsu.|</p>', '-59-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Catutthabhāṇavāro', 'chapter', 60);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 60, '<p>appekacce tuṇhībhūtā tatthevantaradhāyiṃsūti.|</p><p>(D.III.158)</p><p>āṭānāṭiyaṃ paṭhamabhāṇavāro niṭṭhito.</p><p>Tatiyabhāṇavāro niṭṭhito</p><p></p><p></p><p><h1>Catutthabhāṇavāro</h1></p><p>                                                                           2. Āṭānāṭiya suttaṃ </p><p>(The Āṭānāṭiya Discourse)</p><p>atha kho bhagavā tassā rattiyā accayena bhikkhū āmantesi| – “Imaṃ, bhikkhave, rattiṃ cattāro mahārājā| mahatiyā ca yakkhasenāya| mahatiyā ca gandhabbasenāya| mahatiyā ca kumbhaṇḍasenāya| mahatiyā ca nāgasenāya| catuddisaṃ rakkhaṃ ṭhapetvā| catuddisaṃ gumbaṃ ṭhapetvā| catuddisaṃ ovaraṇaṃ ṭhapetvā| abhikkantāya rattiyā abhikkantavaṇṇā| kevalakappaṃ gijjhakūṭaṃ pabbataṃ obhāsetvā| yenāhaṃ tenupasaṅkamiṃsu; upasaṅkamitvā| maṃ abhivādetvā ekamantaṃ nisīdiṃsu.|</p><p>tepi kho, bhikkhave, yakkhā| appekacce maṃ abhivādetvā ekamantaṃ nisīdiṃsu.| appekacce mayā saddhiṃ sammodiṃsu,| sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā ekamantaṃ nisīdiṃsu.|</p><p>appekacce yenāhaṃ tenañjaliṃ paṇāmetvā ekamantaṃ nisīdiṃsu.| appekacce nāmagottaṃ sāvetvā ekamantaṃ nisīdiṃsu.| appekacce tuṇhībhūtā ekamantaṃ nisīdiṃsu.|</p><p>ekamantaṃ nisinno kho bhikkhave vessavaṇo mahārājā maṃ etadavoca:</p><p>“Santi hi, bhante, uḷārā yakkhā bhagavato appasannā.| santi hi, bhante, uḷārā yakkhā bhagavato pasannā.| santi hi, bhante, majjhimā yakkhā bhagavato appasannā.| santi hi, bhante, majjhimā yakkhā bhagavato pasannā.| santi hi, bhante, nīcā yakkhā bhagavato appasannā.| santi hi, bhante, nīcā yakkhā bhagavato pasannā.| yebhuyyena kho pana, bhante, yakkhā appasannāyeva bhagavato.| taṃ kissa hetu? Bhagavā hi, bhante, pāṇātipātā veramaṇiyā dhammaṃ deseti,| adinnādānā veramaṇiyā dhammaṃ deseti,| kāmesumicchācārā veramaṇiyā dhammaṃ deseti,| musāvādā veramaṇiyā dhammaṃ deseti,| surāmerayamajjappamādaṭṭhānā veramaṇiyā dhammaṃ deseti.|</p><p>yebhuyyena kho pana, bhante, yakkhā| appaṭiviratāyeva pāṇātipātā,| appaṭiviratā adinnādānā,| appaṭiviratā kāmesumicchācārā,| appaṭiviratā musāvādā,| appaṭiviratā surāmerayamajjappamādaṭṭhānā.| tesaṃ taṃ hoti appiyaṃ amanāpaṃ.|</p><p>santi hi, bhante, bhagavato sāvakā| araññe vanapatthāni pantāni senāsanāni paṭisevanti| appasaddāni appanigghosāni| vijanavātāni manussarāhasseyyakāni paṭisallānasāruppāni.| tattha santi uḷārā yakkhā nivāsino,| ye imasmiṃ bhagavato pāvacane appasannā.| tesaṃ pasādāya| uggaṇhātu, bhante, bhagavā āṭānāṭiyaṃ rakkhaṃ| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāyā”ti.|</p><p>adhivāsesiṃ kho ahaṃ, bhikkhave, tuṇhībhāvena.| atha kho, bhikkhave, vessavaṇo mahārājā me adhivāsanaṃ viditvā| tāyaṃ velāyaṃ imaṃ āṭānāṭiyaṃ rakkhaṃ abhāsi| –</p><p>“Vipassissa namatthu, cakkhumantassa sirīmato.</p><p>Sikhissapi namatthu, sabbabhūtānukampino.</p><p>“Vessabhussa namatthu, nahātakassa tapassino;</p><p>namatthu kakusandhassa, mārasenāpamaddino.</p><p>“Koṇāgamanassa namatthu, brāhmaṇassa vusīmato;</p><p>kassapassa namatthu, vippamuttassa sabbadhi.</p><p>“Aṅgīrasassa namatthu, sakyaputtassa sirīmato;</p><p>yo imaṃ dhammaṃ desesi, sabbadukkhāpanūdanaṃ.</p><p>“Ye cāpi nibbutā loke, yathābhūtaṃ vipassisuṃ;</p><p>te janā apisuṇātha, mahantā vītasāradā.</p><p>“Hitaṃ devamanussānaṃ, yaṃ namassanti gotamaṃ;</p><p>vijjācaraṇasampannaṃ, mahantaṃ vītasāradaṃ.</p><p>“Yato uggacchati sūriyo, ādicco maṇḍalī mahā.</p><p>Yassa cuggacchamānassa, saṃvarīpi nirujjhati;</p><p>yassa cuggate sūriye, ‘Divaso’ti pavuccati.</p><p>“Rahadopi tattha gambhīro, samuddo saritodako;</p><p>evaṃ taṃ tattha jānanti, ‘Samuddo saritodako’.</p><p>“Ito ‘Sā purimā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p>', '-60-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 61, '<p>“Gandhabbānaṃādhipati, ‘Dhataraṭṭho’ti nāmaso;</p><p>ramatī naccagītehi, gandhabbehi purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asīti dasa eko ca, indanāmā mahabbalā.</p><p>“Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“Jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ’.</p><p>“Yena petā pavuccanti, pisuṇā piṭṭhimaṃsikā.</p><p>Pāṇātipātino luddā, corā nekatikā janā.</p><p>“Ito ‘Sā dakkhiṇā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Kumbhaṇḍānaṃ ādhipati, ‘Virūḷho’ iti nāmaso;</p><p>ramatī naccagītehi, kumbhaṇḍehi purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asītiṃ dasa eko ca, indanāmā mahabbalā.</p><p>Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“Jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ’.</p><p>“Yattha coggacchati sūriyo, ādicco maṇḍalī mahā.</p><p>Yassa coggacchamānassa, divasopi nirujjhati;</p><p>yassa coggate sūriye, ‘Saṃvarī’ti pavuccati.</p><p>“Rahadopi tattha gambhīro, samuddo saritodako;</p><p>evaṃ taṃ tattha jānanti, ‘Samuddo saritodako’.</p><p>“Ito ‘Sā pacchimā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Nāgānañca ādhipati, ‘Virūpakkho’ti nāmaso;</p><p>ramatī naccagītehi, nāgeheva purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p>', '-61-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 62, '<p>asīti dasa eko ca, indanāmā mahabbalā.</p><p>Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“‘jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotamaṃ’.</p><p>“Yena uttarakurū rammā, mahāneru sudassano.</p><p>Manussā tattha jāyanti, amamā apariggahā.</p><p>“Na te bījaṃ pavapanti, napi nīyanti naṅgalā;</p><p>akaṭṭhāpākimaṃ sāliṃ, paribhuñjanti mānusā.</p><p>“Akaṇaṃ athusaṃ suddhaṃ, sugandhaṃ taṇḍulapphalaṃ;</p><p>tuṇḍikīre pacitvāna, tato bhuñjanti bhojanaṃ.</p><p>“Gāviṃ ekakhuraṃ katvā, anuyanti disodisaṃ;</p><p>pasuṃ ekakhuraṃ katvā, anuyanti disodisaṃ.</p><p>“Itthi vāhanaṃ katvā, anuyanti disodisaṃ;</p><p>purisa vāhanaṃ katvā, anuyanti disodisaṃ.</p><p>“Kumāri vāhanaṃ katvā, anuyanti disodisaṃ;</p><p>kumāra vāhanaṃ katvā, anuyanti disodisaṃ.</p><p>“Te yāne abhiruhitvā, sabbā disā anupariyanti; pacārā tassa rājino.</p><p>“Hatthiyānaṃ assayānaṃ, dibbaṃ yānaṃ upaṭṭhitaṃ;</p><p>pāsādā sivikā ceva, mahārājassa yasassino.</p><p>“Tassa ca nagarā ahu, antalikkhe sumāpitā;</p><p>āṭānāṭā kusināṭā parakusināṭā, nāṭapuriyā parakusitanāṭā.</p><p>“Uttarena kasivanto, janoghamaparena ca;</p><p>navanavutiyo ambaraambaravatiyo, āḷakamandā nāma rājadhānī.</p><p>“Kuverassa kho pana, mārisa, mahārājassa visāṇā nāma rājadhānī;</p><p>tasmā kuvero mahārājā, ‘Vessavaṇo’ti pavuccati.</p><p>“Paccesanto pakāsenti, tatolā tattalā tatotalā;</p><p>ojasi tejasi tatojasī, sūro rājā ariṭṭho nemi.</p><p>“Rahadopi tattha dharaṇī nāma, yato meghā pavassanti;</p><p>vassā yato patāyanti, sabhāpi tattha bhagalavatī nāma.</p><p>“Yattha yakkhā payirupāsanti, tattha niccaphalā rukkhā;</p><p>nānā dijagaṇā yutā, mayūrakoñcābhirudā; kokilādīhi vagguhi.</p><p>“Jīva jīvakasaddettha, atho oṭṭhavacittakā;</p>', '-62-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 63, '<p>kuktthakā kuḷīrakā, vane pokkharasātakā.</p><p>“Sukasāḷikasaddettha, daṇḍamāṇavakāni ca;</p><p>sobhati sabbakālaṃ sā, kuveranalinī sadā.</p><p>“Ito ‘Sā uttarā disā’, iti naṃ ācikkhatī jano;</p><p>yaṃ disaṃ abhipāleti, mahārājā yasassi so.</p><p>“Yakkhānañca ādhipati, ‘Kuvero’ iti nāmaso;</p><p>ramatī naccagītehi, yakkheheva purakkhato.</p><p>“Puttāpi tassa bahavo, ekanāmāti me sutaṃ;</p><p>asīti dasa eko ca, indanāmā mahabbalā.</p><p>“Te cāpi buddhaṃ disvāna, buddhaṃ ādiccabandhunaṃ;</p><p>dūratova namassanti, mahantaṃ vītasāradaṃ.</p><p>“Namo te purisājañña, namo te purisuttama;</p><p>kusalena samekkhasi, amanussāpi taṃ vandanti;</p><p>sutaṃ netaṃ abhiṇhaso, tasmā evaṃ vademase.</p><p>“‘jinaṃ vandatha gotamaṃ, jinaṃ vandāma gotamaṃ;</p><p>vijjācaraṇasampannaṃ, buddhaṃ vandāma gotama’”nti.</p><p>“Ayaṃ kho sā, mārisa, āṭānāṭiyā rakkhā| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāya.|</p><p>“Yassa kassaci, mārisa, bhikkhussa vā bhikkhuniyā vā| upāsakassa vā upāsikāya vā| ayaṃ āṭānāṭiyā rakkhā suggahitā bhavissati samattā pariyāputā.|</p><p>taṃ ce amanusso| yakkho vā yakkhinī vā| yakkhapotako vā yakkhapotikā vā| yakkhamahāmatto vā yakkhapārisajjo vā yakkhapacāro vā,|</p><p>gandhabbo vā gandhabbī vā| gandhabbapotako vā gandhabbapotikā vā| gandhabbamahāmatto vā gandhabbapārisajjo vā gandhabbapacāro vā,| kumbhaṇḍo vā kumbhaṇḍī vā| kumbhaṇḍapotako vā kumbhaṇḍapotikā vā| kumbhaṇḍamahāmatto vā kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā,|</p><p>nāgo vā nāginī vā| nāgapotako vā nāgapotikā vā| nāgamahāmatto vā nāgapārisajjo vā nāgapacāro vā,|</p><p>paduṭṭhacitto,|</p><p>bhikkhuṃ vā bhikkhuniṃ vā| upāsakaṃ vā upāsikaṃ vā| gacchantaṃ vā anugaccheyya,| ṭhitaṃ vā upatiṭṭheyya,| nisinnaṃ vā upanisīdeyya,| nipannaṃ vā upanipajjeyya.|</p><p>na me so, mārisa, amanusso labheyya| gāmesu vā nigamesu vā sakkāraṃ vā garukāraṃ vā.|</p><p>na me so, mārisa, amanusso labheyya| āḷakamandāya nāma rājadhāniyā vatthuṃ vā vāsaṃ vā.|</p><p>na me so, mārisa, amanusso labheyya| yakkhānaṃ samitiṃ gantuṃ.|</p><p>apissu naṃ, mārisa, amanussā| anāvayhampi naṃ kareyyuṃ avivayhaṃ.|</p><p>apissu naṃ, mārisa, amanussā| attāhipi paripuṇṇāhi paribhāsāhi paribhāseyyuṃ.| apissu naṃ, mārisa, amanussā| rittaṃpi pattaṃ sīse nikkujjeyyuṃ.|</p><p>apissu naṃ, mārisa, amanussā| sattadhāpissa muddhaṃ phāleyyuṃ.|</p><p>“Santi hi, mārisa, amanussā caṇḍā ruddhā rabhasā,| te neva mahārājānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, amanussā| mahārājānaṃ avaruddhā nāma vuccanti.|</p><p>seyyathāpi, mārisa, rañño māgadhassa vijite mahācorā.| te neva rañño māgadhassa ādiyanti,| na rañño māgadhassa purisakānaṃ ādiyanti,| na rañño māgadhassa purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, mahācorā| rañño māgadhassa avaruddhā nāma vuccanti.|</p><p>evameva kho, mārisa, santi amanussā| caṇḍā ruddhā rabhasā,| te neva mahārājānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ ādiyanti,| na mahārājānaṃ purisakānaṃ purisakānaṃ ādiyanti.| te kho te, mārisa, amanussā| mahārājānaṃ avaruddhā nāma vuccanti.|</p><p>yo hi koci, mārisa, amanusso| yakkho vā yakkhinī vā| yakkhapotako vā yakkhapotikā vā| yakkhamahāmatto vā yakkhapārisajjo vā yakkhapacāro vā,|</p><p>gandhabbo vā gandhabbī vā| gandhabbapotako vā gandhabbapotikā vā| gandhabbamahāmatto vā gandhabbapārisajjo vā gandhabbapacāro vā,| kumbhaṇḍo vā kumbhaṇḍī vā| kumbhaṇḍapotako vā kumbhaṇḍapotikā vā| kumbhaṇḍamahāmatto vā kumbhaṇḍapārisajjo vā kumbhaṇḍapacāro vā,|</p><p>nāgo vā nāginī vā| nāgapotako vā nāgapotikā vā nāgamahāmatto vā nāgapārisajjo vā nāgapacāro vā,|</p><p>paduṭṭhacitto bhikkhuṃ vā bhikkhuniṃ vā| upāsakaṃ vā upāsikaṃ vā| gacchantaṃ vā anugaccheyya,| ṭhitaṃ vā upatiṭṭheyya,| nisinnaṃ vā upanisīdeyya,| nipannaṃ vā upanipajjeyya.|</p>', '-63-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', 'Blessing', 'chapter', 64);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '1. Jayamaṅgala aṭṭha gāthā', 'title', 64);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 64, '<p>imesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ| ujjhāpetabbaṃ vikkanditabbaṃ viravitabbaṃ –|</p><p>‘Ayaṃ yakkho gaṇhāti, ayaṃ yakkho āvisati,| ayaṃ yakkho heṭheti, ayaṃ yakkho viheṭheti,| ayaṃ yakkho hiṃsati, ayaṃ yakkho vihiṃsati,| ayaṃ yakkho na muñcatī’ti.|</p><p>“Katamesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ?|</p><p>“Indo somo varuṇo ca, bhāradvājo pajāpati;</p><p>candano kāmaseṭṭho ca, kinnighaṇḍu nighaṇḍu ca.</p><p>“Panādo opamañño ca, devasūto ca mātali;</p><p>cittaseno ca gandhabbo, naḷo rājā janesabho.</p><p>“Sātāgiro hemavato, puṇṇako karatiyo guḷo;</p><p>sivako mucalindo ca, vessāmitto yugandharo.</p><p>“Gopālo supparodho ca, hiri netti ca mandiyo;</p><p>pañcālacaṇḍo āḷavako, pajjanno sumano sumukho;</p><p>dadhimukho maṇi mānīvaro dīgho, atho serissako saha.</p><p>“Imesaṃ yakkhānaṃ mahāyakkhānaṃ| senāpatīnaṃ mahāsenāpatīnaṃ| ujjhāpetabbaṃ vikkanditabbaṃ viravitabbaṃ – |</p><p>‘Ayaṃ yakkho gaṇhāti, ayaṃ yakkho āvisati,| ayaṃ yakkho heṭheti, ayaṃ yakkho viheṭheti,| ayaṃ yakkho hiṃsati, ayaṃ yakkho vihiṃsati,| ayaṃ yakkho na muñcatī’ti.|</p><p>“Ayaṃ kho sā, mārisa, āṭānāṭiyā rakkhā| bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāya.| handa ca dāni mayaṃ, mārisa, gacchāma| bahukiccā mayaṃ bahukaraṇīyā”ti.| “Yassadāni tumhe mahārājāno kālaṃ maññathā”ti.|</p><p>“Atha kho, bhikkhave, cattāro mahārājā uṭṭhāyāsanā| maṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyiṃsu.|</p><p>tepi kho, bhikkhave, yakkhā uṭṭhāyāsanā| appekacce maṃ abhivādetvā padakkhiṇaṃ katvā tatthevantaradhāyiṃsu.|</p><p>appekacce mayā saddhiṃ sammodiṃsu,| sammodanīyaṃ kathaṃ sāraṇīyaṃ vītisāretvā tatthevantaradhāyiṃsu.|</p><p>appekacce yenāhaṃ tenañjaliṃ paṇāmetvā tatthevantaradhāyiṃsu.| appekacce nāmagottaṃ sāvetvā tatthevantaradhāyiṃsu.|</p><p>appekacce tuṇhībhūtā tatthevantaradhāyiṃsu.|</p><p>“Uggaṇhātha, bhikkhave, āṭānāṭiyaṃ rakkhaṃ.| pariyāpuṇātha, bhikkhave, āṭānāṭiyaṃ rakkhaṃ.| dhāretha, bhikkhave, āṭānāṭiyaṃ rakkhaṃ.| atthasaṃhitā, bhikkhave, āṭānāṭiyā rakkhā bhikkhūnaṃ bhikkhunīnaṃ| upāsakānaṃ upāsikānaṃ| guttiyā rakkhāya avihiṃsāya phāsuvihārāyā”ti.| idamavoca bhagavā. Attamanā te bhikkhū bhagavato bhāsitaṃ abhinandunti.|</p><p>āṭānāṭiyasuttaṃ niṭṭhitaṃ.</p><p>Catutthabhāṇavāro niṭṭhito</p><p></p><p></p><p> </p><p><h1>Blessing</h1></p><p>                                                                              </p><p><h1>1. Jayamaṅgala aṭṭha gāthā</h1></p><p>bāhuṃ sahassamabhinimmitasāyudhaṃ taṃ -</p><p>girimekhalaṃ uditaghorasasenamāraṃ</p><p>dānādi dhammavidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Mārātireka mabhiyujjhitasabbarattiṃ - ghorampanālavakamakkhamathaddhayakkhaṃ</p><p>khantī sudantavidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p>', '-64-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '2. Jinapañjaraya', 'title', 65);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 65, '<p>Nālāgiriṃ gajavaraṃ atimattabhūtaṃ -</p><p>dāvaggicakkamasanīva sudāruṇaṃ taṃ</p><p>mettambusekavidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Ukkhittakhaggamatihatthasudāruṇaṃ taṃ - dhāvaṃtiyojanapathaṅgulimālavan taṃ</p><p>iddhībhisaṅkhatamano jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Katvāna kaṭṭhamudaraṃ iva gabbhinīyā -</p><p>ciñcāya duṭṭhavacanaṃ janakāyamajjhe</p><p>santena soma vidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Saccaṃ vihāya matisaccakavādaketuṃ -</p><p>vādābhiropitamanaṃ atiandhabhūtaṃ</p><p>paññāpadīpa jalito jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Nandopanandabhujagaṃ vibudhaṃ mahiddhiṃ -</p><p>puttena therabhujagena damāpayanto</p><p>iddhūpadesavidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Duggāha diṭṭhi bhujagena sudaṭṭha hatthaṃ -</p><p>brahmaṃ visuddhi jutimiddhi bakābhidhānaṃ</p><p>ñāṇāgadena vidhinā jitavā munindo -</p><p>taṃ tejasā bhavatu (te/me) jayamaṅgalānī.</p><p>Etāpi buddha jayamaṅgala aṭṭha gāthā -</p><p>yo vācako dinadine sarate matandī</p><p>hitvāna neka vividhāni-cupaddavāni -</p><p>mokkhaṃ sukhaṃ adhigameyya naro sapañño.</p><p>                                                                            </p><p><h1>2. Jinapañjaraya</h1></p><p>jayāsanagatā vīrā - jetvā māraṃ savāhiṇiṃ</p><p>catusaccā matarasaṃ - ye piviṃsu narā sabhā</p><p>taṇhaṅkarādayo buddhā - aṭṭha vīsati nāyakā</p><p>sabbe patiṭṭhitā(tuyhaṃ/mayhaṃ) - matthake te munissarā</p><p>sire patiṭṭhitā buddhā - dhammo ca (tava) locane</p><p>saṅgho patiṭṭhito (tuyhaṃ/mayhaṃ) - ure sabba guṇākaro</p><p>hadaye anuruddho ca - sāriputto ca dakkhiṇe</p>', '-65-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 66, '<p>koṇḍañño piṭṭhibhāgasmiṃ - moggallānosi vāmake</p><p>dakkhiṇe savane (tuyhaṃ/mayhaṃ) - āhuṃ ānanda rāhulā</p><p>kassapo ca mahānāmo - ubhosuṃ vāmasotake</p><p>kesante piṭṭhibhāgasmiṃ - suriyo viya pahaṅkaro</p><p>nisinno sirisampanno - sobhito munipuṅgavo</p><p>kumārakassapo nāma - mahesī citravādako</p><p>so (tuyhaṃ/mayhaṃ) vadane niccaṃ - patiṭṭhāsi guṇākaro</p><p>puṇṇo aṅgulimālo ca - upālī nanda sīvalī</p><p>therā pañca ime jātā - lalāṭe tilakā (tava/mama)</p><p>sesāsīti mahātherā - vijitā jinasāvakā</p><p>jalantā sīlatejena – aṅgamaṅgesu saṇṭhitā</p><p>ratanaṃ purato āsi - dakkhiṇe mettasuttakaṃ</p><p>dhajaggaṃ pacchato āsi - vāme aṅgulimālakaṃ</p><p>bandha mora parittañca - āṭānāṭiya suttakaṃ</p><p>ākāsacchadanaṃ āsi - sesā pākāra saññitā</p><p>jināṇābalasaṃyutte - dhamma pākāralaṅkate</p><p>vasato (te/me) catukiccena - sadā sambuddha pañjare</p><p>vātapittādisañjātā - bāhirajjhantupaddavā</p><p>asesā vilayaṃ yantu - ananta guṇa tejasā</p><p>jinapañjara majjhaṭṭhaṃ - viharantaṃ mahītale</p><p>sadā pāletu (tvaṃ/maṃ) sabbe - te mahā purisā sabhā</p><p>iccevamaccantakato surakkho - jinānubhāvena jitūpapaddavo</p><p>buddhānubhāvena hatārisaṅgho - (carāhi/carāmi) saddhammanubhāvapālito</p><p>iccevamaccantakato surakkho - jinānubhāvena jitūpapaddavo</p><p>dhammānubhāvena hatāri saṅgho - (carāhi/carāmi) saddhammanubhāvapālito</p><p>iccevamaccantakato surakkho - jinānubhāvena jitūpapaddavo</p><p>saṅghānubhāvena hatāri saṅgho - (carāhi/carāmi) saddhammanubhāvapālito</p><p>saddhammapākāra parikkhitosi/parikkhitosmiṃ - aṭṭhāriyā aṭṭhadisāsu honti</p><p>etthantare aṭṭhanāthā bhavanti - uddhaṃ vitānaṃva jinā ṭhitā(te/me)</p><p>bhindanto mārasenaṃ tava (tava/mama) sirasi ṭhito bodhimāruyha satthā</p><p>moggallānosi vāme vasati bhujataṭe dakkhiṇe sāriputto</p><p>dhammo majjhe urasmiṃ viharati bhavato mokkhato morayoniṃ,</p><p>sampatto bodhisatto caraṇayugagato bhāṇu lokekanātho</p><p>sabbāvamaṅgalamupaddavadunnimittaṃ - sabbītirogagahadosamasesanindā</p><p>sabbantarāyabhayadussupinaṃ akantaṃ - buddhānubhāvapavarena payātu nāsaṃ</p><p>sabbāva maṅgalamupaddava dunnimittaṃ - sabbīti roga gahadosamasesa nindā</p>', '-66-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '3. Aṭṭhavīsati Buddhā', 'title', 67);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '4. Bojjhaṅgapirita', 'title', 67);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 67, '<p>sabbantarāya bhaya dussupinaṃ akantaṃ - dhammānubhāvapavarena payātu nāsaṃ</p><p>sabbāva maṅgalamupaddava dunnimittaṃ - sabbīti roga gahadosamasesa nindā</p><p>sabbantarāya bhaya dussupinaṃ akantaṃ - saṅghānubhāvapavarena payātu nāsaṃ.</p><p> </p><p>                                                                       </p><p><h1>3. Aṭṭhavīsati Buddhā</h1></p><p>taṇhaṅkaro mahāvīro - medhaṅkaro mahāyaso</p><p>saraṇaṅkaro lokahito - dīpaṅkaro jutindharo</p><p>koṇḍañño janapāmokkho - maṅagalo purisāsabho</p><p>sumano sumano dhīro - revato rativaddhano</p><p>sobhito guṇasampanno - anomadassī januttamo</p><p>padumo loka pajjoto - nārado vara sārathī</p><p>padumuttaro sattasāro - sumedho aggapuggalo</p><p>sujāto sabbalokaggo - piyadassī narāsabho</p><p>atthadassī kāruṇiko - dhammadassī tamonudo</p><p>siddhattho asamo loke - tisso varada saṃvaro</p><p>phusso varada sambuddho - vipassī ca anūpamo</p><p>sikhī sabba hito sattā - vessabhū sukhadāyako</p><p>kakusandho satthavāho - koṇāgamano raṇañjaho</p><p>kassapo sirisampanno - gotamo sakya puṅgavo</p><p>tesaṃ saccena sīlena - khanti metta balena ca</p><p>tepi(tvaṃ/maṃ) anurakkhantu - ārogyena sukhena cāti.</p><p></p><p></p><p>                                                                              </p><p><h1>4. Bojjhaṅgapirita</h1></p><p>saṃsāre saṃsarantānaṃ - sabba dukkha vināsake</p><p>satta dhammeva bojjhaṅage - mārasenappamaddano</p><p>bujjhitvā ye pi me sattā - tibhavā muttā hi uttamā</p><p>ajātiṃ ajarākhyādhiṃ - amataṃ nibbhayaṃ gatā</p><p>evamādī guṇopetaṃ - aneka guṇa saṅgahaṃ</p><p>osadhaṃ va imaṃ mantaṃ - bojjhaṅagaṃ taṃ bhaṇāmahe</p><p>bojjhaṅago sati saṅkhāto - dhammānaṃ vicayo tathā</p><p>viriyaṃ pīti passaddhi - bojjhaṅagā ca tato pare</p><p>samādhupekkhā bojjhaṅagā - satte te sabbadassinā,</p><p>muninā sammadakkhātā - bhāvitā bahulīkatā</p>', '-67-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '5. Mahā Jayamaṅgala Gāthā', 'title', 68);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 68, '<p>saṃvattanti abhiññāya - nibbānāya ca bodhiyā,</p><p>etena sacca vajjena - sotthi (te/me) hotu sabbadā</p><p>ekasmiṃ samaye nātho - moggallānañca kassapaṃ,</p><p>gilāne dukkhite disvā - bojjhaṅage satta desayi</p><p>te ca taṃ abhinanditvā - rogā mucciṃsu taṃ khaṇe,</p><p>etena sacca vajjena - sotthi te (/me) hotu sabbadā</p><p>ekadā dhammarājāpi - geḷaññenā’bhipīḷito,</p><p>cundattherena taṃ yeva - bhāṇāpetvāna sādaraṃ</p><p>sammoditvā ca ābādhā - tamhā vuṭṭhāsi ṭhānāso,</p><p>etena sacca vajjena - sotthi te (/me) hotu sabbadā</p><p>pahīṇāte ca ābādhā - tiṇṇanampi mahesinaṃ</p><p>maggāhata kilesāva - pattā’nupatti dhammataṃ</p><p>etena sacca vajjena - sotthi te (/me) hotu sabbadā.</p><p>                                                                             </p><p><h1>5. Mahā Jayamaṅgala Gāthā</h1></p><p>mahākāruṇiko nātho - hitāya sabbapāṇinaṃ</p><p>pūretvā pāramī sabbā - patto sambodhimuttamaṃ</p><p>etena sacca vajjena - hotu (te/me) jayamaṅgalaṃ</p><p>jayanto bodhiyā mūle - sakyānaṃ nandi vaddhano</p><p>evaṃ tuyhaṃ(/mayhaṃ) jayo hotu - jayassu jayamaṅgalaṃ</p><p>sakkatvā buddharatanaṃ - osadhaṃ uttamaṃ varaṃ</p><p>hitaṃ devamanussānaṃ - buddha tejena sotthinā</p><p>nassantu’paddavā sabbe - dukkhā vūpasamentu (te/me)</p><p>sakkatvā dhammaratanaṃ - osadhaṃ uttamaṃ varaṃ</p><p>pariḷāhūpasamanaṃ - dhammatejena sotthinā</p><p>nassantu’paddavā sabbe - bhayā vūpasamentu (te/me)</p><p>sakkatvā saṅgharatanaṃ - osadhaṃ uttamaṃ varaṃ</p><p>āhuneyyaṃ pāhuneyyaṃ - saṅgha tejena sotthinā</p><p>nassantu’paddavā sabbe - rogā vūpasamentu (te/me)</p><p>yaṃ kiñci ratanaṃ loke - vijjati vividhā puthū</p><p>ratanaṃ buddhasamaṃ natthi - tasmā sotthi bhavantu (te/me)</p><p>yaṃ kiñci ratanaṃ loke - vijjati vividhā puthū</p><p>ratanaṃ dhammasamaṃ natthi - tasmā sotthi bhavantu (te/me)</p><p>yaṃ kiñci ratanaṃ loke - vijjati vividhā puthū</p><p>ratanaṃ saṅghasamaṃ natthi - tasmā sotthi bhavantu (te/me)</p><p>natthi me saraṇaṃ aññaṃ - buddho me saraṇaṃ varaṃ</p>', '-68-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '1. Jaya Paritta', 'title', 69);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 69, '<p>etena sacca vajjena - hotu (te/me) jayamaṅgalaṃ</p><p>natthi me saraṇaṃ aññaṃ - dhammo me saraṇaṃ varaṃ</p><p>etena sacca vajjena - hotu (te/me) jayamaṅgalaṃ</p><p>natthi me saraṇaṃ aññaṃ - saṅgho me saraṇaṃ varaṃ</p><p>etena sacca vajjena - hotu (te/me) jayamaṅgalaṃ</p><p>sabbītiyo vivajjantu - sabbarogo vinassatu</p><p>mā (te/me) bhavatvantarāyo - sukhī dīghāyuko bhava (/bhave)</p><p>bhavatu sabbamaṅgalaṃ - rakkhantu sabba devatā</p><p>sabba buddhānubhāvena - sadā sotthi bhavantu (te/me)</p><p>bhavatu sabbamaṅgalaṃ - rakkhantu sabba devatā</p><p>sabba dhammānubhāvena - sadā sotthi bhavantu (te/me)</p><p>bhavatu sabbamaṅgalaṃ - rakkhantu sabba devatā</p><p>sabba saṅghānubhāvena - sadā sotthi bhavantu (te/me)</p><p>nakkhatta yakkha bhūtānaṃ - pāpaggahanivāraṇā</p><p>parittassānu’bhāvena - hantu tuyhaṃ /mayhaṃ upaddave</p><p>yaṃ dunimittaṃ avamaṅgalañca - yo cā’ manāpo sakuṇassa saddo</p><p>pāpaggaho dussupinaṃ akantaṃ - buddhā’nubhāvena vināsamentu</p><p>yaṃ dunimittaṃ avamaṅgalañca - yo cā’ manāpo sakuṇassa saddo</p><p>pāpaggaho dussupinaṃ akantaṃ - dhammā’nubhāvena vināsamentu</p><p>yaṃ dunimittaṃ avamaṅgalañca - yo cā’ manāpo sakuṇassa saddo</p><p>pāpaggaho dussupinaṃ akantaṃ - saṅghā’nubhāvena vināsamentu</p><p></p><p></p><p>Appendix</p><p>Paritta Chanting for occassion</p><p>                                                                              </p><p><h1>1. Jaya Paritta</h1></p><p>siridhitimati tejo jayasiddhi mahiddhi mahāguṇaṃ aparimita puññādhikārassa sabbantarāya nivāraṇasamatthassa bhagavato arahato sammā sambuddhassa-dvattiṃsa mahāpurisa lakkhaṇānubhāvena, asītyānubyañjana lakkhaṇānubhāvena, aṭṭhuttarasata maṅgala lakkhaṇānubhāvena, chabbaṇṇaraṃsyānubhāvena, ketumālānubhāvena, dasa pāramitānubhāvena, dasa upapāramitānubhāvena, dasa paramattha pāramitānubhāvena, sīlasamādhi paññānubhāvena, buddhānubhāvena, dhammānubhāvena, saṅghānubhāvena, tejānubhāvena, iddhyānubhāvena, balānubhāvena, ñeyyadhammānubhāvena, caturāsītisahassa dhammakkhandhānubhāvena, navalokuttara dhammānubhāvena, aṭṭhaṅgika-maggānubhāvena, aṭṭhasamāpatyānubhāvena. Chaḷabhiññānubhāvena, mettā-karuṇā-muditā-upekkhānubhāvena, sabba pāramitānubhāvena ratanattaya saraṇānubhāvena tuyhaṃ(/mayhaṃ)sabba roga soka upaddava dukkhadomanassupāyāsā vinassantu, sabba saṅkappā tuyhaṃ(/mayhaṃ) samijjhantu, dīghāyuko hotu sata vassa jīvena samaṅgiko hotu sabbadā.</p><p>Ākāsa pabbata vana bhūmi taṭāka gaṅgā mahāsamudda ārakkha devatā sadā tumhe(/amhe) anurakkhantu. Sabba buddhānubhāvena, sabba dhammānubhāvena, sabba saṅghānubhāvena, buddharatanaṃ, dhammaratanaṃ, saṅgharatanaṃ, tinnaṃ ratanānaṃ ānubhāvena, caturāsītisahassa dhammakkhandhānubhāvena, piṭakattayānubhāvena, jinasāvakānubhāvena, sabbe te(/me) rogā, sabbe te(/me) bhayā, sabbe te(/me) antarāyā, sabbe te(/me) upaddavā sabbe te(/me) dunnimittā sabbe te(/me) avamaṅgalā vinassantu:</p><p>āyuvaḍḍhako, dhanavaḍḍhako, sirivaḍḍhako, yasavaḍḍhako, balavaḍḍhako, vaṇṇavaḍḍhako, sukhavaḍḍhako hotu sabbadā,</p><p>dukkha roga bhayā verā - sokā sabbe paddavā</p><p>anekā antarāyā pi - vinassantu ca tejasā</p><p>jaya siddhi dhanaṃ lābhaṃ - sotthi bhāgyaṃ sukhaṃ balaṃ</p><p>siri āyu ca vaṇṇo ca - bhogaṃ vuddhi ca yasavā</p><p>satvassā ca āyū ca - jīvasiddhi bhavantu te(/me).</p><p>                                                                             </p>', '-69-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '2. Rāhulovāda gāthā', 'title', 70);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '03. Parittaānāpanaṃ ', 'title', 70);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 70, '<p><h1>2. Rāhulovāda gāthā</h1></p><p>sabbadā sīlasampanno        paññavā susamāhito,</p><p>āraddhaviriyo pahitatto        oghaṃ tarati duttaraṃ.</p><p>Kāyakammaṃ tidhā vuttaṃ        vācākammaṃ catubbidhaṃ,</p><p>manasā tividhaṃ ceti        dasakammapathā ime.</p><p>pāṇaghātaparaddabbaṃ        paradārañca kāyato,</p><p>musāpesuññapharusā        samphappalāpā vācato,</p><p>abhijjhā ceva byāpādo        micchādiṭṭhī ca mānaso.</p><p>Vattaṃ na paripūrento        na sīlaṃ paripuratī,</p><p>asuddhasīlo duppañño        dukkhā na parimuñcati.</p><p>Yo gavaṃ na vijānāti        na so rakkhati gogaṇaṃ, evaṃ sīlaṃ ajānanto kiṃ so rakkheyya saṃvaraṃ.</p><p>Kikī’va aṇḍaṃ camarī’va vāladhiṃ piya’va puttaṃ nayanaṃ’va ekakaṃ,</p><p>tatheva sīlaṃ anurakkhamānasā        supesalā hotha sadā sagāravā.</p><p>Suladdhā vata me laddhā        saddhā sugatasāsane,</p><p>sīlaṃ me yassa kalyāṇaṃ        parisuddhama’khaṇḍitaṃ.</p><p>Pātimokkhaṃ visodhento appeva jīvitaṃ jahe,</p><p>paññattaṃ lokanāthena        na bhinde sīlasaṃvaraṃ.</p><p>Imamhi sīlaratanaṃ        idhace’va parattha ca,</p><p>ānisaṃsavare datvā        pacchā pāpeti nibbutiṃ.</p><p>Paṃcakāmaguṇe hitvā        piyarūpe manorame, saddhāya gharā nikkhamma        dukkhassa’ntakaro bhava.</p><p>Mitte bhajassu kalyāṇe        panthaṃca sayanāsanaṃ,</p><p>vivittaṃ appanigghosaṃ        mattaññū hohi bhojane.</p><p> cīvare piṇḍapāte ca        paccaye sayanāsane, etesu taṇhamākāsimā lokaṃ punarā’gami.</p><p>Saṃvuto pātimokkhasmiṃ        indriyesu ca pañcasu, </p><p>sati kāyagatātyatthu        nibbidā bahulo bhava.</p><p>Nimittaṃ parivajjehi        subhaṃ rāgūpanissitaṃ,</p><p>asubhāya cittaṃ bhāvehi        ekaggaṃ susamāhitaṃ.</p><p>Animittaṃ ca bhāvehi        mānānusayamujjaha,</p><p>tato mānābhisamayā        upasanto carissasi.</p><p>Appamattā satīmantā        susīlā hotha bhikkhavo,</p><p>susamāhita saṅkappā        sacittamanurakkhatha.</p><p>Sādhū! sādhū! sādhū!</p><p><h1>03. Parittaānāpanaṃ</h1> </p><p>1. Ye santā santacittā nisaraṇasaraṇā ettha lokantare vā, bhummā bhūmmā ca devā guṇagaṇagahaṇabyāvaṭā sabbakālaṃ, ete āyantu devā varakanakamaye merurāje vasanto santo santo sahetuṃ munivaravacanaṃ sotumaggaṃ samaggaṃ,</p><p>2. Sabbesu cakkavāḷesu, yakkhā devā ca brahmuṇo, yaṃ amhehi kataṃ puññaṃ, sabbasampattisādhakaṃ, sabbe taṃ anumoditvā, samaggā sāsane ratā, pamādarahitā hontu, ārakkhāsu visesato.</p><p>3. Sāsanassa ca lokassa, vuddhi bhavatu sabbadā, sāsanampi ca lokañca, devā rakkhantu sababadā, saddhiṃ hontu subī sabbe, parivārehi attano, anīghā sumanā hontu, saha sabbehi ñātiṅabhi.</p>', '-70-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '04. Aṅgulimālasuttaṃ', 'title', 71);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '05. Vaṭṭaka paritta', 'title', 71);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '06. Paritta for Raining', 'title', 71);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_iit_chants', '07. Narasīhagāthā', 'title', 71);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 71, '<p>Rājatoo vā corato vā manussato vā amanussato vā aggito vā udakato vā pisācato vā bāṇukato vā kaṇṭakato vā nakkhattato vā janapadarogato vā asaddhammato vā asandiṭṭhito vā asappurisato vā caṇḍahatthīiassamigagoṇakukkura ahivicchikamaṇisappadīpi acchataracchasūkaramahisa-yakkharakbakhasādīhi nānābhayato vā nānā rogato vā nānāupaddavato vā ārakkhaṃ gaṇhantu. Panidhānato paṭṭhāya tathāgatassa dasapāramiyo dasaupapāramiyo dasaparamatthapāramiyo pañcamahāpariccāge tisso cariyā pacchimahave gabbhāvakkantiṃ jātiṃ abhinikkhamaṇaṃ padhānacariyaṃ bodhipallaඹaṅke māravijayaṃ sabbaññutañāṇapaṭivedhaṃ navalokuttaradhammeti sabbepime buddhaguṇe ācapajjitvā vesāliyaṃ tīsu pākārantaresu tiyāmarattiṃ parittaṃ karonto āyasmā</p><p>ānandatthero viya kāruññacittaṃ upaṭṭhapetvā. </p><p><h1>04. Aṅgulimālasuttaṃ</h1></p><p>Parittaṃ yaṃ bhaṇantassa - nisinnaṭṭhānadhovanaṃ, udakampi vināseti - sabbameva parissayaṃ, sotthinā gabbhavuṭṭhānaṃ - yaṃ ca sādheti taṃ baṇe, theressaṅgulimālassa - lokanāthena bhāsitaṃ, kappaṭṭhāyimahātejaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Yatohaṃ bhagini ariyāya jātiyā jāto nṛbhijānāmi sañcicca pāṇaṃ jivitā voropetā, tena saccena sotthi te hotu sotthi gabbhassāti.</p><p><h1>05. Vaṭṭaka paritta</h1></p><p>                                                                              1. pūrentaṃ bodhisambhāre - nibbattaṃ vaṭṭakajātiya,</p><p>yassa tejena dāvaggi - mahā sattaṃ vivajjayi.</p><p>                                                                              2. Therassa sāriputtaksa - lokanāthena bhāsitaṃ,</p><p>kappaṭṭhāyi mahātejaṃ - parintaṃ taṃ bhaṇāmahabhe.</p><p>                                                                              3. Atthi loke sīlaguṇo - saccaṃ soceyyanuddayā,</p><p>tena saccena kāhāmi - saccakiriyamanuttaraṃ.</p><p>                                                                              4. Āvajjitvā dhammabalaṃ - saritvā pubbake jine,</p><p>saccabalamapassāya - saccakiriyaṃ akāsahaṃ.</p><p>                                                                              5. Santi pakkhā apatanā - santi pādā avañcanā,</p><p>mātā pitā ca nikkhantā - jātaveda paṭikkama.</p><p>                                                                              6. Sahasaccakate mayhaṃ - mahāpajjalito sibī,</p><p>vajjesi soḷasa karsāni - udakaṃ patvā yathā sibī.</p><p>Saccena me samo natthi - esā me saccapāramiti,</p><p>etena saccavajjena - sotthi te hotu sabbadā.</p><p>        </p><p><h1>06. Paritta for Raining</h1></p><p>                                                                              1. Subhūto ca mahā thero - mahā kāyo mahodaro, nīlavaṇṇo mahā tejo - pavassantu valāhakā.</p><p>                                                                              2. Abhitthanaya pajjunna - nidhiṃ kākassa nāsaya, kākaṃ sokāya randhehi - maṃ ca sokā pamocaya, etena sacca vajjena - sammā devā pavassatu.</p><p><h1>07. Narasīhagāthā</h1></p><p>                                                                              1. Cakkavaraṅkita rattasupādo lakkhaṇa maṇḍita āyata paṇhī,</p><p>cāmara chatta vibhūsita pādo esa hi tuyga pitā narasīho.</p><p>                                                                              1. Sakya kumāra varo sukumālo lakkhaṇa cittita puṇṇa sarro,</p><p>lokahitāya gato naravīro esa hi tuyha pitā narasīho.</p><p>                                                                              2. Puṇṇa sasaṅkha niho mukha vaṇṇo devanarāna piyo nara nāgo,</p><p>matta gajinda vilāsita gāmī esa hi tuyha pitā narasīho.</p><p>                                                                              3. Battiya sambhava agga kulīno deva manussa namassita pādo,</p><p>sīla samādhi patiṭṭhita citto esa hi tuyha pitā narasīho.</p><p>                                                                              4. Āyata yutta susaṇṭhita nāso gopabūmo abhinīla sunetto,</p><p>inda dhanū abhinīla bhamūko esa hi tuyha pitā narasīho.</p><p>                                                                              5. Vaṭṭa sumaṭṭa susaṇṭhita gīvo sīhahanū miga rāja sarro,</p>', '-71-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 72, '<p>kañcana succhavi uttama vaṇṇo esa hi tuyha pitā narasīho.</p><p>                                                                              6. Siniddha sugambhira mañjusu ghoso hiṅgula bandhu suratta sujivho;</p><p>vīsati vīsati seta sudanto esa hi tuygaha pitā narasīho.</p><p>                                                                              7. Añjana vaṇṇa sunīla sukeso kañcana paṭṭa visuddha lalāṭo, osadhi paṇḍara suddhasu uṇṇo esa hi tuyha pitā narasīho.</p><p>                                                                              8.  gacchati nīlapathe viya cando tāragaṇā parivedhīita rūpo, sāvaka majkdhagato samaṇindo esa hi tuyha pitā narasīho.</p><p></p><p></p><p>08. Buddhist Era</p><p>amhākaṃ kho pana bhagavā dīpaṅakara pāda mūlato paṭaṭhāya, paṭhamaṃ dāna pāramī dutiyaṃ sīla pāramī tatiyaṃ nekkhamma pāramī catutthaṃ paññā pāramī pañcamaṃ viriya pāramī chaṭṭhamaṃ khanti pāramī sattamaṃ sacca pāramī aṭṭhamaṃ adhiṭṭhāna pāramī navamaṃ mettā pāramī dasamaṃ upekkhā pāramī. Dasa pāramiyo dasa upa pāramiyo dasa paramatta pāramiyoti, samatiṃsappāramiyo pūretvā, vessantara attabhāve nibbattitvā pañca mahā pariccāge katvā tusitapure nibbattitvā, pañca mahā vilokane viloketvā suddhodana mahā rājānaṃ nissāya mahā māya deviyā kucchismiṃ paṭisandhiṃ ganhitvā dasamāsaccayena mātu kucchito nikkhamitvā ekūnatiṃsatime saṃvacchare mahābhinikkhamanaṃ nikkhamitvā chabbassāni mahāpadhānaṃ padahitvā pañcatiṃsatime saṃvacchare sammāsambodhiṃ abhisambhujjhitvā pañcacattāḷīsa saṃvacchārani vasitvā sappa saṃvacchare vesākha puṇṇamiyaṃ bhummavāre parinibbāyi.</p><p>Tassa kho pana bhagavato arahato sammāsambuddhassa sāsanaṃ pañcavassasahassāni pavattissati. Idāni pana deve sahassa pañcasata........ Saṃvaccharāni ceva.........māsāni ca.........divasāni atikkhantāni. Deve sahassa catussata......saṃvaccharāni ceva........ Māsāni ca........divasāni avasiṭṭhāni.</p><p>Ayaṃ......saṃvacchare.........utuasmiṃ utumhi.......māsassa......pakkhe....... .........vāra midanti daṭṭhabbaṃ.</p><p></p><p></p><p>Appendix for Buddhist Era</p><p></p><p></p><p>Mūsiko vasabho vyagga - sasanāgāni mevaca</p><p>sappassaja kapīceva - kukkuṭo soṇasūkaro</p><p>12 Years</p><p>	12 Months</p><p>	3 Seasons </p><p>	7 days</p><p>	                                                                              1. 	mūsika</p><p>	Citta</p><p>	gimhāna</p><p>	                                                                              1. ravivāro</p><p>	                                                                              2. 	vusabha</p><p>	vesākhaka</p><p>	                                                                              2. candavāro</p><p>	                                                                              3. 	vyagga</p><p>	jeṭṭha</p><p>	                                                                              3. bhubhammavāro</p><p>	                                                                              4. 	sasa</p><p>	āsaḷlha</p><p>	                                                                              4. budhavāro</p><p>	                                                                              5. 	nāga</p>', '-72-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 73, '<p>	sāvana</p><p>	vassāna</p><p>	                                                                              5. guruvāro</p><p>	                                                                              6. 	sappa</p><p>	poṭṭhapāda</p><p>	                                                                              6. sukkavāro</p><p>	                                                                              7. 	assa </p><p>	assayuja</p><p>	                                                                              7. soravāro</p><p>	                                                                              8. 	aja</p><p>	kattika</p><p>	</p><p></p><p>	                                                                              9. 	kapi</p><p>	māgasira</p><p>	hemanta</p><p>	</p><p></p><p>	                                                                              10. 	Kukkuṭa</p><p>	phussa</p><p>	</p><p></p><p>	                                                                              11. 	Soṇa</p><p>	māgha</p><p>	</p><p></p><p>	                                                                              12. 	sūkara</p><p>	Phagguna</p><p>	</p><p></p><p>	</p><p></p><p>2 pakkha</p><p>	2 Uposatha</p><p>	                                                                              1. kālapakkha</p><p>	                                                                              1. Cātuddasī</p>', '-73-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 74, '<p>	                                                                              2. sukkhapakkha</p><p>	                                                                              2. Paṇṇarasī</p><p>	</p><p></p><p>Ārabha gāthā for Parittas</p><p>Paṭhamabhānavāro</p><p>                                                                              1. Dasadhammasuttaṃ:</p><p>bhikkhūnaṃ guṇa saṃyuttaṃ - yaṃ desesi mahā muni;</p><p>yaṃ sutvā paṭipajjanto - sabba dukkhā pamuccati;</p><p>sabba loka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              2. Mahāmaṅgalasuttaṃ:</p><p>yaṃ maṅgalaṃ dvādasasu - cintayiṃsu sadevakā;</p><p>sotthānaṃ nādhigacchantī - aṭṭhatiṃsaṃ ca maṅgalaṃ;</p><p>desitaṃ devadevena - sabbapāpavināsanaṃ;</p><p>sabbalokahitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              3. Ratanasuttaṃ:</p><p>koṭīsatasahassesu - cakkavāḷesu devatā;</p><p>yassānaṃ patigaṇhanti - yañca vesāliyaṃ pure;</p><p>rogāmanussadubbhikkha - sambhūtaṃ tividhaṃ bhayaṃ;</p><p>khippamantaradhāpesi - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              4. Mettasuttaṃ:</p><p>yassānubhāvato yakkhā - neva dassenti bhiṃsanaṃ;</p><p>yamhi cevānuyuñjanto - rattiṃ divamatandito;</p><p>sukhaṃ supati suttoca - pāpaṃ kiñci na passati;</p><p>evamādi guṇopetaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              5. Khandhasuttaṃ:</p><p>sabbāsīvisajātīnaṃ - dibbamantāgadaṃ viya;</p><p>yaṃ nāsesi visaṃ ghoraṃ - sesañcāpi parissayaṃ;</p><p>āṇakkhettamhi sabbattha - sabbadā sabba pāṇinaṃ;</p><p>sabbasopi vināseti - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              6. MettānisaṃsaSuttaṃ:</p><p>aggikkhandhopamaṃ sutvā - jātasaṃvegabhikkhunaṃ,</p><p>assāsatthāya desesi - yaṃ parittaṃ mahāmuni,</p><p>sabba loka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              7. Mittānisaṃsasuttaṃ:</p><p>pūrento bodhisambhāre - jātho temiyajātiyaṃ,</p>', '-74-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 75, '<p>mittānisaṃsaṃ yaṃ āha - sunandaṃ nāma sārathiṃ,</p><p>sabbaloka hitatthāya - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              8. Morasuttaṃ:</p><p>pūrentaṃ bodhisambhāre - nibbattaṃ morayoniyaṃ;</p><p>yena saṃvihitā rakkhaṃ - mahāsattaṃ vane carā;</p><p>cirassaṃ vāyamantāpi - neva sakkhiṃsu gaṇhituṃ;</p><p>brahmamantanti akkhātaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              9. Candasuttaṃ:</p><p>rāhunā gahito cando - mutto yassānubhāvato,</p><p>sabbaveri bhayaṃ nāsaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              10. Suriyasuttaṃ:</p><p>suriyo rāhugahito - mutto yassānubhāvato,</p><p>sabbaveri bhayaṃ nāsaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              11. Dhajaggasuttaṃ:</p><p>yassānussaraṇenāpi - antalikkhepi pāṇino,</p><p>patiṭṭhamadhigacchanti - bhūmiyaṃ viya sabbathā,</p><p>sabbūpaddava jālamhā - yakkhacorāri sambhavā,</p><p>gaṇanā naca muttānaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Dutiyabhānavāro</p><p>                                                                              1. Mahā kassapatthera bojjhaṅaga suttaṃ:</p><p>yaṃ mahākassapatthero - parittaṃ munisantikā,</p><p>sutvā tasmiṃ khaṇe yeva - ahosi nirupaddavo,</p><p>bojjhaṅagabala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              2. Mahā moggallānatthera bojajhaṅaga suttaṃ:</p><p>moggallānopi theroyaṃ - parittaṃ munisantikā,</p><p>sutvā tasmiṃ khaṇe yeva - ahosi nirupaddavo,</p><p>bojjhaṅaga bala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              3. Mahā cundatthera bojjhaṅaga suttaṃ:</p><p>bhagavā lokanāthoyaṃ - cundattherassa santikā,</p><p>sutvā tasmiṃ khaṇe yeva - ahosi nirupaddavo,</p><p>bojjhaṅaga bala saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              4. Girimānanda suttaṃ:</p><p>theroyaṃ girimānando - ānandattherasantike,</p><p>sutvā tasmiṃ khaṇe yeva - ahosi nirupaddavo,</p><p>dasasaññūpa saṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>                                                                              5. Isigili suttaṃ:</p>', '-75-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 76, '<p>paccekabuddhanāmāni - desento yaṃ adesayi,</p><p>anantabalasaṃyuttaṃ - parittaṃ taṃ bhaṇāmahe.</p><p>Merit sharing with late Mahātheras</p><p>sīlādinā guṇagaṇena katūbhayatthā</p><p>therāsabhā mama garū tidivaṃ supattā</p><p>puññenanena pakatena samiddhacittā</p><p>santiṃ bhavantu hadayassa sukhena pattā.</p><p>Sādhū!....sādhū!....sādhū!....</p><p></p><p></p><p>Tāyana gāthā</p><p>‘‘chinda sotaṃ parakkamma, kāme panuda brāhmaṇa;</p><p>nappahāya munī kāme, nekattamupapajjati.</p><p>‘‘kayirā ce kayirāthenaṃ, daḷhamenaṃ parakkame;</p><p>sithilo hi paribbājo, bhiyyo ākirate rajaṃ.</p><p>‘‘akataṃ dukkataṃ seyyo, pacchā tapati dukkaṭaṃ;</p><p>katañca sukataṃ seyyo, yaṃ katvā nānutappati.</p><p>‘‘kuso yathā duggahito, hatthamevānukantati;</p><p>sāmaññaṃ dupparāmaṭṭhaṃ, nirayāyūpakaḍḍhati.</p><p>‘‘yaṃ kiñci sithilaṃ kammaṃ, saṅkiliṭṭhañca yaṃ vataṃ;</p><p>saṅkassaraṃ brahmacariyaṃ, na taṃ hoti mahapphala’’nti.</p><p>‘Sabbe sattā bhavantu sukhitattā’</p><p>buddha sāsanaṃ ciraṃ tiṭṭhatu!</p><p>buddha sāsanaṃ ciraṃ tiṭṭhatu!</p><p>buddha sāsanaṃ ciraṃ tiṭṭhatu!</p><p></p><p></p><p>sādhu! sādhu! sādhu!</p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p>', '-76-');
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_iit_chants', 77, '<p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p></p><p>No Commercial value</p>', '-77-');
